INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bonfire of the Vanities", "虚栄のかがり火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "　ウォール街で成功を収め、リッチな生活を満喫していたマッコイは妻ジュディの目を盗み、愛人マリアと浮気を楽しんでいた。そんなふたりを乗せた車がブロンクスで黒人をはねてしまう。新聞記者ファローはこのネタを掴み、記事に。白人エリートによる轢き逃げ事件は波紋を巻き起こし、人種問題に発展。マッコイは窮地に立たされるが……。トム・ウルフのベストセラー小説を豪華キャストで映画化。都会に生きる者たちの野心と欲望、そして転落をシニカルに描いたブラックコメディ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スチームボーイ", "スチームボーイ", "", "", "http://image.eiga.k-img.com/images/movie/1672/315x210.jpg?2422", 126, "1988年の「AKIRA」で世界に日本のアニメーションを知らしめた大友克洋が、製作期間9年、製作費24億円、作画枚数18万枚で描く空想冒険活劇。舞台は19世紀半ば、栄華を誇る大英帝国。発明と機械いじりの好きな13歳の少年レイは、祖父と父が開発した世紀の発明“スチームボール”をめぐる陰謀に巻き込まれていく。実際の19世紀英国の風景や建築物に基づく緻密な美術、錆びた鉄や蒸気のゆらぎなどの“質感”の描写にも要注目。", "2004-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美代子阿佐ヶ谷気分", "美代子阿佐ヶ谷気分", "", "", "http://image.eiga.k-img.com/images/movie/54502/315x210.jpg?3919", 86, "1970年代に「月刊漫画ガロ」などで活躍した安部慎一の傑作青春劇画を映画化。1970年代初頭、九州から上京してきた漫画家・安部慎一は、恋人の美代子と阿佐ヶ谷で同棲生活を送っていた。やがて美代子をモデルに描いた漫画が雑誌に掲載されて好評を呼ぶが、私生活をモチーフにした創作活動は次第に慎一の神経をすり減らしていき……。安部慎一を実力派俳優の水橋研二、後の妻・美代子を町田マリーが好演する。", "2009-07-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いのちの地球 ダイオキシンの夏", "いのちの地球 ダイオキシンの夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "少女ジュリアが11歳の誕生日を迎えた日に、近所の化学工場が爆発し、まもなくして苦痛を訴える人やペットが急増する。ジュリアは友達と探偵団を結成し、原因解明に乗り出すが……。1976年にイタリアのセベソで起きたダイオキシン汚染をベースに記された児童文学をアニメーション化。子供たちに地球汚染の警鐘を鳴らす意欲作。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花より男子ファイナル", "花より男子ファイナル", "", "", "http://image.eiga.k-img.com/images/movie/53302/315x210.jpg?1927", 131, "神尾葉子の人気コミックをテレビドラマ化し高視聴率を記録した「花より男子」の劇場版。井上真央、松本潤、小栗旬ほかテレビ版でおなじみのキャストやスタッフが再結集し、原作にはないエピソードを描き出す。道明寺がつくしにプロポーズしてから4年後、両家が揃った結納の席で、司の母・楓からつくしに100億円の価値のあるティアラ“ビーナスの微笑”が贈られる。しかしその晩、ティアラが何者かに奪われてしまい……。", "2008-06-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("第２回 欽ちゃんのシネマジャック", "第２回 欽ちゃんのシネマジャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "１本20分６本立て、料金自己申告制という型破りの興行で話題となった、“欽ちゃんムービー”第２作。今回は、萩本欽一自らの監督作が３本。閉店間際の寿司屋で、仲の良い主人夫婦の幸せを客が邪魔する「蛍の光」。昭和30年代、チンピラと音大生との異なる世界に生きる二人の愛を描いた「やさしい嵐」。桃井かおりと欽ちゃんが、夫婦役で初共演したコメディ「なんかヘン？Part２」。この３本に加え、食事制限をうけた老人の憂欝を、コメディ・タッチで描いた山本晋也監督作「食べる ある愛のカタチ」、画家・原田泰治が愛娘に送った絵本を原作とする「さだおばさん」、山田邦子の自伝的エッセイをアニメ化した「邦ちゃんの一家ランラン」というラインナップ。製作母体は、欽ちゃん監督作３本が石原プロモーション、「食べる…」がマイルストーン、「さだおばさん」がプルミエ・インターナショナル、「邦ちゃんの一家ランラン」が日本アニメーション。これに、新人監督・丹野マサシによる、おまけの“１分映画”が付いてくる。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("藤原義江のふるさと", "藤原義江のふるさと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "日本初の本格的トーキー「マダムと女房」に先立ち、溝口が手掛けた“ミナトーキー”と呼ばれる部分発声映画。売れない声楽家・義夫とホテルの掃除婦あや子は貧しいながらも幸福な日々を暮らしていたが、あるきっかけで義夫にチャンスが訪れたために二人の仲にヒビが入る……。実際に国際的な声楽家だった藤原義江が主演。", "1930-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青葉城の鬼", "青葉城の鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "山本周五郎の小説『樅の木は残った』の映画化。主人公、原田甲斐は、実説では伊達六十二万石の存続を危うくした、お家騒動の黒幕的存在。これを、実はこの騒動が、老中・酒井雅楽頭の外様大名取り潰しの策謀であるとし、伊達家存続に尽力しながら、一人で罪を背負って死んだ男として、原田甲斐を解釈している。長谷川一夫が、善悪の境界線上で苦悩する甲斐を熱演。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REAP THE WILD WIND", "絶海の嵐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "スペクタクル映画の巨匠C・B・デミル監督が手掛けたテクニカラー海洋活劇。貨物船を難破させては積み荷の引き揚げを請け負い、暴利をむさぼるカトラー兄弟と闘う人々の勇姿を描いていく。兄弟に船を沈められて復讐に燃える船長にJ・ウェイン、協力する弁護士にR・ミランドが扮している。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHO’S BEEN SLEEPING IN MY BED？", "僕のベッドは花ざかり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "全世界で人気、各国の女性ファンにちやほやされているTVプロデューサー、ジェイソンと婚約したメリッサ。だがジェイソンは彼女から逃げるのに一生懸命で、その結果神経が参ってしまい精神病院へ。なんとか彼と結婚したいメリッサは友人と共謀して“ニセ結婚”をデッチ上げるが……。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCHMUTZIGER ENGEL", "めざめ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "金持ちの娘ベアーテは何不自由なく暮らしてきたわがままな少女。その彼女が望んだものは自分の担任の先生を恋人にすることだった。だった。生徒の人気の的である先生に、ベアーテはあの手この手を使って接近しようと試みるが、おかげで学校も家庭も大騒動に……。主演のC・コリンズの魅力が十分に発揮された青春ドラマ。", "1958-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いちげんさん", "いちげんさん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "D・ゾペティが、自らの留学体験を日本語で著し、すばる文学賞を受賞した恋愛小説の映像化。京都の大学で日本文学を専攻していた“僕”は、目の不自由な京子の対面朗読を引き受ける。外国人であるがゆえ、日本語を話し、箸を使うだけで奇異の目で見られることに苛立っていた“僕”は、外見でなくその本質を見ようとする京子に惹かれていく。次第に二人は、特別な愛で結ばれるが……。観光映画に堕することなく、みずみずしい京都の街や風土をさりげなく捉えた映像が印象的。また、自らも日本に滞在した経験を持つE・アタートンの流暢な日本語による朗読シーンも見もの。鈴木保奈美の大胆なヌード・シーンも話題を呼んだ。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ある夜のできごと", "ある夜のできごと", "", "", "http://image.eiga.k-img.com/images/movie/55682/315x210.jpg?1444", 0, "「エンプティー・ブルー」の秦秀明が主演する大人の青春ストーリー。同じ中学を卒業したカッちゃん、ヤスタカ、ケンさん、ナオ、真由美の男女5人は、地元の同じ大学に進学して青春時代をともに過ごすが、社会に出てからはすっかり疎遠になっていた。数年後、久々に帰郷したカッちゃんは知人の通夜でヤスタカやケンさんと再会するが、彼らとの会話に微妙な距離感を感じていた。", "2010-10-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("虹の女神 Rainbow Song", "虹の女神 Rainbow Song", "", "", "http://image.eiga.k-img.com/images/movie/33712/315x210.jpg?3583", 117, "岩井俊二が自身の監督作以外で初めてプロデュースを手掛けた青春ラブ・ストーリー。大ヒット作「ニライカナイからの手紙」の熊澤尚人監督が、人気若手俳優の市原隼人と上野樹里を主演に迎え、男女の切ない恋と友情を描く。原案は女子中高生たちから絶大な支持を受ける作家・桜井亜美。大学時代に一緒に自主制作映画を撮っていた智也とあおい。卒業後、映像制作会社に入社した智也は、渡米したあおいが飛行機事故で亡くなったことを知る。", "2006-10-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　PAPILLON", "パピヨンの贈りもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "本国フランスで高い評価を得ているP・ミュイル監督が撮り上げたのは、おませな少女と孤独な老人が、幻の蝶“イザベル”を求めて旅に出る物語。ぶつかり合いながらも、旅を続けるうちに互いに打ち解け合っていく二人の心の移り変わりが、フランスの雄大な自然の中に綴られていく感動作。少女役の新星C・ブアニッシュの愛らしさも印象的。パリに住む８歳の少女エルザは、忙しい母親にかまってもらえずいつも一人ぼっち。同じアパートの老人ジュリアンも、息子を亡くしてからは趣味の蝶収集に没頭する日々。ある日二人はひょんなことから、幻の蝶探しの旅に出る。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEEP RISING", "ザ・グリード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "海底に潜む巨大生物の大暴れを描いたモンスター・パニック映画。東シナ海を処女航海中の豪華客船で、原因不明の大事故が発生。通りがかりの密輸船の乗組員たちが客船に移ると、船内は血みどろの地獄絵図と化しており、３千人の乗客は消えていた。やがて彼らも、人間を食べる不気味な怪物の襲撃に遭い……。ロブ・ボッティンのデザインによる、グロテスクなクリーチャーは一見の価値あり。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Un film comme les autres", "ありきたりの映画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "", "1968-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DeadHeads", "ゾンビ・ヘッズ　死にぞこないの青い春", "", "", "http://image.eiga.k-img.com/images/movie/57812/315x210.jpg?3960", 95, "「死霊のはらわた」の特撮に携わっていた父親の影響で、幼い頃からホラー映画に親しんで育ったブレット＆ドリュー・ピアース兄弟が手がけた異色のゾンビ映画。人間としての記憶や知性を保ったままゾンビになった青年が、人間だった時に付き合っていた女性に婚約指輪を渡すため旅に出る姿を描く。死体袋で目を覚ました青年マイクは、自分がゾンビになっていることに気づく。しかし、なぜか人間としての記憶や知性を取り戻しており、ゾンビとしても人間としても中途半端な状態に思い悩む。そんな時、恋人に渡しそびれた婚約指輪を見つけたマイクは恋人探しの旅に出るが、その行く手をゾンビハンターたちが阻み……。", "2012-04-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUEBEARD’S EIGHTH WIFE", "青髭八人目の妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "“青髭”といっても殺人鬼の話ではなく、お金のために気難しい億万長者の後妻になったフランス貧乏貴族の娘が、野暮な夫を飼い慣らす、という筋立て。「天使」と「ニノチカ」の間に製作された巨匠のルビッチ円熟期の作品だが、どこか精彩を欠く。E・E・ホートンは相変わらずの好演。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAZEPPA", "ジェリコー・マゼッパ伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ロマン派の画家、テオドール・ジェリコーと曲馬の名手フランコーニとの師弟関係を、現代舞台芸術の寵児であり、馬術劇団とも呼ばれる“ジンガロ”を率いるバルタバスが映像化。18世紀初頭、ジェリコーは『突撃する近衛猟騎兵士官』と題された作品を発表し好評を博す。しかしその絵を見たフランコーニの評価は、ジェリコーは馬については素人だというものだった。連日、荒馬に乗って野山を駆けめぐるほど馬好きのジェリコーはこの一言に大きく傷つく。そして二週間後、フランコーニの一座を訪れたジェリコーは、馬の魅力がより深いことを知り、劇団の一員としての生活を始める……。ビデオ発売時タイトルは「マゼッパ伝説」。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カクトウ便 Vol.1 Battle Run XX", "カクトウ便 Vol.1 Battle Run XX", "", "", "http://image.eiga.k-img.com/images/movie/53472/315x210.jpg?2049", 90, "どんな物でも届ける裏の運び屋“カクトウ便”の活躍を、「学校の階段」の甲斐麻美ほか人気アイドル共演で描く本格アクションのシリーズ第1弾。ジャーナリスト志望の女子大生アカネは、ある議員の悪事を立証する書類を手に入れたために何者かに追われていた。“カクトウ便”の存在を知った彼女は助けを求めるが、現われたのはなんと1人の女子高生だった。香港映画界でも活躍する谷垣健治がアクション監督を担当。", "2008-03-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ツルモク独身寮", "ツルモク独身寮", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "『ビッグコミック・スピリッツ』に連載された同名人気コミックの映画化。四国から上京し、ツルモク家具に就職した正太。そんな彼が田舎に残してきた恋人のともみと、独身寮のマドンナ・みゆきの間で揺れ動きながらも、寮の個性的な仲間たちとともに、男として成長していく姿を軽快なテンポで描いた青春映画。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒカリ、その先へ", "ヒカリ、その先へ", "", "", "http://image.eiga.k-img.com/images/movie/55632/315x210.jpg?2722", 56, "俳優としても活躍するR＆Bシンガーの松下優也が本人役で映画初主演を果たす青春ドラマ。プロのカメラマンを目指して専門学校に通う少女きいと、ミュージシャンとして多忙な日々を送る青年ユウ。夢を追い続ける2人の若者が、葛藤（かっとう）や挫折を乗り越えながら成長していく姿を描きだす。きい役にNHK連続テレビ小説「ウェルかめ」の倉科カナ。数多くの人気アーティストのミュージックビデオを手がける江原慎太郎監督がメガホンを取る。", "2010-10-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TREASURE　ISLAND", "宝島〈パオタオ〉：トレジャー・アイランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "世紀末の台北を舞台に、事件に巻き込まれていく青年の悲劇を描く。20世紀末の台北。田舎から出てきた青年フォンは、カラオケ・ルームで生活費を稼いでいた。ある日、友人たちと立ち寄った喫茶店で、彼はミステリアスな美女タンに心惹かれ、彼女が忘れていった日記帳を手掛かりに行方を探し始める。暗黒街のボス、チャオの情婦であるタンは、いつしかフォンの優しさになぐさめを求めるようになる。やがて、タンは自分自身の偽装誘拐を計画し、フォンに協力を求めるが、そのため二人は組織から追われることになる。", "1993-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Factory Girl", "ファクトリー・ガール", "", "", "http://image.eiga.k-img.com/images/movie/53392/315x210.jpg?2330", 91, "名家の令嬢として生まれ、アンディ・ウォーホルのミューズとして60年代を代表するポップアイコンとなったイーディ・セジウィック。彼女の28年の生涯を、現代のトレンドアイコンとして注目を浴びるシエナ・ミラー主演で映画化。アンディ・ウォーホルを「メメント」のガイ・ピアース、ウォーホルと同じくイーディに翻弄されるボブ・ディランを「ジャンパー」のヘイデン・クリステンセンが演じる。キュートな60年代ファッションも見所。", "2008-04-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("吸血髑髏船", "吸血髑髏船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "「吸血鬼ゴケミドロ」に続く、松竹怪奇映画の第２作。数億の金塊を積んだ貨物船の乗組員が、金目当ての５人組によって皆殺しにされる。やがて貨物船は幽霊船と化し、殺された船員の妹に霊が乗り移って、５人組への恐ろしい復讐が始まった。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太と銀河超特急", "ドラえもん のび太と銀河超特急", "", "", "http://ecx.images-amazon.com/images/I/61ykSV6OY2L._SL210_.jpg", 97, "ドラえもんが未来の列車の切符を手に入れてきた。みんなは“ミステリートレイン”に乗り込み、宇宙の旅を満喫する。ところが、途中で立ち寄った遊園地がナゾの宇宙生物ヤドリに襲われる。遊園地を乗っ取ったヤドリをドラえもんたちは倒すことができるのか？", "1996-03-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("All or Nothing", "人生は、時々晴れ", "", "", "http://image.eiga.k-img.com/images/movie/1252/315x210.jpg?3112", 128, "タクシー運転手フィルは、スーパー店員の妻ベニー、老人ホーム清掃員の娘、無職の息子の4人家族。問題を抱えつつも穏やかな暮らしだが、ある日、フィルの息子が心臓発作で入院。それを契機に、各人がそれまで表に出さなかったさまざまな感情が噴出していく。監督は「ネイキッド」「秘密と嘘」でカンヌ映画祭はじめ各国の映画賞を受賞した英国の巨匠マイク・リー。俳優たちとディスカッションしながら映画を作る手法は今回も同じ。", "2003-06-21", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIVA DJANGO", "皆殺しのジャンゴ　復讐の機関銃〈ガトリングガン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "T・ヒルが“初代ジャンゴ”のフランコ・ネロに負けず劣らずの不死身ぶりを発揮したマカロニ・ウエスタン。愛する妻を殺された男ジャンゴの復讐劇がハードなタッチで描かれている。棺桶の中に隠した機関銃で敵を一掃するラストは圧巻。", "1969-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("101 DALMATIANS", "101", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "アニメ「101匹わんちゃん」としても有名なD・スミスの児童文学を実写で映画化。ダルメシアン犬ボンゴの主人ロジャーとファッション・デザイナーのアニタは犬を散歩している途中で出会い、たちまち恋に落ちて結婚。ボンゴもアニタの愛犬パーディタと結ばれ、15匹の子供が生まれる。だが、アニタの上司でファッション界の女王クルエラはダルメシアンの皮でコートを作るために、子犬を盗み出す……。犬のネットワークを使い子供たちの居所をつかんだボンゴとむく犬の大佐が手を組み、全部で99匹のダルメシアンの子犬を救出するまでを描いた動物ファンタジー。クルエラを演じたG・クローズの怪演が強烈。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("How I Spent My Summer Vacation", "ハウ・アイ・スペント・マイ・サマー・バケーション（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Anacondas: The Hunt for the Blood Orchid", "アナコンダ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "'97年にジェニファー・ロペスが主演した動物パニック映画の続編。今回のキャストはほぼ無名ながら、密林を訪れた人間たちに容赦なく襲いかかるヘビが質量共にパワーアップ。アニマトロニクスとCGで生命を吹き込まれた凶暴なヘビ軍団が、ジャングルで猛威をふるう絶叫ムービーだ。不老薬の素となる希少な花を求め、ボルネオの密林に入った科学者チーム。しかし交尾期を迎えて凶暴化したアナコンダが彼らに迫り……。", "2005-03-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIRENS", "泉のセイレーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "オーストラリアの画家ノーマン・リンゼイをモデルにしたフィクション。自由奔放に生きる画家の生活に触れた女性が、自らの欲望や感性に目覚め、心の解放をするまでを描く。1930年代初頭。リンゼイが女性の裸体を描いた『磔のビーナス』に憤慨したシドニー聖職教会は、絵画展への出展をやめさせるために、英国教会の牧師アンソニー・カンピオンを派遣する。彼は妻のエステラを伴って、妻と３人のモデルと住むリンゼイを訪れた。最初はモデルたちの奔放な立ち振る舞いに驚いていたエステラだったが、次第に彼女たちの生き方に触発されていく。さらに彼女は、盲目の美しい男と出会ったことから本来の生き方に気付いていくのだった。", "1993-01-01", "11,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BILLY THE KID AND THE GREEN BAIZE VAMPIRE", "ビリー・ザ・ハスラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ビリヤードの名人として青春を送るビリーの前に、中世から幾多の時代の荒波をかいくぐってきた吸血鬼ドラキュラが、生涯の強敵として立ちはだかる。史上最強のチャンピオンの座を懸け、ついに決戦の日が来るが……。古典的なジャンルに挑みつつ、近未来ミュージカルのスタイルを提示してみせたロック・オペラ。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("落語物語", "落語物語", "", "", "http://image.eiga.k-img.com/images/movie/55912/315x210.jpg?2022", 111, "映画監督としても活動する落語家・林家しん平が、自らの実体験を織り交ぜながら落語の世界を舞台に撮りあげた人情ドラマ。真打の今戸家小六に弟子入りした引っ込み思案な青年・真人が、温かい師匠やおかみさんに見守られながら成長していく姿を描く。主人公の真人役には、二つ目の柳家わさびが大抜てき。師匠役を「百万円と苦虫女」のピエール瀧、おかみさん役を「さんかく」の田畑智子が演じる。", "2011-03-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("テケテケ", "テケテケ", "", "", "http://image.eiga.k-img.com/images/movie/54412/315x210.jpg?4073", 72, "綾花と加奈は学校の帰り道に、“テケテケ”という不気味な音を耳にする。その翌日、綾花は下半身の無い死体で発見され、世間では下半身のない人間の都市伝説「テケテケ」の話題で持ちきりになる。そのテケテケが遂に可奈の目の前にも現われ……。主演はAKB48の大島優子、監督は「口裂け女」「グロテスク」の白石晃士。", "2009-03-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バカがウラヤマシイ", "バカがウラヤマシイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "東京・下北沢の短編映画館トリウッドと専門学校東京ビジュアルアーツが「学生による商業映画製作」をコンセプトに立ち上げたトリウッドプロジェクトの第5弾作品。大きな失敗もせず、なんでもそつなくこなしてきた希だったが、就職活動では不採用ばかり。失敗を周囲に知られたくない希は、大手企業からの不採用を周囲に隠し、なんとか小さな会社に就職する。そこで出会ったどこか冴えない男・曽根は、どんな失敗をしても笑ってばかりで、希は曽根を冷たい目で見ていたのだが……。", "2010-10-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Faster", "ファースター　怒りの銃弾", "", "", "http://image.eiga.k-img.com/images/movie/55882/315x210.jpg?3174", 98, "ドウェイン・ジョンソン主演のアクションドラマ。銀行強盗を働いた際に仲間の裏切りに遭い、兄を亡くしたジェームズ（ジョンソン）は、10年の刑期を終えて出所すると、裏切り者の処刑を開始する。だが、引退を10日後に控えたベテラン刑事や身勝手なヒットマンらがジェームズの前に立ちはだかる。監督は「ザ・ダイバー」「ソウル・フード」のジョージ・ティルマン・Jr.。共演にビリー・ボブ・ソーントン、トム・ベレンジャー。", "2011-05-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PHONE", "ボイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "携帯電話という身近なツールを通して拡散する呪いの恐怖を描いたホラー映画。随所に散りばめられた巧妙なショック演出、子役が見せる鬼気迫る怪演など、怖がりどころがたっぷりの一作だ。脅迫電話に悩まされていた女性ジャーナリスト、ジウォン。ある日、彼女の携帯電話を手にした友人の幼い娘ヨンジュが異常な振る舞いを見せる。ジウォンはその携帯番号の過去の持ち主を調べていくが、次々と怪現象に襲われてしまう。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刑事物語３ 潮騒の詩", "刑事物語３ 潮騒の詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "長崎県・五島列島。片山は民宿“海っ子”を張り込む。そこでは逃走中の殺人犯・仁科の妻・清子と娘の海子が暮らしていた。海に落ちた子供を助けようとして気絶した片山は“海っこ”に担ぎ込まれ民宿を手伝うはめに。そんなある日、仁科から清子に連絡が……。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水滸伝", "水滸伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "中国古典を原作に、宋代の悪政に反抗し梁山泊に集まった豪傑たちの活躍を描くスペクタクル。梁山泊を拠点に、悪徳高官を成敗する勇者たちは、民衆の支持を得ていた。面白くない大臣・高球は軍勢を差し向けるが逆に敗れ、続いて武術大会でリーダー暗殺を図るが……。", "1983-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hannah Montana & Miley Cyrus: Best of Both Worlds Concert Tour", "ハンナ・モンタナ　ザ・コンサート 3D", "", "", "http://image.eiga.k-img.com/images/movie/53452/315x210.jpg?2928", 100, "世界中のティーンを熱狂させた大ヒットTVドラマ「シークレット・アイドル　ハンナ・モンタナ」から飛び出したキャラクター、ハンナ・モンタナのコンサートを、最新鋭の3D技術を駆使して捉えたコンサートムービー。新世代のスーパーアイドルとして世界中の注目を浴びているハンナ・モンタナ＝マイリー・サイラスの魅力が炸裂する。さらに全米で大人気のロックバンド、ジョナス・ブラザーズがゲスト出演。", "2009-05-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野獣死すべし", "野獣死すべし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "大薮春彦の原作を須川栄三監督が映画化したハードボイルド・アクション。主人公・伊達邦彦は勤勉な秀才の大学院生。反面、彼は完全犯罪に異常な興味を示し、殺人のスリルに陶酔するという二つの顔を持っていた。クールな伊達に扮した仲代達矢が好演。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST VALLEY", "最後の谷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "戦乱にあえぐ村を破壊から守り抜く、平和を愛する一人の男の姿を描いた戦争アクション。17世紀に起こった30年戦争を題材に、個性派俳優O・シャリフがシリアスな演技で主人公を好演。それにクセ者俳優M・ケインが絡み、娯楽映画的な面白さにもあふれた作品となっている。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISSING IN ACTION2: THE BEGINNING", "チャック・ノリスの地獄のヒーロー2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ベトナム戦争末期、捕虜として収容所で強制労働させられているアメリカ兵たちがいた。その中の一人、ブラドック大佐はリンチに苦しむ仲間を救うため、武器を奪い反撃を開始する。主演のC・ノリスのダイナミックなアクションが見もの。「地獄のヒーロー」に続く、シリーズ第２作。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jurassic Park III", "ジュラシック・パークIII", "", "", "http://image.eiga.k-img.com/images/movie/1462/315x210.jpg?3898", 92, "スピルバーグは製作総指揮のみ担当の第3作。特殊効果のマイケル・ランティエリ、ライブ・アクション・ダイナソーのスタン・ウィンストンは前2作と同じ。編集はジョンストン監督と「ジュマンジ」「遠い空の～」で組んだシェリー・ジョンソンが務める。前作から4年。パラセイリング中に、今も恐竜たちが生息するあの島に不時着した12歳の少年エリックを救助するため、第1作の恐竜博士、グラントらの捜索隊が島に向かう。", "2001-08-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SANDLOT", "サンドロット 僕らがいた夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1962年の夏、ある町に引っ越してきた少年スコッティは、野球を通じて近所の子供たちと仲間になる。ある日、義父の宝もののベーブ・ルースのサインボールを持ち出したスコッティは、怪物が住むという屋敷にボールを打ちこみ……。少年時代の思い出を1960年代の音楽にのせてノスタルジックに描いた愛すべき一作。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROSENCRANTZ AND GUILDENSTERN ARE DEAD", "ローゼンクランツとギルデンスターンは死んだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "英国で活躍する、チェコ生まれの戯曲家T・ストッパードが、その代表作を自らの手で映画化。シェイクスピアの『ハムレット』に、ほんの端役として登場する、ハムレットの学友ローゼンクランツとギルデンスターンを主役にすえ、彼らの眼から“ハムレット”の世界を見つめている。『ハムレット』の人間関係を知ったうえで見ると面白い。", "1990-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beautiful Boxer", "ビューティフル・ボーイ", "", "", "http://image.eiga.k-img.com/images/movie/1282/315x210.jpg?3809", 119, "性同一性障害を抱えながら、タイの国技ムエタイの選手として活躍した実在の人物パリンヤー・ジャルーンポーンの半生を描く感動作。パリンヤーは苦悩の中で化粧をしてリングに立つようになる。主人公を演じるのは、実際にムエタイ選手として活躍した後に俳優に転身したアッサニー・スワン。彼の友人役で、タイの人気俳優シティポン・ニヨムが共演。日本遠征で対決する女子プロレスラー役で、人気レスラー井上京子が本人役で出演する。", "2005-10-15", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渡り鳥いつまた帰る", "渡り鳥いつまた帰る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "佐渡に流れて来た“渡り鳥”滝伸次は、戦時中に日本軍が隠匿した貴金属を独り占めにしようとする悪玉の野望を阻止する。宍戸錠が主人公のライバル“ハジキの哲”として登場し敵対するが、最後は協力して悪玉を倒す。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARRAD EXPERIMENT", "青い接触", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "性に対して進歩的な全寮制の大学で、新学期から男女一組が同室となる。シーラは女性に人気のあるスタンレーと同室となり、恋に落ち自然と結ばれる。が、彼の方はシーラを恋人だと思ってはいなかった……。T・ヘドレンとD・ジョンソンが共演しているのも意外。ちなみにヘドレンの娘メラニー・グリフィスはジョンソンと２度離婚した後、アントニオ・バンデラスと再々婚をしている。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("踊る龍宮城", "踊る龍宮城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "昭和の歌姫こと美空ひばりが、子役として映画に出演した一作。松竹歌劇団の女優たちが多数参加したお祭りコメディで、浦島太郎が帰郷してからのその後の話が面白おかしく綴られる。太郎の故郷は近代化の波を受けて都会になっていた。そこでは、浦島伝説にまつわるコンクールが開催中で、応募者に間違えられた太郎は優勝してしまい、その隙に誰かに玉手箱を盗まれる。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SONATINE", "ソナチネ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "“誰も止めなければ死んでしまう”というプラカードを持って地下鉄に乗り込むが、大人たちが無関心とみると、やおらカラフルな色彩の睡眠薬を飲んでみせる二人の少女。思春期の少女の無邪気で無謀な姿を描く。カナダの女流監督ランクトのみすみずしい映像が光る。", "1984-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神の子たち", "神の子たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "四ノ宮浩監督が「忘れられた子供たち　スカベンジャー」に続き、フィリピン社会の過酷な現実に迫ったドキュメンタリー。マニラ近郊のケソン市にある巨大ゴミ捨て場で大規模な崩落事故が発生し、ゴミの搬入が停止される。映画はこのゴミ捨て場で暮らし、生活の糧を失ってしまった三つの家族への４ヵ月に及ぶ取材を実施。試練に立ち向かう彼らの生き様を通して、家族の絆や人間の尊厳といった根源的なテーマを観る者に問いかける。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jam Films", "Jam Films", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "日本映画界を代表する７人の実力派監督が、自由なテーマで独自の世界を追求した７本のショートフィルム集。岩井俊二が広末涼子を主演に据えて贈る不思議な物語「ARITA」、篠原哲雄が倦怠期のカップルのドラマを綴る「けん玉」、堤幸彦が警察に追われる男と人質の人間模様を描く「HIJIKI」や、足に秘密を持つ女の体験を描いた望月六郎の「Pandora-Hong Kong Leg」、北村龍平の「the messenger−弔うは夜の果てで−」、飯田譲治の「コールド スリープ」、行定勲の「JUSTICE」。各作品に短編ならではのユニークな演出が効いている。", "2002-12-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Batman Returns", "バットマン　リターンズ", "", "", "http://ecx.images-amazon.com/images/I/514ibEEtdUL._SL210_.jpg", 126, "ボブ・ケイン原作の同名アメリカン・コミックスの映画化第2弾。クリスマス間近のゴッサムシティに、再び悪の手が迫っていた。怪人ペンギンと、復讐に燃えるキャットウーマンが、バットマンに立ちはだかる。前作同様、監督は「シザーハンズ」のティム・バートン、バットマンを演じるのはマイケル・キートン。共演に、ダニー・デビート、ミシェル・ファイファーなど。", "1992-07-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("衝動殺人 息子よ", "衝動殺人 息子よ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "行きずりの殺人で、罪のない息子を失った夫婦が、同様の被害にあった人々とともに、被害者遺族を保護する法律を成立させようと国に働きかける姿を描く、実話をもとにした作品。1980年５月１日、“犯罪被害者等給付金支給法”が公布され、映画の訴えは実を結んだ。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRIMSON TIDE", "クリムゾン・タイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "199X年。ロシアの国粋主義者がシベリアの核ミサイル基地を占拠。世界中に緊張が走った。アメリカの戦略原子力潜水艦アラバマは太平洋に向けて緊急出動する。が、そこで人類の未来に大きく影響する未曾有の事態が起こった！　 部下の絶大なる信頼のもと作戦を強行しようとする艦長にはG・ハックマン、そして知的エリートながら確固たる信念のもと艦長と対立する副官にはD・ワシントンを配し、核戦争の危機に直面した状況をスリリングに描き出す軍事サスペンス。現実に起こりうる核の恐怖がリアルに胸に迫る。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オカルト", "オカルト", "", "", "http://image.eiga.k-img.com/images/movie/54462/315x210.jpg?3330", 110, "「ノロイ」「口裂け女」などで知られるホラーの鬼才・白石晃士が、オカルト・ドキュメンタリー番組を製作する人々のスリルや恐怖を描いた意欲作。「アカルイミライ」「トウキョウソナタ」の黒沢清監督や漫画家の渡辺ペコなどが特別出演している。3年前にとある観光地で起きた通り魔殺人事件に興味を持った映画監督の白石は、事件の唯一の生存者で現在はネットカフェ難民の青年・江野に取材を敢行する。", "2009-03-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伝説巨神イデオン　発動篇", "伝説巨神イデオン　発動篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「機動戦士ガンダム」の富野喜幸（現：由悠季）監督が手がけたTVアニメーションの劇場版2部作の完結編。無限エネルギー「イデ」をめぐる地球人とバッフ・クラン人の争いに巻き込まれたコスモやベスらソロシップのクルーたちは、地球からも見捨てられ、必死の逃亡を続けていた。そんな中、バッフ・クラン人のカララがベスとの間にできた子を身ごもったことから、コスモらは2つの種族が分かり合える道を模索する。しかし、その思いとは裏腹に2つの種族の争いは壮絶な最終決戦へとなだれ込み、イデの「無限力」が解放されてしまう。", "1982-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("近頃なぜかチャールストン", "近頃なぜかチャールストン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "次郎は行きずりの少女を公園で追いかけ、婦女暴行未遂でブタ箱入り。彼はそこで非行中高年たちと知り合う。この老人たちは自分たちを独立国“ヤマタイ国”の閣僚と称している。翌日、釈放された次郎は、お手伝いのタミ子とヤマタイ国を探すが、何とそこは蒸発中の次郎の父が、彼らに無償で提供した家だった。かくして老人たちと次郎の奇妙な交流が始まる。戦中派の心情をコミカルに描くことに定評のある岡本喜八の作品。高齢化社会と日本の右傾化を、奇想天外な設定で風刺した。カッティングのセンスが喜八タッチの健在を示した。脚本と主演の利重剛は自主映画出身で、PFF1981入選者である。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FILLE AUX YEUX D’OR", "金色の眼の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "アルビコッコ監督のデビュー作。写真家が、金色に輝く瞳を持った神秘的な娘に出会って恋に落ちるロマンティックなドラマ。当時、M・ラフォレと結婚寸前の仲だったアルビコッコ監督が、彼女の美しさを耽美的映像で引き出している。", "1961-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("棒の哀しみ", "棒の哀しみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "組のために８年をムショで過ごしたこともある田中は、サラリーマン風の暴力団幹部。二人の情婦を囲い、素人娘をシャブ漬けにして売り飛ばすチンピラ中年だ。冴えないシノギにすっかり飽き、抗争や跡目争いに乗じて成り上がることを思わないではないが、実はそう本気でもない……。「ベッドタイムアイズ」に続く神代＆奥田のコンビ作。長回し撮影を多用しつつ、堕ちていく男の悲哀をハードボイルド・タッチで描いている。翌年、世を去った神代にとって本作が遺作となった。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Smultronstallet", "野いちご", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "スウェーデンの巨匠イングマール・ベルイマンが、ひとりの老人の1日を通して人生のむなしさや孤独をつづり、ベルリン国際映画祭金熊賞をはじめ数々の映画賞に輝いた傑作ドラマ。名誉博士号を授与されることになった老教授が車で授与式場へと向かう道のりを、老教授の回想や悪夢を織り交ぜながら描いていく。老教授を演じるのはサイレント期の名監督として知られるビクトル・シェストレムで、本作が遺作となった。2012年、デジタルリマスター版でリバイバル公開。", "1957-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La proie", "プレイ　獲物", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "", "2012-06-30", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AMITYVILLE HORROR", "悪魔の棲む家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "マイケル・ベイ率いる製作会社プラチナムデューンズが2003年の「テキサス・チェーンソー」に続き、往年の人気ホラーをリメイク。70年代に起こった有名な実話を基に、幽霊屋敷に引っ越してきた一家を襲う惨劇を描く。CGや音響効果が生み出すショック・シーンの数々は、ハリウッド・メジャー作品ならではのド迫力！　念願の一軒家を手に入れた夫婦ジョージとキャシー。しかしその邸宅には悪霊が宿っており、次第にジョージの精神を蝕んでいく。", "2006-01-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Letter", "月光の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "東南アジアでゴム園を経営する男の妻が、一人の男を射殺した。しかし、彼女が男に書き送った手紙から、この犯罪が愛憎のもつれであったことが明らかになる……。S・モームの小説を映画化した犯罪メロドラマ。全編を彩るエキゾチックなムードに加え、ヒロインを演じるB・デイヴィスの魅力も際立つ。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRAXX", "私立警官トラックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ある日突然、故郷に舞い戻ってきた歴戦の勇士トラックスが、クッキー・ショップをオープンするために、町の悪を一掃しようとするコミカルなアクション映画。彼が作るクッキーが、実はとてつもなくまずいという設定も面白い。主演のS・スティーヴンスは、全米No.1のDJとして知られている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逆襲大蛇丸", "逆襲大蛇丸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "大谷友右衛門主演の「忍術児雷也」の後編にあたる忍術時代映画。前編で児雷也に退治されたかに見えた大蛇丸が逆襲に転じる。前編は萩原遼との共同監督であったが、後編は加藤泰の単独監督。若山富三郎が白ぬりの二枚目剣士役でデビュー。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pacific Rim", "パシフィック・リム（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-08-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAIR GAME", "フェア・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "スーパーモデルのC・クロフォードが映画初出演で主演を務めたアクション大作。舞台はマイアミ。やり手弁護士のケイトは、ジョギング中何者かが発砲した銃弾で左腕を負傷。事情聴取を担当した殺人課のマックス刑事は、偶然の発砲事件に巻き込まれたと判断するが、その夜、彼の目の前でケイトのアパートが爆発し、運河に投げ出された彼女に向かってマシンガンが乱射されるという事件が発生する。実はKGB残党のテロリスト集団が、自分たちの犯罪計画に彼女が邪魔で抹殺しようとしていたのだった。マックスとケイトは、敵の正体もわからぬまま孤立無援の逃避行を余儀なくされるが……。爆破、銃撃戦、カーチェイスと相次ぐアクションシーンに、シンディが体当たりで挑み、ヒロインを熱演。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLIRTING WITH DISASTER", "アメリカの災難", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ニューヨークの昆虫学者メルは、サンディエゴで暮らしているという生みの親を訪ねることにする。ところが妻のナンシー、生まれたてのベビー、養子縁組協会から派遣された美女ティナとともに現地に着いてみると、とんだ人違いだと判明。これを皮切りにメルと一行は、ミシガンからアンテロープへとアメリカ横断の旅を繰り広げるはめに……。これが長編第２作となる監督D・O・ラッセルのオフビートなセンスが光るロード・ムービー。ゲイのFBI捜査官が旅の道連れになったり、セックス絡みの珍トラブルが勃発したりと、先の読めない過剰なドラマ展開に、笑いを通り越して呆然とさせられてしまう。現代人の悩める姿を、痛烈かつあっけらかんと風刺したブラック・コメディ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALLONSANFAN", "アロンサンファン　気高い兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "1816年、ナポレオン没落後のイタリア王政復古時代。秘密結社のひとつ<気高い兄弟>に属すフルビオがようやく釈放された。農民を解放すべく南伊へ向う仲間たちは、彼の妹の密告により皆殺しに。アメリカへ逃亡せんとするフルビオの前に、生き延び、なおも革命に希望を託す青年アロンサンファンが……。タビアーニ兄弟が19世紀イタリアのある男の苦悩と悲劇を描く。", "1991-06-30", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAPE", "テープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "E・ホーク、U・サーマンが共演した心理劇。モーテルの一室だけに舞台を限定し、高校時代の友人同士である男女３人の会話が、意外な方向に白熱していく様をスリリングに描く。帰郷した若手映画監督ジョンが、旧友のヴィンセントに呼び出される。モーテルで再会した二人は高校時代のある出来事をめぐって対立。そこにヴィンセントの元恋人エイミーが現れて……。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("残侠", "残侠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "1930～40年代の京都を舞台にした任侠映画。若いうちから旅に出て男を磨き、戦時下は軍事物資運搬、戦後は闇市でしのぎを削って生きる岩城辰五郎の波乱の半生が熱く描かれる。流れ者の悪党を演じるビートたけし、一宿一飯の恩義から岩城のために死んでいく極道に扮した中井貴一ら豪華俳優陣が脇に回り、骨太な演技を見せる高嶋政宏を引き立てている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("森の石松", "森の石松", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ご存じ森の石松の物語を、吉村公三郎監督と脚本の新藤兼人が現代風喜劇にアレンジした作品。吉村公三郎としては初めての時代劇である。清水次郎長一家に見習いとして入ったものの、生来のドジぶりが災いして失敗ばかりしている石松の姿に焦点が当てられており、いわゆるチャンバラものや暴力ものとは違った味わいの石松ものとなっている。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花のお江戸の法界坊", "花のお江戸の法界坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "浅草観音の境内で勧進をする法界坊は、景気が悪く、大道商人や芸人と一緒に本所のハキダメ長屋の住人となる。だがある日、悪人から救った少女を長屋にかくまったことから大騒動が……。フランキー堺と伴淳三郎のコンビで描く時代喜劇。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Himalaya - l'enfance d'un chef", "キャラバン", "", "", "http://image.eiga.k-img.com/images/movie/1322/315x210.jpg?2238", 108, "北ネパールのドルポ地方。長年食料を得るためのキャラバンを率いてきた長老ティンレは、息子を事故で失った。幼い孫が指導者に相応しい年齢になるまで隊を率いると言う彼に対し、村の若者の間では有能な青年カルマを長老に推す声が高まっていた。そして次のキャラバンに発つ吉日が神託により決まるが、カルマは「嵐が来る」と吉日より前に多数の若者とヤクを連れて出発してしまう。", "2000-11-15", "2,66,3,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千羽鶴", "千羽鶴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "川端康成文学を吉村公三郎監督が映画化。若い茶の湯の師匠をめぐる女弟子たちの心理的葛藤を描く。叙情的なタッチで女の不思議な世界を現出させている。木暮・乙羽・杉村ら豪華な女優陣が見もの。1969年に増村保造監督で再映画化された。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("28 1/2 妄想の巨人", "28 1/2 妄想の巨人", "", "", "http://image.eiga.k-img.com/images/movie/55582/315x210.jpg?3549", 74, "鬼才・押井守監督が、初めて演出を手がけた舞台「鉄人28号」のメイキング映像と、「東京ゾンビ」の奥田恵梨香演じるカメラマンを主役にしたフィクションのドラマを融合させた意欲作。現場付きカメラマンの言子（あきこ）は、舞台の主人公・正太郎を思わせる少年の姿を、しばしば稽古場で目撃していた。一方、初の演出に意欲を燃やしていた映画監督の護（押井）は、夏を過ぎたころから情緒不安定気味になり、立ち稽古（げいこ）を前にこつ然と姿を消してしまう。", "2010-07-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("変身", "変身", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "『秘密』などが映画化されている人気作家、東野圭吾の同名小説を基にしたラブ・ストーリー。これがデビュー作となる新鋭、佐野智樹監督が、脳移植により別人に支配されていく男の苦悩と最愛の人への愛を丹念に描き出す。人格が次第に変化していく難役の主人公に扮した玉木宏の熱演も光る。脳に損傷を負い、脳移植により一命を取り留めた純一。体調も万全に戻り、以前の生活を取り戻す彼だが、次第に別の人格が現れ始める。", "2005-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOYOCHU SEXと代々木忠の世界", "YOYOCHU SEXと代々木忠の世界", "", "", "http://image.eiga.k-img.com/images/movie/55822/315x210.jpg?2979", 115, "80年代初頭のAV黎（れい）明期より数々のヒット作や問題作を世に送り出し、72歳になった2010年も新作を撮り続けているAV界の巨匠“ヨヨチュウ”こと代々木忠を追ったドキュメンタリー。その波乱に満ちた人生と作品世界を紹介するとともに、AV界の関係者や落語家の笑福亭鶴瓶、漫画家の槇村さとるら著名人へのインタビューを交えながら、彼の素顔と作品に込められたメッセージに迫る。田口トモロヲがナレーションを担当。", "2011-01-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOOG", "MOOG〈モーグ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "シンセサイザーの代名詞といっても過言ではない電子楽器“MOOG”を開発した、ロバート・モーグ博士の半生を通じて電子音楽の歴史を巡るドキュメンタリー。マッドサイエンティストなモーグ博士のキャラが愛らしい。キース・エマーソンやリック・ウェイクマンら、シンセの巨人も登場する。シンセサイザーを小型化し、音楽界に革命をもたらしたモーグ博士。14才の時に電子楽器を自作した、彼のシンセサイザーひと筋の人生をたどる。", "2005-02-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギャルバサラ　戦国時代は圏外です", "ギャルバサラ　戦国時代は圏外です", "", "", "http://image.eiga.k-img.com/images/movie/57082/315x210.jpg?4730", 109, "有村架純、竹富聖花ら注目の若手女優の共演で、戦国時代にタイムスリップしてしまった高校生たちの奮闘を描くオリジナル作。名古屋に住む女子高生あさみは、ある日突然、440年前の戦国時代へタイムスリップしてしまう。同級生のヒロ子を含め一緒にタイムスリップした仲間たちはそろって日本史が赤点で、歴史の知識が全く役に立たない。それでもあさみらは、持ち前のバイタリティで数々のピンチを乗り越えていく。名古屋が活動拠点の人気アイドルグループ「SKE48」の木崎ゆりあ、小木曽汐莉が映画初出演を果たすほか、「AKB48」の篠田麻里子が豊臣秀吉の妻・寧々役で出演。", "2011-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水の旅人 侍KIDS", "水の旅人 侍KIDS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "お椀の船に乗り、時空を越えて現代の東京に流れ着いた一寸法師のように小さい老侍と、ひ弱な少年が織り成す友情を描いた心温まるSFファンタジー。大林宣彦監督が環境問題や自然保護のテーマも盛り込んだみずみずしい作品だ。全編の80パーセントを占める、ハイビジョン映像を駆使した合成シーンが見もの。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かずら", "かずら", "", "", "http://image.eiga.k-img.com/images/movie/55072/315x210.jpg?1353", 94, "お笑いコンビ「さまぁ～ず」の三村マサカズが主演するコメディ。監督は「ぼくたちと駐在さんの700日戦争」の塚本連平。原案は小林信也のベストセラーエッセイ。若ハゲに悩むサラリーマン・森山（三村マサカズ）は、見知らぬ土地への転勤を機に、かつらを被ることを決意する。しかし想像以上にコストのかかるかつらに手の届かない森山は、格安の「大和田カツラ」のチラシを見つける。", "2010-01-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Good Girl", "グッド・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "人気TVシリーズ『フレンズ』のレイチェル役で知られるJ・アニストンが主演し、全米の批評家から絶賛を受けた１本。日常に不満を抱え込んだ30歳の主婦が、年下青年との不倫に救いを求めたことから、収拾のつかない事態に巻き込まれていく。いわゆる“女性応援映画”とは一味違い、“人生そんなにウマくはいかない”といった教訓を織り交ぜつつ、ヒロインの悪あがきがユーモアたっぷりに描き出される。なかなか自分の殻から抜け出せない妙にリアルなヒロインの姿は、おかしさと切なさを同時に味わわせてくれる。ディスカウント・ショップに勤める30歳の主婦ジャスティンは、仕事にも家庭にも嫌気がさし、諦めの境地で毎日をやり過ごしていた。しかし、同じ店でレジ係をする青年ホールデンとの出会いによって、彼女の生活に変化が起き始める。", "2002-01-01", "1,7,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L' affaire Farewell", "フェアウェル　さらば、哀しみのスパイ", "", "", "http://image.eiga.k-img.com/images/movie/55492/315x210.jpg?3145", 113, "1980年代初頭、ソ連のKGBに所属するグリゴリエフ大佐は、東西冷戦の敵側陣営であるフランスに接触。KGBの内部極秘情報を次々と受け渡していったが……。グリゴリエフのコードネームを冠した「フェアウェル事件」として知られる20世紀最大のスパイ事件を、「戦場のアリア」のクリスチャン・カリオン監督が映画化。「パパは、出張中！」「アンダーグラウンド」の巨匠エミール・クストリッツァが、役者として初主演を飾った。共演にギョーム・カネ、ウィレム・デフォー、アレクサンドラ・マリア・ララ。", "2010-07-31", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天上的恋人／SKY LOVERS", "天上の恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "中国の山村を舞台に、障害を抱えるも大らかに生きる二人のピュアな心の交流を描いた純愛ドラマ。「山の郵便配達」の若手注目株リュウ・イエ、「至福のとき」で主演デビューしたドン・ジエの瑞々しい存在感が、清らかな印象を与える。幼い頃に聴覚を失ったチャークァンは、盲目の父と二人暮らし。そこへ口のきけない少女ユイチェンが一緒に暮らすことに。兄妹のような二人だったが、ユイチェンは自分の恋心に気づく。", "2006-06-10", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD HEAT ON A MERRY−GO−ROUND", "現金作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "色男の天才詐欺師コッチが、次々と美女を口説き利用しながら、ソビエト首脳訪米で混乱するロサンゼルス空港の大金庫を狙う。軽快でハードボイルドなJ・コバーンのキャラクターを生かした、犯罪サスペンス・アクションの佳作。東海岸のボストンから西海岸のロスまで、アメリカ全土にわたるロケーションが美しい。H・フォードが端役で映画デビュー。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙戦士バルディオス", "宇宙戦士バルディオス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "打ち切りとなったTVシリーズに新作部分を加えて、劇場版に再編集したSFアクション。2350年のS-1星から、ブラックホールにのみこまれ、2100年の地球に漂着した若者マリン・レイガン。彼は亜空間から来襲するS-1星の侵略軍に対して、地球を守るブルー・フィクサー・チームの一員となって活躍する。巨大ロボットも登場するが愛憎の人間ドラマが基調となっている。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠狂四郎 勝負", "眠狂四郎 勝負", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "シリーズの実質的な出発点となった秀作。三隅研次の演出により風格ある仕上りをみせている。ある年の正月、狂四郎が偶然知り合った老人は幕政改革に燃える勘定奉行だった。彼は、将軍の子・高姫の御化粧代二万両を支給停止にするなどの政策で、幕府の特権階級やその取り巻きから命を狙われていた。老人の人柄に惹かれた狂四郎は、彼の命を守るため高姫の一味と斬り結ぶ。謎の女に操られた刺客たちとの決闘、柳生との御前試合など見せ場も数多い。また二八そば屋の呼び声や正月の風物などの江戸情緒が詩情を添える。狂四郎の人物像はより虚無的になり、もはや、いささか一徹な、かの老人にしか心を開かない。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHANGHAI　NOON", "シャンハイ・ヌーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "1881年、中国の紫禁城から王女ぺぺ姫が誘拐され、犯人は身代金として金貨10万枚を要求。アメリカのネバダ州カーソン・シティで取引が行われることになり、近衛兵のチョンは、王が直々に選んだ３人の屈強な兵士とともに渡米することになる。だが、列車強盗に襲われ、兵士たちとはぐれたチョンは仲間に裏切られた強盗団の一人ロイと旅を続けるハメになってしまう……。J・チェン主演、ハリウッド発のアクション・アドベンチャー。西部開拓時代のアメリカを舞台に、中国人とアメリカ人のコンビによるおもしろおかしい道中と、ジャッキーの華麗なアクションが繰り広げられる。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BATTLE OF THE VILLA FIORITA", "湖愁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "美人で貞淑だった人妻が、作曲家と恋に落ち、激しい情熱を燃やす。もうどうにもならないと悟った夫は二人をイタリアに行かせるが、それぞれの子供たちには納得のいかないことだった。二人が陶酔の日々を送るフィオリタ荘のまわりで、結託したそれぞれの子供たちによる奪還作戦が始まる。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　RUNDOWN", "ランダウン／ロッキング・ザ・アマゾン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "人気プロレス団体・WWEのレスラー、ザ・ロックが「スコーピオン・キング」に続いて主演した冒険活劇。彼が扮するのはタフな賞金稼ぎ、ベック。マフィアの息子を連れ戻すために南米に飛んだ彼は、秘宝を狙う白人の独裁者と対決することになる。そんなベックの活躍をエキサイティングな見せ場とともに描写。A・シュワルツェネッガーのカメオ出演にも注目だ。借金の取り立て屋べックは、レストランを開くのが夢。引退前の最後の仕事に、ギャングの息子トラビスを南米のジャングルから連れ戻す仕事を引き受ける。ところが探し当てたトラビスは、とんでもないトラブルメーカーだった。ビデオ、DVDタイトルは「ランダウン」。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A¨LSKA MEJ", "Love me", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "幼くして父と死別しアルコール中毒の母に見捨てられ、愛を求めてさまよう少女スーシー。肉親の愛情に飢え、里親をたらい回しにされる彼女の孤独な姿を、真正面から描いた秀作。A・リンデーンがシカゴ国際映画祭で主演女優賞を受賞。彼女はまた、スーシーと同じく里子の経験を持ち、“天才”と騒がれながらもこの一作で引退している。", "1986-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Yakuza's Daughter Never Cries", "ヤクザガール　二代目は10歳", "", "", "http://image.eiga.k-img.com/images/movie/56922/315x210.jpg?1558", 82, "「モンゴル」のセルゲイ・ボドロフ監督が、日本のヤクザ文化を題材に撮り上げたコメディ。暴力団組長の孫娘で、クマのぬいぐるみが手放せない可憐な10歳の少女ユリコは、身辺に危機が迫る祖父のすすめでイタリアへ留学することになる。しかし、搭乗した飛行機が悪天候で緊急着陸し、敵対する組織に追われたユリコは、ひとり見知らぬロシアの街へ。途中、海辺で倒れていたロシア人青年を助けたことから事態はさらに混迷し、敵味方が入り乱れた追走劇が始まる。主演は映画、ドラマ、CMで活躍する子役の荒川ちか。", "2011-10-22", "24,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOMMAREN MED MONIKA", "不良少女モニカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ストックホルム市内の陶磁器店員ハリーは孤独な生活の中で、不良っぽい17歳の少女モニカと出会い恋に落ちる。やがてモニカは妊娠、出産するが、単調な貧乏暮らしに飽きた彼女は子供を置いて出て行ってしまう……。ベルイマンの特異な描写が鮮烈な印象を残す一編。", "1952-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHO IS HARRY KELLERMAN，AND WHY IS HESAYING THOSE TERRIBLE THINGS ABOUT ME？", "ケラーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "今は成功を収めてN.Y.の高級アパート住まいのロック作曲家ジョージ・ソロウェイ。だがこの地位や名誉の代わりに彼は愛する女性、子供、友人を失い、孤独な毎日だった。脳裏に浮かぶ謎の人物ハリー・ケラーマンを追って、現実と幻想の合間をさまようジョージ。D・ホフマンが「恋におちて」のU・グロスバード監督と組んだ問題作。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マライの虎", "マライの虎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "戦時中に創設された大映映画が「シンガポール総攻撃」現地ロケーションの副産物として製作した、戦争アクションの異色作。“マライの虎”と呼ばれ、当時日本中を沸かせた怪盗ハリマオの活躍を、伝説通りに描いた戦意高揚映画である。ハリマオの真実の姿は、のちに和田勉監督が「ハリマオ」で描いている。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泪壺", "泪壺", "", "", "http://image.eiga.k-img.com/images/movie/53412/315x210.jpg?2360", 110, "「失楽園」「愛の流刑地」の渡辺淳一による短編小説を原作に、ひとつの壺をめぐる男女の純愛を描いた官能ラブストーリー。自分に想いを寄せる朋代の気持ちに気付かないまま、彼女の妹である愁子と結婚した雄介。しかし癌を患った愁子は若くしてこの世を去ってしまう。雄介は愁子の遺言に従い、彼女の遺骨を使って美しい壺をつくる。それから数年後、雄介と朋代は偶然にも再会し……。テレビや映画で活躍する小島可奈子がヒロインを体当たりで演じる。", "2008-03-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Grace is Gone", "さよなら。いつかわかること", "", "", "http://image.eiga.k-img.com/images/movie/53402/315x210.jpg?2345", 85, "ホームセンターで働くスタンレーの妻は、兵士としてイラクに赴任している。2人の幼い娘たちと上手くコミュニケーションが取れず、ぎこちない生活を送っていたある日、彼の元に妻が死亡したとの報せが届く。娘たちに事実を言い出すことができず途方に暮れたスタンレーは、衝動的に2人を旅行に連れて行き……。ジョン・キューザックが主人公を好演するほか、クリント・イーストウッドが自身の監督作以外で初めて音楽を提供している。", "2008-04-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FALLING　FROM　GRACE", "スモールタウン・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "久し振りに故郷を訪れたカントリー・ロック・スター。その懐かしい田舎町で、かつての愛に溺れかかり、自分自身を見失いながらも、妻、そして家族への愛に再び目覚めるまでを描いた感動作。主人公のロック・スターを、アメリカの人気ミュージシャン、J・メレンキャンプが熱演する。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIAMO　DONNE", "われら女性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "イタリアの名匠たちが当時の人気女優を主役に描いた全５話から成るオムニバス。イングリッド・バーグマンが隣家の鶏を捕まえたことで起こる騒動を描くロベルト・ロッセリーニ監督作、アンナ・マニャーニが割増料金を請求するタクシーの運転手と攻防を繰り広げるルキノ・ヴィスコンティ監督作、イザ・ミランダが子供を産まずに女優に専念する道を選んだことで孤独を痛感するルイジ・ザンパ監督作など、興味深い作品がそろった。", "1953-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花と果実", "花と果実", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "青春ものを得意にする石坂の原作を得て作られた、女の子のピュアな青春映画。淡路島から上京し、大学に通う活発な女の子・のぶ子。恋人であるクラスメイトの五郎と、彼女は楽しい学生生活を過ごしていた。そんなある日、病床に伏していた母の容態が悪化したという知らせを受け里帰りをするが……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REBELOVE", "プラハ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "本国チェコでは20人にひとりは見たという恋愛ドラマ。チェコ国民ならば誰もが知っている'68年の“プラハの春”事件を題材に、若い男女の出会いと別れが描かれる。古き良きヨーロッパの街並みが残る、プラハの風景も見どころだ。理想の恋人探しに胸をときめかすテレザは列車で偶然出会ったシモンにひと目惚れ。一方、シモンも彼女に好意を抱く。だが、シモンはアメリカ亡命を望み、軍を脱走してきた兵士で……。", "2006-04-29", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花のお江戸の無責任", "花のお江戸の無責任", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "父親を闇討ちされた古屋助六は、仇討ちのために江戸へ向かうが、仇の目印は、背中の刀傷だけという心細さ。だが根っから陽性の助六は、そんなことは意に介さず、“早いとこパッパッと勝負をつけて、半年ばかり江戸で遊んでこよう”といたってゴキゲン。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　CONVOYEUR", "ブルー・レクイエム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "フランスの新鋭ニコラ・ブークリエフが、問題作「アレックス」のアルベール・デュポンテルを主演に撮り上げたフィルム・ノワール。最愛の息子を殺され、自身も傷を負った男の壮絶な復讐劇をスリリングに描く。現金輸送車の襲撃事件に巻き込まれ、最愛の息子を殺されたアレックス。頭に銃弾を浴びながらも、彼は奇跡的に一命を取り留めるが……。", "2005-05-14", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新道・前篇朱実の巻／後篇良太の巻", "新道・前篇朱実の巻／後篇良太の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "五所平之助としては、初めて長編小説を原作としたもので、彼自身によれば“もっと突っ込もうと思ってだんだん長くなってしまった”とか。当時としては、かなりの大作である。評価においても、通俗小説の映画としては最高のできと言われた。五所監督が、その後「大阪の宿」「お嬢さん乾杯」などでコンビを組む佐野周二を起用した最初の作品。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("潮騒", "潮騒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "三島由紀夫の古典的青春小説を、谷口千吉監督が久保明、青山京子主演で映画化した青春映画。伊勢湾の小島を舞台に、りりしい漁師の若者と、美しいアワビ採りの娘との運命的な結びつきが描かれる。当時流行していた性典ものや思春期ものとは、一線を画す格調ある青春もので、三島由紀夫の理想主義的な青春像を、谷口千吉監督がツボを押さえた演出で映像化した。主役の若い男女に扮する久保明と青山京子は、ともに初々しい演技で好感が持て、完倉泰一の現地ロケーションによるカメラも効果を上げた。三船敏郎が若い二人を見守る船長役で特別出演。なお、この原作は、1964年・1971年・1975年・1985年と５度映画化されている。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Outfit", "組織", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "R・スタークのベストセラー“悪党パーカー”シリーズの『犯罪組織』の映画化。兄を殺された男が出所後、一人で組織に復讐していく。冒頭からラスト・シーンまで、映画はスピーディーなテンポで走り抜ける。アクションに次ぐアクション。フリンの乾いた、しかも荒けずりな演出が全編に息つく暇を与えない緊迫感を生み出す。マクリンが出所すると兄はすでに殺され、愛人ベットも執拗な脅迫を受けていた。そして、自分まで狙われていることを知るや組織に敢然と戦いを挑んでいく。人間臭いヒーローをデュヴァルが好演。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chaw", "人喰猪、公民館襲撃す！", "", "", "http://image.eiga.k-img.com/images/movie/56932/315x210.jpg?4715", 121, "凶暴な巨大イノシシと人間たちの死闘を描いた韓国製モンスターパニック映画。ある日、都会から離れた平和な山村で、引き裂かれた惨殺死体が発見される。村人たちは恐れおののくが、事件の犯人は巨大な人食いイノシシだった。イノシシは次々と人間を襲い、村を混乱と恐怖のどん底に陥れていく。", "2011-10-22", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SINCE YOU WENT AWAY", "君去りし後", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 172, "1940年代を代表する映画スター競演による文芸大作。第二次大戦に夫を送り出したアンは、足りぬ男手を補うため、退役大佐スモレットを下宿させる。大佐の孫ビルは虚弱な青年で戦争を好まなかったが、彼はしばしばアンの家を訪れるうちにアンの娘ジェーンと親しくなる。しかし、そのビルもやがて戦地に赴き、ジェーンは従軍看護婦を希望。そしてアンの家族のもとには夫の行方不明の知らせが入る。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Girl Next Door", "隣の家の少女", "", "", "http://image.eiga.k-img.com/images/movie/55262/315x210.jpg?1416", 91, "「オフシーズン」「襲撃者の夜」など、カルト的人気を誇る米ホラー作家ジャック・ケッチャムによる、実在の少女監禁事件を基にした小説の映画化。1958年夏、少年デビッドの住む郊外の町に、美しいミーガンとスーザンの姉妹が引っ越してくる。交通事故で両親を亡くした姉妹は、デビッドの家の隣に住む伯母ルスの元に身を寄せることになり、デビッドと姉妹は徐々に親しくなっていく。やがてデビッドはミーガンに恋心を抱くが、ルスの嫉妬と憎悪の入り混じった虐待に、姉妹が苦しんでいることを知る。", "2010-03-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("方面俾／ DON’T GIVE A DAMN", "金城武 死角都市・香港", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "返還直前の香港を舞台に、巨大麻薬密売組織に立ち向かう刑事３人の友情を描いたアクション。中国への返還に揺れる香港の街では凶悪犯罪が横行し、裏では麻薬密売組織が暗躍していた。独自のルートで彼らを追うベテラン刑事二人と、イギリス帰りの若きエリート刑事は互いに対立を繰り返しながら、同じ敵を追ううちに友情が生まれる。そして彼らは巨悪組織に挑むが……。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAVEN’S　BURNING", "ヘヴンズ・バーニング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "工藤夕貴が「ヒマラヤ杉に降る雪」以前に主演したオーストラリア映画。「L.A.コンフィデンシャル」でブレイクする前のR・クロウが共演している。新婚旅行先のシドニーで忽然と姿を消した花嫁ミドリ。彼女は不倫相手と落ち合うため狂言誘拐をたくらんだが、結局男に裏切られてしまう。途方に暮れていたミドリは銀行で強盗に遭遇し、人質となるが、強盗の一味の運転手コリンに助けられ、行動をともにすることを決意。ところが、あてどのない旅を続ける二人を、笑い者にされ怒りに燃えるミドリの夫ユキオが追いかけてきた……。スキンヘッドの復讐鬼に変身するユキオ役の磯村憲司の怪演も見もの。", "1997-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TOWERING INFERNO", "タワーリング・インフェルノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 158, "サンフランシスコに138階建ての“グラス・タワー”が完成。落成パーティーの最中に81階の倉庫から出火、炎に包まれた超高層ビルはパニック状態に。ビルの設計者や消防隊長らは、思い切った救出作戦に出る。P・ニューマンとS・マックィーンの共演シーンでも有名な、豪華キャストによるパニック超大作。この作品の大ヒットにより、パニック映画ブームが頂点に達した。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("七瀬ふたたび", "七瀬ふたたび", "", "", "http://image.eiga.k-img.com/images/movie/55142/315x210.jpg?1490", 105, "これまで4度にわたってTVドラマ化されてきた筒井康隆の名作SF小説を、「シルク」の芦名星主演で初映画化。人の心を読むことのできる少女・七瀬は、自分の能力を他人に知られることを恐れ、各地を転々として暮らしてきた。やがて同じ悩みを持つ能力者たちと出会った彼女は、彼らと力を合わせ、能力者抹殺を企む強大な組織に立ち向かっていくが……。監督は「なぞの転校生」などSF作品の演出に定評のある小中和哉。", "2010-10-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HO＾TEL DES AME´RIQUES", "海辺のホテルにて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "冬の避暑地ビアリッツを舞台に、死んだ恋人の思い出に生きるエレーヌと彼女に惚れた孤独な男ジルの心の迷いや葛藤を静かに描く。翌年自殺したドベールとドヌーヴが好演。A・テシネ監督のメロドラマ演出にも味わいがある。夜が美しい良質のフランス映画。", "1981-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRAINSCAN", "ブレインスキャン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "高校生マイケルは“最高の恐怖。潜在意識と連動する、現実よりリアルなゲーム”というCD−ROMゲームソフト“ブレインスキャン”を手に入れた。ゲームを始めた瞬間、彼はバーチャル・ゾーンへ入り込み、残忍極まりない殺人を行う。仮想現実と呼ぶにはあまりにリアルな体験……。ところが、ニュースでその殺人が現実に起きたと知り、愕然とする。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("平成金融道・裁き人", "平成金融道・裁き人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "“借王〈シャッキング〉”シリーズの和泉聖治監督、夏樹陽子主演のコンビによる金融ドラマ。企業の倒産回避のプロフェッショナルが、悪の仕事人に立ち向かう。関西から上京した極悪３人組が、経営危機に陥ったホテルを乗っ取ろうと巧妙な罠を仕掛けた。ホテルの支配人から助けを求められた立花亜希子は、持ち前の行動力で調査に乗り出していく。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TILLSAMMANS", "エヴァとステファンとすてきな家族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "デビュー作「ショー・ミー・ラヴ」が世界中で絶賛されたスウェーデンのルーカス・ムーディソン監督作品。既存の制度や価値観に対抗し、自分たちで理想郷を作ろうと自由を求めて共同生活を送る1970年代のコミューンを舞台にした群像劇だ。ズームを強調した映像と、個性が強い18人もの登場人物を見事にまとめ上げる脚本で、家族のあり方を問う演出が光る。1975年、ストックホルム。夫婦ゲンカで家を出た母親に連れられ、エヴァとステファンは叔父の家へ行く。そこは個性豊かな人々が共同生活を送るコミューン。二人は習慣の違いに戸惑うが、次第に新しい生活に馴染んでいく。", "2000-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’MOUREUSE", "恋する女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "季節はずれの週末バカンスをノルマンディーの別荘で過ごそうとやって来た娘たち。グループの一人マリーと、アメリカ人青年の恋が成就するまでを中心に、それぞれの娘たちが恋に悩む姿を描いている。おしゃべりの断片をドキュメンタリー風に捉えた、1980年代フランスの恋愛のスケッチ画。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("弟切草〈おとぎりそう〉", "弟切草〈おとぎりそう〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "1992年に発売された人気ゲームソフトを映画化したホラー。ゲーム会社でバイトをしている奈美は、自分の出生の秘密を知るために亡き父が遺した洋館を訪れる。館を探索するうちに、奈美は双子の妹が存在したことを知る……。ミュージック・ビデオを中心に手掛けてきた下山天監督による凝った映像が見もの。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("21 Grams", "21グラム", "", "", "http://image.eiga.k-img.com/images/movie/1002/315x210.jpg?3698", 124, "余命1カ月と診断されて心臓移植の提供者を待つポール、夫と娘たちと幸福な生活を営むクリスティーナ、強い信仰心を持つ前科者ジャック。ある事件を契機に、3人の男女の運命が交錯していく。デビュー作「アモーレス・ペロス」で注目を浴びたメキシコの鬼才イニャリトゥ監督が、名優たちの共演で描く人間ドラマ。ベニチオ・デル・トロとナオミ・ワッツが、それぞれアカデミー賞にノミネート。", "2004-06-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火垂るの墓（2008）", "火垂るの墓（2008）", "", "", "http://image.eiga.k-img.com/images/movie/53562/315x210.jpg?2530", 100, "スタジオジブリによってアニメ映画化され日本中を涙で包んだ野坂昭如の名作小説を、「誰がために」の日向寺太郎監督が実写映画化。1945年6月の神戸。空襲で母を亡くし、出征中の父とも連絡が取れなくなった14歳の清太と幼い妹・節子は、遠い親戚の家に身を寄せることに。しかし2人を待ち受けていたのはおばさんからの非情な仕打ちだった。ついに耐え切れなくなった2人は、親戚宅を飛び出して防空壕の中で暮らし始める。", "2008-07-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Man on the Moon", "マン・オン・ザ・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/1832/315x210.jpg?2989", 119, "伝説のコメディアン、アンディ・カフマン。子供の頃からの夢だったコメディアンとしての道を選び、各地のコメディクラブを転々と営業して回っていた彼は、ある時、有名プロモーター・シャピロの目に留まる。アンディは人気の生コメディ番組「サタデー・ナイト・ライブ」へ出演、それをきっかけにスターへの仲間入りを果 たした。しかし彼は絶頂期に突然過激なパフォーマンスを始め、観客はもちろん、家族や親友さえも彼に理解を示せなくなっていく。", "2000-06-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bruno", "ブルーノ", "", "", "http://image.eiga.k-img.com/images/movie/54752/315x210.jpg?3964", 80, "問題作「ボラット／栄光ナル国家カザフスタンのためのアメリカ文化学習」でブレイクした人気コメディアンのサシャ・バロン・コーエンが、ブルーノというゲイのオーストリア人ファッション・レポーターに扮して大暴れするコメディ。ハリウッドでセレブになることを夢見るブルーノは、マドンナのように養子をもらったり、テロリストに誘拐されたりと、自分なりの“セレブになる方法“を実践していく。", "2010-03-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIGHTY DUCKS: CHAMPIONS", "飛べないアヒル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "弱小少年ホッケー・チームに新任コーチ、ゴードンが来た。彼は若手敏腕弁護士で、法廷でも試合でも勝つことだけを考える男。しかしひたむきな子供の姿にゴードンは、自分が選手だった頃の熱いハートを思い出す。人生には勝つことよりも大事な何かがあるのだ……。笑いと涙が詰まった痛快スポーツ・コメディ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女は夜化粧する", "女は夜化粧する", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "借金返済のために新劇女優の道を捨て、芸者になった道子。建設会社社長・橋田の尽力によって彼女はナイトクラブのママに。店も繁盛するが、そんなとき、かつて思いを馳せていた作曲家の阿久津がパリから帰ってくる。二人は互いに愛情を確かめ合うが、それは同時に阿久津の作曲家としての評判を下げることになっていく。泣ける仕上がりとなった大人のメロドラマ。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OMEGA SYNDROME", "オメガシンドローム 恐怖の暗殺団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「ワンダラーズ」「アパッチ砦・ブロンクス」で知られるK・ウォール主演のB級アクション映画。有色人種ばかりを狙う殺人集団“オメガ”に一人娘を誘拐されたベトナム帰りの新聞記者ジャックの命懸けの奪回を、壮絶なアクションの連続で描く。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("有りがたうさん", "有りがたうさん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "“有りがとうさん”は南伊豆を廻る定期バスの青年運転手である。狭い道をバスが通れるようにと道をゆずってくれる土地の人たちのみならず、牛や山羊や犬などの動物たちまでにも“有りがとう”と礼を言って走り去って行く青年に“有りがとうさん”というアダ名がついた。今日もバスには若い娘を売りにいく老母、水商売の女、怪しげな保険の勧誘員、金山かもわからぬ山で金を求めて採掘をし続ける男、狩猟家、行商人たちが乗っていた。そんな乗客たちと道往く人々の人生の点景が描かれる。原作は、川端康成の原稿用紙５枚ほどの短編。まるでヌーヴェル・ヴァーグの作品を見るかのごとき若々しくはつらつとした清水宏・珠玉の一編。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("M★A★S★H", "M★A★S★H　マッシュ", "", "", "http://ecx.images-amazon.com/images/I/41Ma1eWYZOL._SL210_.jpg", 116, "　1950年代、朝鮮戦争のさなか。第4077MASH（移動野戦外科病院）に、3人の外科医、ホークアイ、デューク、トラッパーが赴任。彼らはいずれも名医だったが、平気で軍規を無視し、とんでもない悪戯を繰り返す。やがてその行状を将軍が知るが……。軍医たちが巻き起こす騒動を描き、戦争や体制を風刺したブラック・コメディ。原作は実際に従軍医だったリチャード・フッカーの同名小説。カンヌ国際映画祭グランプリに輝き、アメリカン・ニューシネマの代表作として知られる傑作。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SILENCER", "アンジェリカ　狙撃者〈スナイパー〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "普通の女として生きられない宿命を負った、コードネーム“エンジェル”という女殺し屋アンジェリカ。彼女の愛と暴力の間で苦悩する日々を綴ったバイオレンス映画。主人公アンジェリカを演じるのはL・ウォールデン。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("源九郎義経", "源九郎義経", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "東映大衆時代劇路線の中核を担った松田定次の晩期に位置する作品。脚本はマキノ雅広監督の「清水港」「大暴れ五十三次」などで知られる小国英雄。奥州平泉に育った義経が、兄・頼朝の平家追討に加勢するため、故郷を出発するところから始まり、次々と武勲を立てたものの幕府内で冷遇されることになるまでの、若き日の姿を描いている。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小さき勇者たち　ガメラ", "小さき勇者たち　ガメラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「ガメラ」の生誕40周年を記念して制作された冒険ファンタジー。母親を事故で亡くした少年・透は、海辺で小さな卵を見つける。卵から生まれた亀をトトと名づけた透は、トトを可愛がることで母親のいない寂しさを紛らわせていた。ところが、トトは驚異的なスピードで成長していき、やがて空まで飛べるようになってしまう。そんなトトの姿を目撃した隣家の娘・麻衣は、かつて自らの命を犠牲にして人間を守った怪獣・ガメラとの関連を疑うが……。", "2006-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OBLIVION", "スペース・リザード3001　宇宙の極道蜥蜴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ヒューストン国際映画祭金賞に輝く、西部劇調のSFアクション。時は31世紀、発展途上の名もない開拓星のある町“オブリビオン”に、凶悪なトカゲ型宇宙人レッドアイがやって来る。レッドアイによって保安官は殺され、町は無法と化した。だが、保安官の息子ザックが、父の復讐に立ち上がり……。監督は、長年ブライアン・デ・パルマの助監督を務めた、S・アーヴィン。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLUE MAN", "ブルーマン 魂の彷徨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "自らの魂が肉体を離れて空中をさまようという不思議な感覚に悩まされる主人公ポール。ある日、奇妙な殺人事件に巻き込まれ、刑事に容疑をかけられてしまう。そこで自ら真犯人を捜し始めるのだが、事実は意外にも……。意表を突くストーリーが展開するオカルト・ホラー。", "1986-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("受験のシンデレラ", "受験のシンデレラ", "", "", "http://image.eiga.k-img.com/images/movie/53542/315x210.jpg?2542", 106, "大学受験のカリスマ講師・五十嵐は、友人の外科医から癌の宣告を受ける。余命1年半と知らされた彼は、帰りがけに偶然出会った少女・真紀が数学の才能を秘めていることを見抜き、家庭環境に恵まれず高校を中退した彼女を東大受験へと導いていく。受験アドバイザーとしても活動する精神科医・和田秀樹が、自らの体験を基に初監督を手掛け、第5回モナコ国際映画祭で最優秀作品賞をはじめとする4冠に輝いた人間ドラマ。", "2008-03-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEGEND OF VALENTINO", "バレンチノの伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1920年代、サイレント映画の大スターとして活躍したルドルフ・ヴァレンチノ。26歳の時に映画「黙示録の四騎士」で爆発的な人気を得ながら31歳で謎の死を遂げた彼が、女性脚本家ジューン・マティスと繰り広げる激しい恋を劇的に綴る。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A｀　LA　MODE", "ア・ラ・モード 誘惑の曲線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "悲劇を乗り越えて人生を切り開く少年の成功を、ポップなタッチで描いた青春ドラマ。突然の交通事故で家族を亡くした17歳のファウストは孤児院を脱走し、自分の人生を歩もうと決意。仕立て屋に弟子入りし、大胆なデザインの女性服を作り出した彼は、孤児院仲間の協力を得てPRに精を出し……。コインと紙幣で出来た服や、草のジャケットなど、ユニークなファッションも見どころ。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少年歌", "少年歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 27, "イメージフォーラムフェスティバル2002に入選経験を持つ新人、工藤里沙の劇場デビュー作。二人の少年の心のうつろいを、どこか懐かしいロケーションの中に浮かび上がらせる。母と二人暮らしの夢見がちな少年。ある日、彼はマジシャンと旅をするもう一人の少年と出会う。不思議な友情で結ばれた彼らは、賽銭泥棒で集めた５円玉を持ち、町を飛び出していくが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男の花道", "男の花道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "この頃、長谷川一夫とのコンビで「昨日消えた男」 「家光と彦左」「阿波の踊子」とヒット作を連打して、東宝のドル箱監督だったマキノ雅広が、「家光と彦左」で初共演した長谷川一夫と古川緑波を再び配して作った作品。題材は講談でもお馴染みの、中村歌右衛門と土生玄磧の物語で、長谷川を歌舞伎の女形に仕立てようと企画された。ストーリーは中村歌右衛門が失明を隠して舞台に上がっているのを、眼科医・土生玄磧が治し、それに恩義を感じた歌右衛門が今度は玄磧の窮地を救うというもの。長谷川は劇中、『紙治』や『保名』の舞台を見せ、映画は正月興行として大ヒットした。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ghost Ship", "ゴーストシップ", "", "", "http://image.eiga.k-img.com/images/movie/1402/315x210.jpg?4514", 91, "海難救助船アークティック・ウォリアー号の乗組員たちは、40年前に遭難した豪華客船を発見。その船を手に入れようと乗船した彼らを次々と異常な現象が襲う。製作は実はホラーおたくのロバート・ゼメキス率いるダーク・キャッスル・エンターテインメント。ウィリアム・キャッスル監督作のリメイク専門の製作プロだ。監督は同製作プロで「13ゴースト」を撮ったスティーブ・ベック。「インディ・ジョーンズ／最後の聖戦」「アビス」などの視覚効果アート・ディレクター出身。", "2003-01-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RELENTLESS ３", "処刑マニア３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ロサンゼルスで起こった連続殺人は、その残忍な手口のため、手がかりも皆無。さらには、敏腕刑事ディーツのところに、犯人からの挑戦状が送られてくる。ディーツの脳裏には、殺人鬼ポーターの顔が浮かび、さっそく、刑務所にいるはずのポーターを訪ねるが……。シリーズの顔となったL・ロッシが刑事ディーツを演じるシリーズ第３作。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Season of the Witch", "デビルクエスト", "", "", "http://image.eiga.k-img.com/images/movie/55962/315x210.jpg?1634", 95, "「60セカンズ」のニコラス・ケイジとドミニク・セナ監督が再タッグを組んだ歴史アクションホラー。14世紀、十字軍遠征からフランスに帰郷した騎士バーメン（ケイジ）は、ペスト（黒死病）の大流行により街が崩壊しているのを目の当たりにする。教会は、街にペストをもたらした魔女の嫌疑をかけられている少女を、悪魔払いが行われている遠方の聖堂に護送するため2人の騎士を雇う。共演はロン・パールマン、クリストファー・リー。", "2011-07-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("奈緒子", "奈緒子", "", "", "http://image.eiga.k-img.com/images/movie/53132/315x210.jpg?1597", 0, "「ビッグコミックスピリッツ」で連載され人気を博した同名コミックを上野樹里＆三浦春馬主演で映画化した青春ドラマ。喘息の療養のために長崎県波切島を訪れた12歳の奈緒子は、走ることが大好きな10歳の雄介と出会う。しかし海に落ちた奈緒子を助けようとした雄介の父が帰らぬ人となってしまい、2人とも心に大きな傷を負う。それから6年後、奈緒子は天才ランナーに成長した雄介と東京で再会を果たし……。監督は「ロボコン」の古厩智之。", "2008-02-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A GREAT WALL", "グレート・ウォール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "30年前に中国からアメリカに移住し、今はサンフランシスコのコンピュータ会社に勤めるレオ・ファン。彼は休暇をとり、妻のグレースと息子のポールを連れて久々に北京に里帰りする。北京には彼の妹夫妻とその娘リリが暮らしている。リリは大学受験を控えた美しい娘で、やはり受験生のリュウとつき合っているが、西洋文化への憧れは強い。お互いの再会を喜びながらも、レオ・ファンの家族にとっても妹夫妻の家族にとっても、長い間異なった文化で暮らしてきた意識の違いは大きかった。異文化が作り出す心の壁をホームドラマ風に描いたニューヨーク在住の中国人によるインディーズ作品。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハワイ・ミッドウェイ大海空戦 太平洋の嵐", "ハワイ・ミッドウェイ大海空戦 太平洋の嵐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "太平洋戦争におけるハワイ真珠湾奇襲からミッドウェイ海戦の日本軍敗退までを、パノラマ的に描いた特撮戦争大作。円谷英二の特撮は、のちにアメリカ映画「ミッドウェイ」にフィルムを流用されたほどのものだ。海中に沈んだ空母の中で三船と田崎の両軍人が語り合うラストは、僧侶でもある松林監督らしい出色の出来だ。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("３NINJAS KNUCKLE UP", "クロオビキッズ 夏休み決戦！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ロッキー、コルト、タムタムの３兄弟は、日本人のおじいちゃんから忍術を習い、幼いながらも空手の達人。ある日、得意の空手でならず者を倒し、少女ジョーを救った。だが、今度は彼女の父親がインディアンの土地に有毒廃棄物を捨てる悪徳業者ハーディングに拉致されてしまう。３人は誘拐されたジョーの父親を救うためにハーディングのアジトに侵入するのだが……。空手を習う３人のちびっ子が悪徳業者を相手に大暴れするアクション・アドベンチャー。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビルマ戦記", "ビルマ戦記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "昭和16年12月に開始されたビルマ・ルート攻略作戦での、マンダレー陥落までの数ヶ月に及ぶ激しい銃撃戦を追った話題作。イギリス軍の支配下に置かれ、苦しめられているビルマの佳境。そんな彼らの声援を受けながら進む、日本軍の姿や激しい銃撃戦の数々を克明に綴っている。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("(秘)色情めす市場", "(秘)色情めす市場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "華麗なる様式美の傑作「(秘)女郎責め地獄」を手掛けた田中登が、モノクロ＝ドキュメンタリー・タッチで一人の娼婦のしたたかな生き方を描く。釜ヶ崎の旧赤線地帯を舞台に展開する、19歳の貧しくも心優しい娼婦・トメと周囲の人々との交流。トメと客を取り合う母親、彼女に頼まれトメを脅すダメヤクザ、会社の金を使いこみヤクザと一緒に無理心中する男女、飛べないニワトリに絶望し首を吊るトメの白痴の弟、強盗殺人犯にそっくりの不思議な男。そんな人間関係を経て、トメはこの地で生き抜く決心をする。圧巻は、ラスト近くトメと結ばれた弟がニワトリを手に通天閣に登り、大阪の街々を展望するところ。ここから映画はカラーとなる。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lara Croft: Tomb Raider", "トゥームレイダー", "", "", "http://image.eiga.k-img.com/images/movie/1202/315x210.jpg?3341", 101, "全世界で販売本数が2400万本を超えるアクション・ゲーム・シリーズの映画化。トレジャー・ハンターのララ（アンジェリーナ・ジョリー）は失踪した父（ジョン・ボイド）の隠し部屋から謎めいた時計を発見する。それは5000年に1度の惑星直列でパワーを発揮する古代の秘宝への手がかりだった。", "2001-10-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　はらぺこヤマガミくん", "劇場版　はらぺこヤマガミくん", "", "", "http://image.eiga.k-img.com/images/movie/57712/315x210.jpg?2588", 79, "「電人ザボーガー」「片腕マシンガール」の井口昇監督と「ヘルドライバー」の西村喜廣監督が手がけた着ぐるみコメディ。自然破壊でエサがなくなり、人間を食べようと人里へ降りてきた山の神さま＝ヤマガミくんとその仲間たちが引き起こす騒動を描く。1話3分・全25話のうち20話を公式サイトやYouTube、ニコニコ動画で配信。劇場版はネットで未配信の5話を含めた全25話を一挙公開する。ナレーションにアイドルグループ「AKB48」の仲谷明香。", "2012-03-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("END　OF　THE　CENTURY", "END OF THE CENTURY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "パンクの祖といわれ、以後のロックンロールに多大な影響を与えたバンド、ラモーンズの内幕を明かしたドキュメンタリー。ロックに革命をもたらしながらもセールス的には恵まれず、成功とは無縁のポジションに追いやられ、メンバー間の亀裂も深刻化。にもかかわらず、彼らを20年間も突き動かし続けたのは何だったのか？　愛憎を赤裸々に語るメンバーの証言はファンにはショッキングだが、ロックへの愛情を抱き、疾走を続けた彼らの熱意は輝きを失わない。亡きメンバー３人の在りし日の姿も泣ける。", "2004-11-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("原子力戦争 LOST LOVE", "原子力戦争 LOST LOVE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "原発の町へ、失踪した情婦を探しにやって来たヤクザが、知らず知らずのうちに原発事故をめぐる汚職に巻き込まれ、命を狙われる。黒木和雄監督が、田原総一朗のノンフィクション・ノベルを原作に、原発問題に取り組んだ。途中挿入される実際の小名浜原発を撮影するスタッフたちのナマの姿はドラマ以上に迫力がある。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あぁ...閣議", "あぁ...閣議", "", "", "http://image.eiga.k-img.com/images/movie/57942/315x210.jpg?3876", 0, "2012年・第4回沖縄国際映画祭の長編プログラム「Laugh」部門にて上映。", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("棚の隅", "棚の隅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "直木賞作家・連城三紀彦の短編小説を、ベテラン俳優・大杉漣主演で映画化。小さなおもちゃ店を営む康雄の前に、8年前に自分と幼い息子・毅を捨てて蒸発した元妻・擁子が現われる。再婚して幸せな家庭を築いていた康雄だったが、頻繁に店を訪れるようになった擁子の行動に心が乱れてしまう。一方、毅への思いを抑えることができない擁子は、新しい恋人・進藤からのプロポーズを素直に受けいれることができずにいた。", "2007-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すずかけの散歩道", "すずかけの散歩道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "編集社に勤める久美子は、嫁いだ姉・信子の訪問を受け、単調な家庭生活にいや気がさしたと告白される。ある日彼女は編集長の家を訪ね、母親を亡くして寂しがっている子供たちの相手をして幸福を感じた。そんな日々のなか、信子と同僚の野崎が交際しているのを知って久美子は驚かされる。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEW JACK CITY", "ニュー・ジャック・シティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ニュー・ジャック・シティのドラッグ王ニーノは、若くしてハンサムなアメリカンドリームの体現者。しかしそのニーノの麻薬帝国を崩壊させるために、執念を燃やす二人のスラム育ちの黒人捜査官がいた。アメリカで封切り直後に大ヒットを記録しながら、各地で暴動が起きて上映中止の騒ぎにもなった話題のギャング映画。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The First Grader", "おじいさんと草原の小学校", "", "", "http://image.eiga.k-img.com/images/movie/56172/315x210.jpg?1999", 103, "イギリスの植民地支配から独立して39年がたった2003年、ケニア政府は無償教育制度を導入。何百人もの子どもたちで溢れる小学校に、84歳の老人マルゲが訪れる。いままで教育を受けたことがなかったマルゲは、何度門前払いを受けてもあきらめず、その情熱に動かされた若い教師ジェーンは、子どもたちと一緒にマルゲに勉強を教える。マルゲは学ぶことの楽しさを体験し、独立戦争の兵士として戦った若かりし日の悪夢がよみがっても勉強を続けるが……。「ブーリン家の姉妹」のジャスティン・チャドウィック監督が、実話をもとに手がけたヒューマンドラマ。", "2011-07-30", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blood Diamond", "ブラッド・ダイヤモンド", "", "", "http://image.eiga.k-img.com/images/movie/33912/315x210.jpg?3200", 143, "内戦下のアフリカで発見された巨大なピンク・ダイヤモンドを巡って繰り広げられる争いをサスペンスフルに描く社会派ドラマ。それぞれ異なる目的のためにダイヤの行方を追う3人の男女の運命が交錯する。冷酷なダイヤ密売人にレオナルド・ディカプリオ、ダイヤ採掘場での労働を強いられた漁師にジャイモン・フンスー、真実を求める女性ジャーナリストにジェニファー・コネリーが扮する。監督は「ラストサムライ」のエドワード・ズウィック。", "2007-04-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bonnie and Clyde", "俺たちに明日はない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "大不況の1930年代、テキサス州ダラスを中心に派手に暴れ回ったアベックの銀行ギャングの壮絶な青春物語。ベイティが製作を兼ねて主演した、アメリカン・ニューシネマの先駆として傑作の誉れ高い問題作。ボニーを演じたF・ダナウェイは一躍スターの座を築いている。ラストで、87発の銃弾を浴びて絶命する二人の姿が強烈な印象を残す。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火の鳥 鳳凰編", "火の鳥 鳳凰編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "生まれついて片目と片腕を失った我王が悪行の限りを尽くし、鳳凰を探し求めて旅する彫物師・茜丸の大切な右腕を斬ってしまう。やがてある事件がきっかけで改心した我王は、因縁ある茜丸と再会することに……。手塚治虫の同名漫画をりんたろう監督がアニメ化。大仏建立が行われた奈良時代を舞台に、対照的な道を歩む二人の男の人生が交錯する様を描き、人間の業の深さをあぶり出す。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猟銃", "猟銃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「わが愛」「白い牙」に続く五所平之助のいわゆる“井上靖・愛の３部作”にあたる。離婚した女は従妹の夫の愛人となる。ある日、別れた夫と再会し復縁を迫られた彼女は、戸惑いながらもそれを断る。やがてその前夫が他の女と結婚することを知り、女は自殺する。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("望郷", "望郷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "歴史の波にほんろうされながらも、たくましく生きる少年の姿を描き出した人間ドラマ。突然の破産宣告によって少年、操の家族は離散してしまった。ともに住み家を転々としていた姉は借金の形に売られ、妹は里子に。太平洋戦争の勃発で厳しさを増す世相の中、操は母や姉に会いたい一心で一人、放浪の旅を始める。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LISZTOMANIA", "リストマニア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "楽聖フランツ・リストの生涯をクラシックとロックのクロスオーバーと、ドタバタ喜劇で構成。特に、チャップリンのパロディー場面で、バックにリストの『愛の夢』が流れるセンスは秀逸。ザ・フーのR・ダルトリーがリストに扮している。", "1975-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Gunfighter", "拳銃王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "西部きっての早射ちの名人といわれ、その悪名が津々浦々まで届いていたジミー。そんなヤクザ稼業から足を洗い、別れた妻の許へ戻り静かな生活を過ごそうと道を急いでいた。途中、命を狙う様々なチンピラを倒し、妻の住む町に着いた彼を待っていたのは……。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Searching for Bobby Fischer", "ボビー・フィッシャーを探して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "わずか７歳にして天才の異名をとるチェスプレーヤー、ジョシュは世界一を目指して厳しい訓練を積む。だがある日、彼と同年代のもう一人の天才、ジョナサンが現れる……。チェスを通して家族の絆、人生の苦しみや喜びを描き、さわやかな感動を与える一作。ジョシュ役のM・ポメランツの初々しさも魅力的。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("英二", "英二", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "TVドラマ『とんぼ』で生まれた孤独のヒーロー、英二。長渕剛がライフワークともいうべき英二を演じたアウトロー・ムービー。演出は長渕剛主演の「オルゴール」で1989年に監督デビューを飾った黒土三男。10年前に姿を消した妹を捜して熊本を訪れた英二が悲しい運命を背負った中国人女性、梅花(メイホワ)と出会い、彼女をヤクザの村川組から助け出すが……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DALLAS", "ダラス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "南北戦争後のダラスの町を舞台に、G・クーパーを主演に描いた西部劇。無法者が横行するテキサスの田舎町ダラス。新保安官として、東部のボストン出身のマーチンが赴任して来た。彼は、町を牛耳るマーロウ兄弟に対抗するため、元南部軍の部隊長で今はお尋ね者となっているブライドを保安官に迎えるが……。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初一十五／THE MISSED DATE", "密会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "広告代理店に勤める夫は、日々仕事に忙殺されていて、美しい妻メイは面白くない。夫が自分に気をかけてくれないので暇つぶしにと出かけたテニス場で、優しいイップと出会い恋に落ちる。心は許しても身体は許さないメイだったが、夫の不倫現場を目撃して……。しゃれた会話が魅力の香港製ラブ・コメディ。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("流星語／THE　KID", "流星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "チャップリンの「キッド」を下敷にした人間ドラマ。株式投資に失敗したエリート証券マン、ウェイが捨て子の赤ん坊を発見。辞職した彼は赤子にミンと名づけ、自分の子供のように育てる。４年後、貧しいながらも楽しく暮らす彼らの前に、ミンの実の母親が出現。ウェイはミンを彼女に返すべきか悩み……。実の父子のような絆をドラマティックに浮き彫りにし、観る者の涙を誘う。", "1999-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGEL TOWN", "仁義なき戦い インL.A.—エンジェル・タウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "キックボクシングの元世界チャンピオン、O・グルナー主演のアクション映画。フランスからロサンゼルスに留学のためやって来たジャックが一帯を支配する不良グループに怒りの鉄拳を振るうというストーリー。監督はチャック・ノリスやジャン・クロード・ヴァン・ダムなど、グルナーの“先輩”たちを演出したE・カーソン。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SSSSSSNAKE", "怪奇！吸血人間スネーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "科学者ストーナーは、人類が生き延びるにはヘビに進化する以外にないという考えに取り憑かれ、娘の恋人で助手のデビッドにキング・コブラの血清を注射する。徐々にヘビ男に変身していくデビッドは……。“ヘビ人間”の恐怖を描いた珍作。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUT", "OUT", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "桐野夏生のベストセラー小説を映画化。主人公は、弁当工場の深夜パートで働く30歳から51歳の女性４人。夫はリストラ、息子は引きこもりで家庭崩壊寸前の雅子、痴呆の義母の介護に追われるヨシエ、買い物依存症の邦子、ギャンブル狂いの夫の暴力に苦しむ身重の弥生。ある日、弥生が衝動的に夫を殺してしまう。その死体処理を雅子が引き受けてしまったことから、平凡な主婦たちの人生が転がっていく。先行したTVドラマ＆原作と同様に、生活臭漂うリアルかつコミカルな描写とセリフを散りばめて軽快な印象にしている。４人の実力派女優のアンサンブルも心地よく、したたかで潔いウーマン・パワー全開の、疾走感あふれる娯楽作として楽しめる。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラーマーヤナ ラーマ王子伝説", "ラーマーヤナ ラーマ王子伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "インドでは知らぬ者のいない世界的な叙事詩『ラーマーヤナ』を、日本のスタッフが中心となってアニメ化。ラーマ王の若き日の逸話や英雄行為を、神話的な世界観の中に写し出す。深遠さより、単純明快なアクション・エンタテインメントとしての側面を強調。「ムトゥ　踊るマハラジャ」のヒットに便乗して公開が実現した。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("同級生", "同級生", "", "", "http://image.eiga.k-img.com/images/movie/53632/315x210.jpg?1342", 0, "競泳部のエースとして活躍していた高校3年生の潤は、夏のインターハイ予選でライバルの村井に負けてしまう。競泳部の監督である父親から受験勉強に専念するよう言われ気を落とす潤の元に、北海道在住で名前も誕生日も同じ“ジュン”という相手からメールが届き……。“JUNON恋愛小説大賞”の第1回優秀賞受賞作を「仮面ライダー電王」の中村優一主演で映画化。同じ主人公による別のストーリー「体育館ベイビー」も同日公開される。", "2008-05-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Husk", "ザ・フィールド", "", "", "http://image.eiga.k-img.com/images/movie/57582/315x210.jpg?3685", 83, "トウモロコシ畑を舞台に謎のクリーチャーに襲われる若者たちの恐怖を描いたシチュエーション・ホラー。ドライブを楽しんでいた5人の男女の乗った車が、突然発生したカラスの群れに巻き込まれて事故を起こしてしまう。生い茂るトウモロコシ畑に囲まれ人気もない土地で行き場を失った若者たちに、日暮れとともに得体の知れないクリーチャーが襲いかかる。次々と仲間が消えていく中、生き残った者は廃墟を見つけて逃げ込むが、さらなる絶望的な状況に追い込まれていく。", "2012-02-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("栗山大膳", "栗山大膳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "時代劇のベテラン・池田富保がメガホンを執ったオールスター・キャストによる大作。江戸幕府の外様大名取りつぶし政策が厳しさを増す頃、黒田家では主君・忠之が乱行を欲しいままにしていた。家老・栗山大膳は黒田家を断絶から救うため、忠之を幕府に告訴する。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("２００１：SPACE　TRAVESTY", "レスリー・ニールセンの2001年宇宙への旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「裸の銃を持つ男」シリーズのL・ニールセン主演によるSF映画のパロディ満載コメディ映画。連邦保安官リチャード・ディックスは行く先々で大騒動を引き起こしてしまうトラブルメーカー。そんな彼に与えられた今回の任務とは意外なほど重要な任務だった。米大統領がエイリアンに誘拐され、クローンとすり替えられたらしいのだ。彼は真相究明の為、エイリアンが定住する月基地へと向かうのだが……。", "2000-01-01", "10,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEEN WITCH", "魔女は16才〈シックスティーン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "夢みる普通の女の子が16歳の誕生日を迎えるや、魔法を使えるようになり、学校の人気者になってしまうファンタスティックな青春映画。主人公の少女にはR・ライブリー。Z・ルービンシュタインが、少女の相談相手になる占い師に扮している。劇場公開時は94分だったが、ビデオでは再編集され119分で発売された。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kung Fu Panda", "カンフー・パンダ", "", "", "http://image.eiga.k-img.com/images/movie/53362/315x210.jpg?4811", 92, "食いしん坊で怠け者のパンダのポーは、ひょんなことから伝説の“龍の戦士”として悪のカンフー・ウォリアーのタイ・ランと決闘する羽目になってしまい……。「シュレック」「マダガスカル」のドリームワークス・アニメーションによるカンフーアクションで、駄目パンダのポーが自分を信じて成長していく姿を描く。主人公ポー役のジャック・ブラックほか、ダスティン・ホフマン、アンジェリーナ・ジョリーらの豪華ボイスキャストも話題。", "2008-07-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("菜の花配達便", "菜の花配達便", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "小説・演劇・映画と幅広い分野で活躍を続ける、つかこうへい原作の小説『菜の花郵便局』を映像化。新任の警察署長の恋物語を軸に、彼と彼を見守る私設郵便局の人々との心の交流を描いた心温まる物語。警察学校を卒業したばかりの田草川裕二郎は、違法行為を続ける私設郵便局“菜の花配達便”を潰すために、つばめが丘署に赴任された。田草川は先に潜入している百合子刑事に、この郵便局を通じてラブレターを出すという調査を思いつく。だが、今まで一度もラブレターなど書いたことのない田草川は戸惑い、次第に本気で百合子を愛し始める。そんな彼を、菜の花配達便の人たちは心温かく見守り、打ち解け始めた彼がこの任務に疑問を持った時、一つの事件が起こる。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陽炎４", "陽炎４", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "任侠シリーズの第４作。高島礼子が第２、３作に続いて女胴師・不知火おりんを演じている。永田組と松岡組が覇を競うある地方の町。ひょんなことから永田組に世話になることになったおりんはお互いに腕を認め合う胴師、徳三と再会する。徳三は何とか二つの組の抗争を阻止しようと奮戦しており、おりんはそんな徳三に惹かれ、身も心も許してしまうのだが……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CITY’S EDGE", "逆光の中の青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "田舎町からシドニーにやって来た作家志望のアンディは、原住民や麻薬中毒といった落ちこぼれの住人だらけの安アパートへ入る。そこで彼は、ローラという女性に惹かれていくが……。大都会の片隅で生きる若者たちの、行き場のない青春を鮮烈に描いた社会派青春ドラマ。", "1983-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グローウィン・グローウィン", "グローウィン・グローウィン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "日本映画学校、映画美学校、日本大学芸術学部の学生を中心とした平均年齢22歳のスタッフが、35ミリ・フィルムで撮り上げた青春映画。殺人を犯した青年、公宣が茨城県の故郷に戻り、親友の純と再会する。そんな二人が“グローウィン・グローウィン”という団体が開催する集団自殺の会に参加するため、行き当たりばったりの自転車旅行を繰り広げる。自由を探し求める若者の心の叫びを真摯に描き出した作品。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌12　史上最大の有給休暇", "釣りバカ日誌12　史上最大の有給休暇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "人気シリーズとして定着した“釣りバカ”シリーズ通算第14作。鈴木建設の高野常務が、退職して田舎に帰ることに。社長のスーさんは、出張のついでに高野を訪ねようとハマちゃんを誘うが、ハマちゃんは有給休暇を使い果たしていて……。死にものぐるいで有給を取ろうとするハマちゃんのアプローチが爆笑もの。高野常務に青島幸雄が扮しコミカルな演技を披露すれば、高野常務のめい役を宮沢りえが好演し、作品に花を添える。前作に続き本木克英が監督を務め、笑いの中にも静かな感動と涙を誘う演出でシリーズの流れを変えた。また、これまで無かったのが不思議ともいえるシリーズのテーマ曲を本作で青島が作詞し、ハマちゃん役の西田敏行が歌った。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'Homme du train", "列車に乗った男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「仕立て屋の恋 」「橋の上の娘」のパトリス・ルコント監督が描く、全く異なった人生を歩んできた2人の男の友情物語。主演は「髪結いの亭主」のジャン・ロシュフォール、「ゴダールの探偵」のジョニー・アリディ。列車から降り、薬局に立ち寄ったミランは、そこで初老の男性、マネスキエと出会い、彼の家に立ち寄ることになる。2002年のベネチア映画祭観客賞、男優賞を受賞。", "", "2,7,3,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHEAT", "肉欲の報酬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "権力を手にして高貴な女性を誘惑し、相手に恥をかかせた方が勝利者になるという危険な賭けに興じていたヴィクターとルドルフ。やがてルドルフは、世間知らずで純情可憐な兄妹をひっかけ、彼らを破滅させてその罪をヴィクターになすりつけようとする。18世紀の城など、東欧に残されている風景が美しい、愛と欲を描いた官能的な作品。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VISIT TO A SMALL PLANET", "底抜け宇宙旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "おなじみ“底抜け”シリーズにエイリアンが登場。地球の人間の生活に興味を抱いた宇宙人クリートンが、地球へやって来て超能力を乱発、町中てんやわんやの大騒ぎに……。地球上の文化や風俗を、異星人の目を通して痛快に笑い飛ばすさまが圧巻だ。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い三度笠", "黒い三度笠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "関八州に出没し、弱者を助ける黒い三度笠、実は大前田英五郎。彼は道中で出会った新助から飯岡の助五郎と悪代官一味の悪事を知る。英五郎は新助の恋人おえんを訪ね、飯岡の宿に赴くが、助五郎たちの罠に落ちて燃えさかる小屋の中に。そこへ彼を慕う国定忠治らが駆けつけるのだが……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MALONE", "マローン 黒い標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "舞台は、オレゴンの大峡谷にはさまれた小さな町カムストック。そこは、謎の富豪デラニーの手段を選ばぬ土地収奪にあえぎ、ゴーストタウンと化していた。そんなある日、こつ然と一人の男が現れる。彼の名は、リチャード・マローン。精悍な風貌とタフな物腰、そして鍛え抜かれた身体。彼は、世界を股にかけて諜報活動を行ってきたCIAの特殊工作員だった。町を牛耳るデラニー一派の前に立ちはだかった彼は、デラニーの野望を打ち砕くべく敢然と戦いを挑んでいった……。B・レイノルズが奮闘するB級活劇。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWING　HIGH，SWING　LOW", "スイング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "C・ロンバード、F・マクマレイ主演のラブ・ストーリー。ナイトクラブのダンサー、マギーと結婚したトランペッター、スキッドが、武者修行のため単身ニューヨークへ。そこで一度は元恋人とよりを戻した彼が、自分の過ちを悔い改め、マギーとの愛を再確認するまでの紆余曲折が描かれる。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Confessions of a Shopaholic", "お買いもの中毒な私！", "", "", "http://image.eiga.k-img.com/images/movie/54442/315x210.jpg?3829", 105, "ソフィー・キンセラのベストセラー小説「レベッカのお買いもの日記」を、「ベスト・フレンズ・ウェディング」のP・J・ホーガンが映画化したロマンティック・コメディ。お買い物中毒の雑誌編集者レベッカが、経済誌の編集部に異動したことから巻き起こる騒動を描く。主演は「ウエディング・クラッシャーズ」のアイラ・フィッシャー。「セックス・アンド・ザ・シティ」の人気スタイリスト、パトリシア・フィールドが手掛けた衣装にも注目。", "2009-05-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLIGHT FROM JUSTICE", "フライト・フロム・ジャスティス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "空軍のエリート・パイロットにJ・レノが扮するサスペンス・アクション。フランス空軍のエリート戦闘機パイロット、チャーリー・ベール中尉のもとに、彼の古い親友であるマイケルが重傷を負ったとの連絡が入った。マイケルは誇り高いパイロットだったが、今は落ちぶれて密輸組織とかかわりを持つようになり、トラブルに巻き込まれたのだった。現場にはすでにマイケルの姿はなく、彼の安否を気遣うチャーリーにも危険な魔の手が迫っていた……。", "1993-01-01", "1,2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　FOOD　OF　THE　GODS", "巨大生物の島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "H・G・ウェルズの『神々の糧』を映画化したパニック・スリラー。鹿狩りのために孤島にやってきたハンターたちを襲うナゾの影。仲間を殺された彼らが、とある農場にたどり着くと、そこには巨大な鶏が。それは突然、この世に湧き出た、家畜の成長を促進させる飼料によるものだった。やがて、それによって異常繁殖した鼠の大群が人間たちに襲いかかり……。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANS LA VILLE BLANCHE", "白い町で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "白い町リスボンミの街角。貨物船の機関員ポールはふと立ち寄ったバーで、店の手伝いをするローザと出会う……。映画は、二人の親交と離反を、ポールとスイスに残してきた妻エルザの郵便のやりとりを交じえて描き出す。ポールが撮る白い町の８ミリは彼の心を映し出すかのようにうつろだ。", "1983-01-01", "26,25", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　魔法少女まどか☆マギカ　後編　永遠の物語", "劇場版　魔法少女まどか☆マギカ　後編　永遠の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "2011年に放送され話題となったTVアニメーション「魔法少女まどか☆マギカ」の総集編劇場版2部作の後編。", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博徒列伝", "博徒列伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "昭和初期、大木戸一家の若松組組長の若勇は、一家の跡目を弟分の川田に譲り、自ら川田の右腕となって働いていた。一方、監獄一家は若勇の縄張りを狙っている。一味の甘言に乗せられた川田は、やがて闇討ちに合う。怒り爆発の若勇は監獄一家に殴り込みをかける。任侠映画の一枚看板俳優総出演の大作。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸いろは祭", "江戸いろは祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "町火消しの粋なまとい持ち“め”組の半次を中心に、神明の鉄火芸者、御家人ヤクザ、悪旗本、力士などが入り乱れてドラマが展開するにぎやかな娯楽時代劇。二川文太郎、伊藤大輔などに師事した職人監督・内出好吉が手際よくさばいている。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("稲妻", "稲妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "林芙美子の同名小説を、田中澄江が脚色した成瀬巳喜男＝高峰秀子コンビの名作。４人の子供の父親がみな違うという複雑な母子家庭で、母と異父兄姉たちの醜さに愛想をつかした末娘が、家を出て自立する過程を物語の骨子としている。作者はすべての登場人物を肯定も否定もせずありのままに描いており、俳優たちの好演も相まって、類型に属さない生身の人間の存在感が鮮烈に迫ってくる。バスガイドとして働くヒロインを映した冒頭シーンでの車窓からのぞく風景をはじめ、東京という都市の息づかいを捉えている点でも出色で、娘の実家のある下町と新居の世田谷とは見事に描き分けられている。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グワシ！楳図かずおです", "グワシ！楳図かずおです", "", "", "http://image.eiga.k-img.com/images/movie/55022/315x210.jpg?3088", 91, "1955年のデビュー以来、「まことちゃん」「漂流教室」など数々のヒット作を生み出してきた漫画家・楳図かずおの素顔に迫ったドキュメンタリー。「楳図かずお公式サイト」の運営スタッフのひとりである伊藤弘二が監督し、楳図のプライベートを密着取材。さらに、イラストを仕上げていく姿を捉えた貴重な映像や、楳図自らが設計した自宅「楳図ハウス」の内部の様子、関係者による証言などを通し、楳図の魅力をあますところなく描いていく。", "2009-11-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOURGLASS", "ヒッチャー'95", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「ヒッチャー」で主演したC・T・ハウエルが監督・脚本・主演を務めた一編。若手青年実業家のマイケルは、地位も名誉も金も女も欲しいままにする野心家。そんな彼が、パーティーで神秘的な女ダラに遭遇する。一瞬にしてダラの虜になったマイケルは、仕事そっちのけでダラに溺れていく。そんな折、最近発生した殺人事件の嫌疑がマイケルにかかる。破滅の道へと転落するなかで、マイケルはダラの本当の素性と、彼女が仕組んだ恐るべき復讐劇の全容を知るが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗黒街の対決", "暗黒街の対決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "鬼才・岡本喜八が大ヒット作「独立愚連隊」に続いて、再びメガホンを取った“暗黒街”もの。緻密で大胆なカット割り、テンポの早い展開、黒づくめのギャングにコーラスを歌わせるというブラック・ユーモアなど、鬼才ぶりをいかんなく発揮している。またギャング映画のパロディーにとどまらず、善玉のヤクザが着流し姿で悪玉が背広姿といった、のちに任侠映画が作り上げるパターンや西部劇へのオマージュも楽しめる。舞台は暴力団抗争の激しい荒神市。暴力団大岡組と小塚組が、砂利採取の利権をめぐって争っていた。そこへ東京から藤丘刑事が来た。彼は汚職警官としてこの町に左遷させられたのだ。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON HER MAJESTY’S SECRET SERVICE", "女王陛下の007", "", "", "http://ecx.images-amazon.com/images/I/51raPYb1F6L._SL210_.jpg", 144, "初代ボンド役のショーン・コネリーの後任として、新人レーゼンビーを起用したシリーズ第６作。スイス・アルプスを舞台に、国際的犯罪組織の細菌工場を破壊するボンドの活躍が見もの。とりわけ、山の頂上から、ボンドがスキーで脱出する大アクション場面が迫力満点だ。", "1969-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ticker", "沈黙のテロリスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "爆弾魔と刑事の死闘が展開するアクション。テロ一味のリーダーが、逮捕された恋人の釈放を要求してシスコ市内に爆弾を仕掛けた。次々と惨事が起こるなか、刑事レイは爆弾処理のプロ、グラスとともに犯人を追う。爆弾処理エキスパートに扮したS・セガールは前半こそ出番は少ないが、クライマックスでは得意のマーシャルアーツで大奮闘。「スピード」に続いて爆弾魔を演じたD・ホッパーの怪演も見もの。ビデオ、DVDタイトルは「TICKER」。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SHOW", "ザ・ショウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ヒップホップ・ブームの立て役者となったレコード会社、デフ・ジャムの10周年を記念したドキュメンタリー。ノーティ・バイ・ネイチャーやRUN D.M.C.など、アメリカを代表するヒップホップ界のスターたちが総出演し、ライブはもちろんのこと、移動中やレコーディング風景なども追跡。生い立ち、ストリート生活、ラップに託したメッセージなど、貴重なインタビューも交え、彼らの本音に迫る。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE INNOCENTS", "回転", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "冬枯れた英国の旧家。幼い兄妹の家庭教師として雇われた若い女ギデンスは、屋敷の周りで黒衣の幽霊を目撃する。幽霊たちは兄妹の体を通じて、自らが地上で果たせなかった恋を語っていたのだ……。幽玄の世界を映像に移したオカルト映画の草分け的名作。", "1961-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TABU", "タブウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "今は失われた“南海映画”なるジャンルを代表する秀作。のどかな南太洋に生まれ、神のしもべとして選ばれた娘が戒律によって愛する青年と永遠に引き裂かれてしまうという悲恋物語。かつて「サンライズ」を全編セット撮影で華麗に撮り上げた巨匠ムルナウが、遺作にオールロケ、しかも現地の素人のみを使ってセミドキュメンタリーを作った驚き。絵のように美しい南洋の風物が、記録とフィクションの“結晶”となって結実している。共同監督に当たったR・フラハティは、「極北の怪異〈ナヌーク〉」などで知られる無声期のドキュメンタリーの大家であった。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OFF AND RUNNING", "マイアミ・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「バイブス　秘宝の謎」でコメディ・センスを高く評価されたシンディの主演第２作。女優としての成功を夢見ながらホテルのクラブで働く女の子シド。ある日、ボーイフレンドが金色に光る謎の鍵を残して何者かに殺される。やがて殺し屋の手は彼女にも及んで……。ファンキーなイメージを脱却した、“黒髪”のシンディに注目。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あがた森魚ややデラックス", "あがた森魚ややデラックス", "", "", "http://image.eiga.k-img.com/images/movie/54742/315x210.jpg?2268", 90, "1972年の大ヒット曲「赤色エレジー」で華々しいデビューを飾り、その後もシンガーソングライターのみならず詩人、俳優など様々なジャンルで活躍しているあがた森魚。2008年、たった1台のキャンピングカーに乗りこんで全国67カ所を巡ったライブツアーに完全密着し、その魅力に迫ったドキュメンタリー。「A」「A2」のドキュメンタリー作家・森達也監修のもと、長編デビュー作「半身反義」で高い評価を受けた新鋭・竹藤佳世が監督した。", "2009-10-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("British Rock - Ready for the 80s", "ブリティッシュ・パンク・インベンション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "1970年代後半に世界的ムーブメントを巻き起こした、イギリスのパンク／ニュー・ウェーブ・シーンに焦点を当て、アーティストたちの貴重なライブ映像やインタビューを収録したドキュメンタリー。セックス・ピストルズ、ザ・クラッシュはもちろん、ザ・スペシャルズ、マッドネスといったスカ・バンドや大御所ザ・キンクスまで、幅広いアーティストを取り上げている。監督は「パンク・イン・ロンドン」のボルフガング・ビュルト。", "1980-01-01", "45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホームレス・ワールドカップ", "ホームレス・ワールドカップ", "", "", "http://image.eiga.k-img.com/images/movie/55352/315x210.jpg?1535", 99, "2006年にケープタウンで行われた、ホームレスの人々のみが参加できるサッカーの国際大会「ホームレス・ワールドカップ」に出場した、ある7人の選手を追ったドキュメンタリー。アフガニスタンの戦場地からナジブ、ケニアのスラム街出身のアレックス、アイルランドの薬物リハビリ施設からダミアンとサイモン、ロシアの不法入国者スラバなど、生活にさまざまな問題を抱える7人が、サッカーを通して人生に希望を見出していく。コリン・ファレルがナレーションを務める。", "2010-05-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("汗", "汗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "巨匠・内田吐夢が、無声映画時代に演出した中編喜劇。退屈な日々を送っていた金持ちが、ふとしたことからルンペンと着物を取り換える。労働者としての生活を送ることになった彼は、初めて汗を流して働くことの意義を知るというもの。主演はのちの名監督・島耕二。", "1929-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Savages", "マイ・ライフ、マイ・ファミリー", "", "", "http://ecx.images-amazon.com/images/I/51xijQ3H23L._SL210_.jpg", 113, "「Fカップの憂鬱」（98）のタマラ・ジェンキンス監督がローラ・リニー、フィリップ・シーモア・ホフマンを主演に描く家族をテーマにした悲喜劇。ニューヨーク・バッファローに住む大学教授のジョン・サベージ（ホフマン）とニューヨークシティ在住の劇作家ウェンディ・サベージ（リニー）は、ともに未婚の兄妹。お互い独り気ままに暮らしている2人だが、ある日、長らく会うのを避けていたアリゾナ在住の父親から病の知らせを受ける。2人は仕方なくアリゾナまで出向くが、すでに父親の痴呆が始まっていた……。", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NUDE BOMB", "それ行けスマート 世界一の無責任スパイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "人気TVシリーズ『それ行けスマート』の映画版。米国情報部員“0086号”ことマクスウェル・スマートが、ヌード爆弾で、世界征服を企む謎の巨大組織KAOS相手に、ドジを踏みながらも大奮闘するパロディー。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents すき　「春まで十日間」", "桃まつり presents すき　「春まで十日間」", "", "", "http://image.eiga.k-img.com/images/movie/57882/315x210.jpg?1716", 13, "若手女性監督による映画製作・上映集団「桃まつり」が、「好き」をテーマに女性監督たちが撮り上げた短編作品を上映する「桃まつり presents すき」の1本。2011年3月11日、東日本大震災が発生した折に、アムステルダム在住の若い日本人女性が故郷の母親を心配してなんとか連絡をとろうとする姿を描く。監督は日本に留学経験もあり、今回が初監督のオランダ人ステファニー・コルク。「弐のすき」として「口腔盗聴器」（上原三由樹監督）、「最後のタンゴ」（熊谷まどか監督）と同時上映。", "2012-03-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タラウマラの村々にて", "タラウマラの村々にて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "国際協力のための若い人の人材育成の場、NGO風の学校。そこの井戸掘り専門家、石田恵慈は、井戸掘りの調査のためメキシコ、チワワ州のタラウマラ先住民族の住む山岳地帯に入っていく……。標高2300メートルの山中に点在するタラウマラの村々で、石田恵慈が現地の人々とともに井戸掘り作業をする姿、そして彼らとの心温まる交流をとらえたドキュメンタリー。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COUPE DE VILLE", "キャデラック　俺たちの1,000マイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "故郷を離れ、それぞれの生活を送っている３人兄弟が、父の呼びかけで再会した。まじめな空軍兵士の長男マーヴィン、大学を卒業したばかりの次男バディ、マーヴィンとウマの合わないはみ出し者の末っ子ボビー。父は３人に対し、母親の誕生日に、かねてから欲しがっていたクラシックなキャデラックを傷つけず届けるように命令を出す。ミシガンから故郷フロリダまで３人の珍道中が始まった。マーヴィン役は「ホーム・アローン」のドジな泥棒マーブを演じたD・スターンが扮し、末っ子ボビーを「モブスターズ・青春の群像」で注目されたP・デンプシーが演じている。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TSUGARU・津軽", "TSUGARU・津軽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ぴあフィルムフェスティバルで長谷川和彦に絶賛された「幻影肢」の監督・五十嵐匠が津軽方言詩人・高木恭造の詩集『まるめろ』をもとに作り上げた16ミリの自主製作作品。40年ぶりに故郷津軽を訪れた男の魂の彷徨を幻想的に綴っている。主演は、名バイプレーヤー、室田日出男と全盲の新人、川島昭恵。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BELPHE´GOR：LE　FANTO＾ME　DU　LOUVRE", "ルーヴルの怪人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "フランスに古くから伝わるルーヴル美術館の呪いの伝説を映画化したスリラー。ルーヴル美術館の地下収蔵庫から謎のミイラが発見された。その日から、館内では不可解な現象が続発。美術館の向かいに住む美女リザは、ひょんなことから美術館に迷い込み、以来、彼女の身に変化が起き始める……。何かに取り憑かれていくヒロインにS・マルソーが扮し、ミステリアスな官能美を放つ。劇映画初のルーヴル美術館での長期ロケを敢行したことでも話題となった。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喧嘩笠", "喧嘩笠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "清水の次郎長の力を借りて許嫁の父の仇を討つ大前田栄次郎の活躍を描く娯楽時代劇。“小天狗の若親分”こと栄次郎は縄張り争いで殺されたお喜代の父の無念を晴らそうと仇のあとを追うが、そこで次郎長一家と仇を間違えてしまう。清水の次郎長には大友柳太朗が扮している。大川橋蔵の若々しさが見もの。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒナゴン", "ヒナゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "70年代に広島県比婆郡で実際に起きた珍獣発見騒動を基に、人気作家、重松清が書き下ろした『いとしのヒナゴン』を映画化。謎の生物の目撃情報で沸く町で巻き起こる大騒動がコミカルに綴られる。原作の魅力を存分に引き出した「ぷりてぃ・ウーマン」の渡邊孝好監督の手腕も見事だ。かつてヒナゴンという未確認生物の目撃が相次いだ広島の比奈町。その町役場に30年ぶりにヒナゴンの目撃情報が入り、町は大騒ぎになるが……。", "2005-07-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DEATH OF STALINISM IN BOHEMIA", "スターリン主義の死", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 11, "世界的に名高いアニメーション作家J・シュヴァンクマイエルの短編。粘土で出来たスターリンの顔がメスで切り刻まれ、そこからゴドワルドが出てくる。ゴドワルドは、1948年のクーデターで大統領になったチェコスロバキア共産党書記局長だ。ゴドワルドは、頭を叩かれた瞬間、“労働者の大統領が誕生した”という。しかし、スターリンとゴドワルドは骸骨に食いちぎられてしまう。次には、トウモロコシの中からフルシチョフの顔が現れるが、のし棒につぶされ、骸骨に食いちぎられてしまう。次に、出てくるのは誰か？　DVDは「ヤン・シュヴァンクマイエル　短編集」に収録。", "1989-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("折鶴さんど笠", "折鶴さんど笠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "闇討ちにあった父の仇を討つために旅に出た折鶴の秋太郎は、５年ぶりに故郷・潮来に帰ってくる。そこでは、仇の猪之松は中風で寝たきりとなり、後を継いだ重三郎が悪事の限りをつくしていた。秋太郎が旅の途中に出会った娘、お美津に重三郎が目を付けたため、秋太郎は重三郎に立ち向かうが……。当時宝塚を退団し、松竹の娘役で人気を集めた、伊吹友木子が、秋太郎を想うお美津役で出演。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOT SHOTS！", "ホット・ショット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "トム・クルーズの「トップ・ガン」を徹底的にパロディ化、主役にはC・シーンを起用した壮大なスカイ・アクション・パロディ。「フライング・ハイ」「殺したい女」などで共同監督を担当したエイブラハムズが、そのコメディ・センスを十二分に見せつける。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WATERLAND", "秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "高校で歴史を教えるトムは、反抗的な教え子マシューと向き合うことで、思いがけなく目を背け続けた過去を思い出す。第二次世界大戦下、トムは幼なじみのメアリーと若い性をむさぼりあっていたが、知的障害を持つトムの兄ディックもまた、メアリーに恋心を抱いていた。そして、純粋すぎるディックの思いは、隠された秘密を暴き出すのだった。", "1992-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アタゴオルは猫の森", "アタゴオルは猫の森", "", "", "http://image.eiga.k-img.com/images/movie/33692/315x210.jpg?4682", 81, "ますむらひろし原作のロングセラー・コミックを3DCGアニメで映画化。年に1度の祭りで賑わうアタゴオル。トラブルメイカーのデブ猫ヒデヨシは、大好物の紅マグロに目が眩んで封印の扉を開けてしまう。中から出てきたのは、世界征服を企む植物女王ピレアだった……。ファンタジックなアタゴオルの世界をCGで描き出したのは、CGアニメ「アップルシード」や「デスノート」の死神CGで高い評価を得たクリエイター集団、デジタル・フロンティア。", "2006-10-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("荊軻刺秦王", "始皇帝暗殺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 166, "歴史上はじめて中国の統一に成功した、始皇帝の暗殺にまつわるエピソードを、ふたりの男とひとりの女の愛憎劇を絡めて描く壮大な歴史ドラマ。後に始皇帝となる政と趙姫、心に傷を持つ暗殺者・荊軻の交錯する心情を軸に、運命の皮肉という・チェンカイコー監督ならではのテーマが、重厚に浮かび上がる。伝説の宮殿、威陽宮を再現した巨大セットや騎馬隊の戦闘など、壮大なスペクタクルシーンも見どころのひとつ。", "1998-01-01", "8,2,1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HURRICANE", "ハリケーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "南太平洋のある島では情け容赦ない総督が島民を労役としてこき使っている。島の青年テランギはそれに反抗的な態度をとったことで、捕らえられてしまう。恋人に会いに行こうとして脱獄した彼のもとには総督の手が伸びてくるが、ちょうどその時、島にはかつてない大型の台風が近づいていた。その中で青年は決死の活躍で島民を救い、恋人である酋長の娘と誰の手も届かない場所へ旅立っていく。台風来襲の空前のスペクタクル・シーンは迫力十分。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES BOIR NOIRS", "ボワ・ノワール 魅惑の館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "人生に希望を失くしかけていたヴィオレットは、地方貴族のグスタフと知り合い、やがて結婚により幸福をつかみかけたかのように思えた。しかしグスタフの正体は冷酷な支配者であり、時に取り残された“暗黒の森”の中、ヴィオレットはついに彼と衝突する。J・ドレー監督による倒錯的ラブ・サスペンス。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GLINTOCCABILI", "明日よさらば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "数々の強盗をはたらいた伝説的なギャング、マッケインが釈放される。迎えにきたのは息子のジャックで、保釈金はラスベガスのカジノを襲撃し、100万ドルを手に入れる計画だという。マッケインはさっそくラスベガスに事前調査に出かけるが、実はジャックは、組織によって操られていたのだった。若き日のJ・カサヴェテス主演のイタリア産アクション。", "1969-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PORKY’S", "ポーキーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "公開時大ヒットを記録した、お色気タップリのドタバタ・コメディの代表作。1950年代の南フロリダ、エンジェル・ビーチ高校。寝ても覚めても“セックス”のことしか頭にない高校生たちの日常を、おおらかに描く。バックに流れる1950年代のヒット曲オンパレードがうれしい。", "1981-01-01", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・陽気な未亡人", "喜劇・陽気な未亡人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "フランキー堺が一人６役を演じるコメディ。アパート経営の啓子、野菜の移動販売をする伸枝、保険の外交員・くめなど、未亡人たちのたくましい生活力を名匠・豊田四郎が軽快なフットワークで演出した。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("剣は知っていた　紅顔無双流", "剣は知っていた　紅顔無双流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "東京新聞に連載された柴田錬三郎の小説を映画化した痛快時代劇。豊臣天下前夜の戦国時代を背景に中村錦之助扮する美男剣士、眉殿喬之介の端麗な活躍を描く。武士として真に歩むべき道を模索していた喬之助の前に男装の令嬢が現れる。彼女こそは徳川家康の娘、あやであった。父母の敵を討つ野心に燃えながら喬之助はあやを嫁にとる決意を家康に告げるが……。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あらしのよるに", "あらしのよるに", "", "", "http://ecx.images-amazon.com/images/I/519WY3Z7WGL._SL210_.jpg", 107, "シリーズ累計220万部を超えるベストセラーとなった、人気の絵本を映像化した心温まるファンタジー。オオカミとヤギ、食うもの食われるものの間に芽生える友情がユーモラスかつ感動的に描かれる。原作から離れた柔らかみのある愛らしいキャラクターや、中村獅童と成宮寛貴ら声優陣の好演にも注目。嵐を逃れた山小屋の暗闇で出会い、心を通わせるオオカミのガブとヤギのメイ。彼らの友情は、それぞれの種族に波紋を投げかけることに……。", "2005-12-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火線地帯", "火線地帯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "異才、石井輝男が手掛けたセミ・ドキュメンタリータッチの犯罪ドラマシリーズの１本。重宗商事と梶川組の抗争のさなか、チンピラの伸一と健次は重宗の親分に度量を買われて仕事を任される。それは梶川組が密売で競り落とした拳銃を強奪するというもの。難なく拳銃を手に入れたものの、彼らの行く手には思わぬ落とし穴が待ち受けていた。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("師姐出馬／THE BLONDE FURY", "香港レディ・レポーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "13才で空手を始め、1981年に全米女子空手チャンピオンの座を獲得したC・ラスロックは、香港映画界でデビュー。その時代の出演作で、FBIの腕利き女捜査官に扮する。ラスロックのマーシャル・アーツ対香港カンフーのアクション・シーンが見もの。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHILD IN THE NIGHT", "ナイトチャイルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「わが心のボルチモア」で、祖父と孫の心のつながりを好演した子役、E・ウッド主演のサスペンス。８歳の少年ルークは、目の前で父親を殺され、心を閉ざしてしまった。バース刑事は、唯一の目撃者であるルークの記憶を取り戻そうと、精神科医ジャッキーに依頼する。次第に心を開くルーク少年であったが、その間にもさらに３人の人間が殺されてしまう。魔の手は少年とジャッキーにものびてきて……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shooting Dogs", "ルワンダの涙", "", "", "http://image.eiga.k-img.com/images/movie/33822/315x210.jpg?4797", 115, "1994年にアフリカのルワンダ共和国で起こった民族大虐殺を、現場を目の当たりにした外国人たちの視点で捉えた社会派ドラマ。当時、現地を取材していたイギリス国営放送BBCの記者デビッド・ベルトンの体験を基に、「メンフィス・ベル」のマイケル・ケイトン＝ジョーンズ監督がメガホンを取る。国連さえも目を背けた虐殺の真相と、極限状態の中で究極の選択を迫られる人々の姿を生々しく描き出す。出演はイギリスの名優ジョン・ハートほか。", "2007-01-27", "3,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AMERICAN NIGHTMARE", "アメリカン・ナイトメア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "1960～70年代のホラー映画の傑作に反映されていた社会性を検証するドキュメンタリー。「ナイト・オブ・ザ・リビングデッド」における公民権運動、「悪魔のいけにえ」におけるベトナム戦争、「ラビッド」の性の解放など社会の動きを反映した描写を、それぞれの監督が解説する。夢や希望が打ち砕かれた1970年代当時の虚無的な風潮が恐怖映画に反映されていた、という視点が興味深い。", "2000-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEENAGE MUTANT NINJA TURTLES III", "ミュータント・ニンジャ・タートルズ3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "“忍者ガメ”が活躍する実写映画化シリーズ第３作。不思議な骨董品の笏（しゃく）に秘められた魔法で、戦国時代の日本へ飛ばされてしまったエイプリル。彼女を助けるべく、我らが忍者ガメ４人組（レオナルド、ラファエロ、ドナテルロ、ミケランジェロ）もタイムスリップ。南蛮の武器商人たちに操られた悪大名と、痛快な大チャンバラ合戦を繰り広げる。“映画の中のおかしな日本人”も楽しめる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バタアシ金魚", "バタアシ金魚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「三月」「田舎の法則」などの８ミリ映画がPFFに入選し、一躍注目された松岡錠司が望月峯太郎の同名人気コミックを原作に、商業映画を初監督した話題作。周囲の迷惑お構いなしの自信過剰高校生・カオルと、彼に一目惚れされ、一方的な愛の押しつけに心身ともに疲労困憊しつつも反撃に出る少女・ソノコが繰り広げる愛と戦いの青春映画。夏の緑と青を眩しく映す美しい映像の中、カオル役のために生まれたとしか思えない筒井道隆と、高岡早紀がハツラツと好演。松岡監督は、永遠不滅の思春期の揺れ動きを見事に活写した。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母紅梅", "母紅梅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "三益愛子主演のいわゆる“母もの”連作の第２作。１作目の「母」に続いて三條美紀が娘役で共演している。サーカス育ちのお京は上流階級に嫁ぐが、育ちの違いから失敗を繰り返し、娘の泰子までが友だちにばかにされてしまう。気を病んだお京は娘の将来を考え、家出して元のサーカス団に戻る。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍が如く　劇場版", "龍が如く　劇場版", "", "", "http://image.eiga.k-img.com/images/movie/33872/315x210.jpg?1238", 110, "プレイステーション2用の人気ゲーム「龍が如く」を、鬼才・三池崇史監督が実写映画化。欲望と暴力が渦巻く歓楽街・神室町に、かつて“堂島の龍”と呼ばれた伝説の極道・桐生一馬が、10年の刑期を終えて帰って来た。母親を探す少女・遙と出会った桐生は、成り行きで彼女と行動を共にすることに。そんな彼を、彼との対決を望む狂気の極道・真島が執拗に追い回し……。主人公・桐生に北村一輝、彼の宿敵・真島に岸谷五朗が扮する。", "2007-03-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヤマトナデシコ", "ヤマトナデシコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "「恋のたそがれ」で劇場映画デビューした山口貴義監督の第２作。スナックでのバイト生活を送りながら、新しい人生を始めたいと思い立った24歳の女の子・優香。３人の愛人と別れるために強引な作戦を実行する彼女の姿が、レトロなギターサウンドにのせて綴られる。若い男女のモラトリアムな生態をフワフワと浮き彫りにした一編。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コンセント", "コンセント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "田口ランディの同名小説をもとに、兄の死に戸惑う女性の心の軌跡を描く。フリーライター、ユキの兄が餓死した。部屋にはコンセントにつながれたままの掃除機があった。引きこもりだった兄は、なぜ生きることをやめたのか？　その日から、彼女は死臭や幻覚が付きまとう不思議な感覚にとらわれていく……。さまよいながら、やがて自己を解放させていくヒロインを演じる市川美和子が、エロティックなシーンにも果敢に挑戦し、熱演を披露。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Juventude em Marcha", "コロッサル・ユース", "", "", "http://image.eiga.k-img.com/images/movie/53502/315x210.jpg?2485", 155, "「骨」「ヴァンダの部屋」で世界に衝撃を与えたポルトガルの俊英ペドロ・コスタ監督が、再びリスポン郊外のスラム街フォンタイーニャス地区を舞台に、地元の人々をキャスティングして撮り上げた作品。アフリカ系移民が多く暮らすフォンタイーニャス地区では、土地開発に伴い住民たちの強制移住が行われていた。妻に見捨てられた老人ベントゥーラも、荒れ果てたフォンタイーニャス地区と近代的な集合住宅の間で新たな生き方を模索しており……。", "2008-05-24", "25,2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緋ぼたん浪人", "緋ぼたん浪人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "商家の身代乗っ取りをめぐる陰謀を描いた娯楽時代劇。公儀御用達の廻船問屋和泉屋は、御用金を用立てた見返りに、大奥一の美女、浪江を妻にした。だが彼は婚礼の夜に殺され、その後も和泉屋番頭たちが、次々に死んでいく。和泉屋の世話を受けた目明しの辰次と、浪人・水上大六は、事件の究明に乗り出す。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死の棘", "死の棘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "「伽耶子のために」以来６年ぶりの小栗康平の新作。高度経済成長期に入る前の日本を舞台に、ミホとトシオという結婚10年目の夫婦がトシオの浮気をきっかけに精神的な危機に陥るさまを描いたもの。全編ノーメイクでミホ役を演じた松坂慶子の演技が話題となった。島尾敏雄の自伝的私小説の映画化。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES LIBERTINES", "夜の放蕩者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "色情狂、露出狂などのセックス・ノイローゼの女性ばかりを集めて、療養させるというスペインの城館。ここを舞台に、１億7000万フランの宝石をめぐって展開する、快楽と物欲の世界を描いたエロティック・サスペンス。", "1971-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KRASNAYA　PALAKTA（THE　RED　TENT）", "赤いテント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "16名の隊員を乗せた飛行船イタリア号が、北極探検中、極点に到達した後に遭難。ノヴィレ隊長はじめ奇跡的に助かった数名は、赤く染めたテントを張り酷寒に耐えながら国際救助隊の捜索を待つのだが、それは絶望的だった……。列強各国が北極における権益を求めた20世紀初頭を背景に、極地征服の夢に憑かれた男たちの勇気と挫折が力強く描かれる合作映画。監督は、名作「鶴は翔んでゆく」などで知られるM・カラトーゾフ。", "1970-01-01", "12,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　CONQUEST　OF EVEREST", "エベレスト征服", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "標高8,848メートルの世界最高峰エベレスト。20世紀初頭からいく度か登頂が試みられてきたが、1953年5月29日、イギリスのハント大佐率いる二人の探検隊員が初めて頂上征服に成功。この壮挙をフィルムに収めた感動のドキュメンタリー。前人未踏の過酷な山行や神々しい景色は16ミリで撮影され、劇場ではそれを35ミリにブローアップしたもので上映された。", "1953-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すーちゃん　まいちゃん　さわ子さん", "すーちゃん　まいちゃん　さわ子さん", "", "", "http://image.eiga.k-img.com/images/movie/58082/315x210.jpg?2940", 0, "", "2013-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VICE SQUAD", "ザ・モンスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "冷血非情のポン引き、娼婦、そして風紀犯罪特捜班のデカ。夜のロサンゼルスを舞台に追う者と追われる者の鮮烈な駆け引きを描いた、小品ながら掛け値なしの傑作アクションだ。おとりになった娼婦プリンセスに迫る、冷血漢ラムロッドの狂気。シャーマン演出の真骨頂である。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("面打 men-uchi", "面打 men-uchi", "", "", "http://image.eiga.k-img.com/images/movie/54542/315x210.jpg?4238", 60, "史上最年少で文部科学大臣奨励賞を受賞した22歳の能面師・新井達矢にカメラを向けたドキュメンタリー。ひとつの能面の製作過程から、その能面が公演で使用されるまでを追う。言葉による説明を一切排し、木の塊から面が削りだされて表情を帯びていく様子をひたすら見つめ続けることで、天才能面師として注目を集める新井の繊細な技をあますところなく記録していく。監督は、「蝕旋律」「白日」の三宅流。", "2009-04-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'INCHIESTA", "インクアイリー〈審問〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "D・ダミアーニ監督が、キリスト復活の謎に挑んだ歴史スペクタクル。時の皇帝ティベリウスに命ぜられ、イエスの死体調査に乗り出すタウラス。パレスチナに赴き、ピラト総督やその妻クラウディアと知り合い、墓地の調査をしているうちに次第にイエスの“存在”に取り憑かれていく。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HANS CHRISTIAN ANDERSEN", "アンデルセン物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "童話の父アンデルセンのファンタスティックな世界を、歌と音楽バレエを通じて描きだしたミュージカル大作。子供たちを集めては話をする靴屋のアンデルセンがバレリーナに恋をする。彼は童話形式のラブレターを出すが、彼女はそれを台本と間違え、それがバレエ『人魚姫』となって大ヒットする……。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE−EYED JACKS", "片目のジャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "ブランドの初監督作品。カリフォルニアを舞台に、かつての仲間に裏切られた男が、刑務所を脱獄して復讐を遂げるまでを描いた本格西部劇。原作はネイダーの『銃をあげろ』だが、ブランド自身が手を加えている。監督第１作とは思えない堂々たる演出ぶりは見事。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エロス＋虐殺", "エロス＋虐殺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 167, "アナーキスト・大杉栄の衝撃的な半生を前衛的な手法で描いて、自由が政治に圧殺される構図を映像化した力作。1960年代頃の松竹出身の若手監督を中心として、松竹ヌーヴェル・ヴァーグという言葉が使われたことがあったが、そのなかでも最も特異で最も過激なスタイルに突入していったのがこの吉田喜重である。とりわけ、本作はその頂点に位置するもので、大正アナーキズムを呼吸した人物たちと、1970年当時のフリー・セックス志向の風俗が、時間軸・空間軸を超越して同一画面に現れるという斬新な試みに成功している。露出オーバーを意図的に貫いたモノクローム撮影、きわめて精神性の高い音楽も特筆に値する。DVDは「吉田喜重全集 [68-73] 性と政治の季節」に収録。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("甲虫王者ムシキング　スーパーバトルムービー　闇の改造甲虫", "甲虫王者ムシキング　スーパーバトルムービー　闇の改造甲虫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "子供たちの間で大ブームを巻き起こしたカードゲーム“甲虫王者ムシキング”の劇場版アニメ。森の妖精ポポや甲虫の王者ムシキングは、森の奥深くにある楽園で多くの昆虫たちと共に暮らしていた。ある日、別の森からやって来たお調子者の甲虫スジブトヒラタクワガタがムシキングに勝負を申し込む。そこへ突然、森を支配しようと企む闇の妖精アダーに操られた甲虫たちが襲い掛かる。ゲスト声優として人気お笑いコンビのドランクドラゴンが出演。", "2007-03-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏休みの宿題は終らない", "夏休みの宿題は終らない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "イギリス、フランスの核燃料施設周辺に住む人々と、その生活を記録したドキュメンタリー。「六ヶ所人間記」の撮影終了から４年後、イギリスのセラフィールドとフランスのラ・アーグを訪れた作者は、そこの気候や地理、経済条件など、想像もしていなかった様々な事実に直面していく。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さらば掟", "さらば掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "武井吾郎は、神竜会から命を狙われている元ヤクザ。彼はある日、自殺しようとしている杏子を救い、新聞に写真が載ってしまう。やがて愛し合うようになる二人。ところが、彼女の父は二人を引き離そうと画策。おまけに彼の居場所を突き止めた神竜会は、追っ手を差し向けてくる……。渡哲也の松竹初主演作である。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING SOLOMON’S MINES", "キング・ソロモンの秘宝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "古代イスラエルの王ソロモンが所有したといわれる金銀財宝をめぐっての愛と冒険の物語。秘宝に魅せられた冒険家、消息を絶った父を探す美女、悪徳軍人、さらに人喰い人種など、この手の映画に欠かせぬ要素が勢ぞろいした娯楽アドベンチャー。ビデオ発売時タイトルは「キング・ソロモンの秘宝 ロマンシング・アドベンチャー」。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パチスロひとり旅", "パチスロひとり旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "リストラに遭い、サラ金から多額の借金を抱えた男、名波誠が全国を放浪、パチスロの稼ぎでお金を返済していく一風変わったロード・ムービー。とある地方で誠は家出少女、ハルナと出会うが、彼女は突如姿を消してしまう。だが、誠が50万円を賭けてチンピラの勝部とパチスロ勝負を繰り広げていると、そこにハルナがひょっこりと現れた。幸運の女神の到来と思った矢先、誠の顔が苦痛にゆがみ……。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水俣—患者さんとその世界—", "水俣—患者さんとその世界—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 167, "1960～70年代に瀕発した日本の公害病のなかでも、最も深刻な禍根となった水俣病。その発祥地、熊本県水俣を拠点として、患者たちの闘病生活や裁判闘争に、徹底した長期取材を敢行した土本典昭の“水俣”連作の第１作。1969年、チッソを相手に裁判を起こした29世帯を中心に、万単位にふくらんでいく潜在患者の発掘の過程が、多くの証言を得て詳細に記録されている。なかでも瞠目すべきは、患者たちの日々の営みへの肉迫だ。漁業をなりわいとしてきた彼らの、汚染された海への断ちがたい愛着。聴覚を失った胎児性患者が、スピーカーの震えを手に感じて音楽を“聴き”取ろうとする情愛の深さ。映画は水俣病の告発記録にとどまらず、“患者さんとその世界”を丸ごとにつかみ出そうとする真摯な眼差しに貫かれている。第１回世界環境映画祭グランプリをはじめ、受賞多数。２時間の短縮バージョンもある。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCREAMERS", "スクリーマーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「ブレードランナー」や「マイノリティ・リポート」の原作者P・K・ディックの傑作『変種第２号』（別名『人間狩り』）を忠実に映画化した作品。西暦2078年、惑星シリウス6Bでは、新しいエネルギーをめぐって惑星開発企業新経済ブロック（NEB）と、科学者や労働者らの連合軍との戦争が10年にも渡って繰り広げられていた。そんな時、連合軍によって開発された防御用兵器であるスクリーマーズが、突然人間を襲い始めた。自己修復機能を持ち、生きるものすべてを破壊することをプログラムされているスクリーマーズは、急速に進化し人間になりすます。誰が敵で誰が味方か？　究極のバトルが始まる。", "1996-01-01", "1,10,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOW TO MAKE LOVE TO A NEGRO WITHOUT GETTING TIRED", "間違いだらけの恋愛講座", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "街を歩けば必ず女の子に声をかけ、モーションをかけまくる主人公の“男”。その男の巧妙な恋の手練、様々な女との恋愛模様を通して、人類の究極のテーマ“恋愛”について考える恋愛映画。主人公に役名がなく、すべて“男”という表現で扱われている点もユニーク。", "1989-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火星のわが家", "火星のわが家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ステージに立つと声が出なくなってしまう症状に悩まされているジャズ・シンガーの未知子が、父の元へ帰ってきた。かつて宇宙旅行協会会長（!?）でもあった父は、そんな彼女に「人間の悩みなど、宇宙から見ればちっぽけなものだ」と優しく励ます……。火星に移住することを夢見る父を通して、家族のあり方を模索していくヒューマン・ドラマ。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BATAILLE DU RAIL", "鉄路の闘い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "第二次世界大戦のさなか、ドイツ占領下のフランスを舞台に、鉄道破壊工作によるレジスタンスに生命を懸ける人々を描いたセミ・ドキュメンタリー。名匠クレマン監督の長編処女作で、短編映画の経験を生かした緻密な演出と編集構成が光る秀作。", "1945-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和のいのち", "昭和のいのち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "石原裕次郎をはじめ、当時の日活オールスター・キャストによる任侠ものの大作。昭和初期、主人公は右翼テロ組織の刺客だったが、首相暗殺に失敗。危ないところをテキ屋の親分に助けられ、その道に生きようとする。ラスト、大義名分のためではなく自分のために殴り込みに出かける主人公のクローズ・アップが印象的。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISTER FREEDOM", "ミスター・フリーダム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「ポリー・マグー お前は誰だ」で長編劇映画デビューを果たした写真家W・クラインが、「ベトナムを遠く離れて」に続いて取り組んだスパイ・コメディ。日本では製作から30年間を経て1998年に正式公開された。東西両陣営の対立が深まる1960年代パリに、アメリカの秘密エージェント、ミスター・フリーダムが送り込まれる。さっそく共産主義者を一掃するために作戦を開始するフリーダムだったが、彼の強引な活動は世論の理解を得られず、次第に孤立するはめに……。アメフトのユニフォームに身を包み、しつこいほど“自由”を叫んで暴走するマッチョマン、フリーダムのキャラクターは皮肉たっぷり。さらにD・セイリグやP・ノワレといった名優たちまで、キッチュなコスチュームで登場。クラインのポップなアート感覚が、破壊的なまでに弾けまくる怪作だ。", "1968-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROMISED LAND", "プロミスト・ランド ～青春の絆～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "田舎の高校を出た４人の若者たちは、夢を抱いてそれぞれの道を歩み始める。しかし月日は彼らを変えていった。そして数年後、故郷で再会した４人をある運命が待ち受けていた……。現代アメリカの若者の追い求める夢と挫折を描いた感動の物語。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RIVER OF NO RETURN", "帰らざる河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1875年のカナディアン・ロッキー。開拓者マットは、妻が死んでから酒場の歌手ケイに預けてあった９番目の息子と再会、新しい農場の開拓に乗り出す。農場の横を流れる“帰らざる河”と呼ばれる激流で、ある日マットはケイと彼女の婚約者ハリーがイカダで漂流しているのを救った。ところがハリーは、マットから馬と拳銃を奪って旅立ってしまう。折しもそこへインディアンの襲撃。父子と残っていたケイの３人組はイカダで“帰らざる河”を下り、奇跡的に町に着く。シネスコ初期作品で、激流を下るシーンをクライマックスに迫力たっぷりと描く。主題歌も大ヒット。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋鎖", "恋鎖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "自主映画界で手腕が注目された新鋭、西條雅俊監督の劇場デビュー作となる恋愛劇。恋に振り回される男女の激しい感情の行方が描かれる。“初恋”というテーマを甘く切ないものにするのではなく、シビアにとらえた視点が斬新だ。体が17歳に若返っているという不思議な現象に見舞われた29歳のプレイボーイの柏木。ある日、彼は偏愛の性癖で正直な恋愛ができない祥子と出会う。軽薄な恋愛しかできない柏木は、彼女に同調していくのだが……。", "2006-09-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若き日の信長", "若き日の信長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "時は、戦国。若き信長は、尾張八ヵ郷を治め、清州城で駿遠三ヵ国の領主・今川義元と対していた。歴代織田家に仕えていた山口左馬之助は、今川方の優勢に屈し、今川のスパイとなったが、遠謀深慮の武将・信長はすべてを見抜いていた……。大佛次郎の歌舞伎劇を、多作・絶頂期の森一生監督が折り目正しく映画化している。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEN NORTH FREDERICK", "秘めたる情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "不幸な家庭生活を送る名家の当主と、親子ほども年の違う女との秘められた恋を描いたドラマ。娘の回想というスタイルをとっており、父のありし日の姿が淡々と綴られていく。ラストの、父が持っていた首飾りを親友が結婚式の席でしているのを見て、父と彼女の秘められた情事を知る娘の姿まで、ハリウッドらしい良質なドラマとなっている。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Red Hot Chili Peppers Live: I'm with You", "レッド・ホット・チリ・ペッパーズ　ライブ“アイム・ウィズ・ユー” in シアター", "", "", "http://image.eiga.k-img.com/images/movie/57122/315x210.jpg?4570", 117, "米人気バンド「レッド・ホット・チリ・ペッパーズ」が10枚目のアルバム「アイム・ウィズ・ユー」のリリースを記念し、2011年8月30日にドイツで行ったライブの模様を映画館で上映。「アイム・ウィズ・ユー」の収録曲全曲や往年のヒットナンバーを多数披露している。", "2011-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEET SWEETBACK’S BAAD ASSSSS SONG", "スウィート・スウィートバック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "スパイク・リーをはじめ、若手の黒人監督たちが尊敬してやまない奇才M・V・ピーブルズの、伝説的なブラック・ムービー。卑劣な白人刑事を殺した黒人青年スウィートバックの逃亡劇をエネルギッシュに描く。ピーブルズの息子で、「ニュー・ジャック・シティ」の監督マリオが、少年期の主人公役で出演。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GENOU DE CLAIRE", "クレールの膝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ヌーヴェル・ヴァーグを代表する監督の一人、ロメールの“六つの教訓話”シリーズ第５作。結婚を目前に控えた30代半ばの男ジェロームは、独身生活最後の夏を過ごしにアンヌシー湖畔の別荘へやって来る。そこでふと知り会った17歳の少女クレールの若々しく美しい膝に、はからずも心奪われて……。主人公が男の本性を露呈していく過程が滑稽。DVDは「エリック・ロメール コレクション(2)」に収録。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忠臣蔵 桜花の巻・菊花の巻", "忠臣蔵 桜花の巻・菊花の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 183, "大石内蔵助に片岡千恵蔵が扮した、松田定次監督、東映オールスター・キャストによる忠臣蔵二部作。「桜花の巻」はお家断絶となった内蔵助が赤穂城を去るまでを、「菊花の巻」では吉良邸討ち入りまでを描く。中村錦之助の浅野内匠頭が迫力。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CENTRAL　DO　BRASIL", "セントラル・ステーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ベルリン映画祭でブラジル映画史上初の金熊賞に輝いたロード・ムービー。リオ・デ・ジャネイロの中央駅で手紙の代筆業をしている初老の女ドーラが、身寄りをなくした少年ジョズエの父親探しに付き合うことになる。見知らぬ田舎町への旅を続けるうちに、ドーラは忘れかけていた人間らしい温かみを取り戻し、ジョズエとの間に母子にも似た絆が芽生えていく。つい広大な風景に目が奪われてしまうが、主役二人の微妙な感情の変化を通して、様々な問題を抱えるブラジル社会の現状にまで言及した真摯な視点も印象深い感動作。", "1998-01-01", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("熊本物語", "熊本物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "三池崇史が、熊本の自治体からの依頼で撮った中編の作品群。大和朝廷時代の肥後を舞台に、神の庇護を受けて朝廷軍に立ち向かった男の神話的な物語「隧穴幻想・トンカラリン夢伝説」、1200年前、中国大陸からの脅威にさらされた九州の防衛のために、送りこまれた若き兵士たちの思いを綴る「鞠智城物語・防人たちの唄」、わずか900の兵で豊臣秀吉の軍一万に立ち向かった肥後国衆の女性たちの物語「おんな国衆一揆」の３編から成る。2001年までは熊本県内の限定上映だった。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠山の金さん たつまき奉行", "遠山の金さん たつまき奉行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "北町奉行遠山金四郎は、金塊三百貫を載せた船が佐渡の荒海に消えた謎を追い、悪奉行と対決。金さんの佐渡での活躍を描いている。金さんを演じる御大・片岡千恵蔵とともに老練マキノ雅広のキビキビとした演出が見もの。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RED ROOM", "レッド・ルーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ハリウッドで、連続してモデルたちが失踪し、二人の女性捜査官がモデルとして潜入捜査を始めた。二人は死体も手掛かりも残さない、殺人鬼の罠にハメられていく。エロティック・シーン盛りだくさんのサイコ・サスペンス。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACKBOARD JUNGLE", "暴力教室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ハイスクールの非行少年と教師の闘いを描いた学園ドラマ。生徒が図書室で先生を暴行するシーンや、教室内でのナイフを使ってのイヤガラセなどのセンセーショナルな内容と描写で話題になり、公開当時には上映禁止問題も起きた。テーマ曲のロックも大ヒット。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEGRETI，SEGRETI", "女テロリストの秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ベルナルド・ベルトルッチの実弟で「1900年」などの脚本の執筆もしているG・ベルトルッチの作品。犯罪アクションではなく、女テロリストと、彼女とかかわりのある女たちそれぞれの苦悩を描いた女性映画である。登場するのは、テロリスト、その乳母と実母、殺されたテロ仲間の母と妹、ヒロインを尋問する女判事、などなど。そしてそれらを演じるのは、イタリアを代表する女優ばかり。なかでも、ヒロインの乳母役のA・ヴァリの堂々たる存在感は絶品。テロリズムが日常生活のなかに描かれる興味深い一編。", "1984-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Signs", "サイン", "", "", "http://ecx.images-amazon.com/images/I/41AP6Y89CRL._SL210_.jpg", 107, "妻の事故死により信仰を失った元牧師グラハムが2人の子供と弟メリルと暮らす農場で、怪現象が発生。とうもろこし畑に一夜にして巨大な図形＝ミステリー・サークルが出現したのだ。いったい誰の仕業なのか。監督は「シックス・センス」、「アンブレイカブル」のシャマラン。ミステリー・サークルは「X-ファイル」でもおなじみの実在の怪現象。直径20メートルにも及ぶ巨大な幾何学図形が畑などに出現するもので原因は不明。", "2002-09-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道兵器", "極道兵器", "", "", "http://image.eiga.k-img.com/images/movie/56052/315x210.jpg?4259", 100, "永井豪と共作の「ゲッターロボ」などで知られる漫画家・石川賢の同名コミックを実写映画化したバイオレンスアクション。西日本を牛耳る岩鬼組組長の一人息子・岩鬼将造は、父親の敵討ちと親友との友情を守るため、片腕をマシンガン、片脚をバルカン砲に改造して戦いに挑む。監督・主演は「VERSUS」「魁！男塾」の坂口拓。「地獄甲子園」の山口雄大が共同監督を務める。", "2011-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太の日本誕生", "ドラえもん のび太の日本誕生", "", "", "http://ecx.images-amazon.com/images/I/31SAZYJM0KL._SL210_.jpg", 100, "劇場版第10作で、ドラえもんの記念碑的作品。７万年前の日本で国づくりを始めたのび太たちは、現代に戻ってから７万年前の原始少年ククルに出会う。ククルの部族ヒカリ族が凶暴なクラヤミ族に襲われたと知ったドラえもんたちは、一族を救おうと古代中国へと向かう。原始時代の国づくりのためにドラえもんが出すひみつ道具がすばらしく、観るものを唸らせる。西田敏行が歌う主題歌『時の旅人』も話題になった。", "1993-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROSA LUXEMBURG", "ローザ・ルクセンブルク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "19世紀末から第一次大戦にかけてベルリンを舞台に活動した女性革命家、ローザ・ルクセンブルクの伝記映画。大戦に際し反戦を唱え、1919年に右翼軍人に虐殺されるまでのドラマティックな人生を行動的な活動家、そして女性としての側面から描く。女流監督M・V・トロッタの力作。", "1985-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ME，MYSELF　＆　IRENE", "ふたりの男とひとりの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "「メリーに首ったけ」のファレリー兄弟が、「ジム・キャリーはMr.ダマー」でもコンビを組んだJ・キャリーを主演に撮った爆笑コメディ。かつて妻に裏切られたことから二重人格となってしまった警官チャーリー。普段はおとなしい彼だが、時々暴力的なキャラクター、ハンクに変貌してしまうのだ。そんな彼が、交通違反の容疑で指名手配されている女アイリーンをロードアイランドからニューヨークに護送することになる。だが、知らぬうちに環境保護庁にまつわる汚職事件に巻き込まれていたアイリーンは命を狙われ続ける。しかも、チャーリーは二重人格抑止の薬を忘れたために、突如分身ハンクが顔を出してしまうハメに……。J・キャリーが持ち前の話術とテンションの高い演技で一人二役(？)を演じている。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRUE IDENTITY", "トゥルー・アイデンティティー 正体知られて大ピンチ!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "スティーヴ・マーティンやエディ・マーフィのものまねで有名なイギリスのコメディアン、L・ヘンリー主演のコメディ。黒人俳優マイルズは、どうにも芽が出ず、毎日の仕事にもあぶれる状態。その彼が、ある日偶然マフィアの親分の秘密を知り、命を狙われることになる。マイルズは、友人のメイクアップ・アーティストに頼んで白人に変装するのだが、今度はなんとマイルズを狙っている殺し屋に間違われるのだ。白人に変身した自分が、黒人の自分を殺すという、とんでもない状況に陥ったマイルズの行く手には……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Acne", "アクネ ACNE", "", "", "http://image.eiga.k-img.com/images/movie/57872/315x210.jpg?1965", 84, "ウルグアイの海岸の町を舞台に、異性とのキスにあこがれる少年の思春期特有の悩める姿を初々しく描いた青春映画。13歳の中学生ラファエルは、まだキスの経験もないことが悩みで、女の子とキスができないのは自分の顔にニキビがあるせいだと思っていた。学校のマドンナのニコルに淡い恋心を抱いているが、告白する勇気も自信もないラファエルは、男性としてのさまざまな経験を積んで自信をつけようとするが、空回りばかりで……。", "2012-05-26", "75,22,19,9,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BITE", "ブラッド・バイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "日本人SFXアーチスト、スクリーミング・マッド・ジョージが、イタリア映画界の俊英と手を組んで手掛けたショッキング・ホラー。突然変異のミュータント・スネークに噛まれた人間が、その猛毒性の生物細胞に侵され、恋人を愛する人間的な心と、破壊への衝動という爬虫類の本能との板ばさみに苦しむ。", "1990-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A SHOCK TO THE SYSTEM", "ショック・トゥー・ザ・システム　～殺意のシステム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "順調に出世街道を歩んでいたはずの男が、ある日部下に上役の座を奪われる。静かな怒りに震える彼は、部下の暗殺を決行。さらに気にくわない妻をも殺害しようとするが……。M・ケインの不気味な演技が光を放つサスペンスフルなブラック・コメディ。ビデオ発売時タイトルは「殺意のシステム 出世のための殺人フルコース」。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OPEN FIRE", "ルパート・グレイブス　極悪非道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "実在した凶悪犯デビッドの、すさまじい犯罪歴を描いたクライム・ストーリー。凶悪強盗により逮捕、投獄を繰り返していたデビッドは、３年ぶりに出所すると女装をして夜の街へ出かけていった。クラブで知り合ったモデルのスー・スティーブンスと恋仲になったデビッドは、彼女に銀行強盗をし国外逃亡する話を持ちかける。そしてすぐに行動を開始するが……。", "1994-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH WEEKEND", "ウィークエンド／ デッドエンド・ホリデイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "孤独なファッション・モデルが、知り合ったばかりの歯科医と出かけた彼の別荘で、チンピラ４人に襲われる。やがて医者は殺され、彼女の肉体に欲望をたぎらす男たちに向かって、女はたった一人の戦いを挑むという復讐バイオレンスもの。", "1976-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("O Signo da Cidade", "星の導き", "", "", "http://image.eiga.k-img.com/images/movie/54772/315x210.jpg?4311", 95, "ブラジルの大都市サンパウロを舞台に、孤独を抱えて生きる男女の姿をオムニバス・タッチで描いたヒューマンドラマ。浮気を重ねてしまう人妻リディア、男性として生まれてしまったジョジアルド、ラジオの深夜番組でリスナーの悩みに答える占い師テカ……。少しずつ交錯する彼らの人生を通し、大都市の希薄な人間関係やブラジル社会に残る差別・貧困といった問題を浮き彫りにしていく。テカ役で主演も務めたブルーナ・ロンバルディが脚本も手掛けている。", "2009-10-03", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SCORPIO FACTOR", "メタルスコーピオン 死者への接吻〈くちづけ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "中央アメリカの某国からマイクロ・チップが盗み出され、その奪回を狙うある組織は、スコーピオンと呼ばれる人物にその仕事を依頼する。スコーピオンというコードネームで呼ばれる処刑人は、一年のうち蠍座の時期にしか仕事をしない一流の殺し屋だ。悪党には手段を選ばない殺し屋の凄絶な戦いを描く。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LET　IT　COME　DOWN ： THE　LIFE　OF　PAUL　BOWLES", "ポール・ボウルズの告白／シェルタリング・スカイを書いた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "1999年11月にこの世を去った『シェルタリング・スカイ』の作者、ポール・ボウルズ。北アフリカのモロッコで自ら世捨て人となったボウルズの人生を、生前の彼、そして、彼と親交のあったW・バロウズやA・ギンズバーグなどの芸術の巨匠達への貴重なインタビューを交えて描き出したドキュメンタリー作品。", "1998-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WITH A SONG IN MY HEART", "わが心に歌えば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "第二次大戦中、前線慰問に献身的活躍をみせたアメリカの女性シンガー、ジェーン・フローマンの半生をミュージカル仕立てで描いた感動作。主演のS・ヘイワードの歌声は、フローマン自身が吹き替えている。慰問旅行中の飛行機事故で重傷を受けながらも見事カムバックを果たすまでの物語だ。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PEUR SUR LA VILLE", "恐怖に襲われた街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "一人暮らしの女性ばかりを狙う連続殺人事件が発生。パリ警察のルテリエの必死の捜査にもかかわらず、謎の殺人鬼はまた新たなる獲物を狙い始めていた。なかなか全貌を現わさない犯人の正体とは……。ベルモンドが型破りな敏腕警部に扮するサスペンス・アクション。", "1974-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Thoroughly Modern Millie", "モダン・ミリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "「サウンド・オブ・ミュージック」のジュリー・アンドリュース主演のミュージカル。1920年代、カンサスの田舎町から華やかなニューヨークの街にやってきたミリー。あっという間にモダン・ガールに変身した彼女は、就職した会社の若社長トレバーに恋をする。ところが彼は、ミリーと同じホテルに滞在中の女優志望ドロシーにひと目惚れ。ある日、ドロシーが何者かに連れ去られてしまい、ミリーは彼女の救出に向かうが・・・・・・。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("てなもんや商社", "てなもんや商社", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "女性新入社員が仕事に生きがいを見いだしていく姿を描くさわやかなコメディ。お気楽な女子大生のひかりが、ほんの腰掛け程度のつもりで萬福中国貿易に就職する。ところがいきなり、取引先の中国での大仕事を任されて大あわて。一人異国の地でオロオロする彼女だったが、中国人のいい加減な仕事ぶりに怒りが爆発、がぜんやる気を出して大奮闘する。明るく元気なヒロインに扮する小林聡美のコミカルな演技が絶妙。上司役の渡辺謙も、ひょうひょうとした演技で笑いを誘う。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魔法戦隊マジレンジャー　THE MOVIE　インフェルシアの花嫁", "魔法戦隊マジレンジャー　THE MOVIE　インフェルシアの花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 39, "男の子たちの人気を集める“戦隊”シリーズの劇場版。テレビ以上にファンタジックなアドベンチャー・アクションの世界が広がる作りとなっている。天空世界のマジトピア、地底世界のインフェルシア、そして地上の３界にまたがって展開。５人と、恋人を奪った地底世界最強の怪獣との凄まじい戦いが繰り広げられる。", "2005-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("牙狼 GARO RED REQUIEM", "牙狼 GARO RED REQUIEM", "", "", "http://image.eiga.k-img.com/images/movie/55382/315x210.jpg?4725", 97, "雨宮慶太監督が手がけた特撮TVシリーズの3D劇場版。同シリーズでブレイクした小西遼生が主演を続投し、松山メアリ、斉藤洋介、津田寛治、中尾彬らが脇を固める。魔戒騎士たちは、魔界からヤミを通じて出現する魔獣ホラーと長年にわたり死闘を繰り広げていた。ある日、最強の魔獣7体が復活し、人間界へ降臨する。最強騎士の証“ガロの称号”をもつ冴島鋼牙は、魔戒法師の烈花とアカザ、その弟子シグトとともに、魔獣討伐の旅に出る。", "2010-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("晩春", "晩春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "小津安二郎といえば、父と娘、あるいは母と娘など、平凡な家庭の日常を淡々と描いた作風で知られた作家だが、実はそういう家族劇は後期、脚本家の野田高梧と組んだ頃のものであり、初期にはギャング映画やコメディなど様々なジャンルを手掛けていた。そんな後期の小津スタイルが確立されたのがこの「晩春」である。北鎌倉に住む大学教授は、婚期を逃がしかけている娘を結婚させようとする。父を一人にはしたくないと、娘は結婚に乗り気ではないが、父は寂しさをこらえて娘を嫁がせるのだった……。父娘二人が、最後に水入らずで枕を並べて過ごす夜のシーンは特に有名で、壷の空ショットが挿入されることから“壷のシーン”として多くの評論家にとり上げられ、研究されてきた。余計なセリフや感情表現を用いず、その場の空気ですべてを語ってしまう小津演出の真髄がここにある。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("顧城別恋", "詩人の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "妻を殺して自殺した中国の詩人グー・チョンの生涯を、実話に基づいて描く人間ドラマ。文化大革命時、反逆者とみなされた父から本をもらい、詩心に目覚めた幼いグー。詩人となった彼は創作に専念するため妻とニュージーランドに渡るが、大学の教え子を手伝いとして呼び寄せたことから夫婦の間に不穏な空気が流れ……。官能的な映像美が印象深い。", "1998-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Next Stop, Greenwich Village", "グリニッチ・ビレッジの青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "　1953年、俳優志望のラリーは大学を卒業すると、母親の反対を押し切り、グリニッチ・ビレッジに移り住む。そこには彼と同様、俳優や画家、作家を目指す若者たちが大勢住んでいた。ラリーは健康食品店で働きながら、演劇教室に通う一方、仲間たちと心を通わせていく。やがてグループのひとりセーラと仲良くなるが、彼女が妊娠してしまい……。主人公と彼の仲間たちの姿を描いた、ほろ苦く切ない青春群像劇。監督ポール・マザースキーの半自伝的な作品でもある。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("When Pigs Fly", "豚が飛ぶとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "さびれた街の小さなパブ“エリンの薔薇”のダンサー、シーラが元ジャズマンの家主マーティに店の物置にほうり出されていたロッキング・チェアをプレゼントする。ところが、この古い椅子には二人組の幽霊が棲んでいた。メランコリックな女ゴーストとおしゃまな少女ゴースト。以来、世捨て人のように生きてきたマーティの心に生気がよみがえり始める。どこかにありそうで、どこにもない街に展開する心温まる幽霊物語。ヴェンダースやジャームッシュとの名コンビで知られるカメラマン、R・ミューラーのアバンギャルドでファンタジックな映像センスが際立つ。原題は“起こりそうもない話”の意。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRISONER OF ZANDA", "ゼンダ城の虜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ホープ原作による永遠の名作、５度目の映画化。19世紀の仮想王国ルリタニアを舞台に、国王と瓜二つの快男子が、王位を奪わんとする陰謀者たちを相手に大活躍する物語。正義の剣士と皇太子の２役を巧みに演じ分けるセラーズの名演ぶりに注目。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr. Smith Goes to Washington", "スミス都へ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "腐敗した政界にひとり立ち向かう新人議員の姿を描いた、名匠フランク・キャプラ監督による社会派ドラマの傑作。急死した上院議員の後任として田舎から担ぎ出されたスミス氏。政界の事情を知らない彼を議員に祭り上げようという政治家たちの目論見をよそに、彼は積極的に政治活動を始める。ある日議員の汚職を知った彼は、それを議会で追求しようとするのだが……。理想主義に燃える主人公スミスをジェームズ・スチュワートが熱演。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・影の軍団第参章／地雷火", "新・影の軍団第参章／地雷火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "千葉真一が「キル・ビル」でも演じた当たり役、服部半蔵に再挑戦した痛快娯楽活劇。徳川家康の孫娘、千姫の命を守るため、半蔵率いる“影の軍団”が暗躍する。徳川家康の孫、千姫の命を、猿飛佐助が命じた何者かが狙っている。知らせを受けた服部半蔵は、影の軍団を呼び寄せようとするが、彼らは風魔忍者の襲撃に遭い、全員討ち死にしてしまう。", "2003-09-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BACK FIRE", "バックファイア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ベトナム帰りで心身症気味の夫にいや気がさしていたマーラは、愛人のジェイクと組んで殺害を計画するが失敗に終わる。植物人間となった夫の看病は姉に任せ、派手な服装で夜遊びする毎日。そんなある日、影のある謎の男リードと出会った彼女は、再び夫の殺害計画を考えるが……。アレンの悪女ぶりが見もの。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どてらい男", "どてらい男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "福井の田舎から大阪の機械工具問屋に奉公した男が、一番番頭をはじめとする先輩たちのシゴキとイビリに耐えながら出世していく様を描いた、花登筐お得意の上方根性もの。TVシリーズで国民的人気を得た後、同じ西郷輝彦主演で映画化された。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEHIND　THE　SUN", "ビハインド・ザ・サン", "", "", "http://ecx.images-amazon.com/images/I/31LKoC4kIcL._SL210_.jpg", 92, "「モーターサイクル・ダイアリーズ」の俊英W・サレス監督による愛憎劇。対立する二つの一族の抗争による悲劇を、ドラマティックに描き出す。サレスの落ち着いた演出はもちろん、恋に身を焦がす若者に扮したR・サントロの好演も光る。土地の利権をめぐり長年の抗争を続ける二つの家族。だが、青年トーニョは恋を知ったことで、この争いの渦中から抜け出そうとする。", "2004-11-06", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蛇女", "蛇女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「らせん」や「うずまき」など、ホラーへの出演が続く佐伯日菜子主演による怪奇映画。バイオテクノロジーによって不老化の研究を続ける医科大助教授と知り合った女性モデルが、彼と彼の妹の禁断の関係を知り、さらなる驚愕の“秘密”を知ることになる。終盤でやおら破裂する佐伯のエキセントリックな演技もさることながら、助教授の妹に扮した夏生ゆうなのおどろおどろしい怪演に凄みあり。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AXION アクション", "AXION アクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "岩手県出身の男性デュオ“STGM（ステゴマ）”をモデルに、ミュージシャンを目指す2人の青年の成長を描いた青春ストーリー。岩手県の田舎町で暮らす高校生の達也と裕之は、ミュージシャンになる夢を実現させるため故郷を飛び出す。ヒッチハイクで大阪に辿り着いた彼らは、時には苦い思いを経験しながらも路上ライブを重ねていく。そんな彼らに、ある日絶好のチャンスが訪れ……。出演は「パッチギ！／LOVE＆PEACE」の清水優ほか。", "2008-03-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLIGHT OF THE PHOENIX", "フライト・オブ・フェニックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "ロバート・アルドリッチ監督の'65年作品「飛べ！フェニックス」をリメイク。砂漠の灼熱地獄、減少する一方の物資と、状況は極めて過酷。そんな中、生き残るために希望を失わずに奮闘する男たちの姿は痛快で、飛行機製造の過程もスリリングだ。謎の男に扮するジョヴァンニ・リビシの怪演も光る。真夏のゴビ砂漠に貨物機が不時着。生き残った者たちは砂漠から脱出するため、残骸から新しい飛行機を造ろうとするのだが……。", "2005-04-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTERIORS", "インテリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「アニー・ホール」でアカデミー監督、脚本賞を受賞したアレンが、初めて笑いを排したシリアス・ドラマを手掛けたことで話題となった作品。彼の映画でたびたびキーワードとして出てくる、イングマール・ベルイマン的手法を用いて、家族の崩壊を緊張感を持続させながらもクールに描ききっている。ニューヨーク近郊ロングアイランド、30年も連れ添った両親に、突然別居話が。インテリア・デザイナーの母はショックを受け、自殺未遂事件を起こす。父が連れてきた、母とはまったく個性の違う女性の出現で３人の娘たちの胸中も複雑だが……。「ゴッドファーザー」「アニー・ホール」のオスカー受賞者ウィリスの精巧で優美なカメラワークは見事。女の絶望と怒りをあらわに演じるG・ペイジ、対照的なM・ステイプルトンをはじめ、演技陣も充実。アカデミー賞に５部門ノミネートされた秀作である。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEADBOLT", "マニアック・コレクター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ルームメイトの男、アレックスにかつての夫を殺され、そのうえ、部屋に監禁されてしまったマーティー。異常な状況の中、狂気に取り憑かれたアレックスとマーティーの締めつけられるような葛藤が見もののサイコ・スリラー。J・ベイトマンが、追い詰められる女、マーティーを好演。A・ボールドウィンの怪演も不気味な一作。", "1992-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Saw IV", "ソウ4", "", "", "http://image.eiga.k-img.com/images/movie/34302/315x210.jpg?1676", 93, "残虐かつ巧妙な殺人ゲームに強制参加させられた人々の運命を描く大ヒット・スリラー「ソウ」シリーズの第4弾。「ソウ2」「ソウ3」のダーレン・リン・バウズマンが引き続き監督を務める。ついに死を遂げたジグソウと彼の弟子アマンダ。ところが、ジグソウの遺体の胃袋から発見されたテープで、ゲームがまだ終わっていないことが明かされる。ジグソウ逮捕に執念を燃やしていたSWAT隊員リッグが新たなターゲットとなり……。", "2007-11-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Elizabethtown", "エリザベスタウン", "", "", "http://image.eiga.k-img.com/images/movie/1382/315x210.jpg?4485", 123, "オーランド・ブルームが現代劇に挑戦、「スパイダーマン」のキルステン・ダンストと共演する青春ドラマ。8年がかりのプロジェクトに失敗して会社を解雇されたドリューは、父の葬儀のために故郷エリザベスタウンに行き自分を取り戻していく。「デッドマン・ウォーキング」のオスカー女優スーザン・サランドン、「テキサス・チェーンソー」のジェシカ・ビールらが共演。監督は「あの頃ペニー・レインと」のキャメロン・クロウ。", "2005-11-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRAUDS", "保険屋に気をつけろ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "映画界での活躍も目立ってきたミュージシャン、P・コリンズの主演第２作。平凡な夫婦ジョナサンとベスのところに強盗が入った。二人はその強盗を殺してしまう。しかも死んだのは親友のマイケルだった。そこへ現れた、超変わり者の保険屋ローランド。彼は二人にしつこくまとわりつき、例の事件が保険金目当ての狂言だと言い出した。", "1992-01-01", "11,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEMETRIUS AND THE GLADIATORS", "ディミトリアスと闘士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1953年に製作された「聖衣」の続編。ローマ皇帝カリギュラは、伯父のクローディアスに聖衣を探せと命じる。聖衣はディミトリアスが持っていたが、皇帝の護衛隊がやってきた時、彼はうまくそれを隠した。だがディミトリアスは捕まり、闘士養成所に収容される。ディミトリアスは神の教えに従い、闘技場でも相手を殺さずにいたが、恋人が他の闘士に殺されたことを知り復讐を誓う。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEMOLITION HIGH", "トイ・ソルジャー'96", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "軍施設から核弾頭搭載の小型ミサイルを強奪し、全世界を手中に収めようとしていたテロリストが、施設の近くにあるメイフィールド高校を占拠。生徒と教師は人質となり、警察もSWATも手をこまねいて見ているしかなかった。だが、校内にはニューヨークから転校してきたばかりのレニーが捕まらずに残っていた。そしてレニーは、そこらにある教材を武器にテロリストたちに立ち向かっていくのだった。奇想天外なアイデアでテロリストを次々と倒していく高校生レニー役を、C・ハイムが熱演。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chandni Chowk to China", "チャンドニー・チョーク・トゥ・チャイナ", "", "", "http://image.eiga.k-img.com/images/movie/54172/315x210.jpg?3434", 155, "中国へ渡ったインド人青年の活躍を描くボリウッド製カンフー・コメディ。インド・デリーの市場で働くシドゥは、2人組の中国人に“あなたは中国の英雄の生まれ変わりだ”と言われ、すっかりその気になってしまう。つまらない人生から抜け出すチャンスだと考えたシドゥは、彼らに誘われるままに意気揚々と中国へ出発。しかし、彼らの真の目的は、故郷の村を牛耳るギャング・北条をシドゥに退治させることだった……。", "2009-05-30", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON THE FIDDLE", "ダイナミック作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "イギリス陸軍に入隊したジプシーが、そこでロンドン子のポープと意気投合し、軍務そっちのけで金もうけ作戦に精を出すおふざけ軍隊映画。二人はやがて最前線に追っぱらわれてしまうが、なんと敵を全滅させて……。S・コネリーには珍しいコミカルな作品。", "1961-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名門！多古西応援団", "名門！多古西応援団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「スケバン刑事」の脚本で知られる橋本以蔵の劇場用デビュー作。男らしさを追求するために応援団に入った高校生の周りで起きる大騒動を描く。技術やテーマ主義に走ることなく、単純明快なストーリーをオーソドックスに撮った作品。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAYE　MAKBFI", "１票のラブレター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "2001年のヴェネチア映画祭で監督賞に輝いたイランの新人ババク・パヤミ監督による好編。伝統的なイスラム社会の島で選挙の投票に奔走する選挙管理委員の女性と兵士の１日を描く。選挙の日を迎えたイランのキシュ島。島に派遣された選挙管理委員の若い女性は票を集めるために警備兵と各所を回る。ところが選挙に不慣れな島民から票を集めるのは思いのほか大変で……。", "2001-01-01", "15,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使のいた屋上", "天使のいた屋上", "", "", "http://image.eiga.k-img.com/images/movie/54092/315x210.jpg?4163", 108, "携帯小説サイトで映画化を前提に公募した作品の映画化。監督はクラムボンのドキュメンタリー「たゆたう／GOOD TIME MUSIC of clammbon」の高木聡、出演は「トウキョウソナタ」の小柳友、「恋空」の波瑠ほか。静かな港町、ある悲劇から名門高校サッカー部が休部になり、隼人は大きな、溝を残したままサッカー部を退部した。夢をなくし、閉塞した日々を送る隼人だったが、ある日屋上で写真を撮る不思議な少女、瑞生に出会う。", "2008-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ULYSSES", "ユリシーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "古代ギリシャの詩人ホメロスの『オデュッセイア』の映画化。一つ目の巨人、魔女の住む島での冒険など、神話の世界が活劇調に描かれる史劇。製作はC・ポンティ、D・デ・ラウレンティス。脚本はB・ヘクト、I・ショウなど７人があたるという豪華版。", "1954-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Road to Utopia", "アラスカ珍道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "歌手B・クロスビー、コメディアンのB・ホープ、それにグラマー女優D・ラムーアを主役にした痛快ギャグ・シリーズ“珍道中もの”の４作目。「シンガポール」「アフリカ」「モロッコ」に続いて作られたシリーズ中でも出色の１本である。ゴールドラッシュ時代のアラスカ。ラムーアの父は金鉱の地図を盗まれ殺された。偶然その地図を手に入れた芸人クロスビーと相棒のホープは、思わぬ争奪戦に巻き込まれ、ドタバタを演じるはめに……。パロディーとおふざけの連発、明るい笑い、そしてエピローグのジョークと、とにかく最初から最後まで笑わせてくれる。ロバート・ベンチュリーの淡々としたナレーションも、絶妙の隠し味として効いている。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ray", "Ray レイ", "", "", "http://image.eiga.k-img.com/images/movie/1152/315x210.jpg?4109", 152, "本作の完成直前に惜しくも他界した盲目の天才ミュージシャン、レイ・チャールズの生涯を映画化。音楽界のジャンル概念と制度を打ち破ろうとする彼の情熱と苦闘を描く。監督は「愛と青春の旅だち」「ホワイト・ナイツ／白夜」のテイラー・ハックフォード。身振りもそっくりにレイ・チャールズを演じ、オスカー主演男優賞候補と噂されているのは、「コラテラル」でトム・クルーズと共演して注目を集めたジェイミー・フォックス。", "2005-01-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ad Lib Night", "アドリブ・ナイト", "", "", "http://image.eiga.k-img.com/images/movie/53322/315x210.jpg?4447", 99, "長編デビュー作「チャーミング・ガール」で高い評価を受けた韓国のイ・ユンギ監督が、日本の女流作家・平安寿子の同名短編を映画化。ソウルの雑踏の中で、10年前に家出したまま行方がわからない少女ミョンウンに間違えられた若い女性。彼女は成りゆきから、ミョンウンになりすまして危篤状態の父親に会うことになってしまい……。日本でも人気を博した韓国ドラマ「春のワルツ」のハン・ヒョジュがヒロインを好演している。", "2008-02-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Then She Found Me", "いとしい人", "", "", "http://image.eiga.k-img.com/images/movie/54232/315x210.jpg?3521", 100, "39歳の小学校教師エイプリルは、年下の同僚ベンと結婚して間もない新婚だが、年齢的になるべく早く子供を授かりたいと考えていた。しかし突然ベンに別れを告げられ、その翌日には養母が他界し、さらにお騒がせテレビタレントのバーニスが実母だと名乗り出てきた挙句、往年の名優スティーブ・マックイーンが実の父親だと告げられ……。「恋愛小説家」のオスカー女優ヘレン・ハントが初監督＆主演を務め、等身大の30代女性を体当たりで好演。", "2009-03-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sunflower", "胡同のひまわり", "", "", "http://image.eiga.k-img.com/images/movie/33512/315x210.jpg?4535", 132, "1976年、文化大革命後の北京。母と幸せに暮らしていた少年シャンヤンのもとに、強制労働に駆り出されていた父が6年ぶりに帰ってくる。過酷な労働により画家への道を断たれた父は、息子にその夢を託し厳しい教育を施すが、シャンヤンはそれを受け入れられず反発する。「こころの湯」で世界中から賞賛を浴びたチャン・ヤン監督が、激動の時代の中で失われゆく町、胡同を舞台に、父子の30年間にわたる愛と葛藤の日々を描いた感動作。", "2006-07-08", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("同胞", "同胞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "高度経済成長の波から取り残された岩手県の過疎の農村に、移動劇団の公演を村の青年団に依頼するため、東京から一人の女性がやって来る。しかし、65万円という高い公演費用に青年団長は二の足を踏み、なかなか話は進まない。だが、やっとのことで説得に成功するや、青年団の団員たちはあふれる情熱を演劇のPR、切符売り、会場の確保などに注ぎ始め、村は徐々に活気づいていく。岩手県・松尾村で実際にあった話をモデルに、“統一劇場”という新劇サークルを実際に登場させたドキュメンタリー・タッチのシリアスドラマ。プロに混じって村の青年団員たちも多数出演している。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二宮金次郎物語・愛と情熱のかぎり", "二宮金次郎物語・愛と情熱のかぎり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "勤勉家として有名な江戸時代後期の偉人・二宮金次郎の若き日を描いた伝記ドラマ。川の氾濫によって田畑を失い、父親にも死なれ、幼くして一家の働き手となった金次郎。貧困のなかで懸命に働きながら学問を志し、一家の再建を目指すそのたくましい生を、初恋のエピソードを絡めて感動的に描く。「青春デンデケデケデケ」の林泰文が金次郎役を力強く好演。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リムジンドライブ", "リムジンドライブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「JUNK FOOD」の山本政志監督が、ニューヨークを舞台に描くオフビートな人間ドラマ。リムジンカーの運転手として働くマリークは、離婚した妻に一人娘を取られて孤独に暮らす43歳。日本人の渋谷系ギャル・エリに雇われるが、ひょんなことから彼女の面倒を見る羽目になる。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Matinee Idol", "陽気な踊子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "フランク・キャプラ監督によるコメディ。人気喜劇俳優のドンとプロデューサーのウィンゲートは、新作に使う新しいネタを探す旅に出る。ある田舎町で旅芸人の一座に出会い、成行きで彼らに雇われることになってしまったドンは、座長の娘ジンジャーに恋をする。ウィンゲートは一座の芝居を見て、彼らをニューヨークで上演する新作喜劇として売り出そうと考えるが、自分たちが笑いものにされることに我慢ならないジンジャーは……。", "1928-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DARK VICTORY", "愛の勝利", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "父の遺産を受けつぎ、何不自由ない暮らしを営むジュディスは、精神的に空虚な日々を送っていた。ある日、体の具合が思わしくないので医者に診察してもらうが、彼女の命はあと10ヵ月しかもたない状態にあることが判明する。B・デイヴィスが運命に耐えながらも愛に生きる主人公を好演。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVER　GIRL", "Candy Lover Girl", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "夫婦で共同監督をしているリサ＆ジョーの長編デビュー作。風俗店で働く女性たちの明るくたくましい生き方を、色彩豊かな映像でポップに描き出す。16歳の少女ジェイクは、母が恋人と家出してしまい一人ぼっち。一人暮らしの姉を頼ってみるが相手にしてもらえない。気のいい女性と知り合い、成り行きでマッサージパーラーで働くことになった彼女は、自由奔放な人々との共同生活に安らぎを見いだしていく。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOONRIDERS THE MOVIE 「PASSION MANIACS　マニアの受難」", "MOONRIDERS THE MOVIE 「PASSION MANIACS　マニアの受難」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1976年のデビュー以来、常に時代を先取りした音楽を作り続け、日本のポップ・ミュージックシーンに多大な影響を与えてきたロックバンド「ムーンライダーズ」を題材にしたドキュメンタリー。豪華ゲスト陣を迎えて行われた日比谷野外音楽堂での30周年記念ライブの模様を軸に、メンバーや関係者へのインタビュー、アーカイブ映像を交えながら、バンドの30年の軌跡と現在を追う。監督は、これまで数々の音楽映像を手がけてきた白井康彦。", "2006-12-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真救世主伝説　北斗の拳　トキ伝", "真救世主伝説　北斗の拳　トキ伝", "", "", "http://ecx.images-amazon.com/images/I/51TXMvINBDL._SL210_.jpg", 60, "1980年代に社会現象を巻き起こした大ヒットコミックを新たにアニメ化した「真救世主伝説　北斗の拳」5部作の第4作。核戦争によって世界中が荒廃した世紀末。北斗神拳伝承者としての道を絶たれたトキは、北斗神拳を医学として役立てるため女医サラと共に旅に出るが、兄ラオウによって捕らえられ、カサンドラ監獄に幽閉されてしまう。やがてケンシロウに救出されたトキは自らの死期を悟り、ラオウとの約束を果たすことを決心する。", "2008-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAJOR DUNDEE", "ダンディー少佐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "ダンディー少佐は、騎兵隊や民間の人々を虐殺したアパッチを追跡するために、無法者と南軍捕虜の混成部隊を編成する。やがて、メキシコ領に入った一行は、アパッチとの闘い、そして、当時メキシコを侵略していたフランス軍と三つ巴の激しい闘いを始める……。シチュエーション作りの巧みなペキンパーらしく、ストーリーは二転三転するが、見せ場たっぷり、最後まで飽きさせない本格西部劇。だが、この作品は予算をかなりオーバーしたために、ペキンパー自身によれば“編集権を奪われ、ひどいカットをされた”という。実際、その後、1969年の「ワイルドバンチ」までペキンパーはこの業界からほされてしまう。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　坂東玉三郎　鷺娘", "シネマ歌舞伎　坂東玉三郎　鷺娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "歌舞伎の舞台公演をスクリーンでデジタル上映する「シネマ歌舞伎」シリーズ第2弾。歌舞伎俳優・坂東玉三郎が2005年5月に歌舞伎座で上演した「鷺娘」、同年10月に上演した「日高川入相花王」を、高性能HDカメラで撮影しデジタルシネマ化。雪の降る水辺に立つ1本の柳。その下に、蛇の目傘をさした白無垢姿の娘がたたずんでいる。その娘は、実は白鷺の精だった。白鷺の精は華やかな町娘の姿となり、その恋心をさまざまに見せていくのだが……。圧倒的な美しさが国内外で評判を呼び、玉三郎の代表作のひとつとなった演目。", "2006-04-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Eagle Has Landed", "鷲は舞いおりた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "1943年、ナチス親衛隊長ヒムラーは、奇想天外ともいえるチャーチル暗殺計画“イーグル作戦”を実行する。命を受けたシュタイナー大佐を隊長とするナチ・コマンド部隊の精鋭16名は、チャーチルの保養地スタドリーに降り立った……。J・ヒギンズのベストセラーを映画化したスタージェスの痛快作。", "1977-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅路", "旅路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "美貌と知性に恵まれながらも、父の不義の子として生まれてきたという暗い秘密を背負いながら、独り都会の本流に身を置く妙子。そんな彼女のけなげな青春と愛の日々を描いたメロドラマ。松竹女性映画の名匠・中村登監督が、大佛次郎の原作、そして岸恵子＆佐田啓二という時の若手スターを得て製作したものである。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THROW MOMMA FROM THE TRAIN", "鬼ママを殺せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "妻に代表作の原稿を盗まれ、失意のラリーは小さな大学で創作を教える小説家だ。彼の生徒オーウェンは、生まれてからずっと鬼のような母親にいじめられてきたドジ息子。ついに堪忍袋の緒が切れたオーウェンは、ラリーのアドバイスを交換殺人の申し入れと勘違いし、憎きママに対し、完全殺人計画を実行に移すのだが……。『バラエティー』誌のランキング一位に初登場後、連続９週間もトップ10入りしたブラック・コメディ。怪優D・デヴィートの初監督作で、鬼ママ役のA・ラムゼイともども怪演が見もの。ストーリーやカメラワークなど、ヒッチコック作品のパロディーとしても楽しめる。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("駿河遊侠伝 賭場あらし", "駿河遊侠伝 賭場あらし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「座頭市」の生みの親、子母沢寛の原作を新藤兼人が脚本化し、ベテラン森一生がメガホンを執った異色の“次郎長”ものの第１作。旅の坊主に３年の命と占われて、ヤケクソになった清水港の長五郎が、放蕩無頼の結果、イカサマ博奕を打つヤクザになって旅に出る。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　BIG　HEAT", "ビッグ・ヒート／復讐は俺にまかせろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "何者かによって最愛の妻を爆死させられた巡査部長が、警察バッジを叩きつけて執念の単独調査を開始し、ギャングと癒着した署内の腐敗を暴いていくポリス・アクション。F・ラングとしては珍しい社会派的な犯罪告発映画だが、個人的な恨みに駆られた主人公が巨悪に突進していくストーリーには、やはり凄まじい迫力がある。L・マーヴィン扮する凶悪な用心棒が、主人公に協力した娼婦の顔面に煮えたぎるコーヒーをぶっかけるシーンが衝撃的。痛々しい包帯姿のヒロインを演じるG・グレアムの鬼気迫る存在感も光っている。DVDは「フィルム・ノワール・コレクション(2)」に収録。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Panic Room", "パニック・ルーム", "", "", "http://image.eiga.k-img.com/images/movie/1142/315x210.jpg?2310", 113, "夫と離婚し、娘と住む新居に越してきたメグは、その夜、強盗団の侵入に気づき、娘とともにパニック・ルーム＝緊急時の避難用個室に逃れる。が、引っ越ししたばかりのこの部屋の電話がまだ通じていない。しかも、強盗の狙う大金はこの部屋に隠されていた。脚本は、監督とは初コンビの「スネーク・アイズ」のデビッド・コープ。監督と「ファイト・クラブ」で組んだ撮影のコンラッド・W・ホール、編集のアンガス・ウォールが参加。", "2002-05-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ondine", "World Classics＠CINEMA オンディーヌ", "", "", "http://image.eiga.k-img.com/images/movie/55112/315x210.jpg?2705", 103, "世界のオペラやバレエを映画館の大スクリーンで堪能できる「World Classics＠CINEMA」シリーズの1作で、イギリスのロイヤル・バレエによる2009年上演作「オンディーヌ」をデジタルシネマ化。人間の男と恋に落ちてしまった水の精オンディーヌがたどる悲劇を描く。ロイヤル・バレエの最高位であるプリンシバルを長年にわたって務めた日本人トップ・ダンサー、吉田都の可憐で神秘的な演技に注目。", "2010-05-15", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUNGO 日本文学シネマ　黄金風景", "BUNGO 日本文学シネマ　黄金風景", "", "", "http://ecx.images-amazon.com/images/I/51R-8Y%2BK82L._SL210_.jpg", 30, "文豪たちが残した傑作短編を映像化する「BUNGO」シリーズの1作で、太宰治の短編を向井理＆優香共演で描いたラブストーリー。津軽の名家・津島邸に生まれた修治は、幼い頃、人の良い女中のお慶を家来のように扱っていた。やがて青年となり、売れない小説家として冴えない毎日を送っていた修治は、お慶が結婚して幸せに暮らしていることを知る。なぜか素直に喜べず、お慶の訪問を断ってしまう修治だったが……。", "2010-05-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOW TO MARRY A MILLIONAIRE", "百万長者と結婚する方法", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "20世紀FOXが「聖衣」に続いて製作したシネマスコープ第２作。ニューヨークのモデル３人が、いかにして百万長者を見つけ、モノにしていくかをコメディ・タッチで描く。Z・エイキンズの舞台劇の映画化だが、３人の中ではバコールの軽妙な演技が特に光る。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLEAN AND SOBER", "偽りのヘブン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "「バットマン」のM・キートンが正統派人間ドラマの主人公に扮する。麻薬とアルコールに溺れる男が、事業にも失敗し警察にも追われるハメに。すべての道を閉ざされた彼が、いかにして立ち直っていくのか？　その過程を丹念に映し出す。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El Secreto De Sus OJos", "瞳の奥の秘密", "", "", "http://image.eiga.k-img.com/images/movie/55132/315x210.jpg?4053", 129, "09年度米アカデミー賞外国語映画賞を受賞したサスペンス・ドラマ。2000年のブエノスアイレス。刑事裁判所を定年退職したベンハミンは、25年前に担当した未解決の殺人事件についての小説を書くことを決意する。事件当時の職場を訪れ、元上司の検事補イレーネと再会したベンハミンは、イレーネとともに当時の捜査を振り返りながら、殺人事件の裏側に潜む謎に迫っていく……。主人公ベンハミンににアルゼンチンの国民的俳優リカルド・ダリン。監督はファン・ホゼ・カンパネッラ。", "2009-01-01", "9,22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Amadeus", "アマデウス", "", "", "http://ecx.images-amazon.com/images/I/51St0YmKKNL._SL210_.jpg", 160, "ピーター・シェイファーによる戯曲を「カッコーの巣の上で」のアカデミー賞コンビ、製作ソウル・ゼインツ＆ミロス・フォアマン監督で映画化。19世紀の楽聖ウォルフガング・アマデウス・モーツァルトの半生を、彼を妬む宮廷音楽家サリエリの視点から描く。出演はサリエリにF・マーレイ・エイブラハム、モーツァルトにトム・ハルス。1984年度のアカデミー賞では作品賞、監督賞、主演男優賞を含む8部門で受賞した。", "1985-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ますらを派出夫会", "ますらを派出夫会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "商売繁盛の“ますらを派出夫会”で働く“派出夫”たちの活躍でお得意さんが減り、巻き返しを図る“なでしこ派出婦会”。その顧客争奪合戦をコミカルに描いた作品。第１部「ますらを派出夫会」と第２部「お供は辛いねの巻」の２部構成。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUEENS LOGIC", "クイーンズ・ロジック　女の言い分・男の言い訳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "仲間の結婚を祝うために故郷のクイーンズへ集合した幼なじみの男女７人。だが、花婿のルイが式を目前に結婚に対して不安を訴えた。このことは、仲間たちの間にも様々な波紋を巻き起こしていく。青春の終わりを迎えた大人たちの戸惑いをコミカルに描出。人気の個性派俳優たちによる豪華な競演も楽しい。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　PAYS　SANS　ETOILES", "星のない国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "36歳の若さでこの世を去ったG・フィリップが23歳の時に撮った初主演作。100年前の事件を調査しているシモンが、とある町にたどり着く。彼はそこで、美しい女性カトリーヌと出会い、恋に落ちるが……。不思議な運命にほんろうされる男女の悲劇的な愛の行方を描く。", "1946-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAVIOR", "セイヴィア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "かつてイスラム原理主義グループのテロで妻子を亡くし、今は外人部隊としてボスニア・ヘルツェゴビナ紛争に参戦しているギイ。彼は、捕虜の妊婦ベラを実家に送り届ける命令を受けるが……。ボスニア・ヘルツェゴビナ紛争を背景に、争いの愚かさや、その内側でうごめく人間の絆の深さを描いた問題作。製作にO・ストーンが参加している。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRESTON STURGESTHE RISE AND FALL OF AN AMERICAN DREAMER", "プレストン・スタージェス　アメリカンドリーマー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ハリウッド黄金期の1940年代、その頂点に君臨しコメディを愛してやまなかった希代の映画作家プレストン・スタージェス。成功の甘美と挫折の残酷さが表裏一体のハリウッド映画界の中で、風のように駆け抜けた彼の生涯をさわやかに語った１作。彼の監督した12本の映画の名場面を折り込みながら、それらの作品が作られていった背景を、当時彼とともに活躍した様々な人たちのコメントで綴る。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("T.G.M. OSVOBODITEL", "解放者マサリィク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "自由と独立と統一に命を懸けたチェコスロバキア初代大統領T・G・マサリィクのユニークな人生を、同国の切れ味鋭い女流監督V・ヒティロバが映像化したドキュメンタリー。鍛冶職人の従弟から身を起こし、代用教員、大学教授を経て政治の道へと進んだマサリィク。隣人への愛と思いやりに生きた建国の父の姿を等身大で綴っていく。", "1990-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARLEY DAVIDSON AND THE MARLBORO MAN", "ハーレーダビッドソン＆マルボロマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "近未来のカリフォルニア。ハーレーダビッドソンとマルボロマンという、友情に厚い二人のバイク狂がいた。仲間の経営する酒場を救おうと現金輸送車を襲った彼らは思わぬ事件に巻き込まれていく……。「イージー・ライダー」と西部劇をミックスし、古き良きアメリカの男の臭いをプンプンさせた痛快アクション。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明日は咲こう花咲こう", "明日は咲こう花咲こう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "吉永小百合が迷信深い土地で奮戦する保健婦に扮した一編。恋人と口ゲンカして僻地医療に携わる決意を固めた娘・ひろ子は、赴任先の住民の想像を絶する貧困と無知に驚く。不衛生な水を飲み、コンコリ教なる信仰だけで病気を治すという彼らは、彼女をまるで邪魔者扱いするが、やがて赤痢が発生してしまう。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PEAU　D’ANE", "ロバと王女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「シェルブールの雨傘」「ロシュフォールの恋人たち」で知られるジャック・ドゥミ監督によるファンタジー。フランスの童話作家、シャルル・ペローの原作を基に、幻想的な愛の物語が展開する。王女役で格別の輝きを放つカトリーヌ・ドヌーヴに魅せられる。実の父である青の国の王に求婚された王女。悩んだ彼女は赤の国に逃れる。そこで彼女は赤の国の王子と運命的な出会いを果たす。2005年にデジタル・リマスター版が公開された。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BELOVED ENEMY", "市街戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "1920年代のアイルランドを舞台に、アイルランド人の青年とイギリス人の女性との恋愛の葛藤を描くラブ・ロマンス。二人は、思想と政治の違う国情に振りまわされ、愛をとるか、国に従うかで悩む。やがて、国への忠誠を誓う恋人に心を打たれた青年は、国を捨てて彼女との愛に生きる決意をする。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE　ON　THE　RUN", "空駈ける恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "J・クロフォード＆C・ゲーブルの共演作品で、名作「或る夜の出来事」を彷彿させるロマンティック・コメディ。ヨーロッパの公爵との結婚式前夜、金目当ての結婚をいやがる富豪の令嬢サリイは、マイケルと名のる謎の男の協力でホテルから脱出し、飛行機で逃れようとする。マイケルの正体は、結婚の記事を書くべく派遣された新聞記者なのだが……。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Experiment", "エクスペリメント", "", "", "http://image.eiga.k-img.com/images/movie/53812/315x210.jpg?4154", 97, "ドイツ映画「es[エス]」でも題材となった “スタンフォード大学監獄実験”を、エイドリアン・ブロディとフォレスト・ウィテカーのオスカー俳優共演で映画化した心理スリラー。研究所内につくられた監獄で「看守役」と「囚人役」に分かれて過ごすことになった男たちが、実験が進むにつれて極限状態に陥っていく様子を描く。大ヒットTVシリーズ「プリズン・ブレイク」を手がけたポール・シェアリングが監督、脚本も兼ねる。", "2010-12-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN FROM SNOWY RIVER", "スノーリバー ～輝く大地の果てに～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "全米、全豪で大ヒットを記録した青春アドベンチャー・ロマン。オーストラリア国民に親しまれたA・B・“バンジョー”・パタースンの叙事詩を映画化。舞台は1888年のオーストラリア西部。父を亡くした山育ちの青年ジムは、町へ下りて牛成り金のハリソン家に牧童として雇われた。その家の一人娘ジェシカは父親に束縛された生活を送っていたが、彼女の出生にはある秘密が隠されていた。ジムはそんなジェシカと恋に落ちるが、ハリソン氏はそれを快くは思わなかった。ジムの死んだ父は、ハリソン氏とジェシカの母を争った実兄の親友だったからだ……。大自然を背景に世代間の確執を鮮やかに描き出す。", "1982-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SALAAM　CINEMA", "サラーム・シネマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "イランの名匠モフセン・マフマルバフが手掛けたドキュメンタリー。映画100周年記念作品の出演者を一般公募したマフマルバフが、殺到する応募者を面接。カメラは、その模様を淡々と写し出していく。悪役志願者や子連れで面接を受ける中年男、女優になりたい16歳の女の子など、多くの志願者がやってきては、監督から時に厳しい要求をつきつけられる。マフマルバフとやり合った後に面接官にさせられ、情け容赦なく志願者を責め立てる女の子の変貌ぶりが、オーディションにおける支配する者とされる者の立場を象徴しているようで興味深い。", "1995-01-01", "15,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARI E DISPARI", "笑激のギャンブルマンODDS AND EVENS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "コメディ映画のヒット作「風来坊」の爆笑コンビ、ヒルとスペンサーの二人がマイアミを舞台に、あらゆるギャンブルで奇想天外な手を用い、大混乱を起こすコメディ。劇中、ルーレット、ポーカー、ブラック・ジャックなど、多くの賭けごとが次々と登場し、楽しませてくれる。", "1978-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("本命年／BLACK　SNOW", "黒い雪の年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "不良仲間フォークと傷害事件を起こし刑務所入りした慧泉は、模範囚として一年間で出所した。まともな人生を歩もうと決意した彼は少女歌手の送り迎え役をやるうち、次第に生きる喜びを感じ始める。そこにフォークが脱獄したとの報が入ってきて……。原作・脚本は『菊豆』で知られる新人作家、リュウ・ホンでこれが初の脚本作。", "1989-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I LIKE IT LIKE THAT", "ブロンクス・ストリート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ニューヨークのスパニッシュ・タウンを舞台に、貧しくもパワフルに生きる若者たちの姿を描いたストリート・ムービー。20代前半で３人の子持ちのリセットとチノは、貧しいながらも楽しい生活を送っていた。ある夜、停電が起こったどさくさに紛れチノはリセットが欲しがっていたステレオを盗もうとするが、捕まってしまう。夫の代わりにリセットはレコード会社社長プライスの秘書として働き始めるが、チノは妻が浮気していると勘違いしてしまい……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HORROR SCOPEIII", "デビル・コール 魔界からの誘惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "屋内プールで若い女性が、残忍に殺された事件の犯人は、その町の高校のグルーベック校長だった。しかし彼が留置所に入ったあとも次々と残忍な手口での殺人は続く。事件の鍵は魔界からの殺人コール“976−EVIL”だった……。シルヴェスター・スタローンの元夫人B・ニールセン、３代目エマニエル夫人のM・ガブリエル、ジャック・ニコルソンの元愛人K・M・チャンドラーといったスキャンダラスな女優が共演するエロティック・ホラー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fortune Cookie", "恋人よ帰れ！わが胸に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "　ジャック・レモンとウォルター・マッソーの黄金コンビが誕生したコメディ作品。フットボールの試合を撮影中、カメラマンのハリーは選手に激突される。軽症で済んだが、義理の兄にあたる悪徳弁護士の入れ知恵で、重傷に仕立て上げられる。なんと、選手の所属チームなどを相手に損害賠償を請求しよう言うのだ。気の進まないハリーだったが……。弁護士役のマッソーがアカデミー助演男優賞を受賞。", "1967-09-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お墓と離婚", "お墓と離婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "結婚10年目を迎えたお墓のセールスマンの石野と妻、厚子の関係が微妙に軋み始めた。石野は夫婦仲を元に戻そうとあせるが、そんな折、修善寺と名乗る風変わりな客が彼の前に現れ……。劇作家・演出家の岩松了が、夫婦の絆をユニークな視点で描いていく。忌野清志郎の怪演も話題に。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬夜叉／天下覇道の剣", "犬夜叉／天下覇道の剣", "", "", "http://ecx.images-amazon.com/images/I/31D7DET06QL._SL210_.jpg", 98, "週刊少年サンデーやTVで圧倒的な支持を得る、高橋留美子原作の人気アニメの完全オリジナルストーリーによる劇場版シリーズ第３作。妖怪の父と人間の母の間に生まれた半妖・犬夜叉と殺生丸。父から譲り受けた二本の剣のほかに、“叢雲牙（そううんが）”と呼ばれる剣の存在が発覚。天下を制すといわれるこの魔剣をめぐり、兄弟の因縁の闘いが激化、さらに犬夜叉の出生の秘密が明らかになる。", "2003-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPIRIT：STALLION　OF　THE　CIMARRON", "スピリット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ドリームワークス製作の冒険アニメ。開拓前のアメリカ西部を生きる野生馬の波乱に満ちた運命を描き出す。主人公スピリットの“心の声”をナレーションで表現するのはM・デイモン。西部シマロンに生まれた子馬スピリットはたくましく成長し、やがて群れのリーダーとなる。カウボーイの手から逃れた彼は、ネイティヴ・アメリカンのリトル・クリークや雌馬のレインと心を通わせていくが……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スケバン刑事　コードネーム＝麻宮サキ", "スケバン刑事　コードネーム＝麻宮サキ", "", "", "http://image.eiga.k-img.com/images/movie/33522/315x210.jpg?2923", 99, "セーラー服の少女がヨーヨーを武器に悪と戦う「スケバン刑事」の劇場版。4代目麻宮サキを人気アイドルの松浦亜弥が襲名。「バトル・ロワイアルII」の深作健太監督がメガホンを取る。ニューヨークから強制送還された少女K。逮捕された母親の釈放と引き換えに“スケバン刑事・麻宮サキ”として働くことになった彼女は、謎のサイト“エノラゲイ”の捜査を開始するが……。サキの母親役で、初代スケバン刑事の斉藤由貴が特別出演。", "2006-09-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新宿区歌舞伎町保育園", "新宿区歌舞伎町保育園", "", "", "http://image.eiga.k-img.com/images/movie/54032/315x210.jpg?3963", 95, "ミュージカル「テニスの王子様」で人気を博した若手男優を中心に描く青春映画シリーズ“キラキラMOVIES”の第3弾。主演は「テニスの王子様／二人のサムライ The First Game」の鎌苅健太、「地球でたったふたり」の河合龍之介ほか。成り行きで子供を預かることになった歌舞伎町のホスト4人が次第に一致団結していき、保育園を起業するまでを描いた青春コメディ。", "2009-01-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blue Velvet", "ブルーベルベット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "平凡な青年が、ふとしたことをきっかけに悪夢のような世界に引きずり込まれていく様子を描く、鬼才デビッド・リンチ監督によるサスペンス作品。ノース・キャロライナ州ののどかな田舎町。急病で倒れた父を見舞うために帰郷した青年ジェフリーは、病院からの帰り道、切り落とされた人間の片耳が野原に落ちているのを発見する。警察に通報した彼は、ドロシーという女性が事件にかかわっていることを知り、好奇心から彼女に接近するが……。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("にっぽん三銃士 博多帯しめ一本どっこの巻", "にっぽん三銃士 博多帯しめ一本どっこの巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "前作と同じスタッフ・キャストによる「にっぽん三銃士」第２部。東京をおさらば、きれいさっぱり過去と決別した３人が、九州・博多を舞台にシンジケートの大組織と闘う。仲間の説得で殴り込みを断念するラストシーンに、各世代に共通する挫折感が表現されている。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄金の犬", "黄金の犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "北海道で飼主とはぐれてしまった紀州犬のゴロは、帰巣本能で東京に向かう途中、武器輸出の汚職事件に巻き込まれた通産省の役人に助けられる。汚職の秘密を握る彼は、マイクロ・フィルムをゴロの首輪に取り付けるのだが……。北海道、青森、東京と大がかりなロケを敢行した、サスペンス・アクション。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALLEMAGNE ANNE´E 90 NEUF ZERO", "ゴダールの新ドイツ零年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "1990年代ゴダールの幕開けを高らかに宣した、内省的な、そして孤独な映像詩。ベルリンの壁が崩壊した1990年。旧東ベルリンに潜入していたスパイ、レミー・コーションを捜し出すべく、元軍事情報部のゼルテン伯爵が動いていた。やがてゼルテンは、小さな町の美容院に身を隠していたコーションを発見、二人は西を目指す……。旧東ベルリン市内をめぐりながら、次々と引用されるドイツ文学、ドイツ的なるものへのオマージュと追想。切り刻まれた音（楽）と、孤独の翳りを帯びた映像のポエジー。1980年代を“光への闘争”としてきたゴダールがたどりついた、哲学する映画とも言いうる一編。主役には、「アルファヴィル」のE・コンスタンティーヌ。当初は58分の作品だったが、ヴェネチア映画祭に出品する際、出品規定（60分以上）に合わせるべく４分が追加され、現在ある形となった。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海市蜃僂／MIRAGE", "天山回廊／ザ・シルクロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "腕も度胸もある冒険児タン。物語は、そのタンがひょんなことから偶然に見かけた絶世の美女を捜しに大冒険を繰り広げるというもの。かつて東西交易路の重要な役割を果たしていたシルクロードを舞台にした、バイオレンス・アクション。", "1987-01-01", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とっとこハム太郎／ハム太郎とふしぎのオニの絵本塔", "とっとこハム太郎／ハム太郎とふしぎのオニの絵本塔", "", "", "http://ecx.images-amazon.com/images/I/61DAEBMPDJL._SL210_.jpg", 40, "ハム太郎が大冒険する人気TVアニメの劇場版シリーズ第４作。不思議なけむりに巻き込まれ、ハムちゃんずが辿り着いたのは不思議な絵本塔。そこで作家ハムスターの女の子と出会う。絵本書きに行き詰まっている女の子のお話作りを手伝うために、ハムちゃんずは絵本の世界に飛び込んでいくが……。", "2004-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("七人の侍", "七人の侍", "", "", "http://ecx.images-amazon.com/images/I/51Ka0RNI-AL._SL210_.jpg", 207, "数多くの傑出した黒澤監督作品の中でも、特に観客のみならず世界中の映画人に多大な影響を与えた代表作。これ以降「荒野の七人」「宇宙の七人」など、この映画を手本とした作品が多く作られたのは周知の事実。時は戦国時代のとある貧しい農村。農民たちは野盗と化した野武士たちの襲撃を恐れ、おののいていた。そこで村を守るために用心棒を雇うことを決意、食うに窮する七人の侍を探し出し、彼らとともに野武士に対抗すべく立ち上がる……。複数のカメラワークによる迫力ある立ち回りのシーンは見事。", "1954-04-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("柳生連也斎・秘伝月影抄", "柳生連也斎・秘伝月影抄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "宮本武蔵の弟子である鈴木綱四郎と、尾州藩の指南役、柳生兵庫介の息子・兵介は、幼い頃からの友だちであったが、成長してライバル同士となっている。恋仇でもある二人は、運命に導かれて凄絶な果たし合いをすることになる……。五味康祐の原作の映画化。クライマックスで壮絶な剣劇が展開するバトル時代劇。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("未来の想い出 Last Christmas", "未来の想い出 Last Christmas", "", "", "http://ecx.images-amazon.com/images/I/518G0CF1XXL._SL210_.jpg", 118, "不慮の死を迎えた漫画家と主婦が、記憶は現在のまま、10年前の世界で蘇る。人生をやり直す2人の女の姿を通じて、人生の楽しさ悲しさを描くファンタジー。藤子・F・不二雄がこの映画のために書き下ろした『ビッグコミック・スピリッツ』連載の同名漫画をもとに、森田芳光がSFに初挑戦。当時18歳の狂言界のホープ、和泉元彌が俳優デビューしたのも話題に。", "1992-08-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("県庁の星", "県庁の星", "", "", "http://image.eiga.k-img.com/images/movie/1472/315x210.jpg?1309", 131, "同名ベストセラー小説を「踊る大捜査線」シリーズの織田裕二主演で映画化する痛快コメディ。共演は「メゾン・ド・ヒミコ」の柴咲コウ。監督はTVドラマ「白い巨塔」（03～04）「ラストクリスマス」の西谷弘。上昇志向の県庁のキャリア公務員・野村は、民間企業との人事交流研修のメンバーとなり、三流スーパーに派遣されることに。彼は年下のパート店員・二宮に指導されるが、マニュアル主義の野村と現場主義の二宮は対立していく。", "2006-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In the Land of Women", "ランド・オブ・ウーマン　優しい雨の降る街で", "", "", "http://ecx.images-amazon.com/images/I/51F6DZMqtVL._SL210_.jpg", 98, "", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Amants Reguliers", "恋人たちの失われた革命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 182, "「白と黒の恋人たち」の名匠フィリップ・ガレル監督が、パリの五月革命を背景に若者たちの情熱や絶望を描き、2005年ベネチア国際映画祭で監督賞を受賞したラブ・ストーリー。1968年、兵役を拒否した20歳の詩人フランソワは、革命に燃えるパリでデモに参加する日々を送っていた。そんなある日、彼は彫刻家志望の女性リリーと出会い恋に落ちるが……。主演は、ガレルの息子で「ドリーマーズ」で注目されたルイ・ガレル。", "2007-01-02", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラマンティガ＆　ウルトラマンダイナ＆ウルトラマンガイア　超時空の大決戦", "ウルトラマンティガ＆　ウルトラマンダイナ＆ウルトラマンガイア　超時空の大決戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "TVで放映されていた『ウルトラマンガイア』の劇場版。最新デジタルSFX満載で、ガイア、そしてウルトラマンティガ、ウルトラマンダイナの３大ヒーローの活躍を描く。テレビで超人気特撮番組『ウルトラマンガイア』が放映されている異次元の世界に住む少年、勉は願いをかなえてくれる赤い玉を手に入れ、XIG隊員、我夢ことウルトラマンガイアを呼び寄せる。だが、今度は勉の上級生たちが赤い玉に怪獣の出現を念じてしまい……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE まさお君が行く！", "LOVE まさお君が行く！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "テレビ東京系の長寿番組「ペット大集合！ポチたま」（2000年10月～10年3月）で人気を博したコーナー「まさお君が行く!!ポチたまペットの旅」を原案に、食いしん坊で天然なラブラドールレトリーバーのまさおと、コンビの相方にも同棲していた恋人にも去られてしまい伸び悩む若手芸人の松本が、全国を行脚しながら絆を深めていく姿を描く。主人公の芸人・松本秀樹役で香取慎吾が主演。松本の元恋人・里美役で広末涼子、番組AD役で成海璃子らが共演する。監督は「NANA」「ランウェイ☆ビート」の大谷健太郎。", "2012-06-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水の花", "水の花", "", "", "http://image.eiga.k-img.com/images/movie/33592/315x210.jpg?2757", 92, "「運命じゃない人」「バーバー吉野」の大ヒットで注目を浴びるPFFスカラシップの第15回作品。幼い頃に母に捨てられ、父とふたりで暮らしている中学生の美奈子。母が他の男のもとへ行ってしまった事は、彼女にとって大きなトラウマになっていた。ある日美奈子は、母が男との間に産んだ娘、優を連れて町に戻ってきていることを知る。優がひとりでいるところを見かけて声を掛けた美奈子は、彼女を連れて亡き祖父母の家へと向かう。", "2006-08-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／A　GOOD　LAWYER’S　WIFE", "浮気な家族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "韓国・ソウルの高級住宅地に住む30代の主婦ホジョンは弁護士の夫ヨンジャクと７歳の息子スインの３人暮らし。一見、裕福で幸せそうに見えるが、家族はそれぞれに人には言えない事情を抱えている。夫は浮気に忙しく、スインは自分が養子であることに戸惑っていた。一方、ホジョンもある秘密を持ち始め……。「ディナーの後に」のイム・サンス監督が韓国社会の現実を鋭い視点で描出。「オアシス」のムン・ソリが、隣家の少年を誘惑する主婦に扮して大人の色香を漂わせる。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH−BALLIN’", "ハイローリング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "巧妙な手口でハイウェイを走っているトラックをジャック、車体を塗装し直しては売り飛ばしているハイジャッカーの一味と、親友のトラック仲間を救うため一味に挑む男との壮絶な戦いを描いたアクション。主人公が40台の18輪トラックを率いて一味のアジトに乗り込む様は圧巻だ。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUR OF THE ASSASSIN", "大統領暗殺指令", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "数十年ぶりに民間からの大統領が生まれようとしている中南米のサン・ペトロ共和国で、着々と進む軍部の陰謀。秘密警察のチーフ、フォルコ将軍は自らの利権を守るために大統領への就任間近となったロバート・ビラバードの暗殺を計画する。主人公のフィエロは父を死に追いやった軍部への復讐心を逆に利用され、殺し屋としてかりだされそうになるが……。CIAの冷酷な諜報員には、R・ヴォーンが扮している。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裸 over8", "裸 over8", "", "", "http://ecx.images-amazon.com/images/I/41gSJvPM8AL._SL210_.jpg", 90, "気鋭の若手監督5人によって結成されたプロジェクト「over8」の第2弾となるオムニバス映画。映画祭で数々の受賞歴を持つ前田弘二、ドキュメンタリー映画「Fragment」をヒットさせた佐々木誠、俳優としても活躍する川野弘毅、短編作品「サイヴァイ」で国際的にも注目された桑島岳大、松江哲明監督作「童貞。をプロデュース」に“童貞”として出演した加賀賢三ら、個性豊かな監督たちが「裸」をテーマに描く。", "2007-09-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一騎当千　集鍔闘士血風録", "一騎当千　集鍔闘士血風録", "", "", "http://image.eiga.k-img.com/images/movie/57252/315x210.jpg?2245", 0, "「三国志」の英雄たちの宿命を引き継いだ高校生たちの戦いを描く塩崎雄二の同名コミックを原作に、2003年～10年まで計4シーズンが放映された人気アニメーションのOVA。孫策伯符率いる南陽学院の面々は、英雄の魂が封じ込められた勾玉の霊力を補充するため京都へ「集鍔（しゅうがく）旅行」に旅立つ。しかし、京都でライバル校の劉備率いる成都学園、曹操率いる許昌学院と出くわしてしまい、新たな戦いが勃発する。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RED DUST", "紅塵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "インドシナの農園で働くアメリカ人青年デニス。彼は長期滞在中の若い娘バンティーンと愛し合っているものの、その積極的なアプローチを持て余し気味だ。やがて、彼は、赴任してきた若夫婦の美しい妻バーバラとひそかな愛情を確かめるようになる。が、三角関係に悩むバーバラは、自制を失って彼を銃で撃ってしまう……。後にジョン・フォード監督が「モガンボ」としてリメイクし、C・ゲーブルは同じ役を演じている。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("憐 Ren", "憐 Ren", "", "", "http://image.eiga.k-img.com/images/movie/53802/315x210.jpg?2475", 101, "人々を「有用」と「不要」に分ける500年後の未来から、「不要」であるために「過去流しの刑」で現代に送られてきた朝槻憐。未来世界のコンピュータ「時の意思」により、憐は学校と自宅以外では誰にも認識されない「孤独の刑」も与えられていた。しかしクラスメイトの鳴瀬玲人だけは憐を認識し次第に惹かれていくが、ある日謎の転校生シュウがやってきて……。主演は「赤い糸」の岡本玲、「カフェ代官山」の馬場徹、原作は水口敬文の同名ライトノベル。", "2008-07-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOXIC AFFAIR", "可愛いだけじゃダメかしら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "アジャーニが「カミーユ・クローデル」以来、４年ぶりに主演したラブ・コメディ。24歳のモデル、ペネロープは最近仕事に身が入らず、精神分析医に通っている。恋人からも、別れ話を持ちだされ、情緒はいよいよ不安定になって……。美人なのに男運がない、可愛い女の心揺れる３日間を描いている。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　GRASS　HARP", "グラスハープ 草の竪琴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "カポーティの自伝的小説『草の竪琴』の映画化。両親と死別した孤独な少年コリンが、遠縁にあたる独身の姉妹ベレーナとドリーの屋敷に引き取られる。やがてドリーに秘かな思いを寄せる引退した老判事チャーリーとも親しくなり、コリンの周囲に家族的な愛の輪が広がっていく。監督のC・マッソーは、チャーリー役で味のある助演を見せる名優W・マッソーの息子。1940年代のノスタルジックな雰囲気を再現したロケ地、アラバマ州の自然が美しい詩情を漂わせる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCHWARZ UND WEISS WIE TAGE UND NA¨CHTE", "昼と夜のような白と黒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "チェスに取り憑かれた男が、その狂気のうちに破滅していくまでを描いたサスペンス・ドラマ。コンピュータ・プログラマーが、コンピュータを使ってチェスの世界チャンピオンに挑んでいくのだが……。勝負に燃え狂っていくその姿を、「ベルリン・天使の詩」の天使役の好演で知られるB・ガンツが見事な演技で見せる。", "1978-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMMANUELLE ５", "エマニエル５", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "『PENTHOUSE』誌の元モデルだったM・ガブリエルが３代目エマニエル役で初登場したシリーズ第５作。映画女優としての華々しいデビューを遂げたエマニエルは、アメリカ人大富豪と奔放な愛を繰り広げ、いっそう妖艶になっていく。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("薄雪太夫より 怪談千鳥ヶ淵", "薄雪太夫より 怪談千鳥ヶ淵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "千鳥ケ淵で薄雪太夫と心中を図った美之介は、つらくも一人生き残ってしまう。その後、彼は別の女性を妻にして人生をやり直そうとするが、やがて太夫の怨霊に導かれて、かつて入水した千鳥ヶ淵へと舞い戻っていく。時代劇スター中村錦之助（後の萬屋錦之介）が若き日に主演した怪談映画。その端正な二枚目ぶりも魅力的。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙戦艦ヤマト2199 第一章「遥かなる旅立ち」", "宇宙戦艦ヤマト2199 第一章「遥かなる旅立ち」", "", "", "http://image.eiga.k-img.com/images/movie/57462/315x210.jpg?4387", 50, "1974年にTV放送されブームを巻き起こし、その後も数多くのシリーズ作品が作られてきた名作アニメ「宇宙戦艦ヤマト」を新たなスタッフで描くTVシリーズ。第1作をベースに、ガミラスの侵攻を受けて危機に陥った地球を救うため、16万8000光年の彼方にある惑星イスカンダルを目指す宇宙戦艦ヤマトの旅路を描く。全26話を7章に分けて映画館でイベント上映（第一章は1～2話、第二章以降は各4話ずつ）。総監督は「機動戦士ガンダム　逆襲のシャア」「機動警察パトレイバー」のメカデザインや「ラーゼフォン」で知られる出渕裕。キャラクターデザインは「ロードス島戦記」の結城信輝。音楽はオリジナルを手がけた故・宮川泰の実子・宮川彬良。主題歌をオリジナルと同じささきいさおが歌う。", "2012-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Deaths of Ian Stone", "今日も僕は殺される", "", "", "http://image.eiga.k-img.com/images/movie/53842/315x210.jpg?2967", 87, "VFX界の巨匠スタン・ウィンストン製作のサスペンスで、“死”を繰り返す男の絶望と恐怖を描く。主演は「クローバーフィールド／HAKAISHA」のマイク・ボーゲル。アイスホッケーのスター選手のイアンは、何不自由ない生活を送っていたが、試合に負けたある日、踏切で死体のようなものを見つけ、近づくとそれに襲われ列車に轢かれてしまう。しかし翌日目覚めると、イアンは会社員としてオフィスにいた……。", "2008-09-06", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THRILLER ZONE", "スリラーゾーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "とある田舎町を訪れた女教師を襲う恐怖を描く「魔女狩り」。40年ぶりに冷凍睡眠から目覚めた超能力者を描く「パラダイスの異邦人」。二つのエピソードを描いたホラー・オムニバス作品。「ハロウィン」シリーズの個性派俳優、D・プレゼンスが出演。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("КОТЁНОК／THE　KITTEN", "こねこ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "寒々しい冬のモスクワの街角。一匹の子猫の小さな冒険を綴る温かい物語。ペット市場で売られていた一匹の子猫は、ある音楽一家に貰われて“チグラーシャ”と名づけられる。やんちゃなチグラーシャはある日、窓からトラックの荷台に落ちてしまう。着いた先でワーシャという猫に助けられ、たくさんの迷い猫を自宅で世話している雑役夫の男の元で様々な猫たちに出会う。だが男が部屋を追い出されてしまい……。雑役夫役の役者は、世界でも珍しいといわれる本物の猫の調教師。猫たちの活き活きとした動きと、そのかわいらしさに思わず顔がほころぶ。猫好き、動物好きを越えて好評を博し、公開当時ロングランヒットとなった。", "1996-01-01", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAKOTA", "ダコタ高原", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "1880年代、鉄道建設によるダコタの土地の値上がりを見越して、暴力で土地を奪おうとする悪漢と、それに対抗する農民たちとの争いを描いた西部劇。鉄道王の娘と駆け落ちしてダコタにやって来たカウボーイが、やがて農民側のリーダーとなり悪と戦っていく。ビデオタイトルは「ダコタ荒原」。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lassie", "名犬ラッシー", "", "", "http://image.eiga.k-img.com/images/movie/33772/315x210.jpg?4149", 100, "これまで何度も映画やアニメ化され愛され続けてきた名作小説を基に、少年と犬の友情を描いた感動作。イギリスの炭坑町で暮らす少年ジョーは、コリー犬ラッシーと大の仲良し。しかし、生活に困った両親がラッシーを裕福な公爵に売ってしまう。やがてラッシーは遠く離れたスコットランドへと連れて行かれるが……。少年ジョーを演じるのは映画初出演のジョナサン・メイソン。共演はピーター・オトゥール、サマンサ・モートンほか。", "2006-12-23", "33,3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋極星", "恋極星", "", "", "http://image.eiga.k-img.com/images/movie/54222/315x210.jpg?3508", 103, "「デスノート」シリーズの戸田恵梨香と「ギララの逆襲」の加藤和樹共演によるラブストーリー。かつて“星の降る町”と言われていた北海道の小さな町。19歳の菜月は、両親を亡くして以来、知的障害の弟をひとりで抱えて生きてきた。そんな彼女の前に、11年間も音信不通だった初恋相手・颯太が現われる。彼の存在によって少しずつ笑顔を取り戻していく菜月だったが……。世界的に活躍する女性写真家・AMIY MORI監督による美しい映像にも注目。", "2009-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Drillbit Taylor", "Mr.ボディガード　学園生活は命がけ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天国と地獄", "天国と地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "エド・マクベインの原作を得て、黒澤明監督が映画化した全編息づまるサスペンス。製靴会社の専務権藤の息子と間違えられて、運転手の息子が誘拐された。要求された身代金は三千万円。苦悩の末、権藤は運転手のために全財産を投げ出して三千万円を犯人に受け渡し、無事子供を救出する。非凡な知能犯の真の目的とは。鉄橋を利用した現金受け渡しのシーンは秀逸で、実際にこれを模倣した誘拐事件が発生した。また白黒作品であるにもかかわらず、最も重要なシーンで一個所のみ着色を施すなど新たな演出も印象深い。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cremaster 3", "クレマスター3", "", "", "http://image.eiga.k-img.com/images/movie/33502/315x210.jpg?4526", 182, "妻ビョークとのコラボレート作品「拘束のドローイング９」が話題を呼んだ現代美術家マシュー・バーニーが手がける5本のアート・ムービー「クレマスター・サイクル」のシリーズ最終作。ニューヨークのクライスラービルとグッゲンハイム美術館を舞台に、ケルト神話、ミステリー、アクション、ミュージカルの要素が複雑にからみ合う。出演はバーニー自身のほか、彫刻家リチャード・セラや義足のアスリート、エミー・マランス。", "2006-07-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALONE IN THE DARK", "ジャンク・イン・ザ・ダーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ニュージャージー州の精神病院の患者たちが脱走し、かねてより不信をいだいていた新任医師の家庭を襲う。後に「ヒドゥン」で話題を集めたJ・ショルダー監督のデビュー作。J・パランスが脱走患者たちのリーダー格として怪演。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AIGLE A｀ DEUX TE＾TES", "双頭の鷲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1946年初演のJ・コクトーの同名の戯曲を自らが映画化したコスチューム・ドラマ（時代劇)。舞台でも主役フイエールとマレーは同じ役を演じ大成功を収めた。とある王国でのこと。ある嵐の晩、無政府主義者の詩人スタニスラスは、警察に追われて城に逃げ込む。城では美貌の王妃が自室に閉じこもって亡き夫、フレデリック王の思い出に浸っていた。そこへ転がり込んできた、亡き王と瓜二つのスタニスラスと王妃の悲恋。舞台の名女優フィエールの貫禄、ラストで階段からの逆さ落ちを見せるマレーの力演、絢爛豪華な美術と衣装など見せ場たっぷりの名作。", "1947-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LATINO", "ラティノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "アメリカン・ニューシネマを支えた名カメラマンとして知られるH・ウェクスラーが、「アメリカを斬る」に続いて自ら監督した意欲作。メキシコ系アメリカ人でグリーンベレー隊員の主人公が、認識票すら身につけず、ニカラグアのゲリラ戦へ出撃を命じられる……。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANDRE：BASED ON A TRUE STORY", "アンドレ 海から来た天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "傷を負い、小さな漁港に打ち上げられたアザラシのアンドレを救い出したハリー一家。動物好きな娘のトニーは必死に看病し、元気になったアンドレを学校に連れて行く。いつしかアンドレはアメリカ中の人気者に……。1960年代アメリカの実話に基づいた、心温まる感動のドラマ。なお、監督のG・ミラーは「マッドマックス」の監督とは別人。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE PIECE ワンピース", "ONE PIECE ワンピース", "", "", "http://ecx.images-amazon.com/images/I/61nfVrVLsNL._SL210_.jpg", 50, "海賊王を夢みる少年モンキー・D・ルフィと仲間たちが繰り広げる大冒険を描いた、尾田栄一郎原作による人気コミックの劇場版アニメ第1弾。「ひとつなぎの大秘宝（ワンピース）」を求めて航海を続けていたルフィたちは、海賊エルドラゴに捕らわれていた少年トビオを救う。世界の3分の1の黄金を手にしたという大海賊ウーナンにあこがれるトビオの話を聞き、ウーナンが黄金を隠したとされる島へ向かうルフィたちだったが……。", "2000-03-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("soeur スール TWILIGHT FILE IV", "soeur スール TWILIGHT FILE IV", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "オムニバスシリーズ「TWILIGHT FILE」第4弾の1作。外見も性格も正反対の姉妹が織りなす人間模様を描いた、宝生舞主演によるドラマ。新人フォトグラファーの姉・夕美と、売れないモデルの妹・小夜は、時にはケンカしながらも互いに支えあって生きてきた。そんなある日、小夜が撮影した写真が、夕美が働くファッション誌「月間soeur」の表紙に抜擢され……。「あのころ…Summer Memories」の西川文恵がメガホンを取り、姉妹の心の機微を詩情たっぷりに描きだす。", "2007-11-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サーキットの狼", "サーキットの狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "矢田部モータースで働く風吹裕矢は、休日にはいつもサーキットに出かけていた。ある日、偶然から出場したレースで優勝したことがきっかけとなり、次から次へとレースに出場し賞金を稼ぎ続けていく。そして、ロータスの技師であった、今は亡き父の達成できなかった夢をやりとげるべく名車ロータス・ヨーロッパを入手する……。当時、スーパーカー・ブームの火付け役となった同名の漫画の映画化。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雪之丞変化", "雪之丞変化", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "三上於菟吉の原作を、伊藤大輔と衣笠貞之助が脚色、さらに和田夏十が潤色した時代劇の傑作。1935年版で三役をこなした長谷川一夫は、ここでも雪之丞と闇太郎の二役を演じており、30年近い年月の隔たりを感じさせない。長谷川一夫300本記念映画。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　笑ってさよなら　四畳半下請け工場の日々", "劇場版　笑ってさよなら　四畳半下請け工場の日々", "", "", "http://image.eiga.k-img.com/images/movie/57392/315x210.jpg?2935", 60, "自動車産業の4次下請けを担う小さな町工場が、不況のあおりを受けて閉鎖を決意するも、笑顔で日々を送り続ける姿を追ったドキュメンタリー。名古屋市南区で小さな工場を営む小早川弘江さんは、近所の主婦2人と一緒に楽しくおしゃべりをしながら1個5円の部品を1日数千個作り続ける日々を送っていた。しかし、2008年のリーマンショックが発端の円高にトヨタ自動車の輸出産業が直撃を受けた時期は、半年間仕事がなく、当たり前にあると思っていた仕事がいつなくなるかも知れないと知った小早川さんは、工場の閉鎖を決意する。最近ではエコカーブームで工場は再び忙しさを取り戻し、周囲からも引き止められたが、小早川さんの決心は変わらず、工場最後の日が訪れる。名古屋の放送局CBCが製作したドキュメンタリーで、モンテカルロ・テレビジョン・フェスティバルのニュースドキュメンタリー部門最優秀賞を受賞した。", "2012-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("侠客列伝", "侠客列伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "マキノ雅広監督・高倉健以下オールスター・キャストによる“列伝”もの。明治末期、賭博行為禁止条項が帝国議会を通過したのをきっかけとし、当局の方針にこたえようとする関東の一派と、愛国団体の仮面をかぶり全国制覇をたくらむ関西一派の、血で血を洗う対決を描く。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COLD FEET", "チキンハート・ブルース サギ師と娼婦と殺し屋と", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "小心者の泥棒モンティ。彼にぞっこんの娼婦モリーン。自然食志向の殺し屋ケニー。３人はエメラルドをアメリカに密輸しようとたくらむが、モンティが途中で宝石を持ち逃げし、モリーンとケニーは後を追う。果たしてエメラルドは誰の手に？　エキセントリックでシニカルで、どこかトボけたロード・ムービー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HANGMEN", "ハングマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "元グリーンベレーのCIA工作員が、反乱分子の疑いをかけられ本部に狙われる。彼は昔の仲間を集めて襲撃部隊に立ち向かう。銃撃スタントマンのウォシュバーンが主演しているだけに銃器類へのこだわり方は異常なほど。マニア好みのガン・アクション映画。ビデオ発売時タイトルは「サンドラ・ブロック in ザ・エスケープ」。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("This Means War", "Black & White ブラック＆ホワイト", "", "", "http://image.eiga.k-img.com/images/movie/57472/315x210.jpg?3046", 98, "「スター・トレック」のクリス・パインと「インセプション」のトム・ハーディが共演したアクションロマンス。任務に失敗して謹慎処分中のCIAエージェント、FDRとタックは、奔放な美女ローレンと出会う。しかし、ローレンが二股をかけていることがわかると、2人は彼女がどちらを選ぶかをかけて、盗聴器や麻酔銃、無人偵察機などCIAのハイテク技術を濫用したなんでもありのバトルを繰り広げる。2人の戦いに巻き込まれるローレン役にリース・ウィザースプーン。監督は「チャーリーズ・エンジェル」のマックG。", "2012-04-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("特警新人類／GEN-X COPS", "ジェネックス・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "香港の若手スター共演で話題を呼んだ刑事活劇。香港警察で爆破事件が起こった。これを解決すべく起用されたのが、警察学校の生徒３人組。落ちこぼれだが、それぞれに個性的な彼らは危機を乗り越え、マフィアや日本人ヤクザに立ち向かっていく。俳優陣のイキのいいアクションはもちろん、「インデペンデンス・デイ」のSFXスタッフを招いて生み出した壮絶な爆破シーンも見もの。仲村トオルが日本人ヤクザのボスを演じている。", "1999-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("O Cheiro do Ralo", "下水って、匂う。", "", "", "http://image.eiga.k-img.com/images/movie/54782/315x210.jpg?4326", 95, "「クアトロ・ディアス」のセルトン・メロが主演を務めるブラジルのブラック・コメディ。ロウレンソが経営する骨董店には、なぜかいつも下水のにおいが漂っている。店には金に困った人々が毎日のように品物を売りにやって来るが、ロウレンソはいつも彼らを冷たくあしらうばかり。ある日、ロウレンソは偶然目にしたウェイトレスの完璧な尻に魅せられてしまう。その時から彼の日常は徐々に異常をきたしはじめ……。", "2009-10-04", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRIME TARGET", "プライム・ターゲット 標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "FBIから２万5000ドルの報酬で、マフィアのドン護送を依頼された、はみだし刑事のジョン。しかし、彼らの行く手には、マフィアのヒットマンやFBIの陰謀が立ちはだかる。主演のD・ヘベナーが、自ら製作、監督、脚本、主演、主題歌と５役をこなしたアクション。マフィアのドン役で、T・カーティスが出演している。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Renaissance", "ルネッサンス", "", "", "http://image.eiga.k-img.com/images/movie/34062/315x210.jpg?1396", 106, "ある誘拐事件を追う警部が人類の未来を左右する巨大な陰謀に巻き込まれていく姿を、コントラストの効いた斬新なモノクロ映像で描いた近未来SFアクション。発展と荒廃が混在する2054年のパリで、医療関連企業の女性研究者イローナが何者かに誘拐された。捜査を開始したカラス警部は、イローナが子どもを襲う早老症の治療を研究していたことを知る。主人公カラス警部の声を務めるのは「007／カジノ・ロワイヤル」のダニエル・クレイグ。", "2007-07-14", "2,3,58", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("侠女", "侠女　第一部：チンルー砦の戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "原作は明末清初期の作家、蒲松齢が書いた「柳斎志異」というの伝奇小説集の一編である。宦官の陰謀で処刑された忠臣の娘の復讐劇を描いた、アジアの巨匠キン・フーの代表作。一部二部あわせて3時間強という、75年カンヌ国際映画祭高等技術委員会グランプリに輝く武侠映画の大傑作である。", "1971-01-01", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カクトウ便 Vol.2 VS謎の恐怖集団人肉宴会", "カクトウ便 Vol.2 VS謎の恐怖集団人肉宴会", "", "", "http://image.eiga.k-img.com/images/movie/53512/315x210.jpg?2028", 90, "危険な仕事も請負う裏の運び屋“カクトウ便”のメンバーである遠藤は、私生活ではアイドルのおっかけに精を出すアキバ系オタクだった。お気に入りのアイドル、風花が恐ろしい人肉愛好家たちに狙われていることを知った遠藤は、彼女を救うため立ち上がるが……。日本のみならず香港でも活動する谷垣健治がアクション監督を務める本格アクション・シリーズの第2弾。出演は「妄想少女オタク系」の木口亜矢ほか。", "2008-03-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN HARM’S WAY", "危険な道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "O・プレミンジャーによる豪華キャストの戦争ドラマ。1941年、日本軍による真珠湾攻撃で傷を負ったトリー艦長は、海軍病院でマギーという看護婦と出会う。地上勤務を命ぜられていた彼は、やがて日本への反攻作戦を一任され……。70ミリ、白黒の大作。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Monsoon Wedding", "モンスーン・ウェディング", "", "", "http://image.eiga.k-img.com/images/movie/1542/315x210.jpg?4420", 114, "現代のニューデリー。華麗な伝統的結婚式がくり広げられるなか、花嫁アディティの不倫、彼女と父の絆、不器用なウェディング・プランナーとメイドの恋など5つのストーリーが並行して展開していく。監督は「サラーム・ボンベイ！」「カーマ・スートラ　愛の教科書」のミーラー・ナーイル。01年ベネチア映画祭でインド人女性監督初の金獅子賞を受賞。撮影は「リービング・ラスベガス」のデクラン・クインが担当。", "2002-08-17", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダー555〈ファイズ〉／パラダイス・ロスト", "仮面ライダー555〈ファイズ〉／パラダイス・ロスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "人気特撮シリーズをモチーフにしたSF活劇。一万人のライダーが登場するスペクタクルは見逃せない。人類の進化形オルフェノクが人間を支配しようとしている未来世界。人類の存亡は“帝王のベルト”と呼ばれる強大なパワーを秘めた武器を見つけることにかかっていた。記憶喪失の青年・巧は、その使命に目覚め、やがて戦いを決意し……。", "2003-08-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("She's Out of My League", "ある日モテ期がやってきた", "", "", "http://ecx.images-amazon.com/images/I/51qPLOV4AML._SL210_.jpg", 104, "「ミリオンダラー・ベイビー」「トロピック・サンダー／史上最低の作戦」のジェイ・バルチェル主演のラブ・コメディ。平凡な容姿の空港警備員カーク（バルチェル）は、荷物検査での忘れ物がきっかけで知り合った美女モリーからデートに誘われる。自分自身はもちろん、彼の親、友だち、元彼女までもががく然とするなか、カークは自分とは釣り合いがとれないほど高レベルな美女モリーとの愛を成就させようと奮闘するが……。監督はイギリスのコメディアン兼放送作家のジム・フィールド・スミス。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEX AND THE LORD OF THE DEEP", "魔境のガン・ファイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "砂塵舞う荒野で起きた襲撃事件。犯人を追うテキサス・レンジャーと古代アステカの末えいの壮絶なガン・ファイトが展開する。ジェンマのアクションが冴える、マカロニ・ウエスタンの要素を盛り込んだSFXアドベンチャー。", "1985-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Roommate", "ザ・ルームメイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ロサンゼルス近郊の大学に入学したサラは学生寮でレベッカという女性とルームメイトになる。お互いに協力し友情を育んでいた2人だったが、サラに悪友やボーイフレンドが出来ると、レベッカは「サラを守るため」という理由でサラと交流のある人間たちを攻撃するようになる……。出演はレベッカにレイトン・ミースター、サラにミンカ・ケリー。監督はデンマーク出身の新鋭クリスチャン・E・クリスチャンセン。", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LADY IN THE FOG", "霧の中の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "霧のロンドン、一人の青年がひき逃げにあって死亡する。犯人は顔を長いマフラーで覆った若い女性ドライバーだった。アメリカ人記者フィリップは、偶然知り合った被害者の姉ヘザーと、真犯人を探すことになる。意外なことに、青年の死は故意に仕組まれた犯罪だった……。スリルとサスペンス、ユーモアとエロチシズムという娯楽映画のすべてを満たした作品。加えて、主人公の女好きで陽気なアメリカン・ジェントルマンのキャラクターも楽しい。", "1952-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふたたび swing me again", "ふたたび swing me again", "", "", "http://image.eiga.k-img.com/images/movie/55562/315x210.jpg?4558", 111, "50年の時を経て仲間との約束を果たそうとする元ジャズトランペッターとその孫がたどる旅を、ジャズ音楽に乗せて描いたロードムービー。ジャズを愛する大学生の大翔はある日、50年ぶりにハンセン病療養所を出た祖父・健三郎を引き取ることになったと父から聞かされる。初めて会う祖父との接し方がわからず戸惑う大翔だったが、かつてのバンドメンバーを訪ねる旅に出るという祖父についていくことになる。「ビートキッズ」の塩屋俊監督がメガホンをとる。", "2010-11-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("New Year's Day", "ニュー・イヤーズ・デイ　約束の日", "", "", "http://image.eiga.k-img.com/images/movie/1572/315x210.jpg?1969", 106, "イギリス南部に住む17歳のジェイクとスティーブンは、クラス仲間と出かけたスキー旅行の最中に雪崩に遭遇、2人だけが生き残る。その日は奇しくも1年の始まりを告げる“ニュー・イヤーズ・デイ”、2人は自分たちの命の期限を「1年間」と定め、「12の約束」の遂行を誓う。やがて彼らは、この奇妙な課題を通して家族への愛、自然の美しさ、ちょっとした名誉、思いやりの心、命の重みを知っていく。だが期限の“ニュー・イヤーズ・デイ”は、すぐそこまで迫ってきていた……。", "2001-06-30", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハリマオ", "ハリマオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "TVドラマ界の名演出家・和田勉がフリーになって初めて映画演出を手掛けたことで話題を集めたアクション大作。太平洋戦争におけるマレーシア戦線で破格の活躍を見せ、“マレーの虎・ハリマオ”と称された現地日本人青年の実像を描いていく。ハリマオを利用する日本軍将校役を山崎努が怪演している。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOWHERE TO HIDE", "怒りの裁き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「ストリート・オブ・ファイヤー」の女闘士マッコイ役で知られるA・マディガン主演のハード・アクション。米海兵隊のカッター少佐が、連続するヘリ墜落事故の調査中に殺害される。その妻で元海兵隊員のバーバラは、子供を守るため、夫の無念を晴らすため、黒幕の組織を倒すべく再び銃を取る。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PACIFIC HEIGHTS", "パシフィックハイツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "J・シュレシンジャー監督が挑戦したサイコ・スリラーの快作。サンフランシスコにあるおもちゃ工場を任されているドレイク。結婚を目前にした彼は、婚約者のパティとともに、高級住宅地パシフィック・ハイツに古ぼけた屋敷を買った。いくぶん手直しし、二部屋を人に貸すことにした彼ら。一部屋には人のよい日本人の夫婦が住むことになり、もう一部屋は奇妙な人物、カーターが借りることになった。だが、カーターが屋敷に来てから、不思議なことが次々と起こり始め、やがてドレイクは精神の均衡を失っていく……。「ビジョン・クエスト／青春の賭け」「バーディ」「フルメタル・ジャケット」と、難しい役柄に挑み、見事にこなしてきたM・モディンが、微妙な演技を要求されたドレイク役を演じきっている。カーター役のM・キートンもまた、怪演を見せており、この二人の対照的なキャラクターの演技が見どころの一つだ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Homegrown", "ワイルド・スモーカーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "マリファナの栽培と密輸をめぐって展開するサスペンス・コメディ。ジャック、カーター、ハーランの３人組は北カリフォルニアのマリファナ畑で働く仲間同士。そんな彼らのボスがある日突然、彼らの目の前で殺されてしまった。彼らは目撃者がいないのをいいことに給料分のマリファナを売りさばくことに成功する。さらに調子に乗った彼らは、手付かずの残りの500万ドルに相当するマリファナをも売りさばこうとするのだが……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("縄文式２", "縄文式２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "AV監督、緊縛師、男優、映画評論家と、多方面で精力的に活動するダーティ工藤。彼が、緊縛による“縛り痕”へのこだわりを、1999年に発表した第１作同様、本人のモノローグで構成した異色作。縛る者と縛られる者、そして刺青を彫る者と彫られる者の関係を綴っていく。縄文式とは、女性の体に残る縛り痕と縄文式土器の縄模様が似ていることからつけられた言葉。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PART DE L’OMBRE", "しのび泣き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "J・L・バローが、酒で身を持ち崩した中年ヴァイオリニストに扮した悲恋のドラマ。運命の糸に引き寄せられるがごとく、数年ごとに別れてはめぐり逢う男と女。だがそのたびに、恋の成就をはばむ出来事が起こり……。E・フィエールが美貌の人妻に扮した秀作。", "1945-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MON　PE｀RE　IL　M’A　SAUVE´　LA　VIE", "父よ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "フィルム・ノワールの巨匠ジョヴァンニが、死刑台から生還した若き日の体験を綴りポ−ル・レオトー賞に輝いた原作を自ら映画化。レジスタンスとして勇敢に戦いながらも、戦後は暗黒街に身を投じたマニュ。仲間とともに銀行を襲った彼は、脚を撃たれ勾留。見せしめのために死刑を宣告されていまう。迫りくる死刑執行を前に、息子との確執を抱えていた父親が息子の命を救うべく一人孤独な戦いを挑む……。老境に達したジョヴァンニが、父と子の絆を万感の思いを込めて謳い上げた感動作。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AFTER FOOD OF THE GODS", "ゴッド・フード 巨大生物の恐怖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "“矯小発育症”に冒された息子を救うため、まだ開発途中の治療薬メサイアノルを投与してしまったトレガー博士。少年の体は急激に成長したが、だがその成長は止まることがなかった。一方、食物を巨大化させる研究をしていた同僚のハミルトン博士は、自分の研究にメサイアノルの投薬を試みる。バイオ・テクノロジーに材を得たホラー・スリラー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブ　キル　キル", "ラブ　キル　キル", "", "", "http://ecx.images-amazon.com/images/I/51AoxKFGJ%2BL._SL210_.jpg", 91, "日本映画界の第一線で活躍する名監督に気鋭の新人監督たちが挑む企画「映画番長」シリーズの第2弾「エロス番長」の1作。ハローワークの職員・皆川は、職探しにやって来た美女・サユリに一目ぼれする。友人のフリーター少女・ナオにサユリの身辺調査を依頼するのだが、今度はナオがサユリに恋してしまい……。新人・西村晋也監督がベテラン俳優・津田寛治を主演に迎え、男女の奇妙な三角関係を軽妙なタッチで描きだす。", "2004-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SPOILERS", "スポイラース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "アクションものの古典として親しまれている作品で４度目の映画化。20世紀初頭のアラスカ。ゴールドラッシュに一獲千金を夢みて群がる荒くれ男たちと、酒場女が織り成す男臭いドラマ。猛烈な殴り合いシーンが一番の見どころ。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミュージックソン THE MOVIE オードリーの爆笑24時間宣言", "ミュージックソン THE MOVIE オードリーの爆笑24時間宣言", "", "", "http://image.eiga.k-img.com/images/movie/57572/315x210.jpg?3578", 90, "ニッポン放送で毎年クリスマスに行われている目の不自由な人のためのチャリティ企画「ラジオ・チャリティ・ミュージックソン」のドキュメンタリー。お笑いコンビ「オードリー」がメインパーソナリティを務めた2010年の第36回ミュージックソンの模様に密着し、オードリーの2人のテレビでは見せない素顔やラジオ番組の裏側なども記録されている。キャンペーンの初代出演者でもある萩本欽一をはじめ、徳光和夫、テリー伊藤、熊田曜子、ベッキーら著名人も多数ゲスト出演。11年・第37回ミュージックソンのメインパーソナリティを務める音楽デュオ「ゆず」がイメージソングを担当。", "2012-01-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("楢山節考", "楢山節考", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "“東西東西、このところ御覧に入れまするは本朝姥捨の伝説より楢山節考……”との黒子の口上に始まる木下惠介の画期的傑作。深沢七郎の同名小説を木下自らが脚本化し、演出した。スタイリスト、木下のアイデアとテクニックは、全編に歌舞伎から着想を得たような舞台装置による場面転換の面白さを見せ、そこに人工的色彩の世界を加味するというものであった。主役を演ずる田中絹代は歯を折る場面のために、何本かの前歯を実際に抜いてしまったという逸話を残している。また、評論家時代のフランソワ・トリュフォーはこの作品を取り上げ、その様式的演出を称讃している。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハレンチ学園 身体検査の巻", "ハレンチ学園 身体検査の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "話題を集めた第１作にさらに輪をかけたハレンチぶりを見せる“ハレンチ学園”シリーズ。今日は聖ハレンチ学園の女生徒の身体検査の日。ヒゲゴジラと丸越らは校医を追い出して女生徒に襲いかかるが、ハレンチ・トリオの活躍で難を逃れる。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAULIE", "ポーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "人間の言葉をしゃべるオウムの冒険を描くファンタジー。会話が苦手な少女マリーの唯一の友達だったオウムのポーリーは、離れ離れになってしまった彼女に会いたい一心で旅に出る。優しく接してくれた女性との別れを経験したり、金もうけのために自分を利用しようとする男から必死で逃げたり、様々な事件に遭遇する彼の活躍をユーモラスに綴る。スタン・ウィンストンの特撮により、歌ったり踊ったりする愛嬌たっぷりのオウムのリアルな動きに注目。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KABUTO", "兜 KABUTO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "関ケ原の戦いで、雨でも使用可能な最新式の銃の必要を痛感した家康は、その買い付けのため、イスパニアに四男、頼宗と家臣の前田大五郎を派遣する。だがそのイスパニアでは、国王の座を狙って側近らが暗躍。また、彼らが乗った船の中にも裏切者がいた。本性を現した敵に、サムライたちは戦いを挑んでいくが……。ショー・コスギのアイデアによる、日本人をヒーローにしたアドベンチャー・ムービー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIG GUNS", "ビッグ・ガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "息子の７歳の誕生日を機に組織から足を洗おうと決心したトニー。しかし、それを首領に打ち明けた数日後、妻と息子は無残にも殺されてしまった。復讐を誓ったトニーは組織幹部を次々と倒し、首領を残すのみとなる……。ドロンが哀愁漂う犯罪者を好演。", "1973-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUGAR HILL", "シュガー・ヒル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "貧困とドラッグが蔓延するニューヨークのブラック・ハーレムで、ドラッグ売買の頂点にのし上がった兄弟の葛藤を描く。シュガー・ヒルと呼ばれるハーレム深層部で生まれ育ったレイネイサンとロメロの兄弟は、今ではかなりの富と力を得るまでになっていた。しかし、ロメロは今の生活に疑問を持ち始めていた時、よそからやって来たギャンググループとの間で抗争が起きるのだった……。主演のW・スナイプスが知的で冷徹、しかも優しさを併せ持つロメロを熱演している。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("O Ceu de Suely", "スエリーの青空", "", "", "http://image.eiga.k-img.com/images/movie/53282/315x210.jpg?2161", 88, "「モーターサイクル・ダイアリーズ」のウォルター・サレス製作の下、サレス監督作「ビハインド・ザ・サン」の脚本を手掛けたカリン・アイヌーがメガホンを取った感動作。抜けるような青空が広がるブラジルの田舎町イグアトゥ。かつて恋人と一緒にこの町を出たエルミーラが、幼い息子を連れて2年ぶりに帰ってくる。サンパウロにいる恋人に捨てられたと知った彼女は、再び町を出ることを決意。その資金を稼ぐためにある計画を思いつくが……。", "2008-02-09", "17,7,25,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自由ヶ丘夫人", "自由ヶ丘夫人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "高級住宅街の自由ヶ丘に住む池上良策と若く美しい妻、淳子。彼女は近所の夫人連中と連れだって、茶道や生け花などの習い事に毎日通っていた。その夫人連中の一人が良策が浮気をしているのを知り、淳子に伝える。怒った淳子は前から彼女に興味を持っていた写真家、高桑と出歩くようになる。双方の浮気を乗り越えて一つサヤに収まるまでを軽快に描く夫婦愛ドラマ。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARBINE WILLIAMS", "カービン銃第一号", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "カービン銃の発明者、デヴィッド・マーシャル・ウィリアムスの実話の映画化。父の反対を押し切って幼なじみと結婚したが、財産を分けてもらえず、金欲しさから酒の密造団に加わったマーシャル・ウィリアムス。やがて彼は刑務所で30年の重労働を科せられるが、そのベッドの中で、彼は銃の設計図を描く。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIG STEAL", "仮面の報酬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "D・シーゲル監督が、R・ミッチャムとともに作り上げたハード・アクション。カット割りを多用したスピーディーなストーリー展開と歯切れの良いセリフで、強盗団を追う米陸軍将校の活躍を描いている。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHILDREN OF THE CORN", "チルドレン・オブ・ザ・コーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "S・キング原作によるショッキング・ホラー第１作。愛くるしい子供たちが突然、大人を襲う恐怖を描く。トウモロコシ畑が果てしなく広がる田舎町へ、何かに導かれるようにやって来た二人の男女。トウモロコシと子供しかいない町の異様な雰囲気に気付くが、子供たちは二人に襲いかかる。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Cove", "ザ・コーヴ", "", "", "http://image.eiga.k-img.com/images/movie/55322/315x210.jpg?4017", 91, "日本のイルカ漁に実態に迫り、世界中の映画祭で激しい議論を巻き起こしたドキュメンタリー。第82回アカデミー長編ドキュメンタリー賞を受賞。1960年代に人気TV番組「わんぱくフリッパー」で調教師兼俳優として活躍したリック・オバリーは、和歌山県の太地町で行われているイルカの追い込み漁を知り、イルカ漁中止を訴えて太地町を訪れる。入り江に隠しカメラを設置した撮影隊は、イルカ肉の水銀問題や偽装販売疑惑、さらにイルカ肉を学校給食に使用していたことなどを次々に明らかにしていく。", "2010-07-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Seven Pounds", "7つの贈り物", "", "", "http://image.eiga.k-img.com/images/movie/53622/315x210.jpg?2657", 123, "海辺の家にひとり暮らすベン・トーマスは、全くつながりのない7人の名前の載ったリストを持っている。彼らは、ベンが進める“ある計画”のために選んだ人々だった。ベンはある意思をもって、彼らの人生を変えるため計画を進めるが……。「幸せのちから」の主演ウィル・スミスとガブリエレ・ムッチーノ監督が再びタッグを組んだヒューマンドラマ。共演にロザリオ・ドーソン、ウッディ・ハレルソン、バリー・ペッパーら。", "2009-02-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COWBOYS", "11人のカウボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "牧場主のウィルは、人手不足のため、やむなく11人の幼い少年たちを雇い牛追いの旅に出かける。しかし牛泥棒の手が……。ウエスタンの英雄ウェインが劇中、非業の死を遂げるという、それまでになかった設定で、新時代を象徴した西部劇。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WINNING", "レーサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "“インディ500レース”の激突をクライマックスに、スピードに命を懸けた男たちの闘魂と友情と、その華やかな栄光の陰に隠された私生活での愛の苦悩やマシーンとの孤独な闘いを描く。スピード・マニアとして有名なP・ニューマンが主演のほか製作にも参加した。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHEYENNE", "高原児", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "R・ウォルシュお得意の西部劇アクション。ララミーの町で荒稼ぎする賭博師ジム・ワイリーは、私立探偵から殺人容疑を突きつけられるが、“詩人”と呼ばれる駅馬車強盗を捕まえてくれれば、これまでの罪は不問にしてやると取引をもちかけられる。それに乗って“詩人”探しの旅に出るジムだが……。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("能登の花ヨメ", "能登の花ヨメ", "", "", "http://image.eiga.k-img.com/images/movie/53712/315x210.jpg?2786", 109, "結婚を間近に控えた都会育ちのOL・みゆきは、婚約者の母・松子が交通事故で足を怪我したことを知り能登半島を訪れる。松子の厳しい態度や初めての田舎暮らしに挫けそうになるみゆきだったが、震災に遭いながらもたくましく生きる人々の姿を通して\"家族\"の大切さに気付き……。石川県能登半島地震の復興支援作として製作されたヒューマンドラマ。石川県出身の女優・田中美里がヒロインを好演。姑役に泉ピン子。", "2008-08-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THIS IS ELVIS！", "ジス イズ エルビス／THIS IS ELVIS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "E・プレスリーの誕生から死までを、未発表の曲とプライベートフィルムを交えて編集したドキュメンタリー。1935年、ミシシッピー州で双子の兄弟の一人として生まれたプレスリーは、幼い頃からブルースに親しみ、1955年RCAと契約。『ハートブレイクホテル』のスマッシュヒットでスターとなり以後、数々の記録を生み出していく。しかし、1975年頃から、病魔の影が彼に忍び寄っていた……。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　MAITRES　DU　TEMPS", "時の支配者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "「ファンタスティック・プラネット」のR・ラルー監督が、タイムパラドックスを題材にしたSF作家S・ウルの小説『ペルディド星の孤児』を映画化。事故に巻き込まれ、不気味な惑星ペルディドで独りぼっちになってしまった男の子ピエールと、彼を救おうとする宇宙船の人々の冒険が展開する。バンド・デシネ界の大物アーティスト、メビウスによる繊細な絵が魅力のファンタジー・アニメ。ペルディド星の奇妙な昆虫や植物などの造形も見もの。", "1980-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続二等兵物語・五里霧中の巻", "続二等兵物語・五里霧中の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "第二次大戦中、古山源吉は強盗の片棒をかついで捕まり中部33部隊へ送られるが、新任の班長は源吉に罪をかぶせて逃げた強盗・大垣だった。大垣は源吉を目の仇にしてことあるごとに痛めつけるが、ついに源吉に叩きのめされる。やがて源吉たちは南方戦線へ送られる。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAPER MASK", "背徳の仮面", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "医者を夢見て病院のポーターをしている一人の青年が、友人の医師が事故死したことから、彼の名を語り、大きな総合病院の医師になる。だがまったくといっていいほど医学知識を持ち合わせていない彼は誤診で人を死なせ、さらに自分の正体がバレそうになると次々と殺人を犯していく……。斬新なタッチで描いたサスペンス・スリラー。", "1990-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Just Sex And Nothing Else", "反恋愛主義", "", "", "http://image.eiga.k-img.com/images/movie/54042/315x210.jpg?3246", 104, "「君の涙　ドナウに流れ／ハンガリー1956」のクリスティナ・ゴダ監督が、適齢期の女性をユーモラスに描いた恋愛映画。結婚間近だった恋人に妻子がいることを知った人気劇団の脚本家ドラは、そのショックから男に依存しない生き方を心に決める。インターネットに恋愛関係のないセックス・パートナーを求める広告を出すと、かなりの数の候補者が集まるのだが……。", "2008-12-20", "30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銭形平次捕物控・まだら蛇", "銭形平次捕物控・まだら蛇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "元火付け盗賊の大沼は、悪商人、勘定奉行らの仲間とニセ小判を作ってひともうけしようとたくらむ。その手口は悪ラツで、鍛冶屋のノドを潰してから地下工場へ無理やり連れこんで作らせるというものだった。平次は女船頭の小吉と協力して悪党どもをやっつける。山本・木暮・ひばりと３人の個性の違う美女が競演し、特にひばりの若衆姿は美しい。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Look Who's Talking Too", "リトル★ダイナマイツ／ベイビー・トークTOO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "　タクシー運転手からパイロットに転職したパパのジェームズ、そして会計士のママ、モリーの間に、女の子ジュリーが誕生。3歳になるマイキーとともに、一家の幸せな毎日が続いていた。ところが、モリーの弟スチュアートが家に転がり込んできたことから事態は一変。彼をめぐってモリーと喧嘩になったジェームズが家を出てしまう……。妹ジュリーの声をロザンヌ・バーが務めるほか、メル・ブルックスやデイモン・ウェイアンズも加わり、ますますにぎやかになったシリーズ第2弾。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("右向け左！ 自衛隊へ行こう", "右向け左！ 自衛隊へ行こう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "史村翔、すぎむらしんいち原作の人気マンガの映画化。富士の裾野の陸上自衛隊演習地。坂田光男は、その戦車部隊に所属しつつも、今一つなじめないまま日々を過ごしていた。そんなある日、坂田は陸上自衛隊のなかでも精鋭のエリート集団として知られる空挺部隊の募集の知らせを目にする。憧れの美人自衛官、浅野三曹が、空挺の広報班に転属されたこともあり、坂田は空挺部隊への入校希望を提出する。度重なる、地獄のような訓練や厳しい降下資格基準検定も仲間とともに乗り越えて、最後の実降下訓練を行うことになるが……。防衛庁が全面協力をし、本物の自衛隊が登場するのも見もの。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Take Care of My Cat", "子猫をお願い", "", "", "http://image.eiga.k-img.com/images/movie/1482/315x210.jpg?2193", 112, "20歳を迎えた女友達5人が、それぞれの道を歩み始める青春群像ドラマ。夢想家のテヒ、美貌の野心家ヘジュ、アウトサイダーのジヨン、陽気な双子ピリュとオンジョは高校時代からの仲良し5人組。だが、証券会社で働くようになったヘジュと無職のジヨンは互いに反発しあうようになり、仲間の絆を守ろうとするテヒは、家庭では父の無理解に不満を募らせていた。監督はこれが長編デビュー作となる女性監督チョン・ジェウン。", "2004-06-26", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEST LAID PLANS", "完全犯罪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「オーシャンズ11」の脚本家T・グリフィンが、大学時代に書いた短編小説をもとに脚本を執筆したクライム・サスペンス。綿密に練られた犯罪計画の意外なてん末をスリリングに描く。金に困った青年ニックが、恋人キャシーと共謀して友人ブライスをレイプ犯に仕立て、金を引き出そうと計画する。だが、ブライスがキャシーを監禁したため、計画は思わぬ方向へ……。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冬の幽霊", "冬の幽霊", "", "", "http://image.eiga.k-img.com/images/movie/33572/315x210.jpg?2271", 43, "「お散歩」の松田彰監督がプロデュースした5人の監督によるオムニバス作品集「Born in the 5」の一編で、松田監督自身がメガホンを取ったハートフルコメディ。結婚を間近に控えた亜津美の前に、2年前のバイク事故で死んだはずの元彼、冬樹の幽霊が現われた。ところが、当の本人は自分が幽霊になって出てきた理由がさっぱりわからないという。亜津美は友人の丸子と共に、彼が成仏できない理由を探し求めるが……。", "2006-07-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ASSASSINATION", "トップレディを殺せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "大統領の留守中に夫人のガードを任命されたシークレット・サービスのキリアン。夫人の周辺で続発する事故に疑惑を抱く彼は、夫人の協力を得て真相究明に乗り出す。C・ブロンソンと彼の妻J・アイアランドとの息の合った演技が見もの。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lara Croft Tomb Raider: The Cradle of Life", "トゥームレイダー2", "", "", "http://image.eiga.k-img.com/images/movie/1712/315x210.jpg?2580", 117, "同名人気ゲームの映画化第2弾。今回のララ・クロフトが狙う財宝は、アレキサンダー大王が隠した“パンドラの箱”。しかしこれを生物兵器として悪用しようとする悪の天才科学者ライスも、この財宝を狙っていた。監督は「スピード」シリーズのヤン・デ・ボンに交替。香港の高さ300メートルの高層ビルからウイング・スーツで急降下、45メートルの岸壁からバンジージャンプなどのアクションの数々を、あえてCGを使わずに描いている。", "2003-09-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIVA MARIA！", "ビバ！マリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "B・バルドーとJ・モローという、まったくタイプの違う女優が共演し、南米の小国を舞台に革命劇を描いた、異色のお色気アクション。旅芸人一座の人気者、マリーとマリアのマリア・コンビ。二人は革命戦争に巻き込まれ、自らも銃を持って立ち上がる……。L・マルらしいお遊びが随所にきいており、ちょっとハズした物語展開の中にそれが生きている。また、二人の女優の演技合戦も見もの。", "1965-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIRTY PRETTY THINGS", "堕天使のパスポート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "社会の片隅でひっそりと生きる不法滞在者の男女。プラトニックな愛で結ばれた二人が、命懸けで夢を追う姿を描いたヒューマン・ドラマ。移民たちを餌食にする卑劣な犯罪行為をサスペンス・タッチで描くなど、趣向を凝らしたストーリー展開で2003年度のアカデミーオリジナル脚本賞候補に。健気に生きる移民女性をA・トトゥが清らかな魅力で熱演している。ロンドンのホテルで働くトルコ移民のシェナイは、N.Y.へ脱出する日を夢みている。彼女の心の支えは、ナイジェリア人の同居人オクウェだけだった。だが、二人はホテルの秘密を知ってしまったため、窮地に追い込まれていく。", "2002-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRANKENSTEIN MEETS THE WOLF MAN", "フランケンシュタインと狼男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "フランケンシュタイン博士が創り上げたモンスターと、狼男が一大決戦をするクラシック・ホラー作品。埋葬されていた狼男が満月の夜に復活するが、変身の呪縛から解放されることを望んでいた。そこで生命の謎を解明したフランケンシュタイン博士に救いを求めるが、彼はすでに死亡していた……。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("痴人の愛", "痴人の愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "谷崎潤一郎の同名原作を、女を描くことには定評のある増村保造が監督した３度目の映画化。ふと知り会った少女ナオミを自分の思いどおりの女に育て上げようとする譲二と、その肉体に磨きをかけやがて譲二を征服するナオミの逆転劇を、緊張感あふれる演出で描く。安田（現・大楠）道代が新しいナオミ像を生き生きと演じている。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FEAR", "ザ・フィアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "人間の潜在的恐怖の正体を探り、それを克服する心理実験を行うために、人里離れた山小屋に集まった９人の男女。だが、そこはかつて人殺しのあった場所で、守り神のモーティという名のインディアン人形も不気味な雰囲気。９人はそれぞれ自分の恐怖について話しはじめ、レイプ、近親相姦、親殺しなど忌まわしい過去が明らかにされていく。やがて、連続殺人の幕が切って落とされる……。人間の内なる悪の願望を描き出したホラー作品。W・クレイヴン監督がカギを握る大学教授役で出演している。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Faust", "ファウスト", "", "", "http://image.eiga.k-img.com/images/movie/57602/315x210.jpg?1619", 140, "ドイツの文豪ゲーテの名作「ファウスト」を、ロシアの名匠アレクサンドル・ソクーロフが新たな解釈で映画化。2011年・第68回ベネチア国際映画祭で金獅子賞を受賞した。19世紀初頭、神秘的な森に囲まれたとある町。あらゆる学問を探求するファウスト博士は、研究を続けるために悪魔と噂される高利貸しのマウリツィウスのもとを訪れる。マウリツィウスはお金を貸すかわりに生きる意味を教えるとファウストを説き、ファウストは言われるままに町へ繰り出す。そこで出会った美しい女性マルガレーテに心を奪われたファウストは、マルガレーテを求めるあまり魂と引き換えにマウリツィウスと契約してしまう。", "2012-05-31", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DREAM MACHINE", "ドリーム・マシーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "イカすガールフレンドとゴキゲンなポルシェ。これが、退屈なキャンパス・ライフを夢色に変える必須アイテム。ところがその夢を運んでくれるはずのポルシェが、彼女のハートと一緒に死体まで運んできてしまって……。当時、人気アイドル・スターだった、ハイム主演の青春コメディ。アメリカ独特のキャンパス・ライフの断面がリアルに描かれている点にも注目。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AND THEN THERE WERE NONE", "そして誰もいなくなった", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "A・クリスティの同名小説の映画化第１作。孤島にある館に招待された見ず知らずの男女８人が集まってくる。だが、そこに主人の姿はなく、１週間前に雇われたばかりの召使いの夫婦がいるだけだった。やがて、彼らの罪を告発する声が聞こえ、一人また一人と不審な死を遂げてく……。仏の名匠R・クレールが米国時代にオールスター・キャストで撮り上げた上質のミステリー。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版NARUTO ナルト　ブラッド・プリズン", "劇場版NARUTO ナルト　ブラッド・プリズン", "", "", "http://image.eiga.k-img.com/images/movie/55952/315x210.jpg?4662", 94, "岸本斉史の人気コミックが原作のアニメーション劇場版第8作。雲隠れの雷影暗殺未遂、さらには霧隠れと岩隠れの上忍を殺害した罪で捕われたナルトは、絶海の孤島に建つ収監所「ブラッド・プリズン（鬼灯城）」に送られる。ナルトは無実を主張するが、何者かに命を狙われ……。", "2011-07-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOLERO", "ボレロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "一流ダンサーを夢見るラオールはパートナーのヘレンとともにロンドンにやってくる。苦労の末、自分の店を持つまでになったラオールだったが、彼は出世のために戦争に行くことを決意。ひそかにラオールを愛していたヘレンは、絶望のまま、かねてから求婚されていた富豪に嫁いでいくが……。男女の愛を名曲『ボレロ』に乗せて描く。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("番場の忠太郎 瞼の母", "番場の忠太郎 瞼の母", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "長谷川伸の名作戯曲を、当時新進の監督・稲垣浩が演出。片岡千恵蔵は、この年と翌年に、稲垣や伊丹万作と組み、「一本刀土俵入」や「花火」そして「国士無双」など質の高い時代劇を自らのプロダクションで製作、主演している。物語は、おなじみ渡世人の番場の忠太郎が、幼い頃に生き別れた母と出会い、精神的に葛藤するという人情もの。", "1931-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOU’LL　NEVER　GET　RICH", "踊る結婚式", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "興行主コートランドは、美しいコーラスガール、シーラの気を引こうとブレスレットをプレゼントする。ところが、このことがコートランドの妻にバレそうになり、コートランドは振付師ロバートの仕業だと嘘をつく。嘘に嘘を重ねることで起こる騒動に巻き込まれるうち、シーラとロバートは、いつしか惹かれあっていく……。フレッド・アステアとリタ・ヘイワースが初めて共演したミュージカル・コメディ。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Muppets", "ザ・マペッツ", "", "", "http://image.eiga.k-img.com/images/movie/57542/315x210.jpg?1814", 0, "ジェイソン・シーゲル＆エイミー・アダムスの主演で、かつて世話になった古い劇場を強欲な石油成金から守るためにマペット（＝手で頭を、棒で手足を動かす操り人形）たちが立ち上がる姿を描いたコメディ。クリス・クーパー、ラシンダ・ジョーンズが共演。アラン・アーキン、ザック・ガリフィアナキス、ケン・チョン、ジャック・ブラックらがゲスト出演。第84回アカデミー賞で主題歌賞を受賞。", "2012-05-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAULINE　＆　PAULETTE", "ポーリーヌ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ベルギーの新鋭L・デブローワーの長編デビュー作。知的障害を持つ初老の女性ポーリーヌと、彼女の姉妹との関係を優しいまなざしで見つめた人間ドラマ。一人では何もできないが、ピュアな心を持つポーリーヌ。その姿から浮かび上がる豊かな人間像が、ホロ苦くも温かい感動となって心に響く。舞台となったベルギーの花々で彩られた幻想的な風景にも目を奪われる。知的障害を持つ66歳のポーリーヌは、優しい姉の手厚い世話を受けながら暮らしている。だが、その姉が急死。姉の遺産を相続するためにもう一人の妹ポーレットがポーリーヌと暮らし始めるが、共同生活は災難続きだった……。", "2001-01-01", "37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Voie du Chat", "ネコを探して", "", "", "http://image.eiga.k-img.com/images/movie/55542/315x210.jpg?3336", 89, "フランスの女性ドキュメンタリー作家ミリアム・トネロットが行方不明のまなネコを探す旅を通し、世界中のネコと人間の関係性を描くドキュメンタリー。和歌山県のローカル線で人気を集める駅長ネコ・たま、米ミネソタ州で宿泊客をもてなすネコ・ジンジャー、米ロードアイランド州で認知症患者の最期をみとる介添えネコ・オスカーなど、ユニークなネコたちの姿を映し出す。", "2010-08-14", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モルモット", "モルモット", "", "", "http://image.eiga.k-img.com/images/movie/55902/315x210.jpg?2035", 60, "「サル」「saru phase three」に続き、葉山陽一郎監督が治験をテーマに扱うシリーズ第3弾。主演を「細菌列島」の三輪ひとみが務める。児童相談所の職員・玲子（三輪）は、アルコール依存症の妊婦・美咲に酒を断つよう説得していた。妊娠中の飲酒のせいで第一子が知能障害をもった過去をもつ美咲は、覚悟を決めて産婦人科に相談に行くが、院長にアルコールの欲求を抑える効果があるという厚生省無認可の新薬を与えられ……。", "2011-01-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARRAKECH EXPRESS", "マラケシュ・エクスプレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ある夜、現れた一人の女。マラケシュで恋人が捕まってしまったため救い出して欲しいという女に、かつての仲間たち４人は、保釈金となるはずの金を用意し、マラケシュに向けて車で旅立つ。幾多の苦難のうちにも、昔の思いを取り戻し、自由な空気にふれる彼らだったが、たどり着いた先では……。「エーゲ海の天使」で戦争から取り残された人々を描いたイタリアの新鋭G・サルヴァトレスが、ここではおおらかな空気に満ちたロード・ムービーを紡ぎ出す。サッカーで結びつけられた５人の男たちの友情、そして生きるという喜びが伝わってくるような爽快な映像が魅力。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("C’EST ARRIVE´ PRE´S DE CHEZ VOUS", "ありふれた事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "都市の孤独や住宅事情を憂いながら、心臓病のばあさんを脅して心臓発作を起こさせ、平気で金を巻き上げる。詩・音楽・絵画さらには映画的教養を振り巻きながら、ハエを殺すように人を殺し、ゴミを捨てるように死体を捨てる。初期デ・ニーロの芸風を思わせもする、このニヒルで偏執狂的な主人公を一切の感傷を排して描写。さらには、ドキュメンタリーの客観的な装いをあざ笑うように、カメラ自身が殺人鬼の共犯者の役を演じ始めるさまに、ブラックユーモアが横溢する。“無名芸術家”という名のプロダクションを作った、ベルギーの若き映画スタッフの不敵な面構えが似合う異色作。", "1992-01-01", "37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Free Wheels East", "僕たちのバイシクル・ロード 7大陸900日", "", "", "http://image.eiga.k-img.com/images/movie/56902/315x210.jpg?3868", 94, "自転車で7大陸を走破した2人の若者の姿を追ったドキュメンタリー。2005年4月6日、ベン・ウィルソンと従兄弟のジェイミー・マッケンジーは英ブルックファームを出発。英仏海峡フェリーに乗ってフランスに向かい、飛行機に乗らず自転車だけで7大陸を走破する3年に及ぶ壮大な旅に出る。2人が自らカメラを回し、33カ国、900日間を超える旅を記録した。ナレーションを「E.T.」「エリン・ブロコビッチ」の名優ピーター・コヨーテが担当。", "2011-11-03", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD AND SAND", "血と砂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "闘牛士になることを夢見て上京した青年ホアン、その情熱で一歩一歩成功への階段を上っていく。しかし栄光と名声を手にした彼は、金持ちの悪女ドンナに誘惑され、堕落と破滅の道を選んでしまう……。ハリウッドで二度映画化された古典を本家スペインが映画化。ドンナ役のS・ストーンの存在感が全編を圧倒。", "1989-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("侍", "侍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "群司次郎正の原作『侍ニッポン』を橋本忍が脚色、岡本喜八の監督で映画化した“幕末”もの。万延元年、時の大老・井伊直弼の開国強行策に反対する水戸浪士が、暗殺を企てる。井伊大老が妾に生ませた子・新納鶴千代は、一獲千金、立身出世の打算から水戸浪士の一味に加わり、実父とは知らぬまま桜田門で井伊直弼の首を落とす。雪の降りしきる桜田門での井伊直弼の暗殺のシーンは、ラグビーまがいの集団殺陣を、岡本喜八監督十八番のスピーディーなカッティングによるアクション処理でこなし、見ごたえ十分である。三船敏郎が、豪快な中にも繊細な神経を潜ませた新納鶴千代を過不足なく演じ、伊藤雄之助のニヒルですごみのきいた浪士の頭目役も絶品。なおこの作品は、三船プロと東宝による第１回提携作品である。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SMIC，SMAC，SMOC", "恋人たちのメロディー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "造船所で働く貧しいけれどいつも陽気な３人組。ある日、一人がパン屋の娘と結婚することになり、仲間たちはあれこれ世話を焼くが……。フランシス・レイが特別出演。", "1971-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三文役者", "三文役者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "多くの鬼才たちに愛され、1989年に惜しまれつつ世を去った名バイプレーヤー、殿山泰司の生涯を映画化。多くの作品で彼を起用した新藤兼人監督が、人間味豊かなドラマに仕上げている。酒グセと女グセの悪さによるトラブルを抱えながらも、名作から娯楽作、教育映画、ポルノまで出演し、俳優業に殉じた男の生を描出。バイタリティーあふれる主人公になりきった竹中直人の妙演も見もの。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANNE OF GREEN GABLES: THE CONTINUING STORY", "赤毛のアン／アンの結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 147, "世界的名作小説の映画化第３作にして完結編。聡明な女性に成長したアンが、第一次大戦下という激動の時代に揉まれながら、力強く生きていく様を生き生きと描く。アンは作家への夢を抱いて大都会ニューヨークへ。だが有名作家にだまされ、故郷に戻り婚約者ギルバートと結婚式を挙げる。その直後、軍医として入隊したギルバートが行方不明となり、アンは果敢に戦場へ乗り込んでいくのだった。", "2000-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONCE WERE WARRIORS", "ワンス・ウォリアーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "マオリ族の父とヨーロッパ系の母の間に生まれたタマホリ監督が、都会に住むマオリ族の現状を描いた問題作。ニュージーランド・オークランド郊外、マオリ族の王族の子孫だったベスは、地位も名誉もすべてを捨て、元奴隷の一族だったジェイクと結婚した。５人の子供に恵まれたベスは、野性的な魅力にあふれたジェイクに今も夢中だ。だが、彼はパブに入り浸りで腕っぷしの強さを自慢してばかりで。それはベスに対しても同じだった。やがて暴力によって家族の絆は次第に崩れていき……。1994年モントリオール国際映画祭でグランプリと最優秀女優賞（R・オーウェン）を受賞。", "1994-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無人喝彩／NO　MORE　APPLAUSE", "北京四重奏〈カルテット〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "文化大革命により北京から下放されて僻地の飛行場で働くリーは、フルート奏者の妻シャオの努力により北京に戻るが、夜間警備員の仕事しかなかった。夫婦にはケンカが絶えず、ついに離婚することになる。二人は同居を続けるが、それぞれに新しい相手の気配を感じるのだった。中国の都市生活者たちの孤独な心理を、おだやかに描いている。", "1993-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青いソラ白い雲", "青いソラ白い雲", "", "", "http://image.eiga.k-img.com/images/movie/57042/315x210.jpg?3409", 96, "東日本大震災の発生直後を舞台に、少女と1匹のイヌの交流を描く青春映画。高校を卒業したリエはアメリカの大学に進学するため母親と渡米するが、その翌日の2011年3月11日、東日本大震災が発生。母親と離婚した父親や友人が心配で日本に取って返したリエは、ボランティアをしている恋人から震災で飼い主を失ったイヌ、ソラを預けられる。一方、父親は勤めていた旅行代理店が震災の影響で顧客が激減し倒産。途方に暮れるリエに残されたのはソラだけになってしまう。監督は「ばかもの」の金子修介。主演は本作が映画デビューとなるモデルの森星（もり・ひかり）。", "2012-03-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONTAND LE FILM", "モンタン、パリに抱かれた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "フランスが誇るY・モンタンの生涯を振り返っていくドキュメンタリー。生前のモンタンの肉声とともに進行し、彼がかつて住んだ土地をめぐり、彼の人生の足跡をたどっていく。「デデという娼婦」「告白」「夕なぎ セザールとロザリー」など出演した映画が38本登場するほか、『枯葉』などの歌声を聞くことができる。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES MISE´RABLES", "レ・ミゼラブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 174, "文豪ヴィクトル・ユゴーの名作『ああ無情』を現代に置き換えて映像化した大河ドラマ。ユダヤ人迫害や第二次世界大戦を絡め、激動の時代を生き抜いた一人の男の生涯を描く。1895年、映画誕生の年に生まれたアンリ・フォルタン。５歳の時に両親を亡くしたアンリは居酒屋で過酷な少年時代を過ごした後、プロボクサーから運送業に転職。そして、第二次世界大戦中ユダヤ人弁護士のジマン氏一家の引っ越しを請け負ったことから、時代にほんろうされる波瀾万丈の人生がスタートするのだった……。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PicNic", "PicNic", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "精神病院へ入院することになったココは、そこでツムジとサトルに出会う。規則で塀の向こうに行けない彼らだったが、塀の上を歩いて3人で世界の終わりを見に行くことにする。監督は「Love Letter」の岩井俊二、主演は歌手のCharaと浅野忠信。本作をきっかけに知り合い、のちに結婚するCharaと浅野の仲に気づいた岩井監督が、3人が中心のストーリーから、2人の演じるココとツムジのラブストーリーへと変更した。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("燃えろ！青春", "燃えろ！青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "学園青春映画の爽快作。先生転任反対の実力行使で騒然とする女学校に赴任し、バレー部の部長となった若い教師と、それに反感を抱きながらも徐々にうち解けていく生徒たちとのさわやかな交流を描いた青春もの。ラストの息づまるバレーボール県大会決勝戦のシーンは学園青春ものの王道。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Free Zone", "フリー・ゾーン　明日が見える場所", "", "", "http://ecx.images-amazon.com/images/I/51xj6Bdy%2BKL._SL210_.jpg", 90, "「キプールの記憶」などで知られるイスラエルの名匠アモス・ギタイが、実力派女優ナタリー・ポートマンを主演に迎えて撮りあげたヒューマンドラマ。イスラエル人の婚約者と別れてタクシーに乗り込んだアメリカ人レベッカ。行くあてのない彼女は、ヨルダンの自由貿易区「フリー・ゾーン」にいる通称「アメリカ人」に会いに行くという女性運転手ハンナについていくことにするが……。ハンナ役のハンナ・ラズロがカンヌ国際映画祭女優賞を受賞。", "2005-01-01", "46,2,37,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("亡霊怪猫屋敷", "亡霊怪猫屋敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "大蔵貢製作・中川信夫監督による化け猫映画の決定版。中年の医師が病弱な妻の療養を兼ねて故郷の屋敷で病院を開業するが、夜ごと不気味な老婆が現れて妻の命を狙う。和尚の話によると、昔この屋敷で行われた殺人に加担した若党が妻の先祖であった……。戦前は鈴木澄子、戦後は入江たか子のおどろおどろしいメイクで有名な化け猫映画を、中川信夫監督は現代のエピソードを前後につけ加えることによって、現代にも起こり得るリアルな恐怖譚として提示した。「東海道四谷怪談」「地獄」「女吸血鬼」「憲兵と幽霊」と並ぶ中川信夫監督の新東宝時代の代表作。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("晴れ舞台はブロードウェイで！　シニア劇団の挑戦", "晴れ舞台はブロードウェイで！　シニア劇団の挑戦", "", "", "http://image.eiga.k-img.com/images/movie/57322/315x210.jpg?1346", 68, "2004年に大阪・箕面市で市民企画から誕生したアマチュアシニア劇団「すずしろ」が、米ニューヨーク・ブロードウェイでの公演実現へ向けて奮闘する姿を収めたドキュメンタリー。04年、ひとりの女性のシニア劇団を作りたいという思いから立ち上げられたワークショップで劇団「すずしろ」が結成される。60代から80代のシニア世代で構成され、それまでの職業や経歴もさまざまな人々が、病気や家庭事情などそれぞれに問題を抱えながらも夢に向かって挑戦する姿を追う。", "2012-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひよどり草紙", "ひよどり草紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "吉川英治の原作を映画化した時代活劇。慶長15年、朝廷より徳川家に賜わったひよどりを、曲者のために逃がした二人の武士は切腹を命じられる。しかし、その子らに向けて３ヵ月以内にひよどりを捕えた者の父を許すとの沙汰があり、二人の娘と息子が旅立つ。中村錦之助の映画デビュー作。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おにぎり・ARCADIA物語", "おにぎり・ARCADIA物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "「親分はイエス様」で知られる斎藤耕一監督が、“米”をテーマにして撮り上げたコミカルな人間ドラマ。都会から、とある農村に逃げてきた若い男女。彼らは“大日本生き残り隊”と名乗る農業に従事する人々との出会いを通じて、生きる意味を知る。そんな人間愛にあふれた物語が、雄大な山形県の自然をバックに、温かい視点でゆっくりと綴られていく佳作だ。東京から逃げ出してきた若いカップルの慎二と友美。行くあてのない二人は、とある農家に転がり込んだ。そこの主人、篤郎は、ワケありな人達とともに“大日本生き残り隊”を結成して、農業に従事して明るく暮らしている男だった。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こわしや甚太", "こわしや甚太", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "仕事は遅い、金もない、女にもモテないさえない男。しかしこの男、こと“コワす”ことに関しては無類の才能を発揮する。縁談、浮気、就職、商談、なんでも派手にぶち壊していくが、ついには自分の恋までも壊してしまう……。情報屋役で伴淳三郎が好演。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新吾二十番勝負・第ニ部", "新吾二十番勝負・第ニ部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "葵新吾は四国・西条の領主松平頼安の居城に身柄を預けられるが、剣の宿怨を込めた柳生一門は、四国にまで執拗に新吾を追って来る。無明流の使い手・白根弥次郎は、新吾の師・真崎庄三郎を倒し新吾との対決を迫る。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hotel", "HOTEL", "", "", "http://image.eiga.k-img.com/images/movie/1432/315x210.jpg?3245", 116, "「リービング・ラスベガス」（95）でニコラス・ケイジをオスカー俳優にしたマイク・フィギス監督が、俳優のアドリブ演技を尊重、4場面をカメラ4台で同時撮影、4分割場面にするなど、ユニークな演出で描く異色作。ベニスのホテルに集まったのは、ジョン・ウェブスターの「マルフォイ夫人」映画化作を撮影中のクルーと俳優たち。が、監督が撮影中に何者かに銃撃されて昏睡状態に。実はこのホテルには恐るべき秘密があった。", "2003-08-30", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ケータイ小説家の愛", "ケータイ小説家の愛", "", "", "http://image.eiga.k-img.com/images/movie/54522/315x210.jpg?3944", 100, "地味な女子高生の愛は、ケータイ小説を読みふけることが趣味で、いつしか自分もケータイ小説家になることを夢見ていた。ある日、愛は不良に絡まれているところを同級生の友樹に助けられ、2人は交際するようになる。しかし、友樹にドラッグやレイプを仕掛けられ傷ついた愛は、それらの出来事をケータイ小説に綴っていき……。過激な事件が次々と起こるケータイ小説の映画化。", "2009-05-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PANIC IN THE STREETS", "暗黒の恐怖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "E・カザン監督がドキュメンタリー・タッチで描くサスペンス映画。ニューオリンズに密航上陸したペスト患者を殺害した犯人を48時間以内に逮捕しなければ、病毒が全米に広がると予測した保健局員と警察の協同捜査を描く。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INVASION OF THE SAUCER MAN", "暗闇の悪魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "田舎町の森の中に空飛ぶ円盤が着陸。そうとは知らずドライブするカップルが、道を歩く宇宙人を轢き殺してしまう。警察に届けても誰もその話を信じてはくれず……。間抜けな宇宙人と、情けないストーリー展開で、観る者をガックリと力ない笑いへと誘うB級SFの珍品。キャベツ頭のチープな宇宙人のデザインは、一度見たら忘れられない。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お祭り半次郎", "お祭り半次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "港町の祭りに集まる様々な芸人や商人たち、今年はその中に新入りの半次郎がいた。彼は侍の家の出だが、奉納試合で伊太八の眼を潰したことから家を飛び出し、竹刀で自分の頭を叩かせる一文叩きの商売をしていた。ある日、祭りに伊太八が現れ、無抵抗の半次郎を思う存分打ち続けた。人情時代劇作品で、脚本の藤木弓は稲垣監督のペンネームである。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マルドゥック・スクランブル　圧縮", "マルドゥック・スクランブル　圧縮", "", "", "http://image.eiga.k-img.com/images/movie/55182/315x210.jpg?4138", 65, "第24回日本SF大賞を受賞した冲方丁の小説を3部作で映画化する第1部。未来都市マルドゥックシティの少女娼婦ルーン・バロットは、裏社会の陰謀に巻き込まれ命を落としかける。しかし、人命保護を目的とした緊急法令「マルドゥック・スクランブル09（オー・ナイン）法」によって救われ、禁じられた科学技術を身に宿したバロットは、相棒のネズミ型万能兵器ウフコックとともに自らが巻き込まれた陰謀に立ち向かっていく。", "2010-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌６", "釣りバカ日誌６", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "マドンナに久野綾希子、その娘役に喜多嶋舞が扮した第６作。バブル崩壊、妻の海外旅行などで、公私ともにつまらない日々を送るスーさんを励まそうと、ハマちゃんは釜石にアイナメ釣りに行くことを提案。さっそく釜石へ着いた二人だが、スーさんは釣りのほかにも、社長としての講演会が待っていた。ところが、地元の人々はハマちゃんを社長と間違えて……。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEUX INTERDITS", "禁じられた遊び", "", "", "http://ecx.images-amazon.com/images/I/51FSnpbJXML._SL210_.jpg", 86, "1940年６月、パリから逃げる途中でナチス戦闘機に両親を殺された５歳の少女ポーレット。農家の少年ミシェルはこの孤児となった少女と仲良くなり、彼女の死んだ愛犬の墓を立ててやる……。イエペスの哀愁を帯びたギター音楽があまりにも有名な名作。", "1952-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クロエ", "クロエ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "「BeRLiN」以来、６年ぶりの作品となった利重剛監督作品。様々な分野で活躍したアーティスト、ボリス・ヴィアンの小説をもとにした純愛物語だ。プラネタリウムで解説員をしている高太郎は、叔母の絵画展で出会ったクロエと一目で恋に落ちる。大勢の友人に祝福されて結婚式を挙げ、幸せな日々を送る二人。だが、クロエの右肺に“花の蕾”のようなものが発生し、肺を圧迫し始める。一度は手術で取り除いて回復するがもう片方の灰にも蕾が見つかり、クロエは次第に衰弱していく。映画監督の青山真治が、カリスマアーティストの役で出演。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長三国志・次郎長売出す", "次郎長三国志・次郎長売出す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "米屋の長五郎こと清水の次郎長のきっぷに惚れて桶屋の鬼吉、関東綱五郎そして侍くずれの大政が子分の盃をもらう。次郎長は折から義兄弟・大熊が渡世の仁義から和田島の太左衛門に助っ人して津向の文吉を討とうとするのを命を張って仲裁、一躍、次郎長の名を売り出す。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Brake", "ブレーキ", "", "", "http://image.eiga.k-img.com/images/movie/58002/315x210.jpg?3224", 91, "密閉された箱に閉じ込められた男が、次々と予測不能な罠に襲われる姿を描いたシチュエーションスリラー。主演は「ブレイド」「SOMEWHERE」のスティーブン・ドーフ。シークレットサービスのジェレミーは突然何者かに誘拐され、気がつくと車のトランクに積まれた箱の中に閉じ込められていた。箱の中では恐ろしいトラップが作動し、すんでのところで停止。しかし4分後にまた新たな攻撃が加えられる。4分ごとに際限なく繰り返される死の恐怖から脱しようと試みるジェイミーだったが……。", "2012-05-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("時には娼婦のように", "時には娼婦のように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "なかにし礼の同名ヒット曲をモチーフに、本人が脚本・音楽・主演を引き受けたロマンポルノ。ポルノ小説の翻訳家・良介は妻との愛に安住できず、怠惰な放埒を繰り返すうちに胸を患い不能になってしまう。そんなある日、雨の公園で見かけた若い狂女に、良介は自身の青春の傷跡を重ねていくのだった。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRUCE LEE THE LEGEND", "ブルース・リーの神話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "1973年７月20日に32歳の若さで急逝したアクション・スター、ブルース・リー。本作品は彼の生涯を４つのパートに分け、子役時代から謎めいた死に至るまでを、彼にかかわった人々の証言や貴重なフィルムをもとに生々しいタッチで綴る。ビデオ発売時タイトルは「ブルース・リー 神話」。", "1984-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天龍母恋い笠", "天龍母恋い笠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "日本一の大親分を夢みて貸元飯田屋熊五郎一家に身を寄せていた、材木商信濃屋の跡取り息子・新太郎。だが、熊五郎の命による旅先で、目明かしに十手を突き付けられ島送りにされる。熊五郎は新太郎を追い出し、信濃屋の身代を狙っていたのだ。美空ひばりが一人二役を演じる股旅もの。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ねずみ物語　ジョージとジェラルドの冒険", "ねずみ物語　ジョージとジェラルドの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "", "2007-12-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HOSPITAL", "ホスピタル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "大病院内部組織の真実を暴露する問題作。一人の老患者の誤診死に始まる病院機構の乱脈のなかで、精神的に疲れ果てた院長は、やがて院内の手術患者すり替え事件に巻き込まれていく。それは、病院の非道を神に代わって制裁しようとする者の怒りの行為でもあったのだ。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大当り三色娘", "大当り三色娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「ジャンケン娘」「ロマンス娘」に続いてひばり・チエミ・いづみの三色娘が繰り広げるラブ・コメディ。３人はお手伝い仲間。ある日ひばりの主人が、その弟といづみを見合いさせようとする。しかし弟はハシャギ過ぎて蜂に刺され、かわりにひばりが宝田を見合いの場に送り込んだため一騒動が起こる。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Minnie and Moskowitz", "ミニー＆モスコウィッツ", "", "", "http://ecx.images-amazon.com/images/I/511aNu1g-KL._SL210_.jpg", 115, "駐車場で働くモスコウィッツはなにかにつけ騒ぎを起こす青年。美術館に勤務する女性ミニーは既婚者と付き合っていたが、そのために思い悩むことも多かった。そんなミニーとモスコウィッツがひょんなことから出会う。モスコウィッツはミニーに心を奪われるが、ふたりはうまく交流できず……。男女の姿を通して、結婚の意味を描くドラマ。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MES NUITS SONT PLUS BELLES QUE VOS JOURS", "私の夜はあなたの昼より美しい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "幼い頃、池で両親を失い、言葉の記憶をなくす恐怖から絶望的にしゃべり続ける男と、超能力で人の未来を予知する若く美しい女。この二人の運命的な出会いと、三日三晩にわたる狂気と錯乱の愛の軌跡を描いたセンセーショナルな作品。異才ズラウスキーが、私生活上のパートナー、マルソーと撮った、鮮烈な愛の叙事詩でもある。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("2010", "2010年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "　2001年、黒石板モノリスの謎を解明すべく調査の旅に出た宇宙船ディスカバリー号が消息を絶った。その9年後、ディスカバリー計画に関わったフロイド博士、そしてコンピューターHAL9000の開発者チャンドラー博士らが原因究明のために、ソ連の宇宙船レオノフ号に乗り込み木星へ向かう。一方、地球上は米ソ間の緊張が高まり、一触即発の状態に。はたしてフロイド博士は謎を解明できるのか？　映画史に燦然と輝く傑作SF映画「2001年宇宙の旅」の続編。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("We 命尽きるまで", "We 命尽きるまで", "", "", "http://image.eiga.k-img.com/images/movie/53782/315x210.jpg?2571", 0, "「憲法9条改憲阻止」のスローガンの下、党派を超えて堂に会した全学連・全共闘・学生運動のOBたちが、国会前でのハンスト、座り込みなどを決行し、改憲準備法案である国民投票法案の採決に反対すべく戦う姿を追ったドキュメンタリー。監督の藤山顕一郎自身も元学生運動の活動家で、本作に描かれる運動にも参加している。", "2008-06-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HER ALIBI", "彼女のアリバイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「ドライビング・ミス・デイジー」で知られるB・ベレスフォード監督の未公開ドラマの１本。人気推理作家フィルは、編集者のサムと取材のため裁判所に行く。そこで殺人容疑の被告ニーナに魅せられたフィルは、サムとともに彼女のアリバイ作りをする。そして彼は彼女をモデルにして小説を書くため一緒に暮らし始める。ところがニーナは異常な行動ばかりとる。果たして彼女は本当に殺人犯なのか……？", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロード", "ロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ロックバンド・THE虎舞竜のバラード・ヒット曲『ロード』を映画化、運命的に出会った男女の純粋な恋を描く。小さな地方都市で美容師を目指していた美雪は、自分の働く美容室で妻子持ちのしがないヤクザ司と出会う。美雪に心奪われた司はヤクザを辞め妻とも別れる決心をする。しかし、司は一人息子の優太を巡り妻と言い争いが絶えず、一方、美雪も一緒に暮らす母に司のことを言い出せないでいた。そんなある日、美雪は司の子供を妊娠したことに気づく。主人公の司を、THE虎舞竜のボーカル・高橋ジョージが演じている。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Summer Whispers", "ささやきの夏", "", "", "http://image.eiga.k-img.com/images/movie/56772/315x210.jpg?2082", 96, "妻と死別したノ教授は、夏休みに息子に会うために渡米。部屋に残した本の整理を教え子のヨンジョに、植木の世話を町内の花屋の青年ユンスに頼んでいく。ヨンジョとユンスは、互いに与えられた仕事を果たすため入れ違いで教授の部屋を訪れるが、次第に互いの存在を知るようになり、微妙な感情がわきあがっていく。2011年「真！韓国映画祭」にて上映。", "2011-05-28", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魔法のiらんど teddy bear", "魔法のiらんど teddy bear", "", "", "http://ecx.images-amazon.com/images/I/41aCyNwrW7L._SL210_.jpg", 108, "若者たちから絶大な人気を集める携帯サイト「魔法のiらんど」から生まれた感動作を映像化した青春ラブストーリー。高校に入学したばかりなのに学校生活にむなしさを感じはじめた晴奈は、不登校やリストカットを繰りかえすようになってしまう。そんなある日、晴奈は成也という青年と恋に落ち、少しずつ生きる希望を取りもどしていくが……。ヒロイン役に、ファッション誌「セブンティーン」の人気モデル・桐谷美玲。", "2008-11-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("積木くずし", "積木くずし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "幼い頃の腹部腫瘍がもとで赤毛になり、そのため不良グループにいじめられて心傷つき、徐々に手のつけられない非行少女になっていく娘と、両親や警察の懸命な努力を描く。俳優・穂積隆信が自分の娘の補導更生記録をまとめたベストセラーの映画化。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本侠客伝 刃", "日本侠客伝 刃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "博多出身の車夫・松吉は、恩人の芸者・小芳を身請けするため、金沢の北陸逓送で懸命に働く。総選挙の際、代議士・青山を応援した北陸逓送は、敵対する救国社に迫害され、倒産に追い込まれる。松吉は救国社に殴り込み、かつては厚い絆で結ばれた御家政と対決する。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOOKING FOR AN ECHO", "奇跡の歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "生きる目的を見失っていた男が、再び夢と希望を取り戻していく姿を描く人間ドラマ。1960年代に人気ドゥワップグループのヴォーカルとして、全米を魅了したヴィンス。現在はしがないやもめ暮らしだが、４人の子供と孫たちに囲まれてそれなりに幸せな日々を送っている。唯一の心配事は、病気で入院中の娘のことだけだった。ある日、ヴィンスは娘の看護婦ジョアンに一目ぼれするが、彼女がかつてヴィンスのファンだったことを知って複雑な思いを抱く……。A・アサンテ、D・ヴェノーラほかベテラン勢が、味わい深い演技で大人のドラマを見せてくれる。劇中に登場するノスタルジックなドゥワップの数々も魅力的だ。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BEGGAR’S OPERA", "三文オペラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "『マハーバーラタ』などの演出家として知られる演劇界の鬼才P・ブルックが、L・オリヴィエを主演に撮ったオペラ映画。原作は、ブレヒトの『三文オペラ』の種本となったことでも知られるジョン・ゲイの舞台劇。18世紀のイギリス、牢獄で脱獄を計画している盗賊マッキースのもとへ、オペラ書きの男が投獄されて来た。彼はマッキースを主人公にしたオペラを書き持っており、その物語が映画のなかで語られていく。", "1953-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON QUICHOTTE", "ドン・キホーテ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "M・D・セルバンテスの名作を名匠G・W・パブストが、世界最高のバス歌手といわれるF・シャリアピンSrを主演に据えて映画化。書物の中の英雄に憧れ狂気に囚われた男が、騎士道を実践しようと甲冑に身を固め従者と旅に出るが……。", "1933-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本の悪霊", "日本の悪霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "瓜二つの刑事とヤクザが、互いに間違えられたことをきっかけに立場を入れ替える。刑事は潜在的にあった自由への憧れを満喫し、ヤクザは自分を罪に陥れた当時の犯罪事件を調べ上げる。佐藤慶が一人二役で熱演した高橋和巳の同名小説の映画化作品。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("武士道シックスティーン", "武士道シックスティーン", "", "", "http://image.eiga.k-img.com/images/movie/54642/315x210.jpg?4765", 109, "誉田哲也の剣道少女を描いた青春小説を、「ロボコン」「ホームレス中学生」の古厩智之監督が映画化。主演は成海璃子と北乃きい。幼い頃から剣道の修行を積んできた香織は、ある大会で無名の選手・早苗に負けたことを引きずり、早苗を追って剣道の強豪高校へ入学する。だが、久々に再会した早苗は気楽に剣道を楽しむ平凡な女子高生だった。香織は因縁のライバルの本来の力を引き出そうと奔走するが……。", "2010-04-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("２番目の彼女", "２番目の彼女", "", "", "http://ecx.images-amazon.com/images/I/51dOJx-toaL._SL210_.jpg", 65, "数々のTVドラマを手掛けてきた人気脚本家、大森美香がメガホンを執り、男の情けなさと切なさを軽やかなタッチであぶり出す恋愛映画。自称・小説家の草野にはフライト・アテンダントの美しい恋人がいるのに、つい関係してしまった女子大生から“２番目でいいから付き合って”と志願されてしまい……。", "2004-11-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("熱海殺人事件", "熱海殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "“たかが熱海で、一介の工員が、よりによって浴衣の腰ヒモでブスを絞め殺したチンケな事件”を、“捜査は芸術だ”がモットーの警視庁の名物刑事が、一級の殺人事件に仕立て上げていくという、つかこうへいの傑作戯曲の映画化。名物刑事役を仲代達矢が怪演している。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トキよ舞いあがれ・巣だちの記録", "トキよ舞いあがれ・巣だちの記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "かつては日本、朝鮮、中国、シベリアにかけて広い範囲で生息していながら、現在では絶滅の危機にあるトキを追ったドキュメンタリー作品。中国陜西省の森に生きる、地球上で最後のトキ30数羽の生態が、克明に記録されている。トキのペアが巣を作り、産卵し、ヒナが羽ばたくプロセスが深い印象を残す。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦争と人間 第二部・　愛と悲しみの山河", "戦争と人間 第二部・　愛と悲しみの山河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 182, "軍部が権力を強めるなか、伍代産業は積極的に満州に進出する。そんな時期、俊介と人妻・狩野温子の激しい恋は悲劇的な別離に終った。標耕平は、俊介の妹・順子の愛を感じながらも、反戦運動に身を投じる。大陸では、日本軍が盧溝橋で攻撃の火蓋を切った。ビデオは前篇と後篇がある。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大菩薩峠", "大菩薩峠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "中里介山不朽の名作を、市川雷蔵を主演に映画化。秀麗・富士を遠望する大菩薩峠の頂上に現れた黒紋付着流しの机龍之助が、何のいわれもなく一刀のもとに老巡礼を斬り捨てるプロローグから、幕末動乱の京洛に現れた龍之助の狂乱までを描く。DVDは「大菩薩峠　DVD-BOX」に収録。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAMLET", "ハムレット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "『ロミオとジュリエット』の映画化でシェイクスピア古典劇に一大革命をもたらしたゼフィレッリが今度は悲劇『ハムレット』に挑戦。デンマークの王子ハムレットは、母と結婚し王位に就いている叔父が、実は父を毒殺していたことを、父の亡霊により知らされて……。アクション・スター、M・ギブソンがハムレットを演じるなど、豪華で異色のキャスティングが見ものの正統派古典劇。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RACE WITH THE DEVIL", "悪魔の追跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "旅先で偶然に悪魔崇拝の儀式を目撃したニ組のカップルに、次から次へと執拗に襲いかかる魔の手……。オカルトとカーチェイスを巧みにドッキングさせたアクション・ホラーの異色作。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOP", "パパってなに？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「誓いの休暇」で知られるロシアの巨匠グレゴリー・チュフライの息子パーヴェルが手掛けたヒューマン・ドラマ。1952年のロシアを背景に、初めて大人の男と心を通わせた６歳の少年の揺れる心情を綴っている。母親が列車で知りあった軍人トーリャと出会ったことから、新しい“パパ”ができたサーニャ。トーリャはサーニャにケンカのやり方など、男としての生きる術を教えてくれるが、実は彼はウサン臭い盗人だった。", "1997-01-01", "24,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CHAIR DE L’ORCHIDE´E", "蘭の肉体", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "ハードボイルド作家J・H・チェイスの小説を映画化。莫大な遺産の相続人でありながら、叔母によって精神病院へ入れられた女クレール。病院を抜け出した彼女は、ルイという殺人を目撃した男と出会い、二人で逃避行を続けることになるのだが……。DVD発売時タイトルは「愛の肉体／無修正ノーカット完全版」。", "1974-01-01", "2,4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROMY ＆ MICHELE’S HIGH SCHOOL REUNION", "ロミーとミッシェルの場合", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "気ままな現代女性二人組の自分捜しの道のりを、あっけらかんと描いたコメディ。ロスで共同生活を送るロミーとミッシェルのもとに、高校の同窓会の知らせが届いた。イジメられっ娘だった彼女たちは嘘の出世物語をでっち上げて、10年ぶりに再会する級友たちの鼻を明かそうとするが……。露出度の高い派手な服に身を包み、ピントのズレた会話を交わすヒロインたちのノーテンキぶりに、思わず笑ってしまう。シンディ・ローパーやハワード・ジョーンズらの1980年代のヒット曲が、ノスタルジックな彩りを添えている。ビデオ、DVDタイトルは「ロミー＆ミッシェル」。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("剣難女難", "剣難女難", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "「沓掛時次郎・遊侠一匹」や「緋牡丹博徒・花札勝負」などの名作を生んだ名匠・加藤泰が、３本の記録映画ののちに手掛けた劇映画第１作。兄の仇討ちと地に堕ちた家名を再興するために旅に出た青年剣士が、剣難女難の苦行の果てに目的を達するという物語。何の変哲もないようにみえるストーリーだが、面白いのは青年剣士の剣の腕がまったくダメなところ。めっぽう強く女にもモテるという従来のチャンバラ劇のパターンから外れて、女には追われるが、あまりに弱くて敵にも追われるまさに剣難女難。チャンバラの巨匠で恩師として私淑した伊藤大輔のもとでの経験を生かして、加藤泰が痛快に撮り上げた入魂のデビュー作である。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GEORGY GIRL", "ジョージー・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "のっぽでのろま、特に魅力もないが激しい恋に憧れる22歳のジョージー。ある日、ルームメイトが恋人の子を妊娠。産みたくないと言うルームメイトに対し、面倒をみてもいいと言うジョージー。恋人の心は自然にジョージーに傾いていく。現代娘であろうとも、一度は体験する女心の微妙なうつろいをさわやかなタッチで描く。", "1966-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE VIE", "女の一生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "箱入り娘のジャンヌが最初に出会った男はジュリアン。彼は家柄も良く、優しい紳士だった。だが、ジャンヌと結婚した彼は態度を豹変させ……。離婚が許されなかった時代の女の一生を描いたモーパッサンの原作をアストリュックが見事に映画化した文芸作品。", "1958-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モスラ２・海底の大決戦", "モスラ２・海底の大決戦", "", "", "http://ecx.images-amazon.com/images/I/51809TNBPSL._SL210_.jpg", 99, "1996年に主役として再生した「モスラ」の続編。メッセージ性の強かった前作とは違い、純然たる冒険映画として楽しめる作品になっている。舞台は沖縄、石垣島。幻の王国“ニライカナイ”の科学者が作った海獣ゴーゴが復活。海に入れないモスラは苦戦を強いられ、やがてピラミッドパワーでトビウオのような水中モードに変身する。大人がほとんど登場せず、美少女・満島ひかりをはじめとする子供たちが主人公なのも嬉しい。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICANA", "アメリカーナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "アクション・スターとしておなじみのD・キャラダインが、製作、監督、脚本、音楽、編集、主演と一人６役をこなしたベトナム後遺症をテーマにした作品。カンザスの小さな町を舞台に、一人の復員兵と心を閉ざした少女との交流を通じて、戦争の無意味さを訴えかける。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兵隊やくざ・俺にまかせろ", "兵隊やくざ・俺にまかせろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "またもや新しい部隊に入った大宮と有田は、戦死必至の作戦に狩り出され孟家屯へ向かう。オンボロトラックに乗った一行を待っていたのは共産ゲリラの凄まじい襲撃だった……。見どころは初めて敵の捕虜となる大宮の一大脱出作戦。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ben-Hur", "ベン・ハー", "", "", "http://ecx.images-amazon.com/images/I/51FDcpDhzvL._SL210_.jpg", 222, "ルー・ウォーレスによる同名ベストセラー小説の3度目の映画化。西暦1世紀の初め、ローマ帝国支配下のエルサレムに生まれたユダヤ人貴族の息子ベン・ハーの波乱に富んだ半生を、イエス・キリストの生涯と絡ませて描いた歴史スペクタクル大作。監督は「ミニヴァー夫人」「我等の生涯の最良の年」の巨匠ウィリアム・ワイラー。タイトルロールにチャールトン・ヘストン。59年度のアカデミー賞では作品賞、監督賞を含む史上最多の11部門を受賞した。", "1960-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AGATHA", "アガサ・愛の失踪事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "『オリエント急行殺人事件』などで、世界中を魅了した、女流推理作家の第一人者アガサ・クリスティ。この映画は、彼女が10日間に及ぶ謎の失踪を遂げた事実に基づき、綿密な資料収集、分析によって、その失われた日々の解明に挑んだ意欲作。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Absolute Power", "目撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "デビッド・バルダッチ原作のサスペンスを、クリント・イーストウッド監督・主演で映画化。プロの泥棒ルーサー・ホイットニーは、忍び込んだ豪邸で現職大統領の不倫現場と大統領による不倫相手殺害の一部始終を目撃してしまう……。脚本は「明日に向かって撃て」「大統領の陰謀」の名手ウィリアム・ゴールドマン。共演にジーン・ハックマン、エド・ハリス、ローラ・リニー、スコット・グレン、デニス・ヘイスバートら。", "1997-05-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さわこの恋 上手な嘘の恋愛講座", "さわこの恋 上手な嘘の恋愛講座", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "斉藤慶子が映画初主演したラブ・ストーリー。OL８年目の佐和子は退屈な日々を送っていた。ある日、彼女はフランス料理のシェフ・由紀夫と出会い、一夜をともにする。一流ホテルのオーナーの娘と偽り、由紀夫との恋愛遊戯を楽しむが……。斉藤慶子のベッドシーンが話題に。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIFE POD", "ライフポッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "アルフレッド・ヒッチコックの1943年作品「救命艇」を、舞台を未来の宇宙空間に置き換えて作ったSFサスペンス。地球へ帰途中の巨大宇宙船が爆発し、生き残った９人がライフポッド（救命艇）に乗り込んだ。食料も水もなく、わずかな希望にすがりつく人々。だが実は、爆発は人為的なものであると判明、９人の中に犯人がいることがわかる。お互い疑心暗鬼に陥るなか、一人が殺される……。TVフィーチャー作品。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("好小子第２集", "カンフーキッド・続集", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "田舎から都会に出てきて悪人どもをやっつけた、大人顔負けのカンフー３兄弟の活躍を描いた「カンフーキッド」の続編。カンフーの師でもあるおじさんを誘拐した組織を壊滅しようと、再び活躍するチビッ子カンフーを描く。彼らのカンフー・アクションは必見。", "1988-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DICIOTTENNI AL SOLE", "太陽の下の18才", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "パリ生まれのベルギー人でありながら、1960年代イタリアで成功したアイドル女優、C・スパークの魅力が弾ける陽気なバカンス・ムービー。ナポリ沖のイスキア島を訪れた若き男女の恋のさやあてが、カラフルな彩りも鮮やかに展開される。フランス娘ニコルに扮したスパークは、名前が似ているために知り合った青年ニコラとコミカルな恋を体現。彼女が海辺のテラスでツイストを踊りまくるシーンには、E・モリコーネ作曲、ジミー・フォンタナ作詞＆歌による『TWIST　No.9／太陽の下の18才』が流れる。", "1962-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サソリ 殺す天使", "サソリ 殺す天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「サソリ・女囚701号」に続く、小松千春主演のアクション映画。前作から数年後、松島ナミは妹を殺したヤクザ組長、郷田を追っていたが、郷田は対立する組の放ったヒットマン、英次に殺されてしまう。傷を負った英次を助けたナミは、彼の恋人、一子が刑務所にいることを聞く。そのころ、刑務所内では所長と看守長が女囚をクスリ漬けにし、悪の限りを尽くしていた。ナミは一子を助け出すために、嘱託医として刑務所に潜入するが……。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("吉星拱照／THE FUN LUCK AND TYCOON", "ゴールデン・ガイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "香港の大スターで日本でも根強い人気を誇るチョウ・ユンファ主演の恋愛コメディ。次々に主人公に振りかかる災難をドタバタ調で描く。相手役には、新進実力派女優で、自ら監督も手掛ける才女シルヴィア・チャンが起用され、はつらつとした魅力を十分に発揮している。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トニー滝谷", "トニー滝谷", "", "", "http://ecx.images-amazon.com/images/I/51O6C1LS-2L._SL210_.jpg", 75, "「東京マリーゴールド」の市川準監督が村上春樹の『レキシントンの幽霊』に所収された同名短編小説を映画化。トニーと名付けられ、孤独に生きてきた男が一人の女性と出会い、初めて幸せと愛情に触れる。主人公トニーの暮らす家でストーリーの大半が推移するミニマムな設定ながら、原作の裏側までも読み取って映し出したような市川監督の演出と、主演を務めたイッセー尾形と宮沢りえの力量ある演技で、人の心の移ろいを見つめる奥深い一作となった。坂本龍一の音楽もすばらしい。母を早くに亡くし、父も家を空けがちで家庭を知らずに育ったトニー滝谷。そんな彼は、いつしか孤独を孤独と感じずに生きていた。だが妻と死別した彼は生まれて初めて孤独を知り……。", "2005-01-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("惑星のかけら", "惑星のかけら", "", "", "http://image.eiga.k-img.com/images/movie/57482/315x210.jpg?3193", 73, "3人の女性監督が女性ならではの視線で「女性の恋とセックス」をテーマに撮り上げた「ポルノチック」シリーズの1作。「映画番長」シリーズ第2弾で瀬々敬久監修の「エロス番長」シリーズの1作「ともしび」（2004）を手がけた吉田良子監督が、渋谷の街で出会った男女の一夜の刹那的な恋を描く。母親の再婚を嫌い、帰らぬ父との思い出を求めてかつて住んでいた渋谷の街にやってきた少女・和希は、元恋人を思い続ける青年・三津谷と出会う。和希は三津谷と行動をともにするうちに、ひとりの女性を思い続ける三津谷の姿にひかれていくが……。", "2011-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IGBY　GOES　DOWN", "17歳の処方箋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "『ライ麦畑でつかまえて』の現代版と絶賛された珠玉の青春物語。家族との関係や恋愛に悩み、葛藤する普遍的な少年の成長物語ではあるが、彼を取り巻く個性的な大人たちが様々な“大人の形”を見せ、他にはない面白みを出している。主人公イグビーを演じ、ゴールデン・グローブ賞にノミネートされたK・カルキンは、地で演じているかのように役にハマっている。高校生のイグビーは、ヒステリックな母に落ちこぼれ扱いされ、いつもエリートの兄と比べられていた。無理やり入れられた士官学校で退学をくらったのをきっかけに、イグビーは、名づけ親を頼ってニューヨークへ逃げるが……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Confessions of a Teenage Drama Queen", "彼女は夢見るドラマ・クイーン", "", "", "http://ecx.images-amazon.com/images/I/515KVBV9MGL._SL210_.jpg", 0, "", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NUTTY PROFESSOR", "ナッティ・プロフェッサー　クランプ教授の場合", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ジェリー・ルイス主演の「底抜け大学教授」を、主人公が大デブというアレンジでリメイクした爆笑コメディ。体重180キロに悩むウエルマン大学のクランプ教授は、脂肪遺伝子を刺激する薬を開発して、スリムなボディーを手に入れることに成功する。しかも性格まで積極的になり、話術巧みなプレイボーイに大変身。化学講師の美女カーラを口説き始めるが、薬の効果はわずか数時間だったのだ。E・マーフィが巨漢や色男などを巧みに演じ分け、一人７役をこなす。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE WILDERNESS", "白い荒野", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ディズニー・プロ製作“自然の冒険”シリーズの一編。３年の撮影期間をかけて、集団自殺する小動物レミング、北極海を遊泳する白鯨、氷河時代の生き残りジャコウ牛の生態などを紹介。アカデミー賞をはじめベルリン、エジンバラ映画祭でも最優秀賞に輝くドキュメンタリー映画。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE REPOS DU GUERRIER", "戦士の休息", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "父の遺産で何不自由なく暮らす25歳の女のもとに、ふとしたきっかけで男がころがり込んでくる。女は彼を愛しながらも、その自堕落な生活ぶりに嫌気がさし、父親のようなカトフのもとに走るのだが……。バルドーの美しさを遺憾なく見せた作品。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CORRUPTION", "若妻の匂い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "裕福ではあるが留守がちな夫と性的にうまくいかない若く美しい人妻アンナ。やがて彼女は小間使いの娘アンジェラの危険な誘いを受け、官能の世界へ徐々に飼い慣らされていく。ナイーブな感性が受けたS・サンペリ監督によるソフト・ポルノ。", "1986-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HALFMOON STREET", "ハーフムーン・ストリート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ロンドンの中東戦略研究所に勤める学者ローレンは、夫と離婚寸前でボロアパートに一人で住んでいる美貌の女性。ひょんなことから高級エスコートクラブに誘われた彼女は、それ以来理性から解放され、自由気ままに男を楽しむようになる。しかし、クラブを通して中東問題の権威ブルベック卿と知り合ったことから、国際的な陰謀に巻き込まれていく……。知性と美を兼ね備えた現代女性が、愛と政治の間で揺れ動く姿を描いたラブ・サスペンス。S・ウィーヴァーが、新しい側面を見せた作品。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("椿三十郎（1962）", "椿三十郎（1962）", "", "", "http://ecx.images-amazon.com/images/I/51onZYoWV2L._SL210_.jpg", 96, "名作「用心棒」の続編ともいえる作品で、前作では桑畑を名乗った三十郎が椿三十郎として活躍。キャラクターとしてはより人間味が増し、ユーモアと知略を駆使し、上役の不正を暴こうと立ち上がった9人の若侍をその凄腕で助けていく。加山雄三をはじめとした血気にはやる若侍たちをうまく制御し、敵方の用心棒仲代達矢と知恵比べをしつつ、有名なラストの決闘シーンへと物語は導かれていく。", "1962-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Things to Do in Denver When You're Dead", "デンバーに死す時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "遺言ビデオの製作会社を経営するジミー。ギャング時代に世話になったボスの頼みで彼は、ボスの息子をフった女の新しい恋人を痛めつけることになる。旧知の仲間とともに計画を実行したジミーだが、思わぬ失敗をしてしまう……。まるで日本のヤクザ映画のような心意気をA・ガルシアが見せるハードボイルド・ロマン。G・フレダーが新人らしからぬ語り口で綴っていく。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うる星やつら いつだってマイ・ダーリン", "うる星やつら いつだってマイ・ダーリン", "", "", "http://ecx.images-amazon.com/images/I/210562W204L._SL210_.jpg", 78, "オリジナル・ストーリーのイベント用フィルムとして製作された映画版第６作。幼なじみの青年の心を射とめようと、宇宙の王女・ルピカが繰り広げる惚れ薬獲得騒動に、例によってナンパ高校生・諸星あたると宇宙人少女・ラム、そしてその仲間たちが巻き込まれる。動きは粗いもののドライなタッチのアクション・コメディとして、そのイキのよさが楽しめる。DVDタイトルは「うる星やつら 劇場版 いつだって・マイ・ダーリン」。", "1991-11-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画監督 山本薩夫", "映画監督 山本薩夫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "山本薩夫監督の没後10周年記念として作られた作品。「真空地帯」（1952）、「戦争と平和」（1970～1973）などが作られた過程や、山本薩夫本人の思想の背景が、当時のスタッフの言葉などを中心に描いている。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOST COMMAND", "名誉と栄光のためでなく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "インドシナからアルジェリアへ転戦したフランス軍パラシュート部隊を待ちかまえていたのは、かつての戦友で今は独立運動のリーダーとなったアラブ人の反撃であった。しかも隊の中尉が恋に落ちた女はリーダーの妹。戦争の無意味さを描くベストセラー小説の映画化。DVDタイトルは「ロスト・コマンド　名誉と栄光のためでなく」。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRAGONHEART", "ドラゴンハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "勇敢な騎士と最後の生き残りのドラゴンの友情を描いた冒険ファンタジー。時は10世紀。ドラゴンに命を助けられたアイノン王子は冷酷な王に成長。子供の頃の心優しい王を知る騎士ボーエンは、ドラゴンのしわざと思い込み、ドラゴン退治へと出かける。だが、最後の生き残りドラゴンのドレイコと出会い、王を暴君にさせたのはドラゴンの心でないことがわかり、ドレイコとボーエンは王を倒すために立ち向かっていく。「ジュラシック・パーク」のILM視覚効果チームが、躍動感あるドラゴンを作り出した。また、ドレイコの声をS・コネリーが担当している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRAGONARD", "ドラゴナード～カリブの反乱～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "18世紀、カリブ海のセント・ジョセフ島に奴隷として売られてきたリチャードをめぐり、女たちの愛欲が入り乱れる。“ドラゴナード”とは龍の舌のような、脂ぎった巨大な皮のムチによる残虐な処刑法のこと。締めつけられた奴隷たちの怒りが一気に爆発するクライマックスは壮快。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モンローのような女", "モンローのような女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1962年８月に急逝したマリリン・モンローに似た運命をたどる一人の女を主人公にした、舟橋聖一の同名の連載小説の映画化作品。17歳の少女がその美しい肉体ゆえに水着モデルとなり評判になるが、家庭的には恵まれずいや気がさして家出。やがてヌードモデルとしてやっていこうと決心するまでを描く。監督は渋谷実。主人公の少女には、松竹の公募で選ばれた真理明美が起用されている。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Frost/Nixon", "フロスト×ニクソン", "", "", "http://image.eiga.k-img.com/images/movie/54322/315x210.jpg?3659", 122, "1977年、英国のコメディアン出身で人気トーク番組の司会者デビッド・フロストが、ウォーターゲート事件で辞職した元米国大統領リチャード・ニクソンにインタビューを申し込む。フロストはアメリカ進出を、ニクソンは汚名返上と政界復帰を賭けてインタビューに臨むが……。4500万人が視聴したといわれる米テレビ界伝説のインタビュー番組収録の模様とその舞台裏を、ロン・ハワード監督が映画化。原作は「クィーン」のピーター・モーガンによる舞台劇。第81回アカデミー賞では7部門にノミネート。", "2009-03-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒポクラテスたち", "ヒポクラテスたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "京都の医科大学に学ぶ若者たちの日常を、自らも医大生であった大森一樹がいきいきと描いた青春映画。洛北医科大学に通う医大生・愛作と彼を含む７人の臨床実習グループの様々な出来事と、愛作の寮での日常生活が同時進行で描かれる。医学生の最終学年は臨床実習にあてられ、７人ほどのグループに分けられる。愛作のグループには、医者になることに不安を抱くみどり、プレイボーイの河本、野球の大好きな王、脱サラで二人の子持ちの加藤、ガリ勉の大島、どことなく頼りない西村がいた。寮では、新入生の野口、左翼運動家の南田が連日のように寮運営や現代医学への疑問について論議をしていた。愛作はそうした中で医者の卵として成長していく……。ラジカセからキャンディーズの曲を流したり、大森の自作８ミリを登場させたり、楽屋オチも楽しい。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雨の町", "雨の町", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "とある地方都市のある町に、内臓の全くない子どもの死体が流れ着いた。フリーライターの兼石荘太は、事件を調べるため町に乗り込むのだが、遺体の安置現場から死んでいたはずの子どもが立ち上がり、姿を消すのを目撃してしまう。果たして事件の真相は……？「吸血鬼ハンター“D”」などで知られる人気作家、菊地秀行の短編小説を映画化したホラー・サスペンス。主役のフリーライターを「県庁の星」の和田聰宏が演じている。", "2006-03-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Shining", "シャイニング", "", "", "http://ecx.images-amazon.com/images/I/51HfI2zRMML._SL210_.jpg", 119, "スティーブン・キングの小説をスタンリー・キューブリック監督が映画化した名作ホラー。冬の間は豪雪で閉鎖されるホテルの管理人職を得た小説家志望のジャック・トランスは、妻のウェンディーと心霊能力のある息子ダニーとともにホテルへやってくる。そのホテルでは、かつて精神に異常をきたした管理人が家族を惨殺するという事件が起きており、当初は何も気にしていなかったジャックも、次第に邪悪な意思に飲みこまれていく。主演のジャック・ニコルソンがみせる狂気に満ちた怪演は見どころ。高い評価を受けた作品だが、内容が原作とかけ離れたためキングがキューブリック監督を批判したことでも知られる。", "1980-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すんドめ", "すんドめ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "岡田和人の同名コミックを映画化したエロティック・コメディ。退屈な学生生活を送っていた童貞高校生・英男。そんな彼の日常が、転校してきた美少女・胡桃によって一変。胡桃のサディスティックな命令に従うことに快感を覚えるようになっていく。一方、胡桃にライバル心を燃やす巨乳の同級生・京子も英男にちょっかいを出しはじめる。胡桃役に、本作が映画初主演となるグラビアアイドルの鈴木茜。", "2007-11-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Joy Ride", "ロード・キラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "S・スピルバーグの「激突！」を彷彿とさせるサスペンス・スリラー。アメリカ東部から西部へと車を走らせる若い兄弟フラーとルイスが、ラジオ無線で交信したドライバーをからかう。ところがラスティ・ネイルと名乗る相手の男は予想外に激怒し、巨大トラックで執拗に兄弟を追跡してくるのだった。まったく姿を見せない殺人ドライバーに命を狙われる若者たちの悪夢を、L・ソビエスキー扮するヒロインとの三角関係を絡めて描写。夜のモーテル、ガソリンスタンド、トウモロコシ畑などの空間を利用してじわりと恐怖を盛り上げるJ・ダール監督の巧妙な演出が光る１本だ。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Countdown to Zero", "カウントダウンZERO", "", "", "http://image.eiga.k-img.com/images/movie/55722/315x210.jpg?2818", 89, "「ブラインドサイト　小さな登山者たち」のルーシー・ウォーカー監督が、「不都合な真実」の製作者とともに撮りあげた社会派ドキュメンタリー。2009年、アメリカ合衆国のバラク・オバマ大統領が表明した構想「核なき世界」の一方で、2010年現在、全世界で核兵器保有国は9カ国、核兵器開発技術を持つ国は40カ国に上る。各国の元首脳や工作員が、核兵器の実態と脅威を訴える。", "2011-09-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラマンゼロ THE MOVIE 超決戦！ベリアル銀河帝国", "ウルトラマンゼロ THE MOVIE 超決戦！ベリアル銀河帝国", "", "", "http://image.eiga.k-img.com/images/movie/55592/315x210.jpg?4481", 0, "ウルトラマンシリーズの45周年を記念して製作された劇場版。ウルトラセブンの息子・ウルトラマンゼロに倒されたはずの邪悪なウルトラマンベリアルが、“カイザーベリアル”として生き延びていた。ある惑星で出会った勇敢な人間の青年・ランと一体化したゼロは、巨大な銀河帝国を築いて宇宙制覇を目論むカイザーベリアルを阻止するため、仲間たちとともに大宇宙へと飛び立つ。ラン役に抜てきされたのは、「クローズZERO」の小柳友。", "2010-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖花鏡", "妖花鏡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "後継ぎを生むため富豪のもとへと嫁いだものの、妊娠の兆しがない娘オンニョ。乳母と御用医者はこれに乗じて財産を乗っ取ろうと、下男のユボクを彼女に近づける。やがて彼らは恋に落ち、夜毎激しい情交を重ね……。「真由美」でキム・ヒョンヒを演じたユン・アヨンが大胆な濡れ場を演じるコリアン・エロス。", "1988-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Exorcist", "エクソシスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "　女優クリスの12歳の娘リーガンはある時から何かに憑かれたかのようにふるまうようになる。彼女の異変は顕著になるが、病院の科学的な検査でも原因は判明しない。やがて醜い顔に変貌したリーガンは緑色の汚物を吐き、神を冒涜するような卑猥な言葉を発するようになる。悪魔が彼女に乗り移ったのだ。その後、リーガンの前にふたりの神父メリンとカラスが訪れ、悪魔祓いを始めるが……。世界中にオカルトブームを巻き起こした戦慄のホラー映画。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("虞美人草", "虞美人草", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "夏目漱石の同名小説を映画化。甲野は父の後妻の豊乃とその娘、藤尾と暮らしている。豊乃は、甲野家を藤尾と二人のものにしたいと願ったが……。身勝手な母子と、それを取り巻く人々との愛憎劇が描かれる。原作の味わいを残した脚本と、藤尾役の霧立のぼるの好演が光る。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大いなる驀進", "大いなる驀進", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「大いなる旅路」（1960）の姉妹編で、東京−長崎間を走る特急“さくら”を舞台にしたヒューマン・ドラマ。殺人犯の同乗、すり、乗客の自殺未遂、台風の襲来、崖崩れ。様々な事件とそれに絡む人間模様、そして過ぎ去って行く町々の景色をあざやかに描いた、紀行ものとしても楽しめる一編。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOTHING", "NOTHING〈ナッシング〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "斬新なヴィジュアル・センスとアイデアでセンセーションを巻き起こした「CUBE」「カンパニー・マン」の異才ヴィンチェンゾ・ナタリの長編第３作。仕事や人間関係など、嫌なものをすべて消し去り、何もない“無”の世界に迷い込んだダメ男二人。その混乱をオフビートな笑いで描く。幼なじみ二人が、災難続きで世の中のすべてが嫌になる。やがて彼らは、自分たちを煩わせるものを消す能力があることに気づき……。", "2005-09-17", "10,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CASA SPERDUTA NEL PARCO", "真夜中の狂気", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "一見善良そうに見えながら、実はレイプ殺人を繰り返しているアレックス。彼のサディスティックな正体を知らずに、パーティーに招待したため、死の週末を送ることになる６人の男女の恐怖を描いたスプラッタ・ムービー。ビデオ、DVDタイトルは「アニ−・ベル　真夜中の狂気」。", "1980-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ろくでなし野郎", "ろくでなし野郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "奇想天外なストーリーが展開するコメディ・アクション。自称“イタリア帰り”の神父・佐伯権太郎がふらりと町に現れて、黒い法衣をたくし上げながら並みいる悪と対立する日活アクション映画。パルプ工場設立のための土地買収騒ぎにまつわる恩人の死の真相を知った権太郎は、町の浄化のために教会を建立しようとし、悪の一味と決闘するハメになる。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mrs.Brown", "Queen Victoria　至上の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "1861年、ビクトリア女王は夫のアルバート公を亡くして以来、喪に服した生活を続けていた。財政界は女王の馬の世話係として亡きアルバート公のスコットランド人従僕ブラウンを派遣する。純朴な彼は憔悴しきった女王の気持ちが和らぐよう、忠誠を尽くし続け、やがて女王は心を開くようになっていく……。女王と従僕の秘めた恋を描く文芸作品。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Way", "マイウェイ 12,000キロの真実", "", "", "http://image.eiga.k-img.com/images/movie/55982/315x210.jpg?1553", 145, "「シュリ」「ブラザーフッド」のカン・ジェギュ監督が韓国映画史上最大級となる製作費25億円を投じ、アジアからノルマンディーまで1万2000キロを生き抜いた男の実話を映画化。1944年、史上最大の上陸作戦として知られる「ノルマンディー上陸作戦」が行われ、その作戦終了後、ドイツ軍捕虜の中から1人の東洋人が発見される。男は日本兵として日本統治下の朝鮮半島を出発し、ソ連、ドイツを経てノルマンディーへ到達。日本、ソ連、ドイツの3カ国の軍服を着て戦い、敗戦に次ぐ敗戦で何度も捕虜になりながらも1万2000キロを生き抜いていた。主演はオダギリジョーとチャン・ドンゴン。", "2012-01-14", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("林檎のうさぎ", "林檎のうさぎ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "WOWOWの〈J・MOVIE・WARS〉シリーズのプロデューサーとして活躍している小林広司が監督デビューを果たしたホームドラマ。やんちゃな６歳の幼稚園児・雄大は、外国航路の船長をしている父親が不在がちで、ちょっと寂しい毎日。そんな雄大にとって、運動会で母親と踊るフォークダンスが楽しみ。運動会が間近に迫ると、大好きな“林檎のうさぎ”を作ってもらうため、りんごを山ほど買い込み、その日に備えるのだった。ところが母親は病気になり、運動会当日も弁当を作るのが精一杯。フォークダンスは姉と踊ることになるのだが……。廃園となった幼稚園にセットを組み、園児のエキストラを集めて、父兄参観日、運動会のシーンを撮影。さわやかな感動が漂う好編になった。第２班監督の演出助手として、小林と「エレファントソング」で仕事をともにした利重剛が参加。母親役も「エレファント…」の松田美由紀が演じた。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DEAD CLASS", "THE DEAD CLASS 死の教室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "現代演劇界の鬼才、タデウス・カントールが主宰する“クリコット２”によって上演された前衛演劇を、ワイダ監督が忠実に映像化した作品。もはや死を待つばかりとなった12人の老人たちが、かつてともに過ごした小学校の教室に集まり過去の再現を試みるが……。", "1976-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SADE", "発禁本・SADE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "“サディズム”の語源になったマルキ・ド・サド。スキャンダルな作家にして哲学者とも語られる彼の後半生を描いた人間ドラマだ。サド役に挑んだフランスの名優、ダニエル・オートゥイユの熱演が光る。革命の嵐が吹き荒れる1794年のフランス。要注意人物と見なされるサド伯爵は再び獄中生活を送っていた。そんな彼の前に、性の体験に憧れを抱く貴族令嬢のエミリーが現れる。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BILOXI BLUES", "ブルースが聞こえる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "アメリカきっての人気劇作家N・サイモンが自らの青春期を書き綴った戯曲『ビロクシー・ブルース』を、「パーフェクト・カップル」のM・ニコルズ監督が映画化。舞台は1945年、ミシシッピー州ビロクシー軍事訓練所。そこに高校卒業と同時に徴兵されてきた純情なジェロームら６人の仲間が、厳しい軍隊生活の中で成長していく姿を、後に作家となった彼の回想を通して綴っている。サイモンならではのユーモアとノスタルジックなムードがたっぷり。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Havre", "ル・アーヴルの靴みがき", "", "", "http://image.eiga.k-img.com/images/movie/57822/315x210.jpg?4062", 93, "「浮き雲」「過去のない男」のアキ・カウリスマキ監督が、フランスの港町ル・アーブルに生きる人々の姿を描いた人情劇。北フランスの港町ル・アーブルの駅前で靴磨きをして暮らしているマルセルは、妻アルレッティと愛犬ライカとともにつつましい生活を送っていた。そんなある日、港にアフリカからの不法移民が乗ったコンテナが漂着し、マルセルは警察に追われていた1人の移民の少年イングリッサと出会う。そしてその頃、アルレッティは医師から余命宣告を受けており……。妻のアルレッティ役にカウリスマキ作品常連のカティ・オウティネン。", "2012-04-28", "20,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の武士", "風の武士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1960年代なかばに流行した忍者もの時代劇の一つ。絵巻物をめぐって繰り広げられる、幕府隠密と紀州藩の戦い。加藤泰はこれに大川橋蔵と桜町弘子のロマンスを絡めて、チャンバラ・メロドラマに仕上げた。スピード感ある演出は絶品。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("清作の妻", "清作の妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1924年日活版（村田実監督）に次ぐ再映画化。元囲われ者の若尾文子が、村の模範青年と結婚する。村人たちの白い目。夫は日露戦争で負傷して帰るが、二度と戦場へ行かせぬよう、妻は夫の目を五寸釘で突くのであった。増村流の女性のパッションが凄まじく鬼気迫る。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Y.M.O.PROPAGANDA", "Y.M.O.PROPAGANDA", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "80年代にテクノ・ブームを起こしたユニット“YMO”。彼らが6年の活動に終止符を打ったラストコンサートツアー“散開”の模様に追加映像を加えたドキュメンタリー。世界の坂本龍一が“教授”と呼ばれていたあの時代。彼らの独特のスタイルは誰にも真似できない。当時を知る者にとっては懐かしい映像が満載だ。DVDタイトルは「A Y.M.O. FILM PROPAGANDA」。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STOP THE BITCH CAMPAIGN 援助交際撲滅運動", "STOP THE BITCH CAMPAIGN 援助交際撲滅運動", "", "", "http://image.eiga.k-img.com/images/movie/54372/315x210.jpg?3595", 85, "カルト・シリーズ「援助交際撲滅運動」「援助交際撲滅運動・地獄変」に続く第3弾。監督は前2作に引き続き鈴木浩介。妹の自殺の謎を追っていたライターのあずさは、“エンボクのクニ”と呼ばれる変質者の情報を得る。クニは“援助交際撲滅運動”と称しては女子高生を強姦し、身体に“メスブタ”という焼印を押すという。あずさは妹の体に残されていた焼印を思い出し、クニの捜索を始めるが……。", "2009-03-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILLING MACHINE", "キリング・マシーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "アメリカ政府によって、記憶を消され容姿も変えられてよみがえった暗殺者ギャレット。彼は体に染み着いた殺しのテクニックだけを残し、政府のための殺人機械となることを求められたのだ。生き延びるために、次々と下される暗殺指令をこなしていくギャレット。しかし殺人機械だったはずの男は、標的の女性科学者を愛してしまう……。テコンドーの達人でもあるジェフ・ウィンコットが、本物の迫力で激しいアクションシーンを演じる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EDGE", "ザ・ワイルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "大富豪の老紳士チャールズが、若いモデルの妻とその仕事仲間とともにアラスカにやって来る。彼は妻がカメラマンのロバートと浮気をしているのではないかと疑い、二人の様子をうかがっていた。そんなある日、チャールズとロバート、その助手を乗せた水上飛行機が湖に墜落。奇跡的に命を取り留め、山中をさまよう彼らは、自然の猛威と人喰い熊の恐怖にさらされることになる。劇作家D・マメットが生死を懸けた男たちのドラマを重厚に脚色したサバイバル・アクション。墜落シーンや巨大熊と格闘するシーンなど、リアルなアクション描写はかなりの迫力。さらに、敵意を秘めた二人の男の心理を克明に描き、人間の内に潜む暗部をえぐり出すなど、物語の節々ににじむ厳しい視点がズシリと重く心にのし掛かってくる。寡黙な紳士役A・ホプキンスとカメラマン役A・ボールドウィンが、憎しみと友情が交錯する複雑な関係にある役どころを熱演。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜を賭けて", "夜を賭けて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "劇団・新宿梁山泊の座長である金守珍が、直木賞候補になった梁石日の長編小説を映画化。戦争の焼け跡が残る昭和30年頃の大阪を舞台に、兵器工場跡から鉄屑を盗んで売りさばいたという実在の集団“アパッチ族”の破天荒な生き様を描く。韓国に壮大なオープンセットを打ち建て、在日コリアンの男女が住む集落を再現。恋やケンカに奮闘する主人公・金義夫に扮した山本太郎をはじめ、役者たちのハイテンションな芝居が観る者を圧倒する熱血ドラマだ。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地球攻撃命令 ゴジラ対ガイガン", "地球攻撃命令 ゴジラ対ガイガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "地球征服をたくらむ、M宇宙ハンター星雲のインヴェーダーに操られた新怪獣ガイガンとキングギドラが飛来し、怪獣島から馳せ参じたゴジラとアンギラスが対決することに。ゴジラとキングギドラの対決シーンには、大量のライブフィルムが使用され安易な印象を与える。", "1972-03-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嵐の勇者たち", "嵐の勇者たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "暗黒街のボスのシンジケート結成を祝うパーティーの席上でともにボスに恨みを持つ二つのグループ（石原裕次郎率いる元刑事組と渡哲也率いるボスにつぶされた組織の生き残り組）が彼の愛妾を狙う。渡のグループが妾の誘拐に成功し、隠れ場所に油壷のボスの別荘を選ぶが、シンジケートは渡一派も石原一派もひとまとめにして消してしまおうと攻撃を開始。二派は連合して敵を迎え撃つことに。石原一派に二谷英明、浜田光夫ら、渡一派に郷英治、藤竜也ら、そしてボスの愛妾・浜美枝の助手たちには吉永小百合、山本陽子らを配した賑やかなオールスター日活アクション。舛田利雄が登場人物を手際よくさばく。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女校生 天使のはらわた", "女校生 天使のはらわた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "1970年代、若者たちに圧倒的な支持を得た石井隆のコミックを原作としたロマンポルノ。ひったくりや強姦を繰り返す二人の兄妹が出会った女子高生・名美。彼らを通して、暴力と性の狂おしいまでの青春が綴られていく。現代の若者たちの“はらわた”を赤裸々に描き続けた“名美シリーズ”の第１作。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京騎士隊", "東京騎士隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "前年の「くたばれ愚連隊」に続く、鈴木清順監督、和田浩治主演の第２作。父の急死で松原組三代目を継いだ高校生の孝次が、父の死に陰謀が絡んでいることを知り、真相を突き止める。和田浩治の若さにまかせたアクションが見られるが、主演を張るには貫祿不足。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富江 re-birth", "富江 re-birth", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "伊藤潤二の人気ホラー・コミックを映画化したシリーズの第３作。殺しても殺しても増殖と再生を繰り返す富江が、美大生の前に姿を現し、周囲の人々を恐怖の底へと陥れていく。富江に扮するのは、「愛する」の酒井美紀。次々と男たちを虜にしていく魔性の女を熱演し、これまでにない新境地を見せている。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("電撃戦隊チェンジマン", "電撃戦隊チェンジマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 24, "神秘の力アースフォースを浴びてチェンジマンとなり、地球征服を目論む大星団ゴズマに立ちむかう若者たちの闘いを描くテレビシリーズ「電撃戦隊チェンジマン」の劇場版。悪の星王バズーが、レドン鉱石を地球のマグマと反応させて全てを焼き尽くす「地球ファイヤー作戦」に乗りだした。チェンジマンたちはパトロール中に発生した不審な地震を調査するため震源地近くの大鷹山国立地震研究所へと向かうが、職員たちはすでに宇宙獣士カミラによって操られていた。", "1985-03-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INFINITY", "インフィニティ 無限の愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "ノーベル賞を受賞した物理学者で、原爆の開発者としても知られるリチャード・ファインマンの自伝を映画化。主演のM・ブロデリックが母パトリシアの脚本をもとに初監督し、ファインマンの切なくも美しい愛の日々を描き出す。1934年、若き科学者リチャードは、パーティーで知り合った知的な女性アーリーンと恋に落ちる。しかし、彼女は不治の病に冒されていた。苦悩するリチャードは、周囲の反対を押し切って彼女との結婚を決意するが……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎夢枕", "男はつらいよ・寅次郎夢枕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "マドンナに八千草薫を迎えた、寅さんの恋愛教室といった趣のある一編である。死んだ香具師仲間の墓参りをして柴又へ戻った寅さんだが、自分の部屋にはT大教授が入りこんでいる。むくれた寅の前に幼なじみで美しくなった千代が現れる。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旗本退屈男 謎の七色御殿", "旗本退屈男 謎の七色御殿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "月照宮に仕える７人の巫女の一人が、何者かに暗殺されるという事件が起こる。これを目撃した娘芸人の千鳥と小百合も危うく殺されそうになるが、退屈男・早乙女主水之介に救われる。共演陣も村田英雄、こまどり姉妹といったにぎやかな顔触れ。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざ道入門", "やくざ道入門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "山城新伍の監督第５作。実在の清水次郎長５代目をモデルに、今やすたれた“任侠道”とは何かを描く。次郎長５代目に菅原文太、彼の所に“おとこ”を学びに来る若者、三郎に文太の実子・菅原加織が扮した。切ったはったの描写はまるでなく、庶民生活の中に今も生きる侠気を、笑いを交えて捉えていく。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWELVE O’CLOCK HIGH", "頭上の敵機", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "第二次大戦時、対独戦略爆撃に活躍した、第918空軍の姿を実話をもとに映画化した戦争ドラマ。大量の犠牲者を出した部隊は、これまでの隊長に代わってその親友のサベージが指揮を執ることに。だが彼の猛訓練は部下の反感を買い、全員が転任願いを出す事態となった……。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACES", "エイセス 大空の誓い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "かつては敵として戦いながら、今は航空サーカスでチームを組む米・英・日・独の４人の元エースパイロット。麻薬密輸事件に巻き込まれて死んだ部下の仇を討つため、彼らは圧倒的に不利な戦いに飛び出していく。知恵と経験と友情だけを頼りにして……。千葉真一（サニー千葉）がアメリカで主演を務めた航空アクション。ビデオタイトルは「エイセス／ アイアン・イーグル III」。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WAR AT HOME", "THE WAR　戦場の記憶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "E・エステヴェスが監督、主演を兼任したヒューマン・ドラマ。ベトナム戦争の傷を心に抱えた男の苦悩を、真正面から描く。ベトナムから帰還したジェレミーは、帰国して１年たっても悲惨な体験をぬぐいきれない。必要以上に明るく振る舞う母や、あくまでも厳格な父、自分の事しか考えられない妹に、彼のイライラは募る一方だった。やがて感謝祭の日、ジェレミーの我慢は限界に達し……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪名無敵", "悪名無敵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "“悪名”コンビの朝吉と清次は、売春組織で荒稼ぎする新湊組から家出娘を救出し、売春組織を解散させる。藤村志保が、売春組織の女親分を演じる。一見ミスキャスト風な配役がミソ。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VIE EST UN LONG FLEUVE TRANQUILLE", "人生は長く静かな河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "絵に描いたような中流家庭ルケノワ家と、盗みやバクチでしたたかに生きる労働者階級のクロゼイユ家。ある日ルケノワの娘とクロゼイユの息子が産院で取り違えられていたことが発覚。一方はパニック状態となり、また一方は金づるができたとほくそえむ。フランスで大ヒットした皮肉たっぷりのコメディ。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA LEGGENDA DEL SANTO BEVITORE", "聖なる酔っぱらいの伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "パリを舞台に、ある酔っ払いが体験する奇妙な出来事を描いている。日曜の朝に教会で返すことを条件に、初老の紳士から200フランを貸してもらうことになったアンドレアス。それからというもの彼の身の上に奇妙な出来事が続く。いつの間にか時は過ぎ、彼が約束を果たせないでいるうちに、彼の前に少女に姿を変えた聖テレーズが現れる……。", "1988-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MELAMPO", "ひきしお", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "エーゲ海の孤島にたどり着いた美しい女性、リザが、島に一人住む中年男と暮らしはじめ、犬のように従順にかしずくという、鬼才フェレーリの異色作。ドヌーヴの大胆な演技が話題を呼んだ。", "1971-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOR　ROSEANNA", "ロザンナのために", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "J・レノの温かい個性を活かしたハートフル・コメディ。気のいい中年男マルチェロはイタリアの小村でトラットリアを経営している。彼の妻ロザンナは不治の病に冒されており、愛娘と同じ墓地に眠ることを願っていた。だが、墓地にはあと３人分の空きしかない。しかも、マルチェロの恋敵だった地主は、かたくなに墓地拡張用の土地を譲ろうとしない。かくして、マルチェロは涙ぐましい努力を重ねる……。死者を出すまいと、重病患者の見舞いや献血、はては交通整理にも乗り出す、主人公の愛すべき単純さが微笑ましい。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女教師", "女教師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "耽美的映像感覚の持ち主である田中登監督が初めて挑んだ社会派ロマンポルノ。美しい女教師が放課後、数人の中学生グループに暴行される。事件の事実が広まり、退職まで考えた彼女は、やがて気を取り直して再び登校するのだが……。非行少年役で、古尾谷康雄（のちの雅人）が鮮烈な印象を与えている。泉谷しげるのフォーク・ソング『春夏秋冬』が効果的に使われている。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マリと子犬の物語", "マリと子犬の物語", "", "", "http://image.eiga.k-img.com/images/movie/53112/315x210.jpg?1700", 124, "新潟県中越地震発生時の実話を基に、被災地に取り残されながらも、産んだばかりの子犬たちを必死で守り抜こうとする母犬の奮闘を描いた感動作。新潟県山古志村で暮らす石川家に、新しい家族がやって来る。小学生の亮太と妹の彩が子犬を拾ってきたのだ。“マリ”と名付けられたその子犬はすくすくと成長し、やがて3匹の子を産む。しかし、新しい命の誕生に喜んだのもつかの間、村をマグニチュード6.8の大地震が襲い……。", "2007-12-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LONG VOYAGE HOME", "果てなき船路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "第二次世界大戦初頭、貨物船グレンケーン号は乗組員にも極秘で爆薬輸送のための任務に就いた。目的も知らされることなく、危険だけを告げられての航海に、船員たちの不安と緊張は高まっていく。男たちはおし黙ってそれに耐えて日々の任務を遂行するが、次々に試練が訪れる。錨が外れる事故、一人の船員へのスパイ疑惑、敵機の攻撃。その度に人が死んだり犠牲を払わなければならない。やがて港に着いた彼らは二度と船に戻らぬというのだが……。ユージン・オニールの初期の戯曲をもとにJ・フォードが「わが谷は緑なりき」と同じ年に、戦争をにらみながら愛国心を発露させた作品。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Book of Eli", "ザ・ウォーカー", "", "", "http://image.eiga.k-img.com/images/movie/55212/315x210.jpg?3793", 118, "ジョエル・シルバー製作、デンゼル・ワシントン主演の近未来アクション。あらゆる文明が崩壊した近未来の地球。イーライと名乗る男（ワシントン）は、世界でたった1冊残る本を運び、30年間、ただひたすら西へ向かって旅をしていた。そんな彼の前に、その1冊の本を探し続ける独裁者・カーネギーが現れる……。監督は「フロム・ヘル」のヒューズ兄弟。共演にゲイリー・オールドマン、ミラ・クニス、トム・ウェイツ。", "2010-06-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2010-2011 グルック「タウリスのイフィゲニア」", "METライブビューイング2010-2011 グルック「タウリスのイフィゲニア」", "", "", "http://image.eiga.k-img.com/images/movie/56472/315x210.jpg?1823", 153, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を劇場のスクリーンで上映する「METライブビューイング」2010～11年シーズンの7作目。父アガメムノンにより、月の女神ディアーヌの怒りを鎮めるための生けにえにささげられたイフィゲニアだったが、女神の哀れみにより助けられ、スキタイ人の島タウリスで巫女として生きることになる。生き別れた最愛の弟オレストを思いながら長い年月を暮らしていたある時、オレストが島にたどり着き……。3大テノールの1人、プラシド・ドミンゴが主演。", "2011-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラスト・フランケンシュタイン", "ラスト・フランケンシュタイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "“劇団第三エロチカ”主宰の川村毅が、４年前の舞台作品を自ら映画化した意欲作。“自殺病”が大流行している現代社会。三枝教授は自殺病の解明と、超能力を持つ娘の病気の治療法を得るために、超人類の研究をしている阿良博士の家を訪ねるが、そこで三枝が見たものは……。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEAP OF FAITH", "スティーブ・マーティンの奇跡を呼ぶ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "神の名を語って商売するインチキ伝導師が、旅先で出会った不幸な人々に触発され、本当に奇跡を起こしてしまう、まさにマーティンならではのハート・ウォーミングなコメディ。D・ウィンガーをはじめとする共演陣も豪華な顔ぶれ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談乳房榎", "怪談乳房榎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "絵師の菱川重信が弟子入りしてきた浪人者の浪平によって惨殺され、彼の遺産と妻までもが浪平のものとなってしまった。乳飲み子を抱えていた妻は、哀しみのあまり乳が出なくなってしまうが、そんなとき亡霊となった重信が現れ、乳の出る不思議な榎の木を教える……。三遊亭円朝の『乳房榎』を原作にした怪談映画。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鴬城の花嫁", "鴬城の花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "第１回東映スコープ作品「鳳城の花嫁」の姉妹編であり、同作品の脚本を執筆した松村昌治が監督にあたった。鴬城に松、竹、梅の美人姉妹がいたが、強情っぱりの松姫はなかなか縁談を承諾しようとしない。心配した城主及び家臣一同は、彼女をなんとか目覚めさせようと四苦八苦するのだが……。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIPSTICK CAMERA", "リップスティック・カメラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "カメラマン志望のオミーは、雇ってもらおうと訪ねたトップカメラマンの所で、妖艶な人妻リリーと出会う。リリーの“私のベッドシーンを撮って欲しい”という依頼に、オミーは友人のジュールから借りた超小型カメラ(＝リップスティック・カメラ)をリリーの屋敷に設置する。翌日、カメラを回収するためリリーの屋敷を訪ねると、怪しい男が現れて“そんな女はいない”という。事件の臭いを察知したオミーとジュールは、その怪しい男の正体を突き止め、リリーの屋敷に潜入するが……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dream Cruise", "ドリーム・クルーズ", "", "", "http://image.eiga.k-img.com/images/movie/33972/315x210.jpg?1321", 88, "東京の海に潜む怨念に引き寄せられるようにクルーズに出た3人の男女の恐怖を描く、ハリウッド発のジャパニーズ・ホラー。「リング」シリーズの原作者・鈴木光司による短編小説「夢の島クルーズ」を「予言」の鶴田法男監督が映画化。主演は本作でハリウッドデビューを果たした木村佳乃。共演に「THE JUON／呪怨」の石橋凌、「スパイダーマン2」のダニエル・ギリスほか。劇中のほとんどの台詞を英語で演じている。", "2007-05-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八荒流騎隊", "八荒流騎隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "工藤栄一監督・市川右太衛門主演によるスペクタル時代劇。信州・星名郷を代々守り抜いている星名一族を駆逐しようと、代官・岩村隼人正は、星名の三男・小次郎を色仕掛けで丸め込んで館の絵図面を手に入れ、一族を窮地に陥れる。悪代官を平幹二朗が熱演。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE KINDS OF HEAT", "トリプル・エージェント ホシを挙げろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "R・ギンティ主演によるポリス・アクション。ケネディ空港で起きた突然の銃撃戦。特捜刑事エリオット、空港警察官テリー、国際密輸団を追うシェン少佐の３人は高飛びした犯人を追いロンドンへ。事件は意外な組織の全貌を暴くことに……。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("社長千一夜", "社長千一夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "万国博も目前に控えて上昇気流に乗りたい庄司観光会社。そんな時、外人目当ての超近代的ホテルを天草に建築するという南米の日系三世が登場。この男を引き込もうとして庄司社長以下の面々、お色気の方も盛んな珍交渉とあいなった。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("60歳のラブレター", "60歳のラブレター", "", "", "http://image.eiga.k-img.com/images/movie/53942/315x210.jpg?3097", 129, "長年連れ添った夫婦が、互いに言えない感謝の言葉を1枚のはがきに綴る企画「60歳のラブレター」に着想を得て製作された、熟年男女のラブストーリー。夫の定年退職を期に離婚することになった孝平とちひろ、喧嘩は絶えないが友だちのように仲の良い正彦と光江、愛妻を亡くした医師の静夫と彼に好意を抱く麗子……3組の男女が織り成す人生の悲喜こもごもを描く。監督は「真木栗ノ穴」の深川栄洋。中村雅俊、原田美枝子らが主演。", "2009-05-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒロイン！", "ヒロイン！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "バレーボールに情熱を注ぐ、大阪の下町に暮らす主婦たちの奮戦を描いた痛快コメディ。女手一つで酒屋を切り盛りする百合子は幼なじみの天敵、紀子に挑発されてバレーのチームを結成。百合子をはじめとする寄せ集め集団が、紀子率いるハイソなチームに戦いを挑む……。室井滋がヒロイン・百合子をハイテンションで快演。関西演劇界のユニークな顔ぶれが脇を固めている。ビデオタイトルは「ヒロイン！　なにわボンバーズ」。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAST　LIFE　IN　THE　UNIVERSE", "地球で最後のふたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "浅野忠信が神経質な男を快演し、ヴェネチア映画祭コントロコレンテ部門で主演男優賞を受賞。監督は「6ixtynin9」のP・ラッタナルアーン。撮影には、ウォン・カーウァイやチャン・イーモウの代表作などを手掛け、その映像美に定評のあるC・ドイルが参加。国境を越えたコラボレーションによって、切ない恋が独特の浮遊感で綴られる。タイの日本文化交流センターで働くケンジは、図書館でニッドという魅力的な女性と出会う。その夜、川に身を投げようとしたケンジの目前で、ニッドが車に跳ねられてしまう……。", "2003-01-01", "23,76,70,2,54", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霧の中の男", "霧の中の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "石原慎太郎の原作・脚本を蔵原惟繕が監督した日活アクションの一編。捕虜収容所での生活から賭博場荒らし、殺し屋という道を歩んだ男の数奇な運命を、回想形式で綴る。葉山良二がギャング役に初挑戦。情婦に扮する左幸子が日陰の女の哀しさを好演している。影を巧みに使った画面構成が秀逸。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVERY TIME WE SAY GOODBYE", "さよならは言わないで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "第二次大戦下、イスラエルで運命の出会いをしたアメリカ人空軍大尉とスペイン系ユダヤ人の19歳の娘。国境を越え、二人は激しい恋に落ちるが……。甘く切ない純愛を描いたラブ・ロマンス。それまでコミカルな演技を得意としていたT・ハンクスのシリアスな演技に注目。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AMOUR A｀ VINGT ANS", "二十歳の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "フランス、イタリア、日本、西ドイツ、ポーランド５ヵ国の当時の新鋭監督が競作したオムニバス映画。パリ、ローマ、東京、ミュンヘン、ワルシャワの５都市を舞台に、若者たちの青春が描かれている。各編の間をG・ドルリューによる主題歌が流れ、アンリ・カルチエ・ブレッソンのスティール写真が挿入されている。トリュフォー編のみ「柔らかい肌」に併録されてビデオ発売されていた。DVDは「フランソワ・トリュフォー 14の恋の物語(1)」に収録。", "1962-01-01", "2,76,4,7,42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRANCER", "プランサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "足に傷を負って倒れているトナカイを見つけた少女は、クリスマスまでに間に合うようにと、そっと自分の家の馬小屋に隠して看病する。ところが元気になったトナカイは馬小屋から抜け出し、家の中をメチャクチャに。怒った父親はトナカイを売ってしまう……。幼い少女とトナカイの姿を通して、クリスマス本来の意味を考えさせてくれるメルヘン・ファンタジー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リトルチャンピオン", "リトルチャンピオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ボストン・マラソンを制した日本人女性ランナー、ゴーマン美智子の自伝を映画化した作品。「将軍」の好演によってアメリカで注目された女優・島田陽子を起用し、アメリカの女性が監督、オール・アメリカ・ロケを敢行して話題を呼んだ。製作総指揮を梶原一騎が務めている。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MATILDA", "マチルダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "R・ダールの原作を、D・デヴィートが映画化。マチルダは生まれつき本を読んだり計算する能力が非常に高かったが、両親はマチルダには無関心だった。それどころか、マチルダの知識欲を嫌い、学校にも行かせてくれなかった。ある日、マチルダが本を読んでいると、突然パパが怒りだし、本をとり上げ破ってしまう。頭にきたマチルダがブラウン管をにらむと、ブラウン管が爆発する。なんとマチルダには超能力があったのだ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　BELLS　OF　ST．　MARY’S", "聖メリーの鐘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "アカデミー作品賞ほかを受賞した名作「我が道を往く」の続編。本作もオスカーで作品賞を含む７部門にノミネートされた、ハートウォームなヒューマン・ドラマ。前作に続く監督・主演コンビに加え、当時大スターだったバーグマンがヒロインを演じている。再び経営難の教会にやって来た神父が学園の尼僧長と反発しながらも、教会の立て直しに挑んでいく。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海軍", "海軍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "海軍報道部の企画による太平洋戦争２周年記念映画。「五人の斥侯兵」「土と兵隊」で戦争映画の手腕を認められた田坂具隆が起用された。鹿児島の商家に生まれた愛国少年が兵学校を経て海軍に入り、厳しい実地訓練のあとに特殊潜航艇に乗り込んで真珠湾へ向かうまでの物語。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fly, Daddy, Fly", "フライ・ダディ", "", "", "http://image.eiga.k-img.com/images/movie/33942/315x210.jpg?1298", 117, "日本でも岡田准一＆堤真一で映画化された金城一紀のベストセラー小説「フライ、ダディ、フライ」を、「王の男」のイ・ジュンギを主演に迎え韓国で映画化。娘を傷つけた高校生ボクシング・チャンピオンに復讐するため、刃物を手に高校へと向かった中年サラリーマンのガピル。ところが彼は、その高校に通うケンカの達人スンソクにあっけなく取り押さえられてしまう。愛する娘のために強くなりたいガピルは、スンソクに弟子入りすることになり……。", "2007-04-21", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROUSTABOUT", "青春カーニバル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "“ロックンロールの神様”、プレスリー主演の青春映画。空手とオートバイが好きな歌手チャーリーが、旅回りのカーニバル一座に加わり、そこで大スターとなるまでを描いている。華やかなショーと、プレスリーの熱唱が聞きものの娯楽映画。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸っ子祭", "江戸っ子祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "“永遠の二枚目スター”長谷川一夫が一心太助を演じた痛快時代劇。徳川三代将軍の座をめぐって長男・竹千代派と次男・国松派が対立する江戸城内、退屈気味の竹千代は夜ごと辻斬りをし始め、大久保彦左衛門は激怒。太助に竹千代を小僧として預ける。太助は竹千代にスパルタ教育をほどこすのだが……。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DE ILLUSIONIST", "イリュージョニスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "オランダの奇才J・ステリングが監督、異色俳優F・D・ヨングが脚本・主演。夢を追う兄と精神病院に入れられる弟(ヨングが一人ニ役)。彼らをめぐる周囲の人々の人間模様を、現実と幻想を織り交ぜて描く。いっさいの台詞を排したアコースティックな音楽効果と、シュールな映像が陶酔を誘うファンタスティック・ムービー。", "1983-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冬冬的假期", "冬冬（トントン）の夏休み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "台北の小学校を卒業したトントンは、母が入院したため、幼い妹と二人で夏休みを田舎の祖父の家で過ごすことに。近所の子供たちともすぐに仲良くなり、トントンは田畑を駆け回って楽しいひと夏を過ごす。緑豊かな田舎の風景が、誰もが心に持つほのぼのとした郷愁を誘い、見るものの胸を熱くする秀作。父役は「恐怖分子」のエドワード・ヤン監督。", "1984-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOBSTERS", "モブスターズ 青春の群像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "禁酒法時代のニューヨーク。大物ボス二人が牛耳るこの街で、徐々に頭角を現わしてきた４人の若者がいた。彼らの名はルチアーノ、ランスキー、シーゲル、コステロ。後にアメリカ全土に犯罪シンジケートを築き上げる男たちだった……。実在の４人の大物ギャングの若き日を描いた青春アクション。若手４人の脇をA・クイン、M・ガンボンといった強面がガッチリと固めている。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILLARD", "ウイラード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ベストセラーを記録したS・ギルバードの『ねずみ男の手帖』の映画化で、アメリカでは大ヒットした。孤独な若者ウイラードが２匹のネズミ、ソクラテスとベンと仲良しになり、やがてネズミたちを使って復讐を企てるが……。500匹のネズミの演技(!?)が圧巻。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("田神有楽〈でんしんゆうがく〉", "田神有楽〈でんしんゆうがく〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "青森県上北郡にある六ヶ所村。ここに高レベル放射性廃棄物がフランスから初めて搬入された1995年４月から、３年半にわたって村の様子をカメラに収めたドキュメンタリー。原子力船“むつ”の解体や３度に及ぶ放射性廃棄物の搬入、使用済み核燃料の受け入れ是非の討論会、そして米作りを地道に続ける一人の老人の姿などを淡々とつづっていく。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市（2003）", "座頭市（2003）", "", "", "http://image.eiga.k-img.com/images/movie/1222/315x210.jpg?4120", 115, "北野武監督が脚本も書き、自ら金髪にジーンズ姿で演じる「座頭市」。居合い斬りの名人座頭市は、とある宿場で幼い頃に両親を盗賊に殺され、その復讐を誓う芸人姉妹と、故あって浪人する剣の名手、服部源之助に出会う。宿場を仕切るヤクザの銀蔵が、姉妹の親の敵らしいと判明するが、銀蔵が源之助を用心棒に雇ったことから、市と源之助は闘うはめになる。源之助役は浅野忠信。飲み屋店主役で柄本明、農婦役で大楠道代らが出演。", "2003-09-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("式日", "式日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "『新世紀エヴァンゲリオン』で独自のアニメーション世界を築いた庵野秀明監督の「ラブ＆ポップ」に次ぐ実写第２作。庵野の故郷である山口県宇部市で撮影された、自伝的な作品だ。撮るべきテーマを失った“カントク”は故郷に戻る。そこで彼は、孤独を抱えて“誕生日の前日”を生き続ける“彼女”に出会う。“彼女”の不可解な行動をビデオカメラに収めるうちに、“カントク”はその孤独感を共有し、次第に二人は惹かれ合い……。赤を基調にした“彼女”の住居や、シネスコの画面いっぱいに広がる宇部の光景が圧倒的な存在感を放つ。ナレーションを多用した語り口には詩情が溢れ、人物たちの孤独と困惑をコトバとカタチに変えていく。庵野の分身である“カントク”役には、映画監督である岩井俊二が俳優として挑戦。原作も手掛けた“彼女”役の藤谷文子は、ナイーブさと狂気を併せ持つ不思議な雰囲気を見せている。ちなみにタイトルの「式日」とは、古い言葉で儀式を執り行う日のこと。2000年東京国際映画祭で優秀芸術貢献賞を受賞した。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版 鉄腕アトム 宇宙の勇者", "劇場版 鉄腕アトム 宇宙の勇者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "1960年代にお茶の間を席巻した日本初の30分連続TVアニメの、オンエア分の中から3話をセレクトし劇場映画用として再編集したアニメ。ジャパニメーションの夜明けを告げるロボットSFアニメの名作。宇宙人にお茶の水博士が拉致される「地球防衛隊」は、全編カラーで再製作。地球に迫る星をアトムが防ぐ「地球最後の日」の一部がカラーに。もう1話は偽アトムとの対決。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アレクサンダー戦記", "アレクサンダー戦記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ビデオ、DVD、CD、ノベルなどの多メディア展開をした“アレクサンダー戦記”プロジェクトの集大成として作られたアニメ。伝説の英雄、アレクサンダー大王の激動の生涯をモチーフに大胆なアレンジを加え、ヒーロー・ファンタジーに仕上げている。父王が暗殺され、マケドニアの王となったアレクサンダーは次々とギリシャ諸国を陥落させていく。謎の魔術集団ピュタゴラス教団の陰謀、ペルシアの姫ロクサネとの出会い、宿敵のペルシア帝国ダリウスIII世との対決。アレクサンダーに数々の試練が襲いかかる。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAFAR　E　GANDEHAR", "カンダハール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "イランのM・マフマルバフ監督が隣国アフガニスタンを旅する女性の運命を見すえたロード・ムービー。日本では2001年９月11日のアメリカ同時多発テロ事件の後、アメリカ軍によるアフガニスタンへの報復爆撃が行われているさなかに公開され、大きな話題を呼んだ。カナダに亡命したアフガニスタン人ジャーナリスト、ナファスが、祖国のカンダハールに留まる妹から自殺を予告する手紙を受け取り、現地に向かうことを決意。そんなヒロインの目を通して、タリバン政権下の女性差別、教育、難民などの諸問題が浮き彫りにされる。パラシュート付きの義足が空から降ってくるシーン、色とりどりの民族衣装ブルカをまとった人々が荒野を歩くシーンなど、マフマルバフ監督らしい鮮烈なイメージも随所に見られる一編。", "2001-01-01", "15,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRIPPED TO KILL", "ストリッパー殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "アメリカの風俗営業でも根強い人気を持つストリップ・カフェで殺人事件が起きた。女性捜査官のコディはダンサーになりすまして捜査を開始するが彼女の身にも魔手が……。R・コーマンが製作総指揮にあたったエロティック・サスペンス。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母山彦", "母山彦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "三益愛子お得意の“母もの”映画。３人の子供を女手一つで育てた母・おはま。だが末娘の家出を期に不幸が家族を見舞う。小学校の教師となった長女は遠足で生徒を溺死させ、ボクサーとして将来を嘱望された長男は大事な試合に負けたあげく酒に酔って警察に連行され……。母の苦労を描くこのシリーズは人気を博した。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("近未来蟹工船／レプリカント・ジョー", "近未来蟹工船／レプリカント・ジョー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "インディーズ映画界で“おバカ映画の女王”なる異名を持つ松梨智子監督作。肉体改造でサイボーグと化した男を主人公に、荒唐無稽なストーリーが爆走する。自動車工場で働くジョーは、会社の賃下げに怒り心頭。会社に対抗しようと労働組合を結成したジョーだが社長の巧みな策略にはまり、愛妻と仕事を一度に失ってしまう。復讐に燃えるジョーはサイボーグに変身して……。あの水野晴郎もカメオ出演。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE 有頂天ホテル", "THE 有頂天ホテル", "", "", "http://image.eiga.k-img.com/images/movie/1722/315x210.jpg?1495", 130, "「ラヂオの時間」「みんなのいえ」の三谷幸喜が、32年に製作されたグレタ・ガルボ、ジョーン・クロフォード主演の「グランドホテル」で確立された「グランド・ホテル形式」そのままに、オールスターで作り上げた群像劇。新年のカウントダウンまであと2時間の高級ホテル。副支配人・新堂の使命はこのイベントを無事に済ますことだが、次々と困った事態が起こる。共演は篠原涼子、オダギリジョー、寺島進、唐沢寿明、西田敏行ら。", "2006-01-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若きロッテちゃんの悩み", "若きロッテちゃんの悩み", "", "", "http://image.eiga.k-img.com/images/movie/56402/315x210.jpg?4682", 80, "青春と性をテーマに監督たちが独自の個性で自由に製作する「青春H2（セカンド）」シリーズの2作目。ピンクサロンで働くかよは、店の常連客・宏に本番を迫られて不機嫌な状態で帰宅すると、彼氏の洋介が若い女性を部屋に連れ込んでいた。怒り心頭で家を飛び出したかよは、車に乗った宏に偶然出会い、勢いで宏の車に乗る。そこへなぜか洋介も加わり、3人で海に行くことになるが……。監督は「ゴーストキス」（青春Hシリーズ）のいまおかしんじ。", "2011-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CET OBSCUR OBJET DU DESIR", "欲望のあいまいな対象", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "正体不明のテロ事件が頻発するセビリアの町から、パリ行きの列車に乗り込んだのは、初老のブルジョワ紳士マチュー。追いすがる女にバケツの水を掛けた彼は、驚く乗客たちに奇妙な愛の体験談を語り始める……。姿を現すたびに表情を変える若く美しい小間使コンチータを2人1役という史上初の試みで描いた巨匠ブニュエルの遺作。", "1984-11-03", "2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEAN MACHINE", "ミーン・マシーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "G・リッチーが製作総指揮を務める痛快な人間ドラマ。八百長試合で引退を余儀なくされた元プロ・サッカーのスター選手が、刑務所で囚人にサッカーのコーチをすることによって、自分を取り戻していく姿をコミカルに描く。主演は、元プロ・サッカー選手の個性派V・ジョーンズ。こわもてな中にもコミカルな味を感じさせてハマリ役だ。本作で監督デビューを飾ったB・スコルニックの、スタイリッシュでキレのある映像も見もの。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SOUND AND THE FURY", "悶え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "アメリカ南部の旧家を舞台とする人間葛藤のドラマ。郊外にある荒れ果てたコンプソン家は、後妻の連れ子ジェイソンが跡を継ぎ、未婚の母となって家出した先妻の長女の子クエンティンらとともに生活していた。ある日、彼女が学校をさぼり、派手な身なりをしていることをジェイソンが叱る。彼女は何かと自分を束縛するジェイソンを憎み始めていた。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fired Up!", "俺たちチアリーダー！", "", "", "http://ecx.images-amazon.com/images/I/515BLTXmT-L._SL210_.jpg", 91, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風前の灯", "風前の灯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "木下が「喜びも悲しみも幾歳月」と「楢山節考」の間に撮った本格喜劇。小金をめぐって、ケチな家庭と強盗があの手この手の駆け引きをするところから生まれるユーモアが大いに笑える。木下はもっぱら叙情的な作風で知られるが、このような辛口の喜劇でも、その才気は際立っている。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIRACOLO A MILANO", "ミラノの奇蹟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ザヴァッティーニ原作『善人トト』の映画化。キャベツ畑でロロッタ婆さんに拾われた捨て子のトトは、今や原っぱにある“掘っ建て小屋”部落のリーダー。ある日、土地の所有者が立ち退きを迫って実力行使……。現実と夢が混じり合った不思議な味わいの寓話ドラマ。", "1951-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベロニカは死ぬことにした", "ベロニカは死ぬことにした", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ブラジル人作家パウロ・コエーリョの同名小説に惚れ込んだ人気脚本家の筒井ともみが、舞台を日本に置き換えてシナリオを執筆。さらに自らプロデュースを買って出て、映像化にこぎつけた渾身のヒューマン・ドラマだ。退屈な人生にうんざりして、自殺を図ったトワ。気がつくと、風変わりなサナトリウムに収容されていた。院長から残り７日間の命だと宣告されたトワは、自らの人生を再発見していく。生死の境目で一筋の活力をつかみとっていくヒロインの姿と、心に傷を持つサナトリウムの患者たちの人生模様を絡めて綴る。", "2006-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FRONT", "ウディ・アレンのザ・フロント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1953年の悪名高き“赤狩り”の嵐が吹き荒れる芸能界を舞台に、反共ヒステリーの理不尽な弾圧に振りまわされる人々の姿を軽妙なユーモアを交えて描いた社会派コメディ。W・アレンが俳優に徹している珍しい作品でもある。現実に“赤狩り”に巻き込まれたスタッフが手掛けた、リアリティーあふれるシニカルな映画。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAS AUSSCHWEIFENDE LEBENDES MARQUIS DE SADE", "マルキ・ド・サド 愛欲と官能の果て", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "自分の小説がもとで警察に追われているサド公爵は、途中逃げ込んだ屋敷で彼に倒錯的な快楽を教え込んだ男と出会った。男は公爵を朽ち果てたあばら屋へと誘う。そこで彼が体験したこととは……。サドの思想と遍歴を強烈な映像で描き出す異色作品。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅の拳銃", "紅の拳銃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "赤木圭一郎扮する青年が、殺し屋製造業の男によって、プロの殺し屋に仕立て上げられ暗黒街へ入り込んでいく。プロの殺し屋の世界の非情さとともに、赤木圭一郎ならではの淡いロマンスが並行して描かれている。トニーが殺し屋の修行をする前半の張りつめたムードも悪くないが、ほのかに慕情を寄せる少女に自分の正体を明かせぬもどかしさを、トニーの悲し気なまなざしで表現したラストシーンは忘れがたい。裕次郎や旭とは違う独特のムードで将来を期待された赤木圭一郎は、この作品が公開された年に21歳の若さで事故死した。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eat, Pray, Love", "食べて、祈って、恋をして", "", "", "http://image.eiga.k-img.com/images/movie/55272/315x210.jpg?4090", 133, "エリザベス・ギルバートが自らの体験をつづった世界的ベストセラー「食べて、祈って、恋をして　女が直面するあらゆること探究の書」を、ジュリア・ロバーツ主演で映画化。ニューヨークで活躍するジャーナリストのエリザベスは、離婚や失恋を経験し、35歳で人生をリセットするために1年間の旅に出る。イタリアで“食”に魅せられ、インドで瞑想にふけり、最後に訪れたインドネシア・バリ島では運命的な恋に落ちるが……。共演にハビエル・バルデム、ジェームズ・フランコ、リチャード・ジェンキンス。監督はTVシリーズ「NIP/TUCK マイアミ整形外科医」「Glee」のライアン・マーフィ。", "2010-09-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一生、遊んで暮らしたい", "一生、遊んで暮らしたい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「岸和田少年愚連隊」の原作者、中場利一の人気エッセイの映画化で、当時の人気コメディアン二人組、猿岩石が初主演した青春ドラマ。大阪・岸和田に住む二人の不良、リイチと小鉄は“一生、遊んで暮らしたる”を座右の銘に無鉄砲な生活を送っていた。やがてリイチは男運の悪い道子と、小鉄はシンナー中毒のみどりと同棲を始める。これを機に二人は真面目に働こうと決心するが……。彼らの涙と笑いと怒り、そして成長が様々なエピソードの中で語られていく。監督はTV畑出身の金澤克次。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONVOY", "コンボイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "陽炎の立ち昇るハイウェイを黒い大型トレーラーが突っ走ってくる……。アメリカ版“トラック野郎”を描いたS・ペキンパー作品だが、トレーラーが主役になってしまい、肝心のドラマの方はやや低調。紅一点、アリ・マッグローのショートカット・ヘアーが好印象。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　VOGLIA　MATTA", "狂ったバカンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "女性には目がない精力的な中年男アントニオが、浜辺のバンガローで青春を謳歌する若者グループと知り合う。そのグループの中の美しい女性フランチェスカに興味を持ち一緒に過ごすうちに、自分と彼らの世代ギャップに打ちのめされていく……。主人公を残酷なまでにほんろうする妙にセクシーなロリータ娘フランチェスカ役にC・スパーク。夏の終わりを捉えたシネマスコープの白黒映像、E・モリコーネの音楽が、アンニュイな退廃ムードを醸し出している。", "1962-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A SHOT IN THE DARK", "暗闇でドッキリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "迷警部ジャック・クルーゾーが活躍する“ピンク・パンサー”シリーズの第２作。パリの大邸宅やヌーディスト・キャンプで起こる連続殺人事件を追うクルーゾー警部の捜査上に浮かんだ容疑者は絶世の美人。クルーゾーは彼女は犯人ではないと信じ込むが……。P・セラーズが最も脂がのっていた頃の一編。DVDは「ピンク・パンサー フィルム・コレクション」に収録。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FRESHMAN", "ロイドの人気者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "“ロイド眼鏡”にその名を残す、キートン、チャップリンと並ぶ無声期の３大喜劇王の一人、H・ロイドの代表作。新入大学生の田舎青年ロイドが、ハチャメチャな学園生活を送りながらフットボール部の花形選手になるまでを描く。本作は無声映画だが1963年にロイド監修のサウンド版も日本公開された。", "1925-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハサミ男", "ハサミ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ミステリーとして高い評価を受けた殊能将之の同名小説を、豊川悦司と麻生久美子の主演で映画化。“ハサミ男”と呼ばれる連続猟奇殺人鬼に間違えられた男女の奔走をミステリアスに描く。故・伊丹十三監督作品で知られる本多俊之による楽曲が、サイコな世界観を彩る。たまたま死体を発見した知夏と安永の二人は、ジョギング中の男に巷を騒がしている連続猟奇殺人事件の真犯人だと誤解されてしまい……。", "2005-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真幸くあらば", "真幸くあらば", "", "", "http://image.eiga.k-img.com/images/movie/54812/315x210.jpg?4367", 91, "遊ぶ金欲しさに空き巣に入った家で、居合わせたカップルを衝動的に殺害してしまった淳は、一審で死刑を宣告される。淳は、弁護士が提出した控訴を自ら取り下げ、死刑執行の日を待つことに決める。そんなある日、淳の殺害した男性の婚約者だったクリスチャンの女性・薫が面会に訪れ、2人は互いに惹かれていく。主演は「殯の森」の尾野真千子、「最後の早慶戦」の久保田将至。監督は詩人・作詞家として知られる御徒町凧。", "2010-01-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NAGISA／なぎさ", "NAGISA／なぎさ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "村上もとかの人気コミックを、ロマンポルノの名匠、小沼勝が映画化したさわやかな青春映画。1960年代、レコードプレーヤーがほしくてたまらない12歳の少女、なぎさ。江ノ島の海の家でアルバイトに励む彼女は、東京から帰省した上流階級の友人と再会したり、浜辺で出会ったひ弱な男の子に泳ぎ方を教えたりと、ちょっぴり特別な夏休みを過ごしていく。ベルリン映画祭キンダーフィルムフェストグランプリ受賞。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("学校の怪談", "学校の怪談", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "小中学生の大ベストセラー『学校の怪談』に題材をとった冒険ファンタジー。明日から夏休みという終業式の日、旧校舎に閉じ込められた先生と子供たちが、妖怪や怪奇現象に遭遇。いまや失われてしまった冒険やスリルを通して、恐怖に打ち勝つ心、勇気や夢の大切さを学んでいく。子供たちにお馴染みの怪談話やキャラクターが、最新のSFXを駆使した斬新な映像によってスクリーンに躍動する。DVDは「学校の怪談　DVD-BOX」に収録。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZERO　EFFECT", "ゼロ・エフェクト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "B・プルマンが研ぎ澄まされた客観性と観察力をもつ探偵ゼロを演じるハードボイルド・サスペンス。ゼロとその相棒である饒舌な弁護士アローは、ある富豪から消えた鍵束の捜索を依頼される。そんな二人の前で起きる殺人事件。だが、重要参考人の女に特別な感情を抱いていたゼロは心を惑わされていくのだった……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last Waltz", "ラスト・ワルツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "1976年、サンフランシスコで行われた人気ロック・グループ“ザ・バンド”の解散コンサートの模様を収録。だがこれは単なる記録映画ではない。監督のスコセッシ自身がザ・バンドのメンバーの一人一人にインタビュー。彼らの活動の歴史、それに懸けた青春の思い出、かかわり合った仲間のミュージシャンについて語る言葉が印象的に捉えられる。豪華なシャンデリアを飾った『椿姫』の舞台装置の上で繰り広げられるコンサートは、一種荘厳な感じさえする。ボブ・ディラン、ニール・ヤング、ニール・ダイアモンド、エリック・クラプトン、Dr.ジョンらがゲスト出演。ラストの全員で歌う『アイ・シャル・ビー・リリースト』が感慨深い。ビデオ発売時タイトルは「ザ・バンド／ラスト・ワルツ」。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("へそくり社長", "へそくり社長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「三等重役」「続・三等重役」のヒットに気をよくした東宝が製作したサラリーマン喜劇。その後“社長”シリーズとして30本を越える長寿を保った。明和商事の新社長・田代善之助は三等社長とかげ口をたたかれている。先代社長の令嬢と結婚したがゆえの今日の地位だからである。今日も小唄の師匠のところへ入りびたり。この女師匠、一見社長に気のあるそぶりだが、実はカネ目当て。シリーズ恒例のアトラクションになる森繁のドジョウすくいも、すでにこの第１作から登場している。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜明けの街で", "夜明けの街で", "", "", "http://image.eiga.k-img.com/images/movie/55312/315x210.jpg?1305", 129, "累計発行部数120万部を突破する東野圭吾の人気小説を、岸谷五朗、深田恭子の主演で映画化。「沈まぬ太陽」の若松節朗監督がメガホンをとる。バッティングセンターでの偶然の出会いをきっかけに、不倫関係におぼれていく上司・渡部和也と部下・仲西秋葉。やがて渡部は、秋葉が15年前に父親の愛人が殺害された事件で容疑者として疑われており、まもなく時効を目前に控えていることを知るが……。", "2011-10-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆心（仮題）", "爆心（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新警察故事", "香港国際警察／　NEW POLICE STORY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "ハリウッドから香港に復帰を果たした、ジャッキー・チェン主演のポリス・アクション。ジャッキー扮する敏腕警部の特捜チームが、ゲーム好きの若い武装集団に惨敗を喫するという意表を突いた導入部から物語はスタート。自信を喪失した主人公が、因縁の敵との死闘に身を投じていく様を描き出す。ジャッキーがいつものコミカルな魅力は抑えて、絶望から這い上がる主人公を熱演。高層ビルの壁などを使った超絶アクションも満載だ。また、香港映画界を担う若手スターが多数出演しているのも見逃せない。銀行を襲った若者グループのアジトに乗り込んだチャン警部が、敵の罠によって部下を皆殺しにされてしまう。1年後、覇気を失った彼は、再び動き出した一味に立ち向かうことに。", "2005-03-05", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　MIDSUMMER　NIGHT’S　DREAM", "真夏の夜の夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "シェイクスピアの喜劇をドイツの舞台演出家M・ラインハルトとW・ディターレが共同で映画化。当時の特撮技術を散りばめてファンタジー世界を映し出す。アテネの都に近い魔の森に、駆け落ちしたカップルのハーミヤとライサンダー、ハーミヤを追うデミトリヤスとヘレナといった男女４人、そして大公の結婚式の余興に芝居をやる職人たちが稽古のために入り込む。夜の森で勝手なふるまいをする人間たちに腹を立てた悪戯好きの妖精パックは、媚薬を使って彼らに様々な騒動を起こさせるのだった……。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子宮の記憶　ここにあなたがいる", "子宮の記憶　ここにあなたがいる", "", "", "http://image.eiga.k-img.com/images/movie/33812/315x210.jpg?4789", 115, "直木賞作家・藤田宜永の同名小説を「ホワイトアウト」の若松節朗監督が映画化。母親の愛情に飢えた17歳の少年と、新生児の頃の彼を誘拐した女性との心の交流を繊細なタッチで描く。裕福な家庭で育った真人は、母親の愛情を感じられず孤独を感じていた。彼は自分が生後まもなく誘拐されていたことを知り、誘拐犯の女性・愛子が暮らす沖縄を訪れる。ヒロインの愛子に扮するのは「フラガール」の松雪泰子。共演は「夜のピクニック」の柄本佑。", "2007-01-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tristan & Isolde", "トリスタンとイゾルデ", "", "", "http://image.eiga.k-img.com/images/movie/33702/315x210.jpg?1880", 126, "シェイクスピアの戯曲「ロミオとジュリエット」の原点になった恋愛悲劇を映画化。コーンウォールの領主マークに育てられた騎士トリスタンは、戦闘で重傷を負い、敵国アイルランドの海岸に流れ着く。瀕死の彼を救ったのは、アイルランド王の娘イゾルデだった。2人は恋に落ちるが、やがてマークとイゾルデが政略結婚することになり……。主演は「スパイダーマン」のジェームズ・フランコと「アンダーワールド」のソフィア・マイルズ。", "2006-10-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コイ☆セント", "コイ☆セント", "", "", "http://ecx.images-amazon.com/images/I/61MmLDGcJWL._SL210_.jpg", 25, "西暦2710年の奈良を舞台に繰り広げられるSFラブコメディ。遷都2000年祭を迎えた奈良に修学旅行にやってきた高校2年生のシンイチは、白いシカにカバンを奪われてしまう。シカを追いかけるなかで、何者かに追われているナゾの美少女トトを助けたシンイチは、トトと一日デートをすることに。しかし、デートの最中にトトを追っていた連中に見つかってしまい……。「スチームボーイ」「FREEDOM」「いばらの王 King of Thorn」などを手がけたサンライズの荻窪スタジオが制作を担当したオリジナルアニメーション。バンダイビジュアルの新作アニメを劇場で上映する「ANIME FES.“VS”」にて上映。", "2010-11-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うなぎ", "うなぎ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "1997年度カンヌ映画祭で見事グランプリに当たるパルム・ドールを獲得。「黒い雨」以来、８年の空白を経て、ベテラン今村昌平が発表した人間ドラマ。吉村昭の小説『闇にひらめく』を原作に、飼っているウナギにしか心を開かない男の心情の移ろいを見極めようとしている。浮気した妻を殺害し、８年の服役の後、仮出所した男・山下拓郎。理髪店を開き、自戒の日々を送る彼は周囲の人々に心を開くことはなかった。そんなある日、山下は自殺未遂の女性・桂子と出会い、彼女の希望から共同生活を始めることになる。彼女の優しい人柄にふれ、打ち解けていく山下だったが、そんな彼の前にかつての囚人仲間が、さらに桂子の夫までもが現れる……。静かな語り口ながら、鋭い人間観察の目が随所に光る佳作。とぼけた笑いも盛り込まれ、今村の初期作品を思わせる仕上がりとなった。役者陣もそろって好演を見せるが、特に桂子の母親役の市原悦子が怪演を披露して印象的。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO WAY OUT", "追いつめられて", "", "", "http://ecx.images-amazon.com/images/I/51y61%2BytjQL._SL210_.jpg", 114, "米国防省（ペンタゴン）を舞台に展開するスリルに満ちた、ファッショナブルで知的な政治サスペンス。CIAの連絡員としてペンタゴン入りしたファレルには、美しい恋人スーザンがいた。だが、彼女は国防長官の愛人でもあった。ある夜、スーザンが殺害され、嘆き悲しむファレルに犯人調査の依頼が回ってくる。しかし、犯人と目される“週末をスーザンと過ごした男”とは、ファレル自身であったのだ……。ほとんどスタントなしでこなしたという、K・コスナーのアクション・シーンが迫力。ラストの思いがけないドンデン返しも見どころの一つ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の一生", "女の一生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "文豪モーパッサンの傑作小説を基に、時代設定を日本の戦後に置き換えて映像化された女性映画。山田洋次、森崎東ほか後の大物監督たちが参加している点にも注目。また、田村正和の母親役が岩下志麻という、今では信じられない豪華共演で贈る。伸子は宗一と結婚したが、夫は浮気症で女中を孕ませてしまう。しかし、伸子も妊娠し男の子を産み、子供を生き甲斐に生きていこうとする。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACKROCK", "ブラックロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "実話をもとに、無軌道な青春を描いた衝撃のドラマ。サーフィンに明け暮れる若者たちのもとに、伝説のサーファーが戻ってきた。その歓迎パーティーはアルコールとドラッグにまみれ、狂気的な方向に暴走。翌朝、集団レイプされた少女の死体が海岸に残されていた。その夜、いったい何が起こったのか？　事件の裏には、誰も語ろうとしない意外な事実が秘められていた……。", "1996-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIESTAS　＆　OLAS", "シエスタ&オラス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "メキシコを舞台に、“ビッグ・ウェーブ”を求めて旅するサーファーたちを追ったドキュメンタリー。サーフィンをこよなく愛する弁護士のトムとその友人ジョシュは、完璧な波を求めて旅に出る。途中地元のサーファーと交流を深め、ヒッチハイカーと知り合い、世界一のビーチ・ブレイク、プエルト・エスコンディートを目指す。気ままな男たちの開放感あふれるロード・ムービーとしても楽しめる。ダイナミックな波乗りテクニックも見ごたえあり。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DEAD POOL", "ダーティハリー5", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "強引かつ手荒な捜査方法ゆえに“ダーティハリー”と呼ばれる一匹狼ハリー・キャラハン刑事のシリーズ第５作。今回は彼自身が、凶悪犯の標的にされる。物語の発端は、有名な歌手や映画評論家の連続殺人事件。その背後に、とある低予算映画のシナリオにある“死亡予想（デッド・プール）”ゲームが絡んでいると読んだ彼は、映画監督をマーク。しかしそのリストの最後にはハリーの名があった……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪盗ジゴマ 音楽篇", "怪盗ジゴマ 音楽篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "音楽とセリフを先録りして作られた、寺山修司原作の短編ミュージカル・アニメーション。女性歌手から“心の歌”を盗んだ怪盗ジゴマを、探偵が追跡するという独特のファンタジーが、和田誠のほのぼのとしたイラストのタッチで綴られていく。由紀さおりら個性的なキャストによる、歌と声も聴きもの。「快盗ルビイ」の併映作品として公開された。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In einem Jahr mit 13 Monden", "13回の新月のある年に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "新月にまつわる逸話をヒントに、人間の脆さを描くファスビンダー監督作品。男を愛するために女へと性転換した主人公が、自らの暗い過去について語るという導入部から、性転換者の悲しい運命が綴られる。精肉工場の工程を長々と見せる衝撃的なシーンや、ロキシー・ミュージックのデカダンな哀愁ナンバー『ヨーロッパ哀歌』の使用など、監督独自の演出スタイルを垣間見ることができる。", "1978-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROCKERS 完全版", "ROCKERS 完全版", "", "", "http://image.eiga.k-img.com/images/movie/53972/315x210.jpg?4608", 72, "1978年、東京のインディーズ・ロックシーンに衝撃を与えた伝説のムーブメント“東京ロッカーズ”の軌跡を追った79年製作のドキュメンタリー。FRICTION、LIZARDなどムーブメントの中核を担ったバンドのライブシーンやメンバーへのインタビューをはじめ、彼らと共に当時のシーンを盛り上げたバンドの数々といったレアな映像が満載。さらにビデオ版ではカットされていた英バンド・ストラングラーズの来日時映像などを加え、完全版として上映。", "2008-10-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("任侠東海道", "任侠東海道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "新春恒例の東映オールスター顔見せ映画として作られた。清水港の次郎長親分は一家28人衆を連れて、東海道の秋葉祭に乗り込んだ。一家は子分の伯父を殺した３人組と死闘を繰り広げる。次郎長の片岡千恵蔵と、後半で大活躍する吉良の仁吉役の市川右太衛門の熱演が光る。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Redline", "レッドライン", "", "", "http://image.eiga.k-img.com/images/movie/53732/315x210.jpg?2816", 95, "天才的な運転能力を持つ美女ナターシャは、レーサーの父を事故で亡くして以来レースの世界から遠ざかっていた。現在は歌手を目指す彼女は、アメリカ西海岸で賭博ストリートレースに興じるインフェイマスに誘われ、歌手として出演することを条件にラスベガスのレース会場へと向かう。しかしインフェイマスの計略によってレースに出場することになってしまい……。「エンドゲーム／大統領最期の日」のアンディ・チェン監督によるカーアクション。", "2008-07-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かにゴールキーパー", "かにゴールキーパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「いかレスラー」「コアラ課長」といった奇想天外な作品で話題を集め続ける河崎実監督が、今度はカニを主人公に描いたコメディ映画。漁師の息子・真一は、浜辺に打ちあげられていた巨大なカニを助け、家に連れて帰る。次第に言葉を覚えはじめたカニは、真一の父が自分を売り飛ばそうとしていることに気づき、単身都会へと向かう。やがて、プロサッカーチームの監督・藤村と出会ったカニは、ゴールキーパーとしてチームに加入することになり……。", "2006-05-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火垂〈ほたる〉", "火垂〈ほたる〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 164, "「萌の朱雀」の河瀬直美監督の長編第２作。プロデューサーは「EUREKA〈ユリイカ〉」「五条霊戦記//GOJOE」の仙頭武則。生きる意欲を失っていた孤独なストリッパーのあやこは、唯一の家族である祖父を亡くしたばかりの大司と知り合う。やがて二人は強く惹かれ合い、付き合い始めるが……。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HERCULES", "ヘラクレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ギリシャ神話の英雄ヘラクレスの青年期を描くディズニーの長編アニメ第35作。神々の神殿オリンポス山の支配者ゼウスを父に持つヘラクレスは、死者の国の神ハデスの陰謀で、半神半人の体になってしまい、地上に置き去りにされる。心優しき青年へと成長する彼だが、生まれつきの怪力のため、人々に溶け込めない。ある日、己の出生の秘密を知った彼は、神の国に戻るため、ふさわしい真の英雄になろうと決意。伝説の男ピロクテテスの教えを受けながら、彼は試練に立ち向かう。ヘラクレスの愛と冒険の物語が、数々のディスニー作品を手掛けてきたA・メンケンによるゴスペル調の音楽をバックに、ダイナミックに展開。ハデス役のJ・ウッズやピロクテテス役のD・デヴィート、ナレーションのC・ヘストンら、豪華俳優の声の競演もうれしい。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AU PETIT MARGUERY", "パリのレストラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "パリの街角にあるレストラン“プチ・マルグリィ”は今夜、30年続いた歴史に終止符を打とうとしていた。店には、店とともに成長した一人息子や縁のある人々が最後の夜を囲むために集まる。そして厨房には、最後の晩餐に腕をふるうオーナー・シェフの姿があった。彼には料理人を続けられない理由があったのだ……。アペリティフからデザートまでフランス料理のコースを味わうように、オーナー夫婦とレストランの歴史、店を愛した人々の人間模様、そして閉店を決意した理由が丁寧に描かれていく。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大学は出たけれど", "大学は出たけれど", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 15, "昭和初期の不況を背景に、コメディ・タッチで描く快作。大学を卒業しながらも、いまだに定職に就けないでいる徹夫は今日も紹介状片手に会社を訪問するが、結局ダメ。友人と痛飲して帰宅すると、下宿には“就職した”とニセの電報を受け取った母親と婚約者が訪ねて来ていた。あわてる徹夫だが……。なお、現存するのは一部分のみ。ビデオは松田春翠の活弁つき。", "1929-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EINGESCHLOSSEN　FREI　ZU　SEIN", "フランツの自由", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "長編第２作「ワラー最後の旅」で評価を獲得したC・ワグナーのデビュー作。自由を求めてさまよう青年の苦悩と葛藤を、モノクロの美しい映像に描き出す。幼い頃から微罪を重ねているフランツが刑務所から脱走、盗んだスクーターで旅に出る。途中、家出少女アンドレアと出会い、同棲を始めるが、彼は心を開くことができない。そんなある日、フランツはアンドレアに刑務所を脱走した本当の理由、心に傷を負った苦い過去を語り始める。", "1983-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("古今笑叢２", "韓国官能秘話 かまきり２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "異色のコリアン・エロス。村で評判の美しい娘タルビは、外見からは想像できないほどの欲望を隠し持っていた。男たちを誘惑しては、結局満足を得られないまま日々が過ぎていく。ある時、若者ペジギが村へ立ち寄り、荷車の下敷になっていた村人を助けたことから、村に住むようになり……。", "1990-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Short Cakes", "Short Cakes", "", "", "http://ecx.images-amazon.com/images/I/518PZT1S2SL._SL210_.jpg", 80, "「ココニイルコト」「13階段」の長澤雅彦監督が、2003年夏より自身のウェブサイトで公開した全5話のショートムービーを集めたオムニバス作品。24時間営業のダイナー「Book café」を舞台に、奇妙な噂話ばかりを集めてくる女性・照美をはじめとするどこか風変わりな常連客たちと、ウエイトレスのナナ、マスターの小暮が織りなす交流をコミカルに描きだす。ナナ役に「茶の味」の相武紗季。", "2005-02-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NIGHT BEFORE", "キアヌ・リーブス　ミッドナイトをぶっとばせ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "K・リーヴスが「危険な関係」で大役を射止める直前の主演映画。多少ドジで間抜けな高校生ウィンストンが彼の役どころ。幸運にも学園のマドンナとダンス・パーティーへ行くことになったウィンストンが、思いがけないトラブルに巻き込まれる青春コメディ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Meet Joe Black", "ジョー・ブラックをよろしく", "", "", "http://ecx.images-amazon.com/images/I/51xBA5UUymL._SL210_.jpg", 181, "B・ピットがクールな死神役に扮したラブ・ファンタジー。人間の姿を借りた死神が、N.Y.の大実業家パリッシュを“お迎え”にやって来た。パリッシュの寿命が尽きるまでのわずかの間、死神は“ジョー・ブラック”と名乗り、パリッシュを案内役にして人間界を探索することに。やがて彼は、パリッシュの娘スーザンと恋に落ち、彼女を自分の世界に連れ帰ろうとするが……。死神の恋を軸に、死を受け入れるパリッシュの人間ドラマと彼の会社の危機のエピソードが絡む。全般的にB・ピットの魅力を捉えたショットが多く、ロマンスの濃度はきわめて高い。", "1998-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Round 1", "ROUND1", "", "", "http://image.eiga.k-img.com/images/movie/1622/315x210.jpg?1571", 104, "ボクシングの元世界スーパー・フェザー級＆ライト級チャンピオン、畑山隆則を主演に、ソン・ソンミらが共演、「湘南爆走族」の山田大樹が監督、「友へ／チング」の撮影・照明スタッフが集合した日韓合作映画。役者を目指すトッポは、韓国映画のスターをめざしてソウルへ行くが、ひょんなことから韓国ヤクザの用心棒に。日本に帰国しプロの詐欺師を目指す。やがて時価約30臆円の財宝、百済の黄金香炉を巡る詐欺に誘われるが。", "2003-03-01", "76,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロスト・バイ・デッド", "ロスト・バイ・デッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「六月の蛇」に出演していた辻岡正人が自ら主演し、監督に初挑戦。荒削りなモノクロ映像の中、恋人の自殺から立ち直れない青年の自己破滅的な暴走が綴られる。メジャー・デビューのチャンスを目前に、恋人・真理の自殺のショックから立ち直れないアキラ。バンドの面々は、彼を心配しながらも苛立っていた。やがて、アキラの破滅衝動は日増しに深まっていく。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA TREGUA", "遙かなる帰郷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "アウシュビッツ収容所から奇跡的に生還したユダヤ人作家P・レーヴィの小説『休戦』を映画化。1945年、ドイツ軍の撤退によってアウシュビッツが解放され、ユダヤ人のプリーモは故郷トリノへの帰郷を決意。だが、戦後の混乱の中では旅もままならず、彼の道中には数々の障害が待ち受けていた。厳しい旅ながら、したたかなヤミ屋や、美しい看護婦との交流を通して、彼は生きる意欲を取り戻していく。収容所生活で植え付けられた絶望が少しずつ希望へと変わる、その過程をじっくりと描いた味わい深い人間ドラマ。", "1996-01-01", "4,2,7,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("箱根山", "箱根山", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "箱根の２軒の旅館、玉屋と若松屋は血縁同士なのに先祖代々犬猿の仲。互いに敵意をむき出しにして対抗意識に燃えていた。だが、若松屋の一人娘・明日子の家庭教師を、玉屋の後継ぎ・乙夫がすることになった。やがて、二人の間には恋が芽ばえて……。川島雄三監督の文芸ドラマ。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BEAR", "子熊物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "夢にうなされる子熊、大粒の涙を流す馬、情に厚い大熊などなど、人間以上に人間的な動物たちの生態。ドキュメンタリーではない、動物たちのキャラクターを生かした前代未聞の動物映画として、動物愛護の国フランスはもとより、世界中でヒットを飛ばした作品。カナディアン・ロッキーの山中。落盤事故で母熊が死んでしまい、厳しい自然の中に、ただ一匹放り出された子熊。蝶を追いカエルを追って草原を駆けまわる彼は途中、孤独な大雄熊と出会い、そのあとをつけて行くが……。言葉を持たない動物たちの、微妙な感情の動きを捉え、画面に定着させた監督の演出力は見事。セザール賞で監督賞を受賞した。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH TRAP", "悪魔の沼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「悪魔のいけにえ」の成功で、フーパーがハリウッドに招かれて撮った第２作。これも実話に基づいている。テキサスで連続失踪事件が発生した。捜査に当たった警察は、ワニの飼われている池で、髪や肉片を発見。狂人が、旅行者の首を大鎌ではね、捨てていたのだ……。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Almaz Black Box", "アルマズ・プロジェクト", "", "", "http://image.eiga.k-img.com/images/movie/54482/315x210.jpg?3888", 89, "1998年、地球の軌道を回っていたロシアの極秘宇宙ステーション“アルマズ号”が突如レーダーから消失し、その4日後に地球の大気圏に突入して爆発した。この事件は“アルマズ・プロジェクト”と呼ばれ、ロシア政府により闇に葬られた。宇宙船の残骸から発見されたブラックボックスに残されていた映像を編集した衝撃作。", "2009-06-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("島の色　静かな声", "島の色　静かな声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "写真家・映像作家として国際的に活動する茂木綾子が、美しい自然や貴重な動物たちの残る沖縄・西表島で、“色”をテーマに撮りあげたドキュメンタリー。島の伝統文化や環境を守るために尽力する染色作家・石垣昭子とパートナーの三味線奏者・石垣金星の日常を軸に、自然と共に生きる島人たちの素朴な暮らしを静かに見つめていく。撮影を手がけるのは、茂木監督の夫でもあるドイツ人映像作家、ベルナー・ペンツェル。", "2009-04-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOLSON SINGS AGAIN", "ジョルスン再び歌う", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "アメリカのショービジネス界の礎を築いた一大エンターテイナー、アル・ジョルスンの半生を描いた「ジョルスン物語」の続編。妻に去られ、一時は引退かと思われたアル・ジョルスンは、海外将兵の慰問で世界各国を訪ねる。帰国後、看護婦エレンと結婚した彼は、慰問先で出会った部隊長で、現在は映画製作者のブライアントから彼の伝記映画の話を持ちかけられ……。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新網走番外地", "新網走番外地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "マキノ雅広が石井輝男から受け継いで監督した一編。昭和23年、復員した末広勝治は、新橋で石津組と華僑連合の対決に巻き込まれ犠牲者となった親友の死に怒り、悪徳ヤクザをカービン銃で葬る。マキノ雅広らしいにぎやかな顔ぶれによる新シリーズだが、今ひとつしっくりこない。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京の宿", "東京の宿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "坂本武を主人公にした人情劇、“喜八もの”の一編。失業した父が二人の子供を連れ、工場地帯を職を求めて歩き回る。次々に門前払いを食った父子は、安宿に転がり込み、同じ放浪の身にある母娘に出会う……。深刻な失業問題を取り上げ、殺伐とした風景の中に人間の孤独感や絶望をしみ出させた作品。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長三国志・第二部", "次郎長三国志・第二部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "森の石松は追分の三五郎と知り合い、ある賭場で投げ節お仲の妖艶ぶりに二人ともスッテンテンにされてしまう。一方、次郎長一家は、開帳中を役人に踏み込まれ小川武一の身代わりで入牢。そこで悪らつな牢名主を叩きのめす。長門裕之の石松が快調。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬とあなたの物語　いぬのえいが", "犬とあなたの物語　いぬのえいが", "", "", "http://image.eiga.k-img.com/images/movie/55572/315x210.jpg?3083", 0, "犬と人とが織りなす心温まるエピソードをつづったオムニバス映画「いぬのえいが」の第2弾。「ゴースト　もういちど抱きしめたい」で3年ぶりに映画主演を務めた松嶋菜々子と大森南朋が夫婦役で初共演するほか、北乃きい、中尾彬、内野聖陽ら多彩なキャストが結集する。「西の魔女が死んだ」の長崎俊一監督やCM界のトップクリエイター5人がメガホンをとり、涙あり笑いありのストーリーを紡ぎだす。", "2011-01-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦ふ兵隊", "戦ふ兵隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "陸軍省情報部の後援で、「上海」のスタッフが武漢攻略作戦に従軍して撮影したドキュメンタリー。戦闘シーンは少なく、戦い疲れた日本の兵隊と、残された中国民衆たちの悲惨さを冷静に描写したこの作品は、軍の怒りを買い、公開は禁止。さらに亀井文夫監督は、唯一戦争に批判的な映画人として投獄された。戦場で倒れる軍馬の生々しい姿が、厭戦の哀切を訴えてやまない。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　GLANEURS　ET　LA　GLANEUSE", "落穂拾い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "「百一夜」のA・ヴァルダによるドキュメンタリー。都市で道に落ちているものを拾う人々を見るうちに、ミレーの名画『落穂拾い』を連想するヴァルダ。そこから、フランス各地で“現代の落穂拾い”を探す旅が始まる。収穫が終わった畑に捨てられたジャガイモを拾う人々や、廃品やごみを集めてオブジェを作る美術家などを独自の視点で取材していく過程は、詩情にあふれていて味わい深い。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAWNMOWER MAN", "バーチャル・ウォーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "バーチャル・リアリティーとドラッグで知能と運動能力を伸ばす実験をしていた工学博士は知恵遅れの男ジョーブを被験者に選ぶが、彼は完璧な存在、つまり神になるべく暴走を始めた。電脳世界の中で荒れ狂うジョーブを止めることはできるのか？　少々大味な出来ながらCGによるSFXが見事。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUCH ADO ABOUT NOTHING", "から騒ぎ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "「ヘンリー五世」に続いて、K・ブラナーが、シェイクスピアの戯曲を映画化した喜劇。イタリア・トスカーナ地方の町メッシーナ。この町のレオナート知事の元に、アラゴンの大公ドン・ペドロ一行がやって来る。そして、知事の娘ヒーローと伯爵クローディオ、知事の姪ベアトリスと独身主義者のベネディックという、二組のカップルが生まれるのだが……。ペドロ役にD・ワシントン、さらにM・キートンなど、異例の豪華キャストが見もの。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プージェー", "プージェー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ドキュメンタリー番組「グレートジャーニー」で知られる日本人探険家・関野吉晴と、モンゴル遊牧民の少女との5年間にわたる交流を記録したドキュメンタリー。1999年、南米最南端からアフリカを目指して旅を続けていた関野は、モンゴルの大草原を馬に乗って駆けぬける6歳の少女プージェーと出会う。プージェーとその家族の姿を通し、市場経済の導入が遊牧民たちの暮らしにもたらした厳しい現実を浮き彫りにしていく。", "2006-06-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CYCLONE", "超高性能兵器サイクロン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "大気中の水素をエネルギーに変えるトランスフォーマーを備えたスーパー・バイク“サイクロン”。その秘密を狙う組織は発明者の青年を殺し、恋人を奪われた美女テリーは“サイクロン”を操って組織に立ち向かう。劇画タッチのSFXカー・アクション。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PERMISSION TO KILL", "殺しの許可証", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "国際的な協調のためにオーストラリアに帰ろうとする追放者ディアキムと、帰国を妨害しようとするスパイ、カーティス。ディアキムとかかわりのある人間を利用し、暗殺を謀るスパイだが……。特異なムードを放つ、スパイ・ミステリー。", "1975-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チーム・バチスタの栄光", "チーム・バチスタの栄光", "", "", "http://image.eiga.k-img.com/images/movie/53292/315x210.jpg?3170", 118, "第4回「このミステリーがすごい！」大賞に輝いた現役医師・海堂尊のベストセラー小説を「アヒルと鴨のコインロッカー」の中村義洋監督が映画化。難易度の高い心臓手術を連続26回成功させた医師集団“チーム・バチスタ”。そんな彼らが3回続けて手術に失敗、患者はいずれも死に至る。内部調査を引き受けた心療内科の医師・田口（竹内結子）は真相を究明できぬまま事故と結論づけようとするが、報告書に納得できない厚生労働省の役人・白鳥（阿部寛）は再調査に乗り出す。", "2008-02-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バトル・ロワイアル3D", "バトル・ロワイアル3D", "", "", "http://image.eiga.k-img.com/images/movie/55612/315x210.jpg?1300", 0, "過激な暴力表現が物議をかもした深作欣ニ監督のバイオレンス・アクション「バトル・ロワイアル」を3D化。少年犯罪の増加が深刻化する近未来の日本で、全国の中学3年生から無作為に抽出された1クラスを最後の1人になるまで殺し合わせる“新世紀教育改革法＝BR法”が施行された。その対象となり、強制的に無人島へと連れ去られた城岩中学3年B組の生徒たちは、3日間の制限時間の中で生き残りをかけて死闘を繰り広げる。主演は藤原竜也。", "2010-11-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ГОРЯЧИЙ СНЕГ", "白銀の戦場・スターリングラード大攻防戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "U・ボンダリョフ原作の『熱い雪』の映画化。1942～43年の約200日間にわたって繰り広げられた独・ソのスターリングラード攻防戦を壮大なスケールで再現する。80万人に及ぶ死傷者を出し、2000に及ぶ戦車と、飛行機が破壊されたという戦闘の模様を叙事詩的に語った大作。ビデオ、DVDタイトルは「スターリングラード大攻防戦」。", "1972-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一人息子", "一人息子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "小津が初めて手掛けたトーキー作品。信州の田舎でたった一人の息子を進学させるために田畑を売り、身をけずって働いた母が、年老いてから東京の息子に会いにやって来る。だが、大学を出て出世していると思っていた息子は夜学の教師にすぎず、妻子とともに貧しい暮らしを送りながら将来への野心も失っていた……。小津が得意とする“親子”ものであるが、絶望的に暗いその世界は戦前の世相を反映していたのであろうか。全体的にトーキー１作目としては音に対する実験的な姿勢はみられず、あくまで現実音とセリフを使っただけのサイレント的演出で終始しており、小津スタイルの祖型となっている。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOUNTAINS OF THE MOON", "愛と野望のナイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "植民地開拓と探検家の活躍が華やかだったころの19世紀ヴィクトリア朝大英帝国。その当時に実在した世界探検史上に名高い二人の冒険家、リチャード・バートンとジョン・スピークが挑んだアフリカ4700キロの過酷な旅。彼らの苦難と栄光、そして波乱に満ちた愛と野望の半生を描いたアドベンチャー・ロマンが壮大なアフリカの原野を舞台に繰り広げられる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メイン・テーマ", "メイン・テーマ", "", "", "http://ecx.images-amazon.com/images/I/413176V4YDL._SL210_.jpg", 100, "昨日まで幼稚園の先生だった小笠原しぶきは、房総の海岸で手品の修行のために全国を回っている健と出会う。2人は彼の４WDで一緒に旅をすることになるが、ソリが合わずケンカばかり。やがて2人は沖縄へ……。若いカップルの道中に中年の男女の恋を絡めた、ライト感覚の青春映画。森田と薬師丸ひろ子のコンビはこれが初めて。相手役・野村宏伸は新人だった。", "1984-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("西住戦車長伝", "西住戦車長伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "日中戦争当時、実際に大陸で数々の華々しい殊勲をたて、軍神と呼ばれた西住小次郎中尉の姿を描いた菊池寛原作評伝の映画化。いわゆる“軍神もの”の一編だが、通りいっぺんの讃歌には終わらず、その人間的な内面を良く描き出したものとして映画的にも評価されている。「暖流」の興行的・批評的大成功で一挙にネームバリューを高めた吉村公三郎監督が、その次回作として撮り上げた作品。脚本は「晩春」「秋刀魚の味」など、小津安二郎の処女作から遺作に至る大部分の作品でコンビを組んだ野田高梧。助監督に木下惠介、中村登らがついている。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("影狩り・ほえろ大砲", "影狩り・ほえろ大砲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "“影狩り”ものの第２作。ある藩が幕府に内密に大砲を作るが、影一味が暗躍し始める。そこで影狩り３人衆が招かれ大砲をめぐって影一味との間に大攻防戦が行われる。太りすぎで動きのにぶい裕次郎を尻目に、ニヒルな成田三樹夫とコミカルな内田良平が場面をさらう。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天国の駅 HEAVEN STATION", "天国の駅 HEAVEN STATION", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "美しさゆえに男たちの欲望にされ、二人の夫を殺して死刑囚となった女・林葉かよの半生を映画化した作品。吉永小百合が初めて殺人犯という汚れ役に挑戦し、世のサユリストたちをうならせたことでも話題に。彼女に愛を捧げる知恵遅れの男を、西田が好演。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JULIA", "ジュリア", "", "", "http://ecx.images-amazon.com/images/I/415jzB80waL._SL210_.jpg", 117, "女流劇作家リリアン・ヘルマンの回顧録の映画化。ジュリアとリリアンは幼なじみであったが、第二次世界大戦前夜、ジュリアは反ナチの運動に加わっていた。そんなある日、劇作家として成功したリリアンのもとへ、ジュリアが人を介して反ナチの運動資金を届けてくれと依頼してくる。かけがえのない親友のためにリリアンは危険を覚悟でベルリンに向かう……。二人の女性の生涯にわたる友情と、リリアンと作家ダシール・ハメットのプライベートな生活とを交互に描いたこの作品は、戦争によって親友を奪われたリリアンのナチに対する怒りが、深く静かに描かれている。ジュリア役のレッドグレーヴの熱演が感動的な一編。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前金融", "喜劇・駅前金融", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "場末の繁華街にある金成ビルの管理人・伴野孫作は金の亡者で商人相手の高利貸し。40歳にして経理士の資格を取った森田徳之助とインテリのバンドマン・次郎が絡む結婚コメディ。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Texas Chainsaw Massacre", "テキサス・チェーンソー", "", "", "http://image.eiga.k-img.com/images/movie/1692/315x210.jpg?2520", 99, "73年の夏、コンサート会場に向かうためテキサスの田舎を車で通過しようとする5人の若い男女。彼らは想像を絶する事態に遭遇、ひとりずつ切り刻まれていく。73年のトビー・フーパー監督のカルト映画「悪魔のいけにえ」をまさかのリメイク。このリメイクのために製作プロダクションを設立したのは「アルマゲドン」「バッド・ボーイズ」の監督マイケル・ベイ。実は熱烈なホラー映画ファンで「どうしても作りたかった」と発言。", "2004-03-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALONG CAME JONES", "無宿者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ハリウッドの大スター、G・クーパーが自ら製作したコメディ・タッチのウエスタン。鼻歌はうまいが射撃はてんでダメな男が、相棒とあてどもなく西部をさまよっているうちに、美しい娘と出会う。そのために殺人者に間違えられて駅馬車強盗と対決するハメになってしまう。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stolen", "消えたフェルメールを探して　絵画探偵ハロルド・スミス", "", "", "http://image.eiga.k-img.com/images/movie/53832/315x210.jpg?2296", 83, "1990年、ボストンのイザベラ・スチュワート・ガードナー美術館から盗まれたフェルメールの絵画「合奏」は、未だに発見されていない。この事件解決に、絵画探偵のハロルド・スミスなる人物が挑むドキュメンタリー。08年8月から東京美術館で開催された「フェルメール展　光の天才画家とデルフトの巨匠たち」にタイミングを合わせて公開。", "2008-09-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人らしく生きよう・国労冬物語", "人らしく生きよう・国労冬物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1987年に民営化、“JR”となった国鉄。その分割民営化によって仕事を奪われた国労＝国鉄労働組合の組合員の14年間の闘いを追ったドキュメンタリー。運転士、保線労働、車両検査に携わった労働者３人を軸に、彼らの声や家族の表情をとらえていく。職場復帰を求め、権力と闘い続けてきた鉄道員たちの怒りと抵抗、そして容赦ない弾圧の実態が浮き彫りに。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Going by the Book", "正しく生きよう ", "", "", "http://image.eiga.k-img.com/images/movie/54122/315x210.jpg?3361", 102, "「韓流シネマ・フェスティバル2008ラブ＆ヒューマン」上映作品。銀行強盗犯になりきった巡査のドタバタを描くコメディ。原作は日本映画の「遊びの時間は終わらない」（91）。「小さな恋のステップ」のチョン・ジェヨンが真面目な警察官を熱演。サンポ市の新任警察長イ・スンウは、多発する銀行強盗事件の予行演習を行うが、犯人役の交通課巡査チョン・ドマンは真面目で実直。あまりにも完璧に強盗役を演じてしまい、特殊部隊出動という事態にまで発展してしまう。", "2008-10-18", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沈黙の春を生きて", "沈黙の春を生きて", "", "", "http://image.eiga.k-img.com/images/movie/56822/315x210.jpg?1855", 87, "ベトナム戦争時の枯葉剤被害を追い続ける「花はどこへいった」（2007）の坂田雅子監督によるドキュメンタリー。1960年代、ベトナムの戦場ではゲリラが隠れるジャングルをなくすためアメリカ軍により枯葉剤が散布されていた。当時の米政府は「人体に影響はない」としたが、戦後35年がたってもベトナム人はもちろんのこと、作戦に従事した米兵やその子どもにまで被害は広がっていた。手足に障害をもって生まれた帰還兵の娘ヘザーが初めてベトナムを訪れる姿を中心に、化学物質の危険性を描きだしていく。", "2011-09-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HUNDE， WOLLT IHR EWIG LEBEN", "最後の戦線 壮烈第六軍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "第２次世界大戦下の1942年、独ソ戦でドイツ軍敗北のきっかけともなったスターリングラードの戦いを、ドキュメンタリー・タッチで淡々と描いた西ドイツ製作の戦争映画。実際に当時、第六軍に従軍してスターリングラードの凍える雪の寒さを体験したハインツ・シュレーターが脚本に参加している。DVDタイトルは「壮烈第六軍　最後の戦線」。", "1958-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BARKLEYS OF BROADWAY", "ブロードウェイのバークレー夫妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "かつて黄金時代を築いたアステアとロジャースのコンビが、10年のブランクを経て共演した作品。絶大な人気を誇るダンシング・カップルの夫妻に扮した二人が、ため息の出るようなダンス・シーンをたっぷり披露する華麗なミュージカル。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三岔口／DIVERGENCE", "ディバージェンス／運命の交差点", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "アーロン・クォックら香港の３大スターが華やかに共演したクライム・アクション。彼らが扮した刑事、弁護士、殺し屋の人生が、ある事件をきっかけに交錯していく様を巧みな構成で描出。主役３人が体現する男の美学と悲哀も見逃せない。実業家のマネーロンダリング事件の重要証人を殺し屋コークに射殺された刑事シュン。そんなシュンが捜査中にめぐり合った弁護士トウの妻エイミーは、失踪した彼の恋人に瓜ふたつだった。", "2006-04-22", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Woo Woo Kid", "ウー・ウー・キッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　「フィールド・オブ・ドリームス」でスマッシュヒットを飛ばしたフィル・アルデン・ロビンソンが、実話をもとに初メガホンをとった作品。2度の人妻との駆け落ちで、全米の注目を集めた少年の青春コメディ。15歳のソニー役を演じたパトリック・デンプシーは、その後「メラニーは行く！」などの映画出演のほか、米テレビ界のアカデミー賞といわれるエミー賞を受賞している。音楽は「キャバレー」などで２度オスカーに輝いたラルフ・バーンズ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人嚇人／THE DEAD AND DEADLY", "霊幻師弟 人嚇人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "サモ・ハン・キンポーによる“霊幻”ものの第２作。親友が死体となって帰って来る。そして復讐のため肉体を幽霊に貸したジーだが、手違いで自分の魂が元に戻れなくなってしまい……。抱腹絶倒のスラップスティックなホラー・コメディ。", "1983-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La stanza del figlio", "息子の部屋", "", "", "http://image.eiga.k-img.com/images/movie/1552/315x210.jpg?2561", 99, "01年カンヌ映画祭パルムドール受賞作。イタリアの小さな港町で暮らす、精神科医の夫と画商の妻、高校生の娘と息子の4人家族は、平穏な日々を送っていた。が、突然の息子の事故死により、その生活はこれまでと同じではなくなっていく。モレッティ監督自身が父親役、妻役は、80年代のモレッティ作品に出演していたラウラ・モランテ。音楽は「ライフ・イズ・ビューティフル」のニコラ・ピオバーニが担当。", "2002-01-19", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀座二十四帖", "銀座二十四帖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "和歌子は幼い頃、五郎という放浪家に少女像を描いてもらった。画家との再会を夢見る彼女は、その絵を銀座の画廊に持ち込むが、現れるのはニセ者ばかり。そんな折、花屋のコニイが、画家の正体は実兄の三室戸五郎であり、さらに、麻薬密売にかかわる和歌子の別居中の夫がその名を名乗っていることを知る……。風俗小説家として人気を博した井上友一郎の原作を川島雄三が映画化。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナンミン・ロード", "ナンミン・ロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "下町の鋳物工場で働くベトナム難民のトク。その仕事はとてもつらいものだが、愛する恋人キムと実の妹ランが彼の心の支えとなっていた。そんな彼の前に９年前にある事件をきっかけに別れた親友ヨンが現れ……。自主製作ながら注目を集めた「津軽」の五十嵐匠が、在日ベトナム人青年の悲しさを描く。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHADOW　FURY", "シャドー・フューリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "パンクラスの創設者である格闘家、舟木誠勝の主演による近未来アクション。遺伝子研究所ノバに雇われた用心棒マドセンは、研究所から逃亡した狂気の博士を追跡。だが、博士の生み出した無敵のクローン戦士タケルのまえに苦戦を強いられる。やがてノバの陰謀を知らされたマドセンは、タケルと組んでノバへと殴り込み……。タケルに扮した舟木は、クライマックスでは元パンクラス王者バス・ルッテンを相手にバトルを熱演。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("dead BEAT", "dead BEAT", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "新人、真野きりなが独自の存在感を見せた犯罪サスペンス。いわくありげなバーのマスターとともに、盗難車を売り飛ばす毎日を送る青年。ある日、彼が発見したのは多額の金を積んだベンツのトランクに押し込まれていた若い女だった。左目に眼帯をしたこの女はいったい何者か？　やがてベンツの持ち主のヤクザが、マスターと青年のもとに迫ってくる。３人の男の人生を変容させていくヒロイン——昨今の日本映画には珍しい“ファム・ファタル”的なキャラクターを画面に現出させた真野のたたずまいが絶品。ビデオタイトルは「さらば極道 dead BEAT」。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("11・25自決の日　三島由紀夫と若者たち", "11・25自決の日　三島由紀夫と若者たち", "", "", "http://image.eiga.k-img.com/images/movie/57402/315x210.jpg?2203", 119, "「実録・連合赤軍　あさま山荘への道程（みち）」「キャタピラー」の若松孝二監督が、1970年11月25日、防衛庁内での割腹自決へといたるまでの三島由紀夫と「楯の会」の若者たちの物語を映画化。学生運動全盛期の1968年、三島は文筆業の傍らで民族派の学生たちと「楯の会」を結成し、有事の際には自衛隊とともに決起するべく訓練にいそしんでいた。しかし、警察権力の前に自衛隊は出動の機会すらなく、楯の会の若者たちは苛立ちを募らせていく……。三島を演じるのは、本作のため芸名をARATAから本名に戻した井浦新。", "2012-06-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE UNDEFEATED", "大いなる男たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "たそがれた味わいのJ・ウェイン主演作。南北戦争が終わり、今は牧場を経営する元大佐のヘンリーはメキシコに馬を売りに行く途中、反乱軍鎮圧に向かう元南軍大佐ラングドン率いる一隊と出会い、二人は互いに友情を感じていく。やがて反乱軍にラングドン一行が全員捕らわれたと聞いたヘンリーは、危険を顧みずに敵地に単身乗り込んでいくが……。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ENDLESS　SUMMER　II", "エンドレスサマーII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "約30年ぶりに製作された古典サーフィン・ドキュメンタリーの続編。プロサーファー、ウィーバーとオコンネルを主演に据えて、完璧な波を求めて世界をまわる彼らの旅に、カメラは密着。国や地域ごとに異なる波やサーフィンの風習を捉えていく。トップサーファーたちの妙技はもちろん、前作に主演したR・オーガストの出演も見どころ。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROCKY III", "ロッキー３", "", "", "http://ecx.images-amazon.com/images/I/515Zri8cGPL._SL210_.jpg", 99, "ヘビー級チャンピオンの座を獲得して３年。ロッキーは愛妻と一粒種のロッキーJr.に囲まれて人生の至福にあった。そんなある日、かつてない強敵黒人ボクサーが現れる……。相棒のミッキーが死ぬも、盟友アポロをトレーナーに迎えて再びロッキーが燃えるシリーズ第３作。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEVER SO FEW", "戦雲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "第二次大戦中のビルマ。ゲリラ部隊は日本軍の攻撃に必死に抵抗を続けていた。その指揮を執るのが米軍のトムと英軍のダニーだ。連絡のためカルカッタに飛んだ二人は、そこでカーラという美しい女性にめぐり会う。戦争の絡んだメロドラマ。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VOICES OF SARAFINA！", "サラフィナの声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "南アフリカでのアパルトヘイト。それは、自国でありながら、人口のわずか15パーセントを占める白人によって“分離”されてきた南アフリカ国民の屈辱の社会体制である。本編は、そのアパルトヘイトに反対する、ロングランのブロードウェイ・ミュージカル『サラフィナ』の出演者を追ったドキュメンタリーである。キャストはすべて南アフリカの子供たちから選ばれた。誰もがアパルトヘイトによる残虐な抑圧を身近に体験しているのだが、彼らの素顔は明るく、抵抗の歌をコーラスする声は力強い。祖国の惨状を世界に訴える使命感に燃える、若い彼らのエネルギーに引きずり込まれてしまう秀作だ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TURTLE BEACH", "抱きしめたいから", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "マレーシアの美しい浜辺で出会ったオーストラリア人女性ジャーナリストと、ベトナム難民としての過去を持つ大使夫人。友情を深めた彼女たちは、それぞれの故郷に残してきた子供たちへの熱い思いを胸に、定められた運命に突き進んでいく。難民問題を背景にして、二人の女性の勇気と友情を壮大なスケールで描いた衝撃の問題作。", "1991-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAPEHEADS", "テープヘッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "しがない警備会社に就職したアイヴァンとジョシュは親友同士。一念発起してビデオ制作会社を設立した二人はくだらない仕事でもとりあえずこなしていたが、ある日大統領候補者をめぐる珍事件に巻き込まれる……。ミュージック・ビデオ畑出身のB・フィッシュマン監督が作ったポップなコメディ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜の流れ", "夜の流れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "花柳界に生きる雇われおかみと、大学へ通うその娘との確執を描いた女性映画。古い世代の人物が登場する場面を成瀬巳喜男、若い世代の場面を川島雄三が担当するという、かつてない共同監督が試みられた。若者たちが大騒ぎするテンポの早いシーンと、母娘の情感が通い合うしっとりとした場面との対比が興味深い。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Elf", "エルフ　サンタの国からやってきた", "", "", "http://ecx.images-amazon.com/images/I/517H10ASYAL._SL210_.jpg", 95, "小さな妖精エルフに育てられた人間の男性が巻き起こす騒動をウィル・フェレル主演で描き、全米大ヒットを記録したファンタジックコメディ。ある年のクリスマス、孤児院で1人の赤ん坊がサンタの袋に紛れこみ、そのまま北極へと連れて行かれてしまう。エルフに預けられた赤ん坊は、バディと名づけられすくすくと成長していく。そして30年後、立派な大人の男性になったバディはニューヨークにいるという実の父親を探しに旅立つが……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・日本列島震度０", "喜劇・日本列島震度０", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "おそらく当時東宝で映画化された小松左京の「日本沈没」を意識して作られたものであろう。大地震が襲来するという噂が飛び交うなか、女占い師が地震の起こる月日を予言したことで巻き起こる、人々のパニック状態を描く。前田陽一お得意の喜劇で、地震対策委員に選ばれるフランキー堺が大いに笑わせてくれる。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COLD FIRE", "L.A.コマンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「地獄の軍団　スクワッド」などの個性派俳優W・ハウザーの初監督・主演作。犯罪都市ロサンゼルスで、新しく開発された超強力な麻薬“コールド・ファイアー”をめぐり、それによって莫大な利益をもくろむシンジケートと、麻薬組織壊滅作戦を進めるロス市警との麻薬戦争を描くポリス・アクション。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHERLOCK JR.", "キートンの探偵学入門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "キートンによる長編の無声スラップスティック・コメディで、彼のベスト５に間違いなく入る名作。映画館で映写技師として働くキートンは、恋仇きの策略によって恋人の前で泥棒呼ばわりされて意気消沈。上映中にうたた寝して映画の中へ入って行った彼は、そこで名探偵シャーロックJr.となって悪漢相手に大活躍を繰り広げる……。後年、ゴダールの「カラビニエ」、ウディ・アレンの「カイロの紫のバラ」などにも影響を与えたという、“夢”のシークエンスがともかく素晴らしい作品。また、暴走するオートバイのハンドルの上に座ってスタントをこなす姿など、感動的なまでの芸人根性もすごい。「忍術キートン」というタイトルでも上映された。", "1924-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("就職戦線異状なし", "就職戦線異状なし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "激化する就職戦線にスポットを当て、より良い企業の内定を取るべく奮闘する学生たちの就職活動をユーモラスに描いた青春ドラマ。「咬みつきたい」に続いて連続登板の金子修介が、織田、的場、仙道、和久井ら当時のTVのトレンディー俳優の個性を引き出した青春群像劇の佳作である。TVドラマ『東京ラブストーリー』などで知られる坂元裕二が脚本協力をしている。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("「超」怖い話 THE MOVIE／　闇の映画祭", "「超」怖い話 THE MOVIE／　闇の映画祭", "", "", "http://ecx.images-amazon.com/images/I/51MJQT7QG3L._SL210_.jpg", 84, "実話系ホラー作家として人気を博す、平山夢明の原作を映画化したオムニバス・ホラー。平山自身が初監督に挑戦した要注目の短編をはじめ、日本映画界で活躍する、遠藤憲一、津田寛治、快楽亭ブラック、矢部美穂らが、それぞれ監督や主演を兼任。独自の恐怖演出を競い合う異色の短編集だ。帰宅途中のOLが、リストカッターの不気味な女に追い回される。OLはどうにかその女を振り切るが……。(「深夜ノ墜」)", "2005-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE COLNEL CHABERT", "愛の報酬　シャベール大佐の帰還", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "バルザックの小説を映画化した文芸作品。1871年のパリ、シャベール大佐が弁護士デルビルを訪ねて来るところから物語が始まる。彼は10年前に戦死したことになっており、妻はすでに再婚しフェロー伯爵夫人となっていた。戦場で死体に間違えられ、その後記憶喪失になり10年間さまよったという大佐の言葉に興味を持った弁護士は、侯爵夫人の顧問弁護士でもあった。弁護士事務所で対面した大佐と夫人だが、夫人はその男は大佐ではないと言うのだった……。F・アルダンとG・ドパルデューが「隣の女」以来、13年ぶりに共演。「伴奏者」などの名カメラマン、Y・アンジェロの監督デビュー作でもある。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クライシス2050", "クライシス2050", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "日本の原案と資本により、ハリウッドのスタッフ、キャストを得て製作された異色のSFアドベンチャー。西暦2050年、太陽が突然膨脹を始め、荒れ狂う熱と磁気と放射線の牙は容赦なく地球に襲いかかる。最後の手段として反物質爆弾を積んだ宇宙船ヘリオスが太陽に向かうが……。「ブレードランナー」などのヴィジュアル・フューチャリスト、シド・ミードが作り出す2050年の創造物も必見。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kit Kittredge: An American Girl", "キット・キトリッジ　アメリカン・ガール・ミステリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Think Big", "バーバリアン・ブラザーズの シンクビッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "　筋肉隆々の双子の兄弟レイフとビクターが巻き起こすアクションコメディ。トラック運転手を営む2人が、仕事で訪れた研究所で少女を救ったことから、事件に巻き込まれていく。主演は、アーノルド・シュワルツェネッガーに、‟バーバリアン・ブラザーズ”と命名されたボディビルダーの双子。翌年、続編にあたる「バーバリアン刑事」が製作された。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAREWELL，MY LOVELY", "さらば愛しき女よ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "アメリカのハードボイルド小説が生んだ３大探偵といえばハメットのサム・スペード、マクドナルドのリュー・アーチャー、そしてチャンドラーのフィリップ・マーロウだろう。過去ハンフリー・ボガートやエリオット・グールドなどが演じたこの名探偵を、今回はR・ミッチャムが好演、都会に疲れた男の哀しみを見事ににじませている。1941年ロサンゼルス。ある女の消息を依頼されたマーロウは、わずかな手掛かりをもとに捜査を進めていたが、彼の前で次々と殺人が発生、重要参考人にされてしまう。警察の追及と、暗殺者に狙われながら、マーロウは事件の核心に迫るのだが……。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使行動之二　火鳳狂龍／ANGELII", "天使行動２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "西城秀樹の主演で話題となった「天使行動」の続編。“エンジェル”というコードネームで呼ばれる秘密部隊が、マレーシアで進められている武力革命を叩きつぶすべく行動を開始する。武力革命の首謀者は、意外にも“エンジェル”のリーダーのかつての親友で……。全編に展開する派手なアクションが見もの。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Contagion", "コンテイジョン", "", "", "http://image.eiga.k-img.com/images/movie/57182/315x210.jpg?4078", 106, "「トラフィック」「オーシャンズ11」のスティーブン・ソダーバーグ監督が、マリオン・コティヤール、マット・デイモン、ジュード・ロウ、ケイト・ウィンスレットら豪華キャストを迎え、地球規模で新種のウィルスが感染拡大していく恐怖を描いたサスペンス大作。接触感染により数日で命を落とすという強力な新種ウィルスが香港で発生。感染は瞬く間に世界中に拡大していく。見えないウィルスの脅威に人々はパニックに襲われ、その恐怖の中で生き残るための道を探っていく。", "2011-11-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("976 −EVIL", "ホラー・スコープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "サイキック・ホラーの名作「エルム街の悪夢」で、夢の中の怪人フレディを演じ、一躍カリスマ的ヒーローになったR・イングランドが演出を手掛けるSFXホラー。“976−EVILホラースコープサービス”と印刷された一枚のテレフォンカードを拾ったことから巻き起こる恐怖が、凄絶に描かれる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lovely Still", "やさしい嘘と贈り物", "", "", "http://image.eiga.k-img.com/images/movie/55122/315x210.jpg?4078", 90, "年老いたロバートは、アメリカの小さな町でひとり寂しく暮らしていた。そんなある日、ロバートはメアリーという美しい女性と恋に落ち、彼の孤独だった日々に光が差し始める。しかし、実はメアリーはロバートの妻で、ロバートは全ての記憶を忘れてしまっているのだった。主演は「エド・ウッド」のマーティン・ランドー、「アリスの恋」のエレン・バースティン。", "2010-03-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Los Bastardos", "よそ者", "", "", "http://image.eiga.k-img.com/images/movie/54792/315x210.jpg?4342", 90, "長編監督デビュー作「Sangre」で2005年カンヌ国際映画祭国際批評家連盟賞を受賞したメキシコ人若手監督アマ・エスカランテの長編第2作となる犯罪ドラマ。ロサンゼルスで暮らす2人の不法移民青年ヘススとファウスト。日雇い労働者としてストレスだらけの日々を送っていた彼らは、ある日舞い込んできた割の良い仕事に飛びつく。ところが、銃を手に入れたことをきっかけに2人の運命は大きく狂いはじめ……。", "2009-09-18", "19,2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("強盗放火殺人囚", "強盗放火殺人囚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「脱獄広島殺人囚」「暴動島根刑務所」に続く松方弘樹主演による“刑務所”ものの第３作。今回は中島貞夫に代わって山下耕作が監督した。刑務所内の陰謀で仮釈放を取り消された主人公が、懲役48年の男とともに脱走し、不正入試に絡む刑務所内の腐敗を暴く。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEW YORK STORIES", "ニューヨーク・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "魅力に満ちた世界の中心の街、ニューヨーク。３人の監督がこの街へのそれぞれの思いを各自独特のスタイルでぶつけ合ったオムニバス映画。第１話はN.Y.のアート・シーンを背景に、抽象表現主義の画家とその弟子兼愛人の関係をダイナミックな色彩で描く。第２話はN.Y.の超上流階級に属する少女ゾイがゴージャスなだけだった暮らしの中から、世界的なフルート奏者の父や母と一緒にいることの幸せを見つけるまでを描く。第３話はW・アレン自作自演による“母親恐怖症”の男の話で、アッと驚くSF的な展開によってユダヤ人の肉親愛や男と女のすれ違いなどをユーモラスに描いている。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANANANGONG BANGUNGOT／PERFUMED　NIGHTMARE", "悪夢の香り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ドキュメンタリーとフィクションの境界を軽々と乗り越えるフィリピンの映画作家K・タヒミックが、監督・主演など５役をこなして作り上げた長編第１作。タヒミック自身が演じる主人公は、植民地主義が染みついたフィリピン社会に育った“僕”。アメリカ人のビジネスマンに気に入られてパリに旅立ち、ニューヨーク、ドイツと渡り歩いていく彼の旅の物語が、ナンセンスとも思える展開と詩情豊かなユーモアで語られる。", "1977-01-01", "35", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ossessione", "郵便配達は二度ベルを鳴らす", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "　アメリカ人作家の同名小説をもとに、舞台を北イタリアに移し男女の策略と悲哀を描いた傑作。ルキノ・ビスコンティのデビュー作であり、イタリア映画界においてネオレアリズモの先駆けとなった作品である。飲食店を営む夫婦のもとに、ジーノという風来坊が転がり込む。妻とジーノはすぐに深い仲になり駆け落ちを計画するが、いつしかお互いに疑心が芽生え始める。二転三転する2人の関係に、ラストまで目が離せない。", "1979-05-26", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("36 quai des orfevres", "あるいは裏切りという名の犬", "", "", "http://image.eiga.k-img.com/images/movie/1262/315x210.jpg?3798", 110, "ダニエル・オートゥイユとジェラール・ドパルデューというフランスを代表する名優を主演に、正義を貫く刑事と野心家の刑事2人による宿命の闘いを描くフランス製本格派フィルム・ノワール。パリ警視庁に勤務する警視、レオとクランはともに次期長官候補。かつては親友だったが、ひとりの女を取り合ったことから仲違いし、今はお互いに敵視している。そんな中、レオが長官に任命されるが……。ロバート・デ・ニーロ製作によるハリウッドリメイクも決定している。", "2006-12-16", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ララピポ", "ララピポ", "", "", "http://image.eiga.k-img.com/images/movie/53892/315x210.jpg?3624", 0, "直木賞作家・奥田英朗の同名ベストセラー小説を、「下妻物語」「パコと魔法の絵本」の中島哲也が脚色し、中島の弟子である宮野雅之が長編デビュー作として監督した群像コメディ。出演に成宮寛貴、お笑いトリオ森三中の村上知子ほか。たくさんの人々が行き交う東京で、風俗専門のスカウトマン、デブ専AV女優、対人恐怖症のフリーライターなど、堕落した日常を送る人々が交錯する。", "2009-02-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("驚魂記／WEB OF DECEPTION", "蜘蛛に抱かれた女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "女弁護士リンをゆすっていたアシスタントのメイとその親友クィーニー。その頃、刑務所から出所して来た双子の妹キャットがヤクザから金を脅かされ、早急に金が必要になったクィーニーが、リンの家に盗みに入る。しかし、リンに見つかり、誤って殺されてしまう。動揺したリンはメイに助けを求めるが、翌日リンの前にクィーニーが現れ……。主演のJ・ウォンが一人二役に挑戦。女のしたたかさを描いたサスペンス。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Return To Goree", "ユッスー・ンドゥール　魂の帰郷", "", "", "http://image.eiga.k-img.com/images/movie/54132/315x210.jpg?3376", 112, "世界的人気を誇るセネガル出身のミュージシャン、ユッスー・ンドゥールが、アフリカ音楽と黒人音楽のルーツやその背景に迫った音楽ドキュメンタリー。ンドゥールは盲目のジャズ・ピアニスト、モンセフ・ジュヌと共に、苦痛に満ちた歴史の中で黒人が生み出してきたブルース、ジャズ、ラップといった音楽の起源を探る旅を敢行。各国のミュージシャンたちとのセッションを通し、文化的違いを超えた黒人音楽の魂に触れていく。", "2009-02-14", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五毒／THE FIVE VENOMS", "五毒拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ムカデ拳、ヘビ拳、サソリ拳、ガマ拳、ヤモリ拳を、それぞれ習得した５人の拳士、通称“五毒”。彼らの拳を邪拳として封印しようとする老師は門弟ヤンに命じて、５人の善悪を見極めさせるが……。奇抜なマスクを被った五毒のビジュアルや、ダークなタッチが鮮烈な印象を残す異色武術アクション。タランティーノが「キル・ビル」でオマージュを捧げたことで、再評価の波が高まった。", "1978-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子連れ狼・子を貸し腕貸しつかまつる", "子連れ狼・子を貸し腕貸しつかまつる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "小池一雄・小島剛夕のコンビによる人気劇画を三隅研次監督と若山富三郎のコンビで映画化。柳生一族によって公儀介錯人の職を追われ、妻をも殺害された拝一刀は、一子・大五郎を箱車に乗せ、すご腕の刺客“子連れ狼”となる。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無頼の遺言　棋士・藤沢秀行と妻モト", "無頼の遺言　棋士・藤沢秀行と妻モト", "", "", "http://image.eiga.k-img.com/images/movie/55232/315x210.jpg?3507", 90, "2009年5月に亡くなった囲碁棋士の藤沢秀行を追ったドキュメンタリー。天才棋士として知られながら、私生活では競輪・競馬のギャンブルにのめり込み借金地獄にあえいでいた藤沢。そんな無頼派の藤沢を支えたのは妻・モトだった。NHKで放送されたドキュメンタリー番組を劇場で公開するプロジェクト「This is REAL／NHK-BSドキュメンタリー・コレクション」にて上映。", "2010-02-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録不良少女 姦", "実録不良少女 姦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "学校生活から逃がれ、奔放に生きる不良少女マコの青春を描いたロマンポルノ。生活力のない男の子供を宿したマコは、出産したあと乳飲み子を抱えながらもたくましく生きていく。16歳ですべてを体験した少女の姿を通して、藤田敏八監督は1970年代末の老成化した青春を鋭い目で見つめている。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES FOUS DU STADE", "クレイジー・ボーイ　金メダル大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "国際スポーツ大会開催を目前に控えたアビニョン近くの町。祝賀パーティー準備で連日忙しい八百屋のジュールにはキュートな娘がいるのだが、大会にかこつけて町にやって来たおかしな４人組と聖火ランナーが彼女に惚れ込んだため大混乱に。", "1972-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひとにぎりの塩", "ひとにぎりの塩", "", "", "http://image.eiga.k-img.com/images/movie/57142/315x210.jpg?2000", 85, "石川県能登半島の最北端・珠洲（すず）で日本最古の「揚げ浜式」と呼ばれる塩づくりに勤しむ職人たちの姿を追ったドキュメンタリー。海水を汲み上げて砂にまき、天日と風で乾燥させて釜で煮詰める原始的な揚げ浜式の塩づくりを続ける職人たちの塩にかける思いや、雄大な日本海と美しい里山に囲まれた人々の営みを季節の移ろいとともに追いかけ、ものづくりの意味や大切さを見つめ直していく。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関東テキヤ一家 天王寺の決斗", "関東テキヤ一家 天王寺の決斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "関西の四天王寺を縄張りに、露店商から絶大な人気を集めている三輪会。その三輪会の帳元に菊水会の国分が惚れられる。いずれは一人娘の婿にと、えらい惚れこみよう。暴力団あがりの新興勢力と三輪会と対立する組が共謀、三輪会の縄張りを横取りしようと企んで……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春無銭旅行", "青春無銭旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "1950年、成瀬巳喜男監督によってシリーズ第１作が撮られた、石坂洋次郎原作『石中先生行状記』ものの第４作。東北の田舎町に住む石中先生が来訪した二人の女学生の求めに応じて、中学生時代にした無銭旅行の話をするところから映画は始まる。以後は、中学時代の石中君とその友人によるふんだりけったりの旅行記。石中先生役は第１作以来の宮田重雄、その中学時代の友人の役は和田孝。中川信夫の数少ない現代劇の一つ。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Freud", "フロイド／隠された欲望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "　パリ留学から帰国したフロイドは精神病治療に催眠術を用いることを主張。しかしウィーンの学会はこれに反対した。そんな中、ブロイヤー博士だけが耳を傾け、ヒステリー患者の治療に当たらせた。やがてフロイドは学会で幼児さえ性欲があるという説を唱えるが、大きな反響を巻き起こすことに……。天才科学者の若き日を描く伝記ドラマ。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TORA！ TORA！ TORA！", "トラ・トラ・トラ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "1941年12月８日。太平洋戦争の口火を切った日本軍の真珠湾攻撃の全容を、日米スタッフの協力のもとに再現した70ミリ大作。急襲までの日米間の様々な情報合戦が、ドラマティックに描かれていて興味深い。クライマックスの両国の戦闘場面も迫力満点。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE 6TH MAN", "ゴースト・ブラザー　天国から来たヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「モー・マネー」のM・ウェイアンズ主演のファンタジック・コメディ。ワシントン州立大学バスケ・チームのアントンとケニーは仲のいい兄弟。チームはスーパースターのアントンの活躍でNCAAの優勝を目指していたが、彼はゲーム中の心臓発作で死んでしまう。気弱なケニーは落胆を隠せず、チームも連戦連敗。そんなある日、ゴーストとなったアントンが現れ、彼のアシストでチームは再び活気づく……。ゴーストならではの珍プレイを交えながら、兄弟愛とケニーが自立していく姿を描く。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOST WANTED", "クロスゲージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「グリマーマン」のK・I・ウェイアンズが製作総指揮、脚本、主演の３役をこなしたサスペンス・アクション。D・G・ホーガンによる、派手なアクションとユーモアを散りばめたキレのいい演出が光る痛快作。湾岸戦争で誤って上官を射殺したダンは、死刑免除と引き換えに、ケイシー中佐が率いる軍の秘密部隊“ブラック・シープ”のスナイパーとなる。ところが、大統領夫人の暗殺者に仕立て上げられ、命を狙われるハメに。暗殺の瞬間をビデオに収めていた女医ヴィクトリアの協力を得て逃亡する彼は、やがてケイシー中佐の恐るべき陰謀を知る……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JESUS CHRIST SUPERSTAR", "ジーザス・クライスト・スーパースター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "斬新な感覚でキリスト最後の７日間を描き大ヒットした、ブロードウェイのロック・オペラを映画化。キャストとスタッフがバスに乗り、砂漠のロケ現場に到着、彼らがセットを組むところからドラマがはじまる。ワン・エピソード＝ワン・ナンバーで展開する力作。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤毛のアン　グリーンゲーブルズへの道", "赤毛のアン　グリーンゲーブルズへの道", "", "", "http://image.eiga.k-img.com/images/movie/55442/315x210.jpg?2154", 0, "1908年に出版されたカナダの女流作家ルーシー・モード・モンゴメリーの「赤毛のアン」を原作とするTVアニメ「赤毛のアン　グリーンゲーブルズへの道」を、「火垂るの墓」（88）の高畑勲監督が劇場版として再編集。全50話あるテレビシリーズの1～6話をまとめ、孤児院からカスバート家に引き取られてきた少女アンが、プリンスエドワード島の美しい自然のなかで、自分の居場所を見つけていくまでを描く。", "2010-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野性号の航海・　翔べ怪鳥モアのように", "野性号の航海・　翔べ怪鳥モアのように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "角川春樹が設立した「黒潮文化の会」が、南太平洋から日本まで北上する黒潮ベルトが古代日本の文化摂取のルートであったとする説を実証しようとして、1977年５月にルソン島から帆船“野性号 II”で出発。その波乱に富んだ航海を克明に記録したドキュメンタリー映画。ナレーションの台本を片岡義男が担当している。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADAM AT 6 A.M.", "マイケル・ダグラス アダム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "カリフォルニアで何の不自由もない生活を送るエリート青年が、本当の自分を追い求めて、ビバリーヒルズをあとにし、ミズーリの田舎で生活を始める。そこで彼は一人の少女と出会い、恋に落ちるのだった。M・ダグラスにとって主演第２作の人間ドラマ。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("脅迫", "脅迫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "一流サラリーマン・三沢の家に、世界的なガンの権威である坂田博士の孫を誘拐した川西とサブが逃げ込んできた。彼らに身代金の受け取りを命じられた三沢は、恐怖心に身をすくめるが、やがて妻と子を守る使命感に目覚めていく……。深作欣二と宮川一郎のオリジナル脚本を、深作が監督したサスペンス・ドラマ。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男と男の生きる街", "男と男の生きる街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "石原裕次郎が事件記者に扮したサスペンス・ミステリー。岩崎は、一人の画家が殺された事件に何か割り切れないものを感じ、彼の妹を訪問。そこで１枚の写真を見せられ、殉職した父が追っていた事件に今回の事件が関係していることを突き止める。そして真相究明に動き出すのだった……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELVIS ON TOUR", "エルビス・オン・ツアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "1972年４月５日から19日までの約二週間にわたり全米12州15都市で行われた、E・プレスリーの演奏旅行を綴ったドキュメンタリー。バック・ステージでの素顔やデビュー当時のフィルムなども挿入され、素顔のエルヴィスをリアルに描き出している。「エルビス・オン・ステージ」とともに、偉大な歌手であるプレスリーを知るためには必見の作品。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血と骨", "血と骨", "", "", "http://image.eiga.k-img.com/images/movie/1332/315x210.jpg?4144", 144, "自身の父親をモデルに描いた梁石日の同名小説を「月はどっちに出ている」「クイール」の崔洋一監督が映画化。1920年代、一旗揚げようと済州島から大阪にやってきた金俊平は、カマボコ工場を興し、高利貸しとなってたくましく生き抜いていくが、彼の強烈な生きざまは、家族や周囲の人間たちを思わぬ事態に巻き込んでいく。金俊平役にビートたけし、その妻役で鈴木京香、息子と名乗る男役でオダギリジョーらが出演。", "2004-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄", "地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "強姦や近親相姦の罪を犯して、２代にわたって地獄に堕ちていく母娘。彼女たちを導き手にして、血の池、針の山、ケダモノになってうめく死者たちなど、グロテスクな地獄絵巻が展開する。脚本の田中陽造独特の、因果応報の見世物ワールドが卑俗趣味たっぷりに。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("源氏物語・浮舟", "源氏物語・浮舟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "源氏物語『宇治十帖』に登場する浮舟を主人公にした北条秀司の同名戯曲を衣笠貞之助が映画化した歴史大作。常陸の介の妻中将と貴族八の宮との間に生まれた娘、浮舟と薫の君の悲恋を、長谷川＝山本の美男美女コンビで描く。きらびやかな平安の都を再現する豪華な衣裳や装置、絢爛たる色彩が見もの。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サトラレ・TRIBUTE to a SAD GENIUS", "サトラレ・TRIBUTE to a SAD GENIUS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "「踊る大捜査線　THE MOVIE」の本広克行監督が、佐藤マコトの同名コミックを映画化した感動の人間ドラマ。自分の心の思いが、しゃべらなくても周囲の人々に伝わってしまうという特殊能力を持つ“サトラレ”。政府の特能保全委員会の保護により、本人はそのことを知らずに一般社会で過ごしている。新米外科医・里見健一もそんなサトラレの一人だ。ある日彼のもとへ、委員会から特命を受けて精神科医の小松洋子が派遣される。洋子は身分を偽り健一に近づくが、やがて彼の優しさに触れるうちに、忘れていた何かに気づき始める……。異色のキャラクターを演じる、健一役の安藤政信のストイックな繊細さが魅力的。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BOSS’S　 WIFE", "ブロンドはお好き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ジョエルは大手証券会社のマジメなサラリーマン。彼は、会長に気に入られ、高級避暑地にある別荘に招かれる。そこで出会ったブロンド美人が会長のワイフ。ボスの奥様との浮気騒動をスリリングに描いたすれ違いコメディ。ビデオ発売時タイトルは「ボス'S ワイフ　ブロンドはお好き」。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TERMINATOR", "ターミネーター", "", "", "http://ecx.images-amazon.com/images/I/51pVIuoTG1L._SL210_.jpg", 109, "2029年から1984年のロサンゼルスへやって来た恐るべき殺人マシーン、ターミネーター。彼は、機械社会で反逆を企てる人間側のリーダー、ジョン・コナーの母サラを抹殺せよという特命を受けていた。一方ジョンを敬愛するリースも、その暗殺計画を阻止しようと、ターミネーターを追って現代へ。サラを見つけ出したリースは、ターミネーターと壮絶な闘いを繰り広げるが……。シュワルツェネッガーが、執拗に獲物を狙う不死身で冷酷無比なターミネーターをものの見事に演じ切ったヒット作。機械人間の正体を描くSFXシーンやバイオレンス・アクションが衝撃的で圧倒的支持を得た。アヴォリアッツ国際ファンタスティック映画祭グランプリ受賞。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("波止場の鷹", "波止場の鷹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "佳作「夜霧よ今夜も有難う」に続く裕次郎とルリ子のコンビによるムード・アクション。横浜を舞台に、主人公の経営する会社を悪玉が麻薬の密輸に利用しようとするが、主人公は真っ向から立ち向かう。ムード・アクションの事実上の最終作となった。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃太郎 海の神兵", "桃太郎 海の神兵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "国産アニメーションを開拓した政岡憲三に師事した瀬尾光世が、政岡のいる松竹動画研究所に入社後、当時の海軍省の依頼を受けて製作した作品。戦時下の悪化する状況の中で、約50名のスタッフと劇映画なみの製作費、さらに１年２ヵ月の時間を費やして、１時間を超す長編を完成させた。内容はスマトラのパレンバンを奇襲攻撃した海軍パラシュート部隊の勝利の宣伝が目的で、桃太郎とその率いる動物たちを日本軍に、鬼ヶ島の鬼たちを敵国軍に見立てて描いている。しかし前半で描かれる田舎の牧歌的な風景、南の島の動物たちと、彼らに歌を教える猿、キジ、犬とののどかな合唱シーンなど、随所にアニメならではのユーモラスな表現が盛り込まれ、国策映画のワクを超えた貴重な収穫となった。ネガが発見されたことで、1984年にやはり政岡憲三の「くもとちゅうりっぷ」（1942年製作）とともに再公開、注目された。ビデオには「くもとちゅうりっぷ」を併録。", "1945-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("葵花劫", "戦場に咲く花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「恋人」が2002年の東京国際映画祭で優秀芸術貢献賞に輝いた中国のジャン・チンミンの監督デビュー作。一人の日本人軍人の死に隠された真実から、人間の尊厳と戦争の意味を問う。日中戦争下の1944年、満州鉄道のとある田舎の駅に戦傷療養のため赴任していた菊地が風呂場で死体となって発見された。すぐさま捜査を始めた日本軍は駅長ら中国人４人を容疑者とする。", "2000-01-01", "8,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宮本武蔵", "宮本武蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 147, "戦国時代の剣豪・宮本武蔵を描く吉川英治文学の映画化。本作品の前にも何度も映画化されたが、加藤泰監督版は青春チャンバラ活劇といった趣が強い。第１部・関ヶ原の戦いに生き残った武蔵は許嫁のお通と別れて剣の道を進み、吉岡道場の面々を次々に打ち倒す。第２部・宿敵・佐々木小次郎と出会った武蔵は、剣の迷いを沢庵和尚のもとで断ち切り、決戦の巌流島へと向かっていく。青春から成熟へと移っていく武蔵の姿を見事に捉えている。加藤泰特有のローアングル・ショットや、どしゃ降りの雨のシーンなどが作品に陰影を与えている。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロビンソンの庭", "ロビンソンの庭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "前作の16ミリ自主映画「闇のカーニバル」がベルリンで超ロングランを続けるなど、日本のインディーズの代表選手として欧米で評価の高い山本政志の35ミリデビュー作。様々な国から寄り集まった人々の吹きだまりのような外人ハウス。そこに住むクミはドラッグを売ることで日銭を稼いでいる。そんなある日、緑の生い茂る不思議な廃墟に迷い込んだクミの眼前に忽然と現れた“ロビンソンの庭”。ここに移り住んだ彼女は畑を耕し始める……。主演の太田久美子の圧倒的な存在感。「ストレンジャー・ザン・パラダイス」の撮影監督トム・ディチッロが、東京の倦怠感と緑の神秘性を見事に捉えている。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALIA DILA ALLETY", "王者のためのアリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "世界的なプロレスラー、グラレヴィッチの栄光の軌跡を描いた作品。幼年時代のサーカス一座での日々、あるいは宿敵アプス兄弟との対決など、回想によって過去の戦いがよみがえっていく……。“鉄のズビシッコ”と呼ばれた、プロレスラーをモデルにした話を映画化。ラストの、アプス兄弟との流血の対決が見もの。", "1979-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("好雨時節", "きみに微笑む雨", "", "", "http://image.eiga.k-img.com/images/movie/54802/315x210.jpg?3339", 100, "2008年5月に起きた四川大地震から1年後の成都を舞台にしたラブストーリー。主演は「私の頭の中の消しゴム」のチョン・ウソン、「大秦帝国　QIN EMPIRE」のカオ・ユアンユアン。「八月のクリスマス」「四月の雪」のホ・ジノが監督を務める。建設会社社員のドンハは出張で成都を訪れ、そこで観光ガイドとして働くアメリカ留学時代の友人メイと再会する。当時お互いに惹かれあっていた2人は、再び恋に落ちるが……。", "2009-11-14", "5,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("迅雷 組長の身代金", "迅雷 組長の身代金", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ヤクザ相手に身代金誘拐を成功させた友永、稲垣、ケンの３人組は、一度きりの約束だったが、味をしめた稲垣の誘いに乗り２度目の誘拐に挑戦。しかも今度はヤクザの組長を誘拐し、成功したかのようにみえたが、気の進まなかった友永の予感が的中。ヤクザに追われるはめになり、ケンがヤクザに捕らわれてしまう。高橋監督が、大阪を舞台にヤクザ相手の無謀な誘拐劇を描く。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ADDAMS FAMILY", "アダムス・ファミリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アメリカでは漫画やTVアニメで人気の『アダムスのお化け一家』を実写映画化したヒット作。C・ロイドやA・ヒューストンらクセ者ぞろいのキャストが何といっても見もの。おばけのアダムス一家は彼らなりに平和な日々を送っていたが、アダムス家の莫大な財産を乗っ取ろうと悪徳弁護士が現れて……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT’S ALIVE！", "悪魔の赤ちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "フランクとレノア夫婦の間にできた二人目の子供。だがこの赤ん坊はこの世のものとも思えないグロテスクな姿で、医師や看護婦を惨殺して姿を消した。この日以来、L.A.では次々と事件が起こる……。薬物公害などによる奇形児の出産をめぐる異色ホラー・ムービー。リック・ベイカーが特殊メイクを担当。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キズモモ。", "キズモモ。", "", "", "http://image.eiga.k-img.com/images/movie/53772/315x210.jpg?2879", 0, "気ままな生活を送る自由奔放なフリーターのアキ（馬場）は、いつものようにふらっと旅に出る。旅の途中に偶然訪れた時計屋で、時計職人・弓矢夫妻と幼なじみにそっくりな将也（古川）に出会い、理由あってアキは“時計師”を目指して修行中の将也と共に弓矢家に居候することになる。性格が正反対なため将也としばしば衝突するが、将也はアキの抱える心の傷を知り……。主演に「カフェ代官山」の馬場徹、舞台「テニスの王子様」の古川雄大。", "2008-09-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("END OF THE LINE", "ハロー・マイ・トレイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "鉄道員たちが繰り広げる、ほのぼのとした社会派コメディ。大都市シカゴからのビジネス戦争の余波で、全員解雇されてしまった田舎町の鉄道会社。それに反発する男たちは、鉄道閉鎖を拒み、自分たちの生活と意地を守るため、機関車を奪い、シカゴへと赴こうとする。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Black White + Gray A Portrait of Sam Wagstaff + Robert Mapplethorpe", "メイプルソープとコレクター", "", "", "http://image.eiga.k-img.com/images/movie/54382/315x210.jpg?3749", 73, "HIVにより42歳で他界した天才フォトグラファーのロバート・メイプルソープと、稀代のアートコレクターのサム・ワグスタッフの人生から、1970～80年代のニューヨーク・アートシーンとそのビジネスの裏側に迫るドキュメンタリー。ゲイであることを公言し、性のボーダーを越えた芸術表現で人々を魅了したメイプルソープは、名家出身のアートコレクターのサムと運命的な出会いを果たす。メイプルソープが唯一愛した女性であるパンクロックの女王パティ・スミスも登場。", "2009-03-28", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AVANTI POPOLO", "アバンチ・ポポロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "第三次中東戦争終戦の日。エジプト兵のハレドとカッサンがシナイ砂漠の最前線で目のあたりにする歓喜と苦渋。イスラエル映画が、宗教や民族の厚い壁を超えて、初めて敵側の視点で描いた反戦映画の佳作。タイトルは劇中、敵兵との交流のきっかけを作るイタリアの有名な労働歌からとられている。ロカルノ映画祭で、グランプリとアイザック・スターン賞を受賞。", "1986-01-01", "46", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHILDREN OF THE CORN: THE GATHERING", "アーバン・ハーベスト2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "S・キング原作『トウモロコシ畑の子供たち』の４度目の映画化。ネブラスカ州のある町では、子供たちだけが感染する原因不明の疫病が猛威をふるっていた。生まれ故郷のこの町に帰ってきた看護婦グレースは、妹もこの病気に罹っていることを知る。高熱に冒された子供たちが殺到し、グレースの勤める診療所はパニック状態になる。ところが、ある日を境に疫病は終息し、子供たちは全員快方に向かっていく。しかし、ホッとしたのも束の間、この疫病には恐ろしい後遺症が隠されていた……！　他に、同小説の映画化作品に「チルドレン・オブ・ザ・コーン」（1984）「死の収穫」（1992）アーバン・ハーベスト」（1994）がある。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sudden Death", "サドン・デス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　全米が注目するアイスホッケーのリーグ優勝戦に、スタジアムの防火責任者ダレンは離れて暮らす息子と娘を招待する。しかし5万人の客と副大統領も観戦するその会場に、元FBIのフォス率いるテロ集団が潜入。副大統領を人質にとったフォス一味は政府が凍結した預金17億ドルの受け渡しを要求してきた。それに気づいたダレンは一味と戦うことに……。主演バン・ダムと監督ハイアムズが「タイムコップ」に続いて組んだ白熱のサスペンス・アクション。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・女生きてます", "喜劇・女生きてます", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "幼なじみであるヤクザの男と一杯飲み屋を始める女、短大出のインテリであるが女は結婚が何よりという持論を持つ女、美人だけど頭が弱く男の慰み者にされる女など、様々な踊り子たちの哀歓が描き出されている。粗野で泥臭く、スマートな喜劇ではないが、踊り子たちが巻き込まれる事件の数々は、怒りとともに笑いとなって、観る者の心にズシリとした感動を与えてくれる。“立て、飢えたる者よ……”と歌いながら肩をいからせて歩くヒモ（橋本功が好演）の姿が印象深い。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREEJACK", "フリージャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "新進のF−1レーサーがレースで事故を起こし死亡した。ところが彼は、実は21世紀の未来に連れ去られ、未来世界の金持ちの延命のために肉体を狙われていたのだった……。M・ジャガー、A・ホプキンスらの怪演にも助けられ、スピーディーに展開する、B級の香りただようSFアクション。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONFIDENCE", "コンフィデンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「スティング」を彷彿とさせるクライム・サスペンス。気心の知れた仲間を率いて信用詐欺を繰り返す青年ジェイクが、暗黒街の大物や捜査官を出し抜き、500万ドルが懸かった完全犯罪に挑む様を描く。名優D・ホフマンやA・ガルシアらがアクの強い脇役に扮し、最後に誰が笑うのか分からない二転三転のストーリーを盛り立てている。天才詐欺師のジェイクが大金を巻き上げることに成功するが、その金は暗黒街の大物キングの金だった。仲間を殺され、窮地に立たされたジェイクは、キングのために500万ドル強奪計画を画策、一世一代の大勝負に乗り出すが……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAMPOインターナショナル ヴァージョン", "RAMPOインターナショナル ヴァージョン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1994年に黛版、奥山版と２バージョン製作された「RAMPO」のうち、奥山版を奥山和由が再編集し、海外でも上映された作品。奥山版をもとに未発表シーン、新しく撮影したシーンなどを加えたほか、音楽を新たに千住明が担当し、チェコ・フィルハーモニーが演奏しているのが違う点。また、ロバート・デ・ニーロがプロモーションに協力したことも話題になった。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NED KELLY", "太陽の果てに青春を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "罪人の子として生まれ、17歳の時に無実の罪で投獄されて以来、無法者としての道を歩んだ実在の人物ネッド・ケリーの人生を鮮烈に描いた青春映画。大自然の雄大さの中に、社会に受け入れてもらえぬ若者の憤りと悲しみが見事に表現された。主演はM・ジャガーというのも注目。ビデオ発売時タイトルは「ミック・ジャガー／ネッド・ケリー」。", "1970-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もっともあぶない刑事", "もっともあぶない刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "“あぶない刑事”シリーズの第３作。シリーズ当初からの宿敵だった銀星会会長・前尾を相手に上司の命令も顧みず、あぶない捜査を展開していくタカとユージの活躍が描かれている。毎度おなじみのCMギャグなど、軽いノリは本作でも好調。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAPTAIN KRONOS：VAMPIRE HUNTER", "吸血鬼ハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1947年設立されたホラー映画の老舗ハマー・フィルムもやがて陰りが見え始める。本作品は、ハマーが起死回生の策として、趣向を凝らし製作されたホラー作品。吸血鬼の弱点の一つ、“眠っている時、心臓に杭を打つ”という定説を履した解釈が珍しい。また、主人公が剣の名手で、ヒーロー活劇とホラーをプラスした娯楽作に仕上げている。", "1973-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE SHOP OF HORRORS", "リトルショップ・オブ・ホラーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ロジャー・コーマン作品の、というより同名ヒット・ミュージカルをベースに映像化。さえない花屋の店員が、中華街で手に入れた奇妙な植物。何とそれは人の血を吸って増殖していく吸血植物だった。“人を喰った”設定に、華麗な（？）歌と踊りが加わった、てんやわんやの爆笑ホラー・コメディ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONGO", "コンゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ベストセラー作家M・クライトンの同名小説を映画化したサバイバル・アドベンチャー。文明から隔絶されたアフリカの密林に送り込まれた調査隊の想像を絶する体験を描く。ダイヤモンドを使用したレーザー光線で、レーザー産業の独占を目論む通信会社トラビコム社から、ダイヤ鉱山発掘のために８人の遠征チームが中央アフリカのコンゴに送り込まれた。だが、鉱山を発見したニュースとともに通信機器が破壊される映像が映し出され、連絡が途絶えてしまう。調査隊が向かうと、そこには正体不明の怪物の姿が……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAZZFIL ラジフィル", "RAZZFIL ラジフィル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "舞台を中心に活躍する俳優の村田賢嗣と中西広和が初メガホンを取り競作したオムニバス作品。田舎町で暮らす平凡な青年に奇妙な出来事が次々と降りかかり、なぜか謎の組織「トランポップリング」から追われるハメになってしまう村田監督作「トランポップリング」、銀座のバーを舞台に、飲んだ者の人間性まで変えてしまうほどの影響力を持つカクテルをつくる伝説のバーテンダーを描いた中西監督作「ミルキーコスモ」の2作で構成。", "2005-10-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MICRO COP", "ミクロコップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "銀河の彼方からやって来た、宇宙最強にして最小の刑事を描くSFアクション。高度に文明が発達したある惑星から、凶悪犯を追って地球に不時着した刑事がいた。彼の身長は地球上ではたったの30センチしかなかった。しかも、不時着した場所は犯罪多発地帯のブロンクス。何もかもが巨大な惑星で、宇宙最強の刑事が悪に立ち向かう。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Blair Witch Project", "ブレア・ウィッチ・プロジェクト", "", "", "http://image.eiga.k-img.com/images/movie/1792/315x210.jpg?2461", 81, "モンゴメリー・カレッジの生徒、ヘザー、ジョシュ、マイク。3人は大学の課題のため、バーキッツヴィルに残る伝説“ブレア・ウィッチ（ブレアの魔女）”についてのドキュメンタリー・フィルムの撮影を始める。共同墓地で最初のシーンを撮り終えた3人は、地元の人々にインタビューを開始。彼らは、程度の差こそあれブレア・ウィッチについて聞いたことがあった。翌日、インタビューを終えた3人はブラック・ヒルズの森に入っていく。", "1999-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Memento", "メメント", "", "", "http://image.eiga.k-img.com/images/movie/1532/315x210.jpg?4078", 113, "強盗犯に襲われて妻を失い、頭部を損傷し、約10分間しか記憶を保てない前向性健忘という記憶障害になったレナード。彼は、ポラロイド写真にメモを書き、体中にタトゥーを彫って記憶を繋ぎ止めながら、犯人を追う。実在するこの障害を持つ男を主人公に、時間を遡りながら出来事を描くという大胆な構成が話題を呼び、全米でインディペンデントでは異例のヒットを記録。監督は本作が第2作の新鋭、クリストファー・ノーラン。", "2001-11-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Big Trouble in Little China", "ゴースト・ハンターズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "　サンフランシスコ、チャイナタウンへやってきたトラック運転手のジャックは友人ワンの婚約者ミャオを迎えに行くが、彼女はその途中で怪しい集団に誘拐されてしまう。2000歳を越える怪人ロー・パンがミャオをいけにえにしようとしていることを知ったジャックはワンや仲間たちとともに敵のアジトに乗り込む。しかし彼らの前には、妖怪や魔術の使い手が待ち受けていた！　カンフー・アクションを取り入れた、コミカルな魔界アドベンチャー。カート・ラッセルがドジ連発の主人公を演じて笑わせる。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAFFLES", "犯人は誰だ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "E・W・ホーナングの『紳士泥棒』の４度目の映画化。1930年代のロンドンを舞台に、社交界で気楽に生活し、趣味として宝石泥棒をやっている男ラッフルズとその恋人、そして警部の巻き起こす珍騒動を描いたシャレたコメディ。日本では公開が大幅に遅れて評判にならなかったが、S・ウッドらしい気品ある作品。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EAST MEETS WEST", "EAST MEETS WEST", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "岡本喜八監督がアメリカ西部を舞台に描いた痛快アクション・コメディ。1860年、開国を決意した幕府は、サンフランシスコに日米修好使節団の護衛として咸臨丸を送りこむ。そこには開国を阻止するためにアメリカにやってきた刺客の健吉と、艦長護衛役の忍者の為次郎もいた。しかし船が着くやいなや、一行の秘密資金３千両が何者かに奪われてしまい、本来なら敵同士の健吉と為次郎がその行方を追うことになり……。ニューメキシコ州サンタフェでロケ撮影を行うなど、岡本監督の熱意が感じられる快作。DVDは「岡本喜八・邂逅編／STRANGERS　DVD-BOX」に収録。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kabhi Khushi Kabhie Gham...", "家族の四季　愛すれど遠く離れて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 210, "アミターブ・バッチャン、シャー・ルク・カーンら、インドを代表する豪華キャストが集結した家族ドラマ。大富豪のラーイチャンドは、養子ながら愛情をかけて育ててきた長男ラーフルに縁談を持ちかけるが、ラーフルは庶民の娘アンジャリーと恋に落ちてしまう。アンジャリーとの恋を反対されたラーフルは、愛する家族に別れを告げてアンジャリーと生きることを決意。数年後、ラーフルの弟ローハンは家族の絆（きずな）を取りもどすため、兄が暮らすイギリスへと向かう。", "2008-08-30", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野良猫ロック 暴走集団'71", "野良猫ロック 暴走集団'71", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "新宿をネグラとするフーテンたちのもとに、ある日、黒ずくめの親衛隊が現れ、仲間の一人、隆明を連れ去る。親衛隊は地方の町のボス・荒木の配下で、荒木の息子である隆明を連れ戻しに来たのだった。梶芽衣子扮するヒロイン、振り子は、彼らとのもみ合いで隆明が親衛隊の一人を刺した罪をかぶって鑑別所に入るが、仲間と脱獄。彼らは荒木邸の近くにアジトを作るが、住民たちは彼らを排斥しようとする。やがてそれは町ぐるみの銃撃戦へと発展。遊戯にも似た若者たちの自由への希求と反抗、ジェネレーション・ギャップと父子の愛憎を、悲喜劇タッチで描く。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WRONGFULLY ACCUSED", "裸の銃〈ガン〉を持つ逃亡者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「裸の銃を持つ男」でおなじみのL・ニールセンが主演したコメディ。テロ事件に遭遇し、殺人の容疑者に仕立てられた天才バイオリニスト、ライアン。逃亡生活を強いられた彼は周囲を混乱に陥れつつも、事件の真相に迫っていく。「逃亡者」をベースに「タイタニック」「ミッション：インポッシブル」など、ヒット作のパロディーを満載。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("下落合焼とりムービー", "下落合焼とりムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "赤塚不二夫、タモリ、高平哲郎など、大物ギャグマンがノリにノリ、お笑い界の異才タレント総出演で撮り上げた、和製「ケンタッキー・フライド・ムービー」。喜劇センスに定評のあるポルノ畑の山本晋也監督を得て、全編パロディとナンセンス・ギャグで押しまくる。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("肩ごしの恋人", "肩ごしの恋人", "", "", "http://image.eiga.k-img.com/images/movie/53082/315x210.jpg?1870", 101, "日本でもドラマ化された唯川恵の同名小説を、韓国の女性監督イ・オンヒが映画化。シングルライフを楽しむカメラマンのジョンワンと、実業家と結婚してリッチな生活を送るヒスは親友同士。ヒスは既婚男性と不倫中のジョンワンを応援していたが、ある日自分の夫の浮気が発覚する。その一方でジョンワンは次第に不倫にのめり込んでいき……。2人のヒロインを「純愛中毒」のイ・ミヨンと、韓流ドラマ「噂のチル姫」でブレイクしたイ・テランが演じる。", "2007-11-23", "76,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子供の四季・春夏の巻／秋冬の巻", "子供の四季・春夏の巻／秋冬の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "「風の中の子供」に続く坪田譲治の“善太と三平”ものの映画化作品。監督は前作同様に天才・清水宏である。主人公の善太・三平の兄弟とその家族は、役名はもちろん配役まで前作と同じ。「風の中の子供」は夏休みの間だけの話であったが、本作品は題名の示す通り２部から成る１年間の物語である。前編では、仲間の金太郎の父に借金をした病気の父親を助けるため、善太と三平がけなげに働くが、そのかいもなく父が死に至るまで。後編では祖父母のもとに引きとられた善太・三平と彼らの仲間・金太郎の友情が描かれる。主人公の兄弟を演ずる葉山正雄と爆弾小僧が素晴らしく、また詩情を漂わせた撮影も美しい佳編。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTERNATIONAL VELVET", "インターナショナル・ベルベット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "エリザベス・テイラーの1944年の主演作「緑園の天使」の後日譚。前作から30数年後。かの映画のヒロイン、ベルベットのもとに姪のサーラがやって来る。最初は反抗的で手のつけられないサーラだったが、往年の名馬パイを知り、乗馬でオリンピック出場を目指す。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版 DIR EN GREY UROBOROS 10", "劇場版 DIR EN GREY UROBOROS 10", "", "", "http://image.eiga.k-img.com/images/movie/55482/315x210.jpg?2697", 0, "人気ロックバンド「DIR EN GREY」が、自他ともに最高傑作と認めるアルバム「UROBOROS（ウロボロス）」のリリースを記念して行ったワールドツアー最終公演を記録した音楽ドキュメンタリー。1997年のバンド結成から13年、日本のみならず欧米でも人気を集める同バンドは、世界15カ国82都市をツアーでまわり、その締めくくりとして2日間の武道館ライブを敢行。伝説となったライブのほか、舞台裏やメンバー5人の素顔に迫る。", "2010-05-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("December Boys", "ディセンバー・ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/53092/315x210.jpg?2127", 105, "「ハリー・ポッター」シリーズのダニエル・ラドクリフ主演による青春ドラマ。1960年代のオーストラリア。孤児院で育てられたマップス、スパーク、ミスティー、スピットの4人は、いつか優しい家族に引き取られることを夢見ていた。12月の夏休みを海辺の村で過ごすことを許された4人は、そこで子供のいない夫婦と親しくなる。少年たちは夫婦が4人の内の1人を養子に迎えようと考えていることを知り、彼らの気を引こうと躍起になるが……。", "2007-12-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEVIE", "スティーヴィー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "'03年の山形国際ドキュメンタリー映画祭で最優秀賞に選ばれたほか、各国の映画祭で話題を呼んだ長編ドキュメント。非行を繰り返す青年スティーヴィーの心の闇に迫り、家族の在り方や社会環境について改めて考えさせられる1本だ。監督を務めたスティーヴ・ジェイムスはアメリカの\"ビッグ・ブラザー\"制度で、少年時代のスティーヴィーの更正に関わった。ところが約10年ぶりに逢ったスティーヴィーの状況はさらに悪化している。そんな彼の半生を紐解いて見えてくるのは、無残な幼少時の体験。イリノイ州で暮らすスティーヴィーは幼い頃から母親に虐待を受け続けていたのだ。心に傷を負った彼は少年時代から問題ばかり起こし、ついに、重大な犯罪の容疑で逮捕される。", "2006-02-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GORGON", "妖女ゴーゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ギリシャ神話の怪物ゴーゴンを、20世紀初頭のドイツの片田舎に出現させた怪奇映画。医学研究所の美人の助手にゴーゴンの悪霊が乗り移っていることを見抜いた教授は、石にされかけた医師を助けてゴーゴンの首を切る。ハマー・プロの怪奇シリーズの一編。", "1963-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Daytime Drinking", "昼間から呑む", "", "", "http://image.eiga.k-img.com/images/movie/55992/315x210.jpg?1626", 116, "恋人と破局したヒュクジンは、飲み屋で友だちと酒盛りをした勢いで、チョンソンという小さな町へ出かける約束をする。しかし翌日、約束した場所にたどり着いたのは自分だけ。ひとりで旅することになったヒュクジンは、なぜか道中で次々と酒盛りにつきあわされ、連日二日酔いの旅が続く。本作で長編デビューのノ・ヨンソク監督が、撮影、編集、音楽など出演以外のほぼすべてをこなし、製作費約1000万ウォン（約100万円）という低予算で完成させたコメディ。", "2011-05-07", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魚からダイオキシン!!", "魚からダイオキシン!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "東京の知事選に敗れたロックンローラーがニューヨークに渡り、クルド人のミュージシャンと出会う。彼らに深く感銘したロッカーは、日本でクルド人難民救済コンサートを開こうとプロモーターに持ちかけるが、相手にされず、彼はついに音楽事務所に銃を持って乗り込む……。内田裕也脚本、主演による意欲作。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イコン伝説 追憶のエルミタージュ", "イコン伝説 追憶のエルミタージュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "日本最初の女流画家であり、絵の勉強のためにロシアに渡った山下りん。天才画家でもなく、歴史に名を残すような業績も残していないが、自分の道をしっかり歩んだ彼女の激動の生涯を描いた作品。ドキュメンタリー畑で活躍をした石侍露堂の劇映画第１作。1881年３月、聖イサク寺院で山下りんはイコンを見つめていた。彼女はイコン画を学ぶために、ロシアまで一人で留学しにきたのだったが……。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Austin Powers in Goldmember", "オースティン・パワーズ　ゴールドメンバー", "", "", "http://image.eiga.k-img.com/images/movie/1072/315x210.jpg?3983", 109, "舞台は2002年。スーパースパイである父がイーブルの差し金によって誘拐されてしまう。愛する父を救うべく75年へタイムスリップしたオースティンは、当時の公私に渡るパートナー、フォクシー・クレオパトラと共にイーブルの協力者、ゴールドメンバーに立ち向かう。ヒロイン役に、デスティニーズ・チャイルドのビヨンセ。トム・クルーズ、グウィネス・パルトロウ、スティーブン・スピルバーグをはじめ、超豪華なカメオ出演陣にも注目。", "2002-08-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・一発大必勝", "喜劇・一発大必勝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「馬鹿まるだし」(1964)、「いいかげん馬鹿」(1964)などで息の合ったところを見せた、山田洋次監督＝ハナ肇主演による一編。瀬戸内海のある町の長屋にボルネオ帰りの男がやって来て大騒動を巻き起こすという、山田流スラップスティック・コメディ。クレージー・キャッツの面々が脇を固めている。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FILLE DE L’AIR", "ブリジット 女が男を奪うとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1986年５月に実際に起こった事件に基づいた犯罪アクション。服役中の夫をとり戻すため、妻のブリジットは自らヘリコプターを操縦し、脱獄させるという大胆な策を実行する。「ベティ・ブルー 愛と激情の日々」で女の情念を官能的に演じたB・ダルが、ここでは執念に燃える妻を演じる。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAT★21", "バット★21", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "舞台は激戦の続くベトナム。敵陣の真っ只中に撃墜されたアメリカ空軍機に乗っていたのは“バット・21”のコードネームを持つ中佐だった。彼はからくも脱出するのだが、空軍の救難チームの助けを受けることはできるのか……。中佐役のG・ハックマンが渋い演技を披露してくれる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PUBLIC ACCESS", "パブリック・アクセス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「ユージュアル・サスペクツ」で注目を集めた新鋭、B・シンガーのサスペンスフルな監督デビュー作。米国のとある平穏な町にやって来た一人の男。彼は地元のケーブルテレビに、町の向上を図る“私たちの町”という市民参加番組を開設、たちまち人気司会者として好評を得る。ところが、彼によって町の問題点が次々と露呈され、人々は混乱に陥っていく。そんなある日、殺人事件が発生する……。男の目的は何なのか、という不安の中で展開するドラマの混沌とした雰囲気が恐怖感をあおる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RISE AND FALL OF THE THIRD REIGH", "第三帝国の興亡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "W・L・シャイラーの著書『第三帝国の興亡』に基づき、製作されたドキュメンタリー。全世界制覇の野望に燃えたヒトラーの政権奪取に始まる、ナチス・ドイツの興亡の歴史を数々の記録フィルムを通して浮きぼりにしていく。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Letters to God", "かみさまへのてがみ", "", "", "http://ecx.images-amazon.com/images/I/51Qmbnn5%2B-L._SL210_.jpg", 110, "実話を基にした人間ドラマ。8歳ながら脳のガンと闘病中の少年タイラーは、神様への手紙を書き始める。一方、その神様宛の手紙を受け取るたびに困惑していた郵便配達員のブレイディは酒浸りで離婚した妻との間の子供の養育権を失っていた。どうしていいか分からないブレイディは教会に相談しにいくと、神父から「あなたがその手紙を受け取っているのは何らかの理由があるはずだ」と諭され、積極的にタイラーの家族と関わりを持つようになる。監督はこれまで多くのドキュメンタリーを手掛けてきたデビッド・ニクソン。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おっぱいバレー", "おっぱいバレー", "", "", "http://image.eiga.k-img.com/images/movie/53932/315x210.jpg?3081", 106, "1979年、北九州市。中学校の弱小男子バレー部の顧問になった新任教師の美香子は、やる気のない部員たちに「試合に勝ったらおっぱいを見せる」という約束をさせられてしまう。張り切った部員たちは練習に打ち込み、実力をつけていくが、試合には勝ってほしいものの、おっぱいは見せたくない美香子は思い悩み……。実話をもとに描かれた同名小説を、「海猿」の羽住英一郎監督が映画化した青春コメディ。主演の美香子役に綾瀬はるか。", "2009-04-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プロゴルファー織部金次郎２ パーでいいんだ", "プロゴルファー織部金次郎２ パーでいいんだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "武田鉄矢が原作・脚本・主演に加えて初の監督業にも乗り出した、人情喜劇第２作。体力の限界を感じて、現役続行と引退の間で苦悩するプロゴルファー“織金”。そんな彼がシニアのゴルファーのレッスンを引き受けたことから、下町の人々の声援にも支えられ、再びやる気を取り戻していく。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIG！", "DIG！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "2004年のサンダンス映画祭ドキュメンタリー部門審査員グランプリを受賞した音楽ドキュメンタリー。“ブライアン・ジョーンズタウン・マサカー”と“ダンディ・ウォーホルズ”という米インディーズシーンの二つのロックバンドを見つめる。内部事情や人間ドラマが赤裸々にとらえられ、音楽ファンならずとも楽しめる。友人であり、ライバルでもある二つのロックバンド。一つはメジャーと契約するが、もう一つは商業主義を断固として拒絶し、破滅的な性格のボーカルは彼らと訣別してしまう。", "2006-04-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使のたまご", "天使のたまご", "", "", "http://ecx.images-amazon.com/images/I/31OIRLTOd4L._SL210_.jpg", 75, "押井守監督がアートデュレクターに天野喜孝を迎えて描いたファンタジー作品。大きなたまごを抱えた少女は、いつかそのたまごから天使がかえると信じ、廃墟の町できれいなガラスのビンを集めながら暮らしていた。ある日、少女はどこからともなくやってきた大きな銃を抱えた少年と出会い、2人は行動を共にする。", "1985-12-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAR OF THE COLOSSAL BEAST", "巨人獣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "巨大モンスターものの傑作「戦慄！プルトニウム人間」の続編。前作のラストで行方不明になった巨人がメキシコで見つかる。今の彼は、理性のかけらもない“巨人獣”にすぎなかった。巨人を止める術はあるのだろうか……。DVDは「アーコフ・ライブラリー 怪奇モンスターズ・セレクション(2)」に収録。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　NEW　ONE−ARMED　SWORDSMAN", "新・片腕必殺剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "前２作よりバイオレンス度合いと活劇のスケールがパワー・アップした香港製武侠シリーズ第３作。クライマックスとなる100人斬りシーンなど怒濤のアクションが繰り広げられ、クエンティン・タランティーノ監督をはじめ欧米の映像作家にも多大な影響を与えた。主人公の剣士は英雄剣士との一騎打ちに破れ、自ら腕を切り剣を捨ててしまう。落ちぶれていく彼だが、ある武侠家との出会いが運命を変える。", "1971-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOT’N COOL", "Mr.レディの私の男を女にして", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "スターになれるとだまされてアメリカへやって来たイタリアのコメディアン、トムとジェリー。事件を起こして女装して逃げ出すが、彼女（？）が地元のボスに惚れられてしまい……という設定は「お熱いのがお好き」と同じ。かの名作とドタバタをミックスさせた、いかにもイタリア映画らしい爽快コメディ。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("在那河畔青草青", "川の流れに草は青々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "田舎の小学校に、青年教師のターニェンが赴任してくる。生徒たちとのふれあい、自然破壊をする村人への怒り、子供の家出や恋人との悶着など、日々の出来事を通して、ターニェンはやがて村の風景の一部になっていく……。ターニェンを演じるのは当時のアイドル歌手ケニー・ビー。ホウ・シャオシエンは監督デビューからこの作品まで低予算のアイドル映画を手がけたが、ロケ地の田園風景と子供たちとの交流の中で、人物が自然に溶け込む彼独特の作風が生まれた。", "1982-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沖縄健児隊", "沖縄健児隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "太平洋戦争末期の昭和20年、アメリカ軍の沖縄上陸に際し、日本軍司令官直属隊として徴集された沖縄の学生たちの悲劇を通して、戦争の悲惨さを訴えた戦争映画。国の命令で無理やりペンを剣に持ち替えさせられ、痛ましく玉砕していった若者たちの過酷な青春と、平和への祈りがつづられていく。石浜朗ら当時の松竹青春スターが総出演。音楽は名匠・黛敏郎が担当している。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jane Eyre", "ジェーン・エア", "", "", "http://ecx.images-amazon.com/images/I/5160XVL24xL._SL210_.jpg", 96, "イギリスの女性作家シャーロット・ブロンテによる名作小説の映画化作品。幼い頃に両親を亡くし、冷酷な叔母に引き取られた少女ジェーン・エア。やがて彼女は孤児院に入れられ、つらい毎日を送ることになる。だがそんな逆境にもめげず、彼女は真っ直ぐにたくましく育っていった。ある日ロチェスター家の家庭教師として雇われた彼女は、そこの主人に恋心を抱くが……。ジェーンの友人役で子役時代のエリザベス・テイラーが出演している。", "1947-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真夜中の少女たち", "真夜中の少女たち", "", "", "http://image.eiga.k-img.com/images/movie/33602/315x210.jpg?2435", 122, "「神の左手、悪魔の右手」の渋谷飛鳥や「蝉しぐれ」の佐津川愛美ほか注目の新進若手女優たちを主演に迎えた、4話からなるオムニバス形式の青春ドラマ。「ベロニカは死ぬことにした」の堀江慶が総合監修を務める。東京近郊の高校に通う5人の少女たち。夜のシブヤでホームレスと出会ったミオ、親友の幸に隠しごとをしてしまった晶、先生を好きになった佳織……。それぞれの悩みを抱えて揺れ動く女子高生たちの日常をリアルに描く。", "2006-08-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNA GIORNATA PARTICOLARE", "特別な一日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ムッソリーニ政権下のローマ。アパートの人間がファシスト集会に出かけたあと、一人残った主婦が、偶然、知り合った反ファシストの隠遁者の男と一日限りの恋をする……。二大スターの迫真の演技が見もの。スコラ監督の心理描写の演出が冴える力作。", "1977-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Deux de la Vague", "ふたりのヌーヴェルヴァーグ　ゴダールとトリュフォー", "", "", "http://image.eiga.k-img.com/images/movie/55202/315x210.jpg?1684", 97, "ヌーベルバーグを代表する監督として敬愛されるフランソワ・トリュフォーとジャン＝リュック・ゴダールに迫ったドキュメンタリー。「大人は判ってくれない」のカメラテストを受けるジャン＝ピエール・レオや演出の細部について語るトリュフォー、商業映画の世界から身を引いていた時期のゴダールの姿など、貴重な資料映像の数々で構成される。トリュフォーやゴダールの評伝本を執筆した「カイエ・デュ・シネマ」元編集長のアントワーヌ・ド・ベックが構成を担当。", "2011-07-30", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("漫☆画太郎SHOWババアゾーン（他）", "漫☆画太郎SHOWババアゾーン（他）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "『地獄甲子園』の原作者・漫☆画太郎のコミックを映像化した、５話から成るオムニバス。遠足中の交通事故を生き延びた、サエない女生徒のサバイバルを、ホラー映画のパロディーとして描いた「たのしい遠足」をはじめ、ナンセンスなギャグが詰まった脱力エピソードがズラリ。画太郎ワールドにマッチしたキャスティングの妙も見逃せない。エロ本を買いたいのに、気が弱くてどうしても買えない弱虫な15歳、エロ本買蔵の前に、雷鳴とともに謎のババアが現れた。買蔵の“気が大きくなりたい”という望みを叶えようと、ババアは“肝っ玉が大きくなる薬”を与えるのだが……。（「ババアゾーン」）", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Just Wright", "恋のスラムダンク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "「TAXI NY」のクィーン・ラティファ主演によるラブコメディ。スポーツ選手専門の理学療法士レスリー・ライト（ラティファ）は花形NBAプレイヤーのスコット・マクナイトが今後のキャリアを左右する大怪我をしたことにより、親密になり、2人は恋に落ちる。だが、レスリーの幼なじみのモーガンもスコットとの結婚を望んでいた……。共演にポーラ・パットン、コモン。監督は「旅するジーンズと19歳の旅立ち」のサナー・ハムリ。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無頼・人斬り五郎", "無頼・人斬り五郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "藤田五郎は、刑務所の中で獄死した弟分の林田の姉を探して林田の故郷を訪ねると、彼女が赤線の女になっていることを知る。地元の暴力団・名振会は、五郎を煙たがり、刺客をさしむけるが……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TREAT", "愛のトリートメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "架空の街ノーブルスヴィルの売春宿で働く娼婦トリオの幸福探しを、おかしな性癖を持つ珍客たちとのトラブルを通して綴るオフビート・コメディ。有名な劇作家である母親P・ジェムズが書いたヒット戯曲を基に、カラフルでキッチュな映像世界を作り上げた新人監督J・ジェムズは「マーズ・アタック！」の脚本を担当した才人。悪女にも天使にも見える人気女優J・デルピーが、惜しみなく衣装チェンジを連発するコスプレ演技もファンにはたまらない。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女子学園 悪い遊び", "女子学園 悪い遊び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "三年生の花子を中心とした白バラ学園中等部を仕切る“ズベ公グループ”の一人・利江が妊娠。彼女の手術代を集めるためズベ公グループと田辺涼子はタカリ、サギ、カッパライとあらゆる手段に出る。夏純子が大胆な演技を披露、記念すべきシリーズの第１作を飾った。一貫して“行動派”の女学生像を描く。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲキ×シネ「朧の森に棲む鬼」", "ゲキ×シネ「朧の森に棲む鬼」", "", "", "http://image.eiga.k-img.com/images/movie/34232/315x210.jpg?2990", 177, "演劇の舞台をスクリーンで上映する新しいエンターテインメント“ゲキ×シネ”の第5弾として、劇団☆新感線が市川染五郎を主演に迎えた舞台「朧の森に棲む鬼」を映像化。いにしえの神々が棲むという神秘の森。野心に満ちた男ライは、森の魔物“オボロ”に取引を持ちかけられる。それは、ライの命と引換えに王の座を約束するというものだった。取引に応じたライは、オボロに与えられた剣を手に血塗られた戦いに身を投じる。", "2007-10-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("There's No Business Like Show Business", "ショウほど素敵な商売はない", "", "", "http://ecx.images-amazon.com/images/I/51XzTePAxIL._SL210_.jpg", 117, "「紳士は金髪がお好き」「モンキー・ビジネス」のプロデューサー、ソル・C・シーゲルによる名作ミュージカルドラマ。20世紀前半に米ショウビジネス界のスターだったドナヒュー一家の苦悩や成長を、歌曲とダンスにのせて綴る年代記。監督は「わが心に歌えば」のウォルター・ラング。出演はエセル・マーマン、ドナルド・オコナー、そしてマリリン・モンロー。", "1955-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HERBIE：FULLY　LOADED", "ハービー／　機械じかけのキューピッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "スクラップ寸前のフォルクスワーゲンが、レーサー一家の女の子に拾われて大ハッスル。笑ったりスネたり表情豊かに意思を示す奇想天外な車“ハービー”は、かつて「ラブバッグ」シリーズで愛されたディズニーの人気キャラ。クライマックスのレース場面にも大興奮の冒険ファンタジーだ。大学の卒業祝いにオンボロのフォルクスワーゲンをプレゼントされたマギー。しかし、その車ハービーは、不思議な力を持っていて……。", "2005-07-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最後の特攻隊", "最後の特攻隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "成功率わずか13%という特攻隊。その特攻隊の指揮官として赴任した宗方大尉と、護衛隊長、矢代中尉との友情を軸に、むなしく命を散らしていった彼らの姿を、哀切を込めて描いた戦争巨編。当時の東映のスターが、一堂に顔をそろえている。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sisters on The Road", "今、このままがいい", "", "", "http://image.eiga.k-img.com/images/movie/55242/315x210.jpg?3685", 90, "反発し合う異父姉妹の和解と成長を描く物語。ソウルの会社で働くミョンウンは、母が亡くなったという知らせを受け、故郷・済州島へと帰郷する。そこで母の魚屋を継いだ姉ミョンジュと久々に再会したミョンウン、2人で幼い頃に家族を捨てた父親を探す旅に出る。しかし、正反対の性格の2人はことあるごとに衝突し、ついには交通事故を起こしてしまう。2010年の「真！韓国映画祭」にて上映。", "2010-02-27", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海兵四号生徒", "海兵四号生徒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "戦争群像ドラマ。昭和12年春、江田島の海軍兵学校に入校した若者たちの猛特訓の日々を描く。“四号”とは１年生の意。殴打の洗礼を受け、水泳、登山、厳寒の中のカッター競技、兵学校名物棒倒しなど地獄の訓練の末、戦場に散っていった生徒たちに、やがて敗戦の日がやってくる。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONLY THE LONELY", "オンリー・ザ・ロンリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "J・ヒューズが「ホーム・アローン」に続いてC・コロンバスと組んで製作した作品。結婚をめぐって起きる嫁と姑の問題をペーソスを交えて描いていく。結婚適齢期を過ぎたシカゴの警官ダニー。少々マザコン気味で太めの肉体の彼が、メイクアップ師を目指すシャイなラレサと出会い恋に落ちるが……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("第三の極道", "第三の極道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "村上和彦の小説の映画化で、中条きよしが主演するヤクザ映画。大阪通天閣周辺を拠点にする昔気質の極道集団、大門組に広域暴力団藤堂組系武田組が地上げ話を持って来る。大門組はきっぱりと断るが、それ以来、藤堂組との間に抗争が続き、劣勢の大門組は解散することに。大門組の正木は、解散後、堅気ながら正木の侠気に惚れている男たちを集め、藤堂組に復讐するのだった。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伝説のワニ・ジェイク", "伝説のワニ・ジェイク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "同名TVシリーズを「ジョゼと虎と魚たち」の犬童一心が映画化。「頭山」で第75回アカデミー賞短編アニメーション部門にノミネートされた山村浩二によるアニメと、総勢24名の俳優たちのコラボレーションによって生まれたファンタジーだ。 心にポッカリ穴が空いたときに現れる幻のワニをめぐるドラマは、観終わった後、温かい気持ちに包まれる。伝説のワニ“ジェイク”に興味を抱いた男が、目撃者を訪ね歩く。ある時は洗面所の蛇口から、ある時は電車の網棚の上に“ジェイク”は突然現れた。年齢も国籍も違う彼らは、なぜ“ジェイク”を見たのだろうか？", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UP YOUR ANCHOR（LEMON POPSICLE VI）", "グローイング・アップ６　恋のネイビーブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "シリーズ６作目。毎度おなじみの悪ガキ３人組の一人ボビーは、渡米してお留守。残った二人は、ボビーのガールハントの戦果を誇る手紙に挑発されて、渡米を決意。資金稼ぎににわか船員となるが……。例のごとく、お色気騒動も軽く楽しめる青春コメディ。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新雪", "新雪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "国民学校の教師・蓑和田に想いを寄せる言語学者の娘と隣組の女医師・千代。町の世話役は千代に蓑和田との結婚を勧めるが、やがて蓑和田に教育召集が……。戦時色濃厚な作品ながら、五所監督が青春の息吹きをみずみずしく描き出している。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY ROAD", "マイ・ロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "日本出身のCFディレクター、キクオ・カワサキ監督が、主演に名高達郎と武田鉄矢を起用したアメリカ映画。二人の日本人青年の大陸横断の旅をユーモラスに描く。完二と博の二人の日本人青年は、ユタからネバダ砂漠を越えてサンフランシスコへ行こうとするが、途中、家出娘のタミーが加わり何やら怪しい雰囲気となる。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("必死剣鳥刺し", "必死剣鳥刺し", "", "", "http://image.eiga.k-img.com/images/movie/55102/315x210.jpg?2512", 114, "藤沢周平の短編時代小説「隠し剣」シリーズの一編を、豊川悦司主演で映画化。天心独名流の剣の達人・兼見三左エ門は、海坂藩の藩政に良からぬ影響を与える藩主の妾を殺める。しかし処分は軽いもので、その腕を買われた三左エ門は藩主の命を狙う別家の帯屋隼人正（おびやはやとのしょう）殺害の命を受ける。一方、三左エ門と血のつながりのない姪・里尾は、密かに三左エ門に恋心を寄せていた。共演に吉川晃司、池脇千鶴、戸田菜穂、小日向文世、岸部一徳。監督は「愛を乞うひと」の平山秀幸。", "2010-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガッツ伝説／愛しのピット・ブル", "ガッツ伝説／愛しのピット・ブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "元チャンピオン・ボクサー、ガッツ石松主演による人情劇。亡き妻の面影をとある女性に重ね、その娘の世話をするようになるペット店の店主、石松。そんな彼が商店街を陥れる悪に立ち向かう。町を舞台に、心優しい男の恋と男気のドラマが、笑いと涙で綴られる。一本気で不器用そのもののキャラクターは、まさにガッツのはまり役。トボケた言動をギャグにしつつ、義理堅い熱血行動で見る者の胸を熱くする、そんな主人公像が魅力的だ。ガッツの和太鼓演奏への挑戦に加え、鉄拳をまじえた肉弾ファイトの熱演にも注目。", "2006-01-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Birth", "記憶の棘", "", "", "http://image.eiga.k-img.com/images/movie/1292/315x210.jpg?3783", 100, "ニコール・キッドマン主演、ニューヨークのアッパー・イースト・サイドで暮らす美しい未亡人アナが再婚直前、夫の生まれ変わりと告げる10歳の少年に出会い苦悩する、切ない愛のミステリー。二コールと互角の演技で圧倒させる少年ショーンに、「X-MEN／ファイナル・ディシジョン」のキャメロン・ブライトが扮する。監督はレディオヘッド、ジャミロクワイのPVで知られるビジュアリスト、ジョナサン・グレイザー。", "2006-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FINAL COUNTDOWN", "ファイナル・カウントダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1980年12月７日、ハワイ沖を航行していた原子力空母ニミッツが突然タイム・スリップ。一瞬の後、1941年12月７日、日本軍による真珠湾攻撃の現場に迷い込んでしまう。ゼロ戦に対するF−14戦闘機の運命は……。アイディア豊かな脚本の持ち味が生かされたSFアクション。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北国の街", "北国の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "橋幸夫、西郷輝彦とともに、“御三家”と呼ばれた、絶頂期の舟木一夫が主演した青春映画。新潟県十日町。ここで絹の手織の伝統を守る職人の息子・海彦はある日、雪子という女子高生に恋をする。同じく雪子を思う不良の藤田は最初、海彦と対立するが、やがて友情で結ばれた。その後、藤田が大きなケンカを起こし、騒ぎに海彦も巻き込まれ……。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAGNIFICENT SEVEN RIDE", "荒野の七人 真昼の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "シリーズ第４作。第１作から10年後、ただ一人生き残ったクリスは、保安官として平和な日々を過ごしていたが、銀行強盗を追ううちに、群盗に皆殺しにされている一団を目撃、再び７人の仲間を集める。女ばかりが取り残された国境の町で、５人の囚人と一人の新聞記者、そしてクリスが群盗と一戦交えることに。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("早春物語", "早春物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "高校生の女の子が中年サラリーマンに寄せるほのかな想い。これが彼女のなかで恋に変わっていく様を、鮮烈なタッチで捉えた澤井信一郎の監督第３作。サラリーマンの梶川は、女の子・瞳の死んだ母親のかつての恋人。しかし、梶川が母親を捨てたという噂を聞き、瞳の心は揺れ動く。澤井監督は、瞳の中に巻き起こるこの疑念と梶川への愛を、抑制した演出で見事に表現していく。これがやがて後半のロマンティックなキス・シーンへ昇華するに至る手腕の冴えは、俯瞰を多用した仙元誠三の快いカメラ・ワークと相まって、全編をメルヘンチックなラブ・ストーリーとすることに成功している。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲキ×シネ　髑髏城の七人～アカドクロ", "ゲキ×シネ　髑髏城の七人～アカドクロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 160, "人気劇団“劇団☆新感線”の舞台をスクリーンで上映するという新しい試みにチャレンジした《ゲキ×シネ》シリーズの記念すべき第１弾。1990年に初演、以降７年ごとに再演された大ヒット時代活劇『髑髏城の七人』の2004年度バージョンだ。主演には劇団の看板俳優・古田新太、共演陣も水野美紀、坂井真紀ら人気俳優が出演している。映像では観られない古田新太の凛々しくも色気満載のヒーローっぷりは必見だ。信長亡き後、豊臣秀吉が天下を治めようとしていた天正18年。関東平野に忽然と現れた漆黒の髑髏城。それは秀吉の天下統一を阻もうとする謎めいた鉄仮面の武将“天魔王”と、彼が率いる関東髑髏党の手によるものだった。一方、同じく関東にやってきた捨之介は、ひょんなことから髑髏党に狙われていた少女・沙霧を助け……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("煙突の見える場所", "煙突の見える場所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "原作は椎名麟三の短編小説『無邪気な人々』。ヒューマン・ドラマの名作。舞台は、４本の煙突が場所によっては２本にも３本にも見える通称“おばけ煙突”がある北千住。戦火で行方不明になった前夫との籍は残しながら弘子は、足袋問屋に勤める隆吉と同棲している。隆吉の家の２階には税務署員の久保が下宿しており、彼は隣室の下宿人・仙子を想っているが仙子は相手にしない。ある日この家に捨て子があったがそれは弘子の前夫の仕業だった。隆吉は弘子を責め、弘子は家出してしまう。しかし赤ん坊が病気になり、一同が心配することによって、そこに今までにはない連帯感が生まれる。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最終兵器彼女", "最終兵器彼女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "高橋しんの人気同名コミックを実写映画化。同級生のちせと付き合い始めた高校生のシュウジ。ある日、爆撃機による突然の攻撃に遭遇した彼は、間一髪のところで何者かに助けられる。それは戦闘兵器に変貌を遂げたちせだった……！ 兵器となって戦う宿命のヒロインと彼女を守ろうとする青年の純愛を、VFX映像満載で描く。極限の状況下で芽生えたピュアな愛が、見る者の心に迫る感動ドラマだ。", "2006-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金色のガッシュベル!!／　101番目の魔物", "金色のガッシュベル!!／　101番目の魔物", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "『週刊少年サンデー』で2001年の連載開始と同時に絶大な人気を博し、2003年よりTVアニメ化、カードゲームも爆発的にヒットした人気作品の劇場版。人間界に送り込まれた100人の魔物と対戦して魔界の王を目指すガッシュと人間の相棒、清磨が魔物たちと戦う中で成長し、友情をはぐくむ。劇場版では魔界を舞台に新たな戦いが展開する。夏休み、富士山へピクニックに行ったガッシュたちは、白い魔本を持った少女コトハと出会う。本のお告げ通りに森の洞窟へ向かうと、その奥は魔界の入り口だった……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("等待黎明／HONG KONG 1941", "風の輝く朝に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1980年代の香港映画のなかでも、傑作の呼び声高い１本。「冒険者たち」「突然炎のごとく」などにも見られる男二人女一人の愛のトライアングルを、1941年の日本占領下における香港の中に描ききった鮮烈なドラマだ。密航に失敗したフェイと、熱血漢のカン。友人となった二人の間にカンの恋人ナンが入り込む。３人は香港を捨てようとするが、その直前に香港は日本軍のものに。無政府状態となった香港で３人は一緒に生きていくが……。友人のカンと、心を寄せるナン。友情と愛情の板ばさみになりながら、二人とともに生きるフェイに扮したチョウ・ユンファが光る。イップ・トンのはかなげで美しいヒロインぶりも見もの。", "1984-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Burma VJ", "ビルマVJ　消された革命", "", "", "http://image.eiga.k-img.com/images/movie/55302/315x210.jpg?3312", 85, "軍事政権により外国人ジャーナリストの入国が厳しく制限されるミャンマーで、拷問（ごうもん）や投獄のリスクを負いながらも情報を発信し続けるビルマ人のビデオジャーナリスト（VJ）の姿を描いたドキュメンタリー。ジュシュアと名乗る青年VJは、ビルマの軍事政権が放つプロパガンダに対する抵抗勢力として潜伏活動を続け、ビルマの革命の様子を世界中に発信する。アンダース・オステルガルド監督が、ビルマの一般市民が小型ハンディカムで撮影した映像を再構築し、ひとつの物語をつむぎ出す。第82回アカデミー賞ドキュメンタリー部門にノミネートされた。", "2010-05-15", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMELIA EARHART", "ラストフライト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "第二次世界大戦前のアメリカ航空界に突如現れて、次々と記録を塗り替え、女性版リンドバーグと呼ばれたアメリア・エアハート。しかし彼女の栄光を利用しようとする国家権力の影が近づいていた……。アメリカ航空史上の謎といわれた最後のフライトをドラマティックに描いたミステリー。数奇な運命をたどるヒロインをD・キートンが熱演。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAD DOGS AND ENGLISHMEN", "侵入者2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ドラッグに溺れるイギリス上流社会の姿を描いたエロティック・バイオレンス。イギリス在住のアメリカ人青年マイク・ストーンはある日、美女アントニアと出会う。彼女の虜となった彼は、次第に彼女の友だちで貴族の子息たちとも交流関係を持つようになる。しかし、彼らはドラック三昧の日々を送るクズのような奴ばかりだった。マイクはアントニアを彼らから突っ放そうと懸命に努力をするが、その一方では彼らの麻薬ルートが判明し警察が捜査を開始する。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Islands in the Stream", "海流のなかの島々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "第2次世界大戦中、バハマの島で彫刻家として暮らす初老の男性の人生を描いたヘミングウェイの自伝的小説を、フランクリン・J・シャフナー監督が映画化。別れた2人の妻との間にできた3人の息子たちとの心の交流を描いた「THE BOYS」、最初の妻の突然の訪問と彼女が持ってきた悲しい知らせを描く「THE WOMAN」、住み慣れた島を出航した彼が、密航中のユダヤ人を救出する「THE JOURNEY」の3章から構成される。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Grave Encounters", "グレイヴ・エンカウンターズ", "", "", "http://image.eiga.k-img.com/images/movie/57832/315x210.jpg?1964", 93, "心霊番組の撮影クルーが廃墟となった精神病院に潜入し、数々の超常現象に巻き込まれていく姿をモキュメンタリー形式で描くホラー。超常現象を追跡するTV番組「グレイヴ・エンカウンターズ」でプロデューサーを務めるランスは、番組スタッフを引きつれ、50年以上前に閉鎖されて以来、心霊スポットとして有名な精神科病院を訪れる。しかし、ランス自身は幽霊の存在など信じておらず、「グレイヴ・エンカウンターズ」も偽者の霊能力者を出演させているヤラセ番組だった。一行はいつものように、もっともらしい映像が撮れるよう演出を始めるが、本物の超常現象としか思えない出来事が続発。徐々に恐怖にかられていく。監督はコリン・ミニハンとスチュアート・オルティスが組んでいる「ザ・ヴィシャス・ブラザーズ」。", "2012-06-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男の紋章・竜虎無情", "男の紋章・竜虎無情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "シリーズの第10作で最終作。竜次の服役中、悪玉ヤクザに村田組の縄張りが荒らされる。竜次は出所後、組を再建して悪玉ヤクザを倒し、晴子と晴れて結婚式をあげるが、流れ者のヤクザ・遠州虎が竜次を狙う。宍戸錠が遠州虎に扮しシリーズ最終作に花を添えた。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUDY", "ルディ 涙のウイニング・ラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "イリノイ州の田舎町で暮らす少年ルディの夢は、名門ノートルダム大学のフットボール・チーム“ファイティング・アイリッシュ”でプレーすること。しかしそれは才能にも恵まれず、家庭も裕福でない彼にはかなわぬ夢だった。ルディは高校を卒業すると父や兄が働く鉄鋼所に勤めたが、夢を捨て切れずに学費を貯め、ひそかにチャンスをうかがっていた。唯一応援してくれる友人のピートが鉄鋼所で起きた事故で帰らぬ人となったことにショックを受けたルディは、家族の反対を押し切り大学へ進むことを決意する。ノートルダム大の伝説の人物、ルディ・ルティガーの青春を忠実に描いた感動的なスポーツ・ドラマ。ビデオ、DVDタイトルは「ルディ」。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILLOW　AND　WIND", "柳と風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "イラン北部の一年中雨が降る村の小学校。教室の窓ガラスを割ってしまったクーチェキは、明日までにガラスを直すよう厳命され、必死にそれを成し遂げようと努力するのだが……。イラン映画界の巨匠A・キアロスタミが脚本を執筆。少年にのしかかる運命をシンプルに、そして残酷に描いた人間ドラマ。", "1999-01-01", "15,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("朱霊たち", "朱霊たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ヨーロッパで活躍する日本人舞踏家・岩名雅記が脚本、演出、製作を手がけ、4年の歳月をかけて完成させた舞踏劇映画。第2次世界大戦から7年後の東京。1人の少年が、空からまかれたビラを追いかけて不思議な館にたどり着く。そこでは太陽の光に当たることのできない難病に冒された人々が、自らの死を願って暮らしていた……。日本、イタリア、フランスの舞踏家たちが、個性的な登場人物たちを存在感たっぷりに演じる。", "2007-01-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAR TREK II：THE WRATH OF KHAN", "スター・トレック２　カーンの逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "人気シリーズ第２作。今回は、カーク提督率いる惑星戦艦エンタープライズ号と、彼への復讐に燃える悪の帝王カーンとの凄絶な戦闘が展開される。前作とは趣向が異なり、TVシリーズに見られたSFX効果抜群のアクション場面に注目。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌１６／　浜崎は今日もダメだった♪♪", "釣りバカ日誌１６／　浜崎は今日もダメだった♪♪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "舞台は基地・造船で知られる長崎県佐世保市。仕事そっちのけで釣りに夢中になっていたハマちゃんが失踪する騒動が勃発。金子昇と伊東美咲が、前途多難な恋に落ちる御曹司とバーの美女に扮する。また、ハマちゃんと意気投合する釣りバカ米兵役に扮するボビー・オロゴンの爆笑演技にも注目だ。スーさんとハマちゃんは、第二西海橋の連結式のため長崎県佐世保市にやって来た。ところが、ハマちゃんが行方不明になってしまう。", "2005-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eye of the Beholder", "氷の接吻", "", "", "http://image.eiga.k-img.com/images/movie/1862/315x210.jpg?3946", 107, "英国諜報局の調査員アイは局長の依頼で彼の息子ポールを調査していた。博物館で愛人らしき女と落ち合うポール。女はポールの別 宅で金を受け取ると、ポールを惨殺した。アイは事態を本部に報告しようとするが、そのとき、アイの元を去っていった愛娘ルーシーの幻影がよぎる。ルーシーの囁く声に従い通 信を切ったアイは、女と同じ列車に乗り込む。列車がニューヨークに付くと、今度は宝石商の遺体が発見される。", "2000-03-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ショージとタカオ", "ショージとタカオ", "", "", "http://image.eiga.k-img.com/images/movie/55762/315x210.jpg?3969", 158, "1967年8月30日、茨城県で起こった強盗殺人事件「布川事件」で、桜井昌司（当時20歳）、杉山卓男（当時21歳）の2人の青年が犯人として逮捕される。2人はえん罪を訴えるが認められず、29年間を獄中で過ごすことになる。2人が仮釈放された96年秋から再審公判が始まった2010年夏までの14年間に密着し、カメラを回し続けたドキュメンタリー。", "2011-03-19", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トマトケチャップ皇帝　オリジナル完全版", "トマトケチャップ皇帝　オリジナル完全版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "27分の短縮版しか公開されなかった「トマトケチャップ皇帝」の完全版。寺山修司本人が所有していた唯一のオリジナル・プリントをもとに修復した完全版で1996年にロードショー公開された。宿題をやらないことで父親に殴られた子供が、振り向き様に父親を刺殺する。それを合図に、全国の子供たちが一斉に蜂起する。それは子供のためのユートピアを作るためだった。のちに「短縮版」と「ジャンケン戦争」に分割縮小されている。ビデオは「寺山修司 実験映像ワールド Vol.7」に収録。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アナザヘヴン", "アナザヘヴン", "", "", "http://image.eiga.k-img.com/images/movie/1842/315x210.jpg?2052", 131, "満月から7日後の夜、アパートから男の死体が発見される。被害者は首を叩き折られ、頭からは脳みそがなくなっていた。部屋に充満するシチューの匂いに気付いた刑事・早瀬がとっさに鍋の蓋を開けると、そこには……！　検死の結果 、犯人は握力150キロ以上の大男と報告されたが、現場から見つかったのは女のものと思われる小さな指紋だった。混乱する捜査本部。ほどなく、同じように脳みそを抜き取られ料理された殺人が次々と発生する。", "2000-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千年の恋・ひかる源氏物語", "千年の恋・ひかる源氏物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "紫式部はなぜ『源氏物語』を書いたのか。その謎の答えを大胆に説き明かした絢爛たる歴史絵巻。吉永小百合、森光子、渡辺謙ら豪華キャストも魅力。源氏物語の中のストーリー（夢）と、物語を執筆する紫式部のいる時代（現＝うつつ）とが交互に絡み合う展開や時折ミュージカルのようなシーンが織り込まれるのがユニークだ。緻密に再現された平安時代の宮殿セットなども必見だが、特筆すべきは光源氏を演じた天海の存在。宝塚時代を彷彿とさせる、彼女の凜とした勇姿なしには作品自体が成り立たなかったのでは、と思わせるほど。深い嫉妬心から怨霊となる六条御息所を体当たりで熱演した竹下景子も光る。監督は数々の名ドラマの演出家として知られ、本作が映画監督デビューとなった堀川とんこう。東映創立50周年記念作品。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAND DES SCHWEIGENS UND DER DUNKELHEIT", "闇と沈黙の国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "幼い頃に視覚と聴覚を奪われた56歳のF・シュトラウビンガー。その彼女が他のろうあ者たちを訪れて世話していく……。外界とのコミュニケーションをいっさい絶たれ、“闇と沈黙”の孤独な世界に生きるろうあ者たちの姿を追った異色ドキュメンタリー。", "1971-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VOUS INTERE´SSEZ−VOUS A｀ LA CHOSE？", "新・個人教授", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "内気な青年パトリックはバカンスにやって来た従妹ディナに思いを寄せていたが、気持ちを告白できず悶々としていた。ある日、若く美しい叔母リーズが訪れ、彼に愛の手ほどきをする。自信をつけたパトリックは、ディナの誕生日の夜に彼女と結ばれる。セクシーな青春映画の一編。", "1973-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONLY THE VELIANT", "勇者のみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ランス大尉は上官の娘キャサリンにひそかな思いを寄せている。ある日、恋敵のホロウェイがインディアンに殺されたことで、ランスはわざとホロウェイを危険な任務に就かせたと皆から卑怯者の烙印を押されてしまうのだが……。「ローマの休日」の二年前、G・ペック35歳の時の騎兵隊もの。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("牝猫たちの夜", "牝猫たちの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "シュールな映像が冴える田中登監督の第２作。新宿のソープランドで働く昌子、ジュン、おみつの売れっ子３人の男性遍歴を描いたロマンポルノだ。ゲイ、童貞、チンピラと、様々な男たちとの悲しいやりとりを繰り返しながらも、彼女たちは今日も明るくセッケンの泡にまみれていく。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌え若人達", "歌え若人達", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "同じ家で生活する４人の大学生、それぞれの明るく苦い青春を軽く描いた風俗喜劇。当時の青春ものといえば、大島渚ら新世代の監督たちが「青春残酷物語」などでその時代の気分とぴったりマッチした作品を発表していた中、保守派の旧世代作家であることを印象づける内容だが、今観てみるとむしろ新鮮な仕上がりになった。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("American Hardcore", "アメリカン・ハードコア", "", "", "http://ecx.images-amazon.com/images/I/51m81QUgTxL._SL210_.jpg", 100, "1970年代パンクを暴力的に進化させたサウンドと究極のDIY精神で80年代アメリカのアンダーグラウンド音楽シーンを席巻し、現代パンクにも多大な影響を与えたアメリカン・ハードコア・ムーブメントを題材にした音楽ドキュメンタリー。「ブラック・フラッグ」「バッド・ブレインズ」といった代表的バンドから地方都市で活動したバンドまで全60バンドをフィーチャーし、メンバーへの最新インタビューや当時の貴重な映像を交えながらその実像に迫る。", "2006-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("百年の絶唱", "百年の絶唱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「雷魚」「汚れた女〈マリア〉」など瀬々敬久監督作品の共同脚本家として活躍する井土紀州が監督した８ミリ作品。中古レコード店でバイトしている青年が、突然失踪した男のレコードを処分することに。彼は男の部屋で、この世のものとは思えない“声”を聞き、やがてテロリストだった彼の“ある思い”に取りつかれていく。ピンク界で活躍する監督・俳優の佐野和宏がレコード店の店主に扮し、瀬々も役者として出演。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE PETIT PRINCE", "星の王子さま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "おなじみのテグジュペリの同名ベストセラー童話の映画化。銀河系の小さな星に住む王子さまの愛と夢に満ちた冒険譚を、原作にかなり忠実に再現。メロディアスなフランス語独特の響きと、ロマンティックな映像のコントラストが素晴らしい。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Leon The Professional: Uncut International Version", "レオン／完全版", "", "", "http://ecx.images-amazon.com/images/I/51GZ1fixTEL._SL210_.jpg", 136, "リュック・ベッソン監督作品「レオン」に、22分の未公開映像を加えた完全版。舞台はニューヨーク。家族を殺され、隣室に住む殺し屋レオンのもとに転がり込んだ12才の少女マチルダは、家族を殺した相手への復讐を決心する。少女マチルダを演じるのは、オーディションで選ばれ、本作が映画初出演となったナタリー・ポートマン。また、寡黙な凄腕の殺し屋レオンをベッソン作品おなじみのジャン・レノが演じている。", "1996-10-31", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Musanilgi", "ムサン日記　白い犬", "", "", "http://image.eiga.k-img.com/images/movie/57272/315x210.jpg?2605", 127, "北朝鮮から韓国に移住した脱北者の若者が直面する厳しい現実を、リアリズムに徹して描いたドラマ。北朝鮮を抜け出し韓国・ソウルに暮らす青年スンチョルは、拾った白い犬だけを心のよりどころに、ポスター張りやビラ配りなど細々とした仕事で生活を続けていた。やがて教会で見知った女性スギョンが勤めるカラオケボックスで職を得たスンチョルだったが、客とのトラブルなどを理由に解雇されてしまい……。イ・チャンドン（「シークレット・サンシャイン」「オアシス」）の助監督を務めたパク・ジョンボムの長編監督デビュー作。監督の亡き親友でもあった脱北者の青年が主人公のモデルとなっている。", "2012-05-12", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AIR FORCE ONE", "エアフォース・ワン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "「U・ボート」「アウトブレイク」のW・ペーターゼンが、H・フォードを合衆国大統領役に据えて描いたスペクタクル大作。アメリカへの帰路についた大統領マーシャルは、専用機のエアフォース・ワンで家族との団欒を楽しんでいた。だが、それもつかの間、ロシアのTVクルーに化けていたテロリスト６人が突如、機内を占拠してしまう。彼らの目的は、合衆国の手引きにより捕らわれたカザフスタンの独裁者、ラデク将軍の解放。一旦は機外に脱出したかに見えた大統領だったが、人質となった愛する家族やスタッフを見捨てるわけにはいかず、一人反撃の機会をうかがうことに。果たして彼に勝機はあるのか？　主人公とテロリストの熾烈な駆け引き、特撮を駆使したダイナミックな映像と、緊張感いっぱいのドラマが展開。ベトナム戦争の経験があるという大統領の設定が、従来では考えられない大統領の孤軍奮闘にリアリティーをもたらしている。テロリストのリーダー役のG・オールドマン、副大統領役のG・クローズの演技も凛々しい。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("世界の中心で、愛をさけぶ", "世界の中心で、愛をさけぶ", "", "", "http://ecx.images-amazon.com/images/I/516FE8DWMBL._SL210_.jpg", 138, "行定勲監督が同名ベストセラー小説の映画化に挑んだ意欲作。“その後”の主人公が狂言回しになるという映画独自の構成がとられており、現在から見つめ直された過去という距離感が、切々とした情感を醸し出す。TV『ウォーターボーイズ』で注目された森山未來と「ロボコン」の長澤まさみによる高校時代の悲恋は号泣必至！　結婚を控えた朔太郎だが、ある日、婚約者の律子が失踪した。行き先が四国だと分かった朔太郎は、自分の故郷でもある四国へ向かう。そこには、初恋の亜紀との思い出が詰まっていて、朔太郎は蘇った記憶に引きずられていく。", "2004-05-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.JONES", "心のままに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "「ジャック・サマースビー」に続いてR・ギアが製作総指揮、主演を兼ねたラブ・ストーリー。天真爛漫さゆえに精神をコントロールできない男ジョーンズと、自分の感情を内に閉じ込めがちな精神科の女医リビー。医者と患者の恋愛という医学界のタブーを越えて惹かれ合う、彼らの恋路をロマンティックに描いていく。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISUNDERSTOOD", "ウインターローズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「E.T.」のエリオット少年、H・トーマスが主演した、ロングセラー小説の映画化。突然の母の死によって残された父子がすれ違いと誤解から、やがて悲劇的な結末を迎えるまでを、「スケアクロウ」の名匠J・シャッツバーグが抑制の効いた演出で描いている。", "1984-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("破戒", "破戒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "自然主義作家の島崎藤村の小説で、瀬川丑松とお志保の愛の物語。この部落差別の問題を扱った原作を、木下監督は千曲川を背景にした自然の中で、きわめて叙情的に描く方法に徹し、差別問題という重い題材には深入りせず、ピュアな青年と少女の恋愛物語として仕立て上げている。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AGATHA　ET　LES　LECTURES　ILLIMITEES", "マルグリット・デュラスのアガタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "マルグリット・デュラスが38歳年下の恋人ヤン・アンドレアと作った1981年作品を日本初公開。自身の著書『死の病・アガタ』を基に冬の海辺にたたずむ兄妹の愛の記憶を、断片的な映像とデュラス自身の朗読で綴った実験的作品だ。海辺の古びた別荘ヴィラ・アガタで久々に兄と再会したアガタ。翌日の早朝にここを旅立つ彼女は、兄と永遠に別れる決意をしていた。やがて二人は自分たちの禁じられた愛を振り返り、思い出を語り合っていく……。", "1981-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOLA", "欲望の裏側", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「危険な情事」にも似たスペイン製ラブ・サスペンス。主人公は、夫と別居中の女ローラ。妻子をフランスに残し、スペインで会社を経営するロベールと愛し合った彼女は、彼と二人で暮らしはじめる。だが、ローラのことを忘れられない夫は、二人のアパートを見つけ出し、彼女に復縁を迫った。時には、暴力をふるって訴えかける夫に負け、一夜をともにするローラ。しかし、彼女は夫との仲に区切りをつけるべく、別れを告げる。その時から、夫の心の中に残忍な思いが浮かび上がった……。", "1988-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DONOVAN’S REEF", "ドノバン珊瑚礁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "南太平洋の孤島に、父を訪ねて一人のイギリス女性がやって来る。しかしその父は原住民の女と結婚し、３人の子供までいたからさあ大変！ 仲間はその場をなんとかごまかそうとするが……。あいさつがわりに殴り合うような荒くれ男たちの世界を、J・フォードが痛快このうえなく描く後期の傑作の１本。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GANG", "友よ静かに死ね", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "第二次大戦直後のフランス全土に名を馳せた、愛車シトロエンを駆って犯行を繰り返した５人組ギャングの物語。素晴らしい友と美しい恋人に囲まれて、犯罪を楽しむかのような若きギャングのリーダーを、ドロンがカーリー・ヘアで演じる。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY SIX LOVES", "ちびっこ天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ミュージカルの振付師としても知られるG・チャンピオンの監督処女作。ミュージカル・スターのジャニスは、過労から、しばらくステージを離れて自宅の屋敷で休養することになった。ところが彼女が屋敷に帰ってみると、近くの掘っ建て小屋に６人の子供たちと一匹の犬が住みついており、不びんに思った彼女は子供たちの面倒を見ることにする。主演のD・レイノルズが生き生きとした演技を見せるほのぼのとした一編。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("НЕОКОНЧЕННАЯ ПЬЕСА ДЛЯ МЕХАНИЧЕСКОГО ПИАНИНО／NEOKONCHENNAYA　PYESA　DLYA　MEKHANICHESKOGO　PIANIN", "機械じかけの　ピアノのための未完成の戯曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "チェーホフの『プラトーノフ』他、数編の短編小説をモチーフに、革命前の富裕階級の人々の一日を描く文芸コメディ。多くの登場人物をスケッチ風に綴り、上流階級のインテリの姿を、シニカルかつ滑稽に表現。ミハルコフ自身も、医者役で出演している。", "1977-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Boy in the Striped Pyjamas", "縞模様のパジャマの少年", "", "", "http://image.eiga.k-img.com/images/movie/54252/315x210.jpg?3554", 95, "強制収容所の柵越しに友情を育む無垢な少年たちを描き、世界的ベストセラーとなった同名小説の映画化。第2次世界大戦下のドイツ、ユダヤ人強制収容所の指揮官を父に持つ少年ブルーノはある日、有刺鉄線で囲まれた風変わりな農場を見つける。そこで縞模様のパシャマを着た少年シュムエルに出会い、次第に心を通わせていくが……。監督は「ブラス！」「リトル・ヴォイス」のマーク・ハーマン。", "2009-08-08", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADVENTURES IN BABYSITTING", "ベビーシッター・アドベンチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "デートをすっぽかされた土曜日。ベビーシッターのバイトをしていたクリスのもとに、街で迷子になったので迎えに来てくれという親友のSOS。仕方がないので、悪ガキを３人引き連れてシカゴのダウンタウンへ。治安最悪のこの街。クリスと悪ガキたちは、多くの危機迫るアドベンチャーを繰り広げる。女子大生女優として活躍していたE・シューの３作目。軽快なポップスにのって踊る冒頭から、迷い込んだ黒人オンリーのライブ・ハウスで聞かせるブルースのノドまで、見ごたえのある一編。バック・コーラスは大御所アルバート・コリンズ。「グレムリン」の脚本を担当し、後にヒットメーカーとなるクリス・コロンバスの監督デビュー作。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホルモン女", "ホルモン女", "", "", "http://image.eiga.k-img.com/images/movie/57502/315x210.jpg?4745", 32, "吉本興業が主催する2011年・第3回沖縄国際映画祭の企画「地域発信型プロジェクト」で製作された6本の短編作品を、「東」と「西」の3本ずつに分けて劇場公開する「沖縄国際映画祭　地域発信型映画」の「西」の1本。05年、国体の開催をひかえた岡山県・津山市の市役所では、観光課の坂井ひろこが選手たちをもてなす郷土料理の選定に頭を悩ませていた。そんなある日、ひろこはお好み焼き屋でホルモンを具材にした焼うどん「ホルモンうどん」を食べる男たちに出会う。油ぎったそのうどんに最初は眉をひそめたひろこだったが……。", "2011-12-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES RIPOUX", "フレンチ・コップス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "花のパリのオマワリさんも人の子。タダ食い、ネコババ、ノゾキにツケ届け、賄賂や罪のモミ消しなどは朝メシ前。ベテランと田舎出の新米警察官がコンビで繰り広げる珍騒動の数々をコメディ・タッチで描く。P・ノワレ、T・レルミットの持ち味を生かした演出がユニークで楽しい。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOUT VA BIEN", "万事快調", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ゴダールの重要作として知られながら日本では、1996年まで公開されなかった１本。政治活動で知られるY・モンタンとJ・フォンダを主演に据えるというキャスティングも実に皮肉たっぷりな“ゴダール流”の政治映画。ラジオ局のパリ特派員スーザンは、同棲中の映画監督ジャックを連れて、食肉工場を取材に訪れる。だが、工場はストライキ中で二人は社長とともに監禁され、経営者側、労働総同盟側、新左翼側、三者の主張を聞かされることに……。商業映画からの撤退を開始した時期の作品のため、メッセージが前面に出すぎてはいるが、ラストの長回しのカットなどに、ゴダール映画特有のダイナミズムは感じられる。", "1972-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市地獄旅", "座頭市地獄旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "座頭市は父の仇を探して放浪の身の若侍とその妹を助けて、仇の浪人・十文字糺を斬る。巨匠・伊藤大輔が脚色を担当しているだけに細かいところに工夫が凝らされ、特に市と十文字が頭の中で将棋を指しながら対決の機会をうかがうシーンは、緊迫感がたっぷり。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・ハリウッド", "ザ・ハリウッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「真夏の少年」の野村惠一が、映画への愛を込めて描いた人間ドラマ。京都のビデオ店で働く少年、次郎と留学生のロバート。好きな映画について対立しながらも友情を交わした二人はそれぞれ恋愛を経験していく。だが、ロバートは恋人の中国人女性エレナが密入国の罪で逮捕されたことから、失意のうちに日本を去ることになる。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA DISOBBEDIENZA", "蒼い本能", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "『暗殺の森』を書いたA・モラヴィアの原作を基に、同作品の映画化作品(監督・ベルトルッチ)にも出演したS・サンドレッリを迎えて描いた官能の世界。17歳の少年ルカを主人公に、優しい看護婦と美しい家庭教師に対して目覚める少年の青い性を、繊細かつエロティックに映像化。", "1981-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LEADING MAN", "妻の恋人、夫の愛人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "一人の謎めいた映画スターにほんろうされ、思いがけない運命をたどる著名な劇作家とその妻を軸に描く群像劇。英国を代表する劇作家フィリックスは、新人女優ヒラリーと浮気中。傷心の妻エレナとの板挟みで苦悩するそんな彼の前に、次回作の主演俳優ロビンが現れる。とある理由でハリウッドを追放された過去を持つロビンは、フィリックスとエレナの関係を見抜き、ある提案を持ちかける。それは、エレナを誘惑するというものだった。巧みに彼女に近付き、心を射止めるロビン。罪悪感に苛まれるフィリックスは、次第に美しさを取り戻していくエレナを見て動揺を隠せない。やがて迎えた舞台の初日、ロビンとヒラリーがキスをしているところを目撃した彼は、ロビンへの憎悪を募らせていき……。裏切りと欲望が入り乱れる男と女の関係が、予想外の出来事へと発展していくストーリーが巧妙。これが映画初主演となるJ・ボン・ジョヴィが、ミステリアスな映画スターを好演し、存在感を放っている。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alza la testa", "頭を上げて", "", "", "http://image.eiga.k-img.com/images/movie/55432/315x210.jpg?1628", 87, "「潮風に吹かれて」のアレッサンドロ・アンジェリーニ監督が、再び描く父と息子の物語。造船所で働く職人メロの夢は、男手一つで育てている一人息子のロレンツォをボクシングのチャンピオンにすることだった。2人は毎日欠かさずトレーニングを重ねていたが、息子がルーマニア移民の少女と出会ったことで、親子は対立してしまう。イタリア映画祭2010にて上映。", "2010-04-30", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AN OCCASIONAL HELL", "シェイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "胸を銃弾で撃ち抜かれ、刑事の職を辞した中年男アーネスト。今は大学で文学を教えている彼の許に、何者かに銃殺された同僚の未亡人エリザベスがやって来る。彼女に夫殺しの犯人割り出しを依頼されたアーネストは、渋々捜査に取りかかるが、そんな彼の前に被害者の情事の相手である金髪美女ジュリーのエロティックな幻影がちらつき始める……。点滴や注射を打ちながら捜査を続ける主人公を、T・ベレンジャーがいぶし銀の魅力で熱演したサスペンス・ミステリー。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版マクロスF イツワリノウタヒメ", "劇場版マクロスF イツワリノウタヒメ", "", "", "http://image.eiga.k-img.com/images/movie/54712/315x210.jpg?1995", 120, "1982年にTV放映されたSFアニメ「超時空要塞マクロス」を発端とする「マクロス」シリーズの、「マクロスF（フロンティア）」を新たな解釈で再構築した劇場版2部作の第1弾。西暦2059年、新天地を目指し宇宙をさまよう銀河移民船団「マクロス・フロンティア」でパイロットになることを夢見る少年・早乙女アルトは、毎日同じことが繰り返される日常に閉塞感を感じていた。そんなある日、歌姫のシエリル・ノームが船団へやって来る。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミニパト・第１～３話", "ミニパト・第１～３話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 38, "劇場版「機動警察パトレイバー」１＆２作品の監督・押井守が、“裏パトレイバー”として作り上げたオムニバス・ショート・アニメ。3DCGを駆使して、割り箸人形アニメ風に処理しているところがミソ。「吼えろリボルバーカノン！」「あゝ栄光の98式AV！」「特車２課の秘密！」と３つのエピソードが、劇場シリーズ第３作の併映として１本ずつランダム上映という特殊な公開形態で紹介された。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・あつい壁", "新・あつい壁", "", "", "http://image.eiga.k-img.com/images/movie/53072/315x210.jpg?2317", 111, "デビュー作「あつい壁」でハンセン病患者とその家族に対する差別の実態を浮き彫りにした中山節夫監督が、実在の事件を基に再びハンセン病問題を描いた作品。ルポライターの卓也は、取材で知り合ったホームレスの男性から、50年前に熊本で起きた殺人事件の話を聞かされる。犯人として捕らえられたハンセン病患者の男性は、裁判で無実を訴えながらも死刑に処されていた。事件を記事にしようと考えた卓也は、取材のために熊本を訪れるが……。", "2007-11-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Underwater Love - A Pink Musical", "UNDERWATER LOVE おんなの河童", "", "", "http://image.eiga.k-img.com/images/movie/56872/315x210.jpg?2938", 86, "「若きロッテちゃんの悩み」「ゴーストキス」など一般映画でも活躍するピンク映画界の雄・いまおかしんじ監督が、ウォン・カーウァイ作品でおなじみの名カメラマン、クリストファー・ドイルを撮影に迎えて撮り上げた日独合作のピンクミュージカル。缶詰工場で働く明日香は恋人との結婚を間近に控える幸福な日々を送っていた。そんなある日、高校時代に水死したはずの同級生・青木哲也がカッパとなって明日香の目の前に現れ……。", "2011-10-08", "76,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("部屋／THE ROOM", "部屋／THE ROOM", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「自転車吐息」で知られる自主映画界の雄、園子温が手掛けた不思議なムードの一編。殺し屋稼業に疲れた初老の男が、今まで殺してきた人間の霊に導かれ、不動産屋の女性係員を伴って、自分の死ぬべき部屋を探し求める。ささやきによるセリフと、長回しの退廃的な映像が醸し出す、奇妙な静寂感に注目。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Show of Force", "テロリストを撃て！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "独立か現状維持か、はたまたアメリカ51番目の州になるのか!? ３派が入り乱れる1978年のプエルトリコを舞台に、独立派のテロリストを追う女性TVレポーター、ケイトは、やがて事件の裏に、FBIの暗躍を見いだす……。未公開なのが不思議なくらいのキャスティングとスケール。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shallow Hal", "愛しのローズマリー", "", "", "http://image.eiga.k-img.com/images/movie/1642/315x210.jpg?4618", 114, "面食いのハルは、精神治療士に「外見ではなく、内面の姿が見える」催眠術をかけられるが、自分はそれに気づかず、街で出会ったローズマリーに一目惚れ。しかし、ローズマリーの外見は極度の肥満体だった。監督は「ジム・キャリーはMr.ダマー」「メリーに首ったけ」のファレリー兄弟。主演は「ハイ・フィデリティ」のオタクなレコード店員ジャック・ブラック。スリムなグウィネス・パルトロウが肥満体メイクにも挑戦。", "2002-06-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIAM　SUNSET", "サイアム・サンセット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "空から降ってきた冷蔵庫で妻を亡くして傷心のイギリス人紳士が、ビンゴ大会でオーストラリア縦断バス旅行に当たったが……。世界最悪のツアーに参加する羽目になった主人公が体験する、奇想天外なハプニングの数々と、新たな恋の顛末を描く。ほのぼの感とブラック・ユーモアが相まったアドベンチャー・コメディ。", "1999-01-01", "11", 0, NOW(), NOW());
