INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAND GIRLS", "スカートの翼ひろげて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "第二次大戦下のイギリス。政府は戦場に駆り出された男たちに代わって、女性たちがボランティアで農業に従事するランドガールズを結成。自ら応募した３人の女性が田舎町に派遣される。名家出身の上品なステラ、知的で情熱的なアグ、労働者階級出身の奔放なブルーは、慣れない農作業に精を出す一方で、恋に揺れ動きながら厳しい戦争を生き抜いていく……。「アバウト・ア・ボーイ」のR・ワイズら英国の美人女優たちが明るく前向きに生きる女性たちをみずみずしく好演。美しい田園風景のなかに、生きることの素晴らしさを鮮やかに描き出した秀作だ。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅燈區／STREET　ANGELS", "不夜街　復讐の天使たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "香港の黒社会で生きるタフでセクシーな女たちの、壮絶な戦いを描いたノワール・アクション。恋人の身代わりに服役した女性アンは出所後、組織からナイトクラブの経営を任される。そんな折、中国マフィアが香港に進出。ビジネス面で一味と対立し、熾烈化する抗争に巻き込まれたアンは、美貌と知性を武器に戦いに乗り出していく。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火天の城", "火天の城", "", "", "http://image.eiga.k-img.com/images/movie/54082/315x210.jpg?3308", 139, "山本兼一が戦国時代の名工・岡部又右衛を描き、第11回松本清張賞を受賞した同名小説を映画化。主演は西田敏行、共演に大竹しのぶ、椎名桔平、福田沙紀ほか。天正4年、熱田の宮番匠・岡部又右衛門は、織田信長から安土に五重の城の建設を命じられる。又右衛門は即座にその命を即座に引き受けるが、城造りを指揮する総棟梁の座を名だたる番匠たちと競うことになってしまう。総棟梁の座を掴むため、城の図面作りに没頭する又右衛門だったが……。", "2009-09-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スペインからの手紙　ベンポスタの子どもたち", "スペインからの手紙　ベンポスタの子どもたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "スペインの北西部にある子供だけの自治共同体“ベンポスタ子ども共和国”を舞台にしたヒューマン・ドラマ。母の死から心を閉ざし、ベンポスタに入国することになった日本人少年の良二。そこの暮らしの中で朗らかさを取り戻した彼は、やがてサーカス団の一員として日本を訪れ、兄と感動の再会を果たす。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUN−SENG　KIM−BONG−DOO", "ぼくらの落第先生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "韓国で300万人以上を動員し大ヒットを記録、笑って泣ける感動のヒューマン・ストーリー。韓国のアカデミー賞、大鐘賞で脚本賞・企画賞を受賞した秀作だ。ほのぼのとしたノスタルジックな風景の中でドラマが展開する。生徒の親から不評を買い、ソウルからド田舎の学校に飛ばされてしまった先生。いったいどうすれば都会に戻ることができるのか、先生は悩んだ末に……。主演は「リベラ・メ」のチャ・スンウォン。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Perfect Murder", "ダイヤルM", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "　破産寸前のスティーブンは妻エミリーを殺し、彼女の莫大な財産を手に入れようとたくらむ。エミリーの愛人デビッドの弱みを握ったスティーブンはそのデビッドに強盗を装わせ、彼女を殺害させようとする。やがて覆面をかぶった強盗が自宅にいたエミリーに接近。だがエミリーは逆に強盗を殺してしまう。しかもそれは見知らぬ男だった……。ヒッチコックの名作「ダイヤルMを廻せ！」を現代風にリメイク。二転三転するサスペンス劇が展開。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Devil's Tomb", "デビル・ハザード", "", "", "http://image.eiga.k-img.com/images/movie/54862/315x210.jpg?2176", 90, "「ザ・エージェント」のキューバ・グッディング・Jr.、「ヘルボーイ」のロン・パールマン、「ディパーテッド」のレイ・ウィンストンら実力派キャストが共演するサスペンス・ホラー。中東に広がる砂漠の地下900メートルから発見された謎の神殿で、極秘に発掘を行っていたCIAの調査隊が消息を絶つ。7人の精鋭からなる傭兵部隊が救出のため現地へと派遣されるが、そこには数千年前に神の力で封印された邪悪な存在が眠っていた。", "2009-10-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Red Line 7000", "レッドライン7000", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "カー・レーサーのジムがデイトナのレースで事故死した。同僚のマイクと雇い主のパットが宿舎に戻ると、ジムの婚約者ホリーがいた。彼女は以前にも恋人を事故で失っていた。やがてパリから招かれたドライバーのダンがホリーに一目惚れするが、ホリーは頑なに拒絶し……。出演者のほとんどが、デビュー間もない新人か演技経験のない若者で、ホークス監督は大いに手を焼いたという。題名の『7000』は車のエンジンの1分間の回転数を示している。", "1966-03-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("命しらずのあいつ", "命しらずのあいつ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "熊五郎とともに故郷・博多に戻った都築は、親友・武原の失踪事件を調べていた。そんな都築を村岡組の新組長・長谷部は執拗に妨害し、都築は爆風を受けて聴力を失ってしまう。だが、熊五郎の調査の結果、長谷部が村岡前組長を殺していたことを突き止める……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("投げたダイスが明日を呼ぶ", "投げたダイスが明日を呼ぶ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ギャンブラー・氷室は神戸の賭博場に流れ着く。そこでイカサマにひっかかった素人賭博師、中島を助けたために、氷室は逆恨みから狙撃される。しかし、銃弾がそれ、中島が命を落としてしまった。氷室は遺族と中島のうらみを晴らすため暴力団と対決する。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鰯雲", "鰯雲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "成瀬巳喜男監督が初めて手がけたカラー・ワイドスクリーン作品。和田伝の同名小説を社会派の橋本忍が脚色、東京近郊の農村問題を扱っている。女性映画を得意とし、都会の下町を好んで描く成瀬監督としては異色の題材ながら、田園風景の叙情豊かな描写が秀逸だ。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARD TO HOLD", "ハード・ツー・ホールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ロック界のスーパースター、ジェームズは群がってくるグルーピーから逃げる途中、一人の女性と出会う……。住む世界のまったく異なる二人の恋模様がサンフランシスコを舞台に展開するラブ・ストーリー。スプリングフィールドの実像に、主人公をダブらせて見るのも一興。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Las flores del vicio", "狂気の楽園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "アメリカン・ニューシネマの象徴的存在、D・ホッパーが主演するミステリアスなサスペンス。スペインの保養地コスタ・デル・ソルを舞台に、D・ホッパー演じる落ちぶれてヘロイン中毒と化した作家が狂気の世界へ飲み込まれていく。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋愛ズバリ講座", "恋愛ズバリ講座", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "三人の監督が恋愛にまつわる３つのエピソードを綴るオムニバス・ドラマ。浮気性だがケチな会社社長が、美人の女社長に一目惚れ。しかし彼女も相当なケチだった。そんなある日、社長は目をつけていたウェイトレスに現金を取られてしまうが、同じ頃、女社長も秘書に預金を持ち逃げされてしう。金を巡って、二人の社長は虚々実々の駆け引きをするが……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PHOTOGRAPHING FAIRIES", "大人のための残酷童話 妖精写真", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "第一次大戦末期のイギリス。結婚間もない最愛の妻を雪山で亡くし、生きる気力を失った写真家チャールズの元へ、妖精が写ったという怪しげな写真が持ち込まれてくる。ガチガチの現実主義者のチャールズだったが、綿密な鑑定によってこれがトリック写真ではないと確信した彼は、撮影現場の森に向かう。妖精を現世と死後の世界を結ぶ使者と見なしたユニークな視点のラブ・ファンタジー。妖精探しの過程で、主人公と妻が愛し合ったエロティックな記憶のイメージが挿入され、失われた愛ゆえに我を見失っていく男の痛切な心情が生々しく表現されている。1998年の英国映画祭で「フォトグラフィング・フェアリーズ」のタイトルで上映された。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LADY AND THE HIGHWAYMAN", "ヒュー・グラントの浪漫騎士〈ロマンナイト〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "チャールズ二世がイギリス国王に返り咲いた1658年。地方ではまだ悪辣な権力争いが続いていた。そんな中で、義賊シルバー・ブレードが現れる。正義の騎士の存在を快く思わない貴族たち、シルバー・ブレードに惹かれる令嬢など、善玉・悪役、美女・悪女が入り乱れてのロマンティック・アドベンチャー。甘いマスクのH・グラントが、劇画そのままの中世の騎士に扮する。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBIN HOOD", "ロビン・フッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "おなじみ森の義賊ロビン・フッドの物語をディズニーがすべてのキャラクターを動物に置きかえアニメ化。暴君のライオン、プリンス・ジョンはシャーウッドの森の住民に重税をかけ始めた。税金を払えない森の動物たちのため、キツネのロビン・フッドは相棒のクマのリトル・ジョンとともに立ち上がる……。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・花も嵐も寅次郎", "男はつらいよ・花も嵐も寅次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "大分で知り合った気の弱い青年・三郎は動物園の飼育係。同宿したOLの蛍子に一目惚れしたがアタックできない。そこで我らが寅さんの恋愛指南となる。沢田と田中はその後実生活でも愛を育てていくが、この共演が知り合うきっかけとなった。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ときめきに死す", "ときめきに死す", "", "", "http://ecx.images-amazon.com/images/I/21VBETQB81L._SL210_.jpg", 105, "歌舞伎町の医者を自称する男（杉浦）は、謎の組織から若い男（沢田）の世話を引き受ける。男は暗殺者で、暗殺指令を待っていた。田舎町の別荘で暮らす2人に、ある日、組織から若い女（樋口）が派遣される。3人の共同生活が始まり、その中で女はやがて暗殺者を愛するように。しかしそれも束の間、暗殺司令が下り、暗殺者は犯行予定地へ向かう。丸山健二の同名小説を基に、大幅にストーリーを改変し、森田芳光らしいセリフの構成、空間の処理などが施された優れた異色作。", "1984-02-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fail-Safe", "未知への飛行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "　コンピュータの故障によって誤った指令を受けた米軍爆撃機がソ連へ侵入。制限地点（フェイル・セイフ）を越えた爆撃機は帰還命令を拒否し、米ソの追撃機も振り切って、ついにモスクワに水爆を投下してしまう。やがてアメリカ大統領は苦渋の決断をすることに……。社会派シドニー・ルメット監督が世界壊滅の危機を描いたポリティカル・サスペンスの力作。大統領執務室や軍の司令部といった密室で繰り広げられる会話劇が緊張を生む。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人の砂漠", "人の砂漠", "", "", "http://ecx.images-amazon.com/images/I/61i2sBQHaXL._SL210_.jpg", 121, "ノンフィクション作家・沢木耕太郎が1970年代に書きあげたルポタージュ集を題材に、東京藝術大学映像研究科の4人の監督が映像化したオムニバス映画。強制移転を拒否する屑屋のオヤジ、町中を騙した70歳の女詐欺師、餓死寸前にゴミ屋敷で発見された老女、家族にも男にも見捨てられた元売春婦など、実在した人物たちの驚くべき生き様が描かれる。キャストには石橋蓮司、夏木マリ、室井滋、小池栄子ら個性派がそろう。", "2010-02-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MYSTERY TRAIN", "ミステリー・トレイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ロックンロールの町、メンフィスのホテルに3組の旅人が現れる一夜を3話オムニバスで描く。夜汽車が走り、エルヴィスの<ブルー・ムーン>が流れる夜が明け、一発の銃声が響く……。ベニーニ夫人のニコレッタ・ブラスキ、スティーブ・ブシェミのほかスクリーミン・ジェイ・ホーキンスやジョー・ストラマーなど異色の顔合わせに、工藤夕起と永瀬正敏の主演が日本でも話題になったジャームッシュ監督の第4作。JVCが出資。", "1989-12-23", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OXFORD BLUES", "オックスフォード・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ネバダ大学の学生ニック。彼の夢は雑誌で見て一目惚れのイギリスの貴族のお嬢様ヴィクトリアをモノにすること。うまいこと彼女の通うオックスフォード大に留学、目指すマドンナにアタックする。R・ロウがブリティッシュ・トラッドに挑戦した、海を越えた青春映画。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春残酷物語", "青春残酷物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "女子高生の真琴と陽子。街へ遊びに繰り出した帰りはいつも車を持つ男たちを誘い、家まで送らせていた。しかし、ある日、真琴は外車に乗る中年男にホテルへ連れ込まれそうになる。そこへ大学生の清が現われ、中年男を殴り倒し真琴を助けるが、二人の出会いはやがて破滅へと導かれる……。激しいラブシーン、即物的な描写などの衝撃が、松竹ヌーヴェル・ヴァーグという言葉を生み、興行的にも大ヒットした大島渚監督の長編第2作。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("皮ジャン反抗族", "皮ジャン反抗族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ディスコで女王然とした女の親衛隊とスケバン・グループの喧嘩をたまたま仲裁したばっかりに女に追いまわされ、やがては嫉妬から刺されることになる主人公の自動車修理工。オートバイを通しての友情や、主人公の恋人への想いがさわやかに描かれた青春映画。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY DOG SKIP", "マイ・ドッグ・スキップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "W・モリスが少年時代の思い出を綴ったベストセラー小説を映画化。孤独な少年と子犬の交流をのどかな風景の中に描いたハートフル・ストーリーだ。1942年ミシシッピー州の田舎町。８歳の少年ウィリーは内気でいつもいじめられてばかり。そんな彼が、母から誕生日に犬をプレゼントしてもらって以来、孤独から一転。父との確執や友情、淡い恋など、様々な経験を犬のスキップとともに分かち合い成長していく。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE NIGHT STAND", "ワン・ナイト・スタンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "19XX年、世界は一触即発の危機的状況に瀕していた。米軍基地のあるシドニーに住む若者４人が、核戦争勃発前夜に短すぎる過去を語り合い、一夜だけの恋をする……。核戦争が現実になった時の恐怖を描いたオーストラリア映画。ごく平凡な日常が、突然奪われる恐怖を実感させられる。", "1985-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAMPBELL’S KINGDOM", "キャンベル渓谷の激闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "カナダ領のロッキー山中で、死んだ祖父が発見したという油田を求めて英国青年キャンベルは現地へ出かける。だが土地のダム建設のボスはことあるごとに作業を妨害し、キャンベルの邪魔をする。H・イネスの冒険小説の映画化。", "1957-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELLRAISER III：HELL ON EARTH", "ヘルレイザー3", "", "", "http://ecx.images-amazon.com/images/I/512YhcMtN%2BL._SL210_.jpg", 93, "今回は、魔道士ピンヘッドの過去とその正体が暴かれ、彼とその前世エリオットが、壮絶な死闘を展開。また、体に埋め込まれたCDを投げ人間を切り裂く“CDヘッド”、ガソリンを手に持ちあらゆるものを焼き尽くす“バービー”など、ニューキャラクターも登場。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兄弟仁義・関東命知らず", "兄弟仁義・関東命知らず", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "大正初期、浅草生まれの竜次は横浜で愚連隊仲間とノミ屋を始め、南部組の組長の息子・政吉と格闘になるが、“人斬り長次”こと稲上長次郎が仲裁に。やがて、政吉と竜次は義兄弟の契りを交わし、悪徳ヤクザ・鳴門組と戦う。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("破れ太鼓", "破れ太鼓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "津田軍平は、戦後の混乱のなかで財をなしたらしい成金土建屋。津田家には、軍平の経営する会社にイヤイヤ勤めている長男、親爺を風刺した『破れ太鼓』を大声で歌いまくっている音楽家志望の次男、父の便を顕微鏡でのぞき回虫の卵を見つけて大喜びしている医学生の三男、『ハムレット』の芝居の練習に熱中している女学生の次女、その相手をさせられてうんざりしている中学生の四男、父の命令で会社の出資者の息子と愛情のない交際を強いられている長女、そして軍平の妻が暮らしている。軍平は無教養で傲慢な、やや時代錯誤的な暴君だった。こんな軍平と家族とが繰り広げる木下惠介得意のユーモア喜劇。日本映画を代表する大俳優、しかもどちらかというと時代劇のイメージの強い阪東妻三郎を、こうした現代ものの喜劇に起用し、見事に成功させてしまう木下の並々ならぬ力量を、本作品は如実に示している。木下惠介というと抒情的な作風で知られるが、こうした大喜劇においてもその才能は際だっている。小林トシ子のデビュー作でもあり、また木下の実弟・木下忠司も次男役で出演。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COME´DIE", "ふたりだけの舞台", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "男は新しい妻を連れて別荘へ行く。もちろんそこで二人の愛を確かめるはずだったのだが、女は別荘のそこかしこに染み込んでいる、彼の昔の女たちの残り香が気になって仕方ない。そして彼女はヒステリックに彼の愛を確かめようとする。愛の不毛をとことん探求する作家ドワイヨンの魅力的な一編。スーションが作詞作曲した主題歌を、彼とバーキンがデュエットしている。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FERN GULLY ： THE LAST RAINFOREST", "グリーンガリー 永遠の熱帯雨林", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "熱帯雨林のパラダイスに住む妖精たちが、人間によって破壊されていく熱帯林を守り、闘っていくというファンタスティックなメルヘン・アニメーション。ディズニー・アニメで経験豊かなスタッフが、15歳のチャーミングな妖精クリスタを中心とするロマンティックな世界をスクリーンいっぱいに展開する。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VICE ACADEMY", "バイス・アカデミー L.A.風俗特捜隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ディディ、ホリー、ショウニーはバイス・アカデミー訓練生。女鬼教官デボンシャイアのもと、日夜激しい訓練に挑んでいる。しかし落ちこぼれの彼女たちは、なかなか卒業できず悩んでいた。そんなある日、彼女たちは未成年のポルノ女優に会い、ある事件の解決を依頼される。ここぞとばかりに張り切る３人であったが……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHUCK NORRIS：WALKER，TEXAS RANGER", "チャック・ノリス　テキサスレンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "仲間を銀行強盗の凶弾で失った“テキサス・レンジャー”のウォーカーは、復讐を決意して新入りレンジャーのトリベティと組み、犯人の行方を追う。だが、二人はたちまち衝突。単独で調査を再開するウォーカーだったが、犯人たちに先回りされ、事務所を爆破されてしまった……。おなじみ肉体派アクションのC・ノリスが、今回はキック・ボクシングも披露してくれる。TVフィーチャー作品。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUEEN CHRISTINA", "クリスチナ女王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "スウェーデンの女王クリスチナは、城を抜け出しては男装をして旅をするのが好きな自由奔放な性格。とある村の宿で、クリスチナを女性と知らず相部屋になった若きスペイン大使は彼女と恋に落ちる。女王の地位を捨て、彼とともに旅に出ようと決心するクリスチナだが、彼女の王位を狙う大臣の手により、大使は殺害されてしまう。最愛の恋人を失ったクリスチナは復讐の炎を胸に、大臣一味に単身立ち向かう……。スウェーデンからハリウッドに渡り、銀幕の女王に君臨したG・ガルボが、故郷を舞台に男まさりの気丈な女傑を演じる。最もガルボ本人に近いキャラクターとして大評判になった彼女の代表作。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("傷物語", "傷物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ファンタスティポ", "ファンタスティポ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「ピカ☆ンチ」をはじめ次々にヒット作を飛ばす“ジェイ・ストーム”映画の第４弾。これまでの直球の青春映画路線とは外れて、変わり者の兄弟が可笑しくも真摯に、家族、自由、愛の意味を探し求める異色作。ブラックユーモア満載のシュールなコメディ・タッチで描かれた青年の再生物語だ。父親から会社を継いだ息子のトラジとハイジ。二人は意外にも天才的な経営手腕を発揮、その後も成功は続くかと思われたが……。", "2005-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風流深川唄", "風流深川唄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「蟹工船」「黒い潮」などで演出にも非凡な才を示した俳優・山村聰の監督作。富岡八幡宮の祭礼に沸く、古き良き時代の東京・深川を舞台に、大料亭の暖簾を守るためにつらい別れを決意した若い男女の姿を描いている。日本映画の黄金期を担った名優たちが、大挙出演している点も見逃せない。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千刀萬里追／DYNASTY", "空飛ぶ十字剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "17世紀、明朝時代の中国。王朝を牛耳る宦官ツァオは自分の地位に固執するあまり、天子の弟と、彼をかくまっていた修業僧たちを虐殺。難を逃れた青年タンは、家族を殺された恨みを胸に、ツァオへの復讐に挑む。劇場公開時、“ウルトラ・キュービック方式”と呼ばれる立体映像が話題となった、香港製の時代活劇。かたびらが変化した十字剣をはじめ、ユニークな武器が次々と登場。", "1977-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蕾の眺め", "蕾の眺め", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "早坂暁の初の成人映画脚本、そして“ピンキー”こと今陽子のポルノ出演と、話題を集めたにっかつのロマンポルノ大作。陥没した乳首を持つストリッパーと、彼女に絡む二人の男を通して、女の持つ母性愛とその強さ、はかなさが数々と流れる歌謡曲とともに語られている。監督の田中登は久々のにっかつ演出。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOMETHING WICKED THIS WAY COMES", "何かが道をやってくる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ブラッドベリの詩情に満ちた同名の幻想小説を、ディズニー・プロがファンタジーとして映画化。10月のある夜、イリノイ州の小さな町グリーンタウンに、季節外れのカーニバルがやって来る。夜中にこっそりとカーニバルに忍び込んだ二人の少年ウィルとジムは、町の大人たちがフラフラとやって来るのを目撃する。彼らは魔法のメリーゴーランドに乗って、若返ろうとしていたのだ。少年たちは、父親やカーニバルの秘密を知る避雷針売りとともに、魔術師である団長に立ち向かっていく。秋の日の寂しさ、父と子の会話、夢のようなカーニバルと、不思議なノスタルジーを漂わせた逸品。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Drive Angry 3D", "ドライブ・アングリー3D", "", "", "http://image.eiga.k-img.com/images/movie/56082/315x210.jpg?4591", 101, "ニコラス・ケイジ主演の3Dアクションスリラー。娘を殺され、孫娘を誘拐された男ミルトン（ケイジ）が復讐のために地獄から生還し、残忍なカルト教団や彼らを操る悪魔と戦う姿を描く。監督は「ブラッディ・バレンタイン 3D」のパトリック・ルシエ。共演にアンバー・ハード、ウィリアム・フィットナー、デビッド・モース。", "2011-08-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜汽車の女", "夜汽車の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "シュールな映像美で注目された日活ロマンポルノ初期の秀作。つつましやかで母性的な姉の裕美。わがままで母の不義の子である妹の冴子。この美しく異常なほど仲のよい姉妹が、姉の婚約者の出現で近親相姦的心中を遂げる。様々なオブジェの挿入による鮮烈な映像表現は田中監督独自の世界。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪太郎", "悪太郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "時は大正初期、悪太郎の異名をとり次々に転校を繰り返す青年、紺野東吾の青春を描いた鈴木清順の佳作。このあと、清純美学の立役者となる美術の木村威夫が、スタッフに初参加。小道具となる巻き紙の手紙を木村自らが書くなど、こだわりの美学の片鱗が見られる。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BOTTOM OF THE BOTTLE", "脱獄囚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "フランスの探偵小説作家G・シムノンの『瓶の底』を、ウエスタンを得意とするH・ハサウェイ監督がメキシコ国境を舞台に映画化した異色アクション。ノガレスの町の大牧場主パトリックのもとに、弟のドナルドが現れた。彼は殺人罪で服役中のところを脱獄してきたのだが……。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOMCATS", "トムキャッツ　恋のハメハメ猛レース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "独身でいることを誓った若者たちの騒動を描く青春コメディ。友人の結婚式で新郎の姿に幻滅したプレイボーイ８人組が、最後まで独身だった者が賞金を獲得する賭けを始める。７年間積み立てをし、今では賞金は50万ドル。最後に残ったマイケルとカイルは、賞金を勝ち取るため、相手を出し抜こうと壮絶な闘いを繰り広げる。ビデオ、DVDタイトルは「爆走!!トムキャッツ　天下無敵のプレイボーイ同盟」。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ONE THAT GOT AWAY", "ガルフ・ウォー　スカッドミサイル爆破指令", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "湾岸戦争を舞台にしたアクション。イラク領内にあるスカッドミサイル発射基地の破壊を命じられた英軍特殊部隊の８人のエキスパート。彼らがイラク兵士らと激しい戦いを繰り広げながら、任務を遂行する様をドキュメンタリー・タッチで描く。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZAPROSZENIF", "招待", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "老女医アンナは、戦死したと思っていた夫と40年ぶりに出会う。夫に語る悲惨な戦争体験＝ナチスの強制収容所生活を通じ、今なお癒えることのない戦争の傷跡が鮮明に浮かび上がる。再びキナ臭さたち込める現代に、平和への祈りを込めて打ち鳴らす“警鐘”である。", "1985-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("交渉人　真下正義", "交渉人　真下正義", "", "", "http://image.eiga.k-img.com/images/movie/1522/315x210.jpg?4382", 127, "大ヒット・シリーズ「踊る大捜査線」の中心的キャラクターのひとり、真下正義を主人公にしたスピン・オフ。03年の劇場版第2作「踊る大捜査線2　レインボーブリッジを封鎖せよ」で、警視庁初の交渉人となった真下正義警視。その1年後、地下鉄の実験車両が何者かに乗っ取られて地下鉄網を暴走するという事件が勃発。この事件を引き起こした犯人は、交渉の窓口に真下を指名する。シリーズの常連登場人物たちが多数登場。", "2005-05-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANACONDA", "アナコンダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "文化人類学者ケイルは伝説のインディオを発見しようと、撮影隊を率いてアマゾンへやって来た。一行は船でアマゾン川の支流ネグロ川を溯っていく途中、ボートが座礁して困っていたサローンという男を同乗させる。数日後、ケイルは猛毒をもつ蜂に襲われ、意識不明に陥った。サローンは病院への近道だという支流に船を進めるが、それは幻の巨大アナコンダの生け捕りを計画する彼の罠だった。そして、ついに乗組員の一人がアナコンダの犠牲になってしまう……。「山猫は眠らない」のL・ロッサが、アナコンダと人間の死闘を描いた動物パニック映画。最新CG技術を駆使して造形された全長14メートルのアナコンダが水陸をうねり、人を襲う様がリアルに再現され、恐怖を盛り上げる。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Polar Express", "ポーラー・エクスプレス", "", "", "http://image.eiga.k-img.com/images/movie/1602/315x210.jpg?3980", 100, "クリス・バン・オールズバーグの名作絵本「急行『北極号』」を「バック・トゥ・ザ・フューチャー」シリーズのロバート・ゼメキス監督がフルCGでアニメ映画化。少年がクリスマス・イブの夜、サンタクロースの住む北極点に向かう列車で冒険の旅へ。パフォーマンス・キャプチャーという技術で、俳優の表情と動作を全方位から記録、CGI製キャラに反映させて作成。この技法でトム・ハンクスが主人公の少年、車掌などの5役を担当。", "2004-11-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crossing Over", "正義のゆくえ I.C.E.特別捜査官", "", "", "http://image.eiga.k-img.com/images/movie/53902/315x210.jpg?1766", 113, "「インディ・ジョーンズ」シリーズのハリソン・フォードが移民局の捜査官を演じる社会派ドラマ。新鋭ウェイン・クラマーが監督と脚本を担当。ロサンゼルスで密入国者や不法就労者を取り締まる機関I.C.E.（移民税関捜査局）に所属するマックスは、不法者への対応が人道的過ぎると同僚からからかわれていた。そんなある日、同僚捜査官の妹が殺害され、遺品の中から偽造グリーンカードを発見したマックスは独自に調査を開始するのだが……。", "2009-09-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAMES AND THE GIANT PEACH", "ジャイアント・ピーチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "T・バートンが「ナイトメアー・ビフォア・クリスマス」のスタッフとともに作り上げたストップモーション・アニメ作品。突然、暴走したサイにより両親を殺されてしまった９歳のジェームズ少年は、冷酷な二人の叔母に預けられる。ひどい仕打ちに耐えるジェームズの前に、ある日、見知らぬ老人が現れ、緑色の不思議なものを与える。それを食べると素晴らしいことが起こるというのだが、ジェームズは誤って、庭の桃の木の根元にそれを落としてしまう。すると、桃の実がどんどん大きくなり、とうとう直径６メートルにもなってしまう。その桃の実をジェームズが少しかじると、巨大桃にトンネルが現れ、ジェームズは引き込まれるように入っていく。そこには巨大な６匹の虫たちがいて、ジェームズに一緒に旅に出ようと誘う。そしてその目的地は、夢の叶う街、ニューヨークだった。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SHEIK", "シーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アラビア旅行を企てサハラ砂漠へとやって来た美しいイギリス人女性ダイアナ。アラビア人の酋長（シーク）は彼女を一目見て気に入り、なんとか手に入れようとするが……。エキゾチックな雰囲気のなかに綴られるロマンス。サイレント時代の二枚目スター、R・ヴァレンチノの当たり役となった作品であり、この作品以降、彼のニックネームが“シーク”となったことでも知られる。", "1921-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ありがとう", "ありがとう", "", "", "http://image.eiga.k-img.com/images/movie/33752/315x210.jpg?4738", 126, "阪神淡路大震災を背景に、還暦を目前にしてプロテストに合格した実在の現役プロゴルファー、古市忠夫の姿を描いた感動ドラマ。1995年1月17日未明、激しい揺れが神戸市を襲った。もともと消防団のボランティア活動をしていた古市は、家も財産も失いながら街の復興に奔走する。そんなある日、古市は自分のゴルフバッグが無傷で残っていたことに運命を感じ、ゴルフのプロテスト挑戦を決意する。主演は「どついたるねん」の赤井英和。", "2006-11-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小判鮫（愛憎七変化）（総集編）", "小判鮫（愛憎七変化）（総集編）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "離れ島の役人の息子・百太郎は、無実の罪で流刑となった万兵衛と出会い、真犯人を暴き、行方不明になった万兵衛の息子・千太郎を見つけ出そうと江戸に向かう。万兵衛を陥れたのは、商売仇に雇われた伝助。その伝助の娘・お蘭は、役者となった千太郎に恋をする身であった……。長谷川一夫が百太郎と千太郎の二役を演じる。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Invasion", "インベージョン", "", "", "http://image.eiga.k-img.com/images/movie/34152/315x210.jpg?1497", 99, "「ボディ・スナッチャー／恐怖の街」（56）、「SF／ボディ・スナッチャー」（78）などに続くジャック・フィニイの古典SF小説「盗まれた街」、4度目の映画化。精神科医キャロルの周囲で、ある日を境に人々が感情を失った“何か”に変貌していく。その症状は瞬く間に伝染し、いまだ感情を残す人々を襲い始める。キャロルは愛する息子を守るために、恋人の医師ベンらとともに原因究明を急ぐが……。", "2007-10-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Charlie Wilson's War", "チャーリー・ウィルソンズ・ウォー", "", "", "http://image.eiga.k-img.com/images/movie/53242/315x210.jpg?2102", 101, "79年のソ連軍によるアフガン侵攻に対するゲリラ組織の支援を行った米下院議員チャーリー・ウィルソンの奮闘をシニカルな視点で描いた戦争風刺ドラマ。出演はトム・ハンクス、ジュリア・ロバーツ、フィリップ・シーモア・ホフマンら3人のオスカー俳優の他にエイミー・アダムス。脚色は「アメリカン・プレジデント」「ア・フュー・グッドメン」のアーロン・ソーキン。監督は「卒業」「パーフェクト・カップル」のマイク・ニコルズ。", "2008-05-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA GRITETA", "新リバイアサン／リフト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "スペイン アカデミー賞最優秀特殊効果賞を受賞したSFX深海ホラー。ノルウェー沖で消息を絶った潜水艦を救助すべく組織されたクルーたちが、深海で遭遇する正体不明の怪物の恐怖を描いていく。クルーの艦長役はL・アーメイ。ピーター・ウェラー主演の「リバイアサン」とは何の関係もない。", "1989-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パニック4ROOMS", "パニック4ROOMS", "", "", "http://image.eiga.k-img.com/images/movie/54452/315x210.jpg?3166", 0, "主に演劇界で活躍する4名の脚本家が、東京・下北沢の古アパートを舞台に描くシュールな群像コメディ。中学生のハリ子は人気上昇中の売れっ子マジシャンで、収入も右肩上がり。そこでハリ子の母親は、遺産で受け継いだ古アパートを取り壊し、マンションを建てようと計画するが、そこには立ち退きに応じない4組の住人が残っていた。母親はハリ子の人気を利用し、住人たちを立ち退かせようとするのだが……。", "2009-04-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ファー・イースト・ベイビーズ", "ファー・イースト・ベイビーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "日本のパンク・シーンを素材にドキュメントを撮り続けた安田潤司の、初の長編劇映画。南の島で平穏に暮らす青年のもとに突然、現れた昔の仲間３人組。暴力によって関係を保つ彼らの奇妙な交流を、イメージ・ショットを織り交ぜて描く。現実と虚構が交錯する不思議なムードと、シャープな語り口が魅力。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lourdes", "ルルドの泉で", "", "", "http://image.eiga.k-img.com/images/movie/57242/315x210.jpg?4296", 99, "フランス南西部ピレネー山脈の麓に位置し、「聖母マリア出現の地」「奇跡の水が湧き出る地」として知られるルルドを舞台に、奇跡が起こった女性とそれを目の当たりにした人々の間とで起こる人間模様をサスペンスフルに描くドラマ。不治の病で車椅子生活を送っていたクリスティーヌがルルドの泉を訪れると、次第に体が動くようになり、歩くことができるまでに回復した。しかし、さほど熱心な信仰心ももっていなかったクリスティーヌに奇跡が起こったことで、周囲には羨望と嫉妬の感情が渦巻き……。2009年・第66回ベネチア国際映画祭で国際批評家連盟賞ほか5部門を受賞した。", "2011-12-23", "77,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Texasville", "ラスト・ショー2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "1971年度アカデミー賞２部門に輝いた、青春映画名編の、19年ぶりの続編。時代設定は、前作から33年後の1984年になっている。かつての多感な青年、デューエンとサニーも、今や中年。デューエンは、経営難の石油会社を抱えながら、女性たちと浮名を流し、サニーは市長に収まったが、過去の思い出に生きる男だった。そこへ、彼らの幼なじみのジェイシーが、美しい大人の女性として故郷、アナリーンへ帰ってきた。彼女の帰郷は、明日の見えないデューエンとサニーを変えていき……。前作と同じスタッフ、キャストによる、大人たちの黄昏めいた日々を描いた佳作。デューエンの妻、カーラに扮したA・ポッツの好演が光る。ビデオ発売時タイトルは「新 ラスト・ショー」。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子宝騒動", "子宝騒動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 34, "チャップリンそっくりの口ひげをつけ、赤ん坊をおぶったねんねこ姿の主人公の悪戦苦闘ぶりを、ナンセンスなギャグ満載で描いた傑作無声喜劇。貧乏子だくさんの夫婦が７人目の赤ん坊の出産費用の工面に四苦八苦。そんな折、褒賞金が懸けられた“極東随一の名豚”の話を耳にした夫が一大奮起、トン走中のブタを追って追って追いまくる……。“喜劇の王様”と呼ばれた斎藤寅次郎監督による数多くの無声喜劇のほとんどが散逸してしまった中で、本作のみが唯一残っているという貴重な作品。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Chronicles of Narnia: The Witch, The Lion and The Wardrobe", "ナルニア国物語　第1章：ライオンと魔女", "", "", "http://ecx.images-amazon.com/images/I/61loZHQTtgL._SL210_.jpg", 140, "世界中で愛されている名作ファンタジー「ナルニア国ものがたり」をディズニーが映画化。半馬半人のケンタウロスや半山羊半人のフォーンなど伝説上の生物たちが住み、動物たちが人間の言葉で話す不思議の国ナルニアで、白い魔女の支配から住民たちを救うため、少年少女の冒険が繰り広げられていく。特殊メイクや特殊造型は「ロード・オブ・ザ・リング」のSFX工房WETA。監督は「シュレック」シリーズのアンドリュー・アダムソン。", "2006-03-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天残地缺／THE CRIPPLED HEAVEN", "ミラクル・カンフー 阿修羅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "残忍な悪玉リンの罠に落ち両腕を奪われたリー・ホー。チャンはリンの部下だったが怒りを買い両足を奪われる。そして苦行修練の末、手のない者、足のない者が一体となってリンを倒す。これを実際に両手両足のないカンフーの達人が演じる。", "1980-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("満城尽帯黄金甲", "王妃の紋章", "", "", "http://image.eiga.k-img.com/images/movie/53342/315x210.jpg?2248", 114, "「HERO」「LOVERS」に続くチャン・イーモウ監督の歴史アクション大作第3弾。豪華絢爛な王宮を舞台に、ある王族の愛憎劇を描く。10世紀の中国、五代十国時代。栄華を極める後唐王朝の王宮に、遠征に出ていた王や第2王子が帰還し、久々に一族が顔を揃える。しかし王と王妃は互いに憎みあい、3人の皇子もまた、それぞれ胸に秘めた思いを抱いていた……。主演の王と王妃はチョウ・ユンファとコン・リー。また、台湾の人気歌手ジェイ・チョウが第2王子を演じる。", "2008-04-12", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勝利者", "勝利者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "当時売り出し中の石原裕次郎が、若いボクサーに扮したボクシング映画。かつてチャンピオンを目指して失敗した男が、自分の夢を実現するために若いボクサーをスカウトして、見事にチャンピオンに仕上げる。小林旭が、北原三枝のバレエ・シーンに観客としてワン・カット出演。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Firewall", "ファイヤーウォール", "", "", "http://image.eiga.k-img.com/images/movie/1392/315x210.jpg?3340", 106, "万全を期したセキュリティ・システムの弱点はそれを管理する人間だという今日的なドラマが展開する犯罪サスペンス。監督リチャード・ロンクレインは、本作では悪役を演じるポール・ベタニーが、落ち目のテニスプレーヤーを演じたラブコメ「ウィンブルドン」の監督。「サイドウェイ」のバージニア・マドセン、「24 TWENTY FOUR」のメアリー・リン・ライスカブ、「ターミネーター2」のロバート・パトリックらが共演。", "2006-04-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Frostbiten", "フロストバイト", "", "", "http://image.eiga.k-img.com/images/movie/34092/315x210.jpg?1428", 96, "太陽の昇らない極夜の闇に閉ざされた北極圏の町で繰り広げられる惨劇を描いた、スウェーデン発のバンパイア・ホラー。雪に覆われたラップランド地方の小さな町に引越してきた女医のアニカと娘のサガ。新しい生活を始めた2人だったが、アニカは自分の働く病院に邪悪なものを感じる。やがて、この町で不可解な事件が続発するようになり……。サガに扮するのは「ロッタちゃん」シリーズで主人公を演じ人気を集めたグレーテ・ハブネショルド。", "2007-07-28", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いい湯だな全員集合!!", "いい湯だな全員集合!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "銀行強盗の汚名を着せられ世間の冷たい視線にさらされた長吉をリーダーとする５人は、人生を太く短く生きようとハードボイルド学を特訓。やがて彼らは北海道のホテルに雇われるが、女主人を殺して財産と美人の養女を手に入れようと……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sound Horizon 5th Anniversary Movie “Across The Horizon”", "Sound Horizon 5th Anniversary Movie “Across The Horizon”", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "サウンドクリエイターのRevoを中心とした音楽ユニット「Sound Horizon」の5周年を記念して製作された劇場版。2009年に全国8カ所、計13公演が行われた「Sound Horizon Live Tour 2009 第三次領土拡大遠征」のファイナル公演「第三次領土拡大遠征凱旋記念『国王生誕祭』」の2日間の模様を劇場上映用に再編集した。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TOY", "おもちゃがくれた愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "フランシス・ヴェベール監督の映画「LE JOUET」をもとにしたアメリカ映画。ジャーナリスト志望だが失業中の黒人青年と、金には恵まれているが、人間の愛を知らずに育った少年の心のふれあいを描く。“愛とは人を愛することで手に入れるもの”というあたりまえの言葉が、新鮮な響きを持って伝わるヒューマン・メルヘン。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FIGHTING SEABEES", "血戦奇襲部隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1944年の南方での日本軍とアメリカ軍の戦いを描く。建設会社社長のウェインは部下である建設技術大隊を指揮して滑走路造りの大任を負う。しかし正規軍人と意見衝突し……。大戦中も銃後で映画に出演し続けたウェインが好んで選んだ戦意高揚映画の１本で、後年の作品に通じる豪快なリーダー役を演じている。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("One Missed Call", "ワン・ミス・コール", "", "", "http://image.eiga.k-img.com/images/movie/53262/315x210.jpg?4322", 88, "女子大生ベスの周囲で友人たちが相次いで変死を遂げる。友人たちはいずれも死の数日前に携帯電話に奇妙な着信を受け、留守電に残された自分の悲鳴を聞いていた。妹を亡くした刑事アンドリュースと共に謎の解明を急ぐベス。しかし彼女までもが着信を受けてしまい……。柴咲コウ主演、三池崇史監督によるJホラー「着信アリ」をハリウッド・リメイク。フランス人監督エリック・バレットがメガホンを取り、「カタコンベ」のシャニン・ソサモンが主演を務める。", "2008-07-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crash", "クラッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "　倦怠期にあったジェームズと妻キャサリンはハイウェイで衝突事故を起こす。やがてジェームズはその事故で夫を亡くしたヘレンと再会。事故の瞬間にエクスタシーを感じ、それを忘れられなかったジェームズはヘレンとセックスをし、さらに自動車事故で性的快感を得た者たちによる集会に参加することに。その指導者的存在の男ボーンはジェームズとキャサリンをさらなる官能の世界に導いていくが……。自動車事故をきっかけに倒錯的セックスにのめりこむようになった男女の姿を描く衝撃作。", "1996-01-01", "10,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東邪西毒／ASHES OF TIME", "楽園の瑕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「欲望の翼」以上のオールスター・キャストを投入しながら、完成まで数年を要したW・カーウァイの苦心作。伝統的な武侠映画をベースに置きながらも、監督独自のリミックス感覚で時空を越える編集を施している。砂漠で暮らす孤独な殺し屋の元締め“西毒”ことオウ・ヤンホウと、彼の親友“東邪”ことウォン・ヤオシーの悲恋の数々が物語の中心。とはいえ、登場人物は雑多で、時間の流れも空間の据え方も無限の広がりを見せていく。考えるのではなく感じる、筋を追うのではなく空気に触れる、そんな風に楽しみたい一編。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE OUTRAGE", "暴行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "悪名高いメキシコ人の強盗カラスコが旅行中の紳士を刺し殺し、その妻を犯したという事件の裁判が開かれる。しかし、カラスコ、妻、瀕死の紳士から事情を聞いた老インディアンの証言はそれぞれ違っていた。カラスコは堂々と決闘の末彼を殺したといい、妻は自分が殺したといい、さらにインディアンは彼は自ら死んだのだと証言した。カラスコは縛り首になるが、真実はそんな人間的なものではなく、もっと醜い、本能的なものだった。黒澤明監督の「羅生門」を西部劇に置きかえた作品。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍虎門", "かちこみ！ドラゴン・タイガー・ゲート", "", "", "http://image.eiga.k-img.com/images/movie/33922/315x210.jpg?1280", 94, "香港で国民的人気を誇るコミックを映画化したアクション・ムービー。道場“龍虎門”で育ったタイガーは、悪の秘密結社“江湖”の一味となった生き別れの兄ドラゴンと再会する。やがて、巨大犯罪組織“羅刹門”によって江湖のボス・マーと龍虎門の師ウォンが殺され、2人は復讐を誓う。「HERO」のドニー・イェン、「PROMISE」のニコラス・ツェー、「インファナル・アフェア」シリーズのショーン・ユーらが壮絶なアクションを繰り広げる。", "2007-04-14", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THEY WERE EXPENDABLE", "コレヒドール戦記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "巨匠フォードの数多い戦争映画歴のなかでも、独特の情感漂う１本。コレヒドール没落後、太平洋戦争初期の戦闘をPTボートの活躍を中心に描く。原題にあるように“消耗品である彼ら”兵士たちのやりきれない悲しみが、胸を打つ。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GENDARME A｀ NEW YORK", "ルイ・ド・フュネス　ニューヨーク大混戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "フュネス主演のポリス・コメディのシリーズ第２作。主人公の巡査部長が、ニューヨークでの国際警察会議に出席。だが彼の娘ニコルの密航に端を発する大騒動がもち上がる。フランスとアメリカのお国柄の違いが巻き起こす笑いが盛り込まれた娯楽編。", "1965-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニュータウン物語", "ニュータウン物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "高度成長期、日本各地に建設され、現在は街の空洞化などの問題で転換期を迎えている“ニュータウン” にスポットを当てたドキュメンタリー。監督は内部告発した科学者を描いた「科学者として」で注目を集めた気鋭の映像作家、本田孝義。美術家、映像批評家としても活躍する彼が、自分の育った岡山のニュータウンを訪れ、両親や同級生、恩師らを取材。現在に至るニュータウンの歩みを検証していく。個人的な記録でありつつも、街の現在と過去の実態が鮮明に浮かび上がる興味深い１作だ。1970年代に岡山市郊外に造成されたニュータウン・岡山県山陽団地。そこで４歳から18歳まで過ごした映画監督・本田孝義は、家族や同級生にインタビューを行い、自らの思いを重ねながらニュータウンの歩みをたどる。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHAT'S NEW， PUSSYCAT？", "何かいいことないか子猫チャン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "スラップスティック・コメディの要素も入ったラブコメの快作。「アラビアのロレンス」とは正反対の演技で魅せるオトゥールとセラーズの掛け合いに加え、W・アレンのオリジナル脚本とコミカル演技での出演も見どころ。婚約者がいるにも関わらず女にモテまくる花形雑誌の編集長が、友達の精神科教授が狙う女にちょっかいを出したことから、とんでもない珍騒動に巻き込まれていく。", "1965-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIGHT OF DAY", "愛と栄光への日々／ライト・オブ・デイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "オハイオ州クリーブランド。町の工場に勤めるジョーは、夜はクラブで姉と組んでロックバンドのギタリストとして活躍している。しかし心が痛むのは、ツッパリの姉と母のいさかいだった……。元ランナウェイズのJ・ジェットの熱演が光る一編。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版デュエル・マスターズ　黒月の神帝　ルナティック・ゴッド・サーガ", "劇場版デュエル・マスターズ　黒月の神帝　ルナティック・ゴッド・サーガ", "", "", "http://image.eiga.k-img.com/images/movie/54312/315x210.jpg?4376", 0, "対戦型トレーディングカードゲームの劇場版アニメ。切札勝舞（きりふだしょうぶ）の持つ神を呼び出すことが出来るカードが、謎のデュエリスト・神月ミカドに狙われる。全てのデュエリストに恨みをもち、4体神を操るミカドとの壮絶なデュエルが繰り広げられる。", "2009-09-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Un conte de Noel", "クリスマス・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/55342/315x210.jpg?1546", 150, "カトリーヌ・ドヌーブ、マチュー・アマルリック、エマニュエル・ドゥボス共演の家族ドラマ。クリスマスを祝うため、フランス・ルーベにあるビュイヤール家に家族が集まってくる。母ジュノンの病気をきっかけに、疎遠になっていた子供たちも遠方から駆けつけるが、“役立たずのアンリ”の登場で一家のクリスマスが台無しになってしまう。監督は「そして、僕は恋をする」「キングス＆クイーン」のアルノー・デプレシャン。", "2010-11-20", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Redacted", "リダクテッド　真実の価値", "", "", "http://image.eiga.k-img.com/images/movie/53652/315x210.jpg?4332", 90, "「カジュアリティーズ」でベトナム戦争での集団強姦殺人を描いたブライアン・デ・パルマ監督が、2006年にイラクで実際に起こった米兵による14歳少女のレイプ、及び彼女を含む家族4人を惨殺した事件を題材にした衝撃作。ドキュメンタリータッチの斬新な映像で、戦争の残虐性と情報操作の闇に迫る。", "2008-10-25", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿修羅／SAGA OF THE PHOENIX", "孔雀王 アシュラ伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "日本と香港合作の「孔雀王」シリーズの第２作。人間界で一週間だけ生活することを許された魔少女アシュラをめぐって、彼女を守る者と奪おうとする者との戦いが繰り広げられていく。新しく孔雀を演じる阿部寛のさわやかなアクションが魅力的だ。日本とは別編集による香港広東語バージョンもあり、ユン・ピョウが主演している。", "1990-01-01", "6,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("放送禁止　劇場版　密着68日復讐執行人", "放送禁止　劇場版　密着68日復讐執行人", "", "", "http://image.eiga.k-img.com/images/movie/53872/315x210.jpg?3229", 0, "深夜TVシリーズ「放送禁止」を映画化したフェイクドキュメンタリー。監督はTVシリーズに引き続き長江俊和。復讐を請け負う闇ウェブサイト「シエロ」の管理人ノラムを追っていた取材スタッフは、ノラムの提案で復讐の密着取材を行うことに。結婚詐欺のホステスや医療ミスを起こした医者、スクープを狙うジャーナリストへの復讐を取材するスタッフだったが……。", "2008-09-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KNIGHT", "サイボーグ・キラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "冷酷なサイボーグに支配された世界。虐殺から生き延びた女戦士が善玉サイボーグとともに、殺された家族の仇を討つべく敵地へと乗り込んでいく。「バットマン・リターンズ」でミシェル・ファイファーのスタントを務めたマーシャル・アーツの達人K・ロングが、ハードな立ちまわりを見せるSFアクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風のある道", "風のある道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "川端康成の原作を西河克己が演出したメロドラマ。直子には華道矢田流の後継者・光介という恋人がいたが、結婚には踏みきれなかった。そんな折、精薄児収容所みどり学園の教師・小林と知り合い、飾り気のない人格に惹かれていく。アメリカ映画「或る夜の出来事」風のラストが気持ちいい。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドンマイ", "ドンマイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "父を亡くした少年が、母親とともに温かい下町の人情に支えられて生きていく様を、少年が所属する野球チーム“ちどりボーイズ”の熱戦話を絡めて描いた青春ドラマ。カット・バックを使わず、球が投手の手を離れ、捕手のミットに収まるまでをワン・カットで描いた野球シーンなど、地道な演出で見せる。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SECRET LIFE OF WALTER MITTY", "虹を掴む男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ゴールドウィンの秘蔵っ子だったD・ケイの日本初公開作品。ユーモリストのJ・サーバーの原作を得て、夢見る主人公ウォールター・ミティ青年にD・ケイが扮している。想像の世界に遊ぶこの男が、ある時は帆船の船長、そしてある時は空軍の撃墜王、はたまたある時は賭博師になるなどして、奇想天外な物語が展開する。D・ケイの多芸さが光り輝く一編。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美人図", "美人図", "", "", "http://image.eiga.k-img.com/images/movie/55622/315x210.jpg?4526", 109, "18世紀末に実在した天才絵師シン・ユンボクが女性だったらという仮説をもとに描くラブストーリー。「僕の、世界の中心は、君だ」（2006）のチョン・ユンス監督がメガホンをとり、主演のキム・ギュリ（キム・ミンソン）とキム・ナムギルが今作をきっかけに大ブレイクした。4代続く宮廷絵師の家に生まれたユンボクは、幼いころから絵画の腕を称えられていたが、ある日、自ら命を絶ってしまう。実はユンボクの絵を描いていたのは7歳の妹だったため、その日からユンボクと名乗り、男として生きていくことになる。しかし少女へと成長したユンボクは、鏡職人のガンムと恋に落ち、一気に女として目覚めていく。", "2010-09-25", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURDER，INC.", "殺人会社", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "表向きは衣料会社、実は暗黒街を牛耳る男の隠れみのという“殺人会社”を背景にした、社会派の暗黒映画。羽振りのいいナイトクラブの乗っ取りをたくらむ暗黒街のボスは、殺し屋を雇い入れ、クラブの所有者を始末させるが……。マフィアに震える人々と殺し屋たちの暗躍を冷徹なタッチで描き出しており、殺し屋の一人をP・フォークが演じる。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兄貴の恋人", "兄貴の恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "当時、東宝青春映画の看板娘であった内藤洋子と酒井和歌子との共演が見ものの作品。女子大生の節子は、兄貴・鉄平のことになると細かいことまで世話を焼きたがる。特に女性のことを聞くと目の色が変わる。ある日、鉄平は上司の勧めで金持ちで美人の緑に出会い、仲良くなるが、その時から急に会社を辞めた元同僚の和子のことが気がかりに。意を決して和子にプロポーズする鉄平。しかし、和子はかたくなに鉄平の申し出を断る。和子に嫉妬し反対していた節子だが、兄の真剣さに打たれ、和子を説得するようになる。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ASTRAGALE", "ある日アンヌは", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "19歳のアンヌは未成年売春の罪で刑務所に入っていたが脱走。やがて同じ前科者のジュリアンと恋に落ちるが……。ある若い女性が数奇な運命を経て、汚辱にまみれながらも純愛を貫く姿をエロチシズム豊かに描いた異色作。原作はフランスでベストセラーとなったサラザンの自伝小説。", "1966-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("屋根裏の散歩者", "屋根裏の散歩者", "", "", "http://image.eiga.k-img.com/images/movie/30442/315x210.jpg?3151", 87, "江戸川乱歩の傑作「屋根裏の散歩者」を、「村の写真集」の三原光尋監督が映画化。舞台を現代に、主人公を女性に置き換えて原作に大胆な解釈を加え、乱歩による猟奇的・耽美的な世界を再現する。今は亡き画家・郷田三郎を取材するため、彼が生前に暮らしていた東栄館を訪れた編集者の富岡奈緒子。郷田が好きだったという屋根裏に上った彼女は“散歩者”となり、館の住人たちが繰り広げる惨劇を目撃してしまう……。", "2007-07-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENDURING LOVE", "Jの悲劇", "", "", "http://ecx.images-amazon.com/images/I/51BFZA0NVTL._SL210_.jpg", 101, "衝撃的なストーリーが話題を呼んだベストセラーを原作に、「ノッティングヒルの恋人」のロジャー・ミッチェル監督が、赤を象徴的に使った美しい映像で魅せた心理サスペンス。突然の悲劇の事故により、愛の罠にはまってしまった男女の心理が巧妙に綴られる。草原でピクニックをするジョーとクレアの前に気球が落下。ジョーは近くにいた男たちと救助に当たったが、一人が転落死してしまう。やがて罪悪感を引きずるジョーに、事故現場で出会った青年ジェッドがまとわりついてきて……。", "2005-11-19", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ДРЕВО ЖЕЛАНИЯ", "希望の樹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "革命前のグルジア、コーカサス地方の小さな村が舞台。新しい時代の訪れをその身に感じながら、なお残る村の古い慣習のなかで、自らの愛を貫き、誠実に生きようとする男と女……。コーカサスの谷間にめぐる四季を捉えた造形的で表現豊かな映像など、監督やスタッフの故郷の山河に対する愛情、自由と希望を謳い上げる詩的精神が見事に映像化された、現代グルジア映画の重鎮アブラーゼの代表作。", "1977-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("友を送る歌", "友を送る歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "父の病気により、親友より２年遅れて横浜港へやって来た船乗り志望の青年・良夫。快活な少女みどりと知り合った彼は、二人で親友の玄一を捜すことにするが、やっと見つけた玄一は密輸の片棒を担いで生活していた。友情や恋が熱くほとばしる青春ヒューマン・ストーリー。アイドルの言葉ができる前の映画だが、アイドル映画ノリも。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Matthew Barney: No Restraint", "マシューバーニー：拘束ナシ", "", "", "http://image.eiga.k-img.com/images/movie/53882/315x210.jpg?3028", 71, "アメリカを代表する現代芸術家マシュー・バーニーが、公私に渡るパートナーである歌手ビョークとの共演で制作した映画「拘束のドローイング9」の裏側に迫ったドキュメンタリー。女性ドキュメンタリー作家アリソン・チャーニックが日本で撮影された同作の撮影に密着、バーニーとビョークの人間味あふれる素顔を捉えると共に、美術関係者へのインタビューや過去の作品を通し、現代美術界のカリスマの魅力を余すことなく紹介する。", "2008-10-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Great Escape", "大脱走", "", "", "http://ecx.images-amazon.com/images/I/51QSsd8XEKL._SL210_.jpg", 165, "史実を基にしたポール・ブリックヒルの同名小説を、スティーブ・マックィーン、チャールズ・ブロンソン、ジェームズ・コバーンらオールスターキャストで映画化した戦争ドラマ。第2次大戦末期、ドイツにある絶対に脱走不可能といわれた捕虜収容所で、連合軍兵士250人の大量脱走計画が持ち上がる。連合軍兵士とナチス軍のせめぎ合いや集団脱走の顛末が壮大なスケールで描かれる。監督は「荒野の七人」の巨匠ジョン・スタージェス。共演にリチャード・アッテンボロー、ジェームズ・ガーナー。", "1963-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("廓育ち", "廓育ち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "川野彰子の同名小説を佐藤純彌監督が映画化した文芸作品。６歳の時に実母に捨てられ、お茶屋の女将・お仙の養女として育てられたたみ子は、色町の空気になじめないまま芸者に仕立てられた。遊女と侮られないよう懸命に生きる彼女だったが、希望はことごとく破れ去っていく……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRAND PRIX REQUIEM", "グッバイ・ヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "モーター・スポーツの花形“F1グランプリ”のレース模様を、余すことなくカメラに収めたドキュメンタリー作品。主として1976年から1986年にスポットを当て、37年間に及ぶ歴史の中で繰り広げられた名勝負から、アクシデントによるレーサーたちの死の瞬間まで克明に捉えている。", "1987-01-01", "76,1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("71 Into the Fire", "戦火の中へ", "", "", "http://image.eiga.k-img.com/images/movie/55752/315x210.jpg?3338", 121, "1950年の朝鮮戦争を捨て身で戦い抜いた、韓国の学徒兵たちの壮絶なドラマを描く戦争アクション大作。韓国軍司令部が置かれた浦項女子中学校の守備を任されることになった71人の学徒兵たち。物静かな青年ジャンボムは指揮官に任命されるが、気弱な自分が大役を任されたことに戸惑いを隠しきれずにいた。必死に任を果たそうとするが、問題児のガプチョらが規律を乱す言動を繰り返し……。", "2011-02-19", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO METEORO VIMA TOU PELARGOU", "こうのとり、たちずさんで", "", "", "http://ecx.images-amazon.com/images/I/41J8Z8CVQGL._SL210_.jpg", 142, "ひとりのTVディレクターが取材の途中で失踪中の大物政治家を見かけるが、政治家夫人も彼も本人であると認めようとはしない。彼はなぜ落ちぶれた別人生を生きているのか。ディレクターはカメラでその姿を執拗に追うが……。アンゲロプロス監督が“国境”というテーマに正面から取り組んだ作品。川を挟んでの無言の結婚式のシーンのためにダムまで作ったという逸話や、宗教団体の撮影妨害の報に世界の映画人がエールを送って完成したという経緯が、主演したマストロヤンニ、ジャンヌ・モローとともに世界規模で話題になった1本。", "1992-09-19", "27,2,26,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPUTERO’SU　MIO　PADRE−NEROLIO−", "パゾリーニ・スキャンダル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1975年、撲殺という形で、その生涯を閉じたイタリアの詩人にして映画監督のピエル・パオロ・パゾリーニの死の実像に迫るドキュメンタリー風作品。物語は、パゾリーニを模した旅する詩人を追う３つのエピソードで構成されている。モノクロによる画面が映画により深みを持たせている。ロカルノ映画祭でプレミア上映され、話題となった。", "1996-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白と黒", "白と黒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "弁護士・宗方治正の妻が殺され、不審尋問にかかった前科４犯の脇田が殺人を自白した。死刑廃止論者である宗方は、若手弁護士の浜野と組んで脇田の弁護を買って出るが、真犯人は、浜野自身であった。そして捜査が進むうち、事件は意外な方向に……。社会派で知られる橋本忍のオリジナル脚本を、堀川弘通が監督した推理ドラマ。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLICE ACADEMY3: BACK IN TRAINING", "ポリスアカデミー3 全員再訓練！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "母校“ポリスアカデミー”廃校の危機に立ち上がるOBたち、ダメ・ポリス軍団の破天荒ぶりを描く爆笑コメディ第３作。原作者ニール・イズラエルとパット・プロフトの創造したキャラクターを、２作目と同じパリスが監督。この作品が彼の遺作となった。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Antique", "アンティーク　西洋骨董洋菓子店", "", "", "http://image.eiga.k-img.com/images/movie/54182/315x210.jpg?2061", 111, "TVドラマ＆アニメ化されて話題を呼んだよしながふみ原作のコミックを韓国で映画化。一流企業を突然退社したジニョクは、小さな洋菓子店“アンティーク”を開店。パティシエの腕は一流だが男性関係のトラブルで店を転々としてきた“魔性のゲイ”ソヌら3人の仲間も加わり、アンティークは評判の店となっていく。そんなある日、町で連続誘拐事件が発生し……。主人公ジョニク役に韓国ドラマ「魔王」でブレイクしたチュ・ジフン。", "2009-04-18", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎サラダ記念日", "男はつらいよ・寅次郎サラダ記念日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "シリーズ20年目にして、40本目の作品。俵万智のベストセラー『サラダ記念日』にヒントを得て作られた寅さんの大人の恋物語。寅は信州で出会った美しい女医さんに一目惚れ。柴又にもどった寅は、さっそく早稲田大学に通う彼女の姪を訪ねる。今回のマドンナは三田佳子。短歌好きの女子大生に三田寛子が扮し、俵万智の作品を詠ませている。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チー公物語 ネズミ小僧のつくりかた　　世紀末版", "チー公物語 ネズミ小僧のつくりかた　　世紀末版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "借金取りに誘われて、詐欺師まがいの商売で金もうけをするグループに加わった青年と、そのグループの個性的な面々の奮闘を描いたシニカル・コメディ。主人公の青年に唐十郎の息子・大鶴義丹が扮する他、柄本明、平田満、高品格らが独特の個性を発揮して、“チー公”グループの面々を好演。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ОБЫКНОВЕННЫЙ ФАШИЗМ／ORDINARY　FACISM", "野獣たちのバラード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "ソビエト国立映画学校で教鞭をとり、優れた人材を世に送り出したM・ロンム監督によるドキュメンタリー。ナチス・ドイツを素材にファシズムの姿を暴いていく。全部で16の章で構成されており、ナチス・ドイツが宣伝用に使ったフィルムやニュースリールなどを利用しながら、市民の延長線上にあるファシズム（原題は“ありふれたファシズム”）が立ち現れてくる仕掛けとなっている。", "1965-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CHARGE AT FEATHER RIVER", "フェザー河の襲撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "G・ダグラス監督が３D映画として演出したウエスタン。南北戦争終結直後、各地へ鉄道が敷設されようとしていたころ、それに反抗を示すインディアンのシャイン族と第12騎兵隊との激戦を描いていく。３D映画らしく、インディアンの矢が画面を見る側に向けて飛んでくるカットが多い。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヨコハマBJブルース", "ヨコハマBJブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "工藤栄一監督・松田優作主演によるハードボイルド・アクション。横浜のうらぶれたバーでブルースを歌う探偵BJが、麻薬取引に絡んだ暴力団の抗争に巻き込まれる。横浜ロケが効果をあげ、劇中で松田優作が数曲渋いノドを聞かせてくれる。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEVIL SHIP PIRATES", "海賊船悪魔号", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "1588年。スペイン艦隊は英国艦隊に敗れ去り、スペインの海賊船ディアブロ号は英国領の入江に立ち寄る。キャプテン・ロベレスは村の住民を“我が国に敗れた英国は我々の掌中にある”とだまし、海賊たちを受け入れる準備にかからせた。だが勇敢な村人ハリーらが激しく抵抗する。記録映画出身のD・シャープが監督した海洋アクション。", "1964-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛情物語", "愛情物語", "", "", "http://ecx.images-amazon.com/images/I/51HbqD5YCdL._SL210_.jpg", 101, "ミュージカル・ダンサーを目指す少女が、幼い頃から毎年、誕生日にバラの花束を送ってくれる“あしながおじさん”を探して旅に出るという角川春樹監督によるミュージカル映画。劇中劇『カーテン・コール』をはじめ、７曲のダンス・ナンバーを盛り込んだ。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BREAK　OUT　SPARK　THE　LIGHTER", "ライターをつけろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "韓国で130万人の観客を動員したノンストップ列車パニック・ムービー。TVドラマ『ホテリアー』で人気を集めたキム・スンウが、最初は冴えない無職の男を演じながらも、徐々に勇敢なヒーローになって活躍する姿を描く。ソウル駅で唯一の所持品であるライターを忘れた男ボングは、それを拾ったギャングのボス、チョルゴンを追うことに。だがチョルゴンはその列車をハイジャックしようと企んでいた。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十階のモスキート", "十階のモスキート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "崔洋一監督の骨太なデビュー作。「水のないプール」に続いて内田裕也が企画した実録犯罪ものの第２作にあたる。離婚した現職警官が、慰謝料、子供の養育費などのためにサラ金から金を借りる。しかし、支払いが滞り、交番にまで催促の電話がかかってくるようになる。悩んだ末に男は郵便局強盗をするのだが……。主人公の姿を執拗に追いかけるカメラワークが堕ちていく男の姿を見事に捉え、内田裕也も初の脚本とは思えないほどの堂々とした語り口だ。歌手デビュー前の小泉今日子が竹の子族姿で特別出演している。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白昼堂々", "白昼堂々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "九州の筑豊に実在した“泥棒村”を題材にした作品で、炭鉱が閉鎖されたため昔の稼業“スリ”を再開。仲間を集めて一大万引集団を形成し、彼らが日本全国で“成果”を上げる様を面白おかしく描く。原作は『週刊朝日』に連載された結城昌治の小説。撮影は野村芳太郎監督と名コンビの川又昂。DVDは「渥美清メモリアル　渥美清・もうひとつの世界」に収録。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDERGROUND U.S.A.", "アンダーグラウンド U.S.A.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "一文無しのヴィクターは、かつてアンダーグラウンドのスターだったビッキーと出会うが……。往年の名作「サンセット大通り」のアンダーグラウンド版。ニューヨークのカルチャー・ムーブメントを描きながら、1980年代インディーズの先駆けとなった作品である。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLAY IT AGAIN，SAM", "ボギー！ 俺も男だ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "原題は「カサブランカ」の中でボギーがピアニストのサムに、『アズ・タイム・ゴーズ・バイ』を弾いてくれと言う時のセリフ。ボギーに憧れるダメな男アランを主人公に、「カサブランカ」のラスト、空港での別れの場面から始まる。映画評を書いているアランはボギーの映画ならセリフを空で言えるくらい繰り返し見ている。だが映画に熱中するあまり、実生活に身が入らず妻ナンシーが家出。友人ディックとリンダ夫婦は心配して女性を紹介するが、デートをしてもボギーのようにはキマらない。やがて親身になってくれるリンダに愛情を抱くが……。W・アレンの妙演が光るファンタジー・コメディ。アレン監督作品「カイロの紫のバラ」との比較も面白い。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAD MISS MANTON", "美人は人殺しがお好き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "社交界のトップレディが、ある晩殺人事件を目撃。警察に通報するが彼女自身が容疑者にされてしまう。八方ふさがりのなか、彼女は自ら真犯人発見に乗り出していくが……。当時のハリウッドが誇る二大スター、フォンダとスタンウィックが共演。特にフォンダがコメディに新境地を拓いた1作。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Mackintosh Man", "マッキントッシュの男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　謎の男マッキントッシュが準備した強盗計画を実行する泥棒リアデン。しかし彼は逮捕されてしまう。その後、ソームズという男がリアデンに近づき、秘密結社スカーペラーの手で脱獄させてやると告げる。条件はソ連のスパイ、スレードの脱獄に手を貸すこと。やがてリアデンは脱獄に成功するが、自分がスカーペラーの組織を暴き、スレード暗殺を目論む諜報部員であることを知られてしまう……。ヨーロッパ・ロケで撮影されたスパイ・サスペンス。", "1973-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浅草の灯", "浅草の灯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "大正時代から戦前にかけての浅草は、様々な見世物小屋が軒を並べ繁華する大娯楽街だった。そんな浅草を愛した浜本浩の新聞連載小説を原作に、神田駿河台生まれの島津保次郎がメガホンを執った一種の風俗映画である。日本座という劇場を舞台に、コーラス・ガール麗子と画学生の恋、その麗子を利用して成金にとり入ろうとするバーのマダム。それが不成功に終わった腹いせに劇団を潰してしまおうとするマダム夫妻の悪だくみなどが展開していく。しかし見るべきものは物語の語り口というよりも、むしろ浅草という街とバックステージの世界の風俗的ディテールの正確な再現であろう。杉村春子が浅草オペラのプリマドンナ役で歌い踊る。助監督は木下惠介。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROVIDENCE", "プロビデンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "プロビデンスという名の館に、病に冒された老作家クライブが横たわっている。彼は自分と家族を題材にした最後の作品を思い描くが、同時に悪夢にもさいなまれる。そして小説と悪夢の世界はクライブの生きる現実と混ざり合っていく……。迫りくる死と創造の苦悩の間に揺れる男の姿を重層的に描く異色作。衣装にイヴ・サンローランが参加していることでも話題になった。仏セザール賞で作品賞、監督賞など６部門を総ナメにした。", "1977-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A TALE OF LOVE", "愛のお話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "アメリカのポスト・フェミニズムの旗手として注目されたT・T・ミンハ監督が、故郷ベトナムに伝わる詩歌をモチーフとした長編処女作。編集やモデルの仕事をしながら家族への仕送りを続ける主人公の日常を描き、アメリカに渡ったベトナム移民の体験を映し出す。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("36 Vues du Pic Saint-Loup", "小さな山のまわりで", "", "", "http://image.eiga.k-img.com/images/movie/54992/315x210.jpg?4630", 85, "フランスの巨匠ジャック・リベットが、小さなサーカス団で織り成される人間模様を描いた物語。とあるサーカス団の団長が夏の巡業の直前に突然この世を去った。かつて一座にいた団長の娘ケイトは、団員たちから頼まれて再加入を決意する。さらに巡業の途中で出会ったイタリア人ビットリオもついてくることになり……。「美しき諍い女」のジェーン・バーキン、「赤いアモーレ」のセルジオ・カステリットらが出演。", "2009-10-19", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画　プリキュアオールスターズDX みんなともだちっ☆奇跡の全員大集合！", "映画　プリキュアオールスターズDX みんなともだちっ☆奇跡の全員大集合！", "", "", "http://image.eiga.k-img.com/images/movie/54242/315x210.jpg?2379", 0, "女の子たちに絶大な人気を誇る「プリキュア」シリーズの劇場版。桃園ラブはダンスコンテストに向かう途中で道に迷ってしまい、そこに街を飲み込もうとしている悪者が現れる。横浜みなとみらいを舞台に、プリキュアたち14人が力を合わせて強敵に挑む。ゲスト声優にお笑い芸人のエド・はるみ。", "2009-03-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("からみ合い", "からみ合い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "東都精密工業社長の河原専造は自分がガンと知り、３億の財産を以前関係のあった女たちが生んだ子供たちに分割することにした。人間の欲望と深層心理の不可解さを描く。背景をできるだけシンボリックに抽象化するという小林正樹監督の実験的な試みが成功している。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROSWELL", "ロズウェル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1947年７月４日、アメリカ・ニューメキシコ州ロズウェルの砂漠に正体不明の物体が墜落した。ロズウェル空軍基地の情報将校であるマーセル少佐とその上司ブランチャード大佐、カーソン将校の３人はその残骸を調査して驚く。その物体は驚異の回復力を持つ、見たこともない素材だった。アメリカ空軍はUFO墜落事故という公式見解を発表。しかしすぐにこの発表は国防省によって取り消され、気象観測気球の墜落事故であったと訂正されたのだった……。長年に渡って論争を呼んできた、UFO事件史上で最も有名なロズウェル事件を映画化。クリーチャー製作は「エクソシスト」「アルタード・ステーツ」を手掛けたディック・スミスが担当している。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RASING FURY", "レイジング・フューリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "おとなしく真面目な女教師を突然襲った悪夢。教え子のツッパリ少年のいやがらせのために、精神的に追い詰められた彼女に、少女時代の忌まわしい記憶がよみがえる。彼女は教え子を解剖すべく、実験用メスを振り下ろす……。スプラッタ・ホラーの学園版。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水戸黄門", "水戸黄門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "諸国を漫遊中の水戸黄門一行は、江戸入りするや高田藩のお家騒動に巻き込まれてしまった。その背後には、藩の筆頭家老はおろか、幕府老中の柳沢吉保らまでもが一枚噛んでいたのだが……。月形龍之介の映画生活30周年を記念して作られた作品。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Singin' in the Rain", "雨に唄えば", "", "", "http://ecx.images-amazon.com/images/I/51BUytVQTlL._SL210_.jpg", 102, "無声映画からトーキーに移行し始めた頃のハリウッド。人気スターのドンとリナは何度も共演し結婚を噂される間柄だが、ドンはつけ上がった態度のリナに愛想を尽かしている。そんなある夜、ドンは歌も踊りも上手い新進女優キャシーと恋に落ちる。その後、ドンとリナの新作がトーキーで製作されることになるが、リナの致命的な悪声のために不評を買ってしまう。そこでドンはリナの声をキャシーに吹き替えて製作することを思い立つ。「踊る大紐育」のジーン・ケリーとスタンリー・ドーネン監督によるミュージカル映画の傑作。", "1953-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Don Giovanni", "World Classics＠CINEMA ドン・ジョヴァンニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 172, "世界各地のオペラやバレエをスクリーンで上映する「World Classics＠CINEMA」の1作として、イギリスが誇るロイヤル・オペラ・ハウスが2008年に上演したモーツァルト作曲のオペラ「ドン・ジョヴァンニ」をデジタルシネマ化。狙った女性の父親を殺害してしまった稀代のプレイボーイ、ドン・ジョバンニの運命を描く。「カタログの歌」「手を取り合って」など、登場人物それぞれに用意された名アリアの数々は必聴。", "2010-05-29", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FANNY OCH ALEXANDER", "ファニーとアレクサンデル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 311, "この作品は、最初から劇場用とTV用の両方を念頭において製作され、アメリカでは３時間８分の劇場用で公開、日本ではファンの要望にこたえて５時間版で上映された。プロローグとエピローグ、５つの章からなる構成で、スウェーデンの伝統的な大学街で暮らすブルジョワ一族エークダール家の人々（かつての大女優ヘレナと３人の息子たち、その妻、孫など）が織り成す人間模様を、1907年のクリスマスから二年間にわたって描いている。タイトルは孫たちの名前。ベルイマンの自伝的要素や宗教の偽善性に対する批判などを含みつつも、肩のこらない娯楽作品として楽しめる。", "1982-01-01", "14,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("隠密秘帖・まぼろし城", "隠密秘帖・まぼろし城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "TVで人気だった隠密シリーズの映画化。飛騨山中にそびえたつ、まぼろし城。ここには徳川打倒をたくらむ小西寿安が、魔力を使うまぼろし妙王とオランダの妖術師髑髏仮面を従えてチャンスをうかがっている。だが、小西一派に立ち向かう隠密剣士がいた。大友柳太朗が５役を早変わりで演じる痛快時代劇。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOMSHODEI　DAR　ARAQ", "わが故郷の歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "自分たちの国家を持たない少数民族クルド。国境に引き裂かれ、数々の戦火にさらされてきた苦難の歴史を、魅力あふれるクルド民謡満載で伝えてくれるロード・ムービー。厳しい現実を見据えながらも、ユーモアを忘れずに希望を描くゴバディ監督の姿勢が感動的。イランで暮らすクルド人の大歌手ミルザのもとに、かつてバンドメンバーと駆け落ちした元妻から“助けて欲しい”と知らせが届く。同じミュージシャンの息子二人をムリヤリ道連れにして、ミルザは戦乱の続くイラクへ旅立つが……。", "2002-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽のない街", "太陽のない街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "昭和20年代後半の独立プロ運動の代表作の一つ。大正15年の共同印刷ストライキを描いたプロレタリア文学の映画化。“太陽のない”長屋に住む印刷工とその家族１万5000人が決起した。会社と警察の弾圧は激化する……。山本薩夫が労働者ヒロイズムをうたい上げた一編。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Zeki,Florian and Kelly！", "Zeki,Florian and Kelly！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "「PICKLED PUNK」の山岡信貴（秀雄）監督、初めての35ミリ作品。死の直前に見るという人間の“走馬灯”を描く野心作だが、前作にあったような衝撃的な描写はやや影を潜め、むしろ特異な時間感覚を中心に据えた穏やかなタッチとなっている。テロリスト４人が、肉体的にも精神的にも自滅していく物語。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("抱かれた花嫁", "抱かれた花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "浅草の老舗“鮨忠”の女将・ふさは、育てあげた長男も次男もまったく店を継ぐ気を見せず、あせりの面持ち。仕方なく長女・和子に養子を取らせようとするのだが……。松竹の大型映画路線“松竹グランドスコープ”(タテ・ヨコ比、1：2.35)の第１作。監督は、本作に代表される大船調喜劇に才を発揮した番匠義彰。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUIEN　PUEDE　MATAR　A　UN　NINO？／WHO　CAN　KILL　A　CHILD？", "ザ・チャイルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "バカンスを過ごすため、ある島に渡った生物学者夫妻。しかし、その島にはなぜか子供の姿しか見かけない。やがて夫妻は、大人たちが子供たちに惨殺される光景を目の当たりにしてしまう……。子供たちの理由なき無気味な反逆を描いたカルト・ホラー。お腹の中の胎児が母親を殺すというショッキングな一幕もある。", "1976-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SAINT’S RETURN", "セイントの帰還", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "“セイント＝聖者”の異名を持つスゴ腕の私立探偵、サイモン・テンプラーの活躍を描くハードボイルド・ストーリー。依頼人の女性の死に始まり、複雑な様相を呈する事件だったが、サイモンは事件の裏に、ギャングの影を見いだす……。警察（スコットランド・ヤード）を出し抜いたり、情け容赦のない捜査ぶりといい、そのオーソドックスなヒーローぶりが心地良い。", "1953-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAZE〈ヘイズ〉", "HAZE〈ヘイズ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 49, "塚本晋也監督による凄まじい“密室”劇。腹部に傷を負った状態で、異様に狭いコンクリート空間に閉じ込められた男の極限の恐怖と、意識の変容を圧倒的な緊迫感で描き出す。塚本監督が小型デジタルカメラの特性を活かして撮影を行い、生と死の境界でもがき苦しむ主人公を自ら熱演している。身体を動かすこともままならない密閉空間で目覚めた男。彼は必死の思いでその場を移動する内に、一人の女とめぐり合うのだが……。", "2006-03-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猫と鰹節", "猫と鰹節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "五人の大物詐欺師たちが“仕事”をする様を面白おかしく描く堀川弘通の本格喜劇。詐欺師が詐欺師をだますというのがこの作品のヤマ場である。森繁、三木、千葉、ミッキー、森川の詐欺団の浮き沈みとアノ手コノ手、口八丁手八丁の詐欺っぷりが大いに笑わせる。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビターコーヒーライフ", "ビターコーヒーライフ", "", "", "http://image.eiga.k-img.com/images/movie/57652/315x210.jpg?1819", 0, "「銭形平次」や「水戸黄門」などで活躍した名脇役で、2011年12月にガンで他界した俳優・入川保則が主演する親子愛を描いたドラマ。余命宣告を受け延命治療を拒否して撮影に参加した入山にとって、本作が遺作となった。元警視庁捜査一課の刑事でいまは喫茶店「ビターコーヒーライフ」を営むマスターは、20年前に逮捕した殺人犯の娘を引き取り、養女として育ててきた。しかし、末期ガンに侵され余命わずかであることを悟ったマスターは、出所後に行方が知れないままの養女の父親を探すため奔走する。", "2012-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デスカッパ", "デスカッパ", "", "", "http://image.eiga.k-img.com/images/movie/55802/315x210.jpg?1498", 0, "「片腕マシンガール」「東京残酷警察」に続く“TOKYO SHOCKシリーズ”第3弾。日本を代表する特殊メイクアーティストの原口智生監督がメガホンをとり、ミニチュア模型を駆使した昔ながらの特撮で怪獣“デスカッパ”とその敵“ハンギョラス”の激闘を描く。「ウルトラマンメビウス」のアマガイ・コノミ役でブレイクした平田弥里が、“デスカッパ”と心を通わせるヒロインを演じる。", "2010-11-27", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUDDENLY", "三人の狙撃者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "白黒映画をコンピュータ技術によってカラー化した作品。アメリカ中西部の小さな町を舞台に、大統領の暗殺を企て高台の家に立てこもった３人の狙撃者と保安官の息詰まる攻防戦を緊迫したサスペンス・タッチで描いたアクション。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stay Tuned", "カウチポテト・アドベンチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "　異次元テレビの中にパパとママが吸い込まれてしまった。それを知った子供たちは何とかしてふたりを助け出そうとするが、機械装置の故障でハチャメチャな状況に。一方、パパとママは不思議でとんでもない番組の世界を次々と体験していくが……。「羊たちの沈黙」「ウェインズ・ワールド」「スタートレック」やクイズ番組のパロディまで詰め込んだ爆笑冒険ファンタジー。アニメの監修を、「ルーニー・トゥーン」を手がけた大御所チャック・ジョーンズが務めた。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GLENN MILLER STORY", "グレン・ミラー物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "第二次大戦中、飛行機事故で死んだスイング・ジャズの人気バンドリーダー、グレン・ミラーの伝記映画。スチュアート＝アリスンのコンビで大ヒット。『ムーンライト・セレナーデ』『真珠の首飾り』『茶色の小瓶』『ペンシルバニア6−5000』などのヒットナンバーが聴ける。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海は見ていた", "海は見ていた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "黒澤明が撮影寸前までいきながら、製作費などの問題で断念した企画を、熊井啓が映画化。江戸時代、深川にある岡場所（幕府非公認の私娼地）を舞台に、一人の若い娼婦を主人公に、たくましく生きぬく女たちの姿を活写した群像ドラマだ。職業柄、客に惚れるのは御法度と言われているのに、恋をしてしまうヒロインを、熊井と「日本の黒い夏・冤罪」で組んだ遠野凪子が好演。愚直なまでにまっすぐ前を向いて生きる姿がすがすがしい。また、そんな彼女を心ならずとも“騙してしまう”若侍を吉岡秀隆が妙演している。クライマックスの大洪水を念頭に置いた熊井の地形的な演出も見事で、黒澤の「一番美しく」や「酔いどれ天使」を彷彿とさせる生命力あふれる作品になっている。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LADDER 49", "炎のメモリアル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "9.11同時多発テロの現場で、自らの命を顧みずに英雄的な活躍を繰り広げた名もなき消防士たちに捧げた感動ドラマ。レスキュー活動に身を投じた一消防士の人生を基軸に、死と隣り合わせの過酷な職務に挑む彼らの勇気や気高さを描く。本物の炎を使って撮影した火災シーンの緊迫感は圧倒的。新米消防士のジャックは、ボルティモア消防署に温かく迎えられる。ほどなくラダー隊に配属されたジョンは、初出動に意気込むが……。", "2005-05-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Life Aquatic with Steve Zissou", "ライフ・アクアティック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「ザ・ロイヤル・テネンバウムズ」のウェス・アンダーソン監督最新作。有名な海洋冒険家で、自らの冒険を映画にする映画監督でもあるスティーブ・ズィスーは、新作の不評にもメゲずいつもの乗組員たちと探査船で海へ。ところが今回は彼の息子だと名乗る青年や女性記者が乗船していて、さまざまなドラマが起きていく。デビッド・ボウイの70年代の名曲の数々を、乗組員のひとりがポルトガル語でボサノバにカバーして歌うのも印象的。", "2005-05-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOMBAY TALKIE", "ボンベイ・トーキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ゲイをテーマにした小説など、常にスキャンダラスな作風で知られる女流作家ルシア。インスピレーションを求めインドを訪れた彼女は、ボンベイの撮影所で人気スター、ビグラムと出会った。互いに惹かれあった二人は狂気の愛へと進んでいくが……。J・アイヴォリーが全編インド・ロケを敢行して描くある愛の物語。", "1970-01-01", "21,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キングコングの逆襲", "キングコングの逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "北極の極点近くに眠る核物質・エレメントXを発掘するため、ロボット怪獣メカニコングを開発したドクター・フー。しかしその計画は失敗し、彼は本物のキングコングを使おうとする……。後半、東京に上陸したキングコングと、それを追ってきたメカニコングとの、東京タワーでの死闘が見もの。この東京タワー、実際に鉄骨を組み上げて作り、重量感あふれる出来になっている。特技監督・円谷英二は、本家「キングコング」（1933・米）への思い入れが強く、コングがゴロザウルスと戦うシーンでは、本家のコングと恐竜との死闘とまったく同じカット割りで撮影した。出演陣では、ドクター・フー役の天本英世がマッド・サイエンティストを巧演。東宝35周年を記念して作られた作品。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("なんとなく、クリスタル", "なんとなく、クリスタル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ミュージシャンと同棲し、大学に通いながらファッション・モデルのアルバイトをし、食べるものも着るものも、すべてブランド志向という若い女の子の典型的日常を追った作品。当時“クリスタル族”の流行語を生んだ、元・長野県知事田中康夫の同名ベストセラー小説の映画化。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Starting Over", "結婚ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "自立しようとする妻から突然離婚を言い渡されたフリーライターが、妻に未練を残しながらも新しい女性との結婚を決意するまでのあわてぶりや悩みを描いたコメディ。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La mala educacion", "バッド・エデュケーション", "", "", "http://image.eiga.k-img.com/images/movie/1272/315x210.jpg?4599", 105, "80年のマドリード。若き人気映画監督エンリケの元に、イグナシオと名乗る青年が映画の脚本を持って現れる。その脚本には、彼らが親友だった少年の頃、寄宿舎で起こったある悲劇が描かれていた。アルモドバル監督が10年以上も企画を温めてきた半自叙伝的物語を、「モーターサイクル・ダイアリーズ」でブレイクしたガエル・ガルシア・ベルナルの主演で映画化。ベルナルが極彩色の衣装でドラッグクイーンぶりも披露。", "2005-04-09", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版TRICK 霊能力者バトルロイヤル", "劇場版TRICK 霊能力者バトルロイヤル", "", "", "http://image.eiga.k-img.com/images/movie/55092/315x210.jpg?2409", 0, "深夜ドラマとして放送され人気を博した「TRICK」シリーズ10周年記念となる劇場版第3弾。主演は仲間由紀恵と阿部寛、監督にドラマ版や劇場版前2作を手がけた堤幸彦。絶大な力を持つ霊能力者・カミハエーリが治める村で、そのカミハエーリが他界する。村には100日以内に次代カミハエーリを決めなければ災いが訪れるという言い伝えがあり、村人たちは後継者を選出するためにバトルロイヤルを開催する。非現実的な風習を廃止しようと考える天才物理学者・上田と、賞金狙いの自称奇術師・奈緒子はバトルロイヤルに参戦するが……。", "2010-05-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Deja Vu", "デジャヴ", "", "", "http://ecx.images-amazon.com/images/I/51EPHgbtt2L._SL210_.jpg", 127, "「パイレーツ・オブ・カリビアン」シリーズなどのヒットメーカー、ジェリー・ブラッカイマー製作によるサスペンス・アクション。フェリー爆破事件の捜査を開始したダグは、現場で遺体となって発見された女性・クレアの顔に奇妙な見覚えを感じる。彼は、政府が極秘で開発した“4日と6時間前”の映像を見ることのできる装置でクレアの過去を調べ始めるが……。オスカー俳優デンゼル・ワシントンが主人公の捜査官ダグを熱演。", "2007-03-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・極道の妻たち 惚れたら地獄", "新・極道の妻たち 惚れたら地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "生命を賭けた行動に出る“極妻”の戦いを、緊迫感たっぷりに描く。病床の組長に代わって、大阪、ミナミの御蔵組を仕切る妻の芙由は土地開発に便乗し、組の地位を固めようとする。だが、敵対する侠和会がこれを阻止せんと暗躍。御蔵組の組長、幹部が次々と殺され、芙由の身にも危険が迫る。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAD ABOUT MAMBO", "マンボ！マンボ！マンボ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "パッとしないがサッカーが大好きな高校生ダニーは、スター選手の“サッカーに必要なものはリズムだ！ ”という言葉を耳にする。マンボを習うことにした彼は教室で出会ったお嬢様に一目惚れし、ライバルもなんのそので必死の練習に励む。そんな彼に思わぬピンチが訪れるが……。ドラマティックな青春ラブ・ストーリー。ビデオ発売時タイトルは「MAD ABOUT MAMBO」。", "2000-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フランダースの犬", "フランダースの犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "世界的に有名な童話であり、日本ではTVアニメーションとして高い人気を誇った感動の物語を劇場用にアニメ化。キャラクター・デザインこそTV時代の名残を残したものの、作画、音楽などをすべて一新し、新たな涙を誘う好編に仕上げている。舞台は19世紀のベルギー、フランダース地方。祖父とともに牛乳配達で生計を立てていた少年ネロと飼い犬パトラッシュがたどる悲劇が、優しく繊細に綴られる。ドラマの冒頭とラストに、20年後のアロアが修道女として登場。その声を鈴木保奈美が演じている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAPPY PEOPLE", "HAPPY PEOPLE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "『ヤングジャンプ』に連載された同名コミックに基づく、全３話オムニバス形式のブラック・コメディ。第１話「いつからか僕は」は、殺人の妄想に憑かれた男が一線を超えるまでの物語。２話「意味もなくみんなで笑おうハッピーに!!」は生徒の暴力に耐えかねた教師の復讐劇。３話「ピアスをはずせ!!」は家族の幸福で描いている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hobbit: An Unexpected Journey", "ホビット　思いがけない冒険", "", "", "http://image.eiga.k-img.com/images/movie/53762/315x210.jpg?3947", 0, "「ロード・オブ・ザ・リング」の前章となるJ・R・R・トールキン著「ホビットの冒険」を映画化した2部作の前編。「ロード・オブ・ザ・リング」より60年前の中つ国（ミドル・アース）を舞台に、指輪の前所有者であるホビット族のビルボ・バギンズが繰り広げる冒険を描く。ドラゴンに支配されたエレボールのドワーフ王国の再建をかけ、旅へ出ることになったビルボは、戦士トーリン・オーケンシールドら13人のドワーフとともに、ゴブリン、オーク、巨大なクモなど危険が待ち受ける荒野を進む。やがて旅の途中でゴラムと出会ったビルボは、中つ国の運命と深く結びついた指輪を手に入れ……。若きビルボを演じるのは、英国の新鋭マーティン・フリーマン。ガンダルフ役のイアン・マッケラン、ゴラム役のアンディ・サーキスら「ロード・オブ・ザ・リング」でもおなじみのキャストが再登場。監督も「ロード・オブ・ザ・リング」3部作のピーター・ジャクソンが務める。", "2012-12-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("下町の太陽", "下町の太陽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "山田洋次の２作目にして初の長編映画（デビュー作の「二階の他人」は60分に満たない実験作だった）。下町、荒川沿いの町工場ではつらつと働く町子。彼女には出世欲に燃える恋人がいたが、だんだんと彼の利己的な生き方についていけなくなる。そんな時、口は悪いがひたむきに生きる青年・良介と出会い、次第に心惹かれていく……。倍賞千恵子の歌う同名大ヒット曲に便乗した歌謡映画だが、下町の庶民の思いを伝える山田洋次らしい生真面目な作品になっている。役者の演出にはこの当時から目を見張るものがあり、特に恋人同士の会話シーンでの表情、仕草の芝居のつけ方は天下一品。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Runaway Jury", "ニューオーリンズ・トライアル", "", "", "http://image.eiga.k-img.com/images/movie/1562/315x210.jpg?3715", 128, "「ペリカン文書」などの人気リーガル・サスペンス作家ジョン・グリシャムの「陪審評決」を、「コレクター」「サウンド・オブ・サイレンス」のゲイリー・フレダー監督が映画化。銃乱射事件の裁判の裏側で、陪審員工作、弁護士の取引きなど熾烈な裏工作合戦が展開していく。下積み時代からの親友のジーン・ハックマンとダスティン・ホフマンが、陪審コンサルタント役と原告側の弁護士役という敵対する役柄で初共演したのも話題。", "2004-01-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("friends after 3.11 劇場版", "friends after 3.11 劇場版", "", "", "http://image.eiga.k-img.com/images/movie/57702/315x210.jpg?3304", 0, "宮城県仙台市出身の岩井俊二監督が、東日本大震災後に再会した友人やツイッターなどを通じて出会った人々と震災後の日本の現在や未来について語り合う姿を追ったドキュメンタリー。脱原発宣言を掲げて話題となった城南信用金庫理事長の吉原毅、京都大学原子炉実験所助教授で「原発のウソ」の著者・小出裕章、環境エネルギー政策研究所の飯田哲也所長、脱原発に向けて奔走する俳優の山本太郎ら、さまざまな立場の人々がそれぞれの思いを打ち明ける。女優の松田美由紀がナビゲーターを務める。", "2012-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブルージーンズメモリー", "ブルージーンズメモリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「スニーカーぶる～す」と同じ監督、脚本のコンビで製作された、たのきんトリオ主演映画の第２作。横浜を舞台に、仲間の一人の祖母が経営する大赤字のホテルを再建すべく、結束していく３人の友情と冒険の日々を描く。埠頭でのミュージカル・シーンが見もの。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROCK−A−DIE BABY", "ロック・ホラー・ベイビー 死霊の子守唄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ある静かな満月の夜。ダダをこねる娘のベッドの脇で、母親が３話のホラー・ストーリーを聞かせている。１話目はインドネシアでベトナム女性を捕らえたアメリカ兵の話。２話目はイタズラ好きの大学生たちが、グズな仲間をだまそうと幽霊を呼び出す。そして３話は互いに秘密を隠し持つ新婚夫婦の物語。すべての話が終わったあと、親子は本物の恐怖を味わうことになる……。R・クックのオムニバス・ホラー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録・安藤昇侠道〈アウトロー〉伝／　烈火", "実録・安藤昇侠道〈アウトロー〉伝／　烈火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "三池崇史が「荒ぶる魂たち」「新・極道の墓場」で組んだ武知鎮典のシナリオを得て作り上げた破天荒な任侠活劇。敵対する組織に組長を殺された男が無謀な復讐に燃え上がりながら、黒幕の敷いた謀略図に巻き込まれていく。武闘派ヤクザが巨大な力に立ち向かうという定番の設定ながら、そこからハミ出す展開や人物造型が実にパワフル。兄弟分、そして女二人と逃亡する主人公が裏社会の中国人から戦争用兵器を手渡された途端、映画は一転。終盤は、組織が差し向けた殺し屋コンビとの決闘模様に肉迫していく。時空を超えた肉弾戦を、謎のフィクサーに扮した丹波哲郎がビシッと締めている。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Play Misty For Me", "恐怖のメロディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "巨匠クリント・イーストウッドが監督デビュー作として手掛けた心理スリラー。米カリフォルニアにある海辺の町でラジオDJをしているデイブ（イーストウッド）は、行きつけのバーで、E・ガーナーの名曲「ミスティ」を毎晩リクエストしてくるエベリンと出会う。デイブは出来心からエベリンと一夜を共にするが、その日からエベリンはデイブにつきまとうようになる……。共演にジェシカ・ウォルター、ドナ・ミルズ。イーストウッドとのコンビで知られるドン・シーゲル監督も友情出演。", "1972-04-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("らんま1／2 決戦桃幻郷！　花嫁を奪りもどせ!!", "らんま1／2 決戦桃幻郷！　花嫁を奪りもどせ!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "劇場２作目となる、高橋留美子原作のドタバタ格闘アニメ。南の島でバカンス中の天道一家。そこで次々に女性がさらわれる怪事件が発生する。犯人は、桃源郷の幻術皇子、トウマ。トウマは生き物を人間の男に変える秘薬を持っていて、乱馬は色めき立つ。さらわれた許嫁、あかねと薬を手に入れるため、乱馬は桃源郷に乗り込んでいくが……。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POCIAG", "夜行列車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "恋人との別れを決意して旅に出た傷心の女と逃亡中の殺人犯……。列車には人生の重荷を背負った様々な人々が乗り合わせている。ワルシャワからバルチック海沿岸の町に向かう列車の中で、人それぞれの人生模様をメロドラマ仕立てで描く、カワレロウィッチの秀作。", "1959-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Southland Tales", "サウスランド・テイルズ", "", "", "http://ecx.images-amazon.com/images/I/51PDmyK8xuL._SL210_.jpg", 144, "「ドニー・ダーコ」のリチャード・ケリー監督が2008年の独立記念日のロサンゼルスを舞台に描くコメディ群像劇。嗜眠症のアクション・スターや、リアリティTV番組を持とうと目論むポルノ女優などが登場。", "2006-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空の少年兵", "空の少年兵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 37, "霞ヶ浦海軍飛行予科練習生たちが受験を経て入隊し、猛特訓に励む姿を収めたドキュメンタリー。文部大臣賞を受賞しながらも製作サイドの意向に反して一部カットを余儀なくされ、アメリカに原版を没収されたという逸話も残っている。なおDVDには「勝利の基礎〈いしずえ〉」が併録されている。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vicky Cristina Barcelona", "それでも恋するバルセロナ", "", "", "http://image.eiga.k-img.com/images/movie/53992/315x210.jpg?3171", 96, "婚約者もいる真面目なビッキーと、情熱的な恋を求める自由奔放なクリスティーナ。親友同士の2人は、夏のバカンスを過ごすバルセロナで魅力的な画家フアン・アントニオに出会い、それぞれ彼に惹かれていく。しかし、そこへ画家の美しい元妻が現れ……。ウッディ・アレン監督が、スカーレット・ヨハンソン、ハビエル・バルデムを主演にスペインで撮影したロマンス・コメディで、ペネロペ・クルスが第81回アカデミー助演女優賞を受賞。", "2009-06-27", "1,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Thursday's Children", "木曜日の子供たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 21, "聴覚障害児のための学校に通う子どもたちの姿を捉えた短編ドキュメンタリー作品。「ifもしも……」「オー！ラッキーマン」等の作品で知られるリンゼイ・アーダーソンと、ガイ・ブレントンが共同でメガホンを取る。またナレーションを「史上最大の作戦」「イグアナの夜」等の作品で世界的に認められる前の、イギリス出身の名優リチャード・バートンが担当している。1954年度、アカデミー短編ドキュメンタリー賞を受賞。", "1954-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SALOME", "サロメ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「カルメン」「タンゴ」などのスペインの名匠カルロス・サウラが、オスカー・ワイルドの有名な戯曲を、虚実入り交じった舞踏劇として映画化。大胆な色彩設計、光と影のコントラストが織り成す幻想的な映像の中に、王女サロメの狂おしい愛の物語が繰り広げられていく。妖艶な踊りを披露する主演女優は、世界的なフラメンコ・ダンサー、アイーダ・ゴメス。ダンサーのアイーダは舞台のリハーサル中に“私は現実が好きじゃない。踊っているほうが好きだ”と語る。やがてリハーサルが始まり、王女サロメを踊るアイーダ。サロメは義父ヘロデ王に寵愛されていたが、洗礼者ヨハネに恋こがれ……。", "2000-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'ARGENT", "ラルジャン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "1枚のニセ札が、普通の人々の人生をどん底まで転落させていくさまを描く、名匠ブレッソンの遺作。高校生から写真機店の店員へ、そしてガソリン配達員へ、ニセ札が巡ってきてしまった彼らは次第に犯罪の道へと堕ちていく。投獄され家族も失った元配達員は自暴自棄で娑婆に戻り、さらなる凶悪犯罪へとつき進む。そして次なる彼の標的は、ひとりで家族の面倒を見ている善意の老婆だった……。", "1986-11-29", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOSTILE INTENTIONS", "逃走国境　狙われた女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "リラ、モリーン、キャロラインの３人は、メキシコへと旅立つ。楽しい旅のはずが、クラブでトラブルに巻き込まれ留置所へ。そこで、警官にレイプされ、怒ったリラは警官二人を射殺してしまう。人間不信に陥った３人は、留置されていた殺人容疑者デルガードとともに逃走する。警察は彼女らを執拗に追い回し、壮絶なカーチェイスが繰り広げられる。セクシーな美女３人がナイスバディを惜しげもなく披露。追いつ追われつのアクションを展開する。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES BIJOUTIERS DU CLAIR DE LUNE", "月夜の宝石", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "偶然ある男を助けたことから彼の運命に巻き込まれ、数奇な道をたどることになる娘の姿を、スペインの美しい土地を背景に描くスリリングな愛欲のドラマ。監督・脚本はR・ヴァディム。主役の娘には当時のヴァディム夫人、B・バルドーが扮している。", "1958-01-01", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤胴鈴之助 飛鳥流真空斬り", "赤胴鈴之助 飛鳥流真空斬り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "鈴之助は、剣聖・大島赤心斎に真空斬りの秘太刀を教えてもらうべく、富士山麓の赤心斎の庵に向かう。しかし、“武芸の道は教わって上達するものではなく、自分の心で磨くものだ”と弟子入りを許されない。そんなある日、赤心斎を暗殺せんとする悪党一味を鈴之助がくいとめ、ついに、彼は弟子入りを果たす。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGEL 車椅子の夏", "ANGEL 車椅子の夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "怪我のため車椅子生活を余儀なくされた女性に訪れる変化を描いたドラマ。広告代理店で働く明子は仕事も人間関係も充実して自信に満ち溢れていたが、自らの不注意でアキレス腱を断裂する怪我を負ってしまう。入院や手術で当分の間は車椅子生活を送らなければならなくなり、さまざまな不自由を身をもって経験した明子は少しずつ変化していく。名古屋市立大学芸術工学部映像研究室のゼミ生が手がけた作品。アイドルグループ「AKB48」の田名部生来が看護師役で出演。", "2011-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Iron Sky", "アイアン・スカイ", "", "", "http://image.eiga.k-img.com/images/movie/58042/315x210.jpg?2974", 93, "第2次世界大戦での敗北後、月に逃げ延びていたナチスの残党が、UFOの大群を率いて地球を侵略するSFアクション。", "2012-09-30", "20,7,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shantaram", "シャンタラム（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GATE II", "ゲート２ デモンボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "パリ国際ファンタスティック映画祭で最優秀SFX賞に輝いた「ザ・ゲート」の続編。前作から２年経ち、15歳になった主人公の少年は再び悪霊学に興味を持ち始め、廃墟で古代神を呼び戻す儀式を行う。現れた小鬼ガバリアンは彼らの願いをかなえる力を備えていたが……。魔界の謎と恐怖を描くSFXアドベンチャー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Family", "ファミリー", "", "", "http://image.eiga.k-img.com/images/movie/33762/315x210.jpg?4746", 96, "父娘の深い絆を描き、韓国で200万人を動員した感動のヒューマンドラマ。3年の刑期を終えて出所し、父と弟が暮らす家に戻ってきたジョンウン。父はそんな彼女に厳しい言葉を投げかける。やがて彼女はかつての不良仲間と接触するが……。韓国テレビドラマの“涙の女王”と呼ばれるスエが主人公ジョンウンを熱演。「友へ チング」のチュ・ヒョンが父に、「青春漫画 僕らの恋愛シナリオ」の名子役パク・チビンが弟に扮する。", "2006-12-02", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Catch.44", "キリング・ショット", "", "", "http://image.eiga.k-img.com/images/movie/57102/315x210.jpg?2720", 94, "ブルース・ウィリス、フォレスト・ウィテカー、「ウォッチメン」のマリン・アッカーマンが共演するクライムサスペンス。女強盗のテスは、仲間のドーン、カラを連れて夜のラスベガスの古びたダイナーにやってくる。マフィアのボス・メルから、メルのシマを荒らす連中の正体を暴くよう依頼されていたテスは、ダイナーの女主人の口を割らせようとするが、ダイナーの奥から現れたコックのビリーが「お前たちを殺せばメルから金がもらえる」とライフルをつきつけてくる。そこへ警官姿の謎の男も現れて現場は三つ巴の様相を呈し、事態は混乱していく。", "2012-04-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Varjoja paratiisissa", "パラダイスの夕暮れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "仲間の死によって独立の道を断たれた、ゴミ収集車の運転手ニカンデル。スーパーのレジ係イロナに恋をした彼は、彼女をデートに誘ったものの、ビンゴ会場に連れて行ってしまい大失敗。ところが、仕事をクビになったイロナが彼のもとに転がり込んでくる。2人の関係はうまくいったかのように見えたが……。アキ・カウリスマキ監督の長編第3作。主演は、カウリスマキ作品には欠かせないマッティ・ペロンパーとカティ・オウティネン。", "1986-01-01", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("B-ON 不良全滅篇", "B-ON 不良全滅篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "不良男子高校生たちの戦いを描いた「ビーオン」シリーズ第3作。経営不振に陥った蘭城高校が東下高校に合併され、偏差値の低い蘭城高校生たちは東下のエリート教師に囲まれてすっかり孤立してしまう。そんなある日、不良たちが次々とある人物につぶされていく事件が発生……。", "2010-11-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛のぬくもり", "愛のぬくもり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "学者でまじめ一筋の中上は、バーで出会ったリナに一目惚れする。リナと官能の一夜を過ごした中上は彼女にマンションを買い与え、外出を禁止した。しかし彼女は遊び歩き、やがてマンションに帰らなくなる。中上はすべてを投げ捨てて、リナを探し出しもう一度暮らし始める。「嘆きの天使」の和製ポルノ版といった趣。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOWLING VI：THE　FREAKS", "ハウリングVI ～突然変異体～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ジョー・ダンテ監督の「ハウリング」から続く、現代版狼男シリーズの第６作。狼男である青年を悪の存在としてではなく、孤独で悲しい運命を持つものとして描く。狼男と対決するのは怪しげなフリークス集団とそれを率いる男＝吸血鬼。狼男の変身シーンはもちろんのこと、奇怪なフリークスたちの特殊メイクなども見もの。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("疾風小僧", "疾風小僧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "放浪好きのハイティーン、サブを主人公にした和田浩治主演の“小僧”シリーズの第２作。旭川にやって来たサブは、世話を受けたキャバレーの社長で、暴力団の組長でもある笠井が何者かに殺される現場に遭遇する。東京の黒須組が笠井組を狙っていたという情報を聞き、黒須の身内になりすますサブだったが……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRIGANDS，　CHAPITRE　VII", "群盗、第七章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "中世、現代、グルジア、パリなど、時空間と場所が目まぐるしくシンクロする構成の中で、人々の連帯と人間賛歌を謳いあげる。着想の面白さを流れるようなカメラ構図で見せるユニークな史劇スタイル。戦争を繰り返す権力者と、圧政に苦しみながらも懸命に生きる庶民の姿を、時制をバラバラにして伝える。中世の王が現代ではホームレスになる設定など随所に仕掛けが。", "1996-01-01", "2,26,4,24,68", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PEOPLE THAT TIME FORGEET", "続・恐竜の島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "前作が、全米で予想外のヒットを記録したため製作された、SF作家E・R・バロウズの『時間に忘れられた国』の映画化第２作。“恐竜の島”カブローナ島に取り残された船員タイラー救出のため、新たな冒険隊が出動する。P・ウェイン主演のSFアドベンチャー。", "1977-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lords of Discipline", "影の私刑（リンチ）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "アメリカ南部の士官学校を舞台に、覆面リンチ集団に立ち向かう士官候補生の姿を描いたサスペンス作品。厳しい規律で知られるカロライナ陸軍士官学校には、士官候補生としてふさわしくない者にいやがらせやリンチを繰り返しては退校に追い込む“テン”と呼ばれる覆面集団があった。彼らの次のターゲットは、初の黒人生徒ピアース。大佐から彼の力になってやるよう言われていた上級生ウィルは、“テン”の実態究明に乗り出すが……。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Batalla en el Ciello", "バトル・イン・ヘブン", "", "", "http://image.eiga.k-img.com/images/movie/55002/315x210.jpg?4646", 98, "長編デビュー作「ハポン」で世界を驚嘆させたメキシコの鬼才カルロス・レイガダス監督が2005年に手掛けた長編第2作。メキシコ・シティで運転手として働くマルコスは、誘拐した赤ん坊を誤って死なせてしまう。罪の意識に苛まれる彼の前に、気晴らしで売春をする少女アナが現われ……。同年のカンヌ国際映画祭コンペティション部門に出品され、劇中の過激な性描写で賛否両論を巻き起こした問題作。", "2009-10-23", "2,19,7,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("セーラー服と機関銃", "セーラー服と機関銃", "", "", "http://ecx.images-amazon.com/images/I/51FCwIAEC6L._SL210_.jpg", 112, "「翔んだカップル」で鮮烈なデビューを果たした相米慎二監督が、再び薬師丸ひろ子と組んで撮り上げた奇妙で痛快な一編。ひょんなことからヤクザの一家の組長にさせられてしまった高校生の女の子が、おっかなびっくり、そして少しずつ組長になりきっていくのだが……。幼さが消えて、次第に女っぽくなってきた薬師丸ひろ子の人気で映画は大ヒット。彼女がマシンガン片手に相手の暴力団の事務所に乗り込んで、弾丸の雨をまき散らし、うっとりした表情でつぶやく“カイカン……”という言葉が流行した。翌年の1982年には131分の完璧版が公開された。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GHOUL", "ブラッディ ドクターローレンスの悲劇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ドライブの途中に道に迷った娘が、途方に暮れているところを助けてくれたドクター・ローレンスの屋敷へ招かれる。しかしそこには人肉を食べる怪物が潜んでいた……。スプラッタものとは一味違う重厚なムードが味わえるホラー・ムービー。", "1974-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO THE DEATH", "デス・キック 復讐のリング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "かつてチャック・ノリスのスタント・コーディネーターで「アメリカン・キックボクサー」に主演したJ・バレットの格闘技アクション。引退したキックボクサーと、そのライバルが、リング外で恨みを晴らそうと闘う。その果てには、負けたものが撃ち殺されるという、究極の試合が待っていた。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Driver", "ザ・ドライバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "　賭博場から強盗団が逃走。パトカーの追跡を振り切り、一行を逃がしたドライバーは類稀な運転テクニックで知られるプロの逃げ屋だった。しかしひとりの刑事がそんなドライバーを執念深く追い続けていた。刑事は逮捕した強盗犯を利用し、ドライバーを罠にかけようと画策。一方、ドライバーは彼と同様に孤独な女性ギャンブラーと心を通わせていくが……。ロサンゼルスを舞台に展開されるクライム・サスペンス。徹底したニヒリズムを貫き通し、悪党たちの顛末を描く。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どっこい生きてる", "どっこい生きてる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "一株50円の株主を募集して、400万円の製作費で作られた独立プロ作品であり、映画のオープニングには“この映画は日本映画を愛する多くの人々の協力によって作られたものである”とクレジットされている。職安の前にあふれ返る人波。毛利は今日も仕事にあぶれ、わびしい思いで家に帰る。仕事が見付からず、生活も苦しくなり、妻との折り合いも悪くなってきたので、毛利は妻と子供を実家に帰すことにする。やがて、やっと仕事に就くことができた毛利は喜々として働くが、給料日にポケットの金を誰かに盗まれる……。イタリアン・ネオ・レアリスモに影響を受けた今井正の代表作の１本。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TELLING LIES IN AMERICA", "17 セブンティーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「氷の微笑」「ショーガール」などの脚本家、J・エスターハスの自伝的物語をもとにしたノスタルジーあふれる青春映画。1961年、オハイオ州クリーブランド。ハンガリー移民の17歳の少年カーチーは、英語の発音を完ペキにすることと年上の女性ダニーの気を引くことに一生懸命。そんな彼が、ひょんなことからあこがれのD.J.ビリーのアシスタントに抜てきされる。華やかな生活を送るビリーにますます傾倒していくカーチー。ところが、権力を利用したビリーの悪事を知ってしまい……。B・レンフロが大人の世界に足を踏み入れ戸惑う少年を好演。劇中、黒人少年が歌う歌はK・ベーコンが作詞・作曲を担当した。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRAND THEFT AUTO", "バニシングIN TURBO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "俳優として活躍してきたR・ハワードの監督デビュー作。主演も兼ねてのデビューとなった。ロスに住む大物実業家の娘ポーラはサムと結婚しようと考えていたが、父が反対。ポーラとサムは父のロールスロイスを盗んでラスベガスへと逃避行の旅に出る。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MALA ORDINA", "皆殺しハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ミラノを舞台にした復讐のイタリアン・アクション映画。殺し殺されといったアメリカのギャング映画やフレンチ・ノワール、日本のヤクザ映画などにも通じる隠れた傑作だ。イタリアからアメリカへ渡るはずの麻薬が消えたことから、元売人の主人公が組織から嫌疑をかけられ、二つの組織から命を狙われる羽目に。だが、妻子が殺された彼は復讐に向かうが……。", "1972-01-01", "4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ざくろ屋敷　バルザック『人間喜劇』より", "ざくろ屋敷　バルザック『人間喜劇』より", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "", "2007-07-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い陣羽織", "赤い陣羽織", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "女房持ちの代官が、ある人妻になんとか近づこうとするが……。木下順二の戯曲を映画化した、一種の風刺喜劇。赤い陣羽織が、当時の権力の象徴として使われている。「あゝ野麦峠」など、後年の山本薩夫作品とは違った味わいの楽しめる一編。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES GRANDES GUEULES", "男たちの掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "父親の残した木材伐採場を継ぐことになった男が、出獄したばかりの二人を雇うが手が足りず、刑期終了間際の７人にも応援を仰ぐ。前科者ではあるが律義な男たち。しかし彼らと町の顔役の木材業者との対立が激化して、ついには乱闘騒ぎとなる。「冒険者たち」の名トリオが手を組んだアクション・ロマン。", "1965-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トゥルース", "トゥルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "萩原聖人が、職務と正義の間で苦悩し狂気へと走る報道記者を熱演する力作。南海のガデラ共和国で、先住民ラジャ族が政府に反旗を翻した。現地の様子を取材している先輩記者・沢田に合流した新人記者の羽済は、スクープをものにするためには手段を選ばない彼のやり方に反発する。戦火が広がる中で何度も身の危険にさらされる羽済は、やがて反乱を引き起こした意外な真相を知り……。真実を追うスリリングな取材過程と殺りくの残酷描写を交えて、ジャーナリストとは何かを鋭く訴えかける。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花火の舞", "花火の舞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "将来の約束を交わし、自分のためにまだ未完成ではあるが曲を贈ってくれた作曲家を進駐軍に連れ去られた柳橋芸者、蔦次。彼女は、戦争孤児を人気歌手、丘田に弟子入りさせたことで、政治家で丘田の後援会長の早川と知り合った。作曲家を釈放させてやると、早川に身体を要求された彼女は、乗り込んできた丘田に助けられたが……。朗らか歌手の岡晴夫を起用した、歌謡メロドラマ。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誘拐ラプソディー", "誘拐ラプソディー", "", "", "http://image.eiga.k-img.com/images/movie/54622/315x210.jpg?2565", 111, "前科と借金しかない男・秀吉は、人生に失望し、自殺を図るが無残にも失敗に終わる。そんな秀吉の前に、資産家の息子で家出中の伝助が現れる。秀吉は人生最後の逆転を賭け、伝助を誘拐し、身代金5000万円を要求する。しかし、完全犯罪が成立するかと思われた矢先、秀吉は何者かに命を狙われるはめになる。「明日の記憶」の荻原浩の小説が原作。俳優の榊英雄が長編映画3度目のメガホンをとる。", "2010-04-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("回想 川本輝夫　ミナマタ 井戸を掘ったひと", "回想 川本輝夫　ミナマタ 井戸を掘ったひと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "環境破壊の問題が続く中、公害の原点として世界の人々に知れ渡った水俣病。その患者運動のリーダーであった川本輝夫が逝った。そんな彼を、水俣病を追いかけてきたドキュメンタリー作家の土本監督が撮り上げた。周囲の人々からの差別と孤立の中で、彼の飾らないそのままの姿と挿話が、一編のドラマのように映し出されていく。そして遺言が発せられる感動のラストへ。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日常の戦ひ", "日常の戦ひ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "島津保次郎が終戦の約１年前に発表した遺作。英文学助教授という、ひっ迫した戦時下ではほとんどお国の役に立たぬ職業に就いていることに悩む谷口が、隣組の組長としてお国のために働こうと決意してからの“日常の戦い”を描く。原作は石川達三の新聞連載小説。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAY WINE", "恋はワインの香り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "甘いアバンチュールを体験しようとパリへやってきたアメリカ人母娘が、同時にパリの医者を好きになったことから巻き起こす珍騒動をユーモラスに描いたセクシー・コメディ。言葉巧みなフランス男の絶倫ぶり、フランス映画の愛すべき不道徳ぶりをそっくりまねた母娘の姿など、登場人物のキャラクターが実に愉快。", "1990-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あの橋の畔で　完結篇", "あの橋の畔で　完結篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "長い時を経て二人の男女は結ばれるが、不治の病に冒された妻は……といった、現在に通ずる“泣けるドラマ”にも影響を及ぼしている名作シリーズの完結篇。政略結婚の末、葉子は数々の不遇に遭ったが、遂に夫と離婚し、光晴のもとへ。しかし、光晴は担当医から彼女が病気で２年の命だと知らされる。それから時は流れて２年後、二人に奇跡は訪れるのか？", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARDBALL", "陽だまりのグラウンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "実体験を綴った原作をもとに描く感動のヒューマン・ドラマ。ギャンブルで借金苦に陥った男コナーが、週５ドルで少年野球のコーチを引き受ける。報酬目当てで始めた仕事だったが、コナーは、野球を愛し、自分を信頼してくれる少年たちの純粋な心に触れ、次第に生きる情熱を取り戻していく。だが、そんな矢先、思いがけない悲劇が……。人生に挫折した男と少年たちの交流をユーモラスに描きながら、スラムの少年たちを取り巻く過酷な現実を見つめホロ苦い後味を残す。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("配達されない三通の手紙", "配達されない三通の手紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "山口県・萩の町の上流階級を舞台に、５人の女性の愛に対する闘い、その華やいだムードのなかで起こる恐怖の惨劇を描いたミステリー作品。エラリー・クイーンの本格推理小説を、舞台を萩に移して脚色した。歌舞伎俳優・片岡孝夫（当時）が主演。犯人当てのミステリーが展開する。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("星の牧場", "星の牧場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "庄野英二の同名のロングセラー小説の映画化を20数年間夢見続けてきた若杉光夫の執念の作品。高原の牧場で働く戦争で記憶を失った男と、森に住むジプシーたちとの心の交流をニュージーランド・ロケを敢行して仕上げたメルヘンタッチの作品。撮影は名手・岡崎宏三。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌舞伎町案内人", "歌舞伎町案内人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "中国人専門の観光ガイドとして新宿歌舞伎町で成功を収めた李小牧の自伝的作品の映画化。アジア最大の歓楽街である同地を舞台に、留学生として来日した一人の青年が体験する異国の地で生き抜くことの過酷さが、生々しい裏社会の描写を交えながら克明に綴られる衝撃作だ。知恵と誠実さを武器に歌舞伎町を渡り歩く主人公を演じたチューヤンの熱演に注目。中国人就学生の李暁強は、バイトをしながら日本語学校に通い、一生懸命に生きていた。しかし、日本政府の政策転換による入管法改正の煽りを受け、李の生活は一変。李は生きるために、歌舞伎町で観光ガイド業を始める。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("終りよければすべてよし", "終りよければすべてよし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "「安心して老いるために」「山中常盤」などの作品で知られるドキュメンタリー作家・羽田澄子監督が、終末期医療の問題に迫った作品。医療機関での死が8割を超え、自宅で親しい人々に囲まれて安らかな最期を迎えることが困難な状況となっている現在の日本。国内の優れた在宅医療システムや福祉施設、さらにオーストラリやスウェーデンの例を紹介し、医療サイドから見た終末期医療の現状と課題を明らかにしていく。", "2007-06-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TULSA", "タルサ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "石油ブームでにぎわう1920年代のオクラホマ州タルサ。まじめ一筋の牧場主ランシシングは、石油の害で牛が次々と死んでいくのを見かねて、石油業者に抗議を申し込むが謀略にあい、殺されてしまう。娘のチェロキーは、父の仇をとろうと父の友人から石油採掘権を買うが……。ブニュエルの「グラン・カジノ」を思わせるストーリーの中に、恋あり、復讐ありと、盛りだくさんのドラマとなっている。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE NIEBELUNGEN", "ニーベルンゲン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 186, "歌劇でおなじみの題材を、F・ラング監督以下、戦前ドイツの最高スタッフが映像化した無声映画大作。日本では第１部を「ジークフリード」、第２部を「クリームヒルトの復讐」と題して別々に公開された。前半は、伝説の黄金を求めて旅する王子が竜の生き血を浴びて不死身の英雄となるが、弱点を突かれて謀殺されるまで。後半は、彼の妻が仇を討ち果たす様を描く壮絶な復讐劇となっている。半裸の美青年が、白馬にまたがり森の中を進む第１部の有名なシーンなどは、まるで一幅の名画さながら。長くファンタジー映画のお手本とされた古典は、現代でもほとんど古びていない。", "1924-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きみのためにできること", "きみのためにできること", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "録音技師の青年が、取材で訪れた宮古島で出会った女性バイオリニストに心惹かれる。しかし、彼女には不倫関係にあった中年男性がいた……。年上の女性との恋模様を、篠原哲雄がゆったりと綴った作品。物語構造と人物関係は同監督の前作「洗濯機は俺にまかせろ」に酷似しているが、映画の肌合いは正反対。「絆−きずな−」で映画デビューしたバイオリニストの川井郁子が、エキセントリックなヒロインを熱演している。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIDWAY", "ミッドウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "太平洋戦争の命運を分けたといわれる歴史的戦いであるミッドウェイ海戦。この戦いを日米の豪華キャストで描いた作品。当時の実際の記録フィルムなども交えながら、迫力ある戦闘シーンが繰り広げられ、臨場感を盛り上げている。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("田園に死す", "田園に死す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "寺山修司が、自らの同名歌集をもとに映画化。自伝的色彩が濃く、想像的に自らの少年時代を回想したものである。青森県の下北半島を舞台に、愛情の押し売りをする母親から逃れようとする少年が、死んだ父親をイタコに口寄せしてもらったり、憧れの隣りのお嫁さんとのかけ落ちを夢想したりする。過去の出来事がモザイク的に、現在の映画監督をしている私を襲い、過去を美化するのか、現在を正当化するのかを映画監督は悩む。監督は、過去の母親を殺そうとするが、それもできず母とともにメシを食べる。寺山の記憶に対するジレンマが、直接的な思いと、イメージ的な映像によって観る者に突き刺さってくる作品である。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Swing Shift", "スイング・シフト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "　第2次大戦中、夫を戦場に送り出したケイは南カリフォルニアにある飛行機工場で働くことに。寂しい毎日を送る彼女の前に現れたのは、ラッキーという名の男。彼はケイをデートに誘うが……。ゴールディ・ホーンとカート・ラッセルを結びつけるきっかけとなった作品。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT HEAD 劇場版", "NIGHT HEAD 劇場版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "1992年10月より半年にわたってフジテレビ系で放送されるや、深夜帯にもかかわらず一躍カルト的な人気を呼び、発売されたビテオや写真集、小説、コミックのいずれもが爆発的売れ行きを示した『NIGHT HEAD』。その同じスタッフとキャストによる映画化作品。超能力者の兄弟、霧原直人と直也は15年間、岐阜の山奥にある研究所での生活を余儀なくされた。多感な時期を隔離された中で過ごす苦痛に耐えかねた二人は、研究所を脱走する。しかし、それは二人にとって新たなる苦悩の旅の始まりだった。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キツツキと雨", "キツツキと雨", "", "", "http://image.eiga.k-img.com/images/movie/56032/315x210.jpg?4243", 129, "「南極料理人」の沖田修一監督が、無骨な木こりと気の弱い映画監督の出会いから生まれるドラマを役所広司と小栗旬の初共演で描く。とあるのどかな山村に、ある日突然、ゾンビ映画の撮影隊がやってくる。ひょんなことから撮影を手伝うことになった60歳の木こりの克彦と、その気弱さゆえにスタッフをまとめられず狼狽する25歳の新人監督・幸一は、互いに影響を与えあい、次第に変化をもたらしていく。そして、そんな2人の交流が村と撮影隊の奇妙なコラボレーションを生み出していく。2011年・第24回東京国際映画祭で審査員特別賞を受賞。", "2012-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沓掛時次郎（活弁トーキー版）", "沓掛時次郎（活弁トーキー版）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "かつての日本映画には“股旅もの”と称するジャンルがあったが、これはそのハシリともいうべき名作。ヤクザ渡世の義理のために一匹狼の渡世人・沓掛時次郎は一人の男を斬るが、その男の死に際の頼みで女房と子供を彼らの実家へ送り届ける旅をすることになる。旅を続けるうちに、時次郎を夫の敵と憎む彼女の心も次第に変わっていく……。昭和初期の大スター、大河内伝次郎の代表作の一つ。同作品のリメイク版は数多く製作され、戦後だけでも1954年日活の佐伯清監督作品、1961年大映の池広一夫監督作品、さらに衣笠貞之助の「浅間の鴉」（1953・大映）や加藤泰の「沓掛時次郎・遊侠一匹」（1966・東映）などがある。", "1929-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("下駄とジャズ", "下駄とジャズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "関西をベースに８ミリ映画を作っていた住野真子が、“伊丹映画祭５周年記念映画”の監督に選ばれて初挑戦した16ミリ作品。1941年の夏、敵国アメリカの音楽であるジャズに魅せられてバンドを始める仲間たちの群像と、その50年後の姿を描く。落語家・桂三枝の弟子、高杉二郎が新境地を見せる。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pora Umierac", "木洩れ日の家で", "", "", "http://image.eiga.k-img.com/images/movie/55792/315x210.jpg?4589", 104, "ポーランド、ワルシャワ郊外の古い屋敷でひとり暮らす91歳のアニェラは、長くない余生ですべきことを考える。若き日の甘い思い出や幼き日の息子の愛らしい姿を回想するが、一方で静かな生活をかき乱す現在の息子夫婦や隣人たちとの関係から、アニェラは自らの望みをかなえるため、ある決断をする。撮影時91歳だったポーランドの大女優ダヌタ・シャフラルスカを主演に、老婦人の人生最後の日々を美しいモノクロームの映像でつづったドラマ。", "2011-04-16", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CASA DE BERNARDA ALBA", "ベルナルダ・アルバの家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "スペイン・アンダルシア地方の小さな村。アルバ家のベルナルダは夫の死を機に、この家の主となる。彼女は５人の娘たちに８年間の喪に服することを強制するが、末娘のアデラは彼女に反して長女の婚約者と逢引きを重ねる。女だけの密閉された空間でせめぎ合い、苦悩する欲望と嫉妬。オール室内ロケの異色作。", "1987-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A LITTLE STEP", "A LITTLE STEP", "", "", "http://image.eiga.k-img.com/images/movie/54832/315x210.jpg?3588", 28, "「硫黄島からの手紙」などで知られる俳優・伊原剛志がプロデュースを手がけ、自ら主演も務めたショートフィルム。アメリカを舞台に、自分さがしの旅に出た少年と日本人旅行者が織りなす交流を描く。小学生のアレックスは、父とはうまくいかず、仕事で忙しい母とはすれ違ってばかりの寂しい毎日を過ごしていた。ある日、両親に内緒で祖父を尋ねる旅に出た彼は、ある目的を果たすためにアメリカにやって来た日本人の一郎と出会う。", "2009-08-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOME GIRL", "サム・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「スリープ・ウィズ・ミー」のR・ケリーが、1998年、L.A.インディペンデント・フィルム・フェスティバル最優秀監督賞を受賞したロマンティック・コメディ。男運のないクレアが魅力的な俳優の卵チャドと出会う。クレアは101回目にして本物の恋の予感を感じていた。ところが、彼女とは対照的に自由奔放に恋を楽しむ親友エイプリルとチャドが関係を持っていることを知り大ショック。やけくそになった彼女の反撃が開始する。主演と脚本を兼任したM・リビシの友人J・ルイスが、ヒロインのクールな親友役で存在感を放つ。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BABY IT’S YOU", "ベイビー・イッツ・ユー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1966年、ニュージャージーのハイスクール。ジルは郊外の高級住宅街に住むユダヤ系の大金持ちのお嬢さん。美人で成績も良い模範生の彼女が、育ちの違うシークというイタリア系の若者に恋してしまう。シークは労働者の息子で、何度も退学の憂き目にあった不良青年だが、学校では人気者。やがて楽しい高校生活も終わり、二人はそれぞれの道を歩んでいく。ジルは南部の名門大学に入学し、シークは第2のシナトラを目指して旅立つ。趣味も育った環境も違う若者たちが一緒に送る楽しい高校生活。そんな青春の一頁を1960年代の数々のヒット・ナンバーとともに描いた、ノスタルジックな青春ドラマ。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不沈艦撃沈", "不沈艦撃沈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "昭和16年、海軍省から10割の増産を厳命された昭和精器は、粉骨砕身の努力でその要請に応え、やがてその目標を達成させていく……。太平洋戦争の時局悪化も著しい昭和19年に、銃後の士気を高めようとする軍部の意図に応じて、名匠マキノ雅広監督がメガホンを握った戦意昂揚映画。タイトルの勇ましいイメージとは裏腹に、実際は国のためにみな働けといった強要のメッセージが全編を支配している人間ドラマである。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FORGET PARIS", "彼と彼女の第2章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ハッピーエンドを迎えた恋愛のその後の結婚生活をコミカルに描いたラブ・コメディ。NBAプロバスケットリーグで審判を務めるミッキーは、父の遺言で遺体を埋葬するために一路パリへ。だが、到着するやいなや遺体が紛失するという大事件が起こる。この時、力になってくれたのが、航空会社に勤めるパリ在住のアメリカ人女性エレンだった。二人は急速に惹かれ合い、エレンの離婚が成立するとともに結婚。アメリカで結婚生活をスタートさせた二人だが、いざ一緒に暮らし始めるとすれ違いばかりで……。NBAのスーパースターがずらり登場するシーンも見どころの一つ。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冬冬的假期／A SUMMER AT GRANDPA’S", "冬冬の夏休み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「風櫃〈フンクイ〉の少年」に続いて撮られたホウ・シャオシエンの長編第５作。小学校を卒業した冬冬は、母が病気で入院したため、妹のとともに夏休みを田舎の祖父の家で過ごすことになる。祖父はとても厳格だったが、冬冬は田舎の子供たちとすぐに仲良くなり、毎日思う存分遊んで過ごす。一方、はなかなか仲間に加えてもらえず、兄たちにいたずらしたりの日々。やがて台北から父が迎えにくる……。父役は「恐怖分子」のE・ヤン監督。田舎の祖父の家は、原作・脚本のチュー・ティエンウェンの実家でロケされた。３大陸映画祭グランプリ受賞作。", "1984-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊莎貝拉", "イザベラ", "", "", "http://image.eiga.k-img.com/images/movie/56372/315x210.jpg?1736", 109, "香港の鬼才パン・ホーチョンの監督第8作「ドリーム・ホーム」（2010）の公開を記念して、ホーチョン監督の日本未公開作を一挙上映する特集上映「パン・ホーチョン、お前は誰だ!?」で公開。1999年、中国返還直前のポルトガル領マカオを舞台に、窮地に追い込まれた汚職警官と、彼の娘を名乗る少女との奇妙な共同生活を描く。主演は後に「インファナル・アフェア」シリーズで活躍するチャップマン・トウ、ヒロイン役にイザベラ・リョン。", "2011-05-28", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEW YORK DOLL", "ニューヨーク・ドール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "1970年代のロックシーンを席巻したバンド、“ニューヨーク・ドールズ”のベーシスト、アーサー・ケインのその後を見つめたドキュメンタリー。ニューヨーク・ドールズ解散後、アルコール中毒を克服してモルモン教に改宗したアーサー。教団の施設で働く彼にロック・スターの面影はない。そんなある日、バンド再結成の誘いが……。神に仕える身となった彼に訪れる、奇跡のような出来事が、あまりにドラマチックで胸を打つ。", "2006-06-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOYS LOVE 劇場版", "BOYS LOVE 劇場版", "", "", "http://image.eiga.k-img.com/images/movie/34162/315x210.jpg?1510", 88, "男性同士の禁断の愛を描き大ヒットしたDVD「BOYS LOVE」を新たなストーリーで映画化。DVD版に引き続き、監督・脚本を寺内康太郎、主演を「テニスの王子様」の小谷嘉一が務める。全寮制高校に勤める教師・蒼井のクラスに1人の少年が転校してくる。蒼井はかつてその少年・天上と関係を持っていた。1度だけの過ちと、天上に対し冷たく接する蒼井。やがて天上は、学園内の権力者・花園に目を付けられてしまい……。", "2007-09-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画クレヨンしんちゃん　嵐を呼ぶ黄金のスパイ大作戦", "映画クレヨンしんちゃん　嵐を呼ぶ黄金のスパイ大作戦", "", "", "http://image.eiga.k-img.com/images/movie/55892/315x210.jpg?1582", 107, "臼井儀人原作による人気アニメーションの劇場版第19作。しんのすけの前に突如現れたナゾの少女レモンは、しんのすけがあこがれるヒーロー「アクション仮面」からのメッセージを携えていた。それによれば、しんのすけはアクションスパイに選ばれ、正義のために戦うのだという。すっかりその気になったしんのすけは、レモンの指導のもとスパイ訓練を始めるが……。", "2011-04-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("岸和田少年愚連隊", "岸和田少年愚連隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "かつて「ガキ帝国」で頭角を現した井筒和幸が、今度はナインティナイン主演で1970年代の青春グラフィティーに挑んだ作品。中場利一の同名小説を快活なタッチで映画化している。1975年、大阪の岸和田。悪友たちとケンカに明け暮れる少年二人組、チュンバと小鉄の中学から高校時代を追っていく。遂には鑑別所にぶち込まれてしまうチュンバに扮した矢部浩之の男気ある快演が光る。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RAZOR’S EDGE", "剃刀の刃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "第一次大戦後、恋人と別れ傷心でチベット奥地へ向かった一兵士。ラマの僧院で心の安らぎを得た彼はパリに戻るが、そこには娼婦に身をやつしたかつての恋人がいた……。B・マーレーがなんとモームのシリアス世界に挑んだ話題作。1946年にT・パワー主演で作られた同名作品のリメイク。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Little DJ 小さな恋の物語", "Little DJ 小さな恋の物語", "", "", "http://image.eiga.k-img.com/images/movie/34272/315x210.jpg?2872", 128, "野球とラジオのDJが大好きな少年・太郎は、入院中の病院でふとしたことから昼休みの院内放送のDJを担当することに。放送を通じて一躍人気者になる太郎。そんな時、太郎は1つ年上の美しい少女たまきと出会い、2人の間には小さな恋が芽生えるが……。70年代の海辺の町を舞台に、人気子役の神木隆之介と福田麻由子が織り成す、少年少女の淡い初恋と成長の物語。ラジオから流れる70年代の名曲が作品を彩り、小林克也がラジオDJとして特別出演。", "2007-12-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ketabe Ghanouin", "法の書", "", "", "http://image.eiga.k-img.com/images/movie/54982/315x210.jpg?4615", 94, "イラン人男性とフランス人女性の国際結婚をテーマにしたコメディ。テヘラン出身のラフマンは、出張先のベイルートでペルシャ語の通訳を担当したフランス人女性ジュリエットと相思相愛の仲になる。めでたく結婚することになった2人は、テヘランにあるラフマンの実家で新婚生活をスタートさせる。ところがジュリエットは、文化や習慣の違いから彼の母や姉妹とギクシャクしっぱなしでなのだった。", "2009-10-17", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("この愛の物語", "この愛の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "スタントマンたちの愛と友情、そして無器用な彼らの愛。常に死と隣り合わせの状況の下での彼らの人間ばなれしたスタントぶりを織り交ぜながら、つかこうへい原作・脚本によって描かれた人間ドラマ。近藤真彦らがスタント・シーンを体当たりで演じる。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("社長漫遊記", "社長漫遊記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "洋行帰りの太陽ペイント社長・堂本平太郎はすっかり外国かぶれになって、社の方針もアメリカン・スタイルにしてしまった。九州一帯の販売店をまわる旅の途中で芸者〆奴に会った社長、一気に浮気ムード。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("基老４０／A QUEER STORY", "BE MY BOY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "保守的な家庭環境に育ち、ゲイであることを秘密にしている46歳のロー。美容室を経営し、自分がゲイだとおおらかに宣言する若者サニー。深く愛し合っている二人だったが、ゲイであることへの自覚の差から別れの危機が訪れる……。「喝采の扉　虎度門〈フードゥモン〉」のシュウ・ケイ監督が、世代も性格も異なるカップルに降りかかるトラブルを通して、ゲイに対する社会的な問題を緊密に描くラブ・ストーリー。", "1997-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIDNIGHT RUN", "ミッドナイト・ラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "徹底した役作りで知られるデ・ニーロが、前作「アンタッチャブル」のアル・カポネ役から一変。スリムになったボディで挑んだ、アクティブでサスペンスフルでユーモラスな、熱き男たちの友情物語。元シカゴ警察刑事のジャックは、今は金次第でどんな危険もいとわない“バウンティ・ハンター”に。ある日、ギャングの金を横領し慈善事業に寄付した奇得な男マデューカスを、ロサンゼルスに連れ戻す仕事を依頼され、さっそくニューヨークへ。難なくマデューカスを見つけ出し、ロスへ引き返そうとするジャック。ところが、マデューカスの命を狙うギャング一味と、やはり彼の行方を探すFBIの双方に追われるハメに。かくて男二人、５日間にわたるアメリカ大陸横断の大逃走劇が始まった。一癖も二癖もあるマデューカスと、骨太で気の荒い一匹狼ジャックのコンビネーションが絶妙。デ・ニーロ自身“最も気に入っている作品”とのコメントを残している。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIMIT UP", "リミット・アップ 天使にご用心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ケイシー・フォールズはシカゴの穀物取引センターで使い走りをしながらも、いつかはこの業界での成功を夢見ていた。ある日、謎の黒人女性ナイキが現れ、自分は悪魔の使いだと告げる。ケイシーの魂と引き換えに、取引業者としての成功を与えるというのだ。ナイキの情報をもとに、仕事、新車、新居そして結婚まで、次々と順調に進むケイシーだったが……。N・アレンがサクセス・ストーリーのヒロインを演じる心温まる映画。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道", "極道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "長い間悪役に甘んじてきた若山富三郎が、久々の主演を果たした“極道”シリーズ。釜ヶ崎の愚連隊上がりの極道・島村は、大阪中を自分の縄張りにしようというとてつもない欲と望みを燃え立たせ、踏まれても蹴られても、根性むき出しの闘志で大組織をブチ破っていく。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不良少年 3,000人の総番（アタマ）", "不良少年 3,000人の総番（アタマ）", "", "", "http://image.eiga.k-img.com/images/movie/57742/315x210.jpg?1708", 74, "斎藤工、窪塚俊介の共演で1970年代を舞台に不良少年たちの生き様を描いた青春劇。3000人の全校生徒のうち半数がツッパリのヤンキーという工業高校に通う千藤鷹也は、親友の菱川晃希らとつるんで他校の不良たちとの喧嘩に明け暮れていた。やがて3年生の番長グループに目をつけられた千藤は、学校の「総番（アタマ）」の座をかけた壮絶な権力争いに巻き込まれていく。かつて走り屋グループ「ルート20カークラブ」で3代目リーダーを務めた作家・遠藤夏輝が、自身の経験を盛り込んで執筆した「東京不良少年伝説」が原作。", "2012-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・座頭市物語", "続・座頭市物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "市がまだ盲目になる前に、一人の女をめぐって争った実兄・与四郎が、凶状持ちの浪人となって市の前に現れる……。市の実兄の浪人を勝新太郎の実兄である城健三朗（のちの若山富三郎）が好演。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE OF STRANGERS", "他人の家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "７年ぶりで出所したマックスが家に帰って来た。ところが街の様子は一変しており、兄弟たちも彼には冷たかった。恋人のアイリーンとともに、平和に暮らそうと心に決めた彼だったが、兄弟たちは人を雇ってマックスを襲わせた……。イタリア移民の一家を主人公にした家族ドラマ。一家の厄災を一身に被る男を演じるE・G・ロビンソンの熱演が光る。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("孤独なツバメたち　デカセギの子どもに生まれて", "孤独なツバメたち　デカセギの子どもに生まれて", "", "", "http://image.eiga.k-img.com/images/movie/58092/315x210.jpg?1978", 88, "静岡県浜松市に暮らす日系ブラジル人青年たちの2年半を追ったドキュメンタリー。自動車産業を支える労働力として、浜松市には全国の中でも多くの日系ブラジル人が暮らしている。ブラジル国籍であるため義務教育も保障されず、安定した仕事にもつけない青年たちだが、どんなにつらくとも生き生きとして明るかった。しかし、2008年のリーマンショック以降、仕事を失い帰国を余儀なくさる者も現れる。日本で生まれ育った日系ブラジル人の青年たちは家族や友人、恋人と別れなくてはならず……。", "2012-06-30", "76,17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大空のサムライ", "大空のサムライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "太平洋戦争で戦闘機乗りとして活躍、戦後その経験を書いてベストセラーになった坂井三郎の原作による空中戦映画。ラバウルでの米空軍との消耗戦のなか、坂井軍曹は自分自身と配下の列機の損傷を少しでも食い止めるべく、慎重かつ勇敢に戦い続ける。戦争映画というより、坂井三郎の人間的魅力が光る。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　やきそばパンマンとブラックサボテンマン", "それいけ！アンパンマン　やきそばパンマンとブラックサボテンマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "やなせたかし原作の人気アニメーション劇場版第12作「それいけ！アンパンマン　人魚姫のなみだ」と同時上映の短編。", "2000-07-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　TURANDOT　PROJECT", "トゥーランドット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "1998年、プッチーニの名オペラ『トゥーランドット』が、中国紫禁城で初めて上演された。その制作過程を克明に追ったドキュメンタリー。中国の伝統を取り入れつつ新しい試みにチャレンジしようと奮闘するのは、中国の映画監督チャン・イーモウ。彼とスタッフの対立、ヨーロッパと中国文化の違い、言葉の壁……。様々な困難を乗り越えて壮大な芸術作品を作り上げていく人々の情熱を、ドラマティックに映し取っていく。", "2000-01-01", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("童貞先生行状記", "童貞先生行状記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "新米教師の野々宮は小諸の聖心女学院に赴任することに。小諸の駅では女学院を温泉場にしようと計画する町のボスが、東京から来る大学教授のもう一人の野々宮を待っていた。女学院の野々宮は、大学教授の野々宮と間違えられ料亭に連れて行かれ、大歓迎を受けるが……。春原政久お得意の喜劇。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EN　RACHACHANT", "アン・ラシャシャン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 7, "日本未公開の異色ドキュメンタリー「セザンヌ」や「ルーヴル美術館訪問」などを世に輩出したコンビ監督が、若き頃に撮り上げた16ミリの短編映画。文学者として有名なM・デュラスが絵本用に書き下ろした童話『ああ、エルネスト！』を原作にした楽しいショートフィルムになっている。学校の詰め込み教育に対して小学生が反旗をひるがえすストーリー。", "1982-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sleepers", "スリーパーズ", "", "", "http://ecx.images-amazon.com/images/I/514IMMePUOL._SL210_.jpg", 147, "少年院で虐待を受けた若者たちによる復讐劇を、ロバート・デ・ニーロ、ブラッド・ピット等豪華キャストで描く社会派ドラマ。ちょっとした悪ふざけが大事故を引き起こしていまい、少年院に送られることとなった4人の少年。彼らを待ち受けていたのは、看守たちから暴力と性的虐待を受け続ける地獄のような毎日だった。心に傷を負ったまま大人になり、それぞれの道へ進んだ彼らは、ある事件をきっかけに看守たちへの復讐に乗り出す。", "1997-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不法滞在", "不法滞在", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "在日外国人問題を背景に描く、陣内孝則主演の異色サスペンス。入国管理官、通称“入管Gメン”として外国人不法滞在者たちの摘発に追われる辰巳洋一。ある日、辰巳は偽造パスポート組織の捜査で、かつて強制送還した台湾女性・李美菱と再会する。捜査のため接近するうち、彼女に一人娘の死がもとで別れた妻を二重写しにしてしまう辰巳。一方、美菱も彼に惹かれ始めるが、娘を人質に取られ組織を離れることはできない。そんな折、台湾にいる娘が病気で危険との知らせが届いたことから、美菱は思い切った行動に出るのだが……。撮影は「(ハル)」「失楽園」の高瀬比呂志。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Men in Black", "メン・イン・ブラック", "", "", "http://ecx.images-amazon.com/images/I/51r2GbLfDpL._SL210_.jpg", 97, "スティーブン・スピルバーグが製作総指揮を担当、ローウェル・カニンガムのコミックをもとに地球上に存在する宇宙人を監視する秘密組織「MIB（メン・イン・ブラック）」の活躍を描くSFアクションコメディ。主演にトミー・リー・ジョーンズとウィル・スミス。ニューヨーク市警の若き警官ジェームズは、追跡していた犯人を目の前で逃してしまうが、そこへ黒服の男「K」が現れ、逃がした犯人が宇宙人であったことを知らされる。Kはジェームズの素質を見込んでMIBにスカウトし、ジェームズはMIBの新たなエージェント「J」としてコンビを組んで活動を始める。一方その頃、地球壊滅を企む昆虫型エイリアンが地球に侵入し、不穏な動きを見せはじめて……。", "1997-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("切腹", "切腹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "武家社会の非人間性、権力者の体面を取りつくろう偽善を“切腹”という形で鮮烈に描いた作品。小林正樹監督にとっては初の時代劇である。彦根藩邸で津雲半四郎と名乗る男が、生活に困窮し、いっそ武士らしく切腹して果てたいので玄関先を借りたいと申し出る。どこの武家屋敷でも玄関先を血で汚されてはかなわぬと、いくばくかの金を与えて追い返す。つまりこれは当節流行のたかりであった。井伊家の家老はこれを聞くと、しばらく前に同じような申し手をした若い武士のことを思い出し、半四郎に話して聞かせる……。物語はふんだんに回想形式を取り入れ、半四郎の真の目的を明らかにしていく。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SHADOW OF THE RAVEN", "エネミーランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "アイスランド中世の伝説を映像化したスペクタクル・アドベンチャー。ノルウェーで神学を学び、故郷アイスランドへ帰ってきた青年が、“目には目を、歯には歯を”という、村の野蛮な思想を改革しようとする。だが、彼の母が敵対する村民の矢を受けたことから、集落全体の戦いに発展していく。", "1988-01-01", "77,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生きてるうちが花なのよ 死んだらそれまでよ党宣言", "生きてるうちが花なのよ 死んだらそれまでよ党宣言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "チェルノブイリの原子炉爆発事故以降、世界中で反原発運動の気運が高まったが、この映画はそれを先取りしたという意味で、森崎東の時代感覚の鋭さを改めて見直させる作品だ。とはいえ決してマジメ一直線にはならず、森崎監督独特の“人間模様”となっている。原発問題、ジャパゆきさん、修学旅行積立金強奪騒動など面白い事件や現象を、やや多少盛り込み過ぎて全体に未整理なところが目につくが、はみ出し人間たちの生き方は、“総中流階級”となった当時の日本人に痛烈なパンチを浴びせていて小気味良い。旅回りのストリッパー、バーバラは、原発を転々と渡り歩く原発ジプシーの宮里とは腐れ縁の仲。彼女はそろそろ二人で堅気の仕事について結婚したがっていたが、そこに事件が起こる。原発作業中に廃液漏れで被爆し、事故隠しのために命を狙われる安次が逃げ込んできて……。物干し台でのセックス、墓場での結婚式など、エキセントリックでありながらどこかもの哀しいアウトローの心情を共感を込めて描いている。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mrs.Parker and the Vicious Circle", "ミセス・パーカー／ジャズエイジの華", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "　1920年代のニューヨーク。ドロシー・パーカーと友人ロバート・ベンチリー、そして文士仲間たちはアルゴンキン・ホテルのラウンジにある大きな丸テーブルを囲み、そこで交流を温めていた。ドロシーは最初の結婚に失敗した後、チャールズという演劇記者と恋に落ちるが、幸せは長く続かなかった。自殺を図り、酒に溺れる彼女を支えるベンチリー。しかしふたりは最後まで一線を越えることはなかった……。華やかな時代に生きた女流作家の恋と苦悩を実話に基づいて描く。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Evita", "エビータ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "アルゼンチンの田舎町で私生児として生まれ、不遇の少女時代を送ったエバ・ピロン。故郷を飛び出し女優として成功した彼女は、やがて大統領夫人にまでのしあがる。”聖母エビータ”と国民から慕われた彼女の激動の人生を描いた大ヒットミュージカルをアラン・パーカー監督が映画化。実現までに15年の年月を費やし、ようやく製作された本作。主演には多くの候補が挙がったが、最終的に決定したマドンナが主人公エビータを熱演している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("老親〈ろうしん〉", "老親〈ろうしん〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "老いた親の介護、高齢者の自立、性別による役割分担をユーモラスに描いたヒューマン・ドラマ。TV『天うらら』などで知られる門野晴子の同名原作の映像化。痴呆で、なにもできない舅の世話に疲れ果てた成子は子育てが一段落ついたのを機に夫との離婚を決意する。そして、成子が娘と東京で作家として自立しようとしていた頃、今では他人の元舅の兼重がひょっこり現れて、成子のもとに居候を始めた。兼重は主婦業もマスターし、新たな人生を見いだしたのだが……。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE NICKY", "リトル★ニッキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "アメリカで大人気のコメディアン、A・サンドラーが主演。100億円ともいわれる製作費をかけ、最新のVFX技術満載で作られたノーテンキ・コメディ大作。地獄を統一する魔王一族の末っ子ニッキーはオタクな性格で、毎日をお気楽に過ごしていた。しかし、二人の兄が父である魔王に反旗を翻し、地上に飛び出してしまう。しかも、二人の策略で魔王の命は徐々に消えいく運命。この危機に、地獄復活の使命を受けたニッキーも地上に向かうのだが、兄たちはニューヨークの“地獄化計画”を進めていた。果たしてニッキーは兄たちの陰謀を止めることができるのか？", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KVINNORNA PA TAKET", "屋根の上の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "世界的な撮影監督スヴェン・ニクヴィストの息子、C・G・ニクヴィスト監督デビュー作。父親譲りの映像センスが各地で評判を呼んだフェアリー・テールである。第一次大戦前夜のストックホルムを舞台に、屋根裏部屋に住む無垢な若い女とミステリアスな女性写真家との謎めいた関係を官能的に描いていく。", "1989-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CZLOWIEK Z MARMURU", "大理石の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 160, "ポーランドの代表的監督ワイダが、本国ではタブーとされているスターリン時代の暗黒面に挑んだ問題作。映画大学の女子大生は製作中の記録映画の取材のため博物館へ。そこに放置された大理石の像から、やがて彼女が知る歴史の裏の真実とは……。監督の鋭い洞察力が1980年の“連帯”結成を予見しているのも興味深い。", "1977-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUITE BY CHANCE", "興味本位の女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "イタリアのファッション界を舞台に、都会で生きる女性たちのサクセス・ストーリーを描くお洒落な作品。華やかなファッション界に生きる男女７人の夢・成功・愛が、様々な人間関係を通じて描かれる。主人公のキャリアウーマンに、K・キャプショーが扮する。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILENT ASSASSINS", "サイレント・アサシン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "全人類を絶滅させるほど危険な力を持つ病原菌を発見したロンドンの博士が何者かに誘拐された。病原菌の発見は世間に公表されていなかったものの、誘拐犯人に脅された博士がいつ口を滑らせるか不安を抱いた公安当局は、必死で捜査するが……。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜がまた来る", "夜がまた来る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "宿命のように堕ちていく男女関係のなかに男の献身と女の強靭さを描き込む石井ワールド、充実一途の秀作。名美は麻薬Gメンの夫を横流しの濡れ衣を着せられたまま殺されたうえ、組員たちにレイプされ、後追い自殺を図る。それを救った組員の一人、村木。村木に惹かれながら復讐に燃える名美はクラブホステスになって組長に近づくが……。悪夢から目覚めると、そこには悪夢。そんな明けない夜の物語を、酷使され追いつめられてツヤを帯びる女優の肉体に、石井監督は刻み込む。夏川結衣が体当たりの好演、彼女を支える根津甚八がいぶし銀の味をみせる。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勝手にしやがれ!! 成金計画", "勝手にしやがれ!! 成金計画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "「櫻の園」のじんのひろあきが脚本に参加。交通事故を起こしたOLを助けたことから、雄次と耕作は彼女の持っていたヘロインをめぐる騒動に、巻き込まれていく。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SALOME", "情炎の女サロメ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "紀元前一世紀。男たちを狂わせた美貌の王女サロメの、愛と波乱に富んだ半生を描く、絢爛豪華な歴史絵巻。往年の美女L・ヘイワースが聖書に登場する悪女・サロメを、妖艶な魅力で演じている。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAND BEFORE TIME", "リトルフットの大冒険 謎の恐竜大陸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "スピルバーグ＆ルーカスの黄金コンビによる劇場用長編アニメ。原始時代、たび重なる地震と肉食恐竜の襲撃によって、草食恐竜たちは絶滅の危機にあった。今、群れのただ一匹の生き残りであるプロントザウルスの赤ちゃん恐竜が、幸せの“みどりの谷”を目指して旅立つのだが……。ダイアナ・ロスが歌う主題歌が大ヒットした。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEMONIC TOYS", "ドールズ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "女警官ジュディスは、犯人を追跡中、仲間とともに古い倉庫へ入り込んだ。ジュディスが犯人を捕まえようとした時、背後の扉が閉まり、彼女は閉じ込められる。外では、謎の少年“キッド”によって命を吹き込まれた人形たちが、一人、また一人と仲間を襲う。キッドの邪悪な力は、ある目的のために、ジュディスの胎内に宿る新しい命を狙っていた。「エルム街の悪夢 ザ・ファイナルナイトメア」などで活躍のMMI（マジカル・メディア・インダストリーズ）がSFXに協力している。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("寓話・伝令", "寓話・伝令", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "灼熱の砂丘を走る二人の伝令。彼らは奴隷の身分から解放されるという条件の下、故郷の城に戦いの勝利を伝えに先を急ぐ。互いに相手を傷つけ合いながら……。非現実的な極限状況の中で、欲望と狂気を剥き出しにする男たちの姿を描いた実験的な作品。1986年のブルーデンツ国際映画祭で監督賞を受賞。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Longer", "下宿人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "", "1926-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ART OF WAR", "アート・オブ・ウォー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "N.Y.沖に、ベトナム難民の死体が詰め込まれた国連の大型船が発見された。国際保安エキスパート・チームのリーダー、ニール・ショーは、事件の背後に中国国連大使が絡んでいると睨み、捜査を開始する。常に死と隣り合わせに任務を遂行する“最終兵器”とも称される彼の正義の活躍を描いたアクション映画。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人妻椿 前篇・後篇", "人妻椿 前篇・後篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "社長の殺人罪容疑を自らが身代わりになって行方をくらました営業部長・矢野。それは、とり残された妻・嘉子にとっては、転落の人生の始まりであった……。夫が消息不明となった人妻が、数々の運命にもてあそばれていく姿を、長年にわたって描き出した小島政二郎の同名小説を映画化したもの。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ESCAPE FROM FORT BRAVO", "ブラボー砦の脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "西部劇監督の名匠、J・スタージェスの古典的傑作。南北戦争たけなわの1863年、北軍が陣を構えるブラボー砦には南軍の敗残兵たちが捕虜になっていた。その捕虜の一人マーシュを脱出させるため、婚約者のカーラは北軍のリーダー、ロウパー大尉に近づく。しかし、カーラは本気でロウパーを愛してしまうのだった。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Frozen", "フローズン", "", "", "http://image.eiga.k-img.com/images/movie/55532/315x210.jpg?3344", 93, "新鋭アダム・グリーン監督が手がけるソリッド・シチュエーション・スリラー。スキー場にやってきたダン、ジョー、パーカーの3人は、日暮れ前に最後の滑りを楽しもうとリフトに乗り込むが、山頂への途中でリフトが停止してしまう。大声で助けを呼ぶが届かず、地上15メートルの空中に置き去りにされてしまう。ゲレンデが営業を再開するのは1週間後。3人は食料不足とマイナス20度の極寒に耐え切れず、何とか脱出を試みるが……。", "2010-08-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONDO NEW YORK", "モンド・ニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "世界有数のビジネス・タウンであり、ハーレムやチャイナタウンを抱える人種のるつぼ、ニューヨーク・マンハッタン。その裏の世界で、狂気に取り憑かれたようにパフォーマンスを続ける人々や、ブードゥー教信者たち。アヴァンギャルドなアーティストに迫った圧倒的なドキュメンタリー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("洗澡", "こころの湯", "", "", "http://image.eiga.k-img.com/images/movie/1652/315x210.jpg?4709", 92, "父が下町で営む銭湯を継がずに都会で働くターミンは、知的障害のある弟アミンから父が横たわる絵の描かれた葉書を受け取り、もしや父が倒れたのではと帰郷する。幸い父に変わりはなかったが、アミンが迷子になるなどの“事件”が続き、ターミンはしばらく銭湯を手伝うことに。毎日来るお客さんの幸せそうな顔を見ているうちに、この仕事を愛する父を理解し始めたターミン。だがそんな矢先、地域の再開発のため銭湯の取り壊しが決まってしまう。", "2001-07-07", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こつなぎ　山を巡る百年物語", "こつなぎ　山を巡る百年物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "大正6年、岩手県北部にある小繋（こつなぎ）集落で、山から自然の恵みを得て暮らしてきた農民たちが山の入会権を求めて起こした裁判に端を発する「小繋事件」を題材にしたドキュメンタリー。いまだ闘争が繰りひろげられていた約50年前、3人のジャーナリストが取材した膨大な量の映像資料を7年の歳月をかけて編集し、現在の現地の様子も追加。山とともに生きる人々の姿を通し、日本人の暮らしのあり方を問いかける。", "2010-05-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("King Arthur", "キング・アーサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "様々なファンタジーに影響を与えたイギリスのアーサー王伝説を、伝説としてではなく、この伝説が生まれる原因となった実際の出来事を推測して描く歴史大作。製作は大作指向のジェリー・ブラッカイマー、監督は「トレーニング・デイ」のアントワン・フークア、脚本は「グラディエーター」で共同脚本を担当したデビッド・フランゾーニ、撮影はリドリー・スコットの戦争映画「ブラックホーク・ダウン」のスラボミール・イジャック。", "2004-07-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A MATTER OF LIFE AND DEATH", "天国への階段", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "第二次世界大戦中、被弾した英軍機のパイロット、カーターは奇跡的に命拾いして砂浜に流れつく。しかし、彼はそもそもそこで死亡している運命であり、そのため天国から執拗にお誘いがかかっていく……。「黒水仙」「赤い靴」などのスタッフが地上をカラー、天国をモノクロと色分けして送る、実験的かつ独創的なコメディ。", "1946-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A LIFE LESS ORDINARY", "普通じゃない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「トレインスポッティング」の監督・主演コンビがアメリカに乗り込んで撮り上げたラブ・ストーリー。巨大企業の清掃人をしながら小説家になる夢を持つロバートは気のいい青年だったが、清掃ロボット導入のためクビになると知り、怒って社長室に駆け込む。そこには社長から説教を受けている娘のセリーンがいた。そして、なぜかロバートは彼女を誘拐して逃げ出すハメに。すべては天国の警察署の署長から二人をくっつけるように命令された天使オライリーとジャクソンの仕業だった……。E・マクレガーが心優しい誘拐犯を、C・ディアスが気ままに育ったお嬢様を楽しく演じ、二人の逃亡劇がロマンティック＆コミカルに描かれる。口の悪い天使を演じたH・ハンターの妙演も見どころ。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真夏の少年", "真夏の少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "高校野球の盛んな、ある海辺の地方都市を舞台に、12歳の少年と東京から帰ってきたヤクザな兄との再会から別れまでの、ひと夏のエピソードを温かいタッチで綴った作品。当時TVドラマに引っ張りだこだった江口洋介が兄役に扮し、死を前にした青年の美しくも悲しい情感を好演している。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLACK HOLE", "ブラックホール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "人類が誰も知らない謎の暗黒空間“ブラックホール”。そこに呑み込まれて消息を絶った大型宇宙船シグナス号は20年もの間、宇宙を漂っていた。船体の中には正体不明の人造人間が……。ピーター・エレンショーの特撮が見もののハードSF。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANNY ＆ LO", "のら猫の日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "サンダンス・インスティテュート出身の女性監督L・クルーガーの長編デビュー作。里親の元を逃げ出し、モデルハウスを泊まり歩く天涯孤独な姉妹マニーとロー。16歳の姉ローが妊娠していることが判明し、森の中の別荘に落ち着くことにした二人は、出産に詳しいエレーンという元看護婦らしき中年女性を誘拐するのだが……。アウトローな旅を繰り広げる10代の姉妹と中年女性の奇妙な共同生活を通して、“家族”というテーマをユーモラスに描出。アメリカのインディペンデント映画らしいシンプルかつクールな語り口の中に、監督の心優しいまなざしが感じ取れる。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RATE IT X", "X指定", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "町なかでのインタビューを通して、アメリカ人男性の女性観、性差別意識を浮かび上がらせようとする異色ドキュメンタリー。カントリー・ミュージックをバックにTVレポーターや雑誌編集者など、様々な職業の男たちの告白を綴っていく。低予算ながら５年の歳月をかけ、全編をシニカルなタッチで通しているのが面白い。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN-GO episode：0 因果論", "UN-GO episode：0 因果論", "", "", "http://image.eiga.k-img.com/images/movie/57172/315x210.jpg?3661", 45, "坂口安吾の「明治開化　安吾捕物帖」を原案に、舞台を近未来の東京に置き換え、主人公の探偵・結城新十郎とパートナーの謎を秘めた少年・因果が数々の難事件を解決していく姿を描くTVアニメーション「UN-GO」の前日譚となるエピソード。2011年10月からフジテレビ系深夜アニメ枠「ノイタミナ」で放送のTVシリーズでは描かれない、新十郎と因果の出会いと初めて挑んだ事件が明らかにされる。監督は「機動戦士ガンダム00」「鋼の錬金術士」の水島精二。新十郎の声を俳優の勝地涼が担当している。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　NAKED　MAN", "ホネツギマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "コーエン兄弟の弟イーサンが脚本を担当したB級ノリのハチャメチャ・コメディ。整体師とプロレスラーを兼業する男が、何者かに家族を殺されて心神喪失。整体テクとレスリング技で、関係ない人をやっつけまくる。話のつじつまなどは完全に無視して、好き勝手に暴走しまくる展開にただただア然。力の抜けた遊び心が詰まった低予算ならではの“痛怪作”だ。昼はカイロプラクティックの整体師、夜は覆面レスラーとして働くエドワードは、自分の治療院の開業をキッカケにレスラー引退を決意。ところが何者かに妻と両親を殺され、乱心したエドワードはリングに戻って大暴れしてしまう。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超高層ハンティング", "超高層ハンティング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "夢枕獏原作の同名短編小説を映画化したサイキック・バトル・アクション。現代の東京を舞台に、亜人種ASH（アッシュ）と戦う武装科学組織ジャセップ。その一員、土方は死んだチーフの残した日記の謎を解明していくうちに巨大な陰謀に巻き込まれていくのだった……。小劇場出身の新鋭・岡森諦が主人公のエスパーを好演。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VALBORGSMA¨SSOAFTON", "ワルプルギスの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "職場の上司に恋してしまったレナは、彼に妻がいることに心を痛め、会社を辞めてしまう。一方で、レナが思いを寄せていたボルイは、妻と離婚してレナと結婚するつもりであったが、妻がひそかに堕胎をしたことでロジェという男から脅迫されていた事実を知り、妻を守ろうとする。この事件で、レナとボルイの関係は微妙になっていき……。バーグマンのスウェーデン時代のメロドラマ。", "1935-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NE´NETTE ET BONI", "ネネットとボニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「パリ、18区、夜。」で注目されたフランスの女性監督C・ドゥニの第２作。両親の元を飛び出し、ピザ屋でバイトをしている19歳の青年ボニの家に、ある日15歳の妹ネネットが転がり込んで来た。しかも彼女は妊娠中。ボニはしぶしぶ同居することにするが、次第にネネットとの間に不思議な絆が芽生え始めていく……。兄と妹の交流を見つめる繊細なムードや彼らの仕草、さりげなく置かれた物など、官能性を際立たせる柔らかな映像が印象的。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動警察パトレイバー2 the Movie", "機動警察パトレイバー2 the Movie", "", "", "http://ecx.images-amazon.com/images/I/51psa-T7F9L._SL210_.jpg", 113, "押井守監督が手がけた人気オリジナルビデオアニメーション「機動警察パトレイバー」の劇場版第2作。2002年、突如として横浜ベイブリッジが爆破される事件が発生。自衛隊機によるものと報道されるが、該当する機体は存在しなかった。同様の不審な事件が都内で相次ぎ、警察と自衛隊の対立が深刻化。事態を重くみた政府は実働部隊を治安出動させ、東京は事実上の戦争状態に置かれてしまう。警視庁特車二課第2小隊隊長の後藤らは、東京に戦争を再現させてみせたテロリストの正体とその真相を追う。竹中直人、根津甚八が声優として出演している。", "1993-08-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNCHAINED: THE TRUE STORY THE MAN WHO BROKE1,000 CHAINS", "エリオット・バーンズの帰還", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "第一次大戦の帰還兵バーンズは夢を求めて南部へと旅立つが、無実の罪を着せられてジョージア州の刑務所に収容されてしまう。脱獄しシカゴで成功するまでの実話を、名匠D・マンが描く。「ウィロー」で人気を博したV・キルマー主演。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みなさん、さようなら", "みなさん、さようなら", "", "", "http://image.eiga.k-img.com/images/movie/57982/315x210.jpg?2273", 0, "「アヒルと鴨のコインロッカー」「ゴールデンスランバー」の中村義洋監督と濱田岳が5度目のタッグを組み、久保寺健彦の同名小説を映画化。生まれ育った団地から出ずに生きる男の孤独や葛藤、成長を描き、濱田は主人公・渡会悟の13歳から30歳までを演じきる。1980年代に団地で生まれたごく普通の少年・悟は、小学校の卒業とともに「団地から一歩も出ずに生きる」と決める。中学校には通わず、団地内のパトロールを日課に日々を過ごし、やがて団地内のケーキ屋に就職。同級生と婚約もして人生をそれなりに謳歌していたが、時代の変遷とともに多くの人が団地を去り、悟は1人取り残されていく。", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みゆき", "みゆき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "血のつながっていない妹と、マドンナ的存在の同級生に恋心を抱き、二人の“みゆき”の間で戸惑う少年を描く青春ラブ・コメディ。あだち充の同名マンガの映画化。原作のほんわかとした感じと井筒和幸のとぼけた演出がうまく溶け合い、微妙な思春期の心の動きを捉えた佳作となった。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オー・ド・ヴィ", "オー・ド・ヴィ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "函館の美しい景観をバックにした愛憎のドラマ。酒のコクにこだわる男たちが眩しい。さらに、男と女の駆け引きを官能的な夜の映像を交えて描写。あがた森魚によるバンドネオン調の音楽が、ミステリアスな雰囲気を盛り立てる。素っ裸の女性の死体が浜辺に上がったことが騒ぎになっている函館。バーテンダーの順三郎は、憂うつを酒で紛らわそうとしていた。そこにシェフ見習いの女性、火露見が現われ、二人は急速に惹かれ合っていくのだが……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バカヤロー！３ へんな奴ら", "バカヤロー！３ へんな奴ら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "オムニバス喜劇の第３作。前２作と同様に４人の新人監督を起用。今回は「SO WHAT」などで知られる山川直人もメガホンを執っている。渋滞に巻き込まれた男、出戻りの姉にほんろうされる娘、部下の転職を止める上司、クリスマスを迎えた商店街の若者たちと、それぞれが怒りを爆発させるまでを描く。各エピソード名は「こんなに混んでどうするの」「過ぎた甘えは許さない」「会社をナメるな」「クリスマスなんか大嫌い」。", "1990-10-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Volcano", "ボルケーノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「ツイスター」「ダンテズ・ピーク」などとともに、1970年代に一世を風靡した自然災害パニックものを、1990年代の最新SFXテクニックを投入することで“再生”させた１本。ロサンゼルスの観光名所ラ・ブレアにある公園のタール池から、ある日突然マグマが噴出する。マンホールのフタを吹き飛ばすほどの勢いで、ひたすら突き進む溶岩。火山弾がビルを襲い、火山灰は雪のように降り積もる。ロス郡危機管理局（EOC）のローク局長は、町の被害を最小限に食い止めるために地道な努力を続けていく。ドロドロ感もリアルな溶岩は、CGとは思えない迫力。また、人災をむやみに飛び火させてスリルを高めるのではなく、マグマの破壊力を真正面から直視。それに対峙する庶民それぞれの姿勢こそを、丹念に拾い上げていく。娘思いの局長にT・L・ジョーンズが扮するほかはスター俳優が見当たらないことも、シークエンスそのものの感動を高めていると言えるだろう。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Happiness", "ハピネス", "", "", "http://image.eiga.k-img.com/images/movie/1822/315x210.jpg?4256", 134, "", "2000-07-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("This is It", "マイケル・ジャクソン THIS IS IT", "", "", "http://image.eiga.k-img.com/images/movie/54822/315x210.jpg?3851", 111, "2009年6月に急逝したマイケル・ジャクソンが、同年夏にロンドンで開催するはずだった幻のコンサート「THIS IS IT」のリハーサルとその舞台裏を収めたドキュメンタリー。100時間以上に及ぶ楽曲とパフォーマンス映像や、舞台裏でのマイケルの素顔を記録。監督は、ロンドン公演そのものの演出も務めていたケニー・オルテガ。", "2009-10-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陽暉楼", "陽暉楼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "五社＝宮尾コンビによる２本目の作品で、土佐・高知の遊廓が舞台になっている。侠客の世界に生きる父と遊女となった娘との愛憎を中心に物語が展開される。五社監督のダイナミックな演出は、女同士が風呂場で切り合いの格闘をする場面などで発揮。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURDER IN THE HEARTLAND", "マーダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "1958年、アメリカの犯罪史上、最悪の事件の一つとして挙げられているスタークウェザー＝フューゲート事件を映画化。かつてマーティン・シーン主演で作られた「地獄の逃避行」のリメイクでもある。19歳で恋人の両親を射殺、14歳の女は彼についていくしかなかった。あてのない逃避行で二人はゲームのように44時間で11人を殺す。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トパーズ", "トパーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "高級SMクラブに勤めるアイは、シティホテルで様々な客を相手にするコールガールでもある。目的や意志がないまま生活をする彼女に快楽の極限を迫る客たち。次第に歪んでいく彼女の前に高級娼婦のサキが現れた……。自身の手による同名小説を村上龍が映画化。OLを中心とする女性客の支持を受け大ロングラン上映を果たした。ビデオ、DVDタイトルは「TOPAZ（トパーズ）TOKYO DECADANCE」。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Everything Will Be Ok", "きっと全て大丈夫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 14, "", "2006-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・若い季節", "続・若い季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "当時の“３人娘”中尾ミエ、園まり、伊東ゆかりが、OLに扮して魅力を見せる青春喜劇。プランタン化粧品では、新製品の男性化粧品を発売するが、まったく売れない。社員のミエ、まり、ユカは、ボウリング場で知りあった男性を、ポスターのモデルに使って大成功を収める。ところがこの男性、会社が融資を頼んでいたサン興業の社長だった……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お嬢さん", "お嬢さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "大会社重役の一人娘・かずみもお年頃。よく家に来る父の部下たちの中で、いちばんハンサムな牧に求婚されたものの、彼女はやたらに女の子にモテるちょっと不良っぽい沢井を選んでゴールイン。しかし彼らの新婚生活は、彼の昔の恋人たちの出現が続き騒ぎが絶えない……。若尾文子主演の楽しい青春映画。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LURED", "誘拐魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "20世紀初頭のロンドンで若い女性の失踪事件が相次ぐ。友人が行方不明になったサンドラは警察に協力し、おとりとなって犯人をおびき出そうとする。彼女は様々な怪しい人物と遭遇していくことになるが……。B・カーロフ、C・コバーンなどクセのある脇役俳優たちの怪演が存分に楽しめる傑作サスペンス。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BODY SNATCHER", "死体を売る男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "R・ワイズがスティーヴンソンの短編を映像化した劇場未公開作。19世紀エジンバラ。医学者マクファーレンに、ある男が研究用の死体を売ろうとするが、マクファーレンは男を殺してしまう……。ホラー映画の二大スター、カーロフ、ルゴシの共演がマニアにはうれしい。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kurosawa, la voie", "黒澤　その道", "", "", "http://image.eiga.k-img.com/images/movie/57352/315x210.jpg?2661", 52, "", "2011-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VOIE LACTEE", "銀河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "無神論者のレッテルを貼られそれを忌み嫌ったブニュエルによる<福音書>。現代からキリストの時代へ、あるいは中世へ、18世紀へ、4世紀へ−−SF映画のように自由闊達に飛びながら、パリ郊外からスペインの聖地サンチャゴに至る<銀河>をゆくふたりの怪しげな巡礼ピエールとジャンの冒険譚を描く。", "1984-07-28", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺たちの世界", "俺たちの世界", "", "", "http://image.eiga.k-img.com/images/movie/54922/315x210.jpg?2120", 0, "いじめ、体罰、集団レイプなど、過激な衝動に駆られる若者を描いた群像劇で、新鋭・中島良の長編デビュー作。第29回ぴあフィルムフェスティバルで審査員特別賞を受賞。両親を殺害し、爆弾を持って街へでかけていくリョウ、見ず知らずの少女を強姦して警察から逃亡するヒロキ。ある日、彼らの運命が交差する。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love in Space 全球熱恋", "ハッピーイヤーズ・イブ", "", "", "http://ecx.images-amazon.com/images/I/51qfVaYxSHL._SL210_.jpg", 0, "", "2011-01-01", "8,6,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("輪回／SAWSARA", "輪廻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "1980年代の解放政策まっさかりの社会に育つ青年たちの姿を描き、好評の作家、ワン・シュオの小説の映画化。高級幹部の子弟として生まれ、個人経営ブローカーの仕事をしてその日暮らしの生活を続けるシー・パー。そんな彼がある時、舞踊家のユイに恋をするが、それとともに仕事は行き詰まりだして……。監督は「黒砲事件」などの風刺劇的作風で知られるホァン・チェンシン。", "1988-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("借王〈シャッキング〉", "借王〈シャッキング〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "『リイドコミック』に連載されていた同名コミックを映画化。15億円を使い込んでしまった銀行員と、援助交際にうつつを抜かすうちにサラ金地獄にはまった中年刑事、経営に行き詰まった高級クラブのマダムが手を組んで、悪徳不動産業者を相手に一大詐欺計画を画策。騙し騙されるマネー・ゲームがスリリングに展開する。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏時間の大人たち", "夏時間の大人たち", "", "", "http://ecx.images-amazon.com/images/I/21KBJP0RSJL._SL210_.jpg", 73, "CMディレクターであり、「バカヤロー！　私、怒ってます」の一話を監督した中島哲也の長編劇映画第１作。小学４年生のたかしの父親が女子高生とカラオケボックスに立てこもり、母親がその篭城を撃破。いとこの夏子はヘアヌードになってしまう。大人たちのそんな行動に、たかしの疑問は迷宮に入り込むのだった。今時の少年の現在を軸に、大人たちの少年少女時代が絡み、それぞれの思いが浮かび上がってくる。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIROCCO", "モロッコ慕情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "J・ケッセルが書いた小説の映画化。フランス占領下のダマスカスで暗躍する武器密輸商人が自由への闘争と激しい恋に身を焦がす。トレンチコートに身をかためた、「カサブランカ」を思わせるH・ボガートの魅力が光るラブ・ロマンス。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nowhere Boy", "ノーウェアボーイ　ひとりぼっちのあいつ", "", "", "http://image.eiga.k-img.com/images/movie/55362/315x210.jpg?1918", 98, "若き日のジョン・レノンと彼の2人の母親の交流を描いた青春ドラマ。1950年代のリバプール。厳格な伯母ミミに育てられているジョンは、近所に実の母親ジュリアが住んでいることを知り、ミミに内緒で会いに行く。自由奔放なジュリアからロックミュージックを聞かされたジョンは音楽活動にのめりこんでいくが、17歳の誕生日にある事件が起こり……。英国の人気芸術家サム＝テイラー・ウッドの長編映画デビュー作。ジョン役には「キック・アス」のアーロン・ジョンソン。共演にクリスティン・スコット・トーマス。", "2010-11-05", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEE SEASON", "綴り字のシーズン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "全米で人気のスペリング大会をモチーフに、すれ違っていく家族がその危機を乗り越えるまでを描く感動作。自らの才能に戸惑う可憐な少女に扮したのは、映画初出演のフローラ・クロス。リチャード・ギア、ジュリエット・ビノシュといったベテラン俳優に劣らぬ名演を見せる。大学教授でユダヤ神秘主義に傾倒する父親は、スペリング大会を勝ち進む娘の才能に夢中になりすぎ、家族との関係が悪化していく。", "2005-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HONEYMOON ACADEMY", "ハネムーン・アカデミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "交際５週間目で電撃結婚、ハネムーンはヨーロッパ旅行と幸せの絶頂のクリスとショーン。ところがこのクリス、旅行代理店のOLというのは表向き、実は国防省の女スパイだった。ハネムーンでも、さっそくスパイ戦に巻き込まれるが、ショーンはクリスの正体を知らない。二人の運命は……。全編スピーディな展開でみせるコメディ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIMEBOMB", "タイムボンバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "失われた自分の記憶を捜し求める男を主人公にしたSFタッチのアクション。平凡な日々を送るエディーは、何者かに命を狙われて以来、悪夢に悩まされるようになる。彼は、自分がCIAによって記憶を消され、殺し屋として養成されたことを思い出していく……。P・ケンジットのセクシーなヌードも見もの。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AFRICA SCREAM", "凸凹猛獣狩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "ラジオ全盛期からコンビで全米の人気者になったアボット＆コステロは1940年代から1950年代にかけて映画でもドル箱的存在だった。日本では“凸凹コンビ”として戦後のスクリーンを大いにわかせ、これはその17作目にあたる。珍コンビがアフリカを舞台に繰り広げるドタバタ劇。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAD BOMBER", "マッド・ボンバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "麻薬中毒による一人娘の死を社会のせいにして学校や病院を連続的に爆破していく男と、性的コンプレックスから強姦魔になった男。それを追うロス警察の敏腕刑事の強引捜査。汚濁しきったアメリカ社会の一側面をサスペンスたっぷりに描いた異色編。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天平の甍", "天平の甍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 152, "日本仏教界確立のため、唐の高僧・鑑真に渡日を要請する使命を帯びて遣唐船に乗った４人の青年僧の波乱に富んだ人生を、本格的な中国ロケでスケール豊かに描いた、壮大な歴史ロマン。日本・中国間の文化的交流のルーツを知るうえでも恰好の作品といえる。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’UCCELLO DALLE PIUME DI CRISTALLO", "歓びの毒牙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「サスペリア」「フェノミナ」などで知られるホラーの巨匠、D・アルジェントのデビュー作。巻き込まれ型の導入部分や犯人の二重人格、鳥や昆虫、加えて細部にこだわるカメラワークなど、後年のアルジェント映画に欠かせない要素を詰め込んだ猟奇的サスペンス。", "1969-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISS FIRECRACKER", "ミス・ファイヤークラッカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "優秀で美しい、いとこのエレインといつも比較され、劣等感に苛まれていたカーネル。彼女は美人コンテストにチャレンジし、自信を取り戻そうとするが……。オフ・ブロードウェイで評判を呼んだ舞台劇の映画化。舞台版に主演し、“ハマリ役”と称賛されたH・ハンターが引き続きヒロインを演じた。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RACE FOR YOUR LIFE，CHARLIE BROWN", "がんばれ！スヌーピー大旋風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "米国の漫画家C・M・シュルツが作り出した人気キャラクター“ピーナッツ・チーム”が、サマーキャンプを舞台に繰り広げるアニメーション。「スヌーピーとチャーリー」（1969）、「スヌーピーの大冒険」（1972）に続く第３作。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MON BEL AMOUR MA DE´CHIRURE", "わが美しき愛と哀しみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "大女優を夢見るカトリーヌは、ある夜、見知らぬ男に路上で犯される。以来、彼女はその男パトリックの虜となり、我を忘れるほどの肉欲の悦楽に身を焦がしていくが……。あまりにも赤裸々なセックス描写が、物議を醸した問題作。動物的愛の世界が生々しくも刺激的。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SとM 劇場版", "SとM 劇場版", "", "", "http://image.eiga.k-img.com/images/movie/55252/315x210.jpg?1368", 104, "平凡なサラリーマンの戸田は、妻と娘3人で慎ましくも幸せな生活を送っていた。そんなある日、戸田は、勤務先に配属された派遣社員の沙耶に執拗に迫られ、肉体関係を持つようになる。やがて沙耶の行為は次第にエスカレートしていき……。", "2010-03-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("甜蜜蜜／COMRADES，ALMOST　A　LOVE　STORY", "ラヴソング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「君さえいれば　金枝玉葉」の俊英P・チャンが、L・ライ、M・チャンという人気絶頂の２大スターを配して撮ったラブ・ストーリー。それぞれの事情で大陸から香港に渡ってきたシウクワンとレイキウ。マクドナルドで偶然出会った二人が、離別を重ねながらも深い絆で結ばれていく10年間の軌跡を綴る。ささやかな夢を抱きながら根無し草のような都会生活を送る男女のピュアな恋を、マンハッタンでのクライマックスまで、このうえなく丹念な演出で描き込んだP・チャンの手腕が鮮やか。劇中に何度も流れるテレサ・テンの歌がフィクションと現実をリンクする役割を果たし、ドラマをエモーショナルに彩っている。また“カメラを抱えた渡り鳥”ことC・ドイルが、英会話教室の酔いどれ講師に扮して泣かせてくれるのも要チェック。香港アカデミー賞ではグランプリを含む９部門を独占した、まぎれもない傑作！", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Charlotte et son Jules", "シャルロットとジュール", "", "", "http://image.eiga.k-img.com/images/movie/14422/315x210.jpg?2102", 14, "「勝手にしやがれ」の2年前に撮影されたゴダール監督ベルモンド主演の短編で、クレジットされていないが台詞協力はトリュフォー。ロメール監督ゴダール主演の短編から始まったアンヌ・コレット主演の<シャルロット>シリーズの1篇で、ゴダール監督では「男の子の名前はみんなパトリックっていうの」に続く2作目。ゴダール自身のアパルトマンで撮影され、兵役中だったベルモンドの声をゴダールが吹き替えた。初夏のパリ、うなだれるジュールのアパルトマンにシャルロットが戻ってきたが……。", "1958-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばりのすべて", "ひばりのすべて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "昭和46年５月の四国公演に始まり、６月の新宿コマ、７月の帝国劇場の両記念公演や、その間の私生活など、芸能生活25周年を迎えた美空ひばりの人間ドキュメント。敗戦の焦土から生まれ、日本人の心を潤してくれた『東京キッド』『りんご追分』など、ひばりの数々の名曲もたっぷり聞ける。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春の居場所", "春の居場所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 59, "2004年に急逝した作家・鷺沢萠の遺作となった未完の小説をもとに、新たに創作部分を追加して実写映画化した青春ドラマ。家庭の事情で東京に引越すことになり、都立高校に通いはじめた芽衣子は、同級生の男子ゼンコーに恋心を抱き、高校2年のバレンタインデーに告白しようと決意するが……。「ALWAYS 三丁目の夕日」の堀北真希がヒロインを好演。「真昼の花」の秋原正俊監督がメガホンを取る。", "2006-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ECHOES OF A SUMMER", "別れのこだま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「タクシードライバー」で妖しい魅力をふりまいた天才子役、J・フォスターが、不治の病に冒された少女を好演。彼女が自分は長く生きられないと知りながらも、大人の女に憧れる姿が胸を打つ。監督を、役者出身のD・テイラーが手掛けている。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ISLANDS", "アイランズ／島々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「トルペド航空隊」などで知られるS・D・アラノヴィッチ監督の呼びかけに応じて製作された、日露合作による北方領土ドキュメンタリー。映画は２部構成となっており、日本側の大塚汎監督の手になる「昭和」とアラノヴィッチが監督した「平成」に分かれる(全体の構成はアラノヴィッチが担当)。最初の「昭和」では、失われた島に対する元島民の想いが語られ、そして「平成」では今現在、北方四島に住むロシア人たちの姿を追っていく。前半部、元島民の表情を徹底的に狙った映像はそれほどの感動を呼ぶことはないが、後半部にさしかかるや映画は一気に生気を帯びる。島々の荒涼たる風景と濁った光、そしてそこに生きる人々の感動的なモノローグ……。打ち捨てられた島々に生きねばならない悲劇をことさらに嘆いてみせることなく、それに甘んじて生きていく彼らの姿を切り取ったカメラの素晴らしさ。O・カラバイチュークの音楽も印象深い。", "1993-01-01", "76,24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春の鐘", "青春の鐘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "名門・依田家の家庭教師となった東大生の村瀬は、小学生ながらガリ勉を強いられている春夫と急に相撲をとったりして母・宗子を驚かす。そんな村瀬に好意を寄せる姉・久美子の態度に気づいた秘書の合田は、横恋慕して二人を引き離すべく卑劣な策を練るのだが……。倉本聰のオリジナル脚本による青春映画。", "1969-01-01", "76", 0, NOW(), NOW());
