INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tuppen", "気取り屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「ショコラ」等の作品で知られる、スウェーデンが生んだ名匠ラッセ・ハルストレム監督初期の作品。第2次世界大戦中のスウェーデンの田舎町を舞台に、奔放な女たちに振り回される男の姿を描くラブ・コメディ。若い男がほとんど戦場へ行ってしまい、寂しく町に取り残された乙女たち。ある日、彼女たちが働く小さな工場に、1人の男性エンジニアがやって来る。可愛らしい女の子に次々と誘惑されて夢見ごこちの彼だったが……。", "1981-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海はふりむかない", "海はふりむかない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "西郷輝彦を主役にすえた青春悲恋メロドラマ。エリート・コースをひた走る兄とは対照的に弟・礼次は気まま暮らしだ。出世のために兄がふった恋人に礼次は同情するが、原爆症を病んだ彼女の余命の短さを知り、同情は激しい愛へ……。彼女の亡骸を前に礼次が絶句するクライマックス・シーンは見ものだ。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("10thアニバーサリー　劇場版　遊☆戯☆王　超融合！時空を越えた絆", "10thアニバーサリー　劇場版　遊☆戯☆王　超融合！時空を越えた絆", "", "", "http://image.eiga.k-img.com/images/movie/55052/315x210.jpg?4249", 49, "TVシリーズ化もされた人気カードゲーム・コミック「遊☆戯☆王」の劇場版第3弾。カードバトル・デュエルによってネオ童実野シティを守っている不動遊星は、世界の破滅を目論む未来から来た謎の男・パラドックスに自分のエースモンスターであるスターダスト・ドラゴンを奪われてしまう。遊星は時空を超え、カードの精霊の力を使うことができる遊城十代を訪ね、そこで事件の真相を知る。2人は伝説のデュエリスト・武藤遊戯と力を合わせ、パラドックスとの壮絶なデュエルに向かう。", "2010-01-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEVEN　DAYS　IN　MAY", "５月の７日間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "19XX年の５月。アメリカ大統領が提案した核禁止条約案をソ連が受諾、議会でも批准される。だが、それは軍需産業の終末を意味することから軍部や企業家が反発。米軍統合参謀本部議長のスコット将軍は、この条約反対の急先鋒だった。ある日、スコット直属のケイシー大佐は、通信室で妙な通信文を目にしたことから、スコットの行動に疑惑を持ち始める。彼は何をたくらんでいるのか？　シビリアン・コントロール（＝文官統治）を嫌う軍関係者の陰謀と対決を、社会派J・フランケンハイマーがリアルかつスリリングに描いた政治サスペンス。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガメラ３・邪神〈イリス〉覚醒", "ガメラ３・邪神〈イリス〉覚醒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "金子修介による新生“ガメラ”シリーズの第３作。４年前にガメラと死闘を繰り広げた怪鳥ギャオスが大量発生し、渋谷の街が破壊される。折しも、かつての激戦で両親を失って以来、ガメラを恨み続ける中学生の綾奈は、森の中で奇妙な生物を誕生させていた。ギャオスの変異体であるその生物イリスはみるみる巨大化し、ガメラの新たな敵として人々を恐怖に陥れる。前半、渋谷の街が破壊されるリアルな映像が圧巻。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地下道", "地下道", "", "", "http://image.eiga.k-img.com/images/movie/54492/315x210.jpg?3904", 29, "「トリコロール」3部作など多くの傑作を手掛けた名匠クシシュ トフ・キエシロフスキーが1973年に制作した短編ドラマ。ポーランドの小さな町で教師として働く男。別居中の妻と寄りを戻そうとワルシャワを訪れた彼は、ワルシャワ中央駅の地下道で彼女と再会するが……。妻役にアンジェイ・ワイダ監督作「コルチャック先生」のテレサ・ブジシュ＝クシジャノフスカ。キエシロフスキー自らがナレーションを務めている。", "2009-06-20", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少女戦争", "少女戦争", "", "", "http://image.eiga.k-img.com/images/movie/56212/315x210.jpg?4065", 82, "「ひぐらしの泣く頃に」シリーズの及川中監督が描くサバイバルアクション。女子高生のリオカは、数日間学校に姿を見せていない友人のミキから、携帯メールで女子高生限定の裏サイトゲームの勧誘メールを受け取る。そのサイトでは、あるゲームをクリアすると望むものが手に入るという。リオカは、ミキを助け出そうと裏サイトに入会するが、そのゲームとは女子高生同士の殺し合いだった……。", "2011-01-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("5x2", "ふたりの5つの分かれ路", "", "", "http://image.eiga.k-img.com/images/movie/1232/315x210.jpg?1652", 90, "「8人の女たち」「まぼろし」「スイミング・プール」の人気監督、フランソワ・オゾンが一組のカップルの恋愛をユニークな手法で描く。離婚調停を進めるカップル、マリオンとジル。彼らがどのようにしてそこに至ったのかを、時間を逆行させて、ある日の夕食、出産、結婚、恋に落ちた瞬間まで、5つの季節を経ながら遡って描いていく。ヒロイン役は「天使の肌」「愛する者よ、列車に乗れ」のヴァレリア・ブルーニ・テデスキ。", "2005-08-20", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOLOGRAM MAN", "ホログラムマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "極悪非道の犯罪者スラッシュ・ギャラガーは、刑事デコーダーに逮捕され、ホログラム刑に処される。ホログラム刑とは、人間をホログラム化し、再教育を施すという最新の刑罰だが、ギャラガーは、配下によってホログラムのまま再生してしまう。弾丸も炎も受け付けない不死身の身体となったギャラガーは、街を牛耳る高級幹部たちを襲い始める。ギャラガーに殺されかけたデコーダーは、自らもホログラムマンとしてよみがえり、ギャラガーと対決する。映像効果のイメージ先行型のSFアクション珍作。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FULL MONTY", "フル・モンティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "本国イギリスをはじめ、世界中で記録的な大ヒットとなった快作コメディ。男性ストリップに挑んだ失業者たちの大奮闘劇が展開する。主人公は、無職ゆえに愛する息子の養育権を失いそうなガズ。男性ストリップ・ダンサーの人気に目をつけた彼は、“裸で一攫千金！”とばかりに、ショーの興行に乗り出す。自分を含めて総勢６人の失業者たちからなるダンス・チームを結成したガズだったが、踊れない、恥ずかしい、妻には言えないなどの問題が相次ぎ……。“フル・モンティ”とは、素っ裸を指す俗語。様々な紆余曲折を経て、タイトル通り、すっぽんぽんになるまでの人間模様が軽快に描かれる。英国の不況を反映しつつ、なりふり構わず前向きに前進する男たちの姿が痛快だ。愛すべきダメ男ガズを、R・カーライルが妙演。笑って泣けるサントラも必聴。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ボレロ", "ボレロ", "", "", "http://image.eiga.k-img.com/images/movie/57672/315x210.jpg?3950", 75, "監督2作目の新鋭・田島基博が手がけたモノクロサイレントのラブストーリー。恋人と別れ、母親が病気になってしまったギタリストのフトシは失意のどん底で街をさまよい、5匹のイヌを連れた聴覚障害者のユキと出会う。障害に負けずに生き、母親の介護にも協力してくれるユキにフトシは次第にひかれていき、母親とユキのために真剣に音楽に向き合おうと決意する。そんなある日、音楽プロデューサーからCD発売の話が舞い込むが……。", "2012-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MASK OF ZORRO", "マスク・オブ・ゾロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "今まで何度となく映画やテレビで描かれてきた、黒ずくめの衣装に身を包んだ黒マスクの英雄、ゾロの活躍を描くエンターテインメント大作。独立宣言したメキシコ、本国から召還されたカリフォルニア知事のモンテロは宿敵ゾロがディエゴであることを見抜き、その幼い娘を奪い、ディエゴを投獄してしまう。それから20年後、モンテロがカリフォルニア統治の野望を抱き、再びメキシコに戻ってくる。ディエゴは彼の横暴を断つために脱走。酒場にいた若者アレハンドロに目をつけ、彼を二代目ゾロにするため特訓を開始する…。ゾロが一人ではなく、世代から世代へ受け継がれていく正義の象徴として捉えているところが新味。A・バンデラスがセクシーかつユーモアの効いたゾロを熱演。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋は舞い降りた。", "恋は舞い降りた。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "不幸な女性を幸福にするという契約を天使と交わして地上に戻る、死亡した売れっ子ホストと、離婚の傷が癒えない孤独なOL。愛を信じない男と、愛を信じたい女の間に芽生える心の絆をロマンティックかつコミカルに描いたラブ・ファンタジー。クリスマス・イヴに奇跡が訪れるクライマックスでは、ハート・ウォーミングな味わいを与えてくれる。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WONDERFUL WORLD OF THE BROTHERS GRIMM", "不思議な世界の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "シネラマで製作されたグリム童話の映画化。グリム兄弟の伝記物語を中心に、劇中劇として「踊るお姫さま」（イベット・ミミュー、ラス・タンブリン)、「くつ下直しと小妖精」（ハーヴェイ)、「歌う白骨」（テリー・トーマス）が挿入される。幻想の世界を再現したG・パルの特撮が見もの。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Armageddon", "アルマゲドン", "", "", "http://ecx.images-amazon.com/images/I/51NSBK4P50L._SL210_.jpg", 150, "「ザ・ロック」でヒットを飛ばした監督M・ベイと製作J・ブラッカイマーが再び手を組んで送り出したSF映画の超大作。NASAはテキサス州に匹敵する大きさのアステロイド(小惑星)が時速３万５千キロで地球に接近していることを発見。このままでは地球が壊滅することは間違いない。これを回避する手段として、アステロイドに穴を空け、その中で核爆弾を爆発させることで軌道を変化させようと思いついたNASAは世界一の掘削のプロ、ハリー・スタンパーに白羽の矢を立てる。訓練を終えた彼とその仲間７人は、NASAの操縦士たちとともにスペースシャトルに乗り込む。残された時間はあと３日しかない……。未知のアステロイド上で彼らが繰り広げるアドベンチャーの数々をノンストップ・アクションで描いていく。なお、L・タイラーの父スティーヴン・タイラー率いるエアロスミスが主題歌を手掛けたことも話題となった。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHINATOWN", "チャイナタウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "1960年代ポーランド映画を代表するポランスキーがアメリカで製作した、ハードボイルド映画の傑作。1937年のロサンゼルス。私立探偵ギテスのもとに、ミセス・モーリーと名乗る女がやって来る。ところがそれは、モーリーという人物に仕掛けられた罠だった。それを知った本物のモーリー夫人がギテスの事務所を訪れた直後、モーリーの溺死体が上がった。調査を始めたギテスは、事件の裏にモーリー夫人の実父で政界の黒幕クロスの存在を見いだすが……。ニコルソンの好演はもちろん、異様な輝きを持った目で宿命の女を全身で演じ切るダナウェイが圧巻。退廃ムード漂う1930年代ロサンゼルスの風俗を克明に映し出すカメラも出色だ。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺に賭けた奴ら", "俺に賭けた奴ら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「百万弗を叩き出せ」に続く、ボクシングを題材にした鈴木清順＝和田浩治コンビ作。今にも東洋タイトルマッチが行われようとしていた。チャンピオン石原に挑むのは藤原清次。しかし、彼がその場に立つまでには彼自身と、彼に賭けた奴らの傷だらけのドラマがあったのだ。前作に続き好評を博したが、清順は犬がウロチョロするなど本筋に関係ない部分をやたら熱心に撮ったという。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STROKER ACE", "ストローカーエース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "レイノルズお得意のカー・アクション映画。派手で陽気で女好きのストローカー・エースはデイトナ500レースで負けたためスポンサーを失い、ようやく現れた次のスポンサーは彼を散々こき使う。頭にきたエースは契約を破棄するため、一計を案じるが……。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Atame!", "アタメ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "スペインの鬼才ペドロ・アルモドバル監督が、初期作品の常連アントニオ・バンデラスを主演に迎えて描く異色のラブ・ロマンス。精神病院に入退院を繰り返す青年リッキーは、まともな生活を送るために結婚を決意する。彼はその相手として以前から憧れていたポルノ女優マリーナを選び、一方的に求愛するが、当然彼女には相手にされない。そしてとうとうマリーナの家に押し入った彼は、怒り出した彼女をベッドに縛り付けてしまう……。", "", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("中国盒子／CHINESE　BOX", "チャイニーズ・ボックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「スモーク」のW・ワンが中国返還に揺れる故郷、香港を見つめたヒューマン・ドラマ。香港に魅せられて15年住み着いたイギリス人ジャーナリスト、ジョンは中国人女性ヴィヴィアンに恋をした。だが、実業家の婚約者がいるヴィヴィアンは心動かされながらも彼を突き放す。そんなある日、ジョンは自分が白血病に冒されて余命いくばくもないことを知った。ビデオカメラを手に、返還前の香港を見つめる彼は、生命感にあふれた香港女性ジーンの行方を追いかけるうちに、ヴィヴィアンの意外な過去を知ってしまう……。国籍の違う３人の男女の関係に、香港の国際的な立場を投影。政治的なテーマとともに、求めてもすれ違う恋愛模様が強く胸を打つ。", "1997-01-01", "3,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("縛師 Bakushi", "縛師 Bakushi", "", "", "http://image.eiga.k-img.com/images/movie/53672/315x210.jpg?4638", 94, "SMビデオやイベントなどでモデルを縛る職業「縛師」を題材にしたドキュメンタリー。「M」「ヴァイブレータ」など男女の関係を描いた作品を撮り続けてきた廣木隆一監督がドキュメンタリーに初挑戦し、SM映画「花と蛇」で緊縛指導を担当した有末剛ら3人の現役縛師に密着。絶対的な信頼関係のもとで行なわれる縛師とモデルとの「対話」を真正面からとらえ、日本独特の文化である「緊縛」の本質を浮かびあがらせていく。", "2008-05-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ノン子36歳（家事手伝い）", "ノン子36歳（家事手伝い）", "", "", "http://image.eiga.k-img.com/images/movie/53852/315x210.jpg?2983", 105, "「鬼畜大宴会」「フリージア」の熊切和嘉監督が、30歳半ば女性の再生を描いたドラマ。主演は「実録・連合赤軍　あさま山荘への道程（みち）」「ビルと動物園」の坂井真紀、共演に「69　sixty nine」の星野源、鶴見辰吾、津田寛治ほか。実家の神社の手伝いをするバツイチ出戻りのノブ子（通称ノン子）の前に、神社の露店でひと儲けしようとする青年マサルが現れる。世間知らずだが純粋なマサルに、やる気もなく殻に閉じこもっていたノン子は次第に心を開いていく。", "2008-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デンデラ", "デンデラ", "", "", "http://image.eiga.k-img.com/images/movie/56022/315x210.jpg?2733", 118, "佐藤友哉の同名小説を映画化。老人を山に捨てる「姥（うば）捨山伝説」を題材に、死んだと思われていたはずの老女50人が、さまざまな思いを抱えながら、過酷な自然状況のなかで懸命に生き延びていく姿を描く。浅丘ルリ子、倍賞美津子、山本陽子、草笛光子ら日本映画界を代表するベテラン女優陣が集結。同じく姥捨山伝説をテーマにカンヌ国際映画祭グランプリを受賞した「楢山節考」（1983）で知られる故今村昌平監督の息子・天願大介がメガホンをとる。", "2011-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLASTIC CITY プラスティック・シティ", "PLASTIC CITY プラスティック・シティ", "", "", "http://image.eiga.k-img.com/images/movie/53952/315x210.jpg?1240", 95, "日系ブラジル人のキリンは、幼い頃にジャングルでアジア系ブラジル人のユダに拾われ育てられた。青年へと成長したキリンはユダの闇稼業を手伝っていたが、ユダの命が狙われたことをきっかけに人生が急激に変化する……。「悲夢」など国際的に活躍するオダギリジョーがキリン役で主演。ユダ役に「インファナル・アフェア」のアンソニー・ウォン。監督は「長江哀歌」などジャ・ジャンクー監督作品の撮影監督を務めるユー・リクウァイ。", "2009-03-14", "8,6,17,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El Dorado", "エル・ドラド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "エル・ドラドに戻ったガンマンのコールは、旧友の保安官が、水源の権利を持つ牧場主に味方していることから、権利を狙う新興牧場主から命を狙われていることを知る。その後コールは、ガンマン一味が新興牧場主に雇われたことを知り……。本国より半年近く先んじて、日本で世界最初に公開されヒットを記録した。アメリカでも年間第12位の成績を残し、商業映画の巨匠ホークスの健在ぶりを示した。", "1966-12-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Spit on Your Grave", "アイ・スピット・オン・ユア・グレイヴ", "", "", "http://image.eiga.k-img.com/images/movie/56352/315x210.jpg?3401", 106, "男たちに強姦（ごうかん）された女性の壮絶な復しゅう劇を描くバイオレンススリラー。「発情アニマル」の邦題でポルノ映画として劇場公開された後、ビデオ発売時は「悪魔のえじき」、TV放送時は「女の日」と邦題を変化させた1978年製作のカルトムービーのリメイク。美しい小説家のジェニファーは新作執筆のために森の奥の別荘にやってくるが、そこで地元の男たちに襲われ、果てしない暴力にさらされる。殺される寸前に川に身を投じて逃げ切ったジェニファーは、自分を辱めた男たちに復しゅうを開始する。", "2011-06-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Busting", "破壊！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "　ロサンゼルスの刑事コンビ、キニーリーとファレルは麻薬組織のボス、リゾーを追いつめようとする。しかし彼は警察署長と手を結んでいた。不正と汚職がはびこる中、それでも悪に対する怒りを燃やすキニーリーとファレルは執拗にリゾーを追う……。しがない刑事コンビの戦いを描くピーター・ハイアムズの劇場用映画監督デビュー作。銃撃戦も迫力にあふれている。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひかりごけ", "ひかりごけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "昭和18年の冬、北海道で４人を乗せた船が座礁し、船長一人が生還した。しかし船長には、残りの３人の死体の肉を食べて生き延びたのではないかと疑惑がかかる。果して極限状況下で４人に一体何があったのか。人間の生と存在に鋭く問いかけた力作。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("炎の舞", "炎の舞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "戦地で傷を負い送還されてきた拓次と、献身的な看護で拓次との闘病生活を続ける妻・きよの。やがて再び、拓次のもとに召集令状が送られてくる。狂おしいまでの二人の愛の日々を描いた百恵・友和コンビの９作目。1964年の「執炎」のリメイク。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("唐獅子 姐御", "唐獅子 姐御", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "西川峰子主演の壮絶な復讐劇。関西吉本組のヒットマンに夫が殺されたため、代わりに鮭城組四代目を襲名した冴子。一方、組長を守れなかった英二は、単身関西に乗り込み吉本の命を取る。英二が刑務所から出てくると、鮭城組では組長代行の黒木が好き勝手をしていた。黒木は吉本組の元若頭竜崎と組んでいたのだ。それを知った英二は黒木を撃ち、冴子も差し違える覚悟で竜崎に向かう。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("必殺始末人", "必殺始末人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "人気“必殺”シリーズのニューバージョン。名うての刺客だった浪人、山村只次郎はその腕を買われて、与力の白鳥右京の下、正義のために悪人を成敗する闇の始末人となる。だが、実は右京こそが無実の人間を罠にはめ、私腹を肥やす極悪人であることを知った只次郎と仲間のりゅう、かもめは彼に立ち向かっていくのだった。監督は同シリーズの撮影を担当していた石原興で、独特の陰影を生かした映像が本作でも効果を上げている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人生、いろどり", "人生、いろどり", "", "", "http://image.eiga.k-img.com/images/movie/57412/315x210.jpg?1463", 0, "高齢化と過疎化が進む徳島県の上勝町で、70～80代の女性が中心となって葉っぱや道端の草を料理のつまものとして販売し、売上高2億6000万円をあげるビジネスとして成功させた実話を映画化。人口2000人のうち48％が高齢者で過疎化の一途をたどる徳島県の上勝町で、ある日、農協職員の江田が葉っぱをつまものとして売物にしようと立ち上がる。周囲から冷ややかな目で見られる江田だったが、薫、花恵、路子という平均年齢70歳の3人の幼なじみの女性が参加することに。葉っぱビジネスは、それぞれの悩みを抱えていた薫、花恵、路子の3人の人生にも変化をもたらしていく。", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANTARCTIC　JOURNAL", "南極日誌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "韓国映画界が、南極大陸を踏破しようとする探検隊6人の悪夢のような運命を映像化。ウイルスが存在するはずのない極寒の地で風邪をひくなど、不可解な現象に見舞われていく隊員たちの不安と恐怖を描く。押井守監督作などで活躍する川井憲次の音楽が、壮大かつ謎めいた映像を盛り立てている。南極の到達不能点を目指して過酷な旅を続ける韓国の探検隊。その21日目、彼らは80年前に書かれたイギリス探検隊の日誌を見つけ……。", "2005-08-27", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HORRORS OF THE BLACK MUSEUM", "黒死館の恐怖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "犯罪学者でベストセラー作家のバンクロフトは、自ら犯した殺人をネタに小説を書いていた。のぞくと凶器が飛び出す双眼鏡、氷バサミ、硫酸など、凝った小道具を駆使して今日も彼の殺りくは続く……。マニアの間で信奉者の多い怪優M・ガフ主演の異色ホラー。", "1959-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIG　SHOT’S　FUNERAL", "ハッピー・フューネラル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "中国映画らしからぬ軽妙なノリで展開するユーモラスで風刺的なヒューマン・ドラマ。葬式が一大イベントに発展する珍騒動を描きながら、資本主義が浸透した中国の現在を浮き彫りにする。世界的な映画監督タイラーがロケ先の中国で危篤状態に陥る。“笑える葬式を”との要望を受け、現地のカメラマン、ヨーヨーは奔走。やがて葬式にスポンサーが付くという前代未聞の事態に発展する。", "2001-01-01", "8,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Thunderbolt and Lightfoot", "サンダーボルト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "　銀行強盗のサンダーボルトはかつての仲間レッドとエディに追われる身。そんな彼が出会ったのが、風来坊の若者ライトフット。意気投合したサンダーボルトとライトフットはレッドとエディも加え、現金輸送会社を襲うことに。しかし小さなミスが生じて……。「ダーティ・ハリー2」の脚本でイーストウッドに認められたマイケル・チミノが監督・脚本を務めた犯罪ドラマ。大胆な犯行と男同士の友情をダイナミックに描く。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YUMENO〈ユメノ〉", "YUMENO〈ユメノ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ピンク映画「危ない情事　獣のしたたり」などで注目を集めた鎌田義孝監督が、奇妙な逃避行を描いたロードムービー。北海道の荒涼たる風景をバックに、追われるチンピラ、不倫中の女子高生、家出した小学生の３人が織り成す、あてどのない道行きと不思議な絆の行方を綴る。北海道南西部の町。ヤクザに３日以内に100万円用意しろと脅されているヨシキは、女子高生ユメノの家に空き巣に入るが……。", "2005-01-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINISH LINE", "フィニッシュ・ライン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ステロイドに手を出してしまうランナーの苦悩と愛を描く。足が速く、高校時代まで陸上のスターだったグレンは、友人ティトとともに大学へ進み、オリンピック強化チームへ。しかし全国から集まったメンバーたちはものすごく、グレンは力のなさを痛感する。そんな時、ステロイドを使用する重量挙げ選手と知り合う。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOAH", "SHOAH〈ショア〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 570, "数多くの映画が描いてきたナチス・ドイツのユダヤ人に対する歴史的な大虐殺を、全く新たな視点と話法によって捉えてみせた渾身のドキュメンタリー。残された記録を再構成するのではなく、1939年に始まったこの世紀の悲劇に実際かかわった人々に綿密なインタビューを敢行し、彼らが語る“真実”だけを映し出していく。被害者であり、強制収容所から奇跡的に助かったユダヤ人はもちろん、加害者たる元ゲシュタポのメンバー、そして傍観するしかなかった収容所周辺のポーランド人らが、それぞれにとっての“事実”を披露。その言葉、その表情から個人の歴史と現在、アイデンティティーや人間性が静かに浮き彫りになっていく様は、厳しくも感動的である。実に９時間30分という怒濤の長尺だが、ゴダール作品などでおなじみのW・リュプチャンスキーらによる美しすぎるロケーション撮影とも相まって、その映像は穏やかな凝視を誘発するはずだ。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・佐々木小次郎", "続・佐々木小次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "「佐々木小次郎」３部作の第２作。海賊船で自由都市・堺にやって来た小次郎は、琉球娘・奈美と出会うが、元の恋人・兎弥とも再会。三角関係のドラマが繰り広げられる。恋人との出会いと別れを繰り返す小次郎の色男的なキャラクターが固まった。海賊・郡智丸に扮した戸上城太郎と曽呂利伴内役の山本礼三郎の好演が光る。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’INHUMAINE", "人でなしの女・イニューメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "1920年代前衛映画の旗手M・レルビエの幻の傑作が、1986年にサウンド版として復元されよみがえった。パリ郊外の不思議な館に住む歌姫クレールに恋するノーセンは、彼女に冷たくあしらわれ自殺を試みる。しかし、死んだはずのノーセンはクレールのもとに再び姿を現わし、ある機械を彼女に見せるのだが……。", "1923-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホームレス", "ホームレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ニューヨークだけで８万人、全米で300万人と言われるホームレス。社会構造の合理化、核家族化がその原因とされるが、根本的な解消案ができていないのが現状となっている。秋元康がオノ・ヨーコの協力を得て、この問題に挑んだ作品である。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版NARUTO ナルト　ロード・トゥ・ニンジャ", "劇場版NARUTO ナルト　ロード・トゥ・ニンジャ", "", "", "http://image.eiga.k-img.com/images/movie/57622/315x210.jpg?4740", 0, "大ヒットコミック「NARUTO ナルト」のアニメ化10周年を記念し、シリーズ初となる原作者の岸本斉史による完全オリジナルストーリーで描かれる劇場版。", "2012-07-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO LOVE／二つの愛の物語・君と歩いた道", "TWO LOVE／二つの愛の物語・君と歩いた道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "人気シンガー、浜田省吾の楽曲『Thank You』に着想を得て製作された短編映画。15歳の少年少女の淡く切ない恋を瑞々しい映像で綴る。中学３年の夏、“将来の夢”という作文の宿題を書けずに夏休み明けを迎えた大吾。そんなとき、彼の前に真琴という美しい転校生が現れる。次第に彼女に惹かれていく大吾だったが……。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二十世紀少年読本", "二十世紀少年読本", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "サイレント映画をモチーフにした「夢みるように眠りたい」でデビューを飾った林海象が、今度は日本のサーカスをテーマにしたノスタルジックな世界を奏でるモノクロ映画。サーカス団に育った二人の兄弟が、別々の未来へと進んでいく様を描いていく。木村威夫と山崎秀満の美術が秀逸だ。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WOLF AT THE DOOR", "黄金の肉体 ゴーギャンの夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1893年、45歳のゴーギャンはタヒチからパリに戻る。それから二年間の偉大な画家のヨーロッパでの生活を、淡々と描いた伝記的作品。ゴーギャンはタヒチから持ち帰った絵画で、ヨーロッパの画壇に殴り込みをかけようとしていたが……。", "1986-01-01", "2,18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Soldier's Daughter Never Cries", "シャンヌのパリ、そしてアメリカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "「シン・レッド・ライン」の原作者ジェームズ・ジョーンズの娘ケイリーが描いた自伝的小説の映画化。厚い信頼関係に裏打ちされた父娘の絆を中心に、次第に変貌を遂げていく娘シャンヌの成長を名匠J・アイヴォリーが丹念に、そして穏やかに描く感動作。主演のL・ソビエスキーが持ち前の清楚な美しさと知性あふれる演技で好演している。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("趙氏孤児", "運命の子", "", "", "http://image.eiga.k-img.com/images/movie/56962/315x210.jpg?4109", 128, "司馬遷の「史記」にも記載され2000年以上にわたり語り継がれている悲劇「趙氏孤児」を、「さらば、わが愛　覇王別姫」のチェン・カイコー監督が映画化。春秋時代の晋国。文官の趙盾（チョウジュン）は対立する武官の屠岸賈（トガンコ）の謀略により一族を皆殺しにされるが、息子の妻で妊娠中だった荘姫（ソウキ）だけが処刑を免れる。やがて生まれた男児は趙盾の友人だった程嬰（テイエイ）の助けで生き延び、15年後、程嬰は晋国の忠臣・韓蕨（カンケツ）とともに男児に真相を語ってきかせ、ともに仇討ちの決意をする。", "2011-12-23", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・鉄砲犬", "続・鉄砲犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "好評の第５作「鉄砲犬」に引き続き、田宮と坂本のキャラクターをメインに展開。今回は、それと知らずに密輸の金を運ばされた大介が、ストリッパーとなった玉子やスリの安さん、そして木村刑事とともに、ボスのもとに殴り込みをかけていく。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕の彼女とその彼氏（ゆうれい） Drop in Ghost TWILIGHT FILE III", "僕の彼女とその彼氏（ゆうれい） Drop in Ghost TWILIGHT FILE III", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "幽霊が見えてしまう少女と、幽霊の青年との交流を描いた青春ファンタジー。霊能力を持つ女子高生・小夜は、ある晩、赤い橋で幽霊の青年・亮と出会う。亮は離れ離れになった恋人に未練を残し、成仏できずにいたのだ。やがて、亮の恋人が自分の担任の遥先生だと知った小夜は、2人を会わせようと決意する。ヒロインの小夜を演じるのは、アイドルグループ「Folder5」として活動後、映画「デスノート」などで女優として活躍する満島ひかり。", "2007-02-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN DOLLARO BUCCATO", "荒野の１ドル銀貨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "当時、モンゴメリー・ウッドというハリウッド名で出演していたG・ジェンマの日本公開第１作。南北戦争直後を時代背景にしたマカロニ・ウエスタン。悪漢に撃たれたジェンマが、胸ポケットに入れた１ドル銀貨で一命を取り留めるという設定からこのタイトルが付いた。", "1966-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOTEL", "ホテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "ベストセラー作家A・ヘイリー原作による、ホテル業界の抗争劇。ニューオリンズに建つセント・グレゴリー・ホテルは古風な品格を売りものにする名門ホテルだったが、近代的ホテルが建ち並ぶなかでは少々時代遅れ。支配人ピーターは経営不振を打開しようと懸命だが、そのころ、ホテル王オキーフはグレゴリー・ホテルを買収するためにジャンヌという女性を送り込んだ……。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白蛇抄", "白蛇抄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "京都で火事にあい、夫を失った悲しみから自殺しようとしていた女が、山寺の住職に助けられる。住職は彼女を後妻に迎えるが、息子が彼女の肉体の虜になっていると知り激怒する。初ヌードを披露した小柳ルミ子が体当たりの演技をする文芸エロチシズム。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NAKED JUNGLE", "黒い絨氈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "空想科学映画の権威ジョージ・パルとバイロン・ハスキン両巨匠のコンビで描いたパニック・スペクタクル映画。南米アマゾン川上流奥地の農園を舞台に、農園主と新妻との葛藤と愛を交えながら、やがて襲い来る人喰い蟻マラブンタの大群の恐怖が綴られていく。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OH，GOD！ YOU DEVIL", "オー！ゴッド3 ～悪魔はこわい～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "シリーズ第３作。「くたばれ！ ヤンキース」などコメディの分野ではよくある“悪魔に魂を売った男”の話で、G・バーンズが今回は神様と悪魔の二役を演じ分けているのが新鮮だ。出世のために悪魔と取り引きした売れないシンガー・ソングライターのボビーの前に突然神様が現れて……。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エレキの若大将", "エレキの若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "エレキ・ブームに便乗したシリーズ第６作。京南大学アメリカン・フットボール部の主将・田沼雄一は、実家のすきやき屋・田能久の財政難に、仲間とエレキバンドを結成して資金稼ぎ。見事店を再建する。寺内タケシとブルージーンズが得意のエレキギターを披露。挿入歌『君といつまでも』が大ヒットした。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハーケンクロイツの翼", "ハーケンクロイツの翼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "無気力に生きる無口でシャイな若者が、生きることの意味を探し求めて疾走する。ポップでパワフルな青春暴走劇。主演は「あずみ」「ロボコン」の若手注目株・小栗旬。監督を務めるのは、「ポストマン・ブルース」「ピストルオペラ」のプロデューサー、片嶋一貴。脇を固める鈴木清順、津田寛治、韓華恵ほか個性的な面々の活躍にも注目。退屈な日々に嫌気がさしていたRIKUOは、自由奔放な男GASと、突然住み着いた美女NAMIKOと出会って人生が一変。GASとは暴挙を繰り返し、周囲からは怖れられる存在に。そして暴走はどんどんエスカレートして……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Battle of Wits", "墨攻", "", "", "http://image.eiga.k-img.com/images/movie/1022/315x210.jpg?2789", 133, "92年～96年まで、小学館“ビッグコミック”に連載していた同名人気漫画を日中韓＋香港の合同プロジェクトで映画化。2000年以上も前の中国に存在した、自ら攻撃を仕掛けることはなく、守るための戦闘「非攻」を掲げた戦闘集団“墨家”。そのメンバーの1人である革離（かくり）の活躍を描く。監督・脚本は、「流星」のジェイコブ・チャン。主演のアンディ・ラウ他、韓国を代表する名優アン・ソンギら中韓キャストが共演。日本からは阪本善尚（撮影）、川井憲次（音楽）らが参加。", "2007-02-03", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミンボーの女", "ミンボーの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "ヤクザによるゆすり、たかりなどの民事介入暴力（ミンボー）専門の女性弁護士が超高級ホテルに雇われた。彼女がホテルのヤクザ担当者に伝授していく数々の対ヤクザ戦術とは……。ヒットメーカー、伊丹十三監督がジェットコースターのような語り口で描く痛快娯楽作。宮本信子はじめ、おなじみの豪華キャストも魅力。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とある飛空士への追憶", "とある飛空士への追憶", "", "", "http://image.eiga.k-img.com/images/movie/56382/315x210.jpg?4491", 99, "2008年の発売後、口コミで評判が広まり、ラジオドラマ化や漫画化もされた犬村小六の同名ライトノベルをアニメーション映画化。制作は「時をかける少女」「サマーウォーズ」のマッドハウス。中央海と呼ばれる海を挟んだ帝政天ツ上と戦いを続けている神聖レヴァーム皇国の飛空士シャルルは、次期皇妃ファナを水上偵察機に乗せ、婚約者のカルロ皇子の元へ送り届けるという極秘任務に就く。敵の領域を横切る1万2000キロにおよぶ旅は、2人にとって身分違いの恋の始まりでもあった。", "2011-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Swordfish", "ソードフィッシュ", "", "", "http://image.eiga.k-img.com/images/movie/1212/315x210.jpg?4417", 99, "元スパイで天才的犯罪者、ガブリエル・シアーは95億ドル（1兆1500億円）を強奪するため、かつて世界一のハッカーと呼ばれたスタンリーを仲間に引き入れ、システムに侵入。そして白昼堂々装甲車で銀行に突入し、人質をとって立てこもった……。", "2001-11-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MYTH", "THE　MYTH／神話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ジャッキー・チェンが初めて本格武侠映画に主演した意欲作。始皇帝の時代と現代を舞台に、伝説の宮殿を巡るミステリーと愛の物語が壮大なスケールで描かれる。太古の歴史ロマンあふれるアジア全土のロケーション映像も必見だ。考古学者のジャックは、毎夜、同じ夢ばかりを見ていた。それは、今から2200年前の秦の始皇帝時代。皇帝の信頼厚い将軍モンイーは、朝鮮から迎える美しい姫ユシュウの護衛を任される。やがて、ふたりは禁断の恋に落ち……。", "2006-03-18", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄の破門状", "地獄の破門状", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "日活スター勢ぞろいといった感のある任侠映画。大正末期の浅草を舞台に、４人の男がそれぞれの思惑を胸に秘めながら、協力して悪徳ボスを倒す。小林旭・浅丘ルリ子という、かつての名コンビが久々に本格的な共演をした作品で、二人の日活時代の共演はこれが最後。浅丘の芸者姿がひときわ美しい。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オペレッタ狸御殿", "オペレッタ狸御殿", "", "", "http://image.eiga.k-img.com/images/movie/1582/315x210.jpg?1495", 111, "1939年の「狸御殿」以来、何作も製作されてきた人間の姿をした狸たちが歌って踊るミュージカル映画＝狸御殿シリーズに、「ツィゴイネルワイゼン」「ピストルオペラ」の鈴木清順監督が挑戦。がらさ城の城主は、世継ぎである息子・雨千代が自分の美しさを凌ぐと知り霊峰・快羅須山中に捨てようとするが、雨千代はこの陰謀を逃れて、唐の国から来た美しい狸姫と出会って恋に落ちる。故・美空ひばりのデジタル出演も話題。", "2005-05-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沈黙の啓示 TRUE JUSTICE PART2", "沈黙の啓示 TRUE JUSTICE PART2", "", "", "http://ecx.images-amazon.com/images/I/51E5a9%2BrubL._SL210_.jpg", 90, "アクション俳優スティブン・セガールが特別捜査班「SIU」のリーダーに扮するクライム・アクション・シリーズ「TRUE JUSTICE」の第2弾。シアトルの日本人キャンプ「キャンプ・ハーモニー」で、フェンスに張りつけられたストリッパーの死体が発見される。死体の口には白いカーネーション、足元には赤い布と狐の像が置かれていた。SIUの女性捜査官ジュリエットとサラは、被害者が働いていたストリップで潜入捜査を開始するが……。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BIONDA", "ザ・ブロンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ある晩、車を運転していたトマソは若い金髪女性を車でひっかける。不運にも彼女はショックで記憶のすべてを失っていた。早いうちに厄介払いをしようと考えていたトマソだったが、次第に彼女に恋をしてしまう。しかし記憶の戻った彼女は、トマソの以前の離れ元の生活に戻ってしまった。わずかな手がかりからトマソが見つけだしたブロンドの彼女クリスティーヌ。しかし、彼が目にしたのは、ギャングのアルベルト、買収した刑事、手段を選ばない金融業者とともにひと旗あげ、一人で新しい生活を始めようとしていたクリスティーヌの姿だった。トマソが現れたことで、彼女の計画が危険にさらしてしまう……。普通の生活を営んでいた男と、記憶喪失の女、悪事をたくらむ男の３人の運命が絡み合い、予期せぬ終局を迎える。", "1993-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宵待草", "宵待草", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「青春の蹉跌」（1974）でコンビを組んだ長谷川和彦の脚本を映画化した神代辰巳監督の一般映画。昭和初期に生きた二人の無政府主義者の若者と、彼らに誘拐された右翼の黒幕の令嬢との奇妙な関係を、ある滑稽さをもって描く。令嬢役の高橋洋子の若々しい肉体と美貌が魅力的。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAY AMEN，SOMEBODY", "マザー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "“ゴスペルの父と母”という異名を持つT・A・ドーシーとW・M・フォード・スミス。本作はこの二人が、人生について語り、歌う様子を収めたもので、豊かなキャリアを持つ彼らのスピリチュアルな魅力が味わえる。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STONE PILLOW", "ストーン・ピロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ニューヨークの街角で暮らす年老いたホームレスのフローラは、ある日キャリーという女の子に出会う。フローラは彼女を家出娘と思い込み、世話をしてやることに……。ニューヨークのホームレス問題に取り組んだ作品。フローラを演じるのは人気TV番組『ルーシー・ショー』などでおなじみのL・ボール。この作品が彼女にとって遺作となった。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サラリーマン出世太閤記", "サラリーマン出世太閤記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "コネをつけたライバルとは違い、ただ黙々とアルバイトをしながらライバルと同じ会社の入社を目指していた男が、持ち前の器量の良さと親切な女性たちに支えられて出世していくさまを描いた明朗サラリーマンもの。小林桂樹が若さあふれる演技を披露。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マンゴと黒砂糖", "マンゴと黒砂糖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "第ニ次世界大戦下、激しい戦闘に見舞われたサイパン島。戦後、この地から日本に引き上げた南洋群島帰還者の人々は、戦争犠牲者の慰霊のため、いまも毎年同地を訪れている。本作はその慰霊祭を見つめたドキュメント。戦後60年経っても決して消えることのない戦争の爪痕を伝える１本だ。太平洋戦争時、激戦が繰り広げられたサイパン島。この地で昭和44年から行われている南洋群島帰還者による慰霊祭の模様を伝える。", "2005-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すっ飛び駕", "すっ飛び駕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "子母沢寛の新聞連載小説を伊藤大輔脚色、マキノ雅広監督、大河内伝次郎主演という強力トリオで映画化した娯楽時代劇。河内山宗俊が金子市之丞の難儀を救うことで知られる『天保六花撰』の一席で新味はないが、マキノ節が随所に冴えわたる。大河内伝次郎が貫禄十分に河内山を演じるラストの御用提灯の波は圧巻。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A NIGHT TO REMEMBER", "SOSタイタニック−忘れえぬ夜−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "1912年に起きた有名な海難事故“タイタニック号の遭難”を数百人の出演者でセミ・ドキュメント風に描いた大作。英国の豪華客船タイタニックは処女航海の途上、氷山にあたり沈没。生存者は約2000人の乗員のうち、705人にすぎなかった。", "1958-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dr.CALIGARI", "Dr.カリガリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "映画史に残る1920年のドイツ映画「カリガリ博士」をカリガリ博士の孫娘という設定で、妖しげな女医として復活させた映画。彼女は祖父の名誉のみならず祖父自身をよみがえらせようともくろみ、日々過激な秘密実験を繰り返す。異常性愛患者やエログロな登場人物とクリーチャー、原色多用の映像——70年前の名作をアブナい感覚で料理している。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("When a Stranger Calls", "ストレンジャー・コール", "", "", "http://image.eiga.k-img.com/images/movie/34022/315x210.jpg?1360", 87, "1979年に製作されたカルト・ホラー「夕暮れにベルが鳴る」を「トゥームレイダー」「コン・エアー」のサイモン・ウェスト監督がリメイク。ロサンゼルスの人里離れた豪邸にベビーシッターとしてやって来た女子高生ジル。その晩、留守を預かった彼女の元に不審な電話が掛かってくる。執拗に繰り返される電話に脅えた彼女は、発信元を調べようと逆探知を依頼するが……。主演は「姉のいた夏、いない夏」のカミーラ・ベル。", "2007-06-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRIDE OF FRANKENSTEIN", "フランケンシュタインの花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "「フランケンシュタイン」（1931）の続編。前作の有名なラスト・シーン、風車小屋に逃げたフランケンシュタインが果たして死んでいたのか、というところから始まる。生き残ったフランケンシュタインに、悪の科学者が花嫁を造って与えるというお話。DVDは「ユニバーサル・モンスター・コレクション【緑】」に収録。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crash", "クラッシュ", "", "", "http://image.eiga.k-img.com/images/movie/1042/315x210.jpg?1449", 112, "「ミリオンダラー・ベイビー」の製作・脚本でアカデミー賞にノミネートされたTV界のベテラン、ポール・ハギスの監督デビュー作。現代のロサンゼルスを舞台に、黒人刑事、ベテラン警官、新進政治家、TV演出家、雑貨店主人などさまざまな階層、さまざまな人種の人々の生活が交錯していく。先日発表されたアカデミー賞では作品賞、監督賞、助演男優賞（マット・ディロン）、オリジナル脚本賞、編集賞、音楽賞の6部門にノミネート。", "2006-02-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新諸国物語 笛吹童子　第一部 どくろの旗", "新諸国物語 笛吹童子　第一部 どくろの旗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "応仁の乱の後のこと。丹波国満月城々主・修理亮の二人の息子は、明国に留学し、兄の萩丸は武芸を、弟の菊丸は面作りを学ぶ。また、弟は笛吹童子と呼ばれる笛の名人であった。不吉な前兆に二人が帰国してみると、城は野武士の首領・玄蕃に乗っ取られ、父は自害していた……。家臣の娘を救う巨大な竜の登場が見もの。DVDは「新諸国物語　笛吹童子」に収録。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄昏流星群／星のレストラン", "黄昏流星群／星のレストラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "『人間交差点』『課長　島耕作』などで知られる漫画家・弘兼憲史の原作をもとにしたヒューマン・ドラマ。フランス料理店で働く青年・ヒロミが、一人の初老の男と出会う。男から料理の欠点を次々と指摘されたヒロミは、男がフランスで活躍した名シェフだったこと、料理の世界から去る決断をした悲しい過去を知る……。伝説の料理人の再起のドラマと、切ない恋物語を、鮮やかな料理の数々で彩る。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BELIEVERS", "サンタリア 魔界怨霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "６歳から10歳までの長男を生贄として悪魔に捧げている宗教集団。そんな集団に狙われた息子を守るため、怪しげな魔術に苦しみながらも、生死を懸けて戦う父親の姿を描くサスペンス。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／CHIHWASEON", "酔画仙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "これまでに発表した作品が100本を超える韓国の名匠、イム・グォンテク監督。「春香伝」で韓国の伝統民謡の世界を描いた彼が、激動する19世紀に生きた画伯、チャン・スンオプの生涯の映画化に挑んだ。このスンオプは朝鮮３大画家に数えられる天才。イム監督は、そんな彼の破天荒な生き様を丹念に描出。結果、至高の芸を求めた男の苦悩を浮かび上がらせた、見ごたえ十分の人間ドラマに仕上がった。また、主人公を演じる「オールド・ボーイ」のチェ・ミンシクの渾身の演技も痛烈な印象を残す。朝鮮時代末期。下級の身分ながら絵の才能を高く買われたスンオプは、イ通訳官の家に仕えることに。その後、家を出た彼は放蕩生活、放浪の旅を経験しながら自らの絵を追究し続ける。", "2004-12-18", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("行楽猿", "行楽猿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "夏休みの旅行に出かけた４人家族だが、旅先ではトラブルばかり。優柔不断な父は母に怒られっぱなしで、娘は体調を崩して寝込んでしまう。家族内のコミュニケーションが欠けていると悟った父は会話をとり戻そうとするが……。一見平凡な家族ドラマを、カットごとに色調や音響を変えるなど、奇妙な手法で描く怪作。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丹下左膳・百万両の壺", "丹下左膳・百万両の壺", "", "", "http://image.eiga.k-img.com/images/movie/1632/315x210.jpg?3677", 119, "日本映画史に残る名作とひとつと言われる山中貞雄監督の「丹下左膳餘話　百萬両の壷」を「冷静と情熱のあいだ」で日本アカデミー賞撮影賞を受賞した津田豊滋監督が監督・撮影・編集の3役を担当してリメイク。製作・脚本は津田監督と「王様の漢方」でも組んだ映画評論家でもある江戸木純。右目と右腕を失った剣の達人、丹下左膳は5歳の孤児ちょび安の面倒を見るはめに。ちょび安が持つ金魚鉢には隠し軍資金の秘密が隠されていた。", "2004-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("多羅尾伴内・七つの顔の男だぜ", "多羅尾伴内・七つの顔の男だぜ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "七つの顔を持つ男、多羅尾伴内が活躍する片岡千恵蔵主演による“多羅尾伴内”シリーズの最終作。富豪の令嬢・馬場きみ子が何者からによって誘拐され、捜査中の刑事が射殺される。名探偵・多羅尾伴内は、大沢警部とともに捜査に乗り出す。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IVORY HUNTERS", "アイボリー・ハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ケニアでアフリカ象を追っていた女性カメラマンが失踪。依頼者のロバートはケニアへ向かい、現地で動物学者のマリア、ジュモ警部と協力、必死の捜査を始める。やがて象牙が目的で虐殺されたと見られる象の死体が発見された。そこには、象牙密猟組織の影が……。I・ロッセリーニがマリア役で主演するサスペンス・アドベンチャー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("打倒〈ノックダウン〉", "打倒〈ノックダウン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "トニーこと赤木圭一郎主演による日活得意のボクシング映画。拳闘クラブの会長は、大学のボクシング部で有望な新人を発見する。最初拒絶するが、彼の兄が悪玉の陰謀で研究所をクビになり、研究資金を稼ぐためにプロになる。この作品は日活十八番のボクシング映画のルーティン通りに作られた平凡な作品だが、クライマックスのタイトルマッチにおける白熱したファイト・シーンは、本編の凡庸さを補なってあまりある迫力。トニーは筋肉質の引き締まった肉体をキビキビした動きで、日活スター中随一のボクサーぶりをみせた。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄金を抱いて翔べ", "黄金を抱いて翔べ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ベストセラー作家・高村薫が1990年に発表し、第3回日本推理サスペンス大賞を受賞したクライム小説を井筒和幸監督、妻夫木聡主演で映画化。過激派や犯罪者相手に調達屋をしてきた幸田は、大学時代からの友人・北川から銀行地下にある15億円の金塊強奪計画を持ちかけられる。幸田と北川は、銀行のシステムエンジニア・野田、自称留学生のスパイ・モモ、北川の弟・春樹、元エレベーター技師の爺ちゃんを仲間に加え、大胆不敵な作戦を決行する。共演は浅野忠信、桐谷健太、溝端淳平、チャンミン（東方神起）、西田敏行。", "2012-11-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Legend of Zorro", "レジェンド・オブ・ゾロ", "", "", "http://ecx.images-amazon.com/images/I/61XWImIP9tL._SL210_.jpg", 126, "伝説のヒーロー、怪傑ゾロの活躍を描く98年のヒット作「マスク・オブ・ゾロ」の続編を、同じアントニオ・バンデラス＆キャサリン・ゼタ＝ジョーンズ主演、マーティン・キャンベル監督で映画化。カリフォルニアがアメリカ合衆国の州になろうとしていた1850年。アレハンドロは怪傑ゾロとしての活動を休止し、妻エレナと息子ホアキンと共に平穏な生活を送っていたが、住民投票を妨害する陰謀があることを知り、再び怪傑ゾロとなる。脚本は「アイランド」「M:I-3」のアレックス・カーツマンとロベルト・オーチー。", "2006-01-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("2002　異霊霊異", "トランサー霊幻警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "悪霊退治専門の若手刑事の活躍を、現代風にアレンジしたワイヤーワークで魅せる香港発のホラー・アクション。香港映画界をけん引する「ジェネックス・コップ」のニコラス・ツェー、スティーブン・フォン、サム・リーの若手実力派３人が再結集。シリアスな人物描写も登場する脚本に、単なるアイドル映画で終わらせない香港映画界の底力が垣間見える逸品だ。多発する謎の怪事件。香港警察は特殊部隊“2002”に霊感の強い刑事ヤウを専属させる。ヤウは才能を活かし任務を遂行。しかし一方で、親しい相手を不幸にする運命に悩む。やがてヤウには新しい相棒フォンが抜擢され……。", "2001-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Black Sunday", "ブラック・サンデー", "", "", "http://ecx.images-amazon.com/images/I/51n57izoP5L._SL210_.jpg", 143, "　ベイルートの地下組織“黒い九月”は元アメリカ軍士官と結託し、マイアミで開催されるスーパーボールのスタジアムの観客8万人を一挙に殺害するというテロ計画を立てていた。その阻止に動き出すイスラエル特殊部隊のカバコフ少佐とFBI。彼らの息詰まる戦いを描いたサスペンス・アクション。「羊たちの沈黙」で知られるトマス・ハリスのベストセラーを映画化。日本では劇場公開が中止になったいわくつきの作品。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GAL", "かさぶた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "A・ジャリリ監督の長編第３作。“ドキュ・ドラマ”といわれる彼特有の作風に魅了される、みずみずしい子供たちの物語だ。反政府的なチラシを配っているとの理由で逮捕され、少年院送りにされた文盲の男の子ハメッド。そこで看守の不条理な指導に面食らいながらも、同世代の少年らと打ち解けていく彼の姿が深い感動を呼ぶ。出演者の多くは、実際に少年院に収容されている子供たち。主人公ハメッドが“僕はいつ自由になれるの？”とつぶやくラストが心に突き刺さる。", "1987-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NORTH DALLAS FORTY", "ノース・ダラス40", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "フィルはダラス市のプロフットボール・チームの落ちこぼれ選手。しかもコーチはコンピュータで選手の行動を管理し勝手なプレーを許さない。マシーンになりきれないフィルは……。若き日のN・ノルティが、その肉体派ぶりを発揮した熱血スポーツ映画。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Young Doctors In Love", "病院狂時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "外科医プラングのもとに優秀なインターンが７人集まり、１年間の研修が開始されたのだったが、病院は変人揃いで……。市立病院を舞台に、多彩なエピソードを盛り込んだドタバタ・コメディ。ゲイリー・マーシャル監督のデビュー作。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　IN−LAWS", "セイブ・ザ・ワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "花婿の父はCIAエージェント。彼の危険な任務に花嫁の父も巻き込まれる……というユニークな設定のアクション・コメディ。武器商人に立ち向かう、性格の異なる父親二人の迷コンビぶりが笑いを誘う。M・ダグラスとA・ブルックスの、息の合った共演も見もの。 CIAの秘密捜査官スティーブは、任務で世界を飛び回る多忙な男。ある日、息子の婚約者の父親ジェリーを、成り行きで任務に巻き込んでしまう。神経質で堅物のジェリーは無理やり、犯罪組織の壊滅に協力させられるハメに……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN CARNET DE BAL", "舞踏会の手帖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "若くして未亡人となったクリスチーネが社交界にデビューした時の手帖を見つけ、そこに書かれた踊りのパートナーを訪ねる旅に出る……。デュヴィヴィエが女性ロマンチシズムをうたい上げた一編。当時のフランス映画の名優をよりすぐった香り高い大作である。", "1937-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("同居蜜友", "ファイティングラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "香港の実力派、T・レオンと、歌手のS・チェンが初顔合わせしたロマンティック・コメディ。車の接触事故で知り合った牛モツ麺屋のトンザイと、気ばかり強いキャリアウーマンのデボラ。最初はいがみ合ってばかりの二人がふとしたことから一夜をともにし、次第に惹かれあっていくが、トンザイには芸能人の恋人がいて……。", "2001-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("X-Men", "X-メン", "", "", "http://image.eiga.k-img.com/images/movie/1752/315x210.jpg?1451", 104, "DNAの突然変異により特殊な能力を持つ“ミュータント”。彼らはその特異性を理解できない人間たちから疎外される存在だった。カナダの雪深い国道沿いのバー。家出少女ローグはドッグファイトで圧倒的な強さを見せる野性的な男、ローガンと出会う。彼が自分と同じく孤独なミュータントであることを知り、親近感を感じた彼女は無理矢理彼のトレーラーへ乗り込むが、突然二人は毛むくじゃらの巨漢に襲われる。", "2000-10-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE DIABLE AU CORPS", "肉体の悪魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ラディゲの有名な悲恋物語を、J・オーランシュとP・ボストの名脚本コンビがロマンティックに脚色した戦後のフランス映画を代表する傑作。第一次世界大戦末期のパリ。高校生のフランソワは年上の看護婦マルトと出会い、恋に落ちる。マルトには従軍中の婚約者がいたが、二人の情熱は抑えることができなかった。やがてマルトはフランソワの子供を身ごもってしまう……。当時、美男美女俳優として人気だったG・フィリップとM・プレールを主役に配し、叙情的演出には定評のあるC・オータン・ララが監督するというこれ以上はない顔合わせによって結実した、甘く切ないメロドラマの最高峰である。", "1947-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("祇園囃子", "祇園囃子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "溝口の得意とする花街を舞台とした作品で、前作「雨月物語」で脚本を担当した川口松太郎が原作を、依田義賢が脚本を担当している。祇園ではちょっと名の知れた勝気な芸者・美代春は、舞妓志願に来た少女・栄子の熱心な頼みに負け舞妓に仕込むことを決心。１年間栄子は芸事に励み、いよいよ舞妓として売り出す日がくる。栄子は現代っ子らしい振舞いが評判となり、楠田という男に目をつけられるが……。溝口に大抜擢された若尾文子、ベテラン・浪花千栄子、進藤英太郎の演技が光る珠玉の作品。とりわけ、祇園になじみの且那・楠田に迫られて、大騒ぎしたあげくに男の唇を噛み切るという大胆な行為に及ぶ、芸妓の卵・若尾文子の奔放ぶりに目を見張らされる。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SS エスエス", "SS エスエス", "", "", "http://image.eiga.k-img.com/images/movie/53252/315x210.jpg?4506", 100, "東本昌平の同名コミックを哀川翔主演で実写映画化したカーアクション。走り屋たちがタイムアタックを競う箱根レインボーラインで、幻のラリーマシン・三菱スタリオン4WDに乗る謎の男が最速タイムを叩き出した。自動車評論家の栗原は、その男がかつて共にWRCを目指した相棒・ダイブツであることを知る。過去への熱い想いを胸に秘めた2人は、やがて運命のバトルへと導かれ……。監督は「アンフェア　the movie」の小林義則。", "2008-01-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AVETIK", "アヴェティック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "アルメニアに生まれ、ベルリンに亡命したD・アスカリアン監督の自伝的要素を取り入れた作品。主人公アヴェティックの望郷と現実をモチーフに、少年時代のアルメニアと現在のベルリンを交錯させ、トルコによるアルメニア人の大量殺人、アルメニアの自然、その自然の中での原初的なエロチシズムなどが、美しく官能的な映像で綴られていく。", "1993-01-01", "74,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AMATEUR", "ザ・アマチュア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "『ニューズ・ウイーク』誌の記者としての経歴を持つR・リテルのスパイ小説の映画化。CIAのコンピュータ技師ヘラーの恋人がミュンヘンでテロリストに殺害される。ヘラーはCIAに犯人逮捕を求めるが、彼らは一向に動こうとしない。業を煮やしたヘラーは単身調査を開始。何者かに命をねらわれるなどの恐怖の中で、やがて彼は事件の背後に隠された政治的陰謀を突き止める……。", "1982-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("息子", "息子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "椎名誠原作『倉庫作業員』をもとに、山田洋次が久々に手掛けた社会派ドラマ。岩手の山村で一人暮らしをする老いた父親と、東京でアルバイト生活を送る息子の葛藤から和解までの物語を四季の情景の中で描いた名作である。生への執着が薄れていく父親役の三國連太郎をはじめ、倉庫作業員として働く聴覚障害者の美しい女性との出会いによって将来に希望を見いだし始める息子役の永瀬正敏と、その恋人役の和久井映見の好演が光る。ラストでの父親と息子の恋人との感動の対面シーンに至るまで、山田洋次は現代の日本が抱えている家庭問題をくどくどと描かず、サラリとしたタッチで綴り、新たなる飛躍を遂げている。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOUL　ASSASSIN", "ミッションブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "舞台演出やMTV監督として知られるローレンス・マルキン監督の長編デビュー作。オランダで多国籍投資銀行のセキュリティ・エージェントとして働く男がある日突然、目の前で恋人を殺され、その死の真相を探るうちに巨大な闇の事件へ足を踏み入れていく。全編に寂寥感が漂うサスペンス・アクション。劇中、場面のスピードがアップダウンしたり、画面のカラーが変わったり、同じ場面が何度も繰り返されたりとユニークな手法が多数使われている。S・ウールリッチがノースタントでアクション・シーンに挑戦、驚愕の真実を解き明かすヒットマンを熱演。2002年ゆうばり国際ファンタスティック映画祭出品作品。", "2001-01-01", "70,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Volver", "ボルベール　帰郷", "", "", "http://image.eiga.k-img.com/images/movie/1962/315x210.jpg?2696", 120, "「オール・アバウト・マイ・マザー」「トーク・トゥ・ハー」のペドロ・アルモドバル監督が、故郷ラ・マンチャのたくましい女たちの生きざまを郷愁と共に描き出したヒューマン・ドラマ。失業中の夫と15歳の娘を養うライムンダ。伯母の訃報を聞き故郷のラ・マンチャを訪れた彼女は、火事で死んだはずの母親の姿を見たという噂を耳にする。06年のカンヌ国際映画祭では、主演のペネロペ・クルスほか6人の女優たち全員が最優秀女優賞に輝いた。", "2007-06-30", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("永すぎた春", "永すぎた春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "大学生の郁雄と百子は、家庭の格式の違いを乗り越え婚約する。清い関係であった二人は、結婚までは満足だったものの、何かもの足りなかった。そんな時、郁雄はデザイナーの本城のぶ子と出会い、今まで抑圧されていた自分の性に目覚めるが……。若き男女のモラルと恋愛を描いた三島由紀夫の同名小説の映画化。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀幕ヘタリア Hetalia Axis Powers Paint it, White（白くぬれ！）", "銀幕ヘタリア Hetalia Axis Powers Paint it, White（白くぬれ！）", "", "", "http://image.eiga.k-img.com/images/movie/55062/315x210.jpg?1455", 82, "WEBコミックを発祥とする日丸屋秀和の「ヘタリア Axis Powers」劇場版。陽気なイタリア、生真面目なドイツ、物静かで控えめな日本の枢軸3人など、国を擬人化したキャラクターが、それぞれの風俗や気質を体現するアニメ。世界中でナゾの怪事件が発生し、人類は滅亡の危機に直面する。アメリカは国際会議を開いて一連の指揮を執ろうとするが、フランス、イギリス、ドイツ、日本、ロシア、イタリア、カナダの足並みがそろわず、それぞれがバラバラに動き出してしまう。", "2010-06-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生命", "生命〈いのち〉／希望の贈り物", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "山形国際ドキュメンタリー映画祭2003で優秀賞を受賞した本作は、台湾のドキュメンタリー界で圧倒的な支持を得るウー・イフォン監督が、'99年の台湾大地震で被災した人々を静かに見つめた感動作。身近な家族を突然失って悲しみに暮れる人々の再生を追い、生命の意味を力強く問いかける。'99年9月21日に台湾を襲った台湾大震災。ウー・イフォン監督はカメラを携えて被災地に赴き、被災者家族の悲しみを目の当たりにする。", "2005-01-29", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAPONE BEHIND BARS", "新アンタッチャブル カポネの逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "獄中にあっても暗黒街にその強大な影響力を行使するアル・カポネに、もう一人の“アンタッチャブル”、マイケル・ロークが立ち向かう。ギャングとアメリカ政府のその後の仁義なき戦いを描く。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜逃げ屋本舗２", "夜逃げ屋本舗２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アノ手コノ手の法的テクニックを駆使して、借金で首の回らなくなった善良な市民を救う“夜逃げ屋本舗”たちの活躍を描いた痛快活劇の第２作。カード・ローン地獄に陥った人々のため、今は自己破産手続きの代行業に精を出している“夜逃げ屋”の面々。悪徳金融会社の冷酷非情な取り立てに、怒りを爆発させた彼らの闘いぶりをコミカルに活写する。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Murder on the Orient Express", "オリエント急行殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "　1935年、私立探偵ポアロは大陸横断列車オリエント急行に乗り、ロンドンを目指していた。その車内でアメリカ人の億万長者が殺害されるという事件が起こる。ポアロは一等車両の乗客ひとりひとりを調べることに。偶然乗り合わせた彼らは国籍も身分も異なっていたが、やがて意外な事実が明るみになる……。アガサ・クリスティの同名推理小説をオールスター・キャストで映画化。人々の愛憎、そして名探偵の活躍を描くゴージャスなミステリー。", "1974-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WYATT EARP", "ワイアット・アープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 191, "伝説の保安官ワイアット・アープの人生を知られざる側面から描いた大作ウエスタン。厳格な父に育てられた子供時代から、恋、結婚、妻子の死、酒びたりの日々、そして正義に目覚めた保安官の誕生……。もちろん有名なOK牧場での決闘や盟友ドク・ホリデイとの友情も描かれる。ドク役のD・クエイドの好演が光る。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alien: Resurrection", "エイリアン4", "", "", "http://ecx.images-amazon.com/images/I/41gJd8asNWL._SL210_.jpg", 107, "名作SFシリーズの第4作。前作「エイリアン3」から200年後を舞台に、リプリーとエイリアンの最後の戦いが描かれる。惑星フィオリーナで溶鉱炉に消えたリプリーだったが、エイリアンの軍事利用をたくらむペレズ将軍率いる一派が、残されていたDNAからクローンを生み出し、リプリーは復活する。リプリーの体内に宿っていたエイリアンをもとに宇宙船オリガ号の中で養殖が開始されるが、成長したエイリアンが脱走。オリガ号は緊急事態のため地球へ向けて動き出してしまう。リプリーはエイリアンを地球に降ろすまいと戦うが……。「デリカテッセン」「ロスト・チルドレン」など独創的なビジュアルセンスで知られるフランス人監督ジャン＝ピエール・ジュネがメガホンをとった。タイトルバックやエンディングのカットなどが異なる数分長い完全版も03年に発表されているが、ジュネ自身は「劇場公開版がディレクターズ・カット」と明言している。", "1998-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KNOCKIN’ON　HEAVEN’S　DOOR", "ノッキン・オン・ヘブンズ・ドア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ドイツで350万人を動員する大ヒットを記録した娯楽作。脳腫痕に冒された青年マーチンと末期骨髄腫のルディ。末期患者の病室で同室となった彼らは、死ぬ前に海を見ようと病院を抜け出し、車を奪って逃走。ところが、この車にギャングの大金が積まれていたことから、犯罪組織や警察が追跡を開始。一方、大金を見つけたマーチンとルディは豪遊を繰り広げつつ、危機を乗り越えながら目的地を目指す……。カーチェイスや銃撃戦などのアクションに、ユーモアとロマンをプラス。難病モノながら湿っぽさを感じさせないカラッとした作りが気持ちよい。エンディングでは、タイトルにもなったボブ・ディランの名曲がドイツのバンド、ゼーリッヒによるカバー・バージョンでフィーチャーされている。", "1997-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("3615 CODE PE｀RE NOE¨L", "ウォンテッド Mr.クリスマス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "子供たちに夢を与えるサンタクロースを殺人鬼として描くホラー・ムービー。10歳の少年が、パソコン通信でサンタクロースとアクセスし、サンタを捕える計画を企てる。イヴの夜、テーブルの下に隠れてサンタが来るのを待つ彼の前に殺人鬼が現れ、少年と恐怖のバトル・チェイスを繰り広げる。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Yves Saint Laurent L' amour Fou", "イヴ・サンローラン", "", "", "http://image.eiga.k-img.com/images/movie/55742/315x210.jpg?1669", 103, "フランスが生んだ世界的ファッションデザイナー、イブ・サン＝ローランの姿に迫ったドキュメンタリー。ブランド「イヴ・サンローラン」が成功した背景と、名誉や栄光の裏側にあったサン＝ローランの苦悩を、公私にわたってパートナーだったピエール・ベルジェが語る。1998年サッカーフランスW杯のセレモニーで「イヴ・サンローラン」の衣装を身にまとった300人のモデルが登場する映像や、10万点以上の貴重なアーカイブから厳選された写真・映像、サン＝ローランの肉声などを多数初公開。", "2011-04-23", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Up in the Air", "マイレージ、マイライフ", "", "", "http://image.eiga.k-img.com/images/movie/54872/315x210.jpg?1597", 109, "「JUNO／ジュノ」でアカデミー賞を受賞したジェイソン・ライトマン監督が、ジョージ・クルーニーを主演に描くヒューマンドラマ。リストラ請負人のライアンは、飛行機で全米を飛び回り、リストラ対象者に次々クビを言い渡す日々。出張の副産物・航空会社のマイレージも貯まる一方で、彼はいつしか1000万マイル獲得を目標に定め今日も前向きに機上の人となる。ところがある日、コスト削減のため出張は一切禁止という命令が下り、彼の野望に暗雲がたれこめる。", "2010-03-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎頑張れ！", "男はつらいよ・寅次郎頑張れ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "電気工の青年と仲よくなった寅さん。青年が思いを寄せる食堂のウエイトレスとの仲をとりもつ指南役をかってでるが、失恋専門の寅さんのこと、万事ウラ目に出て青年は失恋。寅は彼女の美しい姉に一目惚れ。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あばれ行燈", "あばれ行燈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "渡世の義理で顔見知りの美濃の藤太郎を斬った沼津の秋太郎は、今はの頼みを聞き入れて藤太郎の母に金を届ける。ところが、ボケた母に息子と間違われ、そして許婚と相思相愛の仲になってしまう……。鶴田浩二が例によって義理と人情の板ばさみになる股旅ものの人情噺。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOWNTOWN８１", "DOWNTOWN81", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "27歳で世を去ったN.Y.アート界の伝説J・M・バスキアの唯一の出演作。1981年に撮影されたものの、資金難で製作中止となり行方不明となっていたフィルムを、当時のスタッフが1998年に発見し、2000年に完成させた。無名のアーティスト、ジャンがN.Y.の街をさすらい、美人のモデルやミュージシャンらと交流を重ねるという、スケッチ風のシンプルな物語。1980年代N.Y.の熱気あふれる音楽シーンの貴重な記録としても価値がある。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SU¨RU¨", "群れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "カンヌ映画祭グランプリ作品「路〈みち〉」で知られるトルコの代表的監督ギュネイが“反体制的行動”によって投獄されていた時、獄中から指示を送って撮り上げた入魂の傑作。東トルコの広大な砂漠地帯。そこで牧畜を営むハモ家の長男シバンは、反目し合うハリラン家の娘と結婚。彼女が病弱で子供も産めない体とあって、彼ら若夫婦に対する家族の態度は冷たい。ある日、彼ら親子は羊370頭を売りさばくため、汽車で首都アンカラに向かう。シバンは街の医者に連れて行くため妻を同行した。途中、貨車に残留していた毒薬DDTのため幾頭もの羊が死に、しかも山賊の襲撃に遭い、また何頭かは盗まれる。やっと街に着いた時、父親は羊の数が足りないとシバンに約束した報酬を支払わないうえ、最愛の妻は医者の所に運んで間もなく息絶えた。トルコ社会に現存する封建的家長制度を痛烈に批判。権力によって自由を束縛され続けるギュネイの、怒りと哀しみに満ちた心の叫びなのだ。", "1978-01-01", "32", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JESSE JAMES", "地獄への道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "西部にその名をとどろかせた実在のアウトロー、ジェシー・ジェームズの半生を描いた傑作ウエスタン。悪徳鉄道会社に土地を奪われ、母も殺されたジェシーとフランクのジェームズ兄弟は、列車強盗になってその復讐をすることを誓う。強盗団を組織して暴れ回る彼らの名前は、やがて国中に轟いていく。モノクロオリジナル版にカラー着色したビデオが以前リリースされていた。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Deep Impact", "ディープ・インパクト", "", "", "http://ecx.images-amazon.com/images/I/51UuHvL24lL._SL210_.jpg", 121, "TVシリーズ『ER／緊急救命室』の演出家として注目され、「ピースメーカー」で劇場デビューを果たしたM・レダーの監督第２作。彗星との衝突の危機に直面した地球の混乱ぶりを、壮大なスケールで描くパニック大作だ。巨大彗星が地球に接近、合衆国大統領は一年後に衝突すると発表する。米国とロシアは宇宙船で彗星を破壊し、軌道回避を試みるがあえなく失敗。そこで、政府は地下巨大都市“アーク”の建設に着手する。避難できるのは100万人まで、しかも条件は50歳以下と限定。最後の望みを絶たれた人々がパニックに陥る中、地球滅亡の日は刻一刻と迫っていた……。マンハッタンを飲み込む大津波などの迫力のSFXシーンや、専門家たちの協力で地球の大異変に迫ったリアルな描写によって、とてつもない緊迫感が漂う。地下都市の収容人物の選出によって引き裂かれた家族や恋人たちの苦悩、世界の未来を託された宇宙飛行士たちの姿など、死に直面した人々のドラマが感動を呼ぶ。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVIL　ED", "エヴィル・エド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "映画会社に勤める真面目な編集マン、エドが極悪ホラー“美女切断”シリーズの編集を頼まれたことから、奇妙な幻覚を見始め……。狂気に陥った彼が殺人や暴力事件を繰り返し、SWATと対決するまでを描くホラー映画。日常の中に忍び寄る恐怖を静かに捉えた前半から、異常性あふれる後半までをダークかつハイテンションな映像で見せていく。「キャスパー」のG・ランドストロムが特殊メイクやクリーチャー・デザインも担当。", "1996-01-01", "1,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("万家灯火／MYRIADS　OF　LIGHTS", "家々の灯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "旧体制下の上海を舞台に、そこに生きる庶民の苦しい生活を描いた、イタリアン・ネオレアリスモにも通じる傑作。胡智清は、妻と子供を持ち、貿易会社の中堅社員として仕事に打ち込む生活を送っている。やがて母が次男夫婦を連れて智清の家にやってくる。そのころ智清は会社の経営に対して苦情を言い、会社をクビになってしまう。一家は一文無しとなり……。", "1948-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fabulous Baker Boys", "恋のゆくえ ファビュラス・ベイカー・ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ホテルの冴えないショーでその日暮らしを続けるピアノ・デュオの兄弟、ジャックとフランクは女性歌手を加えようと思い立つ。オーディションで発掘したスージーを加えたトリオは大成功をおさめた。しかしジャックとスージーが恋に落ち、何かが狂い始める……。ブリッジス兄弟が初共演した話題作。スージーに扮するM・ファイファーが自ら歌うナンバーも魅力的だ。どこかハードボイルド的なムードが、大人のラブ・ストーリーをいっそう魅力的にしている。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPY　KIDS　３−D：GAME　OVER", "スパイキッズ３-D：ゲームオーバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "子供スパイの活躍を描いた人気シリーズの完結編。姉カルメンを救うためにビデオゲームの世界に飛び込んだスパイキッズ、ジュニの最後の冒険が描かれる。特製メガネを装着して楽しむ3D仕様の映像は、メカやロボットが目の前に飛び出し迫力満点だ。悪役S・スタローンの怪演に加え、シリーズおなじみの豪華ゲスト出演陣の顔ぶれにも注目だ。諜報機関OSSを辞職したスパイキッズ、ジュニの元に姉カルメン危機の知らせが届く。人気ビデオゲームの世界に捕らわれの身となった彼女を救うため、ジュニはゲームをプレイする。やがてゲームの中の彼に、悪党トイメーカーの罠が迫る。", "2003-10-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピラニア", "ピラニア", "", "", "http://ecx.images-amazon.com/images/I/51%2BZnaTwBiL._SL210_.jpg", 76, "青春と性をテーマに扱う「青春Hシリーズ」第5作。「行旅死亡人」の井土紀州監督がメガホンをとる。吃音症を気にしている奈津は、思いを寄せる弁当屋の青年・曽根にうまく告白できずにいた。そんな奈津の前に、クビにした上司を食わせるためにピラニアを飼っている失業中の男・晴男が現れる。晴男は純粋な奈津を思い、代わりに曽根に気持ちを伝えてやるが……。", "2010-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Miss Boys！　友情のゆくえ編", "Miss Boys！　友情のゆくえ編", "", "", "http://image.eiga.k-img.com/images/movie/57532/315x210.jpg?3664", 78, "女装をテーマに男子高校生たちの青春を2部構成で描いた「Miss Boys！」の第2部。女装甲子園から1年が過ぎ、2年生に進級した真実はもといたバスケ部のエースとして活躍。女装応援団で苦楽をともにした金子は、副団長として応援団を支えていた。そんなある日、芸能事務所から女装オーディションに出演して芸能界デビューを目指すよう提案された金子は、真実を誘ってオーディションを受けることに。しかし、オーディション当日がバスケ部の大事な試合とバッティングしてしまい……。主演は5人組バンド「ココア男。」でも活躍する俳優の鎌苅健太と鈴木勝吾。", "2012-01-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BORROWER", "ボディ・チェンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "人間の首をもぎ取っては、乗り移っていくエイリアンと、女刑事ダイアナとの戦いを描くSFホラー。「ヒドゥン」「チャイルド・プレイ」で特殊効果を演出した、K・イェーガーが、この作品のSFXも担当。「エイリアン」「遊星からの物体X」を思わせる“寄生生物もの”だが、新味は次々と転がる人間の生首!?", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Water Horse: Legend of the Deep", "ウォーター・ホース", "", "", "http://image.eiga.k-img.com/images/movie/53152/315x210.jpg?1981", 112, "第2次大戦時のスコットランド。出征中の父の帰りを待つ少年アンガスは、ネス湖で青く光る不思議な卵を見つけ、そこから産まれた不思議な生物をクルーソーと名付けて、母親の目を盗んで育てるが……。スコットランド北部のネス湖で、数々の目撃証言や写真、映像が発表され“ネッシー”として有名な謎の生物と、スコットランドに伝わる伝説の海獣“ウォーター・ホース”とを題材に、少年の成長と友情を描いた心温まるドラマ。", "2008-02-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("麻將", "カップルズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "急激な経済成長を遂げた台湾の都市・台北で、日々利潤追求ゲームに興じる4人の少年たち。巧妙な手口で大人も女も操り、金も自由も手に入れようとする彼らの前に、ある日突然フランス人の少女マルトが舞い込んで来る。彼女の出現によって、深い絆で結ばれているはずだった4人の関係は揺らぎ始めるが……。現代に生きる若者たちが内包する不安や孤独、絶望感、そして焦燥感を、斬新なタッチで描いた傑作。", "1996-11-30", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blindsight", "ブラインドサイト／小さな登山者たち", "", "", "http://image.eiga.k-img.com/images/movie/34082/315x210.jpg?1415", 104, "チベットに住む盲目の子供たちがエベレスト登頂に挑む姿を追ったドキュメンタリー。多くの盲人が差別的な扱いを受けてきたチベットに初めて盲学校を設立した盲目のドイツ人教育者サブリエ・テンバーケンとその学校に通う6人の生徒たちが、盲人として初めてエベレスト登頂に成功したアメリカ人登山家エリック・バイエンマイヤーと共に、エベレストの北側に位置する標高7000メートルのラクパリを目指し奮闘する。", "2007-07-21", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バウムクーヘン", "バウムクーヘン", "", "", "http://image.eiga.k-img.com/images/movie/34212/315x210.jpg?1571", 120, "「スリーピングフラワー」の柿本ケンサク監督によるファンタジック・ドラマ。幸せを探す川野辺家の3兄弟とその恋人たちの物語を中心に、3兄弟の噂話で盛り上がるバーを舞台にした物語、そのバーでの人間模様を描いた小説を読んでいる少女ユミの物語、ユミの物語を執筆する小説家マサトシの物語といった4つの世界が展開していく。キャストには「ナイン・ソウルズ」のマメ山田、「リアリズムの宿」の山本浩司ら個性派俳優が集結。", "2007-09-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILD SAFARI", "ワイルドサファリ３D", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "野生動物の宝庫、南アフリカの草原をオープンカーで探検する3Dドキュメンタリー。雄大な自然の中に棲息する多種の生物の中でも、危険で希少な5種類の動物ゾウ、ヒョウ、ライオン、サイ、ケープバッファロー。カメラは“ビッグ・ファイブ”と呼ばれる彼らに接近。その神秘の世界を迫力映像で映し出す。", "2005-01-01", "37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Exploding Girl", "エクスプローディング・ガール", "", "", "http://image.eiga.k-img.com/images/movie/55042/315x210.jpg?4706", 79, "「ソルト」のブラッドリー・ラスト・グレイ監督・脚本による青春映画。遠く離れて暮らす彼氏を想いながら幼なじみの青年と共に休暇を過ごすことになった女子大生アイビーの揺れ動く心を繊細なタッチで綴る。巨匠エリア・カザンの孫娘としても知られる「レボリューショナリー・ロード／燃え尽きるまで」のゾーイ・カザンがヒロインを演じる。共演に「シルク」のマーク・レンドール。ベルリン国際映画祭フォーラム部門上映作品。", "2009-11-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シブがき隊　ボーイズ＆ガールズ", "シブがき隊　ボーイズ＆ガールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "東京の全寮制を飛び出した三人の若者のリゾートでのアバンチュールを描いた青春映画。森田監督がシブがき隊の映画デビュー作を瑞々しい感性で演出した。", "1982-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Underworld: Rise of the Lycans", "アンダーワールド：ビギンズ", "", "", "http://image.eiga.k-img.com/images/movie/54002/315x210.jpg?4293", 90, "吸血鬼（バンパイア）族と狼男（ライカン）族の戦いを描いた「アンダーワールド」シリーズの第3作で、両部族の戦いの起源を描く。家族を狼男族に殺された復讐のため、女処刑人となった吸血鬼セリーンの誕生の裏には、セリーンに生き写しの女戦士ソーニャの存在があった……。主演は「ハイウェイマン」「ナンバー23」のローナ・ミトラ。前作のクリーチャー・デザインを務めたSFX界の鬼才パトリック・タトポロスがメガホンを握る。", "2009-03-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Super Mario Bros.", "スーパーマリオ　魔界帝国の女神", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "TVゲーム『スーパーマリオ・ブラザース』の実写版映画化。隕石の落下で異次元に閉じ込められた恐竜の一族がマンハッタンに通じる道を開き、現代世界を乗っ取ろうとしていた。異次元とマンハッタンを融合することのできるクリスタルを持った女学生デイジーは、魔王クッパにさらわれる。配管工のルイージとその兄マリオは彼女を救うため異次元に飛び込んで行く……。CGによるSFXを駆使して描き出された異次元世界はゲーム以上にエキサイティング。マリオそっくりのB・ホスキンスや、クッパ役のD・ホッパーの怪演も見どころだ。ビデオ発売時タイトルは「スーパーマリオ」。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WESTERN UNION", "西部魂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "西部の未開地で電柱建設に従事する男たちと地元の盗賊団との対決を描いた、名匠F・ラングによる西部劇。電信会社技師長のクレイトンは、工事に着手するため地元で人夫を募集するが、その中にショウという若者がいた。やがて彼らの牛が盗まれる。それはかつてショウもメンバーだった盗賊団の仕業だと分かるが……。リバイバル上映の際にはカラー版が公開された。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カーネーション ROCK LOVE", "カーネーション ROCK LOVE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "1983年の結成以来、人生の哀楽をつづった歌詞や圧倒的な演奏力で多くの音楽ファンを熱中させてきたロックバンド「カーネーション」を追ったドキュメンタリー。50代を目前に葛藤（かっとう）しながらも走り続けるメンバーたちの心象風景を、臨場感たっぷりのライブ映像と共に描きだす。監督を務めるのは「東京スカパラダイスオーケストラ」を題材にしたドキュメンタリー「SMILE 人が人を愛する旅」で注目を集めた牧野耕一。", "2007-11-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOG×POLICE 純白の絆", "DOG×POLICE 純白の絆", "", "", "http://image.eiga.k-img.com/images/movie/56062/315x210.jpg?1324", 104, "「海猿」の原作者・小森陽一が原案、市原隼人の主演で若き警察官と警備犬のきずなを描くオリジナル作品。人一倍正義感が強く刑事を目指していた警察官の勇作は、警備犬を育てる警視庁警備部警備二課装備第四係へ異動となる。希望と違う職場に戸惑いつつも、ハンディキャップを抱える犬・シロと出会った勇作は、ハンドラー（犬の訓練士）としてシロとともに成長していく。そんな中、警視庁を揺るがす連続爆破事件が発生し……。", "2011-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("cherry pie チェリーパイ", "cherry pie チェリーパイ", "", "", "http://image.eiga.k-img.com/images/movie/33682/315x210.jpg?2046", 80, "映像（Cinema）と音楽（Music）のコラボレーションを目指す「CineMusica（シネムジカ）」シリーズ第1弾。「ワイルドスピードX3／TOKYO DRIFT」でハリウッドデビューを果たした北川景子の映画初主演作。恋や仕事に悩む新人パティシエと友人たちの甘く切ない恋模様を描く。新人パティシエのキヨハラは、想いを寄せる先輩パティシエ辻内の死と向かい合うことが出来ずにいる。そのため、彼女は辻内が教えてくれた人気メニュー、チェリーパイを上手く作れなくなってしまった。そんな彼女の親友である八千代や文もそれぞれの恋に悩んでおり……。", "2006-10-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Deathtrap", "デストラップ・死の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "　スランプが続く劇作家のシドニー。そんな彼の教え子クリフが書いたミステリー戯曲は素晴らしい出来ばえだった。その才能に嫉妬したシドニーはクリフをおびき出して殺害し、作品を我が物にしようとたくらむ。妻マイラの制止も聞かず、シドニーは計画を実行。しかしこの後、事態は二転三転していく！　ベストセラー作家アイラ・レビンが書き下ろし、ブロードウェイで大ヒットを記録した舞台劇を映画化。意表を突く展開とユーモアが魅力のサスペンス・ミステリー。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Secondhand Lions", "ウォルター少年と、夏の休日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "夏休みを田舎で過ごす少年と、二人の老人の交流を温かい視点で綴ったヒューマン・ドラマ。無愛想でガンコな老人コンビが語る、戦時の思い出。それは少年の想像力を通して、壮大な冒険劇となる——そんな昔話と現在のドラマを織り交ぜつつ、人間の成長を描き出す。H・J・オスメントの好演はもちろん、M・ケインとR・デュヴァルの名優二人にも注目。母子家庭で育った少年ウォルターは、ひょんなことから夏休みの間、無愛想なおじいさんたちと過ごすことに。初めは馴染めないでいたが、ある日、トランクの奥から古い女性の写真を発見し、彼らの過去について興味を持ち始める。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("СКАЗАНИЕ О ЗЕМЛЕ СИБИРСКОЙ／A TAIL OF SIBERIA", "シベリヤ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "シベリアの雄大な自然をバックにした、旧ソ連製メロドラマの秀作。第二次大戦で左手を負傷し、ピアニストの道を閉ざされたアンドレイは、ソプラノ歌手として成功している恋人ナターシャのためを思い、シベリアへと去る。そこで耳にした民謡の響きは、沈んでいた彼の心に生きる情熱を取り戻させていく。そんなある日、ナターシャを乗せた飛行機が偶然にも、シベリアに不時着し……。DVDは劇場初公開時に検閲でカットされたシーンを含むノーカット版。", "1947-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIDAY THE 13TH", "13日の金曜日", "", "", "http://ecx.images-amazon.com/images/I/51MIVURq4yL._SL210_.jpg", 91, "スプラッタ映画の元祖“13金”シリーズの記念すべき第１作。1958年にニュージャージー州ブレアーズタウンで実際に起きた惨劇をもとに映画化された。舞台はクリスタル・レイクのキャンプ場。昔、若い男女が惨殺されて以来、呪われた場所として忌み嫌われているこの土地で、10数年ぶりに夏のキャンプが再開される。だが準備のために集まって来た若者たちは、その日、13日の金曜日に次々と何者かに殺されていく。無感動に人間が殺りくされていく有り様は、殺される者に対する憐れみや哀しみといったものが一切なく、一種のショー的な要素すら感じられる。本編とシリーズ第４作のメイクアップは、今やスプラッタ効果の第一人者、トム・サヴィーニが担当し、見事な視覚的効果を出している。謎の殺人鬼、“マッド・キラー”ジェイソンの誕生を告げ、以来数多くの続編を作り出していく基礎となり、その後のホラー映画に大きな影響を与えた。", "1980-08-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VOYAGER（HOMO FABER）", "ボイジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "V・シュレンドルフ監督が、「ブリキの太鼓」以来12年ぶりにドイツ語のテクストに基づき描いた一編。世界を飛び回るエンジニアのウォルター・フェイバー。ベネズエラからニューヨークに向かう飛行機で、旧友の兄に出会ったことから、彼の人生は狂い始めた。男に誘われるまま、友がいるメキシコへ飛んだ彼は、友の自殺に出くわす。ニューヨークに帰ったあと、さらに旅を続けると、今度は船の上で女子学生と出会い、そして恋に落ちるのだが、旅の果てでは恐ろしい事実が二人を待ち受けていた……。ベネズエラからメキシコ、ニューヨーク、そしてフランス、アテネと、ヨーロッパ文明の始源へとたどる二人の旅。それはあたかもギリシャの古典劇へと映画を運ぼうとしているかのようでもある。原作となるマックス・フリッシュの『アテネに死す』を読んで30年来、映画化を温めていたというだけあって、充実した仕上がりを見せている。ビデオ発売時タイトルは「ボイジャー　運命の航海者」。", "1991-01-01", "1,2,7,27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢と残酷のエクリチュールシュヴァンクマイエルの不思議な世界", "夢と残酷のエクリチュールシュヴァンクマイエルの不思議な世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「アリス」などで知られるチェコ・アニメの鬼才の短編を集めた作品。(1)「自然の歴史（博物誌)」（1967・９分）(2)「部屋」（1968・13分）(3)「地下室の怪」（1983・14分）(4)「陥し穴と振子」（1983・15分）(5)「男のゲーム」（1988・14分）(6)「闇・光・闇」（1989・７分）(7)「対話の可能性」（1982・11分）を収録。クレイ（粘土）アニメを中心に、様々な技法が駆使されており、“夢”をそのままフィルムに移したような(2)(3)(5)や、徹底したシニカルさを突き抜けてコミカルに転じた(6)(7)など最近の代表作を網羅している。特にベルリン、アヌシー、メルボルンなど各国の映画祭で絶賛された(7)は、20世紀末の病理をえぐる同時代性を感じさせる。ビデオ、DVDタイトルは「シュヴァンクマイエルの不思議な世界」。", "1967-01-01", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KAZOKU　CINEMA", "家族シネマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "芥川賞を受賞した柳美里の同名小説を「301・302」のパク・チョルスが映画化。バラバラになった５人家族が再会して巻き起こる騒動を、日韓のスタッフ、キャストで描く。クールなOL、素美が妹の提案で家族とともに映画に出演することになる。ところが、競馬狂の父、若い男と駆け落ちした母、AV女優の妹、そして自閉症気味の弟とは20年ぶりの再会。台本通りに一家団らんを演じてみるものの、両親が過去のことで口論するなど撮影は大混乱となり……。家族が映画の中で演技しながら互いを見つめ直していくという構成がユニーク。家族の実態をブラック・ユーモアで包んだ異色作だ。", "1998-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONCEIVING ADA", "クローン・オブ・エイダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "19世紀と現代という時代を超えて接触する二人の女性を、ビデオ・アートの分野で活躍している女性監督リン・ハーシュマン・リーソンが神秘的な映像美で映し出すSF映画。人類初のコンピュータ・プログラマーのエイダの波瀾万丈で奔放に生きた半生を映し出すとともに、現代女性が抱える様々な悩みを描き出している。記憶遺伝子学を研究するエミーはとある装置により、19世紀に生きるコンピュータ発案者であるエイダとの交信に成功。研究と家庭の両立に悩み、アヘンに走るエイダに、エミーはクローン技術でその存在を後世に残すよう提案する。", "1997-01-01", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Taking Woodstock", "ウッドストックがやってくる", "", "", "http://image.eiga.k-img.com/images/movie/54882/315x210.jpg?1946", 121, "「ブロークバック・マウンテン」のアン・リー監督が、歴史的な音楽の祭典「ウッドストック・フェスティバル」開催までの波乱に満ちた裏舞台を描く青春映画。原作はエリオット・ダイバーによるノンフィクション。人気コメディアンのディミトリ・マーティンが主演を務め、イメルダ・スタウントン、エミール・ハーシュ、リーブ・シュレイバーなど豪華キャストが脇を固める。うだつの上がらない青年エリオットは、別の町で開催予定だった「ウッドストック」が住民の反対によりキャンセルされたことを知り、町おこしのために地元への誘致を働きかける。", "2011-01-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A TALE OF TWO CITIES", "嵐の三色旗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "才能がありながらロンドンで酒浸りの生活を送っていた弁護士カートンは、フランスから亡命してきてスパイ容疑をかけられた貴族ダーネイの弁護のため腕を奮う。しかし、ダーネイの恋人、ルシーに心を奪われ、二人は恋仇となって……。革命直前のパリとロンドンを舞台に展開される、文豪ディケンズの名作を映画化したもの。ビデオ発売時タイトルは「ニ都物語」。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUNS＆TALKS", "ガン＆トークス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "ウォンビンら韓国の新世代スターが共演したユーモラスなアクション・ストーリー。情にもろく恋もする若き殺し屋たちの波乱に満ちた日常と、敏腕検事との駆け引きを軽快に描き出す。サンヨンをリーダーとする４人組は、クールでちょっぴりマヌケな殺し屋たち。次々と奇妙な仕事をこなす彼らは、オペラ上演中の劇場で暗殺を遂行するという危険な依頼を受ける。しかし敏腕検事チョの警備網が敷かれ……。", "2001-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忘れられた子等", "忘れられた子等", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "前作「手をつなぐ子等」で一人の特異児童を描いて好評だった稲垣浩が、再び同じ田村一二の原作を映画化。今回は特殊学級の子供たちを描いている。精神薄弱児を集めたクラスに新任の青年教師がやって来る。最初は手のつけようがないように見えた子供たちだったが、忍耐強く接していくうちに主人公にも彼らを理解する光が見え始め、次第に両者はうちとけていく。青年が故郷の琵琶湖に生徒たちを連れて行く場面や学芸会のために歌の練習をする場面などは、教師と生徒の美しい絆を感じさせ涙を誘う。子供たちの生き生きとした姿や新任教師を温かく見守る笠智衆の校長も印象的。心洗われるような感動作だ。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("処刑遊戯", "処刑遊戯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「最も危険な遊戯」「殺人遊戯」に続く、松田優作主演“遊戯”シリーズ第３作。錯綜した殺しの駆け引きを、一匹狼の殺し屋を主人公にハード・タッチで描く。冒頭やラストの銃撃戦の、ドライブ感たっぷりの長回しが見事。のちに売れっ子となる脚本の丸山昇一がこれでデビュー。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続大番 風雲篇", "続大番 風雲篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "前作で大もうけした金も株の大変動で一文なしとなり、丑之助は故郷に帰る。故郷で平和な日々を過ごしていると、兜町に帰ってこいという連絡がくる。そして好景気に乗って株で大もうけするのだが、折からの日華事変で大暴落し再びスッテンテンになってしまう。加東のギューちゃんも２作目とあってのびのびと演じきっている。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モロヘイヤWAR", "モロヘイヤWAR", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "2002年のゆうばり国際ファンタスティック映画祭で審査員特別賞を受賞した「ナッツ」の新人監督・蔭山周の初長編作品。仲間からいつもバカにされているダメ軍人が、母親と瓜二つの売春婦に恋をしたり、戦場で自分と全く同じ顔をした男に出会う。クローン技術の弊害が生んだ戦争の悲劇を、過激なバイオレンスと性描写を用いて表現したカルトな反戦映画。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRIME OF CRIMES A.K.A.PLAY GROUND", "クライム・チェイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "犯罪都市ロサンゼルスを舞台に、連続少女誘拐犯とすご腕刑事チームとの対決を描く刑事アクション。「バード・オン・ワイヤー」には悪役で出演、刑事ドラマを得意とする名優D・キャラダインが主演している。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若さま侍捕物帖 お化粧蜘蛛", "若さま侍捕物帖 お化粧蜘蛛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "剣を取らせれば滅法強いが女には甘い若さま。相手役として東映の美女を総ナメにした橋蔵の、今回の相手は佐久間良子。秘剣“一文字崩し”を操り、悪党を斬りまくる若さまの活躍を描く。シリーズ最終作となった。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふたりのイーダ", "ふたりのイーダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "直樹とゆう子の兄妹は雑誌記者の母に連れられて広島にやって来た。二人は森の中で言葉をしゃべる不思議な椅子と出会う。椅子は原爆で死んだ少女をずっと待っているのだった……。原爆の悲惨を訴えて有名な松谷みよ子の原作を映画化した美しいメルヘン。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Science Des Reves", "恋愛睡眠のすすめ", "", "", "http://image.eiga.k-img.com/images/movie/1812/315x210.jpg?4084", 105, "「ヒューマンネイチュア」「エターナル・サンシャイン」のミシェル・ゴンドリー監督が、シャイで引っ込み思案の男の恋愛妄想をコミカルに描くラブコメディ。父親の死をきっかけにメキシコから母親のいるパリに引っ越してきたばかりのステファンは、隣人のステファニーに恋心を抱く。だがシャイな性格が災いして、理想の夢ばかり見るようになり、やがて現実と夢の区別がつかなくなっていく……。", "2007-04-28", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEET JUSTICE", "レディ・スコルピオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "肉体と美貌と力とワザありの６人の女性が暴れ回るアクション映画。汚職事件を調べていた議員が殺され、その妹が仲間のレディ・スコルピオンを集めて姉の敵を討ち、町の巨悪を一掃するべく立ちあがる。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALLIGATOR II：THE MUTATION", "アリゲーター2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ある日、沼地で作業をしていた男が、何者かに五体を引き裂かれて無残に殺される。やがて同じような事件が次々と発生、事態を重く見た警官と爬虫類学者は巨大アリゲーターのしわざと判断するが、当局は相手にしてくれず……。軍隊まで登場するワニ対人間のバトルシーンが圧巻。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOIN DU VIE＾TNAM", "ベトナムから遠く離れて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "ベトナム戦争が際限なくエスカレートしていたころ、いくら活動家でないとはいっても、“もはや沈黙は共謀を意味する”と考えたフランスの監督たちが、ベトナムに関する思い思いの短編をそれぞれに持ち寄って、一本の作品にしたのがこの映画。映画作家がどこまで現実にかかわり、現実を変えることができるか、というジャーナリスティックな問題意識を全面的に押し出した点がユニーク。ニュース・フィルムあり、ゴダールのモノローグあり、フィクションのような画面ありと、姿勢も方法も感覚も違う11の章で構成され、全体をC・マルケルがまとめている。", "1968-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍者武芸帳 百地三太夫", "忍者武芸帳 百地三太夫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "父・百地三太夫を殺された鷹丸の仇討ちと、謎を秘めた百地家の財宝探しの絡む忍者合戦を、曲芸的なアクションの連続で荒唐無稽に描いた時代劇。天守閣からのダイビングシーンなど、JACのメンバーによるアクションが冴える。アクション監督は千葉真一が担当。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROCKET GIBRALTAR", "ジブラルタル号の出帆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "老作家兼教授ロックウェルの77歳の誕生日を祝うため、子供たちが家族を連れてやって来た。孫たちはプレゼントに祖父が愛したボート、ロケット・ジブラルタル号を修繕して贈ろうと決めたが……。往年の大スター、B・ランカスターが物静かで知的な老人を好演。３世代にわたる家族の心の交流を、淡々ときめ細かく描いた感動作。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みな殺しの拳銃", "みな殺しの拳銃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "長谷部安春監督によるハードボイルド・アクションの佳作。ヤクザ組織の卑劣な手段に３兄弟が反撃に出る。組織の縄張り内の賭場や遊戯場を荒らし回り、ボスを殺すが次男も銃弾に倒れる。長男は、単身ライフル片手に幹部らとの最後の決戦に挑んでいく。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とられてたまるか!?", "とられてたまるか!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "郊外の一戸建て住宅と仲良し家族だけが生きがいの公務員・一太郎の家に泥棒が入る。警察も警備システムもあてにできないと知った一太郎は、“再び来る”という泥棒の挑戦にたった一人で家と家族を守る決心をするが……。「ホーム・アローン」を思わせるような泥棒撃退ドタバタ騒動を描いたホーム・コメディ。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER ROSENKO¨NIG", "薔薇の王国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "実験的な映像表現でヴェンダースやヘルツォークといった、ドイツの監督たちに多大な影響を与えたといわれる、W・シュレーター監督の初の一般公開作品。映画は、薔薇の園芸家アンナと彼女が溺愛する息子アルベルト、そして無垢な若者フェルナンドを加えた不可思議な三角関係を描いたもの。近親相姦とホモセクシュアルな雰囲気を色濃くたたえた幻想世界が、美しい映像の中に展開していく。", "1986-01-01", "7,25", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE　BADGES", "ホワイト・バッジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "ベトナム戦争に出兵した韓国人兵士の苦悩を描く問題作。戦時の後遺症に悩まされる帰還兵の姿と、彼が戦友との再会によって回想する熾烈な戦いの模様を交錯させながら、ベトナム戦争が残した傷あとを浮き彫りにする。1992年の東京国際映画祭ではグランプリを受賞。主人公に扮したA・ソンギの熱演も光る。", "1992-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラの逆襲", "ゴジラの逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "第１作が空前の大ヒットを記録したため急きょ製作された一編。本多猪四郎に代わって小田基義が監督し、新怪獣アンギラスが登場。アンギラスはゴジラと死闘を展開するが、一敗地にまみれる。ゴジラは前作で東京に上陸したので、今回は大阪に上陸するという律義なところをみせるが最後は氷づけにされてしまう。", "1955-04-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("網走番外地", "網走番外地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "石井輝男監督が新東宝時代から温めてきた題材を高倉健を得て映画化。貧農出身のヤクザ者・橘真一は、母恋しさに網走刑務所からムショ仲間と手錠につながれたまま脱獄するが、二人の行手には大雪原が待ちうけていた……。原作はいちおう伊藤一になっているが、石井輝男は“原作は自分だけが二枚目になったような辛気臭い内容なんで、僕がずっと温めてきたオリジナルでやりました。原作から使ったのはタイトルだけです”と語っている。添え物のモノクロ映画として鶴田浩二主演の「関東流れ者」とともに封切られたにもかかわらず、受刑者の間に流布していた歌を採譜した独特の哀愁に満ちた主題歌と、チンピラの臭いを残す高倉健が予想外の人気を博した。急きょ続編が製作されるが、この時点ではまだシリーズ化される予定はなかった。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デストロイ・ヴィシャス", "デストロイ・ヴィシャス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "架空の街ネオオオサカシティーを舞台に、ヤクザとパンクロッカーの女がたどる破滅的な愛を描いた人間ドラマ。勘違いから敵対するヤクザの組員を10人も殺害してしまった新見は、逃亡中に聾唖（ろうあ）のパンクロッカー、マリアと出会う。不器用にしか生きることのできない2人は次第にひかれあっていくが、新見の命を狙う刺客が次々と現われ……。「電撃BOPのセクシーマザーファッカーズに!!」の島田角栄監督がメガホンをとる。", "2011-03-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AFRICA AMA", "残酷猟奇地帯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "満月の夜に行われる首斬り葬送、処女の性器縫合、牛フンのスープ、石斧で歯を叩き割る医者、犬を絞め殺す成人式など、アフリカに今も残る残酷な奇習を克明にリポートした衝撃のドキュメンタリー。", "1972-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Talk to Her", "トーク・トゥ・ハー", "", "", "http://image.eiga.k-img.com/images/movie/1682/315x210.jpg?2152", 113, "前作「オール・アバウト・マイ・マザー」でアカデミー外国語映画賞を受賞したアルモドバル監督、3年振りの新作。ドイツの舞踏家ピナ・バウシュの「カフェ・ミュラー」「炎のバズルカ」の舞台が登場、また、彼女と交際中のブラジルを代表するミュージシャン、カエターノ・ベローゾが歌う場面もある。劇中に登場する昔のサイレント映画「縮みゆく恋人」は、ムルナウ監督の「サンライズ」(27)を意識してアルモドバル監督が撮ったもの。", "2003-06-28", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪猫五十三次", "怪猫五十三次", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "大映“怪猫”シリーズ第５作。岡崎藩の勢力争いに巻き込まれた家老の娘・浪路が、留守居役一味に襲われ命を落としてしまう。浪路の婚約者・三次郎は、彼女の仇を討とうとするが、そこに浪路の愛猫の姿が見え隠れする。スリラーの要素を加味した意欲作。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ruins", "パラサイト・バイティング　食人草", "", "", "http://ecx.images-amazon.com/images/I/51I1fPHxHqL._SL210_.jpg", 91, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Exit Through the Gift Shop", "イグジット・スルー・ザ・ギフトショップ", "", "", "http://image.eiga.k-img.com/images/movie/56102/315x210.jpg?4339", 90, "決して素顔を明かさないナゾの覆面グラフィティ・アーティスト、バンクシーの初監督作。第83回米アカデミー長編ドキュメンタリー賞にノミネートされて話題となった。ストリート・アートに関するドキュメンタリーを制作していた映像作家のティエリー・グエッタは、幸運にもバンクシーの取材に成功する。しかし、グエッタに映像の才能がないと気づいたバンクシーはカメラを奪い、グエッタを“ミスター・ブレインウォッシュ”というアーティストに仕立てあげ、カメラの前に立たせる。", "2011-07-16", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STOLEN FACE", "盗まれた顔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "整形外科医のフィリップは、すべての女性が美しければ、女性の犯罪者はいなくなるという学説を持っていた。この自説を実証するため、犯罪者リリーに整形を施す。美しくなったリリーは、フィリップと結婚する。ところが、自分の顔が元フィリッブの恋人アリスと同じであることを知り、再び犯罪の道へ。フィリップは、そんなリリーを殺そうと計画するが……。元恋人アリスがピアニストという設定により、バックに流れるクラシカルな音楽もサスペンスを盛り上げる。", "1952-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Happy", "happy しあわせを探すあなたへ", "", "", "http://image.eiga.k-img.com/images/movie/57962/315x210.jpg?3551", 75, "「ジンギス・ブルース」（1999）で第72回アカデミー長編ドキュメンタリー賞にノミネートされたロコ・ベリック監督が、「幸せ」をテーマに手がけたドキュメンタリー。米ルイジアナ州の湿地帯からナミビアのカラハリ砂漠、ブラジルのサーフィン村、ブータンの山麓、東京新橋のサラリーマン街まで世界5大陸16カ国をめぐる撮影を敢行。アメリカで1981年から始まった「幸福度」の研究の権威でもあるエド・ディーナー博士やリチャード・デビッドソン博士らとともに、経済的豊かさが必ずしも幸福度に比例しない現代社会における「本当の幸せとは何か？」を問う。", "2012-05-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MRS.SOFFEL", "燃えつきるまで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "D・キートンとM・ギブソンという二大スターの顔合わせで描いた悲劇のラブ・ストーリー。死刑囚と刑務所長夫人の恋の逃避行という、ショッキングな実話をベースにした一編。時は20世紀初頭、ピッツバーグの刑務所を舞台に、弟とともに殺人の容疑で死刑の判決を受けた若者と、信仰厚い夫人の出会いから、脱獄に至るプロセスを、丹念かつドラマティックに描いていく。「わが青春の輝き」でデビューしたオーストラリア出身の女流監督G・アームストロングの第３作。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE SUNSHINE", "悪魔の狂暴パニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "幻の麻薬“ブルー・サンシャイン”の後遺症で頭髪が抜け落ち、発狂した男女が次々と人を殺し、町はパニック状態に陥る。濡れ衣を着せられた主人公は、真相を探っていくうちに意外な事実を知る。頭髪の抜け落ちた殺人鬼がオモシロい(？)ホラー映画。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人生をうまく跳べない人", "人生をうまく跳べない人", "", "", "http://ecx.images-amazon.com/images/I/51lpyFKhT5L._SL210_.jpg", 60, "", "2009-09-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少年※、安※！／DUST OF ANGELS", "天幻城市", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ホウ・シャオシエンが主催するシティフィルムズ・プロダクション（城市電影公司）が送り出す記念すべき第１作。監督はこれがデビュー作となるシュウ・シャオミン。さびれた港町に住む、二人の幼なじみを主人公に、後戻りすることのできない危険な極道の世界へと足を踏み入れていく少年たちの姿をみずみずしい感性で描く。監督のシュウは、「童年往時」の助監督を務めていたこともあり、そこここにホウ・シャオシエンの影響が垣間見られるが、全編を貫く冷徹なリアリズム的視線が見事。", "1992-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　DE´PART", "出発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ゴダールの「男性・女性」の出演者二人を主演に据えた、鮮烈な青春映画。レース出場を夢みて、ポルシェを探し歩く青年と、同行するハメになった美容師見習いの関係が、躍動感あるモノクロ画面に生き生きと綴られる。弾けるようなシークエンスの数々に映える、ハマリ役というべきJ・P・レオーの名演。そして、フィルムが焼失していく衝撃的な幕切れは圧巻の一言。", "1967-01-01", "37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("８月のクリスマス", "８月のクリスマス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "韓流ラブ・ストーリーの名作「八月のクリスマス」を、日本に舞台を移してリメイク。余命いくばくもない主人公と小学校の臨時教員の女性との恋を、優しい眼差しで描き出す。人気ミュージシャンの山崎まさよしが、主演・音楽・主題歌を担当。映画全体のトーンを決める大役を担う。写真館を営む寿俊は、余命わずかなことを知り、静かに世を去ろうとしていた。ところが近所の小学校教員、由紀子と恋に落ちてしまう。", "2005-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぷるぷる 天使的休日", "ぷるぷる 天使的休日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "恋に憧れて天界から降りてきたイケイケ・ギャルの天使・エルちゃんと、彼女を連れ戻そうとするコケティッシュな天使・ヒロリンが地上で巻き起こすお色気冒険物語。スケベな男たちの魔の手を逃れて、人間の愛に目覚めていく天使役がフーミンと愛ちゃんとくれば、眼目は“愛”よりも悩殺ボディだ。橋本監督は、このセクシー路線アイドル・コメディをハイテクSFXで彩ってみせる。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FAMIGLIA", "ラ・ファミリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "1906～87年までの激動のイタリアを背景に、三世代にわたる一家族の人間模様を心優しく、かつコミカルに描いていく大河ドラマ。実験精神の旺盛なエットーレ・スコラ監督は、この作品においても、舞台を家の中だけと限定し、外の世界は窓から見える程度にとどめながらドラマを進行させていく。カメラは住居内を縦横無尽に動きまわり、時代の経過を１ショットで説明してしまう。室内劇という限られた空間を逆に開放感へと導く見事な手法ともいえるだろう。主人公であり、語り部ともなるヴィットリオ・ガスマンの名演をはじめ、出演する名優たちのそれぞれが、惚れぼれする演技を披露している。", "1987-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tutta colpa di Giuda", "それもこれもユダのせい", "", "", "http://image.eiga.k-img.com/images/movie/55422/315x210.jpg?1612", 102, "前衛的な演出家のイレーナは、刑務所の教戒師の依頼を受け、収容者とキリストの受難劇を上演することになる。しかし、誰ひとりとして裏切り者のユダを演じたがらず……。「トリノ、24時からの恋人たち」のダビデ・フェラーリオ監督が、トリノの刑務所の実際の収容者を役者に起用して撮影を敢行したコメディ。イタリア映画祭2010にて上映。", "2010-04-30", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天涯海角／LOST AND FOUND", "世界の涯てに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「HERO」の金城武と人気歌手ケリー・チャンが共演したラブ・ストーリー。不治の病に冒された女性ケリーが“何でも屋”のチュンに、心の支えになってくれた英国人男性テッドの居所を探し出してもらう。チュンに心惹かれながらも、テッドを追って英国へと渡るケリー。やがて、死が間近に迫っていることを感じた彼女は、テッドが教えてくれたスコットランド沖の“世界の涯て”と呼ばれる場所へと旅立つ。スコットランドの壮大な風景の中で迎えるクライマックスが幻想的で美しい。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("またいつか夏に。", "またいつか夏に。", "", "", "http://image.eiga.k-img.com/images/movie/57952/315x210.jpg?1737", 0, "2012年・第4回沖縄国際映画祭の企画「地域発信型プロジェクト」で製作された作品。茨城県筑西市を舞台に、両親のいない少年ヒロキとフリーターの青年・渡辺の年齢を超えた友情を描く。", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("曽根崎心中", "曽根崎心中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "近松門左衛門の浄瑠璃が原作。堂島新地の遊女・お初と醤油屋の手代・徳兵衛は深く言い交わした仲であるが、夫婦になるには幾多の障害がある。この二人が心中を決意し、互いに刺し違えて死ぬまでを描いている。原作ではお初を、進退きわまった徳兵衛に同情して心中を決意する女として描いているのに対し、増村監督は、愛する男と夫婦になれなければ心中しか道はないと決意する女として描出。女の意地の凄まじさを梶芽衣子が美しく体現している。徳兵衛が女郎屋の床下に隠れる動作、セリフのやり取りなど、歌舞伎の演出がそっくり生かされていて、作品に奥行きを与えている。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIDEKICKS", "サイドキックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "格闘技アクション・スターのベテランともいうべきC・ノリスが、自分自身を演じるヒーローもの。体も気も弱い少年バリーは、父親の勧めで空手を習い始め、格闘技選手権に出場する。そこでバリーは、憧れのC・ノリスと出会い、なんと一緒にチームを組んで戦うことになったのだ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("方世玉續集", "レジェンド・オブ・フラッシュ・ファイター　電光飛龍　方世玉２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "J・リー(リー・リンチェイ)が伝説の英雄に扮した武術アクション・シリーズの第２作。秘密結社、紅花会の一員となったサイヨは、会長の出世の秘密を記した巻物を刺客から取り戻すために奔走。さらに、捕らわれた会長や母を救うために、単身、敵地へと乗り込んでいく。得意のカンフーはもちろん、日本刀による華麗な立ち回りも披露するJ・リーの熱演に注目。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BALLAD OF CABLE HOUGE", "砂漠の流れ者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "灼熱の砂漠の真ん中で仲間に裏切られ、銃もロバも水も奪われたケーブル・ホーグは、不屈の生命力で砂漠を歩き続け、４日目に奇跡的に水を掘り当てる。駅馬車の通り道が近いと知った彼はそこに給水所を作り、大もうけができると考えた。たまたま通りかかったうさん臭い牧師のジョシュアとともに土地の所有権を手に入れたケーブル・ホーグは、町のグラマーな娼婦ヒルディとも意気投合し万事はうまく運んだ。が、そこに昔、彼を裏切った二人の仲間が戻って来る……。西部をこよなく愛したペキンパー監督が、終わりゆく開拓時代への挽歌をホロ苦いタッチでうたい上げた名作。「ケーブル・ホーグのバラード」というタイトルで公開されたこともある。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙大怪獣ギララ", "宇宙大怪獣ギララ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "松竹初の怪獣もの。月ステーションから地球に帰った宇宙船に付着していた物体が巨大な怪獣に成長。あらゆるエネルギーを栄養分として成長するこの怪獣に、人間はなすすべもない。特撮はかなり安っぽく、結局松竹はこれ１本で怪獣ものを打ち切る。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シンク", "シンク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "PFF1997でグランプリに輝いたビデオ作品。準グランプリ作品「鬼畜大宴会」同様、そのまま一般公開されるという快挙を成し遂げた。物語の主人公は、携帯電話を使わずにケータイと同じコミュニケーションをとる女の子一人と青年二人。テレパシーと呼ぶにはあまりに日常的で無防備な彼らの“超”能力を、SFテイストを排したプライベート感覚あふれる語り口で紡いでいく。ビデオならではのフレキシブルな編集とやわらかな映像の感触が織り成す、独特の演出効果が出色。ナイーブでたくましい、ビデオ世代による“おとぎ話”と言えるだろう。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ADVENTURE OF MARCO POLO", "マルコ・ポーロの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "13世紀の中国を舞台にイタリア人、マルコ・ポーロの華やかな恋愛と冒険を綴った歴史活劇。マルコはどんな危険に際しても無事に切り抜ける運にめぐまれ、悪漢アーメッドの手からクカチエン姫を救う。クーパーのゴールドウィン作品出演第１作。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BAD AND THE BEAUTIFUL", "悪人と美女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ハリウッドの内幕を描いたブラッドショーの小説の映画化。ハリウッドの一流監督フレッド、大スターのジョージア、第一級の脚本家のジェームズのもとに、パリのジョナサンから電話がかかってくる。映画製作の協力依頼だ。しかし３人にはジョナサンにまつわる苦い思い出があった。フレッドは彼とたもとを分かち、ジョージアは彼に捨てられ、ジェームズは彼のために生活を破壊されたのだった。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("楳図かずお恐怖劇場 DEATH MAKE", "楳図かずお恐怖劇場 DEATH MAKE", "", "", "http://ecx.images-amazon.com/images/I/51GWAB88MXL._SL210_.jpg", 60, "ホラー漫画界の巨匠・楳図かずおの短編6作品を映像化したシリーズ「楳図かずお恐怖劇場」の1編。VFXアーティストとして活躍する太一が初監督に挑み、心霊スポットを訪れた人々を襲う恐怖を描く。心霊サイトで出会ったメンバーたちと共に、心霊スポットとして知られる廃屋へやって来た美少女クロエ。そこには、不気味なマスクが残されており……。主人公クロエ役に、本作が映画初主演となるモデルのアリス。", "2005-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("軽蔑", "軽蔑", "", "", "http://image.eiga.k-img.com/images/movie/55832/315x210.jpg?2992", 136, "芥川賞作家・中上健次の遺作長編小説を、高良健吾と鈴木杏のダブル主演で映画化。「余命1ヶ月の花嫁」「雷桜」の廣木隆一監督がメガホンをとる。東京で賭博に明け暮れる日々をおくっていた名家出身のカズは、新宿歌舞伎町で働くポールダンサーの真知子と熱烈な恋に落ちる。2人はカズの故郷で新生活を始めるが、カズの父親をはじめ周囲は2人の関係を認めなかった。やがて真知子は東京へ戻り、自暴自棄におちいったカズは、借金にまみれながら賭博で身を滅ぼしていく。", "2011-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Point Break", "ハートブルー", "", "", "http://ecx.images-amazon.com/images/I/51B1CZ5MW3L._SL210_.jpg", 121, "キアヌ・リーブス、パトリック・スウェイジ共演のアクション映画。カリフォルニアのベニスビーチ周辺で、元大統領のマスクを被った集団による銀行強盗事件が続発。その手口から犯人はサーファーではないかと推測したFBIは、新人エリート捜査官ユタをサーファーグループに潜入させるが……。メガホンをとるのはアクション映画を得意とする女性監督キャスリン・ビグロー。海や空を舞台に繰り広げられる迫力満点のアクションシーンは必見。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SGT.BILKO", "スティーブ・マーティンの　Sgt.ビルコ　史上最狂のギャンブル大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "1955～59年にかけて全米で放映され、人気を博したTVシリーズ「Sgt.Bilko」をS・マーティン主演でリメイクした軍隊コメディ。バクスター駐屯地の車両部を率いるビルコ曹長は、裏では兵隊相手に様々な賭け事を取り仕切る元締め。宿敵ソーン少佐の査察を受けたビルコはあらゆる作戦で切り抜けていくが、彼の罠にはまり、新兵器ホバー戦車資金横領の濡れ衣を着せられてしまう……。ハートウォーム・コメディの多かったS・マーティンが久々にアナーキーな演技を披露し、口八丁手八丁の無責任男を快演。「いとこのビニー」のJ・リン監督のツボを心得たコメディ演出も光る。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hollywoodland", "ハリウッドランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "TV版「スーパーマン」シリーズで主演を務め人気を誇った俳優ジョージ・リーブスの謎に満ちた死をテーマに描いたミステリー。スーパーヒーローを演じた男の苦悩や事件を追う探偵の姿を織り交ぜながら、50年代のハリウッドに衝撃を与えたスキャンダラスな事件の真相に迫る。徹底的なリサーチを行ってリーブスになりきったベン・アフレックは、ベネチア国際映画祭最優秀男優賞を受賞。共演はエイドリアン・ブロディ、ダイアン・レインほか。", "2007-06-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAREFUL，HE MIGHT HEAR YOU", "少年の瞳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "両親のいない少年PSは、叔母ライラの愛情をうけて育ったが、６歳のある日もう一人の叔母バネッサが現れ、彼の養育権を主張。PSは二軒の家を行ったり来たりするはめになる。バネッサは財力にまかせてPSに愛情を注ぐが、彼の心は離れていく一方だ。そんな時、行方不明だったPSの父が突然現れた……。哀しく切ない大人たちの生きざまを見つめながら、自らも哀しみを知っていく少年の珠玉の感動ドラマだ。監督は「第七の予言」のC・シュルツで、オーストラリア・フィルム協会賞の作品賞他８部門を受賞。彼のハリウッド進出のきっかけとなった。", "1984-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JIMI　HENDRIX", "ジミ・ヘンドリックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "天才ギタリストJ・ヘンドリックスの急死から３年後の1973年に製作された音楽ドキュメンタリー。ジミの短い生涯における華やかな日々と、偉大なアーティストを失った悲しみを父親、恋人、多くの友人らが語り、一人の人間としての彼の姿を浮き彫りにしていく。1969年のウッドストック・フェスティバルでの伝説のライヴパフォーマンスなども多数収録。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CHARTREUSE DE PARME", "パルムの僧院", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 167, "1815年。ナポリからパルムに帰ってきたファブリスは伯母の公爵に迎えられた。数年ぶりに見る甥の姿に、彼女の肉親の愛が恋心に変わっていく。宮廷で催された夜会で、刑務所所長ファビオの娘クレリアは、ファブリスを見てその面影を心に焼きつける。ファブリスは女のことで男と争い、彼を刺したため捕らえられ城砦に幽閉された。そこで彼はクレリアに出会い、二人は愛し合うようになる。20年の禁固刑を言い渡された甥を救うべく、公爵は反王党派に助力を求め脱獄を成功させた。しかしこの事件でクレアの父は刑務所所長を罷免され、クレアとも別れなければならなくなる……。スタンダールの小説をC・ジャックが監督した波乱万丈の時代活劇。日本での劇場公開版は142分。", "1947-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("You're my sunshine", "ユア・マイ・サンシャイン", "", "", "http://image.eiga.k-img.com/images/movie/33722/315x210.jpg?4710", 122, "韓国で本当にあった奇跡の純愛物語を映画化し、大ヒット作「私の頭の中の消しゴム」を抜いて韓国恋愛映画史上No.1のヒットを樹立した感動作。辛い過去に苦しめられたあげくHIVに感染してしまった女性と、妻として迎えた彼女を愛しぬこうとする純朴な青年との愛を描く。ヒロインのウナに扮するのは「スキャンダル」のチョン・ドヨン。彼女を一心に愛し続ける青年ソクチュンを「甘い人生」のファン・ジョンミンが演じる。", "2006-11-03", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Let the Bullets Fly", "レット・ザ・バレッツ・フライ（英題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-08-31", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北斗の拳", "北斗の拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "“おまえはすでに死んでいる”のセリフがあまりにも有名な人気漫画『北斗の拳』の、日米合作による初の実写映画。暴力が支配する世界最終戦争後の世界。“北斗神拳”の伝承者であるケンシロウは、“南斗聖拳”の達人シンから最愛の女ユリアを奪還するため荒野をさまよっていた。シンが待ち受けるサザンクロスへ単身乗り込むケンシロウの運命はいかに。アクション俳優、G・ダニエルズが完璧な肉体で伝説の英雄ケンシロウを演じる。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OLIVER　＆　COMPANY", "オリバー／　ニューヨーク子猫ものがたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "文豪チャールズ・ディケンズの小説『オリバー・ツイスト』を下敷きにしたディズニー・アニメ。ニューヨークのマンハッタン。捨て猫オリバーは、犬のドジャーや彼の仲間の犬たちと知り合う。騒動に巻き込まれたり、心優しい少女と出会うなど、様々な経験をするオリバーの冒険を描く。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Vow", "君への誓い", "", "", "http://image.eiga.k-img.com/images/movie/57842/315x210.jpg?3713", 104, "「きみに読む物語」のレイチェル・マクアダムスと「親愛なるきみへ」のチャニング・テイタムが主演するラブストーリー。幸せな新婚生活を送っていたペイジとレオだったが、交通事故にあいペイジが5年間の記憶を失ってしまう。レオはペイジを支えようと奮闘するもすれ違いが生じ、葛藤する。", "2012-06-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("揺啊揺、揺到外婆橋", "上海ルージュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "　「紅いコーリャン」「菊豆」「秋菊の物語」に続き、チャン・イーモウとコン・リーがタッグを組んだ都会派活劇。リーはこれまでのイーモウ作品に見られる耐え忍ぶ農村の女性とは違い、派手な衣装に身を包む歌姫を演じる。1930年代、シュイシェン少年は叔父に連れられマフィアのタンのもとへ。そこで、タンの情婦チンバオの召使いになる。華やかな世界に隠された、裏切りや対立組織の抗争が少年の目前で繰り広げられる。", "1995-01-01", "8,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE NEMO", "リトル・ニモ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "世界の一流スタッフが集結して作り上げた劇場アニメの大作。少年主人公による夢と冒険にあふれた内容で、善悪の戦いという分かりやすい構図で展開するファンタジー・ロマン。メリサ・マンチェスターによるさわやかな主題歌がラストを飾る。夢の国に来たニモが、悪夢界に拉致された夢の国の王を連れ戻すため、女王、教授、善良な市民たちと共に悪夢界へ旅立つ。DVDタイトルは「リトル・ニモ」。", "1989-01-01", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRE´PAREZ VOS MOUCHOIRS", "ハンカチのご用意を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "B・ブリエ監督が、子供ができない夫婦の悲喜劇を綴った一編。ときおり原因不明の発作を起こす妻に愛人まであてがう人の好い夫。３人は奇妙な共同生活を始めるのだが、妻のいらだちが子供ができないためだと判明し、13歳の少年を養子に迎える。やがて精神的にも肉体的にも少年と結ばれ彼女は妊娠。二人の男たちは寂しく去っていく……。B・ブリエらしい破天荒な物語展開が魅力。", "1975-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スクラップ集団", "スクラップ集団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "野坂昭如の原作を田坂具隆が松竹で初演出した喜劇。元汲み取り屋、元福祉事務所のケースワーカー、公園の清掃人、安楽死の研究に没頭する医者の４人が、大阪・釜ヶ崎を舞台に人間生活につきまとうすべての屑をスクラップにする商売を考えつき大繁昌となるのだが……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フィレンツェの風に抱かれて", "フィレンツェの風に抱かれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "鎌倉、フィレンツェを舞台に、二人の男の間で揺れ動く女の心情を描いたラブ・ストーリー。ヒロインがフィレンツェで知り合う有名デザイナー役で、マカロニ・ウエスタンで知られるG・ジェンマが出演。また和泉聖治は、この時期「さらば愛しのやくざ」「ゴールドラッシュ」（ともに1990年）と連打を放ち、本作はその直前に撮られた作品である。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FULL MOON IN BLUE WATER", "ブルーウォーターで乾杯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "テキサスの湾岸にある小さな町に、そのグリル・バー“ブルーウォーター”はあった。行方不明になった妻ドロシーの映っている８ミリを見て、一日を過ごすこのバーの主人フロイドと、彼に好意を寄せるバスの女運転手ルイーズや、店の常連の客、店の不動産を狙う男たちらの交流を描く。「バウンティフルへの旅」のマスターソン監督らしい題材の佳作。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("T−REX・BACK　TO　THE　CRETACEOUS", "T−REX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "アイマックス方式の立体映像で、恐竜の世界を体感できるアドベンチャー・ドラマ。凶暴なティラノザウルスや宙を飛ぶプテラノドンなど、白亜紀の巨大生物が目の前に出現！　古生物学者が発掘した恐竜のものらしき卵。博物館でガイドをしている娘、アリは誤ってこれを落としてしまう。突然、彼女の周囲は６万５千年前の恐竜の世界に変わり……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bubble", "Bubble バブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "米オハイオ州の小さな人形工場で働くマーサとカイルは長年、惰性で友情を続けていたが、新たに従業員としてやってきた若いシングルマザーのローズとカイルが親しくなったことで関係が破たん。そんな中、町で起こった殺人事件が3人の日常を崩壊させていく。スティーブン・ソダーバーグ監督がアマチュアのキャストを起用するなど実験的手法で撮影した心理ドラマ。「ガールフレンド・エクスペリエンス」（2010）にあわせて日本初公開された。", "2010-07-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELECTRIC DREAMS", "エレクトリック・ドリーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ヴァージン・レコードが設立した映画会社、“ヴァージン・ピクチャーズ”の第１回作品。監督のバロンはマイケル・ジャクソンやa～haのミュージック・ビデオで知られる俊英だ。人間のように思考や感情を持ったパソコンとその持ち主、そして同じアパートに住む女性との恋愛三角関係をコメディ・タッチで描いたラブ・ファンタジー。建設会社に勤めるマイルズは苦手のパソコンを買うが次第に夢中になり、今では部屋中オートメーション化。そんな彼の上の部屋に、美人のチェロ奏者マデリーンが越してくる。マイルズはたちまち彼女に恋するが、シャンペンがかかったことから意志を持つようになったパソコンも彼女の演奏に聞き惚れ、音楽で心を通わせるうち恋に落ちる。パソコンが作ったラブ・ソング（歌：ボーイ・ジョージ）のCGの場面は感動的。エピソードの一つ一つ、音楽の使い方のセンスも冴えた素敵なラブ・ロマンス。", "1984-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("推手", "推手", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "のちの「ウェディング・バンケット」、「恋人たちの食卓」とあわせて“父親3部作”といわれるアン・リー監督の記念すべきデビュー作。太極拳の師匠、朱老人は、息子夫婦を訪ねてニューヨークへやって来る。しかし息子の嫁マーサはアメリカ人で言葉も通じず、食事も合わず気まずくなる一方。安らぎはチャイナタウンで太極拳を教えるだけという老父に、息子は……。カルチャーギャップに戸惑いながらも、“アジアとアメリカ”、“父と子”という異なる価値観が調和を見いだしていく優しき人間ドラマ。", "1991-01-01", "53,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TAMARIND SEED", "夕映え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "コメディの名手B・エドワーズ監督が、ソビエトの諜報部員とイギリスの外務省に勤める女との危険な愛を描いたラブ・ストーリー。イギリス外務省に勤めるジュディスは、休暇で出かけたカリブ海のバルバドス島でKGBの男、フェオドールと恋に落ちた。しかし二人の恋は、英・ソ両国の思惑によって、無残にも押しつぶされていく……。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIAM", "がんばれ、リアム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "７歳の少年リアムの目を通して、1930年代の港町リバプールで不況の波に揉まれながら必死で生きる一家の姿を描き出す。勤め先の造船所閉鎖に苛立ち、ファシズム運動に身を投じていく父、そんな父を見守り家族を支える母、若くして働く姉。そんな中、リアムは疲れた母の髪を梳いてあげたり、初めて目にする女性の裸画に驚いたり……。厳しくつらい現実の中、ちょっと内気でどもり気味だけど、素直で健気なリアムの表情に心が温まる好編。", "2000-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("傷害恐喝 前科十三犯", "傷害恐喝 前科十三犯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "黒沼組組長を殺し、刑務所に入っている峰岡組幹部・沖津政次は、面会に来た弟分から、組長が何者かによって殺され、組が危機に立っていると知らされる。黒沼組の仕業とわかった沖津は、刑務所の中で仲間を集め、組長の仇をとるために立ち上がる……。過剰ともいえる暴力が炸裂するアクション・ヤクザ映画。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("定年退職", "定年退職", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "源氏鶏太お得意のサラリーマン小説『男と女の世の中』を映画化。定年退職を１ヵ月半後に控えたサラリーマン、矢沢章太郎を主人公に、定年までの間に彼の周囲で起こる騒動を描いている。失恋した章太郎の娘、妻に先立たれた彼に再婚を迫るバーのマダムなど、絡み合ういくつかの恋愛模様を島耕二がうまくさばいている。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPERSTAR：THE LIFE AND TIMES OF ANDY WARHOL", "アンディ・ウォーホル スーパースター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "1987年に急逝した、ポップ・アート界の巨匠、アンディ・ウォーホル。この作品は、彼の生前のフィルム、友人・知人へのインタビュー、そして彼の作品などから、ウォーホルの実像を映し出したドキュメンタリー。D・ホックニーら美術業界ゆかりの著名人が、インタビューで登場する。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MALE´FICHE PRESENZE", "エクソシストの謎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "誰も寄りつかない無人島に、魔女が住むという荒れ果てたホテルがあった。そこに妊婦のジェーンを含む８人が、ホテルを改装し営業を再開させるために訪れる。しかし人々の噂通り、８人に次々と奇怪な現象が襲いかかり、何者かによって惨殺が繰り返されていく……。リンダ・ブレア主演だが「エクソシスト」とは無関係のイタリア製ホラー。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラマンメビウス＆ウルトラ兄弟", "ウルトラマンメビウス＆ウルトラ兄弟", "", "", "http://image.eiga.k-img.com/images/movie/33352/315x210.jpg?1640", 95, "1966年の放送開始以来、世代を超えて愛され続けるウルトラマンシリーズの40周年を記念して製作された劇場版。究極超獣Uキラーザウルスの復活をたくらむ宇宙人連合の魔の手に堕ちた新ヒーロー・ウルトラマンメビウスを救うため、伝説のウルトラ兄弟が復活。歴代の怪獣たちと激しい戦いを繰り広げる。初代ウルトラマンでハヤタ隊員を演じた黒部進ほか当時のキャストが集結し、往年のファンにとっても楽しめる作品となっている。", "2006-09-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Night of The Iguana", "イグアナの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "　元・牧師のシャノンはメキシコ旅行ツアーのガイドをしていた。しかし彼はミッション・カレッジの女性客一行をガイド中、少女に言い寄られたことがきっかけで、解雇の危機に。助けを求め、女主人マキシーンが経営するホテルに逃げ込むシャノン。やがて彼は旅行中の女性画家のハンナと知り合うことになるが……。元・牧師の災難、そして彼を取り巻く女性たちとの交流を乾いたユーモアを織り交ぜて描く。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wedding Campaign", "ウェディングキャンペーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "田舎の純朴な男たちが花嫁探しの旅に出る姿を、実話をモチーフに描いたハートウォーミング・ドラマ。田舎で暮らす38歳の独身男性マンテクは、同じく独身の親友ヒチョルと共に、ウズベキスタンへ花嫁探しの旅に出る。女性と目を合わせることすらできず失敗続きのマンテクは、いつしか通訳の女性ララに心を開くようになり……。主演は、「夏物語」のスエ、「トンマッコルへようこそ」のチョン・ジェヨン。", "2006-12-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("МЫ ИЗ ДЖАЗА", "ジャズメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ソ連の港町オデッサに、1920年代、ジャズがソ連にもやったきたころ、ジャズ・プレーヤーを目指した一団があった。革命後まもなくの社会背景にもまれながらバンドを結成、モスクワまで進出するのだが……。ソ連のジャズ草創期のエピソードを戦後生まれの作家が描く。", "1983-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUSHROOMS", "マッシュルーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "死体を抱え込んだ老人が思いもかけぬ方法で処理するブラック・コメディ。万引きで生計をたてている初老の老人ミニーとフローが静かに暮らす下宿屋に、突然指名手配の強盗犯グラブが舞い込んできた。グラブはフローの万引きをネタに居座り、さらには聞き込みにやって来た警官のハリーも住むことになる。ところが、酔って寝込んだグラブを知らないうちに窒息死させてしまい、二人は死体を処理するために料理して食べることを思いつくが……。1995年東京国際ファンタスティック映画祭第１回観客大賞グランプリを受賞した。", "1995-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大悪党", "大悪党", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "洋裁学校の生徒が街のヤクザに睡眠薬を飲まされたうえにヌード写真を撮られ、人気歌手と寝ることを強要される。そのことでゆすられた歌手は弁護士の所へ駆け込むが、これがまたとんでもない悪徳弁護士だった……。田宮二郎が、名実ともに脂の乗りきった頃に主演したピカレスク・ロマン。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MUMMY", "ミイラ再生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "“ホラーのユニバーサル”の名声を確実にした古典的名作。英国考古学者が発見したミイラは、若い学者が好奇心から読み上げた呪文で蘇生してしまう。彼（ミイラ）は、3000年前の恋を果たそうと、昔の女王そっくりの女性に迫る。特殊メイクされたミイラ役、B・カーロフの顔は、大迫力でコワイ。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陸軍残虐物語", "陸軍残虐物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "佐藤純彌監督の記念すべきデビュー作で、日本の軍隊の矛盾を痛烈に暴いた意欲作。補充兵として召集された犬丸らは、厳しい軍紀の中、鬼軍曹の亀岡らに徹底的にいびられまくり、ついには面会に来た犬丸の妻まで亀岡に犯されてしまう。人間としての忍耐の限界を超えた犬丸は……。西村晃の徹底した悪役ぶりも見ものである。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("発狂する唇", "発狂する唇", "", "", "http://image.eiga.k-img.com/images/movie/1872/315x210.jpg?3281", 75, "女子中学生の首が次々に切り落とされるという連続殺人事件が発生。しかし容疑者とされた倉橋美智夫は行方をくらまし、残された母と二人の妹は、マスコミや近隣の住民、刑事からも執拗な嫌がらせを受ける。兄の無実を願う妹・里美は街で偶然見つけた“心霊研究所”を訪ね、霊能者・間宮悦子に兄の居場所を探って欲しいと頼む。悦子は降霊実験を行い、殺された少女たちの霊を呼び寄せると、少女たちの首なしの霊は自分の首を求めてさまよい始めた。", "2000-02-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マリアのへそ", "マリアのへそ", "", "", "http://image.eiga.k-img.com/images/movie/53352/315x210.jpg?2265", 106, "マニラの路上で生活する実在のストリートチルドレンたちをキャステングし、貧しくも温かい彼らの日常をリアルに綴ったドラマ。6歳の少女マリアは、父や2人の兄と一緒に路上で暮らしている。過酷な状況ではあったが、マリアは家族4人で過ごせることに幸せを感じていた。ところが、仕事に就こうと奮起した父が思わぬ事件を起こし……。監督は、在日朝鮮人女性の人生を描いたドキュメンタリー「ハルコ」で高い評価を受けた野澤和之。", "2008-02-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花婿の寝言", "花婿の寝言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "「花嫁の寝言」の大ヒットを受けて、その２年後に製作されたコメディ。川崎弘子と林長二郎のキャスティングが面白い。新婚早々の花嫁が寝言ばかり言うという話を聞いて、離婚沙汰が起きるが、実は毎晩花婿が寝言を言うためだと判明。友人らが花婿の寝言を拝聴することになるが……。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続 向う三軒両隣り—第三話 どんぐり歌合戦—", "続 向う三軒両隣り—第三話 どんぐり歌合戦—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "いつも威勢がいい車屋の坂東亀造。ところが威勢がよすぎて色々と間違いが起きる。今日も若者から風呂敷包みを御茶の水まで届けてほしいと頼まれるが、“車ってのは人間様が乗るもんだ”とタンカをきって行ってしまう。そんな亀造はある日、有楽町で夕刊売りの少年・健吉と出会い、すっかり気に入り里親になる。長くラジオで放送を続けた人気ラジオドラマの映画化。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LADY DROP〈レディ・ドロップ〉", "LADY DROP〈レディ・ドロップ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "雑誌で人気のグラビア隊の面々が総出演したアクション。重要任務に就いた落ちこぼれ婦人警官たちの奮闘を描く。フレッシュなヒロインたちの活躍はもちろん、水野晴郎の特別出演も見もの。数年先の近未来。抗争を繰り広げる暴力団と中国系マフィアを壊滅すべく、４人の婦人警官が潜入捜査を命じられた。落ちこぼれの彼女たちは力を合わせ、それぞれの得意分野で奔走する。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海底３万マイル", "海底３万マイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "地上国征服をたくらむ地底国と平和を望む海底国の戦いを描くSFアニメ。冒険好きの少年イサムが火山見学に出かけたところ、海底国の王女エンジェルと知り合う。しかしその直後、地底から火焔竜が現れマグマで辺りを溶かし始めた。危機一髪でその場を脱出したイサムは、火焔竜を操る地底国の恐ろしい陰謀を知ることに。彼はエンジェルたち海底人とともに地底国のマグマ王に挑んでいく。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("痴呆性老人の世界", "痴呆性老人の世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "九州にある公立病院のボケ老人病棟内にカメラを持ち込み、痴呆性老人の姿を通して“ボケ”について理解を深める意図で撮られたドキュメンタリー。まずカメラは日常の診療風景や介護家族を通して、老人たちを紹介していく。78歳なのに自分を嫁入り前の娘だと主張する人。何日もご飯を食べていないと訴える人。いつも荷物を背負って病院内を歩き回る人。一方で、家族の名前を忘れてしまっているのに、百人一首を全部暗誦している人など、老人たちの世界をエピソードを交えて描いていく。映画は、この病院の治療法である“説得より納得”を分かりやすく描きつつ、今日的テーマを愛情を持って訴えている。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Deadly Friend", "デッドリー・フレンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "　母親とともに町に引っ越してきた天才少年ポールは、サマンサという少女に恋をする。しかし彼女は父親の虐待を受けたあげく、脳死状態に。悲しみに暮れるポールは彼女の体に、自ら作り上げたロボットのコンピューターを埋め込む。だが、ロボットとして復活したサマンサはやがて殺人を犯すことに……。少年の悲劇的な恋、そして惨劇を描く青春ホラー。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Year of The Dragon", "イヤー・オブ・ザ・ドラゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "　チャイニーズ・マフィアの内部抗争が激化。その中で若き幹部ジョーイがのし上がっていく。ニューヨーク市警の刑事スタンリーはチャイナタウンの犯罪組織壊滅に乗り出す。スタンリーが強引な捜査を続ける一方、ジョーイも容赦ない殺戮を繰り返し、やがてドンの座へ。怒りに震えるスタンリーとジョーイがついに激突する！　ハリウッド史に残る大失敗作となった「天国の門」に続くマイケル・チミノ監督のバイオレンス・アクション。ミッキー・ロークとジョン・ローンの対決が話題を呼んだ。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAVEMAN", "おかしなおかしな石器人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "原始時代を舞台にした映画はあまたあるが、“石器人語”がセリフとして喋られたのは初めて。若者アトウークが酋長の娘に恋したばかりに追放され、原野を放浪するハメになる。ユニークな恐竜たちと競演(？)するリンゴ・スターの持ち前のキャラクターが、なんともおかしい珍品コメディ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANASTASIA", "追想", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "G・ボルトンの原作をマルセル・モウレットが戯曲にしたものを映画化。ロシア革命から、からくも逃げ延びたと伝えられるロマノフ王朝の皇女、アナスタシアに絡む、恋と陰謀を描く。I・バーグマンが、過去の記憶を失った謎の女アンナに扮し、アンナをアナスタシアに仕立てようとするボーニン役のY・ブリンナーと恋に落ちていく。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙戦艦ヤマト　復活篇　ディレクターズカット", "宇宙戦艦ヤマト　復活篇　ディレクターズカット", "", "", "http://image.eiga.k-img.com/images/movie/57522/315x210.jpg?1976", 120, "2009年に劇場公開された「宇宙戦艦ヤマト　復活篇」のディレクターズカット版。企画当初に「地球消滅篇」として構想されていた内容で、劇場公開版とは異なる、09年のプレミア試写会上映版のエンディングを大幅に再構成。劇場公開版ではカットになったシーンの再編や新作パートの追加、新規アフレコなども行われた。小林誠（「復活篇」副監督）や羽原信義（「復活篇」メカニック演出）ら「復活篇」に携わったスタッフが、「宇宙戦艦ヤマト」シリーズの生みの親で10年に死去した西崎義展監督の遺志を継ぎ、完成させた。", "2012-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちぎれた愛の殺人", "ちぎれた愛の殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "“天使のはらわた”シリーズや「死んでもいい」などで知られる異才・石井隆の脚本を得て、池田敏春が鮮烈な映像で描くサイコ・サスペンス。猟奇殺人事件の捜査線上に浮かび上がった助教授、村木を見張る刑事の陽子。事件の裏にはノイローゼで療養中の村木の妻・名美の存在が見え隠れしていた……。村木に扮した佐野史郎の怪演も見もの。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mutant Chronicles", "ミュータント・クロニクルズ", "", "", "http://image.eiga.k-img.com/images/movie/55192/315x210.jpg?1973", 111, "スウェーデンの人気ゲームをベースに、人類とミュータントの壮絶な戦いを描くSFアクション。トーマス・ジェーン、ロン・パールマン、ジョン・マルコビッチ、デボン青木ら個性派キャストが集結。西暦2707年、全世界を巻き込むエネルギー戦争のさなか、人間を突然変異体のミュータントに変えてしまう「マシーン」の封印が解かれる。ミュータントたちの脅威にさらされた人類を救うため、世界中から集められた精鋭部隊が決死の戦いに挑む。", "2010-03-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EAST　IS　EAST", "ぼくの国、パパの国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "イギリス・アカデミー賞の最優秀英国映画賞に輝くなど、世界的に絶賛されたヒューマン・コメディ。1971年のマンチェスター地方。イギリス人の妻を持つ頑固なパキスタン人ジョージの悩みは、７人の子供たちがイスラム文化を尊重せず、すっかり西洋の価値観に染まっていること。ついに業を煮やしたジョージは、二人の息子をパキスタン人女性とお見合いさせようとするが、家族の猛反発を食らって孤立していく。ゲイになって家出してしまう長男、一日中パーカーのフードをすっぽり頭にかぶっている内気な末っ子など、子供たちの描写が何ともユーモラス。切実な親子の文化的ギャップを描きつつ、常に笑いが絶えない軽快な語り口と微笑ましい人物描写が、観る者に親しみを抱かせる。", "1999-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Band's Visit", "迷子の警察音楽隊", "", "", "http://image.eiga.k-img.com/images/movie/53122/315x210.jpg?3039", 87, "イスラエルの小さな町に迷い込んだエジプトの警察音楽隊と地元民との交流をユーモラスに綴り、カンヌをはじめ世界各地の映画祭で絶賛された人間ドラマ。文化交流のためにエジプトからイスラエルへ招かれたアレキサンドリア警察音楽隊は、目的地を間違えてホテルすらない辺境の町に辿り着いてしまう。住人の家に泊めてもらうことになった彼らは、自分たちと言葉も宗教も違う人々に戸惑いながらも、音楽を通して次第に打ち解けていく。", "2007-12-22", "46,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUSAN　LENOX　HER　FALL　AND　RISE", "スザン・レノックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "G・ガルボとC・ゲーブルが唯一共演した恋愛ドラマ。叔父との農場生活に嫌気がさし、家出したヘルガは、建築技師ロドニーと恋に落ちる。だが、叔父に連れ戻され、再び逃げ出したヘルガは、男から男へと渡り歩き、ついには裕福な男の妾となり、スザン・レノックスと名乗るようになる。やがて二人は再会するが、ロドニーは絶望して彼女のもとを去り、ヘルガは初めて真実の愛に気付くのだが……。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.DESTINY", "MR.デスティニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "高校野球の決勝戦で空振り三振、以来自分の人生も空振りになってしまったと信じる男がMR.デスティニーという不思議な男に会い、彼の差し出すカクテル“スピルト・ミルク”を飲んだとたん、15歳の彼はホームランをかっ飛ばすのだった……。“覆水盆に返らず”のコトワザを逆手に取った、ファンタスティックなヒューマン・コメディ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DR.JEKYLL AND MS.HYDE", "ジキル博士はミス・ハイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "スティーヴンソンの名作『ジキル博士とハイド氏』に大胆なアレンジを施したSFXコメディ。科学者のリチャードは、死んだ大祖父の遺産相続でジキル博士の日記を受け取る。日記に記されていた遺伝子の研究を実行し、自ら被験者になったリチャードに、もう一人の人格“知的な悪女ヘレン・ハイド”が現れる。やがて心ばかりか肉体的にも変化が起き、ヘレンはリチャードの体を乗っ取ろうと画策するが……。ヘレン役のS・ヤングがセクシーでしたたかな女性を巧みに演じている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("餓狼の群れ", "餓狼の群れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "松方弘樹が「強奪」に続いてダーティ・ヒーローを演じた犯罪映画。言われなき汚名により警察を辞職した唐木のもとに強奪屋の会田から中国マフィアの秘密カジノ襲撃計画が持ちこまれた。そこは、かつての唐木の後輩刑事、倉橋を死に追いやった麻薬の裏取引所だった。倉橋の死に不信を抱く唐木は計画に加わるが、会田の裏切りに遭い、危機一髪になる。彼を助けたのは、カジノに潜入していた倉橋の姉だった。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画　怪物くん", "映画　怪物くん", "", "", "http://image.eiga.k-img.com/images/movie/55972/315x210.jpg?1882", 103, "藤子不二雄A原作の人気漫画を実写化し、2010年4月～6月に放送された同名ドラマを3D映画化。主演はドラマ版に続き人気アイドルグループ「嵐」の大野智。「チーム・バチスタの栄光」「ゴールデンスランバー」の中村義洋監督がメガホンを取る。国民から怪物ランドの王位継承を反対された怪物くんは、怒り心頭で怪物ランドを飛び出してしまう。日本へ向かったつもりが「カレーの王国」にたどり着いてしまった怪物くんらは、王国の権力者ヴィシャールから反乱軍にさらわれた姫と王子の救出を頼まれ……。", "2011-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("テイルズ・オブ・ヴェスペリア The First Strike", "テイルズ・オブ・ヴェスペリア The First Strike", "", "", "http://image.eiga.k-img.com/images/movie/54562/315x210.jpg?2058", 0, "全世界でシリーズ累計1000万本を売り上げる人気RPGゲームをプロダクションI.Gが映画化。人間と魔物の壮絶な戦い「人魔戦争」が終結し、皇帝不在ながらも帝国は平和を保っているように思われていた。人々は根源たる力・エアルによってあらゆる暮らしを支えていた。ある日、帝国騎士団ナイレン隊に所属する新人騎士ユーリ、フレン、双子のヒスカとシャスティルは、エアル異常噴出の調査のため深い森へと足を踏み入れていった。", "2009-10-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("X THE UNKNOWN", "怪獣ウラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "1955年のヒット作「原子人間」に次いで、翌年公開されたSF怪奇映画。ドロドロ溶けていく被害者の特殊メイクや、ゼリー状の怪物など、モノクロ映画ならではの不気味さが伝わってくる。スコットランドの荒野で、不思議な爆発が起こり、調査に向かったアダム博士は、放射性火傷を負っている二人の兵士を発見。彼らを襲ったのは、地下のウラン鉱が生命体となった怪獣ウランだった。", "1956-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フライング 飛翔", "フライング 飛翔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ボート・レース中、事故で大ケガを負って引退した叔父の夢を受け継いだ一人の少女が、プロのレーサーを目指して苛酷な試練に立ち向かっていく姿を描いた青春映画。ボート・レーサーとしても知られた漫才師の横山やすしが、競艇レースをテーマに自ら企画・製作した作品。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIX DEGREES OF SEPARATION", "私に近い６人の他人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ニューヨーク五番街にあるキドリッジ夫妻の高級マンションに、突然ケガを負って転がり込んできた黒人青年ポール。ハーバード大学に在籍する息子の同級生という彼は、洗練された巧みな会話で夫妻を魅了する。しかも彼の父親は名優シドニー・ポワチエ。驚嘆する夫妻を父が監督する映画に出演させてやるとまで約束するが、どれもこれも真っ赤な嘘だということが判明する。やがてミステリアスな彼の正体が徐々に明らかになっていくのだが……。世界各国で賞賛を浴びた舞台劇を原作者J・グエア自身の脚本により映画化したユニークな人間ドラマ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RESURRECTION", "レザレクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "シカゴで残虐な猟奇連続殺人事件が発生。被害者はいずれも使徒の名を持つ33歳の男性で、腕や頭部、脚など体の一部を切り落とされ、謎のローマ数字を遺体に刻まれていた。刑事のプリュドムは事件の儀式性に注目し、復活祭の日に向けた犯人の恐るべき目的に気付く……。光と影を巧みに配したショッキングな映像で、観る者を恐怖に陥れるサイコ・スリラー。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AWAKENINGS", "レナードの朝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "30年間も半昏睡の状態で精神病院で暮らしてきた障害者レナードが新任医師の努力と誠意で奇跡的に“めざめの朝”を迎える。果たして彼は普通の生活に入っていくことができるのだろうか？ 実話をもとに女流監督P・マーシャルが、R・デ・ニーロとR・ウィリアムズの競演で描く感動作。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Odd Couple", "おかしな二人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "女房に逃げられた男同士が奇妙な共同生活を始める爆笑コメディ。N・サイモンの大ヒット舞台劇の映画化で、彼が脚本も担当（アカデミー賞にノミネート)。W・マッソーは舞台と同役を演じ、J・レモンとのコンビは絶妙の味を見せる。舞台俳優、演出家出身のG・サックスは、これが映画監督第２作。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOLORES CLAIBORNE", "黙秘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "S・キングがK・ベイツのために書いたミステリー小説を映画化。日食を背景に、母と娘の絆、女たちの男社会への復讐をあざやかに描く。夫に虐待される主婦ドロレスと娘のセリーナだが、日食の夜に夫が不審な死を遂げる。それから20年の月日が流れ、ニューヨークでジャーナリストとして活躍するセリーナの元に、母親が殺人事件の容疑者となっているという匿名のFAXが届く。数年ぶりに故郷に帰ったセリーナは事件の真相を調べるうちに、母親の驚くべき秘密を知ることに……。K・ベイツがドロレス役を熱演。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOY CUBA", "怒りのキューバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "'59年のキューバ革命後の、キューバ庶民の苦渋を捉えた抒情詩『私はキューバ』が原作。オール・キューバ・ロケで撮られた、美しいモノクロ作品だ。'64年にミラノ映画祭で最高賞を受賞。ハバナの娼婦マリア、さとうきび畑で働く老農夫ペドロ、ハバナ大学の学生闘士のエンリケ。3人のエピソードが綴られるが、それぞれの方法で支配者への抵抗の意志を示していく。", "1964-01-01", "12,49", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FANTO＾ME AVEC CHAUFFEUR", "運転手つき幽霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "殺されて幽霊となった二人の男が、あの世に行くまでの間、犯人探しに奮闘するコメディ。大会社の社長フィリップのお抱え運転手として、15年間忠実に務めてきたジョルジュは、いつものように車を出そうとした時、何者かに撃たれてしまう。その24時間後、フィリップも腹心マルティーグの偽装殺人の罠にはまり命を落とす。幽霊となった二人は、あの世に行くまでの48時間の間、残された家族の行動を見に行くことに。ショックを受けている息子セバンチャスを目のあたりにしたフィリップは、自分が殺された事実を伝えようとジョルジュと奮闘する。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("処女ゲバゲバ", "処女ゲバゲバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "若松孝二の「狂走情死考」に続く監督作品。ボスの女に手を出し、私刑を受けて荒野をさまよう男と、キリストのように磔刑にされた女との出会いと別れを描く。荒野を一つの密室として捉え、荒野のほかにはテント一つと十字架１本しか出て来ない不思議な作品。出口出こと大和屋竺が脚本を書いた。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUCKY　BREAK", "ラッキー・ブレイク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「フル・モンティ」以来、４年ぶりにP・カッタネオ監督が手掛けたヒューマン・ドラマ。銀行強盗に失敗して服役中のジミーは、刑務所の更生プログラムの一環として演劇の上演が行われることを知り、これを利用して脱獄を画策。率先して演劇プロジェクトを牽引する彼だったが、凶悪な囚人の出現や、更生指導員との恋によって、計画が揺らぎだし……。「フル・モンティ」のような突き抜けた陽気さは控え目に、さえない悪党たちの人間模様をユーモアとペーソスを交えて描写。のどかな空気を醸し出す、レゲエ風の音楽も印象深い。", "2001-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Timecop", "タイムコップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "　タイムマシンの開発によって時間犯罪が多発。そこで時空間の管理を行なう警察組織が創設された。2004年、その任務にあたっていた捜査官のウォーカーはある事件の黒幕が次期大統領候補でもあるマコームであることに気づく。1994年に赴き、その調査を行なうウォーカー。それは彼の妻が死亡した年でもあったが……。愛する人を亡くした傷心の捜査官が時空を超える戦いに挑むSFサスペンス。バン・ダムが火を噴くタイムマシンに乗り込んで大活躍。", "1994-01-01", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIDAS　PRIVADAS", "ブエノスアイレスの夜", "", "", "http://ecx.images-amazon.com/images/I/51F2PS76Z8L._SL210_.jpg", 105, "1970年代にアルゼンチンの軍事政権が残した傷跡を、恋人たちの悲劇を通して浮き彫りにした社会派劇。軍事政権下で受けた拷問の記憶に苛まれる女性と、過酷な歴史を知らずに育った若者。親子ほど歳の離れた彼らの愛の深まりを、官能的な映像で描写する。皮肉な運命に引き裂かれる二人をC・ロスと、G・G・ベルナルが好演。", "2004-12-11", "22,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南京的基督／THE CHRIST OF NANJING", "南京の基督", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "文豪・芥川龍之介の同名小説を映画化した香港・日本合作作品。1920年代の南京を舞台に、妻子ある日本人小説家とキリストの存在を信じる中国人娼婦の悲恋を描く。小説家の岡川は、年老いた父の借金返済のために遊廓に身を任せた金花と出会う。二人は一緒に暮らし始めるが、岡川に妻子がいることを知った金花は姿を消し、岡川も日本へ帰国する。しかし、行くあてのない金花は遊廓へと戻り、不治の病に冒されてしまう……。キリストを信じ続ける薄幸なヒロインを富田靖子が熱演し、1995年東京国際映画祭の最優秀女優賞を受賞した。", "1995-01-01", "6,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TENEBRAE", "シャドー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「サスペリア」「インフェルノ」などでイタリアン・ホラーの巨匠となったアルジェント監督の意欲作。ニューヨークからローマにやって来た売れっ子の推理小説家が、次々と猟奇殺人事件に巻き込まれる。それは自分の書いた小説の中の殺人犯とまったく同じ手口だった。アルジェント監督は、日本でも大当たりをとった「サスペリア」で確立したスタイリッシュなホラー演出に磨きをかけ、血の臭い漂う“恐怖”を、ラストシーンまで一気に描ききっている。殺人鬼が、若い女性を襲う際の、光と影のコントラストの絶妙さ、夜のプールに映るにぶい光、砕けたガラス窓の破片など、恐怖感を盛り上げる映像美が秀逸。", "1982-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミッドナイトイーグル", "ミッドナイトイーグル", "", "", "http://image.eiga.k-img.com/images/movie/34172/315x210.jpg?1522", 131, "高嶋哲夫原作の同名ベストセラー小説を「ホワイトアウト」「亡国のイージス」の小滝祥平プロデューサー、「油断大敵」「フライ,ダディ,フライ」の成島出監督が映画化した山岳アクション。北アルプスで忽然と姿を消したステルス型戦略爆撃機、通称“ミッドナイトイーグル”に搭載されていた特殊爆弾を巡って、男たちが雪山で死闘を繰り広げる。出演は大沢たかお、竹内結子、玉木宏、吉田栄作、藤竜也ら。", "2007-11-23", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺陣師段平", "殺陣師段平", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "長谷川幸延の原作を黒澤明が脚色、瑞穂春海が監督した芸道映画。大正元年、演劇が大衆から離れていきつつあることを感じた沢田正二郎は“新国劇”を創立し、剣劇ものに活路を見出そうとした。もと殺陣師の一座の頭取・市川段平は、必死に立ち回りを研究し、一座に深く貢献していくのだが……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACCIDENT", "できごと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "“赤狩り”でアメリカを追われイギリスに亡命したロージーが、知識階級の人間の偽善を告発した問題作。中年の大学教授が教え子の若い娘に恋愛感情を持つが、彼女にも複数の男性関係があることを知る。三角関係のドラマの中に人間の嫉妬と狡智が冷徹に描かれた佳作。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江分利満氏の優雅な生活", "江分利満氏の優雅な生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "原作は山口瞳の第48回直木賞受賞作。原作では主人公は電機メーカーに勤める宣伝部長だが、映画では洋酒メーカーの宣伝部員（山口瞳が当時サントリー宣伝部に勤めていた）に置き換え、主人公が直木賞を受賞するまでを描いている。それまで男性アクションで定評があった岡本喜八が、この１作で戦中派の屈折した心理を見事に捉え、本格的に評価された。ストーリーは、洋酒メーカーの宣伝部員というしがないサラリーマンの“才能のないだらしない奴が一生懸命生きること”の大変さを、自分史や戦後史と重ね合わせながら描く。岡本の演出は、主人公と妻との若い頃のロマンスを、「残菊物語」にたとえて、下駄と靴だけの合成アニメーションで描いたり、父親の事業の盛衰を書き割りのセットやアニメーションで描いたりと自由奔放をきわめる。また主人公の背後にいる同僚をストップモーションにして、カメラの背後でもう一人の自分が画面内の自分が置かれた立場を分析してみせるシーンなど、ナレーションの使い方も秀逸。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女殺油地獄", "女殺油地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "大阪の油屋の次男与兵衛は、元締の娘小菊と許されぬ恋仲になっていた。与兵衛の乳母がわりとして油屋に奉公していたお吉は、二人を別れさせるために手をつくすが、そこには血ぬられた悲劇が待っていた……。女の情念を描き続けた五社英雄監督が、そのきわめつけともいえる近松文学に挑戦した意欲作であり遺作ともなった。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Life as We Know It", "かぞくはじめました", "", "", "http://image.eiga.k-img.com/images/movie/55402/315x210.jpg?1480", 115, "「幸せになるための27のドレス」のキャサリン・ハイグルと「トランスフォーマー」シリーズのジョシュ・デュアメル共演のラブコメディ。やり手のケータリング業者のホリー（ハイグル）は、TVディレクターのエリック（デュアメル）とデートするが、お互いに最悪な印象をもったまま別れる。だが、2人の共通の友人夫婦が事故で死んでしまい、孤児となった1歳の女の子ソフィーを育てることになる……。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("顔役", "顔役", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "勝新太郎の意欲に満ちた監督第１作だが、発表当時はほとんど評価されなかった。勝自身が扮する破天荒な刑事が、信用金庫の不正融資事件の黒幕を暴き、その真犯人を倒すという内容。その映像から炸裂するパワーは類型的なジャンル映画の範疇から文字通りハミ出していく。手だけを追い続けることで賭博場の空気を伝える白熱の冒頭から、いきなり足の指の間から顔をのぞかせる主人公の初登場シーン、夢幻世界を思わすカー・クラッシュのめくるめくイメージ、そして画面が土に覆われ暗くなっていくクライマックスまで、目も覚めるような場面の連続。徹底的にクローズアップにこだわった構図は個性派俳優たちの面構えや存在感をシンプルに引き出し、また要所要所に挿入された“路上”のショットの数々は社会への確かな視点も感じさせる。その一方で、鏡像を効果的に用いたり、ファンキーな劇中音楽を寸断したりと、繊細な演出も発揮。なにはともあれ、唯一無二のグルーブ感が全編を疾走する一大娯楽作であることは間違いないだろう。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼極道", "鬼極道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "梅沢一家若頭、人斬り義貞こと伊庭義貞は刑務所に入っている間に、親分である梅沢を創仁会系綱島一家との抗争で殺される。出所した伊庭は手打ちによって話が済み、自分が梅沢の跡目を継ぐことになっていることを知った。復讐を誓って組を辞めた伊庭は綱島一家の情報を集めるうちに、創仁会３代目組長の次男、財前悟道と出会い、似た者の二人はお互いに惹かれ合うものを感じるのだった。しかし、伊庭が綱島を撃ち殺したことから抗争は激化。血で血を洗う戦いとなっていく。そしてついに伊庭と悟道は銃を向け合うことに……。極道の中の極道である伊庭と武闘派である悟道、二人の壮絶な生き様を描くアウトロー・バイオレンス。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OCEAN　MEN", "オーシャンメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "「グラン・ブルー」のモデルとなったフリー・ダイバー、ジャック・マイヨールと交流の深かった世界的海洋カメラマン、ボブ・タルボットが手掛けたドキュメンタリー。登場するのは二人の天才ダイバー、キューバ生まれのフランシスコ・ピピン・フェレーラスと、イタリア生まれのウンベルト・ペリッツァーリ。肉体の限界に挑戦し続ける彼らの姿を追いながら、神秘的な深海の世界の魅力を、息をのむような美しい映像で映し出す。ビデオ、DVDタイトルは「ラスト・グラン・ブルー　永遠のライバル」", "2001-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WACO", "渡り者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ならず者のジョー・ゴーア一味に保安官を殺され、彼らの言うがままの無法地帯と化していた、ワイオミングの小さな町エンポーリア。思い余った町長は、服役中のならず者、ウェイコに特赦を与えて釈放する。ウェイコもさっそくその腕前を披露するが、彼自身にも策謀があり……。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Trespass", "トレスパス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "　消防士のビンスとドンは消火作業中に宝の地図を手に入れる。後日、ふたりはその地図に記された工場跡の廃屋に侵入し、捜索を開始。ところが偶然そこで黒人ギャングによる殺人を目撃してしまう。彼らに見つかったビンスとドンはギャングのボス、キング・ジェームズの弟を人質にして、一室に立てこもるが……。武装ギャングを相手に、決死の脱出を試みる男たちの姿を描いた密室クライム・アクション。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT EYES", "ナイト・アイズ 危険な肉体", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "妻との離婚裁判を有利にするため、妻の浮気現場をビデオテープに収めようとする人気ロック歌手。だが、その監視を依頼されたまじめな青年ウィルは、ビデオカメラごしに彼女のベッド・シーンをのぞくうち、自分の中の男としての本能と野性を爆発させる。ロバーツのお色気満点なセクシー・サスペンス。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DAEK CORNER", "闇の曲り角", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "スター娯楽映画をたくさん撮ってきたハサウェイ監督が、本作では後にTV番組『ルーシー・ショー』で米国民のアイドルとなるルシルを起用した、犯罪映画の快作。フィルム・ノワールによく似合う摩天楼の街NYが舞台。刑務所を出所した主人公は、秘書を雇って探偵事務所を始める。ある日、自分を尾行する男に気付いた彼は、その男から意外な人物の名前を聞く。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK WATER TRAIL", "ブラックウォーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ある殺人事件の再捜査に乗り出した小さな田舎町の警察官アンディが、不審な死を遂げた。その死の直前、アンディの妹キャシーは、彼からその殺人事件に関するテープを受け取っていた。そのテープには、聖書を引用した、かつての連続少女誘拐事件についてのメッセージが吹き込まれている。兄の死とかつての殺人事件とのかかわりに疑問を持ったキャシーは、かつての恋人マットとともに、テープに込められた暗号を解読する。そして、一つ一つの鍵が解け始める時、意外な犯人像が浮かび上がるのだった。", "1995-01-01", "3,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大奥絵巻", "大奥絵巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "11代将軍・徳川家斉は、愛妾に町家育ちの純真な娘・お阿紀を選ぶが、大年寄らの激しい嫉妬を買い苦境に追い込まれる。叙情派・山下耕作監督が、溝口健二の愛弟子・成沢昌茂の脚本を得て、腰のすわった演出を見せる。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAMMER HEAD", "刑事ハマー カリブの熱い風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ベトナムの戦友が自らの目前で無惨に殺される。彼が残した鍵を手掛かりにハミダシ刑事のハマーは捜査を開始、事件の背後にフロリダ半島からジャマイカを結ぶ巨大な麻薬シンジケートが浮かび上がる。灼熱の国ジャマイカを舞台に、一人のハードボイルド刑事が活躍するポリス・ストーリー。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROADWAY MELODY OF 1940", "踊るニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "F・アステア、E・パウエル主演による“ブロードウェイの内幕”３部作最後の作品。ある芸人コンビの友情と確執を軸に、楽しいショービズ界の人間模様を描く。『Begin The Beguine』などコール・ポーターの名曲が散りばめられ、特に６分に及ぶアステア＆パウエルのダンス場面が見もの。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents 真夜中の宴　「daughters」", "桃まつり presents 真夜中の宴　「daughters」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 24, "", "2008-04-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SECOND ARRIVAL", "アライバル2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "チャーリー・シーンが地球を守るため、エイリアンたちに立ち向かう科学者ゼインを演じたSFサスペンスの続編。物語はゼインがエイリアンの手にかかって無残な最期を遂げるところから始まる。彼の義理の弟ジャックとその恋人ブリジットは、ゼインが死ぬ前に発送した小包を受け取った。中にはエイリアンの地球侵略計画の証拠書類が入っており、それによると彼らは自分たちの住みやすい環境を作るために、オゾン層を破壊し、地球温暖化計画を実行するつもりらしい。ジャックとブリジットはゼインの意志を引き継ぎ、人間に姿を変えたエイリアンたちに立ち向かっていく。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ポケットモンスター　ミュウツーの逆襲", "ポケットモンスター　ミュウツーの逆襲", "", "", "http://ecx.images-amazon.com/images/I/617LVlevWiL._SL210_.jpg", 75, "大ヒット・ゲームのキャラクターが活躍する、子供たちの間で大人気のTVアニメ初の劇場版。“ポケモンマスター”を目指す少年サトシたちがニューアイランドのポケモン城で開催されるポケモン・トーナメントに出場。未知の戦いに挑んでいく。それまでTVでは登場しなかったミュウが活躍するなど映画ならではの趣向が盛り込まれている。", "1998-07-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自来也（忍術三妖伝）", "自来也（忍術三妖伝）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "忍術映画の代表作「兒雷也」をマキノ雅広監督・片岡千恵蔵主演で映画化した娯楽時代劇。野武士・佐久間正盛一味によって滅ぼされた更科輝隆の一子・太郎丸は、一夢仙人より妖術を授かり、自来也と名乗って復讐を開始するが、悪の妖術使い大蛇丸が立ちはだかる……。この題材は日本映画の父でありマキノ雅広の父でもあるマキノ省三が、サイレント時代初期に“目玉の松ちゃん”こと尾上松之助主演で製作した一連の忍術映画の代表的なもの。戦後も加藤泰監督による「忍術児雷也・逆襲大蛇丸」の２部作をはじめ、何度も再映画化されるが、シンプルな語り口のこの作品が一番印象深い。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陽は沈み陽は昇る", "陽は沈み陽は昇る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "パリ＝イスタンブール＝インドと１万５千キロに及ぶ大ロケーションを敢行。ほとんどが外人キャストで、音楽に巨匠ニーノ・ロータを迎えた日活創立60周年記念作品。監督・脚本は「執炎」「栄光への5000キロ」などの蔵原惟繕と山田信夫の名コンビ。日本人とアメリカ人の二人の青年とフランス人の女が、２台のオートバイと１台の車で旅をする。女をめぐる対立と友情。女を襲う回教徒の群れ。果たせぬ復讐。女の死。男たちの別れ……。作品は成功とは言いがたいが、その意欲は買える。封切当時、アフガニスタン大使館から強姦場面にクレームがつき、再編集したあとに公開された。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents うそ　「愚か者は誰だ」", "桃まつり presents うそ　「愚か者は誰だ」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 28, "", "2010-03-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLANK GENERATION", "ブランク・ジェネレーション／　リチャード・ヘル＆　　ザ・ヴォイドイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "N.Y.パンクの立役者として活躍したリチャード・ヘルが主演した青春映画。脚本を手がけたのは、現在作家として活動しているヘル自身、プロデューサーはアンディ・ウォーホルという興味深い顔合わせ。貴重なライブ映像が、当時のパンク・シーンの熱狂を伝えてくれる。フランスからやって来たジャーナリストのナダは、次世代のロックスターと目されるビリーの才能にほれ込み、二人は恋に落ちるが……。", "2005-07-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月はどっちに出ている", "月はどっちに出ている", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "WOWOWで放送した“J・MOVIE・WARS”の崔洋一編を、監督自ら劇場用に映画化。在日コリアンのタクシー・ドライバー、姜忠男を主人公に、フィリピン・パブのホステス、コニーとの恋を柱にして、彼を取り巻く人々との悲喜劇を描いている。現在の人種のるつぼと化した東京を戯画化して見せる崔洋一監督の演出と、どこか煮え切らない主人公・忠男を演じた、岸谷五朗の好演が見もの。コニー役のルビー・モレノも、女優として一皮むけた演技を見せた。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALOHA SUMMER", "アロハ・サマー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1959年のハワイを舞台に、二人のアメリカ人、二人のハワイアン、二人の日系人の少年たちの一夏の体験を綴った青春メモワール。人種的偏見、文化的ギャップを乗り越え、サーフィンに熱中し、恋に心ときめかし、友情を確かめあう彼ら。ハワイのまっ青な空と海にこだまする、当時のヒット曲やスタンダードなハワイアン・ソングも聴きもの。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCARY MOVIE2", "最‘新’絶叫計画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ホラー映画のパロディーを盛り込んだ「最終絶叫計画」の続編。超常現象を引き起こす屋敷の調査に出向いた大学教授と、学生たち。幽霊に遭遇するものの、彼らは怖さもそっちのけで乱痴気騒ぎを繰り広げる。冒頭の「エクソシスト」を皮切りに、「ハンニバル」「チャーリーズ・エンジェル」などのパロディーが満載。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Greenfingers", "グリーンフィンガーズ", "", "", "http://image.eiga.k-img.com/images/movie/1412/315x210.jpg?4568", 91, "自由で開放的なエッジフィールド更正刑務所に、人生を諦めた男コリンが移送されてきた。ある日所長は、コリンと大男ロウ、女好きのトミー、風変わりな老人ファーガスなどを指名し庭造りを命令する。ガーデニングによって命を育てる喜びに目覚めるコリン。なんと彼には、天才庭師（＝グリーンフィンガーズ）の素質があったのだ！　そんな時、その“囚人庭師チーム”に女王陛下も鑑賞する“ハンプトンコート・パレス・フラワーショウ”出場の話が舞い込んでくる。", "2001-04-07", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜・明", "孫文 100年先を見た男", "", "", "http://image.eiga.k-img.com/images/movie/54682/315x210.jpg?3249", 127, "辛亥革命を成功させ、「中国革命の父」とも呼ばれた革命家・孫文を描いた歴史ドラマ。主演はメイベル・チャン監督の「宋家の三姉妹」でも孫文を演じたウィンストン・チャオ。1910年、孫文は9度目の武装決起に失敗し、清朝政府は70万両の白銀を懸賞金に孫文の命を狙っていた。国外亡命を余儀なくされていた孫文は、革命資金調達のためマレーシアのペナンへ向かうが……。", "2009-09-05", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROMAN SCANDALS", "羅馬太平記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "いつでもローマ時代に憧れ、そのことばかりを考えている夢想家の青年が巻き起こす騒動を描くラブ・コメディ。彼は、自分の住む町の指導者が、悪名高いローマ皇帝バレリアスと同じくらいの悪事を行っていることを知り、なんとかローマ時代に戻って自分のようなタイプの男に会いたいと考える。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いつか読書する日", "いつか読書する日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "「独立少年合唱団」でベルリン映画祭新人監督賞を受賞した緒方明監督による、大人の切ない恋愛ドラマ。青春時代、引き裂かれるように別れてしまった中年男女の再燃した愛の行方を描く。恋愛に不器用な50代独身のヒロインを心の機微までしっかりと表現した田中裕子の熱演が印象深い。長崎在住、50歳で独身の美奈子は、わけあって別れた槐多を密かに思い続けていた。そんな折、彼女はがんで床に伏す槐多の妻、容子に呼び出される。", "2005-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丹下左膳 妖刀濡れ燕", "丹下左膳 妖刀濡れ燕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "元ネタは、林不忘の『大岡政談』に始まる丹下左膳ものだが、この頃になると左膳のキャラクターのみが一人歩きしている。飲み代のために道場破りに精を出す左膳のもとに、藩金護送に絡んだ難事件が転がり込む。当時の名子役・松島トモ子のチョビ安がカワイイ。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kindergarten Cop", "キンダガートン・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "麻薬犯罪を追うロス警察の鬼刑事キンブル。組織のボスの息子が通う幼稚園に潜入捜査することになり、保父になりすますが、子供相手に四苦八苦……。シュワルツェネッガーと子供たちが次第に仲良くなっていく様子がほのぼのと描かれているアクション・コメディ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACKTHUNDER", "ステルスVSステルス　エグゼクティブコマンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "エアバトル・アクション「エグゼクティブコマンド」の続編。アメリカ国防省が開発したレーダー探知されない最新鋭ステルス機“ノヴァ”がテスト飛行中、テロリストに奪われた。この追跡不可能な戦闘機を利用してパリ壊滅を企むテロ一味に、海軍のエリート・パイロット、コナーズが戦いを挑む。スリリングな空中戦が見どころ。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("3年目の浮気", "3年目の浮気", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "結婚3年目の夫婦に訪れた危機を描いたエロティックドラマ。結婚して3年になる秀樹と京子は、互いに浮気もせず真面目に夫婦生活を営んできた。そんなある日、秀樹に同僚の知美が急接近してきて、2人は不倫関係に。突然帰りが遅くなった秀樹に疑いを抱きはじめた京子は、寂しさを紛らわせるためにパブ勤めを決心するが……。「家族ゲーム」の森田芳光が脚本を手がけ、「犯され志願」の中原俊がメガホンを取る。", "1983-07-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WITH　OR　WITHOUT　YOU", "いつまでも二人で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "結婚５年目の夫婦の前に、妻の初恋の人が現れた。彼はロマンティックなフランス人。夫は嫉妬し、妻の心は揺れ動く。二人の愛の行方は……。「ウェルカム・トゥ・サラエボ」「ひかりのまち」のM・ウィンターボトムが、結婚の夢と現実を描いたロマンティック・コメディ。随所にF・トリュフォーさながらのコミカルな演出が施され、1960年代のフランス映画を彷彿させる仕上がりになっている。原題でもあるU2のラブソングが劇中にも使用され、観る者の感情を煽っている。", "1999-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("麦田", "麦田", "", "", "http://image.eiga.k-img.com/images/movie/54972/315x210.jpg?4598", 107, "「哀戀花火」のハー・ピン監督が、「墨攻」のファン・ビンビンを主演に描く歴史ドラマ。戦国時代の中国、全ての男が戦へと駆りだされて女だけが取り残された趙国の町に、敵国・秦から逃げ出してきた2人の兵士が現われる。生き延びるために趙国人のフリをして趙国が勝利したと偽った彼らは女たちから歓迎されるが、武将の妻・驪だけは彼らの話を疑っていた。そんな中、町が冷酷非道な盗賊たちによって襲撃され……。", "2009-10-18", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Barry Lyndon", "バリー・リンドン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 185, "　18世紀のアイルランドを舞台に、野心に燃える若者の半生を描いた歴史ロマン。爵位に執着するバリー役は、「ペーパー・ムーン」「ある愛の詩」のライアン・オニール。彼との出会いで人生が大きく変化する伯爵夫人を、モデルから女優に転向したマリサ・ベレンソンが演じる。アカデミー賞作品賞をはじめ7部門にノミネートされ、撮影、衣装デザイン、美術監督、編曲の4部門を受賞。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rise of the Guardians", "不思議の国のガーディアンズ（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ギレルモ・デル・トロが製作総指揮を手がける、ドリームワークス・アニメーションのファンタジーアニメ。", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・高校生ブルース", "新・高校生ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ひと月後の学園祭までに童貞を捨てようと奮闘する３人の高校生を中心に描く青春学園ドラマ。他の二人が妖艶な中年女やキャバレーで働く薄幸の少女と体験を済ませたのに、椎名だけは同級生の京子の前に手も足も出ない。関根の裸身がまぶしい。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOS　HIJOS　DEL　ULTIMO　JARDIN", "最後の庭の息子たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "アンデス先住民の文化と世界を描き続けてきたボリビアの映画製作グループ、ウカマウ集団が、ボリビアの都市に暮らす若者たちに着目。混沌とした社会の中で正義感に駆り立てられる青年、展望を持てずに快楽主義に走る青年など、さまざまな青春像を真正面から見つめた社会派ドラマだ。社会情勢が悪化するボリビアの首都ラパス。貧しい人々の窮状に心を痛めた青年は、汚職議員の家から金を盗み、先住民に届けるが……。", "2005-06-17", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アシュラ", "アシュラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE KLAGE DER KAISERIN", "嘆きの皇太后", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "泥だらけの原っぱを歩くコーラスガール、雪の中の天使、雨の中のカップル、呼び笛を持った女、水中のバレリーナ、恐ろしい森の中の孤独な女……。様々なイマージュが唐突に現れては消えていき、また現れる。それらのシークエンスは、身体によって何物かを表現しようとするダンスであると同時に、映画の身振りでもあるといえよう。試行錯誤しながらも前へ、自らが有り得るべき姿へと歩を進めるダンサーたち。そしてそれらの身体を捉えた生々しいカメラの息遣いが素晴らしい。ヴッパタール・ダンスシアターの芸術監督として来日公演も多い、バレエ界のカリスマ、P・バウシュが初めて監督に挑んだ映画である。", "1989-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京ゴミ女", "東京ゴミ女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "日本映画界の俊英６人がデジカムを用いてそれぞれの愛の形を描き上げる企画“ラブ・シネマ”の第１作。同じマンションに暮らすミュージシャンに片想いするウェイトレスの女の子が、彼のすべてを知るために、ゴミの日に彼が出すゴミを漁りまくるという内容。こんなことを男がやったらたちまちストーカー視されるところだが、中村麻美扮する不器用なヒロインのキュートさが際立ち、奇妙にリアルで切ない青春映画に仕上がっている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("1000年の山古志", "1000年の山古志", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "2004年10月の新潟県中越地震で全村崩壊の被害にあった新潟県山古志村にスポットを当て、再生への道のりを追ったドキュメンタリー。監督は、前作「掘るまいか　手掘り中山隧道の記録」でも山古志村を題材に描いたドキュメンタリー作家・橋本信一。仮設住宅への避難を余儀なくされた住人たちが徐々に村へと戻り、住居や田畑の復旧に取り組む姿を通し、太古の昔から防災列島で生きぬいてきた日本人の心の根源に迫る。", "2009-10-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大地の子守歌", "大地の子守歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "売春宿に売られた少女の苛酷な運命と、流転する生のたくましさを描く。一貫して力強い女を描き続けてきた増村保造にピッタリの素材である。四国の山奥で祖母と二人で暮らしていた少女りんは、祖母の死後、瀬戸内海の小さな島に売春婦として売られていく。13歳だったりんは初めのうちは下働きをさせられていたが、初潮を迎えるとすぐに客をとらされた。狂ったように働いたりんは、視神経を冒され盲目になるが、それでも働き続ける……。当時、デビュー２作目の新人だった原田美枝子は、この作品で演技開眼したといっても過言ではないくらいの熱演。時としてオーバーに聞こえるセリフ回しも、逆に生命の息吹きを感じさせるほどだ。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ong-Bak 2", "マッハ！弐", "", "", "http://image.eiga.k-img.com/images/movie/55082/315x210.jpg?4071", 98, "世界中で大ヒットを記録した、タイのムエタイ・アクション映画「マッハ！」の続編。前作の主演トニー・ジャーが、監督と主演を務める。15世紀頃の、アユタヤ王国による侵略が進むタイ。家臣ラーチャセーナによるクーデターで国王とその妻が殺害されるが、息子のティンは悪名高い山賊「ガルーダの翼峰」に救出され、彼らの暮らす山奥の村で生活を始める。そこでムエタイ、剣術、カンフーなどさまざまな格闘技の修行を積み、最強戦士へと成長したティンは、ラーチャセーナへの復讐を誓う。", "2010-01-09", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Mushroom Club", "マッシュルーム・クラブ", "", "", "http://image.eiga.k-img.com/images/movie/34312/315x210.jpg?1688", 35, "日系アメリカ人のスティーブン・オカザキ監督が、原爆投下から60年を経た2005年の広島の姿を綴ったドキュメンタリー。「はだしのゲン」の作者として知られる中沢啓治や、原爆で亡くなった人々のボタンを河原で拾い続ける佐伯敏子、さらに母胎内で被爆した原爆小頭症患者たちを紹介する一方、原爆の記憶が風化しつつある広島の現状を映し出す。2005年度のアカデミー賞では短編ドキュメンタリー部門にノミネートされた。", "2007-11-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAKE PLACID", "U.M.A／レイク・プラシッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "メーン州北部の人里離れた湖でダイビング中の男が胴体を真っ二つに食いちぎられる事件が起きた。現地に飛んだ古生物学者ケリーは現地の狩猟監視官ジャックらと調査を続けるうちに、敵は体長10メートル、体重５トンにも及ぶ巨大なワニらしいことを突き止める。そこに大富豪のワニ愛好家ヘクターが現れ、この巨大ワニの生け捕り作戦を始めるが……。B・フォンダ、B・プルマンなど豪華なキャスティングでおくるパニック・ホラー。巨大ワニと人間の対決が、ユーモアを散りばめながらスリリングに展開していく。ちなみに邦題にあるU.M.Aとは“Unidentified Mysterious Anima”（未確認生物）の意味。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あゝ独身", "あゝ独身", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "吉本興業の当時の若手喜劇人のオールキャストによるコメディ映画。社長夫人のお抱え運転手、バーテン、アパート管理人、ラーメン屋の出前持ち、社長令息といった面々があの手この手を使ってガール・ハントに精を出す青春群像。やす・きよ、三枝をはじめとする、笑いの大御所たちの若き日の姿にご注目。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("川の底からこんにちは", "川の底からこんにちは", "", "", "http://image.eiga.k-img.com/images/movie/55222/315x210.jpg?1355", 112, "上京して5年目のOL佐和子は、目標もない自堕落な生活を送っていた。ある日、父親が末期がんのため余命わずかだという知らせが入り、一人娘の佐和子が実家のしじみ工場の跡を継ぐことになる。しかし工場は倒産寸前で、労働者の中年女性たちからはいびられる毎日。追い込まれた佐和子は、生まれて初めて自分の人生を見つめ直すことになる。新人監督の登竜門「ぴあフィルムフェスティバル」でグランプリを受賞した石井裕也の商業映画デビュー作。主演は「愛のむきだし」の満島ひかり。", "2010-05-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まむしの兄弟・刑務所暮し四年半", "まむしの兄弟・刑務所暮し四年半", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ムショを出たゴロ政は勝と神戸へ繰り出し、バーのママ・倉石優子に一目惚れする。あげくに、彼女を暴力的に犯して娘を生ませた芝江組の組長邸へパトカーで突っ込んで大暴れ。またまた、ムショへ逆戻りする。三益愛子が得意の“日本の母”を演じる。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rompecabezas", "幸せパズル", "", "", "http://image.eiga.k-img.com/images/movie/56492/315x210.jpg?1568", 90, "夫と息子の幸せが生きがいの専業主婦マリアは、50歳の誕生日にプレゼントされたジグソーパズルに思いがけない才能を発揮。大富豪の独身紳士ロベルトにもその才能を見初められ、一緒にパズル大会の世界選手権出場を目指すことになる。マリアは家族に内緒でロベルトの邸宅に通い、パズルゲームの規則を学んでいくが……。ごく平凡な女性が、夢中になるものを見つけたことで新しい自分を発見していく姿を描いたヒューマンドラマ。第60回ベルリン国際映画祭のコンペティション部門に出品された。", "2011-10-01", "22,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lemony Snicket's a Series of Unfortunate Events", "レモニー・スニケットの世にも不幸せな物語", "", "", "http://image.eiga.k-img.com/images/movie/1492/315x210.jpg?4107", 109, "世界的ベストセラーの児童文学「世にも不幸な出来事」シリーズの最初の3作を、「キャスパー」のブラッド・シルバーリング監督が映画化。孤児となったボードレール家の3人の子供たち、14歳の発明家バイオレット、その弟の読書家クラウス、なににでも噛みつく幼児サニー。3人は知恵と勇気で、彼らの遺産を狙う親戚オラフ伯爵の陰謀に対抗していく。ジム・キャリーが原作の挿し絵そっくりの特殊メイクでオラフ伯爵役を怪演。", "2005-05-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COVERT ACTION／OPERATION RED STAR", "レッド・コマンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1968年、ベトナム戦争中のカンボジア。アメリカ軍で、ある極秘指令が下った。カンボジア駐屯中のソ連軍ゾレンコフ将軍を秘かに亡命させようというものだった。ベトコンとソ連軍最強部隊を相手に、アメリカ軍特殊部隊の精鋭８人が困難な任務に向かう。「ダーティ・ファイター／燃えよ鉄拳」でクリント・イーストウッドとの殴り合いを演じたW・スミスが堂々の貫禄でソ連軍将軍を演じている。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THIS　LAND　IS　MINE", "自由への闘い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "画家ルノワールの息子にして“ヌーヴェル・ヴァーグ”の父と称されるフランス映画の巨匠J・ルノワール監督が、第二次世界大戦中にアメリカへ渡って撮った異色の戦争ヒューマン・ドラマ。ドイツ軍に占領されたある都市が舞台。抵抗運動の参加による処刑をきっかけに釈放された教師の姿を映し出す。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あそこの席", "あそこの席", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "中高校生を中心に絶大な支持を受ける若手作家、山田悠介の同名小説を映画化したホラー。いわくつきの席に座ってしまった少女がおぞましい恐怖に巻き込まれる。第8回全日本国民的美少女コンテストでグランプリに輝いた阪田瑞穂が映画初主演。恐れ慄くヒロインを熱演する彼女の頑張りが光る。ある学校に転校した加奈は、そこでわざと空けられた席に座ることになる。その座席は座った者を必ず不幸に陥れる呪われた席だった。", "2005-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAUSTRECHT DER FREIHEIT", "自由の代償", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ナイーブで気の弱いホモセクシャルの男フランツは、友人の紹介で上流のホモセクシャルのサークルに参加することになる。そこで企業家の息子オイゲンに知り会った彼はお互いに愛し合い、一緒に暮らすことになる。突然自由と富を手にしたフランツだったが……。当時“ニュー・ジャーマン・シネマ”とくくられた作家の一人、ファスビンダー監督自ら主演し、ドイツ国内はもとより外国でも高い評価を得た異色作。", "1975-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DES GENS SANS IMPORTANCE", "ヘッドライト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "中年トラック・ドライバーと、ドライブインのウェイトレスとの哀しい恋の物語。平凡で陰気な生活から脱出したいという彼の気持ちと、彼女の一途な愛情から二人は結ばれるが、彼の子供を宿した彼女は医者の手当ての甲斐もなく死んでいく。しっとりとした白黒の映像が出色。", "1955-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HUM　DIL　DE　CHUKE　SANAM", "ミモラ～心のままに", "", "", "http://ec1.images-amazon.com/images/G/09/nav2/dp/no-image-no-ciu.gif", 187, "インドからイタリアへ。二人の男性への愛に悩む女性の心の変化を綴るラブ・ロマン。18歳のナンディニの前に、陽気な青年サミルが現れた。著名な古典声楽家の父の元にインド音楽を学びにやって来たサミルとナンディニは惹かれ合うが、父の反対に合い、サミルはイタリアへ帰国、ナンディニは青年弁護士ヴァンラジと結婚。だがサミルを忘れられないナンディニに心を痛めたヴァンラジは、彼女を連れイタリアへ旅立つ。みずみずしい若者のラブ・ストーリーの前半から、後半は夫婦の絆と葛藤を描く、しっとりとした大人の物語へ変化。インド映画ならではの豪華絢爛ミュージカル・シーンも美しい。", "2002-04-27", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROOMMATES", "最高のルームメイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "並外れたバイタリティーと勤勉さで人生を切り開いてきた頑固者のパン職人ロッキー。そんな祖父に一人前になるまで育てられた孫のマイケルは、医大に入学してコロンバスに移り住む。数年後、アパートの取り壊しに抵抗するロッキーを引き取って、再び一緒に暮らし始めるが、ロッキーは孫が大人になっても口を出さずにはいられなかった。二人の間に険悪なムードが流れる。やがて、祖父がその役目を終える頃、ようやくマイケルは一人立ちをする……。1996年度アカデミー賞メイクアップ賞にノミネートされた、75歳から107歳までを演じるP・フォークのメイクは必見。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AMERICAN PRESIDENT", "アメリカン・プレジデント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "一人の男として恋に落ちてしまったアメリカ大統領の姿を描いたラブ・ロマンス。分刻みで仕事をこなし、世界中からその行動が注目されるアメリカ大統領。アメリカ大統領アンドリュー・シェファードは、思わぬことから環境問題のロビイスト、シドニー・ウェイドと知り合い恋に落ちる。ブレーンたちの心配をよそにデートを重ねる二人だが、折しも大統領選が目前。政敵のボブ・ラムソンからスキャンダルとして攻撃され、シェファードの支持率はみるみるうちに低下する……。様々なジャンルで秀作を手掛けてきたR・ライナー監督が、豪華キャストを据え、エンターテインメントの新境地に取り組んだ作品。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEVILIN", "ガンドッグ 復讐の導火線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ニューヨーク市長候補が情婦とともに、ホテルの一室で射殺された。刑事フランク・デブリンは、アル中だった過去が災いして、この殺人事件の第一容疑者に仕立てられる。頼れるのは、恋人でありテレビ局のニュースレポーターのアイリーンと同僚サム。フランクは二人の力を借りて、政治的陰謀と犯罪を暴いていく。「ダイ・ハード」の原作者ロデリック・ソープの小説をもとにしたポリス・アクション。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("行旅死亡人", "行旅死亡人", "", "", "http://image.eiga.k-img.com/images/movie/54722/315x210.jpg?4233", 113, "ノンフィクション作家を目指すミサキは、成果も挙げられないままアルバイトに追われる日々を送っていた。そんなある日、ミサキの名前を名乗った女が重病で入院したという奇妙な連絡が入る。ミサキは友人のアスカとともに女の入院している病院へ向かうが……。日本ジャーナリスト専門学校が製作・配給を担当し、同校の講師・井土紀州が監督・脚本を務める。", "2009-11-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fargo", "ファーゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "コーエン兄弟が贈る、ブラックユーモアをちりばめた異色のクライム・サスペンス。厚い雪に覆われるミネソタ州ファーゴ。多額の借金を抱える自動車ディーラーのジェリーは、妻ジーンを偽装誘拐して彼女の裕福な父親から身代金をだまし取ろうと企てる。ところが誘拐を請け負った2人の男が警官と目撃者を射殺してしまい、事件は思わぬ方向へ発展していく……。アカデミー脚本賞、主演女優賞をはじめ、多数の映画賞を獲得した話題作。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不貞の季節", "不貞の季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "大杉が初期のピンク映画以来久しぶりに主演した、一人の男の物悲しい愛憎劇。鬱屈した思いを抱えながら、悶々とSM小説を書き続ける中年男が、妻と編集者の肉体関係を知り、さらに身悶えていく様を描く。長回し撮影を多用した廣木隆一監督のけだるい映像世界の中で、大杉はどこか滑稽な主人公の有様をひょうひょうと見せている。原作は、団鬼六の私小説を思わせる同名短編。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bitch Slap", "ビッチ・スラップ　危険な天使たち", "", "", "http://image.eiga.k-img.com/images/movie/55672/315x210.jpg?2300", 0, "3人のセクシー美女が砂漠を舞台に暴れまくるバイオレンス・アクション。B級映画の帝王ロジャー・コーマンに才能を見出されたリック・ジェイコブソン監督が、50～70年代のカルト映画へのオマージュたっぷりに描く。やみのフィクサーが隠した2億ドルのダイヤを手に入れるため、砂漠へとやって来た3人の悪女。襲いかかってくる凶暴な男たちを次々と血祭りにあげながらも、実はそれぞれ重大な秘密があった……。", "2010-10-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOHN AND MARY", "ジョンとメリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ある朝、同じベッドの中で目を覚ましたジョンとメリー。昨晩バーで知り合った二人は、酔った勢いで一夜をともにしたらしい。お互いのことを何も知らないままぎこちない時間が過ぎていくが……。短い時間の中に変化する男女の感情を淡々とした演出で描き、静かな感動を呼ぶラブ・ストーリーの隠れた名作。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女帝 春日局", "女帝 春日局", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "徳川300年の歴史の中で初めて天皇から“局”の称号を授かり、一代で“女帝”の名を欲しいままにした、春日局の生涯を描いた大作。権力闘争の真っ只中をしぶとく這い上がっていく一人の女のすさまじいまでの生きざまを十朱幸代が熱演。当時の資料をもとに見事に再現された江戸大奥のセットが圧巻だ。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA RAGAZZA DI BUBE", "ブーベの恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "第二次大戦下のレジスタンスを背景としたリリシズムあふれるメロドラマの名作。大戦末期、パルチザンのブーベは、憲兵の息子を殺したことから追われる身となり、14年の刑を宣告される。ブーベをひたむきに愛するマーラは、14年間刑務所に通い続ける決心をする。ルスティケリのテーマ音楽が大ヒットした。", "1963-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わるいやつら", "わるいやつら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "女を手玉にとり、彼女らから金を巻き上げては病院経営の赤字を埋め、頼まれてそれぞれの夫を巧妙に薬殺していた病院長が、美貌の新進デザイナーに目がくらみ、彼女をも手玉にとろうとする。だが、彼を上回る悪女たちに操られ犯罪の道をつき進む……。女の恐ろしさを改めて思い知らされる、清張＝野村監督コンビによるサスペンス。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・三等重役／当るも八卦の巻", "新・三等重役／当るも八卦の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "世界電機が広告のモデルに採用して大好評だったミス・ワールド電機・秋山晴子が失踪する。さっそく重役会議を開く。そこに大株主の宮口会長夫人が乗り込み、武田宇宙先生の八卦によると会社に難事があるに違いないと指摘する。若林映子の出世作。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トウキョウソナタ", "トウキョウソナタ", "", "", "http://image.eiga.k-img.com/images/movie/53692/315x210.jpg?3862", 119, "「アカルイミライ」「LOFT」の黒沢清監督が、東京のごく普通の家庭の崩壊と再生を描いたホームドラマ。主演は香川照之、小泉今日子。小学6年生の次男・健二は父に反対されているピアノをこっそり習っている。しかし父親はリストラされたことを家族に打ち明けられずにおり、兄は米軍に入隊しようとしているなど、やがて家族全員に秘密があることが明らかになっていく……。第61回カンヌ映画祭では、ある視点部門審査員賞を受賞した。", "2008-09-27", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("投名状", "ウォーロード　男たちの誓い", "", "", "http://image.eiga.k-img.com/images/movie/54292/315x210.jpg?4441", 113, "ジェット・リー、アンディ・ラウ、金城武が義兄弟を演じる歴史大作アクション。監督は「ウィンター・ソング」のピーター・チャン。アヘン戦争で腐敗した清朝の圧政が続く19世紀末期、自分の部隊を全滅に追いやられたバン（ジェット・リー）は、失意の中で荒野を彷徨っているところで、盗賊団のアルフ（アンディ・ラウ）とウーヤン（金城武）と出会う。国の秩序と平和のため、バンは運命を共にする証として2人と義兄弟の契り“投名状”を結ぶが……。", "2009-05-08", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MOUSE THAT ROARED", "ピーター・セラーズのマウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "名優P・セラーズが一人３役を演じる、抱腹絶倒のコメディ映画。南ヨーロッパの小国フェンウィック公国は、名産のワインをアメリカに輸出し、大きな利益を上げていた。が、ワインの輸出がダメになり国は破産。困った首相が案じた一計とは……。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トラック野郎・望郷一番星", "トラック野郎・望郷一番星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "北海道へ向かうカーフェリーの中で出会った美しき牧場経営者・亜希子に一目惚れの桃次郎。彼女の牧場で重病の小馬を助けて信頼を厚くするが、亜希子にはすでに婚約者がいた……。金造の子供がついに10人になる。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CHAT ET LA SOURIS", "“猫”警部事件簿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "大富豪のリシャール氏が射殺死体となって発見され、名画のコレクションもすべてなくなっていた。この事件の謎の解明に“猫”警部が乗り出すが、怪しいと思われる人間には皆アリバイがあり、謎も深まっていく。C・ルルーシュがしゃれたタッチで描いたサスペンス・ドラマ。", "1975-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RE−ANIMATOR", "ZOMBIO 死霊のしたたり", "", "", "http://ecx.images-amazon.com/images/I/51MkLNUpAqL._SL210_.jpg", 89, "“死体蘇生”を可能にする薬を研究中の学生が実験を重ねるうち、よみがえった死体が暴れ出して珍騒動が起きる。怖さの中にもコミカルな場面がいっぱいのスプラッタ・ホラー。アヴォリアッツ国際ファンタスティック映画祭最優秀恐怖映画賞を受賞。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hannibal Rising", "ハンニバル・ライジング", "", "", "http://image.eiga.k-img.com/images/movie/33932/315x210.jpg?1288", 121, "「羊たちの沈黙」「ハンニバル」などで世界中を震えあがらせた天才シリアルキラー、ハンニバル・レクター誕生の秘密に迫るシリーズ第4弾。原作者のトマス・ハリスが初めて自ら脚本を手掛け、頭脳明晰にして残忍な殺人を繰り返すレクター博士の人格がいかにして形成されたのかを描き出す。レクター博士の青年期を演じるのは「ロング・エンゲージメント」のギャスパー・ウリエル。監督は「真珠の耳飾りの少女」のピーター・ウェーバー。", "2007-04-21", "1,2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACES GO PLACES", "悪漢探偵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "盗まれたダイヤをめぐって香港のギャング、強盗、刑事が入り乱れる痛快ドタバタ・アクション。スパイダーマン、怪盗白手袋、刑事コジャック、マッドマックスなどの人物が登場。“007”まがいの小道具、カー・アクション、秘密兵器も続出。公開当時は香港映画史上空前の興収記録を樹立した。", "1982-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUESS WHO", "ゲス・フー／招かれざる恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "往年の名作「招かれざる客」を基に、人種間の恋愛の難しさを描いたコメディ。「バタフライ・エフェクト」のアシュトン・カッチャーが、黒人である恋人の父に手を焼く青年役を、体を張ったドタバタを見せつつ妙演する。タンゴ・ダンスやゴーカート・レースのコミカルな描写にも注目。黒人銀行員パーシーは娘の恋人サイモンが白人青年だと知って交際に猛反対。彼はパーシーに気に入ってもらおうと悪戦苦闘するが……。", "2005-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Candy", "キャンディ", "", "", "http://image.eiga.k-img.com/images/movie/34202/315x210.jpg?1610", 108, "オーストラリアの詩人ルーク・デイビスによる自伝的小説を基に、自堕落な生活を送るカップルの愛を描いたラブ・ストーリー。主演は「ブロークバック・マウンテン」で注目を集めたヒース・レジャーと、「プロヴァンスの贈りもの」のアビー・コーニッシュ。詩人志望の青年ダンは、画家を目指すキャンディと恋に落ちる。激しく愛し合いながらもドラッグに溺れていく2人。そんな中、キャンディの妊娠が発覚し……。監督はオーストラリアを起点に世界的に活躍する現代演劇界の鬼才ニール・アームフィールド。", "2007-09-22", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUEENシネマライブ in 六本木", "QUEENシネマライブ in 六本木", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "1986年に行われた英ロックバンド「クイーン」オリジナルメンバーでの最後のライブツアー「マジック・ツアー」から、ハイライトとなった7月11日、12日のロンドン・ウェンブリー・スタジアムでのパフォーマンスを完全収録。バンド結成40年、ボーカルのフレディ・マーキュリー没後20年となる2011年、東京・シネマート六本木にて劇場上映。", "2011-04-02", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒロシマ・ナガサキ　ダウンロード", "ヒロシマ・ナガサキ　ダウンロード", "", "", "http://image.eiga.k-img.com/images/movie/56862/315x210.jpg?2215", 73, "2009年、自分探しを続ける青年2人が、アメリカ在住の原爆被害者を訪ねる旅の姿を追ったドキュメンタリー。終戦後、かつての「敵国」であるアメリカに移住し、原爆の記憶や後遺症への恐怖に怯えながらもアメリカで暮らし続けている被爆者たちがいる。戦争を知らない世代の若者たちは、広大なアメリカを旅しながら被爆者から何を受け継ぎ、伝えていくべきなのかを考えていく。", "2011-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRANSYLVANIA 6−5000", "突撃バンパイア・レポーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "J・ゴールドブラムとG・デイヴィスが、レポーターと女ドラキュラに扮したコメディ。グレン・ミラーのサウンドにのせて、トランシルバニアを舞台にバンパイア騒動がにぎやかに展開。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　空の境界　第四章　伽藍の洞", "劇場版　空の境界　第四章　伽藍の洞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "奈須きのこの同名小説を原作に、万物の死を見ることのできる能力「直視の魔眼」を持つ少女・両儀式の苦悩と戦いを描いた人気アニメ劇場版「空の境界」7部作の第4章。不慮の事故から2年。眠り続けていた両儀式は目を覚ますが、自身の中にいたもうひとりの自分「織」がいなくなり、その一方で万物の死が見えてしまう「直視の魔眼」を得たことに気がつく。", "2008-05-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TORQUE", "トルク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ミュージック・ビデオ界出身のJ・カーンが、長編映画初監督で撮り上げた痛快バイク・アクション。殺人の濡れ衣を着せられた男が自らの汚名を晴らすため、バイカーギャング、警察、FBIと繰り広げる壮絶なチェイス・シーンが圧巻だ。ヘリコプター用エンジンを搭載した“Y２K”と呼ばれるマシンをはじめ、マニア垂涎のバイクの数々も見逃せない。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DELTA FORCE COMMANDO ２", "デルタ・フォース・コマンド２ 暁の奪還", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "デルタ・フォースを辞め、一匹狼になったブレッドのもとに輸送中の核兵器が奪われたという情報が入る。奪ったのは仲間だったシャロン。ブレッドはシャロンと接触、彼女が米軍の権力者マッカラン将軍がひそかに仕組んだ戦争勃発計画に利用されていることを知る。特殊部隊デルタ・フォースの活躍を描くミリタリー・アクション第２作。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENTERTAING ANGELS：THE DOROTHY DAY STORY", "天使の旅立ち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "福祉活動に人生を捧げた実在の女性ドロシー・デイの人間像を綴る伝記ヒューマン・ドラマ。不況の嵐が吹き荒れる1920年代アメリカで、わずかな協力者とともに貧しい人々に希望を与えるために情熱を燃やすヒロインを、「きっと忘れない」のM・ケリーが熱演する。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パラダイス・キス", "パラダイス・キス", "", "", "http://image.eiga.k-img.com/images/movie/55692/315x210.jpg?3169", 115, "「NANA」で知られる矢沢あいが手がけた人気漫画を、北川景子主演で実写映画化する。ファッション誌「Zipper」で1999～2003年に連載され、累計発行部数500万部を超える大ヒットを記録。有名進学校に通う高校3年生で、受験勉強に追われる主人公の早坂紫（ゆかり）が、服飾専門学校生・小泉ジョージ（向井理）らの学園祭でファッションモデルを務めることなり、夢を追いかけるジョージと仲間たちの姿に自らも歩む道を見出していく姿を描く。", "2011-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わたしの見島", "わたしの見島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「全身小説家」以来となる原一男の監督作。監督自身が主宰する“CINEMA塾”夏季合宿中に制作されたもので、山口県萩市の離島、見島にカメラを向けている。過疎の島で暮らす雑多な人々に“地元観”をまるごと語ってもらおうとする内容で、原としてはかなりストレートなドキュメンタリー。高校進学で島を離れる中学生を一列に並ばせ、順々に彼らの将来の夢を語らせる長回し場面などに、従来の作品に通底する映像のうねりが感じられる。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボールZ　この世で一番強いヤツ", "ドラゴンボールZ　この世で一番強いヤツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "劇場版シリーズ第5作。悟空をはじめ、ピッコロ、ブルマ、亀仙人、悟飯らのレギュラー・キャラクターに加え、強敵Dr.ウィローが登場する。雪と氷壁に覆われた山奥を舞台に、悟空とその仲間たちが、悪の権化Dr.ウィローとその配下のならず者たちを相手に、凄まじい戦いを繰り広げていく。DVDは『DRAGON BOX THE MOVIES』に収録。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEDTIME STORY", "寝室ものがたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "M・ブランドとD・ニーヴンという、異色の顔合わせによるエロティック・コメディ。女にはモテモテ、しかもそれだけが生きがいというフレッドとローレンス。互いにライバル意識をむき出しにする二人は、相手をどうにかして蹴落とそうと、アノ手コノ手の大作戦を開始する……。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Public Housing", "パブリック・ハウジング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 195, "", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("看車人的七月", "胡同（フートン）愛歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "再開発の影で姿を消しつつある北京の細い路地・胡同を舞台に、ひたむきに生きる庶民たちの姿をあたたかい眼差しでつづったヒューマンドラマ。駐車場の案内係として働く杜は、高校生の息子・小宇とともに、胡同でつつましくも平穏な生活を送っていた。そんなある日、杜は再婚を決めていた相手・小宋と前夫・劉三の離婚が成立していないことを知る。さらに、劉三は2人の再婚を阻止しようと嫌がらせを繰りかえすようになり……。", "2007-04-28", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレヨンしんちゃん アクション仮面VSハイグレ魔王", "クレヨンしんちゃん アクション仮面VSハイグレ魔王", "", "", "http://ecx.images-amazon.com/images/I/615cqYKyFeL._SL210_.jpg", 93, "人気TVアニメ『クレヨンしんちゃん』の記念すべき劇場版第１作。しんのすけは、お菓子で当てたカードを使い、アクション仮面のアトラクションハウスにやってくる。ところが、乗り物に乗ったまま異次元へタイムスリップ。そこはオカマのハイレグ魔王が君臨する恐ろしい場所だった。アクション戦士となったしんのすけは、アクション仮面とともに戦いに挑む。", "1993-07-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀座愛物語　クラブアンダルシア", "銀座愛物語　クラブアンダルシア", "", "", "http://image.eiga.k-img.com/images/movie/54332/315x210.jpg?4084", 0, "人気TVドラマ「夜王」「女帝」などの原作として知られる倉科遼が、実在する銀座のクラブオーナーをモデルにした「クラブアンダルシア」を松方弘樹主演で映画化。銀座の高級クラブ「アンダルシア」の支配人・奥村が、枕営業をするホステスや子持ちで生活に追われるホステスなど、銀座ホステスたちの様々な愛のかたちを見守る。", "2009-01-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAELSTRO¨M", "渦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "現代社会を生きる女性の孤独と苦しみを悪夢のような奇抜な映像と不思議なストーリー展開で語る、転落と再生のドラマ。ヴィヴィアンは大女優の娘で雑誌などでも注目されるセレブリティの一人。彼女は飲酒運転で家に戻る途中、誤って男性を轢いてしまう。良心の呵責に苛まれる彼女は、罪の償いを考える。ヴィヴィアンが運命的に出会う若者エヴィアンを演じたJ・N・ベローのセクシーなルックスが目を引く。カナダ・アカデミー賞主要５部門、モントリオール国際映画祭最優秀観客賞、最優秀芸術貢献賞、トロント国際映画祭審査員特別賞受賞。ビデオタイトルは「渦／官能の悪夢」。", "2000-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Charlie and the Chocolate Factory", "チャーリーとチョコレート工場", "", "", "http://image.eiga.k-img.com/images/movie/1032/315x210.jpg?2452", 115, "ティム・バートン監督がロアルド・ダールの児童小説「チョコレート工場の秘密」を、盟友ダニー・エルフマンの音楽による歌と踊り満載で映画化。ジョニー・デップ扮する怪しいチョコレート工場主ウィリー・ウォンカが、黄金のチケット入りのチョコレートを買った子供5人を工場見学にご招待。貧しいが純真な少年チャーリーはじめチケットを手に入れた5人の少年少女たちは、チョコレート工場に招かれて奇妙な体験をしていく。", "2005-09-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEE SPOT RUN", "スポット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ジェイムズ君はシングルマザーのママに片思いしている郵便配達のお兄ちゃんと留守番することに。ところがお兄ちゃんと一緒に乗った郵便配達車に、なぜかブサイクな犬が乱入。実はこの犬、ある事情からマフィアに命を狙われていた。FBI捜査犬と少年の冒険劇を描いたドタバタ・コメディ。普段は冷静でコワ面なのに、実は自分の育てた捜査犬を溺愛しているFBI訓練士を「グリーンマイル」の巨漢M・C・ダンカンが熱演し、笑いを誘う。劇場公開時には“キッズ対応はじめて字幕”と銘打ち、初めて字幕版映画を観る小学生低学年向けに字幕文字を大きくするなど、様々な工夫がなされた。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フライング☆ラビッツ", "フライング☆ラビッツ", "", "", "http://image.eiga.k-img.com/images/movie/53332/315x210.jpg?2233", 120, "夢と希望を抱いてフライトアテンダントになった早瀬ゆかりは、ひょんなミスから会社のバスケットボールチーム「ラビッツ」に入部する羽目に。過酷なバスケの練習に加え、仕事の研修では教官になじられる日々。さらに、彼氏が実家に帰ってしまって遠距離恋愛状態に……。原作は実在のチーム「JALラビッツ」をモデルにした深田祐介の「翔べ！ラビッツ」で、若手女優が体当たりで演じるバスケットボールシーンには本物のJALラビッツの選手も出演。", "2008-09-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BASKET CASE2", "バスケットケース2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "蛙男や鼠人間など、社会から“怪物”呼ばわりされ、サーカスの見せ物となっていたフリークスたちが形成するコミュニティーを舞台に、ノーマルな肉体を持つがゆえに悩む主人公の姿を描く。主人公の感情変化を巧みに捉えた鬼才ヘネンロッターの活躍ぶりが光るホラー。前作から８年、今回は兄・ベリアルの凶行よりも、弟・ドゥエインの普通の社会に復帰したいという精神的葛藤に焦点が当てられている。ベリアルの恋人(？)も登場。DVDは「バスケット・ケースBOX」に収録。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪名", "悪名", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "当時の『週刊朝日』に連載中だった今東光の同名小説を、勝新太郎と田宮二郎のコンビで映画化。河内の農民のせがれ・朝吉は無類の暴れん坊で、松島遊廓で土地のヤクザ・モートルの貞と対決し、ぶちのめす。貞は朝吉を親分に。二人は足抜きをして因島へ売られた琴糸を救うべく島へ乗り込む。そこでピンチに陥ったところを島の女親分に救われて……。勝新太郎が「座頭市物語」の前に主演し最初のヒットを記録した作品で、古い任侠精神の持ち主・朝吉と、腕も度胸も人一倍だがドライな感覚の持ち主・モートルの貞のコンビが対照の妙を見せた。貞に扮する田宮二郎にとってはこれが出世作となった。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("URBAN　LEGEND", "ルール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ニューイングランド州、ペンデルトン大学の女学生が、ドライブ中に何者かに惨殺された。やがて大学内で第２、第３の殺人が発生し、ことごとく現場に居合わせるはめになったナタリーは、学生新聞の記者ポールとともに真相究明に乗り出す。アメリカで広く知られる“都市伝説”の数々をストーリーに絡めたところが新味の学園ホラー。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死刑弁護人", "死刑弁護人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "", "2012-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("必殺！ブラウン館の怪物たち", "必殺！ブラウン館の怪物たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "“松竹創業90周年・朝日放送創立35周年”記念作品。時は幕末へと移され、徳川家康が京都御所を爆破する仕掛けを設けたという、黒谷屋敷の謎をめぐり、倒幕派や仕事人、新撰組までが入り乱れての大騒動。全編コメディ・タッチで“必殺”ものとしては違和感の残る作品。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Knocked Up", "無ケーカクの命中男　ノックトアップ", "", "", "http://image.eiga.k-img.com/images/movie/1912/315x210.jpg?3818", 129, "「40歳の童貞男」のジャド・アパトウ監督によるラブコメディ。主演はセス・ローゲンと「幸せになるための27のドレス」のキャサリン・ハイグル。高校時代の交通事故の賠償金で食いつなぎ、バカ仲間との共同生活を送っているダメ男のベン（ローゲン）は、昇進祝いでクラブにやって来たキャスターのアリソン（ハイグル）のナンパに成功し、一夜を共にする。しかしアリソンがベンの子どもを妊娠してしまい……。", "2008-12-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("After the Banquest", "結婚式の後で", "", "", "http://image.eiga.k-img.com/images/movie/55412/315x210.jpg?2160", 101, "日本人脚本家と韓国のスター俳優がタッグを組む、日韓共同ドラマプロジェクト「テレシネマ7」シリーズ。メガホンをとるのは、人気韓流ドラマ「私の名前はキム・サムスン」のキム・ユンチョル監督。大学時代にバンド仲間だったジホンとユリが結婚することになり、当時の仲間が次々と結婚式にやってくる。そして式の後も、彼らはジホンとユリの経営するペンションに集まり、久々の再会を楽しんでいた。だが、ジホンのかつての恋人でキーボードを担当していたジョンヒだけがなぜか姿を現さず、代わりに彼女の娘だと名乗る少女ミレがやってきて、会ったことのない父親を探していると話し出す。", "2010-05-29", "5,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Perfect Couple", "パーフェクト・カップル／おかしな大恋愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　コンピューターのデート・サービスを介して知りあったアレックスとシーラ。しかしふたりはどう見ても不釣合い。アレックスはアンティークのディーラーで、クラシック音楽を愛するギリシャ系アメリカ人家庭で育った中年。シーラはロックバンドで歌手を務めている若い娘。こんなふたりの予期せぬ恋を描いたコメディ。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALA，MY　DOG！", "わが家の犬は世界一", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「活きる」でカンヌ映画祭主演男優賞を受賞した中国の庶民派スター、グォ・ヨウが、情けない中年男の大奮闘を好演。舞台は衛生上の都合で犬の飼育を厳重制限する北京。警察に捕まった愛犬を必死で取り戻そうとする様をコミカルに描きつつも、理不尽な権力への批判を吐露した意欲作だ。北京の中年労働者ラオは妻子から煙たがられ、楽しみは愛犬カーラだけ。しかし高額の登録料が払えず、カーラが警察に捕まってしまう。", "2005-04-29", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジョゼと虎と魚たち", "ジョゼと虎と魚たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "「金髪の草原」の犬童一心監督が、田辺聖子の同名短編小説を映画化。主人公にそっけない態度をとりながらも、時に屈託のない笑顔を見せるジョゼを池脇千鶴が体当たりで熱演、貫禄すら感じさせる演技を披露している。雀荘でアルバイトする大学生の恒夫は、明け方マスターの愛犬と散歩していると、最近お客の間で話題になっているボロボロの乳母車が坂の上から勢いよく走ってくるのに遭遇する。中を覗くと震える少女が包丁を握り締めていた。そんな風変わりな少女に、恒夫は心惹かれるが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Golden Compass", "ライラの冒険　黄金の羅針盤", "", "", "http://image.eiga.k-img.com/images/movie/34262/315x210.jpg?2416", 112, "カーネギー賞など多数の文学賞を受賞したフィリップ・プルマンのファンタジー小説「ライラの冒険」3部作の映画化第1弾。現実の世界と似て異なるパラレルワールドの英国、オックスフォード。動物の姿をした守護精霊“ダイモン”と人間が生命を共有しているこの世界で、子供たちが謎の組織に次々と連れ去られる事件が発生。親友ロジャーを連れ去られた少女ライラは、ロジャーと子供たちを救うため、あらゆる物事の真実を示す“黄金の羅針盤”を手に、謎の組織の秘密があるという北の大地へ旅立つ……。", "2008-03-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2011-2012 モーツァルト「ドン・ジョヴァンニ」", "METライブビューイング2011-2012 モーツァルト「ドン・ジョヴァンニ」", "", "", "http://image.eiga.k-img.com/images/movie/57162/315x210.jpg?3799", 217, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を映画館で上映する「METライブビューイング」2011～12年シーズンの2作目。モーツァルトが1787年に作曲した傑作オペラで、伝説の放蕩者ドン・ジョバンニ（ドン・ファン）の没落を鮮やかに描く。18世紀スペイン。2000人以上の女性をものにしたプレイボーイのドン・ジョバンニは騎士長の娘アンナの寝室に忍び込むが、アンナに騒がれ、父の騎士長と争ううちに殺害してしまう。一方、かつてドン・ジョバンニに捨てられたエルビーラは、あきらめきれずにドン・ジョバンニを追いかけていたが……。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤線地帯", "赤線地帯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "国会に売春禁止法案が上程されていた頃、赤線地帯と呼ばれる区域にあった特殊飲食店“夢の里”の主人は、法案が通過すれば売春婦は投獄されると言って女たちを驚かせる。一人息子のために働く女、入獄中の父の保釈金のために働く女、夫が失業しているので通い娼婦をする女、元黒人兵オンリーだった女たち。そんな吹きだまりの“夢の里”にある日、下働きの少女がやってくる。時が経ち法案が４度目の却下となった頃、少女はおそるおそる道往く客に声をかけるのだった。「浪華悲歌」や「祗園の姉妹」を手掛けた溝口健二が得意とする娼婦たちの世界。法案は映画の封切後同年５月に成立。その３ヵ月後の８月24日、溝口は骨髄白血症のため58歳の生涯を閉じた。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("剣風練兵館", "剣風練兵館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "阪東妻三郎と月形龍之介の二大スター共演による幕末時代劇。江戸の剣豪・斎藤彌九郎率いる道場、練兵館に入塾した桂小五郎。彼は古参の塾生たちの反感を買うが、日夜稽古に没頭し、勉学にいそしんでいく。やがて斎藤にも一目置かれるようになった桂は、道場内でじわりと頭角を現していく。DVDは「阪東妻三郎傑作選　DVD-BOX」に収録。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("没事偸着楽", "しあわせの場所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ある家族の奮闘を暖かい視点で描いた中国発ハートフル・ヒューマンストーリー。お人好しで人情家のターミンは、大黒柱でもある父を亡くして以来、一家の主として家族を支えて来た。待ち受ける数々の問題に機転を利かして解決して行くターミンだったが……。ユーモアあふれる主人公とその家族の姿に中国中の国民が共感した。原作は中国の人気作家リウ・ホンの『へらずぐち男、チャン・ターミンの幸せな生活』。", "1998-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I　WOKE　UP　EARLY　THE　DAY　I　DIED", "クレイジー・ナッツ／　早く起きてよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "史上最低の映画監督として名高いエド・ウッドの脚本を映画化。「タイタニック」で強烈な悪役を演じたB・ゼーンが製作・主演、「バッファロー’66」のプロデューサーが共同制作し、その「バッファロー’66」のC・リッチを共演に据えて描いたダーク・コメディ。金融会社の現金を奪った男が、騒動で殺した男の葬儀に向かうが、なぜか死体が紛失。棺桶に金を落とした男が翌朝墓地に戻ってみるとカバンは空っぽで……。全編通してセリフは一切なし。効果音や音楽のみで進んでいくという、構成もノリも異色の作品。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUNGLE ASSAULT", "ジャングル・パトロール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "中米のジャングルを舞台に、米軍退役将校が、ベトナム戦争での部下たちとともに繰り広げる、少女救出作戦。退役将校ジョージ・ミッチェルは、自分の娘が中米のテロリスト・グループの一員に加わっている姿をTVで見てしまう。ミッチェルはすぐに娘の救出を考えるが、彼女にはCIAの手が……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("共犯者", "共犯者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「ビー・バップ・ハイスクール」の原作者、きうちかずひろの演出によるバイオレンス活劇。彼が監督したオリジナルビデオ「カルロス」の続編にあたる。伝説のブラジリアン・マフィア、カルロスの刑務所脱出に動揺した敵対組織はスゴ腕の殺し屋を呼び寄せる。一方、カルロスは夫の暴力に悩む人妻、聡美を助け、行動をともにするが……。カルロスに扮した竹中直人、聡美役の小泉今日子、殺し屋を怪演する内田裕也の個性派共演が見どころ。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHARK", "シャーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "B級映画の巨匠、S・フラーがハリウッドを離れ、「裸のキッス」以来５年ぶりにメキシコで撮った海洋アクション。サメに守られた宝物を海底から引き上げるダイバーを、当時売り出し中だったアクション・スター、B・レイノルズが体当たりで演じる。撮影中からトラブルが続き、公開後も再編集され、“MAN−EATER”とタイトルも変えられて再公開される（一説には３つのバージョンがあるともいわれている）など、いわくつきの映画。ブニュエル作品で知られる女優のS・ピナルや、フラー映画の常連、B・サリヴァンなどが脇を固めており、フラー節とも呼べる圧倒的に力強いショットが随所に見られる。", "1970-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN　AMOUR　DE　SORCIE｀RE", "奥サマは魔女 ?魔王の陰謀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "V・パラディがキュートな魔女に扮したコミカル・ファンタジーで、往年のTVドラマとは無関係。幼い息子の父親になってくれる人を探す魔女のモーガンは、パリを訪れた米国人の天才発明家マイケルに接触。二人は恋に落ちるが、彼女の子供を跡取りにしようとする魔王がジャマに入り……。威圧感のある魔王をJ・レノが怪演。", "1997-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIVEMENT DIMANCHE", "日曜日が待ち遠しい！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "南フランスの小さな町の不動産屋で働く女秘書。殺人事件に巻き込まれた彼女の名探偵なみの活躍が、フィルム・ノワールばりのモノクロームのナイト・シーンに描き出される。雨の打ちすえる路面、謎のナイト・クラブ……。みずみずしい映像にあふれたトリュフォーの遺作。", "1982-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("我が家は楽し", "我が家は楽し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "植村考作は、妻・なみ子との間に４人の子女を持ち、貧しいながらも明るい家庭を営んでいる。勤続25周年を表彰されて得た賞金をすられたり、住みなれた家から立退きを迫られるなどの困難もあったが、愛と信頼の強い絆で結ばれた一家は、様々な障害を乗り越えていく。田中澄江の原案を中村登が映画化した小市民映画の佳作。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KODO Live at ACROPOLICE Athene，Greece", "KODO Live at ACROPOLICE Athene，Greece", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "和太鼓集団“鼓童”の1995年ヨーロッパツアーにおけるギリシァでの演奏をビデオで収めたドキュメンタリー。アテネの円形劇場(アクロポリス)での演奏は、和太鼓のリズム、メンバーたちのパフォーマンスなどによって、観衆を魅了していく。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAS SCHRECKLICHE MADCHEN", "ナスティ・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "カトリック教会が強大な影響力を持つドイツの小さな町。少女ソーニャは論文コンクールのテーマに“ナチス時代の我が町”を選び、それまで誰も触れようとしなかった町のナチス時代を調べる。それを快く思わない人間たちは様々な妨害を繰り返すのだが……。原題は“いけない子”の意。どんなに妨害されてもめげずに挑戦するソーニャの飽くなき戦いが、斬新な演出の中に描かれる。", "1989-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXILE　IN　SARAJEVO", "エグザイル・イン・サラエヴォ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "オーストラリア在住の俳優T・カンビスが、亡き母親の故郷でもある戦火のサラエヴォにビデオカメラを持ち込んだドキュメンタリー。撮影後に破裂弾で死亡する女の子の生前の姿や、７歳の少女が集団レイプの現場を目撃したエピソードなどの衝撃的な事実を収録しながらも、サラエヴォの人々の生と死、愛と笑いの日常を、等身大の視点で捉えている。", "1997-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELECTRIC−BOOGALOO IS BREAKIN’２", "ブレイクダンス２ ブーガルビートでT・K・O！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ターボ、ケリー、オゾンの頭文字をとった、TKOトリオ。若者たちのブレイクダンス・スタジオ建設地を地上げしようとする土建会社や市当局を相手に、ヤング“ブレイキング”パワーが爆発。ブルドーザーを押しのけるブレイクダンス場面のパワーが圧巻のシリーズ第２作。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA GIBBIA", "スキャンダル —愛の罠—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "舞台は現代のパリ。16年前に捨てられた男が残した心の傷跡が癒えず、周到な計画をもって男にサディスティックな復讐を遂げる女マリーの姿を描く異色作。男に対してサディスティックな拷問を加えるアントネッリの熱演が見もの。", "1985-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ツヒノスミカ", "ツヒノスミカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "2001年の長編デビュー作「ジム」で注目された新鋭・山本起也監督が、90歳になる実の祖母・山本マツを主人公に撮りあげたドキュメンタリー作品。息子夫婦と同居するため、長年にわたって暮らし続けてきた家を取り壊すことになった祖母のひと夏にスポットを当て、誰にでもいずれは訪れるであろう大切な人や物との別れの時を静かに見つめていく。ナレーションを務めるのは、ベテラン俳優の寺島進。", "2006-09-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Brood", "ザ・ブルード／怒りのメタファー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "　幼少期に受けた虐待が原因で神経症を患うノラは医師ラグランの診療施設に入院する。しかしノラの夫フランクは彼女を隔離し面会させないラグランに不信感をいだく。一方、ラグランは人間の怒りを実体化する実験を行なっていた。ノラの体にできた腫瘍から異形の群れが現れ、やがて復讐を開始。ノラとフランクの娘キャンディスにも危険が迫る……！　夫婦間や親子間の亀裂というテーマとともに、驚愕の科学実験が生んだ凄惨な恐怖を描く。", "1979-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("One California Day", "ワン　カリフォルニア　デイ", "", "", "http://image.eiga.k-img.com/images/movie/53572/315x210.jpg?2587", 96, "サーフ・カルチャーを通してカリフォルニアの魅力に迫ったドキュメンタリー。西海岸のサーフ・スポットを北上しながら各地の有名サーファーたちのライフスタイルを映し出していくとともに、過去の伝説的サーファーたちにも目を向け、カリフォルニアに根付いたサーフ・カルチャーの真髄を浮かび上がらせていく。カリスマ・サーファーたちが披露する超絶テクニックのみならず、西海岸に広がる美しい風景にも注目。", "2008-06-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANCE WITH A STRANGER", "ダンス・ウィズ・ア・ストレンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1955年、イギリスで最後に絞首台にのぼった女性ルース・エリスの実話をもとに、彼女の二人の恋人との愛の葛藤、苦悩から殺害事件に及ぶまでを綴る。情念の犠牲者となる若者デビッドをR・エヴェレットが好演している。", "1984-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Man Hunt", "マン・ハント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "世界的な狩りの名手と知られるソーンダイクは、ヒトラー暗殺未遂事件の罪で追われロンドンへとやって来るが、そこにも追っ手が。危ういところを美しい娘に助けられた彼だったが……。「死刑執行人もまた死す」に先立ちハリウッドで撮影された、巨匠F・ラングの幻のサスペンス・スリラー。ライフルの照準がヒトラーを捕捉する瞬間の映像から始まるというショッキングなオープニング、ドイツの古城からロンドンの霧の風景、そして巨大なおどろおどろしい影を効果的に使った演出など、ラング美学が随所に発揮されている。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("40歳問題", "40歳問題", "", "", "http://image.eiga.k-img.com/images/movie/53982/315x210.jpg?3155", 87, "80年代のバンドブームを支え、現在は40代になったミュージシャンたちに迫るドキュメンタリー。監督は「ナヴィの恋」「ホテル・ハイビスカス」の中江裕司。社会の変化や体力の衰えを感じながらも、音楽を続けている彼らの本音や苦悩をカメラが捉える。リリー・フランキーや角田光など40代の著名人にもインタビューを敢行。ナビゲーターは30代のスネオヘアー。", "2008-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Takers", "テイカーズ", "", "", "http://image.eiga.k-img.com/images/movie/55282/315x210.jpg?1529", 107, "マット・ディロン、ヘイデン・クリステンセン、ポール・ウォーカー共演の犯罪アクション。数々の銀行強盗を成功させ、贅沢な暮らしをしているジョン（ウォーカー）、A.J（クリステンセン）ら5人組のところに、刑務所から出たばかりのかつての仲間が2000万ドル強奪の大仕事を持ってくる。ジョンたちは新たな強盗に向けて慎重に準備を進めるが、そこにロサンゼルス警察のウェールズ刑事（ディロン）が立ちはだかる。", "2011-11-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("札幌オリンピック", "札幌オリンピック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 166, "東宝創立40周年記念作となった、札幌冬季オリンピックのオフィシャル・ドキュメンタリー。1972年2月3日から11日間、北海道・札幌で開催された本大会の模様と選手達の熱気を伝える。「心中天網島」の篠田正浩監督がメガホンを取っている。日本唯一のメダル獲得は70メートル級のスキー・ジャンプだったが、金・銀・銅を独占するという快挙。その感動のシーンをはじめ、当時のアイドル・スケーター、ジャネット・リンも登場。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALL　ABOUT　MY　MOTHER", "オール・アバウト・マイ・マザー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "各国の映画賞を総なめにしたスペインの誇る鬼才、P・アルモドヴァルによるヒューマン・ドラマ。移植コーディネーター、マヌエラは女手一つで育てあげた一人息子、エステバンから17年前に別れた夫について聞かれる。ずっと隠していた夫の秘密を遂に話さなければと覚悟を決めた矢先、エステバンはマヌエラの目の前で事故死を遂げる。息子が抱いた父への思いを伝えるために、マヌエラは職を棄て、かつての思い出の地、バルセロナへと向かった。徐々にわかる夫の秘密とは？　オープニングからは予想も出来ないようなストーリーの急展開は、アルモドヴァルの真骨頂。女装の娼婦役、アントニア・サン・ファンの怪演に注目。", "1999-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VS GANGS", "VS GANGS", "", "", "http://ecx.images-amazon.com/images/I/51zuL1ZU2qL._SL210_.jpg", 70, "街を牛耳るカラーギャングに立ちむかう男女3人のドラマを描いた群像劇。黄色をフラッグとしたカラーギャングが支配し、麻薬で汚染されたストリート。ある日、過去の出来事から麻薬と自分自身を憎む青年・操魏禅斗のもとに、現金1千万円と女が迷い込んでくる。ダンスを愛する女・劉美良子は、カラーギャングの呪縛から解放されることを望み続けていた。一方、暴力団から依頼を受けた殺し屋・董堂卓也は、依頼とは違う野心を内に秘めており……。", "2010-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Word, Sound and Power", "ワード・サウンド・パワー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "レゲエ界屈指の名ギタリスト、アール・チナ・スミスを中心とした伝説のグループで、ボブ・マーリーのバックバンド「キング・タビーズ」の一員としても知られる「ザ・ソウル・シンジケート・バンド」に密着したドキュメンタリー。メンバーたちが音楽や思想を語るインタビューやライブ映像、さらに撮影が行われた1979年のジャマイカの様子を映しだし、世界中でレゲエが盛りあがりを見せていた当時の空気を存分に堪能することができる。", "2007-10-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仲よし音頭 日本一だよ", "仲よし音頭 日本一だよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "スポンサーをライバル会社に取られてばかりの広告代理店に勤める新米企画部員、加納周治。現状を打開せよとの至上命令を受けた彼は、スポンサーの宣伝部長が、ある女に夢中なのを利用しようとするがこれは失敗。今度はしがないチンドン屋の父親が助け舟を出し、一流のスターを集めたショーを企画する。ドッと笑える、てんやわんやコメディ。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ため息の理由", "ため息の理由", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "音楽と映像のコラボレーションを果した、「min.Jam」シリーズの一編。アーティスト、斉藤和義の書き下ろし楽曲に乗せて描くショートシネマ。外国語が話せない帰国子女、消極的な女の子、冷めた目で物事を見ている浪人生。ままならない日々に右往左往しながらも、一生懸命生きる女の子３人の切ない現実を、繊細なタッチで描く。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天浴／XIU　XIU ： THE　SENT　DOWN　GIRL", "シュウシュウの季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「ラストエンペラー」の皇后役で注目された、ジョアン・チェンの初監督作品。文化大革命末期の中国を舞台に、歴史の波にほんろうされた少女の痛々しい悲劇を重厚に描き出す。1975年、10代の少女シュウシュウは政府の下放政策に従い、寡黙な男ラオジンとともに辺境の地で放牧の任務に着いた。ところが、任期の６ヵ月を過ぎても軍は迎えをよこさない。実は革命はすでに終焉を迎えており、混乱した本部ではシュウシュウのことは忘れられていた。そうとは知らぬシュウシュウは、帰郷したい一心で、時折訪れる有力者に体を任せる。そんな彼女をラオジンは心配げに見守るが、彼女の転落はとどまるところを知らず……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("島国根性", "島国根性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "郷土・福島に根づいた映画を作り続ける渡辺文樹監督の作品。家庭教師を営む主人公の長男が、教え子の女の子に一目惚れする。しかし彼女の母とデキてしまい、それが周囲に知れわたったために、恋も思うようにままならない……。独自の島国民族崩壊論を展開させる土着型リアリズム映画。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In a Better World", "未来を生きる君たちへ", "", "", "http://image.eiga.k-img.com/images/movie/56122/315x210.jpg?1544", 118, "デンマークで暮らす少年エリアスは学校でいじめられる日々が続き、医師としてアフリカの難民キャンプに赴任している父アントンの存在だけが心の頼りだった。そんなある日、転校生クリスチャンがエリアスをいじめから守ったことで、2人は親交を深めていく。一方、アフリカにいるアントンは自身の離婚問題や、ひん死の患者たちの存在に苦悩していたが、そんな彼の前に子どもや妊婦までも手にかける悪党が現れ……。「アフター・ウェディング」のスサンネ・ビア監督が、暴力や憎しみに満ちた世界の中でも希望を見出していく人々の姿を描いたドラマ。第83回米アカデミー賞で外国語映画賞を受賞した。", "2011-08-13", "18,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘密戦隊ゴレンジャー　爆弾ハリケーン！", "秘密戦隊ゴレンジャー　爆弾ハリケーン！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "特撮テレビシリーズ「秘密戦隊ゴレンジャー」の劇場版第4作となるオリジナル作品。日本征服を目論む黒十字総統は、日本全土を標的とするミサイル攻撃を計画し、四国松山にミサイル基地の建造を進めていた。イーグル本部からこの情報を得たゴレンジャーは、フェリーに乗りこんで現地へと向かう。そこでは、黒十字軍きっての無法者・鋼鉄剣竜がゴレンジャーを倒すべく待ち受けていた。「東映まんがまつり」の1編として公開。", "1976-07-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドリフターズですよ！全員突撃", "ドリフターズですよ！全員突撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "シリーズ最終作でドリフの面々が扮するのは、いつもドジばかり踏んでいるギャング団。いつものように現金強奪に失敗してアジトに戻ると、孤児院を脱走してきたピッピという女が待っていた。彼女に10万ドルの懸賞金がかかっているのを知った面々は……。佐々木守の脚本にも注目。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エスカフローネ", "エスカフローネ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "生きる意味を失いかけている高校生のひとみは、突然謎の男に導かれ、奇妙な世界“ガイア”へと連れていかれる。そこで彼女は、エスカフローネと呼ばれる巨大な鎧をめぐる戦いに巻き込まれていく……。人気TVアニメの劇場映画だが、TV版とはパラレル・ワールド的設定で構築し直したことで、よりスケールの大きい冒険世界が描かれている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("送還／REPATRIATION", "送還日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 148, "“北のスパイ”として30年以上韓国で投獄生活を送った老人たちを、12年間追い続けた渾身のドキュメンタリー。釈放後も祖国に帰れず、韓国社会で孤立しながらも、気丈に生きてきた長期囚たち。長い年月をかけて彼らとの信頼を築き上げたキム・ドンウォン監督の映像が、胸に迫ってくる。'92年、刑務所から出獄したが行き場のない長期囚たち。キム・ドンウォンは同じ町に住む彼らとふれあい、その日常を撮り続けた。", "2006-03-04", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flash Point", "導火線 FLASH POINT", "", "", "http://image.eiga.k-img.com/images/movie/56792/315x210.jpg?4719", 87, "「SPL 狼よ静かに死ね」（2005）、「かちこみ！ドラゴン・タイガー・ゲート」（06）のドニー・イェン、ウィルソン・イップ監督がタッグを組んだアクション。中国への返還を間近にひかえた1997年の香港。正義感の強いマー刑事は、ベトナム人のアーチャー、トニー、タイガーの3人兄弟が率いるマフィア組織を追っていた。相棒のウィルソン刑事が組織に潜入するも正体を見破られ、ウィルソンの恋人ジュディを人質に取られてしまう。怒りに燃えたマーは、マフィア相手にたった1人で戦いを挑む。", "2011-07-16", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AS GOOD AS IT GETS", "恋愛小説家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "J・ニコルソンが「愛と追憶の日々」に続いて再びJ・L・ブルックスと組んだ笑いと感動のラブ・ストーリー。人気小説家のメルヴィンは極度の変わり者。毎日同じ時間、同じ席に座るレストランには自分のフォークとナイフを持参する潔癖症で、他人の意を介さない毒舌家でもある。ただ一人、ウエイトレスのキャロルだけには頭が上がらない。やがて、ゲイの隣人から嫌々ながら犬を預かった彼は、犬になつかれるうちに心の安らぎを見いだし、キャロルへの愛にも気付き始めるのだが……。アカデミー賞とゴールデン・グローブ賞の両賞で主演男優と主演女優賞に輝いたJ・ニコルソンとH・ハントが、素直になれない男女の心の揺れを見事に体現。ユーモラスで機知に富んだ二人の会話のやり取りと、軽妙なストーリー展開でさわやかな大人のラブ・ロマンスとなった。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN MONDE SANS PITIE´", "愛さずにいられない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "1961年生まれのフランスの俊英エリック・ロシャンのデビュー作。パリを舞台に、定職も持たず弟と二人でその日暮らしの日々を送るイッポ。そんな彼が、ある日、典型的なエリート女性ナタリーに出会い、恋心を抱く姿を新人らしいタッチで描いていく。1989年東京国際映画祭ヤングシネマに出品されたほか、ヴェネチア映画祭国際評論家賞、ルイ・デリュック賞などを受賞。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふがいない僕は空を見た", "ふがいない僕は空を見た", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEVEL", "LEVEL", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "脱サラして家業のパチスロ店を継いだ青年と、彼の強引なやり方に憤慨する同業者の闘いを描いたサスペンス・アクション。主演は仲村トオル。平凡なサラリーマンだった豊田は、父の後を継ぎパチスロ店を経営することになった。しかし店は業績不振で潰れる寸前。野心を燃やした彼はあくどい手を使い店を軌道にのせるが、そこには同業の巨大暴力シンジケートがはびこり、やがて犯罪の世界にまで足を踏み入れてしまうのだった。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HUNTERS", "追撃機", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "実際に朝鮮戦争に参加したアメリカ空軍のジェット・パイロットが書いた原作をもとにした航空戦争アクション。戦闘機隊に編入されたサビル少佐は、死の恐怖を紛らすために酒浸りのアボット中尉やエドベル中尉を率いて韓国上空に出動。ある日少佐の編隊は、北朝鮮側の強者“ケーシー・ジョーンズ”の一隊に出会い、激しい空中戦を展開するが……。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("我愛厨房／KITCHEN　AGGIE　ET　LOUIE", "kitchen キッチン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "吉本ばななのベストセラー小説を、舞台を香港に移して映画化。「息子の告発」「太陽に暴かれて」などの社会派ドラマを手掛けてきたイム・ホーが、詩的な映像美で包み込む。最愛の祖母を亡くしたアギーは、祖母の知り合いの青年ルイと彼の母エマの家に身を寄せる。親切な二人に優しく迎えられながらも、彼女は自分の殻に閉じこもり、お気に入りの場所のキッチンで一人過ごしていた。そんな彼女が少しずつ心を開くようになり、３人の楽しい共同生活が始まったある日、エマに悲劇が起こる……。富田靖子が「南京の基督」に続いて、全編広東語でヒロインを熱演する。", "1997-01-01", "76,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Memorias del subdesarrollo", "低開発の記憶　メモリアス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「苺とチョコレート」の名匠トマス・グティエレス・アレアが1968年に手がけた、キューバ映画の原点ともいえる社会派ドラマ。キューバ革命後の揺れる世相を、ドキュメンタリー映像を挟みながら描きだす。1961年、カストロによる社会主義宣言直後のハバナ。周囲の人々がアメリカへと亡命していく中、ひとりハバナに留まった資産家の青年セルヒオは、自国と国民を「低開発」と見なして傍観していたが……。", "2007-05-26", "49", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crank 2: High Voltage", "アドレナリン　ハイ・ボルテージ", "", "", "http://image.eiga.k-img.com/images/movie/54432/315x210.jpg?2921", 96, "「トランスポーター」シリーズのジェイソン・ステイサムが、アドレナリンを分泌し続けないと死ぬ男を演じた「アドレナリン」の続編。殺し屋のシェブは、ヘリコプターから落下してロサンゼルスの路上に叩きつけられたところを、謎の中国系ギャングに拉致されてしまう。その3カ月後、やっと目を覚ましたシェブは自分の体に電池式の人工心臓が埋め込まれていることに気付く。自分の心臓を取り戻すためギャングの行方を追うシェブだったが、人工心臓には定期的な充電が必要で……。", "2009-09-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EWORKS: THE BATTLE FOR ENDOR", "エンドア・魔空の妖精", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "魔法と科学が混在する緑の星エンドアに、一人取り残された少女シンディー。彼女を救い出すため、勇者ティークとその仲間が邪悪の王エクラに立ち向かう……。「スター・ウォーズ」のスタッフによるSFXを駆使したスリルと愛の冒険ファンタジー。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不良少年〈ヤンキー〉の夢", "不良少年〈ヤンキー〉の夢", "", "", "http://ecx.images-amazon.com/images/I/417Sh5405FL._SL210_.jpg", 114, "“ヤンキー先生”の愛称で知られる義家弘介の自伝を映画化。問題児を受け入れる北星学園余市高校に編入した不良少年の義家が、心ある教師や大人との出会いを通して、立ち直り、教師として母校に戻るまでを描く。松山ケンイチが義家役を熱演している。行く先々でトラブルを起こす弘介は、問題児を受け入れる北海道の余市高校へ転入。親身な先生に囲まれた彼は少しずつ変わり始める。", "2005-10-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("元禄美少年記", "元禄美少年記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "赤穂義士銘々伝の一つ、矢頭右衛門の物語を題材とする伊藤大輔作品。病弱な父の果たせなかった遺志を継ぎ、赤穂浪士に加わった右衛門の足跡を、切腹の順番を待つ彼の回想という構成でたどっていく。脚本は八尋不二のオリジナルで、自ら脚本を手掛けることが多い伊藤監督とのコンビはこれが４作目。中村賀津雄の美丈夫ぶりも見ものだ。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Derailed", "すべてはその朝始まった", "", "", "http://ecx.images-amazon.com/images/I/514M835C6SL._SL210_.jpg", 107, "クライブ・オーウェン＆ジェニファー・アニストン共演によるサスペンススリラー。会社員のチャールズは美しい妻や可愛い娘に囲まれて幸せな暮らしを送っていたが、ある日通勤電車の中で出会った女性ルシンダと恋に落ちてしまう。ところが、場末のホテルで一夜を共に過ごそうとしたその時、銃を持った男が乱入し2人に襲いかかる。その後も男は、ルシンダとの不倫をネタにチャールズを脅迫するようになり……。", "2005-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピンクリボン", "ピンクリボン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "全盛期は過ぎたものの、今も年間90本以上の新作が制作される“ピンク映画”。いわゆるポルノ作品でありながら、一般フィールドに飛躍する名監督を多く生むという、世界的に見ても珍しい歴史を築いてきたこのジャンル。本作は、そんなピンク映画の世界を紐解くドキュメント。「≒〈ニア・イコール〉森山大道」の藤井謙二郎監督が丹念な取材で、ピンク映画を多角的に捉える。黒沢清や高橋伴明といったピンク経験のある名監督たちがインタビューに続々と登場。彼らが語る当時の裏話が興味深い。", "2005-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUNDAY IN NEW YORK", "ニューヨークの休日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ニューヨークの兄を訪ねた田舎娘。知り合った男に迫られて困惑したり、兄の私生活を知ってビックリしたりしながら、彼女は成長していく。ウブなヒロインを演じたJ・フォンダの初々しさに注目。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOOTING　FISH", "シューティング・フィッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ロンドンで暮らす孤児院育ちの若者ジェズとディランは、大邸宅を購入するという夢を叶えるため、怪しげな詐欺商法を行っている。美しき医学生ジョージーを加えた二人は、カモを探しては金を稼ごうとするが、懲役３ヵ月の刑を食らってしまい……。これが長編２作目となるイギリスの新鋭監督S・シュワルツが、全編にブリティッシュ・ポップを散りばめ、軽快なタッチで撮り上げた青春コメディ。若い役者たちが新鮮な魅力をふりまき、その口八丁手八丁のやりとりが愛すべき笑いを呼び起こす。タイトルは“ダマすのは簡単”という意味のスラング。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お小夜恋姿", "お小夜恋姿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "売れない小説家が書いている三角関係メロドラマと、その小説のヒロインのモデルである現実の女の姿を対比的に描いた、軽いタッチの小唄映画。小説界でも現実界でもお小夜と呼ばれるヒロインを演ずるのは田中絹代で、それぞれの世界で正反対の性格を示す彼女の演技力がなによりも見もの。", "1934-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAN-GAN教師", "DAN-GAN教師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "身体に無数の傷を持つ謎の新任教師・犬神弾丸は、問題児ばかりを集めたクラスを受け持つことになる。しかし、生徒に暴力をふるうわ、ヤクザにケンカを売るわで、そのやり方はめちゃくちゃ。あまりの破天荒ぶりに、多くの生徒たちが、授業をボイコット。しかし、弾丸のなかに何かを感じた３人の生徒は、彼と心を通わせるようになる。そんな時、弾丸の命を狙う一人の男が現れた。その男の魔の手が生徒に及んだ時、弾丸の怒りが爆発する。プロレスラー・大仁田厚が熱血教師を体当りで演じている。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アジアンビート日本篇 アイ・ラブ・ニッポン", "アジアンビート日本篇 アイ・ラブ・ニッポン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "日本を含むアジア６ヵ国の若手監督たちが、同じ主人公（＝永瀬正敏）を起用して競作する“アジアンビート”シリーズ第１作。ふとしたことがきっかけで、日本を揺るがす極秘情報を握るテロリストのフィリピン少女・バナナと出会ったことによって様々な事件に巻き込まれていく主人公“TOKIO”。本作ではTOKIOの登場と日本からの脱出までを描いている。なお、劇場公開されたのは本作のみで、あとの５作品はビデオ・リリースのみで、タイトルは「…シンガポール篇　ラブ・フロム・テマセク」「…タイ篇　パウダー・ロード」「…マレーシア篇　サンライズ・イン・カンポン」「…台湾篇 シャドー・オブ・ノクターン」「…香港篇　オータム・ムーン」。林海象が総監修にあたっている。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A3D II ayumi hamasaki Rock'n'Roll Circus Tour FINAL 7days Special", "A3D II ayumi hamasaki Rock'n'Roll Circus Tour FINAL 7days Special", "", "", "http://image.eiga.k-img.com/images/movie/56092/315x210.jpg?1472", 100, "人気アーティスト・浜崎あゆみのライブ映像を3Dで上映する「A3D」の第2弾。浜崎が2010年全国アリーナツアーの特別公演として同年10月に東京・代々木体育館で行ったライブ「ayumi hamasaki Rock'n'Roll Circus Tour FINAL 7days Special」の模様を収録。前作同様3Dシネマライブならではの演出も加える。", "2011-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青い山脈'88", "青い山脈'88", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "青森県のある私立女子校を舞台に、先生と生徒、大人と子供のかかわりを辛辣に描いた石坂洋次郎原作の永遠の青春文学を５度目の映画化。７年ぶりに映画復帰した梶芽衣子、第１回の映画化作品に主演以来、実に40年目に特別出演となった池部良など、多彩な出演陣も話題に。工藤夕貴が可憐。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四大天王／DRAGON SQUAD", "怒れるドラゴン・不死身の四天王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ジミー・ウォンが監督、主演したカンフー・アクション。暗黒街のゴッド・ファーザー、スー・チン一味と、流れ者、銀行の親衛隊長、漁師、アル中の男という４人の個性豊かな空手の達人との死闘を描く。", "1973-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUMMER HEAT", "真夏の情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "「郵便配達は二度ベルを鳴らす」を彷彿させる異色の女性心理ドラマ。ノース・カロライナ州の小さな町で夫と二歳になる娘とともに生活する20歳の若妻ロキシーは、流れ者との情事の果てに、取り返しのつかない事態へと巻き込まれていく。女流監督M・グリースンはキメ細かくヒロインの心象を描いていく。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CUBA　FELIZ", "キューバ・フェリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "76歳の孤高のストリート・ミュージシャン、エル・ガジョが、ギターを携えてキューバ国内を旅する姿を、チュニジア生まれの映画作家K・ドリディが追いかけたドキュメンタリー。スラム街出身の若いラッパー、老人たちのバンドなど、地域ごとに音楽性の異なるミュージシャンが登場し、エル・ガジョと魅惑的なセッションを繰り広げていく。ナレーションを排したシンプルなアプローチで、キューバ音楽の伝統と多様性を浮き彫りにした１本。", "2000-01-01", "49,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BELLE E´POQUE", "ベルエポック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "スペインのアカデミー賞にあたるゴヤ賞で、主要９部門を独占し、同国の映画史上第２位の興行成績をあげた作品。美人４姉妹と純情な青年。彼女たちの母親で世界的なオペラ歌手と愛人、そしてかつての夫。二世代にわたる男女の恋のやりとりを、ウィットとユーモアを交じえて描く。４姉妹が、いずれもはち切れんばかりの魅力を見せてくれる。", "1992-01-01", "9,2,25", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paradise", "愛に翼を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "夏休みに母の友人夫婦のもとに預けられることになった少年ウィラード。しかしその夫婦は最近息子に急死されたばかりで、二人の関係は冷え切っていた。フランス映画「フランスの思い出」を基にした作品。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Meet the Spartans", "ほぼ300", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EBONY TOWER", "ローレンス・オリヴィエ　画家と美女の奇妙な生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "南フランスの美しい風景をバックに、老画家と二人の美女との、優雅で謎めいた日常生活を、一人の若い画家の目を通して描き出した文学的な香りのある作品。落ちついた関係の中に、一人の若い画家が入り込んで、それぞれの内面が揺れ始める……。", "1985-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNFORGETTABLE", "アンフォゲタブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "「レッドロック　裏切りの銃弾」や「甘い毒」など、スタイリッシュなフィルム・ノワールを連打する鬼才J・ダールが手掛けたサスペンス・ミステリー。シアトルの検死官クレインは、かつて何者かに妻を惨殺された過去を引きずっていた。そんなある日、女性脳医学者マーサが、他人の記憶を追体験できる画期的な誘発剤を開発したと知ったクレインは、すかさず彼女に接近。盗んだ誘発剤と死んだ妻の脳髄液を自分に注射した彼は、恐るべき殺人事件の真相を知る……。ふだんは加害者役が多い個性派R・リオッタが、薬の副作用による激痛に悶絶しながら犯人探しに燃える主人公を大熱演。現実と記憶の中の出来事をカットバックさせた緻密な映像と音響効果が、有無をいわせぬ迫力をみなぎらせている。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PASSENGER 57", "パッセンジャー57", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "テロリスト一味に乗っ取られたジャンボ旅客機。偶然乗り込んでいたテロ対策の専門家が、得意の武道と明晰な頭脳を駆使して残忍な一味に戦いを挑んでいく。テンポの良い展開とダイナミックなアクションで描いた娯楽活劇。W・スナイプスがハードな立ち回りを披露する。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LETHAL WEAPON4", "リーサル・ウェポン4", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "M・ギブソン演じるリッグスとD・グローヴァー演じるマートフの刑事コンビが活躍するアクション・シリーズの第４作。前作でリッグスと恋に落ちた、R・ルッソ扮する女刑事ローナが彼の子を妊娠しているという設定で登場。また、２作目以降、コメディ・リリーフとして顔を見せているJ・ペシ扮するレオ・ゲッツも迷活躍を見せる。リッグスとマートフが今回立ち向かうのは、ロサンゼルスのチャイナタウンを牛耳る中国マフィア。大量の中国紙幣の偽札を製造しているボス、クーの野望を打ち砕く姿を、ド派手なカー・アクションあり、爆破シーンありで見せていく。香港映画界の大スター、ジェット・リーが悪役クーを演じ、迫力のカンフー・アクションを披露している。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FILLMORE", "フィルモア・ラストコンサート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ロック革命の立役者ビル・グラハムが創り上げた“ロックの殿堂”フィルモア・オーディトリアム。1960年代後半に活躍した大物スターが、数多く登場したこのステージの最後のコンサートを収録したライヴ・フィルム。1965年から６年間にわたる軌跡の総決算ともいうべき一週間連続ライブが、ホットに捉えられている。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("台北飄雪", "台北に舞う雪", "", "", "http://image.eiga.k-img.com/images/movie/54912/315x210.jpg?3531", 106, "「山の郵便配達」「故郷の香り」のフォ・ジェンチィ監督によるラブストーリー。主演は「カンフー・ダンク！」のチェン・ボーリン、アジアの新星トン・ヤオ。台北郊外にある渓谷の町チントンで孤児として育ったモウは、歌手デビュー目前に声が出なくなり、台北を飛び出してきた少女・メイと出会う。人懐こいモウと打ち解けたメイは、モウのなじみの食堂で働き始め、次第に声と元気を取り戻していくが……。", "2010-02-20", "8,76,6,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピューと吹く！ジャガー THE MOVIE", "ピューと吹く！ジャガー THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/34242/315x210.jpg?4523", 99, "うすた京介の大人気ギャグマンガを実写映画化した脱力コメディ。ロックミュージシャンに憧れる清彦は、オーディション会場で謎の笛吹き男ジャガーに出会う。彼が講師を務める芸能人養成所“ガリプロ”の笛科にいつの間にか入学させられてしまった清彦は、超個性的なクラスメイトたちにいつしか溶け込んでいく。しかし、笛科は資金難で廃止寸前に追い込まれており……。ジャガー役の要潤をはじめとする絶妙な配役に注目。", "2008-01-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("出口のない海", "出口のない海", "", "", "http://image.eiga.k-img.com/images/movie/33652/315x210.jpg?3335", 121, "太平洋戦争末期、2度と帰れないと知りながら、日本軍最後の秘密兵器、人間魚雷「回天」に乗り込むことになった1人の元甲子園優勝投手の生き様を通して、人間の尊厳を描く戦争ドラマ。04年の映画賞を総なめにした「半落ち」の佐々部清監督が再び横山秀夫の原作に挑戦。脚本に山田洋次、冨川元文。主演は映画初出演となる歌舞伎俳優・市川海老蔵。", "2006-09-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THAT’S ENTERTAINMENTII", "ザッツ・エンタテインメントPART２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "収録映画75本、登場するスター125人、司会役はビング・クロスビー、ジェームズ・スチュアート以下11人の大スター。この豪華絢爛な顔ぶれは、百花繚乱の趣。加えて、たった一度しかないというJ・スチュアートの歌まで聴けるのだから、ファンにはたまらない。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD SILENCE", "デッドサイレンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "J・ディーヴァーのベストセラー小説『静粛の叫び』を映画化。３人の脱獄犯が、ろうあ学校の女性教師メラニーと生徒たちを人質に廃屋に立てこもった。誘拐犯との交渉のプロであるFBI捜査官ジョンが指揮を執り、人質救出に乗り出す。ところが、犯人は見せしめのように一人を射殺。緊迫した空気が流れる中、ジョンはメラニーが手話で合図を送っていることに気が付く。彼女は見つけた抜け道から脱出を図ると伝えていたのだ。ジョンは危険を覚悟でその脱出計画に協力するのだが、犯人のワナが待ち受けていた……。犯人とFBI捜査官の駆け引き、女性教師の勇気ある行動が緊張感たっぷりに展開。「パトリオット・ゲーム」「今そこにある危機」の脚本家D・スチュアートによる意外なストーリー構成がとにかく巧妙。", "1996-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NEW ADVENTURE OF TARZAN", "ターザンの新冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ターザンを主人公にした映画は全部で40本以上作られているが、この作品は12本目のもの。原作者のE・R・バロウズが製作にかかわったもので12編からなる連続活劇版と長編版の２種を作った。これは前者のバージョン。相手役となるジェーンは出ていない。ビデオ発売時タイトルは「ターザン 魔宮の大冒険１」。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("92黒攻瑰對黒攻瑰／92LEGENDARY　LA　ROSE　NOIRE", "黒薔薇 VS 黒薔薇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1992年７月に香港で公開され、香港映画史上初の175日間のロングラン記録を作ったドタバタ・コメディ。“黒薔薇”とは、1960年代の香港映画に登場した怪盗の名前で、それをパロディーとギャグを満載させ、現代に復活。レオン・カーファイが、ミュージカル・シーンも含めた怪演を見せ、電影金像奨（香港のアカデミー賞）で、主演男優賞に輝いた。売れない脚本家のウォンと親友のキュンは、ある日、麻薬組織の殺人現場を目撃した。二人は、警察の目をごまかすために、“黒薔薇参上”と書き残す。これがもとで、麻薬組織と本物の黒薔薇、ウォンに惚れているロイ刑事を巻き込んだ騒動となり……。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La frontiere de l'aube", "愛の残像", "", "", "http://image.eiga.k-img.com/images/movie/54362/315x210.jpg?2568", 108, "", "2012-06-30", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Os Famosos e os Duendes da Morte", "名前のない少年、脚のない少女", "", "", "http://image.eiga.k-img.com/images/movie/55772/315x210.jpg?3220", 101, "国際的に高い評価を得るブラジルの新星エズミール・フィーリョ監督による初長編作品。ブラジル南部の小さな町に母親と2人で暮らす16 歳の少年は、“ミスター・タンブリンマン”というハンドルネームを名乗り、日々現実から逃避するようにインターネットに没頭している。ある日、ネット上で神秘的な女性が映された動画を見つけ、魅了されていく。", "2011-03-26", "17,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("学校裏サイト", "学校裏サイト", "", "", "http://image.eiga.k-img.com/images/movie/54672/315x210.jpg?3888", 109, "インターネット上の誹謗中傷がいじめとして問題視されている「プロフサイト」「SNS」など、ウェブサイトの恐怖を描いた学園ドラマ。伊吹学園で、ある男子生徒が校舎から飛び降り自殺を図る。生徒会長の藤原は、自殺の原因であるいじめの裏に何かあるのではないかと案じ、ひとり事実の究明に奔走する。すると、学校のプロフサイトに登録している者たちによる奇怪なゲームの存在が明らかになっていく。", "2009-07-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Megamind", "メガマインド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "悪役のメガマインドは、正義の味方でライバルのメトロ・マンを殺してしまったため戦う相手を失ってしまい、退屈な日々を過ごしていた。そこで、メガマインドは新たな敵タイタンを作り出すが、タイタンは力を正義のために使わずに世界を破壊し始める。焦ったメガマインドは人生で初めて正義のために戦うことになる。声の出演にウィル・フェレル、ブラッド・ピット、ティナ・フェイ。監督は「マダガスカル」シリーズのトム・マクグラス。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Just Go with It", "ウソツキは結婚のはじまり", "", "", "http://ecx.images-amazon.com/images/I/51CQIKEPqTL._SL210_.jpg", 116, "アダム・サンドラー、ジェニファー・アニストン初共演のラブコメディ。独身であるにもかかわらず、結婚指輪をちらつかせてカラダ目当ての恋愛を楽しんでいる整形外科医ダニー（サンドラー）は、休暇中のハワイで理想の女性パルマーと出会う。本気で惚れたため、指輪を隠してデートを楽しんでいたダニーだったが、パルマーにその指輪を見られてしまう。そこでダニーは彼女に既婚だと嘘をつき、職場のアシスタント・キャサリン（アニストン）に離婚間近の妻の役を演じてもらうよう頼み込むが……。監督は「ビッグ・ダディ」「エージェント・ゾーハン」のデニス・デューガン。", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブ＆ポップ", "ラブ＆ポップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "庵野秀明が、初めて実写映画に挑戦。村上龍の小説を原作に、大胆な手法で迫っている。主人公は、16歳の吉井裕美。彼女は、トパーズ・リングに一目惚れする。その日、その店の閉店時間、午後９時までにどうしてもその指輪が欲しい。この“リアル”な気持ちがなくなる前に、リングを手に入れよう。“援助交際”を購入資金調達の手段に選んだ裕美は、様々な男性たちと出会い時を過ごしていく。全編を軽量ビデオカメラで撮影。渋谷の街が、ルポルタージュのように切り取られていく様は、庵野監督らしい編集の妙味。しかし、本作の見どころはビデオならではの映像の質感にある。客観性を排した、ゆがんだカメラアングル。そこには、従来の映画が求めてきた“奥行き”が完全に失われており、奇妙に背景を欠いた“生きることの実感”の薄さが、映像化されているようだ。また、ドキュメント班にはAV界の雄であり、デジカムの名手であるカンパニー松尾監督が参加。カメラを媒介に“世界”と触れ合ってきた二人の個人作家を起用した繊細なセンスも見逃せない。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("童黨／GANGS", "ギャングス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ツイ・ハーク監督作品の助監督を務めたこともあるラウ・アモン監督の映画デビュー作。香港の街を徘徊するストリート・ギャングたちの青春と抗争、そして逃亡の日々を、緊張感あふれる演出でリアルに描いていく。出演する若者たちはみな、実際のストリート・ギャングたち。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SECURITY UNLIMITED", "新 Mr.Boo！ アヒルの警備保障", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "香港の人気コメディアン、マイケル扮する警備会社の鬼隊長が巻き起こすドタバタ騒動を描いた大爆笑映画。強盗団の手先となってしまった気の弱い新入隊員をなんとか助け出そうと、アノ手コノ手の大救出作戦を実行に移す隊長ら数人の勇姿(？)であったが……。ことのてん末は？", "1981-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIXーSTRING　SAMURAI", "シックス・ストリング・サムライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ロックンロールとチャンバラ、ウエスタンの要素を融合させた、荒唐無稽の低予算アクション。核戦争後の楽園ロスト・ベガスを目指して旅をする、ギターと剣を持った流れ者バディ。孤児を連れて荒野を行く彼は、ベガスのキングの座を賭けて死神たちと壮絶なバトルを繰り広げる。香港でアクションを学んだ主演のJ・ファルコンが、華麗なカンフーを披露。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅高梁", "紅いコーリャン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "　カメラマン出身チャン・イーモウの初監督作品。デビュー作でベルリン国際映画祭グランプリを獲得するという快挙を成し遂げる。1920年代、18歳のチアウルは、病に伏す年の離れた酒屋の主人の元へ嫁に出される。しかし程なく主人が行方不明になってしまう。チアウルがまとう花嫁衣装や夕陽に照り返る大地、コーリャン酒など紅を効果的に配した画面構成は、イーモウ作品ならでは。紅はエロティシズムや自由へのエネルギーの象徴として描かれている。", "1987-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銃声／LAST DROP OF BLOOD", "銃声／LAST DROP OF BLOOD", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "才人、秋元康が監督を務め、多彩なアイデアを詰め込んだヤクザ映画。奇想天外なバイオレンス描写、意表を突くキャスティング、ツボを心得たキメのセリフなど、随所に秋元節が光る。暴力団、竜成会の会長が狙撃され、重傷を負った。事件は関西系ヤクザとの抗争が原因と思われたが、背後には身内の陰謀が。かつての組員、正一はカタギになっていたが、昔の弟分が事件に巻き込まれたことから、無視できなくなり……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIG　MOMMA’S　HOUSE", "ビッグ・ママス・ハウス", "", "", "http://ecx.images-amazon.com/images/I/315RCNHE8SL._SL210_.jpg", 99, "「バッドボーイズ」で人気俳優となったM・ローレンスが、特殊メイクに身を包み爆笑を誘うコメディ。マルコム捜査官は凶悪犯を張り込み中、その恋人シェリーの母親に変装するハメに。しかしシェリーと話は合わない、作る料理はめちゃくちゃと散々だった。そんな中、本物の母親が家に戻ってくることになり、事態は思わぬ方向へ！", "2001-02-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　CONVOYEURS　ATTENDENT", "24時間４万回の奇跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "賞品のスポーツカー欲しさに“ドア開閉世界新記録”に挑むダメ親父とでくの坊の息子。しかし記録挑戦中、息子にトラブルが……。必死に生きるベルギーの家族をユーモラスに描いたちょっと切ないハートフル・ドラマ。バカバカしい世界記録にトライしようとする父親を「ありふれた事件」のB・ポールブールドが好演。", "1999-01-01", "37,2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A bout portant", "この愛のために撃て", "", "", "http://image.eiga.k-img.com/images/movie/56612/315x210.jpg?4155", 85, "看護師助手のサミュエルは妊娠中の妻ナディアを何者かに誘拐され、交通事故で病院に担ぎ込まれた指名手配犯サルテを、警察の監視下から連れ出すよう犯人から要求される。理由も分からぬまま要求に従うサミュエルは、やがて警察からも追われる身となるが、妻を助けるため奔走する。デビュー作「すべて愛のために」（2008）がポール・ハギスにより「スリーデイズ」（10）としてハリウッドリメイクもされたフレッド・カバイエ監督の第2作で、パリを舞台に描くサスペンスアクション。", "2011-08-06", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KAMIKAZE TAXI", "KAMIKAZE TAXI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "最愛の恋人をヤクザに殺されたチンピラの達男は、ヤクザに復讐しようとするが、逆に自らも命を狙われる状況に。逃亡生活の中、達男はペルー育ちの日系人で、タクシー運転手をしている寒竹と出会う。熾烈を極める組からの追及の手を逃れて逃避行を続けるうちに、二人はいつしか奇妙な友情で結ばれていく。東京ファンタスティック映画祭で絶賛され、ビデオ発売後にロードショー公開された経緯を持つ作品。悪徳政治家、新興宗教、在日外国人など、日本の抱える様々な問題を内包しているのも見どころの一つ。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神田川淫乱戦争", "神田川淫乱戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "「SCHOOL DAYS」「しがらみ学園」などの８ミリ作品で高い評価を集めていた黒沢清の記念すべき35ミリ・デビュー作。窓下に神田川を臨む都内某所のアパートの２階に住むOL・明子と女子大生・雅美が、川向こうのマンションの一室で母親に犯されている少年を発見。この少年を助け出すべく二人は行動を開始する。第１次攻撃はマンション正面玄関からの突入。だがこれにはあえなく失敗。第２次攻撃では神田川を渡って直接少年の部屋に突入するが、これも失敗。第３次攻撃で、ついに突入に成功するが……。いわゆるピンク映画として撮られた低予算作品。だがそれまでの８ミリ作品でゴダール的手法、長回しの多用など、普通の映画ではないもう一つの映画、変わった映画の作り手として評価されがちだった黒沢清が、むしろきわめて正統派的なジャンルで勝負し続けていく決意を示した作品として非常に貴重なものといえるだろう。周防正行が助監督の一人として参加、マンションの管理人役で出演もしている。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("斬る", "斬る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "天保４年、上州の地。二人の男がこの地に現れた。一人は、かつて武士を捨てたヤクザの源太、もう一人は百姓を捨て武士になろうと血気盛んな半次郎。当地の家老殺害事件をきっかけとした権力者対若手武士の対決で敵味方に別れながらも、ついには二人して改革運動を担っていく……。山本周五郎作『砦山の十七日』を岡本喜八監督が映画化。岡本は本作までの東宝系娯楽路線にあきたらず、同年、続いてATGで「肉弾」を発表した。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TREMORS　THE　LEGEND　BEGINS", "トレマーズ４", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "地中から現れる怪物との死闘を描くシリーズの第４弾は、西部劇の時代にまでさかのぼってストーリーが展開。ウエスタン活劇を加えたモンスター・パニック映画の１本。小さな炭鉱町に伝説の凶暴な怪獣“グラボイズ”が出現し、家屋を破壊し人々を襲う。ガンマンたちが集結し、立ち向かっていくが……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昨日消えた男", "昨日消えた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "江戸中期、同心に変装した長屋に住みついた八代将軍・吉宗は、浪人・大橋兼四郎と知り合う。二人は、己之吉という男の死をキッカケに、幕府転覆をたくらむ美濃屋の陰謀を察知し、これに立ち向かっていく。“遠山の金さん”の将軍版とも言える、市川雷蔵の推理時代劇。相棒の剣豪に扮する宇津井健がもうけ役。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("安寿と厨子王丸", "安寿と厨子王丸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "森鴎外の名作『山淑太夫』を、田中澄江が脚本化した、東映長編動画の第４作。同じ時代劇ながら、第２作の「少年猿飛佐助」の美術により洗練さを加えて、動く日本画ともいうべき仕上がりを見せている。陸奥の国に暮らす岩木判官の一家は、横暴な鬼倉陸奥守の怒りを買い、父は流刑の地で死去。母と生き別れになった安寿と厨子王丸の姉弟は、由良の長者・山淑太夫に売られてしまう。つらい使役の果てに安寿は入水して命を絶つ。脱出して京の都へたどりついた厨子王丸は、成人して陸奥の国守に任ぜられる。その後ようやく姉の死を知った彼も、母とだけはめぐり会うことができた。安寿のキャラクターのモデルを佐久間良子が務め、またその声の出演で、アニメ声優を体験した。気品さえ漂わせた作画に注目すべき反面、アニメーション的な面白さは、厨子王丸と大グモとの対決など、わずかなシーンに留まっている。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IMAGINE: JOHN LENNON", "イマジン／ジョン・レノン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "オノ・ヨーコ私蔵のレノンに関する200時間にも及ぶフィルムとビデオに、新たに彼の親近者によるインタビュー映像を交えて完成したドキュメンタリー。作品はミュージシャンでありアーティストであるジョン・レノンの生涯を、少年時代、ビートルズ時代、ソロ時代そして1980年12月８日の衝撃の死までを、彼の妻、息子たちのインタビューによって浮き彫りにする。ジョン自らがナレーションを担当。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女囚性欲魔", "女囚性欲魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "ヤクザ映画で活躍する和泉監督の1970年代のアクション・エロス。レイコの記憶を取り戻すため刑務所から脱走した女囚・マキ、トシエ、レイコの３人が、新たなる犯罪に巻き込まれていく。それぞれ複雑な事情を抱える女たちの業が痛々しい。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUDGEMENT AT NUREMBERG", "ニュールンベルグ裁判", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 179, "ドイツのニュールンベルグで行われた、連合国によるナチ戦犯の戦争裁判の模様を描いた法廷ドラマ。かつてナチの司法大臣を務め、第三帝国憲法起章にかかわったヤニングをはじめ、司法関係者を被告としたこの裁判劇によって、あの非人道的なユダヤ人虐殺の元凶が誰にあったのかを、鋭く問いかけていく。ドイツ人側弁護人ロルフに扮した、M・シェルが、一世一代ともいえる熱演を披露。その他、法廷に集まる裁判長、検事、証人に大スターをそろえているのも目を引く。特に、絞首刑になったドイツ軍将軍の未亡人に扮した、M・ディートリヒが、強烈な印象を残す。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裏階段", "裏階段", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "キャバレーのピアノ弾き、木島健一は海堂という男から、遺産相続に絡む取引のために妹、理枝のニセの許婚者になりすますように頼まれる。だがその裏には２年前に起きた宝石強盗事件に絡んだ陰謀があった。身の危険を感じながらも、理枝を愛し始めた健一はあえて渦中に身を投じるのだが……。田宮が主演した快作サスペンス。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOHNEN 定の愛", "JOHNEN 定の愛", "", "", "http://image.eiga.k-img.com/images/movie/53612/315x210.jpg?4692", 0, "カメラマンのイシダは海岸で出会った謎の老紳士に、彼の妻・サダの撮影を依頼される。サダの妖艶な魅力に一瞬で心を奪われたイシダは撮影のことなど忘れて彼女と激しく愛し合う。やがて2人の情念は時空を超えて昭和初期の東京へと辿り着き……。大島渚監督の「愛のコリーダ」などでも描かれた“阿部定事件”を、「鬼火」「皆月」の望月六郎監督が映画化した官能ドラマ。猟奇殺人者・阿部定に扮するのは「花と蛇」シリーズの杉本彩。", "2008-05-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("「超」怖い話A〈アー〉闇の鴉", "「超」怖い話A〈アー〉闇の鴉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "のべ14巻を発行し、同一シリーズの怪談小説としては日本で最長の部類に含まれる実話系ホラー“「超」怖い話”シリーズ。13作目を映画化した本作は、身近なコンビニでの恐怖体験をリアルに描いている。「ピンポン」のCGや「アップルシード」などを手掛けたデジタル・フロンティアがCG制作を担当し、原作の慄くような描写を見事に映像化。女子高生・奈緒がバイトするコンビニは異様な空気と異臭が漂っていた。店主は、大手チェーンの営業マンの大ケガを聞いて狂喜し、その妻は人格破壊していて、明らかに様子がおかしい。そしてある種の客にだけ異変が起こる。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Clash of the Titans", "タイタンの戦い（2010）", "", "", "http://image.eiga.k-img.com/images/movie/54532/315x210.jpg?1697", 106, "「アバター」のサム・ワーシントンが主演する、ギリシャ神話に登場する神々の壮絶な戦いを描いた3Dアクション。監督は「トランスポーター」シリーズを手がけるルイ・レテリエ。神と人が共存していた時代、神々は己の欲望を満たすため、激しい権力争いに明け暮れていた。ある日、そんな神々に対し人間の王が反乱を起こす。人間の創造主でもある神々の王ゼウスはこれに激怒し、人類を滅ぼそうと冥界の王ハデスを解き放つが、人間の心を持つ神ペルセウスが人類救出のため立ち上がる。", "2010-04-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOUNG WINSTON", "戦争と冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 157, "自伝『わが半生』をもとに、従軍記者時代から26歳で総選挙に圧勝し、政治家としての第一歩を踏み出すまでの、若き日のチャーチルの姿を描く。チャーチルを熱演するS・ウォード、彼の両親を演じるR・ショウ、A・バンクロフトら、名優が一堂に会した力作。", "1972-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンの法界坊", "エノケンの法界坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "浅草のボードビルで名声を上げたエノケンが、一座とともに東宝へ入社しての第１回作品。斎藤監督は得意のギャグを豊富に取り入れ、スピーディーなスラップスティック映画に仕上げている。エノケン映画の最高傑作とこの作品を推す人も多い。インチキ坊主の法界坊が、宝物の掛軸をめぐる争いに巻き込まれたり、道具屋の美しい娘に惚れて騒動を引き起こしたりとストーリーは他愛ないが、シャンソンやジャズの名曲を独特の節まわしで歌うエノケンの歌が素晴らしい。本格的ミュージカルをやるのが夢だったというエノケンだけあって、難曲を歌唱力で歌いこなしている。日本最初のヒップホップといえるだろう。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Legend of Lylah Clare", "女の香り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "　かつてハリウッドの人気女優だったライラ・クレアが他界してから20年が経った。彼女の半生の映画化が決まり、その主演にライラそっくりのエルザという新人が選ばれる。監督はライラの元・夫ザーカンが務めることに。映画の撮影が進む一方、エルザは、ライラをめぐる複雑な人間関係と彼女の死に関する謎を知ることに……。キム・ノバクがライラとエルザの二役を演じる。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OLIVER TWIST", "オリヴァ・ツイスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "孤児のオリヴァは奉公先を飛び出し、ロンドンで子供たちを使って様々な悪事をはたらいているフェイギンの傘下に入る。ある日、老人から財布をすったところを捕らえられて起訴されそうになったが、老人の救いによって難を逃れる。老人の家で、生まれて初めて幸せな日々を過ごすオリヴァだが、隠れ家が警察に知られることを恐れたフェイギンは、老人の邸からオリヴァを連れ出させて……。名匠リーンが、ディケンズの名作に挑戦した。", "1948-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY MAN GODFREY", "いとしの殿方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1936年にキャロル・ロンバートとウィリアム・ポーエルの主演で作られた風刺喜劇「襤褄と宝石」の戦後版。裕福な有閑人種が集まって繰り広げるゲテもの品評会。ブロック家の姉妹コーデリアとアイリーンは日ごろからゲテもの集めを競っていたが、ある日アイリーンは浮浪者ゴッドフリーを拾い、彼を品評会に出す。アイリーンは一等賞を取るが、良心の呵責から彼を執事に雇って……。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パセリ", "パセリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "希望を失って自暴自棄になったストリート・ミュージシャンの青年と“パセリ”と名乗る少女の奇妙な数日間の交流を綴った切ないファンタジー。「非・バランス」の派谷恵美が透明感のある演技で、自ら苦悩しながらも青年を勇気づける謎の少女を好演し、不思議な世界観に深みを与えている。ミュージシャンの一輝は夢を抱いて上京したが、デビューは白紙になり、恋人にもフラれてしまう。そんな時、パセリと名乗る少女が現れ……。", "2005-05-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELVIS　HAS　LEFT　THE　BUILDING", "トラブル IN ベガス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "プレスリーのそっくりさん大会が開かれるラスベガスを舞台に、出場者に図らずも災厄をもたらしてしまう美女の、数奇な恋を描いたラブ・コメディ。キム・ベイシンガーがヒロインをキュートに好演する。プレスリーのコスプレを披露する大物スターの特別出演も見逃せない。なぜかプレスリーのそっくりさんの事故死現場に何度も遭遇する化粧品販売員ハーモニー。そんな彼女の前に理想の男性が現れるが……。", "2005-06-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COLD BLOODED", "ヤング・ヒットマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "マフィアの下でノミ屋として働く孤独な青年コズモは、ある日ボスのゴードンからヒットマンになることを命じられる。それまで銃を手にしたこともなかったコズモだが、彼の才能は天才的なものだった。命じられるままに人を殺していくコズモは、ヨガ教室の講師ジャスミンと出会い、ヒットマンから足を洗おうとするが……。TVドラマシリーズ『ビバリーヒルズ高校白書』で人気のJ・プリーストリーが主人公を演じ、M・J・フォックスがプロデュースした青春バイオレンス。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUN FURY", "限りなき追跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "駅馬車を襲われ、婚約者を連れ去られた男が限りなき追跡を始めていくウエスタン。隻眼の監督として知られる名匠R・ウォルシュ監督作品で、オリジナルは3D映画として製作されたが、日本での公開は通常上映された。無法者の首領役のP・ケリーをはじめ、クセ者俳優が脇を固めている。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RENT−A−KID", "レンタル・キッズ　お子様貸します", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "レンタル会社の経営者ハリーは10日間、息子が院長を務める養護院の子供たちの面倒を見るハメに。だが、したたかな彼は子供をレンタルすることを発案。子供を作るかどうかで悩む若い夫婦ラスとヴァレリーに、問題児の３兄妹と犬１匹を貸し出す……。何とか３人一緒に養子にしてもらおうと考える子供たちが大騒動を繰り広げるキッズ・コメディ。やがて、子供たちと夫婦の間に温かい交流が生まれていくという定番の作りが心地よい。子供たちに味方するマンションのドアマン役のC・ロイドが、さすがの好演を見せる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRIZZLY", "グリズリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アメリカの森林公園で、突如現れた巨大な熊による殺人事件が連続的に発生する。脅威にさらされ逃げまどう人々。そしてついに人間対人喰い熊の壮絶な闘いが始まった。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アギ・鬼神の怒り", "アギ・鬼神の怒り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "平安末期の説話集『今昔物語』の中のエピソード“安義橋の鬼”をヒントに、中学時代から８ミリ映画を撮っていたという当時23歳の早川光が、監督・脚本および撮影を担当。ワーグナーの音楽を全編に使用した新感覚の古典伝奇映画。人間が鬼へと変身するシーンの特殊メイクアップは見もの。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAZORBACK", "レイザーバック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "オーストラリアの大草原の小さな町ガムラを舞台に、伝説の巨獣レイザーバックに孫を奪われた老カンガルーハンター、ジェイクの執念の復讐劇を描く異色ホラー。プロモーション・ビデオから劇場映画に進出したマルケイ監督の第１作。", "1984-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TILT", "ブルック・シールズのプリティ・ギャンブラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "B・シールズ扮するピンボールの天才少女ティルトが巻き起こすドタバタ騒動を描いた青春映画。相棒ニールとともに各地のピンボール名人と対戦し、腕に磨きをかけた彼女は、ついにピンボールの神様レーメンスと対決するのであるが……。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOLLOW　THAT　DREAM", "夢の渚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "未開の国有地の所有を宣言して土地を得た、気ままに暮らす一家。そこに賭博場を開こうとする悪辣な男が現われ、一家の権利の買収を持ちかける。これを拒否した一家は騒動に巻き込まれ……。1950年代ののどかなアメリカの片田舎を舞台にしたコメディ。一家の長男に扮したプレスリーは歌でも活躍し、タイトル曲をはじめ５曲を披露。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い帽子の女（Die Frau mit dem ro¨ten Hut）", "赤い帽子の女（Die Frau mit dem ro¨ten Hut）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "芥川龍之介の作品とも噂される同名ポルノ小説を、神代辰巳監督が西ドイツの協力を得て撮り上げた文芸ポルノ。インフレの嵐が吹き荒れる1923年のドイツに先輩の誘いでやってきた主人公の“私”が、赤い帽子の女にひかれてゆき、やがて破滅を迎える。退廃を鋭く映し出す姫田真左久の撮影は見事だ。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHA-CHI-HO-KO", "SHA-CHI-HO-KO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "名古屋のFM局でDJを務めるKENの元に一人の少女から自殺を匂わすFAXが送られてくる。KENは少女を探し出し、自殺を止めさせることが出来るのか……。みうらじゅんのプロデュースでCDデビューを果たした“OKAMA NO KEN with　スーパーホモンキーズ”の初主演映画。心優しいオカマたちが奮闘する痛快青春グラフィティー。ゆうばり国際ファンタスティック映画祭2002にて、ゆうばりファンタランド大賞受賞。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幕末純情伝", "幕末純情伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "“新選組の沖田総司は女だった”という大胆な発想によって描いたつかこうへいの同名小説を映画化。総司をめぐって恋の対決を繰り広げる土方蔵三や坂本竜馬らの姿をユーモラスに描いたコメディ。竜馬役の渡辺謙は病気のため、約２年間休業していたが、本作で見事な映画復帰を遂げた。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPEECHLESS", "眠れない夜はあなたと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ある晩、コンビニで睡眠薬の最後の一箱をめぐって知り合ったケビンとジュリア。強度の不眠症という共通する悩みを抱えた二人はすぐに意気投合、真夜中のドライブへ。お互いに熱い恋の予感を感じた二人だが、彼らにはもう一つ共通点があった。なんとそれぞれが、敵対している政治家のスピーチライターだったのだ！　果たして、ケビンとジュリアの恋の行方は……。公私ともに友人同士であるというM・キートンとG・デイヴィスの息のあった演技が楽しめるロマンティック・コメディ。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAISERS VOLE´S", "夜霧の恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「大人は判ってくれない」「二十歳の恋」のアントワーヌ・ドワネル少年を主人公として作った、トリュフォーの自伝的なシリーズの第３作。アントワーヌ少年が兵役を終えて社会に出て、私立探偵の職に就き、恋愛を体験していく過程を温かい視点で演出した。DVDには「アントワーヌとコレット」が併録されている。", "1968-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BETWEEN THE KNEES", "膝と膝の間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "少女時代に白人の音楽教師に膝を愛撫されていた現場を母親に見つかってしまった主人公。以来、彼女は性を厳しく管理され続けたせいで、性行動がゆがんでしまい、男にもてあそばれればもてあそばれるほどエクスタシーを感じる体となっていく。韓国の異才イ・チャンホが濃厚な性描写で描いた意欲作。不協和音で構成された音楽が、ヒロインの内面を浮き彫りにする。", "1984-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊賀の水月", "伊賀の水月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "阪東妻三郎が荒木又衛門に扮し、伊賀・鍵屋の辻における義父のあだ討ち、36人斬りが描かれる大チャンバラ時代劇。鬼気迫る阪東の殺陣、効果的な構図でそれを捉えた名コンビ・池田富保の演出など、格調高い乱闘の美学を見せてくれる。戦後再公開の折、「剣雲三十六騎」と改題された。DVDは「阪東妻三郎傑作選　DVD-BOX」に収録。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おもひでぽろぽろ", "おもひでぽろぽろ", "", "", "http://ecx.images-amazon.com/images/I/51yWi3IUC8L._SL210_.jpg", 119, "小学５年生の少女・タエ子が主人公の同名コミックをもとに、27歳に成長したタエ子の物語を新たに加えて女性の精神的な自立を描く。1982年の夏、山形の義兄の実家へ向かう、東京のOL・岡島タエ子は、途中16年前の様々な出来事を回想する。山形では25歳の農業青年・トシオが彼女を迎え、田舎ですごす休暇の中で、タエ子はまた昔の思い出を甦らせるのだった。そして田舎での暮らしの中で、トシオとも打ちとけたタエ子は、わだかまる思い出から少しずつ解き放たれる。10日が過ぎて東京へ戻る日、彼女はある決心をする……。回想シーンをソフトなタッチで、1982年のシーンをリアルなタッチで描き、それぞれの時代のリアリティを描出しようとした手法は新鮮。すぐれた“日本映画”となっただけでなく、アニメの表現として異色のリアリズムを感じさせる女性の青春ドラマだ。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("六神合体 ゴッドマーズ", "六神合体 ゴッドマーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "'82年から'83年にかけてオンエアされた連続TVアニメの劇場版。70年代にTVを席巻した合体系ロボット・アニメの進化型となるSF映画だ。1999年の世紀末が舞台。主人公は自分が地球を狙う異星から地球へ刺客として送り込まれたことを知るが、育ての父がいる地球を守るために、ロボットを駆使して異星人たちと対決する。主人公と彼の実兄や実父との戦いが壮絶な出来。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("父後七日", "父の初七日", "", "", "http://image.eiga.k-img.com/images/movie/56152/315x210.jpg?2701", 92, "台北で働くアメイは、父の訃報を受けて急いで田舎に戻る。一族がそろい、叔父アイーの指図で7日後に葬儀が執り行われることになるが、古いしきたりにそった葬式に大騒ぎで悲しむ間もない。そんな時、ふとしたことから父との思い出がよみがえる……。台湾中部の田舎町を舞台に、父の死から葬儀までの7日間を優しさとユーモアを交えて描くヒューマンドラマ。台湾のアカデミー賞にあたる金馬賞で7部門にノミネートされるなど、高い評価を受けた。", "2012-03-03", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Amours d'Astree et de Celadon", "我が至上の愛　アストレとセラドン", "", "", "http://image.eiga.k-img.com/images/movie/53552/315x210.jpg?1616", 109, "17世紀にパリの貴婦人たちの間で流行した小説「アストレ」を、ヌーベル・バーグの巨匠エリック・ロメールが映画化。5世紀のローマ時代、羊飼いのアストレと青年セラドンは純粋に愛を育んでいたが、お互いの両親の不仲に悩んでいた。村の祭りの日、2人は両親の目を逸らすため別の相手と踊っていたのだが、アストレはセラドンが本気で他の女性に惹かれてしまったと思い込み……。", "2009-01-17", "2,4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIXTEEN CANDLES", "すてきな片想い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "J・ヒューズ監督の処女作。16歳の誕生日を迎えた少女の数日間にわたる心のときめきを、みずみずしく描いた作品。監督と初コンビを組んだリングウォルドの好演も印象深い。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トリック　劇場版2", "トリック　劇場版2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "深夜ドラマとして放送され人気を博した「TRICK」シリーズの劇場版第2弾。主演は仲間由紀恵と阿部寛、監督はドラマ版からメガホンをとってきた堤幸彦。天才物理学者・上田次郎の研究室に、青沼という青年が訪ねてくる。彼は10年前に霊能力者・筐神（はこがみ）佐和子により連れ去られた幼なじみの美沙子を救い出してほしいと懇願してくる。上田は、失業中のマジシャン・山田菜緒子を連れ、筺神率いる「箱のゆーとぴあ」と呼ばれる宗教団体が待ち受ける筺神島へと向かうが……。", "2006-06-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピンク・レディーの活動大写真", "ピンク・レディーの活動大写真", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "70年代後半、“昭和の時代”を数々の大ヒット曲で彩ったピンク・レディー。ミーとケイの二人が初主演した'78年のお正月映画。スーパー・アイドルの魅力を引き出すために、持ち歌の内容に合わせたオムニバス・ドラマ風に展開していく。当時、時代の先を行くプロモーション・ビデオのイメージ映像が連続。『ペッパー警部』調西部劇から『UFO』のSF映画風まで、多彩かつポップな映像が楽しい。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIGER CLAWS", "タイガークロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "おとり捜査に嫌気がさしてる反抗的な女刑事マスターソンと、過剰捜査のため停職中の刑事タレク。二人が組んで、連続殺人“死の配達人”事件の担当となる。この事件の鍵は“タイガークロー”といわれる格闘技の必殺技だった。格闘技のスペシャリストであるマスターソンとタレクは、身分を隠し自分の肉体と技を武器として、暗黒街へ入っていく。女刑事に扮するC・ラスロックはマーシャル・アーツ（格闘技）において女性で唯一“ナンバー・ワン・ウェポン”の称号を持ち、「バック・トゥ・ザ・フューチャー２」のスタント・ウーマンを経て、主役を射止めた女優。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FEAST OF THE WHORES", "不倫体験", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "主人公は美しいアドリア海に面したダルチアで民宿を営む20歳の青年。ある日彼の宿にドイツ人夫婦がやって来る。そのドイツ人の若妻に主人公の青年は誘惑される。やがて二人は結ばれ快楽の日々を過ごすのだが、青年は次第に若妻を虐待しはじめる。社会主義国の一国だったユーゴスラビアの珍しく大胆な描写が話題になった愛の物語。", "1988-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜明けのランナー", "夜明けのランナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "郷里の兄がガンで倒れたために、東京を離れることになった青年が、かつて別れた恋人ともう一度だけ会おうとし、負傷した足を引きずりながら約束の場所である朝方の公園に向かうが……。渡辺徹の映画デビュー作で、城戸賞を受賞した脚本家・中岡京平の第１回監督作品。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OH！ BROTHERS", "オー！ ブラザーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「イルマーレ」をはじめ誠実な役柄が多かった美男俳優、イ・ジョンジェが汚れ役に挑戦した感動の人情コメディ。主人公サンウの元に、母を捨てて別の女に走った父親の死が知らされた。しかもその妻が蒸発中の上に父は借金まみれで、その借金をサンウが肩代わりするはめに。さらに、彼の前に腹違いの弟ボングが現れて……。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Halloween", "ハロウィン（2007）", "", "", "http://image.eiga.k-img.com/images/movie/34182/315x210.jpg?2245", 109, "デスメタル界のカリスマでありながら映画監督としても活躍するロブ・ゾンビが、ジョン・カーペンター監督の「ハロウィン」（78）をリメイク。出演に「時計じかけのオレンジ」のマルコム・マクダウェル、「X-メン」のタイラー・メインら。友達にも家族にも愛されない孤独な少年マイケルは、ハロウィンの夜、家族やその恋人らを惨殺し、厳重警備の精神病院に収容される。そこで著名な精神科医の治療を受けることになったマイケルだが……。", "2008-10-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEEL JUSTICE", "ランボー者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "普通の生活に適応できなくなったベトナム帰還兵が、アメリカで刑事として成功したベトナム人一家を狙う麻薬組織に戦いを挑むアクション映画。組織のボスは、かつてともに戦ったベトナムの将校だった。“ランボー・シンドローム”映画の一つ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("道", "道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "長距離トラックの運転手ジャン・ギャバンと、ドライブインのウェイトレス、フランソワーズ・アルヌールの哀しい大人の恋を描いたフランス映画の名作「ヘッドライト」（1956）の日本版リメイク。仲代と藤谷が恋に落ちるロード・ムービー・スタイルの作品。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Survivre avec les Loups", "ミーシャ　ホロコーストと白い狼", "", "", "http://image.eiga.k-img.com/images/movie/54352/315x210.jpg?3982", 119, "1942年、ベルギーの首都ブリュッセル。学校に行っている間に両親がナチスに連行されてしまったユダヤ人の少女ミーシャは、両親が東の方に連れて行かれたと知り、コンパスひとつを頼りに東へ向けて歩み始める。やがて、森の中で行き倒れそうになった彼女のもとに、一匹の白い狼が現れ……。ホロコーストから逃れた少女が、狼とともに放浪しながら生き延びる姿を描いたドラマ。監督・脚本は「女優マルキーズ」のベラ・ベルモン。", "2009-05-09", "2,37,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA HABANERA", "南の誘惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1950年代に「悲しみは空の彼方に」などでハリウッド・メロドラマの巨匠として活躍したダグラス・サークが、戦前ドイツでまだデトレフ・ジールクと名乗っていたころの作品。スウェーデン娘アストリーは、伯母に連れられキューバの西にあるプエルトリコに旅をした。地元の豪族ペドロと恋に落ちた彼女は伯母をやり過ごして彼と結婚した。歳月はたち、一人息子も成長したが、ペドロはアストリーを奴隷のように扱い、二人の心は離れていった。そこへ伝染病の研究のために故国からナーゲル博士がやって来た。ナーゲルは様々な妨害に遭いながら研究に着手するかたわら、アストリーと再会、幼なじみの彼女にひそかに思いを寄せていたことを告げた。ペドロは嫉妬心に燃えて、ナーゲルヘの妨害は生命を脅かすまでになるが、折から島に熱病が猛威をふるいはじめ、ペドロも病に倒れてしまうのだった。どこからともなく画面にあふれ出る南国の感じをジックリ味わいたい作品。", "1937-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DESCENT", "ディセント", "", "", "http://image.eiga.k-img.com/images/movie/33552/315x210.jpg?2852", 99, "「ドッグ・ソルジャー」のニール・マーシャルが監督・脚本を手掛け、英国インディペンデント映画賞で最優秀監督賞を受賞したサスペンス・ホラー。アパラチア山脈奥地の地下洞窟へ冒険旅行に訪れた女性6人の仲良しグループ。しかし、落盤によって出口がふさがれ、迷宮のような洞窟に閉じ込められてしまう。不安と疲労にさいなまれながら出口を求めて暗闇の中をさまよう彼女たちを待ち受けていたのは、想像を絶する恐怖だった……。", "2006-07-15", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPLIT SECOND", "スプリット・セカンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "冷酷な殺人鬼や人造人間の役が似合うR・ハウアーが、巨大な殺人エイリアンを追う刑事に扮したSFアクション。2008年のロンドンで、手当たり次第に人間を襲うエイリアンが出現。それは、人間の体を引き裂き、心臓をむしり取るという残虐な手口だった。過去にこの怪物によって同僚を殺された刑事ストーンは、怪物を追ううちに、それが想像を絶するほど巨大なパワーを持つことを知る。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Afro Samurai", "アフロサムライ", "", "", "http://image.eiga.k-img.com/images/movie/34282/315x210.jpg?2326", 116, "サミュエル・L・ジャクソンが製作総指揮と声優を務め、「ブレイブストーリー」のGONZOがアニメーション制作を手掛けた日米合作の時代劇アニメ。世界一の剣客を意味する一番ハチマキの男に父親を殺された少年。一番ハチマキを持つ者と戦えるのは、二番ハチマキを手に入れた者のみ。やがて成長した少年は、二番ハチマキを持つアフロサムライとなる。父の仇を討つべく旅を続ける彼に、謎の宗教集団“無無坊主”の刺客が次々と襲いかかり……。", "2007-10-27", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("林則徐／LIN　TSE−HSU", "阿片戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "19世紀中国の阿片戦争を、阿片取り締まりにあたる林則徐の姿を中心に描いた歴史映画。19世紀、イギリスの悪徳商人たちは阿片を中国に運び込み、人々の生活を汚染していた。林則徐は清朝の命でその禁止にあたるが、イギリスは武力で介入。清朝の役人も次第に動揺し始める。「カラスと雀」(1949)で名高いチョン・チュンリー監督作品。", "1959-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴーゴー仮面ライダー", "ゴーゴー仮面ライダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 24, "悪の組織ショッカーから人類を守るため立ちあがった仮面ライダーの活躍を描く特撮テレビシリーズ「仮面ライダー」の第13話「トカゲロンと怪人大軍団」を再編集し、「東映まんがまつり」の1編として劇場公開。ショッカーは東洋原子力発電所のバリアを破るため、サッカー選手の野本を改造して怪人トカゲロンを誕生させる。強力なキック力を持つトカゲロンに一度は敗北した仮面ライダーだったが、新たな技・電光ライダーキックを完成させて再び戦いを挑む。", "1971-07-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DEFIANT ONES", "手錠のまゝの脱獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "豪雨のハイウェイで、一台の囚人護送車が転落。この車から白人のジャクソンと黒人のカレンが脱走した。二人は手首と手首を手錠でつながれている。だが、相手に対する人種的偏見を互いに持っていて……。人種問題に脱走のサスペンスを絡ませたS・クレイマーの第３作。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROWN’S　REQUIEM", "ブラウンズレクイエム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "J・エルロイの処女作『レクイエム』を映画化したハードボイルド・ミステリー。ゴルフ・キャディをしている謎めいた巨漢男から、犯罪組織にかかわった彼の妹を守ってほしいと頼まれた私立探偵フリッツ・ブラウン。これをきっかけに次々と思いがけない事件に遭遇する彼は、かつて警察時代の自分を免職にした因縁の仇敵と対面することに。コワモテのバイプレーヤー、M・ルーカーが、しがない酔いどれ探偵を味わい深く演じている。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LASERMAN", "激光人 レーザーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "P・ワンが監督した前作「グレート・ウォール」とはまったく違うコミカルな犯罪ドラマ。レーザー光線の科学者アーサー・ワイズは、実験に失敗して職を失う。そんな彼を雇い入れたのは、彼に殺人光線を開発させようとする悪の組織。その陰謀に気付いたアーサーは、N.Y.市警のルー警部に助けを求めるが……。テーマ曲は坂本龍一によるもの。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A CHANGE OF SEASONS", "LOVEシーズン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "結婚21年目のエバンズ夫妻。大学教授の夫は、教え子の女学生と浮気。それに対抗しようとばかりに、妻のケアレンも若い建築家を引っぱり込む。この浮気者同士の二組のカップルが、山荘で一緒に休暇を過ごすことになり……。大人の愛の騒動を描いたコメディ。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人生劇場", "人生劇場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 167, "小説家を志す青成瓢吉、任侠道に生きる飛車角、吉良常、宮川らの生きる姿と、彼らをめぐる女たちの情熱を描く大河ドラマ。加藤泰はお得意の長回し、ローアングルのカメラで、男と女の愛を情感たっぷりに描いた。大正５年、ヤクザを殺して服役していた吉良常が出所した日の夜、吉良常の主人であった瓢太郎がピストル自殺をする。小説家を志して上京していた瓢太郎の息子・瓢吉は、それを聞いて急いで帰郷した。葬儀を済ませると二人は上京し、飛車角と知り合う。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Gnome-Mobile", "小びとの森の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ウォルト・ディズニー製作によるファンタジー映画。製材会社の社長とその2人の孫は、森の中で小びとの妖精ノームたちに出会う。ノームたちによれば、人間が木を切るせいで、森に住んでいたノームたちがいなくなってしまったという。彼らはノームの青年のお嫁さんを探すため、旅に出るが……。監督は「メリー・ポピンズ」のロバート・スティーブンソン。ウォルター・ブレナンが、人間の社長と小びとのお爺さんの2役を演じている。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アントニー・ガウディー", "アントニー・ガウディー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "スペインの偉大な建築家アントニー・ガウディーの建築作品の世界を描いた記録映画。カタローニャ地方の風土やバルセロナの歴史と文化などを背景として、グエル公園や聖テレサ学院など、彼の主要な作品を網羅しており、その思想や表現がじっくりと味わえる。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lincoln Lawyer", "リンカーン弁護士", "", "", "http://image.eiga.k-img.com/images/movie/56162/315x210.jpg?2345", 119, "ハードボイルド作家マイクル・コナリーの同名小説をマシュー・マコノヒー主演で映画化したサスペンス。高級車リンカーンの後部座席を事務所代わりにロサンゼルスを駆けめぐる敏腕弁護士のミック・ハラーは、女性への暴行容疑で告発された資産家の御曹司ルイス・ルーレの弁護を依頼される。無実を主張するルイスの身辺調査を進めるうちに、ミックが過去に担当した事件と今回の事件とに類似点が見つかり、そのことからミックはかつてない危機に巻き込まれていく。共演にマリサ・トメイ、ライアン・フィリップ、ウィリアム・H・メイシーら。", "2012-07-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stanno tutti bene", "みんな元気", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "　「甘い生活」のマルチェロ・マストロヤンニとフランス人女優ミシェル・モルガン、往年の名優が共演。シチリアに住む70歳のマテオは、バカンスにも帰ってこない5人の子どもたちを訪ねてイタリア各地を旅する。しかしそこには、父のために幸せを取り繕う子どもたちの姿があった。ジュゼッペ・トルナトーレ監督の前作「ニュー・シネマ・パラダイス」でトト少年を演じたサルバトーレ・カシオが、息子の幼少期を演じている。", "1990-12-15", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Welcome to People", "ウェルカム・トゥ・ピープル（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CHAMP", "チャンプ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "いつの日か栄光の座に返り咲くことを夢見る元世界チャンピオンのボクサーと、彼を尊敬しチャンプと呼んで慕う息子。強烈なヒューマニズムで、親子の強い絆を描く名作。とりわけ、子役リッキーの飾り気のない愛らしさが観る者の涙を誘う。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・社長さん", "喜劇・社長さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "陽気で人情もろい中小企業の社長をコミカルに描く。おもちゃ工場の社長・伴太の熱意にほだされて秋田から少年が就職のため上京してきたが、その時すでに工場は倒産しており……。脚本に山田洋次が加わり、ハナ肇のキャラクターは“男はつらいよ”の“タコ社長”に通じるものとなった。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛情萬歳／VIVE　L’AMOUR", "愛情萬歳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「青春神話」でデビューした台湾の俊英T・ミンリャンがヴェネチア国際映画祭グランプリを獲得し、世界的な脚光を浴びた監督第２作。ロッカー式共同墓地のセールスマン、露天商をしている青年、不動産の営業ウーマンという見知らぬ男女３人が都会の高級マンションの一室で交錯する様を、不気味なまでに淡々としたタッチで描き出す。一切音楽を使用せず、極端にセリフを切り詰めた実験的な演出が印象的。これといってつかみどころのないドラマの中に、現代の台北に生きる若者たちの孤独がリアルに浮かび上がる。", "1994-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GODS AND MONSTERS", "ゴッド・アンド・モンスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "謎の死を遂げたハリウッド黄金期の映画監督ジェームズ・ホエールの晩年を描いた意欲作。1930年代に恐怖映画で一世を風靡した映画監督、ジェームズ・ホエールは映画界を引退、L.A.郊外の自宅で隠居生活を送っていた。ホエールは彼の家に新しく入った海兵隊上がりの庭師クレイトンに自分の絵のモデルを誘いかける。クレイトンはホエールの栄光の経歴を知り、ホエールに次第に興味を抱くのだが……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HUSH", "沈黙のジェラシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "嫁と姑の壮絶な闘いを描くサイコ・サスペンス。結婚を機に、夫ジャクソンの故郷ケンタッキーで農場を経営することになったヘレンは、義母マーサに温かく迎えられる。ところが、二人の仲を引き裂くような行動をとるようになったマーサに不審を抱いたヘレンは、義父の事故死にもマーサが絡んでいるのではと疑い始める。そんなある日、激しい陣痛に襲われたヘレンは、マーサに部屋に監禁されてしまい……。息子を溺愛する母親にJ・ラング、彼女に追い詰められていく新妻にG・パルトロウが扮して激しい火花を散らす。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAPS", "タップス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "アメリカの陸軍幼年学校が経済的な理由から閉鎖されることに……。それに抗議する学生たちは学内に立てこもり反乱を起こす。アメリカ映画としては異色の題材を扱った注目作。ハットン、クルーズが若き士官候補生としてみずみずしい演技を披露する。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GAMIANI", "禁断の寝室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ミュッセの発禁文学である『ガミアニ』を映画化した官能ロマン。16歳の処女ガミアニが、年上の女友だちと若い男によって二日間にわたる性の手ほどきを受け、死に至るまでの、快楽に溺れていく姿を描いていく。監督はスペイン映画界の“エロスの巨匠”、I・ゴンザレス・ディアス。", "1981-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRE：TRAPPED ON THE 37TH FLOOR", "ファイアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "超高層ビルの大火災といえば1970年代パニック映画「タワーリング・インフェルノ」を思い出すが、本作も地上62階のビルで発生した火災に挑む勇敢な消防士たちを主役にした映画。猛火に包まれたビルに、とり残された市民を助けるため、炎の中に飛び込んでいくのはTV『600万ドルの男』のヒーロー、L・メジャース。サイボーグならぬ、生身の体でアクション・シーンを演じている。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太とふしぎ風使い", "ドラえもん のび太とふしぎ風使い", "", "", "http://ecx.images-amazon.com/images/I/319DG991STL._SL210_.jpg", 83, "のび太とドラえもんが、風を操る人々の暮らす美しい村を訪問する冒険ファンタジー。お転婆なフー子などキュートなキャラクターが多数お目見えする。ある日、可愛らしい台風の子、フー子と出会ったのび太が、どこでもドアを使って“風の民”が暮らす不思議な村を訪れる。ところが、風を使って村を乗っ取ろうとしている“嵐族”が、力を持つフー子を奪い取ろうとして……。", "1981-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刀手怪招／MASTER WITH CRACK FINGERS", "ジャッキー・チェンの必殺鉄指拳", "", "", "http://ecx.images-amazon.com/images/I/51llZ9u3LtL._SL210_.jpg", 85, "J・チェン最初期の主演アクション。リュウは５歳の時からカンフーを学び、以来10数年、今や強い青年になっていた。街では悪がはびこり、ある日育ての親である叔父が殺される。犯人は実父をも殺した人物であった。師と仰ぐ老人から、リュウはカンフーの神髄を学び、悪の巣窟へと向かう……。", "1974-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KING OF KINGS THE KING OF KIINGS", "キング・オブ・キングス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "「十戒」のC・B・デミル監督が、“王の中の王”であるイエス・キリストの生涯をスペクタクル描写の連続で綴った無声期の大作。H・B・ワーナー演じるキリスト、J・シルドクラウトのユダなど名優たちの熱演で、聖書の物語が巧みに視覚化されている。なお、オリジナルでは“復活”の場面が二色式テクニカラーになっていた。", "1927-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("詭絲", "シルク", "", "", "http://image.eiga.k-img.com/images/movie/54012/315x210.jpg?2292", 107, "台湾シネマ・コレクション2008上映作品。TVドラマ「白い巨搭」で台湾でも人気の江口洋介、「グリーン・デスティニー」のチャン・チェン、「カンフー・ダンク！」のチェン・ボーリンらが出演するSFサスペンス・ホラー。台北で研究を行う天才物理学者の橋本は、死後の世界に興味を惹かれ、ついには少年の霊を捕獲することに成功するが、その霊は近寄る人々を次々に殺してしまう。橋本は生前の少年について敏腕刑事のイェ・チートンに調査を依頼するが……。", "2008-08-30", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALMOST AN ANGEL", "Mr.エンジェル 神様の賭け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "出所したばかりの泥棒が危険を顧みずに幼い子供の命を救う。意識不明の中で神と出会った彼は、神からもう一度地上に戻り、天使として人助けをしなさいと命じられる。半信半疑のまま意識を取り戻した彼は相も変わらず泥棒稼業を続けるが、やがて自分は天使だと確信する事態が連続して起こって……。P・ホーガンが、製作総指揮・脚本・主演の３役をこなしたハートウォーミングなファンタジー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KELLY'S HEROES", "戦略大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "「荒鷲の要塞」でスケールの大きい戦争アクションを発表したハットン監督の話題作。前作同様、主役にイーストウッドを起用。無頼のアメリカ兵が、ドイツ軍占領下のフランス銀行に眠る金塊を盗み出す。兵士たちのユーモアを交えた豪快な活躍が印象的だ。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AUTUMN HEART", "うちへ帰ろう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "脚本・主演のD・リー・ウィルソンの自伝的体験をもとに家族の再生を描いたハートウォーミングなヒューマン・ドラマ。息子に会いたいと言う病床の母親のために、デヴ、ドナ、ダイアンの３姉妹は、20年前に別れた弟ダニエルを探しだす。父親と暮らす弟は、３姉妹とは正反対の裕福な家庭に育ち、結婚を控えた幸せな毎日を送っていた……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FEELING MINNESOTA", "フィーリング・ミネソタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "S・ベイグルマンが、まるでコーエン兄弟の映画のような物語展開を見せた監督デビュー作。20年ぶりに故郷ミネソタに戻ったジャックス。そりの合わない兄サムの婚約者フレディと恋に落ちた彼は、逃避行を決意する。怒り狂ったサムは、二人を追い詰めついにはフレディを殺してしまうが……。一定のジャンルに収まらず、二転三転する内容に脚本の冴えが感じられる。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロック誕生 The Movement 70's", "ロック誕生 The Movement 70's", "", "", "http://image.eiga.k-img.com/images/movie/54022/315x210.jpg?3961", 75, "オリジナリティ溢れる和製ロックを奏でる“ニュー・ロック”の台頭によって日本にロックが定着しはじめた1970年代前半の音楽シーンにスポットを当てたドキュメンタリー。海外からも高い評価を受けたフラワー・トラベリン・バンドや、現在も熱心なファンを持つはっぴぃえんど、頭脳警察といったバンドの貴重なライブ映像を、内田裕也、ミッキー・カーチス、近田春夫ら当時のシーンを支えた人物による証言を交えながら映し出していく。", "2008-10-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Married a Communist", "十三号桟橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "元共産党員のブラッドは、過去を隠したまま、妻ナンと幸せな新婚生活を送っていた。ある日、彼はかつての愛人で共産党員のクリスティンと再会する。彼女はブラッドに近づくうちにナンの弟ドンに恋心を抱き始め、自分が共産党員であることを明かしてしまう。党幹部は口封じのためにドンを殺害。クリスティンから事情を聞いたナンは、犯人を捜すために埠頭へと向かうが……。監督は「ジェーン・エア」のロバート・スティーブンソン。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("我愛太空人／THE OTHER HALF AND THE OTHER HALF", "あの愛をもういちど", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "1997年の中国への返還を前に、海外移住者が増大していた香港。そんな製作当時の事情を反映して生まれた作品。妻子を異国に残して戻ってきた男と、夫を一足先にカナダへ送り出した女。ひょんなことから同居し始めた二人がやがて恋に落ち、互いの伴侶との四角関係に思い悩む姿を描いていく。女流監督C・ローのデビュー作。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　PATINOIRE", "アイスリンク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "『浴室』などの小説で知られ、また、映画監督でもあるJ・P・トゥーサンが「カメラ」に次いで、自作を映画化した意欲作。全編がアイスリンクの上で撮影されるという前代未聞の映画「ドロレス」。この映画でヴェネチア映画祭グランプリを狙う為、やり手の女性プロデューサーはアメリカの大物俳優の出演やリトアニア代表のホッケー選手をかき集め、いよいよ撮影を開始したのだが……。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A NIGHT IN HAVANA: DIZZY GUILLESPIE IN CUBA", "ディジー・ガレスピー ナイト・イン・ハバナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "1940年代にビバップを生み出し、アフロ・キューバン・リズムとジャズを融合させたD・ガレスピー。以来そのリズムの故郷キューバを訪れることを夢みていた彼が“第５回ハバナ国際ジャズ・フェスティバル”のゲストとして招待され、40年ぶりにその地を訪れた時の模様を収めたドキュメンタリー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Women in Cages", "女体拷問鬼看守パム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ど根性物語 図太い奴", "ど根性物語 図太い奴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "腕っぷしはめっぽう強くて図太いところがあるが、純情いちずの辰。大正末期を舞台に、彼が意地と根性で世間を渡っていく様を描く。趣としては、同じ勝新太郎主演のヒット・シリーズ「悪名」の大正版といったところ。勝の豪快な演技が見もの。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("馬路英雄／OFF TRACK", "爆走!! 高速トライアル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ポルシェをはじめ、スーパーカーが繰り広げる壮絶なカーチェイスが魅力のアクション。一般道路での違法カーレースに興じる香港の若者たち。そのなかでもジャッキーとマックスのグループは、常に敵対し張り合っていた。やがてマックスは、ジャッキーの妹アンと出会い恋に落ちる。しかし、それがきっかけとなって、二つのグループの抗争は、さらに激化していく……。香港暴走版の「ウエスト・サイド物語」といった趣の作品。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Maid", "メイド　冥土", "", "", "http://image.eiga.k-img.com/images/movie/33962/315x210.jpg?1313", 90, "「the EYE」シリーズの製作総指揮を手掛けたダニエル・ユンが、シンガポールの俊英ケルビン・トン監督を迎えて放つ衝撃ホラー。シンガポールの道教信者の間で、地獄の門が開いて死者の霊が人間界で悪事を働くと言われる太陰暦の7月。フィリピンから出稼ぎに訪れ、中国人一家にメイドとして雇われた少女ローサは、知らぬ間に様々なタブーを侵してしまう。やがて彼女の周囲で次々と不可解な出来事が起こり始め……。", "2007-05-05", "54", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　薄桜鬼", "劇場版　薄桜鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЗЕРКАЛО", "鏡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "A・タルコフスキーが、自伝的な心象風景を独自の水と火をモチーフにして映像の中に展開した美しい映像詩。木々に囲まれた木造りの家にたたずむ母の思い出、母に似た妻ナタリアへの愛と別離、そして戦争の悲劇……。まるで万華鏡のように現れては消えるイメージの渦だ。A・タルコフスキーの実父で詩人の、アルセニー・タルコフスキーの詩が監督自身の朗読により効果的に挿入されている。", "1975-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("斜陽", "斜陽", "", "", "http://image.eiga.k-img.com/images/movie/53792/315x210.jpg?2906", 80, "太宰治の生誕100周年となる2009年、太宰の同名代表作を佐藤江梨子主演で映画化。監督は、「富嶽百景／遙かなる場所」でも太宰作品を映画化した秋原正俊。良家の子女・かず子は、とある事情から都会の喧騒を逃れ、母とともに田舎へと越してくるが、母は慣れ親しんだ旧家への思いから病に倒れてしまう。そんなある日、無頼漢の弟・直治が舞い戻り、直治の仲間・上原と知り合ったかず子の人生は思いもよらない方向へと向かう……。", "2009-06-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("２００９　LOST　MEMORIES", "ロスト・メモリーズ", "", "", "http://ecx.images-amazon.com/images/I/511F5MJ7YNL._SL210_.jpg", 136, "「ブラザーフッド」のJ・ドンゴンを主演に据えた日韓合同作品。その見どころはずばり意表をついた歴史設定で、物 語は“1909年、伊藤博文韓国統監が暗殺されなかったら？”と想定。なんと100年後の朝鮮半島は日本統治が続いているという設定の下で進展していく。また、主人公の親友役を務める仲村トオルは本作で韓国のアカデミー賞、大鐘賞の助演男優賞を受賞した。2009年、日本第三の都市として統治される韓国・ソウル。JBI（日本特殊捜査局）に所属する西郷と朝鮮系日本人、坂本は親友同士。ある事件について捜査するうち、二人は歴史に隠ぺいされた恐るべき謎にたどり着く……。", "2004-03-27", "76,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("First Love of Millionaire", "百万長者の初恋", "", "", "http://image.eiga.k-img.com/images/movie/33782/315x210.jpg?1514", 113, "テレビドラマ「私の名前はキム・サムスン」で大ブレイクしたヒョンビンが映画初主演を果たしたラブ・ストーリー。18歳の誕生日に亡き祖父が残した莫大な遺産を相続することになっている財閥3世の青年ジェギョンは、相続の条件としてある田舎の高校に通うことに。今までとはかけ離れた生活に戸惑い憤りを感じるジェギョンだったが、クラスメートのウナンが気になりはじめ……。監督は「火山高」「オオカミの誘惑」のキム・テギュン。", "2006-12-23", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("For the Love of Benji", "ベンジーの愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "“ベンジー”シリーズ第２作。チャプマン一家は休暇旅行で、ギリシャへ向かう。ところが飛行機の中で、何者かが愛犬ベンジーの足の裏に暗号を書いたことから、一家はスパイ戦争に巻き込まれてしまう。ベンジーの恋人ティファニーも登場して動物ファンを魅了する。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それから", "それから", "", "", "http://ecx.images-amazon.com/images/I/51fA9GcDLbL._SL210_.jpg", 130, "漱石の名作の映画化。生きるためだけに働くのは非人間的だとして“遊民”の生活を送る代助（松田）は、かつて友の本望に殉じて密かに愛し合っていた三千代（藤谷）を平岡（小林）に譲るが、三千代は代助を愛し代助を待ちながら、世俗的な平岡のもとで苦しんでいた。やがて代助は愛を告白するが、友と家からの絶縁が待っており……。明治末期の雰囲気を忠実に再現し、森田独特のリズムと映像美に貫かれた恋愛映画の傑作。国内の多くの映画賞を獲得した。", "1985-11-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレージー黄金作戦", "クレージー黄金作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 157, "博奕狂いの破戒僧・心乱はひょんなことからラスベガスに行くことになり、機中、代議士の板垣、アメリカ人の遺産を相続することになった梨本といっしょになる。遺産は廃鉱と坑道地図だけであったが、坑道には海賊が隠した財産が眠っていた。ザ・ドリフターズが先輩クレージーとワン・シーン共演。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無法松の一生", "無法松の一生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "伊丹万作が岩下俊作の『富島松五郎伝』をシナリオ化し、病臥に伏した伊丹に代わって、稲垣浩が監督し完成させた作品。伊丹のキャリアの中でも屈指の名脚本を得て、稲垣は叙情あふれる名編に仕上げている。北九州の小倉を舞台に、博奕好きで喧嘩っ早い人力車夫・松五郎の生涯を描く。陸軍大尉の一家とふとしたことから知り合った松五郎は、大尉の急逝のあと、未亡人と息子にあれこれと世話を焼く。息子が明るく活発な少年に成長していくに至り、彼は一家になくてはならない存在となっていく。そして時は流れ……。松五郎を演じた阪東妻三郎は、初の現代劇だったが見事な名演。また、時の流れを示す人力車の車輪の流麗な映像処理が美しい。この映画は戦時の検閲にあい、年老いた松五郎が未亡人に我知らず思慕のたけを告白するシーンなど計10分ほどが、戦後にはGHQの検問によってカットされ、稲垣は15年後、三船敏郎でリメイクし無念を晴らした。DVDは幻のシーンを追加し、「阪東妻三郎傑作選 DVD-BOX」に収録。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TALK RADIO", "トーク・レディオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "バリーはテキサス州ダラスの地方局の深夜放送“ナイト・トーク”の人気DJ。この番組は電話による聴取者参加番組で、日常の悩みから、セックス、人種問題、果ては犯罪までいろいろな人々からかかってくる電話とバリーとのやりとりで構成されている。彼が語ることはすべて真実だったが、言葉は汚く過激で聴取者の神経を逆なでし怒らせることが多かった。当然彼を憎む者もいたが、バリーのスキャンダラスな姿勢は人気を呼び、ついに番組は全国ネットに格上げされた……。電波によって怪物化した言葉の恐ろしさをテーマにしたS・シンギュラーの著書に惚れ込み、自ら戯曲化・上演して話題を呼んだE・ボゴジアンが、映画でも主演している。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Twin Peaks: Fire Walk with Me", "ツイン・ピークス／ローラ・パーマー最期の7日間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "日本でも大ブームを巻き起こしたアメリカの人気テレビシリーズ「ツイン・ピークス」の前日譚となるエピソードを、デビッド・リンチ監督自らが手掛けた劇場公開版。アメリカ北西部の田舎町ツイン・ピークスで起きたローラ・パーマー殺人事件。テレビシリーズの発端となったこの事件にスポットを当て、彼女が殺害されるまでの7日間を描く。シリーズ本編には登場しなかった捜査官に、キーファー・サザーランドとデビッド・ボウイが扮する。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tropical Malady", "トロピカル・マラディ", "", "", "http://image.eiga.k-img.com/images/movie/57732/315x210.jpg?2159", 118, "「真昼の不思議な物体」「ブリスフリー・ユアーズ」のアピチャッポン・ウィーラセタクン監督が手がけ、2004年・第57回カンヌ国際映画祭で審査員賞、第5回東京フィルメックスで最優秀作品賞を受賞。フランスの名門映画誌「カイエ・デュ・シネマ」が選出する04年のベストワンにも挙げられた一作。前半で愛し合う2人の青年の日常をみずみずしく淡々と描き出しながらも、後半は予想もつかない物語が展開されていく。2012年、ウィーラセタクン監督作を一挙上映する「アピチャッポン・イン・ザ・ウッズ」にて上映。", "2004-01-01", "23,2,4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA COSA BUFFA", "家庭教師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "家が貧しく、家庭教師をしながら学校に通う一人の青年。ある日、彼は境遇の違う金持ちの美しい令嬢と恋に落ちる。好奇心いっぱいの若い男女の愛と性。ベニスの美しい町並みを背景に、甘ずっぱい思いがよぎる純愛物語。デビュー間もないピッコロがまだあどけない。", "1974-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAR　REQUIEM", "ウォー・レクイエム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "デレク・ジャーマン監督が「ラスト・オブ・イングランド」「ザ・ガーデン」の間に発表しながら、長らく日本未公開だった長編作品。ベンジャミン・ブリテン作曲による『戦争レクイエム』に、戦争詩人ウィルフレッド・オーエンの詩編を重ね合わせ、第一次大戦からフォークランド紛争まで終わることのない戦争への壮大な“鎮魂歌”を彼ならではの映像で綴る。オペラ『戦争レクイエム』と、戦争詩人による９つの詩。そして古今東西の戦争記録映像のリミックス。ジャーマン独自の戦争観を反映したセリフのない“物語”。兵士に被差別者でエイズ患者の悲哀が重なり合う特異な構成だ。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お茶漬の味", "お茶漬の味", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "戦時中に検閲当局から却下された脚本を戦後になってから映画化。地方出身の商社マンが社長の親友の娘と結婚し、その夫婦が中年にさしかかった時の物語。妻は、旅行に野球観戦にと遊びまわり、夫のヤボったさや田舎者丸出しの習慣を嫌っていた。が、夫の海外出張をきっかけに、彼の頼もしさを見直す。夜中に二人でお茶漬けを食べながら、夫婦は人生はお茶漬けのようなものだと実感する。この作品では、飾らない生活がいちばんだと言う夫の考えを支える、庶民的な生活のディテールが、娯楽（パチンコ、競輪）、食べ物（トンカツ、ラーメン、漬物）、嗜好品（煙草の“朝日”）といったかたちでとり入れられている。若き日の川又昴が撮影助手を担当。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kasi Az Gorbehayeh Irani Khabar Nadareh", "ペルシャ猫を誰も知らない ", "", "", "http://image.eiga.k-img.com/images/movie/55032/315x210.jpg?3762", 106, "「酔っぱらった馬の時間」「亀も空を飛ぶ」などで国際的に高い評価を受けたイランのバフマン・ゴバディ監督が、テヘランのアンダーグラウンド・ミュージック界を題材に、17日間に渡る無許可ゲリラ撮影を敢行して撮りあげた青春群像劇。西洋的な文化が厳しく規制されているイランで、当局の目をかいくぐってバンド活動に熱中する若者たちの姿をリアルに描く。第62回カンヌ国際映画祭「ある視点」部門で特別賞を受賞した。", "2009-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("温泉こんにゃく芸者", "温泉こんにゃく芸者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "1968年の「温泉あんま芸者」（石井輝男監督）に始まる一連の“芸者”ものの一編。孤児の珠枝はコンニャク屋の徳助に育てられたが、徳助の窮状に発奮、温泉芸者として働く。やがて珠枝の名器ぶりが評判となり、剛の者が次々と挑んでくる。性具の研究に没入する徳助役を演じる殿山泰司がハマリ役。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOT SHOT", "炎のストライカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "親友同士のジミーとビニーは“サッカーの神様”と呼ばれるブラジルのスター選手サントスを崇拝し、スーパースターを夢見て練習に明け暮れていた。運良く新人テストに合格するが素行不良でチームを追い出され……。ペレのバイシクル・シュートが見もの。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("彼女が結婚しない理由", "彼女が結婚しない理由", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "夫と離婚し、娘の怜子と二人暮らしのブライダル・コーディネーター、有子。ある女性から結婚を前提に交際している恋人のことで相談を受けた彼女は、結婚が自分にとってビジネスになっていたことに気付いた。そんな時、客の一人である社長令嬢から、またもや相談を受けてしまう。尾美としのりや峰岸徹など大林組のゲスト出演も見ものの女性映画。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あらくれ", "あらくれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "自然主義文学の代表的作家・徳田秋声の同名小説を水木洋子が脚色し、成瀬巳喜男が演出した作品。生まれつき気性が激しく、そのくせ情にほだされやすい女が、次々と男に捨てられながらも生きていく姿を、距離をおいて凝視する。強い女を描くことには定評のある成瀬監督だが、これほど荒々しく終始感情をむき出しにするヒロインは珍しい。成瀬との絶妙のコンビを誇る主演の高峰秀子は、その意志の強さを具現したような肉体で、やや陰影を欠く主人公をはじけ飛ばすように演じ、他作品でのきめ細かな名演とは違った味わいを残す。路地裏の物売りの声や拍子木を打つ音など、音声の表情も実に豊かである。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEEN WOLF", "ティーン・ウルフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「バック・トゥ・ザ・フューチャー」のM・J・フォックス主演の青春映画。興奮したり、追い詰められたりすると、なんと狼に変身してしまう主人公スコットの、不安、とまどい、いらだち、絶望、喜びを心優しく描く。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自転車とハイヒール", "自転車とハイヒール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "PFFアワード2001で入選した16ミリ中編。子供のころ、自転車を買ってもらえなかったことがトラウマとなっている男が、大切にしている自転車を盗まれてしまう。ようやく見つけた自転車に乗っていたのは小学校時代の親友だった。彼の家まで追いかけた男は、親友の同棲相手の女性と出会い、不思議な三角関係に巻き込まれていく。親友同士の再会に女性が嫉妬するという設定を隠し味に、大人になりきれない男たちの物語を紡ぐ。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("O TE VELKE MLZE", "広がる霧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 14, "学校帰りに不思議な庭の入口を見つけた男の子たち。早速、庭に入っって遊ぼうとした彼らだったが、そこにはものすごく怒ったネコがいて……。チェコアニメ映画祭2006で上映された一作。", "1975-01-01", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BINGO", "ビンゴ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "サーカス団の雑役犬をやっていたノラ犬のビンゴは、チャッキーという少年に偶然命を救われ、たちまち友情の絆で結ばれる。が、少年が引っ越すことになって彼らは別れ別れに。しかし、小さな主人と離れがたいビンゴは、持ち前の嗅覚と勇気で猛然とハイウェイを突っ走るのだった。ドラマの中で13種もの必殺芸を披露するスーパー・ドッグに注目。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Breakdown", "ブレーキ・ダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "　旅をしていた夫婦ジェフとエイミーの車が故障。エイミーは通りかかったトラックに乗って、近くの食堂へ行き、修理の手配をすることに。ところがそれを最後に彼女はジェフの前から姿をくらましてしまう。必死に妻を捜すジェフ。そんな彼に、やがてトラック運転手が身代金を要求してくるが……。行方不明になった妻を救い出そうとする主人公の闘いを描くサスペンス・スリラー。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シーズン・オフ", "シーズン・オフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "伊豆式根島のペンションに泊まり合わせた男女数名のふれあいと愛の葛藤をみずみずしく綴っている。「櫻の園」「12人の優しい日本人」と力作が続いた中原俊が、肩の力を抜いて取り組んだ、小品だが好作に仕上がった。脚本は人気劇団“自転車キンクリート”の飯島早苗。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BERSERK！", "姿なき殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ロンドンのサーカス一座での連続殺人事件を往年の美人女優、J・クロフォード主演で描いたミステリー。ある日、一座の綱渡りの名人が綱から転落して死ぬ。これは、何者かによる細工が原因であったことが判明。そして数日後、再び殺人鬼の手によって第２の犠牲者が……。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘密結社　鷹の爪 THE MOVIE 総統は二度死ぬ", "秘密結社　鷹の爪 THE MOVIE 総統は二度死ぬ", "", "", "http://image.eiga.k-img.com/images/movie/33892/315x210.jpg?2846", 0, "テレビ朝日系で深夜に放送されカルト的人気を集めたFlashアニメ番組「THE FROGMAN SHOW」の劇場版。世界征服を企むベンチャー秘密結社・鷹の爪は、いつものように資金難に悩まされていた。家賃の取立てから逃れようとしたところ、誤って宇宙へと飛び出してしまった彼らは、宇宙ステーション“ピースボール”に救出されるが……。映画予算残量のリアルタイム表示や観客参加型“告白タイム”の設置など奇抜な仕掛けも満載。", "2007-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lily", "Lily", "", "", "http://image.eiga.k-img.com/images/movie/56042/315x210.jpg?3003", 89, "若手脚本家のビンセントは、デビュー作こそ成功したものの、次回作が書けずにスランプ状態。同せい中の恋人との実生活をもとに書き始めたラブストーリーも、すぐに行き詰ってしまう。やがて映画会社のエージェントから、1週間以内に脚本を仕上げるように命じられ、追いつめられたビンセントは、あらためて自らの気持ちを見つめ直す。アメリカで映画制作を学び、脚本家としてキャリアをスタートさせた中島央が、自身の体験をモデルに完成させた初長編監督作。", "2011-04-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Outland", "アウトランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "　木星の第3衛星イオにある採鉱ステーション。苛酷な環境の僻地に保安官のオニールが妻と子供を連れて赴任する。その頃、ステーション内では不可解な自殺事件が急増。女医ラザラスの協力を得て調査にあたったオニールはまもなく危険な薬物の存在と、採掘事業の管理者シェパードのたくらみを知る。しかし真実を知りすぎたオニールを始末すべく、殺し屋たちが送り込まれ……。宇宙版「真昼の決闘」と呼ばれたSFサスペンス。", "1981-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Let Me In", "モールス", "", "", "http://image.eiga.k-img.com/images/movie/55782/315x210.jpg?2058", 116, "スウェーデン製の傑作ホラー「ぼくのエリ 200歳の少女」を、「クローバーフィールド HAKAISHA」のマット・リーブス監督がリメイク。学校でいじめられている孤独な少年オーウェンは、近所に越してきたばかりの少女アビーと出会う。お互いに孤独な2人はやがて心を通わすようになるが、アビーにはある秘密があった。出演は「キック・アス」のクロエ・モレッツ、「ザ・ロード」のコディ・スミット＝マクフィー、リチャード・ジェンキンス。", "2011-08-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴキブリたちの黄昏", "ゴキブリたちの黄昏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "CM界出身の吉田博昭監督のデビュー作。安アパートの住人は無精者で、ゴキブリたちと平和に共存してきた。ところが恋人ができて、二人はアパート掃除に殺虫剤を乱射し始めたのだ。ゴキブリたちとの決死の戦いが開始された。実写とアニメの合成が珍妙で面白い。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男ありて", "男ありて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "家族のことをいっさい顧みず、仕事一筋に生きるプロ野球監督・島村。家族に相談もなしにルーキー選手を家に同居させるほどの彼が、ある日１ヵ月の出場停止を命じられ……。ホーム・ドラマには定評のあった丸山誠治が、新たに仕事と家庭というテーマに踏み込んだ作品。撮影は、成瀬巳喜男とのコンビで知られる玉井正夫。志村喬がお好み焼きを焼くシーンと、ラストの妻の墓に語りかけるシーンは、秀逸。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("３RD DEGREE BURN", "灼熱の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "警官を辞職し、離婚した私立探偵スコット。ある日、彼はエリート・サラリーマンのジョーンズから妻のアンの行動を探るよう依頼された。そして彼はアンの追跡を始めるが、彼女と恋に落ちる。だがジョーンズが死に、スコットは殺人犯に……。V・マドセンが妖艶な魅力を見せるサスペンス作品。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マチコのかたち", "マチコのかたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "前作「眠る右手を」が香港国際映画祭をはじめ、各国の映画祭で高い評価を獲得したインディーズ映画界の異才、白川幸司監督作。不倫の恋に悩む主人公が、自分とまったく同じ服装をした女性と出会い、奇妙な信頼関係を築いていく過程が描かれる。前作の製作スタッフが引き続き結集して、極彩色でまとめた斬新な映像が目を引くコミカルなミュージカルだ。ある晩、レストランで不倫相手を待ち続けていたマチコは、そこで自分と全く同じドレスを着たマダム白金と出会う。マダム白金は、不倫の恋に悩むマチコに対して“女のかたち”を変える魔法をかけてあげると言うのだが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざ非情史 血の盃", "やくざ非情史 血の盃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "昭和20年、復員帰りの緒形哲と土井は、米兵に襲われた直美を救う。その後二人は別れ別れに。緒形は直美の父が組長の阿佐田組に入り、メキメキと勢力を伸ばして大和会と対立。ところが、一方の土井は、大和会の土井興行の社長に収まっていた。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cypher", "カンパニー・マン", "", "", "http://image.eiga.k-img.com/images/movie/1342/315x210.jpg?4292", 95, "98年に日本で超ロングラン・ヒットした「CUBE」のビンチェンゾ・ナタリ監督。その5年振りの新作は、60年代のスパイ映画やSF映画へのオマージュを盛り込み、人間の記憶をモチーフに描く異色サスペンス。 アニメ・スタジオのストーリーボード・アーティスト出身監督ならではの、映像展開にも注目。主演はロバート・アルトマン監督の「ゴスフォード・パーク」などで活躍の英国俳優ジェレミー・ノーザム。「チャーリーズ・エンジェル　フルスロットル」「シカゴ」「キル・ビル」と話題作の控えるルーシー・リューが共演。", "2003-01-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VSETKO　CO　MAM　RAD", "私の好きなモノすべて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "妻と離婚して失業中の男が迎える人生の転機。自分と一緒にロンドンに来るように誘う恋人と、再婚が決まった先妻。彼の心は微妙に揺れる。本国スロヴァキアで、ベスト・フィルム・オブ・ジ・イヤーの栄冠を勝ち取った巨匠M・シュリーク監督の佳作ドラマ。", "1992-01-01", "72", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("制服サバイガールI", "制服サバイガールI", "", "", "http://image.eiga.k-img.com/images/movie/54212/315x210.jpg?2909", 78, "女子高生のさくらは、友達の叔父が経営するテーマパーク「サバイバルランド」へ遊びにいくことに。そこではリアルなシューティングゲームができ、敵役の男たちは攻撃してこないので初心者でも安心して楽しめるという。しかし人間をゾンビ化させる謎の植物が人間に寄生してしまい、敵役の男が暴走を始め……。監督は「クレーマー」シリーズの金子大志、出演に「ひぐらしのなく頃に」の飛鳥凛、「ひゃくはち」「二重心臓」の有末麻祐子ほか。", "2008-12-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIMI", "ミミ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "G・ノエの「カルネ」で撮影を担当し、彼の公私に渡るパートナーでもあるL・アザリロヴィックが手掛けたダーク・ファンタジー。ママが大量の薬を飲んで入院してしまい、パリ郊外に住む叔母夫婦のマンションに引き取られた12歳の少女ミミ。そこで大人たちの凶暴なセックスを目撃し、いやらしいオヤジに迫られる彼女の恐怖体験が、童話『赤ずきんちゃん』になぞらえて綴られる。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mary Poppins", "メリー・ポピンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "ウォルト・ディズニー製作による傑作ミュージカル。やんちゃな子供たちの元に、風に乗ってやって来た不思議な家庭教師メリー・ポピンズ。優しくて美人で、おまけに魔法まで使えるメリーに、子供たちは大喜び。ところが彼らのパパは型破りな彼女を快く思わず……。メリー・ポピンズを演じたジュリー・アンドリュースがアカデミー主演女優賞を獲得。また同歌曲賞を受賞した「チム・チム・チェリー」をはじめ、物語を彩る名曲の数々も魅力。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Carmen 3D", "カルメン 3Dオペラ", "", "", "http://image.eiga.k-img.com/images/movie/56112/315x210.jpg?1836", 175, "2010年6月に英ロンドンのロイヤル・オペラ・ハウスで上演されたジョルジュ・ビゼーによる名作オペラ「カルメン」を、米リアルD社の最新3Dカメラで撮影し映像化。17世紀セビリア、タバコ工場で働くカルメンは竜騎兵の伍長ドン・ホセを誘惑するが、同郷の娘との結婚を決めていたホセに無視されてしまう。ある日、ケンカ騒ぎを起こして投獄されたカルメンは、ホセを再び誘惑し牢から逃げ出すのだが……。", "2011-04-09", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白夜", "白夜", "", "", "http://image.eiga.k-img.com/images/movie/54592/315x210.jpg?1904", 84, "フランスのリヨンで出会った日本人男女の切ない恋を描くラブ・ストーリー。別れた恋人に会うためリヨンにやって来た朋子は、世界を放浪するバックパッカーの立夫に出会う。初めは不信感を抱いていた朋子だが、自分のことを一生懸命考えてくれる立夫の優しさに惹かれていく。しかし立夫はその夜、リヨンを去らねばならず……。主演はEXILEの真木大輔と吉瀬美智子、監督に「バッシング」「愛の予感」の鬼才・小林政広。", "2009-09-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHERRY，HARRY ＆ RAQUEL", "チェリー、ハリー＆ラクエル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "ソフト・ポルノで一世を風靡したR・メイヤー監督が、夢と現実を交錯させて描くエロティック・ファンタジー。アリゾナの辺境地、コロラドの片田舎ではマリファナ密売に加担している保安官ハリーをめぐり、男たちの間で殺し合いが起こる。そんな街でイギリス人の美人グラマー看護婦チェリーと、メキシコ人エンリケと同棲しているラクエルの二人は出会い、快楽の新境地に目覚めていく。炸裂するカー・アクションや官能的なダンスシーンを、エロティックなカメラワークで映し出した快作。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Just for Kicks", "JUST FOR KICKS", "", "", "http://image.eiga.k-img.com/images/movie/33982/315x210.jpg?1329", 82, "世界中で社会現象を巻き起こし、ただの運動靴から年商約30兆円もの巨大市場を抱えるファッションアイコンへと発展したスニーカーにスポットを当てたドキュメンタリー。頼まれていないのに“My Adidas”という曲を作ったRUN-DMC、NBAのルールを破ってまでエア・ジョーダンを履き続けたマイケル・ジョーダンら、スニーカーを愛してやまないスターたちが語るエピソードを通し、有名ブランドの成長の裏側を暴き出す。", "2007-05-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪夢のエレベーター", "悪夢のエレベーター", "", "", "http://image.eiga.k-img.com/images/movie/54612/315x210.jpg?4071", 105, "エレベーターに閉じ込められてしまった刑務所帰りの三郎、他者の心が読める超能力者の静夫、妻の出産立会いに向かう順、自殺願望を持ったゴスロリ少女のカオリ。4人は救助の来ない密室で秘密を暴露しあっては互いに不信感を募らせていくが、エレベーターの外では更なる悪夢が4人を待ち受けていた。木下半太の処女小説を、TV構成作家の堀部圭亮が映画化。", "2009-10-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幻想魔伝・最遊記／Requiem選ばれざる者への鎮魂歌", "幻想魔伝・最遊記／Requiem選ばれざる者への鎮魂歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "峰倉かずや原作の少年向けコミックをアニメ化したアドベンチャー。『西遊記』をベースにして孫悟空、猪八戒、沙悟浄を引き連れた玄奘三蔵の天竺に向かう冒険を描く。大屋敷に迎えられ、一夜の宿をとることになった三蔵たち。だが邸内には妖気が漂い、彼らは罠にはまったことに気づく……。タバコも吸えば酒も飲む三蔵をはじめ、型破りのキャラクターが面白い。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新宿純愛物語", "新宿純愛物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "新宿を舞台に、ヤクザと警察両方に追われる謎の青年と純真な少女の逃避行を描くノンストップ・アクション。肉体を駆使し、拳銃、火炎放射機まで使って大暴れする仲村トオルの独壇場。演出は荒っぽいが、最後まで一気に見せてしまうパワーはスゴイ。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ghost Dog: The Way of the Samurai", "ゴースト・ドッグ", "", "", "http://image.eiga.k-img.com/images/movie/1802/315x210.jpg?1609", 116, "殺し屋ゴースト・ドッグ。彼の座右の書は「武士道といふは、死ぬ ことと見つけたり」で有名な「葉隠」だ。ある日命の恩人、マフィアのヴーゴ・ファミリーの幹部ルーイから、ファミリーの一員フランクを殺すよう指令を受ける。ファミリーのボス、レイは溺愛する一人娘ルイーズにファミリーの全財産を託したが、彼女はフランクを愛してしまったのだ。彼女に父の指令と悟られないようフランクを消す。この完全犯罪の実行人に選ばれたのがゴースト・ドッグだった。", "1999-11-27", "1,76,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN CYBORG STEEL WARRIOR", "アメリカン・サイボーグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "核戦争後の地球で、生殖機能を失った人類はサイボーグとコンピュータに支配されている。そこへ人類最後の生殖機能を持つ女性メアリーが発見された。人類復活を図る人間たちに、殺人サイボーグの魔の手が伸びる。ヒーローに扮するJ・ラーラは18代目ターザン俳優で、それを追う殺人サイボーグ役のJ・P・ライアンはキック・ボクサー。双方の迫力あるアクションが見もの。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　PLUS　BEL　A＾GE", "いちばん美しい年令〈とし〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "フランスの最高学府グランゼコールへの進学を目指す少年少女の物語。パリの下町からアンリ４世校に通う17歳のデルフィーヌが、知り合ったばかりの上級生の女の子クロードの自殺現場に遭遇する。やがてデルフィーヌは、クロードの恋人だった悪魔的な魅力を持つ青年アクセル、クロードの弟ベルトランとめぐり合い、彼らとの微妙な愛憎関係を織り成していく。監督のD・オードパンは、少年時代に主演した「悲しみの天使」など、もともと役者として活動してきた人。若手俳優たちのアンサンブルを指揮しながら、イジメや思想的な葛藤といった問題を抱える進学校のピリピリした緊迫感を刻み込んでいる。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EARTH　VS．　THE　FLYING　SAUCERS", "世紀の謎　空飛ぶ円盤地球を襲撃す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "UFOがまだ“空飛ぶ円盤”と呼ばれていた時代、宇宙人が地球に大挙襲来し、地球人類がこれに敢然と立ち向かっていく様を描いた空想科学特撮映画の名作。当時の軍事機密研究書をもとにストーリーが構築されているとのことだが、キッチュな夢ふくらむファンタジーとして楽しく観られる作品だ。特撮はレイ・ハリーハウゼンが担当。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A World Without Thieves", "イノセントワールド　天下無賊", "", "", "http://image.eiga.k-img.com/images/movie/33952/315x210.jpg?1306", 116, "「インファナル・アフェア」シリーズのアンディ・ラウ主演によるアクション・ドラマ。疾走する列車内を舞台に、スリを生業とする男性と彼との生活に嫌気がさした恋人の女性、さらに窃盗団のリーダーも加わり、農民の青年が持つ大金を巡って争いを繰り広げる。本国中国では2005年の正月映画として公開され、「カンフーハッスル」を押さえて大ヒットを記録した。監督は「ハッピー・フューネラル」のフォン・シャオガン。", "2007-04-28", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GADJO DILO", "ガッジョ・ディーロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "パリの青年ステファンは、亡き父が遺したテープのジプシーの歌に魅せられ、声の主を探しにルーマニアのジプシーの村へやって来る。最初は警戒していたジプシーたちも、次第にステファンと交流を深めていく。やがて彼はジプシー女性と恋に落ちるが、そんな折、村で悲劇的な事件が起こる……。「僕のスウィング」のT・ガトリフが手掛けた人間ドラマ。“ガッジョ・ディーロ”とは、ジプシーの言葉で“よそ者”の意味。異邦人である青年とジプシーたちの言葉を超えた交流の温かみが、じっくりと観る者の胸に滲み込んでくる。", "1997-01-01", "2,56", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WORKING TRASH", "ワーキング・ガーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「ワーキング・ガール」のサクセス・ストーリーと「ウォール街」の緊迫株式ゲームを合わせた軽妙なコメディ。証券会社で掃除夫として働くラルフとフレディ。ゴミ箱のインサイダー情報を利用し、株で大もうけ。しかしそこには当然落とし穴も……。TVフィーチャー作品。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEMOLITION MAN", "デモリションマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "2032年、コンピュータにより暴力も犯罪も完全に排除された社会に、冷凍刑に処されていた凶悪犯フェニックスが蘇生する。この殺人鬼を倒すため、凍結中の悪くれ刑事ジョンも解凍され、命懸けの対決が始まる。ハイテンポでコミックのりのノンストップ・アクションが見どころのSF大作。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHAKESPEARE IN LOVE", "恋におちたシェイクスピア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "1998年度アカデミー賞で13部門にノミネートされ、７部門を制した秀作ラブ・ロマンス。シェイクスピアの不朽の名作『ロミオとジュリエット』誕生の陰には、彼自身の燃えるような恋物語があった……。そんな大胆な仮説をもとに、現実の恋と劇中劇を巧みに交錯させる細かなストーリー構成と軽妙な展開が観る者を魅了する。1593年、エリザベス朝のロンドン。新作の執筆に取り掛かった人気作家シェイクスピアは、商家の令嬢ヴァイオラと恋に落ちる。女が舞台に上がることが許されなかったため、男装したヴァイオラを主演に据えたシェイクスピアの芝居は着々と進行していった。ところが、ヴァイオラに親が決めた結婚相手がいることが発覚。絶望したシェイクスピアは喜劇を悲しい愛の物語へと変えていく……。ヒロインを気品を漂わせながら快活に演じるG・パルロトウ、情熱的なシェイクスピアを颯爽と演じるJ・ファインズをはじめ、脇を固める名優陣の熱演も光る。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JACK JOHNSON", "史上最強のボクサー ジャック・ジョンソン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "黒人初の世界ヘビー級チャンピオンとなった、ジャック・ジョンソンの数奇な生涯を描いた異色ドキュメンタリー映画。1908年にトミー・バーンズからヘビー級王座を奪い、1915年にジェス・ウィラードに敗れるまで、“史上最強のボクサー”として君臨したジョンソン。だが、黒人ゆえのいわれなき人種偏見によって“白人奴隷法”で有罪となり、ヨーロッパに逃亡、20世紀初頭の歴史の変転の目撃者となった。第一次世界大戦、ロシア革命、メキシコ革命などに遭遇し、レーニン、怪僧ラスプーチン、パンチョ・ビラらに出会う……。古いニューズ・リールによって綴られるジョンソンの生涯はまさに激動する20世紀の歴史である。チャップリン、バット・マスターソン、ジャック・ロンドンといった著名人の姿が見られるのも興味深く、またM・デイヴィスのトランペットも大きな魅力。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Juvenile Court", "少年裁判所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RIO GRANDE", "リオ・グランデの砦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1880年、メキシコ国境近く、リオ・グランデ河のスターク砦。ヨーク中佐は横暴なアパッチ族の退治に日々追われている。彼らは西部を荒らしてはメキシコに逃げ、騎兵隊の追撃をかわしていた。ある日ヨーク中佐の部隊に彼の息子が赴任して来るが、中佐は上官としての態度で厳しく接する。そこへ南北戦争時のある事件から、中佐と別居していた彼の妻が、息子を連れ戻しに来るのだが……。勇敢なアメリカの騎兵隊魂を顕揚したフォード監督の騎兵隊３部作の一編。サンズ・オブ・パイオニアーズのコーラスが色を添えている。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋山彦・総集篇", "恋山彦・総集篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "もともとは「風雲の巻」と「怒涛の巻」の２部から成る作品で、これはその総集編。阪妻プロを解散した阪東妻三郎と、マキノ・トーキーを解消したマキノ雅広の日活京都入社第１回作として作られたものである。五代将軍・綱吉の治世、信州伊那の山奥深くに一族とたてこもる伊那小源太は、故あって江戸から逃げのびてきたお品という柳川流三絃の名手・源四郎の娘を妻に迎えた。一方、柳沢吉保の命で檜材伐り出しのため、伊那一族の所領に飯田六万石・堀鶴之丞が勝手に斧を入れたことから伊那一族は憤激。小源太は同志を引き連れ江戸に乗り込み大暴れし、柳沢吉保と対決する。阪妻の二役も見られる痛快アクション時代劇。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flightplan", "フライトプラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "夫を不慮の事故で失い、その遺体を故郷のニューヨークに還すため、自身が設計した最新鋭旅客機に搭乗した飛行機設計士のカイルだが、フライト中に居眠りをした隙にひとり娘が姿を消してしまう。乗員や乗客は誰も娘を目撃していないというが……。ジョディ・フォスター3年ぶりの主演作で、監督は本作がハリウッド・デビューとなるドイツの俊英ロベルト・シュベンケ。撮影は名手ミヒャエル・バルハウスの息子、フロリアン・バルハウス。", "2006-01-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DYKKERNE", "デュカネ・小さな潜水夫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "深海に沈むナチス・ドイツの潜水艦を発見した兄弟が体験する一夏の冒険物語。デンマーク沖の美しい海。アスクとクリスチャンの兄弟は、夏休みを過ごすためにおじいさんの住む港町にやってくる。不思議な現象が起きた海域でスキューバ・ダイビングをしているうちに、二人はある宝物を乗せたまま沈没したという潜水艦U−461を発見する。ビデオ、DVDタイトルは「デュカネ　U−461の謎」。", "2000-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DADDY−LONG−LEGS", "ふたつの恋と砂時計", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ジーン・ウェブスターの傑作児童文学『あしながおじさん』を基にしたラブ・ストーリー。ピュアな女性を主人公に、ふたつの恋物語が思わぬ展開を見せながら描かれる。作品のテーマである“無償の愛”が切なく胸をしめつける1本だ。天涯孤独のヨンミは、いつも自分を救ってくれる謎の紳士と会えることを心待ちにしていた。社会人になった彼女は同じ職場のジュンホと恋に落ち、やがて衝撃の事実を知ることになる……。", "2006-06-24", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("援助交際撲滅運動 地獄変", "援助交際撲滅運動 地獄変", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "山本英夫、こしばてつや共著による同名コミック映画化作品の続編。前作に引き続き遠藤憲一が主演、バトルを繰り広げる相手役に人気AV女優の蒼井そらが扮している。人に言えない欲望を満たす風俗サロンを取り仕切るアオイには、過去に犯された経験があった。ある日、そのサロンに店長の友人だという男が現れ、店を貸し切りたいと要求してくるが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUTBREAK", "アウトブレイク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "アフリカのモタバ川流域にある小さな村に派遣された米国陸軍伝染病医学研究所のサムは、体中の皮膚が赤黒くふくれあがり苦痛にうめきながら死んでいく住民たちの姿を目にする。同じ頃、カルフォルニア州のシーダー・クリークという町で同じ症状の伝染病が発生した。ペスト以上に確実に死をもたらすというこの絶望的なウイルス。ところが、陸軍から持ち出された未知の血清が、このウイルスに奇跡的な効果をもたらすという意外な出来事が起こった。発見されたばかりのウイルスに効く血清をなぜ陸軍が持っていたのか。疑念を抱いたサムは、陸軍幹部マクリントック少将が企んでいた驚愕すべき事実を知る。世紀末的な新型ウイルスの脅威を描いたサイエンス・スリラー。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VON RICHTHOFEN AND BROWN", "レッド・バロン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1918年４月21日、フランス上空でイギリス軍のロイ・ブラウン編隊を迎え撃つ、フォン・リヒトオーフェンの真紅のアルバトロス三世機は苛烈な空中戦を展開。一瞬の差でブラウンの銃は25歳のドイツ人貴族を空中から消し去った。ドイツの第一次大戦の英雄の生と死を描いたR・コーマンの代表作。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HO＾TEL DU NORD", "北ホテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "フランス映画史上を代表する監督の一人マルセル・カルネの名作で、この映画のモデルになったパリのサン・マルタン運河のほとりにある北ホテルはいまだに観光客が多く訪れるという。ちなみに映画の中に出てくる北ホテルは本物そっくりに建てたセットである。パリ市内の静かな一角に建つ北ホテルには貧しい住人が集まっている。ある日そこに若い男女が泊まりに来た。男が失業し、希望を失った二人は思い余って心中を企てたのだ。最後のキスを交わして女を射った男は、ベッドに倒れる女を見ておじけづき、自分の胸にピストルをあてることができない。隣の部屋のエドモンは銃声を聞いてかけつけると男を逃がしてやった。男はやがて自首するが、女も幸運なことに命を取り留める。傷も癒えて退院した女は北ホテルに住み込みで女中として働き始めるが、エドモンは彼女に心を惹かれていた……。当時のフランス映画界を担った豪華なキャスティングだ。", "1938-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STARCRASH", "スタークラッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "SFXを駆使したスペース・オペラ大作。宇宙狭しと駆けめぐるスペース・パイレーツ、ステラ＆アクトン。二人は、ひょんなことから皇帝の危機を助けるはめになり、反乱軍の主領ザースの秘密基地へと乗り込んでいく。TVシリーズ『ナイトライダー』で知られるD・ハッセルホフの活躍ぶりが見もの。", "1978-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三婆", "三婆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "年老いた女３人の熾烈ないがみ合いの世界を描いた、中村登晩年の作品。妻ある身の会社社長が、妾として囲っている駒代の家で急死する。残された妻・松子は夫の財産や借金を整理し、悠々自適の生活を考えるのだが、そこに駒代や夫の妹で独身のタキが転がり込んできて……。ベテラン３女優のすさまじい演技バトルが見もの。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／CHRISTMAS　IN　AUGUST", "八月のクリスマス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "韓国で観客のすすり泣きを誘う大ヒットを記録し、世界各国の映画祭でも好評を博したラブ・ストーリー。ソウルの街角で小さな写真館を経営する青年ジョンウォン。美しい交通取締員タリムが客としてたびたびこの店を訪れるようになり、二人は心惹かれ合っていくが……。難病ものでありながら感情に直接訴えかけることを避け、むしろさわやかな余韻を残す１本。これが長編デビュー作のホ・ジノ監督が丹念な日常スケッチに冴えを見せ、お互いに本心を明かすことができない男女の“距離”を絶妙の演出で描いている。主演俳優二人の魅力もみずみずしい。", "1998-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鞍馬天狗 黄金地獄", "鞍馬天狗 黄金地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "天狗を幕末から明治４年の横浜に登場させた名匠・伊藤大輔のオリジナル。ハリウッドの日本人スター・上山草人演じる悪徳ユダヤ商人の贋金偽造計画を天狗が阻止する。悪玉を西洋人にしたのは戦時中の締めつけによるもの。一般的には宣伝文句にうたわれた“鞍馬天狗横浜に現わる！”が、そのまま題名として通用している。戦後には、「黄金地獄」と改題された。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("右門捕物帖 帯とけ仏法", "右門捕物帖 帯とけ仏法", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "1929年以来、“右門捕物帖”シリーズは数えきれないほど映画化されたが、その先鞭をつけたのが山中貞雄。本作は彼が初めて脚本、演出ともに担当した作品を基に、彼と親交の深かった三村伸太郎が新たに脚色したもの。西念寺の本堂で夜ごと開かれる怪しい集会。邪教の匂い漂う寺に入っていった一人の武士。その正体は……。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON’T TELL HER IT’S ME", "恋のためなら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "重い病から奇跡的に回復し、そのうえ女の子もモノにしたいと考えているガス。ところが彼は超オクテなために、女の子に声すらかけられない。そんな彼のため、ロマンス小説家の姉が恋人探しに乗り出すのだが……。恋をすると女の子ばかりでなく、男だって美しくかっこよくなれるのだということを見せてくれた１作。姉のリジー役に名コメディエンヌ、S・ロングが扮し絶妙の演技を披露しているほか、ガス役には“ポリスアカデミー”シリーズのS・グッテンバーグが扮し、恋のために大変身する男を見事に演じている。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beyond the ONEDAY Story of 2PM & 2AM", "Beyond the ONEDAY Story of 2PM & 2AM", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "韓国の人気男性アイドルグループ「2PM」と「2AM」のメンバーが集合したユニット「2PM＋2AM‘Oneday’」のドキュメンタリー。", "2012-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タイヨウのうた", "タイヨウのうた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "難病を抱える孤独な少女が、初めての恋を通して生きる喜びを見いだしていく姿をつづったラブストーリー。16歳の少女・薫は、太陽の光にあたることができないXP（色素性乾皮症）という病気に冒されている。深夜に駅前で歌うことだけを生きがいにしている薫は、サーフィンに熱中する青年・孝治と出会い、恋に落ちるが……。シンガーソングライターのYUIが、演技初挑戦ながらヒロインを好演。共演は「木更津キャッツアイ」の塚本高史。", "2006-06-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チンピラ", "チンピラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "川島透が1984年に「チ・ン・ピ・ラ」として映画化した金子正次の遺稿を、またまた大胆にアレンジした作品。血気盛んな青年と、うだつの上がらない35歳のチンピラニ人組の悲哀を綴る。「Helpless」の青山真治が効果的に長回しを用い、不思議な情感を醸し出している。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悲しき口笛", "悲しき口笛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "外地から引き揚げてきた兄が、歌の好きだった妹を探す。手掛かりは彼が出征前に作曲した『悲しき口笛』という歌だけ。これに麻薬の密輸も絡んでドラマが展開する。大ヒットした同名主題歌を歌った美空ひばりは、天才少女歌手として全国に名を知らしめた。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Jewel of the Nile", "ナイルの宝石", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "快作「ロマンシング・ストーン／秘宝の谷」の続編。舞台を南米からアフリカに移し、女流作家ジョーン・ワイルダーが破天荒な活躍を繰り広げる冒険活劇。“ナイルの星”と呼ばれる救世主の誘拐事件をめぐって、砂漠の大逃走が展開する。ターナーの身軽な快演が見もの。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Popeye", "ポパイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "　港町スィートへブンにやってきたセーラーマンのポパイは慌て者の娘オリーブの母親が営む下宿屋に住むことに。オリーブは町を取り仕切る乱暴者の保安官ブルートと結婚目前だったが、彼のことが好きになれず、思い悩んでいた。そんな彼女とポパイは次第に心を通わせていく。一方、ふたりは予知能力を持つ不思議な赤ちゃんを引き取ることになるが、ブルートがその子を狙い……。アメリカの人気コミックを実写映画化。ロビン・ウィリアムスら出演者も見事にそのキャラになりきった。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Dreamers", "ドリーマーズ", "", "", "http://image.eiga.k-img.com/images/movie/1372/315x210.jpg?2875", 117, "68年、5月革命前夜のパリ。双生児のイザベルとテオは、両親が留守のアパルトマンにシネマテークで出会ったアメリカ人留学生マシューを招き、映画ゲームに興じていく。テオ役は異才監督フィリップ・ガレルの息子ルイ・ガレル。イザベル役は「雨の訪問者」の女優マルレーヌ・ジョベールの娘エバ・グリーン。グリーンは本作の後、オーランド・ブルーム共演、リドリー・スコット監督の「キングダム・オブ・ヘブン」に抜擢された。", "2004-07-31", "3,2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WOMAN CHASES MAN", "木によじ登る女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "貧しいけれど野心家の女性建築士が、ある土地開発業者と知り合い、彼の開発計画を成功させることで自分も大きな仕事をつかもうとするが、あいにく彼は借金だらけ。そこで彼の息子の大金持ちを口説いてなんとか金を出させようとするが……。洒落た都会センスが楽しい喜劇。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("It Could Happen to You", "あなたに降る夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "お人好しの警官チャーリーがウェイトレスのイヴォンヌに、チップ代わりに分け前を約束した宝クジが大当たり。チャーリーは賞金の半分をイヴォンヌに与えるが、強欲な彼の妻はこれを独り占めしようとする……。ニューヨークで実際にあった話をもとに、粋な“奇跡”を描いた、フランク・キャプラ作品を思わせるハートフル・コメディ。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Milagro Beanfield War", "ミラグロ／奇跡の地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "「普通の人々」でアカデミー監督賞を受賞したロバート・レッドフォードの監督第2作。ニューメキシコ州の小さな田舎町ミラグロに、レジャーランドを建設するという計画が持ち上がる。平和な町はその話題で持ち切りに。そんなある日、無職の男ホセが、使用を禁じられている小川を利用して乾ききった土地を耕し始める。町の人々はそんな彼を暖かく見守るが……。再開発を狙う企業に立ち向かう人々の姿を描く、感動のヒューマン・ドラマ。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAIS NE NOUS DE´LIVREZ PAS DU MAL", "小さな悪の華", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ボードレールの『悪の華』に心を奪われ、焼身自殺する二人の少女の姿を通して思春期の苛立ちを描いた問題作。早熟で魔性を持った15歳の娘アンヌとロールは、禁断の書を読みふけり、農夫に裸を見せつけ、行きずりの男を誘拐し殺してしまう。そしてついに……。ヨーロッパ各地で上映禁止となった作品。", "1970-01-01", "2", 0, NOW(), NOW());
