INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("湯の町悲歌〈エレジー〉", "湯の町悲歌〈エレジー〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "当時のヒット歌謡曲に想を得た歌謡映画にしてラブ・ストーリー。伊豆の温泉旅館の娘と、そこへ投宿した画家は一目惚れで恋に落ちるが、娘は帰京した彼を追って東京に画家の家を訪ねる。しかしそこには、妻らしい女がいた。が、実は娘の一方的な誤解だった……。脚本にクレジットされている錦美奈仁とは監督・野村浩将と伏見晁の共同筆名である。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Day of the Dead", "デイ・オブ・ザ・デッド", "", "", "http://image.eiga.k-img.com/images/movie/53702/315x210.jpg?4464", 85, "ジョージ・A・ロメロ監督の名作ホラー「死霊のえじき」を、「ファイナル・デスティネーション」シリーズのジェフリー・レディック脚本、「13日の金曜日」シリーズのスティーブ・マイナー監督でリメイクしたサバイバル・アクション。無数のゾンビがうごめく街に足を踏み入れた人間たちが、生き残りを賭けて凄惨な闘いを繰り広げる。「アメリカン・ビューティー」のミーナ・スバーリが本格アクションに初挑戦して新たな魅力を開花。", "2008-08-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINAL ROUND", "ファイナル・ラウンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "巨大な倉庫を舞台に、人間を標的に武装したハンター５人が腕を競うという究極の格闘技“ファイナル・ラウンド”。その様子は全世界に生中継され、賭博までもが行われていた。ある日、無敵のファイター、タイラーが大会を仕切る組織に拉致され、ゲームの標的にされてしまう。タイラーは自分の持つ力と技でハンターたちと戦うのだった。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワールドクラシック＠シネマ2011 オペラ「シモン・ボッカネグラ」　ミラノ・スカラ座", "ワールドクラシック＠シネマ2011 オペラ「シモン・ボッカネグラ」　ミラノ・スカラ座", "", "", "http://image.eiga.k-img.com/images/movie/56462/315x210.jpg?4717", 150, "世界各地の有名オペラハウス、バレエ団の上演を映画館のスクリーンで上映する「ワールドクラシック＠シネマ2011」（全8作品）の1作。2010年4月、ミラノ・スカラ座で上演されたベルディ作曲によるオペラ。ガンの闘病のため活動休止していた3大テノールの1人、プラシド・ドミンゴが主人公シモン・ボッカネグラを熱演し、復帰を果たした公演を上映する。", "2011-05-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("巨人伝", "巨人伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "ヴィクトル・ユゴーの『レ・ミゼラブル』を映画化した、伊丹万作最後の監督作品。時代を幕末から明治維新直後に設定し、前科者で何度も脱獄を試みたものの失敗、19年の刑に処された大沼という男が、ついには役人を殺して脱獄に成功、時代の混乱に乗じて世の中を渡り歩いていく姿を描く。ある時、彼は不幸な少女・千代子を引き取り、育て上げていくのだが……。これまでにもゴルズワジーの『逃走』を翻案した「逃げ行く小平次」、モルナールの『リリオム』の翻案「金的力太郎」で多大な評価を得ていた伊丹が、再び『レ・ミゼラブル』を見事に日本の風土に展開した作品。この頃から闘病生活に入った彼は、以後、稲垣浩の「無法松の一生」「手をつなぐ子ら」など脚本を担当したのみで、ついに監督に復帰することなく、1946年、世を去った。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Barbarian and The Geisha", "黒船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　1856年の夏、タウンゼント・ハリスが初の日本総領事として下田に上陸。下田奉行の田村左衛門守は目つけ役として美しい芸者・お吉をハリスの宿舎に住まわせる。ハリスを冷たい目で見ていた村人たちはお吉にまで悪意をぶつけることに。そんな中、ハリスは通商条約調印に尽力するが……。日本で五ヶ月間の長期ロケを敢行。台本監修として衣笠貞之助なども協力した。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Welcome", "君を想って海をゆく", "", "", "http://image.eiga.k-img.com/images/movie/55812/315x210.jpg?2450", 110, "イラクからフランス最北端カレにたどり着いたクルド難民の青年ビラルは、恋人の住むイギリスへ密航を試みるも失敗してしまう。そんなある日、かつて水泳選手として名を馳せたシモンは、英仏を隔てるドーバー海峡を泳いで渡ろうとするビラルに出会う。シモンは別居中の妻の気を引くためビラルと特訓を始めるが、やがて2人の間には親子のようなきずなが芽生えていく。「すべて彼女のために」のバンサン・ランドンが主演を務め、「パリ空港の人々」のフィリップ・リオレ監督がメガホンをとる。", "2010-12-18", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE IRON MAJOR", "栄光のタッチダウン　フランク・カヴァーノ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "カレッジ・フットボール界にその名を残す名コーチ、フランク・カヴァーノの栄光の生涯を描くスポーツ伝記映画。第一次世界大戦の英雄であったカヴァーノ。戦後、彼が全米カレッジ・フットボールの名コーチの称号を与えられるまでを描き出す。古き良きアメリカの国民的ヒーローの半生を描いた秀作だ。名優P・オブライエンが、静かな心の内面と情熱的で激しい闘志を熱演。原作はカヴァーノ夫人フローレンスが書いた伝記である。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lili Marleen", "リリー・マルレーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ナチ支配下のドイツ。ビリーの歌う『リリー・マルレーン』は敵味方双方の兵士から愛唱され大人気となる。しかしユダヤ人の恋人は彼女をナチのマスコットと非難する……。ララ・アンデルセンの自伝をもとに、戦時下に生きる女の心の葛藤を描く。シグラのもの憂い歌いぶりが魅力。", "1981-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スーパーカミング", "スーパーカミング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "アメコミの世界を映像化したカルト・ミュージカル。伝説の予言書に、世界の終末をもたらすと記されたチャップマン。その不吉な男が巨大都市トキオラマに現れ、腹話術人形トレイシーとともに人気スターとなってしまう。帝王カイザー・アニクはその人気に嫉妬して、チャップマンを攻撃。二人はまた、無欠のドラッグ「SUPER-COMING」の争奪でも対立を重ね、戦いは世界の終わりを予想させるほどの規模へと発展していく。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CIRCUS WORLD", "サーカスの世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "「西部開拓史」の巨匠ハサウェイがサーカス一団の姿を描く。19世紀、アメリカ最大のサーカスを率いるマットは、ヨーロッパ巡業へと向かった。その旅は、娘トニを残し、突如姿を消した曲芸師のリリーを探す旅でもあった。しかしその途中、マットらの乗った船が転覆。すべてを失ったマットは、ヨーロッパでの再起を目指す。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("傷だらけの天使", "傷だらけの天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "1970年代の同名TVシリーズをモチーフに、アウトロー・コンビの気ままなみちのく旅行を綴るロード・ムービー。横田基地の近くにオンボロ探偵事務所を構える木田満が、死に際のヤクザから一人息子、蛍を母親のもとに連れていってほしいと頼まれる。八戸に向かう途中、満の相棒、久も車で駆けつけるが、ようやく会えた母親はケンもほろろで、怒った満たちは大ゲンカを起こしてしまう。トレンチコートにサングラスという豊川悦司のファッションや人情もろいキャラクターがいい味を出しており、“カッコ悪く生きることのカッコ良さ”がじわりとにじみ出る。観光地的なスポットがまったく登場しない東北旅情も妙に心地よく、主人公がみちのくプロレスのリングに乱入するシーンが爆笑を誘う。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("群衆の中の太陽", "群衆の中の太陽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "城南大学ラグビー部部員の尾崎とその仲間３人は、祝勝会の料理屋で洋子と知り合う。洋子は尾崎の親友・武井と結ばれ、やがて彼らは大学を卒業するが、実業家志望の武井は事業に失敗、洋子も失踪する。新聞記者になった尾崎が乗り出すが……。地味ながら印象的な青春ドラマ。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陥邊邊沿／EDGE OF DARKNESS", "狼の墓場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "CIDの潜入刑事であるカイは、長年の夢が実って犯罪シンジケートの奥深くに潜り込む職を与えられる。ごく親しい友人にも自分の地位を明かせられないカイは、意外にも上司のウォンによって裏切られ、ついには犯罪組織のボスに殺されてしまう。それを知った友人のマンが復讐を誓うのだが……。元刑事が製作、脚本を担当した問題作。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おんなの朝 あまから物語", "おんなの朝 あまから物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "裸一貫から会社社長となった磯貝栄之進は、とんでもない頑固もので、娘の結婚にも大反対。次男の進次は姉と恋人に家出を勧め、二人を伯父の家に居候させる。それを知った栄之進はご機嫌ななめで、そのうえ縁談を進めていた長男に恋人がいるとわかり……。ワンマン親父と反発する子供たちの姿をコミカルに描く。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAISSEZ−PASSER", "レセ・パセ／自由への通行許可証", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 170, "ナチス占領下のフランスを舞台にした人間ドラマ。それぞれの事情で敵国ドイツ資本のスタジオで働くことになった実在の映画人二人の波乱に満ちた日々を、エネルギッシュかつ感動的に描く。1942年、ナチス占領下のパリ。レジスタンス活動をしている映画助監督ドヴェーヴルがドイツ資本の映画会社に雇われ、検問がいらない通行許可証を手に入れる。一方、女たちの間を渡り歩いていた脚本家オーランシュにも転機が訪れる。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LADY IN CEMENT", "セメントの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "シナトラ主演の私立探偵『トニー・ローム』シリーズの第2弾。サスペンス・ミステリーの快作で、彼は今回も連続殺人事件に挑み、最後は見事な解決を示してくれる。トニーはある日、足をセメント詰めにされた美女の死体を海で発見し、さらに彼女の女友達も何者かに殺されてしまう。二人を知るウェルチ扮する女にはマフィアとの交際があり、その線を辿っていくとある人物に行き着き……。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Thing", "遊星からの物体X ビギニング（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ハワード・ホークス製作の「遊星よりの物体X」（1951）、ジョン・カーペンター監督の「遊星からの物体X」（82）と2度にわたり映画化されたジョン・W・キャンベル・Jr.の短編小説「影が行く」の3度目の映画化作で、カーペンター版の前日譚となる。", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Way of the Strong", "闇を行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "主人公ハンサムは、自らが経営する酒場でバイオリンを弾く盲目の少女ノラに想いを寄せていた。ある日、以前からハンサムと敵対していた密輸入者ルイによって、ノラが連れ去られてしまう。ハンサムは彼女を救うため、仲間を連れてルイのアジトへ乗り込む。死闘の末、彼はなんとかノラを救い出すことに成功するが……。監督をつとめるのは、後に「或る夜の出来事」「素晴らしき哉、人生！」などの名作を遺す名匠フランク・キャプラ。", "1928-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はだしのゲン", "はだしのゲン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "原爆投下前後の広島で、非国民呼ばわりされながらも、力強く生きる少年ゲンの姿を描いた反戦映画。中沢啓治の同名劇画を、独立映画運動の推進者、山田典吾が映画化。主婦層を中心に共感の輪が広がった。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ride, Rise, Roar", "ライド・ライズ・ロウアー", "", "", "http://image.eiga.k-img.com/images/movie/56912/315x210.jpg?1732", 87, "元「トーキング・ヘッズ」のデビッド・バーンが、「ブッシュ・オブ・ゴースツ」（1981）以来、27年ぶりにブライアン・イーノと共作したアルバム「エブリシング・ザット・ハップンス・ウィル・ハップンス・トゥデイ」（2008）を引っ提げて行ったツアーを収めた音楽ドキュメンタリー。数々のミュージシャンやダンサーたちにより演出されていく圧巻のステージの模様のほか、イーノや振付師へのインタビューなども収められている。", "2011-08-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FROGS on Screen", "FROGS on Screen", "", "", "http://image.eiga.k-img.com/images/movie/53742/315x210.jpg?3508", 0, "ダンスが得意な少年カケルは、従兄弟のテルが住む田舎町で夏休みを過ごすことに。ある日彼らは、山奥の\"お帰り神社\"と呼ばれるいわくつきの場所を訪れる。すると2人の前にアマネとフクロウという不思議な少年たちが現われ……。ベテラン俳優・岸谷五朗が演出を手掛け、「アクエリアンエイジ劇場版」の桜田通やテレビドラマ版「砂時計」の青柳塁斗ら期待の若手俳優が主演を務めた舞台「FROGS〜フロッグス」をスクリーンで上映。", "2008-06-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A SUMMER PLACE", "避暑地の出来事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "避暑地として有名なパイン・アイランド。そこにあるホテルはかつて栄華を誇ったが、今は没落して昔の面影もない。そこにかつてこのホテルで救命員として働き、今は大金持ちとなったケンが家族と一緒にバカンスにやって来る。ホテルの主人バートはなにかと皮肉を言うが、妻のシルヴィアはかつての恋人との再会に胸をときめかす。だが、そうした大人たちの思惑をよそに、バートの息子ジョニーとケンの娘モリーはしだいに愛し合うようになる。親たちの禁じられた恋を再びたどる子供たち。S・ディーとT・ドナヒューのコンビが話題を呼んだ青春映画。主題曲『夏の日の恋』が大ヒット。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前団地", "喜劇・駅前団地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "東京近郊のマンモス団地。脇には第二団地も建設中で、地主の権田孫作は笑いが止まらない。駅前の小料理屋・高砂亭の君江がお目当てで孫作は足しげく店に通っているが、外科医の金太郎や近所の連中も常連だ。土地問題をからめたコメディ。地主が伴、外科医が森繁。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KOMODO", "コモド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "全長６メートル、体重１トンに異常成長を遂げたオオトカゲ、コモド・ドラゴンが絶海の孤島で大暴れするパニック・アクション。コモドに両親を惨殺されたショックで自閉症となった少年。彼の心を解き放とうと女性精神科医は、因縁の島に彼を連れていくが……。監督は「ジュラシック・パーク」などでSFX担当として活躍、本作で初メガホンを執ったM・ランティエリ。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太平洋戦争・謎の戦艦睦奥", "太平洋戦争・謎の戦艦睦奥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "瀬戸内海柱島沖の秘密停泊地に停泊中の戦艦陸奥が、謎の爆沈を遂げた事件に基づいて作られた作品。ここでは、スパイの仕掛けた時限爆弾によって爆沈させられたという設定に。しかしその語り口はうまく、サスペンスの盛り上げ方も堂々たるものだ。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千客万来", "千客万来", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "天ぷら屋の一人息子・銀之助と幼なじみの多佳子、それに建設会社の有能な技師・謙太郎と社長秘書の啓子という２組のカップルの紆余曲折に、建設会社社長の妻への深い愛情を絡ませて、愛することの素晴らしさをうたった青春劇。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GHOST AND THE DARKNESS", "ゴースト&ダークネス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1898年、イギリス政府の東アフリカ鉄道計画も最終段階に入り、橋梁技術者ジョン・パターソンはツァボ河の橋建設を指揮するため現地に飛ぶ。だが、彼を待ち受けたのはアフリカ人が夜の悪魔と呼ぶ二頭の人喰いライオンとの戦いだった。動物とは思えぬ狡猾さと残虐さを持つ二頭は作業員を次々と襲い、３ヵ月で30人以上の人間を殺りく。人々は彼らをゴースト、ダークネスと呼んで恐れ、作業を中断してしまう。そこに政府に雇われた高名なハンター、レミントンが現れ、パターソンは彼と協力して野獣を追うが……。実話をもとにしたスリリングな冒険譚。派手な手法を使わず、音もなく近づく猛獣の恐怖をじっくりと描き込んだS・ホプキンスの演出も光る。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ペルソナ", "ペルソナ", "", "", "http://image.eiga.k-img.com/images/movie/53272/315x210.jpg?1826", 84, "人気グラビアアイドルの山崎真実が映画初主演を果たした本格バトルアクション。“プロジェクト・ペルソナ”と呼ばれる謎の人体実験により、驚異的な戦闘能力と別人格を埋め込まれてしまった女子大生・日和。研究所から脱走した彼女は、妻を亡くしたばかりの医師・幸一郎に助けられ、一緒に行動することに。追手が次々と襲い掛かる中、何故かお互いを他人とは思えない2人は、次第に惹かれ合うようになり……。", "2008-01-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("百合子、ダスヴィダーニヤ", "百合子、ダスヴィダーニヤ", "", "", "http://image.eiga.k-img.com/images/movie/56622/315x210.jpg?4651", 102, "チェーホフなどのロシア文学や演劇の翻訳家として知られ、「女を愛する女」であることを公言していた湯浅芳子と、戦後民主主義文学の旗手として活躍した作家・宮本百合子の7年間におよぶ共同生活や、百合子の夫・荒木茂をまじえた同性愛、異性愛が錯そうする愛憎を描いた伝記ドラマ。主演は「ヘヴンズストーリー」の菜葉菜と、歌手の一十三十一。「百合祭」「こほろぎ嬢」などで知られる女性監督・浜野佐知がメガホンをとった。", "2011-10-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Altered", "地球外生命体捕獲", "", "", "http://image.eiga.k-img.com/images/movie/53142/315x210.jpg?1964", 88, "「ブレア・ウィッチ・プロジェクト」のエドゥアルド・サンチェス監督が8年ぶりにメガホンを取ったSFホラー。アメリカの片田舎に拡がる不気味な森で、5人の少年が謎の生命体に拉致された。1人は無残にも命を落とし、1人は狂乱状態、残る3人も心に大きな傷を負う。それから15年後、事件のことを未だ忘れられずにいた彼らは、復讐を果たすべく武装して森を探索していた。そして遂に生命体の捕獲に成功するが……。", "2007-12-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AFFAIRE BLUM", "罠 ブルーム事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "チンピラ、ガブラーが殺人強盗で捕まった。被害者は、ユダヤ人ブルームが経営する会社の従業員。ところが、被害者が会社の脱税をかぎつけていたらしいと知った警察は、この事件をユダヤ人弾圧に利用しようとし……。ナチスの台頭し始めた1925年のドイツを舞台にした話題作。", "1948-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FROM HERE TO ETERNITY", "地上〈ここ〉より永遠〈とわ〉に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "1941年夏のハワイ米軍基地。ラッパ手のプルーが転属されてくる。ここの中隊長はボクシング狂で、自分のチームの強化を図っていた。プルーにも誘いがくるが、かつてボクシング試合で親友を失脚させた過去を持つプルーは、その誘いを断わる。そのため、プルーは中隊長にゴマをする小隊長に徹底的にしごかれる。中隊内で孤立したプルーの味方はアンジェロだけだが、そのアンジェロも営倉にぶち込まれる。そして、運命の12月７日がやってくる。日本軍の奇襲のさなか、プルーの背中に味方の銃が向けられる。軍隊の階級制度と個人の関係を通して、アメリカの軍隊生活における苛酷な現実を暴露した作品。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COMA", "コーマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "同名ベストセラー小説の映画化。ボストン記念病院に勤務する女医が、親友の麻酔事故に不審を抱く。調べると、８号手術室でオペを受けた患者ばかりが、昏睡（コーマ）状態に陥っていた……。“密売用臓器”として、植物人間と化した患者が裸で吊られている研究所のセットが鮮烈。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZATHURA", "ザスーラ", "", "", "http://ecx.images-amazon.com/images/I/518QD3343HL._SL210_.jpg", 110, "ボードゲームの内容が現実世界で引き起るトンデモ冒険映画「ジュマンジ」の続編が登場。壮大な宇宙空間を舞台に巻き起こる、危険きわまりないハプニングの連続にハラハラさせられっぱなしだが、ケンカばかりの仲の悪い兄弟が困難に直面して次第に助け合っていく姿に心が温まる。幼い兄弟が古いボードゲームを始めると家ごと宇宙に飛ばされ、ゲームの中の“流石群激突”“宇宙人襲来”などが現実に起こり出してしまう……。", "2005-12-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Marvel's The Avengers", "アベンジャーズ", "", "", "http://image.eiga.k-img.com/images/movie/54262/315x210.jpg?4284", 0, "人気シリーズ「アイアンマン」や「キャプテン・アメリカ」「マイティ・ソー」「ハルク」から各ヒーローが集合するアクション大作。自ら開発したパワードスーツをまとったアイアンマンとして戦う、億万長者で天才発明家のトニー・スターク、70年間の眠りから覚めた伝説の戦士キャプテン・アメリカ、神の王の息子で神々の国アスガルドから追放されたソー、怒りにより巨人ハルクに変身する科学者ブルース・バナー、女スパイのブラック・ウィドウ、エリートエージェントで弓の達人ホークアイは、人類史上最大の敵の襲来に備えた「アベンジャーズ」として召集される。それぞれの戦いで心に傷を負っていた彼らは、チームとして戦うことを拒むが……。ロバート・ダウニー・Jr.、クリス・エバンス、スカーレット・ヨハンソン、ジェレミー・レナーら、これまでの作品で各キャラクターを演じてきた豪華キャストが集結。", "2012-08-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野郎に国境はない", "野郎に国境はない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "小林旭がパリを拠点に活躍する国際刑事警察の捜査官に扮した、刑事アクションもの。バンコクのメナム河畔で二つの死体が発見され、国際刑事警察捜査官・辺見は、さっそくパリからバンコクへと飛ぶ。やがてこの殺人事件の背後に日本の組織によるニセドル札偽造がからんでいることがわかり……。中平康が得意のアクション演出に才を発揮した作品。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ne le dis a Personne", "唇を閉ざせ", "", "", "http://image.eiga.k-img.com/images/movie/56812/315x210.jpg?3466", 131, "カンヌ、ベルリン、ベネチアの世界3大映画祭の受賞作を中心に、各地の映画祭で話題になりながらも日本未公開だった作品を一挙上映する「三大映画祭週間2011」にて上映。8年前、何者かに襲われ妻マルゴを失ったアレックスは、ある日突然、マルゴからのメールを受け取る。そこには「誰にも言わないで」という一言とともに、現在のマルゴの元気な姿が写しだされていた。アレックスはマルゴの居場所と事件の真相を探るべく調査を始めるが……。リュック・ベッソンがプロデュース。俳優ギョーム・カネの長編監督第3作で、2007年・第32回セザール賞で最優秀監督賞、男優賞を受賞。", "2011-08-13", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("着信アリ２", "着信アリ２", "", "", "http://ecx.images-amazon.com/images/I/518HBME4KBL._SL210_.jpg", 106, "興行収入15億円を記録した三池崇史監督＆柴咲コウ主演の大ヒット・ホラー映画の続編。スタッフ＆キャストをほぼ一新し、女性保育士の周囲で再び発生した“死の着メロ事件”の悪夢を描く。これが映画初主演のミムラが、想像を絶する恐怖に打ち震えながらも事件を解明しようとするヒロインをリアルに熱演している。映画の中盤以降、事件のカギを探し求めて台湾へと舞台が転じる意外なストーリー展開に加え、前作の“美々子”に続いてお目見えする新たな怨霊キャラに要注意。", "2005-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEROES", "幸福の旅路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ベトナム戦争終結から４年、戦争で心に傷を受け、いまだ病院暮らしの青年ジャック。脱出を試みた彼は旅に出、その途中で知り合った娘の助力で、社会復帰を果たす。当時盛んに作られたベトナム後遺症ものの一編。若手監督が同世代に献じたハートのある小品。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Freedomland", "フリーダムランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "人気作家リチャード・プライスのベストセラーを映画化したヒューマン・ミステリー。アフリカ系住民が大半を占める団地内でカージャック事件が発生。被害者の女性の証言で、奪われた車には幼い息子が乗っていたことが判明する。警察は誘拐事件として捜査を開始するが……。事件を追うロレンゾ刑事にサミュエル・L・ジャクソン、息子を誘拐された母親にジュリアン・ムーアが扮する。監督は「アメリカン・スウィートハート」のジョー・ロス。", "2007-01-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　CYCLIST", "サイクリスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "イランでほとんどの国民が見たといわれる程の大ヒットを記録し、世界中の映画祭で上映されるなど、M・マフマルバフ監督の人気と名声を確立した人間ドラマ。貧しいアフガニスタン難民の主人公ナシムが、妻の入院費を稼ぐために、一週間続けて自転車に乗り続けるという無謀な見せ物にチャレンジ。多くの野次馬が見守る広場をグルグル走り回り、極度の疲労や睡魔と闘いながら完走をめざす彼の姿を、荒々しいカメラワークで追っていく。人生に行き詰まった男のひたむきな根性ドラマの形を取りつつ、リアリズムとファンタジー性が同居する映像世界が、イランの社会問題を浮き彫りにする鮮烈な作品だ。", "1989-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Persepolis", "ペルセポリス", "", "", "http://image.eiga.k-img.com/images/movie/53162/315x210.jpg?4084", 95, "イラン出身でパリ在住のマルジャン・サトラピによる自伝的グラフィック・ノベルを、サトラピ自身のメガホンでアニメ映画化。70～90年代のイランを舞台に、自分らしく生きようとする少女マルジの成長と3代に渡る母娘の愛を描く。マルジとその母の声を務めるのは、実生活でも母娘であるカトリーヌ・ドヌーブとキアラ・マストロヤンニ。2007年のカンヌ国際映画祭でアニメーション作品としては30数年ぶりに審査員賞を受賞した。", "2007-12-22", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE INCREDIBLE SHRINKING WOMAN", "縮みゆく女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ある日突然、平凡な一主婦の体が縮み始めた。みるみる小学生の身長になり、幼児の大きさになり、生まれたばかりの赤ん坊と同じ位の大きさに……。年齢が若返るのではなく、成人のまま肉体が縮んでいくのだ。R・マシスンの原作『縮みゆく人間』は、恐怖と悲劇に彩られた何とも気の重くなるSFだが、二度目の映画化になるこの作品は、コメディに仕上げられている。悲劇と喜劇は紙一重の言葉通り、豆つぶの人間の前で火を吐く巨大な怪獣＝キッチンのガス・レンジ、うずまく激流＝流しなど、第三者的立場に立つカメラが捉えた風景が笑いを誘う。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続スーパー・ジャイアンツ　（第９部）／毒蛾王国", "続スーパー・ジャイアンツ　（第９部）／毒蛾王国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "シリーズ第９作でこれが最終作となった。ピアス王国の皇太子を暗殺すべく革命団が暗躍する。エンゼル学園の文雄とみち子は、革命団の本拠地を発見するが捕えられてしまう。スーパー・ジャイアンツは二人を助け、ピアス王国の皇太子の暗殺を阻止する。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEART", "HEART", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "“心臓”と“心”をテーマにした、スタイリッシュなサイコ・スリラー。TVドラマ『心理探偵フィッツ』の原作者として有名なJ・マクガヴァンがシナリオを手掛け、先の読めないドラマを展開させている。飛行士ゲイリーは妻の浮気に悩んだあげく、心臓の持病に倒れて移植手術を受けた。彼の妻は、これを機に夫婦関係をやり直そうとするが、嫉妬深いゲイリーは、なおも妻を責める。そんな折り、臓器提供者に興味を抱いた彼は、その母マリアと知り合った。息子を溺愛していたマリアは、その後ゲイリーにつきまとい、その行動は常軌を逸していく……。まったく予想もしないエンディングが衝撃的。ブラック・ユーモアにあふれた選曲も光る。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高校教師", "高校教師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1993年１月から３月にわたって放映され話題を呼んだTVドラマの映画版。過去にトラウマを持つ女子高の教師と愛を知らずに育った孤独な生徒の恋という衝撃的なテーマを、ショッキングな描写を交えて描く。主要スタッフはTV版と同じ。森田童子の歌う『たとえばぼくが死んだら』がフィーチャーされている。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドグラ・マグラ", "ドグラ・マグラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "映画化不可能とまで言われてきた夢野久作の同名幻想怪奇小説を、実験映画の鬼才・松本俊夫監督が論理的に映画化。婚約者を殺しかけて精神病院に収容された青年と、彼を治療しようとする二人の医師との確執を縦糸に、人間の意識内の幻想と狂気が描かれる。理路整然と組み立てられた、ホラー風味の迷宮ワールド。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN IN THE GRAY FLANNEL SUIT", "灰色の服を着た男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 153, "イタリア戦線で落下傘部隊として活躍していたトムは現地の少女を愛し、身ごもらせながらも彼女を置き去りにしてしまったことから、日常生活に復帰できないほどの心の傷を負っていた。G・ペックが過去と対決して、現在と対峙していく姿をシリアスに演じきったヒューマン・ドラマ。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHERE THE DAY TAKES YOU", "ハートブレイク・タウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ハリウッドの路地裏にたむろする家出少年たちのリーダー的存在であるキングは、麻薬や暴力、売春に走る仲間たちの生活を変えようと苦悩するが……。家庭でも学校でも得られなかった温もりを求めて群がるストリート・キッズたちの暴走を描いた、辛口の青春映画。ヤング・スターの豪華な競演が魅力的。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トリコン!!!リターンズ", "トリコン!!!リターンズ", "", "", "http://image.eiga.k-img.com/images/movie/53962/315x210.jpg?1270", 89, "3人のイケメン探偵の活躍を描いた「トリコン!!!」（08）の続編。同じ施設で育ったエース、キング、ジャックの3人は、正義に憧れ横浜某所に探偵所を開くが、その実態は便利屋状態。理想と現実のギャップに悩んでいたある日、旧友ユージの失踪をきっかけに謎の事件が起きる。3人は事件を究明しようと必死に追跡するのだが……。", "2008-10-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トップ・ファイター", "トップ・ファイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "夜のジャングルを進む一群の戦闘服の男たちを捉える開幕はいかにも戦争アクション映画風。実はこれ、日本の自衛隊の活動を描く限りなくPRに近い記録映画。B級ホラーの添えものとして洋画系に出たが、宣伝なし、新聞広告も題名のみで、観客にも製作者にもアンフェアな公開だった。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜明けの二人", "夜明けの二人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "日本人がハワイに渡って100年という“ハワイ百年祭”を記念して作られた青春歌謡映画。橋幸夫と日系三世を演じる黛ジュンが、美しいハワイの風景の中で恋に落ちるラブ・ロマンス。ハワイ島で開催される“素人のど自慢大会”や“桜まつり”も楽しい一編。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Silence of the Lambs", "羊たちの沈黙", "", "", "http://ecx.images-amazon.com/images/I/51zcBvg-65L._SL210_.jpg", 118, "　FBIアカデミーの優秀な訓練生クラリスは連続誘拐殺人事件の捜査スタッフに組み込まれ、犯罪者として収監されているレクター博士と面会する。それは、天才的な精神科医でありながら、自らの患者を次々と死に追いやったレクターこそ事件の謎を解く鍵になると見込んでのことだった。レクターはクラリスに興味を示し、捜査の手がかりを与える。ふたりが次第に心を通わせていく一方、新たな誘拐事件が。そしてレクターは脱獄を図り……。ジョナサン・デミ監督の代表作となった衝撃のサイコ・サスペンス。", "1991-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NORTH SHORE", "ノース・ショア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「グリース」「青い珊瑚礁」の監督として知られるR・クレイザーが製作総指揮を務め、商業映画で経験を積み、サーフィン・ドキュメンタリーで実力を認められたW・フェルプスが監督した青春映画。アリゾナで生まれ育ったサーフィン大好き少年リックは地元のサーフィン大会で優勝し、その賞金で、ハワイにやって来た。“魂のサーファー”と呼ばれる男との出会いや、地元の女の子キアニーとの恋愛を通して本物のサーファー、そして真の男へと成長するさまがさわやかに描かれている。登場するサーファーはすべて本物で、ジェリー・ロペスなどのプロが華麗なテクニックを披露している。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHARLY", "まごころを君に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "いつも子供たちと公園で遊んでいた、精神薄弱の青年チャーリーはある日、女教師アリスの勧めで脳手術を受け成功する。学者となり自分の病状を研究するうちに、限界を知り悲嘆にくれる彼。アリスの熱心なプロポーズにもかかわらず、自ら精薄の世界に戻る。D・キイスのSF小説『アルジャーノンに花束を』を映画化したR・ネルソンの代表作。ビデオタイトルは小説同様「アルジャーノンに花束を」。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Marley & Me", "マーリー　世界一おバカな犬が教えてくれたこと", "", "", "http://image.eiga.k-img.com/images/movie/54291/315x210.jpg?4555", 116, "子育ての予行演習のつもりで、ラブラドール・レトリーバーの子犬を飼い始めた新婚夫妻のジョンとジェニー。マーリーと名付けたその犬のやんちゃな振る舞いに、さんざん手を焼く2人だったが、いつしか喜怒哀楽を共にするかけがえのない存在になっていく。犬との生活を通して人生を学んでいくカップルの姿を描いたドラマ。監督は「プラダを着た悪魔」のデビッド・フランケル。主演はオーウェン・ウィルソン、ジェニファー・アニストン。", "2009-03-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAKING　LIVES", "テイキング・ライブス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "狙った人物を殺害し、その人生そのものを乗っ取る。そんな奇妙な連続殺人鬼を冷静沈着な女性捜査官が追うサイコ・スリラー。ヒロインを演じるA・ジョリーは、クールさの中にも禁断の恋に溺れていく弱さも垣間見せ、人間味豊かな演技を披露。E・ホーク、K・サザーランドら実力派が脇を固め、緊迫したドラマを盛り上げる。残虐な犯行による猟奇殺人事件が続発。プロファイルの天才である特別捜査官イリアナは、犯人が被害者に成りすまして生きていることを突き止める。だが、唯一の目撃者に魔の手が迫り……。ビデオ、DVDはディレクターズカット版で発売。", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANNA KARENINA", "アンナ・カレニナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "原作はトルストイの長編小説。デュヴィヴィエ監督が渡英して製作した作品。ガルボ主演の1935年の作品とともに話題になった秀作で、アンナの悲劇的な恋を焦点に描いている。薄幸のアンナをリーが熱演。画家セシル・ビートンによる衣装デザインにも注目。", "1948-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TOXIC AVENGER PARTIII LAST TEMPTATION OF TOXIE", "悪魔の毒々モンスター 毒々最後の誘惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "トロマ社製作による“毒々”シリーズ第３作。恋人の視力を回復させるため、安定した職業につこうとする毒々モンスターを、彼の宿敵アポカリプス社が雇う。会社の策略も知らず、悪の手先になったかに見えた毒々モンスターだが、やがて最後の敵デビルと壮絶な戦いを繰り広げることになる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CHEAT", "チート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 44, "赤十字の基金を使いこんでしまった社交婦人が、金持ちの日本人から１万ドルを借金する。返せなければその男の情婦にならなければならない。婦人は夫とともに金策にかけずり回るが……。ハリウッド初の日本人スター、早川雪洲の出世作で、冷酷でサディスティックな彼の存在感は当時、大反響を呼んだ。", "1915-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナニワ金融道／灰原勝負！　起死回生のおとしまえ!!", "ナニワ金融道／灰原勝負！　起死回生のおとしまえ!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "中居正広主演のTVシリーズや、故・青木雄二の原作マンガで人気を呼んだ『ナニワ金融道』の映画化。主人公、灰原役の杉浦太陽をはじめキャストの顔ぶれを一新。ストーリーもゼロからの再スタートを切る。もちろん金融の裏情報も満載。面白くてためになる、一石二鳥の痛快作だ。勤務先が倒産し、大阪に流れ着いた灰原は、ナニワきっての街金“帝国金融”に就職。ところが4500万円を貸した相手が行方不明に……。", "2005-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Zodiac", "ゾディアック", "", "", "http://image.eiga.k-img.com/images/movie/1921/315x210.jpg?4138", 157, "「セブン」「ファイトクラブ」のデビッド・フィンチャー監督が、実際に起こった未解決事件を題材に放つサスペンス・ドラマ。周到に用意した手掛かりで人々を翻弄する連続殺人犯“ゾディアック”に挑み、人生を狂わされた4人の男たちの姿を描き出す。出演はジェイク・ギレンホール、ロバート・ダウニー・Jr.、マーク・ラファロほか。", "2007-06-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paris, je t'aime", "パリ、ジュテーム", "", "", "http://image.eiga.k-img.com/images/movie/33871/315x210.jpg?3510", 120, "パリの街角を舞台に男女の愛の物語を描いた18本のオムニバス・ムービー。「オー・ブラザー！」のコーエン兄弟、「グッド・ウィル・ハンティング／旅立ち」のガス・バン・サント、「トゥモロー・ワールド」のアルフォンソ・キュアロンら世界各国の名匠が集結。出演もイライジャ・ウッド、ナタリー・ポートマン、ジュリエット・ビノシュほか豪華キャストが顔を揃える。日本からは「M／OTHER」で国際的に高い評価を得た諏訪敦彦監督が参加。", "2007-03-03", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兵隊やくざ・大脱走", "兵隊やくざ・大脱走", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "悪徳軍曹たちの魔の手から慰問団の少女を救った大宮は、有田とともに馬廠の103部隊に将校を装って潜り込んだ。だが、そこには二人の正体を知る青柳憲兵がいた。準レギュラー・成田が悪徳憲兵役を好演。本作で大宮との友情が芽ばえるが……。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("上陸第一歩", "上陸第一歩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "貨物船の釜たき・坂田は、船着場で身投げしようとしていた若い女を救う。ヤクザ者に追われるこの女に、坂田は次第に同情を超えた何かを感じ始めるのだが……。ジョゼフ・V・スタンバーグの1928年作品「紐育の波止場」を翻案した、島津保次郎監督のトーキー第２作。ジョージ・バンクロフトの釜たき役に岡譲二、相手の身投げ女に水谷八重子が扮した。", "1932-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NONHOSONNO", "スリープレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "イタリアン・ホラーの帝王D・アルジェントが、古典的なタッチで描くサスペンス・ホラー。『サスペリア』シリーズほか、アルジェント作品ではおなじみのプログレ・バンド、ゴブリンが久々に音楽を手掛けている。北イタリアのトリノで、17年前と同じ手口の連続殺人事件が発生する。当時から事件を追っていたモレッティ警部と、17年前の被害者の息子ジャコモは事件の真相解明に乗り出す。", "2001-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEAN DE FLORETTE／MANON DES SOURCES", "愛と宿命の泉（フロレット家のジャン／　泉のマノン）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "映画雑誌『カイエ・デュ・シネマ』の生みの親であり、フランスの国民的作家兼監督として親しまれているパニョルの２巻の小説を基にした、フランス・プロバンス地方の一大叙事詩。第１部では、生命の源である水泉をめぐる農民特有の強欲さのために、苦悩し絶望してついに命果てる理想主義の男の姿を、第２部では、その10年後、父を死に追いやった農民に復讐する美しい娘マノンの愛と憎しみを綴る。緑豊かなプロバンスの素晴らしい景観に、自然の偉大さとその中に生きる人間の喜びと悲しみが脈打ち、感動を呼ぶ。日本では、世界で初めて２部一挙上映に成功。オートゥイユが1987年セザール賞最優秀男優賞を獲得した。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAD", "ザ・モンキーズ　恋の合言葉 HEAD！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "当時の人気グループ“ザ・モンキーズ”を主演に、ベトナム戦争たけなわの1960年代後期を舞台に32のエピソードを積み重ねて描き出した作品。ベトナム戦争の招集令状を受け取ったモンキーズ４人組は、銃弾を浴び、砂漠に放り出されるが、そこにコーラの自販機があって……。物語性を無視した構成が話題となったB・ラフェルソン監督のデビュー作。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チルソクの夏", "チルソクの夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "「半落ち」を手掛けた佐々部清の監督第２作。故郷・下関を舞台に、高校生の日本人少女と韓国人青年の淡い初恋をみずみずしく描き出した青春ドラマだ。一年後の再会を夢みる切ない乙女心や、ヒロインを応援する陸上部員たちの友情を、若手女優陣がさわやかに好演。教師役で出演もしているイルカの名曲『なごり雪』が効果的に使用され、郷愁を誘う。1977年、下関市。姉妹都市・釜山との親善事業として陸上競技が開催される。高校生・郁子は、同じ高飛びの韓国人選手・安と出会い、来年の夏に再会することを約束した。同じ陸上部の友人たちは、彼女の初恋のために奔走するが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NIGHT FLIER", "スティーヴン・キング ナイトフライヤー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "アメリカ各地の飛行場で続発する猟奇殺人事件。奇抜なスクープ記事が売り物のタブロイド紙のベテラン記者リチャードは、関係者への粘り強い聞き込みを続けていくうちに、セスナ機で現れる謎の吸血殺人鬼“ナイトフライヤー”に一歩一歩近づいていくが……。漆黒の夜空から舞い降りてくる黒マントの怪人の恐怖を描くサスペンス・ホラー。ヒーローとは程遠いダーティーな一面を持つ主人公のキャラクターには、原作者S・キングらしい強烈なブラック・ユーモアが感じられる。また、主人公がナイトフライヤーと対面を果たす血塗られたクライマックスにも、異様な迫力がみなぎっている。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はやぶさ　遥かなる帰還", "はやぶさ　遥かなる帰還", "", "", "http://image.eiga.k-img.com/images/movie/55981/315x210.jpg?2802", 136, "2010年6月13日、7年間60億キロの宇宙航海を経て小惑星「イトカワ」の微粒子を地球に持ち帰った小惑星探査機「はやぶさ」。世界初となるその偉業を支えた人々のドラマを描く。主演の渡辺謙がプロジェクトマネージャーの山口駿一郎に扮し、「犯人に告ぐ」「星守る犬」の瀧本智行監督がメガホンをとる。はやぶさが地球から3億キロ離れたイトカワの地表に着陸し、岩石サンプルを持ち帰るという前人未到のミッションは、予定よりも3年延期になるアクシデントに見舞われ、ようやく打ち上げられた後も、エンジンの不良や燃料漏れなどさまざまな危機が続く。そうした困難の中、プロジェクトチームははやぶさを帰還させるため強く結束していく。", "2012-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO GUN GUSSIE", "ロイドの二梃拳銃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "西部の酒場を舞台にしたサイレント短編。お尋ね者が、こっそり自分の指名手配の写真をピアノ弾きのロイドのそれとすり替えた。何も知らない保安官は、ロイドを追いかけ回すが、追われれば逃げたくなるのが人情というもので……。“都会の青年”のイメージが強いロイドが、珍しく西部男を演じた作品。(ランニングタイムは併録の「猛進結婚」と合わせたもの)。", "1918-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOSTAGE", "ホステージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ロバート・クレイスの小説に惚れ込んだブルース・ウィリスが自ら製作を兼任。豪邸で起きた人質事件と、自分の妻子の誘拐事件。複雑に絡み合う両事件に挑む元交渉人の苦悩と活躍を描く。「スズメバチ」のフローラン・エミリオ・シリによるシャープな演出もさえる骨太のクライム・ドラマだ。元敏腕交渉人ジェフが、豪邸に人質をとって立てこもった犯人グループの交渉に行き詰まる。同時に彼は別の犯罪組織に妻子を誘拐され……。", "2005-06-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARM’S CASE", "ハルムスの幻想", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "最後のロシアン・アバンギャルド、“異端の作家”ダニエル・ハルムスの夢と現実の交錯する二日間を描いた異色のユーゴスラビア映画。時代は1938年の夏。彼が秘密警察に逮捕されるまでの二日間の出来事を描く。当局の尋問を受けていたハルムスが久しぶりにアパートに帰ってきた。ほこりのたまった部屋にたたずんでいると、突然天使が現れ、奇妙な幻想の世界が始まる……。ユーゴ映画界の気鋭、ペシチ監督はモノクロとカラーを巧みに使い分け、美しい映像でマルチ・アーティスト、ハルムスに迫る。アイデアはハルムスの著作より得て、美術は当時のものが復元された、見どころ多い一編。", "1988-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CHINA SYNDROME", "チャイナ・シンドローム", "", "", "http://image.eiga.k-img.com/images/movie/46741/315x210.jpg?3251", 122, "人気キャスター、キンバリー・ウェルズは、原子力発電所の取材中、原子炉の事故に遭遇。事故の真相を世論に訴えようとするが、何者かに命を狙われる……。公開当時、実際に事故が発生したため、より現実味を帯びて衝撃を広げた。", "1979-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REMEMBER　THE　TITANS", "タイタンズを忘れない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1971年、ヴァージニア州のアレキサンドリアという田舎町での実話を映画化した感動作。根強く人種差別が残る保守的な田舎町に押し寄せた公民権運動の波と、それに飲まれた地元高校生たちの友情を描く。白人学校と黒人学校が統合され、T・C・ウィリアムズ高校が開校する。フットボールチームも統合され、タイタンズが結成された。統合に反対する住民たちのデモが起こる中、ヘッド・コーチとして黒人のブーンがやってくるが、チーム内での偏見と確執はなかなか消えない。しかし、何度も衝突する選手たちの中に都会育ちの白人選手、ロニーが加わったことで雰囲気は一変、チームも州大会へと進出していく。だが、大事な決勝戦を控えた夜、誰もが予測しなかった悲劇が彼らを襲う……。偏見や差別を乗り越えて勝利を手にしていくタイタンズの姿は人々の心を揺り動かし、当時アメリカが抱えていた大きな壁を崩していくきっかけとなった。主演のブーン役にオスカー俳優、D・ワシントンが扮し、情熱的なコーチを熱演。若手俳優陣のみずみずしい演技も話題になった。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・ここから始まる物語", "喜劇・ここから始まる物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "主演が植木等であり、題名にも喜劇とうたっているものの、実はシリアス・ドラマという珍妙な一編。売れない小説家が講演会で行う自作の再現と、同じことが現実にも起こるという２部構成で、前半が喜劇調に描かれる。霊感少年と記者の物語で、後年、斎藤耕一はヴィットリオ・デ・シーカ監督の「ミラノの奇蹟」になるはずだったと語っている。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハレンチ学園", "ハレンチ学園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "永井豪の同名マンガを映画化。聖ハレンチ学園では卒業式が催されるが、生徒は全員しらけきった様子。校長は教育委員到着の知らせになにかとその場をつくろうが、それを知った生徒たちは教師を脅迫し全員にオール５を認めさせる。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE BLECHTROMMEL", "ブリキの太鼓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "ポーランドのダイツィヒ（現在のグダニスク）を舞台に1927年からの45年間の暗い時代を少年オスカルの目を通して描く。３歳の誕生日を迎えたオスカルは大人の世界を拒絶するため自ら地下の倉庫へ転落。その日から彼の成長は止まり、同時に奇声を発するとガラスが壊れるという超能力を身につける。オスカルの母は夫アルフレートの目を盗んで従兄と情事を重ねるが、良心の呵責に耐えかね、成長しないオスカルへの絶望もあって自殺する。そのころヒトラーが政権を取り、町ではユダヤ人狩りが始まろうとしていた……。奇声を発しながらブリキの太鼓を叩き続ける成長を止めたオスカルは、ナチス政権下のポーランドの歴史そのものであり、シュレンドルフは、原作者グラスの意図する二重構造の世界を的確に捉えている。超能力やサーカスの小人たちなど非日常的要素を加えながらポーランドの暗い時代をストレートに表現した映像が強烈な印象を残す。難しいオスカル役をこなしたベネントの名演も素晴らしい。", "1978-01-01", "7,42,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Final Fantasy: Spirts Within", "ファイナルファンタジー", "", "", "http://image.eiga.k-img.com/images/movie/1391/315x210.jpg?4470", 106, "監督・原作はゲームシリーズを手がけた坂口博信。アニメーション監督は「タイタニック」のアンディ・ジョーンズ。ステージング監督は「マトリックス」のストーリーボードを担当したタニ・クニタケ。VFXスーパーバイザーはデジタル・ドメインで「フィフス・エレメント」「タイタニック」に携わったレモ・バルセルス。ゲーム「FF」の小高忠男、ジェームズ・ロジャーズらも参加。声の出演には、ミンナ・ウェンはじめ有名俳優が集合。", "2001-09-15", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRINCE OF THIEVES", "ロビン・フッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "リチャード王の親友アランとその妹マリアンはシャーウッドの森で何者かに襲われ、危ないところを義賊ロビン・フッドに救われる。実はアルウィン卿が、娘の政略結婚に邪魔なアランを襲ったのだ。アルウィン卿は、今度はマリアンを誘拐し、“ロビンの首と引き換えに釈放する”と脅迫してくる。「ハリケーン」などの美男スター、J・ホールがロビン・フッドに、マリアン役のP・モリソンは1930～40年代ターザン映画などで活躍した女優。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃尻娘・プロポーズ大作戦", "桃尻娘・プロポーズ大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "橋本治原作により大ヒットしたシリーズの第３作。受験に失敗したレナは、アングラ劇団について北海道に女優修行に行った裕子を訪ねて自分も北海道へ渡る。レナは劇団員の明子の家に泊めてもらうことになるが、明子の弟で北大志望のガリ勉青年の秀敏はレナに惹かれていく。レナも秀敏が気になるが……。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRANKENSTEIN", "フランケンシュタイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "メアリー・シェリー女史の書いた古典的な怪奇小説の映画化。生命の創造の夢に取り憑かれた科学者フランケンシュタインが作り上げたモンスターが、村人を虐殺していくというもの。モンスター役のカーロフは、科学が生んだ悲劇の怪物を好演し、この一作で脇役から怪奇スターとしての座をつかみとった。DVDは「ユニバーサル・モンスター・コレクション【赤】」に収録。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRIDE OF THE MONSTER", "怪物の花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "“映画史上最低の監督”エド・ウッド初のSF映画。放射線によって人間を巨大化する研究をひそかに続けるボーノフ博士。森の奥深くにあるその研究所に紛れ込んだ女性記者ジャネットは、催眠術をかけられ人体実験の危機に遭う。そのとき真の愛に目覚めた博士の助手ロボが彼女を助けるが、逆上した博士は自ら放射線を浴びて巨大化し彼らに襲いかかる。低予算で怪物を作る金などなかったため、登場する巨大なタコやワニはすべて実物。人間との対決シーンは、すべて編集でごまかしている。DVDは「エド・ウッドDVDコレクションBOX」に収録。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EINSTEIN’S　BRAIN", "アインシュタインの脳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "天才物理学者アインシュタインの脳を探し求める大学教授の姿を追ったドキュメンタリー。1995年の山形国際ドキュメンタリー映画祭で上映され、フィクションとも思える奇想天外なユーモアで評価を得た異色作だ。近畿大学の助教授、杉元賢治が、敬愛するアインシュタインの脳を一目見たいがためアメリカのプリンストン大学を訪れる。ところが、保存されているはずの脳は紛失していた。杉元はカタコトの英語で、かつて研究室に在籍していた病理学者の行方を追い始める。", "1994-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANZIO！", "アンツィオ大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "イタリア半島のアンツィオとネッツノー海岸に上陸した米英連合軍。アメリカ人特別通信員のエニスは連合軍とともに、ドイツ軍相手に死闘を繰り広げる。生き残った数人の兵士たちは、農民の家族の協力を得て、ドイツ軍の堅固な要塞へ戦いを挑むのだが……。R・ミッチャムの好演が光る戦記ものの佳作。", "1968-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PPIL　GU", "初恋白書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "韓国の大物俳優ぺ・ヨンジュンが、映画初主演作「スキャンダル」以前に出演していた幻の青春映画。美人教師と高校生の少年が織りなす学園ラブ・ストーリーで、ヨン様は不良グループの一員役で登場する。不良グループを率いるピルグは新任教師ユナに一目惚れ。しかし彼女の気を引こうとするあまり暴走したピルグは、退学になってしまう。", "2005-06-18", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RADIO", "僕はラジオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "実話をもとにした人間ドラマ。ラジオだけが友達だった知的障害の青年。仕事もなく町をさまよっているだけの彼に、高校のアメフト・コーチが声をかけ……。そんな二人を中心に広がっていく友情の輪を温かいまなざしで描写。他人とかかわることの大切さを感動的にうたい上げている。主人公ラジオに扮したオスカー俳優C・グッディングJr.の好演も見もの。州大会での優勝を目指すアメフト部の鬼コーチが、学校の周囲をうろつく知的障害者の青年ラジオと出会う。なぜかラジオのことが気になったコーチは、ラジオを仲間として招き入れ、彼はたちまち人気者になるが……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE UNBELIEVABLE TRUTH", "ニューヨーク・ラブ・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ニューヨーク郊外の町を舞台に、乾いた人間関係と、輝くばかりの恋をユーモラスに描いたテレフィーチャー。オードリーは、かわいくて成績も優秀な女子学生だが、物事に無感動で時々おかしなことを口走ったりする一面も。そんな彼女が、刑務所帰りのジョシュに恋をする。ジョシュは朝から晩まで自動車修理工場で働き、過去に恋人を殺したという噂の他は謎に包まれていた……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CREEP", "0：34〈レイジ34フン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "古い歴史を持つ英国・ロンドンの地下鉄を舞台に、深夜の構内に閉じ込められた女性のサバイバルを描くスリラー。フランカ・ポテンテが不条理な恐怖を抱えて逃げるヒロインを熱演。ベンチで寝過ごして終電を逃し、地下鉄構内に閉じ込められてしまった女性ケイト。必死に出口を探す彼女に正体不明の殺人鬼の魔の手が迫る。", "2005-07-16", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Iwo Jima 36 Days of Hell", "硫黄島　地獄の36日間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 351, "クリント・イーストウッド監督作「父親たちの星条旗」「硫黄島からの手紙」の公開で注目を集める硫黄島の戦いを、アメリカ軍海兵隊のカメラマンが撮影した貴重な映像や総勢27名の元アメリカ軍兵士へのインタビューなどから振り返るドキュメンタリー。アメリカ軍海兵隊員の約3分の1が命を落としたとされる硫黄島の戦い。硫黄島攻撃計画から36日間に及ぶ激戦の末の占拠、そして第2次世界大戦の終結までを5時間51分に渡って映し出す。", "2007-01-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE WOMEN", "若草物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "L・M・オルコット原作の不朽の名作、８度目(TV映画を含める）の映画化。19世紀半ば、南北戦争時代のマサチューセッツ州を舞台に、家族の深い愛と絆によって育まれた４姉妹の成長を描いた感動ドラマで、少女たちの恋のときめきや夢、悩みが丹念に綴られている。今回初めて製作、監督、脚本を女性が務め、ドラマの中のセリフや考え方などが、これまでにない現代女性の共感を呼ぶものとなっている。次女役のW・ライダーは快活なジョーを等身大で演じ、アカデミー主演女優賞にノミネートされた。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VENUS IN FURS", "毛皮のヴィーナス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "湖畔のホテルを仕事場にしている作家の趣味は、壁の穴から隣室をのぞくことだった。ある日、その部屋に入ってきたのはグラマラスなモデルのワンダ。白い毛皮をまとってオナニーにふける彼女を目にした時から、彼は愛と倒錯の世界に堕ちて……。作家ザッヘル・マゾッホが書いたマゾヒズムの古典を映画化。", "1971-01-01", "7,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HOUSE THAT DRIPPED BLOOD", "ブラッド・ゾーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "霧が立ち込める林の中にある古めかしく格調高い館。ここを借りた人々は次々と奇怪な死に見舞われていくが、借りたいという人間はあとを絶たない……。「サイコ」のR・ブロック原作、P・カッシング、C・リー共演でアミカス・プロが作った４話のオムニバス・ホラー作品。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEET ANGEL MINE", "スウィート・エンジェル・マイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「グレアム・ヤング毒殺日記」のプロデューサー、S・テイラー製作によるホラー・サスペンス。行方不明の父親を探して、カナダのある島にやって来た英国人青年ポール。寂れた一軒家で美少女ルシーンと出会った彼は恋に落ちるが、ルシーンの母親は決してそれを許そうとはしなかった。ついには島から離れたモーテルで一夜を明かした二人だったが……。過剰に抑圧された性の行方と、常軌を逸した母性愛の異常ぶりが見もの。ビデオ発売時タイトルは「猟奇殺人心理」。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Public Enemies", "パブリック・エネミーズ", "", "", "http://image.eiga.k-img.com/images/movie/53851/315x210.jpg?1939", 141, "「ヒート」「インサイダー」のマイケル・マン監督が、伝説の銀行強盗ジョン・デリンジャーの半生をジョニー・デップ主演で映画化した伝記アクション。大恐慌時代のアメリカ中西部を舞台に、大胆不敵な手口で銀行強盗と脱獄を繰り返し、FBIから「パブリック・エネミーNO.1」（社会の敵）として指名手配されたデリンジャーと彼の恋人ビリーの逃亡劇が描かれる。共演にクリスチャン・ベール、マリオン・コティヤール。", "2009-12-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("台北の朝、僕は恋をする", "台北の朝、僕は恋をする", "", "", "http://image.eiga.k-img.com/images/movie/55841/315x210.jpg?2235", 85, "ビム・ベンダース製作総指揮のもと、台湾の新鋭アービン・チェン監督が夜の台北を舞台に描いたロマンティック・コメディ。台北で暮らす青年カイは、恋人がパリへ留学して以来、彼女のことを思っては本屋でフランス語の本を読むという寂しい日々をおくっていた。一方、本屋で働くスージーは、毎日のように来店するカイのことが気になりはじめる。そんなある日、カイはパリ行きの資金を工面するため、怪しげな運び屋の仕事を請け負う。", "2011-03-12", "53,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PISCINE", "太陽が知っている", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "A・ドロンが重要参考人とされた“マルコヴィッチ事件”のさなかに製作された記録破りの大ヒット作。南フランスのプール付きの別荘を舞台に展開する愛憎のドラマと完全犯罪。映画のストーリーが実際の事件と重なり合い、話題を呼んだ傑作ミステリー。", "1968-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Mummy Returns", "ハムナプトラ2 黄金のピラミッド", "", "", "http://image.eiga.k-img.com/images/movie/1551/315x210.jpg?2918", 130, "1933年、リックとエブリンがハムナプトラから生還して8年。2人は8歳になる息子アレックスと共に幸せな生活を送っていた。再びエジプトへ足を運び、新たな秘宝を探していたリックたちは、伝説の巨人スコーピオン・キングが残したというブレスレットを発見する。しかしリックたちがロンドンへ帰国した後、そのブレスレットを腕にはめたアレックスが何者かに誘拐されてしまう。同じ頃、街では大英博物館の部屋の奥で、あのイムホテップが再び甦ろうとしていた……。", "2001-06-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チルスとマンス", "チルスとマンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「旅人〈ナグネ〉は休まない」などでイ・チャンホ監督のアシスタントを務めてきたパク・カンスの監督デビュー作。マイアミ行きを夢みる若者チルスと、30代後半になったというのに看板の絵かきをしてその日暮らしの生活を続けるマンスとの奇妙な友情を描いていく。マンス役は「風吹く良き日」「ディープ・ブルー・ナイト」の韓国映画界のトップスター、アン・ソンギ。", "1987-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("田園交響楽", "田園交響楽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "山本薩夫にとっては、成瀬巳喜男の助監督から監督になって３作目。A・ジイドの同名小説を翻案したもので、当時東宝きってのスターだった、原節子のために監督自らが立てた企画である。北海道の寒村の小学校長が、盲目の娘を引き取って育て、彼女は成長して感受性豊かな女性になり、校長の弟と恋に落ちていく……。カメラの宮島義勇と山本監督の初コンビ作で、海外ではジャン・コクトーにも評価されたと監督は語っている。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AMAZING COLOSSAL MAN", "戦慄！ プルトニウム人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "巨大モンスター映画は数あれど、「キング・コング」「ゴジラ」と並び映画史にそびえる最高峰の１本といえるのが本作。プルトニウム爆弾の実験で放射能を浴びたマニング大佐は突然変異で体が巨大化し始める。政府のトップ・シークレットとして隔離されたマニング大佐だが、その成長は止まらず、身長は18メートルまで達した。自暴自棄となった大佐は逃げ出し、ラスベガスの街で暴れ出すが……。巨大ものの第一人者B・I・ゴードン監督が、名カメラマン、J・バイロックの絶妙の撮影と、見事なミニチュア・ワークを得て作り上げた巨人のラスベガス襲撃シーンの迫力と美しさは、怪物映画史上の白眉中の白眉。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今度は愛妻家", "今度は愛妻家", "", "", "http://image.eiga.k-img.com/images/movie/54101/315x210.jpg?3335", 131, "俊介はかつての人気カメラマンだが、今はろくに仕事もせずだらけた日々を過ごすダメ亭主。結婚10年目を迎えたある日、何かと世話を焼く妻のさくらが旅行に出かけ、しばしの独身気分を味わう俊介だったが、さくらが何日待っても戻らないことに次第に不安を募らせていき……。2002年に初演された大ヒット舞台を、「世界の中心で、愛をさけぶ」の行定勲が映画化。豊川悦司と薬師丸ひろ子が結婚10年目の夫婦を演じるラブファンタジー。", "2010-01-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悦楽", "悦楽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "山田風太郎の「棺の中の悦楽」を大島渚監督が映画化。思いを寄せる女性・匠子が結婚したことに脇坂はショックを受けた。実は脇坂には匠子のために人殺しを犯した過去があった。絶望した脇坂はその殺人がらみで手に入れた大金で、次々と女性遍歴を始める。その悦楽の果てにあるものとは……。悦楽を買うことしかできない現代人の官能の世界と性の深淵に大胆に挑んだ大島渚監督の野心作。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("URAHARA", "URAHARA", "", "", "http://image.eiga.k-img.com/images/movie/53521/315x210.jpg?3972", 0, "小坂由佳、山本早織ら人気グラビアアイドル共演による青春ドラマ。進路を決めないまま高校を卒業した彩は、無駄な毎日を過ごすことに焦りを感じていた。そんな思いを発散させるかのように壁に落書きをしていた彼女は、偶然通りかかったデザイナー志望の奈央美に声を掛けられる。自分とは正反対の奈央美の生き方に魅力を感じた彩は、デザインに行き詰っているという奈央美のため、裏原宿に店を立ち上げようと資金稼ぎを始めるが……。", "2008-03-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KIM", "キム　ある少年の闘いと冒険の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "舞台は1890年代のインド。孤児のキムは仏教の聖者ラーマと出会い、親子の契りを結んだ。そんなある日、キムが実はイギリス軍の兵隊の息子であることが判明。やがて彼は訓練を受け、ロシア軍のインド侵入に立ち向かうが……。", "1984-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Management", "10日間で彼女の心をうばう方法", "", "", "http://ecx.images-amazon.com/images/I/51LngfcTdML._SL210_.jpg", 94, "セールスウーマンがビジネスの旅の途中で出会ったホテル・マネージャーにひと目ボレされて、なんとか彼から逃れようとするコメディ。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("呪霊 THE MOVIE", "呪霊 THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "あまりの恐ろしさで話題を呼んだビデオシリーズの劇場版。病院を舞台に３話の恐怖が描かれる。虫垂炎で入院した高山市子の病室には、毎晩不思議な少女が現れていた。日に日に容態が悪化していく市子を不審に思った同級生の雅美が調べてみると、その病院では年に一度、異様な儀式を行っていることが分かり……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DEMOLITIONIST", "デモリショニスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "西暦20XX年。犯罪地下都市“メトロシティ”に、市長の計画で最新技術を駆使した女戦士“デモリショニスト”が誕生する。彼女の元の体は、凶悪犯マッド・ドッグに惨殺された潜入捜査官アリッサ。しかしバイオロイドとしてよみがえったアリッサは人の心を失い、人命より犯人逮捕を優先する冷酷な戦士になっていた。やむなく市長はアリッサの命を絶つことを指令するが、彼女は警察を振り切り行方をくらましてしまう。アリッサは心の中で、自分をこんな体にしたマッド・ドッグへの復讐を誓っていたのだった。「プレデター」や「エイリアン２」などの特殊メイクで知られるR・カーツマンの初監督作品。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("密陽", "シークレット・サンシャイン", "", "", "http://image.eiga.k-img.com/images/movie/53431/315x210.jpg?2387", 142, "夫を亡くし、生きる希望を失っていた32歳のシネは、夫の故郷の地方都市ミリャン（密陽）でピアノ教室を開いて新たなスタートを誓う。だが、ミリャンでも彼女を新たな悲劇が襲い……。監督は「ペパーミントキャンディ」「オアシス」のイ・チャンドン。主演のチョン・ドヨンが第60回カンヌ国際映画祭で主演女優賞を受賞。共演はソン・ガンホ。", "2008-06-07", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みんなのいえ", "みんなのいえ", "", "", "http://image.eiga.k-img.com/images/movie/1541/315x210.jpg?3375", 115, "30代半ばの飯島夫婦は、マイホームを夢見て新進気鋭のデザイナー柳沢に家の設計を依頼する。一方、施工は妻の父親の大工・長一郎が行うことに。しかしアメリカ建築をデザインするアーティスト感覚の柳沢と、とにかく頑丈な和風の家を建てようとする職人肌の長一郎はことごとく対立。夫婦には想像を絶するトラブルが襲いかかり、状況は混沌を極めていく。出来つつある家の様子も当初の予定からずいぶんと狂ってきているのだった……。", "2001-06-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海と毒薬", "海と毒薬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "太平洋戦争末期に実際にあった米軍捕虜に対する生体解剖事件を基に書かれた遠藤周作の問題作を、社会派の熊井啓が映画化。“文芸スプラッタ映画”といわれるほど、手術シーンがリアルに描かれている。輸血の代わりに食塩水を注射することがどこまで可能か、といった残酷な実験が戦争の名のもとに平然と実行されていく恐ろしさを、二人の青年医師の目を通して問いかけている。医学研究生の勝呂は、米軍捕虜の生体解剖に参加させられてひどく苦悩し、良心の呵責に苦しむ。それとは対照的に、戸田は良心の呵責にこそ苦しまないものの、人間的な感性が欠如しているのではないかと悩み、勝呂と対立する。原一男監督が監督補として参加している。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("揺滾青年／ROCK KIDS", "ロック青年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "中国第五世代の鬼才、ティエン・チュアンチュアンが珍しく手掛けた青春娯楽映画。“一般大衆からは遠い距離にある”といわれてきた彼がその定説を覆し、多大な興行成績を上げた。芽の出ないロック・ダンサーが、年上のデザイナーに拾われ、振付師として成功。だが忙しさの中で虚無感に苛まれていく。ミュージカル仕立てのプログラム・ピクチャーだが、随所にティエン監督らしい暗示的な場面描写が盛り込まれている。", "1988-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRINCESS BRIDE", "プリンセス・ブライド・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "中世らしき時代のとある王国を舞台に、ハンサムな馬丁と美しいお姫様とが、数多くの困難を乗り越えて、恋を成就させるまでを描いたファンタスティックなラブ・ストーリー。大男や怪獣、６本指の男といった絵本から抜け出てきたかのようなキャラクターが続々登場し、神秘的なムードを盛り上げている。監督は「スタンド・バイ・ミー」で、少年時代における繊細な感受性を描いたR・ライナー。音楽はダイアー・ストレイツのリーダー、M・ノップラーが担当。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIDGET GOES HAWAIIAN", "ヤング・ハワイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "４人の男女が織り成す青春ドラマ。恋人のジェフと喧嘩をしたままハワイ旅行に立ったギジェットは、飛行機の中でアビーとエディの二人組と知り合う。エディはギジェットに好意を寄せた。ある日、海から上がった二人はどちらからともなく接吻、その時二人の前にジェフが現れる。ギジェットに新しい恋人ができたと誤解したジェフは、アビーとことさら仲良くふるまうのだが……。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MASHGH SHAB", "ホームワーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "A・キアロスタミ監督がイランの教育問題に斬り込んだドキュメンタリー。監督自らが“宿題をやってきた？”“なぜ宿題をしてこなかったの？”などと質問を投げかけ、カメラはそれに答えようとする小学生の反応を捉えていく。ごくシンプルな構成ながらも、宿題ができなかった様々な理由を述べる子供たちの姿から、イラン社会の実情が間接的に浮かび上がっていく。", "1989-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Uninvited", "ゲスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHAOLIN　VS.　EVIL　DEAD", "少林キョンシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "80年代に日本でも大流行した“キョンシー”と、伝統あるカンフー映画を融合させた痛快アクション・ホラー。ライバル同士の法術合戦とキョンシー軍団に加え、ゾンビや隕石などありえないスケールで物語が展開。主演は「キル・ビル」にも参加していたカンフー界のベテラン、ゴードン・リュウ。霊を成仏させる白道士と、悪霊の根絶を目指す黒道士。兄弟弟子同士が戦いを繰り広げる中、邪悪なキョンシー大魔王が復活しようとしていた。", "2005-11-12", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISSING", "ミッシング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "1973年９月、南米チリの軍事クーデターのさなか、アメリカ人が一人サンチャゴの自宅から姿を消した。近所の人の話では、兵士らしい者に銃を突きつけられ小型トラックに乗せられていったという。また別の目撃者によれば、そのトラックは反政府運動者たちを監禁しているサッカー競技場へ向かったという。突如行方不明になった彼を追って、妻がそして父親が、ことの真相を求めて国家権力と正面から対決する。時、場所、出来事、人物すべて事実に基づく、社会派コスタ・ガヴラス入魂の会心作である。レモンとスペイセクの難しい役柄を見事にこなした熱演ぶりが特に印象に残る。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Future of Food", "食の未来", "", "", "http://image.eiga.k-img.com/images/movie/53151/315x210.jpg?1979", 90, "遺伝子組み換えを始めとする近代型農業の危険な実態を描き出し、アメリカの遺伝子組み換え作物反対運動に影響を与えたドキュメンタリー。企業による支配が進んでいる近年の農業。大量生産が可能になった一方で、遺伝子組み換え作物を巡る企業と政府の癒着や健康被害、環境汚染など様々な弊害が生じている。専門家や農家らの証言を交えながらその問題点を検証し、消費者ひとりひとりが正しい選択をすることの大切さを訴える。", "2007-12-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("縁切り村　デッド・エンド・サバイバル", "縁切り村　デッド・エンド・サバイバル", "", "", "http://image.eiga.k-img.com/images/movie/56441/315x210.jpg?4521", 80, "人気アイドルグループ「AKB48」の藤江れいなが映画初主演を務める3Dホラーサスペンス。ダブル主演にミュージカル「テニスの王子様」の八神蓮。グルメ番組の撮影に奥多摩のとある村を訪れたTVクルーたちは、観光地や食文化の豊かさに気をよくしていた。しかし、村の忌まわしき過去にまつわる怨霊「縁切り様」の呪いに翻弄され、1人また1人と犠牲になっていく……。監督は「ヨムトシヌ DEATH COMIC」「渋谷怪談・サッちゃんの都市伝説」の椿光一。", "2011-10-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("罪とか罰とか", "罪とか罰とか", "", "", "http://image.eiga.k-img.com/images/movie/53951/315x210.jpg?3112", 110, "「1980」「グミ・チョコレート・パイン」のケラリーノ・サンドロビッチが、「神童」の成海璃子を主演に描くコメディ。共演に「フレフレ少女」の永山絢斗、「俺たちに明日はないッス」の安藤サクラほか。売れないグラビア・アイドルのアヤメは、売れっ子のライバル、モモが表紙の雑誌を衝動的に万引きしてしまうがあっけなく捕まり、見越婆警察署の1日警察署長を務めることに。その警察署でアヤメは元彼に再会し、封印していた想いが再燃する……。", "2009-02-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かぞくのひけつ", "かぞくのひけつ", "", "", "http://image.eiga.k-img.com/images/movie/53131/315x210.jpg?4492", 83, "井筒和幸監督作品などで助監督を務めてきた小林聖太郎が初メガホンを取り、大阪で暮らす家族の日常を描いた人情喜劇。高校生の賢治には付き合って半年になる彼女がいるが、ある悩みが原因で手も握れず、愛想を尽かされそうになっていた。ある日、嫉妬深い母から女癖の悪い父の尾行を頼まれた賢治は、父の浮気現場を発見。父をあきらめるよう愛人を説得するが、翌日、両親が営む不動産屋のバイト募集にその愛人が現われ……。", "2007-12-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("津軽じょんがら節", "津軽じょんがら節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "津軽の寂れた漁村。そこは、東京のバーで働いていたイサ子の郷里だった。イサ子はヤクザらしい男を連れている。男は徹男といい、よその組の幹部を刺したために追われていて、彼女は彼をかくまうために戻って来たのだった。さっそく海辺の小屋で二人の新しい生活が始まるが、単調な毎日が男にはやりきれない。ただ、盲目の少女・ユキと遊んでいる時だけ男はとても明るい……。都会の空気にしかなじめないはずの男が、次第に日本的な風土に惹かれていく様を描いた斎藤耕一の代表作。津軽の荒涼とした海と津軽三味線のもの哀しい音色が、情感を盛り上げている。原作は長部日出雄の同名直木賞受賞作。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGIE", "愛に気づけば…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "女流監督M・クーリッジが、未婚の母の苦悩を繊細なタッチで綴ったヒューマン・ドラマ。妊娠したアンジーは、退屈な結婚生活を恐れて恋人のプロポーズを拒絶。やがて出産を終えた彼女は、新しい恋の破局や生き別れた母親との再会という出来事を通し、現実に目覚めていく。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK KNIGHT", "ブラック・ナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "喜劇俳優M・ローレンスが、持ち味の軽いノリを発揮した奇想天外なコメディ。中世を再現したテーマパークで働く男ジャマールが、お濠に落下。這い上がると、何とそこは本物の中世の世界だった。乱世にとまどいつつも、反王勢力の侍女に恋したジャマールは、反乱軍の側について戦うことになり……。ナイキなどの現代ブランドを身につけた主人公がカルチャーギャップを体現し、笑いを誘う。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悲しき天使", "悲しき天使", "", "", "http://image.eiga.k-img.com/images/movie/33711/315x210.jpg?4457", 113, "ある殺人事件を巡って交錯する3人の女性たちの人生模様を描いたヒューマンドラマ。「恋する女たち」で10代の少女たちの揺れ動く心をみずみずしいタッチで描いた大森一樹監督が、本作ではそれぞれ境遇の異なる30代の女性たちの生きざまを鮮烈に描き出す。恋人との関係に悩みながらも事件を追う女刑事・薫に高岡早紀、被害者の娘で、容疑者として追われる那美に山本未來、那美の元恋人の妻・敦子に河合美智子が扮する。2008年3月15日より、渋谷シネ・アミューズにて2週間限定レイトショーで再上映。", "2006-10-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("F2グランプリ", "F2グランプリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "F2レースのレーサーたちの、愛と野望を描いた作品。メインとなるのは、中井貴一演じる中野と、田中健扮する井本との争い。これに中野の恋人役・石原真理子が絡む。スタンド・インとして、元F-1ドライバー中嶋悟をはじめとするプロのレーサーが出演している。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SALVATION HUNTERS", "救ひを求める人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "マレーネ・ディートリヒとのコンビ作で知られるJ・V・スタンバーグ監督の処女作。アメリカへとやって来た移民たちの惨めな姿を、ロサンゼルス近郊の港湾都市を舞台に描いている。後のスタンバーグ作品に見られる人工的な演出は希薄だが、港湾都市に林立する巨大な機械を生々しく捉えた無機的な映像、そして人物たちの抑制された演技と表情が印象深い。", "1925-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CROSSOVER DREAMS", "クロスオーバー・ドリーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "サルサ界のトップ・スター、ブラデス主演で描いた人間ドラマ。彼が扮するシンガーがビッグになろうともがき、失敗していくさまを恋人や友人とのエピソードをはさみながら描いている。バックに流れる哀愁漂うサルサのリズムも魅力。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TERMINAL ENTRY", "ファイナル・オペレーション ～コンピュータの侵略～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "コンピュータ・ゲームを楽しむ６人の若者が、あるときテロリスト・コミュニケーション・ネットワークに参加。外交官の暗殺、石油コンビナートの爆破など、彼らのインプットするプログラムはテロリストを動かした。しかし、このゲームの最後のターゲットは彼ら自身だったのだ……。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神田川", "神田川", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "かぐや姫が歌って一大ヒットとなった『神田川』を作詞した喜多条忠の自伝的小説をもとに、人形劇サークルに籍を置く大学生と一人の少女との同棲生活を描く。かぐや姫伝説を絡ませて現代の愛を表現した点は新鮮。当時の流行や風俗も垣間見ることができる。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OBRAZY STARE´HO SVETA", "百年の夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "チェコの奥地ファトラ山地で暮らす老人たちの姿をインタビューを交えて撮り上げたドキュメンタリー。病気で立つことができず膝で歩いて家を建てた男、宇宙に飛び立つ日を夢見る羊飼い、鶏に聖書を読んで聞かせる者。人里離れた厳しい自然の中で紡ぎ上げられる生と死についての哲学世界がここにある。", "1972-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MONEY PIT", "マネー・ピット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「マネー・ピット」とは“金喰い虫”のこと。地価高騰の日本ならゼロがいくつつくか見当もつかない豪邸を、超破格値で手に入れたある若夫婦。幸福を約束してくれるはずのその住まいが、生き物さながらに崩壊していく金喰い虫で……。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRANGERS", "ストレンジャーズ 情事の報酬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "株式仲買人のゲーリー・ヤングは妻との離婚訴訟で経済的に脅かされ、恋人とも破局を迎えつつあった。そんなある日、ゲーリーの前にエキゾチックで奔放な美女アンナが現れ、二人は一夜をともにした。ゲーリーにとっては火遊びのつもりだったが、アンナはゲーリーに異常な執着をみせる。やがて妻が変死。事態は恐ろしい展開を迎えることに……。", "1990-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HO¨HENFEUER", "山の焚火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "スイス・アルプスの中腹。私設のケーブルカーを持ち、郵便や通信販売の品物も村まで月に一度取りに行くというへんぴな土地で、祖父母の家は代々村から孤立して農牧業を営む。“坊や”と呼ばれる10代半ばの息子はろうあで、話せず聞こえぬもどかしさから、ときどき発作を起こす。彼の勉強などの面倒をみているのは教師志望だった姉のベッリ。夏の終わり、息子はカンシャクを起こして草刈り機を壊し、父に怒られ家出、山の上の小屋で一人で暮らしながら石垣作りに熱中した。様子を見に来た姉と焚火を囲み、二人だけの一夜を満天の星の下で過ごす。やがて雨の日に訪れた父と和解し、息子は家に戻る。だがそのころから姉の体に変化が。お腹をさすりながら自分の裸体を鏡に映す姉……。雪の日に母に打ち明けたが父は激怒し、思いもよらぬ悲劇がこの一家に訪れる。四季の自然、ろうあ少年の視覚を伝える音と映像の繊細な演出。古典悲劇のように美しい感動作。", "1985-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROTOTYPE", "プロトタイプ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "西暦2077年、人類はオメガと呼ばれる改造人間たちを創った。しかし製造から数年を経て自我を持ったオメガたちは、反乱を開始。彼らを抹殺すべく、政府は新たに、殺人マシン“プロトタイプ”を開発、オメガ絶滅を図る。だが、たった一人、虐殺を逃れたオメガの子供がいた。やがて20年がたち、子供は美しくタフな女戦士チャンドラーに成長していた……。「ブレードランナー」「ハードウェア」を思わせるサイボーグ・アクション。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AYA", "AYA〈アヤ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "幼い頃から11年間を日本で過ごし、京都大学に学んだ女流監督スルーン・ホアスの処女長編。戦争直後、オーストラリア兵と結婚して異国の地に渡った日本女性アヤの姿を通して、国籍や文化の違いを超えた普遍的な愛の在り方を追求する。アジア・太平洋映画祭をはじめ、数々の国際映画祭で賞を受けた１本。", "1990-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミニモニ。じゃムービー・お菓子な大冒険！", "ミニモニ。じゃムービー・お菓子な大冒険！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "モーニング娘。から派生したユニット、ミニモニ。が主演するショートムービー。彼女たちが繰り広げる大冒険を実写＋CGアニメで描いたファンタジー。ケーキが自慢の“ミニモニ。カフェ”を舞台に、お菓子の世界に迷い込んだミニモニ。メンバーたちの活躍を描く。ひょんなことからお菓子の世界に迷い込んだミニモニ。の４人。大切な店のパーティーまでに彼女たちは現実世界に戻ることができるのか？", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊賀の乱　拘束", "伊賀の乱　拘束", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "伊賀の国が滅ぼされる直前、伊賀復興のために密命を受けた忍者たちの戦いを描いたアクション時代劇。甲賀と内通する伊賀の下忍・義盛の行方を追っていた多岐野、李局、辻壱は、伊賀の忍者だけが知る秘密の洞窟で義盛が死んでいるのを発見する。死体の横には、見知らぬ女忍者が両手を縛られ目隠しされた状態で倒れていた。3人は女を義盛の仲間と疑って問いただすが……。「巌流島」の千葉誠治がメガホンをとる。", "2007-12-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AT CLOSE RANGE", "ロンリー・ブラッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "若く野望に燃えるブラッドJr.にとって田舎町の平凡な毎日はつまらない。家族を捨てた父親ブラッドSr.のヤクザ稼業に憧れ、家を飛び出し父親のもとへ走るが……。S・ペンと、個性派俳優C・ウォーケンの演技のぶつかり合いが見もの。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Atomic Wounds", "核の傷　肥田舜太郎医師と内部被曝", "", "", "http://image.eiga.k-img.com/images/movie/57811/315x210.jpg?4445", 53, "自身の被ばく体験から、戦後66年間にわたり被ばく者の治療と核廃絶運動に献身してきた肥田舜太郎医師の歩みを追ったドキュメンタリー。1945年8月6日、軍医として広島陸軍病院に赴任していた際に被ばくした肥田医師は、原爆の衝撃波や熱戦を直接浴びていない人々までもが、次々と原因不明の死を遂げてくのを目の当たりにする。冷戦下で核配備を進める米国は放射線が人体へ与える影響についてのデータを隠匿し、日本政府も内部被ばくについて被ばく者認定を限定するなどし、被ばく者たちは苦悩を強いらていく。", "2012-04-07", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUG", "燃える昆虫軍団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "カリフォルニアを襲った大地震。その時できた地割れから、恐るべき怪虫が異常発生した。火を噴く昆虫群が引き起こす怪事件の数々。調査に乗り出した科学者は、妻を焼き殺され復讐の鬼と化すが……。リアルな昆虫軍団がグロテスクで迫力もの。少々うす気味悪いSFパニック映画。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザザンボ", "ザザンボ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "沈滞する日本映画界を地方から揺さぶろうとする異端児、渡辺文樹監督が地元・福島で実際に起こった事件を題材に、綿密なリサーチと想像力によってたたきつけた問題作。とある過疎の村で、知恵遅れの少年が首を吊った。マスコミは中学校内で起こった盗難事件の容疑者に、少年を一方的に仕立てた学校側を非難する。が、少年の出生には複雑な家族関係の闇が口をのぞかせていた……。地元民を役者に起用しての、ぎくしゃくとした動きや固い表情が、画面に特異な土俗の匂いを吹き込む。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("転山", "転山", "", "", "http://image.eiga.k-img.com/images/movie/57361/315x210.jpg?4307", 90, "", "2011-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bobby", "ボビー", "", "", "http://image.eiga.k-img.com/images/movie/1021/315x210.jpg?2947", 120, "68年6月5日、アメリカ大統領候補“ボビー”ことロバート・F・ケネディが暗殺された。世界の歴史を変えたと言われるその夜、事件現場のアンバサダーホテルに居合わせた人種も境遇も異なる22人の男女が織りなす人間模様を描く群像劇。俳優としても知られるエミリオ・エステベス監督のもとに、アンソニー・ホプキンス、シャロン・ストーン、イライジャ・ウッド、リンジー・ローハンら豪華キャストが集結。", "2007-02-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎／野田版・鼠小僧", "シネマ歌舞伎／野田版・鼠小僧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "2003年に東京・歌舞伎座で上演された大ヒット人情喜劇『野田版・鼠小僧』。その舞台を高性能カメラで撮影し、スクリーンで上映した新たなる試みの第一弾。立体的なデジタル映像と、臨場感溢れるサウンドは本物の舞台を観るのと遜色なし。演劇界の鬼才、野田秀樹による作・演出とあって現代的で非常に親しみやすい内容になっており、歌舞伎を観たことのない人でも十分楽しめるはずだ。江戸の正月は鼠小僧の芝居が大人気。一方、ずる賢い棺桶屋の三太は金儲けに励む中、実の兄が死んでしまう。ところが三太は悲しむどころか、仕事にもなるし、遺産もあると聞いて喜ぶのだが……。", "2005-01-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パレスチナ1948・NAKBA（ナクバ）", "パレスチナ1948・NAKBA（ナクバ）", "", "", "http://image.eiga.k-img.com/images/movie/53461/315x210.jpg?2434", 131, "世界各地の戦場を捉え続けるフォトジャーナリスト・広河隆一が、40年間に渡ってパレスチナを取材した膨大な量の記録を元に、現地の知られざる歴史と現実を明らかにしたドキュメンタリー。イスラエルが建国された1948年、70万人以上のパレスチナ人が故郷を追われて難民となった。廃墟と化した村の様子や、パレスチナ人、ユダヤ人の証言を通し、パレスチナ人が\"NAKBA（大惨事）\"と呼ぶこの事件の真相をあぶり出していく。", "2008-03-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千利休 本覺坊遺文", "千利休 本覺坊遺文", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "千利休はなぜ死ななければならなかったのか？　歴史の秘める謎にスポットをあてた井上靖の原作を、社会派の熊井啓が映画化。オール男優キャストにより、武士のような求道的生き方をした男、利休の精神面にスポットをあて、同時期に公開された勅使河原宏版の「利休」とは違った重厚な作りになっている。利休が死んで27年後、人里離れた隠宅に住む利休の愛弟子・本覚坊は、ある日、織田有楽斎のもとを訪れる。有楽斎は、今も、利休が秀吉に切腹させられた理由を知りたがっていた。本覚坊は有楽斎に問われるまま、死に至るまでの利休の行動を語り始めるのだが……。かつて同じ熊井啓の「お吟さま」で秀吉に扮した三船敏郎が、今度は自分の道と密約に殉じた男・利休を力強く演じている。ビデオタイトルは「本覺坊遺文　千利休」。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Drag Me to Hell", "スペル", "", "", "http://image.eiga.k-img.com/images/movie/54661/315x210.jpg?3988", 99, "「スパイダーマン」シリーズを手がけるサム・ライミ監督が、10年の構想を経て描くカルト・ホラー。主演に「ホワイト・オランダー」「ビッグ・フィッシュ」のアリソン・ローマン。昇進を間近に控える銀行員のクリスティンは、老婆・ガーナッシュが懇願する不動産ローンの延長を断固拒否する。その夜、クリスティンは狂人と化したガーナッシュに襲われ、聞きなれない呪文のような言葉を浴びる。それからクリスティンの周囲で、次々と不穏な現象が起こり始める。", "2009-11-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本一の男の中の男", "日本一の男の中の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "有能なセールスマン・小野子等は、会社の会長をどなりつけて子会社に左遷される。しかし、ここでも奇抜なアイデアで手腕をふるう小野子と会長の孫娘の間にいつしかロマンスが……。それまでのマドンナ・浜美枝に代わり浅丘ルリ子が共演した痛快な一編である。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOW I GOT INTO COLLEGE", "カレッジ・ウォーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "アメリカ版“受験地獄”を「ポーキーズ」「ザ・カンニング」のノリで描く青春コメディ。落ちこぼれの高校生が、優等生の美少女に恋をする。なんとか彼女の目指す名門大学に一緒に合格したいのだが、彼の成績はどん底で入試センター・テストにも失敗。そこで、彼は、ある方法を思いつく……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELVIS ： THAT’S THE WAY IT IS", "エルビス・オン・ステージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "戦後のアメリカを代表するエンターテイナー、E・プレスリーによる、ラスベガスのインターナショナル・ホテルでのステージを記録した音楽ドキュメンタリー映画。登場するナンバーはざっと25曲。エルヴィスの素顔をかいま見ることができる作品として話題になり、公開時はエルヴィス・ブームが再燃した。DVDタイトルは「エルヴィス・オン・ステージ　スペシャル・エディション」。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Time Traveler's Wife", "きみがぼくを見つけた日", "", "", "http://image.eiga.k-img.com/images/movie/54831/315x210.jpg?4396", 110, "自分の意思とは関係なく、時空を超えて過去や未来へ行き来してしまう男ヘンリーと、そんな彼と運命的な出会いを果たし、結婚した女性クレアの生涯の恋を描いたラブストーリー。「ゴースト／ニューヨークの幻」の脚本家ブルース・ジョエル・ルービンが、ベストセラー小説を脚色、「フライトプラン」のロベルト・シュベンケ監督がメガホンを取った。主演は「きみに読む物語」のレイチェル・マクアダムス、「ミュンヘン」のエリック・バナ。", "2009-10-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地上最強のカラテ", "地上最強のカラテ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "カラテ映画のブームのなか、真の空手家たちの姿をとらえ、スマッシュヒットを記録したドキュメンタリー。極真会館館長、大山倍達の悲願であった、オープントーナメント全世界空手道世界選手権が1975年11月、東京都体育館で開催された。その熱戦の模様と、大会にいたるまでの選手たちの過酷なトレーニング、大山の悲壮な決意を映し出していく。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAR AMERICA：LETTERS HOME FROM VIETNAM", "ディア・アメリカ 戦場からの手紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ベトナムの戦場で戦った若き兵士たちが、家族や友人、あるいは恋人に宛てて書き送った手紙をもとにして構成されたドキュメンタリー映画。当時ベトナムで兵士たちが撮った８ミリなどプライベート・フィルムやニューズ・リールの映像に手紙を朗読する声が重ねられていく。戦場での兵士たちの楽しそうな姿に当の本人の手紙が読まれていき、映像と音声との隔たりから彼らの苦しみが力強く伝わり、悲痛な思いを禁じえない。朗読するのは、S・ペン、M・J・フォックス、R・デ・ニーロ、M・ディロン、K・ターナーといったスターたち。バックに流れる1960年代のポップスも、ベトナムで過ごし、死んでいった多くのアメリカの若者たちを讃えているようで哀しい。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Finding Neverland", "ネバーランド", "", "", "http://image.eiga.k-img.com/images/movie/1561/315x210.jpg?1510", 100, "1903年のロンドンを舞台に、実在した作家ジェームズ・M・バリが名作「ピーター・パン」を書いた背景にあったエピソードを、「チョコレート」の監督マーク・フォースターが映画化。バリは父を失い母と暮らす幼い4人兄弟と出会い、彼らとの交流に触発されて「ピーター・パン」を構想していく。3男のピーター少年を演じたフレディ・ハイモアはジョニー・デップの推薦でティム・バートン監督の「チョコレート工場の秘密」に出演。", "2005-01-15", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("avec mon mari／アベックモンマリ", "avec mon mari／アベックモンマリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "PFFで２度の入賞経験を持つ大谷健太郎の劇場映画デビュー作。現代的な“理想の夫婦”の在り方を、ナチュラルかつ生々しい会話劇のスタイルで綴っている。雑誌編集者の美都子は仕事はデキるが家事はまったく駄目。フリー・カメラマンのタモツは仕事はヒマだが家事はテキパキこなす。そんな対照的な性格の夫婦が、タモツの浮気疑惑によって離婚するはめに。やがてモデルのマユ、その愛人の中崎を巻き込んで、男女４人の関係はどこまでもモツレていく。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA ROUE", "鉄路の白薔薇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "大作「ナポレオン」などで知られる、フランス・サイレント期の巨匠A・ガンスが描いた人間ドラマ。機関車に乗務する機関士とその息子、そして鉄道事故から救い出し、自らの子として育てた娘の３人を軸に、濃密なドラマが形成される。物語は娘ノルマを中心に、次第に彼女を恋するようになってしまう機関士の父親シジフとその息子エリの苦悩が綴られる。さらに、驀走する機関車が脱線するシーンでのフラッシュバック効果などにより、音楽的なリズムをもったサイレント映画といわれる。なお本作は、「黒の交響曲」と「白の交響曲」と呼ばれる２部から構成されている。", "1922-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE PARISIENNE", "殿方ご免遊ばせ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "B・バルドーのコケティッシュな魅力をフィーチャーしたコメディ映画。フランス首相の一人娘ブリジットは、官房長のミシェルを追いかけまわし首尾よく結婚するが、彼の女性関係の多さにうんざり。そこで新しい恋人を物色し……。", "1957-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母恋星", "母恋星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "三益愛子が得意とする“母もの”の中の１本。市橋家の一人息子、高行は小間使いのお澄との間に子供を作り、単身３年間の勉学旅行に向かう。その間にお澄は、高行の父・高秀によって運転手の坂上と無理に縁談を組まされ、嫌気がさし子供とともに姿を消す。そして10数年の時が経ち、お澄の息子・高夫は父と出会うが……。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若さま侍捕物帖 黒い椿", "若さま侍捕物帖 黒い椿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "“旗本退屈男”的な痛快時代劇に本格ミステリーをドッキングさせた、シリーズに新味を加えた１作。江戸を離れ、伊豆の大島へ遊びにきた若さまはそこで謎の殺人事件に遭遇し、島の美女に気をとられながらも事件を解決に導く。沢島作品の名物男・田中春男が意外な殺人鬼役を巧演。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЧЕЛОВЕК С БУЛЬВАРА КАИУЦИНОВ", "カプチーノ街から来た人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "開拓史時代のアメリカ西部を舞台に、女流監督A・スーリコワが撮り上げた、ソ連製のミュージカル・コメディ。映画発祥の地、カプチーノ（キャプシーヌ）通りから、フィルムと映写機をかついで西部にやってきた青年が、荒くれ男や踊り子たちに映画の楽しさを教えていくという奇想天外なお話。", "1987-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("吸血鬼ゴケミドロ", "吸血鬼ゴケミドロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "岩山に不時着した旅客機の乗客が、宇宙から来襲した吸血生物ゴケミドロによって一人、また一人と血を吸われていく……。怪奇派・佐藤肇が、ガッチリと演出した怪奇SF映画の佳作。特にゴケミドロが人間の血を吸うシーンのサディスティックな描写、一片の救いもない冷徹なラストなどは見事だ。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEST OF THE BEST", "ベスト・オブ・ザ・ベスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ソウルでアメリカ対韓国の空手の試合が行われることになる。その選考会を勝ち抜いた５人の選手は、コーチのクゾーのもとで３ヵ月の合宿生活を送ることになる。５歳の息子を持つアレックス、15年前に空手の大会で兄を失ったトミーをはじめ、個性的な５人の選手はクゾーの厳しい訓練に耐えながら、チームワークを築き上げていく。大会当日。韓国選手の中にはトミーの兄を強烈な打撃で死に追いやったダエ・ハンの名もあった。果たしてトミーは宿敵を倒せるのか、そしてアメリカ・チームは勝利できるのか……。迫力ある試合シーンとともに、スポーツマン・シップの大切さを描いた格闘アクション。続編に「帝王伝説」（1993）がある。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Manuale d'amore", "イタリア的、恋愛マニュアル", "", "", "http://image.eiga.k-img.com/images/movie/34061/315x210.jpg?1394", 118, "世代も立場も異なる4組のカップルが織りなす恋愛模様を、それぞれのエピソードを少しずつ絡ませながらオムニバス形式で描いたロマンティック・ラブストーリー。偶然出会った女性にひと目惚れした青年、倦怠期を迎えた中年夫婦、夫の浮気現場を目撃した女性、妻に逃げられてしまった男性。“恋愛の達人”であるはずのイタリア人が、多くの人々が経験する悩みに一喜一憂しながら、愛を手に入れるため奮闘する。", "2007-07-14", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミツコ感覚", "ミツコ感覚", "", "", "http://image.eiga.k-img.com/images/movie/57221/315x210.jpg?4745", 106, "ソフトバンクモバイルの「白戸家」シリーズなど人気CMを多数生み出し、舞台演出なども手がけるクリエイターの山内ケンジの劇場映画初監督作品。ごく普通の幸せを求めて生きる姉妹の日常を、シュールでユーモラスな独特の感性で描く。東京郊外に暮らすOLのエミは会社の上司・松原と不倫関係に陥っていた。エミの妹ミツコは写真学校に通っているが就職口が見つからず、街のスナックで働き始めた。そんなある日、怪しげな男・三浦が現れミツコにつきまとうようになる。ミツコは三浦を恐れるが、エミは三浦の嘘を信じて受け入れてしまい……。", "2011-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くれないの盃　前編", "くれないの盃　前編", "", "", "http://image.eiga.k-img.com/images/movie/54071/315x210.jpg?3685", 75, "お笑いコンビ品川庄司の庄司智春、スピードワゴンの井戸田潤、バットボーイズの佐田正樹が主演するヤクザ映画。前編と完結編の2部にわけて公開される。雷轟一家の組長・牧原総一郎は自分の体調不良から跡目を選ぶことにするが、若頭の門倉勇三、幹部の柿崎、実子の牧原翔一、3年の勤めを終えて一家へ帰ってきた景山晃の候補4人の誰を選ぶかで悩む。そんなある日、新興勢力の桜田総業が土地の利権問題にからみ、雷轟一家の縄張りに足を踏み入れてきて……。", "2008-10-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い犯行 夢の後始末", "赤い犯行 夢の後始末", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "映画製作現場で働く人間たちの情熱と欺瞞、そして欲望渦巻く内幕をスキャンダラスに描いた異色ドラマ。生活のためにアダルト・ビデオを作り続けている映画監督、細野は自作の企画を実現するため躍起になる毎日を送っていた。しかし、不安と焦燥に駆られたある日、彼は脚本家の自宅からシナリオを盗み出して……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ランデブー", "ランデブー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "本作が監督デビューとなった山本浩資が描く一晩だけのロード・ムービー。幼なじみ、ユウの葬式に出た夜、トオルは終電を乗り過ごし、夜の街を歩き出す。そこで彼は川に飛び込んだ女の子ナオを助ける。ナオはユウの友人だったが、葬式には出席してなかった。二人はユウの思い出を語りながら歩き、やり場のない想いを共有していく……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修禅寺物語", "修禅寺物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "北条氏によって修禅寺に追放され、絶望の淵にあった鎌倉幕府の二代将軍・源頼家と、夜叉王の娘・桂の激しい情艶の世界を描いた作品。桂役の淡島千景が堂々と大役をこなし、高橋貞二の頼家とともに重厚な演技を見せる。歌舞伎役者の坂東簔助も好演。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUNCRAZY", "ガンクレイジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "コッポラに師事した女流監督T・デイヴィスの処女作。学校ではイジメの対象にされ、家庭では義父に性的虐待を受ける女子高生アニータ。クラスの研究課題で刑務所に服役中のハワードと文通を始めた彼女は、次第に彼との結婚を夢見るようになる。ところがある晩、酒に酔った義父を拒もうとしてアニータは彼を殺してしまう。家を飛び出したアニータは、模範囚として出獄を許されたハワードと結婚し、生まれて初めて幸せをかみしめる。ところが、ハワードがかつてアニータを暴行した青年を殺してしまい、彼らは逃亡生活を余儀なくされる。。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Frisco Kid", "フリスコ★キッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "　19世紀半ば、ユダヤ教の伝道師アブラムがポーランドからアメリカへとやってきた。彼はフィラデルフィアからサンフランシスコへ向かうが、途中で追いはぎに遭い、身ぐるみはがされてしまう。そんなアブラムが出会ったのは銀行強盗のトミー。見るに見かねたトミーはアブラムの旅に付き合うことに。しかし予期せぬトラブルが続いて……。デコボコ・コンビの珍道中を描いたウェスタン。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVENT　HORIZON", "イベント・ホライゾン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "宇宙船という閉ざされた限定空間を舞台にしたSFスリラー。難破した船の内部で起こる恐るべき超常現象がスリリングに描かれる。2047年。７年間行方不明だった宇宙船イベント・ホライゾンからSOSが届き、救助隊は現地へ飛ぶ。だが、無人の船中では不気味な怪奇現象が続発。やがて救助隊員たちは次々と謎の死を遂げる。宇宙の果ての究極の恐怖を浮き彫りにした哲学的な設定が魅力。ダークな映像に巨大な金属音がかぶさり、衝撃度を高める。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひとりぼっちの二人だが", "ひとりぼっちの二人だが", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "好色な社長のもとを逃れた少女・ユキは、チンピラの三郎やボクサーの英二に助けられる。ストリップ小屋で働く九太も加勢。３人はユキを守るために団結する。当時の日活を代表するスターを集めた青春もので、三枚目役の坂本九がとぼけた役どころで印象深い。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CHARME DISCRET DE LA BOURGEOSIE", "ブルジョワジーの秘かな愉しみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ブルジョワの紳士淑女が集う晩餐。しかしレストランでの奥では通夜が行われており、宴は仕切り直しとなった。その後も集まるたびに偶然の闖入に邪魔され、一同はどうしても食事にありつけない。これは悪夢なのか、それとも現実か……。「銀河」と「自由の幻想」とあわせて3部作とされ、ブニュエルのめざした大胆で自由な映画づくりの豪華さを堪能できる1本。", "1974-05-31", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Reset", "Reset", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ある夜、自殺の名所であるビルの屋上に集まってきた５人の男女。様々な確執の果て、彼らは結局ビルから飛び降りるのか否か？　全く脚本なしで、演じる俳優には役柄のシチュエーションだけが渡され、スタッフもキャストも結末が予想つかないまま、78分ノンストップで撮影して完成させた実験的要素の強いヒューマン・ドラマ。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五日市物語", "五日市物語", "", "", "http://image.eiga.k-img.com/images/movie/56681/315x210.jpg?1334", 119, "1995年に秋川市と五日市町が合併して発足した東京・あきる野市の市制15周年を記念して製作されたヒューマンドラマ。TV番組向けの情報収集を専門に行う「あつめ屋」勤務の友里が、「平成の大合併」の先駆的役割を果たしたあきる野市の歴史を探る中で、あきる野市の穏やかな土地柄と、旧五日市町で暮らしてきたトシ子おばあちゃんとの出会いを通じ、自分を見つめ直していく姿を描く。主演は遠藤久美子。草村礼子、布施博らが脇を固める。", "2011-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNHOOK THE STARS", "ミルドレッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "故ジョン・カサヴェテスの息子ニックの初監督作品。母G・ローランズを主演に据え、中年女性の“旅立ちのとき”を優しいタッチで綴っている。未亡人ミルドレッドは娘に家出され、一人ぼっちに。向かいの主婦モニカから彼女の息子JJを預かったミルドレッドは、“母”としての自信を回復する。本当の息子のようにJJとの絆を深めていく彼女だが……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOLLS", "ドールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "「ZOMBIO 死霊のしたたり」でデビューし、一躍スプラッタ・ムービーのニューウェーブとして脚光を浴びたS・ゴードンの日本公開第２作。古い屋敷に住む老夫婦の手作り人形が、ある嵐の夜に心ない大人たちを襲いはじめ、惨殺していくという“残酷メルヘン”。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Resident Evil: Apocalypse", "バイオハザードII アポカリプス", "", "", "http://image.eiga.k-img.com/images/movie/1291/315x210.jpg?1548", 91, "ヒット作「バイオハザード」の直後からストーリーが続く続編。前作の監督ポール・W・S・アンダーソンは脚本と製作に回り、監督は「デアデビル」などの第2班監督を務めてきたアレクサンダー・ウィット。主人公は前作と同じミラ・ジョボビッチ扮するアリスだが、今回は原作ゲームに登場するキャラ2人も登場。ジル・バレンタインを英国女優シエンナ・ギロリーが、カルロス・オリべイラをイスラエル出身のオデッド・フェールが演じる。", "2004-09-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("手のひらの幸せ", "手のひらの幸せ", "", "", "http://image.eiga.k-img.com/images/movie/54911/315x210.jpg?2591", 0, "歌手・作詞家として活躍する布施明が、身寄りを亡くした兄弟の絆を描いた童話の映画化。監督は本作が監督初挑戦となる撮影監督の加藤雄大。主演は「GSワンダーランド」の浅利陽介、「ふぞろいな秘密」の河井龍之介。両親のいない健一と龍二は祖父に育てられるが、その祖父が他界し、児童養護施設に預けられることになる。父の帰りを信じて施設に留まる兄・健一と、竹林家に養子として迎えられる龍二は、離ればなれに暮らすことになるが……。", "2010-01-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLETCH", "フレッチ 殺人方程式", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ニューズ・トリビューン紙の事件記者フレッチは、街の麻薬ルートの摘発に乗り出す。ところが浜辺で会った謎の男から自分を殺してほしいと頼まれて……。原作は1974年アメリカ探偵作家クラブ新人賞を受けた大ベストセラー。チェイスの軽快な演技が魅力のシャレたサスペンス。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Dangerous Man", "沈黙の鉄拳", "", "", "http://image.eiga.k-img.com/images/movie/55321/315x210.jpg?3601", 94, "スティーブン・セガール主演の“沈黙シリーズ”。「沈黙の逆襲」「クリスマス黙示録」のキオニ・ワックスマンがメガホンをとる。えん罪で服役していた元特殊部隊のシェーンは、6年にも及ぶ刑務所生活の後、晴れて自由の身となる。しかし、服役中に彼の名誉は失われ、最愛の家族は自分のもとを離れていく。そんなある日、ヤケを起こしていたシェーンは強盗に襲われるが、これまで身につけた武術でいとも簡単に犯人を撃退する。しかし警察を信用できないシェーンは、現場から逃走してしまい……。", "2010-04-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LOST WORLD", "ロスト・ワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "アマゾン川上流に棲息するといわれる前世紀の恐竜を探すべく、探検隊が組織され、様々な困難の末にプロントザウルスを捕獲し、ロンドンへと連れ帰ってくるが……。イギリスの文豪コナン・ドイルのSF小説を映画化したサイレント映画の名作。特撮は後に映画史上に残る「キング・コング」を担当することになるウィリス・オブライエン。", "1925-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IS IT HEAVEN YET？", "イズ・イット・ヘヴン・イェット？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "屈折光学を応用してワインを分析するという怪しげな“発明品”を持って、ワルド・ソニークロフトは大もうけしようとサンフランシスコにやって来る。だがドジで世間知らずで女に惚れやすい彼は、麻薬の密売に巻き込まれたり、高名な教授に間違えられたり、追いはぎにあったり、いつも女に振られたり、はては肝心の“発明品”も壊されたりと散々な目に遭う。だけど彼はくじけない。いつか“HEAVEN”に着くことを夢みて、今日も生きていくのだ……。C・カルダナがほとんど一人で作り上げた作品だが独特の才能で愛すべき映画に仕上げている。超低予算で、シンプルな映像ではあるが、その中にはストイックで、リリシズムあふれる笑いとペーソスが全編に流れている。どぎついドタバタやナンセンス、悪意のあるブラック・ユーモアは一切排除し、洗練されたギャグとセンスの良い演技が、見る人をほのぼのと温かくさせる不思議な魅力を持った作品だ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROUGH　MAGIC", "ラフ・マジック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "愛と犯罪が交錯する男女の逃避行を描いたサスペンス。州知事による殺人事件を目撃した女性マイラは危険を察知してL.A.を去ることに。旅の途上で謎めいた男ロスと出会って恋に落ち、ともにメキシコを目指すが、その先には思わぬ罠が……。「ジャッキー・ブラウン」のB・フォンダと「L.A.コンフィデンシャル」で脚光を浴びたR・クロウがミステリアスな逃避行を体現。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shine A Light", "ザ・ローリング・ストーンズ　シャイン・ア・ライト", "", "", "http://image.eiga.k-img.com/images/movie/53401/315x210.jpg?3811", 122, "「ディパーテッド」「アビエイター」のマーティン・スコセッシによるモンスター・バンド“ザ・ローリング・ストーンズ”のライブ・ドキュメンタリー。2006年秋にニューヨークのビーコン・シアターで行われたライブの模様と、バンドのフロントマンのミック・ジャガーとスコセッシ監督のせめぎ合いが臨場感あふれる映像で収録されている。ライブには、ホワイト・ストライプスのジャック・ホワイトやクリスティーナ・アギレラも飛び入りゲストとして登場している。", "2008-12-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／中毒", "純愛中毒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "兄と弟、そして兄の妻という３人の生活が、事故をきっかけに崩壊していく様を描くラブ・サスペンス。事故に遭遇した兄弟の魂が入れ替わってしまったことから、狂おしいまでの愛の物語が展開される。兄の魂が宿ってしまう弟を演じる「誰にでも秘密がある」などのイ・ビョンホンが見せる憂いの表情が切なさを誘う。家具アーティストのホジンは、愛する妻とレーサーの弟テジンと一緒に暮らしていた。ある日、ホジンとテジンが同時刻に事故に遭い昏睡状態になる。一年後、奇跡的にテジンの意識が戻ったのだが、自分はホジンだと主張して……。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母の地図", "母の地図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "東宝移籍後、不調がささやかれていた島津保次郎が、久々に持てる力を発揮したと評価された作品。没落した旧家・岸家の家族それぞれの人生模様を描いていく。杉村春子、中村伸郎、森雅之ら文学座のメンバーが顔を並べた、東宝映画としては珍しいキャスティングが見もの。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAY BACK", "侵入者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "強盗罪で投獄されたオスカーは、刑務所で知り合ったマックから、出所後元看守トムの殺害を頼まれる。莫大な報酬を条件に殺害を引き受けたオスカーは、海辺でカフェを営むトムの元へと向かうが、トムは盲目になっていた。オスカーは偽名を使ってカフェに居座り殺しのチャンスを待つが、次第に彼の妻ローズに惹かれていくのだった……。男と女の愛憎に大金をめぐる人間の欲を絡めて描いたバイオレンス・スリラー。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE OSCAR", "オスカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "アカデミー賞の黄金像“オスカー”をめぐる様々な裏話を描いたユニークな作品。アカデミー主演男優賞の本命と見られているフランキーは、ここまで来るのに辛酸をなめつくしてきた。彼はさらにオスカーを決定的なものにするため、新聞記者を買収して、他の候補があたかも彼を陥れようとしているような暴露記事を書かせる。しかし、逆に彼は昔の秘密を暴かれることになるのだった。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWING", "僕のスウィング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "10歳の少年のひと夏の恋を綴るファンタスティックなドラマ。「モンド」のトニー・ガトリフ監督が“マヌーシュ・スウィング”という軽快なギター音楽をバックに、自然と戯れる子供たちの姿を活写する。夏休みにストラスブールの祖母の家に預けられた少年マックス。彼はロマのギタリスト、ミラルドからギターを教わるうちに、スウィングという自分より少し大人の野性的な美少女に淡い恋心を抱く。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOUBLE O KID", "バーチャル・ウェポン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ロレンダ、タイラー、キャッシュポットの３人は、通信衛星を使い、要人の乗る飛行機のコンピュータにウイルスを侵入させ、墜落をたくらむ犯罪請負人だった。今回も1200万ドルで科学者たちの乗る飛行機の墜落依頼をゲームのようにこなしていた。ところが、そこへ計算外のジャマが入った。彼らの動きを察知したCIAが一人の新米エージェントを派遣したのだ……。CGを駆使した映像と、ハイテンポなストーリー展開のアクション映画。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青い山脈", "青い山脈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "石坂洋次郎の同名小説を当時の日活青春映画のスタッフ・キャストで撮り上げた佳作。脚本を今井正監督版「青い山脈」と同じ井出俊郎が再び担当しているのも面白い。吉永、浜田、高橋らのフレッシュスターたちのさわやかな魅力が満載の好編。監督はリメイクものの帝王ともいえる西河克己。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("特警新人類 ２", "ジェネックス・コップ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "香港の若手スターが共演、刑事３人組の活躍を描いたアクション第２作。アメリカの科学技術研究所で、最新鋭の戦略メカロボットRS1が開発された。RS1が出品される国際ハイテク軍備展の警備を任された新人警官３人。RS1を狙う見えない敵に、彼らは勇敢かつ無鉄砲に立ち向かっていく。高層ビルや市街地での銃撃戦、カーチェイスやボートチェイスなど、スピーディーなアクション・シーンは前作同様見ごたえ充分。３人組の一人に、ニコラス・ツェーに代わり新星エディソン・チャンが加入。", "2000-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Key Largo", "キー・ラーゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "　第2次大戦後のフロリダ。退役将校フランクは部下の死を知らせるため、その父親と未亡人が経営するホテルを訪ねる。ところがホテルは凶悪なギャング、ロッコ一味によって占拠され、フランクたちは人質となってしまった。ハリケーンも接近し、外部に助けを求めることもできない。ギャングは次第に苛立ち始め、一触即発の状況に……。監督ヒューストンと主演ボギーがコンビを組んだハードボイルド・ロマン。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CYCLE サイクル", "CYCLE サイクル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "鬼才・山本政志監督が、和製ゾンビホラー「東京・オブ・ザ・デッド」に続いて放つ新感覚サイコホラー。コンビニ店員の麻愛は、最近できた彼氏・藤岡と幸せな日々を送っていたが、その一方で毎日同じ時刻に聞こえてくる自転車の音に悩まされていた。そんなある日、麻愛は自転車に乗った怪しい女を目撃する。女の正体を突きとめるべく動きだす麻愛だったが、女について調べるうちに次第に精神をむしばまれていき……。", "2006-04-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ズッコケ三人組 怪盗X物語", "ズッコケ三人組 怪盗X物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "小学生の間で人気を博した同名児童書を「いちご同盟」の鹿島勤が映画化。なぞの怪盗や洞窟の宝捜しに挑む３人の少年たちのひと夏の冒険譚を、瀬戸内の美しい景色を背景にいきいきと描く。わんぱくなハチベエと成績優秀なハカセ、食いしん坊のモーちゃんは、小学６年生の仲良しトリオ。夏休みのある日、町で怪盗Xが出現したとのウワサが広まり、３人は調査に乗り出す。ところが、彼らの冒険は教育実習でやって来た美人教師も巻き込んで、思わぬ事態へと発展し……。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAYNE’S　WORLD", "ウェインズ・ワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "自宅にスタジオを作りケーブルTVでトークショーを放送するロック小僧のウェインとガースの二人組。若者に大人気のこの番組を乗っ取ろうと狡猾なプロデューサーが現れるが、ウェインたちも負けずに珍作戦を展開する。『サタデー・ナイト・ライブ』出身の人気コンビ、M・マイヤーズ＆D・カーヴィによる全米大ヒットの痛快コメディ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バルトの楽園（がくえん）", "バルトの楽園（がくえん）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "第1次世界大戦中、徳島県鳴門市の板東俘虜収容所で起きた実話をもとに描いた人間ドラマ。収容所の所長・松江豊寿の人道的な計らいによってドイツ人捕虜と収容所員、地元住民が交流を深めていき、ドイツ人たちが感謝の意をこめて、ベートーベンの「交響曲第九番　歓喜の歌」を日本で初めて演奏するまでを描きだす。松江所長役で松平健が主演。「ベルリン・天使の詩」「ヒトラー　最期の12日間」の名優ブルーノ・ガンツがドイツ人将校で共演する。", "2006-06-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("「わたし」の人生（みち）　我が命のタンゴ", "「わたし」の人生（みち）　我が命のタンゴ", "", "", "http://image.eiga.k-img.com/images/movie/58021/315x210.jpg?3074", 0, "認知症を抱える父とその娘がアルゼンチンタンゴを通じて家族の絆を再確認していく姿を、秋吉久美子と橋爪功の主演で描くドラマ。監督は精神科医でもあり、デビュー作「受験のシンデレラ」（2007）がモナコ国際映画祭の作品賞を受賞した和田秀樹。主婦として子育てを終え、長年の夢だった大学教授への道を歩みはじめようとしていた百合子だったが、その矢先に父の修次郎が認知症を患っていることがわかる。不安や介護に追われて家族はバラバラになっていくが、同じ状況の家族が集う認知症家族の会でアルゼンチンタンゴを習い始めた修次郎に変化が訪れ、そんな父の姿を見た百合子も再び夢に向かう決心をする。", "2012-08-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALWAYS 三丁目の夕日", "ALWAYS 三丁目の夕日", "", "", "http://ecx.images-amazon.com/images/I/5133NXJJ6NL._SL210_.jpg", 133, "西岸良平の人気漫画を基にした人情ドラマ。昭和30年代の東京を舞台に、下町の人間模様を笑いと涙で描き出す。商店街や建物のノスタルジックなヴィジュアルは郷愁を誘うに違いない。子供の屈託のない笑顔や、大人のひたむきさなど、現代社会で忘れられた大切なものを思い出させてくれる好編だ。昭和33年、東京の下町に身寄りのない少年がやってきた。売れない作家の茶川は冒険小説の好きなこの子を引き取るハメになり……。第29回日本アカデミー賞では最優秀作品賞をはじめ堂々13部門に輝き、2005年を代表する邦画の“顔”となった。", "2005-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Served the King of England", "英国王給仕人に乾杯！", "", "", "http://image.eiga.k-img.com/images/movie/54111/315x210.jpg?2356", 120, "「スイート・スイート・ビレッジ」「厳重に監視された列車」などで知られるチェコの巨匠イジー・メンツェル監督による長編第15作。ヨーロッパの小国チェコが辿った激動の20世紀現代史が小さな給仕人ヤンの人生を通して、軽やかなタッチで描かれる。原作はチェコの国民的作家ボフミル・フラバル。2人1役の主人公ヤンにはイバン・バルネフとオルジフ・カイゼル。ゲストとして「メフィスト」「太陽の雫」のイシュトバン・サボー監督も出演。", "2008-12-20", "43,72", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PASSION BE´ATRICE", "パッション・ベアトリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "スタンダールらロマン派の創作意欲をかきたてた、イタリア・ルネサンス時代の実在の人物で“父殺しの天使”といわれたベアトリーチェ・チェンチをモデルにした作品。タヴェルニエはこの主人公を中世フランスの女としてよみがえらせ、父と娘の形を変えた愛をテーマに、愛と憎しみに彩られた壮絶な史劇として再現した。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ランディーズ", "ランディーズ", "", "", "http://image.eiga.k-img.com/images/movie/54921/315x210.jpg?2139", 0, "人気漫画家・永田晃一のデビュー作を、「ナイト★キング」の藤原健一が映画化。主演は「ROOKIES／卒業」の川村陽介。喧嘩だけは負け知らずの桜井とケンジは、今は亡き仲間のお墓を建てるために便利屋助っ人稼業「ランディーズ」を営み、池袋の街で資金集めをしていた。そんなある日、非道なチームとして恐れられていた「ジャイアンズ」のリーダー・武が街に帰ってくる。", "2009-11-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Righteous Kill", "ボーダー", "", "", "http://image.eiga.k-img.com/images/movie/53911/315x210.jpg?4445", 0, "「ヒート」（95）のロバート・デ・ニーロとアル・パチーノが12年ぶりに再共演を果たした刑事ドラマ。監督は「88ミニッツ」のジョン・アブネット、脚本は「インサイド・マン」のラッセル・ジェウィルスが担当。ニューヨーク市警で20年以上コンビを組むタークとルースターは固い絆で結ばれていた。ある日、彼らが捕えていない犯罪者を標的にした連続殺人事件が発生。その全ての証拠がタークの犯行を示していた……。", "2010-04-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tinker Bell and the Great Fairy Rescue", "ティンカー・ベルと妖精の家", "", "", "http://ecx.images-amazon.com/images/I/51t8rj12hHL._SL210_.jpg", 77, "名作ディズニー・アニメ「ピーター・パン」に登場する妖精ティンカー・ベルが、ピーター・パンに出会う前の物語を描く4部作の第3作。夏を迎える準備のため、人間たちが暮らすメインランドにやって来た妖精たち。そこでティンクは人間の少女リジーが作った“妖精の家”を発見するが、中に入ったところをリジーに捕まってしまう。リジーが忙しい父親に寂しい思いを抱いていることを知ったティンクは、リジーと友情を育んでいくが……。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("インシテミル 7日間のデス・ゲーム", "インシテミル 7日間のデス・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/55381/315x210.jpg?1529", 0, "「リング」シリーズを手がける中田秀夫監督が、米澤穂信のベストセラー小説を映画化した心理サスペンス。藤原竜也、綾瀬はるか、石原さとみ、北大路欣也ら豪華キャストが結集する。「暗鬼館」という館に、「時給11万2000円で実働7日間」という求人広告につられた男女10人が集まってくる。仕事内容は24時間監視されるだけというものだったが、2日目に銃殺による死者が生まれ、参加者たちは互いに疑心を抱くようになる。", "2010-10-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETREAT，HELL！", "地獄への退却", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "朝鮮戦争を背景にした戦争アクション映画。予備役ながら朝鮮戦争の開始とともに召集されたポール・ハンセンたち。厳しい訓練のあと、彼らは仁川へ向け進軍の途についた。壮絶な戦闘を経て一人前になった彼らは、なおも敵陣めがけて進んで行くが、中共軍の人海戦術に遭い、退却を余儀なくされる……。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハチ公物語", "ハチ公物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "有名な忠犬ハチ公の物語。秋田の片田舎で生まれた子犬が、東京の大学教授の家にもらわれてハチという名をもらう。成長したハチは、いつしか教授を渋谷駅まで送り迎えするのが日課となるが、教授は病気で帰らぬ人となる。しかしハチは雨の日も風の日も渋谷駅で教授の帰りを待つ……。渋谷駅前の名物になっている銅像“ハチ公”の物語が、てらうことなく丁寧に描かれ、ストレートな感動を与えた。特に秋田の片田舎で子犬が生まれる場面は、山本嘉次郎監督の動物映画の名作「馬」（1941）を思わせる名シーン。主人を求めていつもの道を駆けるハチ公を、カメラは距離を置き、犬の目の高さで捉えている。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画　プリキュアオールスターズ NewStage みらいのともだち", "映画　プリキュアオールスターズ NewStage みらいのともだち", "", "", "http://image.eiga.k-img.com/images/movie/57551/315x210.jpg?3804", 70, "人気アニメーション「プリキュア」の劇場版通算12作目で、歴代のプリキュアが集結する「プリキュアオールスターズ」シリーズの第4弾。2012年放送開始のシリーズ最新作「スマイルプリキュア！」の5人を含めた28人のプリキュアが集う。横浜みなとみらいではプリキュアたちが謎の怪物フュージョンを倒したという話題でもちきりに。そんな中、転校してきたばかりでひとりぼっちの少女あゆみは、謎の生き物と出会い、フーちゃんと名づけて仲良しになる。しかし、フーちゃんの正体はプリキュアたちが倒したフュージョンの欠片で、闇の力を持つフーちゃんはあゆみの嫌いなものを次々と飲み込み街を大混乱に陥れてしまう。", "2012-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("打ち上げ花火、下から見るか？　横から見るか？", "打ち上げ花火、下から見るか？　横から見るか？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "小学校最後の夏休み、プールの掃除当番ため登校していた典道、祐介、なずな。祐介は、典道に水泳の競争で勝った方がなずなに告白するという話をもちかける。「Love Letter」「花とアリス」の岩井俊二が当初TVドラマ用に制作した本作は、TVドラマとして初の日本映画監督協会新人賞を受賞する快挙を成し遂げ、のちに同監督作品「Undo」とともに劇場公開された。主演は奥菜恵、山崎裕太。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富士に立つ若武者", "富士に立つ若武者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "平家に敗れ、捕われの身となって処刑寸前までいった源頼朝が、源氏再興を信じ、やがて決起していく姿を描いた時代劇ロマン。平清盛の母の命ごいで救われ、伊豆の配所に流された頼朝。10年後、彼は静かに読経三昧の日々を送っていたが、この頃源氏再興を図る草の実党が生まれ情勢が次第に動き始める。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("姉の問題", "姉の問題", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "久しぶりの外食に出かけようとしていた郵便局長夫妻。そこに局長の姉プンニャが、嫁に邪魔者扱いをされていると、荷物を持ってやって来た。ところがこの問題、原因はプンニャにあったのだった……。どこの国にもあるといわれる嫁姑戦争。そこに繰り広げられる人間模様を描いた作品。", "1982-01-01", "48", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRUE STORIES", "デヴィッド・バーンのトゥルー・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "テキサスの架空の町バージル。カウボーイ・スタイルの正体不明の男が、町で起こる事件や愛すべき人々のことをスケッチ風に語り始める。「ストップ・メイキング・センス」で人気を博したトーキング・ヘッズのリーダー、バーン監督・主演の不可思議な魅力を放つ作品。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生きてるものはいないのか", "生きてるものはいないのか", "", "", "http://image.eiga.k-img.com/images/movie/57071/315x210.jpg?2633", 113, "「狂い咲きサンダーロード」「爆裂都市 Burst City」の石井聰亙監督が石井岳龍に名を改め、「五条霊戦記 GOJOE」以来およそ10年ぶりに手がけた長編監督作で、第52回岸田國士戯曲賞を受賞した前田司郎の同名戯曲を映画化。病院に併設された大学キャンパスを舞台に、いつもと変わらない日常を送っていた三角関係に悩む学生と喫茶店員、再会を果たした兄妹、アイドル大学生ら18人の登場人物が、ある女子大生が倒れたことをきっかけに次々と原因不明の“最期”に伝染していく様子を描く不条理劇。主演は「ヒミズ」でベネチア国際映画祭新人賞を受賞した染谷将太。", "2012-02-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEQUILA SUNRISE", "テキーラ・サンライズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "麻薬組織の大物として知る人ぞ知るマック。彼は悪の道から足を洗おうと決意するが、周囲がなかなかそれを許さない。そんな彼に救いの手を差しのべたのが、高校時代からの親友で今は麻薬捜査官のニックだった。だが二人は同じ一人の美女を愛し始めていて……。監督・脚本はポランスキー作品「チャイナタウン」でアカデミー・オリジナル脚本賞受賞のR・タウン。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PASSION OF DARKLY NOON", "聖なる狂気", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "突然現れた女にほんろうされる男の姿を描いたサイコ・スリラー。厳格な宗教により性的に抑圧されて育った青年ダークリーは、両親がなくなったショックから森の中をさまよい、恋人クレイと隠れるように暮らすキャリーに助けられる。ダークリーは彼女と出会い、初めて性的なものを感じるようになる。そしてクレイとキャリーが愛し合う姿を盗み見してしまい……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NEIGHBOR", "ザ・ネイバー 恐怖の館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "古びた洋館に越してきた若い夫婦ジョンとメアリー。隣りに住むのは、町でも評判の産婦人科医マイロン・ハッチだった。ハッチは若い二人にも親切で幸福な日々が続くかに思われたが、ハッチの微笑の下には恐るべき過去と狂気が隠されていた。そしてメアリーの妊娠を知ったハッチは……。アカデミー賞俳優R・スタイガーの迫力ある演技が見もののサイコ・サスペンス。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEUX OU TROIS CHOSES QUE JE SAIS D’ELLE", "彼女について　私が知っている二、三の事柄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "『ル・ヌーヴェル・オブセルバトワール』誌に報じられた実話をもとに、ゴダールがドキュメンタリー風に取り上げた人間ドラマ。1966年、夏。団地に住んでいる平凡な人妻ジュリエットは売春をしている。昼間、娘を託児所（そこが売春宿）へ預けてショッピングへ。そして欲しい物があると、行きつけのカフェで男を探す。F・トリュフォーが製作を担当している。", "1966-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("祇園の姉妹", "祇園の姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "日本を代表する映画作家・溝口健二監督の代表作である。京都の芸者の姉妹を主人公として、人情に厚い姉と打算的で気の強い妹という二人の対照的な生き方を描きながら、どちらも男にもてあそばれてみじめに捨てられていくという女の現実を、きびしい目で捉えた作品。芸者姉妹を演じた梅村蓉子と山田五十鈴も見事にその対照を演じ分け、非情な世界に生きる女性の哀感や怒りを充分に表現しつくした。全体的なテンポは男たちとのやりとりなどユーモラスな面もあり軽い感じも与えるが、克明な京都の風物描写もあいまってずっしりと観ごたえのある一編。依田義賢の脚本も冴え、まさに溝口芸術の一級品である。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STONE COLD", "ストーン・コールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "元NFLシアトル・シーホークスのラインバッカー、ボズワースが肉体派ヒーローとしてスクリーン・デビューを果たしたアクション快作。手腕を買われてFBIのおとり捜査官にスカウトされたハミダシ刑事が、潜入先の悪名高きバイク集団で大暴れする。暴走バイク・チェイス、重火器、爆薬、攻撃ヘリと、何でもござれのクライマックスはサービス満点。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rabbit Hole", "ラビット・ホール", "", "", "http://image.eiga.k-img.com/images/movie/56121/315x210.jpg?2295", 92, "ニコール・キッドマンが自ら製作・主演し、第83回アカデミー主演女優賞にノミネートされたドラマ。ピュリッツァー賞、トニー賞を受賞したデビッド・リンゼイ＝アベアーの原作戯曲を「ヘドウィグ・アンド・アングリーインチ」のジョン・キャメロン・ミッチェル監督のメガホンで映画化した。ニューヨーク郊外に暮らすベッカとハウイーの夫婦は、8カ月前に交通事故で息子を失い、絶望の淵にいた。ベッカは現実から目をそらし、ハウイーは思い出にふけることで夫婦の関係が次第にほころびはじめていたある日、ベッカは息子の命を奪った車を運転していた少年を街で見かける。ベッカは偶然を装って少年の後を追うが……。", "2011-11-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明治血風録・鷹と狼", "明治血風録・鷹と狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "松尾昭典監督・高橋英樹主演による異色任侠アクション。白虎隊の生き残りですご腕の侠客だが目が不自由な主人公が、鉄道工事の利権をめぐって対立する二つのヤクザ組織の抗争に巻き込まれる。“座頭市”のバリエーション的な作品。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("National Treasure: Book of Secrets", "ナショナル・トレジャー　リンカーン暗殺者の日記", "", "", "http://image.eiga.k-img.com/images/movie/53061/315x210.jpg?4316", 124, "ジェリー・ブラッカイマー製作、ニコラス・ケイジ主演で大ヒットを記録したアクション・アドベンチャー第2弾。歴史学者で冒険家のベン・ゲイツのもとに、ウィルキンソンと名乗る男から、第16代大統領リンカーンを暗殺した犯人の日記の一部が届けられるが、そこにはベンの祖先トーマス・ゲイツの名が記されていた。“リンカーン暗殺者”の汚名を着せられた祖先の無実を証明するため、ベンは日記に隠された謎を解き明かそうと決意する。", "2007-12-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルパン三世 1st. TVシリーズ", "ルパン三世 1st. TVシリーズ", "", "", "http://image.eiga.k-img.com/images/movie/54391/315x210.jpg?3339", 105, "1971年に放送されたTVアニメ「ルパン三世」から、高畑勲、宮崎駿が“Aプロダクション演出グループ”として演出した第11話「7番目の橋が落ちるとき」、第4話「エメラルドの秘密」、第19話「どっちが勝つか三代目！」の3話を上映するほか、作画監督の大塚康生、宮崎駿監督のインタビューを交えた「ドキュメント『ルパン三世』とその時代」（約30分）も同時上映する。", "2009-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESPERADO", "デスペラード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "A・バンデラスが、クールでタフなガンマンを熱演するバイオレンス・アクション。ギター・ケースいっぱいに高性能武器を詰め込んだ黒装束の男マリアッチ。伝説のヒーローと噂される彼が、メキシコ国境の町サンタ・セシリアで壮絶な闘いを繰り広げる……。監督は弱冠23歳でハリウッドの目に留まった俊才R・ロドリゲス。Q・タランティーノのゲスト出演も話題の一つ。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARNE", "カルネ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "フランスで若者層の支持を受けた衝撃的な一編。パリ郊外で馬の肉を売る男と言葉を発さない13歳になるその娘の葛藤を、近親相姦的なムードを漂わせて描出。血の色を生々しく捉えた映像とブラック・ユーモアに終末感が漂い、わずか40分の作品ながら鮮烈な印象を残す。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIND GAME", "MIND GAME", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「Shall we ダンス？」「ラヂオの時間」などでコミカルな演技を披露している俳優・田口浩正の初監督作。柏原崇を主演に迎え、多重人格者の内面に迫ったサイコ・サスペンス。大学病院のセラピスト・片山は、心理学研究員の恋人、三鈴の依頼で多重人格者の青年、田所のカウンセラーを引き受ける。片山は田所の過去を探って原因を突き止めようとするが、逆に彼にほんろうされてしまう。そんなある日、田所の内に潜む邪悪な人格が現れ、三鈴を破滅へと追い込んでいく。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Razor's Edge", "剃刀の刃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "第二次世界大戦後、人生の目的を見失った男を主人公に、二人の娘との恋のさやあてを絡めながら描いたドラマ。S・モームのベストセラーを映画化したもので、T・パワーが主人公を演じ高い評価を得た。E・グールディングらしい流れるようなカメラ・ワークの演出も見もの。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Arsenic and Old Lace", "毒薬と老嬢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ブロードウェイでロングランヒットした戯曲を名匠フランク・キャプラ監督が映画化した作品で、キャプラ作品としては珍しいブラック・コメディ。ブルックリンの屋敷に住む老姉妹エビイとマーサ。先の短い孤独な老人を安らかに死なせてやりたいという考えにとらわれる2人は、老人たちを屋敷に連れ込み、毒入りのワインを飲ませて殺していた。ある日彼女たちを訪ねてやって来た甥モーティマーが、地下に隠されていた死体を発見する。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使も夢を見る", "天使も夢を見る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "NHKのラジオ小説を原作とした川島雄三監督のハイカラ・コメディ。ノンプロ野球部のキャプテンである淀川は、社長のわがまま娘・礼子に恋人役を頼まれて彼女のお見合いをブチ壊しにする。ことの真相を知った社長は大いに激怒し、野球部を廃止しようとするが……。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子猫物語", "子猫物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ムツゴロウこと畑正憲が市川崑の協力を得て監督した、子猫・チャトランの冒険物語。夏休みに公開されるや大ヒットとなり、日本中を子猫ブームに巻き込んだ。親からはぐれたチャトランが子犬のプー助の力を借りて旅をしながら成長していく姿が北海道の美しい自然を背景に描かれる。名演技ともいえそうな子猫の愛らしい動きを見ているだけで飽きない。坂本龍一の音楽、谷川俊太郎の詩、小泉今日子の朗読、露木茂のナレーションといった豪華なスタッフが映画をより魅力的なものにしている。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL GRAN CALAVERA", "のんき大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「銀河」や「ブルジョワジーの秘かな愉しみ」などで鬼才の名をほしいままにしたブニュエル監督が、メキシコ時代に撮った娯楽映画。妻を亡くした悲しみから、酒びたりの自堕落な生活を送っている百万長者ラミロ。家族たちもまた、彼が無頓着なのをいいことに浪費を重ねるばかり。このままでは財産を失ってしまうと心配した弟のグレゴリオが、大芝居を打ってなんとかラミロを立ち直らせようとするが……。ご都合主義的なストーリー展開とハッピーエンドに終わる結末。ハリウッド映画そのままの楽天的なコメディとなっている。常に登場する動物のイメージや“扉”の描き方など、ブニュエル映画になじみ深い記号はここでもあちこちに見受けられるが、ハリウッド映画のパロディーがどうのこうのという必要などなく、素直に楽しめる一編と言っていいだろう。そしてこの「のんき大将」の興行的成功によって、ブニュエルは翌年の問題作「忘れられた人々」を撮ることができたのである。", "1949-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lie with Me", "寂しい時は抱きしめて", "", "", "http://image.eiga.k-img.com/images/movie/33981/315x210.jpg?1329", 93, "セックスはできても心まで満たされない女性が本物の愛を見出すまでをリアルに描いた、カナダ発のラブ・ストーリー。女流官能小説家タマラ・フェイス・バーガーによる原作を、夫であるクレメント・バーゴ監督が映画化。本当の恋愛ができずセックスに依存してしまうヒロインを、人気テレビドラマ「ミュータントX」のローレン・リー・スミスが体当たりで演じる。相手役はテレビドラマ「The OC」のエリック・バルフォー。", "2007-05-19", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIEBESTRAUM", "オブセッション 愛欲の幻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「氷の微笑」の大ヒットをきっかけに数多く作られたエロティック・サスペンスの一編。ニックは、病気で倒れた母を見舞うため、ある町を訪れる。そこで友人ポールに偶然出会い、彼が古いデパートの取り壊し作業にかかわっていることを知る。そのデパートは殺人事件が原因で40年もの間、閉鎖されていた。そしてニックは、何かにつき動かされるように、友人の妻ジェーンと深く愛し合うようになっていく。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビー・バップ・ハイスクール 高校与太郎狂騒曲", "ビー・バップ・ハイスクール 高校与太郎狂騒曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ある日、トオルはマジメ学生に変身して、遊園地でダブルデート。モテないと悩んでいたヒロシも、美人女子大生・まゆみと付き合うことに。恋に夢中のヒロシは、トラックにはねられ、入院する……。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE TREASURE", "トレジャーinメキシコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "メキシコのとある小さな村で出会った、しがないダンサーのマーゴと旅回りの興業師ウィルソン。彼らはマーゴの父親の遺言で隠し財産の存在を知り、さっそく大金を求めて旅に出る。だが、様々な苦難の末得たのは“愛”という名の宝物だった。異色ウエスタン「ワイルド・アパッチ」やS・ペキンパー監督の遺作「バイオレント・サタデー」などの脚本家で知られる、A・シャープの監督デビュー作。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不死身なあいつ", "不死身なあいつ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "都築浩介は、ヤクザを嫌って刑事と結婚したかつての恋人と再会する。しかし、彼女の夫は誤殺事件を起こして刑事を辞め、悪玉の用心棒になっていた。彼女の夫は、悪玉から都築の殺害を依頼されるが、彼はそれを拒否して殺されてしまう……。二谷英明が悪になりきれない男を好演。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Private Life of Sherlock Holmes", "シャーロック・ホームズの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "　ビリー・ワイルダーが長年の製作パートナー、I・A・L・ダイヤモンドと共に、コナン・ドイルの名探偵作品に挑戦。キャラクターはそのままだが、ユーモアを交えたオリジナル脚本が生きている。死後50年封印されていたワトソン博士の遺品が公開される。そこには、シャーロック・ホームズが解決してきた数々の難事件の記録と、ワトソンとホームズの共同生活についても記されていた。ホームズの映像化は数あれど、推理だけでなくこれほどまで私生活を描いたものは他に類を見ないだろう。", "1971-03-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT HAPPENED TOMORROW", "ルネ・クレールの明日を知った男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "クレール監督が戦火を逃れてハリウッドへ渡った時代に作られた４作品中、最も画期的といわれる異色サスペンス。新聞記者ラリーの手にどうしたことか翌日の新聞が舞い込んだ。おかげで彼はあっという間に地位も名声も手に入れる。しかし次に送られてきた新聞には自分の殺害記事が載っていた……。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MINISTRY OF FEAR", "恐怖省", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "精神病院を退院したニールは、駅前で催されていた慈善バザールで、占い師の予言によってケーキの重さを当てたことから妙な事件に巻き込まれる。ロンドンに着いた彼は、私立探偵を雇って事件の謎を追い、慈善バザールを主催した協会本部を訪れてウィリーとカーラの兄妹と知り会い、また占い師のベレイン夫人も訪れる。だが、ベレイン夫人に誘われて降霊術に参加したところ殺人事件が起こり、ニールは殺人犯として警察に追われる身となり、ますます事件は錯綜していく。何も知らない主人公がしだいにナチのスパイ事件に巻き込まれ、暗躍するスパイ組織を壊滅する。ナチ支配を逃れ、アメリカへと亡命した名匠F・ラングによるサスペンス・スリラー。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("半七捕物帖・三つの謎", "半七捕物帖・三つの謎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "江戸の酒問屋、津の国屋の養女殺し。真庭念流岩下道場の主、左内殺し。横浜の異人殺し。３つの事件を、半七の得意の推理で下手人をあげるまでを描く。主役の半七には、片岡千恵蔵が、その脇を東千代之介、鶴田浩二の二人が固める痛快時代劇。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KODO", "KODO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "和太鼓集団“鼓童”のライヴ演奏をビデオに収めたミュージック・ドキュメンタリー。迫力のパフォーマンスを自在に動くカメラによって追った映像に圧倒される。曲と曲との間に映し出される自然の風景も効果的に挿入されている。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Doutores da Alegria", "ドクター・ピエロ", "", "", "http://image.eiga.k-img.com/images/movie/54781/315x210.jpg?4325", 96, "赤鼻のピエロに扮して病院を訪れ、闘病中の子供たちを笑わせるブラジルの役者グループ「Doutores da　Alegria（喜びのドクター）」の活動を追ったドキュメンタリー。病棟で子供たちと触れ合う彼らの姿を生き生きと捉えた映像やメンバーたちへのインタビューを通し、病を抱える子供たちにとって喜びと笑いの生み出す力がいかに大切かを浮かび上がらせていく。ニューヨークブラジル映画祭最優秀作品賞など数々の映画賞を獲得。", "2009-10-06", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON’T LOOK BACK", "ドント・ルック・バック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1962年のデビュー以来、常に第一線で活躍し続けているミュージシャン、ボブ・ディランの、1965年のツアーの模様を追ったドキュメンタリー。アコースティックからエレクトリックへとギターを持ち変えたディランの転換期に当たり、当時、彼が生み出したサウンドは“フォーク・ロック”の先駆けとなった。映画は熱狂的に迎えられた全英ツアーの模様から始まる。インタビューや会話の中で、鋭い返事を投げかけるディラン。そんな彼の姿をステージの模様を交えて追い、そのカリスマ性を浮き彫りにしていく。“盟友”ジョーン・バエズや、ディランの思想的背景となったビート文学の代表的作家アレン・ギンズバーグも登場する。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リンゴ園の少女", "リンゴ園の少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "人気連続ラジオ・ドラマを映画化した作品で、美空ひばりの元気な演技と歌を売りものにした明朗な一編。津軽の少女マルミは近所でも評判の歌上手。ある時“リンゴ祭”の音楽祭で作曲家の野村はマルミを見いだすが、彼女はなんと野村の昔の恋人の娘だった……。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("傷だらけの天使", "傷だらけの天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "故郷指宿に戻って来た少年院帰りの八郎は、２年前の姉の自殺の真相を探り始める。当時、姉は雨宮章治という男とつき合っていた。だが雨宮家を訪ねた八郎は章治の不在を教えられ、追い返されてしまう。実は姉の死の影には思わぬ陰謀があったのだ。西郷輝彦自作のストーリーをもとにした歌謡映画。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL DITO NELLA PIAGA", "栄光の戦場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "第二次世界大戦のさなか、激戦地イタリアに赴いた米軍の若い中尉の任務は囚人兵の監督だった。最初に担当した死刑囚たちの処刑の時、突然ナチスの襲撃が。生き残った彼と二人の死刑囚は、３人だけで激戦下の敵陣地へ乗り込んでいくが……。怪優K・キンスキー主演の戦場アクション。", "1970-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コネコノキモチ", "コネコノキモチ", "", "", "http://image.eiga.k-img.com/images/movie/56111/315x210.jpg?4070", 72, "2年前に父親を交通事故で亡くした専門学校生の遥は、公園でマンチカンという足の短いネコを拾う。しかし、遥の母親は最愛の夫を失ったことが受け入れられず、ネコを飼うことにも反対する。そんな矢先にネコが姿を消してしまい、遥は家を飛び出しネコを探す。アイドルグループ「アイドリング!!!」の遠藤舞が映画初主演を飾り、主題歌でソロデビューも果たす。", "2011-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLOWERS IN THE ATTIC", "屋根裏部屋の花たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "父親の事故死によって、祖母の住む邸宅へと身を寄せることになった母と４人の子供たち。しかし祖母は子供たちの本当の親はお前たちの叔父だと告白し、屋根裏部屋へ隔離してしまう。やがて母の帰りを待ちわびる子供たちは母親が内緒で再婚しようとしていることを知らされて……。全米ベストセラー小説を映画化した悲劇的なダーク・ファンタジー。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAG　AR　NYFIKEN　GUL", "私は好奇心の強い女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "1968年のアメリカ公開時、その過激な性描写がセンセーショナルを巻き起こし、裁判にまで発展。1971年の日本公開時には45ヵ所もカットされたといういわくつきの衝撃作のノーカット版(４ヵ所のみ修正)。SEX大好きの破天荒な演劇学生レナ・ニーマンが、映画監督シェーマンの新作映画にかかわるうちに政治活動にのめり込んでいく。女優も監督も本人役で登場、黒人運動家キング牧師の映像を挿入するなど、ドキュメンタリー・タッチの大胆な構成が刺激的。60年代後半の、政治とカルチャーが渾然一体となった反体制ムードを如実に反映している。DVDはイエロー編とブルー編がある。", "1967-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Little Secrets", "ひみつの番人", "", "", "http://ecx.images-amazon.com/images/I/51VGFZFXF8L._SL210_.jpg", 97, "「シモーヌ」のエバン・レイチェル・ウッド主演によるファミリードラマ。バイオリニストを夢みる14歳の少女エミリーは、夏休みになっても友達とのキャンプには参加せず、練習に明け暮れる毎日を送っていた。その一方で、エミリーは近所の子どもたちの秘密を聞いてアドバイスする「秘密の番人」の仕事を請け負っていた。多くの子どもたちから頼られるエミリーだったが、実は自身も誰にも言えない秘密を抱えており……。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NINCOMPOOP", "Mr.プープの初恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "自分が勤める会社の女性オーナー、ローレンにひそかな思いを寄せるワルド。ところがマーカスという男が現れて会社も彼女も自分のものにし、ワルドは会社をクビになる。しかし、ローレンへの思いを断ち切れない彼は、陰ながら彼女を支え続けるのだった。「イズ・イット・ヘブン・イエット？」で称賛を得たカルダナが出演した、心優しき男の純愛物語。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シナリオライター★ 松本マリコの課題", "シナリオライター★ 松本マリコの課題", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "それなりに充実してはいるけれど、何か物足りないと感じている新人シナリオライター“松本マリコ”を主人公に、都会に生きる30代の女性をリアルに描いた女性応援歌。オセロの松嶋尚美が映画初主演で等身大の女性を生き生きと演じ、現代女性にちょっとした勇気を与えてくれる。いつも原稿を書くカフェで、誤って他人のパソコンを持ち帰ってしまったマリコ。その日から持ち主との奇妙な関係が始まる。", "2005-05-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おとうと", "おとうと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "気の向くままに遊びに没頭する弟と、口喧嘩ばかりしながらも唯一、心の通じ合う相手である姉。美しいばかりの姉弟の心の交流と、不治の病によって弟の青春が途絶される悲しさを描き出した青春ロマン。郷ひろみがみずみずしい若々しさで奔放な弟役を演じる。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜の影", "夜の影", "", "", "http://image.eiga.k-img.com/images/movie/54951/315x210.jpg?2434", 77, "コカインの売買で生き延びるマリアは人生に希望を失い、町の外れに建つ小さな教会で毎日祈り続ける日々を送っていた。そんなある日、組織の車が襲撃され多額の現金が奪われてしまう。その翌日に、廃墟で血塗れた状態で横たわる男を見つけたマリアは、自分の隠れ家に男を連れ帰り手厚く介抱するが……。主演は星乃舞、岡島博徳、田中冬星。", "2009-11-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人民英雄／PEOPLE’S HERO", "野獣たちの掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "閉店間際の銀行にチンピラ二人が強盗に入り、すぐに警官に包囲された。偶然、人質の中にいた強盗殺人犯は、これを機に国外逃亡を企てるが……。T・ロンが冷血な殺人犯に扮している。優れた脚本と演出によって、一級の社会派ドラマに仕上がっている。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAR FROM THE MADDING CROWD", "遥か群衆を離れて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 163, "T・ハーディの原作を映画化した一大ロマン。イギリスのウェセックスに住む農場の女主人が、紆余曲折を経て、やっと“宿命の男性”が誰であるかを悟り結ばれるまでを描く。“幸せは足元にある”という教訓的ニュアンスもあり。ローグの捉えた田園風景がこの上なく美しい。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トテチータ・チキチータ", "トテチータ・チキチータ", "", "", "http://image.eiga.k-img.com/images/movie/57431/315x210.jpg?3645", 95, "東日本大震災後の福島を舞台に、太平洋戦争で引き裂かれた家族の生まれ変わりが現世で再会し、再び人生に希望を見出していく姿を描くドラマ。事業に失敗して自殺を考えていた一徳は、不思議な少女・凛と出会い、導かれるように福島へ。一徳と凛、そして原発事故で避難生活を余儀なくされていた高校生の健人は、60数年前に戦争でバラバラになってしまった家族の生まれ変わりだった。さらに、一徳の前世での妹・百合子は戦争をただ1人生き延び、痴呆症を抱えながらいまも福島に独居していた。", "2012-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うぐひす侍", "うぐひす侍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "「春秋一刀流」でデビューし、絶賛を浴びた丸根賛太郎の第２作。主演は前作に引き続き片岡千恵蔵で、これは当時の新人監督としては破格の待遇であった。青葉藩御台所のミソ用人の由解新八郎はうぐいす捕りの内職で糊口をしのぐ軽輩者。うぐいすを通じて知り合った百姓の子供たちから農民の窮状を聞いた新八郎は、彼らの力になることを決意する。後半は困窮に追いやった悪家老退治で面白味に欠けるが、前半のうぐいすを使って娘に恋文を届ける、といったメルヘン調ともいうべき展開が新鮮。リズミカルな場面転換やキザな台詞、加えて凝った字幕を多用する才気抜群の映画青年・丸根賛太郎の演出ぶりが鮮やかである。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　CICATRICE　INTERIE´URE", "内なる傷痕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "前衛的映画作家P・ガレルが、彼の作品としては比較的大きな予算をつぎ込み、当時結婚生活を送っていた伝説の歌姫ニコとのコラボレーションで作り上げた中編。ガレルとニコが荒涼とした白い砂漠を延々と歩き続けるオープニングを経て、アイスランド、エジプト、イタリアなどの大自然で撮影した神話的イメージが連なり、全編にニコの荘厳な歌声が重ねられていく。", "1971-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MATILDA", "マチルダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "カンガルーのマチルダが、ボクシング界に殴り込むという奇想天外な動物アクション。カンガルーのジャンプ力を生かしたパンチが、ボクサーを倒していくさまが見もの。原作は、P・ギャリコの書いた同名小説で、E・グールド、R・ミッチャムの好演が光るB級作品。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVELY　RITA", "Lovely Rita", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "カンヌ映画祭“ある視点部門”に出品され絶賛を受けた、オーストリアの新鋭J・ハウスナーの長編デビュー作。思春期特有の不安や孤独感に押しつぶされていく、少女リタの心の揺らめきが鮮烈に浮かび上がる。郊外の住宅地に暮らすリタは、なぜか心が満たされずいつも不機嫌な顔をしている女の子。隣家の男の子やバスの運転手に興味を惹かれ、初めての化粧にチャレンジする。ところが、平凡な日常に圧迫されて、次第にリタは壊れ始める……。", "2001-01-01", "7,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents kiss!　「地蔵ノ辻」", "桃まつり presents kiss!　「地蔵ノ辻」", "", "", "http://image.eiga.k-img.com/images/movie/54481/315x210.jpg?1541", 15, "", "2009-03-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トリコ3D 開幕！グルメアドベンチャー!!", "トリコ3D 開幕！グルメアドベンチャー!!", "", "", "http://image.eiga.k-img.com/images/movie/55781/315x210.jpg?4482", 70, "週刊少年ジャンプに連載中の島袋光年による人気コミックを3D映画化。少年ペックは、未知なる食材を求めて世界中をわたり歩く“美食屋”たちのカリスマ、トリコを探してグルメマーケットへとやって来る。やっとの思いでトリコを発見したペックは、故郷の村を壊滅寸前に追い込んだ怪鳥ゲロルドを退治してほしいと頼み込む。トリコは、捕獲が極めて困難とされるゲロルドを食材として手に入れるため、命を懸けた戦いに挑む。「ONE PIECE 3D 麦わらチェイス」と2本立て上映。", "2011-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PETER’S FRIENDS", "ピーターズ・フレンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ケネス・ブラナー監督、主演のハートウォーミングな人間ドラマ。1982年、そろって大学を卒業した６人の男女が、仲間の一人、ピーターの屋敷で10年後の大晦日に再会。昔のように楽しい時を過ごしたいピーターだったが、旧友たちはそれぞれ結婚や家庭の問題を抱え、それどころではない。彼らのバラバラの心を最後に結びつけたのは、ピーターの重大な告白だった……。「再会の時」を思わせるノスタルジックなムードに1990年代的な社会性をプラス。リアルな物語ながら、ブラナーのユーモアに富んだ語り口は限りなく温かい。愛妻E・トンプソンのいきいきとしたコメディエンヌぶりや、バックに流れる1980年代ヒット・ポップスも秀逸。", "1992-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怖がる人々", "怖がる人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "筒井康隆、椎名誠ら人気作家の短編小説５編をもとに、日常生活の陰に潜む不条理な恐怖を描いた和田誠の監督第３作。エレベーターの中に、カミソリを持つ女と一緒に閉じ込められる男の話「箱の中」、乗り越し料金をめぐり駅員から異常な制裁を加えられる「乗越駅の刑罰」など、笑いの中の悪夢が展開する。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野獣を消せ", "野獣を消せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "渡哲也主演・長谷部安春監督によるニュー・アクション。米軍基地のある地方都市にプロハンター・浅井がやって来る。彼の妹は地元の不良グループによってレイプされ、自殺していた。浅井は、不良グループのメンバーを捜し出し、野獣を狩るように次々と殺していく。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("テニスの王子様／二人のサムライ The First Game", "テニスの王子様／二人のサムライ The First Game", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "'99年より『週刊少年ジャンプ』で連載スタートし、大好評を得た人気漫画を映画化。テニス少年たちのクールな戦いを描いた新時代のスポ根もので、スクリーン狭しと迫力マッチが繰り広げられる。主人公リョーマの兄リョーガをはじめ、映画オリジナル・キャラクターの登場も見逃せない。大富豪の桜吹雪から、船上特設コートでの試合に招待された青学テニス部メンバーたち。しかし、桜吹雪にわざと負けるように言われ……。", "2005-01-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Heaven Knows, Mr. Allison", "白い砂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　第二次世界大戦下の南太平洋。日本軍に包囲された孤島に、アメリカ海兵隊員のアリスン伍長が漂着する。彼は教会をたったひとりで守る美しい尼僧アンジェラと出会うことに。ふたりは日本軍の攻撃を逃れながら、洞窟に身を潜める。やがてアリスンはアンジェラを愛し始めるが……。極限下で展開される男女のラブ・ストーリー。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chernobyl Heart", "チェルノブイリ・ハート", "", "", "http://image.eiga.k-img.com/images/movie/56611/315x210.jpg?3185", 61, "1986年4月26日の旧ソビエト連邦（現ウクライナ）・チェルノブイリ原子力発電所事故から16年後の2002年に製作されたドキュメンタリー。16年を経てもなお高濃度の汚染にさらされている「ホット・ゾーン」と呼ばれる地域や、生まれつき重度の疾患を持って生まれる子どもたちを指す「チェルノブイリ・ハート」など、事故の被爆被害の実態に迫る。2003年・第76回アカデミー賞で短編ドキュメンタリー賞を受賞。その後、06年にさらなる追加撮影も敢行。原発事故後に初めて故郷に帰った青年の姿も収めている。", "2011-08-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠狂四郎 人肌蜘蛛", "眠狂四郎 人肌蜘蛛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "母の墓参りに甲府に立ち寄った狂四郎は、そこで将軍・家斉の妾腹の兄妹が暴虐の限りを尽くしていることを知る。己れと同じ混血の青年を救うため、その鬼館に向かった狂四郎は毒矢を受け意識を失ってしまう。村人をなぶり殺す闘技場や情欲にふける姫など、猟奇度の高い一編。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UKオペラ＠シネマ　モーツァルト：フィガロの結婚", "UKオペラ＠シネマ　モーツァルト：フィガロの結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 185, "イギリスが誇るオペラの殿堂、ロイヤル・オペラ・ハウスの2006年上演作「フィガロの結婚」をデジタルシネマ化。舞台をフランス革命直後の宮廷に移し、結婚を控えたフィガロとスザンナ、そんな2人に対してそれぞれの思惑を抱く周囲の人々が巻きおこす騒動が展開していく。モーツァルトの代表作として世界中で愛され続けてきた傑作が、人気演出家デイビッド・マクビガーによって魅力たっぷりに描かれている。", "2008-12-27", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEFORE THE NICKELODEON", "ニッケル・オデオン以前 アメリカ映画の誕生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "アメリカ映画の草創期に活躍した、アメリカ映画の生みの親ともいわれるエドウィン・S・ポーター。その代表作を中心に、彼が生きた時代を考察するドキュメンタリー。1869年に生まれたポーターが、エディスン社の監督となり、映画史上に名をとどめる「アメリカ消防夫の生活」(1902)や「大列車強盗」(1903)などを撮りながら映画に革新をもたらし、“ニッケルオデオン”時代と呼ばれる、映画が爆発的な人気を呼ぶ時代を築いたことがよく分かる。これは映画史に興味のある人だけでなく、映画ファンなら十分に楽しめるもの。声の出演に多くの映画人が協力しているのも興味深い。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NEGOTIATOR", "交渉人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "交渉人とは人質を取って立てこもる犯人を説得し、事件解決に導くプロフェッショナルのこと。シカゴ警察でトップの実績を持つ交渉人のダニーは殺人と横領の濡れ衣を着せられてしまう。このままでは身の破滅につながると考えた彼は警察ビルに人質を取って籠城、その間に時間を稼ぎ、真犯人を突き止めることにする。そして署内の人間さえも信用できない彼は交渉相手として他の管轄区のトップ交渉人のクリスを指名した。人間の心理を知りつくし、研ぎ澄まされた頭脳を持つ二人の交渉人の熱い駆け引き、そして敵対するなかで生まれていく男の絆をスリリングに描き出す。S・L・ジャクソン、K・スペイシーという演技巧者二人を配したキャスティングも絶妙。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サロゲート・マザー", "サロゲート・マザー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "不妊症に悩む日系アメリカ人のナオミは、夫のマイクと相談して代理出産という手段を選び、ジェニーという女性に代理母を依頼する。ジェニーは二人の家に同居して間もなく妊娠するが、それ以来ナオミとマイクの周囲では不吉な事件が相次いで起こる。全編ロサンゼルスでロケが敢行され、松田聖子以外はスタッフ、キャストともすべて外国人によって作られたサスペンス。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alaska", "アラスカ 小さな冒険者たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "アラスカの大自然に暮らすショーンとジェシーの兄妹。ある日二人は、父親が操縦する飛行機が山奥に墜落したと知らされる。捜索活動が打ち切られても父の生存を信じる彼らは、二人だけで救出に向かう。その途中、密猟者から救出した白クマに見守られながら危険な旅に挑む。激流や氷壁に立ち向かうなどのスリリングな冒険が、壮大な大自然を背景に展開する感動作。賢い白クマが、幼い兄妹の窮地を助けて大活躍！", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE OBJECT OF BEAUTY", "幸福の選択", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "高級ホテルを泊まり歩き、ぜいたくな生活を送る青年実業家のジェイクと恋人ティナ。ところが、ある日彼らはクレジット・カードの使用停止を宣告され、突然の破産状態に。一変した生活の中で、二人の心はすれ違いはじめ……。愛情の危機に瀕したカップルの姿を辛らつに描き出す、スタイリッシュなラブ・ストーリー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CORTO　MALTESE：　LA　COUR　SECRE｀TE　DES　ARCANES", "コルト・マルテーズ／　皇帝〈ツアー〉の財宝を狙え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "フランスをはじめ、ヨーロッパで絶大な人気を誇るH・プラット原作のコミック・シリーズを劇場アニメ化。世界を旅する英国海兵隊員コルト・マルテーズの冒険を、歴史上の出来事に絡めてスリリングに描くアクション・アドベンチャー。日本のアニメーションにインスパイアされて製作されたという、緻密な映像と迫力あふれるアクションで観る者を魅了する。1919年、ロシアの反革命軍政府の金塊を積んだ装甲列車が満州へ出発。伝説の冒険家コルトは、命を救ってくれた秘密結社レッドのために金塊強奪を計画。相棒の脱走兵ラスプーチンとともに列車に乗り込む。", "2002-01-01", "2,4,58", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("襲う！", "襲う！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "レイプから始まる女としての性の目覚めをストレートに描いたロマンポルノ作品。緩急を駆使した長谷部安春監督の演出力が光る。特に前半部は、犯人探しのサスペンス劇が展開。ミニ・パトカーに乗っていた婦人警官の久美子は、トラックに衝突されて気を失っている間、二人の男に暴行される。彼女は犯人探しの末、彼らを特定したが、再び男たちに襲われてしまう。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wild Hogs", "団塊ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/1901/315x210.jpg?4126", 100, "ハーレーでアメリカ横断の旅に出た中年男4人組の珍道中をジョン・トラボルタ、ウィリアム・H・メイシーら主演で描き、全米で大ヒットを記録したロード・ムービー。妻に逃げられ自己破産に追い込まれた実業家ウディは、歯科医のダグ、自称小説家のボビー、パソコンマニアのダドリーら、同じく人生に行き詰った4人の仲間を誘って自由気ままなバイクの旅に出る。ところが彼らの行く先には思わぬ災難が待ち受けていて……。", "2008-02-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俗物図鑑", "俗物図鑑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "筒井康隆の同名小説を映画化したブラック・ユーモアに彩られた異色作。世の良識に反撃しようとする、奇妙な評論家集団、梁山泊プロダクションの面々が、毒舌パフォーマンスを展開する。南伸坊、松田政男、石上三登志など、実際の評論家が評論家を演ずる疑態に似た演技が見もの。なかでも大林宣彦の自殺評論家の怪演が面白い。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄人28号", "鉄人28号", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "『鉄腕アトム』と並ぶ元祖ロボット漫画の実写映画化。「ごめん」の冨樫森監督が、あえて派手さを抑え、少年の成長物語に仕上げている。CGを駆使しながらもレトロな雰囲気を漂わせるロボット描写も見もの。主人公の正太郎少年を演じたのは「ラスト・サムライ」の池松壮亮。謎の老人・綾部に導かれ、亡き父が遺した鉄人28号の操縦を託された正太郎少年は、悪のロボット、ブラックオックスと戦うハメに……。", "2005-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　VIKINGS", "バイキング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "９世紀ごろ、北欧の海を支配していた海賊＝ヴァイキングの戦いを赤裸々に描いた海洋スペクタクル大作。主演カーク・ダグラスが主宰するブライナ・プロの製作作品だが、海賊の王子役の彼はどちらかというと悪役で、むしろ彼らに立ち向かう奴隷役のトニー・カーティスがヒーロー。両者が刃を交えるクライマックスは残忍かつ圧巻である。実際にヴァイキング船を３隻復元して撮影を行っている。悪名高いヴァイキングの王者ラグナーの息子、ヘスティングスは、モーガナ姫を奪う。しかしラグナーがかつてイングランド女王に産ませた子、オリックが彼女に愛を寄せるようになり、ヘスティングスとオリックの決闘が始まる……。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A SCHOOL OUTING", "追憶の旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "銀行で不意の発作に見舞われた老女ウララは、死の床に就きながら、彼女の人生で最も美しかった思い出を回想する。それは女学生の頃の徒歩旅行の思い出。この旅行の間に様々な出来事があった……。切なくはかない青春のひとコマを、繊細なタッチでフィルムに刻み込んだ作品。", "1983-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREAT BANK ROBBERY", "空かける強盗団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "悪玉のキンケードが市長をしている町の銀行を狙って、神父の一行、野盗の一味、悪事を暴くための帳簿が欲しいテキサス・レンジャー隊ら４組の“大泥棒”たちが、激しい争奪戦を展開するアクションもの。巧みな戦略により、銀行の大金庫の中味は神父の一行の手に落ちそうになるが……。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("While She Was Out", "アライブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2008-01-01", "7,10,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("5 FINGERS", "五本の指", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "スパイ史上で最も高額な報酬を得たといわれるトルコ駐英大使の執事ディエロ、コードネーム＝シセロ。彼は駐英大使館から盗んだイギリスの機密をドイツに売り渡し、英諜報部とゲシュタポを出し抜いて南米に逃亡したのだった……。第二次大戦中の実際の事件をもとに、名優J・メイスンの主演で描くスパイ・サスペンス。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOOKIN’ TO GET OUT", "大狂乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ギャンブラーのアレックスは１万ドルの借金を作ってしまい、相棒のジェリーとラスベガスに行き借金の穴埋めを狙う。豪華ホテルに宿泊した彼らは、社長のゲストを装って１万ドルの軍資金を得る。ところがたちまちすってしまい、ついには14万ドルの借金に。二人は一発逆転の賭けに出る。H・アシュビー晩年の１本。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トーリ", "トーリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "俳優だけでなく、ミュージシャンやアーティストとしても独特の才能を発揮している浅野忠信が、映画制作に挑んだ初監督作品集。実写やアニメーションなど様々な手法を使って描いたオムニバス作品だ。浅野が原作で佐藤佐吉脚本の時代劇「心の刀」は、北村道子の衣裳や、映像美、主演二人のみずみずしい演技が光る斬新な侍ストーリー。鳥のアニメーション作品「BIRD」、デコトラとグラフィティのコラボレーション「ATO」、お笑い芸人の話「続く二人」、バレエの美しさを堪能できる「or」の全５作。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("14才のハラワタ", "14才のハラワタ", "", "", "http://image.eiga.k-img.com/images/movie/54751/315x210.jpg?4312", 60, "「学生による商業映画製作」をコンセプトにしたトリウッドスタジオプロジェクト作品。原田ワタル（通称ハラワタ）は、成績を競うナツキと青木さんを尻目に授業をサボってはいつもひなたぼっこしているマイペースな女子中学生。家では仕事帰りのお母さんとスーパーのお惣菜を食べて、今日も帰らないお父さんをマイペースに待ち続ける。", "2009-10-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版ポケットモンスター　ベストウイッシュ　ビクティニと黒き英雄　ゼクロム", "劇場版ポケットモンスター　ベストウイッシュ　ビクティニと黒き英雄　ゼクロム", "", "", "http://image.eiga.k-img.com/images/movie/55951/315x210.jpg?1346", 97, "2010年から始まった人気アニメ「ポケットモンスター」の新シリーズ「ベストウイッシュ」の劇場版。1本の物語をベースに、登場するポケモンやシーン、セリフの異なる「ビクティニと黒き英雄　ゼクロム」「ビクティニと白き英雄　レシラム」の2作品を同日公開する。かつて「大地の民の王国」として栄えたアイントオークにやってきたサトシらは、不思議なパワーをもつ幻のポケモン、ビクティニに出会う。しかし、王国復活を願う大地の民の末えいがビクティニの力を利用しようと企み、その影響で街に異変が発生してしまう。", "2011-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("That Darn Cat!", "シャム猫FBI／ニャンタッチャブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "誘拐事件の解決に奮闘するシャム猫と人間たちの姿をコミカルに描いた実写版ディズニー映画。シャム猫のDCは、散歩の途中で銀行強盗のアジトに迷い込んでしまう。そこには、人質として連れてこられた女性が監禁されていた。DCが着けてきた腕時計が女性からのSOSだということに気づいた飼い主の少女は、FBI捜査官と共に女性の救出に向かうが……。1996年に「誘拐騒動／ニャンタッチャブル」というタイトルでリメイクされている。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("家族同盟", "家族同盟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "世代の異なる一人者の男女６人が寄り集まって、それぞれ勝手に相手を妹や息子や母に仕立てて“家族ごっこ”を開始。やがて本物の家族のような絆で結ばれていくという、コメディ・タッチだがホロリとさせられる擬似家族ドラマ。ダ・カーポの歌う主題歌も印象的。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pink Narcissus", "ピンクナルシス", "", "", "http://image.eiga.k-img.com/images/movie/33581/315x210.jpg?4586", 70, "60年代のゲイ・アイコン、ボビー・ケンダール主演のアート・フィルム。1971年の初公開時には制作・監督・脚本・撮影すべてが匿名とされ、様々な憶測を呼んだ。ギリシャ神話に登場する美少年ナルシスをモチーフに、カラフルに彩られた幻想的な世界を描く。ケンダールと一緒に暮らしていたジェームズ・ビドグッド監督が、ゲイ雑誌の表紙を飾るなどして活躍していたケンダールの妖艶な魅力を最大限に引き出している。", "2006-07-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋と花火と観覧車", "恋と花火と観覧車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "作詞家、放送作家、番組プロデューサーなど、マルチな顔を持つ秋元康が企画した大人のラブ・ロマンス。NHKの朝の連続ドラマ『ひまわり』で人気を得た松嶋菜々子の映画デビュー作でもある。長塚京三扮する森原邦彦は妻に先立たれ、大学生の一人娘ひとみと暮らす中年サラリーマン。いつまでもやもめ暮らしを続ける彼をひとみは強引に結婚情報サービス会社に入会させるのだった。邦彦は渋々、イベント・パーティーに出席するが、そこで会社の取引相手の若いOL、史華と出会う……。恋の現役を退いたと自覚する邦彦とそんな彼を慕い始める史華の恋の行方を、コミカルかつロマンティックに綴っていく。当時、プロ野球の投手だった佐野重樹が内気な独身男を演じて、いい味を出している。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Buena Vida", "サンティアゴの光", "", "", "http://image.eiga.k-img.com/images/movie/54791/315x210.jpg?4340", 108, "夫と離婚し思春期の娘との関係に悩むテレサ、幼い息子を育てるために体を売るパトリシア、ベルリンの音楽学校を出てチリに帰国したものの職探しに駆け回るマリオ。チリの首都サンティアゴを舞台に、大都会の片隅で幸せを求めて生きる人々の姿を描いた群像ドラマ。2004年の「マチュカ」で高い評価を得たアンドレス・ウッドがメガホンを取る。2009年スペイン・ゴヤ賞で最優秀スペイン語外国映画賞を受賞。", "2009-09-19", "77,22,9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUTHU", "ムトゥ　踊るマハラジャ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 166, "年間製作本数は800本以上といわれるインド映画界の底力を見せつけ、日本では単館公開ながら連日大入りの記録的ロングラン・ヒットを飛ばしたエンターテインメント巨編。タミルナードゥ州の大地主ラージャーに仕えるムトゥは、馬車使い兼用心棒として周囲の絶大な信頼を集めていた。そんなある日、ラージャーが劇団の看板女優ランガに一目惚れ。ところがランガは、こともあろうにムトゥと恋に落ちてしまう。その事実を悪党に吹き込まれたラージャーは激怒し、ムトゥを屋敷から追放する……。痛快な勧善懲悪ドラマをベースに、ロマンスや主人公の出生の秘密を盛り込んだ超ドラマティックなストーリー。インド国民に崇拝されるスーパースター、ラジニカーントが、ブルース・リーばりの鉄拳アクションや馬車チェイスにフル回転の大活躍を連発。アイドル女優ミーナとともに披露するエネルギッシュな極彩色のミュージカル・シーンも圧巻だ。", "1995-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA ODISEA DE LOS ANDES", "アンデスの聖餐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "1972年10月13日。ウルグアイのモンテビデオからチリのサンチャゴに向かった飛行機が、アンデスの山中に墜落した。飛行機の乗客45名のうち32名が墜落時には生き残っていたが、最終的に救出されたのは16名。生存者は、人肉を食べて生き長らえたという。この事件を現場のフィルム、捜索フィルムなどで構成したドキュメンタリー作品。イーサン・ホーク主演の「生きてこそ」（1993）は、この事件を映画化したもの。", "1975-01-01", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BACHELOR", "プロポーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "主演のC・オドネルが自ら製作総指揮を手掛け、バスター・キートンの傑作喜劇「セブン・チャンス」をリメイク。独身生活に見切りをつけて、恋人アンとの結婚を決意した青年ジミー。珍妙なプロポーズで彼女を激怒させてしまった彼が、24時間以内に結婚しないと遺産相続がパーになる状況に追い込まれ、ドタバタ騒動を繰り広げていく。人気歌手M・キャリーが主人公の元恋人役で出演。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博奕打ち 殴り込み", "博奕打ち 殴り込み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "シリーズ第5弾で正統派任侠映画に回帰した、お馴染みの鶴田浩二が活躍する一作。東映ヤクザ映画だけではなく、抗争ものは男同士のストレートな対決であればあるほど光り輝く。さらに男同士の厚い友情が泣かせてくれる本作は、シリーズ中の最高傑作だ。大正時代末期、抗争相手の組を潰した二人組がいた。一人は刑務所に入り、一人は行方不明に。出所後、主人公・幸次郎は相棒・吉五郎の消息を追うが……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEU　DE　MASSACRE", "コミック・ストリップ・ヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "フランス、ヌーヴェルヴァーグのポップな側面を象徴する異色作。コミック作家の夫婦ピエールとジャクリーヌの前に現れた、漫画を熱狂的に愛する男ボブ。スイスの別荘に招かれた夫妻は、風変わりな彼の行動に戸惑いつつも、やがてボブをモデルにして新たな創作を開始する。実写とコミックが交錯する映像、グルーブ感のある音楽が印象的。日本公開時のタイトルは「殺人ゲーム」。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Legendary Amazons", "女ドラゴンと怒りの未亡人軍団", "", "", "http://image.eiga.k-img.com/images/movie/57991/315x210.jpg?2199", 108, "ジャッキー・チェン製作で、中国・宋王朝の時代を舞台に、亡き夫の後を継いで戦場に赴いた女たちの戦いを描くアクション。製作費5000万元（約6億円）がつぎこまれた大作だが、中国のラジー賞と呼ばれる「金のほうき賞」で作品賞、監督賞、男優賞、女優賞の全部門にノミネートされ、主演のセシリア・チャンが「最も失望させられた女優賞」を受賞したトンデモ映画。宋王朝の皇帝が酒池肉林の生活を送り、度重なる戦争のため国家は崩壊寸前。宋の楊家将軍と一族の男たちは西夏王朝の侵攻に挑むが、全滅してしまう。夫の死に怒りを抑えきれない将軍の未亡人・桂英は、12人の未亡人を率いて戦場へ向かう。", "2012-04-28", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FALLO！", "桃色画報", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「カリギュラ」「郵便屋」などでおなじみのイタリアン・エロスの大家T・ブラスが、大らかなタッチで紡ぎ上げたオムニバス映画。バカンス先のホテルでウェイターを巻き込んでエッチに耽る夫婦など、様々なシチュエーションの６つのショート・ストーリーで構成。美しくグラマラスな女優陣の開けっぴろげな艶技にもア然とさせられる１本だ。テレビ局重役夫人のステファニアは、ショーガールのエリカと情事を重ねる夫の裏をかこうと画策し、エリカの夫で野心深く出世を狙うディレクターのブルーノを誘惑した。すると、抑えていた欲情が爆発し……。", "2003-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pride and Glory", "プライド・アンド・グローリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON THE AVENUE", "陽気な街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ロマンチックなラブ・ストーリーを、陽気な歌と音楽で包んだミュージカル。30年代に頻繁に作られたブロードウェイを舞台にした作品だが、ほのぼのとした雰囲気にあふれている。名曲『ホワイト・クリスマス』『アニーよ銃を取れ』で有名なI・バーリンの楽曲も心地良い。台本作家へ自らの家族の風刺演劇の抗議に行った娘は、作家と恋に落ちる運命に。やがて女性歌手との三角関係へ発展して・・・・・・。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("S．L．C．PUNK！！！", "SLC PUNK！！！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「スピード」などのヤン・デ・ボン監督が製作総指揮を務めるパンク映画。スティーボとヘロイン・ボブはソルトレイクシティーのパンクスだった。しかし、この町はパンクスにとってはおとなしくて優しい町だ。彼らのパンクスの仲間はホームレスになったり、町を出ていったりと結局、彼ら二人だけになってしまった。そんなある日、鎮痛剤の飲み過ぎでヘロイン・ボブが死んでしまう。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スクラップ・へブン", "スクラップ・へブン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "バスジャックに遭遇したことから無力感に苛まれるようになった若い男女3人が、それぞれのやり方で爆発＝解放していこうとする様を、あるときはユーモアたっぷりに、あるときはヒリヒリするような肌触りで見つめる。疾走と停滞が織り成す、青春の光と影が独特の風合いを醸し出す一作。バスジャックに遭遇したシンゴ、テツ、サキ。警官なのに無力だったシンゴは後悔の念から、過激派テツと共に“復讐代行業”を開始する。", "2005-10-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤頭巾ちゃん気をつけて", "赤頭巾ちゃん気をつけて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "芥川賞を受賞した庄司薫の同名のベストセラー小説を「育ちざかり」「兄貴の恋人」など東宝青春路線の佳作を数多く手掛けた森谷司郎監督が映画化。東大入試の中止が発表された1969年の冬、卒業を間近にひかえた名門・日比谷高校３年生・薫の１日を、隠し撮りや手持ちカメラの使用などの斬新な手法で描き、微妙に揺れ動く若者の心情を見事に表現した。主人公の薫役の岡田裕介と、その女友だちの由美に扮する森和代はともに公募で選ばれ、そのみずみずしい存在感がこの作品に素晴らしい魅力をもたらしている。ただし、本編が終わったあと、当時の風俗を象徴するヌードやデモ隊の写真が連なるラストには、賛否両論があった。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY　BEAUTIFUL　GIRL，MARI", "マリといた夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "田舎の漁村を舞台に、異世界の少女に恋をした少年のひと夏を描いたノスタルジックな韓流アニメ。繊細で美しい作品世界に惚れ込んだイ・ビョンホンが、主人公ナムの声優として参加。“アニメ界のカンヌ映画祭”と呼ばれるアヌシー国際アニメーションフェスティバルでは、グランプリに輝いた。孤独な少年ナムは、壊れた灯台で別世界に続く道を発見。そこでは不思議な生き物に乗ったマリという少女が暮らしていた。", "2005-08-06", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明日は明日の風が吹く", "明日は明日の風が吹く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "石原裕次郎が、今は亡きヤクザの組長の次男に扮し、ふとしたことからヤクザの世界に足を踏み入れていくさまを描くアクション映画。1958年といえば、裕次郎がデビューして３年で、乗りに乗っている時期。北原三枝を相手役に迎え、きっぷのいい青年を好演。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZIR−E DERAKHTAN−E ZEYTOON", "オリーブの林をぬけて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "前作「そして人生はつづく」の撮影中の実話をもとに、キアロスタミ監督が想像力を膨らませ紡ぎ出したみずみずしいラブ・ストーリー。舞台となるのは本作「オリーブの林をぬけて」の撮影現場で、スクリーンにはこの作品のスタッフたちが登場、虚構と現実の狭間でストーリーは展開していく。撮影の手伝いをしていたホセインは、新婚夫婦の夫役に抜擢された。偶然にも、妻を演じるのは以前プロポーズした少女タヘレ。両親から断られていたが、彼女自身の気持ちが知りたいホセインはひたむきに話しかける。撮影が終われば会えない。最後の日、彼は返事をもらうため、オリーブの林をぬけ、ひた走る……。", "1994-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("American Ninja 4: The Annihilation", "ブラックフォース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "困難な作戦に立ち向かう特殊部隊デルタフォースの激闘を描く、戦争アクション。湾岸戦争後の中東を舞台に、人質となった親友を救うべく、デルタフォースに復帰したジョーは、現地人の協力で、決死の救出作戦を敢行する……。派手な撃ち合いはもちろん、日本刀やヌンチャク・アクションも楽しめる。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Emperor's New Groove", "ラマになった王様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "南米の険しいジャングルの彼方にある王国の王様、クスコは人もうらやむ美貌と才能を持ちながら、性格が悪く傲慢で人に意地悪ばかりしていた。ある日リストラされた魔女イズマの恨みをかったクスコは、魔法の薬で“ラマ”に変えられてしまう。ジャングルで迷子になったクスコを助けてくれたのは、心優しい農夫のパチャだった。ラマになっても王様気取りのクスコとパチャは仲違いを繰り返しながらも城を目指して旅をするが……。", "2001-07-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Punktchen und Anton", "点子ちゃんとアントン", "", "", "http://image.eiga.k-img.com/images/movie/1691/315x210.jpg?2258", 108, "小学生の点子ちゃんとアントンは、大の仲良し。でも近頃アントンは忙しくて、なかなか一緒に遊べない。アントンは2人暮らしのお母さんが病気で、家計のためアイスクリーム屋で働いているからだ。裕福な家庭の点子ちゃんもなんとかアントンを助けたいと両親に相談するが、2人とも仕事ばかりで無関心。そんな両親にあきれた点子ちゃんは、地下鉄の駅でストリート・パフォーマンスをやってお金を稼ごうと思いつく。", "2001-06-30", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MENACE", "メナース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "父の死後、莫大な財産と運送会社を相続したドミニクは、会社の副社長サバンを熱愛していた。だがサバンは彼女の激しい気性に我慢できず、独立を決意し、新しい恋人ジュリアもできた。この三角関係の中に起きた情念の事件がスピーディーに展開する。DVDタイトルは「イヴ・モンタン IN メナース」。", "1977-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ともしび", "ともしび", "", "", "http://ecx.images-amazon.com/images/I/519N8lvy87L._SL210_.jpg", 79, "日本映画界を代表する監督と新人監督たちが競作する「映画番長」シリーズ第2弾「エロス番長」の1作として、新人女性監督・吉田良子がメガホンを取り、都会で孤独に暮らす女性の歪んだ愛をつづったラブストーリー。図書館で働く裕子は、周囲からは笑顔も見せない内気な女性だと思われている。しかし裕子には、思いを寄せる青年・守が暮らすマンションに忍び込んでは住人を怖がらせて追い出してしまうという裏の顔があった。", "2004-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("笑拳怪招／THE FEARLESS HYANA", "クレージー・モンキー 笑拳", "", "", "http://ecx.images-amazon.com/images/I/51NcYwPeRML._SL210_.jpg", 98, "ジャッキー初の監督・主演で彼の名声を決定的にしたヒット作。拳の師でもある祖父を殺されたジャッキーの怒りが爆発。殺し屋、鉄の爪との闘いがはじまる。今回の技は“笑拳”。人間の喜怒哀楽の感情に基づくこの秘技を自在に操り、敵を追い詰め、ついに勝利を収める。", "1979-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOCK CORRIDOR", "ショック集団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "精神病院で起こった殺人事件を追うため、ニューヨークのルポライターが患者を装って病院に潜入する。犯人を突きとめ、病院の実態をも暴き、その記事はピュリッツァー賞に輝くのだが、その時彼の精神もまた、むしばまれていた……。狂気と暴力を視覚化して衝撃的な作品。S・コルテスの幻惑的な映像が魅力。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Sicilian", "シシリアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "1940年代のシチリア。青年ジュリアーノは親友アスパヌと組み、土地を持つことができず貧困で苦しむ農民のために盗みをはたらき、金品を与えていた。マフィアの大物ドン・マジーノからも一目置かれていたジュリアーノは群盗団を組織し、強奪を繰り返す。しかし政治的な駆け引きに巻き込まれ、やがて農民を敵に回すことに……。「ゴッドファーザー」で知られるマリオ・プーゾの原作を、シチリア・ロケで映画化。ビデオ発売のノーカット版は146分。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE BIG LEAGUE", "リトル・ビッグ・フィールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "父を亡くし、母と二人暮らしのビリーは野球が大好きな12才の少年。地元大リーグチーム、ミネソタ・ツインズのオーナーである祖父トーマスの影響を受け、ビリーの野球の知識はプロ顔負け。そんなある日、トーマスが急死してしまう。悲願に暮れるビリーだったが、祖父の遺志を継ぎミネソタ・ツインズのオーナー兼監督に就任。初めのうちはガキ相手にやる気のなかった選手たちも、純粋に野球を愛するビリーに心を動かされていく。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うる星やつら 完結篇", "うる星やつら 完結篇", "", "", "http://ecx.images-amazon.com/images/I/21S5V7N1KVL._SL210_.jpg", 90, "TVシリーズ放映終了後の公開作。監督は押井守、やまざきかずおに続きベテランの出崎哲が担当。友引高校にラムの婚約者と称するルパという若者が現れ、ラムを闇の宇宙に誘拐する。あたるたちはランのUFOでそのあとを追い、ルパの幼なじみのカルラと手を組んで、ルパとラムの結婚式に突入。だがあたるとラムは、互いに意地を張り合い和解しない。しかもルパと仲たがいしたカルラのミスで、闇の国のキノコが大繁殖し、地球滅亡の危機を迎えてしまう。危機回避の方法はただ一つ。あたるがラムに“好きだ！”と言い、ルパの飼うブタにキノコを食べさせることだった。しかしあたるは告白せぬまま、最後の日が……。前４作と異なり、原作シリーズの最終話「ボーイ・ミーツ・ガール」が忠実にアニメ化されている。原作のファンからは好評を得たが、一方で、アニメ版独自のオリジナリティーを欠落させていることも否定できない。DVDタイトルは「うる星やつら 劇場版　完結篇」。", "1988-02-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("街燈", "街燈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「狂った果実」で石原裕次郎とともに鮮烈なデビューを飾った中平康監督が、永井龍男の原作を月丘夢路主演で映画化した現代風俗もの。定期券が取り持つ縁で知り合った２組の男女のドライな駆け引きが、軽快なテンポで描かれる。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちづる", "ちづる", "", "", "http://image.eiga.k-img.com/images/movie/56191/315x210.jpg?2275", 74, "立教大学心理学部映像身体学科の赤崎正和が卒業制作作品として監督したドキュメンタリー。重い知的障がいと自閉症を抱えた赤崎の妹・千鶴と母親を1年にわたり撮影し続けた。赤崎は、身近な存在でありながら正面から向き合えなかった妹にカメラを通して対面し、家族との新しい関係を築きあげていく。赤崎の指導教員でもあり、ドキュメンタリー「蟻の兵隊」の監督として知られる池谷薫を中心に、現役の立教大生が製作、配給、宣伝など一連の業務を担った。", "2011-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Big Bird Cage", "残虐全裸女収容所", "", "", "http://image.eiga.k-img.com/images/movie/58131/315x210.jpg?4309", 88, "B級映画の巨匠ロジャー・コーマンが製作総指揮を務めた女囚映画「残酷女刑務所」（1971）シリーズ第3作。舞台は罪人の女たちが送り込まれる巨大収容所。残酷で強欲な所長により囚人たちは痛めつけられ、次々と殺されていく……。「ジャッキー・ブラウン」のパム・グリアが主演する、エロス＆バイオレンスアクション。2012年、ロジャー・コーマンの軌跡をたどったドキュメンタリー「コーマン帝国」（11）の公開にあわせて日本初公開。", "2012-04-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Trent's Last Case", "トレント大事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "名探偵トレントが富豪シグズビーの死の真相を暴くミステリー。1920年にリチャード・ガリック監督がすでに映画化していたにもかかわらず、小説を高く評価していたホークスは、これを初のオールトーキーとして映画化しようと意欲的だったが、主演のレイモンド・グリフィスの声が第一次大戦中に吸引した毒ガスの影響でかすれてしまったことと、フォックスが取得した映画化権が無声映画のみに限定されていたことなどの理由で、最終的には無声サウンド版に落ち着いた。", "1929-11-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIG WAVE", "ビッグウェイブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ハワイのノース・ショアやアメリカ西海岸のマリン・スポーツのメッカにカメラを持ち込み、ウィンドサーフィンやサーフィン、ヨットなどを収録したスポーツ・ドキュメンタリー。美しい海の景色と山下達郎サウンドが満喫できるBGV的な一編だ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIST OF HONOR", "レイジング・フィスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "２大マフィア間の抗争で、戦場と化した町。恋人を、そして親友を失った最強のストリートファイター、“フィスト”サリバンは、深い悲しみを胸に、２大組織の壊滅に立ち上がる。サリバンを演じるのは、「フラッシュ・ゴードン」主演の肉体派、S・ジョーンズ。久しぶりにシェイプアップされたボディとアクションを披露してくれる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きさらぎ無双剣", "きさらぎ無双剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "おなじみ“旗本退屈男”の活躍を描く痛快時代劇。将軍家壊滅を狙う尾張藩。この野望に気づいた大岡越前守らは、それを阻止せんと、将軍吉宗の親友で播州三日月城主の森安芸守・竜胴月之介を呼ぶ。だがその一方で、尾張と手を組んでいる老中和泉守が、剣客や浪人を次々と小伝馬町牢内に囚人として送り込み、暴動を起こさせようとしていた。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逆光／A　BACK−LIT　PICTURE", "逆光", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "造船所で働く青年・星明、高級幹部の娘・茵茵の二人は、ある日ふとしたきっかけで知り合い、階級が違うにもかかわらず結婚することとなる。しかし彼らの周囲の男女は、愛だけで結婚できるわけではなかった……。上海ロケの美しさが印象的な作品。", "1982-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALL QUIET ON THE WESTERN FRONT", "西部戦線異状なし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "第一次大戦中、ドイツ軍兵士として参戦して負傷し、戦場と病院を行き来した体験に基づいて書かれたレマルクの世界的ベストセラーの映画化。ドイツの小さな町の学校では戦争の話でもちきりだった。先生は生徒たちに愛国の精神を説き、それにのせられたポールら６人が出征志願する。しかし、現実の戦場は生易しいものではなかった。友人が次々と戦死するなか、白兵戦で敵の若い兵士を殺したポールは、休暇で故郷へ帰るが、暗たんたる気持ちだった。再び戦場へ戻ったが、飛んできた蝶々を捕ろうと体を乗り出した時、敵の銃弾に倒れてしまう……。静かな語り口ながら、戦争の悲惨さ、無意味さが胸に重く伝わってくる反戦映画。", "1930-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二十四の瞳", "二十四の瞳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "小豆島の美しい自然を背景に、女教師と生徒たちの交流を描く感動作。昭和初頭から終戦直後という激動の時代を生きながら、変わることのなかった彼女らの師弟愛を様々なエピソードを交えて描き出す。高峰秀子主演で木下惠介が監督した作品の、実に33年ぶりのリメイク。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　女殺油地獄", "シネマ歌舞伎　女殺油地獄", "", "", "http://image.eiga.k-img.com/images/movie/56391/315x210.jpg?3845", 0, "歌舞伎の舞台公演をスクリーンでデジタル上映する「シネマ歌舞伎」シリーズ第13弾。複雑な家庭環境により荒んだ生活を送る大阪天満の油屋河内屋の息子・与兵衛の哀感や狂気、同業の豊嶋屋七左衛門の女房・お吉への思い、与兵衛の継父・徳兵衛と実母・おさわの情愛を描く。近松門左衛門作の浄瑠璃で、第4期歌舞伎座のさよなら公演として2009年6月に上演された。", "2011-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMITYVILLE: THE EVIL ESCAPES", "悪魔の棲む家・完結編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "実際に起きた超常現象を映画化したシリーズの完結編となるTVムービー。アミティヴィルの屋敷に、悪魔払いのための６人の神父が訪れるところから物語は始まる。監督・脚本のS・スターンは１作目の脚本家。またSFX担当のR・スタンツマンは「ジョーズ」や「ポルターガイスト３」を手掛けている。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pippi Longstocking", "ピッピの新しい冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "スウェーデンが生んだ人気児童文学作家アストリッド・リンドグレーンによる、世界中で愛され続けてきた児童文学の名作を映画化。トミーとアニカの隣家“ごたごた荘”に引っ越してきたのは、赤いおさげにそばかすだらけの風変わりな女の子ピッピ。彼女のお父さんは船長で、しかも南の島の王様。今は世界中を航海しているという。たちまち仲良くなった3人は楽しい毎日を過ごすが、ある日、ピッピの金貨を狙う泥棒がやって来て……。", "1970-01-01", "14,45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ramen Girl", "ラーメンガール", "", "", "http://image.eiga.k-img.com/images/movie/54171/315x210.jpg?3433", 102, "日本で働く恋人と暮らすため東京にやってきたアビーだったが、恋人に振られて傷心。アパートの向かいの小さなラーメン屋で泣き崩れる。その時、店主マエズミがくれた一杯のラーメンに心を打たれたアビーは、ラーメン作りを志し、言葉も通じない中でマエズミに無理矢理弟子入りするが……。東京を舞台に、行き場のないアメリカ人女性と、頑固者のラーメン店主の交流を描いたコメディドラマ。主演はブリタニー・マーフィ、ラーメン店主マエズミ役に西田敏行。", "2009-01-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("網走番外地・吹雪の斗争", "網走番外地・吹雪の斗争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "橘真一は網走刑務所の独房を脱獄するが、部下に裏切られロシアのスパイの汚名を着せられて自殺した、父の仇を探し求める。石井輝男監督による“網走番外地”シリーズはこの作品で終了し、他の３人の監督による新シリーズへと継続される。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I KYNIGHI", "狩人", "", "", "http://ecx.images-amazon.com/images/I/413PXDV70RL._SL210_.jpg", 172, "76年、雪山で狩人たちが死体を見つけた。まだ温かく、血がしたたっているその死体は、30年前の内戦兵士だった……。「旅芸人の記録」の1シーン・1カットの手法をアンゲロプロス監督がさらに推し進め、繰り返す愚かな歴史の姿を全編わずか47カットで描いた“現代史3部作”の第3作。完成当時はまだ「旅芸人の記録」1本しか紹介されていなかった日本では、あまりの斬新さに公開までに10年以上を要した作品。", "1992-11-14", "27,7,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高橋英樹の激流に生きる男", "高橋英樹の激流に生きる男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "昔、誤って人を殺してしまったことからボクサーをやめ、船員になった男・槙。彼はひょんなことから若く美しい女・さかえがマダムをしているナイト・クラブで働くことになる。しかし、その店は、権利を奪おうとする黒岩興行に狙われていた……。赤木圭一郎のための企画であったが、彼が不慮の死を遂げたため高橋英樹が急遽、主演を務めた。吉永・二谷共演のアクション。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("焦げ女、嗤う", "焦げ女、嗤う", "", "", "http://image.eiga.k-img.com/images/movie/56351/315x210.jpg?4170", 54, "結婚を意識していた彼氏にふられた26歳の冬子は、高校時代の元彼・久太と再会し、鍋パーティに誘われる。楽しい仲間たちとつかの間の穏やかな時間を過ごした冬子は、そのまま勢いで久太をベッドに招き、一夜をともにする。しかし、久太に思いを寄せていたシュウコがその現場を目撃。シュウコは翌日から冬子の後をこっそりとつけはじめる。水戸短編映像祭など数々の映画祭で入選した新鋭・瀬川浩志監督が描く、5人の男女がおりなす青春群像劇。", "2011-05-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAY HUNGRY", "ステイ・ハングリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "裕福な一家に育ったクレーグは、一族の企業に入ることを拒み、さびれたヘルス・クラブのオーナーとして自立する。だがクラブの代表者エリックソンは腹黒い奴で、クラブの取り壊しを企んでいた。いまや実力派俳優の大物、J・ブリッジスとオスカー女優S・フィールドが売れない時代に撮られた作品で、ボディビルダー役には、これもまた下積み時代のA・シュワルツェネッガーが登場する。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Simpsons Movie", "ザ・シンプソンズ　MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/34111/315x210.jpg?1449", 87, "87年の初放映以来、ホーマー（父親）、マージ（母親）、バート（長男）、リサ（長女）のシンプソン一家をユーモラスな視点で描いてきた長寿アニメ番組の映画版。今回、放映20周年を記念して初めてのスクリーン進出となった。TVシリーズ同様、アメリカの架空の田舎町スプリングフィールドを舞台に、シンプソン一家のコミカルな日常生活とはちゃめちゃな大騒動がブラックユーモアたっぷりに描かれる。日本語吹き替え版の声優を所ジョージ、和田アキ子、田村淳（ロンドンブーツ1号2号）、ベッキーが担当。", "2007-12-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海の若大将", "海の若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "田沼雄一は京南大学水泳部のエース。スーパーの店員・芦野澄子は恋人だ。彼は航海術を勉強すべく船に乗り込み八丈島へ。そこで島の娘に惚れられ、誤解した澄子は青大将とドライブを決行。日豪対抗水泳大会に出場した雄一は苦戦するが、誤解の晴れた澄子の応援で逆転優勝する。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極東黒社会　DRUG CONNECTION", "極東黒社会　DRUG CONNECTION", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "イタリアの組織を巻き込み、新宿歌舞伎町の裏世界を牛耳る香港マフィア。彼らに仲間を次々と殺された麻薬密輸業者が、奇妙な友情で結ばれたニューヨーク市警の覆面捜査官とともに危険な戦いを挑む。ハリウッドで活躍した日本人俳優、ショー・コスギが捜査官を熱演、役所広司が麻薬の売人役で渋い魅力を発揮したハード・アクション。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男の償ひ・前後編", "男の償ひ・前後編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 166, "若き考古学者・伊狩滋。幼なじみの女を資産家の息子に取られた彼は、伊豆の温泉旅館で出会った娘と結婚。研究に熱中する滋だが、そこに性質の悪い義兄がやってきて……。『主婦の友』連載小説を、小津とのコンビで知られる名脚本家・野田高梧が脚色。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CRAFT", "ザ・クラフト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "家族とともにロサンゼルスへ引っ越して来た霊感少女のサラは、転校した高校で魔術マニアの女の子３人と親しくなる。かねてから黒魔術に興味があった彼女たちは、サラの持つ不思議な力に目を付け、彼女を黒魔術に引き入れる。サラの加入によって、魔術は本物となり、４人は次々と望みをかなえていく。しかしその力が大きくなればなるほど、事態は恐ろしい方向へと進み始める。黒魔術にハマってしまった少女たちの恐怖を、SFXを駆使した映像で表現。特に、クライマックスシーンでの、数万匹の虫や蛇が床や壁を埋め尽くすというグロテスクな演出は圧巻。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホノカアボーイ", "ホノカアボーイ", "", "", "http://image.eiga.k-img.com/images/movie/54241/315x210.jpg?1428", 111, "ハワイ島の小さな町ホノカアで青春時代の数カ月を過ごした吉田玲雄が自らの体験を綴った同名小説を、「ハルフウェイ」の岡田将生主演で映画化。失恋したショックで大学を休学したレオは、かつて恋人と訪れたことのあるホノカアの映画館で映画技師として働くことに。ちょっと風変わりだけど温かいホノカアの住人たちとの交流を通し、レオは少しずつ成長していく。共演には倍賞千恵子、松坂慶子、蒼井優ら豪華キャストが揃う。", "2009-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tatsumi", "TATSUMI", "", "", "http://image.eiga.k-img.com/images/movie/57341/315x210.jpg?3900", 96, "", "2010-01-01", "54", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　TICKLE　IN　THE　HEART", "キングス・オブ・クレズマー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "迫害の歴史の中で放浪のユダヤ民族が育んできた、クレズマー・ミュージックと呼ばれる伝統音楽の世界に迫るドキュメンタリー。本作がスポットを当てるのは“キング・オブ・クレズマー”ことマックス、ウィリー、ジュリーのエプスティン兄弟。80歳に至っても現役アーティストとして活躍した彼らの演奏ぶりやインタビューを通じて、偉大な民族音楽の素晴らしさが伝えられる。", "1996-01-01", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミックスマシン", "ミックスマシン", "", "", "http://ec1.images-amazon.com/images/G/09/nav2/dp/no-image-no-ciu.gif", 0, "19歳の専門学校生・斉藤可奈子が監督・脚本を手がけ、理想と現実のギャップに直面した思春期の若者たちの奮闘を描いた青春ドラマ。家でも学校でも本当の自分を押し殺して暮らしている少女タエ。ある日彼女は、病気の母を救うための発明品づくりに熱中する少年ミツルと出会う。互いに共通する部分を感じたタエとミツルは次第に惹かれあっていき、寂しさを紛らわせるための発明品を一緒につくりはじめるが……。", "2006-11-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青葉繁れる", "青葉繁れる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "井上ひさしの半自伝的小説を岡本喜八が監督した明朗な青春映画。1950年代の話を、当時の視点でアレンジしている。女を強姦することばかり考えている名門校の落ちこぼれ４人組の物語だが、印象は骨っぽく、カラッと明るい。主人公たちが純愛を捧げ、現実からスクリーンに越境するヒロインのモデルは若尾文子。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("方世玉", "レジェンド・オブ・フラッシュ・ファイター　格闘飛龍　方世玉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "中国の実在したカンフー・ヒーロー、方世玉（フォン・サイヨ）役にJ・リー(リー・リンチェイ)が挑んだアクション。染物屋の一人息子サイヨは、町の顔として知られる武術の達人。漢民族の秘密結社、紅花会で働いていた父を誘拐された彼は、皇帝が放った刺客を相手に奮闘し、やがて漢民族再興の大義に目覚めていく。スピード感あふれる武術アクションに、コミカルな騒動を交えた娯楽作。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KELID", "鍵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "ある日、４歳のアミール少年は、出かけようとする母親から留守番と赤ん坊の子守りを頼まれる。しばらくすると、台所からは料理の焦げる匂いがし、赤ん坊は泣き叫び出した。しかし、小さいアミールは水道に手が届かず、ガスの消し方もわからない。そのうち、焦げ臭さと赤ん坊の泣き声に気付いたアパートの人たちがやって来るが、母親が鍵がかけて行ったために部屋に入ることができない。ドアの中と外で大騒ぎとなるのだったが……。A・キアロスタミが書いたシンプルなストーリーを、スリルに満ちた作品に仕上げている。日本では1996年に初公開された。", "1987-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("American Pie : The Wedding", "アメリカン・パイ3：ウェディング大作戦", "", "", "http://ecx.images-amazon.com/images/I/51Y3HHK9DEL._SL210_.jpg", 103, "全米大ヒット青春コメディ「アメリカン・パイ」シリーズの第3弾。大学卒業を目前に控えたジムは、付きあって3年になる恋人ミシェルとの結婚を決意。ミシェルの両親に気に入ってもらおうと必死のジムだったが、高校時代からの悪友スティフラーたちがバチェラー・パーティを企画して大暴走。さらに悪友たちは、ミシェルの妹を気に入って取りあいまで始めてしまい……。主演は「僕のニューヨーク・ライフ」のジェイソン・ビッグス。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WARLOCKIII", "ワーロック　ジ・エンド・オブ・ミレニアム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "世界征服をもくろむダークマスター、ワーロックの陰謀と戦いを描くSFシリーズの第３作。女子大生のクリスは大学の仲間を誘って古い屋敷で週末を過ごすことにする。だが、その前に現れたのはクリスの一族と350年前に死闘を繰り返した宿敵ワーロックだった。彼の出現で、秘められていたサイキック・パワーが覚醒したクリスは一人立ち向かっていく……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("右門捕物帖 蛇の目傘の女", "右門捕物帖 蛇の目傘の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "大友柳太朗による“右門捕物帖”第７作。ある土砂降りの夜、土蔵破りの下手人の友吉が牢役人を殺して脱獄した。友吉が土蔵から盗んだ千両箱の隠し場所を知る唯一の人間であると右門は睨み、捜査を開始する。ところが、友吉は蛇の目傘の女お仙にかくまわれたあと、何者かに殺されてしまう……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fly Me to the Moon 3D", "ナットのスペースアドベンチャー3D", "", "", "http://image.eiga.k-img.com/images/movie/54301/315x210.jpg?3163", 85, "アメリカ・フロリダ州の宇宙センターでは、月へ向かって飛び立つロケットが発射の時を待っていた。発射台近くの廃品置き場に暮らすハエの子供ナットは、祖父から大冒険の話を聞かされて育ち、いつか祖父のような大冒険をしたいと夢見ていた。そんなある日、ナットは親友のIQとスクーターと共に月を目指すことを決意し、ロケットに潜り込む。全編3D上映で、主人公ナットの吹替えに「ALWAYS　三丁目の夕日」の堀北真希。", "2009-03-28", "37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("痴人の愛", "痴人の愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "谷崎潤一郎の同名原作を木村恵吾監督が再映画化。堅物の会社員が、偶然知り会った少女ナオミを思いどおりの女に育て上げるが、結局は逆に支配されるまでの官能の物語を描く。京マチ子が主演した1949年の第１回映画化では、家を出たナオミが改心して譲二のもとへ帰るという結末であったが、本作はほぼ原作通りの物語展開となっている。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　怪傑ナガネギマンとやきそばパンマン", "それいけ！アンパンマン　怪傑ナガネギマンとやきそばパンマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 20, "やなせたかし原作の人気アニメーション劇場版第13作「それいけ！アンパンマン　ゴミラの星」と同時上映の短編。", "2001-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カタルシス", "カタルシス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "殺人を犯したティーンの息子を抱える家族の贖罪と再生の物語。14歳の時に連続殺人事件を起こした少年が仮釈放を機に、家族とともに母の実家の離島へと移住。しかし家族の心はバラバラになったままで、島民からも白い目を向けられる。そんな一家の苦境を見つめつつ、家族の再生と希望を浮き彫りに。救いを求める姿が胸に響く、歯応えのある人間ドラマだ。少女連続殺人事件の犯人として逮捕された14歳の少年、青紀。３年後、彼の少年院からの仮退院を機に、母の永遠子は家族とともに実家の離島に帰ることに。しかし、新天地でも青紀の噂は広まり、彼は家族にも心を閉ざし続け……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("感染列島", "感染列島", "", "", "http://image.eiga.k-img.com/images/movie/53451/315x210.jpg?4454", 138, "「ユダ」「フライング☆ラビッツ」の瀬々敬久監督がウイルスの脅威を描くパニック映画。主演に妻夫木聡と壇れい、共演に国仲涼子、爆笑問題の田中裕二、池脇千鶴、カンニング竹山、佐藤浩一、藤竜也ほか。市立病院で働く救命救急医の松岡のもとに、新型インフルエンザに感染したと思われる急患が運び込まれてくる。しかしあらゆるワクチンや治療が効果を持たず、患者は死亡。やがて医療スタッフや入院患者にも感染が発覚し、病院はパニックに陥る……。", "2009-01-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歓待", "歓待", "", "", "http://image.eiga.k-img.com/images/movie/56061/315x210.jpg?2664", 96, "東京下町の一角で印刷屋を営みながら平穏な日常を送っていた小林家に、流れ者の男・加川が突然訪れ、そのまま居座ってしまう。突然の来訪者に翻弄される家族の姿をユーモラスに描き、第23回東京国際映画祭・日本映画「ある視点」部門を受賞した。キム・ギドク監督の「絶対の愛」にも出演した杉野希妃ほか、山内健司ら平田オリザ率いる青年団所属俳優が出演。", "2011-04-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道社長", "極道社長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "中島貞夫監督、梅宮辰夫主演によるコメディ・アクション。３人の詐欺師が協力してポルノ・キャバレーを開業するや大繁盛。しかしたちまち警察や暴力団の横ヤリが入り、またまた奈落の底へ逆戻り。当時、“ピラニア軍団”の一員としてのし上がってきた、室田日出男と川谷拓三が大熱演。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALVAIRE", "変態村", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ベルギーの新人監督ファブリス・ドゥ・ヴェルツが、キリストの受難をモチーフに人間の狂気や欲望を描いた衝撃作。地図にも載らない小さな村に迷い込んだ歌手が体験する恐怖を、幻想的かつ暴力的な過激演出で炙り出していく怪作だ。売れないキャバレー・シンガーのマルクは、次のライブに向かって移動中に車が故障し、人里離れた山の中で立ち往生してしまう。そこに現れた孤独な老人、バルテルに助けられるが……。", "2006-03-18", "2,37,58", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Road to Glory", "永遠の戦場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1916年、戦火のフランス。従軍看護婦のモニクはラロッシュ大尉と婚約していたが、空襲の最中にデネ中尉と出会う。前線では、有刺鉄線でうめき苦しむ二人の兵士を前に、デネは勇敢にも飛び出して一人を救い、ラロッシュは死にきれないもう一人の兵士を射殺したことから、2人は対立する。モニクとデネ中尉は惹かれ合い……。2人の勇敢な将校と従軍看護婦の物語。", "1937-01-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こわい童謡　裏の章", "こわい童謡　裏の章", "", "", "http://image.eiga.k-img.com/images/movie/34091/315x210.jpg?3927", 79, "童謡をモチーフにしたサスペンス・ホラー「こわい童謡　表の章」の後編。東京郊外の名門校・聖蘭女学院の音楽室で、合唱部員12人が惨殺された事件から5年後。廃校になった学校から童謡の歌声が聞こえるという噂を解明するため、テレビの取材班が現場を訪れた。同行した音響分析員の響子は独自に事件の調査を開始するが、スタッフに次々と異変が起こりはじめ……。ヒロインを演じるのは本作が映画初主演となるグラビア出身タレントの安めぐみ。", "2007-07-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARTISTS AND MODELS", "画家とモデル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "D・マーティン＆J・ルイスの“底抜け”名コンビで描いたスパイ・コメディの一編。売れない画家と冒険マンガに夢中の同居人の二人が、寝言の冒険物語をストーリー化して出版社に売り込んだところ、偶然に極秘化学記号と同じ名称のロケット燃料の描写があったため、スパイが潜入しての大騒ぎとなる。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POT BOUILLE", "奥様ご用心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "19世紀後半のパリを舞台にしたE・ゾラの小説が原作。田舎からパリに出てきた野心家の青年オクターブは、オートクチュールの店に勤めるかたわらプレイボーイぶりを発揮。決闘ざたになったりするが、パリで最初の百貨店を作り成功する。", "1957-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I CLOWNS", "フェリーニの道化師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "フェリーニがイタリア、RAIのために制作したTV用映画の傑作で、日本では劇場公開された。少年時代からサーカスの世界に憧れを抱き続けたフェリーニが、真正面からサーカスの道化芸に取り組んだ作品。フェリーニと彼のスタッフは、道化師を求めてイタリアとフランスに旅をする。だが道化師たちの姿は、どこにも見られない。取材の途中、フェリーニは、女優のエクバーグや喜劇俳優エテックス、チャップリンの娘ジョゼフィンらと出会う。道化師たちが老い、道化の芸が失われつつある今、フェリーニは絢爛たる道化のショーを再現してみせる。ラストで二人の道化師がトランペットを吹きながら闇の中へ消えていくシーンは感動的。", "1970-01-01", "4,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前医院", "喜劇・駅前医院", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "私鉄沿線・N駅の商店街のはずれに森田医院がある。若先生と看護婦たちは慰安旅行中で、老先生が留守番をしている。そこへ巡査が若い娘をつれてやって来た。昨夜暴漢に襲われたという。娘がその男にかみついた鼻の傷だけを頼りに捜査に乗り出し、表通りにいたマスクをつけた男が怪しいとにらんだ巡査。街のチンピラ・次郎の助けを借りて逮捕したが、この犯人が次郎の親分の兄弟分である組長の息子だったから大変……。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("軍旗はためく下に", "軍旗はためく下に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "結城昌治の直木賞受賞作を、のちに“仁義なき戦い”シリーズを撮る深作欣二が自費を投入して描いた作品。ストーリーは後年、原一男監督のドキュメンタリー「ゆきゆきて、神軍」の中でも取り上げられた、第二次世界大戦末期のニューギニア戦線での飢餓を主題に進行。新藤兼人の書いたシナリオは原作に従って、戦後、戦地で銃殺された夫の死について、その未亡人が生き残った人々を訪れて真相を聞き出そうとする構成となっている。だが会う人ごとに言うことが食い違い、そのなかで人肉を食べた事実、軍隊のヒエラルキーのなかでの上官の卑劣さが浮かび上がっていく。謎解きにも似た展開の底に、戦争の凄惨さが強烈に焼きついてくる。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("元禄忠臣蔵・後編", "元禄忠臣蔵・後編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "前編に続き、本懐を遂げた四十七士の自害までを描く。物語も佳境に入り、ワン・シーン＝ワン・ショットを徹底させた溝口演出はさらに重厚さを増す。戦線の拡大で物資が窮乏するなか、厖大な製作費を投入したこの作品は、破格の大作として完成を見た。前編に続き、新藤兼人がセット建築を担当している。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダーストロンガー", "仮面ライダーストロンガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 20, "悪の組織ブラックサタンに立ちむかう仮面ライダーストロンガーの活躍を描いた特撮テレビドラマ「仮面ライダーストロンガー」の劇場版。ブラックサタンの奇械人ワニーダが鎌倉のドライブインを占拠し、訪れた人々を奴隷人間にしてしまった。事態を察知したストロンガーと電波人間タックルは、人々を救うべくワニーダに戦いを挑むが……。テレビ版第7話「ライダー大逆転!!」の短縮版で、「東映まんがまつり」の1編として公開された。", "1975-07-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魔法のiらんど　幼なじみ", "魔法のiらんど　幼なじみ", "", "", "http://ecx.images-amazon.com/images/I/51H-4va3khL._SL210_.jpg", 95, "人気携帯サイト「魔法のiらんど」で配信された同名小説を原作に、幼なじみの男女が織りなす純愛を描いた青春ラブストーリー。1つ年上の拓也を追って旭高校に入学した遥。幼なじみの2人は、互いに思いを寄せあいながらもそばにいるだけで満足してきた。ある日、そんな2人の関係を揺るがす出来事が起こり……。「ジャージの二人」の田中あさみとテレビドラマ「風魔の小次郎」の村井良大が主人公カップルを爽やかに演じる。", "2008-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOURNAMENT", "TOURNAMENT", "", "", "http://image.eiga.k-img.com/images/movie/57821/315x210.jpg?3938", 45, "「ハイキック・ガール！」「KG カラテガール」の西冬彦監督が手がけた異種格闘技アクション。空手、柔術、忍術、剣術、軍隊格闘技など、さまざまなジャンルの格闘家たちが集い、ルールや審判、台本や演出もない状態で「最強の格闘技は何か？」を決めるため本気の戦いを繰り広げる。全日本空手道選手権王者の杉澤一郎、空手家からK1ファイターに転向した大渡博之、実戦武術の倉本塾師範・児玉秀人、イギリス人空手家リチャード・ヘセルトンらが出演。", "2012-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("御用牙", "御用牙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "シリーズ第１作にあたるこの作品は、すでに“子連れ狼”シリーズで小池一夫作品を映画化している三隅研次が監督し、シリーズ中最高の迫力をもった作品に仕上げた。島破りにより逃げ出した４人を捕えるため江戸中で無宿人狩りが行われた。噂では重罪人の人斬り屋・三途の竿兵衛も江戸に潜入しているという。竿兵衛の情婦・お美乃が上役目付の妾と知った半蔵は、お美乃を捕らえ、天井からつるして“座禅ころがし”なる責めを加える。お美乃の白状により、大奥にまで広がる悪の根源を叩きつぶすべく、半蔵は身の危険も顧みず、男子禁制の大奥に乗り込んでいくのだった……。ビデオは90分。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El milagro de P. Tinto", "ミラクル・ペティント", "", "", "http://image.eiga.k-img.com/images/movie/1831/315x210.jpg?3075", 106, "小さい頃からいつだってペティントの夢はただひとつ、子宝に恵まれた大家族のお父さんになること。そしてとうとう幼なじみのオリビアとゴールイン、2人はコウノトリが赤ちゃんを運んで来てくれるのを待つのだった。それなのに……。年月が流れても、子供のできる気配は全くナシ。だって2人は“タラリン、タラリン”という呪文で赤ちゃんがができる、という話を子供の時に聞いて以来、それを信じたまま老人になってしまったのだから……。", "2000-06-10", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Riding High", "恋は青空の下", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "1934年に製作された「其の夜の真心」を、フランク・キャプラ監督がセルフ・リメイクしたミュージカル作品。ヒギンズ財閥の長女と婚約して製紙会社の社長となったダンは、競馬への情熱を捨てきれず、愛馬を連れて家を飛び出す。手持ちの資金をすべてつぎ込み、愛馬をダービーに出場させようとしていた彼の元へ、以前から密かに彼に想いを寄せていたヒギンズ家の末娘アリスが現れ……。主人公ダンをビング・クロスビーが好演。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN A LONELY PLACE", "孤独な場所で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "破滅型の脚本家と女優の卵との、愛と破局を描いたフィルム・ノワールの傑作。ハリウッドの高級クラブで働く女性が何者かに殺され、容疑者として脚本家ディクソン・スティールが拘束される。彼のアリバイを証明したのは隣人の若手女優ローレル。これをきっかけに二人は仲を深め、愛し合うようになる。が、ディクソンに対する警察の追求の手は強まっていき、やがてローレルも彼を疑いはじめ、孤独にさいなまれたディクソンは酒と暴力に溺れていく……。運命にほんろうされる男の憂いをH・ボガートが切々と演じ見事。また当時N・レイ夫人だったG・グレアムも揺れ動く女性を繊細に演じた。ちなみにレイとグレアムは撮影終了後、映画と同様に破局を迎えている。日本では1996年に劇場公開。DVDは「フィルム・ノワール コレクション(1)」に収録。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GANG RELATED", "ギャングシティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「ラッシュアワー」の脚本家J・コーフによるクライム・サスペンス。これが遺作となった人気ラッパー、T・シャクールと個性派J・ベルーシが悪徳刑事コンビに扮し、裏切りと犯罪が渦巻く危険なゲームを展開する。殺人課の刑事ダビンチとロドリゲスは、捜査で手に入れたドラッグを売って小銭を稼いでいる悪徳刑事。ある日、彼らは潜入捜査官を殺してしまい大ピンチに。早速、証拠隠滅に奔走するが、事態はますます悪化していき……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FIGHTING TEMPTATIONS", "ファイティング・テンプテーションズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "寄せ集めで結成された聖歌隊がゴスペル大会で優勝を目指すハートフル・コメディ。聖歌隊を指揮するハメになる主人公にキューバ・グッディングJr.、メンバーにスカウトされるヒロインに映画出演2作目となるビヨンセ・ノウルズ。パワフルかつ軽快なパフォーマンスにワクワクさせられる。広告代理店に勤める男が叔母の遺産を相続できることになる。ところがその条件は、聖歌隊を結成し、優勝に導くことだった……。", "2005-02-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Antoine et Antoinette", "幸福の設計", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ジャック・ベッケル監督の「パリ下町3部作」と呼ばれるシリーズの第1弾。パリ下町のアパルトマンで暮らすアントワーヌとその妻アントワネット。貧しいながらも幸せな生活を送っていた彼らだったが、ある日アントワネットが何気なく購入した宝くじが80万フランもの大当たり。ところがアントワーヌが賞金を受け取りに行く途中で宝くじを落としてしまい……。パリの下町に生きる人々の姿を生き生きと描いたヒューマンドラマ。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE VAMPIRE LOVERS", "バンパイア・ラヴァーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ホラー映画の名門、ハマー・プロが1970年代に作り上げた“吸血鬼カーミラ”映画。永遠の吸血美女の毒牙が次々と若き娘たちを襲う……。原作は怪奇小説の大御所レ・ファニュ。DVDは「ハマー・フィルム　怪奇コレクション　DVD−SPECIAL　吸血エロス編」に収録。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IMMORTAL BELOVED", "不滅の恋 ベートーヴェン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "偉大な作曲家、ベートーヴェン。彼が遺書に残した“不滅の恋人”とは誰かを探るロマンティック・ミステリー。1827年のウィーン。ベートーヴェンの死後間もなく、一通の遺書が発見される。そこには彼のすべての楽譜と財産を“不滅の恋人”に捧げると記されていた。莫大な財産をあてにする親族を説き伏せ、その恋人探しに乗り出したのが、ベートーヴェンの長年の弟子であり親友であったアントン・シンドラー。彼は遺品の中にあった３通の手紙を手がかりとした。シンドラーの熱心な調査は、偉大な作曲家の心のベールを一枚ずつはがしていく。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おばあちゃん女の子", "おばあちゃん女の子", "", "", "http://image.eiga.k-img.com/images/movie/57111/315x210.jpg?3140", 31, "「ウルトラミラクルラブストーリー」「ジャーマン＋雨」の横浜聡子監督が、最低限のスタッフと予算で撮り上げた夫婦愛を描く短編。どこかのアパートに住む夫婦たかしとはつえ。たかしはいつも通りに仕事へ出かけ、はつえは家事をしているが、はつえのお腹の中でいつもと少し違うことが起こっていた。はつえ役に「ジャーマン＋雨」の野嵜好美、たかし役に「ウルトラミラクルラブストーリー」の宇野祥平。", "2011-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("For the Boys", "フォー・ザ・ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "第二次世界大戦からベトナム戦争、朝鮮戦争と、50年間にもわたって戦地の慰問ショーを続けていったエディとディクシーのコンビを描くミュージカル大河ドラマ。ディクシーを演じたB・ミドラーのステージでのパフォーマーぶりは圧倒的。彼女が歌うシーンがうまく配分されていて長尺ながら中だるみしない。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Return of the Living Dead : Necropolis", "バタリアン4", "", "", "http://image.eiga.k-img.com/images/movie/33511/315x210.jpg?4534", 90, "1985年に発表され、世界中で大旋風を巻き起こした「バタリアン」シリーズの第4弾。大企業ハイブラテック社に拉致された友人を救うべく、仲間と共にその研究所に侵入したジュリアン。彼らを待ち受けていたのは、化学兵器“トライオキシン5”によって作り出されたゾンビ軍団だった……。前作「バタリアン・リターンズ」から13年の時を経て制作された本作のメガホンを取るのは、「スパイダーパニック！」のエロリー・エルカイェム監督。", "2006-07-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バス停より愛をこめて", "バス停より愛をこめて", "", "", "http://image.eiga.k-img.com/images/movie/57371/315x210.jpg?4254", 14, "芸能事務所イトーカンパニー所属の俳優たちが出演するショートムービーをネット配信している「イトーチューブ」で公開された作品を映画館で上映する「イトーチューブ Shrot Movie Colleciton」の1本。「仮面ライダー」でショッカー役を演じていた役者が、ライダー役の俳優を殴って撮影現場を飛び出してしまう。相方の役者と2人、ショッカー姿でバス停にたたずむ男の胸の内は……。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAGNIFICENT OBSESSION", "心のともしび", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「風と共に散る」「愛する時と死する時」などでハリウッドを代表するメロドラマ監督として知られる、D・サーク監督作。競争用のボートを転覆させ、近くに住む医師の救命器のおかげで一命をとりとめた裕福な道楽息子のメリック。だが、救命器を彼に貸したため、そのとき発作を起こした医師は帰らぬ人となってしまう。それを知ったメリックは酒浸りの生活を送るが、ある日、生前の医師を知る画家に出会ったことから、医学の道を志すようになる。そして、医師の妻であったヘレンに釈明をしようとするのだが、彼女は彼から逃れたいあまり、走って来た車と接触事故を起こし、失明してしまう……。一連のサーク作品に主演しているR・ハドソンが道楽息子を好演。物語の大きな振幅のなかで、サークらしい濃密なメロドラマが綴られていく。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関の彌太ッぺ", "関の彌太ッぺ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ふとしたきっかけから、孤児のお小夜を旅篭“沢井屋”に届け、名も告げず去っていった旅人・関の弥太っぺは、10年後、お小夜一家が彼を命の恩人として探していると知らされるが、ヤクザ渡世の身をはばかって立ち寄ろうともしない。しかし、同じヤクザ仲間の箱田の森介が、昔の恩人と偽って沢井屋に乗り込み、お小夜を苦しめていると知るや、素性を隠してお小夜の前に現れる……。ヤクザ渡世に生きてきた男の苦渋と心の優しさを、悲しく美しくうたい上げた作品。特に、弥太郎とお小夜が運命的な再会をする場面がいい。今を盛りと咲きほこる白いむくげの花を垣根にして、ヤクザ家業で傷だらけとなり、変わりはてた弥太郎が、美しく成人したお小夜に“お小夜さん、この娑婆にゃあ、悲しいこと辛えことがたくさんある。だが、忘れるこった。忘れて日が暮れりゃ、あしたになる”と10年前と変わらぬ言葉をかけ、さっと立ち去っていく。ハッと気づいたお小夜が“旅人さん”と呼び止めようとする場面では、観る者の瞳を潤ますに違いない。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDERGROUND", "アンダー・グラウンド 狼たちの街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "一匹狼の刑事を主人公にしたサスペンス・アクション。暴力的な捜査法が災いして、署内でも問題児のニューヨーク市警刑事ジョン。彼は、元恋人の女性記者キムから、地下鉄内連続殺人事件の手がかりをつかみ、捜査を開始。凶悪犯ボリスを頭とする犯罪集団に行き着くのだが……。地下の闇を生かした映像に注目。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Death and the Compass", "デス＆コンパス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "アルゼンチンの作家ホルヘ・ルイス・ボルヘスによる難解な短編小説「死とコンパス」を、「シド・アンド・ナンシー」の鬼才アレックス・コックス監督が見事に映像化したSFサスペンス作品。近未来のメガロポリス。犯罪のはびこるこの街で、ユダヤ人の大学教授が殺害されるという事件が発生。捜査に乗り出したランロット警部は、タイプライターに残された謎のメッセージを手掛かりに犯人を追う。やがて第2、第3の殺人が起こり……。", "", "19,1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Was a Male War Bride", "僕は戦争花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "西ドイツの米軍占領地区で物資購入委員をしているフランス人大尉アンリは、通訳に配属された米軍士官キャサリンと組んで仕事をしていた。喧嘩友達の関係からいつしか互いへの恋心を認めた2人は結婚を決意する。外国女性と結婚した米軍兵士については「戦争花嫁」として妻を本国に連れ帰る規定があったが、婦人士官が外国人を夫にした場合は何の規則もなく、アンリはやむなく「男性の戦争花嫁」なる資格でアメリカ入国の許可を貰ったが……。出演者たちの急病などで撮影が度々中断されるなど製作はトラブル続きだったが、全米年間第3位のヒットとなった。", "1950-12-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NANA", "NANA", "", "", "http://image.eiga.k-img.com/images/movie/1131/315x210.jpg?4387", 114, "累計発行部数2300万部を超える同名人気コミックを、人気歌手・中島美嘉と「EUREKA」などで注目の若手女優・宮崎あおいの共演で描く話題作。偶然、出会った同じ名前で同い年の2人。不幸な境遇で育ち、ビッグになることを夢見るパンクバンドのボーカル・ナナと、普通の家庭で育って恋が最優先の奈々。正反対の性格に見える20歳の2人は同居生活を始め、さまざまな体験をしていく。監督は「とらばいゆ」「約三十の嘘」の大谷健太郎。", "2005-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE II ： THE SECOND STORY", "ガバリン２ タイムトラぶラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「ガバリン」の続編だが、内容的には関連がない。ジョシーが遺産相続で受け継いだ館に、先祖のおじいさんが水晶を抱いて、ミイラのままよみがえった。水晶の不思議な力は館の中に異次元空間を出現させ、石器人が出るやらカウボーイが出るやら……。大騒ぎSFコメディ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("echoes〈エコーズ〉", "echoes〈エコーズ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "ニューヨーク在住の映画作家、舩橋淳の長編デビュー作。自分の居場所を求めてさまよう若い女性の姿を追ったロード・ムービー。ニューヨーク、イースト・ヴィレッジ。仲間といても孤独をぬぐいされないレスリーは、あるパーティーで盗んだ財布から一枚の写真を発見する。そこには幼い頃の自分と両親、そして見知らぬ少女が写っていた。家族の隠された過去を探るため、レスリーは故郷ヴァージ二アへと向かう。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("清水港の名物男・遠州森の石松", "清水港の名物男・遠州森の石松", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "村上元三のベストセラー『次郎長三国志』の中の森の石松の金比羅参りのエピソードを、マキノ雅広監督が中村錦之助主演で映画化した時代劇の快作。森の石松は次郎長の命を受けて金比羅参りに旅立つが、途中で夕顔という女郎に会い一目惚れ。夫婦になることを誓うが、卑怯な都鳥一家の闇討ちに遭う。マキノ雅広監督の大ヒット・シリーズ“次郎長三国志”の「海道一の暴れん坊」の再映画化である。前作で森繁久彌が熱演した森の石松を、今回は中村錦之助が体当たりで熱演し見事な成果を収め、アイドル・スターから演技派スターへと鮮やかな脱皮を果たした。マキノ雅広の職人芸を味わうには最適な作品。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("濡れ燕 くれない権八", "濡れ燕 くれない権八", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "とある因縁から姫路藩剣道指南役の棟方鉄心とその弟子・辰之進を斬った紅権八は鉄心の娘・比呂恵と辰之進の弟たちから仇として狙われることに。争いを好まぬ権八は江戸まで逃げ、侠客の長兵衛の世話になる。だがそんな彼を姫路藩江戸屋敷の侍が襲い……。権八と比呂恵の恋を絡めた娯楽時代劇。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版テニスの王子様　英国式庭球城決戦！", "劇場版テニスの王子様　英国式庭球城決戦！", "", "", "http://image.eiga.k-img.com/images/movie/56071/315x210.jpg?4341", 87, "原作漫画は1999年から2008年まで「週刊少年ジャンプ」（集英社刊）で連載。TVアニメーションやミュージカル、実写映画にもなった人気作「テニスの王子様」のアニメ版10周年を記念した劇場版。英ウィンブルドンで開催される世界の強豪ジュニア選手を集めた大会に、青学、氷帝、立海、四天宝寺のテニス部の面々が日本代表として招かれる。しかし大会の裏では不穏な出来事が次々と発生。世界の有名選手たちが襲われていく。やがて日本選手たちにも魔の手が迫り、リョーマらは古城を舞台にした試合にのぞむことになる。", "2011-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おもちゃ", "おもちゃ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "鬼才、深作欣二が「忠臣蔵外伝　四谷怪談」から４年の沈黙を破って世に放った女性ドラマ。舞台は昭和30年代の京都。17歳の時子は、芸妓置屋の雑用係をしていた。彼女が舞妓として水上げされる日も近づき、置屋はその資金繰りのために大わらわ。色と金が絡む、そんな特異な世界を、時子の視点からストレートに描写。華やかさと同時に醜い世界を受け入れていく、ヒロインの心の成長がみずみずしく伝わってくる。カメラはほぼ固定で、深作作品にしては落ち着いた作り。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("燃ゆる月", "燃ゆる月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "「シュリ」の“カン・ジェギュ・フィルム”が贈る壮大な歴史アクション。遥か昔、対立する部族の間に産み落とされた少女。運命にほんろうされる少女と勇者たちの壮絶な愛と戦いを描いた物語。プロデュサーを務めるカン・ジェギュ監督の出世作「銀杏のベッド」の続編であり、当時の韓国映画として破格の制作費、９ヵ月にも及ぶ撮影期間などが話題を呼んだ感動作。", "2000-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　COME´DIE‐FRANCAISE　OU　L’AMOUR　JOUE´", "コメディ・フランセーズ／　演じられた愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 223, "ドキュメンタリー映画の鬼才、F・ワイズマンが、フランスの国立劇団“コメディ・フランセーズ”にカメラを向けた大作。『ドン・ジュアン』など４つの“愛”に関する舞台のリハーサル風景と本公演の模様を中心に、劇団経営に関する舞台裏など多角的な視点で、その全貌に迫っている。臨場感あふれる描写もさることながら、シリアスな局面にも穏やかなユーモアが漂うニュアンス豊かな構成が印象的。肩のこらない３時間43分に仕上がっている。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANIEL BOONE", "大陸の快男児", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "西部開拓史上に名を残す英雄ダニエル・ブーンの半生を描いたウエスタン。ブーンに扮するのはJ・フォード作品などでおなじみの、当時全盛期だったG・オブライエン。仇役の無法者には「駅馬車」などのJ・キャラダイン。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("純", "純", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "純情な痴漢青年の日常を活写して、若さがもたらす苦悶と夢想をビビッドに伝える佳編。痴漢行為が毎日の日課であるように自然な細部描写で捉えられ、ドキュメンタリー・タッチの粗い画面から青年の息づかいが伝わってくる。東映で５年間、助監督を務めた横山博人監督のデビュー作。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大番", "大番", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "四国の寒村に貧農の子として生まれた赤羽丑之助は町の富豪・森家の令嬢・可奈子の美しさに呆然自失となる。やがて上京した丑之助は株屋に小僧として住み込む。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イノセンス", "イノセンス", "", "", "http://image.eiga.k-img.com/images/movie/1091/315x210.jpg?2690", 99, "人間の脳が情報ネットワークに直接接続され、身体の機械化が進む世界で、少女型の愛玩用アンドロイドによる持ち主の殺害事件が多発。事件を担当することになった政府直属機関、公安九課の刑事バトーは、犯行直後の少女型アンドロイドが「助けて」という言葉を残して自壊するのを目撃して、違法アンドロイドの製造を疑い、製造会社ロクス・ソルス社に捜査に向かう。押井守監督「GHOST IN THE SHELL 攻殻機動隊」の3年後が舞台。", "2004-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NADIE　HABLARA，DE　NOSOTRAS　CUANDO　HAYAMOS　MUERTO", "死んでしまったら　私のことなんか誰も話さない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "サン・セバスチャン国際映画祭で２部門、ゴヤ賞で８部門を受賞。人生に挫折した一人の女性の再起のドラマを、バイオレンス・シーンを交えて描く痛快作だ。闘牛士の夫が事故により意識不明になったことで絶望したグロリアは、アルコールに溺れ、失意のドン底に落ちていく。そんなある日、麻薬組織と警察の銃撃戦に巻き込まれた彼女は、瀕死の刑事から一冊の手帳を託される。そこには組織の裏金の隠し場所が記されていた。就職もできず経済的に追い込まれていた彼女は、金の横取りをたくらむ。不幸から抜け出すために大胆な行動に出る主人公を、ペドロ・アルモドヴァル作品で知られるV・アブリルが好演。", "1995-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El espiritu de la colmena", "ミツバチのささやき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "スペイン内戦が終結した翌年の1940年、6歳の少女アナが負傷した兵士に出会う。彼を映画で見たフランケンシュタインだと信じ込んだアナは、まっすぐな瞳で黙ってりんごを差し出した……。主演の少女、アナ・トレントが世界中を熱狂させた、名匠ビクトル・エリセの長編第1作。", "1985-02-09", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Sun", "太陽", "", "", "http://image.eiga.k-img.com/images/movie/1681/315x210.jpg?2659", 115, "「精神の声」「エルミタージュ幻想」などで知られるロシアの鬼才アレクサンドル・ソクーロフ監督が、「モレク神」（ヒトラー）、「牡牛座」（スターリン）に次ぐ、20世紀の権力者を描く4部作の3作目として製作した問題作。神と崇められていた昭和天皇が、終戦を期に「人間宣言」するまでの心の葛藤を描く。昭和天皇にイッセー尾形、皇后に桃井かおり、侍従長に佐野史郎が扮している。", "2006-08-05", "26,24,4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOUL PLAY", "ファール・プレイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "「知りすぎていた男」をベースに、様々なヒッチコック作品を詰め込んだサスペンスタッチの傑作コメディ。グロリアは運転中にヒッチハイクのスコットを乗せ、別れる時に一本のフィルムを預かる。約束の映画館に行くと、スコットは殺されていた。家に帰る途中、グロリアは黒い車に襲われる。翌日には、彼女に家にまでフィルムを要求する男が現れるが、何者かに殺されてしまう。グロリアの親友で刑事のトニーが捜査に乗り出し、スコットが政府の秘密調査員で、ある暗殺事件を追っていたことがわかる。二人は事件の鍵を握る大司教に面会に行くが、大司教こそ怪しく、グロリアは命を狙われることになる。そして彼女は、犯人たちがアメリカ滞在中のローマ法王の暗殺を計画していることを知るのだった。コメディエンヌ、G・ホーンがグロリアを明るく演じている。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女殺油地獄（2009）", "女殺油地獄（2009）", "", "", "http://image.eiga.k-img.com/images/movie/54621/315x210.jpg?1669", 86, "これまでも何度か映像化されてきた近松門左衛門の浄瑠璃「女殺油地獄」を、俳優・演出家としても活躍する坂上忍が映画化。河内屋の跡取り・与兵衛は、妖艶な美しさで男を虜にする女郎・小菊に入れ込み、借金を重ねている。一方、油屋の七左衛門の妻・お吉は、夫に頼まれ与兵衛をいさめようとするが、小菊に夢中な与兵衛は聞く耳を持たない。その帰り道に、お吉は小菊に「もう花ではない」と嘲笑われ……。", "2009-05-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAID　IN　MANHATTAN", "メイド・イン・マンハッタン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ジェニファー・ロペスが、恋や仕事に全力投球するヒロインに扮したラブ・ストーリー。一流ホテル勤務のシングルマザーと、政治家一族の御曹司という男女の運命的な出会いを綴る。マンハッタンの５つ星高級ホテルで客室係をしているマリサが、上院議員候補のクリスとめぐり合う。二人はお互いに好意を抱くが、ある事情からマリサが客室係であることを隠したため、彼らの恋はややこしいことに……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AU　PLUS　PRE｀S　DU　PARADIS", "逢いたくて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1957年の名作「めぐり逢い」のシチュエーションを劇中に取り込んだ、ラブ・ストーリー。カトリーヌ・ドヌーヴとウィリアム・ハートの両名優が成熟した男女をしっとりと演じる。今も忘れられない男性から再会を望む手紙を受け取ったファネット。パリ在住の彼女は出張が重なった後、再会場所のニューヨークに降り立つ。彼女は当初、約束に胸膨ませるが今回の仕事で出会ったマットに急速に惹かれていく。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("That's Entertainment!", "ザッツ・エンタテインメント", "", "", "http://ecx.images-amazon.com/images/I/51NBMKWR9BL._SL210_.jpg", 133, "ガルボ、ゲーブルらの人気スターを擁し、「風と共に去りぬ」をはじめとする幾多の名画で1930～50年代ハリウッドの黄金時代に君臨したMGM映画。これは栄光に包まれたMGM映画の傑作ミュージカルを中心に綴った絢爛豪華なエンターテインメントの集大成。「踊るニューヨーク」や「雨に唄えば」「恋の手ほどき」など収録映画75本、出演スターもアステアやG・ケリー、ゲーブルはもちろん、クロスビーほか特別出演11人も加わる。華やかなミュージカルの歴史を刻んだ数々のスターや名場面がオンパレードの大興奮131分。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MADAM", "MADAM HOLLYWOOD SEXスキャンダル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ハリウッドの高級売春組織の元締め、ハイディ・フライスが逮捕された事件をモチーフに、ハリウッドのセックス・スキャンダルを描いた実録官能サスペンス。美人ニュースキャスターのダニエルは、高級売春組織の全貌を暴くためにコールガールに扮して組織に潜入した。そこで彼女は金欲と性欲の渦巻く世界を目のあたりにし、いつしかダニエルもこの淫蕩な世界にハマッていき……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("けんかえれじい", "けんかえれじい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "鈴木清順の作品には華麗なアクション・ドラマと破天荒な青春ドラマの系列があるが、これは後者の頂点をなす傑作。痛快なアクション、おおらかなユーモア、青春期特有のリリシズムと、そのすべてがうまく融け合い、見事な青春絵巻を展開している。昭和初期、岡山中学の南部麒六は“喧嘩キロク”とあだ名されるほどの喧嘩好き。配属将校にたてついて放校になり、会津へと転校する。そこでキロクは自らの腕っぷしで硬派の頭株にのし上がっていく。一方で下宿屋の娘・道子に想いを寄せるが、道子は修道院に入ると言いキロクに別れを告げる。キロクは敵対する会津中学に大決戦を挑み、勝利するが、“もっと大きな喧嘩をしたい”と東京へ向かう……。当時22歳の高橋英樹が硬派中学生を好演。ヒロイン役の浅野順子はこの作品ののち、すぐ大橋巨泉と結婚し引退したが、彼女の可憐さはこの作品の中で、永遠不滅の輝きを保ち続けている。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROMANCE　OF　THEIR　OWN", "オオカミの誘惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "韓国で話題を呼んだインターネット小説を映画化。一人の女子高生と二人の男子高校生の恋の行方を、疾走感あふれる映像で描き出す。若手スター、チョ・ハンソンとカン・ドンウォンが、それぞれケンカが強い人情派とナイーブな一匹狼という対照的な役を魅力的に演じている。不良高校生ヘウォンが、バスの中で会った女子高生ハンギョンにひと目惚れ。だが、隣の高校のトップ、テソンも彼女を狙っており……。", "2005-03-19", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Catch That Kid", "ミッションX", "", "", "http://ecx.images-amazon.com/images/I/51fXnmDy97L._SL210_.jpg", 92, "", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO SURRENDER／　自分にたどり着くまで・　　Boxer飯泉健二", "NO SURRENDER／　自分にたどり着くまで・　　Boxer飯泉健二", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "９年ぶりに復帰したボクサーの生き様を追ったドキュメンタリー。19歳でデビュー、わずか２年で日本フェザー級チャンピオンになるも、網膜剥離により事実上の引退、そして９年後の1998年、31歳にして新興団体IBFより復帰した飯泉健二。９年間、トラックの運転手をしながら黙々とトレーニングを積んできたファイターの執念と、その秘められた思いに迫る。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ORBIS　PICTUS", "不思議の世界絵図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "スロヴァキア映画界を代表する巨匠M・シュリーク監督のファンタジードラマ。職業訓練学校に通う16歳の少女テレスカ。教師から母親宛ての手紙を渡された彼女は、居所を知らない母を探して、古い地図だけを手がかりに神秘的な森や田園地帯を訪ね歩く。", "1997-01-01", "72", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関東テキヤ一家 喧嘩仁義", "関東テキヤ一家 喧嘩仁義", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "関東テキヤ・菊水一家の国分勝は弟分とともに大阪入り。折しも大阪では近づく西大寺はだか祭りの庭場をめぐり、新興暴力団のテキヤいじめが始まっていた。テキヤ稼業に命を賭ける国分は、一人新興暴力団に立ち向かっていくが……。アクションもたっぷりの東映任侠路線映画。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST OF SHEILA", "シーラ号の謎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "映画プロデューサーのクリントは、妻シーラを死に追いやったひき逃げ犯人を捕らえるべく、６人の容疑者を自分のヨット“シーラ号”に招いた。やがて船内では、６人の旧悪が白日の下にさらされていくのだが……。俳優A・パーキンスが脚本を担当したことでも話題になった謎解きスリラー。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Victory at Entebbe", "エンテベの勝利", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "テルアビブ発パリ行きの旅客機がハイジャックされ、ウガンダのエンテベ空港に着陸。人質を奪還すべくイスラエルは特殊部隊で急襲、奪還に成功する……。1976年に実際に起きた事件をもとに、様々なエピソードを加えて作られた実録アクション映画。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONTE D’HIVER", "冬物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "E・ロメールの“四季の物語”シリーズ第２作。ある夏、ブルターニュの海辺で出会った男と永遠の愛を誓い合ったフェリシー。なのにささいな行き違いから二人は離ればなれに。それでもフェリシーは愛の奇跡を信じ待ち続ける……。同名のシェイクスピア劇をベースに描く現代の恋愛風刺劇。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版 DIR EN GREY UROBOROS 09", "劇場版 DIR EN GREY UROBOROS 09", "", "", "http://image.eiga.k-img.com/images/movie/55481/315x210.jpg?2685", 0, "人気ロックバンド「DIR EN GREY」が、2010年1月9～10日に日本武道館で行ったコンサートを記録した音楽ドキュメンタリー。1997年のバンド結成から13年、日本のみならず欧米でも人気を集める同バンドは、自他ともに最高傑作と認めるアルバム「UROBOROS（ウロボロス）」を08年11月に世界17カ国で同時リリースした。1 年以上を要し世界15カ国82都市をツアーでまわり、その締めくくりとして行われた武道館ライブの2日間に迫る。", "2010-05-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CORONADA", "エル・コロナド／秘境の神殿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "謎の失踪を遂げた婚約者を探すため、秘境の国にやって来たヒロインが、その国の内紛に巻き込まれていく様を、迫力あるスペクタクル・シーンの連続で繋ぐアクション・アドベンチャー。1980年代の冒険映画を彷彿とさせるテイストが楽しめる１本だ。ビバリーヒルズで婚約者ウィルと満ち足りた生活を送っていたクレア。ある日、ウィルは商用でスイスへ出張するが、大事な書類を忘れてしまう。クレアはウィルを驚かすため、書類を届けに自分もスイスへと向うのだが……。", "2003-01-01", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REVENGE OF BILLY THE KID", "ビリー・ザ・キッドの大復讐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "孤島に暮らすマクドナルド一家は、下品で粗野で超非常識家族。父親ガイルズに至っては、散歩中に欲情し連れていた雌ヤギと交尾する始末。しかもそのヤギがガイルズの子供、世にも奇怪な獣人を生んだ！　家族の中で一人正気を保っている末娘ロニーは、その子をビリーと名付け可愛いがる。だが、ガイルズはビリーを湖へ捨ててしまった。生き延びたビリーは、モンスターとなって一家に復讐を始める！　過激で笑えないギャグとブラック・ユーモアたっぷり、スプラッタ・シーン盛りだくさんの低予算ホラー映画。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POTE TIN KYRIAKI／NEVER ON SUNDAY", "日曜はダメよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "古代ギリシャ研究のため、アメリカからやって来たホーマー。ピレウスのグラマーな売春婦イリヤに惚れ込んだ彼は、彼女に近づいていく。そして本物の教養を授けようとするのだが……。J・ダッシンが、妻であるM・メルクーリとともに自らも主演。テーマ音楽も映画史に残る。", "1960-01-01", "1,27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("剣／tsurugi", "剣／tsurugi", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "近未来を舞台に、闘う宿命を背負った戦士のサバイバルが、エネルギッシュなバトル・シーンの連続で描かれる。演劇界の新鋭、加藤靖久ら個性的なキャストにも注目だ。遥か未来、管理社会“ゾーン66”は崩壊寸前にあった。その窮地を救うべく指導者たちは、最終戦士を開発するがあえなく失敗。処分されかかった最後の戦士がゾーンから脱出を図るが、行く手には他の戦士が待ち構え……。", "2006-04-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANJAAM", "アシュラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 170, "悲惨な人生をたどる女性の復讐を描いた異色マサラ・ムービー。実業家からストーカー行為を受けて夫を殺されたあげく、刑務所に投獄された女性シヴァーニー。お腹の子供も流産させられた彼女は怒りを爆発させ、復讐の鬼と化していく。ドロドロとした情念のドラマだが、随所にインド映画ならではのミュージカル・シーンやギャグが盛り込まれている。ビデオ、DVD発売時タイトルは「地獄曼陀羅　アシュラ」。", "1993-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREAT GATSBY", "華麗なるギャツビー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "ロングアイランドの大邸宅で、夜ごとパーティを催している金持ちの美青年ギャツビー。彼は他の男と結婚し、社交界の花形となった元恋人デイジーへの思いを断ち切れずにいた。やがてデイジーとの再会を果たしたギャツビーは、彼女の心を取り戻しかけるが……。フィッツジェラルドの名作小説に基づき、1920年代の上流社会の退廃と屈折、そこに身を置くギャツビーの純愛を綴ったラブ・ストーリー。レッドフォードが失恋をバネにのし上がった主人公をはまり役で演じ、美しい美術と衣装を配した映像も目を引く。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANZA D’AMORE SOTTOGLIOLMI", "楡の木陰の愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "17歳のフランツと精神科医の父ハンスは旅先のチロルの湖畔で、女流画家マルゲリータとその娘ロッテと知り合う。親同士の恋愛に一時は反発するフランツとロッテ。だが二人もすでに恋に落ちていた。美しい大自然を背景に、美少年M・レスターとM・グエルトーレの全裸のラブ・シーンが衝撃的な青春恋物語。", "1974-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Runaways", "ランナウェイズ", "", "", "http://image.eiga.k-img.com/images/movie/55821/315x210.jpg?2052", 107, "1970年代に人気を博した平均年齢16歳のガールズ・ロックバンド「ザ・ランナウェイズ」の軌跡を描いた音楽映画。ボーカリストであるシェリー・カーリーの自伝をもとに、男ばかりのロック界で差別を受けながらも自分たちのスタイルを貫こうとした少女たちの青春を描く。男まさりなギタリスト、ジョーン・ジェットを「トワイライト」シリーズのクリステン・スチュワート、セクシーな衣装とパフォーマンスで人気を集めたシェリー・カーリーをダコタ・ファニングが熱演する。", "2011-03-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KISS OF LIFE", "キス・オブ・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "霊となって現世を彷徨う死者の、愛の交流を題材にしたファンタジー。愛する家族を残して世を去った母親の霊のさまよいを、悲哀に満ちたまなざして綴る。冷徹な視点につらぬかれた映像は、死の悲しみをリアルに伝える。これがデビュー作となったエミリー・ヤング監督の真摯な語り口に注目。海外出張中の夫や子供たちを残して、事故で世を去った人妻ヘレン。彼女の霊は愛する者たちに別れを告げるため、現世に留まり……。", "2004-12-18", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("First Descent", "ファースト・ディセント", "", "", "http://image.eiga.k-img.com/images/movie/33781/315x210.jpg?4762", 110, "スノーボード界のトップライダーたちが、前人未到の“ファースト・ディセント（人間が初めて滑る斜面）”に挑むドキュメンタリー。本作撮影後、2006年のトリノオリンピックで金メダルを獲得したショーン・ホワイトとハンナ・テーターを始め、5人のスノーボーダーたちがアラスカ州バルディーズの雪山に集結。自然の脅威に圧倒されながらも、まだ誰も滑ったことのない究極の斜面を滑走する姿を大迫力で映し出す。", "2006-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mary Reilly", "ジキル＆ハイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "スティーブンソンの古典「ジキル博士とハイド氏」を住み込み奉公人の目線で描いたバレリー・マーティンによる小説「メアリー・ライリー」を、イギリス出身のスティーブン・フリアーズ監督が映画化したサスペンス・ドラマ。19世紀末のイギリス。ジキル博士の豪邸で奉公人として働きはじめたメアリーは、優しい博士を次第に信頼するようになる。ところが、ハイドという凶暴な男が博士の助手として屋敷に出入りするようになり……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("椿三十郎（2007）", "椿三十郎（2007）", "", "", "http://image.eiga.k-img.com/images/movie/53091/315x210.jpg?1426", 119, "黒澤明と三船敏郎の黄金コンビによる傑作時代劇を、織田裕二主演でリメイク。黒澤らの手によるオリジナル脚本を基に、森田芳光監督がメガホンを取る。上級役人の汚職を暴くため、社殿で密議を行っていた9人の若侍。ところが逆に黒幕が仕掛けた罠にはまり窮地に陥ってしまう。そんな彼らを救ったのは、偶然そこに居合わせたひとりの浪人だった。椿三十郎と名乗るその男は、若侍たちに協力することになり……。三十郎の好敵手・室戸半兵衛役に豊川悦司。", "2007-12-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MASK", "マスク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "マスクを着けると人格が一変する男の活躍を描いたコメディ。小心者の銀行員のスタンリーは、ある日、銀行にやって来たクラブ歌手のティナに一目惚れする。実は、彼女は銀行強盗をたくらむ恋人に頼まれ内部をビデオ撮影に来ていたのだ。その日の帰り道、偶然手にしたマスクを着けてみると、突然、竜巻が起き、スタンリーは緑色の頭をした怪人に変身し、思ったことは何でも実現させられる不思議な力を身に付けていた。そして、そのことで彼の生活は驚くべき展開を見せるのだった。J・キャリーの熱演と、ILMのデジタル処理によるマスクを着けた怪人のパワフルな活躍が見もの。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLICE ACADEMY2: THEIR FIRST ASSIGNMENT", "ポリスアカデミー2 全員出動！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "前回のヒットで、さっそく手掛けられた続編。アカデミーを無事に卒業した彼らが、天下の“犯罪地区”に配属され、いっそうスジ金入り（？）のダメ警官ぶりを発揮する。今回は“拳銃狂”タックルベリーと美人警官との甘くて愉快なロマンスも絡む。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN　SAC　DE　BILLES", "小さな赤いビー玉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「ピストルと少年」「ポネット」のJ・ドワイヨンが1975年に発表した長編劇映画第２作。ユダヤ人狩りを恐れ、ナチス・ドイツ占領下のパリから否占領地区の南仏へと旅立った少年たちの境界線を越える冒険、小さな恋、そしてパリ開放までを描いたヒューマン・ドラマ。悲惨な時代背景でも、大らかでたくましい少年たちの生きざまは感動的。", "1975-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PATRICIA—EINMAL HIMMEL UND ZURU¨CK", "魔性の少女パトリシア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ちょっぴりミステリアスなラブ・ファンタジー。レーサーのハリーは車も借金のカタに取られ、金づるの会長にも死なれた。そこで会長の16歳になる娘に近づくが、恋をしたのは彼の方。ところが娘は肉体に魔性をたぎらせ、近づく男をフリーセックスの虜にしていく……。", "1980-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("良寛", "良寛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "多くの日本人に親しまれ、愛され続ける僧侶・良寛の晩年を、瀬戸内寂聴の『手毬』をもとに映画化した作品。40歳も若い貞心尼との温かい交流を通して、彼の心豊かな人生を描いていく。この作品のために10キロの減量をして役に臨んだ松本幸四郎が、無限の慈悲をたたえた良寛を好演。「外科室」の坂本典隆による日本の四季を捉えた美しい映像も印象的だ。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Way", "星の旅人たち", "", "", "http://image.eiga.k-img.com/images/movie/57831/315x210.jpg?2944", 128, "俳優のほか「メン・アット・ワーク」「ボビー」などで監督としても活躍するエミリオ・エステベスが、実父マーティン・シーンを主演に迎え、息子を亡くした初老の男の聖地巡礼の旅を描いたヒューマンドラマ。アメリカ人の眼科医トムは、ひとり息子のダニエルが、スペイン北部ガリシア地方の聖地「サンディアゴ・デ・コンポーラ」を巡る旅の途上で不慮の死を遂げたとの報せを受ける。妻の死後、疎遠になっていた息子が何を思って聖地巡礼の旅に出たのかを知るため、トムは亡き息子の足跡をたどる。エステベスが息子ダニエルに扮し、「THE WAR 戦場の記憶」（1996）以来となるシーンとエステベスの親子役での共演が実現。", "2012-06-02", "1,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殴られた石松", "殴られた石松", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "おなじみ、清水次郎長一家の人気者、森の石松を主人公にした、長谷川伸の同名小説の映画化。石松の幼なじみ、常吉の兄が殺された。犯人は鬼頭桂之進という浪人で、その犯行を目撃したのは、田村の平七という渡世人だった。親分・次郎長に知恵を借り、平七を探すことになった石松だが……。田崎潤が、人の良い石松のキャラクターをうまく出している。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN GOTHIC", "アメリカン・ゴシック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「ヘルハウス」「小さな目撃者」などで知られるJ・ハフが撮り上げた本格的サイコ・ホラー。不注意から赤ん坊を死なせ、精神を病んだ若妻を含む６人の男女がバカンスに出かけることになる。だが彼らの乗った水上飛行機は故障のため小島に不時着。しかもそこには1920年代のまま時間の止まった殺人鬼一家が潜んでいた。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本の夜と霧", "日本の夜と霧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "日米安全保障条約の改定・批准に反対する安保闘争を舞台にした、時代背景色の濃い作品。霧の深い夜、新安保闘争で結ばれた新聞記者・野沢と女子学生・玲子の結婚式が行われていた。しかし、逮捕状が出ている全学連の学生・太田が突然乱入し、国会に向ったまま消息を絶った学生・北見の謎の失踪事件を叫び始めた。そこから、当時の学生運動の陰に起こった様々な事件が暴露されていく。上映4日目にして打ち切りとなった作品で、浅沼社会党委員長刺殺事件、安保闘争直後の世相などにより、再上映にも3年の歳月を要した問題作。大島監督はこの上映中止に抗議し、松竹を退社した。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRIEF ENCOUNTER", "逢びき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "平凡な家庭の主婦ローラは、毎週木曜日、近くのミルフォードの町に買物にいく。そんな彼女がやはり木曜日にミルフォードの友人を往診にくる医師アレックスと知り合う。木曜日だけの短い逢びき……。平凡な人妻の日常に生じた恋心を繊細に描いたメロドラマ。", "1945-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAVEN HAWK", "アイアン・ウーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "女性ボディビル世界チャンピオン、R・マクリッシュが、すばらしい肉体美とキレのあるアクションを披露する、テレフィーチャーのバイオレンス作品。幼い頃、両親を殺され、その罪を着せられて終身刑に処されたライア。12年後、脱走した彼女は、インディアン保留区に核廃棄物処理施設を建設するために両親の命を奪った、悪徳企業家に復讐の闘いを挑む。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUT FOR JUSTICE", "アウト・フォー・ジャスティス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "治安が悪化する一方のブルックリン。麻薬取引のもつれから同僚の刑事が白昼射殺され、妻とは別居、悪ガキ時代のライバルで今はマフィアの幹部となっている友人とも対立せざるを得なくなった刑事フェリーノは、すべての元凶である麻薬組織に敢然と立ち向かっていく。アクション・シーンに、友情や家族の絆といったドラマ性を加味した作品。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TELL ME THAT YOU LOVE ME，JUNIE MOON", "愛しのジュニー・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "恋人に顔を傷つけられたジュニー・ムーン、足の不自由なウォーレン、そしてホモセクシュアルのアーサーという３人の男女が出会い、一軒の家で共同生活を始めた。そんな彼らに大富豪の女が援助を申し出てくるのだが、アーサーが極度の情緒不安定に陥っていき……。奇妙な３人の共同生活とその破綻を、O・プレミンジャーが淡々と綴った感動のドラマ。顔にショッキングなメイクを施したL・ミネリの熱演、そして名手B・カウフマンの手による美しい映像も見逃せない。ただし、メイン・タイトル部分のみはスタンリー・コルテスが撮影を担当している。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Women in Rock", "ウーメン・イン・パンク・ロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "70年代末のイギリスのパンク／ニュー・ウェーブ・シーンで活躍する女性アーティストたちにスポットを当て、ライブ映像やインタビューで構成されたドキュメンタリー作品。監督は、「パンク・イン・ロンドン」を手掛けたドイツのボルフガング・ビュルト。1979年のデビュー当時まだ17歳だったアリ・アップ率いるザ・スリッツや、モーターヘッドとの共演で話題となったガールスクール初期のライブなど、貴重な映像が満載。", "1980-01-01", "45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ESCAPE OF THE BIRDMAN", "空中大脱走", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "1943年。ドイツ軍の収容所に捕らえられた核物理学者を救うために、士官ハリーは自ら捕虜となり潜入。所内でグライダーを制作し、見事脱出に成功する。奇想天外な救出作戦を描いたTVムービーだが、日本では劇場公開された。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("右門捕物帖 片眼狼", "右門捕物帖 片眼狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "三島近くの海津港。町では卍組が幅を利かせ、非道の限りを尽くしていた。そんな彼らのアジトとなっている酒場に、片眼狼なる素浪人がやってきて一味に加わる。だがこの素浪人こそ、実はむっつり右門の変装で……。好評“右門”シリーズのなかでも最高傑作と誉れ高い一編。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PALOMBELLA ROSSA", "赤いシュート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「ジュリオの当惑」等で知られるイタリアの異才ナンニ・モレッティの1989年度作品。学生時代は水球に打ち込んでいたという彼が、自ら水球のスター・プレーヤーである主人公に扮している。イタリア共産党の若き指導者で、名門水球チームの選手でもあるミケーレは競技場に向かう途中、自動車事故に遭い、軽い記憶喪失に陥る。なんとか試合場へ向かうのだが、今や彼の記憶の中にあるのは、母親に愛されていた子供の頃の思い出だけ。すべてを忘れたまま試合に臨むミケーレは、勝敗を決する大事なファール・シュートを託されることになり……。“自分探し”というシリアスなテーマながら、語り口はモレッティらしく軽妙でコミカル。切なさが残る後味にも彼の、並々ならぬ才気がうかがえる。フランスの由緒ある映画雑誌『カイエ・デュ・シネマ』は、1989年度のベストワン作品として、本作を選出した。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ИГЛА／THE　NEEDLE", "僕の無事を祈ってくれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ペレストロイカ後のソビエトを代表する映画監督の一人、R・ヌグマノフが描いた出口なき青春像。生まれ故郷であるアルマ・マタに帰って来た青年モロ。だが、昔の仲間たちと会っているときにケンカに巻き込まれ、敵対するグループから追われるようになる。かつての恋人ディーナを連れて遠くの町までやって来たモロ。幸福な二週間が過ぎ、やがて二人は町へと帰って来るが……。主演のモロを演じるのは、レニングラード（現サンクト・ペテルスブルク）の人気ロック・グループ、キノのリーダー兼ボーカリストのV・ツォイ。映画の中の挿入歌も彼が担当しており、若者たちのやるせない怒りを、見事に表現している。また、彼はこの映画に出演したあと、交通事故で急死しており、映画の中のヒーローと彼の実人生が重なり合うようでもある。", "1988-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOUL RED 松田優作", "SOUL RED 松田優作", "", "", "http://image.eiga.k-img.com/images/movie/54811/315x210.jpg?1650", 120, "2009年に生誕60年を迎える俳優・松田優作の歴史を紐解くプロジェクト「SOUL RED Project」の一環として製作されたドキュメンタリー。40歳の若さでこの世を去った名優・松田優作が残した肉声インタビューや直筆メモなど、貴重な資料を多数収録。実子である松田龍平と松田翔太や、浅野忠信、香川照之、仲村トオル、アンディ・ガルシアなど、多数の俳優たちが優作について熱く語る。監督は「世界はときどき美しい」の御法川修。", "2009-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KINJITE", "禁じ手", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「必殺マグナム」「地獄で眠れ」などでコンビを組んだ、トンプソン＆ブロンソンが手掛けた刑事アクション。売春、ポルノ映画がらみの犯罪、日本人少女の誘拐を追う刑事の活躍を描く。原題が語るように、本作は日本人が絡んだストーリー展開となっており、皮肉交じりのエピソードも登場する。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I AM A FUGITIVE FROM A CHAIN GANG", "仮面の米国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ある脱走犯の手記を映画化し、公開当時のアメリカで論争を巻き起こした異色のドラマ。見張りだけの強盗で10年の刑を言い渡されたジェームズ・アレンは、生き地獄と呼ぶにふさわしい南部の刑務所に入れられる。どうにか、そこを逃げだし、まともに生きようともがくジェームズだが、世間の風当たりは厳しく、彼は次第に犯罪の世界へ。「犯罪王リコ」のM・ルロイ監督、「暗黒街の顔役」のP・ムニ主演の１本。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RAGGEDY RAWNEY", "ジプシー 風たちの叫び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「モナリザ」で見事な演技を見せ、「ロジャー・ラビット」ではコメディアンぶりも披露したB・ホスキンスの監督、脚本、出演作。長い闘いで荒廃した国。新兵のトムはある日出会った少女に赤い服を着せられる。だが少女の一家が惨殺されたのを知った彼は赤い服を着たまま、ジプシーたちと同行する。ビデオ発売時タイトルは「私の愛した魔女」。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ben-Hur: A Tale of the Christ", "ベン・ハー", "", "", "http://ecx.images-amazon.com/images/I/518A3R0tL6L._SL210_.jpg", 128, "古代ローマを舞台に、迫害を受ける初期キリスト教徒たちの姿を描いた一代史劇スペクタクル。アメリカ・サイレント史に残る壮大なスケール、そして映画史に残る戦車による競争シーンが有名。L・ウォレスの原作を映画化したものとしては二度目（1907年に最初の映画化作品がある）となる。", "1926-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Garden State", "終わりで始まりの4日間", "", "", "http://ecx.images-amazon.com/images/I/51Jj0EyQKdL._SL210_.jpg", 103, "「スター・ウォーズ」シリーズのナタリー・ポートマン出演による青春ドラマ。ロサンゼルスで暮らす売れない役者アンドリューは、母親の葬儀に出席するため9年ぶりに故郷のニュージャージーを訪れる。感情の起伏が乏しいアンドリューは母のために涙を流すこともできず、久々に再会した友人との交流にも違和感を抱く。そんなアンドリューの前に、天真爛漫な少女サムが現われ……。若手俳優ザック・ブラフが自身の脚本をもとに初メガホンを取り、自ら主演も務める。", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春駒のうた", "春駒のうた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "群馬県の出身である児童文学者・宮川ひろの同名小説を“辺境の巨匠”神山征二郎監督が、舞台である利根川上流の山村でオールロケして完成させたヒューマンドラマ。終戦直後に生まれた小児マヒの少年と祖父との心の交流を縦糸に、四季の自然、郷里の伝承習俗、戦争へのやり場のない怒りなどを描いていく。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アナーキー・イン・じゃぱんすけ", "アナーキー・イン・じゃぱんすけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "「冷血の罠」に続く瀬々敬久の監督第20作。久しぶりに「獣欲魔乱行」や「破廉恥舌戯テクニック」など、初期に顕著だった開放的な勢いに満ちた快作となった。放蕩の末、不妊となったヒロインが赤ん坊を誘拐。やがて出会ったコンビニ従業員と、擬似家族を形成していくが、思わぬ“しっぺ返し”を食らうことになる。1981年、1989年、1999年と跳躍を重ねる三部構成も鮮やかで、はじけることを知らない中年に扮した佐野和宏も好演。「見られてイク女…」のタイトルでピンク映画としても公開された。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PENELOPE", "美人泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "最新式の防犯装置を取り付けたばかりの銀行から、６万ドルの大金が強奪されてしまった。ところがなんと、これを盗んだのは副頭取の妻だった……。窃盗症という悪癖にかかった女を主人公に描くコメディ。A・ヒラー監督らしい洒落たタッチの演出に加え、N・ウッドが盗癖女を華麗に演じる。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ☆ドラえもんズ　ムシムシぴょんぴょん大作戦！", "ザ☆ドラえもんズ　ムシムシぴょんぴょん大作戦！", "", "", "http://ecx.images-amazon.com/images/I/51FWTGCY5SL._SL210_.jpg", 16, "7人のネコ型ロボット「ザ・ドラえもんズ」の活躍を描く短編アニメーションの第3弾。ロボット学校の修学旅行にやって来たドラえもんズに卒業試験が課せられた。その内容は、ムシロボットとそれぞれペアを組み、秘密道具を使わずに日没までに学校にたどり着くというものだった。間にあわなければ落第してしまうと知ったドラえもんズは、大急ぎでムシロボットを探して学校へと向かうが……。「のび太の南海大冒険」「帰ってきたドラえもん」と同時上映。", "1998-03-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狂わせたいの", "狂わせたいの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "京都のアート・シーンで活躍する石橋義正が、日本映画離れしたセンスで完成させた16ミリ作品。最終バスに乗り遅れたシャイなサラリーマンが、普段乗らない終電に乗り込んだことから、次から次へと驚愕の災難に遭遇していく。電車、タクシー、居酒屋、病院などで、強烈な個性を持った女たちにイビられ続ける主人公。山本リンダによる同名タイトル曲を皮切りに、淫らな毒性を持った歌謡曲の数々がモノクロ画面を横行し、狂乱のミュージカルを形作っている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Trespass", "ブレイクアウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "", "2012-06-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILL ZONE", "戦場のライオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "1968年２月、ベトナム戦争に不満と反逆心を持つウィンギンス大佐は、自分の部下たちが次々と死んでいくのを見るうち、その怒りと内に秘めた狂気が爆発。自らの腕力と地位を利用し、無差別殺りくを始める。見かねた部下のシュリフ中尉は、狂った大佐に銃口を向けていくが……。極限状態のなか、部隊の存亡を懸けて男と男の壮絶な戦いが繰り広げられる。ウィンギンス役のD・キャラダインの狂気あふれる演技が見もの。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("異国の丘", "異国の丘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "久米子は清と結婚し、長男の国雄が生まれ幸せな日々を過ごしていたが、清は召集され出征していく。戦後、友人・文子の夫の戦死が知らされる。文子は夫の死を信じず、神に祈り続けていた。願いが通じて彼女の夫は帰ってきた。だが、今度は久米子のもとに清の戦死公報が届く……。主題歌がヒットした渡辺邦男プロダクション第１回作品は、戦中・戦後の女性を描いたヒューマン・ドラマ。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("洋菓子店コアンドル", "洋菓子店コアンドル", "", "", "http://image.eiga.k-img.com/images/movie/55511/315x210.jpg?4488", 0, "「60歳のラブレター」「白夜行」の深川栄洋監督が監督・脚本を務め、江口洋介と蒼井優が初共演を果たすヒューマンドラマ。恋人を追いかけて上京してきた鹿児島のケーキ屋の娘・なつめは、有名洋菓子店「パティスリー・コアンドル」で突然スイーツ界から姿を消した伝説のパティシエ・十村と出会う。そこで働くことになったなつめは、十村や常連客との出会いを通じて人生や夢を見つめ直していく。", "2011-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUEURS", "略奪者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "アフリカの広大な砂漠を舞台に、逃走する強盗犯たちの裏切りと欲望の人間模様を描いたバイオレンス活劇。駆け引きやだまし合いのみならず、地雷原突破などの危機の連続もスリリングだ。砂金を盗んだ悪党４人組が、砂漠を大型トラックで逃走。暑さも手伝い、ギスギスしていた彼らの関係は、一人が射殺されたことで険悪化する。そのうえ、彼らの行く手にはさらなる危機が待っていた。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBERT ET ROBERT", "２人のロベール／花嫁募集中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "タクシー運転手のロベール・ゴルマン、ドジな青年ロベール・ピリエ。二人は結婚相談所で知り合い、意気投合。いつか自分たちにふさわしい女が現れると励まし合うが、現実は厳しく、彼らはダンス・パーティーでも置いてきぼりをくう始末……。ルルーシュ監督のホロ苦さと温かみの交錯するコメディ。", "1978-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嫉妬", "嫉妬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "夫が失踪してクラブのママと心中。しかし女は生き残り、夫だけが死んでしまった。その謎を探るためにホステスとなってその女の店に勤め始めた妻は、夫が陰謀に巻き込まれ殺されたことを知る。藤本義一の同名小説をもとにしたサスペンス・ドラマ。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪猫逢魔が辻", "怪猫逢魔が辻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "若き日の勝新太郎が出演した“怪猫”シリーズ。女歌舞伎の人気役者、市川仙女は、彼女をねたむ一座の役者の陰謀で惨殺されてしまう。そこに通りがかった仙女の愛猫は、彼女の血をすすったことで、仙女の怨念が乗り移り……。入江たか子の怪猫ぶりが秀逸。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KISS OF THE VAMPIRE", "吸血鬼の接吻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "怪奇映画の名門ハマー・プロ製作による１本。半世紀以上昔のこと。新婚旅行の途中、車の故障でババリア地方の安ホテルに泊まったハーコートとマリアンヌ。その晩二人は村はずれのラブナ博士（実は吸血鬼）から夕食に招待されるのだが……。", "1964-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARNAGES", "めざめ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "フランスの新人女性監督D・グレーズの才気あふれる群像劇。幼少期の記憶がない保育園の先生、自分のホクロにコンプレックスを抱いている売れない女優などの悲しみと苦悩、そして再生への道のりが綴られる。プロローグで語られる“闘牛”というモチーフが各エピソードを結ぶ役目を果たすユニークな構成、イマジネーション豊かな映像感覚に注目を。", "2002-01-01", "2,37,9,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER HIMMEL UBER BERLIN", "ベルリン・天使の詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "像の上からモノクロのベルリンの街を見降ろす天使ダミエルの耳には人々の声が聞こえてくる。姿は見えないはずなのに、アメリカの映画スター、ピーター・フォークが人間になれとダミエルを誘う。そんな彼が、美しい空中ブランコ乗りに恋をした。天使は人間に恋をすると死んでしまうのだが……。ベンダースが10年ぶりに故国に戻って撮った傑作、日本ではそれまでの単館公開記録を塗り替えて社会現象になった。", "1988-04-23", "45,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STARLIGHT　HOTEL", "スターライトホテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "父を探している少女と、警察に追われている青年が、波乱続きの旅を通して心を通わせていくロード・ムービー。1930年代のニュージーランド。父親が職探しをする間、親戚の家に預けられた少女ケイトは、父の跡を追って旅に出る。一方、正義感から傷害事件を起こしたパトリックは、警察に追われ逃亡中。そんな二人が出会い、ケイトは彼を用心棒として、パトリックは警察の目を欺くために彼女と旅をともにする。度重なる危機を乗り越えていくうち、やがて二人は互いに必要な存在となっていく。", "1987-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Mountain Eagle", "山鷲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "", "1926-01-01", "3,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ターチ・トリップ", "ターチ・トリップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "日本を代表するクィーアフィルム（ゲイ映画）の旗手が、「ノー・スキン・オフ・マイ・アス」のドイツ人製作者、J・ブリューニンクの全面出資によって完成させた実験的な16ミリ中編。死を予感する青年が見たけだるい夏の風景が、効果音とともに現れては消える。撮影したフィルムをそのまま、リーダー部分も含めてぶっきらぼうにつないだ映像にきわめて繊細な音響処理が施されており、創作とも記録ともつかぬ独特の作風が素晴らしい。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Countdown", "大統領のカウントダウン", "", "", "http://image.eiga.k-img.com/images/movie/1161/315x210.jpg?3805", 116, "チェチェン軍に囚われ、ロシアで起きた爆弾テロの犯人に仕立てられたFSBエージェントのスモーリン。そのころモスクワでは、イスラム過激派アンサールアラーによるサーカス会場占拠事件が発生、多くの人質の中には、スモーリンの小さな娘も入っていた。実在の事件や世界情勢を織り込み、寡黙なヒーロー・スモーリン少佐の活躍を描くアクション・エンタテインメント。監督は33歳の若き才能エフゲニー・ラブレンチェフ。", "2007-03-25", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CROSS MISSION", "追跡大陸 グレート・ミッション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "某軍事国家で絶大な勢力を持つロメロ将軍は、彼の支配のもと世界中に麻薬をばらまいていた。一方、アメリカ麻薬組織から派遣されて来たコルベットは、ひょんなことから反対制ゲリラ組織“コンドラ”に協力することになる。ロメロ将軍とコンドラの壮絶な攻防戦が、派手なアクションで展開される。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京の女性", "東京の女性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "タイピストとして自動車会社に就職した君塚節子。だが好意を持つ男・木幡から戦闘的なセールスの世界の話を聞き、その職業に心を引かれる。女に何ができるという周囲の冷笑の中で、彼女は歯を食いしばって頑張るが、肝心の木幡は彼女の妹・水代の方に興味を示していくのだった。恋か仕事かに悩む女を当時の女性心理で捉えた、女性映画の快作になっている。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE　OLEANDER", "ホワイト・オランダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ジャネット・フィッチの全米ベストセラー小説『扉』を映画化したヒューマン・ドラマ。離ればなれになった一組の母娘の葛藤が、ミシェル・ファイファーら豪華女優陣の共演で描かれる。カリスマ的な芸術家の母親イングリッドが殺人罪で終身刑となり、里子に出された少女アストリッド。厳しい現実に苦しむ彼女は、面会したイングリッドから優しい言葉をかけてもらうことができず……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jerry Maguire", "ザ・エージェント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "理想主義者のスポーツ・エージェントのジェリーは会社を解雇され、ただ1人共感してくれた会計係のドロシーと独立する。ジェリーはクライアントの落ち目のフットボール選手とドロシーと共に、紆余曲折を経て成功を勝ち取る。フットボール選手を演じたキューバ・グッディング・Jrがアカデミー助演男優賞を獲得。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JIBOA", "アマゾンの秘宝 幻のエメラルド都市を求めて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アマゾン奥地で伝説のエメラルドの神殿を発見した探検隊が消息を絶ち、隊員の一人マークだけが記憶を半ば失って生還した。秘宝を狙うギャングはマークを捕らえ、アマゾンの奥地へと入っていくが……。どこか“インディ・ジョーンズ”シリーズを思わせる典型的な秘宝探しの秘境冒険アクション。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("顔役", "顔役", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "石井輝男監督、鶴田浩二、高倉健主演によるギャング映画。関東のヤクザ連合と関西のヤクザ連合の一触即発の危機を、二人の男が体を張って仲裁する。この作品は当初、深作欣二が監督する予定であったが、トラブルで深作が降り、「網走番外地」シリーズの石井輝男が代打で監督した。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陽炎３", "陽炎３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "前作に続いて、高島礼子が女胴師、不知火おりんを演じる任侠アクション。昭和６年、瀬戸内海に面した小さな町に降り立ったおりんは大勢の極道に袋だたきにされている漁師、次郎を救う。彼は港を仕切る大橋組から抜け出し、漁師たちを集めて組合を作ったことで怒りを買っていたのだ。そして、大橋組と対立する山伝組にわらじを脱いだおりんは次郎が組長、伝吉の息子であることを知る……。抗争に巻き込まれていくおりんの戦いが、大橋組側の博徒、岩田銀造との友情と対決を織り込みながら描かれていく。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREY ZONE", "灰の記憶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「O<オー>」のティム・ブレイク・ネルソン監督が手掛けた重厚な戦争ドラマ。“ゾンダーコマンド”と呼ばれた特別任務担当のユダヤ人に焦点を当て、アウシュビッツの新たな史実が明かされる。1944年、アウシュビッツ強制収容所。ドイツ軍に従事するユダヤ人のホフマンらは同胞の遺体処理を担当していた。そんなある日、ホフマンはガス室で奇跡的に生き延びた少女を発見する。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE DIABLE ET DIX COMMANDEMENTS", "フランス式十戒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "巨匠デュヴィヴィエの元に、当時のフランス映画のスターが集結したブラック・コメディの怪作。旧約聖書の『十戒』をハリウッド流のスペクタクルではなく、フランス的なエスプリを効かせて撮り上げた異色作だ。“悪魔の化身”と称する“毒蛇”がナレーターとなって、人間がモーゼの十戒を破っていく、そのプロセスを描いている。いわば、人間が悪事に手を染めていく話を集めたオムニバス作品。", "1962-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fig Leaves", "無花果の葉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "エデンの園で朝を迎えたアダムとイブが、そのまま時を超えて現代のニューヨークに住むアダムとイブという新婚夫婦になるという設定で、「いつの時代でも女性は衣装を求めて飽くことがない」というテーマを展開させる明朗なコメディ。ホークス監督作品のデビュー作「栄光への道」より先に日本に輸入された。", "1927-01-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("時の香り／リメンバー・ミー", "時の香り／リメンバー・ミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "韓国で大ヒットしたラブ・ストーリー「リメンバー・ミー」のリメイク作品。無線を通して1979年と2000年という時代を越えて交流する、大学生の男女の姿をハートフルに描く。「ときめきメモリアル」で映画デビューを飾った吹石一恵が、ピュアなヒロインをさわやかに演じている。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NAPOLE´ON", "ナポレオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 192, "フランス映画史上空前の豪華キャストで、英雄ナポレオンの半生を綴った史劇スペクタクル。革命勃発の18世紀末から、数々の戦いに勝利してフランス皇帝を名乗り、そしてセントヘレナに流されるまでのナポレオン像を、私生活を彩った女たち、あるいは歴史に残る戦いなどを通して描き出していく。青年の時代のナポレオンをD・ジェラン、壮年となってからはR・ペルグランが演じており、策謀士タレーランを監督S・ギトリ自らが演じる。", "1954-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　LIVERS　OF　A　BENGAL　LANCER", "ベンガルの槍騎兵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "インド北西に位置するカイバー峠を死守するイギリス軍の英雄二人の物語。槍騎兵を率いるストーン大佐の下、中尉のマグレガーとフォーサイスは数々の難関を突破。しかしそんな折、ストーンの息子ドナルドが敵に捕らえられる。彼を救い出すために、二人は敵地に乗り込んでいくが……。G・クーパーが男臭い中尉を熱演している。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOLA", "ローラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "J・ドゥミの長編第１作にして最高傑作とも噂され、ヌーヴェル・ヴァーグの真珠とも評されながら長い間日本未公開だった幻の名作。港町ナントに住む青年ローランは、ある日偶然にも幼い日の初恋の相手ローラに出会う。今やキャバレーの踊り子に身をやつしていたローラだが、ローランは今でもローラを愛していると確信する。しかし彼女もまた初恋の相手で、彼女の息子の父親でもあるアメリカの水兵の面影を忘れられずにいた……。ドゥミの故郷ナントの風景を目もくらむような光の中に捉えたR・クタールの撮影も秀逸な、胸しめつけられる悲恋物語。", "1960-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガメラ２・レギオン襲来", "ガメラ２・レギオン襲来", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「ガメラ　大怪獣空中決戦」(1995)で復活した、新生ガメラの第２作。札幌近郊に隕石が落下し、自衛隊科学チームが調査に行くが、隕石を発見できなかった。その後、札幌周辺で怪事件が続発。地下鉄の中で人間大のいも虫が、乗客を襲い、巨大な植物がビルを突き破った。植物がどんどん巨大化し、札幌が壊滅寸前、ガメラが現れる。植物をプラズマ火球で粉砕するが、怪虫の群れが襲いかかり、ガメラは退却。と同時に、巨大なレギオンが出現、仙台を壊滅し、東京に向かい始める。果たしてガメラはレギオンを倒すことができるのか？", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIP２", "ザ・チップ　引き裂かれたコールガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "売春、人身売買、麻薬、ギャンブルなど様々な犯罪に溺れ自らコールガールとして生きていく女“英子”。時には愛を求め、時には愛におびえながら、男たちにもてあそばれ、堕ちていくヒロインの姿を通して、現代の韓国社会の問題点を鋭く突く。", "1990-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARIS BLUES", "パリの旅愁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "H・ブレンダーのベストセラー小説を映画化。アメリカからパリへ向かったジャズマンと、二週間の休暇旅行でパリに来た女性の恋を描いている。主演は、P・ニューマン、J・ウッドワードの夫婦だが、L・アームストロングなどのジャズマンが脇を固めているのも魅力。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Reader", "愛を読むひと", "", "", "http://image.eiga.k-img.com/images/movie/53191/315x210.jpg?2035", 124, "ベルンハルト・シュリンクのベストセラー小説「朗読者」を、「めぐりあう時間たち」の監督＆脚本家コンビが映画化。1958年のドイツ、15歳のマイケルは、21歳年上のハンナとベッドを共にし、彼女に頼まれて本を朗読してあげるようになるが、ある日突然、彼女は姿を消す。時は流れ、戦時中のある罪を問われて投獄されたハンナのために、マイケルは物語を朗読したテープを刑務所に送り続けるが……。第81回アカデミー賞でケイト・ウィンスレットが主演女優賞を受賞。", "2009-06-19", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OPERATION DAYBREAK", "暁の7人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ナチス占領下のチェコスロバキア。連合軍はレジスタンスを使って、総司令官ハイドリッヒ暗殺を実行。成功したものの、仲間の密告で隠れ家を襲撃された７人の若者は、一人、また一人と散っていく……。極限状態での青春像が見事に描かれた戦争悲劇の一編。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOMB　THE　SYSTEM", "ボム・ザ・システム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ヒップホップ・カルチャーの一環として知られる落書きアート、グラフィティに情熱を傾ける若者の日常を切り取った青春ドラマ。「ストーリーテリング」の好演で注目されたマーク・ウェバーが主演と製作を務め、リアルな青春像を体現する。ストリートの息吹を感じさせる、生々しい映像も魅力。市警の落書き取締班の追跡をかわしてグラフィティに熱中する若者ブレスト。やがて仲間の逮捕や恋の芽生えが彼の心に波紋を投げかける。", "2005-09-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("またまたあぶない刑事", "またまたあぶない刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "劇場版「あぶない刑事」のシリーズ第２作。今回は、政治家までも巻き込んで、ヨコハマの裏舞台でコカインや拳銃の密輸、売春などを仕切る若きドンにタカとユージの名コンビが闘いを挑む。TVシリーズで６本をこなした一倉治雄監督の劇場映画デビュー作。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動警察パトレイバー THE MOVIE", "機動警察パトレイバー THE MOVIE", "", "", "http://ecx.images-amazon.com/images/I/51WpFguSzUL._SL210_.jpg", 99, "レイバーと呼ばれる人型作業ロボットが活躍する世界を舞台に、警察のレイバー＝パトレイバーを駆使して犯罪に立ち向かう警視庁特車二課の姿の描いた人気オリジナルビデオアニメ「機動警察パトレイバー」の劇場版。1999年、東京湾岸部では多数の工事用レイバーを稼動させた大規模開発「バビロン・プロジェクト」が進行していた。そんな折、自衛隊の試作レイバーの暴走事件が発生したのを皮切りに、何者かが仕組んだコンピュータウィルスにより都内各所の作業用レイバーの暴走が続発する。", "1989-07-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUNNY FACE", "パリの恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "カメラマンに扮したアステアと、ファッションモデル役のヘプバーンが、パリを舞台に繰り広げるラブ・ストーリー。華麗なカメラ・ワークのなか、楽しい歌とファッショナブルな踊りが映し出されていく。“ファニー・フェイス”という流行語は、この映画から発生。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("テレビばかり見てると馬鹿になる", "テレビばかり見てると馬鹿になる", "", "", "http://image.eiga.k-img.com/images/movie/34011/315x210.jpg?1352", 85, "漫画家・山本直樹の短編を「心中エレジー」の亀井亨監督が映画化。引きこもりの女性と、彼女の部屋を訪れる男たちとの交流をユーモラスに描き、現代人が抱える心の闇を浮き彫りにする。ゴミだらけの部屋で5年間引きこもりの生活を続けている有紀。部屋を訪れるのは、彼女の生活をネット中継している恵二と、身体目当ての勝也だけだった。そんなある日、母親が依頼したカウンセラーの馬戸がやって来て……。主演は人気AVアイドルの穂花。", "2007-06-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INDAGINE SU UN CITTADINO ALDI SOPRA DI OGNI SOSPETTO", "殺人捜査", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ローマ市警のジャン・マリア・ヴォロンテは、学生と浮気をした愛人と言い争ううちに相手を殺害してしまう。現場には多くの証拠が残っていたが、公安部長に昇進した彼を疑う者はなく、ジャンは権力者の自負と破滅への誘惑の狭間を揺れ動く。", "1970-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Disaster!", "モトリー・クルーのディザスター！ アルマゲドン危機一発", "", "", "http://image.eiga.k-img.com/images/movie/54131/315x210.jpg?4589", 83, "「アルマゲドン」や「ツイスター」などの地球災害映画をベースにした過激描写満載のクレイアニメ。ある日、地球と衝突の可能性のある小惑星が発見され、宇宙機関のASSAは衝突を防止するために選ばれし7人のヒーローと1体のオカマ・ロボットを宇宙へ送り出すが……。ヘビメタ・ロックバンドのモトリー・クルーが本人役で出演している。", "2009-02-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血を吸う宇宙", "血を吸う宇宙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "単館での上映ながら、カルトな人気を博しヒットしたSFスプラッタ「発狂する唇」のシリーズ第２作。前回に引き続き、三輪ひとみ、阿部寛が出演するほか、新ヒロインに中村愛美を抜擢し、前作よりもパワーアップしたアングラワールドが展開する。娘を誘拐された里美が助けを求めて警察に駆け込むが、夫は刑事に対して“家に娘などいない”という。夫の発言に疑問を抱く里美。やがて彼女の前に、宇宙人による地球侵略の動向を調べているというナゾの人物が現れ、里美に驚くべき事実を告げるのだった……。脚本家、高橋洋が病床で書き溜めたシナリオを佐々木浩久が映画化。高橋の旧友でもある映画監督、黒沢清や中田秀夫の特別出演も見逃せない。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("波牛／THE　CHAMPIONS", "チャンピオン鷹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "Y・ピョウ主演で描いたスポーツ・アクション。田舎育ちの青年がふとしたことから一流のサッカー選手を目指すさまを、ギャグを絡めつつ描く。カンフー・サッカーの妙技も見せてくれるさわやかな作品。", "1984-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蒲田行進曲", "蒲田行進曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "映画の花形スターと、その取り巻きの大部屋俳優のサド・マゾ的関係を軸に、スターの子を宿した落ち目の女優を交えた３人の奇妙な人間関係を、映画製作の内幕に絡めて描き出した人情喜劇の傑作。原作および舞台の持つ毒は薄められたが、映画は独自の辛口のコメディとして成功し、各映画賞を独占した。花形スターの銀四郎は、妊娠した恋人の女優・小夏を彼の取り巻きの一人・ヤスに押しつけ結婚させてしまう。ヤスは危険を顧りみず生活費のためスタントマンをやるが、生傷がたえない。松坂が二人の間で揺れ動く女心を好演し、見事なヒロインぶりを見せた。カツドウ屋たちの心意気を感じさせる力作。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Racetrack", "競馬場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録 飛車角 狼どもの仁義", "実録 飛車角 狼どもの仁義", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "大正末期から昭和にかけての不況と社会不安を背景に、地方からやって来た“侠客”石黒彦市が、自分の力と暴力のみを信じて生き抜いていく姿を、実録風に描いた任侠もの。飯干晃一の『狼どもの仁義』を原作に、菅原文太、小林旭らが熱演。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦後最大の賭場", "戦後最大の賭場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "東映任侠映画を代表する監督・主演の組み合わせの決定版。群雄割拠する任侠団体が大同団結して大日本同志会を結成するが、関西代表の組長が急死。そのあと釜を岩佐という男が狙うが、彼のあまりに卑劣なやり口に、部下の五木は反逆の刃を向ける。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("古惑女之決戦江湖", "欲望の街・純愛篇／紅い疾風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「欲望の街　古惑仔I　銅羅湾〈コーズウェイベイ〉の疾風」の大ヒットにあやかって、人気コミックを映画化した姉妹編的な作品。“古惑女”に扮したロレッタ・リー、カレン・モクらのイキのいい演技が見どころ。黒社会の抗争が絶えない街で生きるマーブルが、ゲームセンターで出会った女バンを自分たちのグループに誘う。にぎやかで楽しい共同生活もつかの間、かつての恋敵である凶悪な女アイジーが出所し、マーブルは窮地に追い込まれる。ビデオ発売時タイトルは「不夜街　危険な天使たち」。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大阪ハムレット", "大阪ハムレット", "", "", "http://image.eiga.k-img.com/images/movie/54151/315x210.jpg?3404", 107, "「少年アシベ」「ウチの場合は」などの人気4コマ漫画で知られる森下裕美の同名オムニバス短編集の映画化。悩み多き3兄弟と肝っ玉母ちゃん、そして何故かそこに同居する気弱な“おっちゃん”が織り成す心温まる家族ドラマ。監督は「おぎゃあ。」の光石富士朗。松坂慶子と岸辺一徳が風変わりなカップルを、「ごめん」の久野雅弘、「酒井家のしあわせ」の森田直幸、子役として活躍する大塚智哉が繊細な3兄弟を好演している。", "2009-01-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWING KIDS", "スウィング・キッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "台頭するナチの勢力に反抗して、スウィング・ジャズに酔いしれ、毎夜、自由を謳歌する“スウィング・キッズ”と呼ばれたドイツの若者たちの友情と悲劇を描いた青春映画。ダンスホールで女の子たちの熱い視線を浴びるピーターとトーマス、天才的なギターの腕を持つアーヴィング。ヒトラー・ユーゲントと反目していた３人だったが、時代の残酷な流れは、それぞれの人生に過酷な試練を与えた。スクリーンを彩る、スウィング・ナンバーとエネルギッシュなダンスシーン。1930年代のドイツの若者群像をユニークな視点から描いている。DVDタイトルは「スウィング・キッズ　引き裂かれた青春」。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUDDEN IMPACT", "ダーティハリー4", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "前作から７年ぶりに製作されたシリーズ第４作。イーストウッド自らが製作・監督・主演を兼ねる。残忍な謎の殺人事件を追ってサンパウロへ来たハリー刑事に、執拗に襲ってくる殺人鬼の魔の手……。カーチェイスなど多くの見せ場を持った力作。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POCAHONTAS", "ポカホンタス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ディズニーアニメが史上初めて、歴史的逸話を題材にした長編アニメ第33作。アメリカ建国神話として広く知られる開拓指導者ジョン・スミスと先住民ポカホンタスの恋物語をもとに、愛の尊さや勇気をディズニーならではの繊細な映像とミュージカル仕立てでうたい上げる。1607年、アメリカ・ヴァージニア州にイギリスから開拓者の一団がたどり着いた。探検隊に参加した英国軍人ジョン・スミスは、植民地で自然とともに生きるパウアタン族の娘ポカホンタスと出会い、恋に落ちる。だが、先住民と開拓者の争いは日増しに激化していき、やがてスミスに危機が訪れるのだった……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クリアネス", "クリアネス", "", "", "http://image.eiga.k-img.com/images/movie/53351/315x210.jpg?4311", 112, "第1回日本ケータイ小説大賞を受賞した同名作を「地下鉄（メトロ）に乗って」の篠原哲雄監督のメガホンで映画化したラブストーリー。自宅マンションで客に身体を売っている女子大生さくら。ベランダから見える出張ホスト事務所で働く青年に興味を持った彼女は、青年にレオと名付け密かに観察していた。ある日、嫌な客に困っていたさくらの元に突然レオが現われ彼女を救う。それ以来、2人は次第に惹かれあっていくが……。", "2008-02-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ДЕВЯТЬ ДНЕЙ ОДНОГО ГОДА", "一年の九日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ソビエト国立映画大学の教授として、グリゴーリー・チュフライ、アンドレイ・タルコフスキーなど数々の才能を世に送り出した、M・ロンム監督最後の劇映画。シベリアの地方都市にある原子力研究所。核融合実験を続けるグーセフは人体に重大な影響を与えうる量の放射能を浴びてしまう。他人にはその事実を隠しながら彼は、恋人のリョーリャと結婚。親友で、恋敵でもあったクリコフをも巻き込みながら、研究を続けていくが……。一人の女をめぐる二人の男の恋のさや当てを、放射能の危険をめぐる物語(タイトル通りに一年のうちの任意の９日間だけをピックアップし描く)に封じ込め、その緊張を見事に映画の中につなぎとめたロンムの演出が見事。G・ラブロフによる、奇妙に歪曲されたような緊張感みなぎる映像も印象的。", "1961-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぷりてぃ・ウーマン", "ぷりてぃ・ウーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "芝居に第２の人生を見いだした老女から成る劇団の奮闘を、笑いと涙で綴った痛快作。それぞれに個性的で茶目っ気たっぷりのおばあちゃんたちに扮したベテラン女優たちの熱演に注目。毎日が退屈な老女、葵は脚本家の孫娘、加奈子の作品を読み、市民発表会での上演を決意。町の集会所に集う仲間を集め、嫌がる加奈子をも引き入れて、老女たちの劇団は発足するが……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LONELINESS OF THE LONG DISTANCE RUNNER", "長距離走者〈ランナー〉の孤独", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "イギリスの“怒れる若者たち”を代表する作家、A・シリトーが自らの小説を脚色した青春映画。感化院送りとなった一人の少年の反抗に託して、当時のイギリスの沈滞したムードに反旗を翻した若者たちの姿を描き出している。クロスカントリーに出場し、ただ走る主人公にカットバックするように感化院送りになるまでの経緯が描かれていく。T・リチャードソンは「蜜の味」にも見られたリアリズム映像によってイギリスの現状に迫っており、主役の少年を演じるT・コートネイの好演とあいまって、1960年代初頭の空気を見事にすくいとった。", "1962-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIFE ON THE EDGE", "ライフ・オン・ジ・エッジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "生活必需品がパイプから供給され、食料も楽器でさえもが生きている。摩訶不思議な未来社会に住む家族の１日を描くグロテスクかつコミカルなSFホームドラマ。「キャットピープル」「キャプテンEO」などの特殊メイクで知られるT・バーマンの監督デビュー作。ダリの絵画のような日常描写がすごい。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラ対ヘドラ", "ゴジラ対ヘドラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "日本特撮映画の父・円谷英二の死後久々に登場した一編で、全編がグロテスクなムードに覆われた、シリーズ中の異色作。公害によって生まれ、公害を吸収してどんどん成長し変貌する新怪獣ヘドラが、ゴジラと対決する。ヘドラの強烈なキャラクターはゴジラをしのぐものがあり、興行的にもスマッシュ・ヒットを記録した。", "1971-07-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("けものがれ、俺らの猿と", "けものがれ、俺らの猿と", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "町田康原作の同名小説をDragon　Ash、スガシカオなどのミュージック・クリップを手掛けてきた映像作家・須永秀明が映画化。映画初監督ながら、クールでドライブ感あふれるカメラワークでシュールな町田康ワールドへと誘い込む。廃人寸前の脚本家、佐志が怪しげな映画の脚本依頼を引き受けたことから、災難が次々と舞い込んでくる、という物語。Dragon　Ashの降谷建志が父親（古谷一行）譲りの演技を披露したり、浅野忠信のPEACE PILLが音楽で参加するなど、音楽マニア泣かせな仕上がり。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLIND FURY", "ブラインド・フューリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "盲目だが居合の達人、必殺の仕込み杖で悪人どもをバッタバタ。ご存じ、“座頭市”シリーズをアメリカ映画流に翻案した、R・ハウアー主演のアクション。ショットガンで暴れまくるスカーフェイスたちを相手に、ベトナム帰りの盲目の男ニックが巧みな剣さばきで戦いを挑む。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Quintet", "クインテット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "　時は未来。地球上は雪で覆いつくされ、新たな氷河期を迎えていた。そんな不毛の地に生きる人間たちが楽しんでいたのは、クインテットと呼ばれるゲーム。それは、敗北者が自らの命を投げ出さなくてはならない死のゲームだった。流れ者の男エセックスもそれに加わるが……。ロバート・アルトマン監督が国際的キャストを揃えて作り上げたSFスリラー。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("電撃戦隊チェンジマン　シャトルベース！危機一髪！", "電撃戦隊チェンジマン　シャトルベース！危機一髪！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 20, "スーパー戦隊シリーズ第9作「電撃戦隊チェンジマン」のオリジナル劇場版第2弾。チェンジマンのシャトルベースが、宇宙獣士ドドンに奪われた。ドドンの攻撃によって剣たち4人が海へ投げだされ、ひとり船内に残されたさやかがマーメイドに変身してドドンと激闘を繰りひろげる。そんな中、ドドンが何らかの電波に反応して弱っていることに気づいたさやかは、その電波の発信源を特定するよう剣たちに依頼する。", "1985-07-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIG PARADE", "ビッグ・パレード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "第一次世界大戦下のフランス。とある農村に駐留を命ぜられた若いアメリカ兵ジムは、美しい村娘メリサンドと恋に落ちる。が、進軍命令はこの若い二人を無情に引き裂いてしまう……。戦前の大監督ヴィダーの代表作となった無声映画超大作。戦闘場面のスペクタクル描写は、今見ても圧倒的な迫力である。", "1925-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("T−BONE N WEASEL", "バディ・ハイウェイ 相棒いっちょやったろうぜ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "監獄を仮出所したT・ボーンは、生来の気のいいお調子者で小さな盗みばかりしている。一方、ウィーゼルは気が小さいが抜け目のないザキ師。“気のいい悪者”二人が出会っての珍道中が始まった。タップの名手G・ハインズとC・ロイドの共演が見どころ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　RED　VIORIN", "レッド・バイオリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "４世紀に渡り５つの国に受け継がれていった伝説の名器レッド・バイオリンをめぐる数奇な物語。1681年、イタリア。出産で妻子を失ったバイオリン職人の深い悲しみの中で、美しい赤いバイオリンが作られる。やがてオーストリアの修道院の孤児の手に渡ったそのバイオリンは、激しい愛にほんろうされるイギリスの若き作曲家、文化革命下の中国をめぐり、現代のモントリオールのオークションで一人の男性の目に留まる……。バイオリンを手にしたことで激動の運命をたどることになる人間の愛と苦悩、情熱を名バイオリニスト、ジョシュア・ベルの華麗な演奏で彩る。", "1998-01-01", "10,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BRONX WAR", "ブロンクス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "プエルトリカンの父と黒人の母を持ち、生まれ育ったブロンクスで凶悪犯罪を目の前に成長したJ・B・バスケスが、テンションの高い描写で恐るべきブロンクスの素顔を描く。バスケス監督自身も、ギャングのボス役をリアルに演じている。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIDEWALK STORIES", "サイドウォークストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "スパイク・リーと並び、N.Y.ブラック・インディーズを代表するC・レイン監督の長編第１作。マンハッタンの一角、都会の喧騒とは無縁なダウンタウン。今日を生きるのに懸命な人々が様々にパフォーマンスを繰り広げているなかに、似顔絵書きの男がいた。偶然に偶然が重なって、彼は母親に捨てられた子供の面倒を見ることになる。そんなある日、金のない彼はベビー用品を失敬しようとある店に入るのだが、そこでかつて似顔絵を描いたことのある美しい女性と再会する。淡い恋に落ちた二人。やがて子供の母親も見つかりハッピーエンドとなるが……。チャップリンの「キッド」を彷彿とさせるストーリーと、モノクロ、サイレントの映像。ホームレスの人々が抱える問題や、弱者に厳しい政策などに対し、声高に叫ぶことなく主張している作品だ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHACUN CHERCHE SON CHAT", "猫が行方不明", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "行方不明になった猫探しを通して、一人のパリジェンヌが新しい自分を発見していくキュートなコメディ。舞台は開発の進むパリ11区。メイクアップ・アーチストのクロエは夏のバカンスの間、愛猫のグリグリを人づての紹介で近所のマダム・ルネに預ける。ところが、バカンスから戻ると猫は行方不明。同居人でゲイのミシェルや友人たちの協力をあおぎ、ビラを貼ったり、聞き込みをして八方手を尽くす。クロエに好意を持つ不器用な青年ジャメルも、必死に猫を探すが見つからない。そんな中、クロエは街の人々の温かさに触れ、新たな世界、新たな自分を見つけていく。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FURTHER ADVENTURES OF THEWILDERNESS FAMILY PART２", "続アドベンチャー・ファミリー 白銀を越えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "脱・都会を果たし、雄大なロッキーの山奥で生きる一家を描くファミリー・ドラマの第２作。今回は、大自然の中で初めての冬を過ごすロビンソン一家の厳しい体験を綴る。製作者ダブスの親友の実話をもとにしただけあって、自然や動物の描写は臨場感に富み、説得力がある。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Glass House", "グラスハウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "事故で両親を失った姉弟が、養父母の家で恐ろしい体験をするサスペンス・スリラー。突然の交通事故で、両親を一度に失ってしまったルビーと弟レット。十分な遺産を相続した二人は、昔、隣に住んでいたグラス夫妻の豪邸で暮らすことになる。最初は何の心配もなかったルビーだが、時間がたつにつれて養父母の怪しげな行動に不審を抱き始める。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どこまでもいこう", "どこまでもいこう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "「月光の囁き」に続く塩田明彦の監督第２作だが、前作よりもこちらの方が処女作らしい初々しさに満ちている。親友同士の小学５年生二人。固い絆で結ばれていた彼らが、それぞれ別な同級生との交流によって新たな世界と価値観を発見していく姿が、奇をてらわずに見つめられていく。あるときはダラけ、あるときは凛とするという緩急見事な演出。少年たちの日常がかけがえのない瞬間として映し出され、誰もが覚えのある“別れの季節”が実感として迫ってくる。刻一刻と変わりいく時間をのびのびと体現した主演二人が、いずれも素晴らしい。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’E´TOILE DE MER", "ひとで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "フランスの詩人R・デスノスと、アメリカの写真家M・レイが作り上げた、文字通りのシュールレアリスム映画。ソフト・フォーカスや曇りガラスを通しての撮影などを駆使し、恋人たちの愛と後悔についての詩を、見事に映像化している。", "1928-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Over the Border", "約束", "", "", "http://image.eiga.k-img.com/images/movie/53081/315x210.jpg?1868", 110, "南北の国境に引き裂かれた男女の悲恋を描いた悲劇。結婚の約束をした恋人ヨナを北朝鮮に残し、韓国へと渡った青年ソノ。ヨナの脱北資金を貯めようと必死に働いていた彼の元に、彼女が北で結婚したという知らせが届く。絶望したソノは別の女性と結婚。それから数年後、新しい家族と共に食堂を経営していたソノは、ヨナが脱北して韓国にいることを知る。主演は「リベラ・メ」のチャ・スンウォンと「台風太陽」のチョ・イジン。", "2007-11-23", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE SOUFFLE AU COEUR", "好奇心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "舞台は、1954年。ブルジョワ家庭の思春期の少年を主人公に、家庭生活の機微を繊細に描いた秀作。前半は初々しさや時代感、後半は心臓病療養の湯治場での大好きな母親との日々などが映し出される。チャーリー・パーカーの挿入曲と、近親相姦のエピソードが話題となった。", "1971-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちょちょぎれ", "ちょちょぎれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "少し変わった3組のカップルが織りなす恋愛模様をつづった人間ドラマ。出会った人と必ず握手するという変わり者の青年・津野田。電気工として働く津野田は、顧客の女性・雨宮に密かに恋心を抱いていた。そんなある日、彼は雨宮の家で、きよみという女性と出会う。きよみの強引さに押され、彼女と付きあいはじめる津野田だったが……。キャストには「ラストゲーム　最後の早慶戦」の片山享、「愛の言霊」の齋藤ヤスカら注目の若手俳優を起用。", "2010-09-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Desert Flower", "デザートフラワー", "", "", "http://image.eiga.k-img.com/images/movie/55681/315x210.jpg?4356", 127, "数々の一流ファッション誌の表紙を飾った世界的トップモデル、ワリス・ディリーの自伝「砂漠の女ディリー」をワリス本人による監修のもと映画化。ソマリアの遊牧民家庭に生まれ、貧しい少女時代を送ったワリス。やがて故郷を飛び出し、大都会ロンドンでトップモデルへと転身を遂げるが、その胸中には衝撃的な過去が秘められていた。エチオピア出身の現役トップモデル、リヤ・ケベデが主人公を熱演する。", "2010-12-25", "7,77,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花嫁の寝言", "花嫁の寝言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "トーキーの先駆者として当時売れっ子だった五所平之助が手掛けた娯楽作品で、大ヒットを記録した作品。大学時代の５人の親友のうち、小村の妻の君子が甘い寝言を言うという噂を聞きつけ、残りの４人が酒の勢いで小村の家に乗り込む。新婚宅で酒を飲み交わす男たちを尻目に、泥棒が入り込み……。", "1933-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTERCEPTOR", "インターセプター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "レーダー探知不可能のハイテク戦闘機ステルス。この最新鋭機をめぐる、残忍な武器商人とアメリカ空軍の精鋭パイロットによる熾烈な攻防が描かれる。手に汗握るクライマックスの空中戦が見もののスカイ・アクション。個性派J・プロホノフが悪役に扮して、渋みのある演技を披露する。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ORIGINAL GANGSTAS", "ホットシティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ストリート・ギャング、レベルの横行によって荒廃した町ケイリー。彼らの殺人現場を目撃した老人は警察に協力したために報復を受け、重傷を負う。老人の息子ジョンはそれを聞いて駆けつけ、市長に掛け合うが、話を受け付けてくれない。そこで彼は昔の自警団仲間であるジェイクらを呼び寄せ、レベルに立ち向かっていく。ギャングとかつての自警団という新旧の黒人グループの対決を、ヒップ・ホップのナンバーにのせて描くバイオレンス・アクション。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("展覧会の絵", "展覧会の絵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 39, "手塚治虫がセル・アニメーションの様々な様式を用いて製作した、10話の短編によるオムニバス。構成はムソルグスキー作曲、ラベル編曲の同名曲になぞらえられて、それぞれの短編が間奏曲でつながれている。工場を完全オートメーション化した工場主が、彼自身も機械として扱われてしまう第４話「工場主」のように、どのエピソードにも風刺、皮肉、ブラックユーモアがたっぷりとまぶされているのが興味深い。ただこれは欧米のアニメーションへの憧れの具体化、もしくはオマージュともとれ、その独創性を手放しで評価することはできない。第１～10話の各話タイトルは次の通り。「評論家」「人口造園師」「整形外科医」「工場主」「チンピラ」「チャンピオン」「テレビ・タレント」「禅／葬式」「兵隊」「フィナーレ」。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飛越黄昏／BEYOND THE SUNSET", "黄昏のかなたに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "夫を亡くしてからというもの、すべてを犠牲にして育て上げてきた娘が駆け落ちしてアメリカへ。母親は、どうしても娘の気ままな生き方を認めることができない。一方娘は、母への負い目と反発との板挟みに苦しんでいた。香港を愛しながらも留まることのできない娘と、取り残された寂しさを抱えながらも香港を離れられない母親。人生の黄昏を迎えた老人たちの第二の人生を歩もうとする姿が、美しい風景の中で描かれていく。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兜王ビートル", "兜王ビートル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "「いかレスラー」で異能ぶりを発揮した河崎実監督が放ったカルト・ムービー。永井豪デザインによるカブトムシをモチーフにした実在のレスラー“兜王ビートル”を主人公に抜擢。この異色のヒーローが、悪徳昆虫軍団と壮絶なバトルを繰り広げる。続々と登場する昆虫キャラが強烈。外宇宙軍の昆虫怪人軍団が大阪に突如出現、次々と人を襲い出す。だが、その前に正義に転じた外宇宙軍の兜王ビートルが立ちはだかる。", "2005-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハルフウェイ", "ハルフウェイ", "", "", "http://image.eiga.k-img.com/images/movie/53701/315x210.jpg?2768", 85, "「ロングバケーション」「ビューティフルライフ」などのヒットドラマを手がけてきた脚本家・北川悦吏子の初監督作品で、岩井俊二、小林武史がプロデュースを担当。主演は北乃きいと岡田将生。北海道の高校に通うヒロは、以前から好きだったシュウに告白され、付き合うことに。しかし、シュウが東京の大学を受験することになり、付き合い始めたばかりで離れ離れにならなければならないことに悩む2人は、次第にすれ違っていくが……。", "2009-02-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COMPETITION", "コンペティション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "国際的なピアニストを目指すポールは、最も権威のあるピアノ・コンペティションに挑戦できる最後のチャンスを迎えた。だが、顔見知りのライバル、ハイディを次第に愛し始め苦悩する……。人生で大切な“愛とチャンス”の二者択一を迫られたピアニストをドレイファスが好演。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一本刀土俵入", "一本刀土俵入", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "長谷川伸の同名戯曲の５度目の映画化。内容は、おなじみ親方から暇を出された相撲取り・駒形茂兵衛が、情をかけてくれた酌婦のお蔦に恩返しする話。茂兵衛を加東大介、お蔦を越路吹雪が演じ、マキノ雅広得意の緩急を使い分けた演出も生きている。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わんわん忠臣蔵", "わんわん忠臣蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "「わんぱく王子の大蛇退治」に続く、東映長編動画の第７作。「西遊記」と同じく原案・構成に手塚治虫を迎え、タイトル通りの動物版「忠臣蔵」が試みられた。ただ、虎のキラーが吉良上野介、犬のロック（石）が大石内蔵助のもじりであるように、その名残りも散見できる。ドラマ的には盛り上がる力作だが、仇討ちにすべて終始してしまうところが、やや味けないともいえようか。森の動物を守る母を、虎のキラーに殺された子犬のロック。彼は町へ出たあと、灯台の島の女の子に育てられる。やがて成長したロックは、町の犬たちとともに、今は動物園に君臨するキラーに、仇討ちを挑む。猛獣たちを従えたキラーを相手に犬たちは奮戦し、ついにキラーを倒すのだった。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOR　RICHER　OR　POORER", "大富豪、大貧民", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "愛が冷めきった裕福な実業家夫婦ブラッドとキャロラインが、会計士の脱税のおかげで逃亡者に。彼らがたどり着いたのは、厳格な規律の中で生きるアーミッシュの村だった。物質社会にどっぷり浸かっていた夫婦が愛を取り戻すまでのドタバタ騒動をハートフルに綴るコメディ。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太平洋奇跡の作戦・キスカ", "太平洋奇跡の作戦・キスカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "アメリカ軍が反撃に転じたため、太平洋における日本軍は敗退を余儀なくされた。アッツ島の日本軍は玉砕し、キスカ島にも危機が迫った。海軍はキスカに救出艦隊を派遣する。日本映画には珍しく陽性の戦争映画。サスペンスの盛り上げ方、特撮も迫力があり楽しめる。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD AND SAND", "血と砂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "ルドルフ・ヴァレンチノ主演で有名な作品のリメイク。セビリアの若き名闘牛士フワンは幼なじみと結婚するが、フワンはドニヤの誘惑に溺れていく……。ドニヤに扮したL・ヘイワースの妖しい魅力がふんだんに盛り込まれ、オリジナルにも負けぬ出来栄え。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("８２〈ワニ〉分署", "８２〈ワニ〉分署", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "グラマラスな肢体と超一流の射撃技術を持つ三夏と、対照的にお嬢様風のリンは、時価数十億円の黄金の立像“マリアの微笑み”盗難事件の捜査に当たるが……。通称ワニ分署と呼ばれる凶悪犯罪専門の部所、警視庁82分署に所属する二人の女刑事の活躍を描いたエロティック・アクション。かつて曾根中生監督で映画化された、篠原とおるの同名劇画の再映画化。主演コンビのセクシーなボディとアクションが見もの。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MIGHTY", "マイ・フレンド・メモリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "幼少のころに受けた心の傷と学習障害に悩む中学一年のマックスは、体は大きいが自分の殻に閉じ込もったままのいじめられっ子。そんな彼の前に現れたのが同級生のケビンだった。難病に冒され、余命少ないことを知りながら明るく、前向きに生きている彼はマックスの人生を少しずつ変えていくことになる。だがある日、刑務所に入っていたマックスの父ケニーが仮釈放され、祖父母と暮らすマックスを誘拐してしまう。それを知ったケビンは彼を救出に向かうのだった……。ハンデを背負った少年たちの物語を悲哀という形ではなく、美しい友情の物語として描き出した感動作。ケビンの母親役にS・ストーン。少年たちのピンチを救う心優しいアバズレ女を「X−ファイル」のG・アンダーソンが演じている。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMAZING STORIES", "世にも不思議なアメージング・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "アメリカの人気TV番組『アメージング・ストーリーズ』の中から、スピルバーグ、ディア、ゼメキスの監督した３本をピックアップし、劇場用に再編成したもの。ファンタスティック、コミカル、そしてミステリアスな映像世界が繰り広げられていく。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIGHT HOUSE", "フライトハウス ゾンビの生贄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "イタリア映画お得意の残酷ホラー２話で綴るスプラッタ・オムニバス。血に飢えた悪魔が住む館を興味本位で訪れた若者たちが、血祭りにあげられる「ゾンビの生贄」。アートスクールの地下にある開かずの間に、徐々に引きつけられてしまう少女の恐怖を描く「呪いの美少女」の２話で構成されている。", "1990-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Millenium 3: The Girl Who Kicked the Hornet's Nest", "ミレニアム3 眠れる女と狂卓の騎士", "", "", "http://image.eiga.k-img.com/images/movie/55361/315x210.jpg?2993", 0, "スウェーデンの作家スティーグ・ラーソンによる世界的ベストセラー小説を3部作で映画化した最終作。天才ハッカーのリスベットは、ジャーナリスト殺しという無実の罪を晴らすため、命がけで真犯人を見つけ出す。瀕死の重傷を負ったリスベットは逮捕されるが、敏腕ジャーナリストのミカエルらと協力し、法廷で政府公安警察への反撃を開始する。", "2010-09-11", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EIGHT LEGGED FREAKS", "スパイダーパニック！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「デイ・アフター・トゥモロー」のR・エメリッヒがプロデュース、巨大クモが街を襲うパニックムービー。有毒廃棄物により研究所のクモが次々に巨大化！　町の人々はショッピング・モールに立てこもり反撃のチャンスをうかがうが、何万匹もの大群と化したクモたちは容赦なく襲いかかって来て……。古典的B級テイスト漂う題材ながら、笑いとアクション、最新VFXを絶妙に取り入れ、新しい感覚の作品に仕上げている。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("番場の忠太郎", "番場の忠太郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「百万両の壷」「河内山宗俊」など山中貞雄作品の重要な脚本家として知られる三村伸太郎が、長谷川伸の原作『瞼の母』を脚本化した、中川信夫の中期の作品。消息の分からない母を探しつつ旅を続ける正義漢の忠太郎が、次々と現れる刺客と戦い、ついには母と再会する。だが母は彼を突っぱねて……。音楽は現在の日本現代音楽協会の前身、新興作曲家連盟の発起人で委員長を務めた清瀬保二。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A GUIDE FOR THE MARRIED MAN", "プレイラブ48章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "結婚生活12年のマジメ男ポールは、悪友のエドから浮気の手ほどきを受ける。アリバイの作り方、香水の匂いの消し方など、48章のルールを教え込まれたポールは、さっそく美人を連れてモーテルへ。だがその時、部屋の外で悲鳴が聞こえる。女と密会していたエドが妻に発見されたのだ。驚いたポールは、浮気相手に妻子の写真を見せ、彼女にも幸せな結婚をすすめ、ほうほうの態で愛妻の元へ帰っていくのであった。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伴我闖天涯／WILD SEARCH", "いつの日かこの愛を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "荒れた生活を送る刑事のルウ。ある犯罪組織を追って取引現場を急襲した際、組織の女が死亡。捜査のため女の妹に会ううち、二人の間に恋が芽生える。監督のラム、主演のユンファが共同で設立した製作会社“シルバーメダル”の第１回作品。ユンファ、チャンともに好演のせつなくも心温まるラブ・ストーリー。「インファナル・アフェア」シリーズの監督A・ラウが、本作で撮影を担当している。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それでもボクはやってない", "それでもボクはやってない", "", "", "http://image.eiga.k-img.com/images/movie/1661/315x210.jpg?1374", 143, "「Shall we ダンス？」の周防正行監督、11年ぶりの新作は、痴漢冤罪事件をテーマにした社会派法廷ドラマ。通勤ラッシュ時に電車に乗っていたフリーターの徹平は、電車を乗り換える際に女子中学生から痴漢行為を問いただされ、そのまま駅事務所、そして警察へと連行される。警察、検察の執拗な取り調べにも、徹平は「ボクはやってない」と答え続けるが……。主演の徹平に加瀬亮、共演に瀬戸朝香、役所広司ら。", "2007-01-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・社長行状記", "続・社長行状記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "仏・チオール商会と提携して不況を乗り切った栗原サンライズ。さらなる協力体制を進めようと、チオール日本支配人・安中を下へもおかぬ接待また接待。ところが安中という男、とんでもないドンファンだった。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("化身", "化身", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "中年文芸評論家の愛人となったヒロインが、男との付き合いによって徐々に変身を遂げ、さらには自立へと目覚めていく過程を描いた作品。宝塚出身のヒロイン・黒木瞳が大胆なヌードを見せて話題に。原作は『日本経済新聞』に連載された渡辺淳一の同名小説。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しがらきから吹いてくる風", "しがらきから吹いてくる風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "狸の焼き物で知られる窯業の里、信楽を舞台に、そこで働く知恵おくれの就労者にスポットを当てたドキュメンタリー。19歳になる若者が就職にチャレンジするところから始まる本作は、彼を取り巻く勤続10年に及ぶ先輩たちの職場での人間模様を描き、知恵おくれの人たちの就労の意味を浮き彫りにしていく。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新宿事件", "新宿インシデント", "", "", "http://image.eiga.k-img.com/images/movie/54441/315x210.jpg?3773", 119, "ジャッキー・チェン製作・主演の下、アジア各国で活躍するスタッフ・キャストが集結したバイオレンスドラマ。行方不明の恋人を探すため、中国から日本に密入国した鉄頭。新宿・歌舞伎町にたどり着いた彼は、同じく不法滞在の仲間たちと共に過酷な日々を送っていたが、やがて裏社会へと足を踏み入れ……。監督は「ワンナイト・イン・モンコック」のイー・トンシン。共演は「女帝」のダニエル・ウー、「墨攻」のファン・ビンビン、竹中直人ほか。", "2009-05-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神龍賭聖之旗開得勝", "キング・オブ・ギャンブラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ゴッド・ギャンブラー〈賭神〉の息子ヤムは、ライバルのギャンブラーと因縁の決着をつけるため、カードの秘技を付けようと猛訓練をしていた。一向に上達しないヤムを見兼ねた彼の妻は、天才ギャンブラー〈賭聖〉に夫のコーチを頼むが……。「ゴッド・ギャンブラー」シリーズの一編。香港を代表する二大俳優、L・カーファイとT・レオンの共演で描いたギャンブル・コメディ。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使の恍惚", "天使の恍惚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "若松孝二がパレスチナから帰国後“自分の身を張って闘える奴、本気で孤立できる奴、個的な闘いを個的に闘える奴など、孤立した精鋭こそが世界を変える、世界を創る”というテーマのもとにATGと初提携して監督した１千万円映画。革命軍“四季協会”の秋軍団は、首都総攻撃を期し米軍基地襲撃、武器奪取作戦を敢行する。パレスチナ帰国後、政治的にも性的にも、より一層過激化した若松孝二作品だけに、公開前からトラブルが予想された。公開時には過激派による新宿ツリー爆弾事件が発生し、東宝系での上映は中止され、アートシアター新宿文化のみの公開となった。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("塩狩峠", "塩狩峠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "クリスチャン作家・三浦綾子の『氷点』と並ぶ代表作の映画化。身を挺して列車事故から乗客の命を救った青年の実話がモデルだが、宗教臭さは強くない。事故までに、主人公が通過した信仰への、珠玉のエピソードで綴る感動作。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛よ人類と共にあれ", "愛よ人類と共にあれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 180, "島津保次郎監督のサイレント時代の末期に位置する作品。大富豪でいくつもの会社を経営する山口鋼吉と、その息子や婿との間に生起する感情のもつれや和解を描いたメロドラマで、前後編２部で構成された大作。サイレント期のハリウッドで特異なキャラクター俳優として活躍した上山草人の帰朝記念映画として製作された。監督協力者として豊田四郎、吉村公三郎らがクレジットされている。", "1931-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rang-e khoda", "太陽は、ぼくの瞳", "", "", "http://image.eiga.k-img.com/images/movie/1841/315x210.jpg?2929", 90, "モハマドはテヘランにある盲学校に通 う8歳の少年。母を失い、心を痛めながらも一生懸命生きている。彼は手で、植物や風や小鳥などの自然を見ようとする。夏休みになり、モハマドは家族の住むカスピ海沿岸の村に戻ってきた。妹達の学校にも行き、故郷での夏休みを楽しむモハマド。しかし意中の女性との再婚を望む父は、息子の将来を気にして、モハマドを盲目の大工のところに修行に出そうと考える。", "2000-05-13", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FANTO＾MAS CONTRE SCOTLAND YARD", "ファントマ ミサイル作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "“ファントマ”シリーズの第３作は怪談編。舞台をスコットランドの古城に移し、降霊術に絡んでファントマの犯罪が起こる。おなじみジューブ警部は、スコットランド・ヤードとともに事件の解決にあたるが……。この作品で、麗人マレーの怪人ぶりも見納めとなった。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("重案組／CRIME STORY", "新ポリス・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "香港の不動産王に誘拐予告が送られ、身辺警護を固める警察をあざ笑うかのように犯人は簡単に誘拐を成功させる。警察の特捜班のエディは怒りと屈辱感に燃え、犯人逮捕を心に誓うが……。命にかかわるのでは、と心配になるほど危険なスタントを披露するジャッキー・チェン主演の異色活劇。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CANNABIS", "ガラスの墓標", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "一人の女性を愛したために破滅へと向かう殺し屋の姿を、S・ゲンズブールとJ・バーキンの主演で描く。ニューヨーク・マフィアの殺し屋セルジュは、フランスの麻薬組織エメリー一家の壊滅のためパリへと向かう。到着早々、エメリーの手下が待ち受け、重傷を負ったセルジュは飛行機で知り合ったジェーンに助けられる。ある日、恋に落ち愛の歓喜にひたる二人の前にセルジュの相棒ポールが現れた。彼らは組織壊滅の攻撃を開始するが、セルジュはジェーンのためにマフィアから足を洗おうと決心する……。暴力、麻薬、乱交パーティー、ファッションなど、全編に1969年という時代の匂いを散りばめた異色のフィルム・ノワール。", "1969-01-01", "2,4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AUTRE NUIT", "天使の接吻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "映画評論家から映画作家となったJ・P・リモザンの第３作。夏の避暑地に父母より一足先に発った姉弟は、やがて両親が交通事故で死んだという知らせを受ける。復讐を誓った姉弟は犯人を突き止めるが……。J・デルピーの新鮮な魅力に満ちた好編である。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUTURE ZONE", "メガロコップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "個性派俳優D・キャラダインが主演する近未来アクション。21世紀後半、凶悪化する犯罪の前に警察は名ばかりのものとなり、犯人逮捕は賞金稼ぎのハンターたちが行っていた。その一人ジョンは腕利きのガンマン。ある日ジョンの前に謎の若者が現れる。なんと彼は父を救うため未来からやって来たジョンの息子だった。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE RAPACE", "ベラクルスの男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1938年、独裁政治を倒すため初代大統領の孫を擁して立った反政府運動リーダーに大金で雇われて、南米の某国へやって来た殺し屋の物語。しかし現大統領暗殺に成功した後、彼は口封じのためリーダーに命を狙われるはめとなり、裏切り者への闘志を爆発させる。主演のヴァンチュラ以外はすべて現地俳優を使い、オール・メキシコ・ロケで描く、ジョヴァンニ＆ヴァンチュラの代表作。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents すき　「the place named」", "桃まつり presents すき　「the place named」", "", "", "http://image.eiga.k-img.com/images/movie/57881/315x210.jpg?3951", 36, "若手女性監督による映画製作・上映集団「桃まつり」が、「好き」をテーマに女性監督たちが撮り上げた短編作品を上映する「桃まつり presents すき」の1本。ソートン・ワイルダーの戯曲「わが町」をもとに、田舎町に生きる少女のある一日を、「わが町」第3幕の舞台稽古をしている劇団の姿と交錯させながら描く。監督は、東京芸術大学の大学院に在籍中の小森はるか。「壱のすき」として「帰り道」（竹本直美監督）、「フィガロの告白」（天野千尋監督）と同時上映。", "2012-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EIGHTEENTH ANGEL", "オーメン18 エンジェル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "あの「オーメン」シリーズの第１作、第２作の脚本を担当したD・セルツァーが再び悪魔と人間の最終対決を描き出したホラー作品だが、シリーズとは別物。母の謎の自殺で心を痛める15歳の少女ルーシーはある日、モデルの素質を見いだされ、父ヒューとともにイタリアへ撮影旅行に出かける。しかし、恐ろしい呪いをかけられたルーシーは不審な行動をとり続け、カメラマンたちはヒューと彼女を引き離そうとする。そんな中、いかがわしい教会で子供たちの死体を発見したヒューはルーシーに危険が迫っていることを察知する。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴーカイジャー　ゴセイジャー　スーパー戦隊199ヒーロー大決戦", "ゴーカイジャー　ゴセイジャー　スーパー戦隊199ヒーロー大決戦", "", "", "http://image.eiga.k-img.com/images/movie/56021/315x210.jpg?2199", 81, "1975年の第1作「秘密戦隊ゴレンジャー」から35作目「海賊戦隊ゴーカイジャー」まで、歴代のスーパー戦隊シリーズが総出演する劇場版。「ゴーカイジャー」のもつ特殊能力「二段変身」を駆使して歴代34戦隊、199人のスーパー戦隊をスクリーンに集結させる。広大な宇宙に冒険とロマンを求める5人の若者たちが、恐怖と暴力で支配する宇宙帝国と対立しながら、伝説の宇宙最大の宝を求めて大冒険を繰り広げる姿を描く。", "2011-06-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("female〈フィーメイル〉", "female〈フィーメイル〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "人気のショートフィルム・オムニバス・シリーズ「Jam Films」が新趣向を打ち出し、人気の女流作家５人の書き下ろし小説を、５人の気鋭監督の手で映画化したユニークなプロジェクト。原作・唯川恵、監督・松尾スズキ、主演・高岡早紀のコラボなど、他ではありえない顔合わせは必見。工場で働く正子は、有給休暇中に訪れた南国で好きな人の髪の毛を入れると、その人の夢が見られるという香炉を手に入れる。（「夜の舌先」）", "2005-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAGAI UNITED", "アフロサッカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "無名で背が低く、サッカーの本格的トレーニングも知らない。そんなチームが国王杯に出場!? その爽快感から、本国タイでその年の興行収入第2位を記録した大ヒット作が日本に上陸。借金まみれの監督をはじめ、個性豊かな面々に爆笑必至！ 借金取りに追われるサッカーの審判パオトゥーは、すばらしい運動能力を持った小さなサッカーチームと出会う。彼は借金返済のチャンスとばかりに、彼らを国王杯に出場させるが……。", "2006-06-24", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRIAL AND ERROR", "Mr.ダマー2 1/2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「ジム・キャリーはMr.ダマー」でジムの相棒を演じたJ・ダニエルズが、TVの人気コメディアン、M・リチャーズと組んだコメディ。独身最後のパーティーで羽目を外し過ぎた弁護士チャールズ。翌日、人生を左右する裁判が開かれようとしているのに、彼は強度の二日酔いのうえ、薬の飲み過ぎでフラフラ。それを見た友人の売れない俳優リチャードは、チャールズに代わって法廷に立つのだが……。裁判所で起こる数々の騒動を、「いとこのビニー」のJ・リンが面白おかしく描いていく。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勝手にしやがれ!! 逆転計画", "勝手にしやがれ!! 逆転計画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "競馬狂の父親を持つタバコ屋の看板娘サツキに惚れこんだ雄次は、彼女にハワイ留学をプレゼントしようと考えるが、金はなくギャンブルにも負けてばかりいた。そんな時1000万円が入った鞄を抱えたヤクザが雄次の前に倒れ込んでくるのだが……。失った“ツキ”を取り戻そうとする雄次の運命はいかに……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　COCKETTES", "ザ・コケッツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1960～70年代のヒッピー・ムーブメントを背景に登場した劇団“ザ・コケッツ”をめぐるドキュメンタリー。ゲイの男性、女性、子供らが、天然色のド派手な衣装とメイクで乱舞するステージは強烈そのもの。ファッション界や音楽シーンにも多大な影響を与えた伝説的集団の全貌が明らかに！ 奇抜なパフォーマンスで多くの有名人をも魅了したヒッピー劇団“ザ・コケッツ”。悪趣味映画の帝王ジョン・ウォーターズらが、彼らの魅力を語る。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHARKY’S MACHINE", "シャーキーズマシーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "B・レイノルズが、下町を舞台に、ハードな刑事として活躍するアクション大作。レイノルズ自身、ウィリアム・ディールの原作にほれ込み、製作・監督・主演の３役を兼ねた。アトランタ市警麻薬課の刑事シャーキーは、白昼、市バスの中で犯罪者と凄絶な銃撃戦をやり、風紀課に格下げになる。だがバイタリティーあふれる彼は、同じく市警本部の落ちこぼれ刑事たちをパワーアップさせ、強力チーム、シャーキーズ・マシーンを結成、敢然と悪に立ち向かっていく。痛快なバイオレンス描写と、刑事たちの温かな人間描写、シルヴァらの脇役たちの好演など、一級の娯楽作品に仕上がっている。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Caramel", "キャラメル", "", "", "http://image.eiga.k-img.com/images/movie/53991/315x210.jpg?3169", 96, "「世界で最もパワフルなアラブ人100人」で女性トップに選ばれたナディーン・ラバキーが監督・脚本・主演を務め、レバノンの女性たちを繊細に描いたヒューマンドラマ。不倫の恋に悩むラヤール、フィアンセに過去を隠すニスリン、美しい女性に心惹かれるリマ、女優志望のジャマル、人生を諦める初老のローズ。ニスリンの結婚式を前にそれぞれの人生が動き出す。", "2009-01-31", "55,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PEAU DOUCE", "柔らかい肌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "映画評論から出発し“フランス映画界の墓掘り人”の異名をとった、ヌーヴェル・ヴァーグの旗手トリュフォーが、“伝統的フランス映画の美学”を見事に画面に定着させた秀作。40歳半ばの文芸評論家ピエールは妻と幼い娘との３人暮らし。ある日、文学講演のために乗ったリスボン行きの旅客機の中で、若く美しいスチュワーデスと会い、たちまち恋に落ちる。平和だが刺激のない日常を捨て、激しい恋に身を焦がそうとする中年男の苦悩と焦燥が、鮮烈にスリリングに浮き彫りにされる。妻とも恋人とも別れ、ただ一人食事する彼に妻が向ける銃口……意外な、そしてショッキングな幕切れに、監督の鋭く深い“女性心理”洞察がうかがえる。", "1964-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love You 10,000 Years", "一万年愛してる", "", "", "http://image.eiga.k-img.com/images/movie/57601/315x210.jpg?1896", 108, "台湾の人気アイドルグループ「F4」のビック・チョウと、台湾でも活躍するモデルで女優の加藤侑紀が主演したラブコメディ。語学留学で台湾にやってきたみかんは、だらしない性格の台湾人男性チーホンと出会う。いずれ日本へ帰るみかんと、いつも恋愛が長続きしないチーホンは、3カ月という期間限定の恋愛契約を結び、束の間の恋愛関係を楽しむ。やがて契約期間が終わり別れた2人だったが、離れて初めて互いへの思いに気がつき……。監督は台湾在住の日本人で今回が長編デビューの北村豊晴。", "2012-01-05", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIG BOUNCE", "ビッグ・バウンス", "", "", "http://ecx.images-amazon.com/images/I/61HW5R1C2DL._SL210_.jpg", 88, "「ジャッキー・ブラウン」の人気作家E・レナードの小説を映像化したコメディ調の犯罪劇。クセ者たちの思惑が交錯し、事態が思わぬ方向に進んでいくドラマはスリリングで、先の読めない魅力がある。レナード作品の妙味であるユーモアに富んだセリフも盛り込まれ、息の合った会話に思わずニヤリ。新天地を求めてハワイへとやって来た詐欺師ジャック。極悪建築業者リッチーをハメようと決意した彼は、リッチーを目の敵にする地方判事を巻き込み、一大作戦を敢行するが……。", "2004-11-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スティル・アライブ", "スティル・アライブ", "", "", "http://image.eiga.k-img.com/images/movie/54491/315x210.jpg?3902", 80, "「殺人に関する短いフィルム」や「トリコロール」3部作で知られるポーランドの巨匠・クシシュトフ・キエシロフスキーの没後10年を記念して製作された記録映画。学生時代のドキュメンタリー作品にさかのぼり、20本以上の作品について、キエシロフスキー自身の貴重な声と、アニエスカ・ホランド、イレーヌ・ジャコブ、ジュリエット・ビノシュといった監督とゆかりのある映画監督や女優たち、共に脚本を書いていたクシシュトフ・ピェシェビチらが登場し、その人物像に迫る。", "2009-06-20", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜が崩れた", "夜が崩れた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "暴力団根絶のための捜査に奔走する若い刑事とその恋人。だが、恋人の兄はヤクザで殺人の容疑者になっており、北海道へ逃げていたのだった……。孤児同然に生きてきたヤクザな兄、その妹と、彼女を愛する正義感にあふれる刑事との関係を情感豊かに描いた作品。さらに、後半はサスペンスに満ちた展開が……。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ROOKIE", "ルーキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "弟の死に責任を感じ常に何かにおびえているような若手刑事と、何ものをも恐れぬタフなベテラン刑事がコンビを組んだ。初めは息の合わない二人だったが、若手（ルーキー）は不屈の精神を少しずつ学んでいく……。“ダーティハリー”そのままのイーストウッドとC・シーンの共演は、まさに映画ファンの夢。コミカルな味をにじませながらパワフルなアクションが全編に弾けた。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BARAN", "少女の髪どめ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「運動靴と赤い金魚」のマジッド・マジディ監督が紡ぎ上げた詩的な愛のドラマ。アフガン人少女に恋した少年のひたむきな思いが、イランにおけるアフガン難民の現実を背景に綴られていく。テヘランの建築現場にやってきた新入りの男の子ラーマトが、実は可憐な少女だと知ってしまった青年ラティフ。彼女に心奪われた彼は必死にお節介を焼き始める。だがある日、不法労働者の調査が入り、彼女は姿を消してしまう。", "2001-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES (".45", "ポイント45", "", "", "http://image.eiga.k-img.com/images/movie/33891/315x210.jpg?1254", 96, "「バイオハザード」シリーズのミラ・ジョボビッチがニューヨーク暗黒街で生きる拳銃密売人を熱演し新境地を切り開いたサスペンス・ドラマ。愛人のアルと共に拳銃や盗品類を売りさばき、その日暮らしの生活を送っていたキャット。ある日彼女は、アルに内緒で取引を行なったことが原因で、彼から徹底的に痛めつけられてしまう。彼女は友人たちの忠告も聞かずにアルとの関係を続けるが、実はその裏で、ある罠を仕掛けていた……。", "2007-03-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOO HOT TO HANDLE", "あなたに恋のリフレイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "甘い声と美しくセクシーな姿で男を虜にするクラブ歌手、ヴィッキーとアメリカ屈指の大企業の御曹子でハンサムなプレイボーイ、チャーリー。この二人が繰り広げる結婚、離婚、結婚、そしてまた離婚……。合計４度にわたって同じ相手と結婚した男と女の、摩訶不思議な仲を描いたファッショナブルなラブ・ストーリー。スタンダード・ナンバーを歌うベイシンガーにも注目。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUDREY ROSE", "オードリー・ローズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "広告会社に勤めるビルはある男から、“あなたがたの娘アイビーは、11年前に死んだ自分の娘オードリーの生まれ変わりだ”といわれた。やがてアイビーにその兆候が表われる……。転生現象をテーマにしたオカルト映画の佳作。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ORDER", "ファイナル・レジェンド／呪われたソロモン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "アクション俳優ヴァン・ダム主演による冒険活劇。美術品専門の泥棒ルーディは、秘宝の聖典を追いかけて消息を断った父を捜すためにイスラエルへ。そこには聖典を狙う密教団オーダーの陰謀が待ち受けていた。現地の謎の女性ダリアの助けを得て、ルーディはオーダーの恐ろしい陰謀に迫る。マーシャルアーツの立ち回りはもちろん、ダテ男風のタフガイを演じたヴァン・ダムの新味に注目。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シーズ・レイン", "シーズ・レイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ピュアな語り口が若い女性の間で評判を呼んだ平中悠一の同名小説を、繊細なタッチで映像化。お互いに好意を抱きつつも微妙な友情関係を続ける高校生のユーイチとレイコ。そんな折、ユーイチの幼なじみであるユウコが美しく成長して海外から帰国。急接近するユーイチとユウコに、レイコの心は揺れ動く。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("孤男寡女", "Needing You", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "香港のスター、サミー・チェンとアンディ・ラウの共演によるロマンティック・コメディ。20代半ばのOLキンキーは、男運がなく失恋ばかり。そのたびに情緒不安定になって社内で騒動を起こし、上司のアンディと口論する始末だ。ところが、恋にも仕事にも一生懸命で健気なキンキーに、アンディは次第に惹かれ始める。そんな彼の気持ちに気付かないキンキーは、突然現れた青年実業家に心揺れ動くのだが……。", "2000-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Brooklyn's Finest", "クロッシング", "", "", "http://image.eiga.k-img.com/images/movie/55371/315x210.jpg?1335", 132, "引退を1週間後に控えたベテラン警官エディ（リチャード・ギア）、麻薬捜査課の刑事サル（イーサン・ホーク）、覆面捜査官のタンゴ（ドン・チードル）、そして伝説のドラッグディーラー・キャズ（ウェズリー・スナイプス）が、ニューヨーク・ブルックリンを舞台に繰り広げる犯罪群像劇。監督は「トレーニング・デイ」「ザ・シューター／極大射程」のアントワン・フークア。共演にウィル・パットン、リリ・テイラー。", "2010-10-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DONA FLORE SEUS DOIS MARIDOS", "未亡人ドナ・フロールの理想的再婚生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "カーニバルのさなか、心臓発作を起こして死んだ精力絶倫の夫。未亡人は、うって変わって実直な男と結婚するが、なんと前夫が裸の亡霊となってよみがえる。未亡人は、身も心も満足というファンタスティック・コメディ。ブラジル女性のバイタリティーあふれる快作。", "1976-01-01", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乱れる", "乱れる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "松山善三のオリジナル脚本を成瀬巳喜男監督が演出した名作。子供もいないままに夫に死別して以来、嫁ぎ先の一家をきりもりしてきた未亡人が、年の離れた義理の弟の純粋な愛の告白に揺れ動く姿を描く。かつては批判の対象にもなったが、横幅を十分に使いきらない禁欲的なワイドスクリーンの用法が見事な効果をあげ、一つ屋根の下に住み、互いに意識し合いながら周囲から孤立する男女が広い部屋の中央にぽつんと立ちつくす姿など、たぐいまれな緊張感に満ちている。複雑な女心を表現する高峰の演技はいつもながら素晴らしく、当時、人気絶頂期の青春スター・加山雄三の見違えるような繊細さも魅力である。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Mari de la Coiffeuse", "髪結いの亭主", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "床屋の女主人が大好きだったアントワーヌ少年は、将来、理髪師と結婚することを決心する。そして中年になった彼は、美しい理髪師マチルドという理想の相手を見つけ求婚する。2人は幸せな日々を過ごすが……。監督は「仕立て屋の恋 」「橋の上の娘」のパトリス・ルコント、音楽は「ピアノ・レッスン」のマイケル・ナイマン。出演は、「パリ空港の人々」のジャン・ロシュフォールほか。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TERRORE", "白昼の暴行魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "３人組の銀行強盗が逃げ込んだのは、尼僧と修道院生徒しかいない女だけの別荘だった。男たちは野獣のような本性をあらわにして、女中を殺し女生徒はもちろん尼僧までも犯しまくるが……。追い詰められた女性たちが反撃を開始するサスペンス・ドラマ。ビデオ発売時タイトルは「狼たちの夏」。", "1977-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TARO！ TOKYO魔界大戦", "TARO！ TOKYO魔界大戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "おとぎ話の『桃太郎』を現代風にアレンジしたSFアクション。“桃太郎的素質”を持った音楽好きの中学生・太郎が、新興宗教団体“信輝会”の陰謀によって現代に甦った太古のオニに立ち向かう姿を描く。特殊視覚効果を駆使したクライマックスの対決シーンが見もの。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALMOST PREGNANT", "タニア・ロバーツの僕のワイフはレンタル中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "リンダとチャーリーは人もうらやむ幸せな夫婦だったが、子供ができないことが唯一の悩み。調べてみると、夫チャーリーの方に問題が。そこで悩んだ末、夫公認で間男を募集することにした。楽しくも複雑に入り乱れた毎日が始まった……。監督のM・デルイーズは、「キャノンボール」などで知られるベテラン・コメディアン、ドム・デルイーズの息子である。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAVAGE HARVEST", "マン・ハンティング 人間狩り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "人間に襲いかかる猛獣の恐怖を描いたパニック映画。ケニヤ奥地で大干ばつが続き、猛獣が餌を求めて人里へ下りてきた。ケイシーが別れた妻や子供を心配して農園に来てみると、既に20数頭のライオンが包囲していて……。L.A.の自然動物園の人気者たちをブラジルへ輸送して撮影。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ムルデカ・17805", "ムルデカ・17805", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "太平洋戦争の敗戦後もインドネシアに残り、現地の“ムルデカ（独立）”のために戦った日本軍兵士たちの死闘が展開する戦争映画。旧宗主国のオランダ、イギリス軍を敵にまわしたゲリラ戦を、現地の兵士との友情の逸話をからめて描く。軍の全面協力を得て撮影された戦闘シーンは圧巻。なお、数字の17805は独立宣言文に記された日本の皇紀で、独立記念日である2605年（西暦1945年）８月17日を日、月、年の順に並べたもの。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("瘋狂的代価／OBSESSION", "狂気の代償", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「追跡者」（1987）で現代サスペンス・アクションの演出テクニックが好評だったチョウ・シャオウェンが、再び同種のジャンルに挑んだ作品。両親の離婚で今は二人だけで暮らしている青青と蘭蘭の姉妹。ある夜、妹の蘭蘭は何者かにレイプされた。二人の執念の犯人捜しが始まる……。ポルノ雑誌屋の店長役でシエ・ユアンが出演。", "1988-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JA MILUJEM，TY MILUJES", "アイラブ・ユーラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "スロバキア地方のある町に住む独身の鉄道郵便局員のピシュタとビンゴ。ピシュタは大酒飲みで女たちはまったく寄り付かず、ビンゴはビエラという女と同棲していた。ある日、ビンゴが事故で死亡、彼の子供を身ごもっていたビエラを、親友のピシュタが面倒を見ることになったが……。", "1980-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Kingdom", "キングダム　見えざる敵", "", "", "http://image.eiga.k-img.com/images/movie/34181/315x210.jpg?4085", 110, "絶対君主制の王国サウジアラビア“キングダム”を舞台に、テロをテーマに復讐の連鎖を描くサスペンス・アクション。リヤドにある外国人居住区で、300人以上が死傷する爆弾テロが発生。FBIのフルーリー捜査官（フォックス）は、現地捜査のために4人の精鋭チームを編成しテロリストが潜むサウジアラビアへと向かうが……。監督はマイケル・マンの「コラテラル」に出演するなど、俳優としても活躍するピーター・バーグ。", "2007-10-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KILLING BOX", "キリング・ボックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "南北戦争下、北軍のハーリング大尉らは、森の中ではりつけにされた大量の死体に遭遇した。妖気漂う現場で、撃滅したはずの南軍51連隊のバックルを発見した彼らは、隊の生き残りであるストレインを連れて森の探索を開始する。すると、そこには血を求めてさすらう奇怪な魔物たちの巣窟が……。西部劇と恐怖映画の要素を巧みに交ぜ合わせた、異色のホラー映画。編集に「断絶」などで知られるモンテ・ヘルマン監督が参加している点に注目。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ambulance", "アンビュランス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「マニアック・コップ」で正義の象徴である警察官を殺人鬼にしたL・コーエンが、今度は救急車を殺人カーに仕立てたホラー・アクション。病人やケガ人を助けるはずの救急車が、不気味なサイレンとともに人間たちをどこともなく運び去るという奇抜なアイデアとハデなカー・スタントが見もの。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE，CHEAT ＆ STEAL", "完全犯罪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "M・アミックが魔性のヒロインに扮したサスペンス。銀行家のポールと若く美しいその妻ローレン、かつて彼女に裏切られ投獄されたリノ。脱獄したリノがローレンの兄と偽ってポールの家にやって来たことから、それぞれの思惑が複雑に交錯する。二転三転するストーリーに妙味あり。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELL NIGHT", "ヘルナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1981年シトヘス恐怖映画祭特別賞受賞作。学生クラブの新歓行事として、古ぼけた“呪われた館”で肝だめしが行われる。館にとどまった４人の学生に襲いかかる殺人鬼。定石通りブレアが気丈に闘って逃げのびるラストのカメラワークにも注目。プロ・テニス選手のヴァン・パタンも出演している。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GORILLA BATHES AT NOON", "ゴリラは真昼、入浴す。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ロシア占領軍の少佐であるヴィクトルが病院から出ると、自分の属する隊はすでに退却してしまったあとだった。彼はベルリンを離れず放浪しているうちに、再開発中のこの街が自分にとって最適の場だと感じていく。D・マカヴェイエフが「WR：オルガニズムの神秘」「保護なき純潔」以来遠ざかっていたフィクションとドキュメンタリー、そして既存の映画を融合した“マカヴェイエフ式コラージュ映画術”へ回帰した一作。", "1993-01-01", "44,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOGVILLE　CONFESSIONS", "メイキング・オブ・　ドッグヴィル／告白", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "N・キッドマンとトリアー監督がタッグを組んだ衝撃作「ドッグヴィル」。これはその奇妙キテレツな製作現場の裏舞台を追いかけたメイキング・ドキュメンタリー。奇人変人といわれるトリアー監督が、意外に普通人な素顔を覗かせていて興味深い。家や道路を表す白い線が床に引かれた演劇のリハーサル場のような巨大スタジオ。そこには出演者たちが集められ、映画「ドッグヴィル」の撮影が行われている。セットの隅には本音やグチを言うための懺悔室が設けられ……。ビデオ、DVDタイトルは「ドッグヴィルの告白」。", "2003-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刑事物語２ りんごの詩", "刑事物語２ りんごの詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "札幌で起きた現金輸送車襲撃事件の手掛かりは、一粒のりんごの種だった。種の栽培を引き受けたりんご試験場の所員・忍と片山刑事の間に恋が芽ばえる。双葉が開いた夜、研究室が襲われ忍は命を落とす。犯人は北海道にいると睨んだ片山は札幌へ向かう……。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blissfully Yours", "ブリスフリー・ユアーズ", "", "", "http://image.eiga.k-img.com/images/movie/57731/315x210.jpg?2141", 125, "初監督作「真昼の不思議な物体」（2000）が山形国際ドキュメンタリー映画祭で優秀賞を受賞したタイのアピチャッポン・ウィーラセタクン監督の長編第2作。ミャンマーからやってきた不法労働者のミンと、そのガールフレンドのルンは森の中をさまよう。同じ頃、不倫相手の男と森に入った中年女性オーンは、姿を消した男を探すうちにミンとルンに出会い……。2012年、ウィーラセタクン監督作を一挙上映する「アピチャッポン・イン・ザ・ウッズ」にて上映。", "2002-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("細菌列島", "細菌列島", "", "", "http://image.eiga.k-img.com/images/movie/54351/315x210.jpg?3702", 0, "ウイルスパニックの恐怖を描いた「感染列島」（08）のパロディ映画。「ふ、ふるしちょふ！」という言葉と共に顔が爆発して死に至る感染病が日本中に蔓延していた。その姿がまるで“キタ国”の将軍様のようであることから、細菌テロの疑いがキタ国にかかる。日本に潜入していた将軍様の息子・正一は、昔の恋人の息子の感染を知り、救出のため立ち上がる。将軍様に竹中直人、その息子にお笑い芸人の須藤謙太朗。監督は「ALLDAYS　二丁目の朝日」の村上賢司。", "2009-04-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夕月", "夕月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "マンションの火事で看護婦の潤子はボクサーの森田に助け出される。心を惹かれ合う二人がふとした誤解からすれ違ってしまう。やがて試合で森田の目が見えなくなってしまった……。オーディションにより選ばれた森田健作が好演したホロ苦い青春映画の佳作。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN FLYERS", "アメリカン・フライヤーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "父親を遺伝性脳内出血で失い、自分たちも発病することを恐れている兄弟。検査の結果、健康を証明され、“西部の地獄”と呼ばれる苛酷な自転車レースに出場した二人だが、突然兄が倒れ……。若き日のK・コスナー主演の青春映画。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CHILDREN’S HOUR", "噂の二人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "1936年にヘルマンの戯曲『子供の時間』を「この３人」という題で映画化したワイラーが、25年を経て再映画化。寄宿制私立学校を経営する二人の女性が、突然“同性愛”という汚名を着せられ、やがて悲劇的な結末を迎えるまでを確かな演出力で描ききる。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MABUI", "MABUI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "沖縄戦の現実に迫った「GAMA・月桃の花」の続編。1945～50年、混乱期の沖縄を生き抜いた少年たちの輝きを描き出す。戦禍の沖縄で、妹を亡くした清秀と両親を失った武輝が出会った。二人は収容所で、清秀の祖父や母と再会。アメリカ軍に土地を接収された清秀の一家は、家業の理髪店を再建するべく、奮闘を重ねていく。なお、タイトルは“魂”や“真心”を表す沖縄の言葉。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OBSESSION", "OBSESSION", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "気鋭の映像作家として各方面から高い評価を受ける入江悠監督の短編集。派閥で生きる少女たちの争いを描く表題作「OBSESSION」や、異色のファミリー・ドラマが展開する「黄昏家族」など、計4作品が公開された。いずれも現代を生きる人々を鋭く描き出す入江監督の視点が光る。某女子高校。この学校では“超共産主義研究会”と“宗教愛好会”が対立していた。その衝突がある出来事から激化する。(「OBSESSION」)", "2005-09-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("坊っちゃん", "坊っちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "過去に何度も映画・TVドラマ化されている古典的青春文学のリメイク。前田監督の演出は、中村雅俊の現代的な明るい個性を生かして、文学臭のないさわやかな作品に仕上げている。マドンナ役の松坂慶子の美しさも印象に残る。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Stripper", "七月の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "カンサスのとある町に、マダム・オルガ率いるショウ一座がやってきた。息子のケニーに連れられてショウを見に来たヘレンは、ショウガールのライラが、以前、隣人の養女として暮らしていた少女だということに気付く。ある日、一座のマネージャーでライラの恋人でもあるリッキーが金を持ち逃げしてしまい、困ったライラはヘレンの家に世話になるのだが……。「猿の惑星」のフランクリン・J・シャフナー監督のデビュー作。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続スーパー・ジャイアンツ　（第８部）／ 悪魔の化身", "続スーパー・ジャイアンツ　（第８部）／ 悪魔の化身", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "生理学研究所の大川博士はウル・バイシンという細菌を発明し、地球滅亡を企んでいた。スーパー・ジャイアンツは山中の秘密工場に乗り込み悪玉一味を撃滅。地球滅亡の毒薬はジァイアンツによって無事処理される。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿賀の記憶", "阿賀の記憶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "ドキュメンタリーの名手、佐藤真監督の代表作である1992年製作「阿賀に生きる」の続編的ドキュメンタリー。佐藤監督が新潟県の阿賀野川流域に再び赴き、約10年の月日を経た同地の現在を過去と重ね合わせる。フィルムに刻まれた流れゆく月日が痛切に胸に迫る好編だ。かつて阿賀野川で暮らす人々をフィルムに記録した佐藤真監督は、約10年ぶりに同地を訪れ、再びこの地で生きる人々にカメラを向ける。", "2005-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUNGLE　JUICE", "ジャングル・ジュース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「僕の彼女を紹介します」のチャン・ヒョクが主演を務めた青春グラフィティ。「火山高」でブレイクした彼が再び、爽快なアクションを繰り広げる。真夏のソウルの裏街。偶然に麻薬を手に入れた二人の若いチンピラが、警察に追われるヤクザに巻き込まれ、ともに追われるハメに。逃げる彼らは麻薬を売りさばくために、釜山へ向かうのだが……。", "2001-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Shoe Fairy", "靴に恋する人魚", "", "", "http://image.eiga.k-img.com/images/movie/33641/315x210.jpg?2090", 95, "日本でもタレントとして活躍していたビビアン・スー主演による、名作童話のエッセンスを散りばめたファンタジックなラブ・ストーリー。車椅子の少女ドドの楽しみは、両親から絵本を読んでもらうこと。ある日「人魚姫」を読んだドドは、自分も歩けるようになったら声を失うのかと不安になってしまう。そんな彼女の心配をよそに、手術は無事成功。やがて、靴が大好きな美しい女性に成長したドドは、ハンサムな歯科医スマイリーと恋に落ちる。", "2006-09-09", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOM JONES", "トム・ジョーンズの華麗な冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "H・フィールディングの小説を映画化した作品で、アカデミー作品賞、監督賞を受賞している。大地主オールワージー家の養子として育てられたトム・ジョーンズは幼い頃から女好きで、女性との付き合いも旺盛だった。ある時、トムは暴走している馬から大地主の娘ソフィを助け、愛し合うようになる。しかし、トムを嫌う家庭教師らの策略で、トムはオールワージーに勘当され、ロンドンへ旅立つ。トムは旅の途中でもロンドンでも、年上の女と関係を持つが、心はソフィに向いていた。トムはある男に襲われ、過って殺してしまい、死刑の判決を受ける。しかし、オールワージーの努力で、正当防衛が証明されて釈放される。そして、トムはソフィを強く抱きしめるのだった。", "1963-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WACKIEST SHIP IN THE ARMY", "南太平洋ボロ船作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「アパートの鍵貸します」のジャック・レモンが主演したコミカルなタッチの戦争映画。オンボロ帆船エコー号の船長となったクランダル大尉は、周囲から軽蔑されたのに腹を立て、自ら秘密任務を買って出る。それは日本の海軍輸送船団の動向を探ることだった……。監督は「折れた槍」などの脚本家として知られるリチャード・マーフィ。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スリランカの愛と別れ", "スリランカの愛と別れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "スリランカを舞台に、漁業プロジェクト出張員と宝石商の雇われ買付人との愛の行方を、複雑な過去を持つ大金持ちの老婦人の悲劇を絡ませて描いた人間ドラマ。人間の美しさを徹底的に描こうと、木下監督が取り組んだ意欲作で、スリランカの風景が美しい。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUN AND FANCY FREE", "子ぐま物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "２話からなるオムニバス・アニメーションで、第１話は、サーカスを逃げ出した子ぐまのボンゴがめぐり合う、恋と冒険を描いた「ボンゴ（公開時、がんばれボンゴ）」。第２話は、ミッキーやグーフィーといったおなじみの面々が登場する「ミッキーと豆の木（公開時、巨人征服）」で、童話『ジャックと豆の木』を脚色したもの。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とんぼ返り道中", "とんぼ返り道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "獅子舞いを見るにつけ、群集の中から近寄って来ては獅子面の頭に小刀を投げ、割って歩いているという謎の男と、生みの母の存在を追って可憐な美空ひばりが江戸から越後へ旅する道中記。劇中でひばりは相も変わらぬ美声を聞かせてくれる。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("石碇的夏天／SUMMER，　DREAM", "シーディンの夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "当時25歳だったチェン・ヨウチェーが手掛けた叙情的な人間ドラマ。60分の小品ながら世界各国の映画祭に出品され、高い評価を得た。念願の海外留学が駄目になってしまい、故郷シーディンのおばあちゃんの元で夏休みを過ごすことになった若者シャオツー。そこで彼はケベックから来た女性、エリサと出会う。シャオツーはエリサに憧れを抱くが素直になれず……。", "2001-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・極道〈やくざ〉渡世の素敵な面々／きりとりブルース", "新・極道〈やくざ〉渡世の素敵な面々／きりとりブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「新・極道渡世の素敵な面々　女になった覚えはねぇ」に続き、和泉聖治が三度、安部譲ニの小説を映画化した任侠映画。やくざ修行中のチンピラの恋と友情を描く。人の良い一郎はそれが災いして、やくざの世界に入って１年経ってもうだつの上がらない生活を送っていた。そんな彼に若頭が借金のきりとり(集金)を命じる。相手は劇団の主催者で額は1000万円。一郎はさっそくきりとりを始めるが、劇団の看板女優サキに惚れてしまい……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Death at a Funeral", "お葬式に乾杯！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "英国人家族のお葬式をユーモラスに描いたフランク・オズ監督による「ハウエルズ家のちょっとおかしなお葬式」を、「ベティ・サイズモア」のニール・ラビュート監督が、アフロアメリカン家族に置き換えてリメイクしたコメディ群像劇。皆に愛された父親が亡くなり、アーロンをはじめ、ライアンら兄弟ら近親者が集まり葬式を行うことになるが、様々なハプニングが起こり、大混乱の葬式となっていく……。クリス・ロック、マーティン・ローレンスほか、ダニー・グローバー、ゾーイ・サルダナらハリウッドの人気黒人俳優が大挙して出演。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GIANT SPIDER INVASION", "ジャイアント・スパイダー大襲来", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "アメリカの田舎町に謎の物体が落下し、ブラックホールが生じた。そこから出てきた一匹のクモがあっという間に増殖して巨大化、人々をパニックに陥れる。ジャイアント・スパイダーのグロテスクな姿をSFXを駆使して克明に描いた一編。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふうせん２", "ふうせん２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "1990年に加藤昌也主演で作られた「ふうせん」の続編。登竜会組員の田上有作はある日、シマを荒らす木場五郎に制裁を加える。後日仕返しに現れた五郎だが、組事務所での対決から逆に田上の男気に惚れ舎弟分となる。五郎や妹たちに誕生日の祝福を受けるレストランで田上が再会したのは、かつて人違いで殺してしまった男の恋人。暗い記憶がよみがえった時、五郎が命じられた場所割りの混乱から、その事件に因縁深い一越組との抗争が勃発する……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蜀山／新蜀山剣侠", "蜀山奇傅・天空の剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "5世紀の中国。四川省の霊峰・蜀山は魔人の棲む所として知られていた。正義感の強い青年・童童は天の声を聞き、魔物討伐のための軍を組織する。また同じく立ち上がった仙人やその弟子、魔物の毒を打ち消す力を持つ清姫などと出会い、ともに魔人軍団に戦いを挑む。それまでの武侠映画の枠を超えたエンターテイメントを確立させた記念碑的な作品。84年度パリ国際ファンタスティック映画祭でSF映画特撮賞に輝いた冒険大活劇。", "1984-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄の天使・紅い爆音", "地獄の天使・紅い爆音", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "内藤誠監督、舘ひろし主演による青春アクション。舞台は横浜。組長を殺して警察と組の両方から追われる男と不良少女が出会うが、二人に明日はなかった。不良少女に扮する新人、入鹿裕子がユニークな個性を見せるが、本作のあと芸能界を引退した。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTO　THE　DEEP", "ブルーオアシス／神秘の海へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "地球上で最も美しいといわれる、カリフォルニアの海に広がる高さ30メートルの巨大なケプル（海藻）の森。そこで暮らす様々な海の生き物たちの生態を描いた3D仕様の海洋ドキュメンタリー。穏やかに見える海中で繰り広げられる生命のドラマが感動的。", "1994-01-01", "76,1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PAQUEBOT ‘TENACITY’", "商船テナシチー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "1920年発表の、日本を含めて世界中で上演され好評を博した戯曲をもとに、原作者自身とデュヴィヴィエ監督の二人が映画化にあたって台詞や動作を書き改めた。現実への順応が結局は人生を切り開いていくというのが主題の人間ドラマ。パリの失業者バスチアンとセガールはカナダ移住を決意してアーブル港へやって来た。船を待つ何日かの間に宿の女中テレーズに恋をするセガール。だが、ふとしたことからバスチアンとテレーズが結ばれてしまい、二人はフランスで新しく人生をやり直すとセガールに置き手紙をして港を去る。残されたセガールはもともとカナダ行きにそれほど熱心ではなかったのだが、二人が去ったあと心から自分の人生を切り開いていくことを誓い、カナダ行きの商船テナシチー号の甲板に立つ。主人公の３人をはじめとして、脇役のすべてにまで配慮が行き届いた演出と、それにこたえた演技陣の実力によりきわめて完成度の高い作品に仕上がっている。", "1934-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　HEART　IS　DECEITFUL　ABOVE　ALL　THINGS", "サラ、いつわりの祈り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "多くのアーティストから熱い支持を集めるカリスマ作家J.T.リロイが、18歳の時に発表した衝撃的な自伝的小説を映画化。娼婦でドラッグ中毒の母の愛を求め、自ら男娼となった彼の少年時代を描く。ホラー映画の巨匠ダリオ・アルジェントの娘アーシア・アルジェントが監督と母親役を兼任している。娼婦の母サラの元で、ドラッグと暴力に満ちた生活を送ることになったジェレマイア。不器用ながらも二人は絆を深めていくが……。", "2005-05-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALLIGATOR", "アリゲーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "体長15メートル、体重900キロの巨大化したワニが、大都会に迷路のように張りめぐらされた下水溝を棲み家にして、人間を喰いまくる。対するは、デジタル付き時限爆弾で立ち向かう、腕利き警部……。傑作「ジョーズ」に類似した内容のB級娯楽作品。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎ハイビスカスの花〈特別篇〉", "男はつらいよ・寅次郎ハイビスカスの花〈特別篇〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "山田監督自ら“僕にとってもベスト５に入る作品”と語るシリーズ屈指の名作を全面的にリニューアル。沖縄を舞台に展開する寅さんとリリーの物語に、現代の満男の回想シーンが織り込まれている。当時はモノラル録音だった音楽をステレオ・ドルビー・サウンドに変え、オーケストラの演奏による新曲も追加。最新のCG合成を駆使した寅の“復活”シーンは、ファンならずとも涙モノ。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGELA’S　ASHES", "アンジェラの灰", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "F・マコートがアイルランドでの少年時代の思い出を綴ったピュリッツァー賞受賞作の映画化。1930年代のアイルランドを舞台に、飲んだくれの父と、涙にくれる母、幼い兄弟とともに暮らすフランク少年の奮闘記を名匠A・パーカーが、美しく叙情的な映像で描き出す。N.Y.で出会い結婚したマラキとアンジェラは、５人の子供をもうける。末娘の死を機に、彼らは故郷アイルランドへ戻るが……。個性派のR・カーライルとE・ワトソンが抑えた演技で好演。少年たちの自然な演技も目を引く。", "1999-01-01", "1,33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("主水之介三番勝負", "主水之介三番勝負", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "私怨の果たし合いを行ったことで片倉道場を破門された夢殿主水之介。三年ぶりに帰ってきた江戸では、将軍家指南役の仲介により、紀州家指南役を選定中であった。かつての師・片倉一閑斎と、大塚玄蕃の二人のうち、試合の勝者が指南役を得ることになった。ところが勝ち目のない大塚が手の者を使い、ある夜、一閑斎を斬ってしまう。天知茂、近衛十四郎と芸達者な敵役が魅力の痛快娯楽作。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸っ子奉行 天下を斬る男", "江戸っ子奉行 天下を斬る男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "武家生活を嫌って家を飛び出し、貧乏長屋の住人となった南町奉行所の大岡忠相。長屋では“忠さん”の愛称で親しまれ、彼は隣に住むお栄という娘に惹かれる。そんな折、父が遺言状を残して死に、忠相は遺言状に従って再び武士の生活に戻る。それからひと月。とある豪商が殺された事件で、意外にもお栄が自首してくる。人情時代劇のエンターテインメント。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADAM’S RIB", "アダム氏とマダム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "浮気亭主を狙撃し負傷させた金髪女、その弁護士として法廷で女性の権利のために闘うアマンダ。だが一方の浮気亭主を弁護するのはこともあろうに彼女の夫アダムだった。マスコミはこぞってこの夫婦の戦いを取り上げる。息の合った主役コンビによるコメディの傑作。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fall", "落下の王国", "", "", "http://image.eiga.k-img.com/images/movie/53761/315x210.jpg?2736", 118, "「ザ・セル」で鮮烈なビジュアル世界を築いたターセム監督の長編監督第2作。撮影中の事故で脚を負傷し、恋人を主演俳優に奪われて人生に絶望したスタントマンのロイは、入院中の病院で知り合った無垢な少女アレクサンドリアに、薬剤室から自殺用の薬を取ってきてもらうおうと、彼女の気を引くために壮大な物語を語り始める。それは、暴君に立ち向かう5人の勇者たちの物語だった……。13の世界遺産を含む世界24カ国以上で4年がかりで撮影された美しいロケーションも見どころ。", "2008-09-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　DAY　I　BECAME　A　WOMAN", "私が女になった日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "イランの巨匠M・マフマルバフが脚本を手掛け、妻メシュキニが初めてメガホンを執ったオムニバス映画。女性蔑視という社会問題を抱えるイスラム圏でたくましく生きる女性たちの姿を、女性監督ならではの視線で鮮やかに捉えた。チャドルを初めて身にまとう日を迎えた少女、離婚を望みながら自転車レースに参加する女、幸せなウェディングを夢みた老女をめぐる３つの物語。2000年ヴェネチア映画祭、トロント映画祭、モントリオール映画祭などで最優秀新人監督賞など多数受賞。", "2000-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　AVENGERS", "アベンジャーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "1960年代に世界的な人気を呼んだTVドラマ『おしゃれ(秘)探偵』のリメイク。イギリス政府の極秘諜報機関ミニストリー・オブ・シークレット・サービスのエージェントであるジョン・スティードと女性気象学者エマ・ピールの活躍が展開する。豪雪や猛暑の極端な異常気象に見舞われ、イギリスは混乱。調査に乗り出したジョンとエマは、天才科学者の陰謀と身内の裏切りを突き止める……。1960年代風のムードを重視した軽妙かつポップなユーモアが魅力。イギリス紳士ジョンに扮するR・ファインズ、悪役を軽やかにみせたS・コネリーもいいが、やはりエマを演じるU・サーマンのファッショナブルな魅力が鮮烈だ。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("'hood／フッド", "'hood／フッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "日本のヒップホップ・カルチャーを背景にした青春劇。ストリートにたむろする無気力なミチオは、ダンサーを目指す少女チヒロにバカにされたことをきっかけに、本気でダンスに取り組み始める。チヒロたちとチームを組んだ彼はプロ・デビューを果たすほどに上達。やがてミチオとチヒロは恋に落ちるが、そんな彼らにミチオの昔の悪仲間が嫉妬し、思わぬ事件が起こる。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREAT ROCK’N’ROLL SWINDLE", "グレート・ロックンロール・スウィンドル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "1970年代ロンドン・パンク・シーンを代表するセックス・ピストルズのマネージャー、マルコム・マクラレンの企画によるセミドキュメンタリー。いかにしてロック・グループを作り、有名にし、お金をもうけるかというスウィンドル＝大ペテンの物語。ピストルズの映像もふんだんに盛り込まれたパンク世代の記録映画でもある。", "1979-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CROSSFIRE", "十字砲火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "本国アメリカでは1947年の公開後、３ヵ月でいっさいの上映を禁止されたという幻の名作。第二次大戦後のある夜に起きた一人の復員兵殺人事件。死体の異常な傷の深さがふに落ちない警部は、再調査を開始。意外な事実と根強い人種差別の陰が浮かび上がってくる……。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暖流", "暖流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "都内でも屈指の私立病院長が急逝、病院経営は事業欲と闘争心に満ちあふれた男に委ねられた。院長の娘は現実的な彼に惹かれつつも、貴族的な生活を捨てきれずに苦悶する……。岩下志麻、倍賞千恵子の熱演が光る野村芳太郎の女性ドラマ。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Epic Movie", "鉄板英雄伝説", "", "", "http://image.eiga.k-img.com/images/movie/1961/315x210.jpg?4415", 92, "「最終絶叫計画」の脚本を手掛けたジェイソン・フリードバーグ＆アーロン・セルツァーがハリウッド大作の数々をパロディ化し、全米で大ヒットを記録した爆笑コメディ。「ナルニア国物語」のストーリーをベースに、「チャーリーとチョコレート工場」「パイレーツ・オブ・カリビアン」などメガヒット作でおなじみのキャラクターたちが入り乱れ大騒動を繰り広げる。出演は「キューティ・ブロンド」シリーズのジェニファー・クーリッジほか。", "2007-06-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("監獄風雲II／PRISON ON FIRE２", "プリズン・オン・ファイアー２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "小さな罪で極悪刑務所に送られた主人公が、耐えに耐えて爆発、悪人と対決するという香港アクション。服役中のチャンは、一人息子のために一日だけの出所を申請していたが、刑務所の凶悪看守に、自分のスパイとなることを強要される。抵抗したチャンはリンチを受け、やがて囚人のボスや地元のギャングたちとの抗争に巻き込まれていく。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Art of War II: Betrayal", "アート・オブ・ウォー2", "", "", "http://image.eiga.k-img.com/images/movie/54431/315x210.jpg?3441", 103, "「ブレイド」シリーズのウェズリー・スナイプスが国連諜報員を演じるアクションシリーズの第2弾。諜報員だったニールは同僚の罠によりその職を追われ、今はアクション映画のアドバイザーとして暮らしている。そんなある日、マーシャルアーツの恩師であったマザーの訃報が届き、葬儀に参列したニールはマザーの娘から父親の死は自分の過去と関係があると告げられる。真相を探るため、ニールは裏社会の闇と対峙することになる。", "2009-09-12", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A CONNECTICUT YANKEE IN KING ARTHUR’S COURT", "ドリーム・ナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "『トム・ソーヤの冒険』『ハックルベリー・フィンの冒険』などのマーク・トウェイン原作のファンタスティック・アドベンチャー。６世紀のイギリス。伝説の王、アーサーの前に一人の少女、カレンが現れる。カレンは、1500年もの未来、つまり現代から、タイムスリップしてきたのだ。カメラなどハイテクグッズを使い、“魔術師”としてアーサー王に重用されるが、そのアーサー王に暗殺の魔の手が……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大いなる旅路", "大いなる旅路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "大正末期から満洲事変、日支事変、そして大東亜戦争へと移る激動の時代。ある事故を機に、列車の安全に生涯をかけようと誓った一人の国鉄機関士・岩見と、その家族の波乱に満ちた半生を30年にわたって描き出した人間ドラマ。新藤兼人のオリジナル・シナリオを、社会派・関川秀雄がヒューマン・タッチで映像化した。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BREAKIN’", "ブレイクダンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1975年ごろニューヨークのサウス・ブロンクスで生まれたブレイクダンスは、たちまち全米で大流行。カリフォルニアのベニスビーチで皆に踊りを披露していた若者が、ある日、プロデューサーに見い出されプロへの道を歩みはじめる。ダンス映画ブームの時流に乗った、青春サクセス物語。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOBODY", "NOBODY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "これまでオリジナル・ビデオの演出を数多く手掛けてきた、大川俊道の劇場用映画デビュー作。ある晩、広告代理店のサラリーマン３人が、バーで不審な３人組とちょっとした言い争いを起こす。事はそれだけでは終わらず、サラリーマンは一人また一人と、何者かに殺されて……。日常の中に埋もれている、暴力への衝動と恐怖を、サスペンス・タッチで描いている。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SPITFIRE GRILL", "この森で、天使はバスを降りた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "1996年のサンダンス映画祭で観客賞を受賞した愛と感動の人間ドラマ。暗い過去を持つ純粋な女性と彼女によって心を開いていく人々の交流を、透明感に満ちた幻想的な風景の中に描き、美しくも悲しい寓話に仕上げている。森に囲まれたメイン州の小さな町に若い女性パーシーがやって来た。無愛想な老婦人ハナのレストランにウエイトレスとして住み込むことになった彼女は、５年間服役していた過去があり、いやがうえにも閉鎖的な町の人々の注目の的となる。どこか冷めてはいるが誠実なパーシーの人柄に触れ、戦争で息子を失ったハナや、夫に蔑まれているシェルビーは心の傷を癒していく。人々との交流を深める一方で、パーシーは森の中に住み、決して人前には現れない男のことが気がかりだった。正体不明の男とのささやかな触れ合いの中に安らぎを見いだす彼女に、やがて思わぬ悲劇が襲いかかる……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版NARUTO ナルト　疾風伝　火の意志を継ぐ者", "劇場版NARUTO ナルト　疾風伝　火の意志を継ぐ者", "", "", "http://image.eiga.k-img.com/images/movie/54311/315x210.jpg?2604", 0, "「週刊少年ジャンプ」での原作連載が10周年を迎えた「NARUTO ナルト」の劇場版第6弾。雲、岩、霧、砂の4つの国の里を守る忍たちが行方不明になった。強力な特殊能力・血継限界を持つ彼らを突然失った4つの国は、唯一被害をまぬがれた火の国に疑惑の目を向ける。第四次忍界大戦の危機が迫る中、火の国の大名から指令を受けた火影・綱手は真相を突き止めるべく立ち上がるが……。", "2009-08-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キリトル", "キリトル", "", "", "http://image.eiga.k-img.com/images/movie/54671/315x210.jpg?2790", 60, "専門学校生のカヤがいつものように学校をサボって街の喫煙所で煙草を吸っていると、「どっか行かない？」と若い男がナンパしてくる。答える間もなく男に手を引かれ、彼の行きつけのカフェに連れて行かれたサヤは、段々と男のペースに乗せられ心を開いていく。監督は本作が劇場デビュー作となる田中情。", "2009-07-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOYS", "BOYS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "年上の女性と出会い、大人へと成長していく青年の姿を描いたラブ・ストーリー。シャーウッドのプッレプスクールに通う高校３年生のジョン・ベイカーJr.は、何の変哲もない日々を送っていたが、ある日、学校の近くで馬から落ちてケガをした女性と出会う。ジョンは、頑なに病院に行くのを嫌がる彼女を規則を破って寮の部屋へ連れ込み、ルイーズと名乗るこの女性が逃亡中の身であることを知る。だが、美しいルイーズに心惹かれたジョンは、逃亡の理由も聞かないまま下級生のクークとともに彼女をかくまい続けるのだが……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Avalon", "わが心のボルチモア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「レインマン」でアカデミー監督賞に輝いたバリー・レビンソン監督が、故郷のボルチモアを舞台に、3世代にわたる東欧系移民家族の栄光と挫折を描いた傑作ヒューマンドラマ。1914年、アメリカン・ドリームを胸に東欧からやって来たサム・クリチンスキー。壁紙職人としてボルチモアに定住した彼は、息子ジュールスやかわいい孫に囲まれ、幸せな生活を送っていた。そんなある日、ジュールスが経営するデパートが全焼してしまい……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RAINMAKER", "レインメーカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "ベストセラー作家J・グリシャムの『原告側弁護人』を、鬼才コッポラが映像化。新米弁護士の心の成長を、裁判の行方を絡めながらスリリングに描く。金もコネもなく三流法律事務所に就職した法学生のルーディが、保険会社を相手どった訴訟を担当することに。慣れない法廷で失態を演じながらも、彼は原告の貧しい一家のために奔走。やがて、夫の暴力に悩む若妻との出会いによって、彼は社会的弱者のために闘う意味を学んでいく。主演のM・デイモンのフレッシュな演技も見ものだが、悪らつだが憎めない上司を演じたM・ロークや、助手役のD・デヴィートなど脇に回った個性派たちの奮闘も見逃せない。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lantern Hill", "丘の家のジェーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "　「赤毛のアン」で注目されたケビン・サリバンが、再びルーシー・M・モンゴメリの原作を映画化。母と祖母と暮らすジェーンは、ある日、死んだと聞かされていた父が実は生きていることを知る。父との再会を果たしたジェーンは、両親が別れた理由が誤解にすぎないことに気づき、2人の仲を取り持とうと奔走する。「赤毛のアン」でアンを孤児院から引き取るマリラ役を演じた、コリーン・デュハースト出演。", "1989-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Serpico", "セルピコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "　警察学校を卒業後、ニューヨーク市警の勤務に就いたセルピコ。しかし彼は地元の麻薬を取り仕切るボスからの賄賂を拒否したために左遷させられる。汚職が公然とはびこる中、セルピコはたったひとりで戦い続ける。孤立無援の彼はやがて危険地帯へ送り込まれるが……。実話をもとに、名匠シドニー・ルメット監督が正義に燃える青年刑事の姿を描く。アル・パチーノが迫真の熱演を見せる社会派ドラマ。", "1973-01-01", "4,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Big Wednesday", "ビッグ・ウェンズデー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "　1962年、カリフォルニア。マット、ジャック、リロイの3人はサーファー仲間。彼らの夢は水曜日にやってくるという伝説の大波に挑戦することだった。そんな彼らに過酷な現実が襲いかかる。ジャックが徴兵され、ベトナムへ赴くことになったのだ。その後、マットたちの生活も一変する。ジャックも無事に帰ってくるが、3人組は無鉄砲な青春時代の終わりを知る。そして74年、ついに伝説の大波が……。ダイナミックなサーフィン・シーンとともに、男たちの友情を叙情的に描いた感動作。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIESTA", "シエスタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "空港近くで倒れていた女クレア。彼女は昨晩までの記憶を失っていることに気付き、自分がどうしてここにいるのか、自分はいったい誰なのかもわからない。そして過去と現在をさまよいながら、やがてショッキングな真実を知る。スペインを舞台にした幻想的なミステリー・ロマン。マイルス・デイヴィスの音楽がメイン・テーマとして使用されている。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BREAST MEN", "ブレストマン 　豊胸外科医", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "豊胸手術の先駆者となった、二人の美容整形外科医の成功と危機を、スリリングに描いた人間ドラマ。1962年、医師のケビンとラーソンはシリコンを使った整形手術を行い、1980年代には性に対する意識革命をももたらした。しかし、華やかな成功の影で、二人の医師は予期せぬ事件に巻き込まれる……。TVシリーズ“フレンズ”のD・シュワイマーが主人公の一人を軽妙に演じている。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Midnight Cowboy", "真夜中のカーボーイ", "", "", "http://ecx.images-amazon.com/images/I/51Z4aWkSoOL._SL210_.jpg", 113, "大都会ニューヨークに夢を抱き、その男性的魅力で富と名声を得ようとテキサスからやって来たカウボーイ姿の男。現実は厳しく、孤独感に襲われるなかで彼はラッツォと出会う。肺を病み足の不自由なラッツォの夢は、フロリダへ行くことだったが……。イギリス出身のシュレシンジャーの渡米第１作で、1970年代を先取りしたやるせないタッチが成功を収めた。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Striptease", "素顔のままで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "別れた夫ダレルの盗癖のせいでFBIをクビになり、娘アンジェラの監護権をも奪われたエリン。娘を取り戻すのに必要な裁判費用を稼ぐため、ストリッパーとして働き始めたエリンだが、同僚にも恵まれ、何よりこの仕事が気に入っていた。が、ある晩、ステージ上の彼女に抱きつこうとした酔客を、エリンの熱烈なファンで変態下院議員のディルベックが瓶で殴りつけたことから、何者かの黒い影が彼女に忍び寄る。マイアミを舞台に、殺人事件に巻き込まれたストリッパーの奮闘を描くサスペンス。当時、D・ムーアが女優としては過去最高の13億円のギャラで出演したことでも話題になった。また、娘役はデミの実の娘R・ウィリスが演じている。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACTION IN THE NORTH ATLANTIC", "北大西洋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "H・ボガート主演の海洋アクション。ジャービス大尉や彼の親友のロッシーを乗せた“海の魔女”号を含む輸送船団は、マーマンクスへ向かう途中、ナチ潜水艦の攻撃を受ける。船団は秘密の集合地点を航行中、Uボートに発見される。ジャービスは、夜陰に乗じてUボートの追跡をのがれるが、翌朝、飛来した２機のドイツ機の攻撃を受けるのだった。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お茶の間トランスフォーメーション", "お茶の間トランスフォーメーション", "", "", "http://image.eiga.k-img.com/images/movie/34281/315x210.jpg?1654", 0, "電気ポットや扇風機などお茶の間にある身近な家電製品が“トランスフォーメーション（変身）”するハリウッド大作「トランスフォーマー」のパロディ映画。1話約1～2分のショートムービー20本とナビゲーション映像を併せて公開する。「日本以外全部沈没」などで知られるパロディ映画の名手・河崎実監督が、特別篇として「大人のトランスフォーメーション」を担当。製作発表記者会見では、スティーブン・スピルバーグのそっくりさんが登場し話題に。", "2007-10-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINGERS", "マッド・フィンガーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "カーネギー・ホールで演奏することを夢見ているピアニスト志望の青年。その反面、強引な借金の取り立て業を営んでいたり、強姦魔まがいの性癖の持ち主でもある。カイテルの異常者ぶりが鮮烈。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Savege Grace", "美しすぎる母", "", "", "http://image.eiga.k-img.com/images/movie/53361/315x210.jpg?2280", 97, "大富豪ブルックスとの結婚で貧しい生活から抜け出したバーバラは、夢にまで見た上流階級の生活を満喫していた。息子のアントニーも立派な青年に成長し、その後も順風満帆な人生が続くかと思われたが、ある日ブルックスの浮気が発覚。行き場を無くした彼女は次第にアントニーに依存するようになり……。息子が母親を殺した実在の事件を「エデンより彼方に」のジュリアン・ムーアと「グッド・シェパード」の若手注目株エディ・レッドメイン共演で映画化。", "2008-06-07", "9,2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　CHORISTES", "コーラス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「WATARIDORI」の名プロデューサー、ジャック・ペランが出演と製作を兼ねて作り上げたヒューマン・ドラマ。本国フランスでは国民的ヒットとなった話題作だ。物語の舞台は、恵まれない子供たちが集まった寄宿学校。この学校で合唱団を結成した教師と生徒たちの交流が描かれる。生徒と教師の絆を見つめた物語も印象深いが、それ以上に合唱隊が披露する美しい歌声が印象的。中でも主人公の少年ピエールに扮する、ジャン・バティスト・モニエのボーイ・ソプラノは深い感動の余韻を残す。'49年、フランス。失業中の教師マチューは、わけあって親と暮らせない子供たちが集まる寄宿学校に赴任する。音楽教師の彼は合唱団を結成、歌を通して生徒と深い絆で結ばれていく。", "2005-04-09", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Source", "ビートニク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1950年代に始まったビート・ジェネレーションの祖である詩人のアレン・ギンズバーグ、作家・詩人のジャック・ケルアック、作家のウィリアム・バロウズ。３人がポップ・カルチャーの源である“ビート”を生み出していく過程を、様々な人物の証言映像にJ・デップ、D・ホッパーらの朗読シーンを織り交ぜて浮き彫りにしていくセミ・ドキュメンタリー。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANAK", "母と娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "世界的なヒット曲で日本でも加藤登紀子らがカバーしているフィリピンの名曲『ANAK』を題材にした親子物語。離ればなれの生活で仲違いした母と娘が関係を修復するまでを端正に描く。６年にも及ぶ香港での海外契約労働を終えたジョシー。ようやく故国のフィリピンに戻った彼女は家族や友人たちとの再会に喜ぶが、ジョシーの子供たちはなぜか冷たい。ジョシーがいない間、家族に何が起こったのか？", "2000-01-01", "35", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWIMFAN", "プール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「危険な情事」を彷彿とさせるエロティックな青春スリラー。セクシーな美少女に誘惑され、悪夢のような事件に巻き込まれる高校生の恐怖が若手俳優の共演で描かれる。将来有望な水泳部の高校生ベンが、恋人エイミーと同じ大学に進むことを誓い合う。しかし大人びた転校生マディソンにプールで誘惑され、一度きりの関係を持ってしまったベンは、凶暴な素顔を持つ彼女につきまとわれていく。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAS　TUN，　WEEN，S　BRENNT？", "レボリューション６", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ドイツ映画界の人気スター、ティル・シュヴァイガーの主演作品。犯罪劇のスタイルをとりながら15年ぶりに集結した元アナーキスト６人の友情を鮮やかに描き出す。べルリンでティムら６人はアナーキー活動を展開。だが15年後、メンバーはバラバラになり、ティムとホッテのみが現在も活動を続けていた。そんな中、不発に終わっていた爆弾が今頃になって爆発。証拠を消そうとメンバーが再び集合する。", "2002-01-01", "7,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("柳生武芸帳・十兵衛暗殺剣", "柳生武芸帳・十兵衛暗殺剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "琵琶湖・竹生島を舞台に、柳生新蔭流の剣法をめぐって対立する幕屋大休と独眼の剣豪・柳生十兵衛の死闘を描いたシリーズ最終作。門弟たちを次々と斬られてしまい、一人生き残った十兵衛に果たして勝ち目はあるのか？", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREESOME", "スリーサム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "“ジェネレーションX”と呼ばれるアメリカの新人類たちにスポットを当てた、微妙な三角関係の物語。カリフォルニアの名門カレッジに編入したエディは、寮のルームメイトのスチュアートと奇妙にウマがあった。そんな二人の関係に、新しいルームメイト、アレックスが加わることになった。男のような名前だが、れっきとした女性で、コンピュータへの入力ミスにより、二人の部屋が彼女に割り当てられたのだ。セクシーなアレックスにスチュアートはたちまち夢中になるが、彼女はむしろエディに対して積極的だ。しかし、エディはスチュアートが好きで……。ビデオタイトルは「スリーサム／危険な関係」。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A｀ NOUS LA LIBERTE´", "自由を我等に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "クレールがトーキー初期に作った代表作。刑務所で囚人仲間だった二人の男が、社会に出ても友情を保ちつつ自由気ままに生きる姿を、セミ・ミュージカル形式で描く。純粋に映画的な発想、表現と、ボードビル風音楽の使い方が絶妙で、トーキーとしての実験的精神に満ちた一編。", "1931-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Saint Jacques... La Mecque", "サン・ジャックへの道", "", "", "http://image.eiga.k-img.com/images/movie/33881/315x210.jpg?1246", 112, "「女はみんな生きている」のコリーヌ・セロー監督が贈るハートウォーミングな人間ドラマ。遺産相続の条件として、聖地サンティアゴまでの1500kmにも及ぶ巡礼路を一緒に歩くことになった3兄弟。仲が悪く無神論者の彼らは、渋々ながらも聖地を目指し旅に出る。同行するメンバーは、メッカへ行くと思い込んでいるアラブ系少年や何やらワケありな女性など個性派揃い。彼らはそれぞれの事情を抱えながら長い道のりを歩き始めるが……。", "2007-03-10", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オー！マイキー　フィーバー", "オー！マイキー　フィーバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "マネキン人形のフーコン一家が繰り広げるドタバタ劇を、ブラックユーモアたっぷりに描くショートドラマ集。デパートに出かけたフーコン一家が恐怖のエレベーターガールに遭遇する「デパートへ行こう！」、大人の目が届かないところで豹変（ひょうへん）する腹黒い赤ちゃん・ニコラが大暴れする「悪魔天使ニコラ」など、これまでよりさらに過激さを増した内容となっているほか、マイキーがついに動きだしてダンスを披露する展開も用意されている。", "2007-12-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE INCREDIBLE MELTING MAN", "溶解人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "土星から帰還した宇宙飛行士が溶解人間と化した。人肉を喰べなければ溶けてしまうため、新鮮な肉を求めて次々に殺人を起こすという1950年代風のSF。メイクアップとSFXはリック・ベイカーで、そのグロテスクなメイクは評判となった。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("必殺仕掛人・春雪仕掛針", "必殺仕掛人・春雪仕掛針", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "シリーズの中では、最も見せ場が多い。盗賊の首領・お千代を立ち直らせようとする育ての親の小兵衛が、彼女の取り巻きである勝四郎ら、主要な子分の殺しを依頼する。この子分たちを殺すところが、それぞれの見せ場で、銭湯の湯船で梅安に針を刺される地井武男、梅安を殺しに来て逆に小杉に斬られる竜崎勝はともに好演。ラストでお千代役の岩下志麻を殺しに行く場面では、外は雪で遠くに読経が聞こえ、かつて男と女の間柄だった、梅安とお千代の皮肉な運命の成り行きを盛り上げシリーズ中の名場面となった。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUATRO　DIAS", "クアトロ・ディアス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ブラジルで実際に起きた左翼グループによるアメリカ大使誘拐事件を、首謀者F・ガベイラの自伝的小説をもとに映画化。軍事政権に弾圧される民衆がデモを繰り返す1960年代のブラジル。本名や住所を偽って集まった学生を含む武装グループが1969年9月4日、アメリカ大使の誘拐を決行。４日以内に、15人の政治犯の釈放とマスコミに声明を発表することを要求する。覆面をして監視につくメンバーと、最後まで冷静さを失わなかった大使、そして犯人逮捕に奔走する警察。それぞれの視点から緊迫した4日間をリアルに描出。理想に燃え、厳しい現実との狭間で葛藤する若者たちの心情を克明に綴った重厚な心理劇だ。", "1997-01-01", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蜃気楼劇場", "蜃気楼劇場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "1970年の結成以来、“大阪”を拠点に破天荒とも言える演劇的空間を作り上げてきた“維新派”。その大きな特徴の一つに、彼らが自らの手で作る独自の野外劇場がある。例えば“丸太３千本の迷路劇場”“巨大滑り台舞台”など実にユニークなものばかりだ。そんな彼らが1991年に東京・汐留に巨大な廃墟の街を出現させた『—ジャンジャンオペラ—少年街』の公演のすべてを追ったドキュメンタリー作品。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAIRGAME", "フェアーゲーム 毒蛇マンバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ディスプレイ・デザイナーの恋人に捨てられ、復讐を決意するシステム・エンジニア。その復讐とは、密室に彼女を閉じ込め、そこに毒蛇を放つというものだった。男のひがみや恐ろしさを前面に押し出し、蛇の視点から捉えたカメラ・ワークが一層の恐怖感をあおる密室サスペンス。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秋深き", "秋深き", "", "", "http://image.eiga.k-img.com/images/movie/53751/315x210.jpg?4012", 105, "昭和の文豪・織田作之助の短編「秋深き」「競馬」を原作に、八嶋智人、佐藤江梨子主演で描く夫婦の絆の物語。地味で平凡な中学校教師の寺田は、胸の大きな美人ホステス、一代に惚れ、勇気を振り絞ってプロポーズする。寺田の一途さに惹かれた一代はプロポーズを受け入れ、水商売から身を引いて寺田との新生活を始めることに。婚姻届を提出し、晴れて夫婦となった2人は幸せな日々を送るが、そんなある時、一代の身を思いがけない病が襲う。", "2008-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("娘道成寺／蛇炎の恋", "娘道成寺／蛇炎の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "鐘に隠れた僧の安珍に恋焦がれ、ついには蛇となって鐘に巻きつき、彼を焼き殺してしまったという清姫の説話をもとにした、歌舞伎舞踊の演目『京鹿子娘道成寺』をモチーフにした悲哀物語。歌舞伎衣装を身にまとい、高層ビルから飛び降りた詩織。双子の姉、遥香は、自殺の真相を探るため、詩織を唯一の女弟子としていた歌舞伎女形スターの富太郎を訪ねる。その舞いに魅せられ、遥香も弟子入りするが……。中でも説話の舞台でもある高野山壇上伽藍・根本大塔正面をバックに、映画初主演となる九代目・中村福助の艶やかにして華麗な踊りは圧巻。息を飲むほど幻想的で美しく、歌舞伎ファンならずとも見逃せない。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三面鏡の恐怖", "三面鏡の恐怖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "水力発電所の建設に邁進する十吉は、恋人の嘉代子を捨てて社長令嬢と結婚したが、３年後に彼女は病没。そんなある日、十吉のもとに嘉代子とそっくりの妹が現れる。やがて、二人は愛し合うようになるが……。三面鏡のように様々な角度から映し出される人間の感情を、サスペンス・タッチで描いたラブ・ストーリー。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sut", "ミルク", "", "", "http://image.eiga.k-img.com/images/movie/56181/315x210.jpg?2416", 102, "高校を卒業したばかりのユスフは、母ゼーラの営む牛乳屋を手伝いながら大好きな詩を書き、そのうちのいくつかを文芸誌に発表していた。そんなある日、母と町の駅長との親密な関係を目撃してしまったユスフは当惑し、大人になることに不安を覚え始める。第60回ベルリン国際映画祭で金熊賞を受賞した「蜂蜜」（2010）へと続く、セミフ・カプランオール監督の“ユスフ3部作”の第2部。", "2011-07-30", "32,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTACTO", "10億分の１の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "“強運”だけを武器に命を張った賭けをする勝負師たちの世界を描いたサスペンス。銀行強盗のトマスが乗った飛行機が墜落し、237人の乗客のうちトマスだけが生き残る。警察の監視下で入院中のトマスの前に、謎の男フェデリコが現れる。彼はトマスの強運を見込み、運試し選手権に参加させようとしていた……。", "2001-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユキモノガタリ", "ユキモノガタリ", "", "", "http://image.eiga.k-img.com/images/movie/57951/315x210.jpg?4794", 0, "2012年・第4回沖縄国際映画祭の企画「地域発信型プロジェクト」で製作された作品。北海道沼田町を舞台に、町の雪祭りに出品する特産品のプロジェクトリーダーに任命されてしまった新人女性職員の奮闘を描く。", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("中指姫・俺たちゃどうなる？", "中指姫・俺たちゃどうなる？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "当時、人気のソウル・デュオ“バブルガム・ブラザーズ”が初主演した、スラップスティック・ロード・ムービー。音楽教師の松平金次郎と、自衛隊員の五月女玉雄。田舎での生活に失望した二人は、ニューヨーク行きを決意。彼らは、成田空港行きのリムジンバス車中で、ヤクザの組長の養女・水村香織と出会う。３人の乗ったバスが、バス・ジャックされ、彼らは犯人の仲間と間違われて……。次から次へと不幸が襲う、３人の珍道中記。犯人追跡に命を賭ける桜木虎造刑事を、原田芳雄が怪演。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE DREIGROSCHENOPER", "三文オペラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "B・ブレヒトの代表作をG・W・パプストが映画化。匕首（あいくち）マッキーと異名をとる暗黒街の大物マッキー・メッサーが、結婚したポリーの父親と警察を相手に戦うという、ミュージカル仕立ての作品となっている。音楽を担当しているのはK・ワイル。最初に登場する『匕首マッキーの歌』が最も有名だが、音楽と映像の衝突など、きわめて意識的になされているのが注目される。ちなみにワイル夫人ロッテ・レーニャも、マッキーの愛人役として出演。なお、ローレンス・オリヴィエ主演の「三文オペラ」は、ブレヒトが想を得たイギリスの劇作家ジョン・ゲイの『乞食オペラ』をもとにしたもの。", "1931-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HERO AND THE TERROR", "ザ・ファントム　地獄のヒーロー4", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "３年前に逮捕、投獄された精神異常の殺人鬼サイモンが脱獄し、再び残虐な犯罪を開始した。恋人と幸福な日々を送るロス市警のダニーだったが、相棒をサイモンに殺されるに及んで、彼の怒りは爆発する。「地獄のヒーロー」のC・ノリスが得意の空手技をたっぷり見せてくれるが、シリーズものではない。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("本能", "本能", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "日本映画には数少ない本格的艶笑喜劇。しかもその破格の１本と言い得るほどの見事な出来栄えを示している。観世栄夫の不能ぶりも、「道」のジェルソミーナのように神々しい乙羽信子もいい。大人の童話とも言うべき心優しい作品。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE OGRE", "魔王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "名作「ブリキの太鼓」の巨匠F・シュレンドルフが、フランスで1970年に発表された同名ベストセラーを映画化。第二次大戦下のパリ郊外。人付き合いが苦手で子供好きな自動車修理工のアベルが、仲良くなりかけた少女の嘘から強姦罪で投獄され、前線に送られる。すぐにドイツ軍の捕虜になったアベルは、従順な性格を上官に気に入られ、ドイツ軍幼年学校の少年たちのスカウトを任される。だがソ連軍の侵攻が、アベルと子供たちを残酷な運命に巻き込んでいく。子供のような心を持った男が、子供を救うという信念にとらわれながらひたむきに生きる様を力強く描く叙事詩。無垢な聖人ともいえるアベルを演じたJ・マルコヴィッチの存在感が圧倒的。マイケル・ナイマンの美しく哀しい旋律も、寓話的ムードを盛り上げる。", "1996-01-01", "2,7,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("荒野の渡世人", "荒野の渡世人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "オーストラリアを舞台に、高倉健扮する混血児ケン・カトウが、両親を殺した５人の無法者を探して旅に出る。題名の通り、健さんがガンマン姿で刀を振り回すという和洋折衷の無国籍ヤクザもの。設定は奇抜だが、彼のストイックなイメージと作品がミスマッチ。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOUDU　SAUVE´　DES　EAUX", "素晴らしき放浪者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ルノワールのトーキー第２作。古本屋を営む中年男が、セーヌ河に身投げしたホームレスの老人を救い自宅に連れ帰る。ところが老人は、命を救ってもらったことをまるで感謝しないばかりか、常識では計り知れない奔放な行動を連発し、中年男にトラブルをお見舞いしていく……。“自由”というテーマをそのまま体現したかのようにイキイキとした映像世界に魅了される名作。寓話的な物語でありながら説教臭さは皆無で、クライマックスに広がる水辺の官能的な情景描写はいかにもルノワールらしい。製作も務めた名優M・シモンのひょうひょうとした存在感にも何ともいえない味がある。", "1932-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・夜光族", "喜劇・夜光族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "毎夜、盛り場のネオンの海を渡り歩く“夜光族”たちを描くコメディ。夜の銀座をいく平木平三は“結ばせ屋”、つまりホステスとお客の縁結びの神様である。結婚から入籍まで世話をして、その謝礼で優雅な暮らしをしているが、娘はそんな父の職業に文句を言うようになってきた……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢のまにまに", "夢のまにまに", "", "", "http://image.eiga.k-img.com/images/movie/53871/315x210.jpg?1471", 106, "映画美術の巨匠・木村威夫の監督デビュー作。永瀬正敏、浅野忠信、宮沢りえ、桃井かおりらが出演。映画学校の学院長の木室は、学生の村上が何かと気にかかる。村上は60年前の戦争で多くの若者が死んだことの苛立ちを木室にぶつけ、木室の妻・エミ子も戦争で自分だけ生き残ってしまったという自責の念にとらわれていた。やがて村上は精神病を患い自殺をほのめし、何とか思いとどまらせようと木室は手を尽くすが……。", "2008-10-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サディスティック＆　マゾヒスティック", "サディスティック＆　マゾヒスティック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「リング」などで知られる中田秀夫監督が、自分の師匠であり日活ロマン・ポルノの旗手として名を馳せた小沼勝監督の実像を追うドキュメンタリー映画。小沼監督本人はもちろん、谷ナオミ、片桐夕子など多くの映画関係者にインタビューがなされ、その中から鬼才の様々な顔が浮かび上がっていく。ロマンポルノ側面史として捉えても面白い。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地平線がぎらぎらっ", "地平線がぎらぎらっ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "土居通芳監督、ジェリー藤尾主演による新東宝末期に狂い咲きしたアクション・コメディの佳作。刑務所の相部屋にビート族の新人が飛び込んでくるが、新人がダイヤの隠し場所を知っていると聞き全員が脱獄。苦難の末、隠し場所にたどり着くが……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Catch Me If You Can", "キャッチ・ミー・イフ・ユー・キャン", "", "", "http://image.eiga.k-img.com/images/movie/1031/315x210.jpg?4073", 141, "60年代、16歳から21歳までの間に、世界26カ国で約400万ドルを稼いだ実在の詐欺師フランク・アバグネイル。彼の若き日々を、その犯罪の裏にある破産した父親への愛も含めて、スピルバーグ監督が軽妙なタッチで描写。17歳のフランク役をレオナルド・ディカプリオ、彼を追うFBI捜査官役をトム・ハンクスが演じて人気俳優が初顔合わせ。主人公の父親役でクリストファー・ウォーケンが共演。", "2003-03-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL GIORNI DELL’IRA", "怒りの荒野", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ジェンマ、クリーフ共演のマカロニ・ウエスタン。流れ者によって一人前のガンマンに育てられた私生児が、流れ者が町のボスとなり、卑怯な有力者らと組んで無法をはたらき出すにおよんで師匠と対決する。", "1967-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BECOMING COLETTE", "コレット 水瓶座の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「青い麦」などで知られるフランス人女流作家、シドニー・ガブリエル・コレット。その生涯を決定する最初の夫との結婚前後を描いた実話物語。出版プロデューサーのウィリーは、田舎娘だったコレットをパリへ連れ出し、社交界へと導く。やがて彼女は小説家としての才能も開花させる。しかし、人生の花道を歩み始めたコレットと反対に、ウィリーは人生の坂道を下り始める。", "1991-01-01", "7,2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COMPANY OF WOLVES", "狼の血族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "グリム童話『赤ずきんちゃん』をもとに思春期の少女の性への恐れと憧れを、少女の見る夢そのままのごとき幻想的な映像で描いた新趣向のホラー。アヴォリアッツ国際ファンタスティック映画祭審査員特別賞を受賞し、N・ジョーダンの名を一躍高めた。", "1984-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("心中エレジー", "心中エレジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "共に勤勉な伴侶を持つ男と女が、互いの心にある空虚さを埋めるように不倫に没頭していく。未来など信じられない二人にとって、性愛のすぐ先にあるのは“一緒に死ぬこと”だった……。ロマンティックな悲恋として認識されがちな“心中”をテーマにしながらも、淡々とした乾いたユーモアでアキ・カウリスマキ的な風情も漂う異色作。心中に失敗し続ける二人を待つのは？ うっ積した思いを抱える愚直な弁護士、田中。彼は不動産屋勤務の京子を電車内で見かけ、親近感を覚える。やがて安アパートでの“パートタイム同棲”を彼女に提案する。", "2005-07-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AU　COEUR　DU　MENSONGE", "嘘の心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ブルターニュの海辺の家で、平穏な毎日を過ごす画家ルネと妻ヴィヴィアン。ある日、ルネの絵画教室の生徒である少女の惨殺死体が林の中で発見される。やがて腕利きの女警部が執拗にルネの行動をマークするようになり、ヴィヴィアンは他の男との関係に安らぎを見いだしていく。やがて第二の殺人が……。人間の屈折した心理を探求し続ける巨匠C・シャブロルならではのミステリー。疑心暗鬼の袋小路に陥った夫婦の運命を、冷徹な視線で見据えていく。", "1999-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざ非情史 刑務所兄弟", "やくざ非情史 刑務所兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "安藤昇が原案を書き主演した。飯沢組大幹部・岩本直治は、黒川組と私闘し５年の刑を終えて出所。その矢先に何者かに襲われて傷を負い、兄弟分の誠の家に身を隠す。その頃、関東を一手に握る敷島会から、合併話が持ちあがるが……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今夜は踊ろう", "今夜は踊ろう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "1960年代後半の六本木を舞台に、バーのマスターが女の子をもてあそぶ財界の大物に義憤を感じ、その男と対決していくさまを描いた勧善懲悪ストーリー。荒木一郎のヒット曲にのって田宮二郎、梓英子らが発刺とした青春像を演じる。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Contract", "ザ・スナイパー", "", "", "http://ecx.images-amazon.com/images/I/510UBKFvMJL._SL210_.jpg", 0, "", "2006-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋や恋なすな恋", "恋や恋なすな恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "人形浄瑠璃の「葛の葉」に清元の「保名狂乱」を加え、名脚本家・依田義賢がシナリオ化したファンタスティック・ラブロマンス。無実の罪を着せられ流浪の旅に出た平安時代の天文学者、阿部保名は美しい娘と恋に落ちるが、実は彼女は白狐だった……。部分的にアニメーションを使うなど凝った内容である。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("御存じ紫頭巾", "御存じ紫頭巾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "「怪談鴛鴦帳」の木藤茂による時代活劇。かつて堀越後守忠俊を殺した、家来の松下美濃守。彼の一味がその構内で夜ごとに殺されていく。犯人は紫頭巾に顔を包み、誰もその正体を見た者はいなかった。ある日、美濃守に苦しめられていた一人の娘が投身自殺を企てる。それを救ったのが紫頭巾の男。男は彼女が、別れ別れになったままの実の妹だと知る。果して男の正体は？", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GARDENS OF STONE", "友よ、風に抱かれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "1968年、ベトナム戦争たけなわの頃のアーリントン国立墓地をドラマの中心に据え、戦死者埋葬の儀式を司る第３歩兵隊のベテラン曹長ハザードと、この墓地へ新しく配属されてきた新兵ウィローの、父子愛に似た愛情の交流が、しみじみとしたタッチで描かれる。ハザードはかつてベトナムで活躍し、勲章まで受けた勇士だが、この戦争には懐疑的。一方ウィローは“墓を守る仕事より、最前線で祖国のために戦いたい”と血気盛ん。その彼の念願はやがて叶って、昔なじみの美しい娘と結婚した後、少尉として前線へ赴いていくがある日、新妻のもとへ戦死の訃報が届く……。超大作「地獄の黙示録」でベトナム戦争の狂気を極限まで描いたコッポラ監督が、この作品では戦争終結後15年を経た現在の視点から、あの戦争が一体何であったのかを静かな口調で問い直そうとしている。戦闘シーンはいっさい出ないが、激しい反戦と死者への鎮魂の思いに満ちあふれた佳作である。DVDは「コロムビアトライスター ウォー・ムービー・コレクションBOX(3)」に収録。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("７　DAYS　TO　LIVE", "セブンD", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "事故で息子を亡くした作家のマーティンとエレン夫婦は悲しみから逃れるために、人里離れた村のはずれにある築150年の洋館に引っ越す。ある日、エレンは浴室に書かれた７という文字を見つける。その翌日には、道路標識に書かれた“おまえの命はあと６日”という不気味なメッセージが。そしてそのメッセージは様々な形でカウントダウンを刻んでいく。マーティンに話しても取り合ってもらえないエレンは一人で調査を始めるが……。古びた洋館、暗い沼地、死者の呪いといった古典的な要素を取り込み、雰囲気たっぷりに恐怖感を盛り上げていくホラー。", "2000-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El patrullero", "PNDCエル・パトレイロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "イギリス出身の鬼才アレックス・コックス監督が、メキシコを舞台に警察の腐敗を描き出した社会派ドラマ。警察学校を卒業したばかりの新人ハイウェイ・パトロールマン、ペドロ。理想に燃え、意気揚々と砂漠での職務に就いた彼だったが、思い通りにいかない現実に失望し、やがて汚職に手を染めてしまう。その後も彼は酒や女に溺れ、堕落の一途をたどっていく。そんなある日、彼の親友で同僚のアニバルが麻薬密売人によって殺害され……。", "1991-01-01", "19,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAW NERVE", "ダークショット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "連続美女殺人を追う刑事、事件の謎を握るカーレーサー、そして女性ニュースレポーターが絡んで二転三転と話が展開するサスペンス・アクション。1940年代からのベテラン俳優G・フォードと、今もサーファー・イメージの強いJ・M・ヴィンセント、元ポルノ・クイーンのT・ローズといったB級スターが共演。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Running Scared", "ワイルド・バレット", "", "", "http://image.eiga.k-img.com/images/movie/53901/315x210.jpg?3057", 122, "「ワイルド・スピード」シリーズのポール・ウォーカー主演によるサスペンス・アクション。イタリアンマフィアに所属するジョーイは、ボスの息子が警官殺しに使用した銃の始末を命じられる。ところが、隣家に住む少年オレグが処分したはずの拳銃で発砲事件を起こし、そのまま行方をくらませてしまい……。1丁の拳銃を巡り、イタリアンマフィアとロシアンマフィア、さらに警官やサイコキラーまでが入り乱れて熾烈な争いを繰り広げる。", "2008-10-11", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("世界のどこにでもある、場所", "世界のどこにでもある、場所", "", "", "http://image.eiga.k-img.com/images/movie/55731/315x210.jpg?2409", 97, "「ヒポクラテスたち」の大森一樹監督が、現代社会における心の病と再生をテーマに撮りあげた群像劇。地方の動物園や遊園地で開放治療を行う神経科クリニックを舞台に、個性的な患者たちが巻き起こす騒動を描く。キャストには人気劇団「スーパー・エキセントリック・シアター」の俳優陣が勢ぞろいする。また、日本の唱歌や童謡をアーティストたちが歌い継ぐ「にほんのうた」プロジェクトによる懐かしい曲の数々が物語を盛り上げる。", "2011-02-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬夜叉／鏡の中の夢幻城", "犬夜叉／鏡の中の夢幻城", "", "", "http://ecx.images-amazon.com/images/I/21WS7AERFSL._SL210_.jpg", 99, "『週刊少年サンデー』で連載の人気漫画、『犬夜叉』の劇場用長編第２作。宿敵、奈落をついに捕らえた犬夜叉、かごめたち。しかしその直後から、満月が何日も続く怪現象が起こる。そんな折、犬夜叉たちの前に神久谷（かぐや）と名乗る人物が現れる。時空を自在に操る神久谷に、犬夜叉たちは苦戦を強いられるが……。", "2002-12-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ENFANT SAUVAGE", "野性の少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "実話をもとにF・トリュフォーが自ら博士役で主演した意欲作。フランス中部の森林地帯で獣と同じ生活をしてきた野性の少年が捕らえられた。少年はパリの研究所に引き取られるが、世間の関心を集め、見せ物にさせられたりいたずらされたりと、ひどい仕打ちを受ける。これを見かねたイタール博士は少年を引き取り、彼を人間として教育し直そうと決心する……。少年役のJ・P・カルゴルは数千人の候補の中から選ばれた逸材で、文明に少しずつ目覚めていく獣性の少年を見事に演じた。またトリュフォーとのコンビで何本もの名作を生み出したN・アルメンドロスによる白黒画面も秀逸。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天と地を駈ける男", "天と地を駈ける男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "舛田利雄監督と石原裕次郎のコンビによる航空映画の快作。大型航空機の訓練を受ける主人公のパイロットは、科学一辺倒で高圧的な講師ヘンリーと対立する。「紅の翼」で見事な航空シーンを見せた日活特撮陣が、ここでもいい仕事を見せている。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イエスタデイズ", "イエスタデイズ", "", "", "http://image.eiga.k-img.com/images/movie/53801/315x210.jpg?2919", 119, "本多孝好のベストセラー短編集「FINE DAYS」の1編を映画化。出演は塚本高史、國村隼ら。余命わずかな父親から32年前の恋人探しを頼まれた聡史は、戸惑いながらも父親が当時スケッチブックに描いた肖像画を手がかりに彼女を探し始める。かつて彼女が暮らしていたアパートのドアを開けると、そこには32年前の父親と彼女がいた。", "2008-11-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("呪霊／黒呪霊", "呪霊／黒呪霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "『ほんとにあった！　呪いのビデオ』などで才能を発揮し、同作品の映画版も公開されたホラー界の新鋭、白石晃士が監督。主演に抜擢された人気アイドル、若槻千夏が、映画初出演ながら抜群の演技センスを見せている。インターネットで、悪霊によって真っ黒い霊となった少女の霊がさまよっているとの噂が流れる。その霊に接触すると呪われ、その人に接触した人もまた呪われるという。それが現実になり、主人公だけでなく家族や友人にも恐怖が襲いかかる。友人の仁美から“悪霊に取り憑かれた黒い少女”の話を聞いた紀子。帰宅すると、自室に落ちたハンカチに穴が空き、黒いシミができていた。そこへ紀子の携帯に仁美からの着信。電話口からは不気味な呼吸音が聞こえるだけで……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUGAR　COLT", "必殺の用心棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "南北戦争の終結で帰路についた北軍の兵士100人が失踪した。アメリカ政府の依頼を受け、調査に乗り出したトムが探り当てた真相とは……。ミステリー風のユニークな設定のマカロニ・ウエスタン。シュガー・コルトなる異名を持つ主人公のキャラクターもひねりが利いている。", "1966-01-01", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戀戀風塵", "恋恋風塵（れんれんふうじん）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "鉱山の村で幼いころから兄妹のように育ったワンとホン。中学卒業後、二人はともに貧しい家計を助けるため、台北に出て働く。慣れぬ都会暮しの中、互いに励まし合う二人に当然のように淡い恋情は芽生える。やがてワンのところに兵役の知らせが届き……。ノスタルジックな風景の中に、瑞々しい青春の断章を描いた秀作。", "1987-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE DE´SORDRE ET LA NUIT", "夜の放蕩者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "夜のパリの暗黒街に巣喰う麻薬常用者や犯罪者。それに対決する警察側の活躍を、J・ギャバン扮する警部を中心に描くフィルム・ノワール。暗黒街小説作家J・ロベールの原作。", "1958-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Notes on a Scandal", "あるスキャンダルの覚え書き", "", "", "http://image.eiga.k-img.com/images/movie/1911/315x210.jpg?4132", 92, "アメリカで実際に起こった事件を基に、41歳の陶芸教師と15歳の教え子の禁断の愛を描いたゾーイ・ヘラーによる同名小説の映画化。2人の愛とその終焉が、事件の当事者で逮捕された41歳の陶芸教師シーバの同僚で親友のバーバラによって語られていく。監督はジュディ・デンチ主演「アイリス」のベテラン、リチャード・エアー。06年度のアカデミー賞ではデンチ、ブランシェットが、それそれ主演女優、助演女優賞にノミネートされた。", "2007-06-02", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本解放戦線・三里塚の夏", "日本解放戦線・三里塚の夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "小川紳介とそのスタッフたちが国際空港建設問題に揺れる三里塚に腰を落ちつけて撮った、“三里塚”７本連作の第１作。当局側と、それに抵抗する農民や学生たちとの激しい戦いの日々を、あくまでも農民の立場に立って記録している。第三者の客観的な眼ではなく、闘う生活者の眼に固執したカメラ・アイの発見とその真価は、その後の小川作品を貫くものだ。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Ragazza Del Lago", "湖のほとりで", "", "", "http://image.eiga.k-img.com/images/movie/54231/315x210.jpg?3520", 95, "イタリアの俊英アンドレア・モライヨーリの長編デビュー作品で、イタリアの豪華俳優が数多く出演しているサスペンス・ドラマ。原作は女流作家カリン・フォッスムのミステリー小説「見知らぬ男の視線」。北イタリア、田舎町の湖のほとりで美少女アンナの遺体が発見される。村に引越してきたばかりの刑事サンツィオは、捜査を進めるうちに村人たちの人間関係や家族問題などに直面するが、自らも若年性認知症を患う妻との生活に悩みを抱えていた。", "2009-07-18", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビートロック☆ラブ", "ビートロック☆ラブ", "", "", "http://image.eiga.k-img.com/images/movie/54341/315x210.jpg?4302", 79, "メジャーデビューを目指すビジュアル系バンド・ラブダイビングは、先輩バンドの前座などを中心に活動していた。ある日、ボーカルのシン（武瑠）はメジャーデビューのチャンスをメンバーに相談せずに勝手に断ってしまい、メンバーから反感を持たれる。そんな中、優勝すればメジャーデビューやCMタイアップなども約束される“タイコン”というコンテストへの出場が決まる。主演は若手俳優集団D-BOYSの荒木宏文、監督は青春映画に定評のある内田英治。", "2009-03-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ВОЙНА И МИР", "戦争と平和", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 265, "ソ連映画界が国家的規模で製作した文芸大作。ナポレオンのロシア侵攻を中心に、ロシアの貴族、農民など、あらゆる人間の運命を叙事詩的に描く。原作の執筆期間と同じ５年の歳月をかけて撮影された、実験的映像も興味深い。モスクワ映画祭でグランプリを受賞した。", "1966-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お墓がない！", "お墓がない！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "岩下志麻がお墓探しに熱中する大女優を演じたコメディ。ガンで余命半年と勘違いしてしまった女優、桜咲節子は天涯孤独に育ったために入るべきお墓がないことに気づき、ショックを受ける。彼女は新作映画のロケもそっちのけで、わざわざ老婆に変装してお墓探しを始めるが、世間知らずの彼女は方々でトンチンカンな騒動を起こす。この熱意に打たれた霊園斡旋業者の社員・川嶋は、彼女を大女優とは気付かずに献身的に協力するようになる。監督は“夜逃げ屋本舗”シリーズなど、コメディ作品を得意とする原隆仁。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LENI　RIEFENSTAHL−LHR　TRAUM　VON　AFRIKA", "アフリカへの想い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "ドイツの女流監督であり女優のレニ・リーフェンシュタールが、かつて訪れたスーダンで、ヌバ族との対面を果たすドキュメンタリー。98歳になったレニが、パートナーのホルストとともにスーダンに向かう。彼女はそこで、かつて自分を受け入れてくれたヌバ族と再会するが、スーダンは内戦による混乱が続き、旅は困難を極める。やがてヌバの村に到着するも、友人の多くは戦争のためにこの世を去り、村の生活も大きく変化していた。", "2000-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅蓮花", "紅蓮花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "田中うめのの小説『花一輪』をもとに、たくましく生きる女性の半生を、悲しい恋のエピソードを軸に描いた人間ドラマ。太平洋戦争で夫を亡くしたさくらは苦労して息子を育て、やがて従弟の健造と再婚。だが結婚前から交際を続けていた健造の愛人が、二人の間に割り込み奇妙な共同生活が始まった……。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DOCTOR", "ドクター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "一流病院の最高の地位にある心臓外科医マッキーは幸福と名声の頂点にあったが、ある日ガンの宣告を受け、医者から患者へと立場をかえた。絶望のドン底にたたき落とされた彼は、そこから再び人生を見つめ直すことになる……。女流監督ヘインズが熱い感動をみずみずしい感性で描く人間ドラマ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CLIENT", "依頼人", "", "", "http://ecx.images-amazon.com/images/I/51qSkfaTo4L._SL210_.jpg", 121, "「ペリカン文書」に続いて人気作家J・グリシャムの小説を映画化したサスペンス。秘密を知ったため、ギャングに追われる11歳のマークはわずか１ドルの所持金で女性弁護士レジーに弁護を依頼。彼らの心の絆を軸に、マークに法廷で証言させようとする検察側の思惑を絡めてスリリングな物語が展開する。ビデオ、DVDタイトルは「ザ・クライアント／依頼人」。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　COOL，DRY　PLACE", "クール・ドライ・プレイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "妻に去られた男と一人息子の心温まる交流を描く感動ドラマ。敏腕弁護士のラッセルは、妻が家出をしたため多忙な一流法律事務所を辞めて息子とともに田舎で暮らし始める。仕事、家事、子育てに追われる彼は、母親の帰りを待ち続ける息子の姿に胸を痛めていた。そんななか、新しい恋人もでき、息子と二人の生活が安定してきたある日、突然妻が現れ……。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tim Burton's The Nightmare Before Christmas in Disney Digital 3-D", "ティム・バートンのナイトメアー・ビフォア・クリスマス　ディズニーデジタル3D", "", "", "http://image.eiga.k-img.com/images/movie/34271/315x210.jpg?4833", 76, "ティム・バートンがキャラクター原案・原作を務め、1993年に全米公開された長編ストップモーション・アニメーション「ナイトメアー・ビフォア・クリスマス」を、インダストリアル・ライト＆マジックが最新のデジタル技術によって3D化。“ハロウィン・タウン”の王ジャック・スケリントンがクリスマスの存在を知り、自らの手でクリスマスをやろうとして巻き起こる騒動を描く。初公開は2006年10月21日。2007年10月20日にもリバイバルされ、毎年ハロウィンシーズンに公開されている。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Singles", "シングルス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "シアトルを舞台に、結婚に踏み切れないカップルの行く末、失恋したばかりの女性、スーパー・トレインの設計を夢見る若者、デビューを目指すロック歌手と彼に片想い中のウェイトレスなど、6人の恋模様を描く群像劇。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE BONHEUR", "かりそめの幸福", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "戦前のフランス映画黄金時代の担い手の一人、名匠M・レルビエ監督が描いた1930年代ラブ・ロマンスの代表作。男はテロリスト、女は人気女優。運命の出会いから、二人は許されるはずのない恋に落ちた。かりそめの愛に激しく燃え、やがて男は潔く女のもとを去っていく……。", "1935-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和物語", "昭和物語", "", "", "http://image.eiga.k-img.com/images/movie/55931/315x210.jpg?1616", 100, "昭和39年の東京を舞台に、ある一家のきずなを描いたアニメ映画。大田区蒲田で暮らす小学5年生の山崎公平は、町工場を営む父・有三や母・佳乃子、祖母・ヨシ、大学生の兄・太一、高校生の姉・裕子とともに賑やかな毎日を送っていた。職人気質の有三と太一が新年早々大ゲンカをしたり、裕子が非行に走りそうになったりと、山崎家の日常はトラブル続き。しかし、東京オリンピックをきっかけに、バラバラだった家族の心がひとつになっていく。", "2011-01-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("姉妹坂", "姉妹坂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "京都の美しい四季を背景に、４人姉妹の愛と青春、それぞれの心の綾を描いた、雑誌『プチセブン』長期連載劇画の映画化。大林監督が、三女・沢口靖子の心の揺れ動きに対する周囲のリアクションなどを巧みに演出する。現代版「細雪」といった趣。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("３０００　MILES　TO　GRACELAND", "スコーピオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ラスベガス郊外の町ローズウッドのモーテルに、５人の刑務所仲間が集結した。彼らはエルヴィス・プレスリー・スタイルに身を固め、カジノの金庫を強奪しようとするが、そこで思わぬアクシデントが起こり……。K・コスナーの意外な悪役ぶりが楽しめるユニークなクライム・サスペンス。彼のエルヴィス・スタイルは必見。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版“文学少女”", "劇場版“文学少女”", "", "", "http://image.eiga.k-img.com/images/movie/55061/315x210.jpg?4068", 0, "野村美月によるライトノベルシリーズをアニメーション映画化。入学したばかりの新入生・井上心葉は、大きなモクレンの木の下で本を読んでいる女生徒・天野遠子に出会う。遠子は心葉を強引に文芸部へ入部させ、心葉は毎日三題噺を書かされるはめになる。しかし遠子と心葉は、それぞれに大きな秘密を抱えていた……。", "2010-05-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAY OF ATONEMENT", "流血の絆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "実在した麻薬王とマフィア組織の死闘を描くギャング・アクション。フランス・マフィアの大物レイモンが出獄し、息子の住むマイアミへ。静かな暮らしを願うレイモンだったが、息子はドラッグ・マネーのロンダリングに手を染めていた。やがて彼ら親子は麻薬王パスコの罠にはまり、戦いを余儀なくされる。ビデオ発売時タイトルは「クリストファー・ウォーケン　流血の絆」。", "1993-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GUILTY", "バッド・スパイラル／運命の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "野心家の弁護士クレインが、酒に酔った勢いで新人秘書ソフィーをレイプしてしまう。折しも連邦判事に昇格したクレインは、自らの名声が傷つくことを恐れて一方的にソフィーを解雇。さらに彼は、ソフィーを消すためにある若者に殺しの依頼をするが、その若者はクレインの生き別れた息子だった……。登場人物の思惑が複雑怪奇に絡み合うフィルム・ノワール。クライマックスに向かって加速していく二転三転のストーリー展開が見どころ。ビデオ、DVDタイトルは「脅迫者」。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIX", "ジャズ・ミー・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "1920年代アメリカの狂乱の時代を駆け抜けるように生きた、実在の天才コルネット奏者ビックス・バイダーベックの半生を描いたジャズ映画。両親の反対を押し切って音楽の道に進んだ青年ビックスは、独自の即興音楽を確立するが、譜面に頼る当時のジャズ界では理解されず、孤立し、酒に溺れていく……。", "1990-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TEN COMMANDMENTS", "十戒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 228, "「ベン・ハー」と並び、映画史上に輝く壮大なスペクタクル史劇。巨匠C・B・デミルが1923年に映画化した「十誡」を自らリメイク。当時としては天文学的な1350万ドルを費やして完成させた大作である。舞台はラメシス一世の治めるエジプト。新しく生まれるヘブライの男の子をすべて殺すという国王のおふれを逃れるためナイル河へモーゼという名の子供が流された。王女に拾われたモーゼはやがて王子として勢力を強めるが、王の実子ラメシスもまた宮廷で権力をふるっていた。モーゼはラメシスの計略にはまり砂漠に追放されるが……。有名な海が割れるシーンはもちろん、絢爛豪華な美術セットが圧巻だ。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("てなもんやコネクション", "てなもんやコネクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "香港ロケを敢行した、山本政志監督の無国籍活劇。香港の青年・遅九扇はクイズで日本旅行が当たり、大阪にやって来る。新米ツア・コンダクター、クミの案内で彼は東京まで行くが、今度は香港旅行に当選。クミを連れて香港に戻ってみると、九扇一家の住む地所は、日本の地上げ屋に狙われていた……。前半、日本での凸凹旅行は快調なテンポ、後半香港に帰ってからの地上げ屋との戦いがいささかもたつくのが惜しい。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FORBIDDEN BEAUTY", "ザ・フェイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「ドアーズ」のJ・ルービン主演のSFスリラー。元トップモデルのジャニスは、化粧品会社の経営者兼モデルを務めていた。しかし、年齢とともに進む容貌の衰えはいかんともしがたく、ついに会社の役員たちの意見で、若いモデルを起用することに。しかし、ジャニスの恋人でカメラマンの彼が、その若いモデルと親しげにしているのを目撃し、嫉妬に苦しむ。彼女は若さを取り戻そうと、研究室で実験中の、年齢を逆行させる作用があるという女王蜂のエキスを注射する。ところが、このエキスには恐ろしい副作用が隠されていた。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Titanic 3D", "タイタニック（3D版）", "", "", "http://image.eiga.k-img.com/images/movie/57471/315x210.jpg?1393", 195, "「ターミネーター」「トゥルー・ライズ」のジェームズ・キャメロン監督が、当時史上最大規模の製作費2億ドルを投じて豪華客船タイタニック号沈没の悲劇を描き、全米興行収入歴代1位（のちにキャメロン監督自身が「アバター」で記録を更新）、アカデミー賞で作品賞ほか史上最多タイの11部門受賞という偉業を成し遂げた超大作「タイタニック」（1997）を、最新の技術で全編3D化。1912年4月10日、イギリスの豪華客船タイタニック号がニューヨークに向けて処女航海に出発する。出港直前に乗船券を手にした画家志望の青年ジャックは、新天地アメリカを夢見てタイタニック号に乗船。船内で出会った名家の令嬢ローズと恋に落ちるが、出発から4日目の深夜、タイタニック号の船首が巨大な氷河に激突してしまう。", "2012-04-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("螢川", "螢川", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "初恋、ケンカ、友情、そして親友の死。かつては実業家でありながら、落ちぶれて死んでいく父。母の過去、父の先妻との出会い、故郷との別れ。昭和30年代の富山を舞台に、冬から夏にかけて様々な出来事を経験していく少年の姿を描いた作品。芥川賞を受賞した宮本輝の同名小説の映画化である。ラスト、少年は初恋の少女、母、知り合いのおじさんとともに川の上流で数百万匹の螢の群れと遭遇する。このファンタスティックな光景は、彼が今まで通過してきた日常の中の感動と同次元で捉えられ、観る者の心に同一の感動を呼び起こす。また、特撮がさりげなく効果的に使われ、ドラマを盛り上げている。日本アカデミー賞撮影賞受賞。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くじらとり", "くじらとり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 16, "宮崎駿が館主を務める三鷹の森ジブリ美術館にある「土星座」にて公開された、スタジオジブリのオリジナル短編アニメーション。保育園に通う園児たちは、積み木で作った船に乗り込み、鯨を捕まえる旅に出るのだった。中川李枝子、大村百合子の絵本「いやいやえん」に収録されている同名作品の映画化。", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブポリス　ニート達の挽歌", "ラブポリス　ニート達の挽歌", "", "", "http://image.eiga.k-img.com/images/movie/57791/315x210.jpg?1279", 77, "お笑いコンビ「平成ノブシコブシ」の吉村崇、「ダイノジ」の大地洋輔がダブル主演したコメディ。家族にも見放されたニートの2人組が、下心満載で始めた女性のための復讐代行業のてん末を描く。仕事も夢もない2人のニートは、「失恋直後の女性は落としやすい」という情報を聞きつけ、恋に傷ついた女性を救うサイトを立ち上げる。「あなたを悲しませた男、ラブポリスが復讐します！」というコピーを掲げ、「ラブポリス」となった2人は、姑息な手段で女性の敵を次々と制裁していくのだが……。共演は芦名星、松田悟志、波岡一喜、多田愛佳（AKB48）ら。", "2012-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RESCUERS", "ビアンカの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "二匹のネズミが繰り広げる冒険をファンタスティックに描いたディズニー・アニメーション。悪い質屋にさらわれたみなし子の少女ペニーの助けを求める手紙を受け取り、救助に向かうネズミのミス・ビアンカとバーナード。沼で彼女を発見した二匹のネズミは、小動物たちの協力を得て救出作戦を開始する。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Where the Wild Things Are", "かいじゅうたちのいるところ", "", "", "http://image.eiga.k-img.com/images/movie/53491/315x210.jpg?3372", 101, "モーリス・センダックの名作絵本「かいじゅうたちのいるところ」を、「マルコヴィッチの穴」のスパイク・ジョーンズが映画化。いたずら好きな7歳の少年マックスは、母親とケンカをして家を飛び出し、気がつくと船に乗って大海にこぎ出していた。やがてたどりついた島には見たこともないかいじゅうたちがいて、彼らの王様になったマックスは、かいじゅうたちと一緒に誰もが幸せになれる世界を築こうとするが……。", "2010-01-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Reef", "赤い珊瑚礁　オープン・ウォーター", "", "", "http://image.eiga.k-img.com/images/movie/56491/315x210.jpg?3220", 88, "ルークと友人のマットら5人の若者たちは、グレートバリアリーフでクルージングを満喫していたが、ヨットが暗礁に乗り上げ転覆。近くの島まで泳いで救出を待つことになるが、海にはどう猛なホオジロザメが巣くっていた……。巨大人食いザメに襲われる人間たちの決死のサバイバルを描くパニック映画。巨大ワニに襲われる恐怖を描いた「ブラック・ウォーター」（2007）のアンドリュー・トラウキ監督がメガホンをとった。", "2011-08-13", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr.and Mrs.Smith", "スミス夫妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "　ヒッチコックがアメリカで製作した唯一の純コメディ作品。デビットとアンはごく普通の結婚生活を送っていた。しかしある日、弁護士から手続き不十分により、法的には結婚が認められていないと指摘され……。彼の作品への出演を熱望していたキャロル・ロンバートのために製作されたといわれている。キャロルは、31歳の時クラーク・ゲーブルと再婚したが、今作の翌年、飛行機事故で不慮の死を遂げている。", "1989-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Harold And Maude", "ハロルドとモード　少年は虹を渡る", "", "", "http://image.eiga.k-img.com/images/movie/24141/315x210.jpg?2624", 91, "「さらば冬のかもめ」「シャンプー」「帰郷」といった名作で知られるハル・アシュビーの監督第2作。広大な邸宅に住み、ほしいものは何でも手に入る立場にありながら、死に取りつかれた自殺マニアの少年ハロルドは、ある時、80歳の老女モードと出会う。生きる喜びに満ちていたモードとハロルドはやがて互いに愛し合うようになるが……。ふたりの交流を通じて、愛すること、生きることの素晴らしさを描いた青春映画。日本でも上演された大ヒット舞台劇の映画化作品。1972年日本初公開。2010年「ZIGGY FILMS '70S '70年代アメリカ映画伝説」でリバイバル上映。", "2010-07-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KIRIKOU　ET　LA　SORCIERE", "キリクと魔女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "フランスでアニメ作品としては史上最高の興行記録をあげた佳作。魔女の恐怖に立ち向かうちびっ子の大活躍が、ユッスー・ンドゥールによるアフリカン・ミュージックに乗せて展開する。日本ではスタジオジブリの後押しで公開が実現した。吹替版の監修に高畑勲が参加、魔女の声には浅野温子が抜てきされた。魔女カラバが猛威をふるうアフリカの村に、キリクという男の子が生まれる。カラバが意地悪な理由を知りたいキリクは、カラバの手下の監視を潜り抜け、山の向こうのお祖父さんの元を目指す。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太の創世日記", "ドラえもん のび太の創世日記", "", "", "http://ecx.images-amazon.com/images/I/31KYS521CYL._SL210_.jpg", 98, "夏休みの自由研究で、もう一つの宇宙を作ることにしたのび太。スネ夫やジャイアン、しずかも参加して共同研究が始まった。やがて新しい地球に人類が誕生する。しかし、地底で生活していた昆虫人が地上を取り戻そうと人間に戦いを挑む。原始時代や神話の時代などに登場する、様々なのび太の子孫の存在が興味深い。", "1995-03-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FEMME DE L’AVIATEUR", "飛行士の妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "1980年代初頭にスタートした、E・ロメールの“喜劇とことわざ”シリーズの第１作。法律を勉強している20歳の学生フランソワには、秘書の仕事をしている25歳の恋人アンヌがいる。だが、アンヌは不倫相手だったパイロットのクリスチャンに未練があり、しつこいフランソワにはうんざり気味。一方フランソワも、リシューという年下の女の子に出会うのだが……。1981年サンセバスチャン国際映画祭批評家大賞受賞作品。DVDは「エリック・ロメール コレクション(4)」に収録。", "1980-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BITTER HARVEST", "ブロンドの標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "魅力的なボディを武器に男を誘う悪女の陰謀を描いた、フィルム・ノワール調のサスペンス。巨額の遺産を手にした青年トラヴィスに接近する二人の美女。彼女たちの虜となったトラヴィスは、やがて二人にそそのかされて銀行強盗を計画。だが、これがきっかけで彼女たちの意外な秘密が明らかになり……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES PATRIOTES", "哀しみのスパイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "パリで生活するイスラエル人の20歳の青年アリエルは、祖国の諜報機関のスパイを志願し、採用される。やがて表舞台でのスパイ活動をするアリエルだったが、次第に空しさを覚えていく。ある時、高級コールガールのマリー・クロードと出会った彼は、すぐに恋に落ちるが、彼女は殺人事件に巻き込まれ行方不明になってしまう……。E・ロシャン監督が珍しくスパイものに挑戦。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続こつまなんきん お香の巻", "続こつまなんきん お香の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "幼なじみ、若且那、好色社長からあげくは同性愛者まで、河内の水呑百姓の娘として生まれたお香に群がる男たちと、愛想を尽かして東京を去り、自立していくお香の姿を描いた今東光原作の映画化。スタッフはいずれも前作「こつまなんきん」の顔ぶれ。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("がめつい奴", "がめつい奴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "菊田一夫のヒット戯曲の映画化。大阪の下町で木賃宿・釜ケ崎荘を営んでいるお鹿は“がめつさ”では誰にも負けない中年女。彼女の唯一の楽しみは、台所の梅干しのカメの中に隠した札束を数えること。最近は息子の健太が、住人の小山田姉妹の妹・絹と恋仲になっていることが気に入らない。姉妹が昔の奉公先の娘だったからだ。そんなある日、土地をだまし取った熊吉とモメた姉妹の姉・初江が、彼を刺してしまう……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Messengers", "ゴースト・ハウス", "", "", "http://image.eiga.k-img.com/images/movie/34071/315x210.jpg?1404", 90, "「スパイダーマン」シリーズのヒットメイカー、サム・ライミ製作のもと、アジアン・ホラーの鬼才オキサイド＆ダニー・パン兄弟がハリウッド・デビューを果たした絶叫ホラー。人里離れた古い一軒家に引越してきたソロモン一家。長女のジェスは家の中で次々と怪現象に襲われるが、両親に相談しても信じてもらえず……。主人公ジェスに扮するのは「パニック・ルーム」でジョディ・フォスターの娘を演じたクリステン・スチュワート。", "2007-07-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAPTAIN LIGHTFOOT", "自由の旗風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "19世紀初頭、イギリスの統治下から独立を勝ち取ろうとしたアイルランド闘士たちの行動を描くアクション映画。闘士の一人マイケルは、ある日、運動資金にするためイギリス人から現金を強奪、イギリス当局に追われる身となってしまう。当時売り出し中のR・ハドソンが主演した。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺は地獄へ行く", "俺は地獄へ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "プログラム・ピクチャーの名手、野口博志が監督したアクション映画。譲次とユリはメキシコで成功した島田の遺産の相続人を探していた。遺産は邦貨に換算すると約30億円。しかし受取人である島田の昔の恋人・広津あきの行方は知れなかった。藤村有弘がメキシコ人弁護士を、得意のおかしな日本語を駆使して怪演している。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OH，GOD！ BOOKII", "オー！ゴッド2 ～子供はこわい～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "両親が別居してしまい、母親と淋しい二人暮らしをしている小学生のルーシー。そんな彼女が中華料理店で出会った老人は実は神様その人で、他の人間には見えない存在だった。「オー！ ゴッド」の続編で、G・バーンズが再びとぼけた味の神様を演じる。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RESURREZIONE", "復活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 187, "2002年モスクワ映画祭でグランプリを受賞。ロシアの文豪トルストイの名作をイタリア出身の兄弟作家、パオロ＆ヴィットリオ・タヴィアーニが映画化した意欲作だ。己の保身のために召使いとの愛人関係を絶った若き将校と、ショックで売春婦に身を落とす召使いとの悲恋を壮大なスケールで描く。陪審員として裁判に参加したネフリュードフは、被告席に座るかつての恋人カチューシャの姿を発見する。自分の保身のため、彼女を捨てた過去を悔やんだネフリュードフは、すべてを投げ捨て彼女の救済のため奔走する。", "2001-01-01", "4,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRIKING DISTANCE", "スリー・リバーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "刑事である父を殺された、河岸警備隊員のトム。犯人は挙がったが、続発する凶悪殺人事件との類似点に気付いた彼は真犯人を追う。だが、事件の裏には警察をも巻き込む陰謀が待ち受けていた……。B・ウィリスが人間味あふれるヒーローに扮し、３つの河を舞台に大立ち回りを繰り広げる水上アクション活劇。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILD ORCHID2: BLUE MOVIE BLUE", "新・蘭の女 ブルームービー・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ヘロイン中毒のトランペットプレーヤーの父とともに町から町へ旅をしていた少女、ブルーは、父の死をきっかけに、父の友人だと名乗る女性エルのもとで、高級コールガールとなる。一時は自暴自棄になったブルーだが、エルの運転手兼用心棒のサリーに助けられ、普通の生活に戻ろうとする。しかし、エルの追及の手はすぐそこに迫っていた……。「タッカー」でタッカーの娘役を演じたN・シーマスコが、ブルーに扮し、可憐な少女の面影を残しながら、セクシーな“女”の顔を見事に演じている。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魔女卵", "魔女卵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "大阪を舞台に、無名のロック・バンドとグルーピーを描いた作品。女子高生が無名のバンドをメジャーにしようと狂奔する姿と、その少女に惚れた暴走族の純情を、一途な青春物語として描いている。好きな女の子と一緒に寝ていても手を出せないという暴走族を我王銀次が好演。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴動島根刑務所", "暴動島根刑務所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「脱獄広島殺人囚」に続く、東映の“刑務所”ものの第２作で、監督・脚本・出演も前作と同じ顔ぶれ。主人公たちの自由を欲するエネルギーの爆発を描く。時にユーモラスな描写に、囚人たちの人間臭さが漂う。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富江 REVENGE", "富江 REVENGE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "前作「富江 BEGINNING」に続いて、及川中監督がメガホンを執った“富江”シリーズの第６作。何度殺されても、他人の身体を乗っ取って甦る富江。その“細胞侵略”能力にスポットを当て、若き女医と記憶喪失の少女に降りかかる恐怖を、父娘を絡めた悲劇の物語として綴っていく。少女・雪子を車ではねてしまった女医の和江は、勤務先の診療所に彼女を運ぶ。1年後、元気になった雪子は以前の記憶を無くしていて……。", "2005-04-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WEDLOCK", "ウェドロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "近未来のハイテク刑務所では、囚人たちはすべてある特殊な首輪によって管理されていた。各囚人の首輪は、電子信号で誰だかわからぬ他の囚人の首輪とリンクされ、何かがあると二人の囚人の頭がいっぺんにフッ飛ばされてしまう恐ろしい仕組みだ。つまり、脱出絶対不可能の最終監獄。だが、果敢にもイチかバチかの脱獄を試みる男が現れる。大胆な発想の近未来脱獄アクション。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エリ・エリ・レマ・サバクタニ", "エリ・エリ・レマ・サバクタニ", "", "", "http://image.eiga.k-img.com/images/movie/1381/315x210.jpg?1729", 107, "05年カンヌ映画祭「ある視点」部門に出品された「EUREKA」「レイクサイド・マーダーケース」の青山真治監督最新作。2015年、世界中に死に至る謎の病レミング病が蔓延するが、この病気の進行を抑制する唯一の方法が発見される。それは2人の男が演奏する音を聞くことだった。小説家でもあるミュージシャン暴力温泉芸者＝中原昌也やSF小説家・筒井康隆らが出演。タイトルはキリストの最期の言葉、「神よ、何故に我を見捨てたもうや？」のヘブライ語。", "2006-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fame", "フェーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "ニューヨークのマンハッタンにある芸術学校。ここでは、俳優、ミュージシャン、ダンサーとして成功することを夢見るたくさんの若者たちが、青春の日々を送っていた。夢に向かって奮闘する彼らの姿をドキュメンタリー・タッチで生き生きと描いた青春群像劇。監督は「ミッドナイト・エクスプレス」の名匠アラン・パーカー。大人気を呼んだ本作は、後に「フェーム／青春の旅立ち」というタイトルでテレビシリーズ化されている。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泥棒貴族 ボディハンター", "泥棒貴族 ボディハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "泥棒集団キャッツのメンバーのエリら４人は、ふだんは普通のOLだが、ボスの田所からの指令によってスパイに変身する。その田所にまったく似ていない双子の弟であり、警視庁勤務の庄平がある事件の相談を持ちかける。それは東京湾開発事業の大田原副総裁と城西建設の癒着をめぐり、自殺者が続いたという事件だった。直ちに田所の指令により、エリたち４人が調査に乗り出すのだが……。シェイプUPガールズの４人が主演している、セクシー・アクション。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風のあるぺじお", "風のあるぺじお", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "母親が入院し、急に何もかもしなければならなくなった父と幼い兄妹は、自分のことは自分でする、三度の食事はきちんととる、などの約束をかわす。しかし、兄は自閉的になっていき、母は家族への気遣いを離婚という形で切り出す。“主夫”をすることになったシンガー、はしだのりひこの体験記『お父さんゴハンまーだ』の映画化。ユニークな家族ドラマになっている。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若い娘たち", "若い娘たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "石坂洋次郎の原作を岡本喜八監督、雪村いづみ以下東宝青春スターで映画化した青春映画の快作。地方都市を舞台に下宿屋に住む学生たちと、下宿屋の娘たちの恋のさやあてを描く。「結婚のすべて」に続く岡本喜八監督の第２作で、アクション・タッチのカッティングが実に小気味よい。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Elegiya zhizni. Rostropovich. Vishnevskaya.", "ロストロポーヴィチ　人生の祭典", "", "", "http://image.eiga.k-img.com/images/movie/33941/315x210.jpg?1297", 101, "ロシアの名チェリスト、ムスティスラフ・ロストロポービチと、彼の妻である天才ソプラノ歌手ガリーナ・ビシネフスカヤの波乱に満ちた半生をドキュメンタリー映画化。「太陽」で昭和天皇ヒロヒトの苦悩を描いて大きな話題を呼んだロシアの鬼才アレクサンドル・ソクーロフ監督が、激動の20世紀を生き抜いてきた2人の姿を通して芸術の歴史や普遍的な人間愛を描く。小澤征爾が指揮をとったウィーン・フィルとの演奏シーンも収録。", "2007-04-21", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("竜馬の妻とその夫と愛人", "竜馬の妻とその夫と愛人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "人気脚本家、三谷幸喜の舞台劇を、舞台を鑑賞した映画監督、市川準のたっての希望で映画化。坂本竜馬の13回忌に招くべく、新政府の役人、覚兵衛が竜馬の元妻おりょうを訪ねる。だが、おりょうはテキ屋の松兵衛と結婚してみすぼらしい生活を送っていた。しかも彼女は竜馬似の愛人、虎蔵と駆け落ちを計画していたのだった。竜馬をめぐる男女４人の様々な思惑が絡み合うコミカルなドラマ。ほとんどがセット撮影だが、まるで海が見えるような広がりを感じさせる透明感あふれる映像は市川準ならでは。16年ぶりの映画出演となる木梨憲武が、トボケた表情の中に男の哀愁を漂わせ“抜けた”演技を魅せる。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泣いて笑った花嫁", "泣いて笑った花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "内緒で演出家の助手をしていることが父にばれ、口論から家出した浅草の玩具問屋の一人息子が、自分の力で公演を成功させていくまでを、彼に想いを寄せる娘の母が実は父の昔の恋人だった、などのエピソードを交えて描いた恋愛喜劇。若さあふれる鰐淵が魅力。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fate", "宿命", "", "", "http://image.eiga.k-img.com/images/movie/53831/315x210.jpg?4599", 123, "TVドラマ「夏の香り」のソン・スンホンと「美しき野獣」のクォン・サンウが共演するアクション。父の死後、男癖の悪い母親の家を出たウミン（スンホン）は、道端で闇社会で生きるドワンに出会う。ウミンとドワンは意気投合するが、ドワンと組んでいるチョルジュンはウミンの存在を快く思っていなかった。彼らは人生をやり直すため、対立する組織の金を盗む強奪作戦を実行に移すのだが……。", "2008-10-04", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("姉妹狂艶", "姉妹狂艶", "", "", "http://image.eiga.k-img.com/images/movie/57211/315x210.jpg?3603", 0, "障がいを抱える観客が官能映画を映画館で楽しめるよう、女性による副音声や日本語字幕を付けたバリアフリー仕様の映画を製作・上映する「エロバリ（エロティック・バリアフリー）」プロジェクト作品。ある朝、交際中の康平の家を訪ねた葉子は、姉の幹子が康平の隣で眠っているところを目撃してしまう。たまらず部屋を飛び出した葉子は漫画家の明と出会い、2人はひかれ合っていく。しかし、やがて明にも幹子の影が忍び寄り……。", "2011-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コント55号と水前寺清子の神様の恋人", "コント55号と水前寺清子の神様の恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "伝説の爆笑コンビ、コント55号とチータこと水前寺清子が初めて組んだコメディー。スラップスティック、ラブコメなどの要素がふんだんに盛り込まれた会心作だ。名匠・野村芳太郎監督による喜劇演出にも注目したい。幼馴染みの欽ちゃん扮するヤクザの金一郎と、二郎さん扮するラーメン屋の次郎太が大人になって再会。ある事件に遭遇する彼らだが、やがて共に一人の娘に恋心を抱き……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄線地帯〈イエローライン〉", "黄線地帯〈イエローライン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "石井輝男のスタイリッシュな演出が冴えるセミドキュメント犯罪ドラマ“地帯（ライン）”シリーズの一編。神戸の税関長が殺し屋に殺害された。事件を追う新聞記者、真山は調査を進めるうちに、外国人に日本人女性を提供する秘密の売春組織、黄線地帯の存在を突き止める。一方、殺し屋は真山の恋人を偶然にも人質にとり、逃亡を続け……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARTOUCHE", "大盗賊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "18世紀のパリ。若くしてすご腕の盗賊カルトーシュは、仕事のあとに残す“C”の頭文字で悪徳貴族や金持ちたちを戦慄させ、一躍人民のヒーローとなる。P・D・ブロカ監督が軽妙なタッチで描く時代活劇。華やかなスターを揃えて話題となった。", "1961-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE BELLE FILLE COMME MOI", "私のように美しい娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "社会学者スタニスラフは女性犯罪心理の論文を書くため女囚刑務所を訪れ、カミーユという女性をサンプルに調査を始める。不幸な少女時代から男性遍歴、そして殺人までの物語を聞くうち、スタニスラフはカミーユに心を奪われてしまう……。人生の悲哀を徹底的にコミカルに描いたトリュフォーの佳作。", "1972-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Swimming with Sharks", "ザ・プロデューサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「ユージュアル・サスペクツ」「セブン」で一躍、人気アクターとなったK・スペイシーが初主演し、共同製作も務めた作品。映画学校を卒業したガイは、ハリウッドの映画会社重役バディ・アッカーマンの助手となる。バディは評判は良くなかったが、彼の元で働いた人間は皆ハリウッドで成功していた。初日から、ガイはバディからあらゆる侮辱や難しい仕事を与えられる。そんななか、ガイは売り込みに来た女プロデューサーのドーンに心を奪われ、やがて愛し合うようになる。ガイは彼女の企画をバディに推薦し、なんとか認めさせることに成功。これで自分も昇進ができると喜んだ矢先、一本の電話が彼の運命を変えていく……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Love You, Man", "40男のバージンロード", "", "", "http://ecx.images-amazon.com/images/I/51drrKW7qBL._SL210_.jpg", 110, "結婚式を控えた男性が親友探しに奔走する姿を描いた全米大ヒットコメディ。理想の女性ゾーイとの結婚が決まり幸せの絶頂にいたピーターは、結婚式でのベストマン（新郎付添人）を頼めるような親友がいないことに気づく。慌てて親友を探しはじめたピーターは、偶然出会ったシドニーと意気投合するが……。主人公ピーターと親友候補シドニーに扮するのは、「無ケーカクの命中男　ノックトアップ」でも共演したポール・ラッドとジェイソン・シーゲル。", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("8月のシンフォニー　渋谷2002～2003", "8月のシンフォニー　渋谷2002～2003", "", "", "http://image.eiga.k-img.com/images/movie/54641/315x210.jpg?3450", 118, "シンガーソングライターの川嶋あいが自身の生い立ちを綴った自伝的エッセイ「最後の言葉」を、アニメーションで映画化。歌手になる夢を叶えるために福岡から上京したアイ。渋谷の路上で活動を開始した彼女は、偶然出会った青年社長や学生たちのサポートを得て、いつしか路上の天使と呼ばれるようになっていく。ところがある日、福岡でアイを応援し続けてきた母が病に倒れてしまい……。監督・脚本は「ふるさと−JAPAN」で高い評価を得た西澤昭男。", "2009-08-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビルマの竪琴", "ビルマの竪琴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "1956年に映画化された竹山道雄の「ビルマの竪琴」を同じ市川崑が、前作と同じ故・和田夏十の脚本のもとに再映画化。今作品ではカラーで撮影され、タイでロケが行われた。市川はこれまでにも1971年に、当時の東宝製作担当重役・藤本真澄に「ビルマの竪琴」をカラーで再映画化したいと企画を提出しているほどで、相当に執着した企画だったのだろう。前作と同じ役で北林谷栄、前作では軍曹役の浜村純が村長役で再度出演している。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANYWHERE BUT HERE", "地上〈ここ〉より何処かで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "再婚相手と別れて、ビバリーヒルズの安アパートに引っ越してきた、能天気で派手好みの母アデルと、おとなしい優等生の娘アン。性格、趣味、ファッションと、何から何まで正反対の親娘が、反発しながらも心を通わせていく姿をほのぼのと描いたヒューマン・ドラマ。S・サランドンの巧みな演技、N・ポートマンの清楚な魅力と、見どころも満載。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON AIR／オンエアー", "ON AIR／オンエアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "1987年に「卒業プルーフ」でプロデュースを手掛けた鶴見辰吾が、そのときの監督、牛山真一と再度タッグを組み製作総指揮・主演を務めた業界舞台裏ドタバタ・コメディ。視聴率低迷で打ち切りになった連続ドラマのスタッフたちが、いきなりの最終回をどう締めくくるか、けんけんごうごうのディスカッションを繰り広げる。ほぼ密室劇だけに、出演者それぞれが濃い味つけの演技でしのぎを削り、笑いを呼ぶが、底辺には“物語をいかに終わらせるか”というテーマが隠されている。船越英一郎が謎の男に扮し、要所要所に登場しては狂言回し風にコメントして、不思議なリズムが生まれている。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("るにん", "るにん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 149, "俳優の奥田瑛二が、江戸末期の史実を映画化した監督第２作。幕府によって八丈島へ島流しにされた男女の、生き延びるための壮絶なドラマを描く。過酷な流刑を乗り越えようと奮闘するヒロインを、松坂慶子が圧倒的な貫禄と妖艶さで熱演する。江戸に戻れることを信じ、八丈島に流刑となった花魁の豊菊。そんな彼女を愛し、その願いを叶えたい流人の喜三郎は、ある計画を思いつく。", "2006-01-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gran Torino", "グラン・トリノ", "", "", "http://image.eiga.k-img.com/images/movie/54141/315x210.jpg?2086", 117, "アカデミー作品賞受賞作「ミリオンダラー・ベイビー」以来4年ぶりとなるクリント・イーストウッド監督・主演作。朝鮮戦争の従軍経験を持つ元自動車工ウォルト・コワルスキーは、妻に先立たれ、愛車“グラン・トリノ”や愛犬と孤独に暮らすだけの日々を送っていた。そんな彼の隣家にモン族の少年タオの一家が越してくる。ある事件をきっかけにして心を通わせ始めたウォルトとタオだったが、タオを仲間に引き入れようとする不良グループが2人の関係を脅かし始め……。", "2009-04-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　E´GARE´S", "かげろう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "フランスを代表する監督A・テシネが描くラブ・ストーリー。ナチスの侵攻が激化する時代のフランスを背景に、戦火を逃れる道中で出会った未亡人と青年のつかの間の愛を見つめる。ナチス軍の侵攻で未亡人のオディールと子供二人はパリを離れ、南へ向かう。その道中、爆撃にあった３人はイヴァンという青年に危険なところを救われる。ともに歩き出した４人は無人となった邸宅を見つけ、そこで暮らし始めるが……。", "2003-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　BAISERS", "キス！キス！キッス！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "フランスの当時の新鋭監督５人が競作した“キス”にまつわるオムニバス映画。バカンス先の海で、若い人妻がウブな少年とキスできるかという賭けをする「夏のキス」などの洒落たタッチの５つのショート・ストーリーが展開。1964年に「接吻・接吻・接吻」というタイトルで初公開され、1998年に改題しリバイバルされた。", "1963-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Planet Terror", "プラネット・テラー in グラインドハウス", "", "", "http://image.eiga.k-img.com/images/movie/34141/315x210.jpg?4705", 105, "「デスペラード」「シン・シティ」のロバート・ロドリゲス監督が、凶暴なゾンビ軍団に立ち向かう美女たちの活躍をB級テイストたっぷりに描いたホラー・アクション。米軍部隊長マルドゥーンが密かに取引していた生物化学兵器のガスが街中に蔓延。人々は恐ろしいゾンビと化してしまう。ゾンビに片脚を奪われたゴーゴーダンサーのチェリーは、失った脚にマシンガンを装着。女医のダコタもまた、注射針を武器にゾンビたちと死闘を繰り広げる。", "2007-09-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELLBOUND：HELLRAISERII", "ヘルレイザー2", "", "", "http://ecx.images-amazon.com/images/I/5158ia-RHKL._SL210_.jpg", 99, "ホラー作家C・バーカーの原作を壮絶なスプラッタ描写と斬新な映像で再現した「ヘルレイザー」の続編。自閉症の少女ティファニーとともに魔道士の待ち受ける大迷宮へ進むクリスティ。皮膚のない悪女ジュリアの復活や醜悪な悪魔と化した医師など、今回も次々とショッキングなキャラクターが登場。", "1989-02-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春の雪", "春の雪", "", "", "http://image.eiga.k-img.com/images/movie/1421/315x210.jpg?4022", 151, "「GO」「世界の中心で、愛をさけぶ」「北の零年」の行定勲監督が、三島由紀夫の遺作となった「豊饒の海」4部作の第1章「春の海」を原作に描く純愛ロマン。舞台は大正時代。幼なじみの侯爵家の子息・清顕と伯爵家の令嬢・聡子。成長した2人はいつしか互いに惹かれ会うようになるが、聡子に宮家の王子との縁談が持ち上がる。人気俳優2人「69　sixty nine」「ローレライ」の妻夫木聡と「いま、会いにゆきます」の竹内結子が共演。", "2005-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Delta Farce", "デルタ・フォース　俺たちスーパーソルジャー！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2007-05-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD　MAN’S　CURVE", "デッドマンズ・カーブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "“学生が自殺したら、そのルームメイトの成績は精神的ショックを考慮してオールA”という、米国の大学で実際に採用されている制度を下敷にしたスリラー。大学生二人が成績のために、もう一人のルームメイトを自殺に見せかけて殺害。だが、その死体が発見されず、二人は疑心暗鬼に駆られ……。犠牲者を自殺しそうなタイプに見せようと暗い趣味を捏造する描写などの、ブラック・ユーモアが光る。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君に幸福を　センチメンタル・ボーイ", "君に幸福を　センチメンタル・ボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "身分違いの恋のてん末を描いた青春歌謡もの。日本一のケーキ職人を夢見る史郎は、悪友の勧めるままに遊園地へガールハントに繰り出す。が、運悪く乗り物から落ちてきたハンドバッグが頭に当たり、気絶してしまう。しかし、縁は異なもの。その持ち主・十紀と史郎は急速に仲良しに。ところが、彼女は大富豪の一人娘だった……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELIA KAZAN AN OUTSIDER", "エリア・カザンの肖像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "「エデンの東」「波止場」など数々の名作でジェームズ・ディーンやマーロン・ブランドらを育てた1950年代のアメリカ映画界を代表する監督エリア・カザン。彼が、友人でもある映画評論家のミシェル・シモンのインタビューに答えて、自らの半生、スターや作家たちの思い出、自作をめぐるエピソードなどを率直に語ったドキュメンタリー。“赤狩り”時代に同僚を裏切ってまで映画界に残ろうとしたといわれているカザンだが、今までそのことについて公式に語ったことがなく、この映画で初めて沈黙を破った。カザンの私生活も語られるなど貴重な一編。", "1982-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DROP DEAD FRED", "フィービー・ケイツの　私の彼は問題児〈ドドンパ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "夫に逃げられ、財布と車を盗まれ、ついでに仕事もクビになった不幸のドン底のエリザベスの前に、幼いころの“架空の友人”フレッドが現れる。彼はイタズラ好きでどこか下品なゴーストのような存在で、その姿はエリザベスにしか見えない。果たして彼は彼女を慰めにやって来たのか、それとも……。フレッドが巻き起こす珍騒動を愉快に、そして優しく描いたハートフル・コメディ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Seven Days in May", "五月の七日間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "　アメリカ大統領の核軍縮条約案をソ連が受諾。しかし軍部がこれに猛反発する。反対派の急先鋒、米軍統合参謀本部長スコット将軍の直属だったケイシー大佐は軍部の異変に気づく。調査を進めるケイシーはやがてスコットのクーデター計画を知ることに……。社会派ジョン・フランケンハイマー監督が、テレビシリーズ「ミステリー・ゾーン」の作家として知られるロッド・サーリングの脚本を得て作り上げた緊迫の政治サスペンス。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HIGH CRUSADE", "ハイ・クルセイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "エイリアンたちに立ち向かう騎士たちの勇姿を描いたSFアドベンチャー。中世のイギリス、騎士ロジャーの城の前に地球侵略を狙うワルスゴール星人が降り立つが、逆に倒され宇宙船を奪取されてしまう。そして今度は、ロジャーたちがワルスゴール星を征服するために宇宙へと飛び立ち、時空を超えての大冒険が繰り広げられるのだった……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Haunting of Molly Hartley", "モリー・ハートレイ　血塗られた制服女子高生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIVA", "ディーバ ジ・アルティミット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "スイスの作家デラコルタ（ダニエル・オディエ）が発表したセリ・ノワール小説第２作の映画化。監督は当時35歳のベネックスがあたり、見事なサスペンス・ロマンを作り上げている。パリ、18歳の郵便配達夫ジュールは、いつもオペラを聴きながらバイクで街をまわっている。ジュールの歌の女神＝ディーバは、レコードの発売はもとより、録音も許さないという黒人オペラ歌手。だがジュールは彼女のコンサートをひそかに録音したことから、思わぬ事件に巻き込まれていく……。従来のジャンルで分類しようとしても、すんなりと収まらないクロスオーバー映画である。サスペンスでもあればラブ・ストーリーでもあり、アクション映画であると同時に様式美を持つ一貫した作家の映画でもある。あらゆる要素が渾然一体となり、特別に何かのモチーフを強調するのでもない、いわばまったく新しい不思議な感覚に満ちている作品。1982年度のセザール賞新人監督賞を受賞。", "1981-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海賊船 海の虎", "海賊船 海の虎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "盗みはしても殺しはしないという、海賊・千里の虎。ところがその偽物が現れ残虐な略奪をしたため、彼は偽物探しを始める。彼は、偽物が父親を殺した、ボルネオの鉄だと突き止めるが……。その偽物探しに、謎の風来坊に扮した二谷英明が絡むアドベンチャー・アクション劇。宍戸錠主演作としては、普通の出来。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAMALA2010　a punk cat in space", "TAMALA2010　a punk cat in space", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "キュートなルックスからは想像のつかないアナーキーな言動を連発するメス猫“タマラ”を主人公にした長編アニメ。2010年、数奇な宿命を背負って地球のMEGURO-CITYから宇宙に旅立ったタマラの冒険を描き出す。アーティスト・ユニット、T.o.Lが創造したナンセンスにしてシュールな世界観、武田真治やベアトリス・ダルらの異色ボイス・キャストなど、あらゆる要素が個性的な作品だ。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さよなら、こんにちは", "さよなら、こんにちは", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "舞台の演出家として知られる福田陽一郎の実写映画初監督作。ある日曜日、信号待ちをしていた卓也に突然見知らぬ女が腕を絡めてくる。しつこい男から逃げようとしていた美加が、その女。二人はそれきり別れるが、互いが気になって仕方がない。そんなある日、美加の働く図書館で二人は再会する。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A DAMSEL IN DISTRESS", "踊る騎士〈ナイト〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ロンドンを舞台に、アメリカから来たダンサーが貴族の令嬢に恋する物語。ジンジャー・ロジャースと組んで７本のダンス映画に主演したアステアが、初めてロジャースと離れた作品。ガーシュインの曲とハーミス・パンの振付けが快調。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アゲイン AGAIN", "アゲイン AGAIN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "昭和30年代初頭から40年代にかけて映画ファンの胸をときめかせた“日活アクション”のアンソロジー。その熱烈な信奉者として知られるハードボイルド作家の矢作俊彦が構成・脚本・監督をこなす。彼の考え出したアイデアは殺し屋・ジョーがかつての良きライバルたちを訪ね歩くという設定だ。スクリーン上で繰り広げられる、電光石火の銃撃戦をはじめとして、荒々しい殴り合い、緊迫したギャンブル合戦、美しいヒロインとの出会いと別れ、そしてラブ・シーンの数々が37本の映画からピックアップされ、石原裕次郎、小林旭、浅丘ルリ子、吉永小百合など、かつての日活黄金時代を築いた数々のスターがキラ星のようにスクリーンに蘇る。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dr.TERROR’S HOUSE OF HORRORS", "テラー博士の恐怖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "イギリスの片田舎を走る列車に乗り合わせた乗客が、テラー博士と名乗る謎の男から、自分たちの恐るべき未来の運命を聞かされる……。死を意味する“13”のカードを持つテラー博士に扮する恐怖映画の名怪優カッシングが、ミステリアスな雰囲気を高めて好演。", "1965-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Darjeeling Limited", "ダージリン急行", "", "", "http://image.eiga.k-img.com/images/movie/53121/315x210.jpg?1930", 91, "父の死がきっかけで疎遠になっていたホイットマン3兄弟だったが、長男フランシスの呼びかけで次男ピーター、三男ジャックの3人が揃い、インド横断の列車旅行に出る。しかし、そんな彼らには予想外の出来事が待ち受けていて……。「ザ・ロイヤル・テネンバウムズ」のウェス・アンダーソン監督が描く、3兄弟の心の再生の旅。本編前には三男ジャックの恋を描いた13分の短編「ホテル・シュヴァリエ」（共演ナタリー・ポートマン）を上映。", "2008-03-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Short Cuts", "ショート・カッツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 189, "　群像劇を得意としてきたロバート・アルトマン監督がレイモンド・カーバーの短編小説をもとに数々のエピソードを描く。息子が交通事故に遭い、悲しみに打ちひしがれる夫婦の物語を筆頭に、様々な事件がオムニバス風に展開。ロサンゼルスの郊外を舞台に、20人以上に及ぶ登場人物が織り成すひとつひとつのエピソードは他のエピソードと絡み合い、やがて衝撃のクライマックスへと向かう。", "1994-10-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUSARIYE ABI", "青いベール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "アジアフォーカス・福岡映画祭1995で上映されたイラン映画。トマト農場とソース工場を経営する初老のラスールは妻が亡くなり、子供たちも独立、淋しい一人暮らし。そんな彼が、農場で新しく雇った青いベールの女に惹かれ始める。ノベルという名の女はとても貧しく、ラスールは援助を申し出る。最初は断わったノベルだったが、結局はラスールの願いを聞き入れ、彼の別邸に暮らすことになる。しかし、遺産相続の心配をした子供たちは猛反対をし、ノベルを追い出そうとするのだった。", "1995-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RocknRolla", "ロックンローラ", "", "", "http://image.eiga.k-img.com/images/movie/53531/315x210.jpg?4227", 114, "「ロック、ストック＆トゥー・スモーキング・バレルズ」「スナッチ」のガイ・リッチー監督が、ロンドンを舞台に描く犯罪群像劇。ロシア人マフィアが目をつけた不正な土地取引を引き金に、金に目のくらんだギャングのボスや、ツキに見放されたコソ泥、危険な刺激を求める会計士などがそれぞれ暗躍するが……。出演はジェラルド・バトラー、トム・ウィルキンソン、タンディ・ニュートン、マーク・ストロング、ジェレミー・ピべンら。", "2009-02-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Independence Day", "インデペンデンス・デイ", "", "", "http://ecx.images-amazon.com/images/I/51wAlQ7OKaL._SL210_.jpg", 145, "地球を征服するために巨大なUFOが世界各地を襲い、絶滅の危機に陥った人類が、団結して立ち向かうというSF大作。アメリカ各地に突如現れた巨大宇宙船。何の応答もせず、ただ浮かんでいるだけのその宇宙船が発する信号を分析したN.Y.の天才コンピューター技師デヴィッドは、それが地球を攻撃するためのカウントダウンであることを発見。しかし時すでに遅く、宇宙船は突然攻撃を始め、L.A.やN.Y.は壊滅状態に陥る。絶望する大統領だったが、国防長官から40年前に捕獲した宇宙船と異星人が秘密基地に隠されていることを聞く。その秘密基地に空軍パイロットのヒラー大尉が、空中戦で捕虜にした異星人を連れてやって来たことから基地内は騒然。しかし、それまでふさぎ込んでいたデヴィッドが、宇宙船のマザーシップにコンピュータウイルスを侵入させることで、攻撃のチャンスがあるのではと思いつく。デヴィッドの意見に従い、大統領は最後の攻撃に懸けることに……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tombstone", "トゥームストーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "　アリゾナ・トゥームストーンの町は無法者たちに支配されていた。その地へやってきた元保安官のワイアット・アープは、結核に冒されていた旧友ドク・ホリディと再会。ふたりはワイアットの兄バージル、そして弟モーガンとともにOK牧場で悪党たちと対決し、勝利を収める。しかしその後、兄弟が報復の凶弾に倒れてしまう。ワイアットとドクは復讐を果たそうとするが……。有名なOK牧場の決闘、そしてワイアットとドクの友情が描かれるオールスター・キャストの西部劇。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN THE LINE OF DUTY ： STREET WAR", "ジャンク・アップ・シティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「黒豹のバラード」の監督、M・V・ピーブルズが主演したポリス・アクション。ニューヨークの若き黒人警官ロバートと相棒のレイモンドは将来を期待される有能な警官だった。ある日レイモンドが、麻薬密売を牛耳るバトラーに殺される。上司の制止も振り切り、ロバートは復讐に向かう。TVフィーチャー作品。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不滅の男・エンケン対日本武道館", "不滅の男・エンケン対日本武道館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "1969年のデビュー以来、唯一無二の音楽世界を追求してきた伝説のロック・ミュージシャン遠藤賢司が、自ら監督・主演を果たした異色のライブ映画。観客が一人もいない武道館に巨大ステージを組み上げ、たった一人で演奏するという異様な状況。エンケンのテンションと音楽性の豊かさに圧倒される。武道館に自転車をこいでやって来た遠藤賢司。アンプを背負い、ギターをぶら下げ、巨大ステージに上って『不滅の男』を熱唱し始める。", "2005-10-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("老鼠愛上猫／CAT AND MOUSE", "剣客之恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "北宋時代の中国を舞台に、世直し行脚をする男と武芸に秀でた麗人が織りなす恋愛をコミカルに描いた武侠アクション。ダブル主演のアンディ・ラウ、セシリア・チャンのほか、香港映画界の人気スター総出演による豪華競演も楽しい1本。北宋時代の中国。官吏職を務める展昭は、白玉堂という青年と出会う。意気投合したふたりはその日から友人として付き合うようになるが、展昭は白玉堂が女だという事実を知ってしまう。", "2006-06-10", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT WATCH", "テロリストゲーム2　危険な標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "国際犯罪に立ち向かうUNACO（国連犯罪対策機構）の活躍を描いたシリーズ第２作。今回は北アフリカ、ニューヨーク、香港、アムステルダムを舞台に、ハイテクを駆使した犯罪に挑む。香港の金融市場で謎の爆発事故が発生。インターネットの情報混乱で株価は大暴落し、その余波は瞬く間に世界へと広がっていった。そんな中、アムステルダムではレンブラントの名画が偽物とすり替えられるという事件が起こり、UNACOのマイク・グラハムとサブリナ・カーバーは調査のため現地へと向かう。調査を進めていくうちに二人は、事件の鍵が香港にあると突き止める。", "1995-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PET SEMATARY−２", "ペット・セメタリー２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "スティーヴン・キングの原作を映画化し、大ヒットした前作を受けて作られた第２作。目の前で母親が死んだショックから立ち直れない少年が、死者のよみがえる伝説の墓の存在を知った時……。「ターミネーター２」でティーンのハートをつかんだE・ファーロングが、屈折した少年をナイーブに演じている。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Escape to Victory", "勝利への脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　第2次大戦中の1943年。ドイツ軍の将校シュタイナーは連合軍の捕虜とドイツ・チームのサッカー試合を提案。戦前、イギリス・チームで活躍していた捕虜のリーダー、コルビー大尉はそれを受け入れる。その一方でコルビーは試合を利用した巧妙な脱走計画を立て、アメリカ軍のハッチ大尉ら猛者をメンバーに組み入れる。やがてドイツ対連合軍チームの試合が開始されるが……。壮大な脱走作戦と手に汗握るスポーツ・ドラマが見事に融合。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殿さま弥次喜多", "殿さま弥次喜多", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "“殿さま弥次喜多”シリーズ第３作完結編。八代将軍の候補に選ばれ、缶詰状態になった尾州宗長と紀州義直は、本陣を抜け出し、いろは瓦版屋に逃げ込んだ。そこで記者として働き始める二人だったが、お家乗っ取りを謀る悪家老は、暗殺団を繰り出し、宗長、義直の命を狙い始めるのだった……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Billy's Balloon", "ビリーの風船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 6, "", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さぁ　のはらへいこう　青空自主保育の三年間", "さぁ　のはらへいこう　青空自主保育の三年間", "", "", "http://image.eiga.k-img.com/images/movie/56281/315x210.jpg?4030", 116, "神奈川県鎌倉市で、1985年から野外での自主保育を続けている「青空自主保育なかよし会」に3年間密着したドキュメンタリー。「青空自主保育なかよし会」には園舎もなく、1歳から3歳までの子どもたちが、薄着・裸足で山や海を駆け回って遊んでいる。大人たちは決して手出し口出しをせず見守り、子どもたちは四季を通じて自然と触れ合う中で、ケンカをしながらも助け合いや譲り合う精神を身につけていく。", "2011-05-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WILD ONE", "乱暴者〈あばれもの〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "バイク・レースのトロフィーを強奪した暴走族の一団が田舎町に乗り込んできた。リーダーのジョニーはウェイトレスのキャシーに魅かれ、トロフィーを差し出すが受け取ってもらえない。そこへ敵対する暴走族が現れ、ジョニー一派とトロフィー争奪戦をはじめる。ある夜ジョニーは暴走族からキャシーを助け、彼女もジョニーに好感を抱きはじめるが、ジョニーに反感を持つ町の人々は、彼を誤解し集団リンチを加えてしまう。反抗的で暴力的でありながら、傷つきやすい1950年代のアンチ・ヒーローを、若き日のM・ブランドが強烈な個性で熱演。皮ジャンに身を包み、バイクで疾走する彼のカッコよさは必見だ。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・ガードマン 東京忍者部隊", "ザ・ガードマン 東京忍者部隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "宇津井健主演による「ザ・ガードマン」の映画化第２作。時価２億円の金塊を積んだ輸送車の護衛を請け負ったザ・ガードマンと、金塊強奪をたくらむギャング団との間に壮絶な争奪戦が展開される。アクションにつぐアクションの連続でかなり楽しませてくれる。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シロメ", "シロメ", "", "", "http://image.eiga.k-img.com/images/movie/55591/315x210.jpg?2071", 0, "6人組アイドルユニット「ももいろクローバー」が映画初主演を飾るドキュメンタリータッチのホラー。「ノロイ」「オカルト」などで知られるホラー界の新鋭・白石昇士監督がメガホンをとる。“シロメ”と呼ばれる化け物が潜むと噂される廃墟。そこに足を踏み入れた者は、白目をむいた怪物によって地獄に引きずりこまれてしまうという。真相を確かめるため、テレビ番組の収録で廃墟を訪れた6人は、数々の超常現象に襲われる……。", "2010-08-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("にっぽん三銃士　おさらば東京の巻", "にっぽん三銃士　おさらば東京の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "五木寛之の同名の小説を映画化した岡本喜八監督作品。40代の戦中派、30代の戦後派、20代の戦無派という異なる世代の３人の男が意気投合して引き起こす大騒動を軽妙に描く。小林桂樹が戦中派を好演、岡本監督の心情を代弁する。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チベットチベット", "チベットチベット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "在日韓国人のキム・スンヨン監督が、一個人の目線からチベット問題に迫ったドキュメンタリー。自身のアイデンティティに疑問を抱き、ビデオカメラを手に旅に出たキム・スンヨン監督。インドに亡命しているダライ・ラマ14世に興味を持った彼は、導かれるように北インドへと足を運ぶ。命をかけてチベットの民族性を守りぬこうとする亡命チベット人の姿を目の当たりにした監督は、自分自身の民族性についても見つめなおしていく。", "2008-06-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄昏のアインシュタイン", "黄昏のアインシュタイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "望遠鏡をのぞきながら、宇宙の中の自分たちの存在について問い直す二人の女の子。特急と恋愛の密接な関係を突き止める青年。先祖の霊と語り合う女性。日常的な風景の中に自然の法則を発見していく若者たちを描いた３つの物語に仕立てる。ちょっぴり哲学的で夢を見るような奇妙なエンターテインメント。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE　VISITE　AU　LOUVRE", "ルーヴル美術館訪問", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "セザンヌの強烈な絵画観や世界観を独特の視点で捉えた、コンビ監督によるドキュメンタリー・フィルム。実際の絵画を撮影しているが、セザンヌの言葉から奥深い何かを探ろうとする姿勢からは、劇映画的なアプローチも見え隠れする。セザンヌが観たかもしれない絵画や彫刻、風景を点綴するほか、監督作品「労働者たち、農民たち」の冒頭ショットも収録する。", "2004-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妻よ薔薇のやうに", "妻よ薔薇のやうに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "中野実が新生新派のために書いた『二人妻』を映画化した成瀬巳喜男監督の名作。砂金探しに出たまま家を捨てた男と女流歌人である妻、そして正式な妻ではないものの男に献身的な愛を捧げる芸者上がりの妾の三角関係を、歌人との子である若い娘の目を通して描く。才女だが冷淡な女よりも、優しく無心な女を選ぶ男という劇的構造を持ちながら、各人の微妙な心の揺れを、繊細かつ緻密に表出して単純な図式に陥ることのない演出が秀逸。とりわけ夫を激しく想いながらも、意に反して冷たい態度をとってしまう妻の描写は見事である。娘役を演じる千葉早智子はやがて成瀬夫人となり、その後離婚して映画界から去った。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BOYFRIEND FROM HELL", "多国籍コメディ オージー・サンバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "コメディ・デュオとして数々のヒットアルバムを出し、映画でも活躍のチーチ＆チョンのチーチが一人で出演した、ドタバタ・コメディ。愛しい彼女を幸せにするため、はるばるメキシコからオーストラリアに出稼ぎに来たカルロス。ところが、彼女の方は別な男と結婚。落ち込んでもいられずメキシカン・レストランに職を持ったカルロスだが、生来のお調子者が災いして失敗の連続。おまけにアレックスという女性に、なぜか迫られて……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("だいじょうぶ3組", "だいじょうぶ3組", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("その夜の侍", "その夜の侍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "堺雅人と山田孝之の初共演で、2007年に初演された劇団「THE SHAMPOO HAT」の同名戯曲を映画化。「THE SHAMPOO HAT」で作・演出・出演を手がける赤堀雅秋が、岸田國士戯曲賞にノミネートされた同戯曲を映画用に自ら脚色し、メガホンもとった。鉄工所を営む中村は、5年前に妻をひき逃げ事件で失って以来、無気力な日々を送っていた。一方、ひき逃げ犯の木島は刑期を終え出所したが、しばらくすると匿名の脅迫状が届くようになる。中村の妻の命日に2人はついに対峙することになるが……。", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎と殿様", "男はつらいよ・寅次郎と殿様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "マドンナに真野響子、特別ゲストで嵐寛寿郎が出演。例によって“とらや”の面々とケンカをして寅次郎は愛媛へ。墓参りの美しい未亡人と出会って慕情が募る。そして大洲城18代目の城主だというヘンな老人も登場して……。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VALLE´E FANTO＾ME", "幻の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "50歳代にさしかかった中年の映画監督が、社会に出たばかりの青年とともに、理想の主演女優を求めてヨーロッパやニューヨークをさまよい歩く。「白い町」で知られるスイスの代表的監督A・タネールが名優J・L・トランティニャンを使って撮った作品。理想の女を演じるL・モランテが忘れがたい印象を残す。", "1987-01-01", "26,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Commitments", "ザ・コミットメンツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "アイルランドのダブリンを舞台に、ソウルバンドを結成した若者たちを描いたロディ・ドイルの小説を、イギリスの名匠アラン・パーカー監督が映画化した青春群像劇。本格的なソウルバンドの結成を決意したジミーは、新聞にメンバー募集の広告を掲載する。ところが、彼の元に集まったのはソウルとはとても結びつかない変わり者ばかり。それでも彼は何とかメンバーを集め、ようやく「ザ・コミットメンツ」を結成。早速練習を開始するが……。", "1991-01-01", "33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("J. Edgar", "J・エドガー", "", "", "http://image.eiga.k-img.com/images/movie/57231/315x210.jpg?2612", 138, "FBI（アメリカ連邦捜査局）の初代長官を務めたジョン・エドガー・フーバー（J・エドガー）の半生を、クリント・イーストウッド監督とレオナルド・ディカプリオの初タッグで描くドラマ。1924年、FBIの前身である捜査局BOIの長官に任命され、35年にFBIへと改名した後も、72年に他界するまで長官として在任したJ・エドガーは、カルビン・クーリッジからリチャード・ニクソンまで8人の大統領に仕え、FBIを犯罪撲滅のための巨大組織へと発展させていった。しかし、多くの功績を残した一方で、時に強引な手腕が物議をかもし、その私生活は謎に包まれていた……。脚本は「ミルク」でアカデミー賞を受賞したダスティン・ランス・ブラック。共演にナオミ・ワッツ、「ソーシャル・ネットワーク」のアーミー・ハマーら。", "2012-01-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOES THIS MEAN WE’RE MARRIED？", "私たちが結婚した理由〈ワケ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "スタンダップ・コメディエンヌのアメリカ女性ディーナと、女たらしのフランス人青年作曲家ニックが、お互いの利害から偽装結婚。だが、やがてそれが本物の恋に発展して……。巧みなコメディエンヌぶりを見せるケンジットが、歌に演技に大活躍。女流監督ワイズマンが手掛けたロマンティック・コメディ。", "1991-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLUE MAX", "ブルー・マックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 156, "撃墜王に贈られるブルー・マックス勲章を得るために、ただただ撃墜機の数を増やすことだけに執念を燃やす男スタッヘル。その態度は基地の人々の反感を買ったが、やがて彼は国民の英雄に祭り上げられ、念願の勲章を与えられる。だが英雄は、英雄らしい死を選ばねばならなかった。飛行機のバトル・シーンも見ものの戦争スカイ・アクション。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOHNNY GOT HIS GUN", "ジョニーは戦場へ行った", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "第一次世界大戦で目や耳、さらに手足まで失った青年兵士の苦闘の日々を映像化した反戦映画。“赤狩り”の重圧に耐えつつ幾多の名脚本を発表したトランボが65歳で自ら演出にあたった監督処女作。兵士の過去の記憶と現実の意識がドラマティックに描かれた衝撃作。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MADAM KITTY", "鉄十字の愛人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "第二次大戦下のベルリン。権力欲に燃えるナチス将校バレンベルクは、高級娼館“サロン・キティ”を利用して、将軍や政府要人の秘密を女たちにスパイさせて権力を掌握しようとする。「地獄に堕ちた勇者ども」の続編を狙ったエロチシズム大作。「ナチ女秘密警察・SEX親衛隊」と「サロン・キティ」というタイトルで公開されたこともある。発売時タイトルは「サロン・キティ」。", "1976-01-01", "4,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旗本退屈男 謎の暗殺隊", "旗本退屈男 謎の暗殺隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "何者かの人命調状の呪いにより、将軍・綱吉が日夜病に苦しみ、天下乱るるの兆があった。江戸城中・将軍寝所の床下に呪いの人形を持ち込んだ黒装束の男を、退屈男・早乙女主水之介が取り押さえるが……。退屈男は忍者軍団と死闘を演じる。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'innocente", "イノセント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "　イタリアの人気作家ガブリエーレ・ダヌンツィオの「罪なき者」を原作に、妻と愛人の間で揺れる男の情念を描いたルキノ・ビスコンティの遺作。妻への情熱が薄れてしまったトゥルッリは、伯爵夫人と愛人関係にあった。夫の留守中、孤独な妻のもとに著名な作家が訪れ……。主演は、イタリアのセックスシンボルと言われたラウラ・アントネッリと「セッソ・マット」で共演経験のあるジャンカルロ・ジャンニーニ。", "1979-03-31", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／西便制", "風の丘を越えて　西便制〈ソピョンジェ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "韓国の伝統芸能パンソリの旅芸人一家のたどる運命を、名匠イム・グォンテクが詩情豊かに描き、本国で大ヒットを記録した人間ドラマの佳作。ユボンと娘ソンファ、息子トンホは３人で旅する芸人一家。父は子供たちに厳しい練習を積ませ、芸を仕込むが、トンホはそんな父の情熱を理解できず、姿を消してしまう。", "1993-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベースボールキッズ", "ベースボールキッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "千葉県市原市内のサラリーマンが書いた児童小説を、市民の全面協力のもとに映画化した感動ドラマ。夢中で野球に打ち込む少年たちの姿を通して、親子の絆や人生観を描く。野球は苦手なのに、“市原ジャイアンツ”に所属している12歳の町田翼。いくら練習をしても上達しない翼は、チームメートからいじめられる日々を過ごすが、そんなある日、新人が入団してきて……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDERWATER！", "海底の黄金", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "キューバ沖の海底に眠る300年前の沈没船から宝物を引き揚げるため、海底の捜索を始めた男たち。だが貪欲な漁師のために波乱が起こる。ワイド・スクリーンを縦横に駆使し、水中撮影もふんだんに取り入れた海洋冒険活劇。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Soul to Take", "ウェス・クレイヴンズ　ザ・リッパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「エルム街の悪夢」「スクリーム」シリーズの生みの親ウェス・クレイブン監督による3Dホラー。米マサチューセッツ州にある小さな街リバートンには、かつて連続殺人鬼が「今日生まれた7人の子供を殺しに戻ってくる」と言い遺して死んでいったという伝説があった。16年後、そのリバートンで再び奇怪な殺人事件が発生する……。出演はマックス・シエリオット、デンゼル・ウィテカーほか。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOLD DIGGERS: THE SECRET OF BEAR MOUNTAIN", "ゴールド・ディガーズ　～伝説の秘宝を追え！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ロサンゼルスから母の故郷に引っ越してきたベスは、田舎での生活を退屈に感じていた。そんな折、ベスは野性的な少女ジョディに出会い、父親がいない共通点もあってすっかり意気投合する。二人はある日、目の前のベアー・マウンテンに膨大な金塊が隠されているという伝説を確かめるために、山の洞窟へと分け入るのだが……。C・リッチ、A・クラムスキーという美少女スターの共演で描いたファミリー・アドベンチャー。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Others", "アザーズ", "", "", "http://image.eiga.k-img.com/images/movie/1141/315x210.jpg?3592", 104, "アメナーバル監督が、トム・クルーズからオファーがあった自作「オープン・ユア・アイズ」のリメイクを断って撮ったのが本作（ちなみにリメイクはキャメロン・クロウが監督して「バニラ・スカイ」になった）。撮影はビクトル・エリセ監督の「マルメロの陽光」のハビエル・アギーレサロベ。美術は「エイリアン」以来「グラディエーター」までリドリー・スコット監督との仕事が多いベンジャミン・フェルナンデスが担当。", "2002-04-27", "1,9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("色ぼけ欲ぼけ物語", "色ぼけ欲ぼけ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "上官たちが終戦直後に隠したとされる時価３億円のダイヤモンドの話を聞かされた主人公の金作は、戦友たちとそのダイヤモンドの捜査を開始する。喜劇を得意とする堀内真直演出のユーモア作品。主演は堀内とはなじみの伴淳三郎。脚本には、後の江戸川乱歩賞作家の小林久三が参加している。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MULAN", "ムーラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ディズニーが、中国に昔から語り継がれてきた伝説の少女ムーランを題材にして製作したスペクタクル・ロマン・アニメーション。いにしえの華の都を舞台に、年老いた父の代わりに男となって戦地へ赴いたムーランがフン族を相手に活躍する姿を描く。進化したCG技術によって描かれる二千騎の騎馬軍団の襲撃シーンや、３万人の群衆シーンは圧巻。ムーランの声に「ジョイ・ラック・クラブ」で注目されたM・ウェン。そしてムーランに同行するおしゃべりな守護竜ムーシューの声をE・マーフィが担当している。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HALLOWEEN　RESURRECTION", "ハロウィン／レザレクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "“ハロウィン”シリーズ第８作。不死身の連続殺人鬼マイケル・マイヤーズの生家があるイリノイ州ハドンフィールドで、インターネットのライブ・イベントに参加した若者たちが、その場に出現したマイケルによって次々と抹殺されていく。小型デジタルカメラの粒子の粗い映像を随所に導入。シリーズの顔というべきJ・L・カーティス扮するローリーがマイケルに殺されるエピソードもある。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬神家の一族", "犬神家の一族", "", "", "http://ecx.images-amazon.com/images/I/51OCWFKLg2L._SL210_.jpg", 146, "日本映画に一石を投じた“角川映画”の記念すべき第１回作品。製薬王・犬神佐兵衛が残した遺言状が公開され、その莫大な遺産を相続する権利が３人の孫に与えられる。だがその条件は、佐兵衛の恩人の孫娘・珠世と結婚することだった。珠世をめぐって３人の男たちの間で争奪戦が繰り広げられるが、ついに殺人事件にまで発展する。依頼を受けた名探偵・金田一耕助は捜査に乗り出すが、さらに第２・第３の殺人が……。横溝正史独特のおどろおどろしい世界を、美しい音楽とリリカルな映像処理でセンチメンタルな愛情物語に作り上げることに成功した。", "1976-10-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBERT CAPA IN LOVE AND WAR", "CAPA in Love & War", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "わずか40年でその生涯を閉じた戦争写真家、ロバート・キャパの軌跡をたどる。女優のイザベラ・ロッセリーニが母であるイングリッド・バーグマンとキャパとの情事についての記述を朗読するほか、スピルバーグが監督作「プライベート・ライアン」のオープニングがキャパの写真からインスピレーションを得たものと告白。キャパの人柄から世の中に与えた影響までが伺える秀逸なドキュメントだ。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE JERKY BOYS", "ザ・ジャーキー・ボーイズ　いたずら電話大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "発売後わずか13日でゴールドアルバムとなる大ヒットを記録し、全米ではカルト的人気を博しているザ・ジャーキー・ボーイズのアルバムからヒントを得たE・エステヴェスが、製作総指揮をとった作品。いたずら電話の天才、ザ・ジャーキー・ボーイズがマフィアを相手に大騒動を巻き起こすナンセンス・コメディ。彼らがいちばん得意とするキャラクター、架空の人物フランクになりすましてのイタズラ電話がおもしろい。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOLAR　CRISIS", "クライシス2050", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "日本の原案と資本により、ハリウッドのスタッフ、キャストを得て製作された異色のSFアドベンチャー。西暦2050年、太陽が突然膨脹を始め、荒れ狂う熱と磁気と放射線の牙は容赦なく地球に襲いかかる。最後の手段として反物質爆弾を積んだ宇宙船ヘリオスが太陽に向かうが……。「ブレードランナー」などのヴィジュアル・フューチャリスト、シド・ミードが作り出す2050年の創造物も必見。", "1990-01-01", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NORTHWEST FRONTIER", "爆走機関車 北西戦線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "20世紀初頭1905年のインド。反乱軍が蜂起し、国王を殺害。さらに彼らのねらいは幼い王子へと移る。そこでイギリス軍大尉と家庭教師は、危機の迫った王子を連れてポンコツ機関車で敵陣突破に挑んだ。トンプソン監督による脱出アドベンチャー。", "1959-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("D.P", "D.P", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "「巌流島−GANRYUJIMA−」で独自の解釈に基づく斬新な宮本武蔵像を提示した千葉誠治監督が、TVシリーズ『仮面ライダー 555〈ファイズ〉』の半田健人ら若手俳優たちを起用して撮り上げたバイオレンス・アクション。理由も分からないまま、壮絶な戦いに巻き込まれた７人の若者たちが、首元に刻まれた“D.P”の文字を手がかりに、仕掛けられた謎に挑む。場所も自分自身さえも分からず目を覚ました７人の若者たち。謎に包まれた状況下、彼らに次々と不死身の殺人者が襲いかかる。自分たちの首元に刻まれた“D.P”の文字を手掛かりに、生死を賭けた闘いに挑むが……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOC HOLLYWOOD", "ドク・ハリウッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ビバリーヒルズで一獲千金をもくろむべく、車をとばしていた美容整形医志望の男。だがたった一カ所の標識を見落としてしまったばかりに、南部の小さな町の診療所で医者をするハメになって……。理想とする人生をひょんなことから踏みはずしてしまった青年の悲哀をフォックスが熱演するロマンティック・コメディ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画　ハートキャッチプリキュア！　花の都でファッションショー…ですか!?", "映画　ハートキャッチプリキュア！　花の都でファッションショー…ですか!?", "", "", "http://image.eiga.k-img.com/images/movie/55611/315x210.jpg?1902", 0, "若年層に大人気のTVアニメ「ハートキャッチプリキュア！」の劇場版。ファッションショーにモデルとして出演するため、パリへとやって来たつぼみ、えりか、いつき、ゆりの4人。ところがパリの街では、満月の夜にオオカミ男が現われるという噂が流れていた。そんななか、いつきたちの前に、長きにわたって封印されていたという砂漠の使徒・サラマンダー伯爵に追われる傷だらけの少年・オリビエが現われる。", "2010-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ghostbusters 3", "ゴーストバスターズ3（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARIS-DAKAR15,000　栄光への挑戦", "PARIS-DAKAR15,000　栄光への挑戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "パリからアフリカ西海岸セネガルの首都ダカールまで15000キロを22日間で走破する世界一過酷なラリー、パリ～ダカール・ラリーの第８回（1986年）の模様を描いたドキュメンタリー。日本から出場したACPチームを中心に、その過酷な日々を追う。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陸軍", "陸軍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "西南戦争から日清・日露の両戦争、さらに日中戦争に至る富国強兵政策にダブらせて描く、福岡のある一家の３代にわたる大河ドラマ。田中絹代が出征する息子を捜し、行進する部隊を追っていくラストシーンは感動的である。ただし本作で木下惠介は軍部からにらまれる結果となった。DVDは「木下惠介　DVD-BOX 第１集」に収録。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("哀しい気分でジョーク", "哀しい気分でジョーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "10歳になる息子が脳腫瘍であと数ヵ月の命と知らされたコメディアンの父は、別れた母親のいるオーストラリアへの旅行を計画。しかし、二人だけの旅行を楽しんだ帰路、飛行機の中で息子の命は尽きてしまう。オーストラリア・ロケも美しい、記念すべきビートたけしの初主演作。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Godfather: Part III", "ゴッドファーザーPARTIII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 162, "マフィア・ファミリーのコルレオーネ一族の歴史を綴った大河ロマン“ゴッドファーザー”シリーズの第３部にして最終章となる超大作。物語は前作から20年たった1979年から始まる。コルレオーネ家の権力と財産を継いだマイケルはファミリーの基盤をより大きく確かなものにしていったが、その一方でマフィア世界の中にいる自分の孤独を強く感じていた。多くの愛する者を失ってきたマイケルは、血と暴力による支配から、合法的なビジネスへと一族の事業を移行させるべく努力するが、マイケルの甥で血の気の多い若者ヴィンセントがファミリーに加わったことにより、内部抗争が起こってしまい……。老境に達したマフィアのボスを演じるA・パチーノと、ヴィンセント役に抜擢されたA・ガルシアの新旧スターの共演も魅力だが、なんといっても見どころはオペラ劇場を舞台にしたゴッドファーザー襲撃シーン。ドン・マイケルに迫る暗殺者と阻止しようとするボディガードの対決が、コッポラ得意の絢爛たるモンタージュで描かれていく。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水戸黄門漫遊記", "水戸黄門漫遊記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「女郎蜘蛛の巻」「妖血復讐鬼の巻」「破邪義剣の巻」の３部で構成されるご存じ水戸黄門が大活躍する活劇。美人のコソ泥に持ち金ゴッソリ盗まれてしまう黄門一行。道中、黄門は何者かに命を狙われる。そして犬山城では、お家騒動に巻き込まれる。３部を通して出演するコソ泥のお蝶に扮した千原しのぶが好演。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はいすくーる仁義２　たいへんよくできました。", "はいすくーる仁義２　たいへんよくできました。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "背中に刺青、懐にはピストル、話す言葉は広島弁のヤクザ高校教師・安芸情二。“地獄のキューピー”の異名を持つ彼の活躍を再び描くシリーズ第２作。パワフルな演出が話題を呼んだ自主映画界出身の小松隆志が前作に続いてメガホンを取っている。気になる礼子先生の謎の行動に不安が募る日々を送っていた情二。ある日、舎弟の黒井が政治結社・黒昇会に誘拐されてしまうが……。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOLDIER BOYZ", "ソルジャー・ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ベトナム上空で、国際輸送機が武装テロ集団に襲われ、米財閥の令嬢が人質となった。犯人グループとの交渉が難航する中、カリフォルニア刑務所に服役中のトリバー少佐に、令嬢奪回の極秘作戦が依頼される。州の青少年福祉事業への1000万ドルの寄付と、作戦参加者全員の恩赦を条件に依頼を引き受けたトリバーは、６人の若い囚人とともにベトナムの密林ヘ向かう。トリバーは、クセのある囚人たちに厳しい訓練を強い、やがて彼らは強い連帯感で結ばれていく。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY HOUSE", "MY HOUSE", "", "", "http://image.eiga.k-img.com/images/movie/57771/315x210.jpg?2675", 93, "「20世紀少年」「BECK」など数々の娯楽大作を手がけてきた堤幸彦監督が、音楽や色彩を封印し、実在の人物をモデルとした路上生活者の生き方を通して「本当に必要なものは何か」を問う意欲作。隅田川のブルーシートハウスに住む“都市の達人”鈴木さんを取材し、理想と家と生活のあり方に焦点を当てた、建築家・坂口恭平のルポ「TOKYO 0円ハウス 0円生活」（河出文庫刊）と小説「隅田川のエジソン」（幻冬舎文庫刊）が原作。とある都会の片隅で組み立て式の家に住む鈴木さんは、アルミ缶を拾い集めて換金し、さまざまな不要品を駆使して質素だがそれなりの生活を送っていた。そんな鈴木さんと、エリートコースを目指す中学生ショータや潔癖症の主婦トモコら、交わるはずのない人たちの生活がある事件をきっかけに交錯し……。", "2012-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'Orchestra di Piazza Vittorio", "ヴィットリオ広場のオーケストラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "60以上もの民族が暮らすローマ旧市街のビットリオ広場。ここで長年にわたって愛され続けてきた古く美しい劇場チネマ・アポロを閉館の危機から救うため、ミュージシャンのマリオ・トロンコとドキュメンタリー作家アゴスティーノ・フェッレンテが立ちあがった。人種も宗教も言語も異なるミュージシャンを集めて多国籍オーケストラを結成することになった彼らが、数々の困難を乗り越えてステージに立つまでの5年間を記録したドキュメンタリー。", "2010-11-03", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Amazing Spider-Man", "アメイジング・スパイダーマン", "", "", "http://image.eiga.k-img.com/images/movie/53201/315x210.jpg?4670", 0, "サム・ライミ監督が手がけた「スパイダーマン」3部作（2002、04、07）からリブートされる新シリーズ。「（500）日のサマー」の新鋭マーク・ウェブがメガホンをとり、「ソーシャル・ネットワーク」「わたしを離さないで」のアンドリュー・ガーフィールドが主演する。ごく普通の青年ピーター・パーカーは、13年前に失踪した父親の秘密を思いがけず知ってしまったことから、スパイダーマンとして生きていく決意をする。ヒロインのグウェン・ステイシーをエマ・ストーンが演じ、ベンおじさん役のマーティン・シーン、メイおばさん役のサリー・フィールド、悪役カート・コナーズ博士役のリス・エバンスらが共演。", "2012-06-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALIEN FROM L.A.", "エイリアン FROM L.A. 　地底王国アトランティス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "消息不明の父親の行方を求め、L.A.からアフリカの奥地へ乗り込んだ風変わりな女の子、ワンダ。落盤に遭い地中深くに沈んでしまった彼女は、幻の王国アトランティスにたどり着く。そこには異様な扮装をした地底人たちがいて……。地底人が人間で、地上人がエイリアンという、ユニークな発想のファンタスティック・アドベンチャー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SECOND BEST", "セカンド・ベスト 父を探す旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "両親からの愛情を十分に与えられず、子供の頃から自分を抑制して生きてきた中年独身のグラハムは、自分自身を分かち合うことのできる息子が欲しいと常々感じていた。そんなある日、彼は養子縁組の広告を見つけ、孤児院で暮らすジェームズを選ぶ。二人は親子になるために、食事をしたり、キャンプに行ったりするのだったが……。イギリスの小さな町を舞台に、血のつながりのない父と子の葛藤を描いた感動作。W・ハートが傷ついた少年の心を優しく開かせていくグラハムを好演。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Assassination of Jesse James by the Coward Robert Ford", "ジェシー・ジェームズの暗殺", "", "", "http://image.eiga.k-img.com/images/movie/53241/315x210.jpg?2101", 160, "19世紀後半、北米で最も有名だった無法者ジェシー・ジェームズ（ピット）と、彼を暗殺したロバート・フォード（アフレック）。両者のジェシー暗殺に至るまでの心理を丹念に描き出した西部劇。監督はエリック・バナ主演「チョッパー・リード／史上最凶の殺人鬼」のアンドリュー・ドミニク。製作はピット、リドリー・スコットの他、「許されざる者」（92）のデビッド・バルデス。ピットは本作で第64回ベネチア国際映画祭で主演男優賞を受賞。", "2008-01-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("探偵事務所２３〈ツースリー〉　くたばれ悪党ども", "探偵事務所２３〈ツースリー〉　くたばれ悪党ども", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "大藪春彦の原作を鈴木清順監督が映画化。探偵事務所ツー・スリーの所長、田島英雄は旧知の警部に協力して、銃器取引をめぐって銃撃戦を繰り広げる二大ギャング組織を壊滅させる。この全編奇妙でモダンな感覚にあふれたアクション・コメディは、鈴木清順監督の神話化のきっかけとなった作品であり、これに続くハードボイルド映画の傑作「野獣の青春」への橋渡し的な役割も兼ねていた。宍戸錠が歌い、踊りそしてマシンガンを乱射するというファンにはこたえられないごきげんな主役ぶりを見せてくれる。なお、同年第２作「探偵事務所23・銭と女に弱い男」が作られた。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("源氏物語", "源氏物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "大映創立10周年を記念して作られた大作。製作当時ブームを呼んでいた古典の名作『源氏物語』を、吉村＝新藤コンビは成年した光源氏の１年半の歳月に焦点を絞って描いている。長谷川一夫は、源氏の栄華に隠れた孤独と悲哀を見事に表現した。文豪・谷崎潤一郎が監修を担当している。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAR SLAMMER", "宇宙要塞からの脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "航行中の監獄宇宙船スター・スラマー号を舞台に、惑星アウロスで有罪を宣告された美しい女囚たちが、残酷な女所長や狂暴なエイリアンたちと戦う姿を描いたSF作品。エロティックかつスリリングなアクション・シーンがたっぷりと楽しめる。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビッグ・リバー BIG RIVER", "ビッグ・リバー BIG RIVER", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "国籍も抱えた事情も違う男女が、旅を続ける中でささやかな希望を見いだしていくロードムービー。哲平とパキスタン人のアリは、アリゾナの砂漠のど真ん中で立ち往生しているところを、偶然通りかかったアメリカ人女性サラに助けられる。失踪した妻を捜しにきたというアリの事情に同情し、３人は一緒に旅に出るが……。荒涼とした砂漠や寂れた街の映像が、空虚でありつつも清々しい。", "2006-05-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARQUIS", "マルキ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "登場人物がすべて動物の特殊メイク造形という設定で、マルキ・ド・サド侯爵の真の姿を描く異色ファンタジー。1789年。ちょっとした罪で投獄されたサド。そんな彼の唯一の話し相手は、なんとコランという名の自分の性器だった……。捕らえられた革命の指導者と、彼を助けようとする人々のドラマを、奇想天外なアイデアで描く。日本では東京国際ファンタスティック映画祭で上映されている。", "1989-01-01", "2,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日の果て", "日の果て", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "終戦直前、ルソン島の日本軍は潰滅状態にあり相次ぐ脱走兵に苛立った幹部は、ささいな理由で次々と処刑を行なった。そんな中を脱走した親友の花田軍医を逮捕するため宇治中尉はジャングルに踏み込んで行く……。極限状態にある人間心理を見事に描き、市川崑の「野火」と並ぶ戦争映画の異色作。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Essential Killing", "エッセンシャル・キリング", "", "", "http://image.eiga.k-img.com/images/movie/56251/315x210.jpg?1413", 83, "ポーランドの巨匠イエジー・スコリモフスキ監督が83分間セリフなしで撮り上げ、第87回ベネチア国際映画祭で審査員特別賞、最優秀男優賞を受賞したサバイバルアクション。アフガニスタンで米兵を殺害したアラブ人兵士ムハマンドは、ヘリの爆撃を受け、一時的に聴力を失う。米軍に捕まりどこかへ移送される途中、護送車のアクシデントにまぎれて脱走したムハマンドは、雪に閉ざされた深い森の中を逃走する。主演はビンセント・ギャロ。", "2011-07-30", "42,36,33,30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二人でひとり", "二人でひとり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "青島幸男が「鐘」に次いで発表した風刺喜劇で、青島はここで製作・監督・原作・脚本・音楽・主演という６役を兼ねるという“マルチタレント”ぶりを発揮。父親の囲っている愛人と息子が関係し結婚を宣言するが、父は大反対。ところが、やがて愛人は身ごもり、二人とも自分の子供だと主張したためにひと騒動がもち上がる。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Reservoir Dogs", "レザボア・ドッグス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "Q・タランティーノが、スタンリー・キューブリック監督の「現金に体を張れ」に触発されて撮り上げたというバイオレンス・ドラマ。宝石強盗を実行するために集められた、お互いに名前も素性も知らない６人のクセ者たち。完璧な計画のはずが、彼らは警官隊の襲撃を受ける。誰かが警察に通じていたのだ。命からがら集合場所に集まった彼らの心に、仲間への不信感がわき起こる……。深作欣二監督の「仁義なき戦い」がお気に入りと語るタランティーノらしく、銃撃戦での激しく動き回るカメラや生々しい暴力描写には、その強い影響が見られる。H・カイテルをはじめ、硬派な魅力を発揮する個性派俳優たちのテンションの高い演技合戦も見もの。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alvarez Kelly", "アルバレス・ケリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "南北戦争下の1864年、北軍のために2500頭の牛をメキシコからヴァージニア州へ輸送することになった牧場主アルバレス・ケリーとこれを阻む南軍の大佐との駆け引きと友情を描いたスロー・テンポのドラマ。主題歌はブラザース・フォアが歌っている。DVDは「COLUMBIA TRISTAR　ザ・ウェスタン・ムービーズ Vol.２」に収録。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("True Crime", "トゥルー・クライム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "", "1999-12-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bad News Bears", "がんばれ！ベアーズ", "", "", "http://ecx.images-amazon.com/images/I/51cY77Kwg%2BL._SL210_.jpg", 102, "アメリカ西海岸の町を舞台に弱小野球チームの奮闘を描き、続編やテレビシリーズ化もされた青春スポーツコメディ。メガホンをとるのは、「白銀のレーサー」（1969）などスポーツ映画を得意とするマイケル・リッチー監督。かつてマイナーリーグで活躍していたプール清掃員のモリスは、問題児ばかりの少年野球チーム「ベアーズ」のコーチを市議会に依頼される。モリスはチーム強化のため、不良少年のケリーや元恋人の娘アマンダをチームに招き入れ、ベアーズは快進撃をはじめる。", "1976-12-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TARZAN AND HIS MATE", "ターザンの復讐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ターザン役者の中で最も有名なワイズミュラーのシリーズ第２作。ジャングルで甘い新婚生活を送るターザンとジェーンに危険な敵の影が迫る……。数百頭の象をひきいて猛獣や凶悪な原住民と闘うターザンの勇姿が映える最高作。DVDは「ジョニー・ワイズミュラー ターザン・フィルムズ」に収録。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Clueless", "クルーレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　シェールはビバリーヒルズに暮らす16歳の女子高生。おしゃれ大好き、ダサいこと大嫌いな彼女は、成績アップのために先生に恋の相手を見つけてあげたり、転校生のタイをおしゃれにしてあげたり、と大忙し。リッチな生活を満喫する一方、やがて彼女は本当の自分を、そして本当の恋を知ることに……。ジェーン・オースティンの「エマ」を下敷きに、90年代のティーンエイジャーのライフスタイルを活き活きと描き出した青春コメディ。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROCKY II", "ロッキー２", "", "", "http://ecx.images-amazon.com/images/I/51wp9ZEl5bL._SL210_.jpg", 119, "前作で“アメリカン・ドリーム”を体現したスタローンが、自らメガホンを執った続編。新妻の愛に支えられ、再びリングに立つロッキー。最終15ラウンドの死闘後、その勝敗は？ 信頼し合った男女の深い愛情を織り交ぜた演出が感動を盛り上げる作品。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOWER LEVEL", "視線の先の欲望 9:15P.M.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "偏執的愛情の末に、女を深夜の高層ハイテクビルに閉じ込める男と、そこから必死の脱出を試みる女のサスペンス・アクション。警備員のサムは、自分の警備するビルに勤める建築デザイナー、ヒラリーに思いを寄せていた。しかし身分の違いから愛を告白することもできず、ある計画を思いつく。警備システムの故障を理由に、ヒラリーをオフィス・ビルに閉じ込めるのだ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女殺し油地獄", "女殺し油地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "近松門左衛門の世話浄瑠璃をもとにした古典悲劇で、戦前にも１度映画化されている。河内屋の一人息子・与兵衛は遊女の小菊にいれあげて、莫大な借金をしてしまう。親からも親類縁者からも見はなされた与兵衛は、豊島屋の女房・お吉に相談に行くが、話がもつれ彼女を殺してしまう。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PHYSICAL EVIDENCE", "レンタ・コップ2 証人を消せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "殺人罪で資格を剥奪された刑事と、その無実を晴らすべく奮闘する女性弁護士の活躍を描くアクション。短気な刑事ジョーが口論した男が翌日殺され、彼は刑務所へ。彼の無実を実証しようと女性弁護士ジェニーが事件に臨む。ジョーはあきらめの態度だが、熱心に取り組む彼女にだんだん協力的になっていく。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES CHIMERES DES SVANKSMAJER", "シュヴァンクマイエルのキメラ的世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "幻想的な美しい世界と、生々しいブラックユーモアが混在したシュールな作風で、高い評価を受けるヤン・シュヴァンクマイエル。「オテサーネク」の撮影現場や、個展の模様を追いながらその思想に迫るドキュメンタリー。本人のインタビューや夫人とのプライベート映像などを交えて、その怪しくも目が離せない魅惑の世界の裏側を探る。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雌が雄を喰い殺す 三匹のかまきり", "雌が雄を喰い殺す 三匹のかまきり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "元華族の血をひく京子は、20億の資産にひかれて50男の軍平と結婚する。しかし、軍平の財産を狙うのは京子だけではなかった。軍平が妾に産ませた娘・みどり。処女性を武器に彼に近づく秘書・しのぶ。３人はそれぞれ軍平殺害の策を練るが……。カラリとしたアクションを得意とする井上監督が描く、悪女がうごめく異色ミステリー。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Unstoppable", "アンストッパブル", "", "", "http://image.eiga.k-img.com/images/movie/55191/315x210.jpg?3658", 99, "　「クリムゾン・タイド」「デジャヴ」「サブウェイ123 激突」でタッグを組んできたトニー・スコット監督とデンゼル・ワシントンが、実際の列車暴走事故をもとに映画化したアクション大作。米北東部のペンシルバニアで、大量の化学薬品とディーゼル燃料を搭載した最新式貨物列車が、整備ミスにより暴走してしまう。ベテラン機関士のフランク（ワシントン）は、初めてコンビを組むウィルとともに暴走列車を止めようと奔走する。共演はウィルに「スター・トレック」（09）のクリス・パインのほか、ロザリオ・ドーソン。", "2011-01-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GALAXY OF TERROR", "ギャラクシー・オブ・テラー　恐怖の惑星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "惑星モガンサスで消息を絶った宇宙船リーマス号の救助に向かった捜索隊の一行は、不時着していたリーマス号を発見、さっそく調査を開始する。だが、一人、また一人と、捜索隊員が凄惨な最期を遂げる。典型的なSFホラー映画。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("American Friends", "オックスフォードの恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "19世紀、伝統と格式を重んじ、厳格な規律に縛られたイギリス・オックスフォード大学。恋人との交際すらスキャンダルになる風潮の中で、想いを貫き通した大学教授と米国人女性の恋模様を、さわやかに描いていく。「ステラ」でベット・ミドラーの娘役を演じたT・アルヴァラードがヒロインをみずみずしく好演。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("8 Eight", "8 Eight", "", "", "http://image.eiga.k-img.com/images/movie/55831/315x210.jpg?2026", 0, "ジェーン・カンピオン、ミーラー・ナーイル、ギャスパー・ノエ、ガス・バン・サント、ビム・ベンダースら、世界中から集まった8人の鬼才たちによる短編オムニバス。メキシコ出身の人気俳優ガエル・ガルシア・ベルナルも監督として参加する。それぞれの視点を通し、飢餓、HIV、教育問題などの貧困問題に警鐘を鳴らす。", "2010-11-13", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スキンレスナイト", "スキンレスナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "妻子を持つアダルト・ビデオの監督が、かつての青春時代の夢を交差しながら、自主映画製作に乗り出していくまでの姿を描いた人間ドラマ。かつてピンク映画を数本撮り、AVで活躍していた望月監督自身の自叙伝ともいえる作品。主演の石川欣も、AVも手掛けている映画監督であり、独自の実体験をいかした演技を見せている。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("激殺！ 邪道拳", "激殺！ 邪道拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "千葉真一が少数の主要スタッフだけを日本から引き連れ、東南アジアで製作したカラテ・アクション。千葉演じる無音笛が、超人的な体に生まれ変わり、悪事を重ねるかつての師と兄弟子を倒す。旧態依然としたカラテものだが、千葉の作品に賭ける熱意は伝わる。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お嬢吉三", "お嬢吉三", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "自分たちを牢にぶちこんだ悪旗本、原田にお礼参りを済ませたお嬢、お坊、和尚の三人吉三は、旅に出た。そこで借金のカタに売られた幼なじみのお美和と出会うお嬢。彼は、お美和を救い出そうとするが、彼女は原田のもとに妾奉公に出ることになり、３人は再び江戸に舞い戻る……。市川雷蔵主演の娯楽時代劇。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("True Romance", "トゥルー・ロマンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "エルヴィス・プレスリーとカンフー映画が何より好きなクラレンスは、勤め先のビデオショップの店長が誕生日のお祝いにと差し向けたコールガール、アラバマと意気投合。一目惚れなら善は急げ、と結婚手続きをすませ、彼女の元ヒモと駆け合おうとして殺してしまう。そのうえ、彼女の衣服ケースと思って取ってきたカバンには大量のコカインが……。こうして二人は、ポップな明るさと退却不可能な切迫感に彩られた、愛と暴力の一本道を疾走する。ヒロイン役のP・アークエットが、ハスッパな可愛さと、血にまみれてなお笑みを浮かべるイカれた一途さを演じてチャーミング。", "1994-01-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴風圏", "暴風圏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "企業ドラマとパニック・ムービーを融合させた快作で、ヒロイン映画としての側面も。運輸会社の社長が死んで、その娘の美沙が新社長になり会社再建を計るが、会社乗っ取りを企む藤川がいろいろ難癖を付けてくる。一方、美沙が融資を願い出た建設会社の建設中のダムが、台風の接近による大岩の落下で壊れそうに。そこでトラックに爆破用のダイナマイトを積み、美沙らは急行するが……。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shutter", "シャッター", "", "", "http://image.eiga.k-img.com/images/movie/53601/315x210.jpg?2627", 85, "オール日本ロケのハリウッド発スピリチュアル・スリラー。監督は「感染」「催眠」の落合正幸、製作に「リング」「THE JUON／呪怨」でハリウッド進出に成功した一瀬隆重、主人公たちを追い詰める謎の女で奥菜恵が出演している。新婚夫婦のベンとジェーンは、ベンの出張と新婚旅行を兼ねて日本へやってくる。2人は夜の山道で女を轢いてしまうが、女はその場から忽然と姿を消す。その事故が起きてから2人の旅行に不吉な影が漂い始める。", "2008-09-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AN UNSUITABLE JOB FOR A WOMAN", "女には向かない職業", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "人気作家P・D・ジェームズの“女探偵コーデリア・グレイ”シリーズの第１作を映像化したサスペンス。駆け出しの女探偵コーデリアは自殺した青年の動機について調査を依頼される。単身捜査を始めた彼女は事件が意外なほど複雑であることを知ると同時に、何者かに命を狙われ始める。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kiss of the Dragon", "キス・オブ・ザ・ドラゴン", "", "", "http://image.eiga.k-img.com/images/movie/1481/315x210.jpg?4364", 98, "中国とフランスで麻薬密売を企てる中国人ギャングを追ってパリに来たエリート捜査官リュウ（リー）。現地警察の警部（カリョ）は非協力的だが、組織の娼婦（フォンダ）と出会ったリュウは、事件に巻き込まれていく。映画初挑戦する監督クリス・ナオンはCMやMTVで活躍のフランス生まれの32歳。脚本はベッソンと「フィフス・エレメント」のロバート・マーク・ケイメン。撮影は全ベッソン作を務める、ティエリー・アルボガスト。", "2001-08-25", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大閲兵", "大閲兵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "1984年、建国35周年記念の国慶節閲兵式に向け、厳しい行進訓練に励む若い兵士たちの姿を描いた作品。天安門前広場のわずか96歩の行進のため、半年間にわたり来る日も来る日も歩き続ける兵士たちと教官。それぞれ悩みや欠点を抱える人間味あふれる空挺部隊の面々のひたむきな訓練と空しさを、カイコー監督が迫力ある映像スペクタクルで描く。中国国内では修正が加えられ、上映まで2年の歳月を要した作品。", "1985-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A STAR IS BORN", "スター誕生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "３度目の映画化。前作と異なり、女の自立意識を前面に押し出したあたりが、いかにも1970年代らしい“女性映画”。今や落ち目のロック・スター、ジョンは、人気上昇中の女性シンガーである妻に、嫉妬交じりのやりきれなさを感じていた。そしてついに……。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARLOWE", "かわいい女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ハードボイルドの代表的な作家チャンドラーの同名原作の映画化。行方不明になった兄の捜査をある女から依頼されたマーロウが危険な目に遭いながらも、犯人を追い詰めていく過程が小気味よいタッチで描かれている。B・リーがチョイ役で顔を見せる。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ФРАГИ，РАЗЛУЧЕННЫЙ СО ЧАСТВЕМ", "詩人フラーギ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "18世紀のトゥルクメン共和国。部族間の統一戦争が絶えず繰り広げられる中、名門の息子フラーギは村の娘メングリと恋仲になるが、彼女は抗争に巻き込まれ、有力な地主の側室へ献上されてしまう。戦争の悲惨さと失恋を同時に経験したフラーギは、やがて平和と愛の詩歌を創り出す国民的詩人となっていく。旧ソビエト連邦、トゥルクメン共和国の歴史大作。", "1984-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TERMINAL　RUSH", "ターミナル・ラッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "巨大ダムを舞台としたパニック・アクション。アメリカで最強の安全システムを誇るクーパーダムが、元軍人デッカー率いる傭兵部隊に占拠された。ダムで働く人々を人質に取り、ダムを次々に爆破させていく彼らに、元特殊部隊のハーパーの怒りが炸裂。ダム崩壊阻止と人質奪回のために奮闘していく。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血と掟", "血と掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "元安藤組組長・安藤昇の手記を映画化。戦後の焼け跡、暴力が横行する中で安藤組を結成。幾度かの暴力抗争を繰り返して巨大組織に発展するが、組長が逮捕されて組織が解散に追い込まれていくまでを、安藤自身の主演で描く、実録タッチのヤクザ映画。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAD MOON", "バッド・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "「ヒッチャー」「ブルースチール」の脚本家E・レッドが監督したホラー作品。ネパールのジャングルで狼に襲われ、大ケガを負ったテッド。しばらくして、帰国したテッドを訪ねた姉ジャネットとその息子ブレッドは彼に異変を感じる。夜、森の中に姿を消したテッドを追ったジャネットは彼が狼に変身するのを目撃。秘密を知った彼女は狼男と化したテッドに襲われるが……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月光仮面", "月光仮面", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "1950年代に国民的人気を博した覆面ヒーロー・月光仮面を1980年代によみがえらせ、その活躍を描いたヒーローもの。“愛の理想郷”作りを目指して次々と現金輸送車を襲ってはその資金にあてるレッド・マスク団と、正義の味方・月光仮面の対決を描く。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("湯殿山麓呪い村", "湯殿山麓呪い村", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "決して掘り出してはならぬとされる、湯殿山麓・弥勒寺の地下に眠る幽海上人の即身仏（ミイラ）発掘に興味を抱いた、大学の歴史学者が、次々と不思議な事件に巻き込まれていく。古い宗教の即身成仏伝奇をもとにしたミステリー。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BATTAGLIA EL ALAMEIN", "砂漠の戦場 エル・アラメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "1942年の北アフリカ、エル・アラメンを舞台にドイツのロンメル将軍とイギリス第８軍指揮官モントゴメリー将軍の策略争いと、イタリアのフォルゴーレ空挺部隊の激戦を描く戦争アクション。", "1969-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スクール・ウォーズ HERO", "スクール・ウォーズ HERO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "1980年代にTVドラマ化されて人気を呼んだ、名門・伏見第一工業高校ラグビー部の実話を映画化。ラグビーを通じて不良少年たちを立ち直らせようとする、熱血教師の奮闘劇が展開する。生徒に接することを諦めず、体当たりで指導に当たる教師と、そんな姿に心を開いていく若者たち。熱い思いが伝染していくかのような、彼らの交流のドラマが感動的だ。1974年の京都。校内暴力で荒廃した高校に赴任した山上修治は、元ラグビー全日本のスター選手。“この学校をラグビーで変えてみせる！”と情熱を燃やすが、現実は厳しく、タバコに麻雀に放火と、学内は無法地帯と化していた。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最後の忠臣蔵", "最後の忠臣蔵", "", "", "http://image.eiga.k-img.com/images/movie/55071/315x210.jpg?3823", 133, "TVシリーズ「北の国から」を手がけた杉田成道が、池宮彰一郎の人気小説を役所広司、佐藤浩市主演で映画化。赤穂浪士の吉良邸討ち入りで、大石内蔵助率いる46名が切腹により主君に殉じた中、密かに生き残った瀬尾孫左衛門（役所）と寺坂吉右衛門（佐藤）という2人の武士がいた。討ち入りの事実を後世に伝えるため生かされた寺坂は、事件から16年後、討ち入り前夜に逃亡した瀬尾に巡り会い、瀬尾の逃亡の真相を知る。", "2010-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cave of Forgotten Dreams", "世界最古の洞窟壁画 3D 忘れられた夢の記憶", "", "", "http://image.eiga.k-img.com/images/movie/57271/315x210.jpg?1571", 90, "「フィッツカラルド」「神に選ばれし無敵の男」の鬼才ベルナー・ヘルツォークが、現存する世界最古の壁画を有する南仏・ショーベ洞窟の内部を3Dで撮影したドキュメンタリー。1994年に発見されたショーベ洞窟の奥には3万2000年前に描かれた壁画が奇跡的に当時のまま現存していた。貴重な遺跡を守るため普段は研究者や学者にのみ入場が許可されるショーベ洞窟に、ヘルツォーク監督率いる撮影隊が入り、3Dカメラによる撮影を敢行。壁画を描いた古代の人々へ思いを馳せる。", "2012-03-03", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLOSE ENCOUTERS OF THE THIRD KIND ： THE SPECIAL EDITION", "未知との遭遇〈特別篇〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "地球人と異星人との接触を描き世界的にヒットした作品をS・スピルバーグ自身が、新たなシーンを加え編集し直したもの。巨大マザーシップの内部がのぞけるほか、R・ドレイファスが演じる電子技師ロイの家庭内でのいざこざが詳しく描かれている。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("友よ、静かに暝れ", "友よ、静かに暝れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "町の顔役と癒着する警察に逮捕された大学時代の旧友の窮地を救うため、主人公が巨大な組織に挑んでいくという、ハードボイルド・タッチの友情物語。藤、原田、倍賞はいずれもハマリ役。沖縄を舞台にした映像も、独特の雰囲気が出ている。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("King Solomon's Mines", "キング・ソロモン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "H・ライダー・ハガードによる人気冒険小説を、イギリスのロバート・スティーブンソン監督が初映像化したアドベンチャー作品。金剛石の発掘でひと山当てようとアフリカへやって来たイギリス人オブライエンとその娘ケイシー。ところが計画は上手くいかず、たちまち資金難に陥ってしまう。そんなある日、瀕死のスペイン人から“ソロモン王の宝窟”の地図を手に入れた彼らは、案内人アランらとともに、宝探しの冒険に乗り出すが……。", "1937-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白い馬", "白い馬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "椎名誠の第４作は、大草原に生きるモンゴル遊牧民の少年ナランのひと夏の物語。冬営地から夏営地へ移動する大家族を通して、自然、家族愛、成長、死を悠々としたタッチで捉え、モンゴル最大の祭り、ナーダムの少年競馬で大いなるクライマックスを迎える。出演者は全員モンゴル人、モンゴル語、オール・モンゴル・ロケによる製作で、民主制に移行しつつあるモンゴル社会、その影響を受けつつも古くからの生活を頑に守る遊牧民など、現代モンゴルの姿を忠実に描いている。DVDは「ホネ・フィルム式活動寫眞全記録　椎名誠　DVD-BOX」に収録。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遙かなるふるさと　旅順・大連", "遙かなるふるさと　旅順・大連", "", "", "http://image.eiga.k-img.com/images/movie/56171/315x210.jpg?2673", 110, "「痴呆性老人の世界」（1986）、「嗚呼　満蒙開拓団」（2008）などで知られる羽田澄子監督が自身のルーツをたどり、半生を語るドキュメンタリー。1926年、中国・旧満州の大連で生まれた羽田監督は、旅順で育ち、大連に戻った際に敗戦を迎えた。再びその地を訪ねた羽田監督が重い歴史と向き合う。", "2011-06-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NORMAL LIFE", "ボディ・リップス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "警官のクリスは不思議な魅力を持ったパムと知り合い、結婚する。だが、普通の暮らしを望む彼と刺激を求める彼女の間にケンカは絶えなかった。そのうえ、正義感が強いために同僚とトラブルを起こしたクリスは辞職してしまう。金に困った彼はパムには内緒で銀行強盗を重ねていき、それを知った彼女も仲間に加わる。マイホームを手に入れたクリスは強盗の中止を申し出るが、犯罪に快感を覚えるパムは続行することを願い……。犯罪に取り憑かれた二人が堕ちていく姿を、「恋に落ちたら…」の鬼才J・マクノートン監督がドライなタッチで綴った、愛とバイオレンスのクライム・サスペンス。孤独を嫌い、ドラッグに溺れていくパムを「評決のとき」「ヒート」で注目を集めたA・ジャッドがエキセントリックに演じる。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Red Shoes", "赤い靴　デジタルリマスター・エディション", "", "", "http://image.eiga.k-img.com/images/movie/56301/315x210.jpg?4607", 136, "アンデルセンの童話「赤い靴」をベースにバレエダンサーの愛憎と悲劇を描いた名作を、マーティン・スコセッシ監修のもと、2年間の歳月をかけて修復したデジタルリマスター・エディション。ロンドンのバレエ団にバレリーナのビッキーと新人作曲家の青年ジュリアンが入団。アンデルセンの「赤い靴」をモチーフにした新作バレエが大成功を収め、2人はやがて愛し合うようになる。しかし、バレエ団を主宰するレイモンドは、ビッキーがバレエだけに集中するようジュリアンをクビにしてしまい……。", "2011-07-02", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dreamer: Inspired by a True Story", "夢駆ける馬ドリーマー", "", "", "http://image.eiga.k-img.com/images/movie/1371/315x210.jpg?4029", 106, "実話を元に、骨折した競走馬と、その馬といっしょに立ち直っていく調教師一家を描く感動ドラマ。調教師ベンが託された馬ソーニャドールは、レースで転倒して骨折。ベンは雇い主にこの馬の安楽死を命じられるが、娘ケールと共にこの馬を引き取り、その再起を目指すことを決意する。「コーチ・カーター」などの脚本家ジョン・ゲイティンズの監督デビュー作。「宇宙戦争」の名子役ダコタ・ファニングが、馬を愛する少女ケールを熱演。", "2006-05-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The September Issue", "ファッションが教えてくれること", "", "", "http://image.eiga.k-img.com/images/movie/54721/315x210.jpg?4231", 88, "毎号、アメリカ女性の10人に1人の約1300万人が購読する米ヴォーグ誌の編集長アナ・ウィンターに迫ったドキュメンタリー。2007年秋のファッション特大号である9月号の締め切り5カ月前、編集部やデザイナーの提案する衣装にダメ出しをしたり、コラボレーション企画を開発したり、分刻みで仕事をこなしていくアナ。スパルタながらも真摯に仕事に取り組むアナの素顔を映し出す。", "2009-11-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Timeline", "タイムライン", "", "", "http://image.eiga.k-img.com/images/movie/1201/315x210.jpg?4612", 116, "マイケル・クライトンの同名小説を「リーサル・ウェポン」シリーズのリチャード・ドナー監督が実物大の城を建設してリアルに映画化。量子力学を用いて時間移動を可能にした装置で、考古学教授が中世の英仏百年戦争時代へ。しかし彼は帰還せず、その息子と教え子たちが同時代に彼を捜索に行き、野蛮な中世の戦闘に巻き込まれる。「ワイルド・スピード」のポール・ウォーカー、「トゥームレイダー2」のジェラルド・バトラーが出演。", "2007-01-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("French Connection Ⅱ", "フレンチ・コネクション2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "　アカデミー賞5部門受賞作の続編。前作で麻薬組織のボスを逃がしてしまったポパイことドイル刑事は麻薬ルート壊滅のため、フランスに乗り込む。しかし彼は敵の組織によって麻薬を打たれ、中毒の身に。必死のリハビリを経て、ポパイは再び立ち上がるが……。監督は交代したが、スピーディな演出やアクションの迫力は決して前作に劣っていない。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE RUFFIAN", "冒険野郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「冒険者たち」のL・ヴァンチュラが、ここでは金鉱探しに命を懸ける無鉄砲だが心優しい男を演じる。マフィアとの撃ち合いシーン、渓流での宙吊りシーン、さらには水中撮影の潜水シーンなどハラハラドキドキの連続だが、ヴァンチュラのキャラクターによって、ほのぼのムードのアクション映画に仕上がっている。共演はB・ジロドーと、イタリア映画界のスター女優C・カルディナーレ。実際に刑務所生活の経験があるというフィルム・ノワールのベテラン、J・ジョヴァンニがリアリティーのある演出を行っている。", "1982-01-01", "2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女は二度生まれる", "女は二度生まれる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "川島雄三監督が大映で初めて撮った作品。九段の芸者街の純情な芸妓の自由な恋愛遍歴。学生と上高地へ向かうが一人だけ帰ってきてしまう不思議なラストシーンが魅力的。人間の生きる悲しさを象徴的に描こうとしている。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Planet of the Apes", "猿の惑星", "", "", "http://ecx.images-amazon.com/images/I/51M0S86043L._SL210_.jpg", 112, "地球から320光年の長い旅の末、とある惑星に不時着した宇宙飛行士。そこは、言葉を話す猿たちが原始的な人間たちを支配する惑星だった。人間狩りをする猿の軍隊に捕らえられた主人公テイラーは、コーネリアスとジーラという猿の助けを借り、一緒に捕らえられた人間の娘ノバを連れ逃亡を図るのだが……。SF映画の金字塔となった大ヒットシリーズの第1作。精巧な猿の特殊メイクは高い評価を受け、アカデミー特別賞を受賞した。", "1968-04-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦艦大和", "戦艦大和", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "時代遅れの大艦巨砲主義の頂点でありながらも、日本海軍の象徴として、また日本人の魂の故郷として捉えられることも多い戦艦大和が、1945年４月７日、菊水作戦で沖縄に向かう途中、数百機の米軍戦闘機の猛襲によって沈没していくさまを描いた壮絶な超大作。当時としては画期的なミニチュアワークなど映像的見どころも多数。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIDNIGHT RIDE", "ミッドナイト・ライド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「スター・ウォーズ」のルークことM・ハミルが逃亡中の殺人鬼に、その殺人鬼と遭遇する医者にR・ミッチャムが扮するスリラー。警官ローソンとの生活にあきあきした妻ローラは家を出て、途中一人のヒッチハイカーを拾う。何も知らずに旅を続けるローラだったが、そのヒッチハイカーは人々を恐怖のどん底に叩き落とした連続殺人鬼だった。妻の危険を知ったローソンは、すぐに後を追うが……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KORKARLEN", "霊魂の不滅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 49, "サイレント期のスウェーデンで監督、俳優として名作を残し、後にハリウッドに渡って活躍したV・シェーストレムの代表作。大晦日の夜、酒に酔った浮浪者ダービッドは口論がもとで殴られ、倒れた。彼の目の前には死の馬車が現れ、大鎌を持った御者が彼を連れ去ろうとする。その同じ頃、ダービッドの更生を助けてきた救世軍の女性が、病の床で神にダービッドを救ってくれるよう祈っていた……。死の御者や霊魂が人間界をさまようシーンで使われる合成画面の技術の高さには、これが無声映画かと驚くばかり。神秘主義とも評されるスウェーデン映画を一躍世界に知らしめることになった傑作である。", "1920-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超能力だよ全員集合!!", "超能力だよ全員集合!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "易者・井刈長山の占いは当たったためしがない。そこへ記憶喪失のヒデオがやって来る。長山はヒデオが超能力者であることに気づき“知らない様”と命名して大もうけ。実はヒデオは秋田の材木屋の跡取り息子で、姉・しのが上京してくる。長山は性懲りもなく一目惚れ。荒井注に代わり志村けんが新たに加入。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Running Wild", "美しき野獣", "", "", "http://image.eiga.k-img.com/images/movie/1281/315x210.jpg?2236", 125, "「恋する神父」のクォン・サンウと「オールド・ボーイ」のユ・ジテ主演による男と男の友情ドラマ。自分の力を信じて腕力で犯罪を制しようとする若き刑事、チャン・ドヨン。あくまでも法律で事件を解決しようとするソウル中央地検のエリート検事オ・ジヌ。正反対の男たち2人が、同じ事件を手掛けて大物犯罪者を追いつめていく。「親切なクムジャさん」などで知られるパク・チャヌクらの助監督を務めてきたキム・ソンスの監督デビュー作。", "2006-02-11", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STORY　OF　O　UNTOLD　PLEASURES", "新・O嬢の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「エマニエル夫人」のジュスト・ジャカン監督作品をはじめ、これまで何度も映画化されてきた文芸エロスの古典を、現代版にリメイク。舞台をアメリカに置き換え、経済的問題に悩む女流カメラマンが、援助と引き換えに自らの肉体を差し出し、倒錯した性の世界を体験していく。才能がありながら、経済的な理由で写真集が出版できずにいる美しい女流カメラマン“O”。そんな時、恋人の知人スティーヴン卿から援助の申し出が。しかし、それを承諾する事は、彼女にとって陵辱の日々の始まりでもあった。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("用心棒", "用心棒", "", "", "http://ecx.images-amazon.com/images/I/51Ct%2Bh4um0L._SL210_.jpg", 110, "二組のやくざが対立するさびれた宿場町。そこへ一人の浪人者がふらりと流れ着く。男はやがて巧みな策略で双方を戦わせ、最後には自らの刀を抜きやくざたちを倒す。町の平和を取り戻した彼は、またいずこへとも知れず去っていく……。時代劇に西部劇の要素を取り入れた痛快娯楽活劇。ピストルにマフラーのニヒルな殺し屋を演じた新鋭・仲代達矢の存在感が光る。64年にはセルジオ・レオーネ監督が本作をもとにマカロニウエスタン「荒野の用心棒」を作り、大ヒットした。", "1961-04-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("老寅出更／TIGER ON THE BEAT", "タイガー・オン・ザ・ビート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "香港の人気男優チョウ・ユンファ主演の刑事アクション。腕利きだが、少しいい加減な刑事フランシスは、真面目なマイケルとコンビを組んで麻薬捜査を始める。しかしフランシスがマイケルの妹ミミに惹かれ始めたことから、二人の仲は崩れ始めていく。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刑務所破り", "刑務所破り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "松方弘樹主演の大映任侠映画。流れ者の誠は兄貴分の遺志を継ぎ、刑務所を脱獄して仇敵・天王寺組を倒す。快調なテンポと壮絶な殺陣で、本家東映にひけをとらない仕上がり。兄貴分の田村高廣は貫禄十分。山本麟一が悪の総元締めとして準主役なのも魅力だ。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("森の石松", "森の石松", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "おなじみの森の石松を羅門光三郎が演じた股旅もの。羅門は、東亜、宝塚キネマ、極東キネマなどの弱小プロを流れ歩いた、股旅を地でいく戦前の“三流キネマ”のスター。本編は新興キネマ入社第１作で、以降、彼は役者としてやっと檜舞台へ上がる。この作品は、広沢虎造の浪曲が画面にかぶる浪曲映画で、これはのちの次郎長ものにも受け継がれ、マキノ雅広の「次郎長三国志」によってその手法は結実した。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Day the Earth Stood Still", "地球が静止する日", "", "", "http://image.eiga.k-img.com/images/movie/53211/315x210.jpg?2058", 106, "「ウエスト・サイド物語」「サウンド・オブ・ミュージック」の巨匠ロバート・ワイズ監督の名作「地球の静止する日」（1951）のストーリーを一新し、最先端のVFXを用いて描くSF大作。主演はキアヌ・リーブス、共演にオスカー女優ジェニファー・コネリー、監督は「エミリー・ローズ」のスコット・デリクソン。ある日、未知のテクノロジーとパワーを持つ宇宙からの使者が地球に降り立つ。クラトゥと名乗る彼の目的は“地球を救う”ことで、「人類が滅亡すれば、地球は生き残れる」と言うが……。", "2008-12-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リズムでてあそび　アンパンマンとふしぎなパラソル", "リズムでてあそび　アンパンマンとふしぎなパラソル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-07-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NUOVA GUINEA，DEI CANNIBALI", "残酷人喰大陸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ニューギニアを舞台に、原住民の恐るべき生態を映しとった衝撃のドキュメンタリー。死体にたかるウジ虫を食うクククク族、指切断や鼻に穴をあける儀式、ワニに食われる男など数々の残酷シーンに加え、部族抗争で殺した相手を焼いて食べてしまうという信じがたい現場も収録されている。", "1955-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("我願／MY WILL，I WILL", "相続ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "弟に会社を乗っ取られそうな友人を助けようと、敏腕探偵アレックスが動き出した。調査の結果、弟のバックにはエリックという男が動いており、彼は大富豪ウォン女史の遺産相続人を探す仕事も引き受けていた。ところがウォン女史はなんとアレックスの元恋人だった。入り組んだ人間関係が大逆転に……。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CYBORG３", "サイボーグ3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "荒廃した近未来を舞台に、地球の未来を握る女サイボーグ、キャッシュを守る戦士の死闘を描いたシリーズ第３作。妊娠していることを知ったキャッシュは、自分を救える天才プログラマー、エバンスを探しに出かける。一方、サイボーグの敵リサイクラーたちも妊娠に気が付き、キャッシュを執拗に攻撃。何とかエバンスと出会え、リサイクラーたちの元から脱出した二人は、サイボーグの最後の避難場所サイタウンへと向かうのだった……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　CORPORATION", "ザ・コーポレーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "'05年のサンダンス映画祭を筆頭に、多くの国際映画祭で数々の賞を獲得した社会派ドキュメンタリー。社会に大きな影響を与える存在となり、その倫理観が常に問われる“株式会社”や“企業”の実態をひもとく。「華氏911」のマイケル・ムーアや著名人が、企業の悪行を赤裸々に告発している。世界中に存在し、多くの人が属す“株式会社”や“企業”。その成り立ちから現在に至るまでの軌跡を検証する。", "2005-12-10", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alfred Hitchcock and the Making of Psycho", "アルフレッド・ヒッチコック・アンド・ザ・メイキング・オブ・サイコ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「めまい」「39階段」などで知られるサスペンスの巨匠アルフレッド・ヒッチコックが60年発表の名作「サイコ」を完成させるまでのストーリー。ヒッチコックに名優アンソニー・ホプキンスが扮。", "2013-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANDREJ TARKOVSKY IN “NOSTALGHIA”", "タルコフスキー・ファイルIN「ノスタルジア」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「ノスタルジア」を撮ったタルコフスキーの足跡を追いながら、彼の人間像を捉えたドキュメンタリー映画。1983年、タルコフスキーは初めて故国ソ連を離れ、イタリアで「ノスタルジア」を撮った。そのタルコフスキーの人間としての姿、詩人としての姿を捉えるため、撮影現場の記録・共同脚本家のトニーノ・グエッラ、撮影のジュゼッペ・ランチ、俳優のオレーグ・ヤンコフスキーやエルランド・ヨセフソンらへのインタビュー、また「ノスタルジア」の本編を交錯させながら、タルコフスキーという稀有な映画作家の実像を浮き彫りにしていく。監督のD・バリーボはこの他にもタルコフスキーについて２本撮っており、これがいわばタルコフスキー３部作の最後にあたる。", "1984-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼あざみ", "鬼あざみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "京都から江戸へ遷都されようとしている江戸末期。大赦で島から返された囚人の中に、“鬼アザミ”の異名をとる並木禮三郎の姿があった。彼はかつて代官の息子であったが代官所の用人・軍兵衛の策略で没落。恋人・お光とも別れ、強盗の一味になったのだが……。長谷川一夫が、流転する男の哀感をにじませた時代劇。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WINTER　SLEEPERS", "ウィンタースリーパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "「ラン・ローラ・ラン」のT・ティクヴァ監督の長編第２作。記憶障害を持つレネが友人の車を運転中、雪山で事故を起こす。しかし、事故の記憶は彼の頭の中から消え去っていた。事故で娘を失い復讐に燃える男、記憶を写真で残そうとするレネ、そして彼を取り囲む友人たちの複雑な人間関係を渋いタッチで描き出す。", "1997-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("現代任侠史", "現代任侠史", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ヤクザから足を洗い、すし屋を営む島谷は、戦死した亡父の遺品である日本刀を受け取りに週刊誌記者とともにアメリカへ。一方国内では、自分の代わりに組を継いでくれた松田が、ほかの親分衆によって圧迫されつつあった。切れ目なしに続いていた東映任侠映画路線がいったん途切れたあとの、新味を模索した復興作。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボールZ　危険なふたり！ 超戦士はねむれない", "ドラゴンボールZ　危険なふたり！ 超戦士はねむれない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "“'94春東映アニメフェア”の一作として春休みに公開されたシリーズ第13弾。7つのドラゴンボールが共鳴して見事な奇跡が到来する、冒険アクション・アニメの快作。孫悟空亡きあと、孫悟飯と孫悟天が跡を継いでいた。彼らがドラゴンボールを探す旅に出てある村に到着したが、かつて悟空が倒した伝説の超サイヤ人が蘇り、彼らに襲いかかる。DVDは『DRAGON BOX THE MOVIES』に収録。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Because of Winn-Dixie", "きいてほしいの、あたしのこと　ウィン・ディキシーのいた夏", "", "", "http://ecx.images-amazon.com/images/I/51-N9dZWy7L._SL210_.jpg", 0, "", "2005-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST OF THE MOHICANS", "モヒカン族の最後", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "英・仏の植民地戦争が続くアメリカ。部族の存続のためにイギリスと手を組むことになったモヒカン族の酋長の息子アンカスは、イギリス軍人の娘コーラと人種を超えた愛で結ばれる。しかし、フランスと組むヒューロン族の長マグアの陰謀のために壮絶な死を遂げる……。その後、「ラスト・オブ・モヒカン」など何度も映画化された原作の３度目の映画化。スケールの大きい戦闘シーンや美しい大自然の描写が見もの。", "1920-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三味線とオートバイ", "三味線とオートバイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ボーイフレンドとオートバイでツーリングに出かけた初子はトラックにはねられケガをする。初子の母親・とよ枝は駆けつけた病院で、医師の畔柳と20年ぶりに再会する。初子はとよ枝と畔柳の間の子供であったが、本人は知る由もなかった。桑野みゆきが揺れ動く少女の気持ちを巧みに表現している。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌え！青春 はりきり娘", "歌え！青春 はりきり娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "美空ひばり主演の歌謡映画。新米のバスガールのトミコは、美空ひばりにそっくりだが、顔のホクロのために“びばりちゃん”と呼ばれている。父の家出と音痴に悩む彼女は、ひょんなことから本物の美空ひばりと対面することに。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　PLUS　VIEUX　ME´TIER　DU　MONDE", "愛すべき女・女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "史上最も古い職業ともいわれる娼婦を主人公にしたオムニバス映画。初めての娼婦誕生を綴った有史以前の第１話を皮切りに、古代ローマ、フランス革命などの６つの時代を背景にしたエロティックかつユーモラスなエピソードで構成されている。中でも自由な恋愛を禁じられた近未来を舞台にした、第６話のゴダール編が異彩を放っている。", "1967-01-01", "2,7,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さよなら、クロ", "さよなら、クロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "長野県松本市の高校に住みつき、10年以上も教師や生徒に愛された野良犬の実話を映像化。ただそこに住んでいただけで、人々を励まし続けていた犬の存在を通して、青春群像を見つめる。学園祭で賑わう高校に一匹の犬が迷い込んだ。クロと名付けられたこの犬は学校のマスコット的な存在となり、鎖につながれることなく悠々と時を過ごしていく。10年後、獣医となった卒業生、亮介はクロが病に冒されていることを知り看てみると……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎物語", "男はつらいよ・寅次郎物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "寅次郎の香具師仲間の遺児が“とらや”を訪れ、寅は一緒に母親探しの旅に出る。大阪から吉野へ来て高熱を発した子供の看病をしてくれた、同宿の女性に思いを寄せる寅。やがて伊勢・志摩に至って母と子は対面をはたす。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Big Sleep", "三つ数えろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "私立探偵フィリップ・マーロウは百万長者スターンウッドの依頼を受ける。依頼内容はガイガーという男からの恐喝に関するものだったが、時を同じくして使用人のリーガンが姿を消していた。早速マーロウは張り込みを始めるが、そのガイガーは自宅で殺されてしまう……。「脱出」の大好評を受けて、同じボガートとバコールの主演コンビで作られた探偵映画。", "1955-04-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版BLEACH ブリーチ 地獄篇 ", "劇場版BLEACH ブリーチ 地獄篇 ", "", "", "http://image.eiga.k-img.com/images/movie/55691/315x210.jpg?4077", 0, "死神の力を持つ高校生・黒崎一護の戦いを描いた大人気コミック「BLEACH」の、週刊少年ジャンプ連載10周年を記念して製作された劇場版アニメ第4弾。生前に大罪を犯した者が送り込まれる場所・地獄で、悪人たちが反乱を起こす。一護は現世を守るため、ルキアや雨竜、そしてナゾの男・コクトーらとともに地獄へと乗り込む。原作者・久保帯人による製作指揮のもと、TVアニメ版、劇場版全作を手掛けてきた阿部記之監督がメガホンをとる。", "2010-12-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DES　FREIHERRN　VOM　MUNCHHAUSEN　WUNDERBARE　REUSEN　UND　ABENTEUER", "ほら男爵の冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ラスペの小説をもとに、ほら男バロン・ミュンクハウゼン男爵の回想という形で、彼自身の冒険を描いた作品。まだ青年だった頃、彼はロシアを訪れて女帝エカテリーナと恋に落ちる。王子に嫉妬された彼は魔術師カリオストロから永遠に若さを保つ法を習い、大砲の弾でトルコへと逃げ出す。ハーレムから美女イザベル姫を連れ出した男爵はベニスへと向かうが、そこで姫の兄と決闘、気球で月へと逃げ延びるのだが……。", "1942-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太平洋の奇跡　フォックスと呼ばれた男", "太平洋の奇跡　フォックスと呼ばれた男", "", "", "http://image.eiga.k-img.com/images/movie/55541/315x210.jpg?4239", 128, "太平洋戦争末期のサイパン島で、たった47人の兵を率いて4万5000人ものアメリカ軍に立ち向かい、アメリカ兵から“フォックス”と呼ばれて恐れられた陸軍大尉の大場栄。その史実を題材にしたドン・ジョーンズの実録小説「タッポーチョ『敵ながら天晴』大場隊の勇戦512日」を原作に、絶望的な状況のなか最後まで生き抜いた日本兵たちの真実の物語を、竹野内豊主演で映画化した戦争ドラマ。「OUT」の平山秀幸監督がメガホンをとる。", "2011-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("傷跡", "傷跡", "", "", "http://image.eiga.k-img.com/images/movie/57701/315x210.jpg?3208", 45, "「非・バランス」「ごめん」の冨樫森監督が自身の故郷でもある庄内を舞台に撮影した中編作品。13年前、人生を変えるため故郷を飛び出し東京に出てきた喜美だったが、中学生時代に受けたいじめも忘れられず、仕事も恋愛もうまくいかない。東京でのOL生活を辞めた喜美は、自分と向き合うため再び故郷へ戻ってくる。映画人育成のワークショップ「映画24区」の第1回作品。新人作家・五十嵐愛による脚本を映画化。俳優もワークショップに参加した新人たちが出演している。", "2012-03-17", "76", 0, NOW(), NOW());
