INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新難兄難弟／HE AIN’T HEAVY，HE’S MY FATHER", "月夜の願い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "父と息子が和解していく姿を通して、友情、愛情、人情という“３つの情”の大切さをノスタルジーたっぷりに描いた物語。人情派の父ファンと現実派の息子ユンは、ライフスタイルや考え方の違いからいつも衝突してばかり。ある秋祭りの夜、ファンが強盗に襲われ重傷を負ったのを機に、ユンは父のことがもっと知りたくなり父の秘密の箱を開ける。そこで両親の若い頃の写真が入ったペンダントを見つけた彼は、その頃の二人に会うために不思議な穴に飛び込み、過去への旅を始めるのだった……。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Who Am I This Time?", "クリストファー・ウォーケンのアクターズ・ラブ／舞台は恋のキューピット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "　田舎町の素人劇団に所属するハリーは舞台を降りると、途端に内気になってしまう青年。「欲望のという名の電車」に主演することになった彼は共演者のヘレンに恋してしまうが……。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刺青　堕ちた女郎蜘蛛", "刺青　堕ちた女郎蜘蛛", "", "", "http://image.eiga.k-img.com/images/movie/33811/315x210.jpg?4788", 96, "谷崎潤一郎の名作「刺青」を、「MOON CHILD」「サンクチュアリ」の瀬々敬久監督が映画化。刺青によって翻弄される男女の運命を描く官能ドラマ。不倫の恋に破れ、出会い系サイトでサクラをしているアサミ。メールを通して出会った二ノ宮に連れられて刺青師・彫光のもとを訪れた彼女は、女郎蜘蛛の刺青を入れることを決意する。ヒロインのアサミを体当たりで演じるのは、テレビ、映画、舞台などで幅広く活躍する川島令美。", "2007-01-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ТАБОР УХОДИТ В НЕБО", "ジプシーは空にきえる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "文豪ゴーリキーの処女作『マカール・チュドラー』をもとにした、ジプシーたちの物語。馬を愛するジプシーの若者ゾバールは、馬泥棒で追われ、傷を負った際に、ラッダというジプシーの女性に助けられる。高慢なラッダは周囲から魔女と噂されていたが、ゾバールは強く彼女に惹かれていく。突き放されるほどに燃え上がる彼の恋心は、やがて憎悪に変わり……。", "1976-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PU プ", "PU プ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "PFF入選経験もある映画作家・山崎幹夫の商業映画デビュー作となる、寓話的な冒険ファンタジー。日本列島最北端の島にある、幻の先住民族が住む村“プの里”。過疎から観光客誘致に躍起となっている里にある日、キショウレとパクチョンと名乗る父子がやって来たことから、里は次第に混乱に陥る……。どこかコミカルで幻想的な味わいが魅惑的。元・状況劇場の大久保鷹、怪優・田口トモロヲ、インドネシアのポップ・シンガーのマイズーラなど、異色キャストが結集。公開順は逆になったが、モデル出身の緒川たまきの映画デビュー作。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IF THE SHOE FITS", "夢で逢えたら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ファッション・ショーの着付け係をしながら靴のデザイナーを目指す地味な女の子ケリー。ある日、彼女は不思議な靴を手に入れる。その靴を履くと、彼女は美しく変身するのだ。やがて新しいイメージガールを物色中のデザイナーに見初められた彼女は、靴を履いたり脱いだりの二役生活が始まる。パリを舞台にした現代版シンデレラ・ストーリー。", "1991-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HALLS OF MONTEZUMA", "地獄の戦場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "L・マイルストン監督が撮った戦争ドラマ。第二次大戦下の南太平洋。アメリカ海軍のある中隊が重要拠点となる島に上陸を敢行。だが、敵からロケット砲による攻撃を受ける。中隊長のアンダーソンは、本部からロケット砲を破壊せよとの命令を受け、敵地に進軍する……。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOS SANTOS INOCENTES", "無垢なる聖者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1960年代。スペイン南西部の片田舎の小作農一家。ボケ老人アザリスの唯一の楽しみはヒナから育てたトンビ。一方、鳥の習性に詳しいパコは、いつも公爵の狩猟犬代わりに使われる。ある日、パコが狩猟中に大ケガをして……。封建的荘園制度を背景に、底辺に生きる人々の苦しみと、悲痛な抵抗を、静かな語り口で叙情豊かに描いた佳編。", "1984-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花笠ふたり若衆", "花笠ふたり若衆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "当時共演作が多かった北大路欣也と松方弘樹の顔合わせによるコミカルな時代劇。上州のある宿で知り合った旅人風の源太郎と三次郎。意気投合した二人は、沓掛宿の仁兵衛一家にワラジを脱ぐ。ここでは、問屋場の主人仙右衛門が、仁兵衛の縄張りを狙っていた。二人は、仁兵衛に肩入れして……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("好小子／KUNG FU KIDS", "カンフーキッド 好小子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "東南アジア各国で大ヒットを飛ばした、台湾産のカンフー・アクション。３人のわんぱく兄弟を主人公に、珍騒動の数々や、悪人相手に胸のすくカンフーでの大活躍を見せるもの。かわいい３拳士が、大人顔負けのカンフーを披露している。", "1986-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A BUSINESS AFFAIR", "恋の選択", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "小説家の夫を持つケイトは、高級デパートでオートクチュールの売り場のモデルをしながら、自分でも小説を書いていた。夫のアレックは、芸術の苦悩に取り憑かれていて、ケイトの小説にはまったく関心を示さない。そんな時、デパートの売り場で出版社の社長のバンニに言いよられたケイトは、ここぞとばかりに自分の原稿を売り込む。何としてもケイトを手に入れたいバンニは、ケイトと出版契約を結ぶが、アレックはそれがおもしろくない。ケイトはそんなアレックと別れ、情熱的なバンニのプロポーズを受け入れるが……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昨日消えた男", "昨日消えた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "日活を離れ、フリーとなったマキノ雅広が、古川緑波の病気で中止となった「家光と彦左」の代わりに、急きょ撮った作品。原案は、当時人気のあったMGMの名物探偵シリーズのウィリアム・パウエルとマーナ・ロイ主演の「影なき男」で、脚本の小国英雄はこれを江戸時代の長屋に設定。パウエル演じる探偵は遠山の金さんに置き換え、長谷川一夫が演じた。マキノはこのストーリーを、1956年にも中村扇雀＝尾上さくらのコンビで「遠山の金さん捕物控・影に居た男」として、同じ脚本でリメイクしている。裏長屋の大家・勘兵衛が何者かに殺された。勘兵衛は鬼勘といわれるほど無情な男で間借人の嫌われ者。まず日頃から勘兵衛を殺してカンカン踊りを踊らせてやるといっていた文吉、勘兵衛に借金の返済を迫られていた浪人の篠崎源衛門が疑われた。そこで与力の原六之進は一同を呼んで取り調べをすることにしたが……。日本では珍しい本格的推理ものに加え、コメディ・リリーフの渡辺篤、サトウ・ロクローが笑わせる。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Slap Shot", "スラップ・ショット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "「明日に向かって撃て！」「スティング」のジョージ・ロイ・ヒル監督、ポール・ニューマン主演の痛快スポーツ・コメディ。全米プロ・アイスホッケーのマイナーリーグに所属するチャールズ・ダウン・チーフスは、連戦連敗でリーグ最下位。選手兼コーチのレジは、なんとかチームを再生させようと新人3兄弟を加入させる。ラフ・プレーを連発する彼らのおかげでチームは連勝、ファンの支持も得て人気チームとなるのだが・・・・・・。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("多桑／A BORROWED LIFE", "多桑／父さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "「悲情城市」「戯夢人生」などH・シャオシエン作品の脚本家として知られるウー・ニエンジェンの監督第１作。日本に憧れながら死んでいった父親の生涯を通して、台湾の戦後史を描く自伝的作品。日本統治化に日本人としての教育を受け、戦後50年たっても日本に憧れ続けるセガ。妻や子供たちに日本語で“父さん”と呼ばせるセガは、長男のブンケンにとっては絶対的な存在だった。セガは皇居と富士山を見ることを夢見ながら、果たすことなく死んでいく。そんな父親の思いを実現させるため、ブンケンは父の遺骨を胸に日本を訪れるのだった。“多桑”とは、中国語で父さんの当て字。H・シャオシエンが製作を務め、彼の作品の常連たちが多く出演している。", "1994-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛機南ヘ飛ぶ", "愛機南ヘ飛ぶ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "夫を亡くした妻が、航空機工場で働きながら息子を陸軍航空学校で学ばせる。やがて息子は前線に配属されて手柄を立て、三日間の休暇を得て帰郷し、母子で父の墓前を弔う。太平洋戦争下に製作された戦意昂揚映画。ただし、戦争ものというよりも母子人情ものに仕上がっているのは、いかにも大船調メロドラマを得意とした松竹らしい。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VATEL", "宮廷料理人ヴァテール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "国王ルイ14世のための大宴会を任された宮廷料理人ヴァテールの謎めいた最期を、史実に基づいて描く歴史ロマン。1671年４月10日。コンデ大公は一度失ったルイ14世の信頼を取り戻すため、三日三晩の盛大なる宴を催す。その一切を任された宮廷料理人ヴァテールは、趣向を凝らした演出と究極の豪華メニューでこの大役に全精力を注ぐのだが……。宮廷内に渦巻く愛と陰謀にほんろうされた男の姿を、絢爛たるセットと映像で映し出す。", "2000-01-01", "2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カフェ代官山II 夢の続き", "カフェ代官山II 夢の続き", "", "", "http://image.eiga.k-img.com/images/movie/54011/315x210.jpg?3629", 0, "1軒のカフェで働く青年たちの人間模様を人気若手俳優競演で描いた「カフェ代官山」の前日譚となるシリーズ第2弾。ダンスが大好きなヒビキは、ひょんなことから“カフェ・レーブ・コンティニュエ”のマスターと出会い、店を手伝うことに。やがて店を任されるようになったヒビキのもとに、コトッチ、サーディンら個性的な面々が集まってくる。ある日彼らは、若き日のマスターとその親友・達也が共に思い描いていた夢を知り……。", "2008-10-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONDO TRASHO", "モンド・トラッショ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "“トラッショ（＝どぶ）”映画の巨匠ウォーターズが、スーパー・カルト・スター、ディヴァインを誕生させた記念すべき作品。自分がひき殺してしまった女を引きずり回しながら、奇怪な事件に遭遇していく主人公の姿を、カメラが執拗に追っていく。ウォーターズ映画の常連スター総出演も見もの。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無名家族／FAMILY HONOR", "無名家族 復讐の狼たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "香港映画の美人スターの、ジョイ・ウォン主演のハード・アクション。警官サイモンは、恋人ホーに陰で支えられながら、いつも危険な仕事に向かっていった。サイモンは、麻薬犯罪にかかわる大がかりな手入れで、コブラという男を逮捕する。だが、コブラは取り調べ中に逃走、仲間やサイモンの兄に重傷を負わせる。サイモンはホーとともにコブラを追跡する。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SEVENTH SIGN", "第七の予言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "新約聖書の黙示録に記された“第七の予言”。これがすべて実行された時、世界は滅びる。その予言が次々と現実のものとなっている頃、アビーの家にデビッドという不思議な男が下宿する。そう、アビーの出産こそ七番目の予言なのだった。D・ムーアの妊婦ヌードはセンセーショナルな話題となった。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　空の境界　第一章　俯瞰風景", "劇場版　空の境界　第一章　俯瞰風景", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "奈須きのこの同名小説を原作に、万物の死を見ることのできる能力「直視の魔眼」を持つ少女・両儀式の苦悩と戦いを描いた人気アニメ劇場版「空の境界」7部作の第1章。ある夏の終わり、突発的に少女たちの飛び降り自殺が相次ぐ。少女たちには何のつながりもなく、不可解な連続自殺事件として報道されるが、万物の生のほころびと死線を見ることができる「直視の魔眼」を持つ両儀式は事件の解明に乗り出す。彼女には事件にかかわらざるを得ない理由があった……。", "2007-12-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOWHERE TO RUN", "ボディ・ターゲット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "護送中に脱走した強盗の容疑者サム。山中に逃げ込んだ彼は、牧場を経営する未亡人と、その幼い子供たちに出会い、心を通わせる。やがてサムは、牧場を狙う悪徳土地開発業者に怒りを覚え、果敢に戦いを挑んでいく。人気アクション・スター、J・C・ヴァン・ダムが、「シェーン」を彷彿させる役どころに挑戦。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホームレス中学生", "ホームレス中学生", "", "", "http://image.eiga.k-img.com/images/movie/53301/315x210.jpg?4664", 116, "お笑いコンビ「麒麟」の田村裕が極貧生活を送った中学生時代を綴り、ミリオンセラーとなった同名自伝小説の映画化。監督は「ロボコン」「奈緒子」の古厩智之。主演に「ラブ★コン」「KIDS」の小池徹平。夏休み初日、中学生の“ボク”が帰宅すると、家は借金のため差し押さえられていた。父親は蒸発、兄と姉とも別れて一人で公園で暮らすことになったボク。自販機の小銭を集めダンボールを貪る、中学生のホームレス生活が始まる。", "2008-10-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江湖／VELVET　RAIN", "ベルベット・レイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "香港の新旧4大スターが集結したフィルム・ノワールの秀作。固い絆で結ばれながら互いを疑いあう二人の大物と、熱い友情を培っていく二人の若者。裏社会で生きる彼らの宿命のドラマを交互に綴り、そのてん末を洗練された映像美で語り明かす。予想を覆すラストが、味わい深い余韻を残す。香港裏社会の大ボス暗殺の噂が流れる中、チンピラ仲間のイックとターボは、暗殺者を決めるクジを引くため、会場へと向かうが……。", "2005-10-08", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE CRAZY JERKS", "ベイビー・アカデミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ドジだが心優しい３人の男が一人の美女に一目惚れ。念願かなって彼女の部屋に招かれたものの、その晩に彼女が３人のうちのだれかの子を妊娠したからさあ大変。出産費用を稼ぐため、３人は涙ぐましく努力するが……。「ポリスアカデミー」シリーズの“人間効果音”M・ウィンスローがエンターテイナーぶりを披露するラブ・コメディ。", "1987-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("To Live and Die in L.A.", "L.A.大捜査線／狼たちの街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "　シークレット・サービスの捜査官ジミーは定年退職を2日後に控えながら、追い詰めた偽札犯のエリックによって殺害される。ジミーとコンビを組んでいたリチャードは復讐を誓い、新しい相棒ジョンとともに捜査に乗り出す。しかしリチャードのやり方はあまりにも強引だった……。ウィリアム・フリードキン監督が放つバイオレンス・アクションの快作。フリーウェイを逆走するカーアクション場面は圧巻。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Odd Life of Timothy Green", "オッド・ライフ・オブ・ティモシー・グリーン（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十五才・学校IV", "十五才・学校IV", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "学ぶ所を教室ではなく外の世界に目を向けて描くシリーズ第４作。登校拒否の15歳の少年、大介が、かねてからの夢だった九州・屋久島の縄文杉を見るため、ヒッチハイクで旅に出る。人情味豊かなトラック運転手、我が子のように心配してくれた女性ドライバー、孤独を隠して虚勢を張る老人ら、行く先々で様々な人生模様を見つめた彼は、少しずつ成長していく。思春期の悩みを抱える主人公を自然体で演じる金井勇太をはじめ、共演陣が人間味あふれる好演を披露。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("敗れざるもの", "敗れざるもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "石原慎太郎の『小さき闘い』を山中垣が脚色し、石原裕次郎が主演した感動のドラマ。高村家の運転手・橋本と、脳腫瘍に冒された高村家の長男で中学２年の俊夫の心のふれあいを描く。死に大きな疑問を持ちながら、病魔と闘い大好きな天体観測を続ける少年をいじらしく演じているのは、子役時代の小倉一郎。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ECCE BOMBO", "青春のくずや ～おはらい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "イタリア本国の若者たちから圧倒的支持を受けているN・モレッティの長編デビュー作。学生運動の季節も過ぎて、ミケーレをはじめとする仲間たちは、映画製作を除いてすべての情熱が消え失せてしまった。彼らは直面する現実問題から逃れるかのように青春を駆け抜けようとしているが……。", "1978-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("苦い蜜　消えたレコード", "苦い蜜　消えたレコード", "", "", "http://image.eiga.k-img.com/images/movie/55201/315x210.jpg?3626", 103, "いつものように常連客でにぎわう、仙台にあるバー「リボルバー」。そこに1人の探偵が現れ、1年前にバーで起きた、ビートルズの幻の名盤「ブッチャー・カバー」盗難事件を解決すると言う。すると、バーに集まった14人の抱える悩みが次第に明らかとなり、事件は複雑に絡み合っていく。劇団「御成門」の演出家・亀田幸則がメガホンをとるミステリー仕立ての群像劇。", "2010-04-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("敵機空襲", "敵機空襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "太平洋戦争末期、アメリカ軍の空襲の様子を庶民たちの生活の中から描くことで、敵愾心をあおり、一致団結しての国民の士気を高めようとする意図で製作された戦意昂揚映画。野村浩将、渋谷実、吉村公三郎といった当時の松竹映画を代表する３人の巨匠が連名で演出に当たっているのが興味深いが、３者それぞれの個性を活かしえたものにはなっていない。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT EYES", "処刑マニア2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ロス警視庁のディーツ刑事は、地元で起こった一連の残忍かつ非道な殺人事件を追っていた。不可解な点が多いこの事件は、明らかに手口からしてプロの仕業であった。捜査協力のため派遣されてきたFBIのバルソン捜査官にも不審な点が多かった。ディーツの懸命な捜査にもかかわらず、次々と殺人は繰り返され……。刑事推理ものではあるが、やはり残虐な殺人シーンが見もののシリーズ第２作。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三羽烏三代記", "三羽烏三代記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "初代三羽烏（佐分利・佐野・上原）、二代目（高橋・佐田・大木）、三代目（山本・三上・小坂）を一堂に会したオールスター映画。それぞれの３人組が直面する、恋と友情の問題……。貫禄の初代、働き盛りの二代目、ヤングパワーの三代目と、それぞれの特徴が出ている。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’E´QUIPAGE", "最後の戦闘機", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "第一次世界大戦の頃、若きパイロットのジャンと偵察士モーリーは友好を深めていたが、ジャンがモーリーの妻を愛してしまい……。S・ヴァネルらの名演が心理ドラマに重厚さをもたらし、A・リトヴァク監督の出世作となった。", "1935-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("奇蹟／MR.CANTON AND LADY ROSE", "奇蹟〈ミラクル〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "不況にあえぐ1930年代の香港で、偶然からギャング組織のボスになった男。敵対する組織との闘いの合い間に人助けもする、心優しきボスにジャッキーが扮したアクション映画。ロープ工場での乱闘シーンなどのほか、凝ったセットで再現した1930年代の雰囲気も楽しめる娯楽作。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAVE A NICE NIGHT", "レプスキー大胆不敵 ジェネシスNo.18", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "シリーズ第３作。マイアミの刑事暮らしから足を洗い、今は南仏ニースで保険会社の調査員をしているレプスキー。そんな彼の今度の仕事は、大女優が持つ宝石の警備だ。彼女の行くところ、宝石を狙う者が必ず現れるに違いない。かくして超高級四ッ星ホテルを舞台に、金持ちや有名人、そして怪盗一味らによる様々な人間模様が展開されるのだった。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FESTIVAL　EXPRESS", "フェスティバル・エクスプレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "'70年に開催された移動型ロックフェスティバルを追ったドキュメンタリー。ジャニス・ジョプリンやザ・バンドなど当時の人気アーティストが同じ列車に乗り込み、カナダを横断したというこのツアー。ステージの上でも列車の中でも演奏が続いた伝説の5日間が甦る。'70年。カナダを列車で横断するロックフェスティバルが開催。ところが入場料の支払いを拒否する群集がデモを始め……。", "2005-02-12", "3,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("色，戒", "ラスト、コーション", "", "", "http://image.eiga.k-img.com/images/movie/53341/315x210.jpg?2247", 158, "「ブロークバック・マウンテン」で05年度アカデミー監督賞を受賞したアン・リー監督が、再び禁断の愛を描いたラブサスペンス。1942年日本軍占領下の中国・上海を舞台に、抗日運動の女性スパイ、ワン（タン・ウェイ）と、彼女が命を狙う日本軍傀儡政府の顔役イー（トニー・レオン）による死と隣り合わせの危険な逢瀬とその愛の顛末が描かれる。第64回ベネチア国際映画祭では金獅子賞とオゼッラ賞（撮影賞）のダブル受賞を果たした。", "2008-02-02", "1,53,8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("7915 Km", "7915キロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「いのちの食べかた」のドイツ人監督ニコラウス・ゲイハルターが、世界で最も過酷なラリーといわれるパリ・ダカール・ラリーの舞台裏を描いたドキュメンタリー。パリからセネガルの首都ダカールまでの7915キロを駆けぬけるパリ・ダカ。サハラ砂漠の美しい風景や現地の人々の素朴な暮らしを捉えながら、西欧人から見た“未開の地・アフリカ”、そしてアフリカ人から見た“先進国・ヨーロッパ”という2つの視点からパリ・ダカの裏側に迫る。", "2010-06-12", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ソラノ", "ソラノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "浅野忠信監督作品「トーリ」をめぐる異色ドキュメンタリー。いわゆるメイキングとは一線を画し、精神科医、名越康文が同作に参加した浅野も含むアーティスト5人それぞれへのインタビューを通して、“アートの根源”を考察していく内容。「トーリ」に呼応しながらも飛翔と逸脱を繰り返す構成が見事。「トーリ」を監督した浅野忠信に、精神科医、名越康文がインタビュー。またShing02らとも、クリエイティビティに関する問答を繰り広げる。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAST COMPANY", "デビッド・クローネンバーグのファイヤーボール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「スキャナーズ」「デッドゾーン」「ザ・フライ」などで、ホラー・ファンから圧倒的な支持を受けるD・クローネンバーグによる、ドラッグ・レースを題材にした唯一のカー・アクション。アメリカのドラッグ・レース界にチャンピオンとして君臨するロニー・ジョンソンのレースに懸ける死闘を描く。", "1978-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あの、夏の日・とんでろじいちゃん", "あの、夏の日・とんでろじいちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "「ふたり」「あした」に続いて、大林宣彦監督の“新・尾道３部作”を締めくくる１本。ボケたとの噂のある祖父の相手をするため、尾道を訪れた横浜育ちの小学生。彼の視線を通して、少年時代に回帰した祖父の意外な“素顔”が浮き彫りになっていく。情感に満ちた夏休みの情景。コミカルなテイストもふんだんに盛り込み、楽しさあふれる“童心”ファンタジーに仕上がっている。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雪之丞変化", "雪之丞変化", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "おなじみの娯楽時代劇をマキノ雅広が東映スコープで再映画化した。手代の謀略にかかって処刑された長崎の豪商の一子・雪太郎が、歌舞伎女形・雪之丞となって仇討ちの機会を狙うというお話。大川橋蔵は雪之丞、盗賊・闇太郎及び雪之丞の父親の三役を一人で演じ分けている。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼打鬼", "妖術秘伝 鬼打鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "サモ・ハン・キンポー監督、主演によるホラー映画。有力者に妻を寝取られたダメ男が、なぜか幽霊に付きまとわれ、ついには妖術を会得し有力者に復讐するというハチャメチャなストーリー。SFXも存分に盛り込まれた娯楽作品。", "1981-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUIGLEY DOWN UNDER", "ブラッディ・ガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "オーストラリアを舞台にした異色西部劇。西部劇といえば、土地の無力な人々を迫害する牧場主や悪徳保安官はつきもの。この映画では、「ダイ・ハード」「ロビン・フッド」でたっぷりと悪役を楽しませてくれたA・リックマンが、原住民狩りをする大牧場主に扮し、またもいい味を出している。スゴ腕ガンマン、マシューは、金もうけのためオーストラリアにやって来る。牧場主マーストンが、腕のたつガンマンを探していると聞きつけ訪ねるが、目的が原住民アボリジニ狩りと知るや、取引を拒否。逆にマーストンに狙われることになる。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("見えないほどの遠くの空を", "見えないほどの遠くの空を", "", "", "http://image.eiga.k-img.com/images/movie/56091/315x210.jpg?4387", 99, "映画研究会の卒業制作にのぞんでいた賢だったが、ラストシーンについて意見の対立していたヒロイン役の莉沙が撮影最終日直前に不慮の事故で他界。映画は未完成に終わる。1年後、賢は莉沙の双子の妹・洋子に出会い、彼女を使って映画の最後のショットを完成させようとするが……。劇場支配人やプロデューサー、脚本家など長らく映画業界に携わってきた榎本憲男の初監督作。森岡龍、岡本奈月、渡辺大知（黒猫チェルシー）ら若手俳優が出演。", "2011-06-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレージーだよ・天下無敵", "クレージーだよ・天下無敵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "先祖代々、様々な時代で対決を続けてきたトヨトミ電気の猿飛三郎と徳川ムセンの犬丸丸夫は、相手会社の新製品を探る産業スパイ同士。しかし人が好くマヌケな犬丸は、ずるがしこく要領がいい猿飛にいつも出し抜かれる。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Extremely Loud and Incredibly Close", "ものすごくうるさくて、ありえないほど近い", "", "", "http://image.eiga.k-img.com/images/movie/57181/315x210.jpg?4453", 129, "2005年に発表され、「9・11文学の金字塔」と評されたジョナサン・サフラン・フォアによるベストセラー小説を、「リトル・ダンサー」「めぐりあう時間たち」のスティーブン・ダルドリー監督が映画化。9・11テロで最愛の父を亡くした少年オスカーは、クローゼットで1本の鍵を見つけ、父親が残したメッセージを探すためニューヨークの街へ飛び出していく。第2次世界大戦で運命の変わった祖父母、9・11で命を落とした父、そしてオスカーへと歴史の悲劇に見舞われた3世代の物語がつむがれ、最愛の者を失った人々の再生と希望を描き出していく。脚本は「フォレスト・ガンプ　一期一会」のエリック・ロス。オスカーの父親役にトム・ハンクス、母親役にサンドラ・ブロックらアカデミー賞俳優がそろう。", "2012-02-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人の善意を骨の髄まで吸い尽くす女", "人の善意を骨の髄まで吸い尽くす女", "", "", "http://image.eiga.k-img.com/images/movie/55971/315x210.jpg?1974", 61, "売れない舞台女優の真歩は、自らが主演するネットドラマの制作を企画する。芝居仲間の牧野とともに撮影を開始するが、現場ではトラブルが続出。そこで牧野は、真歩のある隠された秘密を知ることとなる。人間の秘められた「負」の感情をブラックユーモア満載に描く。短編アニメーション「機械人間、11号。」が各地の映画祭で話題となった、加藤行宏監督の初長編作。主演は本作がきっかけで「SR サイタマノラッパー2」にも抜てきされた山田真歩。", "2011-04-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("森の学校", "森の学校", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "城下町で大人たちに叱られながらもヤンチャに遊ぶ子供たち。歯科医の息子、雅雄はそのなかでもとびきりのワンパク少年だったが、勉強についていけないことに劣等感を抱いていた。そんなとき雅雄は東京から転校してきた少女、美代子に、田舎での遊び方を教えてやるのだった。昭和10年代の丹波篠山を舞台に、現代では失われつつある子供たちと自然の触れ合いの尊さをうたい上げた感動作。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宮本武蔵", "宮本武蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "太平洋戦争のさなかに製作された作品で、内容もともすれば戦意高揚映画に見え、溝口独特のきめの細い演出にも欠ける。当時連載されていた菊池寛の『宮本武蔵』を原作に、一乗寺の決闘から巖流島での佐々木小次郎との一騎討ちまでを描き、武士道と、それに絡む人情を取り上げたあたりは溝口らしい。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ESPOIR ： SIERRA DE TERUEL", "希望 テルエルの山々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "内戦の嵐が吹き荒れるスペイン。テルエルの山中にも反乱軍が建設中の飛行場が見つかった。ふもとの村人と共和国軍は飛行場爆破のため必死の作戦を開始するが……。フランスの文学者A・マルローが実体験から書いた小説を自ら映画化。戦火の下で撮影を敢行した果てしなくドキュメンタリーに近い戦争ドラマ。", "1939-01-01", "2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハッピーエンドの物語", "ハッピーエンドの物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "女子高校生が、タイムマシンの実験によって30年後の未来へ、そこで同級生の危機を知った彼女は現代に戻り、未来の彼を救うために活躍するという、日本版「バック・トゥ・ザ・フューチャー」。金子修介の助監督を務めていた栃原広昭監督のデビュー作であり、金子の実弟・金子二郎や脚本家じんのひろあきの実弟・山崎みつぐがシナリオに参加している。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WATERDANCE", "ウォーターダンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "不慮の事故で一生車イスでの生活を余儀なくされた作家のジョエル。恋人との関係も気まずくなる彼が、ともにリハビリを受ける仲間との友情を通じて、前向きな生き方に目覚める。監督の一人、N・ヒメネズの実体験に基づく人間ドラマ。ともすれば暗くなりがちなテーマを、カラッとしたタッチで描いている。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　いのちの星のドーリィ", "それいけ！アンパンマン　いのちの星のドーリィ", "", "", "http://image.eiga.k-img.com/images/movie/33551/315x210.jpg?3347", 51, "やなせたかし原作のテレビアニメ「それいけ！アンパンマン」の劇場版第18作。ゲスト声優に安達裕美を迎える。いのちの星から、アンパンマンのように命を与えられた人形ドーリィ。自由に動けるようになって大喜びの彼女は、自分勝手な行動で周囲の皆を困らせる。そんな中、バイキンマンが作った新型ロボット、スーパーカビダンダンが町を襲い始める。闘いの末、カビだらけになって倒れたアンパンマンの姿を見たドーリィは……。", "2006-07-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イヤータグ", "イヤータグ", "", "", "http://image.eiga.k-img.com/images/movie/57541/315x210.jpg?2395", 67, "自主制作のインディーズ映画「キヲクドロボウ」（2007／石田肇共同監督）が評判となった山岸謙太郎監督が、横浜を舞台に描くサイコサスペンス。伊勢署の生活安全課に勤める刑事・見城陽介が面倒を見ていた前科者の女性が、連続猟奇殺人の4件目の被害者となり、全裸死体で発見される。殺人の手口は4件とも同様で、被害者は全て前科のある者たちばかり。遺体にはどれも牛の管理札として使われる「イヤータグ」がつけられ、15桁の数字が刻まれていた。「キヲクドロボウ」で山岸と共同監督を務めた石田肇監督の新作「KARAKURI」と同時上映。", "2012-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DECLINE OF WESTERN CIVILIZATION", "ザ・デクライン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1970年代後半、L.A.のアンダーグラウンドで巻き起こったパンク・ムーブメントのドキュメント。夜ごとライブハウスで繰り広げられるエキサイティングなステージと、狂ったように踊り、バイオレンスを繰り返す若者たちの様子を如実に捉えている。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALFREDO，ALFREDO", "アルフレード アルフレード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "P・ジェルミ監督が、内気な男がたどる結婚から離婚の悲喜劇を軽妙に描く。気が弱く、モテない男にある日、奇跡が起こる。熱愛していた女性から逆プロポーズされた彼は舞い上がらんばかりに大喜び。だが、それもつかの間、結婚した途端に女のバカさ加減が目につき始める……。いかにもイタリアンなノリのいい結婚コメディ。モテない男を演じるD・ホフマンの名演に加え、熱愛妻を演じるS・サンドレッリのお色気も見もの。", "1972-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裸の夏 THE NAKED SUMMER", "裸の夏 THE NAKED SUMMER", "", "", "http://image.eiga.k-img.com/images/movie/53261/315x210.jpg?4353", 98, "俳優としても活躍する舞踏家・麿赤兒率いる舞踏集団“大駱駝艦”の夏期合宿を追ったドキュメンタリー。毎年、世界中から集まった経歴も参加目的も様々な若者たちが、大駱駝艦の団員らと共に麿の舞踏を体験している。毎日の過酷な練習を通して麿の基本思想“天賦典式”（この世に生まれ入ったことこそ大いなる才能とする）を実践しようとする若者たちの姿を、大駱駝艦35年の歴史を振り返りながら描いていく。", "2008-01-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NAKED AND THE DEAD", "裸者と死者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "N・メイラーの同名ベストセラー小説の映画化。第二次大戦中、日本軍の占領する南太平洋の孤島に反攻作戦に出る米兵たちの姿を描いた戦場ドラマ。カミングス将軍は友人の息子ハーン少尉を隊長とした分隊に偵察を命じる。だが少尉は経験不足で、隊は死戦をさまよう……。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE HAND DON’T CLAP", "カリプソ天国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "カリプソ界の大物、ロード・キチナーの半生を絡ませ、カリプソの特質を描いた音楽ドキュメンタリー。キチナーや、彼の先輩格にあたるグローリング・タイガーから語られるカリプソの歴史、デヴィッド・ラダーの1986年のステージなどファンには堪えられない内容になっている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PARTVIII 詐欺師の運命", "難波金融伝 ミナミの帝王　劇場版PARTVIII 詐欺師の運命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "賭博にのめり込んだ開業医・丸山。商売道具の医療器具さえ売り払い、夜逃げを決め込んだ彼とその妻を銀次郎は追うが……。おなじみ“萬田銀行”の面々が活躍する人気シリーズの第９作。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROSELAND", "ローズランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "J・アイヴォリー監督が、ニューヨーク東52番街のダンスホール“ローズランド”に集う人々を通して人生の哀歓を語る３つのショート・ストーリー。実際にローズランドの司会者でもあるドン・デ・ナターレなど、ローズランド出身のアーティストも多数出演している。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花子", "花子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "夕食の残り物を素材にした“たべものアート”の作者、今村花子と彼女を取り巻く家族の日常を柔らかな眼差しで淡々と切り取ったドキュメンタリー。花子は毎日作業所に通い、週末には絵画教室やバスケット、父とのお出かけを楽しむ。今村家を出ようと決意する姉。そんな毎日の中で、花子は一人毎日変わることなく“たべものアート”を作り続ける……。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEST MAN", "リスキーブライド・狼たちの絆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ジェシーとホープの結婚祝いに高校時代の旧友が集まった。久しぶりの再会を喜ぶ友人たち。しかし、式に向かう途中に寄った銀行で強盗事件が発生。なんと、仲間の一人、ビリーは全米指名手配中の“ハムレット強盗”の犯人だったのだ。共犯者と見なされた彼ら。小さな町では警察、TV局、FBIまでが動きだし、次第に彼ら５人を追い詰める。仲間との友情と、自己犠牲を軸に展開するアクション・ドラマ。逃亡の果て、彼らを待ち受ける結末とは…？ 銀行強盗に巻き込まれる花嫁をD・バリモアが演じている。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("約束の地に咲く花 TWILIGHT FILE III", "約束の地に咲く花 TWILIGHT FILE III", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "オムニバスシリーズ「TWILIGHT FILE」第3弾の1作。幼い頃に交わした約束を胸に、壮絶な戦いへ身を投じていく男の運命を描いたミステリーアクション。結婚を間近に控えた望は、海外でマフィアになったと噂されるかつての恋人・亮のことが忘れられずにいた。亮がこん睡状態の元恋人・小夜の元にいることを知った望は、亮に会うため病院へと足を運ぶ。美しい花で埋めつくされた病室を目にした望は、20年前に亮や小夜と一緒に聞いた、ある花にまつわる話を思い出す。", "2007-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("View from the Top", "ハッピー・フライト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "スチュワーデスを夢みる女の子の奮闘と、恋の行方がコミカルに綴られるサクセス・ストーリー。G・パルトロウが様々なスチュワーデスの制服を身につけ、キュートな魅力を発揮。空回り気味のユニークな鬼教官に扮した「オースティン・パワーズ」シリーズのマイク・マイヤーズの怪演にも注目。ある日テレビで見たカリスマ・スチュワーデスの言葉に心を動かされ、国際線のスチュワーデスになる事を決意したドナ。地元のマイナー航空会社で働き出した彼女は夢を実現するため、同僚二人とともに業界大手の航空会社の面接を受ける。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POPULAR！", "ポプラル！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "キューバ音楽への造詣が深い人気作家、村上龍が製作総指揮を務めたドキュメンタリー。キューバの若者たちが熱狂するオルケスタ“チャランガ・アバネーラ”のステージを収録。キューバ音楽にジャズやファンクを融合させ、セクシーな歌とダンスで観客をハイにさせる彼らの魅力を紹介していく。ロス在住の映像作家J・パズがチャランガ・アバネーラのライブを撮影。彼らの熱いパフォーマンスとファンとの親密な関係を映し出す。", "2005-10-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ruffn' Tuff", "Ruffn' Tuff ラフン・タフ", "", "", "http://image.eiga.k-img.com/images/movie/33691/315x210.jpg?2509", 82, "20年以上に渡り日本とジャマイカにおけるレゲエシーンの橋渡しを続けてきた石井“EC”志津男がメガホンを取ったドキュメンタリー映画。50年代のメントやスカ、60年代のロックステディといった音楽にその起源を持つレゲエ。ゴッドファーザー・オブ・レゲエ・ミュージックと称されるグラッドストン・“グラディ”・アンダーソンをはじめ、当時の生き証人であるアーティストやプロデューサーたちへのインタビューからレゲエのルーツに迫る。", "2006-10-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Titus", "タイタス", "", "", "http://image.eiga.k-img.com/images/movie/1711/315x210.jpg?2147", 162, "古代ローマの武将タイタスは、宿敵ゴート族を滅ぼし女王タモラを連れローマへ凱旋、この戦で死んだ自分の息子を慰めるために彼女の長男を生け贄に処した。愛する息子を奪われて深い悲しみに暮れ、タイタス一族を滅ぼす機会を密かに狙うタモラ。やがてタモラの愛人の筋書き通り、息子を殺され娘までも陵辱されたタイタスは、悲しみと怒りと狂気を胸にたぎらせ復讐を開始する。", "2000-11-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GUNS OF FORT PETTICORT", "赤い連発銃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "女ばかりが留守を守る南北戦争当時のテキサスの町。こうした時にはインディアンと平和を保つのが何より大事と、北軍のウィット中尉は、かねてインディアンとの協調に努力していた。しかし、インディアン嫌いのシビングトン大佐は逆に彼らを懲らしめようと、かつて休戦協定を結んだ平和の地を襲おうと提案する。新聞連載小説を基にした西部劇。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Best Chef", "食客", "", "", "http://image.eiga.k-img.com/images/movie/54371/315x210.jpg?4539", 115, "韓国を代表する漫画家ホ・ヨンマンの人気シリーズを、「僕の、世界の中心は、君だ。」のチョン・ユンス監督が映画化。主演は「シルミド」のキム・ガンウ。最高の宮廷料理人に与えられる名誉の称号“待令熟手食”。頂点を極める老舗料亭「雲岩亭」の料理長が保有するその称号をめぐり、青年ソン・チャンと、料理長の孫オ・ボンジュの対決が行われるが、問題なく毒処理したはずのソン・チャンのフグ料理が思わぬ不幸をもたらし……。", "2009-04-25", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガンガー 俵万智イン・カルカッタ", "ガンガー 俵万智イン・カルカッタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "歌人・エッセイストとして知られる俵万智がインドを旅する姿を通じて、“生きることの喜び”“生命の尊厳”を伝える長編ドキュメンタリー。現地では“ガンガー”と呼ばれる聖なる川ガンジス周辺や、喧噪の大都会カルカッタでの人々との出会いと別れ。特にマザー・テレサとの感動的な会見の風景が素晴らしい。長編初監督となる鈴木健介は名匠・中川信夫監督に師事し、TVドキュメンタリーなどで活躍している。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WINNER", "THE WINNER", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ラスベガスの欲にまみれた人間模様を描く、異才A・コックス監督作品。カジノで勝ちまくる青年フィリップの噂で、ベガスの町は持ち切り。そのたぐいまれなるツキに目をつけた彼の兄やギャング、ギャンブラーたちは、フィリップに接近する。やがて純朴なフィリップは、同じく金目当てで近寄ってきた美女ルイーズと恋に落ちた。彼女のために、フィリップは全財産を賭けた勝負に出るが……。日本では全米公開バージョンとは異なるが、ディレクターズ・カット版が公開された。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love and Other Drugs", "ラブ＆ドラッグ", "", "", "http://image.eiga.k-img.com/images/movie/55881/315x210.jpg?3193", 113, "「ラストサムライ」「ブラッド・ダイヤモンド」のエドワード・ズウィック監督が、バイアグラのセールスマンだったジェイミー・レイディの回顧録「Hard Sell: The Evolution of a Viagra Salesman」をジェイク・ギレンホール＆アン・ハサウェイ主演で映画化したラブコメディ。全米各地の病院を周り薬品を販売するジェイミー（ギレンホール）は、シカゴの病院でパーキンソン病を患う勝ち気な女性マギー（ハサウェイ）と出会い、すぐに恋仲になるが……。", "2011-11-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("City Slickers II: The Legend of Curly's Gold", "シティ・スリッカーズ2 黄金伝説を追え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "疲れた都会人がカウボーイ体験ツアーで人生への意欲をとり戻す姿を描き、大ヒットした前作を受けての続編。前作で死んだ老カウボーイの遺品から、黄金の地図が出てきた。ミッチは友人のフィル、弟のグレンらとともに、黄金の谷に赴く。再び珍道中に乗り出した彼らの前に、死んだはずの老カウボーイが出現する。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("АНДРЕЙ РУБЛЕВ", "アンドレイ・ルブリョフ（上・下）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 182, "激動の中世ロシアを舞台に、史上最高のイコン(聖像画)画家アンドレイ・ルブリョフの生涯を壮大に描いた作品。全体は６部に分かれており、ルブリョフのことは直接描かれず、かわりに彼と同時代の出来事が語られていく。最終部で初めてルブリョフの制作したイコン作品が次々に画面に現れる。しかもここだけがあざやかなカラー画面が用いられ、敬虔な気持ちにさせられてしまう手法が見事。まだタルコフスキーは、後の内面に降りていくようなスタイルを見つけてはいないが、独特なスタイルを持った一大叙事詩となっている。", "1967-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("せかいのおわり", "せかいのおわり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "奇妙な共同生活をすることになった3人の男女の、ビミョーなすれ違いをすくい取った等身大のラブ・ストーリー。監督に風間志織、主演は中村麻美に渋川清彦という「火星のカノン」のトリオが再集結。長塚圭史、田辺誠一といったメンツが脇を固める中、高木ブーが独特の存在感を見せている。ナンパ野郎の慎之介が働く盆栽屋に、恋人と別れた幼なじみのはる子が転がり込んできた。実は慎之介は、はる子に想いを寄せており……。", "2005-09-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE　GOD", "ラヴ・ゴッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "病気が完治しないまま精神病院から退院させられ、担当医ノグチの勧めでホテルに住むことになったラルーは、そこに暮らすヘレンに好意を抱く。一方、ノグチは長年研究していた巨大吸虫が逃げ出したことで悩んでいた。それは寄生した肉体を巨大モンスターに変えてしまう変態生物であり、古代人は変態したモンスターを“ラヴ・ゴッド”と呼んでいたのだ。そしてその虫はラルーの部屋に入り込んでいた……。ラルーとヘレンの恋と、漫画のような造形のモンスターが起こす騒動を、オフビート感覚で捉えたモンスター・ラブ・ロマンス。", "1997-01-01", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rubber", "ラバー", "", "", "http://image.eiga.k-img.com/images/movie/57581/315x210.jpg?4241", 82, "テレパシーで次々と人間を殺害していく「殺人タイヤ」の恐怖を描いた異色のパニックホラー。砂漠に打ち捨てられた古タイヤのロバートにある日突然、命が宿る。やがてロバートは念じるだけで物を破壊できるテレパシー能力をもっていることに気がつき、最初は小動物やゴミを破壊していたが、次第に人間に興味を持ちはじめて……。", "2012-01-21", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アラブの嵐", "アラブの嵐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "祖父の遺言で海外行きを決意した宗方は、旅客機の中で、行方不明になった両親を探すためエジプトに向かうゆり子と知り合った……。エジプトにロケを敢行、石原裕次郎が、かの地で人民運動に巻きこまれる青年を好演した大作。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Inside Job", "インサイド・ジョブ　世界不況の知られざる真実", "", "", "http://image.eiga.k-img.com/images/movie/56261/315x210.jpg?4070", 78, "2008年のリーマン・ブラザーズ経営破たん“リーマン・ショック”に端を発した世界的経済不況の真実に迫るドキュメンタリー。金融業界人や政治家、大学教授、ジャーナリストらキーパーソンへのインタビューや、徹底したリサーチ、データ収集によってあらゆる観点から問題を検証する。第83回米アカデミー賞で長編ドキュメンタリー賞を受賞。ナレーションをマット・デイモンが担当している。", "2011-05-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONCRETE WAR", "コンクリートウォー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ロス警察のジェフの妻スーザンは、ジェフの元を去り今は大富豪のエリックとともに暮らしていた。が、ある夜エリックの屋敷がプロの殺人集団に襲われ、スーザンは連れ去られてしまう。彼女を救うため二人の男は反発し合いながらも犯人たちを追う。「ストリート・オブ・ファイヤー」の不滅のヒーロー、トム・コーディ役がハマりすぎてその後、低迷しているM・パレが再びヒーローに挑む。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wassup Rockers", "ワサップ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ロサンゼルスで最も危険な地域、サウス・セントラルを舞台に、パンクロックとスケボーに明け暮れながら自分たちの居場所を探し求めるラテンアメリカ系の少年たちを描いた青春ドラマ。キャストには現地の少年たちを起用し、死と隣りあわせの日々をたくましく生きる彼らの姿をリアルに映し出す。監督は、これまでにも「KIDS／キッズ」「BULLY　ブリー」などでストリートに生きる若者たちの等身大の日常を描いてきたラリー・クラーク。", "2007-01-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜汽車", "夜汽車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "まだ乳飲み子の幼い妹・里子と生き別れてしまった姉の露子は、妹の成長だけを祈って10数年の流転生活を送る。やがて美しい娘に成長した妹に出会うが、喜びも束の間、姉妹は一人の男をめぐって対立する……。テーマ曲のクロード・チアリのギターが哀感を漂わせる情念のドラマ。高知を舞台にした作品が多い宮尾原作の強い女性ものの１作だ。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キディ・グレイド　劇場版　第三部：KIDDY GRADE TRUTH DAWN 黎明篇", "キディ・グレイド　劇場版　第三部：KIDDY GRADE TRUTH DAWN 黎明篇", "", "", "http://image.eiga.k-img.com/images/movie/34161/315x210.jpg?1351", 90, "小説・コミックも人気のSFアクション・アニメ「キディ・グレイド」。全24話から構成されるテレビ版を再編集し、追加カットと新作カットを加えた劇場版3部作の完結編。高い戦闘能力を持つ肉体派美少女エクレールと、コンピューター並の情報処理能力を持つ頭脳派美少女リュミエール。能力も性格も正反対の2人が、銀河通商関税機構“GOTT”のエージェントとして惑星間や組織間のトラブルを解決していく。", "2007-09-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LIFE AND DEATH OF PETER SELLERS", "ピーター・セラーズの愛し方　～ライフ・イズ・コメディ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "今なお多くの熱狂的ファンを持つ天才喜劇俳優ピーター・セラーズ。'80年に死去するまで「ピンク・パンサー」シリーズなど数々の傑作＆怪作に主演した彼の人生を、知られざる素顔と共に描き出す。実力派俳優ジェフリー・ラッシュが、駄々っ子のようなセラーズをフルスロットルで怪演している。ラジオ界から映画界へ進出し、成功を収めたピーター・セラーズ。女癖の悪さゆえに妻子に去られた彼の前に、若い美人女優ソフィアが現れて……。", "2005-01-29", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAD TASTE", "バッド・テイスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "謎のエイリアンたちによって住民がすべて抹殺された町カイホロ。その小さな町へエイリアン調査隊が潜入、エイリアンの秘密工場へと向かう。その工場ではなんと地球人を材料にした食品が製造されていた。やがてエイリアンのアジトを舞台に、調査隊とエイリアンの激しい銃撃戦が始まる……。ジャクソン監督が新聞社に勤めるかたわら、週末に限ってコツコツと撮影し、４年半の歳月をかけて完成にこぎつけたというスプラッタ作品。", "1987-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　BOULET", "ル・ブレ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "フランスで記録的な大ヒットとなったアクション・コメディ。釈放を間近に控えた強盗犯モルテスが看守のレジオに頼んで買ってもらった宝クジが大当たり。ところが、何も知らないレジオの妻が、宝クジを持ったままアフリカに旅立ってしまった。怒ったモルテスは脱獄し、気弱なレジオを連れて後を追うが、そんな彼らを悪党どもが狙っていた……。対照的な性格のモルテスとレジオのコミカルなかけ合いのほか、転がる観覧車の脇でのカーチェイスなどの豪快な見せ場も見逃せない。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人在紐約／FULL MOON IN NEW YORK", "フルムーン・イン・ニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "祖国を離れ、ニューヨークに暮らす３人の中国系女性たちが、ある日、偶然に出会う。台湾出身で女優として身を立てているシャンピン、結婚するため中国からアメリカへとやって来たチャオホン、そして香港出身で女性実業家として活躍するフェンチャオ。３人それぞれの問題やトラブルを浮き彫りにしながら、ニューヨークで暮らす彼女たちの姿に、中国人としてのアイデンティティーを探り出していく。「誰かがあなたを愛してる」などにも散見するテーマではあるが、監督のS・クァンは中国、香港、台湾という３つの“中国”の女たちを登場させることにより、新しい中国人の姿を模索しているとも言えよう。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A CORPS PERDU", "愛の瞬間〈とき〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "男二人と女一人。３人で理想的な共同生活を送ってきたはずの彼らに、ある日亀裂が入る。ピエールを残してダビットとサラが出て行ったのだ。苦悩するピエールは自分のアイデンティティーを取り戻すため、モントリオールの街をあてどなくさまよい続ける……。", "1988-01-01", "10,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Extract", "シンディにおまかせ", "", "", "http://ecx.images-amazon.com/images/I/51TXJ96RZPL._SL210_.jpg", 90, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETURN　OF　THE　ONE−ARMED　SWORDSMAN", "続・片腕必殺剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "香港で大ヒットした前作を受けて作られたシリーズ第２弾。本作では、多彩な武術と武器を使う敵キャラクターが多数登場して、前作より娯楽性の高い仕上がり。主演のキャラはこの後、日本で“座頭市”に登場して市と対決するなど話題を呼んだ。武侠家の身を離れ主人公は妻と農業を営みながらひっそり暮らしていたが、悪の武侠グループと戦える彼を探し求める一派が駆け込んできた。", "1969-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Along Came Polly", "ポリー my love", "", "", "http://ecx.images-amazon.com/images/I/512rOeukbPL._SL210_.jpg", 90, "超慎重派の男性が自由奔放な女性と恋に落ちたことで起こる騒動を、ベン・スティラー＆ジェニファー・アニストン共演で描いたロマンティックコメディ。保険会社のリスク査定員として働く安全主義者の男ルーベンは、新婚旅行先で妻の浮気現場を目撃してしまう。深く傷ついたルーベンは、親友のサンディに強引に連れて行かれたパーティで、中学時代の同級生ポリーと再会。自分とは正反対な性格のポリーに魅力を感じ、つきあいはじめるが……。", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍城正月／DRAGON　TOWN　STORY", "龍城恋歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「HERO」のチャン・イーモウ監督が製作総指揮として携わった異色のラブ・ストーリー。結婚式当日に愛する者を皆殺しにされ復讐に燃える花嫁と、その彼女に雇われた闇に生きる刺客との恋を情感たっぷりに描き出す。抜群の色彩感覚で定評のあるチャン・イーモウ監督らしく、本作でも赤を強調した映像によって鮮烈な印象を観る者に残す。", "1996-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風流使者 天下無双の剣", "風流使者 天下無双の剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "仙台六十二万石陸奥守の叔父、藤木道満は、陸奥守病弱を利用して伊達家の実権を握り、血気の青年剣士たちを集めて天下を狙っていた。飯山領主豊後守の弟・本多左近は、かねてより道満の内情を探っていたが、その動きを知った道満は、小野派一刀流の達人・島田虎之助を使って左近を討たせようと図る。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GENDARME DE SAINT—TROPEZ", "ルイ・ド・フュネス　大混戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "フランスの喜劇映画人、フュネスの大ヒットとなった警官シリーズの第１作。クリショフ警官が、業務成績を上げ、サントロペに栄転する。そこで発生する国宝名画の盗難事件を、彼はドタバタ騒動の末、見事解決。南フランスの陽光よろしく、明るいコメディ。", "1964-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴメンナサイ", "ゴメンナサイ", "", "", "http://image.eiga.k-img.com/images/movie/56911/315x210.jpg?1535", 92, "「ハロー！プロジェクト」所属のアイドルグループ「℃-ute」の鈴木愛理と、「Berryz工房」の嗣永桃子、夏焼雅の3人が結成しているガールズロックユニット「Buono！」の初主演作。ケータイ小説サイト「魔法のiらんど」で人気を呼んだホラー小説を映画化。女子高生・由香のクラスには、「幽霊」と呼ばれるほど不気味な容姿の比那子がいた。テストで比那子に勝てなかった詩織が逆恨みをし、文化祭で披露する演劇の脚本執筆を比那子に強要するが、それから周囲で不可解な死が連続するようになり……。", "2011-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　CIEL　EST　A｀　NOUS", "シューティング・スター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "プレイボーイの遊び人レニーと、ギャングの情婦ジュリエット。麻薬の取引現場で知り合った彼らは、大金をせしめるためにドラッグを強奪。逃避行のなかで恋が芽生えるが、そんな彼らにギャングの追手が迫る。はみ出し者たちの恋騒動を、ユーモラスに描いた恋愛ドラマ。当時、実生活でもカップルであったM・プポーとR・ボーランジェが、スリリングな恋を妙演。", "1997-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AFTER MIDNIGHT", "アフター・ミッドナイト 恐怖の課外授業", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「エンドア／魔空の妖精」で監督・脚本を手掛けたジム＆ケン・ウィートの共同監督作。女子大生アリスンは友人とともに“恐怖の心理学”という聞き慣れない授業を選択する。担当のデレク教授は、銃や斧、実際に起こりそうな恐しい物を用いて授業を進めていく。そしてある日、課外授業として自宅に集められた生徒たちは、それが身の毛もよだつ実験とは知らずに……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少林寺／THE SHAOLIN TEMPLE", "少林寺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "数千年に及ぶ歴史を持ち、あらゆる武術のルーツといえる“少林寺”。この技を広く知らしめるため、鷹拳、酔拳など中国武術の覇者たちが一堂に会して、それぞれの秘拳を披露するスペクタクル大作。武術の天才、リンチェイの活躍が素晴らしい。", "1982-01-01", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nutcracker", "World Classics＠CINEMA くるみ割り人形", "", "", "http://image.eiga.k-img.com/images/movie/55111/315x210.jpg?2793", 103, "世界各地のバレエやオペラをスクリーン上映する「World Classics＠CINEMA」の1作で、イギリスの名門ロイヤル・バレエで2008年に上演された「くるみ割り人形」をデジタルシネマ化。くるみ割り人形に姿を変えられた王子を助けた少女クララが、お礼としてお菓子の国に招かれるまでを描く。誰もが耳にしたことのあるチャイコフスキーの名曲の数々や、世界的にその名を知られる舞台演出家ピーター・ライトによる豪華な演出が見どころ。", "2010-01-16", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BATTAGLIA DI ALGERI", "アルジェの戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "1950年代から10年に及ぶアルジェリア独立運動の激しい戦闘をドキュメンタリー・タッチで再現した大作。ニュース映画などの記録をまったく使わず、数々の証言や記録をもとに劇映画としてすべて“再現”。アルジェ市民が積極的に協力、８万人の市民が撮影に参加した。100名ほどの端役もほとんどが素人。戦車、大砲、武器類はアルジェリア軍の協力を得ている。「望郷」の舞台、カスバで撮影。製作者で劇中の闘士を演じるY・サーディはカスバの地下活動家だった。この映画は1966年度ヴェネチア映画祭金獅子賞を獲得したが、フランス代表団は反仏映画としてF・トリュフォーを除く全員が退席したエピソードを残している。", "1966-01-01", "4,71", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BACHELOR PARTY", "バチェラー・パーティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "結婚式を間近に控えた花婿とその悪友が、独身最後の夜をハチャメチャに過ごす。負けじと花嫁側も。こんな風習があったとは（!?）オドロキのハチャメチャ・コメディ。監督・脚本のイズラエルは、「ポリスアカデミー」の脚本も手掛けている。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冬の怪談　ぼくとワタシとおばあちゃんの物語 TWILIGHT FILE VI", "冬の怪談　ぼくとワタシとおばあちゃんの物語 TWILIGHT FILE VI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "オムニバスシリーズ「TWILIGHT FILE」第6弾の1作。「ハロー！プロジェクト」所属のアイドルグループ「℃-ute」のリーダーとして活躍する矢島舞美が本格アクションに挑んだロマンティックホラー。家族と一緒に平和な毎日を送っていた17歳の高校生・亮。そんなある日、おばあちゃんが危篤になったとの報せが入る。両親はおばあちゃんの元へと向かうが、風邪をひいた亮は妹と2人きりで留守番することに。すると突然、2人の前に霊能力者の少女・小夜が現われる。それ以来、亮の周囲で次々と不思議な出来事が起きはじめる。", "2009-07-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hocus Pocus", "ホーカス・ポーカス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "魔女伝説の残る町セーレムに引っ越してきたマックスは、町の空気になじむことができず、冴えない毎日を送っていた。そんなマックスはハロウィンの夜、友人と妹ダニーとともに“魔女屋敷”と呼ばれる家に探検に出かける。しかし、思わぬアクシデントから屋敷に眠る三人の魔女が300年の眠りから覚めてしまった！　子供たちの命を狙い夜空を飛び回る魔女と、マックスらの戦いを描くファンタジー・コメディ。B・ミドラーら、芸達者な魔女たちの怪演に注目だ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Babe", "夢を生きた男 ～ザ・ベーブ～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "伝説の大リーガー、ベーブ・ルースの半生を描いた伝記映画。寄宿学校で野球の天分を開花させた少年ルースは、やがて大リーガー入りするとホームラン王としてヤンキース黄金期を築き、ワールドシリーズでの予告ホームランなど数々の伝説を残していく……。ルースそっくりのJ・グッドマンがはまり役で好演。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カイジ　人生逆転ゲーム", "カイジ　人生逆転ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/54161/315x210.jpg?2962", 129, "福本伸行による人気コミックの映画化。主演は藤原竜也、共演に天海祐希、香川照之、松尾スズキほか。定職にもつかず自堕落な日々を送るフリーターのカイジは、ある日、友人の借金の保証人になったことで多額の負債を抱えてしまう。取立てに来た金融会社社長の遠藤から、「一夜にして大金を稼ぐチャンスがある」と話を持ちかけられたカイジは、遠藤の指示に従い、とある豪華大型客船に乗り込む。", "2009-10-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POPSY POP", "太陽の200万ドル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "南米のダイヤ鉱脈を舞台に、二人の男とストリッパー、ポプシー・ポップの間に繰り広げられる200万ドルのダイヤ争奪戦を描く。「パピヨン」の作者H・シャリエールが、妖艶なストリッパーを演じるC・カルディナーレに捧げたオリジナル・シナリオの映画化。", "1971-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROMISES", "プロミス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "紛争が絶えないイスラエル、パレスチナの対立問題を子供たちの目線で考察したドキュメンタリー。テロなどの悲惨な事件ばかりが伝えられるニュース報道とはまったく異なる視点で、和平への可能性を探った作品である。ユダヤ人とパレスチナ人の少年少女7人に取材を行い、彼らのあどけなくも時に大人びた素顔や、思わずハッとするような過激な政治的発言を収録。映画の後半には、スタッフの提案で一日だけ対面することになった子供たちが、不安を抱きながらもすぐに打ち解けて食事やサッカーなどに興じる様子が生き生きと描かれている。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUMMER STOCK", "サマー・ストック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "農場のジュディのもとへショー一座の妹が演出家ケリーを連れて戻り、納屋を劇場にと大騒ぎ。後半、劇中劇で彼女がタキシードに男物の帽子で踊る名場面は「ザッツ・エンタテインメント」所収。ガーランド、ケリーの芸比べの趣もあるMGMミュージカルの傑作。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フォーゴットン・ドリームス", "フォーゴットン・ドリームス", "", "", "http://image.eiga.k-img.com/images/movie/57481/315x210.jpg?2474", 73, "3人の女性監督が女性ならではの視線で「女性の恋とセックス」をテーマに撮り上げた「ポルノチック」シリーズの1作。「Presents 合い鍵」（2006）、「森崎書店の日々」（10）の日向朝子監督が、多忙な日常の中で心も体もすれ違っていく若い夫婦の姿を描く。不動産屋勤務の衿とホテルで夜勤の仕事に就いている裕介の夫婦は、それぞれの出勤時間が異なるため一緒にいる時間はわずか。それでも夫婦愛を育んできた2人だったが、裕介が失業したことから次第に関係がぎこちなくなりはじめて……。", "2011-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレヨンしんちゃん 嵐を呼ぶ栄光のヤキニクロード", "クレヨンしんちゃん 嵐を呼ぶ栄光のヤキニクロード", "", "", "http://ecx.images-amazon.com/images/I/617am33mMrL._SL210_.jpg", 80, "通算で11作目となるお馴染み、クレヨンしんちゃん・ムービー。大人にまで大反響を呼んだ感動の前作から一転、今回はしんちゃんらしい大爆笑のストーリーが展開する。謎の男にいわれのない物を引き渡すよう迫られ、思わず逃げ出したところ、なぜか指名手配されてしまった野原一家。男が“スウィートボーイズ”なる集団の人物と知ったしんのすけたちは誤解を解くため、彼らの本部がある熱海に向かう。", "2003-04-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("跋扈妖怪伝・牙吉", "跋扈妖怪伝・牙吉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「陰陽師 II」などの特殊メイクを手掛けた特殊造型の第一人者、原口智生監督が、「IZO」の原田龍二を主演に迎えた時代劇アクション。江戸時代の動乱期を舞台に、人狼＝狼男の正体をもつ無宿浪人・牙吉の活躍を、特殊造形で作られた妖怪と操演や火薬を駆使した特撮を織り交ぜて描いたリアルな映像が圧倒的。街道を行く無宿浪人・牙吉。実は人間ではなく“人狼”と呼ばれる妖怪だった。ある日、旅の途中に立ち寄った博打場で、主の鬼蔵にワケありな計画を持ちかけられ……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("土方巽・夏の嵐／２００３～１９７３燔犠大踏鑑", "土方巽・夏の嵐／２００３～１９７３燔犠大踏鑑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "舞踏家土方巽の、現存する最後の舞踏映像である1973年の京都大学公演の記録フィルムをデジタル処理。この年を最後に舞踏を封印した彼の貴重な舞いが蘇る。唐十郎や麿赤兒らの演劇人や舞踏家に影響を与えた土方。舞踏ファンならずとも、彼の魂揺さぶる踊りに圧倒される。日本独自の舞踏の創始者・土方巽。彼は、様々な芸術ジャンルに圧倒的な影響を与えてきたが、1973年6月、京都大学西部講堂内の観衆の面前で公演した伝説的な舞台『夏の嵐 燔犧大踏鑑』を最後に、自らの舞踏を封印する。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天国はまだ遠く", "天国はまだ遠く", "", "", "http://image.eiga.k-img.com/images/movie/53711/315x210.jpg?2784", 117, "瀬尾まいこの同名小説を「13階段」「夜のピクニック」の長澤雅彦監督が映画化。主演は加藤ローサと、本格的な演技初挑戦となるお笑いコンビ「チュートリアル」の徳井義実。自殺を考え、京都府宮津の人里離れた山奥に建つ民宿「たむら」にやってきた千鶴。宿には若い主人の田村がひとりいるだけ。千鶴は睡眠薬を大量に飲んで自殺を図るが、普通に目覚めて失敗。やがて豊かな自然と何気ない田村の優しさに、行き場のない千鶴の心は解きほぐされていく……。", "2008-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STREET SOLDIERS", "ストリート・ソルジャー　炎の逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ストリート・ギャングたちが群雄割拠するロサンゼルスで、二大勢力ともいわれる“タイガース”と“JP”。残虐な行為で領地を拡大する“JP”は、リーダーの出獄とともに“タイガース”に一方的な抗争を仕掛けてきた。“タイガース”のメンバーは空手を修得して反撃の時期をうかがうが……。痛快ストリート・アクション。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GOLDEN BLADE", "王者の剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "砂漠を舞台にした伝奇時代劇。何者かに父親を殺された青年ハルンは、仇を追ってバグダッドへやって来た。彼は不敗の力を与える“ダマスカスの剣”を手に入れるが、大守の姫カイルザンをめぐる争いに巻き込まれて剣を盗まれてしまう。剣を取り返すべく、彼は王宮に乗り込んでいくが……。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人生劇場", "人生劇場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "尾崎士郎の『人生劇場』は戦前・戦後を通して何度も映画化されているが、この日活版は舛田利雄＝高橋英樹で「青春編」のみ。バンカラ早大生・瓢吉を演じた高橋のキャラクターは「けんかえれじい」(1966・鈴木清順監督）の南部麟六へとつながっていく。吉良常役の宍戸錠も魅力的。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUNATIC", "LUNATIC", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ピンク四天王の一人、サトウトシキ監督が手掛けた初の一般作。共同生活を送る４人の男女の愛憎を官能的な映像で綴り、新感覚の恋愛を描く。アル中の作家・秋元と、同居中で元女優の由美子はある夜、ヤクザに追われるゲイの麻薬売人・浩一と、その弟で去勢された裕二と出会う。東京を逃れることになった４人は、浩一と裕二の子供の頃の隠れ家で共同生活を始める。平和な生活の中で由美子は裕二を愛していったが、裕二は彼女に肉体的な満足感を与えることができない。そんな中、裕二は浩一に、自分の代わりに由美子を抱いてほしいと頼むが、そのことから男たちの間に嫉妬心が生まれるのだった。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JALOUSIE", "ジェラシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ポップ・スター、リオとフランス映画界のサラブレット、C・ヴァディムが共演した、お洒落な恋愛ドラマ。プレイボーイのピエールと恋に落ち、同棲を始めたカミーユは、彼のちょっとした言葉や仕草が浮気を証明しているように思えてしょうがない。彼女のそんな被害妄想的な思いは、次第に大きくなっていく……。", "1991-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAESAR AND CLEOPATRA", "シーザーとクレオパトラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "紀元前50年、シーザーとクレオパトラの出会いからエジプト平定、そして二人の別れまでを描いた一大歴史スペクタクル。クレオパトラ役にV・リー、シーザー役にC・レインズを配した豪華なキャストが見どころ。原作のG・B・ショウ自ら脚色にあたり、厳密な考証のもと600万ドルを投じた衣装・セットをそろえ、壮大なエジプト・ロケも盛り込まれた作品。", "1946-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Sirena Y El Buzo", "人魚と潜水夫", "", "", "http://image.eiga.k-img.com/images/movie/55011/315x210.jpg?4661", 86, "ニカラグアの大西洋岸に暮らす先住民ミスキート族の生活を、ドキュメンタリーとフィクションを織り交ぜ、独特のタッチで綴った寓話劇。海と共に生きるミスキート族の暮らしには、人間の魂が人魚によって甦るという伝説が根付いている。そんなミスキート族の漁村に生を受けた虚弱な少年シンバッドが、やがてたくましく育ち潜水夫となって海へ帰っていくまでを描き出す。メガホンを取るのはスペイン出身の女流監督メルセデス・モンカーダ・ロドリゲス。", "2009-10-18", "19,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("義胆雄心／GANGLAND　ODYSSEY", "仁義なき抗争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "香港の資産家ブラウンは、部下のチャイと元刑事ファンに、誘拐された息子ポールの救出を依頼する。二人はポールを助け出すことに成功するが、黒幕である日本のヤクザ中原の怒りを買い、命を狙われる。危機一髪のところを救い出してくれたのは、日本の本部から視察に来ていた高田組長。実は高田は、昔、香港黒社会で凶悪な殺し屋として恐れられていたプウであった。やくざの仁義を通すため、再び暗殺者を差し向ける中原。暗黒街を舞台に、香港マフィア、元刑事、日本のヤクザが激突するアクション。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESPERATELY SEEKING SUSAN", "マドンナのスーザンを探して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "舞台はニューヨーク。平凡な主婦がある日“必死にスーザンを探している”という、奇妙な新聞広告に興味を持ったことから事件に巻き込まれていく姿を、新鮮な映像感覚でスリリングに描く。R・アークエットが好演。スーパースター、マドンナの個性もうまく生かされている。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誘惑", "誘惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「象を喰った連中」「安城家の舞踏会」に続く吉村公三郎の戦後第３作目。青年代議士は、妻帯者ながら亡き恩師の一人娘の学業と生活を援助するうちに、愛を抱くようになる。ある夜二人は温泉宿に泊まるが、娘は許されざる恋から逃がれるように宿をあとにする。代議士の妻は死の床で二人の愛を許す——というメロドラマ。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビルと動物園", "ビルと動物園", "", "", "http://image.eiga.k-img.com/images/movie/53681/315x210.jpg?2739", 100, "音大生の慎は、窓拭きのアルバイトで訪れたオフィスビルでOLの香子にひと目惚れする。先輩の機転で香子と知り合うことが出来た慎は、彼女を動物園デートに誘う。上司との不倫関係に疲れきっていた香子は、慎との関係を心の拠り所とするようになり……。「ぼくたちと駐在さんの700日戦争」の坂井真紀と「リンダリンダリンダ」の小林且弥が年の差カップルを演じる切なくも温かいラブ・ストーリー。監督は本作が長編デビューとなる新鋭・齋藤孝。", "2008-07-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ULTIMO MONDO CANNIVALE", "カニバル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "北ボルネオ山中の人喰い人種族の住む地域に不時着した石油開発技師ロバートら一行の、決死の脱出劇を描いたホラー。イタリア映画ならではの目を覆うばかりの残酷シーンが全編に繰り広げられる。1974年に起きた実話をもとに、R・デオダートが映画化。DVDタイトルは「カニバル　世界最後の人喰い族　ノーカット完全版」。", "1976-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LITTLE SHOP OF HORRORS", "リトル・ショップ・オブ・ホラーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "町の片隅にある花屋の店員シーモアは、店の主人にいつもこき使われているドジでマヌケな男。そんな彼が大切にしていた植物というのが、なんと人喰い植物であった。オリジナルはモノクロだが、ビデオ版はカラー化が施されている。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マルタイの女", "マルタイの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "新作ごとにユニークな題材選びでファンを楽しませた伊丹監督が、かつて暴力団に襲われ、警察の身辺警護対象者（マルタイ）となった経験をモチーフに製作したサスペンス・コメディ。ふとした偶然から殺人事件の現場を目撃した女優ビワコ。重要証人である彼女を守るため、警視庁は二人の腕利き刑事、立花と近松に密着ガードを命じるが、犯人のカルト集団、真理の羊の魔手が伸びてくる……。主人公は宮本信子扮するビワコだが、彼女と友情をはぐくむ刑事コンビ役の西村雅彦、村田雄浩のコミカルな演技が楽しい。守る者と守られる者の勇気を描きつつ、カーチェイス、格闘シーン、多彩な脇役の大活躍など、サービス満点の内容は伊丹作品ならではの仕上がり。1997年末、監督が謎の自殺を遂げたため、本作が遺作となった。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Gypsy Moths", "さすらいの大空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "　スカイダイビングのショーをしながら旅を続ける三人の男たちがいた。リーダーは、寡黙なレティッグ。そしてショーの演出を仕切る陽気なブラウディ、若手のマルカム。中でもレティッグは無茶なパフォーマンスを披露して観客を沸かせていた。しかしある時、思いがけない事故が起こってしまう……。ダイビングに命を賭ける三人の生き様を、女性たちとの交流を絡めながら描く。空中で繰り広げられる妙技は迫力満点。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POCKET MONEY", "ポケット・マネー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ニューマン、マーヴィン二人の魅力がいっぱいの佳作。バイヤーから250頭の牛を買いつけて運ぶ仕事を受けたカウボーイ・コンビ。だが、苦労の末、結局金は手に入らず、残ったのは友情だけだったというお話。脚本は「地獄の逃避行」「天国の日々」のT・マリック。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花園の迷宮", "花園の迷宮", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "第32回江戸川乱歩賞を受賞した原作を、「白蛇抄」「花いちもんめ」の鬼才・伊藤俊也が映画化。横浜の豪華な洋館ホテル“福寿楼”。この迷宮を思わせるホテルの中で連続殺人事件が起こるミステリー。島田陽子、内田裕也をはじめとしたキャスト陣も豪華。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Aliens", "エイリアン2", "", "", "http://ecx.images-amazon.com/images/I/41eT66CvHNL._SL210_.jpg", 139, "スイスの画家H・R・ギーガーによるエイリアン・デザインと、感情の一切入り込む余地のない完全無比の生命体であるという設定が、得もいわれぬ恐怖感を生み出した傑作「エイリアン」の続編。前作で唯一、エイリアンの襲撃から生きのびた、S・ウィーヴァー扮する女性航海士リプリーが、地球に無事生還したのもつかの間、今度はあの化け物を絶滅させる使命を帯びて、再度戦いを挑む……。前作が未知の生命体に対する恐怖感を軸にした、いわゆる“ホラー・ムービー”であったが、本作品はエイリアンとの直接対決を正面に据えた、“アクション映画”になっているのが特徴である。また一匹（体）しか登場しなかった前作に比べて、マザー・エイリアンを筆頭に無数のエイリアンが暴れまわるのも、大きな相違点の一つ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOLLOWING", "フォロウィング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "創作のヒントを得るという理由で見知らぬ人への尾行を繰り返している作家志望の青年ビル。彼はその尾行を見破ったコッブという男に誘われ、家宅侵入の犯罪に手を染める。やがてビルは、忍び込んだアパートで見つけた写真の美女に心奪われ、彼女に接近していくが……。C・ノーラン監督が「メメント」で脚光を浴びる前に撮り上げたフィルム・ノワール。映画以外の定職を持つスタッフ＆キャストとともに週一回のペースで撮影を行ったという自主製作の低予算映画だが、複雑に時制を入り組ませた脚本、編集は見事で、犯罪への好奇心ゆえに思わぬ罠に落ちていく主人公の心理が興味をそそる。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chicken Run", "チキンラン", "", "", "http://image.eiga.k-img.com/images/movie/1331/315x210.jpg?4309", 85, "柵の向こうの自由な世界を夢見るトゥイーディー養鶏場の雌鳥ジンジャーは、仲間たちと何度も脱走を試みては失敗を繰り返す日々。そんな時、お調子者の雄鳥ロッキーが現れる。ロッキーの指導の下、飛び方を練習するジンジャーたちだったが、その頃意地悪な女主人トゥイーディーはパイマシンを購入、養鶏場の雌鳥でチキンパイを作って一儲けしようと企んでいた。絶体絶命のニワトリたちは、最後の大脱走作戦を開始する。", "2001-04-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Family Business", "ファミリービジネス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　ニューヨークに暮らすジェシーは泥棒家業ひと筋に生きる男。その息子ビトーは結婚し、息子が生まれたことを機に泥棒を引退していた。ところがビトーの息子アダムは祖父のジェシーを秘かに尊敬し、新製品の強奪計画を提案。ビトーも父と息子を放っておくことができず、それに加わることに。父子、そして孫から成る泥棒チームは計画を実行に移すが……。犯罪一家の絆を描くドラマ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRUTE　FORCE", "真昼の暴動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "熱気あふれるサスペンス映画の傑作「裸の町」の製作者と監督コンビによる脱獄サスペンス映画の秀作。製作より10年の時を経て日本公開され、観客の心を高鳴らせた。ラストで見せる集団アクションのシークエンスは圧巻の一語。アメリカの刑務所が舞台。冷酷非情な看守リーダーに反逆する囚人たちが一致団結して大脱走を計画する。手に汗握る展開が待っている。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANIAC COP3 BADGE OF SILENCE", "マニアック・コップ3 復讐の炎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "地獄からよみがえった殺人警官コーデルの恐怖を描く、アクション・ホラー第３作。今回は、犯人を狙撃しながらも自ら重傷を負った女性警官ケイトに汚名を着せた人々を、コーデルが次々に殺していくというもの。監督は、１作目からのW・ルスティーグが担当。愛に目覚めるコーデルも見もの。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GLEN OR GLENDA", "グレンとグレンダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "“映画史上最低の監督”エド・ウッドのデビュー作。アメリカで話題になった性転換事件にヒントを得たプロデューサーは、当初「わたしは性転換した！」とタイトルをつけたが、エド・ウッドの手でいつの間にか“女装は悪くない！”と訴えるメッセージ作品になってしまった。物語は、運命を司る精霊を絡め、精神科医の語る二つの女装マニアの例で綴られる。その一例となるグレンを演じるのは、実際にも女装マニアだったエド・ウッド。また、恋人バーバラ役で稚拙な演技を見せつけるドロレス・フラーは、現実にもエドの恋人だという。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETURN TO JUSTICE", "拷問警察 ミリタリー・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "アメリカの古き良きオールドスタイルの保安官が、麻薬組織に捕らえられた自分の娘を救うためにコロンビアに乗り込み、麻薬汚染の元凶である軍司令官と対決する。麻薬組織が暗躍する南米コロンビアを舞台にした痛快アクション。西部劇そのままのいでたちで登場する主人公ジェスロのキャラクターが楽しい。ビデオ発売時タイトルは「リターン トゥ ジャスティス」。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEMENTO　MORI", "少女たちの遺言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "韓国の女子高生たちのリアルな姿をみずみずしく描く異色ホラー。女子高生のミナがある日、赤い日記帳を拾う。そこには、同級生のシウンとヒョシンの禁断の関係が赤裸々に綴られていた。ところが同じ日、ヒョシンが投身自殺をしてしまう。彼女たちの関係に疑問を抱くミナだったが、やがて彼女の周辺で奇怪な現象が起こり始める。", "1999-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪獣王ゴジラ", "怪獣王ゴジラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "英語タイトルは“Godzilla King of the Monsters”。「ゴジラ」アメリカ公開に際し、海外で新しく撮り足したシーンを付け加えて再編集された改作版。アメリカの新聞記者がゴジラの東京上陸をレポートするという内容に変更された。そのため、物語の細部は縮まり、ゴジラの破壊性が強められている。", "1957-05-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二等兵物語・女と兵隊・蚤と兵隊", "二等兵物語・女と兵隊・蚤と兵隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "伴淳三郎と花菱アチャコのコンビによる“二等兵物語”シリーズの第１作。戦争も末期の昭和20年、古川凡作は令状を受けて入営するがケガの功名で従卒に抜擢され、将校の娘と結婚させられそうになる。アチャコは親友役の柳田一平二等兵として登場する。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPER SHOW3 3D", "SUPER SHOW3 3D", "", "", "http://image.eiga.k-img.com/images/movie/56791/315x210.jpg?3447", 80, "韓国の13人組男性アイドルグループ「SUPER JUNIOR（スーパージュニア）」のコンサートを3Dで上映。2010年8月にソウルオリンピック公園体操競技場で行われた「SUPER JUNIOR 3rd ASIA TOUR 『SUPER SHOW3』」の模様を3Dカメラで撮影して製作。「SORRY, SORRY」「Don't Don」「美人（BONAMANA）」など多数のヒット曲のパフォーマンスを収録している。", "2011-07-02", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Haunting in Connecticut", "エクトプラズム　怨霊の棲む家", "", "", "http://image.eiga.k-img.com/images/movie/55101/315x210.jpg?3374", 103, "アメリカで起きた実在の事件を、新鋭ピーター・コーンウェルが映画化したホラー。コネティカット州に引っ越してきたキャンベル一家は、ビクトリア朝式の古い屋敷に入居するが、その家はかつて葬儀場で、しかも儀式も行われた場所だった。次々に起こる超常現象が、一家を地獄のような恐怖に陥れる。主演は「サイドウェイ」でアカデミー賞助演女優賞にノミネートされたバージニア・マドセン。", "2010-01-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("のんきな姉さん", "のんきな姉さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "日本に古くから伝わる『安寿と厨子王』の物語にヒントを得た３作品、森鴎外の『山椒大夫』、唐十郎の『安寿子の靴』、漫画家・山本直樹のコミック『のんきな姉さん』の各作品を素材に、新鋭・七里圭が撮り上げた異色の姉弟愛ドラマ。両親の死後、互いに深い絆と愛で結ばれた姉と弟が、姉の妊娠を機にその関係が揺れ動いていく様子を幻想的に紡ぎ出す。両親の死後、二人だけで生きてきた姉の安寿子と弟の寿司夫。二人の間には、姉弟の関係を越えた愛が芽生えていた。しかし、クリスマスの夜、安寿子の携帯に寿司夫から電話がかかり、寿司夫は今夜自殺をすると告げる。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BASKET CASE", "バスケットケース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ゲテものだ、キワものだと蔑むにはあまりにも詩情あふれる快作スプラッタ。ミッドナイト・シアターでカルト化した作品だ。シャム双生児として生まれた兄のベリアルと弟のドゥエイン。切断を望まぬ二人を分離した医者たちへの復讐のため、弟は、“肉塊”となった兄をバスケットに入れ、ニューヨークにやって来る。バスケットから指や目を出したりする場面は「グレムリン」そっくり。またベリアルの復讐場面はかなり凄惨。しかし、この作品の真の面白さは、復讐のベクトルがこの兄弟自身へと向かわざるを得ない点にこそある。弟に恋人ができると、兄は嫉妬のあまり、超能力で弟を責めさいなみ、弟の恋人をも犯し殺すのだ。ホテルの窓から夜空に泣き吠える肉塊のなんと切ないことか。兄弟心中とも思えるラストを迎え、この作品は聖なるフリーク映画へと昇華する。DVDは「バスケット・ケースBOX」に収録。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死ガ二人ヲワカツマデ…　第二章　南瓜花 nananka", "死ガ二人ヲワカツマデ…　第二章　南瓜花 nananka", "", "", "http://image.eiga.k-img.com/images/movie/58141/315x210.jpg?4178", 0, "人気アイドルグループ「AKB48」の藤江れいな、声優・俳優として活躍する豊永利行が主演するサスペンスアクション。不治の病で入院生活を送っている少女・怜羅は徐々に視力を失っていくが、担当医の中里との出会いにかすかな希望を見出す。しかし、そんなある日、院内で患者が次々と自殺する事件が発生し……。人気アイドルや声優、舞台役者など多彩なキャストの共演が実現した「死ガ二人ヲワカツマデ…」全2章の第2章。", "2012-09-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BEAUTICIAN AND THE BEAST", "美容師と野獣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ニューヨークに住む美容師ジョーイはひょんなことから東欧の小国に招かれ、大統領ポチェンコの子供たちの家庭教師をすることになる。国民から野獣のような独裁者と呼ばれ、子供たちにも厳しいポチェンコとジョーイは衝突ばかりしているが……。二人の間に芽生える恋の行方を追ったラブ・コメディ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE OUT−OF−TOWNERS", "おかしな夫婦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "栄転でオハイオの田舎からニューヨークの本社に乗り込むご機嫌のサラリーマン夫婦が、都会と田舎のギャップに戸惑いカルチャー・ショックを巻き起こす。名脚本家N・サイモンお得意の都会派コメディ。A・ヒラー監督の演出も手際よい小品。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE V.I.P.S.", "予期せぬ出来事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ロンドン空港を舞台にしたラティガンの脚本が光る人間群像劇。霧で滑走路が閉鎖され、その飛行機に乗る人たちの一夜の人間模様を描く。O・ウェルズ、E・マルティネリ、E・テイラー、M・ルザフォードなどが繰り広げる演技の競い合いが魅力。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.FORBUSH AND THE PENGUINS", "二人だけの白い雪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "大学の生物学者フォーブッシュは、ある日チャーミングな女、タラと出会う。彼は彼女に恋をするが、タラは彼の軽率な態度が好きになれない。そんな彼に南極でペンギンの生態を研究するチャンスがやって来る。タラの説得もあり、南極に行ったフォーブッシュは自然の中で生きるペンギンに感動を覚える。", "1972-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRISS CROSS", "トワイライト・サマー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "時代は1969年、ベトナム戦争で心に傷を負った夫に捨てられた母と子が、新天地フロリダで様々な問題にぶつかりながらもたくましく生き抜く様を描く。G・ホーンが、自身の製作会社で作る５本目の映画でシリアスな愛情物語となっている。監督のC・メンゲスは撮影監督として「キリング・フィールド」「ミッション」で二度アカデミー賞を受賞した後、「ワールド・アパート」で監督として注目された。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EASTER PARADE", "イースター・パレード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "第二次大戦後、我が国に輸入された最初のミュージカルとして、当時の日本人に強烈な印象を与えた。全編に散りばめられたアーヴィング・バーリンの名曲と、アステア＝ガーランドのニ大スターの共演で、楽しさあふれる佳作となっている。ダンサーのドンはイースターを明日に控え、パートナーを引き抜かれてくさっていた。やけになったドンは、踊りの基本も知らないハンナを誘いチームを組み、二人は大成功を収めるが、ハンナはドンが今でも別れたパートナーを愛していると思い込む。しかしやがて、その誤解もとけ、二人はイースターパレードでにぎわう街に出るのだった。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花ゲリラ", "花ゲリラ", "", "", "http://image.eiga.k-img.com/images/movie/54031/315x210.jpg?3811", 93, "ミュージカル「テニスの王子様」で人気を博した若手男優を中心に描く青春映画シリーズ“キラキラMOVIES”の第2弾。主演は「トリコン!!!リターンズ」「平凡ポンチ」の小西遼生、「砂時計」「旅立ち／足寄より」の伴杏里。OLの映子は帰宅途中、線路脇で花の種を植える青年を見つける。青年に興味を持った映子は勝手に種まきを鉄手伝うことを宣言し、2人の“花ゲリラ”が始まる。", "2009-01-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラストソング", "ラストソング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ロック・スターになる夢を抱いて、博多から東京に出てきた修吉。成功のためには友も捨て、貧しい生活にも耐えた修吉だったが、彼はディレクターから無情にも“歌をやめてマネージャーになれ”と告げられる……。『北の国から』をはじめとするヒューマン・ドラマの得意な杉田成道による、1990年代の青春群像劇。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("涙にさよならを", "涙にさよならを", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "父が後妻をもらったことで家を飛び出した青年と彼の恋人は、ひょんなことから年老いたスリと知り会った。ある日、そのスリがスッた運転免許証がもとで一同は麻薬団に狙われるハメになる。橋幸夫主演の青春アクション映画。演出には喜劇を得意とする前田陽一があたっている。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wild Flower ワイルド・フラワー", "Wild Flower ワイルド・フラワー", "", "", "http://image.eiga.k-img.com/images/movie/57401/315x210.jpg?4778", 70, "青春と性をテーマに監督たちが独自の個性で自由に製作する「青春H2（セカンド）」シリーズの7作目。自分を捨てた母に恨みを抱く孤児院出身のユキコは、すでに他界してしまった母への復讐のため、遺骨を薬品で溶かし、母がこの世に生きていた証を消し去ろうと計画していた。偶然出会った万引き少年のナオキに必要な薬品を調達させ、着々と準備を進めるユキコだったが、その身辺では別の恐ろしい計画が実行されようとしていた。青山真治に師事し、映像ディレクターとして活動する青木紀親の商業映画監督デビュー作。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Tourneuse de Pages", "譜めくりの女", "", "", "http://image.eiga.k-img.com/images/movie/53481/315x210.jpg?2453", 85, "ピアニストを目指す少女メラニーは、音楽学校の入学試験で審査員を務める人気ピアニスト、アリアーヌの無神経な態度に動揺して失敗し、夢を諦める。美しく成長したメラニーはアリアーヌと再会し、演奏の出来を左右する“譜めくり”の役を任されるが……。2人のヒロインの愛憎を、自らも著名なビオラ奏者であるドゥニ・デルクール監督が情感豊かに描き出す。主演は「女はみんな生きている」のカトリーヌ・フロと「ある子供」のデボラ・フランソワ。", "2008-04-19", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("URI GELLER", "超能力者 ユリ・ゲラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "かつて日本でも一世を風靡した超能力ブームの立役者ユリ・ゲラーの人生を、K・ラッセルが映画化。少年ユリは、不思議な能力を持っていることに悩みながらも、肉体の成長とともに超能力も向上させていく。やがて人々の前でその能力を見せるようになった彼は、様々なバッシングを受けながらも、世界中の人気者になる。愛する妻や良き理解者に恵まれた彼は、能力のさらなる解明と、人類への貢献に目覚めていく。センセーショナルな題材と描写で定評があるK・ラッセルが、興味深いキャラクターである超能力者の隠された過去と秘密を独特の感性と映像美で綴っていく。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shirin", "シーリーン", "", "", "http://image.eiga.k-img.com/images/movie/54981/315x210.jpg?4613", 91, "「友だちのうちはどこ？」「桜桃の味」など数々の名作を世に送り続けてきたイランの巨匠アッバス・キアロスタミ監督による実験的作品。12世紀に活躍したペルシアの詩人ニザーミーの古典叙事詩に基づいた悲恋物語「シーリーン」の映画を客席から鑑賞する100人以上の女優たちの表情を、クローズアップして次々と捉え続けていく。スクリーンを見上げる女優たちは「イングリッシュ・ペイシェント」などのフランス人女優ジュリエット・ビノシュなど。", "2009-10-20", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("酔拳２／THE DRUNKEN MASTERII", "酔拳２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ジャッキー・チェン初期の出世作「ドランク・モンキー　酔拳」の16年ぶりの続編。今回は20世紀初頭の中国で活躍した実在のヒーロー、ウォン・フェイフォンにジャッキーが扮する。奇想天外な修行の末に、酔えば酔うほど強くなるという中国武術4000年の歴史において最強といわれる“酔拳”の型をマスターしたウォンだが、精神的な未熟さゆえ、その奥義を極めるまでには至っていない。ある日、ウォンは高名な軍人武闘家フクと知り合い、中国皇帝の証である“翡翠の印章”の略奪事件に関わることとなる。そこには、中国の国宝を大英博物館に持ち去ろうとするイギリス領事一味の陰謀が絡んでいた。武術指導はもちろんジャッキー・チェン。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ATLANTIC CITY", "アトランティック・シティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "近代化の波が押し寄せ、古き良き時代は過去のものとなりつつあるカジノの街、アトランティック・シティ。ここに生きる老ギャングの飄々とした姿を描いた、マル監督のアメリカ時代の代表作。老ギャングのルーとカジノのバーで働きながらディーラーを目指すサリーの二人は、サリーの別れた夫が街に戻ってきたことから思いもかけなかった事件に巻き込まれていく。サリーの元夫は麻薬密売組織からヘロインを奪い、それを売りさばきに来たのだった。新しく生まれ変わろうとする街を背景に、若い女とのささやかなふれあいをユーモラスに、かつ哀歓漂わせながら演じたランカスターが渋い味を出している。", "1980-01-01", "2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La petite Lili", "リリィ", "", "", "http://ecx.images-amazon.com/images/I/51MBV42CDWL._SL210_.jpg", 101, "フランスの名匠クロード・ミレールが、チェーホフの戯曲「かもめ」をモチーフに描いたラブストーリー。フランスの田舎町。映画監督を志す青年ジュリアンは恋人のリリィを主人公にした映画を制作し、家族らを集めて上映会を開く。ところが、大女優の母マドに作品を酷評された上に、リリィがマドの恋人である映画監督ブリスと駆け落ちしてしまう。タイトルロールを演じるのは、「スイミング・プール」などの若手注目女優リュディビーヌ・サニエ。", "2003-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Resident Evil: Retribution", "バイオハザードV リトリビューション", "", "", "http://image.eiga.k-img.com/images/movie/56101/315x210.jpg?2024", 0, "カプコンの人気ゲーム「バイオハザード」をミラ・ジョボビッチ主演で実写映画化したシリーズ第5作。世界人口の大半をアンデッドに変えてしまったT-ウィルスを開発したアンブレラ社。その最高機密が隠された施設に潜入したアリスは、そこで自身の謎めいた過去を知る。そして、人類滅亡の危機を招いた張本人を探すため東京、ニューヨーク、ワシントンDC、モスクワと世界を巡るうちに、さらなる驚くべき新事実を突き止める。監督は前作に続きポール・W・S・アンダーソン。シリーズ第1作に登場したミシェル・ロドリゲス扮するアンブレラ社の特殊部隊隊員レインや、シエンナ・ギロリーが演じる人気キャラクター、ジル・バレンタインらも再登場。", "2012-09-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REMBRANDT", "描かれた人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "レオナルド・ダ・ヴィンチと並び称される“光と闇の画家”レンブラントの半生を描く伝記ドラマ。17世紀半ば、オランダで名声を得たレンブラントは、絵のモデルでもある最愛の妻サスキアを病で失う。失意の中で手掛けた『夜警』も世間には受け入れられず、富と名声を失った彼に再びインスピレーションを与えたのは、メイドのヘンドリッチェだった……。「戦艦バウンティ号の叛乱」などで知られる名優C・ロートンがレンブラントの秘められた苦悩を熱演。", "1936-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コンナオトナノオンナノコ", "コンナオトナノオンナノコ", "", "", "http://image.eiga.k-img.com/images/movie/34311/315x210.jpg?1687", 77, "「パビリオン山椒魚」で脚光を浴びた冨永昌敬監督が、女性のリアルな姿を描き続ける漫画化・安彦麻理絵の同名作を映画化。女性誌の編集部で働く独身キャリアウーマンのチアキと、彼女の元同僚で幼い娘を持つ専業主婦のマサミ。親友同士の2人は、対照的な道を歩みながらも互いの人生に憧れを抱いていた……。幸せを追い求める2人の女性の日常を赤裸々に綴る。主演は「ワンダフルライフ」のエリカと「バウムクーヘン」の桃生亜希子。", "2007-11-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間交差点〈ヒューマンスクランブル〉不良", "人間交差点〈ヒューマンスクランブル〉不良", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "『ビッグコミック・オリジナル』で連載された人気漫画を映画化。昭和30年代のある地方都市を舞台に、打算的な大人社会に嫌悪感を抱き、風変わりなヤクザ者の姿に惹かれる不良少年の青春を、パワフルに描いていく。主題歌として使用され、作品のロマンを盛り上げる森田童子の『蒼い夜は』が印象的。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女奴隷船", "女奴隷船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "大本営に向かう途中遭難、上海に帰るという得体の知れない貨物船に救われた須川中尉。その船は、日本の女たちを上海に売り飛ばす“お唐さん船”と呼ばれている女奴隷船だった。小島でセリにかけられた女たちを救うため須川は悪の首領らに果敢に挑む。舟崎淳の『お唐さん』をもとにした海洋アドベンチャー。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRUTHS：A STREAM", "TRUTHS：A STREAM", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 182, "参議院議員である父の第２秘書を務める橘響子のもとに大学時代の恋人、冴木峻一が訪ねてくる。学者として成功しながらも生に対して充足感を得られない峻一は自殺を決意、響子に一緒に死のうと誘う。山の奥地に入った二人は自らの死を充実させようと、自分たちの墓作りを始める。墓を作りながら人間の有り様について話し合ううち二人の生への充足感は満たされ、同時に死を充実したものにできると考えるが……。緊張感あふれる美しい映像によって、二人が自らの闘いのなかで真実を真摯に見つめ続ける様を描き、生と死などの人間の根源の問題に迫った作品。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴力の街", "暴力の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "敗戦後の混乱期に埼玉県下の地方都市で、実際に起こった事件をルポルタージュした朝日新聞記者の原作『ペン偽らず』をもとに、この作品のために作られた製作委員会が映画化した。暴力と金で町を支配するボスの一派と新聞記者や市民たちが闘う。東宝、松竹、大映など映画会社の枠を越え、広く劇団関係の俳優までが出演し、全国に市民の団結の力を知らせて共感を呼んだ。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仄暗い水の底から", "仄暗い水の底から", "", "", "http://image.eiga.k-img.com/images/movie/1431/315x210.jpg?2008", 101, "「リング」で大ブームを起こした原作者と監督のコンビが再びホラーに挑んだ話題作。主演の黒木瞳はホラー映画初挑戦。テーマが「母性」だけに、結末で感動を呼んでいる。離婚によって新生活を始めることになった淑美と娘の郁子。だが、夫と親権争いに巻き込まれ、引っ越したマンションでは不可解な足音や水漏れに悩まされる。そして郁子が拾ってきた子供用のバッグから、未解決の幼女失踪事件の事実が浮かび上がる。", "2002-01-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KLASSENVERHA¨LTNISSE", "アメリカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "カフカの未完の長編小説『アメリカ』の忠実な映画化。原題の“階級関係”は、『アメリカ』でカフカが描く資本主義社会の歪みを強調したもの。16歳のカール・ロスマンは、女中を妊娠させてしまったため、両親によって伯父ヤコブのいるアメリカに送られる。だが、ニューヨークに下船する前に火夫に同情して船長とやりあってしまった。そこで偶然カールは伯父のヤコブと出会うが、伯父ともうまくいかないカールは西部へと旅立つ。だが、アメリカにおけるカールの生活にはいつもトラブルが絶えず……。そんなカールのアメリカでの遍歴を、これまでにない新鮮な映像スタイルで描き出している。", "1983-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("斬る", "斬る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "三隅研次監督、市川雷蔵主演による“剣３部作”の一編。雷蔵が演じるのは、複雑な出生の秘密を持つ小諸藩士・高倉信吾。彼は藩士の許しを得て江戸に出、千葉道場の栄次郎に剣の腕を認められる。栄次郎の勧めで信吾は大目付・松平大炊頭に仕えることに。そして３年の月日が流れ、信吾は大炊頭とともに水戸藩の取り締まりに赴くが、策略によって主君を殺され、彼も切腹して果てる。とにかく殺陣がすさまじく、すばやいカット割りとダンスをするような立ち回りは圧巻のひと言。また、雷蔵が敵を真上から一刀両断するシーンでは、人が文字通り、まっぷたつになる。雷蔵の虚無的なキャラクターは、翌年からの“眠狂四郎”シリーズに受け継がれた。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンヘッド", "ドラゴンヘッド", "", "", "http://ecx.images-amazon.com/images/I/51SGC5SAB6L._SL210_.jpg", 122, "望月峯太郎の同名漫画を、ウズベキスタン・ロケを敢行して映画化した意欲作。突然、崩壊した世界で生き残ろうとする高校生男女の姿を、ストイックなラブ・ストーリーを軸に真摯に紡ぎ出していく。高校生・テルは修学旅行の帰りの車中で、突然の事故に遭遇。新幹線ごとトンネル内に閉じ込められる。恐怖で錯乱しかけている同級生・ノブオは“光を見た”と言う。いったい何が起こったのか。テルは怯えるアコを連れて、脱出を目論むが……。", "2003-08-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("転生 TENSEI", "転生 TENSEI", "", "", "http://ecx.images-amazon.com/images/I/51gV4ZZj0XL._SL210_.jpg", 99, "22歳で死ぬ宿命を背負った3人の女性が主人公のファンタジックホラー。戦後復興期の1960年代、バブルに沸く80年代、そして2000年代を背景に、転生によって繋がった3人の女性たちがたどる恐ろしくも切ない運命を描いていく。3人のヒロインには、女優初挑戦となる人気レースクイーンの鷲巣あやの、「夜のピクニック」の小野まりえ、「渋谷怪談　The リアル都市伝説」の前田綾花を起用。「ピーカン夫婦」の元木隆史が監督を務める。", "2006-06-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("漫才ギャング", "漫才ギャング", "", "", "http://image.eiga.k-img.com/images/movie/55311/315x210.jpg?4481", 127, "「ドロップ」で小説家＆映画監督デビューを果たしたお笑い芸人の品川ヒロシが、再び自らの書き下ろし小説を映画化。結成10年目の売れないお笑いコンビ「ブラックストーン」のボケを担当する飛夫は、ついに相方から解散を宣告されてしまう。ヤケ酒でトラブルを起こして警察に連行された飛夫は、留置場で出会った不良の龍平にツッコミの才能を見出し……。主演は「ROOKIES」の佐藤隆太、相方役に「クローズZERO」の上地雄輔。", "2011-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESTINATION SATURN", "原子未来戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "フラッシュ・ゴードンと並ぶアメリカン・ヒーロー、バック・ロジャースが活躍するスペース・オペラ。12話からなる連続活劇を90分に編集した短縮版。20世紀人のバックが25世紀によみがえり、独裁者打倒に立ち上がる。反重力ベルトなどのメカが、当時は画期的だった。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE　LIAISON　PORNOGRAPHIQUE", "ポルノグラフィックな関係", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "お互いの素性も明かさぬまま、カフェで待ち合わせし、木曜日だけの情事にふける中年の男女。やがてふとしたことから別れの時を迎える彼らの関係を、ベルギー出身のF・フォンテーヌ監督が洗練された演出で語り明かす。登場人物がカメラに向かって過去の出来事を打ち明けるというドキュメンタリーの手法を導入し、男女それぞれの考え方の微妙な違いを浮き彫りにしている。", "1999-01-01", "37,2,58,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死者の学園祭", "死者の学園祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「新宿少年探偵団」で映画デビューし、「リング２」ではアイドルらしからぬ陰惨な最期を演じた深田恭子の初主演作。舞台は名門私立高校。自殺した部員の脚本を学園祭で上演するべく奔走していた演劇部部長・真知子が、部員たちが次々に謎の死を遂げるという事件に遭遇。彼女は、演劇部の顧問教師・倉林や謎めいた転校生・神山の助けを借り、事件の背後に広がる闇を暴き出そうとする。基本的には赤川次郎原作のオーソドックスなミステリーだが、事件解決後、真知子が校庭で倉林に思いを告白するエンディングには映画的な魅惑が充満。とりわけ、それまで劇中では走りっぱなしだった深田が自転車に乗り滑走し、そこに彼女自身が歌う主題歌が流れるシークエンスはかつての角川映画へのオマージュとして感動的だ。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Warrior's Way", "決闘の大地で", "", "", "http://image.eiga.k-img.com/images/movie/55891/315x210.jpg?4151", 100, "「ブラザーフッド」「PROMISE 無極」の韓流スター、チャン・ドンゴンの海外作品初主演作。長年戦ってきた敵一族の最後の生き残りである赤ん坊の殺害を命ぜられた戦士ヤン（チャン）は、指令を無視して赤ん坊とともにアメリカ西部の小さな街へと逃亡する。だが、間もなく追手に居所を知られ、酔っぱらいのロンやサーカスのナイフ使いリンら街の仲間とともに戦うことになる。製作は「ロード・オブ・ザ・リング」「マトリックス」を手がけたバリー・M・オズボーン。共演にジェフリー・ラッシュ、ケイト・ボスワース。", "2012-04-14", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Longest Yard", "ロンゲスト・ヤード（2005）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ロバート・アルドリッチ監督が1974年に手がけた名作スポーツアクションを、「50回目のファースト・キス」のピーター・シーガル監督＆アダム・サンドラー主演でリメイク。飲酒運転で懲役3年の刑を受けた元NFLプレイヤーのポールは、刑務所長のヘイズンから囚人たちによるアメフトチームの結成を命じられる。嫌々ながらも引きうけたポールは、看守チームとの試合に向けて個性的なメンバーたちをまとめあげていくが……。", "2006-04-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("School Daze", "スクール・デイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "　アフリカ系アメリカ人が通う大学。その学生であり、南アフリカの人種隔離政策に反対する活動に取り組んでいたダプは、ガンマ・クラブのジュリアンとたびたび衝突していた。ダプの恋人レイチェルも、白人のようにふるまうミス・ガンマのジェーンと反目しあう。一方、ダプはガンマ・クラブに入部を希望している従弟のことも気がかりだったが……。ミュージカルのスタイルを取り入れながら、ブラック・アイデンティティにまつわる問題を描いた青春グラフィティ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊賀野カバ丸", "伊賀野カバ丸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "亜月裕の同名人気マンガを映画化。黒崎輝をはじめ、若手を中心にJACが総出演したアクション・コメディ。大食、野蛮、不潔と三拍子そろった忍者カバ丸が、母校・金玉学院のために大暴れする。鈴木則文の徹底したギャグ・タッチの演出が全編を覆う娯楽作。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊豆の娘たち", "伊豆の娘たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "この映画の封切りは昭和20年の８月30日で、つまり終戦後封切り作品の第１号というわけである。混乱する時局の中、急場しのぎで製作されたにもかかわらず、職人、五所平之助らしくうまくまとまっている。父親と娘二人で暮らす伊豆の食堂に、宮内という青年が下宿することになり、騒動が持ち上がる。", "1945-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大学の若旦那", "大学の若旦那", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "サイレント末期からトーキー初期にかけて松竹蒲田によって数多く生み出された“カレッジもの”の代表作。ラグビー部の花形選手・藤井は、ガールフレンドとの関係が公になり、退部させられる。退部後、新しい恋人をみつけた藤井をよそにチームは猛練習を続けている。大切な試合が日一日とせまり、藤井は復部を願うが……。", "1933-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Punk Rock Movie", "THE PUNK ROCK MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/53971/315x210.jpg?3139", 84, "ボブ・マーリーやザ・クラッシュのPVやドキュメンタリー作品を手がけたドン・レッツが、70年代のパンク・ムーブメントを記録したドキュメンタリー映画。77年にロンドンでオープンした「ロキシー・クラブ」は、当時ロンドンでパンク・ロックを演奏できる唯一のクラブだった。クラブのDJだったドン・レッツは、伝説のライブやバンドマンだけでなく、パンクで踊り狂う観客らもカメラにしっかりと収めていた……。", "2008-10-11", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("駅前旅館", "駅前旅館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "駅前旅館の番頭稼業を描いたコメディ。こののち“駅前”シリーズとして1960年代東宝の興行を支えた。この第１作は井伏鱒二の原作小説を八住利雄が脚本し、名匠・豊田四郎が演出。生野次平は上野駅前の旅館の番頭を30年つとめるベテランである。ライバル旅館の番頭・高沢とは張り合いながらも良き友。なじみの飲み屋・辰巳屋には目当てのお辰がいる。上野界隈では悪質な客引きの追放運動が始まり、次平はリーダーとして活躍。番頭に森繁、ライバルに伴、お辰に淡島が扮する。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アトムの足音が聞こえる", "アトムの足音が聞こえる", "", "", "http://image.eiga.k-img.com/images/movie/56041/315x210.jpg?1879", 82, "日本初の国産TVアニメーションとして1963年に放映が始まった「鉄腕アトム」の音響デザイナー・大野松雄に迫るドキュメンタリー。大野は、50年代に生まれたばかりの電子音楽の技術を駆使し、アトムが歩く際の独特の足音など「鉄腕アトム」で描かれる未来世界の音を作り出していく。そのひらめきは周囲をうならせるときもあれば、原作者の手塚治虫ともめることもあった。そんな大野の生き様と与えた影響を紐解く。監督は「パビリオン山椒魚」「乱暴と待機」の冨永昌敬。", "2011-05-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARARAT", "アララトの聖母", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "カンヌ映画祭で審査員グランプリに輝く「スウィート・ヒアアフター」で知られるカナダの名手、アトム・エゴヤン監督作品。トルコによるアルメニア人虐殺の史実を背景に、現代を生きるアルメニア人たちの織り成す、見ごたえある群像劇が展開する。アルメニアの血を引く監督が自らのルーツと向き合い、国と民族の誇り、人と人の絆を見つめた渾身の一作だ。トルコ大使暗殺を企てて亡くなったアルメニア人の父親を持つ青年のラフィ。トルコのアルメニア人虐殺を題材にした映画に携わった彼は、自らのルーツと父の死に対する答えを見つけるべく、虐殺の現場であるアララトへ向かう。", "2002-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿修羅の伝説・死闘篇", "阿修羅の伝説・死闘篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "盲目の格闘家を主人公にしたサスペンス・アクション。日本格闘技チャンピオンとなった島勇作は、謎の赤いドレスの女に背後から目を切り裂かれ盲目となる。３年後、ジムに戻った勇作は目を開いていた時と寸分違わぬ神技を披露。ところが、勇作のファンだという車椅子の少女がジムを訪ねて来た日から、勇作は恐ろしい事件に巻き込まれていく。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALL ME BWANA", "腰抜けアフリカ博士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "アメリカの宇宙カプセルが軌道をはずれアフリカ大陸に墜落した。作家兼探検家のマットが探索に派遣される。一方カプセルに積まれた月の秘密情報を盗むため、某国が秘密情報員をアフリカに送る。未開の地を舞台にハチャメチャな騒動を描くコメディ。“腰抜け”シリーズ全10作の最終作。", "1963-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長遊侠伝・秋葉の火祭り", "次郎長遊侠伝・秋葉の火祭り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "東宝の“次郎長三国志”シリーズのあとを受けてマキノ雅広が日活で監督した次郎長もの。秋葉の火祭りをクライマックスにして、次郎長一家と黒駒の勝蔵一家の対決を軽快なタッチで描く。次郎長には河津清三郎、森の石松には、はまり役の森繁久彌が扮し快演。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHATO’S　LAND", "チャトズ・ランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "C・ブロンソンがインディアンの役に扮し、タフな個性をアピールした西部劇。身を守るために酒場の保安官を射殺してしまったアパッチ、チャト。たちまち追跡隊が組織され、彼を追って山岳地帯へと分け入っていく。しかし、そこはチャトのテリトリー。チャトは地の利を活かし、追跡隊を一人また一人と始末する。", "1972-01-01", "3,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH ON THE NILE", "ナイル殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "運命の糸に操られた男と女たち。悠久の歴史を刻むナイル河、豪華客船カルナーク号は愛の野望を乗せて突き進む。次々と起こる謎の殺人事件。名推理を働かせる灰色の脳細胞を持つポワロ。クリスティの代表作を国際スターの競演でよみがえらせたのは、娯楽大作を得意とするJ・ギラーミン。", "1978-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ladyhawke", "レディホーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "13世紀のヨーロッパ、呪術によってそれぞれ狼と鷹にされてしまった男と女が、人間として出会えるのは、日の出と日没の時だけだった……。中世の伝説を、神秘ムードたっぷりに映像化。SFXを駆使した変身シーンやハード・アクションも見ごたえのある、ラブ・ファンタジー。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TRANSPORTER2", "トランスポーター2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "手に汗握るカーチェイスと豪快スタントが売りの娯楽アクション第２弾。凄腕のトランスポーター、フランクは、危険な仕事から足を洗い、マイアミへとやって来る。富豪の6歳の息子ジャックの送り迎えという仕事に就いた彼は、ジャックと心を通わせ、平穏な日々を送っていた。だがその矢先、冷徹な女殺し屋に襲撃され、ジャックを誘拐されてしまう。容疑者にされたフランクは単身救出に向かうが、事件の裏には恐ろしい陰謀が……。", "2006-06-03", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BEDFORD INCIDENT", "駆逐艦ベッドフォード作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "グリーンランド海峡を対潜パトロールしていた米軍駆遂艦ベッドフォードは航行中のソ連潜水艦をキャッチする。30時間にわたる米・ソ艦の息詰まる対決を描いた海洋サスペンス・アクション。M・ラスコヴィッチのベストセラー小説の映画化。個性派R・ウィドマークとS・ポワチエが対照的な名演技を見せる。", "1965-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("異人たちとの夏", "異人たちとの夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "第１回山本周五郎賞を受賞した山田太一の小説を、大林宣彦が映画化した傑作ドラマ。妻と別れ、孤独な日々を送るシナリオライターの英雄は、生まれ育った浅草へ出かけ、12歳で死別したはずの父母に出会う……。生者と死者が共存してドラマが生まれるファンタスティックな家族劇。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渡世人列伝", "渡世人列伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "鶴田浩二に高倉健、そして藤純子の顔合わせで描く任侠映画。大正末期。浅草三社一家の親分・三田政之助が、闇討ちされた。犯人は背中に大蛇の刺青を背負った渡世人。刑務所から出所したばかりの三社一家の代貸・稲垣長吉は、親分の仇を求めて旅に出るが……。小沢茂弘のツボを押さえた演出が光る。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Never Sang for My Father", "父の肖像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "自分を受け入れようとしない年老いた父親と暮らす息子は、父の拒絶に苦しみながらも何とか愛そうとする。親子の心理を細かに描いた感動の人間ドラマ。「フレンチ・コネクション」などでタフな役柄が得意のG・ハックマンがM・ダグラスと演技を競ったシリアス・ドラマの傑作。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("bd ビーディー Snowboard Rulerz", "bd ビーディー Snowboard Rulerz", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "中井孝治や國母和宏ら、日本を代表するスノーボーダーたちに完全密着したドキュメンタリー。2006年トリノオリンピックへの出場権をかけて熱戦が繰り広げられた世界選手権やワールドカップをはじめ、自身の映像を記録するために行なうフリーライディング・トリップ、さらに仲間たちとの生活などを、04年7月から06年4月までの500時間以上にわたって記録。彼らのスノーボーダーとしての生きざまを、ありのままに映しだしていく。", "2006-09-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("T-REX", "T-レックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "恐竜と人間が共存する未来社会。史上初の恐竜刑事テディとベテラン女刑事ケイティはある日、恐竜とクーロン人間が殺された事件の担当になり、コンビを組まされる。恐竜嫌いのケイティはテディと反発し合いながら友情を育んでいき、やがて犯人はケイティの生みの親で人類滅亡をたくらむエリザー博士であることを突き止める。恐竜と女刑事のコンビが難事件に立ち向かうコメディ映画。1996年日本公開時には日本語吹き替え版のみが上映された。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Devil and Daniel Johnston", "悪魔とダニエル・ジョンストン", "", "", "http://image.eiga.k-img.com/images/movie/1351/315x210.jpg?3848", 110, "カート・コバーン、ジョニー・デップ、ソニック・ユースら多くのアーティスト達が敬愛し続けてきたテキサスの伝説的シンガー・ソングライター、ダニエル・ジョンストンにスポットを当てたドキュメンタリー。高い評価を受けながら、その繊細さ故に精神を病んで苦しみ続けている孤高の天才ダニエル。そんな彼の波乱に満ちた半生を、自らの手によるホームムービーや録音テープ、家族や関係者へのインタビューなどから振り返る。", "2006-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SALZBURG CONNECTION", "ザルツブルグ・コネクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "第二次大戦が終わる頃。敗れたナチの残党は、将来、再び立ち上がる時が来ることを期待して、秘密書類をオーストリア、アルプス山中の湖底に隠す。それを狙ってイギリス、アメリカ、ソ連、中国、そしてオーストリアの５ヵ国のスパイが虚々実々の駆け引きを展開する。息詰まる展開のスパイ・アクション。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARMED ＆ DANGEROUS", "私立ガードマン —全員無責任—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "自分のドジから警官を懲戒免職になり、ガードマンに転職したフランクと、彼の相棒、元弁護士のノーマン。“落ちこぼれ”てガードマンになった二人が人一倍の正義感で強盗事件の解決に挑むが……。個性派J・キャンディが本領発揮の巻き込まれ型コメディの秀作。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・社長洋行記", "続・社長洋行記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "貼り薬“サクランパス”の売り込みに香港へ渡った桜堂製薬の東海林営業部長と南秘書課長は、奮闘するも仕事は進まない。業を煮やした本田社長もやって来るが、香港亭のマダム・悦子のお色気にフラフラ。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("国定忠治", "国定忠治", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "東映時代劇の大御所として活躍した松田定次の中期、大映時代の作品。天保８年、雨がまったく降らず凶作に悩む上州・国定村を舞台に、農民のためになんとか力になろうとする忠治の姿を描いていく。江戸に出た忠治は助成金を獲得し、祝宴に出席する。そこで彼は、代官から押しかけ女房で芸者のお町を所望され……。DVDは「阪東妻三郎傑作選　DVD-BOX」に収録。", "1946-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELVIRA ： MISTRESS OF THE DARK", "エルヴァイラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "アメリカのB級ホラー番組のホステス役、エルヴァイラ（C・ピーターソン）を主人公にした破天荒なホラー・コメディ。超グラマーな美女エルヴァイラが伯母の遺産として田舎の屋敷と奇妙な魔術書を受け継ぐ。彼女を魔女扱いする屋敷の住人たちに、エルヴァイラはその書物を使って反撃を開始することにした。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘密の花園", "秘密の花園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「完全なる飼育」シリーズに代表される、監禁ものの作品。理想の女性に育てるため、父は娘を監禁し、飼育を続けるが……。「呪霊 THE MOVIE」の松田純が妖艶な姿を惜しげもなく披露。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH WARD", "デス・ウォード 地獄の牙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ジェイソン、フレディ、ブギーマンなど殺人鬼ヒーローのブームのなか、作り手も次々と新しいキャラクターを考案する。なかには笑いを誘う殺人鬼も登場するが、この映画は脳手術に失敗の末、発狂して安楽死させられたはずのマントヒヒ“シャクマ”が目を覚まし、殺りくを繰り返すという荒業の設定。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪名幟", "悪名幟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "大阪へ舞い戻った朝吉は“悪名”の足を洗い清次とも別離。しかし女社長をだまして町工場を乗っ取ろうと画策する悪徳ヤクザに彼の“悪名”の血が騒ぐ。結局、そこに駈けつけた清次とともに、悪玉一味を打ちのめすのだった。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏子の冒険", "夏子の冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "文芸映画の名手・中村登が三島文学に挑んだ作品。といっても、内容は軽いコメディ・タッチの恋愛騒動劇。世の中にあきあきした夏子は函館の修道院へ向かう。彼女は船の上で恋人の仇を討つために、熊退治に行く青年と出会う。そして、青年の中に自分の探し求めていたものを発見するのだった。木下惠介の「カルメン故郷に帰る」に次ぐ、松竹カラーの第２回作品。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Spy Game", "スパイ・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/1181/315x210.jpg?3076", 128, "今やハリウッドのトップ・スターであるブラッド・ピットが、出世作「リバー・ランズ・スルー・イット」のメガホンを取っていたロバート・レッドフォードと初共演。2人 の役どころは、CIAのベテラン・スパイと、かつての愛弟子。監督は「トップガン」「トゥルー・ロマンス」のトニー・スコット。北京での非合法工作に失敗し、拘束されてしまった愛弟子を救出するために、引退間際 のスパイがそのネットワークと知力を駆使して奔走する。", "2001-12-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Private Hell 36", "地獄の掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "　30万ドルもの現金強奪事件を追うニューヨークの刑事ブルナーとその相棒ファーナム。やがてふたりは歌手のリリが盗まれた札をチップで受け取っていたことを掴む。リリに近づいたブルナーは彼女に惹かれていく。刑事たちはリリに協力を求め、競馬場で張り込みを開始するが……。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛してよ", "愛してよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "シングルマザーである母親と、10歳の息子の心のすれ違いを、ユーモラスかつリアルに描いた人間ドラマ。父親不在の寂しさをまぎらわす少年と、誰かに愛してほしい母。そんな彼らの思いが時に行き違い、時にぶつかりながら物語は展開する。空虚感を抱え、欠けているものを埋めようとする彼らの切実な思いが胸を締め付け、母子を結ぶ強い絆でしみじみとさせる、人間味にあふれた語り口が魅力。「ナビィの恋」「OUT」の西田尚美による母親役の好演はもちろん、子役の塩顕治の素朴なまなざしも印象深い。シングルマザーの美由紀は10歳の息子ケイジをキッズ・モデルにしようと悪戦苦闘。ケイジはそんな母を冷めた目で見つめつつオーディションに通う。そんなある日、美由紀は恋人をケイジに紹介するが……。", "2005-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　ふるあめりかに袖はぬらさじ", "シネマ歌舞伎　ふるあめりかに袖はぬらさじ", "", "", "http://image.eiga.k-img.com/images/movie/53721/315x210.jpg?4612", 164, "歌舞伎の名舞台をスクリーン上映する「シネマ歌舞伎」シリーズの1作として、2007年12月に歌舞伎座で上演された有吉佐和子原作、坂東玉三郎主演の名作「ふるあめりかに袖はぬらさじ」を映像化。幕末の横浜。芸者のお園は、顔見知りの遊女・亀遊が通辞の藤吉と恋仲にあることを知る。やがて亀遊は藤吉との恋がかなわぬことに絶望して自害するが、アメリカ人の身請けを拒んで死んだ「攘夷女郎」という噂が広まってしまい……。", "2008-05-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サラリーマン十戒", "サラリーマン十戒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "東宝が得意とするサラリーマンもの。“シャルマン化粧品本舗”に勤める新入社員・江口は上司から見込まれて、新製品の男性化粧品の宣伝を命じられた。江口はミスター・シャルマン・コンテストを行うが、商売仇が一足先に若手スターを使って派手な宣伝を始める。江口の努力奮闘ぶりと恋愛を描いたコメディ。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フィッシュストーリー", "フィッシュストーリー", "", "", "http://image.eiga.k-img.com/images/movie/53771/315x210.jpg?3009", 112, "1975年、早すぎたパンクバンド「逆鱗」は世間に理解されぬまま、最後に「FISH STORY」という曲を残して解散した。1982年、2009年と時は流れて2012年、彗星激突の危機に見舞われた地球を救った陰には、「FISH STORY」の存在があった……。伊坂幸太郎の同名小説を、「アヒルと鴨のコインロッカー」に続き中村義洋監督が映画化。「逆鱗」ベーシスト役で伊藤淳史が映画初主演のほか、高良健吾、多部未華子、森山未來、濱田岳、大森南朋らが出演。", "2009-03-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The War Lord", "大将軍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "ブロードウェイでヒットしたレスリー・スティーブンスの歴史劇を、フランクリン・J・シャフナー監督が映画化。11世紀のノルウェー。ノルマンディ海岸一帯を領地とするウィリアム公の命を受け、辺境に侵入するフリースランド人を撃退するために旅立ったクリセゴン将軍。彼は村を襲撃していたフリースランド人を相手に見事な勝利を収めるが、村長の息子の婚約者であるブロンウィンと恋に落ちてしまい、そのことが大きな波乱を呼ぶ。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アモーレの鐘", "アモーレの鐘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アルプスを望む信州の美が原高原を舞台に、高原のみやげ物店で働く山育ちの少年が、弟を探しにきた都会の美女に寄せる淡い恋心を描いた叙情豊かなメルヘン。化粧品のCMガールとして人気の出ていた城戸真亜子をヒロインとして起用した。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　VOLEURS", "夜の子供たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "「私の好きな季節」の監督、主演トリオによるミステリアスな群像劇。泥棒一家に生まれた刑事アレックスは、万引きで捕まえたジュリエットという若い娘と、お互いを蔑視しながらも愛のない肉体関係を続けている。同時にジュリエットは、マリーという成熟した哲学教師の女性と愛し合っていた。そんな折、アレックスの兄である車泥棒イバンが死亡し、彼らの日常に波紋が起こる……。屈折した愛憎関係で結ばれた人々が織り成す多彩な人間模様を、時間の入り組んだ章立ての物語構成で描写。まず最初に登場人物を設定し、そこからドラマが展開していくという手法がとられており、人間の凶暴な感情が生々しく浮き彫りにされている。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beauty and Beast", "美女と野獣　ディズニーデジタル3D", "", "", "http://ecx.images-amazon.com/images/I/512QiUZN-pL._SL210_.jpg", 84, "アニメーション映画として初めてアカデミー作品賞にノミネートされた1991年の名作をデジタル3D化。ごう慢さゆえに、魔法で世にも恐ろしい野獣の姿に変えられてしまった王子は、魔法のバラが散る前に誰かを心から愛し、愛されなければ元の姿に戻ることができない。心を閉ざし、絶望の日々を送る野獣はある日、町の発明家の娘で心の美しい少女ベルと出会い、次第にひかれていくが……。", "2010-10-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Henri Cartier-Bresson - Biographie eines Blicks", "アンリ・カルティエ＝ブレッソン ／瞬間の記憶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "歴史的な瞬間から市井の人々のひとコマまで、素晴らしい作品群で知られる名写真家、アンリ・カルティエ＝ブレッソンの世界を、本人や関係者のコメントで綴ったドキュメンタリー。写真の魅力と楽しさに、思わずカメラを手にしたくなる。2004年にこの世を去った20世紀を代表する写真家カルティエ＝ブレッソン。亡くなる2年前の93歳の時、表舞台に出ることを避け続けていた彼が、自らの写真術と人生について語り明かす。", "2006-05-20", "26,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Indiana Jones and the Kingdom of the Crystal Skull", "インディ・ジョーンズ　クリスタル・スカルの王国", "", "", "http://image.eiga.k-img.com/images/movie/1891/315x210.jpg?1747", 122, "89年のシリーズ第3作「インディ・ジョーンズ／最後の聖戦」以来19年ぶりの続編となる今回は、米ソ冷戦下の1957年を舞台に、南米アマゾンの山奥に眠る秘宝クリスタル・スカルを巡って、考古学者インディ（ハリソン・フォード）とソ連の精鋭部隊が争奪戦を繰り広げる。製作総指揮ジョージ・ルーカス、製作フランク・マーシャル、監督スピルバーグ、音楽ジョン・ウィリアムズはシリーズ前3作と同じ。脚本は「宇宙戦争」のデビッド・コープ。", "2008-06-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Drifting Away", "ただよう想い。", "", "", "http://image.eiga.k-img.com/images/movie/56771/315x210.jpg?2098", 90, "売れないミュージカル俳優のチャニョンは9歳年上の初恋の女性ミソンと結婚し、すでに7歳の娘もいた。ある日、ミュージカルの共演者タンビが舞台裏でチャニョンの手を握ってきたことから、2人の間に愛情が芽生えていく。人生2度目の恋に落ちたチャニョンと、自分の気持ちに真っ直ぐなタンビ、そして夫の浮気を知ってか知らずか、ただ黙っているミソンの3人の関係は、公演が終わりに近づくにつれ変化していく。2011年「真！韓国映画祭」にて上映。", "2011-05-28", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Oscar and the Lady in Pink", "100歳の少年と12通の手紙", "", "", "http://image.eiga.k-img.com/images/movie/55601/315x210.jpg?4434", 105, "フランスを代表する劇作家エリック＝エマニュエル・シュミットが原作・監督・脚本を手掛けた感動作。はれものに触るような態度の大人たちに幻滅していた余命わずかな10歳の少年オスカーにとって、口の悪い宅配ピザ屋の女主人ローズだけが、自分に正直に接してくれる唯一の大人だった。オスカーの希望で12日間毎日、オスカーの元を訪れることになったローズは、1日を10年と考えて過ごすこと、そして毎日神様に手紙を書くことを教える。", "2010-11-06", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オサムの朝〈あした〉", "オサムの朝〈あした〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "会社一筋に生きて来た修はある日、子会社への出向を命じられる。家族の冷たい反応に戸惑う彼は、子供のころ過ごした那須高原へと向かうのだった……。森詠の同名小説を、ベテラン、梶間俊一が映画化。人生に疲れた中年男が回想する少年時代の思い出を通して、自然と人間の関係、人間同士をつなげる温もりを描いていく。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("或る旅人の日記", "或る旅人の日記", "", "", "http://ecx.images-amazon.com/images/I/218ES9WXYSL._SL210_.jpg", 23, "映像作家の加藤久仁生と制作会社ROBOTが手がけ、WEBで配信された短編アニメーション。架空の国をめぐるひとりの旅人が不思議な出来事や人々に遭遇していく姿を幻想的に描く。DVDには、WEBで公開された6編に新作1編も加えられている。加藤久仁生は本作の後に手がけた「つみきのいえ」（2008）でアヌシー国際アニメーション賞や米アカデミー短編アニメーション賞といった世界の名だたる賞を受賞する。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le nain rouge", "リュシアン　赤い小人", "", "", "http://image.eiga.k-img.com/images/movie/1821/315x210.jpg?3896", 102, "法律事務所で働く小人リュシアン。愛など無縁の人生を送ってきた彼は、離婚訴訟が専門で、他人の仲を裂くことに情熱を燃やしていた。しかしある日、依頼人である伯爵夫人の家に呼ばれた彼は、夫人が自分を誘惑していることに気付き、その日初めて愛を知る。彼女の愛人となったリュシアンの生活は派手になり、態度も横柄になって事務所から解雇される。やがて夫人は夫と寄りを戻し、リュシアンとの関係は破局を迎える。", "2000-09-02", "37,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鶏同鴨講／CHICKEN AND DUCK TALK", "ホンコン・フライド・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "店は汚いが、味は一品のローストダックで有名なレストランの店主、ホイ。ところが店の真向かいに、アメリカナイズされたファーストフード、フライドチキンの店が開店。するとお客はみんな移ってしまった。ホイはお客を呼び戻そうと大作戦を展開する……。“Mr.BOO！”でおなじみM・ホイによる軽妙なコメディ。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スーダラ節 わかっちゃいるけどやめられねえ", "スーダラ節 わかっちゃいるけどやめられねえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "青島幸男作詞の同名のヒット曲を題材にしてはいるものの、それを歌ったクレージー・キャッツはハナ肇以外出演していない。新入社員・川口浩と川崎敬三が社長令嬢の獲得を競うラブ・コメディ。実は令嬢にはアメリカに婚約者がいて、二人の夢は破れ、落胆するが、ハッピーエンドが用意されている。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'Ecole Des Facteurs", "郵便配達の学校", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 15, "ジャック・タチの監督デビュー作となる短編コメディ。自転車の乗り方から郵便の渡し方まで、郵便配達のノウハウを教える学校で訓練を受けたフランソワ。彼は航空便の出発時刻に間に合わせようと大急ぎで配達を行う。主人公のフランソワというキャラクターや郵便配達のギャグなど、長編「のんき大将脱線の巻」の原型となった作品。当初はルネ・クレマンが撮る予定だったが、他作品の準備のためにタチに監督を譲ることとなった。", "1947-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STATE FAIR", "ステート・フェア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "日本で戦後初めて公開されたテクニカラー・ミュージカル。ロジャース、ハマースタインの名コンビが音楽を担当した。年に一度のステート・フェアに沸き立つインディアナ州の農家の人々の哀歓を描く人間讃歌である。主題歌『春のように』が、アカデミ賞を受賞している。DVDは「ロジャース＆ハマースタイン コレクション」に収録。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCANDAL", "スキャンダル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "キャバレーのショーガールをやっていたクリスティーンが、社交界・政界の名士と知り合い、ソ連のスパイや、陸相に就任したばかりのプロヒューモと関係を持つ。彼女の心はプロヒューモに傾いていたのだが、醜聞を恐れたプロヒューモがわずかな金を彼女に渡したことで、二人の関係は絶たれてしまう。しかしこれが政界を揺るがすイギリス最大のスキャンダルに発展していく。1962年に起きた実際の事件をもとに、政治の影で消されていった男女の姿を描く話題作。主題歌をダスティ・スプリングフィールドが歌っている。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Traces of Love", "ノートに眠った願いごと", "", "", "http://image.eiga.k-img.com/images/movie/34301/315x210.jpg?1675", 108, "韓国で実際に起きたデパート崩壊事故をモチーフに、恋人を失った男の苦悩と再生を描いた感動作。デパート崩壊事故で婚約者のミンジュを亡くしたヒョヌは、彼女をデパートへ行かせたことに責任を感じ後悔の念に苛まれていた。それから10年後、ミンジュの死から未だ立ち直れずにいたヒョヌの元に、事故現場から奇跡的に発見されたというミンジュのノートが届く。そこには、ミンジュが計画した新婚旅行の内容が綴られていた……。", "2007-11-03", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MALAGA", "マラガ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "北アフリカのタンジール。暗躍する国際密輸団の正体を突き止めるため、OSSの婦人探偵ジョアンナはこの地に乗り込む。しかしすぐさま身近な所で殺人事件が発生。やがて彼女自身も正体不明の船乗りに付きまとわれ……。ヒロインのM・オハラをはじめ、ハリウッド映画人たちがイギリスに招かれて作った密輸活劇。", "1954-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KILLING TIME", "ダブル・トリック 謎のアリバイを追え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "完全犯罪計画の裏に二つの殺意が……。意外な結末へ向かうサスペンス・アクション。舞台はのどかなあるリゾート地。副保安官は、町の実力者と結婚した昔の恋人に夫の横暴ぶりを聞かされ、完全犯罪を計画。その罪を、新しく町へやって来た副保安官の男に被せようとするが、この男にも秘密があった。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　MORT　DU　CYGNE", "白鳥の死", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ポール・モーランの同名小説の映画化。実際のバレリーナの共演で、舞台シーンも盛り上がる。ロシアのバレエ・ダンサー、カリーヌはパリのオペラ座で主演することに。しかし、そのためにスターの座を落とされたボーブレに同情したバレエ学校の少女ローズは、カリーヌに怪我を追わせて二度と舞台に立てなくするが、カリーヌはそれと知らずにローズの才能を見抜き、教師となって彼女を訓練する。やがて己の罪の意識を感じたローズは……。", "1937-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBOCOP2", "ロボコップ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "治安が悪化する一方の犯罪都市デトロイト。市民の生命を守るため半分人間半分マシーンのロボコップ、マーフィの孤軍奮闘が続いていたが、オムニ社はマーフィを抹殺すべくロボコップ２号を開発し、マーフィ破壊の指令を下した！　大ヒットした前作同様にバイオレンス満載のアクション巨編。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SUN ALSO RISES", "陽はまた昇る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "E・ヘミングウェイの初期の傑作といわれる同名小説の映画化。第一次大戦後アメリカの新聞記者ジェイクは、パリで各国から流れこんできた“失われた世代”（戦争によって傷ついた世代）の仲間たちと友人になる。ジェイクにはブレットという愛人がいたが、戦争で負傷、不能者となった彼は苦悩を続ける。一見奔放で退廃的な女性ブレットは、スコットランドの貴族マイクや、ジェイクの親友で作家のロバート、ミピポラス伯、闘牛士ロメロなどとたわむれるが、心の奥ではジェイクを愛していた。パリやパンプローナのフィエスタを背景に、豪華なキャストで描いた超大作。街娼ジョルジエット役のJ・グレコも印象的。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボールZ", "ドラゴンボールZ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 41, "TVシリーズが『ドラゴンボール』から『ドラゴンボールZ』へ切り換わったのに合わせ、劇場版も『Z』へシフトチェンジしたシリーズ第4弾。あの名作『孫悟空』をヒントに、絶妙のキャラクターが造形された冒険活劇アニメの傑作だ。ロボットものが多かったそれまでの東映アニメのイメージを一新した、といっても過言ではないだろう。若き父親へと成長し、伝説の戦士・超（スーパー）サイヤ人として目覚めた悟空と仲間たちが大暴れする。DVDは『DRAGON BOX THE MOVIES』に収録。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("集団殺人クラブ Returns", "集団殺人クラブ Returns", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "騒々しい町にうんざりし、海にやってきた女子高生の美穂子、清恵、静香、綾の四人。地元のイケメン、剛と知り合い４人は大いに盛り上がる。だがそれもつかの間、不死身のゾンビ、タケゾウに彼らは追い回されることになる。轢いても、焼いても、撃っても効き目のない最強ゾンビ。４人はタケゾウを倒すことができるのだろうか？ ハイ・テンポな青春ホラー・アクション。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裸の町", "裸の町", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "狡猾な高利貸しに騙されレコード店を取り上げられた夫婦が、失意ののちに夜店のレコード屋で再起をはかる。力強い夫婦の物語。八木保太郎の創意に溢れたシナリオ、内田吐夢のスキのない演出、高利貸しを演じた小杉勇の名演とが相まって、戦前の文芸映画の秀作の一つとなった。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rampage", "ランページ／裁かれた狂気", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　検事トニーは、死刑反対の立場をとりながらも、上司命令で猟奇殺人事件の犯人リースを死刑にもち込むように務める。一方、リースの弁護士と精神科医は精神異常による無罪を主張。そんな中、護送中のリースが逃走し、またも殺人を犯す。リースは再び逮捕され、裁判が続けられるが……。殺人事件の裁判を通し、死刑制度の是非を問いかける法廷サスペンス。検事、犯人、弁護士、被害者の家族といった者たちの姿が克明に描かれ、リアリティを高めている。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い手裏剣", "赤い手裏剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ハードボイルドの雄・大薮春彦の珍しい時代小説をニヒルな剣豪役を得意とする市川雷蔵で映画化。３組のヤクザが争う宿場町が一人の浪人の出現で修羅場になる、というハメットの「血の収穫」を思わせる設定だ。手裏剣やブーメランが飛び交い、毒婦が暗躍する痛快ハードボイルド時代劇。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WINTER PEOPLE", "ウィンターピープル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "厳寒のノース・カロライナを舞台に、厳しい土地に生きる大家族の愛と規律を描く。山賊に車を奪われた時計職人ウェイランドは、娘とともに凍え死にそうなところを、土地の娘コリーに助けられる。コリーには、父親のわからない赤ん坊がいたが、二人は次第に惹かれあっていた。そんな時、突然現れた赤ん坊の父親コール。彼はウェイランドの車を奪った山賊キャンベル一家の末息子で、キャンベル家とコリーの家は対立していた。やがてコールが死んで、コリーは単身キャンベル家に乗り込んでいく。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRAFFITI　BRIDGE", "グラフィティ・ブリッジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "発表したアルバムとコンセプトをシンクロさせていくスタイルで放ったプリンス主演の音楽映画。一人四役をこなす殿下の独壇場。「パープル・レイン」「アンダー・ザ・チェリー・ムーン」に続く本作も同名アルバムを基調に据えた“天才”ならではの映像世界が展開する。主人公はクラブの共同経営者とことごとく対立し、その争いはいつしかバンド対決へと発展していった。そんな時、主人公は美女と知り合う。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRACK29", "トラック29～天国列車で行こう～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "カメラマン出身のN・ローグが「地球に落ちてきた男」「ジェラシー」に続いて発表した作品。アメリカのノース・カロライナに住む子供のない一見平凡な中年夫婦。夫ヘンリーは老人病院に勤める医師だが、ミニチュア鉄道に夢中で、妻のリンダのことなど眼中にない。そんな結婚生活に倦怠感を抱くリンダの前に、ある日、イギリスから来た奇妙な青年マーティンが現れる。彼は幼いころ生き別れた母親を捜していると言い、リンダはそんなマーティンを10代のころに強姦されて産んだ自分の子供と錯覚し始める……。現実と幻想を錯綜させつつ描く一種のセラピー映画。D・ポッターの脚本が秀逸。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SECRET OF THE INCA’S EMPIRE", "インカ帝国の謎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "南米コロンビアの奥地に存在したとされるインカ帝国。現代科学をもってしても解明できないほどの高度な文明社会の遺跡を残したインカ人たちは、空からやって来た人々だという伝承があった。考古学者リベラは“インカ帝国宇宙人説”を立証し、秘宝のありかを探ろうと、アンデスの奥地へと旅立った。しかしその財宝を狙ったギャング団も彼のあとを追い秘境へと向かっていく……。鋭い牙で襲いかかる大ワニや、飛びかかるピラニアの群れ、血に飢えた首狩り族などが続々と登場し、恐怖感をそそる。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いつか どこかで", "いつか どこかで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "リゾート開発会社に勤めるまもるは、ライバル会社のキャリア・ウーマン冬子に恋をし、仕事と恋愛の間で揺れ動く。平凡なストーリーだが、TVのトレンディー・ドラマの主題歌『ラブ・ストーリーは突然に』などが大ヒットした小田和正が脚本、監督まで手掛けたこの作品、映像と音楽が溶けあった心地よい世界である。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("六本木フェイク・傷だらけの天使たち", "六本木フェイク・傷だらけの天使たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "29歳の綾香と19歳のエチカの二人は、定職を持たず、雑居ビルの一室で暮らしている。ある日、探偵事務所から仕事の依頼が舞い込む。消息不明の女性の捜索を引き受けることになった彼女たちだったが、やがて思わぬ事件に巻き込まれていく。東京・六本木の街を舞台に、くされ縁的な友情で結ばれたヒロインたちの日常と、事件のてん末をストリート感覚あふれる映像とともに描いたドラマ。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents うそ　「FALLING」", "桃まつり presents うそ　「FALLING」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 26, "", "2010-03-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DADDY’S DYIN’ WHO’S GOT THE WILL", "家族狂想曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "アメリカ・テキサス州のある旧家を舞台に繰り広げられる、コミカルな遺産相続物語。家族という人間関係の力学、家族というものがいかに平然と論理を無視しうるかをハートウォーミングに描く。父親の病状悪化の知らせを受けた姉弟４人が、久しぶりに顔をそろえる。だが彼らは父親そっちのけで遺書のありかを探し始め……。希望通りのキャストを得たフィスク監督が、オーソドックスな演出でヒューマン・ドラマを完成させた。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Newsies", "ニュージーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "1899年、ニューヨークの新聞配達少年たちが悪辣な新聞社を相手に戦った実話を、ウォルト・ディズニー・ピクチャーズがミュージカルにした作品。“ニュージーズ”と呼ばれる新聞配達の少年たち。彼らは孤児だったり、家出少年だったりで、リーダーはいなかった。ある時、少年たちの買い取る新聞の卸値が上がることになり、少年たちはストライキに突入。やがて、人種や縄張り争いで分裂しいた彼らは団結していく。作曲を担当しているのは、「美女と野獣」でアカデミー最優秀オリジナル作曲賞を受賞したアラン・メンケン。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last Song", "ラスト・ソング", "", "", "http://image.eiga.k-img.com/images/movie/55221/315x210.jpg?4756", 107, "「きみに読む物語」「メッセージ・イン・ア・ボトル」で知られるニコラス・スパークス原作のベストセラーをマイリー・サイラス主演で映画化した青春ドラマ。ニューヨークで暮らす女子学生ロニーは、夏の休暇を利用して母親や弟たちとともに別居して疎遠になった父親スティーブの住む米南部の浜辺の街にやってくる。スティーブはなんとかロニーとの関係を取り戻そうとするが……。監督は「グレイズ・アナトミー」などのTVシリーズを手掛け、本作で長編映画デビューを飾ったジュリー・アン・ロビンソン。", "2010-06-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AYA　MERI　BEKHUDI", "ボンベイtoナゴヤ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "インドのカップルが名古屋でダンスを繰り広げる奇妙奇天烈マサラムービー。ボンベイの青年ビシャイは正義感の強い警察官。上官である父とともに日夜、密輸の摘発に励んでいた。警察で慈善ダンス公演のスピーチの最中、父は紛れ込んだギャングに襲われる。父を救出する際に、ビシャイは公演中のダンサー、ソナをも助け、それがもとで彼らは恋仲になるが、同時にギャングもビシャイを付け狙うことになる。ギャングが仕掛けた爆弾に両親を殺され、ビシャイは警官を辞職し、復讐を誓う。ギャングのボスが日本に渡ったのを知ったビシャイは一路、日本を目指す。彼がついた先は名古屋だった。なんと、そこには旅公演を回るソナの姿もあった。彼らは日本のヤクザにつかまり……。", "1997-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　BANDE　DU　DRUGSTORE", "好きと言えるまでの恋愛猶予", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "舞台は1960年代のパリ。映画界ではゴダールの出現など、次々に革命的な新風が巻き起こった時代。そんな時代のシャンゼリゼ通りにあったクラブ“ドラッグストア”に出入りする若者たちの不器用なラブ・ストーリーが展開。様々な時代背景をうまく取り入れながら、若者たちの純粋さゆえの未熟で不器用な恋愛の形が、普遍性を持って丹念に描かれている。シャルロットの誕生日パーティーに、親友のナタリーが幼なじみのフィリップを連れてきた。シャルロットとフィリップは、出会った瞬間に意識し合う。しかし、不器用な二人は、わざと無関心な態度を取り、なかなかうまくいかない。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　VICE　ET　LA　VERTU", "悪徳の栄え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "サドの『悪徳の栄え』『美徳の不幸』を、戦時下のフランスに置き換えた官能ドラマ。R・バディム監督が当時恋人だったデビュー間もないC・ドヌーヴを主演に抜擢し、彼女の艶めかしい魅力を引き出している。ナチスの将軍の愛人である魔性の悪女ジュリエットと、彼女の妹で信仰心が厚い薄幸の女ジュスティーヌ。対照的な道徳観の下に生きる彼女たち、罪作りな美姉妹の波乱に満ちた運命が、甘美な退廃的ムードの中に描かれていく。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使の恋", "天使の恋", "", "", "http://image.eiga.k-img.com/images/movie/54681/315x210.jpg?4008", 119, "中高生の間で人気を博し、書籍・コミック化もされた携帯小説の映画化。主演はモデルの佐々木希、谷原章介。容姿端麗でいつも取り巻きに囲まれている女子高生・理央は、過去のトラウマから人を信用することができずにいた。そんなある日、35歳の大学講師の光輝と運命的な恋に落ち、理央は初めて人を愛することを知る。互いに惹かれていく2人だったが、光輝には理央を受け入れられない理由があった。", "2009-11-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アノソラノアオ", "アノソラノアオ", "", "", "http://image.eiga.k-img.com/images/movie/57861/315x210.jpg?2309", 114, "洋食器・金属加工産業が盛んな新潟県燕市を舞台としたご当地ムービー。新潟県出身の俳優・三田村邦彦と息子の中山麻聖が親子初共演を果たした。2004年新潟・福島豪雨（7・13水害）で母親を亡くした陽介は、父親に反対されながらも映画の専門学校に通っていた。学校で知り合った凪音や地元の友人らと一緒にSF映画の撮影を始めた陽介だったが、監督の凪音が事故にあって他界してしまう。撮影は中止され、陽介は日々を無為に過ごしていたが、そんなある日、凪音の友人だという歩が現れ、撮影を再開しようと呼びかける。監督は燕市出身で音楽のPVなどを手がけてきたナシモトタオ。", "2012-03-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHADOW BULIDER", "シャドウビルダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "“ドラキュラ”を生んだ怪奇小説の大家B・ストーカーの短編を、現代に設定を置き替えて映像化したホラー。カルト教団の手によって、影の形をした邪悪な魔物“シャドウビルダー”が現代に復活。人類の終末を目指す、この悪魔は純潔な少年を儀式のいけにえにしようとしていた。ベトナム帰還兵上がりの神父バッシーは、世界を救うべく、シャドウビルダーに戦いを挑む。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フランケンシュタインの怪獣 サンダ対ガイラ 海外版", "フランケンシュタインの怪獣 サンダ対ガイラ 海外版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「怪獣王ゴジラ」とは異なり、もともと日本版と同時に海外用の撮影・編集を行なっているのでストーリーの破綻はない。ラス・タンブリンのナマの声や英語をしゃべる水野久美を楽しめるほか、同じシーンでも、BGMの異なるシーンも多数存在している。英語でのタイトルは“The War of the Gargantuas”。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブラボー！若大将", "ブラボー！若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "三矢物産の営業部に勤務する田沼雄一は、会社側の道義に欠けたやり方に不満を抱き、会社を辞めて熊井鉄工に入る。事業が進展しない三矢物産は、熊井鉄工と合弁会社を作り、雄一は社長に就任。加えて松井節子という恋人も獲得する。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Life in a Day", "LIFE IN A DAY 地球上のある一日の物語", "", "", "http://image.eiga.k-img.com/images/movie/56861/315x210.jpg?3935", 94, "YouTubeのユーザーが、2010年7月24日という特定の1日に各々の日常を撮影して投稿。集められた映像を「ラストキング・オブ・スコットランド」のケビン・マクドナルド監督が編集したドキュメンタリー。世界中を自転車で旅する北朝鮮出身の男性、思いを寄せる女性にふられたアメリカ人の青年、神に供え物するパリの女性など、世界中から選ばれた332組342人の動画が織り成す。リドリー＆トニー・スコット兄弟が製作総指揮を務める。", "2011-08-27", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.AND MRS.BRIDGE", "ミスター＆ミセス・ブリッジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "J・アイヴォリー組のスタッフとP・ニューマン夫妻という異色の顔合わせによる感動ドラマ。堅い保守的観念と、社会通念に縛られた弁護士とその一家。一見裕福で幸せな家庭に見えたが、それは見せかけ。父親の道徳という名の暴力のもとに、一家はそれぞれの幸せよりも世間体を気にする息の詰まるような生活を強いられていた。だがやがて、子供たちの独立、青春という反抗期がやってくる。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Superbad", "スーパーバッド　童貞ウォーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「40歳の童貞男」などのヒットメイカー、ジャド・アパトーが製作を手がけ、童貞喪失を夢みる冴えない高校生たちの奮闘を描いた青春コメディ。オタク高校生のセス、エバン、フォーゲルは、女の子に興味津々の童貞3人組。そんなある日、彼らは人生で初めて女の子からパーティに誘われるが、なりゆきで酒を持っていく約束をしてしまう。どうしてもパーティに参加したい3人は、フォーゲルが手に入れたニセIDで酒を買おうとするが……。", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イル・ベント・エ・レ・ローゼ　愛するということ", "イル・ベント・エ・レ・ローゼ　愛するということ", "", "", "http://image.eiga.k-img.com/images/movie/54501/315x210.jpg?1971", 73, "叶姉妹の姉・恭子のエッセイ「トリオリズム」を基に、イタリア人女性監督エリーザ・ボロニーニが映画化した官能映画。敬虔なクリスチャンの祖母の下で厳しく育てられた少女ジョルジャは、イタリアの田舎町で花を売りながらつましい暮らしを送っていた。そんな彼女が、漆黒の髪を持つゴージャスな美女KOKOとの出会いによって女性としての喜びを知り……。叶恭子がKOKO役で自ら出演し、濃厚なラブシーンを披露。", "2009-05-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カラコルム・ヒンズークシ　学術探検記録 カラコルム", "カラコルム・ヒンズークシ　学術探検記録 カラコルム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "“ゲノム分析法”の遺伝学者、木原均と、“今西進化論”で知られる人類学者、今西錦司も参加した、1955年の京都大学学術探検隊の記録映画。パキスタンとアフガニスタン各地で、植物・地質・人類学の３点から調査を続ける12人の姿を克明にカメラに収めたもの。資料的価値も高い、貴重な映像の連続。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEN AT WORK", "メン・アット・ワーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "E・エステヴェスが監督・主演し、弟のC・シーンと共演したコメディ。南カリフォルニアの海辺の町。巡回ゴミ収集人のコンビ、ジェームスとカールは、ある日ゴミの中から市議会議員の死体を発見する。この議員は、有毒廃棄物の違法処理に絡む陰謀で殺されたらしいのだが……。UB40によるレゲエ・ナンバーが全編を彩る。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無頼・非情", "無頼・非情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "藤田五郎は、ヤクザ三木本への義理から沢田を刺すが、三木本の非情なやり口を知り、賭場の金をさらって沢田の妻や子分とともに逃亡する。けれども、体力尽きて沢田の妻が倒れ、五郎は波止場で働きはじめるが……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEAMBOAT BILL JR.", "キートンの蒸気船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "20世紀初頭のアメリカ南部。オンボロ蒸気船の船長をしている父ビルを訪ねて、都会から一人息子のウィリーがやって来た。およそ20年ぶりの再会を喜ぶ親子。ところが最近、商売仇のキングが金にものをいわせて新しい船を周航、父のオンボロ蒸気船は廃船が決定。しかもイカレた都会人の息子が、キングの娘キティと恋に落ちたからさぁ大変。いがみ合う父親同士の狭間で二人の愛は実るのか？　クライマックスの“すさまじい台風”が語り草となっている、“キートン・プロ”最後の長編無声喜劇。風に飛ばされ頭で滑走、頭上に崩れ落ちる巨大な家壁を小窓ですり抜けるなど、喜劇王の面目躍如たる名場面がいっぱい。「キートンの船長」というタイトルで上映されたこともある。", "1928-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魯迅伝／LU　SHUN−MILITANT FIGHTER", "魯迅伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "中国近代文学を代表する文豪、魯迅の生誕百周年を記念して製作された記録映画。生まれ故郷・紹興での少年時代から、南京での学習生活、そして日本への留学を経て祖国で文学者として活動し、死に至るまでが、実写フィルムを織り交ぜながら描かれている。", "1980-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("C’EST　QUOI　LA　VIE？", "うつくしい人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "フランスの田舎町で牧畜業を営む一家。漠然と都会に憧れて無為な日々を送っていた息子ニコラは、借金を抱えた父親の突然の自殺をきっかけに、新たな人生を踏み出そうと決意する。南仏の美しい風景を収めた絵画的な映像が、観る者を陶酔させるヒューマン・ドラマ。大自然の息吹の中で寄り添うようにして生きる登場人物を見据えた、繊細な演出が秀逸。主人公と年上のシングルマザーの運命的な恋のエピソードも忘れがたい印象を残す。", "1999-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺とあいつの物語", "俺とあいつの物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "南原幸四郎は函館の郵便配達員。妻の純子と幸せな新婚生活を営んでいたが、純子がある日、牧場で働くと言いだしたため、別居生活が始まった……。朝間義隆監督、武田鉄矢主演の人情喜劇。青春ソングの名手、森田公一がサントラを担当した。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京少年", "東京少年", "", "", "http://image.eiga.k-img.com/images/movie/53291/315x210.jpg?2173", 95, "テレビドラマや映画で注目の若手女優・堀北真希が、男女ふたつの人格を併せ持つ少女という難役に挑戦したラブストーリー。幼い頃に両親を亡くした少女みなとの唯一の相談相手は、文通相手のナイト。同年齢の少年であることしか知らなかったが、彼にだけは本音を打ち明けることができるのだった。ある日みなとは、バイト先で出会った浪人生シュウと恋に落ちる。しかし初デートの最中に意識を失ってしまい、目覚めるとシュウの態度が一変していた。", "2008-02-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE MEN CAN’T JUMP", "ハード・プレイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "賭けストリート・バスケットで日銭を稼ぐ白人のビリーは、ある日、知り合った黒人のシドニーとコンビを組む。一獲千金を夢見る彼らは、大金のかかったゲームに挑んでいくがビリーの恋人は愛想をつかして姿を消す……。元プロ野球選手だったR・シェルトン監督が描くリアルで軽快なスポーツ・ハスラーの世界。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEANNE　ET　LE　GARCON　FORMIDABLE", "ジャンヌと素敵な男の子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "悲恋物語を歌と踊りに乗せてポップに展開させたミュージカル。「８人の女たち」のV・ルドワイヤンがヒロインに扮し、恋する少女を生き生きと演じている。自由奔放な少女ジャンヌは地下鉄で偶然出会った青年オリヴィエと恋に落ちる。ところが、オリヴィエはHIV感染者で、死を待つ身。ジャンヌは、そんな彼を励ましつつ真の愛に目覚めていく。", "1997-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和歌謡大全集", "昭和歌謡大全集", "", "", "http://image.eiga.k-img.com/images/movie/1651/315x210.jpg?1561", 112, "とある若者が通りすがりのオバサンを刺殺したことから、彼らが属する若者集団とオバサン集団の間で報復殺人合戦が展開、ついには自衛隊から武器を調達するなどエスカレートしていく。村上龍が94年に「週刊プレイボーイ」で連載した同名小説を「はつ恋」（00）「張り込み」（01）の篠原哲雄が映画化。脚本は02年の「木曜組曲」「命」でも篠原監督と組んだ大森寿美男。「恋の季節」「錆びたナイフ」などの昭和歌謡が全編を彩る", "2003-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Without a Clue", "迷探偵シャーロック・ホームズ／最後の冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "コナン・ドイルの小説でおなじみの名探偵シャーロック・ホームズは、実はドジで飲んべぇで女好きのただの阿呆者であり、助手のワトソンの言われるがままに行動する三流役者であったという奇想天外な発想によるコメディ。ホームズ役のケイン、ワトソン役のキングスレー、二大オスカー俳優が大怪演。", "1988-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トマトのしずく", "トマトのしずく", "", "", "http://image.eiga.k-img.com/images/movie/55501/315x210.jpg?2577", 91, "5年の交際を経て入籍したさくらと真は、2人で美容院を経営しながら、幸せな新婚生活を送っていた。しかし、さくらは中学生のときに母親を亡くして以来、父親の辰夫と疎遠な関係が続き、真は義父に一度も会ったことがなかった。ある日、辰夫が美容院を訪ねてくるが、さくらと気まずい雰囲気になってしまい、トマトのぎっしり詰まった紙袋を真に託しその場を去っていく……。主演に小西真奈美、共演に吉沢悠、石橋蓮司ら。監督は「ぼくのおばあちゃん」「誘拐ラプソディー」の榊英雄。", "2010-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEA KILLER", "少年と鮫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "フォルコ・クイリチ監督が、タヒチの美しい景観を生かして撮り上げた佳編「チコと鮫」の再映画化。少年とサメの友情に、島の黒真珠に目をつけた真珠養殖会社の謀略が絡む。大プロデューサー、ラウレンティスの娘の初製作で、文明批判の味つけも父親似(？)。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北京原人 Who are you？", "北京原人 Who are you？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「超能力者　未知への旅人」の監督、脚本コンビによるSFファンタジー。日本初の有人スペースシャトル内で、化石から採取したDNAをもとに北京原人を甦らせる実験が行われた。その結果、親子３人の原人が誕生し、科学者の竜彦と桃子は彼らとコミュニケーションをとることに成功。同時にシベリアで行われたマンモス復活計画も順調に進み、さらなる野心に燃える生物科学研究所理事長によって、原人たちは陸上競技会に出場させられる。世界記録続出で世間が騒然となる中、原人を取り返そうとする中国のマスコミがひそかに動き始めるが……。秀逸なSFXと中国ロケによって壮大な物語が展開される。特殊メイクで原人役に臨んだ本田博太郎と小松みゆきの熱演にも注目。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YEK　DASTAN−E　VAGHE’I", "トゥルー・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "イランの映画監督アボルファズル・ジャリリがドキュメンタリーとドラマの垣根を取り払って描いた人間ドラマ。“ドキュ・ドラマ”と称される独特のタッチで映画監督と少年の姿を追っていく。映画監督ジャリリは、新作に主演する少年を探していた。とあるパン屋でサマドという少年に出会ったジャリリだったが、サマド少年は幼い頃、足に重度のやけどを負い、放っておけば命にかかわる状態まで悪化していた。そのことを知ったジャリリは新作を撮ることを止め、サマド少年の足を治療しようと決意。その過程をフィルムに収めていくのだった。少年へのインタビューや治療風景といったシーンにドラマシーンが挿入されるという、ジャリリ監督の感性が光る傑作。", "1996-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少年たちは花火を横から見たかった", "少年たちは花火を横から見たかった", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "TVドラマ用に制作され、のちに劇場公開された「打ち上げ花火、下から見るか？　横から見るか？」発表から6年。出演者、スタッフなど関係者の証言や未公開シーン、ショートストーリー「檸檬哀歌」などを交え、「打ち上げ花火」誕生までの経緯を追ったセミドキュメンタリー。監督、企画は「Love Letter」「スワロウテイル」の岩井俊二。", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本列島", "日本列島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "帝銀、下山、三鷹、松川、スチュワーデス殺人事件など敗戦後占領下に相次いで起きた怪事件を軸に、熊井監督がCIAの謀略説を推理ドラマタッチを加味して暴き出したリアリズム大作。パリのシネマテークでは「白い影」という題で上映された。新聞記者と米軍通訳のコンビが、在日米軍に絡む殺人事件を追う。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jag ar med barn", "僕は子持ち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "スウェーデンの名匠ラッセ・ハルストレム監督の劇場映画第3作目で、彼のデビュー作「恋する男と彼の彼女」の続編ともいえる作品。広告代理店に勤める青年ボッセは、ある日レストランで出会ったレナと恋に落ち、一緒に暮らし始める。やがてレナは妊娠するが、彼は不安を感じながらも彼女を支え、ついに出産の日を迎える。新しい家族の誕生に幸せの絶頂にいるかのように見えた2人。ところが、ボッセはやがて子育てを重荷に感じ始め……。", "1979-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SO−RUN MOVIE／ソーラン・ムービー", "SO−RUN MOVIE／ソーラン・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 49, "インターネット配信やBSデジタル放送でオンエアされてきたショートフィルム専門の映像レーベル“SHORT STOP FILM”の佳作３本。スタッフ、キャストともに豪華な顔ぶれが集まり、ハイクオリティの短編が揃う。過去を振り返らずに生きてきた理恵。そんなある日、彼女はカーナビに話しかけられ、そのまま不倫した上司や、元恋人のもとに連れて行かれ……。（「音声案内に従って走行して下さい」）", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二代目若親分", "二代目若親分", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "市川雷蔵亡きあと、後継者として期待された松方弘樹が挑んだ「若親分」のリメイク。熱気ある演技で魅せる。海軍少尉の南条は、父である南条組組長の死後、いさぎよく軍服を脱ぎ、二代目となった。父を殺したのは死の商人と結託し、陸軍過激派をあおる武藤組だった。もちろん、落とし前はきっちりと……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピストルオペラ", "ピストルオペラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "80歳を目前にした鬼才・鈴木清順が年齢を感じさせない大胆な演出で「夢二」以来、10年ぶりに撮り上げたピカレスク・ロマン。殺し屋組織“ギルド”のナンバー３で女殺し屋の皆月が、ギルドの代理人・小夜子から仕事の依頼をされる。ところが、皆月は別の殺し屋から命を狙われるはめに。そんな折、彼女の元に小夜子から別の依頼が入る。ギルドの殺し屋ナンバーワンである“百眼”を殺せというのだ。だが、ほかのギルドの殺し屋たちが笑みを浮かべた顔で次々と殺されていき、次第に皆月も追いつめられていく。やがて、ターゲットである“百眼”から１通の招待状が彼女の元に届くが……。サイケデリックな衣装、原色を多用した照明など、鈴木清順のカラーが爆発。思わず呆然とするクライマックスまで目が離せない。数々の設定が幻のシナリオとなった「続・殺しの烙印」から引き継がれ、引用されている点にも注目したい。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUNGA VITA ALLA SIGNORA！", "偽りの晩餐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "世界中の名士たちが集まるホテルの晩餐会で、給士の実習として働く、ホテル学校の６人の生徒たちの心の揺れ動きと、現実の戸惑いを鋭く描写したE・オルミ監督の秀作。ほんのささいなエピソードの一つ一つが、実にユーモラスかつシニカルに描かれていく。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WINNERS TAKE ALL", "スーパークロス 勝利のデッド・ヒート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "モトクロス・レースの殿堂、スーパークロスに懸けた二人の若者の友情と愛とデッドヒートを描いた青春アクション映画の傑作。R・ジョンソン、D・スタンフィールド、D・ベイリーらが華麗なテクニックを披露している。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("免許がない！", "免許がない！", "", "", "http://ecx.images-amazon.com/images/I/21N9JD4G6VL._SL210_.jpg", 102, "コメディに挑戦した舘ひろし主演作。舘扮する南条弘は、映画スターだが自動車免許がないことにコンプレックスを持っている。南条は撮影中の映画を中断し、教習所の合宿免許取得コースに入るが……。セクシー教官、鬼教官のイジメに耐え、普通の中年男として免許を取ろうとする南条の悪戦苦闘ぶりを描く。", "1994-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRIVATE LESSONS", "プライベート・レッスン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "日本でもヒットしたアメリカ産の青春映画をリメイク。美しい家庭教師と日本人の高校生の淡い恋、その影に潜む事件をコミカルなタッチで描いていく。人気グループSMAPの稲垣吾郎をフィーチャーした、アイドル映画の向きも。モデル出身のJ・パクラが妖艶な魅力で迫る。", "1993-01-01", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("虎度門／STAGE　DOOR", "喝采の扉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "広東オペラの人気女優サムは、引退して家族と豪州への移住を考えているが、彼女の周囲は何かとトラブルが絶えない。娘の同性愛の発覚、夫の事業失敗など事件が相次ぐなか、新人女優ユクションの面倒を見ることになったサムは、彼女の恋人チュンがかつて自分が捨てた息子だと知る……。ストーリーだけを読むとウェットなメロドラマに思われるが、全体のタッチはむしろカラッと明るい喜劇調。“虎度門”とは舞台と現実の世界を隔てる境界線のことで、香港の実力派女優J・シャオが、広東オペラ・シーンでの艶やかな身のこなしと現代女性としての慌ただしい生活感を、見事なまでに演じ分けている。また、コミカルな役柄の多いA・ユンがユクション役をシリアスに演じており、撮影中、大先輩であるシャオの役者魂に深い感銘を受けたという。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RIDING THE EDGE", "ライディング・エッジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "灼熱のサハラ砂漠の太陽熱研究所。そこにある太陽熱衛星の軌道をコントロールするマイクロチップをテロリストが要求する。そのテロリストに、父親を人質にとられた研究所員のマットが立ち向かう。J・ファーゴによる、ポリティカル・アクション・アドベンチャー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋はいつもアマンドピンク", "恋はいつもアマンドピンク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "扱いにくい性格が災いして、27歳の今も処女を通しているやり手のキャリアウーマンが、父親の後妻としてやって来た“新人類母”によって思わぬ活路を見出していくというラブ・コメディ。『漫画アクション』に連載されたの同名人気コミックの映画化。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("家族X", "家族X", "", "", "http://image.eiga.k-img.com/images/movie/56451/315x210.jpg?4577", 90, "2008年ぴあフィルムフェスティバルで審査員特別賞を受賞した吉田光希監督が、第20回PFFスカラシップにより撮り上げた商業デビュー作。東京郊外の新興住宅地に暮らす橋本家の妻・路子は、ごく普通の幸せを手にしたと信じていた。しかし失業の危機にある夫とは会話もなくなり、就職浪人の息子ともすれ違っていく。家族と食卓を囲むことがなくなっても、孤独に毎日食事を作る路子は次第にバランスを失っていき……。", "2011-09-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA TRAVIATA", "トラヴィアータ —1985・椿姫—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "A・デュマ・フィスの原作を舞台演出家としても名高いゼフィレッリ監督がオペラ映画化した佳作。19世紀半ばのパリの社交界で“椿姫”とうたわれた高級娼婦ヴィオレッタと青年アルフレードとの悲恋物語が、華麗な映画美術、リリカルな音楽で語られる。", "1982-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名刀美女丸", "名刀美女丸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "戦争も末期にさしかかり、当時の厳しい検閲と自らの作風の間に悩む溝口が発表した芸道もので、溝口らしさが発揮された小品。幕末の世、恩師と仰ぐ小野田小左衛門が暴徒に襲われる。刀師、清音は師の仇を討つため、名刀を作ることに一心不乱となるが、彼には仇討ちは許されない……。", "1945-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鞍馬天狗 薩摩の密使", "鞍馬天狗 薩摩の密使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "幕府が横浜港にフランス製兵器を陸揚げするとの情報を得た鞍馬天狗は、真相を確かめるべく横浜に乗り込むが、別手組の魔手が襲いかかる……。太平洋戦争が勃発した年だけに、国策的な傾向がうかがえる一編。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MINA TANNENBAUM", "ミナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "1970年代のパリを舞台に、二人の少女が大人への階段を登っていく姿を描いた青春映画。同じ日に同じ病院で生まれたミナとエデルは、深夜のカフェで歌い、パーティーに連れ立って出かける親友同士。ミナは画家を夢見て、エデルはジャーナリストを目指している。それぞれ初恋の相手が現れるが、どちらも身も心も捧げるほど恋愛に熱中できない。自分自身の人生のヒロインになりたいと願う二人は、見栄を張りあい、罵りあい、別れ、そしてまた、一緒に歩むことを繰り返す。パンタロンやロンドンブーツなどを身にまとったボーランジェのファッションと当時のヒットナンバーが、1970年代のパリのムードを伝える。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A MAN FOR ALL SEASONS", "わが命つきるとも", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "16世紀初頭、ヘンリー８世の権力とクロムウェルの陰謀に、高潔な信念をもって抗し、ついには刑場の露と消えたイギリス史に残るトマス・モアの生涯を描いた秀作。ジンネマンの秀逸な演出力と古典的な映像美で見事な作品に仕上がった。", "1966-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crac!", "クラック！", "", "", "http://image.eiga.k-img.com/images/movie/56521/315x210.jpg?3951", 15, "人間と自然をテーマとした作品を手がけ、アカデミー短編アニメーション賞を2度受賞したカナダ在住のアニメーション作家フレデリック・バックの傑作短編4本をまとめ、ニュープリント、新字幕で公開する「フレデリック・バックの世界」にて上映。一脚のロングチェアがたどる運命を通じて、失われつつあるカナダ・ケベックの伝統的な生活や文化、家族愛などをユーモラスに描く。第54回米アカデミー賞で短編アニメーション賞を受賞した作品。", "2011-07-02", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クルテクの冒険　楽しいぼうけん編", "クルテクの冒険　楽しいぼうけん編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "1957年の誕生以来、チェコの国民的アニメとして愛され続けてきた「クルテク」シリーズ。日本では2002年の「クルテク　もぐらくんと森の仲間たち」に続く2度目の劇場公開となる本作では、もぐらのクルテクと仲間たちが大活躍するエピソード10話を、「たのしいぼうけん編」「かわいいぼうけん編」に分けて上映。「楽しいぼうけん編」では、ベルリン国際映画祭をはじめ数々の映画賞を受賞した「まちにいってしまったもぐらくんたち」など6話を収録。", "2005-06-18", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MIEL DEL DIABLO", "イノセント・ドール 虜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "最愛の恋人が事故で死亡、悲しみのあまり錯乱状態になった女は、医師の手術ミスが原因だと思い込み、様々ないやがらせをしたあげくその医師を誘拐、監禁してしまう。やがて女はサディズムの快楽にのめり込んでいく。ホラー映画の大家フルチが、怪奇で倒錯的なエロスの世界に初めて挑んだ作品。", "1988-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　RIVIE｀RES　POURPRES", "クリムゾン・リバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "フランス映画ながらセリフはすべて英語、製作費20億円の巨費を投じて作られた、ハリウッド色の強いアクション・スリラー。J・レノ、V・カッセルとフランスを代表する二大スターの顔合わせは新鮮味があり迫力満点。連続猟奇殺人事件と墓荒らしという別々の事件を追っていた二人の刑事が雪山で遭遇する。それぞれの事件が一つに結びついたとき、田舎町に隠された驚愕の秘密が彼らを襲う……。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEADLINE AUTO THEFT", "バニシングIN 60 デッドライン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "表向きは保険調査員、実は自動車泥棒という男たちが、見事な腕前で超高級車を盗みまくっていた。ところが一台の車が厄介を引き起こし、激しいカーチェイスが始まる。「バニシング IN 60”」「ジャンクマン」に続いて３本目の監督作になる、H・B・ハリッキーが製作・脚本・主演も務めている。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Leningrad Cowboys Meet Moses", "レニングラード・カウボーイズ、モーゼに会う", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "トンガリリーゼントにサングラスのロックンロール集団、レニングラード・カウボーイズを再び主演に迎え、アキ・カウリスマキが贈るシリーズ第2弾。メキシコでヒットしたものの数年後にはすっかり落ちぶれていた彼らの前に、失踪していたマネージャーが突如現れる。自分はモーゼに生まれ変わったと宣言する彼に導かれたレニングラード・カウボーイズは、ボートで大西洋を渡りヨーロッパを転々とした後、ついに故郷ツンドラへ辿り着く。", "1994-01-01", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("の・ようなもの", "の・ようなもの", "", "", "http://ecx.images-amazon.com/images/I/518GZ6N81PL._SL210_.jpg", 103, "修行中の二つ目落語家の若者は、23歳の誕生日に初めてソープランドへ行き、エリザベスと出会う。彼女はペンギンブックスを読むインテリ女だった。そんな時、落研の女子高生がやってきて、若者はそのなかの一人が気に入る。落語家とソープ嬢、女子高生の恋物語を軽やかに描いた森田芳光の劇場用映画デビュー作。", "1981-09-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("華鬼　三部作　響×桃子編", "華鬼　三部作　響×桃子編", "", "", "http://image.eiga.k-img.com/images/movie/55051/315x210.jpg?4301", 80, "「華鬼 三部作／麗二×もえぎ編」の続編。土佐塚桃子（加護亜衣）は鬼の花嫁となり、鬼たちが通う鬼ヶ里高校に通うことになるが、結ばれるはずの鬼にふられてしまい、学園中から好奇の目で見られるようになってしまう。「鬼の花嫁」の刻印を持つという理由で優遇される神無に嫉妬を抱く桃子は、神無の夫で「鬼の頭」の華鬼の失脚を目論む堀川響（村井良大）に出会う。", "2009-12-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲンと不動明王", "ゲンと不動明王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "三船敏郎が不動明王を怪演する児童映画。妹と離ればなれで暮らすいたずらっ子のゲンは、叱られて悲しくなるとお寺の不動明王と話をする。明王はいつも“弱虫！”とゲンを励ましてきた。時がたち、再び妹と一緒に暮らせることになってゲンは大喜び。けれども、新しい母親にはなじめなくて……。特撮は円谷英二が担当している。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドリフターズですよ！　特訓特訓また特訓", "ドリフターズですよ！　特訓特訓また特訓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "数あるドリフ映画のなかでも１、２を争うブラックなノリで押しまくる重喜劇。強盗の片棒を担がされ失敗、１年間のブタ箱行きとなったドリフの面々。出所後、５人はバラバラの道を歩むが、なぜか１週間後に再び全員集合。リーダーの長吉は４人を道連れに、飛び降り自殺を図ろうとするのだが……。後半、船中でヒドい生活を強いられる“部下”たちと“上官”＝長吉のすさまじい攻防が展開。“精神注入棒”なるものでシゴかれる４人の反撃が、観る者の涙を誘う。セットこそ壊れないものの、“全員集合!!”世代必見の１本だ。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緋ざくら小天狗", "緋ざくら小天狗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "美空ひばりが剣術好きのおてんば町娘を演じる時代劇。大阪天満の但馬屋の一人娘お俊は、行儀見習いのため、江戸の乳母のもとへ行くことになった。江戸に着いたお俊は、役者の市川米十郎のためにひと肌ぬぎ、いなくなった役者の変わりに舞台に立つのだった。多彩なスターが、脇でひばりを盛り立てている。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATTO DI SANGUE FRA DUE UOMINI PERCAUSA DI UNA VEDOVA.SI SOSPETTANOMOVENTI POLITICI", "愛の彷徨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1920年代のシシリー。夫をファシストに銃殺され、身篭っていた子供も流産してしまった女と、彼女に好意を寄せる社会主義者と遊び人の二人の男の哀しい三角関係。ウェルトミューラーがシシリーの見事なロケーションを生かして、S・ローレンの大地のような母性を高らかにうたいあげる。カバリエのアリアをバックに、S・ローレンが傷ついた二人の男を泣きながら抱擁するラスト・シーンは圧巻である。", "1978-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONTALVO ET L’ENFANT", "モンタルボと少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "大人の世界を垣間見た少年が、やがて習慣や愛、暴力という現実にほんろうされていく様をシュールに描いた、セリフのほとんどないイメージ・フィルム風の異色作品。フランスのモダン・ダンス界をリードする、J・C・ガロッタによるダンス・シーンが巧みに盛り込まれ、幻想的なムードをかもし出している。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("１リットルの涙", "１リットルの涙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "多くの人々の心をとらえた同名ベストセラーに基づく感動の物語。中学３年生で脊髄小脳変性症を発症し、僅か25年で人生の幕を下ろした実在の女性、木藤亜也の生涯が描かれる。徐々に病魔に冒されていく主人公を体現する新進女優、大西麻恵の好演が爽やかな感動を運ぶ。脊髄小脳変性症という病魔に襲われた、中学３年生の亜也。そんな境遇にめげることなく生きる彼女だが、病気の進行は止まらず……。", "2005-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Se Eu Fosse Você 2", "続・逆転夫婦!?", "", "", "http://image.eiga.k-img.com/images/movie/54771/315x210.jpg?3000", 98, "ブラジルで2005年に公開され大ヒットを記録したコメディ「逆転夫婦!?」の続編。お互いの体が入れ替わるという不思議な出来事を通し、壊れかけていた夫婦の絆を取り戻したクラウディオとエレーナ。ところが数年後、クラウディオが他の女性といるところをエレーナが目撃したことから2人は離婚の危機に陥り、激しく言い争っている最中にまたしても体が入れ替わってしまう。そんな中、18歳の娘ビアの妊娠が判明し……。", "2009-10-03", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARLENE", "マレーネ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "映画がトーキーとなった直後の1930年代に「嘆きの天使」「モロッコ」などで大当たりを取り、大スターとなったマレーネ・ディートリヒ。ジョゼフ・フォン・スタンバーグ監督に可愛がられ、「間諜X27」「上海特急」「西斑牙狂想曲」などで神話的世界を築き、第二次大戦中は自由の戦いを支え、『リリー・マルレーン』をヒットさせて、さらに意欲的に生きたディートリヒの全貌をインタビューによって、解き明かそうとするドキュメンタリー。しかし86歳のディートリヒは強い意志でカメラの前に出ることを拒否する。過去の出演作品のフィルムやスチールが映し出されるそのバックに、やりとりが流される。インタビューをするのは彼女の指名を受けてこの映画の監督も兼ねるM・シェルだが、ディートリヒにやりこめられて、たびたび立ち往生するところがだらしない。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fauteuils d'orchestre", "モンテーニュ通りのカフェ", "", "", "http://image.eiga.k-img.com/images/movie/53571/315x210.jpg?2586", 106, "パリのモンテーニュ通りに実在する“カフェ・ド・テアトル”に集った男女の人間模様を描いたヒューマン・ドラマ。店のギャルソンとして働くジェシカを中心に、人気女優や世界的ピアニストなど、成功を収めながらも人生に迷いを感じている彼らの姿を暖かいまなざしで見つめる。監督・脚本は「シェフと素顔と、おいしい時間」のダニエル・トンプソン。「スパニッシュ・アパートメント」のセシル・ド・フランスがジェシカを演じる。", "2008-04-26", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOSCA", "トスカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "プッチーニの有名なオペラ『トスカ』の映画化。19世紀の初め、国事犯をかくまった画家のカヴァラドッシは、警視総監スカルピアに逮捕され、拷問される。カバラドッシを愛するトスカは、スカルピアから彼女の体を代償にカヴァラドッシの助命の約束を取りつけるが、すきを見てスカルピアを殺す。翌朝、スカルピアの約束は守られず、カヴァラドッシは銃殺され、トスカは絶望して城壁から身を投げる。この作品は、本来、ジャン・ルノワールが撮るはずだったが、イタリアの参戦によってルノワールは帰国し、助手のコッホとルキノ・ヴィスコンティが協力して完成させた。メロドラマを装ったネオ・レアリスモのレジスタンス映画の先駆作。", "1940-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOOKING FOR LANGSTON", "ルッキング・フォー・ラングストン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "1920年代、アメリカに実在したゲイの黒人詩人ラングストン・ヒューズ。その生涯をモチーフにした、耽美な映像のイメージ・フィルム。コットン・クラブ風の懐古的酒場でタキシードに身を包み、優雅に踊るゲイのカップルたち。ラストシーンではゲイ差別の問題もちらつかせながら、スタイリッシュなドラマが展開していく。英映画界、期待の新鋭I・ジュリアンが32歳で手掛けた出世作。以前発売されたビデオには「レゾナンス」が併録されている。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BANGKOK　DANGEROUS", "レイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "香港出身の双子の兄弟オキサイド＆ダニー・パンの初監督作。バンコクの暗黒街を舞台に、孤独な殺し屋の悲しい運命を描いたノワールタッチの人間ドラマ。生まれつき耳が聞こえない殺し屋のコンは、熱を出して訪れた薬局で心優しい少女フォンと出会う。お互いに心惹かれ合うが、二人の幸せな日々は長くは続かなかった……。", "2000-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("残菊物語", "残菊物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "村松梢風の実録小説の映画化。溝口健二監督（1939・松竹）、島耕二監督（1956・大映）に次いで大庭秀雄が監督した。甘やかされた歌舞伎の御曹子が子守り女から芸の未熟を指摘されて目を覚ますという芸道もの。歌舞伎界の当時のホープ、市川猿之助が主人公を演じている。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幸福なる食卓", "幸福なる食卓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "冬の青森を舞台に、中年女性と青年の奇妙な関係を通して男女の欲望をリアルに描き出した異色ラブストーリー。食品工場の事務員として働く平凡な中年女性・節子は、愛する男性に手料理を食べてもらうことを生きがいとしていた。恋人に別れを告げられた彼女は、寂しさを紛らわそうとスーパーで万引きをする。しかし、その様子を警備員の青年・加藤に目撃されてしまい……。監督は本作が劇場デビュー作となるタテナイケンタ。", "2008-01-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cztery noce z Anna", "アンナと過ごした4日間", "", "", "http://image.eiga.k-img.com/images/movie/54701/315x210.jpg?4205", 94, "ベルリン映画祭金熊賞受賞作「出発」（67）や「早春」（70）、「ザ・シャウト／さまよえる幻響」（77）といった異色作で知られるポーランドの鬼才イエジー・スコリモフスキ、17年ぶりの監督作。ポーランドの地方都市を舞台に、若い看護婦アンナへの恋心を募らせる、うだつの上がらない中年男レオンの哀しくも滑稽な姿が描かれる。製作はマノエル・デ・オリべイラ作品を手掛けてきたパウロ・ブランコ。", "2009-10-17", "2,42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GLASS SHIELD", "グラス・シールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "警察内の腐敗に立ち向かう二人の警官を描いたポリス・サスペンス。ポリス・アカデミーを卒業したばかりの黒人警官J.J.は、配属された警察署でなぜか同僚から冷たい視線を浴びていた。彼の味方になってくれるのは署内唯一の女性警官デボラ一人。ある日、黒人が容疑者として挙げられた殺人事件でJ.J.は、同僚の信頼を得ようと事件の真相も知らず軽率にも偽証をしてしまう。しかし、警察の暗い陰謀に巻き込まれたことに気付いた彼は、デボラの力を借りて真実を暴こうと決意するが……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("酔いどれ無双剣", "酔いどれ無双剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "江戸は小石川に“悪鬼必殺道場”の看板をかかげ、町人から“豪快先生”と慕われる風変わりな町医者・逸見鉄之介。この鉄之介に対して、老中をかつきだし、江戸追放の策謀を練る御殿医吉岡去庵を中心とする町医者たち。鉄之介は計略にひっかかって浪人の一群に襲われ……。右太衛門主演の痛快時代劇。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間椅子", "人間椅子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "江戸川乱歩の名作小説を、宮路真緒・小沢真珠・板尾創路共演で映画化。新人編集者の真理は、美人作家・今野佳子の担当になる。佳子は師匠である大御所作家・大河内俊作が失踪したのをきっかけにデビューし人気を得たものの、ここ数年新作を書けずにいた。佳子の家に頻繁に出入りするようになった真理は、そこにある大きな黒革の椅子が気になって仕方がない。やがて彼女は、その椅子の中に大河内がいるのではないかと思い始め……。", "2007-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("187", "187〈ワン・エイト・セブン〉　—処刑予告", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "“187”とはカリフォルニア州の刑法における殺人罪の条項のことで、“殺す”という意味のスラング。アメリカの荒廃する教育現場の現状を背景に、心身に傷を負った教師の苦闘を、UKクラブ・サウンドにのせてスタイリッシュに活写する。ブルックリンの高校教師ガーフィールドが、落第させた生徒にアイスピックで刺される。生死の境をさまよった彼は、ロサンゼルスの高校に臨時教師として赴任するが、そこにもまた暴力が蔓延していた。やがて不良グループと激しく対立したガーフィールドは、捨て身の賭けに出る……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フェーンチャン／ぼくの恋人", "フェーンチャン／ぼくの恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "幼なじみの少年少女の甘酸っぱい初恋を描き、本国タイで大ヒット。タイの80年代のヒット曲に乗せ、幼少期の思い出がみずみずしく綴られる。チャンバラごっこや“ドラえもん”など、日本でも馴染み深い懐かしのアイテムも登場。四季の移ろいを捉えた美しい映像と共に郷愁をそそる。ジアップにとって幼なじみの少女ノイナーは大切な存在。だが、彼はいたずらっ子の仲間に入るため、ノイナーを傷つけてしまい……。", "2005-03-19", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バネ式", "バネ式", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "ラジオの人気DJとしてお馴染みの吉田照美の初監督作品。大ファンだというつげ義春の漫画『ねじ式』にオマージュを捧げている。頭痛に悩まされる38歳のサラリーマン、アンナミラーズの制服を着たラジオの受付嬢、会社をリストラされ地下道路で八百屋をしている男の兄、謎の和服美女……。奇妙な登場人物による不条理でシュールな世界が展開。乙葉、みうらじゅん、伊東四朗ほかバラエティー豊かなキャスト陣に注目。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本の悲劇", "日本の悲劇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 42, "終戦の翌年製作されたが、GHQにより公開後一週間で上映が禁止され、フィルムを占領軍に没収された幻の戦争映画。この作品で亀井は、支那事変から太平洋戦争にいたる過程を、日本の海外市場支配のための“侵略”と断じて戦争犯罪人たちを告発している。", "1946-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メリーさんの電話", "メリーさんの電話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "女子高の空手部に所属する美奈子と真由子は、部の強化合宿で山奥の合宿施設へやってくる。しかし合宿の終了間際、部員たちが1人また1人と消えていく。美奈子は事件のナゾを調べるが、圏外のはずの携帯電話が突然鳴りはじめて……。幼いころに捨てた人形から電話がかかってくるという都市伝説「メリーさんの電話」をモチーフに、女子高生たちを襲う恐怖を描く。主演はグラビアアイドルの紗綾と、アイドルグループ「AKB48」の菊地あやか。", "2011-02-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Straight Story", "ストレイト・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/1851/315x210.jpg?3564", 111, "アメリカ・アイオワ州に住む73歳のアルヴィン・ストレイトは、娘のローズと二人暮らし。ある日、10年来仲違いしていた76歳の兄ライルが心臓発作で倒れたという電話が入り、アルヴィンは兄に会いに行くことを決意する。ライルが住むウィスコンシン州までは560キロ。車で行けば1日の距離だが、何とアルヴィンは時速8キロのトラクターで旅に出た。", "2000-03-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男の紋章・俺は斬る", "男の紋章・俺は斬る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "修行の旅を続ける竜次は、土地の人々を苦しめている悪徳ヤクザを斬る。竜次は新興ヤクザに母が殺されたとの知らせを聞き旅先から飛んで帰り、悲しみと怒りを刃に託し、見事に母の仇を討つ。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PURE LUCK", "ピュア・ラック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "行方不明の社長令嬢ヴァレリーを捜索すべく、腕利きの黒人探偵カンパネラと、ヴァレリーの父の会社の経理部員プロクターは、アカプルコに向かう。ところがこのプロクター、生まれて以来、ありとあらゆる災難を経験したというツイてない男だった。タフでクールなカンパネラもいつの間にかプロクターのペースに巻き込まれ、上へ下へのドタバタ捜査行が展開する。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COUSIN，COUSINE", "さよならの微笑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "女好きの夫に愛想をつかすマルトと、じゃじゃ馬の妻に手を焼くルドビクが、ある結婚式で知り合う。当然のごとく大人の恋を楽しむ彼らは、やがて手に手をとり合って旅立った……。エスプリのかぐわしい、心温まるフランス流“愛の讃歌”。アメリカ映画「今ひとたび」のオリジナルでもある。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SET IT OFF", "セット・イット・オフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "女性４人を主人公に据えた、パワフルなブラック・ムービー。知人が強盗を企てたことから銀行をクビになったフランキーは、生活に困った３人の友人たちとともに実際に銀行強盗を画策。首尾よく成功を収めた彼女たちは、さらに犯行を重ねるが、思わぬ落とし穴が……。全編を彩るラップ、ヒップホップ・ナンバーが、物語をテンポよく演出。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ランウェイ☆ビート", "ランウェイ☆ビート", "", "", "http://image.eiga.k-img.com/images/movie/55631/315x210.jpg?3137", 126, "原田マハのケータイ小説を、「NANA」の大谷健太郎監督が映画化した青春ドラマ。高校2年の塚本芽衣のクラスに、“ビート”こと溝呂木美糸が転校してくる。天才的なファッションセンスを持つビートは、イジメられっ子の“ワンダ”こと犬田悟を大変身させることに成功する。ビートの才能に驚いたクラスメイトたちは、彼を中心に文化祭のファッションショーに熱中していく。D-BOYSの瀬戸康史、「最後の忠臣蔵」の桜庭ななみら人気若手俳優が結集する。", "2011-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春のめざめ", "春のめざめ", "", "", "http://ecx.images-amazon.com/images/I/51ZWzNAbLnL._SL210_.jpg", 27, "「老人と海」でアカデミー賞短編アニメーション部門を獲得したアレクサンドル・ペトロフ監督が、油彩画を使用した独特のタッチで、思春期の少年の心象風景を綴った短編アニメーション。19世紀末のロシア。貴族学校に通う16歳の少年アントンは、ツルゲーネフの「初恋」に夢中だった。自身の恋についても妄想を膨らませるアントンは、家に住み込みで働く同じ年頃の少女パーシャと、隣の家に引越してきた年上の令嬢セラフィーマとの間で揺れ動くが……。", "2007-03-17", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("臥虎藏龍", "グリーン・デスティニー", "", "", "http://image.eiga.k-img.com/images/movie/1411/315x210.jpg?2565", 120, "剣の英雄たちが群雄割拠する時代。天下の名剣“グリーン・デスティニー”の使い手としてその名を轟かせる英雄リ−と女弟子ユーは、心惹かれ合いながらも長い間人々のため正義に生きてきた。リーは剣を置く決意をしてグリーン・デスティニーをユーに託し、依頼されたティエ氏に無事剣を届けたユーは、そこで貴族の娘イェンと出会う。その夜、グリーン・デスティニーが何者かに盗まれ、ユーはイェンを疑い彼女の家を訪ねる。", "2000-11-03", "1,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("War Party", "ウォー・パーティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "現代のアメリカで巻き起こった白人とインディアンの紛争を描いたアクション映画。「さらば青春の光」のフランク・ロッダム監督がメガホンを取る。アメリカのとある町で、100年前に起きたインディアン虐殺の悲劇を再現する、観光客向けのショーが開催されることになった。ところがそのショーの最中、騎兵隊に扮する白人青年が実弾でインディアンを撃ってしまう。これが発端となり、白人対インディアンの壮絶な戦いが始まった……。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Act of Valor", "ネイビーシールズ", "", "", "http://image.eiga.k-img.com/images/movie/57891/315x210.jpg?3118", 0, "オサマ・ビンラディンの殺害作戦も遂行した米海軍特殊部隊「Navy SEALs（ネイビーシールズ）」の戦いを描き、無名のキャスト、スタッフながらも全米ボックスオフィスで初登場1位を記録したミリタリーアクション。過酷な訓練をくぐり抜けた一握りの精鋭で組織される特殊部隊SEALsに、誘拐されたCIAエージェントの救出という任務が下る。しかし、その事件は世界規模のテロ計画の入り口に過ぎなかった。物語はフィクションだが、SEALsが全面協力し、本物の現役隊員も出演している。", "2012-06-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOKYO MELODY", "トーキョー・メロディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "YMO解散後、ソロアルバム『音楽図鑑』を制作していた頃の坂本龍一の姿を追ったドキュメンタリー映画。“世界のサカモト”として出発したばかりの坂本を、1984年という時代の中に鮮烈に描き出していく。ロケ場所は明治神宮、新宿のアルタ前、パチンコ屋、地下鉄車内などに及び、坂本自身が生い立ちや音楽観、現代文化の状況を語っていく。夫人である矢野顕子（後に離婚）と自宅のピアノで『東風』を連弾するシーンは圧巻だ。また、YMOの解散ライブにおける『ビハインド・ザ・マスク』の演奏も収められている。セリフは日本語で、英語字幕付き。", "1985-01-01", "2,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デコトラの鷲／恋の花咲く清水港", "デコトラの鷲／恋の花咲く清水港", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "哀川翔が義理人情に篤い浅草っ子のトラック野郎に扮した“デコトラの鷲”シリーズ第３作。今回は商店街の出しもので清水の次郎長を演じることになった主人公、鷲一郎が、窮地に陥った記憶喪失の美女を救うため大ハッスル。哀川とレギュラー陣のにぎやかな掛け合いが笑いを提供してくれる。仮眠中に不吉な夢を見たトラック野郎、鷲一郎は義母が重病と思い込み、浅草にとんぼ返り。そんな中、謎の一味に拉致されかけた蝶子という美女を救った鷲一郎は、彼女に一目惚れしてしまう。", "2006-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE INNER CIRCLE", "映写技師は見ていた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "KGBクラブの映写技師イワン。結婚式を挙げたばかりの彼は、KGBによって連行され、スターリン専属の映写技師に取り立てられた。巨大権力の中枢に近付いた彼は、全身全霊をスターリンに捧げていくのだった……。実話をもとに、コンチャロフスキーが崇拝者の視点からスターリン像に迫った作品。ビデオ発売時タイトルは「インナー・サークル／映写技師は見ていた」。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("治郎吉格子", "治郎吉格子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "伊藤大輔が、無声映画時代に大河内伝次郎主演で映画化した「御誂次郎吉格子」のリメイク。江戸を去った鼠小僧次郎吉が大阪に入り、借りた金のために妾勤めを強いられている娘を救う。長谷川一夫が、小粋な怪盗・鼠小僧次郎吉に扮し、好演している。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プロゴルファー織部金次郎", "プロゴルファー織部金次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "プロ生活17年で１勝もしていない中年ゴルファーの織部。レッスンプロとして生活している彼になんとか悲願の１勝をあげさせようと支援する生徒たち。彼らに支えられ織部は勝利を狙うが……。『ビッグコミック・スペリオール』に連載していた人気コミックの映画化。原作者でもある武田鉄矢が主演、脚本と一人３役を担当している。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GO FISH", "GO fish", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "監督のR・トローシュ自身が体験した、等身大のレズビアンたちの日常を描く。一年以上も恋人がいない小説家志望のマックスは、ルームメイトのキアから、獣医の助手ではにかみ屋のイーライを紹介されるのだが……。恋人募集中の二人を、仲間たちがなんとか結ばれるよう画策するラブ・コメディ。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BURNING", "バーニング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "園芸用の巨大なハサミを凶器として、次々に人間を殺していく殺人鬼“バンボロ”。学生たちでにぎわうキャンプ場は修羅場と化してしまう。なかなか姿を現わさないバンボロの巨大なシルエット、ドタ靴の足のショットが恐怖を盛り上げているホラー・ムービー。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Monkey Trouble", "ゆかいな天使〈ペット〉 トラブるモンキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "少女エヴァは、ある日、木にぶら下がっている猿を家に連れ帰り、ペットとして飼い始める。だがこの猿は、人の言葉を理解し、文字を書き、金目のものは何でも盗んでしまうというなんとも困ったお騒がせ天才モンキーだった……。天才モンキーのとぼけた名演技が楽しい、心温まるファミリー・コメディ。ビデオ発売時タイトルは「トラブるモンキー」。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺陣師段平", "殺陣師段平", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "長谷川幸延の原作を黒澤明脚色・マキノ雅広監督で映画化した芸道もの。大正時代、新国劇を創立した沢田正二郎は、一座の殺陣師・市川段平にリアルな殺陣の創作を依頼する。段平は無学からリアリズム精神が理解できず、師匠と決別しどん底生活に陥るが、殺陣の創作に生涯を捧げる。大映が社運を賭けた「羅生門」を準備中の黒澤明が片手間に書いた脚本を、マキノ雅広が事実上書き直して仕上げた速成品だが、段平役の月形龍之介、妻役の山田五十鈴がそれぞれ好演し見ごたえのある作品に仕上がった。なお、長谷川幸延の原作は1962年大映で瑞穂春海監督、中村鴈治郎の段平で再映画化され、マキノ雅広自身も1955年「人生とんぼ返り」という題名、森繁久彌の段平で再映画化。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PINK LIGHTNING", "ピンク・ライトニング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1962年のカリフォルニアを舞台に、19歳の女の子５人組の青春をノスタルジックに描く。“ピンク・ライトニング”（ピンクの稲妻）とは、女の子が初めてつける口紅のことを意味する。トウッキーたち５人娘は、共同で購入したピンクのオープンカーを乗りまわし、いつも一緒にはしゃいでいた。ある夜、いつものように恋やセックス、将来のことを語り合ううち、トウッキーの恋人パトリックの初体験相手が、５人組の一人、ジルだとわかる。しかもその関係は今でも続いていると知り、友情にヒビが入ってしまう。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("虞美人草", "虞美人草", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "夏目漱石の名作を巨匠・溝口健二が映画化。漱石作品が山の手の上流階級の若者を描いた知的で都会的なものであったのに対し、本作品ではそうした人々と並行して描かれる昔気質の庶民の描写に本領が発揮されている。恩師の娘に扮する大倉千代子の演技が絶品。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("色即是空2", "セックス・イズ・ゼロ2", "", "", "http://image.eiga.k-img.com/images/movie/54121/315x210.jpg?3359", 115, "「韓流シネマ・フェスティバル2008ラブ＆ヒューマン」上映作品。02年に韓国で大ヒットした大学生の性欲に溢れた青春をユーモラスに描いたコメディ「セックス・イズ・ゼロ」の続編。司法試験合格を目指すウンシク は、水泳部に序属する大学一の美女、キョンアと付きあって3年経つが、キス以外は許してもらえず性欲を持て余していた……。", "2008-10-18", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("踊る大捜査線 THE FINAL（仮題）", "踊る大捜査線 THE FINAL（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-09-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MASTER OF THE ISLANDS", "大洋のかなたに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "ハワイ島が宣教師に発見されてから約10年。まだまだ未開のハワイだが、労働力が不足していた。やがて中国から大量の移民がやって来て、島のために懸命になって働く。未開にして不毛の大地ハワイに移民してきた二組の家族の、たゆまない努力と開拓と苦悶の物語。「ハワイ」の姉妹編にあたる。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("All the King's Men", "オール・ザ・キングスメン", "", "", "http://image.eiga.k-img.com/images/movie/33911/315x210.jpg?1271", 128, "「シンドラーのリスト」のスティーブン・ゼイリアン監督がショーン・ペン＆ジュード・ロウ主演で放つ政治サスペンス。実在の政治家をモデルにしたロバート・ペン・ウォーレンの小説「すべての王の臣」を、1949年のアカデミー作品賞受賞作に続いて2度目の映画化。理想を胸に州知事に上りつめた叩きあげの政治家と彼を追う貴族階級出身の新聞記者の運命を描く。アンソニー・ホプキンス、ケイト・ウィンスレットら豪華キャストが脇を固める。", "2007-04-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUNNY GIRL", "ファニー・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 147, "1964年にブロードウェイで上演された、1920年代のエンターテイナー、ファニー・ブライスの伝記ミュージカルの映画化。舞台で当たりをとったバーブラが好演して見事オスカーを受賞。ジュールス・スタインの美しい曲が随所に散りばめられている。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ИДИ И СМОТРИ／COME　AND　SEE", "炎628", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "1985年モスクワ国際映画祭金賞に輝いた作品。第二次世界大戦中のドイツ軍によるロシア人虐殺という衝撃的な事実をもとに、その陰惨なドラマを、ある“白ロシア”の村に住む一人の少年の目を通して描く。村人が参加した殺りくシーンがリアル。", "1985-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タッチ3 君が通り過ぎたあとに", "タッチ3 君が通り過ぎたあとに", "", "", "http://ecx.images-amazon.com/images/I/212CYZG0YNL._SL210_.jpg", 83, "人気漫画家・あだち充原作による大ヒットテレビアニメの劇場版シリーズ第3作。高校3年生になった達也と南。達也が甲子園への最後のチャンスをかけて練習に励む一方、南は新体操部をやめて野球部のマネージャー活動を再開することに。そんなある日、入院した西尾監督の代理として柏葉英二郎がやってくる。明青野球部を除名された過去を持つ柏葉は、その恨みを晴らすべく部員たちを鬼のようにしごきはじめる。", "1987-04-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和最大の顔役", "昭和最大の顔役", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "鶴田浩二主演の現代ヤクザもの。昭和27年、関西の藤政組の東京進出を嫌う大嶋組によって藤政親分が刺殺された。ムショ帰りの緒形は親分の遺言に従って土木工事に全力を注ぐ。やがて昭和40年……。初々しい大原麗子の姿も見逃せない。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("執炎", "執炎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "蔵原惟繕の最高傑作ともいえる純愛もの。主演の浅丘ルリ子にとっては主演100本目の記念作にあたり、事実この映画は彼女が女優として大きく飛躍する分岐点となった。浅丘演じる娘・きよのは、水産学校を卒業した拓治と結婚する。情熱的に愛し合う二人だが、やがて拓治は戦争に駆り出される。ほどなく拓治は右足を負傷して帰還、きよのの必死の看病もあって彼は回復するが、再び赤紙が舞い込む。きよのは戦地の拓治の無事を祈ってお百度を踏むが、その甲斐もなく拓治は戦死。きよのも後を追って崖から身を投げる……。浅丘ルリ子の、愛する者を奪われ、精神的に追いつめられていく時の演技は、そのテンションの高さといい、思いつめる表情といい、一人芝居ながら緊張感にあふれ、観る者の胸を刺す。純愛が女のエゴに転化し、それがエロチシズムにまで昇華するには、同じ蔵原＝浅丘による「愛の乾き」（1967）まで待たねばならないが、彼女の女優としての美が一つの結実をみたのは、この作品によってであった。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪魔の街", "悪魔の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "鈴木清順の３作目にして初のアクション映画。脱獄したギャングのボスをめぐる、一の子分・早崎の忠誠と裏切りの物語を軸に、競馬の八百長レース、現金輸送車襲撃、警官殺しなどが盛りこまれる。ありふれた題材ながら、清順演出はキレの良さを見せる。当時、根強い人気を保っていた河津清三郎のギャングもの。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIPSTICK", "リップスティック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "“強姦（レイプ）”事件をどう扱うか、社会的関心が高まっていたころに作られた話題作。トップモデルのクリスが犯され、裁判は男に優勢のまま進行、しかも妹までレイプの毒牙にさらされて……。ラスト、ライフルを持って猛然と男に立ち向かうクリスの姿が強烈。女性の復讐を肯定的に描いている点がユニーク作品。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獵魔群英／DEVIL HUNTERS", "群狼大戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "国際的な麻薬密売人フォンは、取引の最中、香港警察の手入れを受ける。銃撃戦のあと、逃げ切ったフォンは取引相手が密告したと思い彼を殺害した。この事件が引き金となってギャングたちの争いと、警察が入り乱れての死闘が始まる。ムーン・リーをはじめ香港映画界のスターが勢ぞろいしたバイオレンス・アクション。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("口笛を吹く渡り鳥", "口笛を吹く渡り鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "雁（かりがね）の伊太郎は旅の途中、医師のト斎とその娘・おせつと知り合う。土地の顔役・五兵衛ににらまれ、傷を負った伊太郎を看病するうちにおせつには、彼への恋心が芽ばえ始める。しかし、伊太郎は再び旅に出て……。八尋不二のオリジナル・シナリオを名匠・田坂具隆の弟、勝彦が監督した股旅もの。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Liar Liar", "ライアーライアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "お笑いスター、J・キャリーの主演によるハートウォーミング・コメディ。彼が扮するウソが得意な弁護士が、息子の誕生日の願い事によって１日だけウソをつけない体になってしまったことから、大騒動が巻き起こる。「エース・ベンチュラ」のシャドヤック監督が、キャリーのアクティブな魅力を活かしてコミカルな見せ場を演出。笑いの一方で、父と子の絆がさわやかな後味を与えてくれる。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本侠客伝 昇り龍", "日本侠客伝 昇り龍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "前作に続き、若松が舞台。小頭組合の結成を呼びかけた玉井金五郎は、共同組の友田に狙われる。傷ついた玉井を救ったのはお京だった。お京は畢生の昇り龍を玉井の背中に彫り込む。傷が癒えたのもつかの間、玉井と友田の争いは新たな局面を迎える。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("From Paris with Love", "パリより愛をこめて", "", "", "http://image.eiga.k-img.com/images/movie/55251/315x210.jpg?4375", 95, "「96時間」の製作リュック・ベッソン＆ピエール・モレル監督のコンビによるスパイ・アクション。パリのアメリカ大使館に勤務し、人も羨む生活を送っているリース（ジョナサン・リース＝マイヤーズ）だったが、彼の裏の顔はCIAの下級職員で、夢は正真正銘のエージェントになることだった。そんなある日、CIAからリースにある事件の捜査指令が下り、ワックス（ジョン・トラボルタ）というスキンヘッドの男とコンビを組むことになる。", "2010-05-15", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Always", "ただ君だけ", "", "", "http://image.eiga.k-img.com/images/movie/58041/315x210.jpg?1676", 105, "", "2012-06-30", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Dangerous Lives of Altar Boys", "イノセント・ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/1451/315x210.jpg?2218", 104, "74年の米ジョージア州、校則の厳しいカトリック系学校に通う14歳のフランシスは、親友ティムたちと自分たちがヒーローに変身して女性校長を倒すコミックを描くのが生き甲斐。そんな彼が同級生のマージに恋をする。クリス・ファーマンの小説「放課後のギャング団」（早川書房）をジョディ・フォスター製作・出演で映画化。監督は本作でデビューのピーター・ケア。劇中の70年代風アニメを「スポーン」のマクファーレンが制作。", "2002-09-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真救世主伝説　北斗の拳　ユリア伝", "真救世主伝説　北斗の拳　ユリア伝", "", "", "http://ecx.images-amazon.com/images/I/615CQP985VL._SL210_.jpg", 0, "1980年代に社会現象を巻きおこした大ヒットコミック「北斗の拳」を、「真救世主伝説」として新たにアニメ化した全5部作の第2弾。南斗最後の将・ユリアを主人公に据え、原作では描かれなかった数々のエピソードが明かされる。世界大戦によって大地が荒廃し、混沌と暴力が支配する世界。南斗正当血統の少女ユリアは、北斗の聖地で幼いケンシロウやラオウらと出会う。やがて成長したユリアはケンシロウと婚約するが……。", "2007-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仁義と抗争", "仁義と抗争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「沖縄やくざ戦争」「北陸代理戦争」に続くローカルヤクザものの第３作。大阪の巨大ヤクザ組織が、東京進出の拠点とした北関東の温泉街を舞台に、地元のヤクザ、関東の尖兵隊、そして大阪の巨大組織が三つ巴の抗争を展開する。アクションの醍醐味と、息づまるストーリー展開が特長のヤクザ映画。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OMEGA COP", "オメガ・コップ1999 地球最後のデカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1999年、オゾン層の破壊により太陽熱でミュータント化した人間が、従来の人間を奴隷として虐待していた。地球最後の警官トラビスはミュータントのボスを倒し、人類を救うため旅に出るが……。人間VSミュータントの戦いを描く近未来アクション。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Stars Fell on Henrietta", "ヘンリエッタに降る星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "石油の鉱脈を探して旅をする山師コックスと猫のマチルダは、ある日、ヘンリエッタという町にたどり着く。ここに暮らすデイ家の小さな農場の地下に、石油が流れるのを感じ取ったからだ。困惑するデイ夫妻をよそに、コックスは皿洗いから強盗（！）まで、あらゆる方法で資金を集め始める。一方、貧しい生活から抜け出したいと考えていたデイ家の主人ドンにも、コックスの夢が飛び火して……。数々のヒット作を世に送り出したC・イーストウッドとD・ヴァルデスの製作コンビが、古き良き時代のアメリカン・ドリームをよみがえらせる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("That Cold Day in the Park", "雨にぬれた舗道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "　裕福だが、孤独な女性フランセスは公園のベンチで雨に打たれていた青年に惹かれ、彼を自分の家に連れ帰る。フランセスは無口な青年を誘惑するが、彼は応じようとしない。青年がフランセスの金に惹かれていたことがわかっても、彼女の気持ちに変わりはなかった。それどころか青年に気に入られたいばかりにフランセスは若い娼婦を彼にあてがうが……。年下の青年を愛した女性の心理を描くドラマ。", "1969-01-01", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Click", "もしも昨日が選べたら", "", "", "http://image.eiga.k-img.com/images/movie/33661/315x210.jpg?4511", 107, "「50回目のファースト・キス」のアダム・サンドラーと「アンダーワールド」のケイト・ベッキンセール共演によるハートウォーミング・コメディ。人生をコントロールできるリモコンを手にした男が、次第にそのリモコンに翻弄されていく姿を描く。出世を夢見る仕事人間のマイケルは、ひょんなことからあらゆるものを操作できるリモコンを手に入れる。面倒な家族との時間を早送りするなど、マイケルは自分の人生を思い通りに操るようになるが……。", "2006-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DU MICH AUCH", "イカレたロミオに　泣き虫ジュリエット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "東西統合前のベルリン。なかでもとりわけアナーキーな一画、クロイツベルクを舞台に、体制からドロップアウトしかかった青年たちの姿を描いた青春映画。スイスからベルリンへ流れてきた青年ロミオと、彼の恋人で生粋のベルリン娘ユリア。いつの間にか愛し合うようになった二人の間に亀裂が生じ始めたころ、老政治家の家でのパーティーに招かれ演奏する彼らの前で、突然殺人事件が起こる。何がなんだかわからないうちに追われるはめになってしまう二人。ギリギリの中で生きていく彼らの姿を描いて、ベルリンでは異例の２年間というロングランを記録。スラップスティックな展開も楽しい。", "1986-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美貌の都", "美貌の都", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "大阪の町工場で働く千佳子は、同じ工場で働く恋人、耕一がいながら、貧しい生活から脱して陽の当たる場所にいきたいと願っていた。そんな彼女の前に現れた青年副社長、一彦。千佳子は彼の住む夢のような世界の虜となり、耕一を冷たくあしらい、工場を辞めてしまうのだが……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE TRICHEURS", "危険な曲り角", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "実存主義にかぶれ、サン・ジェルマン・デ・プレにたむろする若者たち。そんな若者の一人であるボブは、友人のパーティーでミックという若い娘と親しくなるが……。セックス、スリルなど、1950年代末パリの若者たちの生態を巨匠カルネが描く。", "1958-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ESCALIER C", "C階段", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "パリ14区のありふれたアパルトマンにかかる“C階段”。痛烈な批評で知られる美術評論家フォステールや、痴話ゲンカを繰り返すビジルとベアトリス夫妻など、今日もC階段を舞台に様々な人間模様が繰り返される。エスプリの効いた会話と繊細な映像があざやか。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タイムレスメロディ", "タイムレスメロディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「ピクニック」「砂漠の民カザック」でPFF連続入選を果たした奥原浩志が、スカラシップ作品として撮り上げた劇場デビュー作。自主制作作品がそうだったように、ここでも男女の三角関係を基盤に置きながらも、生臭さをまったく感じさせない透明なトライアングル・ドラマを奏でている。古びたビリヤード場を切り盛りする青年と、彼の音楽仲間である女の子。二人の微妙な距離を、常連客の中年男の存在やその突然の死によって招かれる彼の息子の登場によって浮き彫りにしていく。いわば二枚の三角形を重ねズラしたかのような複合的な設定。だが、会話や登場人物のキャラクターに頼らず、その場その場の空気そのものが体現する“関係性”そのものを見つめた誠実な作品に仕上げている。第４回釜山国際映画祭でグランプリを受賞。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カラフル", "カラフル", "", "", "http://ecx.images-amazon.com/images/I/21VF2RQRR7L._SL210_.jpg", 98, "児童文学作家、森絵都の同名小説を、「失楽園」の森田芳光が脚本を担当し、「コキーユ　貝殻」の中原俊が演出したハートウォーミング・ストーリー。生前の悪行がたたって地獄に落ちるところを抽選に当たり、自殺未遂を起こした少年・真として生き直すことになった僕。真として生活しながら自分の悪事を思い出し反省すれば、輪廻のサイクルに戻れるという。僕が乗り移ったことによってネガティブだった真が、急に明るくなって周りは驚きを隠せない。そんなとき、僕はすべてを思い出し……。表面層では見えにくい少年少女たちの傷つきやすく感受性豊かな心の内を、ユーモアを交えながらさわやかに描いている。", "2000-10-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FORBIN PROJECT", "地球爆破作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "アメリカとソ連に誕生した完璧な知能と判断力を持つ自給自足のコンピュータ・システム。世界平和のために生み出されたこの二つの機械は知らぬ間に自分たちで情報交換をするようになり、やがて人間をその支配下に置こうと反逆を始める……。ブラックなメッセージが込められたSF恐怖映画の隠れた傑作。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・隠密剣士", "続・隠密剣士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "1962年から放映され人気を呼んだ同名TVドラマの映画化。北条家が発掘した伊豆金の所在地を記した“風神帳”をめぐって公儀隠密・秋草新太郎と風魔小太郎率いる風魔一族の壮絶な争奪戦が展開される。秋草新太郎に扮する大瀬康一は、当時の少年たちのアイドルであった。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犯された白衣", "犯された白衣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "1963年に「甘い罠」でデビューして以来、数多くのピンク映画を監督してきた“性と暴力”の巨匠・若松孝二の代表作とされる１本。当時シカゴで実際に起きた大量殺人事件をモデルに、看護婦寮に紛れ込んだ少年が意味もなく“白衣の天使”たちを陵辱したあげくに殺してしまうという物語を、どことなくメルヘンの匂いがするシニカルなタッチで描いている。密室を凄絶なまでにワンショットで動き回るカメラ、そして唐十郎演じる役名“美少年”が殺した女たちの死体の真ん中で、まるで胎児のようにうずくまるシーンが印象的だ。寮に機動隊が突入するラストでは突然ドキュメンタリー映画のような変調を見せ、衝撃的に幕が閉じる。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市物語", "座頭市物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "子母沢寛の随筆集『ふところ手帖』に収められた短いエピソードである座頭の市の話をもとに、犬塚稔脚色・三隅研次監督・勝新太郎主演で映画化した大ヒット・シリーズの第１作。ツボ振りでも居合い抜きでも目明きの及ばぬすご腕の座頭市は、飯岡助五郎の客分となる。市は釣りで知り合った肺病やみの浪人・平手造酒に友情を感じるが、平手は助五郎と犬猿の中の笹川繁造の食客であった。やがて運命の糸は市と平手を対決に導く……。のちにはスーパーマンになってしまう座頭市だが、この作品では冒頭、市が丸木橋をヘッピリ腰で渡るシーンに代表されるように、盲目という致命的なハンデが随所に示され、市の世をすねて生きているアウトロー性を強調。三隅研次の淡々とした中にもメリハリの利いた演出が、見事に成功した。座頭市に扮した勝新太郎の好演はいうまでもないが、それ以上に素晴らしいのが平手造酒を演じる天知茂で、新東宝時代に養ってきたニヒルな持ち味を十二分に発揮した名演技であった。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ファイブスター物語", "ファイブスター物語", "", "", "http://ecx.images-amazon.com/images/I/517Fyq-oDlL._SL210_.jpg", 65, "「重戦機エルガイム」「機動戦士Zガンダム」のメカデザインなどを手がけた永野護による同名コミックをアニメーション化。星団暦2988年。惑星アドラーに降り立った青年レディオス・ソープは、友人のクローム・バランシェから、領主ユーバー・バラダ大公に捕われたファティマ・ラキシスの救出を依頼される。ソープはラキシスを助け出すためユーバー大公の城へと乗り込むが……。原作コミック第1巻のストーリーをもとに映画化した。", "1989-03-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOMMES　FEMMES ： MODE　DEMPLOI", "男と女、嘘つきな関係", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "「男と女」から30年。名匠C・ルルーシュが、出会うはずのなかった二人の男の奇妙な友情と運命を味わい深く描き出した恋愛群像ドラマ。舞台はパリ。病院の待合室で知り合った刑事ファビオと実業家ブノワ。互いにそれぞれの人生観を語り合った彼らは、友情に目覚める。そんな折、美しい女医アレックスが二人を“誤診”したことから、男たちの運命は大きく反転していく……。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丹下左膳 怒涛篇", "丹下左膳 怒涛篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "今回は財宝のありかを秘めた金竜の香炉をめぐって、激しい争奪戦が展開。いさかいに巻き込まれた左膳の活躍を描く。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BHOWANI JUNCTION", "ボワニー分岐点", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "独立寸前のインドを舞台に、イギリス人として生きるか苦悩する混血女性を描くスペクタクル大作。無抵抗主義インド独立運動を武力で圧迫していくイギリス軍の仕打ちに憤ったヒロインは、やがてインド暴力革命派の人質にとられてしまい、列車爆破計画に巻き込まれていく。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KANAL", "地下水道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「灰とダイヤモンド」とともにポーリッシュ・リアリズムの傑作と絶賛されたワイダの代表作。大戦末期、20万人もの犠牲者を出したワルシャワ蜂起の模様をリアルなタッチで描き出す。ワルシャワの対独レジスタンスは、ソ連軍進攻に希望を燃やし一斉に武装蜂起をしたが、むなしくもドイツ軍によって壊滅的な打撃を与えられ、地下水道に追い込まれる……。悪臭と泥と迷路のような地下水道で苦悶するレジスタンスの姿を非情なドキュメンタリー・タッチで描出。ワイダ監督の名を、世界中に広めた作品として見逃すことのできない傑作。", "1957-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜逃げ屋本舗", "夜逃げ屋本舗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "あらゆる手段を用いて借金を踏み倒し、正当な手続きで“夜逃げ”を実行する夜逃げ屋こと“ミッドナイトラン”。借りたものは返すのが当たり前と債権回収に燃えるクレジット会社との丁々発止をコミカルに描くシリーズ第１弾。自己破産など社会問題となった“クレジットカード問題”を独自な視点で捉えている。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APPOINTMENT WITH DEATH", "死海殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ミステリーの女王クリスティの傑作『死と約束』の映画化。アメリカを旅立った豪華客船が、欧州を横切りエルサレムに到着。砂漠と死海に囲まれたこの聖地で、突如連続殺人事件が発生するが……。名探偵ポアロの謎解きに胸がドキドキ。バコールの好演が光る。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("星のフラメンコ", "星のフラメンコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "義弟の好意で、行方不明の母親を捜すための航海に出た英司。終戦直後に台北の小学校で音楽教師をしていたこと、「田道間守」という小学唱歌を愛唱していたという情報だけを頼りに、異国の地での母親捜しを続ける彼は、ペテンにかけられて財産を失いながらも、とうとう日本に帰ることができなかった母の数奇な運命に出会う。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆撃機の眼", "爆撃機の眼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "八坂俊行が映画美学校高等科修了作品として製作した監督第２作。平凡な二人の女子高生が後戻りできない世界に向かって疾走する姿を、暴力や喪失感、裏切りや偏愛といったテーマを織り込みながら描く。閉塞的な時代の空気を捉えた、陰影のコントラストが美しい映像も強烈な力作。退屈な毎日を送る女子高生の時子と雫は、ある日、偶然忍び込んだアパートで帰宅した住人の男を縛り上げ、監禁。彼女らは男の部屋で過ごし始める。そんな中、女性の眼がつぶされるという猟奇殺人事件が続発し……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE VENUSFALLE", "嫉妬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ニュージャーマン・シネマの旗手R・V・アケレン監督が描く、二人の女と一人の男のラブ・ストーリー。M・ルーセルが、間違い電話をきっかけにして結婚間近の医者を虜にする謎めいた“理想の女”に扮し、魅力的な演技を披露している。", "1988-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROPE", "ロープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ロープ＆レオポルド事件に基づくパトリック・ハミルトンの戯曲の映画化。マンハッタンのアパートを舞台に大学生二人が、自分たちの優秀性を証明するために同級生を絞殺し、衣装箱に死体を詰める。二人はさらにスリルを味わうために殺人現場のアパートの一室でパーティーを開き、被害者の父や、恋人たちを招く。一同を前に二人は衣装箱の上に酒や食物を並べ、手のこんだ演出をみせるが、やがて死体を見せたい衝動にかられていく……。夕方から夜にかけての１時間半を、映画の進行時間と現実時間を一致させ、１シーン１カットにまとめた実験作。ヒッチコック最初のカラー・スリラーでもある。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本の黒幕", "日本の黒幕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "政界に暗躍する右翼の大物の生きざまを描く大作。東映実録ヤクザ映画の系譜に入るが、アクション・シーンは抑えて、人間ドラマを重視した物語になっている。オーバーアクション気味の演出は気になるが、数多い登場人物の描き分けは明確でわかりやすい。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARVEY", "ハーヴェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ピュリッツァー賞を受賞した大ヒット舞台劇を映画化したハートフル・コメディ。アメリカ中西部の小さな町。名門ダウド家の主人エルウッドは、ハーヴェイという身長６フィートの白ウサギを親友だと思い込んでいる。他の人には見えないこの親友を会う人ごとに紹介したがる彼に、同居している姉ヴィタや彼女の娘マートルは大迷惑。ついに彼を精神病院に入院させる決意をするのだが……。風変わりな男が巻き起こす騒動を、ほのぼのとしたタッチで描き出す。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京ラプソディ", "東京ラプソディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "有名な同名大ヒット曲を用いた音楽映画。アコーディオンの演奏と歌の得意な青年が一躍人気歌手に。けれども、仲間との間に溝ができたり、ゴシップ欄にあることないことを書かれたりで恋人とも気まずくなって……。他愛ない物語だが、ラストに主人公が表題曲を歌い、それが街々に広がっていくシーンが秀逸。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄泉〈よみ〉がえり", "黄泉〈よみ〉がえり", "", "", "http://ecx.images-amazon.com/images/I/41QAE4RMDYL._SL210_.jpg", 126, "「害虫」の塩田明彦監督がSMAPの草なぎ剛を主演に迎えて描いた感動のファンタジー・ドラマ。３週間だけ甦った死者と家族の再会と別れの情景を、多彩なエピソードを効果的に配しながら綴る。九州の阿蘇地方で、死者が突然、死んだ当時の姿のまま蘇るという怪現象が次々に発生する。調査のために現地を訪れた厚生労働省勤務の川田平太は、死者とその家族に何度か会うたび、そこに“ある法則”があることに気づくのだが……。", "2003-01-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きかんしゃ　やえもん", "きかんしゃ　やえもん", "", "", "http://image.eiga.k-img.com/images/movie/54801/315x210.jpg?2237", 0, "同名名作絵本を3Dアニメで映画化した作品。古くなって動けなくなった機関車のやえもんは、駅の片隅に追いやられ、駅員からも邪魔者扱いされていた。それでもやえもんを住み家とするネズミの兄弟たちと仲良く平和に暮らしていたやえもんだったが、遂にやえもんを処分することが決まってしまう。短編3Dアニメーションを集めた「とびだす！3D東映アニメ祭り」で上映。", "2009-10-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("毒薬の匂う女", "毒薬の匂う女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "裏で悪どい恐喝を続ける経済研究所の所長と、そのための手段として働かされる悲劇の養女との愛憎を描くサスペンス映画。一流宝石店のオーナーである美也子は、店の客から情報をつかんでは父親の信也に密告していたが、次第に信也を憎むようになっていく。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHADOWS OF THE PEACOCK", "ラスト・ジゴロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "生活に疲れた人妻マリアは、タイ南端の小島を訪れる。南の楽園の中で、彼女は魅惑的なバリ舞踊のダンサー、ラカと出会う。家族を気にかけながらも、ラカと過ごす南国での神秘的な暮らしが捨てられなくなるマリアだったが……。京劇出身のJ・ローンのベリーダンスが見どころ。", "1987-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("「フィリップ君。」", "「フィリップ君。」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "関西自主映画界の雄、岩崎友彦が手掛けたファンタジー。死んでしまった少女、たえ子の姿を探し求めるロボット、フィリップ君。人々が次第に彼女のことを忘れていくなか、フィリップ君はその正確な記憶力ゆえにどうしても忘れることができない。思い余った彼は天国へと向かうロケット作りを始める……。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALL NORTHSIDE 777", "出獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "『シカゴ・タイムス』紙の記者J・マクガイアーが経験した事実を書いた手記を映画化したセミ・ドキュメンタリー。殺人犯に仕立てられた息子の無実を証明するために奔走する老女と、それを助ける新聞記者。威信を守ろうとする警察の激しい妨害をかわしながら、記者は徐々に真相へと近づいていく。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CAGE AUX FOLLES ３：THE WEDDING", "ウェディングベル　Mr.レディ Mr.マダム３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "日本でも舞台上演されるほど人気を集めたシリーズの第３作。莫大な遺産を相続するには一年半以内に子供を作るべし、との遺言を受けとったアルバン。そこで始まる“女”が女に子供を生ませる涙ぐましい努力の数々。トニャッツィが相変わらずの名演。", "1986-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE SKY", "ブルー・スカイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "1990年にクランク・インしたものの、T・リチャードソン監督の死や製作会社の倒産などによって、完成、公開が４年も先送りされていたといういわくつきの作品。自らをコントロールできずに、常に衝動的な行動に走ってしまうカーリー、そしてそんな彼女の感情の起伏の激しさにほんろうされながらも、彼女を愛し続ける夫ハンク。軍人である彼が“ブルー・スカイ”という略号名の核実験プログラムに関与したことから、カーリーとハンクの大きな試練が始まる。本作品で初のアカデミー主演女優賞、ゴールデン・グローブ賞主演女優賞を獲得したJ・ラングが、奔放で不安定なカーリーを体当たりで演じている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.WONDERFUL", "最高の恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "電気技師のガスは別れた妻、リーへの慰謝料の支払いに追われて、仕事仲間の間で持ち上がっているボウリング場の再建話にのることができない。そこでリーに“最高の恋人”を紹介して再婚させ、慰謝料から逃れようと考えるが……。ニューヨークを舞台に、愛に揺れる男女を描いたロマンティック・ラブ・ストーリー。主役のM・ディロンが愛に不器用な男を好演。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PONG NYO", "桑の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "コリアン・エロスの代表作「桑の葉」から生まれた官能映画。村の娘がある青年と恋に落ちるが、青年は姿を消してしまった。彼は事故にあったのだと噂され、数年が過ぎた後、別の男と結婚していた娘の前に青年が現れる。期待の新人、ソン・トーヨンがヒロインを熱演。", "1990-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZIEMIA OBIECANA", "約束の土地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "ポーランドの作家B・レイモンドの同名小説を映画化した社会派の人間ドラマだが、広角レンズを多用するなど、エキセントリックなカメラ・ワークが目につく怪作。19世紀末のポーランドの町ウツジ。ユダヤ人のモリツ、ドイツ人のマックス、ポーランド人のカルロが製糸工場を建設することを夢見て共同作業をスタートさせるが……。モスクワ映画祭金賞受賞作。", "1974-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sunshine", "サンシャイン2057", "", "", "http://image.eiga.k-img.com/images/movie/33921/315x210.jpg?1279", 108, "「トレインスポッティング」「28日後...」のダニー・ボイル監督によるSFアドベンチャー。太陽の消滅が迫り、人類滅亡へのカウントダウンが始まった近未来。太陽再生計画を託された男女8人の精鋭が、宇宙船イカロス2号に乗り込んで旅立った。しかし、彼らは想像を絶する恐ろしい事態に巻き込まれていく……。主演は「麦の穂をゆらす風」のキリアン・マーフィ。またイカロス2号の船長カネダを真田広之が演じる。", "2007-04-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIAMI BLUES", "マイアミ・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "刑務所暮らしで失った時間を取り戻そうと焦りながらも、心の風穴を埋めることができずに暴走する若い男と、平凡な幸福を夢見ながらもそれをつかむことのできない若い女、そしてプライドを傷つけられて初めて生き返った刑事の物語。役作りのために実際に服役中の男たちと話をしたという、A・ボールドウィンの性急で、無計画な役どころが見もの。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOURNEY INTO FEAR", "恐怖への旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "様々な国籍の乗客でごった返す客船内で、ゲシュタポに追われる男の恐怖を描いたサスペンス小説を、O・ウェルズの脚色・主演で映画化。逃げ場のない客船内でのスリリングな人間ドラマが楽しめる。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE THREE MUSKETEERS", "三銃士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "王の銃士になろうと旅に出た青年ダルタニアンは、豪傑無頼の三銃士とともに腹黒い枢機卿の陰謀を破り、見事念願の銃士となる。おなじみの剣豪小説の前半部分を、才人レスター監督が、ドタバタ調の笑いとアクションを交じえて、さわやかに映画化。豪華キャストも魅力的。", "1973-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MICROCOSMOS", "ミクロコスモス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "ある一日の昆虫たちの生態を、愛嬌たっぷりに捉えた異色ファンタジー。変態を遂げ、食物を求め、交尾し、他の生物と闘いながら身を守る数十種の昆虫の世界を、センチメートルの単位で接写。カタツムリのラブ・シーンやコガネムシの玉転がし、ケムシの行列など、ユニークな映像を連ねながら、自然の神秘を浮き彫りにする。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAKE ME OUT TO THE BALL GAME", "私を野球につれてって", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "F・シナトラとG・ケリーが見事なタップダンスを披露するミュージカル・コメディ。舞台は20世紀初頭。女性に買収されてしまった野球チームの選手たちを中心に、恋あり笑いありのにぎやかなストーリーが展開する。主題歌は現在でもアメリカ国民の愛唱歌であり、大リーグの試合中に歌われている。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAVID AND GOLIATH", "ダビデとゴライアス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "旧約聖書におけるダビデとゴライアスの神話に基づき、ダビデが巨人ゴライアスを倒すまでの闘いを大スペクタクルな史劇として映像化。監督としても著名なウェルズが、サウル王に扮して出演しているのも見どころの一つ。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・柴又慕情", "男はつらいよ・柴又慕情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "“寅さんの憧れの人”ファン投票で第１位に輝いた吉永小百合をマドンナに迎えた一編。北陸で出会った３人娘の一人・歌子に想いを寄せる寅次郎だが、彼女は小説家の父親と恋人の間で悩んでいた。２代目おいちゃん、松村達雄が登場。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Color of Money", "ハスラー2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "あのミネソタ・ファッツとの死闘から25年、かつてのハスラー、エディは現役を離れ酒のセールスマンをしていた。ある日、エディは若いハスラー、ヴィンセントに出会う。次々と相手を打ち負かす彼に、自分の若いころの姿を見出したエディは、アトランティック・シティーの大会を目指してヴィンセントと恋人カルメンの３人で旅に出る。ロバート・ロッセンの名作「ハスラー」（1961）の続編ともいえる作品。小説家のR・プライスがM・スコセッシ、P・ニューマンとともに練ったアイデアを脚本にまとめ、老ハスラーの復活もスリリングにストーリー化。技巧をこらしたスコセッシ演出も相まって、見ごたえ十分の人間ドラマになっている。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幌馬車は行く", "幌馬車は行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "赤木圭一郎が「海の情事に賭けろ」に次いで主演した西部劇の開拓ものを思わせるアクション映画。季節の花々の開花を追って旅する移動養蜂隊に、負傷した青年が転がり込んで来る。彼は逃亡中の強盗団の一人であったが改心する。しかし、強盗団の仲間が幌馬車隊へ乗り込んできて……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("濡れた赫い糸", "濡れた赫い糸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "情欲と嫉妬が渦巻く色町を舞台に、愛するがゆえに傷つけあう男女を描いた官能ラブ・ストーリー。大阪に実在する売春街の住人たちをモデルにした激しくも情の深いエピソードを、「皆月」の望月六郎監督が映画化。北村一輝と奥田瑛ニという新旧を代表するやさぐれセクシー系の競演も見もの。娼婦の一美と茂は惹かれあうが、一美はヤクザの夫のもとへ去ってしまう。1年後、色町の住人となった茂の前に再び一美が現れるが……。", "2005-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("西陣の姉妹", "西陣の姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "かつては全盛を誇った京都西陣の織元は、その昔気質ゆえに時世の波についていけず、莫大な借金を残して自殺した。あとに残された女房と美しい３姉妹、そして主人に囲われていた芸者。これらの人々が没落していく様子が旧家を舞台に繰り広げられる群像劇。吉村監督的女性映画のフレイバーに満ちている。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("０cm４〈ゼロ・センチメーター・フォー〉", "０cm４〈ゼロ・センチメーター・フォー〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "園子温監督がファッション・デザイナー、荒川眞一郎によるパリ・コレクションのプレゼンテーション用に製作した短編映画。永瀬正敏扮する色覚異常の主人公、前田が、手術を受けるまでの一週間の出来事を描写。彼の不安や焦燥をデジタルカメラによる幻惑的な映像で浮き彫りにし、色覚、視覚というコレクションのテーマを表現している。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Da Capo", "空を歩く少年", "", "", "http://image.eiga.k-img.com/images/movie/55241/315x210.jpg?3646", 93, "バイクで配達のアルバイトをする女は、母親の死と父親の再婚を受け入れられずにいた。ある日、結婚式場にいる新郎のもとに8歳の少年を届けるよう頼まれるが、新郎は、自分の子供ではないと受け取りを拒否する。少年に「ここではないどこか」へ配達してほしいと頼まれた女は、少年を連れて当てもなくバイクで走り出す。2010年の「真！韓国映画祭」にて上映。", "2010-02-27", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIRAGE DE LA VIE", "人生の幻影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "1950年代ハリウッドでユニバーサル・メロドラマの黄金時代を築いた監督、D・サークがスイスで余生を送っていたところを、D・シュミットが訪れ、限りないオマージュを捧げる。サークのインタビューと彼の作品で綴られたドキュメンタリー映画。", "1984-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOEING BOEING", "ボーイング・ボーイング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "カモレッティのヒット舞台喜劇をT・カーティス主演で映画化。３人のスチュワーデスを手玉にとる新聞記者のバーナード。彼女たちがアパートで鉢合わせしないようスケジュールをびっちり組む念の入れようだったが、競争紙の記者ロバートにその秘密を握られたうえに、各航空会社のフライト・スケジュールが変更になって、バーナードは対応に四苦八苦するのだった。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROADWAY DANNY ROSE", "ブロードウェイのダニー・ローズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "W・アレンがブロードウェイに生きる芸人たちの姿を、人情味たっぷりに描いた佳作。アレンは売れない芸人のマネージャーに扮し、自分の抱えている歌手の恋を実らせようと奔走する。かつてはスターだった歌手を演じたN・A・フォルテがいい味を出している。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAKESHIS'", "TAKESHIS'", "", "", "http://image.eiga.k-img.com/images/movie/1191/315x210.jpg?1746", 107, "北野武が監督・脚本・編集・主演の4役をこなして、北野武とビートたけしの関係性を描く。テレビや映画で活躍するセレブ、ビートたけし。彼にそっくりな北野武はうれない役者で、オーディションを受けても落ちてばかりで、コンビニの店員をしていた。が、そんな北野がビートたけしと出会ったことから、たけしが演じる映画の世界へと迷い込んでいく。寺島進らたけし映画の常連が出演。05年ベネチア国際映画祭正式出品作。", "2005-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("快傑黒頭巾", "快傑黒頭巾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "幕府が外国から買い入れた新式銃の設計図をめぐり、これを奪おうとする倒幕派と、それを助ける黒頭巾の活躍を描く。ここで黒頭巾は、易者の天命堂をはじめ、天命堂の弟・艶歌師与作、乞食、火傷男、船頭、幕府の要人などに変装する。この作品からカラー作品になり、アクションも派手さを増した。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAGECOACH", "駅馬車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1885年、アリゾナからニューメキシコへ９人の乗客を乗せた駅馬車が疾走していた。突如として襲い来るアパッチの一団とそれによる激しい銃撃戦が巻き起こる。西部劇の名作をW・ネルソンらの人気スターでリメイク。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHO’LL STOP THE RAIN？", "ドッグ・ソルジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "1960年代初め、イギリスの“怒れる若者たち”の一人だったカレル・ライス監督が、「熱い賭け　ザ・ギャンブラー」に続いてアメリカで撮った、ヘビーなアクション映画。輸送船の船員レイは、海兵隊時代の友人で、今はベトナム従軍記者のジョンからヘロインの密輸を頼まれる。サンフランシスコに着いたレイは、ジョンの妻マージに連絡を取るが彼女は何も知らない。ヘロインを狙って組織がレイを襲ってくる。あくまでジョンとの約束を守ろうとするレイは、マージを伴って逃走し、最後はニューメキシコ山中での攻防戦となる。映画編集の著作もあるライス監督らしく、山中の銃撃戦の編集で複雑な音響が見事に処理されている。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAMORRA", "殺しのギャンブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ナポリを中心に根を張るギャング組織“カモーラ”の中でのし上がっていく青年の姿を描いた作品。貧民街育ちのトニーノは、“カモーラ”のボス、カペーチェにけんかの腕を認められ、運転手として雇われる。やがて彼は、用心棒、賭場の支配人、と次々に出世していくのだが……。カペーチェの情婦役にJ・セバーグが扮している。", "1973-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白日夢", "白日夢", "", "", "http://image.eiga.k-img.com/images/movie/54731/315x210.jpg?4248", 80, "武智鉄二が谷崎潤一郎の同名戯曲を3度にわたり映画化した官能ドラマのリメイクで、ピンク映画界の鬼才・いまおかしんじと、過去の「白日夢」でヒロインを演じた愛染恭子が監督を務める。派出所で居眠りをしていた警官の倉橋は、拳銃で撃たれる悪夢にうなされていた。空き巣の通報で目を覚ました倉橋は、急いで被害のあったアパートに向かうが、そのアパートが先ほどの悪夢と同じ現場であることに気付く。そこに住む清楚な女性、千枝子から被害状況を聞く倉橋だったが……。", "2009-09-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN ZOO，LA NUIT", "ナイト・ズー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "二年の刑期を終えて帰還した息子マルセルをかたくなに拒絶する父。だが父の命は、もういくばくもなかった。ある夜、父の夢（＝猛獣狩り）を叶えてやろうと思い立ったマルセルは……。フィルム・ノワール風に描かれる、静かで深遠な人間ドラマ。", "1987-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長三国志・第一部", "次郎長三国志・第一部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "マキノ雅広監督が東宝の９部作に続いて再び鶴田浩二の清水次郎長で映画化した“次郎長三国志”シリーズの第１部。清水港きっての暴れん坊・次郎長のきっぷに惚れた桶屋の鬼吉、関東綱五郎、大政そして増川仙右衛門が、次々と彼の子分になる。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Movie　Box-iｎg", "Movie　Box-iｎg", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "2002年の函館港イルミナシオン映画祭の“シナリオ大賞”受賞作の短編シナリオ３本を新鋭監督３人が映画化。作品はそれぞれ、風変わりな４人が殺し屋に怯えて走りまくる「Run-ing」、ところ構わずクルクル回っている小学生が主人公の「自転少年」、人気お笑いコンビ“シャカ”の大熊啓誉が主演する青春映画「巡査と夏服」と、いずれ劣らぬドギつい個性に満ちた意欲作だ。。", "2004-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sound Horizon 7th Story Concert 「Marchen」　キミが笑っている、眩いその時代に…　劇場用スペシャル3Dバージョン", "Sound Horizon 7th Story Concert 「Marchen」　キミが笑っている、眩いその時代に…　劇場用スペシャル3Dバージョン", "", "", "http://image.eiga.k-img.com/images/movie/56201/315x210.jpg?1597", 109, "サウンドクリエイターのRevoを中心とした音楽ユニット「Sound Horizon」が2010年12月に行ったコンサートの模様を収録したDVDを、発売に先がけて3Dで劇場上映。", "2011-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夾辺溝", "無言歌", "", "", "http://image.eiga.k-img.com/images/movie/57121/315x210.jpg?2731", 109, "文化大革命前の中国で行われた反体制狩り「反右派闘争」の悲劇を描く。1956年、中国共産党の毛沢東は、党に対する批判を受け入れる「百花斉放百家争鳴」運動を推進した。これにより国民からさまざまな意見がうまれるが、毛沢東は翌年に方針を転換。党を批判した人々を反体制者として容赦なく粛清していった。60年、中国西部ゴビ砂漠の収容所に、上海からひとりの女性がやってくる。捕らえられた夫に会いたいとひたすら懇願する彼女の声が、男たちの心に変化をもたらしていく。", "2011-12-17", "6,2,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("7月24日通りのクリスマス", "7月24日通りのクリスマス", "", "", "http://image.eiga.k-img.com/images/movie/33721/315x210.jpg?3486", 109, "「電車男」の村上正典監督・金子ありさ脚本のコンビが贈るロマンティック・コメディ。妄想の中でポルトガルのリスボンに暮らし、周囲の男性に“自分だけの王子様ランキング”をつけて楽しんでいる地味で目立たない女の子が、クリスマスを前に憧れの先輩との恋に奮闘する姿を描く。「電車男」では主人公が憧れる女性・エルメスを演じた中谷美紀が、本作では正反対の冴えないヒロインを演じる。憧れの先輩に扮するのは実力派俳優の大沢たかお。", "2006-11-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エロスは甘き香り", "エロスは甘き香り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "桃井かおり演ずる服飾デザイナーの家で奇妙な共同生活を送る青年男女４人の姿を描いた、藤田敏八のロマンポルノ第３作。浩一、昭の二人は仕事にあぶれ、それぞれ悦子、雪絵のヒモとしてこの家に居候。だが浩一はある日、雪絵に手を出し、それ以来この共同体的生活に微妙なヒビが入り始める……。藤田敏八ならではの根無し草的青春ドラマで、脚本は「八月はエロスの匂い」の大和屋竺。デザイナー・悦子役の桃井、ホステス・雪絵役の伊佐山ひろ子の好演が光る。長谷川和彦が助監督についている。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東海道四谷怪談", "東海道四谷怪談", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "何度も映画化されている鶴屋南北の『東海道四谷怪談』を、怪談映画の巨匠・中川信夫が映画化した極めつけ。浪人・民谷伊右衛門は、身持ちの悪さを理由にお岩との仲を引き裂かれたのを恨みに思い、お岩の父・四谷左門と佐藤彦兵衛を殺す。２年後、伊右衛門はお岩と夫婦になったが、すでに二人の仲は冷え切っている。たまたま悪旗本に絡まれている伊藤喜兵衛の娘・お梅を救ったことから、伊右衛門に養子縁組と仕官の道が開けてくる。お岩を邪魔に思った伊右衛門は、お岩に毒を盛り、あんまの宅悦との不義を理由に殺してしまう。晴れてお梅と祝儀をあげた伊右衛門だが、その夜からお岩の亡霊が現れ、ついには呪い殺される。中川の演出は、当時の国産カラーの不十分さを補って余りある様式美を見せ、寺で仲間の直助が伊右衛門に斬られるシーンでは、直助が倒れ込む畳が一瞬、沼となるなど鮮烈なイメージを展開した。そのほか、冒頭の移動撮影やお岩が毒を飲むシーンなどは忘れがたい。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNA STAGIONE ALL’INFERNO", "ランボー 地獄の季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "19世紀フランスの天才詩人、アルチュール・ランボーのアフリカ時代に焦点を当てた伝記映画。詩人ヴェルレーヌとの共同生活が破綻した後、ランボーは詩を捨て、アフリカに渡って武器商人となる。演出は水準を保ち、ランボーに扮したスタンプの演技も優れている。", "1971-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬夜叉／紅蓮〈ぐれん〉の蓬莱島〈ほうらいじま〉", "犬夜叉／紅蓮〈ぐれん〉の蓬莱島〈ほうらいじま〉", "", "", "http://ecx.images-amazon.com/images/I/312W6RS4GCL._SL210_.jpg", 86, "週刊少年サンデーの人気漫画『犬夜叉』の劇場版シリーズ第４作。50年に一度現れるという幻の“蓬莱島”を舞台に、シリーズの集大成とも呼べる壮大なスケールのストーリーが展開する。最強の敵、四闘神との壮絶な死闘の末に明かされる、犬夜叉たちの知られざる過去。果てしなく続く戦いの呪縛を断ち切るべく、犬夜叉たちが自身の運命に立ち向かう。犬夜叉が桔梗に封印される50年前。二人は幻の“蓬莱島”で妖怪たちと死闘を繰り広げていた。そして今、再び現れた蓬莱島では、彼らの過去を知る半妖の子供たちが助けを求めていた。", "2004-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("30 Minutes or Less", "ピザボーイ　史上最凶のご注文", "", "", "http://image.eiga.k-img.com/images/movie/56751/315x210.jpg?3879", 82, "「ゾンビランド」のルーベン・フライシャー監督とジェシー・アイゼンバーグが再タッグを組んだコメディ。30分の配達時間からいつも遅れてしまうピザ配達人のニックは、ある日、銀行強盗を企てる2人組から注文を受けてピザを届けたところ、2人組の計画に巻き込まれてしまう。10時間で爆発するという時限爆弾を背負わされ、強盗をしなければならなくなったニックは、絶交中のインド人の親友チェットに頼み込み即席強盗コンビを結成するが……。ベン・スティラーがプロデューサーを務める。相棒のチェック役にアメリカの人気コメディアン、アジズ・アンサリ。", "2011-12-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガラスの中の少女", "ガラスの中の少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "中学時代、いたずらでラブレターを交わしたことがあった靖代と陽一。やがて再会し、本気で愛し合うようになる二人だが、靖代の厳格な父親が、二人の交際を反対し……。少年少女の純愛を描いた日活青春映画の佳作。吉永小百合の記念すべき初主演映画であり、その初々しさも大きな見どころだろう。また共演の浜田光夫もこれが本格デビューとなり、やがて両者は数多くのコンビ作を連打し、日活青春映画の顔となっていった。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Johnny English Reborn", "ジョニー・イングリッシュ　気休めの報酬", "", "", "http://image.eiga.k-img.com/images/movie/56721/315x210.jpg?4443", 101, "「Mr.ビーン」として世界的に知られるイギリスの人気コメディアン、ローワン・アトキンソンが、「007」のジェームズ・ボンドを彷彿とさせる諜報部員に扮して繰り広げるスパイコメディ「ジョニー・イングリッシュ」（2003）のシリーズ第2作。祖国の危機を救い、諜報機関「MI：7」のエースとなったジョニー・イングリッシュだったが、再びヘマを犯してしまい自信を喪失。チベットの僧院で引きこもり生活を送っていた。そんな折、英中首相会談に出席予定の中国首相を暗殺する動きがあることから、イングリッシュに情報収集と暗殺計画阻止の命令が下るが……。イングリッシュに振り回される上司役に「X-ファイル」のジリアン・アンダーソン、ヒロインのケイト役にロザムンド・パイクが扮する。", "2012-01-21", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELECTRA", "エレクトラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ユウリピデスの高名な悲劇をM・カコヤニスが映像化したもの。国王アガメムノンは、妃とその不倫の相手である家臣アエギシウスに暗殺される。オレステス王子とエレクトラ姫は苦悩に満ちた歳月を送るが、やがて復讐のチャンスをつかみ……。サロニカ映画祭最優秀作品賞ほか多数の賞を受賞した名作。", "1961-01-01", "27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shadow Conspiracy", "ザ・ターゲット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "　学生時代の恩師殺害事件から、アメリカ政府の内部で陰謀が進められていることに気づく大統領補佐官ビショップ。やがて彼は黒幕のひとりが信頼を寄せていた首席補佐官コンラッドであることを察知。コンラッドら政府の主要スタッフは大統領暗殺計画を実行しようとしていたのだ。ビショップは命を狙われながらも、新聞記者アマンダとともに、計画を食い止めようとするが……。若き補佐官の活躍を描いたサスペンス。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏目漱石の三四郎", "夏目漱石の三四郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "異才・中川信夫監督が1941年の「虞美人草」に続き再び夏目漱石の名作に挑んだ作品。三四郎に山田真二、美彌子に八千草薫が扮し、大正時代の若者のひそやかな愛と、その挫折を描く。名手・玉井正夫による撮影が素晴らしく、夏目文学の風趣を香り豊かに再現している。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Potiche", "しあわせの雨傘", "", "", "http://image.eiga.k-img.com/images/movie/55721/315x210.jpg?2336", 0, "「8人の女たち」のフランソワ・オゾン監督とカトリーヌ・ドヌーブが再びタッグを組んだコメディ。ブルジョワ主婦のスザンヌは、雨傘工場を経営する亭主関白な夫ロベールと優雅で退屈な毎日をおくっていた。ある日、心臓発作で倒れたロベールに代わり、スザンヌが工場の運営を任されてしまう。彼女は主婦ならではの感性で、傾きかけていた工場を見事に立て直すが……。", "2011-01-08", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK EMANUELLE", "愛のエマニエル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "世界的にも有名なアメリカの美人カメラマン、エマニエル。そんな彼女がある日、取材のため急きょアフリカのケニアに行くことになり……。神秘の国、ケニアを舞台にしたエロティックなラブ・ロマンス。", "1975-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ME´ANCOLIE BABY", "メランコリー・ベイビー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "フランスの大女優J・バーキン。彼女がキュートなセックス・シンボルから知性派女優への脱皮を果たした作品。若くてセクシーな人妻オルガが平穏無事な生活を捨てて自立の道へと歩み出すまでを、女流監督C・ガブスが繊細に綴る。J・L・トランティニャンの好演も光る。", "1979-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キャプテン翼　世界大決戦！！　Jr.ワールドカップ", "キャプテン翼　世界大決戦！！　Jr.ワールドカップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "原作コミック、TVアニメ、劇場版と続いた、80年代半ばに大ヒットした人気アニメのシリーズ最終話。本作では原作者が新たに加えたキャラクターが登場し、シリーズ中最高の盛り上がりを見せながらエンディングへ着地。全日本ジュニア選抜チームがJr.ワールドカップに出場し、欧州、アメリカ、南米の各チームと優勝を目指して激戦を展開する。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("椿姫", "椿姫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "山田＝朝間のコンビがオペラの名作『椿姫』をモチーフにして、一人のオペラ好きのタクシー運転手の回想を通して薄幸な芸者との恋物語が語られる。北海道の大自然を背景にした丹念な画面作り。男やもめのタクシー運転手・加藤健一、芸者・松坂慶子ともに好演。日本アカデミー賞で音楽賞を受賞。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOMICILE CONJUGAL", "家庭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "F・トリュフォーがJ・P・レオーを主演に自伝的要素を盛り込んで描いた一連の“アントワーヌ・ドワネルもの”の第４作。アントワーヌはバイオリン教師のクリスティーヌと結婚し、幸せな日々を送っていた。二人の間には子供も生まれるが、その頃、アントワーヌは、ある偶然から日本人女性キョーコと出会う。女好きのアントワーヌは彼女に惹かれ、親しくなるが、夫の浮気に気付いたクリスティーヌは彼を家から追い出してしまう……。発表当時、日本では公開されなかったが、その原因として日本人女性の描き方に問題があるからともいわれている。地味ながら味わい深い秀作である。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOW　BOAT", "ショウ・ボート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "有名な舞台劇の二度目の映画化。ミシシッピ川をめぐってショーを上演している船。船長の娘マグノリアは賭博師ゲイロードと恋に落ちて結婚。だが、旅芸人の生活よりもバクチが好きなゲイロードは船を離れるが、やがて運も尽きてマグノリアの前から姿を消した。幼い娘を抱えるマグノリアは再びショーの世界に戻り……。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALONG CAME A SPIDER", "スパイダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1997年公開の「コレクター」に続き、ジェームズ・パターソンの小説『アレックス・クロス』シリーズの一編を映画化したミステリー。２度目のクロス役となるM・フリーマンの名演とL・タマホリのタイトな演出、J・ゴールドスミスの音楽が先読み不可能なドラマをスリリングに盛り立て、前作を上回る出来ばえとなった。ワシントンDCの私立学校から大統領の令嬢が誘拐されるという大事件が発生。犯人のソーンジから挑戦状を叩きつけられた敏腕刑事クロスは、女性シークレット・サービス、ジェジーの協力を得て犯行の意図を読み取っていくが、この事件にはもう一つの驚くべき陰謀が仕組まれていた……。ビデオタイトルは「スパイダー／コレクター２」。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("0093 女王陛下の草刈正雄", "0093 女王陛下の草刈正雄", "", "", "http://image.eiga.k-img.com/images/movie/34251/315x210.jpg?3834", 88, "二枚目俳優として知られる草刈正雄が本人役で主演を務めるおバカなスパイ・アクション。大スター草刈正雄には、スパイという裏の顔があった。コードナンバー“0093”として、女王陛下のために諜報活動を行っていたのだ。ある日、女王からの指令を受けた草刈は、テレビの電波を通して人々を洗脳しようと企むIT企業社長・三輪を阻止するべく立ち上がるが……。草刈の実娘である麻有が、同じく本人役で出演。", "2007-10-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BILL ＆ TED’S EXCELLENT ADVENTURE", "ビルとテッドの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "歴史の授業で落第寸前の成績をとった二人の高校生が、電話ボックス型のタイムマシンに乗って、原始時代から西暦2688年までを駆けめぐる。そこで出会ったソクラテス、ナポレオン、ベートーベンなどを現代に呼びよせて試験合格をねらおうとする。奇想天外なストーリーにロック・サウンドをかぶせた新感覚コメディ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GREEN CARD", "グリーン・カード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "俊英、P・ウィアーによる、ロマンティックなラブ・ストーリー。園芸家のブロンティは、温室付きアパートの入居資格をとるために、フランス人のジョージは、アメリカでの労働許可証をとるために、二人は紙キレだけの結婚をする。だが、その二人の結婚を入国管理局は怪しみ、面接調査するという。昨日まで見ず知らずの二人は、最初は互いの身を守るために同居。そのうち、本当に恋が芽生えて……。G・ドパルデューが、見かけは無骨だが、繊細な心を持つジョージを好演。「セックスと嘘とビデオテープ」でスターになった、A・マクダウェルも、このフランスの名優と互角に渡り合った。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こころの山脈", "こころの山脈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1963年のオムニバス「嘘」を撮り上げたあと、病に倒れていた吉村公三郎が３年ぶりに発表した再帰第１作。福島県本宮町本宮小学校のPTA、教師たちによる映画教育運動の一環として吉村、新藤による近代映協の協力のもとで製作された。話は、臨時教員として教壇に立ったある母親が、先生やPTAに批判されながらも誠心誠意教育を続け、ついにはそれが認められていくというもの。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOT AS A STRANGER", "見知らぬ人でなく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "S・クレイマーの初監督作にあたるヒューマン・ドラマ。子供のころから医者に憧れを抱くルカスは、医学生として学資が続かなくなり、金持ちの看護婦クリスチナと恋に落ちたと見せかけて結婚。医者として独り立ちするが、やがて妖艶な未亡人ハリエットに惹かれていき……。R・ミッチャムが、苦悩する医師を好演。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("波羅羯諦／COME, COME, COME UPWARD！", "ハラギャティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "「シバジ」の監督、主演コンビによる骨太の女性映画。自分の言動から、憧れていた教師が罷免されたことに悲嘆し、尼僧となったスンニョ。それ以後、彼女は愛した男たちが次々と死んでいくという悲運の人生を歩み始める。愛を注いだ者たちをすべて失いながら、たくましく生き抜くヒロインをカン・スヨンが熱演。", "1989-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("必殺！", "必殺！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "TVのスタッフ・キャストを使った映画化第１作。TVシリーズ600本記念作でもある。江戸に謎の殺し屋・庄兵衛一味が現れ、商売仇の仕事人を次々と殺していく。主水たちは、これに立ち向かう。ラスト30分近くにわたる殺し合いはいささか冗漫。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子どものころ戦争があった", "子どものころ戦争があった", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "父がアメリカ人で対日放送を受け持っているため、土蔵に隠された混血児の少女と、柿の木に登って話しているうちに仲良くなった少年の、幼いがさわやかな恋を描く。昭和20年の福島県を背景に、つつましく反戦の意を打ち出した斎藤貞郎監督のデビュー作。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHAT DREAMS MAY COME", "奇蹟の輝き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "「心の地図」のV・ウォードが、死後の世界を舞台に“永遠の愛”を描く力作。４年前に事故で二人の子供を失った医師クリスと画家アニーの夫婦は、強い愛で支え合い、悲しみを乗り越えてきた。ところが、クリスが事故死し、彼の魂は天国へと導かれていく。そこは、アニーの風景画が広がる美しい場所だった。一方、家族を失った悲しみに絶えきれず自殺してしまったアニーは地獄へと落ちる。クリスは彼女を救うため、危険な旅に出るのだが……。SFXを駆使してヴィジュアル化した、幻想的で崇高な雰囲気が漂う天国と、暗く不安をかき立てる地獄の圧倒的な映像の中に、魂で結ばれた夫婦愛の美しさを描き出す感動作。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Consenting Adults", "隣人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "隣に越してきたエディと仲良くなり、一晩限りの夫婦交換をすることになったリチャード。しかし、官能の夜が終わると、エディの妻は殺され、リチャードが犯人として捕まってしまう……。社会派パクラ監督が描く、色と欲が交錯する大人のサスペンス。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イヌゴエ　幸せの肉球", "イヌゴエ　幸せの肉球", "", "", "http://ecx.images-amazon.com/images/I/518HbytSNzL._SL210_.jpg", 100, "言葉を話す犬と人間の交流を描き大ヒットしたコメディ「イヌゴエ」の第2弾。あまりの頼りなさに、同棲中の恋人から愛想を尽かされてしまった青年・凌。落ち込んで街をさまよう彼の前に、彼女と全く同じ声で話すフレンチブルドッグのぺスが現れる。凌はペスの声を頼りに彼女の実家を探す旅に出るが……。主演はテレビドラマ「花より男子」で注目された阿部力。前作で主人公を演じた山本浩司が脚本を担当している。", "2006-12-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH WARMED OVER", "マイドク いかにしてマイケルはドクター・ハウエルと改造人間軍団に頭蓋骨病院で戦いを挑んだか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "珍しいニュージーランド製のスプラッタ・ホラー。タイトル通り、主人公がマッド・サイエンティスト、ハウエル博士と戦うという内容で、見どころは後半の派手なバイオレンス・シーン。ストーリーに捉われない、当時28歳のD・ブライス監督の特異な感性が受けたか、第14回パリ国際ファンタスティック映画祭では、グランプリを受賞した。ちなみに原題は“二日酔いで頭がクラクラ”の意。", "1984-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("安藤昇のわが逃亡とSEXの記録", "安藤昇のわが逃亡とSEXの記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "安藤昇主演の実録タッチの異色作。昭和３年６月11日、安藤昇は組員に命じて極東船舶社長・早川哲司を襲撃する。警視庁は大捜査網を始動。安藤は組員全員に徹底的な逃亡を命じ、自分は都内の愛人宅を転々とし……。34日目についに逮捕されるまでの刹那的なセックスを、「実録阿部定」などにっかつロマンポルノでならした田中登監督が濃厚に演出している。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅桜", "修羅桜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "松竹映画3000本記念作品。将軍家斉の治世。老中・大野備前守は、幕府転覆を企て、その資金として、大阪城に隠してある黄金300貫を江戸まで、岩波半兵衛に運ばせようとする。半兵衛は道中で黄金の使い道を不審に思い、どこかへ隠した。半兵衛は幽閉され、その息子・又四郎は、消えた父と黄金を探すが……。次々に登場するスターの顔ぶれも楽しい。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Masked and Anonymous", "ボブ・ディランの頭のなか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ロック界の偉大なカリスマ、ボブ・ディランが、脚本と主演を兼ねた風変わりな近未来ファンタジー。軍事政権が支配する架空の独裁国家を舞台に、カリスマミュージシャンの復帰コンサートと政権交代の珍騒動が交錯する。ディラン人気を証明するかのように、超豪華なキャストが集結。ジェフ・ブリッジス、ジェシカ・ラング、ペネロペ・クルス、ミッキー・ロークにエド・ハリスら、名優や大スターたちの共演が見もの。ディランの演奏も満載され、クライマックスのライブシーンは鳥肌モノ。内戦が続く独裁国家。カリスマロッカーのジャックが、チャリティーコンサートに出演するために刑務所から出獄。ところが政権が突然交替し、ライブ会場には軍隊が差し向けられ……。", "2005-07-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HITCHER", "ヒッチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "殺人ヒッチハイカーの恐怖を描いた快作。いかにもB級スリラーの設定を救っているのは、E・レッドの丹念に書き込まれた脚本とR・ハーモンの静かに不条理へ突入していく緊迫の演出。その非情とも言うべき展開に加え、被害者に“精神的関係”さえ迫る偏執狂の魂を見事に体現したR・ハウアーの名演が忘れがたい。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRIKING BACK", "放課後", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "学園を舞台にして、校内暴力を中心に描くバイオレンス青春映画。高校生兄妹と不良グループが嫌がらせのやり合いの末、最後には対決するという図式でストーリが展開する。両親の死をきっかけに、叔父に引き取られ田舎の学校に転校してきた兄妹。しかし、妹に目を付けた上級生の不良たちが妹をストークし、嫌がらせを繰り返す。そこで兄は“目には目を”の対抗策に出る。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROOSTER COGBURN", "オレゴン魂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「勇気ある追跡」（1968）の大酒飲みの片眼の保安官役でアカデミー主演男優賞に輝いたJ・ウェインが、再び同じ役柄に扮した続編。派手な人殺しをやり過ぎて保安官バッジを取り上げられたルースターは、友人ブリードとその一味が起こしたニトログリセリン強奪事件によって、再び任務に復帰。彼ら一味の追跡を開始する。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GOOD SON", "危険な遊び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「ホーム・アローン」で世界中の人気者になったM・カルキンが、悪役に挑戦したサスペンス・スリラー。母を失った少年マークは叔父夫婦のもとに預けられる。仲良くなったいとこのヘンリーはいたずら好きだったが、やがて度を越した危険な遊びになっていく。マークはヘンリーが殺人を計画していると信じるようになり……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どうぶつ宝島", "どうぶつ宝島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "児童文学の名作『宝島』を、主役の少年少女を動物に設定を変えてアニメ化した痛快冒険活劇。海賊フリント船長の宝のありかが描かれた地図を手に入れたジム少年。ネズミのクランとともに自作の樽船で宝島目指して旅立つ。だが、ブタのシルバー船長率いる海賊船に捕まり、宝をめぐる争いに巻き込まれる。宮崎駿がアイデア・構成に参加している。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・とんかつ一代", "喜劇・とんかつ一代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "東京の下町にある実在の名門とんかつ屋を舞台に、豪華キャストで綴る風俗喜劇。それぞれの主役スターによる短いエピソードが並列的に進行していく構成になっている。“駅前”シリーズなどの東宝喜劇の得意のパターンを踏襲した作品といえるだろう。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガキ帝国", "ガキ帝国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "昭和42年、万博を３年後に控えた大阪を舞台に、ケンカと遊びに明け暮れる少年たちの青春を鮮烈に描いた一編。少年院帰りのリュウは仲間のケン、チャボとともに、大阪をとりしまるキタの北神同盟、ミナミのホープ会の二大グループの抗争の間で、徒党を組まないことをモットーにつっぱって暮らしていく。が、ついに壊滅状態に陥ったホープ会から頭になってくれと頼まれたリュウとチャボはその話を引き受けてしまい、ケンは二人のもとを離れていくのだった……。当時の風俗をたくみに取り入れながら、少年たちの生活をスピーディーにスケッチした好編で、井筒和幸の名が知れわたった代表作。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’HISTOIRE D’ADE｀LE H.", "アデルの恋の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "19世紀の中頃。初恋の男を愛し、その情念だけで生き抜いた文豪ヴィクトル・ユゴーの次女、アデルの半生を描く恋と冒険の物語。初恋の相手ピンソンを追い、異境の地までやって来たアデル。しかし、彼女の狂気じみた行動はエスカレートし……。“恋”に取り憑かれ、男を追い求めるアデルを当時20歳のアジャーニが熱演、鮮烈な印象を与えた。DVDは「フランソワー・トリュフォー DVDコレクション」に収録。", "1975-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOHNS", "ジョンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ストリート・ハスラー（男娼）として生きる若者たちの、行き詰まった日常を描く哀切な青春ドラマ。男娼のジョンは、誕生日のために貯めこんでいた金を盗まれる。彼に思いを寄せていた新米のストリート・ハスラー、ドナーは金の工面に奔走。だが、そんなドナーを、ジョンは冷たく突き放つ……。仲良く振る舞いながらも、時に欺き、傷つけ合う、若者たちの孤独が鮮烈。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOIS PLACES POUR LE", "想い出のマルセイユ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「シェルブールの雨傘」「ロシュフォールの恋人たち」などの傑作ミュージカルを生み出した名コンビ、J・ドゥミとM・ルグランが組んだ、ユニークなミュージカル映画。ちなみに振付けはマイケル・ジャクソンのミュージック・ビデオも手掛けたマイケル・ピーターズがあたっている。内容は偉大なシャンソン歌手、Y・モンタンが、彼自身の半自伝的物語を自分で演じるという型破りのもの。モンタンが育った港町マルセイユに戻り、ミュージカル公演の用意をしながら、彼の半生が回想されていくというロマンティック・ファンタジー。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CIEL SUR LA TE＾TE", "頭上の脅威", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "演習訓練も終了間近の空母クレマンソーに非常警報が発令された。空母は急旋回、警報の内容すら明らかにされないまま、空母に呼び戻され、スクランブル体制に入ったパイロットたちの間には重たい空気が流れるのだった。そんな中、偵察飛行中のパイロットが謎の飛行物体と接近遭遇。西側諸国は、これをソ連の衛星と断定し、ロケットを発射。全面戦争の危機を迎える。果たして、謎の飛行物体の正体は……。約48時間にわたる出来事を、ジェット戦闘機エタンダールのテイクオフ、ランディング・ショットを交え、ドキュメンタリーを思わせる緊張感に満ちた精密な映像で描く。", "1964-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大冒険家／THE　ADVENTURERS", "復讐のプレリュード～大冒険家～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "少年時代に目の前で両親を殺された主人公アンディ。20年後、空軍パイロットとなった彼は復讐に燃え、仇である香港の麻薬王に接近する。しかし、その情婦ボニーと知り合い危険な恋に落ちてしまう……。A・ラウ主演による華麗なアクション・ロマン。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD RECKONING", "大いなる別れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1980年に初めて日本で公開された、ボガート主演のハードボイルドもの。殺された親友の過去を単身調査していたリップは、彼が殺人罪で告発されていたことを知る。粋なセリフ、ローレン・バコール風の悪女と「マルタの鷹」のような趣で楽しめる。ボガートうってつけの役柄である。DVDは「フィルム・ノワール コレクション(1)」に収録。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El Bano Del Papa", "法王のトイレット", "", "", "http://image.eiga.k-img.com/images/movie/54061/315x210.jpg?3274", 97, "第5回スペイン・ラテンアメリカ映画祭上映作品。実話をもとに、ウルグアイの田舎町を独特なユーモアで描いた切なくも暖かいヒューマンドラマ。1988年、ブラジル国境沿いのウルグアイの寒村にローマ法王がやって来ることになり、貧しい村人達はたった1日の観光客相手の商売に知恵を絞り始める。妻と娘と3人で暮らすベトも、自転車で不法に品物を運ぶその日暮らしの生活を変えるため、ある商売を思いつくが……。", "2008-09-15", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロストパラダイス・イン・トーキョー", "ロストパラダイス・イン・トーキョー", "", "", "http://image.eiga.k-img.com/images/movie/55621/315x210.jpg?1458", 115, "社会の片隅で今を必死に生きる3人の男女が小さな希望を見つけるまでを描き、国内外の映画祭で注目を集めた人間ドラマ。両親を亡くし、知的障害を持つ兄・実生と2人で暮らすことになった幹生。兄の性欲処理のため、部屋にときどき風俗嬢を呼んでいる幹生は、秋葉原でアイドル活動を続けながら風俗で働くマリンと出会う。メガホンを取るのは、若松孝二監督の下で演出を学び、本作が長編デビュー作となる新鋭・白石和彌。", "2010-09-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bus Palladium", "バス・パラディアム（原題）", "", "", "http://image.eiga.k-img.com/images/movie/55341/315x210.jpg?1519", 100, "80年代のパリを舞台にした青春ドラマ。幼なじみのルカ、マニュ、フィリップ、ジャコブ、マリオの5人は、ロックバンド「ラスト」を結成する。バンドは徐々に人気を集め、レコード会社との契約も夢ではなくなるが、学業か音楽の道かで5人は悩み始める。そこにローラという可憐な少女が現れ、5人は揃ってローラに夢中になってしまう。フランス映画際2010にて上映。", "2010-03-20", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRAINED TO KILL", "リベンジャー 殺しの特訓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "陸軍の特殊部隊員であるマットとサムの異母兄弟は、家族の仇を討つべく日夜特訓を重ねていた。マットは正妻マーサの子で、サムは父エドが16年前にカンボジアから帰国した時に連れてきた愛人マイとの子だった。その時サムが赤ダイヤを持っていたことから部下たちが一家を襲撃し、二人が残ったのだった。ハードなアクションが見もの。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひめゆりの塔", "ひめゆりの塔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "戦後50年記念作品として作られた名作の再映画化。神山征二郎が原作『ひめゆりの塔をめぐる人々の手記』をもとに実在の人々をモデルとし、各エピソードも慎重に検証を重ねた上で再現、“真実に忠実”な「ひめゆりの塔」となっている。従軍前の少女たちの日常や半ば強制的に従軍させられていく過程から描き、“なぜ彼女たちが戦場へ向かったのか”を克明に記している。少女たちの死に至る数々のエピソードを積み重ねる一方、勇気を持って投降し“生きること”を選んだ者や、戦場に取り残されたものの、のちに米軍によって命を助けられた者のエピソードを加え、生きることの尊さをうたい上げている。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Essene", "エッセネ派", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若い季節", "若い季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "“飲むクリーム”や“液体口紅”など同じ製品が同時に出来あがってしまったプランタン化粧品とライバル会社のトレビアン化粧品。トレビアンの女社長は社内に産業スパイがいるのではと、給仕に調査を依頼し……。連続TVドラマの映画化。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BRIDGE AT REMAGEN", "レマゲン鉄橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "第二次大戦末期の1945年２月。アメリカ軍の歩兵大隊は、ライン河にかけられた最後の鉄橋レマゲン突破を目指して進攻していた。迎え討つドイツ軍精鋭部隊は、必死の反撃を試みる。ベテラン、ギラーミンの迫力ある戦闘シーンが見ものの戦争アクション。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新訳：今昔物語", "新訳：今昔物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "東京藝術大学大学院映像研究科の黒沢清・北野武ゼミ1期生が手がけた4編からなるオムニバス作品。平安時代に編さんされた説話集「今昔物語」をモチーフに、それぞれ大胆な解釈をくわえて現代風にアレンジ。人間ドラマ、サスペンス、ミステリー、アクションと多様なジャンルの作品がそろった。キャストには「紀子の食卓」のつぐみ、「呪怨」シリーズの柳ユーレイ、タレントのインリン・オブ・ジョイトイら個性ゆたかな顔ぶれが集結。", "2007-05-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Casque d'or", "肉体の冠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "19世紀末のパリを舞台に、実在した伝説の娼婦マリーをめぐる愛憎劇を描く。監督はフランスの名匠ジャック・ベッケル。美しいブロンドの髪を兜型に結っていることから、黄金の兜と呼ばれる娼婦マリー。彼女にはロランという情夫がいたが、彼への恋心は冷め、気まずくなっていた。ある日彼女は、カフェで仕事をしていた大工のマンダと恋に落ちる。激しい決闘の末、マンダはロランを殺してしまい、マリーを連れて逃亡するのだが……。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ならず者", "ならず者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "石井輝男監督と高倉健のコンビによるアクション映画。殺し屋、南条は仕事先の香港で麻薬の密輸に巻き込まれて香港の裏街を逃げまわる。前作「東京ギャング対香港ギャング」で香港の裏街を活写した石井輝男が、ここではさらに裏街へと踏み込んでいく。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市逆手斬り", "座頭市逆手斬り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "座頭市は牢内で、老ヤクザ・島蔵から無実を証明してくれる親分を訪ねてくれるように頼まれる。牢を出た市は親分を訪ねるが、その親分こそ島蔵に罪を着せた張本人であった。藤山寛美がニセ座頭市に扮して場面をさらう。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鐘の鳴る丘・ 隆太の巻／修吉の巻／クロの巻", "鐘の鳴る丘・ 隆太の巻／修吉の巻／クロの巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "戦後、まだ混乱さめやらぬ1947年。菊田一夫が、戦災孤児救済をテーマにラジオドラマとして書きあげた作品の映画化。佐田啓二をメインに据え、彼が扮する賀々見修平が、弟の修吉を探すうちに、様々な孤児と出会い、彼らが安心して暮らせる丘の家を築くまでを感動的に描く。終戦後という時制がポイントの作品だが、孤児たちに扮した創作座の少年たちの演技は、今見てもすがすがしい。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎あじさいの恋", "男はつらいよ・寅次郎あじさいの恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "京都である老人を助けた寅さん。実はこの老人、陶芸家の大先生だった。老人の家のお手伝い・かがりと仲よくなった寅さんは、彼女の失恋に同情するうちにいつしか心惹かれ、鎌倉のあじさい寺でデートしたまではよかったのだが……。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("尼寺(秘)物語", "尼寺(秘)物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "尼僧のエロスに迫る女性映画。尼寺、琳光寺に住む浄真尼は門跡尼に命じられ、本寺の総務長、覚全に本堂の修理の依頼に行って体を奪われてしまう。それからも、彼女は費用の無心をするたびに覚全にもてあそばれる。そんな浄真尼は、下女のはなと寺男のキクの奔放な愛の交わりをうらやましく思い、同時にキクにも好意を感じ始めていく。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Superhero Movie", "スーパーヒーロー　ムービー!!　最“笑”超人列伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOCIETY", "ソサエティー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ハリウッドで活躍する日本人SFXアーティスト、スクリーミング・マッド・ジョージが、その独特の悪夢的幻想センスで特殊効果を担当したホラー。自分の両親と妹が通う“社交界”の謎を突き止めようとする青年ビル。彼がついに知った“社交界”の意外な真相とは……？「死霊のしたたり２」のB・ユズナ監督のデビュー作。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少年黄飛鴻之鐡馬※／IRON　MONKEY", "ワンス・アポン・ア・タイム・　イン・チャイナ〈外伝〉　アイアンモンキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "19世紀の終り、汚職がはびこり、荒廃していた中国の満中王朝。漢方医のヤンは夜は金持ちや権力者から金を盗み、それを貧しい人たちに分け与えていた。彼は人々からアイアンモンキーと呼ばれていたが、役所は彼を捕まえるために諜報員のヤンを送り込むのだったが……。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少年H", "少年H", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "作家・妹尾河童の自伝的小説で、上下巻あわせて340万部を突破するベストセラーを、「ホタル」「鉄道員（ぽっぽや）」の降旗康男監督が映画化。太平洋戦争下という時代に翻弄されながらも、勇気や信念を貫いて生きた家族の激動の20年間を描き、実生活でも夫婦の水谷豊と伊藤蘭が夫婦役で映画初共演を果たした。昭和初期の神戸。名前のイニシャルから「H（エッチ）」と呼ばれる少年・肇は、好奇心と正義感が強く、厳しい軍事統制下で誰もが口をつぐむ中でも、おかしなことには疑問を呈していく。Hはリベラルな父と博愛精神に溢れる母に見守られ成長し、やがて戦争が終わり15歳になると独り立ちを決意する。", "2013-08-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("eiko〈エイコ〉", "eiko〈エイコ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "『HERO』など人気TVドラマを手掛けてきた加門幾生の映画監督デビュー作。キャッチセールスに引っかかって高額の買い物をしたり、ヒモの彼氏をお金目当てと気づかなかったり、人をすぐに信じてしまうエイコ。そんな純粋な女の子を麻生久美子がみずみずしく演じる。そして、エイコと奇妙な共同生活をするボケ気味老人に、沢田研二。異色の組み合わせで温かい気分の、ほのぼのワールドが展開する。エイコは、仕事帰りに声をかけられた女性の言葉を信じ、幸せになれるという指輪を買ってしまう。次の日、出社すると社長は給料未払いのまま夜逃げしていた。そこで社長の自宅を訪ねてみると、見知らぬ初老の男性に出迎えられ……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VOYAGE OF TERROR", "ボヤージ オブ テラー 死の航海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "死のウイルスが蔓延した豪華客船内の恐怖と、政府の陰謀を描くパニック映画。豪華客船オリオン・スター号で突然死したクルーから未知のウイルス、BL4エボラウイルスが発見される。乗り合わせた医師は感染者を隔離して治療に当たるが死者は続出、乗客は絶望感に襲われる。その頃、国家防衛顧問は国内への感染を恐れ、船の爆破を計画していた……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SECRET OF SANTA VITTORIA", "サンタ・ビットリアの秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "街の財源である100万本のワインを、ドイツ軍の掠奪から守ろうとするサンタ・ビットリア市の人々の活躍を描いたコメディ。機知にたけたガンコな市長をA・クインが好演。ドイツ軍との丁々発止のやりとりが、本編の見どころだ。果たして、彼らはワインを守りきれるのか？", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE MYSTE｀RE KOUMIKO", "不思議なクミコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 47, "世界各地の記録映画を撮り続ける映像作家C・マルケルが、一人の日本人女性との対話を軸に、日本を捉えたドキュメンタリー。オリンピックに沸く1964年の東京を背景に、フランス語を学ぶクミコという名の日本人女性が、監督の問いにたどたどしいフランス語で答えていく形で構成されている。カメラは東京の街の雑踏からそこに暮らす人々、現代風俗と伝統を映し込みながら、個性的に生きるクミコの姿を浮き彫りにしていく。映画評論家の山田宏一やフランス映画社社長の柴田俊らが助監督として参加しているのも興味深い。", "1965-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不良少年", "不良少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "それまで岩波映画社で記録映画を作り続けていた俊英・羽仁進の劇映画第１作である。原作は、この作品の主人公でもある浅井少年の手記『とべない翼』で羽仁自身が脚色している。チンピラ社会の序列を嫌う浅井は、収容された少年院の中でボス格の少年と対立し喧嘩する。院内での浅井のそんな生活を、ドキュメンタリー・タッチで描く秀作。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dear Pyongyang ディア・ピョンヤン", "Dear Pyongyang ディア・ピョンヤン", "", "", "http://image.eiga.k-img.com/images/movie/33621/315x210.jpg?4620", 107, "在日コリアン2世の映像作家ヤン・ヨンヒが、自らの家族を10年間にわたり撮り続けたドキュメンタリー。大阪市生野区で両親と暮らすヤン。朝鮮総連の幹部として人生を“祖国”北朝鮮に捧げた父は、30数年前に3人の息子たちを北朝鮮へ送り出した。しかし日本で生まれ育ったヤンは、そんな父の行動に違和感を感じていた……。両親やピョンヤンで暮らす兄の日常を映し出すことで、そこに潜む様々な問題や家族の絆を浮かび上がらせていく。", "2006-08-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霧の子午線", "霧の子午線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "高樹のぶ子の同名小説をもとに、強い絆で結ばれた女性二人の友情と愛の葛藤を描く岩下志麻と吉永小百合の初共演作。クローン病という難病に冒された沢田八重は、ちぎり絵師として第二の人生を送るため、学生時代の親友・鳥飼希代子のいる函館にやってきた。40代を迎えた二人は大学時代からの友人で、学生運動のリーダー・淡路新一郎をともに愛し、希代子は未婚の母となっていた。再会を喜ぶ二人だが、希代子は息子・光生が、死んだことになっている父親が生きているとの噂を聞き真実を問いただしてくるという悩みを打ち明け、八重に新一郎のことは話さないように頼む。そして今、地方紙の文化部・記者として働く希代子には、高尾耕平という後輩の恋人がいた。だがある日、ちぎり絵の本を出版することになった八重を取材した耕平は、彼女の魅力に惹かれてしまうのだが……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緊急呼出し エマージェンシー・コール", "緊急呼出し エマージェンシー・コール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "フィリピンはマニラのスラム街にある国立病院で、日夜懸命に病気・貧困・無知と闘う日本人医師が、様々な体験を経て成長していく姿を描いたヒューマン・ドラマ。太田靖之の実体験に基づく小説『緊急呼び出し』を、自らも医大出身で「ヒポクラテスたち」など医学ものが多い大森一樹が映画化。邦画初のオール・フィリピン・ロケを敢行、スラムの実態などがリアルに捉えられ、フィクションの枠組みを超えた感動を呼ぶ。現地の人気女優を共演に孤軍奮闘する真田広之の熱演が光る。JAC時代の彼の後輩で、現在はシンシア・ラスターの名で香港映画界で活躍する大島由加里がゲスト出演。ビデオタイトルは「エマージェンシー・コール　緊急呼出し」。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Superstar Mr. Gam", "スーパースター☆カム・サヨン", "", "", "http://image.eiga.k-img.com/images/movie/53041/315x210.jpg?1431", 113, "韓国プロ野球創成期に実在した左腕投手カム・サヨンをモデルにした感動作。プロ野球チームの入団テストに合格したカム・サヨン。ところがチームは負けてばかりで、彼はいつしか敗戦処理専門の投手になってしまう。そんなある日、強豪チームとの対戦で、ついにサヨンが先発を務めることが決まる。5年間で1勝15敗1セーブという成績に終わったものの、野球を愛し夢を追い続けたカム・サヨンを、「ひとまず走れ！」のイ・ボムスが演じる。", "2006-09-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREEDOM FIGHTERS", "勇者の大地 フリーダム・ファイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "アフリカでダム建設反対の部族弾圧のため、傭兵部隊が組織される。しかしその残虐なやり方に疑問を持ち、雇い主の企みを知った二人が、部族解放側へとまわる。現代アフリカの弾圧的な政治状況を南アフリカの実写映像を盛り込んで、アクション・タッチで描いた作品。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDERWORLD", "アンダーワールド", "", "", "http://ecx.images-amazon.com/images/I/51HsXWNgvBL._SL210_.jpg", 121, "「インデペンデンス・デイ」などのグラフィック・アーティストとして活躍した映像界の鬼才、レン・ワイズマンの長編初監督作品。吸血鬼と狼男族の種族の存亡をめぐる壮絶な死闘に終止符を打つべく闘う、ヴァンパイアの女戦士を描いたアクション・ホラー。「パール・ハーバー」で注目を集めたケイト・ベッキンセールが、清純なイメージを一新、スタイリッシュな女戦士役に体当たりで挑む。長い間死闘を続けてきた吸血鬼と狼男族。その闘いを終わらせるために送り込まれたヴァンパイアの女戦士セリーンは、狼男族が人間の青年医師マイケルを探していることを知る。不審に感じたセリーンは、マイケルを追跡。ところが、狼男族の奇襲に遭い、マイケルが肩をかまれてしまう。。", "2003-11-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MICHAEL COLLINS", "マイケル・コリンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "アイルランドの伝説的な革命の闘士マイケル・コリンズの生涯にスポットを当てた、俊英N・ジョーダンの意欲作。1916年、アイルランドの革命軍はイギリス軍の強大な軍備の前に敗北。逮捕された兵士コリンズは釈放後、独立運動の新しい担い手としてゲリラ活動に精を出す。彼の強力な指導によって、英国は苦戦を強いられた。やがてコリンズは英国と休戦協定を結ぶが、ここで結ばれた条約をめぐってアイルランド軍は分裂し……。カリスマ性と人なつっこさを併せ持つコリンズの人間像を、彫り深く描写。平和を願いつつも同胞と戦わざるをえなかった、その悲劇が痛切に迫る。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN IN GREY", "灰色の男 —変質侯爵物語—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "舞台は19世紀のイギリス。変質者っぽい侯爵を中心に、純情女、悪女、好青年たちが織り成す心理劇。侯爵役のJ・メイスンをはじめ、S・グレンジャー、M・ロックウッドなど、当時としてはイギリス最高のキャスト陣が出演し、好演しているサイキック・ホラー。", "1943-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Slither", "スリザー", "", "", "http://image.eiga.k-img.com/images/movie/53111/315x210.jpg?1915", 95, "「ドーン・オブ・ザ・デッド」の脚本家ジェイムズ・ガンが監督デビューを果たしたSFホラー。グロテスクな宇宙生命体“スリザー”に侵食される人間たちの恐怖を描く。アメリカ南西部に謎の隕石が落下。街の有力者グラントがそれを発見するが、突然そこから何かが飛び出し彼の体内に侵入してしまう。その日から、街では行方不明者が続発するように。グラントの変化に不信感を抱いた警察署長ビルは、事件との関連を調べ始めるが……。", "2007-12-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A THOUSAND AND ONE NIGHTS", "千一夜物語 魔法のランプ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "『アランビアン・ナイト』のエピソードの一つ、『アラジンと魔法のランプ』をもとにしたファンタジー・アクション。バグダッドに住む青年アラジンは、王女アルミナを一目見ようと宮殿の中に忍び込んだ。ところが見つかってしまい投獄される。ようやく逃げ出し、たどり着いた洞窟でアラジンは魔法使いより不思議なランプをもらい受ける……。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜の鼓", "夜の鼓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "近松門左衛門の姦通もの３作の内の一つ『堀川波の鼓』を翻案した、今井正監督初の時代劇。長年の江戸勤務を終えて帰宅した武士が知る妻の不義密通を推理劇的構成で描く。現在と過去を自在に交錯させる手法には橋本忍の本領が発揮されている。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("姑娘と五人の突撃兵", "姑娘と五人の突撃兵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "宇津井健主演の戦場ドラマ。便衣隊と激しい攻防を繰り広げる最前線。杉山少尉は陳秀麗という混血女性に危ういところを救われる。一方、根本小隊が便衣隊の包囲を受けて危機に瀕し、救援に赴いた加村本部隊も丘のトーチカにさえぎられ釘づけにされてしまう。杉山少尉はニトログリセリンをトラックに積み、トーチカを爆破することを思い立つが、秀麗がそれを止めた……。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINAL ANALYSIS", "愛という名の疑惑", "", "", "http://ecx.images-amazon.com/images/I/51GY694HWKL._SL210_.jpg", 125, "裁判で証言することも多い精神分析医のアイザックは、ブロンドの魅惑的な女性ヘザーと知り合う。二人は恋に落ちるが、彼女は少量の酒にも過激に反応して、暴力的になるうえ、記憶が残らないという、きわめて異常な病的酩酊症に悩まされていた。そんなある日、彼女はマフィアである自分の夫を殴り殺してしまう……。サンフランシスコを舞台に欲望と欺瞞の交錯する緊張の心理ドラマ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("或る剣豪の生涯", "或る剣豪の生涯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "鼻が人一倍大きい駒木兵八郎、彼は自らの容貌を気にして、ひそかに想いを寄せる千代姫にもその心を打ち明けることができない。そして彼女の恋人・十郎太に恋の手ほどきまでしてやる始末。言うまでもなくこれは「シラノ・ド・ベルジュラック」の翻案。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　どうぶつの森", "劇場版　どうぶつの森", "", "", "http://image.eiga.k-img.com/images/movie/33771/315x210.jpg?3314", 87, "子どもから大人まで大人気のゲームソフトをアニメ映画化。動物と人間が仲良く暮らす“どうぶつ村”を舞台に繰り広げられる全４話のオムニバス・ストーリー。どうぶつ村でひとり暮らしを始めた女の子あいは、たぬきちが経営するタヌキ商店でアルバイトをしながら、村長のコトブキやさすらいのミュージシャン・とたけけなど個性的な村人たちと打ち解けていく。ある晩、浜辺を訪れたあいは、不思議なメッセージが書かれた手紙を拾う。", "2006-12-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダライ・ラマの般若心経", "ダライ・ラマの般若心経", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "仏教最高の経典に挙げられる、“般若心経”の教えを紐解くドキュメンタリー。チベット仏教の最高指導者であるダライ・ラマ法王14世がカメラを前に、世界に知られながら本来の意味が必ずしも正確に伝わっていない“般若心経”をわかりやすく説く。法王の言葉に触れられる貴重なフィルムだ。チベット仏教の最高指導者であるダライ・ラマ法王14世。世界中の人々から尊敬を集める彼が、仏教の経典“般若心経”について語る。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Unforgiven", "許されざる者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "　テキサスの平原で牧場を営むザカリー一家。その養女レイチェルがカイオワ族の娘であるという噂が広まる。牧場を切り盛りする長男のベンは真相を突き止めようとする。やがて事実が母親によって明るみになるが、ザカリー家は孤立し、さらにレイチェルを奪還せんとカイオワ族が攻めてくる。ベンたちはそれに立ち向かうが……。オードリー・ヘップバーンにとって最初で最後の西部劇出演作。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROUTE58 ver.zero", "ROUTE58 ver.zero", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "沖縄のとあるバーで顔見知りになった裕輔、五郎、実和子ら男女６人。意気投合した彼らは共同経営でレジャーランドを始める。初めは上手くいっていた彼らだが、次第に友情にヒビが入り……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEADLY PURSUIT", "影なき男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "S・ポワチエが、10年ぶりにスクリーン復帰を果たした作品。単なる刑事ものでも、友情ものでも、アクションものでもなく、そこには同じ目的に向かう立場の違う男二人が見事に描かれ、単なる娯楽映画には留まらない出来。凶悪犯が、ハイカーに交じってワシントン山中に逃げ込んだ。FBI捜査官ウォーレンは、山岳ガイドのジョナサンを説得するが、この仕事は受けないと言い張る。だが、犯人の逃げ込んだグループのガイドを務めるのが、自分の恋人だと知り、引き受けることに。犯人たちを追い山中に入る二人だが、さすがのFBIもここでは手も足も出ず、ガイドの足を引っぱるだけだった……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WXIII〈ウェイステッドサーティーン〉機動警察パトレイバー", "WXIII〈ウェイステッドサーティーン〉機動警察パトレイバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "近未来SFアニメ・シリーズの劇場版第３作。原作コミックのなかでも特にファンの人気が高い『廃棄物13号』をもとに、シリアスなドラマが繰り広げられていく。城南署の刑事・久住と秦は、東京湾で続発している何者かによるレイバー破壊活動の捜査を続けていた。そんな折、湾岸に浮かぶ基地に人間を食い殺す巨大生物が出現し……。", "2002-03-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CATASTROPHE", "カタストロフ 世界の大惨事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "世界各地で実際に起きた悲劇的な大惨事の決定的瞬間を捉えたドキュメンタリー。“カミル”と名付けられたアメリカ史上最大のハリケーンの猛威、50人以上の死者を出したインディ500マイル・レースの事故、高層ビルの火災など、ショッキングなシーンが続出する。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("公安警察捜査官", "公安警察捜査官", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "スパイやテロに関する事件を捜査する警視庁公安部にスポットを当てたポリス・アクション。原作は、公安所属経験もある元刑事で、現在はテレビのコメンテーターとして活躍する北芝健。公安部への異動を命じられた刑事・狩場健は、ヨーロッパで起こった日本人集団拉致事件を捜査する過程で、アフリカのハキラ共和国が関与しているとの情報を得る。やがて、洗脳された拉致被害者の1人が東京駅で自爆テロを起こし……。", "2007-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KISS ME DEADLY", "キッスで殺せ！〈ディレクターズ・カット〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "M・スピレーンの“マイク・ハマー”シリーズの一編を映画化。裸足の美女が深夜のハイウェイを駆けてくるオープニング・ショットを皮切りに、鬼才R・アルドリッチの演出にぐいぐい引き込まれる凶暴なフィルム・ノワールだ。謎の集団にコテンパンにされたあげく、偶然拾ったばかりの女を惨殺された私立探偵ハマー。彼は美しき助手ヴェルダを敵に誘拐されながらも、核開発が絡んだ巨大な陰謀に迫っていく。無敵のタフガイ、ハマーが邪魔者を次々と殴り飛ばす猪突猛進ぶりも凄いが、いったい彼が何者と闘っているのかすら判然としないドラマの曖昧さも、恐ろしく異様な印象を残す。この映画は長らく初公開時の105分版が伝説化していたが、海辺のラストシーンに鮮烈な16ショットが付け加えられたプリントが発見され1998年に日本公開された。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hardcore Chambermusic", "スイス・ハードコア 30日間 in チューリッヒ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "1990年にスイスで結成され、高度なテクニックに裏づけられたエネルギッシュで感性豊かな演奏で、世界的人気を誇るハードコア・インプロ／エレクトロジャズトリオ「コッホ・シュッツ・シュトゥーダー」。2005年、チューリッヒに建てられた特設クラブで、30日間にわたって毎晩2セットのステージを行なうという企画に挑んだ彼らに密着し、臨場感あふれるパフォーマンスやオフステージのメンバーの姿をとらえたドキュメンタリー。", "2007-10-06", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COFESSIONE DI UN COMMISSARIO DI POLIZIAAL PROCURATORE DELLA REPUBBLICA", "警視の告白", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "マフィアの幹部ロムンノを捕らえようとする警察署長のボナピアは、仕返しに黒い噂を立てられ解雇されてしまう。正義感からロムンノを殺したボナピアは逮捕されるが、この事件に疑問を抱いた検事補トレイニは、調査を進めるうちに、背後にあるマフィアの巨大な力を知る。モスクワ映画祭金賞受賞。", "1970-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("迷宮物語", "迷宮物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "眉村卓原作のオリジナル・ビデオ作品。りんたろう監督・脚本の「ラビリンス・ラビリンス」、川尻義昭監督・脚本・キャラクターデザイン・作画監督の「走る男」、大友克洋監督・脚本・キャラクターデザインの「工事中止命令」の３話で構成され、1987年の東京国際ファンタスティック映画祭で初公開された。アニメ作家、マンガ家がそれぞれの個性を前面に押しだして描いた、実験色の濃い異色オムニバス。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("折鶴お千", "折鶴お千", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1929年に「日本橋」1933年に「滝の白糸」と、泉鏡花の原作を見事に映画世界によみがえらせた溝口健二が、三たび泉鏡花をとりあげた名作である。原作の題名は『売色鴨南蛮』。神田明神の境内近くにはいかがわしい商売をして暮らす連中がいた。首領は熊沢といい、若い娘お千も熊沢たちの食い物にされていた。熊沢に養われている少年宗吉は、毎日ただ同然にこきつかわれながらもお千に励まされ生きていた。ある日、あまりの仕打ちに耐えかねた宗吉は、お千とともに熊沢のもとから逃げようと決意するが……。前２作が幻想的なタッチであったのに対し、ここではあくまでもリアリズムに徹して秀逸。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOWLING V：THE　REBIRTH", "ハウリングV 最後の復活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "シリーズ第５作の舞台はハンガリー・ブダペスト、500年間封印されていたという古城。ミステリアスな伯爵の案内で城内を探索するツアーの一人が、怪物に襲われた。折からの吹雪で外界との連絡が遮断されたなか、一人また一人と姿を消していく。秘密の迷路や地下水道で発見される血まみれの死体。残された一行の中に、狼男がいるらしい。果たしてそれは誰か？", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("証言記録　マニラ市街戦　死者12万　焦土への一ヶ月", "証言記録　マニラ市街戦　死者12万　焦土への一ヶ月", "", "", "http://image.eiga.k-img.com/images/movie/55231/315x210.jpg?3524", 0, "太平洋戦争末期、日米両軍はフィリピンのマニラにおいて1カ月にもおよぶ市街戦を展開し、死者12万人を出した。しかし、そのほとんどの被害者は戦闘に巻き込まれたマニラ市民だった。NHKで放送されたドキュメンタリー番組を劇場で公開するプロジェクト「This is REAL／NHK-BSドキュメンタリー・コレクション」にて上映。", "2010-02-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まむしの兄弟・傷害恐喝十八犯", "まむしの兄弟・傷害恐喝十八犯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "18回目のムショ暮らしから解放されたゴロ政は、出迎えた勝と神戸へ戻ってくる。バラック建ての歓楽街“おかめ横丁”の用心棒・鉄の娘に惚れた政は、横丁を叩き壊そうとする矢東会相手にド迫力の大暴れ！", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARIS QUI DORT", "眠るパリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "夜のパリの姿を、静かに描いた一編。ある科学者が発明した光線によって深い眠りに落ちたパリ。たった数人の人間だけがこの夜、眠らないでいた……。R・クレールが、サイレント映画にSFという要素を初めて持ち込んだ作品。ダダイズム運動最盛期における、前衛的な映像も魅力。なおビデオには「幕間」が併録されている。", "1923-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jeans", "ジーンズ　世界は2人のために", "", "", "http://image.eiga.k-img.com/images/movie/1801/315x210.jpg?1606", 175, "ハリウッドで医大に通う双子の兄弟ヴィスとラムーは何から何まで瓜二つ。父の経営する高級インドレストランはビバリーヒルズで大繁昌、LA国際空港職員向けに料理の配達も請け負い、2人も配達を手伝っている。配達の途中、2人は空港の入国審査ゲートで居住先のメモを無くし立ち往生するインド人家族を見かけ、同郷のよしみで家に招く。家族は、八頭身美人のマドゥ、兄マデッシュ、そして祖母。インドでは難しい手術を祖母に受けさせるために来たと言う。", "1999-11-27", "21,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富江VS富江", "富江VS富江", "", "", "http://image.eiga.k-img.com/images/movie/53071/315x210.jpg?4478", 86, "伊藤潤二による同名ホラー漫画を映像化した人気シリーズの第7弾。完結篇となる本作では、何度殺しても甦り増殖を続ける魔性の美女・富江が、オリジナル富江の血を必要とする無数のコピーたちと壮絶な戦いを繰り広げる。恋人の尚子を目の前で殺された一樹は、心に大きな傷を負ったことから事件当時の記憶があやふやになっていた。社会復帰を目指してマネキン工場で働き始めた彼は、尚子にそっくりな女性・富江と出会う。", "2007-11-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HERBST MILCH", "秋のミルク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "８歳にして一家の母親代わりを務めてきた、控えめで我慢強い農家の少女が、やがて戦争の嵐の中、家族を守るために、力強くたくましい女性へと成長していく。西ドイツ・バイエルンの農村を舞台に、ヴィムシュナイダーが彼女自身の体験を綴った大ベストセラーの映画化。農村の温かい家庭の味、そしてロマンスがほどよくブレンドされた心温まる作品。", "1988-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GHOST DAD", "ゴースト・パパ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "企業戦士として必死に働き、３人の子供たちを育て、企業合併という大プロジェクトを目の前にしてあっけなく他界した男。だがこの世にやり残したことがいっぱいある彼は、霊界管理人に３日間の猶予をもらい、プロジェクトを成功させ、子供たちに満足な生活費を渡すために戻ってくるが……。当時、アメリカ映画界でブームとなったゴーストものの一編。B・コスビーが心優しい魅力を披露。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cold Mountain", "コールド・マウンテン", "", "", "http://image.eiga.k-img.com/images/movie/1341/315x210.jpg?4380", 155, "全米図書賞を受賞したチャールズ・フレイジャーの同名ベストセラー小説を「イングリッシュ・ペイシェント」のアンソニー・ミンゲラ監督が映画化し、レニー・ゼルウィガーがアカデミー助演女優賞を受賞。南北戦争末期、兵士インマンは恋人エイダに会うため死罪を覚悟して脱走し、故郷コールド・マウンテンを目指す。エイダは彼を待つため故郷で必死に生きる。撮影は「イングリッシュ・ペイシェント」でオスカー受賞のジョン・シール。", "2004-04-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("牢獄の花嫁", "牢獄の花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "左手の中指を切り取られ、一刀のもとに殺された女の死体が発見された。名与力・塙の命令で死体を運んでいた男を泳がせると、捜査線上に意外な人物が浮かび上がった。それは、塙の息子・郁次郎だった……。ミステリー・タッチの一作。脚本の五都宮章人は、比佐芳武、依田義賢、尾門敏雄、八尋不二、柳川真一の共同ペンネーム。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAINTREE COUNTY", "愛情の花咲く樹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 167, "南北戦争と、その前の数年間のラブ・ロマンスを、あの「風と共に去りぬ」を上回る製作費によるロケーション撮影で描いた超大作。M・クリフト扮する理想主義者の青年と、テイラー扮する大富豪令嬢との、悲劇的な結末を描いた壮大な叙事詩。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動戦士ガンダム0083 ジオンの残光", "機動戦士ガンダム0083 ジオンの残光", "", "", "http://ecx.images-amazon.com/images/I/51bLwfpdsxL._SL210_.jpg", 120, "一年戦争終結から3年後の宇宙世紀0083年。ジオン軍の残党部隊デラーズ・フリートが、連邦軍の開発した核弾頭搭載の新型ガンダム2号機を強奪する事件が発生。偶然その場に居合わせた連邦軍の新米パイロット、コウ・ウラキ少尉は、残されたもう一機の新型ガンダム1号機に乗り、奪われた2号機を追う。全13話のOVA「機動戦士ガンダム0083 STARDUST MEMORY」（1991～92）の総集編に、新規カットを加えて公開された劇場版。", "1992-08-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("口笛が流れる港町", "口笛が流れる港町", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "“渡り鳥”滝伸次は阿蘇山中の鉱山に姿を現し、鉱山を乗っ取ろうとする悪玉から鉱山主を助ける。前作ではまだ助演にすぎなかった宍戸錠が、主人公のライバルとして大きなウエイトを占めるようになる。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幕末残酷物語", "幕末残酷物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "新選組に入隊した、気弱で力もない青年の物語。舞台は新撰組の屯所内に限定され、その中で厳しい規律に縛られた若者たちが人間性を失い、破壊していく様を描く。徹底したワンセット・システムの撮影と、まだ幼さの残る藤純子が印象的。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SASORI IN USA", "SASORI IN USA", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "かつて梶芽衣子の主演によって映画化された篠原とおるの『さそり』が、舞台をカリフォルニアに置き換えて復活。オール・アメリカ・ロケによって、熱くハードなアクションが繰り広げられていく。殺人の罪でロサンゼルスの女刑務所に投獄された松島ナミは日本人少女ユキコとともに、性的倒錯者である所長の魔の手から逃れるため脱獄をするが……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOGNI D’ORO", "監督ミケーレの黄金の夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "映画監督ミケーレに振りかかる数々の災難を、いつもながらのモレッティ自作自演でコミカルに描いた作品。新作準備中の監督ミケーレは評論家たちからさんざんな言葉を浴びせられていた。そこに有能な新人監督がデビュー。ミケーレの足元はますます怪しくなる。やがて二人はTV番組で、どちらが最強の監督かをめぐって争うことになり……。", "1981-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VEGAS VACATION", "ベガス・バケーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「ナショナル・ランプーン／ホリデー・ロード4000キロ」から続く、グリスウォルド家の愉快な家族旅行を描いたコメディ・シリーズの第４作。クラークは妻のエレンと二人の子供を連れてラスベガスを訪れる。だが、クラークはギャンブルに負けて一文無しに。そのうえ、エレンは歌手のウェイン・ニュートン（本人が演じている）に誘惑され、プロポーズまでされてしまう……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍騰虎躍／FEARLESS HYNEA II", "ジャッキー・チェンの醒拳", "", "", "http://ecx.images-amazon.com/images/I/61UIZw8UX4L._SL210_.jpg", 90, "J・チェンの出世作となった「ドランク・モンキー　酔拳」などの流れをくむ最後の作品となるコミカル・カンフー・アクション。役どころは、醒拳という必殺拳の達人を父に持つ青年。ところが武術にはてんで興味がなく、遊び三昧。その彼が、奇妙な縁で再会した、やはりぐうたらな従兄とともに、天鬼拳一派に殺されたそれぞれの父親の復讐をと、一念発起。八面六臂の大活躍を見せる。", "1982-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOODHOUNDS OF BROADWAY", "ワンナイト・オブ・ブロードウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "1920年代のニューヨーク。禁酒法をものともせず、豪華なショーが繰り広げられているナイトスポットを舞台に、様々な人間たちが織り成す不思議な一夜の物語を描く。マドンナをはじめ、R・ハウアー、M・ディロンといった、豪華な異色の顔合わせが魅力的なロマンティック・コメディ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちぎれ雲～いつか老人介護～", "ちぎれ雲～いつか老人介護～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "高齢者社会の問題に真正面から向かい合った感動のドラマ。老人問題に直面したOLの姿を通して、命の尊さを訴える。広告会社のデザイナーをしている23歳の百合子は、はい徊癖のある親友の祖母に出会い心を痛める。その後、老人ホームを抜け出してきた老婆と知り合ったり、祖父を引き取ることになった家族の苦労を目の当たりするなどして、彼女は高齢者の介護の重要性に目覚めていく。やがてヘルパーの仕事に興味を持つようになった彼女は、介護福祉士になる決意をするのだった。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("のぞみ・ウィッチィズ", "のぞみ・ウィッチィズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "極楽とんぼで素直さだけが取り柄の少年と天真爛漫、スポーツ万能の少女の二人が繰り広げる、フレッシュな青春学園物語。ドジでお人好し、何をしても失敗ばかりの遼太郎。だが隣に住む少女、望に出会ってから不思議な魅力を持つ彼女の言葉に刺激され、遼太郎のボクサーとしての才能が花開いていく。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バカヤロー！2 幸せになりたい", "バカヤロー！2 幸せになりたい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "オムニバス喜劇の第２作。今回はCM、演劇界などから４人の新人監督を起用した。藤井郁弥が、山のようにレコードを抱え、CD全盛の世に取り残された男に扮し、微妙な世代のズレを感じさせる「新しさについていけない」、加えて、オールドミスのOLが年齢のために再就職できない、山田邦子主演の「女だけトシとるなんて」の２話が、都市生活の心の痛みを映し出して、他のエピソードの群を抜いている。", "1989-07-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CE´SAR ET ROSALIE", "夕なぎ セザールとロザリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "フランスで大ヒットした大人の愛のドラマ。中年の屑鉄屋セザールは、ロザリーと知り合い、同棲する。そこへロザリーの昔の恋人ダビッドが現れ、だだっ子のような性格のセザールはあわてふためく。だが、ロザリーにはダビッドが必要なことを知ったセザールは、海辺の家で３人で過ごすことにする。ある日、ロザリーは二人の男を残して去ってしまうのだが、残された彼らの間には友情が芽ばえる。そんな二人のところへ、ひょっこり帰って来るロザリー。シュナイダーが二人の男の間で揺れ動く女心を見事に演じる。モンタン、フレイも好演。フランス・シネマ大賞受賞。", "1972-01-01", "2,4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Don's Plum", "あのころ僕らは", "", "", "http://ecx.images-amazon.com/images/I/21BPRY88Y4L._SL210_.jpg", 90, "L・ディカプリオとT・マグワイアが、友人R・D・ロブ監督の初長編作で等身大の若者を演じた、ほろ苦い青春ドラマ。ロサンゼルスの土曜の夜。いつものダイナーに、若い男女８人が集まってくる。恋、セックス、友人のことなど、他愛もない会話を楽しんでいた彼らだが、夜が深まるにつれ、いつもは言えないことまで言ってしまい……。即興演技によるリアルな会話が飛び出し、若さゆえの純粋さや不安定な心の内面が浮き彫りにされる。", "2000-01-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOBRUK", "トブルク戦線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "第二次大戦下の1942年秋。北アフリカのドイツ軍補給地トブルク港を舞台に、敵兵に変装して潜入し、砲台、貯蔵庫を爆破したイギリス兵とユダヤ人の特殊部隊の活躍を描く、戦争アクション。1950～60年代に活躍した傍役L・ゴードンの脚本で本人も自ら出演している。スペクタクル・シーンが圧巻。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETURN TO PARADISE", "楽園に帰る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1929年、南太平洋のマタレバ島に突然モーガンという男が現れた。彼は圧政に苦しむ島の人々を元気づけながらも、愛する女性の死にショックを受けて島を去る。そして第二次大戦が始まり、モーガンが再び島を訪れたとき、そこには彼の娘テュリアの美しく成長した姿があった。淡々としたタッチの南海映画。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白日夢", "白日夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "武智鉄二が1964年に監督した同名作品の、武智自身によるリメイク。愛染恭子と佐藤慶の本番濡れ場が封切り前から話題を集め、ハード・コアとしては異例のヒットを記録したが、作品的には前作同様酷評された。愛染恭子はこの作品で一躍ポルノ界の大スターになった。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　TRANSPORTEUR", "トランスポーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "リュック・ベッソンの製作、脚本によるアクション。「スナッチ」のジェイソン・ステイサムによるタフな熱演がヒロイックで印象深い。運び屋のフランクは、どんな危険物でも正確に、確実に目的地に届けるプロ。犯罪組織からの“依頼品”である縛られた女性を成り行きで助けてしまったことから、彼は追われる身となる。", "2003-02-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("餓狼伝", "餓狼伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "原作者が映像化は無理と語っていた小説を、“鍛え上げられた肉体”という武器を持つ現役格闘家をメインキャストに得、映画化。格闘技最強を目指す空手家・丹波文七は、道場破りに行った東洋プロレスで梶原年男に関節をきめられ、敗れてしまう。その日から文七は、梶原への復讐のため修行を重ねる。数年後、アメリカで活躍中の梶原が帰国することを知り、再び東洋プロレスへと向かうのだった。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Earth Girls Are Easy", "ボクの彼女は地球人／BRAND　NEW　GIRL", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "地球人の女性の前に現われた3人の宇宙人たちが巻き起こす騒動を描いた、ジーナ・デイビス主演のSFラブ・コメディ。婚約者に浮気をされ落ち込んでいた主人公バレリー。そんなある日、彼女の家のプールに宇宙船が不時着し、中から3人の毛むくじゃらエイリアンが現われた。ところが毛を剃ってみると、3人ともかなりの男前。バレリーはその中の1人マックと恋に落ちるが……。まだブレイクする前のジム・キャリーも出演している。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人形佐七捕物帖・妖艶六死美人", "人形佐七捕物帖・妖艶六死美人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "横溝正史の原作を若山富三郎主演で映画化した“人形佐七捕物帖”シリーズの第１作。花のお江戸に評判高い似顔絵画家・空来山人が描いた風流六歌仙とうたわれる６美人が次々に殺される。人形佐七が犯人究明に乗り出すが、女房のお条が黒覆面の一団に誘拐されて……。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("引き出しの中のラブレター", "引き出しの中のラブレター", "", "", "http://image.eiga.k-img.com/images/movie/53881/315x210.jpg?3027", 119, "ラジオのパーソナリティを務める真生は、4年前に喧嘩して絶縁した父親と仲直りできないまま、父親に他界されてしまう。間もなく真生のもとに生前の父親が書いた手紙が届くが、真生は手紙を開封することができず引き出しの中にしまってしまう。そんなある日、真生は心の奥底に隠されている想いを伝える企画「引き出しの中のラブレター」を思いつく。主演は常盤貴子、共演に林遣都、八千草薫、仲代達矢ほか。", "2009-10-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神奇兩女侠／WONDER WOMEN", "トップ・レディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "グラマーだが頭は弱いアンと、頭はキレるがお色気不足のリンは、美人コンテストに出場して、あえなく落選。同じ境遇で意気投合する二人の前に、今度はハンサムな男性が現れた……。香港映画界から生まれた新感覚の都会派コメディ。演技派女優イップ・トンが好演。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pride & Prejudice", "プライドと偏見", "", "", "http://image.eiga.k-img.com/images/movie/1151/315x210.jpg?2922", 127, "ジェーン・オースティンの同名小説を「パイレーツ・オブ・カリビアン」のキーラ・ナイトレイ主演で映画化。18世紀末のイギリスの田舎町。5人の姉妹が母親と暮らすベネット家の近所に、資産家ヒングリーが引っ越してくる。次女エリザベスはヒングリーの親友ダーシーに出会い、その高慢な態度に反感を抱くが。監督は本作で映画デビューのジョー・ライト。ダーシー役は「エニグマ」のマシュー・マクファディン。", "2006-01-14", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ローカルニュース", "ローカルニュース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "地方TV局のニュース・ディレクターが、やる気のないアルバイトたちに手こずり、取材現場で様々なトラブルに遭遇していくコメディ。“文鳥が言葉をしゃべった”“現代に生きる忍者たち”などの、まさしくローカル色豊かで珍妙なニュースの数々が、ひょうひょうとしたユーモアをふりまいている。PFF入選の経験を持つ中村義洋監督の劇場デビュー作。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEADLY BET", "カジノファイター 地獄の拳闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "格闘技の達人J・ウィンコット主演のハード・アクション。賭博の街ラスベガスで、リング・ファイトをするアンジェロ・スカラは、最強のファイター、リコとの勝負に恋人イザベラまでも賭けてしまう。金と名声とプライドを賭けた男たちの死闘が始まった。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A FOOL AND HIS MONEY", "恋人たちの街角", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「スピード」で一躍スターダムに躍り出たS・ブロック主演のロマンティック・コメディ。広告代理店に勤めるモーリスは神の啓示を受け、新興宗教を利用したビジネスを始める。彼は弁護士の恋人デビーの忠告も聞かず、掃除夫を教祖にでっち上げ、巧みな話術により信者を増やして、大成功をする。だが、詐欺がバレてしまい窮地に追い込まれ……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blue Steel", "ブルースチール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ニューヨークを舞台に、殺人犯と女性警官との闘いを描いたポリス・アクション。警察学校を卒業し、以前から憧れていた警官になったメーガン。彼女は勤務初日に強盗犯を射殺するが、犯人の銃が現場で見つからなかったことから停職処分を受けてしまう。その銃は、偶然その場に居合わせ、彼女に魅せられたユージンという男が密かに持ち帰っていた。メーガンへの歪んだ想いから無差別殺人を始めた彼は、正体を隠して彼女に近づくが……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Dear Enemy", "素晴らしい一日", "", "", "http://image.eiga.k-img.com/images/movie/55991/315x210.jpg?2627", 124, "仕事も恋人もなく、貯金も底をついた30代の女性ヒスは、1年前に別れた男ビョンウンに貸したままの350万ウォンを返してもらうため、ビョンウンがいる競馬場へ行く。能天気で甲斐性なしのビョンウンを信用できないヒスは、ビョンウンがあらたに借金をするために知人の女性たちを訪ね歩く旅に同行する。日本の人気作家・平安寿子の短編小説を、韓国で映画化。主演は「シークレット・サンシャイン」のチョン・ドヨン、「チェイサー」「国家代表!?」のハ・ジョンウ。", "2011-04-16", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春のお通り　愛して泣いて突っ走れ！", "青春のお通り　愛して泣いて突っ走れ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "吉永小百合の明朗青春もの「青春のお通り」第２作。短大卒業後、TV作家のもとで住み込み家政婦として働くことになった、チャッカリスカヤこと南原桜子の愛と青春の楽しき日々を描いていく。桜子は親友・久子の兄である圭太に好意を抱くが、もう一人の親友・中子もまた圭太に恋焦がれていた……。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEENAGE MUTANT NINJA TURTLES II：THE SECRET OF THE OOZE", "ミュータント・ニンジャ・タートルズ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "アメリカだけで１億3300万ドルの興収を上げ、キャラクター・グッズなどの関連商品も記録的な売り上げとなった前作「ミュータント・タートルズ」の続編。前作で死んだと思われたタートルズの宿敵シュレッダーが、再びフット団を率いてニューヨーク乗っ取りを企てるが……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コキーユ・貝殻", "コキーユ・貝殻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "妻子とともに平穏な日々を過ごすサラリーマンの浦山が、中学校の同窓会で元クラスメイトの直子と30年ぶりに再会する。直子が経営するスナック、“コキーユ”に何度も足を運ぶようになった浦山は、かつて彼女が自分に抱いていた切ない恋心を知り……。ぎこちなく愛を育んでいく男女を、地方都市の落ち着いた街並みを背景に綴る大人のメロドラマ。心を通わせるうちに、未来ではなくはるか昔の初恋の思い出へと回帰していく二人の姿が、ノスタルジックな切なさを呼び起こす。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワールドクラシック＠シネマ2011 バレエ「白鳥の湖」　ボリショイ・バレエ", "ワールドクラシック＠シネマ2011 バレエ「白鳥の湖」　ボリショイ・バレエ", "", "", "http://image.eiga.k-img.com/images/movie/56461/315x210.jpg?1607", 146, "世界各地の有名オペラハウス、バレエ団の上演を映画館のスクリーンで上映する「ワールドクラシック＠シネマ2011」（全8作品）の1作。ロシアの作曲家チャイコフスキーの3大バレエ曲の中でも、最も親しまれている作品。同作を1877年に初演したボリショイ劇場のバレエ団が2010年に上演した最新版。", "2011-04-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブ★コン", "ラブ★コン", "", "", "http://image.eiga.k-img.com/images/movie/33561/315x210.jpg?3077", 100, "中原アヤ原作の人気コミックを、モデル出身の藤澤恵麻とWaTの小池徹平主演で実写映画化。それぞれの“ラブリー・コンプレックス”を抱える高校生カップルの恋愛を描いたラブコメディ。身長が高いことが悩みのリサと、身長が低いことが悩みの敦士。「チビ」「デカ女」と呼び合いながらも息がぴったりの2人は、クラスメイトから冷やかされてばかりいる。やがてリサは次第に敦士に惹かれるようになるが、やはり身長のことが気になり……。", "2006-07-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ケイゾク　映画 Beautiful Dreamer", "ケイゾク　映画 Beautiful Dreamer", "", "", "http://image.eiga.k-img.com/images/movie/1871/315x210.jpg?3570", 119, "TBS系で放送されカルト的人気を博した刑事ドラマ「ケイゾク」の劇場版。15年前、船の沈没事件で9人の乗客のうち2人だけが死亡。生き残った7人には殺人容疑がかけられるが、証拠不十分で事件は迷宮入りとなっていた。警視庁捜査一課二係の柴田純と真山徹は、ナゾを解明する過程で厄神島を訪れるが、事件の犠牲者・霧島夫妻の娘・七海が仕かけた死のゲームに巻き込まれてしまう。主演の中谷美紀、渡部篤郎、監督の堤幸彦らがTVシリーズから続投。", "2000-03-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IM NORDWIND", "ひとすじの温もり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "会社を突然リストラされた熟年男性の苦悩と、それに呼応するかのように歯車が狂っていく家族の姿を見すえたヒューマン・ドラマ。女性監督ベティナ・オベルリが、リアリスティックな心理描写で夫、妻、娘それぞれの立場の怒りや悲しみを掘り下げ、現代的な家族の肖像を映し出している。勤務先を解雇されたエルウィンが、家族に内緒で職探しを始める。しかし彼はさらなる失意を味わい、妻と娘の悩みも深刻化していく。", "2006-02-11", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bright Eyes", "輝く瞳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "子役女優のルーツ的な存在感を示していたシャーリー・テンプルの魅力全開の一作。その可愛さを引き出すために、'34年製作当時では珍しいカラー作品として製作された。パイロットの父は飛行機が墜落して死亡し、母は交通事故死。孤児になった娘だが、金持ちの老人に引き取られる。だが、同居する一家の人たちが彼女をいじめたので家出し……。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アニメーション・マジック　アート・アニメーションのちいさな学校生徒作品上映会", "アニメーション・マジック　アート・アニメーションのちいさな学校生徒作品上映会", "", "", "http://image.eiga.k-img.com/images/movie/54711/315x210.jpg?3136", 62, "アニメーション専門学校「アート・アニメーションのちいさな学校」の生徒による短編アニメーションを集めたオムニバス作品。「羅生門」を西部劇版にアレンジした「赤いスカーフ」、動物たちが音楽を奏でる「A little symphony in the forest」など、テーマも作風も様々な作品を上映。真賀里文子、保坂純子ら日本のアニメ界を牽引するクリエイターが講師陣として名を連ねる。", "2009-07-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生きてみたいもう一度 新宿バス放火事件", "生きてみたいもう一度 新宿バス放火事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "1980年、東京・西新宿で起きた京王帝都バス放火事件で、皮膚の80パーセントを火傷する重傷を負った被害者の手記の映画化。事故後の被害者の生きる姿を桃井かおりが坊主頭になってまで熱演する。監督の恩地日出夫も半年かけて関係者に再取材、事実に重きを置いた。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONLY THE STRONG", "オンリー・ザ・ストロング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "グリーンベレーの若き勇士だったルイスは、除隊して故郷マイアミへ戻った。ところが帰ってみると、かつて平和だった町は麻薬漬け、すっかり凶悪なギャング団に牛耳られていた。彼は特殊部隊仕込みのゲリラ作戦とブラジルの格闘技カポエイラで、単身ギャングどもに戦いを挑む。S・レティック監督によるアクション編で、主演は本物の格闘家だったというM・ダカスコス。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCAVENGERS", "KGB追跡 ～聖書の秘密～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "マイアミ大学で教鞭をとる動物学者トム・リードのもとに突然送られてきた聖書。そこには巨大な麻薬組織の実体を暴く暗号が隠されていた。トムはこの聖書がきっかけとなって、KGBとCIAの派手なスパイ合戦に巻き込まれていく。往年の連続活劇を彷彿させる、コメディ仕立てのアクション映画。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラザロ LAZARUS", "ラザロ LAZARUS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 201, "「レフト・アローン」の井土紀州監督が、現代日本の格差社会によって生みだされた怪物的ヒロインの戦いを描いた愛憎ドラマ。主人公マユミが社会からはみだした女たちを率いて資産家の子息たちを殺害していく姿を描いた「蒼ざめたる馬」編、愛を信じることのできないマユミの苦悩と孤独を描く「複製の廃墟」編、平凡な女性だったマユミが怪物となっていくまでを明かした「朝日のあたる家」編の3部構成。", "2007-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍者秘帖・梟の城", "忍者秘帖・梟の城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "織田信長により壊滅させられた伊賀忍者の生き残り、重蔵は山中にこもり読経三昧の日を送っていた。時は天正19年、光秀の乱で豊臣秀吉が天下を握った頃、重蔵は師匠から伊賀を復興させるために秀吉を暗殺するよう命じられるが……。豪快でスピード感あふれるアクションシーンが見どころの忍者もの。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRANKIE AND JOHNNY", "フランキーandジョニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "芸人コンビの恋の行方を描いたラブ・コメディ。ミシシッピー河を行くショーボートで評判の歌と踊りのコンビ、フランキーとジョニーは愛し合っているが、ジョニーは博打に夢中で、それがフランキーの悩みのタネ。そんな彼らの前に赤毛の娘が現われ、博打のツキをもたらしたことからジョニーは彼女に入れあげる。ところが、この娘はショーボートの持ち主の恋人で……。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE UNBEARABLE LIGHTNESS OF BEING", "存在の耐えられない軽さ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 173, "フランスに亡命中のチェコの作家、クンデラのベストセラー小説の映画化。激動の時代のチェコを舞台に、一人の男とまったくタイプの違う二人の女の間で展開する大人の愛のドラマ。1968年、プラハの春を謳歌するチェコ。そこに一人の有能な脳外科医がいた。男の名はトマシュ。独身で無類の女好きのトマシュは、テレーザという生真面目な娘と出会い、同棲から結婚に踏み切る。だが、一方でトマシュは、彼の分身のような女流画家サヴィーナとも自由な関係を結んでいた。人生を軽く生きるトマシュ。彼の意識の中での自分の存在の“軽さ”に耐えられないテレーザ。突然のソ連の軍事介入にトマシュとテレーザは、ジュネーブに逃れるが、トマシュの浮気を知ったテレーザはプラハに逆戻りして……。ヨーロッパの一流のスタッフと俳優が結集した知的なアメリカ映画。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DR.JEKYLL AND MR.HYDE", "ジキル博士とハイド氏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "R・L・スティーヴンソンの有名な恐怖小説『ジキル博士とハイド氏』。1920年の「狂える悪魔」以来何度も映画化されているが、本作は３度目にあたる。“人間の精神は善と悪からなっている”という結論に達した医学者ジキル博士は、精神の分離薬を開発。自ら人体実験に臨む。しかし、温厚だった博士は薬を飲むと、苦悶の果てに凶悪なハイドという人物に変身してしまう。もとに戻る薬も作るが、実験を繰り返すうち、次第にジキルに戻れなくなり、悲劇のエンディングを迎えることに。ストーリーで重要な役割を果たす商売女アイビ役には名女優I・バーグマンがあたっている。DVDは「ジキル博士とハイド氏　コレクターズ・エディション」に収録。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Breakfast of Champions", "ブレックファースト・オブ・チャンピオンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　自動車販売会社の社長ドウェインは自らCMにも出演する実業家。しかしオフィスでは女装癖のある従業員ハリー、愛人でもある秘書のフランシーヌといった面々と仕事をし、そして家では自殺願望のある妻セリアやゲイの息子バニーとの生活を抱え、ストレスを溜め込んでいた。そんな彼にしても拳銃自殺を考える始末だったが……。アラン・ルドルフがかねてから映画化を希望し、さらに主演のブルース・ウィリスも自らプロデューサーを務めた意欲作。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SMALL SOLDIERS", "スモール・ソルジャーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "玩具会社を傘下に収めた軍事産業企業グロボテック社が、永久電池を使って新たに作り上げたアクション・フィギュア、心優しいモンスター種族“ゴーゴナイト”と、彼らの壊滅を目指す部隊“コマンドー・エリート”。発売前にこれを手に入れた少年グレゴリーはゴーゴナイトのリーダー、アーチャーが自分の意思を持って動き、話すのを目撃。最初は驚くが、次第に仲良くなっていく。だがそのころ、彼らを倒すためコマンドー・エリートたちは活動を開始していた……。オモチャ同士の戦いとそれに巻き込まれた子供たちの反撃を描く。フィギュアのデザイン、製作を手掛けたのは「ジュラシック・パーク」で恐竜に命を吹き込み、オスカーを受賞したスタン・ウィンストン。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kal Ho Naa Ho", "たとえ明日が来なくても", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 186, "ニューヨークを舞台に3人の男女が織りなす恋と友情を描いたインド発の感動ラブストーリー。ニューヨークで暮らすインド人女性ナイナーは複雑な家庭の事情を抱え、もう長いあいだ笑顔を忘れていた。そんなある日、近所に陽気な青年アマンが引越してくる。アマンの悪ふざけに怒りながらも、いつしか心ひかれていくナイナー。そしてアマンもまたナイナーに好意を抱くが、アマンはなぜかナイナーを同級生のローヒトと結びつけようとする。", "2008-08-30", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("姑獲鳥の夏", "姑獲鳥の夏", "", "", "http://image.eiga.k-img.com/images/movie/1721/315x210.jpg?4446", 123, "「ウルトラQ」シリーズや「帝都物語」の実相寺昭雄が、京極夏彦のベストセラーを映画化。脚本はフィギュア・オタクを描いた「ブリスター！　BLISTER」の猪爪慎一。シリーズ共通の主要キャラは、京極堂に堤真一、関口に永瀬正敏、榎木津に阿部寛という配役。昭和27年の東京。作家の関口は、ある産婦人科医院の娘が20カ月も身籠もったままだという噂を追って、怪事件に巻き込まれていく。", "2005-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超強台風", "超強台風", "", "", "http://image.eiga.k-img.com/images/movie/55671/315x210.jpg?3630", 94, "巨大台風の脅威を描く中国のディザスター・ムービー。CGに頼らず、精巧なミニチュアを駆使した特撮で迫力の映像を作り上げる。太平洋上に発生した観測史上最大級の台風が、人口120万人の中国沿岸部都市に上陸する。逃げ遅れた人々に高波と暴風雨の脅威が襲いかかるなか、市長は自らの命をかけて市民の救出に挑む。第21回東京国際映画祭コンペティション部門正式出品作品。", "2010-09-25", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄火場の風", "鉄火場の風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「清水の暴れん坊」に続く石原裕次郎と赤木圭一郎の顔合わせによるアクション・ドラマ。ヤクザ・畑中英次は、敵対する組の代貸・高木に組長殺しの罪を着せられ服役するが、出所後、単身高木の賭場に乗り込み、親分衆の前でイカサマを暴く。すっかり男を下げた高木は、野球場襲撃を企てるが……。前半の単調な展開に比べて、後半のフィルム・ノワール調の野球場襲撃シーンは緊迫感が盛り上がる。このあたりは熊井啓の緻密なシナリオの功績である。「清水の暴れん坊」では裕次郎の前で、まだチンピラ然としていた赤木圭一郎だが、今回は裕次郎と堂々と渡り合えるだけのスター性を感じさせた。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Amazing Journey: The Story of The Who", "ザ・フー：アメイジング・ジャーニー", "", "", "http://image.eiga.k-img.com/images/movie/54041/315x210.jpg?2209", 237, "英国が誇るロック・バンド“The Who”の栄光と軌跡を総括するドキュメンタリー。フー初期の幻のライブ映像などがスクリーンに蘇る。また、スティング、エディ・べダー（パール・ジャム）、ノエル・ギャラガー（オアシス）、エッジ（U2）ら現在のポップ・ミュージック界のトップ・スターたちがカメラの前に登場し、伝説のバンド“The Who”を熱く語る。監督は「毛沢東からモーツァルトへ／中国のアイザック・スターン」のマーレイ・ラーナー。", "2008-11-22", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("April Fool's Day", "血のエイプリルフール", "", "", "http://image.eiga.k-img.com/images/movie/54581/315x210.jpg?4026", 91, "大富豪の令嬢デズリーと弟のブレインは、毎年4月1日のエイプリル・フールになると、友人たちを招いて盛大なパーティーを開いていた。しかし、ある年のパーティーで、デズリーとブレインのいたずらが原因で招待客の1人がバルコニーから転落死する事故が起きてしまう。そして、その1年後のパーティーでは、参加した7人の男女が次々と殺されていき……。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月蝕", "月蝕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "男への復讐に身をやきながら、自らも堕ちていく女の恋愛心理を井上梅次監督がモダン・タッチで描いた作品。ナイトクラブで殺された一人の女・綾子のことを、彼女に惹かれていたバンド・マスターの和馬が回想する。かつて婚約者に去られた綾子は、男への不信感と彼への復讐心だけを頼りに生きてきた……。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WINDS OF KITTY HAWK", "大空への挑戦 ライト兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1903年、大空に飛ぶことだけを目指して世界初の動力飛行に成功し、一躍アメリカの英雄になったライト兄弟の物語。名声を得るまでの兄弟愛や家族の応援、ライバルとの戦いなど、ハート・ウォーミングなエピソードを散りばめつつドラマティックに描き出した航空映画。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUEGOS", "フエゴス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "“FUEGOS”とは、家族、炎、情熱の意味。20世紀初頭の南米の小村。世界中で民主主義革命の炎が燃えさかるなか、典型的な封建的家族制度に支配されたある一家が、一人の少女によって崩壊していくさまを、ギリシャ悲劇さながらに映し出す。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES OISEAUX VONT MOURIR AU PE´ROU", "ペルーの鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ゴダール監督の「勝手にしやがれ」に出演、ヌーヴェル・ヴァーグのヒロインとして脚光を浴びたジーン・セバーグが、当時の夫でもあった作家ロマン・ギャリーの映画初監督作で主演を務めた異色ドラマ。南米ペルーを舞台に、異常な性欲にとらわれている女と、その夫の苦悩を倦怠なムードの中に描いていく。", "1968-01-01", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山形スクリーム", "山形スクリーム", "", "", "http://image.eiga.k-img.com/images/movie/53841/315x210.jpg?2965", 116, "個性派俳優・竹中直人が4年ぶりにメガホンを取ったホラーコメディ。都立高校に通う美香代は、所属する歴史研究会の合宿で山形県の御釈ヶ部村に行くことに。そこでは村おこしの一環として「祠倒し」というイベントが行われており、テーマパーク建設を企む村長たちによって、落ち武者の霊が祀られた祠が掘り起こされてしまう。その晩、800年の眠りから蘇った落ち武者たちが村人たちを襲いはじめ……。主演は「神童」「罪とか罰とか」の成海璃子。", "2009-08-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jean-Michel Basquiat: The Radiant Child", "バスキアのすべて", "", "", "http://image.eiga.k-img.com/images/movie/55761/315x210.jpg?3082", 93, "80年代のニューヨーク・アートシーンをすい星のごとく駆け抜けた夭逝の画家ジャン＝ミシェル・バスキアに迫るドキュメンタリー。路上のスプレーペイントからキャリアをスタートしたバスキアは、瞬く間にトップ・アーティストの座にのぼりつめるが、薬物の過剰摂取で27歳の若さでこの世を去った。バスキアの友人でもあったタムラ・デイビス監督が、バスキア本人の貴重なインタビュー映像や関係者の証言を交えながらその素顔と真実に迫る。", "2010-12-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("英雄", "HERO", "", "", "http://image.eiga.k-img.com/images/movie/1081/315x210.jpg?3710", 99, "監督デビュー作「紅いコーリャン」（87）でベルリン映画祭金熊賞、「紅夢」（91）でベネチア映画祭銀獅子賞、「活きる」（94）でカンヌ映画祭審査員賞の名匠チャン・イーモウが、唐代の詩仙、李白の詩に発想を得て、武侠映画に初挑戦。「ストーリーを色彩で語る」をコンセプトに、撮影にクリストファー・ドイル、アクション監督に「スパイダーマン」「少林サッカー」のチン・シウトンを配し、始皇帝暗殺を企てる刺客5人を描く。", "2003-08-16", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("智勇三宝／Mr.BOO MEETS POM POM", "帰ってきたMr.Boo！ ニッポン勇み足", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "香港のドル箱シリーズ“Mr.Boo”と“ポムポム刑事”の二つが合体した爆笑アクション・コメディ。愛人と日本へ逃避行したブーの妻を追って、ブーとポムポム刑事も日本へ……。香港コメディの立役者３人が、これでもかとばかりに繰り出すギャグの連続で、大いに笑える作品。", "1985-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAT BOY", "ラット・ボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ウィンドー・ドレッサーをしているニキは、全身ネズミの体をした“ラット・ボーイ”の存在を知り、彼をマスコミに売って一獲千金を得ようとたくらむ。ある日、ボーイを連れTV局へ売り込みに行ったニキは、局の男と談笑中にボーイを見失ってしまう。イーストウッドのパートナーだったS・ロックの監督作品。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nothing in Common", "恋のじゃま者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "仕事も私生活も順風満帆の広告代理店のエリート、デビッド。気ままな生活を楽しんでいた彼の元へ、疎遠だった父親が突然、転がり込んでくる。デビッドは別居したという両親双方に振り回され……。親と子の愛情をコミカルに綴った家族ドラマ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El Baile De La Victoria", "泥棒と踊り子", "", "", "http://image.eiga.k-img.com/images/movie/55131/315x210.jpg?1239", 130, "金庫破りの名人ベルガラとアンヘルは、民主政権が戻ったチリで、大統領の恩赦のもと刑務所を出所する。離ればなれになってしまった家族を取り戻し、人生をやり直そうとするベンガルに対し、アンへルは刑務所の所長に復讐するため、再び犯罪に手を染めようとしていた。監督は、「ベルエポック」で第66回アカデミー外国語映画賞を受賞したフェルナンド・トルエバ。2009年のアカデミー外国語映画賞のスペイン代表作品で、「スペイン映画祭2009」にて上映。", "2009-12-10", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Year of the Gun", "イヤー・オブ・ザ・ガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "　1978年、ローマに訪れたアメリカ人ジャーナリストのデビッドはテロ組織“赤い旅団”に興味を抱き、それを題材にした小説を書き始める。しかしその後、フィクションであるはずの小説と酷似した事件が発生。やがてデビッドと、フォト・ジャーナリスト、アリソンの身に危険が迫る……。実際に起こった事件をもとに書かれた小説を映画化。ジャーナリストの葛藤を描くサスペンス。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Assignment", "アサインメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "数々のテロを繰り返し、罪なき人々を殺してきたテロリスト、カルロス。米海軍少佐ラミレスは彼と瓜二つであることから、CIAのヘンリーからカルロス逮捕の協力を頼まれる。ヘンリーとイスラエル諜報機関のアモスの監視の下、ラミレスはカルロスになりきるための訓練を開始した。その中で、ヘンリーたちは彼に精神さえもカルロスに近づくことを強要していく。やがて、作戦の実行の日がくるが……。誠実で愛国心にあふれた主人公が任務のために心の悪の部分をさらけ出し、苦悩する姿を描く。脇を固めるD・サザーランド、B・キングスレーのクセのある演技も光る。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("URBAN COWBOY", "アーバン・カウボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "ロデオ・マシーンに熱中する若者の恋愛模様を描く青春ラブ・ストーリー。田舎町から叔父を頼ってヒューストンにやって来たバッドの唯一の楽しみは、全米一の規模を誇るバーで遊ぶこと。彼はそこで知り合ったシシーと結婚し、二人はバーにあるロデオ・マシーン“ブル”に夢中になる。結婚生活は様々なトラブルが生じ、バッドは叔父の死をきっかけに、ロデオ・コンテスト優勝に意欲を燃やす……。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛情来了／LOVEGOGO", "ラブゴーゴー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "デビュー作「熱帯魚」に続くC・ユーシュン監督のラブ・コメディ。平凡なパン屋職人アシェン、彼の前に客として現れた小学校時代の憧れの女の子リーファ、ポケベルを拾って恋の予感にウキウキのおデブな少女リリー、痴漢撃退グッズのセールスをしている青年アスン。偶然交錯する彼らの人生が、オムニバス風のシャレた構成で語られていく。特にアシェンとリーファのエピソードの舞台となるパン屋を、ファンタスティックな空間に仕立てた演出が絶品。ポップな音楽の使い方もうまく、切なくも清々しい後味が残る秀作だ。", "1997-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("棒／Bastoni", "棒／Bastoni", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "アダルト・ビデオの男優、山口はAV女優スターの夏生と結婚し、その後も夫婦でAV業界で活躍し続けていたが、そこに山口のかつての恋人・美由紀が現れたことから彼の生活が狂い始めていく……。AV業界に生きる若者の、悲喜こもごもの青春模様を、これがデビューとなる中村和彦が軽やかに描いた佳作。業界の内幕を覗き見られるというお楽しみもある。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mein Fuhrer", "わが教え子、ヒトラー", "", "", "http://image.eiga.k-img.com/images/movie/53651/315x210.jpg?2699", 95, "\"ヒトラーに演説指導をした人物がいた\"という史実を基に、独裁者ヒトラーの再生を命ぜられたユダヤ人男性がたどる壮絶な運命を、ブラックユーモアを交えながら描いた人間ドラマ。惜しくも本作が遺作となった「善き人のためのソナタ」の名優ウルリッヒ・ミューエが、強制収容所にいる家族のため憎きヒトラーへの忠誠を余儀なくされた主人公の葛藤を見事に表現。監督・脚本は、自身もユダヤ人である「ショコラーデ」のダニー・レビ。", "2008-09-06", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELLO，DOLLY！", "ハロー・ドーリー！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "ブロードウェイの大ヒット・ミュージカルの映画化。世話好きの未亡人ドーリーはホーレスに結婚の世話をしているうちに彼に好意を持ちはじめ、自分を売り込んでしまう。J・ハーマンのタイトル・スコア、『ハロー・ドーリー』を歌い踊るシーンは絢爛豪華でミュージカル・ファンならずとも必見。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版BLOOD-C The Last Dark", "劇場版BLOOD-C The Last Dark", "", "", "http://image.eiga.k-img.com/images/movie/57241/315x210.jpg?1599", 0, "押井守企画協力のもと製作された中編アニメーション「BLOOD THE LAST VAMPIRE」（2000）を発端に、「セーラー服に身を包んだ少女が日本刀を武器にバンパイアと戦う」というコンセプトのみを引き継ぎ、TVアニメ「BLOOD＋」（05）、香港・フランス合作で韓国の人気女優チョン・ジヒョンが主演した実写映画「ラスト・ブラッド」（08）とさまざまな作品が展開されてきた人気シリーズのTVアニメ「BLOOD-C」（11）の劇場版。青少年保護法の下、未成年の夜間外出やネット利用が規制された東京で、自由を求める若者たちのグループ「サーラット」が、東京を支配する七原文人の背後につく組織「塔」で人間を使ったある実験が行われていることをつかむ。サーラットのメンバーが真相を暴こうと動き出したとき、突如として異形のモノが出没して市民を襲うが、そこへ日本刀を手にした少女・小夜が現れる。監督はTVシリーズのコンセプトデザイン、オープニング演出を手がけた塩谷直義。", "2012-06-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Relation of Face, Mind and Love", "顔と心と恋の関係", "", "", "http://image.eiga.k-img.com/images/movie/55411/315x210.jpg?1995", 105, "日本人脚本家と韓国のスター俳優がタッグを組む、日韓共同ドラマプロジェクト「テレシネマ7」シリーズ。若手の有名建築家テプンはある日、自慢の高級車を運転中に衝突事故を起こしてしまう。幸い大怪我には至らなかったが、医師から一時的な視覚障害が起こる可能性があると診断される。その夜、テプンは事務所の前で泥酔している雑誌編集者のソジュンに一目ぼれするが、それは視覚障害でブサイクなソジュンが美女に見えているだけだった。2人はデートを重ね、ついに結ばれるが……。「映画は映画だ」のカン・ジファンが主演を務める。", "2010-05-29", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUCKY LUCIANO", "コーザ・ノストラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "マフィアのドン、ラッキー・ルチアーノの晩年を社会派のF・ロージ監督がドキュメンタリー・タッチで描く。1946年、アメリカの刑務所から釈放されたルチアーノは母国イタリアに戻る。長年麻薬を追跡した捜査官が組織を暴こうとするが……。", "1973-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZU NEUEN UFERN", "世界の涯てに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "スウェーデン出身で、戦前ドイツのオペレッタの女王ツァラ・レアンダーの映画での出世作。“第２のガルボ”といわれただけあって、レアンダーの端正な美しさは一目見る価値がある。愛する男の罪を被ってオーストラリアへ流刑される美貌の歌姫がたどる運命の過酷なすれ違い。どこまでも男をかばい信じ続ける女心のいじらしさ。レースや紗を使った美しい画面作りの感じは、できればスクリーンで観てみたい作品。ダグラス・サーク監督のドイツ時代の作品で、クレジットはドイツ時代の名前デトレフ・ジールク。", "1937-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／CHUNHYANG", "春香伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "韓国の伝統芸能パンソリで歌い継がれてきた名作を「風の丘を越えて　西便制〈ソピョンジェ〉」のイム・グォンテクが映画化したメロドラマ。李朝時代。貴族の息子、夢龍（モンニョン）が、妓生の娘、春香（チュニャン）に一目惚れし、二人はたちまち恋に落ちる。人目を忍んで愛を育み結婚を誓い合うが、幸せはつかの間、夢龍が父の昇進で土地を離れることになる。再会する日を心の支えに離れ離れになった二人は、その後降りかかる数々の試練に立ち向かい愛を貫こうとする。身分を超えた若い男女の純愛を、パンソリの力強い歌声と、四季を捉えた美しい映像でみずみずしく映し出す。", "2000-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SECRET BEYOND THE DOOR", "扉の陰の秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "多彩な娯楽作を撮り続けてきた映画史に残る巨匠監督F・ラング監督による本作は、サスペンス・スリラーのジャンルでその手腕を遺憾無く発揮した逸品。ヒッチコック監督作品の「サイコ」などともシンクロする名作だ。メキシコに観光でやって来た富豪の娘が、知り合った男性にひと目惚れしてそのまま結婚することに。しかし、彼の自宅を訪れると、彼の息子、姉、女秘書やらがおり何やら怪しげな雰囲気。そして、さらに……。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄と鉛", "鉄と鉛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "オリジナル・ビデオ作品を含めると第４作目となる漫画家、きうちかずひろの監督作。ガン・アクションへのこだわりを見せた独自のバイオレンス感覚を発揮。ベテランの渡瀬恒彦を主役に、殺人を予告された私立探偵の運命をスリリングに描く。自分が探し当てた男が殺されたために、その父親であるヤクザの組長の恨みを買った探偵。犯人の女は組長が放ったヒットマンに殺害され、探偵にも命の危険が迫るなか、彼は残された最後の仕事に取りかかるのだった。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刺馬／THE BLOOD BROTHERS", "ブラッド・ブラザーズ　刺馬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "武術映画の鬼才チャン・チュが手掛けたカンフーアクション。山中で出会い、義兄弟の契りを交わした３人の武術の達人。数年後に再会を果たしたとき、そのうちのひとりは将軍の座にまで上り詰めていた。やがて彼らの友情を引き裂く事件が起こり……。若き日のジョン・ウーが助監督として参加。本作を参考にして、彼は「ワイルド・ブリット」を作ったといわれている。", "1956-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヘルドライバー", "ヘルドライバー", "", "", "http://image.eiga.k-img.com/images/movie/56051/315x210.jpg?1595", 117, "ある事件をきっかけに多くの人間がゾンビとなった未来。日本列島は元人間のゾンビと生き残った人間たちを壁で分離し、戦いを続けていた。その事件に関係する母親によって父親の命を奪われ、自らの心臓も失った少女キカは、ナゾの組織により人工心臓を埋め込まれて生きながらえていたが、母親に復しゅうするため、日本刀型のチェーンソーを武器にゾンビ軍団と戦う。「東京残酷警察」が話題となった西村喜廣監督が手がけるゾンビアクション。", "2011-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("寒夜／CHILLY NIGHT", "寒夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "中国の著名作家、パー・チンの生誕80周年を記念して製作された作品。1944年、抗日期の重慶。注文宣と曽樹生の二人は、10年前の学生時代からともに理想に燃え、同棲生活を送っていた。二人の理想は、新しい教育のための学校を作ることだった。しかし、現実は厳しく……。", "1984-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENEMY OF THE STATE", "エネミー・オブ・アメリカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "有能な若手弁護士ディーンはある日、汚職の疑いをかけられ、その地位や名誉を失ってしまう。罠にはめられたのを知った彼は独自に調査を進めていくが、実はこの陰謀の裏には国家安全保障局の行政官レイノルズが絡んでいた。彼は国民のプライバシーを侵害する可能性を秘めた“通信システムの保安とプライバシー法案”を成立させるために反対派の議員を殺害したが、その現場を捉えたビデオが何も知らないディーンの手に渡っていたのだ。レイノルズは持てる最新技術を駆使し、ディーンを追い詰めていく。一方、ディーンは謎の情報ブローカー、ブリルとの接触を図るが……。いとも簡単にプライバシーを暴き出す発信機、盗聴器、衛星監視カメラといったハイテク装置の不気味さ。そして、それらに付け狙われる男が味わう恐怖を描いたポリティカル・サスペンス。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Que he hecho yo para merecer esto!!", "グロリアの憂鬱／セックスとドラッグと殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "平凡な主婦が常軌を逸していく姿を、「オール・アバウト・マイ・マザー」等の作品で知られるスペインの鬼才、ペドロ・アルモドバル監督が描いた作品。マドリッドの巨大な団地に住む主婦グロリア。夫とはうまくいかず、家計は火の車。しかも長男はドラッグの売人で、次男はゲイ……。そんなうんざりするような毎日に我慢ならなくなった彼女は、次第に行きずりのセックスやドラッグに溺れていく。そしてついに殺人まで犯してしまい……。", "1984-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRISONER OF ZENDA", "ゼンダ城の虜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "1894年に発表された英国作家アンソニー・ホープの同名小説の４度目の映画化。国王の戴冠式を目前に控えた欧州の王国ルリタニアで、国王が何者かに毒酒を飲まされて死亡する。たまたま休暇を過ごしにやって来ていた英国青年ルドルフが国王と瓜二つであったことから、彼は側近に言われるままに、王になりすまして戴冠式に臨むが……。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("中島みゆきLIVE 歌旅劇場版", "中島みゆきLIVE 歌旅劇場版", "", "", "http://image.eiga.k-img.com/images/movie/58081/315x210.jpg?1321", 123, "日本を代表するシンガーソングライター・中島みゆきが2007年に全国17カ所で行い、10万人を動員した「中島みゆきコンサートツアー2007」の模様を収録した「歌旅　中島みゆきコンサートツアー2007」を再編集し、5.1chサラウンドの音響など映画館での上映のために新たに製作された劇場版。「地上の星」「ファイト！」「糸」「宙船（そらふね）」などのヒット曲を網羅し、今回が初公開となる「荒野より」（木村拓哉主演ドラマ「南極大陸」主題歌）のPVロングバージョンも収められている。", "2012-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Let's Get Harry", "ハリー奪還", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "南米コロンビアでテロリスト・グループに誘拐されたアメリカ人建築技師ハリー。複雑な政治情勢から、なかなか腰を上げない政府に業を煮やし、ハリーの弟コーリーが仕事仲間とともに救出作戦を敢行するが……。スリル満点のアクション映画。なお、製作時のトラブルにより監督のスチュアート・ローゼンバーグはクレジットされず、アラン・スミシーという匿名でクレジットされている。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHRUNKEN HEADS", "シュランケン・ヘッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "いつもギャングにいじめられていたトミーら３人は、ある日、ギャングたちの悪行を警察にチクったことで、殺されてしまう。ところが、街のスタンドで雑誌を売っていたスマトラが、実はハイチの妖術師で、ブードゥーの秘術を使って彼らをよみがえらせてしまった。超自然の力を持ち、頭の部分だけよみがえったトミーたち。彼らはいじめ殺された復讐のため、ギャングを襲い始める。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH ROAD TO CHINA", "ハイ・ロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "実在した人物をモデルにしたJ・クリアリー原作の冒険小説の映画化。失踪した父親を捜しにヒーローとヒロインが二機の複葉機に乗り込み中国大陸へと出発する。クライマックスのヒマラヤ越えやネパールでの空中戦など、飛行機ファンにはうれしい一編。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Auf der Anderen Seite", "そして、私たちは愛に帰る", "", "", "http://image.eiga.k-img.com/images/movie/53941/315x210.jpg?1241", 122, "2007年カンヌ国際映画祭にて最優秀脚本賞と全キリスト協会賞を授賞したドイツ・トルコ合作のヒューマンドラマ。監督・脚本は「愛より強く」（2004）でベルリン映画祭金熊賞を受賞した俊英ファティ・アキン。余生を娼婦と暮らす父と大学講師の息子、娼婦の母と反政府活動家の娘、友人を救うためイスタンブールに旅立つ娘と娘の身を案じながらも愛情を伝えられない厳格な母。ドイツとトルコを舞台に、すれ違う3組の親子の人生の旅路を描く。", "2008-12-27", "7,32", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMMANUELLE ４", "エマニュエル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "35歳を迎えたエマニュエルがブラジルで魔術的な美容整形を受け、20代初めのみずみずしい肉体に若返り、真実の愛を探して性のアバンチュールにのめり込んでいく。手術前のヒロインに当り役のS・クリステルが扮して特別出演している。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Riot in Cell Block 11", "第十一号監房の暴動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "　刑務所の所長レイノルズは囚人たちの待遇改善について刑務委員会に進言。しかしこれが受け入れられず、囚人たちの暴動が起こる。首謀者のダンとカーニーは必死の抵抗を試みる。レイノルズは彼らとの交渉を開始するが。囚人たちの戦いを描いたドラマ。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("永遠の人", "永遠の人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "昭和７年の九州阿蘇。地主・小清水の小作人の娘・さだ子には隆という恋人がいた。地主の息子・平兵衛はある日、片足を負傷して戦地から復員する。さだ子は平兵衛に犯され入水自殺を企てるが助けられる。12年の歳月が流れ、隆に去られたさだ子は平兵衛と結婚する。木下惠介得意のいわゆる年代記もので、全体が５章より成る昭和７年から昭和36年までのさだ子と平兵衛の愛憎、というよりは憎悪の半生記である。フラメンコのリズムに乗せて、従来の木下調リリシズムを裏返したように展開する“永遠の憎しみ”に貫かれた異色のホームドラマ。木下惠介はこの作品を木下作品には欠かせない高峰秀子のために作り上げたと言っている。吉田喜重が助監督として参加している。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BETRAYED", "背信の日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "FBI秘密捜査官のケイティは、やもめ暮らしの農場主ゲリーと恋に落ちる。だが、ゲリーは実は、白人至上主義テロリスト・グループの首謀者的存在だった……。社会派監督コスタ・ガヴラスが、人種差別が根強くはびこる現代アメリカの暗部を暴き出した、衝撃の問題作。愛と正義の間で苦悩するケイティを、D・ウィンガーが体当たりで熱演している。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flags of Our Fathers", "父親たちの星条旗", "", "", "http://image.eiga.k-img.com/images/movie/33411/315x210.jpg?2515", 132, "　「許されざる者」「ミリオンダラー・ベイビー」のクリント・イーストウッド監督が、太平洋戦争最大の激戦だったといわれる硫黄島の戦いを日米双方の視点から描く映画史上初の2部作。本作はその第1弾で、アメリカ側の視点による作品。硫黄島の擂鉢山に星条旗を打ち立てた6人の兵士の写真の真実と、戦場から生き残り米本土に帰還した3人のその後の人生を描く。", "2006-10-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PERFECT BLUE", "PERFECT BLUE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "キャラクター原案に江口寿史、企画協力に大友克洋が参加した異色アニメーション。美少女アイドルに降りかかる恐怖を、リアルな描写と巧妙なストーリー展開で描くサイコ・サスペンス。人気絶頂期にアイドル・グループから脱退した美少女・未麻。女優への転身を図る彼女の周囲で怪事件が続発する。熱狂的なファンのストーカーまがいの行為におびえる未麻は、やがて意外な犯人の正体を知る……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺人がいっぱい", "殺人がいっぱい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "マザーグースの童謡にのせて次々と起こる殺人事件の謎に挑む、お嬢さま育ちの母娘の活躍をコミカルに描いたユーモア・ミステリー。ピンク映画の奇才として知られる中村幻児が「V・マドンナ大戦争」以来、６年ぶりの劇場映画で、随所に独特のパワフルな映像演出を見せる。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DINNER　RUSH", "ディナーラッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ミュージック・ビデオやCMの分野で活躍してきたB・ジラルディ監督が、自らが経営するレストランで撮影したサスペンスフルな群像劇。ニューヨーク、トライベッカのイタリアン・レストラン“ジジーノ”で売り出し中の花形シェフ、ウードと、その父親である昔気質のオーナー、ルイス。まったく異なる価値観を持つ彼らの葛藤を軸に、店の乗っ取りをもくろむ新興マフィア、個性豊かな店員や客たちの一夜の人間模様が繰り広げられていく。戦場のごとき慌ただしさで次々と華麗な料理を作るキッチンの様子など、臨場感あふれる映像に引き込まれる。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忠臣蔵 赤垣源蔵 討入り前夜", "忠臣蔵 赤垣源蔵 討入り前夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "赤穂浪士の一人赤垣源蔵の、ご存じ徳利の別れを、阪東妻三郎が情感豊かに演じる時代劇。のんべで浪士くずれの赤垣は、毎日酒びたりの生活を送っていたが、大石内蔵助の密書を持つ二人連れを敵から救ったことから討ち入りに参加していく。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女と三悪人", "女と三悪人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "江戸両国の女座長率いる芝居の一座。大道芸人の元締め、竜運和尚と一座のお囃子をつとめる浪人、鶴木は、女座長に惚れている。そこへスリの一味と間違えられた旅の男が逃げ込んで来る。彼女はこの男に一目惚れ。一座の借金を肩代わりする代わりに座長の体を自由にしている但馬屋から座長を救うべく、旅の男と鶴木らは協力するのだが……。コメディ・タッチのドタバタ時代劇。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("港祭りに来た男", "港祭りに来た男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "マキノ雅広監督が情感豊かに描く時代劇の好編。七夕祭りに湧く港町に、曲芸師や香具師の一行がやって来る。その中にいた居合い抜きの名人は、藩主の妾・お夕の恋人だった漁師・彦一の成れの果て。それを知った藩主は二人をなぶり者にし、二人は抱きあって死んでいく。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刺青 IREZUMI", "刺青 IREZUMI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "犯罪都市ロサンゼルスでヤクザの抗争に巻き込まれ、愛児を失った娘ジーナ。彼女は犯人の背に“青い虎”の刺青を見た。復讐を誓った彼女は、伝説に従って“赤い虎”を彫り、運命の出会いを待つ……。ビデオ用に米国で製作された“東映Vアメリカ”の一編だが、日本では劇場公開された。「キャンディマン」のV・マドセン、「パリ、テキサス」のH・D・スタントン、「レザボア・ドッグス」のM・マドセンらの怪演は見逃せないところ。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pearl Jam Twenty", "パール・ジャム 20", "", "", "http://image.eiga.k-img.com/images/movie/56961/315x210.jpg?2780", 123, "1990年代のオルタナティブ・ロック・ムーブメントを「ニルヴァーナ」とともにけん引し、2010年に活動20周年を迎えた世界的ロックバンド「パール・ジャム」に迫ったドキュメンタリー。パール・ジャムと親交も深いキャメロン・クロウ監督が、1200時間に及ぶ映像を編集。未公開のインタビュー、ライブ映像を多数織り交ぜ、パール・ジャム20年の成長と苦悩の軌跡をたどる。ニール・ヤングや故カート・コバーンら、パール・ジャムと関係の深い人物たちも登場する。", "2011-10-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOROCCO", "モロッコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "外人部隊一のプレイボーイ、トムは酒場の女アミーに惹かれるが、嫉妬深い副官の手でサハラの戦線に送られる。一人とり残されたアミーは絶望し、パリの紳士との結婚を決意する。披露パーティーの日、トムが重傷を負ったと聞いたアミーは現場に駆けつけるが……。ドイツでトーキーの技術をマスターしたスタンバーグがその本領を発揮した傑作。粋な外人部隊の帽子をかぶり、楽屋の鏡に口紅で女への別れの言葉を書きなぐるクーパーの伊達男ぶりにもまして、ラスト、砂漠へ出発するトムを、ハイヒールを脱ぎ捨てながら追いかけていくディートリヒの迫真の演技は、絶望的な愛をあざやかに捉えていて秀逸のきわみ。", "1930-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Immortal", "ゴッド・ディーバ", "", "", "http://image.eiga.k-img.com/images/movie/1071/315x210.jpg?3678", 104, "人間やミュータント、エイリアンが混在する2095年のニューヨーク。他の神々から死刑を宣告された古代神ホルスは、マンハッタン上空に浮かぶピラミッドから飛び立ち、青い涙を流す女ジルを探すため、冷凍刑から目覚めた政治犯ニコポルの体に入り込む。フランス・コミック界の巨匠で、様々アーティストに影響を及ぼすエンキ・ビラルの監督第3作。俳優は主演の3人以外はほぼ全てがCGで作られている。", "2004-05-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋するマドリ", "恋するマドリ", "", "", "http://image.eiga.k-img.com/images/movie/34131/315x210.jpg?1471", 113, "TVドラマやCMで人気急上昇中のアイドル・新垣結衣が映画初主演を果たしたラブストーリー。1人暮らしを始めた美大生のユイは、新しい部屋の元の住人アツコと出会い、大人っぽい彼女に憧れを抱くように。同じ頃、ユイは上の階に住む物静かな男性タカシのことが気になり始める。しかし、タカシは元恋人であるアツコへの想いを引きずっており……。共演には「バベル」の菊地凛子、松田龍平、世良公則ら豪華キャストが顔を揃える。", "2007-08-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モスラ", "モスラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ゴジラと人気を二分する正義の怪獣・モスラの1990年代版シリーズ第１作のSFファンタジー。北海道の森林開発現場の崖の中から、不気味な岩が現れる。商社マンが、娘のプレゼントにと岩の中央に光るメタルを剥がし取って持ち帰る。同じ頃、インファント島で危険を感じた妖精のモルとロラの姉妹は東京の後藤家に向かうが、すでにメタルを奪うために黒い妖精が現れていた。メタルを巡り戦いを繰り広げるが、ベルベラが勝利し北海道の巨大な岩に封じ込められていた魔獣デスギドラを解き放してしまう。姉妹はモスラを呼び寄せるために歌い始める……。地球環境問題や、家族の絆をテーマに映画化。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("藏", "藏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "『毎日新聞』連載時より評判を呼んだ宮尾登美子のベストセラーの映画化。大正から昭和初期、雪深い新潟の蔵元・田乃内家に生まれ、“烈”という女の子に似合わぬ猛々しい名をつけられた少女とその養母・佐穂のドラマを中心に、旧家の波乱に満ちた変転を描く文芸大作。烈が、視力を失いながらも、親の反対を押し切って、若い蔵人・涼太との恋を成就し、女性初の蔵元となって家を継ぐまでが描かれる。宮尾文学はこれが４度目になる高田宏治が脚本を担当、監督は、文芸作品で常に堅実な力量を見せる降旗康男。なお、主役の烈には当初、宮沢りえが予定されていたが、キャスティングの不満から降板、一色紗英に交代した。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIBLING RIVALRY", "マージョリーの告白", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "平凡な主婦の浮気騒動を描いたラブ・コメディ。結婚８年目の医者の妻マージョリー。一見幸せそうだが、実は満たされていない。夫一族の異様な団結の固さと、夫との性生活にいや気がさしていたのだ。ある日、彼女はとうとう浮気をする。ところが、その相手が腹上死、しかも彼は、その夜一族の歓迎パーティーで初めて会うはずの夫の兄だった……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN WHO SHOT LIBERTY VALANCE", "リバティ・バランスを射った男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "東部の大学で法律を学び西部へとやって来た青年ランスは、途中、無法者で鳴らすリバティ・バランスに襲われ重傷を負う。その看護をしてくれたのは地元の牧場主トムとその恋人ハリーだった。やがてハリーとランスは愛し合うようになる。ある時、ランスは住民運動をめぐってリバティ・バランスと対決、彼を倒したことで住民たちから信頼を得、議員としてワシントンに送り出されようとするのだが……。J・フォードならではの男のドラマが全編に展開する傑作西部劇の１本。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAPTAIN AMERICA", "キャプテン・アメリカ　帝国の野望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "第二次大戦中、米軍の開発により生まれた改造人間“キャプテン・アメリカ”が活躍する。第二次大戦という時代を反映して、彼の任務はナチスの進撃をくい止めること。だが、ナチスが作り上げた悪の戦士レッド・スカルの罠に落ち、アラスカの奥地に閉じ込められてしまう……。“キャプテン・アメリカ”の友人としての大統領役にR・コックス、正義感あふれる新聞記者にN・ビーティといったベテラン脇役がならんでいる。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DELTA FORCE COMMANDO", "デルタ・フォース・コマンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "イタリア産のバイオレンス・アクション。反政府ゲリラに妻を殺された元“デルタ・フォース”隊員が、復讐のため友人とたった二人で敵陣へと乗り込んでいく。多勢を相手に二人で熾烈な戦闘を繰り広げるその戦いぶりは、理屈抜きにケタはずれの迫力。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雨が舞う　金瓜石残照", "雨が舞う　金瓜石残照", "", "", "http://image.eiga.k-img.com/images/movie/54461/315x210.jpg?2736", 0, "かつて東洋一の金鉱として栄えた台湾北部にある“金瓜石”にまつわる人々を追ったドキュメンタリー。 1930年の全盛期を迎え1987年に閉山するまで、金瓜石では数多くの日本人と台湾人が生活を共にしていた。しかし太平洋戦争が始まると金鉱の労働力は不足し、金瓜石には捕虜収容所が設置され……。", "2009-03-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ソーローなんてくだらない", "ソーローなんてくだらない", "", "", "http://image.eiga.k-img.com/images/movie/56871/315x210.jpg?4714", 102, "青春と性をテーマに監督たちが独自の個性で自由に製作する「青春H2（セカンド）」シリーズの4作目。自称役者の青年・晴生はバイト先のかわいい研修生・百瀬とセックスを試みるが、早漏でズボンを脱ぐ途中で終わってしまう。その日から晴生は早漏克服のための涙ぐましい努力を始めるがうまくゆかず、同じアパートでルームシェアするノンに助けを求める。アパートの共用スペースの掃除を条件に、ノンも嫌々ながら晴生に手を貸すことになるが……。監督は「ユリ子のアロマ」の吉田浩太。", "2011-08-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("教室の子供たち", "教室の子供たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "東京の下町の、小学校２年生の授業風景を生き生きと捉えたドキュメンタリー。勉強する子供たちの自然な生態を記録する方法としての隠し撮りを、羽仁監督は拒否。子供たちが教室に据えられたカメラやスタッフを意識しなくなるまで、十分な時間をかけてこれを撮り上げた。当てられて困ってしまう子供、好き勝手にふるまう子供、椅子から離れて遊び歩く子供。文部省の委嘱作品ながら、子供たちが一心に教科書を読み、黒板を見つめ、教条的なお役所映画とはひと味違う活気的な仕上がりとなった。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二刀流開眼", "二刀流開眼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "片岡千恵蔵主演、伊藤大輔監督による吉川英治原作『宮本武蔵』の映画化。二刀流開眼に旅立つ剣豪・武蔵の若き日の物語を、細かなエピソードを積み重ねて力強く語る。武蔵は千恵蔵の当たり役の一つであり、1937年の第２回映画化（監督・尾崎純）に始まって、1940年、1942年の稲垣浩監督版、そして本作の後、同じ伊藤大輔監督の「決闘般若坂」と続く。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JANIS", "ジャニス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1960年代末にロック界最高のボーカリストとして君臨し、1970年10月、27歳で夭逝したジャニス・ジョプリンの姿を捉えたドキュメンタリー。ナレーションや劇的構成をいっさい省き、ステージで歌うジャニスと彼女に対するインタビューのみでシンプルに仕上げられ、それだけにジャニスの生の声が胸にしみる。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEFENSELESS", "ディフェンスレス 密会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "女弁護士のT.K.は、ポルノ映画の密造事件で訴訟中の依頼人と不倫の仲に。だが、依頼人が何者かに殺されたことから、彼女は殺人の容疑者として逮捕される。やがて彼女は獄中で襲撃の真実を知り……。「ライトスタッフ」の夫婦役B・ハーシーとS・シェパードが再共演を果たした、愛憎渦巻くサスペンス。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMANDLA！　A　REVOLUTION　IN　FOUR　PART　HARMONY", "アマンドラ！希望の歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "南アフリカの黒人たちからすべてを奪った白人政府による人種隔離政策“アパルトヘイト”。この悪しき人種差別は同国で40年以上に渡り続いた。本作はこのアパルトヘイトに“歌”を武器に対抗した人々のドキュメンタリー。人権無視に、歌を歌うことで立ち向かった勇気ある黒人たちの闘いの日々がフィルムに刻まれる。その心の叫びともいえる歌と詞が胸を打つ。アメリカの人権活動家だった青年が南アフリカで見たものは、アパルトヘイトで抑圧される黒人たちの姿。彼らは処刑台に立たされても自由と反逆を歌い続け、“歌による革命”はついに40年以上にも及んだ白人支配に終止符を打つ。", "2002-01-01", "67,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOLAS", "ローサのぬくもり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "都会暮らしの女性と、その母親の愛と葛藤を描いた人間ドラマ。35歳のマリアは奔放な一人暮らしを続けるうちに図らずも妊娠。そんなある日、入院した父の付き添いで母ローサが上京してきた。マリアは母に反発するが、彼女は娘の荒んだ生活をとがめず、そっと気配りをみせる……。静かな描写のなかに親子の愛がにじみ、深い余韻を残す。", "1999-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POPIELUSZKO", "ワルシャワの悲劇 神父暗殺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "戒厳令下の激動のポーランドを舞台に、自由を訴え、信念を貫き通そうとする若き神父と、狂信的な愛国者である秘密警察官の闘いを描く社会派ドラマ。二人の主人公を演じるC・ランバートとE・ハリスの迫真の演技が、観る者を引きずり込む。解放前のポーランドで起こった実話の映画化。", "1988-01-01", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユメノ銀河", "ユメノ銀河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「エンジェル・ダスト」「水の中の八月」で幻想的な映像世界を押し進めてきた石井聰亙監督が、夢野久作の小説『少女地獄』の中の一編『殺人リレー』を映画化した作品。バスの車掌のトミ子が、別の町に住む親友ツヤ子の死を知らされる。ツヤ子の婚約者、新高はバスの運転手だが、連続殺人鬼ではないかという噂が立っていた。そんな矢先、トミ子のバス会社に新高が配属されてくる。ツヤ子の仇を討つために、トミ子は新高に接近していく……。危険人物の美青年に恋してしまう少女の揺れる心を、16ミリのモノクロ・フィルムに焼き付けた叙情的な恋愛映画。地方都市の閑散とした空間を緩やかな時間感覚で映し出すカメラが、観る者を夢の中をさ迷っているような錯覚へと導く。捨て身のヒロインに扮した小嶺麗奈、まるで生活感が欠落したバス運転手を演じた浅野忠信の存在感も印象深い。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PORTE APERTE", "宣告", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ファシズム下の1937年３月。トマッソは解雇された芸術家ファシスト同盟を訪れ、元上司、後釜に座った同僚を惨殺し、車で待つ妻も森に連れ込み殺害する。誰もが死刑の判決が下されるのを確信するなか、死刑に反対の裁判官ビトは、何とかその時の隠された状況を探り出そうとするが、上司や同僚、家族、被告のトマッソさえもが彼に背を向ける。そんななか、陪審員・農夫のジョヴァンニが協力を申し出、ビトは味方を得て再び邁進していく。裁判は大詰めを迎え、宣告の日が近づいていた……。死刑をモチーフにし、人間の尊厳とは何なのか？　という問いかけや日常生活の中で失いかけているものを静かに語りかける。", "1990-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バンビ", "バンビ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "ディズニー・アニメのスタイルを確立した記念碑的傑作。後のディズニー作品はもちろん、手塚治虫、宮崎駿監督ら日本のアニメーターにも多大な影響を与えた作品だ。鹿のバンビは、ウサギやスカンクや森の動物たちと一緒に育って大きくなる。その間、人間に母を奪われる悲しみを味わい、美女のシカを巡って恋の争いをする。やがて人間の火の不始末により森が火事になり……。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN AUTRE HOMME，UNE AUTRE CHANCE", "続・男と女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "1870年代のアメリカ西部。妻を失い、息子を寄宿学校に入れて獣医をしている男と、フランスから移住し、写真家の夫に先立たれ、娘を寄宿学校に入れて働く女が、出逢い……というおなじみの「男と女」を西部に置き換えて11年ぶりにリメイクしたルルーシュ作品。", "1977-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eastern Promises", "イースタン・プロミス", "", "", "http://image.eiga.k-img.com/images/movie/34221/315x210.jpg?1581", 100, "「ヒストリー・オブ・バイオレンス」のビゴ・モーテンセン＆デビッド・クローネンバーグ監督が、ロンドンを根城に暗躍するロシアンマフィアの犯罪を描いたサスペンスドラマ。病院で助産婦をしているアンナ（ワッツ）は、駆け込み出産をして死んでしまった少女が持っていたロシア語の日記を手がかりに少女の身元を探し始める。だが、彼女が辿り着いたのはロシアンマフィアによる人身売買、売春の実態だった……。共演にナオミ・ワッツ、バンサン・カッセル、アーミン・ミューラー＝スタール。", "2008-06-14", "3,10,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUTTERFLISTA すべては、愛の行為。", "BUTTERFLISTA すべては、愛の行為。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「叶姉妹」の叶恭子が初監督を務める官能映画。妹（役）の叶美香と、恭子自身も出演する。調教、SM、ラブトイプレー、同性愛、3Pなど常識にとらわれない“愛の行為”をテーマに、「ジェラシーとパッションと信頼関係」「心の目で見えること」「一番感じる部分」 「薔薇の接吻」「彼の望むこと」「プレシャスドールの歓び」の6編のショートフィルムで構成される。", "2010-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("燃える勇者", "燃える勇者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "見知らぬ地方都市にたどり着いた青年が、とある家の世話になる。が、その家の長男が、都市を支配している独裁者の悪事の証拠をつかんだことから殺された。殺人の証拠を見つけるため、長男の友人だったルポライターと青年は、真相究明に乗り出す……。若き日の真田広之の躍動的な立ち回りでみせるアクション。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春よこい", "春よこい", "", "", "http://image.eiga.k-img.com/images/movie/53331/315x210.jpg?2231", 108, "佐賀県唐津市で母と暮らす少年ツヨシは、4年前に殺人を犯し逃亡中の父をいまだに慕っている。父の指名手配写真に手を触れるツヨシを目撃した新聞記者・利夫は、その場面をカメラに収め感動話として地元紙に掲載。しかし記事は町に思わぬ波紋を呼び……。「オリヲン座からの招待状」の三枝健起監督が、逃亡犯の父を待ち続ける母子の姿を通して家族の絆を描いた人間ドラマ。ツヨシの母・芳枝を「SAYURI」などの国際派女優・工藤夕貴が好演。", "2008-06-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プルシアンブルーの肖像", "プルシアンブルーの肖像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "夏休みに入った小学校で、次々と生徒が消えたり、先生が死体で発見されたりと、不可解な現象が起こり始める。そのどの事件にも、学校の用務員の不気味な影があった……。安全地帯の玉置浩二が陰のある用務員役に扮し、少年少女の純愛を絡ませて描いたオカルト映画。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AWDAT IBNU DAAL", "放蘯息子の帰還", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "1960年代末期のエジプト。無実の罪で捕らわれた後、12年ぶりに帰ってきた男を迎える家族のドラマ。一族の柱で工場を経営する独裁者の父トルバは、ヨーロッパ留学を夢見る高校生の息子と対立していたが……。家族の崩壊を描くミュージカル悲劇。シャヒーンの名を高めた作品。", "1976-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA LAPIN DE NOE¨L", "クリスマスのうさぎ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "クリスマス・イヴの日、女学生のベロニクは図書館に行くためタクシーに乗るが、イヴを楽しみたい運転手によって、知らぬ間に他の時代に引きずり込まれてしまうのだった。TVのバラエティー番組だが、日本では1996年、ゲンズブール特集の時に、劇場で上映された。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛の新世界", "愛の新世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "風俗ギャルの生の声を綴ったエッセイと、荒木経惟の写真を融合した同名の写真集を基に映画化。SMクラブで女王様として働きながら女優の道を進む女性と、ホテトルで働きながら結婚資金の貯金に励む現代女性の姿を描いた青春映画だ。小劇団の女優でSMクラブに勤めるレイはある日、ホテトルで働き医者の卵と同棲中のアユミと出会い意気投合する。二人は、夜の街に繰り出すようになり……。日本映画初のヘア・ヌードや主人公を一般公募するなどでも話題を呼んだ。また、オーディションで選ばれた鈴木砂羽が体当たりの熱演を見せ、各種新人賞、女優賞を受賞した。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダー対ショッカー", "仮面ライダー対ショッカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 32, "子どもたちの間に“変身”ブームを巻き起こした特撮テレビシリーズ「仮面ライダー」の劇場オリジナル作品。地質学者の大道寺博士が、地球の地軸さえも変えることのできる人工重力装置GXの設計図を完成させた。これを狙うショッカーの大幹部・死神博士は大道寺の娘を誘拐し、命と引きかえに設計図を渡すよう迫る。指定場所の地獄谷に向かったダブルライダーの前に、エリート怪人ザンジオーと再生怪人軍団が立ちはだかる。", "1972-03-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PEGGY SUE GOT MARRIED", "ペギー・スーの結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "夫と別居し、人生を半ばあきらめかけていたペギー・スーは、同窓パーティーの席でタイム・スリップしてしまう。1960年、高校３年に逆戻りした彼女は二度目の青春を謳歌する……。コッポラが描いた女性版「バック・トゥ・ザ・フューチャー」とも呼べるファンタジー。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水で書かれた物語", "水で書かれた物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "母子相姦を描いた石坂洋次郎の同名小説を、松竹を離れフリーになった吉田喜重が撮った独立プロ第１作。松谷静雄は母・静香との二人暮らしのサラリーマンだが、幼い頃から静雄の父が胸を病み、病院暮らしが多かったためか、内向的な性格だった。静香は女手一つで静雄を育ててきたが、町の権力者・橋本と不倫の関係を結んでいた。静香と橋本は静雄に、橋本の娘・ゆみ子との結婚を勧める。“異母兄弟の結婚だ”と叫ぶ静雄だが、やがてゆみ子と結婚する。苦悩する静雄は母と関係を結ぶ。吉田はフォトジェニックな映像を使って、母子相姦を内向する観念として描いた。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEST SELLER", "殺しのベストセラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1972年、ロスの証拠物件保管所が３人組に襲撃され、ガードの警官３人が射殺された。事件は手掛かりもなく迷宮入りし、時は1987年。先の事件で唯一生き残ったデニスは、ベストセラー作家となり警官の仕事との両立に悩んでいた。ある日、おとり捜査中のデニスは突然現れたクリーブという男に命を救われる。クリーブはかつて殺し屋で、小説を書いてもらうことを条件に元ボスの秘密を暴露するというが……。世界一の犯罪都市を舞台にした、ハードなシーンたっぷりのバイオレンス・アクション。B・デネヒーとJ・ウッズの好対照なキャラクターが見もの。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOUCEMENT LES BASSES！", "もういちど愛して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "A・ドロンが前妻N・ドロンと共演して話題を呼んだコメディ作品。死に別れたと思っていた妻との因果なめぐり合わせにほんろうされる神父の姿を描く。二枚目の代名詞、ドロンが珍しく披露するコミカルな演技が見どころ。", "1971-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京原発", "東京原発", "", "", "http://image.eiga.k-img.com/images/movie/40961/315x210.jpg?3238", 110, "「卓球温泉」の山川元監督が、原発問題をクローズ・アップした問題作。財政難にあえぐ首都・東京を舞台に、カリスマ都知事の“東京に原子力発電所を誘致する！”という爆弾発言から巻き起こる珍騒動が、ブラックな笑い満載で描かれる。原発が現在抱えている諸問題を、リアルなデータを元に次々と浮き彫りにしていく展開がショッキングな一本だ。東京都のカリスマ知事・天馬は、臨時会議の席上で、東京に原子力発電所を誘致すると宣言する。この爆弾発言を巡って会議は大激論へと発展するが、その頃、お台場にはフランスから極秘に大量のプルトニウム燃料が到着していた。", "2004-03-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プライベート・レッスン", "プライベート・レッスン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "日本でもヒットしたアメリカ産のエッチな青春映画をリメイク。美しい家庭教師と日本人の高校生の淡い恋、その影に潜む事件をコミカルなタッチで描いていく。人気グループSMAPの稲垣吾郎をフィーチャーした、アイドル映画の向きも。モデル出身のJ・パクラが妖艶な魅力で迫る。アメリカで製作された作品。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOY", "JOY ジョイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "フランスのベストセラー小説を映画化したエロティック・ドラマ。幼い頃に両親のセックスを目撃して以来、ジョイは男性に嫌悪感を抱くようになる。美ぼうを活かしてモデルになった彼女は、ある日出会った建築家のブルースによって愛欲に溺れるようになるが……。パリ、メキシコ、ニューヨークの美しい街並みを背景に、性に目覚めてファザーコンプレックスを克服していく女性の成長を官能的に描き出す。", "1983-01-01", "10,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Letter to Brezhnev", "リヴァプールから手紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "自由奔放なテレサときまじめなエレイン。仲良し二人組が港に寄った二人のロシア人船員と知り合い、エレインは本当の恋に落ちる。彼が港を去った後、彼の愛が本物か思い悩み、ブレジネフ書記長に手紙を書くのだった。ロンドンなどで大ヒットした低予算映画。", "1985-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Fille sur le Pont", "橋の上の娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ナイフ投げの曲芸師ガボールは、セーヌ川にかかる橋の上から自殺しようとしていたアデルをナイフの的にならないかと誘う。コンビを組んで巡業に出た2人はツキに恵まれ人気も出るが、アデルは行きずりの男たちと関係を重ねるのだった。監督は「仕立て屋の恋 」のパトリス・ルコント。アデルを「ハーフ・ア・チャンス」のバネッサ・パラディ、ガボールを「サン・ピエールの生命」のダニエル・オートゥイユが演じている。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("熊笹の遺言", "熊笹の遺言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "現代では治療法が確立され、完治する病気となったハンセン病。だが患者の多くは国の終生隔離政策により、ようやく違憲を認められたつい最近まで人権を無視され続けてきた。日本映画学校の卒業制作として今田哲史監督が発表した本作は、“日本におけるハンセン病”にアプローチしたドキュメント。映画は群馬にある国立ハンセン病療養所で生活する３人の元患者の現在を追う。ハンセン病違憲国賠訴訟に尽力した谺雄二さん、ハンセン病で亡くなった妹への想いを胸に絵筆を走らせる鈴木時治さん、目の不自由な浅井あいさんのそれぞれが語る過去の事実は想像以上。その重みのある一語一句に耳を傾けてほしい。群馬県草津町にある国立ハンセン病療養所・栗生楽泉園。今もこの施設で暮らす元患者で、ハンセン病違憲国賠訴訟を勝訴に導いた立役者の谺雄二さん、50年程前から絵画を描き続けている鈴木時治さんらが、過去と現在の心境を語る。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Murderball", "マーダーボール", "", "", "http://image.eiga.k-img.com/images/movie/33681/315x210.jpg?1981", 85, "戦車のような車椅子で激しくぶつかり合うことから“マーダーボール”と呼ばれる過激な競技、ウィルチェアーラグビー（車椅子ラグビー）で世界一を目指す男たちに迫ったドキュメンタリー。“荒くれ者”マーク・ズパン率いるアメリカ代表チームと、かつてアメリカ代表選手として活躍していた“裏切り者”ジョー・ソアーズがコーチを務めるカナダ代表チームとの因縁の対決など、個性豊かな選手たちによる熱い戦いが圧倒的な迫力で描かれる。", "2006-10-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("心の杖として鏡として", "心の杖として鏡として", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "東京都八王子市の精神科・平川病院にある造型教室を舞台に、様々な心の病を抱えながらこの教室に通う人々と、彼らの創作活動を38年間にわたって支えてきた主宰者・安彦講平氏の活動をとらえたドキュメンタリー。創作することによって自分自身と真剣に向きあい続ける人々の姿や作品の数々を通し、「病む」とは何か、そして「生きる」とは何かを問いかけていく。ナレーションを務めるのはベテラン女優の吉行和子。", "2005-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON’T BE A MENACE TO SOUTH CENTRALWHILE DRINKING YOUR JUICE IN THE HOOD", "ポップ・ガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "TV番組『In Living Color』で人気を集めたウェイアンズ兄弟のうち３人が集まり、作り上げたコメディ・アクション作品。「グリマーマン」などに出演したK・アイヴォリー・ウェイアンズが製作を担当、ショーンとマーロンが脚本、主演。母親から真の男になるようにと悪名高い犯罪多発地帯サウスセントラルに送り込まれたアッシュトレイはダシキという魅力的な女に出会い、恋に落ちる。ところが彼女は７人の子持ちで、そのうえムショ帰りのワル、トゥースピックの元恋人だったのだ……。否応なしに争いに巻き込まれてしまった主人公の戦いを、ブラックなギャグや「ジュース」などの黒人映画のパロディーを交えて描いていく。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACCATTONE", "アッカトーネ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ローマのスラム街に住む、最底辺に生きる人間たちを真正面から見据え、イタリア映画界に衝撃を与えたパゾリーニの処女作。“アッカトーネ(乞食)”と呼ばれる娼婦のヒモ、ヴィットリオは、マッダレーナの稼ぎで職にも就かず優雅に暮らしている。ところが、マッダレーナが刑務所に入れられ金づるを失ってからは急速に運が傾きだす。そして、うぶな娘ステッラに恋して真面目に働き始めるが、体が続かず、とうとう仲間と盗みをはたらき、交通事故であえなく死んでしまう。キリストの殉難を思わせる荘厳な悲劇。助監督は若きベルナルド・ベルトルッチ。日本で初めて紹介された時のタイトルは「乞食」。", "1961-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("肖像", "肖像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "ともに戦中から注目された新人監督だった黒澤が、木下のためにオリジナル脚本を書き下ろしたのが本作品である。借家人の老画家一家を追い出すために父娘を装って不動産屋と妾が入居するが、老画家一家の温かい人情に触れ自分の過ちに気づき、妾は旦那と別れて自立の道を歩む。可憐な桂木洋子のデビュー作でもある。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("多羅尾伴内・十三の魔王", "多羅尾伴内・十三の魔王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "満員の競馬場でスタンドから飛び降りた娘の死を不審に思い、七変化を駆使して麻薬王の悪事を暴く名探偵の活躍。例によって荒唐無稽な物語だが、語り口の巧みさも快く、マニアでなくとも一見の価値ある作品。悪役として名高い進藤英太郎が善玉も二役で演じるほか、意外なスターの端役出演も拾い物。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEN OF HONOR", "ザ・ダイバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "アフリカ系アメリカ人で初めて“マスターダイバー”の称号を獲得した米海軍ダイバー、カール・ブラシアの半生を描いた実録ヒューマン・ドラマ。差別と偏見にめげず潜水に命を懸ける主人公にC・グッディングJr.が扮し、彼を徹底的にしごき、後に友情の絆で結ばれる教官をR・デ・ニーロが演じている。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神勇飛虎覇王花／THE INSPECTORS WEAR SKIRTS PARTII", "レディ・スクワッドII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ジャッキー・チェンがプロデュースしたコメディ・タッチのアクション映画のシリーズ第２作。香港警察屈指の女性コマンド部隊、通称“妖精部隊”に新メンバー４人が加わり、国際的なテロリストを相手に派手なアクションを披露する。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヌードの夜　愛は惜しみなく奪う", "ヌードの夜　愛は惜しみなく奪う", "", "", "http://image.eiga.k-img.com/images/movie/55571/315x210.jpg?3723", 135, "「死んでもいい」「夜がまた来る」などの鬼才・石井隆監督が1993年に発表した「ヌードの夜」の17年ぶりとなる続編。前作に引き続き竹中直人が主演を務める。“なんでも代行屋”の紅次郎は、美少女れんから「父の形見のロレックスを探して欲しい」という依頼を受ける。捜索の途中でれんの驚くべき過去を知った次郎は、彼女を救おうとするが……。心に深いやみを抱える少女・れんを、清純派グラビアアイドルの佐藤寛子が熱演。", "2010-10-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Swing Vote", "ケビン・コスナー　チョイス！", "", "", "http://ecx.images-amazon.com/images/I/51ZoMFx03pL._SL210_.jpg", 120, "アメリカ大統領選挙の行方を左右する1票を思いがけず握ってしまった男の運命を、ケビン・コスナー主演で描いたコメディドラマ。ニューメキシコ州で暮らすシングルファザーのバドは、仕事をクビになりバーで飲んだくれる毎日を送っていた。そんなある日、愛国心の強い娘モリーがバドの名前で大統領選挙に投票してしまったことをきっかけに、バドは次期大統領を決める最後の1票を持つ重要人物として、アメリカ中から注目されるハメになる。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベッドタイム・ストーリーズ", "ベッドタイム・ストーリーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 26, "男と女、女と女、男と男……など、7組のカップルがベッドインする様子を描いた7話からなる短編集。", "2004-05-28", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Untouchables", "アンタッチャブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "　1930年、禁酒法下のシカゴ。財務省から派遣された特別捜査官エリオット・ネスは街を牛耳るギャングのボス、アル・カポネに敢然と戦いを挑む。ベテラン警官のマローンを始め、射撃の名手ストーン、税理士のウォレスといったメンバーに支えられ、ネスの捜査が始まる。しかし巨悪カポネの差し向けた殺し屋によって、ひとり、またひとりと犠牲者が……。かつてテレビドラマでも人気を博したアクション・ロマンを映画化。ケビン・コスナーはこの作品で一躍トップスターに。そしてデ・ニーロがまたも肉体改造に挑戦。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クリスマス黙示録", "クリスマス黙示録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "元宝塚のトップスター、天海祐希の映画初出演作。クリスマス・イブの夜、シアトルの町に爆弾が仕掛けられる。警視庁から派遣された女刑事・杉村葉子は、FBI特別捜査官のサラとともに、殺りくを阻止するため、爆弾犯を探し出そうとするが、タイムリミットは72時間しかなかった。撮影は２ヵ月間シアトルで行われ、天海のセリフはすべて英語で話されている。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みな殺しの歌 拳銃よさらば！", "みな殺しの歌 拳銃よさらば！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "大薮春彦のハードボイルド小説を「野獣死すべし」に続いて須川栄三が監督した銃撃アクション。兄を殺された主人公が、その裏に銀行強奪によって得た大金の分け前をめぐる争いがあることを知り、兄の遺品ワルサーP-38で復讐を開始する。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エンマ", "エンマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "密室に監禁された6人の男女に襲いかかる死の恐怖を描いたサスペンススリラー。大学生の遥冬が目を覚ますと、そこは見覚えのない病室のベッドだった。密室状態の部屋には、遥冬の他に見知らぬ5人の男女がいる。年齢も職業も異なる彼らの共通点は、注射の跡と「渋谷にいた」という記憶だけ。やがて1人の若者が頭痛を訴えはじめ、そのまま死んでしまう。残った者たちが混乱に陥る中、また1人、犠牲者が生まれ……。", "2007-02-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARCH OF TRIUMPH", "凱旋門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "レマルクのベストセラーを映画化した大メロドラマ。主人公は第二次大戦直前のパリで愛しあったジョーンとオーストリア人医師ラビック。国外退去、新たな恋人、ナチスへの復讐と、二人は運命と歴史の荒波に仲を裂かれ続ける。悲痛なラストが心を打つ佳作。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　すくえ！ココリンと奇跡の星", "それいけ！アンパンマン　すくえ！ココリンと奇跡の星", "", "", "http://image.eiga.k-img.com/images/movie/56151/315x210.jpg?4188", 47, "やなせたかし原作の人気アニメーション劇場版第23作。宇宙の彼方にあるヘンテ星の住人ココリンが、星の危機を救ってほしいとアンパンマンに助けを求めてくる。ヘンテ星では食事といえばカプセルだけで、食べる喜びを知らなかったココリンは、ジャムおじさんの作ったパンに感激。パン作りを習ってヘンテ星を救い、人気者になろうと考えるココリンだったが、なかなかうまくパン作りができず……。「うたって　てあそび　アンパンマンともりのたから」と同時上映。", "2011-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Look Who's Talking", "ベイビー・トーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "　不倫の末、妊娠した会計士のモリーは未婚の母になることを決意。やがて彼女は男の子を産む。マイキーと名づけられたその子のために、理想的な父親を見つけようとするモリー。そんな彼女はタクシーの運転手ジェームズにベビーシッターを依頼することに。やがてモリーとジェームズは惹かれあうようになるが……。大人たちの生活や恋愛を、ブルース・ウィリスが心の声を演じる赤ちゃんの視点から見つめるというアイデアが人気を博したハートウォーミング・コメディ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Reservation Road", "帰らない日々", "", "", "http://image.eiga.k-img.com/images/movie/53511/315x210.jpg?2498", 102, "「ホテル・ルワンダ」のテリー・ジョージ監督が描くシリアスドラマ。主演に「グラディエーター」のホアキン・フェニックス、「死ぬまでにしたい10のこと」のマーク・ラファロ。コネチカットの田舎町、大学教授のイーサンは最愛の家族と平穏に暮らしていた。だが突然のひき逃げ事故で息子を失くし、その悲しみと罪悪感から家族はバラバラになっていく。イーサンは犯人を突き止めるため弁護士に調査を依頼するが、実はその弁護士ドワイトがひき逃げの犯人で……。", "2008-07-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UP！　MEGAVIXEN", "UP！ メガ・ヴィクセン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "巨乳を大フィーチャーした異才R・メイヤーお得意のモンド・フィルム。ヒトラーに似たSM好きのナチ党員が、バスタブにピラニアを放たれて殺されたと思いきや、物語はカフェのグラマーな店員をめぐる欲望のストーリーへと転じ、どんどん意味不明になる。ユーモアはもちろん、バイオレンスをも含むシュールでキッチュな怪作。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mauvais Sang", "汚れた血", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "レオス・カラックスの、アレックスという青年を主人公にした3部作の2作目。愛のないセックスで伝染する死の病、STBOが蔓延する近未来のパリ。父を亡くしたアレックスは、父と同じく借金を抱える父の友人マルクから、ある製薬会社が開発したSTBOの特効薬を盗む話を持ちかけられる。マルクの愛人アンナをジュリエット・ビノシュが、アレックスのガールフレンドのリーズをジュリー・デルピーが演じている。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キューポラのある街", "キューポラのある街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「青春の門」「青春の門・自立篇」「夢千代日記」などで知られる浦山桐郎の監督デビュー作。舞台は鋳物の町・埼玉県川口市。そこに住む職人かたぎのガンコな父を持つ、ジュン、タカユキの姉弟が、貧しいながらもけなげに生きていく様子を描いている。これにタカユキの友人・サンキチの複雑な家庭環境、ジュンの進学問題、鋳物工場の組合問題と、当時の世相を反映した社会状況が織り込まれているが、これを浦山は大上段にではなく、あくまで日常的に捉え、さわやかな感動を呼ぶ。当時18歳の吉永小百合は、本編で史上最年少のブルー・リボン賞主演女優賞の受賞者となり、女優として開眼。アイドル・スターの筆頭として黄金時代を築いていく。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私の骨", "私の骨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "小説家・佐藤英一は、亡き父が森で苦しげに蠢く麻袋を鉄パイプで殴っている幻想を見る……。現実と幻想が交錯する迷宮の世界を描いた高橋克彦の短編小説を、CMディレクターなどを経て本作で映画監督デビューした荻野憲之が映像化したヒューマン・ホラー。演技派・田口トモロヲが苦悩に満ちた小説家を絶妙に表現していて見事だ。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Pere de mes enfants", "あの夏の子供たち", "", "", "http://image.eiga.k-img.com/images/movie/55331/315x210.jpg?1673", 110, "第62回カンヌ国際映画祭のある視点部門で審査員特別賞を受賞した家族ドラマ。多くのアート系映画を手掛けてきたプロデューサーのグレゴワールは、不況の波に押され、未完成の映画と多額の借金を残したまま自殺してしまう。幸せな日々から一転、遺された妻と3人の娘たちは絶望の中に光を見出そうとするが……。ミア・ハンセン＝ラブ監督の実体験から生まれたストーリー。主演は「愛のめぐりあい」のキアラ・カゼッリと「隠された記憶」「不完全なふたり」のルイ=ドー・デ・ランクザン。", "2010-05-29", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TABLE FOR FIVE", "5人のテーブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "両親の離婚は、子供たちにとってどんな意味を持つのか、離れて暮らす父親と子供たちに親子のつながりはあるのか？“離婚後の父”をテーマに、親権を新しい父親に奪われそうな男が、子供たちにとって本当の“父”となる足跡を描いた情感あふれるドラマ。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GASPARD ET ROBINSON", "ガスパール　君と過ごした季節〈とき〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "失業中のガスパールとロバンソンの二人は、海辺の廃屋を改装して、軽食堂を作ることを夢みながら、一緒に暮らしている。ある日、ロバンソンが道に置き去りにされた老女を家に連れて帰ってくる。妻が家出して以来、家族というものにうんざりしていたガスパールは老女と暮らすことに強く反対するが、ロバンソンの説得に根負けし、しぶしぶ同居を受け入れる。しかし、ロバンソンのお人好しは終わらなかった。幼い娘を連れ、生活に困っている未亡人ローザに一目惚れし、母娘までも家に連れてくるのだった。こうしてロバンソンは、人々の愛を拾い集め疑似的な家族を作っていくのだが……。「MONDO〈モンド〉」のT・ガトリフの人間洞察が冴えた、味わい深い１作。ビデオ発売時タイトルは「海辺のレストラン　ガスパール＆ロバンソン」。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沼津兵学校", "沼津兵学校", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "撮影準備に入りながら、俳優が次々と兵隊にとられて、ついに完成まで２年を費したといういわくつきの作品。旧幕軍を再編成するための兵学校が沼津にできると聞き、町人の子・千倉は沼津へ向かう。秀才の正邦や、うっかり者の桑原らとともに厳しい勉強に励む千倉だが、やがて友と別れる時がくる……。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Wedding Planner", "ウェディング・プランナー", "", "", "http://image.eiga.k-img.com/images/movie/1741/315x210.jpg?2610", 104, "幼い頃から花嫁に憧れ、素敵な結婚式を夢みてきたメアリー・フィオレは、恋人たちに最高の結婚式を提供するウェディング・プランナー。5年間のキャリアで業界のトップに昇りつめた彼女は、今まで仕事一筋に生きてきた。そんなメアリーがある日、偶然のアクシデントから命を救ってくれた医師スティーブに出会い、心を奪われる。しかし彼は、彼女が結婚式を手がける大切なクライアントのフィアンセだった。", "2001-06-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さくらん", "さくらん", "", "", "http://image.eiga.k-img.com/images/movie/33861/315x210.jpg?2185", 111, "女性たちから絶大な支持を集める漫画家・安野モヨコの同名コミックを、世界的フォトグラファーの蜷川実花が映画化。江戸時代の吉原遊郭を舞台に、自分らしく生きるひとりの花魁の人生をゴージャス＆ビビッドに描いた青春時代劇。吉原の遊郭・玉菊屋に連れてこられた8歳の少女・きよ葉。彼女はその美貌と負けん気の強さで、やがて吉原一の花魁へと成長していく。主演は、モデルやミュージシャンとしても活躍する土屋アンナ。", "2007-02-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙戦艦ヤマト 完結編", "宇宙戦艦ヤマト 完結編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "ブームを巻き起こした“宇宙戦艦ヤマト”劇場版シリーズの、文字通りの完結編。作品を代表するヒーロー・古代進と、ヒロイン・森雪の、メイクラブが描かれて話題を呼んだ。西暦2203年、銀河系と他の小宇宙との接触を調査しに飛び立った宇宙戦艦ヤマトは、ディンギル星の宇宙艦隊に攻撃される。地球も水の惑星・アクエリアスによる水没の脅威と、ディンギル軍の猛襲にさらされ、ヤマトは決死の戦いの果てに、ディンギル軍を打ち破るが……。物語の途中で、第１作で死んだはずの沖田艦長が実は……という、意外な展開がある。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOST　AND　DELIRIOUS", "翼をください", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "森の中にある寄宿学校を舞台に、少女たちの友情と愛を描く切ない青春ドラマ。女子寄宿学校のルームメイト、トリーとポーリーは友情を超えた愛で結ばれていた。だが、二人の関係がうわさになり、トリーはレズビアンであることを否定しようとポーリーを避け始める。絶望したポーリーは、思い詰めていき、ついに衝撃的な決断を下す。思春期の少女たちの傷つきやすい心の内面と、ほとばしる情熱を美しい映像の中に鮮烈に描き出す。", "2001-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOIGNE TA DROITE", "右側に気をつけろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "今夕までに映画を1本作れば罪が許されるというゴダール白痴／公爵殿下の旅と、新しい音を求める音楽家の旅、<地上にひとつの場を>求める異星人のような男の旅が交錯、コミック漫画のように、冒険活劇のように展開していく。文学からの引用が音と映像のコラージュの中で疾走する、ゴダール監督の80年代劇場用長編映画第6作。", "1989-01-28", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sheva Dakot Be'gan Eden", "天国の七分間", "", "", "http://image.eiga.k-img.com/images/movie/55031/315x210.jpg?4689", 94, "イスラエルの新鋭オムリ・ギボンがメガホンを取り、テロが一般市民に与える心の傷を訴えた意欲作。エルサレムで自爆テロに巻き込まれた女性ガリヤは、ショックから事件当時の記憶を失ってしまう。それから1年後、彼女の元に差出人不明の手紙が届く。その中には彼女が事件当時に着けていたネックレスが入っており……。ベネチア国際映画祭金獅子賞に輝いた「レバノン」のレイモンド・アムサレムがヒロインを演じる。", "2009-11-23", "46", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Power", "キングの報酬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "　候補者のイメージ戦略など選挙運動の仕掛け人として活躍するピートは業界一と謳われている凄腕。ある時、ピートは古いクライアントである上院議員サムが引退することを知らされる。その後、後釜として出馬するケードが彼を雇うことに。しかしサムの引退は陰謀によるものだったことが発覚し……。数多くの社会派ドラマを手がけてきたシドニー・ルメット監督が選挙活動の裏側とそこにうごめく者たちの野望と葛藤をスリリングに描く。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LASER MISSION", "バトルドラゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「ファイアー・ドラゴン」に続く、ブルース・リーJr.のB・リー主演の第２作。恐るべき破壊力を秘めたレーザー兵器を開発した科学者と、その製造に不可欠な世界最大のダイヤモンドをめぐるKGB、CIA、武器商人の三つ巴の争奪戦。その渦中に、すご腕の一匹狼スパイのリーが飛び込んでいく。", "1989-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PUMPING IRON", "アーノルド・シュワルツェネッガーの鋼鉄の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "A・シュワルツェネッガーのボディービル選手時代の活躍ぶりを描いたドキュメンタリー。“Mr.オリンピア”なる大会で６年連続優勝を狙う彼が、毎日厳しいトレーニングに励み、ついに栄冠を勝ち取るプロセスを克明に映し出している。DVDタイトルは「アーノルド・シュワルツェネッガ−のパンピング・アイアン」。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Klemlin Letter", "クレムリンレター／密書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "　ソ連の政治犯収容所で、クレムリンの密書を買い戻そうとして捕らえられた反ソのスパイが自殺を図る。その密書とは、自由諸国とソ連が共同で中国の基地を粉砕する協定を結んだ文書だった。アメリカ情報部は元・海軍少佐ローンを密書奪還の任務に就かせる。ローンと仲間たちはモスクワに潜入し、事件の関係者に接近するが……。国際スパイ戦を描いたベストセラー小説を映画化。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VALENTINO", "バレンチノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "31歳で他界したサイレント映画最大のアイドル、ルドルフ・ヴァレンチノの、周囲の熱狂と自らの衝動に駆り立てられた生涯を描く。ヴァレンチノに世界屈指のバレエ・ダンサー、ヌレーエフを起用。熱に浮かれたような1920年代のハリウッドを、ラッセル監督のシュールな映像が再現。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHRISTIANE F.", "クリスチーネ・F", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "麻薬中毒の娼婦だった少女クリスチーネ。荒廃した家庭に育ち、13歳で麻薬を覚えた彼女の日常をドキュメンタリー・タッチで描き出す。デヴィッド・ボウイが音楽を担当し、彼自身も映画に登場。病める現代の若者の生の姿を捉えた問題作だ。", "1981-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪猫夜泣き沼", "怪猫夜泣き沼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "入江たか子主演の“化け猫”もの「怪談佐賀屋敷」の再映画化。佐賀藩主・鍋島丹後守は悪家老・豊前のそそのかしに乗って、忠臣・閑斎を斬殺し、底なし沼に沈めるが、閑斎の愛猫が一部始終を見ていた。次の夜から城中には猫のたたりが現れ始める。入江たか子の堂に入った化け猫ぶりが見もの。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Noah", "ノア（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「ノアの方舟」を題材とした大作ドラマ。", "2014-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AWAY　WITH　WORDS", "孔雀／KUJAKU", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ウォン・カーウァイ作品のカメラマンとして有名なC・ドイルの監督デビュー作。沖縄の孤島で生まれた青年アサノが、香港のゲイ・バーに流れ着く。そのバーの孔雀の羽を思わせるソファーが気に入った彼は、英国人ゲイのオーナー、ケビンや中国人娘スージーとの奇妙な共同生活を過ごすことになる。さすらいの人生を送ってきたドイル自身を思わせる主人公を演じるのは浅野忠信。記憶に囚われた主人公の心象風景をヴィジュアル化した映像が、夢幻的な美しさを放っている。", "1998-01-01", "76,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ice Age: Dawn of the Dinosaurs", "アイス・エイジ3 ティラノのおとしもの", "", "", "http://image.eiga.k-img.com/images/movie/54081/315x210.jpg?3413", 0, "大ヒットアニメ「アイス・エイジ」のシリーズ第3弾。ある日、ナマケモノのシドは氷の穴から氷河の真下に広がっていた恐竜ワールドに落ちてしまい、そこでティラノザウルスの卵を拾う。卵の父親になると張り切るシドだったが、卵の母親恐竜に見つかり恐竜ワールドへと連れ去られてしまう。マンモスのマニーとサーベルタイガーのディエゴはシドの救出に向かうが……。", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("何がジェーンに起ったか？", "何がジェーンに起ったか？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "　かつてベビー・ジェーンという名で子役として活躍したジェーン。そして美しいスターだったが、事故で不具となったその姉ブランチ。年老いたふたりは古い屋敷で隠遁生活を送っていた。ジェーンは酒に溺れ、異常な行動を繰り返し、そのあげく体の不自由なブランチに暴力を振るうようになる。追い詰められていくブランチに、やがてジェーンは……？　女性を主人公にした映画でも数々の名作を残したロバート・アルドリッチ、その代表作とも呼べるサスペンス。ジェーン役のベティ・デイビスは鬼気迫る怪演。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIBES", "バイブス 秘宝の謎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ある日、大学の超心理学研究会のパーティーで、シルヴィアとニックは知り合う。そこに二人のことを熟知しているハリーが登場し、彼は二人にアンデスで行方不明になっている息子捜しを依頼する……。C・ローパーが映画初出演のサイキック・アドベンチャー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GEORGIA", "ジョージア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "美しく澄んだ歌声で人々を魅了し、カントリーウエスタン歌手として成功したジョージア。安酒場のステージでどぎつい化粧とパンクファッションで感情を吐き出すように歌っている妹のセイディ。幼い頃から音楽という同じ夢を見ていたはずの姉妹は、いつしかまったく異なる人生を歩んでいた……。姉のような才能はないが、歌をあきらめず、アルコールやドラッグで身を擦り減らしながらも歌い続けるセイディの姿が感動的。", "1995-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIERRA BRAVA", "新・荒野の用心棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "復讐と金で人生が揺れ動いていくメキシコ青年の悲劇。メキシコ人の青年ローハスは、ある日無法なアメリカ人に家を焼かれ土地を追われる。復讐の鬼となったローハスは義賊となるが、殺し屋の計画にのせられて強盗をはたらき、彼の運命は狂い出す。", "1968-01-01", "4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十三太保／THE HEROIC ONES", "英雄十三傑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "武侠映画で名を馳せたチャン・チュ監督の代表作の一つ。ワイヤーワークとトランポリンを駆使したダイナミックな武術アクションや、壮絶なバイオレンスが圧倒的。舞台は唐末期の中国。沙陀の国の王には血気盛んな13人の息子がいた。策略家の陰謀に踊らされたことによって彼らは、武芸の腕を競いつつ殺し合いを始める。", "1973-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女帝", "女帝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "“謎の女相場師”と噂され、バブル経済最盛期には数千億円の金を動かし、バブル崩壊とともに転落していった料亭の女将。当時の新聞紙上をにぎわした実在の人物をモデルに、その半生が再構築される。日活ロマンポルノ後期にデビュー、「女教師狩り」などの佳品を手掛けたすずき監督は、縫という存在を、男を手玉にとる魔性の女ではなく、成就できない男への愛情をマネー・ゲームで埋め合わせようとする悲運の道化として造型してみせた。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOHOSHINKI THE LIVE 2009", "TOHOSHINKI THE LIVE 2009", "", "", "http://image.eiga.k-img.com/images/movie/55091/315x210.jpg?2848", 0, "2004年に韓国でデビューし、05年には日本デビューして一躍人気となった5人組グループ「東方神起」が09年に行った初のドーム公演の模様や、野外ライブ「a-naiton」でのステージパフォーマンス、ファン投票で選ばれた人気PVなどを映画館で上映する。", "2009-10-31", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANGEROUS MINDS", "デンジャラス・マインド　卒業の日まで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "元海兵隊員のルアンは、ノースカリフォルニアのパークモント高校で、長年の夢であった教師としてのキャリアをスタートしようとしていた。しかし、受け持ったクラスは低所得家庭に育つ問題児ばかりが集まり、授業に耳を貸さないばかりか、10代にしてすでに夢も希望も失っているかのようだった。意欲に満ちたルアンは、それまでの規律を無視し、独自のカリキュラムを組み生徒たちを指導していく……。自らの体験を綴ったL・ジョンソンの小説『ルアン先生にはさからうな』を映画化、ルアン役のM・ファイファーが体当たりの演技を見せる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("破線のマリス", "破線のマリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "江戸川乱歩賞を受賞した野沢尚の同名小説を映画化したミステリー。首都テレビのニュース番組編集者、遠藤瑤子は、視聴者を心理操作してしまいかねない独自の映像編集テクニックで、視聴率獲得に貢献していた。そんな彼女の元に、郵政省の犯罪を内部告発したビデオテープが届けられる。瑤子が編集を手掛け、オンエアされた特集は、明らかに官僚の麻生公彦を容疑者扱いしていたが、当の麻生は完全な濡れ衣だと主張してきた……。自ら熟知していたはずの“映像の罠”に陥ってしまう汚れ役のヒロインを、黒木瞳が体当たりで熱演。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE FREUDLOSE GASSE", "喜びなき街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "第一次大戦後の猛烈なインフレに見舞われるウィーン。株が暴落し、一夜にして全財産を失った父のため、成り金男の愛人にされそうになった娘のグレタは、危ないところをアメリカ赤十字の士官に救われる……。寒風が吹きすさぶなか、肉を求めて列を作る人々など社会の底辺に生きる庶民の姿と、豪奢なパーティーを毎晩のように開く戦争成り金たちの姿を対比させるようなリアリズム映像が目を引く。また、当時駆け出しだったG・ガルボと、大女優の名をほしいままにしていたA・ニールセンの共演が見られる点にも注目。", "1925-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猛鬼大厦／OPERATION　PINK　SQUADII", "チャイニーズ・ゴースト 香港の幻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "新婚旅行から帰って来たばかりの美人刑事サンドラは、ほかの３人の美女とともにニセ札事件の内偵の指令を受け、組織の一員マディーに近づく。しかし、サンドラたちが借りたアパートは、幽霊のうごめくいわくつきの建物だったから、さあ大変。ニセ札犯とゴースト、そして美人刑事たちが入り乱れて壮絶な死闘が始まる……。香港映画ならではのコミカルなアクション描写と、セクシーな美女たちの熱演が光る。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今昔伝奇／花神〈かしん〉", "今昔伝奇／花神〈かしん〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "新感覚の“アウトサイダー時代劇”と銘打って製作された“今昔伝奇”シリーズ三部作の第１作。僧侶になるため山奥で厳しい修行に励む青年、弥彦と、実父に近親相姦を強いられている哀れな女、千代。大自然の中で激しく交わる二人の壮絶な愛の行方を、望月六郎監督が妖しいエロチシズムの漂う映像で迫力たっぷりに描き出す。眉毛を剃り落としてヒロインに扮した金谷亜未子の熱演も圧巻。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL FASCINO SOTTILE PECCATO", "禁断の情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "同性愛に走ろうとしている息子に、自らの体を与えてしまった母親。そして、実の父親を誘惑する娘。一人の女の自殺がきっかけとなって、ある家族の乱れた生活が暴かれていく。イタリア映画お得意のテーマを、セックス・シーンふんだんに描いた作品。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お散歩", "お散歩", "", "", "http://image.eiga.k-img.com/images/movie/33571/315x210.jpg?2311", 49, "4カ月前に1度だけ関係を持って以来、毎週日曜日にだけお散歩をしてきた小春とナベ。歩きながら交わされるとりとめのない会話を中心に、友達以上恋人未満の2人の微妙な関係をリアルに描き出す。監督は、97年に「餓鬼の季節」でPFF審査員特別賞を受賞した松田彰。平田オリザ主催の「青年団」に所属する村田牧子が小春を、ドキュメンタリー映画「えてがみ」の被写体となった絵手紙作家、鍋山晋一がナベを演じる。", "2006-07-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("High School 2", "高校2", "", "", "http://image.eiga.k-img.com/images/movie/57311/315x210.jpg?4512", 220, "1967年のデビュー以降、現代アメリカ社会を独自の視点で見つめ続けてきたドキュメンタリー作家フレデリック・ワイズマン監督が、映画「ミュージック・オブ・ハート」の舞台にもなったニューヨークのハーレム地区の進学校を題材に撮り上げたドキュメンタリー。革新的教育者デボラ・マイヤーが設立した同校が誇るユニークなカリキュラムをはじめ、父兄や職員が議論を交わす会議の模様や生徒会活動にも密着する。2011年、ワイズマン監督作を一挙上映する「フレデリック・ワイズマンのすべて」にて日本初公開。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明治・大正・昭和 猟奇女犯罪史", "明治・大正・昭和 猟奇女犯罪史", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "“異常性愛路線”と呼ばれて当時、大ヒットしたシリーズものの一作。タイトル通り、実際に女が犯した猟奇犯罪の始終を再現した全５話で構成されるオムニバス作品。特に1936年に起こった有名な“阿部定事件”は、のちに「愛のコリーダ」などの映画化作品で盛んに取り扱われた。愛する男を殺害した後、男性器を切り落として逃亡した犯人、阿部定本人が特別出演。彼女がインタビューに応じる映像まで挿入するという徹底ぶり。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("けものがれ、俺らの猿と", "けものがれ、俺らの猿と", "", "", "http://image.eiga.k-img.com/images/movie/1471/315x210.jpg?3661", 107, "元パンクバンド、イヌの町田町蔵、現・芥川賞作家、町田康の同名短編小説を原作に、Dragon Ash、スガシカオなどのクリップで注目を集める須永秀明が、映画初監督。売れない脚本家、佐志はゴミを不法投棄されるほどの廃屋寸前の家で暮らしているが、その家主である義父から立ち退きを命じられても、引っ越す金がない。映画プロデューサーを名乗る老人が持ってきた仕事の話にとびつくが、それが災難の始まりだった。", "2001-07-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若き日の血は燃えて", "若き日の血は燃えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "戦時中の1944年、国策映画の１本として製作された「激流」でデビューした家城巳代治監督の、３年ぶりの第２作。酒場で働く一人の女をめぐって、二人の男子高校生が対立する学生青春映画。決闘を経て、二人は互いに和解するが……。高橋貞二が、女の酒場に入りびたる荒れた高校生の役を主演している。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHILL FACTOR", "チルファクター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "化学兵器をめぐる巨大な陰謀に巻き込まれた男二人の姿を描くノンストップ・アクション。元軍人が華氏50度以上になると大爆発を起こす最強の化学兵器エルヴィス奪還を画策。陰謀を察知した科学者からエルヴィスを託されてしまったコーヒーショップの店員メースンは、トラック運転手アーロを巻き込んで、決死の逃避行を開始する。陸・海・空に渡って繰り広げられ壮絶なチェイスが展開。テロリストに立ち向かう男二人の活躍をユーモアを交えて快テンポで描く。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Something to Talk About", "愛に迷った時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「ギルバート・グレイプ」「マイライフ・アズ・ア・ドッグ」のラッセ・ハルストレム監督が、ジュリア・ロバーツを主演に迎えて贈るラブ・コメディ。ある日、夫エディの浮気現場を目撃してしまったグレース。問い詰めてもシラを切り通すばかりの夫に腹を立てた彼女は、娘を連れて実家に転がり込む。そしてこの出来事をきっかけにして、グレースは満ち足りていたはずの結婚生活や、1人の人間としての自分の人生を見つめ直す。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Seven Minutes in Heaven", "ジェニファーの恋愛同盟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "オハイオ州の小さな町を舞台に、女性大統領を夢見る真面目な女の子と、好奇心旺盛なその親友という対照的な少女たちの恋愛模様をコミカルに綴った青春コメディ。イメージ通りといえる役どころを、コネリーが清楚な魅力たっぷりに演じる。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドキュメント'89 脱原発元年", "ドキュメント'89 脱原発元年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "自らを対象と一定の距離を保つ取材者と呼び、水俣から被差別部落へと反骨の譜を貫く小池征人が、原発問題に挑む迫真のドキュメント。映画は広島の被爆者の言葉で始まり、チェルノブイリ事故を引用しつつ各地の原発事情や反対運動の現状を描く。抗議のダイ・インの老若男女と機動隊の衝突は凄絶の一語。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冷血の罠", "冷血の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "黒沢清の「蛇の道」を想起させるサスペンスに、瀬々敬久が挑んだ異色作。黒沢作品の常連・哀川翔と「ニンゲン合格」の西島秀俊が主演しているため、特にその印象は濃い。妹の自殺にこだわっている探偵と、自殺の原因を執拗に調査し続ける妹の元夫。過去にとらわれた二人の男の関係と葛藤が物語の中心だが、調査報告をビデオで送り続ける元夫や宙吊りの虚無感の中で生きる探偵のキャラクターの特異さがミステリーを盛り上げている。東京渋谷、桜丘町の路地裏を迷宮のように仕立て上げた点も新鮮。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NUTS", "ナッツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "B・ストライサンドが、ブロードウェイの同名舞台を見て共鳴し、製作・音楽・主演の３役を担って完成させた意欲作。客を殺し、過失致死罪に問われたコールガール、クローディア。だが、彼女の両親は醜聞を恐れ、裁判を回避するため、弁護士と精神科医を抱き込み、クローディアを精神異常者として病院に閉じ込めようとする。私は“ナッツ（精神異常者）”ではない!! 人間の尊厳を貫くために彼女のつらく厳しい闘いが始まった……。攻撃的だがどこかいたいけなクローディアを、ストライサンドが迫真の演技で見事に体現。彼女の唯一の理解者、官選弁護士アラン役、R・ドレイファスの巧演も光る。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パビリオン山椒魚", "パビリオン山椒魚", "", "", "http://image.eiga.k-img.com/images/movie/1591/315x210.jpg?3975", 98, "自主製作の異色短編シリーズ「亀虫」により各界から注目されてきた気鋭の映画作家、冨永昌敬の長編デビュー作。伝説の動物国宝であるオオサンショウウオ“キンジロー”によって巡り会った自称“21世紀の天才レントゲン技師”の飛鳥（オダギリジョー）と財団の令嬢あづき（香椎由宇）の恋と冒険を描くコメディタッチの狂騒劇。音楽は菊地成孔。共演に高田純次、麻生祐未、津田寛治ら。", "2006-09-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hot Rod", "ホット・ロッド　めざせ！不死身のスタントマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("えんとこ", "えんとこ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "知的障害者の姪を見つめた「奈緒ちゃん」で知られる記録映画作家、伊勢真一が、今度は脳性マヒで寝たきりの友人、遠藤滋にカメラを向けたドキュメンタリー。“えんとこ”とは、“遠藤がいるところ、縁がある場所”の意味で、元教員である遠藤を介護するネットワークを指す。様々な若者たちが、遠藤を通して“介助”を経験することで、大切な何かを学んでいく３年間が、淡々と綴られる。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗室", "暗室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "華道の師匠、バーで知り合ったレズの女、近所に住む読者などと奔放な性生活を繰り返すが、そこに愛のないことに悩む流行作家と、それぞれに自信を持って自分の生き方を選ぶ女たちの姿を描く。にっかつ創立70周年を記念して作られたエロティックな文芸作品。芥川賞作家・吉行の代表作が原作。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("証言／A SAIGON TOO FAR", "ホワイト・バッジ ファイナル　史上最大の作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "38度線で繰り広げられる朝鮮戦争の壮絶な攻防を、韓国軍の英雄と、そのフィアンセの視点を通して描いた戦争ドラマ。「ホワイト・バッジ」とのドラマ面での接点はない。北朝鮮軍が大戦車軍団を配して韓国への猛攻を開始。首都ソウルを制圧され、壊滅的なダメージを受けた韓国軍はゲリラ戦による反撃に出るが……。本物の戦車や武器を使用したリアルなバトル・シーンは臨場感満点。", "1985-01-01", "5,60", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEADLY ROULETTE", "国際秘密結社", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "自分が撮った写真が原因で、国際的な秘密陰謀集団に追われるハメになったカメラマンのジャック。何が起きたのかよくわからないまま、彼は組織に連れ去られてしまう。やがてエレベーターに閉じ込められてしまった彼は、毒ガスを吹きかけられて絶体絶命のピンチに……。巻き込まれ型サスペンスの佳作。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN LIKE FLINT", "電撃フリント アタック作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「電撃フリント GO！GO作戦」の続編で、製作・脚本・音楽・主演の二人は前作のまま。世界征服を企てるアマゾン団によって、米大統領が瓜二つの男とすりかえられる。怪しい気配を感じたZOWIE（国際連帯秘密諜報機関）の主任クラムデンは、フリントに助力を求めに行く。果たして事件の裏にはソ連の陰謀が隠されていて……。痛快アクション活劇。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の季節", "花の季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ともすれば象徴的な存在としてしか映らない“花”を、人の心と心を結ぶ一つの命として捉え、そこに老人や親子、夫婦、兄弟など人間関係の様々な問題点をオーバーラップさせて描き出した作品。登場人物の心の動きの中に、何らかの形で“花”を意識せざるをえない設定がもたされており、斬新だ。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八甲田山", "八甲田山", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 169, "冬の八甲田山を越えるため、死を賭けて行軍する男たちの姿を描いたヒット作。大部隊で自然を克服しようとする部隊と、少数精鋭部隊で自然に逆らわず、折り合いをつけようとする者たちの運命を、壮大なスケールで捉えた。木村大作のカメラが秀逸。“天は我らを見離した”の決めゼリフは当時流行語になった。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Thousand Years of Good Prayers", "千年の祈り", "", "", "http://image.eiga.k-img.com/images/movie/54691/315x210.jpg?4191", 83, "アメリカ在住の中国人女流作家イーユン・リーの短編小説を、「スモーク」のウェイン・ワン監督が映画化。主演に「ラストエンペラー」のヘンリー・オー、「ジョイ・ラック・クラブ」のフェイ・ユー。妻に先立たれ、ひとり寂しく老後を過ごすシーは、中国から、アメリカに暮らす娘イーランを訪ねることにした。30歳を過ぎた娘は未婚で、すっかりアメリカナイズされた生活にシーは驚くが、娘のことを理解するため、多国籍な隣人たちと積極的に交流しアメリカの文化に触れていく。", "2009-11-14", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RESTORATION", "恋の闇 愛の光", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "退廃的な17世紀イギリスを舞台に、清らかな魂を持った女性との出会いにより真実の愛に目覚める男の物語。1663年、医学生メリベルは、国王チャールズ二世に呼ばれ、王の愛犬の治療に成功。それ以来、宮廷で暮らすことになるが、医学への情熱も忘れ、快楽に耽るようになる。メリベルは王の策略で、王の愛人シリアと結婚することになるが、決して身体には触れることは許されなかった。しかし、メリベルはシリアに惚れてしまい、それを知った国王に追放されてしまう。失意のメリベルは、旧友ピアスを頼り、彼の勤める精神病院へ行く。そこで、彼は自殺未遂を繰り返すキャサリンという女性に惹かれるものを感じる。この病院で、真摯に生きるピアスとキャサリンと付き合うことによって、メリベルは再び医師として生きる決意をするのだった。やがて、メリベルはキャサリンと愛し合うようになり、ロンドンへ出て、医師として活躍するようになるのだったが……。", "1995-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の中の牝鷄", "風の中の牝鷄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "第二次大戦中に外地に行ったきり、戦争が終わっても帰って来ない雨宮。妻・時子はその間、なんとか苦しい生活をやりくりしていたが、子供が入院してしまい、ついに経済的に破綻。一夜だけ売春する。やがて戻ってきた雨宮は、ある日その事実を知って……。夫婦の心情的危機状況を、とことんシリアスに描ききった、小津の戦後第２作。どんなシリアスな状況を描く時でも常にアクションやセリフのギャグを盛り込み、軽みのきいた作品に仕上げてきた小津映画の中で、これは「東京暮色」とともに、異色中の異色ともいえる出来ばえ。小津の画面に一度たりとも姿を現さなかった階段が、本作で初めて真正面から登場。小津全作品中唯一のバイオレンス・シーンが展開される。その即物的な暴力描写には誰もが息を呑まされるはず。なお本作は、小津が野田高梧以外の脚本家とコンビを組んだ最後の作品としても記憶されるべきだろう。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BACK BEAT", "バック・ビート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ビートルズの初期メンバー、スチュアート・サトクリフの半生を描き、伝説と呼ばれたポップスの歴史の裏側にスポットを当てた鮮烈な青春映画。結成当時、ビートルズの中心人物として人気を集めたスチュアートは、若き写真家アストリッドと出会い、やがてグループを離れ彼女への愛と破滅の運命をたどる……。", "1994-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンのびっくりしゃっくり時代", "エノケンのびっくりしゃっくり時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "エノケンが、チャップリンを思わせる町の浮浪者に扮したコメディ。浮浪者の健太は、紳士の黒川が、スリのサブ公に財布をすられそうなのを黒川に注意してやり、逆にスリに間違われる。そこを、楽団と花形歌手の歌ちゃんに救われる。健太は歌ちゃんとともに黒川の財布を捜すが……。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シーサイドモーテル", "シーサイドモーテル", "", "", "http://image.eiga.k-img.com/images/movie/54901/315x210.jpg?1465", 103, "海もないのに「シーサイド」と名づけられた山奥のすたれたモーテルを舞台に、ワケありの男女11人が騒動を巻き起こすアンサンブル・ストーリー。悪徳セールスマン、コールガール、借金まみれのギャンブラー、借金取り、EDに悩む社長、キャバ嬢など、個性豊かなキャラクターが場末のモーテルでだまし合いを繰り広げる。生田斗真、麻生久美子、山田孝之、成海璃子、玉山鉄二、温水洋一、古田新太ほか豪華キャストが揃った。監督は「スクールデイズ」（05）の守屋健太郎、原作は岡田ユキオのコミック「MOTEL」。", "2010-06-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Wrestler", "レスラー", "", "", "http://image.eiga.k-img.com/images/movie/54321/315x210.jpg?3160", 109, "人気レスラーだったランディも、今ではスーパーでアルバイトをしながらかろうじてプロレスを続けている。そんなある日、長年に渡るステロイド使用がたたりランディは心臓発作を起こしてしまう。妻と離婚し娘とも疎遠なランディは、「命が惜しければリングには立つな」と医者に忠告されるが……。主演ミッキー・ロークの熱演で、第62回ベネチア国際映画祭金獅子賞を受賞。ローク自身も第81回アカデミー主演男優賞にノミネートされた。監督は「レクイエム・フォー・ドリーム」のダーレン・アロノフスキー。", "2009-06-13", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Frenzy", "フレンジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "　無実の罪に陥れられた男の悲劇を描いた、アルフレッド・ヒッチコック後期の作品。ロンドン、テムズ川沿いの街では、ネクタイで女性を絞め殺す殺人事件が頻発していた。バーをクビになったリチャードは、金に困り元妻の事務所を訪れるが、口げんかになる。翌日、彼女がネクタイ殺人鬼の餌食になったことから、彼に疑いがかかる。ヒッチコックの前数作品の評価はあまり芳しいものではなかったが、今作で復活を遂げたと報じられた。", "1972-12-31", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT’S A MAD，MAD，MAD，MAD WORLD", "おかしな・おかしな・おかしな世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 192, "シネラマの大画面で壮大に展開されるドタバタ・コメディ。ハイウェイの事故で死んだ強盗の残した大金をめぐり、交通事故現場に偶然居合わせた５人の男と、強盗を追っていたはずの警部が大騒動を繰り広げる。特別出演のB・キートンが見もの。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TONIGHT　AND　EVERY　NIGHT", "今宵よ永遠に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "リタ・ヘイワースが全盛期の頃に主演した作品。第二次大戦下のロンドン。ショー一座の花形ダンサー、ロザリンドは、空軍将校ポールと恋に落ちる。一時はケンカ別れした二人だが、再会して互いの愛を確認し合う。ところが、戦場に赴いたポールが消息を絶ち……。戦争にほんろうされる男女の悲恋物語を描いたミュージカル・ドラマ。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE MOINE", "フランコ・ネロとナタリー・ドロンのサタンの誘惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "幻想文学の傑作といわれるルイスの『マンク』を原作に、シュールレアリスム研究で知られる映画史家のキルーが監督した異色作。脚本はブニュエルとカリエール。魔女に誘惑され、肉欲に溺れる修道僧の物語。", "1972-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海は狂っている", "海は狂っている", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "主演の川地民夫自身をモデルにして描いたという石原慎太郎の小説『ヨットと少年』の映画化。ヨットハーバーで働く牧夫は大島周航レースに参加し、自分のヨットを持つべく、悪事を働いてまで金を貯めるが、ナイトクラブの女給と知り合い、その金を使い果たしていく。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WEEKEND REUNION", "ウィークエンド・カクテル お嬢さまはHがお好き!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "モテモテ男のアーチーは、15年ぶりに同窓会に出席。旧友たちと再会するが、そこには女にモテる彼の邪魔をしようとする男性軍と、未だに彼に惚れている女二人が待ち受けていて……。女の執念と男のひがみが交錯する、サスペンス・タッチのコメディ作品。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("長崎ぶらぶら節", "長崎ぶらぶら節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "20世紀初頭、長崎の花街に生きる芸者・愛八は、大店の12代目にして学者でもある十二郎に心惹かれていく。そんな彼女を、十二郎は長崎の忘れられた古い歌を探す旅に誘う……。なかにし礼による直木賞受賞小説を原作に、吉永小百合と渡哲也、往年の日活コンビ主演で映画化した伝記恋愛ドラマにして、上質の女性映画。TV界の巨匠・深町幸男は、本作品で映画監督デビューを果たした。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAG DER IDIOTEN", "愚か者の日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "それまでドキュメンタリー作家として異彩を放っていた、“ニュー・ジャーマン・シネマ”世代の一人、W・シュレーターの劇映画第１作。精神病院に入れられた美しい娘キャロルが、理想的な精神治療に燃える女医ラウラに反発し、死へと向かっていく姿を描く。元ボンド・ガール、C・ブーケの体当たりの演技が見もの。", "1982-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("朋あり。太鼓奏者 林英哲", "朋あり。太鼓奏者 林英哲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "国内外で高い評価を受ける太鼓奏者、林英哲を追った音楽ドキュメンタリー。太鼓ソロというジャンルを切り開いた林は、これまで30年を超える活動の中で、様々な分野のアーティストとコラボレーションを果たしてきた。そんな彼が積み重ねてきた活動の軌跡は、まさに異文化コミュニケーションそのもの。太鼓を通し、国もジャンルも超え、素晴らしい交遊を育む彼の姿が印象深い。一人の人間としての林の魅力に触れられると同時に、真の意味での異文化交流についても考えさせられる１本だ。太鼓演奏集団“佐渡・鬼太鼓座”や“鼓童”の中心メンバーとして活躍する、林英哲。デビュー以来、意欲的に世界のミュージシャンとコラボレーションを果たす彼の姿をカメラが追う。", "2004-11-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEFENCE OF THE REALM", "ダウニング街の陰謀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "若手新聞記者のニックは、下院議員マーカムの一大政治スキャンダルをスクープ、マスコミを騒然とさせる。しかし事態は、マーカムの無実を主張する先輩記者ベイリスが謎の死を遂げたことで急変。G・バーンとG・スカッキが共演する本格的サスペンス。", "1985-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("転校生　さよならあなた", "転校生　さよならあなた", "", "", "http://image.eiga.k-img.com/images/movie/34031/315x210.jpg?3130", 120, "1982年に公開された青春ファンタジーの名作「転校生」を、大林宣彦監督がセルフリメイク。舞台を尾道から信州に移し、ひょんなことからお互いの身体が入れ替わってしまった男女が繰り広げるドラマを描き出す。転校先の中学校で幼なじみの一美と再会した一夫。2人は思い出の場所である水場を訪れるが、誤って水の中に転落してしまい……。オーディションで選出された新人女優・蓮佛美沙子がヒロインを好演。", "2007-06-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le cose che restano", "ジョルダーニ家の人々", "", "", "http://image.eiga.k-img.com/images/movie/58071/315x210.jpg?2011", 399, "イタリアのある家族に起こる離別や再生を、約6時間半にわたり描き出していくドラマ。ローマに住むジョルダーニ家は技術者として家族を養う父ピエトロ、元医師で家庭を切り盛りする母アニタをはじめ、外務省勤務の長男アンドレア、心理学者の長女ノラ、大学生の次男ニーノ、高校生の三男ロレンツォがそれぞれ充実した生活を送り、幸せな日々を過ごしていた。しかし、ロレンツォが事故死してしまったことをきっかけに、アニタは精神のバランスを崩し、隠れて不倫していたピエトロも逃げるように家を出てしまう。残された子どもたちは困難に直面するなか、さまざまな人々と出会い、新たな絆を育んでいく。", "2012-07-21", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗黒街の静かな男", "暗黒街の静かな男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ハードボイルド・サスペンスの快作。傷心の旅から帰った元プロボクサーの英治は、偶然にも彼の強打で失明に追い込まれ、今は花屋を営んでいる男・英夫と再会してしまう。その一件が傷心旅行の原因でもあったのだ。だが英夫は再会を喜んでくれ、屈託なく英治を迎え入れた。そんな折、英夫が何者かに轢き殺される。英治は犯人を捜すが……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SLEEPING CAR", "地獄の片道切符", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "傲慢な妻と別居して“列車アパート”と呼ばれる奇妙な建物に引っ越したジェイソン。新生活を始めた彼だったが、人間関係は好転せず、妻にも相変わらず愚弄される。やがて彼の激しい憎悪の念は、アパートに潜む悪霊を呼び覚ましてしまう……。壮絶なバイオレンス・シーンが見もののスプラッタ・ホラー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BOATNIKS", "ボートニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "世界で最も多く小型船がひしめき合い、“世界一忙しい港”として有名なカルフォルニアのニューポート港を舞台に展開される宝石泥棒の追跡作戦。そそっかしい警備隊員のトムもトムなら、ドジばかりふむ泥棒も泥棒。追いつ追われつの展開が愉快な、「ラブ・バッグ」の海上版とも言えるコメディ作品。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("肉体の門", "肉体の門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "田村泰次郎のベストセラー小説をマキノ雅広が映画化。敗戦直後の混乱期を生きる娼婦たちの姿を、当時としてはかなり大胆な、女優陣の体当たり演技で描く。リメイク版とは違い、ラストでは登場人物は皆カタギになり、幸福を示すバラ色の太陽が空に昇る。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とべない沈黙", "とべない沈黙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "岩波映画社にいて、土本典昭とともに俊英と呼ばれた黒木和雄、初の劇映画作品。脚本もないまま撮影に入り、現場は即興演出の連続で、脚本の完成と並行して撮影したというエピソードを残す。鈴木達夫の手持ちカメラが素晴らしく、加賀まりこの美しさも手伝って実にみずみずしい作品となっている。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Moliere", "モリエール　恋こそ喜劇", "", "", "http://image.eiga.k-img.com/images/movie/55081/315x210.jpg?4109", 120, "1644年のパリ、売れない俳優ジャン＝バチスト・ボクラン（モリエール）が恋人マドレーヌらと旗揚げした劇団「盛名座」は、破産の危機に瀕していた。モリエールは借金の肩代わりと引き換えに、金持ちの商人ジュルダンの演劇指南役を務めることになるが……。17世紀フランスで活躍した、喜劇作家モリエールの生涯で、空白となっている数カ月間を描くフィクション。主演は「スパニッシュ・アパートメント」のロマン・デュリス。", "2010-03-06", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Road, Movie", "ロード、ムービー", "", "", "http://image.eiga.k-img.com/images/movie/54961/315x210.jpg?4582", 97, "国際的に高い評価を受けたインド映画「スプリット・ワイド・オープン／褐色の町」のデーブ・ベネガルが10年ぶりに長編映画のメガホンを取ったロードムービー。父親が営むヘアオイル業から逃れるように家を飛び出した青年ビシュヌは、おんぼろトラックに乗り込んで砂漠横断の旅に出る。途中で加わった奇妙な仲間たちと旅を続けるうち、彼はトラックの荷台に乗せられていた古い映写機やフィルムで移動映画館を始めることになり……。", "2009-10-21", "21,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼神伝", "鬼神伝", "", "", "http://image.eiga.k-img.com/images/movie/55271/315x210.jpg?4282", 98, "「QED」シリーズなどで知られる人気ミステリー作家・高田崇史の同名小説をアニメ映画化。“人”と“鬼”とが壮絶な争いを繰り広げる平安時代の京都を舞台に、現代から1200年の時を超えて迷い込んだ15歳の少年・天童純の戦いを描く。監督を務めるのは、「天空の城ラピュタ」「AKIRA」といったヒット作のアニメーターとして活躍し、メガホンをとった「スプリガン」も高く評価された川崎博嗣監督。製作には大友克洋が名を連ねる。", "2011-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Miracle at St. Anna", "セントアンナの奇跡", "", "", "http://image.eiga.k-img.com/images/movie/54251/315x210.jpg?3552", 160, "「マルコムX」「ドゥ・ザ・ライト・シング」のスパイク・リー監督が、戦争を舞台に描く人間ドラマ。1983年のニューヨーク。ある日、真面目な郵便局員が突然、客としてやってきたある男を射殺した。その郵便局員は、第2次大戦時、黒人部隊“バッファロー・ソルジャー”の一員としてイタリアの戦線に送り込まれていたが、そこで出会ったひとりの少年との交流が、全ての謎を解く鍵だった……。主演は「アントワン・フィッシャー」のデレク・ルーク。", "2009-07-25", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Zoolander", "ズーランダー", "", "", "http://image.eiga.k-img.com/images/movie/1761/315x210.jpg?3287", 89, "スーパーモデルのズーランダーは、最優秀男性モデル賞でライバルの新人モデル、ハンセンに敗れ、再起を賭けてデザイナー、ムガトゥの専属モデルに。しかし、ムガトゥが彼を雇ったのは某国の要人暗殺のため。ズーランダーはそれを知らぬまま特訓を受けるが。「リアリティ・バイツ」「ケーブル・ガイ」の監督ベン・スティラーが監督・脚本・1人4役で大活躍。ウィノナ・ライダーからベッカム夫人までセレブ40人が豪華カメオ出演。", "2002-09-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Larry Crowne", "幸せの教室", "", "", "http://image.eiga.k-img.com/images/movie/56821/315x210.jpg?1810", 99, "トム・ハンクスが「すべてをあなたに」（1996）以来15年ぶりに長編映画のメガホンをとり、ジュリア・ロバーツを共演に迎えて描いたハートフルラブコメディ。地元の大型スーパーで同僚や常連客からも慕われていたベテラン従業員のラリー・クラウンは、ある日突然、大卒ではないという理由で解雇されてしまう。心機一転して地元の大学に入学したラリーは、いつしか教えることの情熱を忘れてしまった美人教師のメルセデスと出会う。共演にセドリック・ジ・エンターテイナー、タラジ・P・ヘンソン、パム・グリアら。「マイ・ビッグ・ファット・ウエディング」のニア・バルダロスが共同脚本を担当。", "2012-05-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いとはん物語", "いとはん物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "北条秀司の戯曲を溝口健二門下の成沢昌茂が脚色、伊藤大輔が監督した女性映画。不器量だが美しい心を持つ老舗の扇屋の娘の初恋と、その破局を情感たっぷりに描く。「羅生門」「地獄門」で国際的な名声を得た大スター・京マチ子が醜女に扮し、夢想シーンの中でのみ本来の美しい姿で現れるという趣向が興味深い。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GO WEST", "マルクスの二挺拳銃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "マルクス・ブラザースが西部劇に挑戦。ゴールドラッシュや鉄道建設のためにごった返す西部にやって来たチコとハーポは、好意で貸した10ドルの片に、“死者の谷”の土地権利書を預かるが……。クライマックスのSL大暴走は屈指のダイナミック・ギャグ。DVDは「マルクス・ブラザーズ コレクターズ・ボックス」に収録。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嵐の果し状", "嵐の果し状", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "小林旭が主演した日活後期の任侠アクション。時は大正中期。ヤクザ稼業にいや気がさし、征次郎は回漕問屋・沢井屋で出直す決心をした。しかし、その直後、主人が病で倒れ、店は本堂組に取られてしまう。征次郎は沢井屋再建のために動き始めるが、本堂組からは様々な妨害が……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BULL OF THE WEST", "大砂塵の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "西部の大牧場で繰り広げられる、牛をめぐるあらくれ男たちの闘いを描く。流れ者のジョニーとスティーブが牧童として働く牧場で、牧場主たちの協定を無視して、新たに5000頭の牛を買い入れると発表する。牛の食料が脅かされると牧場主たちは反駁したが無視され、やがてトラブルが続出。牧童頭になったジョニーの悪どさにスティーブは嫌気がさしてくる……。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すべては海になる", "すべては海になる", "", "", "http://image.eiga.k-img.com/images/movie/54651/315x210.jpg?1649", 119, "店頭宣伝のポップを書くのが得意な書店員・夏樹は、ある日、万引き犯とおぼしき中年女性を捕まえる。しかし女性は万引きしておらず、謝罪のために女性の家を訪ねた夏樹は、そこで影のある高校生・光治に出会う。父の暴力や妹の不登校などに悩む光治は読書を心の支えとしていて、2人は急速に親しくなっていく。しかし、大手出版社勤務の鹿島と交際を始めたばかりの夏樹はどこか光治に戸惑いを感じ始める。作家の山田あかねが自身の原作小説を脚本化し、初監督に挑戦。主演は佐藤江梨子、柳楽優弥。", "2010-01-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悲しい色やねん", "悲しい色やねん", "", "", "http://ecx.images-amazon.com/images/I/313ACHVHD9L._SL210_.jpg", 102, "小林信彦の原作を森田芳光が大胆にアレンジしたシティ感覚の青春ヤクザ映画。夕張組の跡取り息子トオル（仲村）は銀行員として働いていたが、父が三池組系の元ヤクザとの喧嘩で大ケガしたことからヤクザの世界に身を投じることに。トオルの親友・恵（高嶋）は敵対組織の三池組に属していたが、2人で組んでビッグビジネスに挑戦しようとすすめるが……。男役に挑戦している江波杏子、悪女役の藤谷美和子に加えて、不意に画面に現れる書割を思わせるような道頓堀川のカットが印象的。", "1988-12-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHA＾TEAU EN SUE｀DE", "スエーデンの城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "次々とスター女優を育て上げた耽美派の鬼才ヴァディムが、F・サガンの原作をもとに描く官能ドラマ。スウェーデンの湖中の島にそびえる城館。城主ユーゴーには妻がいたが、彼は若く美しいエレオノールに惹かれていく。しかし、彼女は実の兄セバスチャンと深く愛し合っていた。やがて城にやって来たユーゴーの従弟エリックも、彼女の妖しい美しさに魅せられてしまう……。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大菩薩峠・完結編", "大菩薩峠・完結編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "シリーズ完結編。机龍之助は、お浜の死霊に誘われたかのように、お浜の故郷、武州沢井村にやって来る。兵馬は龍之助と大菩薩峠で対するが、狂気につかれた龍之助は、わが子郁太郎の泣き声を聞き濁流の中に姿を消す。原作が未完のため無理に結末をつけた感があるが、内田吐夢の重厚な演出をおおいに堪能できる。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOW　BOAT", "ショウボート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "E・ファーバーの同名小説の３度目のミュージカル映画化。19世紀末、ミシシッピー沿岸を巡業する劇場船を舞台に、座長の娘と賭博師との恋、南部で労働に精を出す黒人の姿などが名曲『オールマン・リバー』の調べにのって感動的に描かれた作品。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Melinda and Melinda", "メリンダとメリンダ", "", "", "http://image.eiga.k-img.com/images/movie/1531/315x210.jpg?3347", 100, "ウッディ・アレン監督の新作は、ヒロインがたどる2つの道筋＝ラブ・コメ版と悲恋もの版を交互に描くラブ・ストーリー。マンハッタンのレストランにたむろする劇作家たちが「人生は悲劇か喜劇か」を議論するため、ひとりの女性を巡る2つの物語を構成していく。スタートはどちらも監督、俳優、製作者たちが集まるパーティ。そこにメリンダが登場してドラマは2つに分岐していく。メリンダ役は「ネバーランド」のラダ・ミッチェル。", "2005-06-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOY LUCK CLUB", "ジョイ・ラック・クラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "「夜明けのスローボート」で、自らの民族の物語を描いたW・ワン監督が、さらに深く中国系アメリカ人の過去を見据えた一編。ほとんど財産も持たずアメリカに渡ってきた４人の一世たちと、アメリカで生まれ、アメリカの教育を受けて育ったその４人の娘たち。二つの世代のズレを物語の骨格に据えながら、それぞれの世代を超えた愛情と夢を、ゆったりとしたリズムのなかに描き出している。製作総指揮にはO・ストーンと、そのスタッフの中国系アメリカ人、J・ヤンがあたっているのにも注目。なお原作となったA・タンの同名小説は、20ヵ国語に翻訳され、世界中でベストセラーとなっている。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠狂四郎 殺法帖", "眠狂四郎 殺法帖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "加賀蕃とその政商との密貿易に絡む争いに巻き込まれる無頼の徒・眠狂四郎の活躍を描く。生涯ただ一人愛した女の死は狂四郎のニヒルの度を深め、２作目以降にキャラクターの完成をみる。全体に出来はいいが、短銃を至近距離で射たれても傷一つないのは解せない。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兄弟仁義／続・関東三兄弟", "兄弟仁義／続・関東三兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "昭和の初頭、深川の荷役業者・辰馬組の小頭・関根政次は、関東七人会の一員で東京港の荷役を一手に引き受けている大和田組とのいざこざに巻き込まれ、弟分の末広勝次郎と二人で大和田組に乗り込む。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使行動／ANGELS", "天使行動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "西城秀樹が香港で主演したバイオレンス・アクション。秘密諜報部隊“エンジェルス”のメンバーに扮する西城が、二人の仲間とともに女ボスの率いる犯罪シンジケートを壊滅させようと大活躍する姿を描いている。製作当時、香港では西城人気が日本以上にあり、映画も大ヒットを記録した。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海猿", "海猿", "", "", "http://ecx.images-amazon.com/images/I/51B3J1JQXBL._SL210_.jpg", 120, "若き海上保安官たちが厳しい訓練を積み、人命救助のエキスパートである潜水士として成長していく姿を描いた青春劇。原作は「週刊ヤングサンデー」に連載された佐藤秀峰のコミック。潜水士を目指す仙崎大輔は、海上保安官の中でも優秀な者のみが参加できる潜水技術課程研修に参加。バディを組む工藤に足を引っ張られてばかりだったが、工藤の純粋さに共感し、協力して訓練を乗り越えていく。仙崎と工藤はいつしか訓練生たちの中心的存在となっていくのだが……。", "2004-06-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PASSED AWAY", "お葬式だよ 全員集合！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "アイルランド系アメリカ人の大家族出身というC・ピータース監督が、いくつかの通夜を経験してきたなかで思いつき、脚本を書いたという作品。70才になる父の突然の死によって、疎遠になっていた４人の子供たちがアイルランド系の伝統的な葬式を挙げようと集まる。B・ホスキンスが演じる長男が、それを機会に家族の絆を取り戻そうと奮闘する様をコミカルに描いている。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Into the Blue", "イントゥ・ザ・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/34261/315x210.jpg?1924", 110, "貧乏ながらも恋人のサム（ジェシカ・アルバ）と幸せな日々を送っているジャレッド（ポール・ウォーカー）は、いつか財宝を積んだ沈没船を見つけることを夢見ている。そんなある日、ジャレッドはカリブ海でダイビングを楽しんでいる最中に、伝説の沈没船を発見するのだが……。ポール・ウォーカー、ジェシカ・アルバ、スコット・カーン、アシュレー・スコットら若手スターが共演。77年製作「ザ・ディープ」のリメイク。", "2005-11-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パークアンドラブホテル", "パークアンドラブホテル", "", "", "http://image.eiga.k-img.com/images/movie/53501/315x210.jpg?3620", 111, "短編映画「珈琲とミルク」でPFFアワード3冠を受賞した熊坂出監督が長編映画に初挑戦し、2008年ベルリン国際映画祭で最優秀新人賞に輝いたハートフル・ドラマ。亡き夫が遺したラブホテルを営んでいる孤独な女性・艶子と、ラブホテルの屋上にある小さな公園に集まった世代や境遇の異なる女性たちとの心の交流を生き生きと描き出す。女優としても活躍するシンガー・ソングライターのりりィが、圧倒的な存在感で主人公・艶子を演じる。", "2008-04-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES VALSEUSES", "バルスーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ヌーヴェル・ヴァーグ以降低迷を続けていたフランス映画界に新風を吹きこみ、当時「エマニエル夫人」と興行トップの座を争ったヒット作。職も無くヒッピーのような生活を送る二人の男の前に現れた情婦マリー。好きなことをしてきた彼らは、マリーとも寝てみるが彼女は重度の不感症だった。本能的な快楽に生きようとする彼らにとって、それは耐えられない現実だった。その後、二人は40歳の中年女と快楽の時を過ごしたが、セックスのあとにその女が衝動的な自殺を図る……。1970年代半ばという闘争の季節のあとに、目的もなく欲望のおもむくままに生きた若者たちの姿を綴った、B・ブリエの出世作。", "1974-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A LONG WAY HOME", "ロングウェイ・ホーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "深い絆で結ばれた３兄弟の別離から再会までの16年間を切々とうたい上げた感動作。実話をもとに1981年12月、ABCを通じて全米に放映され45％の視聴率を獲得したTVムービーを劇場用に再編集。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一生の？お願い", "一生の？お願い", "", "", "http://ecx.images-amazon.com/images/I/515NxYuqoAL._SL210_.jpg", 0, "ファッション誌やグラビアを中心に活躍する写真家・松村清秀が映画監督デビューを果たしたホラーコメディ。ある晩、ある街の各所で事件や事故が同時多発的に発生する。なりゆきで巻きこまれてしまった人々はそれぞれの考えで死体を処分しようとするが、偶然にも全員が同じ場所を廃棄先に選んでしまう。道中、死体はどんどん増えていくが、その一方で彼らと死体にまつわる様々な事実が明らかになり……。", "2005-10-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Brewster's Millions", "マイナー・ブラザース／史上最大の賭け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "　マイナーリーグ、ブルズの投手モンティは喧嘩が原因で、捕手のスパイクとともにクビになってしまう。そんなモンティに朗報が。なんと彼は3億ドルの遺産を相続することになったのだ。しかしこれには条件があった。誰にも理由を明かさず、30日間で3000万ドルを使い果たすこと。かくしてモンティの浪費大作戦が始まった！　あの手この手で湯水のごとく金を使う主人公の騒動を描く痛快コメディ。未公開のイギリス映画「おかしなおかしなお金持ち」のリメイク。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Crazies", "ザ・クレイジーズ", "", "", "http://image.eiga.k-img.com/images/movie/13561/315x210.jpg?1799", 103, "ペンシルバニアの田舎町に軍用機が墜落。それには軍が秘密裏に開発した細菌兵器が積み込まれていた。細菌は町の飲料水に紛れ、それを飲んだ人々は次々と発狂。平和な町はパニック状態に陥る。政府は軍を使ってこれを隔離し、事件を闇に葬ろうとするが……。狂人が生む惨劇、そして軍隊による殺戮が繰り広げられるパニック・ホラー。", "2010-01-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Limitless", "リミットレス", "", "", "http://image.eiga.k-img.com/images/movie/56161/315x210.jpg?1452", 105, "「ハングオーバー！」のブラッドリー・クーパーが主演、「幻影師アイゼンハイム」のニール・バーガー監督が手がけたサスペンスアクション。原作はアラン・グリンの人気小説「ブレイン・ドラッグ」（文春文庫刊）。スランプに陥り、恋人にも去られてしまった作家のエディは、義弟バーノンから脳を100％活性化できるという薬「NZT48」を手に入れる。薬を服用し一夜で長編小説を書き上げるとたちまちベストセラーとなり、さらにはビジネス界にも進出して株取引や投資で成功を収めるが、やがて恐ろしい副作用に襲われる。", "2011-10-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAYS OF WINE AND ROSES", "酒とバラの日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "仕事のつらさを、酒によってまぎらわそうとする夫と、その夫に感化されて酒の味を覚えた妻。二人が底知れぬアルコール依存症の地獄にはまりこんでいくさまを、B・エドワーズがリアルに描く。J・レモンが、アル中男を鬼気迫る演技で巧演。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スプリガン", "スプリガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "欧米でも出版された、たかしげ宙・皆川亮二の人気コミックを映画化したSFアニメ。大友克洋が総監修、江口寿史がキャラクター・デザインと作画監督を担当している。スプリガンとは、世界各国の超古代遺産を守る秘密組織アーカムの工作員のこと。17歳にして最高の腕前を誇る日本人スプリガン・御神苗（おみなえ）優が、何者かにプラスティック爆弾を送りつけられ、トルコ山岳地帯の遺跡発掘現場へ。そこで“ノアの方舟”をめぐりサイボーグ部隊と衝突する彼の運命が、激烈なスペクタクル描写と幻想的な映像美で描かれていく。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暖きふる里", "暖きふる里", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "東京から戻った娘の結婚話を、牧歌的な雰囲気の中で描いた小品。主演は、“ミス神戸”から映画界入りし、1937年に「愛怨峡」で演技開眼、この作品の当時、進境著しかった女優、山路ふみ子。ちなみに、1977年から設立された山路ふみ子映画賞は、のちに私財を成した彼女が、映画界の発展を望んで作ったもの。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RETURN OF SUPERFLY", "スーパーフライ91", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "要注意人物として警察からマークされていたプリーストは、旧友の葬儀のためにパリから戻って来たところを麻薬取締局に拘束される。そして身柄の自由と引き換えに、捜査側のスパイとして麻薬組織の殲滅を要求される。麻薬に大切なものを奪われていったプリーストは、組織を潰すべく立ち上がった。ブラック・ミュージックと過激なアクションを融合した「スーパーフライ」のリメイク版。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAP", "タップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "「コットンクラブ」「ホワイト・ナイツ」などの映画や、ブロードウェイの舞台で活躍するG・ハインズが主演する一種のバックステージもの。天才的なタップダンサーを父に持つ主人公は自らも素質に恵まれていながら、あえて異なる道を歩み刑務所生活を送る。しかしやがて芸人の世界に戻っていく……。ハインズのタップ・シーンは圧巻。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金枝玉葉２／WHO’S THE WOMAN，WHO’S THE MAN", "ボクらはいつも恋してる！　金枝玉葉２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "香港製のラブ・コメディ「君さえいれば　金枝玉葉」の続編。前作で結ばれた売れっ子音楽プロデューサーのサムと男装の女性歌手ウィンの、その後の恋騒動が描かれる。同居生活を始めたサムとウィンだったが、生活のリズムが合わず、すれ違いばかり。現状を打開すべく、ウィンは芸能界に復帰する。そんな彼女の前に、かつて一世を風靡した伝説のスター、フォンが出現。ウィンにとってフォンは何でも話せる相談相手となるが、フォンは男装のウィンを男と思い込み心惹かれていく。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kung Fu Panda 2", "カンフー・パンダ2", "", "", "http://image.eiga.k-img.com/images/movie/54261/315x210.jpg?3208", 90, "2008年にヒットしたドリームワークス・アニメーション「カンフー・パンダ」の続編。カンフーを習得し伝説の“龍の戦士”となった小心者で食いしん坊のポーは、有能なマスター5たちとともに強力な武器を操り、中国を制圧してカンフーを抹殺するという野望に燃える新たなる敵、孔雀のシェン大老と戦う。声優は前作のジャック・ブラック、アンジェリーナ・ジョリー、ジャッキー・チェンらに加え、ゲイリー・オールドマン、ジャン＝クロード・バン・ダムらが参加。", "2011-08-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BODILY HARM", "覗く女2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ラスベガスでストリッパー惨殺事件が起きる。容疑者で元警官のサム・マッキオンは以前、担当女刑事リタ・ケイツの同僚で不倫関係にあった男。サムとの再会に戸惑うリタだが、ある日サムが彼女の家を訪れ、誘惑に負けたリタは再び彼と肉体関係を持ってしまう。罪悪感とは裏腹に愛欲の日々に溺れていくなか、同一犯と思われる第２の殺人が起こる。本能的に男を求めてしまう女の性を描いたエロティック・サスペンス。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BABYSITTER", "ベビーシッター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ベビーシッターのアルバイトをしている美少女ジェニファーは、恋人や世話をしている子供の父親、そして街のあらゆる男たちの欲望をひきつけていた。ある夜、言うことを聞かない子供たちを寝かしつけ、ゆっくりとバスタブにつかった彼女は、様々な男たちの妄想の中へと溶け込まれていく……。セックスとバイオレンスが渦巻くエロティック・サスペンス。A・シルヴァーストーンが、セクシーなヒロインを演じている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The last emperor", "ラストエンペラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 163, "　3歳で清王朝の皇帝に即位した溥儀の生涯を、絢爛豪華に描いた歴史大作。1950年中国人戦犯として護送される最中、溥儀は自殺を図る。意識が遠のいていくなか、幼き日の情景が脳裏によみがえってくる……。外国人に初めて撮影許可が下りた紫禁城での即位式は、幼い溥儀に500人の家臣がかしずく圧巻のシーンである。1987年アカデミー作品賞をはじめ、9部門に輝いた。", "1988-12-31", "4,3,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("НОЧЕВАЛА ТУЧКА ЗОЛОТАЯ／A GOLDEN CLOUD RESTED IN THE MOUNTAINS", "金色の雲は宿った", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "1944年、モスクワからコーカサスに疎開しにやって来た双子の兄弟コーリャ、サーシャを主人公に、戦争にも負けずにたくましく生きる子供たちを繊細なタッチで描いた作品。疎開先でコーリャとサーシャ兄弟は、農場や缶詰工場から食べ物をくすねたりしながら生きていたが、ある日の銃撃戦でサーシャは殺されてしまう。放心のコーリャの前にチェチェン人の少年アルハズルが出現し、二人は兄弟分の約束を交わすのだった……。", "1989-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人妻集団暴行致死事件", "人妻集団暴行致死事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "東京近郊の半農村で養鶏業を営む男とチンピラ３人組。３人は男の妻を犯してショック死させ、男は妻の体を屍姦する。わかったようでいて実は何もわかっていない人生の虚しさを描いた、田中登監督の代表作の一つ。当時、大麻事件で逮捕された室田日出男の映画復帰第１作ともなった。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN TE´MOIN DANS LA VILLE", "彼奴〈きゃつ〉を殺〈け〉せ!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "妻ジャンヌを彼女の愛人ベルディエに殺されたアンスランスは、ベルディエを自殺に見せかけて殺し、妻の復讐を遂げる。だが、彼が犯行を終えてベルディエの家から出てきたところをタクシーの運転手のランベールに目撃されてしまう。完全犯罪を狙うアンスランスは、ランベールを殺そうとつけ狙う。そしてついにアンスランスは、ランベールのタクシーに乗り込むが、ランベールも新聞で事件を知り、ひそかに無線のスイッチを入れる。車内の会話は営業所に筒抜けになり、ランベールの危険を知った仲間のタクシーが救援に向かう。夜のパリのスリリングなカーチェイスを見せるフィルム・ノワールの秀作。", "1959-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狼少女", "狼少女", "", "", "http://image.eiga.k-img.com/images/movie/1581/315x210.jpg?3970", 106, "昭和の小さな町。小学4年生の明の町に見世物小屋が興行にやってくる。チラシに書かれた狼少女が見たくてたまらない明だが、先生や両親は見世物小屋へ行くことを禁止する。明は、都会からの転校生・留美子といっしょにいじめられっ子の秀子を助けるが、秀子は見世物小屋の狼少女に違いないという噂が流れていた。函館港イルミナシオン映画祭でシナリオ大賞を受賞した大見全の原案を「自転車とハイヒール」の深川栄洋監督が映画化。", "2005-12-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兄貴", "兄貴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "区割整理の裏情報をつかんだヤクザたちが、あるバーの立ち退きを強行。しかし哀れなマダムが世をはかなんで自殺したことから、幹部の一人だった石井は足を洗う決心を固める……。“ダンプ・ガイ”こと二谷英明主演のギャング映画。彼を“兄貴”と慕う少年の視点から描かれた、日活アクションの佳編。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRENCH POSTCARDS", "フレンチグラフィティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「アメリカン・グラフィティ」の脚本コンビが、フランスに留学した経験をもとに、パリのアメリカ人留学生３人の成長を描く青春映画。過保護のジョーエル、女性校長に熱をあげるアレックス、ボーイフレンドと別れてやって来たローラの大人への旅立ち。", "1979-01-01", "7,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOLDEN EIGHTIES", "ゴールデン・エイティーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ベルギー生まれの女流監督C・アケルマンが、今までの実験的な作風から一変して撮り上げたミュージカル・コメディ。パリの地下街に集まった高級ブティックや美容院の人々がにぎやかな恋愛模様を展開する。", "1986-01-01", "37,2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAGIC KIDS2", "GUY／俺たちの戦場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ハリウッドの超人気映画スター、カラテ・ボーイのケビンは、TVや雑誌に追いまくられハードスケジュールをこなす毎日。これも映画会社が、他社の引き抜きを恐れ無理にスケジュールを詰め込んでいるためだった。我慢の限界に達したケビンは脱出計画を決行するが、それを知った悪徳プロデューサーたちは強靭な肉体を持った追っ手を放ち、ケビン捕獲作戦に乗り出す。11歳にして黒帯を持つ天才カラテ・キッズ、T・J・ロバーツがしなやかな肉体で切れのある技を見せた痛快アクション。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La VIE〈いのち〉共生！", "La VIE〈いのち〉共生！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1991年、200年ぶりに火を噴き、５万人を超える人々に大きな被害を与えた雲仙・普賢岳。その被害と苦しみの中でも強く生きる人間の姿を、プロの俳優に交えて多くの地元の人々の出演で描いた作品。200年前の噴火にまつわる民話の再現や、伝統的な精霊流しのシーンなども収め、普賢岳の麓という土地とそこでの生活を立体的に伝えている。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("荒木又右衛門 決闘鍵屋の辻", "荒木又右衛門 決闘鍵屋の辻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "黒澤明が脚本を書き、森一生が初めて東宝で撮った大チャンバラ時代劇。伊賀上野の鍵屋の辻で荒木又右衛門が義弟の助太刀をするまでを描く。従来の芝居や映画では又右衛門が大勢の仇をバッタバッタと斬り倒していたが、本作では実録通りに二人しか斬らないなど、新しい解釈の荒木又右衛門像を作っている。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("撃てばかげろう", "撃てばかげろう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「惨殺せよ 切なきもの、それは愛」の原作者、野村秋介が企画・脚本も兼ねた任侠ドラマ。甲子園出場の夢に破れ、極道の世界に身をやつした一人の若者が、対立する組織との抗争に巻き込まれながら、その若き生命を散らしていくまでの生きざまを哀感漂うタッチで描く。「仔鹿物語」に続いて、澤田幸弘が久々に手掛けたアクション映画でもある。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FLYING DEUCES", "ローレル＆ハーディの天国二人道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "“極楽コンビ”ことローレル＆ハーディ主演によるミュージカル・コメディ。外人部隊に紛れ込んだ二人組の奇想天外な大活躍を、『Shine On，Harvest Moon』などの愉快な歌と踊りを交えつつ描く。実生活では不仲だったという彼らの、栄光の掉尾を飾る長編トーキー映画。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Deal of the Century", "世紀の取り引き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "　武器商人エディは南米の独裁者に最新型戦闘機を売りつけようとする。しかしエディの仲間が宗教に目覚めたことから、世紀の取り引きは危機的状況に……。ウィリアム・フリードキン監督が国際的な兵器売買を皮肉たっぷりに描いたブラック・コメディ。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Small Time Crooks", "おいしい生活", "", "", "http://image.eiga.k-img.com/images/movie/1671/315x210.jpg?1992", 95, "ウッディ・アレン監督の第31作。結婚25年目の夫婦、レイとフレンチー。実はレイは初めての銀行強盗で投獄され刑務所を出たばかり。こんどこそ完全犯罪をと、銀行強盗を計画し、カモフラージュのためクッキー屋を始める。ところが、この店が大繁盛、2人はあこがれの大金持ちになってしまうのだが……。アレン自身が「地球は女で回っている」（97）以来の主演に復帰。毒舌家の妻役は「ブロードウェイと銃弾」（94）のトレイシー・ウルマン。アレンの過去作の出演俳優が多数共演。", "2001-10-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超劇場版ケロロ軍曹3 ケロロ対ケロロ　天空大決戦であります！", "超劇場版ケロロ軍曹3 ケロロ対ケロロ　天空大決戦であります！", "", "", "http://image.eiga.k-img.com/images/movie/53391/315x210.jpg?1532", 0, "地球侵略を目論むカエル型宇宙人が大騒動を繰り広げる人気ギャグアニメの劇場版第3弾。インカ帝国の遺跡マチュピチュを訪れた冬樹とケロロ小隊は、そこで迷路のような遺跡を発見する。冬樹は迷路の中で謎の少女を見かけるが、彼女の正体がわからないまま帰国することに。東京に着いた彼らの前に、外見はケロロにそっくりだが冷徹に地球侵略を実行しようとする“ダーク・ケロロ”が現われ……。福田沙紀、ルー大柴がゲスト声優として登場。「武者ケロ／お披露目！戦国ラン星（スター）大バトル!!」と同時上映。", "2008-03-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("華魁", "華魁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "明治半ばの長崎。絵草紙売りの喜助は、遊廓の菖蒲太夫と恋に落ちる。しかし、菖蒲の美しい肌を狙う刺青師の清吉によって彼は殺され、その怨念は以後菖蒲が男に抱かれるたびに、ひざの部分に顔形のアザとなって現れるのだった……。過激な性交描写に驚く、武智監督の文芸エロス・ロマン。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BELLS OF ST.MARY’S", "聖メリーの鐘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "アカデミー賞７部門受賞作「我が道を往く」の姉妹編。新任神父オマリーは、ベネディクト尼僧と協力して、経営難に陥る教会附属学校の存続に努める。尼僧役のバーグマンが、初めて歌を披露、持ち前の容姿とともにスクリーンに花を添えている。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COMPAGNA　DI　VIAGGIO", "雨上がりの駅で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "気ままなバイト生活を送る19歳の娘コラが、知人から奇妙な仕事を頼まれる。それはアルツハイマー症に陥った老教授を尾行し、その行動を連絡するというものだった。教授の突飛な行動にほんろうされ、イタリア各地を駆けめぐるハメになったコラの心に、やがて奇妙な変化が……。イタリアン・ホラーの巨匠ダリオ・アルジェントを父に持つ若手女優アーシアが、みずみずしい魅力をふりまくロード・ムービー。旅を通して様々な人々との出会いを経験するヒロインの“自分探し”の物語なのだが、その表現はあくまで淡々としていて慎ましい。まったくコミュニケーションが噛み合わなかったヒロインと老人が、何度かはぐれながらも駅で再会し、言葉を発しないまま心を通わせるラスト・シーンは、驚くほど豊かな感動に満ちている。名カメラマン、G・ランチの柔らかい光に満ちたナチュラルな映像美も秀逸。", "1996-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ネコナデ", "ネコナデ", "", "", "http://image.eiga.k-img.com/images/movie/53591/315x210.jpg?2774", 85, "自分にも他人にも厳しいサラリーマンの鬼塚は、ある日公園で子猫が捨てられているのを発見する。子猫のあまりの可愛さにすっかり心を奪われてしまった彼は、家へ連れて帰ることも出来ず会社の研修用施設でこっそり飼いはじめる。「デトロイト・メタル・シティ」の脚本を手掛ける大森美香がメガホンを取り、堅物の中年男と子猫との交流をほのぼのと綴ったハートフル・コメディ。小さな子猫に振り回される主人公を大杉漣が好演。", "2008-06-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT　PASSAGE", "夜の道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "冬が迫るコロラド山脈で、鉄道の工事作業隊への給金輸送列車が襲われる。鉄道会社は腕利きの護衛官をつけるが、彼が遭遇した強盗団には生き別れの弟が加わっており……。肉親への情愛と、正義の間で苦悩する主人公をJ・スチュアートが力演するウエスタン。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("帝都物語 外伝", "帝都物語 外伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「帝都物語」「帝都大戦」に続く荒俣宏原作の映画化第３作。「ドライビング・ハイ！」の橋本以蔵がメガホンを執っている。破壊神・加藤保憲が最後に姿を現してから50年。加藤を封じ込めた祠は、精神科“西条病院”の建設で取り壊されていた。院長の命令によって加藤に扮し、患者たちのパニック反応の臨床実験に協力する看護士の柳瀬仁哉。仁哉には、夜の街で拾った女を殺しては犯す、死姦性愛者という隠れた一面があった。ある夜、彼が拾った女・美千代はセラピストと称し、父親の療法室で慈母のようにふるまう。そして彼女は、加藤の因縁の敵・辰宮一族の末裔でもあった。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NAKED GUN PART331/3 THE FINAL INSULT", "裸の銃〈ガン〉を持つ男　PART331/3 最後の侮辱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "L・ニールセン演じるドレビン警部が活躍するシリーズ第３作。定年引退し、家事に精出すドレビンが爆弾犯捜査のため復職。またもや珍妙な騒ぎを引き起こす。今回のクライマックスはアカデミー授賞会場での大パニック。「マルコムX」「ジュラシック・パーク」等のヒット映画のパロディーは文句なく爆笑モノ。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("K-20 怪人二十面相・伝", "K-20 怪人二十面相・伝", "", "", "http://image.eiga.k-img.com/images/movie/53311/315x210.jpg?1847", 137, "北村想の「怪人二十面相・伝」を金城武主演で映画化。監督は「アンフェア the movie」などの脚本家・佐藤嗣麻子。1949年、一部の特権階級が富を独占している日本の都市・帝都に、金持ちだけを狙って盗みを働く“K-20（怪人二十面相）”が出没。謎の男にだまされてK-20に仕立て上げられ、世間から追われる身となったサーカス団の曲芸師・遠藤平吉は、富豪の令嬢・羽柴葉子や名探偵・明智小五郎らを巻き込み、K-20に戦いを挑むことを決意する。", "2008-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LINE", "LINE", "", "", "http://image.eiga.k-img.com/images/movie/55391/315x210.jpg?1578", 52, "映像作家・小谷忠典が、生まれ育った町・大阪府大正区のルーツと、自らの人生に対峙したドキュメンタリー。沖縄からの移住者が多く住む大正区で、小谷は足の踏み場もないような部屋で暮らしている。酒に溺れる父と、恋人とその子どもとの日々に苛立ちを募らせる小谷は、何かを探しに沖縄へと向かう。そこで出会ったのは、元コザ市の歓楽街に暮らす、傷ついた娼婦たちだった。", "2010-05-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("インディアン・サマー", "インディアン・サマー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "都会の片隅に生きる若者の成長をリリカルに描いたヒューマン・ドラマ。宙ぶらりんな生活を送る青年が、行方不明の女性を探すことで自分の人生を取り戻していく。等身大の好演を見せたいしだが、主演だけでなく主題歌を作詞・作曲、演奏も披露している。26歳のフリーター、アキオは、幼なじみの千春が失踪したと知って、行方を捜し始める。彼女は８年前、同じ電車で上京した仲だった。", "2005-08-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BABY LOVE（LEMON POPSICLE V）", "グローイング・アップ5　ベイビー・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "おなじみワルガキ３人組が騒動を繰り広げるシリーズ第５作。今回ベンジーの恋するマドンナは、なんと親友ボビーの妹ジニー。プレイボーイのボビーが隠し続けた箱入り娘、親友とはいえ絶対に許さぬと頑張るボビー。さて、ベンジーはいかなる手段に出るか？", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARNEGIE HALL", "カーネギー・ホール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "一流ミュージシャンのメッカ、カーネギー・ホール。そこに勤める掃除婦のノラは、一人息子が、ピアニストとしてこの舞台に立つ日を夢みていたが、息子はモダン音楽に憧れ、母のもとを去ってしまう……。ルービンシュタインのほか、ホールとゆかりの深い有名音楽家の多数出演がうれしい音楽映画。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シャタラー", "シャタラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "日・伊合弁の自動車会社で、マフィアによる連続殺人事件が発生した。レーサーの木田光一は、ナビゲーターが殺されたことから、危険を顧みず復讐を誓い、その渦中に身を投じていく。吉川晃司主演によるハードボイルド映画。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄", "地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "日本の監督のなかでもひときわ異彩を放つ鬼才・中川信夫の代表作。血の池地獄、針山など日本古来の地獄絵をスクリーンに再現するだけにとどまらず、人間の罪の意識やそれからの救済を深くえぐっている。また、日本のSFXや特殊メイクの歴史を語る上でも貴重な作品といえる。人一倍罪の意識の強い清水は、悪魔のような友人のために恋人ら３人を過失死させてしまう。故郷へ帰った清水は死んだ恋人とそっくりの女に会うが、過失死させた相手の親に毒を盛られ、登場人物全員が地獄に堕ちる……。ラストに主人公が自分の子供を地獄から救い出す場面はダイナミックで感動的。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRANCESCO", "フランチェスコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "13世紀のイタリアで、修道士となって神に支え続けたフランチェスコの生涯を、彼を愛するキアラをはじめ弟子たちの回想によって描いていく奇跡のドラマ。L・カヴァーニ監督は、デビュー作でもフランチェスコの生涯を映画化している。M・ロークがストイックな主人公を好演。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ASPEN EXTREME", "アスペン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "世界的なスキーリゾート地であるコロラド州アスペンに夢と希望を抱いて二人の若者がやって来た。友情、恋、そしてエキサイティングなスキーシーンが展開するスポーツ・アドベンチャー。ヴィクトリア調の建物が並ぶ美しいアスペンの町並みと、雪崩をかわし、凍った滝や崖を滑走する迫力のスキー・テクニックは見もの。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Parallel Life", "パラレルライフ", "", "", "http://image.eiga.k-img.com/images/movie/55471/315x210.jpg?2979", 110, "仕事一筋の敏腕判事ソクヒョンは、美しい妻ユンギョンと愛娘のイェジンと幸せに暮らしていた。だがある日、何者かにユンギョンが惨殺され、ソクヒョンは失意の底に落ちる。事件の調査を進めるうちに、ソクヒョンは30年前に自分と全く同じような境遇に陥った男の存在を知り、異なる時代を生きる2人の人物が、一定の時間をおいて同じ運命を繰り返すという運命の法則“平行理論”の可能性を疑い始める。新鋭クォン・ホヨン監督がメガホンをとり、人気韓流ドラマ「宮廷女官　チャングムの誓い」のチ・ジニが主演を務める。", "2010-07-24", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヘルメス 愛は風の如く", "ヘルメス 愛は風の如く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "宗教法人“幸福の科学”総裁、大川隆法の原作をもとに、「Coo／遠い海から来たクー」の今沢哲男が、古代ギリシァの英雄ヘルメスの愛と冒険を描く。紀元前2300年のギリシァ。ミノス王の圧制の下、人々が救世主を待ち望む中、クレタ島シティアの王家に、全ギリシァを救うと予言された王子ヘルメスが誕生する。それから26年後、立派な若者に成長したヘルメスはリンドス島に幽閉されていた王女アフロディーテ姫を救い出し、妻とした。そして、シティアの王となった彼は、天地創造の神オフェアリスに遭遇。暴君ミノスを倒すという使命を授かる。自軍の50倍の兵力を持つミノスに、知恵で戦いを挑んだヘルメスは見事に彼を倒すことに成功するのだが……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chantrapas", "汽車はふたたび故郷へ", "", "", "http://image.eiga.k-img.com/images/movie/57081/315x210.jpg?3875", 126, "「素敵な歌と舟はゆく」「月曜日に乾杯！」の名匠オタール・イオセリアーニが、旧ソ連グルジアに生まれ、1979年から仏・パリを拠点に活動してきた自らの体験に基き手がけた半自伝的映画。旧ソ連体制下のグルジアで暮らす映画監督のニコラは、検閲や思想統制により自由な映画作りができないことに耐えかね、フランスに旅立つ。しかしフランスでは、作品に商業性を求めるプロデューサーと対立するなど困難が連続し、なかなか思うような映画作りが進まないが……。", "2012-02-18", "2,68", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("See You In The Morning", "いくつもの朝を迎えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "モデルの仕事を始めた妻と別れ、2人の子連れ女性と再婚した男（ブリッジス）が、新しい家庭や、別れた妻と子の間で揺れ動く様子を描いた恋愛ドラマ。離婚についてパクラ監督が正面から取り組んだ作品である。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MALADOLESCENZA", "思春の森 禁じられた性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "毎年夏休みには森の別荘にやって来る少女ラウラだが、仲良しだった少年が妙によそよそしく自分に意地悪なことばかりをすることに気付く。やがて近所の別荘にいる金持ちの少女も加わり、少年少女たちの秘密の遊戯は禁じられた領域にまで発展していく。ソフトな映像で描く思春期の悲劇。", "1979-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tim Burton's Corpse Bride", "ティム・バートンのコープスブライド", "", "", "http://image.eiga.k-img.com/images/movie/1041/315x210.jpg?4268", 77, "ティム・バートンによる「ナイトメアー・ビフォア・クリスマス」と同じ立体アニメ映画。共同監督はバートン製作の「ナイトメアー～」でアシスタント・アニメーター、「ジャイアント・ピーチ」でアニメーターを担当したマイク・ジョンソン。19世紀ビクトリア朝のロンドン。結婚を控えた内気な青年ビクターは、ふとした間違いからコープスブライド（死体の花嫁）の指に結婚指輪をはめてしまい、死者の世界に連れ去られてしまうが……。", "2005-10-22", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BATTELING BUTLER", "拳闘屋キートン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "富豪の息子で、やることなすことトンチンカンな軟弱者キートンが、たまたま名前が同じボクシングチャンピオンを偽って美女と結婚を果たす。ところが本物のチャンピオンが、世界タイトルマッチに挑戦することが決まって……。従者役で登場するS・エドワーズのトボけた演技が最高。ビデオタイトルは「キートンのラスト・ラウンド」。", "1926-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山と谷と雲", "山と谷と雲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "雪山から救助された山岳カメラマンの次郎は、有馬家の一人娘・寿々子の看護を受けた。彼女は、次郎の兄・一朗からプロポーズされており、寿々子はそのことを次郎に相談した。次郎は、兄との結婚を勧めるが、彼もまた彼女を愛し始めていた……。裕次郎主演の三角関係ものメロドラマ。原作は、檀一雄の「女の山彦」。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幽霊屋敷の恐怖・血を吸う人形", "幽霊屋敷の恐怖・血を吸う人形", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "山本迪夫監督による“血を吸う”シリーズ。交通事故で瀕死の重傷を負った娘を生き延びさせようと医師が催眠療法を行う。その結果、娘の肉体は滅び魂だけが残る。娘は吸血鬼となって甦り血を求めてさまよう。日本の風土に吸血鬼を登場させた意欲作。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蝉祭りの島", "蝉祭りの島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "夫を事故で亡くした身重のストリッパー珠子は、まだ見ぬ姑を頼って、彼の故郷である能古島へ赴いた。しかし、そこで彼女は女流作家と勘違いされ、島を上げての大歓迎を受けてしまう……。はすっぱな女の生きざまをコミカルに、そして切なく描いたヒューマン・コメディ。名曲『ろくでなし』が効果的に用いられている。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Joyeux Noel", "戦場のアリア", "", "", "http://image.eiga.k-img.com/images/movie/1261/315x210.jpg?3972", 117, "1914年、第1次大戦下。フランス・スコットランド連合軍とドイツ軍が連日砲弾を鳴り響かせているフランス北部の村に雪のクリスマス・イブが訪れる。ドイツ軍には10万本のクリスマス・ツリーが届けられ、スコットランド軍の塹壕からはバグパイプの音色が聞こえてくる。そして、美しい歌声の響きと共に奇跡は起こった……。", "2006-04-29", "2,3,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPACE MUTINY", "スペース・ミューティニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "人口過剰のために新天地を求めて地球を飛び立った、巨大宇宙移民船サザンサン号内に起きる軍隊の反乱と、移民船を守るべく立ち上がる鎮圧部隊との戦いを描くスペース・オペラ。R・ブラウンをはじめとして、J・ライアンなどB級アクション映画の常連たちが総出演している。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WICKER　PARK", "ホワイト・ライズ", "", "", "http://ecx.images-amazon.com/images/I/51JCBNRE8FL._SL210_.jpg", 116, "J・ハートネットが愛に生きる男に扮するラブ・ストーリー。冬のシカゴを舞台に、姿を消した恋人を探し続ける男と、彼とかかわることになった女性二人の切ない思いが交差していく。主人公のひたむきな愛が丹念に描かれる一方で、物語が進むにつれて意外な事実が明かされいく展開は、ミステリーとしての面白さも兼ねる。２年前、結婚直前に突然姿を消した恋人のリサとおぼしき人物を見かけたマシュー。どうしてもリサと再会したい彼は、彼女を探しはじめる。やがて彼は、リサという同名の別の女性にたどり着くのだが……。", "2004-12-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("流れる星は生きている", "流れる星は生きている", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "戦争が終わり、夫とはぐれた妻のけい子は３人の子供を連れて日本へ帰って来た。引き揚げ寮に住みながら夫の帰りを待つけい子。彼女の働き先の製本所主人は、次男を引き取りたいと申し出る。しかし、それを知った子供たちは家出をしてしまう……。藤原ていの記録文学を映画化した、三益愛子主演の“母もの”人情映画。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SF・サムライ・フィクション", "SF・サムライ・フィクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ミュージック・ビデオの分野でならした映像作家の中野裕之が手掛けた時代劇。彼ならではのポップかつシャープな感性で、剣豪たちの生き様を綴っていく。浪人から刀番に執りたてられた風祭が藩の宝刀を奪って逃亡。無鉄砲な若き藩士・平四郎は義憤に駆られて後を追うが、返り討ちにされ重傷を追う。そんな彼を助けたのは、人斬りを嫌う中年浪人・溝口。温厚で紳士的な彼は、ある理由からスゴ腕の剣士という過去を封印していた。だが、風祭はその腕を見抜き、溝口に決闘を申し込む……。人気ミュージシャン・布袋寅泰が音楽を手掛けるとともに風祭役に扮し、ニヒルな侍像を演じきっている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("楊貴妃", "楊貴妃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "国際映画祭で日本映画が賞を取るようになったこともあり、“これからは合作の時代”と語っていた溝口健二の思いは、この「楊貴妃」での香港との合作によって実現した。玄宗皇帝の妃となった楊貴妃の悲劇的な半生を描くゴージャスなメロドラマ。新しもの好きの溝口の初の総天然色作品。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄砲犬", "鉄砲犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "拳銃の入ったボストン・バッグを置き引きにスラれてしまった大介。だが懐の30万円は無事だったのでひと安心。ところがその拳銃が、都会の悪を牛耳る新星会によって殺人に利用され、大介は警察に追われる身となる。真相を知る者は新星会によって次々と消され、大介は窮地に立たされるが……。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("…これから物語 少年たちのブルース", "…これから物語 少年たちのブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "一世を風靡したアイドル・光GENJIが、二手に分かれて主演した２本の青春映画の“光”主演編。単純でノリやすく運動神経抜群の矢沢亮介と、父の跡を継いで高校生でありながらすでにビジネスの世界に足を踏み入れている藤岡茂。茂は自分のプロジェクトを成功させるための“商品”として亮介に近づいていく。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TREES LOUNGE", "トゥリーズ・ラウンジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "インディーズ映画の“顔役”であり、メジャー作品の脇役としても印象を残す個性派俳優S・ブシェーミが、少年時代を過ごしたロング・アイランドで撮り上げた監督デビュー作。仕事をクビになり、行きつけのバーで飲んだくれるウダツのあがらない日々を送る中年男トミー。急死した叔父の後を継いでアイスクリームの移動販売を始めた彼は、自分に好意を寄せてくれる知人の娘デビーとつい関係を持ってしまい……。ブシェーミと交流を持つインディーズ系の役者たちが多数出演。写真家ラリー・クラークの監督作「KIDS」でデビューしたC・セヴィニーが、主人公をトキめかせる少女に扮してみずみずしい魅力をふりまいている。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アジア秘密警察", "アジア秘密警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "“日活ダイヤモンド・ライン”の一人として、“日活ダンプガイ”の異名をとった二谷英明。これは彼が、そのラインから外れたあとの、数少ない主演作。二谷扮するアジア秘密警察員が、大量の金塊投入によって日本経済の混乱を計る悪漢と戦う。悪役を演じるのは宍戸錠。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPERSNOOPER", "レッドオメガ追撃作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "マイアミ警察のデイブ巡査は、プルトニウムから放射されたオメガ光線を全身に浴びてしまい、てっきり死んだと思ったのだが……。放射能を浴びて超人になった男が巻き起こす、アクション・コメディで、「スーパーマン」のパロディー的映画。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キタキツネ物語", "キタキツネ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "オホーツク海に臨む冬の北海道。流氷を渡ってやってきたキタキツネのフレップは、オスキツネからメスキツネのレイラを実力で奪い、妻とする。春になり、レイラは５匹の子供を産むが、梅雨の季節に罠にかかって息をひきとった。フレップは子供たちを一人前にするために巣から追いやる。子ギツネたちは密猟者に追いつめられて死んでしまうが、ただ１匹生き残ったシリカは父に再会するのだった……。雄大で美しい北海道の大自然をバックに、キタキツネ一家の成長を根気よく追った感動のドキュメンタリー。人間がほとんど登場しないにもかかわらず、ファミリー客の心を捉えて大ヒットした。「南極物語」「子猫物語」などの動物映画の祖。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hot Summer Days 全城熱恋", "ホット・サマー・デイズ", "", "", "http://ecx.images-amazon.com/images/I/51vHUNEnunL._SL210_.jpg", 0, "", "2010-01-01", "6,1,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蝴蝶飛", "僕は君のために蝶になる", "", "", "http://image.eiga.k-img.com/images/movie/53821/315x210.jpg?2938", 88, "台湾のアイドルグループF4のビック・チョウ、「ドラゴン・キングダム」のリー・ビンビン主演のラブ・スト−リー。監督は「マッスルモンク」「エレクション」のジョニー・トー。大学の人気者でミスキャンパスの恋人もいるアトンに密かに想いを寄せていたエンジャ。思いが通じて2人は次第に惹かれ合い結ばれるが、ある日喧嘩別れしたままアトンは交通事故で帰らぬ人となってしまう。事故から3年。立ち直れないエンジャの夢に毎晩アトンが現れるようになり……。", "2008-10-25", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paycheck", "ペイチェック　消された記憶", "", "", "http://image.eiga.k-img.com/images/movie/27501/315x210.jpg?3022", 118, "「ブレードランナー」「マイノリティ・リポート」などの原作で知られるフィリップ・K・ディックによるミステリアスな短編小説を材に、ジョン・ウー監督ならではのアクションを取り入れた近未来SFサスペンス。多額の報酬と引き替えに3年間の記憶を消された男が、陰謀に巻き込まれたことを知り真実と失われた記憶を取り戻すため奔走する。主演にベン・アフレック、「キル・ビル」のユマ・サーマンを迎え、SFとミステリーの融合という新たな新境地に、アクション映画の巨匠ジョン・ウー監督が挑む。", "2004-03-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CORPS ET BIENS", "肉体と財産", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ハンサムなジゴロのミシェルは、殺人事件を犯す。彼は莫大な遺産を相続した若い娘、アリアーヌと財産目当てで結婚するが、アリアーヌの腹違いの姉エレーヌの肉体に惹かれる。一方、親友をミシェルに殺された老婦人、マダム・クランツは犯人捜しを始めるが……。リゾート地で起きた殺人事件の行方を、非常に洗練されたスタイルで描いたフィルム・ノワール。晩夏の大西洋岸から冬のパリを捉えるベルタのシャープな映像が素晴らしい。D・サンダの存在感が圧倒的。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARRY ＆ SON", "ポール・ニューマンのハリー＆サン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "P・ニューマンが製作、監督、脚本、主演をこなした意欲作。定職に就かず、バイトをしながら作家を志している息子と、建設現場で働くやもめの父親。二人が親子間にできてしまった溝を埋めるまでを描いた物語。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THAT　DARN　CAT", "誘拐騒動　ニャンタッチャブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1967年製作の「シャム猫FBI／ニャンタッチャブル」をリメイクした日本未公開作。「バッファロー1966」のクリスティーナ・リッチ主演によるオフビートなコメディで、「スペシャリスト」「暗殺者」にも出演している猫エリザベスの演技も見もの。資産家夫妻の邸宅に押し入った強盗二人組が、メイドを間違えて誘拐し、身代金を要求する。猫のD.C.はたまたま夜の散歩途中にある倉庫に迷い込み、そこで椅子に縛られたメイドを目撃。それを知った飼い主の高校生パティとともに、警察に訴え出るが……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEVER LOVE A STRANGER", "ニューヨークの顔役", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ニューヨークのギャングの社会を舞台に、思うにままならない人間模様と葛藤を描いた作品。孤児院で育ち、ギャングのボス、フェネリに見出された青年フランキーはジュリーと恋に落ちるが、彼女はフェネリに囲われる。フランキーが助けたことをきっかけに友情を育んだユダヤ人青年マーティは、やがて検事となって心ならずもギャングとなった彼を追い詰めるのだった。正義感あふれる検事マーティを演じるのは、若き日のS・マックィーン。「傷だらけの栄光」のチンピラ役で認められた彼が初めてもらった大役だ。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAFE FLESH", "愛の終焉 カフェ・フレッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1983年に米国の全アートシアターで上映され、記録的なヒットとなったカルト・ムービー。第三次世界大戦後、人類は核の影響により99％がセックス陰性者と化し、残りの１％は政府の強制でセックス・パフォーマンスを演じて見せていた……。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paranoid Park", "パラノイドパーク", "", "", "http://image.eiga.k-img.com/images/movie/53411/315x210.jpg?2359", 85, "オレゴン州ポートランド。スケボーに夢中の16歳の少年アレックスは、不良スケーターが集まるパラノイドパークへ行くようになる。だが、そこで、思わぬ偶然から誤って1人の男性を死なせてしまう。アレックスはその後も何事もなかったかのように日常を過ごすが……。「マイ・プライベート・アイダホ」「エレファント」の鬼才ガス・バン・サント監督が1人の少年の罪の意識と葛藤を描いた人間ドラマ。07年カンヌ国際映画祭では、60周年記念特別賞を受賞した。", "2008-04-12", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("kocorono", "kocorono", "", "", "http://image.eiga.k-img.com/images/movie/55901/315x210.jpg?2466", 116, "1987年の結成以来23年間にわたって独自のスタイルを貫き通し、日本国内のみならず海外からも人気を集めてきたロックバンド「bloodthirsty butchers」を追ったドキュメンタリー。長編デビュー作「77BOADRUM」で注目された川口潤監督がメガホンをとり、極寒の北海道から猛暑の台北までバンドに完全密着。キャリアの長さゆえに生まれた複雑な人間関係など、孤高のバンドの知られざる真実を浮き彫りにする。", "2011-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES EXPLOITS D’UN JEUNE DON JUAN", "蒼い衝動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "20世紀初頭のフランスの田園地帯。男たちはみな出征し、一人屋敷に残された16歳の少年ロジェは、初体験はもちろんのこと６人の女性と次々にアバンチュールを重ねていく。アポリネールの小説『若きドン・ジョアンの冒険』をもとに、イタリアの監督ミンゴッツィがフランスで撮った作品で、大らかな笑いがいっぱいのエロティックな一編。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLAST FROM THE PAST", "タイムトラベラー　きのうから来た恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "キューバ危機が迫る1962年、科学者のウェバー夫妻が、飛行機の墜落を原子爆弾とカン違いして地下の核シェルターにこもってしまう。シェルターで生まれた息子のアダムはすくすく成長し、35年後、初めての地上への冒険に旅立っていく。ポルノショップなどを必要以上に警戒する一家の振る舞いが何ともおかしいコメディ。時代遅れのファッションを着こなして初恋に奮闘する主人公をB・フレイザーが底抜けの明るさで演じ、その父親役C・ウォーケンの絶妙なピンボケぶりも失笑を誘う。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("撃たれる前に撃て！", "撃たれる前に撃て！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "通称コブラとして恐れられる、すご腕の刑事小村。銃砲店人質事件の犯人が何者かに殺されたのをきっかけに、彼は暴力団と政財界を結ぶ巨悪に挑み、汚職をぶっつぶしていく。「恐れ毒蛇・目撃者を消せ！」（1974）に続く田宮二郎主演の刑事もの。すさまじい執念と行動力をもった、タフな刑事像が描かれる。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鶴八鶴次郎", "鶴八鶴次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "第１回直木賞を受賞した川口松太郎の同名小説を映画化した成瀬巳喜男監督の名作。同年「藤十郎の恋」で再起した長谷川一夫と、溝口健二監督の「浪華悲歌」「祇園の姉妹」(1936）で女優開眼した山田五十鈴が主演している。物語は明治から大正初期にかけて人気の絶頂にあった新内の名コンビ・鶴八鶴次郎の愛情と芸道との葛藤を描く。芸の話となると互いに一歩も退かない二人の、想いを胸に秘めながらの喧嘩沙汰がもどかしく、芸人の厳しさを知った鶴次郎が、自らを犠牲にしても鶴八を幸福な家庭生活に収まるように仕向けるラストが切ない。風景のショットの連鎖などにサイレントの名残りを見せる演出が印象的である。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マドンナのごとく", "マドンナのごとく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "直木賞作家・藤堂志津子の原作を映画化したラブ・ストーリー。キャリアウーマンの優子は年下のエリート自衛官、唐沢と恋仲になる。しかし、愛に溺れるあまり訓練でミスを連発した唐沢は、研修への参加を申し出て優子のもとを去る。遊びのつもりだったものの深く傷ついた優子は、唐沢の親友である藤堂に慰められ、新たな恋にのめり込むが……。現代的な愛の三角関係をスタイリッシュに描く。自衛隊の協力によって収められた、リアルな訓練シーンも見どころ。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRIMINAL LAW", "クリミナル・ロウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "純朴な好青年が、レイプ殺人の犯人として逮捕される。敏腕な弁護士は自らの手腕を発揮して裁判で無罪を勝ち取るが、同種の事件がまたもや発生、一見純朴なその青年は、ゆがんだ心に支配された凶暴な殺人者だったのだ……。たとえ真犯人であろうと、その弁舌と法廷技術で無罪を勝ち取ってしまう弁護士のモラルの問題を鋭く突いた社会派の問題作。ビデオ発売時タイトルは「灰色の容疑者」。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("上意討ち—拝領妻始末—", "上意討ち—拝領妻始末—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "滝口康彦の原作をもとに橋本忍が脚色。初めて三船敏郎と組んだ小林正樹監督が、「切腹」に続き、封建社会の非人間的な矛盾を突いた力作である。江戸時代の武士にとって、上からの命令＝上意は絶対であった。藩主の命令により、その側妾を子息の妻として与えられた一領主。彼は誠実にその役目を果たし、息子ともどもその側妾を受け入れ、次第に愛するようにさえなる。が突如、世継ぎ問題のこじれから一転、側妾を返上せよとの上意が下る。あまりに理不尽な藩主のやり方に怒った領主は、お家断絶を覚悟の上で、“家族”を守るため、ついに反乱を起こす。壮絶な戦いの末、領主一族は皆討ち果てていくのだった……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふんどし医者", "ふんどし医者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "長崎で和蘭陀医学を修めた主人公は徳川家御典医の考査のために江戸に上る途中、大井川の川止めに遭ったが、そこで病苦にあえぐ貧しい人々に接した。主人公は江戸へ上るのを止め、この町に留まることを決意する。日本が近代の夜明けを迎えた時代を背景に、医学をめぐって繰り広げられる人間の葛藤劇。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPENCER’S　MOUNTAIN", "スペンサーの山", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "雄大な山々がそびえるワイオミングの大自然をバックに、スペンサー一家の心温まる家庭を描くヒューマン・ドラマの佳作。フォンダ、オハラ、D・クリスプなどのベテランの好演と当時、新人だったM・ファーマーのフレッシュな演技が見どころ。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("memo メモ", "memo メモ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "頭に浮かんだ不快感を打ち消すために同じ行動を繰り返してしまう心の病気“強迫性障害”を題材にした人間ドラマ。優しい両親と3人で暮らす女子高生の繭子は、メモを取らずにはいられないという強迫性障害を持っている。ある日彼女の家に、長い間音信不通だった父の弟・純平が現われる。実は純平も強迫性障害を持っており……。個性派俳優・佐藤二朗が自らの強迫性障害の体験を基に脚本を書き上げ、初メガホンを取った。", "2008-03-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENTROPY", "ウィズアウト・ユー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ミュージックビデオでの実績が認められ、映画の初監督に挑むことになったジェイク。プロデューサーにあれこれ注文をつけられ、難航する撮影に頭を痛める彼だったが、モデルのステラと恋に落ちたことが彼の心の支えとなっていた。そんな折り、彼女の妊娠が発覚。心の準備ができていなかったジェイクは動揺する……。「U2／魂の叫び」で注目されたP・ジョアノー監督が、自身の体験に基づいて綴ったラブ・ストーリー。U2が1997年に行った“ポップマート・ツアー”の模様を織り込みながら、ロマンティックなドラマを展開させる。ヴォーカリストのボノは主人公の友人役としても出演。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さまよえる脳髄", "さまよえる脳髄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "逢坂剛の同名小説を映画化した、サイコ・サスペンス。主人公は、美貌の精神科医・南川藍子。彼女は、女性に暴行を加えた追分という男の精神鑑定を依頼される。一方で、殺害後にマブタを切り取るという、女性専門の連続殺人事件が続発。藍子の恋人で、大脳に障害のある刑事、海藤が事件を捜査し始める。そして、藍子の周りでは、追分と海藤、殺人事件が、奇妙にかかわりを持ち出して……。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スペースポリス", "スペースポリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "奇想天外なストーリーの中にSF、アクション、コメディ、ロマンスなどの要素をごちゃ混ぜにした痛快作。東京の高円寺に飛来した人喰いエイリアンを倒すべく、宇宙からやって来た“スペースポリス”の大暴れを描く。10年ぶりの映画出演の目黒祐樹が外見からしてウサン臭い“スペースポリス”をパワフルに演じ、若手注目俳優の忍成修吾が彼と友情をはぐくむ気弱な青年に扮している。フリーターの耕一は、カツアゲされていたところを謎の男に助けられる。自称“軍曹”と名乗っている風変わりなその男が気になって仕方のない耕一は、古雑誌を拾って公園で売るという男の仕事を手伝うことにするのだが……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いのちの海・Closed World", "いのちの海・Closed World", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "帚木蓬生の『閉鎖病棟』を、ドキュメンタリー畑の福原進が映画化。不登校の島崎由紀は有明海に面した精神病院・かささぎ医院に通院するようになる。そこで彼女は様々な人と巡り会い、暗い過去から立ち直ろうとするのだが……。病院の中で起こる心のふれあい、患者たちの一喜一憂、そして悲しい強かん事件を静謐なタッチで綴っていく。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沈黙を破る", "沈黙を破る", "", "", "http://image.eiga.k-img.com/images/movie/54561/315x210.jpg?3996", 130, "イスラエル軍によるヨルダン川西岸への侵攻作戦の中で起こった、難民キャンプへの侵攻を記録したドキュメンタリー。カメラは2 週間にも及ぶイスラエル軍の包囲、破壊、殺戮にさらされるパレスチナの人々の生活を追う。同じ頃、元イスラエル将兵の青年たちが“沈黙を破る”という名の写真展を開き、自らの加害行為を告白する。監督はジャーナリストの土井邦敏。", "2009-05-02", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MEN", "男たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "1950年代を代表するハリウッドスター、ブランドのデビュー作として注目を浴びた作品。第二次大戦で負傷して、下半身不随となり車椅子の生活を余儀なくされた男を描くドラマ。ジンネマンのまじめな演出にこたえて骨っぽい演技を見せたブランドは一躍スターに。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POWAQQATSI", "ポワカッツィ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「コヤニスカッティ」に続く、G・レジオとP・グラスによる詩的ドキュメンタリー“カッツィ”３部作の第２部。先進国の搾取により荒廃の一途をたどる第三世界の光景を描く。失われつつある文明を悲観的に捉えるのではなく、そこに生きる人々にみなぎる生命力を強調。とかく暗くなりがちなテーマを見事に逆転し、希望あふれる一編となっている。タイトルは、ホピ族の言葉で“自己の繁栄のために他人の生命力を食い物にする生き方”の意。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RANGER 陸上自衛隊　幹部レンジャー訓練の91日", "RANGER 陸上自衛隊　幹部レンジャー訓練の91日", "", "", "http://image.eiga.k-img.com/images/movie/57191/315x210.jpg?2754", 100, "自衛隊の訓練の中でも最も過酷といわれる、陸上自衛隊の幹部レンジャー訓練に密着したドキュメンタリー。基礎的な技術・知識の修得から、戦闘状況を想定した戦場さながらの訓練にいたるまで、全国の駐屯地から陸上自衛隊富士学校に集まった27人の訓練学生たちの91日間に及ぶ苦闘の日々を克明に記録した。監督はテレビドラマ「MM9」や「長髪大怪獣ゲハラ」を手がけてきた田口清隆。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hottest State", "痛いほどきみが好きなのに", "", "", "http://image.eiga.k-img.com/images/movie/53441/315x210.jpg?2401", 117, "「ビフォア・サンセット」のイーサン・ホークが、自伝的小説を自らのメガホンで映画化したラブ・ストーリー。ニューヨークで暮らす20歳の俳優ウィリアムは、シンガーソングライターを目指すサラと恋に落ちる。結婚の約束を交わすほどに愛し合う2人だったが、やがてサラはウィリアムとの間に距離を置きはじめる。主演は「ブロークン・フラワーズ」のマーク・ウェバーと「そして、ひと粒のひかり」のカタリーナ・サンディノ・モレノ。", "2008-05-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Black Widow", "ブラック・ウィドー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "４人の中年資産家が、結婚後、連続して病死した。これに疑問を抱いた司法局の女性職員アレックスは、単独で調査を開始。やがて彼女は、４人の男の妻が同一人物であることをつかむのだが……。４人の女性を演じ分けるT・ラッセルの悪女ぶりが見もの。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOHNNY BELINDA", "ジョニー・ベリンダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "舞台劇を原作にした人間ドラマ映画。主演のJ・ワイマンは難役の聾唖（ろうあ）者を演じ、ゴールデン・グローブ賞に続きアカデミー主演女優賞を獲得、この年のハリウッドを代表するスターとなった。カナダ領の島に医者として来たロバートは、聾唖の女性と出会いいろいろ面倒を見る。医者から手話を教えてもらった彼女は次第に元気を取り戻していくがある夜、男に暴行され妊娠してしまう……。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和ひとけた社長対ふたけた社員　／月月火水木金金", "昭和ひとけた社長対ふたけた社員　／月月火水木金金", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "シリーズ最終作。洋々産業社長・奈良田真介は新製品ベッドの開発にはりきっている。やがて精密スプリングと西陣の高級生地をいかした豪華なベッドができ上がる。一方、死に別れた妻にそっくりなクラブのママも登場して、社長の心は乱れるのであった。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BENJI", "ベンジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "空前のヒットをとなった“ベンジー”シリーズの記念すべき第１作。幼い兄妹に拾われた野良犬ベンジー。ところが二人は悪人たちに誘拐されてしまう。そこで賢いベンジーは、なんとか二人を救い出そうと孤軍奮闘する。サスペンス＆ハートウォーミングと、ファミリー映画のツボをおさえた一編。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("精霊のささやき", "精霊のささやき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "雪に閉ざされた森の奥にたたずむ洋館・ミモザ館。ここには都会に適合できず、心を病んだ人々が静かに暮らしている。この洋館にある日、みほという少女が現れ、彼らの淋しい心をときほぐしていく。自主映画出身の植岡嘉晴が描く幻想的な一編。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　MONDE　DE　MARTY", "約束／ラ・プロミッセ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "入院中の少年と老人の交流を綴った、心温まるドラマ。小児ガンを宣告された10歳のマーティ。彼はある日寝たきりで動くことも喋ることもできない老人べランの病室に潜り込む。アルツハイマー型痴呆症だが意識がはっきりしているベランは、病室でやりたい放題のマーティに頭を悩ませる。だがいつしか彼の訪問を楽しみにするようになっていた。難病ものにありがちな悲しいムードは皆無。コミカルな描写の連続で笑わせつつ、少年と老人の友情にしみじみさせられる。一言も喋らず表情もほとんど変えずに老人の気持ちを表現する、名優ミシェル・セローの存在感が圧倒的。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LITTELE PRINCE", "星の王子さま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "S・テグジュペリ原作の有名な『星の王子さま』をミュージカル仕立てに映画化した作品。子供の頃の空想力を持ち続けて大人になったパイロットは、ある日不時着した砂漠で、宇宙のどこかの小さい星からやって来たという王子さまに会う。S・ドーネンの演出、個性的なキャスティング、幻想的なセットなどで原作の味を損なわぬ出来となった。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太王四神記", "新編集版　太王四神記　第1章：千年の愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "日本でも話題を呼んだペ・ヨンジュン主演のテレビドラマ「太王四神記」を、新たに編集した4部作の第1章。自分たちをチュシンの国の末裔だと信じる高句麗の人々は、2000年もの間、約束の王の登場を待ち望んでいた。そしてチュシンの王の誕生を告げる星が輝く夜、高句麗王ソスリムの弟の家にタムドクが、妹の家にホゲが産声をあげる。そんな彼らと運命で結ばれた少女キハとスジニ。やがて4人は、王位をめぐる争いに巻きこまれていく。", "2010-04-10", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Living Daylights", "007／リビング・デイライツ", "", "", "http://ecx.images-amazon.com/images/I/51D7fTpxvoL._SL210_.jpg", 132, "ソ連の高官コスコフ将軍が西側に亡命を希望したため、ボンドはチェコに飛んだ。美人の狙撃者に狙われはしたが、亡命は成功した。コスコフから、KGBは西側のスパイ暗殺計画を進めており、リーダーはプシーキンという情報を得るが、そのコスコフはKGBに奪われてしまう。Mはボンドに、プシーキンの暗殺を命令する。コスコフの亡命に疑いを持ったボンドは、まず美人狙撃者を洗う。やがて複雑に入り乱れた事件は、意外な方向に。スクリーン登場25周年、4代目のボンド役ティモシー・ダルトンが初登場。シリーズの原点に戻り、空中アクションが最大の見せ場だ。", "1987-12-12", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ICEMAN", "アイスマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "北極で氷づけになった４万年前のネアンデルタール人が科学者の手によって蘇生した。解剖に反対し、彼を観察しようとする人類考古学者シェパード。二人の間に芽生えた“時”を超えた友情と“人間とは何か？”を追求するドラマが、大自然の中で感動的に描かれる。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みなまた日記 甦える魂を訪ねて", "みなまた日記 甦える魂を訪ねて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "水俣病にまつわるドキュメンタリー作品を撮り続けてきた土本典昭監督が、1990年代半ばに１年間にわたり水俣病による死者の遺影を集めていたが、その時に撮りためていたフィルムを編集し、まとめた作品。生き続ける人々の息づかい、美しい風景の数々……。その映像の向こう側に、人間の生と自然の見事な調和と再生が見えてくる。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EREDITA FERRAMONTI", "沈黙の官能", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ドミニク・サンダがカンヌ映画祭主演女優賞を受賞した官能ロマン大作。19世紀末期のローマ、野望に燃える魔性の女が己の肉体を用いて富豪の財産を独り占めしようとする姿を描いていく。名匠M・ボロニーニ監督の鮮烈なタッチが見どころ。E・モリコーネの音楽も美しい。", "1976-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("炎の城", "炎の城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "瀬戸内海に臨む王見城に、明国へ留学に出ていた若君・王見正人が帰ってきた。だが、正人の不在中に父・勝正は叔父・師景に暗殺され、王見城は師景に支配されていた。真相を知った正人は狂気を装って帰城し、父の仇を討つ機会をうかがう……。名匠・加藤泰が『ハムレット』を豪快に翻案した佳作。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LABYRINTH", "ラビリンス　魔王の迷宮", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "魔王ジャレスに連れ去られた弟を取り戻そうとする少女サラの冒険の旅を描いたファンタジー。「オズの魔法使」を思わせる話だが、「ダーク・クリスタル」などでも知られるJ・ヘンソンの手による様々なマペットのキャラクターやジャレスの城のデザインなどが楽しめる。映画のために５曲を書き下ろしたD・ボウイの熱唱にも注目。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DA　UOMO　A　UOMO／DEATH　RIDES　A　HORSE", "新・夕陽のガンマン／復讐の旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "15年前に正体不明の盗賊団に家族を残されて以来、復讐心を燃やし続けているビル。一味のアジトであるニューメキシコの寒村に乗り込もうとしている彼の前に、ライアンという謎のガンマンが現れる……。マカロニ・ウエスタンの大物俳優L・V・クリーフが意外な素性を隠し持つライアンに扮し、さすがの存在感を漂わせている。", "1967-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガイスターズ／光の章・命の章", "ガイスターズ／光の章・命の章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "2Dと3D映像を融合させた映像が話題の人気TVアニメ『ガイスターズ』の選りすぐりシーンを、劇場版として再編集。西暦2507年。2099年の巨大彗星地球衝突により地下都市と宇宙ステーションで生活していた人類が、ようやく地表で生活できるようになった。だが環境の激変により、危険なクリーチャー“シリコニアン”が出現、人類を守るために、５人の若者で構成する特殊部隊“ガイスターズ”が創設され活動を続けていた。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南極料理人", "南極料理人", "", "", "http://image.eiga.k-img.com/images/movie/54331/315x210.jpg?3671", 125, "南極観測隊員の西村淳のエッセイ「面白南極料理人」を、「クライマーズ・ハイ」の堺雅人を主演に映画化。極寒の南極ドームふじ基地にやってきた8人の観測隊員の1人、西村の仕事は隊員の毎日の食事を作ること。約1年半、遠く離れた日本に家族を残し、隊員たちは悪戦苦闘しながらも次第に絆を深めていく。共演に生瀬勝久、きたろう、高良健吾ら個性派が揃う。", "2009-08-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花と蛇3", "花と蛇3", "", "", "http://image.eiga.k-img.com/images/movie/55531/315x210.jpg?2317", 107, "団鬼六による官能小説シリーズの映画化で、谷ナオミ主演「花と蛇」（1974）から連なる8作目の劇場版は、グラビアアイドルからストリッパーに転進した小向美奈子が主演を務める。才色兼備の新進チェリスト・静子は、関西財閥の会長・海東義一郎と結婚し、幸せな生活を送っていた。しかし、卑劣な野心家・遠山隆義が海東を破産に追い込み、借金の抵当として静子を自らの妻にする。遠山の屋敷に迎えられた静子は、そこで綿密に仕組まれた官能のワナにはまっていく……。", "2010-08-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("時が乱吹く", "時が乱吹く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "1969年の処女作「無人列島」で注目された自主映画作家・金井勝久々の長編映画。本作は金井監督が1987年から作り出した３本の短編をひとまとめにしたオムニバス形式で、それぞれ“歌、句、詩”をテーマに、幻想的な映像を見せる異色作。なかでも映画製作をおでん作りになぞったオープニングのアイデアは秀抜。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GAMBIT", "泥棒貴族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "中東の石油王が大切にしている黄金の仏像を盗み出すため、３人の男女が手を組んだ。ところがこの石油王というのが用心深い人物で、まんまと盗み終えたと思った瞬間、警報機が作動し、仲間の一人が彼に捕まってしまう……。R・ニームが手堅い演出を見せる。ラストのどんでん返しも見事なサスペンス・アクション。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUNS FOR SAN SEBASTIAN", "サン・セバスチャンの攻防", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "スペイン支配下のメキシコ。スペインに対し抵抗運動を続けるアラストレーは、重傷を負い教会へと逃げ込んで来た。神父のジョゼフは彼を看病するが、軍隊がアラストレーの引き渡しを要求してくる。二人は軍を出し抜き、砂漠を越えてサン・セバスチャンへと向かう……。H・ヴェルヌイユによるアクション・ドラマの秀作。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Saw 3D", "ソウ　ザ・ファイナル 3D", "", "", "http://image.eiga.k-img.com/images/movie/55581/315x210.jpg?2250", 90, "世界的ヒットを記録した人気ホラーシリーズの第7弾にして完結編が、シリーズ初となる3D映像で登場。前作に続きケビン・グルタートが監督を務める。ジグソウのゲームの生存者たちは、同じくゲームから生還した“精神的指導者”ボビー・デイガンの元に救いを求めて集まる。だが、ボビーの暗い過去が明らかになるにつれ、新たな恐怖が彼らを襲う。一方ホフマンとジルは、ジグソウの遺品をめぐって争いを繰り広げており……。", "2010-10-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kiss me,Stupid", "ねえ！キスしてよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "　嫉妬深い夫が、プレイボーイのディノに妻を誘惑されることを恐れ嘘をつく。しかし逆に妻から浮気の疑いをかけられ、家出されてしまう。イタリアの戯曲をもとにアメリカ版に脚色された。ディノ役は、スケジュールの都合でジャック・レモンからピーター・セラーズに変更されるが、クランクイン1週間後にセラーズが心臓発作を起こす。最終的に役を射止めたのは、「大空港」のディーン・マーティン。ジャック・レモン夫人であるフェリシア・ファーが妻役を演じている。", "1965-04-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少年", "少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "実際に起きたの事件にヒントを得た大島渚の名作のひとつ。戦争で人生を棒に振ったと思い込んでいる酔いどれの父親と、気の強い後妻、少年と幼い弟の4人は当たり屋で生活費を稼ぎながら日本を縦断する。この犯罪に呵責の念を感じるが、少年にはなにもいえない。やがて一家は逮捕されるが……。日本を騒がせた当たり屋家族の顛末を叙情性あふれるロードムービーとして描いた作品でベネチア映画祭でも高く評価された。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCOOBY−DOO　２：MONSTERS　UNLEASHED", "スクービー・ドゥー2／　モンスターパニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "全米で絶大な支持を得たTVアニメを実写映画化したシリーズ第２作。前作でお化け騒動を無事に解決したミステリー社の面々は、その後も次々と奇怪事件を解決に導き、すっかり人気者に。ところが調子に乗った臆病犬スクービーとシャギーが、またもドジをやらかしたことから、とんだ騒動が持ち上がる。次々と登場するユニークな怪物キャラクターたちにも注目だ。怪物がらみの難事件を専門とするミステリー社の功績をたたえる博物館が完成。ところが、お披露目パーティーに謎の怪物が現れて大暴れ。ガタ落ちになった評判を回復するため、ミステリー社は犯人逮捕に乗り出すが……。", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("早春", "早春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "小津作品としては、シリアスな感じの強い人間ドラマ。倦怠期の夫婦を描いた作品では、「お茶漬の味」などがあったが、それらは軽妙なユーモアを感じさせ、ほのぼのとしていた。ここでの夫婦の危機的状況はもっと深刻である。蒲田界隈に住む30歳の正二と昌子の夫婦の暮らしと、正二とサラリーマン仲間との交遊が並行して描かれる。夫の出征中に子供を病死させて以来、二人の仲は気まずく夫は無気力だ。そこへ夫の同僚の若い娘が関係してきて、夫婦間の不和が決定的となる。そんな状況から脱してヨリを戻し再出発を誓うシーンで映画は終わる。珍しく戦後派新世代の生活感を大胆に取り入れ、仕事仲間とのハイキングの小さな出来事がのっぴきならない不倫へと発展していくプロセスを、きめ細かく演出する。小津映画の珍しいキス・シーンに注目。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花と嵐とギャング", "花と嵐とギャング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "石井輝男監督が、倒産寸前の新東宝を出て、東映入社第１回作品として撮った軽快なギャング映画。文字通り役不足でくすぶっていた高倉健を主役に据え、大スターへの道を開いた記念碑的作品でもある。一族郎党がみな犯罪者という生まれついての前科者、通称・スマイリー健が、ずる賢いヤクザの仕組んだ銀行強盗に乗ったのが運の尽き。襲撃は成功したものの、撃たれる奴、裏切る奴、金を横取りする奴が入り乱れ、あげく女房まで誘拐される大争奪戦へと発展する。脚本のクレジットは佐治乾となっているが、撮影中ほぼ全編を石井監督が改変したという。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STELLA DALLAS", "ステラ・ダラス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "1990年に亡くなった、ハリウッドを代表する女優B・スタンウィックが、その才能を開花させた出世作。ベット・ミドラー主演で、リメイク版「ステラ」も作られた。育ちの悪い母親が、娘のためと思っての言動が、すべて裏目に出て娘を困らせる。娘のために、自分を犠牲にする母親の愛情を描く。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AFTERMATH", "エア・パニック1501", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "カンザスシティーへと向かう1501便に、爆破予告が入る。折り悪く天候も急変。ベテランのパイロット、グレッグは緊急着陸を試みるが機体は墜落、炎上する。最悪の事故の原因は、爆破か、操縦ミスか？　パイロットの妻が夫の汚名を晴らすため真相究明を始めるが……。TVフィーチャー作品。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死の十字路", "死の十字路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "江戸川乱歩の原作を井上梅次監督が映画化したミステリー映画。主人公の社長は愛人と密会中に突然現れた妻を発作的に殺してしまい、死体を捨てようとするが、十字路で停車中に別の死体が車に転がり込む。二転三転するストーリーを井上梅次が鮮やかに処理した佳作。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("センコロール", "センコロール", "", "", "http://image.eiga.k-img.com/images/movie/54761/315x210.jpg?2542", 30, "平凡な町に突如巨大なモンスターが出現し、自衛隊が出動する騒ぎが起こる。町中が騒然とする中、ユキは同じ学校に通うテツがそのモンスターと良く似た奇妙な生物・センコをペットのように飼っていたことを思い出す。ユキはテツとセンコの関係に興味を持つが、そこへ巨大モンスターを操る謎の少年が現れ、いきなり戦いが勃発してしまう。映像クリエイターの宇木敦哉が、たった一人で全ての作画を担当したアニメーション。", "2009-08-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bible", "天地創造", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 174, "　旧約聖書のエピソードを映画化したスペクタクル巨編。天地創造、禁断の木の実を口にしたアダムとイブ、ノアの方舟の建造、バベルの塔、ソドムとゴモラの都の滅亡、といった有名なエピソードが豪華スターの顔合わせで描かれる。ノアの大洪水のシーンは圧巻。", "1966-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラVSスペースゴジラ", "ゴジラVSスペースゴジラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "メカゴジラでも倒せなかったゴジラに対して、G対策センターは、対ゴジラ兵器・モゲラによる“Mプロジェクト”と、テレパシーでゴジラを制御しようという“Tプロジェクト”を進めていた。Gフォースの新城功二と佐藤清志は、プロジェクト遂行のため、南太平洋のバース島に送られる。二人を迎えたのは、島の近くに潜んでいるゴジラ打倒に執念を燃やす結城晃。島には、ベビーゴジラの成長したリトルゴジラもいた。ある日、G対策センターのモニターに、宇宙で生まれた凶暴な戦闘動物スペースゴジラの姿が映る。地球存亡を懸け、最終戦争が始まった……。本作では、シリーズ史上初めて戦いの場を宇宙空間にまで広げ、よりスケールアップした超三次元バトルをみせる。", "1994-12-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Inkheart", "インクハート　魔法の声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪女の季節", "悪女の季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "皮肉で痛烈な洗練された喜劇を得意とする渋谷実のコメディ・タッチのサスペンス映画。悪事でしこたま金をためこんだ老実業家と相棒の悪女、金目当てで彼の世話をする元芸者とその娘など、さまざまな物欲につかれた輩の人間模様が展開。ブラックな笑いが未消化ながら，鮮烈な印象を残す仕上がり。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RIZE", "RIZE〈ライズ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "最先端のヒップホップ・ダンスとして注目される“クランプ”の誕生に迫ったドキュメンタリー。L.A.の危険地域サウスセントラルの若者の間で、まともに生きるための手段として始まった、このダンスの精神を浮き彫りにする。スピーディな超人的ボディ・アクションに目を奪われること必至。巡回ピエロの仕事をしている男が始めたクラウン・ダンス。この踊りは若者たちの間に広まり、やがてクランプ・ダンスへと派生し……。", "2006-01-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("長江", "長江", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "1979年に大ヒット曲『関白宣言』を放ち、翌年に中国でコンサートを開いたシンガー・ソングライターさだまさしが手掛けたドキュメンタリー。中国の大河、長江流域に暮らす人々の生活を、雄大な大自然の映像とともに映し出す。さだ自身が監督・製作・音楽・主演を務めて、２年間の長期中国ロケを行った入魂の一作。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IMPULSE", "インパルス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "T・ラッセルがセクシーな女刑事に扮するポリス・アクション。かつてクリント・イーストウッドのパートナーとして名を高めた女優S・ロックの第２回監督作品。女刑事ロティは地方検事スタンとともに麻薬一掃のため、おとり捜査を行う。犯人一味を逮捕した直後、ロティは100万ドルもの大金が絡む事件に巻き込まれ……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イヌ", "イヌ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "自主映画作家・北田直俊が、虐待された老犬の写真から受けた衝撃を、そのまま映画に刻みつけたという異色のドラマ。シュールな物語を通して、世の無関心や悪意、偽善を冷徹に見つめる。逃亡中の凶悪犯が助けた一匹の野良犬。数奇な運命に揺さぶられ、その犬は交通事故に遭い死んでしまう。やがて悪魔の導きでカカシに生まれ変わった犬は、ひたすら歩き続け……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("奇緑／WITCH FROM NEPAL", "奇縁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "香港のゴールデン・ハーヴェスト社がチョウ・ユンファを主演に、SFXを駆使して描いたヒロイック・ファンタジー。デザイナーのジョーは、フィアンセとネパールへ旅したばかりに太古から続く神と悪魔との戦いに巻き込まれる……。ビデオ発売時タイトルは「サイキックSFX 魔界戦士」。", "1985-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LETHAL WEAPON3", "リーサル・ウェポン3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "M・ギブソンとD・グローヴァーの息の合ったコンビと、ド派手な銃撃戦や爆発シーンなどを満載した人気シリーズの第３作。今回は「リーサル・ウェポン２　炎の約束」で狂言回しを演じて強烈な印象を残したJ・ペシが再登場し、全米では前２作を上回る大ヒットを記録した。ロス市警のロジャー・マートフ刑事はあと１週間で引退の身。しかし彼は今、警察の武器庫から銃がストリート・ギャングの手に流れていく重大事件を追っていた。この事件を解決しなければおちおち退職などできない。命知らずの相棒リッグス刑事とともに、マートフは最後の大仕事に取りかかる。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hangover", "ハングオーバー！　消えた花ムコと史上最悪の二日酔い", "", "", "http://image.eiga.k-img.com/images/movie/54531/315x210.jpg?4178", 100, "結婚式を間近に控えたダグは、悪友2人と新婦の弟を連れ、独身最後の夜を満喫するためラスベガスへと向かう。翌日、酒やギャンブルでバカ騒ぎをした4人はひどい二日酔いで目覚め、前夜の記憶はすっぽりと抜けていた。さらにホテルの部屋にダグの姿はなく、代わりに1匹の虎と乳児がいた……。第67回ゴールデングローブ賞でコメディ・ミュージカル部門作品賞を受賞。主演はブラッドリー・クーパー、エド・ヘルムズ、ザック・ガリフィアナキス。", "2010-07-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サイドウェイズ", "サイドウェイズ", "", "", "http://image.eiga.k-img.com/images/movie/54221/315x210.jpg?3506", 123, "仕事も私生活も冴えない中年シナリオライターの道雄は、親友の大介の結婚式に出席するためカリフォルニアを訪れる。結婚式前の1週間を使って大介と2人、カリフォルニアのワイナリーを巡る旅に出るが、その途上でかつて思いを寄せていた麻有子と再会し……。オリジナルはアレクサンダー・ペイン監督による第74回アカデミー脚色賞受賞作「サイドウェイ」。「20世紀少年」や「トランスフォーマー」で第2班監督を務めてきた日本出身の米国人チェリン・グラックが監督を務める。", "2009-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("特命女子アナ　並野容子", "特命女子アナ　並野容子", "", "", "http://image.eiga.k-img.com/images/movie/54821/315x210.jpg?4381", 71, "「特命係長　只野仁」の原作者として知られる柳沢きみおのコミック「特命女子アナ　並野容子」を映画化。主演はグラビアアイドルの堀越のり。朝売テレビの冴えないアナウンサー・並野容子は、白川会長の命令で会社のトラブルを解決する「特命女子アナ」という裏の顔を持っていた。ある日、容子は会長宛に送られてきた脅迫メールの調査を命じられる。", "2009-09-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEET TALKER", "ペテン師ハリーの大逆転", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ペテン師ハリーは、留置所から出所の時、ある海岸付近に沈む帆船の伝説を知る。さっそく現場へ向かったハリーは、伝説を村人に信じ込ませ、テーマパークを建設するといって資金を集める。そこでとんずらするつもりが、父親のいない少年とその美しい母に出会ったことで、人生の選択を迫られるのだった……。B・ブラウン演じるペテン師の調子の良さも楽しいが、彼にだまされる素朴でお人好しな村人たちも楽しい痛快コメディ。", "1991-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Opal Dream", "ポビーとディンガン", "", "", "http://image.eiga.k-img.com/images/movie/1601/315x210.jpg?2546", 86, "英国北部を舞台に生活苦からストリッパーになる男性たちを描いたコメディ「フル・モンティ」のピーター・カッタネオ監督が、ベン・ライスの同名小説を映画化。オーストラリアの田舎町で暮らすアシュモルとケリーアンの兄妹。ケリーアンにはポビーとディンガンという空想上の友だちがいたが、彼女はある日、彼らがいなくなってしまったと言って元気をなくしていく。兄アシュモルは空想上の存在と知りながらポビーとディンガンを探すのだが。", "2005-11-26", "11,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛と死をみつめて", "愛と死をみつめて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "原作は当時話題になった実在の恋人同士の書簡集で、これを「越後つついし親不知」などの八木保太郎が脚色し、斎藤武市が監督した青春純愛映画。美しい吉永小百合と実直な浜田光夫の青春コンビの人気や原作の評判もあり、国民的大ヒットとなった。浪人中の誠は入院した病院で道子と知り会う。大学へ入って２年目に再会した時も道子の病気は思わしくなかった。二人は文通を続け、やがて道子も大学へ入るが軟骨肉腫という奇病で再び入院。道子は誠の愛に支えられて難手術を繰り返し、顔の左半分の切除まで行うが、不治の病には勝てず21歳の誕生日、手術なかばでこの世を去った。同名の主題歌も当時の流行歌となった。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・極道の妻たち 覚悟しいや", "新・極道の妻たち 覚悟しいや", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "北大路欣也を相手役に迎えた、岩下志麻の女極道もの第６作。千之崎組組長の妻、野木安積は、３年ぶりに刑務所から出所する。彼女は、夫が大阪の淡野組の言いなりになっているのに失望し、活路を求めて香港へ。そこで、殺し屋・花杜と知り合い、二人は激しく燃えた。だが、花杜は安積の夫を殺害。この殺しを、裏で操っているのが、義妹の千尋だと知った安積は……。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Trois couleurs: Bleu", "トリコロール／青の愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ポーランドの巨匠クシシュトフ・キエシロフスキー監督による、青、白、赤のフランス国旗をモチーフにした三部作「トリコロール」の第1作。自動車事故で最愛の夫と娘を失ったジュリーは、すべてを引き払いパリでの生活を始める。静かな日々を過ごすジュリーは、音楽家であった亡き夫に愛人がいたことを知る。ジュリーを演じるのは、「イングリッシュ・ペイシェント」「ショコラ」のジュリエット・ビノシュ。", "", "2,42,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オーライ", "オーライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "OLであり、主婦でもある関西の映像作家、安田真奈によるさわやかな青春ストーリー。一人が一番楽だと思っているOLの結が、自分に宛てられた差出人不明のハガキを手に入れる。友人と差出人探しのドキュメンタリーを撮ることになった結が体験する奇妙な一日を、安田監督独特のナチュラルな演出と等身大の視点で映し出す。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MUMMY’S SHROUD", "ミイラ怪人の呪い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "紀元前2000年、内乱のために砂漠に逃げて、悲惨な最期を遂げた若いエジプト王の墓を発掘するため、考古学者バジルをはじめとする４人の探険隊はエジプトに向かう。発見した墓には恐ろしい形相の番人がいて発掘を拒むが、発掘を強行した一行にやがて不思議な事件がふりかかる……。ちょっと安っぽいB級ホラー。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Devil's Chair", "悪魔の椅子", "", "", "http://image.eiga.k-img.com/images/movie/54631/315x210.jpg?4100", 91, "怪奇現象を巻き起こす電気椅子にまつわるホラー。廃墟となっている精神病院の建物に侵入し、使い古された電気椅子を見つけたニックとガールフレンドのサミーだったが、冗談半分でその椅子に座ったサミーが突然の奇怪現象により絶命。容疑者として捕らえられたニックは、真実を話すも信じてもらえず、精神病院に送られてしまう。それから4年後、事件の真相を暴こうと、ニックは再び廃墟へと向かうのだが……。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四十八歳の抵抗", "四十八歳の抵抗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "石川達三のベストセラー小説の映画化。仕事一筋の生活に倦怠を感じる中年サラリーマンのいっときの冒険を描き、その題名は流行語となった。翻案にあたった新藤＝吉村の名コンビは、原作の社会批判を十分に生かし、ひねりの効いた切れ味の鋭い作品に仕上げた。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("非州和尚／CRAZY　SAFARI", "コイサンマン／キョンシー、アフリカへ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "アフリカを舞台に、コイサンマンとキョンシーが遭遇して騒動を繰り広げるコメディ。「ブッシュマン」「コイサンマン」のニカウと「霊幻道士」シリーズで道士を演じていたラム・チェンインが共演している。イギリス在住の中国人サムはご先祖様のミイラを入手。故郷の香港で安らかに眠ってもらおうと、道士を呼んでミイラをキョンシーにしてロンドンを飛び立った。だが、飛行機はアフリカで不時着。そこでサムと道士はニカウさんのいる部族と出会う。彼らが白人の奴隷商人とその手下の部族に苦しめられているのを知った二人はキョンシーを使って戦いに挑むが、敵にはブードゥー教の呪術師がおり……。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Julien Donkey-Boy", "ジュリアン", "", "", "http://image.eiga.k-img.com/images/movie/1461/315x210.jpg?2604", 100, "盲学校の教師をしている青年ジュリアンは、未婚で妊娠している姉パール、エキセントリックな父、レスラーを夢見る弟クリス、犬を溺愛する祖母と一緒に暮らしている。家族は父の異常な言動や怒りの発作に苛まれていたが、そんなジュリアンのたった一つの安らぎは姉のパールだった。しかしある日、ジュリアンたちとアイススケートに出かけたパールは、滑って転倒し病院に運ばれてしまう。", "2000-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bon Jovi: The Circle Tour Live from New Jersey", "BON JOVI ザ・サークル・ツアー　ライブ・フロム・ニュージャージー", "", "", "http://image.eiga.k-img.com/images/movie/57391/315x210.jpg?4204", 108, "ジョン・ボン・ジョビ率いる世界的ロックバンド「ボン・ジョヴィ」のライブステージの模様を映画館で上映。2010年に開かれたワールドツアーの中から、バンドのホームタウンでもある米ニュージャージーのニュー・メドウランド・スタジアムで行われたライブの模様を収録。「禁じられた愛」「It's My Life」「Living on A Prayer」「Keep The Faith」など多数のヒット曲が演奏される。", "2011-11-05", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Estrellas de La Linea", "線路と娼婦とサッカーボール", "", "", "http://image.eiga.k-img.com/images/movie/53161/315x210.jpg?1995", 90, "中米グアテマラの“リネア（線路）”と呼ばれる貧民街でサッカーチームを結成した娼婦たちの姿を通し、差別や貧困などグアテマラが抱える社会問題を炙り出したドキュメンタリー。様々な事情からリネアの娼婦となった女性たち。暴力や偏見に悩まされてきた彼女たちは、サッカーチームを結成することで注目を集め、社会への発言権を獲得しようと考える。彼女たちの活動はやがてマスコミを巻き込み、国内外で大きな反響を呼び始める。", "2007-12-22", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浪花の恋の物語", "浪花の恋の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "原作は近松門左衛門の名作『冥途の飛脚』。これを、溝口の晩年の作品を手掛けた成沢昌茂が脚色している。監督は内田吐夢で、リアリズムの監督としては珍しく、ここでは作者の近松を狂言回し風に登場させるなどして、独特の様式美の世界を創出している。徳川時代の大阪の飛脚問屋の養子、忠兵衛は遊女の梅川を愛するあまり、梅川が他の客に見請けされるのを嫌い、それを阻止しようとする。あげく、他人の大金を勝手に使い、梅川を引き取る。やがて二人は罪人の身となり、人目を忍んで生まれ故郷への逃避行を敢行。しかし、その行手には悲劇的終末が待ち構えていた。あまりにも有名な愛の悲劇を内田吐夢は気高く荘重な作品に仕上げている。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Colors of Life", "Colors of Life", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ロックバンド、“怒髪天”のボーカリスト・増子直純が主演した青春映画。サラリーマン人生の中で生きる目標を見失った主人公・工藤。自分探しの旅に出た彼が、新しい出会いによって再生していく様を、ロックとバイクの1970年代テイストたっぷりに描き出す。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アリア", "アリア", "", "", "http://image.eiga.k-img.com/images/movie/54021/315x210.jpg?3212", 96, "冬の長野県諏訪を舞台にした淡い初恋物語。主演は「パッチギ！／LOVE＆PEACE」の井坂俊哉と「ラストゲーム／最後の早慶戦」の原田佳奈、監督は「ヘイジャパ！」の村松亮太郎。諏訪で生まれ育ったミチは、女子高生ながらステンドグラス作家として暮らしていた。ある日、上京していた幼馴染の俊太郎が3年ぶりに諏訪に帰ってくる。恋心と憧れの思いを胸に秘め、俊太郎と久しぶりに過ごせる日々に幸せを感じていたミチだったが……。", "2008-11-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE QUEST", "魔宮神話　レジェンド・オブ・サンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「カリギュラ２」のスタッフが手掛けた、太古アドベンチャー・ロマン。「コナン・ザ・グレート」を彷彿とさせるストーリーが展開する。はるか昔の神話の時代。邪悪な王によって父を殺された王子が、聖なる剣を手に仇討ちの旅に出る。彼は予言者の言葉通り、美貌の女戦士の救出と王国再建のため、数々の強敵と対決する。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桜の樹の下で", "桜の樹の下で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "京都の老舗料亭の女将・菊乃と、大学卒業後、菊乃のもとで商売の身習いをしている娘の涼子。菊乃には東京の出版社社長で遊佐という愛人がいたが、いつの日からか遊佐は涼子をも愛し始める。女としてライバルの炎を燃やす母と娘。二つの愛に充足しながらも戦慄をおぼえる男。桜の映像の美しさばかりが際立つ恋愛映画。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やればやれるぜ全員集合!!", "やればやれるぜ全員集合!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ある日、日本の各地で５人の男が警察に捕まった。東北の寒村の鼻つまみ者だった彼らは、一旗あげようと上京したが挫折し、悪党の口車に乗せられ誘拐犯として逮捕されたのだった。しかし身の潔白が証明され、５人はまたあてどない生活を送ることになる。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("外事警察　その男に騙されるな", "外事警察　その男に騙されるな", "", "", "http://image.eiga.k-img.com/images/movie/57411/315x210.jpg?1664", 128, "警視庁公安部外事課を主舞台に国際テロリズムとの戦いをリアルに描き、2009年にNHKで放送されて人気を博したサスペンスドラマを映画化。11年、震災で揺れる日本にウランが持ち込まれたという情報が米CIAからもたらされる。かつて警視庁公安部外事4課ウラ作業班主任を務めていた住本健司は、現在は外事課から外され、内閣情報調査室に所属していたが、事件を追うため外事課に復帰。住本と外事4課のメンバーは、テロリストと交流があると疑われている企業・奥田交易の社長夫人を“協力者”に仕立て上げようと動き始める。しかしその矢先、住本が何者かに刺されてしまい……。主演はドラマ版に続き渡部篤郎。「男たちの挽歌 A BETTER TOMORROW」のキム・ガンウらが出演。", "2012-06-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宮本武蔵・一乗寺決闘", "宮本武蔵・一乗寺決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "吉川英治の原作をもとに、片岡千恵蔵が４度目の宮本武蔵役に挑んだ作品。千恵蔵はその生涯に武蔵を10回演じており、持ち役の一つである。名門吉岡道場に勝負を挑んだ武蔵は、清十郎、伝七郎の兄弟を倒し、一乗寺下り松で80余人を相手に吉岡一門と最後の戦いに臨む。千恵蔵の病気により完成に１年を要した。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼畜", "鬼畜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「ゼロの焦点」や「砂の器」など多くのヒット作を生み出した名コンビ、松本清張＝野村芳太郎による作品。印刷屋の主人がよその女に産ませた３人の子供を引き取ることになる。ところが、妻の冷たい仕打ちで赤ん坊が死に、追いつめられた男は東京タワーに娘を置き去りにし、残る長男を崖からつき落とすが……。切っても切ることのできない親と子の絆を衝撃的に描いた作品で、緒形拳がだらしのない父親を好演。またオールスター・キャストのなかにあって子役も印象的。緒形拳が娘を東京タワーに置きざりにして出てくると、夕景にそびえるタワーに灯がともるシーンは圧巻。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新諸国物語　七つの誓い 凱旋歌の巻", "新諸国物語　七つの誓い 凱旋歌の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "NHK連続放送劇のアグファ東映カラーによる映画化シリーズ。いよいよ王家の財宝の謎が解明される。中村錦之助はじめ、“新諸国物語”シリーズでおなじみの若手スターが大活躍。DVDは「新諸国物語　七つの誓い(2)」に収録。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GRAND TOUR", "グランド・ツアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "過去に起きた惨劇を体験するためのツアーに参加し、未来からやって来た訪問者たち。彼らを泊めたホテルのオーナーのベンは、偶然にも町でその訪問者の一人のパスポートを拾う。それは時間を超えることができるタイム・パスポート。ベンはさっそく過去に戻り、妻や娘との失われた愛の再生に挑む。新鋭トゥーヒー監督のSFヒューマン・ファンタジー。", "1991-01-01", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CATS　＆　DOGS", "キャッツ＆ドッグス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "犬と猫による地球の未来を懸けた戦いを、高精度のアニマトロニクス・モデルとCGを駆使して描いたファンタジー・アクション。世界征服を企むイカレた邪悪猫ミスター・ティングルスは、犬アレルギーを解消する新薬の研究をしているブロディー教授一家をターゲットに定めた。人類を守るため秘密諜報機関を擁していた犬たちはティングルスの野望を砕くため、トップ・エージェント犬を送り込むことにするが、手違いから何の知識もないポケット・ビーグルの子犬ルーが一家に入り込むことに。ルーは仲間たちの助けを得ながら、ティングルスが送り出すニンジャ猫や暗殺猫とバトルを繰り広げていく。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Miss Boys！　決戦は甲子園!?編", "Miss Boys！　決戦は甲子園!?編", "", "", "http://image.eiga.k-img.com/images/movie/57531/315x210.jpg?2921", 79, "女装をテーマに男子高校生たちの青春を2部構成で描いた「Miss Boys！」の第1部。中学時代からバスケ部のエースとして活躍してきた真実は、男子校の星修学園でもバスケ部に入部するが、ワンマンプレーを批判され、学内一厳しいといわれる応援団で精神を鍛えなおすことに。しかし、その応援団の実態は男子がチアガールに扮する「女装応援団」で、部の存続をかけたライバル校との対抗戦「女装甲子園」がすぐ目の前に迫っていた……。主演は5人組バンド「ココア男。」でも活躍する俳優の鎌苅健太と鈴木勝吾。そのほか、辻本祐樹、馬場良馬ら若手俳優がそろう。", "2011-12-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無責任遊侠伝", "無責任遊侠伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "上田ヒトシは生来の博才で、勝負ごとには絶対負けないツイてる男。フィアンセの泉カネ子が彼を強引に阿波商事へ入れるが、上田は得意の博才で大商談をまとめてしまう。犬アレルギーという弱点が設定された。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRELUDE TO A KISS", "キスへのプレリュード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ブロードウェイで記録的なロングランとなったC・ルーカスによる舞台劇を映画化。熱烈な恋に落ちたピーターとリタは、出会って数ヵ月で結婚式を挙げることに。多くの参列者の中から見慣れぬ老人が現れ、花嫁と祝福のキスを交わす。その途端、なんとリタと老人の魂がそれぞれの体に入れ代わってしまった。ハネムーンでリタの異変に気付いたピーターは、結婚生活に絶望してしまう。そんな折、彼はリタの魂を持つ老人と再会し……。ファンタスティックな味わいの恋愛ドラマ。評判の舞台劇らしく、ウイットに富んだ会話も楽しいが、何よりも二つの性格を演じ分けるM・ライアンのキュートな魅力が光る。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SPONGEBOB SQUAREPANTS MOVIE", "スポンジ・ボブ／スクエアパンツ・ザ・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "大人も驚愕のぶっ飛んだストーリーとチャーミングなキャラクターが大人気のTVアニメを映画化。海中のハンバーガー店で働く海綿生物スポンジ・ボブの日常が愉快に描かれている。スカーレット・ヨハンソンなど声優陣も豪華だ。海底都市の人気バーガー店で働くスポンジ・ボブは2号店の店長の座を、子供だからという理由で却下されてしまう。そんな中、ライバル店が人気メニューのレシピを奪う作戦を実行していた。", "2006-04-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAX HEADROOM: 20MINUTES INTO THE FUTURE", "電脳ネットワーク23／マックス・ヘッドルーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "CG（コンピュータ・グラフィックス）によって創造された新世紀のキャラクター“マックス・ヘッドルーム”。ロンドンのTV局チャンネル４のMTVのVJとして登場以来、一躍人気を博した“彼”の誕生秘話をドラマ化している。急成長のTVが世界を支配している近未来。“ネットワーク23”はプリップバーツという強制凝縮CMを開発する。だがこのCMは脳に強度の副作用をもたらすことが判明した。この秘密をかぎつけたのが、同局の人気レポーターのカーターだ。局はカーターの抹殺をはかるが、彼の人気も失いたくない。そこでカーターの頭脳だけをコンピューターに移植し、CGで“マックス”を作り出した。マックスはライバル局の手にわたり人気者に。奪い返そうとするネットワーク23。そして九死に一生を得たカーターも、真相を暴露すべく行動を開始する。このオリジナル・ストーリーも好評を博し、シリーズものとしてTV化されている。", "1985-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PICKING UP THE PIECES", "スプラッター殺人事件 クレイジー・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "夜の公園で美女惨殺事件が発生。頭を切断され、口にはエジプト文字で書かれたメモが押し込まれていた。それを見た担当刑事ジョーは12年前によく似た事件があったことを思い出し、当時の相棒を訪ねるが……。グロテスクなシーンを満載したスプラッタ・ホラー。特殊メイクの第一人者トム・サヴィーニのメイクが笑えるほど強烈。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺人遊戯", "殺人遊戯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "殺し請け負い業、鳴海昌平に松田優作が扮した、村川＝松田のコンビによる“遊戯”ものの第２作。互いに抗争中の片方の組から、相手の組長を殺すよう依頼された鳴海は、相手の組長にも同じ報酬で仕事をさせろと持ちかけるのだが……。鳴海のとぼけた味がいい。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月の渡り鳥", "月の渡り鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "天保の頃、下田の港の鳶職・銀平は、お市をひそかに想っていたが、お市が他の男を愛していると一人合点して旅に出てしまう。数年後、故郷に戻ってみると港はヤクザの丑松一家に仕切られ、お市も結婚して子供をもうけながら貧しい暮らしをしており、銀平は立ち上がる。長谷川伸の『鯉名の銀平』を原作にした任侠もの。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("休暇", "休暇", "", "", "http://image.eiga.k-img.com/images/movie/53561/315x210.jpg?2570", 115, "死刑囚を収容する拘置所で働く刑務官の平井は、シングルマザーの美香と結婚することに。死刑執行の際に死刑囚の支え役を務めれば1週間の休暇がもらえると知った彼は、新婚旅行へ出かけるためにその役を引き受けるが……。「闇にひらめく」などで知られる作家・吉村昭の短編を基に、死刑執行に関わる刑務官の苦悩と葛藤を描き出した社会派ドラマ。出演は「歓喜の歌」の小林薫、「パッチギ！／LOVE＆PEACE」の西島秀俊ほか。", "2008-06-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SAD SACK", "底抜け一等兵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ディーン・マーティンとのコンビで“底抜け”シリーズに出演してきたルイスが、マーティンの独立、転向により、一人で出演した“底抜け”シリーズ第１作。頭の弱い新兵ルイスが遊び好きな伍長と手柄を立てる話。原作はG・ベイカーの軍隊漫画『可哀そうな二等兵』。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　THIRTEENTH　FLOOR", "13F", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "R・エメリッヒが製作に回り、「GODZILLA／ゴジラ」で第２班監督を務めたドイツの新鋭監督J・ラスナックが撮り上げたSFサスペンス。仮想現実創造の研究に取り組んでいるダグラスは、上司フラーが殺されたことを知る。殺人の容疑をかけられた彼は、真相を探るため、1937年を背景にした仮想現実の世界に飛び込んでいく。やがて現実と仮想世界を行き来するうちに、二つの世界の境界が崩れつつあることを察知するダグラス。だが、その先にはもっと恐ろしい秘密が……。主人公ダグラスが自分が住む現実さえ仮想世界であることを知るまでを描き、観る者を迷宮の世界に誘い込む。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Producers", "プロデューサーズ", "", "", "http://image.eiga.k-img.com/images/movie/1701/315x210.jpg?3968", 134, "メル・ブルックス監督の68年の同名コメディ映画が、01年にブロードウェイのミュージカルになって史上最多のトニー賞12部門を受賞。この大ヒット・ミュージカルを映画化したのが本作。監督はブロードウェイの舞台版の演出＆振付を担当したスーザン・ストローマン。ショーがコケればコケるほどプロデューサーは儲かるというカラクリを発見したプロデューサーと会計士が、史上最低のミュージカルを上演しようとするのだが……。", "2006-04-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花のあすか組！", "花のあすか組！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "舞台は近未来の、NEW-KABUKI-TOWN。この街には二大勢力、“レッドノーズゴッド”と“PB-４”があった。そこに、謎の少女・九條あすかが現れ、街に波乱を巻き起こす。つみきみほのツッパリ演技と、５千万円をかけたという歌舞伎町の近未来セットが見もの。", "1988-01-01", "76", 0, NOW(), NOW());
