INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("曲がれ！スプーン", "曲がれ！スプーン", "", "", "http://image.eiga.k-img.com/images/movie/54401/315x210.jpg?4617", 106, "超常現象を取り上げるバラエティ番組「あすなろサイキック」のAD・桜井米（ヨネ）は、本気で超能力の存在を信じている天然少女。エスパーを探してカメラ片手に日本全国を歩き回る米は、なかなかネタをつかめずに路頭に迷っていたが、最後の取材で訪れた「カフェ・ド・念力」で本物のエスパーが集うパーティに遭遇する。原作は劇団「ヨーロッパ企画」の戯曲で、監督は「踊る大捜査線　THE MOVIE」シリーズの本広克行、主演に長澤まさみ。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マクナイーマ　デジタルリマスター版", "マクナイーマ　デジタルリマスター版", "", "", "http://image.eiga.k-img.com/images/movie/55791/315x210.jpg?4743", 0, "フランスのヌーベルバーグの影響を受け、ブラジルで起こった映画運動“シネマ・ノーボ”の主要人物として知られる鬼才ジョアキン・ペドロ・デ・アンドラーデ監督が、1969年に手がけた不条理コメディ。ブラジル近代文学の金字塔といわれるマリオ・デ・アンドラーデの同名小説を原作に、アマゾンの奥地で中年の黒人男性姿で生まれた主人公マクナイーマが繰り広げる奇想天外な冒険を、ブラックユーモアや社会風刺を交えて描く。", "2010-12-04", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TALES FROM PRAGUE", "プラハからのものがたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 26, "世界的に名高いアニメーション作家ヤン・シュヴァンクマイエルの世界に迫るドキュメンタリー。シュヴァンクマイエルへのインタビューを交えながら、過去の作品や製作中のスタジオの様子など、貴重な映像である。英国BBCが製作し、日本では1996年シュヴァンクマイエル特集の時に劇場公開された。DVDは「ヤン・シュヴァンクマイエル　短編集」に収録。", "1994-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Date Night", "デート＆ナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "スティーブ・カレル＆ティナ・フェイ主演のアクション・コメディ。ニューヨークに住むフィルとクレアのフォスター夫妻は平凡な生活を送る退屈なカップル。2人はいつものように週末のディナーを楽しもうとマンハッタンのビストロに行くが、店で予約の名前をごまかして席に着いたことから2人組の男たちから追われる羽目に……。監督は「ナイトミュージアム」シリーズのショーン・レビ。共演にマーク・ウォールバーグ、ジェームズ・フランコ、マーク・ラファロほか。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RIDING IN CARS WITH BOYS", "サンキュー、ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "B・ドノフリオが自分の半生を綴った小説を映画化した、破天荒な女性の波乱の物語。小説家になる夢を持った15歳の少女ビバリーは同級生レイと軽い気持ちで交際、妊娠してしまう。高校を中退してレイと結婚したものの、彼は働く気もなく貧乏生活。そして、大学への進学も、レイが貯金をすべてヘロインに使ってしまってあきらめることに。自分の人生設計が完全に狂ったビバリーは離婚を決意、息子ジェイソンと二人で生活していく……。自由奔放だが傷つきやすいヒロインの15歳から35歳までを、D・バリモアが熱演。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Enchanted", "魔法にかけられて", "", "", "http://image.eiga.k-img.com/images/movie/53101/315x210.jpg?1900", 108, "ディズニーアニメのプリンセスが実写の世界へ迷い込んだことで起こる大騒動を描いたファンタジック・コメディ。魔法の国で暮らすジゼルは、王子との結婚式の日に魔女に騙されて現代のニューヨークへと送り込まれてしまう。今までいた世界の常識が通じず困り果てていた彼女は、超現実主義の弁護士ロバートと出会う。ピュアでマイペースなヒロインを演じるのは「キャッチ・ミー・イフ・ユー・キャン」のエイミー・アダムス。", "2008-03-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEYOND BORDERS", "すべては愛のために", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "ハリウッドを代表する人気アクトレス、A・ジョリーの主演作。青年医師ニックとめぐり合ったことをきっかけに、病気や飢えに苦しむ難民たちに目を向けるようになった裕福なイギリス人女性サラ。10年間に渡る彼女の苦悩と成長、ニックとの愛の軌跡がドラマティックに語られる。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOOM", "DOOM／ドゥーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "人気シューティング・ゲームを映画用にデフォルメしたVFXヴァーチャル・アクション。火星の研究所を舞台に、凶暴なクリーチャーに闘いを挑む特殊部隊のサバイバルを描く。ゲームの世界を表現した一人称視点の映像演出は出色の出来！ 火星にある研究所に緊急事態が発生し、海兵隊の特殊部隊所属の精鋭たちが現地の調査に動き出す。研究所へ到着した彼らを待ち受けていたのは、巨大で凶暴なクリーチャーの大群だった。", "2006-04-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・女は男のふるさとョ", "喜劇・女は男のふるさとョ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ストリップ斡旋所“新宿芸能社”を舞台に、肉親の愛薄く、幸せな結婚を夢見るマタタビ笠子と、お人好しで笑っていても泣いているような顔の晃子、この二人を中心に、経営者の金沢夫婦、そこに働く踊り子たちの生活からにじみ出る喜怒哀楽をコミカルに描く。腹にすえかねて人糞を流し込んで報復する場面など少々下品ではあるが、バイタリティーあふれる女の痛快さで笑いが弾ける。また、ささやかな結婚祝いをした夜、男にだまされたことを知り号泣する笠子や、受験に失敗して自殺を思いつめた少年を身体で慰めようとして警察にしょっ引かれる晃子のエピソードには、胸をつかれる。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LILI", "リリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "フランスのある田舎町。孤児になった少女リリーはふとしたことからカーニバルの魔術師に拾われる。彼はリリーに一座のウェイトレスの仕事を与えるが……。ハンサムな魔術師と人形使い、それに人形たちが繰り広げるミュージカル。『ハイ・リリー・ハイ・ロー』がヒット。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOMICIDE", "殺人課", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "雑貨屋を営む老女が散弾銃で殺害され、捜査に当たった腕利き刑事のボビーは、事件の背後にヒトラーを信奉する反ユダヤ主義の殺人集団と、ユダヤ人のテロリスト・グループの存在があることを知る。自分もユダヤ人であるボビーは、刑事であるべきか、民族の血に忠実であるべきか、自らのアイデンティティーに疑問を抱き始める。多民族のアメリカならではの問題点を捉えた衝撃のサスペンス。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FALLS", "THE FALLS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 194, "「英国式庭園殺人事件」や「ZOO」などの傑作を発表してきたP・グリーナウェイ監督の初の長編作品。当時日本未公開になっていたのが不思議なくらい完成度の高い傑作だ。出演者が92名にも及ぶ群像劇スタイル。不慮の事故に遭遇するという運命にある人々が登場して、やがて彼らの偽のプロフィールが語られていく。", "1980-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Spartacus", "スパルタカス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 190, "　スタンリー・キューブリック初の長編カラー作品で、ローマ帝国を舞台にした一大叙述詩。自由を手に入れるため、奴隷スパルタカスは剣闘士や奴隷で組織した軍を扇動し、ローマ貴族クラサスに大規模な反乱を起こす。前作「突撃」に続きカーク・ダグラスが主役に起用され、アカデミー賞俳優ローレンス・オリビエが敵役のクラサスを演じる。ピーター・ユスティノフの助演男優賞をはじめ、第33回アカデミー賞4部門を獲得。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EGG", "エッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "パン屋を営む35歳の独身男ヨハンは、恥ずかしがり屋でおいしいパンを焼く以外なんの取り柄もなかった。彼の結婚相手を探そうと一計を案じる悪友二人。やがてある春の日に一人の女性がバスから降り立った……。人間を温かい目で見つめた物語が絵本のように展開するペーソスあふれる作品。", "1987-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("朧夜の女", "朧夜の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "東京の下町に生まれ育った名匠・五所平之助監督が、自分の身のまわりにいた典型的な下町の人々をモデルにして下町情緒を再現した作品。脚本は小津作品で知られる池田忠雄で、そのセリフのうまさは絶品。洗い張り屋のおかみを演じる飯田蝶子も良い味を出している。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばりのサーカス 悲しき小鳩", "ひばりのサーカス 悲しき小鳩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "昭和を代表する歌姫、美空ひばりがティーンエイジャーの頃、劇中で見事な歌唱力を披露した主演作。父と娘の絆を描いた名編だ。ヒロインは寄宿舎から学校に通っていた。ある日、父親がサーカスの一員だと知った彼女は、学校を中退し父の元へ。一緒に全国巡業を回るうち彼女は舞台で歌い、観客の喝采を浴びる。そんな時、別れた母親が父にある相談を……。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トラック野郎・爆走一番星", "トラック野郎・爆走一番星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "姫路のドライブ・インでアルバイトの女子大生・暎子に一目惚れの桃次郎。文学少女の彼女に合わせるため、古今東西の文学を読破せんと文学青年気取り。彼女に渡すはずの愛の告白が、金造の手違いでバキュームカーの女運転手の手に渡って……。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAD DAY AT BLACK ROCK", "日本人の勲章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "「荒野の七人」「大脱走」などアクション映画を得意とするJ・スタージェスのサスペンスフルな現代西部劇。西部の小さな町に列車から片手のない一人の男が降りる。何もないこの小さな町でよそ者の噂はすぐに伝わり、町のボスや保安官が疑心暗鬼でこの謎の男の目的を探り始める。男はイタリア戦線で自分の命を救って死んだ日本人二世の勲章を、この町に住む彼の父親に届けるためにやって来たのだ。だが、その父親は日本人を憎悪する町のボスたちによって殺されていた……。異色のサスペンス映画であり、S・トレーシーが空手で町のボスたちを倒すアクション・シーンも見もの。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Che: Part One", "チェ 28歳の革命", "", "", "http://image.eiga.k-img.com/images/movie/53741/315x210.jpg?3942", 132, "「トラフィック」でアカデミー賞監督賞＆助演男優賞を受賞したスティーブン・ソダーバーグとベニチオ・デル・トロが、キューバ革命を率いた指導者エルネスト・チェ・ゲバラの生き様を2部作、総上映時間4時間25分で描く大作の第1部。1955年7月、貧しい人を救いたいと南米を旅するゲバラは、故国キューバの革命を計画するフィデル・カストロと出会い、彼に同調。そして1956年、28歳の若さで革命に乗り出したゲバラは、キューバの独裁軍事政権を打倒する。", "2009-01-10", "9,2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARACAS", "カラカス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "妻は浮気性、自分はうだつのあがらぬ中年男のガソリンスタンドの主人ハインツ。ある日彼は妻に瓜二つの娼婦に出会い、妻を殺そうと完全犯罪をもくろむ。用意周到な計画のもと、金と女を携えてペルーのカラカスへ高飛びするつもりだった……。憂うつから殺人に至るまでの主人公の心理が、躍動感をもって描かれるオーストリア映画。", "1989-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRICTLY BALLROOM", "ダンシング・ヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "社交ダンス協会の規定を無視し、新しいステップを目指す青年の恋とダンスの日々。青春ドラマのステロタイプ的展開を逆手にとり、遊び心いっぱいのクサイ演出の連発によって、感動よりは笑いを呼び起こすユニークな青春ダンス映画。もちろんダンス・シーンでは超一流のステップを堪能できる。監督は後に「ロミオ＆ジュリエット」でハリウッドに進出するB・ラーマン。", "1992-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Invasion of Alien Bikini", "エイリアン・ビキニの侵略", "", "", "http://image.eiga.k-img.com/images/movie/56931/315x210.jpg?3875", 75, "2011年ゆうばり国際ファンタスティック映画祭のオフシアター・コンペティション部門でグランプリを受賞した異色の韓国製SFサバイバルムービー。絶世の美女の姿をしたエイリアンのモニカが純潔な精子を求めて地球に降り立ち、30歳の童貞青年ヨンゴンに狙いを定める。モニカが身ごもると地球が滅亡してしまう上に、本当に愛する人に出会うまで純潔を貫くことを誓っていたヨンゴンは、セクシーなビキニ姿で誘惑してくるモニカから貞操を守るために奮闘する。", "2011-10-22", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あずみ", "あずみ", "", "", "http://image.eiga.k-img.com/images/movie/1271/315x210.jpg?4762", 142, "江戸初期。戦争孤児10人が、徳川家の命で最強の刺客として育てられ、仲間同士で殺し合うという過酷な試練を経て、大名暗殺の指令を受ける。監督は、高校を中退してオーストラリアに留学、長編第1作「VERSUS」が評価され、米ミラマックスと2作品のファーストルック契約を結んだ北村龍平。あずみの刺客仲間役で「ちゅらさん」の小橋賢児、「木更津キャッツアイ」の成宮寛貴、「ウォーターボーイズ」の金子貴俊など若手男優が大挙出演。", "2003-05-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tucker: The Man and His Dream", "タッカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ルーカス、コッポラという現代アメリカ映画界の二大巨匠が組んだ作品。1945年アメリカが最も活力にあふれていた時代、当時絶大な力を持っていた自動車業界に敢然と挑戦した一人の男。彼は夢と信念に燃えてドリーム・カーの製造に情熱を傾けた。中傷妨害、権力からの圧力に屈せず自分の道を信じて障害に立ち向かっていく。そしてついにわずか50台の手作り車を完成させる。コッポラの、久しぶりのエネルギッシュな演出が冴える。主人公タッカーに扮するブリッジスもひたむきな演技でなかなかの味を出している。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あ、春", "あ、春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "韮崎紘は良家の娘と結婚し、幼い息子、義母と一緒に暮らす平凡な証券マン。ある夜、彼の前に現れたホームレス風の男は、母親から死んだと聞かされていた父親の笹一だった。そのまま家に居座った笹一は荒っぽい言動を連発し、紘と家族をオロオロさせていく。猫の視点による軽やかなショットで幕を開ける名匠、相米慎二のホームドラマ。山の手の古めかしい日本家屋を舞台に、大混乱に陥った家族の姿をユーモアと切なさを織り交ぜてさらりと紡ぎ出す。順調だったはずの人生を見つめ直す主人公に扮した佐藤浩市、その母親役で懐の深い存在感を見せる富司純子らキャストも絶妙の味わい。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SINGLE WHITE FEMALE", "ルームメイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "B・シュローダーが手掛けたサイコ・サスペンス。恋人と別れ、アパートの同居人を探していたコンピュータ・プログラマーのアリソンは、どこか野暮ったいがマジメそうな娘ヘドラと共同生活を送ることに。洗練されたアリソンに対してコンプレックスを抱くヘドラは髪形から服装まで、彼女の真似をし始める。それだけでは収まらず、やがてヘドラは、よりを戻そうとやって来たアリソンの恋人に接近する……。生理的な部分にまで迫る生々しい嫉妬がリアルで、心理的な恐怖感は十分。狂気に憑かれた女性に扮するJ・J・リーの怪演が目を引く。B・フォンダも、恐怖に脅えるヒロインを熱演。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beyond The Peloton Season 2", "ビヨンド・ザ・プロトン 2010", "", "", "http://image.eiga.k-img.com/images/movie/56501/315x210.jpg?2290", 83, "世界最高峰の自転車ロードレース「ツール・ド・フランス」に出場するプロチームに密着したドキュメンタリーを一挙公開する特集上映「サイクルロードレースの世界」にて上映。2009年、1年目にしてさまざまな活躍を見せた「サーヴェロ・テストチーム」は、翌10年に再びグランツールの頂点「ツール・ド・フランス」へ挑む。同年で解散することとなったサーヴェロ・テストチームが、新しいプロサイクリングチームの形を追い求めた最後のシーズンを収める。", "2011-06-04", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一個好人／MR.NICE GUY", "ナイスガイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ジャッキー・チェンが盟友サモ・ハンの演出の下で、命懸けの立ち回りを披露するアクション活劇。ひょんなことから犯罪組織の取引現場を収めたビデオを手にしてしまった、料理番組の人気ホスト、ジャッキー。ギャングに追われる彼は女性キャスターとともに、組織の悪事を暴くべく奔走を繰り広げる。オーストラリアでロケを敢行。250トンのメガ・トラックを駆使した破壊のクライマックスは圧巻。", "1997-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I WANNA HOLD YOUR HAND", "抱きしめたい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ビートルズが初めてアメリカ公演にやって来た1964年２月。ニューヨークへ向かった男二人と女４人の６人組。ビートルズ・ファンにアンチ・ビートルズ、そしてひたすら恋愛に熱中する男、と“六者六様”の珍騒動。1960年代の若者像を描いた青春ドタバタ・コメディ。ゼメキス監督のデビュー作。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緋牡丹博徒・仁義通します", "緋牡丹博徒・仁義通します", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "大阪堂万一家・三代目の跡継ぎ争いにやぶれた松川は、対立する伝法一家に走り、堂万一家を潰そうと企てる。それをはばむ緋牡丹お竜や堂万一家・三代目の北橋らの活躍が描かれる。藤純子が映画界を引退するためにシリーズの最終作となった。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Foo Fighters Presents Back and Forth & 3D Live", "フー・ファイターズ・プレゼンツ　バック・アンド・フォース＆3Dライヴ", "", "", "http://image.eiga.k-img.com/images/movie/56651/315x210.jpg?2773", 150, "世界的ロックバンド「フー・ファイターズ」のフロントマン、デイブ・グロールが、カート・コバーンとの死別からフー・ファイターズ始動の経緯、新しいバンドメンバーとの出会いやバンド崩壊の危機、最新アルバムの製作経緯など、フー・ファイターズ16年の歴史を語るドキュメンタリー。歴代のバンドメンバーも出演し、それぞれの言葉でバンドの歴史が語られる。2011年4月5日に行われた最新アルバム「ウェイスティング・ライト」の全曲を演奏したライブの模様を3Dカメラで撮影した「3Dライブ」も上映。", "2011-06-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHORT CIRCUIT2", "ショート・サーキット2　がんばれ！ジョニー5", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "前作「ショート・サーキット」で人気を得たロボット、“ナンバー５”が再び大活躍。すっかり人間になじみ、“ジョニー５”という愛称で呼ばれるようになった人気者の彼だが、忙しい大都会の中ではなかなか友だちができない。人間不信に陥った彼に救いの手を差しのべてくれたのは、オスカーという気の良い老人だったが、オスカーは実は銀行強盗の黒幕だったのだ。犯行の片棒をかつがされたジョニー５が、強盗相手に大立ち回り。監督にはTV『バイオニック・ジェミー』などのK・ジョンソンがあたり、前作以上にユーモラスな作品に仕上がっている。ジョニー５の今回のマドンナは日本でも人気を博したC・ギブ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビリーブ", "ビリーブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "知的障がいを持った人々を追い続ける小栗謙一監督の「エイブル」「ホストタウン」に続く、ドキュメンタリー・シリーズの第３作。知的障がい者の男女９人が撮影クルーを結成し、昨年２月に開催された長野スペシャルオリンピックス冬季世界大会の取材に挑むまでの道のりを見つめる。はじめはカメラの扱いも知らなかった９人が、合宿と現場で経験を積むことによって撮影方法を徐々にマスター。その努力が実を結び、小泉純一郎元首相やKONISHIKIといった著名人へのインタビューも果たす。そんな一生懸命な彼らの姿が、爽やかな感動を呼ぶ。", "2006-01-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　空とぶ絵本とガラスの靴", "それいけ！アンパンマン　空とぶ絵本とガラスの靴", "", "", "http://ecx.images-amazon.com/images/I/21JQ80E0GBL._SL210_.jpg", 60, "やなせたかし原作の大人気アニメ「それいけ！アンパンマン」の劇場版第8作。ある日、図書館で大好きなシンデレラの続編を借りてきたメロンパンナ。ところがその内容は、ガラスの靴が2手に別れてシンデレラと王子さまが離れ離れになり、さらに恐ろしい怪物が現われておとぎの国が荒らされてしまうという恐ろしいものだった。その晩、絵本から出てきたフクロウのホッピーに導かれ、アンパンマンたちはおとぎの国を救いに行くが……。", "1996-07-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2010-2011 アダムズ「ニクソン・イン・チャイナ」", "METライブビューイング2010-2011 アダムズ「ニクソン・イン・チャイナ」", "", "", "http://image.eiga.k-img.com/images/movie/56471/315x210.jpg?1805", 219, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を劇場のスクリーンで上映する「METライブビューイング」2010～11年シーズンの6作目。東西冷戦時代の1972年2月、毛沢東主席との会談のためリチャード・ニクソン米大統領が北京空港に降り立つ。じょう舌な毛沢東の弁はニクソンを圧倒していくが……。アメリカ資本主義と中国共産党の歴史的邂逅（かいこう）を通じて人間の真実を探求するジェームズ・アダムズの代表的オペラを、アダムズ自身の指揮により上演。", "2011-02-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Wife Got Married", "妻が結婚した", "", "", "http://image.eiga.k-img.com/images/movie/54841/315x210.jpg?4412", 119, "「私の頭のなかの消しゴム」「四月の雪」のソン・イェジンが主演するラブコメディ。誰もが憧れる才色兼備のイナに一目ぼれした純朴なドックンは、愛の深さと押しの強さで何とかイナと結婚するが、イナから「結婚後も自由な恋愛を許可すること」という条件を付けられる。結婚すればイナを独占できると思っていたドックンだが、自由奔放なイナはある日、「もうひとり結婚したい人がいる」とドックンに提案するのだった。", "2009-09-26", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トリコン!!!", "トリコン!!!", "", "", "http://image.eiga.k-img.com/images/movie/53321/315x210.jpg?1599", 81, "3人の冴えない青年探偵が、ひとりの少女との交流を通して成長していく姿を描いた青春ストーリー。同じ孤児院で育ったエース、キング、ジャックの3人組はハードボイルドに憧れて探偵社を開いたものの、仕事の依頼がさっぱり入らず困り果てていた。そんな3人の前に、ダイヤと名乗る幼い少女が現われる。彼女の存在は3人の運命を大きく動かし始め……。進藤学、南圭介、八神蓮らドラマや舞台で人気の若手俳優3人が主演を務める。", "2008-02-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("洲崎パラダイス 赤信号", "洲崎パラダイス 赤信号", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "東京・洲崎遊廓の入口にある飲み屋を舞台にして、そこに蠢く人々を描いた川島雄三の作品。川島の作品では、しばしば橋が重要な舞台となるが、この飲み屋も遊廓へと続く橋のたもとにある。両親に結婚を反対されたため、上京してきた蔦枝と義治は、ひょんなきっかけで洲崎遊廓の入口にある飲み屋の女将の世話を受けることになる。蔦枝はその飲み屋で働くことになり、まもなく義治もソバ屋に職が見つかる。ある日、蔦枝は田舎に送金したいからといって、義治に給料の前借りを頼むが、義治はしぶる。優柔不断の義治にアイソをつかした蔦枝は、金を工面してくれた店のなじみ客の世話になる。そこへ行方不明となっていた女将の亭主が戻ってくる。女将からソバ屋の娘と一緒になれと諭され、その気になった義治だが、結局、舞い戻ってきた蔦枝とヨリを戻すのだった。蔦枝を探して町をさまよう義治のみじめなダメ男ぶりが、生命力あふれる蔦枝と対照的に描かれる。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("White Night", "白夜行　白い闇の中を歩く", "", "", "http://image.eiga.k-img.com/images/movie/57611/315x210.jpg?3286", 135, "ミステリー作家・東野圭吾の人気作で、日本では綾瀬はるか、山田孝之主演でドラマ化（2006）、堀北真希、高良健吾の主演で映画化（10）もされた「白夜行」を、韓国で映画化。密室の廃船で質屋の店主が殺害される事件が発生し、容疑者の死亡で事件は一応の解決をみる。しかし、担当刑事のハン・ドンスは、容疑者の娘イ・ジアと被害者の息子キム・ヨハンの存在が頭から離れなかった。14年後、美しく成長したイ・ジアとキム・ヨハンの周辺で不可解な事件が連続し、2人の意外な関係性が明らかになっていく。主演は「私の頭の中の消しゴム」のソン・イェジンと「超能力者」のコ・ス。", "2012-01-07", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SMOKEY AND THE BANDIT II", "トランザム7000vs激突パトカー軍団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "全米でヒットした「トランザム7000」の続編。マイアミからダラスへ象の運搬を引き受けたバンデット。結婚式から逃げ出した元恋人のせいで保安官に追われるわ、象は出産するわの大混乱。題名通りコンボイ対パトカーの対決が見もの。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("樹氷のよろめき", "樹氷のよろめき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "札幌で美容院を経営する百合子は、愛人の高校教師・杉野と冬の旅に出る。これを最後に杉野と別れるつもりでいた百合子は、杉野の目覚める前に旅館を出て、かつての恋人・今井のもとへ向かった……。一人の女をめぐる二人の男の葛藤と、その果ての一方の死を描いた吉田喜重監督の現代の愛のドラマ。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スクールデイズ", "スクールデイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「世界の中心で、愛をさけぶ」の森山未來主演によるブラックな青春コメディ。かつて天才子役として人気を集め、8歳で芸能界を引退した相沢晴生。8年後、普通の高校生として冴えない毎日を送っていた晴生は、人気学園ドラマ「はみだし！スクール☆デイズ」のオーディションに合格し、復帰を果たす。ところが、ドラマの中の熱血教師・鴻ノ池先生の教えに感動して役にのめりこんでいくうちに、現実とドラマの世界の区別がつかなくなっていく。", "2005-12-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHE ORA E｀？", "BAR〈バール〉に灯ともる頃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "イタリアの名優M・マストロヤンニと「イル・ポスティーノ」のM・トロイージが1989年のヴェネチア映画祭で同時に主演男優賞を受賞し、10年の歳月を経て日本初公開となった作品。ティレニア海を臨む小さな港町に、初老の父親が息子を尋ねてやって来る。それまでお互いの感情をうまく伝えられずにいた二人が久しぶりに再会し、ともに一日を過ごすことに。買い物をしたり映画を見たり、息子の恋人の家を訪れたりと楽しいひと時を過ごした彼らは、夕暮れ時のBAR（バール）を訪れるが、ささいなことで口論となってしまい……。", "1989-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　FREE　SOUL", "自由の魂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "有能な弁護士スティーヴンは、酒癖が悪く度々トラブルを起こしている。かつて弁護したことのある賭博師エースと娘ジャンの恋を快く思わない彼は、一方的にジャンの結婚相手を決めていた。だが、そのために親子の関係に亀裂が生じ、ジャンの婚約者がエースを射殺するという悲劇へと発展してしまう。父親の複雑な心情をL・バリモアが熱演。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("たゆたう GOOD TIME MUSIC of clammbon", "たゆたう GOOD TIME MUSIC of clammbon", "", "", "http://image.eiga.k-img.com/images/movie/53251/315x210.jpg?4546", 98, "結成12年目を迎える3ピースバンド、クラムボン。約2年ぶりのオリジナルアルバム“Musical”のリリースにあわせ、2007年6月から8月にかけて全国12カ所を廻ったライブツアー“tour Musical”の模様を追ったドキュメンタリー。ライブ映像はもちろん、舞台裏やリハーサル風景、さらにメンバーのインタビューなどを交え、彼らの素顔に迫る。メガホンを取るのは、デビュー以来全てのプロモーションビデオを手掛けてきた高木聡。", "2008-01-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版ポケットモンスター　ベストウイッシュ　キュレムVS聖剣士　ケルディオ", "劇場版ポケットモンスター　ベストウイッシュ　キュレムVS聖剣士　ケルディオ", "", "", "http://image.eiga.k-img.com/images/movie/57621/315x210.jpg?1779", 0, "", "2012-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOREIGN INTRIGUE", "外国の陰謀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "フランスの保養地リビエラで大富豪のダネモアが死んだ。検死の結果は自然死とされるが、これに疑問を抱いた男が、ダネモアの正体を探るうち、様々な謎が浮かび上がる……。TVでヒットした作品を映画用に作り替えたサスペンス・ストーリー。戦後10年を経ていまだ残るナチの残党の暗躍を背景にしながら、一人の男をめぐるスリリングな謎解きゲームが展開していく。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PARTITA／THE GAMBLE", "ギャンブル 愛と復讐の賭け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ベニスの若き貴族フランチェスコは、美貌のギャンブラー、マチルダと生死を賭けた勝負をし、負けてしまう。だがフランチェスコは約束を守らず、彼女から逃げる。凶悪な手下を使い、追いかけるマチルダだったが、次第にフランチェスコに引かれていき……。若き貴族にM・モディン、それを追う女ギャンブラーにF・ダナウェイが扮する。", "1988-01-01", "4,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("香蕉天堂／BANANA　PARADISE", "バナナ・パラダイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "1949年、中国国民党政府が樹立した台湾。この地を楽園と信じてやってきた中国人兵士二人が軍隊に入るものの、一人は拷問の果てに発狂。軍を脱走したもう一人は、すでに死んでいる他人になりすまして役所に就職し、困難を切り抜けながら出世街道を驀進する……。語ることがタブーとされてきた台湾の一時代を、痛烈な風刺とともに描いたシニカル・コメディ。", "1989-01-01", "8,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SL日和　大井川鐵道物語　山口線物語", "SL日和　大井川鐵道物語　山口線物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "時代をこえて愛され続ける蒸気機関車・SLを題材にしたドキュメンタリー。静岡県の大井川鐵道では、一度は姿を消しながらも復活を果たしたSLの雄姿を、復活に奔走した鉄道員の物語や、その志を受けついだ若き鉄道員たちの姿とともに映しだす。一方、山口県から島根県にかけて走る山口線では、日本の原風景の中を駆けぬける優美なSLの姿を、地元湯田温泉出身の詩人・中原中也の人生と詩、そしてSLを愛する沿線住人たちの思いを交えて描いていく。", "2010-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男たちのかいた絵", "男たちのかいた絵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "二重人格に悩むしがないヤクザが主人公という、筒井康隆ならではのブラックユーモアあふれる同名小説『男たちのかいた絵』の映画化。ケンカは下手だし度胸もない鶴丸杉夫。そんな彼がヤクザをやっているのは、不意に姿を見せるもう一人の自分・松夫のせいだ。松夫は凶暴でずる賢い。丹義組の幹部・渡会は、仕事を依頼する時、二人のどちらかを確認する。杉夫にはダサイ仕事と決めているのだ。ある時、丹義組と対立する岩動組が仕切ろうとしていた高いずみの公演を、本人の希望で丹義組が仕切ることになる。いずみに想いを寄せるようになる松夫。いずみを通して杉夫と一つの人格になれるかも……と思ったのもつかの間、杉夫を襲いにやって来た岩動組に松夫は敢然と立ち向かう。神代辰巳の最後の脚本でもある。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃中軒雲右衛門", "桃中軒雲右衛門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "明治時代に一世を風靡した実在の浪曲家・桃中軒雲右衛門の半生を描いた真山青果の同名原作を、成瀬巳喜男監督が映画化。雲右衛門の妻・お妻の存在に重点が置かれ、夫の出世に献身的に尽くす妻を描いた女性映画ともなっている。主演の月形は吹き替えなしで浪曲を実演、細川ちか子の抑制された演技も素晴らしく、翌年、成瀬夫人となる千葉早智子が華やかな彩りを添える。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聯合艦隊司令長官　山本五十六　太平洋戦争70年目の真実", "聯合艦隊司令長官　山本五十六　太平洋戦争70年目の真実", "", "", "http://image.eiga.k-img.com/images/movie/56481/315x210.jpg?3604", 140, "真珠湾攻撃によって太平洋戦争の端緒を開いた戦略家として知られる大日本帝国海軍の軍人・山本五十六の実像に迫るドラマ。在米日本大使館での勤務経験から国力の差を誰よりも把握し、米英を敵に回す日独伊三国同盟に反対していた海軍次官時代から、自らの意思に反して戦争を始めなくてはならなくなった苦悩の司令長官時代を経て、早期終戦による講和を目指しながら志半ばで命を落とすまでを描く。山本五十六役で役所広司が主演。玉木宏、香川照之らが共演。「八日目の蝉」の成島出監督がメガホンをとる。", "2011-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COCA−COLA KID", "コカコーラ・キッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "コカコーラ社のヤリ手営業マンが豪州でコークの売上げゼロ地帯を発見。なんとその地の領主がコークの類似品を製造していたのだ。“コカコーラ社とは一切関係ありません”というオープニングからラストまで、痛快でホロ苦い青春コメディ。異才マカヴェイエフは本作によって、日本で初めて正式に紹介された。ヒロインのG・スカッキが魅力的。", "1985-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("炬燵猫", "炬燵猫", "", "", "http://ecx.images-amazon.com/images/I/51cwV3APPUL._SL210_.jpg", 81, "人気アーティストのPVを数多く手がけてきた映像ディレクター・小川王子が映画監督に初挑戦し、擬人化したネコの目線から飼い主の少年の初恋の行方を描いたラブストーリー。こたつの中で気ままに暮らしている白ネコのシトと黒ネコのゼロ。そんな2匹の飼い主ジュニアは、引きこもりぎみな中学3年生の男の子だ。同じクラスの女の子に片思い中のジュニアの話題で盛りあがる2匹だったが、実はシトも密かに恋をしており……。", "2007-01-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Black Swan", "ブラック・スワン", "", "", "http://image.eiga.k-img.com/images/movie/55751/315x210.jpg?3879", 108, "ナタリー・ポートマン、ミラ・クニス共演の心理スリラー。ニューヨークのバレエ団に所属するニナ（ポートマン）は、元バレリーナの母とともに、その人生のすべてをダンスに注ぎ込むように生きていた。そんなニナに「白鳥の湖」のプリマを演じるチャンスが巡ってくるが、新人ダンサーのリリー（クニス）が現れ、ニナのライバルとなる。役を争いながらも友情を育む2人だったが、やがてニナは自らの心の闇にのみ込まれていく。監督は「レスラー」のダーレン・アロノフスキー。主演のポートマンが第83回米アカデミー賞で主演女優賞を獲得した。", "2011-05-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GAWIN", "ラストファンタジー 星降る夜の贈り物", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "広い宇宙のどこかにE.T.がいると信じている少年フェリクス。毎晩トランシーバーを使ってE.T.＝ガーウィンと交信することを夢見ていた彼を、不治の病が蝕んでいた。そんな我が子の願いを叶えようと、父親のニコラはガーウィンのぬいぐるみを着込み、遊園地から借りてきた宇宙船でフェリクスを宇宙へと旅立たせる……。J・H・アングラードが子供の夢を叶えようとする切ない親心を熱演する感動作。フェリクスの病を癒してやる謎の初老の男を演じるW・プショニャックが、印象深い演技を見せている。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青幻記・遠い日の母は美しく", "青幻記・遠い日の母は美しく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "吉田・大島・篠田ら“松竹ヌーヴェル・ヴァーグ派”の監督たちの代表作を手掛けた撮影監督・成島東一郎の初監督作品。一色次郎の同名小説の翻案で、30年ぶりに故郷を訪れた男の若くして死んだ母への追慕の情を、回想形式で描く。舞台となった沖永良部島の風景が美しい。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAME", "メイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "ブロードウェイで大ヒットしたミュージカルの映画化。父と死別した少年がニューヨークのメイム叔母さんを訪ねてやって来る。しかし、この叔母さん、珍行奇行で有名な変わり者だった。はじめは戸惑った少年だが、やがて自由で大らかな叔母さんに共感し、人生の楽しみ方を少しずつ学んでいく。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cobu", "コブ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "5,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MERY PER SEMPRE", "永遠のマリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "シシリー島の少年院に赴任した教師マルコは、そこで少年非行の実態を垣間見る。麻薬、売春、暴力、略奪、同性愛など、現代社会のダークサイドでしか生きることができない少年たち。そんな彼らとマルコの葛藤とふれあいを、リアルなタッチで描き出す。モントリオール映画祭審査員大賞を受賞。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BACKSTREET DREAMS", "バックストリート・ドリームズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "裏街のチンピラ、ディーンとアップタウンの女子医大生、スティービー。およそ接点のない二人が恋に落ちるという青春ラブ・ストーリー。スティービー役には、B・シールズを起用。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金瓶梅", "金瓶梅", "", "", "http://image.eiga.k-img.com/images/movie/55211/315x210.jpg?1337", 93, "「三国志演義」「水滸伝」「西遊記」と並ぶ中国四大奇書の1つ「金瓶梅」を映画化。監督は香港官能映画界の巨匠チン・マンケイで、日本のAV女優も多数出演している。宋の時代。大富豪の家庭に生まれた西門慶は、幼くして性に目覚め、父親からさまざまな色道指南を受けて育つ。青年へ成長した彼は、性の教育係であった紫煙との初体験を済ませると、処女の尼僧・明月を犯すなど、次々と女性遍歴を重ねていく。", "2010-02-13", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("She, A Chinese", "中国娘", "", "", "http://image.eiga.k-img.com/images/movie/56811/315x210.jpg?1292", 98, "カンヌ、ベルリン、ベネチアの世界3大映画祭の受賞作を中心に、各地の映画祭で話題になりながらも日本未公開だった作品を一挙上映する「三大映画祭週間2011」にて上映。山奥の村から重慶に出てきたメイだったが、夢見ていた都会生活とはほど遠く、ヤクザのスパイキーと恋仲になるもスパイキーは何者かに殺されてしまう。追手から逃れるためロンドンへ渡ったメイは、そこでハントという老人と出会うが退屈な生活に嫌気がさし、小さなレストランを経営するインド人移民リチャードのもとに転がり込むが……。作家としても活動しているグオ・シャオルー監督作。2009年・第62回ロカルノ国際映画祭金豹賞を受賞。", "2011-08-13", "3,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("たそがれ清兵衛", "たそがれ清兵衛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "山田洋次監督が、藤沢周平の原作をもとに時代劇を初演出。幕末の庄内地方の小藩で“たそがれ清兵衛”とあだなされる冴えない武士が、実は剣の腕がたつことが世に知れたことから、藩命で上意討ちの討ち手に選ばれてしまう。綿密に考証された時代設定、リアルな殺陣、そして現代劇ではもはや表現不可能かもしれない“日本人の心”の美しさが、清麗に描かれている。真田広之と宮沢りえの切ない恋模様も、観る者の胸を深く打つ。冨田勲のストイックかつ切れ味鋭い音楽など、スタッフワークの素晴らしさも特筆もの。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　DALAI　LAMA", "ダライ・ラマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ノーベル平和賞に輝くチベットの指導者ダライ・ラマに関するドキュメンタリー。チベットとの人権問題が世界的な論議を呼んでいる中国が自らの見解を示した作品で、世界各国150以上のTV局で放映された後、日本では劇場公開された。ダライ・ラマ本人を知る関係者やチベット人学者へのインタビュー、毛沢東との会見など、史料的な価値の高い映像を多数収録している。", "1997-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京オアシス", "東京オアシス", "", "", "http://image.eiga.k-img.com/images/movie/56371/315x210.jpg?4539", 83, "「かもめ食堂」「めがね」の小林聡美主演によるアンソロジードラマ。東京を舞台に、女優トウコの小さな逃避行と人々とのほのかな交流を描く。撮影現場から逃げ出した女優トウコは、コンビニで出会った見ず知らずの男ナガノの車に乗せてもらう。ある映画館では、以前一緒に仕事をしたことのあるキクチに声をかけられ、人気のない動物園では、浪人生活が続き落ち込んでいるヤスコに思わず声をかける。共演は加瀬亮、黒木華、原田知世。監督は「マザーウォーター」の松本佳奈と新鋭・中村佳代。", "2011-10-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニンゲン合格", "ニンゲン合格", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "黒沢清が初めて“家族”というテーマに取り組んだ一編。と言っても、鬼才として名高い彼が独特のリアリズムを込めて撮った作品だけに、家族の愛や絆を高らかにうたう通常の家族劇からはかけ離れている。交通事故で昏睡状態に陥った吉井豊が、10年ぶりに病院で目を覚ます。とりあえず郊外の実家に帰ってみると、そこには家族は誰もおらず、父の友人と称する中年男の藤森が気ままに釣り堀を経営していた。何もかもがピンとこない豊は、ある日、一頭の馬がさ迷い込んできたことをきっかけに、かつて家にあったポニー牧場を再建しようとする。“崩壊もしなければ再生もしない”という監督の言葉通り、離散していた両親や妹が主人公の元に集まり、またいつの間にかバラバラに散っていく不思議なようでリアルな家族の光景を描いていく。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Coco Chanel & Igor Stravinsky", "シャネル＆ストラヴィンスキー", "", "", "http://image.eiga.k-img.com/images/movie/54611/315x210.jpg?1351", 119, "1913年のパリ。シャンゼリゼ劇場でロシアの作曲家イゴール・ストラビンスキーが音楽を手がけるバレエ「春の祭典」を鑑賞したココ・シャネルは、その革新的な音楽に心を打たれる。だが、内容が急進的すぎたため、ストラビンスキーの才能が認められることはなかった。7年後、莫大な富と名声を手にしたシャネルは、難民となったストラビンスキーに別荘を提供する……。主演に「シャネル」のミューズを務めるアナ・ムグラリスと「007／カジノ・ロワイヤル」のマッツ・ミケルセン。監督は「ドーベルマン」のヤン・クーネン。", "2010-01-16", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARIE", "裸足のマリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ブリュッセルからフランスを抜け、ピレネー山脈を越え、スペインそしてポルトガルへ、マリーは７歳の少年トニオを連れて旅する。南下するにつれ、明るさを増す太陽の光。問題を抱え絶望していたマリーだが、次第に強烈な太陽の日差しに勝るとも劣らない生のエネルギーに輝き始める。旅を絡めて綴られる少女のひと夏の変化、大人への脱皮。監督のM・ハントベルカーがこの作品までの９年間に取り組んだというドキュメンタリー＝フィクションの手法が生かされた、青春ロード・ムービーの秀作だ。パリ映画祭でグランプリと主演女優賞を受賞。", "1993-01-01", "2,37,25", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵コナン／　ベイカー街〈ストリート〉の亡霊", "名探偵コナン／　ベイカー街〈ストリート〉の亡霊", "", "", "http://ecx.images-amazon.com/images/I/61zf%2BSpK3cL._SL210_.jpg", 107, "人気アニメの劇場版第６作。江戸川乱歩賞受賞作家、野沢尚が脚本を手掛け、19世紀末のロンドンで怪事件に挑む名探偵コナンの活躍を描く。ゲーム業界に革命をもたらした体感ゲーム“コクーン”に乗り込んだコナンが、仮想世界に取り残されてしまう。そこは、シャーロック・ホームズが活躍した100年前のロンドン。コナンはゲームに参加していた50名の命を懸けて、連続殺人鬼ジャック・ザ・リッパーの正体に迫る。", "2002-04-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISSION KILL", "必殺コマンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "海兵隊の爆破専門家だったクーパーは、独裁政権下の中南米のある国で、友人が殺されたことから反政府ゲリラに協力することになる。主人公の活躍を、女ゲリラとの恋も絡めて描いたアクション。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Red Dragon", "レッド・ドラゴン", "", "", "http://image.eiga.k-img.com/images/movie/1611/315x210.jpg?2637", 125, "FBI捜査官グレアムはレクターを逮捕した後、引退していたが、一家全員を惨殺し、眼球に鏡の破片を突き刺す猟奇殺人事件が発生、元上司に捜査への協力を求められて、獄中のレクターに会いに行く。トマス・ハリスが「羊たちの沈黙」の前に書いた同名小説を映画化。レクター博士役は前2作と同じアンソニー・ホプキンス。脚本は「羊たちの沈黙」と同じテッド・タリー。監督は「ラッシュアワー」シリーズのブレット・ラトナー。", "2003-02-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コクリコ坂から", "コクリコ坂から", "", "", "http://image.eiga.k-img.com/images/movie/55911/315x210.jpg?2424", 91, "「なかよし」（講談社刊）に連載された高橋千鶴・佐山哲郎による少女漫画をスタジオジブリが映画化。宮崎駿が企画・脚本、「ゲド戦記」の宮崎吾朗が同作以来5年ぶりに手がける監督第2作。1963年の横浜、港の見える丘にあるコクリコ荘に暮らす16歳の少女・海は毎朝、船乗りの父に教わった信号旗を海に向かって揚げていた。ある日、海は高校の文化部部室の建物、通称「カルチェラタン」の取り壊しに反対する学生たちの運動に巻き込まれ、そこで1学年上の新聞部の少年・俊と出会う。2人は徐々にひかれあっていくが……。海役に長編劇場アニメ声優初挑戦の長澤まさみ。俊役は「ゲド戦記」に続き2度目のジブリ作品参加となる「V6」の岡田准一。", "2011-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("照明熊谷学校", "照明熊谷学校", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "1958年以来、名照明マンとして日本映画を支えてきた熊谷秀夫にスポットライトを当てたドキュメンタリー。鈴木清順の「東京流れ者」や長谷川和彦の「太陽を盗んだ男」、相米慎二の「魚影の群れ」など伝説的な作品を手掛けた職人の“仕事”を、「透光の樹」の根岸吉太郎監督らの声を交えて俯瞰する。", "2004-12-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRAGNET", "ドラグネット／正義一直線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ロサンゼルスを舞台に、“ペガン事件”を追うチグハグ刑事コンビのハチャメチャ捜査を描いたポリス・コメディ。堅物エイクロイドと、飛んでる新人類ハンクスの噛み合わないテンポ、次から次へ機関銃のごとく飛び出てくるギャグと笑いのネタはつきない。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("For Love of the Game", "ラブ・オブ・ザ・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/1861/315x210.jpg?4429", 138, "ビリー・チャペルは名門デトロイト・タイガースの右腕エース。しかし既に40歳、腕に肩に痛みと衰えを感じていた。今日の試合は、対戦相手のヤンキースには優勝がかかった一戦。だがタイガースには消化試合だ。しかしチャペルはこのゲームを感無量 の思いで迎えていた。その日の朝、5年間愛を育んで来た恋人のジェーンには別 れを、さらにチームのオーナーからはトレードを通告されたばかりだったのだ。", "2000-01-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　爆竜戦隊アバレンジャー DELUXE アバレサマーはキンキン中！", "劇場版　爆竜戦隊アバレンジャー DELUXE アバレサマーはキンキン中！", "", "", "http://ecx.images-amazon.com/images/I/21FQ2FHMSWL._SL210_.jpg", 30, "超進化を遂げた恐竜＝爆竜とともに戦う若者たちの活躍を描いたスーパー戦隊シリーズ「爆竜戦隊アバレンジャー」の劇場版。夏休みにビーチへやって来た凌駕たちの前に現われた謎の美少女。その正体は、かつて古代ダイノアースを恐怖の爆竜カルノリュータスとカスモシールドンから救った伝説の王女フリージアだった。封印されていた2匹の爆竜が解き放たれたと知ったアバレンジャーたちは、再び封印の儀式を行なうべく行動を開始する。", "2003-08-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裁判長！ここは懲役4年でどうすか", "裁判長！ここは懲役4年でどうすか", "", "", "http://image.eiga.k-img.com/images/movie/55641/315x210.jpg?4736", 95, "裁判ぼう聴ブームを巻き起こした北尾トロのベストセラー小説を、お笑いコンビ「バナナマン」の設楽統主演で映画化。“愛と感動の裁判映画”の脚本を依頼された三流ライターのタモツは、取材のため生まれて初めて裁判所を訪れる。ところが実際に裁判をぼう聴してみると、その内容は映画のテーマとはかけ離れたものばかり。やがてタモツは、裁判所で出会ったぼう聴マニアたちと行動をともにするようになる。「ソフトボーイ」の豊島圭介監督がメガホンをとる。", "2010-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あすも夕やけ", "あすも夕やけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "児童映画の分野で活躍してきた神山征二郎が、埼玉文化協会活動の一環として手掛けた意欲作。彼の監督協会新人賞受賞第１作となった。捨て子の時に拾って育ててくれた男に連れられて飯場を転々としている少女が、埼玉県の新興住宅地を舞台に、地元の対立する子供たちを仲よくさせていく様子を描く。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STOLEN HEARTS", "サンドラ・ブロックの恋する泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "恋人フランクの泥棒を手伝いながら幸せな結婚を夢みるロズ。ある屋敷から一枚の絵画を盗んだ二人は、引き渡しのためにニューイングランドの美しいリゾート地で週末を過ごすことになる。ロマンティックなロケーションでのプロポーズを期待するロズだが、二人にはFBIの捜査の手が迫って来る。結婚に憧れるキュートな泥棒の恋の行方を描いたラブ・コメディ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Mome", "エディット・ピアフ　愛の讃歌", "", "", "http://image.eiga.k-img.com/images/movie/34171/315x210.jpg?3526", 140, "「愛の讃歌」「ばら色の人生」など、数々の名曲を残したフランスの国民的シャンソン歌手、エディット・ピアフ。その波乱に満ちた47年間の生涯を描き、フランスで500万人以上を動員。ピアフになりきった主演マリオン・コティヤールは、第80回アカデミー賞主演女優賞受賞。1915年、第1次大戦中の貧しい家庭に生まれ、祖母の娼館に預けられて育ったピアフは、路上で歌って日銭を稼いでいたところを見出され、プロデビュー。瞬く間に人気歌手へと成長するが……。", "2007-09-29", "2,43,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("God Bless Ozzy Osbourne", "オジー降臨", "", "", "http://image.eiga.k-img.com/images/movie/57281/315x210.jpg?2597", 94, "ヘビーメタル界のカリスマ、オジー・オズボーンに密着したドキュメンタリー。「ブラック・サバス」のボーカルとして活動していた当時の逸話やアルコール依存症に苦しんだ日々についてオズボーン本人が語るインタビューのほか、ライブパフォーマンスやバックステージ、楽屋での姿を撮影したオフショットなど、公私にわたる貴重な映像が盛り込まれている。監督はオジーの末っ子のジャック・オズボーン。", "2011-12-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHISKY", "ウィスキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "カンヌ映画祭をはじめ各国の映画祭でアキ・カウリスマキ作品を思わせる作風が“南米版アキ・カウリスマキ”と絶賛された、ユニークな人間ドラマ。弟への見栄から偽装結婚した初老の男と、彼が経営する靴下工場で助手として働き、“妻”となった中年女性。二人の姿を、微動だにしないカメラが見守り、不思議な温かさを喚起する。小さな靴下工場を運営するハコボは、弟エルマンの突然の来訪に備えて、助手のマルタと偽装結婚することに。彼女はなぜか了承し……。", "2005-04-29", "75,22,7,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Matador", "マタドール＜闘牛士＞・炎のレクイエム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "スペインの鬼才ペドロ・アルモドバル監督が、まだブレイク前のアントニオ・バンデラス主演で手掛けたサスペンス作品。事故で引退したかつての人気闘牛士モンテスの元で学ぶ青年アンヘル。彼は自分の男らしさを誇示するため、未解決の殺人事件の犯人として警察に名乗り出る。しかしその殺人事件は、闘牛中の死と隣り合わせの緊張感が忘れられず、殺人にしか快楽を見出せなくなってしまった彼の師、モンテスによる犯行だった……。", "1986-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ARRANGEMENT", "アレンジメント／愛の旋律", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "E・カザンが、自らの同名小説を製作・監督・脚本した「アメリカ　アメリカ」の後日譚ともいうべき野心作。ロサンゼルス広告業界の切れ者エディは、ある自動車事故をきっかけに、人生を一変させる。無口になり、自分だけの世界に閉じこもるようになった彼は、やがて地位も名声も捨てた生活を望むようになる。当時、大胆なセックス描写が話題となった。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極つぶし", "極つぶし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "極道上がりの若いハミダシ刑事・犬神京太郎と、元警官で今は昔気質のヤクザに身を落とした花輪兆治の不思議な出会い。法の網の目をくぐって悪の限りを尽くす巨大犯罪組織に立ち向かうため、警察上層部から秘かに“極悪人潰し”を命じられた、通称“つぶしコンビ”が大暴れするさまを描く痛快アクション。木村一八と安岡力也の共演による同名人気漫画の映画化。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUELLE JOIE DE VIVRE", "生きる歓び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "クレマン＆ドロンのコンビで「太陽がいっぱい」と同じ年に作られた作品。舞台は1920年代のローマ。何も知らずにファシスト党に入ってしまった青年が、手配中のアナーキストと間違われたことから次第に正義に目覚めていく。エスプリの効いたコメディ・タッチで政治を描いた佳作。", "1960-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山椒大夫", "山椒大夫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "平安時代の末、越後を旅していた母子連れは、人買いにだまされて引き離されてしまう。安寿と厨子王丸の二人の子供は、丹後の大地主・山椒太夫の荘園に奴隷として売られ、毎日苛酷な労働に苦しめられる。山椒太夫のもとで10年間を耐え忍んだ姉弟はある日逃げ出す決心をして荘園を出るが、追っ手が迫り、安寿は弟を救うためにおとりとして池に身を投げる。厨子王丸は逃げ延びて国守となり、母を求めて佐渡に赴く……。森鴎外の小説で有名な話を溝口健二が重厚な演出で描いた秀作。ラストのパン撮影で捉えられた海のシーンは息をのむほど美しく、ジャン・リュック・ゴダールがのちに「気狂いピエロ」のラストで再現した。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TINTIN ET LES ORANGES BLEUES", "タンタンと水色のオレンジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "日本でも人気の絵本『タンタンの冒険旅行』を実写映画化した作品の中の１本。冒険好きの少年タンタンが活躍する。TV番組で世界規模の飢饉の脅威を訴えたターンソル教授は、スペインに住む科学者ザラメア氏から青色に発光する不思議なオレンジを受け取った。飢饉回避のカギを握る“ブルーオレンジ”だったが、一夜にして何者かの手によって盗まれてしまう。事件を解決すべくスペインに向かったタンタンたちだったが、そこにはザラメア氏の姿はなく、ターンソル教授まで誘拐されていた……。", "1964-01-01", "2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE MARGINAL", "パリ警視J", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "敏腕でありながら、その型破りな捜査方法のために“ならず者”と呼ばれている一匹狼の警視J。彼がフランス最大の犯罪シンジケートを相手に大暴れする、ハードボイルド・アクション。さながらフランス版「ダーティハリー」といったところ。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本任侠道・激突編", "日本任侠道・激突編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "市蔵は参次郎とはその妹を恋女房にした義兄弟の間柄だったが、八王子の三業地建設をめぐり二人の仲は険悪になり、やがて血で血を洗う骨肉の争いに発展していく。この作品の興行的失敗により東映任侠映画は事実上の終結を迎えた。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋の罪", "恋の罪", "", "", "http://image.eiga.k-img.com/images/movie/56001/315x210.jpg?3841", 144, "「愛のむきだし」「冷たい熱帯魚」の鬼才・園子温監督が、水野美紀、冨樫真、神楽坂恵を主演に迎え、実在の事件をもとに描く愛の物語。21世紀直前に起こった、東京・渋谷区円山町のラブホテル街で1人の女性が死亡した事件を軸に、過酷な仕事と日常の間でバランスを保つため愛人を作り葛藤（かっとう）する刑事、昼は大学で教え子に、夜は街で体を売る大学助教授、ささいなことから道を踏み外す平凡な主婦の3人の女の生きざまを描く。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES BIDASSES EN FOLIE", "クレイジー・ボーイ 突撃大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "飛びきりクレイジーな５人組が、ロック・グループを結成。ある楽器店の女主人の後押しを得て、ポップス・コンクールの地区予選で見事優勝する。そんな時、彼らのもとに召集令状が届くが、入隊後も５人組のハチャメチャぶりはまだまだ続く。", "1971-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　GENERAL’S　DAUGHTER", "将軍の娘／エリザベス・キャンベル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "ベストセラー作家N・デミルの小説を、「トゥームレイダー」のS・ウエストが映画化したサスペンス。ジョージア州陸軍マッカラム基地で、女性大尉エリザベス・キャンベルが全裸死体となって屋外に打ち捨てられていた。付近には50もの足跡と指紋が残されていたが、レイプの跡も抵抗した痕跡もない。そのうえ、彼女はこの基地を指揮する将軍ジョー・キャンベルの娘だった。陸軍のC.I.D（犯罪捜査部）の敏腕捜査官ポール・ブレナーは、将軍からFBIが動き出す36時間以内に解決せよと命じられる。彼はエリザベスが基地内のほとんどの男性と関係を持っていたことを突き止めるが……。閉ざされた軍という組織の中で行われるセクハラ、レイプの問題を踏まえながら、緊迫の謎解きが展開する。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Starship Troopers: Invasion", "スターシップ・トゥルーパーズ　インベイジョン", "", "", "http://image.eiga.k-img.com/images/movie/57871/315x210.jpg?2639", 0, "ロバート・A・ハインラインの名作SF小説「宇宙の戦士」を原作に、昆虫型宇宙生命体バグと人類の壮絶な戦いを描いた「スターシップ・トゥルーパーズ」（1998）の製作15周年を記念したフルCGアニメーション。監督は「APPLESEED」「エクスマキナ」の荒牧伸志。バグに強襲され占拠された地球連邦の戦艦が、地球へと進路をとっていた。パワードスーツを身にまとい戦う精鋭のトゥルーパーを乗せたアレジア号は、その侵略を阻止するため立ち向かうが……。シリーズ第1作の世界観を踏襲し、主人公のジョニー・リコや女性パイロットのカルメンらおなじみのキャラクターも登場。", "2012-07-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’E´TE´ DE NOS QUINZE ANS", "新 ラ・ブーム 15才の夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "バカンス・シーズンのドービル。14歳の美少女マレーヌと、その幼な友だちの交流。彼女をボート事故から助けてくれた年上の青年と伯母の愛。様々な思いの中で少女は15歳を迎える。典型的な思春期映画の１本。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エデンの園", "エデンの園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "金持ちの娘アレッサンドラと貧しい家の育ちのミケーレは互いに愛し合っていたが、身分の差が原因でアレッサンドラの家族から交際を禁じられる。やがて二人の夢は悲劇的な結末を迎える。増村保造監督の日・伊合作映画だが、出演者はすべてイタリア人で、撮影もイタリアで行われた。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIAGGIO IN ITALIA", "イタリア旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ネオ・レアリスモの巨匠として不滅の名を残すロッセリーニの代表作。ヌーヴェル・ヴァーグの父としてゴダール、トリュフォーらが絶大な敬意を表していたのがロッセリーニだが、本作は彼らの牙城だった『カイエ・デュ・シネマ』誌が選ぶ映画史上の10本の常連といえる存在。ゴダールも「勝手にしやがれ」が「イタリア旅行」の影響下に生まれたことを認めている。ロンドンに住む危機に頻した夫婦がイタリアを旅行中に決定的な破局を迎える。しかし、イタリアの古代美術や遺跡が個人の営みの卑小さを彼らに教え、敬虔な祭りの行列の中で二人は再び結ばれる。観光映画さながらにベスビオス火山、ポンペイの遺跡と名所めぐりを行いながらも、生々しい感情の揺らぎを巧みにすくいとるロッセリーニの演出が傑出しており、レアリスモの言葉に真に値する名作といえる。不幸な結末に終わったバーグマンとロッセリーニの愛が残した幸福感漂う作品。", "1953-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE MAN’S JUSTICE（ONE GOOD COP）", "ニューヨーク・ジャスティス～許された犯罪～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ニューヨークの刑事アーティーは、夫婦円満、相棒のスティーブとも８年間コンビを続ける順調な毎日を送っていた。だが、捜査中スティーブが射殺されてから、アーティーの生活は狂い始める。スティーブの３人の子を引き取ったアーティーは、狭いアパートでの苦しい生活を余儀なくされ、ついに麻薬ディーラーの大金を横取りしてしまう。M・キートンが、犯罪に走った刑事の心の葛藤を繊細に表現している。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bez końca", "終わりなし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「トリコロール」三部作で知られるポーランドの巨匠クシシュトフ・キエシロフスキーの初期監督作品。夫アンテクを突然亡くしたウラは、その現実を受け入れられず、日々アンテクの気配を感じていた。そんなある日、彼女は自分の知らない夫の過去を知る。主演は、グラジナ・シャポーフスカ。日本では、キエシロフスキー監督没後7年にあたる2003年に劇場初公開された。", "", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Get Rich or Die Tryin'", "ゲット・リッチ・オア・ダイ・トライン", "", "", "http://image.eiga.k-img.com/images/movie/1401/315x210.jpg?3067", 117, "「父の祈りを」「イン・アメリカ　三つの小さな願いごと」の名匠ジム・シェリダンが、ヒップホップ界のスーパースター“50セント”の半自伝的物語を、50セント本人の主演で映画化。ニューヨーク・クイーンズを舞台に、父親を知らず、母親も亡くした少年の成長と挫折、そしてヒップホップ界のスターとして羽ばたくまでを描く。共演は「クラッシュ」のテレンス・ハワードほか。", "2006-06-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私の兄さん", "私の兄さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "林長二郎こと長谷川一夫と田中絹代の共演が話題を呼んだ現代劇。ヤクザ者の文雄がひょんなことから深夜、須磨子という富豪令嬢を車に乗せて街を走るハメに陥る。それは文雄にとってヤクザ世界から足を洗うきっかけとなるのだった。典型的な松竹蒲田調の、島津保次郎が得意とするジャンルの作品で、フランク・キャプラの「或る夜の出来事」の影響が指摘されている。", "1934-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONSIEUR RIPOIS", "しのび逢い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "リポアは妻のキャスリンと離婚寸前。彼女の留守に現れた妻の親友パトリシアに昔の女の話をきかせ口説きにかかるが失敗、狂言自殺をはかるが実際に転落してしまう……。女たらししか能のないサラリーマン、リポアをめぐる恋愛喜劇。", "1954-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Midnight in the Garden of Good and Evil", "真夜中のサバナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 155, "", "1998-07-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道VS不良番長", "極道VS不良番長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "一家を解散し大日本ホルモン焼株式会社を設立した極道親分こと島村清吉は、岐阜の柳ケ瀬で新宿カポネ団こと不良番長グループとバッタリはち合わせする。当時人気のあった梅宮辰夫の“不良番長”を合体させた作品。だが、二つの人気シリーズを掛け合わせても面白さは残念ながら２倍にはならなかった。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APOCALYPSE: A REQUIEM FOR THE SIXTIES", "APOCALYPSE:A REQUIEM FORTHE SIXTIES", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "「TONITE！LET'S ALL MAKE LOVE IN LONDON」のP・ホワイトヘッドによる、もう一つのサブカルチャー・ドキュメンタリー。E・バードンやM・ジャガーほかのミュージシャンから、M・ケインら若手俳優、D・ホックニーといったアーティストまで、当時の若きカリスマが世代論について言及。彼らの発言から、体制への怒りがみなぎっていた当時の風潮が垣間見える。", "1977-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("セカンドバージン", "セカンドバージン", "", "", "http://image.eiga.k-img.com/images/movie/56621/315x210.jpg?1674", 105, "2010年にNHKで放送された同名人気ドラマの劇場版。ドラマ版から設定を新たにし、マレーシアのクアラルンプールを舞台に、離婚経験のある45歳の敏腕出版プロデューサーの中村るいが、17歳年下で既婚者のネット証券会社社長・鈴木行との不倫におぼれていく姿を描く。主演はドラマ版から続投の鈴木京香、長谷川博己。鈴木の妻役に深田恭子。スタッフもドラマ版と同じく大石静の脚本で、黒崎博監督がメガホンをとる。", "2011-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すんドめ2", "すんドめ2", "", "", "http://image.eiga.k-img.com/images/movie/53631/315x210.jpg?3393", 95, "岡田和人による人気コミック「すんドめ」の実写映画化第2弾。サディスティックな美少女・胡桃の精神的な奴隷になることに快感を見出すようになった英男は、彼女の気を引くために全裸事件まで起こしてしまう。英男が部長を務める“浪漫倶楽部”は廃部に追い込まれそうになるが、胡桃がアメフト部のマネージャーとして引き抜かれることでその危機を救い……。鈴木茜、二宮敦、次原かなら前作のキャストが引き続き出演する。", "2008-05-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dr.ピノコの森の冒険", "Dr.ピノコの森の冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 7, "手塚治虫の名作漫画「ブラック・ジャック」に登場する人気キャラクター、ピノコが活躍する短編アニメ。ブラック・ジャックに忘れ物を届けようと森に向かったピノコとラルゴは、その途中で迷子になってしまう。やがてケガした動物たちと出会ったピノコは、ブラック・ジャックに代わって動物たちの治療を開始する。女優の竹下景子がナレーションを務める。「ブラック・ジャック　ふたりの黒い医者」の同時上映作品。", "2005-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CAR", "ザ・カー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "無人の殺人カーが暴走を始め、次々と人を襲うというサスペンス・スリラー。殺人カーは“リンカーン・マーク IIIシャーシー”を８週間かけて改造したもの。この映画の“主役”にふさわしい出来栄えとなり、無気質な恐怖感を生みだすことに成功している。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOURSWEET", "サワースィート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "M・ニューウェル監督、香港・台湾映画界のトップ女優シルヴィア・チャン主演で製作された、異色イギリス映画。香港で結婚式を挙げたリリーとチャンは、やがてロンドンに移民する。リリーはこの地でレストランを開くことを夢みているのだが……。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIRDS", "バード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "北朝鮮と韓国、二つの国に別れて暮らさざるを得ない親子の再会のドラマを、実話に基づいて映像化。80歳を超える高齢ながら、鳥類の研究を続ける北朝鮮の学者ユンが見つけたシベリアムクドリ。足に日本製のリングを付けたこの鳥は、実は朝鮮戦争で生き別れたユンの息子が、韓国から放ったものだった。", "1992-01-01", "48", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPACECAMP", "スペースキャンプ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "NASAの基地へ最新鋭設備を駆使したカリキュラムを体験するためにやって来た少年たち。ところが、実習で乗り込んだシャトルが本当に発射し、彼らは宇宙へ向けて旅立ってしまう。NASAの全面協力と見事なSFXを駆使して描かれたスペース・アドベンチャー。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やさいのようせい N.Y.SALDA", "やさいのようせい N.Y.SALDA", "", "", "http://image.eiga.k-img.com/images/movie/55301/315x210.jpg?3328", 65, "NHK教育テレビで放送された人気アニメーションを、劇場版3Dで映画化。テレビアニメーションのリマスター版3話（15分）と、オリジナルストーリー3D版（15分）の二部で構成される。満月の光が差し込むニューヨークのキッチンを舞台に、魔法をかけられた芽キャベツやレタスや白ナスなど、野菜の妖精たちが繰り広げる冒険を描く。原作は「ファイナルファンタジー」のキャラクター・デザインで知られる天野喜孝、ナレーション・声優を原田知世が務める。", "2010-02-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トカゲ飛んだ？", "トカゲ飛んだ？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "ペットをめぐる人間模様を題材に6人の監督が競作した「PETBOX ペットボックス」シリーズの一作で、「解夏」「がんばっていきまっしょい」の磯村一路監督が撮りあげたハートウォーミングなラブコメディ。女子高生の珠子は、先輩の民生に片思い中。カナヘビに夢中の民生を、なんとか振りむかせようと奮闘するが……。主演は「海と夕陽と彼女の涙　ストロベリーフィールズ」の東亜優。共演に「カナリア」の石田法嗣。", "2006-11-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOWDOWN AT BOOT HILL", "決闘!! ブーツヒル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "C・ブロンソンの西部劇主演第１作。カンザス州のとある町に、連邦保安官ルーク・ウェルシュがやって来た。狙いはお尋ね者のコン・メイナーの逮捕。食堂で給仕するサリーをからかうメイナーを見つけ、首尾よく撃ち殺す。しかし、ルークが賞金稼ぎだったとわかり町全体がルークと敵対し始めた。サリーも証言を拒否、スローンを中心とする一団が暴徒と化してルークを襲う。“ブーツヒル”とは町外れの墓地のこと。名もないカウボーイの墓には、墓標の代わりにブーツを立てた習慣からきた呼び名だ。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿寒に果つ", "阿寒に果つ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "天才画家ともてはやされた18歳の娘が、自己陶酔に溺れ、やがて自ら死を選んでいくまでを描いた青春映画。セリフのクサさはちょっとやりきれないが、五十嵐じゅん（現・淳子）の清々しい魅力や、彼女と二宮さよ子とのレズ・シーンなどが清冽に映像化されている。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRACULA: DEAD AND LOVING IT", "レスリー・ニールセンのドラキュラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "M・ブルックス、L・ニールセンというパロディー映画のスター監督と俳優がコンビを組んだギャグ満載のホラー・コメディ。トランシルバニアの血を吸いつくしたドラキュラは、ロンドンに移り住む。ドラキュラは、隣にある精神病院の院長の娘ミナに目を付け、しつこく追い回し始める。逃げ回っていたミナだったが、とうとうドラキュラの毒牙にかかり、淫乱な女に生まれ変わってしまうのだった。ドラキュラの正体に気が付いた吸血鬼学の権威であるバン・ヘルシング教授が、その正体を暴こうとするが、ドラキュラは手強かった。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HERE ON EARTH", "愛ここにありて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "2000年版「ある愛の詩」とも評されたラブ・ストーリー。ガンのため、余命１、２年と宣告されつつも素直に生きる少女サマンサをL・ソビエスキーが熱演。サマンサをめぐる幼なじみとエリート学生との三角関係を絡めながら、難病ものにありがちな暗さを払拭。さわやかな青春映画に仕上げている。C・クライン、J・ハートネットら若手スターの共演も話題を呼んだ。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超淫力 絶頂女", "超淫力 絶頂女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "競馬に明け暮れるヒロシの恋人・サチコは、絶頂を迎えると予知能力を発揮する。サチコの力を利用して、ひともうけしようとたくらむヒロシは、トイレに連れ込みセックスをする。彼女の頭に最終レースの情景が浮かんできて……。数々のピンク映画で才能を発揮した中村幻児監督のエロティック・ドラマ。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWIN SISTERS", "ツイン・ターゲット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "姉、キャロルは実業家と結婚、妹のリンは高級コールガール。双子姉妹は正反対の生活を送っていた。リンは、投資家のブーンの財産を横領しようとたくらむが失敗、逆にブーンに命を狙われるハメになる。一方、異常を知ったキャロルは、リンの行方を探し始めるのだが……。境遇の違う二人の姉妹の複雑な心情を絡めて、事件は意外な展開を見せていく。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUNAWAY", "未来警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "SF作家クライトンがメガホンを執ったSFXアクション。ロボットの氾濫する未来社会。ロボットとコンピュータを駆使して犯罪をたくらむ男と、警察のロボット犯罪担当“ランニング・スカッド”チームの戦いを描く。生物のように迫りくるロボット・スパイダーなど、リアルなメカ・ウエポンが不気味。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレオパトラ", "クレオパトラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "21世紀の世界から３人の男女が、その精神を過去へ転送。紀元前30年のエジプトで、奴隷や侍女に取り憑いて、クレオパトラの時代の歴史を見極めようとする。ところがクレオパトラはとんでもない醜女で、エジプトに伝わる秘術により絶世の美女に変身。ローマから遠征してきたシーザーを、そして彼亡きあと実権を握ったアントニウスを、その恋の虜にしてしまう……。「千夜一夜物語」に続いて虫プロダクションが作り上げた劇場用大作“アニメラマ”の第２作。小島功の独特のエロチシズムを放つキャラクターをアニメに移し変え、ローマ軍とエジプト軍の激突、大海戦などを、デフォルメをいかした作画で描ききった意欲作となった。一方でモブ・シーンの群衆にまざって、ピカソなどの名画の人物が現れたり、オバQなど当時のアニメ、マンガの主人公が随所に顔を出すなど、意図的な脱線を狙った部分も多く、監督・原案の手塚治虫の持ち味が、良きにつけ悪しきにつけ発揮されている。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ABOTT AND COSTELLO MEET FRANKENSTEIN", "凸凹フランケンシュタインの巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "1940年の「Back Private」（日本未公開）から本格的にシリーズ化され、14年間にわたってユニバーサル映画のドル箱として31本の作品に主演したアボット＆コステロの大ヒット作。今回はドラキュラ、フランケンシュタイン、狼男といったユニバーサルの人気キャラクターが続々と登場して恐怖の笑いを巻き起こす。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOGKESELYU", "ザ・バルチャー 哀しみの叛逆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "今までほとんど目に触れることのなかった、社会主義国の上流社会の腐敗を描き、1983年のベルリン映画祭で観客投票一位に選ばれた力作。上流階級に成り上がりながら、あこぎな手段で金を奪う女スリに、有り金全部盗まれたタクシー運転手が復讐を開始する……。", "1983-01-01", "30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Batman", "バットマン", "", "", "http://ecx.images-amazon.com/images/I/51jOwXqtBJL._SL210_.jpg", 126, "ボブ・ケイン原作のアメリカン・コミックスを、「シザーハンズ」のティム・バートンが映画化。架空の都市ゴッサムシティで、犯罪者を退治するヒーロー、バットマン。女性カメラマンが彼の正体を突き止めようと奔走する一方で、彼は犯罪組織と対決する。バットマンを演じるのは、「ビートルジュース」に続きバートンと組んだマイケル・キートン。「バットマン」シリーズ第1弾。", "1989-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀座旋風児 目撃者は彼奴だ", "銀座旋風児 目撃者は彼奴だ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "庶民経済会の前会長謀殺事件を追っていた刑事がある夜殺される。目撃者は銀座旋風児の二階堂卓也とサラリーマンの小山田。警察は小山田を犯人として捕まえるが、卓也は事件の鍵は猟銃にあるとにらみ、東京中の銃砲店を歩いて回るのだった。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cowboys & Aliens", "カウボーイ＆エイリアン", "", "", "http://image.eiga.k-img.com/images/movie/55921/315x210.jpg?2728", 118, "「007」のダニエル・クレイグと「インディ・ジョーンズ」のハリソン・フォードが共演するアクション大作。「アイアンマン」シリーズのジョン・ファブロー監督がメガホンをとり、製作陣にはロン・ハワード、スティーブン・スピルバーグらが名を連ねる。1873年、米アリゾナ。荒野の中で目を覚ました男は記憶をなくし、腕には見慣れない機械の腕輪をはめていた。近くの町にたどりついた男は、自分がジェイク・ロネガンという名であり、その首に賞金がかけられていることを知る。そんな時、突如として町に未知の敵が襲来し、人々をさらっていく。ジェイクのはめた腕輪が敵を撃退できる唯一の武器であることから、ジェイクは息子をさらわれた町の権力者ダラーハイドとともに敵の跡を追跡することになる。共演にオリビア・ワイルド、サム・ロックウェルら。", "2011-10-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HANA-BI", "HANA-BI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "北野武の監督第７作は、自ら主演も兼ねた３度目の作品。そのためか「その男、凶暴につき」や「ソナチネ」を彷彿とさせるが、そこに留まらずこれまでの全作品の集大成であると同時に、新たな挑戦も感じさせる意欲作となった。子供を亡くしたショックで言葉を失った妻と、静かに暮らしている元刑事、西。彼は多くの同僚を傷つけ死なせてしまった数年前の忌わしい事件に責任を感じている。そして、車椅子の生活を余儀なくされ妻にも去られた友人、堀部や殉職した部下の遺族らにひそかな仕送りを続けていた。ヤクザからの借金がかさみ、西は次第に追い詰められていくが……。生と死が織り成す陰影に富んだ表現、寡黙な夫婦像から伝わる豊かなニュアンスなど、北野ならではの演出はますます冴えている。監督自身が描いた絵画の度重なる挿入も鮮やかな効果を発揮。じわじわと様々なイメージが増殖していく様が、単なる夫婦愛のドラマを凌駕して圧巻である。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPYMAKER／THE SECRET LIFE OF IAN FLEMING", "スパイメーカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "“007”ことジェームズ・ボンドの生みの親、イアン・フレミングの私生活にスポットを当て、ボンド誕生の秘密に迫るシリーズ番外編ともいえる作品。厳格な母への反発から、プレイボーイの頭角を現わし始める青年時代。陸軍士官学校をスキャンダルで中退した落ちこぼれは、ロイター通信へ入ってから変貌。ロシアでの取材活動が認められて英海軍情報部に入り、美女を小脇にボンドそこのけの活躍をする。フレミング役に、初代ボンドであるショーン・コネリーの息子ジェイソンが扮している。", "1990-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARGARET’S MUSEUM", "死の愛撫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "演技派H・B・カーターが、愛する者を失った女性の苦悩を迫真の演技で体現する問題作。北大西洋に面した村では、男たちは皆、炭坑夫として働いていた。しかし、生活は貧しく、家族は死と隣り合わせの毎日。マーガレットも父と兄を炭鉱の落盤事故で失った。そんなある日、彼女は元炭坑夫でユーモアあふれる男ニールと出会い、結婚。ささやかな幸せをかみ締めていた二人だが、諸処の事情からニールと弟が炭鉱へ向かうことになる。ビデオタイトルは「ヘレナ・ボナム＝カーター　死の愛撫」。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Japón", "ハポン", "", "", "http://image.eiga.k-img.com/images/movie/55001/315x210.jpg?4644", 133, "メキシコ映画界の異才カルロス・レイガダス監督が2002年に発表し、カンヌ国際映画祭でカメラ・ドール（新人監督賞）を獲得するなど大きな話題を呼んだ長編デビュー作。自らの死に場所を求めて人里離れた荒野へとたどり着いた中年男性は、そこで先住民族の老婆と出会う。彼は、渓谷の上に佇む彼女のあばら家に身を寄せることになるが、広大な自然の中で老婆の人間性に触れるうち、生きることや性の欲求を取り戻していく。", "2009-10-23", "19,7,70,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POKOLENIE", "世代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ポーランドを代表する巨匠A・ワイダの長編第１作。日本では1981年に劇場公開された。第二次大戦中、ナチス占領下のワルシャワ近郊。レジスタンスに加わった若者スタフは、女性リーダーのドロタに憧れを抱くが、ゲシュタポの手は彼女にも伸びていく……。「地下水道」「灰とダイヤモンド」とともにワイダの“抵抗３部作”を成す作品で、戦時下の青春群像劇としてもみずみずしい魅力を持つ一編。監督デビュー前のあどけないロマン・ポランスキーが、レジスタンスの一人として出演している。", "1954-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オーバードライヴ", "オーバードライヴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "天才的才能を持つギタリストが、ひょんなことから津軽三味線の虜になっていく……。三味線の特訓を通して、挫折を味わい、生きがいを見いだしていく主人公を柏原収史が熱演。実際に、兄の崇と組んでいるバンドでギタリストとして活躍している彼が三味線演奏を披露。CGやアニメも挟み込み、軽やかに描いたポップな青春サクセス・ストーリーだ。人気ユニットの天才ギタリスト、弦が突然クビにされる。その日泥酔した彼は、タクシーの運転手に青森の下北半島に連れてこられ、なぜか津軽三味線の特訓を受けるハメに。ところが、次第に三味線の魅力に取りつかれる。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("An Education", "17歳の肖像", "", "", "http://image.eiga.k-img.com/images/movie/55161/315x210.jpg?3015", 100, "1961年、ロンドン郊外。ある雨の日、オックスフォード大学を目指す優等生のジェニーは、倍も年の離れた男性と恋に落ち、勉強漬けの日々からは想像もできなかった刺激的な世界を体感していく。監督は「幸せになるためのイタリア語講座」のロネ・シェルフィグ、脚本は「アバウト・ア・ボーイ」「ハイ・フィデリティ」のニック・ホーンビィ。主演のキャリー・マリガンは第82回アカデミー賞で主演女優賞にノミネート。", "2010-04-17", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Memoirs of a Geisha", "SAYURI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "スティーブン・スピルバーグ製作、「シカゴ」でアカデミー賞6部門を制覇したロブ・マーシャル監督による、アーサー・ゴールデンのベストセラー小説「さゆり」の映画化。昭和初期、貧しさから置屋に売られた幼い少女・千代は、先輩の芸者にイジメられる毎日の中、“会長さん”と呼ばれる男にやさしい言葉をかけられ、彼と再会するために一流の芸者になることを決意する。人気女優チャン・ツィイー、コン・リー、ミシェル・ヨーらが華を競う。", "2005-12-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Rider Named Death", "蒼ざめた馬", "", "", "http://image.eiga.k-img.com/images/movie/53281/315x210.jpg?2159", 106, "「ジャズメン」「ゼロシティ」のロシア人監督カーレン・シャフナザーロフが、帝政ロシア末期に実在したテロリスト、ロープシンの自伝的小説を映画化。20世紀初頭の退廃ムード漂うロシアを舞台に、テロリスト集団“戦闘団”を率いて政府高官の暗殺を企てるイギリス人革命家の心の闇と、2人の女性との愛を描き出す。出演はジュゼッペ・トルナトーレ監督作「題名のない子守唄」で主演を務めたクセニア・ラパポルトほか。", "2008-02-09", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIDING OUT", "ウォンテッド・ハイスクール あぶない転校生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "“もう一度昔に戻ってみたい”という願望をSFタッチで描いた作品は過去にも数多く見られたが、この物語はもっと強引。タイム・スリップもせず27歳のまま17歳になるというもの。舞台となるのは、田舎の名もない高校。唯一の生き証人としてマフィアに追われる主人公アンドリューは、髭を剃り、髪をブロンドに染めてまんまと従弟の通う高校へ“転校”した。派手なルックスと、大人の雰囲気を併せ持った彼は、たちまち人気者に。そこへマフィアがやって来て……。MTV出身の監督らしいポップな作品。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HONEY POT", "三人の女性への招待状", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "冬のベニスを舞台にした、ロマンティックなファンタジー・ドラマ。富豪のセシルは、ベニスの邸に、かつて愛した３人の女性を招く。彼は自分の死期が近いことを告げ、３人に莫大な遺産を残すと語るが、彼の本当の狙いは、彼女たちの反応を見て楽しむことにあった。やがて３人の骨肉の争いが始まって……。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EN　AVOIR（OU　PAS）", "アリスの出発〈たびだち〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "缶詰工場をクビになったことをきっかけに住み慣れた港町を離れ、リヨンの安ホテルに転がり込んだ若い娘アリス。そこで彼女は、サッカー選手になる夢を叶えられず行き当たりばったりの日々を過ごしている青年ブリュノと出会う。何気ない男女の触れ合いを通して、新たな人生を踏み出そうとする若者の思いを繊細なタッチで綴ったL・マッソン監督の長編デビュー作。前向きに生きるヒロインを好演したS・キベルランはセザール賞の有望若手女優賞を獲得。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("凶犯", "凶犯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "チンピラに暴行され、監禁されたユキは宇東の情婦である朋代という女に救われる。数ヵ月後、別の男の情婦となった朋代とバカラのディーラーとなったユキは再会するが、宇東が起こした大金紛失事件に巻き込まれていく。彼女たちを主人公に、金に目がくらんだ無法者たちの命懸けの攻防を描く犯罪サスペンス。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("任侠ヘルパー", "任侠ヘルパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-11-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兒雷也", "兒雷也", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "王家再興を図る尾形周馬弘行が、ガマの妖術を修め、兒雷也となって更科城乗っ取りをたくらむ一味と対決する姿を描いた忍術映画。尾上松之助の旧劇以来、多くのスターによって繰り返し演じられてきた、いわば定番である。テンポの良い物語展開とトリック撮影の多用が興味深い。“化け猫女優”鈴木澄子の見事な立ち回りも魅力。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽の子 てだのふあ", "太陽の子 てだのふあ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "神戸で沖縄出身の夫婦が営む大衆食堂を舞台に、そこに集まる沖縄出身の人々と、夫婦の娘である本土生まれの小学生とのかかわりを描く。「キューポラのある街」を彷彿させる日常のディテールの描写の中から、“日本と沖縄”というテーマが明確なものになっていく。DVDタイトルは「太陽の子」。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHE−WOLF　OF　LONDON", "謎の狼女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "狼男に続き狼女が登場する、狼人間伝説をモチーフにした怪奇ゴシック映画。幻想性に満ちたシーンの数々、おどろおどろしい音響効果など抜群の恐怖演出が光る。広大な屋敷に住む３人の女たち。その一人は両親を亡くしたヒロインだが、フィアンセがいた。しかし、野獣に変身するという隠れた呪いの伝説が一族にあり、彼女もまた狼人間になる運命にあった。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女吸血鬼", "女吸血鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "原作は天草四郎伝説と吸血鬼ものをミックスした、橘外男の『地底の美肉』で、中川信夫が、メリハリのきいた演出をみせてくれる。天草四郎の遺児・勝姫に憧れた画家が、その末裔をさらい、岩山の地下に若さを保たせたまま監禁する。女の娘とその恋人が、それを救おうとする物語。吸血鬼に扮する天知茂が圧倒的に素晴らしい。彼のニヒルさがここでは、はからずも宿命を背負った男の悲しみとして見事に体現された。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Breaking and Entering", "こわれゆく世界の中で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "「イングリッシュ・ペイシェント」の名匠アンソニー・ミンゲラ監督が“真実の愛”をテーマに紡ぎ出すラブ・ストーリー。現代のロンドンを舞台に、繊細な恋人と情熱的な未亡人という対照的な2人の女性の間で葛藤する男性の姿を描く。主演は「コールドマウンテン」でもミンゲラ監督とタッグを組んだジュード・ロウ。「ショコラ」のジュリエット・ビノシュ、「美しい人」のロビン・ライト・ペンら2人の実力派女優との競演に注目。", "2007-04-21", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Babel", "バベル", "", "", "http://image.eiga.k-img.com/images/movie/1781/315x210.jpg?4071", 142, "　「アモーレス・ペロス」「21グラム」のアレハンドロ・ゴンザレス・イニャリトゥ監督の最新作。モロッコ、メキシコ、東京を舞台に起こるそれぞれの悲劇から、ひとつの真実に辿り着くまでが壮大なスケールで描かれる。出演はブラッド・ピット、ケイト・ブランシェット、ガエル・ガルシア・ベルナルに加え、日本からは役所広司と菊地凛子が参加。菊地とアドリアナ・バラッサが第79回アカデミー賞助演女優賞にノミネートされた。", "2007-04-28", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Passion Play", "パッション・プレイ", "", "", "http://ecx.images-amazon.com/images/I/515fug89WcL._SL210_.jpg", 0, "", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("らんま1／2　中国寝崑崙大決戦！　掟やぶりの激闘篇!!", "らんま1／2　中国寝崑崙大決戦！　掟やぶりの激闘篇!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "高橋留美子原作の同名人気コミックをアニメ化した劇場版第１作。早乙女乱馬は格闘技の修行をしている青年。彼には親が決めた許嫁・あかねがいるが、七福道人の長・キリンにさらわれてしまう。あかねを助けに中国へ向かう乱馬だが……。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("M★A★S★H", "M★A★S★H", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "朝鮮戦争のさなか、多忙をきわめる移動野戦外科病院の医者たちのクレイジーな行状をスケッチ風にまとめたアメリカン・ブラック・コメディの大傑作。TVシリーズとしてもアメリカで大ヒット。原作者R・フッカーは外科医の方が本職という変わり種。変わり種といえば、アルトマン監督の起用もそう。サザーランドとグールドというコンビの主演もアメリカ映画として異色。冒頭に流れる主題歌が“自殺のすすめ”を説くものと、その異色ぶりも徹底している。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一年之初", "一年之初", "", "", "http://image.eiga.k-img.com/images/movie/55021/315x210.jpg?2925", 113, "台湾の新鋭チェン・ヨウチェの長編監督デビュー作となる群像ドラマ。主演女優に憧れる映画制作アシスタントのパン、裏社会で働くタイからの不法移民ディンアン、クラブのカウントダウンパーティへ向かう謎の女バタフライとその親友シャオフイ、シャオフイの恋人ハオズ、部下を信じきれない裏社会のボス宗徳、過去の出来事に悩まされる映画監督リーシャン。ばらばらだったはずの7人の時間が少しずつ交錯しながら、やがて衝撃の結末へと向かっていく。", "2009-10-03", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stan the Flasher", "スタン・ザ・フラッシャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "ミュージシャンとして知られるセルジュ・ゲンズブールの4作目の監督作品であり遺作。生活のために子どもたちに家庭教師をしている、売れない脚本家のスタンは、露出狂となって美しい教え子ナターシャたちに自らの裸体を見せることを妄想していた。ナターシャを演じるのは、本作が映画デビューで、のちにカンヌ映画祭やセザール賞など数々の女優賞を受賞し注目を浴びたエロディ・ブシェーズ。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("玻璃樽", "ゴージャス", "", "", "http://image.eiga.k-img.com/images/movie/1791/315x210.jpg?3102", 121, "プウは台湾の漁村に住む美少女。いつものように海岸で遊んでいたとき、彼女は手紙の入ったボトルを拾う。手紙には「君を待っている」という言葉が…。運命を感じた彼女は手紙の主を必死に探すが、やっと探し当てた男、アルバートはゲイだった！　ショックを隠しきれないプウ。次の日、彼女は敵に襲われていたある男を偶然助けることに。男の名はC.N.チェン、香港に莫大な富と地位を築くエリート富豪だった。", "1999-12-18", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TREMORS BACK TO PERFECTION", "トレマーズ3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「宇宙戦争」のように大地の下から現れて人類を悩まし暴れまくるが、宇宙製とは違い製造元は地球の巨大モンスター・パニック映画シリーズ。その第３弾はさらに進化した怪獣“グラボイズ”が大暴れする。ネバタ州の砂漠の町に再々、人食い怪物が帰ってきた。怪物に立ち向かえるのは、命知らずの主人公しかいない。地元の若いカップルの協力を得て凄まじい激戦が始まる。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN AMOUR DE PLUIE", "ロミー・シュナイダーの情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "F・レイの甘美なメロディーにのせて、R・シュナイダーがひと夏の恋に身を焦がす人妻を熱演。３週間のバカンスを過ごすため、14歳の娘とともに温泉地へやって来た美しい人妻。彼女は、そこで魅力的なイタリア人と出会い情熱的な恋に落ちる。そして娘も、一人の少年に恋をするのだった。", "1973-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WALKING TALL", "ワイルド・タウン／英雄伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "人気レスラー、ザ・ロックが「ランダウン／ロッキング・ザ・アマゾン」に続いて主演を務め、悪との戦いを熱演するアクション。'73年の映画「ウォーキング・トール」をベースに、故郷を悪に染める悪漢に立ち向かったタフガイの活躍が描かれる。重量感のあるパンチや蹴りを次々と繰り出すロックの熱演が圧倒的。元米軍特殊部隊隊員クリスが故郷に帰ってくると、町はすっかり荒廃していた。怒った彼は諸悪の根源である権力者に立ち向かい……。", "2005-05-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("馬永貞／HERO", "暗黒街・若き英雄伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "19世紀末、上海の暗黒街で成り上がり、民衆にヒーローと呼ばれた実在のギャングの生き様を、ドラマティックに描く犯罪活劇。上海にやって来た若者マーは、大物ギャングのタンに気に入られ、暗黒街に足を踏み入れた。折りしも、マフィア抗争が激化。タンと固い友情で結ばれたマーは、敵対組織との戦いに乗り出し、巨大な勢力を築くが……。主人公マーに扮した金城武の硬派な魅力が光る。", "1997-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山中常盤", "山中常盤", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "近世初期に活躍した絵師・岩佐又兵衛の作とされる絵巻物『山中常盤』を、音楽とナレーションを付けて記録、構成した一風変わった映画。この絵巻は、母の敵討ちに馳せ参じる牛若丸の勇姿を色彩豊かに綴ったもので、全12巻、全長150nにも及ぶ。源氏の御曹司・牛若丸は15歳の春、平家を討伐しようと旅へ出る。その後、彼から手紙を受け取った母・常盤御前は、平泉へと向かうが……。", "2005-04-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実写版 18倫", "実写版 18倫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "ヤングチャンピオンの人気コミック「18倫」を、グラビアアイドルの田代さやか主演で映画化したエロテッィクコメディ。医者を志して名門お嬢さま学校に通っていた18歳の乙女・池内倫子は、父親の会社が倒産したために突然ホームレス状態となってしまう。生活のために仕事を探しはじめた倫子は、イケメン社長・沢倉が経営するアダルトビデオ制作会社に採用されるが……。共演に「キズモモ。」の河合龍之介。", "2009-04-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Last Night", "恋と愛の測り方", "", "", "http://image.eiga.k-img.com/images/movie/57031/315x210.jpg?1711", 92, "キーラ・ナイトレイとサム・ワーシントンが夫婦を演じるラブストーリー。ニューヨークのマンハッタンで誰もがあこがれる毎日を送っていたジョアンナとマイケルの夫婦だったが、ジョアンナはふとしたきっかけで夫が同僚のローラとひかれあっていることに気づいてしまう。マイケルとローラが泊りがけの出張に出かけた日、ジョアンナのもとに昔の恋人アレックスが訪ねてきて……。共演にエバ・メンデス、ギョーム・カネ。監督は「レオポルド・ブルームへの手紙」「ジャケット」などの脚本家マッシー・タジェディン。", "2012-05-12", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE FLAMBIERTE FRAU", "別れの朝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "西ドイツの監督、アケレンの作品。大学卒業を控えたエバは、ボーイフレンドとの退屈な生活にいや気がさし、独立しようと仕事に就く。そんな彼女が選んだ職業は娼婦だった……。見知らぬ男との肉欲のみのセックス、そしてSM行為。自由を求める若い女の姿を、独自の角度から捉えた異色作。", "1983-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Killer Elite", "キラー・エリート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "民間護衛組織コムテグのマイクは仲間のジョージに裏切られたが、九死に一生を得る。復讐の一念でリハビリし、幹部コリスの指令で、ある政治家の国外脱出を助ける任務の際に再びジョージと対決するのだが……。バイオレンス映画の巨匠ペキンパーが、宿命の対決を描く。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野良猫ロック マシンアニマル", "野良猫ロック マシンアニマル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "横浜を根城にするマヤ率いる不良少女グループと対立する不良グループ、それに米軍脱走兵と彼をスウェーデンに密航させようとするアメリカ人二人が、LSDをめぐって激しい争奪戦を繰り広げる。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MALY TYGR", "ちびトラちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "臆病者の小さなトラは何をするのも怖がってみんなに笑われる始末。ところがある日、母親が病気になってしまう。彼は勇気を出して暗い夜道をお医者さんを呼びに急ぐが……。チェコアニメ映画祭2006で上映された１作。", "1976-01-01", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MON ONCLE D’AME´RIQUE", "アメリカの伯父さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "女優の卵ジャニーヌ、放送局の報道部長ジャン、ジャンの妻アルレットの３人の行動を生物学者ラボリ教授が自らの論文をもとに分析していく。それぞれのエピソードは教授の実験材料にすぎず、冷静な教授の目を通して人間の滑稽な姿を描いていくというシニカルな喜劇で、A・レネ独特の映像空間が魅力だ。", "1980-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("普通に生きる", "普通に生きる", "", "", "http://image.eiga.k-img.com/images/movie/57251/315x210.jpg?4667", 83, "静岡県富士市の生活介護事業所「でら～と」にかかわる人々に密着したドキュメンタリー。「どんなに重い障害をもっていても、本人も家族も普通に生きてゆける社会を目指す」という理念を掲げ、障害をもつ子どもの親たちがゼロから立ち上げた通所介護施設「でら～と」。利用者は人や地域とのかかわりの中で社会性を身につけ、誰からも介護を受けられるように成長していく。親たちもまた自ら行政に働きかけるなどし、明るい地域社会と未来を切り開いていく。", "2012-01-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HALF THE WORLD", "ハーフ・ザ・ワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "オーストリアの新鋭F・フリッカー監督が、太陽光線を避けて生活する人々を描いた近未来SFドラマ。199X年、自然破壊が進む地上では、オゾン層の破壊により人々は太陽光線の下で生活できなくなり、昼はシェルターに隠れる生活を余儀なくされてしまう。国家機関ルナは、人々から太陽や自然の欲望を断ち切ろうと弾圧を開始するが、秘密クラブでは風景の写真やビデオの闇取引が繰り返され、昼間屋外に出て太陽の光を浴びている者もいた。サニーの経営する秘密クラブが摘発された日、サニーとレプロは本物の太陽の下、逃走を始めるのだが……。", "1993-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Leatherheads", "かけひきは、恋のはじまり", "", "", "http://image.eiga.k-img.com/images/movie/53641/315x210.jpg?3655", 114, "ジョージ・クルーニー監督・主演のスクリューボール・コメディ。アメフトのプロリーグが出来て間もない1920年代、アメフトをこよなく愛する中年選手ドッジは何とかチームを軌道に乗せようと奮闘する毎日。ドッジは、花形選手カーターのスクープを狙う美人記者レクシーと恋のかけひきを繰り広げるが、カーターまでもがレクシーに恋してしまい……。共演にレニー・ゼルウィガー、ジョン・クラシンスキー。", "2008-11-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自転車でいこう", "自転車でいこう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "在日韓国人が多く、反差別精神が根付いた大阪生野区の障害者と健常者の共生生活にスポットをあてたドキュメンタリー。大阪在住の知的障害者、リ・プーミョンは20歳の男の子。福祉作業所の営業係を務める彼は、毎日自転車に乗って街へ製品を売りに行くが、おしゃべり好きが高じて街の人々に話し掛けているうちにいつも営業を忘れてしまう。そんなプーミョンと街の人々の交流、障害者と健常者のいる学童保育施設での出来事が、当たり前の日常として真正面から描かれている。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンの鞍馬天狗", "エノケンの鞍馬天狗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "鞍馬天狗といえばアラカン（嵐寛寿郎）、アラカンといえば鞍馬天狗というほどの強烈なイメージを持つこのヒーローにエノケンが挑戦。勤王の志士の行動を新選組に密報する天狗廻状なる怪文書が出回り、志士たちは次々と新選組に倒されていた。その怪文書の出所を暴くべくわれらが鞍馬天狗が、敢然と新選組の詰所へ乗り込んだ。が、折しも詰所では花見の宴会のさなか。鞍馬天狗はその音楽にすっかり使命を忘れ、舞台に上がり歌と踊りを披露する。といったトンチンカンを繰り返したあげく、ついに天狗は駄馬にまたがり、さっそうと新選組の詰所に乗り込んでいく。この大詰めの立ち回りの迫力と面白さがたまらない。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Heavenz／ヘブンズ", "Heavenz／ヘブンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "クラブ・シーンを舞台に、一人の青年の恋と成長を描く青春ドラマ。テツはクラブの人気DJとして活躍しているが、現在の自分に不満を抱いている。そんなある日、FM局の女性ディレクター・ミサキに好意を抱いたテツは彼女の恋人が、ライバルのDJ・リョウスケだと知りショックを隠せない。やがて彼は、苦悩の中で自分の人生を振り返り、ある決意をする……。加山雄三の次男、山下徹大がイラ立ちや焦燥感を抱え、自分の将来に悩む主人公を好演。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CASINO カジノ", "CASINO カジノ", "", "", "http://image.eiga.k-img.com/images/movie/53581/315x210.jpg?1442", 92, "「パッチギ！」の塩谷瞬が主演するギャンブル・ムービー。幼い頃、裏カジノのバカラ勝負で父親を失った野村大樹は、昼も夜も酒屋のバイトに明け暮れる日々。そんなある日、得意先のバーの社長、西尾が始めた裏カジノ“WIN”にディーラーとして誘われ、入院中の母親のため、裏の世界に足を踏み入れる決意をした大樹。しかし、ほどなくして“WIN”も凄腕ゴト師集団の標的になり、その中には右手に傷のある男の姿が……。", "2008-03-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Choose Me", "チューズ・ミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　ラジオの人気カウンセラー、ナンシーは愛とセックスのエキスパートとして知られていたが、実は愛を知らない孤独な女性だった。彼女の放送を聞いているバーの女主人イブは男性との関係に揺れ動いている。そんなふたりはお互いの素性を知らないままルームメイトになる。やがて彼女たちは精神病院を退院したばかりの不思議な男ミッキーと関わることになるが……。都会に暮らす大人の男女の愛をスタイリッシュかつユーモラスに描く。この作品で、アラン・ルドルフ監督は一躍脚光を浴びることに。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火雲伝奇／FIRE DRAGON", "ファイヤードラゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "全編、炎と爆発が連続する香港カンフー・アクション。若き王、六大翁の陰謀を記した密書をお上へ届けるため、ユンは旅に出る。途中、密書を奪おうと六大翁が差し向けたのが火の龍。激しい戦いの末、何とか無事にその場を逃れたユンだったが、六大翁はさらに激しい攻撃を仕掛けてきた……。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IRON WILL", "白銀に燃えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ディズニーの製作によるアドベンチャー・ロマン。事故で父親を亡くし、大学進学を断念しかけた青年が、賞金１万ドルの懸かった犬ぞりレースに挑戦。ライバルの卑劣な妨害や自然の猛威と戦いながら、800キロの苛酷な道程を走り抜く。大雪原で繰り広げられるダイナミックなレース・シーンが見もの。ビデオ、DVDタイトルは「アイアン・ウィル　白銀に燃えて」。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Darby O'Gill and the Little People", "四つの願い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ウォルト・ディズニー製作によるファンタジー・ドラマ。20世紀初頭のアイルランドを舞台に、老人と妖精たちの交流を描く。農場を管理するダービー・オギールは、ある日偶然、地下にある小さな妖精たちの国を発見する。彼は妖精の王様に、願い事を3つだけ叶えてもらう約束をするが……。監督は「メリー・ポピンズ」等の作品で知られるロバート・スティーブンソン。007シリーズでブレイクする前のショーン・コネリーが出演している。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE FEU FOLLET", "鬼火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "アル中患者として療養所で暮らすアラン・ルロワは、かつて社交界の花形だったが、今は死に取り憑かれている。その彼が人生の最期を締めくくるためにパリの旧友を訪れる……。自殺を決意した男の、死に至るまでの二日間の行動を描いた厭世感あふれる傑作。", "1963-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAS BOOT", "U・ボート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "L・G・ブーフハイムのベストセラー小説を映画化した秀作戦争映画。もともと５時間を超えるTVドラマとして製作し、これを２時間強に再編集されたものが劇場公開された。第二次大戦下、ラ・ロシェル港から一隻のUボートが出航する。勇敢な男たちは任務を着々と遂行するが、ジブラルタル海峡にさしかかったとき、敵の攻撃の前にコントロールを失い、海底深くへと没してしまう……。敵との攻防戦を緊迫した雰囲気の中で描き、監督ペーターゼンの名を一躍、世界的なものにした。1999年には３時間29分のディレクターズ・カット版が劇場公開された。DVDは「U・ボート　パーフェクト・コレクション 」に収録。", "1981-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr.Children / Split The Difference", "Mr.Children / Split The Difference", "", "", "http://image.eiga.k-img.com/images/movie/55651/315x210.jpg?1719", 115, "国民的人気バンド「Mr.Children」のプライベート・ライブを収録した音楽ドキュメンタリー。2009年、アルバム「SUPERMARKET FANTASY」が150万枚のヒットを記録した同バンドは、ホームグラウンドにしている都内のレコーディング・スタジオで友人・知人に向けての小規模ライブを行う。ライブの打ち合わせ風景やリハーサルなど貴重な裏方映像を通し、ふだんのステージでは見られないバンドの素顔に迫る。", "2010-09-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒田騒動", "黒田騒動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "内田吐夢監督と片岡千恵蔵が「血槍富士」に続いてコンビを組んだ時代劇の力作。黒田家の新任家老・倉橋十太夫が、殿の妾・お秀の方と結託して幕府に反逆を企てるが、重臣・栗山大膳は常に先手をとって陰謀を打ち砕き主君を守り抜く。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("孔雀王", "孔雀王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "荻野真の同名コミックを原作に、日本と香港初の本格的合作として製作されたスペクタクル・アクション映画。地獄門より現れる皆魔障外人の出現を阻止すべく、二人の孔雀王が魔族に立ち向かっていく。香港では、ユン・ピョウだけが主演となるように再編集されたものが公開されて話題を呼んだ。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("横浜ばっくれ隊　純情ゴロマキ死闘篇", "横浜ばっくれ隊　純情ゴロマキ死闘篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ツッパリ青春コメディのシリーズ第３作。成田常松と常松軍団は、ひょんなことから女子高生４人組を、横浜制圧を狙う凶悪な川崎連合から救う。一方、実家の保育園の前に捨てられた赤ん坊の世話に一所懸命の常松の前に、かつて横浜最強と呼ばれた伝説の男・剛島が現れた。赤ん坊の母親は、剛島の元恋人・サエコだったのだ……。「TVO」などで知られる自主映画出身の太田達也が小気味よい演出を見せる。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鳩", "鳩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 44, "名匠・野村芳亭の息子で、のちに「砂の器」などの大作群で、山田洋次とともに松竹の屋台骨を支えることになる野村芳太郎監督のデビュー作。父親の反対にもかかわらず、鳩を飼い始めた高校生の勇の責任感と周囲の無理解を通して、青春の孤独を描く。この年、２本立て興行用の中編“シスター映画”（SP）路線を松竹は打ち出し、これに新人を起用した。本作もその１本で、ブルー・リボン新人監督賞を受賞している小品佳作。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAIGRET VOIT ROUGE", "メグレ赤い灯を見る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "G・シムノン原作の『メグレ警部』シリーズの映画化で、J・ギャバンがメグレに扮する。死体なき殺人の影にうごめくアメリカのマフィアとアメリカ大使館員。続いて起きる鋭い頭脳で、殺人事件の真相に迫るフィルム・ノワール。", "1963-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Feast of Love", "ラブ・アペタイザー", "", "", "http://ecx.images-amazon.com/images/I/51x%2BQ3%2BV72L._SL210_.jpg", 102, "「クレイマー、クレイマー」のロバート・ベントン監督が、モーガン・フリーマン、グレッグ・キニアら豪華キャストを迎えて描くヒューマンラブストーリー。息子を亡くした大学教授ハリーを軸に、妻が女性と駆け落ちしてしまったカフェ経営者、不倫を続けることに悩む女性、ひと目ぼれで恋に落ち結婚しながらも思わぬ悲劇に襲われる若いカップルなど、それぞれ問題を抱える9人の男女が織りなす人間模様を描き出す。", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHINA　SEAS", "チャイナ・シー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「紅塵」に続き、ゲーブルの男っぽさとハーロウのセクシーな魅力が画面を彩る男女の愛と憎しみの物語。東洋の海をバックに、異国のロマンと冒険の雰囲気に包まれたエキゾチックな一編。金貨を積んだ船が香港からシンガポールへ向けて出港した。船長はその船上で昔の恋人に再会し愛が再燃。船長の情婦は気に入らず商人と親しくなるが、商人の本当の狙いとは……。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE　AGES", "キートンの恋愛三代記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 47, "B・キートンが初めて手掛けた長編サイレント・コメディ。石器時代、ローマ時代、現代の３つの時代で繰り広げられる恋愛騒動。それぞれの時代で、美しい娘を手に入れるまでの男の奮闘劇が軽快に描かれる。戦車のレース・シーンやフットボール・シーン、ビルからビルへ飛び移るアクションなど見どころ満載。", "1923-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女囚と共に", "女囚と共に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "他の娘と結婚した無情な男の新居に放火した女、前科11犯の窃盗犯、年若い夫を殺して乳幼児を抱えた女など、女子刑務所囚人たちの様々な生態を描き、その矯正補導を主題とした異色の社会派ドラマ。木暮・久我・岡田・香川らが女囚人を好演。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON OUR OWN", "グッバイトゥモロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "両親を失い、離ればなれになることが決まった幼い兄弟４人が施設を脱走、トランク一つで冒険の旅を始める……。４人で一緒に暮らしたいと願う子供たちの無邪気な心が、現代人の忘れかけている家族のあり方を改めて教えてくれる、ハートフルなロード・ムービー。素直な子供たちの自然な演技が初々しい。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い糸", "赤い糸", "", "", "http://image.eiga.k-img.com/images/movie/53671/315x210.jpg?2724", 0, "中高生を中心に人気の同名ケータイ小説を映画化。監督は「電車男」の村上正典。映画公開と同時期に連動したTVドラマも放送。主演に「象の背中」「シャカリキ！」の南沢奈央と「ダイブ!!」の溝端淳平。中学生の芽衣は、恋心を抱いていた幼なじみの悠哉が自分の姉に恋していること知り、失恋する。そんな時、クールな優しさを持つ敦史と知り合い、2人は惹かれあっていく。さらに芽衣と敦史は幼い時に一度出会っていたことがわかり、運命を感じる2人だったが……。", "2008-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バウンス Ko GALS", "バウンス Ko GALS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "アメリカに旅立つ資金を調達するため、渋谷のブルセラ・ショップに立ち寄ったリサ。そこで紹介された怪しげなビデオ撮影現場で大切な現金30万円を失った彼女は、マイペースな女の子ラクチャン、コギャルのリーダー的存在ジョンコの協力を得て、何とか翌朝までに資金を稼ごうとする……。90年代半ば、一種の社会現象にまでなった女子高生＝コギャルの生態を、原田眞人監督がライブ感あふれる映像で描く青春映画。援助交際やブルセラなどにかかわるコギャルたちの乾いた生き方をすくい取りながら、少女３人の胸の熱くなるような友情劇に仕上げている。コギャルとのジェネレーション・ギャップを感じさせる大物ヤクザに扮した役所広司の凄みの効いた存在感も印象深い。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Under the Volcano", "火山のもとで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "　1938年のメキシコ、万霊節のパーティでにぎわう火山のふもとの町。アル中の元イギリス領事ジェフリーは一年前に家出した妻のイボンヌと再会する。ふたりはジェフリーの弟で、かつて彼女と関係したことのあるヒューも交えてひと時を過ごすが……。彼らの愛憎関係とその顛末を描いた文芸ドラマ。", "1984-01-01", "19,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY　MOTHER，　THE　MERMAID", "初恋のアルバム　～人魚姫のいた島～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "不思議なタイムスリップを通して、ヒロインの成長が描かれる癒し系純愛ストーリー。「スキャンダル」のチョン・ドヨンと「殺人の追憶」のパク・ヘイルが共演し、ノスタルジーをそそる初恋のロマンスを奏でる。韓国の田舎の美しい風景描写にも注目だ。母の悪口にうんざりして蒸発してしまった父を、娘ナヨンが両親の生まれ故郷の島まで探しに出かけることに。すると、そこには30年前の若き父と母の姿が。二人はときめきの恋の真っただ中にいた……。", "2004-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・三等重役／旅と女と酒の巻", "新・三等重役／旅と女と酒の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "世界電機の営業係・八代波吉は苦心の企画を提出するが、仕入部長に蹴られ罵倒される。八代は毎日酒を飲んで帰宅するものの、新婚早々の舞子夫人が待ちかまえていた。彼の上司・沢村専務は、オールドミスの秘書・章子とともに八代夫妻の仲をうまくさばく。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Man from London", "倫敦から来た男", "", "", "http://image.eiga.k-img.com/images/movie/54361/315x210.jpg?3718", 138, "「ヴェルクマイスター・ハーモニー」で世界を驚嘆させたハンガリーの鬼才タル・ベーラ監督が、「メグレ警視」シリーズで知られる推理作家ジョルジュ・シムノンの同名小説を映画化したフィルムノワール。静かな港町で単調な生活を送ってきた鉄道員の男が偶然にも殺人事件を目撃し、大金を手にしてしまったことから運命を狂わせていく姿をモノクロ映像で描き出す。出演はミロスラブ・クロボット、「フィクサー」のオスカー女優ティルダ・スウィントン。", "2009-12-12", "30,7,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ろくでなしBLUES'98", "ろくでなしBLUES'98", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "『週刊少年ジャンプ』に連載された人気漫画の実写映画化第２作。監督は「SCORE」の室賀厚。主演は前作に続いて、華麗なアクションをみせる現役キックボクサー・前田憲作。吉祥寺帝拳高校の前田太尊はグラマーな美人教師に夢中。おもしろくない彼の恋人・千秋は初恋の人・結城との再会に心ときめかす。しかし、結城は彼女を利用して太尊をおびき寄せ、叩きのめす計画を立てていた……。DVDタイトルは「ろくでなしBLUES２」。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("武勇伝", "武勇伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "国際派のアクション俳優、澤田謙也とK-1ファイター、魔裟斗が激突するバイオレンス映画。歌舞伎町を舞台に、闘うことでしか生きられない男たちの運命的な出会いをハードなタッチで描く。新宿・歌舞伎町でバーを営む吾郎はケンカ沙汰の絶えない男。ある日、彼につきまとう少女アヤが元ボクサー、龍平と出会ったことから事件に巻き込まれてしまう。龍平も吾郎同様、闘いに飢えた獣のような男だった……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2011-2012 ヴェルディ「椿姫」", "METライブビューイング2011-2012 ヴェルディ「椿姫」", "", "", "http://image.eiga.k-img.com/images/movie/57171/315x210.jpg?4310", 187, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を映画館で上映する「METライブビューイング」2011～12年シーズンの11作目。19世紀イタリアを代表する作曲家ベルディの傑作オペラ。19世紀パリ。高級娼婦のビオレッタは、自堕落な生活がたたり結核を患ってしまう。やがて一途な愛を捧げる青年アルフレードの情熱に負け、ともに生活を始めたビオレッタだったが、息子が娼婦にだまされたと思い込んだアルフレードの父ジェルモンが、黙って身を引くようビオレッタに迫る。", "2012-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dr. Dolittle 2", "ドクター・ドリトル2", "", "", "http://image.eiga.k-img.com/images/movie/1361/315x210.jpg?3162", 88, "98年公開の「ドクター・ドリトル」の続編。前作で“動物と話ができる医者”として有名になったドリトルは、TV出演もする人気医師に。美人の妻と2人の娘との平和な日々に、ビーバーから「森が無くなっちゃう！」とSOSが。動物たちの住む森の開発を中止させるためには、そこに住む絶滅危惧種の熊のメ スを妊娠させるしかない。しかし、やっと見 つかった同種のオスはサーカスの熊だった。監督カーはEMPDらラップ系のミュージック・クリップで活躍、本作が長編第2作。", "2001-07-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE HURTS", "パパがきた！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "妹の結婚式のため、生まれ故郷に帰ってきたポール。家には母と妹、妹の婚約者などがいたが、驚いたことに何と別れた妻ナンシーと二人の子供が同居していたのだ。子供たちの冷たい視線に耐えきれず、１度は家を出たポールだったが、様々な人との出会いから再び家族とやり直す決心をする。古き良きアメリカ映画のような、家族愛を描いたラブ・コメディ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LASTA WARRIOR", "ラスト・ウォリアー 最後のサムライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "1945年、太平洋戦争末期。日本海軍の偵察艇が上陸した孤島には、一人のアメリカ兵ギブがいた。彼は孤島に残る尼僧ケイトと自分を守るため、日本兵らと戦って次々と倒していく。しかし、刀の達人である本間だけは手強かった……。国境を越えた男たちの生き様を描くアクション。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAVAGE MESSIAH", "狂えるメサイア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "23歳の若さでこの世を去った天才画家、彫刻家アンリ・ゴーティエの半生を、K・ラッセルが映像化。絵の才能を開花させた彼が、第一次世界大戦で若き命を散らすまでを、20歳年上の女性ソフィとの恋の日々を軸に描き出す。異才デレク・ジャーマンが、装飾スタッフとして参加。", "1972-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("したくて、したくて、たまらない、女。", "したくて、したくて、たまらない、女。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "小さな温泉街に謎の美女が出没し、セックスが抜群だという噂が流れる。噂を聞いた東京の雑誌記者が、謎の美女の正体をつかむ。女はかつての人気スター、杉田ひかるの若い頃の姿で、昔のつらい恋への思いが若い頃の姿となって現れたのだった。そして、正体がばれた時、今は老婆となったひかるは病院で亡くなるのだった。ピンク映画の新東宝が初めて一般劇場で公開した作品。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HANG FIRE", "ハング・ファイヤー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アメリカ西部の田舎町に、凶悪な脱獄囚率いる集団が現れ、次々と町を占拠していく。血と暴力におびえる住人たち。ついに軍部が鎮圧に乗り出すが、その中には元グリーンベレーの英雄スライトンの姿もあった。“B級映画”という言葉を、マイナーな意味ではなく、称賛の意を込めて使えるならば、まさに“オールB級スター映画”といえる豪華キャストの作品だ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘密戦隊ゴレンジャー　火の山最期の大噴火", "秘密戦隊ゴレンジャー　火の山最期の大噴火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "子どもたちから絶大な人気を集めた特撮テレビドラマの劇場版第5弾。黒十字軍の3代目大幹部・火の山仮面マグマン将軍が、ゴレンジャーとイーグル基地を全滅させるべく、移動要塞ナバローンで猛攻撃を開始。バリタンクに乗りこんで応戦したゴレンジャーは、その途中でナバローンの設計図を手に入れ……。テレビシリーズの第54話「真赤な挑戦！火の山最期の大噴火」を再編集したもので、「東映まんがまつり」の1編として公開された。", "1976-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOU'RE A BIG BOY NOW", "大人になれば…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "若きコッポラが監督として本格的デビューを果たした青春ラブ・コメディ。コッポラといえば「ゴッドファーザー」「地獄の黙示録」といった大作、問題作で名高い巨匠だが、「アウトサイダー」「ペギー・スーの結婚」といった青春映画でも、その手腕を発揮していることを思えば、本作もなかなか興味深い。両親から過保護に育てられた青年が、冷淡な女性から人生について学んでいくという物語。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大脱獄", "大脱獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "高倉健と菅原文太共演のアクション・ドラマ。二人が演じるのは、濡れ衣で死刑囚となった男・梢と、ガンで苦しむ母を殺し服役した国岩。彼らが極寒の網走刑務所を脱獄し、梢を陥れた男に復讐するまでを描く。石井輝男脚本・監督の“網走番外地”シリーズの番外編ともいえる。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Once in a Summer", "夏物語", "", "", "http://image.eiga.k-img.com/images/movie/33821/315x210.jpg?4796", 116, "人気韓流スター、イ・ビョンホン主演による感動ラブストーリー。60代になっても独身を貫いている大学教授ソギョンは、かつての教え子で放送作家のスジンから、“初恋の人を探すテレビ番組”の出演を依頼される。ソギョンは戸惑いながらも、スジンに1冊の本を差し出す。そこには、時代の波によって引き裂かれた男女の切ない愛の物語が隠されていた……。ビョンホンが主人公の老教授ソギョンとその青春時代という2世代を見事に演じきっている。", "2007-01-27", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛してる、成都", "愛してる、成都", "", "", "http://image.eiga.k-img.com/images/movie/54971/315x210.jpg?4597", 78, "2008年5月に7万人以上もの犠牲者を出した四川大地震の復興支援プロジェクトとして製作されたオムニバス映画。中国ロック界のカリスマとして知られるツイ・ジエンが初メガホンを取り、大地震で孤児となった男女の21年後の再会を描いた「2029年」のパートと、「ハリウッド★ホンコン」のフルーツ・チャン監督が文化大革命時代の茶館で織りなされる人間模様を描いた「1976年」のパートから構成。", "2009-10-18", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハワイ・マレー沖海戦", "ハワイ・マレー沖海戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "太平洋戦争の幕開けとなった真珠湾の米軍艦隊奇襲攻撃と、マレー半島沖の英軍艦隊急襲の大戦果を記念し、大本営海軍報道部の企画によって製作された開戦１周年記念映画。ストーリーは海軍兵学校の一生徒と、彼を慕って航空兵に志願する従弟を軸に展開。前半部分は帰省した兵学校生徒と従弟の一家の交流と、予科練での厳しい訓練ぶりを描き、後半は、真珠湾とマレー半島沖の海戦に参加した二人の活躍を描く。特筆すべきは円谷英二らによるミニチュアを用いた特撮の素晴らしさであり、戦後の東宝特撮映画の技術的基盤は、この映画によって確立された。実際に予科練で撮影された訓練シーンは見ものだ。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喧嘩番長　劇場版　全国制覇", "喧嘩番長　劇場版　全国制覇", "", "", "http://image.eiga.k-img.com/images/movie/55141/315x210.jpg?2541", 0, "人気アクションアドベンチャーゲーム「喧嘩番長」の実写映画化。負けず嫌いでお調子者のタカシは、修学旅行先で茨城県の番長・鬼沢と間違われ、喧嘩を売られる。実はそこには、全国の番長の頂上を決めるべく、47都道府県の番長が集結していたのだった。", "2010-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざ刑事", "やくざ刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "野田幸男監督と千葉真一がコンビを組んだ“やくざ刑事”シリーズ。八城会は横浜、東京を拠点とする日本最大の暴力組織で、資金ルートである阿久津商事社長は、八城組の屋台骨を背負っていた。秘密捜査官・隼田は潰滅作戦の使命を受け組織に潜入する。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブギーポップは笑わない", "ブギーポップは笑わない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "とある高校で、女子生徒の失踪事件が相次いで起きていた。それとともに学内でささやかれる死神＝ブギーポップの伝説。やがて事件と伝説はリンクしながら、意外なクライマックスを迎える。1990年代末期を代表する少女スターたちが競演するジュヴナイル・ホラー。ザッピング感覚のストーリー展開も、作品の迷宮度を高めるのに貢献している。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市あばれ火祭り", "座頭市あばれ火祭り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ヤクザ組織の盲目の支配者に、座頭市が窮地に立たされてしまうシリーズ第21作。アイデアマン・勝新太郎が、初めて正式に脚本に参加しているが、特に目新しい趣向は見られない。市のライバル役の仲代達矢は、得意のギョロ目をむいてすごんでみせるものの、「椿三十郎」の時のような迫力はない。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JENATSCH", "デ・ジャ・ヴュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "17世紀のスイス・グリゾン解放の英雄イェナチュをめぐる幻想的な作品。ジャーナリストのスプレシャーは、イェナチュゆかりの地を訪れ、取材していくうちに不思議な体験をする……。現在と17世紀が交錯する魅惑的な旅。映画の魔術師の名にふさわしいシュミットの傑作。", "1987-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四月物語", "四月物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "「Love Letter」「スワロウテイル」の岩井俊二が、本作が映画初主演となる松たか子演じる女子大生の淡い恋心を描いた青春ドラマ。北海道の親元を離れ、大学に通うために上京した卯月。新しい人々との出会いなど小さな冒険の中で、卯月は東京の生活に少しずつ慣れていく。そんな彼女には、憧れの先輩と同じ大学を選んだという人には言えない不純な動機があった。", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Searchers 2.0", "サーチャーズ2.0", "", "", "http://image.eiga.k-img.com/images/movie/54181/315x210.jpg?3446", 96, "カルトムービーの鬼才、「レポマン」「シド・アンド・ナンシー」のアレックス・コックスによる、ジョン・フォードの傑作西部劇「捜索者」のスタイルを装ったオフビート・コメディ。子役時代に残忍な脚本家に虐待されたトラウマを持つB級西部劇役者のメルとフレッドは、南カリフォルニアからアリゾナへと復讐の旅に出ることにするのだが……。", "2009-01-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPACE BATTLESHIP ヤマト", "SPACE BATTLESHIP ヤマト", "", "", "http://image.eiga.k-img.com/images/movie/54941/315x210.jpg?2616", 0, "「ALWAYS 三丁目の夕日」の山崎貴監督が、国民的人気を博したTVアニメ「宇宙戦艦ヤマト」をSMAPの木村拓哉を主演に実写映画化。西暦2194年、ナゾの異星人ガミラスが地球に侵攻し、その圧倒的な攻撃力によって人類は絶滅寸前に追い込まれてしまう。5年後、生き残った人間たちは放射能で汚染された地上を逃れ、地下で生活していた。元エースパイロットの古代進は、放射能除去装置があるというイスカンダル星を目指して旅立つが……。", "2010-12-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILENCERS", "サイレンサー 地球侵略", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ロズウェル事件やヒル夫妻事件など、過去に実際に話題になった宇宙人事件をヒントに製作されたSFサスペンス。1960年代に作られた「サイレンサー」シリーズとは無関係。1947年、アメリカのロズウェル陸軍基地の近くで、墜落したUFOの残骸と宇宙人の死体が回収された。1966年ネブラスカ州のノースプラット近郊では、巨大なUFOが出現し、黒ずくめの男たちが目撃者を脅迫した。そして、現代のロサンゼルス。議会では、29年前に地球外生物と技術取引をして建設中の時空間ドームの予算について議論されていた。ところが、このドームこそ異星人たちが地球を侵略するための第一歩だったのだ。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FASTER", "FASTER", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "オートバイ・レースの最高峰とされる、Moto GPの世界を映し出すドキュメンタリー。一瞬の判断の誤りで命を落としかねない極限下で、なおもスピードを求めるライダーたち。彼らの原動力を、関係者の証言を集めて検証していく。バイクフリークで知られる人気俳優E・マクレガーがナレーターを務めている。スキャンダラスなライバル対決など話題の多かったMoto GPの'01～'02シーズン。その舞台裏にカメラが密着。白熱の世界を追いかける。", "2005-09-10", "1,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("群青　愛が沈んだ海の色", "群青　愛が沈んだ海の色", "", "", "http://image.eiga.k-img.com/images/movie/53981/315x210.jpg?4489", 119, "沖縄のとある離島。幼なじみで兄妹のように育った一也と結婚を約束した凉子だったが、一也は、凉子の父で漁師の龍二に認めてもらうために潜った海で、帰らぬ人になってしまう。悲しみのあまり殻に閉じこもってしまった凉子は……。「涙そうそう」「世界の中心で、愛をさけぶ」の長澤まさみが、絶望に打ちひしがれながらも、かすかな再生を遂げていく女性の姿を演じたドラマ。共演に佐々木蔵之介、監督は「真昼ノ星空」の中川陽介。", "2009-06-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あゝ零戦", "あゝ零戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ゼロ戦に乗った若きパイロットたちの戦いを描く青春アクション。第二次大戦期、海軍航空隊に編入された夏堀と峯岸は、特訓のなかでたくましい兵士に成長。零戦乗りとなった彼らは数々の戦果をあげる。だが、戦局が厳しくなり、飛行士は特攻の任務を負わされ、やがて二人にも特攻指令がくだる……。特撮を駆使した飛行シーンが見もの。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラマンティガ＆ウルトラマンダイナ　　光の星の戦士たち", "ウルトラマンティガ＆ウルトラマンダイナ　　光の星の戦士たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "TV放送で好評を得た『ウルトラマンダイナ』の劇場版。無敵の戦闘マシーン“デスフェイサー”を操る宇宙植物獣人モネラ星人が、人類抹殺計画を実行。スーパーGUTSの隊員アスカはすかさずダイナに変身するが、デスフェイサーの猛攻に敗北を喫してしまう……。ダイナとティガの1990年代二大ヒーローによる強力タッグや、体長258メートルを誇る超巨大怪獣クイーンモネラの暴れっぷりが見どころ。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大殺陣 雄呂血", "大殺陣 雄呂血", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "二川文太郎監督による無声映画の傑作時代劇「雄呂血」の再映画化。市川雷蔵扮する実直な道場の師範代が善意で行動したことが誤解され、友人にも裏切られ浪人に身を落とし、流れ流れてついにやり場のない怒りを爆発させ、追っ手を斬りまくる。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("墨東綺譚", "墨東綺譚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "永井荷風の小説をもとに、荷風自身を主人公に脚色して映画化。日本的な官能美が話題となった。大学教授でありながら、粋な遊蕩生活に明け暮れる荷風は、玉ノ井の娼家でお雪と名乗る娼婦に出会う。生まれて初めて理想の女性と出会った荷風は、57歳にして結婚の約束をする。しかし東京大空襲の炎に追われて二人は別れ別れに……。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hunters", "HUNTERS ハンターズ", "", "", "http://image.eiga.k-img.com/images/movie/57591/315x210.jpg?2484", 112, "不気味な廃墟で繰り広げられる人間狩りの恐怖を描いたアクションスリラー。森の奥深くにある廃墟と化した軍事要塞で、5人の男たちが密かに人間狩りを楽しんでいた。そうとは知らずに森の中へやってきてしまった若い女性アリスは狩りの獲物として狙われ、同じく偶然その場所を訪れた刑事ルイセントは、数々のトラップをかいくぐりながらハンターたちに戦いを挑む。人間狩りの恐怖に巻き込まれる女性アリスを、人気TVシリーズ「glee グリー」や「アイ・アム・ナンバー4」のダイアナ・アグロンが演じる。", "2012-04-07", "2,58,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　LA　PLACE　DU　COEUR", "幼なじみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "16歳の白人少女クリムと18歳の黒人少年ベベ。幼い頃からいっしょに育ってきた二人は、肌の色の違いを気にすることもなく、自然に愛で結ばれていく。しかしベベは無実の罪で逮捕され、残されたクリムは彼との子供を妊娠していることを知る……。港町マルセイユでマイペースの活動を続けるR・ゲディギャン監督が、現代アメリカを代表する黒人作家J・ボールドウィンの小説を翻案して映画化。まだあどけない無力な少年少女のピュアな恋を、その家族たちが全力を挙げて支えようとする感動的なストーリーをリリカルな演出で紡ぎ上げている。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DUETS", "デュエット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "G・パルトロウの父B・パルトロウ監督による、カラオケを題材にしたハートフルな人間ドラマ。カラオケで賞金を稼ぐプロのカラオケ・ハスラーと長らく会っていなかった実の娘、不動産会社に勤める冴えない男、刑務所から脱獄してきた黒人……。そんな彼らが、成り行きから賞金5000ドルを賭けたカラオケ大会に出場することになる。パルトロウとH・ルイスのデュエットをはじめ、出演者たちが次々と自前のノドでカラオケを披露する。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE IDLE CLASS", "のらくら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ぐうたらでイカレた上流社会の紳士と浮浪者を演じわける、チャップリンの一人二役の喜劇。貴族的なスポーツとして、いかにゴルフを嫌っていたかを辛らつに見せ、ハイソサエティーを見事に愚弄した短編。「チャップリンの ゴルフ狂時代」という別タイトルもある。ビデオには「キッド」が併録されている。DVDは「ラヴ・チャップリン！短編集」に収録。", "1921-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("精霊夜曲　最も古い首つりの木", "精霊夜曲　最も古い首つりの木", "", "", "http://image.eiga.k-img.com/images/movie/57661/315x210.jpg?4351", 90, "「路地裏のコッペリウス」「第三病棟の鬼」のかわさきひろゆき監督が手がける青春ホラーファンタジー。大学を卒業したものの就職もせずブラブラしていた青年・義行は、知らない森に迷い込み、怪しげな2本の大木とその下の石に飾られた古びた2体の人形を見つける。そこへ現れた老婆から大木と石の伝説を聞かされると石が光だし、2体の人形がしゃべりはじめて……。", "2012-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Four Christmases", "フォー・クリスマス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デコトラの鷲（しゅう）　其の四　愛と涙の男鹿半島", "デコトラの鷲（しゅう）　其の四　愛と涙の男鹿半島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "哀川翔主演による人情コメディのシリーズ第4作。浅草で大騒ぎを起こした鷲一郎は、そこでワケありの美人トラッカー真利江と知りあう。未練を残しつつもデコトラ全国大会に出場するため秋田へと向かった鷲一郎は、デコトラを何者かに乗り逃げされたあげく壊されてしまう。犯人はアキラという子どもで、その母親はなんと真利江だった。真利江が夫の残した借金を背負わされて苦しんでいると知った鷲一郎は、彼女のためにひと肌脱ぐことを決意する。", "2007-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE MISCHIEF", "白い炎の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "“ケニアの白い女王”と呼ばれた実在の女性の半生を描いた映画。第二次大戦中のイギリス。社交界のアイドル、ダイアナは、30歳年上の財産家と結婚し、アフリカのケニアへ行く。戦火の及ばないその地では小さな貴族社会が形式され、毎晩のように宴が催される。ダイアナはプレイボーイのエロル伯爵の誘惑を受け入れるが……。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Q＆A", "Q＆A", "", "", "http://ecx.images-amazon.com/images/I/51cU1n7i21L._SL210_.jpg", 132, "「十二人の怒れる男」「ネットワーク」などの名匠シドニー・ルメットが、警察や司法機関の腐敗を題材に撮りあげた社会派サスペンス。ニューヨーク市警のベテラン刑事ブレナンが麻薬の売人を射殺する事件が発生。事件を担当することになった元警官の新人検事補ライリーは、正当防衛を主張するブレナンの証言に疑問を抱くが……。ブレナン役に「48時間」のニック・ノルティ、ライリー役に「普通の人々」のティモシー・ハットン。", "1990-11-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大秦帝國", "大秦帝国 QIN EMPIRE", "", "", "http://image.eiga.k-img.com/images/movie/54551/315x210.jpg?1444", 260, "司馬遷による中国の歴史書「史記」をもとに、秦帝国を築いた英雄たちの生き様を描いた2パート構成・520分の大長編。紀元前361年、中国西部の弱小国・秦の君主・孝公は、隣国・魏との戦で苦境に立たされていた。孝公は疲弊した国を建て直すべく、「広く奇計の士を求む」と国内外より優秀な人材を募り、国家改革に乗り出す。", "2009-06-06", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦争と人間 第一部・運命の序曲", "戦争と人間 第一部・運命の序曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 194, "財閥総帥の弟・伍代喬介は満州・関東軍の強硬派と結託し、利権を求めて画策していた。次男・俊介は、親友の標耕平や画家の灰山の影響で伍代家には批判的になっていく。長女・由紀子は柘植中尉との愛にすべてを賭ける。やがて、満州事変から上海事変へと戦火は拡大していった……。五味川純平の小説の映画化で、日本財閥と関東軍の謀略を、オールスター・キャストで描き上げた力作。ビデオは前篇と後篇がある。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("謝謝OSAKA", "謝謝OSAKA", "", "", "http://image.eiga.k-img.com/images/movie/57501/315x210.jpg?4762", 32, "吉本興業が主催する2011年・第3回沖縄国際映画祭の企画「地域発信型プロジェクト」で製作された6本の短編作品を、「東」と「西」の3本ずつに分けて劇場公開する「沖縄国際映画祭　地域発信型映画」の「西」の1本。写真に写るある場所を探していた中国人留学生のシューロンと、撮影予定だったモデルがドタキャンして代役を探す日本人女性の小春が偶然出会う。小春はシューロンをモデルになんとか撮影を終え、大阪の町にあるというシューロンの思い出の場所を一緒に探すが……。", "2011-12-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WIELKI TYDZIEN", "聖週間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "祖国ポーランドの悲劇的な歴史に基づく反戦ドラマを作り続けるA・ワイダ監督が、「灰とダイヤモンド」の原作者でもあるJ・アンジェイェフスキの中編小説を映画化。ワルシャワ・ゲットーの住民がナチスに反発し、一斉蜂起した1943年４月のポーランド。建築家の父をドイツ軍に連行されたユダヤ人女性イレーナが、かつての恋人ヤンとその妻の家に身を寄せる。やがてアパートの隣人や家主に見つかってしまい、激しく敵視される彼女の運命とヤン夫婦の苦悩が、緊迫した心理劇として語られていく。", "1995-01-01", "42,7,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blow", "ブロウ", "", "", "http://image.eiga.k-img.com/images/movie/1301/315x210.jpg?3438", 123, "監督はユマ・サーマン、ナタリー・ポートマンらの群像劇「ビューティフル・ガールズ」（96）のテッド・デミ。脚本は「アメリカン・ヒストリーX」（98）のデビッド・マッケンナと、ジョン・カサベテスの息子であり「シーズ・ソー・ラブリー」（97）の監督でもあるニック・カサベテスが共同で担当。撮影はスパイク・リー監督「サマー・オブ・サム」の女流エレン・クラス。元ピーウィー・ハーマン＝ポール・ルーベンスが主人公の相棒役を熱演。", "2001-09-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Leon The Professional", "レオン", "", "", "http://ecx.images-amazon.com/images/I/21VA52XDR0L._SL210_.jpg", 110, "「グラン・ブルー」のリュック・ベッソン監督のハリウッドデビュー作。舞台はニューヨーク。家族を殺され、隣室に住む殺し屋レオンのもとに転がり込んだ12才の少女マチルダは、家族を殺した相手への復讐を決心する。少女マチルダを演じるのは、オーディションで選ばれ、本作が映画初出演となったナタリー・ポートマン。また、寡黙な凄腕の殺し屋レオンをベッソン作品おなじみのジャン・レノが演じている。", "1995-03-31", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エイトレンジャー", "エイトレンジャー", "", "", "http://image.eiga.k-img.com/images/movie/57941/315x210.jpg?1252", 0, "人気アイドルグループ「関ジャニ∞」が、2005年12月のコンサート「関ジャニ∞ Xmas パーティー2005」で初演して以降、各地のコンサートでご当地ネタなどを盛り込みながら、戦隊パロディとして7年間にわたり上演を続けてきた名物コーナーを、「20世紀少年」の堤幸彦監督のメガホンで映画化。荒廃した近未来都市エイトシティーを舞台に、「関ジャニ∞」メンバー7人が演じるヒーロー「エイトレンジャー」が、テロリスト「ダーククルセイド」に立ち向かう姿を描く。映画版オリジナルのキャラクターとしてエイトレンジャーを導き、ともに戦う伝説のヒーロー、キャプテン・シルバーに舘ひろしが扮する。", "2012-07-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真夜中のボクサー", "真夜中のボクサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "八百長試合に嫌気がさして、王座を夢見ていたボクシングを捨て、ただひたすら流氷を見るために、北への旅を続ける男の姿を描くロード・ムービー。芥川賞作家・高橋三千綱の原作、企画、脚本、監督作品。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サカサマのパテマ", "サカサマのパテマ", "", "", "http://image.eiga.k-img.com/images/movie/57651/315x210.jpg?3577", 0, "2008年のネット配信が口コミで広まり、10年に公開された劇場版もロングランヒットを記録した「イヴの時間」の吉浦康裕監督が手がけるオリジナル劇場用長編アニメーション。暗く狭い坑道が広がる地下世界で、人々は防護服を身にまといながらも、明るく日々を過ごしていた。そんな地下世界の集落のお姫様パテマは、坑道を探検するのが毎日の楽しみで、集落の掟（おきて）により立ち入りが禁じられている「危険区域」がお気に入りの場所だった。そんなある日、いつものように危険区域に立ち入ったパテマは、そこで予期せぬ出来事に遭遇し、世界に隠された秘密に触れる。", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOUCHED BY LOVE", "ラスト・レター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "不治の病に苦しむ少女カレンが、看護婦のリナや憧れのスター、エルヴィス・プレスリーからの手紙や贈物に支えられ、けなげに生きた日々を描く実話の映画化。カレンを演じるD・レインのあどけない初々しさが印象深い感動作だ。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("閑古鳥が泣いてたら", "閑古鳥が泣いてたら", "", "", "http://image.eiga.k-img.com/images/movie/57441/315x210.jpg?1971", 15, "無名の若手監督たちが作り上げた15分の短編10作品を特集上映する「NO NAME FILMS」の1本。とある喫茶店のマスター・小林晃は、経営不振で閉店するかもしれないとアルバイトの中村有紀に伝える。すると中村は誰かと電話で話し込み、そのまま店を飛び出してしまう。顔も頭も悪い中年男の心意気を細やかな人間描写で描き出していく。監督は、第32回ぴあフィルムフェスティバルで映画ファン賞を受賞した「真っ赤な嘘」の小林岳。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陰陽師〈おんみょうじ〉II", "陰陽師〈おんみょうじ〉II", "", "", "http://ecx.images-amazon.com/images/I/51894TXGR9L._SL210_.jpg", 115, "ヒット作の２年ぶりの続編。当たり役、安倍晴明に狂言師・野村萬斎が再挑戦し、その魅惑的なキャラクターを倍化している。今回は、ある事件を調査するうちに、神と崇められる術師の野望に直面した晴明が勝ち目の薄い勝負に全身でぶつかっていく。深田恭子が男勝りの姫を抜群の存在感で体現、中井貴一が悪役を嬉々として演じている点にも注目したい。平安時代、親友の源博雅に頼まれ、陰陽師・安倍晴明は、右大臣・藤原安麻呂の娘・日美子の夢遊病の原因を探る。やがて、彼女が安麻呂の実の娘でないことが発覚。晴明は、どんな病も治すという術師・幻角の存在にぶち当たる。", "2003-10-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Black House", "黒い家（2007）", "", "", "http://image.eiga.k-img.com/images/movie/53371/315x210.jpg?4525", 104, "保険会社の査定員ジュノは、自殺でも保険金は支払われるのかという相談電話を受ける。数日後、全く面識のない顧客チュンベの家に名指しで呼び出された彼は、チュンベの義理の息子が首吊り自殺しているのを発見。チュンベによる保険金殺人を疑ったジュノは警察に通報し、会社に保険金の支払いを保留するよう報告するが……。日本でも森田芳光監督により映画化された貴志祐介のホラー小説を韓国で映画化。主演は「甘い人生」のファン・ジョンミン。", "2008-04-05", "5,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EDITH ET MARCEL", "恋に生きた女ピアフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 162, "恋多き女といわれたシャンソン歌手エディット・ピアフと、不世出のボクサー、マルセル・セルダンとの悲恋を描く。第二次大戦直後の1947年、ピアフはニューヨークの公演を成功させる。その時やはりニューヨークで不敗のままで世界チャンピオンとなったマルセル。二人は激しい恋の炎を燃やすが……。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FROM　DUSK　TILL　DAWN　３：THE　HANGMAN’S　DAUGHTER", "フロム・ダスク・ティル・ドーン3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "R・ロドリゲスとQ・タランティーノ製作総指揮による、荒唐無稽なホラー・アクションの第３作。19世紀のメキシコを舞台に、１作目でゲッコー兄弟を苦しめたヴァンパイア・クイーン“地獄のサンタニコ”誕生の秘密を西部劇仕立てで描く。１作目の10年後を描いた「…２」と同時公開された。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("箱根風雲録", "箱根風雲録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "前進座が「どっこい生きてる」（1952年・今井正監督）に続いて新星映画と共同製作した作品。原作はタカクラテルの『箱根用水』。芦ノ湖の水を西へ引く大工事を商人と農民が計画するが、威信の失墜を恐れた江戸幕府は妨害を加え……。権力者VS庶民の構図で切り込む、山本監督のエッセンスがかい間見られる。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ABBA: The Movie", "アバ　ザ・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1976年に発表した「ダンシング・クイーン」が全米第1位を獲得し、絶大な人気を誇ったスウェーデンの4人組ポップ・グループ“アバ”。彼らが1977年に行なったオーストラリア・ツアーの模様を、上司から密着インタビューを命じられたラジオDJが彼らを追うという物語仕立てで描いた、セミドキュメンタリー・フィルム。監督・脚本を担当するのは、「ショコラ」等の作品で知られるスウェーデンの名匠ラッセ・ハルストレム。", "1977-01-01", "11,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY BLOODY VALENTINE", "血のバレンタイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "鉱山町ハニガー。20年前のバレンタインの日に起こった不幸な事件と事故以来、この町ではバレンタイン・デーは禁止されていた。しかし、それが解禁された時ウキウキする若者たちに、次々と惨劇が襲いかかる……。定番ホラーとしては見事なサスペンス演出で、マニア以外にも十分見せる。", "1981-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRUCE LEE IN THE GREEN HORNET", "ブルース・リーのグリーン・ホーネット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "昼は新聞社の青年社長ブリッド・レイド、夜は助手のカトーと組んで悪を倒す神出鬼没のグリーン・ホーネット。３話から構成された迫力のカンフー・スタイルの勧善懲悪活劇。B・リーの記念すべきアメリカTV番組出演第１作で、日本では劇場公開された。ビデオ、DVDタイトルは「ブルース・リーINグリーン・ホーネット」。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NUKIE", "ヌーキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "銀河の彼方からやって来た可愛いエイリアンが、地球の子供たちと仲良くなり、はぐれた兄弟を捜すという心温まるSFファンタジー。どことなく「E.T.」を彷彿させるストーリー展開が魅力的な作品。アメリカのマイアミとアフリカのオラピ砂漠に、それぞれ不時着したエイリアンの兄弟ヌーキーとミコは果たして再会できるのか？", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHY　IS　YELLOW　MIDDLE　OF　RAINBOW？I　AM　FURIOUS　YELLOW…1994／BAKIT　DILAW　ANG　GITNA　NG　BAHAG−HARI？", "虹〈キドラット〉のアルバム　僕は怒れる黄色'94", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 175, "フィリピンを代表する個人映画作家K・タヒミックが、独創的な手法で構築した日記風のドキュメンタリー。撮影は1981年から開始され、1987年に最初のバージョン「僕は怒れる黄色」を公開。以後、追加撮影、編集変更などを繰り返し、多彩なバージョンを生み出し“進化”してきた作品の、とりあえずの最終版である。映画は「僕はとるにたらない緑」から「僕らはインディオ住民の茶色」までの全７部構成。タヒミックと息子の語らいを中心に、マルコス独裁政権崩壊からコリー・アキノ大統領誕生に至る激動のフィリピンの政治状況、“第三世界”についての思索、先住民の問題から大宇宙の摂理など、雑多な事柄についての映像考察をあくまでも日常感覚で映し出していく。過酷な現状や壮大なテーマに彩られてはいるが、エッセイのような語り口が観る者に親近感を与える１本。", "1994-01-01", "35", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ストレンヂア　無皇刃譚", "ストレンヂア　無皇刃譚", "", "", "http://image.eiga.k-img.com/images/movie/34211/315x210.jpg?2020", 102, "戦乱の世を舞台に、ひとりの少年を巡って繰り広げられる男たちの戦いをハイクオリティ映像で描いた時代劇アニメーション。下克上や野盗が横行する戦乱の時代。禅僧・祥庵の庇護の下、仔太郎という名の少年が中国大陸からやって来た。同じ頃、仔太郎の身体に隠されたある秘密を狙う明国の集団も日本へとたどり着く。刺客に襲われたところを謎の剣士“名無し”に救われた仔太郎は、彼と行動を共にすることになるが……。", "2007-09-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談佐賀屋敷", "怪談佐賀屋敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "講談で有名な『鍋島怪猫記』の映画化。お家争いに巻き込まれ、井戸に投げられて死んだ家老の呪いのため、奇怪な化け猫にほんろうされる佐賀藩城内の騒乱を描く。浪花千栄子が猫に憑かれて狂態を見せるほか、“怪猫女優”入江たか子も面目躍如たる怪演ぶりで大いに怖がらせる。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Joe Strummer: The Future Is Unwritten", "LONDON CALLING ザ・ライフ・オブ・ジョー・ストラマー", "", "", "http://image.eiga.k-img.com/images/movie/1931/315x210.jpg?3722", 123, "1977年に鮮烈なデビューを飾り、現在でも多くのアーティストに影響を与え続けているイギリスのパンク・バンド“ザ・クラッシュ”。その中心的存在で、2002年にこの世を去ったジョー・ストラマーの人生を振り返るドキュメンタリー。ジョニー・デップやU2のボノらが思いを語るほか、クラッシュ結成当初のライブなど貴重な映像も満載。監督は「NO FUTURE A SEX PISTOLS FILM」のジュリアン・テンプル。", "2007-09-08", "33,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("失恋殺人", "失恋殺人", "", "", "http://image.eiga.k-img.com/images/movie/55441/315x210.jpg?1642", 100, "江戸川乱歩の「妻に失恋した男」を翻案し、原作には登場しない明智小五郎の妻・文代を探偵役に描いたサスペンス。親の遺産で何不自由なく暮らす収一は、妻・みや子に愛されていないことに苦しみ、夫妻のかかりつけの歯科医・琴浦に悩みを打ち明ける。みや子の浮気を疑った琴浦は、明智小五郎探偵事務所に調査を依頼する。明智の妻・文代は調査を始めるが、事件は誰も想像し得なかった衝撃の結末に向かっていく。宮地真緒が妖艶な妻を演じ、星野真里が探偵・明智文代に扮する。", "2010-04-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さらば映画の友よインディアンサマー", "さらば映画の友よインディアンサマー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ロサンゼルス在住で映画評論家としても活躍していた原田眞人の監督第１回作品。人生の目標は、年365本の映画を20年間見続けることだとうそぶく中年の映画狂と、映画館の暗闇に浸る映画青年、美少女が繰り広げる青春物語。川谷の『雨に唄えば』が見もの。浅野は３本目の映画出演で美しい裸体を見せる。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カラフル（2010）", "カラフル（2010）", "", "", "http://image.eiga.k-img.com/images/movie/55171/315x210.jpg?1946", 127, "直木賞作家の森絵都のベストセラー小説を、「クレヨンしんちゃん」シリーズや「河童のクゥと夏休み」などで知られる原惠一監督がアニメ映画化。原監督と2度目のタッグとなる冨澤風斗、宮崎あおい、麻生久美子らが声優を務める。天上界と下界のはざ間でさまよっていた“ぼく”の魂は、プラプラという名の天使から人生に再挑戦するチャンスを与えられ、自殺したばかりの内気な少年・小林真の体に入り込む。真として生き返った“ぼく”の魂は自殺の理由を知るが、その真らしくない振る舞いで周囲の環境を少しずつ変えていく。", "2010-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I girasoli", "ひまわり", "", "", "http://image.eiga.k-img.com/images/movie/48421/315x210.jpg?3690", 107, "ソフィア・ローレン、マルチェロ・マストロヤンニというイタリアの2大スターを主演に迎え、戦争によって引き裂かれた男女の愛を描いたドラマ。結婚して幸せな日々を送っていたジョバンナとアントニオだったが、第2次世界大戦が勃発し、アントニオはソ連の最前線に送られてしまう。終戦後、帰らない夫を探しにソ連を訪れたジョバンナは、命を救ってくれたロシア人女性との間に家庭を築いていたアントニオと再会する。逃げるようにイタリアに戻ったジョバンナだったが、数年後、もう一度やり直したいとアントニオが訪ねてくる。「ミラノの奇蹟」（1951）、「悲しみの青春」（71）などで知られ、74年に他界したイタリアの名匠ビットリオ・デ・シーカの晩年の名作。2011年、ニュープリントでリバイバル公開。", "1970-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忘却の花びら・完結篇", "忘却の花びら・完結篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "愛のすれ違いドラマの続編。前作で仕事中に怪我をした藤崎は、九州の日南に向かう。鮎子もそのあとを追うが、藤崎は東京へと向かったあとだった。鮎子に結婚を迫っていた庄田は、藤崎は死んだと嘘をつき、結婚を承知させる。だが新婚旅行に旅立った彼女は庄田の嘘を知り……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギター抱えたひとり旅", "ギター抱えたひとり旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "氷室は兄と恋人をイカサマ賭博師の辺見によって失い、復讐のため辺見を追って信州に向かった。ひょんなことから、暴力団貝原組の客人として迎えられた氷室だが、貝原組の主催する賭博場に辺見がやって来ることを知る……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワラライフ!!", "ワラライフ!!", "", "", "http://image.eiga.k-img.com/images/movie/55561/315x210.jpg?1626", 94, "「ニセ札」で映画監督デビューを果たしたお笑い芸人・木村祐一の監督第2作。彼女との結婚に向けて新居を探している青年が、同時期に引っ越すことになった実家を訪れ、家族との思い出を振り返っていく。主演を務めるのは、本作が映画初出演となるお笑いコンビ「しずる」の村上純。香椎由宇、高岡蒼甫、鈴木杏樹、吉川晃司らが脇を固める。", "2011-01-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUNKER PALACE HOTEL", "バンカー・パレス・ホテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "フランスのみならずヨーロッパで最高の劇画家として知られるエンキ・ビラルが初監督した幻想的な近未来SF。アンドロイドによって運営される政府高官たちの避難壕“バンカー・パレス・ホテル”。大統領がやって来るその日に反乱軍のスパイがホテルに侵入した。誰が味方で誰が敵か？　ホテル内に不安が募る。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ГАМЛЕΤ／HAMLET", "ハムレット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "ソ連のシェイクスピア研究家として著名なコージンツェフ監督が、原作をよりドラマティックに演出。本家である英国のオリヴィエ作品以上に評価を受けた。ロシア語によるセリフを「ドクトル・ジバゴ」の原作者、ボリス・パステルナークが手掛けた。", "1964-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("15 minutes", "15ミニッツ", "", "", "http://image.eiga.k-img.com/images/movie/1001/315x210.jpg?3500", 121, "ニューヨークで残酷な放火殺人事件が起きた。事件を担当することになったのはNYPDきっての有名刑事、エディ。彼とコンビを組むことになったのはメディア嫌いの若手放火調査官のジョーディ。そんな中、凶悪な連続殺人犯とそれをビデオカメラで記録し続ける相棒のサイコな2人組は、次の標的をエディに定める……。", "2001-05-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PINK PANTHER", "ピンクの豹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "ユーモラスなテーマ・ミュージックにのせて、ドジなクルーゾー警部の活躍ぶりをコミカルに描いたヒットシリーズ“ピンク・パンサー”の第１作。“ピンクの豹”なる豪華な宝石を狙う超一流の泥棒とクルーゾーの大乱戦が、ドタバタ調で全編に繰り広げられる。DVDは「ピンク・パンサー フィルム・コレクション」に収録。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Transformers: Revenge of the Fallen", "トランスフォーマー　リベンジ", "", "", "http://image.eiga.k-img.com/images/movie/53181/315x210.jpg?2709", 150, "2007年に大ヒットを記録したSFアクション大作「トランスフォーマー」の続編。正義のトランスフォーマー“オートボット”との戦いに敗れた悪の“ディセプティコン”が、合体トランスフォーマーのデバステイターや、メガトロン以上に凶悪なフォールンら、新たな仲間を引き連れて再び地球を襲い、戦いを巻き起こす。マイケル・ベイ監督、主演のシャイア・ラブーフら前作のスタッフ、キャストが再結集。", "2009-06-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILLIAN PETER BLATTY’S THE EXORCIST III", "エクソシスト３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "“オカルト・ブーム”を生み出し、大ヒットした第１作から17年ぶりに作られた第３作。監督には、１作目とそして本編の原作者であるW・P・ブラッティがあたっている。第１作でリー・J・コッブが演じたキンダーマン警部補に、今回はオスカー男優G・C・スコットが扮し、重厚な演技を披露。オカルト色は薄まり、刑事ドラマの色合いが濃い仕上がりとなった。キンダーマン警部補は病院から盗まれた大バサミで首や体を切断されるという猟奇事件を担当。その裏には、またも悪魔憑きの影が……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUNS OF MAGNIFICENT ７", "新 荒野の七人 馬上の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「荒野の七人」シリーズの第３作。メキシコの悪政に立ち向かうため農民が結起、だが、その力を組織化できる唯一の革命家キンテロは獄中の身だった。キンテロの部下の若き中尉マックスは、キンテロを救出すべく仲間を集める。７人の男は、さらに囚人20人を味方につけて刑務所に乗り込むが……。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高原のお嬢さん", "高原のお嬢さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "舟木一夫、和泉雅子のコンビによる歌謡恋愛映画。蓼科高原で牧草の研究に没頭する北川和夫は、ある日、小泉淳子という女性に出会い、一目惚れする。その頃、観光開発会社社長の息子、進が土地買収のため乗り込んで来たが、進は和夫の小学校の同級生であり、淳子のフィアンセだった。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浅草キッドの「浅草キッド」", "浅草キッドの「浅草キッド」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ビートたけしが原作、ダンカンが脚本を手掛けた、お笑い芸人の成長を綴った感動ドラマ。“たけし”は大学を中退し、浅草フランス座で飛び込み芸人修行を始める。尊敬する師匠や夢を持った仲間たちとの出会いの中で、自分を見つめ発見していく過程を描いている。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A｀ DOUBLE TOUR", "二重の鍵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ヒッチコックに傾倒するシャブロルがヒッチコック・タッチで撮ったサスペンス映画。南フランスの広い邸宅にマルク一家が住んでいた。一家の主アンリと隣人レダは愛人関係だったが、ある日、レダは何者かに殺されてしまい……。ドカエのあざやかな色彩撮影が印象的。", "1959-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Machine Girl", "片腕マシンガール", "", "", "http://image.eiga.k-img.com/images/movie/53691/315x210.jpg?2878", 96, "不良グループに弟を殺された女子高生アミは復讐に乗り出すが、逆に捕らえられ拷問の末に片腕を失ってしまう。なんとか逃げ出した彼女は、同じく息子を殺されたミキと特訓を開始。腕のかわりに鋼鉄製マシンガンを装着して戦いに挑む。製作はアメリカ、スタッフ・キャストは全て日本人という異色のB級スプラッター・アクション。「猫目小僧」などの鬼才・井口昇が監督・脚本を手がけ、映画初出演のグラビアアイドル・八代みなせがヒロインに抜擢。", "2008-08-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEVER LOOK BACK", "振り向くことなかれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "弁護士アンは、あるパーティーで元恋人のガイと再会する。ガイは結婚していたが、妻とはうまくいっていなかった。今朝もけんかをしてきたというガイは、アンの家に泊めてもらうことになる。もちろん別々の部屋で……。翌日、帰宅したガイは殺されている妻を発見、当然疑いは彼のもとに。ガイのアリバイを証明できるのは、アンしかいない。そのアンにも弁護士という地位と今の恋人がいる。ガイの弁護を引き受けるアンだったが……。追い詰められていく二人の姿を、F・シール監督が巧みに描いていく。", "1952-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ARISTOCATS", "おしゃれキャット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "企画中に死去したウォルト・ディズニーの遺志を継いで、スタジオのスタッフ陣が完成させたほのぼのアニメ。裕福な老婦人が、遺産を愛猫ダッチェスと３匹の子猫たちに譲るつもりだと知り、執事が大あわて。彼はこれを阻止しようと、ダッチェスたちを郊外に捨ててきてしまう。途方に暮れたダッチェスたちは、野良猫のオマリーに助けられ……。シャンソンの大御所モーリス・シュバリエが歌う主題歌や、ジャズ界のスター、スキャットマン・クローザースが歌う劇中歌が、おしゃれで軽快な作品のムードを盛り上げている。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nurse Betty", "ベティ・サイズモア", "", "", "http://image.eiga.k-img.com/images/movie/1571/315x210.jpg?4643", 110, "カンザスの田舎町に住むウェイトレスのベティは、女癖の悪い亭主に泣かされる日々。でも彼女には大好きな昼メロという支えがあった。ドラマを見ながら、自分が看護婦になって主人公の医師と恋をするという空想にふけるときが最も幸せな時間だった。だがある日、目の前で夫が殺されショックを受けたベティは、現実と夢の境を飛び越え自分を昼メロの中の看護婦だと思い込む。彼女は憧れの医師と結ばれるためハリウッドへと旅立った。夫を殺した犯人に追われているとも知らずに……。", "2001-05-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動戦士ガンダムUC episode1「ユニコーンの日」", "機動戦士ガンダムUC episode1「ユニコーンの日」", "", "", "http://image.eiga.k-img.com/images/movie/55261/315x210.jpg?1930", 58, "「ローレライ」「亡国のイージス」の福井晴敏が手がけた小説を全6話のオリジナルビデオアニメとして映像化し、その第1話「ユニコーンの日」をDVD＆ブルーレイの発売に先行して劇場で上映。シャアの反乱から3年後の宇宙世紀0096年、工業コロニー・インダストリアル7に暮らす少年バナージは、謎の少女オードリーと出会ったことから、歴史を覆す秘密が眠る“ラプラスの箱”を巡る争乱に巻き込まれ、父親のカーディアスから純白のMSユニコーンを託される。", "2010-02-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPERIAMO CHE SIA FEMMINA", "女たちのテーブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "美しいトスカーナ地方の郊外に住む女ばかりの家族のドラマ。館の主、エレーナは、愛人を作って長い間、家を留守にしている夫に代わって農場を経営している。そこへ、ひょっこり帰って来た夫は急死し、娘は突然結婚する——といったことが次々に起こる。そして女たちは去って行く。エレーナは今までの苦労が無駄に終わったと思い、財産を処分して、この地を離れようとする。そこへ再び集まって来る一家の女たち。名匠モニチェッリが描く、ほのぼのとしたホーム・ドラマ。", "1985-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHAFT", "黒いジャガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ダンディーな黒のレザー・コートに身を包み、必殺のリボルバーを携えた黒人私立探偵シャフトの活躍を描いた軽快なアクション。写真家出身のパークス監督をはじめ、スタッフ・キャストすべて黒人で製作されヒットし、続編も作られた。ヘイズのテーマ曲も出色の出来だ。2000年にサミュエル・L・ジャクソン主演「シャフト」というタイトルでリメイクされた。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("砂の器〈デジタルリマスター〉", "砂の器〈デジタルリマスター〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "今は亡き野村芳太郎監督が手がけた1974年の社会派サスペンスの、デジタル修正が施されたニュープリント版。2005年に公開され大ヒットを記録した。格段にクリアになった映像と音声の素晴らしさもさることながら、松本清張原作による人間の哀しき宿命を見つめた物語も心に染みる。迷宮入り寸前の殺人事件を追う刑事の今西と吉村は、僅かな手がかりをもとに奔走。やがて捜査線上に天才音楽家、和賀の名が浮上する。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("楳図かずお恐怖劇場　プレゼント", "楳図かずお恐怖劇場　プレゼント", "", "", "http://ecx.images-amazon.com/images/I/51GWAB88MXL._SL210_.jpg", 60, "恐怖マンガの草分け的存在として長年にわたって活躍してきた楳図かずおの短編コミック6作品を実写映画化した「楳図かずお恐怖劇場」の1編で、「魁!!クロマティ高校 THE★MOVIE」の山口雄大監督がメガホンを取ったスプラッターホラー。クリスマスイブの晩、友人たちに連れられてホテルを訪れた優子は、思いを寄せていた良太と結ばれる。ところが、聖なる夜を冒涜するような行為を続ける2人に、怒り狂ったサンタクロースが襲いかかり……。", "2005-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bak-Jwi", "渇き", "", "", "http://image.eiga.k-img.com/images/movie/55041/315x210.jpg?3953", 133, "「オールド・ボーイ」「サイボーグでも大丈夫」の鬼才パク・チャヌク監督によるバンパイア映画。アフリカで行われたワクチン開発の実験台となり、その際に輸血された血液のせいでバンパイアとなってしまった神父サンヒョン。幼なじみの妻であるテジュと恋に落ちた彼は、テジュから夫を殺害して欲しいと頼まれるが……。主演は「殺人の追憶」「グエムル／漢江の怪物」の名優ソン・ガンホ。第62回カンヌ国際映画祭で審査員賞を受賞。", "2010-02-27", "5,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DARK RIDER", "ハードチェイサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "黒の皮ジャンに身を包み、黒のバイクにまたがった一人のライダーの活躍を描いたハードなアクション映画。彼がネバダ州のとある町を牛耳るギャングたちに立ち向かっていく。迫力のカーチェイスが見もの。マーティン・シーンの双子の兄弟、J・エステベスが、冷酷なギャングのボスを演じている。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の座", "女の座", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "名優・笠智衆が主演した成瀬巳喜男監督作品。正月興行らしい豪華キャストで、家族制度における妻の立場を問う。家庭の中で唯一の他人ともいうべき長男の未亡人が、義理の両親を理解するというラストに、小津の「東京物語」へのオマージュが感じられて微笑ましい。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リトル・シンドバッド／小さな冒険者たち", "リトル・シンドバッド／小さな冒険者たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "イスラム教戒律のため、今まで映画上映さえも禁止されていたというサウジアラビアに、ロケを敢行した合作映画。父の仕事の都合でマレーシアに住む日本人少年が、転校したクラスメイトを追って、地元の少年とともに小舟で航海に出るが……。二人の少年が繰り広げる冒険と友情を描いたファミリー・ピクチャー。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒猫／BLACK CAT", "黒い女豹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ニューヨークでウェイトレスをしている平凡な女性キャサリンは、ある日しつこくからんできた男を殺してしまった。それからキャサリンの運命は大きく変わる。脳にマイクロチップを埋め込まれて、殺人マシーンへと“変身”してしまうのだ……。ニューヨーク、香港、そして日本を舞台に、「ニキータ」を思わせるアクションが展開する。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("13階段", "13階段", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "反町隆史が従来の豪放なイメージを一転、センシティブな新境地を見せたミステリー。江戸川乱歩賞受賞作の３度目となる映画化。ある死刑囚のえん罪を晴らす仕事を請け負った仮出所中の青年が、刑務官の男とともに真実に迫っていく。喧嘩で相手を殺してしまい３年の刑に服していた三上純一が仮釈放された。刑務所の刑務主任、南郷正二は死刑確定囚、樹原亮の無実を証明するための調査を三上に依頼する。三上は弁護士の杉浦とともに調査を進めるが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WEEK−END A｀ ZUYDCOOTE", "ダンケルク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "R・メルルの小説『ズイドコートの週末』の映画化。1940年６月、北フランスのダンケルクに近い海岸に40万人近くの兵士たちがいた。その中の３人の男たちが出会った娘をめぐるエピソードを中心に、戦争による悲劇を厳しく見つめている。", "1964-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("燃ゆるとき", "燃ゆるとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "「金融腐食列島」などで知られる作家・高杉良のベストセラー小説を、中井貴一主演で映画化した社会派ドラマ。実在の食品会社をモデルに、カップ麺でアメリカ市場に戦いを挑んだ男たちの姿を描く。カップ麺を主力商品として大企業に成長し、アメリカ進出も果たした東輝水産。しかし、アメリカでは安価なアジア企業に押され、売りあげが伸び悩んでいた。資材担当の営業マン・川森潔は、工場再生の任務を受けて単身渡米するが……。", "2006-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八日目の蝉", "八日目の蝉", "", "", "http://image.eiga.k-img.com/images/movie/55771/315x210.jpg?4097", 147, "直木賞作家・角田光代の原作小説を、井上真央、永作博美の主演で映画化したヒューマンサスペンス。監督は「孤高のメス」の成島出。1985年、自らが母親になれない絶望から、希和子（永作）は不倫相手の子を誘拐してわが子として育てる。4歳になり初めて実の両親の元に戻った恵理菜（井上）は、育ての母が誘拐犯であったと知り、心を閉ざしたまま成長する。やがて21歳になった恵理菜は妊娠するが、その相手もまた家庭を持つ男だった……。", "2011-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUELQUES JOURS AVEC MOI", "僕と一緒に幾日か", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "“何を考えているのかわからない”表情が魅力の人気者ダニエル・オートゥイユを退院したばかりの自閉症男に配し、彼と彼を取り巻く人々の悲喜こもごもを淡々と描いた作品。クロード・ソーテ監督が階級意識のはっきりしたフランスの地方都市を舞台に、人生の価値を優しく問いかける。マルシャルはすべてに無関心、無感動。大手スーパーマーケットの御曹司である彼が、療養所を出てした社会復帰第一歩は、地方のチェーン店の視察だった。そこで帳簿の不正を見つけた彼は、その街でアパートを借りることにした。実は、支店長の家のメイド、フランシーヌと話したいと思ったからだった……。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOWN WITHOUT PITY", "非情の町", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "第二次大戦直後、米軍駐留下の西ドイツの町で、一人の少女が４人の米軍兵士に暴行された。４人の弁護を引き受けた米軍中佐は、罪を憎みながらも死刑だけは免れさせようと画策したが、そのために少女は自殺。中佐は町の人々に非難の目を向けられる。軍と町の非情な構造を鋭くえぐる異色作。", "1961-01-01", "1,26,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The L.A. Riot Spectacular", "ザ・L.A.ライオット・ショー", "", "", "http://image.eiga.k-img.com/images/movie/33741/315x210.jpg?4728", 74, "1992年に発生したロサンゼルス暴動をブラック・ユーモアたっぷりに描いた問題作。白人警官たちによる黒人男性への暴行事件発覚をきっかけに、日頃からくすぶっていた黒人たちの不満が爆発して大規模な暴動へと発展した。実際のニュース映像などを交えながら事件の真相に迫り、人種差別や経済格差など現代アメリカが抱える問題を浮き彫りにしていく。ロサンゼルス出身の人気ラッパー、スヌープ・ドッグが進行役を務める。", "2006-11-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A WOMAN SCORNED", "罠の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "決して裕福とはいえないまでも、幸せに暮らしていたトルーマンとパトリシア。しかし、トルーマンが勤める会社に、重役としてアレックスが迎えられた時、二人の幸福は崩壊する。トルーマンは会社を解雇され、自殺する。愛する夫を失い、絶望の淵に立たされたパトリシアは、アレックスを憎み、復讐を誓う。彼女は、何食わぬ顔でアレックスの息子ロビーの家庭教師になり、一家に潜入する。そして巧妙な罠を仕掛けて、一家を破滅に追い込もうとたくらむが……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Faubourg 36", "幸せはシャンソニア劇場から", "", "", "http://image.eiga.k-img.com/images/movie/54571/315x210.jpg?4012", 120, "1936年パリ。音楽ホールのシャンソニア劇場が不況のため閉館し、長年裏方を務めてきたピゴワルは職を失う。失業者のままでは子育ての資格はないと、愛する息子と引き離されてしまったピゴワルは、再び息子と暮らしたい一心で、かつての仲間とともに劇場再建に乗り出す。「コーラス」の監督クリストフ・バラティエ、製作ジャック・ペラン、主演ジラール・ジュニョが再結集した人間ドラマ。撮影はクリント・イーストウッド作品で知られるトム・スターン。", "2009-09-05", "2,43,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dragonball Evolution", "DRAGONBALL EVOLUTION", "", "", "http://image.eiga.k-img.com/images/movie/53221/315x210.jpg?2071", 87, "鳥山明原作の人気コミックをハリウッドで実写映画化。7つ集めればどんな願いでも叶うというドラゴンボールをめぐって、永遠の若さを手にして地球制覇を企てるピッコロ大魔王と、悟空たちが激しい争奪戦を繰り広げる。悟空役に「宇宙戦争」のジャスティン・チャットウィン、ブルマ役にエミー・ロッサムのほか、チョウ・ユンファ、田村英里子も出演。監督は「ファイナル・デスティネーション」「ザ・ワン」のジェームズ・ウォン。", "2009-03-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人生のお荷物", "人生のお荷物", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "松竹蒲田お得意の小市民映画。初老のサラリーマン・福島には三女一男があった。長女は医師に、次女は画家に嫁いでいた。三女は未婚だったが、やがて縁談もまとまり軍人のもとに。三女の結婚式を終えた福島は妻とホッとしながら家に帰る。が、それもつかの間、よくよく考えてみると晩年に生まれた小学生の長男がまだ残っていた。この子を一人前にするまではまだ頑張らなくては……。やれやれ、というため息の中に、ほのかな幸福感がにじみ出る蒲田調の味わい。斎藤達雄がうだつの上がらぬ亭主役にペーソスをにじませ、田中絹代が洋服を粋に着こなすモダン・ガールを好演している。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("皇帝のいない八月", "皇帝のいない八月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "社会派の山本薩夫が、自衛隊のクーデターを扱った小林久三の小説を映画化したサスペンス。クーデターの主謀者が乗る列車“さくら号”と、政界での駆け引きを同時進行で描いている。当時、テロやクーデターものはタブー視されていた。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銭形平次捕物控・人肌蜘蛛", "銭形平次捕物控・人肌蜘蛛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "３年前、材木買い占めの罪を上総屋と尾張藩にきせられた新吉が流刑地から脱走した。一方、新吉の弟・新次郎が兄を捜しに江戸へ出てきたが、彼は上総屋の娘と恋に落ちる。その時から謎の連続殺人事件が始まったのだが……。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wedding Crashers", "ウエディング・クラッシャーズ", "", "", "http://ecx.images-amazon.com/images/I/211S83N9HGL._SL210_.jpg", 119, "「シャンハイ・ナイト」のオーウェン・ウィルソンと「Mr.＆Mrs.スミス」のビンス・ボーン共演によるラブコメディ。離婚調停人のジョンとジェレミーには、他人の結婚式に紛れこんで大騒ぎし、そこでナンパした女性をお持ち帰りする「結婚式荒らし」という趣味があった。ある日、2人は財務長官の娘の結婚式に潜入するが、ジョンは次女クレアに本気でひと目惚れ、そしてジェレミーは末娘グロリアに付きまとわれてるようになってしまう。", "2005-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KABUL TRIANGLE", "カブール・トライアングル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "タリバン政権崩壊後のアフガニスタンが復興する姿を追った日本・アフガニスタン合作のドキュメンタリー。長く戦乱が続いた同国の今を3編の作品で見つめ、解禁された芸術活動に込められた平和への思いに胸を打つ必見作だ。夫と子供を失った女性の悲しい告白をベースにした「偽装結婚の果て」。ひとりの少年の家族を通して、苦難を極める民衆の生活環境を見つめる「生計をたてる人々」。タリバン政権下、地位を剥奪された女性受難の時代と復権を追う「刻のなかの女性」。", "2005-01-01", "77,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Morte a Venezia", "ベニスに死す　ニュープリント版", "", "", "http://image.eiga.k-img.com/images/movie/56941/315x210.jpg?2442", 131, "巨匠ルキノ・ビスコンティの「山猫」と並ぶ代表作「ベニスに死す」（1971）を、製作40周年を記念してニュープリントで上映。静養のためイタリア・ベニスを訪れたドイツの高名な作曲家アシェンバッハは、滞在先のホテルでポーランド人の美少年タジオに出会う。タジオの美の虜になったアシェンバッハの苦悩と恍こつを、アシェンバッハのモデルにもなったグスタフ・マーラーの交響曲にのせて描く。原作はドイツの文豪トーマス・マンの同名小説。", "2011-10-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Everybody's Fine", "みんな元気（2009）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "マルチェロ・マストロヤンニ主演、ジュゼッペ・トルナトーレ監督の「みんな元気」（90）をロバート・デ・ニーロ主演でリメイクしたファミリードラマ。毎年、感謝祭シーズンには実家に集まるグード家だったが、今年は誰も帰省しないため、やもめ暮らしの父親フランク（デ・ニーロ）は、全米各地でバラバラに暮らす子供たちに会いに行くが……。フランクの子供たちに、ドリュー・バリモア、ケイト・ベッキンセール、サム・ロックウェル。監督は「ウェイクアップ！ネッド」「ナニー・マクフィーの魔法のステッキ」のカーク・ジョーンズ。", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Conversation", "カンバセーション…盗聴…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「ゴッドファーザー」でオスカーを獲得し、ノリに乗っていた頃にコッポラが手掛けた意欲に満ちたサスペンス。その秀逸な作劇術によって、今度はカンヌ映画祭を制し、名実ともに世界を代表する監督として認められた記念すべき作品である。他人にプライバシーを侵されることに恐れを抱いている男。そんな彼が、自身に禁じていたはずの“好奇心”をあるテープに抱いたことから、血なまぐさい殺人事件に巻き込まれていく。マスメディア社会を鳥瞰するマクロな視点と、音によって想像力を喚起する微細でミクロなニュアンスの演出とのバランスが見事。個性派ぞろいのキャスティングにも見ごたえがある。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　特命戦隊ゴーバスターズ（仮題）", "劇場版　特命戦隊ゴーバスターズ（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-08-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Palermo Shooting", "パレルモ・シューティング", "", "", "http://image.eiga.k-img.com/images/movie/56741/315x210.jpg?2492", 108, "「アメリカ，家族のいる風景」（2005）、「ランド・オブ・プレンティ」（04）など2000年代はアメリカを中心に描いてきたビム・ベンダース監督が、およそ12年ぶりにヨーロッパを舞台に撮影したロマンティックスリラー。世界的に成功を収めた写真家のフィンはイタリア・シチリア島のパレルモへ旅に出るが、跡をつけまわすナゾの男の存在に悩まされる。そんな時、美術館の壁画を修復している魅力的な女性フラビアに出会い、2人はフラビアの祖母との思い出の地へ繰り出すが……。主演はドイツの人気パンクバンド「ディー・トーテン・ホーゼン」のカンピーノ。撮影時は存命だった「アメリカの友人」のデニス・ホッパーが共演。", "2011-09-03", "7,2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北陸代理戦争", "北陸代理戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "深作欣二監督による実録ヤクザ映画。北陸の富安組・若頭の川田は、卑劣な親分・安原を追い出すが、安原は大阪の浅田組と組んで、川田を刑務所に送り込む。数年後、川田が出所すると北陸は浅田組の天下となっていた。川田は仲間を集め、浅田組を北陸から一掃する闘いを始める。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN FROM LARAMIE", "ララミーから来た男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ライフル銃を持つインディアンに殺された、弟の仇を討つのが目的で、ララミーからニューメキシコの町へやって来た男。彼はそのライフルをインディアンに売った男を捜し出すのだが……。A・マン監督とJ・スチュアートが組んだコンビ第７作。DVDは「コロムビアトライスター ザ・ウェスタン・ムービーズBOX(1)」に収録。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワールド・アパートメント・ホラー", "ワールド・アパートメント・ホラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "大都市の一角に建つ取り壊し寸前のアパートを舞台に、住人のアジア系外国人労働者と、地上げのためにそこを訪れたヤクザとの奇妙な葛藤を描く。そしてアパートに取り憑いた謎の魔物が出現し、悪魔払いをするが、そこへ今度はヤクザが乗り込んできて……。劇画界の巨匠・大友克洋が初めて挑んだ実写作品で、国際都市・東京の側面を捉えたコメディ・ホラー。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NAVY SEALS", "ネイビー・シールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "米海軍の精鋭を集めた特殊部隊“ネイビー・シールズ”。世界中どこへでも24時間内に行って任務を果たす彼らの活躍を描いたアクション映画。中東で米海軍のヘリコプター・クルーが拉致された。彼らを救出に向かったネイビー・シールズは、テロリストが隠したスティンガー・ミサイルを発見。テロ行為の前に、ミサイルを奪還しようとするが……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Avanti!", "お熱い夜をあなたに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "　監督お気に入りの俳優ジャック・レモンを再び主演に迎えた、ビリー・ワイルダー後期の傑作。父子2代にわたる恋愛模様がイタリアを舞台に描かれる。アメリカの実業家ウェンデルのもとに、父の自動車事故による訃報が届く。遺体を引き取りにイタリアのイスキア島へ渡った彼は、父親が一緒に亡くなった愛人と毎年この島で過ごしていたことを知り、愛人の娘パメラと共に2人の遺体をこの地に納めようとする。偶然起こった殺人事件も絡み、単純なラブストーリーで終わらないところがワイルダーらしい。", "1973-05-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やるときゃやるぜ！ —COME BACK HERO—", "やるときゃやるぜ！ —COME BACK HERO—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「グッドバイ夏のうさぎ」の山名兌二の本格的デビュー作。なんでも屋の金太が、理想的な結婚を夢見るミーコに惚れてから、彼女の愛をつかむまでの５日間を軽井沢の自然をバックに描く。金太を元気づけるアイドル・グループ、セイント・フォーのラストステージも効果的に使われている。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PADRE NUESTRO", "わが父パードレ・ヌエストロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "聖地バチカンで枢機卿を務めるフェルナンドは、余命１年と知って、故郷の村に帰って来る。家族や昔の恋人と再会し、安らぎを覚えるが、一人娘は行方不明。娼婦になっていると聞いて……。かつては手のつけられぬ放蕩者だった枢機卿とその娘＝娼婦の関係を通して、聖と俗、精神と肉体、抑制と自由の本質を突いた、知的な人間ドラマ。", "1985-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLARA HAKADUSHA", "セイント・クララ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "世紀末のイスラエルを舞台にした青春ファンタジー。予知能力を持つ少女クララが転校してきたことから、学校内は大騒ぎに。クラスのリーダー格である不良少年が彼女に恋をする。しかし、それが原因となって、彼は仲間から疎外される。一方、クララが世界の終わりを予言したことから、町は大騒ぎになり……。近未来の設定に初恋のときめきが絡み合う、ポップで甘酸っぱい物語が展開。", "1996-01-01", "46", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　MEGLIO　GIOVENTU", "輝ける青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 366, "6時間06分の長さを誇る大河ドラマ。異例の尺度ながら、第56回カンヌ映画祭ある視点部門のグランプリに輝くなど、各国で絶賛された1本だ。主人公はイタリアの典型的な中流階級で育った兄弟と、彼らの家族。そんな庶民に位置する彼らの1960年代から21世紀の幕開けに至る、37年間の歩みが描かれる。そのイタリアの現代史がたどれる物語は見応え十分。主演を務める「ペッピーノの百歩」のルイジ・ロ・カーショら役者陣の力量ある演技も印象深い。仲の良い兄弟のニコラとマッテオは、とある精神病院で不当な治療を受ける少女を実家に連れ戻そうとする。だが、その試みは失敗。これを契機に二人は全く違う人生を歩み始める。", "2005-07-09", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOD’S ARMY", "ゴッド・アーミー 悪の天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "神を信じきれず、僧職を捨て、刑事になったトーマスは、ロサンゼルスで不思議な殺人事件を担当する。被害者の男の服には存在しないはずの黙示録の最終章が縫い込まれ、もっと奇妙なことに、解剖してみると彼は人間ではないというのだ。神に裏切られたのを恨み、天国の破壊と人類排除のための戦いを指揮する大天使ガブリエルが、人類抹殺の切り札となる邪悪な魂を得るために送り込んだ使者、それが男の真の姿だった。ガブリエル、彼と敵対する天使シモン、トーマスとともにガブリエルと闘うキャサリンなど、天使、悪魔、人間が、人類を滅亡させる邪悪な魂をめぐって三つ巴の戦いを展開する。日本公開版とは異なるアメリカ公開版も「プロフェシーゴッド・アーミー全米公開版」というタイトルでビデオが出ている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ライブテープ", "ライブテープ", "", "", "http://image.eiga.k-img.com/images/movie/54991/315x210.jpg?4629", 74, "「童貞。をプロデュース」「あんにょん由美香」の注目株・松江哲明がメガホンを取り、ミュージシャンの前野健太が2009年元旦に吉祥寺で敢行したゲリラライブの模様を記録した音楽ドキュメンタリー。初詣で賑わう武蔵野八幡宮で突如としてギターをかき鳴らしはじめ、歌いながら吉祥寺の町を縦断、最終目的地である井の頭公園のステージでバンドメンバーと合流して演奏するまでの74分間をワンカットで収録した異色作。", "2009-12-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Seventh Son", "セブンス・サン（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SNOW WHITE", "スノーホワイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アニメで有名な『白雪姫』は、実はディズニーが大胆に改作した万人向けの楽しいおとぎ話。現在発刊されているほとんどの絵本や童話も、これに追随するように改編されている。しかし、グリム兄弟が著したオリジナルの原作は、実はかなりグロテスクで残虐味もたっぷり。そのエッセンスを忠実に再現したのが、このダーク・ファンタジーだ。“白雪姫”のリリーには「あなたが寝てる間に…」でピーター・ギャラガーの妹役を演じたM・キーナが当たっている。1493年、ドイツの深い森の奥で、ホフマン男爵は最愛の妻を馬車の事故で失った。瀕死の妻の願いを聞いて、自ら取り上げた娘リリーの命を救うことが彼には精一杯だった。16年後、そんな過去の悲劇を引きずることなく、リリーは父親の寵愛を受けて、美しいレディに成長していた。そんな彼女に、周囲はため息の渦。後妻としてやって来たクラウディアは、夫の愛も横取りしかねない彼女がどうも面白くない。やがて子供を流産してしまうと、その嫉妬は憎しみに変わって……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い河", "黒い河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "アパートとは名ばかりの朽ち果てた長屋・月光荘。その立ち退き問題をめぐって繰り広げられる様々な人間模様を、一人の大学生の目を通して辛辣に描き出した異色の人間ドラマ。自分を犯したヤクザに惹かれる思いを断つために、殺意を抱く若い娘の心理を、コントラストの強いモノクロ画面に煮つめた演出パワーが特筆ものだ。撮影を担当したのは、小津作品でも知られる名手・厚田雄春。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ライアン・ラーキン　路上に咲いたアニメーション", "ライアン・ラーキン　路上に咲いたアニメーション", "", "", "http://image.eiga.k-img.com/images/movie/54741/315x210.jpg?3064", 44, "弱冠25歳でアカデミー賞短編アニメーション部門にノミネートされるも、その早熟な才能と創作へのプレッシャーから路上生活を選んだ天才アニメーション作家、ライアン・ラーキンにまつわる短編作品集。ライアンの作品「ウォーキング」「ストリート・ミュージック」「シランクス」「シティスケープ」「スペア・チェンジ／小銭を」のほか、ライアンへのインタビューとCGを組み合わせた意欲作で、2005年アカデミー短編アニメーション賞を受賞したクリス・ライアンによる「ライアン」などを一挙上映。", "2009-09-19", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北の零年〈ぜろねん〉", "北の零年〈ぜろねん〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 168, "明治維新後の時代、故郷を追われ、未開の地である北海道への移住を余儀なくされた武士一家の苦難の道のりを描いた歴史巨編。これまで取り上げられることのなかった北海道開拓史にスポットを当てた壮大な物語は、ドラマチックな群像劇が進展し、見応え十分。主演を務める吉永小百合、ハリウッド進出を果たした渡辺謙ら絢爛豪華なキャストの競演も見ものだ。淡路で武士として名を馳せた、稲田藩の武士たち。しかし、ほとんどの者が政府の命令で北海道移住を命じられる。未開の地に降り立った小松原英明と志乃の夫婦は、険しい開拓の道を歩む。", "2005-01-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Luzhin Defence", "愛のエチュード", "", "", "http://image.eiga.k-img.com/images/movie/1511/315x210.jpg?4627", 109, "29年、イタリアの高級リゾート地。天才チェスプレイヤー、ルージン（タトゥーロ）はナターリア（ワトソン）と出会い恋に落ちるが、彼の精神は試合の重圧に蝕まれ、結婚式当日に悲劇が訪れる。監督は「アントニア」で95年アカデミー外国語映画賞受賞、97年の「ダロウェイ夫人」で文芸作の映画化にも定評のあるゴリス。コーエン作の常連タトゥーロ、「奇跡の海」のエミリー・ワトソンはティム・ロビンス監督作「クレイドル・ウィル・ロック」に続いて2度目の共演。", "2001-09-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Quadrophenia", "さらば青春の光", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "1960年代イギリス。細身のスーツに身を包み、ベスパやランブレッタにまたがるモッズたちのグループは、革ジャンにリーゼントのロッカーズたちのグループと敵対関係にあった。広告代理店で働くジミーは、モッズのリーダー的存在であるエースに憧れ、モッズ仲間たちとドラッグやケンカに明け暮れる毎日を過ごしている。そんなある日、海辺の街ブライトンでモッズ対ロッカーズの大規模な衝突が起こり、暴動騒ぎにまで発展するが……。", "1979-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Die Toedliche Maria", "マリアの受難", "", "", "http://image.eiga.k-img.com/images/movie/33901/315x210.jpg?1263", 106, "「パフューム／ある人殺しの物語」で世界を驚愕させたドイツの鬼才トム・ティクバ監督が1993年に手掛けた幻の長編デビュー作。孤独と妄想にさいなまれる女性の狂気をシュールな映像で描き出す。夫や寝たきりの父と、狭く薄暗い穴蔵のような部屋で暮らすマリア。夫との間に愛情はなく、孤独な毎日を繰り返していた。悪夢のようなこの生活から抜け出すことだけを夢見る彼女は、向かいに住む男性と恋に落ちるが……。", "2007-03-24", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Videodrome", "ヴィデオドローム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "　暴力とポルノが売り物のケーブル・テレビ局を経営するマックスは、ある時、殺人や拷問が繰り返される禁断のビデオ“ビデオドローム”の存在を知る。やがてマックスはビデオドロームのテープを入手。それを見た彼は異様な幻覚世界に取り込まれていく……。驚愕のビデオをめぐる陰謀、そしてそれによって変貌していく男を描いたホラー。カルトムービーとして、今なお熱狂的な支持を集めている。", "1982-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十三人の刺客", "十三人の刺客", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "東映がチャンバラから、任侠ものや実録暴力団路線に移行する過程に生まれた、いわゆる“集団抗争時代劇”を代表する作品である。監督の工藤栄一はこの作品の他に「十一人の侍」や「大殺陣」などこのジャンルでの傑作を撮り、注目を浴びた。将軍の弟で明石藩主である暴君を権力の座から抹殺するべく、刺客が送られた。刺客たちは明石藩一行をある宿場に待ち伏せし、行く手をふさいで殺りくするべく計画を練る。策士、剣の達人、血気にはやる若者など13人の暗殺隊は、宿場を出口のない迷路に作りかえ、数に勝る明石藩の武士たちを迎え撃つ。やがて宿場に到着した獲物と刺客たちの壮絶な死闘が始まった……。宿場のせまい閉じた空間に繰り広げられる死闘と、あちこちに仕掛けられた様々な罠をダイナミックなカメラワークで描き出し、また個性ある刺客たちのそれぞれの戦いぶりをスピード感あふれる映像にしてみせた工藤栄一の演出は素晴らしく、ラスト10数分の大殺陣は長く語り継がれていくだろう。そしてただのチャンバラに終わらず、政治に踊らされ殺人機械と化した侍たちの悲哀をも描いた、テーマ的にも重厚な大作である。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カックン超特急", "カックン超特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "由利徹と南利明のコンビを主演に、ハチャメチャな珍道中を描くコメディ。伊東の運送会社で働く運転手・平助と助手の信吉は東京行きの特急便の常務を任され、東京へと車を走らせた。しかし、女に甘い二人は、途中でハンドバッグを崖下に落として困っている娘を助けたり、ヒッチハイクの娘を乗せてまんまと食事を食い逃げされたりと散々。大遅刻で映画の小道具を撮影所に届けるが……。DVDは「新東宝映画傑作選 BOX FUNNY&ELEGY編」に収録。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マルドゥック・スクランブル　圧縮　完全版", "マルドゥック・スクランブル　圧縮　完全版", "", "", "http://image.eiga.k-img.com/images/movie/56921/315x210.jpg?4149", 69, "第24回日本SF大賞を受賞した冲方丁の小説を3部作で映画化。2010年に劇場公開された第1部「圧縮」に、未公開シーンやさらにハードな描写を追加した「完全版」。未来都市マルドゥックシティの少女娼婦ルーン・バロットは、とばく師シェルの陰謀に巻き込まれ命を落としかける。人命保護を目的とした緊急法令「マルドゥック・スクランブル09（オー・ナイン）法」によって救われ、禁じられた科学技術を身に宿したバロットは、相棒のネズミ型万能兵器ウフコックとともに陰謀に立ち向かうなかで、自らのアイデンティティを模索していく。", "2011-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOOMED LOVE", "ドゥームド・ラブ 宿命之恋〈さだめのこい〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "文学教授のアンドレは、かつて愛した女性のことが忘れられず、思い余って自殺を図る。しかし自殺にも失敗し、精神病院に通うようになった彼は、看護婦のロイスに心惹かれるようになる。アンドレの心には昔の悲恋の思い出がよみがえる。幻想的な映像の中に、メランコリックな物語を織り込んだ個性あふれる一作。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばり チエミ いづみの　ジャンケン娘", "ひばり チエミ いづみの　ジャンケン娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "昭和の時代の代名詞的存在、歌姫・美空ひばりが主演した青春恋愛映画。初共演したチエミ、いづみの三人娘は同い年で、当時の音楽界でも大ヒットを連発していた。東京に住む二人の女子高生が、京都の祇園で舞妓と出会った。次に、その舞妓が上京して二人を訪ねた。お座敷で惚れてしまった美青年を探してほしいと言う。ようやく見つけた彼は実は……。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌５", "釣りバカ日誌５", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "前作で誕生したハマちゃん２世、鯉太郎が新たに加わったシリーズ第５作。待望のベビー鯉太郎もすくすく育ち、そろそろ満１才。みち子さんは目を離すヒマもなく子育てに追われていた。そんな時、ハマちゃんの母・たきが突然上京して来た。おむつをめぐってスーさんと大ゲンカするなど、ハマちゃんの家で元気いっぱいの日々をたきは過ごしていた。ある日、みち子さんは同窓会に出席するために鯉太郎をたきに預けるが……。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コレヒドール総攻撃", "コレヒドール総攻撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 34, "アメリカ軍が、鉄の要塞と豪語するコレヒドール島の攻略戦。昭和17年５月３日から７日にかけての日本軍の総攻撃に焦点をあわせた、激戦が偲ばれる戦記映画。コレヒドール要塞のシンボルともいうべきマリンタトンネル内の記録映像も映し出されている。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STITCHES", "新ポーキーズ お医者さんゴッコ学園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "超一流医科大学に学ぶプッツン３人組。エッチにかけては天才的な彼らだが、悪行三昧がたたって退学の危機にさらされて……。悪ノリギャグ満載の青春コメディ・シリーズの医科大バージョン。「ローマの休日」のカメラマン役が懐かしいE・アルバートが、迫力のワル学長に扮している。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("V for Vendetta", "Vフォー・ヴェンデッタ", "", "", "http://image.eiga.k-img.com/images/movie/1211/315x210.jpg?3164", 132, "「マトリックス」のウォシャウスキー兄弟が大ファンである「フロム・ヘル」「ウォッチメン」の人気コミック作家、アラン・ムーアの同名コミックを映画化。17世紀の英国で、国会議事堂を爆破しようとした伝説の革命家ガイ・フォークス。彼の仮面を付けた謎の人物Vが、独裁主義国家となった近未来の英国政府の転覆を謀る。ナタリー・ポートマンが、Vに出会って真実の自分に気づくヒロイン役をスキンヘッドになって熱演。", "2007-04-22", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LOVER", "妻の愛人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "肉体的な結びつきもなく冷えきった仲のアシアとアダムの夫婦。そんな家庭にある日パリから来たやさ男ガブリエルが住みつくようになる。フランス語をアシアに教えるガブリエル。二人はいつしか愛し合うようになるが、アダムは妻の生き生きした姿に久しぶりに接し、彼らの仲を見て見ぬふりをするのだが……。", "1986-01-01", "46", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("右曲がりのダンディー", "右曲がりのダンディー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "『コミック・モーニング』誌に連載された末松正博の同名人気マンガを「ビー・バップ・ハイスクール」の那須真知子が脚色、夫の那須博之が映像化したコメディ。右曲がりのダンディーこと、主人公・一条まさとが、一度は敗れた取引先の社長、岡部に再び戦いを挑み“一発逆転”を狙う姿が描かれている。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOO FAST，TOO YOUNG", "灼熱の甘い夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ロサンゼルスで奔放な生活を送る若者ダルトンの前に、服役中のいとこのチェイスが脱獄して現れ、ダルトンは現金輸送車の襲撃計画に巻き込まれる。唯一の家族であるチェイスを信じ計画をやりとげたダルトンだが、逃げ込んだ家の周りをロス市警の狙撃隊に取り囲まれてしまう。そのころ、ダルトンを愛するカディの身にもチェイスの魔の手が近づいていた……。「スキャナーズ」「トータル・リコール」のM・アイアンサイド主演によるアクション。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戯夢人生", "戯夢人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "「恋恋風塵」、「悲情城市」で飄々とした名演を見せたリー・ティエンルーは、実は台湾の人間国宝的人形使いである。彼の波乱万丈の人生を、旧日本帝国による植民地時代から日本の敗戦による解放までの50年と重ね合わせ、リー自身の語りとドラマ部分によって描いていく。前作「悲情城市」を第二部とする、ホウ・シャオシェン監督の「台湾現代史三部作」の第一部に当たる作品。", "1993-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AN AMERICAN WEREWOLF IN PARIS", "ファングルフ　 月と心臓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "世界的評価を受けたホラー「ミュート・ウィットネス」のA・ウォラーが監督した異色ホラー。原題から明らかなように、ジョン・ランディス監督作品「狼男アメリカン」の姉妹編ともいうべき内容で、狼人間になってしまった人々の悲劇と騒動を、ブラック・ユーモアたっぷりに描いている。ラストは二つ用意されたが、日本ではハッピーエンドとなる明るいバージョンが公開された。パリの狼女にJ・デルピー、彼女に夢中になって騒動に巻き込まれるアメリカ人青年に「すべてをあなたに」のT・E・スコットが扮している。大学の卒業旅行に仲間とともにパリへとやって来たアンディは、エッフェル搭で投身自殺を図ろうとした美少女セラフィーヌに一目惚れ。彼女の自宅を突き止め、アタックを開始するが、全く相手にされない。その時近づいてきた不気味なスキンヘッドの男に真夜中のパーティーに招待されたアンディだが……。ビデオ、DVDタイトルは「ファングルフ　狼男アメリカン in パリ」。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道ペテン師", "極道ペテン師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "野坂昭如の『ゲリラの群れ』を原作とした関西ノリの社会喜劇。大阪・釜ヶ崎を根城とするサギ師たちが、次から次へとカモをだまして大金をせしめていく。その手口は馬鹿馬鹿しくも豪快。リーダー役のフランキー堺をはじめ、出演するコメディアンたちがそれぞれに適役であり、いきいきと動いている。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BANDE DES QUATRE", "彼女たちの舞台", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 160, "パリ郊外の古屋敷で共同生活をしている4人は演劇学校の女生徒。以前屋敷に住んでいた別の女生徒がやっかいごとに巻き込まれているらしく、稽古場にも屋敷にも何やら不穏な空気が渦巻き始めた。4人をつけまわす男、互いにつのる疑念、そして事件。それでも芝居の稽古は続く……。ヌーベル・バーグの先駆者的存在であったリベット監督の記念すべき日本初公開作。", "1991-02-28", "2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("王立宇宙軍 オネアミスの翼", "王立宇宙軍 オネアミスの翼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "これが初めての監督作品であると同時に、商業作品ともなった山賀博之をはじめとして、アマチュア・グループから大胆にスタッフを起用して作られた、バンダイ製作の劇場用アニメ第１作。どこの国とも違う異世界を、その細部に至るまでリアルに描きだした、野心作となっている。見方によっては人類の近代史の精密なパロディーともとれるが、その壮大な設定の中で、主人公の生き様を追う視線は真摯であり、そこから単なるテクニックだけの作品でないことが感じられる。地球と同じ自然環境を持つ惑星にある王国・オネアミス。そこで軍の落ちこぼれが集まる王立宇宙軍に入隊したシロツグ・ラーダットは、街で神の教えを説く少女、リイクニ・ノンデライコと出会う。やがて初の有人衛星の打ち上げ計画が決定され、宇宙飛行士に志願したシロツグは、訓練にあけくれることになった。リイクニと心を通わすこともできないまま、彼は様々な難問を切り抜けてロケットに乗り込むが、その時国境を越え、隣国の共和国軍が侵攻してきた……。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES VISITEURS DU SOIR", "悪魔が夜来る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "舞台は15世紀のフランス。悪魔によって石に変えられた恋人たち。だが、寄りそう二人の心臓の鼓動は、不思議なことにいつまでも鳴りやまない……。ドイツ占領下にあった1942年当時のフランスの自由を願う民衆の心情を、M・カルネが描いた秀作。", "1942-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嗚呼!!花の応援団 男涙の親衛隊", "嗚呼!!花の応援団 男涙の親衛隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "お花見の席取りで南河内大学と浪華大学の応援団がハチ合わせ。その南河内大の隊長・青田にミス日本との見合い話がもちあがり、青田は急に品行方正になる。試験のシーズンが到来。科目ごとに代役を立てた青田の策略は、もちろん失敗する。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最後の審判", "最後の審判", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "玉つき屋のマスター・次郎は、海外に単身赴任中のいとこ、利一郎の妻・正子と関係を結ぶ。正子に多額の貯金があることに目をつけた次郎は、恐るべき完全犯罪をたくらむのだが……。W・P・マッギバーンの同名原作を堀川弘通が映画化したサスペンス。仲代達矢がハングリーな若者像を好演している。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガス人間第１号", "ガス人間第１号", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "美しい踊りの家元・藤千代に惚れ、彼女のために悪事を働くガス人間。この狂行をやめさせようと岡本警部補(三橋)は、藤千代の手を借りようとするが……。1954年の「透明人間」に始まり「美女と液体人間」(1958)、「電送人間」(1960)、「マタンゴ」(1963)と続く東宝SF路線・変形人間ものの一編。円谷英二が特殊技術を担当し、人間がガスに変貌する過程をリアルに表現。ライターでガス人間が爆発するラストも面白い。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("漂流教室", "漂流教室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "小学校が校舎ごと異次元にタイム・スリップしてしまった。そこで起る奇怪な出来事とパニックに、子供たちは独力で立ち向かっていく。楳図かずおのスケール大きいコミックが原作ゆえ、映画化が可能か興味が持たれたが、大林宣彦色が大きく出た作品になった。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("剣侠江戸紫", "剣侠江戸紫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "旗本の水野十郎左衛門率いる無頼の集団・白柄組と、町奴を束ねる播随院長兵衛の対立が喧伝される江戸。故郷を出奔した浪人・白井権八は播随院の客分となり、花魁芸者の小紫と慕い寄る仲。ところが小紫は、彼を仇と狙う本所助七を斬り、何食わぬ顔で財布を奪った権八を見て、愕然とする。彼の更生のために駆け落ち話を切り出すが……。おなじみの物語を大河内の播随院、嵐寛の水野、大谷の白井というキャストで映画化。新東宝創立７周年を記念するオールスター共演作で、播随院と水野ともに悪人として描かれていない点が面白い。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paris", "PARIS パリ", "", "", "http://image.eiga.k-img.com/images/movie/53861/315x210.jpg?2124", 130, "「猫が行方不明」「スパニッシュ・アパートメント」のセドリック・クラピッシュ監督が故郷パリを舞台に描く群像劇。ジュリエット・ビノシュ、ロマン・デュリスら豪華俳優が共演。心臓病を患うダンサーのピエールは、アパートのベランダからパリの街を行き交う人々を眺めながら臓器提供者を待つ日々を過ごし、ピエールを案じて同居を始める姉のエリーズは、シングルマザーで仕事や育児に追われながらも自らの幸せを模索している。そこに様々なパリの人々の日常が交差する。", "2008-12-20", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いいかげん馬鹿", "いいかげん馬鹿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "山田洋次監督が喜劇における才能を世に示した「馬鹿まるだし」(1964)に続く一編。乱暴者でお人好しの風来坊・安吉が故郷の小島を文化的に向上させようと奮闘するが、彼が連れてくるのはニセ楽団やニセ小説家ばかり。そんなこととは気づかない彼の善意はいつも裏目に出てしまい……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フランドン農学校の尾崎さん", "フランドン農学校の尾崎さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "大阪府豊能郡能勢町で30年以上にわたって有機農業を実践してきた尾崎零さんの1年間を取材したドキュメンタリー。1978年にサラリーマンを辞めて能勢町に移住し野菜づくりを始めた尾崎さんは、生命の基本に立ちかえった自給運動として、産消循環自給農場「べじたぶる・はーつ」を発足。「命・循環・調和とバランス」をモットーに、いのちを大切にする社会づくりを目指して様々な社会活動に取り組む尾崎さんの姿を追う。", "2007-03-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VELVET GOLDMINE", "ベルベット・ゴールドマイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "1970年代前半にロンドンで生まれた“グラム”ブームを背景にした青春ドラマ。1984年、雑誌記者のアーサーは、1970年代に一世を風靡したロッカー、スレイドの消息を探ることに。バイセクシャルを公言し、グラマラスな衣裳に身を包んで新時代のスターとなったスレイドは、ステージ上で暗殺を偽装し、そのまま姿を消した。アーサーはグラムに夢中だった青春期を回想しつつ、スレイドゆかりの人を訪ね歩き、意外な事実にたどり着く……。デヴィッド・ボウイをモデルにした主人公やイギー・ポップを思わせるその友人のキャラクター、全編に散りばめられた新旧のナンバーなど、ロックファンをニヤリとさせる要素を盛り込み、時代を築いたロッカーのカリスマ性を描写。冒頭に異端の作家オスカー・ワイルドを登場させ、いつの時代にも出現するアウトサイダーたちの強烈な個性を浮かび上がらせる。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本女侠伝・鉄火芸者", "日本女侠伝・鉄火芸者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "子供の頃、見ず知らずの男から受けた恩が忘れられず、10年たった今でもその男のために操を守り通していた辰巳芸者の小しず。偶然にも彼女はその男・小林勇吉とめぐり合うが、彼はヤクザ稼業に身を染めていた。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOOP　DREAMS", "フープ・ドリームス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 169, "サンダンス映画祭観客賞をはじめ、数々の映画賞を受賞し好評を博した秀作ドキュメンタリー。NBAを夢見る二人の黒人少年を、14歳の頃から４年間に渡って追い続ける。名門高校に奨学生として入学したアーサーとウィリアム。ともに才能のある二人だったが、学業不振のアーサーは奨学金を停止され他校に転入、成績優秀のウィリアムは選手としても注目されるようになる。ところが、３年になったウィリアムは膝の故障を機に調子を狂わせ、アーサーは才能を開化させていく。NBA予備軍たちの熾烈な競争やそこに絡む金銭問題、さらに家族の期待によるプレッシャーなどが重くのしかかる少年たちの姿、素直な感情を、インタビューを交えて映し出す。同じスタート位置に立ちながら、明暗を分けていく彼らの姿は、作られた物語以上にドラマティックだ。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　STRANGER　WORE　A　GUN", "叛逆の用心棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "往年の名優たちが共演した快作ウエスタン。一匹狼のアウトローの姿を鮮烈な銃撃戦、集団戦を絡めて活写する。略奪の限りを尽くす一味に嫌気が差し、彼らの元を去った主人公。彼は正義感ある行為に惹かれ以前の仲間を頼ったが、彼らの仕事も強盗だった。そこで、男は悪を一掃する計画を実行する。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JACKNIFE", "ジャックナイフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "R・デ・ニーロがベトナム帰還兵に扮して、かつての戦友の家を訪ね、彼の心の傷を癒そうとする感動の小品。主人公のあだ名はジャックナイフ（急停車)。一見破滅的態度をとるこの男を戦友は憎悪するが……。ベトナム戦争の傷の深さを、肩肘張ることなく切々と訴えることに成功している。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHEN THE BOUGH BREAKSII", "犯罪心理捜査官2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "女性プロファイラーの活躍を描くシリーズ第２作。テキサスで女性を狙った殺人事件が続発。死体に花を持たせ、装飾品で飾るという猟奇殺人の捜査に、スワード警部は女性プロファイラー、マクレアーの協力を求める。しかし、学生時代に何者かにら致監禁されたことのある彼女は、捜査を進めるうち、心の奥深くに封印したはずの忌まわしい過去に苦しめられていく。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Brokeback Mountain", "ブロークバック・マウンテン", "", "", "http://image.eiga.k-img.com/images/movie/1311/315x210.jpg?2023", 134, "「楽園をください」「グリーン・デスティニー」のアン・リー監督が描く愛の物語。1960年代初頭のアメリカ・ワイオミング。カウボーイのイニスとジャックは2人だけの厳しいキャンプ生活の中で愛し合うようになり、結婚後も密かに愛を貫いていく。「ブラザーズ・グリム」のヒース・レジャー、「デイ・アフター・トゥモロー」のジェイク・ギレンホールが共演。第78回アカデミー賞の監督賞、脚色賞、音楽賞の3部門を受賞。", "2006-03-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILD STYLE", "ワイルド・スタイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ニューヨークの下町に生まれたヒップ・ホップ（＝街頭芸術）を紹介した一編。地下鉄の車輌をスプレー・インクで飾った伝説的なグラフィック・アーティストを中心に、ブレイク・ダンス、グラフィティ・スクラッチ、ラップといった1980年代の若者文化を描いた“風俗ドキュメント”として一見の価値あり。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空の下の遠い夢", "空の下の遠い夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "東南アジアをめぐる豪華客船の乗客たちを主人公に、様々な人間模様を描いた青春映画。海外へ飛び出すことを夢見る17歳の青年、石川は、ジャズ喫茶の仕事で旅費を手にする。彼が乗った客船チトラル号には、不幸な生い立ちを持つ少女、真理や、シンガポールでひと旗上げようとする少年、山村が乗り込んでいて……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブルークリスマス", "ブルークリスマス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "世界の各地でUFO目撃者が続出、目撃者の血は青色に変わるという噂が飛びはじめる。特殊部隊勤務を任命された国防長参謀本部の沖は、自分の恋人からUFOを見たことを告白され、そして恐るべき展開へ……。倉本聰脚本による日本では珍しいSFロマン。岡本喜八監督のユニークな視点が楽しめる快作にして怪作。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINIAN'S RAINBOW", "フィニアンの虹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "ハリウッドを代表するミュージカル・スター、F・アステアと、F・F・コッポラ監督の顔合わせによるミュージカル・ファンタジー。３つの願いが叶う魔法の壷を手に入れた、レインボウ・バレーに暮らす陽気なフィニアンが巻き起こす騒動を、楽しいミュージカル・シーンもたっぷりに描いている。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RED MONKS", "悪魔の教団 レッド・モンクス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "広大な森に囲まれた古城へ嫁いで来た女流画家ラモーナ。幸せなはずが、夫は彼女を抱こうともせず、身の周りには奇妙な出来事が起こり始める。夫への不信を抱くラモーナを待っていたのは、結婚の裏に隠された恐るべき陰謀だった。鬼才フルチのカラーが強く現れたオカルト・タッチのミステリー。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILLERS キラーズ", "KILLERS キラーズ", "", "", "http://image.eiga.k-img.com/images/movie/1101/315x210.jpg?4698", 115, "押井守「.50 WOMAN」（写真）、きうちかずひろ「Pay Off」、大川俊道「CANDY」、辻本貴則「PERFECT PARTNER」、河田秀二「KILLER IDOL」の5話で構成されるオムニバス。 元々の企画は、雑誌「月刊Gun」で開催された自主映画コンテスト“GUN ＆ ACTION VIDEO CONTEST＝通称・ガンコン”で認められた河田秀二と辻本貴則を映画監督としてデビューさせるというもの。押井らは、このコンテストで審査員を務めていたことから企画に参加。", "2003-06-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若桜千両槍", "若桜千両槍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "槍の権三と呼ばれる槍の名手、笹野権三郎の活躍を描いた時代劇。宝蔵院流をはじめ武芸百般を究めた権三郎は、紀州藩の槍術指南役となるが、御前試合で敗れた羽織組と彼を慕う叔父の娘・雪乃に横恋慕する彦太郎の恨みを買い、江戸へ逃れる。だがその間に叔父が羽織組らの手で殺されてしまう。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("武士の一分（いちぶん）", "武士の一分（いちぶん）", "", "", "http://image.eiga.k-img.com/images/movie/1441/315x210.jpg?3896", 121, "「たそがれ清兵衛」「隠し剣　鬼の爪」に続く、原作・藤沢周平×山田洋次監督による時代劇3部作の最終章。東北・海坂藩の下級武士、三村新之丞は剣術の腕を認められながらも藩主の毒味役に甘んじていた。そんなある日、新之丞はいつも通りに昼食の毒味をしたところ、赤貝の毒にあたり、失明してしまう……。原作は「隠し剣秋風抄」所収の「盲目剣谺（こだま）返し」。", "2006-12-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神様の愛い奴〈決定版〉", "神様の愛い奴〈決定版〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "国家をはじめとする権力に対して抗議し続け、「ゆきゆきて、神軍」も話題になった奥崎謙三の姿を追うドキュメンタリー。13年８ヵ月の獄中生活を終えた奥崎が、理想郷“ゴッド・ワールド”を創造すべく奔走する姿をエネルギッシュに記録した異色作だ。当時77歳の奥崎が、年齢を感じさせない圧倒的カリスマ性を見せつける。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HANNAH AND HER SISTERS", "ハンナとその姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "マンハッタンで暮らす三姉妹と彼女たちを取り巻く人々を通して、誤解や失敗を繰り返しながらも生きていく“人生のほろ苦さ”を、時にシニカルに、時にコミカルに、温かく見守るように描く。長女ハンナの夫エリオットは妻の家庭的な雰囲気に満足しているものの、三女のリーに夢中だ。リーは年の離れた画商とソーホーのロフトで同棲中、だがエリオットの強引なアタックに心は揺れ動く。一方、次女のホリーは熱しやすく冷めやすい性格が災いして恋愛も仕事もおぼつかないが、いつも陽気。これにハンナの前夫ミッキー、彼女たちの両親が加わって、独特の“ウディ・ドラマ”が展開されていく。すべてが一級品の味わいがあるアレンの傑作。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロック　わんこの島", "ロック　わんこの島", "", "", "http://image.eiga.k-img.com/images/movie/55851/315x210.jpg?3245", 123, "伊豆諸島・三宅島で民宿を営む野山家は父・松男、母・貴子、祖母・房子、そして息子の芯の4人で幸せに暮らしていた。しかし2000年8月、三宅島の噴火により全島民が避難することとなり、芯は生まれた時から育てていた愛犬ロックとの別れを余儀なくされる。実話をもとに、1人の少年と1匹のイヌの目を通して、家族のあり方やきずな、生きる力の強さを描く。", "2011-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・一発勝負", "喜劇・一発勝負", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "山田洋次＝ハナ肇コンビの“一発”ものの一編。地方の小都市の旅館の娘は評判のしっかりもの。そこへ10年前に家を出た兄が戻って来るが、これがどうしようもない親不孝者で……。後年の“寅さん＝さくら”兄妹の原形がみられる良質のコメディ。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rango", "ランゴ", "", "", "http://image.eiga.k-img.com/images/movie/55151/315x210.jpg?2439", 107, "人間のペットとして飼われていたお調子者のカメレオン、ランゴは、飼い主と離ればなれになってしまう。西部の荒野にある町ダートにたどりついたランゴは、町の保安官として生きることになるのだが……。「パイレーツ・オブ・カリビアン」のゴア・バービンスキー監督が手がけたフルCGアニメーション。ランゴの動きは、ジョニー・デップの演技をモーションキャプチャーでとらえて再現。声優もデップが務める。第84回アカデミー賞で長編アニメーション賞を受賞。", "2011-10-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("街の灯", "街の灯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ポン引き稼業のちょろ松が、アイドルに瓜二つの少女と、ブラジル帰りの奇妙な老人の二人と知り合い、３人は東京から九州まで旅をすることになる……。世の中の不条理を作者が声高に訴えるのではなく、心優しい笑いでそっと語りかける作品に仕上がった。堺のつぶやく“何か変だぞ……”の台詞が印象深い。ほのぼのとしたロード・ムービー。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IMAGININNG　ARGENTINA", "ジャスティス／闇の迷宮", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "事政権成立に揺れる1970年代のアルゼンチンを舞台にした、実話に基づくサスペンス。軍部組織に妻を誘拐されたカルロスは妻を捜し続けるうち、自分に失踪者たちの姿が“見える”能力があることに気付く。しかし、何故か妻の姿だけは見えないカルロスは、失踪者の家族のためにその力を役立てるが、そんな彼を新たな悲劇が襲う……。1976年の３万人以上にも及ぶ、市民の失踪事件（実際には政権による誘拐）という悲惨な史実を土台にしながらも、単なる政治ドラマに終わらせず、一級のエンターテインメントに仕上げた監督の手腕は見事。A・バンデラスら実力派たちが、作品にさらなる説得力を与えている。児童劇団を主宰するカルロスは、ジャーナリストの妻セシリアと一人娘のテレサの３人で穏やかに暮らしていた。だが、正義感の強いセシリアは、相次ぐ市民の失踪が政府の陰謀であることを告発するが、その直後に誘拐されてしまう。", "2003-01-01", "1,22,9,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tekken", "TEKKEN 鉄拳", "", "", "http://image.eiga.k-img.com/images/movie/54871/315x210.jpg?3681", 93, "世界中で人気の日本製アクション格闘ゲームの実写映画化。監督はTVシリーズ「24」「プリズン・ブレイク」のドワイト・H・リトル。文明が崩壊し、巨大権力を持つ悪徳企業によって支配される近未来の世界。裏社会のトップに君臨する三島平八率いる三島財閥によって母親を殺された風間仁は、三島の開催する格闘技大会に出場し、復讐のチャンスを狙う。しかし、そのトーナメントには恐るべき陰謀が隠されていた。", "2010-03-20", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VENUS PETER", "ビーナス・ピーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "イギリスで脚本家として活躍するI・セラーの初監督作品。スコットランドの最北端の島々の中でも幻想美を誇るオークニー島の小さな漁村を舞台に、一人の少年と彼をとり巻く大人たちの姿を詩的な感性で捉えていく。少年の名はピーター、そしてビーナスとは祖父の漁船ビーナス号のことである。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREAKOUT フリークアウト", "FREAKOUT フリークアウト", "", "", "http://image.eiga.k-img.com/images/movie/56011/315x210.jpg?3192", 145, "静岡県富士宮市にある弘願寺の2代目住職で元ロッカーの角田大龍は1995年、寺で修行をともにする知的障害を抱える僧侶らとバンド「ギャーティーズ」を結成。その前衛的なスタイルが話題となる。心臓を患った大龍の入院中、バンド仲間でもある僧侶たちがいさかいを起しながらも寺での日々を送る姿や、「ギャーテーズ」復活ライブ当日までの様子を克明に刻んだドキュメンタリー。", "2011-03-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Skyfall", "007 スカイフォール", "", "", "http://image.eiga.k-img.com/images/movie/56381/315x210.jpg?2556", 0, "「007」シリーズの第23作で、「カジノ・ロワイヤル」「慰めの報酬」に続きダニエル・クレイグがジェームズ・ボンドを演じる。ボンドの上司Mを中心にストーリーが展開され、ボンドはMにつきまとう過去の亡霊とMI6を標的とした脅威に立ち向かう。「アメリカン・ビューティー」のサム・メンデスが手がけ、シリーズ初のオスカー監督がメガホンをとる。ボンドガールは仏女優ベレニス・マーロウと英女優ナオミ・ハリス。悪役に「ノーカントリー」のハビエル・バルデム。M役はシリーズおなじみのジュディ・デンチ。", "2012-12-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("孤独な惑星", "孤独な惑星", "", "", "http://image.eiga.k-img.com/images/movie/57521/315x210.jpg?3855", 94, "刻々と変化していく男女の三角関係の模様をコミカルに描いたラブストーリー。東京近郊のマンションで一人暮らしをしている真理は、隣室から時折聞こえてくるカップルの痴話ゲンカを耳にしていた。ある日、その隣室の青年・哲男がケンカの末に真理の部屋に逃げ込み、真理は一晩だけのつもりで哲男を部屋に泊めたことから、奇妙な三角関係が始まる。雑誌「ミセス」や「ELLE JAPON」でモデルを務める竹厚綾が映画初主演。監督は「ゆめこの大冒険」「オーバードライヴ」の筒井武文。", "2011-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長血笑記 殴り込み荒神山", "次郎長血笑記 殴り込み荒神山", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "代参の途中殺された石松の仇を討った次郎長は、ほとぼりを冷ますため子分を吉良の仁吉に預けた。その仁吉に兄弟分、長吉が乗っ取られた縄張りを取り返すため助力を請う。相手は仁吉の女房、お菊の兄、安濃吉だ。仁吉の男を立てるためお菊は自害。仁吉は次郎長一家ともども対決の場、荒神山に乗り込む。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD CONNECTION", "デッド・コネクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "アメリカ全土を恐怖に陥れた連続レイプ殺人犯の犯行と思われる女子高生殴殺レイプ事件がロサンゼルスで発生した。執拗に容疑者を追う担当刑事のマットは、独自で犯人を追う美人ジャーナリストのキャサリンと出会い、親密な関係になる。ある日マットは犯人を追い詰めながらも取り逃してしまい、身分証を奪った犯人は刑事を偽り残虐な犯行を重ね、キャサリンに迫っていくのだった……。「レザボア・ドッグス」の冷酷な演技で注目を集めたM・マドセン主演のバイオレンス・アクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("季節、めぐり　それぞれの居場所", "季節、めぐり　それぞれの居場所", "", "", "http://image.eiga.k-img.com/images/movie/57711/315x210.jpg?1791", 82, "「ただいま　それぞれの居場所」（2010）で、理想の介護を実現するために施設を立ち上げる若者たちの姿を追った大宮浩一監督が、再び介護現場で生きる人々にカメラを向けたドキュメンタリー。埼玉県坂戸市にある老舗福祉施設や、千葉県木更津市で若者が立ち上げた宅老所など各地の介護施設のほか、岩手県宮古市や宮城県石巻市の老人ホームなど東日本大震災後の被災地の介護現場も映し出し、利用者とスタッフ双方が老いや死と向き合い、それぞれの居場所を見つめていく姿を描き出していく。", "2012-04-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("M", "M", "", "", "http://image.eiga.k-img.com/images/movie/34191/315x210.jpg?1547", 110, "「ヴァイブレータ」「やわらかい生活」の廣木隆一監督が、馳星周による中篇小説集を基に、人間の“欲望”を描き出した衝撃ドラマ。夫や子供と幸せな日々を送っていた聡子は、携帯サイトを通して出会ったヤクザに騙され、売春を強要されてしまう。夫の秀之は彼女の変化に気づくが、何もできず妄想を膨らませるばかり。一方、母を助けるために父を殺した過去を持つ新聞配達員の青年・稔は、聡子を母の姿に重ね、彼女を救おうとするが……。", "2007-09-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博奕打ち・総長賭博", "博奕打ち・総長賭博", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "昭和９年の東京・江東地区。博徒・天龍一家は組長が病に倒れ、跡目相続を決めなければならなくなる。大幹部会は中井組組長・中井信次郎を推すが中井は辞退し、服役中の兄弟分・松田を推す。しかし大幹部・仙波は石戸を強引に二代目に据え、あくまでも反対する松田を破門にし、中井に殺させる……。壮麗なまでに様式化された画面の中に、悲劇的な男女の相剋が、一分の隙もなく刻み込まれた東映任侠映画の最高傑作である。ラスト、中井は自分の叔父貴で、すべての元兇である仙波に刃を向け“俺はただのケチな人殺しなんだ”と刺し殺す。鶴田浩二、一世一代の名演技であった。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHATEVER HAPPENED TO AUNT ALICE？", "何がアリスに起ったか？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "人里離れた広大な屋敷で、家政婦たちが次々と消えていく。屋敷には美しい未亡人が暮らしていたが、その謎を追う素人探偵の身にも危険が迫る。予期せぬストーリーと緊張感あふれる演出。それをG・ペイジとR・ゴードンのアカデミー女優が、競い演じるサスペンスの佳作。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MA NUIT CHEZ MAUD", "モード家の一夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ヌーヴェル・ヴァーグの先駆者E・ロメールが、1960～70年代にかけて連作した“６つの教訓話”シリーズ第４話。フランスの地方都市を舞台に、日常の中の男女関係の微妙なニュアンスを、ウィットの効いた会話と洗練された映像であざやかに浮き上がらせた、ロメール映画らしい１本である。クリスマスも間近なある日曜日。カトリックで技師の主人公の男“私”は14年ぶりに再会した旧友に誘われるままに、モード家を訪れる。モードは、離婚経験のある美しく知的な女医。話題も豊富で会話も弾む。深夜、雪が降り出すころ、旧友は“私”を残して帰っていく。モードと二人きりになった“私”は……。意中の娘がいながら、モードの魅力に惑わされて、ソワソワする“私”＝男の本性を“同類相憐む”的自嘲を込めて、皮肉たっぷりに描写。雪に煙った街並みを映す、森閑としたモノクローム映像が絶品。DVDは「エリック・ロメール コレクション(2)」に収録。", "1968-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("烈火戦車／FULL THROTTLE", "フル・スロットル　烈火戦車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「夢翔る・色情男女」のトンシン監督がA・ラウを主演に据えて撮り上げた、エネルギッシュな青春映画。バイク事故を乗り越えてレーサーを目指す青年の恋と友情、成長と挫折を、スピード感あふれるチェイス・シーンを交えながら描き出す。精悍な魅力を発揮しながらも、人間的な弱さをも表現したラウの好演が光る。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一代宗師", "グランドマスター（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/57641/315x210.jpg?2344", 0, "「ブエノスアイレス」「花様年華」のウォン・カーウァイ監督が初めてカンフーを題材に取り上げ、ブルース・リーの師匠としても知られる伝説の武術家イップ・マンの物語を描く。", "2013-06-30", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sideways", "サイドウェイ", "", "", "http://image.eiga.k-img.com/images/movie/1171/315x210.jpg?1414", 130, "本年度アカデミー賞の作品、監督、助演男優、助演女優、脚色の5賞ノミネート、脚色賞を受賞。「アバウト・シュミット」のアレクサンダー・ペイン監督が「アメリカン・スプレンダー」のポール・ジアマッティを主演に、中年ダメ男の人生の寄り道を描く。ワインオタクで小説家志望の国語教師マイルスは、結婚を1週間後に控えた学生時代からの友人で売れない俳優のジャックと共にカリフォルニアのワイナリーを廻る旅に出て、自分自身を見つめ直していく。", "2005-03-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Escape Artist", "マジック・ボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "天才マジシャンとうたわれた父を持つ少年ダニーは、まだ16歳。だが、マジックにかけては父親ゆずりで、なかなかの腕前だ。市長のドラ息子をブタ箱から出すマジックを使ってみせたはよかったが、その市長の巨大な陰謀を知ってしまう……。コッポラの製作総指揮によるマジカルな青春ドラマ。主演のG・オニールはライアン・オニールの息子で、テータム・オニールの弟。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIGOT", "ジゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ろうあでお人好しの大男、ジゴはある日雨の中にうずくまっているコレットという女性とその娘・ニコルを自分の部屋に連れ帰り世話をした。貧しいジゴを嫌うコレットはジゴが娘を誘拐したと思い込み、町の人たちと家捜しをはじめるが……。G・ケリーが監督した心温まる一編。原作は主演のJ・グリースン。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("石井輝男映画魂", "石井輝男映画魂", "", "", "http://image.eiga.k-img.com/images/movie/55661/315x210.jpg?2415", 80, "「網走番外地」シリーズなどの大ヒット作や「江戸川乱歩全集　恐怖奇形人間」といったカルト作品まで数々の名作を遺し、2005年に81歳でこの世を去った鬼才・石井輝男監督を題材にしたドキュメンタリー。1995年に手がけた「無頼平野」の撮影現場の模様をはじめ、関係者へのインタビューや膨大なスナップなどを織り交ぜながら、石井監督の創作の裏側に迫る。メガホンを取るのは、石井監督と深い親交のあったダーティ工藤。", "2010-08-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜叉ヶ池", "夜叉ヶ池", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "歌舞伎界の女形の大スター・坂東玉三郎を起用して、鏡花の幻想的世界を映像化した篠田監督の意欲作。越前・三国嶽の山中にある夜叉ヶ池。そこにある鐘楼の鐘を鳴らさないと竜神の化身が現れ、大洪水を起こすという伝説にまつわる一組の男女とその友人の物語。シュールな感覚の映像に彩られて、玉三郎が世にも美しい化身を演ずる。池に棲む魚、カニなどに扮した役者陣の演技も印象深い。イグアスの滝にロケを敢行したラストシーンも圧巻。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LATCHO　DROM", "ラッチョ・ドローム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "自らのルーツでもあるロマ民族の魂を描き続けるT・ガトリフ監督が、世界的にその名を知らしめた音楽映画。約1000年前にインド北西部を出発し、ヨーロッパ各地に散らばっていったロマの人々の歴史を再現するかのような壮大な構成になっており、ルーマニアの人気バンド、タラフ・ドゥ・ハイドゥークスなど８ヵ国のミュージシャンたちが踊りや演奏を披露。ドキュメンタリーともフィクションとも分類しがたい、ユニークかつ感動的な映像叙事詩である。ちなみに“ラッチョ・ドローム”とはロマの言葉で“よい旅を”という意味。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忘れられた子供たち　スカベンジャー", "忘れられた子供たち　スカベンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "東洋最大のスラムと称されていたフィリピン・マニラ市のスモーキーマウンテンの記録。巨大なゴミ捨て場の中に家を建て、再生可能なゴミの転売を仕事とする“スカベンジャー”を中心とした２万1000人以上もの人々が暮らしている。四ノ宮監督はこのスラムに６年以上も住み、スモーキーマウンテンの住民をフィルムに収めた。17歳と16歳の幼い夫婦とその子供、一家の大黒柱としてスカベンジャーからの脱出を目指して仕事を転々とする13歳の少年。あまりに厳しい現実にあっても、子供たちの笑顔は透き通るように美しい。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SALO｀ O LE CENTIVENTI GIORNATE DI SODOMA", "ソドムの市", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "監督のパゾリーニは、この作品の撮影直後、1975年11月２日に、ゲイ関係のもつれから、出演者だった少年に殴り殺されるというスキャンダラスな最期を遂げた。だが、作品はそれ以上にスキャンダラス。サドの原作を、第二次大戦下の敗色の濃いイタリアに置き換えている。４人の権力者たちは、かり集めた美少年美少女たちと、レイプ、スカトロ、ゲイ、ソドミー、オナニー、そして殺人と、ありとあらゆる性的な狂宴の変態パノラマ・パフォーマンスを繰り広げる。そこにあるのは、常に宗教を意識したパゾリーニの強烈な反体制思想だ。DVDタイトルは「パゾリーニ・コレクション　ソドムの市【オリジナル全長版】」。", "1975-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EUREKA", "錆びた黄金", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "1945年のカリブ。かつてカナダで金鉱を見つけ、百万長者となり、今は豪邸住まいをする男。その男が、財産目当てに彼の娘に求婚しようとしたジゴロを射殺したことから、生きる方向を見失ってしまう姿を描いていく。N・ローグ流ともいうべき時間を行き来するストーリーが、賛否両論を招いた異色作。イギリスでは1983年に公開されたものの一週間で打ち切られたが、翌年公開されたアメリカではカルト的な人気を得た。無名時代のM・ロークがチンピラ役で出演している。「ジェラシー」「白い炎の女」のマリット・アレンが担当した衣装が素晴らしい。", "1982-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恐怖新聞", "恐怖新聞", "", "", "http://image.eiga.k-img.com/images/movie/56431/315x210.jpg?1665", 71, "1973年から「週刊少年チャンピオン」（秋田書店）で連載され、当時のオカルトブームをけん引したつのだじろうの同名コミックを実写映画化。大学生の鬼形はある夜、「購読料は1年分の寿命」と書かれた「恐怖新聞」を受け取る。紙面には同級生が刃物で刺され死亡という未来の記事が掲載されており、半信半疑だった鬼形だが、実際に記事通りの事件が起こる。そしてまた新たな恐怖新聞が届き……。主演はダンス＆ボーカルユニット「AAA」の與真司郎。", "2011-05-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("えいがでとーじょー！　たまごっち　ドキドキ！うちゅーのまいごっち!?", "えいがでとーじょー！　たまごっち　ドキドキ！うちゅーのまいごっち!?", "", "", "http://image.eiga.k-img.com/images/movie/53141/315x210.jpg?2491", 88, "社会現象まで巻き起こした大ヒットゲーム“たまごっち”をアニメ映画化。まめっち、めめっち、くちぱっちら個性的な住人が暮らすたまごっち星。物体を瞬間移動させることのできる“転送マシン”を発明したまめっちは、実験中に地球から人間の女の子・たんぽぽを呼び寄せてしまう。たんぽぽは地球に帰れるまでまめっちの家で暮らすことに。ある朝、たまごっち星に異変が起こり……。人気お笑いコンビのオリエンタルラジオが声の特別出演。", "2007-12-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HANUSSEN", "ハヌッセン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "ヒトラーの未来を予言し、ベルリンの一流劇場で超能力を披露し、詐欺師として訴えられ、ナチスの終焉までも見抜いた謎の予言者ハヌッセンとはいったい何者だったのか……。「メフィスト」のコンビ、I・サボー監督とK・M・ブランダウアーが再び組んだことが話題になった芸術大作。", "1988-01-01", "30,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SS501 LIVE at Cinemart", "SS501 LIVE at Cinemart", "", "", "http://image.eiga.k-img.com/images/movie/55491/315x210.jpg?3242", 121, "2005年にデビューした韓国を代表するトップアイドルグループ「SS501」が、2009年8月13日に武道館で行ったライブをスクリーンで上映。韓国版「花より男子」で主要人物ユン・ジフ（花沢類）役を演じ、日本でも大ブレイクを果たしたリーダーのヒョンジュン率いる「SS501」の伝説のライブ映像を収録。", "2010-05-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A計劃／PROJECT A", "プロジェクトA", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "香港が世界に誇るアクション・スター、チェンの10周年記念映画。非道の限りをつくす海賊を倒すため、彼らのアジトに乗り込むチェンの活躍を描く。サモ・ハン・キンポー、ユン・ピョウらを加えた、迫力あるカンフー・シーンが見ものだ。", "1984-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／THE　DAY　A　PIG　PELL　INTO　THE　WELL", "豚が井戸に落ちた日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ソウルに暮らす男女の姿をリアルに描いた愛憎劇。無気力なヒョソプは金と女にだらしない三流小説家。彼には映画館で働く少女ミンジェと、人妻のポギョンという愛人がいた。ポギョンの夫トンワは極端に潔癖症のサラリーマン。物語は、この４人の愛と葛藤をドキュメント風に捉えたもの。愛情に飢えながらも、報われない彼らの姿に、都会の孤独や虚無感が生々しく浮き彫りにされる。ロッテルダム映画祭ではグランプリを受賞。", "1996-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A PRIZE OF GOLD", "黄金の賞品", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "第二次大戦後、占領下のベルリンで、ナチが隠した純金の延べ棒が発見された。米軍のジョーと英軍のロジャーがこれをイギリスまで輸送する任に就くことになった。戦災孤児たちと彼らの母親代わりの娘マリアに同情したジョーは、ロジャーと共謀して金塊の横領を企む……。R・ウィドマークが絶妙の演技を見せたサスペンス快作。", "1955-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLINT EASTWOOD: OUT OF THE SHADOW", "クリント・イーストウッド　アウト・オブ・シャドー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MADELEINE", "マデリーン 愛の旅路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ヴィクトリア時代のイギリス・グラスゴーのブルジョワ娘マデリーンが、スラム街に住む貧乏なフランス青年と夜な夜な通じ合う……。実在したスキャンダルを題材に、許されぬ恋に身を焦がし、情熱をほとばしらせる女心を描くリーンの傑作。", "1949-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THEY ONLY KILL THEIR MASTERS", "大捜査", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "砂浜に打ち上げられた女性の他殺死体。事件は初め、愛犬のドーベルマンの仕業だとして処理されるが、漁師町の素朴な警察署長マーシュが捜査を始めるうちに、意外な事実が明らかになり……。TVシリーズ『ロックフォードの事件メモ』でおなじみのJ・ガーナーのサスペンス。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JADE", "ジェイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ある日、大富豪が猟奇的な手口で惨殺された。その場から州知事の淫らな写真が発見され、捜査線上に浮かんできたのが売春組織の女“ジェイド”。次期の地方検事を目指すコレリ検事補は、知事の妨害にもめげず執拗に捜査を進めるが、その中で友人の妻トリーナのかかわりを匂わす証拠が上がってきたのだった。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RATCATCHER", "ボクと空と麦畑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1970年代のグラスゴー。落ちぶれた工業地帯に暮らす少年ジェイムズは、繊細な感受性を持て余し、周囲の人々から孤立し始めていた。心の支えは“郊外の綺麗な家に引っ越し、この鬱積した町から逃げ出す”という夢だけ。しかし、まだ12歳の少年でしかないジェイムズには耐え難い、衝撃的な出来事が次々と襲いかかる……。1500人余から選ばれたアマチュアの少年少女たちが、誰の心にもある子供時代の思い出を蘇らせる美しい作品。2000年英国アカデミー賞（BAFTA）最優秀新人賞のほか、1999年ブリティッシュ・インディペンデント・フィルム・アワード、ロンドン・フィルム・フェスティバル、エジンバラ映画祭、シカゴ国際映画祭、フランダース国際映画祭でも多くの賞を受賞。スコットランドの女流監督L・ラムジーの第１回監督作。", "1999-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL AMOR BRUJO", "恋は魔術師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "愛の亡霊を追いかける女カンデーラと彼女への不断の情熱を打ち明ける男カルメロ。しかし二人の間を阻む亡霊は、カルメロをカンデーラから遠ざける。呪文の助けを借り、月光の下で“火祭りの踊り”が行われる。虚と実の交錯した独自の世界を作り上げるサウラ監督のフラメンコ・ミュージカル３部作完結編。", "1985-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Arlington Road", "隣人は静かに笑う", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "妻の死から立ち直れない大学教授マイケルの隣に、ラング一家が引っ越してきた。気さくなラングとすぐに親しくなったマイケルだが、ふとしたことからラングの経歴上のウソが発覚。調査に乗り出したマイケルは、やがて意外な事実を知るが、時すでに遅くラングの罠に落ちていた……。サイコスリラー風の展開で引き寄せつつ、衝撃の事実を明かした結末に驚かされる異色サスペンス。恐るべき隣人を個性派T・ロビンスが怪演している。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("民暴の帝王", "民暴の帝王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "地上げ、不正融資、政治献金と巧妙な手口で法の目をくぐり抜け、バブル経済下の政財界を暗躍する大物ヤクザ・江田。そんな彼の生き様を、対立する武闘派との抗争の中に描く。「修羅の伝説」の和泉聖治監督と小林旭が２度目のコンビを組み、息の合ったところを見せている。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CASANOVA1970", "カサノヴァ'70", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "回想録で有名な、中世時代の性豪カサノヴァが現代によみがえった!? NATO軍の将校で名うてのプレイボーイ、アンドレアは、普通の恋愛に飽き足らず、危険な情事を求めて、ヨーロッパ中を駆けめぐるが……。マストロヤンニ主演のエクスタシー・アドベンチャー。", "1964-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("香港也瘋狂／CRAZY HONGKONG", "ブッシュマン４　ホンコン大パニック！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "第３作と同様、香港で製作されたカルチャーギャップ・コメディ“ブッシュマン”シリーズの１本。製作順では、こちらが「ミラクル・ワールド　ブッシュマン３」の前に作られている。アフリカでCF撮影をしていた香港の女性が現地に置いていった、コーラの瓶に入った人形。これを返そうと、ニカウさんが香港にやって来たことから、またまた大騒動が巻き起こる……。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("３NINJAS KICK BACK", "クロオビキッズ 日本参上！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "空手の達人ロッキー、コルト、タムタムの３兄弟が大人顔負けのアクションを繰り広げるシリーズの第２作。忍術大会の優勝者に黄金のありかを秘めた短刀を与えるために、師匠のおじいちゃんは日本に里帰りするが、短刀を狙う悪者コガに襲われてしまう。日本に駆けつけた３人はおじいちゃんに頼まれ、甲賀の忍術大会に出場し優勝者の少女ミヨと友達になる。４人は短刀を総師範に届けるが、総師範は実はニセ者で……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浅草の肌", "浅草の肌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「痴人の愛」のナオミ役で、奔放な女性の魅力を見せた京マチ子が、本作でも男を知りつくした踊り子クルミに扮して、女豹のような妖しい色気をふりまいている。浅草六区の裏通りにあるレビュー劇場・美銀座。そこの演出家・香坂は厳格な男で知られていた。ある日、クルミが劇場にやって来て……。木村監督が京マチ子の持ち味を充分に引き出した。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE PIECE ワンピース　ねじまき島の冒険", "ONE PIECE ワンピース　ねじまき島の冒険", "", "", "http://ecx.images-amazon.com/images/I/616Ltf2GOIL._SL210_.jpg", 60, "「週刊少年ジャンプ」で大好評連載中の尾田栄一郎原作による海洋冒険コミックの劇場版アニメ第2弾。麦わらのルフィ海賊団の大事な船・ゴーイングメリー号が盗まれてしまい、世界一の泥棒を夢みる兄弟ボロードとアキースから、船を盗んだ犯人はトランプ海賊団だと教えられたルフィたちは、トランプ海賊団が根城にしているねじまき島を目指す。そこで彼らを待ちうけていたのは、トランプ海賊団の船長ベアキングが仕かけた数々のワナだった。", "2001-03-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("薔薇合戦", "薔薇合戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "成瀬巳喜男監督が、東宝争議のなかで設立された独立プロ“映画芸術協会”から松竹へ出向して撮った作品。丹羽文雄の小説を原作に、ライバル同士の化粧品会社の競争に巻き込まれた美しい三姉妹の運命を描く。背景にどす黒い企業戦争がある点など、成瀬向きの題材とは言いがたいが、三宅邦子ら３女優の美しさは絶品。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("佐々木小次郎", "佐々木小次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "『朝日新聞』に連載された、村上元三の人気小説の映画化。主役に歌舞伎界の新星・大谷友右衛門（のちに中村雀右衛門）を起用して話題となった。第１作は、恋人と別れてから放浪の生活をするが、第２の恋人の死を経てついに秘剣“つばめ返し”をあみ出すまでを描く。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRINCESS AND THE PIRATE", "姫君と海賊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "B・ホープの絶頂期の作品。海賊に捕らえられた男が美しいお姫様と出会い恋をする。やがて男は海賊のボスになってしまうが……。ラストにはビング・クロスビーが突然出演して姫をさらってしまう、というオチがついている。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　おむすびまん", "それいけ！アンパンマン　おむすびまん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 27, "やなせたかし原作の人気アニメーション劇場版第2作「それいけ！アンパンマン　ばいきんまんの逆襲」と同時上映の短編。", "1990-07-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女子女子 over8", "女子女子 over8", "", "", "http://image.eiga.k-img.com/images/movie/54451/315x210.jpg?3435", 0, "「斬新でオリジナリティのある映画を創る」をコンセプトにしたプロジェクト“over8”の第3弾で、女子2名を主人公にしたショートフィルムのオムニバス。「土井さんの不幸」「ramify」「街の右側」「アメリカ女」「記憶という名のバスと真冬のリディム」「月照の歌」「ライツオブリトルタウンズ」「やまないカーテンコール」の8本を上映する。", "2009-03-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("舌／デッドリー・サイレンス", "舌／デッドリー・サイレンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 38, "海外の映画祭で評判を呼んだホラー映画「L'Ilya〈イリヤ〉」を監督したインディーズ系の注目株、佐藤智也のホラー。監督自らが、製作費200万円、撮影期間は一週間という過酷な条件を課し、ハイビジョンカメラで撮影。ゾンビ映画のフォーマットを借りながら、ある夫婦の姿を綴ったダークなファンタジーだ。主演のまんたのりおは映像作家もこなす個性派。35歳の夫は画家を名乗っているものの、稼ぎはあくまでアルバイト。25歳の妻は、夫の描く絵がお気に入りで、なんとか売り込もうと日々邁進している。お互いに支え合って生きてきたけれど、ある夜、夫が妻の死体を捨てる事件が……。DVDは「マレヒト」を併録。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千禧曼波", "ミレニアム・マンボ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "新世紀を迎えたばかりの台湾・台北を舞台に、ホウ・シャオシェン監督が二人の男性の間で揺れ動く女性の喪失と再生を描いたドラマ。ヒロインが10年前の自らの恋愛を回想する形で、都会に生きる女性の孤独や不安を輝くような映像で詩的に綴っていく。", "2001-01-01", "53,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤胴鈴之助 鬼面党退治", "赤胴鈴之助 鬼面党退治", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "旅道中の鈴之助は街道で黒衣・鬼面の怪武士に襲われ、虫の息の男から頼まれた伝言を、かおる姫に伝えるが、彼女はそんな男は知らぬと否定する……。堀家の財宝を狙う悪人団を鈴之助と竜巻雷之助が撃退する痛快編。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版NARUTO ナルト　疾風伝", "劇場版NARUTO ナルト　疾風伝", "", "", "http://image.eiga.k-img.com/images/movie/34101/315x210.jpg?1449", 95, "原作コミックスの累計発行部数は6900万部を突破し、海外でも高い人気を誇る人気アニメの新シリーズ「NARUTO -ナルト- 疾風伝」の劇場版第1弾。かつて世界を破滅寸前まで追い込んだ恐ろしい魔物が、何者かの手によって復活を遂げた。落ちこぼれ忍者のうずまきナルトは、その魔物を封印する力を持つ巫女・紫苑を守る任務に就く。ところがナルトは、紫苑によって“死の予言”をされてしまう。そして予言通り、ナルトは死を迎え……。", "2007-08-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRIMAL FEAR", "真実の行方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "真実をめぐっての裁判で、名声を手に入れるために無実を主張する弁護士が罠に巻き込まれる姿を描いた法廷サスペンス。冬のシカゴで78カ所を刺された大司教の死体が、胸に謎の文字を残したまま自宅で発見された。敏腕弁護士マーチン・ベイルは、マスコミの脚光を浴びたいがために容疑者である19歳の青年アーロンの弁護を無償で引き受ける。マーチンは無実を勝ち取るためにアーロンの途切れた記憶を利用して、ヤクザ絡みの事件を主張し、かつての恋人ジャネット検事と対峙する。だが、失われた記憶をアーロンが取り戻していくにつれ、裁判は彼のもくろみとは違う方向へと進んでいく。アーロンを演じたE・ノートンは本作でゴールデン・グローブ助演男優賞に輝いた。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ももへの手紙", "ももへの手紙", "", "", "http://image.eiga.k-img.com/images/movie/56031/315x210.jpg?2460", 120, "監督デビュー作「人狼 JIN-ROH」（1999）で国際的にも高い評価を受けた沖浦啓之が、同作以来約12年ぶりに手がけた長編第2作。瀬戸内海の小さな島を舞台に、1人の少女と亡き父親の残した書きかけの手紙を軸に、親子の愛情を描く。ケンカして和解しないまま父親が事故で他界した小学6年生の少女ももは、母のいく子とともに親戚のいる瀬戸内海の汐島に引っ越してくる。父親が「ももへ」と一言だけ書き残した手紙を手にしたももは、父親が本当は何を書きたかったのかが気になり、空虚な日々を過ごす。そんなある日、ももは「見守り組」と名乗るイワ、カワ、マメという3人組の妖怪に出会う。", "2012-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白髪魔女傳2", "白髪魔女伝2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "武闘八派一門の剣豪チョウと恋に落ち、裏切られたとの誤解から復讐の鬼神“白髪魔女”と化したナイチャン。しかしナイチャンの前から姿を消したチョウは、彼女の白髪の呪縛を解き放つため、伝説の魔法の花を守り続けていたのだ。復讐に燃え八派一門を全滅せんとするナイチャンに、チョウの愛は通じるのか……。前作「白髪魔女伝」の脚本家だったデビッド・ウーがメガホンを取った続編。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Dernier Trappeur", "狩人と犬、最後の旅", "", "", "http://image.eiga.k-img.com/images/movie/33601/315x210.jpg?2219", 101, "ロッキー山脈に実在する狩人と愛犬たちとの絆を描き、本国フランスで「皇帝ペンギン」「ディープ・ブルー」を抜いて大ヒットを記録した感動作。自然破壊により、長年暮らしてきた山から去ることを決意した狩人ノーマン・ウィンター。狩りもできない一匹の子犬との出会いが、彼の人生を大きく変えていく。監督のニコラス・バニエは、フランスでは国民的ヒーローとされる著名な冒険家で、小説家・写真家としても活躍している。", "2006-08-12", "2,10,7,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shaft", "シャフト", "", "", "http://image.eiga.k-img.com/images/movie/1641/315x210.jpg?1789", 99, "ニューヨークで起きた白人による黒人の殺人事件。刑事ジョン・シャフトは、犯人ウォルターを逮捕するが、有名な不動産王の息子であるウォルターは逮捕されるたび父親の金の力で幾度となく釈放されていた。自力で正義を貫く決意をしたシャフトは、警官を辞め、復讐に燃えるウォルターやハーレムの麻薬王、昔の同僚だった汚職警官などに命を狙われながら、ただ一人孤独な戦いを挑むのだった……。", "2000-11-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春 PARTII", "青春 PARTII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "競輪選手を志す若者が、競輪学校に入り、厳しい訓練を受けて一人前の選手になっていくまでを、スナップ風に捉えた作品。あまり込み入ったストーリーではなく、学校での訓練の過程をなかばドキュメンタリーのように描いている。主人公の若者に助言するかつての中学教師の役で、舟木一夫が好演。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ASTRONAUT’S　WIFE", "ノイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "宇宙飛行士である夫の帰還後、妻を襲ういい知れぬ恐怖を描くSFスリラー。NASAの宇宙飛行士スペンサーが船外作業中にトラブルに遭い、奇跡の生還を果たす。だが、彼は交信が途絶えていた空白の二分間のことを話そうとはしなかった。以来、深夜にラジオから流れる不気味なノイズに耳を傾けるなど、夫の不審な行動が目立つようになり、妻ジリアンの胸中を不安がよぎる。やがて、スペンサーの同僚の死やNASAの調査員の失踪など不可解な事件が起きるなか、ジリアンは双子を妊娠するのだが……。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE HISTOIRE D’EAU", "水の話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 13, "傑作短編「あこがれ」とヌーヴェル・ヴァーグの金字塔「大人は判ってくれない」の間に位置する、映画史の“小さな忘れ物”。パリが洪水で水びたし、というニュースに映画的感性を刺激された若きトリュフォーは現地に駆けつけ、即興でカメラを回し始める。が、間もなく撮影を断念。フィルムもほったらかしになっていたが、ゴダールがこれを編集、パリの大学に通学する若い娘と、彼女がヒッチハイクした車のドライバーが、いつも通い慣れているのに、洪水ですっかり様変わりした道々を脱線しながらパリを目指す、シネ・スケッチとして再生させた。ゴダール自ら、ナレーションを担当している。ビデオ発売時にはJ・シュヴァンクマイエル他の短編が併録されていた。DVDは「アラン・レネ／ジャン・リュック・ゴダール短編傑作選」に収録。", "1958-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チェッカーズ／　SONG FOR U.S.A.", "チェッカーズ／　SONG FOR U.S.A.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「CHECKERS in TAN TAN たぬき」に続く、チェッカーズ主演映画第２作。自分たちの音楽に疑問を持ちはじめたチェッカーズが黒人ミュージシャンと出会い、彼ら自身の歌を発見するまでを描く。アイドル映画の定石をふまえたラストのコンサート・シーンが圧巻。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大日本殺し屋伝", "大日本殺し屋伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "殺し屋の町と称される町を牛耳る五光会。そのボス佐々木が、何者かの手によって殺害された。幹部たちは、殺し屋プロダクションを通じて日本を代表する10人を集め護衛とするが、その網の目をぬって敵は襲ってくるのだった……。花登筐が自らの原作を、才賀明とともに脚色をした“大日本喜劇”シリーズの一本である。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("牧馬人／THE　HERDSMAN", "牧馬人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "中国に息子を残しアメリカで実業家として成功した父が、30年ぶりに故国に息子を迎えに来る。息子は長い間、右派分子として辺境の牧場に下放されていた。今、父と再会した彼は父とともにアメリカに渡るか故郷での生活を取るかで思い悩む……。「天雲山物語」に続くシェ・チン監督作品で百花奨最優秀作品賞ほか多くの賞に輝いた。", "1982-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Darkman", "ダークマン", "", "", "http://ecx.images-amazon.com/images/I/51AKJD98DYL._SL210_.jpg", 96, "　科学者ペイトンは人工皮膚の研究に打ち込んでいた。ある時、弁護士でもある恋人ジュリーが携わっていた不正事件がきっかけで、ペイトンのラボにギャングのデュラン一味が押し入り、そこを爆破。ペイトンは一命こそ取り留めたが、ふた目と見れない姿になってしまう。やがて彼は完成した人工皮膚で別人になりすまし、デュランたちに復讐を開始するが……。サム・ライミ監督らしいコミック感覚あふれる映像が新鮮なアクション・スリラー。大ヒットし、シリーズ化された。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江湖情／RICH AND FAMOUS", "愛と復讐の挽歌〈野望編〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "大作「愛と復讐の挽歌」２部作の前編。暗黒街に生きる男たちの愛と裏切り、果てしない野望を描いた大河ノワール。中国からの難民としてともに育ったクオ、そしてユンとホイの兄妹。ある事件をきっかけにヤクザに捕らわれたクオだったが、ホイがある夜出会った大物サイのおかげで無事助け出される。これが縁でクオとユンはサイの手下となったが……。人気スター、チョウ・ユンファがヤクザ一家のボスを男気たっぷりに演じている。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ポケットモンスター　セレビィ・時を超えた遭遇〈であい〉", "ポケットモンスター　セレビィ・時を超えた遭遇〈であい〉", "", "", "http://ecx.images-amazon.com/images/I/61eWa%2BaIGHL._SL210_.jpg", 75, "未知のポケモンが登場するシリーズ第４作。森に住む伝説のポケモン、セレビィを守る少年ユキナリが40年の時を超えてサトシらのまえに出現。サトシとピカチュウも彼に協力してセレビィを守ろうして奮闘。そのさなかに強くて動きも素早い、まだ見ぬ未知のポケモンが誕生し……。スリリングな冒険を展開させつつ、真の優しさと強さを問いかける。", "2001-07-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PUBLIC EYE", "パブリック・アイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "事故現場に急行して、誰よりも早くその模様を写真に収めるフリーのカメラマン、レオン。そんな彼が、クラブの美人オーナーに心惹かれ、彼女の依頼した仕事を引き受けたことから、ギャングの抗争に巻き込まれていく。個性派J・ペシの渋い魅力も光る、都会の孤独な男女の恋を描いたサスペンス風のドラマ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ENFANT LION", "シルガ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アフリカの大地を舞台に、同じ日に生まれ、同じ乳を飲んで育った、ライオンの王女シルガと村の酋長の息子ウーレの友情と不思議な絆の物語。短中編映画やドキュメンタリー映画等で経験豊かなP・グランペレが、８ヵ月にわたるアフリカ・ロケによって、ドラマティックに、哀感たっぷりに描き出した。L・ベッソンが製作を担当、音楽はアフリカン・ミュージックの鬼才S・ケイタ。フランスで84週というロングランを記録。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER GOLEM，WIE ER IN DIE WELT KAM", "巨人ゴーレム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "中世のプラハ。ユダヤ人は皇帝の圧政に苦しめられていた。ユダヤ教の法律学者は泥人形ゴーレムに生命を与え、その迫害から救ってもらおうとするが、人々はやがて力を得たゴーレムを恐れはじめる。大映映画「大魔神」の原型になったというサイレントの傑作。", "1920-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("追鬼七雄／THE　TRAIL", "霊幻追鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "M・ホイ企画、製作、L・ホイ主演で描いたホラー・ムービー。1922年の中国湘西省を舞台に、強力なゾンビと遭遇した麻薬密輸ギャング団の恐ろしい体験を、お色気とユーモア、そしてちょっぴり哀感も交えて綴る。ラストは「エクソシスト」のパロディー。", "1982-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グッモーエビアン！", "グッモーエビアン！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青の塔", "青の塔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "少年犯罪の加害者家族を見つめた「カタルシス」で高い評価を得た坂口香津美の長編第１作。ひきこもりの生活を続ける青年の心の内側を、リアルな日常描写を重ねた静かな映像の中に描き出す。主人公を演じるのは、実際にひきこもりの経験があったという中村佑介。監督の依頼により役者に初挑戦、他人との交流を遮断して生きる孤独と葛藤を生々しく演じている。19歳の透は、崖から転落死した妹を助けることができなかった呵責に苦しみ、ひきこもりの生活をしている。一緒に暮らす母は、そんな彼の苦悩が理解できずに苦しんでいた。ある日、透は運河に倒れていた少女と出会い……。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新宿黒社会 チャイナ・マフィア戦争", "新宿黒社会 チャイナ・マフィア戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "黒社会＝チャイニーズ・マフィアと日本のヤクザ、警察との間で繰り広げられるピカレスク・バイオレンス・ムービー。犯罪の坩堝、新宿・歌舞伎町。チャイニーズ・マフィア“龍爪”は、異常なまでの残虐性で急激に勢力を伸ばしていた。これを追う新宿署の一匹狼・桐谷龍仁は、弟の義仁がその組織に関わっていることを知る。兄弟は中国残留孤児二世。日本にも中国にも所属できない疎外感から、家族の絆を大切にする龍仁は、立場を利用し、両親と弟のために不正で莫大な金を手にしていたが、汚い世界から弟の手を引かせるべく、その裏社会のネットワークを使って“龍爪”のボスを追い詰めていく。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ALAMO：THIRTEEN DAYS TO GLORY", "アラモ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "ジョン・ウェイン製作・監督・主演の映画「アラモ」を西部劇の職人B・ケネディが再映像化したTVムービー。J・ウェインが演じたデイビー・クロケットにB・キース、そして今作品の主役トラヴィス大佐役にはA・ボールドウィン(前作はローレンス・ハーヴェイ)、ボウイ大佐役はJ・アーネス(前作はリチャード・ウィドマーク)。前作のスターたちと同系統の俳優を起用している。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIS", "霧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "トルコ映画「路〈みち〉」「群れ」などの映画音楽作曲家として知られるズルフェ・リバネリが、1960～78年までクーデターで揺れ動くイスタンブールに住む、一家族の悲劇を描く社会派ヒューマン映画。E・カザン監督が、漁師だまりのカフェでバックギャモンに興ずる老人役で特別出演している。", "1989-01-01", "32,14,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bachelorette", "バチェロレッテ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/58011/315x210.jpg?3569", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍鳳賊捉賊／LICENSE　TO　STEAL", "レッド・リベンジ 復讐の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "女盗賊ハン・インと、妹分のパートナー、ヤン・ピンは、国際強盗団の一員として、大がかりな盗みをはたらいていた。ある日、ハンは足を洗おうと、最後の仕事に向かう。ところが、ヤンに裏切られて罠にはまり逮捕されてしまった。元ミス香港のジョイス・コウはじめ肉体派の美女たちが、華麗なアクションを見せてくれる。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Badlands", "地獄の逃避行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "　25歳のキットはジェームズ・ディーンに憧れている若者。清掃員として働く彼は15歳の少女ホリーと出会う。互いに惹かれあい交際が始まるが、ホリーの父親はそれを認めようとしなかった。そんな彼をキットが殺害。その後、キットとホリーは逃避行を続け、犯行を繰り返していくが……。実際に起こった事件をもとに、テレンス・マリックが初監督を務めた作品。少女のナレーションで進められる切ない物語、そして詩情豊かな美しい映像が胸を打つ伝説的カルトムービー。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バルーンリレー", "バルーンリレー", "", "", "http://image.eiga.k-img.com/images/movie/57841/315x210.jpg?4275", 66, "ユナイテッド・シネマが主催する「シネマプロットコンペティション」の2010年度テーマ部門受賞作品を映画化。女子中学生のこずえは、街灯に引っかかっていた赤い風船をとろうとして負傷してしまう。怪我のせいでバスケ部のスタメンからはずされてしまい、不満を溜め込んでいたある日、こずえは再び赤い風船を発見する。風船には1枚のSDカードがついており、そこに記録されていた映像から、事態は思わぬ方向へと転がっていく。CM、ドラマ、ファッション誌などで活躍する若手女優・刈谷友衣子が映画初主演。", "2012-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・誘惑旅行", "喜劇・誘惑旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "常夏の国フィリピンを舞台にした第９作。新幹線ひかり号の車掌・大沢泰三と妻の弘子はクイズでフィリピン旅行を獲得する。現地へ着いた二人はそれぞれのアバンチュールを楽しむ……。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　BLOOD　OF　FU　MANCHU", "女奴隷の復讐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "C・リーが悪党フー・マンチューに扮した怪奇アクション・シリーズの第４作で、第２、３作は日本未公開。世界征服の野望に燃えるフー・マンチューは、ヘビの猛毒を体内に蓄えた美女たちを利用して、計画を実行に移す。フー・マンチューの宿敵であるスミス警部は美女の攻撃に遭い、視力を失いながらも、巨悪を追い詰める。DVDタイトルは「怪人フー・マンチュー　女奴隷の復讐」。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤壁　決戦天下", "レッドクリフ Part II 未来への最終決戦", "", "", "http://image.eiga.k-img.com/images/movie/54091/315x210.jpg?3055", 144, "魏軍と呉蜀連合軍が中国・長江で繰り広げた「三国志演義」最大の決戦“赤壁の戦い”を、ジョン・ウー監督が映画化した2部作の後編。周瑜にトニー・レオン、孔明に金城武ほか、チャン・チェン、ビッキー・チャオなどアジアのスターが集結した。2000隻の軍船を率いて侵攻してきた曹操軍は、疫病で死んだ自軍の兵士たちの遺体を対岸の連合軍側に流す。それを見た周瑜たちは憤るが、劉備は戦意を喪失し、自国の兵の撤退を決意。呉蜀の間に亀裂が生じてしまう……。", "2009-04-10", "1,8,76,53,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sabotage", "サボタージュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "　精力的に映画製作を続けたヒッチコック1963年製作の2作目。「サボタージュ」とは破壊行為の意味。映画館主カールは、妻の知らないところでテロ活動に手を貸していた。次の標的はロンドン市長の就任パレードだったが、青果商に成り済ました刑事のテッドに感づかれてしまう。一般人が巻き込まれるバスの爆発シーンなど、テロを助長するなどの理由から上映禁止になった国も。アメリカでは、「A Woman Alone」とタイトルを変更し上映された。", "1936-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("里山", "里山", "", "", "http://image.eiga.k-img.com/images/movie/54601/315x210.jpg?4054", 90, "国内外で高い評価を受けたNHKの傑作ドキュメンタリー番組「映像詩・里山」シリーズに新たなシーンを追加した劇場版。日本特有の自然の中を保有し多くの生物が暮らす動物の楽園でありながら、長きに渡り人間と共生し続けてきた里山。最新ハイビジョンカメラを駆使した斬新な映像で四季の移ろいとともに映し出し、同地で脈々と受け継がれてきた自然と人間との深い結びつきに迫っていく。", "2009-08-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TALOS THE MUMMY", "タロス・ザ・マミー／呪いの封印", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "「ハイランダー」シリーズのR・マルケイがミイラ男の恐怖をSFXを駆使して描くサスペンス・ホラー。古代エジプトの王子タロスの墓を発見した考古学者が行方不明になった。それから50年後、その王子の棺が大英博物館に展示された直後に、人間の臓器がえぐられる猟奇殺人が続発。悲劇的末路をたどった王子の恐るべき呪いが幕を開ける……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空とコムローイ　タイ、コンティップ村の子どもたち", "空とコムローイ　タイ、コンティップ村の子どもたち", "", "", "http://image.eiga.k-img.com/images/movie/54281/315x210.jpg?4631", 90, "監督の三浦淳子が7年の歳月をかけて撮影した、タイ最北端の村メーサイで暮らす山岳民族の女性と子供たちのドキュメンタリー。村ではイタリア人のペンサ神父とタイ人女性のノイが、人身売買の危険に晒されているアカ族の女性達に、売春や麻薬密売に従事しないよう指導を行っている。そのコミュニティで暮らす女性ユイは幸せに暮らしていたが、娘のファを産んだ後にエイズによって命を落としてしまい……。", "2009-01-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ボーイ・ミーツ・プサン", "ボーイ・ミーツ・プサン", "", "", "http://image.eiga.k-img.com/images/movie/34201/315x210.jpg?1558", 80, "映画祭で賑わう韓国・釜山を訪れた日本人青年の恋と成長を描いた青春ストーリー。観光プロモーション用の映像を撮影するために釜山へやって来た新米ディレクターのクリハラ。頼りにしていた現地のガイドと出会えず、言葉もわからないまま街を彷徨っていた彼は、不思議な日本人女性ヨーコと出会い、一緒に行動することになるが……。これまで「パッチギ！」「嫌われ松子の一生」など多数のヒット作で助監督を務めてきた武正晴の監督デビュー作。", "2007-09-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("wkw／tk／1996＠７’55”hk.net", "wkw／tk／1996＠７’55”hk.net", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ファッション・デザイナーの菊池武夫が、自ブランドのスタイルを映像で表現するために企画した短編。中国返還を１年後に控えた香港に、ジャパニーズ・マフィアがある目的のためにやって来る。しかしある女と出会ったことから、男の運命は大きく変わるのだった。W・カーウァイ、C・ドイルという「恋する惑星」「天使の涙」のコンビが監督、撮影を担当。「天使の涙」と同時上映されたほか、編集し直してCMとしてTV放映もされた。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Filth And The Fury", "NO FUTURE：A SEX PISTOLS FILM", "", "", "http://image.eiga.k-img.com/images/movie/47681/315x210.jpg?1742", 105, "1970年代後半にパンク・ブームを巻き起こした「セックス・ピストルズ」のドキュメンタリー。1975年に結成され、過激な音楽と言動で若者に絶大な人気を誇るも、1978年に解散。バンドのベーシスト、シド・ビシャスのドラッグによる突然死など、バンドの歴史を20年の時を経てメンバー自ら証言。当時のライブ映像や関係者の証言も交えて彼らの姿に迫る。", "2008-08-23", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("180° South", "180° SOUTH", "", "", "http://image.eiga.k-img.com/images/movie/55811/315x210.jpg?3748", 87, "世界的に有名なアメリカのアウトドアブランド「パタゴニア」と「ザ・ノース・フェイス」の創業者イボン・シュイナードとダグ・トンプキンス。60年代の終わり、彼らはともにサーフ＆マウンテンの楽園を求めて南米パタゴニアを旅していた。両ブランド設立のきっかけとなったこの伝説的なロード・トリップを現代の青年が追体験し、壮大な自然の中で彼らが見たものを明らかにしていくドキュメンタリー。", "2011-01-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("汝 殺すなかれ", "汝 殺すなかれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "平凡なサラリーマンの石崎はある夜、若い殺し屋、安西の暗殺現場を目撃してしまう。口封じのため仲間に引き込まれる石崎。彼と安西、安西の助手である弥生を含めた３人は、仕事をこなすうち不思議な感情で結ばれていった。ある日、抹殺してきた被害者に共通する秘密が、石崎にも関係あることを知った安西は石崎との別れを決める。しかしその時、彼らには自称傭兵上がりの殺し屋が迫っていた。三角関係の機微を軸に描いた、和風ハードボイルド。佐藤監督の実父・佐藤允が殺し屋の坂田役で出演、独自の存在感でラストを締めくくる。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オロ", "オロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "", "2012-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SARAFINA！ THE SOUND OF FREEDOM", "サラフィナ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ブロードウェイの大ヒットミュージカルの映画化。南アフリカの高校生サラフィナは女優を目指す少女。しかし人種差別と軍の弾圧は、彼女の夢や日常を押しつぶしていく。サラフィナは怒りから過激派に加担するようになるが……。南アフリカ・ロケ敢行によるパワフルな映像が魅力の力作ヒューマン・ミュージカル。", "1992-01-01", "3,7,67", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八つ墓村", "八つ墓村", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "1970年代後半に「犬神家の一族」「悪魔の手毬唄」など５作の横溝正史原作をヒットさせた市川崑監督が、再び横溝原作に挑戦した作品。400年前、毛利家に追われた８人の落武者を、懸賞金目当てに惨殺したことから名前が付いた八つ墓村では、落武者の祟りが言い伝えられていた。そこに招かれた辰弥は、八つ墓村の資産家・田治見家の遺児であり、田治見家を支えてくれと頼まれる。辰弥は、双子の老婆、当主である兄の久弥、姉の春代ら田治見家の人たちに温かく迎えられるが不安を感じる。そして、それは的中し、久弥が毒殺され、続いて第２、第３の殺人が起きる。犯人も動機もわからぬまま、村人たちは祟りだと恐れ始める。そこへ、探偵・金田一耕助が現れ、一つ一つ謎を解き明かしていく……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大洗にも星はふるなり", "大洗にも星はふるなり", "", "", "http://image.eiga.k-img.com/images/movie/54591/315x210.jpg?4041", 103, "バラエティ番組の人気放送作家・福田雄一が脚本と監督を務める群像コメディ。夏休み中、茨城県・大洗の海の家でともにバイトをしていたナルシストの杉本（山田孝之）、奥手なサメマニアの松山（山本裕典）、海の家のマスター（佐藤二朗）らは、憧れのマドンナ・江里子（戸田恵梨香）からの呼び出しの手紙で、クリスマス・イブの大洗に再び集まるが……。", "2009-11-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TREIZE JOURS EN FRANCE", "白い恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "1968年、フランスのグルノーブルで開催された第10回冬期オリンピックの模様を収めたドュメンタリー映画。13日間の選手たちの競技、日常生活などがF・レシャンバックの撮影によっていきいきと記録された。ルルーシュの名パートナー、F・レイの甘美な音楽が印象深い。", "1968-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("You Only Live Twice", "007は二度死ぬ", "", "", "http://ecx.images-amazon.com/images/I/51Wxsd6C%2BBL._SL210_.jpg", 117, "ジェームズ・ボンドが日本に乗り込んで活躍するシリーズ第５作。米・ソ両国の直接対決を画策するスペクターの陰謀を、日本側の諜報部員の協力を得て打ち砕く。ボンドガールには浜美枝。日本ロケの場面が作品の内容とは別に楽しめる。", "1967-06-30", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十八歳、海へ", "十八歳、海へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "中上健次の短編小説集『十八歳、海へ』に収録された“隆男と美津子”を、大島渚監督作品や「青春の殺人者」の鬼才ライター・田村孟と新人・渡辺千明の共作で脚色。「八月の濡れた砂」で始まった1970年代型青春の苛立ちと倦怠を、藤田監督が自ら締めくくる。海での“死にっこゲーム”に取りつかれた予備校生男女の末路を鮮烈に描いた佳品。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le gai savoir", "たのしい知識", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "", "1969-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TROUBLE EVERY DAY", "ガーゴイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "個性派俳優V・ギャロが主演した猟奇的な愛の物語。新婚旅行で妻とともにパリを訪れた科学者のシェーン。しかしそれは、シェーンを蝕むある病をくい止めるための旅でもあった。かつて、同僚のレオと新薬の開発に携わった彼は自ら実験台になり、結果、性的興奮が異常に高まると、相手を噛み殺してしまう身体になってしまったのだ。事実を知らないシェーンの妻は夫の不可解な行動が理解できず、苦悩する。やがてレオの居所を見つけたシェーンは、そこで同じ病の末期症状を迎えたレオの妻コレと、血に染まり息絶えた美しい青年の姿を目にするが……。異形の愛が淡々と描かれ、主人公の苦悩が浮き彫りに。デザイナーのアニエスbが製作に全面協力している。", "2001-01-01", "2,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PIERSCIONEK Z ORLEM W KORONIE", "鷲の指輪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "A・ワイダが、長年夢みながら叶うことのなかったシナリオを、祖国ポーランドの共産主義体制崩壊に始まる東欧諸国の民主化により実現させた執念の一作。ワルシャワ蜂起に参加し敗れたマルチンは、恋人ヴィシカから愛の証しとして、ポーランド王国の紋章である王冠の付いた鷲の指輪をもらう。恋人と引き裂かれた彼は新しい祖国を築くために戦うが、時代の波にもまれ愛と信念を失っていく。共産主義者の手によって王冠を削り取られてしまった鷲の指輪がたどった運命は、当時のポーランドのように苦渋に満ちていた……。酒場でウォッカグラスに火をつけ、殺された同志の名前を呼びながら一つ一つ消していくという「灰とダイヤモンド」の有名なシーンが再現されている。", "1992-01-01", "42,3,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRAVOLTI DA UN INSOLITO DESTINONELL’AZZURRO MARE D’AGOSTO", "流されて…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "女流監督ウェルトミューラーの名を世界的に高めた問題作。ボートが遭難し、地中海に浮かぶ小さな無人島に流れ着いた上流階級のマダムとその使用人の男は……。二人が営む弱肉強食の原始的生活を通して、女と男の赤裸々な愛の姿を描いた話題作。", "1974-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAULINE A｀ LA PLAGE", "海辺のポーリーヌ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ノルマンディーの夏の海辺に避暑にやって来た15歳の少女ポーリーヌと美しい年上の従姉マリオンをめぐって恋のやりとりに花が咲く。ロメール監督の“喜劇とことわざ”シリーズ第３作。ウイットとユーモアにあふれた台詞の美しさが特色の一つ。DVDは「エリック・ロメール コレクション(4)」に収録。", "1982-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Filth and Wisdom", "ワンダーラスト", "", "", "http://image.eiga.k-img.com/images/movie/53931/315x210.jpg?3080", 84, "ポップ・ミュージック界の女王、マドンナの初監督作品。カリスマ的人気を誇るジプシーパンクバンド“ゴーゴル・ボルデロ”のユージン・ハッツが、ミュージシャンを夢見るウクライナ移民の青年を好演。バレリーナになることを夢見るストリッパーのホリー、アフリカ難民を救うことが夢の薬局店員ジュリエットら、ロンドンの片隅で夢を追い続ける若者の姿を描く。", "2009-01-17", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博徒無情", "博徒無情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "“無頼”シリーズに代表される日活ニュー・アクションの全盛期に製作されたアダ花ともいうべき任侠ものの１本。一応、主人公は渡だが、親分の仇を討つのに失敗してすぐ入獄してしまう。残された恋人の松原が苦労する部分が長く、ライバルの露口の好演も、ラストの渡の殴り込みと憤死も映画に活気を与えなかった。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Solo un padre", "ただ、ひとりの父親", "", "", "http://image.eiga.k-img.com/images/movie/55421/315x210.jpg?1610", 93, "30歳のカルロには生後10ヶ月になる娘のソフィアがいるが、両親に娘の世話を任せきりにしていた。しかし両親が長期休暇で家を留守にするため、カルロがソフィアの面倒を見ることになる。カルロは慣れない育児に戸惑うばかりだったが、ジョギング中に出会ったフランス人のカミーユと親しくなり、その出会いがカルロを少しずつ変えていく。イタリア映画祭2010にて上映。", "2010-04-28", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍びの者 新・霧隠才蔵", "忍びの者 新・霧隠才蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "大阪夏の陣で豊臣家が滅亡したあと、駿府に潜み、家康暗殺を狙う霧隠才蔵率いる伊賀忍者たちの活躍を描く。徳川方は風魔一族を警護にあて伊賀ものを次々と殺していく。才蔵は駿府城潜入に成功するが……。ラストの風魔大十郎と才蔵の一騎打ちが迫力十分。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOY SOLDIERS", "トイ・ソルジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「ビバリーヒルズ・コップ」などの脚本家、D・ペトリーJr.の監督デビュー作。良家の子息が通う全寮制の高校に、コロンビアの麻薬王の息子が乱入。彼は、生徒たちを人質に、麻薬王エンリケの釈放を要求した。そんな状況の中で、ビリーをはじめとする問題児５人は、犯人たちに対抗するが……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L.A.CRACK DOWN II", "復讐都市L.A.２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "異常な殺人事件が多発するロスの闇。またしても殺人の最中に被害者の発する断末魔のあえぎをテープに残すといった変質者による事件が起きた。女刑事カレンは、この犯人を捕らえるためにおとりとなるが、殺人鬼は彼女の親友と母親を殺害し挑戦してくる。タフでハードな女刑事をP・ディクソンが好演。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vera Drake", "ヴェラ・ドレイク", "", "", "http://image.eiga.k-img.com/images/movie/1731/315x210.jpg?1747", 125, "「秘密と嘘」のマイク・リー監督がまたもオスカー監督賞＆脚本賞にノミネート。タイトルロールを演じたイメルダ・スタウントンが同主演女優賞にノミネート、シカゴ批評家賞等数々の映画賞を受賞した。1950年のロンドン。家族を愛する献身的な主婦ヴェラは非合法と知りながら、望まない妊娠をした女性たちの堕胎の手伝いをしていたが、通報されて裁判にかけられることになってしまう。", "2005-07-09", "3,2,31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ROBE", "聖衣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "長年にわたり研究開発されたシネマスコープ形式の記念すべき第１回作品。聖書に基づいたロイド・C・ダグラスのベストセラー小説の映画化。ティベリウス皇帝治下のローマ。護民官ガリオは奴隷市でディミトリアスを買いとった。彼はキリスト教弾圧の急先峰であったが、キリストの処刑の際、聖衣に触れたことで神の愛に目覚める。やがて恋人とともに処刑されるまでをスペクタクルなシーンを交えながら描いていく。立体音響を効果的に演出した雷の響き、馬車の音も素晴らしい。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大岡政談 千鳥の印篭", "大岡政談 千鳥の印篭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "おなじみの大岡政談もので、いつもながら明快な裁きを下す越前だが、今回は苦境に立つ。同心・半九郎が身投げした娘を助けたことから、一個の印篭をめぐる争奪戦に巻き込まれる。実はその印篭は越前が昔、夫婦の約束をした女に与えたもの。それが表沙汰になれば、越前の失脚は避けられなかった……。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WITCHTRAP", "サイキックハウス 亡霊の館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "古い屋敷での怪死事件を調査するためにオカルト専門家を含む科学捜査チームが編成される。が、その屋敷で調査を進めるうち、科学者夫婦は惨殺され、美人霊媒師は悪霊に取り憑かれる。彼女を救うため、殺人課の刑事トニーは力を尽くすが……。タッシロ・バウアーによるモンスターの目新しさが目を引くSFXホラー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("White Squall", "白い嵐", "", "", "http://ecx.images-amazon.com/images/I/51VayU6eWjL._SL210_.jpg", 129, "　1960年、17歳のチャックは海洋学校オーシャン・アカデミーに入学。彼を含めた12人の訓練生はシェルダン船長が指揮する帆船アルバトロス号に乗り込み、バミューダ諸島からガラパゴス諸島へ航海に出る。その間、訓練生たちは数多の試練を乗り越え、航海術を学んでいく。ところが帰途で伝説の大嵐ホワイト・スコールに遭遇し……。大海原でのサバイバル、そして少年たちの成長と友情、結束を描いた青春ドラマ。実話の映画化。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOXES", "フォクシー・レディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "親なんてもうウンザリ！　ジニーをリーダーとする16歳の少女４人組は、麻薬、セックス、妊娠と、それぞれに危ない経験を重ねながら、少女と大人の間を揺れ動いていく……。CF出身のライン監督が、新鮮な映像と音楽で、ロサンゼルスの街を捉える。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新吾二十番勝負・第１部", "新吾二十番勝負・第１部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "“新吾十番勝負”の好評を受けて製作された“新吾二十番勝負”の第１部。将軍吉宗を父として生まれた葵新吾は、宿敵・武田一真を倒して天下第一の称を得る。そして老中・酒井讃岐守のはからいで彦根城中において、父子対面を果たさんとするが……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Libertine", "リバティーン", "", "", "http://image.eiga.k-img.com/images/movie/1111/315x210.jpg?3724", 110, "17世紀英国で国王に寵愛された実在の放蕩詩人ロチェスター伯爵の半生を映画化。本作の舞台版に主演したジョン・マルコビッチが映画化を企画、ジョニー・デップを主演に自身は国王役に回って製作も担当。ロチェスターが演技指導する女優役で「マイノリティ・リポート」のサマンサ・モートン、ロチェスターの妻役で「プライドと偏見」のロザムンド・パイクが共演。英国人監督ローレンス・ダンモアはCMを経て本作で映画デビュー。", "2006-04-08", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Aサインデイズ", "Aサインデイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "利根川裕の実話を基に書かれた小説『喜屋武マリーの青春』を原案とし、ベトナム戦争期の沖縄を舞台に、ロックバンドの若者たちの青春を描く。ハーフの少女・エリは人気ロックバンド“バスターズ”のボーカル・サチオに惹かれ、結婚する。結婚生活は苦しくケンカが絶えなかったが、エリはバスターズの一員としてステージで歌うことに生きがいを見出していく。エリが練習で“スージーQ”を嫌というほど繰り返し歌うシーンは迫力があり感動的だ。題名の“Aサイン”とは米軍を相手に風俗営業をすることを許可された店のこと。主題歌を主人公のモデルとなった喜屋武マリーが歌っている。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大曽根家の朝", "大曽根家の朝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "木下惠介監督の戦後第１作で、戦前プロレタリア演劇運動の中心的作家であった久坂栄二郎が、脚本を担当した反戦映画の力作。大曽根家は戦争によって息子たちが次々に出征し、軍人の叔父がわが者顔で家を占拠するが、終戦になり息子たちを失った母は、毅然として叔父の卑劣な態度をなじり、立ち退きを要求し……。戦後、占領軍指令部によって軍国主義の批判と民主主義の啓蒙というテーマが日本映画に課せられた。この作品は、そうした軍国主義批判の最初の力作として記憶に残る作品となった。木下惠介はこの作品の後、セキを切ったように傑作、話題作を次々に発表し、黒澤明と並ぶ戦後日本映画の巨匠となった。DVDは「木下惠介　DVD-BOX 第1集」に収録。", "1946-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUEIMADA", "ケマダの戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "カリブ海に浮かぶ島ケマダを舞台に、植民地化を進める大英帝国と、原住民たちの戦いをダイナミックにドキュメンタリー・タッチで描く。「アルジェの戦い」のポンテコルヴォ監督とM・ブランドが組んで作り上げた政治スペクタクル大作。", "1970-01-01", "4,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TYKHO MOON", "ティコ・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "フレンチ劇画（バンド・デシネ）の先駆者としてカルト的な人気を集める鬼才アーティスト、E・ビラルが、1989年の「バンカー・パレス・ホテル」以来、久々に発表した監督第２作。近未来の月面都市の支配者マクビーが、双子の弟が何者かに暗殺されたことをきっかけに、死の恐怖に取り憑かれる。血液が青くなる奇病に冒されたマクビーを救えるのは、行方不明中の謎の人物ティコ・ムーンだけだった。やがて街にさまよい込んできた記憶喪失の男アニクストをめぐり、娼婦、ジャーナリストら謎めいた人々の思惑が交錯していく……。精密な画風で知られるビラル特有のざらついた質感が、アナログ感覚でフィルムに転化されたSFハードボイルド。「バンカー・パレス・ホテル」のヒロイン、キャロル・ブーケとも共通する赤い髪をしたJ・デルピーの目覚ましい活躍ぶりからは、タフでクールな女性に対するビラルの賛美が読み取れる。", "1997-01-01", "2,7,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大橋下面／UNDER THE BRIDGE", "上海にかかる橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "文革が終わり農村から青年男女が戻ってきた頃の上海。ある橋のたもとに広がる下町で高志華は自転車屋を、泰楠は裁縫屋を営んでいた。高志華は次第に泰楠に惹かれていくが、泰楠はその愛に応えようとしない。というのも泰楠には下放中の男友だちとの間で子供をつくったという過去があり……。コン・シュエは本作で百花奨・金鶏奨最優秀主演女優賞を受賞。", "1983-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("熱砂の誓ひ", "熱砂の誓ひ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "長谷川一夫と李香蘭という美男美女の取り合わせ、１ヵ月以上の中国ロケ敢行など、当時の大作の趣き十分。ラブ・ロマンスと国策を巧みに噛み合わせた作品である。中国の北京から庫倫へ、一直線に大道路を通そうとする杉山一郎、憲治の兄弟の奮闘ぶりと、声楽の勉強をする中国の娘・李芳梅と憲治との恋模様が描かれていく。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last Samurai", "ラスト・サムライ", "", "", "http://image.eiga.k-img.com/images/movie/1491/315x210.jpg?4707", 154, "かつての南北戦争の英雄、オールグレン大尉は戦争の無意味さに疲弊し、今はアル中暮らし。そんな彼が、近代化を目指す日本の軍隊の教官として雇われて日本へ渡り、国を挙げての近代化の波の中でサムライの生き方を貫こうとする武将、勝元に出会う。 監督・共同脚本はデンゼル・ワシントンにオスカーをもたらした南北戦争映画「グローリー」のエドワード・ズウィック、共同脚本は「グラディエーター」のジョン・ローガン。", "2003-12-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("卍舞 大江戸浮世風呂譚", "卍舞 大江戸浮世風呂譚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "江戸時代のソープランドを舞台にした、甘美的で情緒たっぷりのセクシー・アクション絵巻。祝言の直前に犯されたあげく、最愛のいいなずけを殺された武家の娘お蝶。丹前風呂の湯女に身をやつしていた彼女が、客としてやって来た犯人、黒岩を発見し仲間とともに復讐を遂げる。勧善懲悪のストーリー、妖艶なシーン、痛快アクションが楽しませる。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fallen", "悪魔を憐れむ歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "殺人課の敏腕刑事ジョンは、自分が捕らえた連続殺人犯リースの処刑に立ち会う。しかし、その後リースの犯行と酷似した殺人事件が起きる。現場にはリースが死の間際に残した意味不明の呪文が残されていた。ジョンは解明を急ぐが、またも同じ手口の犯行が起きる。しかも、ジョンに不利な状況証拠が見つかり、彼自身に嫌疑がかかってしまう。独自に捜査を進める彼は、事件に悪霊が関わっていることを知り……。オスカー俳優D・ワシントン主演のホラー映画。人間同士の接触によって悪霊が人から人へと憑依していくというアイデアが秀逸。悪霊に乗り移られた人間が口ずさむローリング・ストーンズの『タイム・イズ・オン・マイ・サイド』が耳に残る。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'uomo che verra", "やがて来たる者へ", "", "", "http://image.eiga.k-img.com/images/movie/55431/315x210.jpg?2904", 117, "貧しい農家の一人娘である8歳のマルティナは、弟を生まれたばかりに亡くし、それ以来誰とも話さなくなる。1943年の冬、母親が再び妊娠し、マルティナは新しい家族の誕生を待ちわびる。しかし、村の周囲ではパルチザンとドイツ軍の戦いは激化していた。第2次世界大戦末期にイタリア・ボローニャ近郊のある山村で起こった虐殺事件を描き、2009年ローマ国際映画祭で審査員賞と観客賞を受賞した。日本ではイタリア映画祭2010にて「やがて来る者」のタイトルで上映。11年劇場公開。", "2011-10-22", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("落葉とくちづけ", "落葉とくちづけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "売れないGSのアイドル的存在、コマーシャル・タレントの田代信子は漫画家志望の好青年、ジュンと知り合う。やがて二人は愛し合い、東北の町へ旅に出るが……。斎藤耕一監督がサントラも手掛けた青春悲恋物語。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Devil Wears Prada", "プラダを着た悪魔", "", "", "http://image.eiga.k-img.com/images/movie/1051/315x210.jpg?2566", 110, "オシャレに無関心なジャーナリスト志望のアンドレアは、ニューヨークの一流ファッション誌編集部でカリスマ編集長ミランダのアシスタントとして働くことになる。一見、誰もが憧れる夢のような仕事、しかしそれはミランダの理不尽な要求に振り回される過酷なものだった。TVシリーズ「セックス・アンド・ザ・シティ」のデビッド・フランケルが監督、主人公アンドレアにアン・ハサウェイ、鬼上司ミランダにメリル・ストリープが扮する。", "2006-11-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRONOS", "クロノス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "メキシコ・アカデミー賞で９部門を独占し、世界各国の映画祭で注目された異色のヴァンパイア・ムービー。メキシコ・シティでアンティーク・ショップを営む老人が、永遠の生命を与える謎の生命体クロノスを手にしたため吸血鬼となり、クロノスを狙う大富豪の陰謀に巻き込まれる。メキシコ文化に根ざした怪奇世界を創造したG・デル・トロ監督は、本作をきっかけに次回作「ミミック」でハリウッド進出を果たした。ビデオ発売時タイトルは「クロノス　寄生吸血蟲」。", "1992-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREAKSHOW", "フリークショー 背すじも凍る４つの物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "犯人からの予告電話によって、劇場での大虐殺をスクープした女性レポーター、シャノン・ニコルズ。シャノンは帰路、奇妙な画廊“フリークショー”に立ち寄る。そこで彼女が見たものは、背すじも凍る４つの物語だった……。夢と現実が交錯するオムニバス・ホラー作品。第１話は、廃工場に紛れ込んだホームレスの男の悲劇。第２話は、女子大生寮にうごめくヴァンパイアの恐怖。第３話は、新種のドラッグで、生きながら死んだようになってしまった女の戦慄。第４話は、ゾンビに襲われる墓泥棒の惨劇を描いている。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STALKED", "欲望の足音", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "思い込みの愛情で相手をつけ狙い、ときに妄想がふくらんで異常な行動に出る“ストーカー”の恐怖を描くサイコ・スリラー。レストランを経営する未亡人ブルックが、資産家の青年ダリルと出会ったのはほんの偶然からだった。彼女の息子が崩れたコンテナの下敷きになりかけた時、助けてくれたのがダリルだったのだ。しかしお礼の気持ちでブルックが名刺を渡した時から、彼女の周りには不気味な出来事が起き始める。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINAL MISSION", "ファイナル・ミッション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "テスト飛行を行っていたウォーターズとアミーゴは、コンピュータのシステムに異常を感じ基地へと戻る。が、途中アミーゴの機が、崖に激突し爆破する。事故後も軍はテスト飛行を続け、パイロットには薬物を与えていった。このプログラムに疑問を感じたウォーターズは、単身捜査に乗り出した。「トップガン」の航空シーンを担当したヘス・カルバーグ社が、アメリカ空軍の協力を得て製作している。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("窓辺のほんきーとんく", "窓辺のほんきーとんく", "", "", "http://image.eiga.k-img.com/images/movie/53791/315x210.jpg?3668", 80, "会社が突然倒産して職を失った石岡晃のもとに、大学時代の映研の先輩である村崎が押しかけてくる。村崎の強引な熱意に負けて映画製作を手伝うことになった晃だが、ヒロインに抜擢された眞名水も家に転がりこんできて、奇妙な同居生活が始まる。次第に惹かれあっていく晃と名水だが……。監督は自主映画界出身でカルト的な人気を誇る「異形ノ恋」「泳げない女」の堀井彩。", "2008-09-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cyrano De Bergerac", "シラノ・ド・ベルジュラック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "　フランス映画史に残る巨額の製作費で映像化された、華麗なる時代絵巻。詩人にして剣豪、哲学者でもあるシラノは、大きな鼻にコンプレックスを抱いていることから、いとこのロクサーヌへの愛を隠し通している。彼女が思いを寄せる美青年クリスチャンとの仲を取り持つため、無骨な彼に恋文の代筆をかって出る。カンヌ国際映画祭やセザール賞、ゴールデン・グローブ賞をはじめ、世界各国で作品賞や主演男優賞を獲得している。", "1990-01-01", "2,30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOLT", "メタル・イーグル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ハーレー・ダビッドソンに乗った伝説の流れ者ボルトが、無法地帯と化した地方の町にやって来た。そこで彼はあるインディアン一家の世話になるが、彼らは鉱脈が流れる広大な土地を所有しており、それを狙う実力者メイトリックスから嫌がらせを受けていた。そして業を煮やしたメイトリックスは一家の長と少年を誘拐する……。過激なバイク・アクションが見もの。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("復讐の帝王", "復讐の帝王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "桑原譲太郎原作の同名小説を映画化したバイオレンス・アクション。優しい妻と可愛い娘、それに結婚間近の妹をいっぺんに事故でなくした四条はある日、その事故が殺人だったことを知らされる。妹の婚約者だった東海とともに復讐を果たすことを誓い、戦いのプロフェッショナルになるために特訓を受け始める。やがて、事件の裏には暴力団の犯罪計画があることが明らかになるのだった……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南京大屠殺／DON’T CRY NANKING", "南京1937", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1937年、日本軍が南京占領後に引き起こした“南京大虐殺”の事実をもとに作られた人間ドラマ。中国人医師のチョンと日本人の妻、理恵子を中心にして、３つの家族が体験する悲痛な出来事を通して、戦争の残虐さや人間の狂気、その中で人々がいかに生き、愛し合ったかを描いていく。監督は、ベルリン映画祭銀熊賞を受賞した「晩鐘」や「太陽山」などで知られるウー・ツゥニュウ。", "1995-01-01", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HE´LAS POUR MOI！", "ゴダールの決別", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ゴダールが、ギリシャ神話に材を採ったJ・ジロドーの戯曲『アンフィトリオン』を自由に翻案し、G・ドパルデューを主演に据えて撮った宗教的な色彩の濃い一編。スイス、レマン湖のほとりに暮らす、ごく普通の夫婦シモンとラシェル。しかし、シモンはある日、自らが神であると言い、ラシェルの肉体を強引に奪う……。物語の話者としてアブラム・クリムトという探偵を登場させ、５つの断章によって物語を重層的にしたゴダールは、音そのものをも多層化させる。まるで映像と音の一大ポリフォニーと呼び得るかのような空間を創出し、そこに神と人間との葛藤を描き出す。「新ドイツ零年」（1991）以降のゴダールの方向性が、ここに明らかとなっているかのようだ。", "1993-01-01", "2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちびっ子レミと名犬カピ", "ちびっ子レミと名犬カピ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "エクトル・マローの名作『家なき子』の主人公レミと犬のカピを中心に描く感動アニメ。旅芸人の一座に売り飛ばされたレミは、つらく厳しい毎日を送っている。ある夜、オオカミに襲われ一座の人気動物が殺され、老いた座長も牢に入れられてしまう事件が起きる。途方に暮れるレミだったが、ミリガン夫人という女性が現れ救ってくれた。ミリガンは行方不明の娘を探しているというが……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Grand Prix", "グラン・プリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 179, "　アメリカ人F1レーサーのピートはモナコで開催されたグランプリ・レースでライバルのサルティに敗退。イギリス人選手のスコットも大怪我を負う。各国を転戦するレーサーたちは常に危険と隣り合わせで、その結婚生活にも支障が生じていた。一方、ピートは敗退が原因でチームを追われることに。やがて彼は日本人・矢村のチームに加わり、勝ち進んでいくが……。国際的なキャストを揃え、大迫力の映像で描かれるカーレース映画。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIVE FOREVER", "リヴ・フォーエヴァー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ブラー、オアシスなどの人気バンドを輩出した1990年代のUKロック・シーンの大きなうねり、“ブリットポップ”を検証するドキュメンタリー。英国の若者を熱狂させ、政権交代にも一役買ったこの動きの隆盛から衰退までをたどる。オアシスのギャラガー兄弟ら、当事者の語る裏話が興味深い。全編に流れる当時のヒット曲がファンには懐かしく、嬉しいところ。1990年代に本国イギリスのみならず、日本やアメリカなど世界中を席巻したロック・ムーブメント“ブリットポップ”。ニルヴァーナなどアメリカ音楽への反発から生まれたそのブームは、ブレア首相の選挙戦にも影響を与えていく。", "2002-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("熱帯魚／TROPICAL FISH", "熱帯魚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "現代台湾の青春を、コミカルな味つけで描いた青春ファンタジー。大人になりきれない愛すべき誘拐犯一家と、迷える受験生の交流を、温かい視点で捉えていく。誘拐された少年ツーチャンは、ひょんなことから犯人アケンの家族と暮らすハメに。お人好しのアケンは受験生の彼を入試前に解放してあげようと、身代金の要求に奔走。ツーチャンは、この風変わりな監禁生活に安らぎを覚えていく。その頃、誘拐事件を報道するマスコミは彼を受験生のヒーローとして祭り上げ……。", "1995-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2011-2012 ドニゼッティ「アンナ・ボレーナ」", "METライブビューイング2011-2012 ドニゼッティ「アンナ・ボレーナ」", "", "", "http://image.eiga.k-img.com/images/movie/57161/315x210.jpg?3729", 232, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を映画館で上映する「METライブビューイング」2011～12年シーズンの1作目。19世紀イタリアの作曲家ガエターノ・ドニゼッティによる、16世紀イングランドの王妃アンナ・ボレーナ（アン・ブーリン）の悲劇を描くオペラ。国王エンリーコは、妃アンナの侍女ジョバンナを愛人にし、アンナを厄介払いしたがっていた。そんな折、アンナがかつての恋人ペルシに迫られたことから、エンリーコはそれを口実にアンナを幽閉してしまう。", "2011-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パンツの穴・花柄畑でインプット", "パンツの穴・花柄畑でインプット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "前作では、糞まみれのなかでの純情という幼い男気をうたい、本作ではそのエネルギーをエッチな方向に転換させている。悪ガキ３人組はなんとか、クラス全員の女の子のパンチラ写真を撮ろうと、あの手この手の作戦を展開するのだが……。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獣たちの熱い眠り", "獣たちの熱い眠り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "スキャンダル写真で恐喝されたプロテニス・プレーヤーが、自らの手で見えない組織に立ち向かっていくというバイオレンス・アクション。二枚目役から脱皮してハードなヒーローを演じる三浦友和を、村川透が手慣れたタッチで演出している。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タクミくんシリーズ　美貌のディテイル", "タクミくんシリーズ　美貌のディテイル", "", "", "http://image.eiga.k-img.com/images/movie/55121/315x210.jpg?4234", 82, "ごとうしのぶによる人気コミックシリーズ「タクミくんシリーズ」の映画化第3弾。主演は前作に引き続き、浜尾京介と渡辺大輔。全寮制男子校・祠堂学院の3年生へと進学したタクミとギイだが、ギイのそっ気ない態度でタクミの人間接触嫌悪症が再発してしまう。親友の赤池や片倉もギイの態度の変化を案じるが、2人の心はどんどんすれ違っていく。", "2010-01-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAR 80", "スター80", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "平凡なウエイトレスの人生が一枚のポートレートで変わっていく……。“プレイメイト殺人事件”として知られる実話を、芸能界内幕ものが得意のフォッシー監督が映画化。この映画のために整形までしたM・ヘミングウェイが熱演。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ソナチネ", "ソナチネ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "恩義のある親分から沖縄の友好関係にある中松組へ、加勢に行ってほしいと頼まれた北嶋組の幹部・村川。村川興業幹部の片桐、弟分のケンを連れて沖縄へと飛んだ彼だったが、東京からやって来た助っ人に、かえって島の空気はピリピリしだす。そしていきなり、銃撃戦が勃発。襲撃され、身の危険を感じた村川たちは、身を隠すとともに東京の親分に状況報告を頼むが、羽振りのいい村川たちを妬んだ親分のワナだったことが判明する……。表情一つ変えぬ殺傷場面、ただ純粋な暴力だけを映し撮ったリアルで極限的なシーンなど、「その男、凶暴につき」「３−４×10月」をさらに深化させた緊張感あふれる北野武の映像世界に驚かされる。そして沖縄の隠れ家での、ほんのつかの間の休息を楽しむかのような海辺での戯れの和やかな空気との、絶妙の対比。北野映画の集大成と言っても過言ではない一作だ。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEEP IN THE HEART", "ハンドガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "優しくナイーブな24歳の女教師が、男らしく有能な青年弁護士ラリーと出会う。好感を持ったのもつかの間、彼女はラリーに暴力でセックスを強制される。自らの手で復讐を誓った彼女は孤独な戦闘訓練を重ね、銃をマスターするが……。ラスト・シーンに監督ガーネットの暴力批判の意が込められた作品。", "1983-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("レフト・アローン", "レフト・アローン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "瀬々敬久作品の脚本家として知られる井土紀州が、自身が生まれた“'68年”をモチーフに、左翼運動の転回点となった歴史を再検証した意欲作。'01年の早稲田闘争にも参加した批評家、●秀実を媒介に、気鋭の論者たちを相手に激論を闘わせる様を奔放な映像文体で追う。新左翼運動の誕生、花田清輝と吉本隆明の論争、'68年の安保闘争に至る過程について、●秀実が西部邁、柄谷行人、松田政男らと語り明かす。", "2005-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("URGA", "ウルガ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "モンゴルの大草原で自然と一体化して暮らしている遊牧民一家、ゴンボとバグマ、そして子供たち。ある日、ゴンボは草原でロシア人トラック運転手のセルゲイと出会い、二人の間に奇妙な友情が芽生える。しかし、そのロシアの友人は、幸せな一家に小さな波紋を起こすのだった。大自然のスペクタクルの中で、人間の失われていく感情の豊かさを称えたミハルコフの愛情あふれる作品。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEPA SELA，LEPO GORE", "ボスニア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "ボスニア紛争中の事件を実話に基づいて描き、本国で記録的な大ヒットとなった戦争ドラマ。セルビアの一隊がムスリム軍の攻撃を受けて撤退。兵士ミランをはじめとする生き残った７人は、ムスリムに追われて廃墟のトンネルにたてこもる。食料も援軍もないままの緊張の中で、ミランは敵軍内に、親友の姿を発見するが……。悲劇的な側面よりもブラック・ユーモアを強調。", "1996-01-01", "44", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カマチョップ", "カマチョップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "冴えない幽霊の青年2人組の奮闘を描いたオフビートなヒューマン・ドラマ。草野球の最中に落雷に当たって死亡し、幽霊となったカマチとチョップ。彼らが居座る都会のホテルの一室に、博多からやって来た中年女性・岩瀬智恵子が宿泊する。智恵子の目的は、借金に苦しむニートの息子・圭一に、自身が老後のために貯めたお金を手渡すこと。死に神・恵比寿から指令を受け、智恵子と圭一の再会を阻もうとするカマチとチョップだったが……。", "2008-01-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hanged Man", "犯罪組織（シンジケート）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "　ギャングのハリーは殺された親友ホワイティの仇を討つため、ニューオリンズへ向かう。ホワイティを殺したのは彼らのボス、シーガーだった。ハリーはシーガーの妻であり、ホワイティと密通していたロイスに会い、シーガーが公金を横領していたという情報を掴む。ハリーはそれをネタにシーガーを強請ろうとするが、逆に殺し屋に命を狙われることに……。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.HOLLAND’S OPUS", "陽のあたる教室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "生活のために音楽教師となった男がやがて教師という職業に情熱を持つようになり、定年までを懸命に生きる姿を感動的に描いた人間ドラマ。1965年、生活のために高校の音楽教師となったホランドだったが、生徒たちは音楽に興味を示さず、オーケストラ演奏させても最悪だった。それでもホランドは音楽の魅力を語り、生徒たちもジャズやポピュラーを聞かせる彼の授業に興味を持つようになる。女子生徒にクラリネットの個人レッスンをしたり、単位の足りない生徒の面倒を見たりと、多忙な日々が続いた。そんな時、ホランドに待望の長男が誕生する。大喜びするホランドだったが、長男のコールは生まれつき耳が不自由だった。その現実から逃れるため、ホランドは学校の仕事に没頭する。そしてそのことで、コールは心に傷を負うようになってしまう……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あれが港の灯だ", "あれが港の灯だ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "日本と韓国の緊迫する国際関係を背景にして、身分を隠しながら生きなければならない在日韓国人の青年の姿を描き、民族問題を痛切に訴えた作品。国境を越えて操業したために韓国側の銃弾を浴び、長男を奪われた漁師一家は悲しみにくれる。それを見つめる在日韓国人、木村はやがて身分を暴かれ……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUNA SEA 3D in LOS ANGELES", "LUNA SEA 3D in LOS ANGELES", "", "", "http://image.eiga.k-img.com/images/movie/56131/315x210.jpg?4077", 104, "結成20周年を迎えた人気ロックバンド「LUNA SEA」が初めて行ったワールドツアー「LUNA SEA 20th ANNIVERSARY WORLD TOUR REBOOT ～to the New Moon～」から、10年12月4日のロサンゼルス公演の模様を3Dで映像化。ハリウッドの3D映像会社「3ality Digital」の協力のもと、最新の3D映像でライブの迫力をスクリーンに映し出す。", "2011-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダムライフ", "ダムライフ", "", "", "http://image.eiga.k-img.com/images/movie/57351/315x210.jpg?1802", 84, "", "2011-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'immortel", "バレッツ", "", "", "http://image.eiga.k-img.com/images/movie/55941/315x210.jpg?2199", 115, "「レオン」のジャン・レノが、愛する家族を守るために戦う男を熱演するバイオレンス・アクション。かつてマルセイユを支配するマフィアのボスだったシャルリは、今では家族とともに穏やかな生活をおくっていた。ところがある日、何者かによって銃撃され、全身に22発もの銃弾を撃ち込まれてしまう。奇跡的に命を取り留めたシャルリは、仲間たちの協力により、古くからの親友・ザッキアが犯人だと突き止めるが……。", "2011-02-19", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天国にいちばん近い島", "天国にいちばん近い島", "", "", "http://ecx.images-amazon.com/images/I/515eTP3UxsL._SL210_.jpg", 102, "ニュー・カレドニア諸島のどこかにある、亡き父が５歳の時に語り聞かせてくれた“天国にいちばん近い島”を探すため、ツアーに参加した女子高校生を中心に描いた“大林調”メルヘン。原田知世の主演３作目で、ニュー・カレドニアの風景が美しい。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KISS OF THE SPIDER WOMAN", "蜘蛛女のキス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "南米のある国の刑務所。ベッドが二つに便器とコップという、殺風景で陰湿な監房に、二人の男が収容されている。一人は、反体制運動グループの闘士として投獄された政治犯バレンティン。もう一人は、少年を犯して捕らえられたホモセクシャルのモリーナ。同じ房に閉じこめられながら、二人はまったく別の世界に生きている。現実しか見つめないバレンティンのかたわらで、いつか観た映画の話を始めるモリーナ。耳を塞ぐバレンティンも、モリーナの優しさに触れるうち、いつしか反発を超えた、ある感情の芽ばえに気付いていく……。原作はアルゼンチンの作家プイグの同名小説。すべて会話形式で構成されたこの小説は、誰が話しているのか判別しがたいようになっているため、映画化には困難が伴った。モリーナ役には当初、バート・ランカスターに白羽の矢がたったが、病気のため急きょハートが起用され、結果的にはこのキャスティングが映画の成功をもたらした。", "1985-01-01", "1,17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ce N'est Qu'un Debut", "ちいさな哲学者たち", "", "", "http://image.eiga.k-img.com/images/movie/55741/315x210.jpg?2979", 97, "フランスの幼稚園で世界で初めて取り入れられた、子どもが行う哲学の授業に2年間密着し、成長を見守るドキュメンタリー。仏セーヌ地方にあるジャック・プレベール幼稚園では、3歳～5歳の子どもたちが哲学を学ぶという画期的な取り組みが行われている。幼児クラスを受け持つパスカリーヌ先生は月に数回、子どもたちを集め、男女関係や貧富の差、人種問題などさまざまなテーマについて議論。子どもたちはそれぞれ自分の言葉で語り合うことで考える力を身につけ、大人たちもまた、この取り組みに試行錯誤しながらともに成長していく。", "2011-07-09", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("英雄・鄭成功傳", "国姓爺合戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "17世紀半ばの中国。明から清へと変わろうとする動乱の時代に中国のみならず、アジア、ヨーロッパの列強にまでもその名を轟かせた男がいた。その男とは中国人を父に、日本人女性を母に持ち、長崎で生まれた鄭成功。彼は存亡の危機にさらされた明を救った知られざる英雄だった。中国映画の第五世代を代表するウー監督が壮大なスケールで描く劇的な人間ドラマ。日活創立90周年記念、日中国交正常化30周年記念作品。ビデオ、DVDタイトルは「英雄　国姓爺合戦」。", "2001-01-01", "8,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Femme Fatale", "ファム・ファタール", "", "", "http://image.eiga.k-img.com/images/movie/1061/315x210.jpg?4310", 115, "「殺しのドレス」「ボディ・ダブル」などサスペンス映画にも名作を残すブライアン・デ・パルマ監督が、フィルム・ノワールの必須要素、ファム・ファタール＝宿命の女をタイトルに冠して描く。主演はモデル出身、「X-メン」シリーズのミスティーク役でも魅惑的な肢体を披露するレベッカ・ローミン＝ステイモス。彼女に翻弄されるカメラマン役はアントニオ・バンデラス。撮影は「プロバンスの恋」他でセザール賞受賞のティエリー・アルボガスト。", "2002-08-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Catacombs", "カタコンベ", "", "", "http://image.eiga.k-img.com/images/movie/34231/315x210.jpg?1594", 94, "「SAW」シリーズのプロデュース陣が放つ衝撃のホラー。パリに実在する巨大地下墓地“カタコンベ”に迷い込んだアメリカ人女性の恐怖体験を描く。内気な性格のビクトリアは、ソルボンヌ大学に留学中の姉に誘われてパリを訪れる。彼女は姉に連れられ、700万体もの遺骨が眠っているという地下墓地で開かれるアンダーグラウンド・パーティーに参加することに。しかし、パーティーの途中でひとり会場の外へ迷い込んでしまい……。", "2007-10-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新方世玉／LAST SHAOLIN ： THE YOUNG HERO OF SHAOLIN", "ザ・ラストショーリン　最後の少林寺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "超難技“二指法”の創始者として、また秘伝“回転空中蹴り”の完成者として中国武術史に名を残す、南派少林寺三雄の一角、方世玉の波乱に富んだ半生を描く。全中国格闘技チャンピオンのツィー・ポーワァが主演するほか、少林武術の名手が結集、少林寺のありとあらゆる秘技秘術が明かされる。ビデオ、DVDタイトルは「最後の少林寺」。", "1986-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("bird call〈バードコール〉", "bird call〈バードコール〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "オムニバス映画「Jam Films」の携帯版として始まった「min.Jam」シリーズの一編。彼氏のサトナカと友人のミユキを事故で一度に失い、喪失感に襲われる女子高生・園子の心情を丁寧に映し出す。ヒロインを演じた鈴木えみの透明感のあるたたずまいがイイ。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("5 Days of War", "5デイズ", "", "", "http://image.eiga.k-img.com/images/movie/56801/315x210.jpg?4479", 113, "2008年8月、民族紛争に揺れるグルジアにロシアが軍事介入したことで始まった5日間戦争（8月戦争）を描く社会派ドラマ。07年、イラクで恋人を失ったジャーナリストのアンダースは1年後、再び戦場取材に戻ることを決意。グルジア共和国の首都トビリシに降り立つ。ロシア軍の空爆に巻き込まれたアンダースは、非武装地帯への空爆を告発しようとするも、世界は北京で開催中のオリンピックに夢中で、この事態を伝えようとするメディアはおらず……。監督は「ダイ・ハード2」「クリフハンガー」のレニー・ハーリン。", "2011-10-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("XXX", "トリプルX", "", "", "http://image.eiga.k-img.com/images/movie/1221/315x210.jpg?2799", 124, "アメリカ国家安全保障局が東欧のテロ対策のため、新たに雇ったエージェントは、ストリートの悪ガキ、ザンダー。彼はスケボーやスノボ、マウンテンバイクなどエクストリーム・スポーツの達人。そのワザと反逆心を武器に、悪の陰謀に立ち向かう。雪山の雪崩とスノボで対決するなど過激アクションが続出。イタリアン・ホラーの名匠ダリオ・アルジェントの娘、アーシアがセクシーなヒロイン役。", "2002-10-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JE NE SUIS PAS LA POUR ETRE AIME", "愛されるために、ここにいる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "タンゴを通して巡り合った、愛に不器用な中年男性と幸福なはずの結婚に不安を抱く若い女性。繊細な二人の心が情熱的なタンゴのリズムによって解き放たれていく。じんわりと心に染みわたってくるような静かな高揚感を味わえるドラマだ。仕事も家族関係も良くない中年のジャン＝クロードは人生に疲れていた。ある日タンゴ教室に通いはじめ、結婚を控えた若い女性と出会う。タンゴを踊るうちに二人は親密になっていく。", "2005-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FESTEN", "セレブレーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "T・ヴィンターベア監督が、ラース・フォン・トリアー、クリスチャン・レブリン、ソーレン・クラウ・ヤコブセンというデンマーク映画界の先輩たちとともに結成した監督集団“ドグマ95”の第１回作品。鉄鋼王ヘルゲの豪邸で彼の還暦パーティーが行われることになり、親族や友人が大勢集まってくる。ところがスピーチに立った長男クリスチャンが、ヘルゲとの近親相姦の過去をぶちまけたことから邸内は騒然としていく。手持ちカメラを使う、撮影はすべてロケで行う、人工的な照明は禁止……といった映画制作の“十戒”を定めた“ドグマ95”のルールにのっとり、密室的シチュエーションでの凄まじい群像劇が展開。臨場感、現実感を重んじる一方で、それに反する“幽霊”という超自然的な存在を描くことにチャレンジするなど、一見の価値ある野心作に仕上がっている。", "1998-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ANGE NOIR", "ランジュ・ノワール 甘い媚薬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "1960年代フレンチポップス界のアイドル、S・ヴァルタンが、10年ぶりに映画出演したエロティック・サスペンス。上流階級の美貌の貴婦人として名高いステファーヌ・フーブリエ夫人が、暗黒街の大物ワッデック・アスラニアンを射殺するという事件がパリ郊外で起こった。夫のフーブリエ判事は後輩のポール・ドゥロルム弁護士に弁護を依頼。だが、ポールが彼女を調べていくと、ステファーヌはワッデックの愛人で二人は共謀して悪事をはたらいていたことが判明する。ステファーヌはポールに事実を告白し、二人は愛するようになる。しかし、ワッデックの女が出現し、事件は意外な方向に展開するのだった……。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stereo", "ステレオ／均衡の遺失", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "　近未来、性愛を研究する施設でテレパシーの実験が行なわれる。奇妙な実験とテレパシー交信はやがて被験者に影響を及ぼし、自殺者まで出すことになるが……。のちの「スキャナーズ」にも通ずるテーマを扱ったクローネンバーグ監督の中編作品。", "1969-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャングル大帝 劇場版", "ジャングル大帝 劇場版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "手塚治虫の初期の作品であり、1965年にはTVアニメ・シリーズとして放映された大河コミックの劇場版アニメ。雄大に広がるサバンナ、莫大なエネルギーを秘めた月光石を求める人間ハム・エッグたちは現地調査員ヒゲオヤジの反対を聞かず、神秘の山、ムーン山を捜して、傍若無人に森を切り開いていく。ジャングルの王者レオは反撃に出るが、銃撃され、やむなく退却することに。やがて激しいスコールが止んだとき、ムーン山が姿を現し……。ムーン山の頂上を目指すハム・エッグたちと、ヒゲオヤジ、そしてレオの戦いと冒険。さらには、自らの意志で人間の世界に旅立ったレオの息子ルネの体験が綴られていく。音楽はTVシリーズ第１作から担当している冨田勲。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("2002年の夏休み・ドキュメント沙羅双樹", "2002年の夏休み・ドキュメント沙羅双樹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "河瀬直美の監督作「沙羅双樹」のメイキング・ドキュメンタリー。「あんにょんキムチ」が国内外で絶賛された松江哲郎が、主役に抜擢された福永幸平と兵頭祐香の両新人を追いながら、完成までの過程をつぶさに見つめていく。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　BUTCHER", "肉屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "指揮者のダニエルを夫に持ち、何不自由ない日々を送るアリーナだったが、満たされないものを感じていた。そしてダニエルの演奏旅行中、体調不良で倒れた彼女は医者から肉をもっと食べるようにと忠告される。肉屋を訪れたアリーナは店主のブルーノに心奪われ……。抑えきれない性欲に取り憑かれた人妻の姿を大胆に描写したイタリア製の官能ドラマ。", "1998-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOG FOOD", "DOG FOOD", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 47, "俳優、田辺誠一がデジタルカメラで撮り上げた初監督作品。古びた日本家屋を舞台に、ある青年の崩壊していく日常を繊細なタッチで切り取っている。ベトナムへの転勤を目前に控えた男が、浮気を知った妻に見捨てられた。そこに追い打ちをかけるような愛犬の突然死。やるせない孤独感に襲われ、思わずドッグフードを口にした男の脳裏に、様々な思いが去来する。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どら平太", "どら平太", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "黒澤明、木下惠介、市川崑、小林正樹からなる“四騎の会”が残した脚本を映像化。市川崑が監督を務め、痛快な時代劇に仕上げている。原作は勝新太郎の主演で一度映画化されている、山本周五郎の『町奉行日記』。とある藩の政治腐敗を解決すべく、町奉行として江戸から招かれた遊び人の武士、小平太。さっそく彼は“壕外”と呼ばれる犯罪地帯の浄化に着手。壕外を牛耳る３親分は、藩の役人と癒着して莫大な利益を得ていた。一計を案じた小平太は、ひょうひょうと振る舞いつつも３親分と悪徳役人を追いつめていく……。酒とバクチ、女遊びが大好きで悪名をはせているが、じつは正義感が強く、文武に長ける主人公。そんな豪放なキャラクターが魅力的。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("[牛古]嶺街少年殺人事件", "■嶺街（クーリンチェ）少年殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 188, "1961年夏。14歳の少年が同い年のガールフレンドを殺害するという悲劇が起こった。この台湾初の未成年殺人事件はなぜ起きたのか。不良少年同士の抗争、プレスリーに憧れる少年の夢、大陸に帰りたいと願う少年の親世代の焦りと不安を描きながら、当時の台湾の社会的・精神的背景を浮き彫りにしていく。出口を失い迷走する青春群像を力強い映像で見せる入魂の一作。1991年の東京国際映画祭で絶賛され、審査員特別賞ほかを受賞したエドワード・ヤンの日本初公開作。主人公を演じるのは、当時まったくの素人だったチャン・チェン。", "1992-04-30", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SECRETARY", "セクレタリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "風変わりなインテリ弁護士の秘書教育によって変貌していくオクテな女の子の奮闘を描いたラブ・ストーリー。若手注目株M・ギレンホールの体を張った大胆演技にも注目だ。内向的で自傷癖がある女の子リーが、グレイ氏の弁護士事務所の秘書になる。やがてリーは神経質なグレイ氏から一風変わった秘書教育を受ける……。ビデオタイトルは「秘書　セクレタリー」。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クヒオ大佐", "クヒオ大佐", "", "", "http://image.eiga.k-img.com/images/movie/54521/315x210.jpg?3943", 103, "自らをジョナサン・エリザベス・クヒオ大佐と名乗った、実在の日本人結婚詐欺師を堺雅人が怪演するコメディ。共演に松雪泰子、満島ひかり、中村優子ら。監督は「腑抜けども、悲しみの愛を見せろ」の吉田大八。片言の日本語と整形した高い鼻でアメリカ人になりすまし、数々の女性たちを騙してきたクヒオ大佐は、新たなターゲットとして博物館学芸員の春（満島）に目を付け接近するが……。", "2009-10-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラバーズ・ラヴァー", "ラバーズ・ラヴァー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "人間の超能力を引き出す、極秘の人体実験を行っていたマッドサイエンティスト４人。彼らのもとに、スポンサーから研究中止を通達する秘書がやってきた。逆上した科学者たちは、彼女を機械に縛りつけて……。極限状況を絶叫と轟音の中に写し出すエキセントリックな１本。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BURNT OFFERINGS", "家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "高校教師一家が、夏休みに格安の貸別荘で休暇を過ごすことになった。条件は家主の老母に毎日食事を作ることであったが、別荘の二階に閉じこもった老女は顔を見せようとはしない。ある日、老女のオルゴールをふとしたはずみで妻が開けた時から、怪現象が家族を襲い始める。豪華キャストのオカルト映画。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE VOYAGE DANS LA LUNE", "月世界旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 9, "トリック撮影を初めて考案したG・メリエスの代表作。月世界へと、大砲に乗ってやって来た探険隊一行。そこで様々なことが起こり……。ストーリーそのものは他愛ないが、今見ても新鮮なトリック撮影にこそ、その魅力はある。たとえば、撮影中にカメラを一旦ストップさせ、セットおよび人物を一部改変して撮影を続けたり（登場人物がいきなり消えたりするシーンが有名)、あるいは多重露光など。本作にはこうしたトリック撮影が様々に駆使されており、リュミエール兄弟が発明したシネマトグラフを見世物として成功させた興行師メリエスの、面目躍如たる作品でもある。なお、現存するフィルムは10分程度のものながら、製作当時はおよそ14分、30シーンからなっていた。ビデオ「フランス映画の誕生」に収録されている。", "1902-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOCAL HERO", "ローカル・ヒーロー／夢に生きた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "アメリカ最大の石油会社のエリート社員が主人公。ある日彼に、コンビナート建設用地買収のため、スコットランドの田舎町に飛ぶように命令が下る。自然保護を盾に強硬な反対に遭うと思いきや、地元住民は一獲千金を夢見て大のり気。最後の交渉は海岸のボロ家に住む老人ただ一人となった。交渉が難航するのを見かねて、大社長が現地へ乗り込んでくるのだが……。ちょっとズレたギャグでニヤッとさせ、ヒューマンなユーモアで心を和ませてくれるフォーサイスの演出は見事。ダイアー・ストレイツのギタリスト、ノップラーの主題曲が心に残る傑作。", "1983-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Atonement", "つぐない", "", "", "http://image.eiga.k-img.com/images/movie/53171/315x210.jpg?2009", 123, "1935年イギリス。ある夏の日、タリス家の末娘ブライオニーは、姉セシーリアと使用人の息子ロビー・ターナーの些細ないさかいを目撃し……。ひとりの無垢な少女の嘘によって人生を狂わされてしまった一組のカップルの運命を描く、現代英国文学界を代表するイアン・マキューアンによる傑作小説「贖罪」（新潮社刊）を、「プライドと偏見」のジョー・ライト監督＆キーラ・ナイトレイ主演で映画化。共演にジェームズ・マカボイ、ロモーラ・ガライ、バネッサ・レッドグレイブら。", "2008-04-12", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最後一個冬日／THE LAST DAY OF WINTER", "最後の冬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「晩鐘」で1989年度ベルリン映画祭銀熊賞に輝いたウー・ツゥニュウの第４作。ある冬の終わりの日、ゴビ砂漠にある犯罪者労働改造農場に向かう途中で出会った申絲、大路、園園の３人。彼らはそれぞれ、収容されている自分の兄弟と面会するために来ていた。農場に到着し、おのおの面会をすませた３人は……。ウー・ツゥニュウ作品ではおなじみのタオ・ツォルーが大路役で出演。", "1986-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高校生ブルース", "高校生ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "大映が渥美マリ以来の大型新人として売り出した当時15歳の関根恵子の主演第１作。ほんの好奇心から肉体関係を持った高校生の男女が、妊娠という現実に狼狽する姿を描く。堕胎のため、薄暗い体育館で少年が少女の下腹部を踏みつけるシーンは鮮烈。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画は生きものの記録である　土本典昭の仕事", "映画は生きものの記録である　土本典昭の仕事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「不知火海」「水俣　患者さんとその世界」など、長年に渡って水俣病問題を追い続けてきた土本典昭監督の軌跡を辿るドキュメンタリー。1965年以来、17本にも及ぶ水俣関連作品を手掛けた土本監督。水俣病の公式確認から50年を迎えた2006年、久々に訪れた水俣の海や自宅で自身の作品を振り返り、水俣への思いを語る。メガホンを取るのは「ぼくらはもう帰れない」がベルリン映画祭で上映され注目を集めた藤原敏史。", "2007-06-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("瞬　またたき", "瞬　またたき", "", "", "http://image.eiga.k-img.com/images/movie/55351/315x210.jpg?4152", 0, "北川景子と岡田将生が恋人同士を演じるラブストーリー。河原れんの小説を、「がんばっていきまっしょい」の磯村一路が映画化。美大生の淳一と泉美は、幸せな毎日を送るカップル。ある日、バイクで花見に出かけた帰り道でとトラックとの衝突事故に遭ってしまう。泉美は奇跡的に生き残るが、最愛の恋人とともに事故の記憶をも失ってしまう。苦しみと戦いながらも「最後の真実」を求める泉美が目の当たりにしたのは、極限状態の中で淳一の本能的な愛が生んだ奇跡だった。", "2010-06-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GREAT　EXPECTATIONS", "大いなる遺産", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "C・ディケンズの同名小説の舞台を現代に移し変えて、ドラマティックな愛の物語としてリメイク。フロリダの小さな町。10歳の少年フィンは、脱獄犯に出会い、彼に食べ物を与え逃亡の手助けをする。やがて、フィンは金持ちの老女に気に入られ、彼女の姪エステラの遊び相手に雇われる。ともに成長し、フィンはエステラの虜になっていくが、彼女は突然にヨーロッパヘ。７年後、フィンは、ナゾの人物の計らいでニューヨークへ渡って絵の勉強をするようになる。そこで偶然エステラと再会、再び愛が再燃したフィンは、アーティストとして成功を収めていく。一方、ときに挑発的な態度をとるエステラにほんろうされ続け、彼女の真意を確かめられないでいるフィンは、やがて驚くべき真実を知る……。A・キュアロンが、衣装や美術などすべてグリーンで統一した幻想的な雰囲気の中に、主人公が体験する出会いと別れ、運命の皮肉を描き出す。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES GRANGES BRULE´ES", "燃えつきた納屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "フランスの田舎オートドーフ。この村に“燃えつきた納屋”という変わった名の農家があった。ある雪の日、この農家の近くで殺人事件が起き、農家の家主ローズに嫌疑がかけられる。一家を守ろうとするローズは、新進気鋭の判事と対決する……。A・ドロンとS・シニョレの演技のぶつかり合いが見ものの心理劇。", "1973-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LOSERS", "殴り込みライダー部隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "革ジャンに長髪姿のならず者たちが、日本製バイクを駆って大活躍する戦争アクション。ベトナムの戦火を通り抜けて、一台のトラックが米軍戦線キャンプへたどり着く。降りて来たのは革ジャン姿の男たち。政府要人救出を命じられていた彼らだが、裏には罠が……。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Capote", "カポーティ", "", "", "http://image.eiga.k-img.com/images/movie/1321/315x210.jpg?2102", 114, "05年度アカデミー賞で作品賞、監督賞、主演女優賞、助演女優賞、脚色賞の5部門にノミネートされ、カポーティ本人に生き写しの演技を披露したフィリップ・シーモア・ホフマンが主演男優賞を受賞した話題のドラマ。1959年11月15日、カンザス州ホルカムで農家の一家が惨殺される事件が発生。「ティファニーで朝食を」で名声を高めた作家トルーマン・カポーティは、この事件に興味を覚え、小説「冷血」の執筆を決意し、現地へ飛び、事件の容疑者ペリーと面会する……。", "2006-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小林一茶", "小林一茶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 27, "タイトル通り小林一茶をテーマに、彼の句を配して構成された文化映画。“信濃では月とほとけとおらがそば”“みほとけは寝ておはしても花と銭”などの句を引き合いに出しつつ、それらの句をもとに鋭い社会批判的映像を展開した、亀井文夫ならではの作品。本作の直後、彼は治安維持法によって逮捕、投獄された。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LORD OF THE FLIES", "蝿の王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "飛行機事故で無人島に流れ着いた24人の子供たち。リーダーのラルフは救助が来ることを信じ、みんなで決めたルールに従った生活を送ろうとするが、一生ここから出られないと開き直ったジャックは武器を持ち、狩猟隊のボスとなってラルフと対決、少年たちは二派に分裂する……。ジャマイカの美しい自然が目にしみるスリルに満ちた冒険物語。", "1990-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hesher", "メタルヘッド", "", "", "http://image.eiga.k-img.com/images/movie/56361/315x210.jpg?4481", 106, "自動車事故で母を失った13歳の少年TJと、TJの父で妻の死から立ち直れないポール、人生に意味を見出せずにいる女性ニコールらの前に、長髪に半裸のナゾの男ヘッシャーが現れる。大音量でヘビーメタルを流し、粗野な言動でさまざまなトラブルを起こすヘッシャーだったが……。心に傷を負った人々を、ヘビーメタルの音楽とワイルドな行動で再生させていくナゾの男ヘッシャーの姿を描く。CMやMTVの分野で活躍するスペンサー・サッサーの初監督作。ジョセフ・ゴードン＝レビット、ナタリー・ポートマンらが出演。", "2011-06-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("亡国のイージス", "亡国のイージス", "", "", "http://image.eiga.k-img.com/images/movie/1241/315x210.jpg?4644", 127, "海上自衛隊のイージス護衛艦いそかぜの副艦長・宮津が、某国のスパイと共謀して艦を乗っ取り、最新兵器で首都東京を狙う。彼の行動の理由は何なのか？　東京壊滅までに残された時間は10時間。この事態を解決するべく先任伍長・仙石は、1人でいそかぜの奪取に向かう。監督は「顔」「KT」の阪本順治。原作は「ローレライ」「戦国自衛隊1549」の福井晴敏。真田広之、寺尾聰、佐藤浩市、中井貴一らベテラン俳優4人が競演。", "2005-07-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EDDIE MURPHY RAW", "エディ・マーフィ／ロウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "E・マーフィが、スクリーンの上とは一味違ったステージ中でのパフォーマンスを十分に見せつけてくれるライブ・ムービー。早口、毒舌、下ネタとジョークの洪水が、ダンディーな装いから速射砲のように飛び出してくる。ファン必見の一作だ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("七屍金／THE LEGEND OF THE ７ GOLDEN VAMPIRES", "ドラゴンvs7人の吸血鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "イギリス・ホラー映画の名門、ハマー・プロと香港ショー・ブラザースが組んだ、B級路線の国際合作。ドラキュラと７人の吸血鬼を相手に、トランシルヴァニア吸血学の権威ヴァン・ヘルシング博士と空手の達人シー・チンが勝負に挑む。博士役はもちろんP・カッシング。香港武術映画の第一人者D・チャンが空手の達人役で出演。", "1974-01-01", "3,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("O Lucky Man!", "オー！ラッキーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 167, "「時計じかけのオレンジ」で世界的に注目を浴びたマルコム・マクダウェル主演の異色ヒューマンドラマ。「ifもしも……」に続いてマクダウェルとリンゼイ・アンダーソン監督のコンビが放つ“ミック・トラビス3部作”の第2作。野心家の青年ミックは、幾多の困難を乗り越えついに大富豪の右腕にまでのぼりつめるが、ある事件をきっかけにどん底へと叩き落されてしまう。それでも彼はあきらめず、再び這い上がろうと奮闘するが……。", "1973-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cristovao Colombo O ENnigma", "コロンブス　永遠の海", "", "", "http://image.eiga.k-img.com/images/movie/55181/315x210.jpg?4157", 75, "ポルトガルの巨匠マノエル・デ・オリベイラが、「新大陸の発見で知られる冒険家クリストファー・コロンブスはポルトガル人だった」というマヌエル・ルシアーノ・シルバによる新説に触発され、コロンブス生誕の謎を追った研究者とその妻の半世紀にも渡る旅を描いたドラマ。オリベイラ自身が、妻のマリア・イザベル・デ・オリベイラと長年連れ添った夫婦を演じる。", "2010-05-01", "25,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KATIA ISMAI¨LOVA", "恋愛小説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "タイピストのカチアは、著名な作家である義母イリナの書く原稿を打つという単調な日々を送っていた。ある夏、避暑地で別荘の管理人セルゲイと出会い、官能的で危険な恋に落ちる。イリナにその決定的瞬間を目撃されるが、イリナは心臓発作で倒れてしまう。遺作となった恋愛小説の悲劇的な結末をハッピーエンドに書き変えていくカチア。だが、現実は彼女の思い通りにはいかなかった。彼女は最後の行動によって、自らの“恋愛小説”を完成させようとするのだが……。ロシアの古典小説を下敷きに“小説”と“パズル”という二つの要素を巧みに絡め、フィルム・ノワールの作風を踏襲した新鮮なラブ・サスペンス。", "1993-01-01", "24,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あの橋の畔で　第三部", "あの橋の畔で　第三部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "松竹大船調メロドラマのシリーズ第３部では、東南アジア・ロケを敢行。さらに日本に戻っては東京から能登へと舞台が広がっていく。仕事のためカンボジアに渡った光晴。現地プノンペンである女性に慕われるが、元恋人の葉子のことが忘れられない。そんな時、葉子が現れ二人の仲を疑うが、その後再会を果たすことに。だが、葉子を狙う雑誌記者が捏造記事を書いて、光晴を窮地へと追い込んでいく。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK ROBE", "ブラック・ローブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1634年フランス植民地として毛皮交易の開発が進む北米ケベックでは、同時にカトリック教会の中心地として、先住民族のインディアンたちへの布教が進められていた。身にまとっている黒い僧衣から、インディアンたちに“ブラック・ローブ”と呼ばれている若き伝道師ラフォーグは、行方を失った同志たちの捜索をするという任務を受ける。それは厳寒期の過酷な大自然に身をさらし、狂暴な先住民族を避け2400キロもの距離をカヌーで上っていくという過酷な旅だった……。「ダブル・ジョパディー」のB・ベルスフォード監督が、17世紀のカナダを舞台に描いたネイチャー・ロマン大作。カナダ・アカデミー賞６部門を受賞。", "1991-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crime in The Streets", "暴力の季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "　ニューヨークの貧民街に母親そして弟と暮らすフランキーはストリート・ギャングのリーダーだった。ギャング同士の争いも絶えなかったが、そんな中、仲間のレニーが警察に逮捕されてしまう。フランキーと仲間は警察に通報したマカリスターを恨み、彼を殺そうとするが……。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使の卵", "天使の卵", "", "", "http://image.eiga.k-img.com/images/movie/33701/315x210.jpg?4691", 114, "直木賞作家・村山由佳のベストセラー小説を、市原隼人、小西真奈美 、沢尻エリカら人気若手俳優をキャストに迎えて映画化。19歳の歩太には夏姫という恋人がいるが、ある日電車で出会った女性にひと目惚れしてしまう。数日後、父親が入院している精神病院でその女性・春妃と再会した歩太は、彼女が父親の新しい主治医で、夏姫の姉であることを知る。やがて歩太と春妃は深く愛し合うようになるが、2人の前には思わぬ運命が待ち受けていた……。", "2006-10-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOOTLOOSE", "フットルース", "", "", "http://ecx.images-amazon.com/images/I/51nq4zEcwqL._SL210_.jpg", 107, "ロックもダンス・パーティーも禁じられているアメリカ中西部の小さな町に、都会から転校してきた青年が、自由を取り返すべく仲間たちを集めて大人への挑戦を開始する。ベーコンの若々しい演技が、この青春ドラマにすがすがしさを与えている。ケニー・ロギンスの主題歌も大ヒット。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHERRY 2000", "チェリー2000", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "2017年の暴力が横行する近未来社会を舞台に、セックス・アンドロイドで欲求を満たす男が、愛を求めて過酷な旅を続けるうちに真実の愛をつかむという、SFアドベンチャー・ロマン。「ワーキング・ガール」で女優として大きく飛躍したM・グリフィスのセクシーな魅力が見もの。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE IN THE FACE", "ブルー・イン・ザ・フェイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "1995年に公開され大ヒットした「スモーク」の続編的作品。ブルックリンのオーギー・レーンの煙草屋を舞台に様々な人が登場して繰り広げる人情劇。オーギーの煙草屋には毎日、いろんな人がやって来る。妙なメガネの男はニューヨークの思い出を語り、店のオーナー夫人のドットはオーギーに夫のグチを聞かせる。常連客のボブは禁煙を決意し、最後の煙草をオーギーと一緒に吸おうとやって来た。ある時、オーナーのビニーが“もう煙草屋の時代じゃない、健康食品屋をやろう”と言いに来る。オーギーは、いかにこの店がみんなに愛されているかと説得するのだったが……。「スモーク」の原作者であるP・オースターが、本作では「スモーク」のW・ワンとともに監督を務めている。", "1995-01-01", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("МОСКВАДЮБОВЬМОЯ", "モスクワわが愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "白血病で余命いくばくもない日本のバレリーナが素質を認められて正式にボリショイ・バレエ団へ入団。幸運にも次回公演作のプリマに起用され、血のにじむようなレッスンを重ねるのだが……。彼女とソ連人彫刻家との国境を越えた愛を描いたメロドラマ。", "1974-01-01", "76,12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE HISTOIRE SIMPLE", "ありふれた愛のストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ロミー・シュナイダーが、セザール賞主演女優賞に輝いたラブ・ストーリー。パリのデザイナー、マリーには、最近会社の合併騒動や恋人セハジュとの別れなど、苦悩の種がつきない。そんな時、かつての夫ジョルジュが、マリーに急接近。二人は、再び心を通わせ、愛し合うようになるが……。", "1978-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠山の金さん 火の玉奉行", "遠山の金さん 火の玉奉行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ご存じ“名人左甚五郎の眠り猫と妍を競う”桜吹雪が舞う、遠山の金さん11番目の事件帖。江戸城抜穴の図面をタネにしたゆすり事件裁判で、取り調べ不十分によって役目御免となった父の無念を晴らすため、金四郎は捜査に乗り出す。そして、見事にゆすりの確証をつかむ。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("吹雪と共に消えゆきぬ", "吹雪と共に消えゆきぬ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "園村邦代は、全国各地に散らばっている昔の友だちを訪ねようと旅に出る。しかしそれぞれの友だちは皆、どこか悲しい影を背負っていて……。過去に郷愁を抱く一人の女性が、旧友たちの辿った各々の人生の断面をめぐっていく、ジュリアン・デュヴィヴィエ監督の名作「舞踏会の手帖」そのものの作品。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("拳銃横丁", "拳銃横丁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "港町、横浜。劉一味にことごとく押されぎみの大須賀組は、神戸から、“早射ちマック”の異名をとるガンマンの牧を駆り出す。牧は途中の車内で殺されかけるが、サブという男に助けられる。彼は弟の死の真相を探るため横浜へやって来たという。横浜に到着した牧が大須賀組の用心棒となる一方で、サブは劉一味に用心棒として入り込むが……。意外な展開で驚かせる小粋なサスペンス活劇。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents 真夜中の宴　「きつね大回転」", "桃まつり presents 真夜中の宴　「きつね大回転」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 22, "", "2008-04-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Seven（Se7en）", "セブン", "", "", "http://ecx.images-amazon.com/images/I/51gyRLsuH8L._SL210_.jpg", 126, "　退職を間近に控えたベテラン刑事サマセットと若手刑事ミルズは猟奇連続殺人事件の捜査にあたる。犯人はキリスト教における7つの大罪に基づいて殺人を繰り返していることが明らかに。やがてサマセットとミルズは容疑者を割り出すが、その人物に逃げられ、さらにミルズの素性が知られていたことも発覚する。そしてさらなる殺人事件が続いた後、驚愕の事態が……。独特のビジュアルセンスとダークな物語が話題を呼んだ戦慄のサスペンス・スリラー。", "1996-01-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギルステイン", "ギルステイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "世界に誇るフィギュア作家、韮沢靖がキャラクター・デザインを担当した3DCGアニメ。“ギルステイン”とは“ギルティ”（＝罪）と“フランケンシュタイン”を併せた“罪を背負った生体兵器”という意味。2028年。悪徳企業ヤシロ・アコロジーによって人間兵器ギルステインに改造された人間たちが殺りくを繰り返していた。人間の心を持つギルステイン、カオスは、ゲリラ組織の用心棒となり、ヤシロに立ち向かうDVDタイトルは「GUILSTEIN」。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海峡、血に染めて", "海峡、血に染めて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "現職海上保安官の体験談をもとにした痛快海洋アクション。和田浩治のアクションもこの頃になると、さすがにサマになってきた。鈴木清順も彼の成長に合わせて、初期のコミック・アクションから、この作品のような本格的アクションに、徐々に取り組むようになった。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SON OF PALEFACE", "腰抜け二挺拳銃の息子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「腰抜け二挺拳銃」の続編となるコメディ調のウエスタン。ホープとラッセルの名コンビぶりや歌うカウボーイを演じるロジャースの歌唱ぶりも見もの。加えて、名エンターティナーのビング・クロスビー、スペクタクルの巨匠監督セシル・B・デミルがカメオ出演している。父が隠したという金貨を探しに西部にやって来た息子だが、歌手に変装した女盗賊との争奪戦に発展。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN OUTLAWS", "アメリカン・アウトロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "西部開拓時代のアメリカに実在した義賊の、波乱の半生を描くウエスタン。紳士強盗として英雄視される主人公を、「マイノリティ・リポート」で注目されたコリン・ファレルが快活に好演。19世紀の米国西部の田舎町。鉄道会社の地上げに苦しめられる町民を救うため、青年ジェシーは南北戦争でともに戦った仲間を率いて強盗団を結成。紳士的な手段で次々と銀行から鉄道会社の資金を強奪するが、そんな彼らに追手が迫り……。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　POWERPUFF　GIRLS", "パワーパフ・ガールズ・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "1998年にアメリカで放映が開始された人気TVアニメ『パワーパフ・ガールズ』の劇場版。一見かわいらしいが、実は超人的なパワーを持つ幼児３人組パワーパフ・ガールズの誕生秘話が描かれる。天才科学者ユートニウム博士の実験現場で、偶然の事故によって誕生した３人のスーパー少女。ブロッサム、バブルス、バターカップと名づけられた彼女たちは、鬼ごっこの感覚で街を破壊してしまったことから、市民のバッシングに遭い……。TV版の産みの親C・マクラッケンが総監督を務めている。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY LEFT FOOT", "マイ・レフトフット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "アイルランドの画家であり、詩人であり、作家でもある実在のクリスティ・ブラウンは生まれながら、重度の脳性小児マヒに冒されていた。だが、貧しい子だくさんの家庭にあっても、温かい母の愛と父や兄妹たちの支えで日々を過ごしていた。医者からは、“植物同然にしか生きられない”と宣告されていたが、ある日、クリスティが左足を使ってチョークで何かを書いていた。“MOTHER”それが、彼の書いた初めての文字だった。並々ならぬ努力で、話せるようになり、絵を描き、後に妻となるメリー・カーと出逢うまでを辛抱強く丁寧に描き出している。センチメンタルになりがちな話をドライに、同情しすぎずに、クリスティの苦悩を演出するJ・シェリダンはこれが初監督とは思えないほど。また撮影中、ずっと車椅子に座り、左足だけを使って生活していたD・デイ・ルイスと母親役のB・フリッカーの名演技は、アカデミー主演男優、助演女優賞受賞にまさにふさわしい。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAURA LAUR", "炎の少女ローラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "カナダの大都会ケベックを舞台に、自由奔放に生きる少女ローラの愛と性を描く官能ロマン。彼女はやがて中年の建築技師と、その息子のカメラマンをも自らの肉体の虜にしてしまう。天才女流作家とうたわれたS・ジャコブのカナダ全土を揺るがせたベストセラー小説の映画化。スタッフの多くも女性。", "1989-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャージの二人", "ジャージの二人", "", "", "http://image.eiga.k-img.com/images/movie/53381/315x210.jpg?2313", 93, "「アヒルと鴨のコインロッカー」「チーム・バチスタの栄光」の中村義洋監督が、芥川賞作家・長嶋有の同名小説を映画化。会社を辞めたばかりの息子とグラビアカメラマンの父。それぞれ私生活に問題を抱える彼らが、軽井沢の山荘でひと夏を過ごすことになり……。ジャージ姿の父子2人が微妙な距離感を取りながら過ごすユルい日常を、カリスマロックバンド\"シーナ＆ロケッツ\"の鮎川誠と「アフタースクール」の堺雅人共演で描く。", "2008-07-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Made of Honor", "近距離恋愛", "", "", "http://image.eiga.k-img.com/images/movie/53621/315x210.jpg?2655", 101, "これまで恋愛をゲームのように楽しんできたトムと、結婚に憧れて運命の男性を待ち続けてきたハンナは大学時代からの大親友。ある日、ハンナが出張で6週間スコットランドへ行くことに。ハンナと離れ離れになって初めて彼女の大切さに気付くトムだったが、帰ってきたハンナは別の男性と婚約しており……。「魔法にかけられて」のパトリック・デンプシー主演によるラブストーリー。相手役に「M:i:III」のミシェル・モナハン。", "2008-07-12", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　BITTERSWEET　LIFE", "甘い人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "人気の韓流スター、イ・ビョンホンが持ち前のキラースマイルを封印して挑んだ意欲作。ある女性と巡り合い、ボスの命令に逆らったことで、非情な運命をたどっていくホテル・マネージャーに扮したフィルム・ノワールだ。ハードなアクション・シーンもこなした、その渾身の演技に注目。裏社会に通じるボスの下で働くソヌが、ボスの若い愛人ヒスの監視を命じられる。しかしソヌはボスの命令に背き、ヒスを解放してしまい……。", "2005-04-23", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルナハイツ2", "ルナハイツ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "星里もちるの同名コミックを、グラビアアイドルの安田美沙子主演で映画化したラブコメディの第2弾。建てたばかりのマイホームを会社の女子寮にされてしまい、同僚のまどりら4人の女性と同居することになった隼人。紆余曲折の末、ついに結ばれるかに見えたまどりと隼人の前に、逃げたはずの隼人の婚約者・友美が隼人の子を妊娠して戻ってくる。隼人と夫婦のように振るまう友美の態度に、ショックを隠しきれないまどりだったが……。", "2006-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUSTER", "フィル・コリンズ in バスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "一般大衆から熱狂的な支持を受け、犯罪者というよりは国民的ヒーローとなったイギリスの実在の列車強盗バスターと、その妻ジェーンの逃亡生活、そして彼らの意外な結末を描いたハートウォーム・ストーリー。ヒット曲『恋はごきげん』が主題歌に使われており、そのP・コリンズが映画初主演を果たしている。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jungle 2 Jungle", "ジャングル2ジャングル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "マンハッタンのやり手ブローカー、マイケルは恋人と結婚するため、別居中の妻と離婚を成立させなければならなかった。医者としてアマゾンの奥地に住む彼女と13年ぶりに再会したマイケルは13歳の息子ミミ・シクの存在を初めて知る。そして軽い気持ちで交わした約束を実行するため、彼を連れてニューヨークへ戻ることに。狩りや吹き矢の名手であり、巨大なクモをペットにする自然児ミミ・シクが大都会で繰り広げる冒険と騒動、そして初恋物語をコミカルなシーンを交えながら綴っていく。父親マイケルを演じるのはコメディ俳優のT・アレン。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少女・aｎ adolesceｎt", "少女・aｎ adolesceｎt", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "俳優、奥田瑛二の監督デビュー作。連城三紀彦の短編小説をもとに、落ちぶれた中年警察官と15歳の少女の純愛を描き出す。情緒あふれる静かな田舎町。43歳の警官・友川は、知的障害を持つ青年に銃を撃たせたり、寂しい主婦と情事を重ねるなど道徳観のかけらもない。そんな彼が、ある日15歳の少女に援助交際を申し込まれる。やがて二人は孤独を埋め合うように恋に落ちていくが、幸せは長くは続かなかった……。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渇きの街", "渇きの街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "北方謙三の同名小説を原作に、ある若者のやり場のない感情を描くハードボイルド・タッチの青春映画。友人の復讐のために殺人を犯した青年、高志は、室田というヤクザの下で恐喝や地上げの仕事をこなすようになる。そんなある日、高志はデザイナー志望の娘、真紀と恋に落ちるが、室田が殺された事件をきっかけに破滅への道をたどり出す。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD ON THE MOON", "月下の銃声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ショートの同名小説をR・ワイズが監督した西部劇。開墾入植者の男が、親友が牛飼いグループの悪どいリーダーと知り対立する。戦後すぐに作られた西部劇として、また、あらゆるジャンルをこなす職業監督ワイズの初期の長編として興味深い。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEJAR／BUYUK　ADAM　KUCUK　ASK", "少女ヘジャル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "1923年のトルコ共和国成立以来、長きに渡り対立し合ってきたトルコ人とクルド人。少数民族である後者は母国のクルド語の使用を抑制されるなど、今も苦境の直中にある。そんな一連の“クルド人問題”は現在、タブー視されているのだが、トルコの女流監督H・イペクチは果敢にも真正面からそれに立ち向かう。武装警官によって家族を皆殺しにされたクルド人少女ヘジャルと、彼女を匿うことになった元判事のトルコ人、ルファト。対立民族でありながら、次第に打ち解け心を開いていく二人の姿は、異文化の平和共存の在り方を考えさせると同時に、社会、歴史の変革はいつも、一人の強き意志から始まることを教えてくれる。", "2001-01-01", "32", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Brewster McCloud", "バード★シット", "", "", "http://image.eiga.k-img.com/images/movie/24271/315x210.jpg?3292", 105, "巨匠ロバート・アルトマンが1970年に発表した、パロディとユーモアたっぷりの風刺コメディ。ヒューストンのドーム野球場にこっそり暮らす少年ブルースターは、鳥のように空を飛びたいと願い、ルイーズという協力者の助けを借りながら筋肉を鍛え、翼を組み立てていた。その彼を邪魔する者たちは鳥のフンを浴びて、変死してしまう。そんな中、ブルースターはスザンヌという娘に好意を寄せられるが……。", "2010-07-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIGER BAY", "追いつめられて…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "フランスのミステリー作家カレフの原作を映画化。金持ちの囲われ者となったかつての恋人を射殺してしまった船員と、それを偶然目撃した少女の奇妙な友情を描く。冒険劇タッチとセミ・ドキュメント風演出を巧みに同化させた、サスペンス映画。", "1959-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISSING IN ACTION", "地獄のヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ベトナム戦線をからくも脱出したブラドック大佐が、M.I.A.（戦争行方不明者）救出のため、再びベトナムに潜入。たった一人で捕虜を助け出す。B級アクションのヒーロー、ノリスが「ランボー／怒りの脱出」同様のストーリーで白熱の戦闘シーンを展開。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vampire", "ヴァンパイア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "", "2012-09-30", "76,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I’M DANCING AS FAST AS I CAN", "暗闇からの脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "テレビ・ドキュメンタリーの売れっ子女性プロデューサー、バーバラは、仕事に執着するあまり覚醒剤を常用するようになる。やがて覚醒剤中毒になった彼女は、人生をやり直すため、薬を断つが、恐ろしい禁断症状が彼女を襲う……。J・クレイバーグが、薬に溺れる女性TVプロデューサーを熱演したシリアス・ドラマ。B・ゴードンによるノンフィクションを映画化。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウェルター", "ウェルター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "当時、プロボクサーだった福田健吾を主演に、「サード」の軒上泊の同名小説を映画化。恋人を暴行した暴走族の連中を殴り倒し、ボクサーへの道を自ら放棄した青年リュウイチ。少年院へ送られ、ケンカばかりしていた彼は、院長の勧めで再びボクシングを始める。少年院からボクサーとしてデビューし活躍していく青年の姿を描く。プロだけに拳闘シーンは迫力十分。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIVERE IN PACE", "平和に生きる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "第二次世界大戦下のイタリアの小さな山村。森で発見されたアメリカ軍の二人の脱走兵と村人との心の交流、そしてナチス監視兵との騒動を描いたコメディ。酔っぱらったアメリカ兵とナチス監視兵との浮かれたワンシーンなど喜劇的シーンを織り込みながらも、“平和”の意味を揶揄したラストシーンが痛烈な響きを奏でる。", "1947-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("木村家の人びと", "木村家の人びと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "確たる目的がないにもかかわらず、ひたすら金を愛し、日夜小銭稼ぎに精を出す“木村家の人々”を主人公にした、ブラック・ユーモアに満ちたホーム・コメディ。彼らの異様なまでの金への執着ぶりをユニークなエピソードで綴る。明るい守銭奴を貫く親に対して、このままでいいのかと、子供が少し引いた目で見ているところがミソ。子役の伊崎充則が好演。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("1990牡丹燈籠", "1990牡丹燈籠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "圓朝の怪談『牡丹燈籠』をモチーフにしたモダン・ホラー。かつてはロッカーだった萩原は、今は“B・グッド”というロック・グループのマネージャーをしていた。ある夜、彼はライダースーツを着た若い女と出会い、彼女を抱く。翌朝、彼はその女のことを思い出した。それは７年前に駆け落ちの約束をしながら彼が勝手にすっぽかした女、恵美子だったのだ。以来、恵美子は毎晩彼のもとを訪れ、明け方になると去っていく。恵美子の行動に疑問を抱いた萩原は彼女の故郷を訪れ、彼女が７年前に事故で死んだことを知る。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAZZ爺MEN", "JAZZ爺MEN", "", "", "http://image.eiga.k-img.com/images/movie/57141/315x210.jpg?1972", 92, "町おこしのために結成された即席市民ジャズバンドを通して、不器用に生きてきた大人たちがそれぞれの人生に向き合っていく姿を描く。埼玉県北部の町・本庄で、町おこしのために市民ジャズバンドを結成する計画が立ち上がる。しかし、吹奏楽経験者を募集したはずが、集まったのはこの日のためにビンテージサックスを買ってしまったお調子者の洋菓子店主・後藤をはじめとしたド素人ばかりで……。", "2011-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("温泉ゲリラ・大笑撃", "温泉ゲリラ・大笑撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "犬塚弘となべおさみの凸凹コンビがある地方の町のヤクザとひょんなことからかかわり合い、渋々その手先となるが、そこへ香山美子扮する美女が登場。なべはすっかり参ってドタバタとなる。職務尋問が趣味という町の警察署長役のハナ肇が好演。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lost Horizon", "失はれた地平線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "ジェームズ・ヒルトンのベストセラー小説を、名匠フランク・キャプラ監督が映画化したファンタジック・アドベンチャー。英国領事ロバート・コンウェイらを乗せた飛行機がヒマラヤ山中に不時着する。なんとか脱出したものの吹雪の中をさまよっていた乗客たちは、中国人チャンの案内によって理想郷シャングリ・ラへと導かれる。誰もが幸せに暮らすその地を治める250歳のラマ高僧は、ロバートを自分の後継者に指名するのだが……。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シンデレラ・エクスプレス", "シンデレラ・エクスプレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "日曜日の東京駅発新幹線最終便＝シンデレラ・エクスプレスをテーマにした、ポップでシャレたラブ・ストーリー。週末ごとに東京のベイ・サイドにやって来る神戸の短大生とその仲間たち。すべてが美しく、楽しくありたいとする彼らは恋愛感覚もライトであった……。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東シナ海", "東シナ海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "今村昌平の原作をもとに、本土復帰前の沖縄を舞台にして、５人の若者が殺人事件に巻き込まれていく姿を描く。オンボロマグロ船に人数をそろえるためだけに雇われた、六郎をはじめとする５人。だが船が故障し、彼らは沖縄、那覇港のマグロ基地に取り残される。ある日、船の中に死体が見つかり……。異色のブラック・コメディとして一見の価値あり。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Zの回路 復讐の裏ゴト師", "Zの回路 復讐の裏ゴト師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "パチンコ業界の裏側を背景に、親友の裏切りによってすべてを失った男の復讐を描くサスペンス第２作。親友の神崎によって人生を狂わされたパチンコ・メーカー、セントラル電工の元一流プログラマー狩野は、苦悩の中で新たな人生を始めようとしていた。そんな彼のもとに、新機種のプログラム開発の依頼と神崎の訃報が舞い込む。苦い過去がつきまとう狩野は、娘を誘拐されたのを機に、再び陰謀渦巻くパチンコ業界へと足を踏み入れるのだった……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("深海獣レイゴー", "深海獣レイゴー", "", "", "http://ecx.images-amazon.com/images/I/51oZ81qvP9L._SL210_.jpg", 0, "特撮マニアとして知られる落語家・林家しん平が初メガホンを取った怪獣映画。第2次世界大戦下、南方戦線のトラック泊地に駐留していた日本海軍連合艦隊に、敵潜水艦と思われる影が急接近してくる。戦艦大和は影に向かって主砲攻撃を打ちこむが、その正体は伝説の深海龍レイゴーの子どもだった。子どもを殺されて怒り狂ったレイゴーは、連合艦隊への復讐を開始する。出演は「ウルトラマンコスモス」の杉浦太陽ほか。", "2008-06-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Daft Punk's Electroma", "ダフト・パンク　エレクトロマ", "", "", "http://image.eiga.k-img.com/images/movie/33951/315x210.jpg?1305", 74, "フランス出身の2人組テクノ・ユニット、ダフト・パンクが長編映画監督デビューを果たしたロードムービー。これまでもスパイク・ジョーンズ、ミシェル・ゴンドリー、松本零士らとのコラボレーションPVで話題を呼んだ彼らが、人間になることを夢見る2体のロボットの壮大な旅を映像と音楽のみで描き出す。ステージ衣装であるディオール・オムのレザースーツを劇中でもそのまま使用するなど、ダフト・パンクのレトロフューチャーな世界観を楽しめる。", "2007-04-28", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHO　DRIVES　ME　MAD？　WHO　DRIVES　ME　CRAZY？", "誰が俺を狂わせるか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "“キラースマイル”で日本でも絶大な人気を誇る韓流スター、イ・ビョンホンの映画主演デビュー作。当時TVの青春ドラマのスターとして活躍していたビョンホンだが、一転して不器用かつコミカルな演技を披露した異色作。結婚するために作家への道を諦め、サラリーマンに転職した主人公ジョンドゥ。ある日、人事異動で彼の上司としてやって来たのは何と恋人ジュヨンだった。やがて、同僚たちからも無能扱いを受けるジョンドゥは“最後の決断”をする。映画初主演で見せた俳優ビョンホンの怪演が見もの。", "1995-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チ・ン・ピ・ラ", "チ・ン・ピ・ラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "勝手気ままに毎日を送り、本物のヤクザに追われれば“死ぬのが怖い”と大仰に怯える、義理人情でも反逆でもない自由人として生きる二人のチンピラを主人公にした異色ヤクザ映画。金子正次の遺稿脚本に、川島透が手を加えて演出した作品。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホーム・スイートホーム", "ホーム・スイートホーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "高齢化社会で暮らす老人たちの生を描いた人間ドラマ。元オペラ歌手の宏は痴呆症で、町を徘徊しては大声で歌うなどの奇行をとるようになっていた。そんな彼に悩まされる娘夫婦は、ガマンの限界に達して岩手のグループホーム“おばんでがんす”の前に置き去りにする……。老人施設を舞台にした温かいドラマを通して、介護の在り方を浮き彫りに。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wonderland", "ひかりのまち", "", "", "http://image.eiga.k-img.com/images/movie/1751/315x210.jpg?2574", 109, "11月のある週末。ソーホーのカフェで働くウェイトレスのナディアは、伝言ダイヤルで恋人を探している。姉のデビーは夜遊びに余念がないシングルマザー。妹のモリーはもうすぐ出産だが夫のエディは何故か浮かない顔だ。年老いた両親は、弟ダレンの家出以来互いをなじり合ってばかり。そんな折、ティムと名乗る男からナディアの携帯電話へ連絡が入る。彼と食事を共にし、うまく行きそうな予感に胸を躍らせるナディアだが……。", "2000-09-02", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A NIGHT IN THE LIFE OF JIMMY REARDON", "ジミー　さよならのキスもしてくれない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "R・フェニックスの初主演作。1960年代のシカゴ郊外を舞台に、父親に反抗する17歳の少年が、愛や友情を通して大人へと成長していく姿を綴っている。P・コスロやA・マグナソンらベテラン陣も活躍。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不知火小僧評判記 鳴門飛脚", "不知火小僧評判記 鳴門飛脚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "江戸を騒がした義賊、不知火の新三は大阪に来ていた。また捕物小町、お豊も大阪へ。新三は思わぬことから阿波藩大阪蔵屋敷の侍たちと金貸し万右衛門との争いに巻き込まれていき、万右衛門の番頭の娘、お夏、それにお豊と３人で旅をするはめに……。大川橋蔵が粋な義賊に扮した明朗時代劇。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TORN CURTAIN", "引き裂かれたカーテン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "ヒッチコック50本目、そして監督生活40年の記念すべきスリリングなスパイ・アクション。東ドイツへの亡命者を装い、東側の軍事秘密を探ろうとするアメリカのスパイで、科学者のマイケル。彼はそれを知った当局に追われることになるが……。主人公の婚約者に扮するアンドリュースの珍しいベッド・シーンも見もの。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Home Movies", "悪夢のファミリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "　青年デニスの一家は風変わりな者たちばかり。彼の父の浮気に気づいた母は自殺するフリをし、涙に暮れている。独善的な大学講師で、自然食主義者の兄ジェームズには美人の婚約者クリスティナがいた。彼女に想いを寄せるデニスはひょんなことから覗きが癖に。そんなデニスに、マエストロなる人物は映画を撮るように勧めるが……。デ・パルマ監督の初期のコメディを想起させる作品。スタッフのほとんどはサラ・ローレンス大学に籍を置くデ・パルマの映画製作教室の学生からなる。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘境ヒマラヤ", "秘境ヒマラヤ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "昭和36年６月に日本を出発した西北ネパール学術探検隊を、同行した大森栄カメラマンが記録した長編ドキュメンタリー。かつて外国人が１度も足を踏み入れたことのない西北ネパールの奇妙な風習が、次々と映し出されていく。第10回ブルーリボン教育文化映画賞をはじめ、数々の賞を受賞した。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents kiss!　「タッチミー」", "桃まつり presents kiss!　「タッチミー」", "", "", "http://image.eiga.k-img.com/images/movie/57911/315x210.jpg?2543", 28, "", "2009-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛情の決算", "愛情の決算", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "今日出海の小説『この十年』の映画化。終戦後10年の日本人の家庭を、佐分利信の監督・主演で描いた。復員した楢崎は死んだ戦友の妻と結婚する。失業から特需ブームで再就職へ。やがて夫婦の愛情は冷えていく……。風俗史を交えた辛口ホームドラマ。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女獣", "女獣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "現金輸送車襲撃事件の被害者、中田秀子の体から不良グループの女の指紋が検出された。秀子の交友関係を探ることになった婦人警官・路子は、名前を偽り潜入捜査を開始する。しかし計画は難航し、やがて新たな犠牲者が現れる。婦人警察官の活躍を描くアクション。ヒロインを支える刑事・杉山を、菅原文太が男臭く熱演している。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LANDRU", "青髭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "身寄りのない裕福な未亡人やオールドミスをパリ郊外にある別荘に連れ込んでは、財産を奪ったあげく殺害していたランドルー。ついに官憲の手が及び、裁判の結果、死刑判決が下る。有名な“青髭伝説”をもとにした、怪奇なムード漂う一編。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("与太郎戦記・女は幾万ありとても", "与太郎戦記・女は幾万ありとても", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "与太郎の属する内務班に、女性が１日入隊することに……。女に目がないこととドジなことだけは一向に変わらない与太郎の姿を描いた、シリーズ最終作。第１作の演出を担当した弓削太郎が、再び返り咲き。当時の彼はこのシリーズと“いそぎんちゃく”シリーズを並行して担当していた。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES DEUX ANGLAISES ET LE CONTINENT", "恋のエチュード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "英国娘のアンヌとミュリエルの姉妹の前に、気弱な性格のフランス人青年クロードが現れる。一目でクロードの虜になってしまったミュリエルだが、若すぎるからという理由で両親は結婚に反対、一年の空白期間を置くことになった。“愛があれば……”と思っていたミュリエルだったが、クロードはその間に、パリでアンヌと恋に落ちてしまう……。1971年のパリ公開時に、配給業者の都合で14分カット。日本公開の時はさらに12分カットされていた。1984年、死の直前に完全版が公開され、トリュフォー監督は、“この作品こそ私の1984年の新作だ”と語ったという。その意味では実質的な“遺作”ともいえる。か細く揺れるロウソクの炎を捉えた映像が美しい。", "1971-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO BITS", "天国の約束", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "大恐慌下の1930年代フィラデルフィア。12歳の少年ジェンナーロは、貧しい家計を女手一つで支える母ルイザ、大好きな祖父ガエタノと暮らしていた。町に新装オープンした映画館に憧れを抱くジェンナーロは、入場料を稼ぐために仕事を探すが、ガエタノが倒れるというショッキングな事件が起こる……。厳しい現実に触れて成長する幼い少年の姿を、映画へのノスタルジックな夢を込めて描くヒューマン・ドラマ。A・パチーノがガンコだが優しいお爺ちゃんに扮して、枯れた円熟味を披露している。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富士に立つ影", "富士に立つ影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "幕府は富士の裾野に軍略研究のための城を築くことを計画した。佐藤菊太郎と熊木伯典の二人によって、軍師の地位を懸けた戦いが始まった。富士山麓に建てられた江戸幕府の軍事訓練場・築城の軍師の地位を懸けて対立する二人の男の姿を追う。正義と平和のために闘う人間像を真摯に描き出した傑作時代劇。DVDは「阪東妻三郎傑作選　DVD-BOX」に収録。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無問題〈モウマンタイ〉", "無問題〈モウマンタイ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ナインティナインの岡村隆史が主演した香港映画。突然、別れを告げて香港に行ってしまった恋人、玲子を追って現地へ飛んだ大二郎。友人のコネで撮影所に潜り込んだ彼はいつの間にか映画のスタントマンとして活躍するようになるが、玲子の心は取り戻せないでいた。そんな大二郎のアパートに、不法入国で警察に追われる中国人少女リューが逃げ込んでくる。恋人を追って来たという彼女に同情し、一緒に恋人探しを始める大二郎。二人の心はしだいに接近していくのだが……。スタント・シーンでの派手なアクションや笑い、涙を交えながら、二人の恋愛模様を追ったラブ・ストーリー。サモ・ハン・キンポーが本人の役で特別出演している。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お吟さま", "お吟さま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 154, "大陸制覇の野望を抱き、朝鮮に手を伸ばそうとする豊臣秀吉にあくまでも抵抗する千利休と、その娘でキリシタン大名の高山右近を愛したお吟の悲劇を描いた歴史大作。英雄・秀吉のイメージを大きく変化させた、卑しく狂暴な独裁者としての秀吉を、三船敏郎が力演している。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うずまき", "うずまき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "人気ホラー漫画家、伊藤潤二の原作によるスリラー。高校生・秀一の父親が、うずまきの形に魅せられ病的なまでに、その形状のものを集め出した。秀一の恋人・桐子は異変に気づくが時すでに遅く、うずまき熱は人々に伝染していく。ミュージックビデオ監督のHiguchinskyが、原作を踏襲した異様なビジュアル世界を作り出している。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOVOCAINE", "ノボケイン・局部麻酔の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "美しい歯科衛生士の婚約者ジーンとともに、幸福な日々を過ごすエリート歯科医フランク。彼はスーザンというセクシーで謎めいた患者に誘惑されたことをきっかけに、悪夢のような事件に巻き込まれ、警察に追われる身になってしまう……。先の読めないストーリー展開が魅力のサスペンス・ミステリー。喜劇俳優S・マーティンが、巧妙な罠に落ちていく主人公をシリアスに演じきっている。ビデオタイトルは「局部麻酔・ノボケイン」、DVDタイトルは「NOVOCAINE　ノボケイン」。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("砂絵呪縛", "砂絵呪縛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "徳川６代将軍の座をめぐり、暗躍する水戸藩士の天目党と、柳沢吉保配下の柳影組。この二つの団体は、５代目将軍綱吉が、世継を定めた御直書の隠し場所の鍵を握る花簪をめぐって争いを繰り広げる。この抗争に巻き込まれた浪人・森尾重四郎の活躍は……。近衛十四郎の剣技が冴える娯楽時代劇。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NINA TAKES A LOVER", "ニーナの情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "夫としばらく離れて暮らすことになった30代の人妻ニーナは、ある日、公園で出会ったイギリス人のカメラマンと恋に落ちる。今まで知らなかっためくるめく感覚に戸惑いながらも、次第に愛人との情事に溺れていくが……。何不自由なく暮らすアッパーミドル階級の女を主人公に、女と男の恋愛の綾を小粋に綴ってみせた一編。「セックスと嘘とビデオテープ」を思わせる心理描写に加え、最後に用意された物語の意外な結末にもA・ジェイコブス監督のセンスの良さがうかがえる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青年の樹", "青年の樹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "弟・裕次郎を主人公に想定して石原慎太郎が書いた同名小説を映画化。ヤクザの後継ぎになるのを嫌った若者が、大学に入って同様に家業に反発する料亭の娘と知り合う。やがて、彼女の母がパトロンの政界汚職に絡んで自殺。主人公は、汚職を暴く……。様々な経験を経て人生の痛みを知り、成長していく若者を裕次郎が巧演。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("処刑の部屋", "処刑の部屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ある夜、大学生の島田と友人は二人連れの女子学生と飲み歩いたが、島田は女子学生・顕子に薬を飲ませ友人のアパートへ連れ込み、犯してしまう。島田は金がもとで他の大学の連中にリンチを受ける。そこに顕子が現れ島田は恐怖する。顕子の手にはナイフがあった……。石原慎太郎の性と暴力を描いた原作を市川崑が演出する異色作。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前百年", "喜劇・駅前百年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "第１作「駅前旅館」以来久々に豊田四郎監督がメガホンを執った。上野駅前の旅館“葵館”は伴野孫作が経営しているが、ライバルの“ホテル赤門”では観光会社の次郎のあっせんもあって大繁盛。さびれる一方の葵旅館は近代化をはかる。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hall Pass", "ホール・パス　夢の独身許可証　1週間限定", "", "", "http://ecx.images-amazon.com/images/I/51QCzw8DHpL._SL210_.jpg", 105, "「メリーに首ったけ」のファレリー兄弟とオーウェン・ウィルソンがタッグを組み、中年男2人の悲しい性（さが）を描いたドタバタコメディ。親友同士のリックとフレッドは、お互いに長年連れ添っている妻がいるが、若い女性に目が行きがちでどうも夫婦関係が上手くいかない。そこで妻たちは2人に対して、1週間だけ結婚生活から離れて何をしてもいいという許可証（＝Hall Pass）を与えるが……。共演は人気コメディアンのジェイソン・サダイキスとクリスティーナ・アップルゲイト。", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若者に夢あり", "若者に夢あり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "高橋英樹が、家庭環境の複雑さからヒネくれた不良青年を演じる青春映画。昭夫は、小沼物産社長・重太郎の次男だが、父と愛人との間にできた子供。重太郎は若い愛人を作り、昭夫の兄は妻をお手伝い同様に扱うという、冷めた家族だった。ある日、重太郎の愛人と昭夫がデートしたことから、家庭内に波紋が広がり始めて……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blackout", "パニック・エレベーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "", "2007-01-01", "3,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CHASSE AUX PAPILLONS", "蝶採り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "古き良き時代の産物をノスタルジックに描きながら一方で、豊かさへの警鐘をブラック・ユーモアな視点で捉えた、イオセリアーニ監督渾身の一作。フランスの古城に住む二人の老婆は、森で銃の試射をしたりオーケストラに参加したりと優雅な余生を楽しんでいた。だがある日、日本のビジネスマンが城を売ってくれと交渉に訪ねてきた。二人は拒否したが、老婆の一人が急死して思わぬ方向へ。", "1992-01-01", "2,7,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("へのじぐち", "へのじぐち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "北海道を舞台に自主製作映画で評価の高かった吉雄孝紀が監督した、劇場長編デビュー作。北海道のタウン誌で働くOLのすみれは、“このままただのオバサンになっちゃうんだろうか……”とタメ息ばかりつく日々を送っていた。そこに奇妙な二人の男が現れ……。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Night at the Museum 2: Battle of the Smithsonian", "ナイトミュージアム2", "", "", "http://image.eiga.k-img.com/images/movie/53551/315x210.jpg?2542", 105, "真夜中に展示物が動き出す美術館を描いたコメディ「ナイトミュージアム」の続編。主演のベン・スティラーをはじめ前作キャストが再集結するほか、「魔法にかけられて」「ダウト」のエイミー・アダムスが女性で初めて大西洋横断に成功したパイロット、アメリア・イヤハート役で初登場。元美術館警備員のラリーのもとに、ニューヨークの自然史博物館から世界最大の博物館群スミソニアンに移送された展示物から助けを求める連絡が入る。ラリーはすぐさまワシントンに向かうが……。", "2009-08-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Madame Bovary", "ボヴァリー夫人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALL LADIES DO IT", "背徳小説・第ニ章【ノーカット完全版】", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "夫ポールへの罪悪感もなしに浮気を重ねるダイアナ。彼女は二人の恋愛感情に刺激を与える良い材料と考え、彼にすべてを告白していた。だが、ポールは自分の中に芽生える嫉妬心をどうすることもできず、ダイアナと誠実な恋をやり直そうという結論にたどり着くが……。ベニスを舞台に、二人の愛憎を退廃的なムードの中に描く。", "1992-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　１３TH　WARRIOR", "13ウォーリアーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "M・クライトンが1976年に発表したベストセラー小説『北人伝説』を、アクションたっぷりに描いた伝奇ロマン大作。姦淫のかどで国を追われたアラブの詩人アーメッド・イブン・ファドランは、旅の途中でタタール人の盗賊に襲われているところをバイキングの一団によって救われる。北方の大使になる好機と見て彼らに近づくイブンだったが、折しもそこへバイキングたちの故郷の危機を知らせる伝令が届く。伝説の霧の怪物が再三現れ、スカンジナビア人の国を荒廃させているのだという。占いにより13番目の戦士に指名されたイブンは、バイキングたちとともに遠い北の未開地へ向かう。それは恐ろしく凶暴な人喰い民族との戦いの幕開けであった……。平均身長２nの北欧系の巨体俳優12人と伍して、アラブ人に扮したA・バンデラスが伝説の目撃者を熱演。北人たちと未開の野蛮人たちとの戦闘場面が一番の見ものだ。金管楽器を厚く束ねたJ・ゴールドスミスの音楽もアクションの高揚感をあおっている。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOCUMENTARY of AKB48 to be continued 10年後、少女たちは今の自分に何を思うのだろう？", "DOCUMENTARY of AKB48 to be continued 10年後、少女たちは今の自分に何を思うのだろう？", "", "", "http://image.eiga.k-img.com/images/movie/55861/315x210.jpg?2266", 120, "国民的アイドルグループ「AKB48」の初となるドキュメンタリー映画。2010年の全活動に密着し、1000本を超える収録テープに記録された各メンバーの努力、喜び、葛藤（かっとう）、成長を通して、誰も見たことがない素顔に迫る。収録テープは、各シングルの製作過程、横浜アリーナや代々木第一体育館でのライブ、選抜総選挙、ジャンケン大会といった、あらゆる活動に密着。また、収録の待ち時間にロケバスのなかで熟睡するメンバーやライブのリハーサル中に携帯電話を見つめる等身大の姿なども網羅している。", "2011-01-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARIS S’E´VEILLE", "パリ・セヴェイユ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "『カイエ・デュ・シネマ』誌の批評家として出発したO・アサイヤス監督の第３作。クレマンとアドリアンの父子は、４年ぶりに再会した。18歳の娘ルイーズと同棲する父に、息子のアドリアンは違和感を抱きながら、自身も生き方を模索する。やがてアドリアンはルイーズと接近するが、昔の犯罪に脅える彼は国外に脱出し、クレマンはアルジェに行く。一人残されたルイーズは、プロデューサーに身を委ねあこがれのテレビの世界へ……。登場人物たちがひたすらあがき、動きまわる姿を撮りたかったというアサイヤス監督。その通りに彼らがパリの夜の中で動く過程こそがこの映画の主題といえる。そしてそれと対比されるような、パリ全景を捉えたショットが印象的だ。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("第１回 欽ちゃんのシネマジャック", "第１回 欽ちゃんのシネマジャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "15分の作品を５本立てで上映し、１本300円で観た本数だけ料金を払うという、萩本欽一発案による斬新な興行形態が話題となったオムニバス。借金を残して死んだ男の悲喜劇を描く「生きる—ある臨死」、雪の小樽を舞台に家族愛を綴った「港」、ナンセンスなコメディ「なんかヘン？」、しがない探偵を主人公にしたハードボイルド「探偵ハーレム・ノクターン」、オーストラリアのスタッフ、キャストによる幻想的な「ドリーム・ライダー」と、バラエティに富んだ内容。全部見ると、３分の短編映画「大激走」も観られるというオマケつき。なお、都内の上映期間の後半は、高橋ひとみ主演の「元禄女太陽伝」が加えられた。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WALKER CHUCK NORRIS WAR ZONE", "チャック・ノリス　ダーク・テリトリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "レンジャー部隊のウォーカーは、ピエロのマスクをかぶった強盗団の横行に頭を痛めていた。そんなある日、彼の目の前で、親友のローガンが強盗団に射殺されてしまう。責任を感じたウォーカーは、残されたローガンの子供たちの父親代わりを務めながら、激しい復讐心を燃やす。そしてついに、同僚のジミーがピエロ集団の裏に潜む黒幕を突き止めた……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばりの三役 競艶雪之丞変化 正編・続編", "ひばりの三役 競艶雪之丞変化 正編・続編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "江戸に初舞台お目見えでやって来た雪之丞一座。雪之丞は客の中に父母の仇の姿を見つける。一方で舞台をこなし、他方で父母の仇をうっていく美空ひばりの立ち回りが見もの。しかもタイトル通り、美空ひばりは三役かけもち！ 監督は「明治天皇と日露大戦争」で配収新記録樹立後の渡辺邦男。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狼よ落日を斬れ", "狼よ落日を斬れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 159, "日本のB級映画史を支えた隠れた巨匠・三隅研次の最後の作品。原作は池波正太郎の『その男』をベースに、“人斬り半次郎”を加えたもの。激動の幕末維新を舞台に、血みどろで生きぬいた剣客、英雄たちの淒絶な人生のうねりを描いた巨編。時に荒々しく、時に繊細な三隅演出が冴えた佳作で、今こそ見直す価値のある一編。DVDタイトルは「狼よ落日を斬れ　風雲篇・激情篇・怒濤篇」。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("がんばっていきまっしょい", "がんばっていきまっしょい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "22年前の四国・松山。高校入学を目前にした悦子は海をいくボートに魅せられ、入学と同時に女子ボート部を設立する……。彼女をはじめ、ボート初心者ばかりの女子高校生たち５人が３年間、ボートのナックルフォアに打ち込んでいく様や彼女たちの友情、揺らめく心情を綴る。ひたむきな少女たちのきらめくような表情が胸を打つ青春映画の佳作。主演はオーディション当時、高校２年生でクランクアップののちCMなどで一躍脚光を浴びた新人、田中麗奈。ボートのレース・シーンなどで効果的に流れてくるリーチェの透明感あふれる歌声が、映画の中に見事に溶け込んでいる。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　魔法少女まどか☆マギカ　前編　始まりの物語", "劇場版　魔法少女まどか☆マギカ　前編　始まりの物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "2011年に放送され話題となったTVアニメーション「魔法少女まどか☆マギカ」の総集編となる劇場版2部作の前編。", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANHATTAN", "マンハッタン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ニューヨークと、そこで暮らす都会人の悩みをほろ苦く描くW・アレンの傑作。アイザックは仕事、年齢、セックスその他もろもろに悩みを持つ中年のTVライター。仕事にうんざりし、小説家になろうと番組を降りてはみたものの、突然の収入減はやはり痛い。妻は女の愛人と暮らしていて別居中。友人の浮気相手である雑誌ジャーナリストに惹かれたりもするが、その軽薄なインテリぶりには我慢ならない。唯一の救いは何度かベッドをともにした生意気な女子高生だけ……。『ラプソディ・イン・ブルー』をバックに、白黒で捉えたマンハッタンの情景が秀逸。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BRIDGE", "橋の上の貴婦人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1887年の夏。イザベルと３人の幼い娘たちは、小さな海沿いの村へと避暑に訪れた。そこへ、年に一度のスケッチ旅行のため、画家のフィリップがやって来る。末娘のエマはフィリップに一目惚れするが、彼はイザベルの虜になっていた。そしてイザベルも、彼に惹かれていく。美しく、伝統的価値観を持つ女と、その強い夫、そして若く情熱的な芸術家の３人の軋轢を描くラブ・ストーリー。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒカリ", "ヒカリ", "", "", "http://image.eiga.k-img.com/images/movie/57321/315x210.jpg?1426", 0, "さまざまなアニメーション作品で音響監督を務める岩浪美和が手がけた「音の映画」で、映画館の暗闇の中、登場人物の声がヘッドホンを通してささやきかけるように観客に届く新感覚の体感型ビジュアルサウンドドラマ。恋人を失ったショックで一時的に視力を失い、暗闇に包まれる少女の耳に、医師の励ましや同級生たちの温かな声が届く。そして少女は失った恋人とそっくりな声をもつ青年と出会い……。登場人物の声は神谷浩史、小野大輔、野島健児、岡本信彦、森川智之ら人気男性声優が担当。", "2011-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Sterline Cuckoo", "くちづけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ニューヨークのナイーブな学生と、奔放な性格で突飛な行動をするが実は孤独を抱えた娘・プーキーとの出会い、恋愛、別れを綴る青春映画。パクラ監督のデビュー作。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅路", "旅路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "北海道で鉄道員として働く雄一郎は、故郷紀州で見合いをする。が、彼は見合い相手の長女よりも、優しく古風な風情を持つ次女、有里に心を惹かれる。やがて見合いは破談、雄一郎は北海道から有里へ手紙を送り、有里は母の反対を押し切って雄一郎のもとに走るのだが……。平岩弓枝の同名小説を原作にした文芸もの。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海軍", "海軍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "太平洋戦争勃発の原因となったハワイの真珠湾攻撃に到るまでを、男の友情、男女の純愛をシンクロさせて若者の生き方を描いた戦争映画。北大路と三田が演じる恋愛場面、北大路と千葉の男の友情など、胸を熱くさせる。主人公は兵学校に合格したが、友人は落第。友人の妹は主人公に憧れていたが、兄のため気持ちを胸中に秘め……。やがて、学校を卒業した彼は艦隊に乗る。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BENEATH　THE　VALLEY　OF　THE　ULTRA−VIXENS", "ウルトラ・ヴィクセン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "モンドの鬼才R・メイヤーの、巨乳映画の頂点というべきエロティック・ストーリー。米国の田舎町を舞台に、性的に満たされない恋人たちの性癖をコミカルかつキッチュに描く。この後メイヤーの公私に渡るパートナーとなったヒロイン、K・ナティヴィダッドの102センチのバストは、巨乳を愛したメイヤーの理想の究極といえる。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("影狩り", "影狩り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "『週刊ポスト』に連載されていた、さいとうたかをの同名劇画の映画化。財政難の江戸幕府が、地方の弱小藩をとり潰そうと放った公義隠密“影”。かつてその犠牲となった３人の刺客は“影狩り”となって幕府の陰謀に立ち向かう。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少年は泣かない", "少年は泣かない", "", "", "http://image.eiga.k-img.com/images/movie/54851/315x210.jpg?4428", 98, "「彼女を信じないでください」のペ・ヒョンジュン監督が、北方謙三の傑作小説「傷痕」を基に描いた人間ドラマ。朝鮮戦争で家族を失った子供たちが集まる収容所。気は短いが義理に厚いジョン・ドゥと頭脳明晰なテホは、米軍の密輸品を盗み出して地獄のような収容所を抜け出し、ヤクザの庇護を受けて露天商として働き始めるが……。2人の主人公を演じるのはドラマ「シンデレラマン」のソン・チャンウィと「天国の階段」のイ・ワン。", "2009-11-28", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SPY WHO CAME IN FROM THE COLD", "寒い国から帰ったスパイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "イギリス諜報部員リーマスは、東ドイツの諜報機関を牛耳る元ナチ党員ムントの失脚を目的とした潜入捜査を命じられる。やがて、ムントの次の地位にある切れ者フィドラーに近づいたリーマスだったが、事態は思いも寄らぬ方向へ進んでいき……。J・ル・カレによるスパイ小説の映像化。非情なラスト・シーンとともに、東西冷戦時に暗躍していたスパイたちの虚しい運命がサスペンス・タッチで描かれる。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRIMETIME", "クライムタイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "殺人事件を再現する人気TV番組『クライムタイム』が、ある事件に目をつける。それは女性ばかりを狙った連続殺人で、被害者は眼球をくり抜かれているという猟奇的な事件だった。犯人役に抜てきされた俳優ボビーの迫真の演技で、番組は高視聴率を獲得。事件をリアルに再現するため、さらに役作りにのめり込んでいくボビーに、ある日、本物の犯人から電話がかかってくる。「失踪」のG・シュルイツァーによる異色のサイコ・スリラー。犯人と彼を演じる俳優の間に芽生える奇妙な連帯感、互いがオーバーラップしていく姿に、異様な怖さが漂う。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVERYTHING IS ILLUMINATED", "僕の大事なコレクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "俳優リーヴ・シュライバーが初めてメガホンを執り祖父ゆかりの地を行く米国人青年の旅を描いたロード・ムービー。異国の地でのガイドの青年とのズレたやりとりや、風変わりな人々との交流はユーモアたっぷり。家族に関するものをコレクションしているユダヤ系米国人青年ジョナサンは、祖父の古い写真に写っていた女性の素性を探るためウクライナへ。そこで、彼は陽気な青年と彼の祖父と出会う。珍道中を繰り広げる彼らを待ち受けていたのは、思いもよらぬ事実だった……。", "2006-04-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SURE THING", "シュア・シング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "R・ライナー監督が描く、パワフルな青春ラブ・コメディ。絶対OKの女＝シュア・シングを求めてカリフォルニアへ向かうギブと、ちょっとおカタいギャル、アリソンの珍道中が展開する。ライオネル・リッチーの『ペニー・ラバー』などのヒット曲も効果満点。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カンヌ SHORT５", "カンヌ SHORT５", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "カンヌ映画祭で高い評価を得た短編集。映画のモンタージュで構成された幻想的なアニメ「Fast Film」、廃墟ホテルを舞台にしたゲーム感覚のCGアニメ「Do You Have the Shine？」、水面を漂う女性の白昼夢を映し出す「Play with me」、英国の田舎で暮らす少年の狂気を描いた「Field」、コンテンポラリー・オペラ「Janne da Arc on the Night Bus」など、欧州７ヵ国からの傑作５作で構成されている。", "2001-01-01", "2,3,70,77,58,14,30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京五人男", "東京五人男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "エンタツ、アチャコ、緑波らが主演のコメディ。戦時下、なかなか得意の題材で撮れなかった喜劇の名手・斎藤寅次郎が終戦直後に取り組んだ作品。戦争中、田舎の軍需工場で働いていた５人の男が久しぶりに東京に戻って来る。そして、被災した東京の復興やすさんだ人心を和やかにしようと努力する……。", "1945-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サノバビッチ☆サブ／青春グッバイ", "サノバビッチ☆サブ／青春グッバイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "“低予算でも娯楽作品はできるはず”と、インディーズでのエンターテインメント映画を追求し、“地下テントろばくん”を主催する「毒婦マチルダ」の松梨智子監督による一風変わった青春映画。大人の偽善に塗り固められた高校生活になじめない不良学生のサブとケンは“ビッグ”になるために、東京に出発する。東京で彼らは学生運動に参加するが、やがて、ひょんなことから漫才師を目指すことになり……。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARANOIA", "パラノイア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「ダークマン」で犯罪組織のボス、「Dr.ギグルス」で殺人ドクターを演じたL・ドレイクが殺人鬼を演じるサイコ・スリラー。20年前、田舎町で起きた一家惨殺事件。犯人のカルヴィンは終身刑を宣告され、投獄された。そして現在。一家の唯一の生き残りであるジャナはニューヨークで暮らしていたが、模範囚として出所したカルヴィンが彼女の命を狙い始める……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHADOWS AND FOG", "ウディ・アレンの影と霧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "1920年代のヨーロッパ。連続殺人事件の自警団にむりやり借り出されたさえない男と、恋に破れてサーカス団から飛び出した薄幸な女が街で出会う。ところが小心者の彼に連続殺人犯の濡れ衣がかかり、二人の人生は思わぬ方向に向かって動き始める。陰影に満ちたモノクロの映像美と、アレン映画史上最高の豪華キャストが勢ぞろいした、W・アレンの21本目の監督作品となるミステリアス・コメディ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Donovan Affair", "ドノバン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "フランク・キャプラ監督によるミステリー作品。金と女にだらしがなく、堕落した生活を送るドノバン。彼に恨みを持つ者は大勢いた。ランキンの妻で、ドノバンと不倫して以来彼に金を強請られているリディア。ドノバンにもてあそばれた、ランキン家の使用人メリー。ランキンの娘の恋人で、彼女とドノバンの関係を疑うデビッド。そして、借金の取り立て人ポーター。ランキン主催のパーティで、彼ら全員が一堂に会することになり……。", "1929-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIMIT OF LOVE 海猿", "LIMIT OF LOVE 海猿", "", "", "http://ecx.images-amazon.com/images/I/51M6X7NB9KL._SL210_.jpg", 117, "若き海上保安官たちの活躍を描いて話題となった映画「海猿」（2004）、その続編のTVドラマ「海猿 EVOLUTION」（05）に続くシリーズ。潜水士になって2年がたった仙崎は、異動先の鹿児島・第十管区で機動救難隊員として働いていた。恋人の環菜とは遠距離恋愛を続けていたが、さまざまな原因から関係がぎこちなくなっていることに悩んでいたある日、鹿児島沖で大型フェリーの座礁事故が発生する。仙崎はバディの吉岡らとともに現場に駆けつけるが、そこで想像を絶する光景を目撃する。", "2006-05-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALL　THE　PRETTY　HORSES", "すべての美しい馬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "現代アメリカ文学界が誇る人気作家C・マッカーシーのベストセラー小説を、B・B・ソーントンが映像化。1949年のテキサス州サンアンジェロ。父の死により何もかも失ったジョンは、友人のレイシーとともにカウボーイ生活を夢みてメキシコへ向かう。旅の途中、ならず者気取りの少年ジミーと知り合った二人は、一緒に旅を続けることにするが……。「ボーン・スプレマシー」のM・デイモンが、様々な苦難を乗り越えながらも真のカウボーイとしてたくましく成長していく男を好演。荒涼とした大地を駆けめぐる馬の群れや、調教される前の野生馬の映像も躍動感にあふれていて魅力的だ。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("レディ！レディ READY！LADY", "レディ！レディ READY！LADY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "６人の独立プロデューサーが結集して生まれた集団“アルゴ・プロジェクト”第１作。田舎で問題を起こして東京に出てきた姪と、独身キャリアウーマンのおば。ライフスタイルも考え方も違う二人が、共同生活をするうちに打ち解けていくさまを描く。当時25歳の薬師丸ひろ子が、自分奔放でワイルドな姪に扮した。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄甲子園", "地獄甲子園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "漫☆画太郎の人気コミックを映像化したコメディ。殺りくに満ちた外道野球に挑む、高校球児たちの戦いを描く。青春あり、ファンタジーあり、カンフーありの奇想天外な展開に圧倒される。甲子園を目指す星道高校野球部の予選一回戦の対戦相手は、ケンカ野球で一度は高校野球界を追われた悪名高い外道高校。案の定、死者が出るほど叩きのめされた星道ナインはワイルドな転校生、十兵衛を中心に再結集し、外道高校に殴り込む。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRIFTWOOD", "ドリフトウッド／硝子の檻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アイルランドの人里離れた浜辺に、重傷を負って漂着した青年。彼を自宅のコテージで介抱し、一方的な愛を強要する女彫刻家の屈折した心理を描くラブ・サスペンス。シチュエーションやヒロインのキャラクターに、「サイコ」「ミザリー」などの影響がモロに見受けられる作品で、個性派J・スペイダーが狂気の愛から脱出を試みる青年を演じている。ビデオ発売時タイトルは「ドリフトウッド　狂気の密室」。", "1997-01-01", "33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和鉄風伝・日本海", "昭和鉄風伝・日本海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "昭和末期の東北の地方都市を舞台に、任侠道を重んじる組織と、新興勢力の組織との抗争に巻き込まれていく若きチンピラヤクザの壮絶な生きざまを描いた任侠アクション。舛田利雄の助監督を務めてきた新人・佐々木正人監督が“ダウンタウン”の濱田雅功をはじめ、若手俳優のパワーを引き出した佳作。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京龍 TOKYO DRAGON", "東京龍 TOKYO DRAGON", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "荒俣宏の小説『闇吹く風』を映画化したSFホラー。実写とCGのデジタル合成を駆使して、中国の地相術“風水”をテーマに自然と人間の戦いを描く。偉大な風水師を祖父に持つ経営コンサルタント、黒田龍人を次々と襲う怪事件。祖父と違って“気”を見ることができない彼だが、雨が降り続く異常現象とある守り石との関係を突き止める。やがて助けを求めにやって来た不思議な能力を持つ少女、ミヅチの協力を得て、彼は石に封じ込められていた東京龍に戦いを挑んでいく。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無能の人", "無能の人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "異色の劇画作家として知られるつげ義春の同名漫画を、つげワールドに魅せられた竹中直人の初演出によって映像化。多摩川の川原で石を売る男とその家族を中心に、現代社会から落ちこぼれた人々をブラックユーモアを交えながらも温かい視点で見つめ、そのノスタルジックな映像がつげワールドにふさわしい絵となって再現されている。主人公の助川助三を竹中自身が演じ、その妻モモ子に風吹ジュン。ほかに山口美也子、マルセ太郎、神戸浩、大杉ら個性派が脇を固め、映画監督の神代辰巳が謎の鳥男に扮して映画初出演。そしてさらに豪華かつ異色な面々が１シーン（または１カット）ゲストで登場している。第48回ヴェネチア映画祭国際批評家連盟賞を獲得した。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZERO TOLERANCE", "ラスト・ターゲット 沈黙の復讐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "有能なFBIエージェントのジェフは、麻薬組織“白い手”の売人護送の任務につくが、組織から売人を奪われ、おまけに妻子を殺されてしまう。復讐に燃えるジェフは、緻密かつ大胆な計画のもとに、殺りくと破壊を繰り返しながら標的を一人一人血祭りにあげていく……。アメリカの理想とする、家族を愛し、正義感あふれるタフで強い男が、その愛と正義感ゆえに復讐マシーンと化して暴走する姿を、R・パトリックが熱演。「ターミネーター２」のT−1000に続くはまり役となった。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tempest", "テンペスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "　ニューヨークに住む有名な建築家フィリップは自分の人生を見つめ直すため、妻アントニアを残し、娘ミランダを連れてギリシャへ渡り、孤島で暮らし始める。やがて、そこで知り合った女性アリサと恋に落ちるフィリップ。しかし妻アントニアが島に乗り込んできて……。シェイクスピアの戯曲を現代風にアレンジしたコメディ。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Concert", "オーケストラ！", "", "", "http://image.eiga.k-img.com/images/movie/55164/315x210.jpg?4509", 124, "1980年、ロシア・ボリショイ交響楽団から多くのユダヤ人が連行され、それに反対した天才指揮者のアンドレイも楽団を解雇されてしまう。アンドレイはいつか復職する日を夢見て、30年にもわたり劇場清掃員として働いていたが、ある日パリのシャトレ座から送られてきた出演依頼を見つけ、偽のオーケストラを結成することを思いつく。主演はロシアの名優アレクセイ・グシコフ、共演に「イングロリアス・バスターズ」のメラニー・ロランほか。", "2010-04-17", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MODERN TIMES", "モダン・タイムス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "お金と機械にがんじがらめの“今の時代”を風刺したチャップリンの長編喜劇。大工場の工員チャーリーは、ベルトコンベヤーで運ばれてくる部品のネジをしめ続けるうちに手の動きが止まらなくなり、狂人と思われ病院に送られる。退院するとクビになり、街をうろつくうちに、工員のデモ隊のリーダーと間違えられて監獄行き。放免されて造船所で働くが、未完成の船を進水させてしまい、波止場で食べ物を盗む娘とともに逃亡する。川辺にボロ小屋を見つけて住みながら、二人は職探しに出かける……。トーキー嫌いのチャップリンが、『ティティナ』を歌い、初めて声を聞かせたことでも有名。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DONNIE BRASCO", "フェイク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "1982年７月26日、200件の起訴によってマフィアを壊滅へと追いやったFBI潜入捜査官J・D・ピストーネ。その後、マフィアに懸賞金を懸けられ隠遁生活を送る彼の、６年に及ぶ潜入捜査の全貌を描く実話の映画化。FBI捜査官ジョー・ピストーネが、ドニー・ブラスコという偽名でブルックリンのマフィア組織に潜入、メンバーの一人レフティ・ルギエーロの信用を得る。冷静で行動力のあるドニーは、瞬く間に組織の中枢へと登り詰めるが、盛りを過ぎたレフティは昇進の機会を狙って焦りを募らすばかりだった。レフティに友情以上の絆を感じ、胸を痛めるドニーの潜入が６年目を迎えたある日、彼はFBIから撤退を命じられる……。「モナリザ・スマイル」のM・ニューウェルによる歯切れよい演出が鮮やか。J・デップ、A・パチーノの好演を得て、骨太で悲哀のにじむ男と男のドラマに仕上げた。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hadewijch", "ハデウェイヒ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/55344/315x210.jpg?2477", 105, "13世紀の神秘主義的詩人ハデウェイヒの啓示を受けた修道女のセリーヌは、あまりに激しくハデウェイヒに感化されたため、修道院から追い出されてしまう。パリの大邸宅に戻ったセリーヌは、イスラム系の男性ヤシーヌとナシールに出会い、やり場のない気持ちを抱えたまま、強く2人に惹かれていく。監督は、「ユマニテ」「フランドル」などで知られるフランスの名匠ブリュノ・デュモン。フランス映画際2010にて上映。", "2009-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("酔夢夜景", "酔夢夜景", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "浅田次郎原作の『ラブ・レター』を彷彿とさせる哀しい設定の愛の物語。酒とギャンブルで首が回らない茂は金のために、不法滞在の追及から逃れたい中国人の美玲は日本国籍を得るために、偽装結婚をした。それから約１年後、アル中の茂に、美玲が強盗に刺されて死んだという知らせが入る。遺品の整理に振り回される茂は日記に綴られた彼女の思いを知り、二人で過ごしたわずかな時間を思い起こす。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GONIN", "GONIN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "バブル経済が崩壊し、多額の借金やリストラで社会からはじき出された５人の男たち。人生の瀬戸際に立たされた彼らは、生き残りを賭けて、暴力団の事務所を襲い、金庫に眠っている大金を強奪する。奪った金を山分けして、５人は新しい人生を踏み出すはずだったが、今度は怒りに燃える暴力団の凄惨な報復が始まった。５人は必死で対抗するが……。男たちの血で血を洗う壮絶な闘いが展開される、究極のバイオレンス・アクション・ムービー。監督自ら未公開シーンを追加した特別版もある。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Brave One", "ブレイブ　ワン", "", "", "http://image.eiga.k-img.com/images/movie/34204/315x210.jpg?1562", 122, "暴漢に婚約者を殺され、心身に深い傷を負ったエリカは、1丁の拳銃を手にする。自分たちを襲った男たちを見つけようと街をさまよう彼女は、自らの手で犯罪者たちを裁いていくが……。「告発の行方」「羊たちの沈黙」のオスカー女優ジョディ・フォスターが、復讐のため殺人に手を染める女性を演じたサスペンス・ドラマ。共演は「ハッスル＆フロウ」「クラッシュ」のテレンス・ハワード。監督は「クライング・ゲーム」のニール・ジョーダン。", "2007-10-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マザーウォーター", "マザーウォーター", "", "", "http://image.eiga.k-img.com/images/movie/55444/315x210.jpg?3100", 0, "「かもめ食堂」「めがね」「プール」など、人と場所との関係をテーマにした作品を撮り続けてきたプロジェクトが、豊かな水の流れを持つ街・京都を舞台に描いた人間ドラマ。ウイスキーしか置かないバーを営むセツコ、コーヒー店を開いたタカコ、豆腐屋のハツミら“水”にこだわる3人の女と、彼女たちにかかわる人々の日常をつづる。監督は本作が長編デビューとなる新鋭・松本佳奈。出演は小林聡美、小泉今日子、加瀬亮ら。", "2010-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("焼け石に水", "焼け石に水", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "70年代のドイツ。街で中年男レオポルドに声をかけられた青年フランツは、恋人アナとのデートに行くはずが、なぜか彼の誘いにのってレオポルドの家へ。そして、そのまま彼の家に居候してしまう。やがて、その家にアナやレオポルドの元恋人ヴェラもやってきて……。フランソワ・オゾン監督が、敬愛するライナー・ベルナー・ファスビンダーの未発表の戯曲を映画化。4幕からなる室内劇で、4人の男女の悲喜交々が描かれる。", "2001-07-21", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRI´A CUERVOS", "カラスの飼育", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "母親の死後、父親と暮らす幼い３人姉妹。次女のアナは、美しく優しかった母親を敬愛しており、母親の死が父親のせいと思いこんでいた。その父親がある日突然亡くなり、３人姉妹は伯母に育てられることに。伯母はしつけが厳しく、アナは母親が生きていた頃を懐かしく思い、しばしば母親の幻影を見る。ある日、伯母に叱られたアナは、父親にそうしたのと同様に伯母にも毒薬を飲ませようと決心するが……。幼い少女の視点から、過去と現在、そして大人になった未来からの回想という３つの“時制”を交錯させながら描くサスペンス・タッチの心理ドラマ。「ミツバチのささやき」のA・トレントの瞳は、ここでも映画全体を支えるほど魅力的。", "1975-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOORIII", "DOORIII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "大手保険会社の美人外交員・佐々木京は、昇進を目前に控え成績がいま一つ伸び悩み気味。上司には、客と寝てでも契約を取れと迫られるありさまだ。ある日、新規開拓のために飛び込みで入ったビルで、美貌の青年・美鶴と出会う。彼の不思議な魅力に魅了される京だが、美鶴と会った日から彼女の周りでは奇怪な出来事が起こり始める。シリーズ第３作。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗いところで待ち合わせ", "暗いところで待ち合わせ", "", "", "http://image.eiga.k-img.com/images/movie/33754/315x210.jpg?4740", 129, "人気若手作家・乙一による同名小説を「幻遊伝」の田中麗奈＆チェン・ボーリンの再共演で映画化したサスペンスタッチのラブ・ストーリー。交通事故で視力を失ったミチルは、父を病気で亡くして以来、駅の近くの家でひとり暮らしをしていた。ある日、駅で発生した殺人事件の容疑者アキヒロがミチルの家に忍び込み、彼女に気付かれないように暮らし始める。メガホンを取るのは、故・今村昌平監督の長男で今村作品の脚本も手掛けていた天願大介監督。", "2006-11-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金語楼の子宝騒動（あきれた娘たち）", "金語楼の子宝騒動（あきれた娘たち）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "６男７女の子持ちで３人の孫まであるという銀行の用務員・金太郎とその子供たちの話。戦争中の“生めよふやせよ”という時局スローガンを励行したお陰で13人の子持ちとなった金太郎が、新婚旅行に発つ娘の手に避妊薬を渡すというオチがつく。美空ひばりが歌の上手な小学生役で、母の臨終を前にしてしんみりと自慢のノドを聴かせる。DVDは「新東宝映画傑作選　BOX　FUNNY&ELEGY　編」に収録。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長意外伝 大暴れ三太郎笠", "次郎長意外伝 大暴れ三太郎笠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "第１作と同じスタッフの手によるお笑い時代劇。前作で知り合った由貴路の仇討ちの介添役を親分から命じられた灰神楽の三太郎は、さっそく彼女と旅に出た。しかしある宿場町で、けんちんの豚十という男の怒りを買って彼の放った刺客たちに命を狙われることになり、その度ごとに由貴路が三太郎を救うのだが……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Demoiselles De Rochefort", "ロシュフォールの恋人たち　デジタルリマスター版", "", "", "http://image.eiga.k-img.com/images/movie/54204/315x210.jpg?3184", 127, "カトリーヌ・ドヌーブとドヌーブの実姉フランソワーズ・ドルレアックが双子の姉妹役を演じた、1966年のミュージカル映画のデジタルリマスター版。監督は「シェルブールの雨傘」のジャック・ドゥミで、ジーン・ケリー、ジョージ・チャキリスもアメリカから参加。年に1度のお祭りで賑わうロシュフォールの街で、美しい双子の姉妹ソランジュとデルフィーヌは運命の恋人の出現を待っていた。", "2009-01-31", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A STATE OF MIND", "ヒョンスンの放課後", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "北朝鮮の中産層の日常生活が垣間見えてくるドキュメンタリー。「奇蹟のイレブン1966　北朝鮮VSイタリア戦の真実」のダニエル・ゴードン監督が北朝鮮の国家行事“マスゲーム”に出演する少女を8ヵ月に渡り密着取材する。今の北朝鮮の状況を映し出す貴重な映像が満載だ。ピョンヤンで暮らす13歳のヒョンスンは体操が大の得意。そんな彼女は北朝鮮最大のイベントである“マスゲーム”への出演が決まる。しかし、彼女は練習をついついサボってしまい……。", "2006-04-22", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MACK THE KNIFE", "三文オペラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "『三文オペラ』の４度目の映画化。今回は舞台でも同じ役に扮したR・ジュリアが主役のマックを演じている。歌は吹き替えなしで、ワイルの曲から13曲を使用。1837年、ヴィクトリア女王即位目前のロンドンを舞台に、犯罪者マックとそれを取り巻く人々のドラマが描かれる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COMME UN BOOMERANG", "ブーメランのように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "誤って警官を射殺してしまった息子を救うため、今や大会社の社長となったジャックが、ギャングの本性を取り戻して囚人護送車ごと息子を奪回、イタリアへの逃亡を図る……。ギャングに戻っていくにつれて精彩を放ち始めるドロンのキャラクターが魅力。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版ペンギンの問題　幸せの青い鳥でごペンなさい ", "劇場版ペンギンの問題　幸せの青い鳥でごペンなさい ", "", "", "http://image.eiga.k-img.com/images/movie/54544/315x210.jpg?2912", 0, "永井ゆうじによる人気ギャグ漫画を映画化。木下ベッカムはきりかぶ小学校に通う平凡なペンギンの小学生。ある日、世界を氷漬けにしようと企む南極の氷の王・氷川ニコルソンがベッカムたちの暮らすきりかぶ町にやって来る。ベッカムは、山田なおとや松井ゆみらと協力し、ニコルソンの野望を阻止しようと奮闘するが……。", "2009-09-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shrink", "精神科医ヘンリー・カーターの憂欝", "", "", "http://ecx.images-amazon.com/images/I/51USvEz-GPL._SL210_.jpg", 105, "心を病んだ精神科医の苦悩と再生を、「アメリカン・ビューティー」のオスカー俳優ケビン・スペイシー主演で描いた人間ドラマ。ハリウッドのセレブたちも多く受け持つ精神科医ヘンリー・カーターは、著作もベストセラーになり、テレビにも出演する人気医師。しかし、私生活では妻の自殺をきっかけに精神のバランスを崩し、アルコールやタバコ、ドラッグに溺れる日々を送っていた。そんなカーターの前に、母を自殺で亡くした女子高生ジェマが現われ……。", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("馬路天使／STREET　ANGELS", "街角の天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1930年代の大都会・上海を舞台に、そこに生きる下層階級の人々の生きざまを、あくまでも希望に満ちたタッチで描いた傑作。小雲と小紅の姉妹は人にだまされて上海の売春窟に売られてしまう。小紅のアパートには、窓の向かいに小平というラッパ吹きの青年が住んでいて、二人は次第に親愛を深めていく。しかし、やくざの成龍という男が彼女を自分のものにしようと狙っていて……。中国映画が最も充実していた時代といわれる1930年代映画の代表作。全編にハリウッド映画の影響が横溢している。", "1937-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Big Momma's House 2", "ビッグママ・ハウス2", "", "", "http://ecx.images-amazon.com/images/I/51N%2Be30i5wL._SL210_.jpg", 99, "「バッドボーイズ」シリーズのマーティン・ローレンス主演による大ヒットコメディの第2弾。FBIの敏腕捜査官マルコムは家族のために安全な内勤に転属するが、仲間が殺されたことを知り単独捜査を開始。またしてもビッグママに変装して家政婦に成りすまし、容疑者とされるシステムエンジニア、フラー宅に潜入する。そこでは、それぞれ問題を抱える3人の子どもたちがマルコムを待ちうけていた。", "2006-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELEPHANT　WALK", "巨象の道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "黄金のハリウッド・スターの道を歩み始めたE・テイラーが主演を務めた往年のラブ・ストーリー。男女の三角関係を描いた娯楽作。英国人の主人公の亡き父は、インドで紅茶の大農園を経営し成功した。そんな親の跡を継ぐために、彼は妻を伴いインドに帰って来た。ところが、彼がケガをしてしまい、農園技師が留まり仕事を続けたが、妻の心は夫と技師の間で揺らぎ……。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Silent Hill", "サイレントヒル", "", "", "http://image.eiga.k-img.com/images/movie/1654/315x210.jpg?2999", 126, "シリーズ4作目まで製作されている人気ホラーゲームの1作目を完全映画化。赤ん坊の頃に養女としてシャロンを引き取ったローズ夫妻。だが、9歳になったシャロンが「サイレントヒル…」と呻き声を発するようになる。ローズはアメリカ・ウエストバージニア州にサイレントヒルという街があることを探り当て、シャロンと共にそこを訪れるが、街はすでに廃墟と化していた……。監督は「ジェヴォーダンの獣」のクリストフ・ガンズ。", "2006-07-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("New Nightmare", "エルム街の悪夢　ザ・リアルナイトメア", "", "", "http://ecx.images-amazon.com/images/I/514H0F40NHL._SL210_.jpg", 112, "　大ヒット・ホラー「エルム街の悪夢」シリーズ終了後のこと。第一作で主演した女優へザー・ランゲンカンプは悪夢に悩まされていた。しかも自宅にはフレディからの電話が。やがてへザーは「エルム街の悪夢」の新作製作が決定したことを知らされるが、まもなく特殊効果マンである彼女の夫が事故死し、幼い息子にも異変が。甦ったフレディが映画から抜け出し、現実世界を鮮血に染める！　ヘザーを始め、俳優、製作者、そして監督までが本人役で出演するシリーズ番外編。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Trishna", "トリシュナ", "", "", "http://image.eiga.k-img.com/images/movie/57344/315x210.jpg?3277", 117, "", "2011-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chapter Two", "第2章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "妻を亡くした中年の作家が、夫と別れたばかりの女優の卵と知り合い、強引にデートを申し込む。二人の仲は急速に発展して、結婚にまでこぎつけるが……。自らの体験をもとにしたサイモンの脚本で、男と女の愛の機微を描いた私小説的恋愛映画。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST TIME I SAW PARIS", "雨の朝パリに死す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "第一次大戦を背景にしたフィッツジェラルドの短編『バビロン再訪』を、舞台を第二次大戦後に置きかえて映画化。新進作家と結婚したビッキーの奔放な生き方と悲劇的な最期を描いたメロドラマ。官能的なリズの妖しさはたとえようもない。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遊園驚夢", "華の愛・遊園驚夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "舞台は1930年の中国、蘇州。男装の麗人と、彼女を慕う元歌姫の切ない関係を、退廃的ムードを漂わせて優雅に綴った時代絵巻。中国の伝統戯曲“崑劇”の歌手ジェイドは、貴族の名家に嫁ぎ娘をもうける。第５夫人としての寂しさと孤独を抱えたジェイドの心を癒すのは、夫の従妹で男装の麗人ラン。進歩的で自立した女性ランと愛のような絆を深めていくジェイドだったが……。赤を貴重とした鮮やかな色彩感覚、ゴージャスな衣装やアンティーク家具、小物の数々に思わず溜息が漏れる。あどけなさと美しさをたたえた表情でジェイドを演じた宮沢りえは、2001年のモスクワ国際映画祭で、最優秀女優賞を獲得した。", "2000-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今日と明日の間で", "今日と明日の間で", "", "", "http://image.eiga.k-img.com/images/movie/56744/315x210.jpg?2518", 88, "世界的振付師のモーリス・ベジャールやマシュー・ボーンらに絶賛され、現代日本を代表するトップバレエダンサー・首藤康之の姿に迫ったドキュメンタリー。9歳でバレエと出合い、15歳で東京バレエ団に入団してバレエに人生を捧げてきた首藤が、これまで語らなかった自らの胸のうちを明かす。また、2010年の全ステージとその稽古風景が収めれているほか、歌手の椎名林檎が音楽を手がけた新作ソロバレエ「Between Today and Tomorrow」を作中で初披露する。", "2012-01-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUR VERY OWN", "われら自身のもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ハイスクール卒業間近の少女が、ある時、自分が養女であることを知る。ショックを受けた彼女は、本当の親を捜すための旅に出るが、実の母はすでに再婚をしており、彼女の出現に当惑する。彼女は再びショックを受け……。A・ブライスが日本に初めて顔見せしたメロドラマ。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH STRUNG", "ジム・キャリーinハイ・ストラング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "気難し屋で偏執狂の絵本作家セインの前に、ある日デビルが現れ、“今夜、午後８時きっかりに何かが起こる”と予言する。セインは思わぬデビルの出現に自分の正気を疑う。しかし、セインの災難はそれだけでは終わらない。親友や編集者、サラリーマンやヘビメタ野郎など、いろんな連中が彼の孤高の世界に介入してくる。そして、とうとう運命の８時を迎えるが……。スタンダップコメディアンのS・エドカークと、相も変わらずハイテンションのJ・キャリーの息ぴったりの掛け合いが見どころ。TVフィーチャー作品。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Edison", "F.R.A.T. 戦慄の武装警察", "", "", "http://ecx.images-amazon.com/images/I/51zg8g5LeDL._SL210_.jpg", 99, "", "2005-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLEOPATORA", "クレオパトラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "紀元前48年、ローマ執政ジュリアス・シーザーがエジプトに大軍を率いて上陸。エジプトの女王クレオパトラは、その美貌でシーザーを魅了し、後援を約束させることでエジプトをローマの勢力下に置かせた。だがシーザーは暗殺され、代わって彼の友人アントニーが執政の座に。エジプト遠征を主張する彼は、毒婦クレオパトラの征伐へと向かうが、その美貌の前で決意も揺らぎ……。名匠セシル・B・デミルが手掛けた壮大な古代叙事詩。「或る夜の出来事」のC・コルベールが絶世の美女クレオパトラに扮している。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Emperor's Club", "卒業の朝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "厳格な寄宿学校を舞台に、教師と教え子の葛藤を描いた人間ドラマ。聖ベネディクト男子校で教鞭をとるハンダードは信念を持って生徒の教育に当たっていたが、反抗的な転校生ベルの出現によって、それは揺らぎ出す。やがて退職を間近にしたハンダートは、成人したベルと再会するが……。アカデミー賞俳優K・クラインが、苦悩の教師をリアルに好演。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジム", "ジム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "日本ジュニアバンタム級の元チャンピオン、北澤鈴春が後進を指導する“北澤ジム”の人間模様を収めたドキュメンタリー。６年間の歳月をかけて20歳前後の若い４回戦ボクサーたちの練習風景、彼らの様々な悩みや迷いを記録。ジムに通った経験を持つほどのボクシング・ファンだという人気俳優、寺島進がナレーションを務めている。監督は、これが劇場映画デビューとなる山本起也。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千里走単騎", "単騎、千里を走る。", "", "", "http://image.eiga.k-img.com/images/movie/1684/315x210.jpg?2236", 108, "主演は日本映画を代表する男優・高倉健。監督・原案は、「紅いコーリャン」（88）でベルリン国際映画祭金熊賞、「あの子を探して」（00）でベネチア国際映画祭金獅子賞を受賞、最近は「HERO」「LOVERS」などで活躍する中国の名匠チャン・イーモウ。静かな漁村で暮らす男が、長年疎遠だったひとり息子が重病で余命わずかと知って、息子が中国の俳優と交わした約束を果たすため、ひとりで中国に渡る。だが、さまざまな困難が彼を待ち受けていた。", "2006-01-28", "8,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兵隊やくざ・強奪", "兵隊やくざ・強奪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "大映での最終作。終戦直後の満州、大宮と有田はヒョンなことから捨て子を拾い面倒を見るハメになった。赤ん坊を背負い３人旅を続ける一行は、10万ドル金貨をめぐって繰り広げられる大争奪戦に巻き込まれる。戦争という背景がないぶん、面白さが減じた。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Michael", "マイケル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "　「めぐり逢えたら」で大人のラブストーリーを描いたノーラ・エフロンのファンタジックコメディ。タブロイド紙の記者フランクのもとに、「うちに天使がいる」という手紙が届く。クリスマスのネタにすべく、新人記者ドロシーと共にアイオワへ向かったが、目にしたのは翼のはえた中年男だった。「パルプ・フィクション」でギャング役を好演したジョン・トラボルタが一転、天使役に挑戦。記者役は、「グリーン・カード」のアンディ・マクダウェルとアカデミー賞俳優ウィリアム・ハート。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("11HARROWHOUSE", "新おしゃれ泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ダイヤの市場を一手にコントロールするダイヤ公社の大金庫を狙うチェサーとマレン。しかし厳重な防犯体制をとっている金庫から、果たしてお目当てのダイヤを奪い取ることはできるか。J・A・ブラウン原作の『ハロウハウス11番地』をC・グローディン自身が脚色し、主演も果たした意欲作。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野菊の如き君なりき", "野菊の如き君なりき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "原作は伊藤左千夫の有名小説『野菊の墓』。思い出の信州の故郷を何十年か振りに訪れた一老人の回想のなかに、旧家に生まれ育った少年時代の主人公と年上のいとこの、淡く美しく、そして悲しい恋の物語を描く。木下惠介の筆致は、最も脂の乗り切った時期の作品だけに、練達のリリシズムを感じさせる。若い二人の恋心は封建的な道徳観を持つ大人たちによって無理やり引き裂かれ、少女は他家へ嫁がされる。やがて少女は胸を病み、少年の手紙を抱いて死ぬ。老人は少女が好きだった野菊の花を墓前に供えるのだった……。回想場面を白地の楕円形のマスクで囲い、古い写真帖をめくるような手法が効果的で、詩情あふれる撮影も相まって涙なくしては見られぬ名作となった。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SECRET OF THE ICE CAVE", "アドベンチャー・キッズ 氷河洞窟の謎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "南米を舞台にしたアクション・アドベンチャー。都会っ子のアレックスは、南米の研究所にいる学者の母に会うため飛行機に飛び乗る。ところが飛行機が墜落、かろうじて脱出に成功したアレックスは、偶然にも宝のありかが書かれた地図を発見し、男まさりの美少女オナを連れて幻の洞窟へ向かう……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("学校の怪談３", "学校の怪談３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "大ヒット・シリーズの第３作。今回は「ガメラ２／レギオン襲来」の金子修介が監督を務める。槙町小学校の女教師・八橋は図工室で美しい鏡を見つける。その鏡には昔亡くなった生徒の霊“タイチ”が棲んでいると噂されていた。好奇心に駆られた生徒４人は鏡を見ようと夜の学校に忍び込むが、その場に居合わせた八橋たちとともに鏡の世界に引きずり込まれてしまう……。妖怪たちのキャラクターやSFXも楽しめるが、ドラマとしても子どもたちの成長や心の交流が描かれており、さわやかな印象を残している。DVDは「学校の怪談　DVD-BOX」に収録。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雪の喪章", "雪の喪章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "金沢の金箔商家に嫁いだ妙子は、夫と女中のせいとの不倫を知り、苦悩の日々を送ることになった。そんな彼女をひそかに愛し続ける番頭の群太郎。やがて戦争が始まるとともに、４人の運命は大きく流されていくことになる。三隅研次監督が凄絶な映像美学を駆使して描く文芸映画。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HARD WAY", "ハード・ウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "スランプ気味のアクション・スター、ラングは、次回作の刑事役の役作りをするため、N.Y.市警の腕利き刑事モスの捜査にへ強引に同行する。連続殺人鬼クラッシャーを追い詰めていくモスの捜査を、いつもラングが邪魔してしまい……。M・J・フォックス、J・ウッズが凸凹コンビに扮した、アクション・コメディ。硬派の刑事を演じる、J・ウッズの好演が光る。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COUNTRYMAN", "カントリーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "南の島の選挙でCIAのスパイ呼ばわりされた若者を、同地に生きる“カントリーマン”が救出する。ジャマイカが生んだレゲエの神様B・マーリィの企画による、ラスタファリズム（自然回帰、物質主義否定）に支えられたレゲエ映画。", "1980-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("簪", "簪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "下部温泉のある旅館は身延山への参詣人でごった返している。ある朝、帰還兵・納村が湯につかっている時に足に簪を刺して大ケガを追う。やがて東京からケガのことを知った簪の落とし主・恵美が詫びにやってくる。納村は恐縮し、彼女の身の上も知らぬまま、ほのかな思いを抱く。恵美は東京での妾としての生活に嫌気がさし、ここに逗留して将来を考えることに……。原作は田舎生活をユーモラスに描く作風で知られる井伏鱒二。斎藤達雄、日守新一らのコメディアンぶりがかなり笑わせる。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRACULA", "ドラキュラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "20世紀初頭のイギリスを舞台に、次々と美女を毒牙にかけるドラキュラ伯爵と、我が身を捨て乙女を救おうとするL・オリヴィエ扮するヘルシング教授の死闘を描いた作品。最愛の娘ルーシーを生贄にされた教授の怒りは頂点に達するのだが……。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動戦士ガンダムII 哀・戦士編", "機動戦士ガンダムII 哀・戦士編", "", "", "http://ecx.images-amazon.com/images/I/51ET3552ATL._SL210_.jpg", 134, "1979～80年に放映されたTVアニメ「機動戦士ガンダム」を再編集した劇場3部作の第2部。否応なしに戦いに巻き込まれたアムロ・レイと、新鋭戦艦ホワイトベースの乗組員たちは、地球連邦軍本部のジャブローを目指す。しかし、戦局はますます拡大。アムロは敵の士官ランバ・ラルとの戦いなどを通じ、戦士として成長していく。", "1981-07-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("放浪記", "放浪記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "林芙美子の自伝的小説を、林文学の翻案では定評のある巨匠・成瀬巳喜男が映画化した作品。主演の高峰秀子は、自由奔放な女性という従来の林像を覆し、貧困と戦いながらのし上がる強い女の側面を強調した。甘い二枚目役で売り出した宝田明が演技開眼。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BE＾TE HUMAINE （ JUDAS WAS A WOMAN）", "獣人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "“呪われた血”を受け継ぎ、普段は常人であるが発作を起こすと女を殺したくなる衝動に駆られるという、異常な精神的苦痛に悩む主人公ジャック。鉄道の機関士として働く彼がある日、恐ろしい事件に遭遇したことから悲惨な末路をたどっていく。この不幸な運命を背負った男を名優J・ギャバンが力演し、彼の代表作の一つとなった。原作はフランス自然主義文学の作家E・ゾラによる連作小説の一編。", "1938-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARENTHOOD", "バックマン家の人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "マーティンが、“子育て”に全力投球する現代の父親像を熱演。親に裏切られる子供。我が子にあざむかれる親。４世代、４家族の悲喜こもごもの日常を、時に優しく、時にホロ苦く、ユーモアたっぷりに綴った1980年代のホーム・ドラマ。ラストを飾る、３人の新生児を囲んでの全家族集合が圧巻だ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・悪名", "新・悪名", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "復員してきた朝吉は、戦後の混乱した世相の中で、闇市に生活する庶民の味方となり、戦後派ヤクザの無法に単身立ち向かう。田宮二郎が前作で死んだモートルの貞の実弟・清次に扮して登場し、朝吉の子分になる。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON THE LINE", "オン★ザ★ライン／君をさがして", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "アメリカのポップグループ、イン・シンクのメンバー、L・ベースが映画初出演で、主演と製作総指揮を担当したロマンティック・コメディ。高校時代の苦い思い出から恋にすっかりオクテの広告マン、ケヴィンはある時、通勤電車の中で音楽の趣味やジョークの相性もバッチリ合う女性と運命の出会いをする。しかしいつもの臆病風に襲われて、名前も電話番号も聞かずに別れてしまい大後悔。そんな彼に悪友たちも呆れ顔だ。再会を願ったケヴィンは、思い切った方法で彼女を捜し出すことにする。友人役でイン・シンクのJ・ファトーンが出演しているほか、ソウルシンガーのアル・グリーンが本人役で顔を見せる。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dr. Seuss' Horton Hears a Who!", "ホートン　ふしぎな世界のダレダーレ", "", "", "http://image.eiga.k-img.com/images/movie/53444/315x210.jpg?3000", 88, "世界中で読み継がれてきたドクター・スースの名作絵本を、「アイス・エイジ」の20世紀フォックス・アニメーションがCGアニメ映画化。小さなホコリの上にある平和なダレダーレの国が、風に流されて空中に放り出されてしまった。住人たちが助けを求める声を偶然耳にした心優しいゾウのホートンは、彼らを安全な場所へと連れて行こうと奮闘するが……。主人公・ホートンの声を担当するのはジム・キャリー。", "2008-07-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("原爆の子", "原爆の子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "処女作「愛妻物語」（1951）を撮ったあと、独立プロ“近代映画協会”を設立した新藤兼人監督の自主製作第１作。原爆を直接に取り上げた劇映画としては初めてのもので、労組の協力を得て完成させた。主演の乙羽信子はこれを機に女優開眼。原爆投下の中、一人生き残った女教師が数年後広島に戻り、かつての教え子たちの消息を尋ねていく物語。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不食の時代　愛と慈悲の小食", "不食の時代　愛と慈悲の小食", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "断食療法の第一人者・甲田光雄医師の治療によって難病を乗り越えた人々を題材にしたドキュメンタリー。針灸師の森美智代さんは、甲田医師の指導の下、1日1杯の青汁のみで生活し続けることで、幼少期から苦しんできた脊髄小脳変性症を克服。その素顔とライフスタイルを、山田まりや主演による回想ドラマを交えながら紹介していく。メガホンをとったのは、ひきこもりの若者たちを題材にした「ストーンエイジ」で国内外の注目を集めた白鳥哲監督。", "2010-10-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING RALPH", "ラルフ一世はアメリカン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "イギリスのロイヤル・ファミリーが全員感電死、あわてたイギリス王室関係者たちは、ただちに王位継承者を探し始める。そしてようやくラルフ・ジョーンズという男がこの王座に座るが、彼はなんとアメリカ人で……。バッキンガム宮殿を舞台に、明るくにぎやかな男が国王となって悪戦苦闘する姿を描くファンタスティックなコメディ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遥かな時代の階段を", "遥かな時代の階段を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "林海象＝永瀬正敏コンビによる私立探偵濱マイク３部作の第２作。舞台は、前作と同じ横浜・黄金町。川の利権をめぐってもめていた黄金町に、マイクと妹の茜を捨てた母親で、今はストリッパーのリリーが突然帰ってきたことから話は始まる。リリーが現れて以来、マイクはどこか普通じゃない、と茜が思っていたある日、親友の母親が川で死んだ。間もなく、マイクも姿を消す。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Monkey Business", "モンキー・ビジネス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "製薬会社に勤めるバーナビーは社長から若返り薬の開発を急かされていた。ある時、実験用のチンパンジーが檻から抜け出し、バーナビーの仕草を真似て、手当たり次第に薬品を調合すると冷水器に流してしまう。それを飲んだバーナビーは突然若返り始めて……。人気沸騰前のマリリン・モンローがセクシーな子猫的役割で出演しているが、監督も製作陣も、その後彼女が大スターになるとは思いもしなかったという。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les femmes du 6eme etage", "屋根裏部屋のマリアたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "フランス映画祭2011にて「6階のマリアたち」のタイトルで上映。12年、劇場公開。", "2012-08-31", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CONNECTION", "ザ・コネクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "マンハッタンにある古いビルの一室。廃屋のようなその狭い空間で繰り広げられるジャンキーたちの日常と、ジャズ・バンドのメンバーでもある彼らの練習風景をドキュメンタリー・タッチで捉えた異色作。1950年代に数々の実験映画に才能を発揮した女流監督S・クラークの長編デビュー作で、公開当時、風俗を乱す映画として告発され注目を浴びた。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cape Fear", "ケープ・フィアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "1962年に作られたJ・リー・トンプソン監督の「恐怖の岬」。この作品のリメイクを熱望したスティーヴン・スピルバーグが、M・スコセッシを監督に指名して実現させた、本格派サスペンス・スリラー。14年ぶりに出所したレイプ犯マックスは、異常なる復讐心を抱いて弁護士のボーデン一家に近づく。彼は14年前にマックスの実刑を決定づけた男。やがてマックスは彼の愛人や妻、そして娘にジワジワと魔手を伸ばしていく……。どんどんエスカレートしていく悪らつな復讐の手段、鮮血がほとばしるバイオレンス等、恐怖心を駆り立てるシーンの連続。全身にイレズミを施し、迫真の演技を見せるデ・ニーロの悪役も圧巻だ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京ゾンビ", "東京ゾンビ", "", "", "http://ecx.images-amazon.com/images/I/51YF77Z0JNL._SL210_.jpg", 103, "独特の画風とシュールな内容でカルト的人気を誇る、花くまゆうさくの同名漫画を実写化した異色作。ゾンビに占拠された東京を舞台に、地味な柔術の練習に余念のない二人の男の熱い友情が描かれる。注目は何といっても浅野忠信と哀川翔の初共演。現在の日本映画界を代表する両者が、原作キャラ同様のハゲ頭とアフロヘア姿で見せる息の合った掛け合いがとにかく絶妙だ。町工場で働くフジオとミツオ。強い友情で結ばれる二人は、仕事そっちのけで柔術の練習に明け暮れていた。そんな折、東京中にゾンビが繁殖。彼らはその脅威に立ち向かうが……。", "2005-12-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY WAY（THE WINNERS）", "マイ・ウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "かつてマラソン選手だった主人公は、“絶対に他人に負けてはならない”をモットーに事業に成功。しかし、それを家族にも強要したため、妻子は家を出てしまう。一人になった彼は、再びレースに挑む……。同名テーマ曲が大ヒットした、ヒューマン・ドラマ。", "1975-01-01", "3,67", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Far from Heaven", "エデンより彼方に", "", "", "http://image.eiga.k-img.com/images/movie/1054/315x210.jpg?4677", 107, "「ベルベット・ゴールドマイン」で私的グラム・ロック観を描いたトッド・ヘインズ監督の新作は、50年代のメロドラマを克明に再現してアカデミー主演女優賞、脚本賞、撮影賞、作曲賞にノミネート。50年代のエレガンス溢れる衣装は、前作でも組んだデレク・ジャーマン映画の常連、サンディ・パウエルが担当。撮影は「ヴァージン・スーサイズ」のエドワード・ラックマン。音楽は「荒野の七人」の大御所エルマー・バーンスタイン。", "2003-07-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("虹をつかむ男", "虹をつかむ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "渥美清が亡くなったために終了した“男はつらいよ”シリーズに代わって、1997年、松竹の正月映画として登場した人情ドラマ。大学を出たものの職もなく、ツキもない平山亮は、親と衝突して家を出る。瀬戸内海の小さな町にやって来た亮は、古ぼけた映画館に雇われることになる。映画館は社長の白銀活男と映写技師の常さんの二人で頑張っていた。活男は、常連の八重さんに想いを寄せているが、映画のようには愛を告白できずにいた。亮は活男にハッパをかけるが、活男は煮え切らない。おまけに、借金がかさみ映画館を閉めると言い出して……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Philadelphia", "フィラデルフィア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "一流法律事務所に勤務する辣腕弁護士ベケットは自分がエイズに感染したことを知る。やがて会社はベケットに解雇を宣告。エイズ患者に対する不当な差別に腹を立てた彼は訴訟を決意し、以前は敵として法廷で闘ったことのあるミラーに弁護を依頼する。ベケットの姿に心を打たれたミラーはそれを引き受けることに。しかしこの裁判は日に日に衰弱していくベケットとその関係者にとって過酷なものになっていく……。自由と兄弟愛の街フィラデルフィアで展開される裁判の行方を描いたドラマ。", "1994-04-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("American Madness", "狂乱のアメリカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "大恐慌を背景に、理想に燃える銀行員の姿を描いた社会派ドラマ。監督は後の名匠フランク・キャプラ。ユニオン・ナショナル銀行の頭取ディクソンは、自らの信念に基づいた人道的な融資を繰り返し、銀行役員たちから睨まれていた。ある日、銀行が強盗に襲われたという噂が尾ひれをつけて街中に広がり、預金を引き出そうとする人々で大パニックになる。ディクソンは部下のマットらと共になんとかこの危機を脱け出そうとするが……。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINAL CUT", "ファイナル・カット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "俳優J・ロウが実名で登場する擬似ドキュメンタリー。彼の映画学校時代の仲間D・アンシアーノとR・バーディスが監督、元妻S・フロストをはじめ出演者全員が本人として出演する。突然亡くなったジュードの告別式。親しい友人たちに妻は、彼が生前に撮ったというプライベート・ビデオを回し始める。そこに写っていたのは、隠し撮りした友人たちの人には言えない真実の姿。次々と映し出されるショッキングな映像は、やがてジュードの意外な死の真相も明らかにする。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十六歳の戦争", "十六歳の戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "かつて米軍の大爆撃によって壊滅状態に陥った豊川市。現在は復興したかに見えるが、実は戦争の傷は深く未だ癒えてはいない、と実験映画作家の松本俊夫が訴えかける自主製作映画。主役は当時売れっ子のフォーク歌手・下田逸郎。その相手にデビュー間もない秋吉久美子が抜擢されている。1973年に製作されたが、公開されたのは1976年。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FORBRYDELESENS ELEMENT", "エレメント・オブ・クライム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "警部フィッシャーの捜査哲学は、犯罪者の視点に立って事件を捉え、解決に導くというものだった。しかし、あまりにも犯罪者の心理を分析する能力に優れていたため、犯罪の渦中に巻き込まれるハメに……。セピア調の画面が印象的な一編。", "1984-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マインド・ゲーム", "マインド・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "カルト的人気の同名コミックを、“クレヨンしんちゃん”などで知られる天才アニメーター、湯浅政明が映像化。意気地なしのダメ男が神の啓示を受け、アグレッシブな冒険へと飛び出す姿を、時制や空間が交錯するブッ飛び映像で描く。特にクライマックスの怒涛のカタルシスは圧巻。アニメ表現の可能性を切り開く傑作だ。マンガ家志望の西は、子供の頃から想い続けたみょんちゃんと再会。しかし彼女は体育会系の恋人と婚約中。さらにみょんちゃんを追う借金取りにあっけなく射殺されてしまう。死んだ西の前に、ヘンテコな神様が現れて……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le fabuleux destin d'Amélie Poulain", "アメリ", "", "", "http://image.eiga.k-img.com/images/movie/1254/315x210.jpg?1374", 121, "神経質な両親の元で育ち、空想の中で遊ぶのとこっそり悪戯するのが得意になったアメリ。22歳になり、モンマルトルのカフェで働く彼女は、青年ニノに出会って心ときめくが、どうしたらいいか分からず悪戯を仕掛ける。「デリカテッセン」「ロスト・チルドレン」のジュネ＆キャロのジュネ単独の初長編。セット撮影の多かった彼が、今回はロケ撮影で後加工した作り込んだ映像を展開。美術は「ロスト・チルドレン」のアリーヌ・ボネット。", "2001-11-17", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Black Snake Moan", "ブラック・スネーク・モーン", "", "", "http://image.eiga.k-img.com/images/movie/34154/315x210.jpg?1500", 115, "妻に捨てられた孤独な男とセックス依存症の女の奇妙な交流を、サミュエル・L・ジャクソン＆クリスティーナ・リッチ主演で描いた衝撃のヒューマン・ドラマ。「ハッスル＆フロウ」のクレイグ・ブリュワーがメガホンを取る。アメリカ南部に住む元ブルースマンのラザラスは、道路で血だらけになって倒れていた女レイを家に連れ帰る。レイの深い心の闇を察し、彼女を救うことが自らの使命だと考えたラザラスは、彼女を鎖に繋いで“治療”を開始するが……。", "2007-09-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／WHY HAS BOOH-DHARMA LEFT FOR THE EAST？", "達磨はなぜ東へ行ったのか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "撮影に８年の歳月をかけ、ペ・ヨンギュン監督が撮り上げた、人間の生と死を問う力作。山奥の小さな寺。そこに暮らす老使ヘゴク、盲目の母と妹を町に置いてきた青年僧キボン、そして身寄りのない少年ヘジン。３人の生活は、ヘゴクが死期を悟ったことから、変貌していき……。", "1989-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER STAND DER DINGE", "ことの次第", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "ポルトガルの海岸で、ドイツの監督フリッツがアメリカ資本のSF映画を撮影中。連絡の取れない製作者を訪ねて監督自らがロサンゼルスへ飛ぶが……。ベンダースがコッポラ製作の「ハメット」と同時期に描いた映画製作の混乱の内幕で、ベネチア映画祭で金獅子賞を受賞した。主人公の映画監督フリッツは後に「リスボン物語」にも登場する。", "1983-11-30", "45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shadow of the Vampire", "シャドウ・オブ・ヴァンパイア", "", "", "http://image.eiga.k-img.com/images/movie/1664/315x210.jpg?1249", 93, "22年製作、ドイツ表現主義を代表するモノクロ・サイレント映画「ノスフェラトウ」は吸血鬼映画の元祖。79年にクラウス・キンスキー主演で同名のリメイクも作られている。原案は脚本のスティーブン・カッツが構想。TV脚本を書く彼は本作が映画第1作。脚本を読んだニコラス・ケイジが興味をもっていた監督に話を持ち込み、製作を担当した。監督は91年の長編第1作「Begotten」が米「タイム」誌で年間ベスト10に選ばれた後、MTVや舞台演出で活躍、本作が長編映画第2作。", "2001-08-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新網走番外地・さいはての流れ者", "新網走番外地・さいはての流れ者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "足の悪い子供の治療費をかせぐために漁師になった末広勝治は、漁師町の悪徳網元のあまりの横暴ぶりに、ついに立ち上がる。“グッド・バッド・ガイ”から“グッド・ガイ”になった主人公は魅力がない。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SECRETS ＆ LIES", "秘密と嘘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "家族に秘めていた秘密と嘘の間で揺れ動く、心の葛藤と家族愛を描いた感動作。ロンドン近郊の下町のアパートで暮らすシンシアと、父の顔を知らないロクサンヌは二人きりの親子。だが、実はシンシアには16歳の時に出産し、顔も見ずに養子に出したもう一人の子供がいた。ある日、シンシアに自分の娘だと名乗るホーステンから電話があり、対面することになる。シンシアは黒人であるホーステンに戸惑いながらも頻繁に会うようになる。やがて彼女は、写真家である弟モーリスの家で開く娘の誕生日パーティーにホーステンを誘い、そこで様々な真実が明らかにされていく。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL MARIACHI", "エル・マリアッチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "製作費わずか7000ドル、ほとんどは銃火器などの小道具にかかったのではないかと思わせるような超低予算映画。仕事を求め、とある田舎にたどり着いた流しのマリアッチ（ミュージシャン）が、殺し屋と同じ黒い服を着て、同じギターケースを持っていたために殺し屋に間違われてしまう……。キャストもすべて素人なら、監督のR・ロドリゲスもこれが監督第１作という新鮮な驚きに満ちた作品だ。アレックス・コックスを思わせるような乾いたメキシコの映像に加えて軽快なアクション・シーンも秀逸。1993年のサンダンス・フィルム・フェスティバルにおいて観客賞を受賞。後に監督自身によって、「デスペラード」としてリメイクされた。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOW TO GET AHEAD IN ADVERTISING", "広告業界で成功する方法", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "広告社のエリート社員ハグリー。やり手の彼は今、オデキ・クリームの企画で煮詰まっていた。ある日、彼の右肩に小さなオデキができる。そして、ハグリーはストレスを吹き出すように、上司に悪態をつき会社を辞めるが、オデキは大きくなるばかり。ブラック・ユーモアたっぷりのホラー・コメディ。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名前のない女たち", "名前のない女たち", "", "", "http://image.eiga.k-img.com/images/movie/55594/315x210.jpg?2766", 0, "名前が紹介されることもなく使い捨てられていく企画AV女優たちの衝撃的な性と生をインタビュー形式でつづり話題を呼んだ同名ノンフィクション書籍を映画化。渋谷でスカウトされてAV女優になった22歳の地味なOL純子は、撮影現場で出会った元ヤンキーの先輩女優・綾乃と心を開きあう仲に。いつもとは違う自分に変身することで居場所を見つけたように感じていた純子だったが、そんな彼女にもAV業界の厳しい現実が降りかかり……。", "2010-09-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超力戦隊オーレンジャー", "超力戦隊オーレンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "悪のマシン帝国バラノイアから地球を守るオーレンジャーたちの活躍を描いたスーパー戦隊シリーズの劇場版。バラノイアの皇子ブルドントが、SFXスペクタル映画の撮影を開始した。エキストラとして多くの子どもたちが街から連れ去られ、実弾が飛びかう戦場へと放り込まれてしまう。子どもたちを救うため撮影現場に駆けつけたオーレンジャーたちの前に、ブルドントが放った不気味なマシン獣たちが襲いかかり……。スーパー戦隊シリーズ20周年記念作品。", "1995-04-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDER THE BOARDWALK", "アンダー・ザ・ボードウォーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ザ・ドリフターズの『渚のボードウォーク』にのせて、サーフィンに青春を懸ける若者を描く。南カリフォルニアのマリブビーチ。大学進学を決意した“丘”の一派のリーダー、ニックは最後のサーフィン大会に挑む。しかし彼の前に前年のチャンピオン“浜”のリーダー、リーフが立ちふさがって……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Suburbans", "ザ・サバーバンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "再結成した一発屋バンドの奮闘を描いたコメディ・ドラマ。1980年代にデビュー曲が大ヒットしたものの、その後は売れることなく消えていった4人組ロックバンド「ザ・サバーバンズ」。18年後、再会を果たしたバンドメンバーは結婚式で演奏を披露することに。偶然にもそれを目撃したレコード会社のケイトは、彼らに再デビューの話を持ちかける。ケイト役に、「ラストサマー」シリーズのジェニファー・ラブ・ヒューイット。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エクレール・お菓子放浪記", "エクレール・お菓子放浪記", "", "", "http://image.eiga.k-img.com/images/movie/56114/315x210.jpg?2690", 107, "昭和17年、孤児院育ちのアキオは金平糖を盗んで捕まってしまうが、担当刑事の遠山がくれた菓子パンの甘味に心癒される。少年感化院に入れられたアキオは、教員の陽子が歌う「お菓子と娘」に希望を抱くが、やがて日本は戦争に突入。大切な人たちを空襲で亡くしてしまう。そんなアキオを支えたのは人々の優しい心と、お菓子のもっている不思議な力だった。作家・西村滋の自伝的作品「お菓子放浪記」を映画化。監督は「ふみ子の海」の近藤明男。", "2011-05-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BELOW", "ビロウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「π<パイ>」のダーレン・アロノフスキー監督が脚本を手掛けた怪談調のスリラー。潜水艦内を舞台に、敵の攻撃のみならず心霊現象にも襲われる乗組員たちの極限下のサバイバルを描く。第二次大戦中、米軍の潜水艦が難破船の生存者を救助。だが、その中に女性看護士がいたことから、“女性は縁起が悪い”と信じる乗組員の間に不穏な空気が流れる。やがて艦内で次々と怪事件が発生し……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SUNSHINE BOYS", "ニール・サイモンのサンシャイン・ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "かつてサンシャイン・ボーイズという名で人気のあったコンビの二人。しかし舞台裏では頑固に口も聞かず、反目しあう仲だった。そんな二人は、マネージャーの努力でなんとかTV出演することになる。しかし仲の悪い二人は……。N・サイモンのブロードウェイ劇の映画化。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUMMERTIME BLUES", "グローイング・アップ８　サマータイム・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1950年代のイスラエルの若者たちを描くこのシリーズも、1978年の第１作から８本目、９作目（番外編があるので数字は８）となった。ポール・アンカやレイ・チャールズが活躍していた頃のオールディーズの名曲をバックに、ベンジー、ヒューイ、ボビーの３人が女の子を追いかけたり、追いかけられたりと徹底しておなじみのパターンを貫いている。", "1988-01-01", "46,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THUNDER AND LIGHTNING", "ランナウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "酒の密売人ハーリィは、零細な密造酒屋を襲い酒の蒸留器を爆発する一味を見つけ、プロペラ・ボートで大追跡戦を開始する……。B級アクションに定評のあるコーマン製作による追っかけアクション。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Girl with a Pearl Earring", "真珠の耳飾りの少女", "", "", "http://image.eiga.k-img.com/images/movie/1594/315x210.jpg?3976", 100, "17世紀のオランダを舞台に、画家フェルメールの名画「真珠の耳飾りの少女」が描かれた背景に物語を構築し、それをモデルとなった少女の目を通して描く。監督ピーター・ウェーバーは英国のTV出身で本作が初監督作。撮影は「髪結いの亭主」から「歓楽通り」までパトリス・ルコント監督作を担当してきたエドゥアルド・セラ。美術は「数に溺れて」などのピーター・グリーナウェイ監督作品の常連ベン・ヴァン・オズが担当。", "2004-04-10", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄金の野郎ども", "黄金の野郎ども", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "大山組と黒田組の抗争に巻き込まれた笹田組幹部の立原は、汚ない策略から自分の組に命を狙われる。非情な仕打ちに怒った立原は、組長の笹田を射殺、横浜から姿を消した。それから２年、横浜は黒田組が一帯を掌握し横暴の限りをつくしていた。石原裕次郎が友情のために命を懸けるムード・アクション。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　OBJECT　OF　MY　AFFECTION", "私の愛情の対象", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "愛とセックスの微妙な境界を浮かび上がらせた恋愛ドラマ。恋人に捨てられた同性愛者ジョージは、パーティーで出会った女性ニーナと意気投合。妊娠中のニーナは恋人よりも、ジョージの存在に安らぎを感じ、二人は夫婦のように暮らし始める。そんな平和な日々も、ジョージがゲイの男性に恋したことから揺らぎ始め……。ユーモラスな展開や等身大のキャラクター、生活感にあふれる会話が魅力的。セックスレスの愛をはぐくむ男女の本音が、ストレートに伝わってくる。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INVADER", "ビジター　フロム・マース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "火星から何者かによって放たれた、想像を絶する“生命体”の恐怖をスリリングに描く、テレフィーチャー作品のSFサスペンス。かつて火星に打ち上げられ、消息を絶っていた無人探査機ヴァイキング２号が突然の落下。その調査に乗り出した４人のメンバーだが、探査機にはあまりにも危険な“乗組員”が寄生していた……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAFE", "SAFE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "「ベルベット・ゴールドマイン」で注目されたT・ヘインズ監督による異色の人間ドラマ。郊外の高級住宅地で暮らす専業主婦キャロルが、原因不明の体調不良に襲われる。頭痛や吐血、パニック症状に苦しむ彼女に、家族は戸惑うばかり。やがて、それが家具の塗料や排気ガスなどの化学物質過敏症だとわかり、キャロルは施設に入る決意をするが……。見えない恐怖に怯える主人公の姿に、現代人の不安と孤独を鋭く描き出す。ビデオタイトルは「ケミカル・シンドローム〔SAFE〕」。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Inside Deep Throat", "インサイド・ディープ・スロート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "'72年に公開され、アメリカで社会現象を巻き起こしたハードコア・ポルノ映画「ディープ・スロート」をめぐるドキュメンタリー。珍妙なコメディでありながら、破格のヒットを飛ばし、性の満足感を自ら求める女性を描いたことから女性解放運動の側面からも支持され、一方では保守層から弾圧を受けた“歴史的作品”の裏側を、現代の視点から検証する。『ニューヨークタイムズ』紙が擁護するなど、単なるポルノにとどまらないセンセーションを巻き起こした映画「ディープ・スロート」。だが、社会的注目度の高さから裁判に発展することになる。", "2005-11-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Good Shepherd", "グッド・シェパード", "", "", "http://image.eiga.k-img.com/images/movie/34254/315x210.jpg?1621", 165, "監督デビュー作「ブロンクス物語」以来13年ぶりとなる名優ロバート・デ・ニーロによる監督第2作。アメリカが誇る世界最大の諜報機関であるCIAの誕生秘話と、そこに身を投じた一人の男の葛藤を壮大なスケールで描く話題作。第2次大戦前夜のアメリカ東海岸。名門イェール大学のエリート学生だったエドワード（デイモン）は軍からスカウトされCIAの前身である戦略事務局（OSS）の一員として国家の諜報活動に従事していくが……。脚本は「フォレスト・ガンプ／一期一会」「インサイダー」のエリック・ロス。", "2007-10-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CQ", "CQ", "", "", "http://image.eiga.k-img.com/images/movie/1344/315x210.jpg?4449", 88, "69年のパリ。映画監督志望のアメリカ青年ポールが編集を務めるのは、B級SF映画「ドラゴンフライ」。ところが、監督が2人続けて降板、ポールがこの映画を監督することに。妹ソフィア・コッポラ監督の「ヴァージン・スーサイズ」では第2班監督を務めたローマン・コッポラの監督デビュー作。撮影は「ザ・ロイヤル・テネンバウムズ」のロバート・ヨーマン、美術は「ゴッドファーザー」3部作のディーン・タボラリス。", "2002-12-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bride", "ブライド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "「フランケンシュタインの花嫁」のリメイク作品。かつて造った人造人間ビクターの為に女性の人造人間イーバを造り出したフランケンシュタイン博士。イーバの美しさに魅了された博士は、彼女に礼儀作法を教え、パーティに連れて行く。一方、博士にイーバを取り上げられて家を飛び出したビクターは、途中で出会った小人リナルドと共に旅に出る。「さらば青春の光」で映画デビューしたスティングがフランケンシュタイン博士を演じる。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dead Ringers", "戦慄の絆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "　カナダ、トロント。双子の兄エリオットと弟ビバリーは婦人科医として名声を築いていた。そんな彼らの医院に女優のクレアが訪れる。兄の勧めでクレアのもとを訪ねたビバリーは彼女から誘惑される。クレアの存在は次第に双子のバランスを崩していくことに。薬物にのめり込むようになったビバリーは奇行に走り、ついにはエリオットも……。実話をもとに書かれた小説を映画化。ジェレミー・アイアンズが双子の二役を見事に演じわけている。", "1988-01-01", "10,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEYOND THE POSEIDON ADVENTURE", "ポセイドン・アドベンチャー２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "前作のラストから数時間後にドラマは始まる。謎の貨物がポセイドン号に積み込まれていたという事実を追ううち、黒い陰謀が浮かび上がってくる。パニック映画の最高傑作である１作目に比べるとサスペンス色の強い作品に仕上がっている。ケイン、サバラスの熱演が光る。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君は海を見たか", "君は海を見たか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "倉本聰が自作のTVドラマを脚色し、井上芳夫が演出している感動作。造船所の設計技師・増子一郎は仕事に追われ、息子の正一にかまってあげる暇がなかった。一郎の出張中、正一は身体の異常を訴え、精密検査の結果、ガンであることが判明。一郎は会社をやめ正一と残された少ない時間を、一緒に過ごそうと決意する。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BENNY GOODMAN STORY", "ベニイ・グッドマン物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "クラリネットでスウィング・ジャズの黄金時代を築いたベニイ・グッドマンの自伝映画。少年時代から、1930年代後半“キング・オブ・スウィング”の称号を得た後、カーネギー・ホールでの歴史的なコンサートを成功させるまでを、おなじみのヒット曲にのせて描く。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOYAGE　AU　BOUT　DU　MONDE", "世界の果てへの旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「沈黙の世界」「太陽のとどかぬ世界」などで知られる海中・海底の神秘に迫った名監督が、南極の氷に閉ざされた海底に挑む。海洋学者にして探険家でもある監督が自らリーダーとなって冒険する海洋ドキュメンタリー。荒れ果てた海上とは違い、無数の生命体と美しい美にあふれた海底の世界が画面いっぱいに広がる。絶滅寸前のザトウクジラとの出会いのシーンは感動的。", "1975-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gainsbourg", "ゲンスブールと女たち", "", "", "http://image.eiga.k-img.com/images/movie/55874/315x210.jpg?1906", 122, "歌手、映画監督、俳優など多彩な分野で活躍し、1991年に62歳で急逝したフランスのカリスマ、セルジュ・ゲンズブールの破天荒な半生を映画化。ゲンズブールは、ユダヤ人という出自と決してハンサムとはいえない風貌にコンプレックスを抱いていたが、反体制的な作風でセンセーションを巻き起こし、独特のダンディズムで多くの女性をとりこにしていく。バンド・デシネ作家のジョアン・スファールが初監督を務める。", "2011-05-21", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雷魚", "雷魚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "初の一般映画「KOKKURI・こっくりさん」に続き、瀬々敬久監督が実際に起きた事件にインスパイアーされて作り上げた過酷で骨太な人間模様。昭和63年３月、愛人に子供を焼死させられた過去を持つ和昭は、ガソリンスタンドの勤務中にある男女を見かける。やがてその男がモーテルで刺殺されるという事件が発生。警察で事情聴取を受けた和昭は、スタンドで見た女が取り調べを受けていることを知り、嘘の証言をする。やがて彼女、紀子を追い、関係を結ぶ和昭だが……。ある種の因果に結び付けられたかのように出会ってしまう男女。瀬々はこれまでも追求してきたモチーフをさらに深化させ、ある特定の場が喚起する生々しい情感を力強く映し出す。テレクラ、不倫、殺人とまるでワイドショーのような要素を用いながら、通俗ドラマには絶対に立ち現れない数々の光景で観る者を圧倒する。一般劇場で上映された後、ピンク映画館で「黒い下着の女」のタイトルでも公開された。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャンプ", "ジャンプ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「リボルバー」などの原作者としても知られる人気作家、佐藤正午が2000年に発表した同名小説を映画化。突然失踪した恋人を探してさまよう男の足どりを追いながら、彼女の“真相と深層”に迫っていく。“自分が付き合っていたのは何者だったのか”と自問自答することになる主人公を、TVドラマでも芸達者ぶりを見せている原田泰造が好演している。三谷の恋人、みはるが、りんごを買ってくると言い残して姿を消した。全く心当たりのない三谷は、わずかな手掛かりをもとにみはるの行方を探し始める。だが、みはるの知らない一面が少しずつ分かると同時に、謎も深まっていき……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和八十四年 1億3千万分の1の覚え書き", "昭和八十四年 1億3千万分の1の覚え書き", "", "", "http://image.eiga.k-img.com/images/movie/54714/315x210.jpg?2696", 84, "横浜で一人暮らしをする86歳の飯田進は、青年時代を戦場と牢獄で過ごしてきた。現在は戦争に関する執筆活動のほか、長男のサリドマイド薬害による被害から障害児童福祉の分野でも活動を行っている。大東亜戦争、BC級戦犯、スガモプリズン、サリドマイド、C型肝炎など、昭和という時代が抱える闇に真正面から向き合う飯田の姿を追いかけたドキュメンタリー。", "2009-08-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SHE−CREATURE", "海獣の霊を呼ぶ女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "海辺の小屋で殺人事件が起こり、催眠術師ロンバルディーは太古の怪物の仕わざだと語る。その言葉を疑う精神科医に対し、ロンバルディーは怪物を地上に呼び出そうとするのだが……。数多くの傑作、珍作を生んだB級プロダクション“AIP”創設直後の一作で、後のAIPの“幻想怪奇もの”の先がけとなった。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOUISE（TAKE２）", "LOUISE（TAKE２）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ジャズ・ミュージシャンとしても活動する映像作家シグフリードの長編デビュー作。偶然めぐり合った孤独な少女ルイーズ、ホームレスの子供である少年ギャビー、気ままにナンパを楽しんでいる若者レミの３人が、冬のパリを舞台にささやかな冒険を繰り広げていく。ジャズやヒップホップなど多彩なジャンルのナンバーを盛り込んだ音楽、手持ちカメラが捉えた叙情的な都会の風景も魅力的な１本だ。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUTURES PORT", "デス・ゲーム 2025", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ホッケーにラフ・ファイトを加えた“フューチャー・ゲーム”なる未来スポーツを題材にした近未来活劇。フューチャー・ゲームの花形選手ラムジーは、以前彼が侮辱したことを根に持つHLO（ハワイ解放機構）と対立。HLOは過激なテロ行為に走り、抗争が勃発しようとしていた。ラムジーは平和的な解決を図り、フューチャー・ゲームで決着をつけようとするが……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("We Need to Talk About Kevin", "少年は残酷な弓を射る", "", "", "http://image.eiga.k-img.com/images/movie/57494/315x210.jpg?3964", 112, "「ボクと空と麦畑」「モーヴァン」のリン・ラムジー監督が、強い悪意と執着心を抱く息子とその母親の関係を緊張感たっぷりに描いた人間ドラマ。自由奔放に生きてきた作家のエバは子どもを授かったことでキャリアを捨て、母親として生きる道を選ぶ。生まれた息子はケビンと名づけられるが、幼い頃からエバに懐くことはなく、反抗を繰り返していく。やがて美しい少年へと成長したケビンは反抗心をますます強めていき、それがある事件の引き金となる。「フィクサー」のティルダ・スウィントンが主演。", "2012-06-30", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WINCHESTER 1973", "ウィンチェスター銃'73", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1873年、リンはかつて射撃コンテストで獲得したウィンチェスターのライフル銃を仇敵ヘンリーに奪われてしまう。ところが、逃走中のヘンリーが賭博で負けたことに端を発して、銃はとある商人からインディアンの酋長へ、さらに無法者の手へと転々。再び銃を手にしたヘンリーをリンがついに倒す。１挺の銃が運命の旅路をめぐりめぐって主人公へと戻ってくる過程が、サスペンス豊かに展開。ウエスタンの名手A・マン監督は、西部の街や砂漠の空間を巧みに生かしてアクション・シーンをこなし、歯切れのいい快作に仕上げている。西部の伝説的ヒーローが誕生するラストの詩情も一級品だ。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("特務迷城／THE ACCIDENTAL SPY", "アクシデンタル・スパイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "香港、韓国、トルコを股にかけてジャッキー・チェンが大活躍するサスペンス活劇。孤児のバックは、出生の秘密を知る謎の男と出会い、彼の言葉に導かれてイスタンブールへ。ところが、そこで犯罪一味の襲撃に遭う。逃げるバックは、自分が細菌兵器をめぐる陰謀に巻きこまれたことに気付き……。ジャッキーの体を張ったアクションが最大の見どころだが、なかでも暴走タンクローリーの上で大立ち回りを演じるクライマックスは圧巻。", "2001-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男女逆転　吉原遊郭", "男女逆転　吉原遊郭", "", "", "http://image.eiga.k-img.com/images/movie/54024/315x210.jpg?3217", 0, "若手俳優集団D-BOYSが主演を務める大河ロマンシリーズ第1部作。「櫻の園」「赤い糸」の柳下大が、プライドが高く金にもなびかない花魁を演じる。明暦3年、男遊郭「菊下楼」のナンバー1花魁の鷹尾は周囲から嫉妬されながらも気高く暮らしていた。そんなある日、お花という少女が兄の仇を討つため男に扮し、男遊郭に現れる。", "2008-10-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画ドラえもん　のび太と緑の巨人伝", "映画ドラえもん　のび太と緑の巨人伝", "", "", "http://image.eiga.k-img.com/images/movie/53294/315x210.jpg?2850", 112, "2005年に声優陣やキャラクターデザインをリニューアルした「ドラえもん」新シリーズの劇場版第3弾。過去作品をリメイクした前2作とは異なり、劇場版オリジナルのストーリーが展開する。裏山で苗木を拾ったのび太は、ドラえもんの道具で自由に動き回れるようになったその苗木に“キー坊”と名付け育て始める。ある日キー坊を連れて裏山を訪れたのび太たちは、突然現われた巨大な渦に飲み込まれ、植物が支配する“緑の星”に辿り着く。", "2008-03-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・大泥棒", "喜劇・大泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "武太郎は守衛をやっているが、それは世を忍ぶ仮の姿で、その実体は日本古来の泥棒術、雲霞流に学んだ正統派の泥棒だった。この武太郎とライバル・ケムンパスの竜、そして刑事・鬼山が銀行襲撃をめぐって繰り広げるあの手この手のユーモア編。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SURVIVE STYLE　５＋", "SURVIVE STYLE　５＋", "", "", "http://ecx.images-amazon.com/images/I/316R7ZRN0NL._SL210_.jpg", 120, "シュールな笑いとスタイリッシュな映像でCM業界に革命をもたらしたクリエイターコンビ、関口現と多田琢による、破天荒な新感覚コメディ。妻の幽霊を殺そうとする男。恋人の催眠術師を殺したCFプランナー。催眠術師に催眠術をかけられ人間としての日常を失い、鳥として生きることになった中年男。通訳付きの凄腕殺し屋が雑多な人物をつなぎ、彼らの生き残り術を浮き彫りにしていく特異な構成の奔放な群像劇。エピソードが連なるのではなく束になって襲いかかってくる新機軸の演出が新鮮だ。石垣昌広は、殺しても、殺しても、なぜか生き返る妻のミミに困り果て、イギリスから来た凄腕の殺し屋を雇う。一方、家族想いのサラリーマン小林は、一家そろって見に行った催眠術ショーで自分を鳥だと思い込まされてしまい……。", "2004-09-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("福井青春物語", "福井青春物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "福井県出身の個性派俳優・津田寛治と同県在住の映画監督・森川陽一郎によるご当地インディーズ映画。スランプに陥った原因が福井に住んでいることにあると思い込んでいる、自主製作映画の若手監督の苦悩と再生のドラマを描く。監督＆スタッフ、キャストの深い郷土愛を感じさせる意欲作だ。映画監督を夢みて福井で自主製作映画を撮っている津田は、このまま福井の地でいい映画が撮れるのかという疑問を抱き始めるが……。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カントリーガール", "カントリーガール", "", "", "http://image.eiga.k-img.com/images/movie/57794/315x210.jpg?2022", 70, "高校生と舞妓をテーマに描かれた、京都が舞台の青春映画。「メゾン・ド・ヒミコ」「ノーボーイズ，ノークライ」の人気脚本家・渡辺あやによるオリジナル脚本を、京都出身の若手監督・小林達夫のメガホンで映画化した。自分が暮らす京都の町の歴史や伝統には興味がない高校生のハヤシは、ある喫茶店オーナーの「新しい芸術空間を作る」という構想に魅せられる。外国人観光客をだましてはお金を稼ぎ資金を集めていたが、そんなある日、田舎から出てきて舞妓見習いをしている女の子に恋したことから、ハヤシの日常が少しずつゆらぎ始める。", "2012-05-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KRIPPENDORF’S　TRIBE", "Dr.ジャガバンドー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "人類学の大学教授クリッペンドーフは最愛の妻に先立たれた後、３人の子供の世話に追われて研究など手につかない状態。そんなある日、彼は新部族の発見について講演することになる。しかし、新部族は発見できず、おまけに研究費を生活費に充てていた彼は困ったあげく、でっちあげの新部族シェルミッケドムー族を発見したとウソをついてしまう。そしてウソの上塗りをするために自分と子供たちでその部族の扮装をし、ニセのドキュメンタリーを撮影することにするが……。ばかばかしい笑いの中に、家族の絆などのテーマを盛り込んだハートウォーム・コメディ。R・ドレイファスのノリのいい演技が冴える。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILLER CROCODILE", "キラークロコダイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "カリブの島の小さな町。そこにやって来た生態調査団は、湿原で放射能廃棄物の入ったドラム缶を見つける。実は町の町長がある男とグルになって、この地に放射能廃棄物を捨てていたのだ。その夜、調査団の一人が何者かに襲われる。その死体には巨大なクロコダイルの歯形が……。", "1989-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAGYAR RAPSZODIA", "ハンガリアン狂詩曲〈ラプソディー〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "舞台は第一次世界大戦下の混乱期。大地主の息子は対立していた農民たちを部下にして戦場に赴くが……。そこで起きた事件を通して、戦乱の世に貴族として、あるいは一人の人間として生きる苦悩や、製作者の政治的主張を描いている。幻想・過去・現実が交錯する壮大な叙事詩。", "1978-01-01", "30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガマゴリ・ネバーアイランド", "ガマゴリ・ネバーアイランド", "", "", "http://image.eiga.k-img.com/images/movie/57954/315x210.jpg?4516", 0, "2012年・第4回沖縄国際映画祭の企画「地域発信型プロジェクト」で製作された作品。愛知県蒲郡市を舞台に、海に浮かぶ島の神社に出るという女性の霊を見にでかけた少年たちの冒険と成長を描く。", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Operation: Endgame", "極秘指令　ドッグ×ドッグ", "", "", "http://image.eiga.k-img.com/images/movie/58034/315x210.jpg?2217", 82, "ジョー・アンダーソン、マギー・Q、エレン・バーキンらが扮する秘密工作員が密室で繰り広げるサスペンスアクション。オバマ大統領就任に全米が沸いていた2009年1月20日、旧政権下で国防総省と統合参謀本部を母体に設立された非公認のエリート部隊の工作員たちに、突如として全員抹殺の指令が下される。殺しのプロでもある11人の工作員たちは、ファクトリーと呼ばれる密室空間で生き残りをかけて戦いを繰り広げるが……。監督はニューバランスやペプシなどのCMを手がけてきた映像クリエイターのファアド・ミカティ。", "2012-05-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MASTER OF DISGUISE", "変身パワーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "人気TV番組『サタデー・ナイト・ライブ』や「ウェインズ・ワールド」などで秀逸なパフォーマンスを披露して爆笑を誘ったダナ・カーヴィが、自ら脚本、主演をこなした彼の集大成的作品。主人公が代々受け継いできた変装術などの特殊技術を駆使して、その技術を悪用しようとする犯罪組織と戦うというコメディだ。数々の名作のパロディーも盛り込まれて大爆笑必至！人気コメディアン、アダム・サンドラーが製作総指揮を務めている。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Get on the Bus", "ゲット・オン・ザ・バス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "　95年、ネイション・オブ・イスラムの指導者ルイス・ファラカン師の呼びかけによって、黒人男性150万人がワシントンでデモ行進をすることに。それに参加するため、手錠でつながれた父子、ゲイのカップル、映画学校の学生、白人を母に持つ警官、熱心なイスラム教信者の青年、といった面々が乗り込んだバスがロサンゼルスを出発。やがて乗客ひとりひとり人生や旅の目的が明らかになっていくが……。ツアー参加者たちの葛藤を描く群像劇。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あの空をおぼえてる", "あの空をおぼえてる", "", "", "http://image.eiga.k-img.com/images/movie/34224/315x210.jpg?1584", 115, "幼い兄妹がトラック事故に遭い、兄・英治は奇跡的に命を取り留めるも、妹の絵里奈が他界。悲嘆に暮れる深沢家の面々だったが、それでも時が経つとともに、英治や母・慶子は次第に明るさを取り戻していく。しかし、父・雅仁はひとり暗い部屋に閉じこもり、いつまでも悲しみに暮れるばかりだった……。ジャネット・リー・ケアリーの同名児童文学を映画化した家族ドラマ。一家の父・雅仁役の竹野内豊が、「冷静と情熱のあいだ」（01）以来7年ぶりに主演を務める。", "2008-04-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EN　FACE", "甘い嘘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "パリ・モンマルトルで暮らす結婚７年目のジャンとミシェルは、ある時、向かいに住んでいた見知らぬ富豪、ギメ氏の遺言によって、大邸宅を譲渡される。古いアパルトマンからいきなりの豪邸暮らしに夢見心地になる二人。しかし、かつての彼らの部屋をのぞいていたと思われる望遠鏡や、女たちの卑猥な写真を発見したジャンは、さらにギメ氏の遺品の中にミシェルの手紙を見つけて、妻へ疑惑を抱くようになる。深く愛し合っているはずの夫婦が、思いがけない試練に直面した時の真実の愛の姿を描く。怪しげな使用人やクラシックな屋敷での恐怖、覗きのエロティシズムなど、ヒッチコック・サスペンスを思わせるラブ・ロマンスだ。ビデオ、DVDタイトルは「裏窓の女　甘い嘘」。", "1999-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("熱泥地", "熱泥地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "100万円強盗犯の栗田は、金で契約した酒場の女・カツミと北海道行きの船に乗り込んだ。二人は北海道の山中で暮らし始めるが、船からついてきた怪しい男・千葉がそこに居ついてしまい、彼と栗田は金をめぐって毎日ケンカを繰り返す。カツミはそんな生活にイヤ気がさし、逃げるチャンスをうかがう……。市川崑得意のタイトにまとめた風俗喜劇。藤田進のとぼけた味がいい。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("漂亮媽媽", "きれいなおかあさん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "聴覚に障害がある息子をなんとか小学校へ入学させようと奮闘する母親のリーイン。しかし、息子は発音が不明瞭だという理由で入学試験は不合格になってしまう……。監督のスン・ジョウが確かな演出力で、厳しくも温もりのある親子関係をテンポ良く見せたヒューマン・ドラマ。国際派女優として活躍するコン・リーが女手一つで息子を立派に育て上げることに最大の母性本能を傾ける母親を熱演。全編ノーメイクで役柄に挑み、演技派女優としての新境地を見いだした。", "1999-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REMOTE CONTROL", "リモート・コントロール　ビデオエイリアンの侵略", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "舞台は近未来。とあるビデオ・ショップに“リモート・コントロール”と題された新作が届いた。一見、なにげない1950年代のSF映画だが、見ている人がビデオの中に出現するという怪奇現象が続出。実はそのビデオは、地球侵略をたくらむエイリアンが仕組んだ罠だった……。自分がコントロールしていると思っていたビデオに、逆に支配されていく恐怖を描いたSFアクション。エイリアンに果敢にも戦いを挑んでいくビデオ・ショップの店員にマット・ディロンの実弟、K・ディロンが扮している。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女嫌い", "女嫌い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "城戸四郎松竹社長の企画による、大船調喜劇３部作の「落第生とお嬢さん」に続く第２作。原作の赤穂春雄とは城戸本人の筆名である。結婚生活35年の謹厳実直な初老の男が、ふとしたことから若い娘に浮気の虫を起こし一緒に旅行するが、娘にはヤクザがついていて一騒動となる。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私は負けない", "私は負けない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "祖母と二人暮らしだった娘・有子は、祖母が死んで大阪の実家に移り住む。が、その実家の母は実の母ではなかったのだ。意地の悪い母や姉に負けず明るさを失わない有子に弟・弘志は惹かれていく……。増村保造の秀作「青空娘」のリメイクで軽快なテンポの一編。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチバン　アルティメット", "ガチバン　アルティメット", "", "", "http://ecx.images-amazon.com/images/I/61EKSlrTcAL._SL210_.jpg", 65, "ヤンキー映画「ガチバン」シリーズ第10作。先輩の裏切りにより関東中央少年院に入れられた勇人は、院内でも変わらず暴れまわっていた。一方、全国制覇を目指す途中で仲間のために少年院に入った紋児は、その面倒見の良さから少年院のボスに君臨。お互いのヤンキー道を貫く2人はやがて対峙することになるが……。", "2011-01-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shivers", "デビッド・クローネンバーグのシーバース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "　モントリオール郊外にある島で、寄生虫を人間の内臓に同化させようという実験が行なわれていた。その結果、被験者となった少女は島の男性たちと関係を持ち、そのあげくに凶暴化する。彼女と関係した男性たちも寄生虫に取り付かれ、肉体を乗っ取られてしまう。蔓延する怪現象の行く末は？　鬼才クローネンバーグがのちにハリウッドのヒットメーカーとなるアイバン・ライトマンと組んで送り出した残虐ホラー。", "1975-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ズンドコズンドコ全員集合!!", "ズンドコズンドコ全員集合!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ちっぽけな漁村を根城に暴れ回る、碇鮫吉をリーダーとする５人組の不良グループは村の鼻つまみ者だが、ある日鮫吉は一旗あげようと子分の岩四を連れて東京へ……。数ヵ月後、夢破れ落ちぶれはてた二人のもとに故郷の子分たちが上京して来る。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APO APOワールド　ジャイアント馬場90分１本勝負", "APO APOワールド　ジャイアント馬場90分１本勝負", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "プロレス界のスーパースター、ジャイアント馬場を主人公にした爆笑アニメ。日本プロレスリング界のレスラーたちが実名で登場し、全面協力している。アメリカ・マット界の王者にしてジャイアント馬場の盟友キングマルチノが、極悪覆面レスラー、ドワルスキー軍団によって襲撃、引退へと追い込まれた。次なる標的となった日本プロレスの面々にアクシデントが続出する中、ジャイアント馬場もクイズ番組出演中に命を狙われる。そのうえ、ファンや愛犬にまで危害を加えられ、ジャイアント馬場の怒りが爆発、ついにドワルスキー軍団との対決を誓うが……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STIR CRAZY", "スター・クレイジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "アメリカで大ヒットしたコメディ映画。ワイルダーとプライヤーのコンビが、濡れ衣で刑務所に入れられ、脱獄を敢行するドタバタを描く。監督は俳優として名高いポワチエで、「ブラック・ライダー」以来、本編が６本目の演出作品。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新しき家族", "新しき家族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "現代風俗映画を得意とする巨匠・渋谷実が、浜本浩の２作「流離の人々」「少年の日」より、前者からは地方小資本家の悪業という社会的問題を、後者からは父と子の相克という家庭問題を同時に追求せんとした野心的力作。村一番の名家、村里家の一人息子・健作は自分が元家政婦と父の間の子だと知り、衝撃のため家を出て、叔父の新聞社に入社する。彼はそこで路線バス開通をめぐる贈賄事件に遭遇。その事件の背後には父の姿が……。心理描写はきめ細かく、特に最後に父を許そうとした主人公が父の死を知り、一人の新聞記者として社に電話連絡する場面は深く胸に迫る。現存するフィルムは不完全版。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獨臂拳王／ONE　ARMED　BOXER", "片腕ドラゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "カラテ映画の全盛期に製作された異色カンフー活劇。悪らつな武術家チャオ率いる道場の一派が、敵対するハンの道場に嫌がらせを仕掛けてきた。ハンの弟子ユーはチャオの弟子たちを倒すが、世界中から招いた武術の達人をバックにつけたチャオはハンの道場を潰してユーの右腕を折る。怒ったユーは残った左腕を火で焼いて鍛え、チャオに立ち向かう。様々なタイプの武術家を相手にした主人公の、クライマックスの死闘は圧巻。", "1972-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・夫婦善哉", "新・夫婦善哉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "豊田四郎の代表作といわれる「夫婦善哉」から、８年ぶりに製作された後日譚とでもいうべき作品。すでに中年にさしかかった柳吉と蝶子のカップルのその後が描かれている。蝶子は小料理屋を雇われの女将として、店をまかされるほどになっていたが、柳吉の方はすでに娘が結婚適齢期を迎えているにもかかわらず、相変わらずの道楽ぶり……。森繁・淡島コンビの息もピッタリの演技が見もの。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LION", "ライオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "イギリスのカメラマンとしても知られるJ・カーディフ監督が、J・ケッセルの原作を映画化。別れた妻から子供の教育問題について相談したいという手紙を受け取った新鋭の弁護士は、彼女の待つアフリカのケニアに現れた。彼女はすでに動物保護監視員と再婚していたのだが……。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REIGN OF FIRE", "サラマンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "近未来のイギリスを舞台に、炎を吐く翼竜と人間の攻防を描いた冒険アクション。マシュー・マコノヒー、クリスチャン・ベールが怪物に立ち向かう男たちを熱演する。現代によみがえった太古の巨大竜が文明社会を破壊した。少年時代に母親をサラマンダーに殺されたイギリス人青年クインは、わずかな生存者とともに砦に立てこもる。そんな彼の前にアメリカから思わぬ人物が現れる。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水戸黄門 助さん格さん大暴れ", "水戸黄門 助さん格さん大暴れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "水戸家の新人登用の試験の日、大乱闘を演じて謹慎を命じられてしまった助三郎と格之進の二人。ところが、呼び出しを受けて御老公にあった二人はびっくりしてしまう。というのも、先日野原で握り飯をご馳走になったうえ、散々悪口を言った老農夫が水戸の御老公その人だったのだ。それでも二人は無事試験に合格するのだが……。喜劇風の時代劇。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PUMPKINHEAD", "パンプキンヘッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "人里離れた田舎で暮らしていたエドは、突然やって来たバイク狂の若者たちに息子のビリーをひき殺される。怒りを爆発させた彼は、殺人モンスターをよみがえらせ若者たちを惨殺していく。だが、やがて改心、自らが生み出した怪物に立ち向かっていく。SFXアーティスト、S・ウィントンの初監督作。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聴こえてる、ふりをしただけ", "聴こえてる、ふりをしただけ", "", "", "http://image.eiga.k-img.com/images/movie/57864/315x210.jpg?4480", 99, "", "2012-08-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH SIERRA", "ハイ・シェラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "社会から糾弾され追い詰められる前科者の逃避行をテーマにした“ボニーとクライド”型ドラマのうちでも傑作の１本。“狂犬”のニックネームを持つ凶悪犯ロイは特赦で８年ぶりに出所するが、昔のよしみで仲間と強盗をするはめに。これが最後と、ロイが承知した一流ホテル強盗で彼は警察に知られ、おたずね者となる。ロイに惚れたマリーが行動をともにするが、やがて彼はハイ・シェラ（＝高い山）に追い詰められていく。敵役専門だったH・ボガートが初めて主演男優として注目された記念すべき作品。脚本のJ・ヒューストンが同じボガート主演の「マルタの鷹」で監督となるのもこの年のこと。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RICH AND FAMOUS", "ベストフレンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "ジョン・バン・ドルーデンの舞台劇の映画化。大学時代ルームメイトだった二人の女流作家の20年にわたる友情を、互いのライバル意識やセックス・ライフを織り込みながら描く。現代に生きる女性の苦悩や孤独感を、老練な演出で見事に映像化。監督の遺作となった女性映画の秀作だ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠狂四郎 女妖剣", "眠狂四郎 女妖剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "エロチシズムを前面に出し、抜群の面白さを誇る第４作。淫欲とアヘンに溺れる将軍の娘・菊姫、隠れキリシタンの情報と引き換えにアヘン密輸を黙認されている備前屋、狂四郎と血のつながりを持つという聖女のぴるぜん志摩、この３者に狂四郎が絡み、浜松で意外な結末を迎える。狂四郎に志摩の情報をもたらした隠れキリシタン島蔵と妹・小鈴の処刑場に狂四郎が斬り込むシーンのロングと横移動のあざやかなカメラワーク、大小二刀による狂四郎の見事な居合い。そして第１作以来の宿敵・陳孫との闘いなどアクション・シーンが最高。己れの前に肉体をさらす聖女を一刀のもとに斬り捨てるラストの切れ味も抜群だ。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Yellow Cake: Die Luge von der sauberen Energie", "イエロー・ケーキ　クリーンなエネルギーという嘘", "", "", "http://image.eiga.k-img.com/images/movie/57404/315x210.jpg?3476", 108, "原子力発電の燃料として使われるウランの採掘の裏側を暴くドキュメンタリー。原発の燃料となるウランの鉱石採掘の段階で、処理不可能な放射性物質が大量に発生している事実を、各地のウラン採掘場へ5年間にわたる取材を敢行し、明らかにした。タイトルの「イエロー・ケーキ」は天然のウラン鉱石を精錬して生まれる、原子力発電の燃料となる黄色い粉末を指す。", "2012-01-28", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAGONMASTER", "幌馬車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "西部開拓でにぎわう1871年、モルモン教徒の開拓者たちの幌馬車隊がユタに希望を求めて出発する。一行の護衛はトラヴィスとサンディの二人の若者だ。彼らは荒野を進むうち、飢えた旅芸人の幌馬車を救うことに。やがて一座の踊り子デンヴァーが、トラヴィスに恋心を抱いたその矢先、突如、逃亡中の悪漢一味が幌馬車隊に逃げ込んできて……。スリリングな展開の物語とともに、J・フォード監督の西部的詩情があふれる名作ウエスタン。元ロデオ・チャンピオンで、スタントマンから出発したB・ジョンソンの初主演作で、その見事な騎乗ぶりにも注目。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TESS", "テス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 171, "英国の文豪ハーディの名作『ダーバビル家のテス』の映画化。19世紀末、英国ドーセット地方。貧農の娘テスは、遠縁のダーバビル家に奉公に出される。だがそこでの生活が悲劇の始まりであった。テスはダーバビル家の息子に私生児をはらまされ、実家に帰って来るが、生まれた赤ん坊は死んでしまう。やがて牧師の息子と結婚するが、テスの前歴を聞いた彼は外国へ去る……。テスの流転の人生を、美しい英国の風景を背景に、きめ細やかに描き出した作品。キンスキーの熱演をはじめ、撮影のアンスワース、音楽のサルドなど一流スタッフの手腕もあって見ごたえ十分。", "1979-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIENDLY PERSUASION", "友情ある説得", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "巨匠W・ワイラーがアメリカ人の魂を問い詰めた力作。平和主義者のクエーカー教徒だが、負けず嫌いのジェス。妻のイライザはそんな夫が心配でもあり、また好ましくも思う。時あたかも南北戦争が起こり、平和な一家にもその波は押し寄せてくる。二人の長男ジョッシュはクエーカー教徒の平和主義を批判し、義勇軍に身を投じる。戦いに参加したジョッシュは傷を負い、これを知ったジェスは息子への愛情から銃をとり、戦場に赴く。だが、彼には人を殺すことができない……。クエーカー教徒の平和主義を背景に、古き良き時代のアメリカ人をG・クーパーが好演。アメリカの良心が示された名作である。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Steal Big, Steal Little", "悪魔たち 天使たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "　心優しい農夫のルーベンは、カリフォルニア、サンタ・バーバラの美しい大地に広がる肥沃な土地ランチョ・アロヨスの土地を相続することに。しかし金と権力欲にとりつかれたルーベンの双子の兄ロビーが横取りを画策し、汚い罠を仕掛けてきた！　アンディ・ガルシアが性格の異なる双子の兄弟という二役に挑戦。石橋貴明が「メジャー・リーグ2」に続く2度目のハリウッド映画出演を果たしたことも話題となった。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AIKI〈アイキ〉", "AIKI〈アイキ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "車イスで黒帯を持つ実在のデンマーク人男性、オーレ・キングストン・イェンセンをモデルにした人間ドラマ。ボクシングに打ち込んでいた21歳の芦原太一は、不慮の事故で車イスの生活を余儀なくされる。すべてを失ってしまい、夢も希望も持てずに荒れた生活を送る太一。だが、車イスでも可能な合気柔術（＝AIKI）に出会ったことから、再び生きる希望を取り戻していく。太一を悲劇の主人公としてではなく、普通の青年として描いた天願監督の一歩引いた視点に好感が持てる。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鎗王", "ダブルタップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "L・チャンがサイコキラーという異色の役柄に挑戦したサスペンス・アクション。リアリズムと映画的なけれん味を巧みに融合させた切れ味鋭い銃撃シーンの数々も見どころ。香港屈指の射撃選手リックが競技場に乱入してきた男を射殺する。この事件をきっかけに精神のバランスが崩れ、獣性に目覚めたリックは３年後、残虐な射殺事件を引き起こし、香港警察との全面戦争を繰り広げていく。", "2000-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAR PHOTOGRAPHER", "戦場のフォトグラファー／　ジェームズ・ナクトウェイの世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "偉大な報道写真家ロバート・キャパの魂を受け継ぐ男と呼ばれる戦争写真家、ジェームズ・ナクトウェイ。スイスのドキュメンタリー作家クリスチャン・フレイが、20年以上に渡り戦場を撮り続ける彼の実像に迫る。特殊な超小型カメラにより実現した彼のカメラの視点で捉えた映像と、静かな語り口だが戦争の本質を突いたナクトウェイ自身の言葉の数々が胸に迫る。", "2001-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お加代の覚悟", "お加代の覚悟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "日本舞踊の師匠のもとに12年もいる内弟子・お加代は、写真道楽の若旦那・俊作への恋心を燃やし始める。だがそれははかなくも崩れ去り、お加代は踊り一筋に生きる覚悟を決めるのだった……。田中絹代の延々と続く舞踊を見せ場とした、お正月作品。商業映画作家としての島津らしさが良くも悪くも出ている。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丹下左膳 乾雲坤竜の巻", "丹下左膳 乾雲坤竜の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "乾雲と坤竜の２本の名刀をめぐる争奪戦におなじみの丹下左膳が巻き込まれ、善悪入り乱れての虚々実々の駆け引きに江戸の町は揺れ動く……。従来のチャンバラ映画にはないリアリズムに徹した演出で、独自の丹下左膳像を作り上げた作品ではあるが、興行的には大コケして失敗作とされた。再見の価値はある一作。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OPEN FIRE", "ファイアーイーグル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "密室状態で緊迫した攻防が繰り広げられる「ダイ・ハード」の亜流アクション。科学プラントをテロ集団が占拠するという事件が勃発。一味が擁する強力な劇薬の前に、FBIも手出しができない。同僚を死なせてFBIを辞職した男アレックは、所長である父を救い出すために単身、プラント内へと乗り込む。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("柳生一族の陰謀", "柳生一族の陰謀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "徳川二代将軍秀忠の死後、次期将軍の座をめぐり、幕府内部、柳生一族、浪人、朝廷など様々な人間の策謀が交錯するという大型時代劇。本編が時代劇初演出となる深作欣二監督は、跡目争いを骨子に据え、ある大きな流れの渦に飲みこまれていく人々の姿を、「仁義なき戦い」と同様、群集抗争劇として捉えている。三代将軍に家光を擁立しようとする柳生但馬守を演じた萬屋錦之介は、大時代がかって重々しいながらも、腹黒く権力に執着する男を怪演。息子・十兵衛を演じた千葉真一の奔放さと好対照をなす。千葉にとっては一世の当たり役といえるだろう。ラスト、権力争いに勝ったと思った但馬守の前に、十兵衛が家光の首を投げ捨て、これを拾った但馬守が“夢じゃ、夢でござる”と叫ぶ、凄絶なシーンは衝撃的。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAESTRO", "マエストロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "現在のダンス・ミュージックの基礎を築いたN.Y.のアンダーグラウンド・シーンの伝説を追うドキュメンタリー。ラリー・レヴァンやデヴィッド・マンキューソなどのカリスマDJに焦点を当てて、クラブ・カルチャーの起源や発展、精神を捉えていく。ロフトやガレージなど、今や伝説と化したクラブの映像も登場。クラブ黎明期のダンスフロアの熱気を体感できる。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あんにょん由美香", "あんにょん由美香", "", "", "http://image.eiga.k-img.com/images/movie/54634/315x210.jpg?4105", 119, "「童貞。をプロデュース」で注目を集めた松江哲明監督が、2005年に急逝した女優・林由美香を題材に撮りあげたドキュメンタリー。林由美香が主演した韓国産ビデオ「東京の人妻純子」で改めて林の魅力を発見した松江。生前の林ときちんと仕事する機会を得られなかったことを後悔していた彼は、林の幻を追うかのように取材を開始。かつて林の代表作を撮った平野勝之、カンパニー松尾、いまおかしんじら3人の監督と共に撮影現場を訪れる。", "2009-07-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SKEPP TILL INDIA LAND", "インド行きの船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "老いた船乗りアレクサンデルは港町の踊り子サリーに心を惑わされ、家族や仕事仲間を離れて二人だけの生活を始める。息子のユハンネスはアレクサンデルと激しく対立するが、自分もまたサリーの虜になってしまうのだった……。巨匠I・ベルイマンの長編第３作。屈折した愛憎劇を描く演出力はさすが。", "1947-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHARLIE CHAPLIN’S BURLESQUE ON CARMEN", "珍カルメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 35, "映画史上で最もリメイクが多い、メリメの名作『カルメン』の７度目の映画化。ただし本作はチャップリの監督・主演ゆえ、異色のコメディ版となっている。酒場女のカルメンに恋した、トンマな衛兵ホーゼリ。ハンサムな恋敵の中尉との決闘の果てに彼は……。", "1915-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shy People", "或る人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ニューヨークでジャーナリストをしているダイアナは、麻薬に溺れる娘のグレースとのギャップを埋めるため、叔父一家の住むルイジアナの田舎へやって来た。叔父の未亡人ルースには息子がいたが、ダイアナとは正反対の強引な教育方針で……。日本未公開に終わったコンチャロフスキーの力作。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("2ちゃんねるの呪い　劇場版", "2ちゃんねるの呪い　劇場版", "", "", "http://image.eiga.k-img.com/images/movie/56784/315x210.jpg?2317", 93, "ネットの巨大掲示板「2ちゃんねる」の恐怖スレッドで語られた内容を映像化してきたホラーシリーズ「2ちゃんねるの呪い」の劇場版。2ちゃんねらーの間で架空の事件として知られている都市伝説「鮫島事件」について「真相を知りたい」というスレッドが立ち、何者かが真相を語り始める。一方、オカルト好きの大学生・修平と恋人の楓は、そのサイトを表示すると呪い殺されるとネット上で噂になっている「赤い部屋」にたどりつくが……。人気アイドルグループ「アイドリング!!!」から尾島知佳、野元愛が出演。", "2011-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HURRICANE STREETS", "ハリケーン・クラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "14歳の不良少年の心情をナイーブに捉え、サンダンス映画祭で好評を博した青春映画。主人公のマーカスは、N.Y.で叔母と暮らす万引き常習犯。故郷への帰還を夢見る彼は、一つ年下の少女メレーナと恋に落ちたことから、彼女を連れて荒んだ都会を逃げ出そうと決意。だがメレーナの厳格な父親が二人の交際を禁じ、それが予期せぬ事故を引き起こしてしまう。DVDタイトルは「ハリケーン・ストリート」。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白夜行", "白夜行", "", "", "http://image.eiga.k-img.com/images/movie/55364/315x210.jpg?3619", 149, "テレビドラマ化もされたベストセラー作家・東野圭吾の長編ミステリーを、堀北真希主演で映画化。「半分の月がのぼる空」「洋菓子店コアンドル」の深川栄洋監督がメガホンをとる。共演に高良健吾、船越英一郎ら。並外れた美ぼうをもつ少女・雪穂が、幼児期に起こったある事件をきっかけに、周囲の人間を不幸に陥れてでも底辺からのし上がろうとする姿を描く。", "2011-01-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOME ALONE２：LOST IN NEW YORK", "ホーム・アローン２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "大ヒットを記録したキッズ・ムービーの続編。前作同様、M・カルキン扮するケビン君が大活躍。今回、彼の一家はクリスマス休暇をマイアミで過ごそうとするが、空港ではぐれたケビン君だけがなぜかニューヨークへ。そこであの泥棒コンビと再会し、にぎわう街を舞台にまたまた愉快な闘いが繰り広げられる。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("20世紀少年", "20世紀少年", "", "", "http://image.eiga.k-img.com/images/movie/53304/315x210.jpg?4718", 142, "浦沢直樹の同名人気コミックを全3部作で映画化する第1部。監督は「トリック」「明日の記憶」の堤幸彦。世は20世紀末、小学生の頃に遊びで書いた“よげんの書”の内容通りに、世界滅亡の危機が現実に起こりつつあることを知ったケンヂは、かつての仲間を集め世界を救うために立ち上がる。しかし、事件の裏には“ともだち”と呼ばれる謎の存在が……。唐沢寿明、豊川悦司、常盤貴子ほか、主演クラスの俳優が多数出演する豪華キャストも話題。", "2008-08-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真夏のビタミン", "真夏のビタミン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 46, "恒例の伊丹映画祭で知られる兵庫県伊丹市が、市政施行50年となる平成２年度から始めたいわゆる“伊丹製作映画”の第５作。公募脚本を審査し、市が600万円を提供して製作された16ミリ映画である。同じ高校の２年生で、水泳部に所属する熱血少女ちなつとライバル真央子。二人が“女の命＝髪”を懸けて宿命の対決を果たすが……。“アッという間のお笑いジェットコースター大活劇”とは作者の弁。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かっぱのすりばち", "かっぱのすりばち", "", "", "http://image.eiga.k-img.com/images/movie/57974/315x210.jpg?3873", 20, "福島県の南端に位置する塙町で生まれた民話がもとの短編アニメーション。片貝川に住む子ガッパのかんきちは、村の子どもたちと仲良くなり、遊びながら素潜りを教えていた。しかしある日、泳ぎが苦手な床屋の息子じんろくが溺死してしまい……。塙町の片貝川にある深いすり鉢状の穴を持つ岩「かっぱのすりばち」から着想を得て創作された民話で、絵本として出版化もされた。「だるまちゃん」「フイチンさん」など自主制作も多数手がけるアニメーション制作会社エクラアニマルによりアニメ化。", "2012-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COUNTERFEIT TRAITOR", "偽の売国奴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "第二次大戦中に実在した大物スパイ、エリック・エリクソンの実話に材を取ったサスペンス。ドイツに石油を売っていたため、売国奴と非難されていたエリクソンは、イギリスの諜報部のスパイとなって、ベルリンに飛んだ。やがて彼は、次々と仲間を増やして諜報網を整備するが、ついにはゲシュタポの手に落ちてしまう……。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CURSE OF THE MUMMY’S TOMB", "怪奇ミイラ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ハマー・プロ製作の、ホラー映画の古典ともいうべき作品。3000年の眠りから覚めたミイラが、1920年代のロンドンを恐怖に陥れる。ミイラ男が美女を抱きかかえ、地下水道を歩くシーンは圧巻。ミイラが兄の転生した男に滅ぼされる“運命的結末”も悲しい。", "1964-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嵐を呼ぶ男", "嵐を呼ぶ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "石原裕次郎を一躍国民的スターにした大ヒット作。この１作のヒットにより、それまで赤字経営だった日活は再建を果たした。この年の興行ベスト５のうち、２本が裕次郎の主演作品。このほかにも配収が２億円以上あったものが３本あったことを見ると、当時の裕次郎人気がうかがわれる。国分正一は銀座で評判の暴れん坊。そこで弟の英次は兄を売り出そうと、女流マネージャー・美弥子に頼み込んだ。ジャズドラマーを目指した激しい猛練習の中で、正一と美弥子はお互いに惹かれるものを感じ始める。いよいよライバル・チャーリー桜田とのドラム合戦の前日、正一はトラブルに巻き込まれ、右手をケガする。当日、右手の痛みから苦境に立った正一は、マイクを握り歌を歌い、満場の拍手を浴びる。屈辱を受けたチャーリーは、美弥子に横恋慕する芸能界の黒幕・左京とともに正一の右手を完全に潰す。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青べか物語", "青べか物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "山本周五郎の名作を、川島雄三が散文詩的に描いた叙情味あふれる作品。映画の舞台となった浦安は、今では東京ディズニーランドができてにぎやかだが、当時はさびれた漁村だった。その田舎の人間の生活感や、美しい水辺の風景を捉えた映像が秀逸。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WISDOM", "ウィズダム 夢のかけら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ウィズダムは若気のいたりで、盗難車を乗り回し、有罪判決を受けたことがある。そのために彼は、一所懸命に就職口を探すにもかかわらず、すべてのところが門戸を閉ざす。やり場のない不満を抱き、銀行強盗をはたらく青年とその恋人の逃避行をE・エステヴェスが描いた好編。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DECEIVED", "幸せの向う側", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "結婚６年目のエイドリアンは、素晴らしい娘と理想的な夫に囲まれたバラ色の人生を送っていた。だが、ある事件をきっかけに夫との間にそれとはわからない微妙なすき間が生じる。やがて夫が事故で死亡、その事故を境に、愛する夫への疑念が次々と彼女の人生に襲いかかってくる。人間関係における信頼の崩壊をテーマにした心理サスペンス。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REAL GENIUS", "天才アカデミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "15歳の天才少年ミッチはその才能を認められ、高校を通り越して最先端科学を研究する大学へ入学。ルームメイトのクリスと意気投合し、天才的な発明を利用してイタズラ三昧、大騒動を巻き起こす。全編ユーモアとジョーク満載の爽快な学園コメディ。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Goodbye Bafana", "マンデラの名もなき看守", "", "", "http://image.eiga.k-img.com/images/movie/53424/315x210.jpg?2376", 117, "南アフリカ初の黒人大統領となったネルソン・マンデラの知られざる若き日々を、「ペレ」「愛の風景」の名匠ビレ・アウグストが映画化。反政府運動の指導者として27年間に渡り獄中生活を強いられながらも希望を持ち続けたマンデラの姿を、彼との交流の中で美しい魂を取り戻していく白人看守の視点から描き出す。マンデラ役に大ヒットTVシリーズ「24」のデニス・ヘイスバート、白人看守役に「恋におちたシェイクスピア」のジョセフ・ファインズ。", "2008-05-17", "2,7,37,4,67", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE THREE MUSKERTEERS", "三銃士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "A・デュマ原作の、同名古典小説をハリウッドが５度目の映画化。今作の特徴はデュマが設定したヒーローとほぼ同じ年齢の若手スターを抜てき、抜けるような青空を背景に、ひたすら陽気でさわやかな青春活劇が展開することだ。若き王に仕える三銃士と血気盛んな青春剣士ダルタニアンが邪悪な枢機卿の陰謀から間一髪、王を守る姿を描いている。剣戟の爽快感をはじけさせるには、悪役たちの凄味がやや不足気味。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新日本暴行暗黒史・復讐鬼", "新日本暴行暗黒史・復讐鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "若松孝二監督による「日本暴行暗黒史・異常者の血」に始まる“日本暴行暗黒史”ものの第３作。肺病のため村人から村八分にされた男が、村の人間を次々に殺して復讐する。差別された男の憤慨が殺人、レイプという若松孝二のキーワードで鮮烈に表現される。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("未来忍者 慶雲機忍外伝", "未来忍者 慶雲機忍外伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "ファミコン・メーカーのナムコ製作による奇想天外なSFニンジャ映画。どこかの星のいつかの戦国時代。黒鷺機忍軍団に誘拐された諏訪部家のサキ姫を救うべく戦う、未来忍者の活躍を描く。自由な発想で設計された数々のメカや、SFXを多用したアクション・シーンなど見どころ満載の作品だ。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花のあすか組NEO！", "花のあすか組NEO！", "", "", "http://image.eiga.k-img.com/images/movie/54424/315x210.jpg?2829", 71, "1988年に映画化され、一大ブームを巻き起こした高口里純の大ヒットコミックを再び実写映画化。中毒になると自殺に至るといわれるピンクの飴・ウルトラフルーツ（UF）が若者の間で蔓延していた。あすか（高山侑子）の通う学園で、ノノカ（河合優）達はその危険性を知りながらもUFを生徒たちに売りさばき、多額の報酬を受け取っていた。UFを根絶するため、あすか達は立ち上がるが、その背後には宿敵・紅塾の影があった……。", "2009-04-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VENOM", "恐るべき訪問者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "誘拐犯一味が人質を盾にろう城する家に、猛毒をもつヘビ“ブラック・マンバ”が紛れ込んだ。犯人、人質、警察、そして毒ヘビを交えて息詰まるようなサスペンスが展開する……。一癖も二癖もある個性派が多数登場。特に犯人の一人、K・キンスキーの冷酷な演技に注目。", "1981-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THIS IS MY FATHER", "フィオナが恋していた頃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "母が見知らぬ青年に肩を抱かれて微笑んでいる一枚の写真。自分の父のことをまったく知らない中年の高校教師は、話すことも出来ない病床の母の秘められた青春と自分のルーツを知るために旅に出る。そこには、母の体験した悲恋と悲しい真実が隠されていた……。本作は、兄弟である監督、撮影監督、主演俳優が、母から聞いたアイルランドの悲話を元に完成させた“母の恋物語”である。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八百万石に挑む男", "八百万石に挑む男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "牧野省三・衣笠貞之助・伊藤大輔らにより無声映画時代よりいく度となく製作された講談噺の映画化。浪人・山内伊賀之亮は将軍吉宗の御落胤である天一坊を擁し天下取りを狙うが、“知恵袋”松平伊豆守と名奉行・大岡越前の前に野望をくじかれ、二人の死でこの天下の大騒乱の幕は降りる。この物語は映画化のたびに様々な解釈が施され、天一坊ニセ者説から幕府の事件でっちあげ説まである。本作が東映時代劇の異色作として遇されたのは、右太衛門のチャンバラ場面がないためだけでなく、二人の敗れていく男たちに心情を寄せた中川信夫の演出によるところが多い。中川の真骨頂を示す代表作である。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bad Biology", "バッド・バイオロジー　狂った性器ども", "", "", "http://image.eiga.k-img.com/images/movie/54474/315x210.jpg?2648", 85, "カルト的人気の「バスケット・ケース」などで知られる鬼才フランク・ヘネンロッターが、アブノーマルな愛を描くホラー。7つの性器を持ち、その異常なまでの性欲を満たすため、毎晩行きずりの男とベッドを共にしているジェニファー。コンプレックスから薬物依存に陥った結果、巨大で制御不能になってしまった性器の処理に悩むバッツ。そんな2人がある日出会い、究極の愛とオーガズムを求めて暴走する……。", "2009-06-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("酔っぱらい天国", "酔っぱらい天国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "飲んべえには耳が痛い、苦い笑いを含んだ社会風刺ドラマ。親子二人暮らしの耕三と史郎は、どちらも大酒飲み。ある日、史郎はバーで野球選手とケンカになり、誤ってバットで殴り殺される。息子の死に絶望した耕三は、妊娠しているという史郎の婚約者・規子を引き取った。その規子は、野球選手に復讐を誓うが……。笠智衆が耕三に扮し、小津作品の静かな父親像とはうって変わった、酒乱の型破りな初老の男に扮している。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブルーバ", "ブルーバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "当時、大映と提携していた米サミュエル・ゴールドウィン撮影所で撮られた、珍しい和製ターザン映画。ジャングルの奥地で猛獣と暮らす野生児ブルーバ（実は幼くして行方不明になった日本人という設定）が、ダイヤモンドの鉱山を探す日本人の父娘を助けて奮戦する。主演は五輪の水泳選手、浜口喜博。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL GRANDE COCOMERO", "かぼちゃ大王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "イタリアに実在した医師の手記をもとにして映画化された作品。ある日、アルトゥーロの勤める病院に13歳のピッピが担ぎ込まれた。彼女の症状はてんかん症。はっきりとした原因はわからないが、彼は直感的にピッピの発作が精神的な部分に影響しているのではと感じていた。離婚歴があり、別れた妻へ罪悪感を抱いていた彼は、過去に経験した個人的な問題を手掛かりに、ピッピとの接し方を見つけ出そうとする。ビデオ発売時タイトルは「私が愛した少女」。", "1992-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("瀬戸内少年野球団", "瀬戸内少年野球団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "敗戦直後の淡路島国民学校を舞台に、戦争の影を引きずった大人たちと、無邪気に野球に熱中する子供たちの姿を描く。作詞家・阿久悠が、郷里・淡路島での幼年時代を書いた同名小説の映画化。ヒロインの少女や野球団の少年はすべて公募で選ばれた。DVDは「瀬戸内三部作 メモリアルDVD-BOX」に収録。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE O’CLOCK HIGH", "タイムリミットは午後３時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「U2／魂の叫び」のP・ジョアノーによる学園コメディ。ひょんなことから、ハイスクール一番の巨漢で狂暴な転校生と決闘することになったジェリー。決闘の時刻は午後３時……。絶体絶命の危機に直面した平凡な高校生の恐怖の一日をスピーディーなテンポで描く。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pa-Pa-Pa ザ★ムービー　パーマン　タコDEポン！　アシHAポン！", "Pa-Pa-Pa ザ★ムービー　パーマン　タコDEポン！　アシHAポン！", "", "", "http://ecx.images-amazon.com/images/I/31HRN941STL._SL210_.jpg", 34, "藤子・F・不二雄原作による人気アニメ「パーマン」の平成版劇場シリーズ第2弾となる短編アニメ。南の島でバカンスを楽しんでいた人気アイドル歌手・星野スミレ＝パー子が、謎のタコ軍団に連れ去られた。その犯人は、コンピュータ制御されたタコを使って世界征服を企むドクトル・オクトだった。パー子のコピー・ロボットから連絡を受けて救出に向かうパーマン1号だったが、捕まってマスクとマントを奪われてしまい……。", "2004-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さよなら夏休み", "さよなら夏休み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "日本の原風景が広がる岐阜県郡上八幡を舞台に、ひとりの少年の成長をつづった人間ドラマ。11歳の裕史は、母親の事情で東京から郡上八幡の寺に預けられてしまう。絶望と孤独感にさいなまれて心を閉ざすが、雄大な自然や風土のなかで少しずつ現実を受け止めていく。やがて若き女性教師の水帆に恋心を抱くが、ある日、彼女の身に悲しい出来事が起きる。それから30年後、同所を離れて大人になった裕史は、息子を連れて再び郡上八幡を訪れる。", "2010-11-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿郎的故事／ALL ABOUT AH−LONG", "過ぎゆく時の中で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "貧乏だが一人息子と仲良く暮らす元レーサーのトラック運転手アロンは、10年ぶりに昔の同棲相手と再会する。女はその時初めて、彼の息子が自分の子供であることを知らされ、母親としての本能が芽生える。自分と暮らすより彼女と一緒の方が恵まれた生活を送れると悟ったアロンは、わざと息子につらくあたるのだが……。男と女の大人の愛をタテ糸に、父と子の愛情を巧みに絡ませたラブ・ストーリーの秀作。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DISTANT COUSINS", "血族の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "広告会社のエグゼクティブ、リチャードは、愛する妻ケートと息子アレクスとともに、幸せな日々を過ごしていた。ある日、リチャードの従兄であるハリーとその恋人コニーが突然、彼らの前に現れる。従兄弟との10年ぶりの再会にリチャードは喜び、彼らを大歓迎するが、ハリーとコニーは恐ろしい目的を胸に秘めていた。リチャード一家の信頼を逆手にとり、完全犯罪を目論んでいたのだった。平凡な家庭に突然忍び寄る、いわれなき恐怖を描いたサイコ・スリラー。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BEARS AND I", "遥かなる子熊の森", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ベトナム戦争に疲れた身を癒すため、森林地帯の湖畔に小屋を借りて住み始めた帰還兵ボブ。自然の動物たちと親しむ彼は、母熊を亡くした３頭の子熊の母親代わりとなって、野生動物から身を守る術を教えるが……。人間と子熊の友情、そして別れをインディアンとの交流を絡めて、しみじみと描いていく。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BENJI THE HUNTED", "がんばれ！がんばれ！ベンジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "７年ぶりの“ベンジー”シリーズで、「ベンジー」「ベンジーの愛」「名探偵ベンジー」に続く第４作。演出は、前３作のすべてを手掛けたJ・キャンプ監督。今回は、彼の少年時代からの憧れのディズニーと手を組んだ作品。海難事故に見舞われ見知らぬ土地に流れ着いたベンジーと、ハンターに母親を殺された４匹のクーガーの子供たちとの交流を心温まるタッチで描いている。ベンジーの演技が見もの。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT CROSSING", "気球の８人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "共産圏での生活に息の詰まる思いをしたピーターと親友のグンター。彼らは同僚ヨゼフの息子が射殺された事件を機に、家族を連れ熱気球で亡命することを誓い合った。計画の途中何度も難題にぶつかりながら、あきらめずに実行を目指す彼らは……。実話に基づく映画である。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARACHNOPHOBIA", "アラクノフォビア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「グレムリン」「ポルターガイスト」などを手掛けたスピルバーグ一家の名プロデューサー、フランク・マーシャルの監督デビュー作。カリフォルニアの小さな町に南米の毒グモが紛れ込み、異常繁殖して人を襲いはじめる。いわゆる“動物パニックもの”のバリエーションだが、とぼけたコメディ風の演出がミソ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Stepfather", "ステップファーザー　殺人鬼の棲む家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES SOUS−DOUE´S", "ザ・カンニング〔IQ＝０〕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ルイ14世予備校に通うイタズラ好きの学生たちのドタバタ騒ぎを描いた爆笑映画。バカロレア（フランス大学入試テスト）合格を目指して、前代未聞のカンニング作戦を実行する彼らだが……。奇想天外な方法で、教師の目をくらます姿が印象的。", "1980-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("More Than Blue", "悲しみよりもっと悲しい物語", "", "", "http://image.eiga.k-img.com/images/movie/54844/315x210.jpg?3580", 105, "クォン・サンウ、イ・ボムス、イ・ボヨンら人気スターが集結した韓流メロドラマ。両親に捨てられたラジオディレクターのケイと、交通事故で天涯孤独となった作詞家のクリーム。2人は時には家族、時には親友、そして時には恋人のようにお互いを支えあって同居を続けてきたが、ある日ケイは自身の余命がわずかだと宣告される。ひとりぼっちになってしまうクリームのため、ケイは彼女を自分が見込んだ歯科医ジュファンに託そうと決心する。", "2010-02-27", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("佐々木小次郎", "佐々木小次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 151, "1950年・1951年に連作された東宝版「佐々木小次郎」３部作の再映画化。監督も同じ稲垣浩。天才剣士・佐々木小次郎が旅の修行の中から秘剣“つばめ返し”をあみ出し、宮本武蔵と決戦に至るまでを描く。若き尾上菊之助（現・菊五郎）の太刀さばきが凛々しい。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STILL WATCH", "暗夜に過去がよみがえる 美人キャスターの恐怖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "TV局の美人キャスターは、ある女性上院議員の過去を調べていた。そこで、彼女は意外な事実を発見する。サスペンス界の巨匠M・ピギンス原作の本格ミステリー・ドラマ。女性上院議員には、アネゴ肌の役がよく似合うA・ディキンソンが扮し、L・カーターと対決。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兵隊極道", "兵隊極道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "昭和12年、陸軍に召集され二等兵に転落した島村清吉は、シャバで売った任侠精神を看板に、威張り散らす古参兵を叩きのめす。おかげで、たちまち親分格にのし上がり、島村を中心に極道部隊が編成される。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PUNK ROCK MOVIE", "パンク・ロック・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "1970年代後半に世界的なムーブメントを巻き起こし、その後の音楽シーンに多大な影響を与えたパンク・ロックのドキュメント。当時ロンドンでパンク専門のライヴハウスのDJをしていた監督のレッツが、自らカメラを回して捉えたライヴ・シーンは迫力十分だ。クラッシュやセックス・ピストルズ、スージー＆ザ・バンジーズのオフ・ステージの姿など、ファン必見のプレミア映像が満載。", "1978-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE YOUNG LIONS", "若き獅子たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 168, "A・ショウの原作による第二次大戦を舞台にした作品。ナチの将校とアメリカ兵を軸に戦争という悲劇を鋭く見つめている。白黒のシネマスコープの陰影の強いカメラが妙に生々しい戦場を映し出す。銃後を守るH・ラングの美しさも印象的。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE VALLEY OF GWANGI", "恐竜グワンジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "20世紀初頭、メキシコの禁断の谷で捕獲した大恐竜が町で大暴れ。人々を恐怖のどん底に陥れる……。原案は数々の怪獣映画を手掛けているオブライエン。その弟子R・ハリーハウゼンが作り上げた恐竜の特殊撮影が素晴らしい。", "1969-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("濡れ髪剣法", "濡れ髪剣法", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "市川雷蔵主演によるカラッとして明るいムードの時代劇。松平家の若殿・源之助は、剣にかけては家中第一とうぬぼれていたが、許婚の鶴姫の前でその近習・林主水にこてんぱんに打ちのめされる。源之助は自分の本当の力を試そうと城を飛び出し江戸へ向かう……。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEA FOR TWO", "二人でお茶を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "旅回りの一座が金策に窮し、資産家の伯父に出資を頼んだ歌手のナネット嬢は、48時間一言もしゃべらないという変な賭けをするはめに。ところが間の悪いことに、恋人が意を決してプロポーズしてきた……。舞台劇『ノー・ノー・ナネット』の映画化で、D・デイの魅力と名曲を散りばめた佳編。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUBMERGED", "沈黙の追撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "アクション・スター、スティーヴン・セガール主演によるサスペンス活劇。マインド・コントロールの技術を駆使する凶悪テロ集団を相手に、セガール扮する主人公が仲間と共に死闘を繰り広げる。いつもながらのキレのあるセガールの武術アクションに加えて、迫力の銃撃戦も見逃せない。テロ一味に拉致された部隊を救出するため、元海軍兵士コーディーは仲間と南米へ。だが、そこには意外な罠が彼らを待ち受けていた。", "2006-01-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXCESSIVE FORCEII：FORCE ON FORCE", "リーサル・コップ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "アメリカ陸軍犯罪捜査部（CIDC）捜査官の活躍を描いたハード・アクション。CIDCのハリーは、かつての自分の恋人で元陸軍女性司令官のランデルが、残虐な殺人事件の犯人であることを知る。彼女はプロの暗殺集団を結成し、組織への加入を断ったハリーを狙っていた……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BATTLESTAR: GALACTICA", "宇宙空母ギャラクティカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "故郷を追われ、はるか彼方の伝説の星“地球”を目指し、宇宙空母ギャラクティカは出発した。だが、行く手には、人類絶滅をはかる金属人間サイロンの罠が……。空母から戦闘艇が出撃するシーンは実に見事。TVシリーズのパイロット版３時間を再編集した劇場版。J・ダイクストラが製作及び特殊撮影で参加している。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HARD TRUTH", "ハード・トゥルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "個性派俳優たちが熾烈な三角関係を熱演するサスペンス・アクション。銃撃戦で民間人を巻き添えにし職を追われた敏腕刑事のマンツにとって、唯一の慰めが恋人リサの存在。市会議長報道担当官を務めるリサは、議長が受け取った300万ドルの賄賂を強奪しようとマンツを誘う。彼女の虜になっているマンツは断ることはできず、熟練の金庫破りエスリッジを仲間に加える。しかしリサがエスリッジに急接近したことから、計画は破滅へと走り始めた。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING CREOLE", "闇に響く声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "プレスリーの主演４作目。家が貧しく、アルバイトに明け暮れたため学校を落第した若者が、父を恨んでヤケを起こし、ヤクザ仲間に入る。そんな彼が、父と恋人の愛によってナイトクラブ歌手として認められるまでを描く。プレスリーは11曲を歌う。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖怪タレント　天狗御鼻乃助是清", "妖怪タレント　天狗御鼻乃助是清", "", "", "http://image.eiga.k-img.com/images/movie/57374/315x210.jpg?3389", 16, "芸能事務所イトーカンパニー所属の俳優たちが出演するショートムービーをネット配信している「イトーチューブ」で公開された作品を映画館で上映する「イトーチューブ Shrot Movie Colleciton」の1本。かつては人間に恐れられていた妖怪が、いまや芸能界に欠かせない人気タレントに。その実態を人気ドキュメンタリー番組「情熱大陸」風に描く。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Holcroft Covenant", "第三帝国の遺産", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "　アメリカ人に引き取られたドイツ生まれのノエル・ホルクロフトは、ヒトラーの腹心の部下だった元ナチス将校の亡父が40億ドルの遺産を残していたことを知る。ノエルはその捜索を開始するが、命を狙われることに……。ロバート・ラドラムのベストセラー小説「ホルクロフトの盟約」を映画化。", "1985-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gruppo di famiglia in un interno", "家族の肖像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "　優雅な邸宅を所有する老人のもとに、ブルモンティ夫人とその愛人、夫人の娘らが転がり込んでくる。初めこそ、平穏な生活を阻害されたと感じた老人だったが、次第に彼らに興味を抱き始める。全編室内で撮影され、主要登場人物が5人というルキノ・ビスコンティの意欲作。脚本のスーゾ・チェッキ・ダミーコはもちろん、シルバーナ・マンガーノ、ヘルムート・バーガーなどビスコンティ作品の常連俳優が名を連ねる。", "1978-11-25", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARAHAN", "ARAHAN〈アラハン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "韓国映画界の新旧スターが顔を揃えたカンフー・アクション大作。正義感ばかりが先走る警察官の若者が、伝説のカンフー・マスターに弟子入りを果たす。その過酷な修行の過程をワイヤー＆CG満載のアクションの連続で活写する。ほぼノースタントで挑んだ俳優たちの生身のアクションが見もの。ドジ警官サンファンは、ひょんなことからカンフーを学ぶイジンと出会う。“七仙”と呼ばれるマスターの道場に連れてこられた彼は……。", "2005-10-22", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("倍音　ばいおん", "倍音　ばいおん", "", "", "http://ecx.images-amazon.com/images/I/3134R6AG8FL._SL210_.jpg", 100, "「音と音楽」をコンセプトに、劇団員の女性とバンドマンの恋をつづったラブストーリー。波長が合わない恋人に自分を合わせながら平凡な毎日を送っていた劇団員・鈴香と、バンドメンバーとのすれ違いにいつもいらだちを感じているボーカリスト・カイ。まったく接点のなかった2人が偶然に出会い、ひかれあっていくが……。主演は映画初出演となる山本理恵と加藤隼平。歌手として活躍する元プロレスラーのハヤブサが本人役で出演。", "2004-12-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TINTORERA", "タイガーシャーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "体長６メートルのサメが無残に人間を襲うパニック映画。グラマーなS・ジョージが立派な水着姿で登場してくれるのも楽しみ。カリブ海の保養地に幻のタイガーシャークが出現。命知らずのシャークハンター二人組と巨大サメの死闘が展開される。", "1977-01-01", "3,19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER BLAUE ENGEL", "嘆きの天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ドイツの小説家、マンの『ウィンラート教授』の映画化。ハンブルクで高校教師をしているラート教授は、ふとしたきっかけで街のキャバレーの踊り子、ローラに出会い、心惹かれる。世間知らずの教授は、教職を捨てローラと結婚。数年後、キャバレーの一座に交じって、ローラに捨てられた教授の無残な姿があった。古い権威主義の人間が、女性の性的魅力の前に理性を失い、人生の破滅に至ってしまうまでが、監督の厳しい人間観察によって描出された名作。スタンバーグと、ディートリヒが初めてコンビを組んだ作品で、劇中で披露される脚線美と主題歌は妖しげな魅力に満ち、心に残る。", "1930-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("汚い奴", "汚い奴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "八百長事件で永久追放となった元ボクサーの高須。今や、ヤクザから中学生までを狙い相手をとことん追い詰める恐喝屋となった。彼はヤクザのボスを恐喝して１億円を要求するが、逆に最愛の妹を誘拐される……。同名漫画を“極道記者”シリーズや「新・悲しきヒットマン」でハードボイルドものに才を発揮した望月六郎の監督で映画化。主人公の徹底した“汚い奴”ぶりが小気味よい。相棒に扮した山田辰夫が好演。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海嶺", "海嶺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "1830年代の鎖国時代に、船がしけにあってアメリカ大陸に漂着、そこで出会った博士や宣教師らの影響を受けて初めて聖書の翻訳を試みた３人の男たちの物語。ラストで幕府が彼らの乗った船を砲撃するのは、1830年７月の“モリソン号事件”の史実に基づいている。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍拳／DRAGON’S FIST", "ジャッキー・チェン 龍拳", "", "", "http://ecx.images-amazon.com/images/I/51xGvNHpqNL._SL210_.jpg", 90, "３年間の猛修行を積み、必殺の拳法“龍拳”をマスターした若者、タン。師匠を殺したカンフーの達人チュンと和解した彼は手を取り合って、村人を苦しめるヤクザの一家を潰滅すべく戦いを挑んでいく。今や世界的なビッグスターとなったJ・チェンの若々しい魅力が光る、初期のカンフー・アクション。", "1978-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("螢火", "螢火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "織田作之助の原作『螢』を五所平之助監督が映画化。伏見の船宿“寺田屋”の女将・お登勢はしっかり者で、甲斐性なしの夫・伊助を助けて店をきりまわしているが、伊助は妾を囲っていた。そんなある日、捕り方に追われた坂本竜馬が“寺田屋”へ飛び込んでくる……。五所平之助監督にとって唯一の本格的な時代劇。坂本竜馬のような有名人が登場するにもかかわらず、五所監督はここでも市井の平凡人の視点に立って人生の機微をじっくりと描いている。しっかり者のお登勢に扮した淡島千景が好演、同じ年に出演した成瀬巳喜男監督の「鰯雲」との２作によって毎日映画コンクール女優主演賞を受賞した。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Out on a Limb", "マシュー・ブロデリックの緊・急・事・態", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "大金を巡って繰り広げられる大騒動を描いたドタバタ・コメディ。14億ドルを動かす取引に大抜擢された主人公ビル。ところが、そんな彼の周りに次から次へと風変わりな人たちが現われはじめ……。1989年製作の「3人の逃亡者」でハリウッド進出を果たしたフレンチ・コメディの名匠フランシス・ベベールが、「ウォー・ゲーム」「グローリー」のマシュー・ブロデリックを主演に迎え、再びアメリカで手掛けた作品。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の又三郎", "風の又三郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "原作は宮澤賢治の同名の名作ファンタジーで、これを俳優から監督に転向して間もない島耕二が演出に当たっているが、その演出は原作のムードと味わいをうまく残し、詩情あふれる作品に仕立て上げている。北海道から東北の僻村の小学校に転校して来た高田三郎は、夏休みの明けた９月１日に突然やって来て生徒たちを驚かすが、その日がちょうど二百十日だったので“風の又三郎”というアダ名がついた。皆とも仲良くなった三郎は、ある日河原で相撲をして遊んでいたが、投げつけられた上“風を吹かして見ろ”とからかわれたので、怒った三郎は本当に風を吹かせる。皆は驚いて逃げ出す。翌日、学校へ行くと三郎の姿はなかった。日本児童映画史上最高の作品の一つ。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Happy Together", "ハッピー・トゥギャザー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "大劇作家を目指すマジメ学生と、演技派舞台女優を夢見る乱痴気パーティー大好き少女。この二人が学生課のコンピュータ・ミスから何と同室のルームメイトになってしまう。おもしろおかしい二人の出会いと恋の過程を、「華麗なるギャツビー」や「ロミオとジュリエット」など名作のパロディーを交えて描く。", "1990-04-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THEY　CALL　ME　MR.TIBBS！", "続・夜の大捜査線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "S・ポワチエが刑事に扮し、人気を呼んだシリーズ２作目。サンフランシスコで起きた売春婦殺しの犯人を追う黒人警部ティッブス。やがて捜査線上に数人の容疑者が上がるが、その中には彼の親友シャープ牧師の名もあり……。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT OF THE JUGGLER", "ジャグラー ニューヨーク25時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "人違い誘拐という異常な事件を扱ったサスペンス。ニューヨーク市街でオール・ロケを敢行、朝から事件解決の夜までをドキュメンタリー・タッチで描いている。元警官である父親と不気味な異常者のチェイスは執拗にして強烈。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Daybreakers", "デイブレイカー", "", "", "http://image.eiga.k-img.com/images/movie/55184/315x210.jpg?4205", 98, "永遠の命を持つバンパイアによって世界が支配されている2019年。人類は絶滅寸前で、バンパイアに必要不可欠な人間の血液も底を突きかけていた。血液の代用を探すバンパイアの研究者エドは、人類の生き残りオードリーと出会い、バンパイアと人類の双方を救済できる驚くべき方法を知るが、バンパイアと人類は最終戦争に突入してしまう。監督は「アンデッド」（03）で知られる双子の兄弟ピーター＆マイケル・スピエリッグ。出演はイーサン・ホーク、ウィレム・デフォー、サム・ニール。", "2010-11-27", "11,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("anoretto〈アマレット〉", "anoretto〈アマレット〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "ドリームズ・カム・トゥルーの吉田美和、中村正人が初主演を務め、彼ら自身が温めていた原案を映画化。ドリカムの“そっくりさん”というユーモラスな役どころに扮した彼らが、理想と現実の狭間で悩む男女を背伸びせずに演じてみせる。主題歌はドリカムの『ラヴレター』。売れないフォークバンド“アマレット”のすみれと康夫。ドリカムのそっくりさんとして地方回りしている彼らにスカウトの話が舞い込んできて……。", "2004-12-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("香港の星", "香港の星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "仕事で香港に駐在する透は、難民診療所の医師、王椿伯の娘・星連と出会う。そして日本で医学を勉強する彼女と北海道で再会した透は、互いに愛し合うようになる。だが星連には医師となって父の仕事を手伝うという使命があった。香港、日本、そしてシンガポールを舞台にして展開する愛の物語。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前弁天", "喜劇・駅前弁天", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "諏訪湖のほとりの手打ちそば屋・徳之助と駅前ハイヤーの社長・孫作、美容院の亭主・三平は戦友同士だが、今ではそれぞれ女房の尻にしかれっぱなし。徳之助と孫作はカワイ子ちゃんとお楽しみが、バレてしまってさあ大変。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("転々", "転々", "", "", "http://image.eiga.k-img.com/images/movie/34094/315x210.jpg?1430", 101, "「時効警察」のオダギリジョー＆三木聡監督が再タッグを組んで放つロードムービー。幼い頃に両親に捨てられた孤独な青年・文哉。現在大学8年生の彼は、いつの間にか84万円もの借金を抱えていた。返済期限が迫ったある日、彼は借金取りの福原から奇妙な提案を持ちかけられる。それは、吉祥寺から霞ヶ関まで福原と一緒に散歩するだけで借金がチャラになり、100万円の報酬までもらえるというものだった……。", "2007-11-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("偽れる盛装", "偽れる盛装", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "溝口健二に師事した新藤兼人が溝口の「祇園の姉妹」に対する、いわばオマージュとして書き上げた本作品。監督は吉村公三郎。祇園の静乃家のおきくにはやはり芸者の娘・君蝶がいた。君蝶は男を次から次へと、それこそ蝶のように移っていく新しいタイプの芸者。君蝶には市役所に勤める妹・妙子がいて、同僚の孝次に恋していた。しかし孝次は商売がたき・菊亭の千代の養子だったため、二人の結婚は周囲に反対され……。借金のいざこざで、おなじみさんの且那に踏切で背中を刺される君蝶のエピソードが、祇園を飛び出て新しい世界に生きようとする妹たちと対比的に描かれる。色街の生活・風俗をきびきびと映し出す吉村演出も秀逸。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BOUNTY", "バウンティ 愛と反乱の航海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "1789年、乗組員の反乱によって南太平洋上で忽然と姿を消したイギリスの戦艦バウンティ号。M・ギブソンを主役に据え、今もなお謎に包まれているこの事件を、ラブ・ロマンスを盛り込むなど新解釈を加えて映画化。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YONGGARY", "怪獣大決戦ヤンガリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "元人気コメディアンのシム・ヒョンレが製作した、韓国映画史上初の本格的娯楽怪獣映画。２億年の時を経て眠り続ける大怪獣“ヤンガリー”の化石が発掘され、次々と不可解な事件が勃発する。そして、ヤンガリーの復活。パーカー大尉は、地球の運命を懸けた最後の戦いに挑む。ヤンガリーとは、“YONG”が龍、“GARY”が怪獣を意味する韓国の伝説上の怪獣のこと。ハリウッド俳優を起用するなど、世界を意識した作りになっている。当時破格の118億ウォンという巨費を投じて製作され、怪獣映画オタクの監督の意気込みも伝わる。", "2000-01-01", "5,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER　WEG　NACH　EDEN", "エデンへの道・ある解剖医の一日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ブダペスト在住の解剖医ケシュエリー・ヤノーシュの日常を追ったドキュメンタリー。死体を解体し葬儀のために元に戻す作業を捉えつつ、ヤノーシュの死生観や家族観を冷徹な視点で浮かび上がらせる。頭の皮を剥いで脳を取り出し、はらわたをかき回すなど、スプラッター映画も顔負けのショッキングなシーンもあるが、肉体の死を生命の一部として受け止める製作者の視点には注目すべきものがある。", "1995-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CINDERELLA LIBERTY", "シンデレラ・リバティ　かぎりなき愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "深夜12時までの休暇で町に出た海軍の兵隊ジョンは、酒場でビリヤードをしている女マギーと出会う。ビリヤードの賭けと兵隊相手の売春で生活しているマギーは、11歳の息子ダグがいたうえ、妊娠中でもあった。マギーのすさんだ生活の様子を聞いたジョンは、ちょうど病気が発見され船に乗ることができなかったので、マギー親子の面倒を見ることにする。しかし、病院がジョンの身分証明書を紛失し、彼は給料がもらえなくなる。不幸は続き、マギーが流産。一度はまっとうな生活を始めていた彼女だったが、ショックのあまり元の売春婦に戻り、しかもダグを残して姿を消してしまう。ジョンはダグと一緒にマギーを探すのだった。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EARTHQUAKE", "大地震", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "1971年に、ロサンゼルスを襲った大地震をモデルにした特撮大パニック映画。見る間に崩れ落ちるビル、地割れと火災、ダムの決壊、声もなく濁流に飲み込まれる人々……。公開時、低周波により観客の体を揺さぶるセンサラウンド方式を使用し、話題となった。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢なら醒めて……", "夢なら醒めて……", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "アイドル予備軍として健気に奮闘中の浅香アイは、コンビニの店員、利彦と知り合いになる。利彦はアイの熱烈なファンであり、彼女のことなら何でも知りつくし、彼女そのものの人生を生きたいと願っていた。そんな彼に、やがて心身の変化が訪れる……。ピンク四天王の一人として名を馳せるサトウトシキ監督が、竹内義和の原作をもとに、慕う者と慕われる者の関係を切なく、そして残酷に描きあげたサスペンス・ファンタジー。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("模倣犯", "模倣犯", "", "", "http://ecx.images-amazon.com/images/I/21QN8EP50KL._SL210_.jpg", 123, "宮部みゆきのミステリー小説を映画化。SMAPの中居正広主演。東京の下町で豆腐屋を営む有馬の孫娘・古川鞠子が失踪して10か月、事件は一向に進展していなかった。そんなある日、公園のゴミ箱から女性の右腕とショルダーバッグが発見される。その後、各局に犯人からの犯行声明と共に、それぞれ別の被害者と思われる女性の写真が届く。日本中が騒然となる中、有馬は鞠子を救い出すべく必死の捜索を行うが……。猟奇連続殺人事件を通して、現代社会が抱える病理に鋭く迫る。", "2002-06-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Orange and Sunshine", "オレンジと太陽", "", "", "http://image.eiga.k-img.com/images/movie/57634/315x210.jpg?3498", 106, "社会派で知られるイギリスの名匠ケン・ローチの息子ジム・ローチが長編初監督を務め、イギリスで1970年まで行われていた「児童移民」の実態を明らかにした女性マーガレット・ハンフリーズの物語を描いたドラマ。ノッティンガムで社会福祉士として働くマーガレットはある日、幼い頃に子どもだけで船に乗せられオーストラリアに送られたという女性に出会う。そのことから、英国が児童施設などに入った子どもたちを福祉の名の下に密かにオーストラリアに送っていたことが判明。オーストラリアに送られた子どもたちを待ち受けていたものとは……。主演は「奇跡の海」「ほんとうのジャクリーヌ・デュ・プレ」のエミリー・ワトソン。", "2012-04-14", "3,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("透明剣士", "透明剣士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "“勝プロ”の秘蔵っ子・酒井修が主演し、桂三枝ら関西喜劇陣が脇を固めた特撮時代劇。弱虫剣士・夕月三四郎の父が怪盗団に殺される。親切な妖怪“しょうけら”から、体が透明になる薬の製法を教えられた三四郎は、苦心の末に完成し、見事父の仇を討つ。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEVERLY HILLS COP III", "ビバリーヒルズ・コップ３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "カージャッカーのボスに上司を殺されたデトロイト市警の刑事アクセルは、一味を追ってビバリーヒルズへ乗り込む。かつて何度も騒動を巻き起こしたこの街で、彼は再び悪の組織に挑戦する……。巨大なテーマパークを舞台にしたアクション・シーンとE・マーフィの話芸が見どころの人気シリーズ第３作。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Legend of the Guardians: The Owls of Ga'Hoole", "ガフールの伝説", "", "", "http://image.eiga.k-img.com/images/movie/54534/315x210.jpg?3424", 90, "フクロウの世界を描いたキャスリン・ラスキーによるファンタジー小説「ガフールの勇者たち」を、「300 スリーハンドレッド」のザック・スナイダー監督がアニメ映画化。ヘレン・ミレン、ジェフリー・ラッシュ、ジム・スタージェスらが声優を務める。若きフクロウのソーレンは、世界征服を企む「純血団」からフクロウの王国を守ったガフールの戦士たちの伝説に憧れていた。兄のクラッドはそんなソーレンを笑いものにしていたが、ある日2人は「純血団」に捕われてしまい、大胆な脱走を図ることにする。", "2010-10-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忠臣蔵〈天の巻、地の巻〉", "忠臣蔵〈天の巻、地の巻〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "３月31日封切り、落ち込んでいた興行成績を挽回するための日活、春の大作であった。また1928年「実録忠臣蔵」のフィルム焼失という不運のうちにこの世を去ったマキノ省三の十周忌を記念した作品でもある。息子・正博は、父の省三が忠臣蔵で最も力を入れたという、大石内蔵助と立花左近が出会うシーンを、阪妻の大石、千恵蔵の左近で念入りに仕上げ、二人の腹芸の妙もあって本編中の見どころとした。現在残っているプリントは、昭和31年12月12日再公開のもので、初公開時19巻だったものが12巻になっている。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA GUERRE DES BOUTONS", "わんぱく戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "主役のちびのジュビスを演じたアントワーヌ少年の可愛らしさが人気を呼んだフランス映画。村のわんぱく少年のグループは互いに仲が悪くて、村の境にある原っぱがいつもの戦場。そして勝利の印は相手の上着のボタンを全部取ってしまうこと。そこで片方のわんぱく軍団が考えたのは、なんと素っ裸で戦うというアイデア。これならボタンを取られることもない……。ゴンクール賞受賞作家ルイ・ペルゴーの小説の映画化で、フランスの片田舎ののんびりムードと、少年たちの可愛いおちんちんをぶら下げての戦争ごっこが微笑ましい。２年後、この作品で人気者になったアントワーヌで、「わんぱく旋風」が作られた。", "1961-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シルエット Silhouette", "シルエット Silhouette", "", "", "http://image.eiga.k-img.com/images/movie/54144/315x210.jpg?4465", 75, "恋愛に翻弄される男をユーモラスに描いたモノクロ・サイレント映画。監督は本作が劇場デビュー作となる田島基博、主演は「BULLET BALLET／バレット・バレエ」「窓辺のほんきーとんく」の辻岡正人と元AV女優の長澤つぐみ。冴えないサラリーマンの男がテニスクラブのコーチに一目ぼれし、懸命なアプローチの末に交際することに。しかし、会社を首になり生活のバランスを崩した男は犯罪に手を染め始め……。", "2008-11-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GHOST AND MRS.MUIR", "幽霊と未亡人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "海辺の家に美しい未亡人が越してきた。この家は幽霊が出ると評判の屋敷。勝ち気な未亡人は登場した幽霊にもひるまず、けんかをふっかけるが、やがて彼女は幽霊と心が通うようになる……。ユーモアあふれるファンタジー・ロマンスの傑作。船乗りの幽霊に扮したR・ハリスンのお人好しぶりが楽しい。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOUSE　HUNT", "マウス・ハント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "製糸工場経営者の父が死に、アーニーとラーズの兄弟は古ぼけて荒れ果てた一軒の屋敷を譲り受ける。オンボロ屋敷にがっかりした二人だったが、それが19世紀の有名建築家の手によるもので最低でも数百万ドルの値打ちがあることが判った。二人はさっそくリフォームに取り掛かるが、屋敷には一匹のネズミが住んでいた。たかがネズミとタカをくくった彼らだったが、そのネズミに振り回されることになる。ネズミ捕り器から始まり、凶暴なドラ猫、マウスバスター相手に連戦連勝を重ねるネズミの活躍を、軽快に描き出した爆笑コメディ。マニアックなマウスバスターに扮したC・ウォーケンの怪演も見もの。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last House on the Left", "ラスト・ハウス・オン・ザ・レフト　鮮血の美学", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月給泥棒", "月給泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "“クラウン・カメラ”の営業マン・吉本は出世のためなら何でもする男。四方八方ウソで固めて自社のカメラを売り込むが、やがて思いもよらぬ状況に陥って……。戦争・時代劇アクションに見せる岡本喜八の才能とは別の魅力が楽しめるサラリーマン・コメディ。初期の岡本はこちらの路線が中心であった。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ボディドロップアスファルト", "ボディドロップアスファルト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "フィクションの世界に心奪われた女の子の不思議な世界をポップに綴ったアバンギャルド・ムービー。退屈な毎日を変えようと書き上げた恋愛小説で、一躍売れっ子小説家になったエリ。しかし自分の小説の主人公リエに嫉妬したエリはリエへの復讐を考える。メインストリームではない映像作家を応援する愛知芸術文化センターの第９作企画作品。あがた森魚、手塚眞らゲスト出演者も個性的。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Maverick", "マーヴェリック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "ギャンブラーのマーヴェリックは西部最大のポーカー大会出場のため、資金集めをしながら旅をしていた。道連れになった女スリ、アナベルや謎の保安官クーパーとともに、彼は賞金50万ドルを求めて大会にのぞむが……。「リーサル・ウェポン」の監督・主演コンビによる人気TVウエスタンの映画化。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRIFTING SCHOOL", "漂流教室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "大林宣彦によって映画化されたこともある楳図かずおのコミックを、アメリカで映画化したSFサスペンス。アメリカ国防総省が原子力衛星を爆破したことから時空に歪みが生じ、とあるハイスクールが生徒もろとも異次元に吹き飛ばされてしまう。モンスターの出現や伝染病の発生、殺人鬼の暗躍など、数々の問題を抱えながら、生徒たちは決死のサバイバルを繰り広げ……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IF EVER I SEE YOU AGAIN", "わが心のジェニファー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "“CM界のモーツァルト”と呼ばれるCMソングライターのヤモメ男は、一流ピアニストとしての道を歩む友人と比べて暗たんたる気持ちになっていた。そんな時、ジェニファーという一人の女性が彼を勇気づけた。テーマ曲が魅力的なラブ・ロマンス。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンの猿飛佐助（ありゃりゃの巻・どろんどろんの巻）", "エノケンの猿飛佐助（ありゃりゃの巻・どろんどろんの巻）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "佳編「エノケンのちゃっきり金太」と、「エノケンの法界坊」の間に作られた1938年の正月用作品。戦前のエノケンものはもっぱら山本嘉次郎監督とのコンビ作品が有名であり、東宝喜劇映画のドル箱路線となっていた。しかし山本嘉次郎一人に演出を任せておくわけにもいかず、新たに「あきれた連中」「僕は誰だ」などの喜劇に才を見せた岡田敬を起用したのが本作品である。半人前の忍術師・猿飛佐助が、忍術の師匠である老仙人自らが変身した万人力のしゃもじを使い、真田幸村と十勇士の暗殺を狙う隣国の城主の息女・瀧姫と結婚するハメになるまでを描く。エノケンは、佐助としゃもじに化けるモウロク仙人との二役を演じている。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WORKING GIRLS", "ワーキング・ガールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "N.Y.マンハッタンの中級住宅街にあるアパートメント。その一室、女主人が経営する売春宿には、自ら娼婦を職業に選んだ10人ほどの女たちが２交代で働いている……。ニューヨークの新しい女性像を女性監督L・ボーデンが綿密な調査の上で、リアルに描いたセンセーショナルな一編。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("右門捕物帖 献上博多人形", "右門捕物帖 献上博多人形", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "島原の乱によりキリシタンたちが次々と教えに殉じ、天草島は火に包まれた。老中・松平伊豆守は乱を鎮めるため島に乗り込むが、何者かに襲われる。伊豆守に随行した隠密右門は、一行を妨害する敵を暴くべく立ち上がるのだが……。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CUTTER’S WAY", "男の傷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "カリフォルニアの閑静な住宅地サンタバーバラ。ベトナム戦争で足を負傷した男カッターと、女をだまして生活しているボーンの二人は、ある殺人事件に巻き込まれる。やがてあらわになる静かな町に潜む腐敗……。チェコスロバキア出身でアメリカン・ニューシネマの到来にも大きな影響を与えたI・パッサー監督によるハードボイルド編。J・ブリッジスの生きのいい演技も見ものだ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE OWL AND THE PUSSYCAT", "フクロウと子猫ちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "マンハッタンの安アパートで隣同士に住む、売れない作家フェリクスと鼻っ柱の強い娼婦ドリス。騒音騒ぎがきっかけで知り合いになり、恋に落ちてめでたくゴールイン、というラブ・コメディ。トム・ジョーンズが歌った主題歌がヒット。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOFT DECEIT", "Mr.クライム　知能犯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "天才的頭脳を持った知能犯罪スペシャリストのアダムは、大司教区の神父がマフィアに巨額の融資をしていることに目を付ける。仲間二人とともに、その600万ドルを強奪するが、タレこみによって逮捕される。現金の隠し場所を吐かないアダムに警察は一計を講じる。美人捜査官アンをおとりに使い、脱走させる見返りに現金の山分けを持ちかけさせたのだ。しかし、警察のウラをかいたアダムは、まんまと脱出に成功し、アンを誘惑して、意のままに操ろうとする。", "1994-01-01", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青空ポンチ", "青空ポンチ", "", "", "http://image.eiga.k-img.com/images/movie/53574/315x210.jpg?2591", 90, "「おそいひと」で物議を醸した柴田剛監督の青春映画。ミュージシャンを夢見て上京したカツヲは、デビューを目前に突然地元へと戻る。地元で花火大会のライブ出場バンド募集を知ったカツヲは、高校時代の仲間、マスオにバンド結成を持ちかけ、2人はメンバー探しを始める。やがて、トラブルを起こし祖父の元へ預けられた玉枝がドラマーで、エリート人生の夢を失い途方にくれていた舟木がギターで加わり、4人でバンドを結成するが……。", "2008-09-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROCK STAR", "ロック・スター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "突然スターになったロック狂の青年の音楽への情熱と自分探しのドラマを描く痛快青春映画。アマチュア・ロックバンドのヴォーカル、クリスは、意見の対立からメンバーを外されてしまう。途方に暮れていた彼は、あこがれのロック・バンドのヴォーカルに抜擢され、一躍スターに踊り出る。夢を実現して有頂天になった彼だが、支え続けてくれた恋人との間に溝が出来てしまい……。ステージに上がるとたちまち輝き始める主人公をM・ウォールバーグが熱演。ジェイソン・ボーナムら一流ミュージシャンも大勢出演し、ロック界の光と影を鮮やかに映し出す。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cool Hand Luke", "暴力脱獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "牢獄生活の経験を持つピアース原作の映画化。刑務所内の人気者ルークは、半殺しの目に遭いながらいく度も脱獄に挑戦するが、３度目の脱獄でついに射殺されてしまう。権力に徹底的に反抗する男を、ニューマンが見事に演じきる。硬派監督らしいリアリスティックな演出が光る傑作。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONLY YOU", "オンリー・ユー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "結婚を目前にしながら、子供の頃に占いで告げられた“デイモン・ブラッドリー”という運命の人の名が忘れられない女教師のフェイス。ある日、婚約者の友人から電話が入るが、その人の名は運命の人と同姓同名。電話を受けた彼女は、恋人を振り払い彼を追って義姉と二人イタリアへと向かう。そんな彼女の前にデイモンと名乗る男が現れるが、実は偽物で、イタリアに出張中のピーターというアメリカ青年だった。彼女の怒りを鎮めるためにピーターは、本物のデイモン探しを始めるのだが……。イタリアを舞台に、運命の男性探しを描いたラブ・ストーリー。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EVIL THAT MEN DO", "地獄で眠れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "中米のとある独裁政権国家を舞台に、旧友を惨殺された殺人プロフェッショナルが、政府の暗黒組織に敢然と立ち向かうさまを描く。バイオレンス・シーン満載のアクション。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　ばいきんまんVSバイキンマン!?", "それいけ！アンパンマン　ばいきんまんVSバイキンマン!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 20, "やなせたかし原作の人気アニメーション劇場版第21作「それいけ！アンパンマン だだんだんとふたごの星」と同時上映の短編。", "2009-07-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄砲玉の美学", "鉄砲玉の美学", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "黄金期の東映で、数多くの商業映画の名作を生み出した中島貞夫監督が、ATGで撮った作品。報酬につられ、九州征覇を目論むヤクザの手先となって宮崎に乗り込んだ一人のチンピラを描く。自爆にも似たラストシーンの壮絶な死が強い印象を残す。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("D3: THE MIGHTY DUCKS", "D3　マイティ・ダックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "少年アイスホッケー・チーム“ダックス”の活躍を描くシリーズ第３作。世界ジュニア選手権で優勝したダックスの面々は、有名高校のスポーツ特待生として二軍に迎えられる。ところがそこは、新しいコーチ、オライオンの厳しい特訓と、一軍チーム“ウォリアーズ”の嫌がらせの毎日。ついに、ウォリアーズの挑発に乗ってしまい、アイスホッケーの試合に挑むが、あえなく惨敗してしまう。そんなある日、キャプテンのチャーリーの元へかつて彼らを勝利へ導いたコーチ、ゴードンがやって来る。彼に励まされ、チャーリーはダックスの再起に挑む。ビデオ発売時タイトルは「D3　マイティ・ダックス／飛べないアヒル３」。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WESTERNER", "西部の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "巨匠W・ワイラーが描く正統派西部劇。1880年代のテキサスは、新天地を求めて移住してきた農民と在来の地主たちとのイザコザが絶えない。ロイ・ビーン判事は土地の実力者で、牧童たちを集めて農民に嫌がらせを行い、農民たちの反感を買っていた。ある日、怒った農民たちが判事を私刑にしようと騒動が持ち上がるが、流れ者のコール・ハードンが間に入って、なんとか両者を鎮める。だが、感謝祭の日に判事は農民たちに焼き打ちをかける。それを知ったコールは、副保安官になって、自ら判事と対決する……。開拓時代のテキサスを舞台に、悪徳判事と対決する流れ者の姿を描いた勧善懲悪の西部劇。判事に扮したW・ブレナンの好演が見もの。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("にっぽん零年", "にっぽん零年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "1968年、70年安保闘争・学園闘争といった熱狂的な運動を４人の新人監督、藤田敏矢(敏八)、河辺和夫、浦山桐郎、斎藤光正に撮らせようという企画が持ち上がる。そして、突然の中止命令、浦山と斎藤の降板がありながら、藤田、河辺両監督の撮影部分で完成させたのがこのドキュメンタリー。街中でデモ隊と機動隊が武力衝突を起こすなど、革命前夜のような1968年の東京。東大生の活動家、新宿をさまようフーテン少女、自衛隊に入った青年など、若者たちの揺れる思いと、狂熱が渦巻く当時の日本を切り取っていく。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("世界大戦争", "世界大戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "核戦争の鍵を握る同盟国側と連邦国側は一触即発の緊張状態を続けていたが、バーング海上での両軍の衝突から戦闘状態に突入。ついに両陣営から核ミサイルが発射され、東京をはじめとする主要都市は次々と核の炎に包まれていった……。松林宗恵監督は、外人記者クラブの運転手一家を庶民の代表として描き、この種の特撮映画にありがちなドラマ部分の弱さを見事に克服した。しかしこの映画の見どころはなんといっても特撮で、北氷洋上の両軍による空中戦から、世界主要都市の核ミサイルによる破壊までの数々の特撮シーンは、円谷英二の集大成といえる見事な出来ばえであった。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("葵の暴れん坊", "葵の暴れん坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "農民に肩入れする地回りの親分が、縄張り争いのため宿敵・助五郎に殺された。跡目を継いだ笹川の繁蔵は、遺言を守って子分をいさめていたが、出世欲に取りつかれた代官に取り入り、農民を苦しめる助五郎に堪忍袋の緒を切らし……。大川橋蔵の侠客もので、無頼の剣豪・平手造酒に扮した内田良平が好演。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版マクロスF サヨナラノツバサ", "劇場版マクロスF サヨナラノツバサ", "", "", "http://image.eiga.k-img.com/images/movie/55144/315x210.jpg?1966", 115, "2008年に放送されたSFアニメ「マクロスF」の劇場版2部作の後編。2009年にロングランヒットを記録した前編「劇場版マクロスF イツワリノウタヒメ」に続き、新天地を目指して宇宙を旅する移民船団「マクロス・フロンティア」を舞台に、ナゾの生命体バジュラとの戦いに身を投じる少年アルトと歌姫シェリル、シェリルにあこがれる少女ランカが織りなすドラマを描く。前編と同じ、テレビ版の総監督を手がけた河森正治が監督を務める。", "2011-02-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あさってDANCE", "あさってDANCE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "山本直樹原作の同名人気コミックの映画化。貧乏演劇青年のスエキチのもとへ突然、４億５千万円の祖父の遺産が転がり込んでくる。そんなある日、酔いざめの彼の前に綾と名乗る謎の少女が現れ……。中嶋朋子が今までのイメージを一新。主人公ににまとわりつく小悪魔的なヒロインを好演して話題を呼んだラブ・コメディ。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOEL", "NOEL〈ノエル〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "クリスマス・イヴを背景に、入院中の母親を抱えたバツイチのキャリアウーマンや、婚約者の嫉妬深さに悩む若い女性らの人間模様を描いた感動作。スーザン・サランドンとペネロペ・クルスという人気女優二人が共演。どこか心が満たされない人々の人生が交錯する、ささやかな奇跡の物語だ。離婚後、仕事と母の看病に追われてきたローズ。イヴの夜、年下の男性とのデートに失敗した彼女は、ニーナという美しい女性と出会う。", "2005-12-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ポケットモンスター 結晶塔の帝王", "ポケットモンスター 結晶塔の帝王", "", "", "http://ecx.images-amazon.com/images/I/61DWNzb7lML._SL210_.jpg", 74, "全世界でポケモンブームを巻き起こした人気アニメ劇場版シリーズ第３作。サトシ、ピカチュウ達一行が立ち寄った町、グリーンフィールドが突然、結晶化を始める。さらに結晶の中心部、結晶塔からの使者がサトシのママを連れ去ってしまう。サトシ達はママの救出に向かうのだが……。おなじみのキャラに加えて、結晶塔の幻のポケモン、エンテイも登場。迫力満点のバトルが繰り広げられる。", "2000-07-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霧の旗", "霧の旗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "山口百恵＝三浦友和コンビの第６作。松本清張原作の再映画化で、百恵の役は獄中で死んだ兄の復讐のため、三國連太郎扮する弁護士に近づくという、影ある女。彼女を愛する新聞記者を友和が演じている。百恵のキャラクターと役のイメージが重なり、演技的にも高く評価された作品。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOMO", "モモ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ある村の古代の円形劇場で、掃除夫ベッポがモモを見つける。モモは“聞き上手”で、村人たちが絶え間なく訪れ、不満や日常のことを話していく。いつしかモモは村人たちの人気者になる。そのなかでも特にベッポとジジとは親密だった。しかし、ある日“時間貯蓄銀行員”と名乗る時間泥棒が現れ、人々から時間を奪っていく。幸福だった人々はギスギスしていくのだった。そこでモモは時間泥棒に戦いを挑むが……。時間をつかさどるマイスター・ホラ役にJ・ヒューストンが扮している。M・エンデの原作は、世界30ヵ国で翻訳されているベストセラー。", "1986-01-01", "7,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("闘魚", "闘魚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "丹羽文雄の新聞連載小説の映画化で厚生省が後援した作品。本作製作当時は非常に恐ろしい病気だった肺結核に対する正しい知識や対処法が、映画内で語られている。OLの笙子は、肺を患い不良生活に明け暮れている弟の面倒を見つつ、出征中の婚約者の帰還を待って、日々働いていた。だが婚約者はその母のたくらみで別人との縁談を承諾させられて……。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SCARFACE MOB", "どてっ腹に穴をあけろ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "禁酒法時代のシカゴを舞台に、ギャングのボス、アル・カポネと、彼の野望に立ち向かったエリオット・ネスとその仲間たちの戦いを描くギャング映画。宿敵カポネに対するネスたちの執念の攻勢が展開する。ビデオ発売時タイトルは「ザ・アンタッチャブル」。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THUNDERHEART", "サンダーハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "サウス・ダコタのインディアン居留地で、殺人事件が起きる。この事件の背景には、先住民権運動（ARM）派と、合衆国政府を支持する改革派との対立があった。捜査にあたったのは、スー族の血をひくレイとベテランのクーテル。二人は、殺人現場で地元の警官、ウォルターと知り合うが、ウォルターはなぜか非協力的。やがてレイは、ARMのリーダーの一人、ジミーを容疑者として連行するのだが……。アメリカに厳として存在するインディアン問題とその民族的神秘性を撮りあげたサスペンス・アクション。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TALES FROM THE CRYPT DEMON KNGHT", "デーモン・ナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "アメリカの人気TVホラー・シリーズ『TALES FROM THE CRYPT』の初映画化、ハリウッド屈指のヒットメーカー５人が製作総指揮に名を連ねている。ある日、二台の車が衝突炎上。事故を起こしたブレイカーは奇跡的に傷だけですみ、安ホテルへ逃げ込む。一方、相手はといえば何と無傷。それもそのはず、その男は“悪霊”だったのだ。悪霊はブレイカーを許さず、あとを追ってホテルを目指した。ほかの宿泊客も巻き込んで、ブレイカーと悪霊との死闘が始まる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BARBARE", "ソフィー 遅すぎた出逢い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "死んだ父親の葬儀のために、フランスからチュニジアにやって来たソフィー。医師マイケルに父の面影を見た彼女は、マイケルとの許されない恋にのめり込んでいく。アラン・ドロンとの共演や同棲生活で話題を呼んだM・ダルクの第１回監督作品。自分の意思のままに生きる奔放な少女を描く。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEVEN THIEVES", "賭場荒し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "モンテ・カルロの華麗な街並みを背景に７人の盗賊たちが、あざやかな盗みっぷりを見せるアクション・ドラマ。出獄したポールは、仲間７人でモンテ・カルロのカジノの大金庫を破るという計画に大ノリ気、綿密な計画を立て、作戦は見事に成功するが紙幣がすべて続き番号の新紙幣であることがわかる……。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cadillac Records", "キャデラック・レコード　音楽でアメリカを変えた人々の物語", "", "", "http://image.eiga.k-img.com/images/movie/54294/315x210.jpg?4480", 108, "1947年のシカゴ、バーを営むポーランド系移民のレナード・チェスは、ギタリストのマディ・ウォーターズとハーモニカ奏者のリトル・ウォルターを雇い、彼らのブルースで客を魅了していた。その流れでレコード・ビジネスへ参入したチェスは、チャック・ベリーやエタ・ジェイムズなど数々のスターを生み出すが……。「戦場のピアニスト」のエイドリアン・ブロディ、「バスキア」のジェフリー・ライト、「ドリームガールズ」のビヨンセ・ノウルズら豪華俳優が、シカゴ・ブルース全盛期の時代に生きた人々を熱演。", "2009-08-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Descendants", "ファミリー・ツリー", "", "", "http://image.eiga.k-img.com/images/movie/57144/315x210.jpg?4032", 115, "「サイドウェイ」「アバウト・シュミット」のアレクサンダー・ペイン監督がジョージ・クルーニーを主演に迎え、ハワイで暮らすある家族に起こる出来事を描いたドラマ。祖先の土地を受け継ぎ、ハワイで妻と2人の娘とともに暮らすマット・キングだが、ある日、妻のエリザベスがボートの事故でこん睡状態に陥ってしまう。さらに、エリザベスには不倫の相手がおり、離婚まで考えていたことが発覚。友人や長女もその事実を知っていたことにがく然としたマットは、自らの人生を見つめ直すことになる。第84回アカデミー賞で脚色賞を受賞。", "2012-05-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH BECOMES HER", "永遠〈とわ〉に美しく…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "憎み合う二人の美女が不老不死の薬を飲み、絶対死なない体となって繰り広げる壮絶なバトル。M・ストリープの首が180度回転し、G・ホーンの胴体には風穴があく。R・ゼメキスが、豪華キャストとSFXの満載で描いた、身の毛もよだつブラック・コメディ。", "1992-01-01", "1", 0, NOW(), NOW());
