INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドルチェ・優しく", "ドルチェ・優しく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "A・ソクーロフ監督が「オリエンタル・エレジー」「穏やかな生活」に続いて日本ロケを実施したドキュメンタリー。『死の棘』などで知られる島尾敏雄の妻である作家、島尾ミホとその娘マヤを被写体に、加計呂麻島で暮らす彼女の日常を映し出す。ソクーロフ自身によるナレーションと写真を通して、島尾夫婦の歩みを回想。また正方形という珍しいスクリーン・サイズの映像の中に、島尾ミホが古謡を口ずさむ姿や幻想的ムードに満ちた風景が揺らめいている。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROFESSIONE ： REPORTER", "さすらいの二人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "北アフリカ、ミュンヘン、バルセロナでロケしたミステリー・タッチの作品。TVレポーターのロックは、北アフリカのホテルで死んだ男になりすましてスペインを旅する。途中、女子学生と一緒になり、とあるホテルに投宿するが、女子学生のいない間にロックは死んでいた……。ラストのカメラワークが話題になった。", "1975-01-01", "4,2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釈迦", "釈迦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 156, "スーパー・テクニラマ方式による日本初の70ミリ作品。プリントはロンドンで作られたテクニカル・カラープリントである。仏陀の生涯を伝記的に脚本化、様々なエピソードを散りばめ、活劇風の宗教映画に仕上げている。当時（ラーメン１杯30円の頃）５億円もの製作を掛けた超大作で、特撮を駆使した大スペクタクル場面が売り物だった。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トウキョウ・守護天使", "トウキョウ・守護天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "グラビアアイドルの神楽坂恵が自ら企画に参加して主演も務めたハードボイルド・ファンタジー。都会に夢やぶれ、ビルの屋上から飛び降り自殺を図ろうとしていた希。突然、背後から声をかけられた彼女は、声の主である男と目が合った瞬間、時間が止まったような奇妙な感覚に陥る。その男・須藤は別名を“スナドケイ”と言い、時間の売買を生業としていた。現実に戻って須藤の後を追った希は、とある喫茶店へとたどりつくが……。", "2007-11-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霧笛", "霧笛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "明治維新当時の横浜外人居留地を舞台に、白人以外を虫けらとしか思っていない威張り屋の白人・クーパーと、彼に反感を持ちその財布を奪った男・千代吉との宿命の対決を描く。千代吉はクーパーから奴隷扱いされるが、クーパーが商用で旅に出ている間に、その妾と愛し合うようになる……。菅井一郎のクーパー役が圧巻。村田実の数少ない現存作品。", "1934-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラマンサーガ", "ウルトラマンサーガ", "", "", "http://image.eiga.k-img.com/images/movie/56924/315x210.jpg?1644", 90, "人気特撮「ウルトラマン」45周年記念作品として、シリーズ初の3Dで製作。謎の侵略者バット星人により制圧された地球を救うため、それぞれ異なる時空からやってきたウルトラマンゼロ、ウルトラマンダイナ、ウルトラマンコスモスの3人が集結する。防衛チーム「スーパーGUTS」に所属する新人パイロットのタイガ・ノゾムは、子どもを救うために瀕死の重傷を負ってしまうが、その勇気に感銘したウルトラマンゼロが一体化してノゾムを救う。ノゾムはウルトラマンに変身する能力を拒み苦悩するが、人々の思いに触れるうちに徐々に変化していく。歌手でタレントのDAIGOが主人公タイガ・ノゾム役で映画初主演。ウルトラマンダイナ＝アスカ・シン役はつるの剛士、ウルトラマンコスモス＝春野ムサシ役は杉浦太陽がそれぞれ演じるほか、秋元才加、宮澤佐江ら「AKB48」のメンバー7人が地球防衛隊「チームU」として出演する。", "2012-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CANDY MOUNTAIN", "キャンディ・マウンテン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "世界的な写真家R・フランクが監督した、ロード・ムービー形式で綴る若者の成長物語。ミュージシャンを夢みるジュリアスは、ある日突然N.Y.から姿を消した伝説のギター職人エルモアのことを耳にする。ジュリアスはエルモアを捜す旅に出るが……。ジョー・ストラマー、トム・ウェイツほかのミュージシャンが大挙出演しているのも見逃せない。", "1987-01-01", "26,2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WEIRD SCIENCE", "ときめきサイエンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "内気な高校生がパソコンに理想の女性像をインプットしていたところ、ひょんなことから本当にその女性が現れてしまうというロマンティック・コメディ。「ハード・トゥ・キル」のK・ルブロックが、“完璧な女性”に扮している。ビデオ発売時タイトルは「エレクトリック・ビーナス　ときめきサイエンス」。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キャッチ　ア　ウェーブ", "キャッチ　ア　ウェーブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "現役高校生作家・豊田和真の同名小説を、日本プロサーフィン連盟の全面協力のもと映画化した本格サーフィン映画。夏休みを湘南の別荘で過ごすことになった高校1年生の大洋、小林、田口の3人は、浜辺で別荘の鍵をなくしてしまい、徹夜でカギを探すハメに。その途中、デューク川原という怪しげな男と出会った3人は、川原が経営するサーフショップに住み込みでアルバイトすることになり……。監督は、テレビ版「ウォーターボーイズ」の高橋伸之。", "2006-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Venus", "ヴィーナス", "", "", "http://image.eiga.k-img.com/images/movie/34274/315x210.jpg?1645", 95, "名優ピーター・オトゥールが8度目となるアカデミー主演男優賞ノミネートを受けた人間ドラマ。70歳を越え人気も衰えた俳優モーリスは、今では俳優仲間のイアンと無駄に毎日を過ごしていた。ある日イアンのアパートを訪れた彼は、そこに滞在しているイアンの姪の娘ジェシーに出会う。無愛想だが若く奔放なジェシーに、いつしかモーリスは惹かれていき……。監督は「ノッティングヒルの恋人」のロジャー・ミッシェル。", "2007-10-27", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDER THE CHERRY MOON", "プリンス　アンダー・ザ・チェリー・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「プリンス　パープル・レイン」で映画界に進出したプリンスが、主演、音楽のみならず自ら監督を手掛けた作品。リビエラを舞台に、セクシーなピアニストと大金持ちの娘との恋物語を、1930年代風の風俗と、魅力的なサウンドで彩っていく。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南北少林／MARTIAL ARTS OF SHAOLIN", "阿羅漢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "武勇を誇った少林拳総本山は、清王朝によって北と南に分断させられた。そこで、北拳の勇者、智北と南拳の猛者、智南は、互いに協力し合い、全武道統一と王朝打倒の悲願達成に挑む。本格中国拳法の達人たちによるウルトラCアクションに目を見張る壮大な武闘ロマン。", "1986-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUCES Y SOMBRAS", "ベラスケスの女官たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ベラスケスの名画『女官たち』をテーマにした映画を撮ろうと構想する映画監督と、彼の空想の中の17世紀のスペインでベラスケスや宮廷の人々と出会う少年の物語を交錯させて描く。スペインのヌーヴェル・ヴァーグの作家とも呼ばれるH・カミーノが自ら心酔するベラスケスを題材にした意欲作。", "1988-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小三治", "小三治", "", "", "http://image.eiga.k-img.com/images/movie/54374/315x210.jpg?3689", 104, "全国各地での独演会や落語会、高座での姿や舞台裏を通し、多彩な趣味に没頭する落語家・10代目柳家小三治（郡山剛蔵）の人となりに迫ったドキュメンタリー。「遊びは真面目にやらないと遊びにならない」をモットーに、趣味にも落語にも熱中する小三治の姿をカメラが追う。監督は長編ドキュメンタリーデビューの康宇政。", "2009-02-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELSINKI NAPOLI ALL NIGHT LONG", "ヘルシンキ・ナポリ オールナイトロング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "様々な国籍の人々の交錯から生み出される風変わりなサスペンス・タッチの多国籍映画。ある13日の金曜日のベルリン。フィンランド出身のタクシー運転手アレックスはいつものようにシチリア出身の妻と一緒に夜勤に出た。彼は、裏稼業で生きる親友のロシア人イゴルから仕事を請け負う。その仕事とは、意味ありげなカバンを持ったチンピラ二人を運ぶこと。しかし、二人を乗せて車を走らせたアレックスのあとを、アメリカ人の老ギャングがピストルを射ちながら追いかけてきた……。W・ヴェンダース、J・ジャームッシュと当代人気の監督が特別出演。", "1987-01-01", "20,7,26,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("colors カラーズ", "colors カラーズ", "", "", "http://image.eiga.k-img.com/images/movie/33554/315x210.jpg?3437", 112, "ある日突然、色とりどりの箱の中に迷い込んだ7人の男女。不条理な空間の中で、彼らの脳裏にはそれぞれの過去が浮かぶ。やがてひとつの部屋に集められた彼らの前に、もうひとりの男が現われる……。メガホンをとるのは、中野裕之監督を中心とした映像ユニット「ピースブラザース」の一員で、工藤里紗主演「スリーピングフラワー」で劇場デビューを果たした柿本ケンサク監督。村上淳、光石研、松岡俊介ほか個性的なキャストの競演も見どころ。", "2006-07-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギャング忠臣蔵", "ギャング忠臣蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "片岡千恵蔵を主演に、忠臣蔵を現代風にアレンジ。悪辣な吉良産業に罵られた末、毒殺された浅野組社長。彼の幹部・大石良雄は復讐を誓い、47人の子分を集めて吉良産業に殴り込む。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("０課の女・赤い手錠", "０課の女・赤い手錠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "篠原とおるの人気劇画の映画化。女刑事・零は、大物政治家の一人娘を誘拐犯人たちの手から極秘裡に救出するという特殊任務を命じられる。巧みに一味のなかへ潜り込むが、凌辱された娘を見限った政治家は、犯人たちもろとも、娘も刑事も抹殺することを命じ、警察と犯人たちを交えた三つ巴の戦いが始まる……。想像を絶する暴力の世界で犯されても、殴り蹴られても、表情一つ変えず冷ややかに受けとめる杉本美樹に圧倒される。実の弟を殴り殺して血の海に沈めてしまう郷英治の悪役ぶりもすさまじく、ゴミで埋まった空き地を舞台に繰り広げられる銃撃戦は、今までの日本映画にはない迫力に満ちている。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Staten Island", "ニューヨーク、狼たちの野望", "", "", "http://image.eiga.k-img.com/images/movie/54964/315x210.jpg?2172", 95, "NY市南西部に位置するスタテンアイランドを舞台に、生まれてくる子供のためにギャングのボスの金庫を狙う清掃員、人々の記憶に名を残そうと奔走するギャングのボス、万馬券を当てた聾唖の精肉店員という3人の男の人生が交錯するシニカルなクライム・コメディ。監督は「交渉人」の脚本家で本作が長編監督デビューとなるジェームズ・デモナコ。イーサン・ホーク、ビンセント・ドノフリオ、シーモア・カッセルら実力派俳優が共演。2009年第22回東京国際映画祭コンペティション部門にて「NYスタテンアイランド物語」のタイトルで日本初上映。11年「ニューヨーク、狼たちの野望」としてDVD発売。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DUST DEVIL", "ダスト・デビル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「ハードウェア」で特異な才能を見せた、R・スタンリーの監督第２作。その斬新な映像感覚が冴える、一風変わったホラー作品だ。アフリカ、ベタニア地方で残忍な殺人事件が頻発する。犯人は砂漠の悪魔“ダスト・デビル”。クールな風貌を持つ人間に姿を変えた悪魔は荒野をさまよい、獲物を待っていた。やがて、家出した人妻ウェンディの車に同乗したダスト・デビルは、次なる犠牲者を彼女に定める。そうとは知らず、ウェンディはこの謎めいた男に惹かれていく……。悪魔に扮したR・バークの不気味な存在感が印象的。彼はこの作品の後、ピーター・ウェラーに代わり「ロボコップ３」の主役に抜擢された。", "1992-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE UNSEEN", "恐怖のいけにえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "とある家に泊まった３人の女性を、次々となぶり殺していくモンスター。ホラー映画のモンスターは数々あれど、これは性的倒錯の兄と妹の近親相姦によって生まれた生きもの。人間的自覚も知能もなく、地下牢で怪物のように育てられ、恐怖というよりおぞましさを感じさせる。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒の超特急", "黒の超特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "大阪からさらに岡山方面へ延長されようとしていた新幹線。だがその超特急路線の用地買収の裏には、甘い汁を独り占めようとする政界のボスの陰謀があったのだ。青年不動産業者は一人戦いを挑んでいくが……。“黒”シリーズの最高傑作。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPRINGFIELD RIFLE", "スプリングフィールド銃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "南北戦争末期を舞台に、南軍のスパイを見つけようとする北軍少佐の活躍を描いたスリリングな西部劇。一転、二転するストーリーがサスペンスを生むと同時に、ラストに用意された銃撃戦が爽快なカタルシスを放つ。軍隊を除籍されたかに見せかけて、まんまと敵軍に取り入る主人公を、G・クーパーが快演している。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAC AND ME", "マック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "火星人マックとその家族は、地球からの探査船に吸い込まれ米軍基地に到着するが、幼いマックは迷子になってしまう。ハイウェイをさまよっている時に車椅子に乗った少年エリックと出会い、二人の間には友情が芽ばえる。マック一家を狙う政府の科学者たちに対し、子供たちは火星人を守ろうとするが……。「E.T.」を思わせるファミリー・ムービー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El Bulli: Cooking in Progress", "エル・ブリの秘密　世界一予約のとれないレストラン", "", "", "http://image.eiga.k-img.com/images/movie/57244/315x210.jpg?1718", 113, "「世界で最も革新的なシェフ」と賞賛されたフェラン・アドリアが手がける芸術的料理と、アドリアが料理長を務めたレストラン「エル・ブリ」の裏側に密着したドキュメンタリー。スペイン・ロザスに店を構える「エル・ブリ」は世界中から年間200万件もの予約が殺到し、「世界一予約のとれないレストラン」として人気を誇っていた。しかし2011年1月に突然閉店を発表し、同年7月30日をもって営業を終了してしまう。カメラはアドリアが生み出す芸術的料理やエル・ブリの裏側に密着し、人々を魅了する料理が完成するまでのプロセスすべてを映し出していく。", "2011-12-10", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美女と液体人間", "美女と液体人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "核実験による放射能の影響で、ゼリー状になった“液体人間”。下水道を通って東京に現れた液体人間は、人々を次々に液体化していく。サスペンスあふれる導入部から、液体人間が火の海で絶命するラスト・シーンの悲愴感まで、息をもつかせぬ展開が光る、東宝特撮映画の名作。ドラマ部分と特撮部分の見事な融合が、成功した大きな要因である。単なる怪物ではなく、恋する男として液体人間を描いているあたりも涙を誘う。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A MATTER OF TIME", "ザ・スター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "孤独な老婦人コンデッサは、かつては数々の浮き名を流した大スターだった。田舎育ちのニーナはローマのホテルで彼女に出逢い、多くの助言や励ましの言葉を受ける。そしてニーナもまたスターの座へ……。二大女優が華麗に演じる、ハリウッド・ミュージカルの香りあふれる一編。", "1976-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURPHY’S LAW", "必殺マグナム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "前妻を殺され、無実の罪で手錠のまま逃亡するロス市警殺人課のマーフィ刑事。今、マグナム銃を片手に復讐の追跡が開始された。ブロンソンが、執念に満ちた主人公の孤独な闘いを迫力満点に演じた、ハードな刑事アクションもの。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIPER", "バニシング・チェイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "日本で子役デビューし、帝劇の『アニー』にも出演したL・パール主演のB級バイオレンス。陸軍中枢部が、裏で操るテロリスト集団“バイパー”。そのメンバーのジェームス、パウエル、プロドナックス。ある事件がきっかけでパウエルはジェームスを殺してしまう。ジェームスの妻ローラは、パウエルに復讐を開始するが……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私が棄てた女", "私が棄てた女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "遠藤周作の人気小説の映画化。監督は「非行少女」以来５年ぶりとなった浦山桐郎。自動車部品の会社に勤めるしがないサラリーマンは、出世のため愛してもいない専務の姪と結婚しようとしている。そんな時、かつて遊んで捨てた女が自分の子を中絶し、苦しい生活をしているのを知る。彼は責任を感じるが……。愛とは何か、人生とは何かを考えさせられる叙情的な作品。圧巻は、全編モノクロームの中で、女が悲しく新相馬節を歌うシーンのカラー映像。緑の草原を駆ける馬たちの姿がスローモーションでかぶさる。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTERMEZZO", "別離", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "スウェーデンの世界的名バイオリニストと若い美貌のピアニストのつかの間の恋と別れを描いた悲恋物語。音楽の世界で結ばれた二人は、演奏旅行にかこつけて南フランスでバカンスを楽しむ。しかし妻子のいる男は、次第に罪の意識にさいなまれ……。バーグマンの渡米後の第１作で、その美しさは格別。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rules of Engagement", "英雄の条件", "", "", "http://image.eiga.k-img.com/images/movie/1624/315x210.jpg?4535", 128, "28年前にベトナム戦争で戦友チルダース大佐に命を救われたホッジス少尉は、その後引退して弁護士となるが、今では離婚が原因で自滅的な生活を送っていた。一方、海兵隊屈指の勇士として知られたチルダースは、暴徒に囲まれたイエメンのアメリカ大使館から大使家族を救出する命を受け、作戦を決行。一家の命と引き換えに女子供を含む一般市民多数の死傷者を出し、事件は軍事裁判にかけられることとなる。責任を追求されたチルダースはホッジスに自分の弁護を依頼する。", "2000-08-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bill Cunningham, New York", "ビル・カニンガム，ニューヨーク（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "", "2012-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("科学戦隊ダイナマン　クモシンカ登場", "科学戦隊ダイナマン　クモシンカ登場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 26, "優れた科学力で地上支配を狙う地底の有尾人一族・ジャシンカ帝国と、5人の若き科学者によって結成されたダイナマンの闘いを描いた人気テレビシリーズの劇場版。子どもたちに親しまれていた木田博士が、恐るべき破壊力を持つ銃パワーガンを発明。このパワーガンをめぐり、ジャシンカ帝国が放った進化獣クモシンカとダイナマンが激闘を繰りひろげる。後に再編集され、テレビシリーズ第32話「消えたパワーガン」として放映された。", "1983-03-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NAKED SPUR", "裸の拍車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "賞金のかかったならず者を追うハワードは、途中で知り合った二人の男と協力して目当ての男を捕らえ、一緒にいた女もろとも護送する。逃げる機会をうかがう男、賞金を独り占めにしようとする者など様々な思惑が入り乱れた旅が始まる……。J・スチュアートとA・マン監督の名コンビで描く異色西部劇。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・急行列車", "喜劇・急行列車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "特急列車の専務車掌・青木吾一は鉄道一筋人間で、４人の子どもに“特急”“さくら”“つばめ”“ふじ”と名付けるほど。ある日長崎行き“さくら号”で吾一は初恋の人・毬子に出会う……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shampoo", "シャンプー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　ビバリーヒルズに暮らす上流階級層の女性客を相手に仕事をしている美容師ジョージはモテモテのプレイボーイ。早く独立して自分の店をかまえたいと願う彼は顧客のひとりであるフェリシアの夫レスターを訪ねるが、そこでかつての恋人ジャッキーと再会。そして、あるパーティで思いがけない事件が……。ハリウッドの上流社会を風刺し、そこに生きる人々の姿を赤裸々に描いたコメディ。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pained", "痛み", "", "", "http://image.eiga.k-img.com/images/movie/57324/315x210.jpg?1356", 104, "「友へ　チング」「タイフーン」のクァク・キョンテク監督が、痛みを感じない男とわずかな傷が致命傷になってしまう難病の女の間に生まれた愛情を描くラブストーリー。幼いころにあった交通事故で家族を失い、自身も後遺症で痛みをまったく感じなくなった男、ナムスンは、借金の取立て先でドンヒョンという女と出会う。やがて2人は同居を始め、ドンヒョンはナムスンの事故のいきさつを、ナムスンはドンヒョンがわずかな出血でも死に至る危険をはらんだ血友病患者であることを知り、距離を縮めていくが……。主演は「美しき野獣」のクォン・サンウと「彼とわたしの漂流日記」のチョン・リョウォン。", "2012-02-11", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("寮フェス！　最後の七不思議", "寮フェス！　最後の七不思議", "", "", "http://image.eiga.k-img.com/images/movie/58014/315x210.jpg?1679", 0, "関西ジャニーズJr.のアイドルグループ「7 WEST」が主演する青春映画。関西有数の進学校・三山学園の男子学生寮は、心霊スポット扱いされるほどのオンボロ寮だが、寮長で成績優秀な遠山、バイトが命の龍造寺、バンド活動に忙しいバンドマン3人組、学力競争から脱落したヤンチャコンビなど個性的な面々が生活していた。ある日、そんな寮生の輪には入らず、いつもひとりでいる伊達の妹と名乗る少女が現れる。さらには寮の取り壊しが決まってしまい、寮生たちは取り壊し反対の決起フェスティバルを開催するが……。", "2012-03-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　SERPENT’S　KISS", "悪魔のくちづけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ベテランの名カメラマン、P・ルスロの監督デビュー作。17世紀末、裕福な領主トーマスが荒れ地に庭園を建設するため、若き造園デザイナー、ミニアが招かれる。しかし不思議な予知能力を持つトーマスの娘テアを巻き込み、この造園計画の背後の陰謀が明らかになっていく。オランダ訛りの英語を訓練したE・マクレガーがミニア役で主演した愛憎ミステリー。", "1997-01-01", "3,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　GRINCH", "グリンチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "全米の子供たちに愛されている童話『グリンチはどうやってクリスマスを盗んだか』を映像化したファンタジー・コメディ。舞台は善良な人々が暮らす街フーヴィル。町外れの山の洞窟に住むヘソ曲がりのグリンチはクリスマスが大嫌い。街の人々から嫌われているこの怪人に、クリスマスの狂騒に疑問を抱く純真な少女シンディが興味をおぼえた。彼女の呼びかけで街のパーティーにやってきたグリンチだったが、忌まわしい過去を思い出してしまい、人々からクリスマスの楽しみを奪おうと決意する……。全身を緑の毛に覆われたグリンチのヒネくれたキャラクターがコミカル。特殊メイクで、この強烈な怪人になりきったJ・キャリーの怪演も見もの。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きわめてよいふうけい", "きわめてよいふうけい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "1970年代初頭に既存の写真芸術を否定し、新たな表現を提唱した中平卓馬。1977年に昏睡状態に陥って記憶を失った彼は、再び写真を撮り始め、2003年に初めての本格的な写真展を開催した。本作は現代を代表する気鋭写真家のホンマタカシが、この伝説的な写真家の日常をとらえたドキュメンタリー。起床し、日記を書き、写真を撮って過ごす中平の今を淡々と映し出す。 日常を几帳面に日記につける一人の老人、中平卓馬。かつては気鋭の写真家として激烈な写真理論を展開したが、脳溢血で倒れて記憶喪失になった。リハビリのために沖縄を訪れたことをキッカケに、中平は再びカメラを手にする。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'assedio", "シャンドライの恋", "", "", "http://image.eiga.k-img.com/images/movie/1864/315x210.jpg?1258", 94, "ローマにある古い屋敷に住む、英国人音楽家キンスキーと住み込みの使用人シャンドライ。健気な仕事ぶりからシャンドライを愛するようになったキンスキーは彼女に愛を告白するが、驚いたシャンドライは「じゃあ夫を刑務所から出して！」と口走ってしまう。彼女には故郷アフリカに政治犯として捕まった夫がいたのだ。翌日から再び二人の間には静寂が訪れるが、それ以後何故か日を追うごとに屋敷の中の調度品が少なくなっていく。", "2000-02-05", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("慟哭", "慟哭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "上原謙、佐野周二とともに当時、“松竹三羽烏”の一人として活躍していた俳優、佐分利信が自ら監督、主演を務めた異色ドラマ。病妻を抱えた生活に疲れている劇作家・杉守。その妻が急死したことから、彼は人生の再出発を決心する。そんな折、劇団の研究生として野心あふれる娘・文子が現れる。杉守は元恋人との禁欲的な関係のなか、次第にこの若々しい文子の奔放な振る舞いに魅かれていくのだが……。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VENGEANCE: THE STORY OF TONY CIMO", "許されざる復讐　ベンジェンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "人権擁護のために矛盾が生じる裁判システムや死刑廃止論、人権問題など現代アメリカが抱える様々な社会問題を、実話をもとに描いたドラマ。とある田舎町の雑貨店が強盗に襲われ、店を営むムーン夫妻が射殺される。犯人は即逮捕、だがその裁判は凶悪犯の人権を必要以上に保護する展開に。息子トニーをはじめ残された家族には怒りのヤリ場もない。こうして裁判が長期戦にもつれ込む中、三女が裁判所での乱闘に巻き込まれ流産。犯人は最終的に死刑宣告を受けるが、20年間その前例がないため事実上無期懲役となる。トニーはノイローゼに陥り家庭も崩壊、ついに彼は刑務所内での殺人請け負い人に犯人の殺害を依頼する。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pa negre", "ブラック・ブレッド", "", "", "http://image.eiga.k-img.com/images/movie/57034/315x210.jpg?1280", 113, "スペイン内戦後の小さな村を舞台に、ひとりの少年がある事件を契機に恐ろしい決断を下すことになる姿を描いたミステリードラマ。カタルーニャ地方の小さな村の森の中で、少年アンドレウは親子の死体を発見する。死の直前に「ピトルリウア」と謎の言葉をつぶやく女性の姿を目撃したアンドレウは、内戦によって心に深い傷を負った大人たちがひた隠す真実に否応なしに巻き込まれ、生きていくためにある決断を下す。スペインのアカデミー賞と呼ばれるゴヤ賞で、作品賞をはじめ9部門を受賞。2011年スペイン映画界の話題をさらった。", "2012-06-23", "9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paradice〈パラダイス〉", "Paradice〈パラダイス〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 43, "ロックバンド、THE MODSのリーダー、森山達也が初監督を務めた中編。バイトをクビになったパンク少年と、単調な毎日を過ごすレコード店員の、それぞれの日常の物語が並行して展開。楽園行きのバスが出ることを知った彼らは、それに乗ろうとするのだが……。ロック・キッズの心情をファンタジーとしてとらえ、落ち着くことなく転がり続ける生き方を浮き彫りにするドラマ。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blue Gold: World Water Wars", "ブルー・ゴールド　狙われた水の真実", "", "", "http://image.eiga.k-img.com/images/movie/54934/315x210.jpg?1873", 90, "人々の生活に欠かせない水資源について考察するドキュメンタリー。今後の世界的人口増加に伴い、確実に不足する水資源。地球規模で水資源をビジネスに利用しようと現れたグローバル企業や、ペットボトルをめぐる問題までを多角的に検証する。ナレーションを「時計じかけのオレンジ」のマルコム・マクダウェルが担当。", "2010-01-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dungeons & Dragons", "ダンジョン＆ドラゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ロールプレイング・ゲームの元祖といわれる『ダンジョンズ＆ドラゴンズ』を映画化したファンタジー。王国支配を企む邪悪な宰相と、民のために尽くす女王が対立。宰相の陰謀を阻止すべく、侍女とコソ泥コンビが、伝説のドラゴンを呼び出すことができる杖を求めて冒険を繰り広げる。200匹のドラゴンが宙を舞うSFXスペクタクルは圧巻。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BALLISTIC：ECKS　VS．　SEVER", "バリスティック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "タイの新星カオス監督が、実在する組織、米国政府組織DIA（国防情報局）を題材に銃撃戦や爆破シーンをふんだんに盛り込んで描くアクション大作。何者かに盗まれた暗殺兵器ソフトキルと、DIA長官の息子の誘拐事件の二つの謎が交差する。撮影中に生傷が絶えなかったという、主演のアントニオ・バンデラスとルーシー・リューの体当たりアクションは見ごたえ十分だ。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はだしの花嫁", "はだしの花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "鰐淵晴子・倍賞千恵子主演による明朗青春映画。瀬戸内海の美しい自然を背景に、“週刊レディ”の女性記者・矢野玲子と友人の中島洋子は、一人の青年をめぐって恋のさやあてを演じる。倍賞千恵子を売り出した“花嫁”ものの第３作。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAUVAGE INNOCENCE", "白と黒の恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "若き映画監督と新進女優の悲劇的な恋を描くラブ・ストーリー。新作映画の資金集めをする映画監督のフランソワは、街で出会った女優志望の学生リュシーと恋に落ちる。かつての恋人をヘロインで亡くしていたフランソワは、ヘロインの恐ろしさを描く新作の主役にリュシーを使うことにする。だが、資金繰りに困ったフランソワは、ヘロインの運び屋の仕事を引き受けてしまう。監督のP・ガレルと彼のミューズだったニコとの関係を彷彿とさせる、自伝的要素の濃い作品。ヌーヴェル・ヴァーグを代表するカメラマン、R・クタールの叙情的なモノクロ映像が美しく官能的だ。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bow", "弓", "", "", "http://image.eiga.k-img.com/images/movie/1694/315x210.jpg?1994", 90, "「サマリア」「うつせみ」などで知られる韓国の鬼才キム・ギドクによる恋愛ドラマ。海に浮かぶ一艘の船の上で2人きりで暮らす老人と少女。老人は少女が17歳になったら、少女と結婚することを夢見て毎日を生きている。そして、老人に拾われた少女にとっても、そうすることが彼への信頼と愛情の証だと思っていた。だが、そんなある日、少女が魚を釣りに来た青年と恋に落ちる……。少女役には「サマリア」のハン・ヨルム。", "2006-09-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仁義なき戦い・代理戦争", "仁義なき戦い・代理戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "今回の舞台背景となるのは昭和35年から38年の高度成長時代。前２作ではまだ組員だった広能も広能組の組長になるなど、組織的に固まってきた広島ヤクザの陰謀と裏切りが渦巻く抗争を描く。昭和35年、広島最大の暴力団・村岡組の跡目の座をめぐって、打本と山守が対立していた。打本は神戸の明石組に広能を介して盃を申し入れた。それを快く思わない村岡は跡目を山守に譲り、山守組は広島最大の暴力団となった。打本は孤立し、明石組に逃げ込み正式に明石組の傘下に入った。一方、山守も神戸の神和会と縁組みし、代理戦争の幕が切って落とされた。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HANNIBAL BROOKS", "脱走山脈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "第二次大戦末期、ミュンヘン近くの収容所の動物園で象の飼育係にされていた捕虜のブルックスは、象のルーシーに愛情を感じ始めていた。やがて連合軍の爆撃があり、ブルックスはルーシーを連れて戦火の下を珍妙な脱走劇を演じるはめに……。人間と象との心の触れ合いを描いて感動を呼ぶ異色の戦争映画。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乞食大将", "乞食大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "1945年に製作されながら、「虎の尾を踏む男達」同様GHQ政策により上映を禁じられた戦国時代劇。1952年にようやく公開された。君主・黒田長政の卑怯な仕打ちに憤り、自ら浪人となって動乱の世を駆け回った“乞食大将”後藤又兵衛の半生を描く。1964年に田中徳三監督でリメイクされている。", "1945-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRAGONFLY", "コーリング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ケヴィン・コスナー、スザンナ・トンプソンら大人のキャストでじっくりとみせる、スピリチュアルなラブ・ストーリー。死んでも強い思いで結ばれ続ける夫婦の絆がヒューマンな感動を呼ぶ。妻で同じ医師のエミリーを亡くしたジョー。かつて妻が勤務した小児病棟を訪れた彼は、そこで昏睡状態の中でエミリーに会ったという少年と出会う。それ以後、彼は度々不思議な現象を体験し、亡き妻が何かメッセージを送っていると感じる。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AIRPORT", "大空港", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "吹雪でマヒしたリンカーン空港を舞台に、悲喜こもごもの人生模様をサスペンス・タッチで描き出す。非常事態における人間の葛藤の描写に、シートン監督が力量を発揮。オールスター・キャストも手伝って、アメリカでは大ヒットし、のちに「エアポート」シリーズとして第４作まで製作された。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Finding Mr. Destiny", "あなたの初恋探します", "", "", "http://image.eiga.k-img.com/images/movie/56164/315x210.jpg?3242", 112, "2006年に初演され、25万人を動員した韓国の人気ミュージカルを映画化。ミュージカルの舞台監督をしているアラサー女子のソ・ジウは、仕事は順調だがプライベートでは恋のひとつもできない。かつて旅先のインドで出会った初恋の人をいまだに忘れられずいるジウは、業を煮やした父親に連れられて冴えない草食系男子ギジュンの「初恋さがし株式会社」を訪ね、初恋相手を探すことになるが……。主演は人気ドラマ「コーヒープリンス1号店」のコン・ユと、「ハピネス」のイム・スジュン。", "2011-06-18", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AD OGNI COSTO", "盗みのプロ部隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "E・G・ロビンソン、K・キンスキーら個性派俳優がズラリそろったサスペンス・アクション。まじめな大学教授がその明晰な頭脳を駆使して、1000万ドルのダイヤ強盗を計画。各国から４人の屈強なプロを集め、最新ハイテク大金庫に挑むさまが秒刻みで描かれていく。", "1969-01-01", "4,7,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プウテンノツキ", "プウテンノツキ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "「鬼畜大宴会」の熊切和嘉や「どんてん生活」の山下敦弘らを生んだ、大阪芸術大学映像学科出身の元木隆史が同大学院の修士作品として完成させた35ミリ長編。恋人の許に転がり込み、無目的にその日暮らしを続けるプータロー青年の悲哀を、やさしく見つめる。互いにもたれ合う生ぬるい同棲生活や、親友の成長に置いてきぼりをくらった挫折など、ささやかだがリアルなエピソードの数々を、端正な映像文体の中に結実させている。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルパン三世 風魔一族の陰謀", "ルパン三世 風魔一族の陰謀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "オリジナル・ビデオとして作られ、劇場公開されたシリーズ第４作。若手の作画スタッフを中心にスピーディーなアクションが描かれ、シリーズなかでも正統派スタイルの痛快作となった。黒繩家の先祖が隠した財宝を狙い、そのありかを示す壷を奪おうとする風魔一族。黒繩家の娘・紫と婚約した五右衛門、そして財宝のことを知ったルパンと次元、峰不二子も争奪戦に加わり、やがて錫杖岳の洞窟で、敵味方入り乱れての財宝探しが開始される。なおこの作品のみ、レギュラー・キャストとは違う声優が担当した。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天国までの百マイル", "天国までの百マイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ベストセラー作家、浅田次郎の同名小説を丹念に映画化したロード・ムービー。バブルが弾けて借金漬けになり、同棲相手の愛人マリに食わせてもらっている情けない営業マン、安男。心臓を病んだ母親に死期が迫っていることを知った彼は、周囲の人々の心温かい助力を得て、天才的な医師がいる鴨川の病院まで母親を連れて行こうと決意する。献身的な愛情で主人公を支えるマリに扮した大竹しのぶの天真爛漫な存在感が、現実感あふれるドラマにメルヘンな味わいをもたらしている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAXI", "タクシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ヨーロッパを代表する監督、C・サウラのサン・セバスチャン映画祭審査員特別賞受賞作。人種差別や偏見に取り憑かれた大人たちにほんろうされながらも、純粋な愛を貫こうとする若い男女の姿を描く。大学受験に失敗した18歳のパスは、長かった髪を短く剃り、絶望感から抜け出そうとしていた。そんな娘を理解できない父親は、彼女を無理やり自分と同じタクシードライバーにさせる。“ファミリー”と称する父の仕事仲間に嫌悪感を覚える彼女は、その一員で幼なじみの青年ダニと心を通わせていく。しかし、無線から聞こえてくるファミリーたちの暗号めいた会話や、街で起きたタクシードライバーによる犯罪が、パスを不安にさせる。やがて、ダニが人種差別主義者であることに気が付き深く傷つくパスは、さらにファミリーの恐ろしい秘密を知ってしまう……。愛と憎しみの対立を浮かび上がらせる、名カメラマン、V・ストラーロによる映像美が作品に深みを与える。ヒロインに扮するI・ルビオは、強さと純粋な心を合わせ持つ少女を好演。", "1996-01-01", "9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人肌観音 第１篇", "人肌観音 第１篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "松竹の看板ドル箱スターとして活躍し、毎年正月映画の主役の座を務めてきた林長二郎（長谷川一夫）が東宝に引き抜かれた直後に、久し振りに彼抜きで製作された正月作品。「狂った一頁」「十字路」で国際的大監督の名をほしいままにしていた衣笠貞之助が監督にあたった、オールスター映画。徳川時代を舞台に、徳川家代々に伝わる笛をめぐって悪徳武士や泥棒、切支丹の絵師らが争奪戦を繰り広げる。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サラリーマン忠臣蔵", "サラリーマン忠臣蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ネーミングは違うが、これも“社長”シリーズの変則版と考えられる。丸菱コンツェルンがアメリカ経済使節団を招待し、その接待をめぐってコンツェルン内部で赤穂産業社長・浅野貞巳と丸菱銀行頭取・吉良剛之介が対立。浅野が謹慎処分となり、自動車事故で急死。怒った彼の部下たちは吉良に造反して新会社を設立する。元祿忠臣蔵のシチュエーションをそのままサラリーマン社会にあてはめたコメディ。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画版　ミューズの鏡（仮題）", "映画版　ミューズの鏡（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-08-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARNAL KNOWLEDGE", "愛の狩人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ニューイングランドの名門大学に学んだ二人の青年の20年間にわたる愛と性の遍歴を描いた作品。４人のスター競演とテーマが話題を提供した。また時代を偲ばせるヒット曲『ムーンライト・セレナーデ』などの使い方もおしゃれ。不能者を演じたニコルソンの姿がおかしくも哀れ。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画作りとむらへの道", "映画作りとむらへの道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "今はなき映画製作プロダクションの小川プロを当時同プロの助監督だったドキュメンタリー作家の福田克彦が追った作品。1973年、「三里塚・辺田部落」を完成間近に控えた小川プロは三里塚を後にして、山形へと向かった。小川プロは当時、流行だったデモと言う暴力的なスペクタクルから運命にほんろうされる村の日常へとテーマを変えつつあった。“運動の映画”から視点を変えるという一大転機のプロセスを追う。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CADILLAC MAN", "キャデラック・マン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "客のハートにがっちり食い込む必殺セールスマンシップで圧倒的な業績を上げるニューヨークのカー・セールスマン。そのスゴ腕を女にも活用し、モテモテの日々を送っていた彼のもとに、女房を寝とられた男がマシンガン片手に乱入してきた。お得意のセールスマン魂でこの修羅場をくぐり抜けようとする彼だが……。ギネス級のスゴ腕セールスマンにして驚異のプレイボーイの、熱くて長い一日を笑いとペーソスたっぷりに描く。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VYNA´LEZ ZKA´ZY", "悪魔の発明", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ヴェルヌの同名小説を映画化。実写の人物とアニメーションの組み合わせで、動く銅版画のようなイメージで描き出している。強力な爆発物を研究中のロック教授が、海賊に誘拐されてしまった。海賊の首領は、巧みに教授を欺き、あたかも協力を申し出ているかのように思わせる。さっそく、海賊の本拠地である火山島で研究を進めるロック教授。が、研究を悪用しようとする海賊の野望に気付いた助手ハールは、警告の手紙を気球で放つ。それが全世界に伝わると、ただちに連合艦隊が組織され、戦いが始まった……。古めかしい潜水艦や水中自転車、火山島の巨大な設備など、奇想天外な世界が繰り広げられる。", "1957-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("集団殺人クラブ／最後の殺戮", "集団殺人クラブ／最後の殺戮", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "遠藤憲一扮する殺人鬼タケゾウの恐怖を描いたホラー・シリーズの最終章。前作で本格的な殺りくモンスターへと成長を遂げたタケゾウの前に、不思議な能力を持つ女子高生が登場し、二人の壮絶なバトルが繰り広げられる。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHE DANCES ALONE", "バラの刻印", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "20世紀最高の天才バレエ・ダンサー、バスラフ・ニジンスキー。彼の情熱と狂気に彩られたミステリアスな生涯を、その娘キラの目を通して描く。知られざるニジンスキーの実態に迫った、ドキュメント風フィクション。父親に対し、深い愛と憎しみを抱くキラの言動が大迫力。ニジンスキーのダンスを再現するP・デュポンの艶やかな踊る姿が見事。", "1981-01-01", "1,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAINT−TROPEZ BLUES", "赤と青のブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "南フランス、サント・ロペを舞台に、育ちのいい娘と彼女のボーイフレンドたちの恋や遊びを即興的なタッチで描く。C・シャブロルの脚本をM・ムーシーが演出。主演のM・ラフォレはシャンソン歌手として人気を得た後、「太陽がいっぱい」で映画デビューした。", "1960-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕等がいた　後篇", "僕等がいた　後篇", "", "", "http://image.eiga.k-img.com/images/movie/56234/315x210.jpg?3316", 122, "累計発行部数1000万部を突破する小畑友紀の人気少女漫画を、前後編の2部作で実写映画化。東京へ引越した矢野との再会を夢見て大学進学を果たした七美だったが、待っていたのは最愛の人の消息不明という事実だった。苦渋の決断の末に姿を消した矢野と七美は数年後、思いがけない再会を果たすが……。生田斗真と吉高由里子がダブル主演する純愛ストーリーの完結編。監督は「ソラニン」の三木孝浩。", "2012-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("病院で死ぬということ", "病院で死ぬということ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ガンの告知を受けた死期の迫る入院患者とその家族の触れ合い、そしてターミナルケア（末期医療）に取り組む医師や看護婦の姿を、ドキュメンタリー・タッチで綴った異色ドラマ。据え置きのカメラでじっくりと写し出された穏やかな映像で、死生観を安易なセンチメンタリズムに溺れることなく捉えていく。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETURN TO THE BLUE LAGOON", "ブルーラグーン", "", "", "http://ecx.images-amazon.com/images/I/51tA1eCHExL._SL210_.jpg", 102, "1980年にブルック・シールズ主演で話題を呼んだ「青い珊瑚礁」の続編とも言える作品。前作のラストで、主役二人の一粒種として唯一生き残った赤ん坊。彼は、一組の母娘に拾われて無人島へ流れ着く。やがて母は死に、彼と娘は島で愛を育み始めるが……。M・ジョヴォヴィッチの肢体が鮮烈。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・鞍馬天狗・五条坂の決闘", "新・鞍馬天狗・五条坂の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "市川雷蔵主演による「新・鞍馬天狗」の第２作。所司代と組んで鞍馬天狗の命を狙う、ドクロの面の“山嶽党”との対決を描く。人質にされた杉作少年を助けるため、森や岩窟で敵を斬りまくる雷蔵天狗の華麗な剣さばきに注目したい一編。またほとんどの人物が正体を隠している点も珍しい。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Apache", "アパッチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "　1886年、アパッチの族長ジェロニモはアメリカ合衆国に降伏したが、ただひとり青年マサイだけはそれを拒み、脱走を図る。やがて彼はセントルイスに逃亡。その地でチェロキーの農夫に出会い、トウモロコシの種をもらう。その後、故郷に戻ったマサイは抵抗を続けながらも、生まれてくる子供のために農業を始めることに……。名匠アルドリッチ監督がランカスター演ずる先住民を主人公にすえて描く異色ウェスタン。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("英雄伝説　空の軌跡 THE ANIMATION Vol.2", "英雄伝説　空の軌跡 THE ANIMATION Vol.2", "", "", "http://image.eiga.k-img.com/images/movie/57614/315x210.jpg?1712", 40, "日本ファルコムの人気RPGシリーズ「英雄伝説　空の軌跡」をアニメーション化した全2巻のOVAの第2巻。人々の安全と地域の平和を守るギルドの「遊撃士（ブレイサー）」として戦う少女エステルは、自分の前から姿を消した、同じ遊撃士で義理の弟ヨシュアを探しながら旅を続けていた。しかし、世界の裏で暗躍する謎の組織「身喰らう蛇」のレンに敗れ、誘拐されたエステルは、「身喰らう蛇」の剣帝レーヴェからヨシュアにまつわる驚きの事実を知らされる。", "2012-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勇者無櫂／TWO　FINGER　FIST", "ツーフィンガー鷹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "先輩が殺されたのをきっかけに、洗濯屋の息子が猛然と立ち上がる、Y・ピョウ主演のカンフー・アクション・コメディ。彼が生み出したユニークでパワフルなツーフィンガー・カンフーの魅力を余すところなく収録した香港完全オリジナル版。Y・ピョウの切れ味のいいアクションが楽しめる。", "1979-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("麒麟の翼　劇場版・新参者", "麒麟の翼　劇場版・新参者", "", "", "http://image.eiga.k-img.com/images/movie/56584/315x210.jpg?3367", 129, "東野圭吾の人気ミステリー「加賀恭一郎シリーズ」第9作の映画化。同シリーズが原作で2010年に放送された連続ドラマ「新参者」、11年の単発ドラマ「赤い指」に続き、阿部寛が主人公の刑事・加賀恭一郎を演じる。東京・日本橋の翼のある麒麟像にもたれかかるようにして死んでいた男の捜査に当たる加賀だったが、容疑者の八島が逃亡中に車にはねられ意識不明に。八島の恋人・香織は涙ながらに無実を訴えるが……。ドラマ版から続投の溝端淳平、黒木メイサらに加え、事件の真相に深くかかわる香織役で新垣結衣が共演。監督は「涙そうそう」「ハナミズキ」の土井裕泰。", "2012-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROME ADVENTURE", "恋愛専科", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ワイセツな小説を貸し出したとして大学首脳に呼び出されたプルーデンスは、逆に辞表をたたきつけ、船でイタリアへと旅立つ。そしてローマでドンという青年と親しくなり、一緒に観光地を見物して回るのだが……。D・デイヴィスが、イタリア・ロケを展開して撮った青春ドラマ。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HITLER EINE KARRIERE", "ヒトラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 155, "膨大な記録フィルムの中から、独裁者ヒトラーの実像を解き明かしていく、ドイツ人自身の手で作られたドキュメンタリー。ヒトラーが自分をよく見せるために作り上げた身振り、しゃべり方、撮影される角度に至る演説のテクニックが微細に分析されていくさまは圧巻。", "1977-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ファンキーハットの快男児　二千万円の腕", "ファンキーハットの快男児　二千万円の腕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "「ファンキーハットの快男児」と同時期に撮影された活劇シリーズ第２作。整形外科のインターンが酒に酔って溺死するという怪事件が発生。一方、プロ野球のスカウトが注目する大物選手、川原がナゾの失踪を遂げた。好奇心旺盛な天下一郎は調査に乗り出し、川原の故郷へ。そこでうさんくさい野球部長と面会し、溺死事件との意外な関係を突き止める。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THEY ALL LAUGHED", "ニューヨークの恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "私立探偵ジョンのもとに、二人の男の妻たちの素行調査依頼がきた。彼は相棒のアーサーやチャールスと調査を開始したが、その女性たちに恋をしてしまう……。ニューヨークを舞台にシャレた感覚で描いた都会派ロマンティック・コメディ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Porto da Minha Infancia", "わが幼少時代のポルト", "", "", "http://image.eiga.k-img.com/images/movie/53144/315x210.jpg?1968", 61, "「夜顔」のマノエル・ド・オリベイラ監督が、故郷ポルトでの幼少時代を綴った自伝的作品。2001年、ヨーロッパ文化首都に選出されたポルトガルの美しい港町ポルト。現在の風景を映し出すとともに、オリベイラ少年が過ごした古き良き時代の街並が再現される。オリベイラ監督が自らナレーションを務めるほか、劇中の俳優役としても出演。少年時代のオリベイラを演じるのは、彼の実孫であるリカルド・トレパ。", "2007-12-15", "2,25", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dark Star", "ダーク・スター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "　植民地を求め、遥か彼方の宇宙を行く探査船ダーク・スター。その船に積み込まれていた惑星爆破のための爆弾が流星群に突入した際に故障。クルーたちは船内での爆発を食い止めるため、思考メカニズムの付いた高性能爆弾を説得しようとするが……。ジョン・カーペンター監督が南カリフォルニア大学の映画学科在学時に作った短編をリメイク。パロディ満載のカルトSF。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飛び出せ！青春", "飛び出せ！青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "ケンカは強いがサッカーはまるで弱い、悪童ぞろいの太陽学園サッカー部の部長に就任した河野先生が、持ち前の正義感と意地を発揮して何とかライバル校との試合に勝てるよう努力していく様を描いた学園もの青春映画の佳作。先行したTVシリーズの方は高視聴率をキープした結果、青春もののバイブル的存在となった。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HOLY MOUNTAIN", "ホーリー・マウンテン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "砂漠で十字架に磔にされている盗賊。キリストに似た彼は、十字架を脱し、町へ向かう。兵士たちによる住民の虐殺、メキシコ征服劇を演じるヒキガエルのサーカスなど、喧騒に包まれた町。そこで盗賊は捕らえられるが、なんとか脱出した彼は、高い塔の頂上に逃げる。塔の内部には広々とした部屋があり、錬金術師が彼を待ち受けている。錬金術師は彼を論し、大工場主、兵器製造業者、警視総監といった人々と一緒に不死を求めて“聖なる山”に向かうことを勧め、男女９人が“聖なる山”に向けて旅立っていく。鬼才ホドロフスキーが「エル・トポ」に続いて製作した神秘主義に彩られた異色作。", "1973-01-01", "1,19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無国籍の男 血の収穫", "無国籍の男 血の収穫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「新・悲しきヒットマン」で高い評価を得た望月六郎、石橋凌のコンビが再び手を組んだハードボイルド・アクション。成田空港に降り立った黒木は６年前までは元エリート商社マンだったが、その後ニューヨークのイタリアン・マフィアに所属し、そして今、組織を裏切って日本に戻って来た。当然、組織は彼に刺客を差し向け、一方、組織壊滅を目論むFBIも彼を追っていた。そんな中、彼は組織から盗み出したデザイン・パターンを台湾マフィアに売り渡そうと女ボスに接触する……。黒木、イタリアン・マフィア、台湾マフィア、FBIの要請を受けた警視庁捜査官、日本ヤクザ、そして、謎の女が入り乱れながら、終局の大銃撃戦へと物語は進展していく。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死者の書", "死者の書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "當麻寺に伝わる蓮糸曼荼羅の伝説をモチーフにした古代文学者・折口信夫の同名小説を、人形アニメーションの第一人者・川本喜八郎が映像化。８世紀半ば、奈良の都。信仰心の深い藤原南家の郎女は、二上山の上に見た俤人を一途に思い、嵐の中、山の麓にある寺を目指す……。藤原南家の郎女の一途な信仰によって、処刑された大津皇子の魂が鎮まるという物語が、幻想的な世界で展開する。宮沢りえら豪華な声優陣にも注目。", "2006-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("回路", "回路", "", "", "http://image.eiga.k-img.com/images/movie/1464/315x210.jpg?1697", 118, "一人暮しの平凡なOLだったミチ。しかし最近、彼女の周囲では同僚の自殺、勤め先の社長の失踪など無気味な事件が相次いでいた。友達が、恋人が、そして家族までが次々と消えていく。時を同じくして、大学生亮介の自宅のパソコンには、インターネットにアクセスしてもいないのに「幽霊に会いたいですか」という奇妙なメッセージが浮かび上がり、黒い袋に覆われた異常な人の姿が現れた。次第に廃虚となる町で、ミチと亮介は出会い、迫り来る恐怖に挑むのだが……。", "2001-02-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHEN TIME RAN OUT…", "世界崩壊の序曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "国際的ホテル王ギルモアが南太平洋ポリネシアの島に建設中の新ホテル。が、火山の大噴火によってホテルは一転、火の海に……。「ポセイドン・アドベンチャー」「タワーリング・インフェルノ」で当てた製作者がまたまたオールスター・キャストで描くパニック編。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　ゲゲゲの鬼太郎　日本爆裂!!", "劇場版　ゲゲゲの鬼太郎　日本爆裂!!", "", "", "http://image.eiga.k-img.com/images/movie/53564/315x210.jpg?2782", 0, "1968年の初TVアニメ化から40周年を記念し、2007年から放送された5度目のTVアニメ版「ゲゲゲの鬼太郎」を映画化。風祭華という少女の身の回りで起きる奇怪な現象を調べるため、鬼太郎たちは華のもとに向かうが、華は妖怪・鏡爺に鏡の中に引きずり込まれてしまう。鬼太郎はなんとか鏡爺を倒し華を救出するが……。鬼太郎の声をTVアニメ「名探偵コナン」の高山みなみが担当。", "2008-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("893愚連隊", "893愚連隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ゴダールの「勝手にしやがれ」を意識した新鮮で若々しい映画。京都駅付近で白タクで働く愚連隊がヤクザの１千万円をかっぱらって逃走する。登場人物たちのファッションもサングラスにソフト帽と、まるでジャン・ポール・ベルモンドのよう。最後はハデな展開に……。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・男はつらいよ", "続・男はつらいよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "今回は寅さんが生みの母を訪ねる一編。旅先で優しい母親の夢を見た寅次郎。大阪に探しに行くが、やっと会えた母は女郎屋を経営する元気なオバハンだった。寅さんはかなり怒りっぽいキャラクターのまま。ミヤコ蝶々が母親を演じている。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("What We Do Is Secret", "ジャームス　狂気の秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "1977年～80年にロサンゼルスで活動した伝説的パンクバンド「ジャームス」を題材にした伝記映画。1980年12月に薬物の過剰摂取で急死したボーカリスト、ダービー・クラッシュの破天荒な生きざまを中心に、わずか3年の活動期間ながら、後のアメリカのパンクシーンに絶大な影響を及ぼしたジャームスの栄光と破滅の軌跡を描く。「ウォーク・トゥ・リメンバー」のシェーン・ウェストがダービー役を熱演、オリジナル・メンバーと共にレコーディングにも挑んだ。", "2009-12-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Monsters", "モンスターズ　地球外生命体", "", "", "http://image.eiga.k-img.com/images/movie/56424/315x210.jpg?1347", 94, "地球外生命体のサンプルを採取したNASAの探査機が、大気圏突入時にメキシコ上空で大破。その直後からナゾの生物が増殖し、メキシコの半分が危険地帯として隔離される。6年後、メキシコでスクープを狙うカメラマンのコールダーは、上司から現地でケガをした社長令嬢をアメリカ国境まで送り届けろとの命を受けるが……。主にTV番組のVFXスタッフとして活動していた新鋭ギャレス・エドワーズ監督が、わずか1万5000ドルの低予算で完成させ話題となったSFモンスター映画。", "2011-07-23", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAD BOYS", "バッド・ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "シカゴに住む不良少年ミックは、ふとしたことで少年刑務所に送り込まれてしまう。彼は、殺人、強盗、強姦などの凶悪犯専用のオリの中で、先住者からの容赦ない暴力の洗礼を受けながら、自らの力ではい上がっていく……。硬派の青春映画として見ごたえ十分。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DA", "マーティン・シーンの da ゴーストになったパパ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "美しいアイルランドの風景の中で、父と子の幻想と現実が交錯した世界を描いた人間ドラマ。ニューヨークで暮らす劇作家が、頑固者で生粋のアイリッシュだった父の死を知り、故郷アイルランドへ帰る。葬儀を終えた彼のもとへ、天国にいるはずの父が戻ってくる。オリジナルはブロードウェイの舞台劇。B・ヒューズは舞台でも主役を演じ、トニー賞を受賞している。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Route Irish", "ルート・アイリッシュ", "", "", "http://image.eiga.k-img.com/images/movie/57644/315x210.jpg?2227", 109, "「麦の穂をゆらす風」の名匠ケン・ローチが、実在する危険地帯を題材にイラク戦争の実態を描いた社会派ドラマ。米軍によるイラク侵攻後、バグダッド空港と市内の米軍管理地域グリーンゾーンを結ぶ12キロにおよぶ道路「ルート・アイリッシュ」は、米軍の要人を狙ったテロの第一目標として世界一危険な道路となる。イギリス人の民間警備兵ファーガスは多額の報酬にひかれてイラクにやってくるが、ルート・アイリッシュで親友のフランキーが死亡。その死因について当局の発表に納得がいかずに真相究明に乗り出すが……。", "2012-03-31", "3,2,37,4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Despicable Me", "怪盗グルーの月泥棒 3D", "", "", "http://image.eiga.k-img.com/images/movie/55464/315x210.jpg?2816", 88, "「アイス・エイジ」シリーズのプロデューサーであるクリス・メレダンドリが、米ユニバーサル・スタジオとタッグを組んだ3Dアクション。郊外のさびれた家に暮らす意地悪な怪盗グルーは、あらゆる兵器を使って自分の邪魔になる者を排除してきた。そんなグルーが、仲間のミニオンたちとともに月を盗もうと企む。ところが孤児院育ちで元気いっぱいの幼い三姉妹に出会い、“未来のパパ”と勘違いされてしまったことでグルーの人生は大きく変化していく。声優陣にスティーブ・カレル、クリステン・ウィグ、ジュリー・アンドリュースら。", "2010-10-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("独立愚連隊西へ", "独立愚連隊西へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "岡本喜八の名を一気に高めた出世作“独立愚連隊”ものの第２作。西部劇ファンの岡本監督は、それまでの戦争映画とまったく違い、中国大陸での戦争を西部劇タッチのアクション・コメディとして描いているが、その底流には「肉弾」などに通じる戦争悪、戦争の愚劣さへの痛罵が流れている。北支戦線、消えた軍旗の捜索のため、危険な戦線ばかり転戦している独立左文字小隊が派遣された。日本軍から厄介者扱いされている彼らの活躍で、無事軍旗は戻るが、その時敵の八路軍に周囲を囲まれていた……。ラストの日本軍と中国軍が戦争したフリをして無傷で別れるという男の友情には、第１作をしのぐ感動がある。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WOLFEN", "ウルフェン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "謎の大富豪惨殺事件を捜査するN.Y.市警刑事と女性心理学者は、唯一の手掛かり、狼の毛から事件の意外な背景を知る。かつて虐殺された狼の生き残り、人間をはるかに凌ぐ頭脳、視力、聴覚、嗅覚をもつ“狼を超えた狼”ウルフェン。狼の視覚を特撮で異様に映像化。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Race to Witch Mountain", "ウィッチマウンテン　地図から消された山", "", "", "http://image.eiga.k-img.com/images/movie/54444/315x210.jpg?2818", 98, "禁断の山“ウィッチマウンテン”に隠された宇宙の謎をめぐるSFミステリー。SFファンタジー「星の国から来た仲間」（75）のリメイクで、「ゲットスマート」のドウェイン・ジョンソン、「テラビシアにかける橋」のアナソフィア・ロブらが出演。ラスベガスのタクシー運転手・ジャックは、ひょんなことから誘拐されそうになっていた兄弟セスとサラを救出する。彼らは不思議な能力を持っており、国家最高機密のため地図から抹消された“ウィッチマウンテン”を目指していた。", "2009-07-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sky Captain and the World of Tomorrow", "スカイキャプテン　ワールド・オブ・トゥモロー", "", "", "http://image.eiga.k-img.com/images/movie/1174/315x210.jpg?1714", 107, "1939年のニューヨーク。高名な科学者たちが次々に誘拐され、街には謎の巨大ロボット集団が出現。米空軍傭兵の名パイロット、スカイ・キャプテンと、女性新聞記者ポリーはこの事件を調査していき、天才科学者トーテンコフ博士の陰謀に気づく。本作で監督デビューのケリー・コンランが自宅のマックで4年間の歳月をかけて製作した6分間の映像が、製作者ジョン・アブネットの目に留まり映画になった。俳優以外はすべてCGIで作成。", "2004-11-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tortured", "ディープ・アンダーカバー", "", "", "http://image.eiga.k-img.com/images/movie/54584/315x210.jpg?4029", 107, "コール・ハウザー、ローレンス・フィッシュバーン、ジェームズ・クロムウェルらが出演するクライム・アクション。凶悪犯罪組織のボス“ジギー”の正体を暴くため、組織へ潜入したFBI捜査官のジミーだったが、ある時、組織内で大金が消え去る事件が発生。ジミーは組織の会計士を拷問にかけて自白させようとするが、モラルと自制心を失ったジミーの行動はエスカレートしていき……。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("親バカ子バカ", "親バカ子バカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "医療器会社の社長と少々頭の弱い一人息子が繰り広げる恋愛人情喜劇。この息子・寛一を藤山寛美が演じる。同名テレビドラマの映画化作品で「番頭はんと丁稚どん」の酒井欣也が演出に当たっている。前半は寛一の恋物語、後半は寛一が父の犯した昔日の罪をつぐなうという話。寛美の独壇場。館直志は渋谷天外のペンネームだ。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Boondock Saints II: All Saints Day", "処刑人II", "", "", "http://image.eiga.k-img.com/images/movie/55284/315x210.jpg?3287", 117, "神の啓示を受けた二卵性双生児のマクマナス兄弟が、法では裁けない街の悪人を処刑していくバイオレンス・アクション「処刑人」の続編。主演のショーン・パトリック・フラナリーとノーマン・リーダス、トロイ・ダフィー監督が続投する。コナー＆マーフィー兄弟と、父ノアの3 人が開廷中の裁判所でイタリアン・マフィアのボス、ヤカベッタを処刑してから8 年。3人は密かにボストンからアイルランドに亡命し、牧場でひっそりと暮らしていたが、まもなくして彼らの処刑を模倣した殺人事件が起こる。兄弟は罠（わな）を承知で、ボストンへと向かう。", "2010-05-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOUBLE REVENGE", "ダブル・リベンジ／地獄の標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ホラー演出に定評のあるアーマンド・マストロヤンニ監督が珍しくアクションに挑戦。妻を殺された警官と、その彼に弟を殺された銀行強盗犯人。お互い仇として憎み合う両者が宿命の対決に至るまでを、あたかも現代の西部劇ともいえるタッチで熱く描いていく。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("食神／THE GODS OF COOKERLY", "食神", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1996年の香港映画界で興行成績第２位を記録し、本国でのC・シンチー人気を改めて印象づけたグルメ・コメディ。料理界の“食神”として君臨する若き料理人が、思わぬ罠によって路頭に放り出され、劇的な再起を果たすまでの物語。アクション映画さながらの調理シーンを始め、監督・脚本を兼ねたシンチーがフル回転の熱演を見せる。また「天使の涙」のカレン・モクが、主人公を救う屋台のオバハンに扮し、まぶたを腫れ上がらせた“ブス・メイク”で観る者のド肝を抜く。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ARGENT DE POCHE", "トリュフォーの思春期", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "フランスの地方都市を舞台に、リセの子供たちの日常を、きびきびと点描していくシネ・スケッチ。子供たちの胸のときめき、反抗、いたずらっ気、秘められた思いなどをカメラが温かく見守る。その即興演出が生むリズムは散文詩の境地と言えよう。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤胴鈴之助 どくろ団退治", "赤胴鈴之助 どくろ団退治", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "再び祖父の仇を追って諸国を旅する鈴之助。その頃江戸では、火の山から生まれた不死身の鉄人が、どくろ団にだまされて市民を恐怖におとしいれていた。それを知った鈴之助は、一味の退治へと向かう。鉄砲も真空斬りもはねかえす鉄人に、彼はどう立ち向かうのか？　前作に続き、モノクロ中編仕立てで送るシリーズ第９作にして最終作。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEANNE LA PUCELLE II", "ジャンヌ／薔薇の十字架", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "リベット監督がジャンヌ・ダルクを描いた歴史大作の後編。（前編は「愛と自由の天使」）オルレアンの勝利を導いたジャンヌ・ダルクが捕えられ、獄中で3人のジャンヌに助けられながらもやがてすすんで火刑に処せられるまでの悲劇の後半生を描く。主演のサンドリーヌ・ボネールは撮影終了後に生まれた娘をジャンヌと名付けた。", "1995-04-30", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("252 生存者あり", "252 生存者あり", "", "", "http://image.eiga.k-img.com/images/movie/53514/315x210.jpg?1956", 0, "大災害に見舞われた東京を舞台に人々の「生還」と「救出」を壮大なスケールで描く人間ドラマ。監督は「舞妓Haaaan!!!」の水田伸生、原作は「海猿」原案者の小森陽一。伊藤英明、内野聖陽、山田孝之ら豪華キャストが共演。都心で発生した直下型地震の数週間後、巨大台風が襲来し、雹（ひょう）や高潮などの異常気象で東京はパニックに陥る。新橋駅の地下に閉じ込められた人々を救出するべく、ハイパーレスキュー隊（東京消防庁消防救助機動部隊）が命懸けの救出に挑む。", "2008-12-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鳳凰／わが愛", "鳳凰／わが愛", "", "", "http://image.eiga.k-img.com/images/movie/34294/315x210.jpg?1665", 121, "1920年代の中国を舞台に、激動の時代に翻弄されながらも愛を貫こうとする男女の運命を、実話を元に描いた日中合作の恋愛ドラマ。中井貴一が全編中国語で主演を務め、自らプロデューサーも手掛けている。些細な喧嘩から懲役15年の刑を受け投獄されたリュウ・ランは、暴力夫を殺害し服役中の女性ホンと運命的な出会いを果たす。次第に心を通わせていく2人だったが、ある日ホンが別の刑務所に移送されることになり……。", "2007-11-03", "76,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あゝ特別攻撃隊", "あゝ特別攻撃隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "久松静児や市川崑、増村保造に師事していた井上芳夫の監督デビュー作で、太平洋戦争末期、空しくも散った特攻隊員の青春を描いた戦争映画。明日なき命を人生や戦争についての語り合いのうちに過ごす予備学生出身の特攻隊員と、一身を挺して悠久の大義に生きることこそ自分の使命と感じている海軍兵学校出身の少尉。彼らの間には溝があった……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Germinal", "ジェルミナル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 160, "文豪E・ゾラの傑作小説を映像化した大河ロマン。1880年代、失業の嵐が吹き荒れる北フランスの炭鉱モンスーで、劣悪な労働条件や低賃金に反抗し、団結して労働運動を起こそうとする鉱夫たち。彼らとその家族、対立する経営者側の人間模様を重厚に描きながら、人間の尊厳を力強く浮き彫りにしていく。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOMENT", "MOMENT", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "ヴィジュアリストの手塚眞が大学生の時に手掛けた自主映画。当時のインディーズ作品の既成概念を破り、上映するや大ヒットとなった。スタッフ、キャストに多彩な面々が顔を連ねているのも注目。元気な少女・ポッキーが、100発100中の占い師に死の予言をされ……。一人の少女をめぐり展開していく一大エンターテインメント・ストーリー。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大人の見る絵本　生きてはみたけれど", "大人の見る絵本　生きてはみたけれど", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "東京郊外に引っ越してきた吉井一家。二人の子供たちはすぐに近所の子供たちと喧嘩を繰り返してガキ大将的存在となっていくが、ある日二人は自分たちに厳格な父親が上司にこびまくっている姿を捉えた８ミリ映画を見てしまい……。小津安二郎監督のサイレント映画時代の代表作。子供たちが繰り広げるユーモラスな描写と、当時の深刻な社会不況とを両立させた傑作である。", "1932-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OSAMA", "アフガン零年〈ぜろねん〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "タリバン政権崩壊後のアフガニスタンで初めて製作された長編映画。単独での外出や労働の禁止など、女性の権利を制限したタリバン政権下で、男装をして働くヒロイン。処罰の恐怖に怯え、少年として生きる彼女の姿を通して、貧困や圧制に苦しむ庶民の真実を浮き彫りにする。瓦礫の山と化したアフガンの風景も生々しく、鮮烈な印象を残す。", "2003-01-01", "77,76,33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Enron: The Smartest guys in the Room", "エンロン　巨大企業はいかにして崩壊したのか？", "", "", "http://image.eiga.k-img.com/images/movie/33744/315x210.jpg?3865", 110, "巨大企業エンロンによるアメリカ史上最大のスキャンダルに迫るドキュメンタリー。エネルギー業界の規制緩和の流れに乗って急成長を遂げた巨大企業エンロン。しかし、子会社との癒着が報道されたことをきっかけに粉飾会計や不正取引などが次々と発覚し、わずか46日間で破綻に追い込まれた。ベストセラーとなった原作本を基に、元社員の証言、ビデオ映像やテープ音声といった内部資料からエンロン崩壊の真相を暴く。", "2006-11-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAPPY NEW YEAR", "ピーター・フォークの恋する大泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "宝石専門の泥棒コンビが巻き起こす騒動を描くロマンティック・コメディ。二人は有名高級宝石店を襲撃する計画を立て、強欲な店長をだますため、完璧な作戦を実行する。すべては計画通りだったが、一人が宝石店の隣にある店の美女に恋をしたことから、雲行きが怪しくなっていく。クロード・ルルーシュ監督作「男と女の詩」のハリウッド・リメイク。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幸福の黄色いハンカチ　デジタルリマスター", "幸福の黄色いハンカチ　デジタルリマスター", "", "", "http://image.eiga.k-img.com/images/movie/55324/315x210.jpg?1492", 0, "山田洋次監督が高倉健を主演に描く人間ドラマ「幸福の黄色いハンカチ」（77）のデジタルリマスター版。共演に倍賞千恵子、武田鉄矢、桃井かおり。失恋して自暴自棄になった鉄也は、新車を買って北海道へ傷心の旅に出る。そこで鉄也は一人旅をしていた朱美のナンパに成功し、さらに2人は海岸で勇作という男と知り合う。旅をともにすることになった3人だが、刑務所から出所したばかりだと話す勇作が、愛妻へ出した葉書のことを語り始め……。", "2010-04-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Brothers of The Head", "ブラザーズ・オブ・ザ・ヘッド", "", "", "http://image.eiga.k-img.com/images/movie/1314/315x210.jpg?3827", 93, "70年代のイギリスを舞台に、結合体双生児のトムとバリーが率いる架空のパンクバンド「ザ・バンバン」の伝説をドキュメンタリー形式に則って描いた劇映画。監督はテリー・ギリアム監督未完の大作「ドンキホーテを殺した男」の企画成立から崩壊までを描いた傑作ドキュメンタリー「ロスト・イン・ラ・マンチャ」（01）のキース・フルトン＆ルイス・ペペ。", "2007-01-20", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Passengers", "パッセンジャーズ", "", "", "http://image.eiga.k-img.com/images/movie/53904/315x210.jpg?3061", 93, "飛行機事故で生き残った5人の乗客のカウンセリングを担当することになったセラピストのクレアだったが、5人の生存者がひとりまたひとりと姿を消していく。不審に思ったクレアは事故の真相を探っていくが、そこには驚愕の事実が……。「彼女を見ればわかること」「美しい人」のロドリゴ・ガルシア監督が挑んだサスペンスで、主演は「プラダを着た悪魔」「レイチェルの結婚」のアン・ハサウェイ。共演にパトリック・ウィルソン、デビッド・モースら。", "2009-03-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青空", "青空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "歌舞伎町で風変わりな出会いを果たした順一と久美子が同棲生活を始める。しかしある日、警察の家宅捜査から逃げ出した順一は、習志野の鉄工所で臨時雇いの仕事を得るが、そこでも事件に巻き込まれてしまう。やがて順一と久美子は久しぶりの再会を果たすが……。夏を背景にしていながら、冷ややかなムードの中に鬱屈した人間の感情が渦巻くサトウトシキ監督作品。どこまでもひたすら走り続ける主人公の一瞬の激情、そして殺した女の生首を持って町をさまよう姿が強烈な印象を残す。ビデオ、DVDタイトルは「青空　連続する欲望と殺意」", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIGITAL SHORT FILMS BY THREE FILMMAKERS", "三人三色", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "韓国の全州（チョンジュ）国際映画祭が企画し、毎年アジア圏を中心に選出された3人の映画監督に依頼して製作されるオムニバス映画。2001年はジャ・ジャンクー監督「イン・パブリック」、ツァイ・ミンリャン監督「神様との会話」、映像作家ジョン・アコムフラー「デジトピア」と、中国、台湾、イギリスの若手作家が“デジタルの可能性”をテーマにそれぞれ約30分のドキュメンタリーをデジタルビデオで制作した。ジャ・ジャンクー監督による「イン・パブリック」は第13回マルセイユ国際ドキュメンタリー映画祭グランプリを獲得。", "2002-08-31", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チャイナシャドー", "チャイナシャドー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "柳町光男監督が、J・ローンを主演に、香港ロケを敢行して撮り上げた野心作。様々な陰謀の渦巻く香港で名を成した男、ヘンリー・ウォン。しかしふとしたことから彼の過去の一部が暴かれ、ウォンの野望に影が射す。やがてウォン自身も知らぬ悲劇の歴史が明らかになるのだった……。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("上島ジェーン", "上島ジェーン", "", "", "http://image.eiga.k-img.com/images/movie/54554/315x210.jpg?1279", 99, "お笑いトリオ「ダチョウ倶楽部」の上島竜平が映画初主演を果たしたサーフィンドキュメンタリー。「地球を感じる」ため、日本有数のサーフ・スポットとして知られる千葉県志田下にやって来た上島と、後輩芸人の有吉弘行に密着。地元サーファーらとの交流や、次から次に巻きおこる事件、そして上島に訪れた淡い恋の行方を追う。監督は、「ピューと吹く！ジャガー THE MOVIE」のマッコイ斉藤。", "2009-05-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Big Sky", "果てしなき蒼空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "1830年代アメリカ。猟師のジムとブーンはセントルイスにやって来た。2人は、インディアンとの毛皮交易を目論むブーンの叔父ゼブと共に、毛皮商ジュルドネの船でミズーリを遡ることに。その船にはインディアンの娘、ティール・アイが乗っていた……。ホークス得意の゛男同士の友情゛にインディアンの娘との恋愛が絡む物語だが、キャスティングや撮影も難航し、監督にとって満足のいく作品にはならなかったという。", "1953-04-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リアル鬼ごっこ4", "リアル鬼ごっこ4", "", "", "http://image.eiga.k-img.com/images/movie/57654/315x210.jpg?1651", 75, "山田悠介原作のサバイバルアクション「リアル鬼ごっこ」（2007）、「リアル鬼ごっこ2」（10）に続く、新たな3部作（「リアル鬼ごっこ3」「4」「5」）の2作目。「血液型B型」の人間が理不尽で残酷な鬼ごっこのターゲットとなり、同時刻に異なる場所で起こる3つの事件が3部作を通して描かれる。女子高内でひとり誰ともつるまずにいるツカサは、不良に絡まれていた後輩のユイを助ける。そんな時、血液型がB型の人間を狙う殺人鬼たちが突然校内になだれ込んでくる。ユイを連れて逃げ出したツカサは、途中で出会った男子高校生マサハルと協力することになるが……。", "2012-05-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Road Ends", "デッドエンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "マフィアの裁判で証言をすることになり、ヒットマンに命を狙われた男マセーダ。身を隠すために田舎町に逃げ込んだ彼は、宿の女主人カットとの恋に安らぎを見いだす。だが、カットに思いを寄せる保安官ギルクライストが、マセーダの素性を知ってしまい……。スリリングな逃避行に愛憎劇を絡めたサスペンス・アクション。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("土佐の一本釣り", "土佐の一本釣り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "土佐の荒海でカツオ船に乗る16歳の血気盛んな少年と、おとなしいが芯の強い少女の純愛物語を、一本釣り漁の生活の中に描いた青春映画の佳作。揺れる小舟の上でのキス・シーン、海中で泳ぎながら抱き合うシーンなどは、純愛ものの鏡。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Retour en Normandie", "かつて、ノルマンディーで", "", "", "http://image.eiga.k-img.com/images/movie/53274/315x210.jpg?2149", 113, "「ぼくの好きな先生」のニコラ・フィリベール監督が、自らの映画製作の原点を振り返るドキュメンタリー。ノルマンディーで実際に起きた殺人事件を映画化したルネ・アリオ監督作「私ピエール・リヴィエールは母と妹と弟を殺害した」（76）の助監督としてこの作品に携わったフィリベールが、30年の時を経てノルマンディーを訪れ、当時メインキャストとして映画に参加した農民たちと再会する。", "2008-02-09", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Behind Enemy Lines", "エネミー・ライン", "", "", "http://image.eiga.k-img.com/images/movie/1384/315x210.jpg?2368", 106, "「アルマゲドン」のオーウェン・ウィルソン、オスカー俳優ジーン・ハックマン共演で、熱い男のドラマを描く戦争アクション。監督は報道カメラマン出身の新鋭ジョン・ムーア。ボスニア上空から敵地撮影をしていたクリス大尉は、攻撃を受けて不時着。その地でセルビア人民軍の凶悪犯罪を目撃し、その情報を持ち帰るため、救出ポイントに向かう。彼の上官、レイガード提督は、上層部の意向に逆らって、クリス大尉を救出しようとする。", "2002-03-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Streetracers", "ストリート・レーサー", "", "", "http://image.eiga.k-img.com/images/movie/53764/315x210.jpg?2866", 113, "ロシア発のカー・チェイス・アクション。主演は「ナイト・ウォッチ／NOCHINOI DOZOR」のアレクセイ・チャドフ。自動車修理工場で働くステパンは、車の修理に来た美少女・カーチャに一目惚れするが、カーチャは元恋人のドッカーに未練があった。ストリート・レースの元締めであるドッカーは、ステパンの腕を見込み賭けレースへ誘うが、その裏で密かに大掛かりな犯罪を企てていた。", "2008-09-06", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Code", "ザ・エッグ　ロマノフの秘宝を狙え", "", "", "http://image.eiga.k-img.com/images/movie/53924/315x210.jpg?3353", 0, "モーガン・フリーマンとアントニオ・バンデラスが主演のアクション・サスペンス。監督は「ピースメーカー」「ディープ・インパクト」のミミ・レダー。伝説の大泥棒リプリー（フリーマン）は、生涯最後の仕事として、ロマノフ王朝の秘宝ファベルジェのイースターエッグの強奪をたくらむ。親子ほど年の離れた相棒ガブリエル（バンデラス）とともに厳戒態勢の敷かれた建物に侵入するが、ニューヨーク市警、FBI、ロシアン・マフィアの謀略により、完璧な強盗計画が狂いだす。", "2010-05-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富士に誓ふ　少年戦車兵訓練の記録", "富士に誓ふ　少年戦車兵訓練の記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "15歳から18歳の少年が、日夜猛特訓に励む陸軍少年戦車兵学校。厳しい規律に従い、一人前となった少年たちは戦車兵として第一線に赴いていく。1943年に青少年の戦意高揚映画として公開された作品。彼らが日増しに成長していく様子を刻々と記録。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新 Mr.Boo！ 鉄板焼", "新 Mr.Boo！ 鉄板焼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "中国からパンツ一枚で逃げ出してきたブーは、香港の鉄板焼レストランのオーナーに助けられ、今は料理長。しかし、婿養子という立場と悪妻にいじめられる毎日。そんな時、美女シシーがレストランに現れ……。ゆるい展開も何のその、香港製活劇の真骨頂を見せつける喜劇役者M・ホイのバイタリティーあふれる快作だ。", "1985-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Woman under The Infulence", "こわれゆく女", "", "", "http://image.eiga.k-img.com/images/movie/12254/315x210.jpg?2045", 147, "　専業主婦のメイベルは、多忙な夫のニック、そして3人の子供と暮らしている。メイベルは夫婦ふたりきりで過ごすために子供たちを母親に預けるが、一方ニックは仕事で帰宅できなくなる。ひとり残されたメイベルは夜の街をうろつき、見知らぬ男を誘ってしまう。このことをきっかけに、メイベルの精神状態は不安定になっていく。ニックはそんな妻を理解できず困惑するが……。壊れかけた夫婦の絆とその再生を描くドラマ。カサベテスとジーナ・ローランズ、そしてピーター・フォークの共同出資で製作された。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("任侠列伝・男", "任侠列伝・男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "鶴田浩二主演・山下耕作監督による任侠映画。鶴田扮する主人公は幼い頃に両親を失いヤクザの親分に育てられ、親分のために人を斬る。「傷だらけの人生」に続きこの作品も鶴田浩二のヒット曲『男』をイメージして脚本が書かれ映画化された。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paranormal Activity 2", "パラノーマル・アクティビティ2", "", "", "http://image.eiga.k-img.com/images/movie/55794/315x210.jpg?2619", 91, "超低予算ながら、1億ドル以上の興収を記録し、社会現象となった大ヒットホラーの第2弾。衝撃的なラストで幕を閉じてから1年、何者かが侵入して事件を起こしたと考えたケイティの家族は家全体に警備用のカメラを設置する。だが、新たな超常現象がケイティを襲い……。主演は、前作同様ケイティ・フェザーストーン。監督は「ドア・イン・ザ・フロア」のトッド・ウィリアムズ。", "2011-02-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Moving McAllister", "理想の彼女と3日間で恋に落ちる方法", "", "", "http://ecx.images-amazon.com/images/I/51OW93F9mzL._SL210_.jpg", 90, "", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天国からのエール", "天国からのエール", "", "", "http://image.eiga.k-img.com/images/movie/55814/315x210.jpg?2233", 114, "後に数々のアーティストを輩出する音楽スタジオ「あじさい音楽村」を創設し、2009年にガンで亡くなった仲宗根陽さんの実話を阿部寛主演で映画化。沖縄で小さな弁当屋を営む大城陽は、弁当を買いにくる高校生たちがバンドの練習をする場所がないことを知り、店のガレージをスタジオに改装。「挨拶をすること」「赤点を取らないこと」「人の痛みがわかる人間になること」を条件に無料でスタジオを貸し出す。高校生たちは陽を“ニイニイ”と呼び慕い、練習に没頭するが……。主題歌をあじさい音楽村出身のガールズバンド「ステレオポニー」が担当。", "2011-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("成熟", "成熟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "山形県庄内平野の鼠ヶ関に伝わる“みこし流し”の行事には、みこしを担いだ男の濡れた体は、将来を誓い合った娘以外は拭いてはいけないという言い伝えがある。写真部の高校生・ゆう子はこの伝説を信じず、気軽に見知らぬ青年の体を拭いてしまったため、大騒動が巻き起こる。関根恵子が魅力的な一編。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロボゲイシャ", "ロボゲイシャ", "", "", "http://image.eiga.k-img.com/images/movie/54834/315x210.jpg?4401", 101, "冴えない少女ヨシエは、芸者の姉・菊奴の付き人として毎日こき使われていた。そんなある日、菊奴のなじみの客で大企業の御曹司ヒカルは、ヨシエの天性の戦闘能力に気付き、ヨシエを殺人芸者マシーンの育成組織に入れてしまう。監督は「片腕マシンガール」の奇才・井口昇、主演にグラビアアイドルの木口亜矢。", "2009-10-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("下弦の月／ラスト・クォーター", "下弦の月／ラスト・クォーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "人気漫画家、矢沢あいの同名原作を、ミュージックビデオを数多く手掛けてきた二階健監督が初映画化。19年に一度だけ、同じ形の月が同じ月日に現れる“下弦の月”の奇跡を巡って、交通事故で記憶を失くした女子大生が体験するミステリアスな出来事を描いたラブ・ファンタジーだ。「キル・ビル」の栗山千明がヒロインを好演。19才の女子大生、美月は、ミステリアスな青年アダムと運命的に出会う。ある日、美月は彼との待ち合わせ場所に向かう途中で交通事故に遭遇、記憶を失ってしまう。そして気がつくと、ある古い洋館から出られなくなっていた。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUTTERFIELD ８", "バターフィールド８", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "モデルが本業のグロリアは、“バターフィールド８番”の電話番号で呼び出されるコールガールでもあった。様々な男に体を売る彼女が、妻子ある男を真剣に愛してしまって……。E・テイラーがアカデミー主演女優賞を獲得したメロドラマ。原作はJ・オハラの同名小説。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tournee", "さすらいの女神（ディーバ）たち", "", "", "http://image.eiga.k-img.com/images/movie/56324/315x210.jpg?2088", 111, "「キングス＆クイーン」「潜水服は蝶の夢を見る」の名優マチュー・アマルリックがメガホンをとり、第63回カンヌ国際映画祭で監督賞を受賞したロードムービー。フランスのTVプロデューサーとして活躍していたジョアキムは、トラブルを起こして業界を追われ、全てを捨てて渡米する。数年後、アメリカのショーダンサーたちと結成したグループ「ニュー・バーレスク」を成功させ、意気揚々とパリへ凱旋しようとするが、すでに忘れられた存在のジョアキムはステージをブッキングできない。過去のつてを頼って奔走するジョアキムだったが、次第にダンサーたちとの間に亀裂が生じていき……。", "2011-09-24", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QU’EST−CE QU’ON ATTEND POUR E＾TRE HEUREUX！", "ファンタスティック・カーニバル　フランス式CMの作り方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "C・セローが監督したおしゃれで、シュールなコメディ。自動車CMの撮影のためにスタジオにやって来たエキストラたち。各自の特技をフルに生かしてやろうと思っていた彼らは、彼らの個性を無視したディレクターに反発を感じ、ついに撮影スタジオで大騒動を巻き起こす。", "1982-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("億万長者", "億万長者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "実直で小心者なるがゆえにうだつの上がらない薄給税務署員・館。しかしそんな彼が作成した脱税メモが、周囲の人間を大騒動に巻き込んでいく……。安部公房、和田夏十、横山泰三らのシナリオ協力を得て完成した風刺喜劇。青年俳優クラブ自主製作作品。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Spider lillies", "Tatto 刺青", "", "", "http://image.eiga.k-img.com/images/movie/54014/315x210.jpg?4660", 98, "台湾シネマ・コレクション2008上映作品。1999年に起きた台湾大地震をモチーフに、女と女、女と男、さまざまな愛の形を描いたラブストーリー。出演は台湾の人気アイドル、レイニー・ヤンと「ハムナプトラ3」のイザベラ・リョンら。男たち相手に小銭を稼ぐネットアイドルのジェイドは、憧れの女性である刺青師の竹子に彼岸花の刺青を彫ってほしいと頼む。竹子は彼岸花への悲しい記憶からそれを拒むが、2人はお互いの傷を埋め合うように惹かれあっていき……。", "2008-08-23", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("出世太閤記", "出世太閤記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "本作品は嵐寛寿郎の日活入社第４回主演作品で、ここでは若き日の豊臣秀吉、つまり木下藤吉郎が、群雄割拠の戦国時代に貧農の身分からやがて織田信長の家来となり、ついには信長の有力な侍大将になるまでを演じている。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／CONTACT MOTHER", "シバジ　快楽の報酬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「ハラギャティ」で一躍脚光を浴びたI・グォンテク監督。韓国を代表する映画作家の一人として活躍する彼が、1986年に撮った問題作。朝鮮・李朝末期。両班（ヤンパン＝地方の名家）のシン家では、後継ぎができないため、シバジ（代理母）を雇うことになった。いろいろな候補者を試した末ようやく決まったのはまだ17歳の処女、オンニョだった。シン家にやって来て、その立派な屋敷に驚くオンニョ。そして彼女は代理母としての役目を見事に果たすのだが、シン家の当主、サンギュと愛し合うようになっていた……。社会の底辺に生きる女性の悲しみと封建制の不条理さをえぐり出したストーリー。“韓国の溝口健二”と呼ばれるイム・グォンテク監督らしい作品といえるだろう。主演は「鯨とり２」で注目され、イム監督と再び組んだ「ハラギャティ」のカン・スヨン。", "1986-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAPTAIN BLOOD", "海賊ブラッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "一世を風靡した往年のアクション俳優、E・フリンの名を高めた冒険活劇の傑作。追放されたアイルランド人の精神科医ピーター・ブラッドが名うての海賊となり、宿敵のフランス人公爵を向こうに回して胸躍る大活躍を展開する。趣向を凝らしたチャンバラのおもしろさ、E・フリンの驚異的な動きもさることながら、迫力ある大海戦シーンも見どころの一つで、M・カーティス監督の映画職人ぶりも堪能できる。初公開時は119分だが、その後いくつかの短縮版、コンピュータによる着色版などが存在する。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO　TOMORROW", "ノー・トゥモロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ラッパーのマスターPがメガホンを執り、自らも出演したバイオレンス・アクション。ヒップホップにのせて描く銃撃シーンがユニークな１本だ。武器密売組織に加わり、リーダーの信頼を得た謎の青年。彼の狙いは何なのか。一味の動向を探るFBIの思惑も絡み、彼らは飛行場での死闘になだれ込んでいく。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NORA", "ノーラ・ジョイス／或る小説家の妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "20世紀三大文豪の一人、ジェイムズ・ジョイスと、その妻ノーラの愛憎の日々を描いた恋愛ドラマ。若きジョイスはホテルのメイド、ノーラと情熱的に結ばれる。やがて子供が生まれるが、貧困のなかで関係は悪化。離れては近づき嫉妬に苛まれながら、ジョイスは『ダブリン市民』を書き上げるが……。E・マクレガーが俳優仲間と結成した製作会社ナチュラルナイロンの第１回作品で、彼は激情家のジョイス役を迫真の演技で演じてみせる。", "2000-01-01", "7,33,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MACCHINA AMMAZZACATTIVI", "殺人カメラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ロッセリーニには珍しいコメディ作品。海岸の村に住む写真屋のチェレスティーノはある日謎の男に会うが、その日から彼にはカメラのシャッターを押すと、写真に写った人間を殺すことができる不思議な力が授かった。彼はその力を使って、貧しい村人を苦しめる悪人を退治するために立ち上がるのだったが……。", "1948-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PUNK", "ザ・パンク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "14歳の中学生によって書かれ、1970年代末のイギリスで“パンク世代のバイブル”とされた伝説的小説『THE PUNK』の映画化。警察官の父親に反抗する純粋なパンク少年デビッドとレイチェルの『ロミオとジュリエット』にも似た恋、レイチェルの別れたボーイフレンドであるヘヴィメタ野郎との抗争が、パンク・ミュージックをバックにいきいきと描かれている。スタッフは監督と撮影監督以外すべて25歳以下、TV局や企業スポンサーも付けずに若者の力で作り上げた、いわばハンドメイド・ムービー。", "1993-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Super 8", "SUPER 8 スーパーエイト", "", "", "http://image.eiga.k-img.com/images/movie/55564/315x210.jpg?2507", 111, "「M:i:III」「スター・トレック」のJ・J・エイブラムス監督が、スティーブン・スピルバーグをプロデューサーに迎え、「未知との遭遇」などスピルバーグ初期監督作にオマージュをささげて製作するSF大作。1979年、米空軍はネバダ州エリア51の一部を閉鎖。ある物をオハイオ州の施設に輸送しようとするが、貨物列車が脱線事故を起こしてしまう。そして、その中から何かがうごめきだし……。8ミリカメラで映画製作をしていた少年少女たちが事故現場に遭遇し、エリア51をめぐるナゾに巻き込まれていく。", "2011-06-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兄弟仁義 逆縁の盃", "兄弟仁義 逆縁の盃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "海に廃液を流す工場の工場長と結託し、暴利をむさぼる岩井組と、漁師の味方の巽組が対立する四日市。この地に流れて来た小島松男は岩井の義兄弟、寅次郎に出会って意気投合する。だが真相を知った寅次郎は岩井組に殺されてしまう。すべての陰謀を見破った松男は単身、岩井組に乗り込む。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES BICHES", "女鹿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "舞台はパリとサントロペ。父の莫大な遺産で気ままに暮らしているフレデリックと、彼女が連れてきた貧しい画家志望の娘ホワイ、そして建築家ポール。３人の男女が微妙に絡み合うサスペンス・タッチの女性映画。", "1968-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN ME", "アメリカン・ミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "少年時代から悪事を重ね、やがて刑務所のボスとして君臨、後にイーストL.A.の暗黒街を支配した犯罪王サンタナ。このメキシコ・ギャングの生涯と、メキシカン・マフィアの実体を描いた作品。「ブレードランナー」などの個性派で、アカデミー賞ノミネート経験もあるE・J・オルモスが、17年間温めてきた企画を自ら監督、主演している。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEMONS／DE´MONI", "デモンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「サスペリア」「フェノミナ」などで知られるイタリアのホラー作家、アルジェントが、製作・原案・脚本を手掛けた恐怖映画。ノストラダムスの予言“デモンズの祝祭が始まるとき、暗黒の底、アキロンの大王が目覚める”という謎のレプリカのもとに、“デモンズ劇場”なる映画館でノストラダムスにまつわる作品を見ていた観客たちが、映画さながらの恐怖を館内で味わっていくさまをスリリングに描く。", "1985-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍邪", "忍邪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "天正9年、伊賀の国は織田と甲賀の連合軍に攻め込まれようとしていた。伊賀の腕利き下忍、鈎摘（かぎつめ）、虎鋏（とらばさみ）、独鈷（どっこ）の3人は、埋火（うずみび）というナゾの下忍を甲賀との国境まで護衛するよう上忍に命じられる。しかしその道中、埋火がナゾの伝染病を患っていることが判明し、3人は敵軍へ死病を蔓延させるために利用されたことを知る。合田雅吏、虎牙光輝、柏原収史らが伊賀忍者に扮する。", "2010-06-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Angel Heart", "エンゼル・ハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ウィリアム・ヒョーツバーグの小説「堕ちる天使」を、名匠アラン・パーカー監督が見事に映画化したオカルト・ミステリー。1950年代のブルックリン。私立探偵ハリーの元に、サイファという男から高額の依頼が舞い込む。それは、大戦後に失踪した人気歌手ジョニーを探してくれという内容のものだった。彼は早速調査を開始するのだが、行く先々で不可解な殺人事件が起こり……。謎の人物サイファをロバート・デ・ニーロが怪演。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KNIGHT MOVES", "美しき獲物", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "チェスの世界選手権大会が行われている島で、連続猟奇殺人事件が発生。警察はチェスの天才を容疑者としてマークするが、彼には動機はなかったものの、アリバイもなく、容疑は深まっていく。精神鑑定を依頼された女性心理学者も彼に惹かれていき……。当時、私生活では熱いカップルだったランバートとレインの主演による刺激的なサイコ・スリラー。", "1992-01-01", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌７", "釣りバカ日誌７", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "前作「釣りバカ日誌スペシャル」の森崎東監督から栗山富夫監督に戻ったシリーズ第８作。出張にかこつけ、福井の海岸で釣りをしていたハマちゃんとスーさんは、知的な風貌の美女・田上彩子と知り合う。誘われるまま二人に加わり、釣りの興奮を味わう彩子。東京に戻って、スーさんは偶然入った歯医者で彩子と再会、ハマちゃんには香港に出張と偽って週末に釣りに行く約束をする。ところが、現場でハマちゃんとバッタリ！ 嘘をついていたスーさんが許せないハマちゃんは、絶交宣言。辞表をFAXで送りつける。ハマちゃんの妻・みち子さんを、石田えりに代わって新たに浅田美代子が演じている。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("居酒屋ゆうれい", "居酒屋ゆうれい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "街はずれの居酒屋の主人、壮太郎は、亡き前妻との約束を破り、若い後妻を迎える。しかし初夜の床に現れたのは怒った前妻の幽霊だった。ところがその幽霊と新妻はなぜか意気投合し、やがて３人は奇妙な共同生活を始めることに……。思いっきり笑えて、ホロリと泣けるハートウォーミングなラブ・コメディ。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BATTLE FOR THE TREASURE", "バトル・ヒーロー 秘宝強奪作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "政情不安のカンボジアを舞台に、クメール王朝に伝わる秘宝“ブラッドストーン”と、コング王女をめぐって、戦争のプロたちが強奪作戦を展開する。「フルメタル・ニンジャ」などのあやしげな作品で知られる香港・フィルマーク社のパワーがタイ映画とドッキング。恐れを知らない荒唐無稽なアクションが展開。", "1987-01-01", "6,23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀座の次郎長", "銀座の次郎長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "深刻な人手不足に悩む銀座商店街。同じくヤクザ組織も人手不足に悩み、全国グレン隊の大ボスから叱責された銀座のボス・竜巻は、苦手とする“銀座の次郎長”と手を組み、人材探しに専念することに。さっそく次郎長は全国から人を集めて来るが、竜巻が罠を仕掛けて……。旭主演の“暴れん坊”ものシリーズの第４作。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NED　KELLY", "ケリー・ザ・ギャング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "オーストラリアに実在した伝説のアウトロー“ネッド・ケリー”を取り上げた、実話をベースにするアクション劇。そんな熱血型ヒーロー像をヒースが演じ、「ロード・オブ・ザ・リング」シリーズが当たり役となったオーリーと共演した。主人公はギャング団を結成し銀行強盗を繰り返しては、貧しい人たちに金を与え続けた。やがて、警察との凄まじい銃撃戦が始まる。", "2002-01-01", "11,3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沓掛時次郎", "沓掛時次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "一宿一飯の恩義からやむなく斬った男の妻子の面倒をみることになった旅人、沓掛時次郎の人情厚い振るまいを描いた、長谷川伸の代表作の映画化。戦前から何度も映画になったネタだが、今回は市川雷蔵がしがらみを捨てきれない渡世人の性を見事に表現した。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mad Money", "デンジャラスな妻たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("帰って来た若大将", "帰って来た若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "加山雄三の芸能生活20周年記念作品として、10年ぶりに製作されたシリーズ第18作。サザンクロス島の大統領の片腕で自治政府の顧問をしている若大将こと田沼雄一と、三丸商事の副社長の青大将こと石山新次郎は、美人TVプロデューサーをめぐり、またまた大喧嘩……。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE QUIET MAN", "静かなる男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "アイルランド、カースルタウンの駅にショーン・ソーントンが降りる。アメリカでボクサーだった彼は試合で相手を死亡させ引退を決意、父の故郷の村に戻ったのだ。そこは想像以上に美しく牧歌的だった。そして村の娘メアリー・ケイト・ダナハーに一目惚れし、ラブ・ストーリーが展開する。だがメアリーの兄で家長のウイルが結婚に反対、なんとか式にこぎつけるが持参金を渡さない。メアリーの名誉のため決闘覚悟で談判に臨むショーン。そしてこの静かな男が延々と野を越え、川越えて繰り広げるラストの殴り合い……。美しく魅力的なフォード映画の傑作。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こびと劇場", "こびと劇場", "", "", "http://image.eiga.k-img.com/images/movie/57804/315x210.jpg?2189", 0, "なばたとしたかのベストセラー絵本「こびどづかん」シリーズを映像化した「こびと観察入門」から、人気の高い「カクレモモジリ編」「ヤマビコビト編」「バイブスマダラ編」「シボリオオチチ編」の4編を抜粋した劇場版。", "2012-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひとりかくれんぼ　新劇場版", "ひとりかくれんぼ　新劇場版", "", "", "http://image.eiga.k-img.com/images/movie/55624/315x210.jpg?1746", 0, "女子高生の栞（しおり）は、学校で誰かが都市伝説の降霊術「ひとりかくれんぼ」をしたという噂話を聞く。その日、アルバイトのため入院中の母親の見舞いに行けなくなった栞は、独り暮らしをしている兄を訪ねるが、誰もいない部屋のパソコンには「ひとりかくれんぼ」のネット掲示板が表示されていた。栞は兄の幼なじみの白石とともに、こつ然と姿を消してしまった兄を探すことになるが……。前作「ひとりかくれんぼ　劇場版」でメガホンをとった山田雅史監督が再投球し、アイドルグループ「AKB48」の増田有華が映画初主演を務める。", "2010-07-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アイ・ラヴ・ユー", "アイ・ラヴ・ユー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「遥かなる甲子園」の大澤豊と、実際にろうあ者であり、ろうあ者劇団の代表を務める米内山明宏が共同演出。ハンデを乗り越えて手話で演劇上演を目指す女性の前向きな姿を描く感動作だ。聴者の男性と結婚したろうあ者の朝子は、自分が原因で娘、愛がいじめられていると知りショックを受ける。彼女は自分が一生懸命生きていることを示そうと劇団に入ることを決意。そこで、聴者とろうあ者の様々なトラブルに遭遇し、朝子は母として人間として成長していく。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あゝ陸軍 隼戦闘隊", "あゝ陸軍 隼戦闘隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "268の敵機を撃墜したパイロット、加藤建夫戦闘機隊長の半生を描く戦記ドラマ。日支事変に出動した加藤は戦功を上げ、“撃墜王”と呼ばれるようになるが、部下の死や旧知の敵軍パイロットの撃墜を目の当たりにしたことから、苦悩にさいなまれる。やがて日米は開戦し、加藤も隼戦闘隊を率いて出陣するが……。空に生き、空に散った男の姿を、豪快な特撮アクションとともに活写。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("砂の影", "砂の影", "", "", "http://image.eiga.k-img.com/images/movie/53314/315x210.jpg?1944", 76, "「すべては夜から生まれる」の甲斐田祐輔監督による異色ラブストーリー。OLが恋人を殺害した後に40日間その遺体と一緒に暮らし続けたという実在の事件\"ラストダンス殺人事件\"をモチーフに、愛に取り憑かれた女性の危うい日常を、全編8ミリ撮影による美しい映像で綴る。毎日淡々と会社に通うOLユキエは、恋人の玉川と築いた2人だけの世界を心の拠りどころとしていた。そんなある日、ユキエに興味を抱いた同僚の真島が彼女の部屋を訪れ……。", "2008-02-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HESOMORI ヘソモリ", "HESOMORI ヘソモリ", "", "", "http://image.eiga.k-img.com/images/movie/56684/315x210.jpg?4782", 103, "福井県の豊かな自然と、1500年の伝統をもつ越前和紙工場を舞台に繰り広げられるファンタジードラマ。1970年代の福井県。10歳の少年さとしは仲間たちと山菜取りにでかけた山の中で不思議な祠（ほこら）と穴（へそ）を見つけ、その穴を通じて幕末の世界に迷い込んでしまうが、危ういところを“へそもり”のたけじいに助けられる。40年後、たけしは越前和紙の紙すき職人として働いていたが、穴の存在が世間に発覚。たけじいから“へそもり”の役目を受け継いでいたたけしは、穴の秘密を守るため奔走する。", "2012-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蘇乞兒", "酔拳　レジェンド・オブ・カンフー", "", "", "http://image.eiga.k-img.com/images/movie/56204/315x210.jpg?4000", 116, "1861年清王朝戦乱期、戦いで功績を上げた戦士スー・サンは妻子と穏やかに暮らしていた。しかし5年後、スーの父親に実父を殺された義兄ユアンが、スーの父親を殺し、息子のフォンを奪ってしまう。自身の無力を嘆くスーは酒に溺れていくが、医者のユや伝説のヒゲ仙人とその弟子の武神らに出会い、新たな武術「酔拳」を習得していく。ジャッキー・チェンを一躍スターダムに押し上げた「ドランクモンキー　酔拳」（1978）のユエン・ウーピン監督が、「酔拳」をテーマに新たに描くアクション大作。「ワンス・アポン・ア・タイム・イン・チャイナ」のチウ・マンチェクが主演。ミシェル・ヨー、ジェイ・チョウらアジア圏のスターが集う。", "2011-06-25", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOUNG ADAM", "猟人日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "英国を代表するビートニク作家アレグザンダー・トロッキの小説を映画化した官能サスペンス。ある女性の死をきっかけに、次から次へと相手を替え、性欲に耽っていた男の複雑な心情と秘密が浮き彫りにされる。ユアン・マクレガーが、堕ちていく主人公の心の闇を妖しく虚ろな表情で演じきる。貨物船の船員が女性の水死体を発見する。やがて、女たちと体だけの関係を続けてきた彼と、死んだ女性との関係が明らかになる。", "2005-02-26", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画　スマイルプリキュア！（仮題）", "映画　スマイルプリキュア！（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOKYO BEAST", "TOKYO BEAST", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "夜の渋谷を渡り歩く若者たち。路上で、クラブで酒をあおり、無軌道にたむろする彼らの姿が積み重ねられる。そんな現代の風景の中、陽哉、利明、稔の３人も同じような青春を送っていた。あるクラブで彼らは不思議な魅力を放つ少女、未央子に出会う。陽哉は彼女に聞く。“おまえの夢は？”。彼女が答える。“人生が早く終わること”。空虚な会話が続く中、未央子の空想と回想が重なり、渋谷の夜はただ更けていく……。様々なイメージが矢継ぎ早に交錯し、方向性のないストーリーが脈絡もなく進む。ドラッグ感覚に満ちたユニークな青春映画にして、異色のパンク・ムービーとなった。豊川悦司と共演した「男たちのかいた絵」でスクリーンに登場して以来、「ありがとう」「恋極道」などの作品で注目を集めてきた夏生ゆうなの存在感が際立つ。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談お岩の亡霊", "怪談お岩の亡霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "有名な『東海道四谷怪談』を、いわゆる怪談としてではなく、徹底したリアリズムをもって、出世を狙う貧乏侍・伊右衛門の破滅していく姿を描いた異色作。浪人侍の貪欲さを体あたりで演じた若山富三郎の迫力がものすごく、同様に貧しいながらもエネルギッシュな町民の生活を細かく見せた演出も見事。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACES GO PLACES IV", "スペクターX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「悪漢探偵」「皇帝密使」に続く、C・マックとS・ホイのコンビによる作品。“プリズム”なる科学兵器をめぐって、ハンサムな私立探偵とドジな刑事が、ギャング団とにぎやかな争奪戦を展開。ギャグとアクションの連続が見もの。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春の戯れ", "春の戯れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "マルセル・パニョルの名戯曲『マリウス』を、山本嘉次郎が翻案、映画化した港町ロマン。居酒屋のせがれ正吉は海と異国への憧れを断ち難く、日々、水夫として出港するのを夢に見る。正吉には相愛の幼なじみ・お花がいた。お花は正吉への恋情に苦しみながらも、彼の夢を叶えるべく、一夜の想い出を胸にしまい、彼女を見初めた呉服問屋の旦那のもとに嫁いでいく。やがて、お花は正吉の子を生むが……。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一個都不能少／NOT　ONE　LESS", "あの子を探して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "世界３大映画祭を制したチャン・イーモウに２度目のヴェネチア映画祭金獅子賞をもたらした感動作。小学校の代用教員になった13歳のウェイは、生徒が一人も辞めなかったらもらえるという褒賞金のために、懸命に生徒を見張る毎日を送る。ある日、クラスで一番わんばくなチャンが出稼ぎに行った町で迷子になってしまう。チャンを探すため町へ探しに行く決心をしたウェイは……。一人もプロの俳優を使うことなく嘘のない本物の感情を引き出したイーモウの演出は見事。", "1999-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ITALIENSK　FOR　BEGYNDERE", "幸せになるためのイタリア語講座", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "2001年のベルリン映画祭で銀熊賞をはじめ４部門に輝いたほか、各国の映画祭で高い評価を集めたデンマーク映画。コペンハーゲン近郊のとある町を舞台に、イタリア語教室を通じて出会った、性的不能のホテルマン、妻を亡くしたばかりの神父ら、大きな悩みを抱えた男女６人が、それぞれに幸せを見いだしていく。女性監督L・シェルフィグの優れた人間洞察力に注目。", "2000-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GOONIES", "グーニーズ", "", "", "http://ecx.images-amazon.com/images/I/617EGJ2UHGL._SL210_.jpg", 116, "S・スピルバーグの原案、製作総指揮による冒険活劇。演出は、「スーパーマン」「リーサル・ウェポン」などスケールの大きい娯楽作で知られるR・ドナーが担当。ちょっぴり不良がかった子供たちというニュアンスをもつ“グーニーズ”の７人の面々を主人公に、洞窟から地下の国へと発展する宝さがしの旅を描いた一編。息もつかせぬアクションやユーモアたっぷりの会話が全編に盛り込まれ、まさに“インディ・ジョーンズ”の子供版。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Boys Don't Cry", "ボーイズ・ドント・クライ", "", "", "http://image.eiga.k-img.com/images/movie/1824/315x210.jpg?3248", 119, "ヒラリー・スワンクが第72回アカデミー主演女優賞ほか、全米賞レースで数々の女優賞を受賞したドラマ。1993年、ネブラスカ州の小さな町、フォールズ・シティに突然訪れた美しい青年ブランドン・ティーナ。彼はバーで知り合ったラナという女性に一目で恋に落ちる。地元の男にはない彼の不思議な魅力にラナも強く惹かれていった。ラナの仲間のジョンやトムも、彼を男と見込んで仲間として受け入れつつあった。しかし彼の本名はティーナ・ブランドン。肉体と精神の性の不一致という“性同一性障害”を持って生まれた女性だったのだ。", "2000-07-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BRIDGES AT TOKO−RI", "トコリの橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "朝鮮戦争におもむく、アメリカ空軍中尉の使命と日常生活を描く社会派映画。ハリー中尉は家族とともに、横須賀での休暇を楽しんでいた。一週間後、彼は前線に向かうが……。のどかな休日の風景と過酷な戦闘シーンの対比を劇的に描写したシリアスな一編。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PERMANENT VACATION", "パーマネント・バケーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "作家を目指してパリに渡り映画に目覚めて帰国したジャームッシュ監督が、ニューヨーク大学映画学科の卒業制作として完成した初の長編。社会に適応できない高校生アリーは、パンクを気取ってリリカルに鬱屈した日々を送っている。あきれ顔のガールフレンドにも愛想をつかされ、徘徊する夜の街角でひとりのサックス吹きに出会うアリー。旅立ちの時が迫っていた……。", "1986-07-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・ソレが男の生きる道", "喜劇・ソレが男の生きる道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "藤本義一の小説『骨までいただき』の映画化。敗戦の焼け跡で青春時代を過ごした中年男たちの生き様をコミカルに描く。大阪の焼け跡で進駐軍相手のポン引き商売を始めた二人組。万博でにぎわう25年後、彼らの口八丁手八丁は衰えをしらず……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("希望のシグナル　自殺防止最前線からの提言", "希望のシグナル　自殺防止最前線からの提言", "", "", "http://image.eiga.k-img.com/images/movie/57754/315x210.jpg?1459", 102, "毎年約3万人が自ら命を絶っていく日本の中でも、2010年までに15年連続で最も高い自殺率を記録した秋田県で行われている自殺対策の1年間を記録したドキュメンタリー。人と人とのつながりの大切さを説き、過疎化が進む地域のコミュニティを復活させようと、1杯100円のコーヒーサロンを立ち上げた「心といのちを考える会」会長で住職の袴田俊英さんや、かつての自身の経験から会社経営者とその家族の自殺防止に取り組むNPO法人「蜘蛛の糸」の佐藤久男さんらが、それぞれの取り組みのなかで多くの人と出会い、やがて個々の民間団体が連携して大きな流れを作り出していく様子を追う。", "2012-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GOLDEN CHILD", "ゴールデン・チャイルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "100年に一人という、この世に善をもたらす完璧な子供“ゴールデン・チャイルド”が誘拐された。彼を救出できる唯一の男チャンは遠隔の地、チベットに旅立つ……。E・マーフィが出演したスリルと冒険にあふれた爆笑コメディ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の賭場", "女の賭場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "江波杏子主演の大ヒットシリーズ第１作。関東一円の親分たちを集めた盛大な手本引き花会で、名人と言われる胴師、辰造がイカサマを見破られ自殺した。辰造の娘アキは、二度と花札は手にしないと誓う。ところが、ヤクザに憧れる弟、広志が父を死に追いやった男の率いる立花組に出入りするようになったことから、やがて一世一代の大勝負を仕切ることになる。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHORE", "娼婦たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "秘密裏でありながら公然と存在する性を売りにした職業、娼婦。本作は、人種、国籍、年齢の違うさまざまな娼婦たちのインタビューやプライベート映像をまとめたもの。彼女たちのSEX観を探りながら、深層心理を探った意欲作。娼婦だけでなく、買う側の男たちの声もあり、興味深い内容だ。世界最古の職業、それは“娼婦”。今も昔も変わらず欲望が渦巻く世界で、娼婦たちと彼女たちを買う男たち、双方が性について語る。", "2005-09-23", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAPTAIN SINDBAD", "キャプテン・シンドバッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "千夜一夜で有名なシンドバッドが長い航海から帰ると彼の郷土は暴君カリムに占領され、婚約者は捕らわれていた。魔術師の力を借り不死身となったカリムを追うシンドバッドの冒険活劇。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("松ヶ根乱射事件", "松ヶ根乱射事件", "", "", "http://image.eiga.k-img.com/images/movie/33864/315x210.jpg?4832", 112, "「リンダリンダリンダ」の山下敦弘監督によるブラックコメディ。1990年代初頭の小さな田舎町・松ヶ根。畜産業を営む鈴木家の双子の弟・光太郎は警察官として働いているが、この町では事件らしい事件が起こることはなかった。そんなある日、どうも訳アリなカップルが町にやって来たことから、平和な町に少しずつ不穏な空気が流れはじめる……。主人公・光太郎を「ゲルマニウムの夜」「ゆれる」の新井浩文が好演。", "2007-02-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チョコラ！", "チョコラ！", "", "", "http://image.eiga.k-img.com/images/movie/54344/315x210.jpg?3624", 94, "ケニア共和国の地方都市ティカのストリートで暮らす子供たちは、ゴミ集めや物乞いで生計を立てていることから、“チョコラ”（スワヒリ語で「拾う」の意）と呼ばれ蔑まれている。過酷な生活の中、お互いに助け合い力強く生きる子供たちや、彼らとNGOとの関わりをユーモラスに描いたドキュメンタリー。「阿賀に生きる」でカメラマンを務めた小林茂が監督を務める。", "2009-05-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Si Puo Fare", "人生、ここにあり！", "", "", "http://image.eiga.k-img.com/images/movie/55944/315x210.jpg?1613", 111, "法律により精神病院の撤廃が進められていた1980年代のイタリア・ミラノを舞台に、実話を映画化したコメディドラマ。革新的な考え方をもつネッロは労働組合から疎まれ、精神病院から追い出された元患者たちの協同組合に左遷される。精神病の知識などないネッロだったが、元患者たちとあらたな事業を立ち上げるために奮闘する。イタリア映画祭2009にて「やればできるさ」のタイトルで日本初上映。11年劇場公開。", "2011-07-23", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天脈傳奇／THE　TOUCH", "レジェンド／三蔵法師の秘宝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "アジア屈指の本格派アクション女優、ミシェル・ヨーが初プロデュースしたアドベンチャー活劇。『西遊記』で知られる三蔵法師の秘宝をめぐり、秘宝を守る曲芸師一族と悪の組織との攻防を、手に汗握るアクションの連続で描く。中国大陸4000キロを横断する壮大なスケールの映像美も見もの。三蔵法師にまつわる秘宝を守る一族の末裔インの前に、秘宝発見の手がかりとなる“敦煌の心臓”を持った昔の恋人エリックが現われ……。", "2005-04-16", "8,6,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jennifer's Body", "ジェニファーズ・ボディ", "", "", "http://image.eiga.k-img.com/images/movie/53224/315x210.jpg?4196", 102, "「JUNO／ジュノ」でアカデミー賞を受賞したディアブロ・コーディによるオリジナル脚本を「イーオン・フラックス」「ガール・ファイト」のカリン・クサマ監督＆ミーガン・フォックス主演で映画化したホラー・コメディ。アメリカ中西部の田舎町を舞台に、ある手違いで悪魔に取り憑かれてしまった美しい女子高生ジェニファーと、彼女の秘密を知ってしまった親友のニーディの葛藤と戦いを描く。共演に「マンマ・ミーア！」のアマンダ・セイフライド。", "2010-07-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Avatar", "アバター", "", "", "http://image.eiga.k-img.com/images/movie/53184/315x210.jpg?1287", 162, "巨匠ジェームズ・キャメロンが「タイタニック」（97）以来12年ぶりに放つ、デジタル3DによるSF超大作。22世紀、地球から遠く離れた惑星パンドラへとやってきた元海兵隊員ジェイクは、自らの分身となる“アバター”を操り、先住民ナヴィと交流するが、やがて鉱物資源を巡って勃発する人類とナヴィとの戦争に巻き込まれていく。主人公ジェイクに「ターミネーター4」のサム・ワーシントン。共演にゾーイ・サルダナ、シガニー・ウィーバーほか。", "2009-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋人", "恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "結婚式を翌日に控えた女が、最後の１日を楽しく過ごしたいと、小さい時から兄弟のようにつき合っていた男と奔放に遊びまわる様子を、女の微妙な心理を絡めて描いたドラマ。梅田晴夫の放送劇『結婚の前夜』を、和田夏十と市川崑が脚色した。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRE HEAD", "ファイヤーヘッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "元ソ連特殊破壊工作員だった男が、自由を求めて渡ったアメリカで、巨大な闇組織の陰謀を知り、闘いを挑んでいく。主人公に協力する科学者をジャック・レモンの息子、C・レモンが好演している。「サウンド・オブ・ニュージック」のC・プラマーや、オスカー俳優M・ランドーなど渋い俳優がそろう。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La nuit americaine", "映画に愛をこめて アメリカの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "フランソワ・トリュフォー監督が、映画を愛する者たちに捧げた群像劇。フランス・ニースのスタジオ“ラ・ビクトワール”を舞台に、「パメラを紹介します」という映画の撮影を行っているスタッフ・キャストたちの人間模様が描かれる。出演はジャクリーン・ビセット、ジャン＝ピエール・レオほか。原題の「Day for Night」とは、カメラレンズにフィルターをかけて、夜のシーンを昼間に撮影することを指す。73年度のアカデミー賞では外国語映画賞を受賞した。", "1974-12-31", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("", "ミツバチの羽音と地球の回転", "", "", "http://image.eiga.k-img.com/images/movie/55834/315x210.jpg?2198", 0, "山口県祝島とスウェーデンを舞台に、エネルギーの未来を切り開こうとする人々の姿を描いたドキュメンタリー。瀬戸内海に浮かぶ小さな島・祝島では、対岸に位置する上関町の原発開発計画への反対運動が28年間にわたって続けられている。暮らしや地域を守ろうとする島民たちの活動を追うとともに、脱原発・脱石油による「持続可能」な社会の実現を目指すスウェーデンでの取り組みを紹介する。監督は「六ヶ所村ラプソディー」の鎌仲ひとみ。", "2011-02-19", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLYING LEATHERNECKS", "太平洋航空作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "実写フィルムを交えてガダルカナル島上陸の対日戦を描いた戦争スペクタクル。厳格な隊長に不満を持つ部下たちだが、戦闘を重ねるうちに、彼の本心を理解して……。1955年の劇場公開時のタイトルは「太平洋作戦」だが1963年リバイバル時に改題された。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("突然炎のごとく", "突然炎のごとく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "井筒和幸による“J・MOVIE・WARS”第２期作品。“ハリウッドにはセックスしかなくてね”というレイモンド・チャンドラーの一節と大差ない日常に、飽和感と飢餓感を覚える田舎街の少女かおり。都会の匂いのする中年男、浩介と知り合い、彼に肉欲だけではない愛情を求めるのだが……。少女のさりげない日常の、微妙でしかも男性には不可解な心の動きが、モノクロームの力強い映像の中に綴られる。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("安居／LIVE　AT　PEACE", "榕樹〈ガジュマル〉の丘へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "中国第四世代に属するフー・ピンリウ監督が手掛け人間ドラマ。一人暮らしの老婆アーシーはガンコ者。息子のトンがお手伝いさんを雇っても次々と追い出してしまう。しかし、気立てのいい田舎娘シャンはくじけない。やがてアーシーとシャンは、アーシーの故郷へ旅をしたことをきっかけに打ち解けていくが……。人間同士の交流、現代の親子関係の崩壊を鋭く見つめ、中国国内の映画賞を独占。", "1997-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("化石の荒野", "化石の荒野", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "終戦直前に行方不明となった5000キロの金塊をめぐって男たちが死闘を繰り広げる角川映画のアクション大作。興行的には今一つだったが、複雑な人間関係の絡みを除けば、かなり楽しめる娯楽アクションになっている。渡瀬と夏八木の格闘シーンなど、見せ場も多い。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROADWAY　MELODY", "ブロードウェイ・メロディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "数年ぶりに会った恋人のハンクや、その妹のクィニーらとともにニューヨークの舞台に立つことになった歌手のエディ。やがてエディはクィニーを愛するようになるが、姉に気がねしたクィニーは、ならず者のジャックに近づいていく。一方、エディの心変わりを知ったハンクは……。ブロードウェイの内側を舞台にした愛憎劇。", "1929-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CATCH−22", "キャッチ22", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "第二次大戦下の地中海の小島を舞台に、戦争に嫌気がさした兵士の行動を描いた傑作ブラック・コメディ。J・ヘラーのベストセラー小説を、ニコルズ監督の「卒業」でも脚本に参加したB・ヘンリーが脚色した。カット・バックの多用など、多少難解な点もあって評価が分かれたが、反戦の心情はよく出ている。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い太陽", "黒い太陽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "明はジャズに取りつかれた“ファンキー族”。ある日、白人二人を射殺して脱走した黒人兵ギルと出会い、脅されて一緒に逃走することに……。山田信夫の脚本を蔵原惟繕が監督した異色アクション。黒人文化に憧れる日本の青年と白人を殺して逃げる黒人青年との奇妙な友情を、ジャズのリズムにのせて描く。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LETTER FROM AN UNKNOWN WOMAN", "忘れじの面影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "1900年のウィーン。決闘を明日に控えた男が、名も知らぬ女性から一通の手紙をもらう。そこには彼に思いを寄せた女のいじらしい恋心が綴られていた……。S・ツバイクの小説の映画化でウィーン情緒に満ちた佳作。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワルボロ", "ワルボロ", "", "", "http://image.eiga.k-img.com/images/movie/34174/315x210.jpg?3958", 109, "ゲッツ板谷の同名小説を映画化した青春ドラマ。80年代の東京・立川を舞台に、不良少年たちの青春の日々を描く。中学3年生のコーちゃんは、授業中に頭突きしてきた幼なじみのヤッコにブチギレして大暴れ。その日からヤンキーに転身し、仲間たちと共に他校とのケンカに明け暮れる毎日を送り始める。主人公コーちゃんに松田翔太、コーちゃんが想いを寄せる女の子・山田に新垣結衣が扮する。", "2007-09-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("童貞物語２ チェリーボーイズ", "童貞物語２ チェリーボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "エッチな青春映画「童貞物語」の第２作。今回はグラビアなどで人気のあった宝生桜子を主演に、彼女が扮する純情高校生・わかなと、“経験”したい男女６人が修学旅行先で繰り広げる騒動を描く。木築沙絵子、速水舞など、花を添える当時のビデオ・アイドルも見もの。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL MIO NOME E´ NESSUNO", "ミスター・ノーボディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "西部の町に引退を決意した一人の老ガンマンがいた。しかし、かつての名ガンマンにとって引退とは誰かに殺されることを意味した。彼の前に“ノーボディ”と名乗る若いガンマンが現れるが……。S・レオーネ原案によるウエスタンのパロディーであり、オマージュであり、温かい人間味に満ちた隠れた名作。", "1975-01-01", "4,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BREACH　OF　FAITH ： A　FAMILY　OF　COPSII", "COP", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「狼よさらば」をはじめとする“デス・ウィッシュ”シリーズで、すっかり復讐の鬼というキャラクターがハマリ役となったC・ブロンソン主演のポリス・アクション。家族全員が警察や司法関係の仕事に就いているファイン家。ロシアン・マフィアによる神父殺害事件を追う一家の長、ポールは警官と弁護士の子供たちを傷つけられ、組織との決戦になだれ込んでいく。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CELLAR", "魔獣伝説 ザ・セラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "150年前、土地を荒らす白人どもを喰い殺す魔獣キャグウェイを呼び寄せる、コマンチ・インディアンの呪いが現代によみがえり、キャグウェイの惨殺が始まるモンスター・ホラー。実際に伝説が残されているキャグウェイのSFXをマックス社が担当。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブ・ストーリーを君に", "ラブ・ストーリーを君に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "白血病に冒された少女と、彼女を温かく見守る青年の純愛物語。古色蒼然たる企画だが、ゴクミの稚拙ながらも生意気ぶりを強調した演技、対照的に仲村トオルに受けの芝居をさせた澤井監督のきめの細かな演出が光る。脇役陣の充実ぶりも相まって、芯の通った感動作になっている。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nineteen", "19", "", "", "http://image.eiga.k-img.com/images/movie/55414/315x210.jpg?2085", 103, "日本人脚本家と韓国のスター俳優がタッグを組む、日韓共同ドラマプロジェクト「テレシネマ7」シリーズ。TVドラマや映画など幅広く活躍する人気脚本家・井上由美子がシナリオを手がけた一本。銀行員の父親や一流大学に通う姉から見下されながら、三流大学に通う19歳の青年ジョンフン。19歳の大財閥の御曹司で、気弱な大学浪人生のミンソ。母の入院費用を工面するため、大学を辞めて美容師見習いとして働く19歳の少女ウンヨン。ある日、3人が通うネットカフェの女店員ヨンエが溺死体で発見され、彼らは重要参考人として取調べを受けることになる。", "2010-05-29", "5,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("More Than a Game", "MORE THAN A GAME モア・ザン・ア・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/56104/315x210.jpg?4247", 102, "NBAのスター選手レブロン・ジェームズら5人の若手バスケットボール選手の成長を追ったドキュメンタリー。米オハイオ州出身のジェームズを中心としたチームの5人は、ともに勝利をつかみ取ることを目標として奮闘してきた。ジェームズは2003年にドラフト1位指名でクリーブランド・キャバリアーズに入団し、最年少でルーキー・オブ・ザ・イヤーも獲得。スター街道を駆け上がっていくが、同時に周囲の環境は大きく変化していく。", "2011-04-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Raining Stones", "レイニング・ストーンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "イギリス労働者階級の人々のシビアな現実を厳しくもあたたかい眼差しで描き続けるケン・ローチ監督によるヒューマン・ドラマ。もうすぐ聖餐式を迎える娘のために新品のドレスを買ってやろうと考えるボブ。しかし彼は失業中で借金も抱え、毎日を暮らしていくのがやっとという有様。彼は金を手に入れるため、羊泥棒からディスコの店員までなりふりかまわず必死に働く。そして式が目前に迫ったある日、彼の元に悪徳高利貸の男が現れ……。", "1993-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILLY WONKA ＆ THE CHOCOLATE FACTORY", "夢のチョコレート工場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "子供にとっての夢、チョコレートがテーマのファンタジー。大人気の“ウイリー・ワンカ・チョコレート”は、謎の工場で作られる。しかし、チョコの中に金のカードを見つけた５人の子供が、工場へ入ることを許された。チョコの池など迷宮仕立ての工場内部をめぐる冒険は、童話のようで実に楽しい。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太平洋の地獄", "太平洋の地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "三船敏郎がアメリカで出演した映画で、国際スターの地位を確立した作品。太平洋戦争末期、太平洋の孤島を舞台に、そこで偶然に出会い、お互い生存のために共同生活をせざるをえなくなる日本兵と米国兵の確執を描く。思想信条も異なる二人の男たちの内に秘めた“静かな戦争”をブアマンがシャープな映像感覚で捉えた異色の戦争映画。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mogera Wogura モゲラウォグラ", "Mogera Wogura モゲラウォグラ", "", "", "http://image.eiga.k-img.com/images/movie/54884/315x210.jpg?2802", 0, "慶應大学と早稲田大学の映画研究会が中心となって制作した異色の群像ドラマ。秘密基地をつくる10歳の少年、ダクト掃除のバイトをする21歳の青年、いつも落とし穴にいるヒロと野球をすることだけが楽しみな26歳の青年、引きこもりの娘との接し方がわからずコインランドリーで時間をつぶす45歳の中年、駐車違反切符を切り続ける65歳の老人。ずっと同じ毎日を繰りかえしてきた彼らが、地震の噂を聞いたことをきっかけにそれぞれ行動を起こしはじめる。", "2009-10-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("想い出のかたすみに", "想い出のかたすみに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "歌と主演作から“ふれあいコンビ”と名付けられた、中村雅俊・檀ふみ主演の第２作。遅咲きの初恋を体験した大学生の男女。だが年上の人妻が現れたことから二人の間に亀裂が生じはじめて……。中村と人妻役・浅丘ルリ子との大胆なベッドシーンが話題になった。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅い眼鏡", "紅い眼鏡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "異色アニメ作家・押井守が監督した初の実写映画。主演に「うる星やつら」のメガネ役で、押井ワールドの代弁者でもあった千葉繁を起用し、他のキャストもアニメの声優たちで固めている。近未来、“地獄の番犬”と恐れられた組織の生き残り・都々目が故国に舞い戻り、キナ臭い陰謀に巻きこまれる。DVDは「押井守シネマ・トリロジー／初期実写作品集」に収録。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COMING TO AMERICA", "星の王子ニューヨークへ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "人気絶頂のE・マーフィ扮する王子様が、花嫁探しに出かけることになるが、運が良いのか悪いのか、行った先は恐ろしい大都会ニューヨーク。そこで理想の女性を見初めて、無事故郷へ帰ることができるのか。歯切れの良いテンポで一気に笑いながら見せてくれるポップなコメディ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魂萌え！", "魂萌え！", "", "", "http://image.eiga.k-img.com/images/movie/33834/315x210.jpg?4807", 125, "「OUT」「グロテスク」などで知られる桐野夏生原作の同名小説を「顔」「亡国のイージス」の阪本順治監督が映画化。突然夫が他界し、途方に暮れている団塊世代の専業主婦・敏子は夫の携帯電話から、夫の愛人の存在を知る。そこに子供たちの身勝手な行動も重なり、自らを取り巻く環境にうんざりした敏子は家出を決行する。第2の人生を歩み出す世間知らずの敏子に風吹ジュン。共演に三田佳子、加藤治子、豊川悦司ら。", "2007-01-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PONG３", "桑の葉３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "韓国で大ヒットした「桑の葉」のシリーズ第３作。宿の主人との賭けに負けた男サムボに、借金のかたとして置き去りにされたアナンネ。ほかの男とは簡単に寝る彼女も、なぜか主人にだけは体を許さない。そうこうするうちにサムボが若い娘を連れて舞い戻り……。", "1992-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SATREE−LEX２", "アタック・ナンバーハーフ２／全員集合！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "日本でも大ヒットを飛ばしたタイ発のスポーツ・コメディの続編。見事に国体優勝を飾り、世間の注目を浴びるオカマのバレーボール・チーム“サトリーレック（鋼鉄の淑女）”から数人のメンバーがライバル・チームに移籍してしまう。かくして大ピンチに直面したオカマちゃんたちのコミカルな奮闘を、彼らが大学時代に出会ったときのエピソードを織り交ぜて描く。", "2002-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沈黙の弾痕 TRUE JUSTICE PART4", "沈黙の弾痕 TRUE JUSTICE PART4", "", "", "http://ecx.images-amazon.com/images/I/613IQ9M4DJL._SL210_.jpg", 90, "大都会シアトルを舞台に、悪に立ちむかう特別捜査班「SIU」の活躍をスティーブン・セガール主演で描いたクライム・アクション・シリーズ「TRUE JUSTICE」の第4弾。選挙を前にしたある日、現職黒人議員の暗殺未遂事件が発生した。捜査に乗り出したSIUのリーダー・ケインは、白人至上主義の対立候補ライアンの関与を疑い、過激な白人至上主義者が集うライアンの施設に部下のラドナーを潜入捜査させるが……。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦上海／THE BETTLE OF SHARGHAI", "上海の戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1949年の上海を舞台に、人民解放軍と国民党軍の戦いを描いた戦争映画。上海解放を目指す解放軍は、1949年５月、上海包囲作戦を開始し圧倒的勝利を収める。敗軍の将、湯雲甫は台湾へと逃げ出し……。蒋介石国民党を描いたことで、文革時に上映禁止処分を受けた作品。監督のワン・ビンも文革中の迫害で死去した。", "1959-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOUBLE DECEPTION", "ダブル・デセプション／共犯者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "菊川怜のアクションをフィーチャーしたヒロイン活劇。日系企業の家族の誘拐事件が続発する、L.A.が舞台。日本人の大富豪オザキの娘リサの替え玉が誘拐され、オザキは身代金を払わないと発表。ところが、今度はリサ本人が誘拐された。それは替え玉の女性を助けようとする元警官ルークが仕組んだ罠。彼と行動をともにしたリサは、いつしか共犯者として危険な道に足を踏み入れ……。", "2000-01-01", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT OF THE TWISTERS", "ツイスター・インフェルノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ダニーはアメリカ中西部の小さな町に住むティーン・エイジャー。スポーツよりも音楽を愛する彼は、元フットボールの花形選手だった義父の期待に応えられず、悶々とした日々を送っていた。そんなある日、季節はずれの巨大なツイスターが町を襲う。家屋は倒壊し、通信手段は断たれ、町はパニック状態に陥ってしまい、ダニーの一家も散り散りになってしまった。ダニーは過酷な状況の中、まだ赤ん坊の弟を守りながら、必死のサバイバルに挑戦する。「ツイスター」人気にあやかってビデオ発売された１本。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伝説巨神イデオン　接触篇", "伝説巨神イデオン　接触篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「機動戦士ガンダム」の富野喜幸（現：由悠季）監督が手がけたTVアニメーションの劇場版。視聴率低迷により39話で打ち切られたTV版をまとめた前編「接触篇」と、本来描かれるはずだった結末を描いた後編「発動篇」の2作が同時公開された。第六文明人の遺跡発掘が進む地球の移民地ソロ星に、伝説の無限エネルギー「イデ」を探し求める異星人バッフ・クランがやってくる。2つの種族は誤解から戦闘状態となり、その混乱のさなか、地球人の青年コスモらは遺跡から発掘した宇宙船「ソロシップ」と巨大ロボット「イデオン」を使って宇宙へ逃れるが……。", "1982-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カリブ・愛のシンフォニー", "カリブ・愛のシンフォニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ファッション・デザイナーと建築家という現代の花形職業に扮した聖子＝正輝が、メキシコの異国情緒の中で愛を語らう話題作。百恵＝友和に負けず劣らずの結婚式を挙げた二人の出会いとなった初共演作。婚約発表した直後の作品だけに、ラブ・シーンなど見どころは少なくない。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("全身小説家", "全身小説家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 157, "小説『地の群れ』などで知られる、作家・井上光晴の生を描く長編ドキュメンタリー。約40年にわたって創作＝小説と格闘し、平成４年５月ガンに散った一人の小説家の“虚構と真実”が、インタビューを中心とする従来通りの記録映画的な部分と、“イメージ篇”と名付けられた一種のドラマを交えて綴られる。「ゆきゆきて、神軍」から７年、約５年の歳月を費やした原一男監督の意欲作で、埴谷雄高、野間宏などの作家が登場するのも興味深い。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アイデン＆ティティ", "アイデン＆ティティ", "", "", "http://image.eiga.k-img.com/images/movie/1444/315x210.jpg?1831", 118, "個性派俳優、田口トモロヲの初監督作。原作はみうらじゅんの自伝的コミック、脚本は売れっ子の宮藤官九郎。舞台はバンドブームが席巻した頃の東京。4人組ロックバンド、SPEED WAYはブームの中でメジャーデビューするが、人気はパッとせず生活も同じ。純粋なロック少年であるギターの中島は、ボーカルのジョニーに売れる曲を書けと言われて反撥、いい曲を書こうとするが書けない。そこにロックの神様、ディランが現れる。", "2003-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO SMALL AFFAIR", "恋人ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "名匠シャッツバーグが描く青春ラブ・コメディ。16歳のカメラ少年チャールズが、年上のロック・シンガーに夢中になり、彼女との初体験を通して大人に成長していく。サンフランシスコ・ロケが何とも美しい作品。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Klute", "コールガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "失踪した科学者の行方を探しているクルート（サザーランド）は、彼の残した手がかりから高級コールガールのブリー（フォンダ）と出会う。クルートは屈折した彼女にいつしか惹かれていき……。ニューヨークを舞台に、コールガールの生態、大都会の裏面を描くサスペンス・ミステリー。この作品でフォンダはアカデミー賞主演女優賞を獲得した。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実験4号 It's a small world", "実験4号 It's a small world", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 41, "ロックバンド「The ピーズ」の曲「実験4号」をモチーフに、「天然コケッコー」の山下敦弘監督が撮りあげた短編映画。温暖化により火星への移住が進んだ100年後の地球を舞台に、火星へ旅立つことになった小学生と2人の親友が過ごす最後の2日間を描く。人気作家・伊坂幸太郎とのコラボレーション企画として制作された作品で、伊坂による短編小説「実験4号　後藤を待ちながら」と場所や登場人物がリンクしている。", "2008-03-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お早よう", "お早よう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "松竹の伝統である下町喜劇、長屋喜劇の舞台を郊外の建売住宅地に移した、小津のユーモアセンスの冴えわたった傑作。10軒ほどの家が建ち並ぶ住宅地に住む人々ののどかな日々をスケッチ風に描写。小津安二郎が多くとり上げた、親子、兄弟、夫婦間の葛藤というテーマはここではそれほど深く堀り下げられることはなく、タイトルのように日常のあいさつや平凡な会話を通し、庶民の生活がいきいきと浮かんでくる。「生れてはみたけれど」を思わせる兄弟が登場して、いたずらしたり、父親にテレビをねだったり……。最後にテレビを買ってもらった弟が唐突にまわすフラフープのおかしさ！", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("КОНЁК−ГОРБУНОК", "せむしの仔馬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "ソビエト・アニメーションの創始者ともいわれているI・イワノフ・ワノ監督の代表的な作品。1947年の自作をリメイクしたもので、おなじみの童話『金色の馬』『イワンの馬鹿』『火の鳥』などを巧みな構成でまとめ上げ、欲深い王様に利用されながらも幸せをつかむ少年の姿を、王朝名画を思わせる映像美で描いていく。", "1975-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mission: Impossible - Ghost Protocol", "ミッション：インポッシブル　ゴースト・プロトコル", "", "", "http://image.eiga.k-img.com/images/movie/55154/315x210.jpg?4767", 135, "トム・クルーズ主演の人気スパイアクションシリーズ第4作。ロシアのクレムリンで爆破事件が発生し、米国極秘諜報組織IMFのエージェント、イーサン・ハントと、ハントの率いるチームが事件の容疑者にされてしまう。米国政府は「ゴースト・プロトコル」を発令してIMFを抹消。汚名を着せられたハントは、IMFの後ろ盾もえられないままチームの仲間だけを頼りにクレムリン爆破の犯人をつきとめ、さらには事件の黒幕が目論む核弾頭によるテロを防ぐためロシアからドバイ、インドへとわたり、過酷な戦いに身を投じる。前作を監督したJ・J・エイブラムスが製作を担当。ピクサーで「Mr.インクレディブル」「レミーのおいしいレストラン」を手がけてきたブラッド・バード監督が、初の実写映画のメガホンをとった。", "2011-12-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("熊の八ッ切り事件", "熊の八ッ切り事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 33, "親の遺産をめぐって繰り広げられる奇想天外な珍騒動を絶妙のテンポで描いたドタバタ喜劇の傑作。病床の老人が子供たちを集めて遺言するなか、戒めを言う。“一本の矢はすぐに折れてしまうが、三本合わせれば折れない。それ故、兄弟力を合わせて商売に励め”。ところが、莫大な保険金の証書を目にした長男はびっくり仰天、一同の眼前で束ねた数本の矢を思わずポキリと折ってしまい……。たたみかける様に連発する突拍子もないギャグの数々。“喜劇の王様”と呼ばれた斎藤寅次郎監督のスラップスティック精神がみなぎる抱腹絶倒のナンセンス映画。", "1932-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("能楽師　伝承", "能楽師　伝承", "", "", "http://image.eiga.k-img.com/images/movie/56484/315x210.jpg?1539", 94, "観世流能楽師・関根祥人を中心に、父・祥六、息子・祥丸との稽古風景や、能「舎利」「道成寺」「松風」の舞台を記録。能の伝承についてカメラを回したドキュメンタリー。祥人と俳優・佐野史郎、民俗学者・田中英機との鼎談（ていだん）も収められ、「道成寺」を7度演じることになった祥人の心境にも迫る。監督は「能楽師」「みやび　三島由紀夫」などのドキュメンタリーで知られる田中千世子。", "2011-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PLAIN SMAN", "シャイアン砦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ワイルド・ビル・ヒコック、カラミティ・ジェーン、そしてはバッファロー・ビルなど、西部の伝説的英雄が登場するウエスタン。警備隊とシャイアン族との戦いをメインに、ワイルド・ビル・ヒコックとカラミティ・ジェーンとの恋を絡めつつ物語は進行する。監督は「母の旅路」などで知られる、職人肌のD・L・リッチ。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真赤な海が呼んでるぜ", "真赤な海が呼んでるぜ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "この年、宍戸錠主演の「あばれ騎士道」でデビューした渡哲也の主演第２作。商船大学を卒業したての主人公が、密輸組織に利用されている兄を救うために、同じ船に乗り込み真相を探り始める。後年“無頼”シリーズなどで渡哲也とコンビを組む松原智恵子が初共演。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A BRONX TALE", "ブロンクス物語　愛につつまれた街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "1960年代のブロンクス。バスの運転手の息子カジェロは人種間のいざこざが絶えない町で、黒人少女との恋やマフィアのボスらとの交流を通して一歩づつ大人になっていく……。R・デ・ニーロが初めてメガホンを執り、ニューヨークのストリート・シーンを鮮烈に描く。全編に流れるノスタルジックな1960年代音楽も心地良い。ビデオ発売時タイトルは「ブロンクス物語」。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAFFIN", "孤独のヒーロー タフィン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "アイルランドの田舎町で浮上した化学工場の建設計画。その計画に反対する人々は、町議会と企業の黒い陰謀になす術もなかった。そんな中、町中の嫌われ者タフィンが立ち上がり、一人で戦いを挑んでいく。ダーティー・ヒーローの活躍を描くアクション。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あなたがすきです、だいすきです", "あなたがすきです、だいすきです", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "高知に住む大学生ユウは同性愛者。シンというれっきとした恋人がいながら、駅で会う青年タカに思わず愛の告白をしてしまったことから波紋が……。専門の劇場で上映されるいわゆる“薔薇族映画”だが、「ターチ・トリップ」で知られる自主映画作家、大木裕之の35ミリ商業映画第１作として海外の映画祭で注目された。即興演出と徹底した同時録音による異色の劇映画で、大木監督の面目躍如たる快作となった。英題は「I like you,I like you very much」。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("制覇", "制覇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "暴力団、都城組の組長を狙った狙撃事件が発生。この事件で妻を亡くした組長の次男、恵地はカタギから組に戻ることを決意し、好戦派の長男、勇地を押さえて組長代理となるが……。ヤクザ一家の骨肉の争いを、スリリングに描いた硬派アクション。覇権を求めて陰謀を張り巡らし、血縁者をも裏切る、極道社会の非情を浮かび上がらせている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAGDAD CAFE´", "バグダッド・カフェ", "", "", "http://ecx.images-amazon.com/images/I/417M0VF93TL._SL210_.jpg", 108, "ラスベガスとロサンゼルスを結ぶハイウェイの途中、モハーベ砂漠の中にひっそりとたたずんでいるオンボロのモーテル、バグダッド・カフェ。旅行中の夫と喧嘩をし、そこへふらりとやって来たのはドイツ女のジャスミン。最初は白い目で見ていた住人たちもいつの間にか彼女に親しみを感じるようになる。ジャスミンは他人の心を和ませ、その場を明るくし、店を繁盛させる不思議な女だった。最後までかたくなな態度をとっていた女主人ブレンダも打ち解け、これからという時に……。P・アドロンが“アメリカらしい光景”を乾いた感覚で映し出している。テーマ曲『コーリング・ユー』もストーリーにマッチ。ジャスミン役のM・ゼーゲブレヒトの印象は強烈。", "1987-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OH！DREAMLAND", "五月—夢の国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "1980年５月に韓国・光州で起きた、市民の民主化闘争に対する政府軍による空前絶後の大弾圧、いわゆる“光州事件”を背景に、韓国の若い映画人らがゲリラ的に製作した作品。政府軍との衝突を目前に光州市民軍から逃亡した主人公と、彼の潜伏先である米軍基地の街に住む娼婦やチンピラたちが織り成す青春群像を描く。技法や演出が稚拙であることは否めないが、何かを伝えなければという作り手側の熱意が全編にみなぎっている。", "1988-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hot Chick", "ホット・チック", "", "", "http://ecx.images-amazon.com/images/I/41MMCXS2B5L._SL210_.jpg", 104, "呪われたイヤリングによって中年男と身体が入れ替わってしまった女子高生が巻き起こす騒動を描いたコメディ。高校のチアリーダー部に所属するジェシカは、ショッピングモールでアンティークのイヤリングを手に入れる。しかし片方をガソリンスタンドで落としてしまい、後から来た強盗のクライブがそれを拾う。翌朝、ジェシカは自分がクライブの姿になっていることに気づき……。主演は「アニマルマン」のロブ・シュナイダーと「最終絶叫計画」のアンナ・ファリス。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Vicere", "副王家の一族", "", "", "http://image.eiga.k-img.com/images/movie/54724/315x210.jpg?4236", 126, "イタリアへの統一を目前に控えた19世紀半ばのシチリア。名門貴族ウゼダ家では、封建的な父ジャコモと嫡男のコンサルボが激しく対立していた。遺産相続のため兄弟を追放し、娘に政略結婚を強要するなど自己利益を優先させるジャコモに、コンサルボは憎悪を募らせていく。原作はフェデリコ・デ・ロベルトの「副王たち」。監督は「鯨の中のジョナ」のロベルト・ファエンツァ。", "2009-11-07", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラーゼフォン・多元変奏曲", "ラーゼフォン・多元変奏曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "同名TVアニメ・シリーズに新場面を30分以上追加し、画面も音声も新たにリミックスした劇場版。時空のズレによって12年を失った恋人同士の再会を、近未来ロボットのバトルを背景に描き出す。2017年、異次元からの来訪者“MU”によって東京は隔離された。対MU戦略研究機関“TERRA”の紫東遥は、東京に残された高校生、神名綾人を救出しようとするが、それには深い理由があった。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Am So Proud of You", "あなたは私の誇り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 22, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花と蛇", "花と蛇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "SM小説の大家・団鬼六と緊縛の女王・谷ナオミの名コンビに加え、脚本・田中陽造と監督・小沼勝の鬼才コンビが結集。一流会社勤務の誠は、娼婦だった母を抱いた黒人兵を射殺して以来、緊縛写真でしか勃起しない。ある日、社長から性交を拒む夫人の教育を命じられ、ベッドに縛りつけるのだが、次第に夫人は恍惚の表情を浮かべ……。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和残侠伝・一匹狼", "昭和残侠伝・一匹狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "武井繁次郎にとって桂木竜三は親分の仇だった。繁次郎は銚子にほど近い港町に潮政親分の娘を連れ帰る。潮政は水揚げの独占をもくろむ川銀一家とにらみ合っていた。折しも桂木は川銀の客分となり、繁次郎を斬るように命じられるが……。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("団鬼六 少女縛り絵図", "団鬼六 少女縛り絵図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "大学教師の峰岸は自分の学問にしか興味を示さず、妻からも軽蔑されている。ある日、自殺しようとした少女を助けた峰岸は、廃屋のアパートに彼女を囲い、異常セックスにのめり込んでいく。少女とのSMプレイが非日常的な男を日常へと連れ戻すという、凝った趣向のロマンポルノ。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("台北異想", "台北24時", "", "", "http://image.eiga.k-img.com/images/movie/54974/315x210.jpg?4602", 94, "大都会・台北での24時間を8分割し、8つの物語を8人の監督がリレー形式で連作したオムニバス。「午後3時の初恋」のチェン・フェンフェン、「僕の恋、彼の秘密」のD・J・チェンら気鋭の若手監督が、それぞれの個性を競い合う。俳優のリー・カンションがメガホンを取った午前4時のコーヒーショップのエピソードには、「愛情萬歳」「河」などで知られる台湾映画界の巨匠ツァイ・ミンリャンが俳優として登場。", "2009-10-19", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLIND TERROR", "見えない恐怖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「ミクロの決死圏」などのフライシャー監督によるサスペンス。叔父と同居している盲目の娘が、ある日、家に帰ると人の気配がない。風呂に入ろうと、湯舟に手を入れると、そこには叔父の死体が……。巨匠バーンスタインの音楽がスリリングな効果を高めている。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("余命1ヶ月の花嫁", "余命1ヶ月の花嫁", "", "", "http://image.eiga.k-img.com/images/movie/53964/315x210.jpg?1746", 129, "2007年、がんのため24歳6カ月で生涯を閉じた女性の最後の1カ月間をカメラに収めて話題となったTBS系ドキュメンタリー番組「余命1ヶ月の花嫁／乳がんと闘った24歳　最後のメッセージ」を映画化。主演は榮倉奈々と瑛太。監督は「ヴァイブレータ」の廣木隆一。乳がんに冒され、余命1カ月を宣告された千恵の夢は「ウェディングドレスを着ること」。その夢を叶えようと、友人たちは彼女の恋人・太郎とともに結婚式の準備を進める。", "2009-05-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("We Own the Night", "アンダーカヴァー", "", "", "http://image.eiga.k-img.com/images/movie/34264/315x210.jpg?1632", 117, "ホアキン・フェニックスとマーク・ウォールバーグの主演作「裏切り者」を手がけたジェームズ・グレイ監督が、再び2人を起用して描くクライム・サスペンス。1988年のニューヨーク、マフィアと繋がるナイトクラブで働く弟ボビー（フェニックス）と、父の跡を継ぎエリート警官の道を歩む兄ジョセフ（ウォールバーグ）。ある日、麻薬組織の一斉検挙のため警察がボビーのナイトクラブに踏み込み、兄弟間の溝は深まるばかり。そして、その晩にジョセフが何者かに襲われ……。", "2008-12-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GARWOOD：PRISONER OF WAR", "プリズナー・オブ・ウォー 栄光なき帰還", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ベトナム最前線、一等兵ロバートはベトコンの捕虜となり強制収容される。やがて新たに連行されてきた捕虜アイクの“敵を欺くには敵の言葉を覚えろ”の教えに従いベトナム語を彼に習う。おかげでロバートは特別待遇となり、やがて終戦。しかし14年ぶりに祖国の土を踏んだ彼を、アメリカは温かく迎えてはくれなかった……。今も抑留されているというアメリカ人捕虜たちの実態を描く問題作。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　さらば仮面ライダー電王　ファイナル・カウントダウン ", "劇場版　さらば仮面ライダー電王　ファイナル・カウントダウン ", "", "", "http://image.eiga.k-img.com/images/movie/53994/315x210.jpg?4403", 0, "2008年1月の放送終了後も根強い人気を誇る「仮面ライダー電王」の最後の戦いを描く劇場版第3弾。世間で噂の幽霊列車について調査を開始したモモタロスたちの前に、“仮面ライダー幽汽”が立ちはだかる。なんとその正体は、誘拐された電王・良太郎だった。モモタロスや新たに電王となった幸太郎らは、良太郎の言葉を手がかりにデンライナーで300年前へと向かう。新電王・幸太郎を演じるのは「アクエリアンエイジ　劇場版」の桜田通。", "2008-10-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SONNY", "SONNY／ソニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "個性派俳優ニコラス・ケイジが初めてメガホンを執った意欲作。1980年代のニューオーリンズを舞台に男娼と娼婦の純愛を描き、若者の夢と絶望を紡ぎ出した。女性たちを魅了する容姿と男娼としての才能には満ち溢れているけれど、愛には不器用な主人公ソニーに扮したJ・フランコは、その色気と無邪気さで若者の脆さを絶妙に表現している。ニューオーリンズの歓楽街を歩く軍服の青年。彼はかつてその美貌と才能で大金を稼いだ伝説の男娼ソニーだった。娼館を営む母は復帰を促すが、ソニーは足を洗う決意を固めていた。そんな彼に母は新入り娼婦のキャロルを紹介する。ビデオ、DVDタイトルは「SONNY／ソニー　欲望のニューオリンズ」。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SINK THE BISMARCK！", "ビスマルク号を撃沈せよ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "不沈を誇るナチスの快速戦艦撃破を命ぜられた、英国海軍将校の武勇伝。1941年春、相次ぐ作戦失敗に、時の首相チャーチルもついに主力艦の投入を発令。空母アークロイヤルほかが、続々と北大西洋に配備された。その頃、妻を戦災で失った作戦参謀シェパード大佐は、今また息子の戦闘中行方不明の報を聞くが……。L・ギルバート監督の職人芸が発揮された戦記映画の佳編。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍びの衆", "忍びの衆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "雷蔵の死後、“眼狂四郎”“若親分”と彼の人気シリーズを受け継いだ松方弘樹の“忍びの者”。司馬遼太郎の『伊賀の四鬼』を原作に、秀吉から信長の妹で柴田勝家の側室となったお市の方を誘拐するよう命じられた伊賀者たちの死闘を描く劇画タッチの作品。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ただひとたびの人", "ただひとたびの人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ライブに飛び入りして尺八を演奏している波彦は、家賃も払えないほどの極貧の生活を送っていた。一度は叔父の工場で働き始めたものの、尺八を捨て切れずに再び上京するが、アパートを追い出され、恋人にも去られてしまう。金もなくなり、東京をさまよう波彦だったが彼にはまだ尺八があった。「グッドバイ」の加藤哲が、尺八奏者の生活をドキュメンタリー・タッチで描いた作品。トリノ国際映画祭で審査員特別賞を受賞。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vidocq", "ヴィドック", "", "", "http://image.eiga.k-img.com/images/movie/1734/315x210.jpg?1731", 98, "監督ピトフは、フランスのデジタル映像のパイオニア。CMやクリップを経て、ジュネ＆キャロ監督の「デリカテッセン」の特殊効果を手がけ、同監督の「エイリアン4」で第2班監督を担当、本作で監督デビュー。本作の主人公ヴィドックは、犯罪者から警察の密偵を経て、私立探偵になったフランスの伝説的ヒーロー。19世紀のパリを舞台に、ヴィドックの死の謎を追う作家が、連続殺人を続ける「鏡の顔を持つ男」と出会う。", "2002-01-12", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガール", "ガール", "", "", "http://image.eiga.k-img.com/images/movie/56764/315x210.jpg?3451", 124, "奥田英朗の人気小説を、香里奈、麻生久美子、吉瀬美智子、板谷由夏の主演で映画化。大手広告代理店勤務の滝川由紀子（香里奈）、不動産会社勤務の武田聖子（麻生）、老舗文具メーカー勤務の小坂容子（吉瀬）、自動車メーカー勤務の平井孝子（板谷）という職業も取り巻く環境も異なる4人の女性が、それぞれの悩みを抱えながらも懸命に自分と向き合い、女性としての人生を謳歌しようとする姿を描く。メガホンをとるのは、「白夜行」「神様のカルテ」の俊英・深川栄洋。", "2012-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDER SUSPICION", "アンダー・サスピション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ある殺人事件の真相をめぐり、警察署長と容疑者の駆け引きを描くサスペンス・ミステリー。ジーン・ハックマン、モーガン・フリーマンという実力派同士の火花散る演技合戦が見ものだ。プエルトリコで少女レイプ殺人事件が起こり、著名な弁護士ハーストが容疑者として浮上する。捜査を担当する警察署長ベネゼーは、曖昧な供述を繰り返すハーストと、彼の美貌の妻シャンタルへの粘り強い尋問を行うが……。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("James Dean Story", "ジェイムス・ディーン物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "　ジェームズ・ディーンの死の2年後に製作されたドキュメンタリー。貴重な記録からジミーの短すぎた人生を振り返る。「エデンの東」「理由なき反抗」の特別シーンや、家族、友人へのインタビュー、そしてデニス・ストックら有名カメラマンが提供したスチール写真やフィルムによって構成されている。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'assaut", "フランス特殊部隊GIGN エールフランス8969便ハイジャック事件", "", "", "http://image.eiga.k-img.com/images/movie/57184/315x210.jpg?1868", 91, "フランスの対テロ特殊部隊「フランス国家憲兵隊治安介入部隊」（通称：GIGN）の活躍を描くアクションサスペンス。1994年12月24日、アルジェリア・アルジェで武装イスラム集団（GIA）が仏パリのオルリー空港行きエールフランス8969便をハイジャック。自宅軟禁されていたイスラム救国戦線（FIS）の指導者の釈放を要求する。事件解決のためへファビエ将軍率いるGIGNが出動するが、犯人グループの本当の目的はハイジャックした機体をエッフェル塔に激突させ、大規模なテロを引き起こすことだった。実際に起こった事件をもとに、当時にニュース映像などを交えながらリアリティ満載に描く。", "2011-11-12", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本侠客伝 関東篇", "日本侠客伝 関東篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "築地・魚河岸の老舗問屋“江戸一”は父の亡きあと、男勝りの娘・栄が切り回していた。風来坊の船乗り・緒方勇は、ふとしたことから江戸一で働くことになる。ところが、協同組合の理事長が魚市場を牛耳っていたため、江戸一の商売は思わしくなかった。江戸一が引き受けた外国との取引も、郷田の横槍で失敗に終わった。度重なる郷田の妨害に、勇らの怒りはつのり、河岸を舞台に大乱闘が繰り広げられる。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AND THEN THERE WERE NONE", "そして誰もいなくなった", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "クリスティ原作の推理小説の再映画化。舞台は、砂漠にそびえる古代の宮殿。ここに見知らぬ10人の男女が何者かに招かれてやって来る。やがて一人、また一人と連続殺人事件が始まる……。リードをはじめ豪華キャストによる華麗なミステリー映画。", "1974-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初夜と蓮根", "初夜と蓮根", "", "", "http://image.eiga.k-img.com/images/movie/57944/315x210.jpg?2291", 0, "2012年・第4回沖縄国際映画祭の長編プログラム「Laugh」部門にて上映。", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Frida", "フリーダ", "", "", "http://image.eiga.k-img.com/images/movie/1064/315x210.jpg?3225", 123, "メキシコを代表する女性画家フリーダ・カーロの波乱に満ちた生涯ををサルマ・ハエックが主演、プロデュースを務め8年の歳月をかけて映画化。第75回アカデミー賞で主演女優賞など6部門にノミネートされ、作曲賞とメイクアップ賞を受賞した。夫ディエゴ役には「ショコラ」のアルフレッド・モリーナ、監督は「タイタン」のジュリー・テイモア。ロックフェラー役のエドワード・ノートンはノンクレジットだが脚本にも参加している。", "2002-08-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三聯花", "三聯花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "再婚した母を嫌って東京の恩師のもとに身を寄せる若き医学士・矢野静一と、単身上京してダンサーとして暮らしを立てる妹・みどりとの偶然の再会を軸に、結婚や恋愛、親子の愛情などを描いたメロドラマ。新興キネマの現代劇の形を作り上げた田中重雄が監督。若手俳優たちによる演劇グループ“青空倶楽部”のメンバーが発剌とした演技を見せる。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PENSION MIMOSAS", "ミモザ館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "南フランスの海岸町。ホテル“ミモザ館”の女主人ルイズは、実子同然に育てたピエールが、パリで自堕落な生活をしているのを気に病み連れ戻すが……。子として、そして一人の男としてピエールを愛するルイズの心の機微を、緻密な演出で映し出した名作。", "1935-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOWN BY LAW", "ダウン・バイ・ロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "デビュー作以来の常連ジョン・ルーリーに、監督が敬愛するミュージシャンのトム・ウェイツ、“イタリアのウディ・アレン”ロベルト・ベニーニを迎えたジャームッシュ監督の第3作。刑務所で同房になった3人の男が奇妙な友情で結ばれ、やがて脱獄してそれぞれの道を歩み始める(？)までを独特のユーモアで描く。ウェイツが担当した音楽、ロビー・ミューラーのモノクロ撮影も話題になった人気作。", "1986-11-22", "1,45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wild at Heart", "ワイルド・アット・ハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "鬼才デビッド・リンチ監督がニコラス・ケイジ主演で放つバイオレンスなラブストーリー。ある日、恋人ルーラの目の前で襲いかかってきた男を返り討ちにし、殺してしまったセイラー。その男は、娘に異常なほどの愛情を持つルーラの母マリエッタが送り込んだ刺客だった。2年間の刑期を終え出所したセイラーは、ルーラを連れてカリフォルニアへと旅に出る。マリエッタは再び彼らの元に2人の男を送るが……。カンヌ国際映画祭グランプリ受賞。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白馬城の花嫁", "白馬城の花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "美空ひばりが気心の知れた沢島忠監督のもとで、天衣無縫に演じたミュージカル仕立ての時代劇コメディ。白馬城のほとり、いつか若殿様が娘を迎えに来るという伝説を信じるお君のもとに、二人の家来を連れた若殿が本当に現れる。が、彼らは実はおたずね者の大泥棒が変装していたのだった。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラマン", "ウルトラマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "TVの人気シリーズ『ウルトラマン』を劇場用に再編集したもの。科学特捜隊のハヤタは垂直離着陸機でパトロール中、M78星雲の宇宙船と衝突した。その時、ハヤタはウルトラマンの生命を与えられる。次々と登場する凶暴な怪獣たちを相手に、ハヤタはウルトラマンに変身し、正義のために闘うのだった。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DETROIT ROCK CITY", "デトロイト・ロック・シティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1970年代後期のデトロイト近郊を舞台に、時の人気バンド、KISSを観ようと奔走するロック少年たちの奮闘を描いた青春コメディ。デトロイトでKISSの公演が行われることになり、ハード・ロック大好きの高校生４人組は大張り切り。ところが、KISSを悪魔の音楽と決めつける母親にチケットを燃やされてしまった。あきらめきれない４人はラジオの懸賞でチケットを獲得し、意気揚々とデトロイトに向かうが、そこでさらなる受難が……。バカバカしいほどの失敗を呼んでしまうティーンの熱意が、コミカルであると同時に共感を呼び、痛快なクライマックスまで大いに楽しませてくれる。ラストには本物のKISSも登場し、熱狂のライブ・ステージを披露。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Love Song for Bobby Long", "ママの遺したラヴソング", "", "", "http://image.eiga.k-img.com/images/movie/33914/315x210.jpg?3096", 120, "孤独を抱える少女が、母の死をきっかけに戻った故郷での出来事を通し癒されていく姿を、スカーレット・ヨハンソン主演で描いた感動作。疎遠だった母の訃報を聞き故郷のニューオーリンズに帰ったパーシー。しかし彼女の実家には、元大学文学部教授のボビー・ロングと作家志望のローソンという2人の見知らぬ男がいた。彼らは母の友人で、遺言によると家は3人に住む権利があるという。パーシーは不満ながらも彼らとの共同生活を始める。", "2007-04-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("剣聖 暁の三十六番斬り", "剣聖 暁の三十六番斬り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "柳生流指南、荒木又右衛門の有名な“三十六人斬り”を描いた時代劇。柳生十兵衛から柳生新陰流の秘伝を授かり、江戸へ旅立った又右衛門。途中危機を救った渡辺靭負の住居に旅装を解くが、靭負が日ごろ江戸で乱暴を働いていた河合又五郎に斬られたことから、その子、数馬とともに仇討ちの旅に立つ。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まむしの兄弟・二人合わせて30犯", "まむしの兄弟・二人合わせて30犯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "勝は莫大な資産を持つ老女・弥生の息子で、３歳の時に別れ別れになったとわかり、彼は弥生と涙の対面をする。しかし、これが加賀組が弥生の財産目当てに仕組んだ芝居だと判明。まむしの兄弟は、加賀組の事務所へ殴り込む。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SNOWS OF KILIMANJARO", "キリマンジャロの雪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "アフリカ一の高山キリマンジャロのふもとで、死にかけた小説家が自分の半生を回想する。美しいモデル、シンシアとの恋とその破局、そして彼女の死。シンシアに似た未亡人との結婚……。やがて、奇跡的に助かった彼は都会へと戻っていく。当時、映画化がブームだったヘミングウェイの小説を忠実に、テクニカラーもあざやかに映像化。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("激情版　エリートヤンキー三郎", "激情版　エリートヤンキー三郎", "", "", "http://image.eiga.k-img.com/images/movie/53864/315x210.jpg?4397", 106, "極悪ヤンキーを兄に持つ内気な青年・三郎の受難を描いた、阿部秀司の同名ギャグ漫画を映画化。監督は「地獄甲子園」の山口雄大、主演に「うた魂♪」の石黒英雄、共演にお笑い芸人インパルスの板倉俊之、「梟の城」の橋本じゅんほか。ヤンキーの巣窟・徳丸学園高校に大河内三郎が入学してくると、教師たちは目をつけ、普通の生徒は目もあわせてくれなかった。実は三郎の兄達はかつて学園を統治・支配していた総長だったのだ……。", "2009-02-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("上海帰りのリル", "上海帰りのリル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "原作は雑誌『婦人倶楽部』に連載された同名の小説で、タイトルは有名なヒット歌謡曲に由来している。監督は戦前に喜劇や、「風の又三郎」などの児童映画に才能を発揮した島耕二。戦前の上海。クリフサイド・クラブのダンサー、竹本リルに想いを寄せる男の話。このあと、続編とも言うべき「風の噂のリル」も作られた。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BREAKING−IN", "ブレイキング・イン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "B・フォーサイス監督が描く、親子ほども年の違う泥棒同士の友情。コソ泥のマイクと、プロの泥棒アーニーは、ある日同じ家に侵入してハチ合わせ。これが縁でマイクはアーニーの相棒に。そして、二人で大仕事を計画するが……。かつてのアクション・スター、B・レイノルズが、初老の泥棒を抑制の効いた演技で好演、新境地を見せた。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fear and Loathing in Las Vegas", "ラスベガスをやっつけろ", "", "", "http://ecx.images-amazon.com/images/I/61GC959B27L._SL210_.jpg", 0, "アメリカのジャーナリスト、ハンター・S・トンプソンが1971年に発表した同名小説を、「未来世紀ブラジル」の鬼才テリー・ギリアムが映像化した異色ロードムービー。71年、ジャーナリストのラウル・デュークと弁護士ドクター・ゴンゾーはバギーレースを取材するため、真っ赤なスポーツカーに大量のドラッグを詰めこんでラスベガスへと向かう。しかし、ドラッグまみれの2人は行く先々で騒動を巻き起こし……。", "1999-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤穂浪士", "赤穂浪士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 151, "戦後に作られた“忠臣蔵”のなかでも、秀作の呼び声が高い作品。新藤兼人の脚本は、大仏次郎が創作した堀田隼人の目を通して、浅野方と吉良方を見る形をとり、新鮮な視点が注目を浴びた。市川右太衛門の大石内蔵助は、いささか重々しいが好演。東映で、３本の“忠臣蔵”を撮った松田定次監督は、この作品が最も気に入っているという。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARATHON", "マラソン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "自閉症の青年が、マラソンを通して成長していく姿を描く感動作。絶え間ない苦労を重ねながら愛情を注ぐ母親や、青年と絆を育むコーチとの交流を、時に切なく、時にユーモラスに綴っていく。他人と意思の疎通が図れない青年が、走ることで輝いていく姿には、誰もが心を動かされるはず。自閉症の障害を持つ19歳のチョウォンは、走ることが大好き。彼は母や元マラソン選手のコーチに支えられながら大会出場を決意する。実話の映画化で、韓国では公開直後から社会現象を巻き起こした。", "2005-07-02", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海から来た流れ者", "海から来た流れ者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "大島に流れてきた男が、土建業者同士の対立抗争に巻き込まれていく。“渡り鳥”“流れ者”を通して、小林旭演じる主人公と奇妙な友情で結ばれるライバルに扮した宍戸錠は、ここではまだ完全な悪役。この二人は、このあとコンビとしての面白さのボルテージを上げていく。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLAMENCO", "フラメンコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "C・サウラが、フラメンコの代表的な曲を、装飾と意図的な演出を排除してフィルムに収めたドキュメンタリー。ギターのパコ・デ・ルシア、天才バイオラール（男性舞踏家）のホアキン・コルテスらスペインを代表するフラメンコ・スターたちが大挙出演し、全編、フラメンコが踊られていく。ファンだけではなく、その迫力ある映像には誰もが圧倒されるはずである。", "1995-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Our Family Wedding", "マイファミリー・ウェディング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ロサンゼルスを舞台に、アフリカン・アメリカンの一家の息子と、メキシコ出身のヒスパニック系の一家の娘が結婚式を挙げるまでのドタバタを描いた群像コメディ。大学を卒業したばかりのマーカスとルーシアは、お互いの両親との食事会で結婚を宣言する。だが、マーカスの父ブラッドとルーシアの父ミゲルの2人は、その日、街でちょっとした悶着を起こしていた。出演はフォレスト・ウィテカー、アメリカ・フェレーラ、レジーナ・キングほか。監督は「ソウル・メイト」のリック・ファミュイワ。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOO MUCH", "ロボフレンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "父の仕事で日本に滞在するアメリカの少女の孤独を慰めるために、日本の天才エンジニアが作った万能ロボット。帰国の時が来ても少女は彼と別れられず、一緒に逃げてしまう。それを追う警察と悪徳科学者たち。夢と冒険がいっぱいの感動編。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘密戦隊ゴレンジャー　青い大要塞", "秘密戦隊ゴレンジャー　青い大要塞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "人気特撮テレビシリーズ「秘密戦隊ゴレンジャー」の劇場版第2作。テレビ版の第15話「青い大要塞！大暴れバリブルーン」に再編集を施し、「東映まんがまつり」の1本として劇場公開。強力なバリアーを搭載した“虹の要塞”を完成させた虹仮面。しかし、アフリカ戦線で大勝利を収めた日輪仮面が帰還し、虹の要塞が上からの攻撃に弱いことを指摘する。そこで虹仮面と日輪仮面は、ゴレンジャーの飛行戦艦バリブルーンの強奪に乗り出す。", "1975-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMOTION 伝説の午後＝いつか見たドラキュラ", "EMOTION 伝説の午後＝いつか見たドラキュラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 37, "映像の魔術師の異名をとる大林宣彦の16ミリ自主映画。ロジェ・ヴァディムの吸血鬼映画「血とバラ」に捧げられ、映画に対する作者の熱い想いを、青春の日々への訣別をこめて綴っている。コマ撮りや大胆な字幕の使用、怪奇映画や西部劇のパロディなどが秀逸。なお、ビデオ＆DVDは「大林宣彦 青春回顧録」に収録。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極楽島物語", "極楽島物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "菊田一夫による同名の東宝ミュージカルを、喜劇陣が大挙出演して映画化。太平洋戦争中、元サギ師の柏原一等兵らを乗せた輸送船が、南方に向かう途中、撃沈される。兵隊８名と上杉中佐は、極楽島へと流れ着き、原地の人々との交流が始まるが……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANOTHER DAY IN PARADISE", "アナザー・デイ・イン・パラダイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「KIDS」で衝撃的な監督デビューを果たした写真家L・クラークの第２作。犯罪の深みにはまっていく少年少女と中年カップルの姿を描く暴力と愛に満ちたロード・ムービーだ。麻薬に溺れ、盗みで生計を立てる16歳のボビーは、ある日出会った中年男性メルに見込まれ行動をともにすることになる。それぞれの恋人を連れて旅に出た彼らは、麻薬取引で大金を奪おうと画策。メルを父親のように慕い、刺激的な毎日に喜びを感じるボビーだが、やがて恋人が妊娠してしまい……。犯罪にまみれた日々の中で生きる希望を模索する主人公をV・カーシーザーが繊細に演じる。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HABITUAL SADNESS", "ナヌムの家II", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "戦時中、強制的に従軍慰安婦として働かされていた女性たちを追ったドキュメンタリーの第２作。ソウル市内から車で３時間ほどの田園地帯に移った“ナヌムの家”。畑を耕し、鶏を飼いながら共同生活を送る７人のハルモニ（老婆）たちの日常風景や、仲間の死を悼む彼女たちの素顔を、スタッフとの交流を交えながら写し出す。悲しい過去を乗り越え、社会の偏見に立ち向かう彼女たちの前向きな姿が、観る者に深い感動を与える。", "1997-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪盗まだら蜘蛛", "怪盗まだら蜘蛛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "懐かしの二枚目俳優・黒川弥太郎主演による捕物時代劇。亡き父母の仇討ちのため、悪逆非道な明石藩主の命を狙う義賊・まだら蜘蛛。だが彼の名をかたる残虐な偽者が現れて、江戸の町は大混乱に……。ご存じの名奉行・遠山金四郎に扮した坂東好太郎、霧立のぼるなど助演陣にも注目。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大誘拐 Rainbow Kids", "大誘拐 Rainbow Kids", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "1979年に日本推理作家協会賞を受賞した天藤真の同名小説を、奇才・岡本喜八が映画化した痛快犯罪コメディ。３人の若者に誘拐された大富豪の老婆が、いつの間にかその誘拐犯のリーダーとなり、家族に100億円の身代金を要求。一方、その老婆を自らの恩人と慕う警部は、そんな実情も知らず捜査を開始。警察と老婆の頭脳戦が展開される。主人公の老婆には、岡本喜八がこの人以外にはないとほれ込んだ北林谷栄が、人間味あふれる好演を見せ、共演の緒形拳、樹木希林、嶋田久作らもユニークな持ち味を発揮した快演を見せている。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Equinox", "堕ちた恋人たちへ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "　30年前の出生時に生き別れ、それ以来、互いの存在も知らないまま別々の人生を歩んできた双子の兄弟がいた。ひとりは、養父とともに自動車工場を営んでいるシャイな青年ヘンリー。そしてもうひとりは、悪の手先となっている冷酷な野心家フレディ。会うはずのなかったふたりはついに再会することに……。運命にもてあそばれる双子の兄弟を描いた切なくもほろ苦いドラマ。マシュー・モディーンが全く異なるキャラクターの双子を演じる。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROMAN POLANSKI’S PIRATES", "ポランスキーのパイレーツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "鬼才ロマン・ポランスキーが製作費約40億円を費やした海洋スペクタクル巨編。1986年のカンヌ映画祭で話題を呼んだ１作。元大海賊のレッド船長と部下カエルは、漂流中に通りがかりのスペイン船に救われる。だが船荷に膨大な量の財宝があることを知ったレッドは、船の乗っ取りを計画する……。黄金の財宝をめぐり、スペインの豪華帆船の上で、海の男たちが戦いを繰り広げる。剣と剣が火花を散らす、スリル満載のアドベンチャー大作。", "1986-01-01", "2,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Imagine That", "エディ・マーフィの劇的1週間", "", "", "http://ecx.images-amazon.com/images/I/512GRR5rXRL._SL210_.jpg", 107, "エディ・マーフィ主演のコメディ。優秀な金融プランナーのエバンは、別居中の妻と交代で6歳になる娘オリビアの面倒を見ている。しかしオリビアは、お気に入りの毛布を頭からかぶって空想上の友達と話しているため、学校でも問題になっていた。そんなオリビアとどう接すればよいのか分からないエバンだったが、オリビアが空想の友達の助言で投資の予想を的中させたことで、親子の絆が一気に深まった。", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少年犯／INNOCENT TEEN AGER", "少年犯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "実際に少年鑑別所にカメラを持ち込み、本物の少年犯たちが出演したことで話題となった作品。女性雑誌記者の謝潔心は少年犯罪の実態調査のため鑑別所を訪れ、少年犯たちの生活や非行の背景をフォローし始める。だがその間に、彼女の息子も非行に走り始め……。", "1985-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　仮面ライダーディケイド　オールライダー対大ショッカー", "劇場版　仮面ライダーディケイド　オールライダー対大ショッカー", "", "", "http://image.eiga.k-img.com/images/movie/54394/315x210.jpg?3304", 0, "大人気TVシリーズ「仮面ライダーディケイド」の劇場版。平成・昭和の歴代仮面ライダー26人が集結し、悪の組織・大ショッカーと大迫力のバトルを繰り広げる。とある世界で自分を兄と呼ぶ少女に出会った門矢士は、その世界こそが自分のいた世界なのではないかと考える。そこでも滅びの現象が起こりはじめており、それを防ぐために最強のライダーを決めなければならないと知った士は、ライダー同士の熾烈な戦いに身を投じるが……。", "2009-08-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hell is for Heroes", "突撃隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "　1944年、ドイツ・ジークフリート線で待機中の米軍部隊に、上官に背いたかどで降格させられた兵士リースが配属される。やがてリースたちの隊に出動命令が。彼らは援軍が到着するまでわずかな戦力で陣地を守らなくてはならなかった。その後、ドイツ軍の襲撃に応戦したものの、兵を取り逃がしてしまう。リースは敵に奇襲を仕掛けることを主張するが……。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("American Pastime", "アメリカンパスタイム　俺たちの星条旗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "第2次世界大戦中、アメリカの日系人強制収容所に実在した野球チームをモデルに、不当な扱いを受けながらも力強く生き抜こうとする日系アメリカ人の姿を綴った感動作。ユタ州の強制収容所に入れられたノムラ一家。野球が得意な次男ライルは、大リーガーになることを夢見る看守ビリーの娘と恋に落ちる。2人の仲を認めようとしないビリーに対し、ライルは野球で勝負を挑む。出演は中村雅俊、ジュディ・オング、「ザ・リング2」のゲイリー・コールほか。", "2007-05-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カメレオン", "カメレオン", "", "", "http://image.eiga.k-img.com/images/movie/53264/315x210.jpg?4537", 97, "詐欺グループの一員である伍郎は、ある日偶然、数人の屈強な男たちが一人の男を拉致するのを目撃する。後に伍郎は連れ去られた人物がある事件の重要参考人であることを知るが、すでに謎の組織が伍郞たちの命を狙っていた……。松田優作の“遊戯シリーズ”第2弾として企画されたがお蔵入りになった丸山昇一の脚本を現代風にアレンジし、阪本順治監督＆藤原竜也主演で映画化したクライム・アクション。", "2008-07-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FORBIDDEN PLANET", "禁断の惑星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「2001年宇宙の旅」以前のSF映画のエポック作品。惑星アルティア４に、超光速宇宙船が着陸。乗員の前に、消息を絶った調査隊の生き残り、モービアス博士と娘のアルティア、そして万能ロボットのロビーが現れた。博士は、先住民族のクレール人の怒りにふれると忠告。そのとき、見えない怪物に隊員の一人が殺される。この怪物は博士の潜在意識がクレール人の機械によって具体化した“イドの怪物”だった。怪物が現れると、地面に足跡だけがつき、階段がひとりでにたわむのはスリリング。また、バリアに触れ、ライオンに似た怪物の姿が浮かぶディズニーによるアニメ合成も素晴らしい。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄男 THE BULLET MAN", "鉄男 THE BULLET MAN", "", "", "http://image.eiga.k-img.com/images/movie/54774/315x210.jpg?2895", 71, "世界中にカルト的ファンの多い塚本晋也監督が、自身の代表作「鉄男」（89）を全く新しいストーリーで再構築したアクション・スリラー。東京に暮らす平凡なサラリーマンのアンソニーは、日本人の妻ゆり子と息子トムと幸せな生活を送っていた。しかし、ある日突然トムが何者かに殺されてしまう。事件の謎を追ううちに、アンソニーは解剖学者だった父が関与していた「TETSUO PROJECT」という研究へたどり着く。すると、怒りに我を失ったアンソニーの身体から蒸気と黒いオイルが噴出し、鋼鉄の塊へと変貌してしまう。", "2010-05-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いきすだま", "いきすだま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "ささやななえこのコミックを映画化したオカルト・ホラー。韓国でも活躍する兄弟ミュージシャン、DOGGY BAGが主演し、三輪ひとみ、明日美姉妹が共演している。思いを寄せるあまりに生霊と化した女子高生が、相手に付きまとう「生霊いきすだま」、呪われたマンションに引っ越してきたヒロインの恐怖を描く「空ほ石の…」の２話構成。ビデオタイトルは「いきすだま　生霊」。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版・ハートの国のアリス Wonderful Wonder World", "劇場版・ハートの国のアリス Wonderful Wonder World", "", "", "http://image.eiga.k-img.com/images/movie/56194/315x210.jpg?3793", 86, "名作童話「不思議の国のアリス」をモチーフにした恋愛アドベンチャーゲームを劇場アニメーション化。現実主義で夢や恋愛に興味のない少女アリス・リデルは、ふとしたことから物騒なハートの国に引き込まれてしまう。建物はメルヘン調なのに住人は危険人物ばかりで、乱闘騒ぎや殺し合いが頻発。帽子屋、ハートの女王、ゴーランドによる三つどもえの領地争いも勃発し、おかしな薬を飲まされてしまったアリスは、元の世界に戻るために行動を開始するが……。", "2011-07-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ボディ・ジャック", "ボディ・ジャック", "", "", "http://image.eiga.k-img.com/images/movie/53954/315x210.jpg?3116", 93, "元学生運動の闘士であるテツは広告代理店に勤める中年コピーライターで、冷え切った家庭で毎晩酒浸りの生活を送っている。そんなある日、酔っぱらいなどに幽霊がオーバーラップして見えるようになり、テツは自分が土佐弁の侍の霊にボディ・ジャック（憑依）されていることに気付く。その霊は誰かを探しているらしく、テツは強制的に捜索に協力させられるが……。 主演は「ハッシュ！」「歩いても　歩いても」の高橋和也。", "2008-10-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE ROI ET L'OISEAU", "王と鳥〈デジタルリマスター版〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "宮崎駿と高畑勲という日本が世界に誇るアニメ界の両巨匠が、自らの作品への影響を公言するフランスの傑作アニメーション。独裁者の国王を主人公に据え、社会を風刺したブラックなストーリーが展開していく。字幕翻訳は高畑勲が担当した。気に入らない者は即刻殺してしまうほど残忍で国民に嫌われる王。そんな王だが、絵の中に描かれた美しい娘に恋をする。ところが、その娘が同じ絵の中の青年と王室から逃げ出してしまい……。", "1980-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JANIS　ET　JOHN", "歌え！ジャニス・ジョプリンのように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "中年の危機をロックに乗せて描いたフレンチ・コメディ。金に困った保険マンが、ニセ者の故ジャニス・ジョプリンとジョン・レノンを仕立て上げ、彼らの大ファンである従弟から遺産を横取りしようとする。しかしジャニスを演じた彼の妻は、必要以上にそれにのめり込み……。さえない中年女性からいきいきと輝いていく女性へと華麗な変身を遂げるヒロインをM・トランティニャンが熱演。ジャニスが乗り移ったかのような彼女の熱唱シーンは必見だ。惜しくもこの作品が彼女の遺作となった。", "2003-01-01", "2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("傾城之戀／LOVE IN A FALLEN CITY", "傾城の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "第二次大戦開戦直後の上海。離婚して実家に戻った旧家の養女スーは、妹の見合いに同席し、イギリス帰りの青年ファンと出会う。ファンは妹よりスーに関心を示し、二人に愛が芽生えるが……。香港ニューウェイブの旗手アン・ホイが壮麗な映像美で描くラブ・ストーリー。", "1984-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道めし", "極道めし", "", "", "http://image.eiga.k-img.com/images/movie/55964/315x210.jpg?4412", 108, "グルメ漫画家として知られる土山しげる（協力・大西祥平）の人気作品を、「ブタがいた教室」「陽気なギャングが地球を回す」の前田哲監督が映画化。大みそかの刑務所を舞台に、受刑者たちがおせち料理の争奪をかけて“自らの経験したうまいもん自慢”のバトルを繰り広げる姿を描く。バトルの内容は、うまいもん自慢の話にはまってつばを飲み込み、喉をゴクリと鳴らした人数が最も多い者が、全員からおせち料理を1品ずつもらえるという子どもじみたゲーム。「あのオムライスが忘れられない」などと思い入れたっぷりに語るこわもての男たちを演じるのは、「愛のむきだし」の永岡佑ほか、勝村政信、落合モトキ、ぎたろー、麿赤兒ら。", "2011-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIT！", "黄金のプロジェクター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "麻薬で死んだ娘の復讐と密売組織の撲滅のため、麻薬秘密捜査官ニックは、元パルチザンの警官、射撃の名手、良家の出の高級娼婦など６人の仲間を集め、９人のボスを一人一人消していく。「スター・ウォーズ／帝国の逆襲」でおなじみB・D・ウィリアムズ主演の小粋なアクション。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Old Yeller", "黄色い老犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ウォルト・ディズニー製作総指揮の下、イギリス出身の名匠ロバート・スティーブンソンがメガホンを取ったヒューマン・ドラマ。テキサス州で農場を営むコーツ一家。ある日彼らは、畑を荒らしに来た老犬を末子アーリスの遊び相手として飼うことにする。イエラーと名付けられた老犬とアーリス少年は次第に心を通わせ始め、老犬は一家にとってかけがえのない存在になっていく。ところが、イエラーの本当の飼い主の男性が現れ……。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JADEH−HAYEH SARD", "凍った道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "教師のモサビは病気の父親のために薬を取りに行く生徒を手伝い一緒に町へ行く。厳しい吹雪が吹いていたうえ、町への道のりはとても危険だったので、一人の若者が同行してくれることになる。しかし町からの帰り、彼らは腹をすかした狼の群れに囲まれてしまうのだった。", "1986-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST HARD MEN", "大いなる決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "かつては鬼保安官と呼ばれていたサムも今は年をとり、引退を間近に控えている。娘と二人で静かに隠居生活を送ろうと考える彼のもとに、かつてサムが刑務所に送った強盗犯ザックが脱獄し、復讐するために戻って来る……。J・コバーンの屈折した混血児の悪人ぶりが印象的な本格派ウエスタン。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母を慕いて", "母を慕いて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "昭和を代表するビッグ歌手の美空ひばりが十代の時、歌だけでなく演技に開眼した一作。当時大ヒットを飛ばしていた田端義夫ことバタヤンら歌手陣が特別出演している。親子の絆、家族愛を描いたヒューマン・ドラマ。育ての親の元を離れ、父と離婚した京都の母のところへ引き取られた娘だが、別の家庭を持つ東京の父が、別れたしょく罪から娘を引き受けた。娘の気持ちは？", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旭山動物園物語　ペンギンが空をとぶ", "旭山動物園物語　ペンギンが空をとぶ", "", "", "http://image.eiga.k-img.com/images/movie/53454/315x210.jpg?2885", 112, "幾度もの閉園の危機を乗り越え、年間300万人の入場者数を誇る人気スポットとなった北海道・旭山動物園のサクセスストーリーを、実在の滝沢園長らをモデルに映画化。監督は「次郎長三国志」のマキノ雅彦、主演は「釣りバカ日誌」シリーズの西田敏行。動物への優しさと情熱で溢れる滝沢園長のもとに、人付き合いの苦手な吉田が飼育係としてやってくる。しかし動物園は客足を確保できず、閉園の危機に面していた……。", "2009-02-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RANBU／艶舞剣士", "RANBU／艶舞剣士", "", "", "http://ecx.images-amazon.com/images/I/51lonFz-BaL._SL210_.jpg", 86, "2004年のゆうばり国際ファンタスティック映画祭で好評を博した、時代劇アクション。それぞれに暗い過去を持つ日本舞踏の踊り子３人が、艶舞剣士となって闇組織の党首に戦いを挑む。グラビア・アイドルの小向美奈子が美しき復讐の剣士を艶やかに演じる。盗賊に父を殺された日本舞踊の踊り手、小百合。同じく暗い過去を背負う二人の踊り子とともに彼女は、尼僧から剣の訓練を受けることに。", "2004-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天下の御意見番", "天下の御意見番", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "外様大名と旗本が年始の挨拶に登城する正月二日、それぞれの行列が先を争って大喧嘩を始める。ところが３人の老中が大名びいきだったため、旗本側だけが切腹という片手落ちの断が下される。名物男一心太助から事情を聞いた大久保彦左衛門は、命を懸けて大名と旗本の確執を取り除こうとするが……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かたつもり", "かたつもり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "女性監督、河瀬直美が、養母でもある祖母との関係を８ミリ・カメラで見つめた私的ドキュメンタリー。奈良県で畑仕事に精を出す、おばあちゃんの日常を愛情にあふれた視点で捉えていく。何気ない生活風景の描写の落ち着いた味わいは、この後に河瀬が撮り上げる「萌の朱雀」へと受け継がれていく。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("島田洋七の佐賀のがばいばあちゃん", "島田洋七の佐賀のがばいばあちゃん", "", "", "http://image.eiga.k-img.com/images/movie/53734/315x210.jpg?3442", 112, "漫才師や作家としても活躍する島田洋七が、自身の原作「佐賀のがばいばあちゃん」を映画化。昭和33年、居酒屋で働く母子家庭で育った昭広は小学校に入学すると、超がつくほど貧乏なばあちゃんの家に預けられてしまう。初めは不慣れな田舎暮らしに戸惑う昭広だったが、生きる知恵とトンチのきいた明るいばあちゃんに育てられ、すくすくと成長してゆく。", "2009-04-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dom zly", "ダーク・ハウス　暗い家", "", "", "http://image.eiga.k-img.com/images/movie/54954/315x210.jpg?1741", 109, "ポーランドの新鋭ボイテク・スマルゾフスキ監督によるクライム・サスペンス。1978年秋、ひとりの男が人里離れた農家を訪れて一夜を過ごすが、その晩にとある事件が起こる。それから4年後の冬、戒厳令下のポーランドで同事件を担当することになったムルズ警部補は、事件関係者を立ち会わせて現場検証を行うが……。事件発生と現場検証という2つの時間を並行して描くことで、登場人物たちの複雑な心理を巧妙に浮かび上がらせていく。", "2009-10-17", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アイノカラダ", "アイノカラダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "新人、村上なほが、いまを生きる女性たちのエピソード５編を連作風に綴った監督デビュー作。主演の６人の女優がディスカッションを通してストーリーを形作り、新人カメラマンの戸惑い、地味なOLの秘密、芽の出ない女優の苦悩、威勢のいいダンサーの心の空白、そして別れを前にしたレズ・カップルのすれ違いを、リンクさせながら描き出す。職業カメラマンのミズキは、画廊の主人から一枚だけ撮るように注文される。自分の本当に撮りたいものは彼女だったが、シャッターをおろすことができなかった。同じ頃、売れない女優のメグミは、過度のダイエットで倒れ、病院に運ばれる。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴力", "暴力", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "大阪の歓楽街で、打算的にその日暮らしを続ける浮浪者やスリ、万引き、ムショあがりの人間たち。そうしたなかでポン引きをして暮らす孝子という女の力強い生き方を描いていく。松竹を退社した吉村が、脚本の新藤と近代映協を設立した時期の作品。横丁を徘徊する狂気の詩人の絶叫する詩が萩原朔太郎の作品というのが、興をそそる。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CURSE OF THE WEREWOLF", "吸血狼男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "狼男の伝説を映画化したハマー・プロ作品。変質者の侯爵によって狂乱状態に陥れられた娘は、侯爵を殺し、森の湖で溺れているところを町の学者らに救われる。だが娘はクリスマスの晩に男の子を生んで死亡した。その子はレオンと名付けられ可愛く育てられたが、彼の体の中には狼の精霊が宿っていた。", "1960-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺し屋1", "殺し屋1", "", "", "http://image.eiga.k-img.com/images/movie/1484/315x210.jpg?2905", 128, "原作は山本英夫による全10巻の人気長編コミック。浅野忠信の垣原役ほか原作と同じキャラを、映画監督でもある塚本晋也、SABU、松尾スズキら個性派キャストが演じて映画化。監督は新作ラッシュが続く三池崇史。舞台は新宿歌舞伎町。全身金髪、顔中キズだらけの、暴力に魅せられたヤクザ・垣原と、彼の率いる組を壊滅しようとする謎の男・ジジイ、そしてジジイが操る泣き虫の殺し屋・殺し屋1の熾烈な戦いが繰り広げられる。", "2001-12-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cruel Winter Blues", "熱血男児", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「シルミド SILMIDO」のソル・ギョング、「連理の枝」のチョ・ハンソン共演によるクライムアクション。少年院時代から行動を共にしてきたヤクザのジェムンとミンジェ。ある日、2人は組織からある人物の殺害を命じられるが、誤って別人を殺し、その報復によってミンジェが命を落としてしまう。ミンジェを殺した男デシクへの復讐を誓ったジェムンは、弟分のチグクを連れ、デシクの母チョムシムが経営する食堂に出入りするようになるが……。", "2008-08-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大安に仏滅!?", "大安に仏滅!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "53歳の平凡なサラリーマン・哲夫は念願のマイホームを手に入れたばかり。田舎の父親を呼び寄せ、長女・真由の結婚の準備も着々と進み、すべて順調だった。ところが、マイホームが欠陥住宅であることが発覚。床が斜めになり、トイレのドアが開かなくなる!?　さらに、真由が結婚をやめると言い出し家族は崩壊の危機に……。和泉聖治が「お日柄もよくご愁傷さま」に次いで家族の悲喜こもごもを、欠陥住宅の現状を織り交ぜながら軽妙に綴るホームドラマ。次々と降りかかる災難にオロオロし、奮闘する父親を橋爪功が好演。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("燃ゆる大空", "燃ゆる大空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "日中戦争における97戦闘機の活躍を描いた航空映画。陸軍省後援のもと、実機900機あまりが画面狭しと飛び交う。その迫力は「トップガン」どころではない。翼にカメラを据えつけた宮島義勇の撮影が見事だ。しかし、ラスト“天皇陛下万歳”のセリフがもれるあたりは、戦意高揚映画の宿命でもあろう。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わたしのグランパ", "わたしのグランパ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「ボクの、おじさん」の東陽一監督が、筒井康隆の同名小説を映画化。かつて殺人を犯した祖父と心を通わせた孫娘の成長を描く。正義感の人一倍強い女子中学生の珠子。その彼女の家に、親友の仇であるヤクザを殺した祖父の謙三が刑期を終えて戻ってくる。家族はこれに複雑な面持ちだが、珠子は真摯な謙三に親近感を抱く。名優、菅原文太が久々にアクション・シーンにも挑み、祖父役を好演。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("るろうに剣心　明治剣客浪漫譚　新京都編　前編", "るろうに剣心　明治剣客浪漫譚　新京都編　前編", "", "", "http://image.eiga.k-img.com/images/movie/57094/315x210.jpg?4620", 0, "和月伸宏の人気コミック「るろうに剣心」のアニメ化15周年を記念した全2巻のOVA「新京都編」を映画館で上映。明治11年夏、大久保内務卿が暗殺される事件が発生。かつて人斬り抜刀斎の後継者と目されながらも、その存在が危険視され新政府により殺されたはずの男・志々雄真実（ししおまこと）の関与が浮かび上がる。志々雄が明治政府転覆を企んでいることを知った剣心は、その謀略を食い止めるため京都へと旅立つ。原作でも人気のエピソード「京都編」を新たな解釈で描く。古橋一浩監督ほか、メインキャストの涼風真世、藤谷美紀、冨永みーならTVアニメ版のスタッフ・声優陣が集う。", "2011-12-17", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちいさこべ", "ちいさこべ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 170, "職人気質で仕事に打ち込む大工の茂次が、他人のために無償の世話を続ける近所の女から大きな影響を受けていく姿を描いたもの。山本周五郎が1952年に発表した同名短編小説の映画化。茂次役の中村錦之助は本作で第２回日本映画記者協会最優秀男優賞を受賞、その評価を不動のものとした。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEVIL IN THE FLESH", "肉体の悪魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1923年に発表され、世界的にセンセーショナルを巻き起こしたR・ラディゲの原作をベースに、舞台をオーストラリア、時代を第二次大戦中に設定。原作とは違った風土、社会の中で、変わらぬ愛情ドラマを描き出している。ドキュメンタリー出身のS・マレー監督、主演の二人、ともにこの作品で映画デビューを果たす。", "1985-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’HOMME AU MASQUE D’OR", "グランマスクの男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "孤児たちを育てるためにプロレスラーとして働く神父ビクトールと、風来坊のペドロ。彼らが協力して、“子供の国”を作ろうとする様をほのぼのと描く。メキシコの実在の神父をモデルに描かれるさわやかなドラマ。「グレート・ブルー」で兄弟役を演じたJ・レノとM・デュレが再共演し、味のある演技を見せる。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Extreme Prejudice", "ダブルボーダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　テキサス国境沿いの町に特殊工作部隊が潜入。彼らの目的はキャッシュ率いる麻薬密輸組織の壊滅だった。一方、キャッシュと国境警備にあたるテキサス・レンジャーのジャックは、子供時代こそ友人同士だったが、今ではジャックの恋人サリタをはさみ、複雑な関係にあった。そんな中、ジャックの相棒ハンクが殺され、さらにサリタはキャッシュに拉致される。やがて男たちは激突の時を迎えることに……。硬派ウォルター・ヒル監督とタカ派ジョン・ミリアスが組んだウェスタン・アクション。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЗАМРИ−УМРИ−ВОСКРЕСНИ！／FREEZE−DIE，AND REVIVE", "動くな、死ね、甦れ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "53歳のV・カネフスキーによる長編処女作品。第二次世界大戦直後の極東ロシアの小さな炭坑町を舞台に、母親への反発や淡い恋に揺れる12才の少年ワレルカと、少女ガーリャの感情の行方を追う。収容所地帯と化したシベリアの炭坑町で、大人たちが喪失感と絶望の日常をやり過ごしていくなか、子供たちが周囲に波及させる感情が実に痛々しい。", "1989-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("終極忍者", "忍者", "", "", "http://image.eiga.k-img.com/images/movie/33904/315x210.jpg?1265", 95, "人気K-1ファイター、魔裟斗主演による忍者アクション。全人類をコントロールできるワクチンを封じ込めた箱を巡り、甲賀流や伊賀流をはじめとした忍者たちが激闘を繰り広げる。共演は2007年度ミスインターナショナル日本代表に選ばれた白田久子、「カンフーハッスル」のホァン・シェンイーほか。これまで多くのジャッキー・チェン作品に参加してきたアクション監督ニッキー・リーによる本格アクションは必見。", "2007-03-31", "76,6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LATIN　BOYS　GO　TO　HELL", "ラテン・ボーイズ・ゴー・トゥー・ヘル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "キューバ生まれでニューヨーク在住の映画作家E・トロヤーノが、ラテン社会の日常とアンダーグラウンドなカルチャー・シーンをリアルに描いた愛憎ドラマ。ポップスターを夢みる12歳の少年ジャスティンが、突然同居することになったいとこのエンジェルに恋心を抱く。しかしジャスティンが一夜をともにしたセクシーなゲイ・モデルが何者かに惨殺され、彼らは危うい事件に巻き込まれていく。", "1997-01-01", "1,7,76,3,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GO¨STA BERLINGS SAGA", "イェスタ・ベルリングの伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "伝説の大女優G・ガルボが、初めてその名で銀幕に登場したスウェーデンの無声映画。それまで辺境の駆け出し女優にすぎなかった彼女は、まさにこの作品によって“発見”され、ドイツ映画「喜びなき街」を経てハリウッドで大きく花開くことになる。19世紀末のスウェーデンのベルムランド地方。ここで牧師を辞して無為の生活を送るイェスタ・ベルリング青年を軸に、富裕階級の女たち、男たちの愛憎渦巻く人間模様を描く。華やかな室内シーンと雪の屋外との対比など、繊細な雰囲気描写が見事な文芸大作である。渡米中にガルボの愛人と報道されたこともある、M・スティルレル監督の代表作の一つ。", "1924-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLICE ACADEMY ５：ASSIGNMENT MIAMI BEACH", "ポリスアカデミー５ マイアミ特別勤務", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "またもや勢ぞろいのポリ・アカ笑激軍団。今回の任地はマイアミ・ビーチ。アカデミーの校長が永年勤続で表彰されることになったのだ。そのため卒業生一同や、校長のあとがまを狙うハリス一派もマイアミに集結。加えてダイヤモンド強盗一味まで加わり大混戦に。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXTREME OPS", "EX〈エックス〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "危険を顧みずに大自然を疾走する男女とテロ・グループの攻防を描く冒険映画。エクストリーム・スポーツ界から招いたスタントマンたちが披露する超人的アクションが観る者を興奮させる。CM制作会社のクルーが、ド迫力のアクション映像を求めてアルプスの雪山を訪れる。しかし彼らは武装テロリスト軍団と遭遇してしまい、死と背中合わせの逃亡を繰り広げるハメになる。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・カラテ", "ザ・カラテ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "B級アクションの雄・野田幸男監督による一編。日本で開催される世界武道大会に出場するために、20年ぶりに帰国した空手の達人・山下タダシは、次々と難敵を叩きつぶしていく。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("微熱少年", "微熱少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "1960年代後半の風俗を背景に、音楽に生きる少年の青春を、松本隆が同名の私小説を自ら映画化して描く。斉藤由貴の実弟、斉藤隆治の初主演作で、ナイーブな少年を好演。松本隆自身の音楽と相まってノスタルジックな世界を作り上げた。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("罪と罰　ドタマかちわったろかの巻", "罪と罰　ドタマかちわったろかの巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "万年ヒラ刑事・狭間は警察本部長に、裏ビデオに出ている愛娘の保護を依頼される。彼女を追ううちに、狭間の前に意外な難事件が姿を現してくる……。早撮り低予算の刑事アクションながら、走るだけが得意というパンティー泥棒専門の中年刑事を間寛平がひょうひょうと演じて味がある。拳銃もろくに扱えぬ彼のでたらめな撃ち方に、ツワモノぞろいの敵が怯えて腰が引けてしまうというアクション演出は、井筒監督ならでは。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRIVATE SCHOOL", "プライベイト・スクール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ヤング・アイドル、P・ケイツ主演のコミカルな学園ドラマ。女子高生ケイツは、近くの男子校に通うジムをめぐって、学園きってのセクシー・ガールと恋のサヤ当てゲームを繰り広げる。先生役のS・クリステルさえタジタジとなる、ケイツの性に対する大胆さが痛快。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PIXOTE", "ピショット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "H・バベンコが「蜘蛛女のキス」と「黄昏に燃えて」に先立ち、撮り上げた出世作である。題材は、ブラジルの都市のスラムに何の身寄りもなく、貧困生活を余儀なくされている子供たちが300万人以上もいるという厳しい現実。そんな子供たちの一人、10歳のピショットを主人公に、暴力が渦巻く感化院での悲惨な日々、そして脱走して仲間たちと汚い犯罪に手を染めていく姿をリアルに描写。殺人、レイプなど衝撃的なシーンも随所に映し出されている。", "1980-01-01", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A.I. Artificial Intelligence", "A.I.", "", "", "http://image.eiga.k-img.com/images/movie/1004/315x210.jpg?4178", 146, "テクノロジーが天文学的なペースで発達した近未来。人間は“感情”以外の面において万能なロボットを召使いとする生活を送っていた。そんなとき、サイバートロニクス社は世界で初めて“愛する”ことをインプットしたロボットの少年デイビッドを作り上げる。彼は試験的なケースとしてサイバートロニクスの従業員夫妻の養子となるが、やがて予期せぬ状況の連続で生活を続けられなくなり、デイビッドは家を出る。", "2001-06-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STENDHAL SYNDROME", "スタンダール シンドローム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "若き婦人警官のアンナは、連続猟奇レイプ殺人事件の調査のためにフィレンツェへ向かう。潜入捜査の最中、彼女は世界的に有名なウフィッツイ美術館に立ち寄る。そしてそこで一枚の絵を見たことで、彼女に思わぬ異変が起こってしまう。絵の中に吸い込まれそうな幻想に襲われ、失神し記憶喪失になってしまうという、“スタンダール症候群”にかかったのだ。やがてアンナは、捜査中の事件の犯人に捕らえられ、暴力的なレイプを受ける。さらに犯人は、彼女の目の前で殺人を犯し逃走。追い詰められるアンナ。事件はいつしか幻想と狂気の深淵へと突き進んでいく。イタリアン・ホラーの巨匠D・アルジェント監督によるサイコ・スリラー。", "1996-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Novem", "ノヴェム", "", "", "http://image.eiga.k-img.com/images/movie/53084/315x210.jpg?1873", 87, "わずか6日間しか存在しなかった幻のバンド“NOVEM”を巡る物語をドキュメンタリータッチで描いた音楽ドラマ。1973年、アメリカのインディアナポリスで、9人の大学生が結成したバンド“NOVEM”がキャンプを行っていた。それぞれの想いを詰め込んだ9曲をレコーディングして帰途についた彼らだったが、その途中で事故に遭いメンバー全員が亡くなってしまう。2004年、ある大学生がガレージセールでNOVEMの音源を手に入れ……。", "2007-11-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jamaica Inn", "巌窟の野獣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　ヒッチコックがたびたび原作に起用している女流作家ダフネ・デュ・モーリア作の「埋もれた青春」を、エンターテイメント性たっぷりの冒険活劇に仕上げた。母を亡くしたメアリーは、叔母を頼りに宿屋ジャマイカ亭にやってくる。しかしそこは、略奪品に満ちた海賊たちのアジトだった。オスカー俳優チャールズ・ロートンが主演とプロデューサーを兼ね、「静かなる男」などでのちにハリウッドで成功をおさめたモーリン・オハラがメアリーを演じている。", "1952-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY NAME IS JOE", "マイ・ネーム・イズ・ジョー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "労働者階級の日常を見つめた、鬼才K・ローチの人間ドラマ。37歳の失業者ジョーはアルコール中毒を断ち、素人サッカー・チームの監督をしている。ある日、福祉職員のセーラと出会った彼は心を通わせ、やがて恋を実らせる。だが、甥っ子夫婦がギャングに借金をしていたことから、ジョーは甥を救うために麻薬の運び屋を引き受け、それを知ったセーラは彼のもとを去った。信じられるものをなくしたジョーを、さらなる悲劇が襲う……。住む世界の異なる男女の哀切なラブ・ストーリーのなかに、道徳感を歪ませる劣悪な社会環境を浮び上がらせた意欲作。生活感あふれるリアルな描写にローチの才気がうかがえる。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しいのみ学園", "しいのみ学園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "小児マヒの子供を二人も抱え、全財産をなげうって小児マヒ児童のための学園を創立した主人公と、そこに通う児童の交流を描いた作品。よくある難病ものの日本映画とは異なり、きわめて明るくのびのびとした、さわやかな仕上がりになっている。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("息子の青春", "息子の青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 44, "小説家・越智英夫は『息子の青春』という小説を書こうとしている。18歳の長男が髪をのばしたり、女友だちとつきあい始めたりしたことがきっかけだった。そんな時、次男が不良の友人の巻きぞえで、警察に保護検束される……。林房雄の家庭小説を映画化した小林正樹の処女作。地味な小品ながら、青春や親子愛をみずみずしく描いている。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CROOKED HEARTS", "ねじれた家族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "アメリカ東部に住むウォーレン一家は、夫と妻と３人の息子、一人の娘が喜怒哀楽をともに分かち合う、一見幸福そうな普通の一家であった。だが子供たちが成長するにつれて、家族の歯車が微妙に狂ってくる。P・コヨーテをはじめ、個性的な俳優が不可思議で、パワフルでサスペンスフルな世界を演じる。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泪橋", "泪橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "江戸時代に鈴ヶ森刑場に向かう罪人が、縁者と今生の別れを惜しんだという泪橋を舞台に、今日の男女の出会いと別れを描いた作品。村松友視と唐十郎の個性が生んだ異色作で、愛川欽也の娘・佳村萠がこの作品でスクリーン・デビューした。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大怪獣空中戦・ガメラ対ギャオス", "大怪獣空中戦・ガメラ対ギャオス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "毎年夏と冬に怪獣映画を公開する東宝との直接対決を避けて春休みに公開することになった“ガメラ”シリーズ。富士火山帯の活動で出現したギャオスは、名古屋を火の海にするが、炎を好むガメラが現れ、陸・海・空を舞台に死闘が展開される。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lennonyc", "ジョン・レノン，ニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/56504/315x210.jpg?3503", 120, "「ザ・ビートルズ」のジョン・レノンと、レノンがニューヨークで暮らした1970年代にフォーカスをあてたドキュメンタリー。レノンは、ベトナム戦争下で反戦運動が広がりをみせていた1971年9月にニューヨークへ移住。80年12月に凶弾に倒れるまで9年間を同地で過ごした。オノ・ヨーコの全面的な協力・監修のものと、本人のインタビュー映像やアルバム収録曲の未発表テイクやデモトラック、ヨーコへの最新インタビュー、秘蔵映像を盛り込む。", "2011-08-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL PLEUT SUR SANTIAGO", "サンチャゴに雨が降る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1970年から1974年にかけてチリで繰り広げられた、人民連合と軍部反動派の血みどろの争いをドキュメンタリー・タッチで描く。チリから亡命したソトー監督のもと、フランス映画界のスターが結集。ビデオ発売時タイトルは「特攻要塞都市」。", "1975-01-01", "2,65", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅路 村でいちばんの首吊りの木", "旅路 村でいちばんの首吊りの木", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "奥飛騨に住む母親と、東京で東大受験を目指す娘の手紙のやりとりの中で、不可解な殺人事件が解明されていくまでを描く、サスペンス・ミステリー。手錠をはめて心中した息子の恋人の手首を切るという、母親の嫉妬とすさまじいばかりの執念を倍賞千恵子が熱演。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PREDATOR ２", "プレデター2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "“人間狩り”を最大の喜びとする地球外生物プレデターが、1997年、近未来のロサンゼルスに出現。前作から格段に進歩した彼らは、ハイテクに反応する能力や超自然的な力を備えており、警察は想像を絶する戦いを強いられるのだった……。アクション・シーン満載、ノンストップで圧倒する近未来SF。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ポチの告白", "ポチの告白", "", "", "http://image.eiga.k-img.com/images/movie/54184/315x210.jpg?3451", 195, "「GOTH」の高橋玄監督が、実際の事件をもとに警察支配社会に切り込む意欲作。所轄警察署の巡査・竹田は、その実直さで刑事へと昇進する。汚職まみれの上司の命令に盲目的に従う竹田は、警察犯罪を追う新聞記者を社会的に抹殺するよう指示を受け、知らぬ間に警察犯罪の主格犯となっていく。主演は「ラスト・サムライ」「休暇」の菅田俊。", "2009-01-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ALAMO", "アラモ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "かつてジョン・ウェインの監督、主演で映画化された“アラモ砦の戦い”を、史実に忠実に映像化した歴史スペクタクル。崩れかけた砦に立てこもり、数千人のメキシコ軍相手に戦った200名足らずの戦士たちの悲劇を描いていく。アメリカ人なら誰もが知っているという英雄デイヴィ・クロケットを、B・B・ソーントンが等身大の佇まいで妙演。", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラ対メカゴジラ", "ゴジラ対メカゴジラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ゴジラ生誕20周年記念作品。ブラックホール第３惑星人がゴジラそっくりのロボット、メカゴジラを使い地球侵略を企てる。ゴジラはアンギラスとともに迎え撃つが返り撃ちに遭う。しかし沖縄の伝説怪獣キングシーサーがよみがえり、ゴジラとともに逆襲に転じる。メカゴジラのキャラクターが大好評を博す。", "1974-03-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北緯15゜のデュオ", "北緯15゜のデュオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "第二次大戦の傷跡がいまだに残っているフィリピンのマバラカットの町を旅する俳優・川谷拓三と、戦時中、特攻隊で息子を失った老婆の目を通して戦争がもたらした悲劇をセミドキュメント・タッチで鋭く描く反戦映画。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eragon", "エラゴン　遺志を継ぐ者", "", "", "http://image.eiga.k-img.com/images/movie/33354/315x210.jpg?1929", 104, "邪悪な王が支配する帝国アラゲイジアに住む少年エラゴンは、ある日、森の中で光り輝く青い石を見つける。それは、世界の運命を握るドラゴンの卵だった。卵からかえったドラゴン“サフィラ”を密かに育て始めたエラゴンは、かつて国を守っていた伝説の種族、ドラゴンライダーとして選ばれた自らの使命を知る。原作はクリストファー・パオリーニが15歳の時に執筆したファンタジー小説。主演は18万人の中から選ばれた新鋭、エド・スペリーアス。", "2006-12-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金門島にかける橋", "金門島にかける橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "日活と「恋恋風塵」などの製作会社として有名な台湾の中央電子影公司とが共同製作した、台湾ロケが見もののアクション。東京の病院でかつて楊麗春という台湾女性を治療した武井は、その３年後、船医として上陸した台湾で再び彼女と出会う。彼女は武井とともに日本へ行きたいと、彼の胸に飛び込んでくるのだが……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARADISE", "パラダイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "砂漠に投げ出された15歳の少女と16歳の少年が、サバイバルを続けるうちに愛が芽ばえ、お互いの気持ちを体で確かめ合うようになる。美しい自然の中で、来る日も来る日も二人で“パラダイス”のような生活を送る。P・ケイツのデビュー作。", "1982-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KISS ME", "KISS ME", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "藤竜也主演のハードボイルド・ロマン。初老の殺し屋が、最後の仕事の標的だった女と恋に落ち、逃避行を繰り広げる。監督は「ポップコーンLOVE」でデビューした矢野広成。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DODGEBALL", "ドッジボール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "閉鎖寸前のスポーツ・ジムを守ろうと、ドッジボールに挑んだ人々の奮起を描くスポ根コメディ。小学生の少女にも敗れる負け犬チームながら、結束力とそれぞれの奮闘は胸を熱くするに十分。悪戦苦闘で笑わせつつも、後味は痛快そのものだ。意外に迫力のあるゲーム・シーンも見逃せない。経営危機のスポーツ・ジムを守るため、ドッジボール大会出場を決意した関係者たち。彼らは悪戦苦闘しながらも全国大会に駒を進め……。", "2005-04-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEEL", "スティール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "NBAのスーパースター、S・オニールがDCコミックのヒーローに扮する痛快アクション。軍の新兵器開発に携わっていたアイアンズは、実験中の事故で親友が下半身不随の重傷を負ったことにショックを受け除隊する。ところが、その時の最新兵器が町に流出し、犯罪に利用されてしまう。アイアンズはパワードスーツを身にまとって犯罪者の陰謀に立ち向かうが……。オニールはサントラで歌も披露している。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kick-Ass", "キック・アス", "", "", "http://image.eiga.k-img.com/images/movie/55484/315x210.jpg?1942", 117, "同名人気コミックを「スターダスト」のマシュー・ボーン監督が映画化したアクション・コメディ。ニューヨークに住む少年デイブは「誰もがスーパーヒーローを好きなのに、なぜ、誰もスーパーヒーローになりたがらない？」と思い立ち、何の特殊能力も持たないまま、ひとりコスチュームを着てスーパーヒーロー「Kick-Ass（キック・アス）」になる。だが、正義の味方として悪者を退治するのは骨の折れる仕事で、かなり痛い。やがて、傷だらけになりながらもキック・アスとしての活動を続けるデイブの前に、同じ稼業のビッグ・ダディとヒット・ガールが現れる。", "2010-12-18", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　CENA", "星降る夜のリストランテ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "イタリアの名匠E・スコラが、ローマの街角にたたずむレストランを舞台に、そこに出入りする人々の一夜の出来事を綴ったハートウォーミングな群像ドラマ。病気を患う夫に代わって、フローラが切り盛りする“アルトゥーロの店”。おなじみの常連客や不倫中のカップルなどが現れ、店内は今夜も大盛況だった。やがて夜が更けるにつれて、客たちが抱える秘密や悩みが明らかになっていく。", "1998-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Come See the Paradise", "愛と哀しみの旅路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "第2次世界大戦を背景に、アメリカ人男性と日系女性との恋愛を描いたメロドラマ。ロサンゼルスで映画館を経営する日本人を父に持つリリーは、アメリカ人のジャックと恋に落ち、周囲の反対を押し切って結婚する。彼らは子宝にも恵まれて幸せな毎日を送っていたが、日米開戦にともない、日系人は強制的に収容所へ送られることに。2人は離れ離れになってしまう……。監督・脚本は「ミシシッピー・バーニング」の名匠アラン・パーカー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幸運の壺 Good Fortune", "幸運の壺 Good Fortune", "", "", "http://image.eiga.k-img.com/images/movie/57674/315x210.jpg?3400", 83, "お笑いタレントのほっしゃん。が映画初主演を務め、「幸運の壺」を手にしたことで不幸の連鎖に巻き込まれていく男の奇想天外な運命を描いたコメディドラマ。鬼嫁の麻美にDVを受けながら売れない役者を続けている良作は、マンションの管理人から「幸運の壺」をもらい受けると状況が激変。麻美が突然死してしまい、殺人の疑惑を逃れるためとっさに妻の死を隠ぺいしていつも通り撮影現場に向かう。しかし、そこへ予期せぬ訪問者が現れ事態はさらに混迷。良作の行く先々で死体が増えていく。管理人役で戸田恵子、鬼嫁の麻美役で麻生久美子らが共演。", "2012-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魁!!男塾", "魁!!男塾", "", "", "http://image.eiga.k-img.com/images/movie/53154/315x210.jpg?1984", 110, "週刊少年ジャンプに1985年から91年まで連載され、コミックは2600万部を超える宮下あきらの人気格闘漫画を、アクション俳優の坂口拓が初監督で初実写映画化。武士道精神を継承し、真の男を育てる全寮制の私塾・男塾にやってきた剣の達人・剣桃太郎や富樫源次、虎丸、秀麻呂らは、教官たちの常軌を逸したシゴキや懲罰に耐えながら、やがて友情を育んでいく。そんな時、男塾に私怨を抱く伊達臣人が、男塾乗っ取りを企み、攻め入ってくる。", "2008-01-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CRI DE LA SOIE", "絹の叫び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "絹の肌触りに性的な興奮を覚える女性と、彼女の研究を進める精神科医。彼らは次第に惹かれ合うが、この恋には思わぬ悲劇が待ち受けていた……。フェティシズムを題材にした官能的な恋愛ドラマ。「真夜中の恋愛論」のM・トランティニャンがヒロインに扮し、絹への恍惚とした表情を鮮烈に演じている。ビデオ発売時タイトルは「絹の叫び　フェティシズムの愛撫」。", "1996-01-01", "2,26,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WATCHER", "ザ・ウォッチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "残酷な手口で連続殺人を続ける男グリフィンに、恋人を殺されたFBI捜査官キャンベル。彼はそのショックから身も心もボロボロになっていた。しかし、またしてもグリフィンが殺人を予告してくる。グリフィンにとって殺人はキャンベルとの強い絆を生むものだった。K・リーヴスがセクシーな連続殺人鬼を演じたサスペンス・スリラー。監督はミュージック・ビデオ界の巨匠で本作が映画初監督作となるJ・シャーバニック。撮影には「レイジング・ブル」「逃亡者」でオスカー候補に挙がった大ベテラン、M・チャップマンがあたっている。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Frankie Machine", "フランキー・マシーン（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LADY EVE", "レディ・イヴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "蛇にしか興味のない世間知らずの御曹司チャーリーと、美貌の女詐欺師ジーンが運命的に出会う。執事の機転で正体がばれ、１度は引き下がったジーンだが、図らずも自分が彼を深く愛してしまったことに気付く。一計を案じた彼女は、淑女イヴと身分を偽ってチャーリーの邸宅へ乗り込むが……。スクリューボール・コメディの鬼才、スタージェスの代表作だが、1994年に公開されるまでは『淑女になったイヴ』という題でTV放送されたのみ。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL POSTINO", "イル・ポスティーノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "アカデミー賞５部門にノミネートされ、日本でも単館上映ながら大ヒットした作品。ナポリの沖合の小さな島に暮らすマリオは仕事にも就かず、うっ屈とした日々を送っていた。ある日、チリの偉大な詩人であるパブロ・ネルーダが国外追放になり、この島にやって来る。島の郵便局には世界中からファンレターが送られてくるようになり、その配達人としてマリオが雇われた。マリオは毎日、詩人の所へ配達をするようになり、次第に親しくなる。詩人はマリオに詩を読んで聞かせ、詩の隠喩について教えてくれた。最初は、有名な詩人の名前を利用して女の子に近づこうと考えていたマリオだったが、詩の魅力に惹かれ、自分も詩人になりたいと考えるようになる。マリオを演じたM・トロイージは撮影終了の12時間後に心臓病により死去した。なお、パブロ・ネルーダはノーベル文学賞を受賞している実在の詩人であり、原作者のスカルメタが彼に憧れて、この話を書き上げた。", "1995-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("扉のむこう", "扉のむこう", "", "", "http://image.eiga.k-img.com/images/movie/55254/315x210.jpg?1404", 0, "英国人監督ローレンス・スラッシュが、日本のひきこもりをテーマに描いた作品。キャストのほとんどに、演技経験のない素人を起用している。宏は学校で問題を抱え、ある日突然自分の部屋にひきこもるようになってしまう。その後の2年間、宏は部屋に誰も入れず、部屋から出ることも拒否する。そのことを恥じた両親は、親戚や友人にもその事実をひた隠し、家庭はどんどん崩壊していく。", "2010-03-06", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TERROR AT THE OPERA", "オペラ座／血の喝采", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "不幸を招くといわれるオペラ『マクベス』の主演女優が事故死し、新人ベティが代役に立つが、上演中に次々と猟奇的な殺人事件が起こっていく。残酷恐怖美学の旗手D・アルジェントが、オペラの名曲とロックのリズムにのせて展開させた恐怖と暴力の世界。300メートルの移動クレーンによるカメラからオペラ座の舞台と客席を俯瞰するシーンは圧巻。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天兵童子〈総集編〉", "天兵童子〈総集編〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "雑誌『少年倶楽部』に、連載された吉川英治の少年向け時代小説を、「まぼろし城」のスタッフが映画化した冒険時代活劇。戦国乱世に槍１本で乗り出した、南朝忠臣の子孫・天兵の活躍を描いていく。戦時体制下ということで、内容に国策的姿勢も見られるが、連続活劇としての面白さもピカいち。1955年には再映画化もされた。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嘘", "嘘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "嘘にまつわるエピソードを綴ったオムニバス。４人のボーイフレンドとかけ持ちで付き合う短大生が真実の愛に目覚める「プレイガール」（増村）、愛人と縁を切るため芝居を仕組んだ社長を描く「社用２号」（吉村）、ある男が殺された事件の法廷で、彼をめぐる女たちの証言がくるくると変わって真相が薮の中となる「女体」（衣笠）の３話で構成されている。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ファイティング　オカン TWILIGHT FILE VIII", "ファイティング　オカン TWILIGHT FILE VIII", "", "", "http://image.eiga.k-img.com/images/movie/57084/315x210.jpg?3358", 74, "オムニバスシリーズ「TWILIGHT FILE」第8弾の1作。夫と息子と平穏な日々を送っていた40歳の専業主婦・佐伯小夜だったが、ある日突然、夫から離婚届を突きつけられたことをきっかけにボクシングジムに通い始める。それまで何ひとつ打ち込んだことのなかった小夜は、次第にボクシングの魅力にのめりこんでいく。元「モーニング娘。」の中澤裕子の主演で、ボクシングを通して幸せをつかもうとする女性の姿を描く。", "2011-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROADWAY MELODY OF 1936", "踊るブロードウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "E・パウエル主演による、黄金期のMGMミュージカル。田舎から出て来た少女が、“フランスから来た名コメディエンヌ”として売り出されたことから起こる騒動を描く。『You're My Lucky Star』『Broadway Rhythm』などフリード＆ブラウンの名曲の数々も楽しめる。振付担当のデーヴ・グールドがアカデミー賞を受賞した。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自由学校", "自由学校", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "話題となった獅子文六の新聞連載小説を映画化。松竹と大映が競作、同じ週に封切られている。吉村公三郎の大映版では、主人公に素人の雑誌編集者を起用、社会や家庭から逃げ出し、浮浪者の群落に飛び込んだサラリーマンの姿を通し、当時の世相を痛烈に風刺。松竹版より、喜劇性を前面に押し出した。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDER PRESSURE", "プレッシャー　壊れた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "C・シーンが次第に精神を崩壊させていく男を怪演するサスペンス・スリラー。ロサンゼルス郊外の平穏な住宅街サンフェルナンド・ヴァレー。かつて勇敢な行動で勲章ももらった消防士の英雄ライルは、非番のため自宅にいた。うだるような暑さの中で落ち着かない彼は、隣家の主人リースが今日も自分の家の前に違法駐車していることや、彼の子供たちが庭先で水をかけ合いながら騒いでいることにイラ立っていく。しかし、注意を促しても聞き入れてはもらえない。気分を鎮めようとした矢先、今度は子供たちが操作を誤ったラジコン飛行機が、窓ガラスを破って飛び込んでくる。隣家に乗り込み、母親に今までの不満をぶちまけるライル。やがて家に戻った彼は、隣家と間違えてやって来た電気修理工と口論となり、抑え切れない怒りはとうとう一線を越えてしまう……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Drugstore Cowboy", "ドラッグストア・カウボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ドラッグ欲しさに薬局荒らしを繰り返す若者たちの悲劇を、ガス・バン・サント監督がスタイリッシュな映像でクールに描いた青春映画。1970年代前半のオレゴン州。麻薬常用者のボブは、妻ダイアンや友人リック、そしてまだ10代の少女ナディーンらとともに、薬局を荒らしドラッグを奪うという行為を繰り返していた。ところが、ある日ナディーンが犯した小さなミスが引き金となって彼らの間に亀裂が入り、歯車が狂い始める……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLUKE", "フルーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "飼い主が死んで、孤独になった小犬のフルークは、事故で死んだトムの生まれ変わりだった。自分の前世の記憶を取り戻した彼は、愛する妻と子供に会うために旅に出る。しかし、家族との再会を果たし、家族の一員に戻った彼の前に、彼を死に追いやった張本人のジェフが現れる。家族を守るためにフルークはジェフに飛びかかるが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WAY AHEAD", "最後の突撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1941年、国民兵役法が制定されたイギリスでは、若者たちが軍事訓練に励んでいた。ペリ小隊長の指揮下に配属された８名もやがて一人前の兵士となり、アフリカ戦線へと出発する……。第二次大戦のさなかに製作されたセミドキュメンタリー・タッチの戦争映画。", "1944-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眞心英雄", "ヒーロー・ネバー・ダイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "スゴ腕の殺し屋ジャックとチャウは敵対する組織に属しながらも、たがいに一目置き合う仲で、友情にも似た不思議な絆で結ばれていた。やがて抗争が激化したことにより、二人は重傷を負うが、その後、二つの組織のあいだで和解が成立。用済みとなったジャックとチャウに組織の刺客が迫る……。切れ者同士の男の絆を捉えたハードボイルド・アクション。ストイックなジャックとギャングスター風のチャウの、それぞれのキャラクターを明確に描き分けつつ、彼らの友情をハードかつ、せつなげに描き出す。『上を向いて歩こう』のメロディーが効果的に使用され、凄じいアクション描写に叙情味を加えている。", "1998-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Memory Keeper's Daughter", "メモリー・キーパーの娘", "", "", "http://image.eiga.k-img.com/images/movie/54764/315x210.jpg?4298", 90, "全米で500万部を超えるベストセラーとなった同名小説の映画化。1964年の大雪の夜、医師のデイビッドは妻のお腹から初めての赤ん坊を自らの手で取り上げるが、双子の片方はダウン症を患っていた。デイビッドはとっさの判断で立ち会っていた看護師に赤ん坊を施設に連れて行くよう指示し、妻には死産だったと嘘をついてしまう。しかしデイビッドに想いを寄せていた看護師はこっそり赤ん坊を育て……。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Three Days of The Condor", "コンドル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "CIAの下部組織が襲撃された。偶然助かったコードネーム“コンドル”は知らぬ間に内部抗争に巻き込まれ、命を狙われる。彼は女性カメラマンと知り合い、CIA上層部の陰謀を暴こうとするが……。シドー扮する謎の殺し屋の不気味ぶりが見事。サスペンス・アクションの快作。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("平成狸合戦ぽんぽこ", "平成狸合戦ぽんぽこ", "", "", "http://ecx.images-amazon.com/images/I/51bqc0cFwRL._SL210_.jpg", 119, "舞台は東京の多摩丘陵。宅地開発で次々と自然が失われるなか、そこに棲むタヌキたちが開発阻止に立ち上がった。人間の見ていない所では言葉もしゃべり、２足歩行もする彼らは、先祖伝来の“化け学”を復興すべく特訓を開始する。ついに人間に化けられるようになったタヌキたちは、様々な作戦で開発を妨害するが、タヌキの仕業とは頭から思わない人間たちには、ほとんど効果がない。そこに３匹の長老ダヌキが四国から応援に駆けつけ、その指揮で無数の妖怪に化けての、一大デモンストレーションが開始されるが……。タヌキと妖怪に代表される日本古来の説話絵巻を、現代の市街地風景のなかで再現する。“変身”というアニメならではの見どころ、そして柳家小さんら、声の出演の聞きどころにも溢れた風刺痛快作。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Giulietta degli Spiriti", "魂のジュリエッタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "　精神世界を色彩豊かに映像化したファンタジックストーリー。大邸宅と見事な庭、おしどり夫婦と誰もがうらやむ優雅な主婦生活を送るジュリエッタ。しかしほかの女性の名をつぶやく夫の寝言を聞いた日から、いい知れぬ不安を感じ始める。いつしかその不安は幻想となって彼女を苦しめる……。隣人の豪邸やヒロインの人生がフラッシュバックするシークエンスなど、摩訶不思議な演出はまるで舞台を見ているかのよう。", "1964-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REMINISCENCES OF A JOURNEY TO LITHUANIA", "リトアニアへの旅の追憶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "実験映画やドキュメンタリーの分野で世界の映画人に多大な影響を与えたインディペンデント作家J・メカスの「ロスト・ロスト・ロスト」「時を数えて、砂漠に立つ」とともに代表作として語り継がれる作品。ナチスに追われて戦後ニューヨークに亡命してきたメカスが1950年代初頭のブルックリンをスケッチした映像、1971年に久しぶりにリトアニアの故郷に戻った時の“思い出”の映像、そして戦時中に過ごした強制収容所があったハンブルグ郊外やウィーンの映像という３つのパートで構成されている。カメラとともに生きるメカスの豊かな感性がフィルムに刻まれ、ドキュメンタリーというより“日記映画”と呼ぶにふさわしい感動的な叙情性を醸し出す不朽の名作。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TARKA THE OTTER", "かわうそタルカの大冒険 　～失われた野生の詩～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "生後間もなく両親をハンターに撃ち殺されたカワウソのタルカは、数々の困難を乗り越え大人へと成長していく。やがて幸福な家庭を築いたタルカだったが、狩猟の季節の訪れとともにハンターに狙われはじめ……。絶滅寸前のカワウソの誕生から死までを心優しく描いたファミリー・ドラマ。", "1978-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOTHIKA", "ゴシカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "フランスの気鋭監督であり、俳優としても活躍するM・カソヴィッツがアメリカに渡って完成させたオカルト・ミステリー。精神病患者を収容する女刑務所を舞台に、悪魔や幽霊の存在に怯える女囚たちが殺人事件の真相に迫る。オスカー女優H・ベリーとP・クルスという、タイプの異なる美女の熱演にも注目。精神科医のミランダは、夫殺しの罪に問われ、事件の記憶がないまま刑務所に収監。無実を証明するため記憶の糸を手繰り寄せるが、数年前に惨殺された少女を見たと証言したせいで狂人扱いされる。一方、監獄内では次々と怪奇現象が……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("汪洋中的一條船／HE NEVER GIVES UP", "生きてる限り僕は負けない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "両足に障害を持って生まれながら、多くの試練を乗り越えて大学進学、結婚をした一人の男の苦悩の一生を描いた作品。実話をもとに作られた感動ドラマ。", "1977-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無頼・殺せ", "無頼・殺せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "京浜のある工業都市へやって来た藤田五郎は、入江崎一家の代貸でムショ仲間の守山に再会する。入江崎一家と対立する関東東友会は、ことあるごとに嫌がらせをし、あげく守山を暗殺する。五郎はゴーゴー喫茶で気炎をあげる悪玉一味に怒りのドスをぶち込む。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Playtime", "プレイタイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "ジャック・タチ扮するユロ氏が騒動を繰り広げるコメディ。大会社に就職するため、近代的なビルが立ち並ぶパリにやってきたユロ氏。やっとのことでたどり着いたオフィスで面接官とはぐれてしまい、迷路のようなビル街の中をひとりさまよう。その途中、アメリカから来た団体観光客と遭遇し、若くて美しい娘バーバラと出会う。莫大な製作費をかけ、巨大なセットを建設して撮影された超大作だが、興行的に失敗。タチを破産に追い込んだ。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOTHER’S DAY", "マザーズデー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "森の奥深くにある不気味な小屋。そこに住む年老いた母と二人の孝行息子は、通りすがりの人々をいたぶり責め殺す、人間狩りが最高の楽しみという変態一家だった……。1981年パリ国際ファンタスティック映画祭ブラック・ユーモア賞受賞作。", "1980-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ball of Fire", "教授と美女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ニューヨークのトッテン財団では、8人の学者たちによる百科事典の編纂が大詰めを迎えていたが、文法学者のポッツ教授がある俗語を耳にしたことから、これまでの努力が世間から隔絶していたことに気づき、街へ調査に出かける。そこで出会ったナイトクラブの歌姫、オシェイに協力を求めるが……。公開当時、アメリカでヒットしたものの、時代の影響で輸入されなかった。ビリー・ワイルダーはホークス演出の見学許可を条件に脚本に参加。翌年には監督デビューしており、これに備えての勉強だったという。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Short Films of David Lynch", "ザ・ショートフィルム・オブ・デヴィッド・リンチ", "", "", "http://image.eiga.k-img.com/images/movie/54284/315x210.jpg?3598", 97, "「エレファント・マン」「マルホランド・ドライブ」の鬼才デイビッド・リンチの6つの短編作品を集めたオムニバス。「SIX MEN GETTING SICK」（1967）、「THE ALPHABET」（1968）、「THE GRANDMOTHER」（1970）、「THE AMPUTEE」（1973）、「THE COWBOY AND FRENCHMAN」（1987）、「LUMIERE」（1995）がリンチの解説付きで収録されている。", "2009-02-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄堂霊界通信", "地獄堂霊界通信", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "最強小学生のてつしと、頭脳明晰ハンサムボーイの椎名、恐がりだけど心優しいリョーチンは、上院町では知られた悪ガキトリオ。３人はある日、飛び降り自殺を目撃したことから“抜け首”という生霊に狙われるはめに。だが、見かけは気味悪いけど妖怪のことは何でも知ってる“地獄堂”のオヤジから授かったパワーで、生霊を成仏させることに成功。妖怪の存在を知った彼らは、上院町に忍び寄る妖怪らと戦っていく……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おいしい結婚", "おいしい結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "質屋を営む未亡人、美枝子は一人娘ののんを条件のいい見合いで結婚させようとするが、のんは会社の同僚・川又を恋人に仕立て断ろうとする。ところがいつしか2人は恋に落ち、本当に結婚することになるが、今度はその結婚式をめぐって両家に様々な出来事が起きる。幸福な結婚とは何かを描いたホームドラマ。", "1991-05-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しあわせカモン", "しあわせカモン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WiLD LIFE", "WiLD LIFE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "１作ごとに異なる作風を披露し、映画作家としての懐の深さを感じさせる青山真治の監督第３作。この５年間、決まりきったストイックな日常を過ごしてきたパチンコのクギ師・酒井。かつての同僚・水口が仕掛けた復讐作戦に巻き込まれた彼は、１本のビデオテープをめぐる怪事件に悩まされる……。フィルター処理された冷ややかなトーンの映像の中に、ヤクザの暗躍、主人公と社長の娘との恋物語などがパズル感覚で入り乱れる異色ハードボイルド。どこか不気味で淡々としていたドラマのテンポが、敵陣に乗り込んだ主人公が元ボクサーの本領を発揮するクライマックスでは一気に加速し、観る者をアゼンとさせる。まさに変幻自在、掴みどころのない魅力を秘めた作品だ。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("我愛イ尓／I LOVE YOU", "ウォ・アイ・ニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "結婚生活の理想と現実、男女の愛し方の違いという、誰の身にも起こりうる男女の関係性をドキュメンタリー・タッチで描いた恋愛ドラマ。役者たちに設定だけ伝え、アドリブで演じさせる演出方法を使い、リアルな会話が生み出された。激しい夫婦喧嘩のシーンでは、ぶつかり合う感情が真に伝わってくる。婚約者を事故で亡くした若い女性シャオジューは、いつしか彼の親友ワンと心を通わせ、電撃的に結婚する。しかし彼女の過剰な愛情表現は次第にワンを追いつめ、亀裂を生み始める。", "2006-03-11", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BILLY BADD", "モーター・サイコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "狂気のバイク・ライダーに執拗に追いかけられる恐怖を描く。大陸横断ドライブ中の学生カップル、フランキーとゾーイは、人気のない砂漠でバイカーのビリーに追われる。初めは冗談と思っていたフランキーたちも、次第に恐怖感に迫られる。遂にハンドルをとられ、フランキーたちの車は転倒して大破。それでもビリーの追跡は止まらない。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE SANG DES AUTRES／THE BLOOD OF OTHERS", "他人の血", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "監督としても才女ぶりを発揮しているJ・フォスターが、ヌーヴェル・ヴァーグの鬼才といわれたC・シャブロル監督のもとで主演した作品。第二次大戦前の1938年のパリ。若くて自己中心的なエレーヌは服飾店で働き、左翼の活動家ポールと愛し合っていた。だが、政治への関心の違いで二人は別れる。やがてブルジョア出身のエレーヌは、家族の反対を押して、労働者のために活動をするジャンとつき合うようになった。そして戦争勃発、エレーヌとジャンは、否応なしにその渦中に巻き込まれていった。", "1984-01-01", "2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sherlock Holmes", "シャーロック・ホームズ", "", "", "http://image.eiga.k-img.com/images/movie/54174/315x210.jpg?3772", 129, "アーサー・コナン・ドイルが生んだ名探偵シャーロック・ホームズのキャラクターにインスピレーションを受けたオリジナルストーリーを、「スナッチ」のガイ・リッチー監督が映画化。19世紀末のロンドン。ホームズ（ロバート・ダウニー・Jr.）と医師ワトソン（ジュード・ロウ）の2人は、怪しい黒魔術の儀式を行い、若い女性を次々と殺害するブラックウッド卿を逮捕する。だが、処刑されたはずのブラックウッドが蘇り、再び殺人事件が発生する……。", "2010-03-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クロスファイア", "クロスファイア", "", "", "http://image.eiga.k-img.com/images/movie/1834/315x210.jpg?1841", 118, "念力発火能力＝パイロキネシスを持って生まれた青木淳子は、自分でも制御不能なその強い力を抑えるため、母の教えを守りひっそりとOL生活を送っていた。ある時、思いを寄せていた同僚・多田の妹が惨殺されるが、被疑者・小暮が未成年のため、法の裁きもままならない。犯行をほのめかし、マスコミにもてはやされる小暮に、一人復讐の機会を窺う多田。その悲壮な決意の前に、淳子はひた隠しにしていた能力を使い小暮を追い詰める決心をする。", "2000-06-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHE’S　ALL　THAT", "シーズ・オール・ザット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「ラストサマー」シリーズのF・プリンツJr.主演の学園シンデレラ・ストーリー。学園で一番さえない女の子レイニーをわずか６週間でパーティー・クイーンに変身させるという無謀な賭けに、学園一のモテモテ男ザックは勝てるのだろうか？　最初はレイニーがただの賭けの対象だったザックだが、黒ぶち眼鏡の奥に隠された彼女の真の美しさと優しさに次第に心を奪われていき……。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("国定忠治", "国定忠治", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "新藤兼人のオリジナル脚本を谷口千吉が監督したオールスター大作時代劇。天保７年、子分を連れて故郷の国定村に帰ってきた忠治は、凶作と幕府の圧政のために妹が死んでしまったことを知り、代官屋敷へ殴り込む。その後、お尋ね者となった彼は赤城山に身を隠すが、３年後、ついに山を下りる決意をする。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ある朝スウプは", "ある朝スウプは", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "PFFアワード2004でグランプリに輝いた痛切な愛の悲劇。ビデオ撮影による完全な自主映画ながら、アパートで同棲生活を送る若いカップルの関係が破綻していく様を、生々しい空気感とセリフで描ききったその筆致は圧巻。高橋泉監督とユニットで活動する主演俳優、廣末哲万の怪演も鮮烈だ。電車に乗ることができずパニック障害と診断された北川。恋人の志津は、新興宗教に傾倒し始めた彼を現実に引き戻そうとするが……。", "2005-07-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hanover Street", "ハノーバー・ストリート／哀愁の街かど", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "　1943年、第2次大戦下のロンドン。空軍中尉ハロランは街中でマーガレットと運命的な出会いを果たす。彼女には、娘と優しい夫ポールがいたが、ハロランとの密会をやめることができなかった。その後、ポールが特別任務でドイツ軍領内に侵入することに。それに同行したハロランはポールがマーガレットの夫であることを知ってしまう……。戦火の中で燃え上がる愛の行方を描いた物語。", "1979-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幽閉者　テロリスト", "幽閉者　テロリスト", "", "", "http://image.eiga.k-img.com/images/movie/33824/315x210.jpg?4799", 113, "1960年代を中心に“アングラ界の寵児”と呼ばれた伝説の映画監督、足立正生。その後、重信房子らの日本赤軍に共鳴、パレスチナ革命に参加した監督が35年ぶりに放つ新作。1972年“リッダ空港事件”の主犯のひとり、岡本公三をモデルに、自爆攻撃に失敗したったひとり生き残ったテロリストの、獄中での地獄のような幽閉生活や、自分自身の激しい葛藤を描く。主人公Mを演じるのは個性派俳優の田口トモロヲ。", "2007-02-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A２", "A２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "「A」に続いて、オウム真理教信者の“その後”を追いかけた森達也によるドキュメンタリー。前作では広報部長、荒木浩に焦点が絞られていたが、本作では宗教団体アレフと名を変えた同集団全体の“受難”を見つめた俯瞰的なものになっている。運動のための運動と化したデモによる思想なき排斥模様、地域住民と奇妙な親睦を深める信者たちの意外な横顔など、根城を失い追われ続ける彼らの行方を、混沌とした状況のまま描き出す。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パンツの穴 キラキラ星みつけた！", "パンツの穴 キラキラ星みつけた！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "父親の再婚を素直に喜べないしおりは、その気分を紛らわすかのようにテニス部の合宿で、信州・諏訪湖にやって来る。そんなしおりに一目惚れした宿屋の息子・順平は彼女にアタックするが、しおりの義母がそこに現れて……。鎮西尚一監督がその異才ぶりを存分に発揮し、最近の日本映画には珍しい怪作ミュージカルに仕上がっている。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わかれ雲", "わかれ雲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "東宝争議のあと、古巣東宝を去った五所平之助監督が、独立プロ“スタジオ８プロダクション”を創立して再起した第１作。義母との関係に心を痛める一人の女子大生が、旅行先の信州で、貧しい村人のために献身的に働く人々を見て、自分のあやまちに気付くまでの物語を詩情豊かに描く。オール・ロケによる美しい信州の風景が魅力。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五個女子和一根縄子／FIVE　GIRLS　AND　A　ROPE", "ファイブ・ガールズ・アンド・ア・ロープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "ワン・チン監督の「五人少女天国行」と同じ原作をもとにした作品。中国の田舎で生まれ育った５人の少女は、固い絆で結ばれた親友同士。古い因習にしばられている彼女らは、男たちの酷使から逃れ、自由を手に入れるために一緒に“花園”へ逃走する……。貧しい農村に生まれた少女たちの悲劇を、中国、台湾のアイドルの共演で描く。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Best Friend's Girl", "2日間で上手に彼女にナル方法", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alien vs. Predator", "エイリアンVS.プレデター", "", "", "http://image.eiga.k-img.com/images/movie/1014/315x210.jpg?2327", 100, "南極の地下深くに地球最古のピラミッドが発見されるが、そこは宇宙からやってきたプレデターが、成長の儀式のためにエイリアンと戦う場所だった。企画は10数年前から存在していたが実現しなかった、人気SFシリーズ「エイリアン」と「プレデター」の主役クリーチャーの対決映画。「モータル・コンバット」「バイオハザード」のポール・W・S・アンダーソン監督によって脚本がまとめられ、映画化に至った。", "2004-12-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shaun the Sheep", "ひつじのショーン", "", "", "http://image.eiga.k-img.com/images/movie/53164/315x210.jpg?2000", 0, "日本でも人気を呼んだイギリス産クレイ・アニメ「ウォレスとグルミット」に登場するキャラクター、ひつじのショーンを主人公にした短編アニメーション。牧草地で暮らす羊のショーンは、好奇心旺盛でイタズラが大好き。群れの人気者で新しいことを考えるのが得意なショーンは、今日も牧羊犬ビッツァーの制止もよそに仲間たちと大騒動を巻き起こし……。DVD化を記念して全40話を4プログラムに分けて上映。", "2007-12-22", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五月之恋／LOVE OF MAY", "五月の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "台湾の若手俳優チェン・ボーリンが主演したラブ・ストーリー。メールでの嘘のやりとりをきっかけに対面した台湾の青年と中国人少女の恋を、悲しい歴史的背景を織り交ぜて綴る。台湾の人気バンド、“メイデイ”が出演し音楽も担当。台湾の大学生アレイは、人気バンド“メイデイ”のギタリストの弟。そんな彼がメールで知り合った“メイデイ”のファンの少女シュアンと対面することに。ふたりはお互いに惹かれ合うが……。", "2006-05-06", "53,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACACIA", "ACACIA", "", "", "http://image.eiga.k-img.com/images/movie/53774/315x210.jpg?2831", 100, "元プロレスラーのアントニオ猪木が初主演するヒューマンドラマ。作家・ミュージシャンなど幅広く活躍する辻仁成が、監督・原作・脚本を務める。元覆面プロレスラーの大魔神は、息子に充分な愛情を注げなかったことを悔やみながら、寂れた団地で静かに余生を過ごしていた。そんなある日、母親に捨てられた少年タクロウが大魔神の家に転がり込んでくる。自分にだけ心を開くタクロウと親子のように暮らし始めた大魔神は、やがて本当の家族と再会する覚悟を決める。", "2010-06-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カルテット", "カルテット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "職業としての音楽家に未だなれない元音大生４人がカルテットを組んで、コンクール優勝をめざし日夜励むことに。最初はバラバラだった４人の演奏だったが、次第にそれが美しくまとまりかけた頃、思わぬ事件が……。日本を代表する音楽家であり、宮崎駿監督作品をはじめとする映画音楽の数々でも著名な久石譲の映画初監督作品。音楽とともに生きようと悩み苦しむ若者たちの姿を、繊細に捉えながら温かく包み込んでいく音楽青春映画である。劇中に登場する楽曲は、もちろんすべて久石の作曲によるもの。キャスト陣も吹替えを一切用いずに、演奏シーンを見事に演じきることで、映像と音楽が巧みに融合し、人生の感動が導き出されている。DVDタイトルは「Quartet　カルテット」。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("周恩来", "周恩来", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 160, "日中友交関係の礎を築いた先人、周恩来の生涯、特に晩年の10年にスポットを当てて描いた歴史大作。これまで非公開だった中南海西花庁（周の住居）を舞台に、文革、毛沢東暗殺計画など歴史の些細を明らかにしているほか、ほとんどのシーン、セットで周恩来生存中の貴重な実物を使っている。", "1991-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヤノマミ　奥アマゾン・原初の森に生きる", "ヤノマミ　奥アマゾン・原初の森に生きる", "", "", "http://image.eiga.k-img.com/images/movie/55224/315x210.jpg?3590", 120, "アマゾンの最深部で、1万年以上に渡り独自の文化と風習を守り続けているヤノマミ族。森の奥でひっそりと行われる出産や、精霊を病人に憑依させるシャーマンなど、太古から続く暮らしを150日間にわたって記録。ヤノマミとは彼らの言葉で「人間」の意である。NHKで放送されたドキュメンタリー番組を劇場で公開するプロジェクト「This is REAL／NHK-BSドキュメンタリー・コレクション」にて上映。", "2010-02-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Death at a Funeral", "ハウエルズ家のちょっとおかしなお葬式", "", "", "http://image.eiga.k-img.com/images/movie/54704/315x210.jpg?4210", 90, "「イン＆アウト」「ビッグムービー」のフランク・オズ監督によるハートフルコメディ。舞台はイギリス郊外。ハウエルズ家で行われる葬儀に参列するため、故人の親族や友人たちが続々と集まってくる。真面目な長男ダニエルは父の葬儀を無事終わらせようと奮闘するが、それぞれ問題を抱える参列者たちが次から次へと騒動を巻き起こし……。主人公ダニエルを演じるのは「プライドと偏見」のマシュー・マクファディン。", "2009-08-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幸福のスイッチ", "幸福のスイッチ", "", "", "http://ecx.images-amazon.com/images/I/51FRNzYI1RL._SL210_.jpg", 105, "和歌山県を舞台に、ガンコ親父と3人姉妹が繰り広げる人間模様を描いたハートウォーミングストーリー。儲けにならない仕事ばかり引き受ける電器屋の父・誠一郎に反発して上京し、デザイン会社でイラストレーターとして働きはじめた怜。しかし、自分が思い描いていた仕事ができず、会社を辞めてしまう。そんな中、帰省することになった怜は、父の電器屋を手伝うハメになり……。主人公の不器用な父娘を、沢田研二と上野樹里が好演。", "2006-10-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸川乱歩劇場 屋根裏の散歩者", "江戸川乱歩劇場 屋根裏の散歩者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "昭和初期の東京で“高等遊民”を気取る青年・郷田三郎は、うらぶれた下宿館の屋根裏を徘徊し、女たちの淫らな姿に異常なまでの興奮を覚えていた。そして彼はついに醜悪な中年男を屋根裏から毒殺する計画を実行に移すが、名探偵・明智小五郎が彼の犯罪に気づき……。エロスを追求する映像作家・実相寺昭雄が、私淑する乱歩の世界に挑んだ意欲作。1993年の東京ファンタスティック映画祭で上映された成人指定のインターナショナル・バージョン（77分）と、R指定の劇場公開版の両方ともがビデオ化されている。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Live and Let Die", "007／死ぬのは奴らだ", "", "", "http://ecx.images-amazon.com/images/I/51agz1XTmAL._SL210_.jpg", 117, "3代目ボンド、ムーアが初登場したシリーズ第8作。英国諜報部員が相次いで殺されたため、ボンドはCIAの旧友と調査を開始。麻薬市場の支配を狙うアフリカのカナンガと闘う。空を飛ぶモーターボート、小道具の数々が楽しく、また、ブードゥー教、タロットカードなどオカルト色も。主題歌はポール・マッカートニー。", "1973-07-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("原発切抜帖", "原発切抜帖", "", "", "http://image.eiga.k-img.com/images/movie/56854/315x210.jpg?1796", 45, "「水俣　患者さんとその世界」（1971）などで水俣病と向き合い続ける土本典昭監督が、長年切り抜きを続けてきた新聞記事のスクラップブックから「原子力」をテーマに企画した異色ドキュメンタリー。1979年・米スリーマイル島原発事故などを機に80年代初めからヨーロッパで再燃した反核運動は82年に日本にも及んだが、反対署名運動などは核兵器を対象としたもので原発批判とは結びついていなかった。そうした状況の中、日々配達される新聞から原子力発電所や政府の姿勢について問題を取り上げた記事を抜粋して読み上げ、原発大国・日本の本当の姿を浮き彫りにする。", "1982-11-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わたし出すわ", "わたし出すわ", "", "", "http://image.eiga.k-img.com/images/movie/54264/315x210.jpg?1451", 110, "東京から故郷の北海道に戻ってきた山吹摩耶は、どうやって稼いだのか、莫大な財産を築いていた。しかし、彼女自身は質素な生活しか送らず、代わりに高校時代の友人たちに、彼らの夢を叶えるために無償で大金を差し出していく。お金を受け取った人々の、そして摩耶の行く末は……。森田芳光監督が小雪を主演に迎え、13年ぶりのオリジナル脚本で描く人間ドラマ。黒谷友香、井坂俊哉、小澤征悦、仲村トオルら豪華キャストが共演。", "2009-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("КОЛОКОЛ ЧЕРНОБЫЛЯ", "チェルノブイリ・シンドローム その後の史上最悪の原発事故", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「チェルノブイリ・クライシス」の翌年に完成した、その後のチェルノブイリを伝えるドキュメント。1986年５月、６月、そして９月の取材で現地の人々の生の声を捉えている。家族はもちろん故郷まで失った人々の悲痛な叫びは、“核”に対する警鐘として響く。", "1987-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DE　TWEELING", "アンナとロッテ", "", "", "http://ecx.images-amazon.com/images/I/31H1NAH7H0L._SL210_.jpg", 137, "本国オランダのみならず、ヨーロッパ各国で大ヒットした感動のストーリー。両親の死により、幼くして離れ離れになった姉妹の数奇な運命を見つめる。劇中、幾度となく再会と別れを繰り返すことになる姉妹の命運に深くかかわるのが、第二次世界大戦。オランダでユダヤ人の男性を愛した妹と、ナチス親衛隊のオーストリア人将校と結婚した姉という立場が生んだすれ違いが、永遠に消えることのない大戦の爪跡を伝える。戦争の現実、人種の違い、姉妹愛など、様々なテーマが盛り込まれた感慨深い１本だ。", "2004-12-11", "70,58", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSESITTER", "ハウスシッター 結婚願望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "S・マーティン、G・ホーンという二人の喜劇スター共演による、心温まるコメディ。幼なじみのベッキーにプロポーズし、見事にフラれたデービス。ヤケになった彼は偶然出会った美女グエンと一夜をともにしてしまう。ところが、彼女はなんと、すご腕のサギ師。ベッキーとの暮らしを夢見て建てたデービスの豪邸に、グエンが押しかけてきた。天才的な作り話でデービスの妻になりすまし、彼の家族や友人を味方につけたグエンはぜいたく三昧の日々を送る。果たして、この人生最大の危機をデービスは乗り切れるか？　ユーモラスなマーティンと陽気なホーンのテンポ良い会話が、粋な笑いを誘う。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さいころ奉行", "さいころ奉行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "突然性放浪病に取りつかれた遠山の金さん。桑名の三十石船に乗った金さんは、船中の噂話の中心であった世直し党に秘密を感じ取った。そして浪人風の持ち物を手掛かりに世直し党の中心地に潜入するのだった……。おなじみの千恵蔵による遠山の金さんの１本。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tokyo Gore Police", "東京残酷警察", "", "", "http://image.eiga.k-img.com/images/movie/53944/315x210.jpg?3100", 0, "「片腕マシンガール」「赤んぼ少女」などの特殊造型を手がけた西村喜廣が、アメリカ資本で監督に初挑戦した超絶スプラッター。近未来の東京を舞台に、“東京警察株式会社”として民営化された警察組織が、人々を恐怖に陥れる新種の殺人ミュータント“エンジニア”と凄惨な戦いを繰り広げる。主演は三池崇史監督作「オーディション」で印象を残したしいなえいひ。共演には板尾創路、菅田俊、堀部圭亮ら個性派が揃う。", "2008-10-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("War, Inc.", "エージェント・オブ・ウォー", "", "", "http://ecx.images-amazon.com/images/I/313N2Sd1rTL._SL210_.jpg", 107, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("将軍家光の乱心 激突", "将軍家光の乱心 激突", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "東映が久々手掛けた大型集団時代劇。徳川三代将軍の家光は、我が子・竹千代を亡き者にしようと刺客を送った。しかし竹千代には強者の用心棒７人が護衛につき、命を懸けて江戸へ連れ上ろうとする。千葉真一率いるJACのハード・アクションが全編に満載。千葉はアクション監督も務めている。用心棒の長を演じる緒形拳の好演が光る作品。時代劇に挑んだ織田裕二の怪演にも注目したい。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("親鸞・白い道", "親鸞・白い道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "過激な言動ゆえに流罪となって都を追われた親鸞が、釈放後に新天地を求めて旅立っていく姿を壮大なスケールで描いた作品。三國自身が構想に15年かかったという書き下ろし小説を映画化、親鸞役はオーディションで選ばれた新人・森山潤久を起用した。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAPLIN", "チャーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "映画史上最高のスター、チャールズ・チャップリンの人生を、超豪華スタッフ、キャストで描いた伝記映画。ロンドンの下町の貧しい役者一家に育ったチャーリーは、アメリカ巡業中に“映画”と出会う。ハリウッドからスカウトされた彼は、その無限の可能性を信じて、ガムシャラに映画の世界に飛び込んでいく……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くまのがっこう　ジャッキーとケイティ", "くまのがっこう　ジャッキーとケイティ", "", "", "http://image.eiga.k-img.com/images/movie/55174/315x210.jpg?3302", 28, "山の上の寄宿舎で暮らす12匹のくまの日常を描いた人気絵本シリーズ「くまのがっこう」をアニメ映画化。12匹のくまの末っ子ジャッキーは、きかんぼうでちょっとおしゃまな女の子。優しい兄たちに囲まれて楽しい毎日を送っていた彼女は、草原で女の子のくま・ケイティと出会い、意気投合する。ジャッキーは病弱なケイティを元気にするため、兄たちとともに大冒険を繰り広げる。", "2010-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЩЕСТОЕ ИЮЛЯ", "7／6 ソビエトの一番長い日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1918年７月に行われた第５回全ロシア・ソビエト大会の模様を再現し、指導者レーニンの思想と人となりを描いた政治色の濃い作品。諸外国からの干渉に、穏健に対応しようとするレーニンとブレスト講和条約の破棄を求める野党社会革命党との闘いを、リアルな筆致で描き出す。ソ連邦の政変、ゴルバチョフ大統領来日を機に、日本公開された。ビデオタイトルは「ソビエトの一番長い日／レーニンからゴルバチョフへ」。", "1968-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("津軽百年食堂", "津軽百年食堂", "", "", "http://image.eiga.k-img.com/images/movie/55954/315x210.jpg?2263", 0, "東京で暮らす青年・大森陽一が、さまざまな葛藤（かっとう）を乗り越え、青森県弘前市で100年続く老舗食堂を継ぎ、4代目店主となるまでを描くヒューマンドラマ。現代の陽一の姿とともに、明治時代の初代店主・賢治の人生が重ねられていく。お笑いコンビ「オリエンタルラジオ」の藤森慎吾と中田敦彦が、それぞれ陽一と賢治を好演。森沢明夫の同名小説を、「恋する女たち」「わが心の銀河鉄道　宮沢賢治物語」の大森一樹監督が映画化した。", "2011-04-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前桟橋", "喜劇・駅前桟橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1958年第１作「駅前旅館」から数えて24作目のシリーズ最終作。高松の第一観光丸の船長・徳之助に墓石屋の孫作、うどん屋の次郎、漆器店の三平の４人は“狸会”を結成している悪友連。ある日徳之助の息子・徳太郎が法律の勉強をやめてジャズをやると言い出した。徳之助は息子の決心を変えさせようと色の道を指南するが逆効果。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("City of Ember", "エンバー　失われた光の物語", "", "", "http://image.eiga.k-img.com/images/movie/54864/315x210.jpg?2451", 95, "トム・ハンクスが製作を務め、ジェニー・デュープロのSF小説「エンバー」を映画化したファンタジー・アドベンチャー。主演に「つぐない」のシアーシャ・ローナン、共演にティム・ロビンス、ビル・マーレイほか。地底都市・エンバーに暮らす人々は、地上に出ることのないまま発電機の光を頼りに生活していた。ある日、エンバーで停電が頻繁に起こるようになり、少女リーナは屋根裏で見つけた文書をもとにエンバーの出口を探して旅に出る。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サヨナラCOLOR", "サヨナラCOLOR", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "「連弾」以来となる竹中直人の監督作。高校時代憧れていた女性を担当することになった医師の七転八倒を、哀切とユーモアがすれ違うはかない情感と共に紡ぎ出す。情けなくも図太い主人公を竹中直人が好演し、原田知世、雅子、中島唱子といった女優陣も、陰影の深い演技を披露している。佐々木の勤める病院に、初恋の人・未知子が入院してきた。居酒屋のおかみを恋人に、女子高生と援助交際もする佐々木は純な想いで診察に向かう。", "2005-08-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Borrowers", "ボロワーズ／床下の小さな住人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "メアリー・ノートンによる児童文学作品「床下の小人たち」を映画化した冒険ファンタジー。ある家の床下で暮らしている小人のボロワーズ一家。幸せな生活を送っていた彼らだったが、悪徳弁護士の策略で家が取り壊されることになり……。小人たちと悪徳弁護士の闘いをコメディ・タッチで描く。監督は「ビルとテッドの地獄旅行」のピーター・ヒューイット。「ハリー・ポッター」シリーズの人気子役トム・フェルトンのデビュー作でもある。", "1997-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Letters from a Killer", "ボイスレター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "妻殺しの容疑で無実を主張しながら獄中生活を送る死刑囚のレイスは、４人の女性とボイスレター（カセットテープに肉声を吹き込む文通）を交わしていた。弁護士の奔走もあって第二審で無罪判決を勝ち取った彼のもとに、謎のカセットテープが届く。それは、自分以外の女性ともボイスレターでの文通を行っていたことを知った誰かからの死刑宣告だった。度重なるストーカー行為の果てに、レイスは連続殺人事件に巻き込まれていく。息もつかせぬ展開と最後まで犯人がわからないサスペンス・スリラー。ボイスレターを交換していた４人の女性には、キャスティングで真犯人を推測されないために知名度よりも役柄にふさわしい人選が行われた。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SEA GYPSIES", "サバイバル・ファミリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "改良トロール船で北大西洋横断の旅に出た一行が、出発の二週間後、暴風雨に襲われた。そして、ようやく流れ着いた無人島で彼らのサバイバル生活が始まる。厳しい大自然のなかで、強い絆に結ばれていく人々の姿を描くアドベンチャー・ロマン。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA SALLE DE BAINS", "浴室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "J・P・トゥーサンのベストセラー小説の映画化で、キートンを思わせる無表情のT・ノバンブルの存在感が強い印象を残す異色作。物語は浴室に閉じこもることの好きな若者の無為な日常と、ベニスへの旅行を描きながら、現代人の孤独を浮き彫りにしていく。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("英雄本色Ⅱ", "男たちの挽歌 II", "", "", "http://ecx.images-amazon.com/images/I/51cfwW5byPL._SL210_.jpg", 104, "親友マークの死を悼みながら服役していたホーは、刑事である弟キットに頼まれ偽札製造組織の潜入捜査に協力することに。重要証人セキをニューヨークに避難させるが、そこでホーは命を落としたマークの双子の弟ケンと出会う。亡き親友の双子の弟と凶悪組織に挑む二人の男のダンディズムが熱い復讐劇として描かれていく。後半は舞台を再び香港に戻し、男気と薬莢が飛び交うままドラマはスピードを増し、前作を遥かに凌駕する銃撃戦が展開されていく。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A History of Violence", "ヒストリー・オブ・バイオレンス", "", "", "http://image.eiga.k-img.com/images/movie/1084/315x210.jpg?3431", 96, "「ザ・フライ」「裸のランチ」のデビッド・クローネンバーグ監督が同名グラフィック・ノベルを映画化。田舎町の小さなダイナーで働くトムは、弁護士の妻と2人の子供たちと平穏な日々を送っていた。が、彼が強盗を倒したことから、妻は彼の過去に疑いを抱く。主人公トムを「ロード・オブ・ザ・リング」のビゴ・モーテンセンが熱演。アカデミー賞にはウィリアム・ハートが助演男優賞で、ジョシュ・オルソンが脚色賞でノミネート。", "2007-03-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEUTSCHLAND，BLEICHE MUTTER", "ドイツ・青ざめた母", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "西ドイツの女流監督が、自分の母親をモデルに、戦中戦後を生きた一人の女性の半生を描く。戦争によって夫と引き離されたヘレーナは、戦後再び夫と家庭生活を営むが、心の平和は戻ってはこず、彼女の心は荒む一方だった。娘の立場から母親の生きざまを冷静に見つめた硬質のドラマ。", "1980-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("C’ERAVAMO TANTO AMATI", "あんなに愛しあったのに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "「特別な一日」と並ぶ、E・スコラの1970年代における代表作。モスクワ映画祭金賞をはじめとする、数々の映画賞に輝いた一編である。第二次大戦中、アントニオ、ジャンニ、ニコラは、友情で結ばれたレジスタンスの同志だった。やがて終戦、３人は病院の担架係、弁護士の助手、教師と別々の道を歩むが、ルチアーナという一人の女を全員が愛してしまい……。1945年から1974年にいたる30年間の３人の涙と笑いの人生が描かれる。また、この作品はイタリア映画そのもののオマージュともとれて、V・デ・シーカ、F・フェリーニ、M・マストロヤンニが実名で特別出演しているのも見どころ。特に、フェリーニ、マストロヤンニによる「甘い生活」の噴水シーンの再現は、映画ファンにとってこたえられない。モスクワ映画祭金賞を受賞。", "1974-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OSTATNI ETAP", "アウシュビッツの女囚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "女性監督の世界的先駆者として活躍したヤクボフスカ監督が、自らの体験を映像化。アウシュビッツ強制収容所の苛酷な状況の中で必死に生き抜く女性たちの姿を、共感と愛情を込めて綴っていく。ナチスの戦争犯罪の深さを痛感させられるシリアスな作品。", "1948-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モリノキオク", "モリノキオク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "アイドル・小野真弓が主演する心癒される物語。屋久島を舞台に、ヒロインが成長する姿を美しい自然の中に描出する。駆け出しOLの亜生は、仕事で失敗したり祖母が寝たきりだったりとつらい毎日を送っていた。そんなある日、すっかり意気消沈している彼女を見兼ねた友人たちが、屋久島への旅行を計画。島を訪れた亜生は、森の中で自分にそっくりな女の子と出会うが……。小野真弓のみずみずしい演技にも注目。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大番 完結篇", "大番 完結篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "大ヒット・シリーズも、ついに最終編。1949年に東京証券取引所が再開。故郷に帰っていたギューちゃんは、再びひと旗あげようと株に手を出した。結果は大成功。朝鮮戦争の特需景気もあって、ギューちゃんは大金持ちになる。その勢いで、可奈子未亡人に求婚するが、彼女は結核に倒れた……。原節子が薄幸のマドンナを好演。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("窓際ヒーロー", "窓際ヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "ヒーローに憧れる青年が、とある老人と少女との交流を通して成長していく姿を描いた青春ドラマ。幼い頃からヒーローを夢見ながらも、東京の片隅の小さな喫茶店でピアノを弾くだけの冴えない毎日を送っている翔太。ある日、仕事をクビになってしまった翔太の前に、ピアノを習いたいという老人と、アクションスターを目指す天真爛漫な女子高生が現われる。彼らとの出会いをきっかけに、平凡だった翔太の日常が少しずつ変化していく。", "2010-06-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("21 TWENTY−ONE", "21TWENTY-ONE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ある事件を契機に故郷イギリスを捨て、ニューヨークに移り住んでいる21歳のケイティ。彼女を取り巻く人間たち、親友、家族、妻子ある男との愛のないセックス、そして彼女が愛してやまない麻薬常習者のボビーのこと……。主人公が観客に向かって語りかけるモノローグを多用しながら、少女から大人の女への変換期の心情を淡い思い出とともに綴った作品。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Habemus Papam", "ローマ法王の休日", "", "", "http://image.eiga.k-img.com/images/movie/58004/315x210.jpg?3028", 104, "「息子の部屋」「親愛なる日記」のナンニ・モレッティ監督が、ローマ法王のつかの間の休日を笑いと涙を交えて描くハートフルドラマ。ローマ法王が死去し、新しい法王を選出するため各国の枢機卿がバチカンに集まる。全員が心の中では面倒な法王に選ばれたくないと思うなか、誰もが予想していなかったメルビルが新たな法王に選出される。メルビルはプレッシャーのあまりローマの街へ逃げ出すが、街の人々と触れ合うことで人生において大切なものや法王の存在意義とは何かを見つめ直していく。", "2012-07-31", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("同窓会", "同窓会", "", "", "http://image.eiga.k-img.com/images/movie/53614/315x210.jpg?1886", 105, "映画プロデューサーの克之は、初恋を成就させて結婚した妻・雪の存在がありながら、新進女優との不倫の恋を楽しんでいた。雪との離婚はあっさりと成立するが、かつて雪に想いを寄せていた克之の親友・文太はそれを聞いて大激怒。そんな中、雪の体に異変が起こり……。「花より男子」シリーズの脚本家として注目を集めるサタケミキオの初監督作。サタケ自身が俳優名・宅間孝行で主演も務め、妻を「人のセックスを笑うな」の永作博美が演じる。", "2008-08-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEAL", "スティール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ち密な計画性と抜群の運動能力を兼ね備えた４人組の銀行強盗グループ。犯罪組織の大金が紛れ込んだ現金輸送車を襲ったことから、予期せぬ事態に巻き込まれる彼らの奮闘を描くクライム・アクション。「TAXi」のG・ピレス監督が、X−スポーツを見せ場にフィーチャーし、謎の脅迫者や美貌の女刑事らが入り乱れる物語をスリリングに盛り上げる。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRE DOWN BELOW", "沈黙の断崖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "米国環境保護庁EPAの調査官ジャックは調査中に殺された同僚の復讐のため、ケンタッキー州東部の山中にある町にやって来た。捜査を開始した彼は閉鎖された坑道に猛毒の産業廃棄物が捨てられていることを突き止める。美しい山々と町の住人を守るため彼の戦いが始まった。環境問題をテーマに、S・セガールが奮闘するアクション。「沈黙の戦艦」や「沈黙の要塞」と関係があるわけではない。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAISE THE TITANIC！", "レイズ・ザ・タイタニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "1912年、処女航海の途中、氷山に衝突して北大西洋の海中に没した豪華客船タイタニック号には、核戦略に重要な役割を果たす鉱石が積載されていた。そこで、極秘裏にタイタニック号引き揚げ作戦が始まった……。海中から巨大な客船が浮上するさまが壮観。実物の巨船と特撮を駆使した一編。", "1980-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アジール・セッション", "アジール・セッション", "", "", "http://image.eiga.k-img.com/images/movie/54564/315x210.jpg?4001", 60, "舞台は文明が発展せず、衰退した未来。刑事の父と2人で暮らしているヒヨコは美大を目指す女子高生。ある日、亡き母が描いた絵を父が勝手に処分したことで、ヒヨコは家出を決意する。行くあてのないヒヨコは、人々がテントで生活するアジールというスタジアムに迷い込み、そこでストリートチルドレンのリーダー、アキラと出会う。監督は元ミュージシャンのアオキタクト。", "2009-07-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遊びの時間は終らない", "遊びの時間は終らない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "防犯訓練で銀行強盗に扮した生まじめなヒラ巡査が、犯人役にのめり込みすぎ、暴走してしまったことから大げさに巻き起こる珍騒動を描く。「ファンシイダンス」の本木雅弘が融通の利かない謹厳実直な若き警官を快演、新境地を開拓した。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SCARLET LETTER", "緋文字", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "植民地時代初期のアメリカの清教徒の町で、温かみのあるディムズデール牧師に惹かれた人妻ヘスターは、夫の留守中に彼と関係を持ち、妊娠してしまう。私生児を宿したヘスターは生涯胸に“姦通”を示す“A”の文字を縫いつけられる……。N・ホーソーンの高名な原作をもとに、禁じられた恋の物語を詩情豊かに描いたサイレント後期の傑作。娘役から大人の女性に転身したL・ギッシュの鬼気迫る演技が見もの。ビデオタイトルは「リリアン・ギッシュの真紅の文字」。", "1926-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しあわせなら手をたたこう", "しあわせなら手をたたこう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "幼なじみで大親友の美女3人組が30歳を迎え、仕事や恋愛、友情の狭間で揺れ動き、本当の幸せを見つけるまでを描いた大人のラブ・コメディ。生き方の選択肢が広がった現代の30代女性をテーマに、まったくタイプの違う3人が、自分だけの幸せの形を求めて奮闘する姿に共感。30歳の独身女3人でマンションをシェアして暮らす未幸、千絵、のりこ。友情、恋愛、仕事に悩む、30歳の夏。3人の恋の行方は？", "2005-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Man Who Would Be King", "王になろうとした男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "　1880年代のインド。イギリス人のカーネハンは新聞記者のキプリングに数奇な話を聞かせる。カーネハンと友人ドレイボッドはヒマラヤの奥地、神秘の国カフリスタンに赴き、その王になろうとたくらむ。幾多の危機を乗り越え、ようやく現地にたどり着いたふたりは来賓として迎えられることに。やがてドレイボッドはアレクサンダー大王の息子と噂され、神として崇められるが……。男たちの野望と冒険、その顛末を描いたスペクタクル・ロマン。", "1975-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Layer Cake", "レイヤー・ケーキ", "", "", "http://image.eiga.k-img.com/images/movie/33504/315x210.jpg?2576", 105, "「スナッチ」「ロック、ストック＆トゥー・スモーキング・バレルズ」のスタッフが再集結し、プロデューサーのマシュー・ボーンが初めて監督を務めたクライム・サスペンス。「007／カジノ・ロワイヤル」で6代目ジェームズ・ボンドに抜擢されたダニエル・クレイグが麻薬ディーラーを演じ、下層のチンピラから上層のボスまで裏社会のいくつもの階層（レイヤー）を描き出す。", "2006-07-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("This is my life", "ディス・イズ・マイ・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "　「恋人たちの予感」の脚本家として知られ、のちに「めぐり逢えたら」「ユー・ガット・メール」などのラブコメディを世に送り出したノーラ・エフロンの初監督作。コメディエンヌを目指す2児の母が、才能を開花させ人気者になるにつれ、娘との間に溝が生じ始める……。主演は、「レナードの朝」でロビン・ウィリアムス演じる医師を支える看護婦を熱演したジュリー・カブナー。「スター・ウォーズ」シリーズのレイア姫役で一躍人気に火がついたキャリー・フィッシャーの姿も。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMMANUELLE ７", "エマニュエル７　ヴァーチャル・エロチカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "バンコクでの官能的な性の目覚めから、19年。あのエマニュエルは、パリ郊外の大邸宅にいた。そこではバーチャル・リアリティーの世界の中で、好きな場所や相手を選べる究極のセックスが体験できるのだった。若きエマニュエルを演じるA・ベラックが美しい。", "1993-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAMPAGNE", "シャンパーニュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "恋人との結婚を反対され、フランスへ家出してきた金持ちの娘ベティ、父親は彼女に世間の厳しさを教えるために破産したことにする。キャバレーに勤めたベティは客になるべくシャンパンを飲ませようとするなど悪戦苦闘。やがて人間的に成長し父も結婚を認める。ヒッチコックのイギリス時代の道徳喜劇。", "1928-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ring my bell", "ring my bell", "", "", "http://ecx.images-amazon.com/images/I/51GjqN9yobL._SL210_.jpg", 75, "青春と性をテーマにした「青春Hシリーズ」第9作。鮎子は、幼なじみのイサオと宇津保と組んでいるバンドのリハーサルに参加するため、森の練習場まで通うことが日課になっていた。そんなある日、イサオと宇津保は森で密造酒を作っていることを鮎子に打ち明ける。さらに鮎子の両親が旅行で不在だと知った2人は、合宿と称して鮎子の家で共同生活を始めるが……。神奈川出身の5人組バンド「core of bells」の山形育弘、瀬木俊が出演。", "2011-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜霧の慕情", "夜霧の慕情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "石原裕次郎と桑野みゆきの顔合わせによるムード・アクション。ヤクザの大幹部・堀部は組長が入獄中にその情婦と深い仲になる。もう一人の大幹部は敵方のボスと通じて、出所した組長を殺害する。堀部は単身、敵陣に乗り込み、ボスと大幹部を倒すが、自分も銃弾に倒れる。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海のトリトン", "海のトリトン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "かつてポセイドン族と海の覇権を争った、アトランティスのトリトン族の末裔・トリトンが、現在に生き延びていたポセイドン族と対立する……。手塚治虫の原作で1972年に放送されたTVシリーズを、再編集した劇場版。ただしシリーズ前半のみという不完全な形での公開となり、後のビデオ化でようやく前後編を通して観ることが可能になった。海洋アニメブーム初期に注目された少年冒険アクションだ。DVDタイトルは「海のトリトン オリジナル劇場版」。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KOYAANISQATSI", "コヤニスカッティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ナレーションも台詞もない映像と音楽だけの黙示録的な風景フィルム。前半の大自然から後半の都市の雑踏まで、北米大陸の文化をあらゆる撮影手法を駆使し宇宙的視野で描く。コヤニスカッティとはホピ族の言葉で“平衡を失った世界”。DVDは「カッツィ DVDスペシャルBOX」に収録。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Easy A", "小悪魔はなぜモテる?!", "", "", "http://ecx.images-amazon.com/images/I/51qoYvBlq3L._SL210_.jpg", 92, "「スーパーバッド 童貞ウォーズ」「ゾンビランド」のエマ・ストーン主演によるラブコメディ。カリフォルニアの女子高生オリーブ（ストーン）は、男の友人から頼まれて童貞喪失の相手を演じてあげるが、のちに実はセックスをしていないということがバレて、学校中から白い目で見られる。そんな状況を変えるため、オリーブはたまたま学校で習っていたホーソンの名作「緋文字」の主人公のように胸にAの刺繍を付けて登校する。監督は「俺たちチアリーダー！」のウィル・グラック。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LEGEND OF THE NINJA", "龍の忍者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "真田広之が香港に進出した忍者アクション。父を殺された忍者・玄武が、仇を捜して中国へ渡り、目指す相手・福佐とクンフーの達人の青年の二人と戦う。真田とコナン・リーの日・中アクション・スターの戦いが見もので、香港映画らしいテンポのいい作品になっている。", "1982-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Luisa", "ルイーサ", "", "", "http://image.eiga.k-img.com/images/movie/55574/315x210.jpg?1368", 110, "アルゼンチンの地下鉄会社メトロビアが主催した脚本コンクールの大賞受賞作を映画化した人間ドラマ。ブエノスアイレスの小さなアパートで猫のティノと暮らす60歳のルイーサは、夫と娘に先立たれながらも2つの仕事を掛け持ちして規則正しい毎日を送っていた。そんなある日、ティノが死に、職を2つとも失ってしまう。人付き合いが苦手なルイーサだったが、ティノの埋葬費をどうにかして捻出するため地下鉄の駅である行動に出る。主人公ルイーサにはベテラン女優レオノール・マンソ。監督は本作で長編デビューを果たしたゴンザロ・カルサーダ。", "2010-10-16", "22,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("現代やくざ・与太者の掟", "現代やくざ・与太者の掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "府中刑務所を出た勝又五郎は荒尾組に救われるが、五郎が手形のパクリを企てたことから荒尾組と対決することに。東映移籍後の菅原文太の主演第１作である。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALICE’S ADVENTURES IN WONDERLAND", "不思議の国のアリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "世界中で愛読されている同名の傑作童話を映画化。少女アリスが一匹のウサギを追いかけたことから始まる摩訶不思議な世界を、名優P・セラーズらがライブ・アクションで完全に再現。歌のシーンが全体の４分の１を占めるミュージカル・エンターテインメント。", "1973-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTERMEZZO", "間奏曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "アニタは有能なピアニスト。彼女は著名なバイオリニストであるホルゲル・ブランの娘にピアノを教えていた。これがきっかけとなって、彼女はホルゲルから熱愛されてしまう。さらに彼のツアーに同行してくれと頼まれて……。この作品がハリウッドの目に留まり、バーグマンのアメリカ映画デビューのチャンスを作ったといわれている。後に再びバーグマンを使って、アメリカでリメイクされた。", "1936-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La terra trema", "揺れる大地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 161, "　処女作「郵便配達は二度ベルを鳴らす」から6年を経て発表された、ルキノ・ビスコンティの意欲作。出演者は全員素人で、台詞もシチリア方言。事前に書かれた脚本はなく、シチリア出身作家の長編小説をベースにドキュメンタリータッチで撮影された。小さな村で漁師を生業としているバラストロ一家。寝ずの漁を行っても網元から搾取され、貧しい生活は好転しない。見かねた長男のアントニオは、網元からの独立を決意するが……。1948年ベネチア国際映画祭国際賞受賞。", "1990-01-19", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLAYING FOR KEEPS", "高卒物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "高校を卒業して社会に出た若者たちが、友人の一人が遺産として相続した田舎のオンボロ・ホテルを改造。若者のためのホテルにしようとする物語。目的に向かって団結していく若者たちの姿を、ポップな音楽をバックに、さわやかに描いている。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Public Eye", "フォロー・ミー", "", "", "http://ecx.images-amazon.com/images/I/51tqu0gx1lL._SL210_.jpg", 93, "「第三の男」「オリバー！」の名匠キャロル・リード監督がピーター・シェーファーの戯曲を映画化した恋愛ドラマ。元ヒッピーのベリンダは偶然出会った上流階級出身の会計士チャールズと結婚するが、慣れない上流の暮らしに息の詰まる毎日を送っていた。そんな中、ベリンダの行く先々に白いコートを着た男が現れる。尾行されていることに気づきつつもベリンダは男に興味を持ち始める。主演はミア・ファロー。", "1973-12-31", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新諸国物語 紅孔雀　第二篇 呪の魔笛", "新諸国物語 紅孔雀　第二篇 呪の魔笛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "財宝の在処を示す紅孔雀を巡り、美剣士の小四郎とその恋人・久美、謎めいた盲目の浮寝丸らが死闘を演じる第２部。呪いの術を使う妖婆・黒刀自ら多彩なキャラクターが見せ場を作っている。DVDは「新諸国物語　紅孔雀(1)」に収録。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Road to Roubaix", "ロード・トゥ・ルーベ", "", "", "http://image.eiga.k-img.com/images/movie/54414/315x210.jpg?3791", 75, "フランスのパリとルーベ間のおよそ260kmを1日で走る自転車プロロードレース“パリ～ルーベ”のドキュメンタリー。本レースは100年以上の歴史を持ち、他のレース常勝者も脱落するほどの過酷を極め、選手達は砂埃と擦り傷にまみれがら、命をかけてゴールを目指す。名だたる選手イバン・バッソやジョージ・ヒンカピーのインタビューを加え、そのレースの魅力を語る。", "2009-04-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE LITTLE WORDS", "土曜は貴方に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1920年代に活躍した作詞・作曲家コンビ、ハリー・ビルとバート・カルマーの伝記をもとにしたバック・ステージ・ミュージカル。ダンス・コンビのバートとジェシーは大成功を収めていた。ところが突然、バートが膝の骨を折りコンビは解消、ジェシーは彼のもとを去って行った。気を落としていたバートはある日、かつての盟友ハリーと再会し、作詞・作曲家コンビとして再スタートするが……。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("時の花形", "時の花形", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 59, "島津の下でシナリオを学び「二人の世界」で初めて島津作品の脚色を担当した山形雄策が、引き続き脚色を務めた作品。ノンフィクション文学が流行る戦時下、父からそういう小説を書けと責められた女主人公が、ふとしたことから軽演劇の小屋に入ると、そこでは自分そっくりの境遇の女主人公が出ていたのだが……。変わった趣向の設定を島津ならではの軽いタッチで演出した作品。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZIO　TOM", "ヤコペッティの残酷大陸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "モンド映画の奇才ヤコペッティが、アメリカの恥部、人種差別に切り込んだノンフィクション風の一編。奴隷制度に始まり、歴史上アフリカ系アメリカ人が受けてきた数々の虐待を、二部構成でドラマ化している。奴隷船にすし詰めにされ、商用として“繁殖”されるなど、悪趣味なまでに(？)残酷性にスポットを当てたヤコペッティ節は快調そのもの。", "1971-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きらい・じゃないよ", "きらい・じゃないよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「妹」「スローなブギにしてくれ」などで知られる脚本家・内田栄一が初監督に挑んだ８ミリ作品。ふと知り会った少女・七月と青年クーの奇妙な生活を通して、都会の片隅に生きる若い男女の曖昧な青春模様をモノクロームの映像で描く。これまで内田が描いてきた青春路線の集大成的な作品ともいえる。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃花", "桃花〈ドンハ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1987年「シバジ」でヴェネチア映画祭最優秀主演女優賞を受賞したK・スーヨン主演作品。醜女ケシが死刑囚と関係を持ち、自分の命とひきかえに生みおとした娘、桃花。養父母の愛を受けて成長した桃花は、父母の死、夫の死、放浪、浮気、悪病の五難を持つ災いのある子であると占い師に告げられる。", "1987-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO HAVE AND TO HOLD", "優雅な人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "落馬して下半身不随になってしまった男、ブライアンは、あとわずかな命だと自覚していた。その妻ジェーンは、いとこのマックスと深い仲であったが、それを死って一家を崩壊させ、財産を狙う親族がいた……。様々なドラマが展開するサスペンス。ハマー・フィルムの作品としては珍しいメロドラマ風の格調高い演出。", "1951-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONDUCT　ZERO　NO　MANNERS", "品行ゼロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "甘酸っぱくてほろ苦い、80年代の韓国を舞台にした青春グラフィティ。武侠アクション「ARAHAN〈アラハン〉」で主演を務めたリュ・スンボムが、本作で映画主演デビューを飾っている。伝説的な高校生番長がある日、メガネを掛けた女子高生に胸をときめかせ、一緒にギターを習いに行くことに。そんな時、学園のボスの座を狙う転校生が現れて、風雲急を告げることに。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　BOXER", "ボクサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "「マイ・レフトフット」「父の祈りを」に続いてJ・シェリダンとD・デイ・ルイスが組んだ感動作。抗争が絶えない北アイルランド情勢を背景に、過酷な社会状況にほんろうされる恋人たちの悲恋を描き出す。19歳の時にIRAのテロ活動に参加して逮捕された元ボクサーのダニーが、14年の刑期を終えて出所してきた。ボクサーとしての再起を誓う一方で、彼はかつての恋人マギーへの思いを再熱させていた。しかし、マギーはダニーの親友と結婚し、服役中の夫を待つ身、ほかの男性との恋など許されるはずもない。マギーへの愛を押さえつけるかのようにボクシングに情熱を注ぐダニーだったが、やがて彼女と人目を忍んで会うようになり……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Salt", "ソルト", "", "", "http://image.eiga.k-img.com/images/movie/55104/315x210.jpg?4478", 105, "アンジェリーナ・ジョリーとフィリップ・ノイス監督が、「ボーン・コレクター」以来となるタッグを組んだスパイ・アクション。CIA職員のイブリン・ソルト（ジョリー）は、謎のロシア男性の告白により、大統領暗殺のために送り込まれたロシアの二重スパイの嫌疑をかけられる。CIAから追われる身となったイブリンは顔を変え、髪の毛の色を変え、別人になりすまして真相を突き止めようとするが……。共演にリーブ・シュレイバー、キウェテル・イジョフォー。", "2010-07-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("何処へ", "何処へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "石坂洋次郎の小説の２度目の映画化。明朗快活な学園青春映画。田舎の中学校に若くてスマートな英語教師・伊能琢磨が赴任。町の芸者や同僚の女性教師たちは目の色を変える。そんな中で校長派と反校長派の争いが始まる……。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Legend of Bagger Vance", "バガー・ヴァンスの伝説", "", "", "http://image.eiga.k-img.com/images/movie/1274/315x210.jpg?4656", 126, "1928年、アメリカ南部サバンナ。かつて天才ゴルファーと謳われたジュナは、戦場での悲惨な体験により、ゴルフも富豪の娘である恋人アデールも捨てて隠匿生活を送っていた。一方、父の後を継ぎアメリカ最高のゴルフコースを完成させたアデールは、宣伝のため人気ゴルファー2人を招くエキシビション・マッチを企画、ジュナは2人に対抗する地元選手として試合に出場することとなる。今ではボールをまっすぐ飛ばすことすらできないジュナの前に、不思議な男が現れ5ドルの報酬でキャディを申し出る。", "2001-03-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴーストキス", "ゴーストキス", "", "", "http://image.eiga.k-img.com/images/movie/55634/315x210.jpg?4385", 72, "青春と性をテーマにした「青春Hシリーズ」第2作。数多くのピンク映画を手がけてきたいまおかしんじ監督がメガホンをとる。キャバクラ嬢の康子が仕事から帰宅すると、トイレから女子高生の幽霊のぞみが現れる。ほかの幽霊たちと部屋に入り浸っては宴会をするのぞみに腹を立てる康子だったが、彼女たちの告げ口により同棲中の恋人・修一の浮気が発覚し……。", "2010-08-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ПИРОСМАНИ", "ピロスマニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "グルジア地方の片田舎に暮らすピロスマニは、居酒屋やレストランの壁に絵を描いてはその日の糧としていた。ある日、モスクワからグルジアにやって来た画家により、中央の美術界に紹介された彼は、モスクワへ向かうが、権威主義的な美術界から相手にされず、傷ついてグルジアに帰ることに。周囲の人たちは、温かく見守ってくれるが、彼は失意の日々を送るのだった。20世紀初頭のロシアに実在したニコ・ピロスマニの人生を淡々と描き、観る者に新鮮な感動を与えてくれるグルジア映画。", "1969-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男涙の破門状", "男涙の破門状", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "1960年代半ばから1970年代前半にかけ、ヤクザ映画の大スターとして人々を熱狂させた鶴田浩二の主演作。６年ぶりにシャバに戻った主人公が、組の資金を持ち逃げして破門された義兄弟を探し出して、真相を確かめるべく九州へ向かう。桜町弘子が料亭のおかみに扮して、しっとりした色気をみせる。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVERYBODY’S ALL—AMERICAN", "熱き愛に時は流れて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "1956年、全米カレッジ・フットボールのオール・アメリカン・ヒーローに選ばれ、プロへの道を約束されたギャビンと、“南部の女王”に選ばれ、キャンパスの花となったバブス。二人は周囲の期待通りに結ばれ、幸福な日々を送るが、やがて二人の間に溝ができはじめ、すれちがいの毎日を過ごすようになった時、バブスに思いをよせるギャビンの甥ドニーの存在が明らかになる……。ギャビン、バブス、ドニーの３人の男女の愛と栄光、そして希望と挫折を25年間という時代のなかに映し出した、スタンダードともいえるアメリカン・ラブ・ストーリー。T・ハックフォード監督が「愛と青春の旅だち」に続いて再び取り組んだ、一大恋愛叙事詩。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山の音", "山の音", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "原作は川端康成が戦後最初に発表した同名小説。とある中流家庭を舞台に、老境に入った男が、同居する若く美しい息子の嫁に抱く複雑な感情を綴っている。下町を愛し、市井に暮らす人々の日常を描いてきた成瀬の作品歴においては異色の題材。だが、日本的なものをめぐる川端独特の美意識は、端正な人物像や画づくりに反映し、また原作の物語展開は成瀬固有の大胆かつ繊細なエロチシズムを導き出して、川端文学の映画化としても、成瀬巳喜男監督作品としても、第一級のものとなっている。俳優たちも持ち味を生かした素晴らしい演技で、とりわけヒロイン・原節子の匂いたつような美しさは忘れがたい。成瀬作品では清純な人妻や未亡人を演じている彼女であり、しばしば性的な含みが持たされるが、この作品ではそうした人妻という存在のなまめかしさが強調され、女学生のような三つ折りソックスをはいた普段着姿が卑猥なまでに官能的である。他に女を囲い彼女を顧みない夫の上原謙、義父役の山村聰もそれぞれ好演。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　キラキラ星の涙", "それいけ！アンパンマン　キラキラ星の涙", "", "", "http://ecx.images-amazon.com/images/I/61eCsOgH8XL._SL210_.jpg", 75, "やなせたかしの絵本を原作とするテレビアニメ「それいけ！アンパンマン」の劇場版第1作。パトロール中に嵐に巻き込まれたアンパンマンは、落下しそうになったところを宇宙船に助けられる。しかし、その宇宙船も稲妻に打たれて墜落してしまい、今度はアンパンマンが乗組員のマルデ・ヘンダーを救出。ナンダ・ナンダー姫と一緒にキラキラ星からやって来たというマルデ・ヘンダーは、ドロンコ魔王に奪われた宝石「キラキラの涙」を探していた。", "1989-03-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天堂回信", "北京の天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "６歳の少年チェンチェンは、郵便配達のおじいちゃんと二人暮し。そこに海外赴任していた母親が帰ってきた。息子の将来を思い、教育熱心で洋風の生活に馴染ませようとする母親に反発するチェンチェンだったが……。老人と少年の素朴な心の触れ合いの中に、現代中国の抱える様々な問題をさりげなく織り込んだ家族ドラマ。だが何といっても魅力的なのは、ぷくぷくほっぺのチェンチェン少年の愛らしい表情。むくれたり笑ったり大泣きしたり。くるくる変わる表情と健気な瞳に、思わず顔がほころぶ。", "1992-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shame", "SHAME シェイム", "", "", "http://image.eiga.k-img.com/images/movie/57624/315x210.jpg?2961", 101, "ニューヨークを舞台にセックス依存症の男の苦悩を描き、2011年・第68回ベネチア国際映画祭でマイケル・ファスベンダーが主演男優賞を受賞したドラマ。ニューヨークでエリートサラリーマンとして働くブランドンは、セックス依存症から抜け出せずにいた。そんなブランドンのもとに、恋愛依存症でリストカット癖のある妹シシーが転がり込んでくる。互いに相容れない2人はともに生活することでますます孤独を深めていき、事態は悪い方向へと転がっていく。妹シシー役でキャリー・マリガンが共演。監督はイギリスの新鋭スティーブ・マックイーン。", "2012-03-10", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOING　WEST　西へ…", "GOING　WEST　西へ…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "37年ぶりの主演作となる淡島千景が、快活なおばあちゃんに扮するロード・ムービー。一人暮らしのリュウはボヤ騒ぎがもとで長男夫婦と同居する。口うるさい息子たちとの生活に堅苦しさを感じた彼女は、夫の形見のミニ・クーパーに乗って、四国の松山へ初恋の人に会いに行く。ところが、途中から若い恋人たちを同乗させたことから、様々なトラブルに見舞われ……。リュウと交流を深めていくカップルに大沢樹生と藤谷美紀が扮し、作品にさわやかな印象を与えている。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DASFORUSH", "行商人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "イランの名匠M・マフマルバフ監督が手掛けた、３話からなるオムニバス映画。第１話は、爆撃されたボロボロのバスに暮らす貧しい夫婦に５人目の赤ちゃんが生まれ、やむなく赤ちゃんを捨てる決心をしたものの、赤ちゃんを置くたびに何かが起きてうまくいかないという話。第２話は、車椅子の老いた母親とアパートで暮らす精神的に不安定な若者の話。若者が、銀行に行こうとして外に出るが、道を渡ることもうまくできない。彼は、外界で生きる術を教えられていなかった……。第３話は、ギャングの殺人現場を目撃してしまった行商人の話。ボスの前に連れて行かれた行商人は、これからの自分の行く末をあれこれ想像するが、それはどれも最後には彼が死ぬというものだった……。", "1987-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOR LOVE OR MONEY", "恋のクレジット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "母親が決めた３人の花婿候補と３人の娘たちを、うまく結婚させるよう依頼されたプレイボーイで独身の弁護士の活躍ぶりを描くラブ・コメディ。主演はK・ダグラスとM・ゲイナー。最後に用意されたどんでん返しも楽しい、軽いタッチの娯楽映画。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GORIKY PARK", "ゴーリキー・パーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "モスクワのゴーリキー公園で発見された３体の死体。捜査を担当した若き警官は、裏にKGBの陰謀をかぎつけた。イギリス推理作家協会賞受賞の同名小説を映画化した、サスペンスフルなアクション・ミステリー。W・ハートの渋い演技に注目。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大脱出！", "大脱出！", "", "", "http://image.eiga.k-img.com/images/movie/54744/315x210.jpg?4267", 118, "携帯サイト「脱出ゲーム＠ガットメール」でプレイできる様々な趣向の脱出ゲームをモチーフにした11本のショートムービーからなるオムニバス作品。「運命人間」「グロヅカ」の西山洋市、活弁映画監督の山田広野、「すみれ人形」の金子雅和ら各方面で活躍する気鋭の若手監督が集結し、脱出ゲームの世界観をそれぞれ独自の表現方法で映像化。ジャンルもミステリー、サスペンスからコメディまで多彩な作品が揃う。", "2009-10-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獣人雪男", "獣人雪男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「ゴジラ」の香山滋の原作を得て、日本アルプスを舞台に秘境テーマと雪男伝説をミックスした作品。「ゴジラ」の世界的大成功で意気揚がる円谷英二をはじめとする東宝特撮陣が、総力を結集した意欲作だが、題材的にいささか無理があった。根岸明美が魅力的。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Justice League of America", "ジャスティス・リーグ・オブ・アメリカ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "スーパーマン、バットマン、ワンダーウーマン、フラッシュ、グリーンランタンらアメコミのスーパーヒーローが結集したオールスター・チームが悪と戦う。", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("佐渡テンペスト", "佐渡テンペスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE DREAM", "愛にふるえて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "弟を失った悲しみから世捨て人のようになってしまった元ロック・ミュージシャンが、海で不思議な壷を拾う。その壷には美しい妖精“チャイナ”が住んでいて、二人は恋に落ちる……。ファンタスティックな映画のストーリーを地でいくように、D・レインとC・ランバートはこの共演後に結婚したが、その後1994年離婚している。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I WAS A TEENAGE VAMPIRE", "ティーンバンパイア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ご存じバンパイアの、恐ろしげなイメージを一掃。愛すべき好青年として復活させた青春ホラー・コメディ。平凡な高校生ジェレミーは、ある夜、夢で見る美人そっくりの女性に欺かれ、気付づいたら首筋をガブリ!?“バンパイアになるための手引書”が愉快。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ETERNAL EVIL OF ASIA", "虐殺13大淫魔術", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "メイとその恋人らの４人は、香港からタイのバンコクへ遊びに来ていた。しかし、街中で思わぬ事故を起こしてしまい、タイの女性を死なせてしまう。それを知ったその女性の兄が、復讐のためにありとあらゆる妖術を使い、メイたちを襲い始める。恋人をも囚われてしまったメイは、彼を救うために友人のマジシャンの力を借りて、復讐の鬼と化した男に挑んでいく。針千本魔人や宙を飛ぶ女観音妖人、コンドームマンなど、SFXやCGを駆使したヘンな怪人たちが次々に登場するアクション・ムービー。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUDGE DREDD", "ジャッジ・ドレッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "時は2139年、社会の治安は逮捕から刑の執行までをこなすエリート（＝ジャッジ）によって守られていた。そのジャッジ・システムの秘密を探っていたレポーターが殺され、伝説的な超エリート、ジャッジ・ドレッドに嫌疑がかかる。有罪判決を受けたドレッドは、かつて自分が捕えた犯罪者ファーシーとともに捕らわれの身に。そこへドレッドの恩人ファーゴ長官が現れ、彼に意外な秘密計画を打ち明ける。スタローン主演のコミック風SF活劇。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HALF A SIXPENCE", "心を繋ぐ6ペンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 147, "イギリスの仕立屋に勤めるアーサーは幼なじみのアンに愛情のしるしとして二つに割った６ペンス銀貨の一つを渡す。やがてアーサーは大金の遺産相続人となり、アンのことを忘れはじめるが……。SF作家として有名なH・G・ウェルズの自伝的小説をもとにした舞台ミュージカルを映画化した心温まる作品。", "1967-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("For the Love of Mike", "力漕一挺身", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "フランク・キャプラ監督の長編第3作で、後に「或る夜の出来事」で再びキャプラ監督と組んで大成功をおさめた女優クローデット・コルベールの映画デビュー作。同じアパートに住む仲の良い3人の男性。彼らは捨て子を発見し、マイクと名付けて3人で育てることになる。慎ましい暮らしながらも大事に育てられ、やがて大学へ入学したマイク。彼はボート部の主将となって活躍するが、ある日賭けに負けて借金をつくってしまう。", "1927-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クール・ディメンション", "クール・ディメンション", "", "", "http://image.eiga.k-img.com/images/movie/33374/315x210.jpg?4456", 72, "暗殺者として育てられた3人組の女性たちの苦悩と戦いを描くお色気サスペンスアクション。女暗殺者3人組はある大物政治家の汚職の情報を握っている男を暗殺するように命じられる。任務を果たすべく着実に標的に近づく3人だったが、あと一歩のところで女チーマー軍団の奇襲に遭う……。監督は特撮助監督として「亡国のイージス」（05）「日本沈没」(06)などを手掛け、本作で劇場映画デビューを果たした石井良和。", "2006-07-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金色のクジラ", "金色のクジラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "白血病と闘う家族の実話をもとにした作品。小学校への入学を楽しみにしていた裕一を襲った病気は、急性リンパ性白血病。絶望する父・正樹と母・早苗。仕事と家事に加え、病院通いの毎日に疲れきる早苗、仕事が忙しく見舞いにも行けない正樹、一人放っておかれる兄・努。しかし、周囲の励ましのなか、病気と闘う裕一や他の患者たちの姿は、正樹と早苗に病気に立ち向かう決意を促していく。二人は裕一のために、努からの骨髄移植の道を選ぶ。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("なくもんか", "なくもんか", "", "", "http://image.eiga.k-img.com/images/movie/54314/315x210.jpg?4403", 134, "幼い頃父親に捨てられた祐太は、東京下町・善人通りの惣菜屋「デリカの山ちゃん」の店主夫婦に養子として育てられ、今では2代目店主となって商店街を盛り上げていた。そして密かに思いを寄せていた初代店主夫婦のひとり娘・徹子と結婚にこぎつけた祐太は、生き別れた弟で人気お笑い芸人「金城ブラザーズ」の祐介の存在を知り、再会を果たすが……。「舞妓Haaaan!!!」の水田尾伸生監督・脚本の宮藤官九郎、主演の阿部サダヲが再結集。", "2009-11-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BRONX EXECUTIONER", "レプリコップ 未来刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "近未来のN.Y.は核戦争や天災の後遺症で荒廃していた。人口も極端に減り、労働力は模造人間レプリカントと人造人間アンドロイドに頼るという状態。そしてそれらを悪用した犯罪もまた急増していた。スーパー・シェリフと呼ばれる保安官ジェームズは悪の根源となっている女レプリカントに挑もうと決意をする。", "1985-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES HE´ROS SONT FATIGUE´S", "悪の決算", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "アフリカのリベリアへ流れて来た男たちが、ダイアモンド争奪戦を繰り広げる。アクションの中、人間の醜い色と欲を描き出したドラマ。戦時中はともに空の勇士だった敵と味方が熱い友情に結ばれるという典型的な物語ながら、心地良い作品だ。", "1955-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エロティックな関係", "エロティックな関係", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "パリの裏路地にあるビルで、探偵事務所を開いているKISINと秘書RIE。仕事もなく、観光ガイドで生計を立てている彼らの前に一人の男が現れた。奥山と名乗るその男は、愛人の浮気調査を依頼する。彼らはすぐに行動に移るが調査を進めるうちに不可思議な出来事に遭遇していく……。何かと話題を呼ぶ男、内田裕也がビートたけし、宮沢りえという異色のキャストを得て製作したミステリアス・ムービー。「キスより簡単」「われに撃つ用意あり…」「寝盗られ宗介」に続き若松孝二監督は、ここでも演出にさり気ない切れ味を見せている。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鳴門秘帖", "鳴門秘帖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "昭和元年のマキノ映画・日活・東亜キネマの競作以来、何回となく映画化された吉川英治の伝奇小説に基づく時代劇の大作。徳川十代将軍・家治の頃、公儀隠密の法月弦之丞は虚無僧姿に身を装い、反幕の動きがある阿波藩の動向を探るべく徳島に渡るが……。衣笠貞之助のツボを心得た演出で一級の娯楽作品に仕上がった。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("炎の中忍試験！ナルトVS木ノ葉丸!!", "炎の中忍試験！ナルトVS木ノ葉丸!!", "", "", "http://image.eiga.k-img.com/images/movie/56904/315x210.jpg?4697", 13, "岸本斉史の人気コミックが原作のアニメーション劇場版第8作「劇場版NARUTO ナルト　ブラッド・プリズン」と同時上映の短編。迫り来る「第4次忍界大戦」に備え実力アップを目的に実施されることになった「中忍試験」。木ノ葉隠れの里の下忍・木ノ葉丸も試験を受けることになるが、そこであこがれの忍者ナルトと対決することに。", "2011-07-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE PAYS DES SOURDS", "音のない世界で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "パリのろう学校に通う子供たちが補聴器具を使って自分の声をヴィジュアル化し発声法を学んでいる。カメラはろう学校の生徒、手話教師のプーラン先生、そしてろうあ者同士で結婚するカップルという、三者の日常を現在進行形で追いかけながら、彼らの文化、生活をありのままに描き出す。淡々としながらも、根底にはろうあ者への共感や親愛に満ちあふれている本作は、世界各地で評価され、1994年ボンベイ国際映画祭でグランプリを獲得した。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Space Truckers", "スペース・トラッカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "会社をクビになった宇宙トラックの運転手が、怪しげな荷物を地球に運ぶ仕事を請け負ったことから、恐怖の殺人マシーンと対決するハメに陥るSFコメディ。幌馬車ならぬトラックが宇宙空間をスイスイ走る奇抜なストーリー展開に加え、セットや音楽まで西部劇風に統一した遊び心は、B級映画ファンならずとも爆笑必至。気のいいトラック野郎に扮したD・ホッパーが、息子ほども年下のS・ドーフをライバルに、美人ウエイトレスをめぐる恋の三角関係を陽気に演じている。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白い手", "白い手", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "椎名誠の同名小説の映画化で、昭和30年代の悪ガキたちの奮闘ぶりと、淡い初恋を描く。千葉の外房にある小さな港町。小学５年生のマサルのクラスに降清という転校生がやって来た。仲良くなった二人は、登校途中にある洋館の２階にいる白い手の少女に好奇心を持ちはじめるが……。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聖女伝説", "聖女伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "喧嘩で顔を傷つけられ、その屈辱によって秘めたる獣性と野望を甦らせた男が、アウトロー社会でめきめき頭角を現していく姿を描く、ハードボイルド・ロマン。主役の郷ひろみと、年上の女・岩下志麻、年下の女・小野みゆきとの激しいセックス描写も話題に。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("老人と海", "老人と海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "漁師として一人小舟を操り海に生きてきた82歳の老人・糸数繁の孤独な闘いを飾り気のない厳しい映像の中に描いたドキュメンタリー。ヘミングウェイの小説を思わせる漁師の姿や与那国の海神祭などが綴られていく。主人公の糸数繁氏が映画の完成後、漁船の上で孤独な死を迎えたことが報道され話題となった。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("69 sixty nine", "69 sixty nine", "", "", "http://image.eiga.k-img.com/images/movie/1234/315x210.jpg?4251", 113, "87年刊行の村上龍の自伝的同名小説を、PFFアワードで注目を集めた「青　chong」の李相日が監督、「木更津キャッツアイ」の宮藤官九郎の脚本で映画化。ベトナム反戦運動が高まり、大学紛争に揺れる69年。長崎県佐世保市の高校生ケンは友人のアダマとイワセを誘い、演劇とアートとロックのフェスティバルを開催しようと計画。これはあこがれのレディ・ジェーンの気を惹くのが目的だったが、警察の介入を招く事態になっていく。", "2004-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RIVER OF DEATH", "デス・リバー／失われた帝国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "A・マクリーンの『死の激流』を原作に、B級映画の雄であるS・カーヴァー監督が映画化した冒険アクション映画。アマゾン奥地の都市伝説とナチの残党とを絡ませ、その謎を解こうと激流を逆上る男たちの冒険を描いていく。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレージー作戦・先手必勝", "クレージー作戦・先手必勝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "東宝の“クレージー”シリーズの第１作。口八丁手八丁の上田ヒトシは資本いらずの商売・喧嘩の仲裁よろずまとめ屋を始める。上田を社長に一騎当千のツワ者が７人集まるが、最初は犬の喧嘩や夫婦喧嘩の仲裁が精いっぱい。やがて大口の仕事が舞い込んで……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("晩鐘／EVENING BELL", "晩鐘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "東京国際映画祭出品作「最後の冬」(1986）に続くウー・ツーニュウの第５作。日中戦争が終わった1945年。八路軍の死体収容小隊の５人は、まだほら穴に潜む33名の日本兵の姿を見つける。八路軍兵士たちはほら穴を包囲し、すでに食料も尽きた日本兵たちに対して説得工作を試みるのだが……。", "1987-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("T-ARA First Show Case 3D SHIBUYA-AX 2011.7.5", "T-ARA First Show Case 3D SHIBUYA-AX 2011.7.5", "", "", "http://image.eiga.k-img.com/images/movie/57044/315x210.jpg?4017", 56, "2010年7月に韓国でデビューし、11年9月28日に日本デビューする韓国の7人組女性アイドルグループ「T-ARA」が、日本デビューに先がけ7月5日に東京・渋谷AXで行ったショーケースライブを劇場上映。「ねこダンス」で人気の日本デビューシングル「Bo Peep Bo Peep」などを披露したライブの模様を3Dで上映する。", "2011-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハウルの動く城", "ハウルの動く城", "", "", "http://image.eiga.k-img.com/images/movie/1434/315x210.jpg?1459", 119, "英国のファンタジー作家ダイアナ・ウィン・ジョーンズの「魔法使いハウルと火の悪魔」の映画化作。父親の帽子店で帽子を作って暮らしていた18歳のソフィーは、荒野の魔女の呪いで90歳の老婆の姿になってしまう。彼女はハンサムだが気弱な魔法使いハウルと出会って、彼の居城でいっしょに暮らすようになるが、その城は4本足で歩く動く城だった。ソフィーの声を倍賞千恵子、ハウルの声を木村拓哉が担当。", "2004-11-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blue Valentine", "ブルーバレンタイン", "", "", "http://image.eiga.k-img.com/images/movie/55894/315x210.jpg?2985", 112, "仕事が芳しくないディーンと、長年の勉強の末に資格を取り、病院で忙しく働くシンディの夫婦は、娘のフランキーと3人暮らし。2人はお互いに相手に不満を抱えていたが、それを口に出せば平和な日常が崩れてしまうことを恐れていた……。夢や希望にあふれていた過去と現在を交錯させ、2人の愛の変遷を描くラブストーリー。主演はライアン・ゴズリングと、本作で第83回米アカデミー主演女優賞にノミネートされたミシェル・ウィリアムズ。", "2011-04-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　SONG　OF　SONGS", "恋の凱歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "貧しい農家の娘として育ち、孤児となってベルリンの叔母の家に身を寄せたリリー。彼女は若き彫刻家リチャードのモデルを務めたことから、彼と将来を誓い合う関係になる。しかしリチャードは経済的な理由で身を引き、リリーは悲しみに暮れていく。M・ディートリヒが初めてジョゼフ・フォン・スタンバーグ以外の監督と組んだ悲恋もの。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("六條ゆきやま紬", "六條ゆきやま紬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "北国のある村を舞台に、格式を誇る六條家の九代目当主・久右衛門のもとに嫁いだ芸者・いねが、周囲の中傷にも負けず、夫の死後は夫の意志を引き継いで、ゆきやま紬一筋に生きていく姿を描く。いねを陰から慕い支える治郎を、当時“駅前シリーズ”など喜劇の多かったフランキー堺がシリアスに好演。岡崎宏三のカメラが映し出すモノクロの風景が美しい。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MS.４５（ANGEL OF VENGEANCE）", "天使の復讐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ニューヨークの縫製工場に勤めるろうあで内気な少女ターナー。彼女は帰宅途中、仮面を被った男にレイプされ、さらに部屋に戻った時、待ち伏せていた強盗に再び犯された。それ以来、被害妄想に陥った彼女は街をさまよい、次々に男たちを殺していく。「フューネラル」のA・フェラーラは、猟奇的な設定を借りながら、ここでも“罪”をテーマに言及している。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿波の踊子", "阿波の踊子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "阿波の豪商・十郎兵衛は欲と金に目がくらんだ悪家老のために、密貿易の抜荷買の海賊という汚名を着せられ、阿波踊りの日にはりつけに処せられる。数年後、海賊の群れに飛び込み腕をみがいた十郎兵衛の弟が、兄の復讐に燃えて町へ帰って来る。原作・脚色の観世光太は山上伊太郎の変名で、サイレント時代にマキノ雅広とのコンビにより“浪人街”シリーズ、「首の座」「崇禅寺馬場」など数々の名作を作り出してきたが、この作品が最後のコンビとなった。なお、同じ題材を、マキノ雅広監督は1957年東映で、大友柳太朗の主演で再映画化した。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAYA！　宇宙一のおせっかい大作戦", "WAYA！　宇宙一のおせっかい大作戦", "", "", "http://image.eiga.k-img.com/images/movie/56944/315x210.jpg?4096", 101, "名古屋市西区に実在する円頓寺（えんどうじ）商店街を舞台にしたご当地ムービー。昔ながらの街並みが残る円頓寺商店街は空き店舗も増えているが、それでも毎日がにぎやかに営まれている。商店街青年団の団長に選ばれたクリーニング屋の勘太郎は、頑固一徹な商店街会長シゲさんの営む下駄屋が30周年を迎えるにあたり、シゲさんがかつてケンカ別れした親友と再会させて感動させようと、商店街のメンバーたちと「宇宙一おせっかい大作戦」を練るが……。主演はお笑いコンビ「スピードワゴン」の井戸田潤。名古屋を拠点に活動する人気アイドルグループ「SKE48」の松井珠理奈と矢神久美が映画初出演を果たす。", "2011-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花くらべ狸御殿", "花くらべ狸御殿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "1939年「狸御殿」でオペレッタ喜劇の分野に新境地を開き、1942年「歌う狸御殿」を再び大ヒットさせてミュージカルものの第一人者となった木村恵吾が取り組んだミュージカル映画。舞台はとある狸の国。料亭“ボン”に突然現れた黒太郎という美青年が、その店の給仕となり、持ち前の歌と踊りのうまさで人気者に。そこにこの国の女王がやって来て……。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛と誠　完結篇", "愛と誠　完結篇", "", "", "http://ecx.images-amazon.com/images/I/61aHaLPxqzL._SL210_.jpg", 91, "1970年代に少年マガジンに連載されて大ブームを巻き起こした、梶原一騎・原作、ながやす巧・画による名作コミック「愛と誠」の実写映画化シリーズ3部作の完結編。太賀誠との戦いに敗れた影の大番長・高原由紀が死を選び、つかの間の平和が訪れた花園実業高校。そんなある日、ヤングマフィア「緋桜団」を率いる砂土谷峻が誠に挑戦状を叩きつけてきて……。メガホンを取るのは、本作が長編監督デビューとなる南部英夫。", "1976-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("姐御／ANEGO", "姐御／ANEGO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "これまでに２度映画化され、根強いファンを持つ女任侠ドラマがアクション全開の斬新な装いで３度目の映画化。極妻シリーズを経て新たな姐御像に挑む高島礼子の熱演と妖艶なたたずまいが印象的だ。横浜の老舗一家、磯島組の大幹部をする夫を何者かが殺害、未亡人になった妻の愛は、子どもと生きる決意を固める。しかし先代姐の願いで彼女は３代目代行を引き受け、内部抗争の収拾に乗り出す。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲド戦記", "ゲド戦記", "", "", "http://image.eiga.k-img.com/images/movie/33584/315x210.jpg?1343", 115, "アーシュラ・K・ル＝グウィンによる世界的ファンタジー小説を原作に宮崎駿監督の長男宮崎吾朗が初メガホンをとったスタジオジブリの大作アニメ。世界で最も偉大な魔法使い「大賢人」と呼ばれているハイタカ（別名ゲド）は、世界に災いをもたらす源を探し出すために旅を続けていた。そんな中、ハイタカはエンラッドの王子アレンと出会うが、アレンは父である国王を刺し、国を出たばかりだった……。", "2006-07-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOYA", "情熱の画家ゴヤ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "18世紀末、カルロス４世が治世する動乱のスペインに生きた宮廷画家ゴヤの生涯を描く。『裸のマハ』のモデル、アルバ公妃との熱烈な恋を中心に、宗教裁判や民衆の生活をも絵の題材としたゴヤの芸術観も紹介。苦難と波乱に満ちた彼の半生を情熱的に再現した力作。", "1971-01-01", "12,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兄の花嫁", "兄の花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "見合い結婚をした銀行員・原田浩と下町育ちの春枝。だが浩の妹・昌子は、一度の見合いで結婚を決めた兄の感覚に理解しがたいものを感じていた。一方、春枝の母や祖母は、原田家の家風に早くも違和感を持ち始める。ところが、当の浩と春枝はそんな周囲の心配をよそに幸福な結婚生活にひたっていて……。小市民ものの巨匠・島津が戦時中にもかかわらず、なお自分の本領にこだわろうとした作品。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flaming Star", "燃える平原児", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "　テキサスの牧場に暮らすバートン一家。その次男ペイサーは白人の父サムとインディアンの後妻ネディの間に生まれた混血児だった。カイオワ族の一隊によって白人が襲われたことを機に、村の人々はインディアンを警戒し、ペイサーとネディに対する風当たりも強くなる。白人とインディアンの争いは激しさを増し、やがてネディは銃弾に倒れることに……。エルビス・プレスリーが人種間の争いに苦悩する混血児青年を熱演。その主演映画の中でも最も異色と言われるシリアスな西部劇。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦国少女伝　妖怪忍者　忍", "戦国少女伝　妖怪忍者　忍", "", "", "http://image.eiga.k-img.com/images/movie/57994/315x210.jpg?1941", 0, "「路地裏のコッペリウス」のかわさきひろゆき監督が手がける忍者アクション。戦国時代の人里離れた山中。人々から恐れられる妖術の使い手である母親の鴉と剣術修行中の勝秀と3人で暮らす忍は、2人以外の人間を知らず、父親についても鴉から「いずれ話す」と言われたままだった。そんな時、非道な人体実験を繰り返す外道忍者の平賀源内が、からくり技術を使って死人をよみがえせる術を編み出し、機械忍者軍を結成。戦国大名に取り入り天下を取ろうと、大名たちが恐れる鴉の首を狙って山中に攻め入ってくる。", "2012-04-07", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’E´VE´NEMENT LE PLUS IMPORTANT DEPUIS QUE L’HOMME A MARCHE´ SUR LA LUNE", "モン・パリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "パリの下町のモンパルナス。美容院を持っているイレーヌと、自動車教習所を経営するマルコは恋人同士。ところがある日、男のマルコのお腹が大きくなって妊娠の兆候が……。男が妊娠するという珍事をテーマにしたラブ・コメディ。", "1973-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青の炎", "青の炎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "演劇界の巨匠、蜷川幸雄が21年ぶりにメガホンを執り、犯罪に走った少年の心理を描く貴志祐介のミステリー小説を映画化。人気アイドルの二宮和也、松浦亜弥が披露する繊細な演技にも注目が集まった。高校生、秀一の家に母がかつて離婚した男、曾根が転がり込み、傍若無人な振る舞いを繰り返す。何とか母と妹を守りたい秀一は、社会のシステムが頼りにならないことを知り、自らの手で曾根を殺すために完全犯罪を計画する。", "2003-03-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("七人のおたく cult seven", "七人のおたく cult seven", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "PFFにも応募したことがあるという、映画好きの“ウッチャンナンチャン”が初主演した作品。井加江島にさらわれた赤ん坊を救うため、ミリタリーおたく、格闘技おたく、パソコンおたくなど、７人のおたくが、立ち上がるというアクション・コメディ。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CYBER TRACKER", "サイバー・ウォリアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "時は近未来、コンピュータが裁判を行っていた。この制度を徹底するために犯罪者を追跡するアンドロイドがサイバーコア社によって開発された。シークレットサービスのエリックは同僚ロスの裏切りから、コンピュータの犯罪者リストに載せられてしまう。何とか、逃げることのできたエリックはサイバーコア社に反感を持つコニーらと出会い、彼らと協力して自らの無実を晴らすために立ち上がるのだった。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("郡上一揆", "郡上一揆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "江戸時代の三大一揆の一つであり、唯一藩主更迭という農民の勝利に終わった一揆である郡上一揆を史実に基づいて映画化。宝暦４年、美濃国郡上藩では出来高によって年貢を変える“検見(けみ)取り”という事実上の重税を農民に課すことになる。これに反発した農民たちは決起して城に押しかけ、老中は仕方なく彼らの訴えを聞き入れる約束をした。しかし翌年、藩は庄屋たちに圧力をかけて強行策に打って出る。ついに農民たちは代表を組み、江戸藩邸への直訴を決意した。それはこの後、足掛け４年に及ぶ長い戦いの始まりだった……。岐阜県出身の神山征二郎が長年映画化を切望した念願の力作。緒形直人扮する若者、定次郎を中心とした農民たちの勇気と、勝利を得ながらも死罪となった彼らの悲劇を映し出していく。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野戦軍楽隊", "野戦軍楽隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "戦時中に製作された戦意昂揚映画だが軽快なテンポの陽気な作品。戦線の兵士を慰問するための軍楽隊が結成されることになるが、メンバーは楽器もろくに扱えぬ不器用な兵隊ばかり。慣れぬ楽器を手にした男たちはそれでもドタバタと演奏を始めていく……。李香蘭の歌がフィーチャーされた。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チンピラぶるーす ど・アホ！", "チンピラぶるーす ど・アホ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "風来坊のマサが婚約者の美人精神科医、麗子を追って大阪に戻って来る。その頃、マサの幼なじみで刑事のケンは爆弾犯人、大崎を追っていたが、大崎が狙っているのが麗子だと知り、マサに協力を求める。しかし、マサとケンのガードにも関わらず、大崎に麗子を拉致されてしまうのだった……。人気漫才コンビ、トミーズとC.C.ガールズの藤森夕子が主演したコメディ。大阪、名古屋では通常公開されたが、東京では１館２日間のみの特別上映だった。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dick Tracy", "ディック・トレイシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1931年から現在まで米新聞紙上に連載されている人気コミックスを映画化。W・ベイティが製作・監督・主演を兼ね、赤・青・緑・黄・白・黒というコミックスの原色を生かしたセットと衣装のなかで、大活躍を繰り広げる。妖艶な悪女に扮したマドンナのエロティックなコスチュームも見どころ。1930年代の架空の都市。伝説的ヒーロー、トレイシーは正義のために戦っていた。彼の前に立ちふさがるのはビッグ・ボーイ・キャプリスという、ギャングのボス。彼とビッグ・ボーイとの戦いの行方は……。A・パチーノ、D・ホフマンほか、ビッグ・スターの大挙出演も魅力。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KENNEL MURDER CASE", "ケンネル殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "古典的な探偵スリラーの醍醐味たっぷりの名編。S・S・ヴァン・ダインが生み出した名探偵、ファイロ・バンスが巧妙に計画された密室殺人事件に挑む。それは誰の目にも明らかに自殺と思われる現場だったが……。バンスに扮するのは“影なき男”シリーズで有名なW・パウエル。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LITTLE PRINCESS", "テンプルちゃんの小公女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "バーネットの『小公女』のヒロインに、ハリウッドきっての名子役テンプルが挑戦。19世紀末、寄宿学校のアイドル的存在だった少女は、父の戦死の報で環境が一変、屋根裏生活に耐えることになるが……。シャーリーの逆境をはねのける明朗活発な挙動は、天才子役の真骨頂。ビデオタイトルは「小公女」。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今日から俺は!!", "今日から俺は!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "卑怯なことをしてもケンカには勝つのがポリシーの金髪男、貴志と、正々堂々が主義のギザギザ頭の真司。同じ高校の同じクラスになった二人は、ケンカに、女の子にと大暴れを始めた……。同名コミックの映画化。ビデオ化作品も中高生の間でブームとなった、話題作の劇場公開版。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("延安の娘", "延安の娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ありのままの中国を撮り続ける池谷薫が、文化大革命の悲劇に目を向けたドキュメンタリー。北京から下放された元紅衛兵の両親を探す女性の姿を通して、30年間封印されていた歴史の暗部を浮き彫りにする。強制労働下で恋愛も禁止された青年たちと、彼らを受け入れなければならなかった農民たち。文革の嵐に巻き込まれた人々の心情が詩情豊かな映像の中、切なく映える。文化革命の聖地と呼ばれた延安の貧しい農村。養父母の下で過酷な労働を強いられていた海露（ハイシア）は、実の両親が北京からの下放青年だったことを知る。実の親に会いたいと願う彼女は、手掛かりを求めて下放青年を訪ね歩く。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少女娼婦 けものみち", "少女娼婦 けものみち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "当時新人の吉村彩子が大胆かつ自由奔放な少女を軽快に演じ、ある意味で女性ドラマ的なタッチが際立つ“にっかつロマンポルノ”の一編。神代辰巳監督独特の湿度の高い情欲シーンの数々、そして希望を感じさせるラストまで目が離せない。母子家庭で育った16歳の少女が、ボーイフレンドと初体験を済ました後、さらに大人の男とも関係する。やがて、どちらの子か分からないまま、妊娠してしまい……。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録安藤組・襲撃篇", "実録安藤組・襲撃篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "元安藤組組長・安藤昇が主演した彼自身の実録もの第３作。昭和33年、興行関係と賭場で手広く商売していた安藤昇が、借金の取り立てを頼まれた中江産業の社長から侮辱を受け、これを襲撃。ついには愛人宅で逮捕されるまでを、リアルに描いている。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DISTANT DRUMS", "遠い太鼓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "1840年のフロリダ半島。インディアンに捕らわれている民間人を救出するため、クーパー扮する海軍大尉とその一隊が、猛獣や毒蛇の住む沼沢地帯をわたり、インディアンの砦を爆破するまでを描く。ラストの大尉とインディアン酋長のナイフによる水中格闘が見どころ。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母のいる場所", "母のいる場所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "年老いた親の介護という、避けて通れない問題に深く切り込んだ家族ドラマ。苦難の中に立たされながら、秘められた母の思いに自分を見つめ直し、女性として真に自立していく主人公。さらには介護を通して家族の再生を図っていくその姿に、あらためて“母は強し”を痛感させられるに違いない。泉は、半身不随の母を介護して早７年。父親とは口論が絶えず、息子は不登校。ある日、彼女は老人ホームの施設長、悠子に出会う。", "2005-04-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　STORMRIDERS", "風雲・ストームライダーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "香港伝統のワイヤーワークとCGを駆使して超人的バトルを描いた伝奇活劇。戦国時代。武術家ホンファは天下統一の野望のため、幼い少年“風”と“雲”を弟子にとった。だが、やがて成長した二人はホンファの非道な行いを知り、戦いを決意する。ストップモーションによるスタイリッシュなアクション描写はもちろん、悪役ホンファに扮する千葉真一の熱演も見もの。", "1998-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("−×− マイナス・カケル・マイナス", "−×− マイナス・カケル・マイナス", "", "", "http://image.eiga.k-img.com/images/movie/57334/315x210.jpg?2276", 120, "大阪の郊外を舞台に、それぞれに痛みを抱えた人々の物語が偶然につながりながらもすれ違っていく姿を描いたドラマ。無為な日常を送っていたタクシードライバーの貴治は、ある日、妙な色気と無邪気さをあわせもった女と出会う。一方、ある悩みを抱えた女子中学生の凛は、離婚した両親の間で揺れ動いていたが……。監督は本作が劇場長編デビュー作となる伊月肇。", "2011-12-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sダイアリー", "Sダイアリー", "", "", "http://image.eiga.k-img.com/images/movie/54854/315x210.jpg?4432", 109, "これまでの恋愛遍歴を細かく日記に綴ってきた女性が、交際順調だと思っていた彼氏から突然の別れを告げられたことをきっかけに、過去の男たちの元を訪ねて復讐する姿を描いたラブコメディ。ドラマ「私の名前はキム・サムスン」のキム・ソナが、純情で型破りなヒロインを演じる。彼女を取り巻く男性陣にも「恋の潜伏捜査」のコン・ユ、歌手としても活躍するイ・ヒョヌ、「火山高」のキム・スロら人気スターが揃う。", "2010-01-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GERVAISE", "居酒屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "19世紀半ばのパリの裏町。夫に逃げられた洗濯女のジェルベーズは、実直な屋根職人のクポーと結婚する。だが、屋根から落ちて大ケガをしたクポーはやがて酒びたりになる……。薄幸なジェルベーズの一生を描いたE・ゾラの名作『居酒屋』の映画化。", "1956-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("КОРОЛЬ ЛИР／KING LEAR", "リア王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "シェイクスピア悲劇『リア王』の最初の本格的映画化。中世のイギリス、老王リアは３人の娘に財産と領地を譲ろうと考える。彼は廷臣の助言に従い、３人の娘を試すために、娘に愛と信頼の証を述べるよう命ずる……。シェイクスピア研究家コージンツェフが監督。", "1971-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恍惚の人", "恍惚の人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "有吉佐和子の同名ベストセラー小説を、名匠・豊田四郎が映像化。老人性うつ病になった84歳の茂造と、息子の嫁・昭子との交流を、老人問題としてではなく、人間同士の愛情の発露として描いた秀作。ボケ老人に扮した森繁の名演、流麗なカメラワークなどが光る。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おじさんは原始人だった", "おじさんは原始人だった", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "小学生の勇気は遠足に行った遺跡公園で原始人に出会ってしまい、いつしか彼に連れられて原始時代へ。しかし、そこには二つの派閥が対立しており、勇気は敵側の娘ホシミアにほのかな恋心を抱くが……。子供たちのほのぼのとした“ロミオとジュリエット”の原始時代バージョン。ロケ撮影が効果的だ。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Before Night Falls", "夜になるまえに", "", "", "http://image.eiga.k-img.com/images/movie/1304/315x210.jpg?4223", 133, "アレナスは現代ラテン文学の代表的作家のひとり。1943年、キューバに生まれ、20歳で作家デビューするが、独裁政権下で同性愛ゆえに投獄され、80年にアメリカへ亡命。ニューヨークで執筆活動を続けるが、エイズを発病し、90年に自殺した。監督のシュナーベルは、80年代にニューペインティングの人気画家としてNYで活動していた。音楽は、コーエン兄弟監督作全作の音楽を担当しているカーター・バーウェル。", "2001-09-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　LETTER　FROM　MARS", "天国からの手紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "時空を超えたラブ・ストーリー「リメンバー・ミー」のキム・ジョングォン監督が、再び泣ける純愛ものに挑んだ快作。美人女優キム・ヒソンが繊細な演技で魅せる。少女は“火星へ行く”という遺言を残して逝った父の言葉を信じていた。そんな少女に恋する少年は火星からの父の手紙を代筆し続けたが、やがて少女はソウルへ引っ越すことに。時が経ち、大人になった少年と少女は運命的な再会を果たす。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEDICATO AL MARE EGEO", "エーゲ海に捧ぐ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "池田満寿夫が、同名の芥川賞受賞作と短編小説『テーブルの下の婚礼』とを組み合わせて自ら映画化。製作や美術以外はスタッフ、キャストともすべてイタリア人で、濃厚なエロチシズムの世界を描いている。", "1979-01-01", "76,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE LIGHTNING", "白熱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1970年代特有の空気に包まれた、B・レイノルズ主演の復讐劇。密造酒の製造によって刑に服していたゲイターは、刑務所の中で、弟が保安官の手によって溺死させられたことを知った。復讐に燃える彼は、脱獄を試みるがうまくいかず、財務省に協力することで出獄しようとする。財務省側の条件をのんだ彼は密造酒組織の逮捕に協力、最終的には弟を殺した保安官を死に追いやる。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIG HIT", "ビッグ・ヒット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「新ポリス・ストーリー」のK・ウォンが、ハリウッド進出を果たした痛快アクション。ドジな殺し屋集団が計画した誘拐事件のてん末が、派手なアクションとユーモアを満載してスピーディーに展開する。気弱なヒットマン、メルはフィアンセや愛人のために大金が必要となり、仲間のシスコの誘拐計画に参加。ところが、標的の日本人女性ケイコの名付け親が、自分たちのボスだと分かり大あわて。シスコがボスから犯人追跡を命じられ、いつの間にか首謀者にされたメルは組織から追われる中、ケイコと恋に落ちていき……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダーBLACK", "仮面ライダーBLACK", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "怪人だけの世界をつくるべく暗躍する暗黒結社ゴルゴムと、彼らの改造手術を受けて仮面ライダーBLACKとなった青年・南光太郎との闘いを描いた特撮テレビシリーズ「仮面ライダーBLACK」の劇場版。ゴルゴムのカメレオン怪人によって、子どもたちが次々と連れ去られた。捜索を開始した光太郎は、子どもたちが「鬼ヶ島」と呼ばれる無人島に囚われているという情報を得て現場に駆けつけるが……。原作者の石ノ森章太郎がゲスト出演。", "1988-03-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Maurice", "モーリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "同性愛を描いた作品だが、そのスキャンダラスなイメージとは裏腹の、秀逸な映像美に彩られた芸術性の高さと、高尚で奥深いテーマ性が好評を博した秀作である。20世紀初頭のケンブリッジ大学。モーリスは音楽が縁でクライブと知り合い、親友となる。だが、クライブは同性愛信奉者だった。初めは反発しながらも、情熱のおもむくままに男同士のプラトニックな愛にのめり込んでいくモーリス……。正統派英国トラッド・ファッションに身を包む彼らが、凛々しく魅力的。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("J’AIMERAIS　PAS　CREVER　UN　DIMANCHE", "日曜日の恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "愛を失い絶望の淵に立つ男と、死の淵から蘇生し、自分を死姦した男を追う女を通して、愛と死の狭間に揺れる恋人たちを描く奇妙なラブ・ストーリー。ある夜、病院の死体公示所に美しい女の死体が運び込まれた。彼女の名はテレーザ。この病院の検死官ベンは、衝動の赴くままテレーザの死体を犯し始めた。すると彼女は突然、蘇生してしまったのだ。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イヴの時間　劇場版", "イヴの時間　劇場版", "", "", "http://image.eiga.k-img.com/images/movie/55074/315x210.jpg?4689", 106, "2008年にインターネット上で公開されて人気を博したアニメーションの、ファースト・シーズン全6話分を再編集した劇場版。人々が日常的にアンドロイド（人間型ロボット）を家電として使う時代。高校生のリクオも、幼い頃からの教育でアンドロイドを機械として扱っていたが、人間とロボットを区別しない喫茶店「イヴの時間」を訪れたことで、ロボットに対する考え方が変わっていく。", "2010-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Date...", "Date...", "", "", "http://image.eiga.k-img.com/images/movie/55754/315x210.jpg?3146", 80, "青春と性をテーマにした「青春Hシリーズ」第3作。「イズ・エー[is A.]」「NIGHT☆KING　ナイトキング」の藤原健一監督がメガホンをとる。売れない役者の茂は競馬で万馬券を当て、自分が主役の映画を製作することを思いつく。米映画「デート・ウィズ・ドリュー」に触発され、映画の中であこがれのアイドル・奥田杏奈に愛の告白をすることを決意するが、杏奈の素性を調べていくうちに衝撃の事実が発覚する。", "2010-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はいからさんが通る", "はいからさんが通る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "同名人気マンガの映画化。南野陽子が女学校を出たばかりのおてんばなハイカラ娘をいきいきと好演。大正ロマンも華やかな、恋と冒険の青春映画。南野の恋人の少尉役は、当時『メンズ・ノンノ』で大人気の男性モデルだった阿部寛が演じている。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ODD MAN OUT", "邪魔者は殺せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "リード監督の名を世界的に知らしめた秀作。アイルランド独立運動の闘士が、政治資金を強奪中に重傷を負う。警察に追われる彼を何とか助けようとする仲間たち……。夜の街の陰影を映し出すカメラが抜群。巧妙な演出テクニックを駆使した最高のサスペンス・ドラマだ。", "1947-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL ANGEL EXTERMINADOR", "皆殺しの天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "メキシコ時代のブニュエルのシュルレアリスム映画の傑作で、後の「ブルジョワジーの秘かな愉しみ」は本作の一種のリメイク。オペラの後で邸に集まってきたブルジョワ紳士淑女たちが、なぜかサロンから出られなくなる……という単純といえば単純なストーリーを、美しく詩的な映像で紡ぎ、他作品と同様、その意味するところへの説明を一切拒んだブニュエルならではの謎めいた巨編。", "1981-08-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ИВАНОВО ДЕТСТВО", "僕の村は戦場だった", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "第二次大戦中、ドイツ軍の戦火で故郷の村を失い、母と妹も行方不明になり、独りぼっちとなった12歳のイワン少年。ドイツ軍を憎悪するイワンは少年斥候兵となって活躍し、彼がもたらす多くの情報は味方にとって貴重なものだった。だが、その任務は危険きわまりない。親代わりのホーリン大尉は、そんなイワンを心配して、幼年学校へ入れようとするが、イワンは聞き入れない。そして、ある日、イワンはついに帰ってこなかった……。戦争の悲惨さを12歳の少年の悲劇を通して描いたA・タルコフスキーの長編デビュー作。過酷な戦争シーンに、リリカルで詩的な少年の回想シーンを織り交ぜた美しい映画詩となっている。", "1962-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Be Cool", "Be Cool ビー・クール", "", "", "http://image.eiga.k-img.com/images/movie/1284/315x210.jpg?3944", 118, "ジョン・トラボルタ主演、エルモア・レナード原作の「ゲット・ショーティ」の続編。前作でギャングから映画プロデューサーに転身した主人公チリが、今回は音楽界に進出しようと決意。ユマ・サーマンが演じる旧友の妻と組んで、歌手志望の若い女性を大スターにしようとするが。ハーベイ・カイテル、ダニー・デビート、エアロスミスのスティーブン・タイラーらが共演。監督は「ミニミニ大作戦」のF・ゲイリー・グレイ。", "2005-09-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INSIDE EDGE", "デッド・チェイサー 炎の大捜査網", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "犯罪多発都市ロサンゼルスの麻薬シンジケートに単身挑んだ刑事が主人公。過激な刑事リチャード・モンタナは、ある日ドラッグ王マリオ・ジオの捜査を任される。モンタナはジオの愛人リサに近づくが、不覚にもリサと愛し合ってしまうのだった。仕事か、金か女か究極の選択を迫られるモンタナだったが……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("-less", "-less", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "大阪の映像専門学校ビジュアルアーツで講師を務める映像作家としおかたかおが、長年温めてきたシナリオを映画化。未来にうんざりしている女子高生レイナが家出。その間にレイナが様々な人々と出会い別れを繰り返す中で、未来に繋がる言葉を見い出していく姿を描く。主演はCMなどで活躍し今回が映画初主演となる河瀬輝美。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　DIVINA　COMEDIA", "神曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "ポルトガルの巨匠オリヴェイラの代表作の一つにあげられる野心的な寓話。ある精神病院を舞台に、聖書やドストエフスキーの『罪と罰』『カラマーゾフの兄弟』などになぞらえた男女の罪と救済の物語が、神秘的なムードの中に展開していく。「パルプ・フィクション」の個性派女優M・デ・メディロスが主演を務め、オリヴェイラ作品のミューズであるL・シルヴェイラが脇を固めた。またショパン、シューマンなどのピアノ音楽が挿入され、リスボン出身の国際的ピアニスト、マリア・ジョアン・ピルシュが出演している。DVDは「マノエル・ド・オリヴェイラ DVD−BOX」に収録。", "1991-01-01", "25", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ricky", "Ricky リッキー", "", "", "http://image.eiga.k-img.com/images/movie/55714/315x210.jpg?4260", 90, "「8人の女たち」「スイミング・プール」のフランソワ・オゾン監督が描く家族ドラマ。出演にアレクサンドラ・ラミー、「パンズ・ラビリンス」のセルジ・ロペスら。7歳の娘リザと暮らすシングルマザーのカティは、恋人パコとの間に新たな子ども、リッキーを授かる。しかし幸せな日々もつかの間、すれ違いからパコは家を出ていってしまう。そんなある日、リッキーの背中に翼が生えて空を飛ぶようになり、世間を巻き込んで大騒ぎになってしまう。", "2010-11-27", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女囚701号・さそり", "女囚701号・さそり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "とある女子刑務所。けたたましく鳴り響くサイレンが、女囚の脱走を告げた。逃げたのは松島ナミと木田由紀子である。二人はたちまち捕まり、懲罰房に入れられた。かつてナミには杉見という麻薬捜査官の恋人がいた。杉見は悪徳刑事でナミを利用し、私腹を肥やしていた。だまされたことを知ったナミは杉見を襲ったが、致命傷には至らず逮捕されたのだった。復讐に燃えるナミは、刑務所内で女囚たちから“さそり”と呼ばれ、孤立していた。ある日、女囚たちの暴動が起き、ナミも巻き添えを食うが……。当時、新人だった伊藤俊也監督はナミの回想シーンを回り舞台のように映し出したり、赤い照明や隈取りを使って、様式美を大胆に劇画的にデフォルメした演出を展開。また冒頭とラストに日の丸を映し出したり、ナミが杉見を襲うシーンを警察に設定するなど、私怨の物語を権力に対する公的な怨念へと昇華させた。梶芽衣子の歌う主題歌『恨み節』もヒットした。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼の棲む館", "鬼の棲む館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "谷崎潤一郎の戯曲『無明と愛染』を新藤兼人脚色、三隅研次監督、勝新太郎主演で映画化した文芸大作。南北朝時代、戦火をまぬがれた山寺を舞台として、４人の男女のそれぞれの愛欲模様が描かれる。主人公の妻に扮した高峰秀子が、さすがにうまい。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE 5TH MONKEY", "フィフス・モンキー 4匹と1人の大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ブラジルの密林動物の密売人、クンダは、ある日森の中で４匹のチンパンジーを捕まえる。結婚資金のためこの４匹を売ろうと旅に出るクンダだったが、美女オクタビアとの出会い、チンパンジーの誘拐などの事件を経て、真実の愛に目覚めていく。チンパンジーたちの愛らしい演技に、名優キングスレーもタジタジ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴れ豪右衛門", "暴れ豪右衛門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "これが101本目の監督作となった稲垣浩の痛快スペクタクル時代劇。戦国時代、加賀七党の首領として馬を駆り冷酷な戦いぶりを見せた豪右衛門の活躍を西部劇タッチで描く。三船敏郎が十八番の豪快な役どころで快演するが、演出は今一つ歯切れが悪い。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕らの方程式", "僕らの方程式", "", "", "http://image.eiga.k-img.com/images/movie/53824/315x210.jpg?4758", 107, "鉄道研究部の心平は、学校一の美少女・椿にラブレターを渡したばかりで、翌日に控える鉄道模型コンテストの仕上げも上の空。そんな時、頭は悪いがルックスはいいワタルのバスケットボールが模型を直撃。心平は学校に居残り、徹夜で修復作業を行うことに。そこへワタルや椿、破天荒な教師・秋島らがそれぞれの事情で真夜中の学校にやってきて……。監督は「ガチャポン」「地球でたったふたり」の内田英治、主演は「同級生」「シャカリキ」の中村優一。", "2008-10-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A HOBO’S CHRISTMAS", "再会のクリスマス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "アメリカ版「父帰る」ともいえる心温まるファミリー・ドラマ。妻を亡くした警察官チャーリーは子供二人を連れて新しい土地での生活を始め、恋人もでき幸せを取り戻しつつあった。が、そこへ25年前に母と彼を捨てた父親が、落ちぶれた姿になって訪ねてきた。初めは父を許せないチャーリーであったが、やがて来るクリスマスを父と過ごそうと少しずつ心を開いていく。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("剣光桜吹雪", "剣光桜吹雪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "戦後は大映で名編集者とうたわれた菅沼完二の監督時代の力作。老中・田沼意次の腐敗を一掃すべく立ちあがった松平外記の活躍を描く。殿中で刃傷に及び、人をバッタバッタと斬り倒す嵐寛寿郎のクライマックスの立ち回りが見どころ。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("英霊たちの応援歌・最後の早慶戦", "英霊たちの応援歌・最後の早慶戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "戦時中、学徒出陣のため解散した六大学野球の最後の早慶戦を前半に配し、そのメンバーらが出陣してからの様々なドラマを後半に追うという構成。重くなりがちな題材だが、岡本喜八は随所にユーモアを配しテンポのよい作品に仕上げた。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Five Minutes of Heaven", "5分間の天国", "", "", "http://image.eiga.k-img.com/images/movie/54994/315x210.jpg?4633", 88, "1975年、北アイルランドのプロテスタント過激派組織に所属する少年アリスター・リトルがカトリックの男を殺害し、被害者の弟であるジョー・グリフィンがその現場を目撃する。それから30年後、2人はテレビ番組で対談することになり……。「ヒトラー／最期の12日間」のオリバー・ヒルシュビーゲルがメガホンを取り、サンダンス映画祭で監督賞を受賞した社会派ドラマ。リーアム・ニーソン、ジェームズ・ネスビットらが出演。", "2009-10-19", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆転シュート・ベイブレード　THE MOVIE／　　激闘!!タカオvs大地", "爆転シュート・ベイブレード　THE MOVIE／　　激闘!!タカオvs大地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "子供たちの間で爆発的な人気を呼んだ、おもちゃの対戦型コマを原案にした青木たかおの人気コミック『爆転シュート　ベイブレード』。2001年からは日韓合作でTVアニメの放送も始まった同シリーズ初の映画版だ。TVアニメ版の主人公、木ノ宮タカオと、コミック版の外伝の主人公、皇大地の壮絶なバトルを描く。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sleuth", "探偵スルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "イギリスを代表する二人の名優オリヴィエとケインが演技のすべてを見せる推理劇。ロンドン郊外の高名な推理作家の邸宅を舞台に繰り広げられる、たった二人のスリラー劇だ。シェーファーの大ヒット舞台劇の映画化。", "1972-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("早咲きの花", "早咲きの花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "失明を宣告された女性カメラマンが故郷を訪れ、太平洋戦争下の幼少時代を回想する感動ドラマ。宗田理の小説を基に、「ほたるの星」の菅原浩志がメガホンをとる。ピンホールカメラマンとして長年にわたって海外で活躍してきたシュナイダー植松三奈子は、医者から突然の失明宣告を受ける。目が見えるうちに懐かしい故郷の風景を見ておこうと愛知県豊橋市を訪れた三奈子は、偶然出会った高校生たちに少女時代の思い出を語りはじめる。", "2006-12-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREE　AND　EASY", "キートンのエキストラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "サイレント映画の喜劇王バスター・キートンが主演した、トーキー映画の長編コメディ。女優の卵のマネージャーにされてしまったお人好しの男が、撮影所で繰り広げるドタバタ騒動をコミカルに描いていくが、実際は彼の出番は意外に少ない。MGM映画ならではのミュージカル仕立てになっている。", "1930-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARRY　UN　AMI　QUI　VOUS　VEUT　DU　BIEN", "ハリー、見知らぬ友人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "夏、妻と三人の娘と田舎でバカンスを過ごそうと車を走らせていたミシェルはその途中、高校時代の同級生ハリーと出会う。若い恋人と気ままな旅を楽しんでいたハリーは再会を心から喜び、一緒に一晩を過ごそうと提案、ミシェルたちについてきてしまう。夕食時、ハリーは突然、ミシェルが高校時代に書いた詩を朗読。その詩にどれだけ感動したかを話していく。そして、ミシェルの創作再開を願うハリーは……。ミシェルの文才に惚れ込んでいたハリーの親切心が、やがて彼の創作の邪魔になる者を取り除こうという狂気へと発展していく様、そしてその狂気にさらされたミシェルの恐怖を、ブラック・ユーモアを随所に盛り込んで描いたサスペンス。本国フランスでは大ヒットを記録、セザール賞で最優秀監督賞、最優秀主演男優賞（ハリー役のS・ロペス）など４部門を受賞した。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNCLE JOE SHANNON", "アンクル・ジョー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「ロッキー」の義理の兄貴役で有名なB・ヤングが主演したハート・ウォーミングな作品。1960年代にトランペッターとして名をなしたが、妻と息子を火事で失い、今は身をくずしてスラムで酒びたりという男と、母親に捨てられ孤児となった少年との交流が描かれる。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("過年回家／SEVENTEEN　YEARS", "ただいま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「クレイジー・イングリッシュ」のチャン・ユアンによるヒューマン・ドラマ。16歳のとき些細なケンカから姉を過って殺害したタウ・ランが仮出所を許される。刑務所の女主任は、帰宅をためらう彼女を家まで送り届ける。17年ぶりに両親と再会したタウ・ランは……。近代化の進む北京郊外を舞台に、家族の和解、再生を温かい眼差しで描いた感動作。", "1999-01-01", "8,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOT STUFF", "ホットスタッフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "太陽の光輝くマイアミも、ご多分にもれず犯罪発生率が高い。現行犯で捕らえられないと検挙されにくいのを幸いに、盗品（＝ホットスタッフ）売買がまかり通る——そんな現状を背景に、ニセの故売屋を開いて犯罪者たちを捕らえようとする警官たちの活躍を描く。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cheburashka", "チェブラーシカ　全4話完全版", "", "", "http://image.eiga.k-img.com/images/movie/53604/315x210.jpg?4149", 0, "オレンジの木箱に入って遠い南の国からやって来た、耳の大きな不思議な動物\"チェブラーシカ\"。動物園への受け入れを拒否されたチェブラーシカは、町の電話ボックスで暮らすことに。ある日、チェブラーシカは動物園で働く孤独なワニのゲーナと出会う。愛らしいキャラクターと単純ながらも奥深いストーリーで、01年の劇場公開時も人気を呼んだロシア製人形アニメ「チェブラーシカ」を、全4話完全デジタルリマスター版として上映。", "2008-07-19", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大当り狸御殿", "大当り狸御殿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ひばりといづみが主役の逸品。今となってはその脇を固める登場人物たちの芸と、それを見せるショー構成のうまさを知る上で、資料価値の高い作品であり、コメディとしても成功している。トニー谷の紹介で始まる『バナナ・ボート』の大胆さなど、見事。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hustle", "ハッスル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "　ロサンゼルス警察の刑事フィルは海岸で発見された若い女性グロリアの遺体を調査し、彼女が風俗の仕事をしていたことを突き止める。一方、グロリアの父親マーティは他殺であると主張し、独自に調査を始める。その後、グロリアが大物弁護士レオと関わっていたことを知るフィル。やがてマーティの過去も明るみになるが……。「ロンゲスト・ヤード」の監督・主演コンビによる刑事ドラマ。事件の捜査とともに、フィルと恋人である高級コールガール、ニコルとの愛が描かれる。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺人者を消せ", "殺人者を消せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "石原裕次郎と舛田利雄監督のコンビによるコメディ・アクション。裕次郎扮する平凡なサラリーマンが、大倉庫会社の二代目に顔が生き写しのためその身代わりとなって、会社を乗っ取ろうと画策する悪玉と闘う。ギャグに新味がなく凡打に終わった。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Cradle Snatchers", "新旧恋の三段返し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "スーザンとキティとエセルは仲良し奥様三人組。共に旦那は裕福で、優雅に暮らしていた。ところが、スーザンの夫が出張と偽って野鳥狩りに出かけていたことが判明。キティとエセルの夫もそれぞれ妻にウソをついていたことがバレてしまう。三人の妻は夫へのあてつけに、若い男の子たちを雇ってデートへ繰り出すが……。", "1927-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本黒社会 LEY LINES", "日本黒社会 LEY LINES", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "三池崇史監督が、その力量をまざまざと見せつけた無国籍活劇。中国人と日本人のハーフとして生まれた兄弟が上京。新宿の裏社会に根を降ろし、上海マフィアの向こうを張って闇商売を手掛け、やがてブラジルへの旅立ちをかけて、決死の戦いを挑んでいく。一瞬も休むことなく連続する、テンションの高いシークエンス群。たぐいまれなる突進力に硬派な叙情が加味され、定型から逸脱したラスト・シーンまで、見事な快走が続く。出演陣はいずれも力演を見せるが、なかでも田口トモロヲは従来のイメージを塗り替える初々しいキャラクターを体現。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アンテナ", "アンテナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "「鬼畜大宴会」「空の穴」の熊切和嘉監督の長編第３作は、田口ランディのベストセラー同名小説の映画化。謎の失踪を遂げた妹の影に悩まされる青年を主人公に、その家族の苦悩を描く。青年は自傷癖を背負い、娘の失踪後に生まれた次男を娘のように扱う母とそれに答える弟の異様な姿にも悩んでいた。歪んだ家族関係に光が差すまでの長い道のりが、繊細に綴られる。15年前の夜、突然失踪した妹の隣で寝ていた祐一郎は、本当は何か目撃していたかもしれないと悩み続け、言い知れぬ罪悪感を抱えていた。そんなある日、出雲で監禁されていた少女が発見されたというニュースが流れ……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Leonardo Live", "レオナルド・ダ・ヴィンチ展 in シアター", "", "", "http://image.eiga.k-img.com/images/movie/57964/315x210.jpg?3930", 0, "レオナルド・ダ・ヴィンチが生涯で描いた絵画で現存する15点のうち9点が集められ、2011年にロンドンのナショナル・ギャラリーで開催された展覧会「Painter at the Court of Milan」の模様を映画館で上映。2種類の「岩窟の聖母」や、同展覧会で初めて一般公開された「救世主キリスト」などダ・ヴィンチの絵画を紹介し、展覧会の舞台裏や専門家たちによる解説も盛り込む。", "2012-04-21", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SLEEP WITH ME", "スリープ・ウィズ・ミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "サラ、ジョゼフ、フランクは学生時代からの親友同士。３人で休暇中のドライブを楽しんでいると、突然ジョゼフがサラにプロポーズ。サラにひそかな恋心を抱いていたフランクは、平静を装い二人を祝福するが心中は穏やかでない。二人の結婚後も３人の友情は変わらないかに見えたが、フランクの思いは募るばかり。ディナー・パーティーの夜、ついにフランクはみんなの前でサラの唇を奪ってしまい……。限界ギリギリの恋愛と友情をウィットとユーモアたっぷりに描くロマンティック・コメディ。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MADAME　SANS−GENE", "戦場を駆ける女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "激動のフランス革命からナポレオン時代にかけてのパリを舞台に、洗濯女からやがて貴族へと上り詰めていく女カトリーヌをヒロインに描く、コミカルな70ミリ・ヒューマン・スペクタクル歴史絵巻。イタリア出身の大スター、ソフィア・ローレンの代表作の１本。製作は彼女の夫でもあるカルロ・ポンティ。", "1961-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阪神大震災／再生の日々を生きる", "阪神大震災／再生の日々を生きる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 156, "1996年1月17日。日本が震撼した阪神大震災。多くの被害に見舞われたあの大地震から、被災地に生きる人々がどのように再生してきたのかを記録したドキュメンタリーだ。震災後の神戸に生きる人々のこれまでと、そしてこれからを観る者に問いかける。1996年1月17日。阪神大震災で被害が大きかった神戸。ここでの震災からの復興の姿を、監督は4年3ヵ月に渡ってカメラと共に見つめ続ける。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARMEN", "カルメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "『カルメン』をフラメンコ・ダンスで上演する舞踊団のリーダー、アントニオは、その主演女優を探し出し、リハーサルを始めるが……。『カルメン』の愛と嫉妬の物語はいつしかそれを演じる二人のドラマになる。現実と舞台を交錯して描く情熱的なダンス映画。", "1983-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グループ魂のでんきまむし", "グループ魂のでんきまむし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "劇団“大人計画”から生まれたコント・トリオ“グループ魂”主演による、破天荒なパワーに満ちたビデオ作品。TV出演時の映像なども巧みに導入、グループ魂のサクセス・ストーリーを過激にデフォルメした内容。あくまでもドキュメンタリー形式のフィクションだが、３人組の友情が崩壊し決裂していく様には、ビデオならではのリアリティーが映え、不思議な臨場感を醸し出している。大人計画の主宰、松尾スズキが“負けの哲学”を説く怪人物として登場して以降の後半は、大人計画らしい危険なギャグが盛りだくさん。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天装戦隊ゴセイジャーVSシンケンジャー　エピック ON 銀幕", "天装戦隊ゴセイジャーVSシンケンジャー　エピック ON 銀幕", "", "", "http://image.eiga.k-img.com/images/movie/55854/315x210.jpg?1615", 60, "スーパー戦隊シリーズ「天装戦隊ゴセイジャー」と「侍戦隊シンケンジャー」がタッグを組んだ劇場版。ある日、町にアヤカシのマダコダマが出現。天装術が通じないマダコダマに苦戦していたゴセイレッド＝アラタのもとへ、シンケンレッド＝志葉丈瑠が駆けつけるが、三途の川の水が切れたためにマダコダマは退却してしまう。外道衆の復活を知ったシンケンジャーたちは再び丈瑠の元に集結し、ゴセイジャーと力を合わせて敵に立ち向かう。", "2011-01-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO SIR WITH LOVE", "いつも心に太陽を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "エンジニア出身のマーク・サッカレーは、教師として不良生徒ばかりのイーストエンド地区に赴任。自尊心や責任感を訴える彼の教育に生徒は信頼を抱くが、マークは学校を去ろうとする……。元ギアナ国連大使の自伝小説の映画化。S・ポワチエの出世作。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOTCHA！", "ガッチャ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "サバイバルゲームに熱中する大学生ジョナサン。バカンスのヨーロッパ旅行で出会った美女と、夢のような時間を過ごすことに。しかし、これが彼にとって現実のサバイバルになろうとは思いも寄らなかった……。痛快なサスペンス・タッチの娯楽作品。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ESTHER　KAHN", "エスター・カーン／めざめの時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "「そして僕は恋をする」から４年ぶりにメガホンを執ったA・デプレシャンが、A・シモンズの原作を映画化した愛のドラマ。19世紀のロンドン。内気な少女エスターはある日見た芝居に心打たれ、女優になることを決意。ベテラン俳優の目に留まり、彼の指導で才能を開花させる。女優として成功の道を歩み始めた彼女は、やがて演劇批評家と恋に落ちるが……。頑なに心を閉ざしていた女性が、恋の喜びと痛みを経験し、演技に目覚め、情熱的な女へと生まれ変わる。心を解き放ち、内に秘めた熱い思いをほとばしらせていくヒロインを、S・フェニックスが体当たりの演技で熱演。", "2000-01-01", "2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONCE UPON A TIME", "此の虫十万弗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ラジオ・ドラマ『私の依頼者カーリイ』を原作にしたファンタジー・ドラマ。ディズニーへのパロディも入ったユニークな作品で、少年物語としても秀作。不入りのため劇場閉鎖を間近にした劇場主は、ある日、踊る毛虫をペットにしている少年と出会う。劇場主はその毛虫で儲けようと企むが、その噂が電波に乗り、オモチャ会社やディズニーから引き合いが殺到することに！", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Matrix Revolutions", "マトリックス・レボリューションズ", "", "", "http://image.eiga.k-img.com/images/movie/1154/315x210.jpg?3743", 129, "前作「リローデッド」の直後から続く待望の3部作完結編。ソースに達して精神と身体が分離した状態になったネオを救うため、トリニティらがメロビンジアン一派と対決。一方、ザイオンを襲うセンチネルの総攻撃を、住民たちは装甲スーツAPUを装着して迎え撃つ。ベインに化けたスミスは何を画策しているのか？　はたしてネオは人類を救うことが出来るのか？　だとしたら、それはいったいどんな状態で実現されるのか？", "2003-11-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUGEN", "MUGEN", "", "", "http://image.eiga.k-img.com/images/movie/55084/315x210.jpg?3151", 47, "「砂の影」の甲斐田祐輔監督がパリを舞台に撮りあげた中編作品。パリのアパートで向かいの男女を盗撮するセブとヨシ。彼らは盗撮したビデオテープを組織に納める仕事をしていた。ビデオに映した女ユーリを街で見かけたセブは、ヨシには内緒で彼女にテープを渡してしまう。一方、ユーリに会うためパリへやって来た恋人ユウイチは、ユーリと連絡が取れず街をさまよい歩いていた。偶然にもユウイチと知りあったセブは、ユウイチを自分のアパートに連れて帰るが……。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SMOULDERING FIRES", "燻ゆる情炎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "亡父の工場を任され、懸命に働いているうちに、いつしか中年になってしまった女ジェーン。彼女は若い工員ロバートと年齢を超えた愛をはぐくむが、妹のドロシーが彼を好きなことを知り、一人身を引くことにするのだった……。MGMの看板監督C・ブラウンによる無声期のメロドラマ。愛するが故に別れる女心の葛藤を、名女優P・フレデリックが見事に演じている。", "1925-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STREET KNIGHT", "ナイト・ハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "元警官のジェークは、職務中に少女を死なせて以来、自動車工としてひっそり暮らしていた。ある時、彼は友人のレベッカから、殺人事件の捜査依頼を受ける。L.A.のストリート・ギャング“ローズ”と“ブレイズ”の平和的集会で、メンバーの一人が見知らぬ男たちに殺されたというのだ。唯一の目撃者カルロスは、レベッカの弟だったが姿を消してしまった……。製作者のM・ディサールは、ジャン・クロード・ヴァン・ダムをスターに育てたアクション映画界のプロデューサー。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CITY HALL", "訣別の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ニューヨーク市長とその若き補佐官が市政の汚職をめぐって葛藤する社会派サスペンス。ニューヨークのブルックリンで、黒人少年が警官と執行猶予中のマフィアの銃撃戦に巻き込まれ命を落とした。そのカリスマ性から市民の絶大な支持を得ている市長のジョン・パパスは、イメージダウンを防ぐために彼の右腕で厚い信頼関係にある補佐官ケビン・カルフーンに事件の処理を任せる。女性弁護士のコーガンから、事件の判決に不正があったことを知らされたケビンは、パパスの忠告を無視して捜査を進め、やがて事件の背後にある陰謀に気付く。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浪漫風暴／SOMEBODY　UP　THERE　LIKES　ME", "野獣の瞳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "アクション映画の巨匠J・ウーが本国の香港で製作を務めた作品。試合中に恋人の兄を殺してしまった若きキック・ボクサー、ケン。それ以来、ショックのあまり酒に溺れ、危険な賭けボクシングに身を投じる彼だったが、自らの名誉と命のすべてを懸けて再びリングに上がることを決心。過酷なトレーニングを積んだ後、ケンは“宿命”の戦いに挑んでいく。鬼コーチ役のサモ・ハン・キンポーが、貫ろくの演技を披露。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Yi yi (A One and a Two)", "ヤンヤン　夏の想い出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 173, "エドワード・ヤン監督が「カップルズ」以来4年ぶりに取り組んだ家族劇。ヤンヤンは祖母と両親、姉と台北のマンションに暮らすごく普通の家庭の少年。ところが、叔父の結婚式を境に、様々な事件が起こり始める……。現代の家族を取り巻く多彩なエピソードを同時進行させ、時には対比させた緻密な構成で描いていく。穏やかさの中に異様な緊迫感が同居した映像世界は、ヤン監督作品ならではの濃密さ。イッセー尾形の出演も話題に。", "2000-01-01", "53,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("拳銃残酷物語", "拳銃残酷物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "宍戸錠主演のギャング映画。仮釈放になった錠が組織のボスから日本ダービーの売り上げ金強奪を持ちかけられる。錠は４人の仲間を集め強奪に成功するが……。スタンリー・キューブリックの傑作「現金に体を張れ」を下敷きにした通好みの佳作。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRIEST", "司祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "全米公開の際にカトリック教会から抗議を受けた問題作。同性愛者の司祭を通して、救済や愛の本質を問いかける。リバプールのカトリック教会に着任した若き司祭グレッグ。理想に燃える彼はある日、高校生のリサから、父親から性的虐待を受けているという告白を受けるが、どうすることもできず思い悩む。だが、一方でグレッグ自身も同性愛者であることと聖職の板挟みで悩んでいた。やがて偶然、夫と娘の近親相姦を知ったリサの母親はそのことを知っていたグレッグをなじる。彼は苦しみから逃れるために、恋人グラハムと車の中で愛し合う。が、不運にも警察が通りかかり、グラハムとの密会が新聞沙汰となり、グレッグは自殺未遂を図る。", "1994-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夫婦", "夫婦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ある地方都市から東京へ転勤してきた安サラリーマン・中原伊作とその妻・菊子。彼らは、妻を失い独り身の武村という男の家に同居させてもらうことになるのだが……。すでにハネムーン気分も遠い昔のこととなった結婚６年目の夫婦が、一人の独身男と生活をともにするなかで、曲折の果てに夫婦の深い愛情を確認し合うに至るまでを描いた、成瀬お得意の小市民ホームドラマ。成瀬が最も充実した作品群を連作した1950年代前半の作品で、水木洋子、中井朝一といったベスト・スタッフが脇を固めている。必要最小限の登場人物で人生のすべてを描ききってしまうシナリオと演出の素晴らしさは驚異的。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRUCE LEE THE CURSE OF THE DRAGON", "実録ブルース・リー ドラゴンと呼ばれた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "1973年７月20日に32歳の生涯を終えたブルース・リー。現在もそのカリスマ性はいっそう強く、彼の名は忘れられることはない。主演作のファイトシーン、J・コバーンほか、門下生である俳優たちとの練習風景、「燃えよドラゴン」のリハーサル模様、そして1994年３月に急逝した息子ブランドンのインタビュー等の映像が綴られる。DVDは「燃えよドラゴン ディレクターズ・カット スペシャル・エディション」に特典映像として収録。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("急げ！若者 TOMORROW NEVER WAITS", "急げ！若者 TOMORROW NEVER WAITS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "卒業記念に吹き込んだコーラスのカセット・テープを支えに、約束を固める孤児院育ちの４人組。だが、一人がチンピラとの喧嘩からヤクザと付き合うハメとなり、徐々に結束は乱れ始める。そんな折、彼らにレコード・デビューの話が転がり込み……。フォーリーブス主演のアイドル映画で、当時大人気の郷ひろみが客演している。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("84 CHARLIE MOPIC", "84★チャーリー・モピック ベトナムの照準", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "一人の従軍カメラマンの視点から、ベトナム戦争の戦慄の実態を暴いたドキュメンタリー・ドラマ。ジャングルでの米兵たちの日常生活から、戦闘における様々な状況が主人公のカメラマンの眼（レンズ）を通して描かれ、淡々としているだけにかえってベトナム戦争の暗い側面、冷酷な論理を浮き彫りにさせた。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("酔いどれ天使", "酔いどれ天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "戦後の混乱期。ヤミ市を舞台に飲んべえの医者と結核を患う若いやくざとの交流を描いた作品。新人・三船敏郎は痩せこけた頬にギラギラとした眼光を放ち出色の演技を見せ、この1本でスターダムにのし上がった。映像とかけ離れた音楽を配する演出手法は、この作品以降多用されている。戦後の混沌としたエネルギッシュな雰囲気が見事に描かれた本作は、黒澤＝三船の黄金コンビ誕生の記念すべき作品である。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラ対モスラ", "ゴジラ対モスラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「キングコング対ゴジラ」の空前の大ヒットの夢よもう一度とばかりに企画された一編。名古屋にほど近い干拓地から突然ゴジラが出現し、名古屋市内へ乗り込み破壊の限りを尽くす。正義の怪獣モスラは日本を守るためにゴジラに闘いを挑む。ゴジラがデビュー作をしのぐ徹底した凶暴な悪役ぶりをみせ、モスラの成虫及び幼虫と死闘を演じる。この作品でゴジラの悪のイメージは終わり、以降のシリーズではモスラをしのぐ正義の怪獣として、地球の平和を乱す宇宙人や怪獣と激闘を展開することになる。“ゴジラ”シリーズの一つの転換期を示す作品。", "1964-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鹿鳴館", "鹿鳴館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "三島由紀夫の同名戯曲を市川崑が演出した。明治中期、日比谷の鹿鳴館で催された舞踏会に集った人々と、それを襲撃しようとたくらむ自由党残党の群像を描いた大作。市川崑の演出は原戯曲のムードを、錦絵のごとき絢爛豪華な美術の中に再現することに成功している。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dangerous Liaisons", "危険な関係", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "18世紀、フランス革命直前の退廃ムード漂うパリ社交界を舞台に、貴族たちの愛の駆け引きをスキャンダラスに描く。社交界で幅を利かせるメルトイユ公爵夫人は、ある日愛人が若い娘と結婚するという噂を耳にする。彼女はかつての愛人バルモン子爵を呼び寄せ、ある依頼をする……。禁断の書と呼ばれたラクロの小説を、イギリス出身のスティーブン・フリアーズ監督が、ジョン・マルコビッチ、グレン・クローズ等豪華キャストで映画化。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北京保早／GOOD MORNING BEIJING", "おはよう北京", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "変わりゆく中国を背景に描く、ドキュメンタリー・タッチの人間ドラマ。北京のバスの車掌をしているアイホンは、寝たきりの祖父と二人暮らしだった。日曜日、アイホンはデパートで運転手に採用されたばかりのヨン・チアンと偶然会い、食事をともにする。二人は急速に親しくなるが、リッチな生活に憧れるアイホンと、何事にも慎重なヨンの仲は次第に冷えていく。そんなある日、アイホンはシンガポールからの留学生クークーと出会い、愛しあうようになり妊娠するが、クークーがニセ留学生とわかり路頭に迷うことに……。", "1990-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNA DONNA DA SCOPRIRE", "虜−乱されて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "セクシーな女性ロック歌手と撮影の仕事で知り合った新進カメラマンが、彼女の気まぐれに振りまわされながらも深い仲になっていく。しかし、彼女には恋人でもある嫉妬深いマネージャーがおり、彼にもフィアンセがいて、複雑な四角関係はやがて悲劇的な結末を迎える。モデル出身のスマの肢体美は見事。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The 39 Steps", "三十九夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "　1915年に発表されたスパイ小説「39階段」を脚色したロマンチック・スリラー。無実の罪を着せられた男が、逃走の末に真実と人間の隠された本性に気づくという、ヒッチコックお決まりのテーマが初めて用いられた。リチャードは偶然居合わせた事件がもとで、謎の女性を部屋へ泊めることになる。翌朝、何者かに刺殺される彼女。リチャードはダイイングメッセージを残され……。逃亡中に知り合う人々の嘘が、物語に起伏をもたらしている。", "1936-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Universal Soldier", "ユニバーサル・ソルジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "凶悪テロリストたちが観光客を人質にとるという事件が発生する。この危機をいとも簡単に解決してしまったのが、ペリー大佐率いる特殊部隊“ユニバーサル・ソルジャー”。戦死した後、蘇生された彼らは、記憶や人間性を剥奪された戦闘マシーンだった。この部隊の正体を暴くため、TVレポーター、ベロニカが基地に潜入するが、ソルジャーたちに捕らえられ、処刑されることに。その瞬間、一人の兵士リュックが記憶の断片を取り戻し、とっさにベロニカをかばい、彼女とともに逃走。一方、彼とは逆に戦場での狂気を思い出してしまった兵士スコットは、大佐をも殺し仲間を引き連れ、執拗に二人のあとを追う……。肉体派スター、J・C・ヴァン・ダムとD・ラングレン共演によるSFアクション。特に出世作「ロッキー４　炎の友情」以降、悪役が続いたラングレンの憎々しげなキャラクターが印象的。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明日への遺言", "明日への遺言", "", "", "http://image.eiga.k-img.com/images/movie/1954/315x210.jpg?4154", 110, "第2次大戦後の戦犯裁判で、信念を貫き、部下の命を守った岡田資の生涯を綴った大岡昇平の「ながい旅」を、「雨あがる」「博士の愛した数式」の小泉堯史監督が映画化。第2次大戦時、無差別爆撃を実行した米軍兵士を正式な審理を行わずに処刑した罪で、戦後、B級戦犯として裁判にかけられた元東海軍司令官・岡田資中将。彼は家族が見守る中、法廷で「全ての責任は司令官たる自分にある」と主張する。彼に下された判決とは……。", "2008-03-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("M", "M（エム）", "", "", "http://image.eiga.k-img.com/images/movie/54114/315x210.jpg?3348", 109, "歴史活劇アクション「デュエリスト」（05）のイ・ミョンセ監督とカン・ドンウォンが再びタッグを組んだミステリー。派手なキャリアと外見で人気のベストセラー作家ハン・ミヌは、結婚を間近にしてスランプに陥っていた。ある日、不眠症に悩まされ夢遊病のように路地裏のバーを訪れると、初恋の相手ミミと再会する……。", "2007-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陽炎座", "陽炎座", "", "", "http://image.eiga.k-img.com/images/movie/35574/315x210.jpg?4027", 139, "「ツィゴイネルワイゼン」（1980）の成功を受けて移動式映画館シネマ・プラセットの第2弾作品として製作された“浪漫3部作”の2作目。泉鏡花の同名小説を原作に、偶然知り合った美しい女に翻弄される劇作家の姿を華麗な色彩美の映像で描く。年号が大正から昭和に変わった東京。劇作家・松崎春狐は、奇妙な偶然が重なり謎の美女・品子と3度も出会う。やがて品子から「4度目の逢瀬は恋死なねばならない」と心中を誘う文を受け取った松崎は、品子がいる金沢へ向かうが……。「ツィゴイネルワイゼン」に続き田中陽造が脚本を担当。主演は松田優作。2012年、“浪漫3部作”の「ツィゴイネルワイゼン」「夢二」（91）とあわせてニュープリントでリバイバル上映。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　UNHOLY", "ファイナル・エクソシスト　悪魔の封印", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「スター・ウォーズ」のジョン・ダイクストラがSFXを担当したオカルト・ホラー。主任司祭に任命された神父マイケルが、前任者が怪死を遂げたため３年間閉鎖されていた聖アグネス教会に着任する。しかし、マイケルの周辺で次々と異常現象が発生。マイケルの恩師であるシルヴァ神父は、ある人物を教会に送り込むのだが…。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRISTO SI E´ FERMATO A EBOLI", "エボリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 152, "社会派ロージ監督の集大成ともいうべき作品。ファシズム政権下のイタリアで反ファシズムの作家レービは、南イタリアの荒涼とした村、エボリに流刑にされる。そこでの村人たちとの交流を通じて、レービは北部では忘れられた本当のイタリア人の姿を知る。ロージ固有のリアリズムに加えて豊かな叙情性の感じられる力作。モスクワ映画祭で金賞を受賞した。", "1979-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAKING LOVE", "メーキング・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ホモセクシュアルを真正面から撮り上げた異色作。結婚して８年、裕福な生活を営んでいた医者のザックは、ある日ゲイの患者と出会い、その道へ。やがて、“男の恋人ができた”と言って離婚するザックだが……。同性愛者の生きざまが、シャレたタッチで描かれる。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TREMORS", "トレマーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ネバダの砂漠地帯に異常な震動が観測された。若い女性地震学者と付近の町の住人は謎を解明しようと力を合わせるが、やがて無残に切り裂かれた死体が一つ、また一つと現れる。一体何者の仕業なのか……。低予算ながらテンポのよい演出と登場人物の多彩なキャラクターで最後まで楽しめる快作。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Genius Party Beyond ジーニアス・パーティ　ビヨンド", "Genius Party Beyond ジーニアス・パーティ　ビヨンド", "", "", "http://image.eiga.k-img.com/images/movie/53854/315x210.jpg?2220", 0, "「鉄コン筋クリート」のSTUDIO4℃による、気鋭のクリエイターが各々作りたい作品を作るというコンセプトでオリジナルの短編作品を発表する「Genius Party」の第2弾。参加監督は「アニマトリックス」の前田真宏、「キル・ビル」アニメパートの中澤一登、「MEMORIES」の森本晃司ら。", "2008-10-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　MAN　WHO　CRIED", "耳に残るは君の歌声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1920～30年代という激動の時代を背景に、渡米した父親の行方を探して旅するロシア人少女の波乱に富んだ人生を描く感動作。プッチーニやヴェルディのオペラの名シーンやロマの民族音楽など、幅広いジャンルの音楽が効果的に使われておりドラマを盛り上げる。C・リッチ、C・ブランシェット、J・デップら個性派スターの共演も見もの。", "2000-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ghostbusters Ⅱ", "ゴーストバスターズ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「ゴーストバスターズ」の続編。前作の破壊活動の修理費が払えず、営業停止処分を受けたゴーストバスターズだが、300年の眠りから覚めた霊界の大魔王ビーゴと対決するために再結集。今回のクライマックスには自由の女神が登場。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ali", "ALI アリ", "", "", "http://ecx.images-amazon.com/images/I/21U6G-mv7CL._SL210_.jpg", 157, "伝説的なボクサー、元世界ヘビー級チャンピオン、モハメド・アリの半生を映画化した人間ドラマ。本名の゛カシアス・グレイ゛を名乗っていた頃、ヘビー級の王座を獲得し、一躍スーパースターとなったアリは、徴兵拒否をしたことからタイトルを剥奪されてしまう。社会や差別の壁と闘いながら再び王座に挑戦するアリの波乱の生涯をウィル・スミスが熱演。", "2002-05-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ST.VALENTINE’S DAY MASSACRE", "マシンガン・シティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "有名なシカゴ暗黒時代のアル・カポネによるバレンタインデーの虐殺事件をもとに、B級映画の鬼才R・コーマンがテンポ良く描いたギャング映画の佳作。1920年代のシカゴ。アル・カポネとバグス・モランの二人のボスは激しく対立していた。カポネ側はモラン一味を片づけるための計画を企てる。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　COUPLE　TEMOIN", "モデルカップル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「ポリー・マグー　お前は誰だ」のW・クラインが1967年に発表した異色作。未来都市計画の一環として、実験用モルモットに選ばれた典型的なフランス人の若いカップル。二人の生活は社会心理学者の監視下に置かれ、フランス全土に24時間テレビで完全中継される。一躍スターになるが、“見られる生活”に二人は精神的に追い詰められていく……。アニエスbによる衣装やカルテルのプラスティック家具など、無機質でスタイリッシュなヴィジュアルがいかにもクラインらしい。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニセ札", "ニセ札", "", "", "http://image.eiga.k-img.com/images/movie/53874/315x210.jpg?4690", 94, "お笑い芸人、放送作家といった本業のかたわら、「ゆれる」「少年メリケンサック」など多数の映画に出演し、俳優としても活躍する木村祐一の初監督作品。戦後の混乱が続く昭和20年代、貧しい山村の小学校で教頭を務めるかげ子は、元教え子でブローカーのシンゴからニセ札作りをもちかけられる。初めは断るかげ子だったが、村の庄屋に説得され腹を括る......。かげ子に倍賞美津子、シンゴに板倉俊之（インパルス）の他、村上淳、木村祐一、段田安則ら個性派キャストが集結。", "2009-04-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("S.H.E.（SECURITY HAZARDS EXPERT）", "S.H.E. クレオパトラ・ジャガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "アメリカ情報局の敏腕女性諜報員ラビニアは、イタリア諜報部からの依頼を受けて、西側の国家秘密漏洩事件に取り組む。事件の首謀者とみられるフーパーなる人物の行方を捜すため、彼と関係の深いマニヤスコ男爵の豪邸に潜入し……。グラマラスなヒロインが派手なアクションを見せる。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PILLOW　TALK", "夜を楽しく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "R・ハドソンとD・デイのコンビで描くロマンティック・コメディ。インテリア・コーディネーターのジャンは、階上に住むプレイボーイの作曲家ブラッドと電話回線を共有している。彼女は、いつも彼が電話で女性を口説いているのを聞いて呆れ顔だ。ある日、彼女はナイトクラブで声をかけてきた男と恋に落ちるが、彼こそがブラッドだった……。軽やかなセリフと歌で、波乱の恋の行方を綴る。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("良いおっぱい悪いおっぱい", "良いおっぱい悪いおっぱい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "詩人・伊藤比呂美とその夫の大学教授・西成彦の妊娠・出産・子育てエッセイを、原作の持つほのぼのさを損うことなく映画化。ドラマティックなところは微塵もなく、ひたすらママとパパのわが子に対する苦労と喜びを、淡々と綴っていく微笑ましい作品だ。中村ゆうじ演じるユニークな父親像も見もの。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILVERADO", "シルバラード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "西部の街シルバラードを各々の目的で目指すガンマン４人組が、街で幅をきかす悪人どもを一網打尽にするまでを描く、正統派西部劇の快作。しつこい追っ手を振り切るガンマンのエメット、仲間の裏切りにあって砂漠のど真ん中で身ぐるみはがされた風来坊ベイトン、悪徳保安官の手で縛り首寸前だったジェイク、それに黒人ガンマンのマルと、それぞれの登場を印象づける見せ場のあとに４人は合流、街の悪徳ボスと死闘に至る。馬上のガンプレイ、砂塵、幌馬車隊、開拓部落、街の酒場……。古き良きウエスタンのムードを、俊英カスダン監督が見事に再生。ラストの一大銃撃戦も、サスペンスと迫力にあふれた見せどころだ。DVDは「コロムビアトライスター ザ・ウェスタン・ムービーズBOX(2)」に収録。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Monsters, Inc.", "モンスターズ・インク", "", "", "http://ecx.images-amazon.com/images/I/51kdmy0EEXL._SL210_.jpg", 92, "モンスターズ株式会社は、人間界に侵入して人間の子供の悲鳴を採集、それをエネルギー源にモンスターシティに電力を供給する会社。しかし、子供はたいへん有害なので、シティに連れ帰ることは禁止されている。同社のエリート社員、サリーはみんなが憧れる悲鳴採集の名人だが、ある日、人間の子供、ブーが、彼についてシティに来てしまう。「トイ・ストーリー」の監督、ジョン・ラセターが製作総指揮を担当。", "2002-03-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("異聞猿飛佐助", "異聞猿飛佐助", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "関ヶ原合戦後、忍者が入り乱れて熾烈をきわめている徳川・豊臣の攻防戦。そんな中、真田幸村輩下の猿飛佐助は、徳川方の総帥・郡山帯刀が豊臣方に寝返ろうとしているのを知る。佐助は諏訪の地で帯刀を見つけるが、実は東西に通じていたスパイ・豊臣方の野尻鷹之介が一行を待ち伏せていた……。篠田正浩はこの作品を最後に、会社側の合理化案への不満から松竹を退社した。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("晴れたらポップなボクの生活", "晴れたらポップなボクの生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "会社を辞めて放浪生活を始めた青年の幸せ探しの旅路を爽やかに描いたヒューマン・ドラマ。会社を辞め、寮を追い出された尚樹は、河川敷のダンボールハウス村でホームレスの仲間たちと青空生活を送ることにしたのだが……。映画初主演となる芸人コンビ“カラテカ”の矢部太郎や、“ピーター”こと池畑慎之介がホームレス役を好演。", "2006-01-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エティーのものがたり", "エティーのものがたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 15, "絵本作家・渕上サトリーノが原作・監督を手がけた短編CGアニメーション。自分探しの旅を続けるエティーの物語を、絵本のような淡く繊細なタッチで描いていく。自分が何者なのかを知るため、たった1人で旅を続けていたエティー。ある嵐の夜、雨宿りするために大きな樹の下に入りこんだエティーは、そこで優しいおばあさんと出会う。やがてエティーはおばあさんと一緒に暮らしはじめるが……。", "2007-10-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三匹の侍", "三匹の侍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "TVの人気シリーズの映画化でTVでも演出を担当した五社英雄初の劇場用映画。旅の浪人・柴左近、桜京十郎、桔梗鋭之介の３人が、悪政に苦しむ農民のために悪代官と戦う。ダイナミックなチャンバラ・シーンが見もの。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NURSE", "致死治療", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "父が横領の罪で告発され、無罪を訴えて一家心中を図ったと知った看護婦が、父を告発した社長への復讐を図るサスペンス。心臓発作を起こし、自宅療養するボブの付き添い看護婦として現れたローラ。半身不随で口も聞けないボブにすべてを打ち明けた彼女は、彼の目の前で妻を殺害。次に長男を誘惑する……。医療知識を生かし、自然死に見せかけてクールに殺人を繰り返していく様が恐怖をあおる。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新吾番外勝負", "新吾番外勝負", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "新吾は剣を棄て、母・お鯉の方、幼なじみ・お縫とともに、人里離れた山中に居を構え平和な生活を送っていた。そこに剣鬼・雑賀彦十郎が新吾を追って山中を訪れ、対決を迫る。大川橋蔵の美剣士ぶりも、少々トウがたってきた。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大日本人", "大日本人", "", "", "http://image.eiga.k-img.com/images/movie/1894/315x210.jpg?3207", 100, "ダウンタウンの松本人志が映画監督デビューを果たした注目作。松本自身が主演と脚本も務め、構想に5年の歳月を費やしたオリジナリティ溢れるストーリーが展開していく。共同脚本は、松本の幼なじみでもある放送作家の高須光聖。共演には竹内力、神木隆之介、ミュージシャンのUA、お笑い芸人の板尾創路ら個性的なメンバーが顔を揃える。07年のカンヌ国際映画祭では監督週間部門に正式招待された。", "2007-06-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マークスの山", "マークスの山", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "第109回直木賞を受賞した高村薫のベストセラー小説を、崔洋一監督が映像化したサスペンス大作。猟奇的な犯行を繰り返す犯人のはかない半生、命を狙われたエリートたちの憔悴、そして事件の核心に迫る刑事たちの生身の姿をリアルに描く。1976年南アルプス北岳、将来を約束された５人のエリートは殺人の事実を葬りに山へとやってきた。そして同じ日、同じ山中に一家心中の車から辛うじて助かった少年・裕之がいた。それから19年後、元暴力団員、畠山と検事の松井が殺害される。松井は19年前の殺人事件に関係するエリートの一人だった。畠山と松井を結ぶ線上に弁護士の林原が浮かび上がり、一匹狼の刑事・合田は捜査妨害を受けながらも事件解決に全力を注いでいく……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TESE テセ", "TESE テセ", "", "", "http://image.eiga.k-img.com/images/movie/57484/315x210.jpg?3849", 85, "2006年からJリーグ・川崎フロンターレに所属し、10年にはドイツブンデスリーガのVfLボーフムに移籍した在日コリアン3世のプロサッカー選手、鄭大世（チョン・テセ）に迫ったドキュメンタリー。84年名古屋で生まれたチョン・テセは、父親は韓国籍の在日だが朝鮮学校で教育を受けてきたことから、自身の祖国は北朝鮮としている。06年、川崎フロンターレに入団しストライカーとして活躍するが、ナショナルチームでは北朝鮮代表の道を選ぶ。10年W杯アジア大会予選やW杯本戦、そして帰国後のブンデスリーガ移籍など、チョン・テセの活動に3年半にわたり密着。本人のインタビューも交え、さまざまな葛藤を抱える胸のうちを明らかにしていく。", "2011-12-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏の思い出　沖縄伝説", "夏の思い出　沖縄伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "人気グラビアアイドルの仲根かすみを主演に迎え、沖縄を舞台に描いたファンタジー映画。アイドルとしての忙しい毎日に疲れぎみのかすみの元に、1通の絵はがきが届いた。はがきの写真に懐かしさを覚えたかすみは、幼い頃に暮らしたことのある沖縄へと向かう。かつて遊んだ入江にたどり着いたかすみを待ち受けていたのは、少女時代に憧れていた少年だった。その少年は、沖縄に古くから伝わる精霊・キジムナーで……。", "2006-12-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Smokin' Aces", "スモーキン・エース　暗殺者がいっぱい", "", "", "http://image.eiga.k-img.com/images/movie/33964/315x210.jpg?3768", 108, "「NARC／ナーク」のジョー・カーナハン監督が、「パール・ハーバー」のベン・アフレックや「オーシャンズ11」のアンディ・ガルシアら豪華キャストを迎えて放つ、ハイテンション＆スタイリッシュなバイオレンス・アクション。ラスベガスを舞台に、大物マフィアに100万ドルの賞金を懸けられた1人の男を巡って、7人の殺し屋やFBIが壮絶な争いを繰り広げる。人気R＆Bアーティストのアリシア・キーズが殺し屋役で出演。", "2007-05-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ライフライン", "ライフライン", "", "", "http://image.eiga.k-img.com/images/movie/56564/315x210.jpg?4028", 19, "地球外生命体に侵略され、ゾンビが徘徊（はいかい）する日本を舞台にした異色ホームドラマ。独居老人の二郎は中国人ホームヘルパーのホイと籠城生活を送っていたが、トイレットペーパーを切らしてしまう。ホイは代用品を用意するが、頑固な二郎はそれが気に入らず……。若手クリエーターの発掘・育成事業「コ・フェスタPAO」のプロジェクトの1つとして製作された短編。劇場公開にあわせ、監督の渡辺裕子の過去作「惑星ブーケ」「愚か者は誰だ」「UBIQUITOUS」も同時上映。", "2011-05-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOTH", "GOTH", "", "", "http://image.eiga.k-img.com/images/movie/54094/315x210.jpg?3325", 96, "第3回本格ミステリ大賞を受賞した乙一のベストセラー小説を映画化。主演は「青い鳥」「シルク」の本郷奏多と、本作が劇場デビューとなる高梨臨、監督は「ポチの告白」の高橋玄。人間の残酷な面に異常なまでの興味を持つ高校生、神山樹と森野夜は、周囲からは理解しがたい嗜好を共有し、次第に惹かれあっていく。そんなある日、最近多発している猟奇殺人について事細かに記された手帳を森野が拾ってきて……。", "2008-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAD MAX", "マッドマックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "黒ずくめの革ジャン姿、銃身の短いショットガン、マグナム44、カスタム・チューンのV8エンジンを積んだムスタングを駆って悪を徹底的にやっつけるスーパー・ヒーロー、マックスの登場だ。シリーズ第１作の本作は、暴走族と追跡専門の警官とのワイルドな戦いを非情なタッチで描いたハードな一編。", "1979-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　BOSSU", "愛と復讐の騎士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "フランスの美しい自然と壮麗な城をバックに展開する、剣劇をメインにしたロマン史劇。1997年のセザール賞で衣装デザイン賞を受賞したゴージャスなファッションにも注目。17世紀のルイ十四世時代のフランスが舞台。残酷な裏切りに遭い放浪の身となった元王女を、主人公の騎士が守り復讐に燃えて戦う。", "1997-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEMI・鳴かない蝉", "SEMI・鳴かない蝉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "三池崇史作品の常連俳優、遠藤憲一主演による一風変わった群像劇。暴力事件を起こしたことから人生が急転、タクシー運転手からヤクザに転身した男を中心に、アウトローたちの姿を描く。正義感から暴力事件を起こしてしまったタクシー運転手、石崎。失業、そして離婚と不幸が相次ぎ途方に暮れた彼は、敬愛する工藤が生きる任侠の世界に入る。しかし抗争事件に巻き込まれ、金沢に逃げることに……。ビデオタイトルは「SEMI・男たちの闘い」。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道記者２ 馬券転生篇", "極道記者２ 馬券転生篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "競馬記者の生き様を描く第２作。“極道記者”と呼ばれる競馬記者の松崎は、恋人にもツキにも見放され近頃ついていない。そんな彼の前に、一点買いで万馬券を的中させる老賭博師・徳大寺が現れる。彼に興味を持った松崎が家を訪ねると、彼は関東ヤクザの大物で、関東と関西の抗争を治める中心人物だった……。競馬はもちろん、麻雀、チンチロリン、シリーズ初登場のバカラと白熱したギャンブル・シーンも満載。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FLINTSTONES IN VIVA ROCK VEGAS", "フリントストーン2／ビバ・ロック・ベガス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "地球の石器時代に似た文明都市の騒動を描くファンタジー・コメディの続編。主人公フレッドの独身時代にさかのぼった前日談的な物語となっている。若いフレッドと相棒バーニーは仕事も恋人も得て大はしゃぎ。ところがフレッドの恋人ウィルマは、実は大富豪の令嬢だった。彼女と寄りを戻そうとするカジノ王の罠におちたフレッドは、愛を失い窮地に追いこまれてしまう。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ДЕВУШКА С КОРОБКОЙ", "帽子箱を持った少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "斬新な映像感覚で今もなお数多くのファンを魅了する、旧ソ連の名匠B・バルネットのサイレント・ムービー。軽快な音楽をバックに展開するおしゃれで可愛らしいコメディ。帽子作りで生計を立てるナターシャは、モスクワ行きの列車の中で知り合った貧乏学生イリヤの住まいを確保するため、大胆にも偽装結婚を敢行する。しかし、納品先の主人から受けとった宝くじが当たったことで、二人は大騒動に巻き込まれてしまう……。", "1927-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("草の乱", "草の乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「大河の一滴」「ひめゆりの塔」の神山征二郎監督作。1884年11月１日に勃発した、世にいう“秩父事件”にスポットを当て、明治政府の悪政を変えようと立ち上がった、農民を母体とする困民軍の９日間に渡る戦いが描かれる。監督自身が長年温め続けてきた企画だけに、当時の政治背景や困民党員各々の人物像が深く掘り下げられ、見ごたえある歴史ドラマに仕上がった。事件の中心人物、井上伝蔵を緒形直人が好演している。明治16年。秩父郡下吉田村では、デフレや世界的不況の影響を受けた養蚕農民たちが困窮にあえいでいた。生糸商家を営む井上伝蔵は、今日の暮らしにも困る人々の窮状を国に訴えるため、ある大きな決断をする。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大空の野郎ども", "大空の野郎ども", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "新聞社のカメラマン、清川は仕事熱心のあまり危険を省みず、飛行機を大事にするパイロット、磯部としばしば対立する。ある日、台風が中部地方を襲う。セスナ機で飛び立った二人は、山間に孤立した避難民を発見するが……。大空を駆ける男たちの友情と冒険を描いていく。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mamma Mia!", "マンマ・ミーア！", "", "", "http://image.eiga.k-img.com/images/movie/53484/315x210.jpg?2458", 108, "ABBAのヒット曲で構成されたブロードウェイ・ミュージカルをオスカー女優メリル・ストリープ主演で映画化。エーゲ海に浮かぶギリシャの小島で、20歳のソフィは結婚式を間近に控えていた。母子家庭で育ったソフィの願いは父親とバージンロードを歩くこと。母親ドナの日記を内緒で読んだソフィは、父親の可能性がある母の昔の恋人3人に招待状を出すのだが……。監督は舞台版も手掛けているフィリダ・ロイド。共演に5代目007のピアース・ブロスナン、コリン・ファースほか。", "2009-01-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Goupi mains rouges", "赤い手のグッピー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ヌーベル・バーグの開祖とも言えるフランスの名匠ジャック・ベッケル監督による、ユーモラスなサスペンスドラマ。パリから遠く離れた村に住むグッピー一族は、財産がよそ者に渡るのを防ぐため血縁結婚を繰り返し、お互いをあだ名で呼び合っていた。ある日、彼らはパリで成功をおさめたムッシューを娘の結婚相手にしようと呼び寄せる。その頃、グッピー氏の妻が遺体となって発見され、一族のはぐれ者の赤い手が捜査を開始する……。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハイティーンやくざ", "ハイティーンやくざ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "鈴木清順がB級青春アクションを連作していた時代の１本。商店街でのさばっていたヤクザを倒したことから、本意ならずも、町のヒーロー＆用心棒にまつりあげられていく高校生の青春と苦悩を描く。清順モードに包まれ、画面に登場する唐突なシーンなど、映像的からくりがいっぱい。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フローズンライフ", "フローズンライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "死亡した男の凍結精子をめぐって繰り広げられるヒューマンサスペンス。ある日突然、りりの元に届けられた亡き夫の凍結精子。最愛の夫の死を受けいれられずにいたりりは、凍結精子には触れないまま、都会での暮らしを捨てて田舎の古民家で暮らしはじめる。そこへ古民家に興味を抱く青年・渉が現われるが、渉の本当の狙いは凍結精子を奪うことだった……。主演を務めるのは、本作が女優デビューとなるシンガーの邑羽莉。", "2007-09-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DE GUERRE LASSE", "夏に抱かれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "1942年、ナチス占領下のフランス。レジスタンスに身を投じるアリスは、恋人ジェロームとともに、彼の幼友だちシャルルの家に身を隠す。政治には無関心なシャルルだったが、アリスの美貌に心惹かれて……。フランスの名匠R・アンリコが、３人の男女の“トライアングル・ラブ”を、しっとりとリリカルに綴った、大人の純愛物語。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOGFIGHT", "恋のドッグファイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「殺したいほどアイ・ラブ・ユー」「マイ・プライベート・アイダホ」と２本続けて、風変わりな役を演じ、正統派青春スターの道をあえて自分から嫌っているかに思えたR・フェニックスが、本来のナイーブな青年像に戻った青春ラブ・ストーリー。ベトナム出征前夜の若き海兵隊員たち。彼らは恒例の“ドッグファイト・ゲーム”を楽しんでいた。これは町中から誰が一番ブスな女の子をナンパしてくるかというものだ。隊員の一人、バードレースもウエイトレスのローズを、やっとのこと誘い出すのだが、彼女にドッグファイトであることが知られてしまう。その晩遅く、バードレースはローズにまじめなデートを申し込みに行くが……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火焼紅蓮寺／THE　RAPE　OF　THE　RED　TEMPLE", "レジェンド・オブ・フォース　激闘飛龍方世玉・外伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "17世紀、謎の軍隊によって少林寺は焼き討ちにされ、天才武道家・方世玉は美少女トゥトゥとともに悪の本拠地・紅蓮寺に捕らわれる。そこで少林寺門下生が残虐な拷問を受ける姿を目の当たりにした方世玉は、彼らを救おうと紅蓮寺首領・老色鬼に戦いを挑む。だが、代わりに裏切り者の兄弟子ホンとの戦いを強いられ、絶対絶命のピンチに追い込まれる。少林寺を守るために戦う若き武道家の活躍を描いた格闘アクション。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXIT WOUNDS", "DENGEKI／電撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "キレやすい性格が災いして、ついには交通整理係にまで降格させられたデトロイト警察のハミダシ刑事ボイトは、警察が押収したヘロインが保管庫から強奪される事件の捜査を始めていく。S・セガールがただ強いだけではなく、人間的欠陥も併せ持つ愛すべきタフガイを演じたハード・アクション映画。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ざわざわ下北沢", "ざわざわ下北沢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "なにか不思議な存在感がある街“下北沢”。この街に魅了された市川準を始め製作スタッフが、下北沢とそこに行き交う人々を多彩な角度から描いた地域密着型映画。この街で生まれ育った20歳の有希は、この街のぬるま湯的な暖かさや、自分と恋人の関係に疑問を抱き始めていた。原田扮する芝居役者との出会いをきっかけに、自分も変わらなきゃと小さな勇気が湧いてくる。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOO BEAUTIFUL TO DIE（SOTTO IL VESTITO NIENTE II）", "華麗なる殺人 死ぬには美しすぎて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ファッションの町ミラノで、売れっ子モデルの殺人事件が発生。彼女は殺される前夜モデルクラブの経営者宅に招かれ、業界の実力者に犯されていた。やがてこの強姦現場にいた人々が次々と殺される。カンヌ映画祭コマーシャル部門のグランプリ受賞ほか、CM界で名を上げたD・ピアーナの映画第１作。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・花嫁戦争", "喜劇・花嫁戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "斎藤耕一監督、和田アキ子主演によるコメディ映画。政略結婚を嫌い式場から逃げ出した秋子は、ふとしたことから鎌倉の里見家に家庭教師兼お手伝いさんとして住み込み、すさんだ里見家を明るく改革する。和田アキ子の特異な個性がうまく生かされた。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le vent nous emportera", "風が吹くまま", "", "", "http://image.eiga.k-img.com/images/movie/1794/315x210.jpg?3547", 118, "山肌を曲がりくねって伸びる道を車が行く。乗っているのはTVクルー。彼らはテヘランの西約700キロ離れた村、シアダレへ向かっている。ようやく村の入り口に近付いたとき、ファザードという少年が駆け寄ってきた。少年の叔父からクルーの案内役を言いつかってきたのだ。少年は、クルーの本当の目的−珍しい儀式が行われる葬式を撮影する−を知っているが、「宝を探している」と嘘をつくようクルーに言い聞かせられる。", "1999-12-04", "2,15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Die 4. Revolution - Energy Autonomy", "第4の革命　エネルギー・デモクラシー", "", "", "http://image.eiga.k-img.com/images/movie/57414/315x210.jpg?1864", 83, "太陽光、風力、水力、地熱など再生可能な自然エネルギー源の可能性を伝えるために製作されたドキュメンタリー。ドイツにおいて大量の風力発電導入を促した1990年の電力買い取り法、太陽光発電導入の起爆剤になった2000年の再生可能エネルギー法の2つの法律を制定させた中心人物で、ドイツ連邦議会議員やヨーロッパ太陽エネルギー協会会長を務めるヘルマン・シェーアをナビゲーターに、著名な環境活動家やノーベル賞受賞者、政治家らが、100％再生可能なエネルギーへのシフトが実現できることをさまざまな角度から分析し、紹介していく。", "2011-12-17", "7,18,36,2,9,77,77,1,17,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bowling for Columbine", "ボウリング・フォー・コロンバイン", "", "", "http://image.eiga.k-img.com/images/movie/1024/315x210.jpg?3624", 120, "02年カンヌ映画祭に正式出品、審査員長デビッド・リンチが急遽“カンヌ映画祭55周年記念特別賞”を設けて授賞した話題作。監督は、著作「アホでマヌケなアメリカ白人」（柏書房）が全米ベストセラーになった過激ジャーナリスト、マイケル・ムーア。なぜアメリカで銃犯罪が多発するのかをめぐり、99年のコロンバイン高校銃乱射事件犯人の同級生から、現全米ライフル協会会長である「ベン・ハー」の人気俳優チャールトン・ヘストンまで、アポなし突撃取材。", "2003-01-25", "10,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Justin Bieber: Never Say Never", "ジャスティン・ビーバー　ネヴァー・セイ・ネヴァー", "", "", "http://image.eiga.k-img.com/images/movie/56014/315x210.jpg?4787", 105, "人気ポップス・R＆Bシンガー、ジャスティン・ビーバーの3Dライブドキュメンタリー。カナダの田舎町出身の少年ビーバーが、世界的スターに上りつめるまでの軌跡を、幼いころの貴重な映像や2010年のマディソン・スクエア・ガーデンでのライブ映像を交えながら描く。監督は「ステップ・アップ3D」「ステップ・アップ2 ザ・ストリート」のジョン・チュウ。", "2011-05-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千曲川絶唱", "千曲川絶唱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "豊田四郎監督による純愛ものの秀作。突然襲っためまいから交通事故を起こしてしまったトラック運転手の五所川は、その時の警察と医者のやりとりから自分が白血病であることを知る。やけになった彼は上京し、様々な医者を訪ねて白血病の実態を探るが、不治の病と知り、絶望して富山に帰る。彼の帰りを待っていた奈美は、看護婦としてではなく、一人の女として、間もなく死ぬであろう男に激しい愛を捧げるのだった……。登場人物の動きや美しい風景を、きめ細かいカメラワークで捉えた映像が素晴らしい。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON’T TELL MOM THE BABYSITTER’S DEAD", "ドリーム・ガール ママにはないしょの夏休み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "５人兄弟の長女スウェルは、母親のいない夏休みを楽しむつもりのはずが、年寄りのベビーシッターがやって来た。厳しい毎日に我慢も限界になった時、そのベビーシッターが突然死。残された５人で楽しく過ごすつもりが、全財産をベビーシッターとともに墓の中に埋めてしまった。仕方なく履歴書を偽って、アパレル会社に就職したところ、偶然からトップデザイナーになってしまい……。「ビルとテッドの大冒険」のS・ヘレク監督によるファンタスティック・コメディ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドレイ工場", "ドレイ工場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "奴隷のように酷使され、疲労から事故を起こして死に至る労働者。資本家のあくなき攻撃と生活苦に耐え抜き、統一と団結に目覚め、組合を結成してドレイ工場解放を図る労働者たちの姿を、ドキュメンタリー的手法で力強く描いた作品。時代の一断面を鋭く活写。山本薩夫が総監督にあたっている。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOWNTOWN", "ダウンタウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "まじめなだけが取り柄の新米刑事とベテラン黒人刑事のコンビが繰り広げるアクション・コメディ。新米警官アレックスは、任務に忠実なあまり、町の権力者をスピード違反で検挙して、犯罪渦巻くダウンタウンへ飛ばされる。アップタウン育ちでヒヨッ子刑事のアレックスは右往左往するばかり。ある日、親友の警官が殺された。ここぞとばかりに奮起したアレックスは、経験豊富な刑事デニスと組んで捜査を開始する。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("※脂扣／ROUGE", "ルージュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "一緒に心中したはずの恋人を追って、死後の世界から舞い戻った美貌の幽霊の激しい恋心を描くファンタジー・ロマン。原作は「さらば、わが愛／覇王別姫」と同じリー・ピクワー。1930年代の香港。老舗の薬屋の御曹司チャンと娼館・紅楼の名伎フラワーは、身分違いのかなわぬ恋を嘆いて心中を遂げた。50年後、新聞社に勤めるエンのもとに、尋ね人の広告掲載を依頼する謎めいた女性が現れる。それは、心中後、あの世で一向にチャンに会えず、彼を求めて現世に戻ってきたフラワーだった。エンが、半世紀前の心中事件の真相を探り始めてみると、チャンは一命を取りとめていたことが分かる。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチバンMAX2", "ガチバンMAX2", "", "", "http://ecx.images-amazon.com/images/I/619PozTFU0L._SL210_.jpg", 0, "人気ヤンキーアクション「ガチバン」新シリーズの第2弾。渋谷で激闘の末にヤンキー軍団を壊滅させた黒永勇人は、義男やのんのんと共に歌舞伎町へと拠点を移す。そこでは歌舞伎町高校のヤンキーたちが街を支配しており、金の力でヤクザやホステスを操って悪事の限りを尽くしていた。そんな彼らの横暴が許せずケンカを売る勇人だったが、事態はやがて歌舞伎町全体を揺るがす大事件へと発展していく。前作に続き窪田正孝が主演を務める。", "2010-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Death and Life of Bobby Z", "ボビーZ", "", "", "http://image.eiga.k-img.com/images/movie/53064/315x210.jpg?3535", 96, "「ワイルド・スピード」のポール・ウォーカー主演による痛快アクション。元海兵隊員で現在服役中のティム・カーニーは、麻薬取締局の捜査官から取引を持ちかけられる。瓜ふたつの容貌を持つ麻薬ディーラー“ボビーZ”に成りすまして捜査に協力すれば、自由の身にしてくれるというのだ。しかし、この取引の裏にはある罠が隠されており……。監督は「15ミニッツ」のジョン・ハーツフェルド。共演はローレンス・フィッシュバーンほか。", "2007-11-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bombon : El Perro", "ボンボン", "", "", "http://image.eiga.k-img.com/images/movie/33924/315x210.jpg?1282", 97, "ツキに見放された中年男性が、1頭の犬との出会いをきっかけに次々と幸運に恵まれていく姿を描いた、アルゼンチン発のハートウォーミング・コメディ。20年間勤めてきたガソリンスタンドをクビになり、住む家もなくしてしまったフアンは、ある日人助けのお礼としてボンボンという名前の白い大型犬をもらう。ペットを飼う余裕などないフアンは途方に暮れるが、ボンボンと一緒に過ごすうちに少しずつ幸運が訪れはじめる。", "2007-04-14", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SHOCK", "ザ・ショック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "イタリアン・ホラーの巨匠監督M・バーヴァの遺作となった作品。同じくイタリア産ホラーの傑作「サスペリア」「サスペリア２」で主演を務め、“恐怖の女神”と呼ばれたD・ニコロディ主演。郊外の邸宅に引っ越した夫婦と息子の３人家族。夫とのいさかいを皮切りに、妻が徐々に狂い始め、遂には大爆発する。妻が見る幻覚シーンなど多彩にして鮮烈な色彩表現が恐怖のドラマを盛り上げる。", "1977-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("朱鷺島　創作能「トキ」の誕生", "朱鷺島　創作能「トキ」の誕生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "重要無形文化財保持者である観世流能楽師・津村禮次郎が30年にわたって毎夏訪れつづける新潟県佐渡で、朱鷺を題材にした新作能を制作する過程を捉えたドキュメンタリー。佐渡の小学生たちから寄せられた詩をもとに謡を書き、世界的に知られる佐渡出身の太鼓パフォーマンス集団「鼓童」のメンバーや地元住民との交流を通して佐渡色ゆたかな作品が作りあげられていく様子を追う。監督は「面打 men-uchi」の三宅流。", "2010-07-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("波止場の無法者", "波止場の無法者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "海を捨て、陸で堅気になって働こうと決心した木島だが、飲み屋ではからずもケンカに巻き込まれる。キャバレーのマスターに気に入られた木島は用心棒として店につくことに。しかし、その店は香港の麻薬王に狙われていた……。「ギターを持った渡り鳥」の小林旭・斎藤武市コンビで贈るマドロスものアクション。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOSCA", "トスカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "プッチーニが円熟期に書き上げた同名オペラの映画化。1800年のローマ。共和派の画家カヴァラドッシが、脱獄した友人の政治犯をかくまった容疑で警視総監のスカルピアに捕らえられる。カヴァラドッシの恋人、歌姫のトスカは拷問を受ける恋人の姿に苦しむが、そんな彼女にスカルピアは非情な条件を出し……。“映画化”というよりは“映像化”と表現すべき実験的作品。暗闇に浮かんだかのような舞台上で壮絶な愛のドラマが展開する。また、劇中にローマの町並み、録音中の俳優の顔などのビデオ映像がインサートされ、ドキュメンタリー的要素も含んだ興味深い仕上がりとなった。", "2001-01-01", "2,7,4,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWISS FAMILY ROBINSON", "スイスファミリー ロビンソン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "J・ウィースの同名小説をディズニーがファミリー向けに映画化。船が難破して無人島に漂着したロビンソン一家の冒険生活を描く。「絶海の危難」（1925)、「新ロビンソン漂流記」（1940）に次いで３度目の映画化で、日本語吹き替え版の「南海漂流」というタイトルで公開された。また、「大漂流記」というタイトルでリバイバル上映されたこともある。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　MEDUSA　TOUCH", "メドゥーサ・タッチ／恐怖の魔力", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "サスペンスに満ちた展開と多彩なSFXで魅了するSFモードのスリラー。念力パワーで飛行機を墜落させたり、建物の崩壊シーンなどダイナミックな見せ場に加え、戦慄の映像が連続する。ロンドンでベストセラー作家殺人未遂事件が発生し、フランスの刑事が捜査に乗り出す。作家の日記や精神科医の証言から、彼の知られざる過去や実態が判明する……。", "1978-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hem till jul", "クリスマスのその夜に", "", "", "http://image.eiga.k-img.com/images/movie/56124/315x210.jpg?4775", 85, "「ホルテンさんのはじめての冒険」「キッチン・ストーリー」のベント・ハーメル監督が、クリスマスの夜に大切な人との時間を求めて懸命に生きる人々の複数のエピソードを交錯させて描く群像劇。イブを迎えたノルウェーの小さな町で、結婚に破たんした男がサンタに変装し、かつての我が家にもぐりこむ。男の友人の医師は2度と故郷に戻れないというコソボ出身のカップルの赤ちゃんを取り上げ、ある少年はご馳走を囲む家族よりもクリスマスを祝わないイスラム教徒の女の子と時をともにする。", "2011-12-03", "36,7,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大阪の女", "大阪の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "八住利雄原作の連続TVドラマ『女神誕生』を巨匠・衣笠貞之助が映画化。大阪の焼け残りの一画に暮らす上方落語や漫才の芸人たちの群像を、人情味豊かに描く。大映カラーが捉えた大阪の歓楽街のにぎわいや、華やかなステージの光景が見もの。京マチ子、中村鴈治郎をはじめ、名優たちの芸が冴える。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shadows in the Palace", "宮女（クンニョ）", "", "", "http://image.eiga.k-img.com/images/movie/54124/315x210.jpg?2116", 112, "「韓流シネマ・フェスティバル2008ラブ＆ヒューマン」上映作品。李氏朝鮮時代の宮廷を舞台に、宮に仕える女官の謎の死をめぐる時代劇サスペンス。「銭の戦争」のパク・チニ、「連理の枝」のソ・ヨンヒほか、豪華女優陣が競演。李氏朝鮮・第19代国王スクジョンの治世下、側室ヒビンが王の子を妊娠し、ヒビン付きの宮女ウォルヨンが首吊り死体で発見される。上官たちは宮女の自殺として事を収めようとするが、女医のチョンリョンは殺人事件として捜査を始める。", "2008-10-18", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人生劇場（青春編）", "人生劇場（青春編）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "尾崎士郎の同名小説の最初の映画化で、内田吐夢が新興キネマから日活へ復帰した第１作であると同時に、戦前の日活黄金期の頂点に立つ作品でもある。“青春編”は原作のほぼ前半部にあたり、三州吉良港出身の青成瓢吉が政治家になる志を持って早稲田大学に入学するが、学生運動や恋愛に挫折して酒に溺れる日々が続く。やがて文学を志した瓢吉は、父親・瓢太郎の自殺の悲しみを乗り越え、侠客・吉良常らに見守られながら自立していく、というストーリー。今から見れば、女を泣かせて夢も実現できず生活力もない主人公・瓢吉は、“悩めるインテリ”の典型のような男なのでそれほど魅力的には見えないが、田舎から出て来た文学青年の野暮ったさをうまく表現している点で、小杉勇の演技は出色のものといえるだろう。なお「人生劇場」ほど何度も映画化されたものも珍しく、戦前にはこの他に千葉泰樹、戦後は佐分利信、萩原遼、杉江敏男、弓削太郎、沢島忠、舛田利雄、加藤泰、深作欣二らがそれぞれ監督、佐分利版、内田版と傑作も多い。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不良番長・練鑑ブルース", "不良番長・練鑑ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "関東特別少年院・練馬鑑別所出身の不良グループ・カポネ団は、桜吹雪会と抗争を繰り広げていた。関東挺心会の会長、川島は、両者の対立を利用して、元華族の白河邸を横領しようと画策。川島の手先となった弁護士の藤原は、白河邸の権利書を奪った方に賭博場のマネージメントを任せると約束するのだが……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STILL OF THE NIGHT", "殺意の香り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "中産階級以上のアメリカ人は、個別に精神分析医に通うことが多い。夫婦間、あるいは肉親にさえ話せない悩みや問題を相談するためである。物語は、そうした患者の一人が謎の死を遂げたことから、連続殺人事件に巻き込まれる分析医の活躍をサスペンスフルに描いている。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Six-Pack", "シックス・パック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "パリを舞台に猟奇殺人犯を追う刑事の奔走が展開するサイコ・サスペンス。女性を狙った連続殺人事件が発生し、ベテラン刑事ナタンと相棒ソールは捜査に着手。やがてアメリカ大使館に勤務する米国人が捜査線上に浮上する。しかしフランス国家警察は、対米関係の悪化を懸念してナタンらを捜査から外し……。フランス映画らしいアンニュイな空気は、このジャンルの作品の中でも異色。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Passionless Moments", "キツツキはいない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "誰にも訪れるような、ちょっとした失敗や奇妙な空想の世界を、いくつものエピソードで綴る短編。“コツコツ”という物音にキツツキを連想したが、それは洗濯物をたたく音で、オーストラリアにはキツツキはいないことに気付く婦人。隣人に挨拶されたと思い、愛想笑いをしたが、誤解と知ってバツの悪い思いをする男など、小さく奇妙な世界が不思議なユーモアとともに描き出される。", "1984-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子連れ狼・死に風に向う乳母車", "子連れ狼・死に風に向う乳母車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "夏の暑い街道を、大五郎を箱車に乗せた“子連れ狼”拝一刀は酉蔵という男から、天領地の代官に納まり私腹を肥やす、猿渡玄蕃刺殺の依頼を受ける。クライマックスの拝一刀対刈谷藩200人の対決は、マカロニ・ウエスタン顔負けの迫力。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鴨川ホルモー", "鴨川ホルモー", "", "", "http://image.eiga.k-img.com/images/movie/53534/315x210.jpg?4332", 113, "「鹿男あをによし」が直木賞候補になった作家・万城目学の同名デビュー作を映画化。二浪の末に京都大学に入学した安倍は、ひょんなことから、1000年前から伝わる“オニ”と呼ばれる生き物を使った謎の競技“ホルモー”を繰り広げるという怪しいサークル“京大青竜会”に入部。当初は半信半疑のまま、先輩に言われるままに“オニ”を使役するための特訓に明け暮れる安倍だったが……。主演は山田孝之。監督は「ゲゲゲの鬼太郎」の本木克英。", "2009-04-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOULS　AT　SEA", "海の魂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "G・クーパー、G・ラフトという対照的な名優二人が共演した唯一の作品。1840年のある日、若き船長テイラーの公判が開かれた。彼は奴隷掃討のため、アメリカへと向かう船内で殺人を犯したとして告発されたのだ。同僚、金もうけをたくらむ士官、テイラーに思いを寄せる士官の妹などの思惑が絡み合う海洋群像ドラマの傑作。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRYING FIST", "クライング・フィスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "2005年のカンヌ国際映画祭で国際批評家連盟賞受賞した感動作。仕事も家族の愛もすべて失った中年男と、荒んだ青春を送ってきた若者。そんな見ず知らずの二人の男の人生が、ボクシングのリング上で“激突”する人間ドラマだ。事前の段取りなしで撮影を行ったボクシング・シーンは圧倒的な迫力！　少年院に入れられたサンファンは父親の死と祖母の病をきっかけに、ボクシング新人王戦への出場を決意。彼の対戦相手は、落ちぶれて“殴られ屋”となっていた元プロのテシクだった……。DVDタイトルは「クライング・フィスト　泣拳」。", "2006-04-15", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春琴物語", "春琴物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "谷崎潤一郎の名作『春琴抄』の２度目の映画化。盲目だが、美人の誉れ高い大阪の薬問屋の娘・お琴と、下男の佐助が主従関係を超えた愛で結ばれていく様を描く。当時の大映の看板スター・京マチ子を主演に起用。伊藤大輔監督は、身分の違いと、燃える情愛との相克に悩む男女の心理を、的確に捉えている。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEET MOVIE", "スウィート・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "性と政治への反逆に満ちた、過激でブラックな映画。マルクスの大きな顔を、舳先にかたどった船がアムステルダムの運河をいく。コミューンになっているこの船に加わった戦艦ポチョムキンの水兵の物語に、医者から処女であることを証明されたミス・ワールドが出会う冒険が絡んで、最後は前衛芝居のスカトロ乱痴気騒ぎ。マカヴェイエフが最もノッていた時期の作品だけに、ハツラツとしていて一つ一つのアイデアも面白い。水兵に扮するP・クレマンティが砂糖まみれで大熱演。", "1974-01-01", "2,10,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MORNING TERROR", "ZEROスナイパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "高温とスモッグで壊滅状態へと向かう近未来の地球。そこでは、すべての物質の供給が“エナジーチップ”により管理されていた。人々の生命はチップの残量に等しく、それがゼロになった者はエクスキューター（死刑執行人）によって抹殺されていく。ある日、エクスキューターのレンはサラという女に出会い心惹かれるが、非情にも政府から彼女の抹殺指令が下った……。", "1984-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EIGHTEEN IN A WEEK", "愛と欲望の18才 ギャング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "当時イタリアの人気アイドルだった、シモーナ・カヴァッラリの主演５作目。ある夜、サマンサはパオロという20歳の青年と出会う。彼は仲間と強盗を働き、警察に追われる身だった。だが一瞬で彼の虜になってしまったサマンサは、パオロを助けようと決心。そんな二人の前に、パオロの悪い仲間たちが現れる。", "1991-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Go！", "Go！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "恋した年上美女にピザを届けるため、東京・長崎間1300kmを宅配ピザのスクーターで激走する高校生の珍道中と恋の行方を描いたロード・ムービー。途中で何度も登場する謎のハーレー男を山崎努が飄々と演じている。1999年度サンダンス・NHK国際映像作家賞日本部門受賞作品。主題歌は山崎まさよし。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はだしのゲン", "はだしのゲン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "太平洋戦争後、戦争の悲劇にもめげずにたくましく生きていく少年の姿を描いた名作アニメ。昭和20年の広島。原爆によって町の中心地は壊滅的ダメージを受けていた。少年ゲンの場合は、父と弟を亡くし妊娠中の母と二人だけが生き残ってしまう。だが、すぐに妹が生まれ、ゲンは希望を失わずに生きる決心をする。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("疆屍先生／MR.VAMPIRE", "霊幻道士", "", "", "http://ecx.images-amazon.com/images/I/51kFnDapgUL._SL210_.jpg", 99, "中国版ゾンビ“キョンシー”を国際的に有名にした作品。サモ・ハン・キンポーの“コミック・ホラー”シリーズでは４作目にあたる。昔、中国では呪われたり、埋め方を誤った死体は吸血ゾンビ“キョンシー”となるのだった。ある日、一人の道士が、権力者に父親の墓の改築を頼まれる。ところが墓は呪われており、死体はキョンシーと化して一族を襲った。道士は危機を救うため、秘術をつくして対決するが、二人の弟子のうち一方はキョンシーにやられて自らもキョンシーとなり、もう一人も色情狂の女幽霊に取り憑かれ、てんやわんやの騒動となる……。ホラーながら全編ギャグ・タッチ。香港映画特有の壮絶なカンフー・アクションもたっぷり味わえる痛快作。", "1985-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("書劍恩仇録／THE EMPEROR AND HIS BROTHER", "書剣恩仇録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "武侠小説の大家ジン・ヨンの同名の原作を映像化したスペクタクルロマン。18世紀、清朝の皇帝・乾隆帝の時代。この皇帝が、実は赤ん坊のときすりかえられた漢人であることを知った反清の秘密結社、紅花党の党首は明朝復活の野望に燃え、宮廷に潜入するが……。壮大なセットに加え、クライマックスのカンフーアクションはスケール感たっぷり。", "1981-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents うそ　「代理人会議」", "桃まつり presents うそ　「代理人会議」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "", "2010-03-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("図々しい奴", "図々しい奴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "当時、TVで高視聴率をたたき出した人気ドラマの映画化。東映のプログラム・ピクチャーを支えた瀬川昌治監督が登板し、クレイジー・キャッツのメンバー、谷啓を映画初主演として迎えた喜劇だ。孤児である少年の夢に魅せられた男が、彼を引き取り育てることに。やがて大人へと成長した少年は上京して羊かん屋に就職し、出世街道をひた走るが・・・・・・。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キャプテントキオ", "キャプテントキオ", "", "", "http://image.eiga.k-img.com/images/movie/33854/315x210.jpg?4539", 95, "大地震により崩壊し無法地帯と化した近未来の東京を舞台に、大人たちに翻弄され反発しながらも夢を追いかけて成長していく若者たちの姿を描いた青春ムービー。映画監督を目指すフルタに人気タレントのウエンツ瑛士、その親友でギタリストを目指すニッタに若手実力派の中尾明慶が扮し、泉谷しげる、いしだ壱成、バナナマンら個性派が脇を固める。監督・脚本は、主演も務めたデビュー作「19」で高い評価を得た渡辺一志。", "2007-02-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPEEDTRAP", "新バニシングIN 60”　スピード・トラップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "BMW、ベンツなど高級車ばかりを盗み出す“ロードランナー”。捕らえてみれば、なんと“婦人警官”、というカー・アクション編。颯爽と車をぶっ飛ばす女傑ぶりは、さすが「ダーティハリー３」の女刑事、デイリーならばこそと感心。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マグニチュード 明日への架け橋", "マグニチュード 明日への架け橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "阪神・淡路大震災を機に、防災に貢献したいという映画人が集結し、ボランティア活動として製作された作品。リアルに再現された震災シーンの中に、父と子の絆を描き出す。20年前の大地震、消防署員だった辰雄の出動中に、息子・誠の目の前で母の美代子は死んでいった。以来、妻を救えなかった自責の念に駆られる辰雄と、父を恨む誠はお互い心を閉ざしたまま、誠は母の実家に引き取られた。そんな誠が特別救助隊長となって戻ってくる。相変わらず、わだかまりを抱き合う二人だったが、ある日、辰雄が20年間調べ続けた地震に関する資料を見た誠は父への思いを改めていく。二人の心が通い合ったと思われたその時、再び、大地震が起きる。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Best Chef: Kimuchi War", "食客2 優しいキムチの作り方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "韓国の人気グルメコミックを映画化した「食客」（2007）のシリーズ第2弾。日本で成功を収めた女性シェフのジャンウンは、韓国で開かれる「キムチ大会」に参加するため帰国する。10年ぶりに母親と再会したことで幼少期のつらい思い出がよみがえるが、キムチ大会を通じて自身の本当の思いに気がつく。一方、ジャンウンと姉弟同然に育ったソン・チャンもまた、同じ大会に参加していた。それぞれの思いを胸に決勝戦に進んだ2人が見つけた極上のキムチとは……。主演は「パリの恋人」のキム・ジョンウンと「母なる証明」のチン・グ。", "2011-01-08", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボールZ　龍拳爆発！！ 悟空がやらねば誰がやる", "ドラゴンボールZ　龍拳爆発！！ 悟空がやらねば誰がやる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "冒険アクション・アニメの劇場版シリーズ第16弾。シリーズ初となる音響効果にドルビー・サラウンドが導入されての、上映となった。孫悟飯たちが謎めいた老人ホイからもらったオルゴールの中に、何と勇者タピオンが封印されていた。しかし、それはホイの策略で遂に、恐るべき幻魔人ヒルデガーンが復活してしまう。凄まじい激戦の果てには……。DVDは『DRAGON BOX THE MOVIES』に収録。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VICE ACADEMY３", "バイス・アカデミー３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "元ポルノ女王G・L・アレンが主演するお色気コメディ。脱走した女囚が謎の殺虫剤を浴びて“極悪非道・超淫乱犯罪者”マラシオンに変身、ヘアースプレーを武器に暴れ回る。警察は、バイス・アカデミーの卒業生ホリーとキャンディを立ち向かわせたが……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("師弟出馬／THE YOUNG MASTER", "ヤング・マスター　師弟出馬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "広東の正風道場のチンルンは、兄弟子の裏切りからライバル道場の獅子舞い合戦に負けてしまう。師匠の命令で兄弟子を連れ戻したチンルンは、悪の根源であるキムと対決し、見事息の根をとめる。ジャッキー・チェンが監督、脚本、主演を兼ねたカンフー・コメディ。", "1980-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BETSY’S WEDDING", "本日はお日柄も良く　ベッツィの結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "誰もが経験するであろう結婚式にまつわる様々な出来事をコミカルに描いた作品。監督・脚本と花嫁の父を演じるA・アルダは、彼の末娘が結婚式を挙げた時、この作品を思いついたという。アルダ自身“結婚式とはなんと複雑な騒動を引き起こすイベントであるか”と述べており、映画の中で式に重要なかかわりを持つ両家の親族にも一流どころを配している。J・ペシ、B・ヤング、そして花嫁の母役には個性派女優M・カーンなどが、それぞれ芸達者なところを見せる。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAPE AND MARRIAGE ： THE RIDOUT CASE", "ミッキー・ロークのレイプ＆マリッジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "“夫にレイプされた”と、妻が夫を告発した。全米を騒がせた実在の事件をもとに、夫婦間の“レイプ”という病めるアメリカの大問題に挑んだ社会派裁判劇。夫役には、セクシャリティーと幼児性を併せ持つ危険な男M・ローク。夫婦が敵味方に分かれて、相手を容赦なくたたきのめそうとするクライマックスの裁判シーンは見逃せない。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版ハヤテのごとく！ HEAVEN IS A PLACE ON EARTH", "劇場版ハヤテのごとく！ HEAVEN IS A PLACE ON EARTH", "", "", "http://image.eiga.k-img.com/images/movie/56494/315x210.jpg?3900", 0, "ふとした誤解から大富豪の令嬢ナギのもとで執事として働くことになった少年ハヤテとナギの恋愛を描き、TVアニメ化もされた畑健二郎の人気コミック「ハヤテのごとく！」の劇場版アニメーション。夏休みの最後を過ごすため、ハヤテとナギらの一行は自然豊かな田舎へと赴く。ナギの大好きなゲームやアニメもなく、携帯電話の電波さえも届かない環境の中で、2人の思いを試すかのような事件が発生する。「劇場版魔法先生ネギま！」と同時上映。", "2011-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("道　白磁の人", "道　白磁の人", "", "", "http://image.eiga.k-img.com/images/movie/56954/315x210.jpg?1394", 118, "", "2012-06-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("英雄本色III 夕陽之歌／LOVE AND DEATH IN SAIGON ： A BETTER TOMORROW III", "明日への誓い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "“香港ノワール”の傑作「男たちの挽歌」シリーズ第３作。前２作のジョン・ウーから引き継ぎ、製作のツイ・ハークが監督も務め、愛を背負った男の世界をバイオレンスたっぷりに描き出している。香港映画界のトップを走る男女優、チョウ・ユンファとアニタ・ムイに加え、時任三郎が敵役で出演しているのも見どころだ。1974年、ベトナム戦争末期のサイゴン。ここに従兄弟マイケルを政府軍から救出に来たマークはこれに成功。しかし二人は暗黒街の取引で窮地に陥り、闇の支配者ホー・グループの女ボス、キティに命を助けられる。キティとマークは恋に落ちるが、そこに行方不明になっていたホー・グループの創始者ホーが現れて……。１作目で死んだマークの若き日に焦点をあて、動乱のサイゴンに燃える恋を映し出している。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SAND PEBBLES", "砲艦サンパブロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 179, "1926年の中国、上海。民族主義が台頭するなか、揚子江に出動したアメリカのオンボロ砲艦サンパブロ号と、艦長以下その乗組員たちのドラマ。内外に問題を抱えながら、一行は奥地に入ったアメリカ人宣教師救出に向かうのだが……。マックィーンの好演が光る。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ねこタクシー", "ねこタクシー", "", "", "http://image.eiga.k-img.com/images/movie/55334/315x210.jpg?1264", 106, "カンニング竹山が主演するTVドラマ「ねこタクシー」の劇場版。原作は永森裕二の同名小説で、「幼獣マメシバ」の亀井亨監督がメガホンをとる。自堕落に生きる40歳のタクシー運転手・間瀬垣勤は、社内一売り上げ成績が悪く、家庭でも妻と娘に頭が上がらないダメ男。ある日、勤の前に一匹の野良ネコ“御子神さん”が現れ、ネコを助手席に乗せた「ねこタクシー」を思いつく。", "2010-06-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("足摺岬", "足摺岬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "田宮虎彦の短編小説『足摺岬』『菊坂』『絵本』を新藤兼人が脚色、吉村公三郎が監督した作品。軍国主義の重圧が次第に強まる昭和初期。社会主義者の検挙にあいながらも自己の主張を曲げずに生きる若者を描く。田宮文学に特有の暗い叙情を見事に映像化。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ケータイ刑事 THE MOVIE3 モーニング娘。救出大作戦！　パンドラの箱の秘密", "ケータイ刑事 THE MOVIE3 モーニング娘。救出大作戦！　パンドラの箱の秘密", "", "", "http://image.eiga.k-img.com/images/movie/55804/315x210.jpg?2419", 89, "人気テレビドラマ「ケータイ刑事」シリーズの劇場版第3作。6代目ヒロイン・大政絢、7代目・岡本あずさ、8代目・岡本杏里の歴代ヒロインが結集する。テレビ版も手がけた「blue」（2001）の安藤尋監督がメガホンをとる。国民的アイドル「モーニング娘。」を抹殺するという脅（きょう）迫状が警視庁に届き、銭形海（大政）、銭形命（あずさ）、銭形結（杏理）の刑事3人が警護を担当することになる。しかしコンサート終了後、ホテルへ移動したバスからは「モーニング娘。」と同乗していた命の姿がこつ然と消えていた。「モーニング娘。」が本人役で出演している。", "2011-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEYOND THE VALLEY OF THE DOLLS", "ワイルド・パーティー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「ファスター・プシィキャット！キル！キル！」などでカルト的人気を呼ぶR・メイヤーが、初めてメジャー資本で撮り上げた一編。成功を夢見て、田舎からロスにやって来たガールズ・バンド３人娘とそのマネージャー担当の青年が直面する悲劇を、めくるめく映像世界の中に照らし出す。ショービズ界舞台裏への痛烈な風刺も感じさせる、“苦味”濃厚な青春映画。同監督ならではのカラフルな編集感覚も冴えている。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUSSKIES", "ラスキーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "南フロリダの島に住むダニー、アダム、ジェイソンの３人は祖国愛に燃え、“ラスキーズ（ロシア人）”を敵視する少年たちだ。ある日彼らは海辺で、遭難したロシア人ミーシャを発見。最初は警戒していた３人だが、ミーシャの人柄に惹かれ始め、やがて友情が芽ばえていく。３人は不法入国者となったミーシャを、キューバへ脱出させようとするが……。“敵”だと信じていた者を“友”へ変えていく少年たちの柔軟な感性を生き生きと描いた作品。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN ENEMY HANDS", "Uボート／最後の決断", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "伝染病と敵艦の攻撃という、内外からの危機に瀕したドイツ軍の潜水艦、Uボート内の極限状態を描いたパニック映画。反乱の危機をもはらんだ艦内の不穏な対立が、極限下のスリルを加速させる。捕虜と乗組員の、敵味方を超えての友情のエピソードも面白く、ドラマに厚みを与えている。ドイツ軍に撃沈され、Uボートに拘束された米軍捕虜たち。だが、伝染病が広まったことで、彼らは乗組員と手を組み生還のために命を懸ける。", "2005-02-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE WEEK", "文化生活一週間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "新婚のお祝いに“組立式住宅”をもらったキートン君。だが妻に横恋慕する男のイタズラで、でき上がったのはヘンテコな形の家。何しろ洗面台は外壁にくっついているし、窓は斜め。おまけに台風もやって来て、グルグル回る家から放り出される始末……。無声短編喜劇の最高作の一つで、爆笑は必至。「キートンのマイホーム」というタイトルで上映されたこともある。", "1920-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APEX", "超時空兵団 APEX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "2073年、軍の秘密プロジェクトが1973年に探査用ロボットを送り込んだ。しかし道に迷った一家族に遭遇したことによって、時間軸が乱れ、未来はまったく別のものとなってしまう。ロボットを追ってタイムスリップしたプロジェクトのリーダー、シンクレアが無事任務を終え再び未来に戻ると、自己進化と増殖を遂げた探査用ロボットが、殺人ロボットとなって人間を襲うという地獄の戦場と化していた……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUNIOR BONNER", "ジュニア・ボナー —華麗なる挑戦—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "アメリカン・ニューシネマを代表する“西部劇”。現代文明のなかで変質しつつあるロデオに、なお昔ながらのロマンを託す男の純粋な気持ちをうたい上げた佳作。久しぶりに故郷へ帰ってきたジュニア・ボナーは、昔通りのものは何もないことを知る。両親は離婚し、弟も財産を増やすことしか関心のない俗物になってしまっている。彼の最愛のロデオもいつしか見る人の目には時代遅れのものと映っているようだ。時代に取り残されていく人間や失われた価値観へのあくなき執着が心にしみる。S・ペキンパーの脂の乗りきった時期の作品だけにアクションの豪快さ、華麗さだけでなく、失われゆく西部への哀感、父親への絶ち難い情などそれぞれにメリハリの効いた物語がほどよくミックスされて、心地よい。S・マックィーンも好演。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("９／１０", "９／１０", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ある洋館を舞台に、９人の登場人物が繰り広げる会話によって物語が進行するミステリー。少しの話の食い違いから謎が生まれる斬新なストーリーテリングに引き込まれる。かつての高校野球部のメンバー９人が７年ぶりに再会、思い出話に花を咲かせるが記憶が微妙に食い違っていることが判明する。そこで彼らは７年前に埋めたタイムカプセルを掘り出すことに。ところが、９人それぞれが持っている鍵で開くはずのタイムカプセルに10個目の鍵穴が存在し……。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("荒川アンダーザブリッジ THE MOVIE", "荒川アンダーザブリッジ THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/56214/315x210.jpg?4714", 115, "2度にわたりTVアニメーション化もされた中村光の人気コミックを、林遣人と桐谷美玲の主演で実写化。絶対に他人に借りを作らないことを信条とする大財閥の御曹司リクは、川で溺れかけたところを自称“金星人”のホームレス少女ニノに助けられる。リクは命を救われた借りを返すため、ニノの要求に応えて個性的な住人たちに囲まれながら荒川河川敷で暮らし始めるが……。2011年夏からドラマが放送され、続けて12年春に映画が劇場公開される。", "2012-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市兇状旅", "座頭市兇状旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "上州・下仁田、市は投宿した旅篭の娘・のぶ子を助けて悪党どもをやっつける。前３作で、しがらみをすっかり取り払った座頭市に、この作品あたりからユーモラスなキャラクターが強調されてきた。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("総長の首", "総長の首", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "昭和10年代の浅草を背景に、暴力団同士の抗争から相手の組長を狙撃、逆に相手から報復を受ける格好になった若きチンピラたちの生と死を強烈なリアリズムで描いた作品。時代の底なる“飢え”の形が、作品中の随所に表れている。東映ヤクザ映画の佳作。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヘアスタイル", "ヘアスタイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "「下妻物語」の中島哲也監督が企画・プロデュースを務め、彼が実力を認める３人の新人が監督に挑戦。３つのストーリーの主人公は、まったく異なるヘアスタイルの女の子。それを一人の女優が演じるという新感覚のオムニバスだ。それぞれに強烈なキャラと、ポップな映像で楽しませてくれる。N.Y.生まれで料理が苦手なバーバラ。恋人リドリーのため、日本の家庭料理を習得しようと奮闘する。（「アフロアメリカン」）", "2005-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本一のヤクザ男", "日本一のヤクザ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "舞台は昭和初期の任侠の世界。日本一郎は一宿一飯の恩義から、新興ヤクザの根本組と土地の親分・前野組との抗争に手を貸す。しかし、調子のいい日本一郎は、礼金だけをせしめて姿をくらましてしまう……。“任侠ヤクザ”もののパロディー。沢田研二が歌手役でゲスト出演。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("西遊記第101回之月光寶盒／A　CHINESE　ODYSSEY", "チャイニーズ・オデッセイ 　PART1−月光の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "香港の人気コメディアン、チャウ・シンチー主演による、「西遊記」をベースにしたラブ・ストーリーの第１部。人間に転生した孫悟空の恋を描く。天竺への旅から500年後、山賊チー・チンポウの元に、美人の姉妹が迷い込む。実は姉妹は、三蔵を食べるために孫悟空を探していた妖怪姉妹。二人の企みを知った菩提老師が、チンポウの前に現れ、姉妹の正体とチンポウが実は孫悟空の生まれ変わりであることを教える。やがて姉妹もチンポウの正体を知り、そこへ三蔵の肉を狙う牛魔王も現れて……。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dirty Dancing: Havana Nights", "ダンシング・ハバナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ダンスを通じて恋に落ちていくアメリカ人少女とキューバ人青年の姿を描く青春ラブ・ストーリー。少女は自分自身を表現するため、青年は意地と誇りを賭けて出場したクライマックスのダンス大会シーンは、感動と興奮の名場面。ラテンのリズムと情熱的なダンスに青春のエネルギーが弾ける。1958年、キューバ。おとなしく真面目な18歳のアメリカ人少女ケイティは、キューバ人青年ハビエルの影響でダンスに魅了されていく。", "2005-07-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・スパイダースのゴーゴー・向う見ず作戦", "ザ・スパイダースのゴーゴー・向う見ず作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "素人のど自慢で優勝した美女・チノは、TVで“自分のところにあらゆる障害を越えて一直線に向かってきてくれる人がいたら、その人の愛を受け入れる”と発言。横浜でそれを聞いた７人組の男たちは早速東京へと一直線に行進開始。一方、チノの家の３軒隣にはボーイフレンドの健がいて……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍ぶ川", "忍ぶ川", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "料亭“忍ぶ川”で出会った哲郎と志乃。哲郎は料亭の看板娘である志乃に惹かれ、何度も足を運ぶ。二人はそれぞれの過去を語り合い、やがてお互いの将来についても語り始める……。純愛映画の佳作。第44回芥川賞に輝く三浦哲郎の同名小説を、社会派の熊井啓が映画化。異才・木村威夫による美術も秀逸。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOCTURNE", "殺人夜想曲〈ノクターン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "RKOピクチャーズの誇る会心のハードボイルド・ミステリー。ある夜、一人の男がノクターンを弾きながら死んだ。他殺か、自殺か……。死因で検死官と意見が対立した敏腕刑事が独自の捜査を開始した。やがて死んだ男の背景に美しき女性たちが妖しい姿を現わしはじめ、そして男たちの陰謀が浮き彫りにされていく。主演のG・ラフトは、名優ハンフリー・ボガートと人気を二分したハードボイルド・スターで、「暗黒街の顔役」「ギャング紳士録」の追われる者から今回は追う者に転身、眼光鋭いタフ・ガイ刑事を熱演する。監督のE・L・マリンは男くさい西部劇でおなじみのアクション監督。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEIDI", "アルプスの少女ハイジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "あまりにも有名な少女小説を原作にした同名映画版、初のカラー作品。主演の子役女優から元気がもらえる作品になっている。南アルプスの山荘で、老人と孫のハイジが一緒に住んでいたところへ、叔母さんからハイジに依頼が入り、ハイジは足が不自由な富豪の娘の友達としてフランクフルトへと降りることに。", "1965-01-01", "77,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("After Earth", "アフター・アース（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SANDPIPER", "いそしぎ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "カリフォルニアの美しい海辺の風景をバックに、奔放で自我の強い女流画家と牧師でもある私立校の校長の道ならぬ恋の苦悩を描くメロドラマ。物語に新鮮味はないが、カメラの美しさは絶品で印象深い。また主題歌の『シャドウ・オブ・ユア・スマイル』の美しいメロディーはアカデミー賞に輝き、大ヒットした。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAR BIRDS", "戦争の荒鷲 ウォー・バーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「トップガン」「メタル・ブルー」同様、F−16ジェット戦闘機が活躍するスカイ・アクション。中東の某国で起こったクーデターを鎮圧する若きトップガンが、仲間の死を乗り越えて任務を達成するまでを描いている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OPERATION THUNDERBOLT", "特攻サンダーボルト作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "アフリカ、ウガンダのエンテベ空港で、アラブ・ゲリラのハイジャックに人質にされたユダヤ人100数人をイスラエル軍が夜間急襲して救い出した1976年の事件の映画化。「ブラック・サンデー」同様、政治的理由で公開されずにいたが、完成10年後に初公開された。同じ題材を扱ったものには、「エンテベの勝利」（1976）「サンダーボルト救出作戦」（1977）があるが、前者は公開されたが不入りで一週間で上映打ち切り、後者は未公開のままである。監督は「スター・ウォーズ／帝国の逆襲」のI・カーシュナー。出演のJ・ウッズはこのあと、「ビデオドローム」「サルバドル」などで知られるようになった。もともとはTVムービー。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋愛小説", "永遠の片想い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「猟奇的な彼女」のチャ・テヒョンと若手女優二人が共演した感動作。かけがえのない時間を過ごして固い絆で結ばれながらも、離ればなれになった男女３人。彼らがお互いに伝えることができなかった切なる思いを、ドラマティックなストーリー展開で語り明かす。主人公の青年が働くカフェなどの魅力的な空間設計、詩情豊かな自然美も映画の情感を盛り上げる。ある日、青年ジファンの元に“逢いたい”とだけ書かれた差出人不明の手紙が届く。ふと、ジファンの脳裏によみがえる５年前の思い出。それは、二人の女の子、スインとギョンヒと過ごしたかけがえのない日々だった……。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BALTO", "バルト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "S・スピルバーグ製作総指揮のアニメーション映画。70年前、ジフテリアにかかった子供たちの命を救うために、薬を乗せて雪原を走り抜けた名犬の実話をアニメ化。1925年アラスカ州西部の町ノーム。狼との混血犬バルトは、犬ゾリレースに出場することを夢見ながら孤独な日々を送っていた。その冬、町ではジフテリアが蔓延し、治療に必要なワクチンを犬ゾリチームが運搬をすることになる。だが、彼らは猛吹雪の中で行方不明となり、そのことを知ったバルトは救出のため雪原へ向かい、先導犬として薬を運び届けるのだった。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH PLAINS DRIFTER", "荒野のストレンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「恐怖のメロディ」に続き、C・イーストウッドが監督・主演した第２作。マカロニ・ウエスタンのヒーローとして活躍してきたイーストウッドが撮った異色ウエスタンだ。高原の町ラーゴに一人の無宿者がやって来た。よそ者だといやがらせをする町の与太者を撃ち殺した彼は、道を歩く少女を納屋へ連れ込んで犯してしまう。あっ気にとられる町の住人。そこへ無法者３兄弟が刑務所を出てこの町へ向かっているという知らせが入る。町を守るため無宿者が用心棒として雇われるが……。本作のリメイクともいえる、後の「ペイルライダー」につながる秀作。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不説的、秘密", "言えない秘密", "", "", "http://image.eiga.k-img.com/images/movie/53704/315x210.jpg?2773", 102, "俳優としても活躍する台湾のトップ・アーティスト、ジェイ・チョウが初メガホンを取り、アジア全域で大ヒットを記録したラブストーリー。淡江音楽学校に転校して来た青年シャンルンは、ピアノ室で美しい曲を弾いていたシャオユーと出会う。瞬く間に恋に落ちる2人だったが、シャオユーにはシャンルンに言えない秘密があり……。ジェイ・チョウが監督のみならず主演・脚本・音楽も手掛ける。ヒロイン役に「藍色夏恋」のグイ・ルンメイ。", "2008-08-23", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cafe de Los Maestros", "アルゼンチンタンゴ　伝説のマエストロたち", "", "", "http://image.eiga.k-img.com/images/movie/55354/315x210.jpg?1460", 92, "「ブロークバック・マウンテン」「バベル」でアカデミー作曲賞を受賞した音楽家グスタボ・サンタオラージャがプロデュースするアルゼンチンの伝統音楽タンゴのドキュメンタリー。2006年、ブエノスアイレスの古いレコーディングスタジオに、名曲集アルバム「CAFE DE LOS MAESTROS」を収録するため、タンゴの黄金時代を築いたスターたちが結集した。激動の歴史を生き抜いてきた音楽家たちが、タンゴと祖国への熱い思いを語る。", "2010-06-26", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市海を渡る", "座頭市海を渡る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "座頭市はこれまで斬った人々の菩提を弔うため、四国の札所巡りを続けていたが、図らずも栄五郎という男を斬ってしまった。市は彼の妹・お吉を訪ね、彼女の土地を狙う暴力一家と闘う。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Abong: Small Home", "アボン　小さい家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "フィリピンの唱歌「小さい家」をモチーフに、自然と人間の共生や家族の絆をドキュメンタリータッチでつづった人間ドラマ。フィリピンの日系3世ラモットは、乗り合いジープの運転手として働きながら妻や3人の子どもたちと暮らしている。ある日、妻イザベルが外国へ出稼ぎに行くことになり、子どもたちは山奥の村に住む祖父母の元へ預けられることに。子どもたちは戸惑いながらも次第に大自然の中での生活に慣れていくが……。", "2007-04-21", "76,35", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIGHTER IN THE WIND", "風のファイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "極真空手の生みの親、大山倍達をモデルにした韓国のコミックに基づくスポ魂ドラマ。屈辱的な体験をバネに、自身を徹底的に鍛え上げる格闘家の意思をパワフルに描く。韓国のラッパー、ヤン・ドングンの熱演に魅入られること必至！ 1939年、朝鮮から日本に渡った武術家志望の青年ペダル。戦中・戦後と差別されてきた彼は、負け犬で終わらないためにも武術の腕を磨こうと単身山にこもり、つらい修行を重ねる。", "2006-04-08", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONSIGNOR", "バチカンの嵐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "カトリック総本山のバチカンで、頂上を目指しのし上がっていく男を壮大なスケールで描き、教会内部の腐敗というタブーに迫った問題作。バチカン教会が、暴露された暗黒街とのつながりというスキャンダルを隠そうと、上映ストップを訴え出たといういわくつきの作品。第二次世界大戦に従軍牧師として参加したジョン・フラハティは、戦場での仲間の死に怒り、やむなく銃を取って敵を撃ち殺す。人生観の変わった彼はマフィアと結託し利権をあさり、美しい尼僧を堕落させ、ついには最高権威とされるローマ法王の側近にまで出世していく。F・ペリー監督をはじめ、豪華スタッフで描いた壮大な人間ドラマ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COHEN ＆ TATE", "ジャッカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「ヒッチャー」の脚本家として知られるE・レッドが初監督したアクション・スリラー。マフィア間の殺人現場を目撃したために、両親を目の前で殺され誘拐されてしまう少年の恐怖を描いていく。狂気あふれる冷酷な殺し屋役で名優R・シャイダーが熱演。B・コンティの音楽がスリリングに盛り上げる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Father's Day", "ファーザーズ・デイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "弁護士のジャックと作家のデイルは、かつて同じ女性とつきあっていた。その彼女から1６歳の息子の存在を打ち明けられる。２人は家出したという息子を追って、互いに自分が父親だと主張しながら珍道中を繰り広げる。フランス映画「Les Comperes」のリメイク。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RHYTHM THIEF", "二人の短い物語・ニューヨークの片隅で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "サイモンはN.Y.で非合法にライブを録音したテープを密売し、ギリギリの生活を送っている青年。ある日サイモンのもとに幼なじみのマーティが現れ、彼の母親の死を告げる。M・ハリソン監督が生まれ育ったマンハッタンのロウアー・イーストサイドを舞台に労働階級の人々を鮮明に描写したエキサイティングなストリート・ムービー。1994年ニューオリンズ映画祭第１回監督作品賞、ボストン映画祭特別賞、ロングアイランド映画祭観客賞、1995年サンダンス映画祭監督賞、フロリダ映画祭グランプリなど多くの賞を獲得した。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("中國龍／CHINA DRAGON", "金城武 チャイナ・ドラゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "金城武主演のアクション・コメディ。イェンとロンの姉弟は、ハワイで開催される国際武術大会の中国代表に選ばれる。しかし同時に、中国から盗み出された国家機密文書を、秘密裏に取り戻すという使命も帯びていた。機密文書を握っているのは、巨悪漢ジャック。姉弟はハワイで知り合ったハーフの美男子に助けられながら、ジャックとの闘いに挑んでいく。", "1995-01-01", "6,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("潮騒", "潮騒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "伊勢湾口の歌島を舞台に、婿取りをするため父親に呼び戻された美しい少女と、漁師の少年の思春期の恋を描いた古典的ラブ・ストーリー。嵐の夜、ずぶ濡れになった百恵と友和が山小屋で互いに衣服を剥ぎ取り、裸で愛を確かめ合うシーンが話題になった。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WIMBLEDON", "ウィンブルドン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「ラブ・アクチュアリー」などの大ヒット作を世に送り出すワーキング・タイトル製作の恋愛映画。キルスティン・ダンスト、ポール・ベタニーという米英の人気俳優が、テニスの聖地ウィンブルドンで恋に落ちる男女を熱演。ジョン・マッケンローら往年の大物選手たちのゲスト出演にも注目。今季を最後に引退を決意したテニス選手ピーターと、女子の優勝候補リジーが急接近。しかし、彼らの恋をマスコミがかぎつけ……。", "2005-04-23", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PENNY SERENADE", "愛のアルバム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "回想形式で綴る夫婦のラブ・ロマンスに泣ける名作。後に「ジャイアンツ」ほかの大河メロドラマを撮るスティーヴンス監督のその曙光が見える作品。グラントのシリアス演技も堪能できる。養女を亡くし生き甲斐を失った夫妻が、離婚するその最後に、思い出のレコードをかけて聴く。そして、出会った頃、結婚、夫と東京への移住、流産したその後の夫婦関係などを回想する。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHADOW CHASER4", "ファントム・オブ・ダークネス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "世界各地の古代遺跡に残された不思議な人物像や円盤の様な物体、さらにはナスカの地上絵に示される様に、太古の昔の地球には多数の宇宙船が飛来していた。当時地球を訪れていた異星人たちは、アフリカのある部族と友好関係を深め、様々な交流を行っており、その部族に薬の製造を託していた。ところが、今から5000年前、宇宙船が大爆発を起こし、部族も異星人も絶滅してしまう。時は移って現代。遺跡を発掘していた考古学者が、遺跡の中から瓶を発見する。それは、5000年の眠りについていた悪夢をよみがえらせる前兆であった……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MASTER OF MENACE", "ダン・エイクロイドとジム・ベルーシのハーレーダビッドソン＆クレイジーライダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "刑務所から出てきたばかりのハーレーダビッドソン・クラブのリーダー、バディはさっそくハーレーで繰り出すが、仲間のジプシーが事故死する。バディはラスベガスで行われるジプシーの葬儀に仲間とともにラリーを決行しようとする。が、彼らを一網打尽にするチャンスを狙う警察が、バディたちを待ち伏せていた。バイク・アクションにコミカルなギャグを散りばめた、なんとも騒々しい映画。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新暗行御史〈しんあんぎょうおんし〉", "新暗行御史〈しんあんぎょうおんし〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ユン・インワンとヤン・ギョンイルの二人の韓国人による原作コミックを、日本の志村錠児監督が映画化した長編アニメーション。日本と韓国が初めて本格的にアニメでコラボレーションを果たした作品だ。今は滅んだ国、ジュシンで隠密要員・暗行御史として庶民を悪政から守る任務をしていたムンス。乱世が続く中、流浪の旅を続ていた彼は、悪質な領主の村へたどり着く。", "2004-12-04", "76,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ramona and Beezus", "ラモーナのおきて", "", "", "http://ecx.images-amazon.com/images/I/51yhEGOvcYL._SL210_.jpg", 104, "ビバリー・クリアリーによる人気児童文学「ゆかいなヘンリーくん」シリーズの一編「ビーザスといたずらラモーナ」を実写映画化したファミリーコメディ。ヘンリーの友だちのビーザスは妹のラモーナのいたずらに振り回されっぱなし。次々と事件を起こすラモーナにうんざりのビーザスは、妹を嫌いになってしまう……。監督は「愛しのアクアマリン」のエリザベス・アレン。出演はビーザスにセリーナ・ゴメス、ラモーナにジョーイ・キング。", "2010-07-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Quick", "クイック!!", "", "", "http://image.eiga.k-img.com/images/movie/57064/315x210.jpg?2830", 112, "「TSUNAMI ツナミ」のユン・ジェギュンがプロデュースを手がけたバイクアクション。スピード狂のバイク便ライダー、キスは、生放送の出演に遅刻しそうなアイドル歌手のアロムをスタジオまで“配達”することになる。しかし、アロムのかぶったヘルメットには何者かの手により爆弾が仕かけられており、2人は脅迫の電話に従い、10メートル以上離れることができないまま30分以内に指定の場所にある物を届けることになる。", "2011-10-08", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋の乙女川", "恋の乙女川", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "橋幸夫主演の歌謡映画で、相手役は松竹青春路線のアイドル・スター、尾崎奈々。呉服屋・東屋の若旦那、浩一は父の度重なる多角経営失敗や学生運動に血道をあげる弟の事で頭が痛い。浩一が資金繰りに苦しむ中、財閥の令嬢・純子が彼に言い寄ってくる。だが浩一の意中の人は義理の妹・八重だった……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STREET JUSTICE", "地獄の必殺人シャドー・ウルフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "死んだはずのエージェントが生還すると、妻はすでに再婚。男は“影の存在”として、悪徳政治家と戦う妻に力を貸してやる。名乗り出たいがそれができない男のつらさ、男の存在に気付いてもどうにもできない女の悲しさを描いた異色アクション。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東のエデン　総集編 Air Communication", "東のエデン　総集編 Air Communication", "", "", "http://image.eiga.k-img.com/images/movie/54814/315x210.jpg?4484", 0, "2009年にTV放映された同名アニメの総集編。TV版の監督・神山健治が続投し、オリジナル脚本でTVシリーズを再構築する。2010年11月22日の月曜日、日本各地で犠牲者が1人も出ないミサイルテロが発生する。この事件は「迂闊な月曜日」と呼ばれたが、人々は次第に危機意識を失っていった。それから3ヵ月後、携帯サイトを開発する大学のサークル「東のエデン」に所属する森美咲は、卒業旅行で訪れたアメリカで、全裸の男・滝沢朗と出会う。", "2009-09-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("たどんとちくわ", "たどんとちくわ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "市川準監督が前作「東京夜曲」までの叙情的な筆致から一転、中年男の激情を過激な映像で見せ切った異色のドラマ。椎名誠の小説をもとに、二人の男それぞれのストレスが暴発するまでを二部構成で見つめていく。“職業はたどん屋だ”という客の何げない嘘に逆上してピストルを突きつけてしまうタクシー運転手の「たどん」編。そして、ちくわ好きの貧乏作家の妄想が居酒屋で炸裂する「ちくわ」編。とりわけ後者は、カラフルな極彩色が飛び散る、何ともポップな惨殺劇が展開されており、真田広之の熱演も相まって市川ファンを驚かせた。とはいえ、二人の男が顔を合わせる最終盤においては、市川らしい情感あふれる場面もなぜか用意されており、ねじれた作品の印象を際立たせている。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAVEN", "ウェルカム・トゥ・ヘヴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "女優D・キートンが監督をしたハリウッド版「大霊界」。天国について街頭インタビューを敢行、その答えと古い映画のカットをコラージュしたシーンとで構成されている。スーパーバイザーとしてスティーヴン・スピルバーグが参加している。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Strange Days", "ストレンジ・デイズ／1999年12月31日", "", "", "http://ecx.images-amazon.com/images/I/41052XZ075L._SL210_.jpg", 145, "ジェームズ・キャメロンによる脚本を、「ハートブルー」でヒットを飛ばしたキャスリン・ビグロー監督が手掛けたSFアクション。1999年末のロサンゼルス。他人の体験を体感できるディスクを密売する元警官のレニーの元に、ある1枚のディスクが届く。それは、彼の元恋人フェイスの友人が惨殺される場面が記録されたものだった。フェイスにも危険が迫っていることを知った彼は、友人らの助けを借りて調査を開始するが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ocean's Thirteen", "オーシャンズ13", "", "", "http://image.eiga.k-img.com/images/movie/1924/315x210.jpg?4555", 122, "ジョージ・クルーニー、ブラッド・ピットら超豪華キャストの競演で毎回話題を呼んでいる人気シリーズの第3弾。犯罪ドリームチーム“オーシャンズ”のメンバーであるルーベンが、ホテル王バンクの裏切りによるショックで危篤状態となってしまう。復讐を誓ったオーシャンたちは、これまで敵同士だったベネディクトとも手を組み、カジノ付ホテルのオープンを控えたバンクに罠を仕掛ける。冷酷なホテル王バンクに扮するのは名優アル・パチーノ。", "2007-08-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪党", "悪党", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "谷崎潤一郎の戯曲『顔世』を新藤兼人が脚色し、監督した戦乱の世の物語。14世紀、南北朝の動乱の時代、田舎武士の妻で絶世の美女顔世に恋こがれる足利将軍の執事・高師直の愛と苦悩の日々をエネルギッシュに描く。京都市郊外の亀岡に寝殿造りのセットを組み、その近くにプレハブ住宅を建てて、スタッフ一同合宿して撮影に臨んだ。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LIVING END", "リビング・エンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "アメリカ、インディペンデント・フィルム界の新鋭、G・アラキ監督による、ロード・ムービー。ロサンゼルスの街で出会い、激しい恋に落ちたジョンとルーク。このゲイ・カップルの絶望的な逃避行を、乾いたユーモアを交じえて描いている。シカゴのインディー・レーベル“WAX TRAX！RECORDS”が、音楽で全面協力。ブレインデッド・サウンド・マシーン、クリス・アンド・コージー、コイル、サイキック等、先鋭的アーティストの曲も魅力。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SERIAL　LOVER", "シリアル・ラヴァー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "35歳の美人の独身女性が、偶然から次々と殺人を犯してしまう姿を軽妙に描くブラック・コメディ。ミステリー専門出版社の編集者クレールは、どうしても３人いる恋人を一人に絞れない。そこで35歳の誕生日に彼らを自宅に招き、最高の夫となるべき男を選ぶことにする。ところが、ディナーの最中に恐ろしい事故が発生してしまう。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOUCH OF EVIL", "黒い罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "アメリカとメキシコの国境の町で車が突如爆発し、車中のストリッパーと町の富豪が死んだ。アメリカの敏腕刑事クインランとメキシコの犯罪調査官バルガスは共同捜査を始めるが、二人は衝突ばかり。バルガスはクインランのでっち上げ捜査を見やぶるが、クインランはバルガスの妻を誘拐して殺人事件を擬装する。やがてバルガスはクインランの恐るべき正体を知る……。犯罪映画をフィルム・ノワール（黒い映画）と呼ぶことがあるが、この作品は画面の暗さから物語のどす黒さまですべてがまさにフィルム・ノワールそのもの。また冒頭の長い長い移動撮影は後の多くの映画監督に大きな影響を与えたことでも有名だ。完全版（108分）と短縮版（95分）がある。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Death Sentence", "狼の死刑宣告", "", "", "http://image.eiga.k-img.com/images/movie/34184/315x210.jpg?1537", 106, "「ソウ」シリーズの発案者であるジェームズ・ワンが、ブライアン・ガーフィールドの小説「狼よさらば」を映画化したバイオレンス・アクション。復讐の鬼と化す父親をケビン・ベーコンが熱演。家族と幸せな生活を送っていた平凡なビジネマンのヒュームは、ある日、目の前で息子のブレンダンを不良少年に惨殺されてしまう。心の傷も癒えぬまま裁判を迎えたヒュームだったが、納得のいく刑罰が与えられないことを知り、自らの手で息子の仇を打つこと決める。", "2009-10-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TERROR IN THE AISLES", "ザッツ・ショック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「シャイニング」「ゾンビ」など、戦後アメリカのホラー映画75本から集めた“名場面大全集”。ホスト役のプレザンスとアレンが、劇場での観客席で恐怖シーンを体験する、という構成がユニークだ。アヴォリアッツ国際ファンタスティック映画祭でも好評を博した、ホラー・ファン必見作。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOUS LE CIEL DE PARIS COULE LA SEINE", "巴里の空の下セーヌは流れる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "フランス映画の巨匠デュヴィヴィエが戦後初めて世に問いた作品で、封切り当時大変高く評価を受けたもの。パリを舞台に、何人かのパリジャンの生活描写を通してパリの生態を描き出す。エッフェル塔、セーヌ川などパリの風物詩が物語にうまく溶け込み、素晴らしい効果を生んでいる。主題歌のシャンソンとともに、後世に残る作品となった。", "1951-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUN3 TWILIGHT FILE V", "RUN3 TWILIGHT FILE V", "", "", "http://image.eiga.k-img.com/images/movie/54154/315x210.jpg?3894", 0, "オムニバスシリーズ「TWILIGHT FILE」第5弾の1作。平凡なサラリーマンの風間は、バッティングセンターで尾形と名乗る男に勝負を挑まれ、嫌々ながら勝負を受けるはめに。勝負がつき、尾形の忘れ物の紙袋に気付いた風間は尾形を追うが、尾形は謎の男2人組みに襲われており、風間はその場から逃げ出し交番へ向かうのだが……。「ラストゲーム／最後の早慶戦」「クローズZERO」の渡辺大が謎の組織から走って逃げ回る男を熱演。", "2008-11-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JANE EYRE", "ジェイン・エア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "S・ブロンテの名作の６度目の映画化。幼くして両親を亡くしたジェイン・エアは叔母に預けられるが、残酷な叔母はジェインを孤児のための慈善寄宿学校に送る。そこで彼女を待ち受けていたのは、苛酷な規律と虐待だった。やがてジェインは自立を求め、教師となって雇い主ロチェスター卿の館、ソーンフィールドへと旅立つ。魅力的だが、どことなく暗い影をひきずるロチェスターに惹かれていくジェイン。そして彼もジェインを愛するようになる。が、結婚式の席上で、驚くべき事実が明らかになる……。ジェインの重要な少女時代を、11歳でアカデミー助演女優賞を受賞したA・パキンが演じて話題となった。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("御用金", "御用金", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "五社英雄監督、仲代達矢、中村錦之助、丹波哲郎の三大スター共演による時代劇の大作。天保年間の越前鯖江藩を舞台に、佐渡の御用金をめぐって様々な思惑を持った男たちの激闘がダイナミックなタッチで描かれる。フジテレビの劇場用映画第１作で、日本初のパナビジョン作品。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("呪怨　黒い少女", "呪怨　黒い少女", "", "", "http://image.eiga.k-img.com/images/movie/54594/315x210.jpg?3602", 0, "看護師の裕子が担当する芙季絵という少女に、産まれてくることのできなかった少女の怨念による嚢腫が発見される。少女の怨念は芙季絵の体を蝕み続け、周囲の人間までをも巻き込んでいく。1999年にリリースされたビデオ版「呪怨」の誕生から10周年を記念した作品で、「呪怨／白い老女」と同時公開。シリーズの生みの親である清水崇監督が原案と監修を担当し、「カンフーシェフ」で芸能界復帰を果たした加護亜衣が主演を務める。", "2009-06-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BROTHERS KARAMAZOV", "カラマゾフの兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "ドストエフスキーの長編小説を、欧米のスタッフ＆キャストで映画化した文芸大作。カラマゾフ家の気性の激しい長男ドミトリに焦点を当て、その中から父子、そして女性たちとの愛欲と葛藤を浮き彫りにしていく。社会派R・ブルックス監督の、意欲的な人間ドラマである。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鮮血の記録", "鮮血の記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "野村孝監督・小林旭主演によるニュー・アクション。終戦直後、野尻稔は基地の物資を横流ししてあぶく銭を得るが、暴力組織の陰謀で米軍に逮捕される。数年後出獄した稔は、組織への報復を開始。ところが、組織の背後には大きな黒幕がいた……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("END OF INNOCENCE", "愛の妖精 アニー・ベル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "一人の少女が成長し、無垢な心を失っていく様を、さらりと描いて見せた一編。両親を亡くしたアニーは、後見人のマイケルに伴われ都会へと出て来た。マイケルとの性的な関係や女友達に誘われ出かけたセックス・パーティーなど、次第にアニーは大人へと成長し、やがてそんな中から、本当の愛とは何かをつかみ取っていく……。", "1975-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ネムリバ", "ネムリバ", "", "", "http://image.eiga.k-img.com/images/movie/55784/315x210.jpg?4740", 0, "アメリカから日本にやって来た風変わりな少女ミカは、ひょんなことから心地よい睡眠を求める人々に会話と添い寝を提供するサロン「ネムリバ」で働くことになる。自衛官、プロサッカー選手、老人などさまざまな悩みを抱える客との交流を通し、新たな人生を踏み出していく。埼玉県を舞台に、若手映像クリエイターを支援するプログラム「D-MAP2009」によって製作。第15回函館港イルミナシオン映画祭シナリオ大賞を受賞した新鋭・園田新がメガホンをとる。", "2010-10-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAHILLーUNITED STATES MARSHAL", "ビッグケーヒル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "無法の町に秩序をもたらすために、家族のことも忘れて懸命に仕事に明け暮れる腕利きの連邦保安官ケーヒル。だがある日、家に帰ってみると家庭を顧みない父に反発するかのように、二人の息子が銀行強盗の一味に加わろうとしていたのだった……。父と息子の愛情がさわやかに描かれた西部劇。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BODY MELT", "バイオ・スキャナーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "狂暴なミュータントに変身した一家、異常成長した胎児に内臓を食い尽くされた若妻、頭部が風船のように破裂した男。平和な住宅地ホームズビルで頻発している怪事件の捜査に当たった二人の刑事は、被害者が皆ある製薬会社のビタミン剤を飲んでいたことを知る。さらに突き止めたのは、カレラ博士が開発したという新薬の秘密。それは、人間の身体を内部から破壊し、人間以外の存在に変えてしまうというものだった。ホームズビルは人体実験場として利用されていたのだ。しかし、事態はもはや止めようもないほどの広がりを見せていた。特殊メイク、SFX映像による戦慄の人体破壊SFホラー。", "1993-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LET IT RIDE", "のるかそるか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "しがないタクシードライバーのジェイ。ところがある日、競馬関係者の密談を伝え聞き、半信半疑でその馬に賭けたところ、なんとそれが万馬券の大当たり。それからはもうけの連続で、彼にとって夢のような一日が始まるのだが……。一獲千金を夢見る人々の夢を叶えるアメリカン・ドリーム・コメディ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("探偵物語", "探偵物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「スキヤキ・ウエスタン　ジャンゴ」などの鬼才・三池崇史監督が、自身の原点ともいえるコメディ、バイオレンス、ホラーの要素を取りいれた異色ミステリー。私立探偵・風間雷太の隣の部屋に、同じ名前の男・高島雷太が引越してくる。ある晩、風間の元を訪れた女が肝臓をえぐり取られて殺害される事件が発生。事態はやがて連続殺人事件へと発展していく。捜査に乗りだしたふたりの雷太は、「悲劇の鬼才」といわれる男・青山幽樹の存在にたどりつく。", "2007-09-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Big Miracle", "だれもがクジラを愛してる。", "", "", "http://image.eiga.k-img.com/images/movie/56724/315x210.jpg?3405", 107, "1988年にアラスカで実際に起こった出来事をもとに、氷に閉じ込められたクジラを救出するため奮闘する人々の姿をドリュー・バリモア主演で描いたドラマ。88年10月、アラスカの地方テレビ局でレポーター兼ディレクターとして働くアダムは、氷海の小さな穴にとらわれた3頭のクジラを見つける。その様子をニュースとして放送すると瞬く間に全米に広まり、アダムの元恋人で環境保護団体職員のレイチェルも駆けつける。会社の宣伝になると踏んだ石油会社が資材を提供し、報道陣も押し寄せるなど、現場はさまざまな思惑と熱気に包まれていくが……。監督は「旅するジーンズと16歳の夏」「そんな彼なら捨てちゃえば？」のケン・クワピス。", "2012-07-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAGALL A LA RUSSIE，AUX ANES ET AUX AUTRES", "シャガール：ロシアとロバとその他のものに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "豊かな色彩と夢の世界のような作風で多くのファンを持つ画家、マルク・シャガールに迫るドキュメンタリー。２度の大戦や愛妻の死など波瀾万丈な人生ながら、彼が絵を描き続けたのはなぜか？　ユダヤ人であるがゆえ、政治的にも翻弄されたシャガールは、意外にもユーモラスで人懐っこい人物だった。デュシャンら画家仲間や、本人のインタビューを交え、知られざる素顔に迫る。未公開作品も収めた貴重なフィルムだ。", "2003-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PERSONA", "仮面 ペルソナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "舞台女優のエリザベートは名声と平穏な家庭を得て幸福な日々を送っていた。ところがある日、彼女は舞台上で言語障害を起こしてしまう。エリザベートは看護婦のアルマとともに海沿いの別荘でしばらく生活することに。アルマはエリザベートの言葉にならぬ言葉を理解し、エリザベートも健康なアルマに憧れを抱くようになる。やがて二人の関係は看護婦と患者を超えた深いものになっていく……。“一人は常に語り続け、一人は全くの沈黙を守る、瓜二つの女性二人”というテーマのなかに描いた作品で、ある極限的な状況の中、行き場を失った人たちがその存在の内奥をさらけ出していくという、ベルイマンの得意とする題材であるが、ここではヌーヴェル・ヴァーグの影響ともいえる“映画”への内省が色濃く出ている。冒頭と終結部に出てくる映写機が示すように、これはあくまで映画なのだと言い切ったうえで、その表現の可能性を探り出そうとする実験的野心作であった。", "1966-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATAL ATTRACTION", "危険な情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "1987年全米No.1ヒットのセクシー・サスペンス。弁護士ダンは、妻子の留守中に、仕事で知り合った魅力的な女性アレックスと一夜の情事を楽しんだ。彼にとってはほんの遊びのつもりだったが、アレックスは執拗な誘いを始め……。生々しいセックス・シーンをはじめ、G・クローズの妖しげで恐ろしいまでの演技など、A・ライン監督の演出が冴える問題作。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HINOKIO", "HINOKIO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "映画「FINAL FANTASY」をはじめ、VFX監督として活躍してきた気鋭の映像作家、秋山貴彦の初監督作品。不登校を続ける少年が代理ロボット“ヒノキオ”を通じ、社会と向き合うことを知り、さまざまな人々と友情を育んでいく。秋山監督が存分に手腕を発揮したVFX映像も見どころ。不登校児のサトルは遠隔操作できるロボットを代理登校させるように。友達のいない彼だったがロボットを通し、同級生のジュンと親しくなる。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING OF THE CITY", "キング・オブ・ザ・シティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ハリウッドでの成功を求めて故郷も恋人も捨てて都会に出てきたカル。しかしナイトクラブで働くうちに、彼は華やかな世界の裏側を見てしまい、夢は次第に色あせていく。麻薬や売春などの誘惑に負けじと生きる若者の姿をパワフルに描写。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAURA", "ローラ殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "G・ティアニーの美しさが堪能できるミステリー映画。ニューヨークで美貌のデザイナーが顔面を銃で撃ち砕かれて殺害された。容疑者として浮かんだ３人は、嘘ばかりついて若き敏腕刑事マークを困惑させる。ところが捜査が行き詰まったある晩、殺されたはずの娘がひょっこり帰宅する……。会話と回想を多用した室内劇で、特異な登場人物のディテールが秀逸。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JACKASS　THE　MOVIE", "ジャッカス・ザ・ムービー　〈日本特別版〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "MTVの人気TV番組から派生した、その名の通り“おバカ（＝ジャッカス）”全開の爆笑ドキュメンタリー。スタントに体を張る男たちのトンデモない挑戦が、次から次へと繰り出される。ワニに噛まれたり、サメと戯れたりと命懸けのものから、尻の穴にミニカーを突っ込むわ、尿をかけた氷を食べるわなどバカバカしいものまでネタは様々。下ネタに加えて、ゴルフ場でショット時に怪音を発するなどのイタズラもある。さらに一行は、日本でも大騒ぎを繰り広げる。「アダプテーション」のスパイク・ジョーンズ監督の悪ノリ的な悪戯にも注目！", "2004-12-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SLIME CITY", "ジャンク・シティー 屍肉の館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "アメリカの新進特殊メイクアップ・アーティストによる、グロテスクでリアルなSFXシーンが話題のホラー・ムービー。人間をドロドロした溶解殺人鬼に変える調合薬が引き起こす恐怖を描き、ニューヨークでは、レイトショー・ロングランを記録した。スプラッタ・ファン必見作。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜の河", "夜の河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "山本富士子をスターダムにのし上げた記念碑的な作品。吉村公三郎監督にとっては初のカラー作品となり、宮川一夫カメラマンとともに驚嘆すべき色彩を作り上げた。京都堀割の京染屋の娘・キワは結婚もせずに家業に打ち込んできたが、病弱の妻をかかえた阪大助教授・竹村に惹かれて恋に落ちてしまう。妻の死後、キワは竹村に求婚されるが……。古いモラルと新しい時代の奔放さを共存させている女の役を山本富士子が好演。上原謙がショウジョウバエの研究家というのが面白い。吉村監督は色覚異常だったのでどんな色彩映画になるのかと注目されたが、心配はすべて杞憂に終わり、実に美しいカラー映画になった。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Promise　無極", "PROMISE", "", "", "http://image.eiga.k-img.com/images/movie/1604/315x210.jpg?3981", 124, "「さらば、わが愛／覇王別姫」のチェン・カイコー監督がアジアの名優たちの共演で描くファンタジー。セシリア・チャン演じる、貧しい孤児から王の妃となった美女。真田広之演じる負けを知らない大将軍。チャン・ドンゴン扮する過去の記憶を失った奴隷。彼ら3人が出会ったとき、それぞれに約束された運命は狂い始める。天下を狙う公爵役で香港の人気スター、ニコラス・ツェー、黒衣の刺客役で中国俳優リウ・イエが共演。", "2006-02-11", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("写楽", "写楽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "江戸寛政期、わずか10ヵ月の間に140数点の浮世絵を残し、突然姿を消した東洲斎写楽。彼にまつわる謎を通じて、当時の歌舞伎界、浮世絵界、吉原遊廓の実像に迫り、その時代を支えた人々の力強い生き方を描く。寛政６年５月、突然、東洲斎写楽という名の絵師の役者絵が売り出され、江戸の町の話題をさらう。写楽の絵に最も魅せられたのは、それまでのスター絵師・歌麿だった。写楽の作品は新人絵師にもかかわらず豪華な大判刷りであり、有名役者だけではなく無名役者も多く描いていたなど、謎の部分が多い。おまけに写楽は10ヵ月後には忽然と姿を消してしまうのだった。人間国宝の中村富十郎をはじめ、中村芝雀、市川團蔵ら多くの歌舞伎役者が出演。歌舞伎シーンには重要文化財、四国・琴平の金丸座を借り切って撮影しているほか、デジタル合成技術で再現した、江戸の町の絶景や日本橋、吉原界隈の雑踏など見るべきものが多い。写楽研究家でもあるフランキー堺の企画。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHILD’S PLAY", "チャイルド・プレイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "母親から誕生日のプレゼントとして、言葉を話す“グッド・ガイ”人形をもらった９歳の少年アンディの周辺で、奇怪な殺人事件が次々と発生。やがて人形にまつわる殺人鬼の呪いやブードゥー教の存在が明らかにされる。オカルト・タッチのスリラーもの。人形はトリックを使用せず、中に俳優が入って演技した。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEPTEMBER", "セプテンバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "W・アレンが、第16作にあたるこの映画で、またひと味違うタッチの６人の男女が繰り広げる心理ドラマを映像化している。舞台はバーモントにあるサマーハウス。往年の大女優であるダイアンと、ニューヨークに憧れている娘レーンの確執、魅力的な作家ピーターをめぐるレーンと親友ステファニーの三角関係といったドラマを中心にそれぞれの愛と、監督のニューヨークに対する思いがサラリと描かれている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Odette Toulemond", "地上5センチの恋心", "", "", "http://image.eiga.k-img.com/images/movie/53414/315x210.jpg?2363", 100, "ありふれた日常に喜びを見出すことのできる主婦オデットの楽しみは、お気に入りの作家バルタザールのロマンス小説を読むこと。一方バルタザールは、評論家に作品を酷評された挙句に妻まで寝取られ不幸のどん底にいた。そんな彼の元にオデットからのファンレターが届く。「イブラヒムおじさんとコーランの花たち」の脚本家エリック＝エマニュエル・シュミットが、作家としての実体験を交えて書きあげた脚本を基に初監督を務めた大人のラブコメディ。", "2008-03-01", "2,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・婚前旅行", "喜劇・婚前旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "今回はいつもと少し違い、主人公の田村家が、伴淳・フランキー・森田の３代にわたる鉄道マンというのが面白い。フランキーは男ヤモメで、息子の母校の女教師に惚れるという役回り。当時、人気絶頂のピンキーとキラーズも顔を出す。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("May'n THE MOVIE Phonic Nation 3D", "May'n THE MOVIE Phonic Nation 3D", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "人気アニメ「マクロスF」に登場する歌姫シェリル・ノームの歌パートを担当し、ブレイクした歌手May'n（メイン）が、2010年にZepp東京で行ったライブ「2010 SUMMER TOUR Phonic Nation」の様子を3Dで劇場公開。同ステージでのみ披露されたMay'nの自作曲「Phonic Nation」に込められた思いなど、知られざる舞台裏もつづられている。", "2011-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四畳半 色の濡衣", "四畳半 色の濡衣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "野坂昭如の同名小説をピンク映画界の鬼才・向井寛が、人気上昇中だった美保純を主役に起用して映画化した一般映画。昭和７年、遊廓・旭楼を舞台に、朝子を中心に娼婦たちの人間模様が描かれる。横山博人監督の「卍」の併映用に急きょ製作されたが、本編を食う仕上がりとなった。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE JIGSAW MURDERS", "ジグソー殺人事件 へび肌の女を追え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "バラバラ事件を追う二人の刑事を描いたポリス・ストーリー。ハリウッドの製靴工場で若い女の足が見つかり、初老で飲んだくれのジョーイと若さあふれるエリオットの刑事コンビが担当することになった。手がかりは足に彫られた蛇の入れ墨だけ。聞き込みを続けるうちにジョーイと彼の娘に関する意外な事実がわかって……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女ひとり大地を行く", "女ひとり大地を行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "当時の独立プロが製作した家族ドラマ。炭鉱夫の生活ぶりや労働争議など、第二次世界大戦直後の厳しい現実を映し出す。“大地”のようなたくましい女性に扮した山田五十鈴が快演。昭和7年の秋田。百姓一家は貧農で、夫は北海道の炭鉱へ出稼ぎに出たが、重労働で逃亡。妻は子供連れで炭鉱へ。『独立プロ名画特選DVD-BOX(3)　女性編　女性の自立と哀しみ』に収録。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RUSSIA HOUSE", "ロシア・ハウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "世界の防衛体制を揺るがす驚異的な内容が記された謎のソビエト人作家の原稿が、英国情報部に舞い込む。ことの真偽を確かめるため、英国情報部は直接原稿を運んだロシア人の美人編集者と、その出版を依頼されたイギリス人経営者との接触を図るが……。スパイ小説の傑作を数多く持つJ・ル・カレが、ゴルバチョフ体制下のソ連に徹底取材、“いまのソ連”が克明に描き出されたスパイ・サスペンス。ロマンティックなラブ・ストーリーとしても見ごたえがある。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("氾濫", "氾濫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ある発明によって一技術者から会社の重役に成り上がった男の家庭が体験する、セックスと社会的野心が渦巻く愛と欲望の日々を描く。増村と脚色の白坂依志夫は、この主題を日本の社会的機構と切り離せぬものとして捉え、主人公たちの欲の強さと脆さを痛烈に風刺している。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OGGETTO SESSUALE", "青い体験～誘われて～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "年上の女性と純粋な若者の恋を描いた作品。ギリシャ・イスキア島。風光明媚なこの島に、娼婦のラウラがやって来た。マルチェロという富豪に、“息子を男にしてくれ”と頼まれたのだが、ラウラの本当の目的は、かつてマルチェロから受けた屈辱を、息子セルジオを使って晴らすことだった。ところが彼女は、あまりにも純粋なセルジオをやがて愛し始めるようになって……。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乱", "乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 162, "シェイクスピアの『リア王』を戦国時代の毛利三兄弟物語に翻案した、黒澤明監督の豪華絢爛たる戦国絵巻。過酷な戦国時代を生き抜いた老いた猛将、一文字秀虎。家督を三人の息子に譲る決心をするが、秀虎を待っていたのは息子たちの反逆と骨肉の争いだった。やがて、秀虎はショックのあまり発狂し、三人の息子は次々と死んでいく……。人間の愚かさを静かに描いた地獄絵は、まさに美しく荘麗なる悲劇。", "1985-06-01", "76,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PHOENIX", "フェニックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "個性派R・リオッタ製作総指揮、主演による味のあるフィルム・ノワール。アリゾナ州の敏腕刑事ハリーは、ギャンブル好きが高じて多額の借金を抱え込んでしまう。高利貸しから借金と引き換えに殺人を迫られた彼は、追い詰められ強盗を計画。同僚を巻き込んで計画を実行するが、仲間の裏切りから事態は急変していく。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A NIGHT IN HEAVEN", "ナイト・イン・ヘブン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ブライアン・アダムスのヒット曲『ヘブン』にのせて、青年と人妻の危険な恋が綴られる。昼は普通の大学生、夜は女性専用のクラブで男性ストリッパーとして人気を誇る青年がある日、観客の中に、自分の教師である女性を見つける。その時から、二人の危険な恋が始まった。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPROUT", "スプラウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "サーフィンの多様なスタイルを追いかけて世界をめぐり、4年をかけて制作したドキュメンタリー。名だたるサーファーたちの妙技はもちろん、ボードを使わないボディ・サーフィンなどのユニークなスタイルを覗き見ることもできる。スローモーションの映像を彩るファンキーな音楽も魅力的。ジョエル・チューダーを筆頭に名だたるサーファーが登場し、華麗なテクニックを披露。彼らの自由な思想やライフスタイルにも迫る。", "2005-07-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　NEWTON　BOYS", "ニュートン・ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "犯罪史上最も成功を収めた銀行強盗団の波乱の半生を、M・マコノヒーらスターの共演で描く痛快作。1919年、テキサスの田舎町に育った４人兄弟が銀行強盗を計画。頭がキレる次男ウィリスを中心に、兄弟は次々と犯行を重ね、ついに郵便列車から300万ドルを奪うという一世一代の大勝負に出るが……。庶民からも金を奪い銀行をも襲うケチな泥棒と称し、一人も人を殺さず荒稼ぎした彼らの犯行の手口を鮮やかに描出。それぞれに個性的な兄弟の固い絆、悪人らしからぬ素朴な素顔がユーモアを交えて描かれ、さわやかな印象を残す。ラストにはインタビューを受ける晩年の本人たちが登場。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雑家小子／KNOCK ABOUT", "モンキー・フィスト 猿拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "Y・ピョウの映画初主演作品。ディーとダムは愉快な駆け出しサギ師。二人は紳士風のやたらと強い男に弟子入りするが、これが実は大悪党。それを知って逃げ出すが、ダムは殺される。逃げ出したディーは変てこな浪人から猿拳の極意を授けられる。", "1979-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海ほおずき", "海ほおずき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "林海象がプロデュースした“アジアンビート”シリーズの集大成とも言うべき作品であり、90％が台湾で撮影されている。かつては名探偵と言われたものの、今では薬物中毒となり、人生に何の夢も持てなくなった灰田は、川の流れに身を任せ、これで楽になれると観念した時に、瀕死の状態で浮かんでいる男とぶつかる。灰田に助けられた男は“なぜ放っといてくれなかったのだ”と言い残して姿を消す。その後、灰田の身元引受人でもある女検事の名月から仕事を依頼される。それは、５年前に台湾で行方不明になった真理子を探し出すというものだった。台湾に飛んだ灰田の前に現れたのは、もう一人のまりこ、謎の女、そして灰田が川で助けた男だった……。挿入曲をシンガポールのスター、ディック・リーが、エンディング曲を中島みゆきが歌っている。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILDE", "オスカー・ワイルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "『ドリアン・グレイの肖像』『サロメ』などで知られるイギリスの文豪オスカー・ワイルドの波乱に満ちた半生を描く伝記ドラマ。19世紀後半、円満な結婚生活を送るワイルドが、内に秘めていた同性愛の感情に目覚める。やがてオックスフォード大学に通う青年ボジーの完璧な美しさに心を奪われた彼は、ロンドンにおける初めての男色裁判に問われ、社会の好奇の目を集めることに……。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使のはらわた 名美", "天使のはらわた 名美", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "“強姦された女たち”を題材に、女性週刊誌にルポを書いている女・名美。彼女は取材先で、同じネタを追っているエロ雑誌の記者・村木と出会う。村木には強姦魔に妻を奪われた過去があった……。“強姦”をめぐる様々な哀しい人間模様を描き、胸に迫る秀作。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若君と次男坊", "若君と次男坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "酒色道修行のため江戸入りした南部盛岡二十万石の若君・葵之介。金に目のない指導役の若者・圭次郎と意気統合、二人してお遊び修行に出かける。ふとしたことから圭次郎は、藩の用材を横流ししていた材木商の不正を知るが、逆に彼らに金で抱き込まれ、葵之介に罠を仕掛ける。松方弘樹の放蕩青年ぶりが見もの。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FROM DUSK TILL DAWN", "フロム・ダスク・ティル・ドーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ハリウッドの二大風雲児、Q・タランティーノとR・ロドリゲスがコンビを組んだホラー・アクション。凶悪な強盗犯兄弟の逃亡劇と彼らを待ち受ける事件をスタイリッシュに描く。凶悪なセスとリチャードの兄弟は、銀行を襲撃してメキシコへと逃亡。モーテルに立ち寄った二人は、トレーラーで旅する元牧師一家を人質に取り、無事国境を越え、仲間の待つ酒場へと向かう。だが、酒場が吸血鬼の巣窟だったことから、彼らは夜明けまで壮絶な戦いを繰り広げる。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひゃくはち", "ひゃくはち", "", "", "http://image.eiga.k-img.com/images/movie/53644/315x210.jpg?3005", 126, "野球の名門として知られる京浜高校の補欠部員・雅人とノブは、甲子園のグラウンドを目指して毎日過酷な練習に励んでいた。しかし上級生が引退しても、彼らに与えられるのは雑用ばかり。そんな中、有望株の新入生が入部したことにより、2人は高校最後の甲子園のベンチを巡って争うことになり……。29歳の新鋭・森義隆監督が、補欠部員たちの奮闘を爽やかに描いた青春ドラマ。雅人役に映画初主演の斎藤嘉樹、ノブ役に「恋空」の中村蒼。", "2008-08-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Big One", "ザ・ビッグ・ワン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「華氏911」「ボウリング・フォー・コロンバイン」など、アメリカの社会的問題に踏み込んだドキュメンタリー作品で知られるマイケル・ムーア監督。自身の著書を宣伝するツアー敢行したムーアは、書店でのスピーチを行う傍ら、現地のアメリカ企業に突撃、悪ふざけを交えながらもリストラや重労働などの問題に厳しく疑問を投げかけていく。", "", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The 6th Day", "シックス・デイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "人間のクローンを造ることを禁じた“6d法”が施行された2010年。しかし何者かによってその法律が破られ、パイロットのアダムは自分に瓜二つの男を目撃する。警察に追われる身となった彼は、捨て身の覚悟で巨大な陰謀に立ち向かっていく。バイオ・テクノロジーの進歩がもたらす恐怖を描いたSFアクション。A・シュワルツェネッガーが一人二役に挑戦し、肉弾アクションだけではない巧みな演技を披露。近未来社会をヴィジュアル化したシャープなハイテク映像も見どころだ。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺の背中に陽が当る", "俺の背中に陽が当る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ヤクザの世界から足を洗った滋が、組長殺しの罪を着せられて殺された。弟の健三は、兄の汚名をそそぐべく立ち上がる……。渋谷健の原作を佐原靖郎と中平康が脚色、中平康が監督した青春ドラマ。浜田＝吉永の名コンビが、出口なき状況に追いつめられた恋人たちを演じる。アクション・シーンも見どころ。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STILL　BREATHING", "いつかあなたに逢う夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "B・フレイザーが1997年度シアトル国際映画祭で主演男優賞を受賞したラブ・ファンタジー。テキサス生まれの青年フレッチャーの父や祖父は、夢の中で出会った女性と結婚している。彼もある夜、夢の中に出てきた女性ロザリンが運命の人だと確信。サンタモニカでロザリンを見つけたフレッチャーは、一途な愛を捧げるが、彼女の正体は金持ちを狙う詐欺師だった……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こぼれる月", "こぼれる月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "強迫神経症、パニック障害、PTSDといった現代特有の病を抱えた若者たちの恋愛模様を、新人、坂牧良太監督が自身の体験も交えて描き出す。主演は「アナザへヴン」の岡元夕紀子。強迫神経症で常に手洗いをやめられない高は、通院先で知り合った美容師のあかねと同棲中。だが、ともに病を抱える者の間には亀裂が生じていた。一方、パニック障害で引きこもりの千鶴は、外出中に発作に襲われ、年下の少年ゆたかに助けられる。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les enfants du paradis", "天井棧敷の人々", "", "", "http://ecx.images-amazon.com/images/I/51DcfU2ZwDL._SL210_.jpg", 190, "第2次世界大戦の最中、ナチスドイツ占領下のフランスで3年6カ月の歳月をかけて製作されたジャック・プレベール脚本、マルセル・カルネ監督のコンビによる群像絵巻。19世紀半ばのパリ・犯罪大通りを舞台に、女芸人ガランスをめぐって、役者、犯罪詩人、伯爵など様々な職の男たちが織りなすカラフルな人間模様が、第1部「犯罪大通り」、第2部「白い男」の2部構成で描かれる。出演はジャン・ルイ、アルレッティ、マリア・カザレス。", "1952-12-31", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Evil Dead", "死霊のはらわた", "", "", "http://ecx.images-amazon.com/images/I/31TYGQWDKVL._SL210_.jpg", 86, "　休暇で森にやってきた5人の男女は廃屋の地下で“死者の書”を発見。やがてテープレコーダーに録音されていた呪文によって、死霊が復活してしまう。死霊に乗り移られた者は他の者を襲撃。阿鼻叫喚の中、アッシュは必死の抵抗を試みるが……。ミシガン州立大学時代に撮った映画をリメイクしたサム・ライミの商業映画初監督作。日本でも大ヒットし、スプラッター・ホラー映画ブームの火付け役となった。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DESTRUCTORS", "マルセイユ特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "アメリカの麻薬取締官がフランスのマルセイユで次々と殺される。フランス財界の大者が黒幕とみられたが、なかなかシッポをださない。アメリカ麻薬取締部長ベンチュラは、フランス警察のブリアック警部と組み、捜査にあたるのだが……。麻薬犯罪を追う男たちの死闘を描いた刑事アクション。", "1974-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女必殺拳・危機一発", "女必殺拳・危機一発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "香港富豪の娘が誘拐され、父親から捜索を頼まれた李紅竜は一路日本へ。密輸組織が若い娘の体にダイヤを埋め込み、密輸していたのだった。富豪の娘は惨殺され、怒りに燃える李紅竜の鉄拳が悪をなぎ倒す。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BUTTERFLY EFFECT", "バタフライ・エフェクト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "時間をさかのぼることで過去を変える力を持った青年が、初恋の女性を救うためにとんでもない運命をたどっていくサスペンスに満ちた感動スリラー。オリジナルなアイデアと洗練された演出で、観客を複雑なストーリーにぐいぐい引き込んでいく新人監督、ブレス＆グラバーの手腕に脱帽。過去のある地点にさかのぼる能力を持つエヴァンは、自分の行動が初恋の女性を不幸にしたことを知って、過去を変えようとするが……。", "2005-05-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Your Friends & Neighbors", "僕らのセックス、隣の愛人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あやめ笠・喧嘩街道", "あやめ笠・喧嘩街道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "日本一の大親分を夢見て、当てのない旅を続ける侠客・あやめの雁太郎は、ある日倒れている娘を助ける。娘は父の仇を討つため犯人を探していたのだが、その仇、秋山隼人とは雁太郎が昔、恩を受けた友人だった……。ユーモアを交えながら軽妙なテンポで展開する、股旅人情もの。監督は名匠・加藤泰。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CABIRIA", "カビリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 180, "イタリアの文豪G・ダンヌンツィオがオリジナル・シナリオを書いた、スタリア史劇映画の傑作。エトナ山の噴火を逃れて小船に乗り込んだ少女カビリアがたどる数奇な運命を、波乱万丈の物語のなかに綴る。モロクの神の犠牲にされるところを危機一髪で救い出された少女カビリア。しかし、成人して再び犠牲になりそうになり……。巧みなストーリーが、豪華なセット、当時としては最高の撮影技術（接写や移動撮影）を駆使して描き出される。D・W・グリフィスの名作「イントレランス」にも影響を与えたともいわれる、サイレント映画の頂点を極めた一編。", "1914-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEARTBREAK RIDGE", "ハートブレイク・リッジ　勝利の戦場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "生涯を海兵隊に捧げてきたタフで呑んだくれの軍曹ハイウェイが、小隊の指揮のために帰ってきた。ヤワな連中に本当の海軍魂を叩き込むべく猛訓練が始まる。イーストウッドにとってはカーメル市長就任後第１作。グラナダ事件の再現シーンが見もの。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAILWAYS 49歳で電車の運転士になった男の物語", "RAILWAYS 49歳で電車の運転士になった男の物語", "", "", "http://image.eiga.k-img.com/images/movie/54874/315x210.jpg?2578", 130, "50歳を目前に電車の運転士になる決意をした男と、家族の再生を描いたドラマ。主人公に中井貴一、妻役に高島礼子、娘役に本仮屋ユイカ。一流企業に勤める49歳の肇は、ろくに家庭も顧みず、忙しく仕事に追われる日々を送っていた。そんなある日、故郷の島根で一人暮らしをしている母が倒れたという知らせを受ける。それをきっかけに仕事一筋の人生に疑問を抱き始めた肇は、子供の頃に憧れていた一畑電車、通称“バタデン”の運転士になることを決意する。", "2010-05-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本暴力団・組長と刺客", "日本暴力団・組長と刺客", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "京都博徒の伝統を誇る平安会の花会の席上で、喜多見組組長が大阪の新興暴力団・浪華会の組員に殺される。喜多見組の代貸・堀田は組長を襲名し、岩上会長率いる新興ヤクザ浪華会の縄張り荒しを阻止せんと立ち上がる。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あぶない刑事フォーエヴァー", "あぶない刑事フォーエヴァー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "タカ＆ユージの無鉄砲な刑事が大暴れするアクション・シリーズ５回目の映画版。前編をTVの２時間ドラマとして放映、この映画はその後編というメディアミックスの形で公開された。国際指名手配のテロリスト・城島から大量の武器を押収したユージ。それを取り返そうとする城島VSタカ＆ユージの戦いを描いたのがTV版。映画は、その城島を操っていたテロ・グループのリーダー、伊達が日本に帰国。横浜港内のタンカーに爆弾を仕掛け、 100億円を要求する伊達たちにタカ＆ユージが挑む姿が、派手なカー・チェイスなどを織り込みながら展開する。タカとユージが死んだと思えるようなラストが、ファンとしては気になるところ。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Andres no quiere dormir la siesta", "瞳は静かに", "", "", "http://image.eiga.k-img.com/images/movie/57224/315x210.jpg?4190", 108, "軍事政権下のアルゼンチンで生活するある家族の姿を、子どもの視点から描いたヒューマンドラマ。1977年、アルゼンチン北東部の州都サンタ・フェに暮らす少年アンドレスは交通事故で母を亡くし、祖母オルガの家に移り住む。貧しくとも愛情に溢れた母との生活とは異なり、自分のルールを押し付けてくるオルガの家で次第に居場所を失っていくアンドレスは、安らぎを求めて母と暮らしていた家に忍びこむが、そこで母が隠していた反体制派のビラを見つける。陽気だったアンドレスは次第に無口になり、大人たちや家族を静かに見つめながら真実を見極めていく。", "2011-12-10", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やわらかい肌", "やわらかい肌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ピンク四天王の一人、佐藤寿保監督が、ある家族の崩壊劇をブラック・ユーモアたっぷりに綴ったシニカル・コメディ。欲求不満の人妻、気弱なサラリーマン、過食症の娘、同性愛に目覚めた息子らが、ある偽装誘拐事件をきっかけに、現実と妄想の狭間で家族の絆を発見するというグロテスクな味わいの１本だ。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("変奏曲", "変奏曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "デビュー作「狂った果実」で仏ヌーヴェル・ヴァーグの監督に強い影響を与えた中平康監督の晩年の作品。ファッションモデルの麻生れい子を主役に起用、異国で再会したかつての恋人同士のつかの間の官能を描く。ATG初のオール海外ロケによるパリの街並みが魅力。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wiz/Out", "Wiz/Out", "", "", "http://image.eiga.k-img.com/images/movie/34234/315x210.jpg?3786", 120, "誰もいない東京に迷い込んでしまった男女の運命を描いた異色ドラマ。携帯電話も繋がらない山奥のキャンプ場にやって来た大学サークルの若者たち。翌日、ふもとの町を訪れた彼らは、そこがゴーストタウンになっているのを発見する。乗り捨てられていた車で東京に戻った彼らを待ち受けていたのは、人間が姿を消した渋谷の街だった。訳もわからず彷徨いはじめた彼らだったが、異常事態の中で次第に関係が悪化していき……。", "2007-10-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANCE　WITH　ME", "ダンス・ウィズ・ミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "歌手のV・ウィリアムズとプエルトリコ出身のダンサー、チャヤンが、吹き替えなしのホットなダンス・シーンを披露する青春映画。ダンスの世界選手権を目指して日々レッスンに励むインストラクター、ルビー。そんな彼女のスタジオに、キューバ生まれの雑用係ラファエルがやってきた。サルサ系のクラブを訪れた二人は、文化の違いを乗り越えて恋に落ちていく。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POSSE", "黒豹のバラード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "19世紀末、白人隊長の仕打ちに耐えかねて軍を脱走した黒人たちがいた。アウトローとなった彼らは故郷の村を目指すが、その村も白人に牛耳られていた。彼らは村の黒人を救うために立ち上がる……。ブラック・シネマの気鋭M・V・ピーブルズが監督、主演を務め、黒人の視点から描く異色ウエスタン。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE HUNTER，BLACK HEART", "ホワイトハンター ブラックハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "C・イーストウッド14本目の監督作で、名匠ジョン・ヒューストンの物語である。ハンフリー・ボガートとキャサリン・ヘプバーン共演の「アフリカの女王」を撮影するため、アフリカに赴いたヒューストン（映画の中ではジョン・ウィルソン）が、象を撃つことに取り憑かれていくさまが描かれる。ウィルソン役は、もちろんイーストウッド自身。しゃべり方から葉巻の吸い方まで、ヒューストンを真似てはいるが、どこから見てもイーストウッド本人になってしまうところはご愛敬。これはヒューストンを素材にした、映画監督がハンターという道を通り抜け、監督としてアクションするに至るまでの物語なのである。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("麦秋", "麦秋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "小津安二郎監督の力量が最も充実していた時期に作られた作品であり、「晩春」や「東京物語」など、小津の代表作と並べても決してひけをとらない素晴らしい出来で、小津の最高作とするファンも多い。戦前の小津のサイレント作品で数本の脚本を書いた野田高梧は、「晩春」以後小津との名コンビを続けていくのだが、そのセリフの間(ま)の絶妙さは本作で最大に発揮される。北鎌倉に住む間宮家の気がかりは、28歳になる独身娘・紀子の結婚だ。両親や兄夫婦は紀子の縁談についていろいろと心配するが、本人はあまり気のりでない様子。やがて兄妹のような気軽さでつきあっていた子持ちの男と結婚しようと紀子は決心する……。原節子、笠智衆をはじめ、相変わらず充実したキャストの存在感は大きいが、なかでも紀子の結婚相手の母を演じた杉村春子のコミカルさとシリアスさを使い分けた演技は抜群。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WINGS OF EAGLES", "荒鷲の翼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "不慮の事故で下半身不随になった米海軍航空隊の伝説的人物フランク・ウィードの半生を描いたドラマ。妻の献身的な助力で再起していくフランクの姿が、アイルランド気質のユーモアで綴られる。ウェインとオハラが相変わらず息の合った名コンビぶりを披露。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女王蜂", "女王蜂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "ヒロインに故・佐田啓二の遺児・中井貴恵を起用、彼女の婚約者の座を狙う男たちが、次々に殺されていくという不可解な連続殺人事件を軸に、複雑な人間模様が描かれる。今回はいつもの、血と因襲で彩られたおどろおどろしい横溝世界とは趣を異にして、演出、映像的にも華やかさが目立つ。前３作のヒロインだった高峰、司、岸の三女優の競演、原作の東京から舞台を京都に移した設定など正月映画を意識してか、市川崑らしい様々な試みがなされている。撮影もシリーズ中、最も原色を強調した色使いをしており、他の作品とは異なった出来ばえになった。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京小説　乙桜学園祭", "東京小説　乙桜学園祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "「イノセントワールド」の桜井亜美と、「ZOO」の乙一こと安達寛高という2人の人気作家が映画監督に挑み、「東京でひとりぼっちの少女」をテーマにそれぞれ書きおろした物語を映像化。桜井監督の「人魚姫と王子」では心を閉ざして生きてきた孤独な少女を、安達監督の「立体東京 3D-TOKYO」では初めて東京を訪れたワケあり少女を主人公に据え、東京で次第に変化していく彼女たちの心情を小説家ならではの視点で描きだしていく。", "2007-06-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の兄弟", "花の兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "父の仇を求めて旅を続ける青山市之進はある宿場にやって来た。そしてその仇・相坂伊織がその界隈にいると知らされるが、頼みの懐中物をすられ、意気消沈。そんな時、弟・新次郎と再会し、彼が世話になっている大親分・勘右衛門のもとにわらじを脱ぐ。ところが、その娘・お玉に惚れられてしまい……。市川雷蔵、橋幸夫という異色コンビの娯楽時代劇。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RED SHOES", "赤い靴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "バレリーナのヴィクトリア・ペイジは、レルモントフに見いだされ、バレエ“赤い靴”に出演し大成功を収める。だが男たちの間で苦しんだ彼女は、バレエの内容そのままに、赤い靴を履きながら死んでいく。素晴らしい色彩で描かれたバレエ映画の名作。", "1948-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ladies and Gentlemen: The Rolling Stones", "ザ・ローリング・ストーンズ　レディース・アンド・ジェントルメン", "", "", "http://image.eiga.k-img.com/images/movie/55684/315x210.jpg?4054", 75, "世界的ロックバンド「ザ・ローリング・ストーンズ」の絶頂期、名盤「メイン・ストリートのならず者」（1972）リリース後の72年から73年にかけて行われた北米ツアーをとらえたライブドキュメンタリー。1974年に一度完成したものの一般公開されずにお蔵入りになっていた映像で、日本では2010年に最新のリマスタリング技術が施されたて劇場初上映。11年、再上映。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トニー滝谷メイキング／晴れた家", "トニー滝谷メイキング／晴れた家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "市川準監督の「トニー滝谷」の撮影現場に密着した一作。単なるメイキング・ドキュメントとは一線を画し、「シンク」の村松正浩監督がこの映画の舞台裏に吹いていた“風”をどう感じたかに関する、“シネ・エッセイ”的な趣が漂っている。特異なスタイルで撮影された本作の中にある“普遍”を見事に抽出した。市川準監督が村上春樹の小説を映画化した「トニー滝谷」。だが、横浜郊外の空き地に組まれたセット家屋には、屋根も窓もなかった。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道の妻たち 危険な賭け", "極道の妻たち 危険な賭け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "日本の極道界のトップ坂松組では、４代目の跡目争いで舎弟頭の佐渡拓磨と海原泰明の両者が対立。“北陸の女帝”と呼ばれる洲崎組組長の洲崎香矢は、借金を抱えて分の悪い佐渡に資金を提供し味方に。ところが、これがもとで、夫の弟分である神鳥組組長・神鳥亮平と対立してしまう。その頃、香矢を姐と慕う神鳥の妻・静尾の実弟で刑務所から出て来た新が、挨拶に香矢の家を訪れる。そこで新と香矢が溺愛する一人娘の香織は惹かれ合うが、やがて洲崎組と神鳥組の抗争が激化し……。工藤静香が香織を熱演。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しんぼる", "しんぼる", "", "", "http://image.eiga.k-img.com/images/movie/54524/315x210.jpg?3946", 93, "デビュー作「大日本人」（07）で、カンヌ国際映画祭の監督週間部門に正式招待された人気お笑い芸人、ダウンタウン・松本人志の監督第2作。気がつくと、四方を白い壁に囲まれた何もない部屋に閉じ込められていた謎の男の脱出劇が、コミカルかつミステリアスなタッチで描かれる。前作同様、松本自身が主演と脚本を兼任。共同脚本に高須光聖。音楽は「チャイナシャドー」「ジュブナイル」の清水靖晃。", "2009-09-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ТЕНИ　ЗАБЫТУХ　ПРЕДКОВ／THE SHADOWS OF FORGOTTEN ANCESTERS", "火の馬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ウクライナの文豪コチュビンスキーが1911年に発表した小説をもとに、鬼才パラジャーノフが独自の色彩で映画化。何世代にもわたり争いが続く二つの氏族。だが敵同士であるはずの両家の子供たち、イワンコとマリチカは幼い頃からの親友だった。大人になった彼らは愛し合うようになるが、イワンコの留守中にマリチカは事故で死んでしまう。", "1964-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風のダドゥ", "風のダドゥ", "", "", "http://image.eiga.k-img.com/images/movie/33364/315x210.jpg?4437", 95, "馬との触れ合いによって心を癒す“ホースセラピー”を題材に、心に傷を負った人々が再生していく姿を描いたヒューマン・ドラマ。不登校を続け、孤独感に耐え切れず家を飛び出した16歳の歩美。阿蘇山で自殺を図り倒れている歩美を発見した装蹄師の桜田は、自らが働く牧場へ彼女を連れて帰る。しばらく牧場で暮らすことになった歩美は、元競走馬との出会いを通して次第に心を開いていくが……。監督は「ウィニング・パス」の中田新一。", "2006-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チー公大作戦　メダカを救え！村長選挙", "チー公大作戦　メダカを救え！村長選挙", "", "", "http://image.eiga.k-img.com/images/movie/55904/315x210.jpg?3871", 0, "旧・埼玉県大宮市（現さいたま市）で実際に起きた土地購入疑惑をアイディアとして、この問題追求に尽力した当時の市議会議員・杉崎智介が自ら映画化。政治とカネの問題をコメディタッチで描く。発明家で天然キャラの江藤大作は、発明資金を稼ぐため“チー公”と呼ばれる行商人の一団に加わり、村長選挙の真っ最中の秩父・河童（かわべ）村を訪れる。大作やチー公のリーダー留らはホタルという美名の下に企てられた土地の不正購入を知り、その計画を阻止するべく立ち上がる。", "2010-12-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAWS", "ジョーズ", "", "", "http://ecx.images-amazon.com/images/I/41myg%2B4%2BeoL._SL210_.jpg", 124, "P・ベンチュリーのベストセラー小説をS・スピルバーグが監督した大ヒット作。夏の海水浴場に突如現れた巨大な人喰いザメに、警察署長、漁師、海洋学者の３人が立ち向かう。J・ウィリアムズの音楽が、サメが人を襲うシーンで恐怖感をあおり、一級品の海洋パニックものになっている。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Manina, la fille sans voile", "ビキニの裸女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "50年代から60年代にかけてアイドル級の世界的人気を得ていたB・バルドーの映画出演第２作。宝探しに出かけて恋に落ちるというモチーフは、「冒険者たち」など後の諸作にも影響を与えた。パリの大学生が沈没した船に黄金が眠るという話を聞き、コルシカの海へ。そこで、密輸業者の男と組んで探そうとするが、一方で、灯台守の娘と両思いの恋に発展。そんな時、黄金の壷が発見される・・・・・・。", "1952-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EST−OUEST", "イースト／ウエスト 遙かなる祖国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "「インドシナ」のR・ヴァルニエ監督が、冷戦時代のソビエトの苛酷な現実を描いた一大叙事詩。1946年６月、フランス人女性マリーとロシア人の夫アレクセイはスターリンの恩赦により帰国を許されたため、息子とともにソビエト行きの客船に乗り込む。だが、港へ着いた彼らを待っていたのは大勢の軍人とKGBだった……。「コーカサスの虜」で高い評価を得たロシアのS・ボドロフ監督が、脚本と台詞に参加している。", "1998-01-01", "2,24,9,65", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kites", "カイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "アメリカ、メキシコ国境を舞台にしたインド製アクション・ロードムービー。カジノを経営するトニーのフィアンセ、ナターシャに一目惚れしたインド人の若者ジェイは、カジノの金を盗み、ナターシャと2人で逃亡する。当然のように、トニーと彼の父親ボブ、賞金稼ぎ、警察に追跡される2人だったが、道中で銀行強盗をするなど、更に罪を重ねていく……。主演はインドのスーパースター、リティック・ローシャン。", "2010-01-01", "21,19,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Client", "依頼人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "", "2012-07-31", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ボクの、おじさん THE CROSSING", "ボクの、おじさん THE CROSSING", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "少年と家族、それらを包み込む自然をテーマに描き続ける、東陽一監督３部作の完結編。東京で働く川口浩二のもとに、二つの知らせが届く。父の死と、甥の拓也が郵便局に強盗に入って捕まったことだ。父の納骨のために故郷へ帰った浩二は、甥の拓也の扱いに戸惑う……。14歳の甥と29歳の叔父が、自己存在を探す旅を綴った人間ドラマ。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROPHECY", "プロフェシー 恐怖の予言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "製紙工場がタレ流していたメチル水銀廃液によって奇形化したクマの怪物が暴れ出し、街をパニックに陥れる……。「RONIN」「ブラック・サンデー」などの社会派フランケンハイマー監督による異色モンスター映画。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Highlander: The Search for Vengeance", "HIGHLANDER ハイランダー　ディレクターズカット版", "", "", "http://image.eiga.k-img.com/images/movie/53594/315x210.jpg?1269", 96, "1986年に公開されたSFファンタジー映画「ハイランダー／悪魔の戦士」の20周年を記念し、2006年にアメリカで製作された長編アニメーション。「バンパイアハンターD」など海外からも高い評価を受けるアニメーション作家・川尻善昭と日本屈指のアニメ制作集団マッドハウスがタッグを組み、首を落とされない限り永遠に生き続ける“不死者”の青年コリンの壮絶な戦いを描き出す。日本語吹替版で主人公コリンの声を担当するのは人気俳優の小栗旬。", "2008-07-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女王蜂", "女王蜂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "舞台は横浜。関東港一家の親分・鉄太郎が病に倒れ、一人娘・お竜が跡目を継ぐ。新興ヤクザ、真崎は港一家の縄張りを狙い、圧力をかけ始める。お竜は幼なじみの船員の力を貸りて、シマを守ろうと大奮闘。主演・久保菜穂子の女侠客ぶりが見もの。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Phantom of the Paradise", "ファントム・オブ・パラダイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "　作曲家のウィンスローは腹黒いレコード会社の社長スワンに新作を横取りされ、そのあげく麻薬所持の濡れ衣を着せられてしまう。刑務所に放り込まれたウィンスローはのちに脱獄し、スワンに復讐しようとするが、レコード工場で顔をつぶされ、醜い形相に変わり果ててしまう。やがて仮面の怪人がスワンに近づくが……。「オペラ座の怪人」をベースにした、おぞましくも華麗で、そして悲痛なロック・ミュージカル。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blades of Glory", "俺たちフィギュアスケーター", "", "", "http://image.eiga.k-img.com/images/movie/1814/315x210.jpg?4677", 93, "「主人公は僕だった」のウィル・フェレル＆「バス男」のジョン・ヘダーがフィギュアスケートの世界で大暴れするバカコメディ。マッチョで派手好きのマイケルズ（フェレル）とナヨナヨしたナルシスト、マッケルロイ（へダー）は同点1位になったことから世界選手権で乱闘を繰り広げ、スケート界から永久追放に。その3年後、お互いにスケートへの未練が断ち切れない2人は史上初の男子ペアを組んで再びスケートリンクに上がるが……。", "2007-12-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SORRY，WRONG NUMBER", "私は殺される", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "製薬会社のニューヨーク支店長の妻レオナは、ある夜混線電話で“今夜11時15分に例の女を殺そう”という謎の相談を聞いてしまう。慌てて警察に報告するが、警察には軽く一笑に付された。会社にいるはずの夫とも連絡が取れない。実は夫は、数年前から麻薬の密売をしており、例の女とは自分のことだったのだとレオナが気付いたとき、殺人者の手は彼女のすぐ背後に及んでいたのだった。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOUGH GUYS DON’T DANCE", "タフガイは踊らない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "妻に逃げられ毎日をアルコールに溺れて過ごしている作家ティムは、ある日自分に殺人容疑がかかっていることを知って驚く。さらに驚いたことに、彼の麻薬の隠し場所から女の生首が発見されて……。現代アメリカ文学の巨匠N・メイラーが、自作のベストセラーを自ら映画化し、反響を呼んだ作品。殺人、麻薬、スワッピングなどショッキングな題材と描写が盛り込まれた注目作で、コッポラの協力、そしてオニール、ロッセリーニの共演と話題にことかかない一作。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Waking Life", "ウェイキング・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/1214/315x210.jpg?1967", 101, "「恋人までの距離」のリチャード・リンクレイター監督が、実写映像をさまざまなイラストレーターにデジタル・ペインティングで加工させ、実写でもアニメでもない映像を展開していく。リンクレイター監督本人はじめ、彼の過去作出演者や友人、スタッフらが多数出演。ひとりの少年が夢とも現実ともつかない時間の中で、さまざまな人物と出会い、夢について、人間について、会話を交わしていく。", "2002-11-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOMBRE MIRANDO AL SUDESTE", "南東からきた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ブエノスアイレスの片田舎にひっそりと建つ精神病院。そこで働く医師デニスの担当患者が、ある朝突然、一人増えていた。その男はランテースと名乗り、宇宙船で地球にやって来たのだとデニスに告白する。果たして彼は何者で、何の目的を持ってやって来たのか？　謎は深まるばかりだった……。閉ざされた世界で、他者とは違う息吹きを持ち込んだ男が、まわりの者たちを変貌させていくという、名作「カッコーの巣の上で」を思い出させる作品。監督は、17歳で初めて短編ドキュメンタリーを撮り、これが長編２作目となったアルゼンチン出身のE・スビエラ。1986年のトロント国際映画祭国際批評家賞受賞。", "1986-01-01", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海軍兵学校物語／あゝ江田島", "海軍兵学校物語／あゝ江田島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "芥川賞作家、菊村到の原作に基づく戦記ドラマ。海軍の華とうたわれた江田島兵学校に学ぶ若き兵士たちの青春を描く。昭和17年、石川竜太郎は海軍兵学校に入学。そこで上級生のシゴキにさからう同級生、村瀬と親しくなる。一方、上級の小暮は、村瀬を目の敵のようにして鍛えながら、彼と真の兄弟のような絆で結ばれていくが……。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lost Highway", "ロスト・ハイウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "鬼才デビッド・リンチ監督が「ツイン・ピークス／ローラ・パーマー最期の7日間」以来5年ぶりに手掛けたサスペンス作品。妻の浮気を疑う人気サックス奏者フレッド。彼はある日「ディック・ロランドは死んだ」という謎のメッセージを受け取る。そしてその翌日から、彼の元にビデオテープが届き始める。1本目には彼の家の玄関が、2本目には寝室が、そして最後に届けられたテープには、彼が妻を惨殺する様子が収録されていた……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とっとこハム太郎／ハムハムグランプリン・オーロラ谷の奇跡・リボンちゃん危機一髪！", "とっとこハム太郎／ハムハムグランプリン・オーロラ谷の奇跡・リボンちゃん危機一髪！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "ミニモニ。のメンバーがミニハムズとして歌う数々のナンバーも楽しい人気アニメ。ある大雪の日、ハムちゃんずは見知らぬハムスターに導かれ、不思議な村にやってくる。雪国のその村では雪を降らせるスノー・プリンセスの伝説が残っていた。ところが、プリンセスの末裔に間違えられたリボンちゃんが海賊ハムスターにさらわれてしまった。ハム太郎たちはリボンちゃんを救おうと奮闘する。", "2003-12-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・噂の寅次郎", "男はつらいよ・噂の寅次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "旅先で人生のはかなさに思い至った寅さんは柴又に立ち寄り、修行の旅に出ることを宣言するが、“とらや”のお手伝い・早苗の姿を見るや否やそんな決意はどこへやら。夫と別居中という彼女に同情する寅に、周囲はハラハラ。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Saraband", "サラバンド", "", "", "http://image.eiga.k-img.com/images/movie/33714/315x210.jpg?4703", 112, "「第七の封印」「野いちご」などで知られるスウェーデンの世界的巨匠イングマール・ベルイマンが、自らの最後の映画として製作した家族の愛憎劇。離婚後、30年ぶりにヨハンと再会を果たしたマリアンだったが、ヨハンの息子とその娘の剥き出しの父娘愛を目の当たりにする……。主演はベルイマンと公私を共にしたリブ・ウルマン。", "2006-10-21", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VENGEANCE D’UNE FEMME", "女の復讐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "ドストエフスキーの小説『永遠の夫』を映画化。一年前、交通事故で夫を失ったセシルは、夫にスージーという愛人がいたことを知り、彼女のもとを訪れる。セシルの出現におびえるスージー。そのスージーを、セシルは精神的に追い込んでいき……。B・ダルとI・ユペールが、全編にわたって息苦しいほどの緊張感あふれる演技合戦で見せた妻と愛人の壮絶な戦い。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hotel Transylvania", "ホテル・トランシルヴァニア（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プロゴルファー織部金次郎４　シャンク、シャンク、シャンク", "プロゴルファー織部金次郎４　シャンク、シャンク、シャンク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "第２作、第３作に続き、武田鉄矢が監督を務めるスポーツ人情喜劇。プロ25年で１勝しか上げていないプロゴルファー、織金こと、織部金次郎は、前妻との娘の大学入学金などが必要となり、必死に稼がなければならない状況に。そんな彼にCF出演の依頼が舞い込む。わざとバンカーに落とし、そこからグリーンへ乗せる織金の得意技に目をつけたゴルフクラブ会社が新製品のサンドウェッジ、ステラのキャラクターに彼を抜擢したのだ。愛用のクラブを捨てて、ステラを使い始めた織金。だが、プレイ中にシャンクで友人の阿部プロの顔面にボールを当ててしまってから、織金はシャンクを連発し……。“シャンク”とは、ゴルフボールが真横に飛び出すミスショットのことで、ゴルフ愛好家の間では“寄り道”も意味する言葉。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HONEYMOON", "ハネムーン・キラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "フランス人セシールは恋人とともにN.Y.を訪れたが、恋人が麻薬密輸容疑で投獄されてしまう。ビザの延長を拒否された彼女は、恋人の元に留まるために偽装結婚するが、そこには危険な罠が隠されていた……。恐怖の感覚を巧みに描くサスペンス。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦争と平和", "戦争と平和", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「上海」の記録映画作家・亀井文夫が山本薩夫と組んで作った初の劇映画。中国から帰還した健一は、妻が友人の康吉と再婚しているのを知ってショックを受ける。しかし教師として生きていくことを決め……。ドラマに焼跡の実写フィルムを挿入して描いたヒューマンな反戦映画。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙大戦争", "宇宙大戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "超低温下で重力がなくなる“冷却線”を使って、宇宙人・ナタールが地球侵略を開始した。地球の各地では、鉄橋が光りながら空中へ浮き上がったり、汽船が山に激突したり、海が凍って空に逆流したりと奇怪な事件が次々に起こる。科学者たちのグループは冷却線の攻撃に対抗すべく、熱線砲を開発、彼らの基地があると思われる月へと向かうが……。熱線砲を抱えて月へ出発するスピップ号、途中出くわす宇宙ステーション、そしてナタールの円盤群など、宇宙空間を舞台に特技監督・円谷英二の腕が全編に冴えわたったSF映画。1959年の製作とは思えない凝ったディテールと凄まじいアクションが魅力。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOTALLY F...ED UP", "トータリー・ファックト・アップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "エイズに冒されたゲイカップルの逃避行を描いた「リビング・エンド」で注目を集めたG・アラキ監督が、10代の同性愛の現状をリアルに描いた衝撃作。ホーム・ビデオの映像を織り交ぜながら、６人のゲイ少年の日常を15の断片的なエピソードで綴る。心情を語る彼らの口から、エイズ、ドラッグ、暴力など彼らを取り巻く深刻な状況が浮かび上がる。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIDAY THE 13TH PART3", "13日の金曜日 PART3", "", "", "http://ecx.images-amazon.com/images/I/51OcTpJ4QIL._SL210_.jpg", 96, "公開のたびにショックと恐怖で観客を震撼させた“13金”シリーズの第３作。クリスタル・レイクを舞台に、三たび殺人鬼ジェイソンが若者たちに襲いかかる。今回は電気のヒューズ・ボックス、火かき棒を使っての殺人を“スーパー3D”の超立体映画で表現。", "1983-04-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAVEN", "ヘヴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "名匠クシシュトフ・キェシロフスキの遺稿を映画化した鮮烈なラブ・ストーリー。図らずも犯罪者となった女性と年下の刑務官の逃避行を、トスカーナの美しい風景の中に描き出す。英語教師フィリッパは、夫や教え子を死に追いやった麻薬売人への復讐に失敗。罪なき人々を殺してしまい、逮捕された彼女は絶望の底に沈んでしまう。刑務官フィリッポはそんな彼女に恋をし、意外な行動に出る。", "2001-01-01", "1,7,3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNA", "禁じられた好奇心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "大胆な性描写の連続で、物議をかもした社会主義国ユーゴスラビアの問題作。妻子ある中年の大学教授ミシェルは教え子ウナの小悪魔的魅力に魅せられ恋に落ちる。家庭を捨て愛に生きようとする彼だったが……。", "1985-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TROUBLE IN PARADISE", "極楽特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ハリウッド黄金期のソフィスティケイテッド・コメディの巨匠E・ルビッチが、自分の作品のなかで最も気に入ったものの一つと語った大傑作。ベニスで知り合ったリリーとガストンは二人とも大泥棒。愛を語りながら互いのものを盗み合っていた二人は、相手の腕前を知ると相棒として組んでひともうけしようとたくらむ。彼らはパリの大金持ちの未亡人の家に秘書として住み込み、財産を狙うが、リリーは嫉妬を覚え、事件は思わぬ展開に……。ウィットに富んだセリフ、小道具の利かせ方、巧みなカメラワークなど、ルビッチ・タッチと呼ばれた洗練された手法が存分に楽しめる。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少年ジャックと魔法使い", "少年ジャックと魔法使い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "魔女・グランデルは城の悪魔製造機を使って、子供たちを次々と悪魔に変えていた。その悪魔たちが、新たな子供を探しに行くのだが、少年・ジャックだけはこれに対抗し、たび重なる悪魔たちの襲撃をかわし続ける。そればかりか傷ついた悪魔のキキを助け、キキもグレンデルと戦うジャックを助けるのだった。水晶玉が壊れて醜い正体を現わしたグレンデルは、自分で仕掛けた爆弾で吹き飛んでしまう。一種独特な雰囲気を持った、東映動画設立10周年記念作品。キャラクターの描き方についてはコミカルだが、悪魔の城の暗い色彩とあいまって、全編これ悪夢の連続という印象がある。音楽も物語のテンポと巧みに同調していて、宇野誠一郎はこの作品で毎日映画コンクール音楽賞を受賞した。イギリスの叙事詩『ベオウルフ』から発想された、ノリのいい異色作。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROADSIDE PROPHETS", "蜃気楼ハイウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "友人の遺骨を葬るために、自慢のハーレーに乗って旅をするジョン。人なつっこい青年サムと道中をともにする彼は、路上の風変わりな哲学者たちとの出会いを通して、新たな人生を見いだしていく。「シド・アンド・ナンシー」の脚本に参加していたA・ウールがユーモラスに描く、乾いたタッチのロード・ムービー。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NETHERWORLD", "未来娼館 ネザーワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "行方不明となっていた父の死を知った18歳のコーリーは、生前父が通っていたという娼館を訪れる。欲望を満たす男女で満ちあふれた退廃的な館で、コーリーは父の死の真相を探るのだが……。D・シュモラー監督が描く官能的世界。音楽はボン・ジョヴィのD・ブライマンで、自身も特別出演している。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("13", "ロシアン・ルーレット", "", "", "http://image.eiga.k-img.com/images/movie/56044/315x210.jpg?3892", 97, "病気の父を抱えながら家族を養う貧しい青年ビンス、囚人のパトリック、死にひんした兄を連れ出したナゾの男ロナルドらは、それぞれ導かれるようにある館にたどり着く。そこでは17人の男たちの命をかけた集団ロシアン・ルーレットが行われており、勝率1％だが勝てば100万ドルが得られるゲームに挑むプレイヤーたちの緊張と、彼らの命にばく大な金をかけるギャンブラーたちの欲望が渦巻いていた……。ベネチアやサンダンスなど各地の映画祭で話題となった「13 ザメッティ」（2005）のゲラ・バブルアニ監督が、同作をアメリカでセルフリメイク。", "2011-06-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火の鳥2772 愛のコスモゾーン", "火の鳥2772 愛のコスモゾーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "管理社会化された、22世紀の地球連邦。そこで宇宙ハンターとなるべく誕生させられた、試験管ベビーのゴドーは、育児ロボットのオルガに育てられる。やがて上流階級の娘と恋をしたため、労働キャンプに送られたゴドーだったが、そこで出会ったサルサ博士から、滅びかけた地球を救う唯一の希望、不死の宇宙鳥のことを教えられる。ゴドーは宇宙船を奪い、サルサ博士、オルガとともに地球を脱出。そして発見した宇宙鳥、火の鳥との、壮絶な死闘を開始するが……。実写版の「火の鳥」に続いて、原作者の手塚治虫自身によるオリジナル・ストーリーでアニメ化された劇場版。かつて「千夜一夜物語」などを手掛けたスタッフが、再び手塚のもとに集まり、ロトスコープなどの新技術も取り入れて製作した。原作の持つ思想性は薄れて、最新宇宙船と火の鳥との対決が見せ場になっている。手塚ならではのユニークなキャラクターが多数登場。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LORD OF ILLUSIONS", "ロード・オブ・イリュージョン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "豊かなイマジネーションとストーリーメイキングの巧みさで、カルト的人気を誇るモダン・ホラーの鬼才C・バーカーが、製作、監督、原作、脚本の４役を担当している作品。私立探偵ハリーは新しい仕事のためにロスへやって来た。ところが依頼主の世界的マジシャン、スワンは、ショーの最中に悲劇的な死を遂げる。ハリーは調査を続けながらも、死んだスワンの未亡人ドロシアに強く惹かれていく。だが、その魅力とは裏腹に、彼女には信じられないような過去があった。そしてハリーと幻想の神との対決の時が迫る……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Investigating Sex", "セックス調査団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "　1929年、学者エドガーはセックスにまつわる実験を行なうことに。それは男たちにセックスに関するディスカッションを行なわせ、若い女性に記録させるというものだった。しかし、やがて実験は思わぬ方向へ。客観的な討論になるはずが、それぞれの発言は個人的なものに変わっていく。そして参加者の性的衝動が高まって……。詩人アンドレ・ブルトンがセックスの謎を探るために行なった集団討議の記録を、インディペンデント界の異才アラン・ルドルフ監督が大胆にアレンジして映画化。", "2001-01-01", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四日間の奇蹟", "四日間の奇蹟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "第１回『このミステリーがすごい！』大賞に輝いたベストセラーを「半落ち」の佐々部清監督が自らの脚本で映画化。心に傷を負った元ピアニストと看護婦が、脳に障害のある少女の導きで“奇蹟”に触れ、自分を見つめ直していく姿を描く。芸達者なキャストに負けない、天才子役・尾高杏奈が素晴らしい。看護婦の真理子は、高校時代に憧れていた敬輔と再会。敬輔の養女・千織は、脳障害を抱えながらも、天才的ピアニストに成長していた。", "2005-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DADDY NOSTALGIE", "ダディ・ノスタルジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「田舎の日曜日」でカンヌ映画祭監督賞を獲得したB・タヴェルニエが、残り少ない時を過ごす父娘の情愛を、優しさとユーモアを交えて描き上げた作品。夫と離婚し、パリで一人住まいのカロリーヌに、ダディが心臓発作で倒れたという知らせが届く。ダディはなんとか持ち直すが、実はあと半年の命で……。13年ぶりにスクリーンに復帰したD・ボガードが、黄昏にさしかかったダディを好演。", "1990-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浪漫者たち", "浪漫者たち", "", "", "http://image.eiga.k-img.com/images/movie/54484/315x210.jpg?3891", 75, "劇団燐光群の伊勢谷能宣が主演を務め、ドキュメンタリー作家の田中千世子が初の長編映画に挑戦した青春ドラマ。舞台俳優の伊勢谷は、パリを舞台にした「前夜」の通し稽古を進めていた。その一方で日本的なものにも憧れを持っていた伊勢谷は、能の先生が話していた三輪山へと向かう。そこで “日本浪漫派”を研究している女性に出会い、伊勢谷はより日本浪漫に興味を持つようになる。", "2009-05-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大草原の渡り鳥", "大草原の渡り鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "北海道摩周湖付近の広大な自然を舞台に、ギターを背負った“渡り鳥”が、観光地の利権を狙う悪玉からアイヌ集落を救う。小林旭と宍戸錠が息のあったところを見せ、斉藤武市の演出も好調、シリーズの最高作となった。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADIEU，PLANCHER　DES　VACHES！", "素敵な歌と舟はゆく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "忙しい実業家の母親、飲んだくれの父親、好奇心旺盛な息子らが暮らすパリ郊外の大邸宅。そんな彼らが出くわす人々との交流、恋や様々な事件が、魔術的ともいえる演出で同時進行していく群像コメディ。グルジアからパリに亡命した鬼才O・イオセリアーニが、名カメラマン、W・リュプチャンスキーの巧みな撮影を得て、個性あふれる多数のキャラクターが織り成す人間模様をひょうひょうと描写。イオセリアーニ監督独特の人生観がうかがえる一編で、何ともいい加減で場当たり的な登場人物の行動が不思議な幸福感をもたらしてくれる。", "1999-01-01", "2,26,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Prince of Darkness", "パラダイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "　ロサンゼルスの教会で、司祭が緑色の液体で満たされた棺を発見。それには悪魔が封じ込められているらしい。超常現象の研究をしているバイラック教授と研究員たちが不穏な雰囲気に包まれた現地でその調査・解析にあたるが、やがて緑色の液体を浴びた研究員が他の者を襲撃するという事態に。難を逃れた数名が部屋に立てこもるが……。悪魔の復活とその恐怖を描いた戦慄のオカルト・ホラー。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピラニア軍団・ダボシャツの天", "ピラニア軍団・ダボシャツの天", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "東映所属の脇役俳優グループ“ピラニア軍団”を主役に迎え、チンピラヤクザたちのズッコケぶりをユーモアたっぷりに描いたアクション喜劇。川谷拓三が主役で、同じピラニアながら今回は脇にまわった室田日出男、志賀勝の好演が光る。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハッピーエンド", "ハッピーエンド", "", "", "http://image.eiga.k-img.com/images/movie/55524/315x210.jpg?4736", 0, "映画オタクの桃子の日課は、レンタルビデオ店で借りてきたものをむさぼるように見ること。だが、ビデオ店の店長・黒田にいくら勧められても、ラブコメだけは毛嫌いして避けていた。そんなある日、桃子の周りでラブコメ映画のような出来事が次々と起こり始める。初めは戸惑う桃子も、勤務先の図書館にやってくる男性・村上と急接近し、次第に恋愛に没頭していく。「掌の小説」の菜葉菜が主演を務める。", "2010-06-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メトロポリス", "メトロポリス", "", "", "http://image.eiga.k-img.com/images/movie/1534/315x210.jpg?2123", 105, "人類とロボットが共存する巨大都市メトロポリス──その繁栄は、高度に発達した科学文明を享受する地上都市と、取り残され貧困にあえぐ地下都市から成り立っていた。やがて、この都市に生きる人間とロボット、そして地上都市と地下世界との間に一触即発の対立が始まる。その激しい混乱の中、生まれた人造人間ティマ。ロボットと人類の、そしてメトロポリスそのものの運命が自分に託されていることを、彼女はまだ知らない……。", "2001-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逆噴射家族", "逆噴射家族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "『東大一直線』などで知られる漫画家・小林よしのりの原案を、アクション映画を手掛けてきた石井聰亙が新しい形のホームドラマとして過激な作品に仕立て上げた。やっとのことでマイホームを建てた小林家は郊外の町に引っ越してくる。マイホーム主義のパパ、典型的な主婦のママ、アイドル狂いの娘、東大を目指すガリ勉息子の４人家族。幸せな日々を過ごしていたが、祖父が居候となり、家に白アリが巣くっているとわかった頃からパパの様子がおかしくなってきた……。後半、家族で殺し合いとなるシーンはまさに家を舞台にした戦争映画。石井聰亙らしさが出ている。評価は日本よりも海外の方が高かった。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナトゥ・踊る！ニンジャ伝説", "ナトゥ・踊る！ニンジャ伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "バラエティー番組『ウッチャンナンチャンのウリナリ！』から結成された、南々見組による歌、踊り、笑い、恋ありのマサラ・ムービー。ペンキ職人のナトゥは、妹の大学入学資金のために地域最大の富豪の家で塗装作業をする。そこでナトゥは、歌姫・ミーナのボディガードも彼女のマネージャーから頼まれるのだが、世界征服を狙う一味にミーナを誘拐されてしまう……。監督は「わが心の銀河鉄道～宮沢賢治物語」の大森一樹。テレビの枠を飛び越えて、前代未聞の全編オールインドロケを敢行したことも話題に。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日劇「加山雄三ショー」より・　歌う若大将", "日劇「加山雄三ショー」より・　歌う若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "加山雄三の日劇におけるワンマンショーを中心に、南の島タヒチで遊ぶ若大将、シリーズの名場面集、加山雄三のヒット・パレード（『君といつまでも』『お嫁においで』『夕陽は赤く』など）という構成。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チョムスキー９.11", "チョムスキー９.11", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "2001年９月11日、ハイジャックされた旅客機がニューヨークの世界貿易センタービルに突入した同時多発テロをきっかけに、アメリカ合衆国の武力行使は日増しに強まった。こうした事態を前に、一貫してアメリカ批判を続けている言語学者ノーム・チョムスキーの講演会とインタビューを収録した、見ごたえあるドキュメンタリー。アメリカを“世界最悪のテロ国家”と規定するチョムスキーの訴えは、辛辣でありながらもどこかユーモラス。混沌とした世界状況を、やさしい語り口と明快な視点から切り取ってみせる彼の人柄も伝わってくる内容。要所要所に挿入される忌野清志郎による既成曲も、絶妙な効果をあげている。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今日という日が最後なら、", "今日という日が最後なら、", "", "", "http://image.eiga.k-img.com/images/movie/53724/315x210.jpg?2805", 107, "八丈島で生まれた双子の姉妹・聖子と舞子。しかし母親は病弱な舞子だけを残して島を出て行ってしまう。それから20年後、成長した舞子は活気を失いつつある八丈島を盛り上げるため“八丈祭”を企画。祭りの直前、家族に会おうと都会を訪れた彼女は、姉の聖子と再会を果たす。20年の時を埋め合わせるかのように仲良くなった2人は一緒に八丈島へと渡るが……。自然が広がる八丈島の魅力をたっぷり詰め込んだ青春映画。", "2008-06-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ORIANE", "追想のオリアナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "コロンビアの作家モレノの短編を、ベネズエラ出身の新進女流監督トレスが映画化。20年ぶりに叔母の家を訪ねたマリアの回想を通して、1920年代、ベネズエラの大農園の中で生きたオリアナの姿を描く。現在と過去を交錯させ、オリアナの秘めたる恋と悲しみをミステリアスに語っていく演出が素晴らしい。", "1984-01-01", "2,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スレイヤーズRETURN", "スレイヤーズRETURN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "美少女魔法使いの導士、リナとナーガの珍騒動をファンタジックに描いた人気アニメ劇場版の第2弾。リナの声を担当する林原めぐみが、ノリノリの主題歌も歌っている。彼女たちは宿屋で食い物の恨みからケンカの真っ最中。そこへ、ある村の少女が現れる。悪魔の一団が村を襲い、制圧したのだという。秘密結社を名乗る彼らは、村人を使って何かを企んでいるようだ。リナとナーガは村に乗り込み、彼らと対決することに。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旗本退屈男 謎の竜神岬", "旗本退屈男 謎の竜神岬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "シリーズ30作目となるこの作品が最終作となった。玄海灘の怒箔に臨む竜神岬は、天刑病舎があるため世人が恐れ近づかぬ謎の岬だった。退屈男・早乙女主水之介は、黒田藩に武器弾薬などの禁制品密輸入の疑いありとして、当地に乗り込んでいく。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SASOM I EN SPEGEL", "鏡の中にある如く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "孤島に住む４人の家族がこの映画の登場人物。それぞれが孤独と絶望感のなかで、神と愛による救いを求めている。ベルイマン監督の“神の沈黙”に関する３部作の１本で、白黒の明暗法を駆使して神秘的な雰囲気を作り出し、重々しい主題に魅力を与えている。", "1961-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小太刀を使ふ女", "小太刀を使ふ女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "明治10年の西南の役を背景に、薩摩軍の進攻に立ち向かっていった士族の女二人の運命を感動的に描く女性時代劇。戦火の中を逃げまどう人々を励まし、助け、敵に小太刀を向けるヒロインの姿に“銃後かくあるべし”といった戦時中の雰囲気がうかがえそうだ。戦後、再公開時に「美女剣光録」と改題された。1961年には、京マチ子主演で再映画化もされている。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("双旗鎮刀客／THE SWORDSMAN IN DOUBLE−FLAG TOWN", "双旗鎮刀客〈そうきちんとうきゃく〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "砂漠の町、双旗鎮に住む婚約者の父を訪ねた少年剣士ハイコーは、彼女に乱暴しようとしたヤクザ者を斬ったために村を支配するヤクザ一派を敵に回すことになる。彼は村の入口に一人立ち、敵の馬群を迎え撃つ……。中国映画初の“西部劇”と呼ばれ「七人の侍」や「用心棒」とも比較される傑作アクション。", "1990-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生きものの記録", "生きものの記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "一貫して反戦を訴え続けた黒澤明監督が、原水爆の恐怖を真正面から取り上げた異色のヒューマン・ドラマ。町工場を経営する中島喜一は原水爆の実験に脅威を感じ、地球上で安全な場所は南米しかないと考え、家族にも相談しないで南米への移住を計画。しかし息子たちは父の計画に猛反対し、中島を裁判にかける。次第に孤独に追い込まれていく中島がとった行動は・・・。当時35才の三船敏郎が、メイクによりみごとに70過ぎの老人を熱演。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PURPLE ROSE OF CAIRO", "カイロの紫のバラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "1930年代。映画館に通い詰め、何度も同じ映画を食い入るように見続ける女性に、“君はもう５回も見ているね。そんなにこの映画が好きかい？”と突然ヒーローが語りかけ、スクリーンを抜け出て観客席にやって来る。映画の登場人物はあきれて物語の進行を中断、二人は意気投合して恋を語り合う……。映画を愛する者なら誰もが一度は持つ夢——スクリーンのヒーローやヒロインと直に語り合い、恋をしたい——そんなロマンチストにアレンが贈る粋なラブ・コメディ。映画狂らしいアレンのアイデアが冴える一級娯楽作品。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春男の翔んだ空", "春男の翔んだ空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ファイトにあふれる教師・野杉は北九州で２番目に開設された精神薄弱児特殊学級に自らすすんで担任を申し出る。精薄児のためのソフトボールを考え出すなど、スポーツを通じて言葉の通じない子供たちと心の交流を図る彼だが……。知恵遅れの子供たちの教育に自分の半生を捧げた野杉春男の涙の実話。永六輔が好演。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAZAN", "HAZAN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "陶芸家として初の文化勲章を受章した近代陶芸の先駆者、板谷波山。その半生を「地雷を踏んだらサヨウナラ」「みすゞ」等の五十嵐匠監督が映画化。エリート美術教師としての人生を捨て、妻子がありながらも陶芸家としての道を歩みだす波山。その理想を追求する男の軌跡を、彼を支える妻・まるの献身的な愛と、美しい茨城県の風景とともに心象的に映しとった好編。将来を約束された美術教師の板谷嘉七は、妻と子供たちとともに幸せな生活を送っていた。しかし、幼き日に見た美しい陶磁器を自らの手で作りたい衝動に駆られ、ある夜すべてを投げ打って陶芸に生涯を捧げる決心をする。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Wind and the Lion", "風とライオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "　1904年、仏領モロッコのタンジールで、アメリカ人女性イーデンとその子供たちがリフ族の首長ライズリーによって誘拐されるという事件が起こる。時のアメリカ大統領ルーズベルトは政治的思惑から母子救出のために大西洋艦隊のモロッコ派遣を決定。やがて戦闘が始まるが、その一方、苦しむ民衆を救うために誘拐を図ったライズリーとイーデンは次第に心を通わせていく。そしてライズリーとルーズベルトの間にも尊敬の念が……。陰謀渦巻く国で展開される壮大なスペクタクル・ロマン。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざ坊主", "やくざ坊主", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "とことん強い破戒坊主が、岡場所近くの寺に住みつき、地元のヤクザを追い出して好き勝手し放題。寺を連れ込み宿にするのは序の口で、本堂を賭博場にする始末。その頃役人と結託した井桁一家が岡場所を狙っていた。勝新の個性を生かしたピカレスク・ロマン。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MOSQUITO COAST", "モスキート・コースト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "アメリカの物質文明を忌み嫌う男アリーは５人の家族を引き連れ、大自然の中で生きようと決意する。目指すは中米ホンジェラスの聖なる密林“モスキート・コースト”。しかし新天地とは名ばかり。一家は想像を超えた険しく過酷なジャングルを目の前にし、戸惑うばかり。それでも自分の信念を貫こうとするアリーは、理想の村の建設に着手する。開拓に必死な彼らの前にある日、武装した３人の侵入者が現れ、やがて家族の運命を大きく変えてしまう事件が起こった……。「刑事ジョン・ブック／目撃者」の名コンビ、フォードとウィアーが作り上げた辛口かつ感動的な人間ドラマ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZIEGFELD FOLLIES", "ジーグフェルド・フォーリーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "数々のブロードウェイ・ヒット・ミュージカルを生み出したアメリカ・レヴュー界の父、フローレンツ・ジーグフェルド。彼の生涯を描いた作品には1936年の「巨星ジーグフェルド」があるが、この映画では天国に召されたジーグフェルドが、自作のショーやミュージカルを回想し、懐かしがるという筋立てとなっている。F・アステアやG・ケリー、J・ガーランド、E・ウィリアムズといった当時のMGMが誇るミュージカル・スターが総出演。“これぞミュージカル”という真骨頂を披露してくれる。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Girl and I", "僕の、世界の中心は、君だ。", "", "", "http://image.eiga.k-img.com/images/movie/33624/315x210.jpg?3868", 95, "04年に日本で大ヒットを記録した片山恭一原作、長澤まさみ主演「世界の中心で、愛をさけぶ」を韓国でリメイク。同じ高校の同じクラスに在籍するスホとスウン。どこにでもいる平凡な高校生スホに対し、スウンは男子生徒全員の憧れの存在だが、スウンが中学時代から想いを寄せていたのはスホだった……。主演には「猟奇的な彼女」のチャ・テヒョンと、韓国テレビ界ナンバーワン女優のソン・へギョ。", "2006-08-26", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月形半平太", "月形半平太", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "維新前夜の京洛、いわゆる東山三十六峰を背景に、開国論者の月形半平太が先駆者として凄絶な最期を遂げるまでを、祇園情緒の中に描いた時代劇の秀作。何度も映画化されたが、大衆娯楽編としては右太衛門、ひばりらが出演したこの作品が一番である。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BABY FACE NELSON", "ベビーフェイス　ネルソン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "その童顔ゆえに“ベビーフェイス・ネルソン”と呼ばれる孤高のギャング、ジョージ・ネルソン。彼はそのあだ名を返上し“ビッグジョージ”と呼ばれるため、あえて残忍かつ非道な暴力と強奪を繰り返した。アル・カポネと対立したネルソンは、新勢力を構築するためにジョン・デリンジャーと手を組み、新しい銀行強盗の方法を編み出した。マフィアの帝王アル・カポネやジョン・デリンジャーからも恐れられた、悪名高きダーティ・ヒーロー、ネルソンのすさまじい生涯を壮絶なアクションで描く。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("惨劇館・夢子", "惨劇館・夢子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "夜ごと人が殺される予知夢にうなされている女子高生、夢子。人の命が奪われると知りつつも、彼女にはそれを止める術がなかった。そんなある日、夢子は同じマンションに住む子供が殺される夢を見てしまう。ホラー漫画家、御茶漬海苔の同名コミックを映画化。悪夢と現実が交錯する惨劇に立ち向かう美少女の恐怖を描き出す。強さともろさを併せ持つヒロインに扮した美波はこれが映画初主演。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A HOME OF OUR OWN", "母の贈りもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "オスカー女優K・ベイツが“肝っ玉母さん”役に挑んだ感動的なホーム・ドラマ。女手一つで６人の子供を養う母親が廃屋を改築して、念願のマイホームを入手した。そんな彼女の力強い生き方と、子供たちとの絆を描く。E・ファーロングが長男役に扮してナイーブな好演を見せる。監督は名作「忘れられない人」のT・ビル。", "1993-01-01", "1", 0, NOW(), NOW());
