INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兎の眼", "兎の眼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "阪神工業地帯のゴミ処理場の近くの小学校を舞台に、他の生徒と言葉を交わすことなく、ただただハエを大事に飼っている少年と、なんとか彼との真の触れ合いを得ようとする女教師との交流を描く。灰谷健次郎の著名な児童文学の映画化作品。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT！：THE　TERROR　FROM　BEYOND　SPACE", "恐怖の火星探検", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "リドリー・スコット監督の「エイリアン」の元ネタともいわれるSFホラー。火星から地球に帰還しようとしている宇宙船内に凶暴な異星生物が潜入し、乗組員は一人また一人と犠牲になっていく。宇宙船の多層的な構造を巧みに生かし、怪力エイリアンの暴走を食い止めようとする乗組員の必死の闘いを緊迫感たっぷりに描いている。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tetro", "テトロ　過去を殺した男", "", "", "http://image.eiga.k-img.com/images/movie/57604/315x210.jpg?1941", 127, "「ゴッドファーザー」のフランシス・フォード・コッポラ監督が、ブエノスアイレスを舞台に家族の愛憎や葛藤をモノクロームの映像で描き出したドラマ。生まれ育ったニューヨークを捨ててアルゼンチンで暮らす兄を探しに、異母弟のベニーがブエノスアイレスに降り立つが、名前を「テトロ」と変えた兄はそっけない態度をとるだけだった。そんなある日、ベニーは兄が密かに執筆していた自伝を盗み見てしまい、それをきっかけに兄弟の仲は決裂。さらにベニー自身の出生にかかわる家族の秘密が明らかになっていく。タイトルロールを演じるのは「バッファロー'66」のビンセント・ギャロ。", "2012-01-07", "1,4,9,22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Big Trouble", "ビッグ・トラブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "　保険会社の勧誘員レナードは音楽の才能に恵まれた3人の子供を大学へ進学させようとするが、その資金がなく、困り果てていた。そんな彼の前に現れたブランシェ夫人がとんでもない提案をする。余命幾ばくもない彼女の夫スティーブを保険に加入させ、事故死に見せかけて保険金をせしめようというのだ。レナードはその話に飛びつくが、これにはウラがあった……。保険金殺人をめぐる騒動を描いたコメディ。ジョン・カサベテス監督の遺作となった。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("END OF DAYS", "エンド・オブ・デイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "世紀末を迎えた1999年12月のニューヨークを舞台にしたスペクタクル・アクション。要人警護専門のセキュリティ・スペシャリストを務めているジェリコは不審な狙撃事件に遭遇、犯人は半年前に失踪した神父だった。彼の部屋を調べたジェリコは一人の女性の写真を発見する。一方、その写真の女性クリスティーンは度重なる幻覚に悩まされ続けていた。一体、何が起きようとしているのか？　街では1000年紀＝ミレニアム最後の日を目前にして、信じられないような出来事が次々と起きていた……。次の1000年を支配しようとする闇の魔王サタン。彼の花嫁に選ばれたクリスティーンを守るため、ジェリコが戦いの渦中に身を投じていく。シュワルツェネッガーの体当たりのアクション、サタンを演じるG・バーンのカリスマ的存在感が見もの。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白鳥の歌なんか聞こえない", "白鳥の歌なんか聞こえない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「赤頭巾ちゃん気をつけて」に次いで発表された庄司薫の同名小説の映画化。一人の老人の死を間近で見つめた若い男女の意識の変化を、みずみずしい映像で綴った佳作。主人公・薫に扮した岡田裕介と、公募で選ばれた新人、本田みちこの存在感が魅力。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Road to Bali", "バリ島珍道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "珍道中ものシリーズとして「シンガポール」「アフリカ」「モロッコ」「アラスカ」「南米」と作られ、これは久しぶりに撮られたカラー作品。この名物シリーズは物語そのものよりもシリーズごとに繰り広げられる二人のギャグと豪華なゲスト・スターがお楽しみ。今回もJ・ラッセル、D・マーティン、J・ルイス、H・ボガートなどいずれも当時のパラマウント映画に出演していたゴージャスなスター俳優たちが顔を見せている。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桜の代紋", "桜の代紋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "若山富三郎製作・原作・主演による異色アクション。奥村刑事は暴力団も一目置くヤクザ刑事で、警官殺しのチンピラを捕えるや徹底的に痛めつけて拳銃の隠し場所を吐かせる。しかし、拳銃はなくチンピラも射殺される。奥村は警察内部に密告者がいるとにらむが……。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOLLYWOOD DREAMING", "それゆけハリウッド！ 種馬クンが行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "アレックは自作のシナリオを映画化するのを夢みてハリウッドにやって来る。何とか憧れの女優をヒロイン役で出演することまでは決まったのだが、資金調達はままならない。そこで彼は暇と金を持て余すハリウッドの人々にセックス・サービス作戦を展開するが……。ちょっとHなセクシー・コメディ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRIVING ME CRAZY", "あぶない週末", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "名門の寄宿学校に学ぶ離婚家庭の少年ドイル。感謝祭の休日をシカゴの母の家で過ごすため、母の恋人のダッチがアトランタの学校に迎えにくる。ダッチは労働者階級出身で、ドイルはそんな彼を軽蔑し、反抗的だったが、シカゴまでの旅を通して次第に心がふれあっていく。「ホーム・アローン」を製作したヒューズが、製作とオリジナル脚本を担当した心温まるロード・ムービー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("武闘の帝王２", "武闘の帝王２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ヤクザ同士の抗争を通して男の生き様を描く任侠アクション。３年振りにシャバに出た加賀組の組長、加賀重造は覚醒剤の密売でのし上がった同門の中島と反目。直系である国松組の跡目を狙う中島は、加賀組に圧力をかけ、国松組の会長に就任した。怒った重造は襲名披露の式典をぶち壊そうとするが……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八月の狂詩曲（ラプソディー）", "八月の狂詩曲（ラプソディー）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "村田喜代子原作『鍋の中』を映像化した反核映画。長崎はとある片田舎。かつて原爆を体験した老婆・鉦のもとに、夏休みを過ごすために都会から4人の孫がやってきた。孫たちは田舎生活を退屈に感じながらも、長崎の街に残された戦争の傷跡や鉦が話す昔話を聞くうちに、戦争に対する考えを深めていく。やがてハワイから鉦の甥にあたるクラークがやって来て……。クラーク役にリチャード・ギアを起用し話題を呼んだ作品。", "1990-01-01", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPONTANEOUS COMBUSTION", "スポンティニアス・コンバッション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "火の気がないにもかかわらず人間が突然燃え出し、衣服を残して消失してしまうという世界各地で実際に起きている怪現象SHC（人体自然発火）を題材に、ホラー映画の第一人者T・フーパーが手掛けたSF作品。高校教師のサムは、自分の感情が高まると体から炎が噴出する異常な体質であることを知る……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Monster's Ball", "チョコレート", "", "", "http://image.eiga.k-img.com/images/movie/1334/315x210.jpg?1337", 113, "父の代からの南部の死刑囚棟看守ハンクは、父譲りの人種差別主義者。父の考えに疑問を抱く息子は、和解の前に自殺してしまう。その死の衝撃から立ち直れないハンクは、やはり死刑囚の夫と息子を相次いで亡くした黒人女性レティシアと出会い、心ひかれていく。「ソードフィッシュ」の美女ハル・ベリーが黒人女優初のアカデミー主演女優賞を受賞した話題作。監督はスイス出身の新鋭マーク・フォスター。", "2002-07-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BE-BOP-HIGHSCHOOL", "BE-BOP-HIGHSCHOOL", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "これまで６作の人気シリーズが作られたが、原作者きうちかずひろのメガホンによって完全リニューアル。これまでとはまったく違うハードで暴力的な学園アクションとなった。不良高校生ヒロシとトオルは、美人女子高生ミユキをめぐって立花商業の副番ミノルと敵対。この争いが飛び火して、抗争はとんでもない方向に……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新ハレンチ学園", "新ハレンチ学園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "児島みゆきに代わって２代目ヒロイン・十兵衛に渡辺やよいが起用された。ハレンチ学園では教師と生徒の断絶がエスカレート、ついに大戦争が勃発する。学園は廃墟と化し生き残った生徒たちはハレンチ学園を乗っ取り生徒優先の体制を確立する。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA SE´VILLANE", "カメラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "『浴室』などで知られるフランス文学界のポスト・ヌーヴォーロマンの旗手J・P・トゥーサンが「ムッシュー」に続いてメガホンを執った作品。主人公は自動車教習所に通う“ぼく”と、いつも眠たげな受付け嬢パスカル。トゥーサンは彼らの人物像や直接的な状況説明を一切省き、一人称で平穏な日常を淡々と語りながら、周囲の事物や行動、その場の雰囲気などを克明に描き出していく。ジャン・ジャック・ベネックス一連の作品で知られるロバンによる映像が、肌寒く、乾いた冬の景色に味わい深い色彩を与えている。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("岸和田少年愚連隊 望郷", "岸和田少年愚連隊 望郷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ナインティナインの主演で話題を呼んだ「岸和田少年愚連隊」から始まるシリーズの第３作目で、リイチの少年時代を描く。ケンカに明け暮れる小学校６年生のリイチの日常と、とんでもない父親、優しい母親に囲まれた家族関係、そして担任の先生への思慕などが、時にユーモラスに、時に哀感を込めて綴られていく。監督は第２作の「同・血煙り純情篇」を手掛け、今や世界的に注目を集める三池崇史。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PERSONAL BEST", "マイ・ライバル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "モスクワ五輪を目指す二人の女子陸上選手の物語。とはいえスポーツ根性ものではなく、選抜大会で知り合った二人が友情を越え、レズビアンの関係になっていくという設定。異色のテーマだが、健康的なエロチシズムでさわやかな出来になっている。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fountain", "ファウンテン　永遠につづく愛", "", "", "http://image.eiga.k-img.com/images/movie/1964/315x210.jpg?4160", 96, "病に冒された最愛の妻イギー（レイチェル・ワイズ）の命を救うため、特効薬の研究に没頭する医師のトミー（ヒュー・ジャックマン）。イギーはそんな彼に、トミーの前世を思わせる中世スペインの騎士が、不死をもたらす伝説の＜ファウンテン（生命の泉）＞を探す物語を残していく。そして、2人が現在とは別の姿になった遥か未来の世界でも、2人の愛は続いていた——。監督は「π」「レクイエム・フォー・ドリーム」のダーレン・アロノフスキー。", "2007-07-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤胴鈴之助 一本足の魔人", "赤胴鈴之助 一本足の魔人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "黒い翼を持つ一本足の魔人が出現。名宝名器が盗まれ、それを追う者は瞬時にして溶かされてしまう。千葉周作から将軍家拝領の名刀・小梶丸を預かった赤胴鈴之助は、この魔人にそれを奪われる。反撃に出る鈴之助。必殺真空斬りの前に、ついに魔人は敗れ去る。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NINE 1/2 WEEKS", "ナイン・ハーフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "A・ライン監督の陶酔的映像が官能を刺激する作品。ふとしたきっかけで知り合った男が仕掛けるエロティックな行為によって、女の内側に潜む様々な感情が目覚めていく過程を９1/2週間にわたって描いた、異色のラブ・ストーリー。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Terza Madre　", "サスペリア・テルザ　最後の魔女", "", "", "http://image.eiga.k-img.com/images/movie/54214/315x210.jpg?3114", 98, "イタリアン・ホラー界の巨匠ダリオ・アルジェントが、77年の「サスペリア」、80年の「インフェルノ」に続けて放つ“魔女3部作”完結編。イタリア中部の古い墓地脇から、19世紀の棺と遺品入れが発掘された。ローマの古代美術館で働くサラは、副館長に誘われて遺品入れを調べるうちに、封印されていた邪悪な魔女“涙の母”を甦らせてしまい……。主演は「マリー・アントワネット」などハリウッドでも活躍する監督の実娘アーシア・アルジェント。", "2009-04-25", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宴", "宴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "発表されて間もなく舞台やTVドラマに翻案された利根川裕の同名小説の映画化。二・二六事件を背景に、急進派の青年将校と能楽家元夫人との悲恋を描く。新進スター・中山仁と美貌の女優・岩下志麻とのロマンティックなやりとりが見どころ。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A MID SUMMER NIGHT'S SEX COMEDY", "サマー・ナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "アレン版「夏の夜は三たび微笑む」。20世紀初めのニューヨーク州北部の山荘を舞台に、３組のカップルが恋の喜劇を繰り広げる。例によってこじんまりとまとまった、見事なアレン作品。音楽は『真夏の夜の夢』ほか、メンデルスゾーンのクラシックを使用。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("故郷", "故郷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "山田洋次は“男はつらいよ”シリーズのほかに、素朴な庶民を描いたリアリズム映画を何本か撮っている。「家族」(1970)、「同胞」(1975)、「幸福の黄色いハンカチ」(1977）などとともに本作品もそちらの系譜に属するもの。瀬戸内海の小島に住む夫婦は、家族ぐるみで楽しく砕石運搬船の仕事をしていたが、産業の近代化によって、自営ではやっていけなくなる。呉の造船所を見学して島を離れる決心を固めたあと、一家は最後の運搬のために船に乗り込むのだった……。美しい自然とそれを脅かす工業化社会とを対比させながら、日本の高度経済成長とは何だったのかを問い直す力作。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HORROR SHOW", "デビルジャンク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「13日の金曜日」のカニンガム監督が製作するファンタスティック・ホラー。超自然的パワーを備え持つ殺人鬼から家族の命を救うため、敢然と立ち向かう刑事の姿を描いていく。「ガバリン」や「ザ・デプス」でカニンガムとコンビを組んだJ・アイザックの監督デビュー作。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蛇の道", "蛇の道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "黒沢清が「CURE／キュア」に次いで撮り上げたサスペンス。幼い娘を惨殺された宮下が、偶然知り合った謎めいた男・新島の力を借りて復讐に乗り出す。容疑者のヤクザたちを監禁し、拷問を与えて真相を聞き出そうとするが、彼らは無実を主張。事件をめぐるミステリーに加え、新島の正体と恐るべき目的が徐々に明らかになっていく過程がスリリング。残酷な拷問シーンや人間の怒り、醜い感情を克明に描く心理描写などに、異様な緊迫感があふれる。ビデオ、DVDタイトルは「修羅の極道　蛇の道」。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戒厳令", "戒厳令", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "昭和11年２月26日に青年将校たちが、武装決起してクーデターを起こしたいわゆる“二・二六事件”において施行された戒厳令を背景にして、その事件の陰の指導者として処刑された北一輝の思想と生き様を、別役実の脚本を得た吉田喜重監督が独自の硬質な演出で描き出した問題作。『日本改造法案』を著した北一輝は、多くの若者に影響を与え、自らの思想を実行に移す機会をうかがっていた。五・一五のクーデターに失敗後、血気にはやる青年将校たちは時期を待ちきれず一輝の態度に煮えきらないものを感じる。自分の意に反し、その思想が予想外にふくらんでいくのに戸惑う一輝。そして雪降る２月26日が来て……。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("公主復仇記", "ビヨンド・アワ・ケン", "", "", "http://image.eiga.k-img.com/images/movie/56374/315x210.jpg?1768", 98, "香港の鬼才パン・ホーチョンの監督第8作「ドリーム・ホーム」（2010）の公開を記念して、ホーチョン監督の日本未公開作を一挙上映する特集上映「パン・ホーチョン、お前は誰だ!?」で公開。女優志望のシャーリーの前に、シャーリーの恋人ケンと過去につきあっていたというワイチンが現れる。ワイチンはシャーリーに、以前ケンと一緒にベッドで撮った写真を取り戻してほしいと頼むが……。香港の人気アイドルユニット「Twins」のジリアン・チョンと「ションヤンの酒家」の中国人女優タオ・ホンが主演のガールズムービー。", "2011-06-04", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("携帯彼女＋（プラス）", "携帯彼女＋（プラス）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-08-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・男の紋章", "続・男の紋章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "出所した竜次はヤクザ稼業をやめる決心をし、堅気の土建業に専念しようとするが、大工事の指名争いで、母の率いる村田組に敗れる。しかし母は、息子の代理人という役割を演じ竜次の顔をたてたのだった。竜次は最後に前作で討ちもらした父の仇を討つ。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CUBE", "CUBE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "警官、女医、女子大生ら６人の男女が、ある日突然、不気味な四角形の部屋に閉じ込められる。そこは、いくつもの部屋の集合体である巨大なキューブ状の建物のどこかだった。唯一の脱出口を目指して部屋から部屋へと移動する彼らを、恐怖の殺人トラップが待ち受けていた……。キューブの存在理由などの背景は一切明かさず、カフカ的な不条理状況での脱出劇が展開する異色サスペンス。密室的な緊張感をあおり立てるビジュアルに、仲間割れを起こす人間の心理的なもろさも丁寧に描き込まれ、最後まで誰が生き延びるのか予想できない。ヒッチコックの「救命艇」、ジョージ・ルーカスの「THX−1138」などから影響を受けたというカナダの若手監督、V・ナタリの低予算をものともしない映像センスが光る。DVDは「ELAVATED」を併録。", "1997-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("催眠", "催眠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "都内で変死事件が相次いで発生。被害者たちは死に際に“ミドリの猿”という謎の言葉を残していたことが判明した。捜査一課の櫻井刑事に協力する心理カウンセラー、嵯峨は彼らに催眠暗示がかけられていたのではないかと推測する。さらに事件が続く中、テレビ番組に出ている由香という女性に目を留めた嵯峨は彼女が多重人格症に陥っていると考え、彼女を保護しようとするが……。「パラサイト・イブ」の落合正幸が、社会派ドラマとして高い評価を得ていた松岡圭祐の小説を大幅にアレンジして映画化したサイコ・スリラー。天才的心理カウンセラー役を演じた稲垣吾郎の繊細な演技も光る。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVER SMILE NEW JERSEY", "エバースマイル、ニュージャージー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「王様の映画」でデビューしたアルゼンチン出身の監督、C・ソリンの“伝導歯科医”物語。“虫歯は世界を滅ぼす”をモットーに、南米パタゴニアをバイクで巡回する歯科医ファーガス。牧師のような信念を持つ彼と、道中で知り会った娘エステラの恋模様が綴られる。D・デイ・ルイスが、孤高の医者に扮し、ブラック・ユーモアを交えて好演。", "1989-01-01", "3,22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("探偵事務所５”／　５ナンバーで呼ばれる　探偵達の物語", "探偵事務所５”／　５ナンバーで呼ばれる　探偵達の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "「私立探偵濱マイク」シリーズを手掛けた林海象監督が新たに生み出した探偵物語。川崎に本部を構える名門探偵事務所5の探偵二人が難事件の解決に挑む。主演の成宮寛貴をはじめとする絶妙なキャスティングや独特の映像美、個性的な世界観など、随所に林監督のこだわりが伺える意欲作だ。念願の探偵事務所5のメンバーになった探偵591。新人で仕事のない彼は5の会長の孫娘、瞳に頼まれ、彼女の友人を捜し始めるが……。", "2005-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TARZAN ESCAPES", "ターザンの逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "巨万の富を遺した叔父の遺産を相続するためには、ジェーンのサインが必要と、二人のいとこがイギリスからアフリカ奥地へやってきた。彼らはフライ大尉を道案内にターザンとジェーンが暮らすミュティニー絶壁に向かうが、大尉はターザンを捕らえて見世物にしようとたくらんでいた……。J・ワイズミュラー主演によるシリーズ第３作。DVDは「ジョニー・ワイズミュラー ターザン・フィルムズ」に収録。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE´RE｀SE", "テレーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "テレーズは修道女。15歳で修道院入りし、永遠に自分の人生をイエスに捧げることを誓う。厳しい修行にも耐え、リュシーという友も得る。そんな時不意に、喀血がテレーズを襲った。精緻な舞台劇的演出で、セザール賞の主要６部門を独占した佳作。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mitt liv som hund", "マイライフ・アズ・ア・ドッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "人々との出会いや別れに戸惑いながらも成長していく少年の姿を描いた、心温まるヒューマンドラマ。1950年代末のスウェーデン。海辺の小さな町に住む12歳の少年イングラムは、病気の母親の元を離れ、叔父が暮らす田舎の村へ行くことになった。個性的な村人たちに囲まれて過ごす楽しい日々。しかし、そんな彼の上にも現実は重くのしかかる……。スウェーデンの名匠ラッセ・ハルストレム監督の名を、一躍世界に知らしめた傑作。", "1985-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お嬢さん社長", "お嬢さん社長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "日本一乳菓の鬼社長が持病に悩まされて現役を引退、社長の椅子を16歳の孫娘マドカに譲る。歌劇スターを夢見るマドカは、仕事なんてそっちのけ。浅草こそが彼女の遊び場だ。ところが、偶然そこで自社のお菓子が横流しされているのを見つけてしまう。マドカ社長は一念発起、悪徳幹部を追い落とし、よい子のための日本一よい会社を目指す。若い美空ひばりのハイカラなキャラクターを生かした川島雄三の風俗コメディ。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遊侠列伝", "遊侠列伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "高倉健と藤純子の共演による任侠映画。恋女房に先立たれ、テキヤ稼業で息子を育てていた主人公が、世話になった親分を悪玉ヤクザ一家に殺され、ドスを懐に敢然と身体を張る。老ヤクザに扮する嵐寛寿郎が渋い味を出している。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歩いても　歩いても", "歩いても　歩いても", "", "", "http://image.eiga.k-img.com/images/movie/53384/315x210.jpg?2318", 104, "「誰も知らない」「幻の光」の是枝裕和監督が、年老いた両親の元に久々に集った家族の情景を静かなタッチで切り取り、人生の喜びと悲しみを浮かび上がらせたホームドラマ。ある夏の日、元開業医の横山恭平とその妻とし子が2人きりで暮らす家に、次男の良多と長女のちなみがそれぞれの家族を連れてやって来る。何気ない団欒のときを過ごす横山一家だったが、この日は15年前に亡くなった長男・順平の命日だった……。", "2008-06-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デボラがライバル", "デボラがライバル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "吉川ひなの、篠原ともえなど、個性的なアイドルたちが繰り広げる青春恋愛コメディ。多田かおる原作の人気コミックの映画化。大学のアメフト部のキャプテン・五十嵐に一目惚れした高校生の朝代は、彼と同じ大学に入学。同時に念願の一人暮らしも始めたが、その隣室の住人はアメフト部の花形クォーターバックでデボラと名乗るオカマだった。朝代はデボラと部屋を行き来するくらい仲良くなり、彼のお陰で五十嵐と付き合うことができた。しかし、ふとしたことからデボラも五十嵐を愛していることを知り……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dear God", "ゴッド・エージェント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "詐欺師のトムはおとり捜査員を騙そうとして捕まってしまう。罰として郵便局で働くことになったトムは、神様宛に書かれた手紙などの配達不能郵便の整理係となる。ところが、偶然、トムの行動が神様宛の手紙を書いた送り主の願いを叶えていて……。市民のヒーローになってしまう詐欺師の男を描いたコメディ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RING", "リング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "見せ物小屋で無敵を誇るジャック。そんな彼に挑戦し、善戦した青年ボブはメキメキと頭角を現わし、ついにボクシングのチャンピオンとなる。そしてボブはジャックの恋人ネリーに惹かれ愛を告白、彼女の心も揺れ動く。ヒッチコックがサスペンスではなく、男女の三角関係を描いた、異色の未公開作品。", "1927-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE HEART", "緊急指令・オペレーション　ブルーハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "朝鮮戦争を舞台に、韓国特殊部隊の活躍を描いた戦争アクション。朝鮮戦争のさなか、ジェースン大尉率いる米国諜報船が北朝鮮軍によってだ捕され、収容所に連行された諜報員たちは、クアン少佐から執拗な拷問を繰り返し受けていた。米軍の依頼を受けたチャン大尉をはじめとする韓国軍は、諜報員たちを助けるために決死の覚悟で救出作戦を開始する。", "1987-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Summer of Sam", "サマー・オブ・サム", "", "", "http://image.eiga.k-img.com/images/movie/1844/315x210.jpg?1438", 142, "1977年、真夏のニューヨーク、ブロンクス。44口径の銃を持った殺人鬼が、夜の街を徘徊する市民を無差別 に殺し始めた。新聞はこのサイコパスの記事を書き連ね、犯人を“サムの息子”と呼び始めた。誰もが容疑者で、誰もが次の被害者。“サムの息子”の恐怖はやがてニューヨーク全土を狂気へと駆り立てる。路上で“サムの息子”の被害者の死体を目撃したヴィニーも、次は自分が殺されるかもしれないという不安にとり憑かれていた……。", "2000-04-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COCOANUTS", "ココナッツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "当時ブロードウェイで人気を博していたお笑いグループ、マルクス四兄弟が映画に初進出した作品。ドタバタ調のナンセンス・コメディこそ、彼らの持ち味。フロリダにある経営不振のホテルが舞台。宿泊客の青年は、資産家の未亡人の財産を狙っていた。そこで、同宿の男とつるんで未亡人の高価な首飾りを盗むことに……。クイック・モーションを効果的に使った映像も新鮮。", "1929-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HITCH", "最後の恋のはじめ方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "会話やダンス、さりげないボディタッチなど、女心をつかむためには9つの法則がある。その独自の法則に従ってモテない男たちを幸せにする恋愛のプロにウィル・スミスが扮したロマンチック・コメディ。N.Y.の最新デートスポットの数々や、デートを盛り上げる音楽のチョイスにも注目を。恋愛コンサルタントのアレックスは、何人もの男たちの恋愛を成就させてきた。そんな彼が、新聞記者のサラに恋をしてしまい……。", "2005-06-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("めぐる", "めぐる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "日本に古くから伝わる染色技法「木版染め」の職人・藤本義和に密着したドキュメンタリー。江戸小紋の職人として働いていた頃、古代の木版染めの古布と運命的な出合いを果たした藤本。それ以来、長い歳月をかけて自ら研究を重ねてようやくその技術を自分のものとした藤本は、現在は木版染めの数少ない伝承者として活動を続けている。反物に命を吹きこむべく、ひたすらに版を押し続ける藤本の姿を通し、“職人”という生き方を浮かびあがらせていく。", "2006-11-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE PIECE ワンピース　エピソード　オブ　チョッパー　プラス　冬に咲く、奇跡の桜", "ONE PIECE ワンピース　エピソード　オブ　チョッパー　プラス　冬に咲く、奇跡の桜", "", "", "http://image.eiga.k-img.com/images/movie/53324/315x210.jpg?3593", 110, "大ヒットコミック「ONE PIECE」の中でも特に人気の高いキャラクター、チョッパーがルフィ海賊団に加わるまでの感動的なエピソードを映画化。航海を続けていたルフィ一行の航海士ナミが突然高熱で倒れてしまう。医療大国と言われるドラム王国に辿り着いた彼らは早速医者を探すが、現在王国にいる医者は変わり者の老婆“ドクターくれは”ただ1人。ルフィは苦しむナミを背負い、雪山の頂上にあるくれはの住処を目指す。", "2008-03-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOS　AMANTES　DEL　CI´RCULO　POLAR", "アナとオットー", "", "", "http://ecx.images-amazon.com/images/I/51jt0yIDKbL._SL210_.jpg", 112, "スペインのJ・メデム監督の正式な日本初公開作となったラブ・ストーリー。８歳の時にめぐり会った瞬間、お互いを“運命の人”と確信したアナとオットー。奇しくも親の事情によって義理の兄妹になった二人が、激しく愛し合いながらも離ればなれとなり、白夜のフィンランドでの再会に向かって疾走していく。主役二人のモノローグを全編に導入し、様々な時間軸を交錯させた緻密な編集で、17年間に渡る彼らの軌跡を描写。きわめて内省的な映像世界でありながら、圧倒的な情熱がほとばしる愛の形を焼き付けた監督の才気が光る。", "1998-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美しい青年パク・ヨンハの最後の願い", "美しい青年パク・ヨンハの最後の願い", "", "", "http://image.eiga.k-img.com/images/movie/56434/315x210.jpg?2109", 50, "2010年6月30日に急逝した韓国の人気俳優パク・ヨンハの生前最後の映像となったドキュメンタリーを劇場上映。09年9月、韓国SBSの社会貢献番組「希望TV」の企画に賛同したヨンハがアフリカのチャドへ赴き、貧しさに苦しむ子どもたちのために学校を建設する活動に密着した。学校はヨンハの死後となる10年9月18日に開校し、生前のヨンハと親しかった俳優のパク・ヒスン、パク・ハソンが代理で開校式に出席した様子も収めている。", "2011-06-25", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　GARAGE", "自動車屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 15, "チャールズ・チャップリン、ハロルド・ロイドと並ぶ世界の三大喜劇映画王の一人バスター・キートン主演による、サイレント短編コメディ。自動車修理工場と消防署が一緒になったユニークな空間を舞台に、キートン扮する主人公とその相棒の二人組が、キザで鼻持ちならない女ったらしを相手に大騒動を巻き起こしていく。", "1920-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("12 MA¨DCHEN UND １ MANN", "白銀は招くよ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1956年の冬期オリンピックで、アルペン種目３つの金メダルを独占したオーストリアのスキー・チャンピオン、T・ザイラー主演の、恋と冒険のスキー映画。12人の若い娘がスキーを楽しむ平和な村で、盗難事件が発生、国家警察本部はスキーの名手フロリアンを調査に派遣する。全編を彩る白銀の世界も見もの。", "1959-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OF LOVE AND SHADOWS", "愛の奴隷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ベネズエラ育ちのアメリカ人女性監督、B・カプラン初の長編作品。軍事政権下のチリ。女性ジャーナリストであるイレーネは、華やかなファッション雑誌で名声を得ることで、国家警察による醜い現実から逃れ平穏に暮らしていた。彼女はエリート軍人のグスタポと婚約していたが、雑誌に売り込みに来た写真家フランシスコに惹かれ、愛が芽生える。フランシスコはスペイン人亡命者の息子で、地下活動にも携わり、今のこの国では危険な立場にあった。そして二人は、ある行方不明の女性の追跡捜査をきっかけに、軍の恐ろしい行いを知ることになる。DVDタイトルは「ジェニファー・コネリー　愛の奴隷」。", "1994-01-01", "1,22,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Un long dimanche de fiancailles", "ロング・エンゲージメント", "", "", "http://image.eiga.k-img.com/images/movie/1504/315x210.jpg?3672", 134, "「アメリ」のジャン＝ピエール・ジュネ監督が、同作で人気女優となったオドレイ・トトゥを再び主演に迎えた最新作。第1次世界大戦下のフランス。脚が不自由なマチルドの元に、出征した恋人マネクの死亡通知が届くが、彼の死を信じられないマチルドは、私立探偵を雇い、マネクの戦地での足取りを探っていく。ジョディ・フォスターがフランス語で出演、音楽はデビッド・リンチ監督作で知られるアンジェロ・バダラメンティが担当。", "2005-03-12", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("荒くれKNIGHT", "荒くれKNIGHT", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "伝説の暴走族漫画「湘南爆走族」の原作者・吉田聡による同名漫画を実写映画化。善波七五十がBIKEPACK“輪蛇”の三代目リーダーになる前の時代。17歳の善波は、仲間たちと共にバイクで湘南海岸を走る日々を送っていた。そんなある日、“輪蛇”二代目リーダーの木原が、かつて袂を分かった“がらがら蛇”の残党に襲われて重傷を負ってしまい……。主人公の善波に「テニスの王子様」の城田優、ヒロインに人気アイドルの安田美沙子が扮する。", "2007-04-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARMITAGE III／POLY-MATRIX", "ARMITAGE III／POLY-MATRIX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アメリカでジャパニメーションとしてヒットしたアニメ「アミテージ・ザ・サード」を再編集し、K・サザーランド、E・バークレーらが英語吹き替えをしたインターナショナル版。火星警察に赴任するため空港に向かったロス警部補は、出迎えの少女アミテージと殺人事件に巻き込まれる。殺されたのは火星一の人気シンガー、マッキャノン。しかし、マッキャノンは感情を持つロボット、つまりサードだった。ロスは捜査の担当になり、出迎えの少女アミテージと組むことになる。彼女は火星警察の巡査部長刑事だったのだ……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST OF ENGLAND", "ラスト・オブ・イングランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「ジュビリー」「カラヴァッジオ」などで知られるD・ジャーマン監督の最もパーソナルな作品。監督の自室での夜間撮影をはじめ、1928～53年にかけて彼の両親や祖母によって撮影されたホーム・ムービー、ロンドンやリバプール市街の廃墟でのドキュメンタリー・スタイルの撮影、そして、「カラヴァッジオ」にも出演していたT・スウィントンとS・レイの二人が恋人同士を演じる近未来的世界の映像。この作品は、これら４つの部分からできている。それは、時にはセンチメンタルに、時にはペシミスティック、時にはスピード感あふれる雰囲気を醸し出し、４つの異なるイメージが絡み合った世界を作っている。ベルリン映画祭フォーラム・国際芸術映画委員会賞（CICAE）受賞。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOM AND JERRY：THE MOVIE", "トムとジェリーの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "1940年に誕生して以来、世界中で親しまれているアニメ・キャラクター“トムとジェリー”の初長編劇場公開作品。住み慣れた家が取り壊され、初めて見知らぬ世界に飛び出したトムとジェリーは、かわいい少女ロビンと出会う。いじわるなフィッグおばさんのせいで、家を飛び出したロビンのために、トムとジェリーはロビンのお父さんを探す旅に出る。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Carmel", "カルメル", "", "", "http://image.eiga.k-img.com/images/movie/55044/315x210.jpg?4709", 93, "「キプールの記憶」「フリー・ゾーン／明日が見える場所」といった作品で国際的な注目を集めてきたイスラエルの名匠アモス・ギタイ監督が、紀元1世紀のユダヤ戦争から現代の軍事キャンプに至るまでの祖国イスラエルの歴史を、自らの個人史と絡ませながら描く。フランス映画界を代表する名女優ジャンヌ・モローがナレーションを務める。第34回トロント国際映画祭、第10回東京フィルメックス上映作品。", "2009-11-28", "46,2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("就是溜溜[女也]", "ステキな彼女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "親が決めた結婚に反発し傷心旅行に出た財閥のひとり娘パンは、故郷の農村で測量技師のクーと出会う。パンは彼の純粋さに惹かれ、日々恋心を募らせてゆく。しかし、結婚を目前にした彼女は誰にも告げずひとり村を去る。ところがある日、クーは偶然パンの挙式に出くわしてしまう……。台湾ニュー・シネマを代表するホウ・シャオシェン監督のデビュー作。", "1980-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INVASION FORCE", "コマンド・ウォリアーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「ランボー」「コマンドー」の女性版ともいえる戦争アクション。ある山奥で行われていた映画のロケーション現場に、数百人の兵士が現れた。彼らはアメリカ征服をたくらむテロ軍団で、目撃者となってしまったロケ隊を抹殺しようとする。皆が逃げまどうなか、一人の美人女優が機関銃を手に反撃を始めた。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SPIRAL STAIRCASE", "らせん階段", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "町はずれの不気味な古い館には、病床に就いた女主人と、何人かの怪しい人物たちが住んでいた。この屋敷に女中としてろうあの娘が雇われるが、そのころ町では、障害者の娘ばかりを狙った殺人事件が起こっていた……。R・シオドマクがその繊細な演出力で描ききった非常に美しいスリラーの傑作である。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("牛棚／CHINE，MA DOULEUR", "中国、わがいたみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "チェン・カイコー、チャン・イーモウらと同世代で、フランスで映画を学び、パリ在住の中国人ダイ・シジエの監督デビュー作。内容的には純然たる中国映画に見えるが、実際はフランス映画である。撮影も、中国政府からの許可がなかなか下りず、結局ピレネー山中で行われた。舞台は、文化大革命の嵐が吹き荒れるころの中国。“メガネ小僧”と呼ばれる一人の少年が、禁止されている昔のラブ・ソングのレコードをかけたというそれだけのことで人民の敵とされ、犯罪者として山奥の更生センターに収容される。いったんは脱走を試みるも失敗、以後従順に規律に従う毎日を過ごしていくのだった……。撮影はスレイマン・シセの「ひかり」のJ・M・ユモー。ジャン・ヴィゴ賞、ロカルノ映画祭審査員特別表彰などに輝いた秀作。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・日本の首領２", "新・日本の首領２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "広島峠会会長、長田の跡目を継ぎ二代目会長を襲名した尾崎は、大阪辰野会組長である藤木の杯を受ける決意を固めるが……。日本初のアクション監督出身の映画監督、高瀬将嗣のもとに、松方弘樹をはじめ豪華キャストが集結した任侠ヤクザシリーズ第２作。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HGHLANDER", "ハイランダー 悪魔の戦士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "首をはねられない限り不老不死でいられる男たちの一団が、最後に生き残った者にだけ授けられる“究極の力”をめぐって、時空を超えた凄絶なサバイバル闘争を繰り広げていく。現代ニューヨークと中世のイギリスを舞台に、マクラウドとクルガンの二人のハイランダーの間に迫真のバイオレンスが炸裂する。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY GIANT", "マイ・ジャイアント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "人気コメディアン、B・クリスタルとNBAのスター、G・ミュアサンが共演したハートフル・コメディ。スカウトマンのサミーが、ルーマニアで身長２メートル30センチの巨人マックスを発見。間違いなくビッグスターになると確信し、彼にアプローチを開始する。ところが、田舎で質素に暮らしてきたマックスには全くその気がない。そこでサミーは、マックスが22年間思いを寄せている女性リリアーナと再会させると約束し、アメリカに渡るのだが……。S・セガールが本人の役で特別出演。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MODESTY BLAISE", "唇からナイフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "新聞連載漫画を原作とした奇想天外なナンセンス・コメディ。イギリス秘密情報部は中東マサラ国に石油の代金として送るダイヤモンドがギャングに狙われていることを知り、札つきの女賊モデスティに依頼してダイヤ護衛にあたらせる。相棒のウィリーと組んだモデスティだが、ギャング団の罠にはまり捕虜になったうえダイヤも奪われてしまう。果たしてモデスティの運命は……。突然ミュージカル調になったり、B級SF映画のようなセットが出てきたりと、とにかく破天荒な内容で、本当にこれがあのJ・ロージー作品かと目を疑うほど。M・ヴィッティの喜劇役者ぶりも楽しい快作だ。", "1966-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHISPERS IN THE DARK", "暗闇のささやき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「ゆりかごを揺らす手」で、子供を守るために立ち向かう強き母親を演じたA・シオラが主演するサイコ・スリラー。美しい精神分析医が患者の診療をするうちに、自らの性的な欲求に苦悩し始める。やがて患者の一人が首を吊った死体で発見され、続いて第２の殺人が起こるのだが……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOOR DAR ATASH", "戦火の中へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "イラン・イラク戦争の前線から戻ってくるはずの息子に会いに約束の場所へやって来た父だったが、息子は現れない。仕方なく、父は息子に会うため前線まで行くことにする。次第にイラク軍の攻撃は激しくなり、兵士たちは止めるが、父はさらに前線へ行こうとするのだった。", "1992-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRAIGHT TO HEAVEN 天国へまっしぐら", "STRAIGHT TO HEAVEN 天国へまっしぐら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「傷だらけの天使」など多くのヒット作を手がけてきた脚本家・柏原寛司が自らメガホンを取り、1970年代のB級アクションを意識して撮りあげたハードボイルドアクション。振込め詐欺をして稼いでいた練馬少年鑑別所出身の浩二と達也は、上部組織の元締・児山に保険金をかけられ、命を狙われるハメに。達也の故郷である長崎へと逃亡した2人だったが、そこでも地元ヤクザとのトラブルに巻きこまれてしまい……。", "2008-04-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢一族 ザ・らいばる", "夢一族 ザ・らいばる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "銀行に預金したまま連絡の途絶えている、いわゆる“睡眠口座”に目をつけ、すでに死んでしまった預金者の息子になりすまそうとする男。彼と老いぼれサギ師である預金者の父との数奇な出会いと友情を描いたヒューマン・コメディ。原作はC・ウールリッチの『睡眠口座』。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日蓮と蒙古大襲来", "日蓮と蒙古大襲来", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "日本映画史のなかでも最大級に位置するスペクタクル超大作。日蓮上人が様々な妨害、弾圧に遭いながらも、信念を貫き、日蓮宗を確立するまでを描く。クライマックスの蒙古来襲のスペクタクル・シーンが最大の見もの。大映特撮陣の力のほどがうかがえる。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("2LDK", "2LDK", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "“DUEL（対決の意味）”という同一テーマのもと、堤幸彦監督と北村龍平監督が中編を競作。「恋愛寫眞」で新境地を切り開いた堤監督によるこちらは、女性タレント二人が壮絶バトルを繰り広げる。見どころは野波麻帆と小池栄子の体当たり演技。その女優生命を賭けたといっても過言ではない過激な女の戦いは、一瞬たりとも目が離せない。事務所のマンションをルームシェアする先輩女優のラナと売り出し中のグラビア・タレントの希美。表向きは仲の良い二人だったがある夜、些細なことで口論に。すると日頃、積もり積もった不満が爆発し、二人は大ゲンカになる。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("めがね", "めがね", "", "", "http://image.eiga.k-img.com/images/movie/34054/315x210.jpg?2587", 106, "人生の小休止に、南の海辺の町を訪れたタエコは、不思議な一軒宿ハマダに宿泊する。しかし、観光する場所も何もない小さな浜辺の町で、マイペースすぎる住人たちに振り回されるタエコは、耐えかねてハマダを出て行くが……。フィンランドの日本食堂を舞台に、スローライフな日常を描いて話題となった「かもめ食堂」の監督・荻上直子、主演・小林聡美が、今度は南の海辺を舞台に人生を見つめなおす人々をゆるやかに描く。", "2007-09-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ПРОВЕРКА НА ДОРОГАХ／TRIAL ON THE ROAD", "道中の点検", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "モスクワ・ニューウェイブ派の一人ゲルマン監督の長編処女作。1942年冬、ドイツ軍占領下のロシア北西部で活動を続けるパルチザン部隊に、一人の男が投降する。彼を受け入れる隊長、銃殺を主張する軍事委員……。極限状態における、人間の信頼、友情、懐疑、反目をサスペンスフルに描いた秀作。", "1971-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PISTOL，THE BIRTH OF THE LEGEND", "バスケット・ボーイ ピート・マラビッチの青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "“ピストル”と異名をとった往年の天才バスケットボール選手ピート・マラビッチの少年時代を、本人の自叙伝をもとに映画化した作品。バスケットのコーチをしている父のもとで５歳からプレーを始めたピートは独自のトリッキーな技を身につけていくが、やがて高校に進んだ彼は派手すぎと批判される……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FEEL　LIKE　GOING　HOME", "フィール・ライク・ゴーイング・ホーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "音楽をこよなく愛する映画監督７人が参加したドキュメンタリー・シリーズ“THE BLUES Movie Project”からの一編。かつて音楽ドキュメンタリー「ラスト・ワルツ」を手掛けたM・スコセッシによるこの作品は、ミシシッピから西アフリカまで旅をしながらブルースのルーツをたどっていく。ブルース界の巨人たちの伝説のライブなど、貴重な映像も盛りだくさん。ミシシッピ・デルタからアフリカ大陸のマリへ、M・スコセッシがブルースの根源を求めて旅に出発。現役ブルースマン、コリー・ハリスをガイド役に各地を歩き、タジ・マハールやサリフ・ケイタらとジャムを繰り広げる。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZUGVOGEL…EINMAL　NACH　INARI", "ハンネス、列車の旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ヨーロッパの鉄道を舞台にしたユーモラスなロード・ムービー。ビール配達人ハンネスは時刻表マニア。フィンランドで行われる最速ルート探しの時刻表競技大会に出場するため、彼は休暇をとるが、それが原因でボスにクビを言い渡される。だが、このボスが殺害され、ハンネスに容疑が。そんなこととはつゆ知らず、彼は列車でフィンランドを目指し、車内で出会った女性と心を通わせるが……。ビデオ、DVDタイトルは「逃走特急 インターシティ・エクスプレス」。", "1998-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベッドタイムアイズ", "ベッドタイムアイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "山田詠美原作によるセンセーショナルな同名小説を、女を描かせたら右に出る者がいない名匠・神代辰巳が監督して、劇作家・岸田理生の脚本により映画化。日本人女性と黒人男性の恋人同士の日常を淡々と描いていく物語で、神代独特のテンポと詩情があふれる一編。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Midnight in Paris", "ミッドナイト・イン・パリ", "", "", "http://image.eiga.k-img.com/images/movie/56604/315x210.jpg?3469", 94, "「アニー・ホール」「それでも恋するバルセロナ」のウッディ・アレン監督・脚本によるラブコメディ。ハリウッドで売れっ子の脚本家ギルは、婚約者アィネズと彼女の両親とともにパリに遊びに来ていた。パリの魔力に魅了され、小説を書くためにパリへの引越しを決意するギルだったが、アィネズは無関心。2人の心は離ればなれになり……。キャストはギルにオーウェン・ウィルソン、アィネズにレイチェル・マクアダムスのほか、マリオン・コティヤール、仏大統領夫人としても知られるイタリア出身の歌手カーラ・ブルーニら豪華スターが顔をそろえる。第84回アカデミー賞では、アレン自身3度目となる脚本賞を受賞した。", "2012-05-26", "9,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BISHOP’S WIFE", "気まぐれ天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "新しい教会建設の資金集めに奔走し、妻の存在も忘れがちな司教のもとにある日、一人の不思議な男が現れ、出資者をみつけたり、様々な奇跡を起こす。そんな彼に司教の妻は心惹かれ、彼もまた恋に落ちそうになるが……。ほのぼのと心温まる佳作。1996年に「天使の贈り物」としてリメイクされた。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A MIDNIGHT CLEAR", "真夜中の戦場—クリスマスを贈ります—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "第二次世界大戦時、ヨーロッパ最前線に派遣された若者たちの姿を描いたドラマ。悲惨な現実と恐怖を乗り越えて成長していく若者たちを通して、戦争の非人間性を訴えかけた反戦映画でもある。監督のK・ゴードンは「殺しのドレス」「クリスティーン」などに出演している個性派の俳優である。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神々の深き欲望", "神々の深き欲望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 175, "神話が息づく南の島を舞台に、伝統と近代化との相克、日本民族の根源的な生と性、さらには日本の風俗のルーツまでをも描ききった今村昌平の最高傑作。今村作品としては初のカラー映画だが、南島の陽光と自然が最大限に効果をあげている。南方に現代文明からとり残された島があった。島民は原始的な農耕と土俗信仰に生きているが、そんな島にも東京から観光開発の調査のため、都会人が入り込んで来る。島の神事をつかさどる一家は、一方で近親相姦の一族として島民からさげすまれていた。そして兄妹相姦の果てに悲劇が待ち受けて……。原始的な性により、男を虜にする女の性の強烈さを沖山秀子が好演。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・男樹", "新・男樹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "本宮ひろ志の『男樹』シリーズの１編『新・男樹～完結編～』を映画化した任侠アクション。日本最大の暴力団組織、崇和連合の総裁、村田京介の落とし子である高校生、村田京太郎。京介は京太郎を極道の世界に入れさせまいと考えていたが、彼は度胸と人情で人を束ね、ヤクザまがいの荒稼ぎをしていた。父に対する愛情などない京太郎はやがて崇和連合に牙をむいていく。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妻たちの性体験　夫の眼の前で、今…", "妻たちの性体験　夫の眼の前で、今…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "裕福な環境にある夫婦が、お互いに相手を脅迫し強姦し合いながら、性的関係を高めていく姿を描くロマンポルノ。この異常なまでの夫婦の営みは次第にエスカレートしていき、妻は他人とのセックスを夫に見られることを、夫はそれを見せられることを快楽と感じはじめる。さかりのついた学生たちの相手を一挙に引き受けて、なお毅然としたヒロイン、風祭ゆきのたくましさが圧倒的だ。小沼勝監督の代表作の１本。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON'T SAY A WORD", "サウンド・オブ・サイレンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "エドガー賞受賞作家A・クラヴァンの『秘密の友人』を映画化したサスペンス・アクション。感謝祭の朝、精神科医ネイサンの幼い娘ジェシーが誘拐された。犯人グループは電話で、昨日ネイサンが友人の依頼で診察した少女エリザベスから、６桁の番号を午後５時までに聞き出せと命令してくる。要求に従うしかないネイサンは、父の死を目撃したことでエリザベスが心を閉ざしていることを知り……。ネイサンの行動、過去の強盗事件とエリザベスの父の死の真相が明らかになっていく過程がスリリング。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("限りなき舗道", "限りなき舗道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "アパートで共同生活をする二人の女給のめまぐるしい運命の変転を描いている。北村小松の新聞連載小説の映画化。他の監督のための企画であったが、誰も引き受け手がないまま成瀬に押しつけられたもので、これを最後に彼は古巣の松竹を離れて東宝の前身であるP・C・Lへと転じ、終生同社のもとで製作するようになった。渋谷実、山本薩夫が助監督についている。", "1934-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マジ!?", "マジ!?", "", "", "http://image.eiga.k-img.com/images/movie/55244/315x210.jpg?1389", 57, "北関東の地方都市。中卒のボリは、幼馴染のマー君、タケちゃん、オッキと、毎日くだらない遊びをして過ごしている。4人は「トップガン」が大好きで、口癖は「マジ!?」。ボリはある日、女子高生のカンナに告白するがあっさり振られてしまい、復讐としてあるいたずらを思いつき、深夜の女子高に忍び込む。新鋭・高柳元気が詩人俳優らを起用して描く青春群像劇。", "2010-02-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Front Page", "フロント・ページ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　1931年と40年、2度に渡って映画化（それぞれの邦題は「犯罪都市」、「ヒズ・ガール・フライデー」）された有名コメディを、ビリー・ワイルダーが再びリメイク。脚本家、映画監督になる以前、新聞記者をしていたワイルダーらしくマスコミへの風刺がきいている。敏腕新聞記者が、今日限りで辞職すると言い出す。有能な記者を手放したくない編集長は、引き止めるために……。「恋人よ帰れ！わが胸に」に続き、ジャック・レモンとウォルター・マッソーコンビが記者と編集長をテンポよく演じている。", "1975-05-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jagged Edge", "白と黒のナイフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "嵐の夜、富豪の孫娘が惨殺された。その莫大な遺産を手にする夫ジャックに容疑がかかり、彼は敏腕女弁護士テディを雇い裁判に臨む。テディは、ジャックに疑惑を抱きつつも、やがて心惹かれて……。法廷ドラマにラブ・ロマンスの要素を巧みに盛り込んだ心理劇。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CONFESSION", "告白", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "1951年、チェコスロバキアの外務次官ジェラールが何者かに襲われ拉致される。牢獄に入れられた彼を待っていたのは、国家への背任罪の“告白”を迫る検察官の拷問的な取り調べだった……。後の「Z」「戒厳令」とともにコスタ・ガヴラス監督、Y・モンタン主演による“政治３部作”を成す社会派映画。チェコの暗黒時代に急進的スターリン主義者たちが起こした“スランスキー事件”の恐るべき実態に迫り、人間の尊厳を踏みにじる者への怒りを込めた問題作だ。主人公のジェラールとは原作者のA・ロンドンのことで、彼と妻リーズの体験がこの壮絶なドラマのベースとなっている。劇中に登場するチェコのスチル写真を担当したのはクリス・マルケル。", "1970-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El Pollo, El Pez Y El Cangrejo Real", "ファイティング・シェフ　美食オリンピックへの道", "", "", "http://image.eiga.k-img.com/images/movie/54054/315x210.jpg?3263", 87, "世界的に有名なシェフ、ポール・ボキューズが1987年に設立した高級料理のコンクール“ボキューズ・ドール”を追ったドキュメンタリー。フランス・リヨンで24カ国の代表シェフが競う大会で、20年間結果を出せずにきたスペインはヘスス・アルマグロ代表シェフに希望を託す。大会に賭ける一流シェフ達の情熱、芸術ともいえる料理の数々に魅了される。第5回スペイン・ラテンアメリカ映画祭上映作品。", "2007-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドン松五郎の大冒険", "ドン松五郎の大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "人間の言葉を理解し、パソコンを操る天才犬・ドン松五郎とその息子・ジュニアが、子供たちの楽園“子供ランド”を乗っ取ろうとする成金親子に戦いを挑むシリーズ第２作。今回は、名犬・ドン松親子がハングライダー乗りにチャレンジ。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・口笛を吹く寅次郎", "男はつらいよ・口笛を吹く寅次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "さくらの夫・博の亡父の三回忌に備中を訪れた寅さん。寺の住職の娘が出もどりときいてまたまたホの字になる。養子に入ることを目指した寅は仏に仕えると宣言し、帝釈天の御前様に弟子入りするが……。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MYTH OF FINGERPRINTS", "家族という名の他人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "家族の崩壊を描き、サンダンス映画祭で話題となった人間ドラマ。感謝祭の週末、ウォーレン、ミア、ジェイク、リーの４人兄妹が３年ぶりに両親の家に帰って来る。兄妹はそれぞれ悩みを抱え、父ハルは自分の独善的な態度が子供たちとの間の溝を深めたことを感じている。しかし、久しぶりに再会した家族はわだかまりを抱えたまま、やがて時間は過ぎていき……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUARTET", "四重奏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "サマセット・モームの短編小説を映画化したオムニバス作品。「異国の収穫」（R・スマート）「人生に起こること」（H・フレンチ）「凧」（A・クラブリー）「大佐の奥方」（K・アナキン）の４話からなり、イギリス人気質を地味に描き出した佳作。", "1949-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FULL CONTACT", "キック・ファイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "キック・ボクシングの現役世界チャンピオンたちが共演するアクション。ロスの貧民街に兄ジョニーを訪ねたルークは、兄がストリート・ファイターに殺されたことを知る。兄の仇を討つため、ストリート・ファイトに身を投じるルーク。次々と強敵を倒しながら、突き止めた兄の仇は、意外な人物だった……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AFTER THE FOX", "紳士泥棒 大ゴールデン作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "11年間に９回も刑務所暮らしをしながら、その巧みな腕でけっこう豪勢な生活をしているサギ師・アルドー。しかし、刑務所に行っている留守の間に自分の一家の暮らしが傾いていることを知り、金のノベ棒運びという大仕事に着手する。N・サイモンのオリジナル脚本を巨匠デ・シーカが演出したコメディ。", "1966-01-01", "3,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("９−NINE", "９−NINE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "裏世界の賭けビリヤードのスポンサーと契約を結んだトオルと、政治家の親が決めた人生を生きる少女リカ。大人たちに反発し、自由を追い求める彼らの出会いを、若者の街、渋谷を舞台に描く青春映画。ストリート系ファッション・リーダーとして注目された弓削智久を始めとするフレッシュな若手が大挙出演している。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAVID AND BATHSHEBA", "愛欲の十字路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "聖書に基づく古代史劇映画。3000年前のイスラエル。エルサレムの宮殿に住むダビド王は、第一皇后との仲がうまくいかず、他人の妻バスシバに心を奪われる。彼女もまた不幸な結婚生活を送っており、二人は不義の関係を結んでしまう。折しも聖なる神の棺をイスラエルに移すことを計画中。棺が城門に到着するころ、神の怒りが現れ、エルサレムは日照り続きで大飢饉に見舞われる……。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ADVENTURES OF FORD FAIRLANE", "フォード・フェアレーンの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "R・ハーリン監督が手掛けた異色の探偵もの。人気ハード・ロック歌手が謎の急死。そのころ、音楽業界専門の私立探偵F・フェアレーンは、行方不明の娘捜しを頼まれる。この娘の失踪と歌手の死は裏で結びついていた……。型破りな探偵をA・D・クレイが好演。ビデオ発売時タイトルは「フォード・フェアレーン」。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH OF A SOLDIER", "犠牲 ～ある兵士の死～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "第二次大戦中の実話をもとにした、マッカーサーによる政治工作の秘話。オーストラリアに駐留するGIのレオンスキーは酒が入ると人が変わり、３人もの女性を殺してしまった。軍事裁判で彼の弁護を担当したダネンバーグ少佐は彼の中に二重人格を見いだし、無罪を主張するが……。", "1985-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TANGO BAR", "タンゴ・バー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "タンゴを聴かせるライヴ・ハウス“タンゴ・バー”でトリオを組み、青春時代をともに過ごした３人の主人公たち。その一人が10年にわたる亡命生活から帰ってきて再会し、三角関係のドラマが展開される。主人公たちの心情を見事なダンス・シーンで表現したダイナミックな一編。", "1988-01-01", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　灼眼のシャナ", "劇場版　灼眼のシャナ", "", "", "http://image.eiga.k-img.com/images/movie/33944/315x210.jpg?1299", 0, "原作ライトノベルは中高生を中心に絶大な人気を誇り、テレビアニメも大好評の「灼眼のシャナ」。初映画化となる本作では、原作小説の第1巻を基に、主人公の悠二と少女シャナとの出会いや、異世界から来た化け物・紅世の徒との戦いの始まりを描く。高校生の悠二は、人の“存在”を灯りに変えて喰らう化け物・紅世の徒に襲われたところを、紅蓮の髪と眼を持つ謎の少女に救われる。しかし彼は、自分がすでに“存在”していないことを知らされ……。", "2007-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCIUSCIA", "靴みがき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "第二次大戦直後のローマの街頭で靴みがきをしているパスクアーレとジュゼッペの二人の少年。彼らの夢は馬を買うことだった。そのため彼らは占領軍のヤミ物資の横流しをするが……。敗戦後の混乱に生きる人々の姿を真摯に捉えたイタリアン・ネオ・レアリスモの代表作。", "1946-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Manufacturing Consent: Noam Chomsky and the Media", "チョムスキーとメディア　マニュファクチャリング・コンセント", "", "", "http://ecx.images-amazon.com/images/I/512NaRFCAjL._SL210_.jpg", 167, "アメリカの思想家ノーム・チョムスキーが、現代の民主主義国家においてマスメディアが担っている本当の役割を暴いたドキュメンタリー。東ティモールとカンボジアで起きた集団虐殺についてニューヨーク・タイムズ紙がどのように報じたかを比較・検証し、マスメディアが社会の支配層のメッセージを民衆に浸透させていく仕組みを明らかにするほか、オルタナティブ・メディアの重要性についても言及していく。監督は「ザ・コーポレーション」のマーク・アクバー。", "2007-02-17", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE MEXICO ホワイト・メキシコ", "WHITE MEXICO ホワイト・メキシコ", "", "", "http://image.eiga.k-img.com/images/movie/34134/315x210.jpg?2164", 70, "映像（Cinema）と音楽（Music）のコラボレーションを目指す「CineMusica（シネムジカ）」シリーズ第3弾。ミュージシャンの大江千里と人気モデルのティアラが共演するロードムービー。最愛の娘を殺した犯人への復讐を果たし、死に場所を求めてさまよう男・佐藤と、日本人である祖父の複葉機を引き取るためにロシアからやって来た女・パステル。青森行きの長距離バスで出会った2人は、隣り合った座席で取りとめもない会話を交わしていた。すると突然、バスに1人の刑事が乗り込んで来る。慌てたパステルは、佐藤が持っていた拳銃を刑事に突きつけるが……。", "2007-08-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AVN エイリアンVSニンジャ", "AVN エイリアンVSニンジャ", "", "", "http://image.eiga.k-img.com/images/movie/55204/315x210.jpg?3235", 80, "戦国時代、織田信長の軍勢が伊賀の里を攻略していた「天正伊賀の乱」のさなか、宇宙から飛来した未確認飛行物体が伊賀の里に不時着。耶麻汰、陣内、寝隅の3人の使い手は、不時着したナゾの物体から現れた鋭い牙と爪をもつエイリアンと死闘を繰り広げる。「戦国　伊賀の乱」の千葉誠治監督が手がけるSF忍者アクション。アクション監督は「VERSUS」の下村勇二。", "2011-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEST FRIENDS", "結婚しない族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "シナリオ・ライターとして売れっ子の男女が、長すぎた春にうっかり終止符を打って結婚したことから巻き起こる騒動をユーモラスに綴った都会派コメディ。脚本家自身がモデルという説のあったこの作品、最良の友だちと結婚相手の相違を、実例で教授してくれる。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JE SUIS LE SEIGNEUR DU CHA＾TEAU", "罪深き天使たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「悲しみのヴァイオリン」で監督デビューしたR・バルニエの第２作。1954年、ブルターニュの古城。城主の息子トマと、トマの世話係の息子シャルル。ともに10歳の二人は、階級意識とプライドを持ち、つねに反目しあっていた。だが、両者の父と母が愛し合うようになった時、二人は残酷なイタズラを思いついて……。", "1989-01-01", "2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILENT NIGHT", "マグダレーナ 美しき娼婦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "若き神父と娼婦の愛に、“きよしこの夜”の誕生秘話を絡めて描いた作品。1818年、オーストリア。荒廃した小村・オーベンドルフに、青年神父モアが赴任する。彼は、ある日、酒場で知り合ったマグダレーナという娼婦に心惹かれた。だが、モアを嫌う修道院長によって彼はマグダレーナとの姦通罪の汚名を着せられてしまった……。", "1988-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HARVEST", "ハーヴェスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "仕事上のスランプを解消すべく、メキシコのリゾート地に向かった脚本家チャーリー。クラブで妖艶な美女と出会った彼は、その晩、何者かの襲撃を受け気付いた時には腎臓を摘出されていた。その後も彼の身には次々と危険が降りかかり……。臓器密売という衝撃的なテーマを題材にしたサスペンス・スリラー。", "1993-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Squid and The Whale", "イカとクジラ", "", "", "http://image.eiga.k-img.com/images/movie/1704/315x210.jpg?4495", 81, "「ライフ・アクアティック」の脚本家ノア・バームバックが、86年のニューヨーク・ブルックリンを舞台に、ある家族の崩壊を滑稽に描いた自伝的悲喜劇。落ち目のインテリ作家である父親バーナードと「ニューヨーカー」誌で作家デビューを飾ることになっている母親ジョーンの間に生まれた16歳の兄ウォルト、12歳の弟フランクは、ある日両親から離婚することを告げられる。ウォルトは父親に、フランクは母親についていくが、2人とも学校で問題を起こすようになる……。", "2006-12-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMANTES", "アマンテス 愛人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "フランコ政権下のスペインを舞台に繰り広げられる官能的な愛憎劇。恋人トリーニとプラトニックな関係を続ける青年パコに未亡人のルイサが接近。彼女の奔放な性に魅せられたパコは肉欲にのめり込む。これを思い悩んだトリーニは彼に純潔を捧げるが、ルイサが与える刺激的な快楽にかなうはずもなく……。", "1991-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mao's Last Dancer", "小さな村の小さなダンサー", "", "", "http://image.eiga.k-img.com/images/movie/55544/315x210.jpg?3245", 117, "中国出身の名ダンサー、リー・ツンシンの半生を、「ドライビング・ミス・デイジー」のブルース・ベレスフォード監督が映画化し、バーミンガム・ロイヤル・バレエのプリンシパル、ツァオ・チーが主演を務める。1961年、中国・山東省の貧しい村に生まれ育ったリーは、毛沢東夫人・江青の文化政策により、家族から遠く離れた北京で舞踏学校に通うことになる。愛国心をあおる厳しいバレエの特訓になじめず落ちこぼれていたが、やがて新天地アメリカでその類いまれなる才能を開花させていく。", "2010-08-28", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Spread", "愛とセックスとセレブリティ", "", "", "http://image.eiga.k-img.com/images/movie/55314/315x210.jpg?1471", 97, "ロサンゼルスで自由気ままに暮らすジゴロのニッキは、裕福な女性だけを対象に性的関係を結んでは、その見返りとして贅沢な暮らしを謳歌していた。そんなある日、ニッキはダイナーで出会った女性ヘザーに一目ぼれするが、ジゴロである自分の正体を明かすことができずに悩んでいた。主演はアシュトン・カッチャー、共演にアン・ヘッシュ、マルガリータ・レバエバほか。", "2010-02-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIG JAKE", "100万ドルの血斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "西部劇の王者J・ウェインが主演し、長男が製作、次男と末息子が共演したウエスタン。近代化の波が押し寄せるテキサスのとある牧場から、無法者によって身代金目当てに誘拐された少年を、父と兄たちが救出する様を描いている。『ミスター・ロンリー』などのヒットで知られる歌手、B・ヴィントンの映画初出演作。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大奥(秘)物語", "大奥(秘)物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "“グランド・ホテル形式”の大奥もの。六代将軍・徳川家宣の時世、大奥に集う女たちの欲望と嫉妬に満ちた生き方が、次々に綴られていく。当時新鋭の中島貞夫が、要所を押さえた演出で佳作に仕上げ、興行的にもヒットした。同年、同監督による続編が製作された。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Scooby-Doo", "スクービー・ドゥー", "", "", "http://image.eiga.k-img.com/images/movie/1164/315x210.jpg?4466", 87, "不思議な事件に立ち向かう、4人と1匹（シャギー、ベルマ、ダフネ、フレッドとスクービー・ドゥー）の“ミステリー社”。些細なことで仲間割れした彼らは、2年後、テーマパーク「スプーキー・アイランド」で再会するが、そこには大きな謎が隠されていた。30年来の米人気アニメを「25年目のキス」のラジャ・ゴズネル監督がCGIを駆使して実写映画化。「ビーン」のローワン・アトキンソンが謎の人物を怪演する。", "2002-08-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("残侠の盃", "残侠の盃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "任侠道を誇った辰巳組が暴力団化するのを嘆いた元幹部で、今は一匹狼の島村吾郎が粛静に立ち上がる。“犬”シリーズや“黒”シリーズのスマートな主演ぶりで人気を博した田宮二郎のバタ臭いマスクは、任侠映画にはそぐわなかった。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生地獄", "生地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "深夜番組『つんくタウン』の映画企画募集から生まれた“つんくタウンFILMS”第３作。車椅子の青年にふりかかる恐怖と、彼のおぞましい出生の秘密が世紀末の暗い様相を漂わせながら描かれていく。アメリカに10年間映画留学していた藤井秀剛監督の、帰国後第１回作品。ドイツの第14回サイエンスフィクション・ホラー＆スリラー国際映画祭にも出品された意欲作である。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOOK WHO’S TALKING TOO", "ベイビー・トーク２ リトル・ダイナマイツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "赤ちゃんにセリフをしゃべらせて大ヒットした「ベイビー・トーク」の続編。今回は前作のスーパー・ベイビー、アイキーに妹のジュリーも加わって、赤ちゃんたちの悪ノリぶりも倍増。親子４人仲良く暮らしていたはずのマイキーの家族、夫婦ゲンカがもとで父親のジェームズが家を出てしまい、さぁ大変！", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROJECT X", "飛べ、バージル／プロジェクトX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "知能的にきわめて人間に近いといわれるチンパンジー。そんな彼らの特性を利用し、核戦争のために軍事利用するという秘密計画が立てられた。しかしその計画に気付いた飼育係は、自らが担当していたチンパンジー、バージルを逃がしてやろうとするのだか……。人間の役者をくってしまうほどの、チンパンジーの熱演（？）が見ものだ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bullitt", "ブリット", "", "", "http://ecx.images-amazon.com/images/I/31G34XJBHAL._SL210_.jpg", 114, "スティーブ・マックィーン主演による刑事アクションの名作。サンフランシスコ市警の刑事フランク・ブリット（マックィーン）は、野心家の上院議員チャルマースから裁判の重要証言者の護衛を命じられる。だが、証言者ジョニーはマフィアの裏切り者で、ブリットが護衛して間もなく何者かに射殺されてしまう……。監督は本作がハリウッド進出第1作となるイギリス出身のピーター・イェーツ。共演にロバート・ボーン、ジャクリーン・ビセット。サンフランシスコの急な坂道を舞台に繰り広げられるカーチェイスは映画史上屈指のアクションシーンである。", "1968-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Snake Eyes", "スネーク・アイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "　アトランティック・シティの巨大スタジアム。汚職刑事のリックは観衆１万4千人が見守るボクシングの試合会場にいた。彼の友人ケビン中佐は観戦する国防長官の警護にあたっていたが、その最中に長官が何者かに狙撃されるという事件が起こる。リックとケビンは観客を足止めし、捜査を開始。やがてリックは意外な真犯人を見つけ出すことに……。デ・パルマ監督の映像テクニックが光るサスペンス・スリラー。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("正義だ！味方だ！全員集合!!", "正義だ！味方だ！全員集合!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "横浜・伊勢浜町、インチキ広告屋の錨長太郎は、レジャービル建設をめぐって住民を腕ずくで立ち退かせようとする暴力団・大神組に対し、町内新聞を発行して正義を大衆に訴える。久しぶりにシリーズの原点に戻ったハチャメチャ編。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陸軍中野学校・雲一号指令", "陸軍中野学校・雲一号指令", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "神戸を舞台に、大型軍用船爆破事件の真相を探る椎名次郎の活躍を描く。前作は、中野学校生たちの青春群像劇といった作りだったが、本作は人間ドラマよりもストーリー性を重視した娯楽作となっている。敵スパイとの対決という基本型ができあがった。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOOT THE SUN DOWN", "クリストファー・ウォーケンの　アステカの秘宝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "古代アステカに実在したといわれる“黄金の車輪”を求めて、サンタフェから灼熱の砂漠へとやって来た男たちと一人の女。一獲千金を夢見るならず者、メキシコ軍、インディアンなどが入り乱れての争奪戦に、ナイフ投げの名手の脱走兵と紅一点M・キダーとの甘いロマンスが絡む冒険ドラマ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("父", "父", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "木下惠介お得意の家庭コメディ。日暮菊太郎は、県議会議員に立候補したり、女子プロレスの興行師の仕事に手を出してみたりと、40歳を過ぎても夢ばかり追いかけるダメ親父。家族に苦労をかけながらも、憎めない父親役を板東英二が好演している。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE QUICK GUN", "クイック・ガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "誤って大牧場主トムの息子たちを射殺してしまい、旅に出ていたガン・ファイター、クリントが久しぶりに帰郷。途中クリントは昔一緒に仕事をした無法者スパングラーから銀行強盗に一役買えと脅迫されるが、彼はそれを断る。元愛人のヘレンはクリントに町から出ることをすすめたが、スパングラーの計画を知るクリントは町を出ようとはしなかった。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WE DON'T CARE ABOUT MUSIC ANYWAY...", "WE DON'T CARE ABOUT MUSIC ANYWAY...", "", "", "http://image.eiga.k-img.com/images/movie/55864/315x210.jpg?2936", 80, "フランスの新鋭ガスパール・クエンツとセドリック・デュピールが共同でメガホンをとり、日本のノイズミュージックシーンにスポットを当てたドキュメンタリー。山川冬樹、大友良英、「NUMB」、坂本弘道ら、シーンの最前線で活躍するミュージシャンたちによる迫力のパフォーマンス、そしてノイズ音楽について語り合う様子を東京の風景とともに映し出し、ノイズ文化と都市のあり方との関係について考察していく。", "2011-01-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("公式長編記録映画 日本万国博", "公式長編記録映画 日本万国博", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 173, "傑作「東京オリンピック」と並ぶ一大ドキュメンタリー・フィルム。昭和の歴史に残るイベントを、3時間近い時間で撮り上げた大作だ。“人類の進歩と調和”をテーマに、大阪・吹田の千里丘陵で1970年3月から9月まで開催された歴史的イベントの全容を収める。画家の故・岡本太郎がデザインを手掛けた“太陽の塔”や“月の石”ほか見どころ満載。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本悪人伝・地獄の道づれ", "日本悪人伝・地獄の道づれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "沖仲士・黒川は悪徳刑事の小野塚によってアヘン密輸の罪をきせられ、５年も冷や飯を食わされた。出所後怨念の鬼となった黒川はひょんなことに巻き込まれ宿敵と再会するのだが……。人間の心の奥底に横たわる魔性を描いたリベンジもの。さらにノワールの要素も加えた。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIRTUAL OBESESSION", "バーチャル・ウォーズ3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "コンピュータの専門家、メッセンジャー博士は感情をもたず学習能力に長けた人工頭脳の研究に没頭していたが、それは永遠の命を獲得するパーフェクトな人間の創造でもあった。そんな彼は妻がありながら、助手のジュリエットと不倫の関係に陥る。ガンに冒され、余命少ない彼女は最初の実験台となり、自分の脳をダウンロードすることに成功。永遠の命を得るが、博士との恋が叶わぬことを知った彼女は全世界を支配しようと暴走を始める……。P・ジェームスの『ホスト』を映画化したSFサスペンス。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("温泉スッポン芸者", "温泉スッポン芸者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "セックス路線に転じた鈴木則文が1971年の「温泉みみず芸者」に続いて発表した“温泉”もの。死んだ姉のあとをついで温泉芸者となった妹は、その締まりのいい名器から“スッポン芸者”と異名を轟かせ、次々に強い者が挑戦してくる。作家の笹沢左保、ストリッパーの一条さゆりなどの多彩なゲストも見もの。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PLATINUM TRIANGLE", "ミッドナイト・エンジェル 恐怖の連続殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "マネー、パワー、そしてセックスがすべてを支配する虚栄の町で、世界的な金融ブローカーが若い愛人とともに殺される。その後、同じ手口の事件が起こり、調査に当たった刑事コンビは、殺された二人の男がプラチナ相場を巧みに市場操作し、多額の利益を上げていたことを突き止めるが……。夜の社交界を舞台に、快楽を売る女たちと巨悪に挑む刑事たちの活躍を描く都会派のセクシー・サスペンス。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Expendables 2", "エクスペンダブルズ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EXTERMINATOR", "エクスタミネーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "無法都市ニューヨーク。そこは、ベトナムの戦場よりひどかった。チンピラに戦友のマイケルを動けぬ体にされ、ジョンは怒りの炎を燃やす。そして彼は自ら“処刑人（エクスタミネーター）”となり、悪人狩りに立ち上がる……。人間を挽肉機に落とし、ベッドごと燃やす、戦争さながらの“処刑”が壮絶。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JET PILOT", "ジェット・パイロット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "航空王H・ヒューズが陣頭指揮をとって完成させた航空映画。アメリカのジェット戦闘機隊のシャノン大佐は、ソ連からの亡命女性パイロットのアンナと恋に落ち、結婚。やがてアンナがソ連のスパイであることが発覚した。シャノンは逆にアンナを利用して、ソ連領に入り込むのだが……。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　FINAL　CUT", "ファイナル・カット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "弱冠26歳で本作の脚本を書いたレバノン出身の新鋭ナイーム監督による、人間の記憶をテーマにした独創的なサスペンス。ロビン・ウィリアムズがトラウマを抱えた“記憶編集者”に扮し、記憶チップを発売する企業の腐敗を暴こうとする元同僚役のジム・カヴィーゼルとの息詰まる攻防劇を展開する。近未来、人間の全生涯を記憶するチップを編集するアランは、ある死亡した人物のチップから自分の暗い過去に関連する映像を発見する。", "2005-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("寛美の我こそは一等社員", "寛美の我こそは一等社員", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "天下無敵の阿呆役者・寛美が主演したサラリーマン喜劇。白タクの運チャン・岡は車に置き忘れられたカバンが縁で、晴れて日本一産業の社員になる。張り切る彼の前に京子という見知らぬ娘が出現。聞けば、当社に自分を捨てた父がいるというのだ。岡は彼女のためにひと肌脱ぐのだが……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE GODFATHERS", "三人の名付親", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "銀行を襲撃し砂漠に逃れた３人の無法者が途中、幌馬車の中で瀕死の母親から赤ん坊を託される。赤ん坊の名付け親になった３人は、聖書に導かれた贖罪の旅を始めることになる。ウィリアム・ワイラーの名作「砂漠の生霊」をフォードがリメイクした佳作。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ABYSS", "アビス", "", "", "http://ecx.images-amazon.com/images/I/4133D5FM7ZL._SL210_.jpg", 141, "J・キャメロン監督が、ハイスクール時代に記した原案をもとに映画化したSF深海スペクタル大作。深海で座礁した原子力潜水艦の調査と救助を依頼されたクルーが、同行する海軍クルーと反目し合いながらも任務を開始する。その時に彼らが遭遇する未曾有の超自然現象と、人間の持ち得る崇高な精神を格調高く描き出していく。実際に水中で撮影されたリアルな映像と、ファンタスティックなSFXとの融合が実に壮観。1993年には171分の完全版も公開された。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大東亜戦争と国際裁判", "大東亜戦争と国際裁判", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「陸海軍流血史」に続く新東宝の“戦記”ものの一編。太平洋戦争突入時から敗戦、占領軍による戦争責任糾弾を目的とした、東京国際裁判をクライマックスに描く。「明治天皇と日露大戦争」で明治天皇に扮して話題を集めた嵐寛寿郎が、今度は東条英機に扮する。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真昼ノ星空", "真昼ノ星空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "沖縄を舞台に映画を作り続ける中川陽介監督が贈るラブ・ストーリー。孤独を抱えた男女３人の一瞬だけ交差した人生を見つめる。登場人物たちの内面を切々と描き出す中川監督の演出が秀逸だ。殺し屋のリャンソンはひと仕事を終え、沖縄に身を隠す。そこで彼は弁当屋で働く由紀子を見かけ、好意を抱く。一方、プール監視員のサキは、毎日泳ぎにくるリャンソンが気になり始める。", "2006-06-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダーJ", "仮面ライダーJ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 46, "かつて地球に氷河期をもたらし、恐竜を絶滅させた機械獣母艦フォッグマザーの襲来。人類滅亡の危機を目前に、大地と精霊のパワーを持つ正義の超人が現れた……。通算14代目の仮面ライダー“J”が活躍する劇場用オリジナル中編で、今回はライダーが巨大化するという新趣向がある。前作「同・ZO」に続き、「ゼイラム」の雨宮慶太監督を起用。特撮を知り抜いている精鋭と若手スタッフの混合チームが斬新なSFXを展開する。", "1994-04-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無花果の顔", "無花果の顔", "", "", "http://image.eiga.k-img.com/images/movie/33794/315x210.jpg?4774", 94, "「疑惑」「幸せの黄色いハンカチ」から「SAYURI」「太陽」まで、日本のみならず海外にも活躍の場を広げている個性派女優、桃井かおりが以前に書き下ろした短編小説をもとに、映画監督に初挑戦した人間ドラマ。ある風変わりな家族を娘の視点を通して描いている。映画初出演で主演という山田花子に石倉三郎、高橋克美らベテランが脇を固めている。", "2006-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUN", "RUN 標的にされた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "平凡な市民が偶然の事故によりギャングに追われるハメに……、そして協力者はたった一人の美女という、アメリカ映画得意の巻き込まれ型アクション。大学生チャーリーはバイトの最中、車が故障し、ある町に立ち寄った。時間つぶしにカジノへ行って、ひともうけしたはいいが、男たちに絡まれ、拷問の末、その一人を殺してしまう。死んだ男は町を牛耳るボスの息子だった。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KING IS ALIVE", "キング・イズ・アライヴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "アフリカの砂漠のど真ん中で、乗客11人を乗せたバスが故障。廃墟で助けを待つ間、脚本家の中年男ヘンリーの提案で芝居『リア王』を演じることになる。照り付ける太陽。容赦なく襲う飢えと乾き。そんな状況に耐えられなくなった乗客の一人が砂漠へ飛び出すが、助けを呼びにいったはずの男が死んでいるのを発見する。絶体絶命の窮地に陥った彼らの精神が、徐々に剥き出しになっていく様を描くサバイバル・サスペンス。荒々しくもシャープな映像は、登場人物の息遣いが耳元で聞こえてくるような緊迫感を生み出している。カメラは手持ち、BGMはつけない、人為的な照明は禁止、すべてロケ撮影……など、ラース・フォン・トリアーらと提唱した“ドグマ95”の第４作目。", "2000-01-01", "18,14,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAGANINI HORROR", "パガニーニ・ホラー／呪いの旋律", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ジレンマに陥ったNo.1の売れっ子ロック歌手が、勝負を懸けた新曲を発表する。その新曲は、伝説のバイオリニスト、パガニーニの呪われた遺曲だった。ビデオ・クリップの撮影は、そのパガニーニが最期の夜を迎えた館で行われたが、その夜バンドのメンバーたちに想像を絶する恐怖が襲いかかる。クラシックとロックのメロディーをベースにした新旧融合ホラー。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PEYTON PLACE", "青春物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 157, "女流作家G・メタリアスの同名処女小説をM・ロブスン監督が映画化した青春映画。第二次大戦下のアメリカ中流社会を背景に、大人たちに反抗するアリソンと、自分を暴行した義父を誤って殺害してしまったセレナ、二人の少女を中心にヒューマニズムあふれるドラマが繰り広げられていく。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("けんか空手・極真拳", "けんか空手・極真拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "極真空手を生み出した大山倍達の半生を映画化。大山には千葉真一が扮し、“ケンカ空手”の異名をとり、空手界の鬼っ子的存在だった大山が自らの空手を磨ききわめていく姿を、様々なエピソードを交えて描いている。暴れ牛との格闘シーンは迫力十分。続編、「けんか空手・極真無頼拳」も製作された。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RYON KANGNYO", "韓国絶倫秘伝 女郎蜘蛛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "尽きることのない男女の色と欲を描いたコリアン・エロス。自分の欲望を満たしてくれる理想の男性を探し求める、好き者の美女。そこに絶倫の男二人組が現れるが、彼らは財宝探しに目がくらみ、興味はそちらの方へ。一刻も早くパートナーを見つけたい女は、二人の男を対決させようとするが……。", "1993-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陽光燦爛的日子／IN THE HEAT OF THE SUN", "太陽の少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "文化大革命さなかの1970年代、北京。政治闘争に明け暮れる大人たちを尻目に、ワンパク少年のシャオチュンは学校をさぼり遊び回っていた。そんなある日、彼は勝手に忍び込んだ他人のアパートで、写真の中の年上の少女に一目惚れ。その美少女ミーランはすぐに仲間内のアイドルとなるが、シャオチュンの思いは届かず彼女は兄貴分イクーの恋人になってしまう……。「芙蓉鎮」「紅いコーリャン」などで知られる中国の実力派男優チアン・ウェンの監督デビュー作。悪友たちとケンカやイタズラを繰り返す少年の淡い恋を、逆光を巧みに生かした映像美でみずみずしく切り取っている。", "1995-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ATAME！", "アタメ 私をしばって！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "様々なタブーに触れる題材をエキセントリックな手法で映像化し、世界的な映画作家になったスペインの鬼才、ペドロ・アルモドヴァルが描く“純愛映画”。３歳で孤児になり、精神病院に入退院を繰り返した経歴を持つ23歳の青年が、ある日、ポルノ女優への求婚を決意する。彼のとった“純愛”の方法とは？", "1989-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAX ET JEREMIE", "危険な友情　マックス＆ジェレミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "女性作家T・ホワイトの暗黒街小説を女性監督C・ドベールがフィルム・ノワールに仕立てた男同士の友情物語。引退を伝えられる百戦錬磨の老ギャングは、暗殺の特命を受けて彼に近づく若いチンピラを、最後の大仕事のパートナーにする。殺し、殺され、だまし、だまされる関係の二人が、お互いの立場に引き裂かれつつ親愛の情で結ばれていくところが、苦み走ったムードの中にほのかに甘い隠し味となった。半人前で泣き虫だけど憎めないC・ランバートの、肝っ玉の小さなチンピラ・キャラクターも楽しい。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tintin et Moi", "タンタンと私", "", "", "http://image.eiga.k-img.com/images/movie/57234/315x210.jpg?3791", 75, "スティーブン・スピルバーグも映画化したことで知られ、世界中で愛される漫画「タンタンの冒険旅行」の生みの親であるベルギーの漫画家、エルジェが、いかにしてタンタンを生み出していったかに迫ったドキュメンタリー。エルジェ本人が語りすぎたために30年以上公開されていなかったインタビュー音源を中心に、タンタンを描くエルジェの姿やさまざまなニュース映像、エルジェを知る関係者たちのインタビューを収録している。", "2012-02-04", "18,37,2,26,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Babies", "ベイビーズ　いのちのちから", "", "", "http://image.eiga.k-img.com/images/movie/57694/315x210.jpg?4565", 79, "2009年4月にアメリカ、ナミビア、モンゴル、日本でそれぞれ生まれた4人の赤ちゃんの成長を見つめたドキュメンタリー。生まれた時代こそ同じだが環境は全く異なる4人の赤ちゃんが、それぞれ親の愛情を受けながらどのように成長していくのかを、誕生からの1年間に密着して映し出していく。", "2012-05-05", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("決戦の大空へ", "決戦の大空へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "土浦海軍飛行隊予科練習生を時代のホープとして描き、その予科練がいかに素晴らしいかを訴えた戦意昂揚映画。学生の自由な映画鑑賞が禁止された1943年、数少ない国民映画普及会選定映画として子供たちの鑑賞が許された作品でもある。高田稔と原節子が「望楼の決死隊」に続いて、息の合うところを見せた。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OCEAN’S ELEVEN", "オーシャンと11人の仲間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "大晦日の午前零時きっかりにラスベガスのカジノを襲撃しようという大胆な計画をたてるオーシャンという男と、彼のかつての戦友でその計画にのってきた11人の仲間たちの姿を描いたギャング映画。監督のL・マイルストンが、全編をスリルとユーモアたっぷりにまとめあげている。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Love We Make", "ポール・マッカートニー THE LOVE WE MAKE 9.11からコンサート・フォー・ニューヨーク・シティへの軌跡", "", "", "http://image.eiga.k-img.com/images/movie/57454/315x210.jpg?4050", 93, "「ザ・ビートルズ」のポール・マッカートニーが、9・11テロを受けて開催した慈善コンサートイベント「コンサート・フォー・ニューヨーク・シティ」の模様を撮影したドキュメンタリー。2001年9月11日、アメリカ同時多発テロの発生時にニューヨークの空港に居合わせたマッカートニーは、テロから1カ月後、音楽の力で傷ついた人々を癒そうと、自身の提唱でライブイベント「コンサート・フォー・ニューヨーク・シティ」を開いた。その開催までの道のりと当日のライブの模様、リハーサルなどの舞台裏にも密着。ミック・ジャガー、キース・リチャーズ、エリック・クラプトン、ビリー・ジョエル、エルトン・ジョンらイベント出演者とマッカートニーとのやりとりも克明に記録されている。", "2011-12-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ケータイ刑事 THE MOVIE バベルの塔の秘密　銭形姉妹への挑戦状", "ケータイ刑事 THE MOVIE バベルの塔の秘密　銭形姉妹への挑戦状", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "携帯電話とIQ180の頭脳を武器に難事件に挑む少女たちの奮闘を描いた人気テレビドラマを映画化。これまで数々の事件を解決してきた警視総監の孫娘・銭形姉妹に、何者かが挑戦状を叩きつけてきた。銭形家のピンチを切りぬけるため、姉妹は犯人が要求してきた3つの事件の解決に乗りだすが……。キャストには次女・泪役の黒川芽以、三女・舞役の堀北真希、四女・零役の夏帆ら歴代ヒロインが集結。", "2006-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチバンII 最凶決戦", "ガチバンII 最凶決戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "", "2008-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Arthur", "ミスター・アーサー（2011）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "クリストファー・クロスによるAORの名曲「ニューヨークシティ・セレナーデ」がアカデミー歌曲賞、ジョン・ギールグッドがアカデミー助演男優賞を受賞した1981年のコメディドラマ「ミスター・アーサー」をリメイク。オリジナル作でダドリー・ムーアが演じたタイトルロールにラッセル・ブランドが扮し、ヘレン・ミレン、ジェニファー・ガーナー、ルイス・ガスマン、ニック・ノルティら豪華キャストが共演。", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUBURBIA", "反逆のパンク・ロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "70年代末から80年代前半にかけて発生したウエストコーストのUSハードコア・パンクシーン。その壮絶な嵐をドキュメンタリー・タッチで映し出すロックファン必見の一作。B級映画の名プロデューサー、R・コーマンの参加や今をときめくレッド・ホット・チリ・ペッパーズのフリーが映画デビューを飾るなど話題も豊富。ストリート・チルドレンたちと毎夜、パンクロックを叫ぶ主人公。やがて彼らは住民たちとの対立を深めていく。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("徳川家康", "徳川家康", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "群雄割拠の戦国時代を背景に、人質として捕らわれ波乱に満ちた幼少期を過ごした家康が、やがてその逆境から身を起こし、信長と盟約を結んで天下を取るまでの生涯を描く丹念な作りの時代劇。中村錦之助が信長に扮している。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ある殺し屋の鍵", "ある殺し屋の鍵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "表の顔は日本舞踊の師匠、裏は針１本で人を消す殺し屋という“現代版・必殺仕掛人”に市川雷蔵が扮した「ある殺し屋」の続編。今回のターゲットは政財界の秘密を握る脱税王・朝倉。あざやかな殺しのテクニックを、宮川一夫の流麗なカメラワークで捉えた作品。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DARK BACKWARD", "ワンダーアーム・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "突然、背中から“第３の腕”が出てきた売れないコメディアンとその相棒が、大騒動を巻き起こすブラック・コメディ。第３の腕を持ってしまった主人公には、J・ネルソンが扮する。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOWDOWN", "地獄の対決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "メキシコ国境に近い監獄を脱走した凶悪犯の仲間割れを描くウエスタン。クリフとピケットは、凶悪脱獄囚の一団に利用されて、１万2000ドルの株券を換金したうえに、ピケットは殺されてしまう。すきを見て一味から逃げたクリフの逆襲が始まった。クリフに扮するマーフィの早撃ちが見ものの。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("賭神続集／ GOD OF GAMBLERS ２", "ゴッド・ギャンブラー 完結編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "チョウ・ユンファ扮する“賭神”の異名を取る伝説の天才ギャンブラーが再び登場する“ゴッド・ギャンブラー”シリーズの完結編。今は引退し、妻とともにパリで静かに暮らしていたコウ・チャンが、その座を狙う邪悪な挑戦者の罠によって妻と友を奪われ、さらには殺人者として追われることに……。アクションとギャンブルの息もつかせぬバトルが炸裂。シリーズ第１作を上回るハードボイルド・アクション喜劇の傑作となった。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AU　REVOIR，UFO", "オー！マイDJ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「ブラザーフッド」のイ・ウンジュと個性派イ・ボムスが共演したラブ・ストーリー。視覚障害を持つ不思議な女性に恋をしたバスの運転手が、彼女の気を引こうとあらぬウソをつき、それを隠すため二重生活を始めるが……。", "2004-11-27", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ГОЛУОНЕ ГОРЫ ИЛИ НЕПР АВДОПООНАЯ ИСТОРИЯ", "青い山 本当らしくない本当の話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "自分の本を出版してもらおうと、役所を訪ねる駆け出し作家ソソ。だが、役所の職員たちは仕事もせずに各自がやりたい放題。結局、小説は誰に読まれることもなく、数部あったコピーも紛失してしまう有り様。おまけに役所の建物が崩壊するなど、物語は混乱を極め、異常ともいえる幻想性を帯びていく……。旧ソ連グルジア共和国のE・シェンゲラーヤが官僚主義を皮肉った痛烈な風刺コメディ。", "1984-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("科学者として・笑顔と告発", "科学者として・笑顔と告発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "1992年に品川区から新宿区戸山に移転した国立感染症研究所（旧・国立予防衛生研究所）建設をめぐって、同所員という立場でありながら住宅地への危険性から反対を続けた科学者、新井秀雄の姿をドキュメント。血気盛んな運動家ではなく、一個人の信念から自然体で淡々と闘い続ける様が共感を呼ぶ。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Harem", "ハーレム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ニューヨークに住む普通の女性が、モロッコの豪族に囲われ、何度も脱出しようとしながらも、いつしかその主に惹かれていく姿を描く倒錯的なラブ・ストーリー。マラケッシュのバヤ宮殿のロケによるハーレムと、ヌードも辞さないN・キンスキーの美しさが、大いに見どころだ。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ARCIERE DI FUOCO", "炎の戦士 ロビン・フッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "マカロニ・ウエスタンのヒーロー、G・ジェンマが、中世イギリスの伝説的英雄ロビン・フッドに扮したアクション・スペクタクル。シャーウッドの森を根城に、豪傑たちを引き連れて、王座を狙う貴族や悪党を蹴散らしていく華麗な活劇シーンが見もの。", "1970-01-01", "4,2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一屋兩妻／THE HAPPY BIGAMIST", "香港アーバン・ラブ・コメディ 夫婦前妻！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "料理は上手だが、自立心が旺盛で気が強い前妻と別れ、ソンは新しい恋人と新婚生活に入った。今度の妻は料理はまったくいただけないが、どことなく頼りなく可愛いタイプ。そこに別れた妻が転がり込み、新婚ムードを台無しにされる。頭にくるソン。しかし彼女に恋人ができた途端、今度は嫉妬に燃えて……。コメディの名手A・チェンが脂の乗った自作自演ぶりを見せる。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLACK BELLY OF THE TARANTULA", "タランチュラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ヨーロッパ映画界のグラマー女優たちが恐怖の中にその美しさを競うイタリア製ミステリー・サスペンス。美容室の常連客マリアが謎の惨殺体となって発見されて以来、猟奇的な連続殺人事件が発生する。陰湿で残虐な魔の手は担当警部の美人妻にまで及び、恐るべき犯人の正体が暴かれる。", "1972-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("我が子の碑／　人形たちと生きた６０年", "我が子の碑／　人形たちと生きた６０年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "第ニ次世界大戦末期の長崎の原爆で子供を失った蔵満さん夫婦を追ったドキュメント。我が子の面影を求め、人形を集め続けた親の心の痛みと癒やしをテーマに、夫婦が少しずつ生きる力を取り戻す姿を描く。残された人々が戦後をどう生きてきたか、明かされることのなかった胸の内に迫る。1945年８月９日、長崎の原爆投下で、蔵満さん夫婦は４人の子供を失った。以来夫婦は、子供たちの面影を求め、市松人形を集め続けた。", "2005-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JESSICA", "すてきなジェシカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "F・サンダストームの『ポント・クラリーの助産婦』を原作としたコメディ。素朴で平和な日々を送るシシリー島の村に、美しいアメリカ人看護婦が来たために、男どもはウツツを抜かし、女どもは嫉妬でカッカとなって大騒動となる。果たして、再び村に平和がよみがえるのであろうか？", "1962-01-01", "1,4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("公式長編記録映画　沖縄海洋博", "公式長編記録映画　沖縄海洋博", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 151, "'75年7月20日から'76年1月18日まで開催された、世界初の海洋博『沖縄国際海洋博覧会』の模様を撮影したドキュメンタリー。2時間30分余りの尺の中に、博覧会の見どころを凝縮。テンポ感あふれる編集で、時間の長さを感じさせない。沖縄テイストはもちろん、“昭和”の熱気が充満した記録映像としても楽しめる。エメラルド・グリーンのサンゴ礁の冒頭シーンからアクアポリスの世界へ。主題歌を森山良子が担当している。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Interview with the Vampire: The Vampire Chronicles", "インタビュー・ウィズ・ヴァンパイア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "サンフランシスコのある部屋でインタビューに答える青年ルイ。彼は自らの半生を語り始めるが、それは驚くべき内容のものだった。舞台は18世紀末のニューオリンズ。ルイは氷のような美貌を持つヴァンパイア、レスタトと出会う。ルイの繊細な魂に魅せられたレスタトは、永遠の命をともに生きる相手として彼を選んだ。他人の命を奪って生きることに悲しみを覚えるルイに、レスタトは美しい少女を与える。彼女はやがて大人の心を持つ女に成長するが、体は少女のままでいることに深く絶望する。怒りの矛先はレスタトに向けられ、彼女は彼を殺そうとするが……。N・ジョーダンが格調高い映像で、A・ライスの人気小説に肉薄。当初はT・クルーズのキャスティングに反対していたライスも大喜びする出来となった。「エイリアン」「ターミネーター２」でオスカーを手にしたスタン・ウィンストンが特殊メイクを担当。", "1994-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Role Models", "ぼくたちの奉仕活動", "", "", "http://ecx.images-amazon.com/images/I/51Izo3sSldL._SL210_.jpg", 99, "社会奉仕活動として問題児の相手をするハメになったダメ男2人組の奮闘を描き、アメリカでスマッシュヒットしたコメディ。いつも何かに不満を抱いているダニーと能天気なホイールは、栄養ドリンクの営業で学校を回る日々。そんなある日、恋人にふられて自暴自棄になったダニーが、会社の車で事故を起こしてしまう。刑務所行きの代わりとして150時間の社会奉仕活動を命じられた2人は、ある問題児の世話を任され……。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トワノクオン　第四章　紅蓮の焦心", "トワノクオン　第四章　紅蓮の焦心", "", "", "http://image.eiga.k-img.com/images/movie/56824/315x210.jpg?3530", 45, "「機動戦士ガンダム　第08MS小隊」などで知られ、2010年に他界した飯田馬之介監督が生前に制作を進めていた全6話のオリジナルアニメーションを映画館で上映する第4話。クオンを中心に集まった能力者たちの集団「アトラクター」が拠点としているファンタジアム・ガーデンの存在が、能力者たちを追跡する「クーストース」に察知されてしまう。クオンは仲間を守るため、迫りくるクーストースのサイボーグ部隊（WTOC）と対峙するが、その時、WTOCの一員、イプシロンに突如異変が生じる。", "2011-09-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Superman Returns", "スーパーマン・リターンズ", "", "", "http://image.eiga.k-img.com/images/movie/1184/315x210.jpg?3152", 154, "87年の「スーパーマン4／最強の敵」以来約20年ぶりとなるシリーズ最新作だが、本作は前シリーズ2作目の「スーパーマンII／冒険篇」の続編。クリプトン星の3悪人を倒し、忽然と姿を消したスーパーマンが再び地球に戻ってきた。だが、宿敵レックス・ルーサーは刑務所から出所し、かつての恋人ロイス・レインは他の男と暮らしていた……。監督は「X-MEN3」を蹴って本作に臨んだブライアン・シンガー。", "2006-08-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　VIKING　QUEEN", "虐殺の女王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ローマ軍が侵攻してきた紀元前のイギリス。国王プリウスが死に際に王位を譲った王女サリナは、美しくも勇敢な女だった。和平策を選ぶことにしたサリナはローマ軍の司令官ジャスティニアンと愛し合うが、それを快く思わない副司令官の陰謀に巻き込まれていく。クライマックスには、ヴァイキングの女王の血を引くヒロインの決死の反乱劇が展開するスペクタクル映画。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GENDARME EN BALADE", "ルイ・ド・フュネスの窓際一発大逆転", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "サントロペを舞台にしたドタバタ・コメディの人気シリーズ第４作で、窓際警官３人が爆弾ロケットをめぐって大活躍する。強い者にはペコペコし、弱い者にはいばり散らす巡査長をフュネスが皮肉いっぱいに演じ、全編に毒の効いた笑いが展開する。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Give me a Shake レディースMAX", "Give me a Shake レディースMAX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ダンス・ミュージックで大ヒットしたMAXの４人が「麗霆”子MAX」に続いて、レディース＝女暴走族に扮した青春アクション。それぞれに悩みを抱えた４人が出会い、お互いを巻き込み、傷つけ合いながらも大人へと旅立っていく姿を描く。多岐川裕美が彼女たちの集まるライブハウスの経営者として出演している。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ショムニ", "ショムニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "安田弘之の人気コミックを、高視聴率をたたき出したTVドラマとは違うニュー・キャストで映画化。満帆商事に勤務するOL、塚原佐知子はヘルパーとして会社のお荷物である庶務二課、通称ショムニに派遣される。そこは男勝りの坪井千夏をはじめ、個性的なOLたちのたまり場だった。ショックを受ける佐知子。だが、退社後に千夏と行動することになった佐知子はショムニの連中のユニークなプライベートを知っていく……。会社内の騒動を描いたTV版とは違い、一夜の中で佐知子が成長していく姿を捉えたワンナイト・ストーリーとしたところに監督、渡辺孝好の意欲が感じられる。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蝦夷館の決闘", "蝦夷館の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "群像劇仕様のアクション時代もの。徳川幕府の崩壊寸前。公儀御用達の漕船問屋・越後屋の命を受け、蝦夷館の当主に拉致されているロシアの伯爵令嬢・ワルサを救出しようとする江戸三界を食いつめた８人の男たち。彼らとロシア軍、幕府軍が絡んで凄絶な戦いが始まる。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("借王３〈シャッキング〉", "借王３〈シャッキング〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "莫大な借金を返済するため、暴利をむさぼる悪党を罠にかけて大金を狙う、３人の男女の奮闘を描いた痛快活劇シリーズの第３作。今回のターゲットは、産業廃棄物処理場建設にかかわる悪徳総会屋と大手ゼネコン。逆らう相手は殺してでも口を塞ぐ、非道なやり口の巨悪を相手に、３人組が決死の詐欺計画を実行する。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DJANGO", "続・荒野の用心棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "メキシコ国境の小さな村へ、棺桶を引きずって現れた、ご存じF・ネロ扮する一匹狼ジャンゴ。彼は村の無法グループの対立に巻き込まれてしまうが……。切り札として彼が棺桶から取り出したのは一挺の機関銃。ラストまで目が離せないスリリングな展開で楽しませてくれる作品。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みな殺しの霊歌", "みな殺しの霊歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "東映の時代劇や、任侠映画ばかりを手掛けてきた加藤泰が、初めて松竹に招かれて撮った、彼には珍しい現代劇。ある男が、５人の有閑夫人を次々と惨殺していく。男がなぜ何の因果関係もない女たちを殺していかなければならないのか。その背後には５人によって犯されて自殺した少年の姿が浮かんでくるのだが……。むごい殺しを繰り返す主人公の残忍さが反転すると、心根の優しさが浮かび上がるという演出があざやかで、殺人者を演ずる佐藤允の執念の表情が観るものに迫る。殺される側の女性、菅井きん・中原早苗・沢淑子・応蘭芳・川村有紀の顔合わせも楽しく、無垢な少女・倍賞千恵子の初々しさも光る。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lost Weekend", "失われた週末", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "　アカデミー賞4部門、カンヌ映画祭グランプリ受賞など各国で賞賛を集めたシリアスドラマ。ニューヨークに住む売れない作家のドンは、アルコールに溺れる日々を過ごしていた。酒代に困っては兄の金に手をつけ、果ては商売道具のタイプライターまで売ろうとする。絶望の淵をさまようドンをレイ・ミランドが好演し、オスカー男優の仲間入りを果たす。監督のワイルダーは、この作品で2番目の妻となるオードリー・ヤングと知り合った。", "1947-12-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道戦争 武闘派", "極道戦争 武闘派", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "壱岐島から北九州の都会に出て来た二人の若者を中心に、対立する二つのヤクザ組織の抗争下で生きる“武闘派”と呼ばれる若者群像を描き出していく実録ヤクザ映画。幼なじみでありながらも対照的な性格の若き組織戦闘員を「激動の1750日」に続いてヤクザ映画に挑む中井貴一と映画初出演の松山千春が演じ、迫真に迫る演技を見せる。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VIEILLE QUI MARCHAIT DANS LA MER", "海を渡るジャンヌ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "若い男に憧れる年老いたアバズレ女のレディM、それを嫉妬する長年連れ添ったサギ師ポンピリウス、そしてレディMに拾われた女たらしの青年ランベール。この奇妙な取り合わせによる３人のサギ師トリオの姿を軽妙洒脱に描く、エスプリの効いた大人のためのファンタジー・コメディ。レディMを演じるJ・モロー(1992年セザール賞主演女優賞受賞)の貫禄の演技が、とにもかくにも注目される。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Domestic Violence", "DV ドメスティック・バイオレンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 195, "", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("レイジング★ユーバリ", "レイジング★ユーバリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ゆうばり国際ファンタスティック映画祭がインディーズ映画をサポートした第１作。自主映画界で娯楽色の強い作品を撮り続け、「アフタヌーン・ショウ」で劇場デビューも飾った前島誠二郎が、凍てつく夕張の町を舞台にクレイジーなアクションを展開。悪徳サラ金業者に父を奪われ、サックス奏者の未来も消されてしまった香織は恨みを晴らす日を待ちながら夕張でラーメン屋台を引く人生を歩み始める。そんな香織に運命の日がやって来た……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOVIE MOVIE", "ブルックリン物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ブルックリンを舞台に、二つの独立した物語が綴られる人間ドラマ。まずは妹の手術代のためにボクシングをする青年の話。次はミュージカルのバック・ステージものという、往年のハリウッド映画を彷彿させるレトロ仕立てになっている。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beverly Hills Cop II", "ビバリーヒルズ・コップ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "あのアクセル・フォーリー刑事が帰ってきた！　口八丁手八丁の切れ者刑事が活躍するアクション・コメディ第2弾。アルマーニのスーツに身を包み、フェラーリを乗り回して捜査をするデトロイト警察のアクセルに急報が。かつて世話になったビバリーヒルズ警察のボゴミル刑事部長が重傷を負ったというのだ。ローズウッドとタガート両刑事の依頼を受け、アクセルは現地へ。武装強盗団の事件を手がかりに捜査を進めるアクセルたちはやがて武器密売組織の存在を知ることに……。", "1987-07-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Law Abiding Citizen", "完全なる報復", "", "", "http://image.eiga.k-img.com/images/movie/55024/315x210.jpg?2276", 108, "ジェラルド・バトラーとジェイミー・フォックスが共演するクライム・サスペンス。「交渉人」のF・ゲイリー・グレイ監督がメガホンをとり、「ソルト」のカート・ウィマーが脚本を手がける。米フィラデルフィアで幸せな毎日をおくっていたクライド（バトラー）は、ある日突然、愛する妻子を目の前で惨殺される。しかし犯人は、エリート検事ニック（フォックス）の勝手な司法取引によって極刑を免れる。腐敗した司法制度に憤がいしたクライドは、自らの手で犯人と事件関係者に制裁をくだしていく。", "2011-01-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハラスのいた日々", "ハラスのいた日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "深い愛情で結ばれることになる人間と犬の交流を長きにわたって描き出したハートウォーミングな物語。初めはうとましく思っていた１匹の犬・ハラスが、子供のいない夫婦にとってかけがえのない存在になっていく過程が綴られている。原作は、新田次郎文学賞を受賞した同名のノンフィクション。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("セキ★ララ", "セキ★ララ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "在日韓国人という自身のルーツを探る「あんにょんキムチ」でデビューし、AV監督としても活躍する松江哲明が手がけた、ルーツ探訪＆AVという異色ドキュメンタリー。在日AV女優＆男優との撮影ツアーを通じて、在日の心情に迫っていく。日本に生まれた韓国籍のAV女優・相川ひろみと、中国人留学生でAVに出演を決めた杏奈。そして在日系のAV男優、花岡じった。彼らとの撮影を通じて、浮かび上がってくる在日の人生を見つめていく。", "2006-06-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふみ子の海", "ふみ子の海", "", "", "http://image.eiga.k-img.com/images/movie/34244/315x210.jpg?1609", 105, "視覚障害者教育に人生を捧げた実在の女性・粟津キヨをモデルにした同名小説を基に、彼女の少女時代を綴った伝記ドラマ。昭和初期の新潟。幼くして視力を失った少女ふみ子は、盲学校の教師・高野りんとの出会いから点字の存在を知る。しかし貧しさ故に盲学校へ進学するという希望は叶わず、倒れた母を助けるために按摩への道を歩み始める。そんなある日、ふみ子は偶然にもりんと再会を果たして点字を教えてもらうことになり……。", "2007-10-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アイ・コンタクト", "アイ・コンタクト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "2009年夏、台北で開催された第21回デフリンピック（聴覚障害者のためのオリンピック）に初出場を果たした、ろう者サッカー女子日本代表を追ったドキュメンタリー。代表メンバーは高校生や大学生、30代など年齢も職業もさまざまだが、全国各地から集まり、練習を積み重ねて思いのたけを込めて試合に挑んだ。初めて世界に挑戦し成長していく様子を追いながら、選手たちの等身大の姿も映し出す。", "2010-09-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRAGON：THE BRUCE LEE STORY", "ドラゴン ブルース・リー物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "死後20年を経て製作された、不世出のアクション・スター、ブルース・リーの人間像に迫る伝記映画。香港からアメリカへと渡り、人種差別と闘いながら武道家として活躍する彼のエネルギッシュな人生を、妻リンダとの愛の日々を中心に描いていく。ダイナミックなアクション・シーンの連続も楽しめる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLYING TIGERS", "フライング・タイガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "西部劇スター、J・ウェインが空軍の隊長に扮した戦争アクション作品。第二次大戦下の中国が舞台。J・ウェインが率いる戦闘機隊フライング・タイガーが、日本軍を迎え撃ち、壮烈な空中戦を展開する。全編にあふれる戦闘シーンは迫力満点で、J・ウェインはここでも、彼らしい強さを見せている。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戸田家の兄妹", "戸田家の兄妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "父親を失ったブルジョワ一家で、残された妻と未婚の末娘が、結婚している息子や娘たちの家庭に次々に世話になるが、どこでも厄介者扱いされ、仕方なく二人は古ぼけた別荘に住むことになる。だが、亡父の一周忌に親類一同が集まった席上で、満州から帰国した末の弟が、兄や姉たちの母と末娘に対する仕打ちを知り、憤って激しく罵倒する。そして母と妹を満州に連れていくことにする……。戦時下の作品であり、その物語は当時の情勢に迎合した感じもあるが、端的な演出で高い評価を得た。また、小津と名コンビを組むことになる厚田雄春（当時は厚田雄治）が初めて正カメラマンとしてついた作品でもある。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Loup", "WOLF 狼", "", "", "http://image.eiga.k-img.com/images/movie/55014/315x210.jpg?3797", 105, "「狩人と犬、最後の旅」で高い評価を受けたフランスのニコラ・バニエ監督が、シベリアの大自然を舞台に撮りあげた若き遊牧民とオオカミを描いたドラマ。極寒の地シベリアで暮らす遊牧民エベンク族の少年セルゲイ。彼の一族にとって、オオカミは大切なトナカイを狙う憎き存在でしかなかった。ところがある日、傷ついた母オオカミとその子供たちを見つけた彼は、その人間と変わらない親子愛にすっかり心を動かされてしまう。", "2009-10-19", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Apartment", "アパートメント", "", "", "http://image.eiga.k-img.com/images/movie/1904/315x210.jpg?3725", 91, "", "2007-05-19", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ROCKY HORROR PICTURE SHOW", "ロッキー・ホラー・ショー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "1973年にロンドンで初演されヒットしたR・オブライエン原作、作詞、作曲のロック・ミュージカルの映画化。教会で結婚式を挙げたカップルの友人と、ジャネットは、その場で自分たちも婚約し、恩師に報告しようと車で郊外へ向かう。途中雷雨で道に迷った二人は、近くの屋敷へ電話を借りに行った。二人を迎えたのは恐ろしい形相のせむし男で、城内はトランシルベスタイトの面々を交えての盛大なパーティーの最中だった。この城から一刻も早く撤退しようとした二人は、黒マントの怪人でこの城の主フランクフルターに押しとどめられてしまう……。冒頭の歌『SF怪奇映画二本立』をはじめ全編に有名なSF映画・怪奇映画のパロディーが散りばめられ、SFファンのノスタルジックな感覚をおおいに刺激する。主な曲は『タイム・ワープ』『好きだよ！ジャネット』『汚れなくワイルドに』など。1981年には続編「タッチ・タッチャ・タッチ・ミー」も作られた。", "1975-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道〈やくざ〉渡世の素敵な面々", "極道〈やくざ〉渡世の素敵な面々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "脱サラ後に借金を抱えてヤクザに追われるはめに陥るが、別の組の幹部にかくまわれたことから逆にヤクザに志願していく元銀行マンの男を中心に、彼を取り巻く人々の様々なエピソードを展開させたニューウェーブ・ヤクザ映画。安部譲二原作の３本目の映画化で、陣内孝則が好演。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("何日君再来／AU　REVOIR，MON　AMOUR", "さらば英雄／愛と銃撃の彼方に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "アン・ホイ監督の「望郷」などで美術を担当していたトニー・オウの監督デビュー作となった大河メロドラマ。1930～40年代、激動の上海を舞台にして、歴史の流れに揺さぶられる男女の姿を描く。1941年、大日本帝国軍が上海に進出する中、抗日運動に身を投じるシェンは、北京大学時代の恋人メイと再会するが言葉も交わさず別れる。シェンを海外に逃亡させようとするメイは、自分を愛する日本軍将校、野口に頼み、彼と再び会う。そしてメイは情を交わし、妊娠するのだが、シェンとは日中戦争の中で離れ離れになってしまう。そんなとき、落胆するメイを支えたのは野口だった……。「さよなら、わが愛」というタイトルで上映されたこともある。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("3Ninjas", "クロオビキッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　「クール・ランニング」のジョン・タートルトーブ監督が手掛けたキッズ・ムービー。FBI捜査官を父に持つ3兄弟は、日本人の祖父から忍術を学んでいる。ある日、父が追う武器売人の手下の忍者に子どもたちが連れ去られてしまう。祖父役を演じるビクター・ウォンはアメリカ生まれで、「ラストエンペエラー」にも出演。劇場未公開だが、アメリカではその後1998年までに3本のシリーズがリリースされている。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOUTH CENTRAL", "サウス・セントラル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ロス暴動の発火点となった犯罪多発地区サウス・セントラルを舞台に、スラム社会の現状をリアルに描いた問題作。殺人罪で服役し、獄中で学識を深めてギャングから足を洗うことを決意したボビー。出所した彼は、服役中にギャングの手先となってしまった息子のジミーを更生させるべく、必死の行動をとる。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Little Children", "リトル・チルドレン", "", "", "http://image.eiga.k-img.com/images/movie/34084/315x210.jpg?1417", 137, "「イン・ザ・ベッドルーム」のトッド・フィールド監督が、トム・ペロッタのベストセラー小説を映画化した人間ドラマ。郊外の住宅街を舞台に、何ひとつ不自由ない暮らしを送りながらも別の人生を夢見る“大人になれない大人たち”の日常をシニカルに綴る。「タイタニック」のケイト・ウィンスレットが不倫に溺れる主婦を体当たりで演じる。共演はパトリック・ウィルソン、ジェニファー・コネリー、ジャッキー・アール・ヘイリーほか。", "2007-07-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NON　TI　MUOVERE", "赤いアモーレ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "イタリアでベストセラーとなった小説を映画化した恋愛ドラマ。裕福な家庭を持つ医師と、貧しくも情熱的な女性の恋をドラマチックに描く。イタリア語をマスターし、無償の愛を体現したヒロイン、ペネロペ・クルスの熱演は見もの。危篤の娘の前で、外科医が回想する思い出。15年前、裕福な家庭になじめなかった彼は、偶然出会った女性との恋に安らぎを覚えるが……。", "2005-01-08", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("非・バランス", "非・バランス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "講談社児童文学新人賞を得た魚住直子の同名小説を映画化した青春ドラマ。女子校に通う中学２年生チアキは、かつてイジメに遭って以来、友だちを作らずクールに生きていくという誓いを実践中。ところが風変わりなオカマの菊ちゃんと出会ったことから、少しずつ前向きな未来に目を向けるようになり……。オーディションで抜擢された派谷(はたちや)恵美が映画初主演を務め、名バイプレーヤーの小日向文世がワケありの自由人、菊ちゃんをユーモラスに演じている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SACRED DANCES", "神聖舞踏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "ボブ・ディラン、キース・ジャレット、ロバート・フリップらミュージシャンたちに大きな影響を与えた神秘思想家グルジェフが、宇宙体系の認識の手段として提唱した聖なる舞踏の訓練風景とデモンストレーションを紹介したドキュンメンタリー。“自己覚醒のパフォーマンス”と呼ばれる厳粛な舞踏の集大成。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Running on Empty", "旅立ちの時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "　17歳の少年ダニーは両親とともに逃亡生活を送っていた。反戦活動家だった両親はベトナム戦争当時に事件を起こし、そのかどで指名手配されていたのだ。そんなダニーはニュージャージーの小さな町で音楽教師に才能を認められ、その娘ローナと心を通わせていく。しかしかつての同志が警察に逮捕されたことから、ダニーの一家は窮地に追い込まれる……。大人へと成長していく少年をリバー・フェニックスが好演した感動の青春映画。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風音〈FUON〉", "風音〈FUON〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "東陽一監督が、デビュー作以来久々に縁の深い沖縄を舞台に描き出した、奥深く感動的なドラマ。芥川賞作家・目取真俊の同名小説を、原作者自らが脚色。銃弾の貫通した穴から音が鳴るという頭蓋骨を巡り、それを守り神と信じる島の人々の人間模様が描かれていく。ジプシー音楽やバッハを取り入れた音楽構成も斬新な感触を与えている。夏の沖縄、浜辺の崖の中腹にある風葬場には頭蓋骨が置かれている。銃弾が貫通した穴を通り抜ける風の音が不思議な音色を響かせ、人々は島の守り神として敬っていた。やがて、その頭蓋骨にまつわる秘密が明らかになっていく。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドリーム・スタジアム", "ドリーム・スタジアム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "落ちこぼれサラリーマン、田沼洋介のモットーはラクして楽しい生活を送ること。ある日、バッティング・センターに立ち寄った彼はいとも簡単に 150キロの速球を打ち返してしまう。突然宿った自分の能力に驚く洋介。それを見ていたセンターのオーナー、西山朋江は彼を知り合いである王監督に紹介。洋介は“福岡ダイエーホークス”でプロ野球デビューを飾り、バッターとして華々しい活躍を繰り広げる。ところが、シーズン半ば突然スランプに陥ってしまう。やがて解き明かされていく謎。20数年前、朋江の兄、京太郎はプロへの入団が決まった直後、交通事故で他界しており、洋介の能力は彼の体を借りた京太郎のものであることが判明して……。野球映画にヒューマンな味付けを施した夢物語。京太郎の夢をかなえるシーンでは、福岡ドームで行われる試合に現役のダイエーホークスの選手や、金田正一ら名球会のメンバーが参加している。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("森山大道 in Paris カルティエファウンデーションにて", "森山大道 in Paris カルティエファウンデーションにて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "日本を代表するカリスマ写真家・森山大道を追ったドキュメンタリー。2003年10月から翌年1月までパリのカルティエ現代美術館にて開催された個展「DAIDO MORIYAMA」の制作開始から完成までの過程に密着。作品制作の様子や森山本人へのロングインタビューをはじめ、細江英公、荒木経惟、ウィリアム・クラインといった有名写真家たちのコメント、これまでの代表作の紹介などを通してその魅力に迫る。", "2005-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GANTZ PERFECT ANSWER", "GANTZ PERFECT ANSWER", "", "", "http://image.eiga.k-img.com/images/movie/54944/315x210.jpg?4480", 141, "奥浩哉の人気コミックを、二宮和也、松山ケンイチ主演で実写映画化した2部作の完結編。黒いナゾの球体「GANTZ（ガンツ）」に召還され、異形の敵“星人”を倒すことを強いられる玄野と加藤の戦いは続いていた。戦い続け、生き抜く決意をした玄野と、暴力に支配された世界を嫌悪し、戦いを否定する加藤は対立するが、勝ち続けなければ脱出できないという理不尽な世界に対し、2人はある決断を下す。", "2011-04-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAR TREK IV：THE VOYAGE HOME", "スター・トレック4 故郷への長い道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "謎の飛行物体の影響で、破滅の危機に瀕した23世紀の地球。事態解決の鍵が絶滅したクジラにあると知り、カーク提督ら一行はクジラを求めて現代のアメリカへと向かう。前３作とは趣を一変、生命の尊さをテーマにした心温まるタイム・トラベルSFアドベンチャー。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RAGE CARRIE2", "キャリー2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ブライアン・デ・パルマ監督の前作から、20年以上を経て制作された続編。親友の自殺にショックを受けた孤独な高校生レイチェルが、級友の心ないイジメによって怒りの念動力を爆発させるという内容。二番煎じの感は否めないものの、前作で主人公キャリーの同級生を演じていたA・アーヴィングをカウンセラー役で登場させるなど、デ・パルマ版との関連性を打ち出している。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鞍馬天狗・鞍馬の火祭", "鞍馬天狗・鞍馬の火祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "京の街に鬼女の面をかぶった偽天狗が出没し、徳川幕府打倒の方針書を奪い去った。やがて、京へ舞い戻った鞍馬天狗は偽天狗と対決する。美空ひばりが杉作を演じ、デビュー間もない岸恵子が初々しい。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RED SCORPION", "レッド・スコルピオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ソ連陸軍の特殊部隊に所属するニコライは最強の兵士として育てられ、反政府勢力指導者の暗殺を命じられた。使命を遂行するため敵の陣地に乗り込むが、そこには人間味あふれる指導者や住民たちがいて、彼らとの交流を通して忘れかけていた“心”を取り戻すことになる。結果、ソ連軍を敵にまわし戦いが始まる。「ロッキー４　炎の友情」で人気を博した肉体派スター、ラングレン主演のバトル・アクション。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEED OF CHUCKY", "チャイルド・プレイ／チャッキーの種", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "殺人人形チャッキーが妻ティファニーと共に凶行に走る、人気ホラー・シリーズの第5弾。今回は彼らの子供も登場し、家庭内の珍騒動がブラック・ユーモアたっぷりに描かれる。惨殺のバイオレンスの魅力は衰え知らず、殺され役に扮した奇才監督ジョン・ウォーターズの妙演にも注目を。チャッキー夫婦の前に生き別れた子供が出現。チャッキーは、自分たちと同様に彼に殺人の道を歩ませたいが、子供はそれに逆らい……。", "2005-08-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AN OFFICER AND A GENTLEMAN", "愛と青春の旅だち", "", "", "http://ecx.images-amazon.com/images/I/5198YhCQ58L._SL210_.jpg", 124, "暗い過去に訣別し、海軍士官学校の門をくぐった青年が、厳しい訓練や親友の死にあいながら、次第に成長していく姿を、彼を愛する町工場の娘との恋愛を絡ませて描いた青春映画の秀作。ラストシーンのさわやかな余韻が印象的で、日本でも大ヒットした。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIMMEL OG HELVEDE", "マリアの泉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "デンマーク、コペンハーゲンを舞台に、年上の教師に恋し、傷つきながらも自立していく18歳の少女の青春の光と影を捉えた作品。未熟だが純真な心を持つ娘マリアが、妻子ある教師との実りなき初恋を経て、挫折、自殺未遂、家出、同棲と変貌を遂げていく。ベストセラー小説『天国と地獄』の映画化。", "1988-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エコエコアザラク R-page", "エコエコアザラク R-page", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "黒魔術を操る美少女・黒井ミサの活躍を描く人気コミック「エコエコアザラク」の劇場版第5弾。“破壊の悪魔・エゼキエル”の復活を予言する黒魔術教団のリーダーの指令を受け、とある田舎町にやってきたミサ。この町では、1年前に多数の犠牲者を出す不可解な事故が起きていた……。黒井ミサを演じるのは、「ラブコン」「夜のピクニック」の近野成美。赤をイメージした本作「R-page」と、黒をイメージした「B-page」が同時公開される。", "2006-12-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレージー・メキシコ大作戦", "クレージー・メキシコ大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 162, "秘宝の在りかが記された石像をめぐって、大酒飲みで調子のいい酒森進を中心とする７人の面々が、日本からメキシコへと舞台を移しギャング団と追いつ追われつの大争奪戦を展開する。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・ぼくの伯父さん", "男はつらいよ・ぼくの伯父さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "恋に悩む可愛い甥・満男に恋の手ほどきをする寅次郎の姿を描くシリーズ第42作。浪人生活を送る満男はブラスバンド部の後輩・泉に想いを寄せていた。だが、彼女は両親の離婚で佐賀へ転校。そこで満男はオートバイで彼女のもとへ向かった。ところが、そこで寅次郎とバッタリ出会ってしまい……。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京さのさ娘", "東京さのさ娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "浅草の下町を舞台に、男勝りのイキな啖呵をきり、歌がうまくてきっぷのいい下町娘・杉本千枝子を主人公にした青春メロドラマ。劇中のゲイシャ・パーティで千枝子役の江利チエミが芸者姿で『さのさ節』を披露、彼女のはつらつとした演技が見もの。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まぼろし城 第一・二部", "まぼろし城 第一・二部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "雑誌『少年倶楽部』に連載された高垣眸の原作を映画化した娯楽大活劇。“まぼろし党”と名乗る豊臣家の残党と、徳川家公儀隠密との、財宝のありかを記した山絵図の争奪戦をまがまがしく描いていく。「第一部・飛騨の渦潮」「第二部・死の旋風」と、連続活劇ばりに続く面白さがたまらない。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DADDY DAY CARE", "チャーリーと14人のキッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "エディ・マーフィが「ドクター・ドリトル２」のスティーブ・カー監督と再度組んで繰り広げるヒューマン・コメディ。会社をクビになった元エリート・サラリーマンが一念発起して保育園を開設。男だけで子供たちの相手をし始めるが悪戦苦闘するという物語だ。個性が豊かすぎるキャラ立ちキッズ14名に振り回されながら、成長していく主人公の姿が生き生きと綴られる。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("那須少年記", "那須少年記", "", "", "http://image.eiga.k-img.com/images/movie/53894/315x210.jpg?3047", 0, "1954年の夏、14歳のオサムは、離婚した母とともに栃木県黒磯村（現・那須塩原市）へと転校してきた。新しい学校で友達もできたオサムだったが、生徒思いの担任教師・大月を悩ませる秀才問題児・アキラと対立してしまう。複雑な家庭環境で育ったアキラは、ある日不良グループと決闘することになり……。主演に「バッテリー」「ひゃくはち」の太賀。", "2008-10-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INNOCENT LIES", "イノセント・ライズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "フランス北西部の美しい海辺の街の海を一望できる丘の上で、ロンドン警視庁の元警部が謎の死を遂げる。自殺なのか、他殺なのか、調査のため現場を訪れた刑事クロスは、事件の鍵を握るグレイブズ家の兄ジェレミーと美しい妹セリアに出会う。二人には幼い頃に起こった家族の死にまつわる恐ろしい秘密があった。美しい兄と妹の誰にも知られることのなかった無垢なる嘘。その嘘ゆえに二人が越えてはならない関係となったことがすべての事件の発端だった……。禁じられた恋愛を描くサスペンス。", "1994-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENEMY GOLD", "ピカソ・トリガー エネミーゴールド指令", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "頭脳・美貌ともに備えた女性FBI捜査官の活躍を描いたシリーズもの。高級クラブのオーナー＆国際犯罪組織のボスであるカルロスは、女暗殺者ジュエルを使って、次々と悪の手を広げていた。業を煮やした連邦政府機関の司令官は、最終手段として、美人FBI捜査官ベッキーとアバに指令を下す。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DELITTO D’AMORE", "ミラノの恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ミラノの町工場を舞台に、シシリアから働きにやって来た娘と、同じ工場で働く青年の純愛を綴る。ラブ・ロマンスものながら、そこに公害病など現代の病める都市生活の一端を織り交ぜたところがミソ。G・ジェンマの甘いマスクも魅力的な社会派ラブ・ロマンス。", "1974-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGEL OF DESTRUCTION", "レディ・スコルピオン３ 必殺女刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "元警官の私立探偵ブリットは、女性ロック・スター、デライラの警護を依頼されるが、何者かによって殺されてしまう。ブリットに代わって妹で美人刑事のジョーがデライラの警護につくが、彼女の前にはマフィアの影がちらつき、次々と殺人事件が起きていく……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Quick and the Dead", "クイック＆デッド", "", "", "http://ecx.images-amazon.com/images/I/310Q2BSV45L._SL210_.jpg", 107, "荒野の町リデンプションで開催される早撃ちトーナメントに、ガンマンたちが続々と集結。その中に、父を殺した悪徳保安官ヘロッドへの復讐をたくらむ美女エレン、そしてヘロッドのまだ若い息子キッドや牧師のコートといった面々もいた。凄腕ガンマンのヘロッドは息子にも容赦しない男だった。そんな彼に恐れをなし、一度は逃げ出してしまうエレンだったが……。正義のために戦うヒロインを描いた痛快ウェスタン。ライミ監督らしいビジュアルセンスも健在。", "1995-10-31", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IMMER WENN DER TAG BEGINNT", "朝な夕なに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "昔ながらの教育方法を変えることができずに苦しむ女教師を描く。自分がただ勉強を教えるだけの存在ではありたくないと思ってふるまう彼女に、男子生徒が恋心を抱きはじめる。彼女は、それが若者にありがちな憧れであることを悟るのだが……。", "1957-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の一生", "女の一生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「上海」「戦ふ兵隊」など戦時中に厭戦的な記録映画をつくり治安維持法で拘束されたという経歴を持つ亀井文夫の劇映画第２作目。戦後の労働運動の高揚期につくられ、女性の労働問題を見据えた力作である。印刷工場に働く陽子は早く母をなくし、病気の父と幼い弟妹を支えている。同僚で恋人の周平と結婚するが、彼女は実家に送金するため働き続けなければならない。折しも工場では機械導入のため人員整理が行われようとしていた……。亀井の演出は論理的かつ簡潔であり、労働運動が勝利していくさまを、家庭内の封建制との対立を織りまぜながら冷静に描いている。記録映画のみが称賛されがちな亀井の佳作。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獅子王争覇", "ワンス・アポン・ア・タイム・イン・チャイナ　天地争覇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "清朝末期。北京では、時の権力者・西太后が大武術大会を開催しようとしていた。むやみな闘いを好まぬ黄飛鴻は大会に出まいとするが、外国政府による西太后の側近暗殺計画が絡んでいることを知り、出場を決意する。ラストの各流派の武道家たちによる獅子舞合戦は舞踏のごとき華麗さ。本作では黄と許婚との恋のさや当ても描かれ、より娯楽色が強い作品に仕上がっている。この第3作目まで主役を演じてきたジェット・リーは、契約のもつれから本作以降のシリーズを降板したのが惜しまれる。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はりきり社長", "はりきり社長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "太陽自転車社長・大神田平八郎は販売成績向上にやっきとなっている。大株主に拡張資金の融資を依頼したり、アメリカの大注文先・マーナー氏を接待したり。だがご本人は“ミス・サイクル”に選ばれた桃子にお熱で、それが奥様の千枝子にバレてしまったからさぁ大変……。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タッチ", "タッチ", "", "", "http://image.eiga.k-img.com/images/movie/1714/315x210.jpg?4366", 116, "あだち充のコミックとそのアニメ化作でおなじみの青春ラブストーリーを実写映画化。成績優秀、運動神経抜群で甲子園を目指す弟・和也と、何をやってもダメな兄・達也の双子の兄弟は、ともに幼なじみの南に心惹かれていた。監督は「ジョゼと虎と魚たち」「メゾン・ド・ヒミコ」の犬童一心。ヒロイン、南役は「世界の中心で、愛を叫ぶ」の長澤まさみ。ほかに RIKIYA、平塚真介、上原風馬らが共演。", "2005-09-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("けものみち", "けものみち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "松本清張の原作を鬼才・須川栄三が映画化。病気の夫を焼き殺し、けものみちに迷いこんだ女・民子が、男たちの道具としてもてあそばれながら情欲の渦に巻き込まれていく様を描く。池内淳子が自分の意志でけものみちに堕ちる宿命の女・民子を熱演している。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とっとこハム太郎・ハムハムランド大冒険", "とっとこハム太郎・ハムハムランド大冒険", "", "", "http://ecx.images-amazon.com/images/I/21C5X6D02BL._SL210_.jpg", 50, "小学生の間で人気を博したコミックを元にしたTVアニメの劇場版。人間の気持ちがわかるハムスター、ハム太郎の冒険が描かれる。それぞれに個性的なハムスター仲間の活躍も見もの。ハムスターのハム太郎が偶然見つけた、夢の国ハムハムランドの入口。そこはハムスターばかり住んでいる理想郷で、人間と話ができるようになるヒマワリのタネもあるという。さっそくハム太郎と仲間たちは、タネを探して冒険を始めるが……。", "2001-12-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KEINER LIEBT MICH", "愛され作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「ミー＆ヒム」の異才D・デリエが描く一風変わった恋愛模様。恋に縁のない20代後半のOL、ファニー。エレベーターで出会ったゲイの黒人、オルフェオの占いによれば“黒”と“23”がラッキーポイントだとか。早速、ファニーはカーナンバー23の黒い車に乗るマンションの新しい支配人、ロータルにアタックをかけていく。", "1994-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The X-Files: I Want to Believe", "X-ファイル：真実を求めて", "", "", "http://image.eiga.k-img.com/images/movie/53674/315x210.jpg?4159", 104, "1993年にスタートした大ヒットTVシリーズ「X-ファイル」の映画化第2作。監督・脚本は、前作に引き続きTVシリーズの生みの親であるクリス・カーター。FBIの名コンビ、モルダーとスカリーは、ある女性エージェントが謎の失踪を遂げたことで捜査協力を要請される。事件の鍵を握る神父ジョーは透視能力を持ち、聖職者とは思えない過去を持っていた……。対立しながらも協力して捜査を進める2人は、事件の核心とジョーをめぐる驚愕の真実に迫る。", "2008-11-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LESSER EVIL", "レッサー・エヴィル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "会社社長、神父、警官、弁護士の４人が、山奥の小屋で久しぶりに再会。彼らには警察への発覚を恐れる心配事があった。それは22年前、少年時代のこと。悪ガキグループとして遊び回っていた４人は、１丁の拳銃を手にして図らずも殺人を犯してしまったのだ……。この長編第２作で初めて日本に紹介されたD・マッケイ監督が、二つの時制を並行させた巧妙なシナリオを鮮やかに映像化。少年たちの転落をダイナミックな演出でたどる過去と、大人になった４人の恐怖心理をじわじわ盛り立てる現在のトーンを描き分け、緊張感あふれる秀作サスペンスに仕上げた。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Die Welle", "ウェイヴ", "", "", "http://image.eiga.k-img.com/images/movie/54824/315x210.jpg?4386", 108, "実在の事件をもとに、高校生たちが集団狂気にのめり込んでいった5日間の心理実験を描いたドイツ映画。映画の基になったのは、1967年、カリフォルニアの高校のカリキュラムで行われた、「ナチスの独裁政治を繰り返さないため、実際に独裁政治を行ってみる」という実験。1日だけのはずであったその実験は瞬く間に学校全体へ広がり、その集団は「ウェイヴ」と名付けられ、実験を始めた教師ですら制御できなくなっていた。", "2009-11-14", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("哀憑歌 NU-MERI", "哀憑歌 NU-MERI", "", "", "http://ecx.images-amazon.com/images/I/51-k5mJ5vIL._SL210_.jpg", 76, "", "2008-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANNEQUIN TWO ON THE MOVE", "マネキン2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "1000年前の中世の王国で愛を誓い合った王子と農民の娘。だが身分違いの恋は周囲の猛反対にあい、娘は魔女の呪いで人形にされてしまう。時は流れて1990年。人形となった娘は、王子と瓜二つの青年にめぐり会うが……。カワイイ人形と純な青年の恋をロマンティックに綴った、さわやかなラブ・ファンタジー第２作。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("廣東小老虎／THE CUB TIGER FROM KWANG TUNG", "燃えよジャッキー拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "熱血青年タイガーは、“闘うべからず”という亡き父の教えを守りながらカンフーの修行に精を出していた。だが、あることで悪徳組織から執拗に狙われだし、たまりかねた彼はついに怒りを爆発させる。若き日のジャッキー・チェンの熱演が見もの。", "1974-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千羽づる", "千羽づる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "２歳の時、爆心地から２キロの地点で被爆。幸いすり傷一つ負わずその後すくすくと成長、運動能力にも恵まれクラスの人気者となった少女。しかし小学校卒業間近、彼女は突然原爆症の宣告を受ける。“鶴を千羽折れば病気が治る”と伝え聞き、病床で一所懸命折る彼女だが……。実話にもとづいたドラマ。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Who's Your Caddy?", "俺たちヒップホップ・ゴルファー", "", "", "http://image.eiga.k-img.com/images/movie/34114/315x210.jpg?3427", 91, "人気ヒップホップデュオ「アウトキャスト」のビッグ・ボーイが主演するコメディ。売れっ子ラッパーのクリストファー・“Cノート”・ホーキンスは、音楽レーベル「キラー・インク」を設立し、大成功を収める。ホーキンスは、家の近くにある由緒正しい名門カントリー・クラブ「キャロライナ・パインズ」の会員権を得ようとクラブを訪れるが、そこは若い黒人ラッパーが簡単に入会できるようなクラブではなく、すったもんだの末、クラブ代表者とゴルフで決着をつけることになった。", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Married to the Mob", "愛されちゃって、マフィア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "　アンジェラはマフィアの一員フランクと結婚したが、彼が殺され、未亡人になってしまう。アンジェラは新たな人生を始めることを決意するが、そんな彼女にフランクのボスだったトニーが近づく。一方、自らの正体を隠し、おとり捜査を行なっていたFBIのマイクもアンジェラに惹かれて……。ミシェル・ファイファーら魅力的なキャストが好演したオフビート・コメディ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRO＾LE D’ENDROIT POUR UNE RENCONTRE", "夜のめぐり逢い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "南フランス郊外のフリーウェイ。夫に捨てられた女と、そんな女を待つかのようにパーキング・エリアで車を修理している男との、二日間の予期せぬ出来事を描くF・デュペイロン監督の長編デビュー作。C・ドヌーヴとG・ドパルデューは「フォート・サガン」以来の共演である。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Adjustment and Work", "適応と仕事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談 一つ目地蔵", "怪談 一つ目地蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "恨みが恨みを呼ぶ怪談もの。古池のほとりの一つ目地蔵。20年前、泥棒伝蔵は仕事の足手まといと、ここに赤ん坊のお波を捨て、その後、間柄家に忍び入るがそこの少年京之介に斬り殺される。相棒の重五郎は伝蔵を見捨て、千両箱を持って逃げた。その途中で捨てたお波を殺そうとするが殺しきれず、頬を傷つけただけだった。20年後、京之介とお波は敵とは知らずに出会い恋するが、お波のみにくい頬の傷を知った京之介は、お波を捨て、重五郎の娘、お絹と恋仲になる。お絹との仲をとり持った伊助は、お波を切り殺し、古池に突き落とすのだった……。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("その前夜", "その前夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "山中貞雄を中心として結成された若い映画作家・脚本家のグループの一員として、彼の作品にも協力していた萩原遼の「清水次郎長」に続く監督作である。原作は山中貞雄、脚色の梶原金八は同グループの共同ペンネーム。京都の今はさびれた宿・大原屋を舞台に、その唯一の長居客・瀧川と店の姉娘・お咲の恋を描く。やがて大原屋は洗濯屋として更生することになるが……。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONSTANS", "コンスタンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ポーランド映画界の名匠K・ザヌーシが“連帯”の反乱以前に、社会主義のひずみを描き出した意欲作。大学を卒業した青年ヴィクトルが、ある企業に就職。だが、そこでは様々な不正が、まかり通っていた。同僚に仲間になるよう勧誘されたヴィクトルは、これを拒絶して組合に訴えたことから、転落の道をたどり始める……。淡々と捉えられた主人公の日常が重みを感じさせ、悲劇が現実感とともに伝わってくる。", "1980-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRIME BUSTERS", "笑う大捜査線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ひょんなことから警察官になった二人の男が、麻薬組織を壊滅させようと悪戦苦闘するアクション喜劇。B・スペンサーとT・ヒルのコンビは、本国では大人気で、本作以外にも「サンド・バギー／ドカンと３発」などの出演作があるが、日本ではあまり人気が出なかった。", "1977-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRANCE", "メビウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "アイルランドの荒涼とした風景をバックに、現代によみがえった魔女の恐怖を描くオカルト・スリラー。原因不明の幻覚症状に苦しむ人妻ノーラがアイルランドに帰郷。そこに待ち受けていたのは、狂信的な伯父と、彼女によく似た女性の死体。やがて伯父は不審な行動をとるようになり、ひどくなる一方の幻覚に悩まされるノーラに魔女の呪いが迫る。おどろおどろしさを排したスタイリッシュな映像感覚が鮮烈。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WIDE SARGASSO SEA", "ボディandハーツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "イギリス貴族のロチェスターは、顔も知らないフランス貴族、アントワネットと結婚し、ジャマイカへやって来る。ロチェスターが来島したのは、そこにある大農園を受け継ぐため。南海の宝石と呼ばれるジャマイカの神秘的な美しさと、官能的な美女、アントワネット。しかし、それは、ロチェスターの一族に秘められた悲劇が再び繰り返される前兆だったのだ……。C・ロンバートやN・パーカーという若く美しいカップルが官能的な熱演を見せるラブ・ストーリー。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLACK COBRA", "ブラック・コブラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "シルベスター・スタローンの「コブラ」の黒人版。ニューヨーク市警の腕利き黒人刑事が、連続殺人事件の犯人の顔を偶然撮影した女性カメラマンを守って、犯人の暴走軍団と戦う。本家「コブラ」のスピーディーなテンポには今一つ及ばない。", "1987-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動戦士ガンダム　逆襲のシャア", "機動戦士ガンダム　逆襲のシャア", "", "", "http://ecx.images-amazon.com/images/I/51R1FYHF6VL._SL210_.jpg", 124, "ガンダムシリーズ初の完全オリジナル劇場版。シリーズ1作目「機動戦士ガンダム」から続くアムロ・レイと宿敵シャア・アズナブルの最後の戦いを描く。宇宙世紀0093年、ネオ・ジオン軍の総帥として歴史の表舞台に返り咲いたシャアは、地球環境を汚染し続ける人類を粛正するため、小惑星アクシズを地球に落下させようする。アムロは自ら設計した新型モビルスーツ、ν（ニュー）ガンダムに乗り、シャアの企みを阻止するため戦う。", "1988-03-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREAT SPY MISSION （OPERATION CROSS BOW）", "クロスボー作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ドイツ軍によるヨーロッパ侵略が進んでいた頃、チャーチル首相はドイツ軍が脅威の新兵器を開発したとの情報を得て、３人の情報部員をドイツへ送り込む……。ドイツのロケット兵器開発をくい止めようとした“クロスボー作戦”を描く戦争スパイ・アクション。", "1965-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Devil's Double", "デビルズ・ダブル　ある影武者の物語", "", "", "http://image.eiga.k-img.com/images/movie/57284/315x210.jpg?4461", 109, "イラクの独裁者サダム・フセインの長男ウダイの影武者だったラティフ・ヤヒアの自伝を、「007 ダイ・アナザー・デイ」「トリプルX」のリー・タマホリ監督が映画化。ウダイに顔が似ているという理由から、家族の命と引き換えにウダイの影武者になることを強制されたラティフ。自ら生死を選べる自由もなく、絶対的な権力と莫大な資産、そして狂気に満ちたウダイに寄り添い続けるラティフは、恐ろしい現実を次々と目の当たりにしていく。「マンマ・ミーア！」のドミニク・クーパーが、家族思いの青年ラティフと狂気の王子ウダイという正反対の2役を怪演した。", "2012-01-13", "37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA GRANDE BOURGEOISE", "哀しみの伯爵夫人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "20世紀初頭、水の都ベニスと古都ボローニャを結んで起こった殺人事件。それには暴君である夫との生活に疲れ、実兄との愛欲に溺れる伯爵夫人リンダが事件と深くかかわっていた。M・ボロニーニが実話をもとに描いた悲劇的なラブ・ストーリー。", "1974-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAMNATION ALLEY", "世界が燃えつきる日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ゼラズニーのベストセラー小説の映画化。核戦争後に生き残った米国空軍の人間が特製の装甲車で、スリリングな大陸横断の旅に出る。ハリウッド映画初出演のサンダ以下、豪華キャストに加えて、FOXの特撮技術が話題となったSFアドベンチャー。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・私の寅さん", "男はつらいよ・私の寅さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "マドンナ・岸恵子が画家の役で登場する。柴又に帰った寅さんはある日、小学校時代の友だちに再会。彼の妹である独身の美しい女流画家を知って一目惚れ。ゲージュツを鑑賞しようと寅さんのアトリエ通いが始まり、まわりはハラハラ……。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("河内のオッサンの唄", "河内のオッサンの唄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "大ヒットした同名コミック・ソングをモチーフに関本郁夫と高田純が脚本を書き、斎藤武市が監督した川谷拓三の本格的初主演作。ヤクザの抗争に巻き込まれた河内在住の白タク運転手が、東京から来た仲間とともに組事務所に殴り込み、河内音頭にのって大暴れするという実録もののパロディーともいえる痛快作。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺たちに明日はないッス", "俺たちに明日はないッス", "", "", "http://image.eiga.k-img.com/images/movie/53884/315x210.jpg?3032", 79, "さそうあきら原作の同名漫画を、「赤い文化住宅の初子」「百万円と苦虫女」のタナダユキ監督が映画化。17歳で童貞の比留間は、担任教師と付き合っているクラスメイトの友野とセックスがしたくて仕方がない。周りの友達がそれぞれの相手を見つける中、友野に相手にされない比留間のイライラは募るばかりで……。脚本は「リンダリンダリンダ」「神童」の向井康介。柄本明の次男、柄本時生ら若手実力派俳優が競演。", "2008-11-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("連合艦隊", "連合艦隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "司令官、参謀、下級の兵員など、様々な階級の人々の人間模様を交錯させながら、戦艦大和が撃沈されるまで日本海軍連合艦隊がいかに戦い、いかに敗れていったかを描いた戦争映画の大作。特撮による戦闘場面とミニチュア戦艦大和が秀逸。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("(秘)女郎責め地獄", "(秘)女郎責め地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "日活ロマンポルノを代表する異色コンビ田中登＝田中陽造が絢爛な映像美で社会の底辺に生きる女と男を描き、性の解放をうたう傑作。舞台は江戸時代の裏見世。女郎おせんは一緒に寝た客が３人続いて死んだことから“死神”と仇名され、吉原から落ちた器量よし。そんな彼女と、彼女に想いを寄せる枕絵師、人形使い、ヤクザ者のヒモとの交情をニヒルなコメディ・タッチで綴る。特に人形使いがおせんを人形に見立て、初めて女を抱くシーンの様式美は本編中の白眉。ドン底の境遇でも優しさとバイタリティーを失わないヒロイン・中川梨絵の奇妙な明るさが印象的。田中登は本作で監督協会の新人賞を受けた。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("13の月", "13の月", "", "", "http://image.eiga.k-img.com/images/movie/33674/315x210.jpg?4345", 104, "「カリスマ」の人気俳優・池内博之が映画監督に初挑戦したラブ・ストーリー。余命3カ月を告知され故郷の町へと戻ってきた吉岡佑は、かつての恋人・唯子と再会する。唯子は他の男性と結婚する予定だったが、佑の余命を知り、残された時間を共に過ごすことを決意する。主演は柏原崇と大塚寧々。共演には津田寛治、ミッキー・カーチスら豪華メンバーが顔を揃える。", "2006-09-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クルテクの冒険　かわいいぼうけん編", "クルテクの冒険　かわいいぼうけん編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "チェコの国民的アニメとして半世紀にわたって親しまれてきた「クルテク”シリーズを、2002年の「クルテク　もぐらくんと森の仲間たち」に続いて劇場公開。もぐらの男の子クルテクが仲間たちと一緒に大活躍するエピソード10話を、「たのしいぼうけん編」「かわいいぼうけん編」に分けて上映する。本作「かわいいぼうけん編」では、日本でも絵本が出版されている「もぐらくんとじどうしゃ」など、1963年～98年までに制作された短編・中編4話を収録。", "2005-06-11", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Light Sleeper", "ライト・スリーパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "W・デフォーの渋みを漂わせた魅力が光る、都会派の人間ドラマ。うらぶれた中年男ジョンは、野心家の女性アンとともに麻薬の密売をしながら暮らしている。ある日、彼の昔の恋人が殺された。どうやら何者かがジョンを売ろうとしているらしい。次第に彼は、親友であるアンに疑惑の目を向け始める……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GARDEN OF ALLAH", "沙漠の花園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "父の死後ドミエは新たな人生を求めてアルジェリアの砂漠に旅する。車中知りあったボリスと様々な体験をともにし愛し合うようになるが、彼はチュニス近くのトラピスト修道院から逃げ出した僧だった。東洋的エキゾチズムあふれるテクニカラー作品。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUSPECT", "容疑者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "公正な審理のためには、決して接触してはならない女性弁護士リリーと、陪審員エディの禁じられた愛の高まり。法廷内外の人間の欲望が、さらに二人を危機に追い詰める。ベテラン監督イエーツのサスペンス＆ミステリー・タッチが冴えわたる。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PKNIK MIT WEISMANN", "ワイズマンとのピクニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 11, "世界的に名高いアニメーション作家J・シュヴァンクマイエルのシュールな短編。緑の木立ちの中で、モノたちがピクニックをしている。スーツがフルーツを食べ、チェス盤がゲームをし、チリとりが洋服ダンスの前に熱心に穴を掘っている。陽が陰った時、洋服ダンスが開き、中から手足を縛られた男がころげ出て来るが、そのまま穴に落ち、チリとりがその上に土を被せていく。DVDは「ヤン・シュヴァンクマイエル　短編集」に収録。", "1968-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海上花", "フラワーズ・オブ・シャンハイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "ホウ・シャオシエン監督が中国文学の古典『海上花』を映画化した文芸ロマン。19世紀末の上海を舞台に、清朝末期の高級遊郭で繰り広げられる男と女の愛憎劇。全編を室内カットのみに限定し、アヘンの煙が匂い立つような官能的映像世界を演出構築した。当時の家具や装飾品を買い揃えたという贅沢なセットをバックに、香港、日本、台湾の豪華スターが痛々しいまでの愛の葛藤を演じている。", "1998-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鳶〈とび〉がクルリと", "鳶〈とび〉がクルリと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「凶気の桜」でデビューを飾った薗田賢次監督が、同作に引き続きヒキタクニオの同名小説を映画化。ひょんなことから、とび職の世界と出会ったOLの成長が描かれる。ヒロインに扮してコミカルな演技を披露するのは「ぼくんち」の観月ありさ。彼女と哀川翔扮する頑固な職人の掛け合いが絶妙だ。会社から巨大モニュメント建築の仕事を振られたOLの貴奈子。早速、彼女は頑固な高松の仕切るとび職集団に仕事を依頼するのだが……。", "2005-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飯と乙女", "飯と乙女", "", "", "http://image.eiga.k-img.com/images/movie/56154/315x210.jpg?2303", 75, "ダイニングバーで働く砂織は人に料理を振る舞うのが好きだが、物静かな常連客・九条が食事をしてくれないことに悩む。もう1人の常連客・美江は同せい中の彼氏がまともに働かないストレスから過食症になるが、ある日、妊娠していることに気がつく。美江の務める会社の社長は経営難から食べることができず、空腹に耐えかねて娘のための弁当を食べてしまい……。4組の男女がおりなす「食」についての人間模様を、シューベルトの弦楽四重奏第14番「死と乙女」にのせて描く。", "2011-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRAZY LOVE", "クレイジー・ラブ　魅せられたる三夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「バーフライ」の脚本家としても知られるC・ブコウスキーの小説を映画化した青春ラブ・ストーリー。ハリー・ボスという一人の男が12歳、19歳、33歳で出会った女性との愛の思い出を、３部構成で描く。思春期、青年期、青春の終末——いつの時代も変わらぬ女性への純粋な愛を映し出していく。", "1987-01-01", "37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRACULA，PRINCE　OF　DARKNESS", "凶人ドラキュラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ハマー・フィルムの“ドラキュラ”シリーズ第３作で、第１作「吸血鬼ドラキュラ」の続編。ドラキュラ伯爵役C・リーの凶悪な存在感が見ものだ。カルパチア山脈を旅する二組の夫婦が森の古城に立ち寄る。そこにはドラキュラの下男が暮らしており、彼は４人のうちの一人を殺し、灰となっていたドラキュラ伯爵をよみがえらせる。生き残った男女は僧院へと逃げ込み、ドラキュラと神父の死闘が始まる。", "1965-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REISE DER HOFFNUNG", "ジャーニー・オブ・ホープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "かつて栄華を誇ったが、今は貧因と不況にあえぐ祖国トルコ。“約束の地”を求めて、希望の旅に出ていった多くの同胞と同じく、その男もまたすべての財産を売り払って、遠い異国スイスへと旅立つ。だが、幼い息子と身重の妻を連れた彼の旅は、過酷な運命に押し流されるのだった。故郷を捨て、危険な懸けにあえて命を懸けざるをえない人々の夢、希望、そして闘いをドラマティックに描いた、パワフルで美しい傑作。", "1990-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブレイクブレイド　第四章「惨禍ノ地」", "ブレイクブレイド　第四章「惨禍ノ地」", "", "", "http://image.eiga.k-img.com/images/movie/55764/315x210.jpg?3171", 0, "吉永裕ノ介の人気ロボット漫画を6部作で映画化した第4弾。「昆虫物語みつばちハッチ　勇気のメロディ」のアミノテツロ監督がシリーズ総監督を務める。魔力を持たない青年ライガットは、親友の国王ホズルを守るためにクリシュナ軍へ入隊。やがて、親友ゼスの率いる敵国アテネスのボルキュス大隊が出陣し、迎え撃つバルド将軍率いる大隊との大激突が幕を開ける。", "2010-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lucky Trouble", "エターナル　奇蹟の出会い", "", "", "http://image.eiga.k-img.com/images/movie/57594/315x210.jpg?1452", 97, "「バイオハザード」シリーズなどでアクション女優としても活躍するミラ・ジョボビッチが、婚約者を待ち続ける一途な女性を演じたロシア製ハートフルドラマ。教師をしながら作家を目指す冴えない男のスラバは、交通事故がきっかけで出会った女性ナージャに一目ぼれ。恋に落ちた2人はトントン拍子で結婚まで決まるが、スラバは校長の陰謀で子どもたちのサッカーチームの監督にさせられてしまう。挙式のためモスクワでナージャが待つなかチームは予想外に試合を勝ち進み、スラバはいつまでたってもモスクワに行かれず……。スラバ役は「ナイト・ウォッチ NOCHNOI DOZOR」のコンスタンチン・ハベンスキー。", "2012-05-05", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Moneyball", "マネーボール", "", "", "http://image.eiga.k-img.com/images/movie/55274/315x210.jpg?2306", 133, "メジャーリーグ「オークランド・アスレチックス」のGM（ゼネラルマネージャー）、ビリー・ビーンの半生を、ブラッド・ピット主演で映画化。全米約30球団の中でも下から数えたほうが早いといわれた弱小球団のアスレチックスを独自の「マネー・ボール理論」により改革し、常勝球団に育てあげたビーンの苦悩と栄光のドラマを描く。監督は「カポーティ」のベネット・ミラー。「シンドラーのリスト」のスティーブン・ザイリアンと「ソーシャル・ネットワーク」のアーロン・ソーキンが脚本を担当した。", "2011-11-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sling Blade", "スリング・ブレイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "母の不倫現場を目撃し、母とその相手を殺害した知能障害のカールが、25年の精神病院生活を終えて出所。故郷の町で仕事を得て少しずつ人々と慣れ親しんでいく彼は、フランクという少年と交流を持った。父親のいないフランクは母と二人暮らしで、暴君のように振る舞う母の恋人ドイルに頭を悩ませていた。やがてドイルの乱暴な行為がピークに達した時、カールの胸に、ある決意が芽生え……。過去のある男と孤独な少年の交流を描いた寓話風の人間ドラマ。父子にも似た絆の温かさの一方で、カールが再び殺人を犯すのでは？　というサスペンス的要素が絡められ、観る者の興味を強く惹きつける。監督、脚本のB・B・ソーントンは巧妙なストーリーテリングとともに、カール役でも熱演。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("賭城大亨之 新哥傳奇／CASINO TYCOON", "アンディ・ラウ カジノタイクーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "A・ラウお得意のキャラクターを生かした歴史大作。日本の侵略が始まる頃の香港。大学生のシンは、金持ちの娘ロークの憧れの的だったが、そのことでロークが好きなチンに敵視されていた。日本侵略が始まり、シンはマカオに逃れる。そこで港湾労働者として働いていたシンは、港一帯の事業主アルフォンソとその娘を助けたことから、運送会社に入ることができた。アルフォンソはチンの父親で賭博場を仕切る大物実業家チャオと兄弟関係にあった。あることからチャオの賭博場で働くことになったシンは次々と昇進していくが、チャオとチンの計略によって、誘拐犯に仕立てられ、10年の刑を言い渡される。そして、獄中から復讐を誓うのだった……。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌うヒットマン！", "歌うヒットマン！", "", "", "http://image.eiga.k-img.com/images/movie/56384/315x210.jpg?4334", 82, "狙った獲物は絶対に逃さない最強のヒットマン、JKこと桐生潤一郎は、ある時、ポップな歌を歌い踊り続けなければ心臓が破裂してしまうという薬を飲まされる。暗殺者として致命的なダメージを負ったJKは、なんとか解毒剤を入手しようと奮闘するが、そんなJKの前にかつての弟子・本間恭子が立ちはだかる。草刈正雄が歌って踊るヒットマンを熱演した3Dミュージカルコメディ。ヒロイン役は元宝塚歌劇団・宙組の和央ようか。", "2011-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Man of Steel", "マン・オブ・スティール（原題）", "", "", "http://image.eiga.k-img.com/images/movie/53194/315x210.jpg?3776", 0, "「ダークナイト」のクリストファー・ノーランがプロデュースし、「300 スリーハンドレッド」のザック・スナイダー監督のメガホンでリブートさせる新たな「スーパーマン」シリーズ。", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽有耳／THE SUN HAS EARS", "太陽に暴かれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "中国の気鋭イム・ホーが東京国際映画祭グランプリを得た「息子の告発」に続いて取り組んだ愛憎ドラマ。長引く飢饉によって、軍閥に対する民衆の不満が高まる1920年代。貧しくも純朴な娘ヨーヨーが、いつしか自分の軍隊を持って世直しを図りたいという野望を抱く男パンハオと出会い、激動の運命をたどり始める。これが日本初登場となる中国の人気女優チャン・ユイが、動乱の時代を背景にたくましく変貌を遂げるヒロインを熱演。", "1996-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初戀 Hatsu-Koi", "初戀 Hatsu-Koi", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "同性に恋した男子高校生を主人公に、ゲイの若者たちが様々な問題に直面しながらも成長していく姿を描いた青春ドラマ。高校3年生の唯史は、親友の公太に密かな恋心を抱いている。ある日、一緒に登校していたところを同級生にからかわれたことから、公太は唯史を避けるようになってしまう。失意の中、学校をさぼって電車に乗っていた唯史は、偶然見かけたゲイのカップルの後を追うが……。監督は、ピンク映画を中心に俳優としても活動する今泉浩一。", "2007-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関東流れ者", "関東流れ者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "兄とのいさかいから婚約者・お繁と別れ、渡世人となった大谷清次郎は、ふとしたことから暴れ者の馬頭三五郎と兄弟分になり、彼の一家の世話になる。ある日清次郎は、遊女に身をやつしたお繁と再会し……。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUCIA", "ルシア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 160, "キューバの鬼才ウンベルト・ソラスの長編処女作。３つの異なる時代を生きた３人の女性“ルシア”を通して、キューバ解放の歩みを３部構成で綴った一大叙事詩。1895年の第二次独立戦争から始まり、第３部は解放後の1960年代。自立を目指すルシアと古めかしい彼氏の相克が浮きぼりにされる。モスクワ映画祭でグランプリを受賞した。", "1968-01-01", "49", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メッセンジャー", "メッセンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "デビュー作「私をスキーに連れてって」で大ヒットを飛ばし、いわゆる“ホイチョイ・ムービー”３部作を撮り上げた馬場康夫が前作から８年ぶりに監督した作品。前３作はスキー場や海などのリゾート地が舞台だったが、今回は東京を舞台に自転車便野郎たちの滑走を見せていく。服飾ブランドのプレスの座に収まり、優雅な生活をしていた尚実だったが、会社の倒産に伴ってすべてを失ってしまった上に、自転車便の横田を車ではねてしまう。慰謝料を払えない彼女は仕方なく横田の代わりに、自転車便会社で働くことに。しかし、そこは社員が横田とその友人の鈴木だけのオンボロ会社だった……。鈴木と尚実のほのかな恋、そして彼らとライバルのバイク便とのレースなどを、シンプルな構成で見せるエンターテインメント作品。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SQUIRM", "スクワーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "送電線の切断事故により、地中に流れ出した高圧電流で、ミミズが異常繁殖、巨大化して、人間に襲いかかるというパニック映画。この映画で、のたくりうごめくスクワーム（ミミズ）を見たあとは、しばらく麺類に拒否反応を示すほどのリアルな描写は圧巻。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さゞなみ", "さゞなみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "母と娘、それぞれの胸に秘めた純粋な恋心を、山形県ののどかな風景の中に静かに描き出す。死んだ父の記憶とともに生きる母の悲しげな姿を見て育った稲子は、いつしか恋することに臆病になってしまった。仕事で知り合った男性、玉水に安らぎを感じ、想いを募らせる稲子だが、一歩を踏み出すことができず……。ヒロインの微妙な心の揺れ動きを淡々と演じる唯野未歩子が不思議な魅力を漂わせる。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("公園通りの猫たち", "公園通りの猫たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "カルー、リエ、マユミらはミュージカルスターを夢みる女の子。彼女たちは老演出家の提案から猫を題材にしたミュージカルを思いつき、レッスンを始めるが……。公園通りに住む野良猫たちと夢見る少女たちの交流を描いた青春ドラマ。早坂暁が自らのエッセイ集を脚本化し、中田新一が監督した。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Life in Ruins", "マイ・ビッグ・ファット・ドリーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRAGON FIRE", "トゥルー・ドラゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "2032年のロサンゼルス。地球は、力のあるものが生き残るという暗黒の時代を迎えていた。宇宙コロニーからパワーズは兄のジョニーが何者かに殺されていることを知る。復讐に燃えるパワーズは暴漢に襲われるが、そこを助けてくれたスリックから“ルールなき闇のリングでのキックボクシングで戦い抜くことが犯人を探す方法だ”と言われる。そして、最強のキックボクサー、アーメッドが兄の仇であることを知るのだった。B級映画の巨匠R・コーマンが製作総指揮にあたったSFアクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PIERROT LE FOU", "気狂いピエロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ゴダール監督長編第10作にしてヌーヴェル・ヴァーグの金字塔的代表作。フェルディナンがマリアンヌと再会する。5年ぶりにピエロと呼ばれた彼は、彼女との宿命的な旅に出発する。銃を携え盗んだ車で疾走する日々、やがて事態は急転する。すべてはマリアンヌの罠だったのか…絶望と愛の間でフェルディナンの選んだ結末とは……。「みつかった／何が？／永遠が／太陽にとけこむ／海」", "1967-07-31", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ONLY GAME IN TOWN", "この愛にすべてを", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ラスベガスを舞台に、コールガールと流れ者のピアニストが出会い、お互いを傷つけあいながらも、やがて確かな愛を取り戻していく姿を切々と描きだすラブ・ストーリー。ブロードウェイ舞台劇の映画化で、「シェーン」「ジャイアンツ」などで知られる巨匠G・スティーヴンス監督の遺作でもある。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES LIENS DE SANG", "刑事キャレラ 血の絆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "E・マクベイン原作のロングセラー小説『87分署』シリーズの一編をC・シャブロルが監督した異色心理劇。「いとこ同志」で３人の男女の奇妙な関係をペシミスティックに描いたシャブロルが、本作品でも巧妙な心理描写を披露する。主人公の刑事キャレラ役には、D・サザーランドが扮する。", "1978-01-01", "2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("王首先的夏天／HIGH　SKY　SUMMER", "思い出の夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "映画の撮影隊が、出演者を探すため寂れた田舎町にやってくる。ひょんなことから出演することになった小学生のショーシェン。しかし、彼はいつも“街には行きたくない”という台詞が言えずに止まってしまう……。正直な気持ちしか表現できない純朴な少年の姿を通して、現代中国の都市部と農村部の経済格差を浮き彫りにしていく社会派ヒューマン・ドラマ。", "2001-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女帝", "女帝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "大阪のスナックで働いていた彩香は、彼女に興味を持ったヤクザ・直人の口利きで高級クラブへと移る。やがて彼女は“ミナミの妖怪”と称される大物と知り合い、銀座の女帝の階段を上り始めていく。ホステスたちのバイブルともなっている人気コミックの映画化。哀しい境遇をバネに、野心を胸に生きるヒロインを小沢真珠が熱演。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠狂四郎 卍斬り", "眠狂四郎 卍斬り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "わずか２作と短命に終わった松方弘樹主演シリーズの１作。シナリオを溝口健二作品で鳴らしたベテラン依田義賢が担当している。岸和田藩と薩摩藩の密貿易をめぐる争いに狂四郎が巻き込まれる。混血の刺客を演じた田村正和はその後TVで４代目・狂四郎となった。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAX WORK", "ワックス・ワーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "近くに出来たロウ人形館を訪ねたハイスクール・ボーイのマークと友人たち。不気味な館主とロウ人形たちが迎えるなか、友人たちが次々とロウ人形に変えられていく……。世界征服をたくらむ館主と主人公の闘いに加え、オオカミ男や吸血鬼など怪奇映画のヒーローが次々と登場し、ホラー映画の様々な楽しみが盛り込まれている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モーニング刑事。　抱いてHOLD ON ME！", "モーニング刑事。　抱いてHOLD ON ME！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "TVのオーディション番組から生まれ、人気アイドル・グループとなったモーニング娘。と平家みちよの共演によるサスペンス作品。雑誌の読者モデルに選ばれた８人の女の子たち。その中でダントツ人気のみっちーが、ストーカーに狙われるようになる。警察もあてにはならないと感じた残りの７人と編集部員のゆうこは、みっちーを守るために空手道場に通い、猛特訓を受け、自ら“モーニング刑事（コップ）。”と名乗って犯人捜しを始めるが……。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEUCES　WILD", "デュースワイルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「バスケットボール・ダイアリーズ」のスコット・カルバート監督が手掛けた青春ドラマ。1958年、ニューヨークのブルックリンではデュースとバイパーズのギャング団が対立していた。そんな折、デュースをまとめるレオンに恨みを持つバイパーズのトップ、マルコが出所する。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tell Them Who You Are", "マイ・シネマトグラファー", "", "", "http://image.eiga.k-img.com/images/movie/33814/315x210.jpg?4791", 95, "2度のアカデミー撮影賞を獲得し、「アメリカン・グラフィティ」などの撮影を手掛けたことでも知られるハスケル・ウェクスラー。彼の2番目の妻の子でジャーナリストのマーク・ウェクスラーが、アメリカ映画史に多大な影響を与えてきた父の姿を映し出したドキュメンタリー。ジョージ・ルーカス、マイケル・ダグラス、ジェーン・フォンダほかハリウッドを代表する監督や俳優たちへのインタビューなどから彼の素顔に迫る。", "2007-01-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK MOON", "ブラック・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "L・マルが、渡米の直前に作った異色のファンタジー。近未来、女と男の間で激しい戦争が行われていた。男の占領地区から抜け出した女性兵士は、不思議な空間に入り込んでいく。そこには一軒の館があり、老婆と双子の姉弟、15人の子供、そして動物たちが住んでいた。この家の住人の奇妙な行動に、女性は興味を覚えるが……。マルいわく、“この映画は、ルイス・キャロルの世界のバリエーション”とか。", "1975-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Green Lantern", "グリーン・ランタン", "", "", "http://image.eiga.k-img.com/images/movie/55404/315x210.jpg?3504", 114, "DCコミックの人気ヒーローを「ウルヴァリン：X-MEN ZERO」のライアン・レイノルズ主演、「007 カジノ・ロワイヤル」のマーティン・キャンベル監督で実写映画化。米空軍テストパイロットのハル・ジョーダンはある日、宇宙の平和と正義を守る組織「グリーン・ランタン」の一員に選ばれる。宇宙最強の武器と言われるパワーリングの力に当初は戸惑うハルだったが、幼なじみで恋人のキャロルや仲間たちを守るため、全宇宙を巻き込んだ壮大な戦いに身を投じていく。", "2011-09-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丹波哲郎の大霊界 死んだらどうなる", "丹波哲郎の大霊界 死んだらどうなる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "霊界研究の権威として有名な俳優の丹波哲郎が満を持して製作したワンマン映画。自動車事故で死んだ主人公が死後の世界をさ迷い、再び生き返るまで、各霊界を観光ビデオのように紹介していく。霊界学入門映画ともいえよう。地獄で苦しむ人々をパフォーマンス集団のように描いているあたりが、多少興をそそる。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEEPSTAR SIX", "ザ・デプス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "海底を舞台にした新感覚のホラー。海底１万メートルに作られたアメリカ海軍のミサイル建設基地。そこで働くクルーたちに襲いかかる恐怖の数々。謎の深海怪物、水圧、酸欠、そしてミサイルにも爆発の危機が……。息をもつかせぬサスペンスの連続で、まさに海洋版「エイリアン」といった趣の作品。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワールドプロレスリング G1クライマックス3D 2011", "ワールドプロレスリング G1クライマックス3D 2011", "", "", "http://image.eiga.k-img.com/images/movie/57194/315x210.jpg?2715", 0, "新日本プロレス主催で毎年夏に開催されている「G1クライマックス」の2011年・第21回大会を映画館で3D上映。8月14日の優勝決定戦をノーカット収録（予定）のほか、2010年度のベストバウトに挙げられたIWGPジュニアタッグ選手権試合「プリンス・デビット＆田口隆祐VS飯伏幸太＆ケニー・オメガ」の再戦も行われ、その模様も収録している。", "2011-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHINA　STRIKE　FORCE／CHINA　WHITE", "SPY_N", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "日本から藤原紀香、アメリカからラッパーのクーリオらが出演した国際色豊かなサスペンス・アクション。上海の犯罪組織内で殺人事件が起こり、現地の刑事ダレンらは捜査を開始。事件の背後には、中国に麻薬を流通させようとして米国人ディーラーと組んだ組織のボスの息子の存在が。組織に潜入捜査していた国際警察捜査官ノリカと協力して、ダレンは巨悪を追い詰め……。お色気シーンに加えて、クライマックスでは高所のアクションに自ら挑んだ藤原紀香の熱演が光る。", "2000-01-01", "1,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INCUBUS", "死霊の悪夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アメリカのある小さな町で、次々と猟奇的な暴行殺人事件が起きる。見るも無残な被害者を検視した医師のサム・コーデルは、事件の背後に伝説の怪物“インキューバス”の存在を知るが……。個性派俳優を配したショッキングなホラー映画。", "1982-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRANTIC", "フランティック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "R・ポランスキー会心のサスペンス。ウォーカー医師は学会出席のため、妻と20年ぶりにパリへやって来た。だが、妻が突然ホテルから消えた。どうやら空港で間違えたスーツケースに関係があるようだ。警察に届けるが、言葉の通じぬ異国の地、思うように捜査をしてくれない。自ら妻探しに乗り出したウォーカーは、スーツケースを取り違えた相手ミシェルに接触する。彼女は頼まれてアメリカから何かを運んできたらしい。妻を追ってどんどん深みにはまる二人に、見えざる恐怖が忍び寄る。異国の地の旅行者という設定が、見事に心突き刺す仕掛けとなって唸らされる。後にポランスキーの妻となるE・セイナーが美しい。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("XTROII THE SECOND ENCOUNTER", "エイリアン・ウォーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "タイムワープの実験が行われていた近未来の科学研究所で、別次元に送られた研究員が、異次元からの狂暴な魔物に体を支配されて戻って来る。やがて魔物は成長、醜悪で巨大な姿と化し、研究所の人間を次々と襲い始めた。外部への通路も封鎖され、極限状態の中で残された数人の人間とエイリアンとのすさまじいバトルが展開する。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あらくれ大名", "あらくれ大名", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "徳川方の御曹司の身でありながら豊臣方につき、大阪冬の陣では本陣を急襲、父家康を死地寸前にまで追い込んだ松平直次郎を描く時代劇アクション。講和後、直次郎は野に下るが、家康の軍師、板倉伊賀守は茶臼山の一戦を根に持ち、直次郎を葬り去ろうとひそかに策をめぐらす。やがて伊賀守は卑劣な手段に出て……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("複雑な彼", "複雑な彼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "善悪・明暗と様々な顔を見せる国際線のスチュワード・譲二。世界を駆けめぐる彼を愛してしまう秘書の冴子。二人の恋の行方を描いた恋愛大作。主人公のモデルとなったのは『塀の中の懲りない面々』で一躍流行作家となった安部譲二。リオ、シスコに海外ロケを敢行。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HINDENBURG", "ヒンデンブルグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "ドイツが科学技術の粋を結集して建造した全長246.7メートルの大飛行船ヒンデンブルグ号は1937年５月６日アメリカ、ニュージャージー州で謎の大爆発を起こす。20世紀最大のミステリーといわれるこの事件を、当時のニュース・フィルムを織り込んで描いたスペクタクル大作。ヒンデンブルグ号は1936年に就航、すでに往復10回の航路を無事終了していたが、ミルウォーキーに住むある一人の女性が、“霊感”を受け、ヒンデンブルグ号が時限爆弾によりアメリカ上空で爆破されると予言する。ドイツ宣伝相のゲッペルスはこれを重要視し、警戒態勢をとってゲシュタポを乗り込ませるのだが……。この事件で97名のうち乗員13名、乗務員22名が犠牲となった。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／THE　GREEN　FISH", "グリーンフィッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "経済成長期にあった1990年代の韓国社会のシビアな現実を見つめたヒューマンドラマ。除隊したマクトンが帰郷すると、家族は離ればなれになり、それぞれが生計を立てるために忙しく奔走していた。再び一緒に暮らす夢を抱き、マクトンは犯罪組織の手先となって働き始めるが、それは悲劇を呼び起こしてしまう。「シュリ」のハン・ソッキュが、悪に染まっていく主人公マクトンを好演。国内の映画賞を総なめにし、国外でも高い評価を得た作品。", "1997-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Welcome to Dongmakgol", "トンマッコルへようこそ", "", "", "http://image.eiga.k-img.com/images/movie/1364/315x210.jpg?1736", 132, "05年度の韓国映画界で興収1位の座に輝いたヒューマンドラマ。朝鮮戦争が行われていた50年代初頭。墜落したアメリカ機を追って北朝鮮人民軍と韓国軍のそれぞれの兵士たちが、“トンマッコル”と呼ばれる人里離れた村に迷い込む。村で鉢合わせとなった両軍の兵士は対立するが、戦争のことなど知らないのんびりした村人たちによって、打ち解けていく……。監督は本作が長編デビューとなるパク・クァンヒョン。", "2006-10-26", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO FOR THE MONEY", "トゥー・フォー・ザ・マネー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "名優アル・パチーノと若手実力派マシュー・マコノヒー共演による本作は、全米49の州で禁止されている“スポーツ賭博”に材を求めた人間ドラマ。賭博に熱中している人々に有益な情報を流す予想屋稼業の男たちの栄光と挫折の日々を、実話ならではのリアリティで描き切るスリリングな作品。フットボール選手への夢を断たれたブランドンは、スポーツ試合の情報屋に甘んじていたが、ある日、大手の会社から声をかけられ……。", "2006-03-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("進め独立旗", "進め独立旗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "橘外男の小説『ナリン殿下への回想』を映画化。在日インド人の独立運動を中心に、日本人の援助と英国大使館の弾圧の間で揺れるインド人闘士の姿を描いたもの。第二次大戦中の時局に合わせた作品ではあるが、政治色よりはスパイ・アクションの雰囲気が濃厚。長谷川一夫がインド人の闘士に扮している。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sleuth", "スルース", "", "", "http://image.eiga.k-img.com/images/movie/53394/315x210.jpg?2334", 89, "72年にローレンス・オリビエ＆マイケル・ケイン主演で製作され、2人揃ってアカデミー賞主演男優賞にノミネートされた傑作サスペンス「探偵スルース」を、マイケル・ケイン＆ジュード・ロウ主演でリメイク。一人の女性を巡って、夫であるベストセラー作家（ケイン）と不倫相手の若手俳優（ロウ）が、ロンドン郊外にある作家の豪邸を舞台に心理戦を繰り広げる。監督は「魔笛」「ハムレット」のケネス・ブラナー。", "2008-03-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MASTERS OF THE UNIVERSE", "マスターズ 超空の覇者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "宇宙は戦国時代。全宇宙創造の秘密を握るグレイスカル・パワーをめぐって、魔王スケルターや氷の美女イブルらを相手に、正義のために戦う勇者HE−MANの活躍を描くSFアクション。肉体派スター、D・ラングレンがアメリカで人気のコミック・キャラクターに扮しての初主演作。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("壁の中の秘事", "壁の中の秘事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "ベルリン映画祭に正式出品されて物議を醸した問題作。ニュータウン計画などの当時の世相に合わせ、団地内に閉じ込もり続けると人間はどうなっていくのかを、ネガティヴな視点で捉えていく。予備校生の青年が団地の部屋にずっとひきこもっているうちに、彼は狂気に駆られていく。「若松孝二　初期傑作選　DVD-BOX4」に収録。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lucky One", "一枚のめぐり逢い", "", "", "http://image.eiga.k-img.com/images/movie/55974/315x210.jpg?2023", 0, "「きみに読む物語」「メッセージ・イン・ア・ボトル」のニコラス・スパークスによる原作小説を、「ハイスクール・ミュージカル」のザック・エフロン主演で映画化したラブストーリー。イラクに赴任していた米海軍の兵士ローガンは、戦場で美しい女性の写った写真を拾う。その写真を手にしてから何度も命を救われたローガンは、帰国後、写真の女性ベスを探し出す。突然現れたローガンに当初は不信感を抱いていたベスだったが、次第に2人はひかれ合い、結ばれる。しかし、2人の出会いのきっかけとなった写真が原因となり、幸せを引き裂く事態が起こり……。共演はTVシリーズ「マーシー・ホスピタル」のテイラー・シリング。監督は「シャイン」のスコット・ヒックス。", "2012-06-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋愛寫眞", "恋愛寫眞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「ケイゾク」「TRICK」などトリッキーな映像で知られる堤幸彦監督が、松田龍平と広末涼子という逸材を得て初の恋愛映画に挑戦。青春時代の挫折と成長、恋の切なさを鮮やかに描き出す。カメラマンを目指す誠人のもとに、死んだと聞いていた元彼女・静流からの手紙が届く。消印はニューヨーク、それだけを手掛かりに誠人は静流を探すためアメリカへ旅立つ。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聖石傳説", "聖石傳説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ホウ・シャオシエンの「戯夢人生」に登場した、台湾の人形芝居“布袋戯(プータイシ)”。この伝統芸能とSFXを融合させた異色アクション。目にも止まらぬ速さで激しい動きを見せる人形の、怒濤のアクションシーンに驚かされる。古代中国武術界を舞台に、すべての願いが叶うという“天門石”をめぐって、剛健な剣士や、かつての君主とその娘、天門石を狙う異星人の飽くなきバトルが展開する。", "2000-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEADLY HEROES", "ネイビー・フォース テロリスト壊滅作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "アテネ空港をハイジャックしたテロリストたちの要求は、彼らのリーダーの釈放。その身柄と引き換えにほとんどの人質は解放されるが、若く美しい二人の人妻だけはそのまま連れ去られた。愛する妻の救出に命を懸ける男、宿敵の大物テロリスト打倒に執念を燃やす男、かつて死線をともにした二人の戦士が再び手を組む。空港を舞台にしたスリリングな銃撃戦とシャトルバスでの緊迫のスタントで始まり、最新鋭の小型潜水艇ミニ・サブを使っての潜入作戦、テロリストのアジトを急襲するアメリカ海軍精鋭特殊部隊の激烈なバトルと、息もつかせぬテンポと迫力で圧倒する戦闘アクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAST SCARFACE", "スカーフェイスX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "暗黒街で大物を夢見るギャングが野望のために闘うバイオレンス・アクション。1930年代、禁酒法時代のシカゴ。シシリー生まれのロッキーはある日、マフィアの大ボスの情婦ヴィクトリアを危機から救い、ファミリーの一員に。ヴィクトリアのボディガードとなったロッキーは、酒の密輸で大きなヤマを当てたのを機に、ボスを始末しヴィクトリアを自分の女にする。次々と野望を実現していったロッキーは、やがてシカゴを牛耳るゴッドファーザーへとのし上がるのだが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ESPION LE´VE−TOI", "遅れてきた死神", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "スイスのチューリッヒを舞台に、KGBとフランス情報機関の熾烈なスパイ戦に巻き込まれた男の孤独な闘いが展開。政治サスペンス映画の第一人者、Y・ボワッセ監督がL・ヴァンチュラ、M・ピコリと名優二人を得て描く、非情なエスピオナージもの。", "1981-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Borat: Cultural Learnings of America for Make Benefit Glorious Nation of Kazakhstan", "ボラット　栄光ナル国家カザフスタンのためのアメリカ文化学習", "", "", "http://image.eiga.k-img.com/images/movie/1874/315x210.jpg?1611", 84, "英国人コメディアン、サシャ・バロン・コーエン主演による社会風刺コメディ。英米両国で放映されたTV番組「Da Ali G Show」内の人気キャラクターのひとつ、ボラット（カザフスタンのTVリポーター）が、米大陸横断を敢行し、行く先々で文化的な衝突を繰り返す。監督は「ボブ・ディランの頭のなか」のラリー・チャールズ。06年度ゴールデン・グローブ賞では主演男優賞を受賞した。", "2007-05-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIRACLE MILE", "ミラクル・マイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "間違い電話で数時間後に核戦争が起こることを知った青年のとまどいと、町の人々の混乱をサスペンス・タッチで描くパニック映画。偶然受けた電話の内容が気になるハリーは、恋人のジュリーを連れ出してヘリコプター乗り場に向かうが、次第に町は混乱する人々でパニック状態に陥っていく。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The End", "ジ・エンド（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-08-31", "76,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2011-2012 グラス「サティアグラハ」", "METライブビューイング2011-2012 グラス「サティアグラハ」", "", "", "http://image.eiga.k-img.com/images/movie/57164/315x210.jpg?3608", 232, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を映画館で上映する「METライブビューイング」2011～12年シーズンの4作目。「クンドゥン」「めぐりあう時間たち」など映画音楽でも活躍する作曲家フィリップ・グラスが手がけた、ガンジーの生涯を描く現代オペラの名作。インドで大臣の息子として生まれ育ち、弁護士として就職した南アフリカで激しい人種差別を目の当たりにしたガンジーは、「非暴力、不服従」を意味する「サティアグラハ」を掲げて抵抗運動に目覚めていく。", "2011-12-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Freedom Writers", "フリーダム・ライターズ", "", "", "http://image.eiga.k-img.com/images/movie/34074/315x210.jpg?1726", 123, "「ボーイズ・ドント・クライ」「ミリオンダラー・ベイビー」のオスカー女優ヒラリー・スワンクが初めて製作総指揮を手掛け、自ら主演も務めた感動の学園ドラマ。実在する女性英語教師エリン・グルーウェルとその生徒たちによる同名ベストセラーを基に、ロス暴動直後の荒廃した高校に通う若者たちと、“書くこと”を通して彼らに希望を抱かせようとする教師の姿を描く。監督・脚本は「フィッシャー・キング」「マディソン郡の橋」の脚本で知られるリチャード・ラグラベネーズ。", "2007-07-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COLD HEAT", "コールドヒート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "離婚調停によって、息子の養育権を元妻に与えてしまった大富豪が、ギャング団を使って息子を誘拐。対する元妻は探偵を雇って息子の奪回を企てる。一人の少年をめぐっての両親のエゴをアクションたっぷりに描く異色作。監督はライナー・W・ファスビンダー監督のスタッフとして働いていたU・ロメル。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La belle endormie", "禁断メルヘン　眠れる森の美女", "", "", "http://image.eiga.k-img.com/images/movie/57584/315x210.jpg?3431", 82, "「本当に若い娘」「ロマンスX」のカトリーヌ・ブレイヤ監督が、シャルル・ペローの「眠れる森の美女」とアンデルセンの「雪の女王」の2つの童話をもとに、童話では語られなかった少女のその後の物語を描く官能ドラマ。ある王国の王女として生まれたアナスタシアは魔女にかけられた呪いで100年の眠りについてしまう。夢の中でさまざまな冒険を重ねた16歳のアナスタシアが目を覚ますと、そばで美しい青年が見守っていた。アナスタシアは青年と恋に落ち、身も心も捧げるが……。", "2012-02-18", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Youth Without Youth", "コッポラの胡蝶の夢", "", "", "http://image.eiga.k-img.com/images/movie/53714/315x210.jpg?2790", 124, "「ゴッドファーザー」「地獄の黙示録」の巨匠フランシス・フォード・コッポラが「レインメーカー」以来10年ぶりにメガホンをとった幻想ドラマ。ルーマニアの作家ミルチャ・エリアーデの幻想小説「若さなき若さ」を原作に、落雷によって驚くべき若さを取り戻した言語学者の奇妙な運命を描き出す。主演はティム・ロス。共演に「コントロール」のアレクサンドラ・マリア・ララ、ブルーノ・ガンツ、マット・デイモンほか。", "2008-08-30", "1,7,4,2,56", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("菊地朔太郎之英雄的行為", "菊地朔太郎之英雄的行為", "", "", "http://image.eiga.k-img.com/images/movie/57774/315x210.jpg?2741", 45, "幼い頃から警察官にあこがれる菊地朔太郎は、高校卒業後、何度も警察官採用試験を受けるが不合格続き。ついに30歳未満という募集年齢を過ぎてしまう。それでもあきらめきれない朔太郎は、自作の警察官の制服を着て夜な夜な勝手にパトロールを始めるが……。「ほんとうにあった怖い話」「BOYS LOVE」の寺内康太郎監督が、正義感溢れる若者の姿をユーモアたっぷりに描いたオリジナル作。寺内監督と交流があり、互いにライバルと認め合う山本清史監督（「水霊　ミズチ」「END CALL」）のオリジナル作「BlOODLESS」と同時上映。", "2012-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEETHOVEN", "ベートーベン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ペット泥棒の手を逃れ、ニュートン家に飛び込んだセントバーナードの子犬ベートーベン。お茶目な悪戯もするけれど、時には家族の危機を救う活躍で、次第に一家に溶け込んでいく。ところが非道なペット泥棒がまたも現れて……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARLETTE", "アルレット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "中年女性と美青年のウソから始まった恋の行方を描くロマンティック・コメディ。フランス郊外の片田舎でウエイトレスをしているアルレットの前に、ハンサムな青年実業家フランクが現れる。一目惚れしたという彼の言葉をいぶかしる彼女だが、フランクの誠実な人柄に引かれていく。ところが、実はアルレットはカジノの大富豪の一人娘。フランクは借金をした組織に、彼女の遺産を横取りするために送り込まれたチンピラだった……。ビデオ発売時タイトルは「アルレット　ラスベガス恋物語」。", "1997-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NAKED EDGE", "六年目の疑惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ある会社で６万ドルが盗難されるという事件が発生。偶然その場に居合わせた社員のラドクリフは、同僚が犯人だと証言。だが容疑者の弁護を受け持った弁護士から“おまえが真犯人だ”と詰問される。やがて妻もラドクリフの行状に疑問を抱くようになり……。至るところに落とし穴が仕掛けられた、ヒッチコック型サスペンス映画の秀作。ラスト、バスルームでのドンデン返しが見もの。", "1961-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SIEGE OF FIREBASE GLORIA", "地獄の軍団 スクワッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「フルメタル・ジャケット」の鬼教官役で注目を集めたL・アーメイが、米軍最精鋭部隊の隊長に扮し、テト攻勢下の米軍砦“ファイヤーベース・グロリア”にて北ベトナム正規軍と熾烈な戦いを繰り広げるベトナム戦争映画。アーメイは、この作品の中でテクニカル・アドバイザーも兼任している。公開時タイトルは「スクワッド・栄光の鉄人軍団」。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新宿の肌", "新宿の肌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "当時、脱ぎっぷりの度胸の良さとエキゾチックな魅力で人気があった、松岡きっこの初主演作。純情娘のチヨは、新宿の街で写真家、フーテン青年、ヤクザなどと知り合い、身を持ち崩してストリッパーになる。その彼女を、作曲家志望の青年・今井が救って、チヨは人気歌手になっていく……。新宿の街の当時の風俗（ゴーゴー喫茶やサイケバーなど）が、随所に盛り込まれている。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Be Kind Rewind", "僕らのミライへ逆回転", "", "", "http://image.eiga.k-img.com/images/movie/53544/315x210.jpg?2545", 101, "発電所に忍び込んで大量の電磁波を浴びたジェリー（ジャック・ブラック）は、その電磁波でレンタルビデオ店のビデオテープの中身を全て消してしまう。困ったジェリーと、ビデオ店のアルバイト店員マイクは、2人で名作映画を次々とリメイクしていくが……。「エターナル・サンシャイン」のミシェル・ゴンドリー監督が、「スクール・オブ・ロック」のジャック・ブラックを主演に迎えて送るコメディ。マイク役には人気ラッパーのモス・デフ。", "2008-10-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BROTHERHOOD", "暗殺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "イタリア移民の血を引くマフィアの“兄弟”フランクとヴィンス。だがフランクは組織を裏切りシシリーに逃亡。そして、こともあろうに、ヴィンスに“フランク暗殺”の指令が下る……。時代の変遷につれて変容していくマフィアの“血の絆”。「ゴッドファーザー」より一世代前のマフィア社会を描いた異色作。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("足にさわった女", "足にさわった女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "刑事と女スリが、ふとしたことから知り合いになり、珍道中を繰り広げる。刑事は女の復讐劇に一役買わされ……。戦前に阿部豊が、1960年に増村保造がそれぞれ映画化しているテンポの良い喜劇。伊藤雄之助が女スリの弟分に扮し、快演。市川崑はこの作品がお気に入りらしく、増村版の脚本も担当した。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("望郷と掟", "望郷と掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "野村芳太郎監督・安藤昇主演による“掟”ものシリーズの第３作。密輸組織のために実刑をくらった主人公は、出所後仲間を集めて組織から金塊を強奪する。スリリングなアクションの連続。この“掟”ものは大ヒットした「血と掟」を皮切りに全４作が作られた。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOY　LOVE", "猫は、なんでも知っている", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「ミルクのお値段」が各国で賞賛を受けたニュージーランドの俊英H・シンクレア監督が２年ぶりに手掛けたラブ・コメディ。ゲーム感覚で恋愛を楽しむ今ドキな女の子のクロー。そんな彼女が、CMタレントとして活躍する彼女持ちのハンサム青年を誘惑し、手玉に取っていく様を通して、女性上位なニュージーランドのお国柄を痛快に笑い飛ばしていく。男の子からモテモテの少女クローはバーで知り合った青年ベンに恋をする。ある日、クローが彼の家を訪ねると、ベンは恋人のエミリーと同棲していた。だがクローは、その色気と巧みな話術でベンを誘惑し始め……。", "2002-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hugo", "ヒューゴの不思議な発明", "", "", "http://image.eiga.k-img.com/images/movie/56064/315x210.jpg?3730", 126, "世界各国でベストセラーとなったブライアン・セルズニックの冒険ファンタジー小説「ユゴーの不思議な発明」を、マーティン・スコセッシ監督が3Dで映画化。駅の時計台に隠れ住む孤児の少年ヒューゴの冒険を、「映画の父」として知られるジョルジュ・メリエスの映画創世記の時代とともに描き出す。1930年代のパリ。父親の残した壊れた機械人形とともに駅の時計塔に暮らす少年ヒューゴは、ある日、機械人形の修理に必要なハート型の鍵を持つ少女イザベルと出会い、人形に秘められた壮大な秘密をめぐって冒険に繰り出す。主人公ヒューゴを演じるのは「縞模様のパジャマの少年」のエイサ・バターフィールド。イザベル役に「キック・アス」「モールス」のクロエ・モレッツ。2012年・第84回アカデミー賞では作品賞含む11部門で同年最多ノミネート。撮影賞、美術賞など計5部門で受賞を果たした。", "2012-03-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("行方知れズ 渋さ知らズ　１９９９−２０００", "行方知れズ 渋さ知らズ　１９９９−２０００", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "1989年に結成され、現在は楽器演奏者、舞踏集団、踊り子などを有する巨大バンドに発展した“渋さ知らズ”の活動を追ったドキュメンタリー。彼らがアンダーグラウンドで活躍していた1999年から2000年までに密着し、中心人物・不破大輔の知られざる人間性と、バンドの実態を明らかにする。1989年の公演『リズム』をきっかけに結成された“渋さ知らズ”。各種パフォーマーが入り交じり、変人変態自慢ともいえる衝撃舞台を展開。", "2004-12-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陸軍特別攻撃隊", "陸軍特別攻撃隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "昭和19年10月、マッカーサー率いる連合軍のフリピン・レイテ島上陸に対し、第一陣の万朶隊を始めとした陸軍特別攻撃隊は、必死の体当たり攻撃を行った。出撃前の隊員が子犬と遊ぶシーンや、心中を打ち明けた遺書、隊長の貴重な肉声も収められている戦後未公開の記録映画。", "1945-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山口組外伝・九州進攻作戦", "山口組外伝・九州進攻作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "菅原文太主演の実録ものヤクザ映画。夜桜銀次は一匹狼の鉄砲玉。別府で兄弟分が狙撃されるや相手の幹部を殺して大阪に潜伏する。関西でもヤクザ組織の出入りに飛び込み大暴れ。警察に追われ博多に身を隠すが、ついに地元ヤクザによって射殺される。文太節満載の豪快なアクション劇。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("挽歌", "挽歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "最果ての町、釧路を舞台に、どこか冷めている妖精のような少女と、妻の不貞を知って悩む中年男、そして成熟した魅力を持つ妻との愛の葛藤を描いた作品。原田康子のベストセラー小説の映画化で、都会の高層ビル街には生き得ない女を秋吉が好演。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い賭博師", "黒い賭博師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "東京に舞い戻った氷室浩次は、国際賭博団マルコムの団員・楊と対決する。初めはイカサマと見破れずに負けるものの、再度の対戦では見事に見破って一矢を報いた。ところが、彼の腕を恐れるカード師・犬丸が氷室の左手を痛撃。氷室は奮然と船中の大賭博に乗り込み、傷ついた左手でダイスを握る。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER SCHWARZE BLITZ", "黒い稲妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1956年の冬期オリンピックで活躍した名スキーヤー、T・ザイラー主演のスキー映画。“黒い稲妻”と呼ばれるスキーの名手ミヒャエルは、愛する恋人のため競技会で優勝することを誓うが、大会の日、参加選手の一人のワックスが盗まれ、ミヒャエルが疑われる。ザイラーの華麗な滑りにも注目。", "1958-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEYOND THE CALL OF DUTY", "ラストプラトーン2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "1975年、ベトナム戦争終結直前のメコン川を舞台に、戦線から脱出を試みる、退役軍人と孤児たち、それに合流した米海兵隊武装ボート部隊“リバー・ラッツ”の壮絶な戦いを描く戦争アクション。群がるベトナム兵、欲に目がくらんだ裏切り者など、修羅場が満載。J・M・ヴィンセントが相変わらずのマッチョぶりを発揮している。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刀狩るもの　二本松の冒険", "刀狩るもの　二本松の冒険", "", "", "http://image.eiga.k-img.com/images/movie/53754/315x210.jpg?3388", 101, "昼は電気会社の社員、夜は便利屋として働く二本松の元に、妖艶な魅力を放つ未亡人・響子から奇妙な依頼が入る。何者かによって盗まれた3本の日本刀を取り戻して欲しいというのだ。二本松は響子の手元に残された刀を手がかりに捜査を開始。刀同士が引き寄せられるように共鳴しあう中、刀に宿った霊が二本松に襲いかかり……。テレビや舞台で活躍する尾崎右宗と「あの空をおぼえてる」の水野美紀主演によるアクション・ムービー。", "2008-06-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さらば箱舟", "さらば箱舟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "1983年５月に死去した寺山修司の遺作。架空の村を舞台に、いとこ同士が結婚すると豚のシッポの生えた子供が生まれるという、タブーを犯して一緒になった捨吉とスエの運命を縦糸に、封建的な村落共同体が文明の波をかぶって近代化へと脱皮していく過程を横糸にして織り成された一大叙事詩。ロケ地には沖縄が選ばれ、南国の陽光の中で寺山一流の幻想的、呪術的な映画詩が展開する。実はこの作品は当初ガルシア・マルケスの『百年の孤独』の映画化として企画されたという経緯があるが、トラブルの末、紛れもない寺山ワールドとして見事に完成した。入院の要請を断って撮影を強行した寺山の執念が結実した作品である。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HITMAN", "ザ・ヒットマン 危険な標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ドラッグの密売現場に踏み込んだ刑事ガーレットは、罠に落ち激しい銃撃戦となる。窓から落ちていく彼が見たのは、相棒の刑事の不敵な笑いだった。６年後、一命を取りとめ殺し屋（ヒットマン）として復活したガーレットは、犯罪組織撲滅のためおとりとなって潜入、影の人物を突きとめる。主演のC・ノリスの実弟A・ノリスが監督したバイオレンス・アクション。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A TIME TO LOVE AND A TIME TO DIE", "愛する時と死する時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "「西部戦線異状なし」のレマルクの同名小説をD・サークが映画化した戦争メロドラマ。ロシア戦線から故郷に休暇で帰って来たドイツ兵グレーバーは、医者の娘エリザベスと恋に落ち、結婚する。父母とも再会し、束の間の喜びに浸ったグレーバーだったが、彼は再び戦場に戻らねばならぬ身だった……。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TUVALU", "ツバル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "各映画賞を受賞したドイツの新鋭F・ヘルマーによるハートフル・ファンタジー。様々な人々が集まる寂れた町の室内プール。盲目の管理人の息子で船長になることを夢見るおとなしい青年アントンは、ある日、常連客の娘エヴァに一目惚れする。二人は少しずつ惹かれあっていくが、プール存続の危機に立たされ追い詰められていく。サイレント映画のようなコミカルな味わいと、セピア色のノスタルジックかつ幻想的な映像が不思議な雰囲気を醸し出している。ヒロイン、C・ハマートヴァの透明感あふれる魅力が光る。", "1999-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROCKABYE", "摩天楼ララバイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "最愛の息子を連れ、カリフォルニアからニューヨークへやって来た主人公スザンヌ。ある日、息子を連れマンハッタンを歩いていると、突然何者かが彼女の目を封じ、息子を連れ去る。白昼の誘拐。泣き叫ぶ彼女に、周囲の目は冷たく、警察もまともに取り合ってはくれない。彼女は、事件を聞きつけた女性レポーターとともに息子捜しに奔走し、とある幼児紹介所との取引に応ずる。だがそこは闇のベビー・ブローカーだった。息子の命が危ない……！　治安の悪さでも有名なニューヨークを舞台に、最愛の者を奪われた一人の女性の精神的極限状態を描くサスペンス。フィクションを超えた重厚さが感じられる。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("また逢う日まで", "また逢う日まで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ロマン・ロランの小説『ピエールとリュイス』を水木洋子が脚色し、巨匠・今井正が監督した名作。学生・田島三郎と画家のタマゴ・小野螢子は、空襲警報の鳴り響く地下鉄のホームで知り合う。二人はお互いの境遇を慰め合ううちに恋に落ち、結ばれるが、再度の逢いびきは三郎の兄嫁の急病と螢子の爆死によって阻まれ、三郎もまた出征したまま二度と帰ることはなかった……。今井正監督はこのラブ・ストーリーを力むことなく情感豊かに描き上げることによって、戦中の若い恋人たちのやり場のない怒りとむなしさを浮き彫りにし、日本映画史上最高の恋愛映画に作り上げた。戦争の傷がまだ残る時代のリアリティーが、単なる叙情やセンチメンタリズムを越えた血の通ったものにしている。あまりに有名なガラス越しの接吻シーンのはかない美しさは、観る者の心を激しく揺さぶる。久我美子と岡田英次が若い恋人たちに扮し、初々しい魅力で好演した。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE DE´SORDRE A｀ VINGT ANS", "想い出のサンジェルマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "1940～50年代、ボヘミアン生活を謳歌する若者が集まり、アナーキーな文化が花開いたパリ６区サンジェルマン・デ・プレ。その中心的存在が天才、B・ヴィアンだった。ヴィアンと同年代の監督が愛と郷愁を込めて綴るサンジェルマン讃歌。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋の正しい方法は本にも設計図にも載っていない", "恋の正しい方法は本にも設計図にも載っていない", "", "", "http://image.eiga.k-img.com/images/movie/55704/315x210.jpg?1724", 0, "歌手、声優、タレント、漫画家など幅広く活躍する中川翔子がドラマ初主演を果たしたラブストーリー。中川が歌う楽曲「千の言葉と二人の秘密」から生まれたオリジナルストーリーで、恋に臆病な図書館司書の泉と陽気な建築家ネルの淡い恋が描かれる。「真夏のオリオン」の篠原哲雄監督がメガホンをとり、ケータイ音楽ドラマ「DOR＠MO（ドラモ）」として配信されたが、ファンからのリクエストにより劇場公開される。", "2010-10-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不毛地帯", "不毛地帯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 181, "昭和30年代ごろのFX（次期使用戦闘機）選定をめぐる商社の暗躍と、政治家の利権あさりを織りまぜながら描いた、山崎豊子の同名小説を原作とする骨太の社会派ドラマ。政界の恥部や、冷酷なまでに非情な商社の仕組みを、山本薩夫がダイナミックに描き出している。ロッキード事件とシンクロする、半実話映画。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("豪姫", "豪姫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "古田織部は、秀吉によって死罪となった利休の代わりに茶事を任されることになる。秀吉の養女・豪姫と下人ウスは追われ、豪姫は豊臣が徳川に敗れたことにより生家預かりの身となった。そして時が流れた。ウスはもう40を過ぎてはいるが、まだ美しさを残した豪姫に再会する。また、織部は徳川家の茶の指南役になっていたが、彼と家康との関係は、利休が死罪になる直前の秀吉との関係と酷似していた。そんな頃、豪姫は利休の命日に織部を招いて茶会を開くが……。勅使河原監督の前作「利休」の姉妹編とも言える力作で、宮沢りえの老け役も話題になった。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LONGTIME COMPANION", "ロングタイム・コンパニオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ゲイとエイズをテーマに、あるゲイのグループの１日をスナップ・ショット的に捉えながら、1981年から９年間にわたる人間ドラマを描き出した話題作。1990年サンダンスUSフィルム・フェスティバルでオーディエンス・アワード（観客賞）を受賞。エイズを正面から採り上げた初のメジャー映画ともいえる。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KAMA SUTRA", "カーマ・スートラ 愛の教科書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "古代インドの性愛の聖典『カーマ・スートラ』をモチーフにした愛のドラマ。16世紀、婚礼前の王子と関係を結んだことから宮廷を追われたマヤは、王室お抱えの彫刻家シャイに恋をする。しかし、シャイは彼女に惹かれながらも、恋に踏み切れない。通じぬ思いに苦しんだ末に、マヤは『カーマ・スートラ』の教えを学んで王の愛妾となるが……。「モンスーン・ウェディング」の女性監督M・ナーイルが、悲恋に揉まれる女性の心の成長を描写。オリエンタルなムードと官能美が融合し、めくるめく映像世界が展開する。", "1996-01-01", "3,21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あしがらさん", "あしがらさん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "ボランティアで路上生活者のもとを訪れていた映画作家、飯田基晴が、初老の男性“あしがらさん”と出会う。ふと見せた彼の笑顔に心魅かれた飯田が、あしがらさんの生活を３年間追い続けたドキュメンタリー。厳しい現実を見つめながら、路上生活者と製作側との間に生まれた信頼関係が温もりを漂わせる。新たな人生を歩み始めるあしがらさんの笑顔が印象的だ。20年以上も東京・新宿の路上で生きてきたあしがらさん。残飯で飢えをしのぎ、心を閉ざして誰とも付き合わない。一人の映画作家が、入院、施設入所、再び路上へと、様々な出来事が降りかかるあしがらさんを追い続ける。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵コナン　沈黙の15分（クォーター）", "名探偵コナン　沈黙の15分（クォーター）", "", "", "http://image.eiga.k-img.com/images/movie/55914/315x210.jpg?3524", 109, "青山剛昌原作コミックの人気アニメーション劇場版15周年作品。12月のある日、再選を果たした朝倉都知事宛にナゾの脅迫状が届く。翌日、開通したばかりの都営新地下鉄のトンネル爆破事件が発生。コナンは朝倉に恨みを抱く者の犯行と疑い、朝倉が国土交通大臣時代に建設したダムの関係者を捜査するため、新潟県に向かう。その矢先、捜査先の雪原で死因不明の遺体が発見され……。", "2011-04-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊藤の話", "伊藤の話", "", "", "http://image.eiga.k-img.com/images/movie/53474/315x210.jpg?2442", 74, "小泉八雲原作の「伊藤則資の話」を現代に置き換えて映画化。熊谷の女子高で教鞭をとっていた伊藤は、自著がベストセラーとなったために八戸大学の教授に抜擢される。だが伊藤が八戸に着くと、前任の教授が失踪してしまうなど次々と奇妙な出来事が起こる。美人助手とともに教授を捜すうちに、伊藤は不思議な屋敷へと辿り着く……。主人公・伊藤に温水洋一。共演は加藤夏希、田丸麻紀他。監督は「五重塔」で注目された秋原正俊。", "2008-04-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARTY LINE", "マッド・キラー 鮮血のしぶき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "複数の人と同時に通話できる“パーティー・ライン”を利用した殺人事件を描くサスペンス。母を虐待し自分をレイプした父を憎むアンジェリーナは、父に似た男をパーティー・ラインでひっかけては弟セスに殺させていた。地方検事補ステイシーは、粗野なダン刑事とともにその捜査に当たる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE PIECE FILM STRONG WORLD", "ONE PIECE FILM STRONG WORLD", "", "", "http://image.eiga.k-img.com/images/movie/54334/315x210.jpg?3580", 0, "大人気少年コミック「ONE PIECE」の原作者・尾田栄一郎が初めて劇場用にストーリーを書き下ろした劇場版のシリーズ10作目。伝説の海賊王ゴールド・ロジャーが遺した「ひとつなぎの大秘宝（ワンピース）」をめぐり海賊が壮絶な戦いを繰り広げていた大海賊時代。体がゴムのように伸びる海賊・ルフィとその仲間たちは「偉大なる航路（グランドライン）」をサウザンド・サニー号で突き進んでいたが、世界最強の男で悪魔の実（み）の能力者「金獅子のシキ」がルフィ海賊団の前に立ちはだかる。", "2009-12-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Star Chamber", "密殺集団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "　正義に燃える若き判事ハーディンは法律を遵守しながらも、悪を野放しにしているという矛盾に悩む。凶悪犯が裁きを免れ、ほくそ笑んでいる一方、被害者やその遺族は苦しみ、新たな事件を引き起こしてしまう。そんな折、ハーディンは事件を再審理し、判決を下すだけでなく、処刑まで執行する私設裁判所の話を聞かされる。ハーディンもそこに加わることになるが……。秘密組織の存在を知った判事の姿を描く社会派サスペンス。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("唐獅子警察", "唐獅子警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "暴力団・大成会の直人と腹違いの弟・拓との宿命的な愛憎を描いたヤクザ映画。兄を憎む拓を渡瀬恒彦が好演。直人役の小林旭とガッチリ組んで、ラストの二人の対決を盛りあげた。題名・原案は滝沢解＆かわぐちかいじによる同名劇画からとられている。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("われ一粒の麦なれど", "われ一粒の麦なれど", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "子供が小児マヒにかかったことを訴える間違い電話を受けた官吏の坂田は、その時以来小児マヒ撲滅に執念を燃やし、ついには予防ワクチンの安全性を証明するために人体実験を企てる。“99人を助けるために、一人の犠牲はしかたがない”と言う主人公の迫力はすごい。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIVA ZAPATA！", "革命児サパタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "後にノーベル文学賞を受賞するアメリカ文学の巨匠J・スタインベックが、メキシコ革命で活躍したエミリアノ・サパタの半生をもとに脚本を書き下ろした伝記ロマン。偶然の事故からお尋ね者となってしまったサパタは警察に捕らえられてしまうが、兄たちが民衆の助けで彼を助け出したことが革命の口火となり、メキシコ南部一帯を制圧。北部からはかねてよりサパタに革命を持ちかけていたマデロが攻め入り革命が成立し、サパタは大統領にまでのぼりつめるが……。1950年代のアメリカで、マッカーシー上院議員を先頭に吹き荒れた共産党員弾圧“赤狩り”に際して、元共産党員だったことを暴露されたカザンが、転向後に作った革命映画といういわくつきの作品。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIDA：NATURALEZA VIVA", "フリーダ・カーロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "メキシコの女性画家フリーダ・カーロ。1954年、47歳で亡くなったこの画家の知られざる半生を描き出した伝記映画。トロツキーを虜にし、メキシコに壁画運動を起こし、バイセクシュアルでセクシー。スキャンダラスな彼女の人生が、死の床にあるフリーダのイメージとしてよみがえっていく。", "1984-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REMBRANDT", "レンブラントへの贈り物", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "自らが画家であり彫刻家でもあるC・マトンが製作費13億円を費やしてレンブラントの生涯を映画化。これまで伝記で語られてきた寡黙で厳格なレンブラントのイメージを独自の解釈で払拭し、人間的で官能的、奔放な魅力あふれるレンブラント像を創り上げた。劇中レンブラントが描く人物は、キャストに顔を似せたオリジナルバージョン。これが高く評価され2000年度のセザール賞美術賞を受賞している。ビデオタイトルは「レンブラント」。", "1999-01-01", "2,7,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金色夜叉", "金色夜叉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "“今月今夜のこの月を……”で有名な尾崎紅葉原作の映画化。明治時代、互いに将来を誓い合った恋人同士のお宮と貫一。しかしお宮に富豪の富山が近づき、欲に目がくらんだお宮の両親は嫌がるお宮を一緒に熱海へやってしまう。貫一は怒り守銭奴への道を歩むが……。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TITAN　A.E.", "タイタンA.E.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「アメリカ物語」「アナスタシア」のD・ブルースが3Dと2Dを融合させるという斬新な感覚で描いたSFアニメ。31世紀の未来、銀河系に進出した人類は凶悪なエイリアン種族ドレッドと遭遇し、壮絶な戦争となる。人類は敗北の直前、希望を宇宙船タイタン号に託すが地球は滅亡、人類は宇宙の難民となる。宇宙のスクラップ工場で働くケールは自分の指輪にタイタン号の秘密が隠されているを知り、冒険の旅に出発する。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rent: Filmed Live on Broadway", "レント　ライヴ・オン・ブロードウェイ", "", "", "http://image.eiga.k-img.com/images/movie/54694/315x210.jpg?4195", 152, "1996年にトニー賞を総ナメにし、2005年には映画化もされた傑作ミュージカル「レント」のブロードウェイでのラスト公演を完全収録した作品。1989年、ニューヨークのイースト・ビレッジで毎月の家賃も払えないような生活をおくる若者たちの青春と葛藤を描く。エイズ、ドラッグ、同性愛などショッキングな問題を真正面から捉えた意欲作で、世界各地に「レントヘッド」と呼ばれる熱狂的なファンを持つ。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOSTILE HOSTAGES", "サイレントナイト　こんな人質もうこりごり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "泥棒ガスはクリスマスの夜、豪邸に忍び込むが防犯機に引っかかってしまう。相棒にも逃げられ、しかたなくキャロラインとロイドという夫婦を人質に逃走をはかるが、離婚直前のこの夫婦、人質であることを忘れて大喧嘩を始める始末。イライラしながらも、ようやく二人の家に入り込むガス。しかし、喧嘩は治まらず、そのうえ、ロイドの母親や息子、弟一家までがやってくることが判明。ガスのイライラは、もう爆発寸前となり……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TARZAN’S　SECRET　TREASURE", "ターザンの黄金", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ジョニー・ワイズミュラー主演による第５作。ジェーンと少年と幸せに暮らしていたターザン。しかし、少年が河で金塊を拾ったことから、探検隊のメドフォードにジェーンと少年を誘拐されてしまう。ターザンは二人を助け出そうとするが……。DVDは「ジョニー・ワイズミュラー　ターザン・フィルムズ」に収録。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'Arnacoeur", "ハートブレイカー", "", "", "http://image.eiga.k-img.com/images/movie/56614/315x210.jpg?3730", 105, "ロマン・デュリス、バネッサ・パラディが主演し、本国フランスでヒットしたロマンティックコメディ。別れさせ屋アレックスのポリシーはターゲットとは恋に落ちないこと。次なるターゲットは、ワインの愛好家で10日後にモナコでイギリス人青年実業家と結婚することになっているジュリエットという女性。婚約者との仲も完ぺきでなかなか付け入る隙のないジュリエットに、なんとかして近づいたアレックスだったが……。", "2011-10-29", "2,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スレイヤーズぐれえと", "スレイヤーズぐれえと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "おなじみの美少女魔道士リナと、自称リナの最大のライバル、ナーガの巻き起こす騒動と激闘をコミカルに描いた人気アニメの劇場版第３作。魔力で動く“ゴーレム”人形で知られる町・ストーナーに流れ着いたリナとナーガ。しかし、“ゴーレム”を操り町の覇権を狙う小悪党の争いに、二人は巻き込まれてしまい……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STANLEY AND IRIS", "アイリスへの手紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "夫を失って笑顔を忘れた女と、有能ながらも文盲の男。二人はお互いに惹かれ合い、やがて男は自分の胸の内を筆にすべく、彼女に字を習い始める。R・デ・ニーロとJ・フォンダ、アカデミー賞受賞の２大スターの初顔合わせが見どころのラブ・ストーリー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Quest", "クエスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ヒマラヤ山脈の奥地にある“失われた町”で行われた世界最高の異種格闘技戦“ガンゲン”。ニューヨークのスラムで生き抜いてきたクリストファーは、死闘の末、招待状を手に入れて、戦いの場に乗り込んでいくのだった……。初めて監督業に進出したJ・C・ヴァン・ダムのアクションはもちろんのこと、空手、ムエタイ、ボクシング、テコンドーなどの格闘家たちの戦いぶりも見もの。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本の自転車泥棒", "日本の自転車泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「爽健美茶」など数々の名作CMを手がけてきたCMディレクター、高橋忠和が初メガホンをとり、盗んだ自転車でひたすら走り続ける男の7日間の旅を描いたロードムービー。岩手県釜石市で暮らす製鉄所工員の斎藤鉄男は、ある朝、自分の頭の中で鳴り響いた鐘の音に突き動かされるように自転車を盗んでしまう。思うがままにペダルをこぎ続ける鉄男は、またたく間に釜石を飛びだしていき……。鉄男役に、「ビリケン」（1996）以来10年ぶりの主演となる杉本哲太。", "2006-12-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUNGO 日本文学シネマ　檸檬", "BUNGO 日本文学シネマ　檸檬", "", "", "http://ecx.images-amazon.com/images/I/51V6tWHjxVL._SL210_.jpg", 30, "梶井基次郎の傑作短編「檸檬」を、「純喫茶磯辺」の吉田恵輔監督が映像化。肺を患って不安にさいなまれるカジイは、街をさまよい歩いている途中、果物屋の店先に並べられていたレモンにふと目を留める。檸檬をひとつ買い求めたカジイは、爆弾に見立てたレモンによって自分の不安が吹き飛ばされる様子を思い浮かべていく。主演は「ROOKIES」の佐藤隆太。文豪たちの短編を映像化したテレビドラマ「BUNGO」シリーズの1作。", "2010-05-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("向日葵／SUNFLOWER", "胡同〈フートン〉のひまわり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "変わりゆく現代の中国社会を背景に、父と息子の世代間のギャップを描いた人間ドラマ。「こころの湯」で脚光を浴びたチャン・ヤン監督が、親子の葛藤と絆を味わい深く描く。父親が隠していた意外な事実が判明する結末に、涙を禁じえない。文化大革命末期、９歳のシャンヤンは強制労働から戻ってきた父親に絵の勉強を強要される。不自由を感じつつも画才を発揮する彼だったが、19歳になった時、父への不満は爆発し……。", "2005-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLENTY", "プレンティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "第二次大戦中、レジスタンス運動に参加したイギリス女性は、偶然一夜をともにした男に人生の輝きを見る。終戦後、彼女はその時の輝きを求めてさまようが……。何を犠牲にしてでも、自分の内なる情熱をまっとうしようとする女の愚かしくも激しい生き様を、ストリープが熱演。", "1985-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("微笑みを抱きしめて", "微笑みを抱きしめて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "カナダの児童文学『パパの最後の贈りもの』を、「大阪の章二クン」の瀬藤祝が映像化した感動のヒューマン・ドラマ。物語は28歳の青年、翔太が久し振りに故郷の地を踏みしめたところから始まる。17年前、母親に付き添われて病院から退院した父は、自分がガンに冒されていることを翔太ら子供たちに正直に打ち明けた。そして家族の平和なひと時もつかの間、父親は再び入院して……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIVE GIANTS FROM TEXAS", "５匹の用心棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ゴンザレス３兄弟によって暴力支配をされているメキシコの寒村。そこに住むロザリアは、彼らの従妹でありながら夫と息子を奪われ、自らも辱めを受けてしまう。数年後、テキサスの牧場で働いていた彼女は、町で出会った５人の男に復讐を依頼する……。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEUX D’ARTIFICES", "エリザとエリック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "V・テヴネが、監督・脚本と、マルチ才女ぶりを発揮。映像詩人ジャン・コクトーに捧げた、現代版「恐るべき子供たち」ともいえる作品だ。美しき姉弟エリザとエリックが、パリのアパルトマンでの孤独な二人暮らしで始めたのは、ちょっぴり毒の味のするフォト・コラージュ遊び。様々なモデルが出入りし、新しい愛情が芽ばえるなかで、姉弟の近親相姦にも似た愛に微妙なズレが生じる……。小粋なストリート・ファッション、遊び心に富んだ生活スタイルやモノセクシャルな恋愛ゲームなど、パリの若者ライフが躍動する、おしゃれなモード・エ・シネマ。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAS HURDES", "糧なき土地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 29, "「アンダルシアの犬」「黄金時代」とシュールレアリスム映画を代表する２作を撮り上げた、鬼才ブニュエルによる短編ドキュメント。故国スペインの奥地にある緑もまばらな岩だらけの土地コルデスには、わずかに残った村人がボロをまとって住んでいた。文化から見放された人々の生活を鋭い視点とともに映し出す。", "1933-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("助っ人稼業", "助っ人稼業", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "宍戸扮する黒川哲のケンカっぷりに一目惚れした妖艶な女性・洋子は、警察に拘置されていた哲を引き取る。福田組の助っ人として、関西の小池組の殴り込みに備えるためだった……。しかし、小池組組長が何者かに殺され、哲が犯人にされてしまう。日活の任侠アクション。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("老人の恋　紙の力士", "老人の恋　紙の力士", "", "", "http://image.eiga.k-img.com/images/movie/55674/315x210.jpg?1295", 70, "6人の監督が“性と愛”をテーマに競作した「Love＆Eros CINEMA COLLECTION」の第2作。Vシネマ界で活躍する石川均監督がミッキー・カーチスを主演に迎え、孤独な老人と若い女性ヘルパーの恋をファンタジックに描きだす。妻に先立たれ、息子夫婦との関係も上手くいっていない省吾の元に、ヘルパーのユミがやって来る。省吾はユミと一緒に暮らすうちに、彼女に亡き妻の面影を重ねるようになる。", "2010-10-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SINGING NUN", "歌え！ドミニク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "世界的大ヒットとなった「ドミニク」の生みの親で、修道女のシスター・ナンをD・レイノルズが演じるミュージカル。ギターと歌の好きな尼僧アンが神父の勧めでレコードを吹き込み有名になるが、初心通り伝道と救済のためコンゴへと渡る。無名時代のK・ロスが貧しい下町娘役で出演している。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／TURNING　GATE", "気まぐれな唇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "人間の本質を見透かした鋭い洞察力に定評のある韓国の奇才ホン・サンス監督作。一週間の旅の中で出会った誘惑する女とミステリアスな女の間を彷徨うギョンスは、身勝手にことを押し進めたかと思うと、女々しい態度にもなる。対する女たちは儚いようで、したたかさを持ち合わせる。そんな男と女の本音バトルが絶妙な会話とともに繰り広げられていく。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浪漫殺手自由人／KILLER’S ROMANCE", "ジョイ・ウォンの紅い愛の伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ロンドンに住む日本人ヤクザの大親分を父に持つヤムは、父の葬儀のためロンドンに帰って来た。そこでヤムは、父の右腕の鷹村から、犯人はローという男が率いるチャイナタウンのマフィアだと知らされる。さっそくヤムは復讐を実行、ローとその手下を殺す。しかし、その現場を、ワンという女性に目撃されてしまう。ヤムはやむを得ずワンを殺そうとするが……。意外なヤムの父親の死の真相など、スリリングな展開が光る香港ノワール・アクション。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE YEAR MY VOICE BROKE", "君といた丘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "1962年、オーストラリアの田舎町を舞台に、若い男女の三角関係を描いた作品。高校生のダニーは、一つ年上のフレアに恋をしていた。だが、彼女は不良だがフットボールの花形選手トレバーに惹かれていて……。", "1988-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("永山裕子　薔薇を描く", "永山裕子　薔薇を描く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "みずみずしい独特の色彩の作品で、多くの水彩画ファンの支持を得る画家・永山裕子の創作の過程に密着したドキュメンタリーシリーズ第1作。永山本人による解説とともにバラやダリア、ガラスなどを描く制作過程をカメラに収めた。また、技術面にとどまらず、永山の水彩画に対する考えなども明かされていく。", "2010-10-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEN LITTLE INDIANS", "姿なき殺人者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "英国ミステリー界の女王クリスティの名作『そして誰もいなくなった』の2度目の映画化作品は、孤島の別荘から山中の古城に舞台設定を変えて製作された。登場人物10人全員が死んでしまった。犯人は一体誰だったのかという見事なシチュエーション・ミステリーに忠実に作られていて楽しめる。オーストリアの城に召使二人を含む10人が、何者かに招待され一人ずつ殺されていく……。", "1965-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サラリーマン専科", "サラリーマン専科", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "東海林さだおのマンガを三宅裕司主演で映画化。日向化学工業庶務課長の石橋万作は妻と長男、長女に弟の淳司の５人家族、売れないチェロ弾きの淳司の結婚が心配の種だ。ある日、淳司は迷い犬を返しにいったことから、日向化学の社長宅に出入りするようになる。淳司と万作夫婦は社長の軽井沢の別荘に招待され、そこで社長の姪で世界的なプロゴルファー、ひとみと出会う。驚いたことにひとみが淳司を気に入り、結婚話にまで進むのだった。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天国は待ってくれる", "天国は待ってくれる", "", "", "http://image.eiga.k-img.com/images/movie/33844/315x210.jpg?4815", 105, "「いま、会いにゆきます」など多くのヒット作を手掛けた脚本家・岡田惠和が、男女間の友情をテーマに書き下ろした処女長編小説を映画化。宏樹、薫、武志は子どもの頃からの親友同士。3人の友情はいつまでも続くと思っていた。そんなある日、宏樹の前で武志が薫にプロポーズをし、薫は戸惑いながらもそれを受け入れる。しかしその数日後、不慮の事故が武志を襲い……。主演はV6の井ノ原快彦、「地下鉄に乗って」の岡本綾、元EXILEの清木場俊介。", "2007-02-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE SISTERS", "三人姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 155, "単調な田園生活に安住できず、モスクワに行くことを唯一の夢として生きる３人姉妹。父亡きあとのプローゾロフ家を託された長男アンドレイと、その妻となり変貌していくナターシャ。19世紀末のロシアを舞台にしたチェーホフの名作をオリヴィエが華麗に映像化。", "1970-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あんにょんキムチ", "あんにょんキムチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "韓国人の血を引きながらもキムチが食べられないという松江哲明監督が、日本映画学校の卒業制作として完成させた笑いと涙のドキュメンタリー。戦争の頃に韓国から日本にやってきた祖父が、「哲明、バカヤロー！」と言い残して死んだことが気になり、これまで何となくネガティブなイメージを抱いていた韓国について考えるようになった松江哲明。彼はデジカムを抱えて親戚の元を訪ね歩き、自分と家族のルーツを調べていく。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUN VALLEY SERENADE", "銀嶺セレナーデ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "当時の冬季オリンピックの人気フィギュア・スケーター、ソニアが主演し、ジャズ楽団の名指揮者G・ミラーも出演したミュージカル調のラブ・ロマンス映画。青年ピアニストを巡り女歌手と女スケーターが張り合う三角関係で、軽コメディとしても楽しめる。流行の女歌手が楽団と共にサン・ヴァレイへ演奏旅行に出たが、ノルウェーの外人女も一緒に行くことになり……。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　STUPIDS", "ジョン・ランディスの　ステューピッドおばかっち　地球防衛大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「ブルース・ブラザース」の奇才J・ランディスのナンセンス・コメディ。妻と二人の子供とともに郊外の一軒家に暮らすスタンリー・ステューピッドは、我が家のゴミが盗まれていると勘違いしゴミ収集車を尾行。ゴミの集積場にやって来た彼は、そこで兵器の裏取引現場を目撃してしまう。なぜかCIAのキレ者と間違えられ、命が狙われているとも知らずにゴミ泥棒の追跡に燃えるスタンリー。勝手な想像をめぐらし、これが国家的陰謀だと思い込んだ彼は、戦いを決意する。奇想天外なストーリーに古典的なギャグが満載。悪の総師に名優クリストファー・リー、郵便局員に映画監督のデヴィッド・クローネンバーグが扮している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUBBA HO−TEP", "プレスリーVSミイラ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "エルヴィス・プレスリーが、老人ホームで生きていたら……という奇想天外な設定に基づくホラー・コメディ。ソックリさんと入れ替わって生き続けていたプレスリーが暮らす老人ホームで、住人の怪死事件が続発。自称・JFKの黒人ジャックは、事件がミイラ男の仕業だと突き止める。そこでプレスリーはミイラ男に立ち向かうが……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("精武家庭／HOUSE OF FURY", "ドラゴン・プロジェクト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "カンフーの達人である一家の悪との戦いを描いたアクション・コメディ。監督・主演を兼任した香港の若手スター、スティーブン・フォンが、肉弾戦や棒術の見せ場をパワフルに演出する。どこかトボケたキャラクターの描写も妙味。整体師ユーは、かつては敏腕諜報員。息子のニッキーと愛娘ナタリーは、そんな父の自慢話を信じようとしない。やがて父が何者かに誘拐されたことから、彼らは決死の救出に挑む。", "2006-06-03", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PORKY’S REVENGE", "ポーキーズ 最後の反撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "人気シリーズ第３作。おなじみの連中もいよいよ卒業。だがその前に決着をつけねばならない相手がいる。“ポーキーズ”のオヤジだ。前回、店を破壊された彼の恨みはひとしおで……。相変わらずのハチャメチャぶりに、思わず拍手!?", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("碧血藍天／THE　BLACKSHEEP　AFFAIR", "スティル・ブラック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "中国特殊工作員と武装したカルト教団の壮絶な闘いを描く痛快アクション。中国特殊工作員のドンは上司の命令に背いたため、旧ソ連ラベニア駐在大使のボディガードに左遷させられる。そこで、国際テロリストでカルト教団の教主ミシマの逮捕に成功した彼はやがて、天安門事件の際、国外逃亡した恋人パンと再会。彼女の裏切り行為を許せずに複雑な思いにかられる彼は、教主奪還のためテロ行為に走った教徒たちと死闘を繰り広げる。“ワンス・アポン・ア・タイム・イン・チャイナ”シリーズや「ブレード／刀」のチウ・マンチェクが、足技、剣術を駆使して挑む格闘シーンが迫力満点。", "1998-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トラック野郎・度胸一番星", "トラック野郎・度胸一番星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "新潟へ向かう桃次郎は謎の女に一目惚れ。実は彼女、佐渡島の分教場の先生だった。砂金採りにハッスルする桃次郎だが、彼女の結婚を聞いてガッカリ。その失恋のうさ晴らしに同業の暴れ者・譲治の率いる“ジョーズ軍団”と大ゲンカ……。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Whales of August", "八月の鯨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ベティ・デイビス、リリアン・ギッシュの2大女優競演によるヒューマン・ドラマ。メイン州の小さな島にある別荘で毎夏を過ごしている年老いた姉妹リビーとセーラ。島の入り江には8月になると鯨が現れ、幼い頃は良く見に行ったものだった……。潮風の香る美しい島を舞台に、老姉妹と、彼女たちを取り巻く3人の老人たちが織りなす人間模様を穏やかに描く。本作が遺作となったリリアン・ギッシュは、カンヌ国際映画祭特別賞を受賞。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BERLIN，DIE SINFONIE DER GROβSTADT", "伯林〈ベルリン〉—大都会交響曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "1920年代のベルリンという大都会のダイナミズムを再現したドキュメンタリー映画。文字通り都市の一日を作品の題材としており、早朝から深夜までを様々な角度から捉え、それらを再編集。ドイツ絶対映画の重鎮でもあったW・ルットマンは、映画の中に何らかのストーリー性を持ち込むことなく、都市の一日を純粋な光と影、そして運動の形態へと捉え直す。それによって、映画が本来もち得るはずの映像の純粋さと視覚の美しさを追求し、その中に都市の印象を再創造しようとした。めまぐるしいフラッシュバックやスピード感など、マノエル・ド・オリヴェイラ監督の傑作「ドウロ河」などにも影響を与えたとされる、編集（W・ルットマン自身が担当）が特筆される。", "1927-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤道の花", "赤道の花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "絶えず愛を求めてさまよう女ソヨンと、彼女に一目惚れし、ミスターMと名乗って彼女を見守る、自閉症的青年。ソヨンは快楽の道具にされる女であったが、ミスターMにとっては理想的な美しい女性だった。物語は悲劇へと向かっていく。「黒水仙」のペ・チャンホ監督による青春ニューシネマ。アジア・太平洋映画祭で監督賞を受賞。", "1983-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千年女妖／CHASE FROM BEYOND", "チェイス・フロム・ビヨンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「チャイニーズ・ゴースト・ストーリー」の大ヒットで一躍スターダムにのし上がったジョイ・ウォンと、「孔雀王／アシュラ伝説」に主演したグロリア・イップ。二大美女スターが競演するSFXアドベンチャー。二人は、1000年の時を超えて、邪悪な魔女と戦う聖女に扮し大活躍をみせる。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鍋島怪猫傳", "鍋島怪猫傳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「丹下左膳」でお馴染み、大河内伝次郎主演の時代劇ホラー。試合をしたら必ず一方が死ぬ、という噂のある碁盤があった。それを殿に献上したいという田沼勘兵衛の熱意に負けて、持ち主の金兵衛はこの因縁の碁盤を手放す。しかしそれと同時にお城の人間が次々に殺害され、城下では怪猫のしわざではないかという噂が立つ。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秋立ちぬ", "秋立ちぬ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "女性映画で名高い成瀬巳喜男監督が、前年の「コタンの口笛」に続いて子供を主人公にした作品。父親を亡くして母の実家に引きとられた少年と近所に住む妾の娘との淡い交流を描く。少女の描写に、のちの成瀬映画のヒロイン的要素がうかがわれ、興味深い。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRAWLSPACE", "クロール・スペース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "美女ばかりを住まわせるアパートのオーナーは、各部屋の通気口から間借り人をのぞくことを趣味としている。しかし殺人淫楽症のその男は、のぞくことにとどまらず間借り人たちを次々と奇妙な方法で殺すことを最大の楽しみとしていた。「ノスフェラトゥ」の名優キンスキーが怪演。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オルゴール", "オルゴール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "TVドラマ『とんぼ』の脚本家・黒土三男が、同番組でも主演していた長渕剛を起用して撮り上げた監督デビュー作。一匹狼のヤクザが、離れ離れになった幼い息子との関係を取り戻そうとする姿を、組織との抗争も絡めて描いている。長渕剛が歌ったテーマ曲『激愛』もヒットを記録。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　ゴミラの星", "それいけ！アンパンマン　ゴミラの星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "人気シリーズの劇場版第13作。ゴミだらけになってしまったヤーダ星のプリンセス、ヤーダ姫がアンパンマンに助けを求めてやってきた。そこでアンパンマンは、ゴミが大好きなゴミラと一緒にヤーダ星へ。ところが星では、ばいきんまんが作り出した、強力なゴミ再生怪獣ダストデーモンが大暴れしていた。アンパンマン＆ゴミラVSばいきんまん＆ダストデーモンの手に汗握る大バトルと、ゴミラとヤーダ姫の恋と友情を描く。酒井法子がヤーダ姫の声を担当。", "2001-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUDYARD KIPLING’S THE JUNGLE BOOK", "ジャングル・ブック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "不運な事故により、たった一人でインドの険しいジャングルに取り残されてしまった５歳の少年モーグリ。彼は野生の動物たちと心を通い合わせ、たくましい青年に育っていく。20年近い年月が経ち、ジャングルのリーダーになったモーグリは、ふとしたことからかつての事故で離れ離れになったキティと再会する。彼女を追って華やかな近代文明に飛び込んだモーグリは、人間社会の中で様々なことを学んでいくうちに、恋愛という感情を知る。一方、モーグリが街へ持ってきた古代遺跡の短剣を見つけたブーン大尉は、ジャングルの中に眠る財宝を我が物にしようとたくらんでいた……。ディズニー社による二度のアニメ映画化で人気を博したR・キップリングの小説の初版から100年を記念して作られた、実写作品。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pay It Forward", "ペイ・フォワード　可能の王国", "", "", "http://image.eiga.k-img.com/images/movie/1144/315x210.jpg?3275", 123, "社会科の先生が中学生たちに課題を与える。「もしきみたちが世界を変えたいと思ったら、何をするか？」。母親のアル中を気に病み、近所にたむろするホームレスを気遣うトレバー少年は、そんな、自分の周りの決して幸せとは言えない人々に思いを馳せ、ある考えを思いつく。それは、受けた好意を他人に贈る“ペイ・フォワード”という行動だった。", "2001-02-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("その後の蜂の巣の子供たち", "その後の蜂の巣の子供たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "1948年の「蜂の巣の子供たち」の続編。清水宏の子供の扱い方はますます冴えて、劇的な盛り上げを見せ、“蜂の巣”に暮らす子供たちの日常の情景の積み重ねだけで１本の作品を創出している。独特な構図の作り方にも見るべきものがある。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("砂の器", "砂の器", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "監督・野村芳太郎、脚本・橋本忍のコンビによる松本清張原作の傑作社会派サスペンス。迷宮入りと思われた殺人事件を捜査する二人の刑事の執念と、暗い過去を背負うために殺人を犯してしまう犯人の宿命を描く。後半、二人の刑事がすべてを明らかにする捜査会議と、犯人である天才音楽家が自ら指揮する交響曲の発表演奏会、そして彼の暗い過去である、北陸、若狭、出雲と巡る父と子の道行きの回想シーンが、人形浄瑠璃にヒントを得たカット・バックで描かれる構成は見事。また、そこで演奏される交響曲『宿命』は、四季折々を捉えた映像と相まってたぐいまれな感動を生んでいる。2005年にデジタルリマスター版が公開された。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋化粧", "恋化粧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "今日出海原作『君死に給うことなかれ』の映画化。1954年、日本初のSF大作「ゴジラ」を大ヒットさせた本多猪四郎が、その次回作として撮り上げた作品。とはいえ、これは特撮ものではない純然たる文芸映画。自分の会社の自動車盗難事件の犯人を追う力彌と、彼を慕う芸者・初子の恋の行方を描いている。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自動車泥棒", "自動車泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "スピード感のあるカッティング、街頭撮影のライブ感、エネルギッシュでドライな演出に和田監督デビュー作の才気があふれる。敗戦の落とし子、混血児たちが学ぶミッション系寄宿学校。悪ガキの一人、酋長の夢は、自分たちで組み立てた自動車でアフリカへ行くことだった。一台のポンコツ車を組み立てて横須賀に繰り出すが……。浜辺に追いつめられた彼らは遠い大陸を目指すヨットを幻視する。それがカラーとなって現れるさまが鮮烈だ。チンピラのうっ屈したエネルギーを踊りに込める「ウエスト・サイド物語」風のシーンも捨て難い。これをバーンスタイン風にきめてみせる武満徹の音楽も珍品。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Robin Hood", "ロビン・フッド", "", "", "http://image.eiga.k-img.com/images/movie/53234/315x210.jpg?2507", 140, "「グラディエーター」のリドリー・スコット監督＆ラッセル・クロウが、中世英国の伝説上の義賊ロビン・フッドの闘いを描いた歴史活劇。12世紀末、十字軍の兵士としてフランスで戦っていたロビンは、帰国途上で英国の騎士ロクスリーの暗殺に遭遇する。「家宝の剣を故郷に持ち帰って欲しい」というロクスリーの遺言を受け、彼の父親が領主を務めるノッティンガムを訪れたロビンだったが、やがて英国侵略を目論むフランスの陰謀に巻き込まれていく。", "2010-12-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sorority Row", "スプラッター・ナイト　新・血塗られた女子寮", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏の庭", "夏の庭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "神戸に住むサッカー少年の諄、メガネ小僧の河辺、デブの山下は、同じ学校に通う小学６年生の仲良し３人組。その夏、彼らは“人が死んだらどうなるか”という好奇心から、近所の“いかにも死にそうな”一人暮らしのおじいさんを見張ることを決めた。世捨て人のように暮らすその老人・傳法喜八は、初めこそ子供たちを邪険に扱うものの、次第に優しい素顔で接するようになる。子供らはゴミだらけの庭を片づけてコスモスを植え、廃虚のようだった家にきれいなペンキを塗った。４人はすっかり友達のようになり、おじいさんの哀しい戦争体験を聞き、別れた奥さんとの再会のために奔走するが……。「お引越し」に続いて、相米慎二が関西で撮ったキッズムービー。例によってオーディションで選ばれた３人の少年たちが、実にいきいきとした演技で笑わせ泣かせる。また子供らに随所で食われまくるものの、互角に渡り合う三國連太郎もさすが。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("楽園", "楽園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "九州の島を舞台に、人が他人にあたえる影響を温かく、淡々と綴った人間ドラマ。登校拒否の高校生、シズエは親元を離れ、船大工の祖父と暮らしている。ある日、獅子舞一座を抜け出した若者シンジが祖父と親しくなり、居候することに。木造の船を作っている老人をシンジは手伝い始め、最初は冷やかに見ていたシズエも、しだいに彼に心を開いていく。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キッズ・リターン", "キッズ・リターン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "カンヌ映画祭監督週間正式出品作品として上映され、絶賛された北野武監督の第６作。また、これまで興行的には芳しくなかった北野作品だったが、この作品は成功を収めた。18歳のシンジとマサルはいつもつるんで遊んでいた。冬になり受験シーズンが始まるころ、二人はラーメン屋でヤクザに絡まれたところを若頭に助けられ、マサルは尊敬のまなざしを送る。ある夜、以前カツアゲした高校生から呼び出され出かけていくと、助っ人の男にマサルは簡単にのされてしまう。次の朝から、マサルはボクシングジムに入りシンジもマサルに誘われるままにジムに入る。しかし、素質を見込まれたのはシンジのほうだった。シンジはプロデビューをし、シンジにスパーリングで負けたマサルはジムを辞めてしまう。そして、以前出会った若頭を頼ってヤクザの世界に入るのだった。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TONY ROME", "トニー・ローム 殺しの追跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "大スターのシナトラが私立探偵役を演じ、多彩な登場人物たちの人間関係と殺人事件を探っていくハードボイルド・タッチのサスペンス&ミステリーの秀作。競馬と女に夢中で仕事を全くしていない探偵トニーだがある日、億万長者から娘の素行調査を、そして娘からはダイヤ探しの依頼を受ける。そんな矢先、昔の相棒が殺されてしまい、やがて複雑な人間関係が次第に明るみになっていく。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Morning After", "モーニングアフター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "　女優のアレックスはその朝、二日酔いで目を覚ます。そんな彼女の傍らには、心臓にナイフを突き立てられた男の死体が。酒のせいで前夜の記憶が全くないアレックスは彼が何者かもわからない。混乱しながら男の住まいから逃げ出したアレックスを、元・警官のターナーが助けることに。男を殺したのは、アレックスか、あるいは別人なのか？　ジェーン・フォンダがまたも名演技を披露し、見る者を惹きつけるサスペンス・スリラー。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gladiator", "グラディエーター", "", "", "http://ecx.images-amazon.com/images/I/51Z9--67hrL._SL210_.jpg", 155, "キレのいいアクションと独自の映像世界で異彩を放つR・スコットによるスペクタクル活劇。西暦180年、大ローマ帝国。戦争の英雄マキシマスはローマ皇帝の厚い信頼を得て、時期皇帝に指名される。だが、冷酷非道な皇帝の息子コモドゥスが、皇帝の座を手に入れるためにマキシマスの暗殺を画策。かろうじて生き延びたマキシマスだが、妻子を惨殺されたうえ、剣闘士奴隷として売り飛ばされる。巨大コロシアムでの生死を賭けた闘いを強いられた彼は、コモドゥスへの復讐の機会をうかがいながら次々と放たれる強敵に挑んでいく。怒りを胸に死闘を繰り広げる主人公をR・クロウが貫禄たっぷりに熱演して、オスカー受賞。忠実に再現された巨大コロシアムでの、戦闘シーンの振り付けを「ブレイブハート」などを手掛けたニコラス・パウエルが担当。壮絶極まりない接近戦の数々で圧倒する。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESINGN FOR LIVING", "生活の設計", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "パリ留学中の画家ジョージと劇作家トム。二人のアメリカ人青年は、ある旅行帰りの列車に乗り合わせたジルダに同時に一目惚れする。彼らに好意を寄せたジルダは、肉体的接触を禁じるという紳士協定のもと、トムとジョージの住むアトリエに同居することになるが……。名匠E・ルビッチ監督による抱腹絶倒の快作。男女の三角関係のドラマを得意としたルビッチの原点ともいえる作品で、後世の映画作家たちにも多大な影響を与えた。ジョージ役のG・クーパーは恋愛ものが苦手だったが、この作品で二枚目に開花したという逸話も残っている。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARAKIMENTARI", "アラキメンタリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ビョークや北野武など、世界的なトップアーティストが賞賛を惜しまない写真家・荒木経惟(通称アラーキー)。日本が誇る芸術家でありながら、世間的には“ヘンな髪型をしたスケベなオッサン”と認識されている怪人物の素顔に迫った異色のドキュメンタリー。アラーキーマニアを自認するアメリカの映像作家トラヴィス・クローゼ監督のもと、音楽担当のDJ KRUSHら異色のメンツが参加。肩肘張らないポップなノリが楽しい。ユーモアと感動を兼ね備えた逸品だ。'03年夏、映像作家であるクローゼは荒木経惟の密着取材を敢行。彼のホームグラウンドである夜の新宿を駆け巡る。一方、北野武やビョーク、森山大道らが荒木の魅力を語る。", "2005-03-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナイン・ソウルズ", "ナイン・ソウルズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "「青い春」の豊田利晃の監督第４作。９人の脱獄者の逃避行を、絶望と希望、哀切と痛切が交錯する映像タッチで描く。「青い春」の松田龍平、「ポルノスター」の千原浩史、鬼丸に加え、ベテラン原田芳雄も参加した。父親殺しで服役した金子未散は、そこで９人の男と出会う。元偽札王と名乗る山本は富士山麓に大金を隠したという秘密を打ち明けた後、精神が錯乱。残された９人はその金を目指して脱走する。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CAVE SE REBIFFE", "親分は反抗する", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ギャング界の大物が、仲間たちを誘って昔からの夢であったニセ金作りに乗り出す。しかし、仲間の一人が裏切り、ニセ金を奪って逃走。激怒した大親分は……。ギャングの世界に生きる男たちの、粋で危険なゲームをユーモアとノスタルジーを織り交ぜて描いたフィルム・ノワール。", "1961-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VERGINE DI NORIMBERGA", "バック・トゥ・ザ・キラー 顔のない殺人鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ショッキング・ホラーの古典的秀作。ニュールンベルグの古城のもとに、新妻として嫁いできたアメリカ人女性。だが、その城にはナチスの人体実験によって醜い姿となった男が、殺人鬼と化して待ち受けていた……。中世の拷問具など、不気味な道具立てが雰囲気を盛り上げる。", "1963-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大人には分らない 青春白書", "大人には分らない 青春白書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "政治家の父親と学生バンドのドラマーの息子を対照的に描き出し、新旧世代のモラル観の溝と台頭する若者風俗に光を当てた、27歳の新人監督・須川のデビュー作。大学生の忠は学生バンドの歌手・葉子と恋仲だ。葉子の友だち・真理子は妾の子でバイトをしながら自活する身。ひょんなことから、忠と真理子の父親が同じ代議士の竜太郎と発覚、忠は親への反感から葉子との無理心中を期して湘南へ向かう。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("胎児が密猟する時", "胎児が密猟する時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ピンク映画界の雄・若松孝二監督による衝撃的傑作。性的変質者の中年男が一人の若い女を自分のアパートに誘い込み、彼女を縛り上げてサディスティックにいたぶる。朝も夜も縛られ殴られて女は半死半生になるが、ついに縄をほどいてナイフで男を刺し殺す。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("10,000 B.C.", "紀元前1万年", "", "", "http://image.eiga.k-img.com/images/movie/53344/315x210.jpg?2252", 109, "氷河期が終わりに近づこうとしている頃、狩猟民族のヤガル族の村を謎の集団が襲い、多くの村人を捕らえて去っていった。若きハンターのデレーは、将来を誓い合ったエバレットを救うため、遥か彼方の地を目指して旅に出るが、そこには見たこともない生物や文明の脅威が待ち受けていた。「インデペンデンス・デイ」「デイ・アフター・トゥモロー」のローランド・エメリッヒ監督が、紀元前1万年前の地球を舞台に、青年の冒険と成長を描くスペクタクル・アクション。", "2008-04-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛染かつら（総集篇）", "愛染かつら（総集篇）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "戦後の「君の名は」とともに絶大な人気を誇った“すれ違いメロドラマ”の代表作。前編・後編・続編・完結編と続き、「花も嵐も踏み越えて……」で有名な主題歌「旅の夜風」は大ヒットした。これら４編をまとめたのが総集編で、子持ちの未亡人看護婦と青年医師が恋に落ち、身分の違いや子供の病気、様々なすれ違いといった障害を乗り越えて、ついに結ばれるまでをまとめている。戦後には「新・愛染かつら」（1948・大映、久松静児監督、水戸光子・竜崎一郎）「愛染かつら」(1954・大映、木村恵吾監督、京マチ子・鶴田浩二)「愛染かつら(正・続)」(1962・松竹、中村登監督、岡田茉莉子・吉田輝雄）がリメイクされた。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sammy and Rosie Get Laid", "サミー＆ロージィ／それぞれの不倫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ロンドンに住むインド人サミーとその妻ロージー。2人の仲は表面的にはうまくいっているように見えたが、サミーにはアナという愛人がいた。ある日、サミーの父ラフィが彼らを訪ねてインドからやって来るが……。彼ら3人がそれぞれの愛と人生に悩む姿を通して、1980年代のイギリスにおける人種問題を浮き彫りにしたヒューマンドラマ。メガホンを取るのは「マイ・ビューティフル・ランドレット」のスティーブン・フリアーズ監督。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PURE AMERICA", "アメリカのように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "東京国際映画祭ヤングシネマ1985で最優秀監督賞を受賞したP・ゴタール監督が、その賞金をもとに製作した作品。ニューヨーク観光にやって来たハンガリー人一家の夫は、衝動的に妻子を捨て放浪生活を始めるが……。おかしくも皮肉を込めて綴られる異邦人から見たN.Y.の情景が興味深い。", "1987-01-01", "30,7,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダブル・クラッチ", "ダブル・クラッチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "姉思いの弟が、姉の妊娠を嫌ってわざと悪路をドライブし流産させた男に対して、復讐を遂げるまでを描いた、青春アクション。不良っぽい弟役に郷ひろみ、薄幸の姉に松坂慶子、誠実そうな裏で卑劣さを秘める男に地井武男と、それぞれが巧みな演技で作品を盛り上げている。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE VOYAGE EN BALLON", "素晴らしい風船旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「白い馬」「赤い風船」で童心の世界を見事に視覚化したラモリス監督の作品。パリに住む老科学者が、大空散策用の気球を発明し、実験旅行に出かける。だが一人で旅立つはずの気球に好奇心旺盛な彼の孫が同乗してしまう。二人を乗せた気球は北フランスのリールからパリを経てアルプスへ……。本編でヘリコプターからの撮影方法を確立した監督は、鮮やかな色彩撮影とともに、気球から望まれる自然と動物たちの姿を素晴らしい映像で見せてくれる。都会の喧噪を離れた大自然に気球が悠々と入っていくさまは感動的。ラモリス監督の傑作。", "1960-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　らくだ", "シネマ歌舞伎　らくだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "歌舞伎の舞台公演をスクリーンで上映する「シネマ歌舞伎」の第7弾。岡鬼太郎作の古典落語「らくだ」と2本立て上映。傑作古典落語を題材に、中村勘三郎演じるの久六と坂東三津五郎が扮する半次のコンビが巻き起こす騒動を描く。山田洋次が監督を務めた「連獅子」との2本立て上映。", "2008-12-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vantage Point", "バンテージ・ポイント", "", "", "http://image.eiga.k-img.com/images/movie/53134/315x210.jpg?1951", 90, "スペインで開催されたテロ撲滅の国際サミットに出席したアシュトン米大統領が、大群衆の前で何者かの凶弾に倒れ、続けて現場に仕掛けられていた爆弾が爆発。大統領の警護に当たっていたシークレットサービスのバーンズは、すぐさま犯人の捜索を開始するが……。大統領暗殺の陰謀を複数の異なる視点から描くアクション・サスペンス。監督はTVドラマ出身のピート・トラビス。出演は「LOST」のマシュー・フォックスに、デニス・クエイド、シガーニー・ウィーバーら。", "2008-03-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN NOMME´ LA ROCCA", "勝負〈カタ〉をつけろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "原作者J・ジョヴァンニが自らの監獄時代に知り合った一匹狼をモデルにした正統派暗黒映画。「勝手にしやがれ」で人気絶頂だったベルモンドが主人公を熱演している。後年、同じ原作を、同じベルモンド主演でジョヴァンニが監督した作品が「ラ・スクムーン」である。", "1961-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("葉問2", "イップ・マン　葉問", "", "", "http://image.eiga.k-img.com/images/movie/55774/315x210.jpg?4482", 109, "ブルース・リーの師匠として知られる詠春拳の達人イップ・マン（葉問）の半生を描く伝記アクション。1949年、家族を連れて香港に移住したイップ・マンは、詠春拳を広めるため、とある新聞社の一角に道場を開く。様々な流派の道場主たちとの対決を通してイップ・マンの名は広く知られるようになり、彼のもとに多くの弟子が集まってくるが……。ハリウッドでも活躍する香港のアクションスター、ドニー・イェンが主演を務める。", "2011-01-22", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男が命を賭ける時", "男が命を賭ける時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "大型貨物船の船医をしていた小室丈太郎は、やっと小さな医院を建てる資金を貯め、東京に向かった。ところが、その途中立ち寄った伊豆で殺人事件に巻き込まれる。それが鉄道架設工事の落札に関したものと知った彼は、得意の射撃を生かし、悪に挑んでいく……。ムード・アクションに加え、ムード・サスペンス・モードもある。そんな職人芸に定評がある松尾昭典監督、初期の一編。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tokyo Joe: The Man Who Brought Down the Chicago Mob", "TOKYO JOE マフィアを売った男", "", "", "http://image.eiga.k-img.com/images/movie/54044/315x210.jpg?3793", 93, "アメリカ犯罪史上、唯一の日系人マフィア、ケン・エトーの生涯を追ったドキュメンタリー。元FBI捜査官が“TOKYO JOE”と呼ばれたケンの知られざる生涯と、当時のシカゴ・マフィア壊滅作戦の全貌を明かす。ケンのニックネーム“TOKYO JOE”とは、1949年に公開されたハンフリー・ボガート主演の「東京ジョー」に由来するといわれている。", "2008-12-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅がゆく", "修羅がゆく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "『漫画ゴラク』掲載の人気劇画を映画化。跡目をめぐる極道たちの、激しい争いを描く。岸田組傘下にある本郷組組長・本郷流一は、親分を見舞うため車を走らせていた。その頃病室で、同じく傘下の伊能組組長に、跡目は本郷に継がせると語る親分・岸田巌。跡目を狙う伊能はその場で岸田を射殺し、訪れた本郷に汚名を着せる。さらに兄弟分の京本錠児に刺客を命じるが、京本が事実を知って失敗。しかし伊能はあきらめず、次の罠を本郷に仕掛ける。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北壁に舞う", "北壁に舞う", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "世界三大北壁の全ての登頂に成功した、名登山家・長谷川恒男の半生を感動的に描いた山岳ドキュメンタリー。最も難易度が高いとされるグランドジョラス北壁の単独登はんの成功は、1979年の大きな話題に。森田勝、勝野惇司ら有名クライマーたちと頂上を目指しながらも森田が遭難、勝野は登頂を断念する。長谷川はその後、単独で挑み前人未踏の快挙を成し遂げる。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA DONNA NEL MONDO", "世界女族物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「世界残酷物語」で名を馳せたG・ヤコペッティが再び同じスタッフで作り上げた世界の女性風俗・文化ドキュメント。イスラエルの女性兵士、パリの女性専用クラブから、中国の娘たちの生態などまで、世界中の一風変わった女たちの姿を捉えている。", "1962-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("社長繁盛記", "社長繁盛記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "高山物産社長の圭太郎は老け込んでいると人から言われ、社員を含めた若返り策を打ち出した。３人の営業部員たちは急きょ西ドイツや香港との取引をまとめることを社長から指示されて大あわて。社長自らは若返りに女性が必要とあって、そちらの方も忙しい。登場人物の“老化”がテーマになるなど、シリーズとしての転換期を感じさせる一方で、社長秘書役の黒沢年男の若いパワーが強調されている。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOTTING　HILL", "ノッティングヒルの恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "ごく平凡な男性と世界的に有名な大女優との恋の行方を描く、ロマンティックなラブ・ストーリー。ロンドンの小さな街ノッティングヒル、ウィリアムが経営する旅行専門の小さな書店に、スター女優のアナがやって来る。憧れの女優との偶然の出会いに感激するウィリアムと彼の誠実な人柄に触れたアナは互いに惹かれ合う。彼らは仕事の合間をぬってデートを重ね、愛を育んでいくが、ある日アナのボーイフレンドに遭遇する……。ウィリアムは一人で生きることを決意するが、半年後アナは戻ってきた。しかしその幸せもつかの間、二人はマスコミの標的にされてしまう。スターのオーラと女性の優しさを兼ね備えたヒロインに扮するJ・ロバーツがハマリ役。H・グラントも奇跡的な恋に戸惑う男性をユーモラスに好演。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・宮本武蔵 一乗寺の決闘", "続・宮本武蔵 一乗寺の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「宮本武蔵」の続編で、鶴田浩二の佐々木小次郎が初登場する。鎖鎌の宍戸梅軒との死闘を皮切りに、京八流吉岡一門と、洛北蓮台寺野、蓮華王院、三十三間堂、そして一乗寺下り松と、血で血を洗う文字通り“血闘”を繰り返す。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Clockwork Orange", "時計じかけのオレンジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "　原作者のアンソニー・バージェス自身が”危険な本”と語った同名の小説を映像化。非行少年による暴力が横行する近未来のロンドン。アレックスも仲間を引き連れ、喧嘩とレイプに明け暮れる日々を過ごしている。ある夜、中年女性を死に至らしめた彼は刑務所行きに。しかし2年後、とある治療法の被験者になることを条件に、社会に戻ることを許されるが……。アレックスが心酔するベートーベンの第9交響曲や、レイプシーンに流れる「雨に唄えば」など、音楽による効果的な演出が随所に見られる。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE VIRGINIAN", "落日の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ワイオミングにやって来た少女モリーは、バジニアンとその相棒スティーブと知り合う。スティーブは町の悪漢トランパスに引き込まれて悪事を重ねていた。トランパスはことあるごとにバジニアンと対立しており、ついに対決の時がやってきたが……。西部を舞台に繰り広げられる、男たちのドラマ。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("9月11日", "9月11日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "自ら理想とする介護を実現させるため、若者たちが立ち上げた7つの施設・事業所の日常と、彼らが主催・出演したイベント「Love & Peace & Care 2010 in ヒロシマ　介護バカの集い」の模様を記録したドキュメンタリー。2010年9月11日、自らを「介護バカ」と呼ぶ若者たちが広島に集まり、トークセッションが繰り広げられる。人が老い、死んでいく現実に日々向き合う若者たちが、新しい生き方や価値観を模索していく。", "2010-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MASCHERA DEL DEMONIO", "血ぬられた墓標", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "18世紀の初め、魔術を使ったため王の手で処刑された東欧の王女が、19世紀になって石棺の中からよみがえる。王女は自分の孫の姪にあたる娘に変身し、王の子孫たちに復讐を遂げていくが……。スティールが二役を熱演。", "1960-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵コナン／　銀翼の奇術師〈マジシャン〉", "名探偵コナン／　銀翼の奇術師〈マジシャン〉", "", "", "http://ecx.images-amazon.com/images/I/51FfjNpxxvL._SL210_.jpg", 108, "1994年に『週刊サンデー』で連載が始まり、アニメ化、劇場版の大ヒットと、不動の人気を築き上げている“名探偵コナン”シリーズの劇場版第８作。昨年からフルデジタルとなったその映像は、さらにダイナミックに。東京の新名所・シオサイトの摩天楼を舞台に、コナンと宿敵・怪盗キッドが冒頭から惜しみなく繰り広げる空中バトルは迫力満点。舞台女優・牧樹里から、毛利探偵事務所に仕事の依頼が。樹里のもとに、怪盗キッドから“スターサファイアの指輪を奪う”という犯行予告状が届いたという。そして宿命のライバル・キッドが驚くべき変装で現れ……。", "2004-04-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("激動の昭和史・沖縄決戦", "激動の昭和史・沖縄決戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 149, "沖縄戦における10万の軍人と15万の民間人の運命を描いた壮大な戦争スペクタクル。敗戦色濃厚でもなお抵抗する、勤皇隊員たちの毅然とした態度が印象的。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春の殺人者", "青春の殺人者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "父母の厳格な教育方針と溺愛のなかで身動きできなくなった一人の青年。彼がついに父をそして母を殺し、社会から疎外されていくまでを、冷徹かつ衝撃的に描いた長谷川和彦のデビュー作。音楽を効果的に使ったシーンが満載。中上健次の短編『蛇淫』の映画化で、製作に今村昌平、脚本に田村孟、撮影に鈴木達夫と、新人としては異例の超一流スタッフが脇を固めた。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THIN ICE", "氷上乱舞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "オリンピックで３期連続金メダルを受賞したノルウェーのフィギュアスケート選手S・ヘニーが主演したコメディ・タッチのラブ・ストーリー。スイスの片田舎の村のホテルに某国の王子ルドルフ一行が投宿することになり、村は大騒ぎに。そんな折、ホテルでスケート教師をしているリリは新聞記者のルーディと恋に落ちるが、そのルーディこそ身分を隠した王子ルドルフだった……。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARY SHELLY'S FRANKENSTEIN", "フランケンシュタイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "1992年に「ドラキュラ」を製作・監督したコッポラが、K・ブラナーを監督・主演に起用した英国調ゴシック趣味あふれる古典的ホラー。ヴィクター・フランケンシュタインは母の死を悲しみ、自ら科学者となって永遠の命を見つけ出すことを心に誓い、同じ研究を進めていたウォルドマン教授の弟子となる。ある日、コレラの治療を受けに来ていた男と教授が口論となり、教授は殺されてしまう。教授の書き残したノートを見つけたヴィクターは、絞首刑にされた男の死体を手に入れ、他の死体から切り離した足を繋ぎ合わせて羊水につけると、嵐の夜を待った。稲妻のエネルギーによって電気ショックを与えられた死体は……。人間によって生を与えられながら、醜い姿ゆえ誰にも受け入れてもらえない哀れなクリーチャーに扮したデ・ニーロの演技は鬼気迫る迫力。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BU・SU", "BU・SU", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "CMクリエイターとして知られ、1985年、1986年に日本新語・流行語大賞を２年連続で受賞した市川準の監督デビュー作。森下麦子はどこにでもいるフツーの高校生だが心がブスの女の子。田舎から上京し、芸者修行をしながら通学している。そんな彼女が文化祭で“八百屋お七”を舞うことになったが……。少々ひねくれた女の子がしだいに素直になっていく難しい役を見事にこなした富田靖子は、この映画でアイドルから女優へと大きくはばたいた。サザンオールスターズの原由子が主題歌『あじさいのうた』を歌っている。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TERMINAL MAN", "電子頭脳人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "発作が起こる度に人を殺してしまう技師を治療するために、脳に電極を移植し、コンピュータでコントロールする。しかし彼は病院を逃げ出し、発作が戻ってしまう……。M・クライトンの小説『ターミナルマン』を映画化した異色SF。音楽にバッハの曲を使用。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Traffic", "トラフィック", "", "", "http://image.eiga.k-img.com/images/movie/1204/315x210.jpg?3625", 148, "メキシコ──メキシコ州警察の警官は、国境付近で大量の麻薬を押収したが、犯人を連行しようとした矢先、連邦警察が押収した麻薬と犯人を引き取ってしまう。オハイオ──麻薬撲滅に奔走するアメリカの麻薬取締最高責任者。だが、彼の娘はすでに麻薬に溺れていた。サンディエゴ──突然自宅に捜査官が押し入り、夫が逮捕されてしまった1人の主婦。彼女の夫は、麻薬王だった……。", "2001-04-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Host", "グエムル　漢江の怪物", "", "", "http://image.eiga.k-img.com/images/movie/1074/315x210.jpg?1591", 120, "韓国で爆発的なヒットを記録した、ポン・ジュノ監督（「殺人の追憶」）によるパニック・エンタテインメント大作。ソウルの中心を貫く漢江（ハンガン）の河川敷で、小さな売店を営みながら暮らすパク一家。普段と変わらない日を送っていたパク一家の面々だったが、ある日突然、漢江から飛び出してきた謎の巨大な怪物に娘ヒョンソを奪われてしまう……。主演は「JSA」「殺人の追憶」など韓国を代表する名優となったソン・ガンホ。", "2006-09-02", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東方不敗 風雲再起／THE EAST IS RED", "スウォーズマン 女神復活の章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "香港ニューウェーブの旗手ツイ・ハークのSFX時代劇アクション「スウォーズマン」シリーズの続編。前作「スウォーズマン 女神伝説の章」から10年後、死んだと思われていた“東方不敗”が実は生き延びていた、という設定のもとスペイン軍と明朝政府の役人、そして東方不敗との壮絶な戦いを描く。B・リン扮する東方不敗と彼女に同性愛的な愛情を捧げるスノー役のJ・ウォンとの妖艶でエロティックなシーンが話題を呼んだ。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホールドアップダウン", "ホールドアップダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "DVD作品「ハードラックヒーロー」で組んだSABU監督とV6が、さらなるパワーアップによって完成させたニュー・タイプの活劇。銀行強盗、刑事、トラック野郎、ストリート・ミュージシャン。ワイルドサイドを歩く6人の男たちが、もつれ合っていく様を活写し、人生の摩訶不思議を描き出す。強盗した金をロッカーに隠そうとする木俣と佐川。だが小銭がないため、ストリート・ミュージシャンから小銭を奪ってさらなる逃走を重ねる。", "2005-10-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DARLING", "ダーリング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "英国のフリーシネマ派の一人、シュレシンジャー監督の初期代表作。地方都市出身の若い文学少女が世間に出て、女優、モデルといった様々な職業を遍歴。その彼女の目を通して、複雑な人間社会がシニカルに描かれる。クリスティの出世作。", "1966-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THINGS TO COME", "来るべき世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "H・G・ウェルズが自らの小説を脚本化し映画化した近未来SFの先駆的作品。イギリスの架空都市エブリタウンが、戦争や伝染病、独裁政治を克服し、理想都市に発展する“歴史”を描く。科学文明を重視し、その進歩を肯定した作品。巨大な地下都市のセットが素晴らしい。", "1936-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OPEN HOUSE", "OPEN HOUSE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "「GO」で注目を集めた行定勲が、辻仁成の小説をもとに描いた長編初監督作。会ったばかりで一緒に暮らすことになった若い男女と、離婚したばかりの女性の二つのドラマが並行して展開。孤独や不安を抱えた彼らが淡々とした日々の中で、やがて何かを見い出していく姿を優しく見つめる。揺れ動く心情を包み込むような、繊細な映像も印象的。売れないモデルのミツワは、チラシの仕事ばかりで不満を感じている。そんなある時、偶然出会った無職の青年・トモノリが彼女の家に転がり込んで、奇妙な共同生活が始まる。一方、離婚したばかりのユイコは、まだ心の傷が癒えずに苦しんでいた。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIRIDIANA", "ビリディアナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "自分を誘惑した伯父の自殺に、罪の意識を抱いた若い尼僧ビリディアナは、贖罪のため貧者を救おうと献身的に努力する。しかしそこには淫らなワナが待ち受けていた……。世の中の常識に反逆し続けたブニュエル監督が、故国スペインを追放される原因となった異色問題作。", "1961-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("醜聞〈スキャンダル〉", "醜聞〈スキャンダル〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "黒澤明が初めて松竹に招かれメガホンを取った作品。脚本は「野良犬（49）」と同じく菊島隆三との共著である。新進気鋭の画家（三船）と美しい声楽家（山口）が偶然出遭ったところを雑誌記者に盗撮される。まったくの醜聞（スキャンダル）に巻き込まれていく二人。物語の舞台はやがて裁判へ……。いたずらに醜聞を追うジャーナリズムを糾弾する一方で、後半は志村喬演じる弁護士の心情・行動が焦点となっていく。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄火若衆", "鉄火若衆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "悪徳商人やそれと結ぶ横暴な旗本に楯突く勘定奉行、豊中兵庫の遺児・矢之介。その矢之介が旗本とぶつかった時に救い出した風変わりな浪人・春夏秋兵衛、そして江戸から悪を一掃するために立ち上がった大目付・枚方壱岐守の３人が、非道の限りをつくす悪に立ち向かう。勧善懲悪のドラマが楽しめる娯楽時代劇。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOOPER", "グレート・スタントマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アクション映画作りの舞台裏を、スタントマンの側から描いた、笑いあり、涙ありの娯楽作。超一流のスタントマンだが、いささかトウのたった主役フーパーにレイノルズが扮し、随所で危険なシーンをこなしながら、温かみのある演技を見せている。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WORLD'S GREATEST ATHLETE", "史上最大のスーパー・チャンピオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "TV『エアーウルフ』で人気者になったJ・M・ヴィンセントのディズニーでのターザン・パロディー映画。大学陸上部のコーチがアフリカ旅行で見たものは、超人的な脚力をもつ若者ナヌーだった。彼はアメリカに渡り、負け続きの大学に続々と勝利をもたらすのだったが……。ビデオ発売時タイトルは「ジャン・マイケル・ビンセントのジャングル・ヒーロー」。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("S.A.S. SAN SALVADOR", "サン・サルバドル 地獄の戦火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "政府軍と反政府ゲリラが日常的に衝突を続けるサン・サルバドル。しかし、この抗争も実は大国のエージェントの影が見え隠れするものだった。オーストリアの貴族マルコは、CIAの依頼を受けサン・サルバドルでの危険な工作活動に向かうが……。ヌーヴェル・ヴァーグの名カメラマン、R・クタールがメガホンを執ったアクション・サスペンス。", "1982-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Surveillance", "サベイランス", "", "", "http://image.eiga.k-img.com/images/movie/55134/315x210.jpg?3792", 98, "鬼才デビッド・リンチの娘ジェニファー・リンチの長編監督第2作となるサスペンスドラマ。米ニューメキシコ州サンタフェで猟奇殺人事件が発生。FBI捜査官のエリザベスとサムが捜査に乗り出すが、地方警察で保護されている殺人現場に居合わせた3人の証言はそれぞれ微妙に食い違っていた……。出演はビル・プルマン、ジュリア・オーモンド、マイケル・アイアンサンド。", "2010-02-06", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Reykjavik Whale Watching Massacre", "レイキャヴィク・ホエール・ウォッチング・マサカー", "", "", "http://image.eiga.k-img.com/images/movie/56024/315x210.jpg?4129", 90, "ホエールウォッチングに世界中から観光客が集まるアイスランドのレイキャビク。いつものように出航した観光船が不慮の事故にあい、船長を失ってしまう。そこへやってきた捕鯨船が観光客に救いの手を差し伸べるが、捕鯨禁止により困窮し、正気を失っていた捕鯨船の乗組員たちは、船内に閉じ込めた観光客たちを次々と襲っていく。アイスランドで初めて製作されたホラー。「悪魔のいけにえ」のレザーフェイス役で知られるガンナー・ハンセンのほか、裕木奈江が観光客のエンドウ役で出演。", "2011-06-04", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESERT WARRIOR", "デザート・ウォーリアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "核戦争後の未来世界。タイログ人の王ベクターは、後継ぎをつくるため、部下のゼラクに健康な若い女を連れてくるよう命ずる。ゼラクはドラセン人の美女ラセラを捕まえるが、彼女を愛してしまう。折しも、タイログとドラセンの戦いが始まった時、ゼラクはドラセンの戦士として立ち上がる。「超人ハルク」のフェリグノ主演の近未来アクション。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA RONDE", "輪舞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "A・シュニッツラーの同名小説の２度目の映画化。舞台を原作のウィーンからパリに移し、前作マックス・オフュルス監督の「輪舞」とはひと味違ったフランス風艶笑譚として描いている。", "1964-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆 BAKU！", "爆 BAKU！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "現金輸送車襲撃に失敗し、金庫の中に閉じ込められた恋人を救うため駆けつける圭子。金庫爆破のために用意した小型爆弾を警察からごまかそうと飲み込んでしまうが、爆弾のタイマーが偶然、作動してしまった！ 圭子を演じる西村知美の好演も手伝い、たたみかけるようなテンポが小気味よいサスペンス・アクション。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GUMBALL RALLY", "激走！5000キロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ニューヨークからロスまでの公道5000キロを法律を度外視して走るクロスカントリー・ロードレースを扱ったカー・アクション・コメディ。同年同じ題材の「爆走！キャノンボール」が製作され、こちらは続編も作られた。監督のベイルはスタントマン出身。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・悲しきヒットマン", "新・悲しきヒットマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "元山口組顧問弁護士・山之内幸夫の小説を原作とする劇画を名匠、望月六郎が映画化。敵対する組織の会長を殺し、10年の刑を終えて出所した橘孝。しかし組に戻れば、そこはかつての極道社会ではなく、平和外交のもとに幹部たちは金もうけに明け暮れるばかりだった。橘の行動はなにかにつけ、組との摩擦を生み、時代遅れと冷笑される。足を洗うべきか悩んでいる時、舎弟の勇二が組から、もうけ話にハメられたことへの復讐のためにヒットマンを命じられた。これをきっかけに橘は組に戦いを挑むことを決心する。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LEGEND OF LOBO", "狼王“ロボ”", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "ディズニー製作（監督はノー・クレジット）による、「シートン動物記」をもとにした動物映画の秀作。開拓史時代、人間によって1000ドルもの賞金を賭けられたボス狼・ロボの危難を、美しい大自然の情景の中に描く。西部劇スタイルを借りた、異色のアクション映画としても興味深い作品。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裏切りの季節", "裏切りの季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "大和屋竺が日活を退社し若松プロで監督第１作として発表した問題作。ベトナム帰りの報道カメラマンを主人公にして、大和屋竺の耽美的なイメージが全編を貫く。若松孝二の初プロデュース作品で、ヒロイン役の谷口朱里がSMシーンを体当たりで熱演。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ムンクの叫び", "ムンクの叫び", "", "", "http://image.eiga.k-img.com/images/movie/58064/315x210.jpg?2482", 106, "「サル」「モルモット」の葉山陽一郎監督が、醜く生まれた男の悲恋と不遇を描く。ムンクの絵画「叫び」に描かれている人物そっくりに生まれた赤沼かずおは、その容姿のためにいじめを受け続け、引きこもりになる。デザインの才能があったかずおは、やがて父親が経営するアパレル会社のデザイナーとして在宅で仕事を始めるが、そんなある日、子どもの頃に恋心を抱いていた女性・怜子が父の会社で働いていることを知る。怜子がかずおのデザインを高く評価していると聞き、かずおはわずかな希望を抱くが……。ヒロインの怜子役に「完全なる飼育 メイド、for you」の亜矢乃。", "2012-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("August Rush", "奇跡のシンフォニー", "", "", "http://image.eiga.k-img.com/images/movie/53104/315x210.jpg?1904", 113, "児童福祉施設で育った11歳の少年エバンは、あらゆる音がメロディに聞こえるという類まれな音感の持ち主。不思議な音に導かれて施設を抜け出した彼は、マンハッタンで出会ったストリートミュージシャンにギターの才能を見出されて演奏活動をはじめるが……。「チャーリーとチョコレート工場」の名子役フレディ・ハイモア主演によるハートフル・ドラマ。ジョナサン・リース＝マイヤーズ、ロビン・ウィリアムズら実力派が脇を固める。", "2008-06-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VISITING HOURS", "面会時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "病院で無防備状態の患者に迫る殺人鬼の恐怖を描いたスリラー。非暴力主義を唱えた女性ニュース・キャスターが暴漢に襲われ入院する。だが、彼女を狙う執拗な魔手は病院内まで潜入し……。偏執狂の犯人役を、カナダの性格俳優M・アイアンサイドが怪演。", "1981-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("結婚", "結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "終戦間もない当時を背景に、経済苦のため、なおも結婚できずにいる相思相愛の男女と、彼ら二人をなんとか結婚させてやろうと努力する娘の父親の姿を描いたメロドラマ。木下惠のオリジナル・ストーリーを、前年に脚本家デビューした新藤兼人が脚色。美術は「母を恋はずや」以来、「秋刀魚の味」まで小津の全松竹作品を担当した浜田辰雄。DVDは「木下惠介　DVD-BOX 第１集」に収録。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOOD LUCK MISS WYCKOFF", "さよならミス・ワイコフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "処女のまま更年期に近づき、精神の変調に悩まされる女教師ワイコフ。幼児体験からくるセックスに対する恐怖感が原因であった。黒人学生に強姦された彼女は、それ以来、性の快楽と衝動に憑かれ、暴力的なセックスの強要にも応じ始めたのだが……。“女性の自立”をテーマに、中年女性の性を描く。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Highwater", "ハイウォーター", "", "", "http://image.eiga.k-img.com/images/movie/54654/315x210.jpg?4136", 99, "世界中のサーファーのメッカであるハワイ・オアフ島のノースショアを舞台にしたドキュメンタリー。毎年55日間に渡って行われる3つのサーフィン大会を総称した「トリプルクラウン」に集まったサーファーやグルーピー、ジャーナリストや写真家など、さまざまな人々の人間模様に密着する。監督はサーフィン映画界の巨匠ブルース・ブラウンを父に持つデイナ・ブラウン。", "2009-09-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("手紙 −LETTER−", "手紙 −LETTER−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "監督自身の体験をもとに、３年の期間をかけて作り上げた作品。大学生の“俺”のもとに、ある日、一通の手紙が届く。住所不明、差出人不明。内容はラブレターで、気になった俺は、差出人を捜し始める。半年がかりで、差出人の友人にまでたどり着いたが……。私小説風の物語を、探偵という娯楽映画のタッチでまとめた異色作。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOON44", "MOON〈ムーン〉44", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "地球の資源が枯渇し、鉱山惑星からエネルギーを持ってくるしかなくなった近未来を舞台に、貴重な資源を持つ惑星“MOON 44”を必死で守ろうとする多国籍企業の戦いを描くSFアクション。「インデペンデンス・デイ」のエメリッヒがアメリカの資本で故国ドイツと製作した作品。", "1990-01-01", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LENNY", "レニー・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "政治や社会への痛烈な風刺芸で偽善的な権力に抵抗したレニー・ブルース。離婚、麻薬、肺結核と私生活では恵まれず、ついには大衆からも見放されて死んでいった彼の生涯を、演技派ホフマンが熱演。モノクロの逆光線で捉えられた背を丸めるレニーの姿が印象的だ。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Water for Elephants", "恋人たちのパレード", "", "", "http://image.eiga.k-img.com/images/movie/56074/315x210.jpg?3824", 121, "大恐慌時代のアメリカを舞台に、華やかなサーカスの舞台裏で繰り広げられる危険な恋を描いたサラ・グルーエンのベストセラー小説「サーカス象に水を」を映画化。主演は「ウォーク・ザ・ライン　君につづく道」のリース・ウィザースプーンと「トワイライト」シリーズのロバート・パティンソン。監督は「アイ・アム・レジェンド」のフランシス・ローレンス。大学で獣医学を学んでいたジェイコブは両親を事故で失い、途方に暮れる。ふとしたきっかけでサーカス団の動物の世話係になったジェイコブは、象のロージーを通し、一座の花形で団長オーガストの妻マリーナと親しくなるのだが……。", "2012-02-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cidade dos Homens", "シティ・オブ・メン", "", "", "http://image.eiga.k-img.com/images/movie/53554/315x210.jpg?1630", 106, "ブラジル、リオ・デ・ジャネイロのスラム街ファベーラの実態を描き、アカデミー賞4部門の候補になったフェルナンド・メイレレス監督の「シティ・オブ・ゴッド」の姉妹編。メイレレスが製作を務め、「シティ・オブ・ゴッド」TVシリーズを監督したパウロ・モレッリがメガホンを取る。ファベーラで幼い頃から実の兄弟のように育ったアセロラとラランジーニャ。しかし、ある時、アセロラは自分の父親がラランジーニャの父親に殺されたことを知り……。", "2008-08-09", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MANGLER", "マングラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「クリスティーン」「地獄のデビル・トラック」など“機械が人間に襲いかかる”モチーフはS・キングの最も得意とするところ。クリーニング工場で働いていた自らの経験から生まれた本作品の主人公は、ランドリー工場の中央に鎮座する巨大な洗濯用プレス機“マングラー”。事件は、従業員の少女の血がマングラーに飛び散ったことから始まる。自我と悪魔の魂を持つ、この金属の怪物はランドリーの経営者ガードレーを支配し操りながら、生贄を求め続け人間に襲いかかっていく……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒の斜面", "黒の斜面", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "辻井は妻と二人で暮らす平凡なサラリーマンだが、彼には妻に内密の愛人がいた。ある日、彼は会社の命令で出張することになったが、飛行機の搭乗間際に予定を変え、愛人の家に泊まる。その夜、彼の乗るはずだった飛行機が墜落した……。サスペンス・タッチの愛憎劇。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOLLYWOOD COP", "ハリウッド・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "犯人逮捕のためなら脅しや買収も辞さない型破りな刑事タークと、遊び好きで口八丁手八丁のひょうきん刑事ジャガー。彼ら二人の腕利き刑事がある幼児誘拐事件を発端に、巨大なシンジケートに戦いを挑んでいく姿を描いたアクション映画。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("医（いや）す者として　映像と証言で綴る農村医療の戦後史", "医（いや）す者として　映像と証言で綴る農村医療の戦後史", "", "", "http://image.eiga.k-img.com/images/movie/57424/315x210.jpg?3972", 108, "巡回医療や農村医療に尽力して地域医療の礎を築くとともに、地域社会作りの一端も担う存在となった長野県・佐久総合病院の60年間にわたる取り組みを記録したドキュメンタリー。戦後間もない信州、千曲川沿いの小さな病院に赴任した青年医師・若月俊一（2006年没）は、周辺の農山村への出張診療や全村健康管理（健康診断）を全国に先駆けて行っていった。また、健康に対する啓蒙活動として演劇や「病院まつり」も催し、地域社会の形成に貢献していく。1950年代から30数年間にわたり記録されていた16ミリフィルムに、当時を知る現在の関係者らの証言を加え、完成させた。", "2011-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Princess and the Frog", "プリンセスと魔法のキス", "", "", "http://image.eiga.k-img.com/images/movie/54514/315x210.jpg?2012", 97, "グリム童話「かえるの王子さま」を換骨奪胎したE・D・ベイカーによる児童小説「カエルになったお姫様」を、「リトル・マーメイド」「アラジン」のジョン・マスカー＆ロン・クレメンツが映画化した長編アニメ。1920年代の米ニューオーリンズを舞台に、魔女の策略でカエルの姿に変えられてしまったナビーン王子と彼にキスしたことでカエルになってしまったウェイトレスの少女ティアナが、自らの身体を取り戻すために大冒険を繰り広げる。声の出演にアニカ・ノニ・ローズ、オプラ・ウィンフリー、ジョン・グッドマン、テレンス・ハワード。", "2010-03-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PART11 追憶", "難波金融伝 ミナミの帝王　劇場版PART11 追憶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "大阪ミナミの高利貸し・萬田銀次郎の活躍を描いた金融ドラマシリーズ劇場版第12作。時効が迫った犯人を追う定年間近の老刑事、桜井に金を貸していた銀次郎。自分の臓器を借金のカタに提供してまで捜査を続ける桜井を見て、銀次郎は犯人捜索に協力する。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パンダコパンダ", "パンダコパンダ", "", "", "http://image.eiga.k-img.com/images/movie/53494/315x210.jpg?2472", 35, "今や世界的アニメーターとなったスタジオジブリの宮崎駿＆高畑勲が、パンダブーム真っ只中の72年に手掛けた名作短編アニメ。「長くつ下のピッピ」の主人公をモデルとしたおてんばでしっかり者の少女ミミ子と、ジブリの人気キャラクター“トトロ”のルーツとされるパンダの父子との交流を、ほのぼのとしたタッチでユーモアたっぷりに綴る。翌73年に制作された続編「パンダコパンダ雨ふりサーカスの巻」（38分）も同時公開される。", "2008-03-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花嫁募集中", "花嫁募集中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "アメリカで成功して財を成し、30年振りの再婚を決意して愛娘とともに日本に戻った男が、親類のTVプロデューサーと協力して、『花嫁募集中』なる珍番組を製作。再婚の相手探しに奔走するが。三木鮎郎の原作をミュージカル仕立てで描いた喜劇。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIN　NOTICIAS　DE　DIOS", "ウェルカム！ヘヴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "V・アブリル、P・クルスというスペインの美人女優二人が共演した奇想天外なコメディ。ある魂をめぐる天国VS地獄の争奪戦が、歌と踊りにユーモアを盛り込んで繰り広げられる。清らかな魂の激減で存続の危機に直面した天国が、マニというボクサーの魂を救うため、天国の人気歌手ロラを下界へ派遣。ロラは彼の魂を天国へと送るために奮闘する。だがロラの前に地獄からの美しき使者カルメンが立ちはだかる。", "2001-01-01", "9,2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VALENTINO RETURNS", "傷だらけのキャデラック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "18歳の青年ウェインは、畑仕事をして稼いだ金で“ピンクのキャデラック”を購入した。目的はただ一つ、憧れの美女シルビアをデートに誘い、助手席へ乗せてものにしようということ。だが、事はそう簡単にはいかず……。1950年代の青春を当時のヒット曲にのせてコミカルに描く青春ドラマ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生きない", "生きない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「HANA-BI」などの北野武作品で助監督を務めた清水浩の演出、「3−4×10月」「みんな?やってるか！」のダンカンの脚本・主演による群像人間ドラマ。沖縄で初日の出を迎えるという名目で、バスツアーが那覇空港を出発。しかし、この旅の真の目的は保険金目当ての自殺ツアーで、参加者はいずれも莫大な借金を抱えていた。そうとも知らず、一人の少女が叔父の代理でツアーに参加。屈託のない彼女は、死を決意していたツアー客の間に思わぬ波紋を投げかける。たが、ツアー・コンダクターは、彼女を道連れにしてでも目的を遂げようとし……。生きる意志や、それとは関係なく訪れる死の突発性を混在させ、生死の意味を問いかける。珍道中の描写には、たけし作品に共通するドライなユーモアがチラリ。第51回ロカルノ国際映画祭アキュメニカル（全キリスト協会）賞を受賞。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("密航０ライン", "密航０ライン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "鈴木清順監督が「けものの眠り」に続いて長門裕之とコンビを組んだセミドキュメンタリー・タッチのアクション映画。ライバル同士の新聞記者二人が、国際密輸組織の内幕を暴くべく潜入する。清順独特の斜めの構図を主体にした凝ったカメラ・アングルが見もの。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL CID", "エル・シド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 186, "11世紀、ムーア人の侵略に脅かされるスペイン。若き勇将エル・シドは、恋人シメンの父親を死に至らしめ、また王位継承の争いに巻き込まれて追放の身となる。その直後スペイン滅亡の危機が訪れ、彼は祖国のために立ち上がるのだった……。実在した救国の英雄エル・シドの生涯を壮大なスケールで描いたスペクタクル超大作。ビデオは以前、172分の短縮版で発売された。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わたしの季節", "わたしの季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "滋賀県野洲市にある重症心身障害者施設、“第二びわこ学園”を４年間に渡り取材したドキュメント。「阿賀に生きる」の撮影でも知られる小林茂監督が、同学園の日常にカメラを向ける。陰惨とした雰囲気などなく、常に笑いの絶えない施設で１日を過ごす入居者たちのいきいきとした表情が印象深い。重度の知的障害および身体的障害を抱える人々が生活を送る児童福祉施設、第二びわこ学園。その学園で暮らす123名の姿を見つめる。", "2005-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Yamakasi Les Samourais des Temps Modernes", "YAMAKASI ヤマカシ", "", "", "http://image.eiga.k-img.com/images/movie/1754/315x210.jpg?1516", 91, "「YAMAKASI」とはコンゴのリンガラ語で「超人」のこと。このユニット名を持つフランスで人気のストリート・パフォーマー集団が主人公。音楽はフレンチHIP HOP界の10年選手、シュプレームNTMのジョーイスターとDJスパンクが担当。新進のファビイ、アイロン・サイらを含め、パリのHIP HOPの現在を伝える音に乗って、パリ市街を建物の壁や屋根伝いに自在に疾走する若者たちを映し出す、フレンチ版ストリート・ムービーでもある。", "2001-09-22", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・日本の首領３", "新・日本の首領３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1977年～1978年に公開された飯干晃一原作の任侠シリーズ“日本の首領”をアレンジし、松方弘樹や千葉真一などスターキャストをそろえた極道超大作第３作。関東と関西の二大組長は休戦協定を結び、勢力を拡大するチャイニーズ・マフィアに備えるが……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GILDA", "ギルダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "1940年代半ば、コロンビア映画の看板女優だったL・ヘイワースがタイトル・ロールを演じる。ブラックサテンの肘までの丈の手袋を少しずつはずしながら『みんな彼女のせいにしな』を歌う場面に集約されるように、彼女の魅力を100パーセント発揮した代表作ともいえる作品。監督には「カバーガール」や「カルメン」でもヘイワースとコンビを組んだC・ヴィダーがあたっている。第二次大戦の末期、南米の港町ブエノスアイレスを舞台に、男を狂わす魅惑的な女と二人の暗黒街の男たちの間に起こる愛憎渦巻く三角関係と意外な結末を描き、ハードボイルド・タッチのメロドラマになっている。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ソロモンの指輪", "ソロモンの指輪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "宝塚歌劇を最新鋭のデジタル技術で撮影する「タカラヅカレビューシネマ」の第1弾として、2008年に宝塚大劇場にて上演された雪組公演「ソロモンの指輪」を映像化。あらゆる生物の声を聞くことができるという不思議な指輪を手にした古代イスラエルのソロモン王の伝説をモチーフに、ファンタジックなショーが展開される。水夏希、白羽ゆり、音月桂ら人気スターたちによる煌びやかなステージが美しい映像で堪能できる。", "2009-06-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("百万弗を叩き出せ", "百万弗を叩き出せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "同じ夢を抱きながらも、違った運命に引き裂かれていく二人の青年を描く鈴木清順監督のボクシング映画。チャンピオンを目指す二人に和田浩治と野呂圭介。ボクシングに憑かれた貧乏ジムの経営者兼トレーナーに金子信雄が扮し、好演している。栄光と転落が背中合わせのボクサー物語の定型を押えつつ、そこに時代のうっ屈した気分をも塗り込めてしまう、清順流プログラム・ピクチャーの真骨頂。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LEGEND OF HELL HOUSE", "ヘルハウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「エクソシスト」とともにオカルト映画ブームの口火を切ったヒット作。悪霊に取り憑かれた“地獄の邸”と呼ばれる館に、物理学者や霊媒師など５人が調査隊として乗り込む。しかし邸を支配する悪霊の力は想像を絶するほど大きかった。やがて一人、また一人と霊の犠牲となった者が無残に殺されて……。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HONEY", "欲望の中の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "豪邸の中に出版事務所を構える初老の紳士のもとに、一人の女性作家が訪れる。紳士に銃を突きつけ、自分の作品を読ませようとする女。その物語とは“欲望の宿”なるホテルで様々なエロスの世界が展開するというものだった。倒錯した性の世界が二重構造で展開していく官能編。", "1981-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Days of Heaven", "天国の日々", "", "", "http://image.eiga.k-img.com/images/movie/20384/315x210.jpg?2022", 94, "本作の後「シン・レッド・ライン」（1998）まで20年にわたり沈黙する巨匠テレンス・マリックの監督第2作。79年・第32回カンヌ国際映画祭で監督賞を受賞。1910年代、青年ビリーと妹リンダ、そしてビリーの恋人アビーはテキサスの農場に流れ着き、そこで働き始める。やがてビリーの妹と偽っていたアビーに惹かれる農場主のチャックが病気で余命幾ばくもないことが発覚。ビリーはアビーをチャックと結婚させ、そのお陰で厚遇を受けることに。しかしチャックは妻とビリーの関係を疑い……。彼らの葛藤と皮肉な運命を静謐な演出で綴る。名手ネストール・アルメンドロスの映像美は圧巻。2011年8月、特集上映「ZIGGY FILMS '70s '70年代アメリカ映画伝説　第2弾」でリバイバル。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRANCES", "女優フランシス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "実在のハリウッド女優フランシス・ファーマーの数奇な半生を描く作品。スターを夢見る少女が、結婚の失敗、周囲とのあつれき、投獄、精神病院への入院などを経て、最終的にTVショーにカムバックするまでのすさまじい遍歴を、J・ラングの熱演によりたどっていく。ラングは本作で、モスクワ映画祭主演女優賞を受賞した。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fourth War", "対決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "　ベトナムの勇者だったアメリカ人のノールズ大佐と、アフガニスタンで名を馳せたロシア人のバラチェフ大佐が冷戦後の西ドイツとチェコの国境で激突する。軍人として高いプライドを持ち、闘うことしか知らないふたりの男たち。争いは次第にエスカレートし、ついには雪原で肉弾戦を繰り広げることに……。男同士の激しい闘いを描くアクション・ドラマ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUGUSTIN，ROI　DU　KUNG−FU", "オーギュスタン／恋々風塵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "'95年のカンヌ映画祭で話題となり、日本でもヒットした「おとぼけオーギュスタン」の続編。9年ぶりの本作は、フランス版「Mr.ビーン」と言われた前作から打って変わって、淡いラブ・ストーリー。おとぼけキャラはそのままに、主人公オーギュスタンの純粋さが詩的に表現され、静かな感動を呼ぶ。ジャッキー・チェンに憧れてパリのチャイナタウンを訪れたオーギュスタンは、美しい鍼灸師リンと出逢い、彼女に淡い恋心を抱くが……。", "2005-06-18", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEN", "10話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "前作「ABCアフリカ」同様、DV撮影によるA・キアロスタミ監督の人間ドラマ。自動車内にカメラを設置し、二人の対話をクローズアップで凝視する禁欲的なスタイルで10のシーンを切り取る。離婚して、父親の許に引き取られた息子との関係に悩む一人の女性。彼女は自動車を運転しながら、息子と対話を繰り返し、姉や離婚したばかりの友人、老女と語らいながら、自分自身の現在、そして息子との距離感について思いをめぐらす。", "2002-01-01", "2,15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獅子の血脈", "獅子の血脈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "連心会との抗争中に死亡した秋葉組組長、秋葉伝蔵に代わって二代目となった秋葉東吾。連心会は彼に軍門に下るか、解散するかの選択を迫る。そんな中、東吾の前に現れたのは伝蔵の古い親友である嵐山重成だった。東吾が実の息子と知った嵐山は極道として腰の据わらぬ彼に“獅子が我が子を崖から突き落とす”かのような厳しい愛情を注ぎながら、自らも抗争に身を投じていく。「恋極道」「鬼火」の望月六郎が、ヤクザの抗争劇の中に親子の激しい情愛を織り込んで撮り上げたバイオレンス極道映画。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES POSSE´DE´S", "悪霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "ロシアの文豪ドストエフスキーの小説を、“抵抗三部作”などで知られるポーランドのA・ワイダが映像化。ドストエフスキーを“19世紀の時点ですでに20世紀を予見していた予言者”として信奉するワイダだけに、本作は1870年頃のロシアを舞台にしつつもロシア革命を予感させるものとなっている。主人公のスタブローギンは、進歩的な精神で社会の変化を夢みる若者たちの指導者。血気にはやる若者たちを先導し、暴力によって社会を破壊しようとする彼の姿が、ミステリアスに描かれていく。スタブローギンを演じるのはランベール・ウィルソン。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dog Day Afternoon", "狼たちの午後", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "　ニューヨーク、猛暑の白昼。銀行に3人組の強盗が押し入る。しかしそのうちのひとりは逃亡。その上、銀行には小額の現金しかなかったことがわかる。犯人のソニーとサルはあっという間に警官隊に包囲され、人質とともに篭城せざるをえなくなる。一方、集まった野次馬たちは犯人を応援するという異常な事態に。そんな中、ソニーが犯行に走った理由も明らかになるが。はたして事件の行方は？　実際に起きた事件をもとにした緊張感満点の犯罪サスペンス。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦国番長ガチザムライ", "戦国番長ガチザムライ", "", "", "http://image.eiga.k-img.com/images/movie/55264/315x210.jpg?1420", 0, "「あずみ」「十三人の刺客」でも侍を演じた石垣佑磨が主演するアクション映画。乱世の戦国時代。農民の竜徳とその仲間たちは、立身出世を望み、野武士となって近隣の不良侍と戦いに明け暮れる日々を送っていた。ある日、因縁の村である吉見村を制圧したばかりの竜徳たちの前に、本物の戦から戻ってきた鬼首村の城児魔が立ちはだかる。", "2010-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋するトマト", "恋するトマト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "俳優の大地康雄が13年がかりで企画、さらに製作総指揮・脚本・主演の４役を務めた意欲作。農村の嫁不足問題を題材に、外国人女性との恋を通して再生する中年男の姿を描く。主演の大地が見せる哀愁あふれる熱演が感動的。45歳、独身の農家の長男・正男は、フィリピン女性と婚約するが、結婚詐欺にあってマニラで路頭に迷う。身を持ち崩し、現地のヤクザに仲間入りした正男は、ある日、農家の女性と運命的に出会い……。", "2006-05-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DYING YOUNG/THE CHOICE OF LOVE", "愛の選択", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "求人広告に応募し、裕福な家庭の息子の看護婦となったヒラリー。彼女の患者ヴィクターは白血病に冒されていた。彼女は必死の努力で自暴自棄に陥っていく彼を支えようとするが……。J・ロバーツのキュートな魅力が光る、愛と感動のドラマ。死の影に怯える主人公たちの愛の形が涙を誘う。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("傅奇李少龍／BRUCE LEE TRUE STORY", "ブルース・リー物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "伝統的な中国空手に、韓国の合気道、タイのキックボクシングなどを採り入れた截拳道を創始、「燃えよドラゴン」などのカンフー映画で名高いブルース・リー。彼の32年の生涯を実録部分も織り交ぜて綴る。リーにそっくりのH・チュンタオが主演。", "1982-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROMA", "フェリーニのローマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "古代からの遺跡と現代性が共存する都市ローマを、フェリーニが自らの幻想を通してドキュメンタリーともファンタジーともつかぬ独自の世界のなかに再現する。時に希望に満ち、時に不安に沈む街ローマが1938年から現代へと時間を飛び越えた祝祭的な世界として描かれるさまは、まさにフェリーニならではの迫力。", "1972-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE OPTIMISTS", "別れの街角", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "小説家シモンズが、自らの小説を映画化。幼い姉弟は、街で老大道芸人に出会う。愛犬の死の悲しみを酒で紛らわそうとする老人のため、二人は犬の死体をハイドパークに埋める。庶民的な心のふれあいをさりげなく描写した、心温まる秀作。", "1973-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOUL", "ソウル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "警察官の夫の自殺によって明らかになった愛人の存在と、大がかりな汚職事件。その愛人も何者かに殺され、残された妻は夫の私生児とともに逃亡生活を余儀なくされる。ケネス・イップの名でも知られる香港ニューウェーブの一人S・ケイが乾いたタッチで描いた人間ドラマ。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("On a Clear Day You Can See Forever", "晴れた日に永遠が見える", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "精神科医と不思議な能力を持つ女学生のロマンスを描くミュージカル。知的で明るい女学生デイジーは、予知能力などを持つ超霊感の持ち主。精神医マルクは、彼女に興味を抱き催眠術をかける。その度にデイジーから出てくる18世紀の公爵夫人メリンダに、マルクは恋をしてしまう。ところが、研究材料にされたとデイジーが怒ってしまい、マルクは彼女の機嫌を直そうと悪戦苦闘する。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の不死鳥", "花の不死鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "素人離れした歌声の持ち主であるクラブのママが、作曲家である夫と、これも作曲家である昔の恋人との間で揺れ動く姿を、ヒット曲を配して描いた美空ひばり主演の歌謡ドラマ。過去を隠すため、覆面歌手として懐メロを歌うシーンが圧巻。DVDは「美空ひばり　メモリアルDVD-BOX(1)」に収録。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VEUVE COUDERC", "帰らざる夜明け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "運河のあるフランスの田園。幼少より不幸な身の上の未亡人クーデルクのもとに、おたずね者ジャンが流れて来た。未亡人、そしてその姪とも関係を持つジャン。そんなある日、ジャンを快く思わない妹夫婦の密告で、警官隊が駆け付ける。ジャンとクーデルクは追い詰められ……。フランス映画らしいしっとりとした味わいをもつ好編。", "1971-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シティハンター　愛と宿命のマグナム", "シティハンター　愛と宿命のマグナム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "'87年から'99年まで続き高視聴率をキープした、長寿人気TVアニメの劇場版。その第1弾は、スリリングな銃撃戦が展開するアクション・エンターテインメント。『機動戦士ガンダム』などで知られるサンライズが制作を担当している。おなじみの男女コンビによる華麗なミッションが痛快だ。事件屋稼業の冴羽リョウと美女・香が共に悪を退治する。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEFFERSON IN PARIS", "ジェファソン・イン・パリ　若き大統領の情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "アメリカ合衆国独立宣言の起草者である第３代大統領トマス・ジェファソンの知られざる愛の物語を映画化。1785年、フランス革命前夜のパリに、合衆国大使ジェファソンが年頃の娘パッツィを伴って赴任してくる。二年前に妻と死別したジェファソンは、そこで人妻マリアとのプラトニックな愛を引き裂かれ、奴隷として自分に仕える無垢な黒人少女サリーに心の安らぎを見出していく。ビデオ発売時タイトルは「ある大統領の情事」。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A LITTLE PRINCESS", "リトル・プリンセス　小公女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "母親を亡くし、父親のクルー大尉の深い愛情を受けて育った少女セーラ。父の赴任地であるインドで裕福に暮らしていた彼女だったが、父が戦地に赴くことになり、ニューヨークの厳格な寄宿学校に入学する。彼女の天真爛漫で誰にでもはっきりとものを言う性格は、鬼校長や厳しい規則でがんじがらめに縛られたクラスメイトたちにセンセーションを巻き起こす。そんなある日、セーラは父親が戦場で亡くなったという知らせを受け……。アメリカで公開されるや女性からの圧倒的な支持を得た話題作。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Women", "明日の私に着がえたら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新選組", "新選組", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "おなじみ新選組の物語を三船敏郎の独立プロ・三船プロが製作し、彼自身が近藤勇を演じたオールスター・キャストの娯楽大作。新選組結成、芹沢鴨暗殺、池田屋斬り込み、鳥羽伏見の戦い、流山の別離、近藤勇の最期など盛りだくさんの見せ場がかけ足で紹介されていく。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PURPLE HEARTS", "パープル・ハーツ —愛の勲章—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "富裕な医師が、自らの経験のためベトナム戦争に軍医として参戦する。そこで看護婦と恋に落ちるが、前線に赴いた男はやがて……。死の戦場でめぐりあった男女が、ベトナム戦争の悲劇の中で、愛を貫こうとする姿を描いた秀作。S・J・フューリーが息詰まる戦闘シーンと、恋に生きる純粋な男女という両極端を巧みに演出。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARRISH", "二十歳の火遊び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "D・デイヴィス監督が、ミルドレッド・サヴェージのベストセラー小説を映画化した愛憎渦巻く一編。19歳の息子を連れ、コネチカットのタバコ農場に家庭教師としてやって来たエレン。そして、彼女と息子の二人は、タバコをめぐる土地の争いに巻き込まれていく……。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IMPOSTOR", "クローン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "異星人との戦争下にある近未来。敵が送り込んだクローン人間爆弾と疑われ、保安局に追われる身となった天才科学者が、自らの存在と真実を究明するための戦いを繰り広げていくSFアクション・ミステリー。原作は「ブレードランナー」「トータル・リコール」などで知られる巨匠P・K・ディックの短編小説『にせもの』。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PUMP UP THE VOLUME", "今夜はトーク・ハード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「ウィンドトーカーズ」のC・スレーターが、昼は無口な高校生、夜になると過激なDJに扮する青春映画の佳作。アリゾナの田舎町。ここでは、高校生たちが、毎夜午後10時から聞こえてくる、謎の海賊放送DJ“ハード・ハリー”に熱狂していた。カリスマ的存在のハリーだったが、彼のアドバイスがもとで、高校生が一人自殺してしまう……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Walk Hard: The Dewey Cox Story", "ウォーク・ハード　ロックへの階段", "", "", "http://ecx.images-amazon.com/images/I/51W7qZYy2dL._SL210_.jpg", 96, "架空のロックスターの波乱に満ちた人生を、音楽パロディとブラックジョーク満載で描いた音楽コメディ。幼い頃から音楽が大好きだったデューイ・コックス。やがてロックミュージシャンとして成功した彼は、エルビス・プレスリーやビートルズら時代を代表するスターたちと共に栄光の日々を送るが、その一方でドラッグや酒、女性に溺れて転落していく。「シカゴ」のジョン・C・ライリーが主演を務めるほか、映画界・音楽界から豪華ゲストが多数出演。", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dekalog", "デカローグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 567, "「トリコロール」三部作で知られるクシシュトフ・キエシロフスキーが、ポーランドで制作したTVシリーズ。旧約聖書の十戒をモチーフにした、ワルシャワ郊外の同じアパートに住む人々を描いた10のエピソードからなる。各国で劇場公開され、カンヌ国際映画祭審査員特別賞ほか多数の賞を受賞した。第5、6話はそれぞれ「殺人に関する短いフィルム」「愛に関する短いフィルム」のタイトルで劇場用ロングバージョンが制作されている。", "", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HALLOWEEN II", "ブギーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "“ハロウィン”シリーズの第２作。前作で、撃ち殺されたはずの殺人狂マイケル・マイヤーズが再び死の世界からよみがえり、美女ローリーに襲いかかる。病院に紛れ込んだブギーマンことマイケルが看護婦の眼球へ注射器を突き立てるなど、ショッキングなシーンが連続。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本人のへそ", "日本人のへそ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "教授の指導のもと、東北の貧しい娘がソープランド嬢からストリッパーを経て、いかにして代議士の妾になったか、という劇を演ずる人たち。しかし、劇のさなかに教授が刺されてしまったことから事態が混乱し……。劇中劇を配し、どんでん返しの連続で見せる多重構成のミステリードラマ。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女性の勝利", "女性の勝利", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "溝口健二の戦後第１作。戦争が終り、政治犯として投獄されていた山岡も釈放される。彼を温かく迎えたのは女性弁護士、細川ひろ子であった。山岡は病院へ運ばれるが、ひろ子は民主化を求める気運のなか、法廷で封建思想の持ち主、河野検事と対決する。女性の自立という問題を強く打ち出した裁判劇。", "1946-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バーニングハーツ", "バーニングハーツ", "", "", "http://image.eiga.k-img.com/images/movie/57434/315x210.jpg?4756", 15, "無名の若手監督たちが作り上げた15分の短編10作品を特集上映する「NO NAME FILMS」の1本。生き甲斐をなくしたタクシードライバーのカズと、自信を失い孤独に弱りきったモデルのエル。ある日、尾行されていることに気づいたエルが、カズの運転するタクシーに逃げ込み……。監督は、オーストラリア出身で日本でモデル業をするかたわら、ミュージックビデオなどの映像作品も手がけるジェームズ・マクフェイ。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The White Countess", "上海の伯爵夫人", "", "", "http://image.eiga.k-img.com/images/movie/1744/315x210.jpg?4058", 136, "「眺めのいい部屋」、「ハワーズ・エンド」、「日の名残り」など、長きにわたって名作を生み出してきたマーチャント・アイボリープロダクション最後の作品は、1936年日中戦争開戦前夜の上海を舞台に元外交官の米国人と、ロシアからの亡命未亡人との恋愛を描く歴史ドラマ。オリジナル脚本はブッカー賞受賞作家で、93年の「日の名残り」以来のアイボリー作品参加となるカズオ・イシグロ。", "2006-10-28", "3,1,7,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一条さゆり 濡れた欲情", "一条さゆり 濡れた欲情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "日活ロマンポルノ初期を代表する１本で、成人映画では初めて各種ベストテンに名を連ねた記念碑的作品。引退公演で警察の手入れを受ける、関西の名ストリッパー・一条さゆりを狂言回しに用いて、彼女にライバル心を燃やす踊り子と、いちずに尽くすヒモとの哀歓を中心に物語が綴られていく。俳優・高橋明の歌う猥歌『ナカナカづくし』の流れる中（のちの神代作品にも頻繁に使われている）、ワンピースをだらしなく着て日傘をさす伊佐山と、重い荷物を持ってうしろから付いてくるヒモの粟津を捉えた冒頭のシーンから神代的世界に魅せられてしまう。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OTHELLO", "オーソン・ウェルズのオセロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "O・ウェルズの最高傑作として「市民ケーン」と並ぶとまで絶賛されながら、興行的な失敗からフィルムが行方不明となり、長い間“幻”であった作品。1991年に発見されたフィルムをもとに復元され、その壮大な映像芸術に改めて世界中が驚いたという一編である。物語はもちろんシェイクスピアの有名な悲劇。ヴェネチア公国の将軍オセロの地位をねたむ部下のイアーゴは、オセロを失墜させようと計画を練る。オセロの妻デズデモーナに不貞の疑惑をかぶせて、夫婦仲を裂こうという企みである。オセロはイアーゴの罠にまんまとはまり、破滅への道を歩き始める……。資金難、たび重なる撮影中断などの問題から断片的に撮影された作品であるが、その断片的な各場面を細かく編集していくことで逆に独特の幻惑的リズムが生まれている。この点も天才ウェルズならではの魔術であろう。台詞は原典に忠実なまま、全体の構成を大胆に脚色し常識破りの“オセロ像”が描かれる。", "1952-01-01", "38", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MERCI LA VIE", "メルシー・ラ・ヴィ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "B・ブリエ監督が、S・ゲンズブール、A・グランベールという、フランス注目の若手女優二人を主役にすえ、女性の心のさすらいを描く。大学受験を控えたカミーユは、ある日、男に捨てられたジョエルと運命的な出会いをする。二人は意気投合し、冒険の旅へと出発するが……。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乳母車", "乳母車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "石坂洋次郎の原作を田坂具隆監督、石原裕次郎主演で映画化した青春映画の佳編。利発な娘・ゆみ子は父が囲っている愛人を訪ねるが、突然の来訪にもかかわらず彼女は明るく娘を迎え入れる。娘は父と愛人の間に赤ん坊が生まれているのに驚いたものの、愛人の弟の屈託ない言動に次第に心がなごんでいく。石坂洋次郎の一種のユートピア的な人間関係を田坂具隆監督は決して力むことなく淡々とした演出で描き、作品に不思議なリアリティーを持たせることに成功した。愛人の弟という複雑な役どころを与えられた石原裕次郎が、“太陽族”映画とはまた違った素直な演技を披露し、好感が持てる。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長三国志・甲州路殴り込み", "次郎長三国志・甲州路殴り込み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "いよいよクライマックスを迎えるシリーズ第４作。甲州の猿屋の勘助の行動を探りに行った投げ節お仲は、正体がばれて人質となる。次郎長一家は甲州に殴り込み、見事、勘助を斬りお仲を救い出す。兇状旅に出た次郎長一家。久六の裏切りで窮地に陥りお蝶も病死、と難事が重なるものの、不意をついて久六一家を叩き斬り、お蝶の遺髪を抱いて清水へと急ぐ。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("内部被ばくを生き抜く", "内部被ばくを生き抜く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "「六ヶ所村ラプソディー」「ミツバチの羽音と地球の回転」の鎌仲ひとみ監督が、2011年3月11日の東日本大震災による福島第一原子力発電所の事故を受けて、内部被ばくについて4人の専門医へインタビューしたドキュメンタリー。太平洋戦争時に広島で被ばくした自身の経験から被ばく者医療に尽力する肥田舜太郎医師、白血病やがんの子ども達のために活動している鎌田實医師、福島の現場で働く児玉龍彦医師、チェルノブイリで臨床医として45年間の経験をもつスモルニコワ・バレンチナ医師。それぞれ年齢も経歴も異なる4人の専門医の声に耳を傾け、さまざまな情報が錯綜する内部被ばくにどう対処していくべきかのヒントを探る。", "2012-04-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("維新の篝火", "維新の篝火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "池波正太郎の『色』をもとに、土方歳三とお房との恋と別れを描いた恋愛時代劇。鬼と呼ばれた剣士が、恋の真情に酔いつつも、鳥羽伏見の戦いに身を投じていく姿が、片岡千恵蔵の貫禄ある演技で綴られる。新選組に材を採った作品のなかでも哀切漂う異色の１本。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Oz: The Great and Powerful", "オズ　ザ・グレート・アンド・パワフル（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ROSARY MURDERS", "殺しのロザリオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "デトロイトで聖職者の殺人事件が連続して発生する。被害者の手には黒いロザリオが握らされていた。一方、神父ケラーは、懺悔室で自分が犯人だと名乗る男の告白を聞く。告白を他言できないケラー神父は、自ら犯人を追跡する。個性的な俳優陣を得て、重厚なストーリーが展開する。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フリーズ・ミー", "フリーズ・ミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "結婚を控えたOLのちひろの部屋に、かつて彼女をレイプした男たちが、突如として入りこんできた。自分の幸せを守るため、彼女は男たちを殺害しては、巨大冷蔵庫の中にフリーズしていく……。映画の大半が部屋の中で繰り広げられていく室内劇サスペンス・スリラー。井上晴美の熱演もさることながら、男たちの際立った個性派演技合戦も印象的。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新座頭市物語・折れた杖", "新座頭市物語・折れた杖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "勝新太郎自らが監督。行きずりの老人から形見を娘に届けるように頼まれた座頭市は、銚子の宿で女郎になっている娘を身請けしようとするが、地元の顔役に利き腕をつぶされてしまい……。勝新の演出はポルノ・シーンの挿入など、なかなかサービス精神旺盛である。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUANDO SEI NATO NON PUOI PIU’ NASCONDERTI", "13歳の夏に僕は生まれた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "世界に通じる移民問題を題材にした人間ドラマ。わけあって密航船に乗り合わせた無垢な少年が社会の現実に直面する。現代のイタリアが抱える問題を鋭く見据える、「輝ける青春」のマルコ・トゥリオ・ジョルダーナ監督の視点が光る。船から誤って転落したサンドロは、密航船に乗る不法移民の少年ラドゥに助けられる。だが、その後、船は巡視船に見つかり、乗船者は連行。サンドロはラドゥを救おうとするのだが……。", "2006-06-03", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DU RIFIFI A｀ PANAME", "皆殺しのバラード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "パリで黄金の密輸をやっている大立者のポウロ。大きな仕事をこなし安泰にみえたポウロ一味に、危機が訪れる。アメリカからチャールズという敵対する大物がやって来たのだ……。J・ギャバン、G・ラフトという米・仏を代表するギャング映画の“顔”の競演が見ものの犯罪ドラマ。", "1966-01-01", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAD　BOY　特攻", "特攻！ BAD BOYS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "香港の人気若手俳優が共演したアクション。マカオの裏社会で起きる難事件を解決する三人組のチーム“BAD BOYS”の元に、行方不明の女性を探して欲しいという依頼が飛び込んでくる。依頼人は美容師で、行方不明になっているのは彼の恋人だった。そんな折、チームに別の依頼が舞い込む。その依頼は、優秀な陸上選手だった女性を探して欲しいというものだった。一件無関係に見えた二つの事件。しかし、行方不明の女性の顔が瓜二つだったことから、闇組織による陰謀が次第に明らかになっていく……。キレのいいアクションにラブ・ストーリーが盛り込まれ見ごたえ十分。イーキン・チェンが女好きだが頼れるチームのリーダーを好演している。", "2000-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JE VOUS SALUE，MARIE", "ゴダールのマリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "タクシーの運転手ジョゼフの恋するマリー。そのマリーの前に天使ガブリエルが現れて、近く身ごもることを告げる。現代の“処女懐胎”を描いて物議をかもしたゴダール作品。冒頭にはアンヌ・マリー・ミエヴィルの短編「マリアの本」が付いている。", "1984-01-01", "26,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Zookeeper", "ズーキーパー（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEANCE ON A　WET AFTERNOON", "雨の午後の降霊祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "俳優出身のブライアン・フォーブス監督が手掛けた心霊ミステリー。生まれたばかりの子供を亡くして以来、水曜日ごとに降霊会を行っている霊媒師マイラとその夫ビル。彼らは兼ねてから計画していた少女誘拐計画を実行し、富豪の親から身代金をせしめるが……。狂気に駆られた女霊媒師と臆病な夫の運命を、ひねりの効いたプロットと物憂げなモノクロ映像で語り明かすフォーブス監督の演出が秀逸。黒沢清監督の「降霊」の元ネタでもある。", "1964-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿修羅のごとく", "阿修羅のごとく", "", "", "http://image.eiga.k-img.com/images/movie/1264/315x210.jpg?3602", 135, "79年の冬、70歳を越える父の愛人に子供がいることを知った竹沢家の四姉妹は大騒ぎ。この事件を契機に、長女の不倫、二女の夫の浮気、三女の嫁ぎ遅れ、四女の同棲とそれぞれの抱える問題が顕わになっていく。四姉妹役は大竹しのぶ、黒木瞳、深津絵里、深田恭子。79年にNHKで放映されて人気を博した向田邦子原作・脚本の同名ドラマを、「模倣犯」（02）の森田芳光が監督、「失楽園」（97）の筒井ともみの脚本で映画化。", "2003-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小津と語る", "小津と語る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "世界が誇る小津安二郎監督の生誕90周年、及び没後30周年を記念して企画されたドキュメンタリー。アメリカ、ヨーロッパ、アジアと世界的な小津信奉者の中から７人の映画監督が、亡き巨匠の写真を前にして、それぞれの場所で小津との対話を試みる。小津とともに在ることの幸福感とおののきを身に受けた後輩たちの、言葉の震えや絶句を伴うオマージュが観るものに響いてくる。「東京物語」リバイバルと併映された40分のショート・バージョンと、81分の国際版ロング・バージョンの２タイプが存在する。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Limelight", "ライムライト", "", "", "http://ecx.images-amazon.com/images/I/510N8-V5xjL._SL210_.jpg", 137, "喜劇王チャールズ・チャップリンが最後にアメリカで製作した後期の代表作。1914年のロンドンを舞台に、かつての花形喜劇役者・カルベロ（チャップリン）と、リューマチで脚が動かなくなり自殺を図った若きバレリーナ・テリーの心の交流が描かれる。共演にバスター・キートン、クレア・ブルーム。チャップリンは製作、監督、原作、脚本、音楽、主演の1人6役を務めた。", "1953-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAMILIA", "カット！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "風変わりな家族の、ある一日を風刺やユーモアとともに描いたホームドラマ。家長サンティアゴの誕生日を迎え、妻や祖母、子供たちは落ち着かない。彼らの不安は的中し、プレゼントを手渡されたサンティアゴは突然、怒り出した。そのうえ誕生パーティーの席で、彼は見知らぬ女性を口説き出し、他の人々も尋常ではない行為を見せる。実は、この家族には意外な秘密があり……。DVDタイトルは「カット！世にも奇妙な一族」。", "1996-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COMO AQUA PARA CHOCOLATE", "赤い薔薇ソースの伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "メキシコでベストセラーとなった恋愛小説を映画化した作品。家のしきたりから母の面倒をみなければならず、恋人との仲を引き裂かれた娘ティタ。料理を通じて感情を伝えるという不思議な能力を身につけた彼女の恋心が、寓話風のタッチで描かれる。主演のL・カバソスは、東京国際映画祭で主演女優賞に輝いた。", "1992-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("3.11 A Sense of Home Films", "3.11 A Sense of Home Films", "", "", "http://image.eiga.k-img.com/images/movie/57664/315x210.jpg?3983", 0, "2011年3月11日の東日本大震災を受け、河瀬直美監督が世界の映像作家に呼びかけて実現した短編集。ビクトル・エリセ（スペイン）、ポン・ジュノ（韓国）、ジャ・ジャンクー（中国）、アピチャッポン・ウィーラセタクン（タイ）、パティ・スミス＆スティーブン・セブリング（アメリカ）、桃井かおり（日本）ら21人の監督が「A Sense of Home（家という感覚）」をテーマにそれぞれ3分11秒の短編を手がけ、家族や故郷、祖国への思いをつづっていく。", "2012-01-14", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORSALINO AND CO.", "ボルサリーノ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "親友の仇を討とうと決心したロッコ。一時は敵の罠にかかってアル中患者にされてしまうが、立ち直り、再びマルセイユに舞い戻る。白昼の街中で、ギャングたちの壮絶な死闘が始まった……。製作も兼ねたドロンのまさにワンマン・ショー的作品で、ファンにはうれしいかぎり。", "1974-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’A＾GE DES POSSIBLES", "a.b.c.の可能性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "アルノー・デプレシャン作品の共同脚本家としても知られる女性監督P・フェランの長編第２作。フランス北東部の街ストラスブールを舞台に、AからJまでの頭文字の名前を持つ若い男女10人が、恋や仕事の不安を抱えながら生きる姿を綴っている。ストラスブール国立劇場付属演劇学校の依頼によって製作されたユニークな経緯を持つ作品。出演しているのは同校の最終学年の生徒たちで、等身大の若者像に取り組んだ彼らノンプロ俳優がみずみずしい演技を披露。カンヌ映画祭新人賞を得た長編第１作「死者とのちょっとした取引」(日本では映画祭でのみ上映)の幻想的な作風から一転、知的でさわやかな群像劇にまとめあげたP・フェランの演出、シナリオも秀逸だ。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT’S　ALIVEIII：ISLAND OF THE ALIVE", "悪魔の赤ちゃん３ 禁断の島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "いまや次々と生まれる“悪魔の赤ちゃん”。ついに彼らは無人島に隔離されることになった。だが、その赤ん坊たちも急速に成長し、彼ら同士で新たな胎児を身ごもってしまう。シリーズ第３作は奇形児不安にエイズ・パニックをミックスして、パワーアップ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUE　DU　RETRAIT", "夕映えの道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "イギリスを代表する女性作家、ドリス・レッシングの原作を、「洗礼」のフランス人監督ルネ・フェレが舞台をパリに移して描いた人間ドラマ。キャリアウーマンが老女との出会いを通じ、人生と老いを見つめ直していく心の機微を誠実なタッチで綴る。パリ20区ルトレ通りに暮らす孤独な老女マド。会社を経営している中年女性のイザベルは、薬局で偶然出会ったマドがなぜか気になっている。やがてイザベルはマドの自宅を訪ねるようになり、二人の親交は深まっていくが……。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海のオーロラ", "海のオーロラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "時は未来。地球の深部採掘調査を行う海底基地の周辺に、突然オーロラが発生した。やがて、地球全体を揺さぶる危機が勃発。しかしすべての原因は、採掘調査計画そのものに隠されていた……。日本初の3DフルCGによる35ミリ映画。石田純一といしだ壱成の親子が、声の出演で初共演を果たしている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETURN FROM THE RIVER KWAI", "戦場にかける橋２ クワイ河からの生還", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "第二次世界大戦中、日本軍の捕虜として鉄道建設に従事し、多くの血や汗を流した連合軍兵士たち。彼らはその後、陸路から海路を経て、日本へと送られる。前作から32年、語られることの少なかった“捕虜たちのその後”を克明に描き、戦争の悲劇を真正面から捉えた戦争映画の力作。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JULIUS CAESAR", "ジュリアス・シーザー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "シェイクスピア名作の完全映画化。名優をそろえ、舞台構成そのままに再現された映像が味わい深く印象的だ。シーザー暗殺の一幕、ブルータスらの反乱軍と戦うアントニーの物語などなど……。歴史ものの醍醐味も十分に堪能できるスペクタクル作品。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA COURSE DU LIE｀VRE A｀ TRAVERS LES CHAMPS", "狼は天使の匂い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "「シンデレラの罠」「殺意の夏」の原作者として知られるS・ジャプリゾが、D・グーディスの小説『暗い金曜日』を脚色し、R・クレマンが監督した暗いパッションに彩られた一編。ヘリコプターの墜落により、ジプシーの子供たちを死なせてしまったトニーは、ジプシーの頭領の執拗な追跡を逃れ、逃亡生活を送っている。ところが彼は、ある殺人現場を目撃したことからギャング一味からも追われるようになる……。R・ライアンとJ・L・トランティニャンという米・仏のスターの異色の顔合わせにも注目したいところ。", "1972-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAJOR PAYNE", "デイモン・ウェイアンズはメジャー・ペイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "２万人以上の殺人マシーンを教育したペイン少佐は、そのあまりの過激さに、軍隊をクビになってしまう。一般社会に適応できないペインが、ようやくありついた仕事は、落ちこぼれ生徒を叩き直す教官だった。その訓練の破天荒さゆえに、様々な摩擦を引き起こしつつも、ペインは子供たちと少しずつ心を通わせ始める。チャールトン・ヘストン主演の「メジャー・ペイン」（1955）をコメディ映画としてリメイク。「愛と青春の旅だち」や「地獄の黙示録」などの映画のパロディーや、“ムーンウォーク”などの懐かしのダンスシーンがみどころ。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大菩薩峠・完結編", "大菩薩峠・完結編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "竜神の滝の断崖から落ちた盲目の机龍之助は、危うく一命を取り留める。やがて、顔半分にヤケドの跡があるお銀とともに大菩薩峠に舞い戻った龍之助。宇津木兵馬と宿命の対決と相なるが、足元からくずれ出す笛吹川の濁流にのまれていくのだった。DVDは「大菩薩峠　DVD-BOX」に収録。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dancer in the Dark", "ダンサー・イン・ザ・ダーク", "", "", "http://image.eiga.k-img.com/images/movie/1044/315x210.jpg?2203", 140, "アメリカの片田舎。チェコ移民のセルマは息子ジーンと2人暮らし。つつましい暮らしだが、隣人たちの友情に包まれ、生きがいであるミュージカルを楽しむ幸せな日々。しかし彼女には悲しい秘密があった。セルマは遺伝性の病で視力を失いつつあり、手術を受けない限りジーンも同じ運命を辿ることになるのだ……。", "2000-12-23", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はじまりの記憶　杉本博司", "はじまりの記憶　杉本博司", "", "", "http://image.eiga.k-img.com/images/movie/57734/315x210.jpg?3836", 81, "国内外で多数の賞を受賞している世界的な写真家で美術家の杉本博司に初めて長期密着したドキュメンタリー。杉本が在住するニューヨークをはじめ、日本、南仏、シドニーと国境を超えて創作活動を行う杉本の現場に密着。杉本の素顔や目指すものを明らかにしていく。2010年にWOWOWで放送された「はじまりの記憶　現代美術作家・杉本博司」に新撮映像を加え、新たに劇場版として完成させた。", "2012-03-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霧都情仇／MISTY", "ミスティー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "L・カーファイとG・イップの共演で描いたハードボイルド。マフィアのボスの養女チャーリーに恋をした二人の大学生マークとケントは、ケンカ沙汰に巻き込まれて退学処分に。困った彼らは、チャーリーのボディガードであるハンクの勧めで、黒社会へと身をやつしていく。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレヨンしんちゃん 雲黒斎の野望", "クレヨンしんちゃん 雲黒斎の野望", "", "", "http://ecx.images-amazon.com/images/I/61lUO8ljysL._SL210_.jpg", 96, "劇場版アニメ・シリーズ第３作。両親、犬のシロと時間犯罪者の破壊活動阻止のため、戦国時代にタイムスリップしたしんのすけ。そこで吹雪丸という少年剣士と出会ったしんのすけは、雲黒斎に殺された吹雪丸の両親の敵討ちを手助けすることに。雲黒斎の城にたどり着いた一行は、敵の意外な正体を知ることになる。変身アイテムで大人になるしんちゃんに注目。", "1995-04-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANCING LADY", "ダンシング・レディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "怪しげな寄席芸人一座の踊り子ジェニーは、警察の摘発で投獄されるが、青年富豪トッドによって保釈される。彼女は、失敗したらトッドと結婚することを条件に、名監督パッチの一座でコーラスガールの仕事を得た。めきめきと才能を開花させていくジェニーは、ついに主役の座を獲得するが、トッドの策略によって公演中止の憂き目に遭う……。J・クロフォードと、自身の名で登場し、映画デビューを飾ったF・アステアとの華麗なダンス・シーンに注目。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四季の愛欲", "四季の愛欲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "丹羽文雄の『四季の演技』を中平康監督が演出した愛欲映画。作家・暁の母、浦子はかつて幼い暁を捨てて男のもとへ走り、彼が名声を得たあとに母親として名乗りを上げた女だ。彼女と折り合いの悪い暁の妻・吟子は、浦子の情夫である実力者を寝取り、ミスユニバースで優勝しようとするのだが……。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒミコさん", "ヒミコさん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「ラッパー慕情」の藤原章監督が、1980年代の寂れた地方都市を舞台に、ナゾの美女・ヒミコと彼女を取り巻く男たちの運命を描いた異色の青春群像劇。かつて町を出た少女・ヒミコが、美しい大人の女性となって戻ってきた。甲子園出場を目指して練習に励む野球部員ムカワは、ひょんなことからヒミコと出会い心を奪われるが……。ヒミコ役の個性派女優・宮川ひろみをはじめ、脚本家の高橋洋、映画監督の園子温ら異色の豪華キャストがそろう。", "2007-10-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("覇王別姫", "さらば、わが愛／覇王別姫（はおうべっき）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 172, "演ずることに全てを捧げた二人の京劇俳優の波乱に満ちた生きざまに、中国の近・現代史を重ね合わせた壮大なる愛憎劇。遊女である母に捨てられ、京劇の養成所に入れられた小豆。淫売の子といじめられる彼を弟のようにかばい、辛い修行の中で常に強い助けとなる石頭。やがて成長した二人は京劇界の大スターとなっていた……。時代に翻弄されながらも愛を貫こうとする女形、程蝶衣を演じるレスリー・チャンの妖艶さも話題になった。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Patti Smith: Dream of Life", "パティ・スミス：ドリーム・オブ・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/54644/315x210.jpg?4599", 109, "1970年代にニューヨーク音楽シーンを席巻したパンクの女王、パティ・スミスを、写真家のスティーブン・セブリングが約11年もの歳月をかけて追いかけたドキュメンタリー。コンサートツアーのバックステージやニューヨークの自宅まで密着取材し、詩、絵画、写真など多分野で活動するパティの素顔に迫る。", "2009-08-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE＆DEATH ON LONGISLAND", "ラブ＆デス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「ベニスに死す」の設定を意識したコメディ。頑固者の英国人作家デアスが、間違って入場した映画館で、アメリカ製のB級青春映画を見るハメに。だが、そこに出演していたアイドル、ロニーの美貌がデアスの心を捉えた。それ以来、デアスは彼の熱烈なファンとなり、アメリカに渡ってロニーとの面会を果たすが……。プライドの高い英国紳士がコッソリと雑誌の切り抜きやビデオ収集に励む、その姿が何とも言えずおかしい。名優J・ハートが、そんな主人公をユーモアとペーソスたっぷりに好演。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昆虫探偵　ヨシダヨシミ", "昆虫探偵　ヨシダヨシミ", "", "", "http://image.eiga.k-img.com/images/movie/55124/315x210.jpg?1560", 0, "青空大地の人気ギャグコミックを、哀川翔主演で映画化。どんな生物ともコミュニケーションをとれるヨシダヨシミが営むヨシミ探偵社は、主に昆虫関係全般の依頼を請け負う昆虫専門探偵社。カブトムシの不倫、コオロギの失踪、チョウの拉致事件など、珍事件に挑むヨシダの姿をコミカルに描く。監督は「平凡ポンチ」の奇才・佐藤佐吉。", "2010-04-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRENOM CARMEN", "カルメンという名の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "絵画が前面におしだされた前作「パッション」に対して、録音のフランソワ・ミュジーを得て音楽が主役級で登場する本作は、ゴダール80年代劇場用長編映画の第3作で、スイスに設立したゴダールの新プロダクションの第1作。カルメン神話の生みの親メリメの短編をもとに、脚本のメルビルが舞台を現代のパリに移した。憲兵隊員のジョゼフが銀行強盗一味のカルメンに恋をする。警察に捕まったジョゼフの留守中にカルメンは他の男に心を移していた……。", "1984-06-23", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEYOND THE KILIMANJARO：ACROSS THE RIVER OF BLOOD", "復讐のキリマンジャロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "元マフィアのヒットマンで、今は動物学者の助手となり、ハンターとしてケニアで暮らす男。恋人とともに平和に生きようとする彼だが、彼の過去に気付いた人物が、再びヒットマンに引き戻そうとする……。アフリカの雄大な自然をバックにしたサスペンス・アクション。アフリカン・コーラスを使った音楽も雰囲気を盛り上げる。", "1990-01-01", "4,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブラック・コメディ ああ！馬鹿", "ブラック・コメディ ああ！馬鹿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "出世第一主義のサラリーマンのなれの果てを風刺たっぷりの笑いにくるめた異色喜劇。係長の赤沢が結婚を申し込んでふられたタイピストは常務の２号だ。ところがこの娘が食わせ者。常務の目を盗んで大学生とねんごろになるわ、狂言自殺はするわ。結局、赤沢は常務から彼女を押しつけられる。出世のためと、この厄介者の処理に彼は奔走するが……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夕陽に赤い俺の顔", "夕陽に赤い俺の顔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "篠田正浩監督、寺山修司脚本によるポップアート・コメディの快作。悪徳土建屋に父親を殺された娘が復讐を誓う。土建屋は８人の殺し屋を雇って娘を殺そうとするが、ガンマニアの青年が娘を救う。全編パロディー精神に満ちた寺山修司の脚本が見事。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RIKI−OH", "RIKI-OH 力王（RIKI-OH）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "恋人を襲った暴漢を誤って死なせてしまい、刑務所に入れられたリキオウ。刑務所内でもその正義感ぶりを発揮して、囚人たちにいじめられている老人を助けるが、それが狂暴な囚人管理部長の怒りをかって……。近未来の刑務所内を舞台に、リキオウと冷血非道の殺人鬼らが繰り広げるスーパー・アクション。", "1991-01-01", "76,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HORROR EXPRESS", "ゾンビ特急“地獄”行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "満州の秘境で発見された類人猿の化石。だがそれは200年前に地球にやって来てから眠り続けていた異星人で、シベリア横断鉄道で移送中にいきなり目覚める。やがて乗客が一人ずつ変死体となって発見されていき、車内はパニックに陥る……。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("College Road Trip", "ロード・トリップ　パパは誰にも止められない！", "", "", "http://ecx.images-amazon.com/images/I/51JiHu4EfOL._SL210_.jpg", 83, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SURF NAZIS MUST DIE", "悪魔の毒々サーファー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "巨大地震に襲われた後の、近未来のカリフォルニア。無法の限りを尽くして海岸一帯を支配しているのは、“サーフのヤッちゃん”ことサーフ・ナチス。しかし、彼らに敢然と戦いを挑むタフな婆さんが現れた。低予算映画でおなじみトロマ映画社が配給した、“悪ノリ”ニュー・アクション。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　CINQ　HEURES　DE　L’APRE｀S−MIDI", "午後の五時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「りんご」で18歳にして監督デビューし、２作目「ブラックボード・背負う人」ではカンヌ映画祭審査員賞を最年少で受賞したS・マフマルバフ。そんな彼女が23歳のアフガン女性を主人公に、タリバン政権崩壊後の復興の実情を痛切に描く人間ドラマ。閉ざされた国でサミラが本作を撮る困難を収めた、妹ハナ監督の「ハナのアフガンノート」を観るとより面白い。就学や就労を禁じられているアフガニスタン女性のノクレは、保守的な父になんとか宗教学校へ行くことを許された。しかし父の目を盗み、一般的な学問を学べる学校へ向かう。彼女の夢は、女性初の大統領になることだったのだが……。", "2003-01-01", "15,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WICKED LADY", "２つの顔の貴婦人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "友人の結婚式に出席した貴婦人バーバラは、その場で新郎のスケルトン卿を誘惑し、彼の夫人になってしまう。だが退屈な貴族生活に飽きた彼女は愛人を作り、二人で馬上強盗などの悪事を重ねていくのだった……。17世紀イギリスの実話を映画化。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暁の合唱", "暁の合唱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "戦前の秋田を舞台にした、バスの運転手になろうと奮闘する女と、彼女を助けその夢をかなえてやろうとする男たちの物語。話の内容があまりにリベラルなために、戦後の作品と見まごうばかりである。清水宏と石坂洋次郎という二人の自由人による、完全に時代から逸脱した明朗快活な秀作。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("D−TOX", "D−TOX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "アクション・スター、S・スタローンがサイコ・サスペンスという異色ジャンルに挑戦したスリラー。殺人鬼に恋人を殺されたFBI捜査官マロイは、心の痛手から立ち直れず、雪深い山奥のにある警官専門の精神治療施設D−TOXへ。ところが、そこにも殺人鬼の魔の手が。脱出不可能の密室で、同房の患者たちが次々と殺され……。患者たちにまぎれて息を潜めるシリアルキラーの意外な正体、限定空間の息詰まる攻防がスリルをあおる。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AVVENTURA", "情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "アントニオーニの名を世界に知らしめた傑作。大使の娘アンナは、恋人と親友を伴って地中海を旅行中、突然失踪する。残された二人は彼女を捜すが、いつしか情事を重ねるようになり……。現代人の“愛の不毛”を倦怠感の中にセンシティブに描き出している。", "1960-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WATERHOLE NO.３", "荒野の隠し井戸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "J・コバーンの魅力が炸裂するコミカル・ウエスタン。バクチ打ちのコールは、悪党４人組が盗んだ金塊の隠し場所を記した地図を手に入れる。保安官の馬を盗んで荒野のド真ん中で金塊を手に入れたまではよかったが、ロバに乗って追跡してきた保安官に逮捕され、そこに悪党４人組もやって来て……。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("光る海", "光る海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "石坂洋次郎原作による明朗青春ドラマ。作家志望の美枝子は、TV局に勤める野坂をひそかに思っているが、彼はOLの和子と相思相愛の仲。三角関係に悩む美枝子が、友人の出産、母の再婚といった問題を通して人間的に成長し、野坂と和子を祝福して自らも新人賞に入選する。吉永小百合は、内心はセックスに興味を持ちながらも、外見は勝気なインテリという娘をほのかなお色気を交えて好演。中平康も原作の味わいを生かした軽妙なタッチで仕上げている。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　機動戦士ガンダム00 A wakening of the Trailblazer", "劇場版　機動戦士ガンダム00 A wakening of the Trailblazer", "", "", "http://image.eiga.k-img.com/images/movie/55064/315x210.jpg?2161", 120, "2007～09年にかけて計2シーズンが放映されたTVアニメ「機動戦士ガンダム00（ダブルオー）」の完結編となる劇場版。西暦2314年、刹那・F・セイエイらソレスタルビーイングの面々は、平和を取り戻しつつある世界を陰から見守っていた。しかし、ソレスタルビーイングの設立者イオリア・シュヘンベルグがかつて予見した「来るべき対話」のときが訪れ、刹那は新たなガンダム「ダブルオークアンタ」に乗り、最後の戦いにおもむく。", "2010-09-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月の砂漠", "月の砂漠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "2001年カンヌ映画祭コンペに出品された青山真治監督作品。久々の映画出演となった三上博史がネット・バブルで富を得た主人公に扮し、埋めようのない喪失感を抱えた男の彷徨が描かれていく。ネット関連のベンチャー企業で成功を収めた永井は、今では妻子に逃げられ、会社倒産の危機に直面していた。そんな永井と知り合った青年キーチは、彼の妻アキラを探し出すのだが……。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女衒・ZEGEN", "女衒・ZEGEN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "明治後期から昭和初期にかけて東南アジアを舞台に暗躍した女衒・村岡伊平次の半生を映画化した作品。伊平次は、香港の海賊に捕まっている日本人の娘たちを助け出したことから女衒を始め、やがて商売も繁盛し４つの娼館を経営するまでになる。重喜劇と評される今村独特のユーモア感覚にあふれる作品。「楢山節考」でもを撮影を務めた栃沢正夫が担当している。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALL OF ME", "オール・オブ・ミー 突然半身が女に！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ジャズ狂の弁護士ロジャーの体に、エドウィンという女性の霊魂が突然ちん入。右半身の自由を侵入者に奪われてしまった男に数々の災難が巻き起こる。K・ライナー監督による奇想天外なコメディ映画。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAVY　METAL　F．A．K．K．２", "ヘヴィメタルFAKK２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "アメリカの若者たちから熱狂的な支持を得ているアダルト向けグラフィック・ノベル誌『へヴィメタル』から生まれたSFアニメ映画。女優ジュリー・イーストンをモデルにした女戦士ジュリーの活躍を、暴力とエロスを満載した過激なタッチで描き出す。悪のパワーを宿したタイラーは、飲む者に不死を与え、宇宙を支配できる“闇の間”を目指して進んでいく。彼に家族と仲間を殺された女戦士ジュリーは、復讐と、タイラーの野望を阻止するため後を追うのだった……。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ПОДВИГ　РАЗВЕДЧИКА", "諜報員", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ジャン・リュック・ゴダールやジャック・リヴェットらが、その文体と話術をほめたたえた、ソビエトの知られざる名匠B・バルネット。彼が終戦直後に撮り上げた、スリリングなスパイ・ドラマ。1941年、ドイツ陸軍に潜入し、機密文書を入手するように命じられたアレクセイ少佐。敵の真っ只中で窮地に陥りながら、彼は任務を遂行するが……。", "1947-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クライング・フリーマン", "クライング・フリーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "日本だけでなく欧米でも人気のコミックを「ジェヴォーダンの獣」のフランス人監督のC・ガンズが映画化。サンフランシスコで起きた殺人事件を目撃した若くて美しいエム・オハラは、犯人から“私の名前は薫”と告げられた。警察に呼ばれた彼女は、そこで二度目の殺人を目撃する。それは、日本のヤクザの組長が殺されたもので、犯人は最初の男だった。目撃者であるエムは、警察に保護されるが、犯人は彼女を殺しに姿を現す。しかし、エムと男はどこか惹かれ合うものを感じるのだった……。監督の希望により、「男たちの挽歌」などで知られる香港ノワールの雄、デヴィッド・ウーが編集を担当している。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GAUDI AFTERNOON", "ガウディアフタヌーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "舞台はスペイン・バルセロナ。ガウディの緻密かつ奇抜な建築物に見立てて、複雑に絡まった人間模様をコミカルに描く人間ドラマ。バルセロナに住むアメリカ人女性の翻訳家カサンドラは、謎の女フランキーから、失踪した夫を探すように依頼される。お金に目が眩んで捜索に乗り出したカサンドラだったが、女と思った人間が男だったり、男と思えば女で……と、次々と明らかになる予想外の秘密に、混乱していくはめに。", "2000-01-01", "1,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Street Kings", "フェイクシティ　ある男のルール", "", "", "http://image.eiga.k-img.com/images/movie/53664/315x210.jpg?4817", 109, "ジェームズ・エルロイ（「L.A.コンフィデンシャル」）による原案・脚本をキアヌ・リーブス主演で映画化した犯罪ドラマ。犯人逮捕のためには手段を選ばないという自身のルールを貫くロス市警の刑事ラドロー（リーブス）は、かつての相棒を目の前で殺される。自らのルールでその犯人を追うラドローだったが、事件の裏には更なる巨悪が潜んでいた……。監督は「トレーニング・デイ」の脚本家として知られるデビッド・エアー。共演はフォレスト・ウィテカー、ヒュー・ローリーら。", "2009-02-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAJAMA PARTY", "パジャマ・パーティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "女の子たちがパーティーをしているところに、突然ゴーゴーという火星人たちが落ちてくる。地球侵略にやって来たゴーゴーだったが、地球人の女の子コニーと恋に落ちてしまった。1960年代アメリカが舞台の青春ラブ・コメディ。喜劇王B・キートンの名演技も見られる。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sodom and Gomorrah", "ソドムとゴモラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 154, "　指導者ロット率いるヘブライ人の一行が苦難の旅の果て、ヨルダン川のほとりにたどり着く。腐敗と退廃の町ソドムとゴモラの女王ベラは彼らに土地を与え、友好関係を結び、やがてロットはベラから送られた奴隷イルディスと愛し合うようになる。そんな折、王座を狙っていたベラの弟アスタロフと、彼と密かに通じていたヘラム族が奇襲を仕掛けてくるが……。旧約聖書の物語に材をとり、ソドムとゴモラの町に渦巻く陰謀、そしてヘブライ人たちの戦いと苦難を描く。", "1962-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLATLINERS", "フラットライナーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "死んだ後にはどんな世界が待っているのか？　禁じられた領域に興味を持った５人の医学生が自らの体を実験台として、心臓を停止させ脳死寸前に蘇生するという恐るべき体験をする。果たして彼らが見た死後の世界とは……？　人気スターが結集し、幻想的な映像世界が展開するサスペンス・スリラー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLASH GORDON: SPACESHIP TO THE UNKOWN", "フラッシュ・ゴードン 宇宙帝国の反乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "原作は1934年、A・レイモンドの新聞の連載マンガ。アメリカではスーパーマンと並び人気の高いSFコミックスで、「スター・ウォーズ」の原点ともなった作品。惑星モンゴからの放射能のせいで地球に怪現象が起きていると知った科学者が、フットボール選手のフラッシュを送り込み悪の皇帝ミンと対決させる。「火星人対地球人」というタイトルで公開されたこともある。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴト師株式会社II", "ゴト師株式会社II", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "今回は、彼らの上前をはねるライバル・ゴト師軍団が登場。パチンコの裏側を知りつくしたプロ対プロの、ハイテクを駆使した騙し合いが見もの。敵方ゴト師のリーダー、鳥羽役に名高達郎が扮している。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陽はまた昇る", "陽はまた昇る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "VHSビデオの開発に携わった男たちの実話をもとにしたヒューマン・ストーリー。1970年代、日本ビクターの開発技師、加賀谷はビデオ事業部へ異動。それは非採算部門の事業部を立て直すためでもあった。リストラではなく、新たな商品開発を部下に促す加賀谷の労苦の日々が始まった……。単なる会社美談に終わらせず、あくまでも人間同士の心の葛藤に焦点を絞ったあたり好感が持てる。競争相手のベータ・ビデオを開発したソニーを、きちんと実名で登場させているあたりも良い。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ten Tiny Love Stories", "彼女の恋からわかること", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「彼女を見ればわかること」のR・ガルシアの長編２作目。10人の女性がそれぞれの人生の中での特別な男性についてモノローグで語る愛のオムニバス。一つのエピソードにつき一人の登場人物がカメラに向かって話しかけるシンプルなスタイルで、初体験、元彼との再会、恋人選びのルール等、他愛ない思い出話から、悔悟に満ちた告白まで、様々な出会いと別れが語られる。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺しの烙印", "殺しの烙印", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "“訳のわからない映画を撮る奴はいらない”と当時の日活社長を激怒させ、鈴木清順が日活をクビになる直接の原因となったいわくつきの作品がこれだ。その特異なスタイルが1960年代の観客や批評家の多くに理解されず、興行的には惨敗。清順美学が理解されるにはまだ10年以上も早かったようだが、一部のファンの間では高い人気を誇る。たった１度の失敗で組織から抹殺されるハメになった殺し屋が、単身組織に立ち向かっていく……。ご飯の炊けた匂いが大好きという殺し屋や、アドバルーンを使った殺人などのユーモアが散りばめられ、海外にも清順の名を知らしめた傑作。なお、脚本の具流八郎とは日活助監督たち６人と美術監督・木村威夫、鈴木清順を含めた集団ペンネーム。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I PICARI", "マンマ・ミーア 人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "二人のゴロツキが知り会い、気ままで自由な生活を夢見てひともうけをたくらむコミカルなロード・ムービー。ぐうたらな親に育てられ、あげくの果て売り飛ばされた男が、イカサマ賭博師と受刑人が働く船の中で知り合い、意気投合する。二人は囚人の暴動に乗じて船から脱出し、自由を手に入れる。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES MARIS，LES FEMMES，LES AMANTS", "夫たち、妻たち、恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "パリの人々が一年の最大の楽しみにする、約30日のバカンス。本作では妻たちがパリで女だけのバカンスを楽しみ、一方、夫たちは子供と一緒に童心に帰ってイル・ド・レ海岸でバカンスを楽しむ。様々な夫と妻が思い思いに楽しむ不倫、情事、そして恋。多彩な人間模様を、エスプリを効かせて描いたトマ監督の手腕が光るシャレた作品。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE VENT DE LA NUIT", "夜風の匂い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "フランスのベテラン、P・ガレルが大女優C・ドヌーヴを主演に据えて描いたラブ・ストーリー。年下の恋人ポールと不倫をしている人妻エレーヌ。だが、ポールはエレーヌの一途な思いを負担に思い始めていた。ポールの心変わりを察し、空虚な気持ちで夜の街をあてもなくさまようエレーヌは、偶然出会ったセルジュと意気投合する……。", "1999-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一心太助 天下の一大事", "一心太助 天下の一大事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "腕も度胸も天下一品の魚屋・一心太助が、大久保彦左衛門とがっちりスクラムを組んで、川勝丹波守の御城改築にからむ不正をあばく。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小さな町の小さな映画館", "小さな町の小さな映画館", "", "", "http://image.eiga.k-img.com/images/movie/56354/315x210.jpg?4781", 105, "北海道の南岸、太平洋に面した小さな町・浦河町で90年以上続く小さな映画館「大黒座」にまつわるドキュメンタリー。人口わずか1万4000人余りの浦河町には、大正7年から町の人々に愛され続けている映画館「大黒座」がある。桟敷席で火鉢を抱えて映画を見た時代から、深夜まで映写機を回すほど栄えた時代、テレビやビデオの普及で暖房費のかかる冬場は土日だけ営業していた時代など、大黒座の歴史と歴代館主の思いをたどる。", "2011-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ledge", "ザ・レッジ 12時の死刑台", "", "", "http://image.eiga.k-img.com/images/movie/57444/315x210.jpg?4449", 101, "「ニューオーリンズ・トライアル」「隣人」などの脚本を手がけたマシュー・チャップマンがメガホンをとったサスペンススリラー。高層ビルから身を投げようとする男、ギャビンに対し、刑事のホリスは説得にあたるが、ギャビンは「自分が飛び降りなければ他の誰かが死んでしまう」と話し、飛び降りようとする理由を語りはじめる。ギャビンは職場の新人としてやってきた同じアパートの住人シェーナと親しくなり、ある日一線を越えて愛し合う。しかし、シェーナにはすでにジョーという夫がおり、2人の関係を疑ったジョーが驚くべき行動に出て……。主演はリブ・タイラーと英国の新進俳優チャーリー・ハナム。共演にテレンス・ハワード、パトリック・ウィルソン。", "2012-02-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE JERK", "天国から落ちた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "他人を疑うことを知らない善良すぎる男が、成功を夢見て育ての親の黒人一家をあとにした。苦労はしてもいつもハッピーな彼は、やがてひょんなことから億万長者になったのだが……。風刺のきいたコメディ・タッチの作品。監督のライナーは、俳優からテレビの演出家を経て映画監督になったのだが、この映画でも自分自身の役で出演し、ヨリ目の演技で笑わせてくれる。ビデオ発売時タイトルは「スティーブ・マーティンの天国から落ちた男」。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blood the Last Vampire", "ラスト・ブラッド", "", "", "http://image.eiga.k-img.com/images/movie/53434/315x210.jpg?2931", 91, "プロダクションI.Gのアニメ「BLOOD THE LAST VAMPAIRE」を、「猟奇的な彼女」のチョン・ジヒョン主演で実写化。16世紀から400年に渡って続いてきた人間と“オニ”と呼ばれる吸血鬼たちの戦い。日本刀でオニたちを斬る謎の少女サヤは、在日米軍基地で発生した殺人事件の陰に究極のオニと呼ばれる“オニゲン”の存在を感じ、基地内のアメリカ人学校に潜入するが……。オニゲン役で小雪が出演。監督は「キス・オブ・ザ・ドラゴン」のクリス・ナオン。", "2009-05-29", "6,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("home", "home", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "小林貴裕が日本映画学校の卒業制作で撮ったビデオ作品で、若者の“引きこもり”を扱った衝撃的な内容ゆえに劇場公開されたドキュメンタリー。家族崩壊の危機に直面した小林自身の壮絶な闘いの記録でもある。母親からの電話で久しぶりに長野県の実家に戻った小林が、７年間も家の中にこもっている兄と、彼の暴力に脅える母親の姿を目の当たりにする。悲惨な現状を打開する術を見いだすことができない小林は、やがてハンディカメラのファインダーを通して兄とのコミュニケーションをとろうと試みる。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("心香／THE TRUE HEARTED", "心の香り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "中国映画界でチェン・カイコー、チャン・イーモウ以来の大型新人といわれたスン・チョウ監督の作品。両親の離婚騒ぎのために、まだ会ったこともない遠い町の母方の祖父の家に預けられることになった京劇役者の少年と、今は引退して年金生活を送る京劇の名優であった孤高な祖父、そして二人をとりまく町の人々との心の交流を優しい視点で綴っていく。広東の街のたたずまい、京劇や仏教の信仰など伝統的なものを背景に絡めながら現在の中国に暮らす人々の生の姿を、静謐かつ繊細な映像のなかに叙情的に、しかも生き生きと浮かびあがらせている。近年の中国映画を代表するエポック・メーキングな秀作。", "1992-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROCK＆RULE", "ROCK＆RULE ロックン・ルール", "", "", "http://image.eiga.k-img.com/images/movie/51024/315x210.jpg?2973", 79, "テクノロジーを駆使した映像とロックが一体となったファンタスティックSFアニメ。核戦争後、人類に代わって誕生したミュータントの退廃した社会を背景に、スーパースターと無名のバンドのロック対決が描かれる。豪華ミュージシャンのオリジナル曲がギッシリ。", "2010-12-03", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スーパーの女", "スーパーの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "伊丹監督の“女”シリーズの第５作。ごく普通の主婦の井上花子は、小学校の同級生、小林五郎に偶然再会する。五郎は、スーパー正直屋を経営しているが、まったく駄目なスーパーで商品は売れず、店員もやる気がなかった。スーパー大好きで、スーパーのことなら何でも知っている花子は、正直屋を日本一のスーパーにするために、正直屋に就職するが……。宮本、津川の伊丹映画の常連のほか、三宅裕司、松本明子、小堺一機らバラエティー系のタレントが多数出演して、涙ぐましい努力の行方を盛り上げる、まさにスーパー・コメディだ。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHASING　HOLDEN", "ライ麦畑をさがして", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "永遠の名作として世界中で愛されている小説『ライ麦畑でつかまえて』。同書を心の拠りどころにしている少年が、サリンジャーを探す旅の過程で成長していく姿を素朴なタッチで描き出す。父親との深い確執に悩み傷つく18歳の少年ニール。『ライ麦畑でつかまえて』の作者サリンジャーなら、自分の深刻な事態を救ってくれると考えた彼は、演劇クラスで知り合ったTJと一緒に、ニューヨークからサリンジャー邸を探す旅に出る。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水からの速達", "水からの速達", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "東京都西多摩郡日の出町にある、山を削って作られた巨大なゴミ捨て場。その周辺住民の声を集め、汚染されていく水源の実態を探ったドキュメンタリー。ゴミが地下水を汚染していく現実を、８ヵ月にわたる記録によって描き出し、環境破壊に対するメッセージを伝えている。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BANDA J＆S CRONACA CRIMINALE D", "J&S さすらいの逃亡者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "マカロニ・ウエスタンの原形となった名作「続・荒野の用心棒」のコルブッチ監督の異色のウエスタン。脱獄した強盗犯のジェドは、ひょんなことから無法者への願望を抱く墓掘り女のサニーと行動をともにすることになり、彼らの逮捕に執念を燃やす保安官フランシスカスドと追いつ追われつの追跡劇を展開する。ハリウッド西部劇では決して見ることのできない、雨に濡れドロだらけになったリアルな西部が描かれている。全編を飾るE・モリコーネのロマンティックなテーマ曲が美しい。", "1973-01-01", "4,9,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WATER ALSO BURNS", "水もまた燃える", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "1985年東京国際映画祭ヤングシネマで、小津記念賞に輝いたオズゲントルク監督の第３作。新作の構想にふける映画監督の生活を、現実と幻想を織り交ぜ描き出した、自伝的色合いの強い作品。映画監督の心理的苦悩を鮮明に映す、センシティブな映像美が秀逸。", "1987-01-01", "32,7,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アリス サンクチュアリ", "アリス サンクチュアリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "17歳の一卵性双生児、都立高校３年生の明子とろうあ学校に通う恵子。姉妹は幼い頃から二人にしか聞こえない“心の声”で意思を伝え合ってきた。ある日、明子が柔道部のエース、武司を好きになったことを契機に、姉妹の間に変化が生じる……。静謐な映像と入念に設計された色彩と美術で構築された、愛の迷宮というべき少女たちの寓話。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chasers", "逃げる天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "D・ホッパーの監督作には珍しいコメディ・タッチのロード・ムービー。堅物の中年男と調子のいい青年という二人の海軍兵が脱走兵を護送することに。しかし、この囚人がグラマーな美女であったことから、波乱が巻き起こる。ホッパー自身はダッチワイフを抱えたゲイという、得意のヘンな役で出演。ビデオ発売時タイトルは「チェイサーズ」。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛と青春の宝塚　恋よりも生命よりも", "愛と青春の宝塚　恋よりも生命よりも", "", "", "http://image.eiga.k-img.com/images/movie/54574/315x210.jpg?3440", 0, "元宝塚のトップスターたちと豪華男優陣が共演を果たした大ヒットミュージカル「愛と青春の宝塚」をデジタルシネマ化。昭和14年、リュータンこと嶺野白雪がトップスターを務める宝塚歌劇団に、リュータンに憧れるトモとベニ、自分の生きる場所を求めるタッチーらが入団してくる。様々な経験を重ねながら成長していく彼女たちだったが、やがて日本に戦争の影が迫り……。新宿コマ劇場のラストを飾った2008年冬の公演を完全収録。", "2009-07-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ASSAULT ON DEVIL'S ISLAND", "シャドー・ウォリアーズ　地獄の要塞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "人気プロレスラー、H・ホーガンの主演によるアクション。米海軍特殊部隊を率いるマイクは死闘の末、キューバの麻薬組織の大物ガリンドーを逮捕。ところが、マイクを裏切った部下のフレイカーが人質を取って孤島に立てこもり、ガリンドーの釈放を要求してきた。島の周辺海域はサメの棲息地であり、海中からの侵入は不可能と思われたが、怒りに燃えるマイクは敢えて危険に挑む……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tripping", "幻遊伝", "", "", "http://image.eiga.k-img.com/images/movie/33614/315x210.jpg?4153", 102, "日本・台湾・中国・香港から集められた選りすぐりのスタッフ・キャストにより制作された痛快ファンタジック・アドベンチャー。キョンシーや悪霊たちがはびこる遥か昔の台湾にタイムスリップしてしまった日本人の女の子シャオディエが、盗賊の少年らとともに繰り広げる大冒険を描く。シャオディエを田中麗奈が、その相手役を「藍色夏恋」でのデビュー以来、日本でも人気急上昇中の華流スター、チェン・ボーリンが演じる。", "2006-08-26", "53,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魔法少女を忘れない", "魔法少女を忘れない", "", "", "http://image.eiga.k-img.com/images/movie/55994/315x210.jpg?2886", 95, "ある日突然、母親から紹介された妹みらいが、元“魔法少女”であることを知った高校生・悠也は、自分の知らない世界を生きてきた妹にどう向き合えばよいのか戸惑う。それでも次第に周囲に溶け込んでいくみらいを大切に思うようになってきた悠也だったが、ある日、いずれみらいが皆の記憶から消えてしまうということを知り……。しなな泰之による人気ライトノベルを実写映画化。「D-BOYS」の高橋龍輝が映画初主演。", "2011-04-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cose dell'altro mondo", "別世界からの民族たち", "", "", "http://image.eiga.k-img.com/images/movie/57364/315x210.jpg?1349", 90, "", "2011-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUST LOVE DOGS", "理想の恋人.com", "", "", "http://ecx.images-amazon.com/images/I/51aS6fYVkdL._SL210_.jpg", 98, "クレア・クックのベストセラー小説を映画化したロマンチック・コメディ。離婚で傷ついた女性が、おせっかいな家族に後押しされて、出会い系サイトで恋人探しを開始。多くの男性と奇妙なデートを重ねながら運命の人を探すバツイチ女性を、ダイアン・レインがチャーミングに好演する。幼稚園の先生をしているサラは離婚して8ヵ月。彼女の幸せを願う姉妹が、サラになりすまして出会い系サイトに登録してしまい……。", "2005-10-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鏡心", "鏡心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "「狂い咲きサンダーロード」などで知られるインディーズ映画界の重鎮・石井聰亙監督が、「コンセント」の市川美和子を主演に迎えて手がけたファンタジック・ドラマ。韓国のチョンジュ国際映画祭プロデュースによるオムニバス映画「三人三色」の中の1本として制作された短編作品をもとに、石井監督自らが追加撮影や再編集を施し、「完全版」として完成させた。バリ島の大自然を背景に、1人の女優がたどる心の旅路を独特のタッチで描く。", "2005-03-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A FUNNY THING HAPPENEDON THE WAY TO THE FORUM", "ローマで起こった奇妙な出来事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "古代ローマを舞台にしたブロードウェイのコメディ・ミュージカルの映画化。Z・モステルが舞台と同じく主演している。悪知恵にたけた奴隷プラウドラスは、自由にしてもらうのを条件に主人の息子と契約。息子と隣家の娘との仲を執り持つことになるが……。晩年のB・キートンが、チョイ役ながら体当たりのギャグを披露している。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RETURN OF THE ALIEN’S DEADLY SPAWN", "デッドリー・スポーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "炎と化した隕石が森に落下した。その時、解き放たれたバケモノが喰いものを探してさまよい現れた。それが死の悪食エイリアン、デッドリー・スポーン。バケモノは森の近くの一軒家の地下室に数千もの子供を生み、人間との死闘を始める……。典型的B級ホラー。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last Airbender", "エアベンダー", "", "", "http://image.eiga.k-img.com/images/movie/54104/315x210.jpg?2265", 103, "「シックス・センス」のM・ナイト・シャマランが、米TVアニメ「アバター　伝説の少年アン」を実写映画化。出演に「スラムドッグ＄ミリオネア」のデブ・パテル、「トワイライト」シリーズのジャクソン・ラスボーンら。かつて世界は、気、水、土、火の4つの王国によって均衡を保っていたが、「火の国」が反乱を起こして以来、100年にわたる戦乱が続いていた。伝説によると、全てのエレメントを操る者だけが世界に秩序を取り戻すとされ、「気の国」最後の生き残りである12歳のアンに希望が託される。", "2010-07-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("苔", "黒く濁る村", "", "", "http://image.eiga.k-img.com/images/movie/55474/315x210.jpg?3058", 161, "韓国で人気を博したWEBコミックを、「シルミド　SILMIDO」のカン・ウソク監督が映画化したミステリー。ヘグクは20年間にわたり音信不通だった父の訃報を受け、父が暮らしていた山奥の村を訪れる。村人たちはよそ者のヘグクに対し警戒心をあらわにするが、村長のひと言で態度を一変する。そんな村人に不信感を抱きはじめたヘグクは、父の死因を探るべく行動を開始する。主演は「殺人の追憶」のパク・ヘイル。", "2010-11-20", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルフガイ 燃えろ狼男", "ウルフガイ 燃えろ狼男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "平井和正の人気小説の映画化。原作のイメージを生かしながらも、千葉真一主演ということもあって活劇に徹した作り方をしている。満月の夜、超能力を発揮する狼族の末裔・犬神明は、“幻の虎”に青年が殺されるという怪事件に巻き込まれる。“幻の虎”とは歌手のミキが作り出した怨念の化身であった。明は“幻の虎”と死闘を繰り広げる。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNA DONNA ALLO SPECCHIO", "背徳の謝肉祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ある祭りの最中に出会った男女の、短くも情熱的な恋を描いた官能のラブ・ストーリー。やがて別れの時が来ることを知りつつ、恋の炎を体ごと体現するヒロインを、S・サンドレッリが熱演。イブレア地方の祭り（カルネバーレ）にカメラを持ち込み、名物のオレンジ合戦なども映し出されていく。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TELL THEM WILLIE BOY IS HERE", "夕陽に向って走れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1900年に起きた実話。インディアンの青年が、恋人の父を誤って殺し、二人で逃亡するが、保安官に追い詰められ……。“赤狩り”の犠牲となったポロンスキー監督が20年ぶりにカムバックした第２作。公開当時ニューウエスタンとして話題を呼んだ。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　アンパンマンとおかしな仲間", "それいけ！アンパンマン　アンパンマンとおかしな仲間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "やなせたかし原作の人気アニメーション劇場版第10作「それいけ！アンパンマン　てのひらを太陽に」と同時上映の短編。「おむすびまんとおばけやしき」「クリームパンダとおかしの国」の2話構成。", "1998-07-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("網走番外地・望郷編", "網走番外地・望郷編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "網走ならぬ長崎に舞台を移した一編。長崎に帰郷した橘真一は、恩義ある旭組の縄張りを奪い取り、悪業の限りをつくす安井組に単身殴り込みをかける。前２作のヒットにより本格的にシリーズ化が決定したのがこの作品で、北の果てからいきなり南へ飛ぶという石井輝男ならではの奇抜な発想が見事に成功したシリーズ屈指の傑作。主人公と対決する肺病やみの殺し屋・人斬りジョーを演じる杉浦直樹が素晴らしく、彼と主人公が１対１で対決するクライマックス・シーンは、ロバート・アルドリッチ監督の「ヴェラクルス」の決闘シーンのいただきながら、石井輝男十八番のメリハリのきいた名シーンであった。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wrecked", "エイドリアン・ブロディ　エスケイプ", "", "", "http://ecx.images-amazon.com/images/I/51XgqI1lomL._SL210_.jpg", 90, "「戦場のピアニスト」のオスカー俳優エイドリアン・ブロディが製作総指揮＆主演を務めたソリッド・シチュエーション・スリラー。男が目覚めると、そこは大破した車の中だった。助手席には拳銃、後部座席には見知らぬ男の死体。そしてラジオからは強盗事件のニュースが流れてくる。脚を挟まれて身動きが取れない上に記憶も失ってしまった男は、わずかな手がかりを頼りに自分の正体と事件の謎を解きあかしていくが……。", "2010-01-01", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鎮花祭", "鎮花祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "長い捕虜生活から帰ったばかりの兄・正方と、ディレクターの策略によってタレントに仕立て上げられた妹・公仁子がいる朝比奈家。やがて正方は結婚するが、捕虜生活で凶暴な性格になった彼は妻を虐待、夫婦生活の破綻を迎える。一方で妹がディレクターにだまされているのを知り、彼の妻を犯す。戦争で狂った男の凶暴ぶりを表現する悲劇ドラマ。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("His Girl Friday", "ヒズ・ガール・フライデー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "敏腕女性記者ヒルディは上司であり元夫でもあるウォルターに堅実な保険業者ブルースと結婚すると報告する。ヒルディに未練を残すウォルターは、最後の仕事にと死刑が確定している警官殺しの冤罪事件の取材を無理矢理引き受けさせ、あの手この手で結婚を妨害しようとするが…。史上おそらく最も速いペースの喜劇映画と称されるスクリューボールコメディ。", "1986-09-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("りんどう鴉", "りんどう鴉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "悪事を嫌うりんどうの政は、いかさま師を斬ったためにワラジをはく。道中、信州の伊那富で、土地の親分がんりきの為五郎により、名物の馬市の売上金をそっくり盗んだ犯人とされる政だが……。高田浩吉が見事な剣さばきで悪を斬り倒す痛快娯楽股旅もの。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君が若者なら", "君が若者なら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「若者たち」を製作した独立プロ・新星映画社の下で、深作欣二が才腕を振るった辛口の青春映画。集団就職で上京し、同じ工場で働く５人の若者たち。工場が潰れて失業した彼らは、独立して運送会社を設立しようとする。だが、投獄や予期せぬ事故死などのトラブルに襲われ、彼らの計画は難航。リーダー格の喜久男は、それでも必死に頑張り抜き、念願のトラックを手に入れるが……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Johnny Dangerously", "暗黒街の人気モノ／マシンガン・ジョニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "　1930年代、心優しい青年ジョニー・ケリーは母親の高額な医療費を工面するために、犯罪に手を染める。やがて彼は暗黒街でのし上がっていくが……。ギャングの世界をおちょくったスラップスティック・コメディ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パコダテ人", "パコダテ人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "函館を舞台に、シッポが生えてきた少女をめぐる珍騒動を描いたファンタスティックなストーリー。「害虫」でナント三大陸映画祭主演女優賞を受賞した宮崎あおいが、明るくさわやかなヒロインを好演している。ある朝、目覚めるとなぜかシッポが生えていた女子高生ひかる。すっかり困り果てる彼女をよそに、姉のみちるは自分も作り物のシッポをつけて、ひかるを街へ連れ出すが、その姿をスクープ記者に撮影されてしまって大慌て。けれどシッポ人間はひかるだけではなく、市役所職員の古田にも生えてきていたのだ。やがて二人のシッポは函館市内を巻き込む大騒ぎに発展する。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悶え", "悶え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "結婚初夜に、夫から不能であることを告白された新妻の苦悩を描く異色メロドラマ。満たされぬ日々を送る処女妻は、いつしか若いプレイボーイに心惹かれていくが、どうしても一線を越えることができない。そんなある日、彼女は夫から人工受精で子供をつくろうと提案される……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAMPO 黛バージョン", "RAMPO 黛バージョン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1992年末に完成しながら改訂版（奥山版）が作られ、1994年６月に別々の劇場で同時公開されたミステリードラマのオリジナル版。物語の舞台は昭和初期の東京。猟奇的な新作「お勢登場」がまたもや発禁となった江戸川乱歩は、若い編集者の横溝正史から奇妙な新聞記事を見せられる。そこにはくだんの新作に似た、未亡人・静子の夫殺害の疑惑が。いつしか自らの創作した名探偵・明智小五郎と同化した乱歩は、伯爵家で“毒婦”となった静子に妖しい世界へと誘われていく……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("りぼん RE-BORN", "りぼん RE-BORN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "１年前、担任教師と付き合っていたことを同級生の梅代に知られてしまった中学生の木下さやか。彼女は河童に興味を持つ梅代を誘い出し、“河童探し”に同行させるが……。今関あきよしが、少女の繊細な心情をみずみずしいタッチで捉えたファンタスティック・メルヘン。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水の中の八月", "水の中の八月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "故郷の福岡を舞台に、石井聰亙が初めて少女を主人公にした青春ファンタジー。中州高校３年の桑島真魚は、イルカのプールで初めて葉月泉と出会う。彼女は同じ高校に転校してきたばかりの１年生で、高飛び込みの選手だ。泉は転校早々の試合から好成績をあげるが、大会で大きな失敗をし、瀕死のところを真魚に救われる。事故以来、奇妙な行動に出る泉。そしてある日、彼女は何かに憑かれたように地元の水源地に向かう。心配のあまり同行した真魚だったが……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MORTELLE RANDONNE´E", "死への逃避行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "不幸な少女時代の記憶から逃れるため殺人を繰り返す美貌の女。彼女に死んだ自分の娘の姿をダブらせながら尾行を続ける中年の私立探偵“タカの目”。彼の父性愛は時として彼女の犯行を助けつつも、やがて運命の糸は二人の関係を悲劇と絶望のラストへと導く……。フランスを代表する女優、アジャーニが、殺人を繰り返し、名前を変えるごとに髪や瞳の色、衣装も変え、神秘的な殺人鬼を熱演する。衣装からタバコなどの小道具に至るまで、綿密で豊かなディテールが観る者を魅了する。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歪んだ関係", "歪んだ関係", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "ラテン系のポピュラー歌手から一転して成人映画界にデビューした新高恵子が、城山路子と組んで熟した女のエロスを発散するピンク映画。産婦人科医院の夫人は学生と不倫の仲。それを知った看護婦は院長夫人に収まろうと、彼女を殺害する。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北のカナリアたち", "北のカナリアたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「告白」の湊かなえの著書「往復書簡」を原案に、吉永小百合主演、阪本順治監督で描くヒューマンサスペンス。日本最北の島・礼文島と利尻島で小学校教師をしていた川島はるは、ある事件で夫を失う。それをきっかけに島を出てから20年後、教え子のひとりを事件の重要参考人として追う刑事の訪問がきっかけとなり、はるはかつての生徒たちに会う旅へ出る。再会を果たした恩師を前に生徒たちはそれぞれの思いを口にし、現在と過去が交錯しながら事件の謎が明らかになっていく。脚本を「北の零年」の那須真知子が担当。撮影は「劔岳　点の記」の木村大作。", "2012-11-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あいつとララバイ", "あいつとララバイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "当時の人気アイドル、少年隊の第１回映画主演作品。橘みちはるの同名コミックを原作に、横浜の高校生たちの青春とツッパリが、照れくさいほどストレートに描かれていく。ベテランの井上梅次監督によるモダン演出ぶりは若々しくて頼もしい。秋吉久美子の女教師も、若者たち以上にはじけていて楽しい。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Sting", "スティング", "", "", "http://ecx.images-amazon.com/images/I/51f4mXRH5jL._SL210_.jpg", 129, "「明日に向かって撃て！」のジョージ・ロイ・ヒル監督が、主演のポール・ニューマン、ロバート・レッドフォードと再び組んだ大ヒット作。1930年代のシカゴ。大物ギャングに仲間を殺された若き詐欺師フッカーは、その恨みを晴らすため、賭博師ゴンドーフの助けを借りて一世一代の大バクチを打つ。二転三転するストーリー展開と小気味よいテンポが魅力の傑作犯罪ドラマ。アカデミー賞作品賞など、1973年度最多7部門を受賞。", "1974-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばり・チエミ・いづみの三人よれば", "ひばり・チエミ・いづみの三人よれば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "桜花学園の名物トリオとしてならした３人の乙女たちも、TV局ディレクター、新進ヘア・デザイナー、料亭の女将と、今では立派なキャリアウーマン!?　そんな３人が、ある日恩師から結婚相手を世話すると言われて大騒動を巻き起こす……。美空ひばり、江利チエミ、雪村いずみの黄金トリオが繰り広げる青春コメディ。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPIRIT OF 1976", "サイケデリック・タイム・トリップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ベイシティ・ローラーズの『サタデーナイト』やカーペンターズの『トップ・オブ・ザ・ワールド』などの70'Sヒット・ナンバーをバックに、D・キャシディら、往年のアイドルが出演するSFコメディ。コスチューム・デザインは、「ヴァージン・スーサイズ」を監督したソフィア・コッポラが担当している。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遭難フリーター", "遭難フリーター", "", "", "http://image.eiga.k-img.com/images/movie/54274/315x210.jpg?3583", 67, "派遣労働者・岩淵弘樹が自身の生活を赤裸々にさらけ出し、監督・主演を務めるドキュメンタリータッチの青春映画。時給1250円で工場内での単純作業を毎日繰り返す岩淵は、着実にキャリアを積む友人や居酒屋で会った中年男性らに「お前は奴隷だ」と罵られながらもフリーターの権利を求め、自身の人生を模索していく。", "2009-03-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恐怖の時間", "恐怖の時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「天国と地獄」（1963）で注目された山崎努が、復讐鬼に扮して主演を張ったサスペンス。原作は「天国と地獄」同様E・マクベインで、小説のタイトルは『殺意の楔』。山崎扮する工員次郎は、山本刑事の誤射によって恋人を殺される。翌日、次郎は山本刑事に拳銃をつきつけ、ニトログリセリンを忍ばせて刑事部屋に乱入。だが、彼の仇は別の山本刑事だった……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Full Metal Jacket", "フルメタル・ジャケット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "　原作はグスタフ・ハスフォードの処女小説「短期応召兵（ザ・ショート・タイマーズ）」。米海兵隊3092小隊の新兵たちはいじめ、侮辱が横行する環境下、過酷な訓練を強いられる。人を殺すことを教え込まれ、人間性が奪われていった新兵のひとり、パイルはついに精神を病み自殺してしまう。その後、それぞれベトナムへ送り込まれた新兵たちだったが……。戦火のシーンではスローモーション技術を駆使し、兵士の死をリアルに描いている。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPECIESII", "スピーシーズ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "３年を経て作られたSF大作の続編。前作から引き継がれたH・R・ギーガーのデザインによるスピーシーズの造形が不気味さを醸し出している。政府の秘密研究所では、３年前の事件の張本人であるスピーシーズ、シルのクローンであるイヴが外界から遮断され育てられている。異性を近づけないことで増殖本能を抑えられた彼女は、人間の心を植え付けられた安全な新種だった。そのころ、火星探索宇宙船が地球に帰還するが、その中の一人パトリックはスピーシーズに体を乗っ取られ、種を増やすために女たちを襲い始める。これを知った国防省は３年前にもスピーシーズ退治で活躍したプレスを呼び出し、イヴのテレパシー能力を使ってパトリックの追跡を開始する。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銭形平次捕物控・美人鮫", "銭形平次捕物控・美人鮫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "武蔵屋の娘が殺された事件を追っていた平次は、妙照寺の尼僧が怪しいとにらみ調べるが、彼女も殺され、平次たちは寺社奉行に捕まってしまう。しかし麻薬撲滅に動いていた南町奉行のお陰で無事脱出し、長崎へ。そこで平次は、殺された娘が麻薬と交換に売られた娘だと知る……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("３ HOMMES ET UN COUFFIN", "赤ちゃんに乾杯！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "当時でフランス映画史上最高の興行成績を記録し、フランスのアカデミー賞に当たるセザール賞の最優秀作品、脚本、助演男優賞を受賞したコメディ。３人の独身男の前に置き去りにされた赤ちゃんを通して、“男の中の母性愛”を涙と笑いで問いかける好編。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("10", "テン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "作曲家のジョージは、アカデミー賞にも輝いた売れっ子。仕事は順調だし、恋人の歌手サムもいて、中年男の身の上からすれば不満はないはずだった。それなのに今一つ、満たされない思いで過ごす毎日。だが、ある日、交差点で彼の理想の女性＝10点満点の美女と出会い、事態は一変する。その夜サムと喧嘩した彼は、例の美女を追い、彼女の新婚旅行先、メキシコへと向かう。そこで偶然新郎の命を救ったジョージは、憧れの彼女とついにベッドイン。ところが……。デレクの妖艶さに攻めさいなまれる、なさけない中年男をムーアが好演。理想と現実のギャップをコミカルに描いた、大人向けラブ・メルヘン。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kurt Cobain About a Son", "カート・コバーン　アバウト・ア・サン", "", "", "http://image.eiga.k-img.com/images/movie/34104/315x210.jpg?1440", 97, "ニルバーナのフロントマンとして90年代のグランジ・ムーブメントを巻き起こし、94年に27歳で自殺したカート・コバーン。生前、シアトルの自宅で行われた25時間に及ぶインタビュー・テープを元に、カート本人の肉声で語られるドキュメンタリー。孤独だった少年時代、ロック・スターを夢見ていた駆け出しの頃、シアトルでの大ブレイク、そして妻コートニー・ラブと築いた家族の話など、ひとりの人間としての素顔を映し出す。", "2007-08-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Edouard et Caroline", "エドワールとキャロリーヌ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "パリのアパルトマンで暮らすエドワールとその妻キャロリーヌ。駆け出しのピアニストであるエドワールのために、キャロリーヌの叔父がパーティを計画する。エドワールがそこで腕前を披露し、社交界の人々に売り込もうというのだ。パーティ当日、エドワールはタキシードのチョッキが見当たらず怒り始める。一方キャロリーヌは自分のドレスのことで頭がいっぱい。2人は大喧嘩となり……。ジャック・ベッケル監督によるラブコメディ。", "1951-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GAMER", "フューチャー・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "コンピューター・ゲーム業界を背景にした青春アクション。ゲーム好きのチンピラ、トニーは一攫千金を狙って新種のゲームを開発。ところが、そのデモソフトがゲーム会社の女重役にだまし取られてしまった。怒ったトニーは大逆転を狙い、８ヵ月後に控えるゲーム・ショーに向けて、さらに革新的なゲームを作り出そうと、仲間とともに奔走する。CG映像の効果的なフィーチャーが、物語そのもののゲーム的な感覚を強調している。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い鷹", "赤い鷹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "人気絶頂期の橋幸夫主演の正月映画。東京新報社会部の記者・旗雄次は、ヤクザ組織巽組が芸能界を食いものにしていると知り、その記事を週刊誌に持ち込んだ。実は巽組組長は雄次の兄で、巽組の対抗勢力が雄次を利用していたと分かり……。恋人役は、当時橋幸夫との共演作が多かった倍賞千恵子。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Dark Knight", "ダークナイト", "", "", "http://image.eiga.k-img.com/images/movie/53214/315x210.jpg?4493", 152, "クリストファー・ノーラン監督＆クリスチャン・ベール主演による「バットマン・ビギンズ」の続編。ゴッサム・シティに現れた史上最悪の犯罪者ジョーカー。バットマン＝ブルース・ウェインは、協力するゴードン警部補や新任地方検事ハービー・デントらと共にジョーカーに立ち向かうが……。ジョーカー役のヒース・レジャーは撮影直後に急逝するも、その演技が絶賛され、第81回アカデミー賞で助演男優賞を受賞した。", "2008-08-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霊縛　REIBAKU／少女の縛り", "霊縛　REIBAKU／少女の縛り", "", "", "http://image.eiga.k-img.com/images/movie/33534/315x210.jpg?4549", 75, "キャスト全員が女性のホラームービー。子供の頃から霊感が強い瞳は、暇なOLで作ったサークル「心霊研究会」の面々と一緒に心霊スポットに出かけて、スリルを味わっていた。だが、ある日いつものように出かけた先で全裸の女霊を見てから、何かが狂い始める……。監督はAVで活躍する海山公秀。", "2006-07-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋する幼虫", "恋する幼虫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「クルシメさん」の井口昇監督の６年ぶりの作品。異性恐怖症の男と女が出会い、女がバケモノ化しつつも愛をはぐくむというぶっ飛んだ怪作で、グロなホラーとシュールなコメディ、さらに切ない恋物語をゴッタ煮にした世界観が強烈。「ピンポン」の部長役などで、のほほんとした個性を発揮する荒川良々が、堂々の映画初主演を務めている。マンガ家のフミオと、彼を担当する新人編集者ユキはどちらも異性恐怖症。ある日、ユキのおどおどした態度にキレたフミオは、ついユキの顔に傷を負わせてしまう。悪化した傷は謎の生命体に成長し、人の生き血を吸い始める。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若い突風", "若い突風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "父のいる税関で、押収された麻薬が持ち逃げされるという事件が発生。同じころその息子・健次はタクシー会社に就職、不審な行動を取るチンピラ・ロクの存在を知る。ロクのお抱え的運転手・安さんも怪しい。安さんを気絶させ、その車に乗り込んだ健次だが……。和田浩治主演のアクションとサスペンスのドラマ。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナイスの森 The First Contact", "ナイスの森 The First Contact", "", "", "http://image.eiga.k-img.com/images/movie/1564/315x210.jpg?4026", 150, "“ナイスの森”とは、「茶の味」でもコラボレーションしている3人が結集したユニット名。「鮫肌男と桃尻女」などの監督で「キル・ビル」のアニメーション・キャラクター・デザインでも知られる石井克人、DVDマガジン「Grasshoppa!」の「山岡先生／言わなきゃよかった」などを監督した三木俊一郎、同マガジンの「TRAVA-FIST PLANET episode 1」などに声の出演をしているANIKIの3人が、オチのないエピソード21話を描く。", "2006-03-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長三国志・次郎長と石松", "次郎長三国志・次郎長と石松", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "久慈あさみの投げ節お仲が初登場する。追分三五郎と道連れになった森の石松は、投げ節お仲の妖艶な壷ふりにスッテンテンになる。一方、開帳中を役人に捕らえられた次郎長一家は、その牢内の横暴な牢名主をとっちめる。そんな折、張子の虎三が義兄弟・大熊の危機を知らせる……。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PINK PANTHER STRIKES AGAIN", "ピンク・パンサー3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "おなじみ“ピンク・パンサー”シリーズの第４作。今回のクルーゾーの敵は元署長のドレフュス。彼はクルーゾーを亡き者にしようとあらゆる手を使うが……。ギャグの切れ味も相変わらず好調、エドワーズ、セラーズのコンビネーションが冴えている。DVDは「ピンク・パンサー フィルム・コレクション」に収録。", "1976-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アリエッタ", "アリエッタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "堕ちていく女を描いた、実相寺昭雄監督によるアダルト・エロス作品。ラブホテルで女の死体が発見される冒頭から、彼女の転落人生が語られていく。家出した夫の借金を返済するために働くが、高給でないと返せない。掃除婦、ホステスに続いてホテトル嬢に。そこでSMの世界にはまり込み……。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピカ☆☆ンチ／LIFE IS HARD だから HAPPY", "ピカ☆☆ンチ／LIFE IS HARD だから HAPPY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ただのアイドル映画に収まらない巧みなストーリー展開と映像センスで話題を呼んだ「ピカ☆ンチ」の続編。前作同様、原作はV6の井ノ原快彦、脚本は演出家兼俳優の河原雅彦、そして「恋愛寫眞」の堤幸彦が監督を務め、高校を卒業しそれぞれの道に進んだ主人公たちの３年後を描く。様変わりした５人が再び顔を合わせ、あの時の団結力は蘇るのか!? チュウ、シュン、タクマ、ハル、ボンの５人が青春をともにした八塩団地の貧乏棟は、自治体の“八塩ヒルズ”建設計画により、取り壊されようとしている。そんな中、再会を果たした彼らは、住民の反対運動に巻き込まれる。", "2004-03-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES BELLES DE NUIT", "夜ごとの美女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "いつか自分のオペラを、と希望を抱く音楽教師クロードは、夢を見る名人だった。夜ごとにみる彼の夢には数々の美女が登場しては消えていく。ある晩、夢から目覚めると……。シネオペレッタ風のボードビル・ファンタジー。音楽は「フレンチ・カンカン」などのG・ヴァン・パリ。", "1952-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Life 天国で君に逢えたら", "Life 天国で君に逢えたら", "", "", "http://image.eiga.k-img.com/images/movie/34144/315x210.jpg?2249", 118, "05年に38歳という若さで他界した世界的プロウインドサーファー、飯島夏樹の半生を映画化した人間ドラマ。本人による著作「天国で君に逢えたら」「ガンに生かされて」を基に、肝細胞ガンに冒されながらもひたすら前向きに生きた彼と、そんな彼を支え続けた家族の姿を綴っていく。主人公・飯島夏樹に大沢たかお、献身的な妻・寛子に伊東美咲が扮する。監督は「ただ、君を愛してる」の新城毅彦。", "2007-08-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FORT WORTH", "無法街の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "かつての拳銃の名手ブリッドが、14年ぶりに故郷に帰って来る。だが幼なじみのブレアは、ブリッド暗殺を企てるゲイブの一味となっていた。幼なじみの二人はやがて対決せざるを得ない立場になるが……。ゴールドラッシュ時代のテキサス・カンザス平原を舞台にしたアクション西部劇。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天堂口", "ブラッド・ブラザーズ　天堂口", "", "", "http://image.eiga.k-img.com/images/movie/54304/315x210.jpg?4452", 95, "「レッドクリフ」のジョン・ウーが製作総指揮を務める、男たちの絆を描いたハードボイルド・アクション。1930年代、富が渦巻く大都会・上海に3人の若者、フォン、カン、フーが成功を夢見てやってくる。貧しい3人は義兄弟の契りを交わし、マフィアの手下として大都会で生き抜いていくことを決意するが……。「香港国際警察／NEW POLICE STORY」「女帝／エンペラー」のダニエル・ウー、「ミレニアム・マンボ」「トランスポーター」のスー・チーらが出演。", "2009-02-07", "53,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Guantanamero", "悪魔のリズム", "", "", "http://image.eiga.k-img.com/images/movie/53624/315x210.jpg?2660", 88, "グアンタナモ収容所で想像を絶する恐怖を経験した青年の運命を、エネルギッシュなキューバの街を舞台に描き出したイギリス・スペイン合作の幻想スリラー。グアンタナモから逃亡し、キューバの浜辺に漂着した1人の青年。心身ともに衰弱し、自分の名前さえも思い出せない彼は、女性ダンサーのマヌエラに助けられ一命を取り留める。やがて青年はマヌエラと恋に落ちるが、グアンタナモでのおぞましい記憶が青年を悩ませ続け……。", "2008-10-11", "3,76,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NEVERENDING STORYII THE NEXT CHAPTER", "ネバーエンディング・ストーリー　第２章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "前作で“無”に襲われたファンタジアが、今回は“虚しさ”の脅威にさらされる、M・エンデの原作を映画化したファンタジー作品第２作。バスチアン少年はいまだに母を失った悲しみに心を閉ざしていた。彼は再び『ネバーエンディング・ストーリー』のページを開ける。その時、ファンタジアの姫の叫び声が……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("らせんの素描", "らせんの素描", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "大阪のある同性愛者たちの姿を丹念に追ったドキュメンタリー。ゲイ雑誌で知り合った二人の男を中心に、彼らがゲイの視点から見た世界を芝居にしようと素人劇団を発足させる模様を描いていく。そしてそこに集まる高校教師、詩人、ダンサーなどの姿を通して少数派“ゲイ”の心情を捉えていく。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猿ロック THE MOVIE", "猿ロック THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/55054/315x210.jpg?4212", 112, "天才鍵師を描いた芹沢直樹の人気コミックをTVドラマ化した「猿ロック」の劇場版。主演は市原隼人、ヒロインに劇場版から参加の比嘉愛未。高岡蒼甫、芦名星、光石研、國村隼、小西真奈美ら豪華キャストが脇を固める。どんな鍵でも神業で開けてしまう鍵師の青年・サルこと猿丸耶太郎は、謎の美少女マユミの依頼を受け、とあるスポーツクラブの金庫を開ける。警察にばれ追われる身となったサルは、マユミと共に逃走を続けるが……。", "2010-02-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("彼女と彼", "彼女と彼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "当時、気鋭の劇作家と組み、実験的な映画作りを続けていた羽仁進が「充たされた生活」に続き清水邦夫と組んだ社会派ドラマ。団地に住む一人の平凡な主婦が、隣に見下ろすバタヤ部落の生活に惹かれていく姿を通して、人間にとって自由とは何かを問いかける。日常生活の中の心理的小波乱を、純度の高い映像に結実させた野心作。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COLD STEEL", "ロス市警特捜刑事 惨劇のX'masイブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "かつて警察学校の仲間だったジョニーとアイザックが、刑事と殺人鬼という対極的な関係になって再会する、ユニークなストーリーのポリス・アクション。“アイスマン”の異名を持つ残忍な殺人鬼とロス市警の敏腕刑事の対決の結末はいかに……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO THE SHORES OF TRIPOLI", "トリポリ魂～海兵隊よ永遠なれ～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "軍隊内部の上下関係をはじめとした人間関係を描く戦争ドラマ。士官学校を舞台にした「愛と青春の旅だち」のいわば戦前版の趣き。太平洋戦争直前のカリフォルニアの海兵隊基地に、軍曹の古い友人の息子クリスが入隊してきた。彼は厳しい軍曹に反発したり、フィアンセがいるのに美人の看護師にアタックしたりする。その報いからか、彼は射撃標的の場に一人取り残される。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I'LL NEVER FORGET WHAT'S NAME", "明日に賭ける", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "表面は華やかだが虚飾に満ちた広告業界に嫌気がさし退社したクイント。売れない文芸雑誌の編集者として再出発を切るが、かつてのボスが有能な彼を放っておくはずがなかった……。O・リード、O・ウェルズなどオールスターキャストで描いた苦い青春像。M・ウィナーの演出も野心的だ。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLLOCK", "ポロック／２人だけのアトリエ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "米国モダン・アートの先駆者ジャクソン・ポロックの生涯を、演技派俳優、エド・ハリスが、構想に10年、自ら監督・主演を務めて映画化した意欲作だ。ジェームズ・ディーンにもなぞらえられるポロックの破滅的な人生を、初監督とは思えぬリアリティーあふれる演出で見事に映像化している。1941年。芽が出ずアルコールに溺れる画家のポロックの前に女流画家のリーが現れる。即座にポロックの才能を見抜いたリーは彼を有名画廊に紹介。そのおかげでポロックは画壇の風雲児に。やがて二人は結婚するが幸せは長く続かなかった。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボールZ　超サイヤ人だ孫悟空", "ドラゴンボールZ　超サイヤ人だ孫悟空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "地球を侵略しようとする宇宙人との戦いへと、シリーズの舞台を移行しつつある時期の一作。初期のパワフルさに磨きがかかったシリーズの第7弾。全宇宙征服を狙うナメック星人たちが、地球を攻めてきた。行方不明中の悟空に代わり悟飯が立ち向かうが、敵に7つのボールを奪われてしまう。DVDは『DRAGON BOX THE MOVIES』に収録。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Splice", "スプライス", "", "", "http://image.eiga.k-img.com/images/movie/55554/315x210.jpg?3329", 104, "種類の異なる動物の遺伝子を接合させて、いくつものハイブリッド生命体を作ってきた遺伝子工学の研究者クライブとエルザは、製薬会社が禁止しているにもかかわらず、人間のDNAを使ってハイブリッドを作る実験を秘密裏に行い、“ダレン”と呼ばれる生命体を作り出す。すさまじい早さで成長する“ダレン”にエルザは喜んでいたが、やがて“ダレン”は人間を襲い始め……。出演はクライブにエイドリアン・ブロディ、エルザにサラ・ポーリー。監督は「CUBE」のビンチェンゾ・ナタリ。", "2011-01-08", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lost Symbol", "ロスト・シンボル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若親分を消せ", "若親分を消せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "中西忠三監督のデビュー作となった“若親分”シリーズ第６作。南条武の出獄を迎えにきた三野組の弥五郎が何者かに殺された。犯人を追う武は、その途中で竹村中佐に会い、町の芸者・千代竜の身請金の金策を頼む。彼女は武の海軍時代、海軍粛正運動の責をとって自刃した秋月中佐の娘だったのだが……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("じゃりン子チエ 劇場版", "じゃりン子チエ 劇場版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "高畑勲監督がはるき悦巳の同名人気漫画を映画化。通天閣が見える大阪の下町に住む小学５年生のチエは“ウチは日本一不幸な少女や！”が口癖の女の子。そんな彼女がホルモン焼き屋を切り盛りしながら、バクチ好きの父親テツと家を出た母親をくっつけようと奮闘する姿を綴る。当時参議院議員だった中山千夏がチエの声を担当したほか、西川のりおなど関西のお笑いタレントが多数参加している。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PHANTOM OF THE RUE MORGUE", "謎のモルグ街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "E・A・ポーの推理小説の傑作を、R・デル・ルースが映画化した怪奇サスペンス映画。19世紀末のパリ。何者かの手によって若い女性が次々に惨殺されるという事件がこの街を震え上がらせていた。パリ警視庁のボナール警視は、ソルボンヌ大学の生物学教授デュパンが怪しいとにらむ。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TROUBLE BOUND", "トラブル・バウンド 復讐の銃弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "刑務所帰りのハリーは、ポーカーでツキまくり意気揚々と日々を送っていたが、セクシーな女の子キットと知り会ってからは、ツキが落ちてしまう。手に入れた車の中に男の死体があり、そこには大量の麻薬が隠されていた。キットは父の仇のマフィアに復讐をするが失敗。二人は麻薬組織とマフィアの両方から追われることになってしまうのだった……。「ヒューマンネイチュア」のP・アークエットと「キル・ビル」のM・マドセンの顔合わせで描く、犯罪サスペンス。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOM！", "BOM！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "女子高校生の京子と沙耶は、ある日弾の込められているピストルを拾ってしまった。それはヤクザ組長を襲撃したときに使用されたものと判明するのだが、彼女たち仲良し七人組はそのピストルを用いて、それぞれの悩みを解消しようとしていく。美少女アイドル総出演の青春エンタテインメント。ちょっとワイルドで可愛らしい彼女らの初々しい姿を、鹿島勤監督が軽やかに演出している。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野獣死すべし・復讐のメカニック", "野獣死すべし・復讐のメカニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "大藪春彦原作、須川栄三監督による「野獣死すべし」から15年を経た続編。天才的な頭脳と精悍な肉体を持つ伊達邦彦。12年前に会社を倒産させ、父を死に追いやった者への伊達の復讐が始まる……。ハード・バイオレンス・アクション作。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sleeping Beauty", "眠れる森の美女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ディズニー・アニメを代表する１本で、いまだに世界各国で愛されている名作である。魔女マレフィセントの呪いによって長い眠りの中に閉じ込められたオーロラ姫。彼女の呪いを解くため、婚約者のフィリップ王子は３人の妖精とともに、マレフィセント立ち向かう……。音楽には、チャイコフスキーの同名のバレエ組曲が使用されている。その音楽にのせて、森の中で歌いながらダンスを踊っている姫が、初めて王子と出会うシーンはロマンティックで印象的。その美しい色彩と、滑らかな画面の動きは今見ても息をのむばかりの素晴らしさ。まさに子供だけではなく大人の観賞にも耐える傑作だ。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALMOST BLUE", "幻のマンハッタン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「レザボア・ドッグス」などで人気となったM・マドセンの未公開主演作。モリスは、５年前に最愛の妻を事故で亡くしたサックス・プレーヤー。妻の死の責任を感じ、自分の殻に閉じこもりがちな彼を、親友のチャールズは再びステージに立たせようとする。そんなとき、モリスは、神秘的な雰囲気を持つ女ジャスミンに出会い、ようやく心を開き始める。しかし、モリスは、過去を完全に清算することができずに、やがて、ジャスミンとぶつかり始める。モリスは徐々に不安定な状態に追い込まれる。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WOLF MAN", "狼男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "“狼男”映画の元祖として語り継がれるユニバーサル・ホラー。日本では劇場未公開に終わり、「狼男の殺人」というタイトルでTV放映された。美女が狼人間に襲われているところを救い、自らの手を噛まれてしまったラリー。やがて満月の夜に狼に変身してしまう主人公を、怪奇俳優R・チャニーJr.が演じている。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("弥那邊機點", "ふたつの時、ふたりの時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "台北の路上で時計売りをするシャオカンは、ある日、もうすぐパリに旅立つという女性シアンチーと出会う。彼女を忘れられないシャオカンは、台北中の時計をパリ時間に合わせるために走り回る。父の死後、心を病んでしまった母との暮らしから逃げるように……。同じ頃、パリのシアンチーは不思議な出来事に遭遇する。現代台湾の異才、ツァイ・ミンリャン監督のテーマともいえる“都市の孤独と寂寥感”の物語ではあるが、異空間にいる人間の不思議な結びつきを描くことで、ファンタスティックで優しい味わいを滲ませている。J・P・レオーがキーとなる役で出演しているのも見逃せない。", "2001-01-01", "53,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワールドクラシック＠シネマ2011 オペラ「ドン・ジョヴァンニ」　グラインドボーン音楽祭", "ワールドクラシック＠シネマ2011 オペラ「ドン・ジョヴァンニ」　グラインドボーン音楽祭", "", "", "http://image.eiga.k-img.com/images/movie/56464/315x210.jpg?2043", 180, "世界各地の有名オペラハウス、バレエ団の上演を映画館のスクリーンで上映する「ワールドクラシック＠シネマ2011」（全8作品）の1作。イギリスの音楽祭の中でも最もステータスが高くチケットが取りにくいといわれる夏恒例の「グラインドボーン音楽祭」で、2010年7月に上演されたモーツァルト作曲のオペラを上映。", "2011-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘録・怪猫伝", "秘録・怪猫伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "老舗大映が久々に発表した“化け猫”もの。鍋島丹後守は又七郎の妹・小夜の美しさに心を奪われる。威信の失墜を恐れる家老・矢淵刑部の策に乗せられ丹後守は又七郎を斬殺する。小夜は復讐を愛猫に託し自害する。その夜から城内には怪異が……。大映も末期とあって怪異よりもエロが強調されている。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLIND HORIZON", "ブラインド・ホライズン", "", "", "http://ecx.images-amazon.com/images/I/51VE6K16BWL._SL210_.jpg", 99, "V・キルマーやS・シェパードなど、渋味あふれる名優が共演を果たした迷宮サスペンス。キルマーが演じるのは、ド田舎の町で発見された記憶喪失の男。思い出せるのは“この町で大統領が暗殺される！ ”という物騒な計画だけ。暗殺計画はただの妄想なのか？　次々と現れる謎の人物の正体とは？ 本作の特異さは、それぞれの思惑や陰謀が絡み合い、誰に対しても全貌を悟らせないこと。すべては散りばめられた謎の断片を、観客がどう繋ぎ合わせるかにかかっている。一見オーソドックスなようで、かなり挑発的な怪作。瀕死の状態で発見された男が目を覚ますと、記憶喪失に陥っていた。思い出せるのは、５日後に迫った大統領暗殺計画だけ。婚約者を名乗る美女に戸惑いながら、男は暗殺阻止に乗り出すが……。", "2004-10-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("星よ嘆くな 勝利の男", "星よ嘆くな 勝利の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "オリンピックで金メダルを取ったボクサー・速水は、一度は断ったプロ入りを師匠の死によって決断。彼に自分の夢を託した矢代とともにチャンピオンの道を目指した。やがて矢代の妹・葉子との恋も芽ばえるが、町のボス・加納の妨害が……。次々作の「紅の流れ星」で渡哲也をスターダムに押し上げた舛田監督・日活アクション末期の一編。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMPEROR　JONES", "巨人ジョーンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "戦前のアメリカでバリトン歌手として活躍したP・ロブスンの初主演映画。成功を夢見て故郷を離れたブルータス・ジョーンズは寝台車のボーイを経て出世していくが、賭博のいざこざで殺人を犯してしまい服役。脱獄した後に、とある島に流れついた彼は実力者に取り入って、王位に就くのだが……。成功と挫折を見つめた人間ドラマが展開。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARLOR，BEDROOM AND BATH", "キートンの恋愛指南番", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "サイレント時代から、観客を笑わせ続けた“笑わぬ喜劇王”ことB・キートンが、役者としての真骨頂をみせるトーキー第３作。長身の猛女優C・グリーンウッドに恋の手ほどきを受けるキートンが巻き起こす、珍騒動の数々が笑いを誘う。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HONKYTONK MAN", "センチメンタル・アドベンチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "アメリカ南西部の田舎町のバーを転々とめぐってはカントリー・ソングを歌って暮らしているレッドは、ある日、ナッシュビルのグランド・オール・オープリーのオーディションを受けてみないかと誘われる。レッドは、その道に生きる者なら誰でも憧れるナッシュビルへと夢をふくらませて旅を始める。レッドを尊敬する甥のホイットと、死ぬ前に故郷に帰りたいというホイットの祖父を加えた３人組の珍道中の行く手には様々な人や事件が待ち受けている……。C・イーストウッドがアメリカの大地と人々への愛を込めて描いた作品で、海外では不評だったが後年の「バード」を予告する佳作といえよう。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("442　Live with Honor, Die with Dignity", "442日系部隊　アメリカ史上最強の陸軍", "", "", "http://image.eiga.k-img.com/images/movie/55734/315x210.jpg?4573", 97, "第2次世界大戦時に日系人のみで構成されたアメリカ陸軍442連隊を題材にしたドキュメンタリー。父母の祖国である日本と戦うことに苦悩し、さらにアメリカ国内では人種差別と戦いながら、アメリカ軍史上最も多くの勲章を受けた部隊の真実を、80代から90代となった当事者たちの貴重な証言を交えながら描きだす。メガホンを取るのは「マリリンに逢いたい」などで知られるすずきじゅんいち監督。", "2010-11-13", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bringing Out the Dead", "救命士", "", "", "http://image.eiga.k-img.com/images/movie/1854/315x210.jpg?1599", 121, "麻薬と暴力に支配された90年代前半のニューヨーク。フランクは腕のいい救急救命士だが、あまりにも多くの悲劇を目撃してきた彼の精神は次第にすり減り、燃え尽きる寸前だった。人を救えば、自分も救われる。だがこの何か月もの間、彼は患者の命を救うことができずにいた。きっと助かる、次の仕事はうまく行くはず……そう自分に言い聞かせ眠りにつこうとしても、彼が救えなかった患者たちは亡霊となってフランクにとり憑き、彼を眠らせないのだった。", "2000-03-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小津の秋", "小津の秋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「二人日和」の野村惠一監督が、かつて小津安二郎監督が仕事場として使っていた蓼科の別荘“無藝荘”を舞台に、男女が織り成す人間模様を綴った感動作。新聞記者の佐々木明子は、取材のために蓼科を訪れる。父の遺品を持ってきた彼女には、もうひとつ別の目的があった。探している人物に会えるかもしれないという予感があったのだ。明子は偶然にも、小さな庵・無藝荘の守女である老人・園子に出会う。やがて園子は自らの過去を語り出し……。", "2007-09-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUBY　GENTRY", "ルビイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ノース・カロライナの寒村に住む、多感な女ルビイは、南部貴族の息子ボークと愛し合いながらも彼の裏切りにあい、初老の資産家・ジムと結婚してボークへの復讐を企てる。勝ち気で野性的なヒロインをJ・ジョーンズが好演している。主題曲もスタンダード・ナンバーとして有名だ。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オトコタチノ狂", "オトコタチノ狂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "幕末の武士たちと現代の若者の奇妙な交流を描いた、ユニークな人間ドラマ。監督ジョイ・イシイを中心とした自主映画制作集団\"石井組\"がインディーズ・スタイルにこだわり、4年の歳月を費やして制作した。生の実感を掴めない現代社会のなかで志と夢を問う、深みのあるドラマが繰り広げられる。ある雨の日、家に戻った青年・櫻井を待ち受けていた4人の武士たち。それぞれに立場の違う彼らは、争いを繰り広げて櫻井を翻弄し……。", "2005-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若者の旗", "若者の旗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "早くに両親を亡くした５人の兄弟姉妹が、人生の試行錯誤を繰り返しながらも、たくましく成長していく姿を捉えた青春ファミリー・ドラマ。そのシリーズの第３弾にして完結編が本作だ。前２作の好評を受けての映画化で、彼らはその後どんな運命をたどったのか興味津々の出来ばえ。三郎は教師を辞め出版社へ。オリエは恋人にフラれ、末吉は営業成績優秀で恋愛中……など。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大輪廻／THE　WHEEL　OF　LIFE", "大輪廻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "３人の男女の輪廻転生の物語を、３人の監督が描くオムニバス。第１話は、権力者の侍女になることを強いられた美しい娘とその婚約者、娘に思いを寄せている男の三角関係。第２話は、歌舞伎団の花形娘と彼女と恋に落ちた金持ちの跡取り息子、結婚で娘が一座を離れることを憂う団長である父がたどる悲劇。第３話は、モダンダンスの舞踏家と、彼女に恋こがれる漁師の兄弟の愛憎劇。明時代、民国初年、1980年代と、時を超えて出会いながらも悲劇的な運命をたどる男女の姿を描き出す。", "1983-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("17 Again", "セブンティーン・アゲイン", "", "", "http://image.eiga.k-img.com/images/movie/54454/315x210.jpg?3846", 102, "「ハイスクール・ミュージカル」で大ブレイクを果たしたザック・エフロン主演のコメディドラマ。バスケットボール部のスター選手として活躍する高校生マイクは、恋人スカーレットの妊娠をきっかけにバスケの道を断念する。それから20年後、冴えない中年男になってしまった彼は、ひょんなことから17歳の姿に変身。人生をやり直そうと2度目の高校生活を送りはじめるが……。監督は「17歳の処方箋」のバー・スティアーズ。", "2009-05-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("従奴隷到将軍／FROM SLAVE TO GENERAL", "将軍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 167, "雲南省イ族の奴隷として生まれ、その後将軍にまでなった実在の人物をモデルとした作品。羅筐（ざる）と呼ばれる彼は、結婚したその日、奴隷だったがゆえに妻を奪われてしまう。その晩彼は軍隊に入り、次第に手柄をたてて出世するのだが……。監督は1923年生まれの第三世代監督ワン・ヤン。", "1979-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女生きてます・盛り場渡り鳥", "女生きてます・盛り場渡り鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "前３作で藤原審爾の原作を使い果たしたため、森崎東監督と掛札昌裕によるオリジナル・ストーリーが練られた。男に触られるとジンマシンが出る女・初子と、吃音障害の労務者・善五が結ばれるまでを、おなじみ“新宿芸能社”を舞台に描く。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冷たい熱帯魚", "冷たい熱帯魚", "", "", "http://image.eiga.k-img.com/images/movie/55654/315x210.jpg?3863", 146, "「愛のむきだし」などで知られる鬼才・園子温監督が、実在するいくつかの猟奇殺人事件にヒントを得て人間の狂気と極限の愛を描くサスペンス。小さな熱帯魚店を営む社本の家庭では、年頃の娘が若い後妻に反発しており、そのため彼と妻との関係にも亀裂が生じていた。そんなある日、彼は娘が起こした万引き事件をきっかけに同業者の村田と知り合う。やがて村田の事業を手伝うことになった社本は、いつしか恐ろしい猟奇殺人事件に巻き込まれていく。", "2011-01-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("影の車", "影の車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "かつて母の愛人に殺意を抱いた男が、息子と二人暮らしの女を愛するようになった。しかし男は、“あの息子は俺を殺そうとしている”と思うようになり……。殺意と幻想の虜になった男の破滅を描くニューロティック・スリラー。加速度的に増していく恐怖演出と映像処理が特筆もの。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Trois couleurs: Blanc", "トリコロール／白の愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ポーランドの巨匠クシシュトフ・キエシロフスキー監督による、青、白、赤のフランス国旗をモチーフにした三部作「トリコロール」の第2作。舞台はパリ。性的不能が原因で、妻のドミニクに去られてしまったポーランド人のカロル。故郷へ戻って大金を手にした彼は、どうにかしてドミニカを取り戻そうと策を練る。主演は、ズビグニエフ・ザマホフスキー、ジュリー・デルピー。", "", "2,42,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魂の教育", "魂の教育", "", "", "http://image.eiga.k-img.com/images/movie/54034/315x210.jpg?3857", 100, "海外からも高い注目を集める右脳教育や児童教育の第1人者、七田眞を追ったドキュメンタリー。監督は監督第１作「ストーンエイジ」も評価されたの人気声優の白鳥哲。七田は学生時代から脳の持つ可能性に興味を持ち「七田式右脳教育」を確立、その教育法は海外でも多く実践されている。「イメージすることは実現する」をモットーにする七田の人間性や英知に迫る。", "2008-11-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONQUEST OF THE PLANET OF THE APES", "猿の惑星／征服", "", "", "http://ecx.images-amazon.com/images/I/4191BVP51JL._SL210_.jpg", 88, "ピエール・ブールの小説『猿の惑星』をもとにしたシリーズ第４作。1990年、アメリカのメガロポリス。ここでは猿を奴隷化して重労働を押しつけていた。しかし猿たちは反乱を起こし、“人間”対“猿”の地球の支配権を懸けた壮絶な闘いがはじまる。", "1972-07-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PEEP“TV”SHOW", "PEEP“TV”SHOW", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "国内外で高い評価を受けたドキュメンタリー作品「新しい神様」の土屋豊監督が、劇映画に挑戦し再び物議を醸した一作。9.11の同時多発テロの映像を“美しい”と感じた盗撮マニアと、空虚な毎日を送るゴシック＆ロリータ・ファッションの少女。映画は、生きる実感を得られない両者の行動を通し、すべての面でリアリティーを失いつつある現代社会に鋭くメスを入れる。世界30ヵ国の映画祭に正式招待され、ベルギーやシアトルでは一般公開された注目作だ。アメリカの同時多発テロを見て以来、リアリティーの感覚が麻痺した盗撮魔の長谷川は、生きている実感を得られない萌と出会う。互いに共感を覚えた彼らは手を組み、過激な盗撮に走る。", "2005-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("СЭР：СВОБОДА　ЭТО　РАЙ／FREEDOM　IS　PARADAISE", "自由はパラダイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "カザフ共和国のアルマリアにある少年院。ここに監禁されている13歳の少年、サーシャは、脱走の常習犯。ある日、彼は父親がアルゲンハリスクの収容所で生きていると知り、またも脱走して、父のいる極北の地を目指すが……。自由を求める、少年の姿を捉えたロード・ムービーの佳作。", "1989-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超少女REIKO", "超少女REIKO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "観月ありさの映画デビュー作で、東宝映画期待の新鋭・大河原孝夫の初監督によるサイキック・ホラー。文化祭を１週間後に控えたある高校で不可思議な怪現象が続発。そして、その解明に立ち上がる６人の生徒たちを襲うポルターガイスト現象。その時、メンバーの一人である玲子に不思議な力が備わっていることが判明する……。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPYMATE", "スパイモンキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "敏腕スパイとして働くサルの大活躍を描いた、奇想天外なアクション・コメディ。日本をも巻き込んだ世界的な危機のドラマが、ユーモラスに展開する。本職のスパイを、身の軽さを生かして危機から救出するのみならず、卓越した格闘術で忍者軍団を唸らせる、大胆な発想がユーモラスで楽しい。画期的な発明をした12歳の少女が誘拐された。元スパイの父マイクは、昔の相棒であるサルのミンキーの助けを借りて愛娘の救出に奔走する。", "2006-01-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("H・P・LOVCRAFT’S THE UNNAMABLE RETURNS", "ダークビヨンド 死霊大戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "人類誕生以前から、地球上には先住民として“魔族”が存在したという、独自の伝奇的世界観を構築した恐怖小説家、H・P・ラヴクラフト作品を映画化。300年の呪縛から解放された魔族と人間とのすさまじい戦いが描かれる。美女から怪物へ、そしてまた美女へという、変身のシチュエーションが見もの。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京流れ者", "東京流れ者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "解散した倉田組でその名を知られた男“不死鳥の哲”。今はカタギとなった組長とともにクラブ経営をしようとするが、その資金繰りに苦慮し、倉田組の財産ともいうべきビルを担保に入れる。このビルを、かつての対抗勢力・大塚組が狙っていたことから、哲はやっかいごとに巻き込まれて東京を追われる。そして新潟、北九州と流れ歩くが、安住の地を見いだすことができない。結局東京に戻ってきた哲は組長の倉田が敵方に寝返っているのを知って激怒する。そして最後の対決が……。川内康範のオリジナル・シナリオを異才・鈴木清順が監督した実に奇妙な味わいのある一編。ハードボイルド・タッチの東京編、任侠映画風の新潟編、コメディ・タッチの北九州編とまるでオムニバス映画のような構成が、主題歌『東京流れ者』にのせてミュージカルさながらのテンポで描かれていく。清順一流の現実から逃避した美学があふれる怪作。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ВИЙ", "妖婆・死棺の呪い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "中世ロシア。古都キエフのブラッキー修道院の神学生ホマーは、夏休みに仲間と一緒に帰郷する途中、一夜の宿を求めた農家で魔女の老婆に襲われる。魔女はホマーを連れて天高く舞い上がるが、ホマーが唱える悪魔払いの呪文によって地上に落下する。だが、ホマーが見たのは、気絶している美しい娘だった。その後、ホマーはコサック部落で死んだ地主の娘のため祈とうをあげることを頼まれるが、その死んだ娘はなんとあの気絶していた娘。それから３日３晩、古ぼけた教会の中で、ホマーと魔女との死闘が始まる。文豪N・ゴーゴリの妖怪小説を原作に、すさまじい悪夢の世界を見事に映像化したロシアのホラー映画。", "1967-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RICHARD III", "リチャード三世", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "L・オリヴィエが製作、監督、主演した「ヘンリー五世」「ハムレット」に次ぐ、シェイクスピア劇の映画化。15世紀後半の百年戦争を背景に、不自由な体でありながら野望を広げていくリチャード王を描く。イギリス劇壇の名立たるシェイクスピア役者を一堂に集めた大作。", "1955-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新流星胡蝶剣／BUTTERFLY　SWORD", "新流星胡蝶剣 秘術VS妖術", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ツァオ派とリー派が構想を繰り広げる明王朝。ツァオ派の女密使カオは、リー派から権力者サンへの密勅を奪い取るように命じられる。何とか、密勅を手に入れツァオの元に届ける。しかし、リー派を皆殺しにしたツァオは、カオの命を狙うようになる。カオはツァオの片腕だったシャオ・ハンらを仲間に、ツァオに立ち向かうのだった。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRE−EATER", "白夜の時を越えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ドキュメンタリー映画で活躍してきたフィンランドの女性監督P・ホンカサロが、ある双子の姉妹の不思議な生い立ちを描いた異色作。寂れた酒場で一人の少女と出会ったヘレナは、少女の姿に昔の自分を重ね合わせて貧しかった少女時代を回想していく……。フィンランドの美しい自然や、姉妹が生業とするサーカスのショーを幻想的に捉えた映像が秀逸。", "1998-01-01", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秀子の車掌さん", "秀子の車掌さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "井伏鱒二の『おこまさん』を成瀬巳喜男が映画化。ある山間の田舎町を舞台に、つぶれかかったバス会社をめぐる人間模様を描く。バスガイドのおこまさんに、当時、名子役として絶大な人気を博していた高峰秀子が扮し、のちに多くの名作を生み出していく成瀬、高峰コンビの記念すべき第１回作品となった。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents すき　「最後のタンゴ」", "桃まつり presents すき　「最後のタンゴ」", "", "", "http://image.eiga.k-img.com/images/movie/57884/315x210.jpg?1575", 31, "若手女性監督による映画製作・上映集団「桃まつり」が、「好き」をテーマに女性監督たちが撮り上げた短編作品を上映する「桃まつり presents すき」の1本。結婚を前提に同棲していた年下の恋人シュンジに出て行かれてしまったレイコは、腹を立ててシュンジの荷物を処分し、引越しの準備を始めるが……。監督は、ぴあフィルムフェスティバル2006でグランプリを受賞した熊谷まどか。「弐のすき」として「春まで十日間」（ステファニー・コルク監督）、「口腔盗聴器」（上原三由樹監督）と同時上映。", "2012-03-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIRE´ A｀ PART", "私家版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ミステリー・マガジンの記者であり、ニュースキャスターとしても活躍するB・ラップが監督デビューを果たした秀作ミステリー。編集者ニコラは、30年来の友人エドワードが書き上げた私小説を読んでがく然とする。そこには、30年前の恋人の自殺の真相が記されていた。小説の出版を協力する一方で、それと全く同じ贋作づくりに取り掛かった彼は、着々と復讐の計画を進めるのだった……。名優T・スタンプが、巧妙な罠を仕掛ける編集者を、冷淡かつ知的に好演。作家を精神的に追い詰めていく過程を淡々と追う、緻密なストーリー展開も秀逸だ。ビデオタイトルは「私家版　復讐のページ」。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KORCZAK", "コルチャック先生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "世界初の小児専門医であり、本業の医師はもとより、時には児童作家として、時にはラジオのパーソナリティーとして、生涯を子供たちのためにささげたポーランドの伝説的ユダヤ人、ヤヌシュ・コルチャックの物語。ポーランドのユダヤ人が非業の死をとげたワルシャワ・ゲットーの生活を忠実に描いている点にも注目。", "1990-01-01", "42,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IRON EAGLE II", "メタル・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "米・ソ両国に照準を合わせた中東某国の核ミサイル基地を壊滅させようと、両国の若きパイロットたちが対立しながらも力を合わせて友情を育んでいく様子を描くエアフォース・アクション。迫真の空中撮影シーンが圧巻。ヒット作「アイアン・イーグル」の続編。ビデオ発売時タイトルは「メタル・ブルー　アイアン・イーグル２」。", "1988-01-01", "10,46", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAID", "彼はメイド・イン・パリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "メイドなしではやっていけない、バリバリのキャリアウーマン。その彼女に一目惚れしてしまった銀行員は彼女の家のメイドになりすまし、愛を獲得しようとするのだが……。現代ならではの男と女の関係を表したロマンティック・コメディ。ヨーロッパ的上品さをサラリと演じるビセットと、演技派シーンの競演も見もの。", "1990-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L' Iceberg", "アイスバーグ！", "", "", "http://image.eiga.k-img.com/images/movie/55494/315x210.jpg?4601", 87, "ベルギーで道化師として活動していたドミニク・アベル＆フィオナ・ゴードン夫妻の長編監督デビュー作。ブリュッセル郊外のファストフード店に勤める主婦フィオナは、ある夜、うっかりして冷凍庫に閉じこめられてしまう。翌朝無事に出ることができたフィオナだったが、家族にも彼女の不在を気づいてもらえずショックを受ける。そんな中、氷や冷たい物への憧れを持ち始めていたフィオナはファストフード店に納品に来た冷凍車に乗って氷をめざす旅に出る……。「ルンバ」と同時上映。", "2010-07-31", "37,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Carrie", "キャリー", "", "", "http://ecx.images-amazon.com/images/I/51Nw%2BY5WNxL._SL210_.jpg", 98, "　狂信的な母親のもとで育てられた高校生のキャリー。学校でイジメの標的となっていた彼女には不思議な超能力があった。ダンス・パーティの日が近づく中、クリスらイジメっ子たちは陰湿な嫌がらせを思いつき、キャリーを陥れる。しかしその時、キャリーの怒りが爆発。彼女の超能力は惨劇を招くことに。そしてそんなキャリーに、母親は……。ヒロインの青春を描く異色オカルト・ホラー。衝撃のラストまでデ・パルマ監督の映像マジックが満載。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昆虫物語みつばちハッチ　勇気のメロディ", "昆虫物語みつばちハッチ　勇気のメロディ", "", "", "http://image.eiga.k-img.com/images/movie/55214/315x210.jpg?2185", 101, "タツノコプロの人気アニメ「昆虫物語みなしごハッチ」の劇場版。「おくりびと」の小山薫堂が総合プロデュースを手がける。みつばち王国の王子ハッチはある日、スズメバチの襲撃で母や仲間たちと生き別れになってしまう。独りきりで母を探す旅に出たハッチは、人間が暮らす町・セピアタウンに迷い込み、イモ虫のクネクネ、セミのミンミン、アリのアリコンらと友だちになる。やがてハッチは孤独な少女アミィと出会い、彼女の吹くハーモニカのメロディに心ひかれていく。", "2010-07-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SADA", "SADA", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "昭和11年に起きた阿部定事件の主役である女性、定の半生にスポットを当てた作品。14歳の夏に無理やり処女を奪われた定は、そんな自分を優しく介抱してくれた医学生・岡田に恋心を寄せるが、彼は突然別れを告げて彼女の前から去ってしまう。それから定は芸者置屋に身を置くようになり、一人前の娼婦となっていく。29歳の時、彼女は堅物の市会議員・立花の愛人となる。岡田のことを心の中で慕い続ける彼女は立花に頼んで、彼の行方を捜してもらうが、衝撃の事実を知る。定が、岡田へのプラトニックな愛を持ち続けた女であるという側面から捉えているのが新味。大林流映像が、黒木瞳演じる定の妖艶さとかわいらしさをスクリーンに焼き付けている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白子屋駒子", "白子屋駒子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "封建の世に、あまりにも美しく、あまりにも哀しく、恋に生き、そして恋に散っていった悲恋の女・白子屋駒子の波乱に富んだ半生を描いた大映お家芸の女性作品。衣笠貞之助が、愛弟子の三隅研次のために自ら原作を脚色し、作品を撮らせた。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄道員〈ぽっぽや〉", "鉄道員〈ぽっぽや〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "浅田次郎のベストセラー短編集のなかの同名のエピソードを映画化。北海道のローカル線の終着駅で駅長を勤める初老の乙松は、じき定年を迎えようとしている。来る日も来る日も列車を迎え、送り出してきた彼は、妻や娘の死を見とることができなかった。そんな乙松の前に、ある日、謎めいた少女が出現。人なつっこいその少女は、やがて乙松の人生に“奇跡”をもたらす……。鉄道員としての誇りを持って生き続け、戦後の高度経済成長期を支えてきた主人公。そんな消えゆく世代を、かぎりなく温かいまなざしで描き出していく。「動乱」以来19年ぶりに古巣の東映に戻ってきた高倉健が、ハマリ役ともいうべき乙松を好演。第23回モントリオール世界映画祭では、主演男優賞を受賞している。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON　CONNAI＾T　LA　CHANSON", "恋するシャンソン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "老匠A・レネが若々しい感性で綴ったラブ・ストーリー。新居に引っ越した中年夫婦と、ツアーガイドをしているその娘、やり手の若き不動産業者、風采の上がらない中年の部下など、大人の男女７人がパリの空の下で陽気な恋騒動を繰り広げる。新旧のフレンチポップ・ナンバー36曲を配し、登場人物がセリフ代わりに、それらを唐突に口ずさむというポップなノリが、何とも楽しい。", "1997-01-01", "2,26,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("1984", "1984", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "テレスクリーンによって日夜監視され、セックスも自由思想も違法。それが1984年の世界だった……。G・オーウェルの未来小説『1984』を映画化したアンチ・ユートピア作品。全体主義的な国家権力に逆らい、愛し合う一組の男女の挫折していく姿を淡々と描いている。", "1984-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents 真夜中の宴　「希望」", "桃まつり presents 真夜中の宴　「希望」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 15, "", "2008-04-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE SERENADE", "ラブ・セレナーデ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "オーストラリアの片田舎の町に暮らすビッキーとディミティの姉妹。姉のビッキーは恋愛に過剰な思い入れがあり、恋に恋をしているが、妹のディミティはすでに恋を半分あきらめていた。そんな姉妹の隣に、元人気DJのケンが引っ越して来たことで、二人の生活が一変する。どちらが先にケンに気に入られるかの競争が始まったのだ。ビッキーは手料理攻撃を開始し、ディミティはただ本能のままにケンにまとわりつく始末。二人の思いは募っていくが、実はケンには想像もつかない秘密があったのだ。意外なラストもほほ笑ましい軽妙なラブ・コメディ。", "1996-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PUPPET MASTERS", "ブレイン・スナッチャー 恐怖の洗脳生物", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "アイオワ郊外の森にUFOが撃墜され、現場に向かった科学情報局長アンドリューは、村人たちが宇宙から飛来したナメクジ状のエイリアンに体を支配されていることを知る。人間の脳に寄生するエイリアンは彼の息子サムに取り付き、やがてアンドリュー自身も狙われる……。アメリカを代表するSF作家R・A・ハインラインの古典的名作『人形つかい』を映画化。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("盲獣", "盲獣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "江戸川乱歩のマゾヒズム小説を増村保造が監督した異色極まりない怪作。モデルのアキは、ある日突然盲目の男に誘拐され、巨大な女体像のある倉庫の一室に監禁される。男はアキをモデルに彫像を作り始める。最初は拒んでいたアキも、いつしか男との暗闇の中でのマゾヒスティックなセックスに没入していく。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鮫", "鮫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 166, "海の無法者・サメに因んでつけられた主人公のアダ名が題名になっている。主人公サメはその名のごとく極悪非道の限りをつくし、ついに悪党の首領にまで成り上がる。だが、以前手ごめにした尼僧の美しさが忘れられなくなり、次第に人間愛に目覚めていく。原作は真継信彦の同名小説。田坂具隆監督の異色作。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("菊豆", "菊豆（チュイトウ）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "　チャン・イーモウ初監督作「紅いコーリャン」同様、農村を舞台に情念、エロスを色彩豊かに描いた作品。男に期待せず、自分の力で気丈に生きる女性をコン・リーが力強く演じている。若く美しいチュイトウが嫁いだ先には、年老いた染物屋の主人と甥にも関わらず、下僕のような扱いを受けているティエンチンがいた。ティエンチンが、サディスティックな夫によって痛めつけられた彼女の身体を除き見た日から、2人は禁断の関係に落ちていく。", "1990-01-01", "8,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新妻鏡", "新妻鏡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "文代は、見合いの当日、隣家の少年・邦男に誤って空気銃で目を射たれてしまう。病室に邦男を連れて兄の博が見舞いにくるが、文代は冷たく追い返す。薄幸な美女がたどる数奇な運命と、純粋な愛を描く。小島政二郎の原作の再映画化で、音楽を担当している古賀政男と島倉千代子が自身の役で特別出演している。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地の群れ", "地の群れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "日活を退いた熊井監督がフリー第１作にATGで“一千万円映画”として製作・監督した作品。米軍基地のある佐世保が舞台で、原爆被災者、未解放部落、朝鮮人問題が複雑に絡み、単なる社会派映画でなく日本人の原罪を追及した作品になっている。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Happening", "ハプニング", "", "", "http://image.eiga.k-img.com/images/movie/53504/315x210.jpg?2487", 91, "「シックス・センス」「サイン」のM・ナイト・シャマラン監督が、何の前触れもなく突然起こった人類滅亡の危機をサスペンスフルに描いたパニック大作。ある日突然全米全土からミツバチの姿が消えたのを皮切りに人々が次々と倒れていく異常現象が発生。フィラデルフィアの高校教師エリオットは妻や友人の娘を守るために見えない脅威からの逃避行を続けるが……。出演はマーク・ウォールバーグ、ズーイー・デシャネル、ジョン・レグイザモら。", "2008-07-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版そらのおとしもの　時計じかけの哀女神（エンジェロイド） ", "劇場版そらのおとしもの　時計じかけの哀女神（エンジェロイド） ", "", "", "http://image.eiga.k-img.com/images/movie/56034/315x210.jpg?2938", 96, "「月刊少年エース」（角川書店刊）連載の水無月すうによるコミックを原作に、2009年から10年に放映された人気アニメーションの劇場版。「平和が一番」をモットーにする少年・桜井智樹と、空から舞い降りてきた“未確認生物”のイカロス、ニンフ、アストレアの交流を描くハートウォーミングコメディ。智樹にあこがれる少女・風音日和の視点でTVシリーズを再構成し、新作パートではTVでは描かれなかったナゾが明らかにされる。", "2011-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PREPPIE MURDER", "N.Y.捜査線 ナイトハンター 女子学生殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ニューヨークでの“プレッピー”と呼ばれるエリート学生たちの乱れた私生活と、そのガールフレンドたちの異常なセックス・ライフが明かされる、実際の警察の調書と裁判記録をもとに作られた犯罪映画。容疑者として取り調べられ、意外な内容の自供をする主役の学生をW・ボールドウィンが演じる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOTHING BUT TROUBLE", "絶叫屋敷へいらっしゃい！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "コメディ映画の全米最強トリオが結集、抱腹絶倒のギャグを満載した究極コメディ。ひょんなことから気が合った同じマンションに住む男と女。一緒にドライブに出かける予定が途中から陽気なラテン人が車に便乗、やがて交通違反で保安官に追われ、なぜか治安判事の屋敷に連行される。そこは世にも恐ろしい、からくりだらけの絶叫屋敷だった……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHARON〈カロン〉", "CHARON〈カロン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "'92年の「心臓抜き」がカルト的人気を博した高橋玄監督の異色作。夫と愛人の間で多重生活を送る娼婦カロンの秘められた謎が明かされる。タイ映画「マッハ！」などをビッグ・ヒットに導いた香港のゴールデン・ネットワーク社により、世界配給されるなど注目を集めたインディペンデント映画だ。作家の夫がいながら愛人の道男とも暮らしていた秀子は、関係がバレると同時に消息を絶つ。夫の勝木と道男は彼女の行方を追っていく。", "2005-02-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY FATHER THE HERO", "恋人はパパ ひと夏の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "フランス映画「さよならモンペール」を、主演のG・ドパルデューはそのままにハリウッド調ロマンティック・コメディにリメイク。いつの間にか思春期を迎えた14歳の娘とその父親の、ケムたいような照れ臭いような関係を笑いにくるめて描き出す。南国のバカンスへ旅立った父娘。そこで出会ったハンサム・ボーイの気をひこうと、娘はパパを恋人と偽る奇策に出るが……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAYDAY AT ４０，０００FEET！", "メイデイ 40,000フィート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ロサンゼルス空港を飛び立ったトランスコン602便の機内で、FBIの連れていた凶悪犯グレコが突然暴れ出した。機長はすぐに、遭難信号“メイデイ”を発信するのだが……。極限状態での人間模様を小気味良く描いた、飛行機パニック映画。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAS　NUTZT　DIE　LIEBE　IN　GEDANKEN", "青い棘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1927年、19歳の学生が見習いコックを射殺して自殺。目撃したのは犯人の親友だけだった……。当時ドイツでセンセーションを巻き起こした“シュテークリッツ校の悲劇”を映画化した真実の物語。労働者階級のパウル・クランツと上流階級のギュンター・シェラーがたどった刹那的な青春を、官能的かつ詩的につづる。寄宿学校の親友パウルとギュンターは、歓喜に満ちた瞬間を求めていた。だが、それを感じなくなったときに、彼らはある決断をする。", "2005-10-29", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’INITIATION", "初体験", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "カナダの大学生ヴィクトアールは、フランスの流行作家ジュルベの小説に憧れて、自分も同じような初体験をと願っていた。だが、ボーイフレンドのピエールはまだまだ未熟。そんな彼女の前に、何とジュルベ本人が現れて……。初体験に憧れる女子大生が性を通して本当の恋を知るラブ・ストーリー。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Good Night", "恋愛上手になるために", "", "", "http://image.eiga.k-img.com/images/movie/53834/315x210.jpg?2956", 90, "ペネロペ・クルス、グウィネス・パルトロウらが共演するラブストーリー。監督はグウィネス・パルトロウの実弟ジェイク・パルトロウ。元人気バンドメンバーで現在はCMの作曲家に甘んじているゲリー（マーティン・フリーマン）と、ギャラリーに勤めるキャリアウーマンのドーラ（グウィネス）。2人は長い同棲生活の末、深刻な倦怠期を迎えていた。そんな折、ゲリーの夢に彼が理想とする女性アンナ（ペネロペ）がたびたび現れるようになり……。", "2008-11-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alexandra", "チェチェンへ　アレクサンドラの旅", "", "", "http://image.eiga.k-img.com/images/movie/53984/315x210.jpg?4478", 92, "「太陽」「牡牛座　レーニンの肖像」のアレクサンドル・ソクーロフ監督が、実際のチェチェン最前線で撮影した重厚な人間ドラマ。ロシアが誇る世界的オペラ歌手ガリーナ・ビシネフスカヤが、映画初出演ながら主演のアレクサンドラを好演している。80歳のアレクサンドラは孫のデニスに会うため、チェチェン戦地の最前線までやってくる。人を殺すことを仕事とする軍人の孫を心配するアレクサンドラだったが……。", "2008-12-20", "24,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大江戸七人衆", "大江戸七人衆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "浅草の縄張りをめぐって、旗本一派は鬼留一家から暗黙の挑戦を受けていた。争いを好まぬ旗本の勝川は、単身、鬼留一派の帯刀邸に乗りこむが、逆に訴えられて江戸を追放されてしまう。これを機に、鬼留一家の旗本一派への執拗な嫌がらせが始まるが……。任侠時代劇の骨太作品。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("red letters", "red letters", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「仮面ライダー響鬼」でブレイクした松田賢二＆「踊る大捜査線」シリーズなどの個性派俳優・筧利夫主演によるサスペンススリラー。拉致した女たちを監禁し、とある廃きょビルで深い眠りについた松本。数時間後、女たちの奪還を狙う掛橋とその部下が松本の前に現われる。ところが、掛橋らに無理矢理起こされた松本は、眠る前の記憶をすべて失っていた。掛橋は松本の体の下から1通の古ぼけた手紙を発見するが……。", "2006-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEAST 3D WELCOME BACK TO BEAST AIRLINE", "BEAST 3D WELCOME BACK TO BEAST AIRLINE", "", "", "http://image.eiga.k-img.com/images/movie/57554/315x210.jpg?2734", 0, "2009年にデビューし、11年には日本進出も果たした韓国の6人組男性アイドルグループ「BEAST」が11年2月に韓国で行ったライブ「WELCOME BACK TO BEAST AIRLINE」の模様を3Dで上映。「SHOCK」「BAD GIRL」「FICTION」など日韓のヒット曲をはじめ全13曲を披露したステージのほか、メンバーのインタビューやリハーサルの模様も収められている。", "2012-01-07", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mahler auf der Couch", "マーラー　君に捧げるアダージョ", "", "", "http://image.eiga.k-img.com/images/movie/55844/315x210.jpg?4767", 102, "「バグダッド・カフェ」のパーシー・アドロン監督が、天才作曲家グスタフ・マーラーと妻アルマとの愛と苦悩を描き、未完に終わった大作「交響曲第10番」に秘められた真実を描く。1910年夏、マーラーは、19歳年下で類まれな美ぼうと音楽的才能をもつ妻アルマとの間に亀裂が生じていることに困惑していた。アルマは新進気鋭の建築家グロピウスに慰めを求め、その事実を知ったマーラーは精神分析医フロイトを訪れ、自らの胸の内を語り始める。", "2011-04-30", "7,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ENEMY WITHIN", "アメリカが沈むとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "1997年のアメリカを舞台とするポリティカル・サスペンス。イランとイラクは同盟国となり、核爆弾が北朝鮮によって使われてしまったという時代背景のなか、アメリカ国防総省はかつての強大な力を失っていた。統合参謀本部では、ロイド総司令官を中心に“スラムダンク”なる大規模演習が極秘に計画され、決行まで７日と迫っていたが、ケイシー大佐は、演習が実は大統領に対する軍のクーデターではないかとの疑いを抱く。そんな折り、司法長官ダニエルズが薬物による奇妙な死を遂げた。ホワイトハウスに呼ばれたケイシーは、大統領に疑念を話し、スラムダンク決行前の筋書き解明を誓う。HBO製作のTVムービー。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("變臉／THE KING OF MASKS", "變臉〈へんめん〉　この櫂に手をそえて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「古井戸」で第２回東京国際映画祭グランプリを受賞したウー・ティエンミンが、９年ぶりに手掛けた感動作。1920年代の中国。瞬時に次々と面を変える“變臉の技”で人気の老芸人がいた。芸は男の子にしか受け継いではならないため、跡取りのいない彼はそのことを案じている。そんなある日、子供の売買が行れている場所で、彼は熱心に自分を見つめる男の子を買い取る。老芸人を“おじいさん”と言って慕い、懸命に芸を覚える少年との間に強い絆が芽生えた時、少年が女の子だったことが発覚する。絶望した老芸人は、涙ながらに許しを請う少女を手放すことにするのだが……。混乱の時代の中で、肉親を越えた深い絆で結ばれていく老芸人と少女の交流と、ひたむきな姿が胸を打つ。", "1996-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2010-2011 ロッシーニ「オリー伯爵」", "METライブビューイング2010-2011 ロッシーニ「オリー伯爵」", "", "", "http://image.eiga.k-img.com/images/movie/56474/315x210.jpg?1254", 188, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を劇場のスクリーンで上映する「METライブビューイング」2010～11年シーズンの9作目で、ロッシーニ晩年のコミック・オペラ。13世紀初頭、好色なオリー伯爵は、フォルムティエ伯爵の妹アデルに言い寄るため、変装して男子禁制の城に入り込もうとする。オリーに仕えるイゾリエもアデルに恋し、彼女に会うため主人の作戦を手伝うが失敗。あきらめないオリーは別の手段を試みるが……。", "2011-05-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("S.O.B.", "S.O.B.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "自信作の興行に大失敗した映画プロデューサーが、清純派スターで売っている妻を主演にポルノ映画を撮ろうとする。B・エドワーズがハリウッドへの皮肉を込めて描いた内幕もの。妻でもあるアンドリュースが自作のパロディーに挑戦、ヌードも披露する。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勝利の基礎〈いしずえ〉", "勝利の基礎〈いしずえ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "広島県江田島海軍兵学校の生徒たちの訓練風景や卒業式の模様を、中川順夫監督が３年６ヵ月に渡って記録。戦中の若者たちの生活風習がうかがえる貴重なドキュメンタリーだ。なおDVDには「空の少年兵」が併録されている。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("奇跡のリンゴ", "奇跡のリンゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　PETIT　VOLEUR", "さよならS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "「天使が見た夢」のE・ゾンカ監督の第２作。無名の新人を起用し、暴力と狂気の世界に入り込んだ18歳の少年の野望と孤独を強烈なインパクトで描き出した、ホロ苦い青春映画。平凡な生き方を変えようと思ったSは、ボクシングジムを根城にするギャングの仲間となるが、凶暴になりきれない自分を感じてしまう。そんななか、窃盗仲間と豪邸に忍び込み、警察に追い詰められ一人逃走する。仲間を裏切り、お金も帰るところも失ってしまったSが取った行動は、仲間の祖母を襲うことだった。祖母の狙撃に失敗したSを待っていたものとは……。観る者に、Sの放つパンチ以上の衝撃を与えるラストシーンは見事。本作で初めてE・ゾンカの作品を手掛けたP・ミロンのハンディカメラを駆使した荒々しくシャープな映像が、クローズアップやダイレクトなサウンドで生々しさを刻んでいくゾンカ流の演出をさらに際立たせている。", "1999-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARDLY WORKING", "底抜け再就職も楽じゃない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "サーカス一座の倒産でピエロ業をクビになったボー・フーパーの職探しのドタバタ騒動を描いたコメディ。ディーン・マーティンとコンビを組んで人気を博した“底抜け”シリーズの珍コメディアン、ルイス10年ぶりのカムバック作だ。ルイス単独主演で“底抜け”とタイトルが付く最終作。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD ＆ CONCRETE", "ブラッド&コンクリート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "破滅寸前の車泥棒ジョーイとドラッグに溺れる自殺志願の女性シンガー、モナ。ロサンゼルスのアンダーグラウンドな世界に生きる男と女を中心に、ギャングや刑事、薬の売人、ゲイらが織り成す様々な人間模様を描く。強烈なブラック・ユーモアとスラングで綴られた台詞と、ウィットに富んだ音楽が全編に展開するライナーの監督デビュー作。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金玉満堂", "金玉満堂（きんぎょくまんどう）／決戦！炎の料理人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "名のあるレストランに勝負を挑んでは乗っ取りを繰り返す悪の料理人ウォン。そのウォンから究極の宮廷料理“満漢全席”対決の挑戦を受けた、名門“満漢楼”。ところがオーナーのオウが倒れた上にコックが逃げ出し、オウの娘ガーウェイとコックの見習いサンが戦うハメになる。2人はかつて中国一と言われた幻の名料理人リウを探し出し、助っ人を頼むが……。香港でも放映され大人気を博した「料理の鉄人」を彷彿とさせる料理バトルが見もの。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("有限会社ひきもどし", "有限会社ひきもどし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "現代日本の深刻な社会問題となり、ニュースで報じられることも多い“ひきこもり”をテーマにした人間ドラマ。ひきこもり状態にある少女の力になろうと奔走する若者の活躍が描かれる。北野井子が「HANA-BI」以来、8年ぶりに映画出演。ひきこもりに悩むヒロイン役を好演している。ひきこもり救済とは名ばかりの悪徳会社に就職した京介。当初は会社の悪行に従う彼だが、ひきこもりの紀美子と出会い、考えを改める。", "2005-09-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アナーキー", "アナーキー", "", "", "http://image.eiga.k-img.com/images/movie/54244/315x210.jpg?3540", 90, "1980年にメジャーデビューし、当時若者の間でカリスマ的な人気を誇った日本のパンクロックバンド“アナーキー”のドキュメンタリー。埼玉の不良たちが勢いで結成したアナーキーは、1970代後半に世界同時多発的に盛り上がったパンクムーブメントと見事に呼応した。日本のロックシーンと社会に与えた強烈なインパクトをバンドのメンバーと関係者、そして彼らから影響を受けたアーティストたちのインタビューと共に、当時の貴重な映像を通して辿っていく。", "2008-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やる気まんまん", "やる気まんまん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "「日刊ゲンダイ」に27年間にわたって連載されていた、横山まさみち画、牛次郎原作による成人向けマンガを実写映画化。江戸時代から続く風俗店・淀君グループの総帥を務める神能数馬。ある日、ライバルチェーンのピンキーグループの経営者で数馬の幼なじみでもある釈八子が、風俗店の経営権を賭けたセックスバトルを申し込んでくる。数馬は相棒の“オットセイ”とともに、八子が繰りだしてくる強敵に立ち向かっていくが……。", "2007-12-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANALYZE THAT", "アナライズ・ユー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ロバート・デ・ニーロとビリー・クリスタル共演による都会派コメディ「アナライズ・ミー」の続編。息の合ったかけ合いをみせる、芸達者な実力派アクター二人のお笑い演技に注目を。かつての患者で刑務所に拘束されているマフィアのボス、ポールが奇行に走り出したことから、呼び出しを食らった精神分析医ベン。腐れ縁でポールを引き取るハメになった彼は、ギャングの犯罪計画に巻き込まれてしまう。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NON−STOP KID", "ロイドの猛進結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "娘のビービーをお偉い先生に嫁がせようと、父はヌードル教授を家に招いた。それを聞いた恋人ロイドは、町をかけずり回って教授の服を奪い、いけずうずうしくもビービーの家へとやって来るが……。H・ロイドが主演した、初期の１巻ものスラップスティック・コメディ。(ランニングタイムは併録の「ロイドの二梃拳銃」を合わせたもの)。", "1918-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幕末太陽傅", "幕末太陽傅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "川島雄三の代表作。元ネタになるのは落語の『居残り佐平次』だが、これに『芝浜の革財布』『品川心中』のネタを取り入れている。あと５年で明治維新という、文久２年の11月、品川の遊廓街で、佐平次は仲間を連れて大尽遊び。ところが、金は一文も持っていない。かくして佐平次は居残りとなって、しばらくそこに腰を落ち着けることになる。宿にはもう一組居残り組がいる。高杉晋作を中心とする勤皇の志士たちである。佐平次は、この高杉と仲良くなったり、廓の仕事を要領よくこなし、たちまち廓の人気者となる。映画はこれらの人物を絡ませ、佐平次のバイタリティーをテンポよく活写していく。佐平次は胸を患っており、時折、咳き込みながら見せる暗い顔には死の匂いが漂っていて、そうしたニヒリズムがバイタリティーの裏に見え隠れし、川島の体質を体現した傑作となった。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("200 Pounds Beauty", "カンナさん大成功です！", "", "", "http://image.eiga.k-img.com/images/movie/34304/315x210.jpg?4255", 116, "鈴木由美子の同名コミック（講談社刊）を韓国で映画化したラブコメディ。169センチで95キロの巨体だが、美声と音楽的才能を活かし、スター歌手のコンサートの舞台裏でゴーストシンガーを務めるカンナ。しかし、想いを寄せる音楽プロデューサーは、彼女を女としてみてくれない。彼を振り向かせるため、全身整形を施し、スリムな体形と美貌を手に入れるのだが……。韓国では660万人を動員し06年の興収第1位となる大ヒットを記録。韓国のアカデミー賞といわれる大鐘賞では12部門にノミネートされた。", "2007-12-15", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／ADADA", "アダダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "生まれつき口がきけず、耳も聞こえないアダダ。娘の将来のことを考えた両親は、元両班の家へと嫁がせる。ところが、まじめなだけが取り柄の夫が、友人にそそのかされて街の女を知るようになり、アダダは疎んじられるようになる。家を追い出された彼女は、幼なじみのスリョンに救われ、一緒に暮らすようになる。だが、次第に金の亡者となっていくスリョンにも絶望し……。監督は「シバジ」などで知られる韓国の社会派イム・グォンテクだが、ここでは金に踊らされる一人の女の流転を感動的に描き上げている。アダダを演じるシン・ヘスは、この作品で1988年のモントリオール映画祭主演女優賞を獲得。", "1987-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("梅蘭芳", "花の生涯　梅蘭芳", "", "", "http://image.eiga.k-img.com/images/movie/54194/315x210.jpg?2345", 147, "1993年に「さらば、わが愛／覇王別姫」でカンヌ国際映画祭パルムドールを受賞したチェン・カイコー監督が、再び中国の伝統演劇の京劇を題材にした実話に基づくドラマ。京劇の全盛期に登場した天才的な女形・梅蘭芳（メイランファン）の波乱に満ちた生涯を描く。主演は「天使の涙」「インファナル・アフェアIII」のレオン・ライ、「SAYURI」のチャン・ツィイー。梅蘭芳への敬意と軍への忠誠心とに揺れる日本人の軍人役で安藤政信も出演。", "2009-03-07", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いれずみ半太郎", "いれずみ半太郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "博奕もさることながら腕っぷしも強い渡世人・半太郎。ワラジを脱いだ小田原で、男にだまされ身投げをしようとしていた宿場女郎・お仲を救う。お仲は半太郎のあとを追うが、彼女に逃げられた当地小田原の親分・原の嘉十も黙ってはいなかった。美男スター・大川橋蔵と丘さとみの主演コンビによる股旅もの。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN　TEMPS　POUR　L’IVRESSE　DES　CHEVAX／A　TIME　FOR　DRUNKEN　HORSES", "酔っぱらった馬の時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "イラン＝イラク国境の山岳地帯に位置するクルド人の密輸の村を舞台に、両親を亡くした５人の子供たちが互いに助け合いながら苦難を乗り越えていく姿を描いた感動作。難病の兄マディの手術代を稼ぐため、姉は嫁入りし、次男アヨブは決死のキャラバンに挑む……。2000年カンヌ映画祭カメラドールと国際批評家連盟賞をダブル受賞した。", "2000-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A HANDFUL OF DUST", "ハンドフル・オブ・ダスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "1932年イギリス。トニーは、田舎風景に囲まれた大邸宅で、美しい妻と幼い息子とともに満ち足りた生活を送っていた。が、若いジゴロ、ビーバーの出現で、トニーの一家はまたたく間に崩壊していく。妻の不貞に絶望し、南米へ旅立つトニー。1930年代ロンドンの洗練された上流社会と、南米の未開の風景のコントラストが珍妙で、エキセントリックな人間ドラマ。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空、見たか？", "空、見たか？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "高校の時は級友と、卒業して兄の元に身を寄せてからはやさしい兄嫁と、奔放なセックス遍歴をもつ西川行男。結局は倉敷で所帯を持ち、トンカツ屋を経営するのだが……。主人公の衝動的で、しかも、のほほんとした生き方が魅力を与える、若者のセックスと放浪のロマンを描いた作品。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖女の時代", "妖女の時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "長崎俊一監督、大森一樹脚本のコンビが作ったサイコ・サスペンス。辰野吾郎は学生時代の友人から、美人の女医・葉子の調査を頼まれる。調査を進めるうち、患者から天使のように慕われている葉子が、意外にもホストクラブに通いつめていることを知る。片岡鶴太郎の初主演作で、愛に悩む男をシリアスに演じている。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Colombiana", "コロンビアーナ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-09-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES MISE´RABLES", "レ・ミゼラブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 180, "フランスのギャング・スター、L・ヴァンチュラが、ジャン・バルジャンに挑んだ感動超大作。たった一片のパンを盗んだために、19年間も獄中生活を送ることになったジャン・バルジャン。彼の獄中から出所後の波乱に満ちた人生と、いかなる時も人間としての誇りを守り抜く彼の姿を描き出す。", "1982-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIK TAK", "チックタック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "花売りの祖父と暮らしているサイードは、同級生のハッサンと大の仲良しだった。成績優秀なハッサンは、先生からごほうびに時計をもらうが、実はそれは父の形見で、母が先生に頼んで渡したものだった。サイードがハッサンと一緒に家に帰ると、サイードの祖父が時計を取り上げてしまう。翌日、ハッサンの母が先生にそのことを告げに来て、ハッサンの祖父は学校に呼び出される。そのことで誇りを傷つけられた祖父は、サイードを親元に帰すと言いだすのだった。", "1994-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES GIRLS", "魅惑の巴里", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "３人の踊り子をかかえた舞踊一座“レ・ガールズ”。二人が結婚、引退し一座は解散するが、そのうちの一人が自叙伝を発表。その中で同僚の色恋にふれたことから旧メンバーあげての法廷闘争となる……。三人三様の証言がさもありなんで面白い。ホンノリお色気漂うミュージカル・コメディ。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("＋1 プラスワン Vol.2", "＋1 プラスワン Vol.2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "監督が描いた物語のラフスケッチを基に、プロ・アマ問わず参加した俳優たちが意見交換しながら演技を練りあげていくワークショップから生まれた短編オムニバスの第2弾。監督には「クロエ」の利重剛、「いつか読書する日」の緒方明、「地下鉄（メトロ）に乗って」の篠原哲雄、「僕は妹に恋をする」の安藤尋ら、第一線で活躍するクリエイターたちが参加。監督と俳優たちが映画へのあふれる情熱をこめて作りあげた珠玉の作品が集まった。", "2008-10-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE STREAK", "ブルー・ストリーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ひょんなことから正義の味方となった小悪党の奮闘を描くアクション・コメディ。泥棒のマイルズは工事現場に、盗んだダイヤを隠したまま服役。ところが二年後そこには新しい警察署が完成していた。ダイヤを取り戻すために刑事に扮装した彼は、難事件の捜査を任されることになり……。「ナショナル・セキュリティ」のM・ローレンスが受難の主人公を妙演。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カンフーくん", "カンフーくん", "", "", "http://image.eiga.k-img.com/images/movie/53374/315x210.jpg?2301", 98, "中国からやって来た天才カンフー少年が日本を舞台に大暴れするカンフーアクション。少林寺の武術学校で“南ピン拳”の免許皆伝を目指すカンフーくん。最後の敵がいるという日本を訪れた彼は、太極拳の使い手・泉ちゃんが営む中華料理店を手伝いながら敵を探すことに。そんな彼の前に、日本征服を企む悪の集団\"黒文部省\"が現われる。主人公カンフーくんを演じるのは、実際に少林寺の武術学校に通っていたというチャン・チュワン。", "2008-03-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NINOTCHIKA", "ニノチカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1930年代を代表する大女優ガルボが、それまでのシリアスな役から一転、ソフィスティケイテッド・コメディに挑んだ話題作。貴族を奪った宝石を売りさばくため、ソ連からパリにやって来た女スパイ。ところが彼女は、それを知った者が送り込んだ男の虜になり、まんまと恋に落ちてしまう……。脚本にワイルダーが参加、洗練された台詞にいっそう磨きをかける。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鋼鉄の巨人", "鋼鉄の巨人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 49, "石井輝男監督と宇津井健のコンビによる“スーパー・ジャイアンツ”シリーズの第１作。宇宙人会議の結果、原水爆実験禁止を呼びかけるべくエメラルド彗星のスーパー・ジャイアンツが地球へ派遣される。原爆用X14ウラニウムが入った鞄をめぐってスーパー・ジャイアンツとアトムAB団が対決する……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸の悪太郎", "江戸の悪太郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "茂兵衛長屋で寺小屋を開く浪人・剣持三四郎。その茂兵衛長屋を見下ろす崖の上に住む直参旗本・秋山は巷で人気が高まった祈とう所の建立を企て、その敷地として長屋に白羽の矢を立てる。長屋を買い取るため汚い手段に出る秋山だが、三四郎らも買い戻しの資金集めに懸命につとめて……。かつて嵐寛寿郎主演で映画化された、同じマキノ監督によるリメイク版。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("闇を掘る", "闇を掘る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1995年に閉鎖された北海道、空知炭坑の炭坑夫たちの、その後を追いかけるドキュメンタリー。炭坑閉鎖後の三家族をカメラは追いかけ、町を離れた者や残った者の生活をとらえながら、たくましく生きる人間のエネルギーと、そこから浮かび上がる希望を伝える。あがた森魚による哀愁と素朴さが入り交じった音楽も印象深い。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRAVELING　WITH　CHE　GUEVARA", "トラベリング・ウィズ・ゲバラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "キューバ革命の英雄チェ・ゲバラの青春時代を描いたヒット作「モーターサイクル・ダイアリーズ」のメイキング・ドキュメンタリー。人気沸騰中の若手スター、ガエル・ガルシア・ベルナルの素顔が見られるだけでなく、撮影隊と一緒に南米を縦断している気分になり、旅情が満喫できる。かつてゲバラと南米を旅したアルベルトが「モーターサイクル・ダイアリーズ」の撮影に同行。思い出の地で過ぎ去りし日を追憶する。DVDは「モーターサイクル・ダイアリーズ コレクターズ・エディション」の映像特典として収録されている。", "2003-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beyond Reason", "テリー・サバラスの犯罪病棟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "とある精神病院。マーティ教授は、患者たちから絶大な信頼を寄せられていた。だが、一人の患者を自殺に追い込んだことから、マーティの人間性が問われるようになってしまった……。秀逸な心理描写で綴った異色のヒューマン・ドラマ。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Man from Nowhere", "アジョシ", "", "", "http://image.eiga.k-img.com/images/movie/55984/315x210.jpg?4549", 119, "「母なる証明」のウォンビンと「冬の小鳥」で絶賛された子役キム・セロンが共演し、2010年韓国で630万人を動員したサスペンスアクション。過去のある事件をきっかけに、世間を避けるように孤独に暮らしていたテシク。隣家の少女ソミは母親が仕事で忙しく、テシクを「アジョシ（おじさん）」と呼び、たった1人の友だちとして慕っていた。そんなある日、麻薬密売に巻き込まれた母親とともにソミが犯罪組織に誘拐され、ソミを救うため組織を追うテシクは事件の背後に隠された真実を知る。", "2011-09-17", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JE VEUX RENTRER A｀ LA MAISON", "お家に帰りたい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "鬼才アラン・レネにしては異色の風刺コメディ。アメリカの漫画家J・ファイファーのオリジナル脚本をもとに、初めてパリにやって来たアメリカ人のカルチャー・ギャップをコミカルに描いている。G・ドパルデューが、アメリカ文化に毒されたフランス人教授役を怪演。彼が操る、すごいスピードの英会話が笑える。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Silk", "シルク", "", "", "http://image.eiga.k-img.com/images/movie/53124/315x210.jpg?3076", 112, "「海の上のピアニスト」で知られるアレッサンドロ・バリッコの同名ベストセラー小説を「レッドバイオリン」のフランソワ・ジラール監督が映画化。19世紀のフランスと日本を舞台に、美しき妻と日本で出会った少女のとの間で揺れ動く男の心情を幻想的なタッチで描いている。出演は「ラストデイズ」のマイケル・ピットにキーラ・ナイトレイ、役所広司、中谷美紀、芦名星ら。音楽は坂本龍一。", "2008-01-19", "10,2,4,3,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦闘少女　血の鉄仮面伝説", "戦闘少女　血の鉄仮面伝説", "", "", "http://image.eiga.k-img.com/images/movie/55384/315x210.jpg?3282", 0, "「片腕マシンガール」「ロボゲイシャ」の井口昇、「東京残酷警察」の西村喜廣、「魁!!男塾」の坂口拓の3人が共同で監督を務めるバイオレンス・アクション。主演は杉本有美、高山侑子、森田涼花。いじめられっ子の女子高生・渚凛（杉本）は、16歳の誕生日に痛みを訴えていた右腕が変形し、自分が普通の人間でないことを知る。たちまち追われる身となった凛は、目の前で両親を殺され、その怒りから自分を傷つける人間を容赦なくその右腕で斬り倒していく。そこに見知らぬ男と少女が現れ、顔に鉄火面をはめられてしまう。", "2010-05-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘剣", "秘剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "五味康祐の同名小説を原作としたハードボイルド・タッチの時代劇。九州小笠原藩のなかでも随一の剣の使い手である孤児の早川典膳は、来藩した宮本武蔵を激怒させたことを機に山中にこもり、神技に近い秘剣をあみだす。しかし、その剣は血に飢えた邪剣でもあった……。宮本武蔵役で月形龍之介が出演。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("亡命鷲鷹／ON THE RUN", "オン・ザ・ラン 非情の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ユン・ピョウ主演の香港映画といえばコメディ・タッチのものが大半だが、本作は彼がシリアス演技に徹したフィルム・ノワール。刑事ミンの離婚した元妻が殺された。裏には警察内部の麻薬取り引きが関係していた。ミンは彼女を殺した女殺し屋チョイに接近する。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅の帝王", "修羅の帝王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "過去のしがらみを捨て、堅気として生きようとする兄、兄と連れ立って再び修羅の世界に戻ろうとする弟。まったく異なる人生を歩き始めた極道の兄弟の愛憎を切々と描く。複雑な家庭に育った篤彦と重徳は、義兄弟の倉本とともに暴力団東竜会の３兄弟として武名を轟かしていた。しかし、敵対組織・黒声連合に東竜会の会長が射殺され、激しい抗争のなか重徳は逮捕され、組は解散した。８年後、堅気となった篤彦の元へ出所した重徳が帰って来る。そして黒声連合への復讐のため、篤彦を挑発する……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOHN HUSTON", "ヒューストン・アンソロジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "「マルタの鷹」「黄金」「荒馬と女」などで知られるジョン・ヒューストンを追った資料としても貴重な映画。生前取材された監督へのインタビューを中心に、映画の名場面と、ポール・ニューマン、アーサー・C・ミラーなどの証言で構成されている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("軍神山本元帥と連合艦隊", "軍神山本元帥と連合艦隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "新東宝お得意の戦争大作。山本五十六長官と、彼が率いる連合艦隊の第二次大戦中における活躍と凋落を描いていく。“１年間は随分と暴れ回るが、その先は……”という名セリフを吐き、戦争の行く末を憂いていた山本長官の人物像を映し出している。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE OF THE DEAD", "ハウス・オブ・ザ・デッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "同名の人気シューティング・ゲームを、ドイツの俊英ウーヴェ・ボル監督が映画化。“死の島”と呼ばれる孤島に上陸した若者たちが、凶暴な無数のゾンビの群れに遭遇、決死のサバイバルが描かれる。ゲームさながらのスピード感と臨場感で映像化した、圧倒的な迫力のアクションに注目。サイモンら5人の若者は無人島で秘密のパーティーがあることを知り、島に上陸する。しかし、島には人の気配が全くなく……。", "2005-03-26", "7,1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES COUSINS", "いとこ同志", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ゴダールやトリュフォーらとともに、旧世代の映画に平手打ちをくらわして登場したC・シャブロルが若者たちの風俗を描いたヌーヴェル・ヴァーグの代表作。法学士の受験のためパリに出てきたシャルルは、ポールのアパートにやっかいになる。二人は同じ23歳のいとこ同志だが、性格も考え方もまったく反対だ。シャルルはマジメなタイプで、いつも受験勉強に励んでいる。一方のポールは人生享楽の要領のいいタイプで、いつもアパートに友人たちを招いてパーティーを開いている。だが、どういうわけか、女でも受験でも女神はポールに微笑む。そしてほんの冗談で向けられた銃口さえもポールに背を向ける……。", "1959-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハチミツとクローバー", "ハチミツとクローバー", "", "", "http://image.eiga.k-img.com/images/movie/1414/315x210.jpg?1830", 103, "羽海野チカ原作の大ヒットコミックを、櫻井翔、蒼井優、伊勢谷友介ほか豪華キャストで映画化。美大を舞台に、若者たちが繰り広げる“全員片思い”の恋愛模様を描く。浜美大に通う竹本は、いつもクールな真山や変わり者の森田らと共に楽しい学校生活を送っていた。ある日竹本は、華奢な体で一心不乱に絵を描く天才少女はぐみにひと目惚れしてしまう。監督は、「ライフ・カード」のCMなどを手掛けた人気CMディレクターの高田雅博。", "2006-07-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("СТАЧКА", "ストライキ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "エイゼンシュテインの記念すべき長編第１作。帝政時代の鉄工場で、労働条件の劣悪さに怒った者たちが一致団結。ストライキを敢行した彼らに、資本家の雇ったゴロツキどもが迫ってくる……。エイゼンシュテイン自身が提唱した映画理論“アトラクションのモンタージュ”を自在に使った、群舞のような乱闘場面が圧巻。コントラストの巧みな撮影の素晴らしさも特筆に値する。", "1925-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chrysalis", "インストーラー", "", "", "http://image.eiga.k-img.com/images/movie/54404/315x210.jpg?4818", 90, "フランスの新鋭ジュリアン・ルクレールによる近未来SFサスペンス。連続殺人事件の捜査中に公私共にパートナーであった妻を殺害され、休職していた刑事のダビットは、若い女性の変死体が発見されたことがきっかけで現場に戻ることになる。犯人の手がかりを得るために遺体の網膜スキャン試みるが、彼女の眼からは何故か記憶が消去されていた……。主演は「アレックス」のアルベール・デュポン。", "2009-03-07", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hill", "丘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "　第2次大戦さなか、北アフリカのイギリス陸軍刑務所に送り込まれた5人の囚人兵に刑罰の訓練が行なわれる。ウィルソン曹長のもと、その部下ウィリアムズは灼熱の太陽の下であまりにも過酷な訓練を続行し、ついには死亡者が出てしまう。囚人のひとりであるロバーツは法に訴えようとするが、あと3人の囚人はウィリアムスに反感をつのらせ……。戦時下で展開される社会派ドラマ。", "1965-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("世にも奇妙な物語・映画の特別編", "世にも奇妙な物語・映画の特別編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "単発TVドラマ『世にも奇妙な物語』の劇場公開作品。ジャンボ旅客機が墜落し、生き残った５人の寒さと睡魔との戦いを描く「雪山」。打ち入りなどせずに愛人と遊んで過ごしたいと願う大石内蔵助が携帯電話を拾ったことから運命が変わりだす「携帯電話」。チェスの元チャンピオンが大金持ちの老人に呼び出され対戦に応じると、それは現実の世界が盤になったチェスだったという狂気の世界を描いた「チェス」。もしも結婚にお試し期間があるなら……。二人のデータをコンピュータに入力しシミュレートされた結婚生活を体験する「結婚シミュレーター」。TVシリーズの“奇妙…”を継承しつつ、映画でしか成立し得ないまったく違うジャンルの４作品をスクリーンサイズで展開。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES NOUVEAUX TRICHEURS", "風の中のリセエンヌ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "マルセル・カルネ監督「危険な曲り角」(1958)のリメイク。パリに住むハイティーンの、楽しくも切ない、そして危なげで狂おしい恋模様をリリカルに映し出す。当時のフランス映画界の若手スターが共演したラブ・ストーリー。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEYOND　A　REASONABLE　DOUBT", "条理ある疑いの彼方に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "フィルム・ノワールの巨匠F・ラングが、プロデューサーとの度重なる衝突でハリウッドを去ることになったいわく付きの社会派ミステリー。日本では2000年に正式公開された。死刑問題をめぐる新作を書こうとする作家ギャレットが、婚約者の父親である新聞社オーナーと組んで、自分に無実の罪が降りかかるよう巧妙な細工を施す。ギャレットは予定通り、踊り子殺しの容疑者として逮捕されるが、死刑宣告寸前にすべてのカラクリを打ち明けようとした彼のもくろみは、思わぬことで破綻していく……。社会や人間のモラルを問いかけるラングの巧みなストーリーテリング、ラストのドンデン返しは必見。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Los Cronocrimenes", "タイム　クライムス", "", "", "http://image.eiga.k-img.com/images/movie/54064/315x210.jpg?3280", 88, "第5回スペイン・ラテンアメリカ映画祭上映作品。予期せぬアクシデントから1時間前の過去に迷い込み、自分自身と出会ってしまうエクトル。森の中、裸の少女。ピンク色の包帯で顔を覆う謎の人物、怪しげな施設。タイムトラベラーが驚愕の結末へと向かう綿密なストーリーで練られたサスペンス。デビッド・クローネンバーグ監督のハリウッド版リメイクが決定している。", "2008-09-14", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Birdy", "バーディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ウィリアム・ワートンの小説を元に、戦争で心に傷を負った青年とその親友との交流を描いた、名匠アラン・パーカー監督による傑作ヒューマン・ドラマ。ベトナム戦争から負傷して帰還したアルは、戦争のショックで精神病院に入院中の親友バーディに会いに行く。完全に心を閉ざしてしまったバーディは、病室にうずくまり、ただひたすら鳥になることを夢見ていた。アルは何とかバーディを立ち直らせようと、必死に語りかけるが……。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖怪奇談", "妖怪奇談", "", "", "http://ecx.images-amazon.com/images/I/31s7Wm7UA7L._SL210_.jpg", 86, "「心中エレジー」で国内外から注目された亀井亨監督が、自身の心の闇によって妖怪へと変化してしまった少女たちの運命を描いた切なくも悲しいホラー映画。モデルなのに上手に笑うことのできない山根美智子、幼稚な残酷さを隠し持つ中学生の佐伯まな、ネイルアートに熱中するフリーターの岩崎みひろ。そんな彼女たちの身体に、それぞれ奇妙な変化が起こりはじめ……。主演を務めるのは「約三十の嘘」の伴杏里ほか。", "2007-01-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE MAGNIFIQUE", "おかしなおかしな大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "監督P・ド・ブロカ、主演J・P・ベルモンドの名コンビによる大冒険コメディ。アカプルコを舞台に謎の組織と対決するスーパー・スパイのボブ。実は彼は三文小説家フランソワ・メルランの小説世界の人物で、やがて二人の描写が入り乱れ、虚実交じった奇想天外なストーリーが展開していく。", "1973-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUTE WITNESS", "ミュート・ウィットネス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "殺人を目撃した口のきけない女性に迫る危機を描いたサスペンス。モスクワでB級ホラー映画の製作を手伝うビリーは、深夜ポルノ映画の撮影現場を目撃する。しかし実は、本物の殺人行為をカメラに収める“スナッフ映画”の撮影で、しかも撮影しているのは一緒に仕事をするロシア人スタッフだった。男たちは警察に呼ばれるが死体は跡形もなく消え、身ぶり手ぶりのビリーの証言は退けられてしまい、彼女は犯人たちから追われるはめになり……。「ファングルフ　月と心臓」のA・ウォラー監督の出世作で、巧みなアイデアを映画的に構築し、スリルを高めている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("吹けば飛ぶよな男だが", "吹けば飛ぶよな男だが", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "“寅さん”以前の初期・山田洋次の傑作の一つ。当時、TVの『シャボン玉ホリデー』でクレージー・キャッツとともにおなじみになり、喜劇界のホープとして注目されたなべおさみを主演に起用し、緑魔子との異色コンビを組んでいる。大阪のチンピラ・サブはヤクザの幹部を夢見ている。たまたまナンパした家出娘・花子は、サブと同じく母親のいない貧しい生まれだった。二人は意気投合するが、花子は強姦による妊娠５ヵ月の身重であった、というホロ苦く、哀しいコメディ。有島一郎、犬塚弘、石橋エータロー、安田伸、石井均、芦屋小雁など東西のお笑い陣が顔をそろえており、小沢昭一の活弁調ナレーションが秀逸。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ECLISSE", "太陽はひとりぼっち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "「情事」「夜」で人間関係の断絶をテーマにしてきたアントニオーニ監督の代表作の１本。都会に生きる人間たちの不確かな恋愛感情をモノクロのシャープな映像感覚で追求した作品。ヴィットリアはふとしたことで、証券取引所で会った仲買人のピエロに心惹かれ、関係を持つようになる。だが、肉体関係まで持った二人にも何か埋められない溝があった。お互いを盲目的に愛するという能動的な情熱は二人にはない。ある日、二人は無感動に別れていく。アントニオーニ監督が、感傷に溺れることなく、突き放した冷静さで男女の別離を描いた心理的恋愛映画の秀作。", "1962-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅薔薇夫人", "紅薔薇夫人", "", "", "http://ecx.images-amazon.com/images/I/51RkiAOJYvL._SL210_.jpg", 70, "SM文学の巨匠・団鬼六の「紅薔薇夫人」を、「イズ・エー」の藤原健一監督が映画化。太平洋戦争中、戦地の病院で負傷した青木から1冊のスケッチブックを託された相田は、そこに描かれていた資産家の妻・美希にスケッチブックを届けに行き、彼女の優雅な美しさを目の当たりにする。2年後、日本の敗戦とともに没落した夫の借金に苦しむ美希は、娘とともに高級娼婦館で働かされることになり……。主演は「怪談新耳袋」の坂上香織。", "2006-05-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ファザーファッカー", "ファザーファッカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "プロデューサーとして多くの話題作を世に送り出した荒戸源次郎の初監督作。14歳の少女の妊娠・堕胎、そして養父との肉体関係というショッキングな内容を持つ内田春菊の自伝的小説を映像化。田中静子は、漫画家を目指す多感な中学３年生。ある日の午後、家族を捨てた父にかわって、母の愛人＝養父が家にやってくる。日々、女らしさを帯びる静子を、爬虫類のような眼差しでなめまわす養父。静子がボーイフレンドとの子を宿したことが発覚したとき、激怒した養父は自分との肉体関係も強要する。しかし母は、見て見ぬふりをするだけだった。夢想に現れる少年との美しいイマージュを織り交ぜながら、静子が過酷な現実から脱出するまでの２年間を描く。4097人の中から選ばれた中村麻美が静子を熱演している。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VICTOR／VICTORIA", "ビクター／ビクトリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "第二次大戦前にドイツで作られた作品のリメイク。1934年の花のパリが舞台。オカマの芸人がソプラノ歌手と出会い、彼女をゲイの伯爵に仕立てる。そのうえさらに女装させてステージショーをやらせるが……。二重三重のどんでん返しが楽しいミュージカル・コメディ。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PIN", "サイコ・シンドローム　僕だけを見て", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "父親が子供のオモチャとして、腹話術を使いしゃべらせていた解剖標本人形ピン。しかし息子のレオンは、ピンが生きていると信じたまま成長してしまい、いつしか二重人格の精神分裂症と化し、ピンを用いて次々と邪魔者を消していく。「サイコ」に「マジック」を足したようなサイコ・スリラーの佳作。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TURKISH DELIGHT", "ルトガー・ハウアー　危険な愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "R・ハウアーのデビュー作。奔放な若者エリックはヒッチ・ハイクでオルガに拾われるが、その車はトラックと衝突。一命を取り留めた二人は、オルガの母の反対を押しきって同棲し、身も心も深く結びついていく。だが良き理解者だったオルガの父の死で、二人の関係は悪化。オルガは金持ちと結婚し、渡米することになるのだが、ラストには衝撃的な締めくくりが……。1970年代初めという状況の中で、急速にあぶり出されてヒリヒリと乾いていく青春の心情。そうしたドライな痛さが伝わる青春映画だ。「ロボコップ」とはまったく異なる、P・ヴァーホーヴェン監督第２作。", "1973-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガイサンシーとその姉妹たち", "ガイサンシーとその姉妹たち", "", "", "http://image.eiga.k-img.com/images/movie/34284/315x210.jpg?1658", 80, "日中戦争中、旧日本軍に性的暴力を受けた中国人女性たちの姿を追ったドキュメンタリー。当時被害に遭った中国人女性の話を東京で聞き衝撃を受けたという班忠義監督が、9年の歳月をかけて山西省で取材を敢行。これまで差別や恐怖のために被害を明かすことができずにいた女性たちが、初めて自らの過去に向きあい、カメラの前でその思いを語る。さらに元日本兵たちにも話を聞き、日中戦争の真実を明らかにしていく。", "2007-10-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DEVIL 731", "黒い太陽七三一　戦慄！石井細菌部隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "第二次世界大戦中、満州に細菌兵器開発研究のために派遣された関東軍七三一部隊の残虐行為の全貌を映画化。ベストセラー小説『悪魔の飽食』でも綴られた人体実験の様子が描かれている。満州ハルピンの関東軍七三一部隊に、千葉県から少年兵３人が送り込まれる。軍医、石井四郎中将を部隊長とするこの部隊では伝染病の研究を表向きに、細菌兵器研究開発のために捕虜を使った人体実験が行われていた。次第に少年兵たちは、人間が道具のように使い捨てられていくことに無感覚になっていく……。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Un Homme qui Crie", "終わりなき叫び", "", "", "http://image.eiga.k-img.com/images/movie/56804/315x210.jpg?3774", 92, "カンヌ、ベルリン、ベネチアの世界3大映画祭の受賞作を中心に、各地の映画祭で話題になりながらも日本未公開だった作品を一挙上映する「三大映画祭週間2011」にて上映。内戦で混乱の続くチャド。元水泳選手のアダムは高級ホテルでプールの監視員をしていたが、新たに赴任した中国系のオーナーは仕事を息子に譲るよう強要する。一方、内戦で市民に戦争への参加を求める政府は、志願者に金を与えており、アダムも地区長からたびたび志願を強いられていたが……。2010年・第63回カンヌ国際映画祭審査員賞受賞作。", "2011-08-13", "2,37,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／YESTERDAY", "イエスタデイ／沈黙の刻印", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "韓国映画としては巨額の製作費約７億３千万円を投じた大作。舞台は2020年の近未来。連続誘拐殺人事件を追う特殊捜査隊の隊長と美しき犯罪分析官が、予想もつかない真実へとにじり寄っていく様を描く。TVドラマ『ホテリアー』のK・スンウ、「シュリ」のK・ユンジンら韓国の人気スターが共演。複雑怪奇なミステリーと派手な銃撃シーンが入り乱れるSFアクションだ。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナスターシャ", "ナスターシャ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ペテルブルクへ向かう列車に乗り合わせた二人の青年、幼子のように純粋な心を持つムイシュキンと、自身の荒々しい感情と欲望にほんろうされるラゴージンは、不思議な友情で結ばれる。二人はペテルブルクで、サロンの男たちの心を一身に集める美貌と才知を持った女性、ナスターシャに出会い、ともに激しく心惹かれるのだった……。玉三郎がナスターシャとムイシュキンを一人二役で演じて話題になった舞台作品の映画化。原作はドストエフスキーの『白痴』。ポーランドの巨匠A・ワイダは、数多くの登場人物と場面が複雑に交錯するドストエフスキーの世界を３人の人間模様の中に凝縮させ、しかもそれをたった二人の俳優で描き出した。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA ILUSIO´N VIAJA EN TRANVIA", "幻影は市電に乗って旅をする", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "廃車寸前のオンボロ市電、133番。交通局は新車と交換の時期がきたと判断し、解体工場行きを命令した。それにより、133番に乗車していた車掌のファン・コディネスと修理工のタラハスもまた職を失うことになってしまう。最後の夜、二人は気晴らしに飲みに出かけ、酔っぱらったまま市電を動かす。次々と乗車してくる人々。キリストの像を持った修道女や孤児院の子供たち、外国人やロバまでもが市電に乗り込み幻想的な夜の中を走り抜けていく……。同じく、ブニュエルが乗り物を舞台にした映画「昇天峠」に見られるような荒唐無稽さはなく、むしろイタリアン・ネオ・レアリスモに通じるような、ある重みをそなえている。しかし、夜の街を走る市電は、幻想とも現実とも知れぬ世界へと、観る者を誘い込んでいき、シュールレアリスム的ともいい得る物語の展開と市電の光景が、この映画を、多彩なブニュエル作品にあっても異色の一編とならしめている。", "1953-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SULLIVAN'S TRANELS", "サリヴァンの旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ハリウッド初の“自作を監督した脚本家”、天才スタージェスを代表する監督第４作。コメディ映画のヒット監督サリヴァンが、初めて社会派そのものの次回作を構想。取材のために浮浪者の格好で旅に出る。ところが、なんでも宣伝に使いたがる重役たちのお陰で貧乏人の苦労は味わえず失敗の連続。女優志願の娘と知り合って恋をしたり、事故死したと報じられたりしたあげく、やがて念願かなって、彼は無実の罪で刑務所に入れられるなど艱難辛苦をなめるのだが……。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUNNY BONES", "ファニー・ボーン　 骨まで笑って", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "スター・コメディアン、ジョージ・フォークの息子トミーはラスベガスでコメディアンを目指していたが、二世タレントというプレッシャーに負け、６歳まで暮らしていたイギリスへ渡る。彼はオーディションでオリジナルのネタを手に入れようとするが、うまくいかない。そんな時、天才コメディアン、ジャックの舞台を観たトミーは、ジャックの父トーマスの芸を買おうと考える。だが、トーマスはジョージのかつての仕事仲間であり、しかもジャックはトミーの弟だった！　複雑に絡み合う人間模様をコミカルに描いた作品。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRAITEMENT DE CHOC", "ショック療法", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「コミック・ストリップ・ヒーロー」（日本初公開時のタイトルは「殺人ゲーム」）でカンヌ映画祭脚本賞を受賞したジェシュア監督の異色作。ブルターニュにあるサナトリウムで謎の殺人事件が発生する。だがそこでは日ごと猟奇的な人間治療方法が行われていた。ドロンが二重人格の医師を熱演した怪奇サスペンス。", "1972-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マリアの胃袋", "マリアの胃袋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "６人の気鋭プロデューサーが作った“アルゴ・プロジェクト”の第３作。サイパンで事故死した、ある会社の宣伝課長とモデルのマリア。１年後、死んだ課長の元部下のOL４人がサイパンに遊びに来るが、彼女たちが次々に失踪する。幽霊の課長は、マリアの亡霊に若い女性の肉を食わせていたのだ……。ブラック・ユーモアをにじませたホラーを目指すという、平山監督の意図はすこぶるユニーク。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("This Is England", "THIS IS ENGLAND", "", "", "http://image.eiga.k-img.com/images/movie/54254/315x210.jpg?3802", 98, "「トゥエンティーフォー・セブン」のシェーン・メドウス監督が、自身の体験をもとにサッチャー政権下のイギリス郊外に住む若者たちを描いた青春映画。1983年のイギリス中北部、父親をフォークランド紛争で亡くした少年ショーンは町にたむろするスキンヘッズの不良たちと出会う。友達のいなかったショーンは彼らと心を通わせていくが、やがて過激な愛国主義の男コンボの登場で仲間たちは分裂していく……。", "2009-03-14", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREAT ZIEGFELD", "巨星ジーグフェルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 176, "アメリカ・レビューの創始者、フローレンツ・ジーグフェルドの生涯を描いた超大作。その豪華なセット、群舞などにMGMミュージカルの真髄が見える。ソフィスティケイテッド・ミステリー、「影なき男」シリーズで勇名を馳せたW・パウエル、M・ロイが共演した。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THAT MAGIC MOMENT", "シンシア・ギブのあの頃に恋して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1959年に北カリフォルニアのハイスクールを卒業した８人の男女たちの様々な人生を描いていく青春映画。彼らのその後をラスト・タイトルで流す手法などは「アメリカン・グラフィティ」そして「セント・エルモス・ファイアー」を思わせるものがある。製作総指揮と脚本を担当するリチャード＆エスター・シャピロはTV『ダイナスティ』の生みの親として有名なTV界の大物。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT BREED", "ミディアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "古代から息をひそめて地下で暮らしてきた魔族たち。彼らが住む禁断の地にある青年が足を踏み入れてしまったことから、伝説の秘密都市“ミディアン”への扉が開かれてしまう……。『血の本』などで有名な作家バーカーが自ら監督したホラー映画だけに、ただのスプラッタとは違った出来栄え。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGEL AND THE BADMAN", "拳銃無宿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "流れ者のガンマンの復讐をついに諦めさせる信心深い女の愛……。J・ウェイン主演の西部劇だが、美人女優G・ラッセルを共演に据えてドラマ的な要素の強い作品。オリジナルはモノクロだが、コンピュータによるカラーライズド（着色）版のビデオがある。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネ響「マエストロ6」　ダニエル・バレンボイム　ベルリン・フィルハーモニー管弦楽団", "シネ響「マエストロ6」　ダニエル・バレンボイム　ベルリン・フィルハーモニー管弦楽団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "映画館でクラシック音楽のコンサートを上映する「シネ響」の4作目。イギリス、オックスフォード大学のシェルドニアン劇場で行われた、ベルリン・フィルの2010年ヨーロッパ・コンサートの模様を収録。アルゼンチン出身の名指揮者ダニエル・バレンボイムが得意とするワーグナーをはじめ、注目若手チェリスト、アリサ・ワイラースタインを迎えたエルガーの「チェロ協奏曲ホ短調作品68」などを披露。", "2010-12-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUNKBROGREVEN", "ムンクブローの伯爵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ストックホルムの旧市街地ムンクブローに独身者用のホテルがある。この宿は詐欺師や酒の密輸業者たちの隠れミノになっていて、常に大金が動いているところでもある。その住人の男が、女から大金をだまし取ることに成功。しかし彼はすぐに捕らわれてしまう。バーグマンがホテルの女主人の娘役を演じた、19歳の時のデビュー作。", "1934-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピカレスク・人間失格", "ピカレスク・人間失格", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "作家・太宰治の半生を描いた伝記ドラマ。数々の傑作を世に送り出しながらも、幾多の女性と繰り返し心中事件を起こした太宰は、徐々に身も心も破滅へと向かっていく。主演はミュージシャン、音楽プロデューサー、俳優として幅広く活躍する河村隆一。自己中心的でナルシスティックな太宰に成り切っていてはまり役だ。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("剥き出しにっぽん", "剥き出しにっぽん", "", "", "http://image.eiga.k-img.com/images/movie/53634/315x210.jpg?2672", 91, "高校を卒業したばかりの小松太郎は自分の将来に不安を感じ、自給自足の生活をしようと思いつく。彼は密かに想いを寄せていた洋子を誘って畑付きの古い一軒屋に引っ越そうとするが、会社をリストラされた父親も強引について来てしまう。こうして3人の奇妙な共同生活がはじまり……。08年の「ばけもの模様」で国内外から注目を集めた石井裕也監督が05年に自主制作し、PFFアワード2007グランプリに輝いた長編第1作。", "2008-05-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Welfare", "福祉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 167, "", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京ソーダ水", "東京ソーダ水", "", "", "http://image.eiga.k-img.com/images/movie/53254/315x210.jpg?1378", 76, "変わりゆく大都市・東京に生きる8人の女性たちの日常を捉えたドキュメンタリー。芸能事務所を設立したタレント兼女社長、10年間務めた会社を辞めてミュージシャンを目指す女性、女優デビューを果たしたトップモデル、大手企業を退社して下北沢再開発反対運動に参加する女性など、8人の1カ月間を8人のユニット監督が撮影。再開発が進む東京の街で、自らもまた変わろうとする女性たちの姿をありのままに映し出す。", "2008-01-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("He's Just Not That into You", "そんな彼なら捨てちゃえば？ ", "", "", "http://image.eiga.k-img.com/images/movie/54384/315x210.jpg?3752", 130, "好きな相手から連絡がもらえないジジは、男心が理解できずに四苦八苦。その同僚ベスは、同棲7年目の恋人が結婚してくれないことに悩み、平穏な結婚生活を送っていたジャニーンも夫の浮気が発覚し……。恋愛に対する男女の本音を綴った同名小説を、「旅するジーンズと16歳の夏」のケン・クワピス監督が映画化した恋愛群像劇。ジェニファー・アニストン、ジェニファー・コネリー、スカーレット・ヨハンソンら豪華女優陣が出演。", "2009-08-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦場の夏休み／小学２年生の見たイラク魂", "戦場の夏休み／小学２年生の見たイラク魂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "戦争被災地の人々を撮り続ける吉田逸夫監督のドキュメンタリー。本作では小学生の娘を連れ、家族で2003年、夏のイラクを取材した。前作「笑うイラク魂・民の声を聞け」の登場人物10人に再度インタビュー。180度変化していた彼らの主張は興味深い。娘の目から見えるイラクの現状も織り交ぜ、吉田監督でしか撮りえない等身大のイラクを伝えている。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Driven", "ドリヴン", "", "", "http://image.eiga.k-img.com/images/movie/1374/315x210.jpg?4094", 117, "引退していた元トップ・レーサーのジョー（スタローン）は、旧友のチームオーナーの依頼で、次期チャンピオンの座を狙う新進ドライバー、ジミーのコーチを引き受ける。ジミーは、周囲からのプレッシャーとマネージャーである兄との関係に悩みながら、現チャンピオン、ボーの別れた恋人に惹かれていく。スタローンが事故死したアイルトン・セナに捧げて脚本を執筆。ジミー役は「タイタンズを忘れない」のキップ・パルデュー。", "2001-08-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふれあい", "ふれあい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "中村雅俊・壇ふみ主演の“ふれあいコンビ”１作目。将来漫画家になることを夢見ている大学生・山脇久は、学校にも通わず怠惰な日々を送っている。彼はあるきっかけからハンバーガー・ショップで働く女性と知り合い、お互い心が惹かれ合うが、二人は不幸なすれ違いに……。ホロ苦いラブ・ロマンスの佳作。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Shaggy Dog", "シャギー・ドッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "1959年に公開され大ヒットしたディズニー・クラシックス「ボクはむく犬」をリメイク。ひょんなことから犬に変身してしまった検事補デイブ。犬として家族と触れ合ううちに、デイブは忘れかけていた家族との絆を思い出していく。やがて彼は、動物研究所が企てた恐ろしい陰謀に自分が巻き込まれていたことを知る。主演は「ビッグ・トラブル」のティム・アレン。共演には「グッドナイト＆グッドラック」のロバート・ダウニー・Jrほか。", "2006-10-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜の牝 花のいのち", "夜の牝 花のいのち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ナイトクラブの世界をアクションを交えて描く“夜の牝”ものの第３作。主演は「河内カルメン」の野川由美子。バー・モンプチのホステス、京子は同僚の悦子の借金苦を救うために、ともに経営不振のバー・不知火に移り、再建を任されることになった。しかし、そこには気に入った女を食いものにしようとするオーナーの策略が潜んでいた。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hated", "全身ハードコア GGアリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "観客への暴力やステージでの排泄といった過激すぎるパフォーマンスで悪名を轟かせながらも、幅広い音楽性で高く評価されたパンクロッカー、GGアリンの生きざまをとらえたドキュメンタリー。狂気のライブ映像から薬物の過剰摂取による死に至るまで、GGアリンの波乱万丈な人生をあますところなく描きだす。「ハングオーバー！」シリーズで大成功を収めたトッド・フィリップス監督がニューヨーク時代に手がけた作品。", "2008-01-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("多羅尾伴内", "多羅尾伴内", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "片岡千恵蔵の当たり役である多羅尾伴内を、マイトガイ・小林旭が、二代目として演じる。超満員の観客で湧き返る東京K球場で、ホームランを打った選手が殺される事件が発生する……。小林旭の久々のアクションが見もの。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES CHEVALIERS DU CIEL", "ナイト・オブ・ザ・スカイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "フランスの人気コミックを、「TAXi」で世界的なヒットを飛ばしたジェラール・ピレス監督が実写映画化。映画史上初となるフランス空軍全面協力の下、国際的謀略に立ち向かう空軍とパイロットたちの活躍を活写する。本物の戦闘機にカメラを持ち込み撮影した、実写ならではの臨場感が圧巻!!　イギリス上空でフランスの戦闘機ミラージュ2000が突如失踪する。二人の優秀なパイロットが、この事態の収集に向かうのだが……。", "2006-02-18", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORN IN EAST L.A.", "ボーン・イン・イースト・L.A.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ブルース・スプリングスティーンの大ヒット・アルバム『ボーン・イン・ザ・USA』のパロディー・レコードをもとに作られた、人気コメディアン、チーチ＆チョンによる爆笑ムービー。メキシコ人と間違えられ、メキシコ送りになったアメリカ人が、あの手この手で国境を越えようと悪戦苦闘を繰り広げる。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本のいちばん長い日", "日本のいちばん長い日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 157, "原作は大宅壮一が、終戦当時の政治家や宮内庁関係者、元軍人、民間人から収録した実話を編集した同名の実録。これを岡本喜八が監督したオール男優スター・キャストの超大作。東宝創立35周年記念映画でもあり、またのちのいわゆる“８・15”ものの第１作となった。題名「日本のいちばん長い日」とは1945年８月14日の正午から翌15日正午の玉音放送に至るまでの、本作品のヤマ場として描かれる１日のことを指している。日本に無条件降伏を求める連合国のポツダム宣言が、海外放送で傍受された７月26日早朝を冒頭に、原子爆弾の投下、ソ連の参戦などの事件を背景に、軍部や政府内部の状況が、刻一刻と変化していく様子を克明に描いている。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANNA", "アンナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "交通事故で病院にかつぎ込まれてきた、かつての恋人アンドレアとの過去を回想する看護婦アンナ。アンドレアと彼女、そして彼女の愛人をめぐる醜い三角関係から、修道女になろうと誓ったアンナだったが、奇跡的に命をとりとめたアンドレアから、再びプロポーズされる……。S・マンガーノが、真摯に生きようとする一人の女を熱演したメロドラマ。", "1951-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コードギアス　亡国のアキト", "コードギアス　亡国のアキト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花吹雪鉄火纏", "花吹雪鉄火纏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "油がうなぎ登りに値上がりする天保改革前。そこに目をつけた油問屋の山崎屋は邪魔な油屋を次々と取り潰していった。その標的にされた武蔵屋も放火され、武蔵屋となじみの火消し、長次は事件の探索に乗り出す。長次は山崎屋の様々な妨害を受け、あげくに武蔵屋の主人も殺されてしまい……。すさまじいまでの油屋の抗争劇を、大川橋蔵主演で描いた。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALTERED STATES", "アルタード・ステーツ／未知への挑戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "生命の根源をトリップで探ろうと、若き精神心理学者は、強力なドラッグを服用し、硫酸マグネシウム溶液に浸った。が、細胞の記憶を遡るうち、なんと彼は類人猿に逆進化してしまう。迫真のトリップ描写で、“ドラッグ・ムービー”と話題を呼んだ異色SFサスペンス。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二人の世界", "二人の世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "映画監督・島津保次郎が主宰していたシナリオ塾の塾生・塚原靖と山形雄策が内輪で書いたオリジナル・ストーリーを、山形が脚色して映画化されたもの。技術革新のため研究室の拡充を求める若手技師と、それに反対する重役陣の間で、板ばさみとなって苦悩する技術部長の姿を描いた、島津としては珍しいジャンルの作品。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("迎春閣之風波", "迎春閣之風波", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "元王朝末期。圧政を強いる朝廷に抵抗する革命派の機密文書を巡る争いが、荒野の迎春閣を舞台に繰り広げられる密室アクション劇。豪華な出演者の顔ぶれに加えて民族色豊かな衣装、緻密な構成、多彩なアクションシーンとどれをとっても第一級の娯楽作品である。", "1973-01-01", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("時空警察ハイペリオン", "時空警察ハイペリオン", "", "", "http://image.eiga.k-img.com/images/movie/54614/315x210.jpg?4074", 120, "横浜の片隅で探偵業を営む尾光と助手の冬木。彼らの本当の姿は、22世紀からやって来た「時空警察ヴェッカー」の時空刑事オリオン、レナリーであった。ある日、瀬野という美女から「兄を探して欲しい」という依頼が入り、さっそく捜査を開始した2人だったが、22世紀のバイオテクノロジーで自らを改造した「時間犯罪者」のカルラが2人の前に立ちはだかる。", "2009-07-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLY　ME　TO　POLARIS", "星願・あなたにもういちど", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「少林サッカー」のC・チャンと台湾の人気歌手R・レン共演のファンタスティックなラブ・ストーリー。目も見えず口も利けないという二重苦の青年オニオンは、明るい性格で誰からも好かれる人気者だったが、不慮の事故で急死してしまう。天国の粋な計らいで、５日間だけ別人として地上に戻ることを許されたオニオンは、恋する看護婦オータムに会うため保険外交員と称して彼女の前に現れるが……。アジアの女性たちを涙させた感動作。オータムを演じるセシリア・チャンが歌った『星語心眼』は大ヒットし、香港電影金像奨最優秀主題歌賞を受賞。", "1999-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ADVENTURES OF BULLWHIP GRIFFIN", "黄金作戦 追いつ追われつ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "金鉱探しを夢みる青年とその執事との、波乱万丈の大冒険を描いたアドベンチャー・ストーリー。二人をしつこくつけ狙う悪党ヒギンズとの度重なる対決、そしてラストのハッピーエンドがいかにもディズニー映画らしい一編。執事役をR・マクドウォールが、そして悪党をK・マルデンが好演している。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THRILLING LOVE", "O夫人の背徳II", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ローマからカプリ島にやって来た若く美貌の人妻イングリット。彼女はカメラマンのミシェルと不倫をするが、夫のアルベルトは怒るどころか、それを覗き見しているのだった。男と女の複雑で不思議な関係をミステリー仕立てで描く官能映画。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Los Anos Desnudos／Clasificada S", "ヌード狂時代 S指定", "", "", "http://image.eiga.k-img.com/images/movie/54794/315x210.jpg?2842", 105, "「チル・アウト！」のドゥニア・アヤソ＆フェリックス・サブロソ監督・脚本によるスペインのエロティック・コメディ。独裁政権が終焉を迎え、ようやく表現の自由が認められるようになった1975年のスペイン。映画の検閲も廃止され、性的シーンや暴力表現のあるものは「S指定作品」として公開されるようになった。サンドラ、リナ、エバの3人は、それぞれ新時代の女優を目指して奮闘するが……。主演は「アモーレス・ペロス」のゴヤ・トレドほか。", "2009-09-18", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Night at the Museum", "ナイトミュージアム", "", "", "http://image.eiga.k-img.com/images/movie/1134/315x210.jpg?2616", 108, "バツイチ＆失業中だったラリーはニューヨークの自然史博物館で夜警の仕事を手に入れるが、そこは真夜中になると展示物たちが動き出す驚異のミュージアムだった。主演は「ドッジボール」等コメディー俳優として活躍するベン・スティラー。展示されたルーズベルト大統領にアカデミー賞俳優ロビン・ウィリアムズ。さらにスティラーの実母で伝説のコメディエンヌのアン・メアラ、スティラーの親友である人気俳優もノンクレジットで登場する。", "2007-03-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドドンパ娘、川を行く", "ドドンパ娘、川を行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ある夏の午後、バスに乗り遅れた少女は一案を考じて川を使い船で目的地に向かうことにした。そこで彼女が見たものは……。京都をその水源に持ち、大阪・大正区を流れる木津川を舞台に川から見た街、川から見た世界を描いている。撮影を宝塚映画出身でドキュメンタリーのベテラン、広内捷彦が担当している。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Freddie Mercury: The Untold Story", "フレディ・マーキュリー　人生と歌を愛した男", "", "", "http://image.eiga.k-img.com/images/movie/33704/315x210.jpg?2237", 112, "人気ロックバンドQUEENのボーカリスト、フレディ・マーキュリーの45年の生涯を描くドキュメンタリー。QUEENのブライアン・メイやロジャー・テイラー、フレディの親族、そして最後の恋人ジム・ハットンなど、フレディと親しかった人々への証言や未発表映像を交え、彼の生い立ちから最期までを振り返る。監督・製作は、10年以上に渡ってQUEENやフレディと仕事を共にしてきたルディ・ドレザルとハンス・ロサシェル。", "2006-10-21", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裸でだっこ", "裸でだっこ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "魅力的な女性と若者たちの、大金稼ぎ騒動を描いた青春コメディ。スナックで働くユミは、夢を追う恋人の武志ら常連客３人組に誘われ、もうけ話に乗る。美人局に始まり、インチキ賭場を開いてはユミの色気で客から大金を巻き上げるなど、計画は順調に進行。だが武志は次第に金に執着するようになり、ユミの心は彼から離れていく。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神鎗手", "スナイパー：", "", "", "http://image.eiga.k-img.com/images/movie/55304/315x210.jpg?1461", 86, "香港、台湾、中国の人気俳優が共演するアクション映画。主演のエディソン・チャンによる、2008年のプライベート写真流出事件の影響で公開が延期されていたが、2010年4月に日本公開となる。新人警察官のOJは、たまたま特殊任務部隊SDUの現場に居合わせ、犯人の射殺に成功する。OJはその度胸と腕を買われてSDUへ入隊し、射撃の腕をどんどん上げていく。一方、一般人を誤射した罪で服役していた元SDUのリン・ジンが、刑期を終えて出所してくる。", "2010-04-17", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOMADS", "ノーマッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "心理的なショック・シーンとミステリアスな映像美で批評家たちにショックを与えたサスペンス・スリラー。救急病院にかつぎ込まれた男を手当てした女医が、知らぬ間に危険なワナに落ちていく……。独特のテンポで心を揺さぶる不気味な音楽はB・コンティ。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUN CRAZY Episode３／ 叛逆者の狂詩曲〈ラプソディー〉", "GUN CRAZY Episode３／ 叛逆者の狂詩曲〈ラプソディー〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "話題のアイドル・タレントが女性刑事に扮し銃をぶっ放す、アクション・シリーズ第３作。キャリア刑事となった亜紀は、麻薬の密輸捜査をすることに。現場に向かった亜紀だったが、そこで体に爆弾を仕掛けられたおとり捜査官の姿を目撃し、恐怖のあまり逃げ出してしまう。捜査官は爆破され殉職してしまった。自責の念にかられた亜紀は、組織のボスを愛する麗華とともにテロ集団に挑んでいくが……。DVDは仲根かすみ、大谷みつほの２バージョンが発売されている。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高校三年生", "高校三年生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "富島健夫の小説に、大ヒット歌謡曲『高校三年生』をフィーチャーした青春映画。桜ヶ丘高校３年の小杉知子は、今も祖母の権力が絶大な、老舗の織物問屋の娘。その古い考え方についていけず、知子の姉は、恋人と家出してしまう。姉たちが、同級生・宏の家に下宿していることを知った知子は、やがて宏に好意を持つようになり……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛情麻辣／SPICEY　LOVE　SOUP", "スパイシー・ラブスープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "中国の俊悦、チャン・ヤン監督が、アジアン・ポップスにのせて語り明かすオムニバス形式のラブ・ストーリー。現代人の様々な恋の形を切り取ったみずみずしい世界がウケて、中国で大ヒットを記録した。録音マニアの少年や再婚相手の候補たちと麻雀を囲む老婦人などを主役にした５つのエピソードが展開し、結婚直前の若いカップルの物語を幕間に挿入。タイトルは、辛口とあっさり味の二つの味が楽しめる四川の鍋料理に由来している。", "1998-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOUNG AT HEART", "ヤング・アット・ハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "音楽一家として有名なタトル家。三姉妹の次女ローリーは、若手音楽家のアレックスと恋に落ちる。が、実は、妹のエミイもアレックスに夢中。しかも、アレックスが編曲の手伝いに招いたピアニストで編曲家のバーニーがローリーに恋をしたから、話はややこしくなる。バーニーの心中を知らないアレックスはローリーに求婚し、結婚式の日取りまで決定。しかし、エミイの気持ちを知ったローリーは身を引き、式当日、電報を送っただけで、バーニーと結婚してしまった……。F・シナトラとD・デイが、ガーシュインやコール・ポーターらの名曲を歌う洒落たミュージカル。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAX ET LES FERRAILLEURS", "はめる 狙われた獲物", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "R・シュナイダー、M・ピコリという「地獄の貴婦人」のコンビが主演する犯罪サスペンス。犯罪捜査の失敗にいらだち、犯人の現行犯逮捕に執念を燃やす男が、警察官であることを隠し、チンピラの旧友に近づく。彼は、友人に銀行強盗をするよう、巧妙な罠を仕掛ける。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWILIGHT FILE トワイライト・ファイル", "TWILIGHT FILE トワイライト・ファイル", "", "", "http://ecx.images-amazon.com/images/I/3185EJTKASL._SL210_.jpg", 116, "ラブミステリーをテーマに、気鋭の若手監督たちが競作するオムニバスシリーズ「TWILIGHT FILE」の第1弾。若手編集マンと映像の中から現れた不思議な女との交流を描く「影」、記憶を失ったホストと女性作家が繰りひろげるラブアクション「幻」、満月に支配された男女の切なくも美しい運命をつづった「月光」の全3話。出演は「魁!!クロマティ高校 THE★MOVIE」の金子昇、人気グラビアアイドルの森下千里、ベテラン俳優の車だん吉ほか。", "2005-10-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Seabiscuit", "シービスケット", "", "", "http://image.eiga.k-img.com/images/movie/1634/315x210.jpg?3826", 141, "大恐慌に喘ぐ1930年代のアメリカ。厩舎に見放された競走馬シービスケット、片目が不自由な騎手、時代に取り残された元カウボーイの調教師、息子を亡くした失意の馬主が出会い、それぞれのやり方で自分の傷を治していく。原作は、ローラ・ヒレンブランドのノンフィクション「シービスケット　あるアメリカ競走馬の伝説」。監督は「ビッグ」「デーヴ」などの脚本家を経て「カラー・オブ・ハート」で監督デビューしたゲイリー・ロス。", "2004-01-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THIRD PARTY RISK", "死のゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "ロンドンとスペインを舞台に、マイクロフィルムに隠された秘密をめぐって繰り広げられるサスペンス・ドラマ。全編に流れるフラメンコをバックに、美女との出会い、誘拐、そしてかつての戦友の死と、次々と起こるハプニングにほんろうされる主人公、フィリップをL・ブリッジスが好演。「ホット・ショット」（1991）では大ボケぶりが話題を呼んだが、ここではシリアスでしかも若々しい彼の演技に注目！", "1955-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBIN HOOD", "ロビン・フッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "12世紀のイギリスで、無法者の烙印を押されたロビン・フッドが、シャーウッドの森に兵とともにこもり、ノルマン人に戦いを挑んでいくさまを描くアドベンチャー・スペクタクル。過去何度も映画化され、この年もケヴィン・コスナー主演作と競作となったが、本作は時代考証を正確に再現した点が特徴的。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大日本ハッタリ伝", "大日本ハッタリ伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "花登筐の同名原作を本人自ら脚色（共同）したコメディ。長門裕之がまったく性格の異なる二人の男を軽妙に演じ分ける。青年社長の弘は、現在会社が倒産寸前の大ピンチで、フィアンセの敏子にも見放されて自殺を図るが、彼と瓜二つのハッタリ男・源吉に助けられる。源吉は弘に代わって社長になりすました。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PREMATURE BURIAL", "姦婦の生き埋葬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "コーマン監督の“ポー”シリーズ３作目。硬直症という持病の男。彼は生きたまま埋葬されるのでは、という恐怖におののいていたが、ある日それが現実となる……。完全犯罪を企む妻と、復讐に燃える男との陰惨な戦いを綴った、怪奇ファン必見の一編。ビデオ発売時タイトルは「早すぎた埋葬」。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("「さよなら」の女たち", "「さよなら」の女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「恋する女たち」「トットチャンネル」に続く大森＝斉藤コンビの第３作。大学卒業を控えてバイト先を解雇された安達郁子を待っていたのは、父が元グループサウンズのメンバーだったという驚くべき事実。しかも、ソロ歌手として再デビューするという。さらに、母までが“イルカの調教師になりたい”と言い出した。混乱する郁子は東京の友人を訪ねるが、彼女もまた自分の夢を追うべく宝塚へと奔走していた。跡を追う郁子は様々な人との出会いの中で自分自身を見つめ直していく……。劇中、父親が歌う『さよならの女たち』は、斉藤由貴が歌う主題歌と歌詞が微妙に違い、前者は大森監督、後者は斉藤由貴自身の作詞。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Revolutionary Road", "レボリューショナリー・ロード　燃え尽きるまで", "", "", "http://image.eiga.k-img.com/images/movie/53934/315x210.jpg?1691", 119, "「タイタニック」（97）のレオナルド・ディカプリオとケイト・ウィンスレットが11年ぶりに共演し、ある夫婦の夢や葛藤を描いた人間ドラマ。1950年代アメリカ・コネチカット。郊外の閑静な住宅街に暮らし、子供にも恵まれた理想の夫婦フランクとエイプリル。しかし、2人はマンネリ化する日々に不満を募らせて、次第に溝を深めていく……。監督は「アメリカン・ビューティー」のサム・メンデス。", "2009-01-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富江 replay", "富江 replay", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "菅野美穂が怪演を披露したホラー・ヒロインに、宝生舞が挑戦。一人の少女の体内から摘出され、首だけの状態から復活した怪物“富江”が、再び男たちをほんろうし、破滅へと追いやっていく。遠藤憲一、菅田俊ら個性派が画面を活気づかせる中、若き演技派、窪塚洋介がひときわ存在感を発揮。「オーディション」の山本英夫が見せる端正なカメラワークも見どころだ。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使がくれたもの", "天使がくれたもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "原作者・Chacoの実体験を描き、中高生に支持されたケータイ小説の映画化。高校1年生の日向舞（清水由紀）は、親友・美衣子（徳永えり）の紹介でカグこと香久山聖（鍵本輝）と知り合う。最初のうちは、まったくデリカシーのないガクとは顔を合わせればケンカばかりの舞だったが、不意にみせるガクの優しさに惹かれていく。ガクもまた舞に惹かれていたが、互いに傷つけあう2人。やがて、長い時間と数多くの失敗を経て、結ばれようとしていた2人に衝撃的な事件が起こった……。", "2007-09-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜半歌聲／THE PHANTOM LOVER", "夜半歌聲　逢いたくて、逢えなくて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「オペラ座の怪人」を下敷きにしたサスペンス・タッチのラブ・ストーリー。舞台俳優に扮するL・チャンの甘い歌声が、悲しくも美しい愛の物語を彩る。1930年代の北京。火事で荒れ果ててしまった劇場にやって来た劇団員ウェイチンは、そこで不思議な歌声を耳にする。気になって管理人に尋ねると、彼は重い口を開き始める。それは、激しく愛し合いながらも引き裂かれた、人気俳優と財閥の令嬢の身に降りかかった10年前の悲劇だった……。ビデオ発売時タイトルは「夜半歌聲　ファントム・ラヴァーズ」。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かあちゃんと11人の子ども", "かあちゃんと11人の子ども", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "西伊豆に住む農家の主婦が書いた体験談が原作。11人の子供を産み、夫とともに仕事に励んだ母親の子育てと労働の半生を明るくほのぼのと描く、文字どおりの大型ホームドラマ。昭和の時代をたくましく生き抜いた母親を、左幸子が好演。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("路傍の石", "路傍の石", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "山本有三の原作の２度目の映画化。勤勉な少年、吾一を描いたこの小説は、計４回映画化されている。明治33年、廃藩置県のあおりで土地を失った元氏族の家に生まれた吾一は、貧乏のため中学にあがれず、同級生の実家である呉服屋に丁稚に出される。だが、仕事の苦労にもめげず、彼は勉強を続ける。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЧЕЛОВЕК АМФИВИЯ", "両棲人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "A・ベリャーエフの児童向けの小説に基づき、旧ソ連のレンフィルムで製作されたSFファンタジー。結核に冒され、科学者の父親によって海でも生きられるようにとサメのエラを移植された青年。やがて半魚人となって港町の人々を騒がせる彼は、地元の娘との報われない恋に落ちていく。後に「ハムレット」「戦争と平和」などの文芸大作に連続出演するヒロイン役、A・ヴェルティンスカヤが美しい。", "1961-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CIRCUITRY MAN", "プラグヘッド 悪魔の電脳人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "近未来の地球を舞台に、冷酷無比な無敵の怪人サイバー・パンクと戦う美しい女用心棒と、セクシーな美形のジゴロ・アンドロイドとの愛を描くハイテク・アクション・アドベンチャー。コンピュータ・チップや人間の脳と自分の脳をプラグで接続して、記憶を探ったり快感を得たりするサディスティックな悪の化身プラグヘッドがおぞましい。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刺青　匂ひ月のごとく", "刺青　匂ひ月のごとく", "", "", "http://image.eiga.k-img.com/images/movie/54624/315x210.jpg?3525", 89, "谷崎潤一郎の名作小説を映画化する官能ロマン“刺青”の第2弾。亡き父母が経営していたダンススタジオを継いだ藤堂陽花は、パートナーの亮介と妹の葉月が惹かれあっていることを知る。絶望感に苛まれていた陽花の前に、謎の彫師・桐悟が現われる。嫉妬に狂った陽花の企みによって桐悟の元へと送り込まれた葉月は、次第に刺青の世界にのめりこんでいき……。主人公姉妹を「東京フレンズ／The Movie」の井村空美とさとう珠緒が体当たりで熱演。", "2009-06-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Standing Army", "誰も知らない基地のこと", "", "", "http://image.eiga.k-img.com/images/movie/57724/315x210.jpg?3191", 74, "世界各国で起こっている米軍基地問題の真実を追究したドキュメンタリー。2007年にイタリアで起こった米軍基地拡大への反対運動をきっかけに、ビチェンツァ（イタリア）、ディエゴ・ガルシア（インド洋）、普天間（沖縄）などで取材を敢行。基地の騒音や兵士が起こす事故などに悩まされる付近の住民や、専門家へのインタビューなどを通して、米軍基地問題の実態を明らかにしていく。", "2012-04-07", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents kiss!　「それを何と呼ぶ？」", "桃まつり presents kiss!　「それを何と呼ぶ？」", "", "", "http://image.eiga.k-img.com/images/movie/57914/315x210.jpg?3099", 29, "", "2009-03-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEPHEN KING'S THINNER", "痩せゆく男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "S・キングの『痩せゆく男』の映画化。人生のすべてにおいて成功を収めた弁護士のビリーのただ一つの弱点は、その旺盛な食欲だった。彼は四六時中食べていないと気が済まず、食欲の奴隷と化していた。ある夜、酔っ払い運転をしていたビリーは、誤ってジプシーの老女をひき殺してしまう。しかし、彼にとっては交通事故をもみ消すことくらいは簡単なことだった。ところが、ジプシーの長老レムキは彼を許さなかった。ビリーのところへやって来たレムキは、彼の顔をなでながら、“痩せてゆく”とつぶやいて立ち去る。次の日から、ビリーは痩せ始めていく。初めのうちは喜んでいたビリーだったが、どんどん痩せていき、食べても食べても痩せていくのだった。そして、事故をもみ消した警察署長が自殺する。やがて、ビリーは自分が痩せるのも署長の自殺の原因もレムキの呪いのためだと悟るのだったが……。ビデオ発売時タイトル、DVDタイトルは「スティーブン・キング　痩せゆく男」。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きっかけはYOU！", "きっかけはYOU！", "", "", "http://image.eiga.k-img.com/images/movie/56314/315x210.jpg?3480", 0, "「モーニング娘。」「Berryz工房」「スマイレージ」など人気アイドルが多数所属する「ハロー！プロジェクト」の研究生「ハロプロエッグ」の一員として活動してきた吉川友の初主演作。吉川が、本作の主題歌でもある「きっかけはYOU！」でソロデビューを果たすまでの軌跡を追いながら、新人アーティストを売り出すレコード会社の裏側を描く。劇場公開時には毎回、吉川本人が劇場に登場してラストシーンを生で演じ、上映後にライブを行う。", "2011-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Way of the Gun", "誘拐犯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "二人のアウトローが、大富豪夫妻が大金を支払って出産を依頼した代理母を誘拐した。しかし実はその富豪、裏社会の顔役でもあり、逆に彼らは命を狙われる羽目に……。「ユージュアル・サスペクツ」でアカデミー脚本賞を受賞したC・マックァリーの監督作。緻密に練られたプロットが素晴らしい、緊迫感あふれるクライム・アクション。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さくら、さくら　サムライ科学者　高峰譲吉の生涯", "さくら、さくら　サムライ科学者　高峰譲吉の生涯", "", "", "http://image.eiga.k-img.com/images/movie/55924/315x210.jpg?2646", 133, "止血剤アドレナリンや消化剤タカジアスターゼの開発に成功し、世界の医学界に革命を起こした化学者・高峰譲吉の半生を描いた伝記ドラマ。1883年、イギリス留学から帰国した譲吉は、家業の医院を継がずに化学者になることを決意する。翌年、ニューオーリンズを訪れた譲吉は、12歳年下のアメリカ人女性キャロラインと運命的な出会いを果たす。夫婦となった2人を波乱だらけの毎日が待ち受けていたが、ともに困難を乗り越え、数々の奇跡を起こしていく。", "2011-03-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PROUD ONES", "誇り高き男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "頭に負った傷のためにしばしば失明状態になる保安官と、彼を父の仇と信じ込んでいる若者が、やがて協力しあって悪を一掃するウエスタン。名優R・ライアンの男の魅力がとくと堪能できる。公開当時、L・ニューマンの主題曲も大ヒットした。撮影は名手L・バラード。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PETULIA", "華やかな情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "退屈なパーティーで、中年医師アーチーは奔放な若妻ペトリアと出会い、彼女の誘うままに狂おしい情事が始まる。だが彼女の夫のデビッドは、何不自由ないはずの妻の不倫を理解できず、暴力をふるう。また妻も、優しいはずの夫の暴力の意味がわからない。そしてアーチーがペトリアに本気で夢中になるころ、突然、彼女は夫のもとに帰っていった。時が過ぎ、ペトリアは出産のために再びアーチーを訪れる。とまどうアーチーは、陣痛のさなかに自分の名を呼ぶペトリアを見つめるばかりだ……。R・レスター監督が描いた、異色の女性映画。３人の男女の行動のズレを通じ“愛”とは何かを問う意欲作。", "1968-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STALKING LAURA", "ストーカー 異常性愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "一方的な好意や愛情から、相手に付きまとい、プライバシーを干渉する“ストーカー”。本作品は、ストーキングが社会問題化するきっかけになった実話を、サスペンス映画として再現、その異常さ、恐ろしさを余すとこなく描き出している。ローラはハイテク企業の新入社員。入社の初日、同僚のリチャードからの交際の申し込みを何気なく断わってしまう。しかし、リチャードはローラに異常な執着をみせる。思い余って、ローラは上司に相談し、リチャードは解雇される。しかし、事件はこれで終わりではなかった……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Paper", "ザ・ペーパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "新聞社を舞台にしたアクティブな人間ドラマ。ニューヨーク、午前７時。地方新聞『サン』の編集局次長ヘンリーは自宅で目を覚ます。今日も大手新聞社への移籍問題、間近に迫った妻の出産、いけ好かない女上司との確執など、問題は山積み状態。そんな折、明日の一面を飾る殺人事件の真犯人をめぐって、ある疑惑が彼の脳裏をよぎる。刻々と迫る締切り。ついに、真相を探り当てた時、新聞社の輪転機はまわり始めていた。ピート・ハミルなど本物のジャーナリストが実名で登場し、雰囲気作りに協力している。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女体", "女体", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "優しい夫と一人息子に恵まれて、郊外で平和な生活を送る主婦・管マヤは、ある日、昔の仲間と出会い、暗い過去に引き戻される。彼女は終戦直後の荒廃した東京で売春婦として暮らし、ボルネオ・マヤと呼ばれていたのだった……。田村泰次郎の『肉体の門』『埴女の女』を恩地日出夫が脚色、監督した女性映画。パンパンと呼ばれた当時の娼婦の生き方を描く。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANTITRUST", "サベイランス／監視", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "コンピュータ業界内部の熾烈な闘いを描いたデジタル・サスペンス・スリラー。物語は、巨大ソフトウェア企業ナーブ社の開発チームにスカウトされた若き天才プログラマーのマロイが、驚くべきスピードで完璧なソフトを次々と生み出していく社長ゲーリーに疑問を抱きはじめ……。T・ロビンス演じるナーブ社の参謀の中心人物ゲーリーが、実在するマイクロソフト社のビル・ゲイツ氏に酷似していると話題を呼んだ。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAZZ ON A SUMMER’S DAY", "真夏の夜のジャズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "1958年のニューポート・ジャズ・フェスティバルの模様と、周囲のスケッチとを巧みに織り交ぜた音楽記録映画の傑作。ジミー・ジュフリーの「トレイン＆リバー」、T・モンクの「ブルー・モンク」などのモダン・ナンバーからブルースやディキシーランド、ゴスペルまで盛りだくさん。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RECOIL", "アベンチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ロス警察の刑事モーガンが銀行強盗一味の主犯を射殺。だが、死んだ男は犯罪組織のボス、スローンの息子で、怒ったスローンはモーガンや同僚たちの命を狙う。やがて妻子が犠牲となった時、復讐を決意したモーガンは単身スローンの邸宅に乗り込む……。113台の車をクラッシュさせ、１万３千発の空砲を使用したノンストップ・アクション。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("瀧の白糸", "瀧の白糸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "水芸人・瀧の白糸は、北陸地方の巡業で苦学生・村越と出会い、仕送りして法律を学ばせる。白糸は苦労しながらも仕送りを続け、村越は立派に任官する日を迎えるが……。泉鏡花の原作をもとに野淵監督は運命に流される女の姿を冷めた視点で捉えている。何よりも宮川一夫のカメラが秀逸だ。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ST.LOUIS BLUES", "セントルイス・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "黒人牧師の息子ウィリアムは音楽好きだったが、なかなか父に音楽の道に進むことを許してもらえない。成人したウィリアムはナイトクラブに雇われ、やがてメキメキとその才能を伸ばしていく……。黒人ブルース作曲家のウィリアム・C・ハンディの半生を映画化した作品で、当時、人気絶頂だったN・K・コールが主演している。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Brink's Job", "ブリンクス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "　1940年代、刑務所帰りの金庫破りトニーはアメリカ一と謳われた金融警備会社ブリンクスの集金現場を目撃。のちに彼は準備を進め、1950年に仲間とともに現金強奪計画を実行、まんまと270万ドルを手に入れる。しかしFBIの捜査が始まり、一味は次々と逮捕されていくことに……。実際にあった空前の現金強奪事件を時代色豊かに映画化。ピーター・フォークを始めとする個性派キャストが好演し、泥棒たちの生き様がリアルに描かれる。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Babette's Feast", "バベットの晩餐会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "20世紀のデンマークを代表する女流作家カレン・ブリクセンの同名小説を映画化した心温まる群像劇。19世紀後半、デンマーク辺境の小さな漁村に質素な生活を送る初老を迎えたプロテスタントの姉妹がいた。そこにパリ・コミューンで家族を失ったフランス人女性バベットがやってくる。その後彼女は家政婦として長年姉妹に仕えるが、宝くじで大金を手にいれると、村人のために晩餐会を開きたいと申し出る。87年度のアカデミー賞で外国語映画賞を受賞した。", "1989-12-31", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("証人の椅子", "証人の椅子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "徳島で実際に起こったラジオ商殺し事件を題材とした作品で、山本薩夫が演出にあたっている。裁判費用の負担に耐えかねた洋子は無実の罪を仕方なく受け入れ、刑に服した。弁護士らの努力で真犯人らしき人物が浮かび上がるが、検察の妨害にあい特定できない。検察・警察権力による犯人デッチ上げの構造を暴く力作である。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　LIFE　OF　EMILE　ZOLA", "ゾラの生涯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "フランスの文豪エミール・ゾラの人生を描いた伝記映画。不遇の時代を経て作家として成功するゾラ。折りしも、ユダヤ人であるという理由だけで、機密漏洩の罪を着せられたドレフュス大尉が流刑となった。軍は真犯人を突き止めたにもかかわらず、責任の追及を恐れて、真相を揉み消そうとしていた。夫の無実を信じる夫人から依頼を受けて、この有名な“ドレフュス事件”に異を唱えたゾラは、中傷罪で軍に訴えられ、反逆者の烙印を押されてしまう……。しっかりとした構成と、ストーリー展開でアカデミー賞３部門を制覇。伝記劇を得意としていたW・ディターレ監督が腕を振るっている。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STROMBOLI，TERRA DI DIO", "ストロンボリ 神の土地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ネオ・レアリスモ映画の傑作「無防備都市」を見て感動したバーグマンが、イタリアでロッセリーニとのコンビで製作した第１作。第二次大戦で祖国を追われたカリンの苦悩を描く。シチリアの北に位置する島、ストロンボリにオール・ロケし、火山が噴火するクライマックスの映像は力強い。DVDタイトルは「ストロンボリ」。", "1950-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LOVE PARADE", "ラヴ・パレイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "映像と音楽を大胆に組み合わせた“シネ・オペレッタ”と呼ばれたジャンルのさきがけとして名高い傑作。ヨーロッパのとある王国を舞台に、年ごろの女王とプレイボーイの恋模様がコミカルに描かれる。セリフの続きが歌になっていくという、当時としては型破りな演出が観客を熱狂させ、B・ワイルダーや小津安二郎ら内外の映画人にも多大な影響を与えたといわれる。", "1929-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("揮発性の女", "揮発性の女", "", "", "http://ecx.images-amazon.com/images/I/312BZ93XARL._SL210_.jpg", 80, "「アンテナ」の鬼才・熊切和嘉監督が、「完全なる飼育」の石井苗子主演で描く官能ドラマ。寂れたガソリンスタンドを1人で経営する未亡人・悦子は、ある日、原付で乗り付けた若い男に包丁を突きつけられ、現金を奪われてしまう。警察への通報は諦めた悦子だったが、その日の夕方、逃走に失敗した犯人・理一が戻ってきて……。6人の気鋭監督がエロスをテーマに競作した「ラブコレクションシリーズ」の1作。", "2004-11-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAR TROUBLE", "大痙攣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「アパッチ」でハリウッド・デビューしたD・グリーン監督のコメディ。主演は「炎のランナー」「ガンジー」など重厚なイメージが強いI・チャールソン。主人公ジェラルドは、長年の夢がかなって真っ赤なジャガーEタイプを手に入れる。わが子のようにジャガーをかわいがるジェラルドだが、唯一の心配は妻のジャクリーヌ。案の定、ドジな彼女は大事な車に若い男を連れ込み、あげくの果てに車を真っ二つにしてしまう。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FALLING IN LOVE", "恋におちて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "マンハッタンを舞台に、ふとした偶然から知り合った男女が互いに家庭を持つ身ながら次第に恋に落ちていく……という古典的なストーリーを、演技派デ・ニーロとストリープの共演でしっとりと描く。同名のTVドラマが製作されるなど、日本でも好評を博したラブ・ロマンス。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋愛小説", "恋愛小説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "デビュー作「Laundry〈ランドリー〉」が高い評価を受けた森淳一監督が、直木賞作家・金城一紀の同名小説を映像化。自らを“死神”と呼ぶ青年の経験した最初にして最後の愛にまつわる悲しい真実が明かされる。小西真奈美、玉木宏、平山あやなど成長著しい若手俳優の競演にも注目したい。法学部に通う大学４年生の宏行は、恋人の美和の浮気が原因でケンカ別れしていた。ある日、宏行は同じ大学に通う聡史から、彼の遺言書作りを依頼される。宏行は、自分の数奇な運命を語り始める聡史の話に耳を傾けるが……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("流線の彼方", "流線の彼方", "", "", "http://image.eiga.k-img.com/images/movie/55094/315x210.jpg?4784", 115, "1960年代、日本のモータリゼーションの発展途上期において、自社の技術力を国内外にアピールすべく、ヤマハ発動機とトヨタ自動車が協同開発したハンドメイドの高性能スポーツカー、トヨタ2000GT。67年から70年までの間に、わずか337台しか販売されず、「007は二度死ぬ」ではボンドカーにもなった、伝説のスポーツカーの軌跡を追うドキュメンタリー。", "2010-01-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON'T STOP!", "DON'T STOP!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラムネ", "ラムネ", "", "", "http://image.eiga.k-img.com/images/movie/55514/315x210.jpg?2038", 0, "エイベックスが同社の新人俳優やアーティストを起用し製作する青春映画シリーズ「ニュースター・シネマ・コレクション」の第2弾。高校生活最後の夏、美大への進学をあきらめきれない陽介は、最後のチャンスとなる女子の人物画のモデルを探していた。ある日、夜間部の教室で出会った少女ルミにモデルを志願され、陽介は絵を描いていくうちに、自由奔放なルミにひかれていく。「真夏のオリオン」の篠原哲雄監督がメガホンをとり、人気グループ「AAA」（トリプルエー）の與真司郎が初主演を務める。", "2010-05-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HALLOWEEN　H２０：２０　YEARS　LATER", "ハロウィンH20", "", "", "http://ecx.images-amazon.com/images/I/219914D87CL._SL210_.jpg", 86, "１＆２作目に主演したJ・L・カーティスがヒロインに復活したシリーズ第７作。肉親を惨殺された惨劇から20年、ローリーは名を変え、全寮制高校の校長としてひっそりと暮らしていた。ハロウィンの夜、彼女は胸騒ぎを覚え、パーティーに行くという17歳の息子を止めるが、聞く耳を持たない。その夜、殺人鬼が再び姿を現わし……。", "2002-05-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLIRT", "フラート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ニューヨーク、ベルリン、東京を舞台に、３人の主人公たちによって繰り広げられる３つの物語で、登場人物たちはほとんど同じセリフを話すというユニークな作品。1993年ニューヨーク。ビルは、元の恋人のいるパリへ３ヵ月間帰ることになり、半年間付き合ってきた恋人・エミリーは、“私たちに未来はあるか、イエスかノーか”と問う。ビルは答えるのを引き伸ばし、以前の浮気相手の家に行くが……。1994年ベルリン。ゲイの美術商ヨハンは年下のアメリカ人青年ドワイトと暮らしているが、仕事のため３ヵ月ほどニューヨークに行くことになる。出発前にヨハンはドワイトに二人の関係について迫るが、ドワイトは“90分後に戻る”と言ってその場を立ち去ってしまう……。1995年東京。ミホは６ヵ月前からアメリカ人映画監督のハルとつき合っていたが、ハルが３ヵ月アメリカに帰ることになる。ハルはミホに“二人に将来はあるか”と問うが、ミホは答えられず……。", "1995-01-01", "1,7,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AN INCONVENIENT WOMAN", "都合のわるい女～ジェラシー・ゲーム～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "実業家の老紳士ジュールズと夫人ポーリンは、ポーリンの連れ子の不良息子が原因で結婚生活に行き詰まっていた。そんな折、ジュールズはウェイトレスのフローと出会い愛人契約を結んだ。フローの若い肉体に溺れていくジュールズは、彼女にすべてを与える。フローはだんだんと洗練された女に変身していった。ところが、ある殺人事件をきっかけにフローの周りに怪しい影が……。R・デモーネイはこの作品の出演により「ゆりかごを揺らす手」の悪女役を手に入れた。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Blind Side", "しあわせの隠れ場所", "", "", "http://image.eiga.k-img.com/images/movie/55114/315x210.jpg?3874", 126, "全米アメリカンフットボール・リーグNFLのマイケル・オアー選手の激動の半生を追ったマイケル・ルイスのノンフィクション「ブラインド・サイド　アメフトがもたらした奇蹟」を映画化。米南部ミシシッピのスラム街に生まれ、ホームレスのような生活を送っていた黒人青年マイケルが、裕福な白人女性リー・アンの一家に家族として迎え入れられ、アメフット選手としての才能を開花させていく姿を描く。監督は「オールド・ルーキー」のジョン・リー・ハンコック。主演のサンドラ・ブロックは、第82回アカデミー賞で主演女優賞を受賞した。", "2010-02-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILL PENNY", "ウィル・ペニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "西部劇＝無敵のガン・ファイター、という従来のイメージを一掃。盛りを過ぎた一人のカウボーイの人生の悲哀を、静かで清らかな恋を織り交ぜて描出。ウエスタン映画史に大きな影響を与えた味わい深い人間ドラマだ。幼いころから、だれの手も借りずたった一人で生きてきた生粋のカウボーイ、ウィル・ペニー。そんな彼も、そろそろ年を感じはじめた。仲間二人を連れた旅の途中で、ひょんなことから、ならず者のクイシト一家の争いに巻き込まれてしまう。以来、ウィルを執拗につけ狙うクイシト一家。再三の襲撃で、ついに重傷を負わされたウィルを、献身的に看護するキャサリンに、彼は今まで味わったことのない愛と安らぎを覚えるが……。気が荒く無骨な男たちの世界。一匹狼同士の熱い友情、そして生涯安住することなく荒野を漂流し続ける宿命を背負った流れ者の哀愁を、C・ヘストンが、渋味の効いた男っぽさを漂わせて好演している。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD OF WINTER", "冬の嵐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "実力派の女優M・スティーンバーゲンが３役をこなし、ヒッチコックばりのサスペンスが展開する、A・ペン監督のミステリー。大金持ちの女性が左手薬指を切断され殺された事件と、代役で舞台に立つ女優を襲う恐怖が描かれる。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ARME´E DES OMBRES", "影の軍隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "第二次大戦のさなかに出版された、J・ケッセルの抵抗運動を描いた小説の映画化。ナチス占領下のフランス。対独抵抗運動のため密告されてゲシュタポに捕まった学者のジェルビエは、処刑寸前に脱走してマルセイユのレジスタンス・グループにかくまってもらい、彼らと一緒に裏切り者を始末する。それから彼はパリに潜入。パリではジャン・フランソワや女闘士のマチルドが活動を続けており、ジェルビエはジャン・フランソワの兄の学者ジャルディからロンドンのド・ゴールと合流するように命令される……。戦時下のフランスでレジスタンスとして闘って死んだ人々の姿を簡潔なスタイルで描いた名作。", "1969-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEEP COVER", "ディープ・カバー 潜入捜査", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "少年時代にジャンキーの父親が目の前で死んで以来、ドラッグと酒はやらないと誓ったラッセル。捜査官となった彼は国際的麻薬組織への潜入捜査を命じられる。妊婦や子供にまで薬を売り、大物の売人となっていくラッセル。やがて彼は任務を忘れ、悪の道にのめりこんでいくが……。渋い脇役俳優B・デュークが監督したスタイリッシュなハードボイルド。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING DAVID", "キング・ダビデ 愛と闘いの伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "紀元前1000年、イスラエル初代の王サウルの時代。予言者サムエルに選ばれたダビデはペルシア軍の巨人ゴリアテを倒し、成長して武将となる。サウル王の娘をはじめ５人の妻と結婚、イスラエル王となるがその道は険しかった。洗練された都会的イメージの強いR・ギアが、聖書の中の人物像に挑んだ歴史ドラマ。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHARLIE CHAN AND THE CURSE OF DEAGON QUEEN", "オリエンタル探偵殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "サンフランシスコで次々と起こる謎の連続殺人事件。尊敬する名探偵の祖父チャーリーとともに究明に乗り出したリーは、ある女性を犯人として割り出す。だが、その女性は何とチャーリーが20年前に解決した殺人事件の犯人の愛人だったのだ。「ナイル殺人事件」などでおなじみのP・ユスチノフ主演のミステリー。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Four for Texas", "テキサスの四人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "　銀行の用心棒ザックとジョーが乗り合わせていた駅馬車が強盗団の襲撃を受ける。馬車には大金が積まれていたが、その金をジョーがどさくさに紛れて横取りし、逃亡。その後、ザックはジョーに会うことに。しかし悪党の銃が彼らを狙っていた……。陰謀と闘い、そして男たちの奇妙な友情を描いたコミカルなウエスタン・アクション。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mayu ココロの星", "Mayu ココロの星", "", "", "http://image.eiga.k-img.com/images/movie/34214/315x210.jpg?1497", 123, "21歳の若さでで乳がんに冒された女性の心の成長を、実話を基に描いた感動作。札幌の広告会社に務める竹中まゆは、仲間にも恋人にも恵まれ充実した毎日を送っていた。そんなある日、彼女は自分の胸にしこりがあることに気づく。医者から乳がんの宣告を受けた彼女は、家族や周囲の人々に助けられながら闘病生活を開始する。希望を捨てずに病に立ち向かうヒロインを、「バックダンサーズ！」など女優としても活躍する人気タレントの平山あやが熱演。", "2007-09-29", "76", 0, NOW(), NOW());
