INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　X−FILES", "Xファイル ザ・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "FBIの特別セクション、X−ファイル課。常識外の不可思議な未解決事件を再検討、再調査するこの課に身を置く捜査官モルダーとスカリー。1993年のスタート以来、この二人の活躍を描き続け、全米だけでなく日本でも熱狂的なファンを獲得したTVシリーズの映画版。政府の陰謀を追うというTVシリーズの基本を押さえながらも、物語は映画らしく大きな膨らみを見せていく。テキサス州北部の小さな町で起きた少年と３人の消防隊員の変死事件と、一週間後にダラスで起きた連邦ビル爆破事件。モルダーとスカリーはこの二つの事件を追ううちに、政府の隠された秘密に迫ることに。そしてスカリーを刺した一匹のハチを手掛かりに、二人は南極大陸にまで捜査の手を伸ばしていく……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEST OF THE BEST３ NO TURNING BACK", "蘇える狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "マーシャルアーツのスゴ腕P・リーの初監督、主演によるアクション。韓国系青年トミー・リーが、妹一家を訪ねてとある田舎町にやって来る。だが、そこでは白人優位主義を唱える宗教集団CACが土地買収を狙っていた。やがて、武装したCACによる事件が続発。妹一家や妹の友人である気丈な女性教師マーゴも危険にさらされ、トミーの怒りは爆発。たった独りで凶悪な宗教集団に立ち向かう。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Welcome to Sarajevo", "ウェルカム・トゥ・サラエボ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "イギリスのジャーナリストが自らの体験を綴ったノンフィクション小説を、マイケル・ウィンターボトム監督が映画化。戦火のサラエボを訪れた記者マイケルは、そこでミエラという孤児に出会う。彼はサラエボの悲惨な状況を必死で取材するが、本国イギリスの人々にとっては所詮海の向こうの他人事。そして彼は、ミエラを救う為にある決断をするのだった。殺戮が繰り返されるサラエボの惨状を、実際のニュース映像を織り交ぜながら描いた傑作。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リアル鬼ごっこ", "リアル鬼ごっこ", "", "", "http://image.eiga.k-img.com/images/movie/53284/315x210.jpg?1983", 98, "独創的な設定が話題を呼び、累計100万部以上を売り上げた山田悠介の同名小説を映像化したサスペンス・ホラー。日本全国の“佐藤さん”が相次いで死亡するという事態が発生する。その原因は、国王が独裁政治を敷くもうひとつの日本にあった。そこでは国王の命令により、“佐藤”姓を持つ人々が命がけの鬼ごっこに強制参加させられていたのだ。彼らは1日のうち数時間、国王が放った鬼に捕まらないよう逃げなければならず……。", "2008-02-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Maltese Falcon", "マルタの鷹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "　私立探偵サム・スペードはワンダリーという女性から仕事を依頼される。サムの相棒アーチャーがそれを引き受けるが、その後、彼は何者かによって殺害されてしまう。犯人を追うサムの前に現れる怪しい男たち。やがてサムは莫大な価値を秘めた彫像“マルタの鷹”の争奪戦に巻き込まれていく。ハードボイルド探偵小説の映画化であり、ジョン・ヒューストンの初監督作品。ボギーことハンフリー・ボガートの当たり役にもなった傑作ミステリー。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("森の石松鬼より恐い", "森の石松鬼より恐い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "マキノ雅広監督による1940年の「続清水港」を、リメイクした作品。新進舞台演出家の石井は、『森の石松』の舞台稽古でアイデアが決まらず、泥酔してしまう。彼が目覚めると石松になっていて、清水の次郎長から金比羅代参を命じられる。しかし、この旅は石松にとって死出の旅。困った石松（石井）は、死なないように筋書きを変えようとするが……。マキノ版では、広沢虎造の浪花節が効果的に挿入されていたが、こちらの沢島版では、錦之助の陽性のキャラクターをうまく使っている。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE JOHNSON", "ザンガディクス 鮮血の悪夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "美少女エマリーは毎晩、ある建物の中で７人の少年が人を惨殺するという悪夢を見ていた。その頃ケラー教授は、アマゾン奥地のマクシチュ族を撮影した古いフィルムを発見する。マクシチュ族には“ザンガディクス”という神の伝説があり、それは神の７人の息子が妹を妊娠させてできた子供が世界に永遠の暗黒を与えるというものだった。そしてケラーは、ある精神病院に７つ子が監禁されていることを知る。７つ子は孤児院で16人の子供、病院で医師を殺していた。一方、エマリーは14歳の誕生日に、ひきつけられるように、ある建物の前に出る。それは夢に見た建物だった。そして、狂気の７つ子と母娘の壮絶な闘いが始まるのだった。親子愛の強さをスリリングに描いたファンタジー・ホラー。アヴォリアッツ国際ファンタスティック映画祭グランプリ受賞。", "1991-01-01", "1,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WE LIVE AGAIN", "復活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "文豪トルストイ原作の『復活』の映画化。公爵ネフリュードフとカチューシャの、あまりにも有名な悲恋の物語が綴られる大河ロマン。東欧とソ連の激動の、歴史を知るうえでも興味深い作品。様々な人間たちの生き方を通じて、人間と文学の素晴らしさを伝えてくれる。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("祝祭", "祝祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「風の丘を越えて　西便制〈ソピョンジェ〉」のイム・グォンテク監督が、自国の葬式の風習を捉えた人間ドラマ。母の訃報を知った作家ジュンソプが、家族を連れて久しぶりに帰郷。一族は葬儀の準備で大忙し。やがて一族の嫌われ者である派手好きの姪ヨンスンが現れ、一家の間に波紋を投げかける。そんな葛藤の一方で、３日間にわたって行われる葬儀は、次第に盛り上がりを増し……。宴会の酔っ払いたちや、弔い金を賭けてギャンブルに乗り出す人々など、葬式に集まった人間像が実にユーモラス。にぎやかに死者を送り出す、その模様を通して、大らかな民族性が浮かび上がる。", "1996-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Saboteur", "逃走迷路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "", "1979-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRACULA'S DAUGHTER", "女ドラキュラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "ドラキュラ伯爵直系の実の娘に焦点を当てている“吸血鬼もの”。吸血鬼としての宿命に悩みながらも、やはり血を求めてしまうサガをおどろおどろしく描いていく。ドラキュラの娘である伯爵夫人はその血統を断ちたいがために、イギリスへ渡る。父の遺体を警察から盗んで焼いたが、知らず知らずのうちに夜な夜な獲物を求めてうろつき、驚くべき結末へ。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACCUMULATOR 1", "アキュムレーター1", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "TVに吸い取られたエネルギーを取り戻すために虚構の世界と闘う奇想天外なファンタジー。気が弱くて心優しい測地学者のオルドは、TVの前で眠り続けたまま原因不明の“エネルギー喪失症”に見舞われ、瀕死の状態に陥る。自然学者フィサレクに救われたオルドは、彼と一緒に病気の正体を探るうちに、TVのブラウン管の向こうに虚構の世界が存在し、その世界の人々が現実社会のエネルギーを吸収していたことを突き止める。美人歯科医のアナに恋したオルドは、エネルギーを取り戻すため虚構の世界と闘う決心をするが……。ブリュッセル映画祭でグランプリを受賞したほか、各国の映画祭で様々な賞を受賞した異色作だ。", "1994-01-01", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SISTERHOOD", "エンジェルコマンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "最終戦争後の荒廃しきった地球。人々は小さな村を作り細々と暮らしていた。横暴な支配者の搾取に敢然と立ち向かうマイア、ベラ、ナディアの３人のエンジェルコマンドの大活躍が展開。B級娯楽アクションを多く手掛けているロジャー・コーマン門下のS・H・サンチャゴ監督作。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Red Heat", "レッドブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "バイオレンス・アクションの鬼才W・ヒルと“鋼鉄の男”A・シュワルツェネッガーが初めてコンビを組んだ痛快ポリス・アクション。米ソにまたがる麻薬密売組織を追ってアメリカにやって来たモスクワ警察のすご腕刑事が、現地のはみ出し刑事と協力して組織を追い詰める。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WIND", "風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "スウェーデンの鬼才、シェーストレムが、名花L・ギッシュを主演に据えてハリウッドで撮った無声映画。愛なき結婚をした美しい娘が、夫が留守のある夜、激しい砂嵐の中で貞操を奪われそうになる。恐怖のあまり、女は相手の男を銃で撃ってしまうが……。無声ながら、猛烈な“風”に音を感じさせる名演出が見もの。", "1928-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIDEODROME", "ビデオドローム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "カナダ、トロントの、暴力やポルノを売り物にしているテレビ局の社長が見つけ出した、禁断のビデオ“ビデオドローム”。拷問と殺人が果てしなく繰り返されるそのビデオを見続けると、幻覚が起こり、やがてその人間の体までも変貌させてしまうという、現代文明が生み出した悪夢のような作品である。特殊メイクの第一人者であるリック・ベイカーによる幻覚シーンが話題となった。女陰のようにパックリ割れた腹に銃を突っ込んだり、腕と銃が一体化したり、モニターへの侵入などなど目を見張るシーンが続く。テクノと内臓感覚の見事な結合によって生まれた新しい恐怖である。", "1983-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い月", "赤い月", "", "", "http://ecx.images-amazon.com/images/I/21P49BBF2GL._SL210_.jpg", 111, "なかにし礼の実母をモデルとした同名小説を映画化。第二次大戦下の満州に渡り、激動の時代を生き抜いた一人の女性の半生を描く。邦画初の中国東北部ロケを敢行した壮大な映像が、ヒロインの情熱的な姿をドラマティックに映し出す。男を魅了する女としての艶やかさ、我が子を守ろうとする母としてのたくましさを体当たりの演技で表現した常盤貴子の熱演が光る。1934年に夫とともに満州に渡った波子は、造り酒屋を築き成功を収める。二人の子供に恵まれながらも、彼女は夫以外の男性との恋も楽しんでいた。だが、1945年にソ連が満州に侵攻、波子の栄光の日々は一瞬にして崩れ去っていく。", "2004-02-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WRAPPED　IN　GREEN", "緑色包囲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "高度経済成長を遂げた中国で深刻な問題になっている環境問題について、北京電影学院出身の新鋭ジャン・クーミン監督が、自身の長編第１作ドキュメンタリーとして撮り上げた意欲作。中国河南省の電池の町・新郷市に暮らす、乾電池の回収に情熱を傾ける一人の中年女性を追う。家族の反対や周囲の軋轢も顧みずに、ひたすら回収を続ける彼女の行動は感動的だ。中国河南省で、乾電池の卸売りをしている女主人タ・ケイエイさんは、ある時“使用済み電池が環境に悪影響を及ぼす”という新聞記事を目にする。環境汚染について関心を強く持った彼女は、たった一人で古電池の回収を始める。", "2002-01-01", "8,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OLIVER’S STORY", "続 ある愛の詩", "", "", "http://ecx.images-amazon.com/images/I/5135aMvmUiL._SL210_.jpg", 90, "前作から一年半後の設定で、残されたオリヴァーのその後が描かれる。仕事に打ち込むことで失ったジェニーを忘れようとするオリヴァー。魅力的なマーシーと心を通わせるが、無意識のうちにジェニーと比べていることを悟り、マーシーと別れて父の事業を継ぐ……。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE BLEIERNE ZEIT", "鉛の時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "1977年に過激派メンバーとして逮捕され、獄中死したグードルーン・エンスリンと、その姉で雑誌記者のクリスチーネの二人をモデルに製作された、実話に基づく心理ドラマ。ユリアンネの妹マリアンネはテロリストとなり、やがて獄死してしまう。その死因に疑問を持ったユリアンネは牢獄を何度も訪れるうち、次第に妹の生き方に理解を示し始める。", "1981-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jazz '34", "ロバート・アルトマンのジャズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "　30年代のジャズクラブを再現した映画「カンザス・シティ」。そこでの演奏を撮影したライブ・セッション・フィルム。現代有数のミュージシャンが参加している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I　EKPOMBI", "放送", "", "", "http://ecx.images-amazon.com/images/I/418N4AJ45PL._SL210_.jpg", 0, "シンセサイザー奏者ヴァンゲリスらを主演に据えた音楽映画が製作者との対立で未完に終わった後、アンゲロプロス監督が撮り上げた渾身の短編映画。後に手掛けた作品とは違い、リズム感あふれるハイ・テンションかつシャープな映像の連続で、若かりし監督の才気がほとばしる異色の一編。ロック・ミュージックをサウンドトラックとして大胆に導入するなど時代性を感じさせる演出スタイルが光る。", "1968-01-01", "27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カラスコライダー", "カラスコライダー", "", "", "http://image.eiga.k-img.com/images/movie/54604/315x210.jpg?4058", 0, "「少林老女」「デメキング」などの奇才・寺内康太郎が、楽天野球団の非公認マスコット「Mr.カラスコ」を題材にしたコメディ。寺内監督（村上淳）のもとに「Mr.カラスコ」を主人公にした映画の企画が舞い込んでくる。寺内は相棒の脚本家・佐上（渋川清彦）とともに楽天イーグルスに関連する人々へインタビューを敢行し、謎の覆面男「Mr.カラスコ」の情報を集め始めるが……。", "2009-11-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STARS AND BARS", "イングリッシュマン in ニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "イギリスの名門大出のヘンダースンは、アメリカン・ドリームを抱いたニューヨークのアート・オークション会社の新入社員。ところが、絵の買い付けで出張したアトランタで、絵の持ち主一家に散々振りまわされ……。D・デイ・ルイスがコメディに挑戦し、それまでと違った役柄に個性を発揮。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関東テキヤ一家 浅草の代紋", "関東テキヤ一家 浅草の代紋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "３年前にイザコザを起こして東京から離れていた浅草菊水一家の国分勝。久しぶりに戻ってくると、一家は赤岩興業と対立してゴタゴタが絶えず、菊水一家の三代目に奮起をうながす帳元の関東若義組組長若林は、その裏で赤岩と手を結んでいるのだった。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デジモンアドベンチャー3D デジモングランプリ", "デジモンアドベンチャー3D デジモングランプリ", "", "", "http://image.eiga.k-img.com/images/movie/54804/315x210.jpg?2827", 0, "デジタルモンスター初の3Dアニメーション・シリーズで、2000年7月よりサンリオピューロランドにて2年間上映された作品。ポッドレーサーに乗ったデジモンたちが、白熱のレースバトルを繰り広げる。短編3Dアニメーションを集めた「とびだす！3D東映アニメ祭り」で上映。", "2009-10-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHITTY CHITTY BANG BANG", "チキ・チキ・バン・バン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "飛行機のように空を飛び、ボートのように海を走る驚異のポンコツ車“チキ・チキ・バン・バン”号が活躍するファンタジー・ミュージカル。「007」シリーズで知られるフレミングの童話を、「メリー・ポピンズ」のシャーマン兄弟がミュージカル化。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Girl, Interrupted", "17歳のカルテ", "", "", "http://image.eiga.k-img.com/images/movie/1404/315x210.jpg?3613", 127, "17歳のスザンナはアスピリンを大量に飲んで自殺を図り、親の勧めで精神科に入院する。医師の診断は“境界性人格障害”。情緒不安定で著しい衝動性を持つ精神病だった。同じ病棟の患者は、顔に火傷の痕があるポリー、虚言症のジョージーナ、大食症のデイジー、そしてリサ。常に監視され管理される入院生活の中で絶望感を感じていたスザンナは、病棟のリーダー格であるエキセントリックなリサに惹かれ次第に仲良くなっていく。アンジェリーナ・ジョリーは本作で第72回アカデミー助演女優賞を受賞。", "2000-09-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FOX AND THE HOUND", "きつねと猟犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "幼い頃に仲良しだったきつねと猟犬の友情と勇気の物語を描くディズニー・アニメ。母親を猟犬に殺され、何とか生き延びた子ぎつねは、農家に暮らすトゥード未亡人の元でトッドと名付けられて大切に育てられた。トッドは猟犬の子供コッパーと仲良くなるが、やがて成長した二匹は、森の中で対立することになり……。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若社長大奮戦", "若社長大奮戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "一流ガラス会社の御曹司・丹下次郎は大学の剣道部のキャプテン。ある日、雷社長の異名をとる父が倒れ、彼がピンチヒッターを務めることに。得意先が画策する合併話、外国製品のデザインを盗用した製品の採用など、問題は山積状態。のちにシリーズ化もされた“若社長”ものの一編。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("希望の国", "希望の国", "", "", "http://image.eiga.k-img.com/images/movie/57744/315x210.jpg?2135", 0, "「恋の罪」「ヒミズ」の園子温監督が、大地震で離れ離れになりながらも、それぞれの愛を貫く3組の男女の姿をオリジナル脚本で描く。酪農家の小野泰彦は、妻や息子夫婦と平和でつつましい日々を送っていた。一方、隣家の息子は家業を手伝わずに恋人と遊んでばかり。そんなある日、大地震が発生し一帯の住民は避難を強いられるが、泰彦らは長く住み着いた家を離れることができない。そんな中、息子の妻いずみが妊娠していることが発覚する。主人公・泰彦を夏八木勲が演じ、その妻役に大谷直子。息子夫婦を村上淳と神楽坂恵、隣家の息子とその恋人を清水優、梶原ひかりが演じる。", "2012-10-31", "76,3,7,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CYBORG COPII", "サイボーグコップ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "特別捜査官ジャックを、弟の仇と狙う海兵隊軍曹のジェスは、反テロ組織に誘拐され、サイボーグに改造される。殺人機械と化したジェスは組織を脱走、ジャックに復讐するためサイボーグ軍団を結集。人間対機械の壮絶な死闘が始まる……。ド派手な爆発シーンの連続が売りものの、近未来SFアクション第２作。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OFFRET", "サクリファイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 149, "「惑星ソラリス」や「ノスタルジア」などで知られる名匠タルコフスキーの遺作。スウェーデン南部の平和な島ゴトランド島。誕生日に松の木を植える主人公アレクサンデルの一日を描く。静かな毎日を送る彼の団らんのひと時が、地鳴りのような核ミサイルの響きで断たれる。全面核戦争の勃発。白夜の中でアレクサンデルは誓う——平和が戻り、愛する人々が救われるのであれば、神よ我が身を捧げると……。わずか54歳の若さでこの世を去ったタルコフスキー独特の“水”のイメージを重視した画面構成は、この作品でも十二分に発揮され、見事な終章となっている。", "1986-01-01", "2,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARTIN ROUMAGNAC", "狂恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "プライベートでも親交があった二大スター、ディートリッヒとギャバンの唯一の共演作。前半の恋愛シーンから後半に向かうにつれ、スリリングな展開を見せるサスペンス映画の王道編。美貌の未亡人と建築作業員の頭領がボクシング観戦で知り合い、やがて恋に落ちた。だが、未亡人の過去に嫉妬した頭領は、思わず彼女を殺してしまい逮捕され裁判に。余韻あふれるどんでん返しも見もの。", "1946-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版ムーミン　パペット・アニメーション　ムーミン谷の夏まつり", "劇場版ムーミン　パペット・アニメーション　ムーミン谷の夏まつり", "", "", "http://image.eiga.k-img.com/images/movie/54664/315x210.jpg?3376", 71, "2003年に劇場公開されて話題を呼んだ短編TVシリーズ「ムーミン　パペット・アニメーション」を、最新技術による美しい映像と再編集したストーリーで新たに蘇えらせた長編パペット・アニメーション。トーベ・ヤンソンによる原作小説「ムーミン谷の夏まつり」をもとに、ムーミン谷を襲った洪水によって離れ離れになってしまったムーミン一家と仲間たちが巻き起こす騒動を描く。小泉今日子がナレーションを務める。", "2009-08-01", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渚のシンドバッド", "渚のシンドバッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "PFF出身の橋口亮輔監督の「二十歳の微熱」に続く第２作で、現代の高校生たちの奔放な愛と友情を描く。17歳の伊藤修司は、同じブラスバンド部の吉田浩之にほのかな想いを寄せている。３ヵ月前に転校してきた果沙音は、そんな伊藤の気持ちに気が付き何かと彼に干渉し、けむたがられていたが、やがて伊藤と二人の間には奇妙な友情が生まれる。伊藤は吉田に想いを告げるが、吉田は受け入れることができない。そして、吉田は果沙音に好意を持ち言い寄るのだったが……。東宝とぴあが提携し、日本映画を担う若い才能の発掘と育成を目的に作られたニューレーベル“YES”の第１回作品。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHUCK BERRY HAIL！ HAIL！ ROCK’N’ROLL", "チャック・ベリー／　ヘイル・ヘイル・ロックンロール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ロックンロールの王様、C・ベリーのショー・ビジネスに徹したロック人生を綴った、音楽ドキュメンタリーの佳作。ジョン・レノンのコメント“C・ベリーはロックンロールの代名詞だ！”でスタート。次いで、リトル・リチャード、ボ・ディドリー、J・L・ルイス、ブルース・スプリングスティーン、エリック・クラプトンら名だたるロック・アーティストやチャックの家族、30年来の秘書といった面々の、チャックにまつわる様々な思い出話やエピソードが語られる。メインは、1986年10月18日に開催されたチャックの60歳バースデイ・コンサート。それに向けての練習風景も見どころの一つ。音楽プロデュースを買って出たストーンズのK・リチャーズは、わがままで勝手なチャックに不満顔。それでもグッと我慢してチャックを立てるシーンに、思わずニンマリしてしまう。本番では、ジュリアン・レノンやリンダ・ロンシュタット、エタ・ジェームズらがゲスト出演。『メイベリーン』『スクール・デイ』ほか19曲収録。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eagle Eye", "イーグル・アイ", "", "", "http://image.eiga.k-img.com/images/movie/53744/315x210.jpg?2834", 118, "スティーブン・スピルバーグ製作総指揮の下、「ディスタービア」の主演シャイア・ラブーフ＆D・J・カルーソ監督が再タッグを組んだサスペンス・アクション大作。コピーショップで働く青年ジェリーと法律事務所に勤めるレイチェルの元に、謎の女アリアから突然電話が入る。次々と下される指令に訳もわからず従うハメになったジェリーとレイチェルは、やがて引き合わされ行動を共にするように。そんな彼らをFBIが執拗に追い……。", "2008-10-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PCU", "PCU", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ポート・チェスター大学（PCU）の学生寮ピットは、ひとクセもふたクセもある連中が好き勝手に生活するハチャメチャな寮。高校生のトムも、大学見学でピットの住人たちに出会いド肝を抜かれてしまう。ところが学長とお坊っちゃま学生、ランドがこの寮の取りつぶしを画策したことから大変な騒ぎに。悪ふざけの代償として7000ドルを請求された寮生たちは、資金稼ぎのために大パーティーを決行するのだが……。ファンクの帝王ジョージ・クリントンが寮生主催のパーティーでライヴを披露。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le pacte des loups", "ジェヴォーダンの獣", "", "", "http://image.eiga.k-img.com/images/movie/1494/315x210.jpg?2249", 138, "18世紀フランスの伝説「ジェヴォーダンの獣」は、3年余にわたり100人以上を殺戮してルイ15世を悩ませた、今も正体不明の獣。この正体の解明に、サム・ライミを敬愛するフランス人監督クリストフ・ガンズが挑む。“獣”は「ラビリンス／魔王の迷宮」のジム・ヘンソン・クリーチャー・ショップが担当。編集は「狼たちの挽歌」のデビッド・ウー。「ドーベルマン」のバンサン・カッセル、「マレーナ」のモニカ・ベルッチらが出演。", "2002-02-02", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("助太刀屋助六", "助太刀屋助六", "", "", "http://image.eiga.k-img.com/images/movie/1674/315x210.jpg?4117", 88, "監督歴43年、77歳の岡本喜八監督、第40作は、自身が別名で書いた時代小説「助太刀屋」の映画化。音楽は「ジャズ大名」の山下洋輔。5歳で母と死に別れて孤児になった助六は、故郷、上州を出て江戸に行き、ひょんなことから、仇討ちの手助けをする助太刀屋が商売に。それから7年後、故郷に帰ってきた彼は、幼なじみたちと再会するが、敵討ちの助太刀を断られる。そして偶然出会った仇の方を助太刀することになるが、彼は実の父親だった。", "2002-02-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前温泉", "喜劇・駅前温泉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "奥地の温泉場にデラックス・ホテルが建って以来、駅前温泉はさびれるばかり。誠意がモットーの観光協会理事長と現代的なセンスこそが必要とゆずらない孫作。孫作は次期理事長を狙っているようだ。さて駅前温泉の復興なるか。理事長には森繁、孫作には伴が扮する。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALICE", "アリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "人生の節目を迎えて心惑う主婦が経験する様々な非凡な体験を、マンハッタンを舞台に描くアレンの快作。裕福な生活を送る、主婦のアリス。ある日、友人の紹介で訪れたドクターにもらった薬で彼女は……。富や罪、不倫、家族や愛といったテーマを、アレン得意のコミカルな要素とドラマティックな要素を織り交ぜながら展開していく。M・ファローが見事な演技を見せる。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ULTIMO PARADISO", "最後の楽園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "タヒチ島やボラ・ボラ島など美しい島々にめぐまれた南太平洋。その島の一つ一つに珍しい、そして感動的な風習がある。命を懸けた成人の儀式。海を恐れる少年に自然の厳しさと尊さを説く父親。都会に憧れて島を出る若者。島民の生の営みを美しい映像で綴った４部構成のドキュメンタリー。", "1957-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スタッフ", "スタッフ", "", "", "http://image.eiga.k-img.com/images/movie/54494/315x210.jpg?3907", 67, "「トリコロール」3部作などで知られるポーランドの巨匠クシシュトフ・キエシロフスキーが1975年に発表した自伝的作品。映画 演劇技術学校で学び、衣装係の仕立屋として働くことになった青年ロメクの理想と厳しい現実を描く。マンハイム国際映画祭でグランプリに輝くなど国内外の映画祭で高い評価を受け、キエシロフスキーの出世作となった。出演はアンジェイ・ワイダ監督作「大理石の男」のミハウ・タルコフスキほか。", "2009-06-20", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST MATCH", "怒りのタッチダウン 人質奪回作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "麻薬所持の罪で投獄された娘を、プロ・フットボーラーの父が仲間の協力を得て救出するというアクションもの。何者かにバッグに入れられた麻薬が原因で逮捕されたスーザン。父クリフはすぐに駆けつけるが、裁判は一方的で、弁護士もあきらめろと言わんばかり。だが、失意のクリフの前に、フットボール・チームのコーチが仲間の選手を集めて現れる。", "1990-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CODE46", "CODE46", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "M・ウィンターボトム監督が上海、ドバイなどのアジア各地で撮影を行ったSF映画。管理化が進んだ近未来の上海で滞在許可証を偽造する若い女性マリアと、その犯罪を探る調査員ウィリアムが許されぬ恋に落ちていく。記憶の喪失、 遺伝子テクノロジーといったSFらしいモチーフを採り上げつつ、主役二人の痛ましい愛の行方が情感豊かに描かれる。", "2003-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Game", "ゲーム", "", "", "http://ecx.images-amazon.com/images/I/51GDRKY8Z5L._SL210_.jpg", 128, "投資家として成功したものの、離婚後、孤独な毎日を送っていたニコラスは48歳の誕生日に弟コンラッドに再会し、CRSという会社のゲームの招待状を受け取る。ニコラスは好奇心からそれに参加するが、その時から奇妙で不可解な事件が続発。命まで危機にさらし、そのあげく財産や邸宅まで奪われることに……。「セブン」に続いてデビッド・フィンチャー監督が放ったサスペンス・スリラー。不条理な恐怖と謎の連続が観る者を不安へ陥れる。", "1998-01-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRIME TASK FORCE", "クライム・フォース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ベトナムの戦友を麻薬で失った男が、亡き友の敵を討つべく組織に挑むバイオレンス・アクション。ベトナムのジャングルでともに戦ったリバティとジェシー。リバティは犯罪者更生指導の仕事をし、一方ジェシーはベトナムの悪夢から逃れるため、麻薬に溺れる日々を送っていた。やがてジェシーがリバティの目の前で麻薬がらみのトラブルに巻き込まれ、殺されてしまう……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Günesi Gördüm", "私は太陽を見た", "", "", "http://image.eiga.k-img.com/images/movie/54984/315x210.jpg?4618", 117, "内戦で故郷を追われたクルド難民一家の受難の日々を壮大なスケールで描いたトルコ発の大河ドラマ。トルコ南東部のクルド人居住区で幸せに暮らしてきたアルトゥン家の人々。長引く戦火によって難民となった彼らは、故郷から遠く離れた大都市イスタンブールへの移住を余儀なくされ、さらにノルウェーへと向かう。監督・脚本・主演・音楽を手掛けたのは、トルコでミュージシャンとして活躍するマフスン・クルムズギュル。", "2009-10-18", "32", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神様のパズル", "神様のパズル", "", "", "http://image.eiga.k-img.com/images/movie/53334/315x210.jpg?2236", 134, "双子の弟に代わって大学の素粒子物理学ゼミに出席することになった基一は、教授から不登校の天才少女サラカを連れてくるよう言い渡される。仕方なくサラカの自宅を訪れた彼は、彼女に「宇宙を作ることはできるのか？」という究極の疑問を投げかけてしまう。その疑問に興味を持ったサラカは翌日ゼミに現われ……。機本伸司の同名小説を三池崇史監督のメガホンで実写映画化。主演は「ぼくたちと駐在さんの700日戦争」の市原隼人と「魍魎の匣」の谷村美月。", "2008-06-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・西の王将 東の大将", "続・西の王将 東の大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ライバル意識を燃やしてハッスルする商事会社の同期の桜、ウマ野郎とアンパン野郎。専務に男気を買われた彼らはともに係長に昇進、大阪に転勤して今度は美人芸者を相手にしのぎを削るのだが……。コンビも快調の“王将・大将”ものの第２作。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE CUP OF COFFEE", "ワン・カップ・オブ・コーヒー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "３週間だけメジャーに在籍していたことがある、41歳のプロ野球選手ロイ。マイナー・リーグの弱小球団でメジャーへの復帰を夢見る彼が、17歳の黒人投手の才能に惚れ込み、自分の技術のすべてを教えることに。一杯のコーヒーを味わう間もなく桧舞台から消えた男と、前途有望な少年との交流をしみじみと描く。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER REKORD", "ゴースト・ウィーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ビデオショップを経営するリコは、海賊TV局を作る資金を集めようと、マラソンビデオ・ウォッチングに挑戦する。ところが極度の過労のため、テレビ電波を直接受信する異常体質となってしまう……。SFXを使わず白黒画面で鋭く迫るファナティック・ホラー。", "1985-01-01", "7,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MODERN PROBLEMS", "スーパー念力マン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "トラックからこぼれた放射性廃棄物を浴びたために、テレキネシス（念力）の超能力を持った航空管制官マックスが巻き起こす奇想天外な痛快喜劇。マックスのセクシーな恋人ダーシーに、自信過剰の作家や舞台プロデューサーが大接近。マックスの嫉妬心が燃えあがって恐るべき念力が……。人気コメディアン、C・チェイスをはじめ、ドラマを盛り上げるのは多彩な喜劇役者たち。ビデオ発売時タイトルは「マイ・ダーリン マイ・エスパー」。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月より帰る", "月より帰る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「櫻の園」で映画界の多くの脚本賞を受賞した、じんのひろあきの監督デビュー作。舞台は2039年、スペースコロニーでの生活のデータ収集を目的に作られた無人の地下都市・蘭宿。ある日この街に、月帰りの男・影郎がやって来て人生相談を始めた。するとどこからともなく人々が集まり、争いが起こり始めるのだった……。ウィーピング・ハープ・セノオ（妹尾隆一郎）のハーモニカの音が全編に響きわたるチープなSF映画。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("奇妙なサーカス", "奇妙なサーカス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「夢の中へ」の園子温監督による野心的ファンタジー。実の父親に抱かれる小学生の娘、彼女に嫉妬して夫とのセックスにのめりこむ母親。二人の“女”がやがて一人の“女”になっていく様を、特異な構成で描き出す。近親相姦という過激なテーマを扱いながらも、社会派メッセージに堕することなく、己の道を突き進む内容は同監督ならではのもの。小学生、美津子は、父親、剛三に犯されていた。その姿を目撃した母、小百合は嫉妬に狂い、やがて娘を虐待するように。そんな母を自分の心の中に“宿す”ようになる美津子だが……。", "2005-12-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Prometheus", "プロメテウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「エイリアン」「ブレードランナー」の巨匠リドリー・スコットが自身初の3Dで描くSF大作。地球上の古代遺跡で人類の起源にかかわる重大な手がかりを発見した科学者チームが、その謎を解明するため宇宙船プロメテウス号に乗り、未知の惑星を訪れる。しかし、そこには人類が決して触れてはならない、驚きの真実が眠っていた……。出演はノオミ・ラパス、マイケル・ファスベンダー、シャーリーズ・セロン、ガイ・ピアースら。脚本を「LOST」のデイモン・リンデロフが手がけた。", "2012-08-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lord of the Rings: The Return of the King", "ロード・オブ・ザ・リング　王の帰還", "", "", "http://image.eiga.k-img.com/images/movie/1804/315x210.jpg?2607", 203, "超大作ファンタジー「ロード・オブ・ザ・リング」シリーズ3部作の完結編。邪悪な力を持つ指輪を滅びの山に捨てるべく旅を続けるフロドとサムだったが、同行するゴラムの策略にはまり仲を裂かれてしまう。一方、冥王サウロン軍が総力を結集して人間の国ゴンドールに襲いかかり、ガンダルフやアラゴルンら旅の仲間たちがこれに立ち向かう。第76回アカデミー賞で作品賞をはじめ11部門を総なめにし、「ベン・ハー」「タイタニック」に並ぶ史上最多受賞の快挙を成し遂げた。", "2004-02-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL GRANDE SILENZIO", "殺しが静かにやって来る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "戦後イタリア娯楽映画の土台を支えたコルブッチ監督のマカロニ・ウエスタンの異色作。悪徳判事と彼に操られるロコをボスとする殺し屋集団に支配されている辺境の町、スノーヒルに、かつて判事たちに両親を殺されたサイレンスが復讐のためにやって来た……。C・ルルーシュ監督の「男と女」で当時人気を博していたJ・L・トランティニャンが、口の利けないヒーロー、サイレンス役を演じている。スタイリッシュな決闘シーンや、強烈な残酷描写、斬新なストーリー構成が話題を呼んだ。", "1968-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太の宇宙漂流記", "ドラえもん のび太の宇宙漂流記", "", "", "http://ecx.images-amazon.com/images/I/31F1T4QZMYL._SL210_.jpg", 93, "1980年から始まった“ドラえもん”シリーズの20周年記念作品。ドラえもんとおなじみの仲間たちが宇宙で大冒険を繰り広げる。ゲーム箱の中の宇宙空間で遊んでいたジャイアンとスネ夫が行方不明になる。二人が箱ごとUFOにさらわれたことを知ったドラえもん、のび太、しずかは宇宙救命ボートで追いかけ、UFOに潜り込む。なんとか再会できた５人だったが、銀河少年騎士団に捕らわれ、宇宙生物に襲われてしまう。早く地球に戻りたい５人は“どこでもドア”を使うが、ドアが作動しない……。", "1999-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍術・猿飛佐助", "忍術・猿飛佐助", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "徳川方のいろは城に潜入し、昼は腰ぬけ侍、夜は忍術で情報収集に大活躍の猿飛佐助。だが酒と女にはめっぽう弱く、敵の“くノ一”だとも知らず、奥女中に惚れてしまい……。昔懐しい講談の世界を、現代的なユーモアで描いた時代劇。コミカルな役どころを財津が好演。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勝負犬", "勝負犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "昼は競艇の予想屋、夜は流しをして生活している拳銃マニアの大介は、ある不思議な殺人事件に出くわす。それと前後して、アメリカ帰りの貿易商中沢と知り合った大介は、その裏に殺人事件に絡んだ陰謀をかぎつけるのだが……。人気絶頂期の田宮二郎を主演に快調のリズムで描いた佳作。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOLDIERS OF INNOCENCE", "リトル・ソルジャー　戦場の天使たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "朝鮮戦争を背景に、アメリカ兵と12人の戦争孤児の心の交流と悲惨な戦争を描いたドラマ。米軍兵士、コリンズは部隊からはぐれ、ある教会へ身を隠す。そこにはカソリックの尼僧と12人の子供たちが住んでいた。だが教会は、敵陣の真っ只中。コリンズは子供たちに銃の扱いを教え、反撃を始める……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEUX HOMMES DANS MANHATTAN", "マンハッタンの二人の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "物語は、ニューヨーク国連本部の本会議に出席しているはずのフランス代表ベルティエがいないことを不審に思ったジャーナリストが、カメラマンの協力を得てその行方を追うことから始まる。レジスタンスの勇敢な闘士だったベルティエは、愛人である女優の部屋で急死していたのだ。スキャンダルを隠そうとするジャーナリストと、それで大金を得ようとするカメラマン、二人の男たちの思惑がマンハッタンの夜にうごめく……。ニューヨークの夜の雰囲気が鮮やかに捉えられ、モダン・ジャズや、酒、煙草の紫煙に満ちた闇のけだるさを存分に味わうことができる。「賭博師ボブ」がパリへのラブレターなら、本作品はニューヨークへのそれであると、メルヴィル自身も語っている。結末に見られるメルヴィルのレジスタンスへの敬意と思い入れにも深い味わいがある。", "1958-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vanishing On 7th Street", "リセット", "", "", "http://image.eiga.k-img.com/images/movie/55934/315x210.jpg?2486", 91, "「マシニスト」のブラッド・アンダーソン監督が、ヘイデン・クリステンセンを主演に描くSFスリラー。ある晩、突然の停電とともに地球上の人々がナゾの消失を遂げた。電力が復旧しないまま、やみに包まれた夜の時間だけがどんどん長くなっていく。発電機によりかろうじて明かりの灯ったバーに、偶然にも消失を免れた4人の男女がたどり着く。彼らはやみの恐怖に怯えながらも、人類消失のナゾに迫る。", "2011-02-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("痴漢白書 劇場版　笑顔でサヨナラ", "痴漢白書 劇場版　笑顔でサヨナラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "オリジナル・ビデオで人気を呼んだシリーズの第７作で初の劇場版。当時、グラビア、写真集で活躍中だったおはらひろみ、青沼ちあさ、川上流風が過激な演技を見せる。満員電車の中、祐二はつい隣の女に手を出すが、彼女は逆に手を握り返してきた。しかし、その女、由希はホームから投身自殺をしてしまう。その事件をきっかけに祐二は葉子という女と知り合い、彼女と由希、その周囲の人たちの複雑な人間関係を知る。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵コナン　紺碧の棺（ジョリー・ロジャー）", "名探偵コナン　紺碧の棺（ジョリー・ロジャー）", "", "", "http://image.eiga.k-img.com/images/movie/33934/315x210.jpg?3146", 107, "青山剛昌による原作コミックが少年サンデー史上最長の連載600回を突破し、テレビアニメも大人気の「名探偵コナン」劇場版第11弾。古代遺跡“海底宮殿”と女海賊が残した財宝の伝説が語り継がれる神海島へバカンスに訪れたコナンたち。彼らは宝を狙うトレジャーハンターの一行と遭遇するが、そのうちの1人が突然サメの群れに襲われて死亡してしまう。ハンターの死に事件性を感じたコナンは、灰原と共に捜査を開始する。", "2007-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子ぎつねヘレン", "子ぎつねヘレン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "写真家・エッセイストとして活躍する竹田津実のベストセラー『子ぎつねヘレンがのこしたもの』を基にした感動のドラマ。北海道の大地を舞台に、少年とキタキツネの交流が温かいまなざしで描かれる。目と耳が不自由でヘレン・ケラーのようであることから“ヘレン”と名づけられた子ぎつね。愛情に包まれ、懸命に生きようとするその姿を通して、生きることの意味や命の尊さを語りかける。キタキツネの愛らしい“好演”に加えて、北国の春の美しく雄大な風景や、演技派俳優たちの好演も見もの。東京から北海道に引っ越してきた８歳の太一は、元気のない子ぎつねを見つける。このきつねは目と耳が不自由だった。寂しさをつのらせていた太一は、そのきつねにヘレンと名づけ、愛情を注ぎ懸命に育てるが……。", "2006-03-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Limits of Control", "リミッツ・オブ・コントロール", "", "", "http://image.eiga.k-img.com/images/movie/53844/315x210.jpg?2207", 115, "「デッドマン」「ブロークン・フラワーズ」の鬼才ジム・ジャームッシュが、「花様年華」など独特なカメラワークで知られるクリストファー・ドイルを迎えて描く、孤独な男のロードムービー。主演はジャームッシュ作品常連のイザック・ド・バンコレ、共演にはティルダ・スウィントン、ビル・マーレイ、工藤夕貴ら多国籍な豪華キャストが集結。「自分こそ偉大だと思う男を墓場に送れ」という依頼主の言葉だけを頼りに、殺し屋の男はスペインへと向かう。男は行く先々でコードネームを持った仲間たちからマッチ箱に入った暗号を受け取り、依頼主の標的を探すが……。", "2009-09-19", "9,1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Romeo and Juliet/Romeo e Giulietta", "ロミオとジュリエット", "", "", "http://ecx.images-amazon.com/images/I/512TNoN%2BWTL._SL210_.jpg", 138, "シェイクスピアの傑作戯曲をイタリアのフランコ・ゼフィレッリ監督が映画化。15世紀中頃、イタリア北部の町ベローナ。2大名門として知られるモンタギュー家とキャピュレット家が血で血を洗う抗争をする最中、両家の子息ロミオとジュリエットは舞踏会で出会い、恋に落ちる。2人はお互いの素性を知り落胆しつつも、燃え上がった心を抑えきれず結婚式を挙げるが……。主演にオリビア・ハッセーとレナード・ホワイティング。音楽はニーノ・ロータ。", "1968-12-31", "3,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRETA：THE MAD BUTCHER", "女体拷問人 グレタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "“ナチ女収容所”シリーズで有名になったD・ソーン。彼女が再びその肉体のすべてをさらけ出して挑んだ、女囚残酷シリーズの第２作。目を覆いたくなる残酷シーンの連続にプラスして、ハードなお色気も画面からむせ返る。以前、劇場公開時にはカットされた衝撃場面も加えたノーカット版がビデオで発売されたこともある。", "1977-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅の翼", "紅の翼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "この作品で出演24作目を数え、押しも押されもしない大スターになった石原裕次郎が主演した航空アクション。劇中の６割以上が空の上、しかも狭いセスナ機の中という設定にもかかわらず、少しも飽きさせることなくドラマが進む。二谷英明演じる冷血な殺人犯に銃を突きつけられながらも、持ち前の明るさと度胸で危機を切り抜ける熱血漢の主人公を裕次郎が好演している。民間航空のパイロット・石田に、八丈島の子供が破傷風にかかり至急血清を送れという知らせが来た。セスナ機には紳士と女性記者が同乗。途中で男が殺人犯とわかり、セスナ機はやむなく不時着するが、子供へのヒューマニズムに燃える石田は殺人犯を倒し、傷つきながらも八丈島へ……。少年が生きるか死ぬかというタイムリミットをうまく生かし、その後多くの亜流の作品を生んだ航空サスペンスの傑作。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'annulaire", "薬指の標本", "", "", "http://image.eiga.k-img.com/images/movie/33654/315x210.jpg?4588", 100, "「博士の愛した数式」などの作品で知られる芥川賞作家・小川洋子による同名小説を、フランスの女流監督ディアーヌ・ベルトランが映画化。勤め先の工場で、作業中に薬指の先を切断してしまった21歳のイリス。事故をきっかけに仕事を辞めて港町へ引っ越した彼女は、森の中に佇む不思議な雰囲気の建物で標本技術士の助手として働くことになるが……。ヒロインを演じるのは、ウクライナ出身のトップモデル、オルガ・キュリレンコ。", "2006-09-23", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緋牡丹博徒", "緋牡丹博徒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "女任侠スターとして一時代を築いた藤純子主演による“緋牡丹博徒”シリーズ第１作。九州の博徒矢野組の一人娘・竜子は、闇討ちで殺された父の仇を求めて、全国津々浦々の賭場を流れ歩き“緋牡丹のお竜”の異名をとる。ようやく仇を探しあてたお竜は、一匹狼のヤクザ片桐の助けで仇を討つ。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ladybugs", "恋のレディ＆レディ？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "弱小女子サッカー・チームにヒロイン（実は女装したヒーロー）が出現、チームを快進撃させる、というハリウッド得意のスポーツ・コメディ。サッカー少年マシューは、母親の再婚候補がコーチをつとめる女子サッカー部を立て直すべく、ひそかに入部を依頼される。彼はチームの美少女キンバリーに一目惚れし、これを承諾。女装して決勝戦までヘッポコチームを導くが……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／WHISPERING　CORRIDORS", "女校怪談", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "高校生のジオとジェイは、朝早く登校した際に“古狐”と呼ばれる教師の首吊り死体を発見した。その後も学内で次々と変死事件が発生。そんな中、ジオは古狐の死体に魅せられたかのように、死体の絵を描き始めていくのだが……。教育制度批判を盛り込み、センセーショナルな話題を集めて韓国では大ヒットしたジュブナイル・ホラー映画。", "1998-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暖簾", "暖簾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "大阪の昆布問屋に拾われた主人公が、分けてもらったのれんを守り、戦災にもめげずに父子二代にわたり、粘り強く生き抜いていく物語。山崎豊子のベストセラーを戯曲化したものをもとに映画化し、主人公の父子二代を森繁久彌が一人二役で演じた。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("城南旧事／MY MEMORIES OF OLD BEIJING", "北京の想い出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "少女時代を北京で過ごし、その後台湾に渡った台湾現代女流作家リン・ハイインの小説を映画化したもの。1920～30年代の北京のインテリ家庭に生まれた林英子。彼女はその少女時代を狂女や泥棒の青年、家の乳母とのふれあいの中で過ごしてきた。しかし50年の歳月とともに一人また一人と彼女の前を去っていき……。マニラ国際映画祭グランプリ受賞作。", "1982-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京タワー", "東京タワー", "", "", "http://ecx.images-amazon.com/images/I/41H46RZQ9SL._SL210_.jpg", 126, "直木賞作家、江國香織の同名小説を映画化したラブ・ストーリー。21歳の青年と41歳の人妻。その年の差を超えて惹かれ合う男女に岡田准一と黒木瞳が扮し、ロマンチックで切ない恋物語を繰り広げる。彼らとは対照的に、激しく求め合うもうひと組の不倫カップルを松本潤と寺島しのぶが熱演。21歳の透は、母親の友人である41歳の美しい人妻、詩史と恋に落ちる。純粋に惹かれ合う二人は、人目を忍んで逢瀬を重ねるが……。", "2005-01-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("羊のうた", "羊のうた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "同名漫画を映画化した耽美的なホラー・ファンタジー。吸血鬼の血を受け継いだ少年が、幼いころに別れた姉に再会し、ともに発作にさいなまれながらも、二人で生きていこうとする。人間の血を欲しながらも普通の人間として生活しようとする姉弟の姿が、日本家屋を効果的に取り入れたノスタルジックな情景の中に、切々と刻まれていく。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Notre Musique", "アワーミュージック", "", "", "http://image.eiga.k-img.com/images/movie/1584/315x210.jpg?3971", 80, "73歳になったゴダール監督の最新作。3部構成で、戦争映像のモンタージュ「地獄編」、サラエボを舞台にゴダール自身が本人を演じて、彼の講義に出席した女子学生オルガとの交流を描く「煉獄（浄罪界）編」、殉教したオルガのその後を描く「天国編」が展開。アラブの詩人マフムード・ダーウィッシュ、スペイン作家ファン・ゴイティソーロ、フランスの評論家ピエール・ベルグニウらの実在の小説家・芸術家たちが多数本人役で出演。", "2005-10-15", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自殺サークル", "自殺サークル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "インディペンデント映画の雄・園子温監督が初めて手掛けたエンタテインメント色濃いサスペンス作品。新宿駅のホームで、54人の女子高校生たちが一挙に集団自殺。また同時期に、看護婦たちが病院の窓から飛び降り自殺するという不可解な事件が相次いで起きた。事件を調べる黒田＆渋沢両刑事のもとに、１本のタレコミ電話がかかり……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRITTERS2: THE MAIN COURSE", "クリッター2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "前作から二年、あのおかしなエイリアン、クリッターたちはひそかに地球に卵を産み残していた。折しも時はイースター。恒例の卵探しゲームにクリッターの卵が紛れ込み、街はまたまた大騒ぎに。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マイマイ新子と千年の魔法", "マイマイ新子と千年の魔法", "", "", "http://image.eiga.k-img.com/images/movie/53974/315x210.jpg?3143", 93, "芥川賞作家・高樹のぶ子が自らが幼少時代を描いた自伝的小説を映画化。監督は「魔女の宅急便」（89）などで演出補を務めた片渕須直。昭和30年代、大自然に囲まれた山口県防府市にある旧家。おでこにマイマイ（つむじ）がある小学3年生の新子は、祖父から聞かされた千年前の町の姿やそこに生きた人々のことを空想することが趣味だった。ある日、なかなか田舎に馴染めない東京からの転校生・貴伊子と出会った新子は、次第に貴伊子と遊ぶようになり、心を通わせていく。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nydenion", "惑星戦記ナイデニオン", "", "", "http://image.eiga.k-img.com/images/movie/57504/315x210.jpg?4267", 89, "ドイツの映画業界で特殊効果スタッフとして働いていたジャック・モイクが、「スター・ウォーズ」や「ブレードランナー」にオマージュをささげる作品として、アマチュアや有志のスタッフ、キャストとともに1996年から15年の歳月をかけて完成させたスペースオペラ。西暦2630年、宇宙の覇権をめぐるサイコン帝国と連合軍の終わらない戦いに嫌気がさしたリック・ウォーカーは、戦闘機パイロットを辞め、いまではフリーの運び屋をしていた。そんなリックのもとに、ある日、謎の女性シンシアを秘密裏に行われる和平交渉の場へ送り届けるという仕事が舞い込む。", "2011-12-10", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hyva poika", "グッド・サン", "", "", "http://image.eiga.k-img.com/images/movie/57254/315x210.jpg?4034", 88, "湖畔の別荘を舞台に、青年の心情と母子の関係が次第にゆがんでいく姿を描いた人間ドラマ。スキャンダルから逃れるため2人の息子イルマリとウントを連れて湖畔の別荘にやってきた女優のレイラだったが、静かな生活に飽き、友人を招いて賑やかな週末を過ごす。レイラは招待客の作家アイモと距離を縮めていくが、母をひたむきに支えてきたイルマリはアイモの存在を煩わしく感じ……。最新のフィンランド映画5作品を一挙上映する「フィンランド映画祭2011」にて上映。", "2011-10-02", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STORY OF ROSE", "ローズ・ウォンの青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "10年の歳月を経て少女から女へと成長していく主人公ローズの歩みを克明に綴っていく物語。マギー・チャンが初めてベッド・シーンに挑戦、演技開眼したといわれる作品。近年、ハリウッドでの活躍も目覚しいチョウ・ユンファが共演。DVDタイトルは「マギー・チャンのストーリー・ローズ／恋を追いかけて」。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIRTY LAUNDRY", "ダーティー・ランドリー 悪い奴ほどよく洗え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ごく普通の青年がコインランドリーで洗濯物の袋を間違えたことから、コカインの密輸取引に巻き込まれた。ドジな悪人たちから逃げていくうち、いつしか女性レポーターも道連れとなり、騒ぎは大きくなっていく。陸上の五輪金メダリスト、カール・ルイスが特別出演。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かっ鳶五郎", "かっ鳶五郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "本沢たつやの同名コミックを映画化。浅草の名物男である若きトビ職人の奮闘を、人情味たっぷりに描く。五郎は浅草の人々に愛される昔ながらのトビ職人。ある日、友情で結ばれていたヤクザ者、月橋の悪質な地上げを知り、ショックを受けた五郎は、町の危機を救うためにひと肌脱ぎ……。愛すべき主人公、五郎を的場浩司が好演。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Moj Nikifor", "ニキフォル　知られざる天才画家の肖像", "", "", "http://image.eiga.k-img.com/images/movie/33724/315x210.jpg?4158", 100, "ポーランドが誇る天才画家ニキフォルの晩年を映画化。1960年代のポーランド南部を舞台に、言語障害を持つ頑固で変わり者の老人ニキフォルと、自分の人生を犠牲にしてまで彼の晩年を見守った後見人マリアン・ボシンスキとの絆を描き、世界各国の映画祭で数々の賞を獲得した。ニキフォルに扮するのは86歳のベテラン女優クリスティーナ・フェルドマン。男性であるニキフォルの晩年の姿を、まるで生き写しのように熱演している。", "2006-11-03", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明治天皇と日露大戦争", "明治天皇と日露大戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ワイド・スクリーンで繰り広げられる旅順、黄海、遼陽、奉天の戦い。クライマックスはもちろん、日本海海戦。明治天皇を実名で登場させ、社会的センセーションを巻き起こした新東宝の一大企画で、当時７億円を稼いだ。明治天皇は平和主義者として描かれている。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beverly Hills Chihuahua", "ビバリーヒルズ・チワワ", "", "", "http://image.eiga.k-img.com/images/movie/54134/315x210.jpg?2772", 92, "化粧品会社オーナーのビブの愛犬クロエは、ビバリーヒルズで悠々自適な生活を送るセレブなチワワ犬。しかしある日、ビブの出張で姪レイチェルに預けられ、レイチェルの旅行先メキシコで犬窃盗団に拉致されてしまう。監督は「ホーム・アローン3」「25年目のキス」のラジャ・ゴスネル。「チャーリーズ・エンジェル」のドリュー・バリモアがクロエの声を担当。", "2009-05-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドルフィンブルー　フジ、もういちど宙（そら）へ", "ドルフィンブルー　フジ、もういちど宙（そら）へ", "", "", "http://image.eiga.k-img.com/images/movie/34044/315x210.jpg?2486", 103, "沖縄の美ら海水族館で実際に起きた出来事を基に、世界初の人工尾びれプロジェクトに賭けるイルカと人間たちの奮闘を描いた感動作。原因不明の病気で尾びれを失ってしまったバンドウイルカのフジ。再びフジを泳げるようにしてやりたい新米獣医の植村一也は、フジにゴム製の人工尾びれをつけることを思いつく。フジのために奔走する一也を「神童」の松山ケンイチが好演。監督は「陽気なギャングが地球を回す」の前田哲。", "2007-07-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幸福時光", "至福のとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "継母に虐げられてきた盲目の少女と、金も仕事もない、しがない中年男の奇妙な交流を描いた感動ドラマ。中国の大都市、大連。愛する女性に気に入られたいばかりに、彼女の前夫の連れ子ウー・インを預かることになってしまった中年男チャオ。彼は按摩が得意なウー・インのために、閉鎖された工場に偽の按摩室を作り、友人たちに客のフリをしてもらうことにするのだが……。これまで農村などを題材にすることが多かった中国の名匠チャン・イーモウが、珍しく大都会を舞台にした作品。近代化の進む都市で懸命に生きる市井の人々に注ぐ、優しい眼差しに心が温まる。厳しい現実を見据えたようなラストシーンも印象的だ。", "2002-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Prince of the City", "プリンス・オブ・シティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "　捜査のための特権を与えられていたニューヨーク警察・麻薬特捜班は“町の王子様”と呼ばれていた。やがてその汚職を調査すべく、内部調査委員会が動き出す。彼らは特捜班の一員ダニエル刑事に取引を持ちかける。ダニエルはやむなくそれに応じ、盗聴器をつけることに。やがて刑事たちの不正が次々と明らかになり、ついにはダニエルと彼の仲間までもが窮地に立たされるが……。実話を映画化した社会派サスペンス。刑事たちの生き様がリアルに描き出される。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("稲妻峠の決斗", "稲妻峠の決斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "剣聖、上泉伊豆守に敗れて剣の道の深さを知った槍の使い手、高田又兵衛は修業の旅に出る。その頃又兵衛の友人、赤尾が守る砦に密書を届ける途中の若侍が野盗、鬼神組に殺され……。三村伸太郎の『日本剣豪伝』を基に、又兵衛の極意を極める旅と野党一味との対決を描いた時代劇映画。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海からの使者", "海からの使者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ウェブアニメーションの草分け的存在として人気を集めてきたクリエイター・のすふぇらとぅが、前作「雨に抱かれて」から6年の歳月を費やして作りあげた続編。電脳空間にはびこる“悪意”と10年にわたって戦いつづけてきたのんちゃん。暴力で暴力を倒すことの理不尽さに疑問を抱いたのんちゃんが戦いを放棄したその時、新たな悪意が街を破壊しはじめる。大切なものを守るため、再び戦いに身を投じるのんちゃんだったが……。", "2010-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANTONY ＆ CLEOPATRA", "アントニーとクレオパトラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 170, "シーザーの死後４年、激しい権力争いに明けくれるローマ帝国を舞台に、運命によって引き裂かれていくアントニーとクレオパトラの悲劇を描く。「ジュリアス・シーザー」に続き、ヘストンがシェイクスピアものに挑戦。今回は監督・脚本を兼ね、全力投球で作り上げた大スペクタクル史劇。", "1971-01-01", "1,3,26,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("家光と彦左", "家光と彦左", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "松竹から東宝へ移籍した長谷川一夫と、エノケンと並ぶ喜劇王・古川緑波の初顔合わせによるマキノ雅広監督の娯楽時代劇。大久保彦左衛門は徳川将軍家三代目の世継に死を賭して竹千代を推す。やがて竹千代は三代将軍・家光となり、彦左とともに本多上野介の宇都宮釣天井の陰謀を打ち砕く。コメディ・タッチのストーリー展開とともに、家光が催す大宴会のシーンで日劇ダンシング・チームを踊らせるなど賑やかな趣向の中に、二大スターの持ち味を十二分に生かしたマキノ雅広の巧みな演出はまさに名人芸。この作品の好評により、同年末に同じ顔合わせで芸道映画「男の花道」が製作された。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゼロ・ウーマンR 警視庁0課の女　欲望の代償", "ゼロ・ウーマンR 警視庁0課の女　欲望の代償", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "篠原とおるのコミックを映像化した人気シリーズ「ゼロ・ウーマン」の1作として、「LOVEHOTELS ラヴホテルズ」の三浦敦子がヒロインを務めた劇場版。警察内部でもその存在がほとんど知られていない特命非合法捜査機関・警視庁0課に所属する美貌の女刑事レイ。政界や財界の大物たちを狙う連続殺人事件の捜査に乗りだした彼女は、同じく事件を追う警視庁捜査一課のキャリア・早見と行動を共にするうち、互いにひかれあっていくが……。", "2007-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母子鶴", "母子鶴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "三益愛子主演による、大映お得意の“母もの映画”の一つ。旅回りの女奇術師・信子は偶然、戦火の中で別れた夫の再婚と長女の幸福な生活ぶりを知る。だが、二人の幸せのために彼女はあえて何も告げずに去った。混乱した次女・マリ子は愚直な母を恨みもするが、やがて母の待つ舞台へ帰って来る……。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON ANY SUNDAY", "栄光のライダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "記録映画作家ブラウンが「大脱走」のマックィーンの逃走シーンを見て感激し、この映画の企画を立案。当人のマックィーンも招き、モトクロスのスピード世界を描いたドキュメンタリー。全米チャンピオン・レースほか、有数のレースが登場。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("High Tech Soul", "HIGH TECH SOUL The Creation of Techno Music", "", "", "http://image.eiga.k-img.com/images/movie/33594/315x210.jpg?2926", 63, "現代テクノ・シーンを牽引するアーティストたちへの貴重なインタビューから、テクノ・カルチャーの真実に迫るドキュメンタリー。1980年後半に始まり、世界中を巻き込んで盛り上がりを見せたデトロイト・テクノ。デリック・メイ、ホアン・アトキンス、ケビン・サンダーソンらデトロイト・テクノの代表的アーティストをはじめ、テクノ・カルチャーの歴史をつくりあげてきた総勢34名の人々がその魅力やルーツを明かす。", "2006-08-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOOT FIGHTER", "シュートファイター　−暗黒ドラゴン伝説−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「燃えよドラゴン」以来、100本を超えるアクション映画に出ているB・イエンが主演。拳法の達人シンゴの二人の弟子のもとに、暗黒街からの格闘技トーナメントの誘いがくる。参加を決意した弟子たちだったが、実はシンゴの仇敵テンが仕組んだ罠だった。ルール無用の裏格闘技界での、生死を懸けた戦いが始まる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MANITOU", "マニトウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "400年前のインディアンの悪霊が、カレンという女性の背中の腫瘍に宿り、産声を上げるオカルトもの。異様な出産場面、凍結する病室、そしてインディアンの霊とコンピュータの霊が激突するスペクタクルSFX。これを最後に事故死したガードラー監督の怪作である。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rysopis", "身分証明書", "", "", "http://image.eiga.k-img.com/images/movie/55004/315x210.jpg?4649", 76, "「出発」「不戦勝」などで知られるポーランドの巨匠イエジー・スコリモフスキ監督が、大学の卒業制作として1964年に撮りあげた長編監督第1作。兵役に就くことになった24歳の青年アンジェイが出発するまでの数時間にスポットを当て、恋人や友人たちと共に過ごす様子を描いていく。イエジー・スコリモフスキ自らが主演を務める。共演に「すべて売り物」のエルジュビェタ・チジェフスカ。", "2009-10-21", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IRON MAZE", "アイアン・メイズ ピッツバーグの幻想", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "自分の手で町を活性化させてみせると、夢を追い、雄大な国アメリカにやって来た一人の日本の若者。ピッツバーグの田舎町で彼は遊園地の建設に着手するが、何者かに襲われ、瀕死の重傷を負う。背後には小さな田舎町の、日本に対する複雑な感情の存在があった……。「ゴキブリたちの黄昏」で広く海外で認められた吉田監督が、アメリカ資本で撮ったサスペンス・ドラマ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Zuzu Angel", "ズズ・エンジェル", "", "", "http://image.eiga.k-img.com/images/movie/54784/315x210.jpg?4329", 110, "ブラジルのファッションを世界に広めた女性デザイナー、ズズ・エンジェルの激動の人生を映画化した社会派ドラマ。ファッション・デザイナーとして成功を収め、公私ともに充実した日々を送っていたズズ。ところが軍事政権下の70年代、反政府運動に身を投じていた彼女の息子スチュアートが逮捕されてしまう。愛する息子が軍による拷問で命を落としたと知らされたズズは、政府に立ち向かうことを決意するが……。", "2009-10-05", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAGIC BOW", "令嬢ジャンヌ ～パガニーニ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "天才の名を欲しいままにした、19世紀イタリアのバイオリニストで作曲家のパガニーニ。思うままに生きた彼はまた、数多くのスキャンダラスな恋愛でも知られる。ここでは、フランスの美しい伯爵令嬢ジャンヌとの恋がローマ法王御前演奏の場で実るまでを、華麗なる旋律をバックに描かれる。", "1946-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Sassy Girl", "猟奇的な彼女", "", "", "http://image.eiga.k-img.com/images/movie/1554/315x210.jpg?3120", 122, "原作は、1999年に大学生のキム・ホシクがパソコン通信の掲示板に書き込んだちょっとヘンな女の子とのデート体験談。これが人気を呼んで単行本化され、ベストセラーに。映画も恋愛映画では韓国映画史上歴代第1位の大ヒット。大学生キョヌが出会った美女は、シナリオライター志望で凶暴でちょっと変わった女の子。振り回されながらも彼女が好きになるキョヌだったが……。", "2003-01-25", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HISTOIRES D’AME´RIQUE：FOOD，FAMILY AND PHILOSOPHY", "アメリカン・ストーリーズ 食事・家族・哲学", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "前衛的な作風で知られる女流監督C・アケルマン。ユダヤ系である彼女が、アメリカに移民したポーランド系ユダヤ人のルーツを、独特の手法で描いた作品。ナチス・ドイツにピークを成す、ユダヤ人への数々の迫害など過去の体験を話し出すユダヤ人たちの会話を通して、彼らの民族的記憶を引き出していく。", "1988-01-01", "2,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢は夜ひらく", "夢は夜ひらく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "同名のヒット歌謡曲をモチーフに映画化した純愛もの。園まりが主演し、野口晴康が監督にあたった。家出してヤクザになって死んだ兄の遺産をめぐり、妹のマリは兄の友人の岬と知り合う。やがて二人は愛し合うようになり、遺産を狙う者たちと岬は決闘するはめに……。当時の歌謡界のスターが集結。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狙撃", "狙撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "永原秀一のオリジナル・シナリオによるハードボイルド映画の快作。一匹狼の殺し屋・松下徹は金塊密輸の手助けをするが、敵方の雇ったスゴ腕の殺し屋に仲間を殺されたうえ、恋人までも人質にとられ、ついに１対１の対決を挑む。病老の殺し屋に扮した森雅之が見事な風格を見せた。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウォータームーン", "ウォータームーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "「オルゴール」に続く長渕剛の出演映画第２作。原案・音楽、さらには工藤栄一監督の途中降板により、長渕本人が撮影の残りを演出したとも伝えられる、いわくつきのワンマン映画である。修行僧として山寺にかくまわれている異星人と、彼を利用しようとする国家公安部との確執を描くユニーク作。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOLIDAY INN", "スイング・ホテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "B・クロスビーとF・アステアが初共演を果たしたミュージカル。歌手のジムは恋人のライラを仲間のダンサー、テッドに奪われ、傷心のまま田舎の農場に引っ込んでしまう。一年後、ジムは自宅をホテル“ホリデイ・イン”に改造してショーを開くからとテッドを誘うが、相手にされず、役者志望の花売り娘リンダを仲間にして大晦日にホテルを開業した。しかし、そこにライラに捨てられたテッドが訪ねてきて、今度はリンダをめぐって二人の恋のさや当が始まる……。監督はアステア・ロジャースのミュージカルを５本手掛けたM・サンドリッチ。クロスビーの歌う『ホワイト・クリスマス』は大ヒットし、アカデミー主題歌賞も受賞した。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飼育", "飼育", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "昭和二十年の初夏。米国の爆撃機が山中に墜落、脱出した黒人兵は猟の罠にかかり村人に捕らえられ、そして黒人兵の“飼育”が始まる。しかし村は疎開者を抱え、地主との間にも悶着が絶えない。トラブルの原因はあの黒人兵だ……村人のイライラは黒人兵に向けられ、あろうことか彼を殺してしまう……。大江健三郎の同名小説を田村孟が脚色、松竹を退社した大島渚が監督した社会ドラマの異色作。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("馬鹿が戦車〈タンク〉でやって来る", "馬鹿が戦車〈タンク〉でやって来る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "日永村のはずれに、少年戦車兵あがりのサブと頭の弱い弟の只六、そして耳の不自由な母親の３人が暮らしていた。村の人たちは彼らを村八分にしていた。なかでも長者・仁右衛門は戦後の農地解放でサブに農地を取られたのを根に持ち、サブとのいさかいが絶えない。いさかいが高じて、サブはブタ箱に入れられ、土地を取り上げられてしまう。出所したサブは、納屋に隠しておいた戦車に乗り、村中を大暴れする。“ある男が除隊のときにタンクを納屋に隠しておいて、ある日、それで暴れ出す”という團伊玖磨のひと口話をモチーフに、山田洋次がシナリオ化。どこかにありそうでどこにもない、日本のとある田舎を設定して、方言まで創作したという凝りようだ。喜劇の体裁をとりつつ、偏見に対する痛烈な批判がうかがえる。実に詩心のある仕上がりで、山田監督初期の代表作となった。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAROCCO", "バロッコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ヒッチコックの「めまい」を思わせる、男と女の不条理なラブ・ストーリー。無名ボクサーのサムソンは、選挙戦のさなかに、有力候補者とのホモセクシュアルな関係をネタにして大金をせしめる。彼は恋人のロールと国外逃亡を図るが、駅で殺し屋に殺されてしまう。犯人の殺し屋は、何故かサムソンと瓜二つで、大金の在りかを捜す殺し屋はロールの前に何度も現れる。ロールは、次第にサムソンと殺し屋の区別がつかなくなり、彼を愛していくが……。若きG・ドパルデューが、サムソンと殺し屋の二役を演じる。脇役では、ロールの友人ネリーに扮したM・F・ピジェが好演。この演技で、セザール賞最優秀助演女優賞を受賞している。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Emergo", "エマルゴ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAOS", "女はみんな生きている", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "「赤ちゃんに乾杯！」で知られるフランス人女性監督コリーヌ・セローの作品。自分勝手な夫にうんざりしていた貞淑な主婦と、売春組織に追われるセクシーな娼婦。男たちに虐げられてきた対照的な二人の女が、知恵と勇気と女の魅力を生かして反逆を開始する。愚かな男たちに一泡吹かせる痛快な幕切れまで、先読みできないリズミカルな展開が小気味よい。夫と車で外出した主婦のエレーヌ。通りで男に追われる血まみれの女性に助けを求められたが、夫は彼女を無視してその場を走り去った。罪の意識を抱いたエレーヌは女性の入院先を突き止め、献身的な看病をはじめる。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("This Sporting Life", "孤独の報酬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "「ifもしも……」のリンゼイ・アンダーソン監督の長編映画デビュー作。下宿先の未亡人に恋をした炭坑夫フランクは、彼女を幸福にするため、大金を稼ぐことのできるラグビー選手に転職する。彼は持ち前の運動神経で花形選手として活躍するようになるが、未亡人は彼の粗野な態度を受け入れることができず……。リチャード・ハリスが未亡人への一途な愛に葛藤する主人公フランクを熱演し、カンヌ国際映画祭主演男優賞を獲得している。", "1963-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE JOURNAL D’UN CURE´E DE CAMPAGNE", "田舎司祭の日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "寒村アンブリクールに司祭が赴任して来た。村人たちが無関心のなか、病を抱えながらも信仰の世界に生きる彼は、献身的に村に尽くそうとする。やがて村に住む伯爵一家と交渉を持つようになった彼は、伯爵夫人の悩みを聞くうちに接近。それがよからぬ噂を村に生み、伯爵からも、またその娘からも疎んじられるようになる。そして彼の肉体も衰えていき、誰にも看取られないままに小さな部屋で吐血し、死んでいく……。虚飾をはぎとった、あくまでも禁欲的な映像と重なるかのような、主人公の慎ましやかな生を描く。L・H・ビュレルによる陰影を巧みに生かした撮影も印象深い。ヴェネチア映画祭国際賞および撮影賞を獲得した。ジョルジュ・サドゥールはこの作品をR・ブレッソンにとっての転機と位置づけている。", "1951-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひまわり", "ひまわり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "小学校のときの同級生だった女性が、海難事故で行方不明に。遺体が発見されないまま、始められた葬式に集まった彼女のクラスメイトや恋人たちが“彼女”を回想していく。一見、ノスタルジックな味わいの青春映画ながら、印象が希薄で不在の主人公を登場人物それぞれの記憶と関係性から浮き彫りにしていく語り口に、群像劇ならではの躍動感がはじける。記憶によってたたずまいが変化するヒロインを麻生久美子が妙演。当時、人気のあったお笑い集団“ジョビジョバ”のマギーや土屋久美子、光石研ら個性派の競演も楽しく、とりわけ北村一輝が麻生とのやりとりで見せる生々しい男女のリアリティーは圧巻。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("静かなるドン　新章", "静かなるドン　新章", "", "", "http://image.eiga.k-img.com/images/movie/54754/315x210.jpg?4282", 80, "新田たつおによる同名人気コミックを、袴田吉彦主演で映画化。関東最大の暴力団・新鮮組の総長の1人息子の静也は、ランジェリー・メーカーで働く平凡な社員。しかし、敵対する組織の陰謀で父を亡くし、嫌々ながらも3代目組長を襲名することになってしまう。静也は関西最大勢力の鬼州組との全面戦争を回避するなど、次第に組長としての頭角を現し始めた静也だったが……。", "2009-09-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Marine", "ネバー・サレンダー　肉弾凶器", "", "", "http://image.eiga.k-img.com/images/movie/33874/315x210.jpg?3799", 0, "プロレス団体WWEのスーパースター、ジョン・シナ主演のアクション・ムービー。イラク戦争で英雄となりながらも、軍法違反で海軍を除隊されたジョン・トライトン。帰国後は故郷へ戻り、妻と共に新たな生活を始めた彼だったが、逃走中のダイヤモンド強盗団に妻を連れ去られてしまう。愛する妻を救うため、ジョンは決死の覚悟で追跡を開始する。共演は「ウォーク・ザ・ライン／君につづく道」のロバート・パトリックほか。", "2007-03-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("E.T.: The Extra-Terrestrial", "E.T.", "", "", "http://ecx.images-amazon.com/images/I/41OAwz1ycSL._SL210_.jpg", 115, "少年と宇宙人との心温まる交流を描き、世界的に大ヒットを記録したスピルバーグの代表作。宇宙人を、敵対者や侵略者ではなく、我々と同等かそれ以上に知的で、しかも優しく思いやりのある存在として描いた。「未知との遭遇」で宇宙人との“接近”を描いたスピルバーグだが、この「E.T.」ではより深く人間の生活に入り込んだ宇宙人が、ついには“魂の交流”を成すに至る感動の物語を作り上げている。地球探査に訪れたUFOは、地球人に発見される危機を逃れて、仲間を一人置き去りにしてしまう。彼と出会った10歳の少年エリオットは、兄妹とともに彼を星に帰すべく努力するが、異星人を追うNASAの手が次第に彼らに迫ってくる……。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Food, Inc.", "フード・インク", "", "", "http://image.eiga.k-img.com/images/movie/55724/315x210.jpg?1605", 94, "アメリカの食品産業の裏側に隠された衝撃の実態を暴き出し、アカデミー賞長編ドキュメンタリー部門にノミネートされた話題作。ひと握りの人々が莫大な利益を得る一方で下請けにあえぐ一般農家、さらに日本でも未だに表示義務のない遺伝子組換食品の問題など、農業や畜産業の巨大工業化が生み出した数々の弊害をあぶり出していく。「ファーストフード・ネイション」の原作者として知られるエリック・シュローサーが製作に参加。", "2011-01-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("橋", "橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "数年前に起きた殺人放火事件に関係している繊維デザイナーと、その再審請求を受けて調査している弁護士との恋を描いたラブ・サスペンス。“糸のダイヤモンド”と呼ばれている天然蚕の糸で、実際に作られた着物の数々が使用されている。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAMSON AND DELILAH", "サムソンとデリラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "この手の歴史大作を手掛けては、右に出るものがないデミル監督のスペクタクル作品。怪力サムソンとセマダール、デリラの悲しい愛憎ドラマ。デリラを演じるラマーの悪女ぶりが見事。ラストの寺院崩壊のシーンで、石柱が音を立てて、崩れ落ちていく特殊効果は、今観ても迫力満点。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Giulia non esce la sera", "ジュリアは夕べに出かけない", "", "", "http://image.eiga.k-img.com/images/movie/55424/315x210.jpg?1615", 105, "「青春の形見」「ぼくの瞳の光」のジュゼッペ・ピッチョーニ監督によるラブストーリー。主演はバレリオ・マスタンドレア、バレリア・ゴリーノ。小説家としては順調なキャリアを築きあげたグイドだったが、妻と娘との関係はうまくいっていなかった。ある日、娘が通う水泳教室で先生のジュリアに出会ったグイドは、時分も水泳教室に通うことにする。やがてグイドはジュリアを夕食に誘うが、彼女には夜、外出できない理由があった……。イタリア映画祭2010にて上映。", "2010-04-28", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Halloween", "ハロウィン（1978）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "15年前、包丁で自らの姉を殺害したマイケルが精神病院を脱走し、ハロウィンの夜に故郷に戻る。担当医ルーミスの追跡をよそに、白いマスクをつけ、包丁を手にしたマイケルは殺戮を繰り返すことに。ベビーシッターのアルバイトをしていた女子高生ローリーも命を狙われるが……。ジョン・カーペンター監督の名前を世界中に轟かせることになった大ヒット・ホラー。神出鬼没のマイケルのキャラクターは人気を博し、シリーズ化された。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BACHEHA−YE　ASEMAN", "運動靴と赤い金魚", "", "", "http://ecx.images-amazon.com/images/I/311236NB2GL._SL210_.jpg", 88, "モントリオール国際映画祭でグランプリに輝き、イラン映画としては初のアカデミー外国語映画賞候補にもなった感動作。アリ少年が妹ザーラの修理したての靴を紛失してしまう。家計が苦しくてそのことを両親に言い出せない彼らは、一足の靴を替わりばんこに履いて学校に通うことに。そんなとき小学校のマラソン大会が開催され、アリは賞品の靴を獲得するために３等賞をめざして奮闘する。知人に聞かされた実話をもとに脚本を執筆したM・マジディ監督が、貧困という社会問題を盛り込んで笑いと涙のドラマを完成。クライマックスのマラソン・シーンでは、手に汗握る興奮を呼び起こす。", "1997-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TRUTH ABOUT CATS ＆ DOGS", "好きと言えなくて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ラジオのペット番組でパーソナリティーを務めるアビーはある日、番組内でファッション・カメラマンのブライアンから犬に関する電話相談を受ける。彼女の優しさに感激したブライアンは、アビーに会いに局にやって来るが、容姿コンプレックスの彼女は、居合わせた友だちで美人モデルのノエルを偽ってアビーと紹介してしまう。嘘を告白できないままアビーはブライアンを好きになるが、ノエルもブライアンを好きになってしまい……。同じ男性を好きになった二人の恋の行方を描いたロマンティック・コメディ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機関車先生", "機関車先生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "第７回柴田錬三郎賞を受賞した伊集院静の同名小説をアニメ化した感動作。昭和30年代の瀬戸内海に浮かぶ葉名島。総勢７名の生徒が通う水見色小学校に、北海道から臨時の先生がやって来る。彼は口をきけないために、大人たちは反対するが、剣道の腕前と愛情豊かな授業風景を見てひと安心。生徒たちも口をきかんということから機関車先生と呼んで打ち解けていく。ガキ大将の父親が東シナ海で遭難したり、借金苦から級長の一家が島を出て行ったりという度重なる悲劇を通して、ますます絆を深めていく機関車先生と子供たち。しかし、新任の先生が決まり、機関車先生は北海道へ帰ることになる……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Starman", "スターマン／愛・宇宙はるかに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "　最愛の夫スコットを亡くした悲しみが未だ癒えないジェニー。そんな彼女が暮らす田舎町に宇宙船が飛来。人類の呼びかけに応え、友好のためにやってきた宇宙人はジェニーの家に忍び込み、スコットそっくりの姿に変わる。ジェニーは宇宙人と一緒に旅をするうちに、彼に好意を寄せるようになる。一方、政府も宇宙人の追跡を開始していたが……。スターマンと人間の女性の交流を感動的に描いたSFファンタジー。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANY WHICH WAY YOU CAN", "ダーティファイター 燃えよ鉄拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "トラック野郎を描いたアクション・コメディ「ダーティファイター」の続編。試合を断わったために愛するリンをギャングに誘拐されたウイドウは、試合相手のウイルソンとともに一味を倒す。そして二人だけの勝者決定戦を始めるのだった……。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CUORI SENZA FRONTIERS", "白い國境線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "戦争の傷跡を独特のタッチで描いたイタリアン・ネオ・リアリスモ第一期の代表作。北イタリアの小さな村に、連合国が定めた国境線が引かれ、村は二分されてしまう。農園をユーゴ側に引き離され、遊び場を失ってしまった少年パスカリーノは、ユーゴ側に渡った姉の恋人から国境を越えてくるように説得される。そんな折、ユーゴ側から政治犯ドメニコがやって来て姉と恋に落ちるのだった。", "1950-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("帰って来たヨッパライ", "帰って来たヨッパライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ミリオンセラーになったヒット曲を題名に、曲を歌ったザ・フォーク・クルセダーズの3人を主演させたコメディ。大学生活最後のバカンスに日本海へ遊びに行った悪友3人組は、海で泳いでいる間に衣服を何者かに盗まれてしまう。そこで銭湯に行くが、湯気の中から現れたネエちゃんに服を盗めと言われ……。単なる喜劇に終わらず、日本人と韓国人の間にある民族問題を描く、大島渚監督の異色作。物語の途中でファースト・シークエンスがそっくり回帰する斬新なスタイルも話題に。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風雲児信長（織田信長）", "風雲児信長（織田信長）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "マキノ雅広監督・片岡千恵蔵主演による歴史時代劇。戦国時代、織田信秀の一子・信長は、父の死に乗じて尾張を侵略しようとする義父・斉藤道三をたしなめ、難敵・今川義元を討つべく桶狭間へ向かう。信長の忠臣・平手政秀を志村喬が好演。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TURNING POINT", "愛と喝采の日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "家庭に入り自分の娘の成長を見つめる女、いまだにバレリーナとして自己主張を続ける女。人生のターニング・ポイントで決定的に異なった生き方を選んだ二人の女性の姿を、余すことなく明確に捉えた秀作。互いの人生を嫉妬し、ののしり合う二人が、無人のステージで肩を組み自分に忠実に生きたことを確認するラスト・シーンは感動的。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無法松の一生", "無法松の一生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "阪東妻三郎（1943年・稲垣浩監督）、三船敏郎（1958年・同）、三國連太郎（1963年・村山新治監督）に続く４代目松五郎を、勝新太郎が豪快に演じた。小倉の人力車夫が大尉の未亡人にひそかな慕情を寄せ、彼女の息子の成長を見守っていく。乱暴だが誠実な松五郎のキャラクターは永遠に不滅。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サード", "サード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "原作は軒上泊の『九月の街』で、これを寺山修司が脚色しているが、でき上がった脚本はほとんどオリジナルといってもいいほどの斬新さを見せている。このシナリオにもとづいて「もう頬づえはつかない」の東陽一が演出。高校野球部の三塁手として活躍していたサード、数学 IIBが得意な IIB、新聞部とテニス部の女の子二人。この４人が、“どこか大きな町へ行こう”ということになり、そのためには金が必要だと、売春を始める。最初は順調に行ったが、ヤクザに引っかかり、サードは傷害事件を起こして少年院に入れられることに。４人の少年少女の生きる姿を東陽一と川上皓市のコンビがみずみずしく描き、新鮮な味わいの傑作にしている。もちろん脚本・寺山修司の功績も忘れてはならない。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("縞の背広の親分衆", "縞の背広の親分衆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "森繁が森の石松の後裔で、ブラジルから20年ぶりに里帰りする元ヤクザを演じるという“ヤクザ映画”に対するパロディ精神にあふれた作品。森繁とフランキーの芸達者ぶりが光る。ハチャメチャなストーリー展開とラストのオチには思わずニヤリとさせられるはず。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドリフト", "ドリフト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "「カミュなんて知らない」の柏原収史が主演を務める本格カーアクション。世界ランカーのプロボクサーとして栄光の日々を送っていた南真一は、黒い車の走り屋が引きおこした交通事故によって最愛の妻子を失い、ボクサー生命までも絶たれてしまう。走り屋への復しゅうを胸に誓った真一は、自らも走り屋となって公道で名をあげていく。共演は舞台版「テニスの王子様」の遠藤雄弥、グラビアアイドルの小松彩夏ほか。", "2006-06-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SLUGGER’S　WIFE", "スラッガーズ・ワイフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "人気劇作家サイモンとアシュビー監督がコンビを組んだラブ・コメディ。ロック歌手に惚れた大リーガーが、彼女のためにホームランを連発。結婚にこぎつけるが、彼女は突然家出をし、とたんに彼は不調になってしまう。M・リット監督が野球チームの監督役を好演。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Save the Tiger", "セイブ・ザ・タイガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "　ハリーが経営する服飾工場は業績が悪化し、資金繰りもままならなくなってしまう。若い頃の夢想に耽り、現実逃避する彼は、自分の工場に火をつけ、保険金を騙し取ろうとするが……。窮地に追いやられる男の姿を描いた人間ドラマ。ジャック・レモンが名演を披露し、アカデミー主演男優賞に輝いた。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プール", "プール", "", "", "http://image.eiga.k-img.com/images/movie/54464/315x210.jpg?3862", 96, "日本の家族から離れ、タイ・チェンマイ郊外にあるゲストハウスで働く母・京子を訪ねてきたさよだったが、そこで暮らすオーナーの菊子、従業員の市尾、タイ人の子供・ビーとうまく馴染めずにいた。しかし彼らと数日を共にすると、自分を置いていってしまった京子と自然に向かい合うようになる。「デトロイト・メタル・シティ」「ヘブンズ・ドア」などの脚本家として知られる大森美香が監督を務め、林聡美、加瀬亮、もたいまさこら個性派俳優が出演している。", "2009-09-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("九月の空", "九月の空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "剣道部に所属する高校１年の少年とその同級生の少女。大人の入り口にたった二人のさわやかな恋愛感情と、期待と不安に入り交じった未知なるものへの憧れ、劣等感や優越感を高校生の日常生活の中に描き出した青春もの。原作は1978年上半期の芥川賞を受賞している。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("この子の七つのお祝いに", "この子の七つのお祝いに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "大臣秘書のお手伝いが何者かによって殺されたことから、平凡な母子がいかに傷ついて現在に至ったかを解き明かしていくミステリー。『この子の七つのお祝いに』のメロディーが、謎を解くカギとなっている。原作は第１回横溝正史賞を受賞した斎藤澪の同名小説。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ねこばん3D とび出すにゃんこ", "ねこばん3D とび出すにゃんこ", "", "", "http://image.eiga.k-img.com/images/movie/55884/315x210.jpg?2401", 0, "愛らしいネコたちと中年男性の交流を描いた癒しのTVドラマ「ねこばん」を完全フル3D映像で映画化。定年退職したばかりの元鉄道員の根元勲は、庭に訪れるネコたちを眺めながら暮らしていた。そんなある日、娘から5歳の孫の世話をまかせられてしまう。まったく懐かない孫の世話や慣れない家事に悪戦苦闘するなか、1匹のネコが現われる。ベテラン俳優・伊武雅刀が主人公・勲を哀愁たっぷりに演じる。", "2011-01-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THX 1138", "THX−1138", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ルーカスが学生時代に作った短編を75万ドルの製作費でリメイクした劇映画第１作。25世紀、人間が番号化され、コンピュータが社会を支配する世界を舞台に、そこから脱出しようとする男の姿を描く。コッポラがプロデュースを手掛けた。ビデオタイトルは「ジョージ・ルーカスのTHX−1138」。DVDはディレクターズカット版。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ソドムの市", "ソドムの市", "", "", "http://ecx.images-amazon.com/images/I/315TRCHWPZL._SL210_.jpg", 104, "「リング」の脚本家・高橋洋が長編初メガホンを取ったホラー活劇。18世紀、キャサリンとテレーズは身に覚えのない罪で領主・俎渡海市兵衛に責め殺されてしまう。そして現代、市兵衛の破壊的な性格を受け継いだ俎渡海一族の末裔・市郎は、300年もの時を越えてよみがえった怨念によって極悪人「ソドムの市」へと変貌。世界を恐怖の底に陥れていく。新人監督たちが名監督に挑む「映画番長」シリーズ第3弾で、高橋洋が監修を務める「ホラー番長」の1作。", "2004-10-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OF HUMAN BONDAGE", "人間の絆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "画家を志していた若者フィリップは自分の才能に見切りをつけ、医学校に入る。真剣に勉強に打ち込むフィリップだったが、喫茶店の娘ミルドレッドに一目惚れしてから彼の生活はリズムが狂いはじめる。しかも彼女は生来の浮気女。それでもプロポーズしたフィリップだが……。S・モーム原作の明るい文芸映画。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONTY PYTHON AND THE HOLY GRAIL", "モンティ・パイソン・　アンド・ホーリー・グレイル", "", "", "http://ecx.images-amazon.com/images/I/51FyDv1x74L._SL210_.jpg", 92, "英国が誇るパロディー集団“モンティ・パイソン”が、あの『アーサー王と円卓の騎士』の物語をもとに描いた爆笑パロディー。西暦932年、イギリスを統治していたアーサー王は、お告げに現れた聖杯を求め、円卓の騎士を率いてキャメロット城へと赴く……。舞台は中世のはずなのに、ダイナマイトやパトカーなどが登場するハチャメチャな展開。何が何だかわからなくラストに至るまで、モンティ・パイソンのブラックな笑いに満ちている。", "1975-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TAKING OF PELHAM １，２，３", "サブウェイ・パニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "アメリカで大ベストセラーとなったJ・ゴーディの小説をもとに、J・サージェント監督が描いたハイジャックものの傑作。ニューヨークの地下鉄をハイジャックした４人の粗暴な男たちが、乗客を人質に100万ドルのキャッシュを一時間以内に持ってくるように要求した。これに対する警察官たちとハイジャッカーの対決を、スリリングかつエキサイティングな息をのむ演出で描く。迫力ある演技と放送禁止用語連発でハイジャッカーを演じるのはR・ショウ、T・ロバーツといった渋い男優陣。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若さま侍捕物手帖 べらんめえ活人剣", "若さま侍捕物手帖 べらんめえ活人剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "「地獄の皿屋敷」から続く、将軍から山岡家が拝領した呉須の皿をめぐる事件。ついに何者かによって皿が盗まれてしまう。探索を続ける若さまは、黒衣束一味と横川出羽守のつながりを発見して皿の奪取を企てるが、恋人のおいとがさらわれる。おいとの救出に向かう若さまだったが……。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("３−４x10月", "３−４x10月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「その男、凶暴につき」で好評を得た北野武による監督第２作。ヤクザに刺された草野球の監督の仇をとるため、拳銃を手に入れようと沖縄に渡った二人のチームメイト。彼らはそこで地元の暴力団からはみ出したヤクザと知り合いになるが……。大胆な二部構成、そして全く音楽を使用しない演出など、北野武の美学が感じられる。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モーツァルトの娘たち（仮題）", "モーツァルトの娘たち（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2011-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　RARE　BREED", "スタンピード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "英国人の未亡人マーサと娘ヒラリーが、セントルイスで一頭の牛を売り払った。カウボーイのサムは母娘を伴い、牛をテキサスに運ぶ役目を請け負うが、牛の横取りを狙う悪漢が現れて……。活劇派の職人A・マクラグレンが、J・スチュアート扮するカウボーイの活躍とロマンスを大らかなタッチで描き上げた西部劇。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I SHOT ANDY WARHOL", "I SHOT ANDY WARHOL", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "世界的なアーティストであるアンディ・ウォーホルが、1968年６月３日に熱狂的なファンに狙撃された事件を映画化した作品。ニューヨークのウォーホルのファクトリーにヴァレリーという若い女が入ってきて、ウォーホルに向けて32口径の銃弾を撃ち込む。外に出たヴァレリーは、警官の前に出頭し、“アンディ・ウォーホルを撃ったわ”と話す。ヴァレリーは不幸な少女時代を送り、レズビアンであることに気付く。彼女はすべての男を抹殺すべきだという宣言書を発表するが、相手にされず、ウォーホルなら分かってもらえると考えるようになる。ヴァレリーはウォーホルのファクトリーに出入りするようになるが、過剰な思い込みから、ウォーホルは出入りを禁じる。そして彼女は“最後の手段”に出た……。ビデオタイトルは「アンディ・ウォーホルを撃った女」。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("P2", "P2", "", "", "http://image.eiga.k-img.com/images/movie/53074/315x210.jpg?1859", 97, "残業を終え、地下駐車場に降り立ったアンジェラは、何者かに背後から麻薬を嗅がされ、意識を失う。目が覚めた彼女は手錠、足枷をされ、監禁されていた……。実話を基に製作された、誰もいない地下2階駐車場（P2）で起こる女性の拉致・監禁を描いたサスペンス・スリラー。「ハイテンション」「ヒルズ・ハブ・アイズ」のアレクサンドル・アジャが脚本・製作を務める。主演は人気TVシリーズ「エイリアス」のレイチェル・ニコルズ。", "2008-05-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nashville", "ナッシュビル", "", "", "http://image.eiga.k-img.com/images/movie/22124/315x210.jpg?2185", 159, "テネシー州ナッシュビルで開催されるフェスティバルにたくさんの参加者たちが集まってくる。イギリスからやってきたテレビのレポーター、様々な事情を抱えた歌手たちとその取り巻き、そしてファン、夫のもとから逃げてきた女性や旅の果てに流れ着いた者、さらに大統領候補とキャンペーンのスタッフ。聴衆が見守る中、コンサートが始まるが……。多彩な登場人物たちのエピソードが最後にはひとつに収束。その展開も見事な集団劇。2011年8月、特集上映「ZIGGY FILMS '70s '70年代アメリカ映画伝説　第2弾」でリバイバル。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Island", "アイランド", "", "", "http://image.eiga.k-img.com/images/movie/1094/315x210.jpg?2699", 136, "「アルマゲドン」「パール・ハーバー」のマイケル・ベイ監督が初めて大物プロデューサージェリー・ブラッカイマーと離れて監督するSFサスペンス。大気汚染から守られた清潔な都市で暮らす人々のあこがれは、日々行われる抽選で選ばれて、最後の楽園＜アイランド＞に行くこと。だが、主人公リンカーンは、その都市がクローン製造工場であり、アイランド行きの当選者は臓器を摘出されることを知り、都市を脱出しようとするのだが。", "2005-07-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Clear and Present Danger", "今そこにある危機", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "「レッド・オクトーバーを追え！」「パトリオット・ゲーム」に続く“ジャック・ライアン”シリーズ第３作。麻薬撲滅に成果を上げられないアメリカ大統領は、南米の麻薬カルテル一掃を指示。CIA副長官代行を任命されたライアンは大統領顧問としてカルテルの全容を調査していたが、ホワイトハウスの暗部では大きな陰謀が進行していた。議会の承認なしに他国へ軍隊を派遣するという、許されぬ行為である。事実を知ったライアンは単身コロンビアへ。ベストセラー作家T・クランシーが生んだヒーロー、ジャック・ライアンを、H・フォードが熱演。前２作よりアクション色が強められている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FRENCH LINE", "フランス航路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "3Dミュージカルとして製作されたJ・ラッセル主演作品。テキサスの女富豪メリーが、結婚相手を探し始めてニューヨークからはてはパリまで、大騒ぎの旅を続けていく。ラッセルのお色気と魅力を第一に、L・ベーコン監督が演出したコミカル・タッチの作品である。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("べらんめえ芸者", "べらんめえ芸者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "小春姐さんの親父・政五郎は大工の棟粱で昔気質の頑固者。その政五郎に大学出のインテリ・健一が弟子入りを志願してくる。政五郎は断るが、健一は勝手に居すわり、小春の心も揺れ動く。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIVE DAYS", "殺しの代償", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "会社社長ジェームズは、事業拡大の融資先としてあてにしていたマクガワン氏が、メキシコから帰ってこないため、あせっていた。万策尽きたジェームズは、愛妻アンドレアのため自分に掛けた保険金を残そうと、自分自身の殺人依頼を幼なじみのポールに頼む。執行猶予期限は５日間……。だがその時、皮肉にもマクガワン氏が帰国したのだ。計画中止を告げようとポールを探すが、その消息がつかめない。ジェームズは命を狙われることに！　次々と忍びよる暗殺者の影から、必死で逃れようとする男の恐怖の５日間を描いた傑作サスペンス。", "1954-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("燃えよ剣", "燃えよ剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "司馬遼太郎の同名小説を加藤泰が脚色し、TVでも土方歳三を演じた栗塚旭を主演に映画化した幕末もの。剣にも女にも強い青年剣士・土方歳三はある日、生涯の敵・七里研之助とめぐり会う。時は流れ、新選組の一員となった土方は。池田屋襲撃で七里と相対する。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GONE TO EARTH", "女狐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ヘイゼルはジプシーの血を引く野性的で気性の激しい娘。彼女の心を二人の男が虜にした。一人は荒々しく強引な大地主の息子ジャック、そしてもう一人は知的で物静かな若い牧師エドワード。やがて悲劇的な結末が訪れる。主演のジョーンズをはじめアカデミー受賞者陣で固めた文芸大作。", "1950-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("曼陀羅", "曼陀羅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "実相寺昭雄が「無常」に続いて、ATGで撮った作品。農耕とエロスを柱にしたユートピアを実現しようとする人物と、そこに紛れ込んだ一組のカップルを通して、日本的無常観と革命幻想を描く。大胆な性描写で、公開当時は“芸術ポルノ”と騒がれた。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRUMPIER OLD MEN", "ラブリー・オールドメン　釣り大将LOVE LOVE日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "W・マッソー、J・レモンの名コンビでヒットした「ラブリー・オールドメン」の続編。ジョンとマックスは、釣りとケンカが生きがいのケンカ友だち。しかし、新婚生活を満喫しているジョンに対して、マックスは少々欲求不満気味。そこにセクシーなイタリア女マリアが現れ、彼らが心の拠り所としている釣具店を買い取り、レストランに改装すると言い出した。ジョンとマックスは一致団結し、様々な妨害工作を始めるが、もちろんマリアも黙っちゃいない。そして、壮絶なバトルは意外な結果をもたらすのだった。W・マッソー、J・レモン、S・ローレンといった、往年の大スターたちのコミカルでゴージャスな演技が冴えるハートフル・コメディ。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eight Below", "南極物語（2006）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "1983年に公開され、日本中に感動を呼んだ高倉健主演の名作「南極物語」を、アメリカのディズニー・スタジオがリメイク。全米科学財団の南極基地で働く青年ジェリーは、深いきずなで結ばれた8頭のイヌぞり犬たちとともに、これまで数々の困難を乗りこえてきた。ところが、南極を襲った記録的な猛吹雪により、基地のスタッフが全面退去させられることに。ジェリーは後ろ髪を引かれながらも、イヌたちを残して南極を後にするが……。主演は「ワイルド・スピード」のポール・ウォーカー。", "2006-03-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("観察／永遠に君をみつめて", "観察／永遠に君をみつめて", "", "", "http://image.eiga.k-img.com/images/movie/34314/315x210.jpg?1691", 133, "「イヌゴエ」シリーズの横井健司監督が緒川たまき主演で贈る大人の純愛物語。愛する相手を“見続けた”男と、その存在に気付きながらも“見せ続けた”女の40年に渡る愛の行方を、男女それぞれの視点から2部構成で描く。10歳の少年・茂樹は、丘の上の家に住む少女・弥生に恋心を抱く。それ以来毎日、彼は望遠鏡で彼女の姿を覗くように。やがて大人になって弥生は結婚し、茂樹も妻子を持つ。それでも茂樹は、弥生を見ることをやめられずにいた……。", "2007-11-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニッポン珍商売", "ニッポン珍商売", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "東京オリンピックを狙った珍商売の乱立時代。スキヤキ屋の良造と名古屋で商売を営む兼作、大阪で示談屋として売り出したまさるらは次々と奇想天外な珍案、奇案を生み出しては金もうけをたくらむのだが……。ラストのライバル同士の攻防戦が面白い爆笑喜劇。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CURACAO", "カリブの渇いた銃弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "カリブ海の孤島を舞台に、ハードな“過去”を持つ二人の男が、南アフリカと中国のマフィアに立ち向かうサスペンス・アクション。二重スパイの同僚を殺した元CIAエージェントのゲーリンは、唯一の友人ウェッタリングが、マフィアに狙われていることを知り、その友人の危機を救うべく立ち上がる……。保険金のため、自分のタンカーを沈めた元船長のウェッタリングを、G・C・スコットが怪演。ゲーリン役のW・L・ピーターセンとともに“渋い”男のドラマを盛り上げている。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BODY PARTS", "ボディ・パーツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "片腕を失った精神科医のビルが、他人の腕を移植されたことにより、次々と体験していく悪夢。自らの肉体の反乱といった内部からの恐怖、身体の一部分が、前の細胞と同化しないという閉ざされた恐怖を題材にした衝撃のホラー。移植がもたらす心理的な現象を中心に、残酷描写と重々しいムードを両立させたシリアスな作品になっている。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄変", "地獄変", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "芥川龍之介の同名小説の映画化。平安朝を舞台に、時の権力者である貴族と、地獄絵に憑かれた絵師との葛藤を様式美たっぷりに描く。憑かれたようにリアルな地獄絵を追求する絵師の仲代達矢と、高慢な貴族の中村錦之助が演技の火花を散らす。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五番町夕霧楼", "五番町夕霧楼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "原作は水上勉の同名小説でいわゆる金閣寺放火事件を題材とした作品である。同じ題材による三島由紀夫の『金閣寺』は放火犯の青年を中心に据えた話だったのに対し、この作品では青年が通った廓の女・夕子を主人公として扱っている。夕子は実家が貧しかったため京都五番町の夕霧楼に年若くして身売りされた後、西陣の織元に従った。ある日、学生風の陰気な男が楼に上るようになり、以来必ず夕子と一夜をともにする。青年は鳳閣寺の修行僧・櫟田正順で夕子の幼なじみ。正順は吃音障害だったために誰からも相手にされず、夕子だけが優しく正順をかばうのだった。織元の密告で正順は折檻を受け、その腹いせに寺に火を放って自殺。それを知った夕子も故郷の丘の上で命を絶つ。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lord of the Rings: The Fellowship of the Ring", "ロード・オブ・ザ・リング", "", "", "http://image.eiga.k-img.com/images/movie/1114/315x210.jpg?2638", 178, "世界的ベストセラーであるJ・R・R・トールキンの「指輪物語」3部作の映画化、第1作。監督は「乙女の祈り」のピーター・ジャクソン。イライジャ・ウッド、イアン・マッケラン、ケイト・ブランシェット、リブ・タイラー、クリストファー・リーといった豪華なキャストも話題。世界を滅ぼすパワーを持つ指輪を、遥か彼方にある火山の亀裂に捨てるため、幾多の危険を乗り越えて旅をするホビット族の小さき青年フロドと仲間たちの物語。", "2002-03-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("King Kong", "キング・コング", "", "", "http://image.eiga.k-img.com/images/movie/1104/315x210.jpg?2255", 188, "「ロード・オブ・ザ・リング」3部作のピーター・ジャクソン監督が、映画監督をめざす契機となった1933年の「キング・コング」をリメイク。オリジナル作と同じ30年代を舞台に、美女と野獣の感動ドラマを最新映像技術で映像化。スタッフは「ロード・オブ・ザ・リング」と共通。「マルホランド・ドライブ」のナオミ・ワッツ、「戦場のピアニスト」のエイドリアン・ブロディ、「スクール・オブ・ロック」のジャック・ブラックらが共演。", "2005-12-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("安心して老いるために", "安心して老いるために", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 151, "ドキュメンタリー作家、羽田澄子が「痴呆性老人の世界」に続いて作り上げた老人問題に関する記録映画。ここでは、社会における老人福祉にスポットをあて、岐阜県池田町にある“サンビレッジ新生苑”という特別養護老人ホームに取材。日本の福祉の現状から、デンマーク、スウェーデン、オーストラリアといった外国の様子までを含めて紹介している。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SFホイップクリーム", "SFホイップクリーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「MOON DHILD」の瀬々敬久監督がソ連製SF映画「不思議惑星キン・ザ・ザ」に触発され脚本を書き上げ、自らメガホンを執った異色SFドラマ。地球に不法滞在する宇宙人に拾われたKENは、今は一人でクスリの売人をして生活をしている。KENが25歳になったある日、彼はクスリの売上金をごまかしていたことが組織に発覚。自分の身を守るために麻薬Gメンに駆け込むが、故郷の星に強制送還されることになってしまった。育ての親が出身の、自分は見たこともない星へと結局やってきたKENは、その“故郷”のハチャメチャな現実を目の当たりにする。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCAM", "カリブ 愛欲の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ホテルのバーで、金持ちのビジネスマンをカモにして生計を立てている美女マギーは、ある夜出会った元FBIのスパイ、ジャックに弱みを握られ彼の企みに巻き込まれてしまう。陽光輝くカリブ海を舞台に、セックスとサスペンスが交錯する。C・ウォーケンが、目的のためには危険なやり方をもはばからないアンチ・ヒーローを演じている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽は狂っている", "太陽は狂っている", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "吉永小百合＝浜田光夫の青春コンビによる異色青春ドラマ。大学生・圭一はふとしたことからチンピラと友だちになり、やがてカツアゲをするようになる。ある日、カツアゲしようとした女子学生・知里の美しさに、圭一は心を惹かれる。舛田利雄がのちの吉永＝浜田コンビの青春ものとは一味違う辛口の作品に仕上げた。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("One Day in September", "ブラック・セプテンバー　五輪テロの真実", "", "", "http://ecx.images-amazon.com/images/I/316582GRBGL._SL210_.jpg", 91, "1972年ミュンヘンオリンピックで起こった、パレスチナのテロ集団「ブラック・セプテンバー」によるイスラエル選手団襲撃事件の詳細を追ったドキュメンタリー。第72回アカデミー賞で長編ドキュメンタリー賞を受賞した。", "1999-01-01", "26,7,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("M. Butterfly", "Mバタフライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "　1964年、北京。フランス大使館の外交官ガリマールは京劇の歌姫ソンに心を奪われてしまう。妻の目を盗み、ソンとの逢瀬を重ねるガリマールは公私ともに充実した日々を送る。しかしガリマールはソンの正体に気づいていなかった。ソンは京劇俳優として堕落の罪に問われ、償いのために女に成りすまし、スパイ活動をしていたのだ……。実話を戯曲化し、トニー賞に輝いたヒット作をクローネンバーグ監督が映画化。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トワイライトシンドローム・卒業", "トワイライトシンドローム・卒業", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "大ヒットした同名のプレイステーション用ゲームをベースにした学園ホラー。高校３年の友里は最後の夏休みを迎えようとしている。仲のよい友人たちと過ごすはずだったが、ある儀式をしてしまったために、平穏だった学生生活は一変。友里と仲間たちに次々と悪霊が襲いかかる……。全編をデジタルビデオで撮影したことが話題に。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("成瀬巳喜男／記憶の現場", "成瀬巳喜男／記憶の現場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "'05年で生誕100年を迎えた巨匠、成瀬巳喜男の“現場”を知るスタッフや俳優にインタビューした記録映画。美術助手、衣装担当、助監督、編集助手、照明助手、録音助手、撮影監督、音響効果担当、そして淡島千景、草笛光子、小林桂樹、司葉子が多角的に成瀬の映画世界の“核”と本質を語り明かしていく。「乱れ雲」に主演した司葉子が語り、「鰯雲」の草笛光子が成瀬の眠る円光寺で思い出す。成瀬を知る人々によって彼の“宇宙”が明らかになる。", "2005-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELLRAISER: INFERNO", "ヘルレイザー／ゲート・オブ・インフェルノ", "", "", "http://ecx.images-amazon.com/images/I/515qwFXzAkL._SL210_.jpg", 99, "デンバー警察のダーティーな刑事ジョセフが、殺人現場でパズルボックスを発見。これをきっかけに彼は、相次ぐ猟奇事件に苦悩し、魔道士に襲われる悪夢にうなされていく。おなじみの怪物キャラクター、ピンヘッドを登場させてシリーズのエッセンスを踏襲しつつも、ハードボイルド・ミステリー風の新たな展開を試みたホラー・シリーズ第５作。", "2000-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRAIGHT OUT OF BROOKLYN", "ストレート・アウト・オブ・ブルックリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "スパイク・リー、ジョン・シングルトンらと並び、1990年代の黒人監督作品ブームの一翼を担った作品。監督のM・リッチは弱冠19歳で本作を作り上げ、サンダンス・フィルム・フェスティバルでは審査員特別賞を獲得している。貧困に喘ぐブルックリンの黒人街。デニスはここから抜け出すことをいつも考えている。大酒を飲み白人社会の欺瞞を口にする父は、母に暴力を振るうばかり。貧困から家族生活の崩壊を救うべく、デニスは仲間とともに麻薬密売人から現金を強奪しようとする。首尾よく、計画は成功。だが、この事件は彼の家族をさらに悲劇的な状況へ追い込んでいく……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南の島に雪が降る", "南の島に雪が降る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "1975年に死亡した俳優、加東大介が自らの戦争体験をまとめた原作をもとに映画化。1995年、ニューギニアのジャングルで日本兵の須藤が発見され、戦友の叶谷が迎えに行く。迎えのヘリを待つ間、須藤はなぜジャングルに残ったかを話し始めるのだった。戦争が終わったことを知った時、仲間を戦死させた司令官たちを殺し、自決するという村井中尉と須藤は対立、須藤の説得に負けた村井はその場で自決してしまう。そのことで、須藤はこの地に一人残り、仲間の霊を慰めることを決意したのだった。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンの誉れの土俵入", "エノケンの誉れの土俵入", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "時は江戸時代。エノケン扮する力の強い田舎の若者が、一人前の相撲取りを目指し、許婚を残して江戸へ行く。彼は兄弟子の尽力もあって、メキメキ腕を上げる。そして、晴れの舞台で兄弟子の仇敵と一騎打ち。駆けつけた許婚の声援も実って、勝ち名乗りを受け、メデタシメデタシ、という出世話。中川信夫監督としては「エノケンの弥次喜多」と並ぶエノケン喜劇の成功作。監督自身、エノケンとのコンビは全部ダメと謙遜しつつ、この作品のみ“チョット面白かった”と語っている。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二十四城記", "四川のうた", "", "", "http://image.eiga.k-img.com/images/movie/54234/315x210.jpg?3524", 112, "「世界」「長江哀歌」のジャ・ジャンクー監督が、四川省・成都にある巨大国営工場の閉鎖と、そこで働いてきた労働者たちの姿を通して、現代中国の半世紀を振り返る壮大な叙事詩。実際の工場の風景や労働者たちへのインタビューといったドキュメンタリーと、架空の労働者を俳優たちが演じるフィクションを融合させたセミドキュメンタリー形式で構成。フィクションパートではジョアン・チェン、チャオ・タオらが出演。", "2009-04-18", "8,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猫の息子", "猫の息子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "『太陽にほえろ！』『あぶない刑事』などのTVドラマの脚本を手掛けた柏原寛司の初監督作。異才・花村萬月の同名小説をもとに、心の拠り所を求めて絡み合う男たちの姿をハードボイルド・タッチで描く。猫と呼ばれる私立探偵の父の借金を返すため、バーテンダーとして働く青年タケは、ある日、総会屋の男、鷲尾と出会う。鷲尾の家に住みつく家出少女は、彼を父親のように慕っている。鷲尾のカリスマ性に惹かれ、犯罪に手を染めていくタケは、やがて父と鷲尾の因縁めいた関係を知らされ……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.BASEBALL", "ミスター・ベースボール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "現役の大リーガー、ジャックが日本の中日ドラゴンズにやって来た。しかし頑固者の監督、内山とプライドの高いジャックは初めから衝突。ところがジャックは内山の娘ヒロ子と恋に落ちてしまう……。プロ野球界を舞台に、日米の文化のギャップを超えて理解し合っていく人々の姿をさわやかに描いていく。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛の黙示録", "愛の黙示録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "戦後、韓国で孤児たちのために愛を注いだ日本人女性の波乱の生涯を力強く描き出した人間ドラマ。1938年、韓国の木浦（モッポ）で孤児院を営む韓国人青年と結婚した田内千鶴子。やがて日本は敗戦、夫も行方不明になり過酷な運命にほんろうされる。しかし彼女は激しい迫害のなか、女手一つで必死に孤児たちを育てていく。“韓国孤児のオモニ（母）”と慕われた実在の女性、韓国名ユン・ハクチャを石田えりが熱演。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AU BORD DU LAC", "湖のほとり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "スイスの映像作家ロッドの“残酷物語”３部作の第１話。ある晩、アンドレは妻とともに映画を観に行くが、なぜか妻は途中で帰ってしまう。一人残された彼は、画面の中に妻の姿を見つけ愕然とする……。現実と心象風景が交差するシュールな映像美が絶妙。", "1979-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Dark Knight Rises", "ダークナイト・ライジング", "", "", "http://image.eiga.k-img.com/images/movie/56094/315x210.jpg?4256", 0, "「バットマン・ビギンズ」「ダークナイト」に続く、クリストファー・ノーラン監督が手がける「バットマン」シリーズ第3作。「ダークナイト」から8年後を舞台に、ゴッサム・シティを破壊しようとする残虐な殺し屋ベインと戦い、謎に包まれたキャット・ウーマン／セリーナ・カイルの真実を暴くバットマン／ブルース・ウェインの姿を描く。主演のクリスチャン・ベールのほか、新キャストとしてアン・ハサウェイやノーラン監督の前作「インセプション」にも出演したトム・ハーディ、ジョセフ・ゴードン＝レビット、マリオン・コティヤールらが参加する。", "2012-07-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Mother", "パッション", "", "", "http://ecx.images-amazon.com/images/I/51VOxJ8KfxL._SL210_.jpg", 112, "", "2003-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Killing of Sister George", "甘い抱擁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "　テレビドラマでジョージという看護婦役を演じ人気を博していた女優のジューン。情緒不安定の上、酒癖も悪かったジューンは番組を降板させられるかもしれないという強迫観念にとらわれていた。同性愛者だった彼女は同棲中の恋人アリスに八つ当たりをし、スタジオでもトラブルが絶えない。そんな折、ついにジューンの降板が決定。さらにアリスの心にも変化が……。中年女優の愛憎が、テレビ界の内幕を絡めながら鮮烈なタッチで描かれる。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("凶銃ルガーP08", "凶銃ルガーP08", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "大藪春彦の同名小説の映画化。ナチス・ドイツが使用し、以後は手にした者が必ず殺人を犯すという、呪われた拳銃ルガーP08。偶然その銃を拾った商社マン、土井士郎は、やがて銃の狂気に取り憑かれていき……。普通の商社マンから殺人者へと変貌していく士郎を、これまでのイメージを脱ぎ捨てたかのような阿部寛がワイルドに好演。撮影中は、松田優作の「野獣死すべし」の演技を参考にしたとか。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BAD NEWS BEARS IN BREAKING TRAINING", "がんばれ！ベアーズ 特訓中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "大ヒット作「がんばれ！ベアーズ」の続編。いまやリトルリーグ界でも強豪の仲間入りをしたベアーズにアストロドームでの大試合のチャンスがめぐってきた。新入り投手カーメンの加入でチーム力アップかと思われたベアーズだが、そのカーメンの大乱調で逆にガタガタ。果たしてベアーズは立ち直れるか？", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SONS AND LOVERS", "息子と恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1910年、イギリスの炭鉱町ベストウッド。モレル家の大黒柱として働くウォルターは、次男ポールを坑内夫にしようとしていたが、ポールは画家になろうとしていた。やがて彼の絵は賞を取り、パトロンの援助のもと、ロンドン行きを勧められるのだが……。D・H・ロレンスの名高い原作を映画化した文芸ドラマ。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Forgotten", "フォーガットン", "", "", "http://image.eiga.k-img.com/images/movie/1394/315x210.jpg?3337", 92, "「めぐりあう時間たち」「エデンより彼方に」のジュリアン・ムーアのサスペンス。9歳の息子サムを事故で失ったテリーは、14カ月経った今もその衝撃から立ち直れない。だが、ある日、家族3人の写真から息子の姿が消え、精神科医は彼女に「息子はあなたの妄想の産物だ」と言うが……。監督は「危険な遊び」のジョセフ・ルーベン、脚本は「フェノミナン」のジェラルド・ディペゴ、撮影は「モナリザ・スマイル」のアナスタス・ミコス。", "2005-06-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死ガ二人ヲワカツマデ…　第一章　色ノナイ青", "死ガ二人ヲワカツマデ…　第一章　色ノナイ青", "", "", "http://image.eiga.k-img.com/images/movie/58084/315x210.jpg?4195", 0, "映画初出演となる人気ビジュアル系エアバンド「ゴールデンボンバー」の喜矢武豊と、人気声優の野水伊織が主演するアクション。生きることに執着のない男メイファは、殺し屋を志願する少女・海と出会い、ひかれ合う。しかし、メイファと殺しあう宿命を背負った男・黒騎や兎黒が2人の前に現れ、壮絶な戦いが幕を開ける。人気アイドルや声優、舞台役者など多彩なキャストの共演が実現した「死ガ二人ヲワカツマデ…」全2章の第1章。", "2012-09-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES NOVICES", "パリは気まぐれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "フランスの二大女優バルドーとジラルドが共演したコメディ。修道院から脱け出した若い尼僧がパリで“モナリザ”と呼ばれる娼婦と知り合い、夜の女へ転身する。中年女二人の色気があふれんばかりの１作。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("可愛い悪女 殺しの前にくちづけを", "可愛い悪女 殺しの前にくちづけを", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "業界の一線で働くプレイガールに范文雀が扮し、殺人事件を追うシリーズ第２作。今回のヒロイン・リルは売れっ子グループ・サウンズのリード・ボーカルだ。リルは友人の花絵がワルと結婚させられる危機を救うため、ワルに近づいて自ら濡れ場を演じ、その現場写真を花絵の父に送り付けようともくろむ。ところが、このワルが翌朝殺害されているのを発見され、アリバイのないリルに殺人の容疑がかかり……。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLACE IN THE HEART", "プレイス・イン・ザ・ハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "1935年テキサス。夫を酔っぱらいの黒人に殺されてしまい、その日から一家の柱として生きていかねばならないはめに陥ったエドナ。世間の冷たい目に耐えながら明るく生きていく女性像をフィールドが好演した感動のヒューマン・ドラマ。広大な綿畑を捉えたカメラも絶妙だ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ERRAND　BOY", "底抜け便利屋小僧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "喜劇の王様J・ルイスがハリウッドを笑い飛ばした爆笑コメディ。バーミューチャル映画スタジオで働くモーティは社長に呼ばれ、社員のサボリ具合を監視する便利屋となった。ところが、撮影したフィルムをオシャカにしたり、セットを台無しにしたりなどヘマの連続。とうととうモーティは社長にクビを宣告されそうになるが……。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SANGER　FRAN　ANDRA　VANINGEN", "散歩する惑星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ヨーロッパCF界の巨匠ロイ・アンダーソンが撮影に４年を費やしたという異色作。ある惑星で続発する不条理なトラブルが、アナログな手法による視覚効果を駆使した鮮烈な映像で描かれる。保険金欲しさに自分の家具屋に火を放った男カール。彼の二人の息子トーマスとシュテファンもそれぞれ問題を抱えていた。やがて町には不穏なムードが広がり、現実から逃げ出そうとする人々が空港などに殺到して……。", "2000-01-01", "14,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Albert Nobbs", "アルバート・ノッブス", "", "", "http://image.eiga.k-img.com/images/movie/57354/315x210.jpg?4086", 113, "「彼女を見ればわかること」「美しい人」のロドリゴ・ガルシア監督が、自由を得るために男性として生きなければならなかったひとりの女性の姿を描いたドラマ。独身の女性が自立して生きることがまだ許されていなかった19世紀アイルランドのダブリン。人付き合いを避けて静かに暮らす内気な執事アルバートは、貧しく孤独な生活から逃れるため、性別を偽り男性として生きる道を選んだ女性だった。しかし、ある日、ハンサムなペンキ屋のヒューバートと出会ったことから、アルバートは自らを偽り続けて生きることに悩み始める。主演のグレン・クローズが2011年・第24回東京国際映画祭で最優秀女優賞を受賞。共演に「アリス・イン・ワンダーランド」のミア・ワシコウスカ、「キック・アス」のアーロン・ジョンソンら。", "2011-01-01", "33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CUJO", "クジョー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "自動車の修理にきた母子が、狂犬病になった修理工場の飼い犬クジョーに襲われ、炎天下、車中に閉じ込められてしまう。映画化の難しい、モダン・ホラーの巨匠キングによる恐怖心理小説の映画化に挑戦した動物パニック・サスペンス。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("心理学者　原口鶴子の青春 100年前のコロンビア大留学生が伝えたかったこと", "心理学者　原口鶴子の青春 100年前のコロンビア大留学生が伝えたかったこと", "", "", "http://image.eiga.k-img.com/images/movie/53524/315x210.jpg?1837", 95, "日本人女性初の心理学者・原口鶴子の生涯にスポットを当てたドキュメンタリー。1907年にニューヨークに渡った鶴子は、コロンビア大学で心理学博士号を取得。帰国後に29歳という若さでこの世を去ったが、彼女が残した研究は高い評価を受け、その国際性や自立精神は後の女権運動にも影響を与えることとなった。自身もニューヨークへの留学経験を持つ女性監督・泉悦子が、鶴子本人の手による留学記「楽しき思い出」を基に彼女の軌跡を辿る。", "2008-04-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／THE　SCANDAL", "スキャンダル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "ラクロの有名な恋愛小説『危険な関係』を、李王朝時代の貴族社会に舞台を置き換えて映画化。夫の死後も貞節を守り続ける未亡人と、あの手この手で彼女を誘惑する青年の運命が、色鮮やかな美術と衣装が彩る映像世界の中に描かれていく。TVドラマ『冬のソナタ』でブレイクしたペ・ヨンジュンが、冷徹なプレイボーイに扮してイメージを一新している。政府高官長官の妻、チョ夫人が、従兄弟のチョ・ウォンにある賭けを持ち掛ける。それは、夫の若い側近を妊娠させること。冷血好色のチョ・ウォンはそれでは飽き足らず、貞淑な未亡人をものにできたら褒美をもらうことにするが……。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sad Movie", "Sad Movie サッド・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/33734/315x210.jpg?3946", 109, "8人の男女に訪れる様々な別れの瞬間を描く、韓国発のオムニバスドラマ。自分の気持ちを上手く伝えられない消防士と彼からのプロポーズを待ち続ける女性、先の見えない恋愛に見切りをつけた女性と彼女の心を取り戻すために“別れさせ屋”を始めた青年など、“別れ”にスポットを当てた4つのエピソードが展開していく。「私の頭の中の消しゴム」のチョン・ウソン、「僕の、世界の中心は、君だ。」のチャ・テヒョンら韓国を代表する俳優たちの競演も見所。", "2006-11-11", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INVASION EARTH ： THE ALIENS ARE HERE", "インベージョン・アース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "B級SF映画を映画館で上映しながら地球を侵略しようとする宇宙人と、それを阻止する子供たちを描いたハチャメチャなSF。作品中、“ゴジラ”“ラドン”“半魚人”をはじめ、おなじみのSF映画が35本も次々と登場する、1950年代SF映画の集大成、ならびにオマージュ的作品。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("求人旅行", "求人旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "箱根から、東京、大島、東海道、そして大阪へと、旅館の従業員探しの旅に出た、２組のカップルの恋の行方を描いた喜劇。箱根の旅館“翡翠苑”の女主人、松山千代子は、新館増築に伴う従業員探しのため上京する。彼女の手助けは、求人スカウト会社の丹野。二人はいがみ合いながらも、次第にうちとけていって……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Open Season", "オープン・シーズン", "", "", "http://image.eiga.k-img.com/images/movie/33764/315x210.jpg?3641", 86, "人間に育てられた甘えん坊のクマが、生まれて初めて訪れた森で動物たちと大冒険を繰り広げるCGアニメーション。パークレンジャーのベスに育てられたクマのブーグは、町で幸せな毎日を送っていた。ところが、ある日ハンターに捕らえられたお調子者のシカ、エリオットを助けたことから、ブーグはオープン・シーズン（狩猟解禁）直前の森へ追い込まれてしまう。声の出演はマーティン・ローレンス、アシュトン・カッチャーほか。", "2006-12-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の歌を聴け", "風の歌を聴け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "村上春樹の群像新人文学賞受賞作を大森一樹が映画化した青春映画。大学生の僕は夏休みに帰郷し、毎晩、友人の鼠とジェイズ・バーでビールを飲んで過ごす。ある日、僕は飲み過ぎて意識をなくしてしまった左手の小指のない女の子と知り合うのだった……。原作にあるどこか空虚で無機質な味わい、そこに漂うロマンチシズムを大森は映像化しようとしているが、指からこぼれ落ちたような印象を受ける。ただ、都会派の青春映画の登場という意味では、まさにニュー・タイプの作品だ。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("МОЙ ДРУГ ИВАН ЛАПЩИН", "わが友イワン・ラプシン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "旧ソ連映画界の実力監督ゲルマンが、実父の小説をモチーフに、1930年代のソ連の庶民生活をビビッドに再現。監督自身と思われる少年の目を通し、革命をとユートピアを信じる反面、物質的にも精神的にも過酷だった時代を、リアルに情感を込めて描き出す。1986年ロカルノ国際映画祭銅豹賞、FIPRESS賞受賞作。", "1984-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WARZAWA ANNE´E", "「彼女」の存在", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "第二次世界大戦下のポーランド・ワルシャワ。ジェノサイドに震えるユダヤ人たちのゲットーから逃げ出した男女は、一人のドイツ女に匿われる。保身のため結婚していることを隠す男を、やがてドイツ女は愛するようになるが、一緒に逃げ延びた女はそれに嫉妬する……。生命を賭したギリギリの逃避行を物語の縦軸に据えながら、ナチス・ドイツのユダヤ人虐殺を怒りをこめ告発する。地下水道を伝っての脱出シーンや室内の緊迫した空気など、緊張感あふれる映像を紡ぎ出したJ・キユフスキ監督の演出が随所に輝きを見せている。", "1992-01-01", "7,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WOMAN IN THE WINDOW", "飾窓の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "犯罪心理学の教授は初老に入ってすでに功なり、アバンチュールを楽しむほどの冒険心もなく、単調な毎日を送っている。そんな男がショーウインドウに飾られた絵のモデルに強く惹かれる。ある夜、いつもは１杯の晩酌が３杯になって足元をふらつかせて路上に出ると、あの絵の女がウインドウに反射する位置に立っているではないか。こうして教授は女と運命的な出会いを果たし、犯罪に巻き込まれ、やがて、のっぴきならない事態に追い込まれていく。しっとりと濡れた夜のムード、タバコの火を求める宿命の女、雨の林の死体遺棄、凄腕の刑事、第三者の恐喝、そして追い詰められた末の第２の犯行——。そんな映画的なお膳立てに加え、伏線の効いたストーリーテリング、無駄なく揺るぎのない巨匠の演出、とすべてが一級品の犯罪サスペンス。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Plaza Suite", "おかしなホテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "プラザホテルの一室を舞台に、３組の客が繰り広げるオムニバス・コメディ。サイモンの脚本をヒラーが軽妙に演出した。結婚の思い出にホテルへやって来た実業家、プレイボーイのプロデューサー、娘の結婚に悩む父親の３役をマッソーが好演。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOEL−PETER WITKIN：L’IMAGE INDE´LE´BILE", "ジョエル・ピーター・ウィトキン 消し去れぬ映像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "人間や動物の死体、ちぎれた腕や足、そしてフリークスなど、表の世界からうち捨てられたようなものを被写体に写真を撮り続ける写真家ジョエル・ピーター・ウィトキンの撮影風景を中心に構成されたドキュメンタリー。なぜ彼が一般的には美しいとはいわれないものを好んで撮影するのか、その秘密が明かされていく。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VICE VERSA", "ボクとパパの大逆転 バイス・バーサ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "高級デパートのバリバリ副社長で仕事一筋の真面目な父と、小学生のワンパク息子が、あるとき魔法のガイコツを触ったために互いの体が入れ替わってしまい……。「ビバリーヒルズ・コップ」のJ・ラインホールドが“入れ替わり”役にチャレンジ。スピード感とユーモアセンスあふれる作品となっている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女ざかり", "女ざかり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "入社20数年目にして論説委員を命じられた南弓子は、一人娘と暮らす“女ざかり”の新聞記者。ところが初めて書いた社説が、政界の暗部を刺激して騒動に……。同僚との淡い恋、大学教授との不倫、別れた夫への思慕など、周りの男たちとの関係を通じて、吉永小百合が“女性としての輝き”を見せるドラマ。常時３台のカメラを回し、大林宣彦らしい短いカットでたたみかける、凝りに凝った編集技法が駆使されている。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE´OLO", "レオロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "自分をトマトから生まれたイタリア人と思い込んでいる、カナダの少年レオ。空想癖の強い彼は、風変わりな自分の家族をモデルに小説の執筆を始める。そんな少年の屈折した成長を、虚構と現実を織り交ぜてユーモラスに描く。J・C・ローゾンによる、不思議な味わいのファンタジーだ。", "1992-01-01", "10,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARQUIS DE SADE’S JUSTINE", "マルキ・ド・サドのジュスティーヌ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "サディズムの語源にもなったM・D・サドの『美徳の不幸』を映画化した問題作。身寄りのない可憐な処女ジュスティーヌが行き着いた先は、倒錯的な悪の花園。ゲイやサディストが集うなか、彼女は純潔を守っていくが、姉ジュリエットは悪徳に身をまかせ獣欲の日々を送るのだった。", "1968-01-01", "7,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とびだす絵本3D", "とびだす絵本3D", "", "", "http://image.eiga.k-img.com/images/movie/55614/315x210.jpg?3905", 30, "30年以上にわたり子供たちに愛され続けている名作絵本「ねずみくん」シリーズをはじめ、動物たちの跳ねる姿が楽しい「ぴょーん」、思わず参加したくなる「しりとり」など全5作品のロングセラー絵本を3D映画化。昔ながらのシンプルな読み聞かせと、最新鋭の3D技術を融合させた新感覚アニメーション。「キャンディ・キャンディ」の主題歌などで知られる歌手・声優の堀江美都子が歌とナレーションを担当する。", "2010-10-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CHARGE OF THE LIGHT BRIDAGE", "進め竜騎兵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "クリミア戦争を背景に、虐殺に激怒したイギリス竜騎兵隊一個師団がロシアの要塞に突撃、壮絶な戦闘を繰り広げる。桂冠詩人アルフレッド・テニスンの詩に想を得た壮大な活劇。悲壮ともいえるラスト・シーンが語り草となった。E・フリンとO・デ・ハヴィランドの名コンビの共演に加え、錚々たる名優が脇を固める。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PASSENGER", "パッセンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ヴォーグ誌などで活躍する写真家フランソワ・ロトゲールが長編映画に初挑戦した日仏カナダの合作。一人の青年が愛する人に会うため、東京から北米へと旅するロード・ムービー。日本映画とはひと味違う、浮遊感ただよう東京郊外の景色にも注目だ。愛し合いながらも、引き裂かれてしまった幼馴染のコウジとヒロコ。だがヒロコの父親・三道は、自分を裏切ったカナダ人を見つけることを条件に、コウジとヒロコに再会を許すと申し出る。", "2005-01-01", "2,10,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Penelope", "ペネロピ", "", "", "http://image.eiga.k-img.com/images/movie/53354/315x210.jpg?4324", 102, "クリスティーナ・リッチが豚鼻の少女に扮する奇想天外なロマンティック・コメディ。名家のお嬢様ペネロピは、先祖が魔女にかけられた呪いのせいで、生まれた時から豚のような鼻を持っている。彼女は自分の顔を見ても逃げなかった唯一の青年マックスに恋をするが、実は彼の正体はペネロピを狙う新聞記者が送り込んだスパイだった……。共演は「ラストキング・オブ・スコットランド」のジェームズ・マカボイ、リース・ウィザースプーンほか。", "2008-03-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄龍〈イエロードラゴン〉", "黄龍〈イエロードラゴン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "国際派アクション・スター、倉田保昭が製作総指揮から原案、脚本、主演までを兼ねて挑んだ意欲作。華麗なワイヤー・ワークと生身の身体を使ったアクション・バトルが見ごたえあり。突然何者かに拉致されたところをゴウという男に助けられた純。家に戻ったものの養父母が殺されていた彼女は再びゴウのもとへ。そこで彼女は自分の体が特殊な抗体を持つことを思い出す。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'ora di religione", "母の微笑", "", "", "http://image.eiga.k-img.com/images/movie/55434/315x210.jpg?1631", 102, "イラストレーターとして成功を収めた無神論者のエルネストは、兄に母親を殺害された過去を持つ。ある日、エルネストは親族の強い希望で、母が教会の聖者の列に加えられることを知る。親戚中が利益を得ようとエルネストに協力を求めてくるが、彼はそれを受け入れられずにいた。監督は「肉体の悪魔」「夜よ、こんにちは」のマルコ・ベロッキオ、主演にセルジョ・カステッリット。2002年のカンヌ国際映画祭で審査員賞を受賞した。イタリア映画祭2010にて上映。", "2010-05-02", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WE’RE NO ANGELS", "俺たちは天使じゃない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "フランスの作家ユッソンの戯曲の映画化。ゆきずりの雑貨屋を訪ねた３人の脱獄囚は、人のよい店の主人に雇われることになる。彼はもともとパリに住む従兄の店の支配人だったが、無能のためこの港町に追い出されているのだった。その従兄が帳簿の検査にやってくるという。脱獄囚は店の主人一家に同情して従兄を殺そうと計画するが……。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Harry Potter and the Deathly Hallows: Part II", "ハリー・ポッターと死の秘宝 PART2", "", "", "http://image.eiga.k-img.com/images/movie/54734/315x210.jpg?4791", 130, "J・K・ローリングによる世界的ベストセラーシリーズを映画化した最終章2部作の完結編。魔法省やホグワーツ魔法魔術学校までもが死喰い人の支配下に落ちる中、ハリー、ロン、ハーマイオニーは、ヴォルデモート打倒のカギを握る「分霊箱」を見つけ出す旅を続けていた。しかし、その間にもヴォルデモートは着実に力を蓄えていき……。監督はシリーズ第5作「不死鳥の騎士団」からメガホンをとるデビッド・イェーツ。", "2011-07-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN　FIUME　DI　DOLLARI", "帰って来たガンマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "南北戦争の混乱に乗じて南軍の軍資金を奪ったジェリーとケン。クジ引きでおとりとなり５年の刑に服したジェリーは、ケンが大金を独り占めにしてのさばっている事実を知る。しかもケンはジェリーを抹殺するため、殺し屋を放つのだった。裏切り者の親友への復讐劇というマカロニ・ウエスタンの王道的なストーリーが展開。アメリカから招かれた個性派俳優H・シルヴァが主人公を狙う殺し屋役で助演。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Good German", "さらば、ベルリン", "", "", "http://image.eiga.k-img.com/images/movie/34124/315x210.jpg?1462", 108, "第2次大戦終戦直後、ベルリンを訪れたアメリカ人記者のジェイクは、かつての恋人レーナに再会する。しかし、レーナはジェイクの運転手を務める駐留米兵のタリーの情婦でもあった。そんな時、何者かに殺害されたタリーの死体が発見され、ジェイクは真相を追ううちに巨大な陰謀に巻き込まれていく。スティーブン・ソダーバーグ監督＆ジョージ・クルーニー主演コンビが描くミステリー。全編モノクロ映像で、1940年代の映画のスタイルを再現。", "2007-09-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬神家の一族（2006）", "犬神家の一族（2006）", "", "", "http://image.eiga.k-img.com/images/movie/1454/315x210.jpg?3246", 136, "76年に角川映画の第1作として公開され大ヒットを記録した同タイトルを、市川崑監督＆石坂浩二主演という当時のコンビのままで30年ぶりにリメイク。昭和22年、信州諏訪・犬神財閥の当主佐兵衛が逝去。犬神家の顧問弁護士である若林はその遺言書を巡って家族内で問題が起きることを予期し、東京から探偵の金田一耕助を諏訪へ呼び寄せる。だが、金田一が諏訪に着いた日に若林が殺害される。プロデューサーは「呪怨」「リング」の一瀬隆重。", "2006-12-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばりの娘船頭さん", "ひばりの娘船頭さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "潮来を舞台に、美空ひばりが娘船頭に扮して、タップリ歌を聞かせる歌謡映画。お光の家は、漁師の源助じいさん、船頭の兄・信一の３人暮らし。お光はある日、東京の美術学校生、水野と親しくなる。お光はかねてから東京に出たがっていた信一の就職を水野に頼み、自分が兄の代わりの船頭になるが……。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HOTEL NEW HAMPSHIRE", "ホテル・ニューハンプシャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "現代文学の鬼才アーヴィングのベストセラーの映画化。幻想と現実が相乱れるベリー一家の不思議な体験物語だ。夢想家ベリーは、学生時代からの念願叶ってホテル経営を始める。その名は“ホテル・ニューハンプシャー”。最愛の妻と、フラニーやジョンら５人の子供も彼を応援。だが、フラニーは強姦され、祖父は急死、経営も御難続きだ。そこで一家は心機一転、オーストラリアでの再出発を図る。だがその矢先、飛行機が墜落。乗っていた妻と末弟を失う……。本当は悲しい物語なのになぜかハッピーな気分になるのは、ベリー一家が、常に生きる希望と喜びにあふれているからだ。生と死、青春と晩年が見事に調和した独特な空間を作り出す、映像文学の傑作。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Divide", "ディヴァイド", "", "", "http://image.eiga.k-img.com/images/movie/57104/315x210.jpg?1775", 112, "壊滅したニューヨークの地下シェルターに取り残された男女9人が、恐怖や絶望にかられていく姿を描くシチュエーションスリラー。ニューヨークが突然の爆撃を受け、壊滅状態に陥る。男女9人が地下シェルターに逃げ込み生き延びるが、地上の建物が崩壊したことで出入り口がふさがれ、閉じ込められてしまう。水と食料はわずかしかなく、絶望的な状況の中で9人は互いに憎悪や疑心を募らせていく。さらにそこへ、防護服に身を包んだ男たちがシェルターをこじ開けて現れ、襲いかかってくる。出演は「ターミネーター」のマイケル・ビーン、「HEROES ヒーローズ」のマイロ・ビンティミリアら。監督は「フロンティア」「ヒットマン」のザビエ・ジャン。", "2012-06-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ずべ公番長・夢は夜ひらく", "ずべ公番長・夢は夜ひらく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "新人・山口和彦監督が大信田礼子と賀川雪絵のコンビで映画化した“ずべ公番長”シリーズの第１作。“女ネリカン”と呼ばれる赤城学園を仮退学した影山リカは、ネリカン仲間の集まるキャッチバーのホステスになるが、大羽興業はことあるごとに嫌がらせをしかけてくる。仲間の死をきっかけに、ついにリカたちは殴り込みをかける……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOUBLE TROUBLE", "バーバリアン刑事〈デカ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "双子のマッチョ兄弟“バーバリアン・ブラザーズ”主演のアクション・コメディ。デビッドはまじめな警官、ピーターはプレイボーイの宝石泥棒。外見はそっくり、中身は正反対のこの双子が、国際的宝石密輸組織の事件を解決するためにチームを組むことになった。D・キャラダインをはじめ脇役陣が渋く充実。ビデオ発売時タイトルは「ダブルトラブル　バーバリアン刑事」。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Village of the Damned", "光る眼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　静かな町に暮らす住人たちが一斉に昏睡状態に入り、その後、受胎可能な女性たちが同時に妊娠するという異変が起こる。政府の調査も行なわれるが、原因は不明のまま。その後、生まれた子供たちは急速に成長する。高度な知性と光る眼を持っていた彼らの周囲では不可解な現象が多発。科学者のバーナー博士から光る眼の子供たちが地球上の生命体でないことを明かされた医師チェフィーは彼らに立ち向かおうとするが……。SFスリラーの古典的名作「未知空間の恐怖／光る眼」をリメイク。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Matchstick Men", "マッチスティック・メン", "", "", "http://image.eiga.k-img.com/images/movie/1524/315x210.jpg?1970", 116, "ロイは相棒フランクと組むやり手の詐欺師だが、潔癖性に悩み精神科医に通う日々。そこにかつて別れた妻との間の娘アンジェラが登場、ロイは彼女に翻弄されていく。原案は、恐竜が主人公の異色ハードボイルド小説「さらば、愛しき鈎爪」シリーズで人気のエリック・ガルシア。名監督リドリー・スコットが、フランク・シナトラやハーブ・アルパートなど50年代の音楽に乗せて軽妙に描く親子詐欺師コメディ。", "2003-10-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("No Impact Man", "地球にやさしい生活", "", "", "http://image.eiga.k-img.com/images/movie/56964/315x210.jpg?3434", 92, "大都会ニューヨークで環境への影響を限りなくゼロに近づける生活を実践し、ニューヨークタイムズ紙でも特集が組まれるなど話題を集めたコリン・ビーバン一家を追ったドキュメンタリー。移動は自転車、エレベーターも使わない、外食はせずに食材はファーマーズマーケットで購入し、生ゴミを堆肥にするためマンションの中でミミズを飼うなど、さまざまな努力で“ノー・インパクト（環境を汚さない）”生活を続けるコリン・ビーバン。まだ小さい娘はその生活を楽しんでいたが、贅沢好きな妻は次第に不満を募らせ、夫婦の危機が訪れてしまう。", "2011-10-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hedwig and the Angry Inch", "ヘドウィグ・アンド・アングリーインチ", "", "", "http://image.eiga.k-img.com/images/movie/1424/315x210.jpg?3905", 92, "オフ・ブロードウェイのロック・ミュージカルを、その監督・脚本・主演のミッチェルが映画化。サンダンス映画祭の監督賞、観客賞など数々の映画賞を受賞した話題作。ヘドウィグは売れないロック・バンドのシンガー。旧東ドイツ生まれの彼は、かつてアメリカ兵と結婚するためにした性転換手術の失敗から“アングリーインチ（怒りの1インチ）”を持っていた。そんな彼が探し求める愛は、はたして手に入るのか。", "2002-02-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Darwin Awards", "ダーウィン・アワード", "", "", "http://image.eiga.k-img.com/images/movie/53094/315x210.jpg?4736", 95, "その年最も馬鹿な死に方をした人物に贈られる実在の賞“ダーウィン賞”をモチーフにしたブラック・コメディ。血を見ると失神してしまうプロファイラーのマイケルは、連続殺人犯を取り逃がし警察をクビになってしまう。新たな仕事を探しはじめた彼は、“馬鹿な死に方”の原因が遺伝子にあることを証明できれば多額の保険金が節約できると考え、保険会社へ自らを売り込みに行く。彼は変死担当の女性調査員シリと共に調査を開始するが……。", "2007-12-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ケータイ刑事 THE MOVIE2 石川五右衛門一族の陰謀　決闘！ゴルゴダの森", "ケータイ刑事 THE MOVIE2 石川五右衛門一族の陰謀　決闘！ゴルゴダの森", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "IQ180の少女が携帯電話を武器に難事件に挑むテレビドラマシリーズの劇場版第2作。第4シリーズで雫を演じた夏帆と、第5シリーズで雷を演じた小出早織が主演を務める。赤坂にある「ゴルゴダの森」へ厄払いに訪れた岡野刑事が行方不明となり、捜索していた雷までもが姿を消してしまう。かつての岡野の相棒・松山刑事と一緒に捜査を開始した雫は、ゴルゴダの森で手がかりを発見。そんな彼女の前に、石川五右衛門の子孫・小百合に洗脳された雷が立ちはだかる。", "2007-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TREASURE", "ボーイズ・サマー 黄金の海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "少年たちの冒険旅行をノスタルジックに描いた作品。12歳になるデイブは親友のフレディと宝探しの冒険を計画する。途中、厄介者のジョナサンも加わり出発。古い灯台にまつわる宝の謎と、記憶を失った年老いた船長の捜索など、少年たちはスリルに満ちた経験を重ねていく。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bird's Nest: Herzog & de Meuron in China", "鳥の巣　北京のヘルツォーク＆ド・ムーロン", "", "", "http://image.eiga.k-img.com/images/movie/53694/315x210.jpg?4033", 88, "東京のプラダ・ブティック青山店やロンドンの美術館テート・モダンで知られる、スイスの世界的建築家ヘルツォーク＆ド・ムーロン（ジャック・ヘルツォーク、ピエール・ド・ムーロン）が手掛けた北京オリンピックのメインスタジアム（北京国家体育場）、通称「鳥の巣」が完成するまでの一部始終を追ったドキュメンタリー。", "2008-08-02", "26,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("D坂の殺人事件", "D坂の殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "実相寺昭雄が「屋根裏の散歩者」に続いて、乱歩の『心理試験』『D坂の殺人事件』の２作をヒントに映画化。昭和２年の東京・団子坂。古本屋の美人女将・時子が絞殺死体で発見される。容疑者は第一発見者の従業員・斎藤と、時子に“伝説の責め絵師”の贋作を依頼されていた贋作師・清一郎。探偵の明智小五郎は二人に心理試験を行い、清一郎が真犯人だと確信するが……。真相解明のスリルと、妖しげな人々が交錯する耽美な雰囲気に緊張感が漂う異色作。贋作にのめり込むうちに、倒錯した性に目覚めていく天才贋作師を、真田広之がミステリアスに好演。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("必殺４ 恨みはらします", "必殺４ 恨みはらします", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "TVシリーズ第１作『必殺仕掛人』を演出し、いわば“必殺”の基礎を作った存在である深作欣二が監督した、アクション編。TVシリーズ15周年記念作でもあり、初の悪役を演じる真田広之、一匹狼の殺し屋・千葉真一など、キャストも豪華。なかでも拾いものは、千葉真一の娘になる相楽ハル子のりりしさであろう。内容は、幕府の実権を握ろうと陰謀をめぐらす、小姓あがりの奥田右京亮が町奉行に就任。貧しい者が住む長屋を壊して寺を建てようと、長屋の人々を迫害する。主水は、一匹狼の仕事人・文七とともに、これに立ち向かう。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOB MARLEY AND THE WAILERS", "ボブ・マーリィ／伝説のパフォーマンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "今は亡きレゲエ界のスーパースター、B・マーリィのコンサート・フィルム。1980年６月、最後となったヨーロッパ・ツアーの一環として、行われたドイツ、ドルムント公演の模様が収められている。マーリィは『ノー・ウーマン・ノー・クライ』など全15曲を熱唱。途中にインサートされる、祖国ジャマイカでの最後のパフォーマンスや、国葬などの貴重な映像も見もの。", "1998-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・仁義なき戦い。", "新・仁義なき戦い。", "", "", "http://image.eiga.k-img.com/images/movie/1644/315x210.jpg?3977", 109, "日本最大の暴力団組織・左橋組三代目組長が跡目を決めることなく急死。四代目を継ぐべきナンバー2の若頭が病気を理由に辞退したため、若頭補佐の粟野と中平の跡目をめぐる内部抗争が勃発した。両者は虚々実々のかけひきを展開、その渦中で粟野組の幹部門谷と、中平が企業舎弟にしようとする栃野が30年ぶりに運命の再会を果たす。", "2000-11-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新龍門客棧／DRAGON INN", "ドラゴン・イン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "明の時代、南京から遠く離れた辺境の地で、最高権力者である宦官ツァオによる悪政が人民を苦しめていた。それに抵抗すべく正義の戦士たちが決起。宦官派との激しい攻防戦の末、遂に“龍門の宿”で怒濤の死闘が繰り広げられる。正義の女剣士モウインにブリジット・リン、その恋人ワイオンにレオン・カーファイ、宿の女将シュンユクにマギー・チャンと豪華スターの競演が見もの。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刑事物語５ やまびこの詩", "刑事物語５ やまびこの詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "舞台は群馬県沼田市。ある日の早朝、パラコート入りの牛乳で牛乳配達の少女が倒れる。犯人の狙いは真咲直子・真子の姉妹だった。事件には町の資産家・大神の遺産が絡んでいるのだ。姿を消した直子を追って片山は山荘へと急ぐ。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野火", "野火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "戦時下の極限状況での、人間性と神の問題を描いた、大岡昇平の同名小説の映画化。市川崑夫人でもある脚本家の和田夏十は、原作を大胆に脚色し、直接神の問題に触れることは避けた。第二次世界大戦末期、フィリピン戦線レイテ島。日本軍は山中に追い込まれ、飢えと疲労で極限状態にあった。田村一等兵は病気を理由に軍隊から追い出され野戦病院に行くが、ここでも追い払われ、敗走する仲間の群れに入る。飢餓に苦しむ彼らは“猿”と称して、味方の兵士を殺し、その肉を食べていた。船越英二は役作りのため絶食して、鬼気迫る演技を見せた。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("朱花（はねづ）の月", "朱花（はねづ）の月", "", "", "http://image.eiga.k-img.com/images/movie/56334/315x210.jpg?1474", 91, "「萌えの朱雀」「殯の森」の河瀬直美監督が、「万葉集」の地・奈良県飛鳥地方を舞台に、祖父母の時代に果たされなかった思いを受け継ぐかのように生きる男女、拓未と加夜子の姿を描く。古代より神が宿るとされる大和三山（畝傍山、耳成山、香具山）など、奈良の奥深く美しい歴史を背景に、人間が「生きる」ということの意味を問う。主演は、本作が長編映画の本格デビューとなるこみずとうたと、「ヘヴンズストーリー」などに出演し、モデルとしても活躍する大島葉子。", "2011-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE REBEL", "アウトロー・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "主人公は警察を退職したものの、その力を買われて現場復帰を果たしたニック・リチャード。彼はドイツの要人の警護を引き受けたことから、国際テロ組織におとり捜査官として潜入。組織壊滅のために幾多の戦いを繰り広げていく。「さすらいの一匹狼」「荒野の無頼漢」の撮影監督を経て、監督に転向した、S・マッシが手掛けた刑事アクション。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARPOOL", "ドタキャン・パパ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "家庭を顧みない仕事人間のダニエルはある朝、自分の息子、娘、そして近所の子供たちを学校に送ることになる。しかし、途中で立ち寄った食料品店で強盗事件に遭遇。犯人に間違えられたドジな男の人質になってしまう……。仕事が気になるダニエル、異常な事態を楽しむ子供たちが、警察から逃げようとする新米誘拐犯とともに危険なドライブを展開する。ベテラン、A・ヒラーによるファミリー・コメディだが、切れ味が悪いのが難点。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mademoiselle", "マドモワゼル", "", "", "http://image.eiga.k-img.com/images/movie/1514/315x210.jpg?4640", 85, "製薬会社に勤め、愛する夫と2人の子供をもつクレール。即興劇団を結成、気ままな生活を送る俳優ピエール。会社のコンベンションの余興に劇団が雇われたことから、偶然出会った2人は互いに惹かれ合い、それまでの生活で思いもしなかった可能性に気づく。監督は「パリ空港の人々」のフィリップ・リオレ。「仕立て屋の恋」のサンドリーヌ・ボネール、「ペダルデュース」のジャック・ガンブランが共演。音楽は「夕なぎ」「テス」などのベテラン、フィリップ・サルド。", "2002-11-02", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("肌の隙間", "肌の隙間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "「ユダ」の瀬々敬久監督が浮き彫りにする、男女の美しき逃避行。母親を殺害した少年と、彼と同居していた叔母。二人の旅と禁断の愛欲の行方を、虚飾を排した凄烈な筆致で描き出す。極限まで剥ぎ取られたセリフ、そして音楽の不在。無言のままきらめく描写が、多くのことを語りかけてくる。ひきこもりの秀則は母親を殺し、自閉症の叔母、妙子と逃亡する。彼らはほとんど言葉を交わさないまま旅を続け、愛し合うようになる。", "2005-05-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PARTII 銀次郎VS整理屋", "難波金融伝 ミナミの帝王　劇場版PARTII 銀次郎VS整理屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "劇場では、第１作の３番目のエピソードとして上映された一編。漫才師に借金を踏み倒された銀次郎の弟分・竜一。慌てて漫才師の父親のもとに駆け付けるが、その会社は既に“整理屋”と呼ばれる経済ヤクザによって計画倒産させられていた。銀次郎がひと肌脱ぐことになるが……。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bestseller", "ベストセラー", "", "", "http://image.eiga.k-img.com/images/movie/55824/315x210.jpg?2702", 117, "韓国の鬼才イ・ジョンホ監督が、「TSUNAMI ツナミ」のオム・ジョンファを主演に描くサスペンス・スリラー。国民的ベストセラー作家として名をはせてきたヒスは、発表した新作小説に盗作の疑いをかけられ、一瞬にして名声を失ってしまう。スランプに陥ったヒスは、娘ヨニとともに人里離れた山奥の別荘を訪ねるが、ヨニはそこで正体不明の存在と交流するようになる。ヒスはその奇妙な体験を小説にして再起するが、その物語は10年前に発売されたある小説と全く同じ内容だった……。", "2010-12-04", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Final Destination", "ファイナル・デッドサーキット 3D", "", "", "http://image.eiga.k-img.com/images/movie/54164/315x210.jpg?3705", 82, "全編フルデジタル3Dによる、逃れられない「死の運命」と戦う若者を描く「ファイナル」シリーズの第4弾。監督は第2弾の「デッドコースター」でメガホンを取ったデビッド・リチャード・エリス。卒業間近の大学生カップルのニックとローリは、友人たちとサーキット場でダブルデートを楽しんでいた。レースの盛り上がりが最高潮を迎えたとき、ニックは恐ろしい予知夢を見る。", "2009-10-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Horsemen", "ホースメン", "", "", "http://image.eiga.k-img.com/images/movie/54354/315x210.jpg?3706", 90, "マイケル・ベイが製作を務め、チャン・ツィイーが猟奇的な殺人鬼に扮するサスペンス・スリラー。デトロイト郊外の凍った池で大量の血まみれな歯が見つかり、現場には「来たれ」という謎のメッセージが残されていた。その後まもなく、捜査を任された刑事ブレスリンのもとに奇怪な中年女性の死体が見つかったという知らせが入る。ブレスリンは一連の事件を新約聖書のヨハネの黙示録になぞらえたコンセプチュアルな犯罪だと推測するが……。", "2009-10-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ahasin Wetei", "2つの世界の間で", "", "", "http://image.eiga.k-img.com/images/movie/55034/315x210.jpg?4693", 83, "2005年の長編監督デビュー作「見捨てられた土地」で、第58回カンヌ国際映画祭カメラ・ドールを獲得したスリランカの新鋭ビムクティ・ジャヤスンダラ監督による長編第2作。美しい自然が広がる田舎の村へやって来た青年の物語を、村に伝わる王子の伝説と絡ませながら、神秘的な映像で描き出していく。第66回ベネチア国際映画祭コンペティション部門、第10回東京フィルメックス上映作品。", "2009-11-23", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PER　IL　GUSTO　DI　UCCIDERE", "さすらいの一匹狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「怒りの荒野」「ミスター・ノーボディ」のT・ヴァレリ監督のデビュー作。望遠スコープ付きのカービン銃を操る男の闘いを軽快なタッチで描く。駅馬車の護衛を請け負い、山賊一味に懸けられた賞金をまんまと手にしたハンク・フェロー。彼はかつて兄弟を殺した仇敵ケネベックを倒すために一計を案じるのだった。", "1966-01-01", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DINOSAURS", "ダイナウォーズ 恐竜王国への大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "恐竜が主人公のテレビ番組の世界に入り込んでしまった少年少女たちの冒険を、SFXを駆使して描いたユニークなアドベンチャー娯楽作。ユーモラスで個性豊かな恐竜たちと子供たちとの、何が起こるかわからないハラハラドキドキの冒険ぶりは、まるでロールプレイングゲームのようだ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TERROR", "古城の亡霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "J・ニコルソンがまだ無名の頃、R・コーマンと組んで作った古典的ホラー。製作補にはフランシス・フォード・コッポラの名もクレジットされている。美女を追って不気味な古城へたどり着いた若きフランス人大尉。彼が目撃した美女は城主の亡き妻に生き写しだった……。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の園", "女の園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "良妻賢母型女子育成を教育の理想とする京都の全寮制の名門女子大学。そこでは徹底した束縛によって学生の生活に対する干渉が行われていた。恋人との自由な文通さえも許されず、また男友だちとの交際も禁じられ、これに反すると停学処分に。その一方では学校の有力な後援者の子女は特別扱い。その理不尽さに女子学生たちはついに自由を求めて立ち上がる。「日本の悲劇」に続く木下惠介の問題作。当時フランスでの長い滞在から帰国した木下は、日本の民主化のかげに、いまだ封建的な環境が存在していることを鋭くえぐり出す。来たるべき全国的学園闘争の予兆的作品。田村高廣のデビュー作でもある。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAROUSEL", "回転木馬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "モルナールの戯曲『リリオム』をもとにO・ハマースタインとR・ロジャースがミュージカル舞台劇にした『回転木馬』を、H・キングが映画化。天国で星の番人をしているビリーは、不幸な境遇にある自分の娘を幸せにするため、一日だけ地上に帰ることが許される……。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("親分はイエス様", "親分はイエス様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "本物の極道だった男たちがキリスト信仰の道に入り、伝導集団を結成。更生と布教を続けながら人生を生き直す彼らの姿を描く。実在する伝導集団ミッション・バラバの活動や家族との愛、彼らのたどってきた波乱の人生を感動的に綴っていく。日韓合作でもあるこの作品は、全米で３日間、プレミア上映され話題を呼んだ。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONLY WHEN I LAUGH", "泣かないで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "当代随一の劇作家N・サイモンが愛妻のメイスンを起用した作品で、サイモン初期の舞台劇の映画化。アル中で療養所に入っていた舞台女優と、６年ぶりに再会した実の娘との心の交流を描く。サイモンの軽妙な会話の中に親子の愛情が伝わってくる佳作。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WINGS OF THE DOVE", "鳩の翼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "文豪H・ジェームズの小説に基づく恋愛ドラマ。上流階級の女性ケイトには新聞記者である貧しい恋人マートンがいるが、彼女の叔母はこの交際に反対。そのころ、資産家の米国人女性ミリーが社交界に現れた。実はミリーは大病に冒され、余命いくばくもない身。ケイトはマートンに気のある彼女に友情を感じつつも、ある事をたくらんでいた。やがてミリーは、マートンとケイトを誘い、ベニス旅行に出かける……。男女３人の愛と友情の行方をサスペンスフルに描きつつ、愛の本質を浮き彫りに。ヴィクトリア朝のロンドン、アンニュイなムードを漂わせたベニスの風景が、劇的な効果を上げている。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Descent: Part 2", "ディセント2", "", "", "http://image.eiga.k-img.com/images/movie/54894/315x210.jpg?1933", 95, "世界各国で大きな話題を呼んだイギリスのホラー「ディセント」の続編。アパラチア山脈の洞窟で6人の女性が消息を絶ち、その内の1人であるサラが血まみれの姿で生還する。残る5人の救出に焦る保安官と救助隊は、ショック状態で一時的に記憶を失ったサラを連れて洞窟へと向かうが……。前作で監督を務めたニール・マーシャルの製作総指揮の下、編集を手掛けたジョン・ハリスが監督に抜擢された。", "2009-11-07", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOMEWARD BOUNDII ： LOST IN SAN FRANCISCO", "奇跡の旅2　サンフランシスコの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "二匹の犬と一匹の猫が大冒険を繰り広げるほのぼのコメディ第２作。ブルドックのチャンスは、シーバー一家との旅行の途中にはぐれてしまう。後を追った老犬シャドーと猫のサシーとともにサンフランシスコの街をさまようことになるが、大都会の広さに戸惑うばかり。やがて野良犬のディライラと恋に落ちるチャンスだが、犬を研究所に売りさばくペット・ハンターに捕まってしまい……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("竜馬暗殺", "竜馬暗殺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "慶応３年、坂本竜馬が京都・河原町で暗殺されるに至った最後の３日間を描いた作品。現代劇を中心に演出する黒木和雄による初の時代劇でもある。壮大な革命の野望に燃える竜馬だが、時代は混沌の極み。誰もが敵か味方かさえよく分からない。そんな中、竜馬は一人、革命を叫ぶのだが……。江戸末期に材を得た時代劇だが、同時に1970年代という時代の世相をも反映している。助監督には「パイレーツによろしく」の後藤幸一が名前を連ねている。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LISTEN TO ME", "青春！ケンモント大学", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "カリフォルニア大学の学生が、全米学生ディベート（討論）大会優勝のために猛特訓しつつ、青春の炎を燃やしていく新手の青春映画。R・シャイダーがディベート・コーチの大学教授役で出演している。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛について、東京", "愛について、東京", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "東京・荒川べり。劣悪な生活環境の中で、3Kアルバイト暮らしやイカサマパチンコに活路を開いていく中国人留学生と、日本で生まれ育った中国人少女の愛の道行きが描かれる。“国際化”が進む東京の片隅で、周囲の日本人の偏見をバネにして、仲間たちとしたたかに生き抜いていく若い彼らの姿に、カメラは熱いエールを込めてグイグイ肉迫する。中国人の留学生と少女の間で奇妙な三角関係をとり結ぶことになる不能の日本人ヤクザを藤岡弘が好演。都市や農村の荒廃に切り込んできた柳町監督の汎アジア的ラブ・ストーリー。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE LIPS", "小さな唇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "戦争の傷から不能となった孤独な作家ポールと、12歳の少女エヴァの艶美で悲しい恋の物語。名作「ロリータ」をもしのぐ、エヴァの汚れなくしかも残酷さを秘めたエロティシズムが圧巻だ。特異な美貌で定評のあるクレマンティの、センシティブな演技がまた魅力的。", "1974-01-01", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WEEDS", "ウィーズ　塀の中からブロードウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "アームステッターは、恩赦なしの終身刑。彼はふとしたことから文学に目覚め、やがて自らシナリオを書き、他の囚人たちと刑務所内で芝居を公演する。これが好評を得て、彼らはそこに生きる希望を見いだす。さらに、これに関心を持った女性記者が現れ、彼女の尽力で、アームステッターは数年後、仮出所の機会を与えられる。彼はすでに出所していた刑務所仲間を呼び集め、刑務所劇の旅興行をすることになる……。舞台劇のシーンを中心に登場人物たちの充実感や困難、試練を深く描き込んだ秀作。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("肉弾", "肉弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "主人公は、固有名詞のない“あいつ”。インテリらしい度の強いメガネをかけ、魚雷をくくりつけたドラム缶に入り、女郎屋の番傘をさして、ギラギラの太陽に照りつけられながら、静かな洋上をのんびりと浮かんでいる。敗戦間際の頃“あいつ”は、仲の良さが微笑ましい古本屋の老夫婦や、防空壕の中で結ばれたが空襲で死んでしまった、セーラー服の美しい少女との幸福だった時間を想い浮かべる……。岡本喜八監督の戦争への屈折した思い、青春への痛みと感傷が色濃く反映されている戦争ドラマの傑作。それを悲壮に描くのではなく、コミカルなタッチで綴っていて、より心にしみる作品に仕上がった。寺田農、大谷直子の初々しい演技も印象深い。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すっとび仁義", "すっとび仁義", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "橋幸夫の初主演作。奥州松平家では、若殿新之助が重病で倒れ、後継ぎ騒動が持ち上がる。新之助は、幼い頃に生き別れになった双子の弟、新三を探すことを命じる。見つかった新三は江戸の町人育ちで、大名暮しが大嫌い。それを、用人・結城甚兵衛の娘、お八重が鍛えていくが……。お八重役の中村玉緒と、橋とのやり取りがコミカルで面白い。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Take the Money and Run", "泥棒野郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "悪の道で名をなすことを夢見る落ちこぼれが、ドジを続けながら前科53犯の有名人となるまでをジョークとギャグを散りばめて綴るW・アレンの自作自演作品。ナレーションに導かれたセミ・ドキュメンタリー・スタイルは「カメレオンマン」と同じアイデアともいえるが、才気に歯止めがきかず勇み足もしばしばで、それがまたほほえましい。クロード・ルルーシュの「男と女」からインスピレーションをうけたと思われるアレンとマーゴリンの海辺のラブ・シーンから一転、「仮面の米国」などの監獄もののムードへ……というような、初期のアレンの才気が楽しめる。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無知との遭遇 CLOSE ENCOUNTERS OF THE STUPID", "無知との遭遇 CLOSE ENCOUNTERS OF THE STUPID", "", "", "http://image.eiga.k-img.com/images/movie/55584/315x210.jpg?2843", 100, "千原ジュニアがお笑いコンビ「野性爆弾」のロッシーに仕掛けたドッキリをベースに構成されるSFファンタジー・コメディ。2人がテレビ番組の収録で、ある村を訪れると、その村の住民全員が宇宙人だった。そこに巨大 UFOが出現し宇宙戦争へと巻き込まれていくというでっち上げのストーリーを完全に信じたロッシーは、悪の宇宙人と戦うことを決意する。第2回沖縄国際映画祭の長編プログラム“Laugh部門”に出品。", "2010-08-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙貨物船レムナント６", "宇宙貨物船レムナント６", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "事故に遭った宇宙船乗組員たちの、決死の脱出劇を描いたSFアクション。西暦2046年、火星から地球への帰還中の宇宙貨物船レムナント６に小惑星が激突し、大事故が発生した。その場には宇野航海士、システムオペレーターの西明日香、火星ドーム生まれで９歳の祥太、地球に移送される自衛官の後藤、その監督任務の滝沢と新人の井上の６名の乗組員が居合わせた。井上は事故に巻き込まれるが、残りの５人は本体部に脱出。そこで重力の異常に気づいた明日香はコンピューターにアクセスするが……。総合監修に押井守、レムナントのデザインはSFメカデザインの第一人者の河森正治が務めるなど、日本の特撮・アニメ界の精鋭が集結し、２年に及ぶSF考証を行ってリアルな近未来を再現。宇宙船に当たる太陽光の角度、隕石衝突による破片の動きなど、細部のディテールにまでこだわったCGは見事。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Son of Rambow", "リトル・ランボーズ", "", "", "http://image.eiga.k-img.com/images/movie/53784/315x210.jpg?2565", 94, "1982年のイギリスが舞台。11歳のウィルは、教会の厳しい規律のもと、音楽や映画などの娯楽を一切禁止されて育つ。ある日、学校一の問題児カーターと出会い、彼の家で生まれて初めて映画「ランボー」を見る。大きな衝撃を受けたウィルは、カーターと友情を育みながら、映画作りを始める。人気バンド「ベック」「レディオヘッド」などのミュージックビデオを手がけるガース・ジェニングス監督がメガホンをとり、演技経験のないビル・ミルナーとウィル・ポーターを主演に抜てき。共演は米人気TVシリーズ「ゴシップガール」のエド・ウェストウィックら。", "2010-11-06", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A GATHERING OF EAGLES", "ミサイル空爆戦隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "軍隊のシビアな世界を描いた辛口のドラマ。戦略空軍部隊に着任したジム大佐は、仕事に忠実なあまり、部下たちに過酷な勤務を要求していた。副官として任命された旧友のファーは、部隊内に盛り上がり始めた大佐に対する反抗心を抑えようと苦心するが、夫の冷酷さに不満を持つ大佐の妻ヴィクトリアはいつしかファーと親しむようになり、大佐は彼をも解職しようとするのだった。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クリミナル・ラヴァーズ", "クリミナル・ラヴァーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ある夜、静まり返った学校の体育館にやってきた高校生のアリスとリュック。アリスが1人で奥のシャワー室に向かうと、そこにはアリスと同じクラスのサイードがいた。サイードがアリスを抱き、リュックはその様子を陰から見守るが、アリスが目配せをした瞬間、リュックは隠し持っていたナイフでサイードを刺し殺す。2人は死体を車のトランクに隠して森に向かい、穴を掘って死体を埋めるが……。フランスの鬼才フランソワ・オゾン監督が描くサスペンス・ミステリー。", "2000-09-30", "2,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPERMAN II", "スーパーマン II 冒険篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "クリプトン星から３人の反逆者が追放される前作の冒頭場面から始まる続編。テロリストの取材中、誤ってエッフェル塔から落下したロイスを救ったスーパーマンは、核爆弾を宇宙で爆発させる。だがこの爆発で３悪人が解放され地球へ。スーパーマンの秘密も明らかにされる娯楽大作。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛と誠（2012）", "愛と誠（2012）", "", "", "http://image.eiga.k-img.com/images/movie/56814/315x210.jpg?2186", 134, "1970年代に人気を博し、西城秀樹主演で映画化もされた梶原一騎原作の同名コミックを、「十三人の刺客」「クローズZERO」の三池崇史監督のメガホンで新たに映画化。主演は妻夫木聡と武井咲。1972年の新宿。良家の令嬢・早乙女愛は、幼い頃に危機を助けられた少年・太賀誠と運命的な再会を果たす。札付きの不良となっていた誠を更正させようと献身的に尽くす愛は、誠の後を追って不良の掃き溜めといわれる花園実業に転入。誠が心を通わせていく由紀や、愛を追いかけて花園にやってきた優等生の岩清水、スケバングループのガムコら、それぞれの思いが交錯し、やがて学校全体を巻き込んだ大乱闘へと発展していく。", "2012-06-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sedmikrasky", "ひなぎく", "", "", "http://image.eiga.k-img.com/images/movie/34164/315x210.jpg?1513", 75, "66年にチェコスロバキアで製作されたポップなガールズ・ムービー。奔放な姉妹が繰り広げる大騒ぎを、色ズレやカラーリング、実験的な光学処理、斬新な効果音など冒険心に満ち溢れた手法で描き出す。男たちを騙して食事をおごらせた挙句に嘘泣きして逃げ出したりと自由気ままに生きている2人の姉妹。部屋の中でも牛乳風呂に入ったり紙を燃やしたりとやりたい放題。そんな彼女たちが、今度は党のパーティーに忍び込み……。", "2007-09-01", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伴淳・森繁のおったまげ村物語", "伴淳・森繁のおったまげ村物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "上州赤木山麓の人呼んで“おったまげ村”という村に馬糞の九さんというお人好しの青年がいた。九さんは居酒屋の小夜に惚れていた。化粧品問屋の三八も小夜に想いを寄せていた。その小夜は全国を股にかけるペテン師の情婦だった。恋しい女のために繰り広げられる九と三八のドタバタ喜劇。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("純喫茶磯辺", "純喫茶磯辺", "", "", "http://image.eiga.k-img.com/images/movie/53364/315x210.jpg?2285", 113, "高校生の娘・咲子と2人で暮らしている水道工員の磯辺裕次郎は、父親の急死によって多額の遺産を相続する。生来いい加減な性格の彼は、ただモテたいという理由だけで喫茶店を始めようと思いつき……。「机のなかみ」の吉田恵輔が監督・脚本を手掛け、喫茶店に集った個性的な人々が織りなす人間模様を描いた人情コメディ。俳優としても活躍するお笑い芸人の宮迫博之が主演を務めるほか、麻生久美子、ダンカンら個性派キャストが集結。", "2008-07-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オレンジロード急行", "オレンジロード急行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "小型トレーラーから海賊放送をやっている若者たちと、死んだ親友の故郷を訪ねるため自動車泥棒を続けて旅をする家出老人たちとの出会いを描くロード・ムービー。大森一樹が自らの1977年城戸賞受賞作を演出、松竹が26歳の企業外監督である彼を起用したことでも話題になった。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間の砂漠", "人間の砂漠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "事件の加害者ではあるが、同時に男社会という枠組のなかで被害者でもあった女子受刑者たち。刑務所という極限の場に送られた彼女たちの姿を通して、現代社会が抱える問題を浮き彫りにした衝撃作。栃木刑務所などをまわり、罪を犯した女性を取材して大反響を呼んだ、早瀬圭一の『長い午後』の映画化。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八州遊侠伝 白鷺三味線", "八州遊侠伝 白鷺三味線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "旗本くずれのヤクザ・大岡源太郎は三味線のつま弾きで唄ったりする粋者で、喧嘩にも強いという男。この白鷺の源太が下総飯岡で横暴をきわめる役人・大月忠馬の陰謀を探り出すべく命を受け、下総に乗り込んでいく。恋とアクションの痛快任侠時代劇。近衛十四郎が敵・秋山要役で豪放な剣を振るい、スターの座に返り咲き、高田浩吉が歌う同名主題歌も大ヒットした。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("中仙道のつむじ風", "中仙道のつむじ風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "山中貞雄らと鳴滝組を結成し、彼の作品の重要な脚本家として活躍した三村伸太郎の原作を映画化したもの。中仙道を旅していたつむじ風の百太郎が、道中で出会い恋心を抱いた女のために、悪代官一味と対決する。しかし女には他に一緒になるべき男がいると知った彼は、一味を倒すと再び旅に出るのだった。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LEAGUE OF EXTRAORDINARY GENTLEMEN", "リーグ・オブ・レジェンド／時空を超えた戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "欧米でカルト的な人気を博しているコミックを映像化した冒険アクション。ネモ船長やジキル博士、吸血鬼など文学上のヒーローを総登場させた、奇想天外かつスリリングな物語が展開する。19世紀末、国際和平会議を阻む悪の組織が暗躍。英国政府によって、その阻止を依頼された冒険家クォーターメインは、ネモ船長や透明人間、トム・ソーヤーらを率いて冒険の旅に出る。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("What's Eating Gilbert Grape", "ギルバート・グレイプ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "「マイライフ・アズ・ア・ドッグ」で世界中の注目を浴びたスウェーデンのラッセ・ハルストレム監督が、ハリウッドで手掛けたヒューマンドラマ。身動きが取れないほど太った過食症の母と知的障害者の弟を持つ青年ギルバート。家族の世話に追われる毎日を送っていた彼が、トレーラーで旅をする少女との出会いをきっかけに自分の人生を見つめ直す姿を描く。アカデミー助演男優賞にノミネートされたレオナルド・ディカプリオの好演が光る。", "1994-08-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猛鬼佛跳牆", "チャイニーズ・ゴースト・バスターズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "父の昇進で郊外の別荘に引っ越した建築家一家の長女アチャン（ロレッタ・リー）は、ある朝見知らぬ男に、その家から去るよう忠告を受ける。それ以降怪奇事件が続出、やがてこの家には恐るべき悪霊がとりついていることを知り、一家総出で幽霊退治に乗り出すのだが……。当時香港のトップアイドルだった、ロレッタ・リー主演のコメディ・ホラー。", "1989-02-28", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILL ME AGAIN", "もういちど殺して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "V・キルマーが、妻のJ・W・キルマーと共演したフィルム・ノワール。妻を亡くして自堕落な生活を送る私立探偵ジャックのもとに、美人の依頼人フェイが訪れる。彼女は、ジャックに“自分を殺してほしい”と頼むのだが……。1940～50年代の犯罪スリラーを研究して作っただけあって、ハードボイルド調の雰囲気が本編の身上。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("錆びた鎖", "錆びた鎖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "“渡り鳥”シリーズで小林旭を売り出した斎藤武市監督と、赤木圭一郎の初顔合わせによるアクション映画。横浜港の荷役作業を扱う会社を乗っ取ろうとするヤクザの策謀を、殺された社長の息子兄弟が協力して粉砕する。小高雄二の好演が光る。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("社長外遊記", "社長外遊記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "丸急デパートの社長・風間圭之助は妻と５人の娘のよき父親。ライバルの福助屋が香港に支店を出すと知った社長は一大発奮、ハワイに支店を出すことにした。やがてハワイへ渡った社長たちは、ヤング・ギャルたちの熱いキスに参ってしまう。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マリッジリング", "マリッジリング", "", "", "http://image.eiga.k-img.com/images/movie/53114/315x210.jpg?1919", 99, "「失楽園」「愛の流刑地」などで知られるベストセラー作家・渡辺淳一の短編小説を、「眠り姫」の七里圭監督が映画化。25歳のOL千波は、仕事で忙しい彼氏に不満を募らせていた。そんな時、彼女が働く部署に新しい課長・桑村が赴任してくる。桑村から食事に誘われた千波は、彼が既婚者だという安心感から気軽に付き合うことに。しかし、2人の心の距離は急速に近づいていき……。主演は保阪尚希と「笑の大学」の小橋めぐみ。", "2007-12-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INVITATION TO THE DANCE", "舞踏への招待", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "全編セリフなし、バレエ・シーンだけで見せるミュージカル。G・ケリーが監督した野心作である。「嘆きのピエロ」「腕環のロンド」「船乗りシンドバッド」の３つの短編からなり、日本では１部と２部のみ別々に公開されただけである。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠雷", "遠雷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "東京近郊の農村地帯に生きる若者像を描いた立松和平の小説を、ロマンポルノを監督してきた根岸吉太郎が演出、彼の最初の一般映画にして、代表作となった。主人公はビニール・ハウスでトマトを栽培している若者。近くの団地の人妻と関係したり、ちょっといい加減なところもある毎日。そんな彼が結婚することに。見合いのあとすぐにモーテルに行くような自由奔放な二人。結婚式に振付きで桜田淳子の歌を歌う場面や農地の売買を迫る不動産屋を追い返す場面などユーモアとバイタリティー、そして叙情味を感じさせる優れた描写で、農村の若者の一面を垣間見せてくれる。永島敏行の大きなステップとなった。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハリヨの夏", "ハリヨの夏", "", "", "http://image.eiga.k-img.com/images/movie/33694/315x210.jpg?4684", 99, "1990年の京都を舞台に、高校3年生の少女が両親の離婚や自身の妊娠・出産を通して成長していく姿を生き生きと描いた青春ドラマ。17歳の瑞穂は、唯一心を許せる存在だった別居中の父に恋人がいることを知りショックを受ける。そんなある日、瑞穂は母が働く居酒屋で、どこかかげりのあるアメリカ人男性チャーリーに出会う。「星になった少年」の於保佐代子が、思春期の揺れる心を抱えながらもまっすぐに生きる少女を好演。", "2006-10-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FARMER’S WIFE", "農夫の妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ヒッチコックが描くヒューマン・コメディ。農夫のスウィートランドは、５年前に妻を亡くし、それ以来やもめ暮らし。そろそろ妻を探そうと、家政婦のアラミンタに相談する。そして候補にあがった近所の女を一人ずつ訪ねる彼だったが、ことごとく追い払われて落ち込んでしまう。", "1928-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗黒街の美女", "暗黒街の美女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "鈴木清順と白木マリの「裸女と拳銃」に続くコンビ第２作。密輸ダイヤをめぐるギャング同士の内ゲバを描く。清順初のワイド作品（日活スコープ）で、その特長を生かした、良質の娯楽作品となっている。なお、この作品から清太郎から清順へと名前を改めている。ボス役の芦田伸介と通訳の近藤宏が好演。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京うんこ", "東京うんこ", "", "", "http://image.eiga.k-img.com/images/movie/56794/315x210.jpg?3095", 96, "思いつきで漫画家を目指すと言い出し会社を辞めたダメ男の俊太郎と、バイトをしながら俊太郎との同せい生活を支えている兵庫出身の希梨子。ある日、俊太郎がお腹を壊したことをきっかけに2人の周辺で面倒な出来事が次々と起こり、周囲の人々に少しずつ迷惑が及んでいく。東京生まれのダメ男としっかり者の関西女が都会の片隅で送る同せい生活をユーモラスに描いたラブストーリー。", "2011-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続二等兵物語・決戦体制の巻", "続二等兵物語・決戦体制の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "昭和20年２月、二等兵・古山源吉と柳一太郎は浜松に近い渋川工兵中隊へ配属される。古山は柳の妹・鞠子に惚れるが、ふとしたことから柳と大喧嘩になってしまう。やがて誤解は解け、柳は古山に“戦争が終わったら私の弟になっとくれなはれ”と言う。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BEST LITTLE WHOREHOUSE IN TEXAS", "テキサス1の赤いバラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "テキサス州ヒューストンに近いある田舎町の売春宿を舞台に、宿の女主人と彼女に思いを寄せる保安官、そして売春撲滅を目指すTVキャスターが繰り広げるコメディ・ミュージカル。底抜けに明るくパワフルな、ウエスタン喜劇の知られざる佳作。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Killing Me Softly", "キリング・ミー・ソフトリー", "", "", "http://image.eiga.k-img.com/images/movie/1474/315x210.jpg?2486", 101, "ロンドンに住み始めて18カ月のアリスは、謎めいた登山家アダムと出会い、官能的な体験を経て電撃結婚する。しかし、結婚したアリスのもとに無言電話やアダムとの関係を警告する匿名の手紙が届き、彼女は夫の過去に疑問を抱くようになる。「さらば、我が愛／覇王別姫」の陳凱歌（チェン・カイコー）監督が欧米での監督作を捜していることを知った「ツインズ」のプロデューサー、アイバン・ライトマンが脚本を送って実現。", "2002-02-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNZIPPED", "アンジップト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "ニューヨークのファッション・デザイナー、アイザック・ミズラヒに密着取材したドキュメンタリー。1994年秋冬コレクションの準備開始からショーを完成させるまでの姿を追う。カメラは彼のプライベートライフからアトリエ、スーパーモデルたちの舞台裏へと迫り、ユニークな彼の人物像を浮き彫りにする。1995年サンダンス・フィルム・フェスティバルでドキュメンタリー部門観客賞を受賞。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サバイバル・ビーチ", "サバイバル・ビーチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "無声映画に自ら活弁をつける“自作自演活弁監督”として活躍してきた山田広野が、トーキー（音声入り）長編映画に初挑戦した冒険コメディ。南の海で人喰いザメに襲われ、無人島に漂着してしまったアマンダ、ジェーン、イザベルら3人のセクシー美女と中年ライフセーバーのホリ。彼らはなんとか生き延びようと結束するが、突然の雄たけびとともに現われた何者かが彼らを襲い、逃げ遅れたアマンダを連れ去ってしまう……。", "2007-01-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("笛吹川", "笛吹川", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "木下惠介としては珍しく、戦国時代を背景とした作品。しかし木下はこの作品をよくあるような華々しい合戦絵巻などにはせず、むしろ戦乱の中に生きる百姓の一家を年代記的に捉えることで、合戦がいかに退屈で空虚なものかを描くことに成功している。甲斐の国、笛吹川に架かる橋の下に、貧しい百姓一家が住んでいた。うち続く戦乱で男手は次々戦死。そうしたなかで定平はおけいを嫁にする。数年がたち、ようやく二人の男の子が生まれる。子供たちは成人して戦に出るが、高遠城が落ちたために二人は笛吹川に敗走する……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIVRE SA VIE", "女と男のいる舗道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ゴダールの長編４作目で、12のエピソードからなる娼婦ナナの物語。安月給のレコード店員ナナは生活費のために売春を始めるが、ヒモの争いに巻き込まれ、流れ弾にあたって死んでしまう。映画館でナナが見る「裁かるゝジャンヌ」、哲学者との対話などゴダールならではの感覚あふれる秀作。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("藍色大門／BLUE GATE CROSSING", "藍色夏恋〈あいいろなつこい〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "10代の若者たちのひと夏の恋物語を、繊細なタッチで紡ぎ上げた青春映画。まだ未来への展望が描けない少年少女の不安や期待感をみずみずしく好演した、台湾のフレッシュな顔ぶれに注目したい。女子高生モンが親友ユエチェンに頼まれ、チャンという水泳部の男の子に接近する。しかしチャンは、ユエチェンではなくモンに恋してしまう。やがてモンはひたむきにアタックしてくるチャンに、ある秘密を告白する。", "2002-01-01", "53,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARD CASH", "ハードキャッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "二転三転の息詰まる頭脳戦とスピーディーかつハードなアクションを併せ持つ犯罪劇。主演のクリスチャン・スレーターら個性派俳優たちがひとクセある登場人物を怪演、その絡み合いが絶妙だ。現金強奪専門の知能犯テイラーは、最後のヤマに挑むことに。しかし計画は成功するも、奪った金がおとり捜査用と判明。追われる身となった彼は八方塞がりになる。そんな彼に悪徳捜査官のコーネルが接近する。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("柳川堀割物語", "柳川堀割物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "「風の谷のナウシカ」などの宮崎駿＝高畑勲コンビがつくった初のドキュメンタリー。高度経済成長期に汚く澱んでしまった九州・柳川の堀り割りが、市役所の一職員の提言と市民の協力とで、美しい流れに戻るまでを、アニメーションも交えながらレポートする。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Waterworld", "ウォーターワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "西暦2500年。何世紀にも及ぶ環境破壊のため、地球上の氷はすべて溶け去り、世界は海の底に沈んでしまう。生き残ったわずかな人間たちは巨大な人工の浮遊都市“アトール”を築き、厳しい統制のもと生活している。孤独を愛し、放浪の旅を続けるマリナーはアトールで、謎の少女エノーラとその義母ヘレンと出会う。エノーラの背中には世界に唯一存在するという伝説の陸地“ドライランド”への道標が彫られている。果たしてドライランドは、本当に存在するのか？　マリナーの闘いは始まった……。１億7500万ドルという当時最高の製作費が話題になった海洋アクション超大作。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("田園のユーウツ", "田園のユーウツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "閑静な住宅地を舞台に、自治会をめぐって繰り広げられる騒動と奇妙な人間関係を描くヒューマン・コメディ。郊外の高級住宅地、羊が丘１丁目に暮らす仕事一筋のサラリーマン川喜多は、ある出来事をきっかけに自治会長を引き受けることになる。以前から噂されていた自治会内での不正疑惑を調査し始めた彼は、様々な問題に巻き込まれていく。いがみ合いや権力争いを露呈させていく住民たちの騒動を、三浦友和をはじめ、倍賞美津子、鴻上尚史らベテラン陣がコミカルに演じる。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シャーリー・テンプル・ジャポン　partII", "シャーリー・テンプル・ジャポン　partII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "「亀虫」などの作家性の強い作品で、映画ファンの注目を集めている新鋭、冨永昌敬の長編作品。自身の手による同名サイレント映画をセルフ・リメイクし、風刺の効いた本格的なドラマを展開させている。実験精神に富んだヴィジュアルや、奇想天外な展開を盛り込んだ独特のユーモアに注目。長野県ワシントンDC郡ウォーターゲート町。現職の町長候補、根本は、確実な1票を確保するため有権者たちを神社に隔離するが……。", "2005-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠い一本の道", "遠い一本の道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "昭和35年の安保の年、かつて最も恵まれた職業だった国鉄マンにも高度成長の波が押し寄せ、合理化の名のもとに労働者の生活は次第に貧窮化、主人公・市蔵の職場も人員整理の対象となる。市蔵は労働者としての誇りを捨てずに立ち上がるが……。女優・左幸子の第１回監督作。田中絹代に次ぐ女優出身監督となった左幸子の、骨格のしっかりした堂々の演出ぶりが評判となった。当時の国労が全面的に援助。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あじまぁのウタ／上原知子・天上の歌声", "あじまぁのウタ／上原知子・天上の歌声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "音楽マニアとして知られ、自作の音楽も手掛ける「EUREKA〈ユリイカ〉」の青山真治監督による音楽ドキュメンタリー。沖縄ポップスを代表するりんけんバンドの歌姫・上原知子にスポットを当てる。彼女の歌に焦点を定め、単なるライブ・フィルムとは違った求心力のある“人物像”をクリエイトしている。撮影は「シェイディー・グローヴ」など青山と名コンビを組んでいる、名手・たむらまさき。ガラスを通した長回しに魅了される。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ISLE OF THE DEAD", "吸血鬼ボボラカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "ギリシャの小さな島で伝染病が発生。それを吸血鬼の呪いからだと信じてしまう女のために、若く美しい娘が生きたまま埋葬されるが……。1930～40年代に一世を風靡した怪奇役者、B・カーロフ主演のクラシック・ホラー。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVERYTHING YOU ALWAYS WANTED TO KNOW ABOUT SEX（BUT WERE AFRAID TO ASK）", "ウディ・アレンの誰でも知りたがっているくせにちょっと聞きにくいSEXのすべてについて教えましょう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "初期W・アレンが手掛けた、“セックス”にまつわる悲喜劇を描き出すオムニバス・コメディ。デビッド・ルーベンなる医学博士の著作の映画化という体裁。『媚薬の効能』『ソドミーって何？』『エクスタシーは所を選ばず』『女装の歓び」『これが変態だ』『SFボイン・パニック』『ミクロの精子圏』の全７エピソードで構成。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大幹部 殴り込み", "大幹部 殴り込み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "西尾組の準幹部・風間哲と石岡、野寺の３人は組のために懲役刑に服する。刑期を終えて出所した哲は、平田組がわが者顔にのさばり、兄弟分までがこれにならうのを見るに見かねて、殴り込みをかける。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪名一代", "悪名一代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "近くアメリカから帰国する伯母から３億円の遺産を相続する蔦江の周りには、シルクハット、お十夜一家、沖縄の源八など、金の亡者どもが集まり、蔦江を苦しめる。そこで朝吉と清次が、憤怒の大活躍。悪党一味は一掃される。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルー・テーズ対力道山　世界選手権争奪戦", "ルー・テーズ対力道山　世界選手権争奪戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "1950年代、TVの街頭中継や家庭への普及と共に、国民的ヒーローとして大人気を獲得したプロレスラー、力道山。超ビッグスターとして国民の注目を一身に浴びていた彼が、世界選手権王者ルー・テーズに挑戦した試合の模様を収めた貴重なドキュメンタリー作品。その実況中継の始終がヴィヴィッドにスクリーンに映し出される。必殺技バックドロップを編み出したルー・テーズに対して、力道山は正攻法の空手チョップで挑む。果たして、勝敗の行方はいかに？", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラ×メガギラス・G消滅作戦", "ゴジラ×メガギラス・G消滅作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "防衛庁内に設立されたG対策本部では、ゴジラを完全消滅させてしまう計画が進行。そのためのすべてを吸い込み別次元に葬り去る新兵器、ブラックホール砲“ディメンション・タイド”が完成する。しかし、その実験でブラックホールによる時空の歪みが発生、古代の巨大昆虫の幼虫メガヌロンが現代に蘇り、大繁殖してしまう。そしてメガヌロンの成虫の群れはゴジラを襲ってエネルギーを吸収、群れのリーダーとなる幼虫に注入する。そしてその幼虫が脱皮、孵化し、巨大戦闘怪獣メガギラスに変身を遂げるのだった……。1956年の「空の大怪獣ラドン」に登場したメガヌロンの進化形メガギラスが新怪獣として出現。ゴジラ対メガギラス対人間の戦いが繰り広げられる。監督はこれがデビュー作となった手塚昌明。", "2000-12-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダーティペア 劇場版", "ダーティペア 劇場版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "人気を博したアニメーション・シリーズの劇場版。“ダーティペア”と呼ばれる二人組の女の子が宇宙を舞台に難事件解決に挑む姿を描く。ユリとケイに新たな指令が下り、二人はある惑星へと向かう。新種元素をめぐる国家紛争の解決がその指令だった。様々なトラブルを乗り越え、やがて二人はナゾの科学者ワッツマンが事件の鍵を握っていることに気付く。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アントキノイノチ", "アントキノイノチ", "", "", "http://image.eiga.k-img.com/images/movie/56054/315x210.jpg?4802", 131, "さだまさしによる原作小説を、岡田将生、榮倉奈々の主演で映画化。監督は「ヘヴンズストーリー」の瀬々敬久。高校時代のある事件がきっかけで心を閉ざしていた杏平は、遺品整理業の職に就く。そこで出会った女性・ゆきにひかれていく杏平だったが、ある日、ゆきの衝撃的な過去を知ってしまう。さらにゆきが杏平の前から姿を消してしまい……。過去の傷を引きずる2人の若者が、遺品整理という仕事を通して再生していく姿を描く。共演に原田泰造、松坂桃李、檀れい、柄本明ら。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Wrong Man", "愛の拘束", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「ブレスレス」「グレート・ボールズ・オブ・ファイヤー」のジム・マクブライド監督が手掛けたエロティック・サスペンス。身に覚えのない殺人容疑で警察から追われている男アレックスは、旅行中のある夫婦の車に同乗させてもらうことに。その夫婦とは、粗暴で謎めいた夫フィリップとアレックスを誘惑する若妻ミッシー。彼ら3人の奇妙な逃避行と愛憎にまみれた三角関係を描く。ロザンナ・アークエットのヌードシーンが話題を呼んだ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地震列島", "地震列島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "東海大地震の予言が飛び交う世相をバックに、アメリカ映画「大地震」の状況設定を日本に置きかえて作られた一作。ラスト50分の、大都市・東京を混乱に陥れる地震パニックの描写が見どころ。とりわけラッシュアワーの地下鉄が恐怖に変わるスペクタクルが迫真ものだ。", "1980-01-01", "76", 0, NOW(), NOW());
